using System;
using System.Collections.Generic;

namespace QuranVerses
{
    public class Quran { 
    public List<string> AllVersesBosnian { get { return GetVerses(0, Constants.LanguageBosnian); } }
    public List<string> AllVersesEnglish { get { return GetVerses(0, Constants.LanguageEnglish); } }
    public List<string> AllVersesGerman { get { return GetVerses(0, Constants.LanguageGerman); } }
    public List<Surah> AllSurahBihEngDe { get { return GetCompleteSurahList(); } }


    // private List<string> GetAllVerses(string languageParam)
    #region private List<string> GetVerses(string languageParam)
    public List<string> GetVerses(int surahNumberParam, string languageParam)
    {
        List<string> result = new List<string>();

        try
        {
            if (surahNumberParam == 0)
            {
                for (int i = 1; i < 115; i++)
                {
                    Surah surahTemp = GetSurah(i);

                    foreach (var item in surahTemp.VerseList)
                    {
                        if (item.Number > 0 && languageParam == Constants.LanguageBosnian)
                        {
                            result.Add(item.TextBosnian);
                        }
                        else if (item.Number > 0 && languageParam == Constants.LanguageEnglish)
                        {
                            result.Add(item.TextEnglish);
                        }
                        else if (item.Number > 0 && languageParam == Constants.LanguageGerman)
                        {
                            result.Add(item.TextGerman);
                        }
                    }
                }
            }
            else
            {
                Surah surahTemp = GetSurah(surahNumberParam);

                foreach (var item in surahTemp.VerseList)
                {
                    if (item.Number > 0 && languageParam == Constants.LanguageBosnian)
                    {
                        result.Add(item.TextBosnian);
                    }
                    else if (item.Number > 0 && languageParam == Constants.LanguageEnglish)
                    {
                        result.Add(item.TextEnglish);
                    }
                    else if (item.Number > 0 && languageParam == Constants.LanguageGerman)
                    {
                        result.Add(item.TextGerman);
                    }
                }
            }
        }
        catch
        {

        }


        return result;
    }
    #endregion


    #region 
    private List<Surah> GetCompleteSurahList()
    {
        List<Surah> result = new List<Surah>();

        for (int i = 1; i < 115; i++)
        {
            result.Add(GetSurah(i));
        }

        return result;
    }


    #endregion

    //  public Surah GetSurah(int surahNumberParam)
    #region public Surah GetSurah(int surahNumberParam)
    public Surah GetSurah(int surahNumberParam)
    {
        Surah result = new Surah();
        try
        {
            if (surahNumberParam > 0 && surahNumberParam < 115)
            {
                switch (surahNumberParam)
                {
                    // All 114 Surah hardcoded
                    #region All 114 Surah hardcoded
                    //                       

                    // 
                    #region 1 Al-Fatiha
                    case 1:
                        Surah surah = new Surah(); surah.Name = Constants.Surah1Name; surah.Number = 1; surah.VerseList = new List<Verse>();
                        surah.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "" });
                        surah.VerseList.Add(new Verse { Number = 1, TextBosnian = "S imenom Allaha, Svemilosnog, Milostivog!", TextEnglish = "In the name of Allāh, the Entirely Merciful, the Especially Merciful.", TextGerman = "Im Namen Allahs, des Allerbarmers, des Barmherzigen." });
                        surah.VerseList.Add(new Verse { Number = 2, TextBosnian = "Svaka hvala neka je Allahu, Gospodaru svjetova.", TextEnglish = "[All] praise is [due] to Allāh, Lord of the worlds -", TextGerman = "(Alles) Lob gehört Allah, dem Herrn der Welten," });
                        surah.VerseList.Add(new Verse { Number = 3, TextBosnian = "Svemilosnom, Milostivom.", TextEnglish = "The Entirely Merciful, the Especially Merciful,", TextGerman = "dem Allerbarmer, dem Barmherzigen," });
                        surah.VerseList.Add(new Verse { Number = 4, TextBosnian = "Vladaru Dana sudnjeg.", TextEnglish = "Sovereign of the Day of Recompense.", TextGerman = "dem Herrscher am Tag des Gerichts." });
                        surah.VerseList.Add(new Verse { Number = 5, TextBosnian = "Samo Tebi ibadet činimo i samo od Tebe pomoć molimo.", TextEnglish = "It is You we worship and You we ask for help.", TextGerman = "Dir allein dienen wir, und zu Dir allein flehen wir um Hilfe." });
                        surah.VerseList.Add(new Verse { Number = 6, TextBosnian = "Uputi nas na Pravi put.", TextEnglish = "Guide us to the straight path -", TextGerman = "Leite uns den geraden Weg," });
                        surah.VerseList.Add(new Verse { Number = 7, TextBosnian = "Na put onih kojima si blagodati podario. A ne na put onih na koje se srdžba izlila, niti na put onih koji su zalutali.", TextEnglish = "The path of those upon whom You have bestowed favor, not of those who have earned [Your] anger or of those who are astray.", TextGerman = "den Weg derjenigen, denen Du Gunst erwiesen hast, nicht derjenigen, die (Deinen) Zorn erregt haben, und nicht der Irregehenden!" });
                        result = surah;
                        break;
                    #endregion

                    // 
                    #region 2 Al-Beqarah
                    case 2:
                        Surah surah2 = new Surah(); surah2.Name = Constants.Surah2Name; surah2.Number = 2; surah2.VerseList = new List<Verse>();
                        surah2.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah2.VerseList.Add(new Verse { Number = 1, TextBosnian = "Elif-lam-mim", TextEnglish = "Alif, Lām, Meem.", TextGerman = "Alif-Lam-Mim." });
                        surah2.VerseList.Add(new Verse { Number = 2, TextBosnian = "Ova Knjiga, u koju nema sumnje, uputa je bogobojaznima", TextEnglish = "This is the Book about which there is no doubt, a guidance for those conscious of Allāh -", TextGerman = "Dieses Buch, an dem es keinen Zweifel gibt, ist eine Rechtleitung für die Gottesfürchtigen," });
                        surah2.VerseList.Add(new Verse { Number = 3, TextBosnian = "onima koji u ono što je čulima nedokučivo vjeruju i koji namaz obavljaju i dijele od onoga čime smo ih Mi opskrbili,", TextEnglish = "Who believe in the unseen, establish prayer, and spend out of what We have provided for them,", TextGerman = "die an das Verborgene glauben, das Gebet verrichten und von dem, womit Wir sie versorgt haben, ausgeben" });
                        surah2.VerseList.Add(new Verse { Number = 4, TextBosnian = "i onima koji vjeruju u ono što je objavljeno tebi i što je objavljeno prije tebe, i koji u ahiret čvrsto vjeruju.", TextEnglish = "And who believe in what has been revealed to you, [O Muḥammad], and what was revealed before you, and of the Hereafter they are certain [in faith].", TextGerman = "und die an das glauben, was zu dir (an Offenbarung) herabgesandt worden ist, und was vor dir herabgesandt wurde, und die vom Jenseits überzeugt sind." });
                        surah2.VerseList.Add(new Verse { Number = 5, TextBosnian = "To su oni koji su pod Uputom Gospodara svoga i to su oni koji su uspjeli.", TextEnglish = "Those are upon [right] guidance from their Lord, and it is those who are the successful.", TextGerman = "Jene verfahren nach einer Rechtleitung von ihrem Herrn, und das sind diejenigen, denen es wohl ergeht." });
                        surah2.VerseList.Add(new Verse { Number = 6, TextBosnian = "Onima koji ne vjeruju doista je svejedno upozoravao ti njih ili ne upozoravao, oni ne vjeruju.", TextEnglish = "Indeed, those who disbelieve - it is all the same for them whether you warn them or do not warn them - they will not believe.", TextGerman = "Gleich ist es in Bezug auf diejenigen, die ungläubig sind, ob du sie warnst oder nicht warnst; sie glauben nicht." });
                        surah2.VerseList.Add(new Verse { Number = 7, TextBosnian = "Zapečatio je Allah srca njihova i uši njihove, a na njihovim očima je koprena; njima pripada patnja golema.", TextEnglish = "Allāh has set a seal upon their hearts and upon their hearing, and over their vision is a veil. And for them is a great punishment.", TextGerman = "Allah hat ihre Herzen und ihr Gehör versiegelt, über ihrem Augenlicht befindet sich eine Hülle. Für sie wird es gewaltige Strafe geben." });
                        surah2.VerseList.Add(new Verse { Number = 8, TextBosnian = "Ima ljudi koji govore: 'Vjerujemo u Allaha i Posljednji dan', a oni nisu vjernici!", TextEnglish = "And of the people are some who say, 'We believe in Allāh and the Last Day,' but they are not believers.", TextGerman = "Unter den Menschen gibt es manche, die sagen: „Wir glauben an Allah und an den Jüngsten Tag, doch sind sie nicht gläubig." });
                        surah2.VerseList.Add(new Verse { Number = 9, TextBosnian = "Oni misle da varaju Allaha i one koji vjeruju, a nikog osim sebe oni ne varaju, ali ne osjećaju.", TextEnglish = "They [think to] deceive Allāh and those who believe, but they deceive not except themselves and perceive [it] not.", TextGerman = "Sie möchten Allah und diejenigen, die glauben, betrügen. Aber sie betrügen nur sich selbst, ohne zu merken." });
                        surah2.VerseList.Add(new Verse { Number = 10, TextBosnian = "U srcima njihovim je bolest i Allah im povećava bolest. Njih čeka patnja bolna zbog toga što su lagali.", TextEnglish = "In their hearts is disease, so Allāh has increased their disease; and for them is a painful punishment because they [habitually] used to lie.", TextGerman = "In ihren Herzen ist Krankheit, und da hat Allah ihnen die Krankheit noch gemehrt. Für sie wird es schmerzhafte Strafe dafür geben, daß sie zu lügen pflegten." });
                        surah2.VerseList.Add(new Verse { Number = 11, TextBosnian = "Kada im se kaže: 'Ne pravite nered na Zemlji!', odgovaraju: 'Mi samo red uspostavljamo!'", TextEnglish = "And when it is said to them, 'Do not cause corruption on the earth,' they say, 'We are but reformers.'", TextGerman = "Und wenn man zu ihnen sagt: „Stiftet nicht Unheil auf der Erde! sagen sie: „Wir sind ja nur Heilstifter." });
                        surah2.VerseList.Add(new Verse { Number = 12, TextBosnian = "Naprotiv! Oni su uistinu pravi smutljivci, ali to ne opažaju.", TextEnglish = "Unquestionably, it is they who are the corrupters, but they perceive [it] not.", TextGerman = "Dabei sind doch eben sie die Unheilstifter, nur merken sie nicht." });
                        surah2.VerseList.Add(new Verse { Number = 13, TextBosnian = "Kada im se kaže: 'Vjerujte kao što ovi ljudi vjeruju!', oni odgovaraju: 'Zar da vjerujemo kao što maloumnici vjeruju!?' A doista su oni maloumnici, ali to oni ne znaju!", TextEnglish = "And when it is said to them, 'Believe as the people have believed,' they say, 'Should we believe as the foolish have believed?' Unquestionably, it is they who are the foolish, but they know [it] not.", TextGerman = "Und wenn man zu ihnen sagt: „Glaubt, wie die Menschen glauben!, so sagen sie: „Sollen wir glauben, wie die Toren glauben? Dabei sind doch eben sie die Toren. Aber sie wissen nicht." });
                        surah2.VerseList.Add(new Verse { Number = 14, TextBosnian = "Kada susretnu one koji vjeruju, oni govore: 'Vjerujemo!', a čim ostanu nasamo, sa šejtanima svojim, govore: 'Mi smo s vama. Mi se samo izrugujemo.'", TextEnglish = "And when they meet those who believe, they say, 'We believe'; but when they are alone with their evil ones, they say, 'Indeed, we are with you; we were only mockers.'", TextGerman = "Und wenn sie diejenigen treffen, die glauben, sagen sie: „Wir glauben. Wenn sie jedoch mit ihren Teufeln allein sind, so sagen sie: „Wir stehen zu euch. Wir machen uns ja nur lustig." });
                        surah2.VerseList.Add(new Verse { Number = 15, TextBosnian = "Allah se njima izruguje i - puštajući ih - pomaže im da u svojoj osionosti lutaju.", TextEnglish = "[But] Allāh mocks them and prolongs them in their transgression [while] they wander blindly.", TextGerman = "Allah ist es, Der Sich über sie lustig macht. Und Er läßt sie in ihrer Auflehnung umherirren." });
                        surah2.VerseList.Add(new Verse { Number = 16, TextBosnian = "To su oni koji su zabludu uzeli umjesto Upute, ali im njihova trgovina neće donijeti dobiti, jer oni Uputu nisu slijedili.", TextEnglish = "Those are the ones who have purchased error [in exchange] for guidance, so their transaction has brought no profit, nor were they guided.", TextGerman = "Das sind diejenigen, die das Irregehen um die Rechtleitung erkauft haben, doch hat ihr Handel keinen Gewinn gebracht, und sie sind nicht rechtgeleitet." });
                        surah2.VerseList.Add(new Verse { Number = 17, TextBosnian = "Oni su slični onome koji potpali vatru, a kada ona osvijetli ono što oko njega je, Allah im oduzme svjetlo i ostavi ih u tminama, pa ništa ne vide.", TextEnglish = "Their example is that of one who kindled a fire, but when it illuminated what was around him, Allāh took away their light and left them in darkness [so] they could not see.", TextGerman = "Ihr Gleichnis ist das jemandes, der ein Feuer anzündet. Nachdem es seine Umgebung erhellt hat, nimmt Allah ihr Licht weg und läßt sie in Finsternis zurück; sie sehen nicht." });
                        surah2.VerseList.Add(new Verse { Number = 18, TextBosnian = "Gluhi, nijemi i slijepi su oni, pa ne mogu da se povrate!", TextEnglish = "Deaf, dumb and blind - so they will not return [to the right path].", TextGerman = "Taub, stumm und blind: So werden sie nicht umkehren." });
                        surah2.VerseList.Add(new Verse { Number = 19, TextBosnian = "Ili su oni slični onima koji za vrijeme silnog pljuska s neba, uz tmine, grmljavinu i munje, zbog gromova stavljaju prste u uši bojeći se smrti. A Allah obuhvata nevjernike.", TextEnglish = "Or [it is] like a rainstorm from the sky within which is darkness, thunder and lightning. They put their fingers in their ears against the thunderclaps in dread of death. But Allāh is encompassing[14] of the disbelievers.", TextGerman = "Oder es ist wie ein Gewitterregen, der vom Himmel niedergeht, voller Finsternis, Donner und Blitz. Sie stecken sich die Finger in die Ohren vor den Donnerschlägen, um dem Tod zu entfliehen, doch Allah umfaßt die Ungläubigen." });
                        surah2.VerseList.Add(new Verse { Number = 20, TextBosnian = "Munja samo što ih ne zaslijepi: kad god im osvijetli - oni krenu, a čim ih tami prepusti - zastanu! A da Allah hoće, On bi im oduzeo i sluh i vid. Allah, uistinu, sve može.", TextEnglish = "The lightning almost snatches away their sight. Every time it lights [the way] for them, they walk therein; but when darkness comes over them, they stand [still]. And if Allāh had willed, He could have taken away their hearing and their sight. Indeed, Allāh is over all things competent.", TextGerman = "Der Blitz reißt ihnen beinahe das Augenlicht fort. Jedesmal, wenn er ihnen Helligkeit verbreitet, gehen sie darin. Und wenn es finster um sie wird, bleiben sie stehen. Wenn Allah wollte, nähme Er ihnen wahrlich Gehör und Augenlicht. Allah hat zu allem die Macht." });
                        surah2.VerseList.Add(new Verse { Number = 21, TextBosnian = "O ljudi, ibadet činite Gospodaru svome, Koji je stvorio vas i one prije vas, da biste se uščuvali.", TextEnglish = "O mankind, worship your Lord, who created you and those before you, that you may become righteous -", TextGerman = "O ihr Menschen! Dient eurem Herrn, Der euch und diejenigen vor euch erschaffen hat, auf daß ihr gottesfürchtig werden möget!" });
                        surah2.VerseList.Add(new Verse { Number = 22, TextBosnian = "Koji vam je Zemlju posteljom, a nebo zdanjem učinio; Koji s neba spušta vodu i s njom izvodi plodove, opskrbu vašu. Zato ne pripisujte Allahu ortake, a vi znate.", TextEnglish = "[He] who made for you the earth a bed [spread out] and the sky a ceiling and sent down from the sky, rain and brought forth thereby fruits as provision for you. So do not attribute to Allāh equals while you know [that there is nothing similar to Him].", TextGerman = "Der euch die Erde zu einem Ruhebett und den Himmel zu einem Gebäude gemacht hat und vom Himmel Wasser herabkommen läßt, durch das Er dann für euch Früchte als Versorgung hervorbringt. So stellt Allah nicht andere als Seinesgleichen zur Seite, wo ihr (es) doch (besser) wißt." });
                        surah2.VerseList.Add(new Verse { Number = 23, TextBosnian = "A ako sumnjate u ono što smo Mi objavili robu Svome, sačinite vi jednu suru sličnu tome i pozovite i svjedoke vaše, mimo Allaha, ako istinu govorite.", TextEnglish = "And if you are in doubt about what We have sent down [i.e., the Qur’ān] upon Our Servant [i.e., Prophet Muḥammad (ﷺ)], then produce a sūrah the like thereof and call upon your witnesses [i.e., supporters] other than Allāh, if you should be truthful.", TextGerman = "Und wenn ihr im Zweifel über das seid, was Wir Unserem Diener offenbart haben, dann bringt doch eine Sura gleicher Art bei und ruft eure Zeugen außer Allah an, wenn ihr wahrhaftig seid!" });
                        surah2.VerseList.Add(new Verse { Number = 24, TextBosnian = "Pa ako to ne učinite, a sigurno nećete učiniti, onda se čuvajte vatre čije će gorivo biti ljudi i kamenje; pripremljena je ona za nevjernike.", TextEnglish = "But if you do not - and you will never be able to - then fear the Fire, whose fuel is people and stones, prepared for the disbelievers.", TextGerman = "Doch wenn ihr es nicht tut - und ihr werdet es nicht tun -, dann hütet euch vor dem (Höllen)feuer, dessen Brennstoff Menschen und Steine sind. Es ist für die Ungläubigen bereitet." });
                        surah2.VerseList.Add(new Verse { Number = 25, TextBosnian = "A obraduj one koji vjeruju i dobra djela čine da njima pripadaju džennetske bašče kroz koje rijeke teku! Kad god budu opskrbljeni kakvim plodom, reći će: 'Ovim smo i prije bili opskrbljeni!' A bit će im davani samo njima slični! U njima će oni supruge očišćene imati i oni će u njima vječno boraviti.", TextEnglish = "And give good tidings to those who believe and do righteous deeds that they will have gardens [in Paradise] beneath which rivers flow. Whenever they are provided with a provision of fruit therefrom, they will say, 'This is what we were provided with before.' And it is given to them in likeness. And they will have therein purified spouses, and they will abide therein eternally.", TextGerman = "Und verkünde denen, die glauben und rechtschaffene Werke tun, (die frohe Botschaft,) daß ihnen Gärten zuteil werden, durcheilt von Bächen. Jedesmal, wenn sie mit einer Frucht daraus versorgt werden, sagen sie: „Das ist ja das, womit wir zuvor versorgt wurden; doch es ist ihnen eine ihr ähnliche gegeben worden. Und darin haben sie vollkommen gereinigte Gattinnen. Und ewig werden sie darin bleiben." });
                        surah2.VerseList.Add(new Verse { Number = 26, TextBosnian = "Doista se Allah ne ustručava za primjer navesti komarca ili nešto iznad njega; oni koji vjeruju- pa oni znaju da je to Istina od Gospodara njihova; a oni koji ne vjeruju govore: 'Šta to Allah hoće ovim primjerom?' Time On da da mnogi u zabludu skrenu, a mnoge na Pravi put upućuje, a time na stranputicu skreće samo buntovnike;", TextEnglish = "Indeed, Allāh is not timid to present an example - that of a mosquito or what is smaller[15] than it. And those who have believed know that it is the truth from their Lord. But as for those who disbelieve, they say, 'What did Allāh intend by this as an example?' He misleads many thereby and guides many thereby. And He misleads not except the defiantly disobedient,", TextGerman = "Allah schämt Sich nicht, ein Gleichnis auch nur mit einer Mücke oder mit etwas darüber (hinaus) zu prägen. Was nun diejenigen angeht, die glauben, so wissen sie, daß es die Wahrheit von ihrem Herrn ist. Was aber diejenigen angeht, die ungläubig sind, so sagen sie: „Was will denn Allah damit als Gleichnis? Er läßt damit viele in die Irre gehen und leitet viele damit recht, doch läßt Er damit nur die Frevler in die Irre gehen," });
                        surah2.VerseList.Add(new Verse { Number = 27, TextBosnian = "One koji krše obavezu prema Allahu nakon što je prihvaćena i prekidaju ono što je Allah naredio da se održava i nered na Zemlji prave; to su pravi gubitnici.", TextEnglish = "Who break the covenant of Allāh after contracting it and sever that which Allāh has ordered to be joined and cause corruption on earth. It is those who are the losers.", TextGerman = "die Allahs Bund nach seiner Abmachung brechen und trennen, was Allah befohlen hat, daß es verbunden werden soll, und auf der Erde Unheil stiften - das sind die Verlierer." });
                        surah2.VerseList.Add(new Verse { Number = 28, TextBosnian = "Kako ne vjerujete u Allaha, vi koji ste bili mrtvi pa vas je On oživio, zatim će vas usmrtiti, zatim ponovo oživiti, potom ćete Njemu biti vraćeni?!", TextEnglish = "How can you disbelieve in Allāh when you were lifeless and He brought you to life; then He will cause you to die, then He will bring you [back] to life, and then to Him you will be returned.", TextGerman = "Wie könnt ihr Allah verleugnen, wo ihr doch tot wart und Er euch dann lebendig gemacht hat, euch hierauf sterben läßt und darauf wieder lebendig machen wird, worauf ihr zu Ihm zurückgebracht werdet?" });
                        surah2.VerseList.Add(new Verse { Number = 29, TextBosnian = "On je za vas sve na Zemlji stvorio, zatim se na nebo usmjerio, i kao sedam nebesa ga uredio; On sve dobro zna.", TextEnglish = "It is He who created for you all of that which is on the earth. Then He directed Himself[16] to the heaven, [His being above all creation], and made them seven heavens, and He is Knowing of all things.", TextGerman = "Er ist es, Der für euch alles, was auf der Erde ist, erschuf und Sich hierauf dem Himmel zuwandte und ihn dann zu sieben Himmeln formte. Er weiß über alles Bescheid." });
                        surah2.VerseList.Add(new Verse { Number = 30, TextBosnian = "A kada Gospodar tvoj reče melecima: 'Ja ću na Zemlji nasljednika - halifu postaviti!', oni upitaše: 'Zar ćeš na Zemlji postaviti onoga koji će na njoj nered širiti i krv prolijevati?! A mi Tebe veličamo hvalom i kako Tebi dolikuje štujemo.' On reče: 'Ja, doista, znam ono što vi ne znate.'", TextEnglish = "And [mention, O Muḥammad], when your Lord said to the angels, 'Indeed, I will make upon the earth a successive authority.'[17] They said, 'Will You place upon it one who causes corruption therein and sheds blood, while we exalt You with praise and declare Your perfection?'[18] He [Allāh] said, 'Indeed, I know that which you do not know.'", TextGerman = "Und als dein Herr zu den Engeln sagte: „Ich bin dabei, auf der Erde einen Statthalter einzusetzen, da sagten sie: „Willst Du auf ihr etwa jemanden einsetzen, der auf ihr Unheil stiftet und Blut vergießt, wo wir Dich doch lobpreisen und Deiner Heiligkeit lobsingen? Er sagte: „Ich weiß, was ihr nicht wißt." });
                        surah2.VerseList.Add(new Verse { Number = 31, TextBosnian = "I pouči On Adema svim imenima, a onda ih predoči melecima i reče: 'Kažite Mi imena njihova, ako istinu govorite!'", TextEnglish = "And He taught Adam the names - all of them. Then He showed them to the angels and said, 'Inform Me of the names of these, if you are truthful.'", TextGerman = "Und Er lehrte Adam die Namen alle. Hierauf legte Er sie den Engeln vor und sagte: „Teilt Mir deren Namen mit, wenn ihr wahrhaftig seid!" });
                        surah2.VerseList.Add(new Verse { Number = 32, TextBosnian = "Uzvišen si!', rekoše oni, 'mi znamo samo ono čemu si nas Ti poučio! Zasigurno si Ti Sveznajući i Mudri. '", TextEnglish = "They said, 'Exalted are You; we have no knowledge except what You have taught us. Indeed, it is You who is the Knowing,[19] the Wise.'[20]", TextGerman = "Sie sagten: „Preis sei Dir! Wir haben kein Wissen außer dem, was Du uns gelehrt hast. Du bist ja der Allwissende und Allweise." });
                        surah2.VerseList.Add(new Verse { Number = 33, TextBosnian = "O Ademe', reče On, 'saopći im njihova imena!' I kada im on saopći imena njihova, Allah reče: 'Zar vam nisam rekao da samo Ja znam tajne nebesa i Zemlje i da samo Ja znam ono što pokazujete i što krijete?'", TextEnglish = "He said, 'O Adam, inform them of their names.' And when he had informed them of their names, He said, 'Did I not tell you that I know the unseen [aspects] of the heavens and the earth? And I know what you reveal and what you have concealed.'", TextGerman = "Er sagte: „O Adam, teile ihnen ihre Namen mit! Als er ihnen ihre Namen mitgeteilt hatte, sagte Er: „Habe Ich euch nicht gesagt, Ich kenne das Verborgene der Himmel und der Erde, und Ich weiß auch, was ihr offenlegt und was ihr verborgen zu halten sucht?" });
                        surah2.VerseList.Add(new Verse { Number = 34, TextBosnian = "A kad rekosmo melecima: 'Sedždu učinite Ademu!', oni sedždu učiniše svi izuzev Iblisa.' On ne htjede i uzoholi se, a nevjernik bio je.", TextEnglish = "And [mention] when We said to the angels, 'Prostrate before Adam'; so they prostrated, except for Iblees.[21] He refused and was arrogant and became of the disbelievers.", TextGerman = "Und als Wir zu den Engeln sagten: „Werft euch vor Adam nieder! Da warfen sie sich nieder, außer Iblis. Er weigerte sich und verhielt sich hochmütig und gehörte zu den Ungläubigen." });
                        surah2.VerseList.Add(new Verse { Number = 35, TextBosnian = "I Mi rekosmo: 'O Ademe, nastanite se ti i supruga tvoja u Džennetu i jedite u njemu gdje god želite, ali se ovome drvetu' ne približujte da ne budete među zulumćarima.'", TextEnglish = "And We said, 'O Adam, dwell, you and your wife, in Paradise and eat therefrom in [ease and] abundance from wherever you will. But do not approach this tree, lest you be among the wrongdoers.'", TextGerman = "Und Wir sagten: „O Adam, bewohne du und deine Gattin den (Paradies)garten, und eßt von ihm reichlich, wo immer ihr wollt! Aber naht euch nicht diesem Baum, sonst gehört ihr zu den Ungerechten!" });
                        surah2.VerseList.Add(new Verse { Number = 36, TextBosnian = "Ali, šejtan ih navede na grijeh zbog tog drveta i izvede ih iz onoga u čemu su bili. 'Siđite!',' rekosmo Mi. 'Jedni drugima bit ćete neprijatelji. Do određenog vremena na Zemlji je vaše boravište i neko vrijeme uživanje.'", TextEnglish = "But Satan caused them to slip out of it and removed them from that [condition] in which they had been. And We said, 'Go down, [all of you], as enemies to one another, and you will have upon the earth a place of settlement and provision for a time.'", TextGerman = "Doch Satan entfernte sie davon, und da vertrieb er sie aus dem, worin sie (an Glückseligkeit) gewesen waren. Wir sagten: Geht fort! Einige von euch seien der anderen Feind. Und auf der Erde sollt ihr Aufenthalt und Nießbrauch auf Zeit haben." });
                        surah2.VerseList.Add(new Verse { Number = 37, TextBosnian = "I Adem primi neke riječi' od Gospodara svoga, pa mu On oprosti; On je, doista, stalni Primatelj pokajanja, Milostivi.", TextEnglish = "Then Adam received from his Lord [some] words,[22] and He accepted his repentance. Indeed, it is He who is the Accepting of Repentance,[23] the Merciful.", TextGerman = "Da empfing Adam von seinem Herrn Worte, und darauf nahm Er seine Reue an. Er ist ja der Reue-Annehmende und Barmherzige." });
                        surah2.VerseList.Add(new Verse { Number = 38, TextBosnian = "Mi rekosmo: 'Silazite iz njega svi! Od Mene će vam Uputa dolaziti, a oni koj Uputu Moju budu slijedili, ničega se neće bojati, niti će za bilo čim tugovati.'", TextEnglish = "We said, 'Go down from it, all of you. And when guidance comes to you from Me, whoever follows My guidance - there will be no fear concerning them, nor will they grieve.", TextGerman = "Wir sagten: Geht alle fort von ihm (, dem Paradiesgarten). Wenn nun von Mir Rechtleitung zu euch kommt, dann soll über diejenigen, die Meiner Rechtleitung folgen, keine Furcht kommen, noch sollen sie traurig sein." });
                        surah2.VerseList.Add(new Verse { Number = 39, TextBosnian = "A oni koji ne budu vjerovali i dokaze i ajete Naše budu poricali bit će stanovnici Vatre; u njoj će vječno ostati.", TextEnglish = "And those who disbelieve and deny Our signs - those will be companions of the Fire; they will abide therein eternally.'", TextGerman = "Diejenigen aber, die ungläubig sind und Unsere Zeichen für Lüge erklären, das sind Insassen des (Höllen)feuers. Ewig werden sie darin bleiben." });
                        surah2.VerseList.Add(new Verse { Number = 40, TextBosnian = "O sinovi Israilovi, sjetite se blagodati Mojih koje sam vam podario, i ispunite zavjet koji ste Mi dali, pa ću i Ja ispuniti Svoj zavjet koji sam vam dao! I samo od Mene strahujte!", TextEnglish = "O Children of Israel, remember My favor which I have bestowed upon you and fulfill My covenant [upon you] that I will fulfill your covenant [from Me], and be afraid of [only] Me.", TextGerman = "O Kinder lsra’ils, gedenkt Meiner Gunst, die Ich euch erwiesen habe! Und haltet euren Bund Mir gegenüber, so will Ich Meinen Bund euch gegenüber halten! Und vor Mir (allein) sollt ihr Ehrfurcht haben." });
                        surah2.VerseList.Add(new Verse { Number = 41, TextBosnian = "Vjerujte u ono što sam objavio potvrđujući daje istinito ono što vi imate i ne budite prvi koji u to ne vjeruju! Ne prodajite riječi Moje za bagatelnu cijenu i samo se Mene bojte!'", TextEnglish = "And believe in what I have sent down confirming that which is [already] with you, and be not the first to disbelieve in it. And do not exchange My signs for a small price, and fear [only] Me.", TextGerman = "Und glaubt an das, was Ich (als Offenbarung) hinabgesandt habe, das zu bestätigen, was euch bereits vorliegt. Und seid nicht die ersten, die es verleugnen. Und verkauft Meine Zeichen nicht für einen geringen Preis. Und Mich allein sollt ihr fürchten." });
                        surah2.VerseList.Add(new Verse { Number = 42, TextBosnian = "I ne miješajte Istinu s neistinom, a i ne tajite Istinu, a vi je znate!", TextEnglish = "And do not mix the truth with falsehood or conceal the truth while you know [it].", TextGerman = "Und verdeckt nicht das Wahre durch das Falsche, und verschweigt nicht die Wahrheit, wo ihr doch wißt!" });
                        surah2.VerseList.Add(new Verse { Number = 43, TextBosnian = "Namaz obavljajte, zekat dajite i idite na ruku' zajedno s onima koji na ruku' idu!", TextEnglish = "And establish prayer and give zakāh[24] and bow with those who bow [in worship and obedience].", TextGerman = "Und verrichtet das Gebet, entrichtet die Abgabe und verbeugt euch (im Gebet) mit den sich Verbeugenden!" });
                        surah2.VerseList.Add(new Verse { Number = 44, TextBosnian = "Zar od drugih tražite da dobročinstvo čine dok pri tome sebe zaboravljate, a vi Knjigu čitate? Zar se opametiti nećete!?", TextEnglish = "Do you order righteousness of the people and forget[25] yourselves while you recite the Scripture? Then will you not reason?", TextGerman = "Befehlt ihr denn den Menschen Güte, während ihr euch selbst vergeßt, wo ihr doch die Schrift lest? Begreift ihr denn nicht?" });
                        surah2.VerseList.Add(new Verse { Number = 45, TextBosnian = "Tražite sebi pomoć u strpljenju i namazu. To je doista teško, osim poniznima,", TextEnglish = "And seek help through patience and prayer; and indeed, it is difficult except for the humbly submissive [to Allāh]", TextGerman = "Und sucht Hilfe in der Standhaftigkeit und im Gebet! Es ist freilich schwer, nur nicht für die Demütigen," });
                        surah2.VerseList.Add(new Verse { Number = 46, TextBosnian = "onima koji su uvjereni da će se sresti sa Gospodarom svojim i da će se Njemu vratiti.", TextEnglish = "Who are certain that they will meet their Lord and that they will return to Him.", TextGerman = "die daran glauben, daß sie ihrem Herrn begegnen werden, und daß sie zu Ihm zurückkehren." });
                        surah2.VerseList.Add(new Verse { Number = 47, TextBosnian = "O sinovi Israilovi! Sjetite se blagodati Mojih koje sam vam podario i što sam vas nad drugim svjetovima bio odlikovao.", TextEnglish = "O Children of Israel, remember My favor that I have bestowed upon you and that I preferred you over the worlds [i.e., peoples].", TextGerman = "O Kinder Isra’ils, gedenkt Meiner Gunst, die Ich euch erwiesen habe, und daß Ich euch vor den (anderen) Weltenbewohnern bevorzugt habe." });
                        surah2.VerseList.Add(new Verse { Number = 48, TextBosnian = "I bojte se Dana kada niko ni za koga ništa neće moći učiniti, kada se ničiji zagovor neće prihvaćati, kada se ni od koga otkup neće primati i kada nikome niko neće u pomoć priteći.", TextEnglish = "And fear a Day when no soul will suffice for another soul[26] at all, nor will intercession be accepted from it, nor will compensation be taken from it, nor will they be aided.", TextGerman = "Und hütet euch vor einem Tag, an dem keine Seele etwas anstelle einer anderen übernehmen kann und von keiner Fürsprache noch Ersatz(leistung) angenommen wird und ihnen keine Hilfe zuteil wird!" });
                        surah2.VerseList.Add(new Verse { Number = 49, TextBosnian = "I kada smo vas izbavili od faraonovih ljudi koji su vas najgorim mukama mučili: mušku vam djecu klali, a žensku u životu ostavljali - to vam je bilo veliko iskušenje od Gospodara vašeg.", TextEnglish = "And [recall] when We saved you [i.e., your forefathers] from the people of Pharaoh, who afflicted you with the worst torment, slaughtering your [newborn] sons and keeping your females alive. And in that was a great trial from your Lord.", TextGerman = "Und (gedenkt,) als Wir euch von den Leuten Fir’auns erretteten, die euch eine böse Qual auferlegten, indem sie eure Söhne allesamt abschlachteten und (nur) eure Frauen am Leben ließen. Darin war für euch eine gewaltige Prüfung von eurem Herrn." });
                        surah2.VerseList.Add(new Verse { Number = 50, TextBosnian = "I kada smo vam more rastavili i vas izbavili, a faraonove ljude - vi ste to gledali - potopili.", TextEnglish = "And [recall] when We parted the sea for you and saved you and drowned the people of Pharaoh while you were looking on.", TextGerman = "Und als Wir für euch das Meer teilten und euch so retteten und die Leute Fir’auns ertrinken ließen, während ihr zuschautet!" });
                        surah2.VerseList.Add(new Verse { Number = 51, TextBosnian = "I kada s Musaom, četrdeset noći, susret odredismo, vi ste, nakon što on ode, zulum čineći, tele za obožavanje prihvatili.", TextEnglish = "And [recall] when We made an appointment with Moses for forty nights. Then you took [for worship] the calf after him [i.e., his departure], while you were wrongdoers.", TextGerman = "Und als Wir Uns mit Musa auf vierzig Nächte verabredeten, da nahmt ihr dann nach ihm das Kalb an, womit ihr Unrecht tatet." });
                        surah2.VerseList.Add(new Verse { Number = 52, TextBosnian = "Potom smo vam, i poslije toga, oprostili, da biste zahvalni bili.", TextEnglish = "Then We forgave you after that so perhaps you would be grateful.", TextGerman = "Hierauf, nach alledem, verziehen Wir euch, auf daß ihr dankbar wäret." });
                        surah2.VerseList.Add(new Verse { Number = 53, TextBosnian = "I kada smo dali Musau Knjigu i ono što razdvaja Istinu od neistine, da biste se pravim putem uputili.", TextEnglish = "And [recall] when We gave Moses the Scripture and criterion[27] that perhaps you would be guided.", TextGerman = "Und als Wir Musa die Schrift und die Unterscheidung gaben, auf daß ihr rechtgeleitet werdet." });
                        surah2.VerseList.Add(new Verse { Number = 54, TextBosnian = "I kada je Musa rekao narodu svome: 'Narode moj, vi ste doista sebi zulum učinili prihvativši tele; zato se Stvoritelju svome pokajte i poubijajte se!' To vam je kod vašeg Stvoritelja bolje, pa će vam pokajanje prihvatiti. Zaista je On stalni Primatelj pokajanja, Milostivi.'", TextEnglish = "And [recall] when Moses said to his people, 'O my people, indeed you have wronged yourselves by your taking of the calf [for worship]. So repent to your Creator and kill yourselves [i.e., the guilty among you]. That is best for [all of] you in the sight of your Creator.' Then He accepted your repentance; indeed, He is the Accepting of Repentance, the Merciful.", TextGerman = "Und als Musa zu seinem Volk sagte: O mein Volk, ihr habt euch selbst Unrecht zugefügt, indem ihr das Kalb (zum Gegenstand der Anbetung) genommen habt. Bereut nun vor eurem Erschaffer und tötet dann (die Schuldigen unter) euch selbst! Dies ist besser für euch vor eurem Erschaffer! Und da nahm Er eure Reue an; Er ist ja der Reue-Annehmende und Barmherzige." });
                        surah2.VerseList.Add(new Verse { Number = 55, TextBosnian = "I kada ste vi rekli: 'Musa, mi ti nećemo vjerovati dok Allaha jasno ne vidimo!', pa vas je strašna kazna obuzela, a vi ste gledali.", TextEnglish = "And [recall] when you said, 'O Moses, we will never believe you until we see Allāh outright'; so the thunderbolt took you while you were looking on.", TextGerman = "Und als ihr sagtet: „O Musa, wir werden dir nicht eher glauben, bis wir Allah unverhüllt sehen! Da überkam euch der Donnerschlag, während ihr zuschautet." });
                        surah2.VerseList.Add(new Verse { Number = 56, TextBosnian = "Zatim smo vas poslije smrti vaše oživili, da biste zahvalni bili.", TextEnglish = "Then We revived you after your death that perhaps you would be grateful.", TextGerman = "Hierauf erweckten Wir euch nach eurem Tod, auf daß ihr dankbar wäret." });
                        surah2.VerseList.Add(new Verse { Number = 57, TextBosnian = "I od oblaka smo vam hladovinu načinili i manu i prepelice vam slali. 'Jedite lijepa jela, kojima vas opskrbljujemo!' Oni Nama nisu zulum nanijeli, nego su sami sebi zulum nanosili.", TextEnglish = "And We shaded you with clouds and sent down to you manna and quails, [saying], 'Eat from the good things with which We have provided you.' And they wronged Us not - but they were [only] wronging themselves.", TextGerman = "Und Wir ließen die Wolken euch überschatten und sandten das Manna und die Wachteln auf euch hinab: „Eßt von den guten Dingen, mit denen Wir euch versorgt haben. Und sie fügten nicht Uns Unrecht zu, sondern sich selbst." });
                        surah2.VerseList.Add(new Verse { Number = 58, TextBosnian = "I kada smo rekli: 'Uđite u ovaj grad i jedite što god hoćete! Na kapiju uđite klanjajući se i kažite: 'Oprost!', pa ćemo vam oprostiti grijehe vaše, a onima koji čine dobra djela dat ćemo i više.'", TextEnglish = "And [recall] when We said, 'Enter this city [i.e., Jerusalem] and eat from it wherever you will in [ease and] abundance, and enter the gate bowing humbly[28] and say, 'Relieve us of our burdens [i.e., sins].' We will [then] forgive your sins for you, and We will increase the doers of good [in goodness and reward].'", TextGerman = "Und als Wir sagten: „Tretet ein in diese Stadt und dann eßt, wo immer ihr wollt, reichlich von (dem, was in) ihr (ist). Und tretet, euch niederwerfend, durch das Tor ein und sagt: ,Vergebung´, so vergeben Wir euch eure Verfehlungen. Und Wir werden den Gutes Tuenden noch mehr erweisen." });
                        surah2.VerseList.Add(new Verse { Number = 59, TextBosnian = "Onda su oni koji su zulum učinili zamijenili riječi drugim koje im nisu rečene, pa smo Mi na one koji su zulum učinili s neba kaznu spustili, zato što su buntovni bili.", TextEnglish = "But those who wronged changed [those words] to a statement other than that which had been said to them, so We sent down upon those who wronged a punishment [i.e., plague] from the sky because they were defiantly disobeying.", TextGerman = "Doch da tauschten diejenigen, die Unrecht taten, das Wort gegen ein anderes aus, das ihnen nicht gesagt worden war. Und da sandten Wir auf diejenigen, die Unrecht taten, eine unheilvolle Strafe vom Himmel hinab dafür, daß sie gefrevelt hatten." });
                        surah2.VerseList.Add(new Verse { Number = 60, TextBosnian = "I kada je Musa za narod svoj vodu molio, Mi smo rekli: 'Udari štapom svojim po kamenu!' Iz njega je dvanaest vrela provrelo, i svi ljudi su znali iz kojeg će vrela piti. 'Jedite i pijte Allahovu opskrbu i ne činite zlo po Zemlji nered praveći.'", TextEnglish = "And [recall] when Moses prayed for water for his people, so We said, 'Strike with your staff the stone.' And there gushed forth from it twelve springs, and every people [i.e., tribe] knew its watering place. 'Eat and drink from the provision of Allāh, and do not commit abuse on the earth, spreading corruption.'", TextGerman = "Und als Musa für sein Volk um Wasser bat, da sagten Wir: „Schlag mit deinem Stock auf den Felsen! Da entsprangen ihm zwölf Quellen. Nun wußte jedermann, wo sein Platz zum Trinken war: „Eßt und trinkt von Allahs Versorgung und richtet auf der Erde nicht unheilstiftend Verderben an!" });
                        surah2.VerseList.Add(new Verse { Number = 61, TextBosnian = "I kada ste rekli: 'Musa, mi ne možemo više jednu te istu hranu jesti. Zato, moli za nas Gospodara svoga da nam podari od onoga što zemlja rađa: povrća i krastavaca, pšenice, leće i luka crvenog!' On je rekao: 'Zar da ono što je bolje zamijenite onim što je lošije?! Idite u bilo koji grad, imat ćete ono što tražite!' I poniženje i bijeda na njih padoše i Allahovu srdžbu oni na sebe navukoše, zato što u Allahove dokaze vjerovali nisu i što su vjerovjesnike bespravno ubijali, zato što su neposlušni bili i sve granice zla prelazili.", TextEnglish = "And [recall] when you said, 'O Moses, we can never endure one [kind of] food. So call upon your Lord to bring forth for us from the earth its green herbs and its cucumbers and its garlic and its lentils and its onions.' [Moses] said, 'Would you exchange what is better for what is less? Go into [any] settlement and indeed, you will have what you have asked.' And they were covered with humiliation and poverty and returned with anger from Allāh [upon them]. That was because they [repeatedly] disbelieved in the signs of Allāh and killed the prophets without right. That was because they disobeyed and were [habitually] transgressing.", TextGerman = "Und als ihr sagtet: „O Musa, wir halten eine Speise allein nicht aus. Bitte doch für uns deinen Herrn, Er soll für uns etwas hervorbringen von dem, was die Erde wachsen läßt an Grünzeug, Gurken, Getreide, Linsen und Zwiebeln! Er sagte: „Wollt ihr das, was besser ist, eintauschen gegen das, was geringer (an Wert) ist? Geht fort in (irgendeine) Stadt! Dann werdet ihr bekommen, was ihr verlangt habt. Und es wurde ihnen Erniedrigung und Elend auferlegt, und sie zogen sich den Zorn von Allah zu. Dies, weil sie stets Allahs Zeichen verleugneten und die Propheten ohne Recht töteten; dies, weil sie sich widersetzten und stets übertraten." });
                        surah2.VerseList.Add(new Verse { Number = 62, TextBosnian = "One koji vjeruju, te one koji su bili židovi, kršćani i sabijci, a koji su vjerovali u Allaha i Posljednji dan i dobra djela činili - doista čeka nagrada od Gospodara njihova; za njih nema straha i oni neće tugovati.", TextEnglish = "Indeed, those who believed and those who were Jews or Christians or Sabeans [before Prophet Muḥammad (ﷺ)] - those [among them] who believed in Allāh and the Last Day and did righteousness - will have their reward with their Lord, and no fear will there be concerning them, nor will they grieve[29].", TextGerman = "Gewiß, diejenigen, die glauben, und diejenigen, die dem Judentum angehören, und die Christen und die Säbier - wer immer an Allah und den Jüngsten Tag glaubt und rechtschaffen handelt, - die haben ihren Lohn bei ihrem Herrn, und keine Furcht soll sie überkommen, noch werden sie traurig sein." });
                        surah2.VerseList.Add(new Verse { Number = 63, TextBosnian = "I kada smo od vas zavjet uzeli i brdo Tur iznad vas podigli: 'Snažno prihvatite ono što smo vam dali i neka vam je na umu ono što je u Knjizi, da biste se sačuvali.'", TextEnglish = "And [recall] when We took your covenant, [O Children of Israel, to abide by the Torah] and We raised over you the mount, [saying], 'Take what We have given you with determination and remember what is in it that perhaps you may become righteous.'", TextGerman = "Und als Wir mit euch ein Abkommen trafen und den Berg über euch emporhoben (und zu euch sagten): „Haltet fest an dem, was Wir euch gegeben haben, und gedenkt dessen, was es enthält, auf daß ihr gottesfürchtig werden möget!" });
                        surah2.VerseList.Add(new Verse { Number = 64, TextBosnian = "Zatim ste odustali, i da nije bilo Allahove darežljivosti prema vama i milosti Njegove, vi biste, uistinu, među gubitnicima bili.", TextEnglish = "Then you turned away after that. And if not for the favor of Allāh upon you and His mercy, you would have been among the losers.", TextGerman = "Dann kehrtet ihr euch nach alledem ab. Aber wenn nicht Allahs Huld gegen euch und Sein Erbarmen gewesen wären, gehörtet ihr wahrlich zu den Verlierern." });
                        surah2.VerseList.Add(new Verse { Number = 65, TextBosnian = "Vama je poznato šta se dogodilo onima od vas koji su subotu oskrnavili, pa smo im Mi rekli: 'Budite majmuni prezreni!'", TextEnglish = "And you had already known about those who transgressed among you concerning the sabbath, and We said to them, 'Be apes, despised.'", TextGerman = "Und ihr kennt doch diejenigen von euch, die den Sabbat übertraten. Da sagten Wir zu ihnen: „Werdet verstoßene Affen!" });
                        surah2.VerseList.Add(new Verse { Number = 66, TextBosnian = "To naselje smo učinili opomenom obližnjim naseljima, a poukom za bogobojazne.", TextEnglish = "And We made it a deterrent punishment for those who were present and those who succeeded [them] and a lesson for those who fear Allāh.", TextGerman = "Und so machten Wir dies für alle mit und nach euch zu einem warnenden Beispiel und zu einer Ermahnung für die Gottesfürchtigen." });
                        surah2.VerseList.Add(new Verse { Number = 67, TextBosnian = "I kada Musa reče narodu svome: 'Allah vam, doista, naređuje da kravu zakoljete', oni upitaše: 'Zbijaš li to ti s nama šalu?', a on reče: 'Allahu se utječem od toga da neznalica budem!'", TextEnglish = "And [recall] when Moses said to his people, 'Indeed, Allāh commands you to slaughter a cow.' They said, 'Do you take us in ridicule?' He said, 'I seek refuge in Allāh from being among the ignorant.'", TextGerman = "Und als Musa zu seinem Volk sagte: „Allah befiehlt euch, daß ihr eine Kuh schlachten sollt! Sie sagten: „Machst du dich über uns lustig? Er sagte: „Ich nehme Zuflucht bei Allah (davor), daß ich zu den Toren gehöre!" });
                        surah2.VerseList.Add(new Verse { Number = 68, TextBosnian = "Oni rekoše: 'Moli za nas Gospodara svoga da nam objasni koje dobi ona treba biti!' 'On kaže', odgovori on, 'da krava ne smije biti ni stara, ni mlada, nego između toga, srednje dobi, pa učinite to što se od vas traži!'", TextEnglish = "They said, 'Call upon your Lord to make clear to us what it is.' [Moses] said, '[Allāh] says, 'It is a cow which is neither old nor virgin, but median between that,' so do what you are commanded.'", TextGerman = "Sie sagten: „Bitte für uns deinen Herrn, uns Klarheit zu geben, wie sie sein soll! Er sagte: „Er (Allah) sagt, es soll eine Kuh sein, die weder zu alt noch zu jung zum Kalben ist, sondern dazwischen, in mittlerem Alter. So tut nun, was euch befohlen wird!" });
                        surah2.VerseList.Add(new Verse { Number = 69, TextBosnian = "Zamoli za nas Gospodara svoga', rekoše oni, 'da nam objasni kakve boje treba biti!' 'On kaže', reče on, 'da treba biti svijetložute boje, da se svidi onima koji je vide.'", TextEnglish = "They said, 'Call upon your Lord to show us what is her color.' He said, 'He says, 'It is a yellow cow, bright in color - pleasing to the observers.''", TextGerman = "Sie sagten. „Bitte für uns deinen Herrn, uns Klarheit zu geben, welche Farbe sie haben soll! Er sagte: „Er (Allah) sagt, es soll eine gelbe Kuh sein, von lebhafter Farbe, die die Betrachter erfreut." });
                        surah2.VerseList.Add(new Verse { Number = 70, TextBosnian = "Moli za nas Gospodara svoga', rekoše oni, 'da nam objasni kakva još treba biti, jer nama krave izgledaju slične, a mi ćemo nju, ako Allah htjedne, sigurno pronaći.'", TextEnglish = "They said, 'Call upon your Lord to make clear to us what it is. Indeed, [all] cows look alike to us. And indeed we, if Allāh wills, will be guided.'", TextGerman = "Sie sagten: „Bitte für uns deinen Herrn, uns Klarheit zu geben, wie sie sein soll! Die Kühe erscheinen uns (alle) ähnlich. Doch wenn Allah will, so werden wir wahrlich rechtgeleitet sein." });
                        surah2.VerseList.Add(new Verse { Number = 71, TextBosnian = "On kaže', reče on, 'da krava ne smije biti iscrpljena oranjem zemlje i natapanjem usjeva; treba biti bez ikakve mahane i biljega.' 'E, sad si kazao istinu!', rekoše oni, pa je zaklaše, i jedva to učiniše.", TextEnglish = "He said, 'He says, 'It is a cow neither trained to plow the earth nor to irrigate the field, one free from fault with no spot upon her.'' They said, 'Now you have come with the truth.' So they slaughtered her, but they could hardly do it.", TextGerman = "Er sagte: „Er (Allah) sagt, es soll eine Kuh sein, nicht fügsam gemacht durch Pflügen der Erde noch durch Bewässern von Saatfeld, fehlerfrei und makellos! Sie sagten: „Jetzt bist du mit der Wahrheit gekommen. So schlachteten sie sie, doch beinahe hätten sie es nicht getan." });
                        surah2.VerseList.Add(new Verse { Number = 72, TextBosnian = "I kada ste jednog čovjeka ubili, pa se o njemu prepirati počeli, Allah je dao da iziđe na vidjelo ono što ste bili sakrili.", TextEnglish = "And [recall] when you slew a man and disputed[30] over it, but Allāh was to bring out that which you were concealing.", TextGerman = "Und (gedenkt,) als ihr jemanden getötet hattet und euch darüber strittet; und Allah wollte herausbringen, was ihr verborgen hieltet." });
                        surah2.VerseList.Add(new Verse { Number = 73, TextBosnian = "Pa smo Mi rekli: 'Udarite ga jednim njezinim dijelom!' I eto, tako Allah mrtve vraća u život i pruža vam dokaze Svoje, da biste shvatili.", TextEnglish = "So We said, 'Strike him [i.e., the slain man] with part of it.'[31] Thus does Allāh bring the dead to life, and He shows you His signs that you might reason.", TextGerman = "Da sagten Wir: „Schlagt ihn mit einem Stück von ihr! So macht Allah die Toten wieder lebendig und zeigt euch Seine Zeichen, auf daß ihr begreifen möget." });
                        surah2.VerseList.Add(new Verse { Number = 74, TextBosnian = "Ali su srca vaša, poslije toga, postala tvrda kao kamen ili još tvrđa. A ima kamenja iz kojega rijeke izbijaju; ima ga koje puca i iz kojeg izlazi voda; a ima, doista, i onoga koje se iz straha od Allaha ruši. A Allah ne zanemaruje ono što vi radite.", TextEnglish = "Then your hearts became hardened after that, being like stones or even harder. For indeed, there are stones from which rivers burst forth, and there are some of them that split open and water comes out, and there are some of them that fall down for fear of Allāh. And Allāh is not unaware of what you do.", TextGerman = "Hierauf, nach alledem, verhärteten sich dann eure Herzen, so daß sie wie Steine waren oder noch härter. Und unter den Steinen gibt es wahrlich manche, denen Flüsse entspringen, und andere, die bersten, worauf Wasser aus ihnen herausfließt, und wieder andere, die herabstürzen aus Furcht vor Allah. Und Allah ist nicht unachtsam dessen, was ihr tut." });
                        surah2.VerseList.Add(new Verse { Number = 75, TextBosnian = "Zar vi žudite da vam oni vjeruju, a neki od njih su Allahov govor slušali, a zatim bi ga, nakon što su ga shvatili, svjesno iskrivljavali?", TextEnglish = "Do you covet [the hope, O believers], that they would believe for you while a party of them used to hear the words of Allāh and then distort it [i.e., the Torah] after they had understood it while they were knowing?", TextGerman = "Begehrt ihr (Muslime) denn, daß sie (die Juden) euch glauben, wo doch eine Gruppe von ihnen das Wort Allahs gehört und es dann, nachdem er es begriffen hatte, wissentlich verfälscht hat?" });
                        surah2.VerseList.Add(new Verse { Number = 76, TextBosnian = "Kada sretnu one koji vjeruju, oni govore: 'Mi vjerujemo!', a čim se osame jedni s drugima, kažu: 'Zar ćete im kazivati ono što je Allah vama objavio, pa da im to bude dokaz protiv vas pred Gospodarom vašim? Zar se nećete opametiti!", TextEnglish = "And when they meet those who believe, they say, 'We have believed'; but when they are alone with one another, they say, 'Do you talk to them about what Allāh has revealed to you so they can argue with you about it before your Lord?' Then will you not reason?", TextGerman = "Und wenn sie diejenigen treffen, die glauben, sagen sie: „Wir glauben. Wenn sie aber untereinander allein sind, sagen sie: „Wollt ihr ihnen erzählen, was Allah euch (Juden) enthüllt hat, damit sie es vor eurem Herrn als Beweis vorlegen? Begreift ihr denn nicht?" });
                        surah2.VerseList.Add(new Verse { Number = 77, TextBosnian = "A zar oni ne znaju da Allah zna i ono što kriju i ono što pokazuju?!", TextEnglish = "But do they not know that Allāh knows what they conceal and what they declare?", TextGerman = "Wissen sie denn nicht, daß Allah weiß, was sie geheimhalten und was sie offenlegen?" });
                        surah2.VerseList.Add(new Verse { Number = 78, TextBosnian = "Neki od njih su nepismeni i ne poznaju Knjigu, nego samo gatke. Oni samo izmišljaju.", TextEnglish = "And among them are unlettered ones who do not know the Scripture except [indulgement in] wishful thinking, but they are only assuming.", TextGerman = "Unter ihnen gibt es auch Schriftunkundige, die die Schrift nicht kennen, sondern nur Wunschvorstellungen hegen, und die doch nur Mutmaßungen anstellen." });
                        surah2.VerseList.Add(new Verse { Number = 79, TextBosnian = "A teško onima koji svojim rukama pišu knjigu, a zatim govore: 'Ovo je od Allaha', da bi to za malu vrijednost prodali. I teško njima zbog onoga što ruke njihove pišu, i teško njima što na taj način zarađuju!", TextEnglish = "So woe[32] to those who write the 'scripture' with their own hands, then say, 'This is from Allāh,' in order to exchange it for a small price. Woe to them for what their hands have written and woe to them for what they earn.", TextGerman = "Doch wehe denjenigen, die die Schrift mit ihren (eigenen) Händen schreiben und hierauf sagen: „Das ist von Allah, um sie für einen geringen Preis zu verkaufen! Wehe ihnen wegen dessen, was ihre Hände geschrieben haben, und wehe ihnen wegen dessen, was sie verdienen." });
                        surah2.VerseList.Add(new Verse { Number = 80, TextBosnian = "Oni govore: 'Vatra nas neće doticati više od nekoliko dana.'Reci: 'Da li ste za to od Allaha dobili obećanje, jer Allah neće prekršiti Svoje obećanje, ili na Allaha iznosite ono što ne znate?!'", TextEnglish = "And they say, 'Never will the Fire touch us, except for [a few] numbered days.' Say, 'Have you taken a covenant with Allāh? For Allāh will never break His covenant. Or do you say about Allāh that which you do not know?'", TextGerman = "Und sie sagen: „Das (Höllen)feuer wird uns nur für gezählte Tage berühren. Sag: Habt ihr mit Allah einen Bund geschlossen? Dann wird Allah Seinen Bund nicht brechen. Oder wollt ihr von Allah etwas sagen, was ihr nicht wißt?" });
                        surah2.VerseList.Add(new Verse { Number = 81, TextBosnian = "A nije tako! Jer oni koji budu zlo činili i grijesi ih njihovi budu sa svih strana skolili, stanovnici Vatre oni bit će i u njoj će vječno ostati.", TextEnglish = "Yes, [on the contrary], whoever earns evil and his sin has encompassed him - those are the companions of the Fire; they will abide therein eternally.", TextGerman = "Aber nein! Wer eine böse Tat begangen hat, und wen seine Verfehlung umfangen hält, das sind Insassen des (Höllen)feuers. Ewig werden sie darin bleiben." });
                        surah2.VerseList.Add(new Verse { Number = 82, TextBosnian = "A oni koji budu vjerovali i dobra djela činili, oni će stanovnici Dženneta biti; u njemu će vječno boraviti.", TextEnglish = "But they who believe and do righteous deeds - those are the companions of Paradise; they will abide therein eternally.", TextGerman = "Diejenigen aber, die glauben und rechtschaffene Werke tun, das sind Insassen des (Paradies)gartens. Ewig werden sie darin bleiben." });
                        surah2.VerseList.Add(new Verse { Number = 83, TextBosnian = "I kada smo od sinova Israilovih zavjet uzeli da ćete jedino Allahu u ibadetu biti, i roditeljima, i bližnjima, i siročadi, i siromasima dobročinstvo činiti, a ljudima lijepe riječi govoriti; i namaz obavljati i zekat davati, vi ste se poslije, izuzev nekolicine, okrećući se od toga udaljili.", TextEnglish = "And [recall] when We took the covenant from the Children of Israel, [enjoining upon them], 'Do not worship except Allāh; and to parents do good and to relatives, orphans, and the needy. And speak to people good [words] and establish prayer and give zakāh.' Then you turned away, except a few of you, and you were refusing.", TextGerman = "Und als Wir mit den Kindern Isra’ils ein Abkommen trafen: Dient keinem außer Allah! Und zu den Eltern sollt ihr gütig sein und zu den Verwandten, den Waisen und den Armen! Und sagt Gutes zu den Menschen, verrichtet das Gebet und entrichtet die Abgabe. Danach kehrtet ihr euch - bis auf wenige von euch - ab; ihr seid ja Widerstrebende." });
                        surah2.VerseList.Add(new Verse { Number = 84, TextBosnian = "I kada smo od vas zavjet uzeli da krv jedni drugima nećete prolijevati i da jedni druge iz domova vaših nećete izgoniti, vi ste to onda, prihvatili i tome svjedoci bili.", TextEnglish = "And [recall] when We took your covenant, [saying], 'Do not shed your [i.e., each other's] blood or evict one another from your homes.' Then you acknowledged [this] while you were witnessing.", TextGerman = "Und als Wir mit euch ein Abkommen trafen: Vergießt nicht (gegenseitig) euer Blut und vertreibt euch nicht selbst aus euren Wohnstätten! Hierauf habt ihr euch dazu bekannt und ihr seid dafür Zeugen." });
                        surah2.VerseList.Add(new Verse { Number = 85, TextBosnian = "Nakon toga, vi ipak jedni druge ubijate, a pojedine od vas i iz zavičaja njihovog istjerujete, pomažući se protiv njih, uz grijeh i nasilje, pa ako vam kao sužnji dođu, vi ih otkupljujete. A zabranjeno vam je da ih izgonite! Zar vi u jedan dio Knjige vjerujete, a drugi negirate?! Nikakve kazne za one od vas koji tako čine, sem poniženja na ovome svijetu, neće biti, a na Kijametskom danu bit će stavljeni na muke najteže! Allah, doista, nije nemaran prema onome što vi radite.", TextEnglish = "Then, you are those [same ones who are] killing one another and evicting a party of your people from their homes, cooperating against them in sin and aggression. And if they come to you as captives, you ransom them, although their eviction was forbidden to you. So do you believe in part of the Scripture and disbelieve in part? Then what is the recompense for those who do that among you except disgrace in worldly life; and on the Day of Resurrection they will be sent back to the severest of punishment. And Allāh is not unaware of what you do.", TextGerman = "Dennoch seid (gerade) ihr es, die ihr euch selbst (gegenseitig) tötet und eine Gruppe von euch aus seinen Wohnstätten vertreibt, indem ihr einander in Sünde und feindseligem Vorgehen gegen sie beisteht. Und wenn sie als Gefangene zu euch kommen, so kauft ihr sie los, wo euch doch ihre Vertreibung verboten worden ist. Glaubt ihr denn an einen Teil der Schrift und verleugnet einen anderen? Wer von euch aber solches tut, dessen Lohn ist nur Schande im diesseitigen Leben. Und am Tag der Auferstehung werden sie der schwersten Strafe zugeführt werden. Und Allah ist nicht unachtsam dessen, was ihr tut." });
                        surah2.VerseList.Add(new Verse { Number = 86, TextBosnian = "To su oni koji su život na ovome svijetu uzeli u zamjenu za ahiret; zato im patnja olakšana neće biti, niti će im se u pomoć priteći.", TextEnglish = "Those are the ones who have bought the life of this world [in exchange] for the Hereafter, so the punishment will not be lightened for them, nor will they be aided.", TextGerman = "Das sind diejenigen, die das diesseitige Leben für das Jenseits erkauft haben. Deshalb wird ihre Strafe nicht erleichtert werden, und ihnen wird keine Hilfe zuteil." });
                        surah2.VerseList.Add(new Verse { Number = 87, TextBosnian = "I Mi smo Musau Knjigu dali i, poslije njega, jednog za drugim, poslanike smo slali. I Isau, sinu Merjeminom, jasne smo dokaze dali i Duhom Blagoslovljenim ga pomogli. I kad god vam je koji poslanik donio ono što nije godilo dušama vašim, vi ste se uzoholili, pa ste neke u laž ugonili, a neke ubijali.", TextEnglish = "And We did certainly give Moses the Scripture [i.e., the Torah] and followed up after him with messengers. And We gave Jesus, the son of Mary, clear proofs and supported him with the Pure Spirit [i.e., the angel Gabriel]. But is it [not] that every time a messenger came to you, [O Children of Israel], with what your souls did not desire, you were arrogant? And a party [of messengers] you denied and another party you killed.", TextGerman = "Und Wir gaben bereits Musa die Schrift und ließen nach ihm die Gesandten folgen. Und Wir gaben ’Isa, dem Sohn Maryams, die klaren Beweise und stärkten ihn mit dem Heiligen Geist. War es nicht (so), daß jedesmal, wenn euch (Juden) ein Gesandter etwas überbrachte, was euren Neigungen nicht entsprach, ihr euch hochmütig verhieltet, indem ihr eine Gruppe (der Gesandten) der Lüge bezichtigtet und eine (andere) Gruppe tötetet?" });
                        surah2.VerseList.Add(new Verse { Number = 88, TextBosnian = "Oni govore: 'Naša srca su u omotu; puna su!' A nije tako, nego je Allah njih prokleo zbog nevjerovanja njihova, a malo je njih koji vjeruju.", TextEnglish = "And they said, 'Our hearts are wrapped.'[33] But, [in fact], Allāh has cursed them for their disbelief, so little is it that they believe.", TextGerman = "Und sie sagen: „Unsere Herzen sind verhüllt. Nein! Vielmehr hat Allah sie für ihren Unglauben verflucht. Wie wenig sie glauben!" });
                        surah2.VerseList.Add(new Verse { Number = 89, TextBosnian = "A kada im od Allaha dođe Knjiga koja potvrđuje istinitost onoga što oni imaju - a još otprije su pomoć protiv nevjernika molili - kada im dođe ono što im je poznato, oni to negiraše. Pa neka Allahovo prokletstvo stigne nevjernike!", TextEnglish = "And when there came to them a Book [i.e., the Qur’ān] from Allāh confirming that which was with them - although before they used to pray for victory against those who disbelieved - but [then] when there came to them that which they recognized, they disbelieved in it; so the curse of Allāh will be upon the disbelievers.", TextGerman = "Und als von Allah ein Buch zu ihnen kam, das bestätigend, was ihnen bereits vorlag - und zuvor hatten sie um einen entscheidenden Sieg über diejenigen, die ungläubig waren, angerufen -, als nun das zu ihnen kam, was sie kannten, verleugneten sie es. Darum Allahs Fluch über die Ungläubigen!" });
                        surah2.VerseList.Add(new Verse { Number = 90, TextBosnian = "Jadno je to za što su se prodali: da negiraju ono što Allah objavljuje, zbog zlobe što Allah spušta Svoju darežljivost kome On hoće od Svojih robova; pa su na sebe gnjev za gnjevom navlačili! A nevjernicima pripada patnja ponižavajuća!", TextEnglish = "How wretched is that for which they sold themselves - that they would disbelieve in what Allāh has revealed through [their] outrage that Allāh would send down His favor upon whom He wills from among His servants. So they returned having [earned] wrath upon wrath. And for the disbelievers is a humiliating punishment.", TextGerman = "Wie schlimm ist das, wofür sie ihre Seelen verkauft haben: daß sie das verleugnen, was Allah herabgesandt hat, aus Mißgunst (darüber), daß Allah etwas von Seiner Huld offenbart, wem von Seinen Dienern Er will! So haben sie sich Zorn über Zorn zugezogen. Und für die Ungläubigen gibt es schmachvolle Strafe." });
                        surah2.VerseList.Add(new Verse { Number = 91, TextBosnian = "A kada im se kaže: 'Vjerujte u ono što Allah objavljuje!', oni odgovaraju: 'Mi vjerujemo samo u ono što je nama objavljeno', i neće da vjeruju u ono što se objavljuje poslije, a to je Istina, koja potvrđuje da je istinito i ono što oni imaju. Reci: 'Pa zašto ste još ranije Allahove vjerovjesnike ubijali, ako ste vjernici bili?!'", TextEnglish = "And when it is said to them, 'Believe in what Allāh has revealed,' they say, 'We believe [only] in what was revealed to us.' And they disbelieve in what came after it, while it is the truth confirming that which is with them. Say, 'Then why did you kill the prophets of Allāh before, if you are [indeed] believers?'", TextGerman = "Und wenn man zu ihnen sagt: „Glaubt an das, was Allah (als Offenbarung) herabgesandt hat, sagen sie: „Wir glauben an das, was zu uns (schon zuvor) herabgesandt worden ist, verleugnen aber das, was später (offenbart worden) ist, wo es doch die Wahrheit ist, das zu bestätigen, was ihnen (bereits) vorliegt. Sag: Warum habt ihr denn zuvor Allahs Propheten getötet, wenn ihr gläubig seid?" });
                        surah2.VerseList.Add(new Verse { Number = 92, TextBosnian = "I Musa vam je jasne dokaze bio donio, pa ste, ipak, nakon što on ode, zulum čineći, tele za obožavanje prihvatili.", TextEnglish = "And Moses had certainly brought you clear proofs. Then you took the calf [in worship] after that, while you were wrongdoers.", TextGerman = "Und Musa war doch zu euch gekommen mit den klaren Beweisen. Aber dann nahmt ihr das Kalb an nach ihm, womit ihr Unrecht tatet." });
                        surah2.VerseList.Add(new Verse { Number = 93, TextBosnian = "I kada smo od vas vaš zavjet primili i brdo Tur iznad vas podigli: 'Čvrsto prihvatite ono što vam dajemo i slušajte!', oni su odgovorili: 'Čujemo, ali poslušati nećemo!', jer srca su njihova, zbog nevjerovanja, nadojena teletom bila. Reci: 'Ružno je to na što vas vaše vjerovanje navodi, ako ste vi uopće vjernici.'", TextEnglish = "And [recall] when We took your covenant and raised over you the mount, [saying], 'Take what We have given you with determination and listen.' They said [instead], 'We hear and disobey.' And their hearts absorbed [the worship of] the calf because of their disbelief. Say, 'How wretched is that which your faith enjoins upon you, if you should be believers.'", TextGerman = "Und als Wir mit euch ein Abkommen trafen und den Berg über euch emporhoben (und zu euch sagten): „Haltet fest an dem, was Wir euch gegeben haben und hört. Sie sagten: „Wir hören, doch wir widersetzen uns. Und es geschah ihnen durch ihren Unglauben, daß (die Liebe für) das Kalb in ihre Herzen eindrang. Sag: Wie schlimm ist das, was euch euer Glaube gebietet, wenn ihr gläubig seid!" });
                        surah2.VerseList.Add(new Verse { Number = 94, TextBosnian = "Reci: 'Ako je kod Allaha Džennet osiguran samo za vas, mimo ostalog svijeta, onda vi smrt poželite, ako istinu govorite?!'", TextEnglish = "Say, [O Muḥammad], 'If the home of the Hereafter with Allāh is for you alone and not the [other] people, then wish for death, if you should be truthful.'", TextGerman = "Sag: Wenn die jenseitige Wohnstätte bei Allah euch (Juden) vorbehalten ist unter Ausschluß der (anderen) Menschen, dann wünscht (euch) den Tod, wenn ihr wahrhaftig seid!" });
                        surah2.VerseList.Add(new Verse { Number = 95, TextBosnian = "A oni je neće nikada poželjeti zbog onoga što čine, a Allah dobro zna zulumćare.", TextEnglish = "But never will they wish for it, ever, because of what their hands have put forth. And Allāh is Knowing of the wrongdoers.", TextGerman = "Aber sie werden ihn sich niemals wünschen wegen dessen, was ihre Hände (an Taten) vorausgeschickt haben. Und Allah weiß über die Ungerechten Bescheid." });
                        surah2.VerseList.Add(new Verse { Number = 96, TextBosnian = "I vidjet ćeš sigurno da više žude za životom od drugih ljudi, pa čak i od onih koji Allahu druge u obožavanju pridružuju; svaki od njih volio bi poživjeti hiljadu godina, mada ga to, i kada bi toliko dugo živio, ne bi od kazne udaljilo. A Allah dobro vidi ono što oni rade.", TextEnglish = "And you will surely find them the most greedy of people for life - [even] more than those who associate others with Allāh. One of them wishes that he could be granted life a thousand years, but it would not remove him in the least from the [coming] punishment that he should be granted life. And Allāh is Seeing of what they do.", TextGerman = "Und ganz gewiß wirst du sie als die gierigsten Menschen nach Leben finden, sogar mehr (noch) als diejenigen, die (Allah etwas) beigesellen. (Manch) einer von ihnen möchte gern tausend Jahre am Leben bleiben, aber selbst daß er am Leben bliebe, würde ihn nicht das der Strafe entrücken. Allah sieht wohl, was sie tun." });
                        surah2.VerseList.Add(new Verse { Number = 97, TextBosnian = "Reci: 'Ko je neprijatelj Džibrilu, neka zna da on Allahovom dozvolom tebi na srce stavlja Kur' an potvrdujući istinitost onoga otprije, kao putokaz i radosnu vijest vjernicima.'", TextEnglish = "Say, 'Whoever is an enemy to Gabriel - it is [none but] he who has brought it [i.e., the Qur’ān] down upon your heart, [O Muḥammad], by permission of Allāh, confirming that which was before it and as guidance and good tidings for the believers.'", TextGerman = "Sag: Wer (auch immer) Gibril feind ist, so hat er ihn doch (den Qur’an) mit Allahs Erlaubnis in dein Herz offenbart, das zu bestätigen, was vor ihm (offenbart) war, und als Rechtleitung und frohe Botschaft für die Gläubigen." });
                        surah2.VerseList.Add(new Verse { Number = 98, TextBosnian = "Ko je neprijatelj Allahu, melecima Njegovim i poslanicima Njegovim i Džibrilu i Mikalu' - pa, Allah je, doista, neprijatelj onima koji ne vjeruju!", TextEnglish = "Whoever is an enemy to Allāh and His angels and His messengers and Gabriel and Michael - then indeed, Allāh is an enemy to the disbelievers.", TextGerman = "Wer Allah und Seinen Engeln und Seinen Gesandten und Gibril und Mikal feind ist, so ist Allah den Ungläubigen feind." });
                        surah2.VerseList.Add(new Verse { Number = 99, TextBosnian = "A Mi tebi ajete - jasne dokaze objavljujemo, u koje jedino buntovni neće vjerovati.", TextEnglish = "And We have certainly revealed to you verses [which are] clear proofs, and no one would deny them except the defiantly disobedient.", TextGerman = "Und Wir haben zu dir ja (im Qur’an) klare Zeichen hinabgesandt. Doch nur die Frevler verleugnen sie." });
                        surah2.VerseList.Add(new Verse { Number = 100, TextBosnian = "Zar svaki put kada neku obavezu preuzmu, neka njihova skupina je odbaci?! Štaviše, većina ih ne vjeruje.", TextEnglish = "Is it not [true] that every time they took a covenant a party of them threw it away? But, [in fact], most of them do not believe.", TextGerman = "Ist es nicht (so), daß jedesmal, wenn sie eine Verpflichtung eingingen, sie eine Gruppe von ihnen verworfen hat? - Nein! Vielmehr glauben die meisten von ihnen (doch) nicht." });
                        surah2.VerseList.Add(new Verse { Number = 101, TextBosnian = "A kada im je od Allaha Poslanik došao, potvrđujući da je istinito ono što već imaju, dio onih kojima je Knjiga data za leđa svoja Allahovu Knjigu su odbacili, kao da oni ne znaju.", TextEnglish = "And when a messenger from Allāh came to them confirming that which was with them, a party of those who had been given the Scripture threw the Scripture of Allāh [i.e., the Torah] behind their backs as if they did not know [what it contained].", TextGerman = "Und als (nun) zu ihnen ein Gesandter von Allah gekommen ist, das bestätigend, was ihnen (an Offenbarungen) bereits vorlag, da hat eine Gruppe von denjenigen, denen die Schrift gegeben wurde, das Buch Allahs hinter ihren Rücken geworfen, als ob sie nicht Bescheid wüßten." });
                        surah2.VerseList.Add(new Verse { Number = 102, TextBosnian = "I povedoše se za onim što su šejtani o Sulejmanovoj vladavini lažno kazivali. A Sulejman nije bio nevjernik, nego šejtani, Harut i Marut, nisu vjerovali, podučavajući vradžbini ljude u Babilonu. A to nije objavljeno dvojici meleka. Njih dvojica nikoga nisu učili dok mu ne bi rekli: 'Mi samo iskušavamo, a ti nemoj biti nevjernik!' I ljudi su od njih dvojice učili kako će muža od žene rastaviti, ali oni nisu mogli nikome bez Allahove dozvole nauditi. Učili su ono što će im nauditi i od čega koristi neće imati, iako su znali da onaj ko to sebi u zamjenu za vjerovanje kupi, na ahiretu nikakvog udjela neće imati. A doista je jadno ono za što su se prodali, kada bi samo oni to znali!", TextEnglish = "And they followed [instead] what the devils had recited during the reign of Solomon. It was not Solomon who disbelieved, but the devils disbelieved, teaching people magic and that which was revealed to the two angels at Babylon, Hārūt and Mārūt. But they [i.e., the two angels] do not teach anyone unless they say, 'We are a trial, so do not disbelieve [by practicing magic].'[34] And [yet] they learn from them that by which they cause separation between a man and his wife. But they do not harm anyone through it except by permission of Allāh. And they [i.e., people] learn what harms them and does not benefit them. But they [i.e., the Children of Israel] certainly knew that whoever purchased it [i.e., magic] would not have in the Hereafter any share. And wretched is that for which they sold themselves, if they only knew.", TextGerman = "Und sie folgten dem, was die Teufel unter der Herrschaft Sulaimans (den Menschen) verlasen. Nicht Sulaiman war ungläubig, sondern die Teufel waren es, indem sie die Menschen in der Zauberei unterwiesen und in dem, was auf die (beiden) engel(haften Männer) in Babel, Harut und Marut, herabgesandt worden war. Und sie (beide) unterwiesen niemanden (in der Zauberei), ohne zu sagen: „Wir sind nur eine Versuchung; so werde (darum) nicht ungläubig. Und so lernten sie von ihnen (beiden) das (Zaubermittel), womit man Zwietracht zwischen den Ehegatten stiftet. Doch können sie damit niemandem schaden, außer mit Allahs Erlaubnis. Und sie erlernten, was ihnen schadet und nicht nützt. Und sie wußten doch, daß, wer es erkaufte, am Jenseits wahrlich keinen Anteil hätte. Fürwahr, wie schlimm ist das, wofür sie ihre Seelen verkauft haben, wenn sie (es) nur wüßten!" });
                        surah2.VerseList.Add(new Verse { Number = 103, TextBosnian = "A da su oni vjerovali i čuvali se, pa sigurno nagrada od Allaha bolja je - samo da su to oni znali!", TextEnglish = "And if they had believed and feared Allāh, then the reward from Allāh would have been [far] better, if they only knew.", TextGerman = "Und wenn sie geglaubt hätten und gottesfürchtig gewesen wären, dann wäre eine Belohnung von Allah dafür wahrlich besser (als das, was sie erkauft haben), wenn sie (es) nur wüßten." });
                        surah2.VerseList.Add(new Verse { Number = 104, TextBosnian = "O vi koji vjerujete, ne govorite: 'Čuvaj nas!', nego recite 'Pazi nas!' i slušajte. A nevjernicima bolna patnja pripada.", TextEnglish = "O you who have believed, say not [to Allāh's Messenger], 'Rāʿinā' but say, 'Unẓurnā'[35] and listen. And for the disbelievers is a painful punishment.", TextGerman = "O die ihr glaubt, sagt nicht (zum Propheten): „ra’ina sondern sagt: „unzurna Und hört (auf den Propheten)! Und für die Ungläubigen wird es schmerzhafte Strafe geben." });
                        surah2.VerseList.Add(new Verse { Number = 105, TextBosnian = "Oni kojima je data Knjiga koji ne vjeruju, a ni oni koji Allahu druge u obožavanju pridružuju, ne vole da se vama od Gospodara vašeg bilo kakvo dobro objavi. Allah Svojom milošću odlikuje koga hoće, i Allah je posjednik obilja velikog.", TextEnglish = "Neither those who disbelieve from the People of the Scripture [i.e., the Jews and Christians] nor the polytheists wish that any good should be sent down to you from your Lord. But Allāh selects for His mercy whom He wills, and Allāh is the possessor of great bounty.", TextGerman = "Weder diejenigen unter den Leuten der Schrift, die ungläubig sind, noch die Götzendiener möchten, daß euch etwas Gutes von eurem Herrn offenbart wird. Aber Allah zeichnet mit Seinem Erbarmen aus, wen Er will, denn Allah besitzt große Huld." });
                        surah2.VerseList.Add(new Verse { Number = 106, TextBosnian = "Koji god ajet promijenimo, ili ga zaboravu prepustimo, Mi bolji od njega ili sličan njemu donesemo. Zar ti ne znaš da Allah nad svime ima moć?!", TextEnglish = "We do not abrogate a verse or cause it to be forgotten except that We bring forth [one] better than it or similar to it. Do you not know that Allāh is over all things competent?", TextGerman = "Was Wir an Versen aufheben oder in Vergessenheit geraten lassen - Wir bringen bessere oder gleichwertige dafür. Weißt du denn nicht, daß Allah zu allem die Macht hat?" });
                        surah2.VerseList.Add(new Verse { Number = 107, TextBosnian = "Zar ti ne znaš da Allahu pripada vlast i na nebesima i na Zemlji i da, osim Allaha, ni zaštitnika ni pomagača nemate?!", TextEnglish = "Do you not know that to Allāh belongs the dominion of the heavens and the earth and [that] you have not besides Allāh any protector or any helper?", TextGerman = "Weißt du denn nicht, daß Allah es ist, Der die Herrschaft über die Himmel und die Erde hat, und daß ihr außer Allah weder Schutzherrn noch Helfer habt?" });
                        surah2.VerseList.Add(new Verse { Number = 108, TextBosnian = "Zar da tražite od svog poslanika, kao što su prije od Musaa tražili?! A ko vjerovanje zamijeni nevjerovanjem, taj je s Pravog puta zalutao.", TextEnglish = "Or do you intend to ask[36] your Messenger as Moses was asked before? And whoever exchanges faith for disbelief has certainly strayed from the soundness of the way.", TextGerman = "Oder wollt ihr vielmehr euren Gesandten bitten, so wie zuvor schon Musa gebeten worden war. Wer den Glauben mit dem Unglauben vertauscht, der ist fürwahr vom rechten Weg abgeirrt." });
                        surah2.VerseList.Add(new Verse { Number = 109, TextBosnian = "Mnogi sljedbenici Knjige voljeli bi, iz lične zlobe svoje, da vas, nakon vašeg vjerovanja, vrate u nevjernike, nakon što im je Istina jasnom postala. Ali, vi oprostite i preko toga pređite, dok Allah Svoju odluku ne donese. Allah, doista, nad svime ima moć.", TextEnglish = "Many of the People of the Scripture wish they could turn you back to disbelief after you have believed, out of envy from themselves [even] after the truth has become clear to them. So pardon and overlook until Allāh delivers His command. Indeed, Allāh is over all things competent.", TextGerman = "Viele von den Leuten der Schrift möchten euch, nachdem ihr den Glauben (angenommen) habt, wieder zu Ungläubigen machen, aus Mißgunst von sich selbst aus, nachdem ihnen die Wahrheit klargeworden ist. Doch verzeiht und seid nachsichtig, bis Allah mit Seiner Anordnung kommt! Allah hat zu allem die Macht." });
                        surah2.VerseList.Add(new Verse { Number = 110, TextBosnian = "I namaz obavljajte i zekat dajite, a dobro koje za sebe date, naći ćete kod Allaha. Allah, doista, dobro vidi šta vi radite.", TextEnglish = "And establish prayer and give zakāh, and whatever good you put forward for yourselves - you will find it with Allāh. Indeed Allāh, of what you do, is Seeing.", TextGerman = "Und verrichtet das Gebet und entrichtet die Abgabe. Und was ihr für euch selbst an Gutem vorausschickt, werdet ihr bei Allah finden. Was ihr tut, sieht Allah wohl." });
                        surah2.VerseList.Add(new Verse { Number = 111, TextBosnian = "Oni govore: 'U Džennet neće ući niko osim onoga ko bude židov ili kršćanin!' To su puste želje njihove. Reci: 'Dajte svoj dokaz, ako istinu govorite!'", TextEnglish = "And they say, 'None will enter Paradise except one who is a Jew or a Christian.' That is [merely] their wishful thinking. Say, 'Produce your proof, if you should be truthful.'", TextGerman = "Und sie sagen: „Niemand wird in den (Paradies)garten eingehen außer, wer Jude oder Christ ist. Das sind ihre Wünsche. Sag: „Bringt euren Beweis vor, wenn ihr wahrhaftig seid!" });
                        surah2.VerseList.Add(new Verse { Number = 112, TextBosnian = "A nije tako! Onome ko se bude Allahu predao i usto dobra djela činio, pripada nagrada kod Gospodara njegova; takvi se neće ničega bojati, niti će za bilo čim tugovati.", TextEnglish = "Yes, [on the contrary], whoever submits his face [i.e., self] in Islām to Allāh while being a doer of good will have his reward with his Lord. And no fear will there be concerning them, nor will they grieve.", TextGerman = "Aber nein! Wer sich Allah völlig hingibt und dabei Gutes tut, dessen Lohn steht für ihn bei seinem Herrn. Und sie soll keine Furcht überkommen, noch sollen sie traurig sein." });
                        surah2.VerseList.Add(new Verse { Number = 113, TextBosnian = "Židovi govore: 'Kršćani nisu ni na čemu!',' a kršćani kažu: 'Židovi nisu ni na čemu!', a oni Knjigu čitaju. Tako gotovo istim riječima govore oni koji ne znaju. Allah će im na Kijametskom danu presuditi u onome o čemu su se razilazili.", TextEnglish = "The Jews say, 'The Christians have nothing [true] to stand on,' and the Christians say, 'The Jews have nothing to stand on,' although they [both] recite the Scripture. Thus do those who know not [i.e., the polytheists] speak the same as their words. But Allāh will judge between them on the Day of Resurrection concerning that over which they used to differ.", TextGerman = "Die Juden sagen: „Auf nichts fußen die Christen; und die Christen sagen: „Auf nichts fußen die Juden, obwohl sie doch (beide) die Schrift lesen. Dergleichen Worte führten schon diejenigen, die nicht Bescheid wissen. Aber Allah wird zwischen ihnen am Tag der Auferstehung über das richten, worüber sie uneinig sind." });
                        surah2.VerseList.Add(new Verse { Number = 114, TextBosnian = "Ima li većeg zulumćara od onoga koji brani da se u Allahovim mesdžidima ime Njegovo spominje i koji radi na tome da se oni opustoše?! Takvi bi trebalo da u njih samo sa strahom ulaze. Oni će na ovome svijetu poniženje doživjeti, a na drugom ih čeka kazna velika.", TextEnglish = "And who are more unjust than those who prevent the name of Allāh from being mentioned [i.e., praised] in His mosques and strive toward their destruction. It is not for them to enter them except in fear. For them in this world is disgrace, and they will have in the Hereafter a great punishment.", TextGerman = "Und wer ist ungerechter, als wer verhindert, daß an Allahs Gebetsstätten Sein Name genannt wird, und sich bemüht, sie zu zerstören? Jene sollen sie nur in Furcht betreten. Für sie gibt es im Diesseits Schande und im Jenseits gewaltige Strafe." });
                        surah2.VerseList.Add(new Verse { Number = 115, TextBosnian = "Allahu pripadaju i istok i zapad; kuda god se okrenete, tamo je Allahovo lice. Allah je, doista, neizmjerno darežljiv i Sveznajući.", TextEnglish = "And to Allāh belongs the east and the west. So wherever you [might] turn, there is the Face[37] of Allāh. Indeed, Allāh is all-Encompassing and Knowing.", TextGerman = "Allah gehört der Osten und der Westen; wohin ihr euch auch immer wendet, dort ist Allahs Angesicht. Allah ist Allumfassend und Allwissend." });
                        surah2.VerseList.Add(new Verse { Number = 116, TextBosnian = "Nevjernici govore: 'Allah je sebi dijete uzeo.' Uzvišen neka je On! Naprotiv, Njemu pripada sve što je na nebesima i na Zemlji i Njemu se sve pokorava.", TextEnglish = "They say, 'Allāh has taken a son.' Exalted is He![38] Rather, to Him belongs whatever is in the heavens and the earth. All are devoutly obedient to Him,", TextGerman = "Und sie sagen: „Allah hat Sich Kinder genommen. Preis sei Ihm! Nein! Vielmehr gehört Ihm (alles), was in den Himmeln und auf der Erde ist. Alle sind Ihm demütig ergeben." });
                        surah2.VerseList.Add(new Verse { Number = 117, TextBosnian = "On je Stvoritelj nebesa i Zemlje, a kada nešto odredi, On samo kaže: 'Budi!' - i to biva.", TextEnglish = "Originator of the heavens and the earth. When He decrees a matter, He only says to it, 'Be,' and it is.", TextGerman = "(Er ist) der Schöpfer der Himmel und der Erde in ihrer schönsten Form. Und wenn Er eine Angelegenheit bestimmt, so sagt Er zu ihr nur: „Sei! und so ist sie." });
                        surah2.VerseList.Add(new Verse { Number = 118, TextBosnian = "A oni koji ne znaju, govore: 'Trebalo bi da Allah s nama razgovara, ili da nam kakav dokaz dođe!' Tako su gotovo istim riječima govorili i oni prije njih; srca su im slična! A Mi dokaze objašnjavamo ljudima koji čvrsto vjeruju.", TextEnglish = "Those who do not know say, 'Why does Allāh not speak to us or there come to us a sign?' Thus spoke those before them like their words. Their hearts resemble each other. We have shown clearly the signs to a people who are certain [in faith].", TextGerman = "Und diejenigen, die nicht Bescheid wissen, sagen: „O würde Allah doch zu uns sprechen oder käme zu uns ein Zeichen! Dergleichen Worte führten schon diejenigen, die vor ihnen waren. Ihre Herzen sind einander ähnlich. Wir haben die Zeichen klargemacht für Leute, die überzeugt sind." });
                        surah2.VerseList.Add(new Verse { Number = 119, TextBosnian = "Mi smo te, zaista, poslali s Istinom, kao obveseljitelja i upozoritelja, a ti nećeš biti pitan za one koji će u Džehennemu biti.", TextEnglish = "Indeed, We have sent you, [O Muḥammad], with the truth as a bringer of good tidings and a warner, and you will not be asked about the companions of Hellfire.", TextGerman = "Gewiß, Wir haben dich mit der Wahrheit gesandt als Frohboten und als Warner. Und du wirst nicht nach den (Taten der) Insassen des Höllenbrandes gefragt werden." });
                        surah2.VerseList.Add(new Verse { Number = 120, TextBosnian = "Ni židovi, ni kršćani neće biti tobom zadovoljni sve dok ne budeš slijedio vjeru njihovu. Reci: 'Allahova Uputa jeste Prava uputa!' A ako bi se ti poveo za željama njihovim, nakon znanja koje si dobio, od Allaha te niko ne bi mogao zaštititi, niti pomoći ti.", TextEnglish = "And never will the Jews and the Christians approve of you until you follow their religion. Say, 'Indeed, the guidance of Allāh is the [only] guidance.' If you were to follow their desires after what has come to you of knowledge, you would have against Allāh no protector or helper.", TextGerman = "Weder die Juden noch die Christen werden mit dir zufrieden sein, bis du ihrem Glaubensbekenntnis folgst. Sag: Gewiß, Allahs Rechtleitung ist die (wahre) Rechtleitung. Wenn du jedoch ihren Neigungen folgst nach dem, was dir an Wissen zugekommen ist, so wirst du vor Allah weder Schutzherrn noch Helfer haben." });
                        surah2.VerseList.Add(new Verse { Number = 121, TextBosnian = "Oni kojima smo objavili Knjigu, pa je čitaju onako kako treba, to su oni koji u nju vjeruju. A oni koji u nju ne budu vjerovali, to su sigurni gubitnici.", TextEnglish = "Those to whom We have given the Book recite it with its true recital.[39] They [are the ones who] believe in it. And whoever disbelieves in it - it is they who are the losers.", TextGerman = "Diejenigen, denen Wir die Schrift gegeben haben, lesen sie, wie es ihr zusteht; sie glauben daran. Wer sie jedoch verleugnet, das sind die Verlierer." });
                        surah2.VerseList.Add(new Verse { Number = 122, TextBosnian = "O sinovi Israilovi, sjetite se blagodati Mojih koje sam vam darovao, i da sam vas nad drugim svjetovima bio odlikovao.", TextEnglish = "O Children of Israel, remember My favor which I have bestowed upon you and that I preferred you over the worlds.", TextGerman = "O Kinder Isra’ils, gedenkt Meiner Gunst, die Ich euch erwiesen habe, und daß Ich euch vor den (anderen) Weltenbewohnern bevorzugt habe." });
                        surah2.VerseList.Add(new Verse { Number = 123, TextBosnian = "I bojte se Dana kada niko ni za koga neće moći ništa učiniti, kada se ni od koga otkup neće primiti, kada nikome zagovor neće koristiti i kada im niko neće pomoći.", TextEnglish = "And fear a Day when no soul will suffice for another soul[40] at all, and no compensation will be accepted from it, nor will any intercession benefit it, nor will they be aided.", TextGerman = "Und hütet euch vor einem Tag, an dem keine Seele etwas anstelle einer anderen leisten kann und von ihr keine Ersatz(leistung) angenommen wird, noch Fürsprache ihr nützt; und (an dem) ihnen keine Hilfe zuteil wird." });
                        surah2.VerseList.Add(new Verse { Number = 124, TextBosnian = "I kada je Ibrahima Gospodar njegov s nekoliko riječi na iskušenje stavio, pa ih je on potpuno izvršio, Allah je rekao: 'Učinit ću da ti budeš ljudima vođa.' On je upitao: 'A i neki iz mog potomstva?' Odgovorio mu je: 'Obećanje Moje ne obuhvata zulumćare!'", TextEnglish = "And [mention, O Muḥammad], when Abraham was tried by his Lord with words [i.e., commands] and he fulfilled them. [Allāh] said, 'Indeed, I will make you a leader for the people.' [Abraham] said, 'And of my descendants?' [Allāh] said, 'My covenant does not include the wrongdoers.'", TextGerman = "Und (gedenkt,) als Ibrahim von seinem Herrn mit Worten geprüft wurde, da befolgte er sie. Er (Allah) sagte: „Ich will dich zu einem Vorbild für die Menschen machen. Er (Ibrahim) sagte: „Und von meiner Nachkommenschaft? Er sagte: „Mein Bund erstreckt sich nicht auf die Ungerechten." });
                        surah2.VerseList.Add(new Verse { Number = 125, TextBosnian = "I kada Mi Kabu učinismo utočištem i sigurnim mjestom za ljude: 'Neka vam mjesto na kojem je Ibrahim stajao bude prostor gdje ćete namaz obavljati!' I Ibrahimu i Ismailu stavili smo u obavezu: 'Kabu Moju vas dvojica očistite, za one koji je budu obilazili, za one koji u njoj budu boravili, i za one koji ruku' i sedždu budu činili.'", TextEnglish = "And [mention] when We made the House [i.e., the Kaʿbah] a place of return for the people and [a place of] security. And take, [O believers], from the standing place of Abraham a place of prayer. And We charged Abraham and Ishmael, [saying], 'Purify My House for those who perform ṭawāf[41] and those who are staying [there] for worship and those who bow and prostrate [in prayer].'", TextGerman = "Und als Wir das Haus zu einem Ort der Einkehr für die Menschen und zu einer Stätte der Sicherheit machten und (sagten): „Nehmt Ibrahims Standort als Gebetsplatz! Und Wir verpflichteten Ibrahim und Isma’il: „Reinigt Mein Haus für diejenigen, die den Umlauf vollziehen und die sich (dort) zur Andacht zurückziehen und die sich (vor Allah) verbeugen und niederwerfen." });
                        surah2.VerseList.Add(new Verse { Number = 126, TextBosnian = "A kada je Ibrahim zamolio: 'Gospodaru moj, učini ovo mjesto sigurnim boravištem i snabdij plodovima stanovnike njegove, one između njih koji budu u Allaha i Posljednji dan vjerovali!', On je rekao: 'Onome koji ne bude vjerovao, dat ću da neko vrijeme uživa, a onda ću ga prisiliti da u patnju vatrenu uđe. A užasno je to konačno odredište!'", TextEnglish = "And [mention] when Abraham said, 'My Lord, make this a secure city and provide its people with fruits - whoever of them believes in Allāh and the Last Day.' [Allāh] said, 'And whoever disbelieves - I will grant him enjoyment for a little; then I will force him to the punishment of the Fire, and wretched is the destination.'", TextGerman = "Und (gedenkt,) als Ibrahim sagte: „Mein Herr, mache dies zu einer sicheren Ortschaft und versorge ihre Bewohner mit Früchten, wer von ihnen an Allah und den Jüngsten Tag glaubt! Er (Allah) sagte: „Wer aber ungläubig ist, den lasse Ich ein wenig genießen, hierauf zwinge Ich ihn in die Strafe des (Höllen)feuers - ein schlimmer Ausgang!" });
                        surah2.VerseList.Add(new Verse { Number = 127, TextBosnian = "I dok su Ibrahim i Ismail temelje Kabe podizali, molili su: 'Gospodaru naš, primi to od nas, Ti si, uistinu, Svečujući i Sveznajući.", TextEnglish = "And [mention] when Abraham was raising the foundations of the House and [with him] Ishmael, [saying], 'Our Lord, accept [this] from us. Indeed, You are the Hearing,[42] the Knowing.[43]", TextGerman = "Und (gedenkt,) als Ibrahim die Grundmauern des Hauses errichtete, zusammen mit Isma’il, (da beteten sie): „Unser Herr, nimm (es) von uns an. Du bist ja der Allhörende und Allwissende." });
                        surah2.VerseList.Add(new Verse { Number = 128, TextBosnian = "Gospodaru naš, učini nas dvojicu muslimanima - Tebi predanim, a i u potomstvu našem neka bude zajednica Tebi predana, pokaži nam obrede naše i oprosti nam! Ti si, doista, stalni Primatelj pokajanja, Milostivi!", TextEnglish = "Our Lord, and make us Muslims [in submission] to You and from our descendants a Muslim nation [in submission] to You. And show us our rites [of worship] and accept our repentance. Indeed, You are the Accepting of Repentance, the Merciful.", TextGerman = "Unser Herr, mache uns Dir ergeben und von unserer Nachkommenschaft eine Dir ergebene Gemeinschaft. Und zeige uns unsere Riten, und nimm unsere Reue an. Du bist ja der Reue-Annehmende und Barmherzige." });
                        surah2.VerseList.Add(new Verse { Number = 129, TextBosnian = "Gospodaru naš, pošalji među njih poslanika, jednoga od njih, koji će im ajete Tvoje kazivati, Knjizi i mudrosti ih poučiti i očistiti ih! Ti si, doista, Silni i Mudri!'", TextEnglish = "Our Lord, and send among them a messenger from themselves who will recite to them Your verses and teach them the Book and wisdom and purify them. Indeed, You are the Exalted in Might,[44] the Wise.'", TextGerman = "Unser Herr, schicke zu ihnen einen Gesandten von ihnen, der ihnen Deine Worte verliest und sie das Buch und die Weisheit lehrt und sie läutert. Du bist ja der Allmächtige und Allweise." });
                        surah2.VerseList.Add(new Verse { Number = 130, TextBosnian = "Ko drugi vjeru Ibrahimovu izbjegava do onaj koji ne drži do sebe!? Mi smo njega na ovome svijetu odabrali, pa će on i na drugom svijetu biti među dobrima.", TextEnglish = "And who would be averse to the religion of Abraham except one who makes a fool of himself. And We had chosen him in this world, and indeed he, in the Hereafter, will be among the righteous.", TextGerman = "Wer wird das Glaubensbekenntnis Ibrahims wohl verschmähen außer dem, der selbst betört ist? Wir haben ihn (Ibrahim) ja bereits im Diesseits auserwählt, und im Jenseits gehört er wahrlich zu den Rechtschaffenen." });
                        surah2.VerseList.Add(new Verse { Number = 131, TextBosnian = "Kada je njemu Gospodar njegov rekao: 'Predan budi!', on je odgovorio: 'Ja sam predan Gospodaru svjetova.'", TextEnglish = "When his Lord said to him, 'Submit,' he said, 'I have submitted [in Islām][45] to the Lord of the worlds.'", TextGerman = "(Damals,) als sein Herr zu ihm sagte: „Werde Muslim! Ich habe mich dem Herrn der Weltenbewohner ergeben." });
                        surah2.VerseList.Add(new Verse { Number = 132, TextBosnian = "I Ibrahim je, kao i Jakub, to zapovjedio sinovima svojim: 'Sinovi moji, Allah je vama vjeru odabrao, i nipošto nemojte umrijeti drukčije nego kao muslimani.'", TextEnglish = "And Abraham instructed his sons [to do the same] and [so did] Jacob, [saying], 'O my sons, indeed Allāh has chosen for you this religion, so do not die except while you are Muslims.'", TextGerman = "Und Ibrahim befahl es seinen Söhnen an - (er) und Ya’qub: „O meine Kinder, Allah hat euch die Religion auserwählt; so sterbt denn nicht, außer (Ihm) ergeben zu sein!" });
                        surah2.VerseList.Add(new Verse { Number = 133, TextBosnian = "Da niste možda prisutni bili kada je Jakubu smrtni čas došao?! Kad je on sinove svoje upitao: 'Kome ćete poslije mene ibadet činiti?' Odgovorili su: 'Ibadet ćemo činiti Bogu tvome, Bogu tvojih predaka Ibrahima, Ismaila i Ishaka, Bogu Jedinome! I mi se samo Njemu predajemo!'", TextEnglish = "Or were you witnesses when death approached Jacob, when he said to his sons, 'What will you worship after me?' They said, 'We will worship your God and the God of your fathers, Abraham and Ishmael and Isaac - one God.[46] And we are Muslims [in submission] to Him.'", TextGerman = "Oder wart ihr etwa Zeugen, als Ya’qub der Tod nahte? Als er zu seinen Söhnen sagte: „Wem werdet ihr nach mir dienen? Sie sagten: „Wir werden deinem Gott und dem Gott deiner Vorväter Ibrahim, lsma’il und Ishaq dienen, als dem Einen Gott, und Ihm sind wir ergeben." });
                        surah2.VerseList.Add(new Verse { Number = 134, TextBosnian = "Taj je narod prošao; njemu pripada ono što je zaslužio, a vama što ste zaslužili, i vi nećete biti pitani za ono što su oni radili.", TextEnglish = "That was a nation which has passed on. It will have [the consequence of] what it earned, and you will have what you have earned. And you will not be asked about what they used to do.", TextGerman = "Das ist eine Gemeinschaft, die schon vergangen ist; ihr kommt zu, was sie verdient hat, und euch, was ihr verdient habt. Und ihr werdet nicht danach befragt werden, was jene zu tun pflegten." });
                        surah2.VerseList.Add(new Verse { Number = 135, TextBosnian = "Oni govore: 'Budite židovi, odnosno kršćani, bit ćete na Pravom putu!' Ti reci: 'Ne, mi smo sljedbenici Ibrahima pravovjernika. On nije u višebošce spadao.'", TextEnglish = "They say, 'Be Jews or Christians [so] you will be guided.' Say, 'Rather, [we follow] the religion of Abraham, inclining toward truth, and he was not of the polytheists.'[47]", TextGerman = "Sie sagen: „Werdet Juden oder Christen, so seid ihr rechtgeleitet. Sag: Nein! Vielmehr das Glaubensbekenntnis Ibrahims, Anhängers des rechten Glaubens, und er gehörte nicht zu den Götzendienern." });
                        surah2.VerseList.Add(new Verse { Number = 136, TextBosnian = "Recite: 'Mi vjerujemo u Allaha i u ono što se objavljuje nama, i u ono što je objavljeno Ibrahimu, i Ismailu, i Ishaku, i Jakubu, i potomcima,' i u ono što je dato Musau i Isau, i u ono što je dato vjerovjesnicima od Gospodara njihova; mi ne pravimo nikakve razlike među njima i mi smo Njemu predani.'", TextEnglish = "Say, [O believers], 'We have believed in Allāh and what has been revealed to us and what has been revealed to Abraham and Ishmael and Isaac and Jacob and the Descendants [al-Asbāṭ][48] and what was given to Moses and Jesus and what was given to the prophets from their Lord. We make no distinction between any of them, and we are Muslims [in submission] to Him.'", TextGerman = "Sagt: Wir glauben an Allah und an das, was zu uns (als Offenbarung) herabgesandt worden ist, und an das, was zu Ibrahim, Isma’il, lshaq, Ya`qub und den Stämmen herabgesandt wurde, und (an das,) was Musa und ’Isa gegeben wurde, und (an das,) was den Propheten von ihrem Herrn gegeben wurde. Wir machen keinen Unterschied bei jemandem von ihnen, und wir sind Ihm ergeben." });
                        surah2.VerseList.Add(new Verse { Number = 137, TextBosnian = "Pa, ako oni budu vjerovali u ono u što vi vjerujete, na Pravom su putu; a ako se okrenu, oni su onda raskol počinili. Allah će te sigurno od njih zaštititi. On je Svečujući i Sveznajući.", TextEnglish = "So if they believe in the same as you believe in, then they have been [rightly] guided; but if they turn away, they are only in dissension, and Allāh will be sufficient for you against them. And He is the Hearing, the Knowing.", TextGerman = "Wenn sie an das gleiche glauben, woran ihr glaubt, dann sind sie somit rechtgeleitet. Wenn sie sich jedoch abkehren, dann befinden sie sich in Widerstreit. Aber gegen sie wird Allah dir genügen. Er ist der Allhörende und Allwissende." });
                        surah2.VerseList.Add(new Verse { Number = 138, TextBosnian = "Allahovom vjerom se prožmite!'' A ko od Allaha može bolje vjerom prožeti?! Mi samo Njemu ibadet činimo.'", TextEnglish = "[And say, 'Ours is] the religion of Allāh. And who is better than Allāh in [ordaining] religion? And we are worshippers of Him.'", TextGerman = "Allahs Farbgebung - und wessen Farbgebung ist besser als diejenige Allahs! Und Ihm dienen wir." });
                        surah2.VerseList.Add(new Verse { Number = 139, TextBosnian = "Reci: 'Zar s nama o Allahu raspravljate, a On je i naš i vaš Gospodar! Nama pripadaju naša djela, a vama vaša djela! A mi smo prema Njemu iskreni.'", TextEnglish = "Say, [O Muḥammad], 'Do you argue with us about Allāh while He is our Lord and your Lord? For us are our deeds, and for you are your deeds. And we are sincere [in deed and intention] to Him.'", TextGerman = "Sag: Wollt ihr mit uns über Allah streiten, wo Er doch unser und euer Herr ist? Für uns sind unsere Werke und für euch eure Werke. Und wir sind Ihm aufrichtig zugetan." });
                        surah2.VerseList.Add(new Verse { Number = 140, TextBosnian = "Kako možete govoriti da su Ibrahim, Ismail, Ishak, Jakub i potomci njegovi bili židovi ili kršćani? Reci: 'Znate li bolje vi ili Allah?' Zar je iko veći zulumćar od onoga koji skriva svjedočanstvo Istine dobivene od Allaha! A Allah nije nemaran prema onome što vi radite.", TextEnglish = "Or do you say that Abraham and Ishmael and Isaac and Jacob and the Descendants were Jews or Christians? Say, 'Are you more knowing or is Allāh?' And who is more unjust than one who conceals a testimony[49] he has from Allāh? And Allāh is not unaware of what you do.", TextGerman = "Oder wollt ihr etwa sagen, Ibrahim, Isma’il, lshaq, Ya’qub und die Stämme seien Juden oder Christen gewesen? - Sag: Wißt ihr es besser oder Allah? - Wer ist ungerechter, als wer ein Zeugnis von Allah bei sich verheimlicht? Und Allah ist nicht unachtsam dessen, was ihr tut." });
                        surah2.VerseList.Add(new Verse { Number = 141, TextBosnian = "Taj je narod bio i nestao; njemu pripada ono što je zaslužio, a vama pripada ono što ste zaslužili; vi nećete biti pitani za ono što su oni radili.", TextEnglish = "That is a nation which has passed on. It will have [the consequence of] what it earned, and you will have what you have earned. And you will not be asked about what they used to do.", TextGerman = "Das ist eine Gemeinschaft, die schon vergangen ist; ihr kommt zu, was sie verdient hat und euch, was ihr verdient habt. Und ihr werdet nicht danach befragt werden, was jene zu tun pflegten." });
                        surah2.VerseList.Add(new Verse { Number = 142, TextBosnian = "Reći će neki ljudi kratke pameti: 'Šta ih je odvratilo od kible njihove, prema kojoj su se okretali?' Reci: 'Allahu pripadaju i istok i zapad; On upućuje na Pravi put onoga koga On hoće.'", TextEnglish = "The foolish among the people will say, 'What has turned them away from their qiblah,[50] which they used to face?'[51] Say, 'To Allāh belongs the east and the west. He guides whom He wills to a straight path.'", TextGerman = "Die Toren unter den Menschen werden sagen: „Was hat sie von der Gebetsrichtung, die sie (bisher) einhielten, abgebracht? Sag: Allah gehört der Osten und der Westen. Er leitet, wen Er will, auf einen geraden Weg." });
                        surah2.VerseList.Add(new Verse { Number = 143, TextBosnian = "Tako smo vas učinili najboljom - zajednicom sredine, kako biste svjedočili o drugim ljudima, i kako bi Poslanik o vama svjedočio. I Mi smo promijenili kiblu prema kojoj si se okretao samo da bismo znali one koji slijede Poslanika naspram onih koji ne vjeruju, jer to je doista teško bilo svima, izuzev onim koje je Allah uputio na Pravi put. Allah neće dozvoliti da propadne vjerovanje vaše - a doista je Allah prema ljudima milosrdan i milostiv.", TextEnglish = "And thus We have made you a median [i.e., just] community that you will be witnesses over the people and the Messenger will be a witness over you. And We did not make the qiblah which you used to face except that We might make evident who would follow the Messenger from who would turn back on his heels.[52] And indeed, it is difficult except for those whom Allāh has guided. And never would Allāh have caused you to lose your faith [i.e., your previous prayers]. Indeed Allāh is, to the people, Kind and Merciful.", TextGerman = "Und so haben Wir euch zu einer Gemeinschaft der Mitte gemacht, damit ihr Zeugen über die (anderen) Menschen seiet und damit der Gesandte über euch Zeuge sei. Wir hatten die Gebetsrichtung, die du einhieltest, nur bestimmt, um zu wissen, wer dem Gesandten folgt und wer sich auf den Fersen umkehrt. Und es ist wahrlich schwer außer für diejenigen, die Allah rechtgeleitet hat. Aber Allah läßt nicht zu, daß euer Glaube verloren geht. Allah ist zu den Menschen wahrlich Gnädig, Barmherzig." });
                        surah2.VerseList.Add(new Verse { Number = 144, TextBosnian = "Mi vidimo kako okrećeš lice svoje prema nebu i Mi ćemo te sigurno okrenuti prema kibli koju ti želiš; pa, okreni lice svoje prema Časnoj džamiji! I gdje god bili, lice svoje okrenite prema njoj. Doista, oni kojima je data Knjiga znaju da je to istina od Gospodara njihova. A Allah nije nemaran prema onome šta oni rade.", TextEnglish = "We have certainly seen the turning of your face, [O Muḥammad], toward the heaven, and We will surely turn you to a qiblah with which you will be pleased. So turn your face [i.e., yourself] toward al-Masjid al-Ḥarām.[53] And wherever you [believers] are, turn your faces [i.e., yourselves] toward it [in prayer]. Indeed, those who have been given the Scripture [i.e., the Jews and the Christians] well know that it is the truth from their Lord. And Allāh is not unaware of what they do.", TextGerman = "Wir sehen ja dein Gesicht sich (suchend) zum Himmel wenden. Nun wollen Wir dir ganz gewiß eine Gebetsrichtung zuweisen, mit der du zufrieden bist. So wende dein Gesicht in Richtung der geschützten Gebetsstätte! Und wo immer ihr seid, wendet eure Gesichter in ihrer Richtung! Diejenigen, denen die Schrift gegeben wurde, wissen sehr wohl, daß dies die Wahrheit von ihrem Herrn ist. Und Allah ist nicht unachtsam dessen, was sie tun." });
                        surah2.VerseList.Add(new Verse { Number = 145, TextBosnian = "I kada bi ti onima kojima je data Knjiga sve dokaze donio, oni opet neće prihvatiti tvoju kiblu, kao što ni ti nećeš prihvatiti njihovu kiblu, niti će iko od njih prihvatiti kiblu drugoga. A ako bi se ti za njihovim željama poveo nakon što ti je došlo znanje, tada bi sigurno među zulumćarima bio.", TextEnglish = "And if you brought to those who were given the Scripture every sign, they would not follow your qiblah. Nor will you be a follower of their qiblah. Nor would they be followers of one another's qiblah. So if you were to follow their desires after what has come to you of knowledge, indeed, you would then be among the wrongdoers.", TextGerman = "Selbst wenn du zu denjenigen, denen die Schrift gegeben wurde, mit jeglichen Zeichen kämest, würden sie doch nicht deiner Gebetsrichtung folgen; noch folgst du ihrer Gebetsrichtung. Und auch untereinander folgen sie nicht der Gebetsrichtung der anderen. Würdest du aber ihren Neigungen folgen, nach all dem, was dir an Wissen zugekommen ist, dann gehörtest du wahrlich zu den Ungerechten." });
                        surah2.VerseList.Add(new Verse { Number = 146, TextBosnian = "Oni kojima smo dali Knjigu poznaju njega - Poslanika kao što sinove svoje znaju, ali neki od njih doista svjesno istinu prikrivaju.", TextEnglish = "Those to whom We gave the Scripture know him [i.e., Prophet Muḥammad (ﷺ)] as they know their own sons. But indeed, a party of them conceal the truth while they know [it].", TextGerman = "Diejenigen, denen Wir die Schrift gegeben haben, kennen es, wie sie ihre Söhne kennen. Aber ein Teil von ihnen verheimlicht wahrlich die Wahrheit, obwohl sie (sie) wissen." });
                        surah2.VerseList.Add(new Verse { Number = 147, TextBosnian = "Istina je od Gospodara tvoga, pa zato nikako ne budi od onih koji sumnjaju.", TextEnglish = "The truth is from your Lord, so never be among the doubters.", TextGerman = "(Es ist) die Wahrheit von deinem Herrn, gehöre daher nicht zu den Zweiflern!" });
                        surah2.VerseList.Add(new Verse { Number = 148, TextBosnian = "Svako ima svoj pravac prema kojem se okreće, pa se natječite u dobru! Ma gdje bili, Allah će vas sve sabrati. Allah, doista, nad svime ima moć!", TextEnglish = "For each [religious following] is a [prayer] direction toward which it faces. So race to [all that is] good. Wherever you may be, Allāh will bring you forth [for judgement] all together. Indeed, Allāh is over all things competent.", TextGerman = "Jeder hat eine Zielrichtung, zu der er sich hinwendet. So wetteifert nach den guten Dingen! Wo immer ihr auch sein werdet, Allah wird euch alle herbeibringen. Allah hat zu allem die Macht." });
                        surah2.VerseList.Add(new Verse { Number = 149, TextBosnian = "I iz svakoga mjesta u koje dođeš, ti lice svoje u pravcu Časne džamije okreni; to je, doista, Istina od Gospodara tvoga; Allah nije nemaran prema onome što vi radite.", TextEnglish = "So from wherever you go out [for prayer, O Muḥammad], turn your face toward al-Masjid al-Ḥarām, and indeed, it is the truth from your Lord. And Allāh is not unaware of what you do.", TextGerman = "Und woher du immer heraustrittst, da wende dein Gesicht in Richtung der geschützten Gebetsstätte. Es ist wirklich die Wahrheit von deinem Herrn. Und Allah ist nicht unachtsam dessen, was ihr tut." });
                        surah2.VerseList.Add(new Verse { Number = 150, TextBosnian = "I iz svakog mjesta u koje dođeš, ti lice svoje prema Časnoj džamiji okreni!; i gdje god se nalazili, vi lica svoja prema njoj okrećite, da vam ljudi ne bi imali šta prigovoriti, izuzev onih između njih koji zulum čine. Njih se ne bojte, nego se Mene bojte, da bih blagodat Svoju prema vama upotpunio i da biste na Pravom putu bili.", TextEnglish = "And from wherever you go out [for prayer], turn your face toward al-Masjid al-Ḥarām. And wherever you [believers] may be, turn your faces toward it in order that the people will not have any argument against you, except for those of them who commit wrong; so fear them not but fear Me. And [it is] so I may complete My favor upon you and that you may be guided,", TextGerman = "Und woher du immer heraustrittst, da wende dein Gesicht in Richtung der geschützten Gebetsstätte. Und wo immer ihr seid, da wendet eure Gesichter in ihrer Richtung, damit die Menschen kein Beweismittel gegen euch haben, außer denjenigen von ihnen, die Unrecht tun. - So fürchtet nicht sie, sondern fürchtet Mich! - Und damit Ich Meine Gunst an euch vollende, auf daß ihr rechtgeleitet werden möget." });
                        surah2.VerseList.Add(new Verse { Number = 151, TextBosnian = "Kao što vam Mi jednoga od vas kao poslanika poslasmo da vam ajete Naše kazuje, da vas očisti, da vas Knjizi i mudrosti pouči, i da vas ono što niste znali nauči", TextEnglish = "Just as We have sent among you a messenger from yourselves reciting to you Our verses and purifying you and teaching you the Book and wisdom[54] and teaching you that which you did not know.", TextGerman = "So, wie Wir zu euch einen Gesandten von euch geschickt haben, der euch Unsere Worte verliest und euch läutert und euch die Schrift und die Wahrheit lehrt und euch lehrt, was ihr nicht wußtet." });
                        surah2.VerseList.Add(new Verse { Number = 152, TextBosnian = "pa, sjećajte se vi Mene, i Ja ću se vas sjećati! I zahvaljujte Mi, a nemojte Mi nezahvalni biti!", TextEnglish = "So remember Me; I will remember you. And be grateful to Me and do not deny Me.", TextGerman = "Gedenkt Meiner, so gedenke Ich eurer. Seid Mir dankbar und seid nicht undankbar gegen Mich." });
                        surah2.VerseList.Add(new Verse { Number = 153, TextBosnian = "0 vi koji vjerujete, tražite sebi pomoći u strpljivosti i namazu! Allah je, doista, uz strpljive.", TextEnglish = "O you who have believed, seek help through patience and prayer. Indeed, Allāh is with the patient.", TextGerman = "O die ihr glaubt, sucht Hilfe in der Standhaftigkeit und im Gebet! Allah ist mit den Standhaften." });
                        surah2.VerseList.Add(new Verse { Number = 154, TextBosnian = "I ne kažite za one koji su na Allahovom putu poginuli: 'Mrtvi su!' Ne, oni su živi, ali vi to ne opažate.", TextEnglish = "And do not say about those who are killed in the way of Allāh, 'They are dead.' Rather, they are alive, but you perceive [it] not.", TextGerman = "Und sagt nicht von denen, die auf Allahs Weg getötet werden, sie seien tot! Nein! Vielmehr sind sie lebendig; aber ihr nehmt es nicht wahr." });
                        surah2.VerseList.Add(new Verse { Number = 155, TextBosnian = "Mi ćemo vas u iskušenje dovoditi, malo strahom i gladovanjem, te gubljenjem imetka, života i ljetine. A ti obraduj strpljive,", TextEnglish = "And We will surely test you with something of fear and hunger and a loss of wealth and lives and fruits, but give good tidings to the patient,", TextGerman = "Und Wir werden euch ganz gewiß mit ein wenig Furcht und Hunger und Mangel an Besitz, Seelen und Früchten prüfen. Doch verkünde frohe Botschaft den Standhaften," });
                        surah2.VerseList.Add(new Verse { Number = 156, TextBosnian = "one koji, kada ih kakva nevolja zadesi, samo kažu: 'Mi smo, doista, Allahovi i Njemu ćemo se, sigurno, vratiti.'", TextEnglish = "Who, when disaster strikes them, say, 'Indeed we belong to Allāh, and indeed to Him we will return.'", TextGerman = "die, wenn sie ein Unglück trifft, sagen: „Wir gehören Allah, und zu Ihm kehren wir zurück." });
                        surah2.VerseList.Add(new Verse { Number = 157, TextBosnian = "To su oni kojima pripadaju blagoslovi od Gospodara njihova i milost; oni su na Pravom putu.", TextEnglish = "Those are the ones upon whom are blessings from their Lord and mercy. And it is those who are the [rightly] guided.", TextGerman = "Sie sind es, denen Segnungen von ihrem Herrn und Erbarmen zuteil werden, und sie sind die Rechtgeleiteten." });
                        surah2.VerseList.Add(new Verse { Number = 158, TextBosnian = "Safa i Merva su, doista, Allahova obredna mjesta. Zato onaj ko Kabu hodočasti ili umru obavi, ne čini nikakav prestup da se kreće između njih. A onaj koji drage volje učini kakvo dobro djelo, pa Allah je, doista, zahvalan i sve zna.", TextEnglish = "Indeed, aṣ-Ṣafā and al-Marwah are among the symbols[55] of Allāh. So whoever makes ḥajj [pilgrimage] to the House or performs ʿumrah - there is no blame upon him for walking between them.[56] And whoever volunteers good - then indeed, Allāh is Appreciative[57] and Knowing.", TextGerman = "Gewiß, as-Safa und al-Marwa gehören zu den (Orten der) Kulthandlungen Allahs. Wenn einer die Pilgerfahrt zum Hause oder die Besuchsfahrt vollzieht, so ist es keine Sünde für ihn, wenn er zwischen ihnen (beiden) den Umgang macht. Und wer (von sich aus) freiwillig Gutes tut, so ist Allah Dankbar und Allwissend." });
                        surah2.VerseList.Add(new Verse { Number = 159, TextBosnian = "One koji budu tajili jasne dokaze koje smo Mi objavili, i Pravi put nakon što smo ga u Knjizi ljudima označili, Allah će prokleti, a proklet će ih i oni koji proklinju.", TextEnglish = "Indeed, those who conceal what We sent down of clear proofs and guidance after We made it clear for the people in the Scripture - those are cursed by Allāh and cursed by those who curse,[58]", TextGerman = "Diejenigen, die verheimlichen, was Wir an klaren Beweisen und Rechtleitung hinabgesandt haben, nachdem Wir es den Menschen in der Schrift klar gemacht haben, sie werden von Allah verflucht und auch von den Fluchenden, -" });
                        surah2.VerseList.Add(new Verse { Number = 160, TextBosnian = "Izuzeti će biti samo oni koji se pokaju i poprave, i to javno ispolje; takvima ću Ja oprostiti, a Ja sam stalni Primatelj pokajanja, Milostivi.", TextEnglish = "Except for those who repent and correct themselves and make evident [what they concealed]. Those - I will accept their repentance, and I am the Accepting of Repentance,[59] the Merciful.", TextGerman = "außer denjenigen, die bereuen und verbessern und klar machen. Ihre Reue nehme Ich an, Ich bin ja der Reue-Annehmende und Barmherzige." });
                        surah2.VerseList.Add(new Verse { Number = 161, TextBosnian = "Doista, onima koji ne vjeruju i koji kao nevjernici umru, slijedi prokletstvo Allahovo, prokletstvo meleka i svih ljudi.", TextEnglish = "Indeed, those who disbelieve and die while they are disbelievers - upon them will be the curse of Allāh and of the angels and the people, all together,", TextGerman = "Diejenigen aber, die ungläubig sind und als Ungläubige sterben, auf ihnen liegt der Fluch Allahs und der Engel und der Menschen allesamt," });
                        surah2.VerseList.Add(new Verse { Number = 162, TextBosnian = "U njemu će vječno ostati, a patnja njihova neće se smanjivati, niti će im se šta odlagati.", TextEnglish = "Abiding eternally therein. The punishment will not be lightened for them, nor will they be reprieved.", TextGerman = "ewig darin zu bleiben. Die Strafe soll ihnen nicht erleichtert, noch soll ihnen Aufschub gewährt werden," });
                        surah2.VerseList.Add(new Verse { Number = 163, TextBosnian = "A vaš Bog- jedan je Bog; nema boga osim Njega, Svemilosnog, Milostivog.", TextEnglish = "And your god is one God. There is no deity [worthy of worship] except Him, the Entirely Merciful, the Especially Merciful.", TextGerman = "Euer Gott ist ein Einziger Gott. Es gibt keinen Gott außer Ihm, dem Allerbarmer, dem Barmherzigen." });
                        surah2.VerseList.Add(new Verse { Number = 164, TextBosnian = "U stvaranju nebesa i Zemlje, u smjeni noći i dana, u lađi koja morem plovi s korisnim tovarom za ljude i u vodi koju Allah pušta s neba, pa tako u život vraća zemlju nakon zamrlosti njene, po kojoj je rasijao svakojaka živa bića, u usmjeravanju vjetrova i oblacima koji između neba i Zemlje lebde - ima, doista, znakova za one koji razumiju.", TextEnglish = "Indeed, in the creation of the heavens and the earth, and the alternation of the night and the day, and the [great] ships which sail through the sea with that which benefits people, and what Allāh has sent down from the heavens of rain, giving life thereby to the earth after its lifelessness and dispersing therein every [kind of] moving creature, and [His] directing of the winds and the clouds controlled between the heaven and earth are signs for a people who use reason.", TextGerman = "In der Schöpfung der Himmel und der Erde; im Unterschied von Nacht und Tag; in den Schiffen, die das Meer befahren mit dem, was den Menschen nützt; darin, daß Allah Wasser vom Himmel herabkommen läßt, und damit dann die Erde nach ihrem Tod wieder lebendig macht und auf ihr allerlei Tiere sich ausbreiten läßt; und im Wechsel der Winde und der Wolken, die zwischen Himmel und Erde dienstbar gemacht sind, sind wahrlich Zeichen für Leute, die begreifen." });
                        surah2.VerseList.Add(new Verse { Number = 165, TextBosnian = "Ima ljudi koji pored Allaha druge, kao takmace Njemu, prihvaćaju; vole ih kao što se Allah voli! Ali, oni koji vjeruju, još više Allaha vole! A da znaju oni koji su zulum činili - kada kaznu dožive, da će vidjeti da moć sva Allahu pripada i da Allah strahovito kažnjava.", TextEnglish = "And [yet], among the people are those who take other than Allāh as equals [to Him]. They love them as they [should] love Allāh. But those who believe are stronger in love for Allāh. And if only they who have wronged would consider [that] when they see the punishment, [they will be certain] that all power belongs to Allāh and that Allāh is severe in punishment.", TextGerman = "Und doch gibt es unter den Menschen manche, die außer Allah andere als Seinesgleichen annehmen und ihnen die selbe Liebe schenken wie Allah. Aber die Gläubigen sind stärker in ihrer Liebe zu Allah. Und wenn doch diejenigen, die Unrecht tun, wenn sie der Strafe angesichtig werden, erkennen würden, daß alle Stärke Allah gehört und daß Allah streng im Strafen ist." });
                        surah2.VerseList.Add(new Verse { Number = 166, TextBosnian = "Kada se oni za kojim su se drugi povodili odreknu onih koji su ih slijedili i kaznu budu iskusili, i kada im se svi putevi spasa pokidaju,", TextEnglish = "[And they should consider that] when those who have been followed disassociate themselves from those who followed [them], and they [all] see the punishment, and cut off from them are the ties [of relationship],", TextGerman = "Wenn diejenigen, denen Gefolgschaft geleistet worden ist, sich von denen lossagen, die Gefolgschaft geleistet haben, und sie die Strafe sehen und die Verbindungsstricke zu ihnen abgeschnitten sind!" });
                        surah2.VerseList.Add(new Verse { Number = 167, TextBosnian = "tada će sljedbenici njihovi reći: 'Da nam je samo da se za trenutak vratimo, pa da se i mi njih odreknemo kao što su se oni nas odrekli!' Eto, tako će Allah njima pokazati pogubnim djela njihova i oni iz Vatre neće izlaziti.", TextEnglish = "Those who followed will say, 'If only we had another turn [at worldly life] so we could disassociate ourselves from them as they have disassociated themselves from us.' Thus will Allāh show them their deeds as regrets upon them. And they are never to emerge from the Fire.", TextGerman = "Und diejenigen, die Gefolgschaft geleistet haben, sagen: „Hätten wir doch eine (Möglichkeit zur) Wiederholung, dann würden wir uns von ihnen lossagen, so wie sie sich von uns losgesagt haben. So wird Allah ihnen ihre Werke zeigen, als gramvolle Reue für sie. Und sie werden aus dem (Höllen)feuer nicht herauskommen." });
                        surah2.VerseList.Add(new Verse { Number = 168, TextBosnian = "O ljudi, jedite od onoga što ima na Zemlji, što je dozvoljeno i lijepo, i ne slijedite šejtanove stope! On vam je, zaista, neprijatelj očevidni.", TextEnglish = "O mankind, eat from whatever is on earth [that is] lawful and good and do not follow the footsteps of Satan. Indeed, he is to you a clear enemy.", TextGerman = "O ihr Menschen! Eßt von dem, was es auf der Erde gibt, als etwas Erlaubtem und Gutem, und folgt nicht den Fußstapfen des Satans! Er ist euch ein deutlicher Feind." });
                        surah2.VerseList.Add(new Verse { Number = 169, TextBosnian = "On vas navraća na grijeh i razvrat, i da o Allahu govorite ono što ne znate.", TextEnglish = "He only orders you to evil and immorality and to say about Allāh what you do not know.", TextGerman = "Er befiehlt euch nur Böses und Schändliches, und daß ihr gegen Allah aussagen sollt, was ihr nicht wißt." });
                        surah2.VerseList.Add(new Verse { Number = 170, TextBosnian = "A kada im se kaže: 'Slijedite ono što je Allah objavio!' - oni odgovaraju: 'Ne, nego ćemo slijediti ono na čemu smo naše pretke zatekli.' Zar i onda kada im preci nisu ništa shvaćali, niti su na Pravom putu bili?!", TextEnglish = "And when it is said to them, 'Follow what Allāh has revealed,' they say, 'Rather, we will follow that which we found our fathers doing.' Even though their fathers understood nothing, nor were they guided?", TextGerman = "Und wenn man zu ihnen sagt: Folgt dem, was Allah herabgesandt hat, sagen sie: Nein! Vielmehr folgen wir dem, worin wir unsere Väter vorgefunden haben. Was denn, auch wenn ihre Väter nichts begriffen und nicht rechtgeleitet waren?" });
                        surah2.VerseList.Add(new Verse { Number = 171, TextBosnian = "Oni koji ne vjeruju slični su onome koji viče, ali sam ne čuje ništa osim zov i zapomaganje; oni su gluhi, nijemi i slijepi i oni ništa ne shvaćaju.", TextEnglish = "The example of those who disbelieve is like that of one who shouts at what hears nothing but calls and cries [i.e., cattle or sheep] - deaf, dumb and blind, so they do not understand.", TextGerman = "Das Gleichnis derjenigen, die ungläubig sind, ist wie jemand, der etwas nachschreit, was nichts hört außer Stimmen und Zurufen. Taub, stumm, blind; so begreifen sie nicht." });
                        surah2.VerseList.Add(new Verse { Number = 172, TextBosnian = "O vi koji vjerujete, jedite dobra kojim smo vas opskrbili i Allahu zahvaljujte, ako, doista, samo Njemu ibadet činite.", TextEnglish = "O you who have believed, eat from the good [i.e., lawful] things which We have provided for you and be grateful to Allāh if it is [indeed] Him that you worship.", TextGerman = "O die ihr glaubt, eßt von den guten Dingen, mit denen Wir euch versorgt haben, und seid Allah dankbar, wenn ihr Ihm (allein) dient!" });
                        surah2.VerseList.Add(new Verse { Number = 173, TextBosnian = "On vam, doista, zabranjuje: strv, krv, svinjsko meso i ono što je prineseno kao žrtva u nečije drugo ime, a ne u Allahovo. A onome koji bude primoran, ne svojom voljom i ne pretjerujući, nije mu grijeh. Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "He has only forbidden to you dead animals,[60] blood, the flesh of swine, and that which has been dedicated to other than Allāh. But whoever is forced [by necessity], neither desiring [it] nor transgressing [its limit], there is no sin upon him. Indeed, Allāh is Forgiving and Merciful.", TextGerman = "Verboten hat Er euch nur (den Genuß von) Verendetem, Blut, Schweinefleisch und dem, worüber ein anderer (Name) als Allah(s) angerufen worden ist. Wer sich aber in einer Zwangslage befindet, ohne zu begehren oder das Maß zu überschreiten, für den ist es keine Sünde. Allah ist Allvergebend und Barmherzig." });
                        surah2.VerseList.Add(new Verse { Number = 174, TextBosnian = "Doista, oni koji taje ono što je Allah u Knjizi objavio i to prodaju za bagatelnu cijenu - oni u trbuhe svoje samo vatru trpaju; na Kijametskom danu Allah ih neće ni osloviti, niti ih očistiti; njima pripada patnja bolna.", TextEnglish = "Indeed, they who conceal what Allāh has sent down of the Book and exchange it for a small price - those consume not into their bellies except the Fire. And Allāh will not speak to them on the Day of Resurrection, nor will He purify them. And they will have a painful punishment.", TextGerman = "Diejenigen, die verheimlichen, was Allah von der Schrift herabgesandt hat, und es für einen geringen Preis verkaufen, sie verzehren in ihren Bäuchen nichts als Feuer. Und Allah wird zu ihnen am Tag der Auferstehung weder sprechen noch sie läutern. Für sie wird es schmerzhafte Strafe geben." });
                        surah2.VerseList.Add(new Verse { Number = 175, TextBosnian = "To su oni koji umjesto Upute uzimaju zabludu, a umjesto oprosta patnju; kako li samo oni mogu da trpe Vatru?!", TextEnglish = "Those are the ones who have exchanged guidance for error and forgiveness for punishment. How patient they are for [i.e., in pursuit of] the Fire!", TextGerman = "Das sind diejenigen, die das Irregehen für die Rechtleitung erkauft haben und die Strafe für die Vergebung. Wie beharrlich sind sie gegenüber dem (Höllen)feuer!" });
                        surah2.VerseList.Add(new Verse { Number = 176, TextBosnian = "To je tako, jer Allah je objavio Knjigu s istinom, a oni koji se o Knjizi razilaze doista su u raskolu velikom.", TextEnglish = "That is [deserved by them] because Allāh has sent down the Book in truth. And indeed, those who differ over the Book are in extreme dissension.", TextGerman = "Dies, weil Allah die Schrift mit der Wahrheit offenbart hat. Und diejenigen, die über die Schrift uneinig sind, befinden sich wahrlich in tiefem Widerstreit." });
                        surah2.VerseList.Add(new Verse { Number = 177, TextBosnian = "Nije dobročinstvo u tome da okrećete lica svoja prema istoku i zapadu, nego je pravo dobročinstvo dobročinstvo onih koji vjeruju u Allaha, u ahiret, i u meleke, i u knjige, i u vjerovjesnike, te onih koji od imetka, iako im je drag, daju rođacima i siročadi, siromasima i putnicima namjernicima, prosjacima, i za otkup iz ropstva, i onih koji namaz obavljaju i zekat daju, i koji obavezu svoju, kada je preuzmu, ispunjavaju, te onih koji su strpljivi u neimaštini i u bolesti, i za vrijeme borbe. To su oni koji su iskreni i oni su pravi bogobojaznici.", TextEnglish = "Righteousness is not that you turn your faces toward the east or the west, but [true] righteousness is [in] one who believes in Allāh, the Last Day, the angels, the Book, and the prophets and gives wealth, in spite of love for it, to relatives, orphans, the needy, the traveler, those who ask [for help], and for freeing slaves; [and who] establishes prayer and gives zakāh; [those who] fulfill their promise when they promise; and [those who] are patient in poverty and hardship and during battle. Those are the ones who have been true, and it is those who are the righteous.", TextGerman = "Nicht darin besteht die Güte, daß ihr eure Gesichter gegen Osten oder Westen wendet. Güte ist vielmehr, daß man an Allah, den Jüngsten Tag, die Engel, die Bücher und die Propheten glaubt und vom Besitz - obwohl man ihn liebt - der Verwandtschaft, den Waisen, den Armen, dem Sohn des Weges, den Bettlern und für (den Loskauf von) Sklaven hergibt, das Gebet verrichtet und die Abgabe entrichtet; und diejenigen, die ihre Verpflichtung einhalten, wenn sie eine eingegangen sind, und diejenigen, die standhaft bleiben in Not, Leid und in Kriegszeiten, das sind diejenigen, die wahrhaftig sind, und das sind die Gottesfürchtigen." });
                        surah2.VerseList.Add(new Verse { Number = 178, TextBosnian = "O vi koji vjerujete! Propisuje vam se odmazda za ubijene: slobodan za slobodnog, rob za roba, i žensko za žensko. A prema onome kome od njegovog brata bude nešto oprošteno, neka onaj ko prihvati otkup postupi pravedno, a i on neka njemu isplati lijepo i po propisu. To je olakšanje od Gospodara vašeg i milost. A ko nasilje i poslije toga izvrši, njemu slijedi bolna patnja.", TextEnglish = "O you who have believed, prescribed for you is legal retribution for those murdered - the free for the free, the slave for the slave, and the female for the female.[61] But whoever overlooks from his brother [i.e., the killer] anything,[62] then there should be a suitable follow-up and payment to him [i.e., the deceased's heir or legal representative] with good conduct. This is an alleviation from your Lord and a mercy. But whoever transgresses after that[63] will have a painful punishment.", TextGerman = "O die ihr glaubt, vorgeschrieben ist euch Wiedervergeltung für die Getöteten: der Freie für den Freien, der Sklave für den Sklaven und das Weib für das Weib. Doch wenn einem von seinem Bruder etwas erlassen wird, so soll die Verfolgung (der Ansprüche) in rechtlicher Weise und die Zahlungsleistung an ihn auf ordentliche Weise geschehen. Das ist eine Erleichterung von eurem Herrn und Erbarmung. Wer aber nach diesem eine Übertretung begeht, für den gibt es schmerzhafte Strafe." });
                        surah2.VerseList.Add(new Verse { Number = 179, TextBosnian = "U odmazdi vam je život, o razumom obdareni, da biste se sačuvali.", TextEnglish = "And there is for you in legal retribution [saving of] life, O you [people] of understanding, that you may become righteous.[64]", TextGerman = "In der Wiedervergeltung liegt Leben für euch, o die ihr Verstand besitzt, auf daß ihr gottesfürchtig werden möget!" });
                        surah2.VerseList.Add(new Verse { Number = 180, TextBosnian = "Kada neko od vas na samrti bude, pa ostavlja kakvo dobro, propisano vam je da, na lijep i pravedan način, roditeljima i bližnjima oporuku učinite, što je obaveza bogobojaznih.", TextEnglish = "Prescribed for you when death approaches [any] one of you if he leaves wealth [is that he should make] a bequest for the parents and near relatives according to what is acceptable - a duty upon the righteous.[65]", TextGerman = "Vorgeschrieben ist euch, wenn sich einem von euch der Tod naht, sofern er Gut hinterläßt, ein Vermächtnis zugunsten der Eltern und nächsten Verwandten in rechtlicher Weise zu treffen, als eine Pflicht für die Gottesfürchtigen." });
                        surah2.VerseList.Add(new Verse { Number = 181, TextBosnian = "Ko god to zamijeni nakon što je čuo kako glasi, grijeh za to pada na one koji to mijenjaju. A Allah je, doista, Onaj Koji sve čuje i Onaj Koji sve zna.", TextEnglish = "Then whoever alters it [i.e., the bequest] after he has heard it - the sin is only upon those who have altered it. Indeed, Allāh is Hearing and Knowing.", TextGerman = "Wer es aber abändert - nachdem er es gehört hat-, so liegt dessen Sünde nur bei demjenigen, der es abändert. Allah ist Allhörend und Allwissend." });
                        surah2.VerseList.Add(new Verse { Number = 182, TextBosnian = "Pa ko se boji pogreške ili griješnog postupka oporučioca, pa to valjano između onih kojih se oporuka tiče popravi, nije zgriješio.! Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "But if one fears from the bequeather [some] error or sin and corrects that which is between them [i.e., the concerned parties], there is no sin upon him. Indeed, Allāh is Forgiving and Merciful.", TextGerman = "Wer aber von Seiten eines Erblassers Abweichung oder Sünde befürchtet und zwischen ihnen schlichtet, den trifft keine Sünde. Allah ist Allvergebend und Barmherzig." });
                        surah2.VerseList.Add(new Verse { Number = 183, TextBosnian = "O vi koji vjerujete! Propisuje vam se post, kao što je propisan onima prije vas, da biste se uščuvali,", TextEnglish = "O you who have believed, decreed upon you is fasting as it was decreed upon those before you that you may become righteous -", TextGerman = "O die ihr glaubt, vorgeschrieben ist euch das Fasten, so wie es denjenigen vor euch vorgeschrieben war, auf daß ihr gottesfürchtig werden möget." });
                        surah2.VerseList.Add(new Verse { Number = 184, TextBosnian = "određen broj dana; a onome od vas koji je bolestan ili na putu bude, toliko drugih dana. Onima koji ga teško podnose, otkup je da siromaha nahrane. A ko drage volje da više, za njega je bolje. A bolje vam je da postite, da znate.", TextEnglish = "[Fasting for] a limited number of days. So whoever among you is ill or on a journey [during them] - then an equal number of other days [are to be made up]. And upon those who are able [to fast, but with hardship] - a ransom [as substitute] of feeding a poor person [each day]. And whoever volunteers good [i.e., excess] - it is better for him. But to fast is best for you, if you only knew.", TextGerman = "(Vorgeschrieben ist es euch) an bestimmten Tagen. Wer von euch jedoch krank ist oder sich auf einer Reise befindet, der soll eine (gleiche) Anzahl von anderen Tagen (fasten). Und denjenigen, die es zu leisten vermögen, ist als Ersatz die Speisung eines Armen auferlegt. Wer aber freiwillig Gutes tut, für den ist es besser. Und daß ihr fastet, ist besser für euch, wenn ihr (es) nur wißt!" });
                        surah2.VerseList.Add(new Verse { Number = 185, TextBosnian = "U ramazanu je počelo objavljivanje Kur'ana, koji je putokaz ljudima, jasan dokaz Pravog puta i razlikovanja dobra od zla. Ko od vas u tom mjesecu bude kod kuće, neka posti, a ko bolestan bude ili se na putu zadesi, neka isti broj dana naposti. Allah hoće da vam olakša, a ne da teškoće imate, da određeni broj dana ispunite i da Allaha veličate jer vas je uputio, te da zahvalni budete.", TextEnglish = "The month of Ramaḍān [is that] in which was revealed the Qur’ān, a guidance for the people and clear proofs of guidance and criterion. So whoever sights [the crescent of] the month,[66] let him fast it; and whoever is ill or on a journey - then an equal number of other days. Allāh intends for you ease and does not intend for you hardship and [wants] for you to complete the period and to glorify Allāh for that [to] which He has guided you; and perhaps you will be grateful.", TextGerman = "Der Monat Ramadan (ist es), in dem der Qur’an als Rechtleitung für die Menschen herabgesandt worden ist und als klare Beweise der Rechtleitung und der Unterscheidung. Wer also von euch während dieses Monats anwesend ist, der soll ihn fasten, wer jedoch krank ist oder sich auf einer Reise befindet, eine (gleiche) Anzahl von anderen Tagen (fasten). Allah will für euch Erleichterung; Er will für euch nicht Erschwernis, - damit ihr die Anzahl vollendet und Allah als den Größten preist, dafür, daß Er euch rechtgeleitet hat, auf daß ihr dankbar sein möget." });
                        surah2.VerseList.Add(new Verse { Number = 186, TextBosnian = "A kada te robovi Moji za Mene upitaju, Ja sam, sigurno, blizu: odazivam se molbi molitelja kada Me zamoli. Zato, neka se i oni Meni odazovu i neka vjeruju u Mene, kako bi ispravnim putem krenuli.", TextEnglish = "And when My servants ask you, [O Muḥammad], concerning Me - indeed I am near. I respond to the invocation of the supplicant when he calls upon Me. So let them respond to Me [by obedience] and believe in Me that they may be [rightly] guided.", TextGerman = "Und wenn dich Meine Diener nach Mir fragen, so bin Ich nahe; Ich erhöre den Ruf des Bittenden, wenn er Mich anruft. So sollen sie nun auf Mich hören und an Mich glauben, auf daß sie besonnen handeln mögen." });
                        surah2.VerseList.Add(new Verse { Number = 187, TextBosnian = "Dozvoljava vam se da u noćima posta imate odnos sa ženama vašim; one su odjeća vaša, a vi ste odjeća njihova; Allah zna da ste sami sebe varali, pa je prihvatio pokajanje vaše i oprostio vam; zato se sada sastajte s njima i žudite za onim što vam je Allah odredio; jedite i pijte sve dok budete mogli razlikovati bijelu nit od crne niti zore, a potom upotpunite post, do noći; sa ženama ne općite dok ste u itikafu u džamijama; to su Allahove granice i ne približujte im se; tako Allah u ajetima Svojim obznanjuje propise ljudima, kako bi se uščuvali.", TextEnglish = "It has been made permissible for you the night preceding fasting to go to your wives [for sexual relations]. They are a clothing for you and you are a clothing[67] for them. Allāh knows that you used to deceive yourselves,[68] so He accepted your repentance and forgave you. So now, have relations with them and seek that which Allāh has decreed for you [i.e., offspring]. And eat and drink until the white thread of dawn becomes distinct to you from the black thread [of night]. Then complete the fast until the night [i.e., sunset]. And do not have relations with them as long as you are staying for worship in the mosques. These are the limits [set by] Allāh, so do not approach them. Thus does Allāh make clear His verses [i.e., ordinances] to the people that they may become righteous.", TextGerman = "Erlaubt ist euch, in der Nacht des Fastens mit euren Frauen Beischlaf auszuüben; sie sind euch ein Kleid, und ihr seid ihnen ein Kleid. Allah weiß, daß ihr euch selbst (immer wieder) betrogt, und da hat Er eure Reue angenommen und euch verziehen. Von jetzt an verkehrt mit ihnen und trachtet nach dem, was Allah für euch bestimmt hat, und eßt und trinkt, bis sich für euch der weiße vom schwarzen Faden der Morgendämmerung klar unterscheidet! Hierauf vollzieht das Fasten bis zur Nacht! Und verkehrt nicht mit ihnen, während ihr euch (zur Andacht) in die Gebetsstätten zurückgezogen habt! Dies sind Allahs Grenzen, so kommt ihnen nicht zu nahe! So macht Allah den Menschen Seine Zeichen klar, auf daß sie gottesfürchtig werden mögen." });
                        surah2.VerseList.Add(new Verse { Number = 188, TextBosnian = "Ne jedite imovinu jedni drugih na nepošten način i ne sporite se zbog nje pred sudijama kako biste s grijehom, i znajući to, dio imovine drugih ljudi pojeli.", TextEnglish = "And do not consume one another's wealth unjustly or send it [in bribery] to the rulers in order that [they might aid] you [to] consume a portion of the wealth of the people in sin, while you know [it is unlawful].", TextGerman = "Und zehrt nicht euren Besitz untereinander auf nichtige Weise auf und bestecht nicht damit die Richter, um einen Teil des Besitzes der Menschen in sündhafter Weise zu verzehren, wo ihr (es) doch wißt." });
                        surah2.VerseList.Add(new Verse { Number = 189, TextBosnian = "Pitaju te o mlađacima. Reci: 'Oni su ljudima oznake za vrijeme i hadž.' I nije dobročinstvo u tome da sa stražnje strane u kuće ulazite, nego je pravo dobročinstvo ono što čini onaj koji se Allaha boji. U kuće ulazite na vrata njihova i Allaha se bojte da biste uspjeli.", TextEnglish = "They ask you, [O Muḥammad], about the crescent moons. Say, 'They are measurements of time for the people and for ḥajj [pilgrimage].' And it is not righteousness to enter houses from the back, but righteousness is [in] one who fears Allāh. And enter houses from their doors. And fear Allāh that you may succeed.", TextGerman = "Sie fragen dich nach den Jungmonden. Sag: Sie sind festgesetzte Zeiten für die Menschen und für die Pilgerfahrt. Und nicht darin besteht die Frömmigkeit, daß ihr von der Rückseite in die Häuser kommt. Frömmigkeit besteht vielmehr darin, daß man gottesfürchtig ist. So kommt durch die Türen in die Häuser und fürchtet Allah, auf daß es euch wohl ergehen möge!" });
                        surah2.VerseList.Add(new Verse { Number = 190, TextBosnian = "I borite se na Allahovu putu protiv onih koji se bore protiv vas, ali ne prelazite granice. Zaista Allah ne voli one koji prekoračaju granice.", TextEnglish = "Fight in the way of Allāh those who fight against you but do not transgress. Indeed, Allāh does not like transgressors.", TextGerman = "Und kämpft auf Allahs Weg gegen diejenigen, die gegen euch kämpfen, doch übertretet nicht! Allah liebt nicht die Übertreter." });
                        surah2.VerseList.Add(new Verse { Number = 191, TextBosnian = "I ubijajte ih gdje god ih stignete, i progonite ih odakle su oni vas prognali! A smutnja je teža od ubijanja! I ne borite se protiv njih kod Časne džamije dok vas oni tu ne napadnu. Ako vas tu napadnu, onda ih ubijajte! Takva je kazna za nevjernike.", TextEnglish = "And kill them [in battle] wherever you overtake them and expel them from wherever they have expelled you, and fitnah[69] is worse than killing. And do not fight them at al-Masjid al-Ḥarām until they fight you there. But if they fight you, then kill them. Such is the recompense of the disbelievers.", TextGerman = "Und tötet sie, wo immer ihr auf sie trefft, und vertreibt sie, von wo sie euch vertrieben haben, denn Verfolgung ist schlimmer als Töten! Kämpft jedoch nicht gegen sie bei der geschützten Gebetsstätte, bis sie dort (zuerst) gegen euch kämpfen. Wenn sie aber (dort) gegen euch kämpfen, dann tötet sie. Solcherart ist der Lohn der Ungläubigen." });
                        surah2.VerseList.Add(new Verse { Number = 192, TextBosnian = "Ako se prođu, pa Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And if they cease, then indeed, Allāh is Forgiving and Merciful.", TextGerman = "Wenn sie jedoch aufhören, so ist Allah Allvergebend und Barmherzig." });
                        surah2.VerseList.Add(new Verse { Number = 193, TextBosnian = "I borite se protiv njih dok smutnje ne nestane i dok vjera ne bude samo Allahu iskazivana. Pa, ako se okane, onda neprijateljstvo prestaje, a jedino protiv zulumćara ostaje.", TextEnglish = "Fight them until there is no [more] fitnah and [until] religion [i.e., worship] is [acknowledged to be] for Allāh. But if they cease, then there is to be no aggression [i.e., assault] except against the oppressors.", TextGerman = "Und kämpft gegen sie, bis es keine Verfolgung mehr gibt und die Religion (allein) Allahs ist. Wenn sie jedoch aufhören, dann darf es kein feindseliges Vorgehen geben außer gegen die Ungerechten." });
                        surah2.VerseList.Add(new Verse { Number = 194, TextBosnian = "Sveti mjesec je za sveti mjesec, a i u svetinjama vrijedi odmazda: onima koji vas napadnu, uzvratite istom mjerom i Allaha se bojte; znajte da je Allah uz bogobojazne.", TextEnglish = "[Battle in] the sacred month is for [aggression committed in] the sacred month,[70] and for [all] violations is legal retribution. So whoever has assaulted you, then assault him in the same way that he has assaulted you. And fear Allāh and know that Allāh is with those who fear Him.", TextGerman = "Der Schutzmonat ist für den Schutzmonat, und (für) die unantastbaren Dinge ist Wiedervergeltung. Wenn jemand gegen euch übertritt, dann geht (auch ihr) in gleichem Maß gegen ihn vor, wie er gegen euch übertreten hat. Und fürchtet Allah und wißt, daß Allah mit den Gottesfürchtigen ist." });
                        surah2.VerseList.Add(new Verse { Number = 195, TextBosnian = "I trošite imetak na Allahovom putu, i svojim rukama se u propast ne bacajte, i dobro činite; Allah, doista, voli dobročinitelje.", TextEnglish = "And spend in the way of Allāh and do not throw [yourselves] with your [own] hands into destruction [by refraining]. And do good; indeed, Allāh loves the doers of good.", TextGerman = "Und gebt auf Allahs Weg aus und stürzt euch nicht mit eigener Hand ins Verderben. Und tut Gutes. Allah liebt die Gutes Tuenden." });
                        surah2.VerseList.Add(new Verse { Number = 196, TextBosnian = "Hadž i umru radi Allaha upotpunite! A ako budete spriječeni, onda kurbane što vam se nađu pri ruci zakoljite, a glave svoje, dok kurbani ne stignu do mjesta svoga, ne brijte! A onaj od vas koji se razboli ili ima nešto na glavi što ga muči, neka se postom ili sadakom ili kurbanom iskupi! Kada budete sigurni, ko bude obavljao umru s hadžom, kurban do kojeg može doći, neka zakolje! A onaj ko ga ne nađe, neka tri dana posti u danima hadža i sedam dana po povratku, to je punih deset dana. To važi za onoga čija porodica ne živi u granicama Časne džamije. Bojte se Allaha i znajte da On žestoko kažnjava!", TextEnglish = "And complete the ḥajj and ʿumrah for Allāh. But if you are prevented, then [offer] what can be obtained with ease of sacrificial animals. And do not shave your heads until the sacrificial animal has reached its place of slaughter. And whoever among you is ill or has an ailment of the head [making shaving necessary must offer] a ransom of fasting [three days] or charity[71] or sacrifice.[72] And when you are secure,[73] then whoever performs ʿumrah [during the ḥajj months][74] followed by ḥajj [offers] what can be obtained with ease of sacrificial animals. And whoever cannot find [or afford such an animal] - then a fast of three days during ḥajj and of seven when you have returned [home]. Those are ten complete [days]. This is for those whose family is not in the area of al-Masjid al-Ḥarām. And fear Allāh and know that Allāh is severe in penalty.", TextGerman = "Vollzieht die Pilgerfahrt und die Besuchsfahrt für Allah. Wenn ihr jedoch (daran) gehindert werdet, dann (bringt) an Opfertieren (dar), was euch leichtfällt. Und schert euch nicht die Köpfe, bevor die Opfertiere ihren Schlachtort erreicht haben! Wer von euch krank ist oder ein Leiden an seinem Kopf hat, der soll Ersatz leisten mit Fasten, Almosen oder Opferung eines Schlachttieres. - Wenn ihr aber in Sicherheit seid, dann soll derjenige, der die Besuchsfahrt mit der Pilgerfahrt durchführen möchte, an Opfertieren (darbringen), was ihm leichtfällt. Wer jedoch nicht(s) finden kann, der soll drei Tage während der Pilgerfahrt fasten und sieben, wenn ihr zurückgekehrt seid; das sind im ganzen zehn. Dies (gilt nur) für den, dessen Angehörige nicht in der geschützten Gebetsstätte wohnhaft sind. Und fürchtet Allah und wißt, daß Allah streng im Bestrafen ist!" });
                        surah2.VerseList.Add(new Verse { Number = 197, TextBosnian = "Hadž je u poznatim mjesecima, pa onaj ko se obaveže u toku njih obaviti hadž ne može imati intimni odnos, nikakve grijehe činiti, niti se prepirati u toku hadža. A za dobro koje učinite Allah zna. Za put se opskrbite, a najbolja je opskrba bogobojaznost! I Mene se bojte - vi, razumom obdareni!", TextEnglish = "Ḥajj is [during] well-known months,[75] so whoever has made ḥajj obligatory upon himself therein [by entering the state of iḥrām], there is [to be for him] no sexual relations and no disobedience and no disputing during ḥajj. And whatever good you do - Allāh knows it. And take provisions, but indeed, the best provision is fear of Allāh. And fear Me, O you of understanding.", TextGerman = "Die (Zeit der) Pilgerfahrt (sind) bekannte Monate. Wer in ihnen die (Durchführung der) Pilgerfahrt beschlossen hat, der darf keinen Beischlaf ausüben, keinen Frevel begehen und nicht Streit führen während der Pilgerfahrt. Und was ihr an Gutem tut, Allah weiß es. Und versorgt euch mit Reisevorrat, doch der beste Vorrat ist die Gottesfurcht. Und fürchtet Mich, o die ihr Verstand besitzt!" });
                        surah2.VerseList.Add(new Verse { Number = 198, TextBosnian = "Nije vam grijeh da od Gospodara svoga tražite neko dobro. A kada pođete s Arefata, Allaha spominjite kod Meš'ari-harama; spominjite Ga budući da vas je On uputio, a zaista ste prije toga u zabludi bili.", TextEnglish = "There is no blame upon you for seeking bounty[76] from your Lord [during ḥajj]. But when you depart from ʿArafāt, remember Allāh at al-Mashʿar al-Ḥarām.[77] And remember Him, as He has guided you, for indeed, you were before that among those astray.", TextGerman = "Es ist keine Sünde für euch, daß ihr nach Huld von eurem Herrn trachtet. Doch wenn ihr von ’Arafat hergeströmt seid, dann gedenkt Allahs bei der geschützten Kultstätte. Und gedenkt Seiner, wie Er euch rechtgeleitet hat, obwohl ihr vordem wahrlich zu den Irregehenden gehörtet." });
                        surah2.VerseList.Add(new Verse { Number = 199, TextBosnian = "Zatim pohrlite odakle hrle ostali ljudi i od Allaha oprost molite. Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "Then depart from the place from where [all] the people depart and ask forgiveness of Allāh. Indeed, Allāh is Forgiving and Merciful.", TextGerman = "Hierauf strömt weiter, woher die (anderen) Menschen weiterströmen, und bittet Allah um Vergebung. Allah ist Allvergebend und Barmherzig." });
                        surah2.VerseList.Add(new Verse { Number = 200, TextBosnian = "A kada završite obrede vaše, Allaha spominjite kao što spominjete pretke vaše, pa i više od toga! Ima ljudi koji govore: 'Gospodaru naš, daj Ti nama na ovome svijetu!', a na drugom svijetu neće imati nikakvog udjela!", TextEnglish = "And when you have completed your rites, remember Allāh like your [previous] remembrance of your fathers or with [much] greater remembrance. And among the people is he who says, 'Our Lord, give us in this world,' and he will have in the Hereafter no share.", TextGerman = "Wenn ihr dann eure Riten vollzogen habt, dann gedenkt Allahs, wie ihr eurer Väter gedenkt, oder mit noch innigerem Gedenken. Unter den Menschen gibt es manch einen, der sagt: Unser Herr, gib uns im Diesseits! Doch hat er am Jenseits keinen Anteil." });
                        surah2.VerseList.Add(new Verse { Number = 201, TextBosnian = "A ima i onih koji govore: 'Gospodaru naš, daj nam dobro i na dunjaluku i na ahiretu i sačuvaj nas patnje paklene!'", TextEnglish = "But among them is he who says, 'Our Lord, give us in this world [that which is] good and in the Hereafter [that which is] good and protect us from the punishment of the Fire.'", TextGerman = "Unter ihnen gibt es aber auch solche, die sagen: „Unser Herr, gib uns im Diesseits Gutes und im Jenseits Gutes, und bewahre uns vor der Strafe des (Höllen)feuers!" });
                        surah2.VerseList.Add(new Verse { Number = 202, TextBosnian = "Njima pripada udio koji su zaslužili! Allah doista brzo sviđa račune.", TextEnglish = "Those will have a share of what they have earned, and Allāh is swift in account.", TextGerman = "Für jene gibt es einen Anteil an dem, was sie verdient haben. Und Allah ist schnell im Abrechnen." });
                        surah2.VerseList.Add(new Verse { Number = 203, TextBosnian = "I Allaha spominjite u danima određenim! A onome ko požuri, pa to učini u dva dana, nije grijeh, kao što nije grijeh ni onome koji još ostane, ako je bogobojazan. Bojte se Allaha, i znajte da ćete svi pred Njim biti sakupljeni.", TextEnglish = "And remember Allāh during [specific] numbered days. Then whoever hastens [his departure] in two days - there is no sin upon him; and whoever delays [until the third] - there is no sin upon him - for him who fears Allāh. And fear Allāh and know that unto Him you will be gathered.", TextGerman = "Und gedenkt Allahs während einer bestimmten Anzahl von Tagen. Wer sich jedoch in zwei Tagen (mit dem Aufbruch) beeilt, den trifft keine Sünde, und wer länger bleibt, den trifft keine Sünde; (das gilt) für den, der gottesfürchtig ist. Fürchtet Allah und wißt, daß ihr zu Ihm versammelt werdet!" });
                        surah2.VerseList.Add(new Verse { Number = 204, TextBosnian = "Ima ljudi, poput onog čije te riječi o životu na dunjaluku oduševljavaju i koji se poziva na Allaha kao svjedoka za ono što je u srcu njegovom, a takav je najiskvareniji osporavatelj.", TextEnglish = "And of the people is he whose speech pleases you in worldly life, and he calls Allāh to witness as to what is in his heart, yet he is the fiercest of opponents.", TextGerman = "Unter den Menschen gibt es manch einen, dessen Worte über das diesseitige Leben dir gefallen, und der Allah zum Zeugen nimmt für das, was in seinem Herzen ist. Dabei ist er der hartnäckigste Widersacher." });
                        surah2.VerseList.Add(new Verse { Number = 205, TextBosnian = "Čim se neki od njih okrene od tebe, krene nered po Zemlji praviti, ništiti usjeve i stoku. A Allah ne voli nered!", TextEnglish = "And when he goes away, he strives throughout the land to cause corruption therein and destroy crops and animals. And Allāh does not like corruption.", TextGerman = "Wenn er sich abkehrt, bemüht er sich eifrig darum, auf der Erde Unheil zu stiften und Saatfelder und Nachkommenschaft zu vernichten. Aber Allah liebt nicht das Unheil." });
                        surah2.VerseList.Add(new Verse { Number = 206, TextBosnian = "A kada mu se kaže: 'Allaha se boj!', on se onda ponosi grijesima. Pa dosta mu je Džehennem, a to je, doista, grozno boravište!", TextEnglish = "And when it is said to him, 'Fear Allāh,' pride in the sin takes hold of him. Sufficient for him is Hellfire, and how wretched is the resting place.", TextGerman = "Und wenn man zu ihm sagt: „Fürchte Allah, reißt ihn der Stolz zur Sünde hin. Die Hölle soll seine Genüge sein - wahrlich eine schlimme Lagerstatt!" });
                        surah2.VerseList.Add(new Verse { Number = 207, TextBosnian = "Ima ljudi koji se žrtvuju u težnji za zadovoljstvom Allahovim. A Allah je milosrdan prema robovima Svojim.", TextEnglish = "And of the people is he who sells himself, seeking means to the approval of Allāh. And Allāh is Kind to [His] servants.", TextGerman = "Unter den Menschen gibt es (auch) manchen, der sich selbst im Trachten nach Allahs Zufriedenheit verkauft. Und Allah ist zu den Menschen Gnädig." });
                        surah2.VerseList.Add(new Verse { Number = 208, TextBosnian = "O vi koji vjerujete, uđite u islam potpuno i ne idite stopama šejtanovim; on vam je, doista, neprijatelj otvoreni!", TextEnglish = "O you who have believed, enter into Islām completely [and perfectly] and do not follow the footsteps of Satan. Indeed, he is to you a clear enemy.", TextGerman = "O die ihr glaubt, tretet allesamt in den Islam ein und folgt nicht den Fußstapfen des Satans! Er ist euch ja ein deutlicher Feind." });
                        surah2.VerseList.Add(new Verse { Number = 209, TextBosnian = "A ako posrnete, nakon što su vam došli jasni dokazi, onda znajte da je Allah, zaista, silan i mudar.", TextEnglish = "But if you slip [i.e., deviate] after clear proofs have come to you, then know that Allāh is Exalted in Might and Wise.", TextGerman = "Wenn ihr aber strauchelt, nachdem die klaren Beweise zu euch gekommen sind, so wißt, daß Allah Allmächtig und Allweise ist." });
                        surah2.VerseList.Add(new Verse { Number = 210, TextBosnian = "Čekaju li oni da im Allah dođe u tminama oblaka, i meleci, i da bude svemu kraj?! A Allahu se sve vraća!", TextEnglish = "Do they await but that Allāh should come to them in covers of clouds and the angels [as well] and the matter is [then] decided? And to Allāh [all] matters are returned.", TextGerman = "Erwarten sie (etwas anderes), als daß Allah in Schattendächern aus Wolken zu ihnen kommt und die Engel? Doch dann wird die Angelegenheit entschieden sein. Und zu Allah werden (all) die Angelegenheiten zurückgebracht." });
                        surah2.VerseList.Add(new Verse { Number = 211, TextBosnian = "Upitaj sinove Israilove koliko smo im Jasnih dokaza dali! A oni koji zamjenjuju blagodat Allahovu koja im je došla, pa Allah, doista, strahovito kažnjava!", TextEnglish = "Ask the Children of Israel how many a sign of evidence We have given them. And whoever exchanges the favor of Allāh [for disbelief] after it has come to him - then indeed, Allāh is severe in penalty.", TextGerman = "Frage die Kinder Isra’ils, wie viele klare Beweise Wir ihnen gegeben haben. Wenn aber einer die Gunst Allahs abändert, nachdem sie ihm zuteil geworden ist -, dann ist Allah streng im Bestrafen." });
                        surah2.VerseList.Add(new Verse { Number = 212, TextBosnian = "Uljepšan je, onima koji ne vjeruju, život na ovome svijetu i oni se rugaju onima koji vjeruju. A oni koji su se Allaha bojali i grijeha klonili, na Kijametskom danu bit će iznad njih! A Allah opskrbljuje bez računa onoga koga hoće.", TextEnglish = "Beautified for those who disbelieve is the life of this world, and they ridicule those who believe. But those who fear Allāh are above them on the Day of Resurrection. And Allāh gives provision to whom He wills without account.", TextGerman = "Ausgeschmückt ist denen, die ungläubig sind, das diesseitige Leben, und sie spotten über diejenigen, die glauben. Aber diejenigen, die gottesfürchtig sind, werden am Tag der Auferstehung über ihnen stehen. Und Allah versorgt, wen Er will, ohne zu berechnen." });
                        surah2.VerseList.Add(new Verse { Number = 213, TextBosnian = "Svi ljudi su sačinjavali jednu zajednicu, pa je Allah slao vjerovjesnike da donose radosne vijesti i opomenu, i po njima je slao Knjigu sa Istinom, da po njoj sudi ljudima u onome u čemu se oni razilaziše. A o njoj su se, iz međusobne zavisti, razišli samo oni kojima je ona data, i to nakon što su im došli Jasni dokazi. Zatim je Allah, Svojom dozvolom uputio ka Istini, o kojoj su se oni razišli, one koji vjeruju. A Allah upućuje na Pravi put onoga koga On hoće.", TextEnglish = "Mankind was [of] one religion [before their deviation]; then Allāh sent the prophets as bringers of good tidings and warners and sent down with them the Scripture in truth to judge between the people concerning that in which they differed. And none differed over it [i.e., the Scripture] except those who were given it - after the clear proofs came to them - out of jealous animosity among themselves. And Allāh guided those who believed to the truth concerning that over which they had differed, by His permission. And Allāh guides whom He wills to a straight path.", TextGerman = "Die Menschen waren eine einzige Gemeinschaft. Dann schickte Allah die Propheten als Verkünder froher Botschaft und als Überbringer von Warnungen und sandte mit ihnen die Bücher mit der Wahrheit herab, um zwischen den Menschen über das zu richten, worüber sie uneinig waren. Doch nur diejenigen waren - aus Mißgunst untereinander - darüber uneinig, denen sie gegeben wurden, nachdem die klaren Beweise zu ihnen gekommen waren. Und so hat Allah mit Seiner Erlaubnis diejenigen, die glauben, zu der Wahrheit geleitet, über die sie uneinig waren. Und Allah leitet, wen Er will, auf einen geraden Weg." });
                        surah2.VerseList.Add(new Verse { Number = 214, TextBosnian = "Zar mislite da ćete ući u Džennet, a još niste iskusili ono što su iskusili oni koji prije vas minuše?! Njih je pogađala neimaština i bolest, i toliko su bivali potresani da bi i poslanik i oni koji su s njim vjerovali, govorili: 'Kada će doći Allahova pomoć!?' Eto, Allahova pomoć je, doista, blizu!", TextEnglish = "Or do you think that you will enter Paradise while such [trial] has not yet come to you as came to those who passed on before you? They were touched by poverty and hardship and were shaken until [even their] messenger and those who believed with him said, 'When is the help of Allāh?' Unquestionably, the help of Allāh is near.", TextGerman = "Oder meint ihr etwa, daß ihr in den (Paradies)garten eingehen werdet, noch bevor Gleiches über euch gekommen ist, wie über diejenigen, die vor euch dahingegangen sind? Not und Leid widerfuhr ihnen, und sie wurden erschüttert, bis daß der Gesandte und diejenigen, die mit ihm glaubten, sagten: „Wann kommt Allahs Sieg? Aber wahrlich, Allahs Sieg ist nahe." });
                        surah2.VerseList.Add(new Verse { Number = 215, TextBosnian = "Pitaju te, šta će dijeliti? Reci: 'Imetak koji ćete udjeljivati neka pripadne roditeljima i rođacima, i siročadi, i siromasima, i putnicima namjernicima; a za dobro koje činite Allah sigurno dobro zna.'", TextEnglish = "They ask you, [O Muḥammad], what they should spend. Say, 'Whatever you spend of good is [to be] for parents and relatives and orphans and the needy and the traveler. And whatever you do of good - indeed, Allāh is Knowing of it.'", TextGerman = "Sie fragen dich, was sie ausgeben sollen. Sag: Was immer ihr an Gutem ausgebt, soll den Eltern, den nächsten Verwandten, den Waisen, den Armen und dem Sohn des Weges zukommen. Und was immer ihr an Gutem tut, so weiß Allah darüber Bescheid." });
                        surah2.VerseList.Add(new Verse { Number = 216, TextBosnian = "Propisana vam je borba, a to vam je mrsko! Možda nešto ne volite, a to je dobro po vas; možda nešto volite, a to je zlo po vas! Allah zna, a vi ne znate.", TextEnglish = "Battle has been enjoined upon you while it is hateful to you. But perhaps you hate a thing and it is good for you; and perhaps you love a thing and it is bad for you. And Allāh knows, while you know not.", TextGerman = "Vorgeschrieben ist euch zu kämpfen, obwohl es euch zuwider ist. Aber vielleicht ist euch etwas zuwider, während es gut für euch ist, und vielleicht ist euch etwas lieb, während es schlecht für euch ist. Allah weiß, ihr aber wißt nicht." });
                        surah2.VerseList.Add(new Verse { Number = 217, TextBosnian = "Pitaju te o svetom mjesecu, o ratovanju u njemu! Reci: 'Ratovanje u njemu veliki je grijeh; ali, odvraćanje od Allahova puta i nevjerovanje u Njega, te odvraćanje od Časne džamije i izgonjenje stanovnika njenih, još je veći grijeh kod Allaha. A smutnja je gora od ubijanja! Oni će se neprestano boriti protiv vas dok vas ne odvrate od vjere vaše, ako budu mogli! A oni među vama koji od vjere svoje otpadnu i kao nevjernici umru - njihova djela bit će poništena na dunjaluku, a i na ahiretu. Oni će stanovnici Vatre biti i tamo će vječno ostati.", TextEnglish = "They ask you about the sacred month[78] - about fighting therein. Say, 'Fighting therein is great [sin], but averting [people] from the way of Allāh and disbelief in Him and [preventing access to] al-Masjid al-Ḥarām and the expulsion of its people therefrom are greater [evil] in the sight of Allāh. And fitnah[79] is greater than killing.' And they will continue to fight you until they turn you back from your religion if they are able. And whoever of you reverts from his religion [to disbelief] and dies while he is a disbeliever - for those, their deeds have become worthless in this world and the Hereafter, and those are the companions of the Fire; they will abide therein eternally.", TextGerman = "Sie fragen dich nach dem Schutzmonat, danach, in ihm zu kämpfen. Sag: In ihm zu kämpfen ist schwerwiegend. Aber von Allahs Weg abzuhalten - und Ihn zu verleugnen -, und von der geschützten Gebetsstätte (abzuhalten) und deren Anwohner von ihr vertreiben, ist (noch) schwerwiegender bei Allah. Und Verfolgung ist schwerwiegender als Töten. Und sie werden nicht eher aufhören, gegen euch zu kämpfen, bis sie euch von eurer Religion abgekehrt haben - wenn sie (es) können. Wer aber unter euch sich von seiner Religion abkehrt und dann als Ungläubiger stirbt -, das sind diejenigen, deren Werke im Diesseits und im Jenseits hinfällig werden. Das sind Insassen des (Höllen)feuers. Ewig werden sie darin bleiben." });
                        surah2.VerseList.Add(new Verse { Number = 218, TextBosnian = "Zaista, oni koji vjeruju, i koji se isele i na Allahovom putu bore, oni žele milost Allahovu. A Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "Indeed, those who have believed and those who have emigrated and fought in the cause of Allāh - those expect the mercy of Allāh. And Allāh is Forgiving and Merciful.", TextGerman = "Diejenigen (aber), die glauben, und diejenigen, die auswandern und sich auf Allahs Weg abmühen, sie hoffen auf Allahs Erbarmen. Allah ist Allvergebend und Barmherzig." });
                        surah2.VerseList.Add(new Verse { Number = 219, TextBosnian = "Pitaju te o opojnim pićima i kocki! Reci: 'U njima je veliki grijeh, a i ima i nekih koristi ljudima; samo je grijeh od njih veći od koristi njihove!' Pitaju te, šta da udjeljuju! Reci: 'Višak!' Eto, tako vama Allah propise objašnjava da biste razmislili,", TextEnglish = "They ask you about wine[80] and gambling. Say, 'In them is great sin and [yet, some] benefit for people. But their sin is greater than their benefit.' And they ask you what they should spend. Say, 'The excess [beyond needs].' Thus Allāh makes clear to you the verses [of revelation] that you might give thought", TextGerman = "Sie fragen dich nach berauschendem Trunk und Glücksspiel. Sag: In ihnen (beiden) liegt große Sünde und Nutzen für die Menschen. Aber die Sünde in ihnen (beiden) ist größer als ihr Nutzen. Und sie fragen dich, was sie ausgeben sollen. Sag: Den Überschuss. So macht Allah euch die Zeichen klar, auf daß ihr nachdenken möget," });
                        surah2.VerseList.Add(new Verse { Number = 220, TextBosnian = "i o dunjaluku i o ahiretu. I pitaju te o siročadi! Reci: 'Unaprijediti njihova imanja je bolje!' A ako budete s njima zajedno živjeli, pa to su braća vaša! Allah zna razliku između pokvarenjaka i dobročinitelja! Da je Allah htio, mogao je propisati ono što vam je teško! On je, doista, silan i mudar.", TextEnglish = "To this world and the Hereafter. And they ask you about orphans. Say, 'Improvement for them is best. And if you mix your affairs with theirs - they are your brothers. And Allāh knows the corrupter from the amender. And if Allāh had willed, He could have put you in difficulty. Indeed, Allāh is Exalted in Might and Wise.'", TextGerman = "über das Diesseits und das Jenseits. Und sie fragen dich nach den Waisen. Sag: Ihre Lage zu verbessern ist besser. Und wenn ihr euch mit ihnen zusammentut, so sind sie eure Brüder. Allah weiß den Unheilstifter vom Heilstifter (zu unterscheiden). Und wenn Allah gewollt hätte, hätte Er euch wahrlich in Bedrängnis gebracht. Allah ist Allmächtig und Allweise." });
                        surah2.VerseList.Add(new Verse { Number = 221, TextBosnian = "Ne ženite se višeboškinjama dok vjernice ne postanu; uistinu je robinja - vjernica bolja od višeboškinje, makar vam se ona i sviđala! Ne udajite vjernice za višebošce dok ne postanu vjernici; jer uistinu je rob-vjernik bolji od višebošca, makar vam se i dopadao. Oni zovu u Vatru, a Allah poziva Džennetu i oprostu, dozvolom Svojom, te objašnjava ljudima ajete i dokaze Svoje, da bi se prisjećali i pouku primili!", TextEnglish = "And do not marry polytheistic women until they believe.[81] And a believing slave woman is better than a polytheist, even though she might please you. And do not marry polytheistic men [to your women] until they believe. And a believing slave is better than a polytheist, even though he might please you. Those invite [you] to the Fire, but Allāh invites to Paradise and to forgiveness, by His permission. And He makes clear His verses [i.e., ordinances] to the people that perhaps they may remember.", TextGerman = "Und heiratet Götzendienerinnen nicht, bevor sie glauben. Und eine gläubige Sklavin ist fürwahr besser als eine Götzendienerin, auch wenn diese euch gefallen sollte. Und verheiratet nicht (gläubige Frauen) mit Götzendienern, bevor sie glauben. Und ein gläubiger Sklave ist fürwahr besser als ein Götzendiener, auch wenn dieser euch gefallen sollte. Jene laden zum (Höllen)feuer ein. Allah aber lädt zum (Paradies)garten und zur Vergebung ein, mit Seiner Erlaubnis, und macht den Menschen Seine Zeichen klar, auf daß sie bedenken mögen." });
                        surah2.VerseList.Add(new Verse { Number = 222, TextBosnian = "I pitaju te o mjesečnom pranju! Reci: 'To je neprijatnost!' Zato, ne općite sa ženama za vrijeme mjesečnog pranja, i ne prilazite im dok se ne očiste! A kada se očiste, onda im prilazite onako kako vam je Allah naredio! Allah, doista, voli one koji se kaju i voli one koji se čiste.", TextEnglish = "And they ask you about menstruation. Say, 'It is harm, so keep away from wives[82] during menstruation. And do not approach them until they are pure. And when they have purified themselves,[83] then come to them from where Allāh has ordained for you. Indeed, Allāh loves those who are constantly repentant and loves those who purify themselves.'", TextGerman = "Sie fragen dich nach der Monatsblutung. Sag: Sie ist ein Leiden. So haltet euch von den Frauen während der Monatsblutung fern, und kommt ihnen nicht nahe, bis sie rein sind. Wenn sie sich dann gereinigt haben, so kommt zu ihnen, wie Allah es euch geboten hat. Allah liebt die Reumütigen, und Er liebt die, die sich rein halten." });
                        surah2.VerseList.Add(new Verse { Number = 223, TextBosnian = "Žene vaše su njive vaše, i vi njivama svojim prilazite kako hoćete! A pripremite nešto i za duše svoje! I bojte se Allaha i znajte da ćete se s Njim susresti! A ti obraduj vjernike!", TextEnglish = "Your wives are a place of cultivation [i.e., sowing of seed] for you, so come to your place of cultivation however you wish and put forth [righteousness] for yourselves. And fear Allāh and know that you will meet Him. And give good tidings to the believers.", TextGerman = "Eure Frauen sind euch ein Saatfeld. So kommt zu eurem Saatfeld, wann und wie ihr wollt. Doch schickt (Gutes) für euch selbst voraus. Und fürchtet Allah und wißt, daß ihr Ihm begegnen werdet. Und verkünde den Gläubigen frohe Botschaft." });
                        surah2.VerseList.Add(new Verse { Number = 224, TextBosnian = "I neka vam zakletva Allahom ne bude prepreka da dobro činite, da se Allaha bojite i ljude mirite. A Allah je Onaj Koji sve čuje i Onaj Koji sve zna!", TextEnglish = "And do not make [your oath by] Allāh an excuse against being righteous and fearing Allāh and making peace among people. And Allāh is Hearing and Knowing.", TextGerman = "Und macht nicht Allah mit euren Eiden zu einem Hinderungsgrund, gütig und gottesfürchtig zu sein und zwischen den Menschen Frieden zu stiften. Allah ist Allhörend und Allwissend." });
                        surah2.VerseList.Add(new Verse { Number = 225, TextBosnian = "Allah vas neće kazniti ako se neoprezno zakunete, ali će vas kazniti za ono što s predumišljajem srca vaših uradite. Allah je Onaj Koji oprašta grijehe i Onaj Koji ne žuri s kaznom.", TextEnglish = "Allāh does not impose blame upon you for what is unintentional in your oaths, but He imposes blame upon you for what your hearts have earned. And Allāh is Forgiving and Forbearing.[84]", TextGerman = "Allah wird euch nicht für etwas Unbedachtes in euren Eiden belangen. Jedoch wird Er euch für das belangen, was eure Herzen erworben haben. Allah ist Allvergebend und Nachsichtig." });
                        surah2.VerseList.Add(new Verse { Number = 226, TextBosnian = "Oni koji se zakunu da se neće približavati ženama svojim, sačekat će četiri mjeseca! Pa ako im se vrate, Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "For those who swear not to have sexual relations with their wives[85] is a waiting time of four months, but if they return [to normal relations] - then indeed, Allāh is Forgiving and Merciful.", TextGerman = "Diejenigen, die schwören, sich ihrer Frauen zu enthalten, haben eine Wartezeit von vier Monaten. Wenn sie dann (von ihrem Schwur) zurücktreten, so ist Allah Allvergebend und Barmherzig." });
                        surah2.VerseList.Add(new Verse { Number = 227, TextBosnian = "A ako odluče da se rastave, pa Allah je, doista, Onaj Koji sve čuje i Onaj Koji sve zna!", TextEnglish = "And if they decide on divorce - then indeed, Allāh is Hearing and Knowing.", TextGerman = "Wenn sie aber zur Scheidung entschlossen sind, so ist Allah Allhörend und Allwissend." });
                        surah2.VerseList.Add(new Verse { Number = 228, TextBosnian = "Puštene žene neka čekaju tri mjesečna pranja; i nije im dopušteno da kriju ono što je Allah stvorio u matericama njihovim, ako u Allaha i u ahiret vjeruju! Muževi njihovi su preči da ih vrate dok su u tome, ako žele pomirenje. One imaju onoliko prava koliko i obaveza; treba da ih izvrše lijepo i po propisu. A muškarci su nad njima za jedan stepen! Allah je, doista, silan i mudar.", TextEnglish = "Divorced women remain in waiting [i.e., do not remarry] for three periods,[86] and it is not lawful for them to conceal what Allāh has created in their wombs if they believe in Allāh and the Last Day. And their husbands have more right to take them back in this [period] if they want reconciliation.[87] And due to them [i.e., the wives] is similar to what is expected of them, according to what is reasonable.[88] But the men [i.e., husbands] have a degree over them [in responsibility and authority]. And Allāh is Exalted in Might and Wise.", TextGerman = "Geschiedene Frauen sollen (mit sich) selbst drei Zeitabschnitte abwarten. Und es ist ihnen nicht erlaubt, zu verheimlichen, was Allah in ihrem Mutterleib erschaffen hat, wenn sie an Allah und den Jüngsten Tag glauben. Und ihre Ehemänner haben ein größeres Anrecht, sie zurückzunehmen, wenn sie eine Aussöhnung wollen. Und ihnen (den Frauen) steht in rechtlicher Weise (gegenüber den Männern) das gleiche zu, wie (den Männern) gegenüber ihnen. Doch die Männer haben ihnen gegenüber einen gewissen Vorzug. Und Allah ist Allmächtig und Allweise." });
                        surah2.VerseList.Add(new Verse { Number = 229, TextBosnian = "Puštanje supruge može biti dva puta, nakon čega se ona, ili lijepo i pravedno zadržava, ili joj se uz dobročinstvo razvod daje! A vama nije dopušteno uzimati bilo šta od onoga što ste im darovali, osim ako se njih dvoje plaše da Allahove propise neće izvršavati. A ako se bojite da njih dvoje Allahove propise neće izvršavati, onda im nije grijeh da se ona otkupi. To su Allahove granice, pa ih ne prekoračujte! A oni koji Allahove granice prekoračuju, to su pravi zulumćari!", TextEnglish = "Divorce is twice. Then [after that], either keep [her] in an acceptable manner or release [her] with good treatment. And it is not lawful for you to take anything of what you have given them unless both fear that they will not be able to keep [within] the limits of Allāh.[89] But if you fear that they will not keep [within] the limits of Allāh, then there is no blame upon either of them concerning that by which she ransoms herself. These are the limits of Allāh, so do not transgress them. And whoever transgresses the limits of Allāh - it is those who are the wrongdoers [i.e., the unjust].", TextGerman = "Die (widerrufliche) Scheidung ist zweimal (erlaubt). Dann (sollen die Frauen) in rechtlicher Weise behalten oder in ordentlicher Weise freigegeben (werden). Und es ist euch nicht erlaubt, etwas von dem, was ihr ihnen gegeben habt, (wieder) zu nehmen, außer wenn die beiden fürchten, daß sie Allahs Grenzen nicht einhalten werden. Wenn ihr aber befürchtet, daß die beiden Allahs Grenzen nicht einhalten werden, dann ist für die beiden keine Sünde in dem, womit (an Geld) sie sich löst. Dies sind Allahs Grenzen, so übertretet sie nicht! Wer aber Allahs Grenzen übertritt, diejenigen sind die Ungerechten." });
                        surah2.VerseList.Add(new Verse { Number = 230, TextBosnian = "A ako je opet pusti, ona mu nije nakon toga dopuštena sve dok se ne uda za drugog muža. Pa ako je i on pusti, onda njima dvoma nije grijeh da se jedno drugom vrate, ako misle da će Allahove propise izvršavati. To su Allahove granice koje On objašnjava ljudima koji znaju.", TextEnglish = "And if he has divorced her [for the third time], then she is not lawful to him afterward until [after] she marries a husband other than him.[90] And if he [i.e., the latter husband] divorces her [or dies], there is no blame upon them [i.e., the woman and her former husband] for returning to each other if they think that they can keep [within] the limits of Allāh. These are the limits of Allāh, which He makes clear to a people who know [i.e.,understand].", TextGerman = "Wenn er sich (ein drittes, unwiderrufliches Mal) von ihr scheidet, dann ist sie ihm nicht mehr (als Gattin) erlaubt, bevor sie nicht einen anderen Mann geheiratet hat. Wenn dieser sich von ihr scheidet, so ist es keine Sünde für die beiden, zu einander zurückzukehren, wenn sie (dabei) glauben, Allahs Grenzen einhalten zu können. Dies sind Allahs Grenzen, die Er Leuten klar macht, die Bescheid wissen." });
                        surah2.VerseList.Add(new Verse { Number = 231, TextBosnian = "Kada pustite supruge, pa one ispune svoj razvodni rok, tada ih, ili lijepo i pravedno zadržite, ili uz dobročinstvo otpremite! I ne zadržavajte ih nanoseći im štetu, a onaj ko tako postupi, učinio je sebi zulum! Ne poigravajte se Allahovim propisima i sjetite se blagodati Allahove prema vama i što vam je objavio Knjigu i mudrost kojom vas savjetuje! Allaha se bojte i znajte da Allah sve zna!'", TextEnglish = "And when you divorce women and they have [nearly] fulfilled their term, either retain them according to acceptable terms or release them according to acceptable terms, and do not keep them, intending harm, to transgress [against them]. And whoever does that has certainly wronged himself. And do not take the verses of Allāh in jest. And remember the favor of Allāh upon you and what has been revealed to you of the Book [i.e., the Qur’ān] and wisdom [i.e., the Prophet's sunnah] by which He instructs you. And fear Allāh and know that Allāh is Knowing of all things.", TextGerman = "Und wenn ihr euch von Frauen scheidet und sie dann ihre festgesetzte Zeit erreichen, so behaltet sie in rechtlicher Weise oder gebt sie in rechtlicher Weise frei. Doch behaltet sie nicht mit der Absicht der Schädigung, so daß ihr übertretet. Wer dies tut, der fügt sich ja selbst Unrecht zu. Und macht euch nicht über Allahs Zeichen lustig. Und gedenkt Allahs Gunst an euch und dessen, was Er von dem Buch und an Weisheit auf euch herabgesandt hat, um euch damit zu ermahnen. Und fürchtet Allah und wißt, daß Allah über alles Bescheid weiß." });
                        surah2.VerseList.Add(new Verse { Number = 232, TextBosnian = "A kada pustite supruge, pa ispune svoj razvodni rok, ne ometajte ih da se udaju za muževe svoje, kada se slože da lijepo žive! Ovim se savjetuju oni među vama koji vjeruju u Allaha i Posljednji dan. Tako vam je čednije i čestitije. A Allah zna, a vi ne znate.", TextEnglish = "And when you divorce women[91] and they have fulfilled their term, do not prevent them from remarrying their [former] husbands if they [i.e., all parties] agree among themselves on an acceptable basis. That is instructed to whoever of you believes in Allāh and the Last Day. That is better for you and purer, and Allāh knows and you know not.", TextGerman = "Und wenn ihr euch von Frauen scheidet und sie dann ihre festgesetzte Zeit erreicht haben, so haltet sie nicht davon ab, ihre Gatten zu heiraten, wenn sie sich in rechtlicher Weise miteinander geeinigt haben. Damit wird von euch ermahnt, wer an Allah und den Jüngsten Tag glaubt. Das ist lauterer für euch und reiner. Allah weiß, ihr aber wißt nicht." });
                        surah2.VerseList.Add(new Verse { Number = 233, TextBosnian = "Majke neka doje djecu svoju pune dvije godine, ako žele da dojenje bude potpuno! Očevi su dužni hraniti ih i odijevati na lijep i uobičajen način. Niko se ne zadužuje iznad mogućnosti svojih: majka ne smije naštetiti ocu djeteta zbog djeteta svoga, a ni otac majci djeteta zbog svoga djeteta! Nasljednik ima iste obaveze. Zato, ako njih dvoje na lijep način i po dogovoru odluče dijete odbiti, nisu grešni! A ako zaželite svojoj djeci dojilje naći, nije vam grijeh ako im, kako je uobičajeno, date ono što hoćete dati. Bojte se Allaha i znajte da Allah dobro vidi šta vi radite!", TextEnglish = "Mothers may nurse [i.e., breastfeed] their children two complete years for whoever wishes to complete the nursing [period]. Upon the father is their [i.e., the mothers'] provision and their clothing according to what is acceptable. No person is charged with more than his capacity. No mother should be harmed through her child, and no father through his child. And upon the [father's] heir is [a duty] like that [of the father]. And if they both desire weaning through mutual consent from both of them and consultation, there is no blame upon either of them. And if you wish to have your children nursed by a substitute, there is no blame upon you as long as you give payment according to what is acceptable. And fear Allāh and know that Allāh is Seeing of what you do.", TextGerman = "Und die Mütter stillen ihre Kinder zwei volle Jahre. (Das gilt) für jemanden, der das Stillen zu Ende führen will. Und demjenigen, dem das Kind geboren wurde, obliegt es, für ihre Versorgung und Kleidung in rechtlicher Weise aufzukommen. Keiner Seele wird mehr auferlegt, als sie zu leisten vermag. Keine Mutter soll wegen ihres Kindes zu Schaden kommen, noch einer, dem das Kind geboren wurde, wegen seines Kindes. Und dem Erben obliegt das gleiche. Wenn sie beide jedoch in gegenseitigem Einvernehmen und gemeinsamer Beratung (das Kind vorzeitig) entwöhnen wollen, so ist darin keine Sünde für sie (beide). Und wenn ihr eure Kinder (von einer Amme) stillen lassen wollt, so ist darin keine Sünde für euch, sofern ihr das, was ihr geben wollt, in rechtlicher Weise aushändigt. Und fürchtet Allah und wißt, daß Allah das, was ihr tut, wohl sieht!" });
                        surah2.VerseList.Add(new Verse { Number = 234, TextBosnian = "Oni od vas koji umru ostavljajući supruge svoje, njihove supruge dužne su čekati četiri mjeseca i deset dana. I kada one ispune propisani rok, vi ne snosite grijeh za ono što one lijepo i po propisu urade. A Allah u potpunosti zna ono što vi radite.", TextEnglish = "And those who are taken in death among you and leave wives behind - they, [the wives, shall] wait four months and ten [days]. And when they have fulfilled their term, then there is no blame upon you for what they do with themselves in an acceptable manner.[92] And Allāh is [fully] Aware of what you do.", TextGerman = "Und diejenigen von euch, die abberufen werden und Gattinnen hinterlassen - so sollen diese (mit sich) selbst vier Monate und zehn (Tage) abwarten. Wenn sie dann ihre festgesetzte Zeit erreicht haben, so ist für euch keine Sünde in dem, was sie in rechtlicher Weise mit sich selbst unternehmen. Allah ist dessen, was ihr tut, Kundig." });
                        surah2.VerseList.Add(new Verse { Number = 235, TextBosnian = "I nije vam grijeh ako tim ženama na znanje date da ćete ih vi zaprositi, ili ako to u dušama svojim krijete. Allah zna da ćete o njima misliti, ali im potajno ništa ne obećavajte, nego im samo što je dozvoljeno govorite. I ne odlučujte se na brak prije nego što propisano vrijeme za čekanje ne istekne; i imajte na umu da Allah zna šta je u dušama vašim, pa Ga se pričuvajte, i znajte da je Allah, zaista, Onaj Koji oprašta grijehe i Onaj Koji ne žuri s kaznom.", TextEnglish = "There is no blame upon you for that to which you [indirectly] allude concerning a proposal to women or for what you conceal within yourselves. Allāh knows that you will have them in mind. But do not promise them secretly except for saying a proper saying. And do not determine to undertake a marriage contract until the decreed period[93] reaches its end. And know that Allāh knows what is within yourselves, so beware of Him. And know that Allāh is Forgiving and Forbearing.", TextGerman = "Und es ist für euch keine Sünde darin, daß ihr den Frauen Andeutungen auf einen Heiratsantrag macht, oder daß ihr etwas (derartiges) in euch hegt. Allah weiß, daß ihr an sie denken werdet. Aber trefft nicht heimlich eine Abmachung mit ihnen, außer ihr sagt geziemende Worte. Und schließt nicht den Ehebund, bevor die vorgeschriebene Frist ihre festgesetzte Zeit erreicht hat. Wißt, daß Allah weiß, was in eurem Innersten ist. So seht euch vor Ihm vor! Und wißt, daß Allah Allvergebend und Nachsichtig ist." });
                        surah2.VerseList.Add(new Verse { Number = 236, TextBosnian = "Nije vam grijeh ako žene pustite prije nego u intimni odnos s njima stupite, ili prije nego što im vjenčani dar odredite. I lijepo ih i po propisu darujte, imućan prema svom stanju, a siromah prema svom; to je dužnost onima koji žele dobro činiti.", TextEnglish = "There is no blame upon you if you divorce women you have not touched[94] nor specified for them an obligation.[95] But give them [a gift of] compensation - the wealthy according to his capability and the poor according to his capability - a provision according to what is acceptable, a duty upon the doers of good.", TextGerman = "Es ist für euch keine Sünde darin, wenn ihr euch von Frauen scheidet, solange ihr sie noch nicht berührt oder euch ihnen gegenüber (zu einer Morgengabe) verpflichtet habt. Doch gewährt ihnen eine Abfindung - der Wohlhabende entsprechend seinen Verhältnissen und der Geringbemittelte entsprechend seinen Verhältnissen -, eine Abfindung in rechtlicher Weise. (Dies ist) eine Pflicht für die Gutes Tuenden." });
                        surah2.VerseList.Add(new Verse { Number = 237, TextBosnian = "A ako ih pustite prije nego što ste u odnos s njima stupili, a već ste im vjenčani dar odredili, one će zadržati polovinu onoga što ste odredili, osim ako se odreknu ili se odrekne onaj koji odlučuje o sklapanju braka; a ako se vi odreknete - to je bliže bogobojaznosti! I ne zaboravite da jedni prema drugima dobri budete! Allah, doista, dobro vidi šta vi radite.", TextEnglish = "And if you divorce them before you have touched them and you have already specified for them an obligation, then [give] half of what you specified - unless they forego the right or the one in whose hand is the marriage contract foregoes it. And to forego it is nearer to righteousness. And do not forget graciousness between you. Indeed Allāh, of whatever you do, is Seeing.", TextGerman = "Aber wenn ihr euch von ihnen scheidet, bevor ihr sie berührt und euch ihnen gegenüber schon (zu einer Morgengabe) verpflichtet habt, dann (händigt) die Hälfte dessen (aus), wozu ihr euch verpflichtet habt, es sei denn, daß sie (es) erlassen oder der, in dessen Hand der Ehebund ist. Und wenn ihr (es) erlaßt, kommt das der Gottesfurcht näher. Und versäumt es nicht, gut zueinander zu sein . Was ihr tut, sieht Allah wohl." });
                        surah2.VerseList.Add(new Verse { Number = 238, TextBosnian = "Čuvajte namaz, naročito srednji namaz, i pred Allahom ponizno stojte!", TextEnglish = "Maintain with care the [obligatory] prayers and [in particular] the middle [i.e., ʿaṣr] prayer and stand before Allāh, devoutly obedient.", TextGerman = "Haltet die Gebete ein, und (besonders) das mittlere Gebet, und steht demütig ergeben vor Allah." });
                        surah2.VerseList.Add(new Verse { Number = 239, TextBosnian = "Ako se budete bojali, onda klanjajte hodeći ili jašući! A kada budete sigurni, spominjite Allaha onako kako vas je On naučio onome šta niste znali.", TextEnglish = "And if you fear [an enemy, then pray] on foot or riding. But when you are secure, then remember Allāh [in prayer], as He has taught you that which you did not [previously] know.", TextGerman = "Wenn ihr in Furcht seid, dann (verrichtet das Gebet) zu Fuß oder im Reiten. Wenn ihr aber (wieder) in Sicherheit seid, dann gedenkt Allahs, wie Er euch gelehrt hat, was ihr nicht wußtet." });
                        surah2.VerseList.Add(new Verse { Number = 240, TextBosnian = "Oni među vama kojima se primiče smrt, a koji iza sebe ostavljaju supruge, treba da im oporukom, unaprijed za godinu dana, odrede izdržavanje i da se one ne udaljuju iz kuće. A ako je same napuste, vi ne snosite grijeh za ono što one lijepo i po propisu sa sobom učine. A Allah je silan i mudar!", TextEnglish = "And those who are taken in death among you and leave wives behind - for their wives is a bequest: maintenance for one year without turning [them] out. But if they leave [of their own accord], then there is no blame upon you for what they do with themselves in an acceptable way.[96] And Allāh is Exalted in Might and Wise.", TextGerman = "Diejenigen von euch, die abberufen werden und Gattinnen hinterlassen, sollen ihren Gattinnen eine Abfindung für ein Jahr vermachen, ohne daß sie aus (dem Haus) gewiesen werden. Wenn sie aber ausziehen, so liegt für euch keine Sünde in dem, was sie mit sich selbst an Geziemendem unternehmen. Allah ist Allmächtig und Allweise." });
                        surah2.VerseList.Add(new Verse { Number = 241, TextBosnian = "Puštenim ženama pripada otpremnina u skladu s propisom i na lijep način, i to je obaveza bogobojaznih.", TextEnglish = "And for divorced women is a provision according to what is acceptable - a duty upon the righteous.", TextGerman = "Und den geschiedenen Frauen steht eine Abfindung in rechtlicher Weise zu - eine Pflicht für die Gottesfürchtigen." });
                        surah2.VerseList.Add(new Verse { Number = 242, TextBosnian = "Allah vam tako objašnjava propise Svoje, da biste razmislili!", TextEnglish = "Thus does Allāh make clear to you His verses [i.e., laws] that you might use reason.", TextGerman = "So macht Allah euch Seine Zeichen klar, auf daß ihr begreifen möget." });
                        surah2.VerseList.Add(new Verse { Number = 243, TextBosnian = "Zar ne znaš za one koji su iz straha od smrti iz zemlje svoje pobjegli, a bijaše ih na hiljade, pa im je Allah rekao: 'Pomrite!', a poslije ih je oživio. Allah je, doista, darežljiv prema ljudima, ali većina ljudi ne zahvaljuje.", TextEnglish = "Have you not considered those who left their homes in many thousands, fearing death? Allāh said to them, 'Die'; then He restored them to life. And Allāh is the possessor of bounty for the people, but most of the people do not show gratitude.", TextGerman = "Siehst du nicht jene, die, um dem Tod zu entfliehen, zu Tausenden aus ihren Wohnstätten hinauszogen? Da sagte Allah zu ihnen: „Sterbt! Hierauf machte Er sie wieder lebendig. Allah ist wahrlich voll Huld gegen die Menschen. Aber die meisten Menschen sind nicht dankbar." });
                        surah2.VerseList.Add(new Verse { Number = 244, TextBosnian = "I borite se na Allahovu putu i znajte da je Allah Onaj Koji sve čuje i Onaj Koji sve zna.", TextEnglish = "And fight in the cause of Allāh and know that Allāh is Hearing and Knowing.", TextGerman = "Und kämpft auf Allahs Weg und wißt, daß Allah Allhörend und Allwissend ist." });
                        surah2.VerseList.Add(new Verse { Number = 245, TextBosnian = "Ko je taj koji će Allahu lijep zajam dati, pa da mu ga On mnogostruko vrati, Allah uskraćuje i obilno daje, i Njemu ćete se vratiti.", TextEnglish = "Who is it that would loan Allāh a goodly loan so He may multiply it for him many times over? And it is Allāh who withholds and grants abundance, and to Him you will be returned.", TextGerman = "Wer ist es denn, der Allah ein schönes Darlehen gibt? So vermehrt Er es ihm um ein Vielfaches. Allah hält zurück und gewährt, und zu Ihm werdet ihr zurückgebracht." });
                        surah2.VerseList.Add(new Verse { Number = 246, TextBosnian = "Zar ne znaš kada su prvaci sinova Israilovih poslije Musaa svome vjerovjesniku rekli: 'Pošalji nam kralja da se na Allahovom putu borimo!' 'Možda se vi nećete boriti, ako vam borba bude propisana?'', reče on. 'Zašto da se ne borimo na Allahovom putu?'', rekoše. 'A mi smo iz zemlje naše prognani i od sinova naših odvojeni!' A kada im borba bi propisana, oni, osim nekolicine, okrenuše se. A Allah dobro zna zulumćare.", TextEnglish = "Have you not considered the assembly of the Children of Israel after [the time of] Moses when they said to a prophet of theirs, 'Send to us a king, and we will fight in the way of Allāh'? He said, 'Would you perhaps refrain from fighting if battle was prescribed for you?' They said, 'And why should we not fight in the cause of Allāh when we have been driven out from our homes and from our children?' But when battle was prescribed for them, they turned away, except for a few of them. And Allāh is Knowing of the wrongdoers.", TextGerman = "Siehst du nicht die führende Schar von den Kindern Isra’ils nach Musa, als sie zu einem ihrer Propheten sagten: „Setze einen König für uns ein, damit wir auf Allahs Weg kämpfen. Er sagte: „Werdet ihr vielleicht, wenn euch zu kämpfen vorgeschrieben ist, doch nicht kämpfen? Sie sagten: „Warum sollten wir nicht auf Allahs Weg kämpfen, wo wir doch aus unseren Wohnstätten und von unseren Söhnen vertrieben worden sind? Doch als ihnen vorgeschrieben wurde zu kämpfen, kehrten sie sich - bis auf wenige von ihnen - ab. Und Allah weiß über die Ungerechten Bescheid." });
                        surah2.VerseList.Add(new Verse { Number = 247, TextBosnian = "Allah vam je Taluta za vladara poslao', reče im vjerovjesnik njihov. 'Odakle da nam on bude vladar kada smo mi priličniji od njega da vladamo? Njemu ni veliko bogatstvo nije dato!', rekoše oni. 'Doista je Allah njega da vam vlada izabrao', reče on, 'i velikim znanjem i snagom tjelesnom ga obdario. Allah daje vlast kome On hoće. Allah je neizmjerno darežljiv i Onaj Koji sve zna!", TextEnglish = "And their prophet said to them, 'Indeed, Allāh has sent to you Saul as a king.' They said, 'How can he have kingship over us while we are more worthy of kingship than him and he has not been given any measure of wealth?' He said, 'Indeed, Allāh has chosen him over you and has increased him abundantly in knowledge and stature. And Allāh gives His sovereignty to whom He wills. And Allāh is all-Encompassing [in favor] and Knowing.'", TextGerman = "Und ihr Prophet sagte zu ihnen: „Allah hat euch (hiermit) Talut als König geschickt. Sie sagten: „Wie sollte er die Herrschaft über uns haben, wo wir doch ein größeres Anrecht auf die Herrschaft haben, und ihm nicht Wohlstand gegeben ist? Er sagte: „Allah hat ihn vor euch auserwählt und ihm ein Übermaß an Wissen und körperlichen Vorzügen verliehen. Und Allah gibt Seine Herrschaft, wem Er will. Allah ist Allumfassend und Allwissend." });
                        surah2.VerseList.Add(new Verse { Number = 248, TextBosnian = "Zaista, znak njegove vlasti', reče im vjerovjesnik njihov, 'bit će kovčeg koji će vam stići. U njemu će biti smirenje za vas od Gospodara vašega i ostatak onoga što su Musa i Harun ostavili, nosit će ga meleci! U tome je, doista, znak za vas, ako ste vjernici!'", TextEnglish = "And their prophet said to them, 'Indeed, a sign of his kingship is that the chest will come to you in which is assurance[97] from your Lord and a remnant of what the family of Moses and the family of Aaron had left, carried by the angels. Indeed in that is a sign for you, if you are believers.'", TextGerman = "Und ihr Prophet sagte zu ihnen: „Das Zeichen seiner Herrschaft ist, daß die Bundeslade zu euch kommen wird; in ihr ist innere Ruhe von eurem Herrn und ein Rest von dem, was die Sippe Musas und die Sippe Haruns hinterließen, getragen von Engeln. Darin soll wahrlich ein Zeichen für euch sein, wenn ihr gläubig seid." });
                        surah2.VerseList.Add(new Verse { Number = 249, TextBosnian = "I kad Talut izađe s vojskom, reče: 'Allah će vas sigurno staviti na kušnju jednom rijekom, pa ko se napije iz nje, nije moj, a ko se ne napije, on je moj, izuzev ako šakom zahvati gutljaj!' I oni se, osim nekolicine, napiše, a kad je oni koji su s njim vjerovali prijeđoše, neki rekoše: 'Mi danas ne možemo izaći nakraj s Džalutom i vojskom njegovom.' Oni koji su čvrsto vjerovali da će susresti Allaha, rekoše: 'Koliko su puta malobrojne grupe savladale grupe mnogobrojne Allahovom dozvolom.' A Allah je sa strpljivima.", TextEnglish = "And when Saul went forth with the soldiers, he said, 'Indeed, Allāh will be testing you with a river. So whoever drinks from it is not of me, and whoever does not taste it is indeed of me, excepting one who takes [from it] in the hollow of his hand.' But they drank from it, except a [very] few of them. Then when he had crossed it along with those who believed with him, they said, 'There is no power for us today against Goliath and his soldiers.' But those who were certain that they would meet Allāh said, 'How many a small company has overcome a large company by permission of Allāh. And Allāh is with the patient.'", TextGerman = "Und als nun Talut mit den Heerscharen aufgebrochen war, sagte er: „Allah wird euch mit einem Fluß prüfen. Wer davon trinkt, gehört nicht zu mir. Und wer nicht davon kostet, der gehört zu mir, außer demjenigen, der (nur) eine Handvoll schöpft. Da tranken sie davon - bis auf wenige von ihnen. Und als er ihn überschritten hatte, er und diejenigen, mit ihm glaubten, sagten sie: „Wir haben heute keine Kraft gegen Galut und seine Heerscharen. Diejenigen aber, die glaubten, daß sie Allah begegnen würden, sagten: „Wie so manch eine geringe Schar hat schon mit Allahs Erlaubnis eine große Schar besiegt! Allah ist mit den Standhaften." });
                        surah2.VerseList.Add(new Verse { Number = 250, TextBosnian = "I kada iziđoše pred Džaluta i vojsku njegovu, oni rekoše: 'Gospodaru naš, nadahni nas izdržljivošću i učvrsti noge naše i pomozi nam protiv naroda koji ne vjeruje!'", TextEnglish = "And when they went forth to [face] Goliath and his soldiers, they said, 'Our Lord, pour upon us patience and plant firmly our feet and give us victory over the disbelieving people.'", TextGerman = "Und als sie gegen Galut und seine Heerscharen auf dem Plan erschienen, sagten sie: „Unser Herr, überschütte uns mit Standhaftigkeit, festige unsere Füße und verhilf uns zum Sieg über das ungläubige Volk!" });
                        surah2.VerseList.Add(new Verse { Number = 251, TextBosnian = "I oni ih, Allahovom dozvolom, poraziše, i Davud ubi Džaluta, i Allah mu dade vlast i mudrost, i nauči ga onome čemu je On htio. A da Allah ljude ne suzbija jedne drugima, na Zemlji bi doista nered nastao! Ali, Allah je darežljiv svim svjetovima.", TextEnglish = "So they defeated them by permission of Allāh, and David killed Goliath, and Allāh gave him the kingship and wisdom [i.e., prophethood] and taught him from that which He willed. And if it were not for Allāh checking [some] people by means of others, the earth would have been corrupted, but Allāh is the possessor of bounty for the worlds.", TextGerman = "Und so schlugen sie sie mit Allahs Erlaubnis, und Dawud tötete Galut. Und Allah gab ihm die Herrschaft und die Weisheit und lehrte ihn von dem, was Er wollte. Und wenn nicht Allah die einen Menschen durch die anderen zurückweisen würde, geriete die Erde wahrlich ins Verderben. Aber Allah ist voll Huld gegen die Weltenbewohner." });
                        surah2.VerseList.Add(new Verse { Number = 252, TextBosnian = "To su Allahovi ajeti, koje Mi tebi istinito kazujemo. A ti si, doista, jedan od poslanika.", TextEnglish = "These are the verses of Allāh which We recite to you, [O Muḥammad], in truth. And indeed, you are from among the messengers.", TextGerman = "Dies sind Allahs Zeichen, die Wir dir der Wahrheit entsprechend verlesen. Und du bist wahrlich einer der Gesandten." });
                        surah2.VerseList.Add(new Verse { Number = 253, TextBosnian = "Te poslanike, Mi smo odlikovali jedne nad drugim. S nekima od njih je i Allah govorio, a neke je za više stepeni uzdigao. A Isau, sinu Merjeminu, jasne smo dokaze dali i Duhom Blagoslovljenim ga podržali. Daje Allah htio, oni se ne bi međusobno poslije njih ubijali, kada su im jasni dokazi već došli, ali oni su se razišli: neki su od njih vjerovali, a neki su poricali. A da je Allah htio, oni se ne bi međusobno ubijali! Ali, Allah radi ono što On hoće.", TextEnglish = "Those messengers - some of them We caused to exceed others. Among them were those to whom Allāh spoke, and He raised some of them in degree. And We gave Jesus, the son of Mary, clear proofs, and We supported him with the Pure Spirit [i.e., Gabriel]. If Allāh had willed, those [generations] succeeding them would not have fought each other after the clear proofs had come to them. But they differed, and some of them believed and some of them disbelieved. And if Allāh had willed, they would not have fought each other, but Allāh does what He intends.", TextGerman = "Dies sind die Gesandten; einige von ihnen haben Wir vor anderen bevorzugt. Unter ihnen gibt es manche, zu denen Allah gesprochen hat, und einige, die Er um Rangstufen erhöht hat. Und ’Isa, dem Sohn Maryams, gaben Wir die klaren Beweise und stärkten ihn mit dem Heiligen Geist. Und wenn Allah gewollt hätte, hätten diejenigen nach ihnen nicht miteinander gekämpft, nachdem die klaren Beweise zu ihnen gekommen waren. Aber sie waren uneinig: Unter ihnen gab es manche, die glaubten und andere, die ungläubig waren. Und wenn Allah gewollt hätte, hätten sie nicht miteinander gekämpft. Doch Allah tut, was Er will." });
                        surah2.VerseList.Add(new Verse { Number = 254, TextBosnian = "O vi koji vjerujete, podijelite dio onoga čime vas Mi opskrbljujemo, prije nego što dođe Dan kada neće biti ni otkupa, ni prijateljstva, ni posredništva! A nevjernici su pravi zulumćari!", TextEnglish = "O you who have believed, spend from that which We have provided for you before there comes a Day in which there is no exchange [i.e., ransom] and no friendship and no intercession. And the disbelievers - they are the wrongdoers.", TextGerman = "O die ihr glaubt, gebt aus von dem, womit Wir euch versorgt haben, bevor ein Tag kommt, an dem es keinen Verkauf, keine Freundschaft und keine Fürsprache gibt! Die Ungläubigen sind die Ungerechten." });
                        surah2.VerseList.Add(new Verse { Number = 255, TextBosnian = "Allah je, nema boga osim Njega, Živi i Samoopstojeći Održavatelj! Ne obuzima Ga ni drijemež ni san! Njegovo je ono što je na nebesima i na Zemlji! Ko se može pred Njim zauzimati bez Njegovog dopuštenja?! On zna šta je bilo prije i šta će biti poslije njih i oni ne mogu obuhvatiti od Njegovoga znanja osim koliko On želi. Njegova Kursija obuhvata i nebesa i Zemlju i Njemu nije teško održavati ih! On je Svevišnji i Veličanstveni!", TextEnglish = "Allāh - there is no deity except Him, the Ever-Living,[98] the Self-Sustaining.[99] Neither drowsiness overtakes Him nor sleep. To Him belongs whatever is in the heavens and whatever is on the earth. Who is it that can intercede with Him except by His permission? He knows what is [presently] before them and what will be after them,[100] and they encompass not a thing of His knowledge except for what He wills. His Kursī[101] extends over the heavens and the earth, and their preservation tires Him not. And He is the Most High,[102] the Most Great.[103]", TextGerman = "Allah - es gibt keinen Gott außer Ihm, dem Lebendigen und Beständigen. Ihn überkommt weder Schlummer noch Schlaf. Ihm gehört (alles), was in den Himmeln und was auf der Erde ist. Wer ist es denn, der bei Ihm Fürsprache einlegen könnte - außer mit Seiner Erlaubnis? Er weiß, was vor ihnen und was hinter ihnen liegt, sie aber umfassen nichts von Seinem Wissen - außer, was Er will. Sein Thronschemel umfaßt die Himmel und die Erde, und ihre Behütung beschwert Ihn nicht. Er ist der Erhabene und Allgewaltige." });
                        surah2.VerseList.Add(new Verse { Number = 256, TextBosnian = "Nema prisile u vjeru, Pravi put se jasno razlikuje od zablude! Onaj ko ne vjeruje u tagute, a vjeruje u Allaha, drži se za najčvršću vezu, koja se neće prekinuti. A Allah je Onaj Koji sve čuje i Onaj Koji sve zna.", TextEnglish = "There shall be no compulsion in [acceptance of] the religion. The right course has become distinct from the wrong. So whoever disbelieves in ṭāghūt[104] and believes in Allāh has grasped the most trustworthy handhold with no break in it. And Allāh is Hearing and Knowing.", TextGerman = "Es gibt keinen Zwang im Glauben. (Der Weg der) Besonnenheit ist nunmehr klar unterschieden von (dem der) Verirrung. Wer also falsche Götter verleugnet, jedoch an Allah glaubt, der hält sich an der festesten Handhabe, bei der es kein Zerreißen gibt. Und Allah ist Allhörend und Allwissend." });
                        surah2.VerseList.Add(new Verse { Number = 257, TextBosnian = "Allah je zaštitnik onih koji vjeruju, izvodi ih iz tmina na svjetlo, a onima koji ne vjeruju zaštitnici su taguti, odvode ih sa svjetla u tmine! To su stanovnici Vatre i u njoj će vječno ostati.", TextEnglish = "Allāh is the Ally[105] of those who believe. He brings them out from darknesses into the light. And those who disbelieve - their allies are ṭāghūt. They take them out of the light into darknesses.[106] Those are the companions of the Fire; they will abide eternally therein.", TextGerman = "Allah ist der Schutzherr derjenigen, die glauben. Er bringt sie aus den Finsternissen heraus ins Licht. Diejenigen aber, die ungläubig sind, deren Schutzherren sind die falschen Götter. Sie bringen sie aus dem Licht hinaus in die Finsternisse. Das sind Insassen des (Höllen)feuers. Ewig werden sie darin bleiben." });
                        surah2.VerseList.Add(new Verse { Number = 258, TextBosnian = "Zar ne znaš za onoga koji se s Ibrahimom o Gospodaru njegovu prepirao, kada mu je Allah carstvo dao?! Kad Ibrahim reče: 'Gospodar moj je Onaj Koji život i smrt daje!', on odgovori: 'Ja život i smrt dajem!' Ibrahim reče: 'Allah čini da Sunce izlazi s istoka, pa učini ti da izađe sa zapada!' I nevjernik se zbuni. Allah, doista, zulumćare ne upućuje na Pravi put.", TextEnglish = "Have you not considered the one who argued with Abraham about his Lord [merely] because Allāh had given him kingship? When Abraham said, 'My Lord is the one who gives life and causes death,' he said, 'I give life and cause death.' Abraham said, 'Indeed, Allāh brings up the sun from the east, so bring it up from the west.' So the disbeliever was overwhelmed [by astonishment], and Allāh does not guide the wrongdoing people.", TextGerman = "Siehst du nicht jenen, der mit Ibrahim über seinen Herrn stritt, weil Allah ihm die Herrschaft gegeben hatte? (Damals) als Ibrahim sagte: „Mein Herr ist Derjenige, Der lebendig macht und sterben läßt. Er sagte: „Ich mache lebendig und lasse sterben. Ibrahim sagte: „Allah bringt ja die Sonne vom Osten her; so bringe du sie vom Westen her! Da war derjenige, der ungläubig war, verblüfft. Und Allah leitet nicht das ungerechte Volk recht." });
                        surah2.VerseList.Add(new Verse { Number = 259, TextBosnian = "Ili za onoga koji je, prolazeći pored jednoga naselja koje je bilo do temelja porušeno i pusto, povikao: 'Kako će Allah oživiti ove što su pomrli!' I Allah ga mrtvim učini stotinu godina, a zatim ga oživi i upita: 'Koliko si ostao?' On reče: 'Ostao sam dan ili dio dana!' Tada mu reče: 'Ne, ostao si stotinu godina! Pogledaj jelo svoje i piće svoje, nije se pokvarilo! A pogledaj i magarca svoga, to smo uradili da te učinimo znakom za ljude, a pogledaj i kosti, kako ih sastavljamo, a onda mesom oblažemo!' I kada njemu bi jasno, povika: 'Ja znam da Allah, doista, nad svime ima moć!'", TextEnglish = "Or [consider such an example] as the one who passed by a township which had fallen into ruin. He said, 'How will Allāh bring this to life after its death?' So Allāh caused him to die for a hundred years; then He revived him. He said, 'How long have you remained?' He [the man] said, 'I have remained a day or part of a day.' He said, 'Rather, you have remained one hundred years. Look at your food and your drink; it has not changed with time. And look at your donkey; and We will make you a sign for the people. And look at the bones [of this donkey] - how We raise them and then We cover them with flesh.' And when it became clear to him, he said, 'I know that Allāh is over all things competent.'", TextGerman = "Oder (kennst du nicht) einen ähnlichen, denjenigen, der an einer Stadt vorbeikam, die wüst in Trümmern lag? Er sagte: „Wie sollte Allah diese (Stadt) wieder lebendig machen, nachdem sie ausgestorben ist? Da ließ Allah ihn (für) hundert Jahre tot sein. Hierauf erweckte Er ihn und sagte: „Wie lange hast du verweilt? Er sagte: „Ich verweilte einen Tag oder den Teil eines Tages. Er sagte: „Nein! Vielmehr verweiltest du hundert Jahre. Nun schau deine Speise und dein Getränk an! Sie sind nicht verfault. Und schau deinen Esel an! - Und damit Wir dich zu einem Zeichen machen für die Menschen. Und schau die Knochen an, wie Wir sie zusammensetzen und sie hierauf mit Fleisch bekleiden! Nachdem es ihm klar geworden war, sagte er: „Ich weiß (jetzt), daß Allah zu allem die Macht hat." });
                        surah2.VerseList.Add(new Verse { Number = 260, TextBosnian = "A kada Ibrahim reče: 'Gospodaru moj, pokaži mi kako oživljuješ umrle?', On reče: 'Zar ne vjeruješ?!' 'Dakako, vjerujem', odgovori on, 'ali da mi se srce smiri!' Zatim mu reče: 'Pa, onda, uzmi četiri ptice, isijeci ih, pa po jedan dio njihov stavi na svaki brežuljak. Zatim ih pozovi, doći će ti žureći! Znaj da je Allah, zaista, silan i mudar.'", TextEnglish = "And [mention] when Abraham said, 'My Lord, show me how You give life to the dead.' [Allāh] said, 'Have you not believed?' He said, 'Yes, but [I ask] only that my heart may be satisfied.' [Allāh] said, 'Take four birds and commit them to yourself.[107] Then [after slaughtering them] put on each hill a portion of them; then call them - they will come [flying] to you in haste. And know that Allāh is Exalted in Might and Wise.'", TextGerman = "Und als Ibrahim sagte: „Mein Herr, zeige mir, wie Du die Toten lebendig machst! Er sagte: „Glaubst du immer noch nicht? Er sagte: „Doch, aber (ich frage,) damit mein Herz Ruhe findet. Er (Allah) sagte: „So nimm vier von den Vögeln und zieh sie dann her zu dir. Hierauf setze auf jeden Berg einen Teil von ihnen. Hierauf rufe sie, so werden sie zu dir herbeigeeilt kommen. Und wisse, daß Allah Allmächtig und Allweise ist." });
                        surah2.VerseList.Add(new Verse { Number = 261, TextBosnian = "Primjer onih koji imovinu svoju troše na Allahovom putu sličan je zrnu iz kojega nikne sedam klasova, a u svakom klasu po stotinu zrna. A Allah povećava kome On hoće. Allah je neizmjerno darežljiv i Onaj Koji sve zna.", TextEnglish = "The example of those who spend their wealth in the way of Allāh is like a seed [of grain] which grows seven spikes; in each spike is a hundred grains. And Allāh multiplies [His reward] for whom He wills. And Allāh is all-Encompassing and Knowing.", TextGerman = "Das Gleichnis derjenigen, die ihren Besitz auf Allahs Weg ausgeben, ist das eines Saatkorns, das sieben Ähren wachsen läßt, (und) in jeder Ähre hundert Körner. Allah vervielfacht, wem Er will. Und Allah ist Allumfassend und Allwissend." });
                        surah2.VerseList.Add(new Verse { Number = 262, TextBosnian = "Onima koji troše imetke svoje na Allahovom putu, a onda ono što potroše ne poprate prigovorima i uvredama, pripada nagrada Gospodara njihova. Njima nema straha niti će oni tugovati.", TextEnglish = "Those who spend their wealth in the way of Allāh and then do not follow up what they have spent with reminders [of it] or [other] injury will have their reward with their Lord, and there will be no fear concerning them, nor will they grieve.", TextGerman = "Diejenigen, die ihren Besitz auf Allahs Weg ausgeben und hierauf dem, was sie ausgegeben haben, weder Vorhaltungen noch Beleidigungen nachfolgen lassen, die haben ihren Lohn bei ihrem Herrn, und keine Furcht soll über sie kommen, noch sollen sie traurig sein." });
                        surah2.VerseList.Add(new Verse { Number = 263, TextBosnian = "Lijepa riječ i oprost vredniji su od sadake popraćene vrijeđanjem. A Allah je neovisan i Onaj Koji ne žuri s kaznom.", TextEnglish = "Kind speech and forgiveness are better than charity followed by injury. And Allāh is Free of need and Forbearing.", TextGerman = "Freundliche Worte und Vergebung sind besser als ein Almosen, dem Beleidigungen nachfolgen. Allah ist Unbedürftig und Nachsichtig." });
                        surah2.VerseList.Add(new Verse { Number = 264, TextBosnian = "O vi koji vjerujete, ne kvarite sadake svoje prigovorima i uvredama, kao što to čini onaj koji imetak svoj troši da bi se ljudima pokazao, a ne vjeruje u Allaha i ahiret. Primjer njegov je kao primjer glatke stijene - na kojoj ima zemlje, pa je pogodi veliki pljusak i ogoli je. Oni nemaju nikakve koristi od onoga što su stjecali. A Allah ne upućuje na Pravi put ljude koji ne vjeruju.", TextEnglish = "O you who have believed, do not invalidate your charities with reminders [of it] or injury as does one who spends his wealth [only] to be seen by the people and does not believe in Allāh and the Last Day. His example is like that of a [large] smooth stone upon which is dust and is hit by a downpour that leaves it bare. They are unable [to keep] anything of what they have earned. And Allāh does not guide the disbelieving people.", TextGerman = "O die ihr glaubt, macht nicht eure Almosen durch Vorhaltungen und Beleidigungen zunichte, wie derjenige, der seinen Besitz aus Augendienerei vor den Menschen ausgibt und nicht an Allah und den Jüngsten Tag glaubt! So ist sein Gleichnis das eines glatten Steins mit Erdreich darüber: Ein heftiger Regenguß trifft ihn und läßt ihn nackt. Sie haben keine Macht über etwas von dem, was sie erworben haben. Allah leitet das ungläubige Volk nicht recht." });
                        surah2.VerseList.Add(new Verse { Number = 265, TextBosnian = "Oni koji troše svoj imetak u želji za zadovoljstvom Allahovim i sigurni su u nagradu za to, slični su bašči na uzvišici na koju se izlila obilna kiša, pa daje dvostruki urod! A ako se na nju ne izlije obilna kiša, opet bude rose! Allah dobro vidi šta vi radite!", TextEnglish = "And the example of those who spend their wealth seeking means to the approval of Allāh and assuring [reward for] themselves is like a garden on high ground which is hit by a downpour - so it yields its fruits in double. And [even] if it is not hit by a downpour, then a drizzle [is sufficient]. And Allāh, of what you do, is Seeing.", TextGerman = "Das Gleichnis jener aber, die ihren Besitz im Trachten nach Allahs Zufriedenheit und aus ihrer Gewißheit ausgeben, ist das eines Gartens auf einer Anhöhe, den ein heftiger Regenguß trifft, und da bringt er seinen Ernteertrag zweifach (hervor). Und wenn ihn kein heftiger Regenguß trifft, so doch Sprühregen. Und was ihr tut, sieht Allah wohl." });
                        surah2.VerseList.Add(new Verse { Number = 266, TextBosnian = "Da li bi iko od vas volio da posjeduje bašču punu palmi i grožđa, kroz koju rijeke teku i u kojoj ima svakojakih plodova, a da je ostario i da ima nejaku djecu, a da je onda vatrena oluja zadesi i ona izgori?! Tako vam Allah objašnjava ajete i znakove Svoje, da biste razmislili.", TextEnglish = "Would one of you like to have a garden of palm trees and grapevines underneath which rivers flow in which he has from every fruit? But he is afflicted with old age and has weak [i.e., immature] offspring, and it is hit by a whirlwind containing fire and is burned. Thus does Allāh make clear to you [His] verses that you might give thought.", TextGerman = "Möchte einer von euch gern, daß er einen Garten habe mit Palmen und Rebstöcken, durcheilt von Bächen, in dem er von allen Früchten hat, während ihn (schon) hohes Alter getroffen hat und er (noch) schwache Nachkommenschaft hat, und daß ihn (den Garten) dann ein glühend heißer Wirbelwind treffe und er hierauf verbrenne? So macht Allah euch die Zeichen klar, auf daß ihr nachdenken möget!" });
                        surah2.VerseList.Add(new Verse { Number = 267, TextBosnian = "O vi koji vjerujete, dijelite od lijepih imetaka koje ste stekli, i od onoga što smo vam Mi iz Zemlje izveli! Ne izdvajajte ono što ne vrijedi da biste to udijelili, kada ni sami to ne biste primili, osim zatvorenih očiju! I znajte da je Allah, zaista, Neovisni i Hvaljeni.", TextEnglish = "O you who have believed, spend from the good things which you have earned and from that which We have produced for you from the earth. And do not aim toward the defective therefrom, spending [from that] while you would not take it [yourself] except with closed eyes. And know that Allāh is Free of need and Praiseworthy.", TextGerman = "O die ihr glaubt, gebt aus von den guten Dingen aus eurem Erworbenen und von dem, was Wir für euch aus der Erde hervorgebracht haben. Und sucht nicht zum Ausgeben das Schlechte davon aus, während ihr (selbst) es nicht nehmen würdet, ohne dabei ein Auge zuzudrücken. Und wißt, daß Allah Unbedürftig und Lobenswürdig ist." });
                        surah2.VerseList.Add(new Verse { Number = 268, TextBosnian = "Šejtan vas plaši siromaštvom i navodi vas na zlo, a Allah vam obećava oprost i dobro. Allah je neizmjerno darežljiv i Onaj Koji sve zna.", TextEnglish = "Satan threatens you with poverty and orders you to immorality, while Allāh promises you forgiveness from Him and bounty. And Allāh is all-Encompassing and Knowing.", TextGerman = "Der Satan verspricht euch Armut und befiehlt euch Schändliches. Allah aber verspricht euch Vergebung von Sich aus und Huld. Allah ist Allumfassend und Allwissend." });
                        surah2.VerseList.Add(new Verse { Number = 269, TextBosnian = "On daje mudrost kome hoće, a onome kome je mudrost data, dato je veliko dobro! A samo razumom obdareni prisjećaju se i pouku prihvataju.", TextEnglish = "He gives wisdom[108] to whom He wills, and whoever has been given wisdom has certainly been given much good. And none will remember except those of understanding.", TextGerman = "Er gibt Weisheit, wem Er will; und wem Weisheit gegeben wurde, dem wurde da viel Gutes gegeben. Aber nur diejenigen bedenken, die Verstand besitzen." });
                        surah2.VerseList.Add(new Verse { Number = 270, TextBosnian = "Za sve što potrošite ili se zavjetujete Allah sigurno zna! A zulumćarima nema pomagača!", TextEnglish = "And whatever you spend of expenditures or make of vows - indeed, Allāh knows of it. And for the wrongdoers there are no helpers.", TextGerman = "Und was immer ihr an Spenden ausgebt oder als Gelübde gelobt, Allah weiß es. Und die Ungerechten werden keine Helfer haben." });
                        surah2.VerseList.Add(new Verse { Number = 271, TextBosnian = "Ako javno dajete sadake to je lijepo, ali je za vas bolje ako to prikrijete i date siromasima, i On će pokriti neka vaša loša djela. A Allah dobro zna ono što vi radite.", TextEnglish = "If you disclose your charitable expenditures, they are good; but if you conceal them and give them to the poor, it is better for you, and He will remove from you some of your misdeeds [thereby]. And Allāh, of what you do, is [fully] Aware.", TextGerman = "Wenn ihr Almosen offen zeigt, so ist es trefflich. Wenn ihr sie aber verbergt und den Armen gebt, so ist es besser für euch, und Er (Allah) wird etwas von euren bösen Taten tilgen. Allah ist dessen, was ihr tut, Kundig." });
                        surah2.VerseList.Add(new Verse { Number = 272, TextBosnian = "Što se upute njihove tiče, ne ovisi to o tebi, jer Allah izvodi na Pravi put koga On hoće. Dobro koje podijelite vaše je, a ono što dijelite, dijelite samo u želji za licem Allahovim. A ono što od dobara udijelite, to će vam biti nadoknađeno i neće vam zulum biti učinjen.", TextEnglish = "Not upon you, [O Muḥammad], is [responsibility for] their guidance, but Allāh guides whom He wills. And whatever good you [believers] spend is for yourselves, and you do not spend except seeking the face [i.e., approval] of Allāh. And whatever you spend of good[109] - it will be fully repaid to you, and you will not be wronged.", TextGerman = "Nicht dir obliegt ihre Rechtleitung, sondern Allah leitet recht, wen Er will. Was immer ihr an Gutem ausgebt, (das) ist für euch selbst. Und ihr gebt nur im Trachten nach Allahs Angesicht aus. Und was immer ihr an Gutem ausgebt, wird euch in vollem Maß zukommen, und es wird euch kein Unrecht zugefügt." });
                        surah2.VerseList.Add(new Verse { Number = 273, TextBosnian = "To pripada siromasima koji su iznureni na Allahovom putu, pa ne mogu hoditi po zemlji; onaj ko ne zna misli da su imućni zbog sustezljivosti njihove. Poznat ćeš ih po izgledu njihovu, jer oni ne dodijavaju ljudima proseći. A za ono što od dobara podijelite, Allah, doista, dobro zna.", TextEnglish = "[Charity is] for the poor who have been restricted for the cause of Allāh, unable to move about in the land. An ignorant [person] would think them self-sufficient because of their restraint, but you will know them by their [characteristic] sign. They do not ask people persistently [or at all]. And whatever you spend of good - indeed, Allāh is Knowing of it.", TextGerman = "(Gebt am besten aus) für die Armen, die auf Allahs Weg daran gehindert werden, im Lande umherreisen zu können. Der Unwissende hält sie wegen ihrer Zurückhaltung für unbedürftig. Du erkennst sie an ihrem Merkmal: Sie betteln die Menschen nicht aufdringlich an. Und was immer ihr an Gutem ausgebt, so weiß Allah darüber Bescheid." });
                        surah2.VerseList.Add(new Verse { Number = 274, TextBosnian = "Onima koji dijele imovinu svoju noću i danju, tajno i javno, pripada nagrada kod Gospodara njihova. I za njih nema straha, niti će se oni žalostiti.", TextEnglish = "Those who spend their wealth [in Allāh's way] by night and by day, secretly and publicly - they will have their reward with their Lord. And no fear will there be concerning them, nor will they grieve.", TextGerman = "Diejenigen, die ihren Besitz bei Nacht und Tag, heimlich oder öffentlich ausgeben, haben ihren Lohn bei ihrem Herrn, und keine Furcht soll sie überkommen, noch werden sie traurig sein." });
                        surah2.VerseList.Add(new Verse { Number = 275, TextBosnian = "Oni koji se kamatom hrane dići će se iz grobova svojih baš kao oni koje je šejtan dodirom izbezumio. To je zato što su govorili: 'Trgovina je, doista, isto što i kamata!' A Allah je dozvolio trgovinu, a zabranio kamatu. Onome do koga dopre pouka Gospodara njegova, pa se toga okani, pripada ono što je prije stekao a njegov je slučaj Allahu prepušten. Oni, pak, koji opet to učine, bit će stanovnici Vatre u kojoj će vječno ostati.", TextEnglish = "Those who consume interest[110] cannot stand [on the Day of Resurrection] except as one stands who is being beaten by Satan into insanity. That is because they say, 'Trade is [just] like interest.' But Allāh has permitted trade and has forbidden interest. So whoever has received an admonition from his Lord and desists may have what is past, and his affair rests with Allāh. But whoever returns [to dealing in interest or usury] - those are the companions of the Fire; they will abide eternally therein.", TextGerman = "Diejenigen, die Zins verschlingen, werden nicht anders aufstehen als jemand, den der Satan durch Wahnsinn hin und her schlägt. Dies (wird sein), weil sie sagten: „Verkaufen ist das gleiche wie Zinsnehmen. Doch hat Allah Verkaufen erlaubt und Zinsnehmen verboten. Zu wem nun eine Ermahnung von seinem Herrn kommt, und der dann aufhört, dem soll gehören, was vergangen ist, und seine Angelegenheit steht bei Allah. Wer aber rückfällig wird, jene sind Insassen des (Höllen)feuers. Ewig werden sie darin bleiben." });
                        surah2.VerseList.Add(new Verse { Number = 276, TextBosnian = "Allah uništava kamatu, a umnožava sadake. Allah ne voli ni jednog nevjernika, grešnika.", TextEnglish = "Allāh destroys interest and gives increase for charities. And Allāh does not like every sinning disbeliever.", TextGerman = "Dahinschwinden lassen wird Allah den Zins und vermehren die Almosen. Allah liebt niemanden, der ein beharrlicher Ungläubiger und Sünder ist." });
                        surah2.VerseList.Add(new Verse { Number = 277, TextBosnian = "Doista će oni koji vjeruju, i dobra djela čine, i namaz obavljaju, i zekat daju, imati nagradu u Gospodara svoga. Za njih nema straha i oni ne treba da se žaloste!", TextEnglish = "Indeed, those who believe and do righteous deeds and establish prayer and give zakāh will have their reward with their Lord, and there will be no fear concerning them, nor will they grieve.", TextGerman = "Gewiß, diejenigen, die glauben und rechtschaffene Werke tun, das Gebet verrichten und die Abgabe entrichten, die haben ihren Lohn bei ihrem Herrn, und keine Furcht soll sie überkommen, noch werden sie traurig sein." });
                        surah2.VerseList.Add(new Verse { Number = 278, TextBosnian = "O vi koji vjerujete, bojte se Allaha i ostavite što je preostalo od kamate, ako ste vjernici.", TextEnglish = "O you who have believed, fear Allāh and give up what remains [due to you] of interest, if you should be believers.", TextGerman = "O die ihr glaubt, fürchtet Allah und laßt das sein, was an Zins(geschäften) noch übrig ist, wenn ihr gläubig seid." });
                        surah2.VerseList.Add(new Verse { Number = 279, TextBosnian = "Ako to ne učinite, navješćuje vam se rat od Allaha i Poslanika Njegova! A ako se pokajete, pripadaju vam glavnice imetaka vaših; nećete nepravdu učiniti, niti će vama zulum biti učinjen.", TextEnglish = "And if you do not, then be informed of a war [against you] from Allāh and His Messenger. But if you repent, you may have your principal - [thus] you do no wrong, nor are you wronged.", TextGerman = "Wenn ihr es aber nicht tut, dann laßt euch Krieg von Allah und Seinem Gesandten ansagen! Doch wenn ihr bereut, dann steht euch euer (ausgeliehenes) Grundvermögen zu; (so) tut weder ihr Unrecht, noch wird euch Unrecht zugefügt." });
                        surah2.VerseList.Add(new Verse { Number = 280, TextBosnian = "A ako je dužnik u nevolji, onda pričekajte dok mu bude lakše; a da to date kao sadaku bolje vam je, ako znate.", TextEnglish = "And if someone is in hardship, then [let there be] postponement until [a time of] ease. But if you give [from your right as] charity, then it is better for you, if you only knew.", TextGerman = "Und wenn er (der Schuldner) in Schwierigkeiten ist, dann sei (ihm) Aufschub (gewährt,) bis eine Erleichterung (eintritt). Und daß ihr (es) als Almosen erlaßt, ist besser für euch, wenn ihr (es) nur wißt." });
                        surah2.VerseList.Add(new Verse { Number = 281, TextBosnian = "I bojte se Dana u kome ćete Allahu vraćeni biti, zatim će svakome što je zaslužio isplaćeno biti. I nikome zulum neće biti učinjen.", TextEnglish = "And fear a Day when you will be returned to Allāh. Then every soul will be compensated for what it earned, and they will not be wronged [i.e., treated unjustly].", TextGerman = "Und hütet euch vor einem Tag, an dem ihr zu Allah zurückgebracht werdet. Dann wird jeder Seele in vollem Maß zukommen, was sie verdient hat, und es wird ihnen kein Unrecht zugefügt." });
                        surah2.VerseList.Add(new Verse { Number = 282, TextBosnian = "O vi koji vjerujete, kada jedan od drugoga pozajmljujete do određenoga roka, zapišite to. Neka jedan pisar između vas to pravedno napiše i neka se pisar ne susteže napisati onako kako ga je Allah poučio. Neka on piše, a neka mu kazuje dužnik! Neka se boji Allaha, Gospodara svoga, i ništa od toga neka ne izostavi. A ako je dužnik umno ograničen ili slab, ili ako nije u stanju kazivati, onda neka kazuje njegov staratelj i to pravedno. I uzmite dva svjedoka između vas, pa ako ne nađete dva muškarca, onda jednog muškarca i dvije žene sa kojima ste kao svjedocima zadovoljni. Ako jedna od njih dvije pogrešno šta kaže, neka je druga podsjeti. I neka se svjedoci ne ustežu, ako budu pozvani, da dođu. Neka vam ne bude mrsko da to zapišete s rokom vraćanja, bio dug mali ili veliki! To vam je kod Allaha ispravnije i za svjedočenje jače, i da ne sumnjate bolje, izuzev ako je riječ o svakodnevnoj trgovini koju obavljate među vama, jer onda vam nije grijeh da to ne zapišete. Uzmite svjedoke i kada kupoprodaju zaključujete, i neka ne bude oštećen ni pisar ni svjedok. A ako to učinite - oštetite ih, onda je to vaš grijeh. I bojte se Allaha i Allah će vas poučiti! Allah sve dobro zna.", TextEnglish = "O you who have believed, when you contract a debt for a specified term, write it down. And let a scribe write [it] between you in justice. Let no scribe refuse to write as Allāh has taught him. So let him write and let the one who has the obligation [i.e., the debtor] dictate. And let him fear Allāh, his Lord, and not leave anything out of it. But if the one who has the obligation is of limited understanding or weak or unable to dictate himself, then let his guardian dictate in justice. And bring to witness two witnesses from among your men. And if there are not two men [available], then a man and two women from those whom you accept as witnesses - so that if one of them [i.e., the women] errs, then the other can remind her. And let not the witnesses refuse when they are called upon. And do not be [too] weary to write it, whether it is small or large, for its [specified] term. That is more just in the sight of Allāh and stronger as evidence and more likely to prevent doubt between you, except when it is an immediate transaction which you conduct among yourselves. For [then] there is no blame upon you if you do not write it. And take witnesses when you conclude a contract. Let no scribe be harmed or any witness. For if you do so, indeed, it is [grave] disobedience in you. And fear Allāh. And Allāh teaches you. And Allāh is Knowing of all things.", TextGerman = "O die ihr glaubt, wenn ihr auf eine festgesetzte Frist, einer vom anderen, eine Geldschuld aufnehmt, dann schreibt es auf. Und ein Schreiber soll (es) für euch gerecht aufschreiben. Und kein Schreiber soll sich weigern zu schreiben, so wie Allah (es) ihn gelehrt hat. So soll er denn schreiben, und diktieren soll der Schuldner, und er soll Allah, seinen Herrn, fürchten und nichts davon schmälern. Wenn aber der Schuldner töricht oder schwach ist oder unfähig, selbst zu diktieren, so soll sein Sachwalter (es) gerecht diktieren. Und bringt zwei Männer von euch als Zeugen. Wenn es keine zwei Männer sein (können), dann sollen es ein Mann und zwei Frauen sein, mit denen als Zeugen ihr zufrieden seid, - damit, wenn eine von beiden sich irrt, eine die andere erinnere. Und die Zeugen sollen sich nicht weigern, wenn sie aufgefordert werden. Und seid nicht abgeneigt, es - (seien es) klein(e) oder groß(e Beträge) - mit seiner (vereinbarten) Frist aufzuschreiben! Das ist gerechter vor Allah und richtiger für das Zeugnis und eher geeignet, daß ihr nicht zweifelt; es sei denn, es ist ein sofortiger Handel, den ihr unter euch tätigt. Dann ist es keine Sünde für euch, wenn ihr es nicht aufschreibt. Und nehmt Zeugen, wenn ihr untereinander einen Verkauf abschließt. Und kein Schreiber oder Zeuge soll zu Schaden kommen. Wenn ihr (es) aber (dennoch) tut, so ist es ein Frevel von euch. Und fürchtet Allah! Und Allah lehrt euch. Allah weiß über alles Bescheid." });
                        surah2.VerseList.Add(new Verse { Number = 283, TextBosnian = "Ako ste na putu i ne nađete pisara, onda neka se uzme zalog. A ako jedan drugome nešto povjerite, neka onaj kome je povjereno, to povjerenje ispuni i neka se boji Allaha, Gospodara svoga! I ne skrivajte svjedočenje, jer ko ga uskrati, srce njegovo je, doista, grešno. A Allah dobro zna ono što vi radite.", TextEnglish = "And if you are on a journey and cannot find a scribe, then a security deposit [should be] taken. And if one of you entrusts another, then let him who is entrusted discharge his trust [faithfully] and let him fear Allāh, his Lord. And do not conceal testimony, for whoever conceals it - his heart is indeed sinful, and Allāh is Knowing of what you do.", TextGerman = "Und wenn ihr auf einer Reise seid und keinen Schreiber findet, dann sollen Pfänder in Empfang genommen werden. Und wenn dann einer von euch dem anderen (etwas) anvertraut, so soll derjenige, dem (es) anvertraut wurde, das ihm anvertraute Pfand (wieder) aushändigen, und er soll Allah, seinen Herrn, fürchten. Und verheimlicht kein Zeugnis. Wer es aber verheimlicht, dessen Herz ist gewiß sündhaft. Und Allah weiß über das, was ihr tut, Bescheid." });
                        surah2.VerseList.Add(new Verse { Number = 284, TextBosnian = "Allahu pripada sve što je na nebesima i što je na Zemlji. Pokazivali vi šta je u dušama vašim ili skrivali, Allah će vas za to pitati, pa će oprostiti kome hoće, a kazniti koga hoće. A Allah nad svime ima moć.", TextEnglish = "To Allāh belongs whatever is in the heavens and whatever is in the earth. Whether you show what is within yourselves or conceal it, Allāh will bring you to account for it. Then He will forgive whom He wills and punish whom He wills, and Allāh is over all things competent.", TextGerman = "Allah gehört (alles), was in den Himmeln und was auf der Erde ist. Und ob ihr offenlegt, was in euch selbst ist, oder es verbergt, Allah wird euch dafür zur Rechenschaft ziehen. Dann vergibt Er, wem Er will, und straft, wen Er will. Und Allah hat zu allem die Macht." });
                        surah2.VerseList.Add(new Verse { Number = 285, TextBosnian = "Poslanik vjeruje u ono što mu se objavljuje od Gospodara njegova, a i vjernici, svaki od njih vjeruje u Allaha, i meleke Njegove, i Knjige Njegove, i poslanike Njegove: 'Mi među poslanicima Njegovim ne pravimo razliku!' I govore: 'Čujemo i pokoravamo se! Molimo Te za oprost, Gospodaru naš, a kod Tebe je konačno odredište!'", TextEnglish = "The Messenger has believed in what was revealed to him from his Lord, and[so have] the believers. All of them have believed in Allāh and His angels and His books and His messengers, [saying], 'We make no distinction between any of His messengers.' And they say, 'We hear and we obey. [We seek] Your forgiveness, our Lord, and to You is the [final] destination.'", TextGerman = "Der Gesandte(Allahs) glaubt an das, was zu ihm von seinem Herrn(als Offenbarung) herabgesandt worden ist, und ebenso die Gläubigen; alle glauben an Allah, Seine Engel, Seine Bücher und Seine Gesandten - Wir machen keinen Unterschied bei jemandem von Seinen Gesandten. Und sie sagen: „Wir hören und gehorchen. (Gewähre uns) Deine Vergebung, unser Herr! Und zu Dir ist der Ausgang." });
                        surah2.VerseList.Add(new Verse { Number = 286, TextBosnian = "Allah svaku osobu obavezuje samo onoliko kolike su njene mogućnosti; njoj u korist je ono dobro koje zaradi, a na njenu štetu je ono zlo koje sebi privrijedi. Gospodaru naš, nemoj nas kazniti ako zaboravimo ili nehotice pogriješimo! Gospodaru naš, ne tovari na nas breme kao što si ga natovario na one prije nas! Gospodaru naš, ne opterećuj nas onim za što nemamo snage! Preko grijeha naših prijeđi, oprosti nam i smiluj nam se! Ti si Gospodar naš, pa nas pomozi protiv naroda koji ne vjeruje!", TextEnglish = "Allāh does not charge a soul except [with that within] its capacity. It will have [the consequence of] what [good] it has gained, and it will bear [the consequence of] what [evil] it has earned. 'Our Lord, do not impose blame upon us if we have forgotten or erred. Our Lord, and lay not upon us a burden like that which You laid upon those before us. Our Lord, and burden us not with that which we have no ability to bear. And pardon us; and forgive us; and have mercy upon us. You are our protector, so give us victory over the disbelieving people.'[111]", TextGerman = "Allah erlegt keiner Seele mehr auf, als sie zu leisten vermag. Ihr kommt (nur) zu, was sie verdient hat, und angelastet wird ihr (nur), was sie verdient hat. „Unser Herr, belange uns nicht, wenn wir (etwas) vergessen oder einen Fehler begehen. Unser Herr, lege uns keine Bürde auf, wie Du sie denjenigen vor uns auferlegt hast. Unser Herr, bürde uns nichts auf, wozu wir keine Kraft haben. Verzeihe uns, vergib uns und erbarme Dich unser! Du bist unser Schutzherr. So verhilf uns zum Sieg über das ungläubige Volk!" });
                        result = surah2;
                        break;
                    #endregion

                    // 
                    #region 3 Ali Imran
                    case 3:
                        Surah surah3 = new Surah(); surah3.Name = Constants.Surah3Name; surah3.Number = 3; surah3.VerseList = new List<Verse>();
                        surah3.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "" });
                        surah3.VerseList.Add(new Verse { Number = 1, TextBosnian = "Elif-lam-mim.", TextEnglish = "Alif, Lām, Meem.[113]", TextGerman = "Alif-Lam-Mim." });
                        surah3.VerseList.Add(new Verse { Number = 2, TextBosnian = "Allah, osim Koga drugog boga nema, Živi je i Samoopstojeći Održavatelj.", TextEnglish = "Allāh - there is no deity except Him, the Ever-Living, the Self-Sustaining.[114]", TextGerman = "Allah - es gibt keinen Gott außer Ihm, dem Lebendigen und Beständigen." });
                        surah3.VerseList.Add(new Verse { Number = 3, TextBosnian = "On tebi objavljuje Knjigu s Istinom, koja potvrđuje prethodne, a i Tevrat i Indžil je objavio,", TextEnglish = "He has sent down upon you, [O Muḥammad], the Book in truth, confirming what was before it. And He revealed the Torah and the Gospel", TextGerman = "Er hat dir das Buch mit der Wahrheit offenbart, das zu bestätigen, was vor ihm (offenbart) war. Und Er hat (auch) die Tora und das Evangelium (als Offenbarung) herabgesandt," });
                        surah3.VerseList.Add(new Verse { Number = 4, TextBosnian = "još prije kao putokaz ljudima, a spustio je i Furkan! - ono što rastavlja istinu od zablude. Onima koji u Allahove ajete i znakove ne vjeruju, pripada teška patnja, a Allah je silan i žestoko kažnjava.", TextEnglish = "Before, as guidance for the people. And He revealed the Criterion [i.e., the Qur’ān]. Indeed, those who disbelieve in the verses of Allāh will have a severe punishment, and Allāh is Exalted in Might, the Owner of Retribution.[115]", TextGerman = "zuvor, als Rechtleitung für die Menschen. Und Er hat die Unterscheidung herabgesandt. Gewiß, diejenigen, die Allahs Zeichen verleugnen, für sie wird es strenge Strafe geben. Allah ist Allmächtig und Besitzer von Vergeltungsgewalt." });
                        surah3.VerseList.Add(new Verse { Number = 5, TextBosnian = "Allahu, doista, ništa nije skriveno, ni na Zemlji, ni na nebu.", TextEnglish = "Indeed, from Allāh nothing is hidden in the earth nor in the heaven.", TextGerman = "Vor Allah ist nichts verborgen, weder auf der Erde noch im Himmel." });
                        surah3.VerseList.Add(new Verse { Number = 6, TextBosnian = "On je Onaj Koji vas oblikuje u matericama kako On hoće; nema drugog boga osim Njega, Silnog i Mudrog.", TextEnglish = "It is He who forms you in the wombs however He wills. There is no deity except Him, the Exalted in Might, the Wise.", TextGerman = "Er ist es, Der euch im Mutterleib gestaltet, wie Er will. Es gibt keinen Gott außer Ihm, dem Allmächtigen und Allweisen." });
                        surah3.VerseList.Add(new Verse { Number = 7, TextBosnian = "On je Onaj Koji ti objavljuje Knjigu u kojoj su ajeti jasni; oni su matica Knjige; drugi nisu sasvim jasni. Oni u čijim je srcima zastranjenost slijede one što nisu sasvim jasni u težnji za smutnjom i svojevoljnim tumačenjem. A tumačenje njihovo zna samo Allah. Oni, pak, koji su u znanje duboko pronikli, kažu: 'Mi u to vjerujemo; sve je to od našega Gospodara.' A prisjećaju se samo oni koji razum imaju.", TextEnglish = "It is He who has sent down to you, [O Muḥammad], the Book; in it are verses [that are] precise - they are the foundation of the Book - and others unspecific.[116] As for those in whose hearts is deviation [from truth], they will follow that of it which is unspecific, seeking discord and seeking an interpretation [suitable to them]. And no one knows its [true] interpretation except Allāh. But those firm in knowledge say, 'We believe in it. All [of it] is from our Lord.' And no one will be reminded except those of understanding.", TextGerman = "Er ist es, Der das Buch (als Offenbarung) auf dich herabgesandt hat. Dazu gehören eindeutige Verse - sie sind der Kern des Buches - und andere, mehrdeutige. Was aber diejenigen angeht, in deren Herzen (Neigung zum) Abschweifen ist, so folgen sie dem, was davon mehrdeutig ist, im Trachten nach Irreführung und im Trachten nach ihrer Mißdeutung. Aber niemand weiß ihre Deutung außer Allah. Und diejenigen, die im Wissen fest gegründet sind, sagen: „Wir glauben daran; alles ist von unserem Herrn. Aber nur diejenigen bedenken, die Verstand besitzen." });
                        surah3.VerseList.Add(new Verse { Number = 8, TextBosnian = "Gospodaru naš, nemoj skrenuti srca naša nakon što si nas uputio i daruj nam Svoju milost! Ti si, doista, Onaj Koji obilno daruje!", TextEnglish = "[Who say], 'Our Lord, let not our hearts deviate after You have guided us and grant us from Yourself mercy. Indeed, You are the Bestower.[117]", TextGerman = "Unser Herr, lasse unsere Herzen nicht abschweifen, nachdem Du uns rechtgeleitet hast, und schenke uns Erbarmen von Dir aus. Du bist ja der unablässig Schenkende." });
                        surah3.VerseList.Add(new Verse { Number = 9, TextBosnian = "Gospodaru naš, Ti ćeš, doista, sakupiti ljude na Dan u koji nema sumnje! Allah, doista, ne krši obećanje Svoje!'", TextEnglish = "Our Lord, surely You will gather the people for a Day about which there is no doubt. Indeed, Allāh does not fail in His promise.'", TextGerman = "Unser Herr, Du wirst die Menschen gewiß zu einem Tag versammeln, an dem es keinen Zweifel gibt. Allah bricht nicht, was Er versprochen hat." });
                        surah3.VerseList.Add(new Verse { Number = 10, TextBosnian = "Onima koji ne vjeruju, kod Allaha sigurno neće koristiti imeci njihovi, a ni djeca njihova, i oni su gorivo za Vatru.", TextEnglish = "Indeed, those who disbelieve - never will their wealth or their children avail them against Allāh at all. And it is they who are fuel for the Fire.", TextGerman = "Gewiß, denjenigen, die ungläubig sind, werden weder ihr Besitz noch ihre Kinder vor Allah etwas nützen. Sie sind es, die Brennstoff des (Höllen)feuers sein werden." });
                        surah3.VerseList.Add(new Verse { Number = 11, TextBosnian = "Kao što su to i faraonovi ljudi, te oni prije njih koji su znakove naše lažnim smatrali, pa ih je Allah zbog grijeha njihovih kaznio. A Allah žestoko kažnjava.", TextEnglish = "[Theirs is] like the custom of the people of Pharaoh and those before them. They denied Our signs, so Allāh seized them for their sins. And Allāh is severe in penalty.", TextGerman = "Nach der Art der Leute Fir’auns und derjenigen vor ihnen: Sie erklärten Unsere Zeichen für Lüge, und da ergriff sie Allah für ihre Sünden. Und Allah ist streng im Bestrafen." });
                        surah3.VerseList.Add(new Verse { Number = 12, TextBosnian = "Reci onima koji ne vjeruju: 'Bit ćete pobijeđeni i u Džehennemu okupljeni, a užasno je to boravište!'", TextEnglish = "Say to those who disbelieve, 'You will be overcome and gathered together to Hell, and wretched is the resting place.'", TextGerman = "Sag zu denjenigen, die ungläubig sind: Ihr werdet besiegt und zur Hölle zusammengetrieben werden - eine schlimme Lagerstatt!" });
                        surah3.VerseList.Add(new Verse { Number = 13, TextBosnian = "Vi ste imali znak u dvjema grupama koje su se sukobile: jednoj koja se borila na Allahovom putu, i drugoj, nevjerničkoj. Oni su ih vidjeli dvostruko brojnijim od sebe. A Allah Svojom pomoći podržava koga On hoće! To je, doista, pouka za one koji su razboriti.", TextEnglish = "Already there has been for you a sign in the two armies which met [in combat at Badr] - one fighting in the cause of Allāh and another of disbelievers. They saw them [to be] twice their [own] number by [their] eyesight.[118] But Allāh supports with His victory whom He wills. Indeed in that is a lesson for those of vision.", TextGerman = "Ihr hattet ja ein Zeichen in zwei Scharen, die aufeinandertrafen: Die eine kämpfte auf Allahs Weg und eine andere, ungläubige. Sie sahen sie dem Augenschein nach zweimal so viel, wie sie (selbst waren). Und Allah stärkt mit Seiner Hilfe, wen Er will. Darin ist wahrlich eine Lehre für diejenigen, die Einsicht besitzen." });
                        surah3.VerseList.Add(new Verse { Number = 14, TextBosnian = "Ljudima je uljepšana ljubav prema strastima; ženama, sinovima, gomilama zlata i srebra, rasnim konjima, stoci i zemlji za obrađivanje. To su užici ovosvjetskoga života, a najljepše je mjesto povratka kod Allaha.", TextEnglish = "Beautified for people is the love of that which they desire - of women and sons, heaped-up sums of gold and silver, fine branded horses, and cattle and tilled land. That is the enjoyment of worldly life, but Allāh has with Him the best return [i.e., Paradise].", TextGerman = "Ausgeschmückt ist den Menschen die Liebe zu den Begierden, nach Frauen, Söhnen, aufgehäuften Mengen von Gold und Silber, Rassepferden, Vieh und Saatfeldern. Das ist der Genuß im diesseitigen Leben. Doch bei Allah ist die schöne Heimstatt." });
                        surah3.VerseList.Add(new Verse { Number = 15, TextBosnian = "Reci: 'Hoćete li da vas obavijestim šta je bolje od toga?' Oni koji se budu uščuvali, kod Gospodara svoga imat će džennetske bašče kroz koje rijeke teku, u kojima će vječno boraviti, i supruge očišćene i zadovoljstvo od Allaha. Allah dobro vidi robove Svoje.", TextEnglish = "Say, 'Shall I inform you of [something] better than that? For those who fear Allāh will be gardens in the presence of their Lord beneath which rivers flow, wherein they abide eternally, and purified spouses and approval from Allāh. And Allāh is Seeing [i.e., aware] of [His] servants -", TextGerman = "Sag: Soll ich euch von etwas Besserem als diesem Kunde geben? Für diejenigen, die gottesfürchtig sind, werden bei ihrem Herrn Gärten sein, durcheilt von Bächen, ewig darin zu bleiben, und vollkommen gereinigte Gattinnen und Wohlgefallen von Allah. Allah sieht die Menschen wohl," });
                        surah3.VerseList.Add(new Verse { Number = 16, TextBosnian = "To su oni koji govore: 'Gospodaru naš, mi doista vjerujemo, pa oprosti nam grijehe naše i sačuvaj nas od patnje u Vatri!'", TextEnglish = "Those who say, 'Our Lord, indeed we have believed, so forgive us our sins and protect us from the punishment of the Fire,'", TextGerman = "die sagen: „Unser Herr, gewiß, wir glauben. Darum vergib uns unsere Sünden und bewahre uns vor der Strafe des (Höllen)feuers." });
                        surah3.VerseList.Add(new Verse { Number = 17, TextBosnian = "Strpljivi, i iskreni, i Allahu poslušni, i koji od imetka dijele, i koji u osvit zore za oprost mole.", TextEnglish = "The patient, the true, the obedient, those who spend [in the way of Allāh], and those who seek forgiveness before dawn.", TextGerman = "Die Standhaften und die Wahrhaftigen, die demütig Ergebenen und diejenigen, die ausgeben, und die im letzten Teil der Nacht um Vergebung Bittenden." });
                        surah3.VerseList.Add(new Verse { Number = 18, TextBosnian = "Allah svjedoči da nema drugog boga osim Njega, a i meleci i učeni, postupajući pravedno. Nema boga osim Njega, Silnog i Mudrog.", TextEnglish = "Allāh witnesses that there is no deity except Him, and [so do] the angels and those of knowledge - [that He is] maintaining [creation] in justice. There is no deity except Him, the Exalted in Might, the Wise.", TextGerman = "Allah bezeugt, daß es keinen Gott gibt außer Ihm; und (ebenso bezeugen) die Engel und diejenigen, die Wissen besitzen; der Wahrer der Gerechtigkeit. Es gibt keinen Gott außer Ihm, dem Allmächtigen und Allweisen." });
                        surah3.VerseList.Add(new Verse { Number = 19, TextBosnian = "Vjera je kod Allaha, doista, islam! A oni kojima je data Knjiga podvojili su se baš onda kada im je došlo znanje, i to iz međusobne zlobe. A onaj ko ne vjeruje u ajete i znakove Allahove - pa, Allah, zaista, brzo sviđa račune.", TextEnglish = "Indeed, the religion in the sight of Allāh is Islām. And those who were given the Scripture did not differ except after knowledge had come to them - out of jealous animosity between themselves. And whoever disbelieves in the verses of Allāh, then indeed, Allāh is swift in [taking] account.", TextGerman = "Gewiß, die Religion ist bei Allah der Islam. Doch diejenigen, denen die Schrift gegeben wurde, wurden erst uneinig, nachdem das Wissen zu ihnen gekommen war - aus Mißgunst untereinander, Doch wer Allahs Zeichen verleugnet -, so ist Allah schnell im Abrechnen." });
                        surah3.VerseList.Add(new Verse { Number = 20, TextBosnian = "Ako se oni budu prepirali s tobom, reci: 'Ja se samo Allahu predajem, kao i oni koji me slijede!' I reci onima kojima je data Knjiga i nepismenima: 'Da li ste islam primili?' Pa ako prime islam, onda su oni na Pravom putu, a ako odbiju, tvoje je jedino da obznaniš. A Allah dobro vidi robove Svoje.", TextEnglish = "So if they argue with you, say, 'I have submitted myself to Allāh [in Islām], and [so have] those who follow me.' And say to those who were given the Scripture and [to] the unlearned,[119] 'Have you submitted yourselves?' And if they submit [in Islām], they are rightly guided; but if they turn away - then upon you is only the [duty of] notification. And Allāh is Seeing of [His] servants.", TextGerman = "Und wenn sie mit dir streiten (wollen), dann sag: „Ich habe mein Gesicht Allah ergeben, und (ebenso,) wer mir folgt! Und sag zu jenen, denen die Schrift gegeben wurde, und den Schriftunkundigen: „Seid ihr (Allah) ergeben? Wenn sie (Ihm) ergeben sind, dann sind sie rechtgeleitet. Kehren sie sich aber ab, so obliegt dir nur die Übermittelung (der Botschaft). Allah sieht die Menschen wohl." });
                        surah3.VerseList.Add(new Verse { Number = 21, TextBosnian = "Onima koji ne vjeruju u znakove Allahove i koji vjerovjesnike ubijaju neopravdano, i ubijaju one koji traže da se po pravdi postupa prema ljudima, navijesti bolnu patnju.", TextEnglish = "Those who disbelieve in the signs of Allāh and kill the prophets without right and kill those who order justice from among the people - give them tidings of a painful punishment.", TextGerman = "Diejenigen, die Allahs Zeichen verleugnen, die Propheten zu Unrecht töten und diejenigen unter den Menschen töten, die Gerechtigkeit befehlen, denen verkünde schmerzhafte Strafe." });
                        surah3.VerseList.Add(new Verse { Number = 22, TextBosnian = "To su oni čija djela su bezvrijedna i na dunjaluku i na ahiretu, i kojima niko neće u pomoć priteći.", TextEnglish = "They are the ones whose deeds have become worthless in this world and the Hereafter, and for them there will be no helpers.", TextGerman = "Das sind diejenigen, deren Werke im Diesseits und Jenseits hinfällig werden. Und sie haben keine Helfer." });
                        surah3.VerseList.Add(new Verse { Number = 23, TextBosnian = "Zar ne vidiš one kojima je dat udio u Knjizi?! Pozivaju se Allahovoj Knjizi da im se po njoj sudi, a zatim se neki od njih, okrećući se od toga, udaljavaju.", TextEnglish = "Do you not consider, [O Muḥammad], those who were given a portion of the Scripture? They are invited to the Scripture of Allāh that it should arbitrate between them;[120] then a party of them turns away, and they are refusing.", TextGerman = "Siehst du nicht jene, denen ein Teil der Schrift gegeben wurde? Sie werden zum Buch Allahs aufgefordert, damit es zwischen ihnen richte. Hierauf kehrt sich eine Gruppe von ihnen ab, und sie sind Widerstrebende." });
                        surah3.VerseList.Add(new Verse { Number = 24, TextBosnian = "To je zato što oni govore: 'Vatra nas neće ni dodirnuti više od nekoliko dana!', i u njihovoj vjeri ih zavarava to što su izmišljali.", TextEnglish = "That is because they say, 'Never will the Fire touch us except for [a few] numbered days,' and [because] they were deluded in their religion by what they were inventing.", TextGerman = "Dies, weil sie sagen: „Das (Höllen)feuer wird uns nur für eine bestimmte Anzahl von Tagen berühren. Was sie (an Lügen) zu ersinnen pflegten, hat sie über ihre Religion getäuscht." });
                        surah3.VerseList.Add(new Verse { Number = 25, TextBosnian = "A šta će biti kada ih sakupimo onoga Dana u koji nema sumnje, i kada svako dobije ono što je zaslužio? A njima zulum neće biti učinjen.", TextEnglish = "So how will it be when We assemble them for a Day about which there is no doubt? And each soul will be compensated [in full for] what it earned, and they will not be wronged.", TextGerman = "Aber wie (wird es sein), wenn Wir sie zu einem Tag versammeln, an dem es keinen Zweifel gibt, und jeder Seele in vollem Maß zukommt, was sie verdient hat, und ihnen kein Unrecht zugefügt wird?" });
                        surah3.VerseList.Add(new Verse { Number = 26, TextBosnian = "Reci: 'Allahu moj! Koji svu vlast imaš! Ti vlast daješ kome hoćeš, a oduzimaš od koga Ti hoćeš! Ti uzvisuješ koga hoćeš, a unizuješ koga hoćeš! U Tvojoj ruci je dobro! Ti, doista, nad svime imaš moć.'", TextEnglish = "Say, 'O Allāh, Owner of Sovereignty, You give sovereignty to whom You will and You take sovereignty away from whom You will. You honor whom You will and You humble whom You will. In Your hand[121] is [all] good. Indeed, You are over all things competent.", TextGerman = "Sag: O Allah, Herr der Herrschaft, Du gibst die Herrschaft, wem Du willst, und Du entziehst die Herrschaft, wem Du willst. Du machst mächtig, wen Du willst, und Du erniedrigst, wen Du willst. In Deiner Hand ist (all) das Gute. Gewiß, Du hast zu allem die Macht." });
                        surah3.VerseList.Add(new Verse { Number = 27, TextBosnian = "Ti uvodiš noć u dan i uvodiš dan u noć! Živo izvodiš iz mrtvog i mrtvo izvodiš iz živog! Ti opskrbljuješ koga hoćeš, bez ikakva računa!", TextEnglish = "You cause the night to enter the day, and You cause the day to enter the night; and You bring the living out of the dead, and You bring the dead out of the living. And You give provision to whom You will without account [i.e., limit or measure].'", TextGerman = "Du läßt die Nacht in den Tag eindringen und läßt den Tag eindringen in die Nacht. Und Du läßt das Lebendige aus dem Toten hervorgehen und läßt hervorgehen das Tote aus dem Lebendigen. Und Du versorgst, wen Du willst, ohne zu berechnen." });
                        surah3.VerseList.Add(new Verse { Number = 28, TextBosnian = "Neka vjernici za prisne prijatelje i zaštitnike ne uzimaju nevjernike mimo vjernika, a ko to čini, s Allahom nema ništa! Učinite to samo ako se time od njih štitite! Allah vas na Sebe upozorava, a kod Allaha je konačno odredište.", TextEnglish = "Let not believers take disbelievers as allies [i.e., supporters or protectors] rather than believers. And whoever [of you] does that has nothing [i.e., no association] with Allāh, except when taking precaution against them in prudence.[122] And Allāh warns you of Himself, and to Allāh is the [final] destination.", TextGerman = "Die Gläubigen sollen nicht die Ungläubigen anstatt der Gläubigen zu Schutzherren nehmen. Wer das tut, hat überhaupt nichts mit Allah (gemeinsam) -, es sei denn, daß ihr euch (durch dieses Verhalten) vor ihnen wirklich schützt. Allah mahnt euch zur Vorsicht vor Sich selbst. Und zu Allah ist der Ausgang." });
                        surah3.VerseList.Add(new Verse { Number = 29, TextBosnian = "Reci: 'Skrivali vi ono što je u prsima vašim ili pokazivali, Allah to zna. On zna sve što je na nebesima i što je na Zemlji. Allah nad svime ima moć!'", TextEnglish = "Say, 'Whether you conceal what is in your breasts or reveal it, Allāh knows it. And He knows that which is in the heavens and that which is on the earth. And Allāh is over all things competent.", TextGerman = "Sag: Ob ihr verbergt, was in euren Brüsten ist oder es offenlegt, Allah weiß es; Er weiß, was in den Himmeln und was auf der Erde ist. Und Allah hat zu allem die Macht." });
                        surah3.VerseList.Add(new Verse { Number = 30, TextBosnian = "Onoga dana kada svako ugleda pred sobom dobro koje je uradio, a i zlo isto tako, poželjet će da je između njih i njega udaljenost velika. A Allah vas na Sebe upozorava i Allah je milosrdan prema robovima Svojim.", TextEnglish = "The Day every soul will find what it has done of good present [before it] and what it has done of evil, it will wish that between itself and that [evil] was a great distance. And Allāh warns you of Himself, and Allāh is Kind to [His] servants.'", TextGerman = "An dem Tag wird jede Seele das, was sie an Gutem getan hat, bereit finden. Und von dem, was sie an Bösem getan hat, hätte sie gern, wenn zwischen ihr und ihm ein weiter Abstand wäre. Und Allah mahnt euch zur Vorsicht vor Sich selbst. Allah ist Gnädig zu den Menschen." });
                        surah3.VerseList.Add(new Verse { Number = 31, TextBosnian = "Reci: 'Ako vi Allaha volite, onda mene slijedite, i vas će Allah voljeti i grijehe vam oprostiti! A Allah je Onaj Koji oprašta grijehe i milostiv je.'", TextEnglish = "Say, [O Muḥammad], 'If you should love Allāh, then follow me, [so] Allāh will love you and forgive you your sins. And Allāh is Forgiving and Merciful.'", TextGerman = "Sag: Wenn ihr Allah liebt, dann folgt mir. So liebt euch Allah und vergibt euch eure Sünden. Allah ist Allvergebend und Barmherzig." });
                        surah3.VerseList.Add(new Verse { Number = 32, TextBosnian = "Reci: 'Pokoravajte se Allahu i Poslaniku!' A ako se oni okrenu, Allah, doista, ne voli nevjernike.", TextEnglish = "Say, 'Obey Allāh and the Messenger. But if you turn away - then indeed, Allāh does not like the disbelievers.'", TextGerman = "Sag: Gehorcht Allah und dem Gesandten. Doch wenn sie sich abkehren, so liebt Allah die Ungläubigen nicht." });
                        surah3.VerseList.Add(new Verse { Number = 33, TextBosnian = "Doista je Allah odabrao Adema, i Nuha, i porodicu Ibrahimovu, i porodicu Imranovu nad drugim svjetovima,", TextEnglish = "Indeed, Allāh chose Adam and Noah and the family of Abraham and the family of ʿImrān over the worlds -", TextGerman = "Gewiß, Allah hat Adam und Nuh und die Sippe Ibrahims und die Sippe ’Imrans vor den (anderen) Weltenbewohnern auserwählt," });
                        surah3.VerseList.Add(new Verse { Number = 34, TextBosnian = "kao potomstvo, jedne od drugih. Allah je Onaj Koji sve čuje i Onaj Koji sve zna.", TextEnglish = "Descendants, some of them from others. And Allāh is Hearing and Knowing.", TextGerman = "eine der anderen Nachkommenschaft. Und Allah ist Allhörend und Allwissend." });
                        surah3.VerseList.Add(new Verse { Number = 35, TextBosnian = "Kada Imranova žena reče: 'Gospodaru moj, ovo što je u trbuhu mome ja zaista zavjetujem Tebi na službu, pa primi to od mene, zbilja si Ti Svečujući i Sveznajući.'", TextEnglish = "[Mention, O Muḥammad], when the wife of ʿImrān said, 'My Lord, indeed I have pledged to You what is in my womb, consecrated [for Your service], so accept this from me. Indeed, You are the Hearing, the Knowing.'", TextGerman = "Als ’Imrans Frau sagte: „Mein Herr, ich gelobe Dir, was in meinem Mutterleib ist, für Deinen Dienst freigestellt. So nimm (es) von mir an! Du bist ja der Allhörende und Allwissende." });
                        surah3.VerseList.Add(new Verse { Number = 36, TextBosnian = "A nakon što je rodila, ona reče: 'Gospodaru moj, rodila sam žensko!' - a Allah najbolje zna šta je ona rodila, 'a muško nije kao žensko.':' Dala sam joj ime Merjema. Ja nju i porod njen stavljam pod Tvoju zaštitu od prokletog šejtana'", TextEnglish = "But when she delivered her, she said, 'My Lord, I have delivered a female.' And Allāh was most knowing of what she delivered, and the male is not like the female. 'And I have named her Mary, and I seek refuge for her in You and [for] her descendants from Satan, the expelled [from the mercy of Allāh].'", TextGerman = "Als sie sie dann zur Welt gebracht hatte, sagte sie: „Mein Herr, ich habe ein Mädchen zur Welt gebracht. Und Allah wußte sehr wohl, was sie zur Welt gebracht hatte, und der Knabe ist nicht wie das Mädchen. „Ich habe sie Maryam genannt, und ich stelle sie und ihre Nachkommenschaft unter Deinen Schutz vor dem gesteinigten Satan." });
                        surah3.VerseList.Add(new Verse { Number = 37, TextBosnian = "Pa je Gospodar njezin lijepo primi, i učini da lijepo izraste i da se o njoj Zekerijja brine. Kad god bi joj Zekerijja u mihrab ušao, kod nje bi hrane našao. 'Odakle ti ovo, Merjema?', pitao bi on, a ona bi odgovorila: 'To je od Allaha!' Allah, doista, opskrbljuje koga hoće, bez ikakva računa.", TextEnglish = "So her Lord accepted her with good acceptance and caused her to grow in a good manner and put her in the care of Zechariah. Every time Zechariah entered upon her in the prayer chamber, he found with her provision. He said, 'O Mary, from where is this [coming] to you?' She said, 'It is from Allāh. Indeed, Allāh provides for whom He wills without account.'", TextGerman = "Da nahm ihr Herr sie auf gütigste Art an und ließ sie auf schöne Weise heranwachsen und gab sie Zakariyya zur Betreuung. Jedesmal, wenn Zakariyya zu ihr in die Zelle trat, fand er bei ihr Versorgung. Er sagte: „O Maryam, woher hast du das? Sie sagte: „Es kommt von Allah; Allah versorgt, wen Er will, ohne zu berechnen." });
                        surah3.VerseList.Add(new Verse { Number = 38, TextBosnian = "Tu Zekerijja zamoli Gospodara svoga: 'Gospodaru moj, podari mi od Sebe čestita potomka, Ti doista molbu čuješ!'", TextEnglish = "At that, Zechariah called upon his Lord, saying, 'My Lord, grant me from Yourself a good offspring. Indeed, You are the Hearer of supplication.'", TextGerman = "Da rief Zakariyya seinen Herrn an und sagte: „Mein Herr, schenke mir von Dir aus gute Nachkommenschaft! Du bist ja der Gebetserhörer." });
                        surah3.VerseList.Add(new Verse { Number = 39, TextBosnian = "I dok se on, stojeći u odaji, molio, meleci ga pozvaše i rekoše: 'Allah te raduje Jahjaom koji će vjerovati u onog koji je Riječ Allahova, prvakom, čednim čovjekom i vjerovjesnikom, potomkom čestitih!'", TextEnglish = "So the angels called him while he was standing in prayer in the chamber, 'Indeed, Allāh gives you good tidings of John, confirming a word[123] from Allāh and [who will be] honorable, abstaining [from women], and a prophet from among the righteous.'", TextGerman = "Und da riefen ihm die Engel zu, während er betend in der Zelle stand: Allah verkündet dir Yahya, ein Wort von Allah zu bestätigen, einen Herrn, einen Keuschen und Propheten von den Rechtschaffenen." });
                        surah3.VerseList.Add(new Verse { Number = 40, TextBosnian = "Gospodaru moj', reče on, 'odakle ću ja imati sina kada me starost ophrvala, a i žena mi je nerotkinja?' 'Eto tako', reče On, 'Allah čini što On hoće.'", TextEnglish = "He said, 'My Lord, how will I have a boy when I have reached old age and my wife is barren?' He [the angel] said, 'Such is Allāh; He does what He wills.'", TextGerman = "Er sagte: „Mein Herr, wie soll ich einen Jungen bekommen, wo mich schon hohes Alter überkommen hat und meine Frau unfruchtbar ist? Er (, der Engel,) sagte: „So (wird es sein); Allah tut, was Er will." });
                        surah3.VerseList.Add(new Verse { Number = 41, TextBosnian = "Gospodaru moj', zamoli Zekerijja, 'daj mi neki znak!' 'Znak će biti', reče on, 'što tri dana s ljudima nećeš govoriti, osim išaretorn! I mnogo spominji Gospodara svoga i hvali Ga krajem dana i rano ujutro.'", TextEnglish = "He said, 'My Lord, make for me a sign.' He said, 'Your sign is that you will not [be able to] speak to the people for three days except by gesture. And remember your Lord much and exalt [Him with praise] in the evening and the morning.'", TextGerman = "Er (Zakariyya) sagte: „Mein Herr, setze mir ein Zeichen. Er sagte: „Dein Zeichen ist, daß du drei Tage lang nicht zu den Menschen sprechen wirst außer durch Gebärden. Und gedenke deines Herrn häufig und preise (Ihn) am Abend und am frühen Morgen!" });
                        surah3.VerseList.Add(new Verse { Number = 42, TextBosnian = "I kada meleci kazaše: 'Merjema, tebe je, doista, Allah odabrao i čistom učinio! On te je nad ženama svih svjetova odabrao!'", TextEnglish = "And [mention] when the angels said, 'O Mary, indeed Allāh has chosen you and purified you and chosen you above the women of the worlds.", TextGerman = "Und als die Engel sagten: „O Maryam, Allah hat dich auserwählt und dich rein gemacht und dich auserwählt vor den Frauen der (anderen) Weltenbewohner!" });
                        surah3.VerseList.Add(new Verse { Number = 43, TextBosnian = "Merjema, budi skrušena i pokorna Gospodaru svome, i sedždu čini, a i pregibaj se s onima koji se u namazu pregibaju!'", TextEnglish = "O Mary, be devoutly obedient to your Lord and prostrate and bow with those who bow [in prayer].'", TextGerman = "O Maryam, sei deinem Herrn demütig ergeben, wirf dich nieder und verbeuge dich zusammen mit den sich Verbeugenden." });
                        surah3.VerseList.Add(new Verse { Number = 44, TextBosnian = "To su neke od vijesti o onome što čulima nije dokučivo, a koje ti objavljujemo. Ti nisi bio među njima kada su pera svoja pobacali da bi vidjeli koji će se o Merjemi brinuti, a nisi bio među njima ni kada su se prepirali.", TextEnglish = "That is from the news of the unseen which We reveal to you, [O Muḥammad]. And you were not with them when they cast their pens[124] as to which of them should be responsible for Mary. Nor were you with them when they disputed.", TextGerman = "Dies gehört zu den Nachrichten vom Verborgenen, das Wir dir (als Offenbarung) eingeben. Denn du warst nicht bei ihnen, als sie ihre Rohre warfen (, um durch das Los zu bestimmen), wer von ihnen Maryam betreuen sollte. Und du warst nicht bei ihnen, als sie miteinander stritten." });
                        surah3.VerseList.Add(new Verse { Number = 45, TextBosnian = "I kada meleci rekoše: 'Merjema, Allah te obveseljava onim koji je Riječ Njegova, čije je ime Mesih, Isa, sin Merjemin, ugledan i na dunjaluku i na ahiretu i jedan od Allahu bliskih!", TextEnglish = "[And mention] when the angels said, 'O Mary, indeed Allāh gives you good tidings of a word[125] from Him, whose name will be the Messiah, Jesus, the son of Mary - distinguished in this world and the Hereafter and among those brought near [to Allāh].", TextGerman = "Als die Engel sagten: „O Maryam, Allah verkündet dir ein Wort von Ihm, dessen Name al-Masih ’Isa, der Sohn Maryams ist, angesehen im Diesseits und Jenseits und einer der (Allah) Nahegestellten." });
                        surah3.VerseList.Add(new Verse { Number = 46, TextBosnian = "On će govoriti ljudima i u kolijevci, i kao odrastao, i on je jedan od dobrih!'", TextEnglish = "He will speak to the people in the cradle and in maturity and will be of the righteous.'", TextGerman = "Und er wird in der Wiege zu den Menschen sprechen und im Mannesalter und einer der Rechtschaffenen sein." });
                        surah3.VerseList.Add(new Verse { Number = 47, TextBosnian = "Ona reče: 'Gospodaru moj, kako ću ja imati dijete kada me muškarac nije dodirnuo?!' 'Eto, tako', reče, 'Allah stvara šta On hoće. Kada nešto odredi, On samo kaže: 'Budi!' i to biva.'", TextEnglish = "She said, 'My Lord, how will I have a child when no man has touched me?' [The angel] said, 'Such is Allāh; He creates what He wills. When He decrees a matter, He only says to it, 'Be,' and it is.", TextGerman = "Sie sagte: „Mein Herr, wie sollte ich ein Kind haben, wo mich (doch) kein menschliches Wesen berührt hat? Er (, der Engel,) sagte: „So (wird es sein); Allah erschafft, was Er will. Wenn Er eine Angelegenheit bestimmt, so sagt Er zu ihr nur: ,Sei!’ und so ist sie." });
                        surah3.VerseList.Add(new Verse { Number = 48, TextBosnian = "On će ga poučiti Knjizi i mudrosti, i Tevratu i Indžilu.", TextEnglish = "And He will teach him writing and wisdom[126] and the Torah and the Gospel", TextGerman = "Und Er wird ihn die Schrift, die Weisheit, die Tora und das Evangelium lehren." });
                        surah3.VerseList.Add(new Verse { Number = 49, TextBosnian = "I poslat će ga kao poslanika sinovima Israilovim: 'Donio sam vam znak od Gospodara vašega: oblikovat ću vam od ilovače nešto poput ptice, i u nju puhnuti, pa će, Allahovom dozvolom, ptica biti; iscijelit ću slijepa od rođenja i gubava, i oživljavat ću mrtve dozvolom Allahovom; i kazivat ću vam šta jedete i šta u domovima vašim pohranjujete. U svemu tome ima dokaza za vas, ako vjernici budete.", TextEnglish = "And [make him] a messenger to the Children of Israel, [who will say], 'Indeed I have come to you with a sign from your Lord in that I design for you from clay [that which is] like the form of a bird, then I breathe into it and it becomes a bird by permission of Allāh. And I cure the blind [from birth] and the leper, and I give life to the dead - by permission of Allāh. And I inform you of what you eat and what you store in your houses. Indeed in that is a sign for you, if you are believers.", TextGerman = "Und (Er wird ihn schicken) als einen Gesandten zu den Kindern Isra’ils (, zu denen er sagen wird): ,Gewiß, ich bin ja mit einem Zeichen von eurem Herrn zu euch gekommen: daß ich euch aus Lehm (etwas) schaffe, (was so aussieht) wie die Gestalt eines Vogels, und dann werde ich ihm einhauchen, und da wird es ein (wirklicher) Vogel sein. Und ich werde mit Allahs Erlaubnis den Blindgeborenen und den Weißgefleckten heilen und werde Tote mit Allahs Erlaubnis wieder lebendig machen. Und ich werde euch kundtun, was ihr eßt und was ihr in euren Häusern aufspeichert. Darin ist wahrlich ein Zeichen für euch, wenn ihr gläubig seid." });
                        surah3.VerseList.Add(new Verse { Number = 50, TextBosnian = "Potvrđujući tako istinitost Tevrata, objavljenog prije mene, i dopuštajući nešto što vam je bilo zabranjeno, ja vam donosim dokaz od Gospodara vašega. Pa Allaha se bojte, a meni pokorni budite!", TextEnglish = "And [I have come] confirming what was before me of the Torah and to make lawful for you some of what was forbidden to you. And I have come to you with a sign from your Lord, so fear Allāh and obey me.", TextGerman = "Und das zu bestätigen, was von der Tora vor mir (offenbart) war, und um euch einiges von dem zu erlauben, was euch verboten war. Und ich bin mit einem Zeichen von eurem Herrn zu euch gekommen; so fürchtet Allah und gehorcht mir!" });
                        surah3.VerseList.Add(new Verse { Number = 51, TextBosnian = "Allah je doista i moj i vaš Gospodar i Njemu ibadet činite. To je Pravi put!", TextEnglish = "Indeed, Allāh is my Lord and your Lord, so worship Him. That is the straight path.''", TextGerman = "Gewiß, Allah ist mein Herr und euer Herr; so dient Ihm. Das ist ein gerader Weg’." });
                        surah3.VerseList.Add(new Verse { Number = 52, TextBosnian = "I kada je Isa osjetio da oni ne vjeruju, rekao je: 'Ko su pomagači moji na putu prema Allahu?' 'Mi smo pomagači Allahovi', rekoše učenici, 'mi u Allaha vjerujemo, a ti budi svjedok da smo mi muslimani - predani.'", TextEnglish = "But when Jesus felt [persistence in] disbelief from them, he said, 'Who are my supporters for [the cause of] Allāh?' The disciples said, 'We are supporters for Allāh. We have believed in Allāh and testify that we are Muslims [submitting to Him].", TextGerman = "Als ’Isa aber ihren Unglauben wahrnahm, sagte er: „Wer sind meine Helfer (auf dem Weg hin) zu Allah? Die Jünger sagten: „Wir sind Allahs Helfer. Wir glauben an Allah, und bezeuge, daß wir (Ihm) ergeben sind!" });
                        surah3.VerseList.Add(new Verse { Number = 53, TextBosnian = "Gospodaru naš, mi vjerujemo u ono što si Ti objavio, mi Poslanika slijedimo, pa upiši nas među svjedoke!", TextEnglish = "Our Lord, we have believed in what You revealed and have followed the messenger [i.e., Jesus], so register us among the witnesses [to truth].'", TextGerman = "Unser Herr, wir glauben an das, was Du (als Offenbarung) herabgesandt hast, und folgen dem Gesandten. So schreibe uns auf unter die Zeugnis Ablegenden!" });
                        surah3.VerseList.Add(new Verse { Number = 54, TextBosnian = "A oni su lukavstvo smišljali, pa je i Allah njima lukavstvo priredio. A Allah najbolje lukavstvo priređuje.", TextEnglish = "And they [i.e., the disbelievers] planned, but Allāh planned. And Allāh is the best of planners.", TextGerman = "Und sie schmiedeten Ränke, und (auch) Allah schmiedete Ränke; und Allah ist der beste Ränkeschmied." });
                        surah3.VerseList.Add(new Verse { Number = 55, TextBosnian = "I kada Allah reče: 'Isa, Ja ću ti dušu u snu uzeti i k Sebi te uzdignuti! Očistiti te od nevjernika, i učinit ću da oni koji te slijede budu iznad onih koji ne vjeruju do Kijametskog dana. Zatim ćete se Meni vratiti, pa ću Ja presuditi o onome u čemu ste se razilazili.", TextEnglish = "[Mention] when Allāh said, 'O Jesus, indeed I will take you and raise you to Myself and purify [i.e., free] you from those who disbelieve and make those who follow you [in submission to Allāh alone] superior to those who disbelieve until the Day of Resurrection. Then to Me is your return, and I will judge between you concerning that in which you used to differ.", TextGerman = "Als Allah sagte: „O ’Isa, Ich werde dich (nunmehr) abberufen und dich zu mir emporheben und dich von denen, die ungläubig sind, reinigen und diejenigen, die dir folgen, bis zum Tag der Auferstehung über diejenigen stellen, die ungläubig sind. Hierauf wird eure Rückkehr zu Mir sein, und dann werde Ich zwischen euch richten über das, worüber ihr uneinig zu sein pflegtet." });
                        surah3.VerseList.Add(new Verse { Number = 56, TextBosnian = "Što se tiče onih koji ne vjeruju, njih ću na strašne muke staviti na dunjaluku i na ahiretu, i oni neće imati pomagača.", TextEnglish = "And as for those who disbelieved, I will punish them with a severe punishment in this world and the Hereafter, and they will have no helpers.'", TextGerman = "Was nun diejenigen angeht, die ungläubig sind, so werde Ich sie im Diesseits und im Jenseits mit strenger Strafe strafen; und sie werden keine Helfer haben." });
                        surah3.VerseList.Add(new Verse { Number = 57, TextBosnian = "A onima koji budu vjerovali i dobra djela činili, On će punu nagradu dati. A, Allah ne voli zulumćare.", TextEnglish = "But as for those who believed and did righteous deeds, He will give them in full their rewards, and Allāh does not like the wrongdoers.", TextGerman = "Was aber jene angeht, die glauben und rechtschaffene Werke tun, so wird Er ihnen ihren Lohn in vollem Maß zukommen lassen. Und Allah liebt nicht die Ungerechten." });
                        surah3.VerseList.Add(new Verse { Number = 58, TextBosnian = "Ovo što ti kazujemo jesu ajeti i Opomena usavršena.", TextEnglish = "This is what We recite to you, [O Muḥammad], of [Our] verses and the precise [and wise] message [i.e., the Qur’ān].", TextGerman = "Dies verlesen Wir dir von den Zeichen und den weisen Sprüchen." });
                        surah3.VerseList.Add(new Verse { Number = 59, TextBosnian = "Primjer Isaov doista je kod Allaha isti kao i primjer Ademov: od prašine ga je stvorio, a zatim mu rekao: 'Budi!' i on bi!", TextEnglish = "Indeed, the example of Jesus to Allāh[127] is like that of Adam. He created him from dust; then He said to him, 'Be,' and he was.", TextGerman = "Gewiß, das Gleichnis ’Isas ist bei Allah wie das Gleichnis Adams. Er erschuf ihn aus Erde. Hierauf sagte Er zu ihm: Sei! und da war er." });
                        surah3.VerseList.Add(new Verse { Number = 60, TextBosnian = "Istina je od Gospodara tvoga i ne budi od onih koji sumnjaju.", TextEnglish = "The truth is from your Lord, so do not be among the doubters.", TextGerman = "(Das ist) die Wahrheit von deinem Herrn; gehöre daher nicht zu den Zweiflern!" });
                        surah3.VerseList.Add(new Verse { Number = 61, TextBosnian = "A onima koji s tobom o Njemu budu raspravljali, nakon što ti je došlo znanje, reci: 'Dođite, pozvat ćemo sinove naše i sinove vaše, i žene naše i žene vaše, a doći ćemo i mi i vi, pa ćemo se proklinjati i Allahovo prokletstvo na one koji lažu prizvati!'", TextEnglish = "Then whoever argues with you about it after [this] knowledge has come to you - say, 'Come, let us call our sons and your sons, our women and your women, ourselves and yourselves, then supplicate earnestly [together] and invoke the curse of Allāh upon the liars [among us].'", TextGerman = "Wer nun mit dir darüber streitet, nach dem, was dir an Wissen zugekommen ist, so sag (zu denen): Kommt her! Laßt uns unsere Söhne und eure Söhne, unsere Frauen und eure Frauen, uns selbst und euch selbst zusammenrufen und hierauf flehen und so den Fluch Allahs über die Lügner kommen lassen!" });
                        surah3.VerseList.Add(new Verse { Number = 62, TextBosnian = "Ovo je, doista, istinito kazivanje, i nema boga osim Allaha! A Allah je doista Silni i Mudri!", TextEnglish = "Indeed, this is the true narration. And there is no deity except Allāh. And indeed, Allāh is the Exalted in Might, the Wise.", TextGerman = "Gewiß, dies sind die wahren Geschichten. Und keinen Gott gibt es außer Allah. Allah - Er ist wahrlich der Allmächtige und Allweise." });
                        surah3.VerseList.Add(new Verse { Number = 63, TextBosnian = "A ako se oni okrenu, pa Allah, doista, dobro zna smutljivce.", TextEnglish = "But if they turn away, then indeed - Allāh is Knowing of the corrupters.", TextGerman = "Doch wenn sie sich abkehren, so weiß Allah sehr wohl über die Unheilstifter Bescheid." });
                        surah3.VerseList.Add(new Verse { Number = 64, TextBosnian = "Reci: 'O vi kojima je Knjiga data, dođite da se okupimo uz riječi koje mi i vi podjednako dijelimo: da nikoga osim Allaha ne obožavamo, da Mu ništa ne pridružujemo, i da jedni druge, pored Allaha, bogovima ne smatramo!' Pa, ako se oni okrenu, vi recite: 'Budite svjedoci da smo mi muslimani!'", TextEnglish = "Say, 'O People of the Scripture, come to a word that is equitable between us and you - that we will not worship except Allāh and not associate anything with Him and not take one another as lords instead of Allāh.'[128] But if they turn away, then say, 'Bear witness that we are Muslims [submitting to Him].'", TextGerman = "Sag: O Leute der Schrift, kommt her zu einem zwischen uns und euch gleichen Wort: daß wir niemandem dienen außer Allah und Ihm nichts beigesellen und sich nicht die einen von uns die anderen zu Herren außer Allah nehmen. Doch wenn sie sich abkehren, dann sagt: Bezeugt, daß wir (Allah) ergeben sind." });
                        surah3.VerseList.Add(new Verse { Number = 65, TextBosnian = "O vi kojima je Knjiga data, zašto se o Ibrahimu prepirete kada su Tevrat i Indžil poslije njega objavljeni!? Zar vi ne razumijete?!", TextEnglish = "O People of the Scripture, why do you argue about Abraham while the Torah and the Gospel were not revealed until after him? Then will you not reason?", TextGerman = "O Leute der Schrift, warum streitet ihr über Ibrahim, wo die Tora und das Evangelium erst nach ihm (als Offenbarung) herabgesandt worden sind? Begreift ihr denn nicht?" });
                        surah3.VerseList.Add(new Verse { Number = 66, TextBosnian = "Eto, vi raspravljate o onome o čemu imate neko znanje, ali zašto raspravljate o onome o čemu nemate nikakvoga znanja? A Allah zna, a vi ne znate.", TextEnglish = "Here you are - those who have argued about that of which you have [some] knowledge, but why do you argue about that of which you have no knowledge? And Allāh knows, while you know not.", TextGerman = "Ihr da seid es doch, die ihr über etwas gestritten habt, wovon ihr Wissen habt; warum streitet ihr nun aber über etwas, wovon ihr kein Wissen habt? Allah weiß, ihr aber wißt nicht." });
                        surah3.VerseList.Add(new Verse { Number = 67, TextBosnian = "Ibrahim nije bio ni židov ni kršćanin, već pravi vjernik, musliman. On višebožac nije bio!", TextEnglish = "Abraham was neither a Jew nor a Christian, but he was one inclining toward truth, a Muslim [submitting to Allāh]. And he was not of the polytheists.[129]", TextGerman = "Ibrahim war weder ein Jude noch ein Christ, sondern er war Anhänger des rechten Glaubens, einer, der sich Allah ergeben hat, und er gehörte nicht zu den Götzendienern." });
                        surah3.VerseList.Add(new Verse { Number = 68, TextBosnian = "Doista su najbliži ljudi Ibrahimu oni koji su ga slijedili, i ovaj Vjerovjesnik i oni koji vjeruju. A Allah je zaštitnik vjernika.", TextEnglish = "Indeed, the most worthy of Abraham among the people are those who followed him [in submission to Allāh] and this prophet [i.e., Muḥammad (ﷺ)] and those who believe [in his message]. And Allāh is the Ally[130] of the believers.", TextGerman = "Die Menschen, die Ibrahim am nächsten stehen, sind wahrlich diejenigen, die ihm folgten, sowie dieser Prophet und die, die (mit ihm) glauben. Und Allah ist der Schutzherr der Gläubigen." });
                        surah3.VerseList.Add(new Verse { Number = 69, TextBosnian = "Neki kojima je Knjiga data voljeli bi da vas na stranputicu odvedu. Ali oni ne odvode u zabludu nikoga drugoga do sebe, a da to i ne osjećaju.", TextEnglish = "A faction of the People of the Scripture wish they could mislead you. But they do not mislead except themselves, and they perceive [it] not.", TextGerman = "Gern möchte euch ein Teil von den Leuten der Schrift in die Irre führen. Aber sie führen nur sich selbst in die Irre, ohne (es) zu merken." });
                        surah3.VerseList.Add(new Verse { Number = 70, TextBosnian = "O vi kojima je data Knjiga, zašto u Allahove ajete ne vjerujete, a svjedoci ste da su Istina!?", TextEnglish = "O People of the Scripture, why do you disbelieve in the verses of Allāh[131] while you witness [to their truth]?", TextGerman = "O Leute der Schrift! Warum verleugnet ihr Allahs Zeichen, wo ihr doch (selbst) Zeugen seid?" });
                        surah3.VerseList.Add(new Verse { Number = 71, TextBosnian = "O vi kojima je data Knjiga, zašto Istinu neistinom zaogrćete, i krijete Istinu, a vi je znate!?", TextEnglish = "O People of the Scripture, why do you mix [i.e., confuse] the truth with falsehood and conceal the truth while you know [it]?", TextGerman = "O Leute der Schrift, warum verdeckt ihr das Wahre durch das Falsche und verbergt wissentlich die Wahrheit?" });
                        surah3.VerseList.Add(new Verse { Number = 72, TextBosnian = "Neki kojima je Knjiga data govore: 'Vjerujte u ono što je objavljeno onima koji vjeruju, u početku dana, a porecite to na kraju dana, ne bi li i oni svoju vjeru napustili!?", TextEnglish = "And a faction of the People of the Scripture say [to each other], 'Believe in that which was revealed to the believers at the beginning of the day and reject it at its end that perhaps they will return [i.e., abandon their religion],", TextGerman = "Und ein Teil von den Leuten der Schrift sagt: „Glaubt an das, was auf diejenigen, die glauben, (als Offenbarung) herabgesandt worden ist, am Anfang des Tages und verleugnet es (wieder) an seinem Ende, auf daß sie umkehren mögen!" });
                        surah3.VerseList.Add(new Verse { Number = 73, TextBosnian = "I vjerujte i povjeravajte se samo onome ko slijedi vašu vjeru!' Ti reci: 'Doista je prava uputa Allahova uputa!' I govore: 'Ne vjerujte i ne povjeravajte se ikome, pa da nekome bude dato kao što je dato vama ili da ima dokaz pred Gospodarom vašim!' Reci: 'Zaista je sve dobro u ruci Allahovoj i On ga daje kome On hoće!' A Allah je neizmjerno darežljiv i Onaj Koji sve zna.", TextEnglish = "And do not trust except those who follow your religion.' Say, 'Indeed, the [true] guidance is the guidance of Allāh. [Do you fear] lest someone be given [knowledge] like you were given or that they would [thereby] argue with you before your Lord?' Say, 'Indeed, [all] bounty is in the hand[132] of Allāh - He grants it to whom He wills. And Allāh is all-Encompassing and Wise.'", TextGerman = "Und glaubt nur jemandem, der eurer Religion folgt. Sag: Gewiß, die (wahre) Rechtleitung ist Allahs Rechtleitung. (Und glaubt nicht,) daß (auch) jemandem (anderen) das gleiche gegeben werde, was euch gegeben wurde, oder daß man mit euch (zu Recht) vor eurem Herrn streite. Sag: Gewiß, die Huld liegt in Allahs Hand. Er gewährt sie, wem Er will. Und Allah ist Allumfassend und Allwissend." });
                        surah3.VerseList.Add(new Verse { Number = 74, TextBosnian = "On Svoju milost daje kome hoće. Allah je posjednik velikog dobra.", TextEnglish = "He selects for His mercy whom He wills. And Allāh is the possessor of great bounty.", TextGerman = "Er zeichnet mit Seinem Erbarmen aus, wen Er will, und Allah besitzt große Huld." });
                        surah3.VerseList.Add(new Verse { Number = 75, TextBosnian = "Ima onih kojima je data Knjiga koji će ti vratiti ako im povjeriš tovar blaga, a ima ih koji ti neće vratiti ako im povjeriš i samo jedan dinar, osim ako ga od njih ne budeš stalno potraživao. To je zato jer oni govore: 'Nama nije grijeh što učinimo nepismenima', i o Allahu govore laži, a znaju istinu.", TextEnglish = "And among the People of the Scripture is he who, if you entrust him with a great amount [of wealth], he will return it to you. And among them is he who, if you entrust him with a [single] coin, he will not return it to you unless you are constantly standing over him [demanding it]. That is because they say, 'There is no blame upon us concerning the unlearned.'[133] And they speak untruth about Allāh while they know [it].", TextGerman = "Unter den Leuten der Schrift gibt es manche, die, wenn du ihnen einen Qintar anvertraust, ihn dir (wieder) aushändigen. Es gibt unter ihnen aber auch manche, die, wenn du ihnen (nur) einen Dinar anvertraust, ihn dir nicht (wieder) aushändigen, es sei denn, du bist ständig hinter ihnen her. Dies, weil sie sagen: Gegen uns kann man der Schriftunkundigen wegen nicht vorgehen. Und sie sprechen (damit) wissentlich eine Lüge gegen Allah aus." });
                        surah3.VerseList.Add(new Verse { Number = 76, TextBosnian = "Međutim, ko obavezu svoju ispunjava i grijeha se kloni, pa Allah, doista, voli bogobojazne.", TextEnglish = "But yes, whoever fulfills his commitment and fears Allāh - then indeed, Allāh loves those who fear Him.", TextGerman = "Aber nein! Wer seinen Bund hält und gottesfürchtig ist, - gewiß, Allah liebt die Gottes fürchtigen." });
                        surah3.VerseList.Add(new Verse { Number = 77, TextBosnian = "Doista, oni koji za obavezu svoju prema Allahu i zakletve svoje kupuju nešto što malo vrijedi, na ahiretu nikakva udjela neće imati. Allah s njima neće govoriti, niti će na njih pogledati na Kijametskom danu, niti će ih očistiti. Njima pripada patnja bolna.", TextEnglish = "Indeed, those who exchange the covenant of Allāh and their [own] oaths for a small price will have no share in the Hereafter, and Allāh will not speak to them or look at them on the Day of Resurrection, nor will He purify them; and they will have a painful punishment.", TextGerman = "Diejenigen, die ihren Bund mit Allah und ihre Eide für einen geringen Preis verkaufen, haben am Jenseits keinen Anteil. Und Allah wird am Tag der Auferstehung weder zu ihnen sprechen noch sie anschauen noch sie läutern. Für sie wird es schmerzhafte Strafe geben." });
                        surah3.VerseList.Add(new Verse { Number = 78, TextBosnian = "Jedna skupina njihova uvija jezike svoje!' Knjigu čitajući, da biste vi pomislili da je to iz Knjige, a to nije iz Knjige. I govore: 'To je od Allaha!', a to nije od Allaha. I govore na Allaha laži, a to znaju.", TextEnglish = "And indeed, there is among them a party who alter the Scripture with their tongues so you may think it is from the Scripture, but it is not from the Scripture. And they say, 'This is from Allāh,' but it is not from Allāh. And they speak untruth about Allāh while they know.", TextGerman = "Und wahrlich, eine Gruppe von ihnen verdreht mit seinen Zungen die Schrift, damit ihr es für zur Schrift gehörig haltet, während es nicht zur Schrift gehört. Und sie sagen: „Es ist von Allah, während es nicht von Allah ist. Und sie sprechen (damit) wissentlich eine Lüge gegen Allah aus." });
                        surah3.VerseList.Add(new Verse { Number = 79, TextBosnian = "Nije primjereno čovjeku da mu Allah da Knjigu, i mudrost, i vjerovjesništvo, a zatim da govori ljudima: 'Budite robovi moji, a ne Allahovi!', nego: 'Budite učeni i pobožni, time što Knjizi poučavate i što je i sami proučavate!'", TextEnglish = "It is not for a human [prophet][134] that Allāh should give him the Scripture[135] and authority and prophethood and then he would say to the people, 'Be servants to me rather than Allāh,' but [instead, he would say], 'Be pious scholars of the Lord because of what you have taught of the Scripture and because of what you have studied.'", TextGerman = "Es steht einem menschlichen Wesen nicht zu, daß ihm Allah die Schrift, das Urteil und das Prophetentum gibt, und er hierauf zu den Menschen sagt: „Seid Diener von mir anstatt Allahs!, sondern: „Seid Leute des Herrn, da ihr das Buch zu lehren und da ihr (es) zu erlernen pflegtet." });
                        surah3.VerseList.Add(new Verse { Number = 80, TextBosnian = "On vam ne naređuje da meleke i vjerovjesnike gospodarima smatrate. Zar da vam naređuje nevjerovanje, nakon što ste postali muslimani?!", TextEnglish = "Nor could he order you to take the angels and prophets as lords. Would he order you to disbelief after you had been Muslims?", TextGerman = "Und (es steht ihm nicht zu,) euch zu befehlen, die Engel und die Propheten zu Herren nehmen. Sollte er euch den Unglauben befehlen, nachdem ihr (Allah) Ergebene seid?" });
                        surah3.VerseList.Add(new Verse { Number = 81, TextBosnian = "I kada je Allah od vjerovjesnika čvrsto obećanje uzeo: 'Kad god da vam podarim Knjigu i mudrost, zatim vam dođe poslanik koji potvrđuje da je istina ono što vi imate, da li biste sigurno u njega vjerovali i njega pomogli?!' Da li potvrđujete i prihvaćate obavezu prema Meni?', reče. 'Potvrđujerno!', odgovoriše. 'Budite onda svjedoci!', reče On, 'a i Ja s vama svjedočim!'", TextEnglish = "And [recall, O People of the Scripture], when Allāh took the covenant of the prophets, [saying], 'Whatever I give you of the Scripture and wisdom and then there comes to you a messenger confirming what is with you, you [must] believe in him and support him.' [Allāh] said, 'Have you acknowledged and taken upon that My commitment?'[136] They said, 'We have acknowledged it.' He said, 'Then bear witness, and I am with you among the witnesses.'", TextGerman = "Und als Allah mit den Propheten ein Abkommen traf: Was immer Ich euch an Büchern und Weisheit gebracht habe -, und danach ist zu euch ein Gesandter gekommen, das bestätigend, was euch (bereits) vorliegt, an den müßt ihr ganz gewiß glauben und dem müßt ihr ganz gewiß helfen. Er sagte: „Erklärt ihr euch einverstanden und nehmt ihr unter dieser (Bedingung) Meine Bürde an? Sie sagten: „Wir erklären uns einverstanden. Er sagte: „So bezeugt es, und Ich gehöre mit euch zu den Zeugnis Ablegenden." });
                        surah3.VerseList.Add(new Verse { Number = 82, TextBosnian = "A oni koji se i poslije toga okrenu, doista su to buntovnici.", TextEnglish = "And whoever turned away after that - they were the defiantly disobedient.", TextGerman = "Wer sich aber nach diesem abkehrt, so sind jene die Frevler." });
                        surah3.VerseList.Add(new Verse { Number = 83, TextBosnian = "Zar pored Allahove vjere žele neku drugu, kada se Njemu predaju, milom ili silom, i oni na nebesima i na Zemlji, i Njemu će vraćeni biti?!", TextEnglish = "So is it other than the religion of Allāh they desire, while to Him have submitted [all] those within the heavens and earth, willingly or by compulsion, and to Him they will be returned?", TextGerman = "Begehren sie denn eine andere als Allahs Religion, wo sich Ihm doch (jeder) ergeben hat, der in den Himmeln und auf der Erde ist, freiwillig oder widerwillig? Und zu Ihm werden sie zurückgebracht." });
                        surah3.VerseList.Add(new Verse { Number = 84, TextBosnian = "Reci: 'Mi vjerujemo u Allaha i u ono što je objavljeno nama, i što je objavljeno Ibrahimu, i Ismailu, i Ishaku, i Jakubu i potomcima, i u ono što je dato Musau i Isau i vjerovjesnicima od Gospodara njihovoga; mi nikakvu razliku između njih ne pravimo, i mi smo Njemu predani.'", TextEnglish = "Say, 'We have believed in Allāh and in what was revealed to us and what was revealed to Abraham, Ishmael, Isaac, Jacob, and the Descendants [al-Asbāṭ], and in what was given to Moses and Jesus and to the prophets from their Lord. We make no distinction between any of them, and we are Muslims [submitting] to Him.'", TextGerman = "Sag: Wir glauben an Allah und (an das,) was auf uns und was auf Ibrahim, Isma’il, Ishaq, Ya’qub und die Stämme (als Offenbarung) herabgesandt wurde und was Musa, ’Isa und den Propheten von ihrem Herrn gegeben wurde. Wir machen keinen Unterschied bei jemandem von Ihnen, und wir sind Ihm ergeben." });
                        surah3.VerseList.Add(new Verse { Number = 85, TextBosnian = "A onaj ko želi neku drugu vjeru osim islama, neće mu biti primljena, i on će na drugom svijetu biti među gubitnicima.", TextEnglish = "And whoever desires other than Islām as religion - never will it be accepted from him, and he, in the Hereafter, will be among the losers.", TextGerman = "Wer aber als Religion etwas anderes als den Islam begehrt, so wird es von ihm nicht angenommen werden, und im Jenseits wird er zu den Verlierern gehören." });
                        surah3.VerseList.Add(new Verse { Number = 86, TextBosnian = "Kako Allah da uputi na Pravi put narod koji ne vjeruje nakon što je vjerovao i svjedočio da je Poslanik istina, i Jasni dokazi su mu došli!? Allah neće uputiti na Pravi put narod koji zulum čini.", TextEnglish = "How shall Allāh guide a people who disbelieved after their belief and had witnessed that the Messenger is true and clear signs had come to them? And Allāh does not guide the wrongdoing people.", TextGerman = "Wie sollte Allah ein Volk rechtleiten, das (wieder) ungläubig wurde, nachdem es den Glauben (angenommen) hatte und (nachdem) es bezeugt hatte, daß der Gesandte wahrhaft ist, und (nachdem) zu ihm die klaren Beweise gekommen waren! Und Allah leitet nicht das ungerechte Volk recht." });
                        surah3.VerseList.Add(new Verse { Number = 87, TextBosnian = "A to su oni čija je kazna prokletstvo od Allaha i meleka i svih ljudi!", TextEnglish = "Those - their recompense will be that upon them is the curse of Allāh and the angels and the people, all together,", TextGerman = "Der Lohn jener ist, daß auf ihnen der Fluch Allahs und der Engel und der Menschen allesamt liegt," });
                        surah3.VerseList.Add(new Verse { Number = 88, TextBosnian = "U njemu će vječno ostati i patnja im olakšana biti neće, niti će im se vremena dati.", TextEnglish = "Abiding eternally therein. The punishment will not be lightened for them, nor will they be reprieved,", TextGerman = "ewig darin zu bleiben. Die Strafe soll ihnen nicht erleichtert noch soll ihnen Aufschub gewährt werden, -" });
                        surah3.VerseList.Add(new Verse { Number = 89, TextBosnian = "Izuzetak su oni koji se pokaju poslije toga i poprave, a Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "Except for those who repent after that[137] and correct themselves. For indeed, Allāh is Forgiving and Merciful.", TextGerman = "außer denjenigen, die nach alledem bereuen und verbessern, so ist Allah Allvergebend und Barmherzig." });
                        surah3.VerseList.Add(new Verse { Number = 90, TextBosnian = "Doista, onima koji postanu nevjernici, nakon što su vjerovali, zatim postanu još veći nevjernici, pokajanje neće biti primljeno i oni su sigurno zalutali.", TextEnglish = "Indeed, those who disbelieve [i.e., reject the message] after their belief and then increase in disbelief - never will their [claimed] repentance be accepted, and they are the ones astray.", TextGerman = "Jene (aber), die ungläubig werden, nachdem sie den Glauben (angenommen) haben, und hierauf an Unglauben zunehmen, deren Reue wird nicht angenommen werden, und jene sind die Irregehenden." });
                        surah3.VerseList.Add(new Verse { Number = 91, TextBosnian = "Zaista, nijednom od onih koji nisu vjerovali, a koji i umru kao nevjernici, neće biti prihvaćeno da se otkupe pa makar to bilo sve blago na Zemlji. Njima pripada patnja bolna i oni neće imati nikakvog pomagača.", TextEnglish = "Indeed, those who disbelieve and die while they are disbelievers - never would the [whole] capacity of the earth in gold be accepted from one of them if he would [seek to] ransom himself with it. For those there will be a painful punishment, and they will have no helpers.", TextGerman = "Gewiß, diejenigen, die ungläubig geworden sind und als Ungläubige sterben, - von keinem von ihnen würde die (ganze) Erde voll Gold angenommen werden, auch wenn er sich damit loskaufen wollte. Für jene wird es schmerzhafte Strafe geben; und sie werden keine Helfer haben." });
                        surah3.VerseList.Add(new Verse { Number = 92, TextBosnian = "Nećete postići dobro sve dok ne budete dijelili od onoga što vam je drago. A šta god da dijelite, Allah to dobro zna.", TextEnglish = "Never will you attain the good [reward][138] until you spend [in the way of Allāh] from that which you love. And whatever you spend - indeed, Allāh is Knowing of it.", TextGerman = "Ihr werdet die Güte nicht erreichen, bevor ihr nicht von dem ausgebt, was euch lieb ist. Und was immer ihr ausgebt, so weiß Allah darüber Bescheid." });
                        surah3.VerseList.Add(new Verse { Number = 93, TextBosnian = "Svaka hrana bila je dozvoljena sinovima Israilovim, osim one koju je Israil sam sebi zabranio prije nego je Tevrat objavljen bio. Reci: 'Donesite Tevrat i čitajte ga, ako istinu govorite!'", TextEnglish = "All food was lawful to the Children of Israel except what Israel [i.e., Jacob] had made unlawful to himself before the Torah was revealed. Say, [O Muḥammad], 'So bring the Torah and recite it, if you should be truthful.'", TextGerman = "Alle Speisen waren den Kindern Isra’ils erlaubt außer dem, was Isra’il sich selbst verbot, bevor die Tora offenbart wurde. Sag: Bringt doch die Tora bei und verlest sie dann, wenn ihr wahrhaftig seid." });
                        surah3.VerseList.Add(new Verse { Number = 94, TextBosnian = "A oni koji o Allahu iznose laži i poslije toga, oni su, doista, zulumćari.", TextEnglish = "And whoever invents about Allāh untruth after that - then those are [truly] the wrongdoers.", TextGerman = "Wer also nach alledem gegen Allah (noch) Lügen ersinnt, jene sind die Ungerechten." });
                        surah3.VerseList.Add(new Verse { Number = 95, TextBosnian = "Reci: 'Allah istinu govori!' - da slijedite vjeru Ibrahimovu, koji je pravi vjernik bio. On u višebošce nije spadao!", TextEnglish = "Say, 'Allāh has told the truth. So follow the religion of Abraham, inclining toward truth; and he was not of the polytheists.'[139]", TextGerman = "Sag: Allah hat die Wahrheit gesprochen. So folgt dem Glaubensbekenntnis Ibrahims, (als) Anhänger des rechten Glaubens, und er gehörte nicht zu den Götzendienern." });
                        surah3.VerseList.Add(new Verse { Number = 96, TextBosnian = "Prva bogomolja podignuta za ljude jeste ona u Beki, blagoslovljena i putokaz svjetovima.", TextEnglish = "Indeed, the first House [of worship] established for mankind was that at Bakkah [i.e., Makkah] - blessed and a guidance for the worlds.", TextGerman = "Das erste (Gottes)haus, das für die Menschen gegründet wurde, ist wahrlich dasjenige in Bakka, als ein gesegnetes (Haus) und eine Rechtleitung für die Weltenbewohner." });
                        surah3.VerseList.Add(new Verse { Number = 97, TextBosnian = "U njoj su znamenja očevidna, mjesto na kojem je stajao Ibrahim. I onaj ko u nju uđe, bit će siguran. Hodočastiti Kabu, radi Allaha, dužan je svako ko bude u mogućnosti do nje doći. A ko ne vjeruje - pa, Allah je, doista, neovisan o svjetovima.", TextEnglish = "In it are clear signs [such as] the standing place of Abraham. And whoever enters it [i.e., the Ḥaram] shall be safe. And [due] to Allāh from the people is a pilgrimage to the House - for whoever is able to find thereto a way. But whoever disbelieves [i.e., refuses] - then indeed, Allāh is free from need of the worlds.[140]", TextGerman = "Darin liegen klare Zeichen. (Es ist) der Standort Ibrahims. Und wer es betritt, ist sicher. Und Allah steht es den Menschen gegenüber zu, daß sie die Pilgerfahrt zum Hause unternehmen - (diejenigen,) die dazu die Möglichkeit haben. Wer aber ungläubig ist, so ist Allah der Weltenbewohner Unbedürftig." });
                        surah3.VerseList.Add(new Verse { Number = 98, TextBosnian = "Reci: 'O sljedbenici Knjige, zašto u Allahova ajete i znakove ne vjerujete. Allah je svjedok svega što radite!", TextEnglish = "Say, 'O People of the Scripture, why do you disbelieve in the verses of Allāh while Allāh is Witness over what you do?'", TextGerman = "Sag: O Leute der Schrift, warum verleugnet ihr Allahs Zeichen, wo doch Allah Zeuge dessen ist, was ihr tut?" });
                        surah3.VerseList.Add(new Verse { Number = 99, TextBosnian = "Reci: 'O vi kojima je Knjiga data, zašto onoga koji vjeruje od Allahove vjere odvraćate, želeći da je iskrivite, a svjedoci ste da je istina!? A Allah ne zanemaruje ono što vi radite.'", TextEnglish = "Say, 'O People of the Scripture, why do you avert from the way of Allāh those who believe, seeking to make it [seem] deviant, while you are witnesses [to the truth]? And Allāh is not unaware of what you do.'", TextGerman = "Sag: O Leute der Schrift, warum haltet ihr die, die glauben, von Allahs Weg ab, indem ihr danach trachtet, ihn krumm zu machen, wo ihr doch Zeugen seid? Doch Allah ist nicht unachtsam dessen, was ihr tut." });
                        surah3.VerseList.Add(new Verse { Number = 100, TextBosnian = "O vi koji vjerujete, ako se budete pokoravali jednoj skupini onih kojima je data Knjiga, oni će vas, nakon što ste prihvatili pravu vjeru, ponovo vratiti u nevjernike!", TextEnglish = "O you who have believed, if you obey a party of those who were given the Scripture, they would turn you back, after your belief, [to being] unbelievers.", TextGerman = "O die ihr glaubt, wenn ihr einer Gruppe von denen gehorcht, denen die Schrift gegeben wurde, werden sie euch, nachdem ihr den Glauben (angenommen) habt, wieder zu Ungläubigen machen." });
                        surah3.VerseList.Add(new Verse { Number = 101, TextBosnian = "A kako da ne vjerujete kada vam se kazuju Allahovi ajeti i kad je među vama Poslanik Njegov?! A ko se čvrsto drži Allaha, on je na Pravi put upućen.", TextEnglish = "And how could you disbelieve while to you are being recited the verses of Allāh and among you is His Messenger? And whoever holds firmly to Allāh[141] has [indeed] been guided to a straight path.", TextGerman = "Wie aber könnt ihr (wieder) ungläubig werden, wo euch doch Allahs Zeichen verlesen werden und unter euch Sein Gesandter ist? Doch wer an Allah festhält, der wird gewiß auf einen geraden Weg geleitet." });
                        surah3.VerseList.Add(new Verse { Number = 102, TextBosnian = "O vi koji vjerujete, bojte se Allaha istinskom bogobojaznošću i nipošto ne umirite osim kao muslimani!", TextEnglish = "O you who have believed, fear Allāh as He should be feared and do not die except as Muslims [in submission to Him].", TextGerman = "O die ihr glaubt, fürchtet Allah in gebührender Furcht und sterbt ja nicht anders denn als (Allah) Ergebene!" });
                        surah3.VerseList.Add(new Verse { Number = 103, TextBosnian = "Svi se čvrsto držite za Allahovo uže i ne razjedinjujte se! I sjetite se blagodati Allahove prema vama, kada ste bili neprijatelji, pa je On sjedinio srca vaša i postali ste, Njegovom milošću, braća! I bili ste na ivici vatrene jame, pa vas je On od nje spasio. Tako vam Allah objašnjava ajete i znakove Svoje da biste se ispravnim putem uputili.", TextEnglish = "And hold firmly to the rope[142] of Allāh all together and do not become divided. And remember the favor of Allāh upon you - when you were enemies and He brought your hearts together and you became, by His favor, brothers. And you were on the edge of a pit of the Fire, and He saved you from it. Thus does Allāh make clear to you His verses that you may be guided.", TextGerman = "Und haltet alle fest am Seil Allahs und geht nicht auseinander! Und gedenkt Allahs Gunst an euch, als ihr Feinde wart und Er dann eure Herzen zusammenführte, worauf ihr durch Seine Gunst Brüder wurdet. Und (als) ihr am Rand einer Feuergrube wart und Er euch dann davor errettete. So macht Allah euch Seine Zeichen klar, auf daß ihr rechtgeleitet werden möget!" });
                        surah3.VerseList.Add(new Verse { Number = 104, TextBosnian = "I neka među vama bude grupa ljudi koja na dobro poziva i naređuje dobro i odvraća od zla. To su oni koji su uspjeli.", TextEnglish = "And let there be [arising] from you a nation inviting to [all that is] good, enjoining what is right and forbidding what is wrong,[143] and those will be the successful.", TextGerman = "Und es soll aus euch eine Gemeinschaft werden, die zum Guten aufruft, das Rechte gebietet und das Verwerfliche verbietet. Jene sind es, denen es wohl ergeht." });
                        surah3.VerseList.Add(new Verse { Number = 105, TextBosnian = "I ne budite kao oni koji su se razjedinili i razišli, nakon što su im Jasni dokazi već došli! Njima pripada patnja velika.", TextEnglish = "And do not be like the ones who became divided and differed after the clear proofs had come to them. And those will have a great punishment", TextGerman = "Und seid nicht wie jene, die auseinander gingen und uneinig wurden, nachdem die klaren Beweise zu ihnen gekommen waren. Für jene gibt es gewaltige Strafe." });
                        surah3.VerseList.Add(new Verse { Number = 106, TextBosnian = "Na Dan kada će neka lica pobijeljeti, a neka pocrnjeti. Onima čija lica budu pocrnjela bit će rečeno: 'Zar ste postali nevjernici nakon vašeg vjerovanja?! Pa iskusite patnju zato što niste vjerovali!'", TextEnglish = "On the Day [some] faces will turn white and [some] faces will turn black. As for those whose faces turn black, [to them it will be said], 'Did you disbelieve [i.e., reject faith] after your belief? Then taste the punishment for what you used to reject.'", TextGerman = "An dem Tag, da (die einen) Gesichter weiß und (die anderen) Gesichter schwarz sein werden. Was nun diejenigen angeht, deren Gesichter schwarz sein werden (, so wird zu ihnen gesagt werden): Seid ihr ungläubig geworden, nachdem ihr den Glauben (angenommen) hattet? So kostet die Strafe dafür, daß ihr wieder ungläubig geworden seid." });
                        surah3.VerseList.Add(new Verse { Number = 107, TextBosnian = "A oni čija lica pobijele, pa u milosti Allahovoj su, oni će u njoj vječno ostati.", TextEnglish = "But as for those whose faces turn white, [they will be] within the mercy of Allāh. They will abide therein eternally.", TextGerman = "Was aber diejenigen angeht, deren Gesichter weiß sein werden, so werden sie sich in Allahs Erbarmen befinden. Ewig werden sie darin bleiben." });
                        surah3.VerseList.Add(new Verse { Number = 108, TextBosnian = "To su Allahovi ajeti koje tebi istinito kazujemo. A Allah ne želi da bilo kome zulum učini.", TextEnglish = "These are the verses of Allāh. We recite them to you, [O Muḥammad], in truth; and Allāh wants no injustice to the worlds [i.e., His creatures].", TextGerman = "Dies sind die Zeichen Allahs, die Wir der Wahrheit entsprechend verlesen. Und Allah will keine Ungerechtigkeit für die Weltenbewohner" });
                        surah3.VerseList.Add(new Verse { Number = 109, TextBosnian = "Allahu pripada sve što je na nebesima i što je na Zemlji, i Allahu se sve vraća.", TextEnglish = "To Allāh belongs whatever is in the heavens and whatever is on the earth. And to Allāh will [all] matters be returned.", TextGerman = "Allah gehört (alles), was in den Himmeln und was auf der Erde ist, und zu Allah werden (all) die Angelegenheiten zurückgebracht." });
                        surah3.VerseList.Add(new Verse { Number = 110, TextBosnian = "Vi ste najbolji narod koji se ikada pojavio: tražite da se čini dobro, a odvraćate od zla, i vjerujete u Allaha! A kada bi sljedbenici Knjige vjerovali, bilo bi bolje za njih; među njima ima vjernika, ali - većina njih su buntovnici.", TextEnglish = "You are the best nation produced [as an example] for mankind. You enjoin what is right and forbid what is wrong and believe in Allāh. If only the People of the Scripture had believed, it would have been better for them. Among them are believers, but most of them are defiantly disobedient.", TextGerman = "Ihr seid die beste Gemeinschaft, die für die Menschen hervorgebracht worden ist. Ihr gebietet das Rechte und verbietet das Verwerfliche und glaubt an Allah. Und wenn die Leute der Schrift glauben würden, wäre es wahrlich besser für sie. Unter ihnen gibt es Gläubige, aber die meisten von ihnen sind Frevler." });
                        surah3.VerseList.Add(new Verse { Number = 111, TextBosnian = "Oni vam ne mogu nauditi, nego vas mogu samo uznemiriti. Ako vas napadnu, dat će se u bijeg, i poslije im pomoći biti neće.", TextEnglish = "They will not harm you except for [some] annoyance. And if they fight you, they will show you their backs [i.e., retreat]; then they will not be aided.", TextGerman = "Sie werden euch keinen Schaden zufügen, außer Beleidigungen. Und wenn sie gegen euch kämpfen, werden sie euch den Rücken kehren, und hierauf wird ihnen keine Hilfe zuteil werden." });
                        surah3.VerseList.Add(new Verse { Number = 112, TextBosnian = "Ma gdje se našli, oni su poniženi, osim ako su pod zaštitom Allahovom i zaštitom drugih ljudi. Oni su zaslužili srdžbu Allahovu i bijeda je pala na njih, zato što u ajete i dokaze Allahove nisu vjerovali i što su vjerovjesnike bespravno ubijali, zato što su Allahu bili nepokorni, i što su prešli svaku granicu u griješenju.", TextEnglish = "They have been put under humiliation [by Allāh] wherever they are overtaken, except for a rope [i.e., covenant] from Allāh and a rope [i.e., treaty] from the people [i.e., the Muslims].[144] And they have drawn upon themselves anger from Allāh and have been put under destitution. That is because they disbelieved in [i.e., rejected] the verses of Allāh and killed the prophets without right. That is because they disobeyed and [habitually] transgressed.", TextGerman = "Auferlegt ist ihnen Erniedrigung, wo immer sie angetroffen werden-, außer mit einem Seil von Allah und einem Seil von den Menschen -, und sie haben sich Zorn von Allah zugezogen, und Elend ist ihnen auferlegt. Dies, weil sie stets Allahs Zeichen verleugneten und die Propheten zu Unrecht töteten, weil sie sich widersetzten und stets übertraten." });
                        surah3.VerseList.Add(new Verse { Number = 113, TextBosnian = "Nisu svi oni isti! Među onim kojima je Knjiga data ima ispravnih, koji po cijelu noć ajete Allahove uče i sedždu Mu čine.", TextEnglish = "They are not [all] the same; among the People of the Scripture is a community[145] standing [in obedience], reciting the verses of Allāh during periods of the night and prostrating [in prayer].", TextGerman = "Sie sind nicht (alle) gleich. Unter den Leuten der Schrift ist eine standhafte Gemeinschaft, die Allahs Zeichen zu Stunden der Nacht verliest und sich (im Gebet) niederwirft." });
                        surah3.VerseList.Add(new Verse { Number = 114, TextBosnian = "Oni u Allaha i ahiret vjeruju, naređuju da se čine dobra djela, a odvraćaju od zlih, i žure činiti dobro. Oni su među dobrima.", TextEnglish = "They believe in Allāh and the Last Day, and they enjoin what is right and forbid what is wrong and hasten to good deeds. And those are among the righteous.", TextGerman = "Sie glauben an Allah und den Jüngsten Tag und gebieten das Rechte und verbieten das Verwerfliche und beeilen sich mit den guten Dingen. Jene gehören zu den Rechtschaffenen." });
                        surah3.VerseList.Add(new Verse { Number = 115, TextBosnian = "Kakvo god dobro da urade, neće im biti negirano, a Allah dobro zna one koji se Njega boje.", TextEnglish = "And whatever good they do - never will it be denied them. And Allāh is Knowing of the righteous.", TextGerman = "Und was sie an Gutem tun, das wird ihnen nicht ungedankt bleiben. Und Allah weiß über die Gottesfürchtigen Bescheid." });
                        surah3.VerseList.Add(new Verse { Number = 116, TextBosnian = "Doista, one koji ne vjeruju od Allaha neće odbraniti imeci njihovi, niti djeca njihova; oni će stanovnici Vatre biti, u kojoj će vječno ostati.", TextEnglish = "Indeed, those who disbelieve - never will their wealth or their children avail them against Allāh at all, and those are the companions of the Fire; they will abide therein eternally.", TextGerman = "Gewiß, denjenigen, die ungläubig sind, werden weder ihr Besitz noch ihre Kinder vor Allah etwas nützen. Das sind Insassen des (Höllen)feuers. Ewig werden sie darin bleiben." });
                        surah3.VerseList.Add(new Verse { Number = 117, TextBosnian = "Primjer onoga što oni u ovosvjetskom životu udijele sličan je ledenom vjetru koji pogodi usjeve ljudi koji su sami sebi zulum učinili, pa ih uništi. Njima Allah nije zulum učinio, nego ga oni sami sebi čine.", TextEnglish = "The example of what they spend in this worldly life is like that of a wind containing frost which strikes the harvest of a people who have wronged themselves [i.e., sinned] and destroys it. And Allāh has not wronged them, but they wrong themselves.", TextGerman = "Das Gleichnis dessen, was sie in diesem irdischen Leben ausgeben, ist das eines eisigen Windes, der die Saatfelder von Leuten trifft, die sich selbst Unrecht zugefügt haben; und so vernichtet er sie. Allah hat ihnen kein Unrecht zugefügt, sondern sie selbst fügen sich Unrecht zu." });
                        surah3.VerseList.Add(new Verse { Number = 118, TextBosnian = "O vi koji vjerujete, ne uzimajte za bliske prijatelje i zaštitnike druge mimo vas, jer drugi vam propast žele, jedva čekaju da muka dopadnete! Mržnja izbija iz usta njihovih, dok je ono što kriju grudi njihove još gore. Mi vam dokaze iznosimo, ako razumijete.", TextEnglish = "O you who have believed, do not take as intimates those other than yourselves [i.e., believers], for they will not spare you [any] ruin. They wish you would have hardship. Hatred has already appeared from their mouths, and what their breasts conceal is greater. We have certainly made clear to you the signs, if you will use reason.", TextGerman = "O die ihr glaubt, nehmt keine Vertrauten außer von euch. Sie scheuen keine Mühe, euch zu verwirren, und möchten gern, daß ihr in Bedrängnis geratet. Schon wurde aus ihren Mündern Haß offenkundig, aber was ihre Brüste verborgen halten, ist (noch) schwerwiegender. Wir haben euch die Zeichen bereits klargemacht, wenn ihr begreifen wollt." });
                        surah3.VerseList.Add(new Verse { Number = 119, TextBosnian = "Eto, vi njih volite, a oni vas ne vole, a vi vjerujete u sve Knjige. Kada vas sretnu, oni govore: 'Vjerujemo!', a čim se nasamo nađu, od zlobe prema vama vrhove prstiju grizu. Reci: 'Umrite od muke!' Allah, doista, dobro zna šta je u grudima.", TextEnglish = "Here you are loving them but they are not loving you, while you believe in the Scripture - all of it.[146] And when they meet you, they say, 'We believe.' But when they are alone, they bite their fingertips at you in rage. Say, 'Die in your rage. Indeed, Allāh is Knowing of that within the breasts.'", TextGerman = "Da habt ihr sie doch geliebt, während sie euch nicht lieben, und ihr glaubt an das gesamte Buch. Wenn sie euch treffen, sagen sie: „Wir glauben. Wenn sie jedoch allein sind, beißen sie sich in die Fingerspitzen vor Grimm gegen euch. Sag: Sterbt an eurem Grimm! Gewiß, Allah weiß über das Innerste der Brüste Bescheid." });
                        surah3.VerseList.Add(new Verse { Number = 120, TextBosnian = "Ako vas zadesi kakvo dobro, to ih ozlojedi; a zadesi li vas kakva nevolja, obraduju joj se. Ako se budete strpjeli i bogobojazni bili, njihovo lukavstvo vam nimalo neće nauditi. A Allah, doista, sve što oni rade obuhvata.", TextEnglish = "If good touches you, it distresses them; but if harm strikes you, they rejoice at it. And if you are patient and fear Allāh, their plot will not harm you at all. Indeed, Allāh is encompassing of what they do.", TextGerman = "Wenn euch etwas Gutes widerfährt, tut es ihnen leid. Wenn euch jedoch etwas Böses trifft, sind sie froh darüber. Wenn ihr aber geduldig seid und gottesfürchtig, wird euch ihre Arglist keinen Schaden zufügen. Allah umfaßt, was sie tun." });
                        surah3.VerseList.Add(new Verse { Number = 121, TextBosnian = "I sjeti se kada si poranio, porodicu svoju ostavio, da vjernicima odrediš mjesta za borbu. A Allah je Onaj Koji sve čuje i Onaj Koji sve zna.", TextEnglish = "And [remember] when you, [O Muḥammad], left your family in the morning to post the believers at their stations for the battle [of Uḥud] - and Allāh is Hearing and Knowing -", TextGerman = "Und als du von deinen Angehörigen frühmorgens weggingst, um den Gläubigen Stellungen für den Kampf zuzuweisen. Und Allah ist Allhörend und Allwissend." });
                        surah3.VerseList.Add(new Verse { Number = 122, TextBosnian = "Kada dvije skupine vaše gotovo ne uzmakoše, a Allah je njihov zaštitnik. I samo na Allaha neka se vjernici oslone!", TextEnglish = "When two parties among you were about to lose courage, but Allāh was their ally; and upon Allāh the believers should rely.", TextGerman = "Als zwei Teil(gruppen) von euch vorhatten, aufzugeben, wo doch Allah ihr Schutzherr ist. Und auf Allah sollen sich die Gläubigen verlassen." });
                        surah3.VerseList.Add(new Verse { Number = 123, TextBosnian = "Allah vas je i na Bedru pomogao, kada ste bili malobrojni! Zato se Allaha bojte, da biste bili zahvalni.", TextEnglish = "And already had Allāh given you victory at [the battle of] Badr while you were weak [i.e., few in number]. Then fear Allāh; perhaps you will be grateful.", TextGerman = "Allah hat euch doch schon in Badr geholfen, als ihr verächtlich erschient. Darum fürchtet Allah, auf daß ihr dankbar sein möget!" });
                        surah3.VerseList.Add(new Verse { Number = 124, TextBosnian = "Kada ti reče vjernicima: 'Zar vam nije dovoljno da vas Gospodar vaš pomogne sa tri hiljade poslanih meleka?'", TextEnglish = "[Remember] when you said to the believers, 'Is it not sufficient for you that your Lord should reinforce you with three thousand angels sent down?", TextGerman = "Als du zu den Gläubigen sagtest:„Genügt es euch denn nicht, daß euch euer Herr mit dreitausend herabgesandten Engeln unterstützt?" });
                        surah3.VerseList.Add(new Verse { Number = 125, TextBosnian = "Dakako! Ako strpljivi i bogobojazni budete; i ako vas oni napadnu naglo, Gospodar vaš će vam poslati pomoć od pet hiljada meleka obilježenih.", TextEnglish = "Yes, if you remain patient and conscious of Allāh and they [i.e., the enemy] come upon you [attacking] in rage, your Lord will reinforce you with five thousand angels having marks [of distinction].'", TextGerman = "Ja doch! Wenn ihr standhaft seid und gottesfürchtig und sie unverzüglich über euch kommen, unterstützt euch euer Herr mit fünftausend gekennzeichneten Engeln." });
                        surah3.VerseList.Add(new Verse { Number = 126, TextBosnian = "To je Allah učinio samo da vas obraduje i da se time srca vaša smire. A pomoći nema osim od Allaha, Silnog i Mudrog.", TextEnglish = "And Allāh made it not except as [a sign of] good tidings for you and to reassure your hearts thereby. And victory is not except from Allāh, the Exalted in Might, the Wise -", TextGerman = "Allah machte es nur zu einer frohen Botschaft für euch, und damit eure Herzen dadurch Ruhe finden -, denn der Sieg kommt nur von Allah, dem Allmächtigen, dem Allweisen -," });
                        surah3.VerseList.Add(new Verse { Number = 127, TextBosnian = "I da jedan dio nevjernika uništi, ili da ih osramoti, te da se vrate razočarani.", TextEnglish = "That He might cut down a section of the disbelievers or suppress them so that they turn back disappointed.", TextGerman = "damit Er einen Teil derjenigen, die ungläubig waren, abtrennte oder sie niederwarf, so daß sie enttäuscht umkehrten." });
                        surah3.VerseList.Add(new Verse { Number = 128, TextBosnian = "Ništa ne zavisi od tebe: bilo da im On pokajanje primi, ili da ih kazni, jer oni su, doista, zulumćari.", TextEnglish = "Not for you, [O Muḥammad, but for Allāh], is the decision whether He should [cut them down] or forgive them or punish them, for indeed, they are wrongdoers.", TextGerman = "Es ist gar nicht deine Angelegenheit, ob Er ihre Reue annimmt oder sie straft, denn sie sind ja Ungerechte." });
                        surah3.VerseList.Add(new Verse { Number = 129, TextBosnian = "Allahu pripada sve što je na nebesima i što je na Zemlji! On prašta kome On hoće, a na muke stavlja koga On hoće. Jer, Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And to Allāh belongs whatever is in the heavens and whatever is on the earth. He forgives whom He wills and punishes whom He wills. And Allāh is Forgiving and Merciful.", TextGerman = "Und Allah gehört (alles), was in den Himmeln und was auf der Erde ist. Er vergibt, wem Er will, und Er straft, wen Er will. Und Allah ist Allvergebend und Barmherzig." });
                        surah3.VerseList.Add(new Verse { Number = 130, TextBosnian = "O vi koji vjerujete, ne jedite kamatu više puta udvostručenu, i Allaha se bojte da biste spašeni bili.", TextEnglish = "O you who have believed, do not consume usury, doubled and multiplied, but fear Allāh that you may be successful.", TextGerman = "O die ihr glaubt, verschlingt nicht den Zins um ein Vielfaches vermehrt, sondern fürchtet Allah, auf daß es euch wohl ergehen möge!" });
                        surah3.VerseList.Add(new Verse { Number = 131, TextBosnian = "I čuvajte se Vatre koja je za nevjernike pripremljena.", TextEnglish = "And fear the Fire, which has been prepared for the disbelievers.", TextGerman = "Und hütet euch vor dem (Höllen)feuer, das für die Ungläubigen bereitet ist!" });
                        surah3.VerseList.Add(new Verse { Number = 132, TextBosnian = "I pokoravajte se Allahu i Poslaniku kako bi vam milost ukazana bila.", TextEnglish = "And obey Allāh and the Messenger that you may obtain mercy.", TextGerman = "Und gehorcht Allah und dem Gesandten, auf daß ihr Erbarmen finden möget!" });
                        surah3.VerseList.Add(new Verse { Number = 133, TextBosnian = "I požurite oprostu Gospodara vašega, i Džennetu širokom kao nebesa i Zemlja - pripremljenom za bogobojazne -", TextEnglish = "And hasten to forgiveness from your Lord and a garden [i.e., Paradise] as wide as the heavens and earth, prepared for the righteous", TextGerman = "Und beeilt euch um Vergebung von eurem Herrn und (um) einen (Paradies)garten, dessen Breite (wie) die Himmel und die Erde ist. Er ist für die Gottesfürchtigen bereitet," });
                        surah3.VerseList.Add(new Verse { Number = 134, TextBosnian = "za one koji udjeljuju, i kada su u obilju i kada su u oskudici, koji srdžbu savlađuju i ljudima opraštaju. A Allah voli dobročinitelje.", TextEnglish = "Who spend [in the cause of Allāh] during ease and hardship and who restrain anger and who pardon the people - and Allāh loves the doers of good;", TextGerman = "die in Freude und Leid ausgeben und ihren Grimm zurückhalten und den Menschen verzeihen. Und Allah liebt die Gutes Tuenden" });
                        surah3.VerseList.Add(new Verse { Number = 135, TextBosnian = "I za one koji, kada učine nešto loše, ili sebi zulum učine, Allaha se sjete i oprost za grijehe svoje mole. A ko oprašta grijehe, ako ne Allah?! I za one koji u grijehu svjesno ne ustraju.", TextEnglish = "And those who, when they commit an immorality or wrong themselves [by transgression], remember Allāh and seek forgiveness for their sins - and who can forgive sins except Allāh? - and [who] do not persist in what they have done while they know.", TextGerman = "und diejenigen, die, wenn sie eine Abscheulichkeit begangen oder sich selbst Unrecht zugefügt haben, Allahs gedenken und dann für ihre Sünden um Vergebung bitten - und wer sollte die Sünden vergeben außer Allah? - und (die) nicht auf dem beharren, was sie getan haben, wo sie doch wissen." });
                        surah3.VerseList.Add(new Verse { Number = 136, TextBosnian = "To su oni čija je nagrada oprost Gospodara njihova, i bašče kroz koje rijeke teku, gdje će vječno ostati. Divne li nagrade za one koji tako postupaju!", TextEnglish = "Those - their reward is forgiveness from their Lord and gardens beneath which rivers flow [in Paradise], wherein they will abide eternally; and excellent is the reward of the [righteous] workers.", TextGerman = "Der Lohn jener ist Vergebung von ihrem Herrn und Gärten, durcheilt von Bächen, ewig darin zu bleiben. Und wie trefflich ist der Lohn derjenigen, die (gut) handeln!" });
                        surah3.VerseList.Add(new Verse { Number = 137, TextBosnian = "Prije vas su mnogi narodi bili i nestali. Zato, putujte po zemlji i gledajte kako su završili oni koji su istinu poricali.", TextEnglish = "Similar situations [as yours] have passed on before you, so proceed throughout the earth and observe how was the end of those who denied.", TextGerman = "Schon vor euch sind Gesetzmäßigkeiten ergangen. So reist doch auf der Erde umher und schaut, wie das Ende der Leugner war." });
                        surah3.VerseList.Add(new Verse { Number = 138, TextBosnian = "To je objašnjenje ljudima, i uputa i pouka za one koji se Allaha boje.", TextEnglish = "This [Qur’ān] is a clear statement to [all] the people and a guidance and instruction for those conscious of Allāh.", TextGerman = "Dies ist eine klare Darlegung für die Menschen und eine Rechtleitung und Ermahnung für die Gottesfürchtigen." });
                        surah3.VerseList.Add(new Verse { Number = 139, TextBosnian = "Ne klonite duhom, i ne žalostite se! Vi ćete pobijediti, ako vjernici budete.", TextEnglish = "So do not weaken and do not grieve, and you will be superior if you are [true] believers.", TextGerman = "Und werdet nicht schwach noch seid traurig, wo ihr doch die Oberhand haben werdet, wenn ihr gläubig seid." });
                        surah3.VerseList.Add(new Verse { Number = 140, TextBosnian = "Ako vas rane spopadnu - pa sigurno su takve rane i druge spopale - i te dane pobjede Mi naizmjenice dajemo ljudima, da bi Allah znao one koji vjeruju, i odabrao neke od vas za šehide, A Allah ne voli zulumćare.", TextEnglish = "If a wound should touch you - there has already touched the [opposing] people a wound similar to it. And these days [of varying conditions] We alternate among the people so that Allāh may make evident those who believe and [may] take to Himself from among you martyrs - and Allāh does not like the wrongdoers -", TextGerman = "Wenn euch eine Wunde zugefügt worden ist, so ist dem (ungläubigen) Volk schon eine gleiche Wunde zugefügt worden. Und diese Tage (des Kriegsglücks) lassen Wir unter den Menschen wechseln, - damit Allah diejenigen, die glauben, kennt und (damit) Er Sich von euch (Blut)zeugen nimmt. Und Allah liebt nicht die Ungerechten." });
                        surah3.VerseList.Add(new Verse { Number = 141, TextBosnian = "I da bi vjernike očistio, a nevjernike uništio.", TextEnglish = "And that Allāh may purify the believers [through trials] and destroy the disbelievers.", TextGerman = "Und damit Allah diejenigen, die glauben, herausstellt und die Ungläubigen dahinschwinden läßt." });
                        surah3.VerseList.Add(new Verse { Number = 142, TextBosnian = "Zar mislite da ćete ući u Džennet, a da Allah ne zna one od vas koji se bore, i da ne zna one koji su strpljivi?!", TextEnglish = "Or do you think that you will enter Paradise while Allāh has not yet made evident those of you who fight in His cause and made evident those who are steadfast?", TextGerman = "Oder meint ihr etwa, daß ihr in den (Paradies)garten eingehen werdet, noch ehe Allah diejenigen von euch kennt, die unter euch sich (für Seine Sache) abmühen, und die Standhaften kennt?" });
                        surah3.VerseList.Add(new Verse { Number = 143, TextBosnian = "Vi ste priželjkivali smrt prije nego što ste se s njom suočili, pa ste je, eto, vidjeli i u nju ste gledali.", TextEnglish = "And you had certainly wished for death [i.e., martyrdom] before you encountered it, and you have [now] seen it [before you] while you were looking on.", TextGerman = "Und ihr habt euch ja wiederholt den Tod gewünscht, bevor ihr ihm begegnet seid. Nun habt ihr ihn mit eigenen Augen geschaut." });
                        surah3.VerseList.Add(new Verse { Number = 144, TextBosnian = "Muhammed je samo poslanik, a i prije njega je bilo poslanika. Ako bi on umro, ili ubijen bio, zar biste se vi nazad vratili?! Onaj ko se nazad vrati, Allahu neće ništa nauditi, a Allah će zahvalne nagraditi.", TextEnglish = "Muḥammad is not but a messenger. [Other] messengers have passed on before him. So if he was to die or be killed, would you turn back on your heels [to unbelief]? And he who turns back on his heels will never harm Allāh at all; but Allāh will reward the grateful.", TextGerman = "Und Muhammad ist doch nur ein Gesandter, vor dem schon Gesandte vorübergegangen sind. Wenn er nun stirbt oder getötet wird, werdet ihr euch (dann) auf den Fersen umkehren? Und wer sich auf den Fersen umkehrt, wird Allah keinerlei Schaden zufügen. Aber Allah wird (es) den Dankbaren vergelten." });
                        surah3.VerseList.Add(new Verse { Number = 145, TextBosnian = "Nijedna osoba neće umrijeti bez određenja Allahovog, časa suđenog. Ko želi nagradu na ovome svijetu, dat ćemo mu dio nagrade koji zaslužuje, a ko želi nagradu na ahiretu, dat ćemo mu dio nagrade koji zaslužuje. I zahvalne ćemo nagraditi!", TextEnglish = "And it is not [possible] for one to die except by permission of Allāh at a decree determined. And whoever desires the reward of this world - We will give him thereof; and whoever desires the reward of the Hereafter - We will give him thereof. And We will reward the grateful.", TextGerman = "Keiner Seele ist es möglich zu sterben, außer mit Allahs Erlaubnis (und) nach einer befristeten Vorbestimmung. Wer die Belohnung des Diesseits will, dem geben Wir (etwas) von ihm; und wer die Belohnung des Jenseits will, dem geben Wir (etwas) von ihm. Und Wir werden (es) den Dankbaren vergelten." });
                        surah3.VerseList.Add(new Verse { Number = 146, TextBosnian = "A koliko je bilo vjerovjesnika uz koje su se brojni pobožni ljudi borili, pa nisu klonuli zbog onoga što bi ih zadesilo na putu Allahovom, niti su posustajali, niti se predavali?! A Allah voli strpljive.", TextEnglish = "And how many a prophet [fought in battle and] with him fought many religious scholars. But they never lost assurance due to what afflicted them in the cause of Allāh, nor did they weaken or submit. And Allāh loves the steadfast.", TextGerman = "Und mit wie vielen Propheten zusammen kämpften zahlreiche Bekenner des Herrn! Doch sie gaben nicht auf ob dessen, was sie auf Allahs Weg traf, noch wurden sie schwach, noch unterwarfen sie sich. Und Allah liebt die Standhaften." });
                        surah3.VerseList.Add(new Verse { Number = 147, TextBosnian = "Oni nisu govorili ništa drugo nego: 'Gospodaru naš, oprosti nam grijehe naše i pretjerivanje u postupcima našim, učvrsti korake naše i pomozi nam protiv naroda nevjerničkog.'", TextEnglish = "And their words were not but that they said, 'Our Lord, forgive us our sins and the excess [committed] in our affairs and plant firmly our feet and give us victory over the disbelieving people.'", TextGerman = "Ihre Worte waren nichts anderes, als daß sie sagten: „Unser Herr, vergib uns unsere Sünden und die Maßlosigkeit in unserer Angelegenheit, festige unsere Füße und hilf uns gegen das ungläubige Volk!" });
                        surah3.VerseList.Add(new Verse { Number = 148, TextBosnian = "Pa im je Allah dao nagradu na dunjaluku i ljepotu nagrade na ahiretu, a Allah voli dobročinitelje.", TextEnglish = "So Allāh gave them the reward of this world and the good reward of the Hereafter. And Allāh loves the doers of good.", TextGerman = "Da gab Allah ihnen die Belohnung des Diesseits und die schöne Belohnung des Jenseits. Und Allah liebt die Gutes Tuenden." });
                        surah3.VerseList.Add(new Verse { Number = 149, TextBosnian = "O vi koji vjerujete, ako se budete pokoravali onima koji ne vjeruju, vratit će vas unazad pa ćete postati gubitnici.", TextEnglish = "O you who have believed, if you obey those who disbelieve, they will turn you back on your heels, and you will [then] become losers.", TextGerman = "O die ihr glaubt, wenn ihr denen, die ungläubig sind, gehorcht, lassen sie euch auf den Fersen kehrtmachen, und dann werdet ihr als Verlierer zurückkehren." });
                        surah3.VerseList.Add(new Verse { Number = 150, TextBosnian = "Međutim, samo je Allah zaštitnik vaš i On je najbolji pomagač.", TextEnglish = "But Allāh is your protector, and He is the best of helpers.", TextGerman = "Nein! Vielmehr ist Allah euer Schutzherr; und Er ist der beste Helfer." });
                        surah3.VerseList.Add(new Verse { Number = 151, TextBosnian = "Mi ćemo uliti strah u srca onih koji ne vjeruju, zato što Allahu pridružuju ono o čemu Allah nije spustio nikakav dokaz; Vatra je njihovo boravište, a grozno je stjecište zulumćara.", TextEnglish = "We will cast terror into the hearts of those who disbelieve for what they have associated with Allāh of which He had not sent down [any] authority.[147] And their refuge will be the Fire, and wretched is the residence of the wrongdoers.", TextGerman = "Wir werden in die Herzen derjenigen, die ungläubig sind, Schrecken einjagen dafür, daß sie Allah (andere Götter) beigesellt haben, wozu Er keine Ermächtigung offenbart hat. Ihr Zufluchtsort wird das (Höllen)feuer sein - ein schlimmer Aufenthaltsort für die Ungerechten!" });
                        surah3.VerseList.Add(new Verse { Number = 152, TextBosnian = "Allah je već potvrdio obećanje Svoje, kada ste ih vi uništavali, dozvolom Njegovom, dok niste neuspjeh doživjeli i o naredbi se razišli, i nepokorni bili, nakon što vam je pokazao šta volite: neki od vas žele dunjaluk, a drugi ahiret! Onda vas je On odvojio od njih, kako bi vas iskušao! I oprostio vam je; a Allah je prema vjernicima neizmjerno dobar.", TextEnglish = "And Allāh had certainly fulfilled His promise to you when you were killing them [i.e., the enemy] by His permission until [the time] when you lost courage and fell to disputing about the order [given by the Prophet (ﷺ)] and disobeyed after He had shown you that which you love.[148] Among you are some who desire this world, and among you are some who desire the Hereafter. Then He turned you back from them [defeated] that He might test you. And He has already forgiven you, and Allāh is the possessor of bounty for the believers.", TextGerman = "Allah hat ja Sein Versprechen euch gegenüber gehalten, als ihr sie mit Seiner Erlaubnis vernichtetet, bis daß ihr den Mut verlort und über die Angelegenheit miteinander strittet und euch widersetztet, nachdem Er euch gezeigt hatte, was euch lieb ist. - Unter euch gibt es manche, die das Diesseits wollen; unter euch gibt es aber auch manche, die das Jenseits wollen. - Hierauf wandte Er euch von ihnen ab, um euch zu prüfen. Nun hat Er euch wahrlich schon verziehen, denn Allah ist voll Huld gegen die Gläubigen." });
                        surah3.VerseList.Add(new Verse { Number = 153, TextBosnian = "Kada ste uzmicali penjući se i ne obazirući se ni na koga, dok vas je Poslanik zvao iza leđa vaših, Allah vas je kaznio nedaćom na nedaću, kako ne biste tugovali za onim što vam je izmaklo niti zbog onoga što vas je zadesilo. A Allah u potpunosti zna ono što radite.", TextEnglish = "[Remember] when you [fled and] climbed [the mountain] without looking aside at anyone while the Messenger was calling you from behind. So Allāh repaid you with distress upon distress so you would not grieve for that which had escaped you [of victory and spoils of war] or [for] that which had befallen you [of injury and death]. And Allāh is [fully] Aware of what you do.", TextGerman = "Als ihr fortlieft und euch nach niemandem umdrehtet, während der Gesandte euch von hinten her rief. Da belohnte Er euch mit Kummer für (den) Kummer (des Propheten), damit ihr weder traurig wäret über das, was euch entgangen war, noch über das, was euch getroffen hatte. Und Allah ist Kundig dessen, was ihr tut." });
                        surah3.VerseList.Add(new Verse { Number = 154, TextBosnian = "Zatim vam je, poslije nevolje, spokojstvo spustio, san koji je neke od vas obuzeo, dok su se drugi brinuli samo o sebi, misleći o Allahu ono što nije istina, mislima paganskim. Govorili su: 'Imamo li mi išta u svemu ovom?'!' Reci: 'Odredba u cjelosti pripada samo Allahu!' Oni u sebi kriju ono što tebi ne pokazuju. I govorili su: 'Da smo išta u ovome imali, ne bismo ovdje izginuli!' Reci: 'I da ste u kućama svojim bili, opet bi oni kojima je propisano da poginu izišli na mjesta pogibije svoje, da Allah iskuša šta je u grudima vašim i da ispita šta je u srcima vašim! A Allah dobro zna šta je u grudima.", TextEnglish = "Then after distress, He sent down upon you security [in the form of] drowsiness, overcoming a faction of you, while another faction worried about themselves, thinking of Allāh other than the truth - the thought of ignorance, saying, 'Is there anything for us [to have done] in this matter?' Say, 'Indeed, the matter belongs completely to Allāh.' They conceal within themselves what they will not reveal to you. They say, 'If there was anything we could have done in the matter, we [i.e., some of us] would not have been killed right here.' Say, 'Even if you had been inside your houses, those decreed to be killed would have come out to their death beds.' [It was] so that Allāh might test what is in your breasts and purify what is in your hearts. And Allāh is Knowing of that within the breasts.", TextGerman = "Dann sandte Er nach dem Kummer Sicherheit auf euch herab, Schläfrigkeit, die einen Teil von euch überkam, während ein (anderer) Teil um sich selbst besorgt war, indem sie von Allah in Gedanken der Unwissenheit dachte, was dem Wahren nicht entspricht. Sie sagten: „Haben wir denn irgend etwas in der Angelegenheit (zu entscheiden gehabt)? Sag: Die Angelegenheit ist ganz und gar (in) Allahs (Entscheidung). Sie halten in ihrem Innersten verborgen, was sie dir nicht offenlegen; sie sagen: „Wenn wir etwas von der Angelegenheit (zu entscheiden) hätten, würden wir hier nicht getötet. Sag: Wenn ihr auch in euren Häusern gewesen wäret, wären diejenigen, denen der Tod vorgezeichnet war, an den Stellen erschienen, wo sie (als Gefallene) liegen sollten, - damit Allah prüfe, was in euren Brüsten und herausstellt, was in euren Herzen ist. Und Allah weiß über das Innerste der Brüste Bescheid." });
                        surah3.VerseList.Add(new Verse { Number = 155, TextBosnian = "One od vas koji su uzmakli na dan kada su se dvije skupine sukobile, doista je šejtan naveo da posrnu zbog nečeg od onoga što su prije zaradili. A Allah im je već oprostio, Allah je, zaista, Onaj Koji oprašta grijehe i Onaj Koji ne žuri s kaznom.", TextEnglish = "Indeed, those of you who turned back on the day the two armies met [at Uḥud] - it was Satan who caused them to slip because of some [blame] they had earned. But Allāh has already forgiven them. Indeed, Allāh is Forgiving and Forbearing.", TextGerman = "Gewiß, diejenigen von euch, die sich an dem Tag, da die beiden Heere aufeinandertrafen, abkehrten, ließ nur der Satan straucheln durch etwas von dem, was sie begangen hatten. Aber wahrlich, nun hat Allah ihnen verziehen. Allah ist Allvergebend und Nachsichtig." });
                        surah3.VerseList.Add(new Verse { Number = 156, TextBosnian = "O vi koji vjerujete, ne budite kao oni, koji ne vjeruju i koji govore za braću svoju, kada se na put po svijetu otisnu ili se budu borili: 'Da su bili s nama, ne bi umrli, niti bi poginuli!' Jer, Allah time čini tugu u srcima njihovim, a Allah daje i život, i smrt. On dobro vidi ono što vi radite.", TextEnglish = "O you who have believed, do not be like those who disbelieved and said about their brothers when they traveled through the land or went out to fight, 'If they had been with us, they would not have died or have been killed,' so Allāh makes that [misconception] a regret within their hearts. And it is Allāh who gives life and causes death, and Allāh is Seeing of what you do.", TextGerman = "O die ihr glaubt, seid nicht wie diejenigen, die ungläubig sind und von ihren Brüdern, wenn sie im Lande umhergereist sind oder sich auf einem Kriegszug befunden haben, sagen: „Wenn sie bei uns geblieben wären, wären sie nicht gestorben und nicht getötet worden, damit Allah dies zu einer gramvollen Reue in ihren Herzen mache. Allah macht lebendig und läßt sterben. Und was ihr tut, sieht Allah wohl." });
                        surah3.VerseList.Add(new Verse { Number = 157, TextBosnian = "A ako vi na Allahovom putu poginete ili umrete, pa Allahov oprost i milost bolji su od onoga što oni gomilaju.", TextEnglish = "And if you are killed in the cause of Allāh or die - then forgiveness from Allāh and mercy are better than whatever they accumulate [in this world].", TextGerman = "Und wenn ihr auf Allahs Weg getötet werdet oder sterbt, so sind Vergebung von Allah und Erbarmen fürwahr besser als (all) das, was sie zusammentragen." });
                        surah3.VerseList.Add(new Verse { Number = 158, TextBosnian = "I bilo da umrete ili poginete, vi ćete pred Allaha sigurno sakupljeni biti.", TextEnglish = "And whether you die or are killed, unto Allāh you will be gathered.", TextGerman = "Und wenn ihr sterbt oder getötet werdet, so werdet ihr fürwahr zu Allah versammelt werden." });
                        surah3.VerseList.Add(new Verse { Number = 159, TextBosnian = "S Allahovom milošću ti si blag prema njima; a da si grub i tvrda srca bio, sigurno bi pobjegli na sve strane od tebe. Zato im praštaj i moli da im bude oprošteno, i dogovaraj se s njima. A kada se odlučiš, na Allaha se osloni; Allah, doista voli one koji se na Njega oslanjaju.", TextEnglish = "So by mercy from Allāh, [O Muḥammad], you were lenient with them. And if you had been rude [in speech] and harsh in heart, they would have disbanded from about you. So pardon them and ask forgiveness for them and consult them in the matter. And when you have decided, then rely upon Allāh. Indeed, Allāh loves those who rely [upon Him].", TextGerman = "Durch Erbarmen von Allah bist du mild zu ihnen gewesen; wärst du aber schroff und hartherzig, so würden sie wahrlich rings um dich auseinanderlaufen. So verzeihe ihnen, bitte für sie um Vergebung und ziehe sie in den Angelegenheiten zu Rate. Und wenn du dich entschlossen hast, dann verlasse dich auf Allah! Gewiß, Allah liebt die sich (auf Ihn) Verlassenden." });
                        surah3.VerseList.Add(new Verse { Number = 160, TextBosnian = "Ako vas Allah pomogne, niko vas pobijediti neće; a ako vas napusti, pa ko je taj koji vam, osim Njega, može pomoći?! Pa, na Allaha neka se zato oslanjaju vjernici!", TextEnglish = "If Allāh should aid you, no one can overcome you; but if He should forsake you, who is there that can aid you after Him? And upon Allāh let the believers rely.", TextGerman = "Wenn Allah euch zum Sieg verhilft, so kann euch keiner besiegen. Doch wenn Er euch im Stich läßt, wer ist es denn, der euch dann, nach Ihm, noch helfen könnte? Und auf Allah sollen sich die Gläubigen verlassen." });
                        surah3.VerseList.Add(new Verse { Number = 161, TextBosnian = "Nije moguće da Vjerovjesnik šta utaji! A onaj ko šta utaji, doći će na Kijametski dan s tim što je utajio. Zatim će svakome u potpunosti biti dato ono što je zaslužio, i nikome neće zulum biti učinjen.", TextEnglish = "It is not [attributable] to any prophet that he would act unfaithfully [in regard to war booty]. And whoever betrays, [taking unlawfully], will come with what he took on the Day of Resurrection. Then will every soul be [fully] compensated for what it earned, and they will not be wronged.", TextGerman = "Es steht keinem Propheten zu, (etwas) zu veruntreuen. Und wer (etwas) veruntreut, wird das, was er veruntreut hat, am Tag der Auferstehung bringen. Dann wird jeder Seele in vollem Maß zukommen, was sie verdient hat, und es wird ihnen kein Unrecht zugefügt." });
                        surah3.VerseList.Add(new Verse { Number = 162, TextBosnian = "Zar je onaj koji je Allahovo zadovoljstvo slijedio isti kao onaj koji je Allahovu srdžbu navukao i čije prebivalište je Džehennem?! A užasno je to konačno odredište!", TextEnglish = "So is one who pursues the pleasure of Allāh like one who brings upon himself the anger of Allāh and whose refuge is Hell? And wretched is the destination.", TextGerman = "Ist denn jemand, der Allahs Wohlgefallen folgt, wie einer, der sich Allahs Mißfallen zuzieht und dessen Zufluchtsort die Hölle ist? - Ein schlimmer Ausgang!" });
                        surah3.VerseList.Add(new Verse { Number = 163, TextBosnian = "Oni su kod Allaha na različitim stepenima i Allah dobro vidi šta oni rade!", TextEnglish = "They are [varying] degrees in the sight of Allāh, and Allāh is Seeing of whatever they do.", TextGerman = "Sie nehmen (unterschiedliche) Rangstufen bei Allah ein. Und Allah sieht wohl, was sie tun." });
                        surah3.VerseList.Add(new Verse { Number = 164, TextBosnian = "Allah je vjernike milošću Svojom darovao kada im je poslanika između njih poslao, da im ajete Njegove kazuje, da ih očisti, i da ih Knjizi i mudrosti pouči, iako su prije u očitoj zabludi bili.", TextEnglish = "Certainly did Allāh confer [great] favor upon the believers when He sent among them a Messenger from themselves, reciting to them His verses and purifying them and teaching them the Book [i.e., the Qur’ān] and wisdom,[149] although they had been before in manifest error.", TextGerman = "Allah hat den Gläubigen wirklich eine Wohltat erwiesen, als Er unter ihnen einen Gesandten von ihnen selbst geschickt hat, der ihnen Seine Zeichen verliest, und sie läutert und sie das Buch und die Weisheit lehrt, obgleich sie sich zuvor wahrlich in deutlichem Irrtum befanden." });
                        surah3.VerseList.Add(new Verse { Number = 165, TextBosnian = "Zar niste rekli, kada vas je snašla nevolja koju ste vi njima dvostruko nanijeli: 'Odakle sada ovo?' Reci: 'To je od vas samih!' Allah, doista, nad svime ima moć.", TextEnglish = "Why [is it that] when a [single] disaster struck you [on the day of Uḥud], although you had struck [the enemy in the battle of Badr] with one twice as great, you said, 'From where is this?' Say, 'It is from yourselves [i.e., due to your sin].' Indeed, Allāh is over all things competent.", TextGerman = "Ist es nicht (so), daß, als euch ein Unglück traf, obwohl ihr (den Feind) mit einem zweimal so großen getroffen hattet, ihr sagtet: „Woher kommt das? Sag: Es kommt von euch selbst. Gewiß, Allah hat zu allem die Macht." });
                        surah3.VerseList.Add(new Verse { Number = 166, TextBosnian = "Ono što vas je zadesilo kada su se dvije skupine sukobile, bilo je Allahovom dozvolom i da bi znao ko su vjernici.", TextEnglish = "And what struck you on the day the two armies met [at Uḥud] was by permission of Allāh that He might make evident the [true] believers", TextGerman = "Und was euch an dem Tag traf, da die beiden Heere aufeinandertrafen, so (geschah) das mit Allahs Erlaubnis, - damit Er die Gläubigen kennt" });
                        surah3.VerseList.Add(new Verse { Number = 167, TextBosnian = "I da bi znao ko su licemjeri. Njima je rečeno: 'Dođite, borite se na Allahovom putu, ili se branite!', a oni su kazali: 'Da znamo da će biti borbe, mi bismo vas slijedili.' Toga dana oni su bili bliži nevjerovanju nego vjerovanju. Svojim su ustima govorili ono što nije bilo u srcima njihovim! A Allah najbolje zna što oni kriju.", TextEnglish = "And that He might make evident those who are hypocrites. For it was said to them, 'Come, fight in the way of Allāh or [at least] defend.' They said, 'If we had known [there would be] battle, we would have followed you.' They were nearer to disbelief that day than to faith, saying with their mouths what was not in their hearts. And Allāh is most knowing of what they conceal -", TextGerman = "und damit Er diejenigen kennt, die heucheln, zu denen gesagt wurde: „Kommt her und kämpft auf Allahs Weg oder wehrt ab, worauf sie sagten: „Wenn wir zu kämpfen wüßten, würden wir euch wahrlich folgen. Sie waren an jenem Tag dem Unglauben näher als dem Glauben. Sie sagen mit ihren Mündern, was nicht in ihren Herzen ist. Doch Allah weiß sehr wohl, was sie verbergen." });
                        surah3.VerseList.Add(new Verse { Number = 168, TextBosnian = "Onima koji se nisu borili, a o braći svojoj su govorili: 'Da su nas poslušali, ne bi izginuli!', reci: 'Od sebe smrt odagnajte, ako istinu govorite!'", TextEnglish = "Those who said about their brothers while sitting [at home], 'If they had obeyed us, they would not have been killed.' Say, 'Then prevent death from yourselves, if you should be truthful.'", TextGerman = "Diejenigen, die von ihren Brüdern sagten, während sie (selbst daheim) sitzen blieben: „Wenn sie uns gehorcht hätten, wären sie nicht getötet worden. Sag: So wehrt den Tod von euch selbst ab, wenn ihr wahrhaftig seid!" });
                        surah3.VerseList.Add(new Verse { Number = 169, TextBosnian = "Nikako ne smatraj mrtvima one koji su na Allahovom putu poginuli. Ne, oni su živi i opskrbljeni su kod Gospodara svoga.", TextEnglish = "And never think of those who have been killed in the cause of Allāh as dead. Rather, they are alive with their Lord, receiving provision,", TextGerman = "Und meine ja nicht, diejenigen, die auf Allahs Weg getötet worden sind, seien (wirklich) tot. Nein! Vielmehr sind sie lebendig bei ihrem Herrn und werden versorgt" });
                        surah3.VerseList.Add(new Verse { Number = 170, TextBosnian = "Radosni su zbog onoga što im je Allah iz obilja Svoga dao, i veseli zbog onih koji im se još nisu pridružili, za koje nikakva straha neće biti i koji ni za čim tugovati neće.", TextEnglish = "Rejoicing in what Allāh has bestowed upon them of His bounty, and they receive good tidings about those [to be martyred] after them who have not yet joined them - that there will be no fear concerning them, nor will they grieve.", TextGerman = "und sind froh über das, was Allah ihnen von Seiner Huld gewährt hat, und sind glückselig über diejenigen, die sich nach ihnen noch nicht angeschlossen haben, daß keine Furcht über sie kommen soll, noch sie traurig sein sollen." });
                        surah3.VerseList.Add(new Verse { Number = 171, TextBosnian = "Oni se raduju Allahovoj blagodati i dobroti, te tome što Allah neće dopustiti da propadne nagrada koja pripada vjernicima.", TextEnglish = "They receive good tidings of favor from Allāh and bounty and [of the fact] that Allāh does not allow the reward of believers to be lost -", TextGerman = "Sie sind glückselig über eine Gunst von Allah und eine Huld und (darüber), daß Allah den Lohn der Gläubigen nicht verlorengehen läßt." });
                        surah3.VerseList.Add(new Verse { Number = 172, TextBosnian = "Onima koji su se Allahu i Poslaniku odazvali i nakon zadobijenih rana, onima između njih koji su dobro činili i bogobojazni bili - pripada nagrada velika.", TextEnglish = "Those [believers] who responded to Allāh and the Messenger after injury had struck them. For those who did good among them and feared Allāh is a great reward -", TextGerman = "Diejenigen, die auf Allah und den Gesandten hörten, nachdem ihnen eine Wunde zugefügt worden war - für diejenigen unter ihnen, die Gutes tun und gottesfürchtig sind, wird es großartigen Lohn geben." });
                        surah3.VerseList.Add(new Verse { Number = 173, TextBosnian = "Onima kojima se - kada su im ljudi rekli: 'Neprijatelji se okupljaju zbog vas, pa pazite ih se!' - vjerovanje povećalo, pa su kazali: 'Nama je Allah dovoljan, a divan je On Zaštitnik.'", TextEnglish = "Those to whom people [i.e., hypocrites] said, 'Indeed, the people have gathered against you, so fear them.' But it [merely] increased them in faith, and they said, 'Sufficient for us is Allāh, and [He is] the best Disposer of affairs.'[150]", TextGerman = "Diejenigen, zu denen die Menschen sagten: „Die Menschen haben (sich) bereits gegen euch versammelt; darum fürchtet sie! - Doch da mehrte das (nur) ihren Glauben, und sie sagten: „Unsere Genüge ist Allah, und wie trefflich ist der Sachwalter!" });
                        surah3.VerseList.Add(new Verse { Number = 174, TextBosnian = "Pa su se vratili s Allahovim blagodatima i obiljem; nikakvo ih zlo nije zadesilo. Slijedili su zadovoljstvo Allahovo, a Allah je posjednik velikog dobra.", TextEnglish = "So they returned with favor from Allāh and bounty, no harm having touched them. And they pursued the pleasure of Allāh, and Allāh is the possessor of great bounty.", TextGerman = "So kehrten sie mit einer Gunst von Allah und einer Huld zurück, ohne daß ihnen etwas Böses widerfahren war. Sie folgten Allahs Wohlgefallen, und Allah ist voll großer Huld." });
                        surah3.VerseList.Add(new Verse { Number = 175, TextBosnian = "To vas samo šejtan plaši pristalicama svojim, pa ih se ne bojte, nego se Mene bojte, ako ste vjernici!", TextEnglish = "That is only Satan who frightens [you] of his supporters. So fear them not, but fear Me, if you are [indeed] believers.", TextGerman = "Dies ist nur der Satan, der (euch) mit seinen Gefolgsleuten Furcht einzuflößen sucht. Fürchtet sie aber nicht, sondern fürchtet Mich, wenn ihr gläubig seid!" });
                        surah3.VerseList.Add(new Verse { Number = 176, TextBosnian = "Neka te ne žaloste oni koji srljaju u nevjerovanje. Oni nimalo neće Allahu nauditi. Allah neće da im učini bilo kakvo dobro na ahiretu i njima pripada patnja velika.", TextEnglish = "And do not be grieved, [O Muḥammad], by those who hasten into disbelief. Indeed, they will never harm Allāh at all. Allāh intends that He should give them no share in the Hereafter, and for them is a great punishment.", TextGerman = "Und lasse dich nicht durch jene traurig machen, die im Unglauben dahineilen! Sie werden Allah gewiß keinerlei Schaden zufügen. Allah will, daß ihnen im Jenseits kein Anteil zugewiesen wird. Für sie wird es gewaltige Strafe geben." });
                        surah3.VerseList.Add(new Verse { Number = 177, TextBosnian = "Oni koji su vjerovanje za nevjerovanje prodali, Allahu neće ništa nauditi. Njima pripada patnja bolna.", TextEnglish = "Indeed, those who purchase disbelief [in exchange] for faith - never will they harm Allāh at all, and for them is a painful punishment.", TextGerman = "Diejenigen, die den Unglauben für den Glauben erkauft haben, werden Allah keinerlei Schaden zufügen, und für sie wird es schmerzhafte Strafe geben." });
                        surah3.VerseList.Add(new Verse { Number = 178, TextBosnian = "Neka nevjernici nikako ne misle da je dobro za njih to što im dajemo dug život. Mi im dug život dajemo samo zbog toga kako bi ogrezli u grijehu i njima pripada patnja ponižavajuća.", TextEnglish = "And let not those who disbelieve ever think that [because] We extend their time [of enjoyment] it is better for them. We only extend it for them so that they may increase in sin, and for them is a humiliating punishment.", TextGerman = "Und diejenigen, die ungläubig sind, sollen ja nicht meinen, daß das, was wir ihnen an Aufschub gewähren, gut für sich selbst sei.Wir gewähren ihnen nur Aufschub, damit sie an Sünde zunehmen, und für sie gibt es schmachvolle Strafe." });
                        surah3.VerseList.Add(new Verse { Number = 179, TextBosnian = "I ne može biti da Allah vjernike ostavi u stanju u kojem ste vi, dok loše od dobrih ne odvoji. I ne može biti da vam Allah otkrije ono što je čulima nedokučivo, nego On odabire koga hoće od poslanika Svojih. Zato, vjerujte u Allaha i poslanike Njegove, a ako vjerovali budete i Allaha se bojali i grijeha se čuvali, vama pripada velika nagrada.", TextEnglish = "Allāh would not leave the believers in that [state] you are in [presently] until He separates the evil from the good. Nor would Allāh reveal to you the unseen. But [instead], Allāh chooses of His messengers whom He wills, so believe in Allāh and His messengers. And if you believe and fear Him, then for you is a great reward.", TextGerman = "Nimmer wird Allah die Gläubigen in dem (Zustand) belassen, in dem ihr euch befindet, bis Er das Schlechte vom Guten gesondert hat. Und nimmer wird Allah euch Einblick in das Verborgene gewähren, doch Allah erwählt von Seinen Gesandten, wen Er will. So glaubt an Allah und Seine Gesandten! Und wenn ihr glaubt und gottesfürchtig seid, dann wird es für euch großartigen Lohn geben." });
                        surah3.VerseList.Add(new Verse { Number = 180, TextBosnian = "Neka oni koji škrtare u onome što im Allah iz obilja Svoga daje, nikako ne misle da je to dobro za njih. Naprotiv, to je zlo za njih. Na Kijametskom danu bit će im oko vrata omotano ono čime su škrtarili. A Allahu pripada nasljedstvo nebesa i Zemlje i Allah dobro zna ono što vi radite.", TextEnglish = "And let not those who [greedily] withhold what Allāh has given them of His bounty ever think that it is better for them. Rather, it is worse for them. Their necks will be encircled by what they withheld on the Day of Resurrection. And to Allāh belongs the heritage of the heavens and the earth. And Allāh, of what you do, is [fully] Aware.", TextGerman = "Und diejenigen, die mit dem geizen, was Allah ihnen von Seiner Huld gewährt hat, sollen ja nicht meinen, das sei (so) besser für sie. Nein! Vielmehr ist es schlecht für sie. Mit dem, womit sie gegeizt haben, werden sie am Tag der Auferstehung umschlungen werden. Und Allah gehört das Erbe der Himmel und der Erde, und Allah ist dessen, was ihr tut, Kundig." });
                        surah3.VerseList.Add(new Verse { Number = 181, TextBosnian = "Allah je čuo riječi onih koji su govorili: 'Allah je siromašan, a mi smo bogati!' Mi ćemo zabilježiti ono što su oni govorili kao i to što su bez opravdanja vjerovjesnike ubijali. I reći ćemo: 'Iskusite kaznu u Ognju!'", TextEnglish = "Allāh has certainly heard the statement of those [Jews] who said, 'Indeed, Allāh is poor, while we are rich.' We will record what they said and their killing of the prophets without right and will say, 'Taste the punishment of the Burning Fire.", TextGerman = "Allah hat ja die Worte derjenigen gehört, die sagten: „Allah ist gewiß arm, und wir sind reich. Wir werden aufschreiben, was sie sagten, und daß sie die Propheten zu Unrecht töteten; und Wir werden zu ihnen sagen „Kostet die Strafe des Brennens!" });
                        surah3.VerseList.Add(new Verse { Number = 182, TextBosnian = "To je zbog djela vaših ruku, a Allah, zaista, ne čini zulum robovima Svojim.", TextEnglish = "That is for what your hands have put forth and because Allāh is not ever unjust to [His] servants.'", TextGerman = "Dies (geschieht) wegen dessen, was eure Hände vorausgeschickt haben, und deshalb, weil Allah keiner ist, der den Menschen Unrecht zufügt." });
                        surah3.VerseList.Add(new Verse { Number = 183, TextBosnian = "Onima koji govore: 'Allah je od nas obavezu uzeo da ne vjerujemo nijednom poslaniku dok ne prinese žrtvu koju će vatra progutati!', reci: 'I prije mene su vam poslanici s jasnim dokazima dolazili, a i s tim o čemu govorite. Pa zašto ste ih, onda, ubijali - ako istinu govorite?'", TextEnglish = "[They are] those who said, 'Indeed, Allāh has taken our promise not to believe any messenger until he brings us an offering which fire [from heaven] will consume.' Say, 'There have already come to you messengers before me with clear proofs and [even] that of which you speak. So why did you kill them, if you should be truthful?'", TextGerman = "(Es sind) diejenigen, die sagten: „Allah hat uns verpflichtet, keinem Gesandten zu glauben, bis er uns ein Opfer bringt, das vom Feuer verzehrt wird. Sag: „Schon vor mir sind Gesandte mit klaren Beweisen zu euch gekommen und mit dem, wovon ihr spracht. Warum habt ihr sie dann getötet, wenn ihr wahrhaftig seid?" });
                        surah3.VerseList.Add(new Verse { Number = 184, TextBosnian = "A ako te budu lašcem smatrali, pa i prije tebe su lašcima smatrani poslanici koji su Jasne dokaze i spise i Knjigu svjetilju donosili.", TextEnglish = "Then if they deny you, [O Muḥammad] - so were messengers denied before you, who brought clear proofs and written ordinances and the enlightening Scripture.[151]", TextGerman = "Wenn sie dich der Lüge bezichtigen, so sind bereits Gesandte vor dir der Lüge bezichtigt worden, die mit den klaren Beweisen, den Büchern der Weisheit und den erleuchtenden Büchern kamen." });
                        surah3.VerseList.Add(new Verse { Number = 185, TextBosnian = "Svaka osoba smrt će okusiti, a samo ćete na Kijametskom danu u potpunosti dobiti plaće vaše. Pa ko bude od Vatre udaljen i u Džennet uveden, uspio je. A život na dunjaluku samo je varljivo naslađivanje.", TextEnglish = "Every soul will taste death, and you will only be given your [full] compensation on the Day of Resurrection. So he who is drawn away from the Fire and admitted to Paradise has attained [his desire]. And what is the life of this world except the enjoyment of delusion.", TextGerman = "Jede Seele wird den Tod kosten. Und erst am Tag der Auferstehung wird euch euer Lohn in vollem Maß zukommen. Wer dann dem (Höllen)feuer entrückt und in den (Paradies)garten eingelassen wird, der hat fürwahr einen Erfolg erzielt. Und das diesseitige Leben ist nur trügerischer Genuß." });
                        surah3.VerseList.Add(new Verse { Number = 186, TextBosnian = "Vi ćete sigurno biti iskušavani u imecima vašim, i životima vašim, a slušat ćete od onih kojima je prije vas data Knjiga i od višebožaca mnoge neugodnosti. Ako budete strpljivi i Allaha se bojali, to, doista, u istinske postupke spada.", TextEnglish = "You will surely be tested in your possessions and in yourselves. And you will surely hear from those who were given the Scripture before you and from those who associate others with Allāh much abuse. But if you are patient and fear Allāh - indeed, that is of the matters [worthy] of resolve.", TextGerman = "Ihr werdet ganz gewiß in eurem Besitz und in eurer eigenen Person geprüft werden, und ihr werdet ganz gewiß von denjenigen, denen die Schrift vor euch gegeben wurde, und denen, die (Allah etwas) beigesellen, viel Beleidigendes zu hören bekommen. Doch wenn ihr standhaft und gottesfürchtig seid, so gehört dies gewiß zur Entschlossenheit (in der Handhabung) der Angelegenheiten." });
                        surah3.VerseList.Add(new Verse { Number = 187, TextBosnian = "A kada Allah uze obavezu od onih kojima je data Knjiga da će to sigurno ljudima objašnjavati i ništa iz nje neće kriti, oni su je za leđa svoja bacili, prodajući je za bagatelnu cijenu. A ružno li je ono što za to kupuju!", TextEnglish = "And [mention, O Muḥammad], when Allāh took a covenant from those who were given the Scripture, [saying], 'You must make it clear [i.e., explain it] to the people and not conceal it.' But they threw it away behind their backs and exchanged it for a small price. And wretched is that which they purchased.", TextGerman = "Und (gedenkt,) als Allah mit denjenigen, denen die Schrift gegeben worden war, ein Abkommen traf: „Ihr sollt sie den Menschen ganz gewiß klar machen und sie nicht verborgen halten! Da warfen sie sie hinter ihren Rücken und verkauften sie für einen geringen Preis; wie schlimm ist das, was sie erkaufen!" });
                        surah3.VerseList.Add(new Verse { Number = 188, TextBosnian = "Nikako ne misli za one koje raduje to što su uradili i koji vole biti pohvaljeni i za ono što nisu učinili - nikako ne misli da će se patnje sačuvati. Njima pripada patnja bolna.", TextEnglish = "And never think that those who rejoice in what they have perpetrated and like to be praised for what they did not do - never think them [to be] in safety from the punishment, and for them is a painful punishment.", TextGerman = "Und meine ja nicht, daß diejenigen, die froh sind über das, was sie begangen haben, und denen es lieb ist, für etwas gelobt zu werden, was sie nicht getan haben, - meine ja nicht, sie seien der Strafe entronnen! Für sie wird es schmerzhafte Strafe geben." });
                        surah3.VerseList.Add(new Verse { Number = 189, TextBosnian = "Allahu pripada vlast na nebesima i na Zemlji i Allah nad svime ima moć.", TextEnglish = "And to Allāh belongs the dominion of the heavens and the earth, and Allāh is over all things competent.", TextGerman = "Allah gehört die Herrschaft der Himmel und der Erde. Allah hat zu allem die Macht." });
                        surah3.VerseList.Add(new Verse { Number = 190, TextBosnian = "U stvaranju nebesa i Zemlje, i u izmjeni noći i dana, doista su znakovi za razumom obdarene,", TextEnglish = "Indeed, in the creation of the heavens and the earth and the alternation of the night and the day are signs for those of understanding -", TextGerman = "In der Schöpfung der Himmel und der Erde und in dem Unterschied von Nacht und Tag liegen wahrlich Zeichen für diejenigen, die Verstand besitzen," });
                        surah3.VerseList.Add(new Verse { Number = 191, TextBosnian = "koji Allaha spominju stojeći, sjedeći i ležeći na svojim bokovima, i o stvaranju nebesa i Zemlje razmišljaju. 'Gospodaru naš, Ti ovo nisi uzalud stvorio! Uzvišen si Ti i sačuvaj nas od patnje u Vatri!", TextEnglish = "Who remember Allāh while standing or sitting or [lying] on their sides and give thought to the creation of the heavens and the earth, [saying], 'Our Lord, You did not create this aimlessly; exalted are You [above such a thing]; then protect us from the punishment of the Fire.", TextGerman = "die Allahs stehend, sitzend und auf der Seite (liegend) gedenken und über die Schöpfung der Himmel und der Erde nachdenken: „Unser Herr, Du hast (all) dies nicht umsonst erschaffen. Preis sei Dir! Bewahre uns vor der Strafe des (Höllen)feuers." });
                        surah3.VerseList.Add(new Verse { Number = 192, TextBosnian = "Gospodaru naš, onoga koga Ti budeš u Vatru bacio, već si ga ponizio, a zulumćarima neće biti pomagača!", TextEnglish = "Our Lord, indeed whoever You admit to the Fire - You have disgraced him, and for the wrongdoers there are no helpers.", TextGerman = "Unser Herr, gewiß, wen Du ins (Höllen)feuer eingehen läßt, den hast Du (damit) in Schande gestürzt. Und die Ungerechten werden keine Helfer haben." });
                        surah3.VerseList.Add(new Verse { Number = 193, TextBosnian = "Gospodaru naš, mi smo čuli glasnika koji poziva vjeri: 'da vjerujete u Gospodara vašega!', pa smo povjerovali! Gospodaru naš, oprosti nam grijehe, i pokrij loše postupke naše, i usmrti nas s čestitima!", TextEnglish = "Our Lord, indeed we have heard a caller [i.e., Prophet Muḥammad (ﷺ)] calling to faith, [saying], 'Believe in your Lord,' and we have believed. Our Lord, so forgive us our sins and remove from us our misdeeds and cause us to die among the righteous.", TextGerman = "Unser Herr, gewiß, wir hörten einen Rufer, der zum Glauben aufrief: ,Glaubt an euren Herrn.’ Da glaubten wir. Unser Herr, vergib uns unsere Sünden, tilge unsere bösen Taten und berufe uns ab unter den Gütigen." });
                        surah3.VerseList.Add(new Verse { Number = 194, TextBosnian = "Gospodaru naš, daj nam ono što si nam obećao preko Svojih poslanika i ne ponizi nas na Kijametskom danu! Ti, doista, ne kršiš obećanje Svoje!'", TextEnglish = "Our Lord, and grant us what You promised us through Your messengers and do not disgrace us on the Day of Resurrection. Indeed, You do not fail in [Your] promise.'", TextGerman = "Unser Herr, und gib uns, was Du uns durch Deine Gesandten versprochen hast, und stürze uns nicht in Schande am Tag der Auferstehung. Gewiß, Du brichst nicht, was Du versprochen hast." });
                        surah3.VerseList.Add(new Verse { Number = 195, TextBosnian = "I Gospodar im se njihov odazva: 'Nijednom između vas koji dobro čini, bio muškarac ili žena, djela neću poništiti; vi ste jedni od drugih. Onima koji se isele i koji budu iz domova svojih prognani, i koji budu na putu Mome mučeni, i koji se budu borili i poginuli, sigurno ću loša djela pokriti i sigurno ću ih u džennetske bašče, kroz koje rijeke teku, uvesti. Nagrada je to Allahova, a u Allaha nagrada je najljepša.", TextEnglish = "And their Lord responded to them, 'Never will I allow to be lost the work of [any] worker among you, whether male or female; you are of one another. So those who emigrated or were evicted from their homes or were harmed in My cause or fought or were killed - I will surely remove from them their misdeeds, and I will surely admit them to gardens beneath which rivers flow as reward from Allāh, and Allāh has with Him the best reward.'", TextGerman = "Da erhörte sie ihr Herr: „Ich lasse kein Werk eines (Gutes) Tuenden von euch verlorengehen, sei es von Mann oder Frau; die einen von euch sind von den anderen. Denen also, die ausgewandert und aus ihren Wohnstätten vertrieben worden sind und denen auf Meinem Weg Leid zugefügt worden ist, und die gekämpft haben und getötet worden sind, werde Ich ganz gewiß ihre bösen Taten tilgen und sie ganz gewiß in Gärten eingehen lassen, durcheilt von Bächen, als Belohnung von Allah. Und Allah - bei Ihm ist die schöne Belohnung." });
                        surah3.VerseList.Add(new Verse { Number = 196, TextBosnian = "Neka te nikako ne obmanjuje to što oni koji ne vjeruju svuda po svijetu obijesno bogatstvom raspolažu.", TextEnglish = "Be not deceived by the [uninhibited] movement of the disbelievers throughout the land.", TextGerman = "Lasse dich ja nicht durch den Wandel derer in den Landstrichen täuschen, die ungläubig sind." });
                        surah3.VerseList.Add(new Verse { Number = 197, TextBosnian = "Kratko uživanje, a potom - Džehennem boravište je njihovo; a užasno je to prebivalište!", TextEnglish = "[It is but] a small enjoyment; then their [final] refuge is Hell, and wretched is the resting place.", TextGerman = "(Es ist nur) ein geringer Genuß, daraufhin wird die Hölle ihr Zufluchtsort sein. Und wie schlimm ist die Lagerstatt!" });
                        surah3.VerseList.Add(new Verse { Number = 198, TextBosnian = "Ali, onima koji se Gospodara svoga boje pripadaju džennetske bašče kroz koje rijeke teku; u njima će vječno ostati, Allah će ih ugostiti. A ono što je u Allaha, bolje je za čestite.", TextEnglish = "But those who feared their Lord will have gardens beneath which rivers flow, abiding eternally therein, as accommodation from Allāh. And that which is with Allāh is best for the righteous.", TextGerman = "Aber für diejenigen, die ihren Herrn fürchten, sind Gärten, durcheilt von Bächen, ewig darin zu bleiben; eine gastliche Aufnahme von Allah. Was bei Allah ist, ist besser für die Gütigen." });
                        surah3.VerseList.Add(new Verse { Number = 199, TextBosnian = "Ima i onih kojima je data Knjiga koji vjeruju u Allaha i u ono što je objavljeno vama, i u ono što je objavljeno njima, koji su ponizni Allahu i ne prodaju ajete Allahove za bagatelnu cijenu. Njima pripada nagrada Gospodara njihova, a Allah će, doista, brzo svidjeti račun.", TextEnglish = "And indeed, among the People of the Scripture are those who believe in Allāh and what was revealed to you and what was revealed to them, [being] humbly submissive to Allāh. They do not exchange the verses of Allāh for a small price. Those will have their reward with their Lord. Indeed, Allāh is swift in account.", TextGerman = "Und unter den Leuten der Schrift gibt es wahrlich manche, die an Allah glauben und (an) das, was zu euch (als Offenbarung) herabgesandt worden ist, und was zu ihnen (selbst) herabgesandt worden ist. Dabei sind sie demütig vor Allah und verkaufen nicht die Zeichen Allahs für einen geringen Preis. Jene haben ihren Lohn bei ihrem Herrn. Gewiß, Allah ist schnell im Abrechnen." });
                        surah3.VerseList.Add(new Verse { Number = 200, TextBosnian = "O vi koji vjerujete, budite strpljivi i izdržljivi, postojano bdijte i Allaha se bojte, da biste uspjeli!", TextEnglish = "O you who have believed, persevere[152] and endure[153] and remain stationed[154] and fear Allāh that you may be successful.", TextGerman = "O die ihr glaubt, geduldet euch, haltet standhaft aus, seid kampfbereit und fürchtet Allah, auf daß es euch wohl ergehen möge!" });
                        result = surah3;
                        break;
                    #endregion

                    // 
                    #region 4 An-nisa
                    case 4:
                        Surah surah4 = new Surah(); surah4.Name = Constants.Surah4Name; surah4.Number = 4; surah4.VerseList = new List<Verse>();
                        surah4.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah4.VerseList.Add(new Verse { Number = 1, TextBosnian = "O ljudi, bojte se Gospodara svoga, Koji vas je stvorio od jedne osobe, od koje je njen par stvorio, a od njih dvoje mnoge muškarce i žene rasijao. Bojte se Allaha, čijim imenom jedni druge molite, i čuvajte rodbinske veze. Allah vas, doista, nadzire.", TextEnglish = "O mankind, fear your Lord, who created you from one soul and created from it its mate and dispersed from both of them many men and women. And fear Allāh, through whom[156] you ask one another,[157] and the wombs.[158] Indeed Allāh is ever,[159] over you, an Observer.[160]", TextGerman = "O ihr Menschen, fürchtet euren Herrn, Der euch aus einem einzigen Wesen schuf, und aus ihm schuf Er seine Gattin und ließ aus beiden viele Männer und Frauen sich ausbreiten. Und fürchtet Allah, in Dessen (Namen) ihr einander bittet, und die Verwandtschaftsbande. Gewiß, Allah ist Wächter über euch." });
                        surah4.VerseList.Add(new Verse { Number = 2, TextBosnian = "I siročadima imetke njihove dajte, ono što je loše i zabranjeno ne mijenjajte za ono što je lijepo i dopušteno, i imetke njihove s imecima vašim ne trošite. To je, doista, prestup veliki.", TextEnglish = "And give to the orphans their properties and do not substitute the defective [of your own] for the good [of theirs]. And do not consume their properties into your own. Indeed, that is ever a great sin.", TextGerman = "Und gebt den Waisen ihren Besitz und tauscht nicht Schlechtes mit Gutem aus und zehrt nicht ihren Besitz zu eurem Besitz hinzu. Das ist gewiß ein schweres Vergehen." });
                        surah4.VerseList.Add(new Verse { Number = 3, TextBosnian = "Ako se bojite da prema sirotama nećete biti pravedni, onda se ženite onim ženama koje su vam drage i dopuštene, sa po dvije, sa po tri ili sa po četiri. A ako strahujete da nećete pravedni biti, onda samo jednom, ili onim koje posjedujete. Tako ćete se najlakše nepravde sačuvati.", TextEnglish = "And if you fear that you will not deal justly with the orphan girls, then marry those that please you of [other] women, two or three or four. But if you fear that you will not be just, then [marry only] one or those your right hands possess [i.e., slaves]. That is more suitable that you may not incline [to injustice].", TextGerman = "Und wenn ihr befürchtet, nicht gerecht hinsichtlich der Waisen zu handeln, dann heiratet, was euch an Frauen gut scheint, zwei, drei oder vier. Wenn ihr aber befürchtet, nicht gerecht zu handeln, dann (nur) eine oder was eure rechte Hand besitzt. Das ist eher geeignet, daß ihr nicht ungerecht seid." });
                        surah4.VerseList.Add(new Verse { Number = 4, TextBosnian = "I draga srca ženama darove njihove dajte; pa ako vam one draga srca štogod od toga poklone, to s prijatnošću i ugodnošću trošite.", TextEnglish = "And give the women [upon marriage] their [bridal] gifts[161] graciously. But if they give up willingly to you anything of it, then take it in satisfaction and ease.[162]", TextGerman = "Und gebt den Frauen ihre Morgengabe als Geschenk. Wenn sie für euch aber freiwillig auf etwas davon verzichten, dann verzehrt es als wohlbekömmlich und zuträglich." });
                        surah4.VerseList.Add(new Verse { Number = 5, TextBosnian = "I maloumnicima ne dajite imetke koje vam je Allah za izdržavanje učinio, nego ih njima opskrbite i obucite. I lijepe riječi im govorite.", TextEnglish = "And do not give the weak-minded your property,[163] which Allāh has made a means of sustenance for you, but provide for them with it and clothe them and speak to them words of appropriate kindness.", TextGerman = "Und gebt nicht den Toren euren Besitz, den Allah euch zum Unterhalt bestimmt hat, sondern versorgt sie davon und kleidet sie und sagt zu ihnen geziemende Worte." });
                        surah4.VerseList.Add(new Verse { Number = 6, TextBosnian = "I iskušavajte siročad dok ne stasaju za brak, pa ako se uvjerite da su zreli, uručite im imetke njihove. I ne trošite ih rasipnički i u žurbi, prije nego odrastu. Ko je imućan neka se suzdrži, a ko je siromašan, neka troši onoliko koliko je neophodno. A kada im imetke uručujete, to učinite u prisustvu svjedoka; a dosta je što će se pred Allahom račun polagati.", TextEnglish = "And test the orphans [in their abilities] until they reach marriageable age. Then if you perceive in them sound judgement, release their property to them. And do not consume it excessively and quickly, [anticipating] that they will grow up. And whoever, [when acting as guardian], is self-sufficient should refrain [from taking a fee]; and whoever is poor - let him take according to what is acceptable. Then when you release their property to them, bring witnesses upon them. And sufficient is Allāh as Accountant.", TextGerman = "Und prüft die Waisen, bis daß sie das Heiratsalter erreicht haben. Und wenn ihr dann an ihnen Besonnenheit feststellt, so händigt ihnen ihren Besitz aus. Und verzehrt ihn nicht maßlos und ihrem Erwachsenwerden zuvorkommend. Wer reich ist, der soll sich enthalten; und wer arm ist, der soll in rechtlicher Weise (davon) zehren. Und wenn ihr ihnen dann ihren Besitz aushändigt, so nehmt Zeugen vor ihnen. Doch Allah genügt als Abrechner." });
                        surah4.VerseList.Add(new Verse { Number = 7, TextBosnian = "Muškarcima pripada dio onoga što ostave roditelji i rodbina, a i ženama pripada dio od onoga što ostave roditelji i rodbina. Bilo toga malo ili mnogo, dio je obavezan.", TextEnglish = "For men is a share of what the parents and close relatives leave, and for women is a share of what the parents and close relatives leave, be it little or much - an obligatory share.", TextGerman = "Den Männern steht ein Anteil von dem zu, was die Eltern und nächsten Verwandten hinterlassen, und den Frauen steht ein Anteil von dem zu, was die Eltern und nächsten Verwandten hinterlassen, sei es wenig oder viel - ein festgesetzter Anteil." });
                        surah4.VerseList.Add(new Verse { Number = 8, TextBosnian = "A kada diobi prisustvuju rođaci, siročad i siromasi, opskrbite ih dijelom toga, i lijepe riječi im recite.", TextEnglish = "And when [other] relatives and orphans and the needy are present at the [time of] division, then provide for them [something] out of it [i.e., the estate] and speak to them words of appropriate kindness.", TextGerman = "Und wenn bei der Verteilung Verwandte, Waisen und Arme zugegen sind, dann versorgt sie davon und sagt zu ihnen geziemende Worte." });
                        surah4.VerseList.Add(new Verse { Number = 9, TextBosnian = "I neka se plaše oni, koji bi strahovali za svoju nejaku djecu ako ih ostave iza sebe. Neka se Allaha boje i neka govore samo pravo.", TextEnglish = "And let those [executors and guardians] fear [injustice] as if they [themselves] had left weak offspring behind and feared for them. So let them fear Allāh and speak words of appropriate justice.", TextGerman = "Und diejenigen sollen sich fürchten, die, wenn sie schwache Nachkommenschaft hinterließen, um sie bangen würden; darum sollen sie sich vor Allah hüten und treffende Worte sagen." });
                        surah4.VerseList.Add(new Verse { Number = 10, TextBosnian = "Doista, oni koji nepravedno troše imetke siročadi, u svoje stomake stavljaju vatru i oni će u Ognju gorjeti.", TextEnglish = "Indeed, those who devour the property of orphans unjustly are only consuming into their bellies fire. And they will be burned in a Blaze [i.e., Hellfire].", TextGerman = "Diejenigen, die den Besitz der Waisen ungerechterweise verschlingen, verzehren in ihren Bäuchen nur Feuer; und sie werden der Feuerglut ausgesetzt sein." });
                        surah4.VerseList.Add(new Verse { Number = 11, TextBosnian = "Allah vam nalaže, u vezi s djecom vašom, da muškom pripadne koliko dvoma ženske djece. A ako bude više od dvoje ženske djece, njima pripadaju dvije trećine onoga što je ostavio, a ako je samo jedna, pripada joj polovina. A roditeljima, svakome posebno, pripada šestina od onoga što je umrli ostavio, ako ima djece. A ako ne bude imao djece, i naslijede ga roditelji, onda njegovoj majci pripada trećina. A ako ima braće ili sestara, onda njegovoj majci pripada šestina pošto se izvrši oporuka koju je ostavio, ili podmiri dug. Vi ne znate ko vam je bliži po koristi, roditelji vaši ili sinovi vaši. To je Allahova zapovijed, a Allah je, doista, Onaj Koji sve zna i mudar je.", TextEnglish = "Allāh instructs you concerning your children [i.e., their portions of inheritance]: for the male, what is equal to the share of two females. But if there are [only] daughters, two or more, for them is two thirds of one's estate.[164] And if there is only one, for her is half. And for one's parents, to each one of them is a sixth of his estate if he left children. But if he had no children and the parents [alone] inherit from him, then for his mother is one third. And if he had brothers [and/or sisters], for his mother is a sixth,[165] after any bequest he [may have] made or debt.[166] Your parents or your children - you know not which of them are nearest to you in benefit. [These shares are] an obligation [imposed] by Allāh. Indeed, Allāh is ever Knowing and Wise.", TextGerman = "Allah empfiehlt euch hinsichtlich eurer Kinder: Einem männlichen Geschlechts kommt ebensoviel zu wie der Anteil von zwei weiblichen Geschlechts. Wenn es aber (ausschließlich) Frauen sind, mehr als zwei, dann stehen ihnen zwei Drittel dessen zu, was er hinterläßt; wenn es (nur) eine ist, dann die Hälfte. Und den Eltern steht jedem ein Sechstel von dem zu, was er hinterläßt, wenn er Kinder hat. Wenn er jedoch keine Kinder hat und seine Eltern ihn beerben, dann steht seiner Mutter ein Drittel zu. Wenn er Brüder hat, dann steht seiner Mutter (in diesem Fall) ein Sechstel zu. (Das alles) nach (Abzug) eines (etwaigen) Vermächtnisses, das er festgesetzt hat, oder einer Schuld. Eure Väter und eure Söhne - ihr wißt nicht, wer von ihnen euch an Nutzen näher steht. (Das alles gilt für euch) als Verpflichtung von Allah. Gewiß, Allah ist Allwissend und Allweise." });
                        surah4.VerseList.Add(new Verse { Number = 12, TextBosnian = "Vama pripada polovina onoga što ostave supruge vaše, ako ne budu imale djece; a ako budu imale djece, vama pripada četvrtina onoga što one ostave, pošto se izvrši oporuka koju ostave, ili podmiri dug; njima pripada četvrtina onoga što vi ostavite, ukoliko ne budete imali djece, a ako budete imali djece, njima pripada osmina onoga što vi ostavite nakon što se izvrši oporuka koju ostavite, ili podmiri dug. A ako se muškarac ili žena, koji nemaju ni djece ni roditelja, budu nasljeđivali i budu imali brata ili sestru, onda će svako od njih dvoje dobiti šestinu; ako ih bude više, oni onda zajednički učestvuju u trećini, pošto se izvrši oporuka koja je ostavljena, ili podmiri dug, ne oštećujući time bilo koga. To je zapovijed Allahova, a Allah je Onaj Koji sve zna i Onaj Koji ne žuri s kaznom.", TextEnglish = "And for you is half of what your wives leave if they have no child. But if they have a child, for you is one fourth of what they leave, after any bequest they [may have] made or debt. And for them [i.e., the wives] is one fourth if you leave no child. But if you leave a child, then for them is an eighth of what you leave, after any bequest you [may have] made or debt. And if a man or woman leaves neither ascendants nor descendants but has a brother or a sister, then for each one of them is a sixth. But if they are more than two, they share a third,[167] after any bequest which was made or debt, as long as there is no detriment [caused].[168] [This is] an ordinance from Allāh, and Allāh is Knowing and Forbearing.", TextGerman = "Und euch steht die Hälfte vom dem zu, was eure Gattinnen hinterlassen, wenn sie keine Kinder haben. Wenn sie jedoch Kinder haben, dann steht euch ein Viertel von dem zu, was sie hinterlassen. (Das alles) nach (Abzug) eines (etwaigen) Vermächtnisses, das sie festgesetzt haben, oder einer Schuld. Und ihnen steht ein Viertel von dem zu, was ihr hinterlaßt, wenn ihr keine Kinder habt. Wenn ihr jedoch Kinder habt, dann steht ihnen ein Achtel von dem zu, was ihr hinterlaßt. (Das alles) nach (Abzug) eines (etwaigen) Vermächtnisses, das ihr festgesetzt habt, oder einer Schuld. Und wenn ein Mann oder eine Frau ohne Eltern oder Kinder beerbt wird und er (bzw. sie) einen (Halb)bruder oder eine (Halb)schwester (mütterlicherseits) hat, dann steht jedem von beiden ein Sechstel zu. Wenn es jedoch mehr als dies sind, dann sollen sie Teilhaber an einem Drittel sein. (Das alles) nach (Abzug) eines (etwaigen) Vermächtnisses, das festgesetzt worden ist, oder einer Schuld, ohne Schädigung. (Das alles ist euch) anbefohlen von Allah. Allah ist Allwissend und Nachsichtig." });
                        surah4.VerseList.Add(new Verse { Number = 13, TextBosnian = "To su Allahovi propisi, a onoga ko se pokorava Allahu i Poslaniku Njegovu On će uvesti u džennetske bašče kroz koje teku rijeke i u kojima će vječno ostati. To je uspjeh veliki.", TextEnglish = "These are the limits [set by] Allāh, and whoever obeys Allāh and His Messenger will be admitted by Him to gardens [in Paradise] under which rivers flow, abiding eternally therein; and that is the great attainment.", TextGerman = "Dies sind Allahs Grenzen. Wer nun Allah und Seinem Gesandten gehorcht, den wird Er in Gärten eingehen lassen, durcheilt von Bächen, ewig darin zu bleiben; und das ist ein großartiger Erfolg." });
                        surah4.VerseList.Add(new Verse { Number = 14, TextBosnian = "A onoga ko bude nepokoran Allahu i Poslaniku Njegovom, te preko granica Njegovih bude prelazio, On će uvesti u Vatru u kojoj će vječno ostati; njemu pripada patnja ponižavajuća.", TextEnglish = "And whoever disobeys Allāh and His Messenger and transgresses His limits - He will put him into the Fire to abide eternally therein, and he will have a humiliating punishment.", TextGerman = "Wer sich aber Allah und Seinem Gesandten widersetzt und Seine Grenzen überschreitet, den läßt Er in ein Feuer eingehen, ewig darin zu bleiben; und für ihn gibt es schmachvolle Strafe." });
                        surah4.VerseList.Add(new Verse { Number = 15, TextBosnian = "Za one supruge vaše koje preljubu počine, tražite da posvjedoče četverica između vas, pa ako posvjedoče protiv njih, držite ih u kućama sve dok ih smrt ne umori ili dok im Allah neki put ne nađe.", TextEnglish = "Those who commit immorality [i.e., unlawful sexual intercourse] of your women - bring against them four [witnesses] from among you. And if they testify,[169] confine them [i.e., the guilty women] to houses until death takes them or Allāh ordains for them [another] way.[170]", TextGerman = "Und diejenigen von euren Frauen, die das Abscheuliche begehen, - bringt vier Zeugen von euch gegen sie. Wenn sie (es) bezeugen, dann haltet sie im Haus fest, bis der Tod sie abberuft oder Allah ihnen einen (Aus)weg schafft." });
                        surah4.VerseList.Add(new Verse { Number = 16, TextBosnian = "A ono dvoje koji to učine, izgrdite, pa ako se pokaju i poprave, onda ih na miru ostavite. Allah je, doista, Onaj Koji stalno prima pokajanja i milostiv je.", TextEnglish = "And the two[171] who commit it [i.e., unlawful sexual intercourse] among you - punish [i.e., dishonor] them both. But if they repent and correct themselves, leave them alone. Indeed, Allāh is ever Accepting of Repentance and Merciful.", TextGerman = "Und die beiden von euch, die es begehen, - züchtigt sie. Wenn sie dann bereuen und sich bessern, so laßt von ihnen ab. Gewiß, Allah ist Reue-Annehmend und Barmherzig." });
                        surah4.VerseList.Add(new Verse { Number = 17, TextBosnian = "Allah prima samo pokajanje onih koji učine kakvo loše djelo iz neznanja, a potom se ubrzo pokaju. Njima će Allah oprostiti, a Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "The repentance accepted by Allāh is only for those who do wrong in ignorance [or carelessness] and then repent soon [after].[172] It is those to whom Allāh will turn in forgiveness, and Allāh is ever Knowing and Wise.", TextGerman = "Die Annahme der Reue obliegt Allah nur für diejenigen, die in Unwissenheit Böses tun und hierauf beizeiten bereuen. Deren Reue nimmt Allah an. Und Allah ist Allwissend und Allweise." });
                        surah4.VerseList.Add(new Verse { Number = 18, TextBosnian = "Nema pokajanja onima koji čine loša djela sve dok im se ne približi smrt, pa onda govore: 'Sad se, doista, kajem!', niti za one koji umru kao nevjernici. Njima smo bolnu patnju pripremili.", TextEnglish = "But repentance is not [accepted] of those who [continue to] do evil deeds up until, when death comes to one of them, he says, 'Indeed, I have repented now,' or of those who die while they are disbelievers. For them We have prepared a painful punishment.", TextGerman = "Nicht aber ist die Annahme der Reue für diejenigen, die böse Taten begehen, bis daß, wenn sich bei einem von ihnen der Tod einstellt, er sagt: „Jetzt bereue ich, und auch nicht für diejenigen, die als Ungläubige sterben. Für jene haben Wir schmerzhafte Strafe bereitet." });
                        surah4.VerseList.Add(new Verse { Number = 19, TextBosnian = "O vi koji vjerujete, ne dozvoljava vam se da žene preko volje njihove u naslijeđe uzimate i da im poteškoće pričinjavate s namjerom da nešto od onoga što ste im dali prisvojite, osim ako budu učinile očiti razvrat. Prema njima se lijepo ophodite. A ako prema njima ne osjetite želju, pa moguće je da ne volite nešto u čemu je Allah veliko dobro dao.", TextEnglish = "O you who have believed, it is not lawful for you to inherit women by compulsion.[173] And do not make difficulties for them in order to take [back] part of what you gave them[174] unless they commit a clear immorality [i.e., adultery]. And live with them in kindness. For if you dislike them - perhaps you dislike a thing and Allāh makes therein much good.", TextGerman = "O die ihr glaubt, es ist euch nicht erlaubt, Frauen wider (ihren) Willen zu erben. Und drangsaliert sie nicht, um (ihnen) einen Teil von dem, was ihr ihnen gegeben habt, zu nehmen, außer sie begehen etwas klar Abscheuliches. Und geht in rechtlicher Weise mit ihnen um. Wenn sie euch zuwider sind, so ist euch vielleicht etwas zuwider, während Allah viel Gutes in es hineinlegt." });
                        surah4.VerseList.Add(new Verse { Number = 20, TextBosnian = "Ako hoćete jednu suprugu zamijeniti drugom, i jednoj od njih dali ste blaga, ne uzimajte od toga ništa. Zar da to uzimate čineći potvoru i grijeh očiti?!", TextEnglish = "But if you want to replace one wife with another and you have given one of them a great amount [in gifts], do not take [back] from it anything. Would you take it in injustice and manifest sin?", TextGerman = "Und wenn ihr eine Gattin anstelle einer anderen eintauschen wollt und ihr der einen von ihnen einen Qintar gegeben habt, dann nehmt nichts davon (zurück). Wollt ihr es (etwa) durch Verleumdung und deutliche Sünde (zurück)nehmen?" });
                        surah4.VerseList.Add(new Verse { Number = 21, TextBosnian = "Kako biste mogli to i uzeti, kada ste jedno s drugim intimni bili i kad su one od vas čvrstu obavezu uzele!?", TextEnglish = "And how could you take it while you have gone in unto each other and they have taken from you a solemn covenant?", TextGerman = "Wie könnt ihr es (zurück)nehmen, wo ihr doch zueinander eingegangen seid und sie mit euch ein festes Abkommen getroffen haben?" });
                        surah4.VerseList.Add(new Verse { Number = 22, TextBosnian = "I ne ženite se onima koje su oženili očevi vaši nakon onoga što je bilo - to su bili razvrat, mržnja i ružan put.", TextEnglish = "And do not marry those [women] whom your fathers married, except what has already occurred.[175] Indeed, it was an immorality and hateful [to Allāh] and was evil as a way.", TextGerman = "Und heiratet nicht Frauen, die (vorher) eure Väter geheiratet haben, außer dem, was bereits geschehen ist. Gewiß, das ist eine Abscheulichkeit und etwas Hassenswertes und ein böser Weg." });
                        surah4.VerseList.Add(new Verse { Number = 23, TextBosnian = "Zabranjuju vam se majke vaše, i kćeri vaše, i sestre vaše, i sestre očeva vaših, i sestre majki vaših, i bratične vaše i sestrične vaše, i pomajke vaše, koje su vas dojile, i sestre vaše po mlijeku, i majke supruga vaših, i pastorke vaše koje se nalaze pod vašim skrbništvom od supruga vaših s kojim ste imali intimne odnose; ako s njima niste imali intimne odnose, onda vam nije grijeh; i supruge vaših rođenih sinova, i da dvije sestre sastavite, osim što je ranije bilo! Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "Prohibited to you [for marriage] are your mothers, your daughters, your sisters, your father's sisters, your mother's sisters, your brother's daughters, your sister's daughters, your [milk] mothers who nursed you, your sisters through nursing, your wives' mothers, and your step-daughters under your guardianship [born] of your wives unto whom you have gone in. But if you have not gone in unto them, there is no sin upon you. And [also prohibited are] the wives of your sons who are from your [own] loins, and that you take [in marriage] two sisters simultaneously, except for what has already occurred.[176] Indeed, Allāh is ever Forgiving and Merciful.", TextGerman = "Verboten (zu heiraten) sind euch eure Mütter, eure Töchter, eure Schwestern, eure Tanten väterlicherseits, eure Tanten mütterlicherseits, die Nichten, eure Nährmütter, die euch gestillt haben, eure Milchschwestem, die Mütter eurer Frauen, eure Stieftöchter, die sich im Schoß eurer Familie befinden von euren Frauen, zu denen ihr eingegangen seid, - wenn ihr jedoch nicht zu ihnen eingegangen seid, so ist es keine Sünde für euch (, deren Töchter zu heiraten) - und (verboten zu heiraten sind euch) die Ehefrauen eurer Söhne, die aus euren Lenden (hervorgegangen) sind, und (verboten ist es euch,) daß ihr zwei Schwestern zusammen (zur Frau) nehmt, außer dem, was bereits geschehen ist. Gewiß, Allah ist Allvergebend und Barmherzig." });
                        surah4.VerseList.Add(new Verse { Number = 24, TextBosnian = "I zabranjuju vam se udate žene, osim onih koje su u vlasništvu vašem. To vam je propis Allahov! A ostalo vam je dozvoljeno da želite, svojom imovinom da se njima oženite, a ne da blud činite! A suprugama vašim s kojima imate bračne odnose dajte vjenčane darove kao što je propisano. I nije vam grijeh ako se, poslije određenog vjenčanog dara, s njima nagodite. Allah je, doista, Onaj Koji sve zna i mudar je.", TextEnglish = "And [also prohibited to you are all] married women except those your right hands possess.[177] [This is] the decree of Allāh upon you. And lawful to you are [all others] beyond these, [provided] that you seek them [in marriage] with [gifts from] your property, desiring chastity, not unlawful sexual intercourse. So for whatever you enjoy [of marriage] from them, give them their due compensation[178] as an obligation. And there is no blame upon you for what you mutually agree to beyond the obligation. Indeed, Allāh is ever Knowing and Wise.", TextGerman = "Und (verboten sind euch) von den Frauen die verheirateten, außer denjenigen, die eure rechte Hand besitzt. (Dies gilt) als Allahs Vorschrift für euch. Erlaubt ist euch, was darüber hinausgeht, (nämlich) daß ihr mit eurem Besitz (Frauen) begehrt zur Ehe und nicht zur Hurerei (Frauen). Welche von ihnen ihr dann genossen habt, denen gebt ihren Lohn als Pflichtteil. Es liegt aber keine Sünde für euch darin, daß ihr, nachdem der Pflichtteil (festgelegt) ist, (darüberhinausgehend) euch miteinander einigt. Gewiß, Allah ist Allwissend und Allweise." });
                        surah4.VerseList.Add(new Verse { Number = 25, TextBosnian = "A onaj među vama koji nije dovoljno imućan da se oženi slobodnom vjernicom- eto mu one vjernice koje su u vašem vlasništvu- a Allah najbolje zna vjerovanje vaše - jedni ste od drugih. I ženite se njima uz odobrenje vlasnika njihovih, i dajte im vjenčane darove kako je lijepo i uobičajeno, ako su čedne, a nisu preljubnice, niti tajne ljubavnice. A ako one budu udate, pa počine razvrat, neka budu kažnjene polovinom kazne propisane za slobodne žene. To je za onoga od vas koji se boji razvrata, a bolje vam je da se uzdržite, a Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And whoever among you cannot [find] the means to marry free, believing women, then [he may marry] from those whom your right hands possess of believing slave girls. And Allāh is most knowing about your faith. You [believers] are of one another. So marry them with the permission of their people and give them their due compensation [i.e., mahr] according to what is acceptable. [They should be] chaste, neither [of] those who commit unlawful intercourse randomly nor those who take [secret] lovers. But once they are sheltered in marriage, if they should commit adultery, then for them is half the punishment for free [unmarried] women. This [allowance] is for him among you who fears affliction [i.e., sin], but to be patient is better for you. And Allāh is Forgiving and Merciful.", TextGerman = "Und wer von euch nicht so bemittelt ist, daß er ehrbare, gläubige Frauen zu heiraten vermag, der (soll) von den gläubigen Mädchen (heiraten), die eure rechte Hand besitzt. Und Allah weiß sehr wohl über euren Glauben Bescheid; die einen von euch sind von den anderen. So heiratet sie mit der Erlaubnis ihrer Angehörigen und gebt ihnen ihren Lohn in rechtlicher Weise, wenn sie ehrbar sind, nicht solche, die Hurerei treiben und sich Liebhaber halten! Und wenn sie (durch Heirat) ehrbare Frauen geworden sind und dann eine Abscheulichkeit begehen, soll ihnen (nur) die Hälfte der Strafe zukommen, die ehrbaren Frauen zukommt. Dies gilt für denjenigen von euch, der (in) Bedrängnis (zu kommen) fürchtet. Doch daß ihr Geduld übt, ist besser für euch. Allah ist Allvergebend und Barmherzig." });
                        surah4.VerseList.Add(new Verse { Number = 26, TextBosnian = "Allah vam želi objasniti i uputiti vas putevima kojima su išli oni prije vas i oprostiti vam. Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "Allāh wants to make clear to you [the lawful from the unlawful] and guide you to the [good] practices of those before you and to accept your repentance. And Allāh is Knowing and Wise.", TextGerman = "Allah will euch Klarheit geben und euch rechtleiten nach den Gesetzmäßigkeiten derer, die vor euch waren, und eure Reue annehmen. Allah ist Allwissend und Allweise." });
                        surah4.VerseList.Add(new Verse { Number = 27, TextBosnian = "Allah vam želi oprostiti, a oni koji se za strastima svojim povode žele da daleko skrenete.", TextEnglish = "Allāh wants to accept your repentance, but those who follow [their] passions want you to digress [into] a great deviation.", TextGerman = "Und Allah will eure Reue annehmen; diejenigen aber, die den Begierden folgen, wollen, daß ihr (vom rechten Weg) völlig abweicht." });
                        surah4.VerseList.Add(new Verse { Number = 28, TextBosnian = "Allah vam želi olakšati, a čovjek je stvoren slab.", TextEnglish = "And Allāh wants to lighten for you [your difficulties]; and mankind was created weak.", TextGerman = "Allah will es euch leicht machen, denn der Mensch ist (ja) schwach erschaffen." });
                        surah4.VerseList.Add(new Verse { Number = 29, TextBosnian = "O vi koji vjerujete, jedni od drugih na nedozvoljen način imanja ne prisvajajte, osim trgovinom uz obostrani pristanak, i sami sebe ne ubijajte. Allah je, doista, prema vama milostiv.", TextEnglish = "O you who have believed, do not consume one another's wealth unjustly[179] but only [in lawful] business by mutual consent. And do not kill yourselves [or one another]. Indeed, Allāh is to you ever Merciful.", TextGerman = "O die ihr glaubt, zehrt nicht euren Besitz untereinander auf nichtige Weise auf, es sei denn, daß es sich um einen Handel in gegenseitigem Einvernehmen handelt. Und tötet euch nicht selbst (gegenseitig). Allah ist gewiß Barmherzig gegen euch." });
                        surah4.VerseList.Add(new Verse { Number = 30, TextBosnian = "Onoga koji to nepravično i nasilno učini, Mi ćemo u Vatri pržiti, a to je Allahu lahko.", TextEnglish = "And whoever does that in aggression and injustice - then We will drive him into a Fire. And that, for Allāh, is [always] easy.", TextGerman = "Und wer dies in feindseligem Vorgehen und ungerechterweise tut, den werden Wir einem Feuer aussetzen. Und dies ist Allah ein leichtes." });
                        surah4.VerseList.Add(new Verse { Number = 31, TextBosnian = "Ako se budete klonili velikih grijeha koji su vam zabranjeni, Mi ćemo pokriti vaša loša djela i u divno mjesto vas uvesti.", TextEnglish = "If you avoid the major sins which you are forbidden, We will remove from you your lesser sins and admit you to a noble entrance [into Paradise].", TextGerman = "Wenn ihr die schwerwiegenden (Dinge) meidet, die euch verboten sind, tilgen Wir euch eure bösen Taten und gewähren euch auf eine ehrenvolle Weise Eingang (in den Paradiesgarten)." });
                        surah4.VerseList.Add(new Verse { Number = 32, TextBosnian = "I ne poželite ono čime je Allah neke od vas odlikovao nad drugima: muškarcima pripada udio za ono što zarade, a ženama udio za ono što one zarade. I molite Allaha za obilje Njegovo. Allah, doista, sve dobro zna.", TextEnglish = "And do not wish for that by which Allāh has made some of you exceed others. For men is a share of what they have earned, and for women is a share of[180] what they have earned. And ask Allāh of His bounty. Indeed Allāh is ever, of all things, Knowing.", TextGerman = "Und wünscht euch nicht das, womit Allah die einen von euch vor den anderen bevorzugt hat. Den Männern kommt ein Anteil von dem zu, was sie verdient haben, und den Frauen kommt ein Anteil von dem zu, was sie verdient haben. Und bittet Allah (um etwas) von Seiner Huld. Allah weiß über alles Bescheid." });
                        surah4.VerseList.Add(new Verse { Number = 33, TextBosnian = "Za sve smo odredili nasljednike u onome što ostave roditelji i rođaci, a onima s kojima ste vezani ugovorom dajte njihov dio! Allah je, doista, svemu svjedok.", TextEnglish = "And for all, We have made heirs to what is left by parents and relatives. And to those whom your oaths have bound [to you] - give them their share.[181] Indeed Allāh is ever, over all things, a Witness.", TextGerman = "Einem jeden haben Wir Erbberechtigte bestimmt für das, was die Eltern und nächsten Verwandten hinterlassen. Denjenigen, mit denen eure rechte Hand eine Abmachung getroffen hat, gebt ihnen ihren Anteil. Allah ist über alles Zeuge." });
                        surah4.VerseList.Add(new Verse { Number = 34, TextBosnian = "Muškarci su nadređeni i staraju se o ženama zato što je Allah odlikovao jedne nad drugima i zato što oni troše imetke svoje. Zbog toga su čestite žene poslušne i za vrijeme muževljeva odsustva čuvaju ono što je Allah odredio da se čuva. One čije neposlušnosti se pribojavate vi posavjetujte, a onda napustite postelje njihove, pa ih i udarite. Ako vam postanu poslušne, protiv njih ništa ne činite. Allah je, doista, uzvišen i velik.", TextEnglish = "Men are in charge of women[182] by [right of] what Allāh has given one over the other and what they spend [for maintenance] from their wealth. So righteous women are devoutly obedient, guarding in [the husband's] absence what Allāh would have them guard.[183] But those [wives] from whom you fear arrogance[184] - [first] advise them; [then if they persist], forsake them in bed; and [finally], strike them [lightly].[185] But if they obey you [once more], seek no means against them. Indeed, Allāh is ever Exalted and Grand.", TextGerman = "Die Männer stehen in Verantwortung für die Frauen wegen dessen, womit Allah die einen von ihnen vor den anderen ausgezeichnet hat und weil sie von ihrem Besitz (für sie) ausgeben. Darum sind die rechtschaffenen Frauen (Allah) demütig ergeben und hüten das zu Verbergende, weil Allah (es) hütet. Und diejenigen, deren Widersetzlichkeit ihr befürchtet, - ermahnt sie, meidet sie im Ehebett und schlagt sie. Wenn sie euch aber gehorchen, dann sucht kein Mittel gegen sie. Allah ist Erhaben und Groß." });
                        surah4.VerseList.Add(new Verse { Number = 35, TextBosnian = "A ako se bojite razdora između njih dvoje, onda pošaljite jednog suca iz njegove, a jednog iz njene porodice. Ako oni žele izmirenje, Allah će ih pomiriti; Allah je, zaista, sveznajući i Onaj Koji sve, i u potpunosti, zna.", TextEnglish = "And if you fear dissension between the two, send an arbitrator from his people and an arbitrator from her people. If they both desire reconciliation, Allāh will cause it between them. Indeed, Allāh is ever Knowing and Aware.", TextGerman = "Und wenn ihr Widerstreit zwischen den beiden befürchtet, dann setzt einen Schiedsrichter aus seiner Familie und einen Schiedsrichter aus ihrer Familie ein. Wenn sie (beide) eine Aussöhnung wollen, wird Allah sie (beide) in Einklang bringen. Gewiß, Allah ist Allwissend und Allkundig." });
                        surah4.VerseList.Add(new Verse { Number = 36, TextBosnian = "I Allahu ibadet činite i ništa Mu ne pridružujte, a roditeljima činite dobro, i rođacima, i siročadima, i siromasima, i komšiji bližnjem, i komšiji daljnjem, i drugu pored sebe, i putniku, i onome koji je u vašem posjedu. Allah, doista, ne voli one koji se ohole i hvališu.", TextEnglish = "Worship Allāh and associate nothing with Him, and to parents do good, and to relatives, orphans, the needy, the near neighbor, the neighbor farther away, the companion at your side,[186] the traveler, and those whom your right hands possess. Indeed, Allāh does not like those who are self-deluding and boastful,", TextGerman = "Und dient Allah und gesellt Ihm nichts bei. Und zu den Eltern sollt ihr gütig sein und zu den Verwandten, den Waisen, den Armen, dem verwandten Nachbarn, dem fremden Nachbarn, dem Gefährten zur Seite, dem Sohn des Weges und denen, die eure rechte Hand besitzt. Allah liebt nicht, wer eingebildet und prahlerisch ist," });
                        surah4.VerseList.Add(new Verse { Number = 37, TextBosnian = "To su oni koji škrtare i zapovijedaju drugima da budu škrti, i koji kriju ono što im je Allah iz obilja Svoga darovao, a Mi smo nevjernicima pripremili patnju ponižavajuću.", TextEnglish = "Who are stingy and enjoin upon [other] people stinginess and conceal what Allāh has given them of His bounty - and We have prepared for the disbelievers a humiliating punishment -", TextGerman = "diejenigen, die geizen und den Menschen befehlen, geizig zu sein, und verbergen, was Allah ihnen von Seiner Huld gewährt hat, - für die Ungläubigen haben Wir schmachvolle Strafe bereitet -" });
                        surah4.VerseList.Add(new Verse { Number = 38, TextBosnian = "I oni koji troše imetke svoje pretvarajući se pred ljudima, a u Allaha i ahiret ne vjeruju. A kome je šejtan drug, to mu je zao drug.", TextEnglish = "And [also] those who spend of their wealth to be seen by the people and believe not in Allāh nor in the Last Day. And he to whom Satan is a companion - then evil is he as a companion.", TextGerman = "und diejenigen, die ihren Besitz ausgeben, um von den Menschen gesehen zu werden, und nicht an Allah und (auch) nicht an den Jüngsten Tag glauben; wer den Satan zum Gesellen hat, der hat da einen bösen Gesellen." });
                        surah4.VerseList.Add(new Verse { Number = 39, TextBosnian = "A šta bi im bilo da u Allaha i Posljednji dan vjeruju i da od onoga što im je Allah dao udjeljuju?! A Allah o njima sve zna.", TextEnglish = "And what [harm would come] upon them if they believed in Allāh and the Last Day and spent out of what Allāh provided for them? And Allāh is ever, about them, Knowing.", TextGerman = "Was würde es ihnen denn ausmachen, wenn sie an Allah und den Jüngsten Tag glaubten und von dem, womit Allah sie versorgt hat, ausgäben? Aber Allah weiß über sie wohl Bescheid." });
                        surah4.VerseList.Add(new Verse { Number = 40, TextBosnian = "Zaista Allah ni trunku nepravde ne čini, pa ako to bude dobro djelo, On će ga umnogostručiti i od Sebe nagradu veliku dati.", TextEnglish = "Indeed, Allāh does not do injustice, [even] as much as an atom's weight; while if there is a good deed, He multiplies it and gives from Himself a great reward.", TextGerman = "Allah tut gewiß nicht im Gewicht eines Stäubchens Unrecht. Und wenn es eine gute Tat ist, vervielfacht Er sie und gibt von Sich aus großartigen Lohn." });
                        surah4.VerseList.Add(new Verse { Number = 41, TextBosnian = "A šta će tek biti kada iz svakoga naroda dovedemo svjedoka, a i tebe dovedemo da o ovima svjedočiš?!", TextEnglish = "So how [will it be] when We bring from every nation a witness and We bring you, [O Muḥammad], against these [people] as a witness?", TextGerman = "Aber wie (wird es sein), wenn Wir von jeder Gemeinschaft einen Zeugen bringen und dich über diese da als Zeugen bringen?" });
                        surah4.VerseList.Add(new Verse { Number = 42, TextBosnian = "Taj dan će poželjeti oni koji nisu vjerovali i koji su se protiv Poslanika dizali da zemlja nad njima bude poravnana, a od Allaha nijednu riječ neće moći sakriti.", TextEnglish = "That Day, those who disbelieved and disobeyed the Messenger will wish they could be covered by the earth. And they will not conceal from Allāh a [single] statement.", TextGerman = "An jenem Tag hätten es diejenigen gern, die ungläubig waren und sich dem Gesandten widersetzten, wenn sie gleich der Erde wären. Und sie werden vor Allah keine Aussage verheimlichen." });
                        surah4.VerseList.Add(new Verse { Number = 43, TextBosnian = "O vi koji vjerujete, ne pristupajte namazu pijani, dok ne budete znali šta govorite, niti kada ste džunubi, osim kao prolaznici, sve dok se ne okupate. A ako ste bolesni, ili na putu, ili ako je neko od vas došao iz nužnika, ili ako ste imali odnos sa suprugama, a ne nađete vode, onda dlanovima čistu zemlju dotaknite i lica svoja i ruke svoje potarite. Allah je, doista, Onaj Koji stalno grijehe poništava i oprašta.", TextEnglish = "O you who have believed, do not approach prayer while you are intoxicated until you know what you are saying[187] or in a state of janābah,[188] except those passing through [a place of prayer], until you have washed [your whole body]. And if you are ill or on a journey or one of you comes from the place of relieving himself or you have contacted women [i.e., had sexual intercourse] and find no water, then seek clean earth and wipe over your faces and your hands [with it]. Indeed, Allāh is ever Pardoning[189] and Forgiving.", TextGerman = "O die ihr glaubt, nähert euch nicht dem Gebet, während ihr trunken seid, bis ihr wißt, was ihr sagt, noch im Zustand der Unreinheit - es sei denn, ihr geht bloß vorbei -, bis ihr den ganzen (Körper) gewaschen habt. Und wenn ihr krank seid oder auf einer Reise oder jemand von euch vom Abort kommt oder ihr Frauen berührt habt und dann kein Wasser findet, so wendet euch dem guten Erdboden zu und streicht euch über das Gesicht und die Hände. Allah ist Allverzeihend und Allvergebend." });
                        surah4.VerseList.Add(new Verse { Number = 44, TextBosnian = "Zar ne vidiš kako oni kojima je dat udio u Knjizi kupuju zabludu i žele da vi s Pravog puta skrenete?!", TextEnglish = "Have you not seen those who were given a portion of the Scripture, purchasing error [in exchange for it] and wishing you would lose the way?", TextGerman = "Siehst du nicht jene, denen ein Teil der Schrift gegeben wurde? Sie erkaufen das Irregehen und wollen, daß (auch) ihr vom Weg abirrt." });
                        surah4.VerseList.Add(new Verse { Number = 45, TextBosnian = "Allah najbolje poznaje neprijatelje vaše; Allah je dovoljan zaštitnik i Allah je dovoljan pomagač.", TextEnglish = "And Allāh is most knowing of your enemies; and sufficient is Allāh as an ally, and sufficient is Allāh as a helper.", TextGerman = "Allah weiß sehr wohl über eure Feinde Bescheid, und Allah genügt als Schutzherr, und Allah genügt als Helfer." });
                        surah4.VerseList.Add(new Verse { Number = 46, TextBosnian = "Onima koji su judejstvo prihvatili, koji izvrću smisao riječi govoreći: 'Čujemo, ali se ne pokoravamo!' i 'Slušaj, dabogda ne čuo!' i 'Čuvaj nas!', prevrćući jezicima svojim i huleći vjeru, bolje bi bilo i ispravnije da kažu: 'Slušamo i pokoravamo se!' i 'Slušaj!' i 'Pogledaj na nas!' Ali, Allah je njih zbog nevjerovanja njihova prokleo i malo ko od njih vjeruje.", TextEnglish = "Among the Jews are those who distort words from their [proper] places [i.e., usages] and say, 'We hear and disobey' and 'Hear but be not heard' and 'Rāʿinā,'[190] twisting their tongues and defaming the religion. And if they had said [instead], 'We hear and obey' and 'Wait for us [to understand],' it would have been better for them and more suitable. But Allāh has cursed them for their disbelief, so they believe not, except for a few.[191]", TextGerman = "Unter denjenigen, die dem Judentum angehören, verdrehen manche den Sinn der Worte und sagen: „Wir hören, doch wir widersetzen uns und: Höre!, als ob du nicht hörtest ra’ina, wobei sie mit ihren Zungen verdrehen und die Religion schmähen. Wenn sie gesagt hätten: „Wir hören und gehorchen und: „Höre! und: unzurna, wäre es wahrlich besser und richtiger für sie. Aber Allah hat sie für ihren Unglauben verflucht. Darum glauben sie nur wenig." });
                        surah4.VerseList.Add(new Verse { Number = 47, TextBosnian = "O vi kojima je Knjiga data, povjerujte u ovo što objavljujemo potvrđujući ono što već imate, prije nego što crte lica izbrišemo i vratimo ih nazad ili ih prokunemo kao što smo prokleli one koji nisu subotu poštivali, a zapovijest Allahova biva izvršena.", TextEnglish = "O you who were given the Scripture, believe in what We have sent down [to Prophet Muḥammad (ﷺ)], confirming that which is with you, before We obliterate faces and turn them toward their backs or curse them as We cursed the sabbath-breakers.[192] And ever is the matter [i.e., decree] of Allāh accomplished.", TextGerman = "O ihr, denen die Schrift gegeben wurde, glaubt an das, was Wir offenbart haben, das zu bestätigen, was euch (bereits) vorliegt, bevor Wir Gesichter auslöschen und nach hinten versetzen oder sie verfluchen, wie Wir die Sabbatleute verfluchten. Allahs Anordnung wird (stets) ausgeführt." });
                        surah4.VerseList.Add(new Verse { Number = 48, TextBosnian = "Allah, doista, neće oprostiti da se Njemu išta u obožavanju pridružuje, a oprostit će sve mimo toga, kome On hoće! A onaj ko drugog Allahu u obožavanju pridružuje, taj čini potvoru i grijeh veliki.", TextEnglish = "Indeed, Allāh does not forgive association with Him, but He forgives what is less than that for whom He wills. And he who associates others with Allāh has certainly fabricated a tremendous sin.", TextGerman = "Allah vergibt gewiß nicht, daß man Ihm (etwas) beigesellt. Doch was außer diesem ist, vergibt Er, wem Er will. Wer Allah (etwas) beigesellt, der hat fürwahr eine gewaltige Sünde ersonnen." });
                        surah4.VerseList.Add(new Verse { Number = 49, TextBosnian = "Zar ne vidiš one koji sebe smatraju od grijeha čistim?! Međutim, Allah čisti od grijeha koga hoće i nikome se neće, ni koliko nit na hurminoj košpici, zulum učiniti.", TextEnglish = "Have you not seen those who claim themselves to be pure? Rather, Allāh purifies whom He wills, and injustice is not done to them, [even] as much as a thread [inside a date seed].", TextGerman = "Siehst du nicht jene, die sich selbst für lauter erklären? Nein! Vielmehr läutert Allah, wen Er will. Und ihnen wird nicht um ein Fädchen Unrecht zugefügt." });
                        surah4.VerseList.Add(new Verse { Number = 50, TextBosnian = "Pogledaj kako izmišljaju laž o Allahu! To je Allahu dovoljno kao jasan grijeh.", TextEnglish = "Look how they invent about Allāh untruth, and sufficient is that as a manifest sin.", TextGerman = "Schau, wie sie gegen Allah Lügen ersinnen! Das (allein schon) genügt als deutliche Sünde." });
                        surah4.VerseList.Add(new Verse { Number = 51, TextBosnian = "Zar ne vidiš one kojima je dat udio u Knjizi kako u magiju i taguta - šejtana vjeruju, a onima koji ne vjeruju govore: 'Ovi su na ispravnijem putu od onih koji vjeruju?'", TextEnglish = "Have you not seen those who were given a portion of the Scripture, who believe in jibt [superstition] and ṭāghūt [false objects of worship] and say about the disbelievers, 'These are better guided than the believers as to the way'?", TextGerman = "Siehst du nicht jene, denen ein Teil der Schrift gegeben wurde? Sie glauben an Zauberei und falsche Götter und sagen von denen, die ungläubig sind: Diese da sind eher auf dem rechten Weg geleitet als die Gläubigen." });
                        surah4.VerseList.Add(new Verse { Number = 52, TextBosnian = "To su oni koje je Allah prokleo, a ti nećeš naći da će pomagača imati onaj koga je Allah prokleo.", TextEnglish = "Those are the ones whom Allāh has cursed; and he whom Allāh curses - never will you find for him a helper.", TextGerman = "Das sind diejenigen, die Allah verflucht hat; und wen Allah verflucht, für den wirst du keinen Helfer finden." });
                        surah4.VerseList.Add(new Verse { Number = 53, TextBosnian = "Da možda oni nemaju kakav udio u vlasti?! A da imaju, oni ljudima ništa-ni koliko udubljenje na hurminoj košpici - ne bi dali.", TextEnglish = "Or have they a share of dominion? Then [if that were so], they would not give the people [even as much as] the speck on a date seed.", TextGerman = "Oder haben sie (etwa) einen Anteil an der Herrschaft? Dann würden sie den Menschen nicht ein Dattelkerngrübchen (ab)geben." });
                        surah4.VerseList.Add(new Verse { Number = 54, TextBosnian = "I zar ljudima zavide na onome što im je Allah dao iz obilja svoga?! A Mi smo potomcima Ibrahimovim Knjigu i mudrost dali, i dali smo im veliku vlast.", TextEnglish = "Or do they envy people for what Allāh has given them of His bounty? But We had already given the family of Abraham the Scripture and wisdom[193] and conferred upon them a great kingdom.", TextGerman = "Oder beneiden sie die Menschen um das, was Allah ihnen von Seiner Huld gegeben hat? Nun, Wir gaben der Sippe Ibrahims die Schrift und die Weisheit und gaben ihnen gewaltige Herrschaft." });
                        surah4.VerseList.Add(new Verse { Number = 55, TextBosnian = "Pa, bilo ih je koji su vjerovali, a bilo je i onih koji su od toga odvraćali. A Džehennem je dovoljan kao oganj užareni.", TextEnglish = "And some among them believed in it,[194] and some among them were averse to it. And sufficient is Hell as a blaze.", TextGerman = "Unter ihnen gab es manche, die daran glaubten, und manche, die davon abhielten. Und die Hölle genügt als Feuerglut." });
                        surah4.VerseList.Add(new Verse { Number = 56, TextBosnian = "Doista, one koji ne vjeruju u ajete i znakove Naše Mi ćemo u Vatru baciti, pa kada god im se kože ispeku, zamijenit ćemo ih drugim kožama, kako bi patnju osjetili. Allah je, doista, silan i mudar.", TextEnglish = "Indeed, those who disbelieve in Our verses - We will drive them into a fire. Every time their skins are roasted through, We will replace them with other skins so they may taste the punishment. Indeed, Allāh is ever Exalted in Might and Wise.", TextGerman = "Diejenigen, die Unsere Zeichen verleugnen, werden Wir gewiß einem Feuer aussetzen. Jedesmal, wenn ihre Haut verbrannt ist, tauschen Wir sie ihnen gegen eine andere Haut aus, damit sie die Strafe kosten. Allah ist Allmächtig und Allweise." });
                        surah4.VerseList.Add(new Verse { Number = 57, TextBosnian = "A one koji vjeruju i dobra djela čine Mi ćemo uvesti u džennetske bašče kroz koje rijeke teku. Tamo će zauvijek ostati i supruge očišćene u njima imati; i u debelu hladovinu ćemo ih uvesti.", TextEnglish = "But those who believe and do righteous deeds - We will admit them to gardens beneath which rivers flow, wherein they abide forever. For them therein are purified spouses, and We will admit them to deepening shade.", TextGerman = "Diejenigen aber, die glauben und rechtschaffene Werke tun, werden Wir in Gärten eingehen lassen, durcheilt von Bächen, ewig und auf immer darin zu bleiben. Darin haben sie vollkommen gereinigte Gattinnen; und Wir lassen sie in vollkommenen Schatten eingehen." });
                        surah4.VerseList.Add(new Verse { Number = 58, TextBosnian = "Allah vam, doista, naređuje da emanete date onima kojima pripadaju, a kada ljudima sudite - da pravedno sudite. Savjet Allahov je, uistinu, divan i Allah je, doista, Onaj Koji sve čuje i Onaj Koji sve vidi.", TextEnglish = "Indeed, Allāh commands you to render trusts to whom they are due and when you judge between people to judge with justice. Excellent is that which Allāh instructs you. Indeed, Allāh is ever Hearing and Seeing.", TextGerman = "Allah befiehlt euch, anvertraute Güter ihren Eigentümern (wieder) auszuhändigen und, wenn ihr zwischen den Menschen richtet, in Gerechtigkeit zu richten. Wie trefflich ist das, womit Allah euch ermahnt! Gewiß, Allah ist Allhörend und Allsehend." });
                        surah4.VerseList.Add(new Verse { Number = 59, TextBosnian = "O vi koji vjerujete, pokoravajte se Allahu i pokoravajte se Poslaniku i nadležnim između vas. A ako se u nečemu ne slažete, obratite se Allahu i Poslaniku, ako vjerujete u Allaha i Posljednji dan. To je bolje i posljedice su ljepše.", TextEnglish = "O you who have believed, obey Allāh and obey the Messenger and those in authority among you. And if you disagree over anything, refer it to Allāh and the Messenger, if you should believe in Allāh and the Last Day. That is the best [way] and best in result.", TextGerman = "O die ihr glaubt, gehorcht Allah und gehorcht dem Gesandten und den Befehlshabern unter euch! Wenn ihr miteinander über etwas streitet, dann bringt es vor Allah und den Gesandten, wenn ihr wirklich an Allah und den Jüngsten Tag glaubt. Das ist am besten und am ehesten ein guter Ausgang." });
                        surah4.VerseList.Add(new Verse { Number = 60, TextBosnian = "Zar ne vidiš one koji tvrde da vjeruju u ono što je objavljeno tebi i u ono što je objavljeno prije tebe kako žele da im se pred tagutom - lažnim sudijom sudi, iako im je naređeno da u njega ne vjeruju?! A šejtan samo želi da ih u veliku zabludu navede.", TextEnglish = "Have you not seen those who claim to have believed in what was revealed to you, [O Muḥammad], and what was revealed before you? They wish to refer legislation to ṭāghūt,[195] while they were commanded to reject it; and Satan wishes to lead them far astray.", TextGerman = "Siehst du nicht jene, die behaupten, an das zu glauben, was zu dir (als Offenbarung) herabgesandt worden ist, und was vor dir herabgesandt wurde, während sie sich in Entscheidungsfragen an falsche Götter wenden wollen, wo ihnen doch befohlen worden ist, es zu verleugnen? Aber der Satan will sie weit in die Irre führen." });
                        surah4.VerseList.Add(new Verse { Number = 61, TextBosnian = "I kada im se kaže: 'Pristupite ovome što je Allah objavio i pristupite Poslaniku', vidiš licemjere kako se od tebe sasvim okreću.", TextEnglish = "And when it is said to them, 'Come to what Allāh has revealed and to the Messenger,' you see the hypocrites turning away from you in aversion.", TextGerman = "Und wenn man zu ihnen sagt: „Kommt her zu dem, was Allah (als Offenbarung) herabgesandt hat, und zum Gesandten, siehst du die Heuchler sich nachdrücklich abwenden." });
                        surah4.VerseList.Add(new Verse { Number = 62, TextBosnian = "A šta će tek biti kada ih pogodi nevolja zbog djela ruku njihovih, pa ti dođu zaklinjući se Allahom: 'Mi smo samo htjeli da dobro i pravo učinimo.'", TextEnglish = "So how [will it be] when disaster strikes them because of what their hands have put forth and then they come to you swearing by Allāh, 'We intended nothing but good conduct and accommodation.'", TextGerman = "Aber wie (ist es), wenn sie ein Unglück trifft für das, was ihre Hände vorausgeschickt haben, und sie hierauf zu dir kommen und bei Allah schwören: „Wir wollten (es) ja nur gut machen und Einklang herstellen?" });
                        surah4.VerseList.Add(new Verse { Number = 63, TextBosnian = "To su oni za koje Allah zna šta je u srcima njihovim, pa okreni se od njih i savjetuj ih, i reci im ono što će ih dirnuti.", TextEnglish = "Those are the ones of whom Allāh knows what is in their hearts, so turn away from them[196] but admonish them and speak to them a far-reaching [i.e., effective] word.", TextGerman = "Das sind diejenigen, von denen Allah weiß, was in ihren Herzen ist. So wende dich von ihnen ab, ermahne sie und sag zu ihnen über sie selbst eindringliche Worte." });
                        surah4.VerseList.Add(new Verse { Number = 64, TextBosnian = "A mi nismo nijednog poslanika poslali, osim da bi mu se pokoravalo uz Allahovo odobrenje. A da ti oni dođu kada sami sebi zulum učine i zamole Allaha da im oprosti, pa da i Poslanik zamoli da im se oprosti, oni bi vidjeli da Allah, doista, prima pokajanje i da je milostiv.", TextEnglish = "And We did not send any messenger except to be obeyed by permission of Allāh. And if, when they wronged themselves, they had come to you, [O Muḥammad], and asked forgiveness of Allāh and the Messenger had asked forgiveness for them, they would have found Allāh Accepting of Repentance and Merciful.", TextGerman = "Und Wir haben keinen Gesandten gesandt, ohne daß ihm - mit Allahs Erlaubnis - gehorcht werde. Und wenn sie, wo sie sich selbst Unrecht zugefügt haben, zu dir kämen und dann Allah um Vergebung bäten, und der Gesandte für sie um Vergebung bäte, würden sie wahrlich Allah Reue-Annehmend und Barmherzig finden." });
                        surah4.VerseList.Add(new Verse { Number = 65, TextBosnian = "I, tako Mi Gospodara tvoga, oni neće vjerovati dok za sudiju u sporovima međusobnim tebe ne prihvate, a potom u dušama svojim tegobe ne osjete za ono što si odredio i sasvim se ne predaju!", TextEnglish = "But no, by your Lord, they will not [truly] believe until they make you, [O Muḥammad], judge concerning that over which they dispute among themselves and then find within themselves no discomfort from what you have judged and submit in [full, willing] submission.", TextGerman = "Aber nein, bei deinem Herrn! Sie glauben nicht eher, bis sie dich über das richten lassen, was zwischen ihnen umstritten ist, und hierauf in sich selbst keine Bedrängnis finden durch das, was du entschieden hast, und sich in voller Ergebung fügen." });
                        surah4.VerseList.Add(new Verse { Number = 66, TextBosnian = "I da smo Mi njima propisali 'da sami sebe poubijate!' ili 'da se iselite iz svoga zavičaja!', malo ko od njih bi to učinio! A kada bi onako kako im se savjetuje postupili, bilo bi im bolje i u vjeri čvršće.", TextEnglish = "And if We had decreed upon them, 'Kill yourselves' or 'Leave your homes,' they would not have done it, except for a few of them. But if they had done what they were instructed, it would have been better for them and a firmer position [for them in faith].", TextGerman = "Wenn Wir ihnen vorschreiben würden: „Tötet euch selbst oder zieht aus euren Wohnstätten aus, so täten sie es nicht, außer wenigen von ihnen. Wenn sie aber tun würden, wozu sie ermahnt werden, wäre es wahrlich besser für sie und eine größere Stärkung (im Glauben)." });
                        surah4.VerseList.Add(new Verse { Number = 67, TextBosnian = "I tada bismo im Mi, od Nas, veliku nagradu dali.", TextEnglish = "And then We would have given them from Us a great reward.", TextGerman = "Dann würden Wir ihnen wahrlich von Uns aus großartigen Lohn geben" });
                        surah4.VerseList.Add(new Verse { Number = 68, TextBosnian = "I na Pravi put bismo ih uputili.", TextEnglish = "And We would have guided them to a straight path.", TextGerman = "und würden sie wahrlich (auf) einen geraden Weg leiten." });
                        surah4.VerseList.Add(new Verse { Number = 69, TextBosnian = "Oni koji su pokorni Allahu i Poslaniku bit će u društvu onih koje je Allah blagodario: vjerovjesnika, onih koji srcem iskreno i u potpunosti vjeruju i šehida i čestitih ljudi, a divno li je to društvo!", TextEnglish = "And whoever obeys Allāh and the Messenger - those will be with the ones upon whom Allāh has bestowed favor of the prophets, the steadfast affirmers of truth, the martyrs and the righteous. And excellent are those as companions.", TextGerman = "Wer Allah und dem Gesandten gehorcht, die werden mit denjenigen von den Propheten, den Wahrheitsliebenden, den Märtyrern und den Rechtschaffenen zusammen sein, denen Allah Gunst erwiesen hat. Welch gute Gefährten jene sind!" });
                        surah4.VerseList.Add(new Verse { Number = 70, TextBosnian = "To obilje je od Allaha i dovoljno je da Allah sve to dobro zna.", TextEnglish = "That is the bounty from Allāh, and sufficient is Allāh as Knower.", TextGerman = "Das ist die Huld von Allah, und Allah genügt als Allwissender." });
                        surah4.VerseList.Add(new Verse { Number = 71, TextBosnian = "O vi koji vjerujete, budite oprezni i idite u borbu u grupama, ili odjednom svi.", TextEnglish = "O you who have believed, take your precaution and [either] go forth in companies or go forth all together.", TextGerman = "O die ihr glaubt, seid auf eurer Hut. Und rückt dann in Truppen aus oder rückt alle aus." });
                        surah4.VerseList.Add(new Verse { Number = 72, TextBosnian = "Među vama ima, doista, onih koji će oklijevati, pa će, ako vas pogodi nevolja, takav reći: 'Sam Allah me blagodario što s njima nisam bio.'", TextEnglish = "And indeed, there is among you he who lingers behind; and if disaster strikes you, he says, 'Allāh has favored me in that I was not present with them.'", TextGerman = "Unter euch gibt es sehr wohl manch einen, der ganz langsam tut. Wenn euch dann ein Unglück trifft, sagt er: „Allah hat mir fürwahr eine Gunst erwiesen, da ich nicht mit ihnen zugegen war." });
                        surah4.VerseList.Add(new Verse { Number = 73, TextBosnian = "A ako vas od Allaha dobro zadesi, on će, kao da između vas i njega nije bilo prijateljstva, sigurno reći: 'Kamo sreće da sam s njima bio, veliki plijen bih dobio!?'", TextEnglish = "But if bounty comes to you from Allāh, he will surely say, as if [i.e., showing that] there had never been between you and him any affection, 'Oh, I wish I had been with them so I could have attained a great attainment.'[197]", TextGerman = "Wenn euch aber Huld von Allah zuteil wird, sagt er ganz gewiß, als ob zwischen euch und ihm keine Freundschaft bestanden hätte: „O wäre ich doch mit ihnen zugegen gewesen! Dann hätte ich einen großartigen Erfolg erzielt." });
                        surah4.VerseList.Add(new Verse { Number = 74, TextBosnian = "I neka se zato na Allahovu putu vjernici bore protiv onih koji život na dunjaluku kupuju za ahiret, a onoga ko se na Allahovu putu bori, pa pogine ili pobijedi, Mi ćemo, sigurno, velikom nagradom nagraditi.", TextEnglish = "So let those fight in the cause of Allāh who sell the life of this world for the Hereafter. And he who fights in the cause of Allāh and is killed or achieves victory - We will bestow upon him a great reward.", TextGerman = "So sollen denn diejenigen auf Allahs Weg kämpfen, die das diesseitige Leben für das Jenseits verkaufen. Und wer auf Allahs Weg kämpft und dann getötet wird oder siegt, dem werden Wir großartigen Lohn geben." });
                        surah4.VerseList.Add(new Verse { Number = 75, TextBosnian = "Šta je s vama pa se ne borite na Allahovom putu za nemoćne; za muškarce, žene i djecu koji govore: 'Gospodaru naš, izbavi nas iz ovoga grada čiji su stanovnici nasilnici, odredi nam zaštitnika i daj nam pomagača!'", TextEnglish = "And what is [the matter] with you that you fight not in the cause of Allāh and [for] the oppressed among men, women, and children who say, 'Our Lord, take us out of this city of oppressive people and appoint for us from Yourself a protector and appoint for us from Yourself a helper'?", TextGerman = "Was ist mit euch, daß ihr nicht auf Allahs Weg, und (zwar) für die Unterdrückten unter den Männern, Frauen und Kindern kämpft, die sagen: Unser Herr, bringe uns aus dieser Stadt heraus, deren Bewohner ungerecht sind, und schaffe uns von Dir aus einen Schutzherrn, und schaffe uns von Dir aus einen Helfer." });
                        surah4.VerseList.Add(new Verse { Number = 76, TextBosnian = "Oni koji vjeruju bore se na Allahovom putu, a oni koji ne vjeruju bore se na putu taguta - šejtana. Zato se borite protiv štićenika šejtanovih, a šejtanovo lukavstvo, doista, je slabo.", TextEnglish = "Those who believe fight in the cause of Allāh, and those who disbelieve fight in the cause of ṭāghūt.[198] So fight against the allies of Satan. Indeed, the plot of Satan has ever been weak.", TextGerman = "Diejenigen, die glauben, kämpfen auf Allahs Weg, und diejenigen, die ungläubig sind, kämpfen auf dem Weg der falschen Götter. So kämpft gegen die Gefolgsleute des Satans! Gewiß, die List des Satans ist schwach." });
                        surah4.VerseList.Add(new Verse { Number = 77, TextBosnian = "Zar nisi vidio one kojima je rečeno: 'K sebi ruke svoje, već namaz obavljajte i zekat dajite.' A kada im bi propisana borba, odjednom se grupa njih poboja ljudi, kao što se Allaha boji, ili još više, i rekoše: 'Gospodaru naš, zašto si nam borbu propisao? Da si nam to do određenog vremena odgodio?' Reci: 'Uživanje na dunjaluku je kratko, a ahiret je bolji za bogobojazne.' A vama, ni koliko nit na hurminoj košpici, zulum neće biti učinjen.", TextEnglish = "Have you not seen those who were told, 'Restrain your hands [from fighting][199] and establish prayer and give zakāh'? But then when battle was ordained for them, at once a party of them feared men as they fear Allāh or with [even] greater fear. They said, 'Our Lord, why have You decreed upon us fighting? If only You had postponed [it for] us for a short time.' Say, 'The enjoyment of this world is little, and the Hereafter is better for he who fears Allāh. And injustice will not be done to you, [even] as much as a thread [inside a date seed].'", TextGerman = "Siehst du nicht jene, zu denen gesagt wurde: „Haltet eure Hände zurück und verrichtet das Gebet und entrichtet die Abgabe. Als ihnen dann aber vorgeschrieben wurde zu kämpfen, fürchtete auf einmal eine Gruppe von ihnen die Menschen wie (sie) Furcht vor Allah (haben) oder mit noch größerer Furcht, und sagten: „Unser Herr, warum hast Du uns vorgeschrieben zu kämpfen? O würdest Du uns doch auf eine kurze Frist zurückstellen! Sag: Der Genuß des Diesseits ist gering. Und das Jenseits ist besser für jemanden, der gottesfürchtig ist. Und euch wird nicht um ein Fädchen Unrecht zugefügt." });
                        surah4.VerseList.Add(new Verse { Number = 78, TextBosnian = "Gdje god bili, stići će vas smrt, pa makar bili i u kulama visokim! Ako ih zadesi kakvo dobro, oni kažu: 'To je od Allaha!', a ako ih zadesi kakvo zlo, oni govore: 'To je od tebe!' Reci: 'Sve je od Allaha!' Pa šta je s ovim ljudima?! Oni kao da ne razumiju šta im se govori.", TextEnglish = "Wherever you may be, death will overtake you, even if you should be within towers of lofty construction. But if good comes to them, they say, 'This is from Allāh'; and if evil befalls them, they say,[200] 'This is from you.' Say, 'All [things] are from Allāh.' So what is [the matter] with those people that they can hardly understand any statement?", TextGerman = "Wo immer ihr auch seid, wird euch der Tod erfassen, und wäret ihr in hochgebauten Türmen. Und wenn sie etwas Gutes trifft, sagen sie: „Das ist von Allah. Und wenn sie etwas Böses trifft, sagen sie: „Das ist von dir. Sag: Alles ist von Allah. Was ist mit diesem Volk, daß sie beinahe keine Aussage verstehen?" });
                        surah4.VerseList.Add(new Verse { Number = 79, TextBosnian = "Dobro koje te zadesi, to je od Allaha, a zlo koje te zadesi, to je od tebe. Mi smo te kao poslanika ljudima poslali, a Allah je dovoljan svjedok.", TextEnglish = "What comes to you of good is from Allāh, but what comes to you of evil, [O man], is from yourself.[201] And We have sent you, [O Muḥammad], to the people as a messenger, and sufficient is Allāh as Witness.[202]", TextGerman = "Was dich an Gutem trifft, ist von Allah, und was dich an Bösem trifft, ist von dir selbst. Und Wir haben dich als Gesandten für die Menschen gesandt. Und Allah genügt als Zeuge." });
                        surah4.VerseList.Add(new Verse { Number = 80, TextBosnian = "Ko se pokori Poslaniku, pokorio se i Allahu, a ko se okrene, pa Mi te nismo poslali da im čuvar budeš.", TextEnglish = "He who obeys the Messenger has obeyed Allāh; but those who turn away - We have not sent you over them as a guardian.", TextGerman = "Wer dem Gesandten gehorcht, der gehorcht Allah, und wer sich abkehrt, - so haben Wir dich nicht als Hüter über sie entsandt." });
                        surah4.VerseList.Add(new Verse { Number = 81, TextBosnian = "Oni govore: 'Pokoravamo se!', a kada od tebe odu, neki od njih noću smišljaju drugačije nego što ti govoriš. Allah zapisuje ono što oni noću smišljaju. Zato se okreni od njih i u Allaha se pouzdaj, a dovoljan je zaštitnik Allah!", TextEnglish = "And they say, '[We pledge] obedience.' But when they leave you, a group of them spend the night determining to do other than what you say. But Allāh records what they plan by night. So leave them alone and rely upon Allāh. And sufficient is Allāh as Disposer of affairs.", TextGerman = "Und sie sagen: „Gehorsam. Wenn sie aber von dir weggehen, heckt ein Teil von ihnen etwas anderes aus, als was du sagst. Aber Allah schreibt auf, was sie aushecken. So wende dich von ihnen ab und verlasse dich auf Allah. Allah genügt als Sachwalter." });
                        surah4.VerseList.Add(new Verse { Number = 82, TextBosnian = "A zašto oni ne promišljaju o Kur'anu? Jer, da je on od nekoga drugog, a ne od Allaha, oni bi u njemu našli mnoge protivrječnosti.", TextEnglish = "Then do they not reflect upon the Qur’ān?[203] If it had been from [any] other than Allāh, they would have found within it much contradiction.", TextGerman = "Denken sie denn nicht sorgfältig über den Qur’an nach? Wenn er von jemand anderem wäre als von Allah, würden sie in ihm wahrlich viel Widerspruch finden." });
                        surah4.VerseList.Add(new Verse { Number = 83, TextBosnian = "A kada im dođe nešto što se odnosi na sigurnost ili strah, oni to razglase. A da to prepuste Poslaniku ili pretpostavljenim svojima, to bi sigurno od njih saznali oni koji to ispituju. A da nije dobrote Allahove prema vama i milosti Njegove, vi biste, osim nekolicine, šejtana slijedili.", TextEnglish = "And when there comes to them something [i.e., information] about [public] security or fear, they spread it around. But if they had referred it back to the Messenger or to those of authority among them, then the ones who [can] draw correct conclusions from it would have known about it. And if not for the favor of Allāh upon you and His mercy, you would have followed Satan, except for a few.", TextGerman = "Und wenn ihnen eine Angelegenheit zu (Ohren) kommt, die Sicherheit oder Furcht betrifft, machen sie es bekannt. Wenn sie es jedoch vor den Gesandten und den Befehlshabern unter ihnen brächten, würden es wahrlich diejenigen unter ihnen wissen, die es herausfinden können. Und wenn nicht Allahs Huld und Erbarmen gewesen wären, wäret ihr fürwahr außer wenigen dem Satan gefolgt." });
                        surah4.VerseList.Add(new Verse { Number = 84, TextBosnian = "Zato se bori na Allahovom putu, ne obavezujući osim sebe, a vjernike podstiči. Allah će zaustaviti silu onih koji ne vjeruju, a Allah je od njih silniji i kazne Njegove su strožije.", TextEnglish = "So fight, [O Muḥammad], in the cause of Allāh; you are not held responsible except for yourself. And encourage the believers [to join you] that perhaps Allāh will restrain the [military] might of those who disbelieve. And Allāh is greater in might and stronger in [exemplary] punishment.[204]", TextGerman = "So kämpfe auf Allahs Weg - du wirst nur für dich selbst verantwortlich gemacht und sporne die Gläubigen an. Vielleicht wird Allah die Gewalt derjenigen, die ungläubig sind, zurückhalten. Allah ist stärker an Gewalt und an Bestrafung." });
                        surah4.VerseList.Add(new Verse { Number = 85, TextBosnian = "Ko se bude za dobro zalagao, i on će imati udio u tome, a ko se bude za zlo zalagao, i on će imati udio u tome. A Allah nad svim bdije.", TextEnglish = "Whoever intercedes for a good cause will have a share [i.e., reward] therefrom; and whoever intercedes for an evil cause will have a portion [i.e., burden] therefrom. And ever is Allāh, over all things, a Keeper.[205]", TextGerman = "Wer (in) eine(r) gute(n Sache) Fürsprache einlegt, für den wird es einen Anteil daran geben, und wer (in) eine(r) schlechte(n Sache) Fürsprache einlegt, für den wird es ein gleiches davon geben. Und Allah ist zu allem Fähig." });
                        surah4.VerseList.Add(new Verse { Number = 86, TextBosnian = "Kada pozdravljeni budete, na ljepši način uzvratite, ili isto otpozdravite, a Allah, doista, sve obračunava.", TextEnglish = "And when you are greeted with a greeting, greet [in return] with one better than it or [at least] return it [in a like manner]. Indeed Allāh is ever, over all things, an Accountant.", TextGerman = "Und wenn euch ein Gruß entboten wird, dann grüßt mit einem schöneren (zurück) oder erwidert ihn. Gewiß, Allah ist über alles ein Abrechner." });
                        surah4.VerseList.Add(new Verse { Number = 87, TextBosnian = "Allah, osim Koga drugoga boga nema, sigurno će vas sakupiti na Sudnjem danu, u što nema nimalo sumnje, a čiji je govor istinitiji od Allahovog?!", TextEnglish = "Allāh - there is no deity except Him. He will surely assemble you for [account on] the Day of Resurrection, about which there is no doubt. And who is more truthful than Allāh in statement.", TextGerman = "Allah - es gibt keinen Gott außer Ihm. Er wird euch ganz gewiß zum Tag der Auferstehung versammeln, an dem es keinen Zweifel gibt. Und wer ist wahrhaftiger als Allah in der Aussage?" });
                        surah4.VerseList.Add(new Verse { Number = 88, TextBosnian = "Zašto se podvajate kada su u pitanju licemjeri koje je Allah vratio u nevjerstvo zbog onoga što su sami zaslužili? Zar želite na Pravi put uputiti one kojima je Allah dao da su u zabludi? A kome Allah da da je u zabludi, ti mu nećeš Put naći!", TextEnglish = "What is [the matter] with you [that you are] two groups concerning the hypocrites,[206] while Allāh has made them fall back [into error and disbelief] for what they earned.[207] Do you wish to guide those whom Allāh has sent astray? And he whom Allāh sends astray - never will you find for him a way [of guidance].[208]", TextGerman = "Was ist mit euch, daß ihr hinsichtlich der Heuchler (in) zwei Scharen (gespalten) seid, wo doch Allah sie wegen dessen, was sie verdient haben, umgekehrt hat? Wollt ihr denn rechtleiten, wen Allah in die Irre gehen läßt? Wen aber Allah in die Irre gehen läßt, für den wirst du keinen Weg finden." });
                        surah4.VerseList.Add(new Verse { Number = 89, TextBosnian = "Oni bi voljeli da i vi ne vjerujete kao što oni ne vjeruju, pa da budete jednaki. Zato, između njih ne uzimajte prijatelje dok se na Allahovom Putu ne isele. A ako se okrenu, onda ih hvatajte i ubijajte gdje god ih nađete i između njih prijatelje niti pomagače ne uzimajte,", TextEnglish = "They wish you would disbelieve as they disbelieved so you would be alike. So do not take from among them allies until they emigrate for the cause of Allāh. But if they turn away [i.e., refuse], then seize them and kill them [for their betrVersel] wherever you find them and take not from among them any ally or helper,", TextGerman = "Sie möchten gern, daß ihr ungläubig werdet, wie sie ungläubig sind, so daß ihr (alle) gleich seiet. Nehmt euch daher von ihnen keine Vertrauten, bevor sie nicht auf Allahs Weg auswandern! Kehren sie sich jedoch ab, dann ergreift sie und tötet sie, wo immer ihr sie findet, und nehmt euch von ihnen weder Schutzherrn noch Helfer," });
                        surah4.VerseList.Add(new Verse { Number = 90, TextBosnian = "osim onih koji se sklone kod naroda s kojim vi imate ugovor, ili dođu kod vas, jer im je teško boriti se protiv vas ili svoga roda. A da Allah hoće, on bi ih okrenuo protiv vas, pa bi se oni borili protiv vas! Ako vas oni ostave na miru i ne bore se protiv vas, nego vam ponude mir, onda vam Allah ne daje pravo da ih napadate.", TextEnglish = "Except for those who take refuge with a people between yourselves and whom is a treaty or those who come to you, their hearts strained at [the prospect of] fighting you or fighting their own people. And if Allāh had willed, He could have given them power over you, and they would have fought you. So if they remove themselves from you and do not fight you and offer you peace, then Allāh has not made for you a cause [for fighting] against them.", TextGerman = "außer denjenigen, die sich einem Volk anschließen, zwischen dem und euch ein Abkommen besteht, oder die zu euch gekommen sind, weil ihre Brüste beklommen sind, gegen euch zu kämpfen oder gegen ihr (eigenes) Volk zu kämpfen. Und wenn Allah gewollt hätte, hätte Er ihnen wahrlich Gewalt über euch gegeben, und dann hätten sie gegen euch wahrlich gekämpft. Wenn sie sich jedoch von euch fernhalten und dann nicht gegen euch kämpfen, sondern Frieden anbieten, so hat euch Allah keine Veranlassung gegeben, gegen sie (vorzugehen)." });
                        surah4.VerseList.Add(new Verse { Number = 91, TextBosnian = "Vi ćete nalaziti druge koji žele biti sigurni od vas i naroda svog, pa kada god budu vraćeni na smutnju - širk, oni mu se još više predaju. Ako se oni ne okane vas, i ne ponude vam mir, i ako vas se ne klone, onda ih hvatajte i ubijajte gdje god stignete, a Mi smo vam za borbu protiv njih jasan dokaz dali.", TextEnglish = "You will find others who wish to obtain security from you and [to] obtain security from their people. Every time they are returned to [the influence of] disbelief, they fall back into it. So if they do not withdraw from you or offer you peace or restrain their hands, then seize them and kill them wherever you overtake them. And those - We have made for you against them a clear authorization.", TextGerman = "Ihr werdet andere finden, die vor euch Sicherheit und vor ihrem (eigenen) Volk Sicherheit wollen. Jedesmal, wenn sie wieder der Versuchung ausgesetzt sind, werden sie in ihr zu Fall gebracht. Wenn sie sich nicht von euch fernhalten und euch nicht Frieden anbieten und nicht ihre Hände zurückhalten, dann ergreift sie und tötet sie, wo immer ihr auf sie trefft. Über jene haben Wir euch deutliche Gewalt verliehen." });
                        surah4.VerseList.Add(new Verse { Number = 92, TextBosnian = "Vjernik ne može ubiti vjernika osim greškom, a ko ubije vjernika greškom, on mora osloboditi roba vjernika, i predati krvarinu porodici njegovoj, izuzev ako oni to oproste. Ako on pripada narodu koji vam je neprijatelj, a vjernik je, on mora osloboditi jednog roba vjernika! Ako, pak, pripada narodu s kojim imate sporazum, tada mora dati krvarinu porodici njegovoj i osloboditi roba vjernika! Ko to ne nađe, obavezan je postiti dva mjeseca uzastopno, kajući se Allahu. A Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "And never is it for a believer to kill a believer except by mistake. And whoever kills a believer by mistake - then the freeing of a believing slave and a compensation payment [diyah] presented to his [i.e., the deceased's] family [is required], unless they give [up their right as] charity. But if he [i.e., the deceased] was from a people at war with you and he was a believer - then [only] the freeing of a believing slave; and if he was from a people with whom you have a treaty - then a compensation payment presented to his family and the freeing of a believing slave. And whoever does not find [one or cannot afford to buy one] - then [instead], a fast for two months consecutively,[209] [seeking] acceptance of repentance from Allāh.[210] And Allāh is ever Knowing and Wise.", TextGerman = "Es steht keinem Gläubigen zu, einen (anderen) Gläubigen zu töten, es sei denn aus Versehen’. Und wer einen Gläubigen aus Versehen tötet, (der hat) einen gläubigen Sklaven (zu) befreien und ein Blutgeld an seine Angehörigen aus(zu)händigen, es sei denn, sie erlassen (es ihm) als Almosen. Wenn er (der Getötete) zu einem euch feindlichen Volk gehörte und gläubig war, dann (gilt es,) einen gläubigen Sklaven (zu) befreien. Und wenn er zu einem Volk gehörte, zwischen dem und euch ein Abkommen besteht, dann (gilt es,) ein Blutgeld an seine Angehörigen aus(zu)händigen und einen gläubigen Sklaven (zu) befreien. Wer aber keine (Möglichkeit) findet, (der hat) zwei aufeinanderfolgende Monate (zu) fasten - als eine Reueannahme von Allah. Und Allah ist Allwissend und Allweise." });
                        surah4.VerseList.Add(new Verse { Number = 93, TextBosnian = "A ko hotimično ubije vjernika, kazna mu je Džehennem u kojem će vječno ostati. Na njega se Allah rasrdi i proklete ga, a još mu je i patnju veliku pripremio.", TextEnglish = "But whoever kills a believer intentionally - his recompense is Hell, wherein he will abide eternally, and Allāh has become angry with him and has cursed him and has prepared for him a great punishment.", TextGerman = "Und wer einen Gläubigen vorsätzlich tötet, dessen Lohn ist die Hölle, ewig darin zu bleiben. Und Allah zürnt ihm und verflucht ihn und bereitet ihm gewaltige Strafe." });
                        surah4.VerseList.Add(new Verse { Number = 94, TextBosnian = "O vi koji vjerujete, kada u boj na Allahovom putu krenete, sve dobro provjerite i onome ko vam nazove selam nemojte reći: 'Ti nisi vjernik!', u želji za prolaznim dobrima dunjalučkog života, jer kod Allaha su mnoga dobra. Takvi ste ranije i vi bili, pa vam je Allah darovao milost Svoju. Zato, sve dobro provjerite, a Allah, doista, dobro zna ono što vi radite.", TextEnglish = "O you who have believed, when you go forth [to fight] in the cause of Allāh, investigate; and do not say to one who gives you [a greeting of] peace, 'You are not a believer,'[211] aspiring for the goods of worldly life; for with Allāh are many acquisitions. You [yourselves] were like that before; then Allāh conferred His favor [i.e., guidance] upon you, so investigate. Indeed Allāh is ever, of what you do, Aware.", TextGerman = "O die ihr glaubt, wenn ihr auf Allahs Weg umherreist, dann unterscheidet klar und sagt nicht zu einem, der euch Frieden anbietet: „Du bist nicht gläubig, wobei ihr nach den Glücksgütern des diesseitigen Lebens trachtet. Doch bei Allah ist Gutes in Fülle. So wart ihr zuvor. Aber dann hat Allah euch eine Wohltat erwiesen. Unterscheidet also klar. Gewiß, Allah ist dessen, was ihr tut, Kundig." });
                        surah4.VerseList.Add(new Verse { Number = 95, TextBosnian = "Vjernici koji se ne bore, osim onih koji su nesposobni, ne mogu se izjednačiti s onima koji se na Allahovu putu bore imecima svojim i životima svojim. One koji se bore imecima svojim i životima svojim Allah je odlikovao za stepen nad onima koji se ne bore, i svima Allah obećava lijepu nagradu. A Allah je borce, u odnosu na one koji se ne bore, odlikovao velikom nagradom;", TextEnglish = "Not equal are those believers remaining [at home] - other than the disabled - and the mujāhideen, [who strive and fight] in the cause of Allāh with their wealth and their lives. Allāh has preferred the mujāhideen through their wealth and their lives over those who remain [behind], by degrees. And to all [i.e., both] Allāh has promised the best [reward]. But Allāh has preferred the mujāhideen over those who remain [behind] with a great reward -", TextGerman = "Nicht gleich sind diejenigen unter den Gläubigen, die sitzen bleiben - außer denjenigen, die eine Schädigung haben -, den sich auf Allahs Weg mit ihrem Besitz und ihrer eigenen Person Abmühenden. Allah hat den sich mit ihrem Besitz und ihrer eigenen Person Abmühenden einen Vorzug gegeben vor denen, die sitzen bleiben. Aber allen hat Allah das Beste versprochen. Doch hat Allah die sich Abmühenden vor denen, die sitzen bleiben, mit großartigem Lohn bevorzugt," });
                        surah4.VerseList.Add(new Verse { Number = 96, TextBosnian = "položajima kod Njega, oprostom i milošću, a Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "Degrees [of high position] from Him and forgiveness and mercy. And Allāh is ever Forgiving and Merciful.", TextGerman = "mit Rangstufen von Ihm und Vergebung und Erbarmen. Allah ist Allvergebend und Barmherzig." });
                        surah4.VerseList.Add(new Verse { Number = 97, TextBosnian = "Doista će meleci, kada budu uzimali duše onima koji su prema sebi zulum učinili, pitati: 'Šta je bilo s vama?'', a oni će odgovoriti: 'Bili smo nemoćni na Zemlji.' 'Zar Allahova Zemlja nije prostrana i zar se niste mogli nekuda iseliti?'', reći će im oni. Zato će njihovo prebivalište biti Džehennem, a loše je to konačno odredište!", TextEnglish = "Indeed, those whom the angels take [in death] while wronging themselves[212] - [the angels] will say, 'In what [condition] were you?' They will say, 'We were oppressed in the land.' They [the angels] will say, 'Was not the earth of Allāh spacious [enough] for you to emigrate therein?' For those, their refuge is Hell - and evil it is as a destination.", TextGerman = "Diejenigen, die die Engel abberufen, während sie sich selbst Unrecht tun, (zu jenen) sagen sie: „Worin habt ihr euch befunden? Sie sagen: „Wir waren Unterdrückte im Lande. Sie (die Engel) sagen: „War Allahs Erde nicht weit, so daß ihr darauf hättet auswandern können? Jene aber, - ihr Zufluchtsort wird die Hölle sein, und (wie) böse ist der Ausgang!" });
                        surah4.VerseList.Add(new Verse { Number = 98, TextBosnian = "Izuzetak su nemoćni muškarci, i žene, i djeca, koji se snaći ne mogu i koji ne nađu puta.", TextEnglish = "Except for the oppressed among men, women, and children who cannot devise a plan nor are they directed to a way[213] -", TextGerman = "Ausgenommen die Unterdrückten unter den Männern, Frauen und Kindern, die keine Möglichkeit haben auszuwandern und auf dem Weg nicht rechtgeleitet sind." });
                        surah4.VerseList.Add(new Verse { Number = 99, TextBosnian = "Allah će njima oprostiti; Allah je Onaj Koji stalno poništava grijehe i oprašta.", TextEnglish = "For those it is expected that Allāh will pardon them, and Allāh is ever Pardoning[214] and Forgiving.", TextGerman = "Jenen wird Allah vielleicht verzeihen. Allah ist Allverzeihend und Allvergebend." });
                        surah4.VerseList.Add(new Verse { Number = 100, TextBosnian = "Ko se iseli na putu Allahovom, na Zemlji će naći mnogo mjesta i obilja! A onaj ko napusti svoj dom, iseljavajući se radi Allaha i Njegovoga Poslanika, pa ga stigne smrt, od Allaha će dobiti nagradu. Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And whoever emigrates for the cause of Allāh will find on the earth many [alternative] locations and abundance. And whoever leaves his home as an emigrant to Allāh and His Messenger and then death overtakes him - his reward has already become incumbent upon Allāh. And Allāh is ever Forgiving and Merciful.", TextGerman = "Wer auf Allahs Weg auswandert, wird auf der Erde viele Zufluchtsstätten und Wohlstand finden. Und wer sein Haus auswandernd zu Allah und Seinem Gesandten verläßt, und den hierauf der Tod erfaßt, so fällt es Allah zu, ihm seinen Lohn (zu geben). Allah ist Allvergebend und Barmherzig." });
                        surah4.VerseList.Add(new Verse { Number = 101, TextBosnian = "Kada idete po Zemlji, nije vam grijeh namaz skratiti ako se bojite da će vam smutnju nevjernici učiniti, jer nevjernici su vam, doista, otvoreni neprijatelji.", TextEnglish = "And when you travel throughout the land, there is no blame upon you for shortening the prayer,[215] [especially] if you fear that those who disbelieve may disrupt [or attack] you.[216] Indeed, the disbelievers are ever to you a clear enemy.", TextGerman = "Und wenn ihr im Land umherreist, so ist es keine Sünde für euch, das Gebet abzukürzen, wenn ihr befürchtet, diejenigen, die ungläubig sind, könnten euch überfallen. Die Ungläubigen sind euch ja ein deutlicher Feind." });
                        surah4.VerseList.Add(new Verse { Number = 102, TextBosnian = "Kada ti budeš među njima i budeš ih u namazu predvodio, neka jedna grupa s tobom klanja i neka svoje oružje uzmu; i dok budu na sedždi, neka drugi budu iza vas, a onda neka dođe druga grupa koja nije klanjala i neka s tobom klanjaju, ali neka drže oružje svoje i oprezni budu. Nevjernici jedva čekaju da vi zanemarite oružje svoje i opremu svoju, pa da na vas svi odjednom navale. A ako vam bude smetala kiša ili budete bolesni, nije vam grijeh oružje svoje odložiti, ali, oprezni budite.Allah je, doista, nevjernicima ponižavajuću patnju pripremio.", TextEnglish = "And when you [i.e., the commander of an army] are among them and lead them in prayer,[217] let a group of them stand [in prayer] with you and let them carry their arms. And when they have prostrated, let them be [in position] behind you and have the other group come forward which has not [yet] prayed and let them pray with you, taking precaution and carrying their arms. Those who disbelieve wish that you would neglect your weapons and your baggage so they could come down upon you in one [single] attack. But there is no blame upon you, if you are troubled by rain or are ill, for putting down your arms, but take precaution. Indeed, Allāh has prepared for the disbelievers a humiliating punishment.", TextGerman = "Und wenn du unter ihnen bist und nun für sie das Gebet anführst, dann soll sich ein Teil von ihnen mit dir aufstellen, doch sollen sie ihre Waffen nehmen. Wenn sie (die Betenden) sich dann niedergeworfen haben, sollen sie hinter euch treten’, und ein anderer Teil, die noch nicht gebetet hat, soll (nach vorne) kommen. Und sie sollen dann (auch) mit dir beten; doch sollen sie auf ihrer Hut sein und ihre Waffen nehmen. Diejenigen, die ungläubig sind, möchten, daß ihr eure Waffen und eure Sachen außer acht laßt, so daß sie auf einmal über euch herfallen (können). Es ist keine Sünde für euch, wenn ihr unter Regen zu leiden habt oder krank seid, eure Waffen abzulegen. Aber seid auf eurer Hut. Gewiß, Allah bereitet für die Ungläubigen schmachvolle Strafe." });
                        surah4.VerseList.Add(new Verse { Number = 103, TextBosnian = "A kada namaz završite, Allaha spominjite, i stojeći, i sjedeći, i ležeći na bokovima. A kada budete sigurni, namaz obavljajte u potpunosti; namaz je, uistinu, propisan vjernicima u određenim vremenskim razmacima.", TextEnglish = "And when you have completed the prayer, remember Allāh standing, sitting, or [lying] on your sides. But when you become secure, re-establish [regular] prayer. Indeed, prayer has been decreed upon the believers a decree of specified times.", TextGerman = "Wenn ihr das Gebet vollzogen habt, dann gedenkt Allahs stehend, sitzend und auf der Seite (liegend). Wenn ihr in Ruhe seid, dann verrichtet das Gebet (wie üblich). Das Gebet ist den Gläubigen zu bestimmten Zeiten vorgeschrieben." });
                        surah4.VerseList.Add(new Verse { Number = 104, TextBosnian = "I nemojte malaksati tragajući za neprijateljem; ako vi trpite bol, trpe i oni bol kao i vi, a vi se još od Allaha nadate onome čemu se oni ne nadaju. Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "And do not weaken in pursuit of the enemy. If you should be suffering - so are they suffering as you are suffering, but you expect from Allāh that which they expect not. And Allāh is ever Knowing and Wise.", TextGerman = "Und laßt nicht nach, das Volk (der feindlichen Ungläubigen) zu suchen. Wenn ihr zu leiden habt, so haben sie zu leiden wie ihr. Ihr aber habt von Allah zu erhoffen, was sie nicht zu erhoffen haben. Und Allah ist Allwissend und Allweise." });
                        surah4.VerseList.Add(new Verse { Number = 105, TextBosnian = "Mi tebi objavljujemo Knjigu s Istinom, da ljudima sudiš onako kako ti Allah pokazuje i ne budi branilac izdajicama.", TextEnglish = "Indeed, We have revealed to you, [O Muḥammad], the Book in truth so you may judge between the people by that which Allāh has shown you. And do not be for the deceitful an advocate.", TextGerman = "Gewiß, Wir haben dir das Buch mit der Wahrheit hinabgesandt, damit du zwischen den Menschen richtest auf Grund dessen, was Allah dir gezeigt hat. Sei kein Verfechter für die Verräter!" });
                        surah4.VerseList.Add(new Verse { Number = 106, TextBosnian = "I Allaha moli za oprost; Allah je, uistinu, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And seek forgiveness of Allāh. Indeed, Allāh is ever Forgiving and Merciful.", TextGerman = "Und bitte Allah um Vergebung. Gewiß, Allah ist Allvergebend und Barmherzig." });
                        surah4.VerseList.Add(new Verse { Number = 107, TextBosnian = "I ne zauzimaj se za one koji sami sebe iznevjeravaju, Allah, doista, ne voli onoga koji je pronevjeritelj i grešnik.", TextEnglish = "And do not argue on behalf of those who deceive themselves. Indeed, Allāh loves not one who is a habitually sinful deceiver.", TextGerman = "Und streite nicht zur Verteidigung derer, die sich selbst betrügen. Allah liebt nicht, wer ein Verräter und Sünder ist." });
                        surah4.VerseList.Add(new Verse { Number = 108, TextBosnian = "Oni se kriju od ljudi, a ne kriju se od Allaha, a On je s njima i kada noću smišljaju riječi kojima On nije zadovoljan. A Allah sve što oni rade, obuhvata.", TextEnglish = "They conceal [their evil intentions and deeds] from the people, but they cannot conceal [them] from Allāh, and He is with them [in His knowledge] when they spend the night in such as He does not accept of speech. And ever is Allāh, of what they do, encompassing.", TextGerman = "Sie verbergen sich vor den Menschen; verbergen sich jedoch nicht vor Allah, wo Er doch bei ihnen ist, wenn sie an Worten aushecken, womit Er nicht zufrieden ist. Allah umfaßt, was sie tun." });
                        surah4.VerseList.Add(new Verse { Number = 109, TextBosnian = "Eto, vi se zauzimate za njih u životu na dunjaluku, a ko će se pred Allahom za njih zauzimati na Kijametskom danu, ili ko će im zastupnik biti?!", TextEnglish = "Here you are - those who argue on their behalf in [this] worldly life - but who will argue with Allāh for them on the Day of Resurrection, or who will [then] be their representative?", TextGerman = "Ihr da seid es doch, die ihr im diesseitigen Leben zu ihrer Verteidigung gestritten habt. Aber wer wird am Tag der Auferstehung mit Allah zu ihrer Verteidigung streiten, oder wer wird (dann) ihr Sachwalter sein?" });
                        surah4.VerseList.Add(new Verse { Number = 110, TextBosnian = "Ko učini kakvo zlo, ili prema sebi zulum učini, a zatim Allaha moli da mu oprosti, naći će da Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And whoever does a wrong or wrongs himself but then seeks forgiveness of Allāh will find Allāh Forgiving and Merciful.", TextGerman = "Und wer etwas Böses tut oder sich selbst Unrecht zufügt und hierauf Allah um Vergebung bittet, wird Allah Allvergebend und Barmherzig finden." });
                        surah4.VerseList.Add(new Verse { Number = 111, TextBosnian = "Ko grijeh uradi, na svoju štetu ga je uradio, a Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "And whoever earns [i.e., commits] a sin only earns it against himself. And Allāh is ever Knowing and Wise.", TextGerman = "Und wer eine Sünde begeht’, begeht sie nur gegen sich selbst. Allah ist Allwissend und Allweise." });
                        surah4.VerseList.Add(new Verse { Number = 112, TextBosnian = "Ko počini prestup ili grijeh, a potom zato optuži nedužna čovjeka, natovario je na sebe i kletvu i grijeh očiti.", TextEnglish = "But whoever earns an offense or a sin and then blames it on an innocent [person] has taken upon himself a slander and manifest sin.", TextGerman = "Und wer eine Verfehlung oder eine Sünde begeht und sie hierauf einem Unschuldigen vorwirft, der lädt damit Verleumdung und deutliche Sünde auf sich." });
                        surah4.VerseList.Add(new Verse { Number = 113, TextBosnian = "Da nije Allahove dobrote i milosti Njegove prema tebi, jedna skupina njih bi nastojala da te zavede, ali oni samo sebe zavode, a tebi ne mogu ništa nauditi. Allah tebi objavljuje Knjigu i mudrost i poučava te onome što nisi znao a Allahovo dobro prema tebi je veliko.", TextEnglish = "And if it was not for the favor of Allāh upon you, [O Muḥammad], and His mercy, a group of them would have determined to mislead you. But they do not mislead except themselves, and they will not harm you at all. And Allāh has revealed to you the Book and wisdom and has taught you that which you did not know. And ever has the favor of Allāh upon you been great.", TextGerman = "Und wenn nicht Allahs Huld gegen dich und Sein Erbarmen gewesen wären, hätte ein Teil von ihnen wahrlich vorgehabt, dich in die Irre zu führen. Aber sie führen nur sich selbst in die Irre und fügen dir keinerlei Schaden zu. Allah hat auf dich das Buch und die Weisheit herabgesandt und dich gelehrt, was du (vorher) nicht wußtest. Und Allahs Huld gegen dich ist groß." });
                        surah4.VerseList.Add(new Verse { Number = 114, TextBosnian = "Nema dobra u mnogim njihovim potajnim govorima, osim u govoru onog ko traži da se sadaka udjeljuje, ili dobro čini, ili uspostavlja mir među ljudima. Onome ko to čini iz želje za Allahovim zadovoljstvom, Mi ćemo veliku nagradu dati.", TextEnglish = "No good is there in much of their private conversation, except for those who enjoin charity or that which is right or conciliation between people. And whoever does that seeking means to the approval of Allāh - then We are going to give him a great reward.", TextGerman = "Nichts Gutes ist in vielen ihrer vertraulichen Gespräche, außer derer, die Almosen, Rechtes oder Aussöhnung unter den Menschen befehlen. Und wer dies im Trachten nach Allahs Zufriedenheit tut, dem werden Wir großartigen Lohn geben." });
                        surah4.VerseList.Add(new Verse { Number = 115, TextBosnian = "A onoga ko se suprotstavi Poslaniku, nakon što mu jasno bude pokazan Pravi put, i slijedi put koji nije put vjernika, pustit ćemo da čini što je naumio, i bacit ćemo ga u Džehennem, a loše je to konačno odredište!", TextEnglish = "And whoever opposes the Messenger after guidance has become clear to him and follows other than the way of the believers - We will give him what he has taken[218] and drive him into Hell, and evil it is as a destination.", TextGerman = "Wer aber dem Gesandten entgegenwirkt, nachdem ihm die Rechtleitung klargeworden ist, und einem, anderen Weg als dem der Gläubigen folgt, werden Wir dem zukehren, dem er sich zugekehrt hat, und ihn der Hölle aussetzen, und (wie) böse ist der Ausgang!" });
                        surah4.VerseList.Add(new Verse { Number = 116, TextBosnian = "Allah, doista, neće oprostiti da se Njemu išta u obožavanju pridružuje, a kome hoće oprostit će sve osim toga; onaj ko Allahu išta u obožavanju pridružuje - doista je daleko zalutao.", TextEnglish = "Indeed, Allāh does not forgive association with Him, but He forgives what is less than that for whom He wills. And he who associates others with Allāh has certainly gone far astray.", TextGerman = "Allah vergibt gewiß nicht, daß man Ihm (etwas) beigesellt. Doch was außer diesem ist, vergibt Er, wem Er will. Wer Allah (etwas) beigesellt, der ist fürwahr weit abgeirrt." });
                        surah4.VerseList.Add(new Verse { Number = 117, TextBosnian = "Oni mimo Allaha samo ženske kumire prizivaju i ne prizivaju drugog do šejtana prkosnika.", TextEnglish = "They call upon instead of Him none but female [deities], and they [actually] call upon none but a rebellious Satan,", TextGerman = "Sie rufen außer Ihm nur weibliche (Gottheiten) an, und sie rufen nur einen rebellischen Satan an." });
                        surah4.VerseList.Add(new Verse { Number = 118, TextBosnian = "Allah ga je prokleo, a on je rekao: 'Ja ću sigurno preuzeti za sebe određeni dio Tvojih robova!", TextEnglish = "Whom Allāh has cursed. For he had said, 'I will surely take from among Your servants a specific portion.", TextGerman = "Allah verfluchte ihn. Und er sagte: Ich werde von Deinen Dienern ganz gewiß einen festgesetzten Anteil nehmen," });
                        surah4.VerseList.Add(new Verse { Number = 119, TextBosnian = "I sigurno ću ih navoditi u zabludu, i sigurno ih lažnim nadama mamiti, i zapovjedit ću im sigurno da uši stoci režu, i sigurno ću im narediti da Allahovo stvaranje preinačavaju!' A ko šejtana, umjesto Allaha, za zaštitnika prihvati, on je na gubitku očitom.", TextEnglish = "And I will mislead them, and I will arouse in them [sinful] desires, and I will command them so they will slit the ears of cattle, and I will command them so they will change the creation of Allāh.' And whoever takes Satan as an ally instead of Allāh has certainly sustained a clear loss.", TextGerman = "und ich werde sie ganz gewiß in die Irre führen und ganz gewiß in ihnen Wünsche erwecken und ihnen ganz gewiß befehlen, und da werden sie ganz gewiß die Ohren des Viehs abschneiden; wahrlich, ich werde ihnen befehlen, und da werden sie ganz gewiß Allahs Schöpfung ändern. Wer sich den Satan außer Allah zum Schutzherrn nimmt, der hat fürwahr einen offenkundigen Verlust erlitten." });
                        surah4.VerseList.Add(new Verse { Number = 120, TextBosnian = "On im obećanja daje i lažnim nadama ih mami. A ono što im šejtan obećava samo je obmana.", TextEnglish = "He [i.e., Satan] promises them and arouses desire in them. But Satan does not promise them except delusion.", TextGerman = "Er macht ihnen Versprechungen und erweckt in ihnen Wünsche; aber der Satan macht ihnen nur Versprechungen in Trug." });
                        surah4.VerseList.Add(new Verse { Number = 121, TextBosnian = "To su oni čije je prebivalište Džehennem, i oni iz njega neće spasa naći.", TextEnglish = "The refuge of those will be Hell, and they will not find from it an escape.", TextGerman = "Jene - ihr Zufluchtsort wird die Hölle sein, und sie werden aus ihr kein Entrinnen finden." });
                        surah4.VerseList.Add(new Verse { Number = 122, TextBosnian = "A one koji vjeruju i dobra djela čine Mi ćemo uvesti u bašče džennetske kroz koje rijeke teku, i u kojima će vječno ostati. Obećanje Allahovo je istinito, a čije su riječi od Allahovih istinitije?!", TextEnglish = "But the ones who believe and do righteous deeds - We will admit them to gardens beneath which rivers flow, wherein they will abide forever. [It is] the promise of Allāh, [which is] truth, and who is more truthful than Allāh in statement.", TextGerman = "Diejenigen aber, die glauben und rechtschaffene Werke tun, die werden Wir in Gärten eingehen lassen, durcheilt von Bächen, ewig und auf immer darin zu bleiben - Allahs Versprechen in Wahrheit. Und wer ist wahrhaftiger als Allah in Seinen Worten?" });
                        surah4.VerseList.Add(new Verse { Number = 123, TextBosnian = "To neće biti ni po vašim željama, ni po željama sljedbenika Knjige; onaj koji radi zlo, bit će kažnjen za to i neće naći, mimo Allaha, ni zaštitnika ni pomagača,", TextEnglish = "It [i.e., Paradise] is not [obtained] by your wishful thinking nor by that of the People of the Scripture. Whoever does a wrong will be recompensed for it, and he will not find besides Allāh a protector or a helper.", TextGerman = "Es geht weder nach euren Wünschen noch nach den Wünschen der Leute der Schrift. Wer Böses tut, dem wird es vergolten, und der findet für sich außer Allah weder Schutzherrn noch Helfer." });
                        surah4.VerseList.Add(new Verse { Number = 124, TextBosnian = "a onaj koji čini dobro, bio muškarac ili žena, a vjernik je, ući će u Džennet i nikome, ni koliko udubljenje na hurminoj košpici, zulum neće biti nanesen.", TextEnglish = "And whoever does righteous deeds, whether male or female, while being a believer - those will enter Paradise and will not be wronged, [even as much as] the speck on a date seed.", TextGerman = "Wer aber, sei es Mann oder Frau, etwas an rechtschaffenen Werken tut, und dabei gläubig ist, jene werden in den (Paradies)garten eingehen, und es wird ihnen nicht ein Dattelkerngrübchen Unrecht zugefügt." });
                        surah4.VerseList.Add(new Verse { Number = 125, TextBosnian = "Ko je bolje vjere od onoga ko je predao svoje lice Allahu, a dobročinitelj je i slijedi vjeru Ibrahimovu, koji je bio vjere čiste?! A Allah je uzeo Ibrahima za prijatelja.", TextEnglish = "And who is better in religion than one who submits himself to Allāh while being a doer of good and follows the religion of Abraham, inclining toward truth? And Allāh took Abraham as an intimate friend.", TextGerman = "Wer hätte eine bessere Religion, als wer sein Gesicht Allah hingibt und dabei Gutes tut und dem Glaubensbekenntnis Ibrahims folgt, (als) Anhänger des rechten Glaubens? Und Allah nahm sich Ibrahim zum Freund." });
                        surah4.VerseList.Add(new Verse { Number = 126, TextBosnian = "Allahovo je sve što je na nebesima i što je na Zemlji. Allah sve obuhvata.", TextEnglish = "And to Allāh belongs whatever is in the heavens and whatever is on the earth. And ever is Allāh, of all things, encompassing.", TextGerman = "Und Allah gehört (alles), was in den Himmeln und was auf der Erde ist; Allah umfaßt alles." });
                        surah4.VerseList.Add(new Verse { Number = 127, TextBosnian = "Oni traže od tebe propise o ženama. Reci: 'Allah će vam objasniti propise o njima i o onome što vam se čita u Knjizi o ženama - siroticama, kojima ne dajete ono što im je propisano, a ne želite da se njima oženite, i o nejakoj djeci, i o tome da prema siročadi pravedno postupate. A Allah sigurno zna za dobro koje učinite.'", TextEnglish = "And they request from you, [O Muḥammad], a [legal] ruling concerning women. Say, 'Allāh gives you a ruling about them and [about] what has been recited to you in the Book concerning the orphan girls to whom you do not give what is decreed for them[219] - and [yet] you desire to marry them - and concerning the oppressed among children and that you maintain for orphans [their rights] in justice.' And whatever you do of good - indeed, Allāh is ever Knowing of it.", TextGerman = "Sie fragen dich um Belehrung über die Frauen. Sag: „Allah belehrt euch über sie, und (weiter lehrt,) was euch im Buch verlesen wird über die weiblichen Waisen, denen ihr nicht gebt, was ihnen vorgeschrieben ist, und die ihr zu heiraten begehrt, und die schwachen unter den Kindern, und daß ihr gegenüber den Waisen Gerechtigkeit wahrt. Und was immer ihr an Gutem tut, so weiß Allah darüber Bescheid." });
                        surah4.VerseList.Add(new Verse { Number = 128, TextBosnian = "Ako se neka žena plaši muževe mržnje ili izbjegavanja, onda se oni neće ogriješiti ako se međusobno nagode, a nagodba je bolja. Ljudske duše sklone su pohlepi. I ako vi budete lijepo postupali i Allaha se bojali - pa Allah u potpunosti zna ono što radite.", TextEnglish = "And if a woman fears from her husband contempt or evasion, there is no sin upon them if they make terms of settlement between them - and settlement is best. And present in [human] souls is stinginess.[220] But if you do good and fear Allāh - then indeed Allāh is ever, of what you do, Aware.", TextGerman = "Und wenn eine Frau von ihrem Ehemann Widersetzlichkeit oder Meidung befürchtet, so ist es keine Sünde für sie (beide), sich in Frieden zu einigen; denn friedliche Einigung ist besser. Und die Seelen sind der Habsucht zugänglich. Doch wenn ihr wohltätig seid und gottesfürchtig, so ist Allah dessen, was ihr tut, gewiß Kundig." });
                        surah4.VerseList.Add(new Verse { Number = 129, TextBosnian = "Vi nećete moći potpuno jednako postupati prema suprugama svojim ma koliko to željeli. I nemojte prema nekoj sasvim naginjati pa da neku učinite kao da je bez muža, a nerazvedena. A ako se popravite i Allaha bojite, pa Allah je, uistinu, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And you will never be able to be equal [in feeling] between wives, even if you should strive [to do so]. So do not incline completely [toward one] and leave another hanging.[221] And if you amend [your affairs] and fear Allāh - then indeed, Allāh is ever Forgiving and Merciful.", TextGerman = "Und ihr werdet zwischen den Frauen nicht gerecht handeln können, auch wenn ihr danach trachtet. Aber neigt nicht gänzlich (von einer weg zu der anderen), so daß ihr sie gleichsam in der Schwebe laßt. Und wenn ihr (es) wiedergutmacht und gottesfürchtig seid, gewiß, so ist Allah Allvergebend und Barmherzig." });
                        surah4.VerseList.Add(new Verse { Number = 130, TextBosnian = "A ako se njih dvoje ipak rastave, Allah će ih, iz obilja Svoga, neovisnim učiniti. Allah je neizmjerno darežljiv i mudar.", TextEnglish = "But if they separate [by divorce], Allāh will enrich each [of them] from His abundance. And ever is Allāh Encompassing and Wise.", TextGerman = "Und wenn die beiden sich trennen, wird Allah jeden aus Seiner Fülle bereichern. Allah ist Allumfassend und Allweise." });
                        surah4.VerseList.Add(new Verse { Number = 131, TextBosnian = "Allahovo je ono što je na nebesima i ono što je na Zemlji. Mi smo onima kojima je data Knjiga prije vas, a i vama, već zapovjedili da se bojite Allaha. A ako ne budete vjerovali, pa, uistinu, Allahovo je ono što je na nebesima i ono što je na Zemlji, Allah je neovisan i hvaljen.", TextEnglish = "And to Allāh belongs whatever is in the heavens and whatever is on the earth. And We have instructed those who were given the Scripture before you and yourselves to fear Allāh. But if you disbelieve - then to Allāh belongs whatever is in the heavens and whatever is on the earth. And ever is Allāh Free of need and Praiseworthy.", TextGerman = "Allah gehört (alles), was in den Himmeln und was auf der Erde ist. Und Wir haben bereits denjenigen, denen vor euch die Schrift gegeben wurde, und euch anbefohlen: Fürchtet Allah! Wenn ihr aber ungläubig seid, gewiß, so gehört Allah (alles), was in den Himmeln und was auf der Erde ist. Allah ist Unbedürftig und Lobenswürdig." });
                        surah4.VerseList.Add(new Verse { Number = 132, TextBosnian = "Allahovo je ono što je na nebesima i što je na Zemlji, i Allah je kao zaštitnik dovoljan.", TextEnglish = "And to Allāh belongs whatever is in the heavens and whatever is on the earth. And sufficient is Allāh as Disposer of affairs.[222]", TextGerman = "Allah gehört (alles), was in den Himmeln und was auf der Erde ist, und Allah genügt als Sachwalter." });
                        surah4.VerseList.Add(new Verse { Number = 133, TextBosnian = "Ako On hoće, vas će ukloniti, o ljudi, a druge će dovesti! On je kadar to učiniti.", TextEnglish = "If He wills, He can do away with you, O people, and bring others [in your place]. And ever is Allāh competent to do that.", TextGerman = "Wenn Er will, läßt Er euch vergehen, ihr Menschen, und bringt andere. Allah hat dazu die Macht." });
                        surah4.VerseList.Add(new Verse { Number = 134, TextBosnian = "Onaj koji želi nagradu na dunjaluku, pa u Allaha je nagrada i ovoga i onoga svijeta; Allah je Onaj Koji sve čuje i Onaj Koji sve vidi.", TextEnglish = "Whoever desires the reward of this world - then with Allāh is the reward of this world and the Hereafter. And ever is Allāh Hearing and Seeing.", TextGerman = "Wer immer die Belohnung des Diesseits will, so ist bei Allah die Belohnung des Diesseits und des Jenseits. Allah ist Allhörend und Allsehend." });
                        surah4.VerseList.Add(new Verse { Number = 135, TextBosnian = "O vi koji vjerujete, budite uvijek pravedni, svjedočite radi Allaha, pa makar to bilo protiv vas samih, ili roditelja i rodbine. Bude li onaj o kome svjedočite bogat ili siromah, pa Allah je preči njima! I ne slijedite strasti, kako ne biste bili nepravedni. A ako budete krivo svjedočili ili svjedočenje izbjegavati, pa Allah, zaista, u potpunosti zna ono što radite.", TextEnglish = "O you who have believed, be persistently standing firm in justice, witnesses for Allāh, even if it be against yourselves or parents and relatives. Whether one is rich or poor, Allāh is more worthy of both.[223] So follow not [personal] inclination, lest you not be just. And if you distort [your testimony] or refuse [to give it], then indeed Allāh is ever, of what you do, Aware.", TextGerman = "O die ihr glaubt, seid Wahrer der Gerechtigkeit, Zeugen für Allah, auch wenn es gegen euch selbst oder die Eltern und nächsten Verwandten sein sollte! Ob er (der Betreffende) reich oder arm ist, so steht Allah beiden näher. Darum folgt nicht der Neigung, daß ihr nicht gerecht handelt! Wenn ihr (die Wahrheit) verdreht oder euch (davon) abwendet, gewiß, so ist Allah dessen, was ihr tut, Kundig." });
                        surah4.VerseList.Add(new Verse { Number = 136, TextBosnian = "O vi koji vjerujete, vjerujte u Allaha i Poslanika Njegova, i u Knjigu koju On Svome Poslaniku objavljuje i u knjige koje je objavio prije. A onaj koji ne bude vjerovao u Allaha, i u meleke Njegove, i u knjige Njegove, i u poslanike Njegove, i u Posljednji dan - daleko je zalutao.", TextEnglish = "O you who have believed, believe[224] in Allāh and His Messenger and the Book that He sent down upon His Messenger and the Scripture which He sent down before. And whoever disbelieves in Allāh, His angels, His books, His messengers, and the Last Day has certainly gone far astray.", TextGerman = "O die ihr glaubt, glaubt an Allah und Seinen Gesandten und das Buch, das Er Seinem Gesandten offenbart und die Schrift, die Er zuvor herabgesandt hat. Wer Allah, Seine Engel, Seine Schriften, Seine Gesandten und den Jüngsten Tag verleugnet, der ist fürwahr weit abgeirrt." });
                        surah4.VerseList.Add(new Verse { Number = 137, TextBosnian = "Onima koji su bili vjernici, a zatim postali nevjernici, pa opet postali vjernici i ponovo postali nevjernici, i pojačali nevjerovanje, Allah, doista, neće oprostiti i neće ih na Pravi put izvesti.", TextEnglish = "Indeed, those who have believed then disbelieved, then believed then disbelieved, and then increased in disbelief - never will Allāh forgive them, nor will He guide them to a way.", TextGerman = "Gewiß, diejenigen, die gläubig sind, hierauf ungläubig werden, hierauf (wieder) gläubig werden, hierauf (wieder) ungläubig werden und dann an Unglauben zunehmen - es ist nicht Allahs (Wille), ihnen zu vergeben noch sie einen (rechten) Weg zu leiten." });
                        surah4.VerseList.Add(new Verse { Number = 138, TextBosnian = "Bolnu patnju navijesti licemjerima,", TextEnglish = "Give tidings to the hypocrites that there is for them a painful punishment -", TextGerman = "Verkünde den Heuchlern, daß es für sie schmerzhafte Strafe geben wird," });
                        surah4.VerseList.Add(new Verse { Number = 139, TextBosnian = "koji prisno prijateljuju s nevjernicima, mimo vjernika! Zar od njih traže moć, a sva moć pripada samo Allahu?!", TextEnglish = "Those who take disbelievers as allies instead of the believers. Do they seek with them honor [through power]? But indeed, honor belongs to Allāh entirely.[225]", TextGerman = "(sie,) die die Ungläubigen anstatt der Gläubigen zu Schutzherren nehmen. Begehren sie (etwa) Macht bei ihnen (zu finden)? Gewiß, alle Macht gehört Allah." });
                        surah4.VerseList.Add(new Verse { Number = 140, TextBosnian = "On vam je već u Knjizi objavio: 'Kada čujete da su Allahove riječi predmet poricanja i izrugivanja, ne sjedite s onima koji to čine dok ne stupe u drugi razgovor, inače, bit ćete kao oni.' Allah će, sigurno, sastaviti u Džehennemu sve licemjere i nevjernike,", TextEnglish = "And it has already come down to you in the Book [i.e., the Qur’ān] that when you hear the verses of Allāh [recited], they are denied [by them] and ridiculed; so do not sit with them until they enter into another conversation. Indeed, you would then be like them.[226] Indeed, Allāh will gather the hypocrites and disbelievers in Hell all together -", TextGerman = "Er hat euch (doch) bereits im Buch offenbart: Wenn ihr hört, daß man Allahs Zeichen verleugnet und sich über sie lustig macht, dann sitzt nicht mit ihnen (zusammen), bis sie auf ein anderes Gespräch eingehen.Sonst seid ihr ihnen gleich.Gewiß, Allah wird die Heuchler und die Ungläubigen alle in der Hölle versammeln," });
                        surah4.VerseList.Add(new Verse { Number = 141, TextBosnian = "one koji iščekuju šta će biti s vama. Pa ako vam Allah daruje pobjedu, oni reknu: 'Zar nismo bili uz vas', a ako sreća posluži nevjernike, onda govore njima: 'Zar vas nismo mogli pobijediti, i zar vas nismo odbranili od vjernika?!' Na Kijametskom danu Allah će vam svima presuditi! A Allah neće dati priliku nevjernicima da unište vjernike.", TextEnglish = "Those who wait [and watch] you. Then if you gain a victory from Allāh, they say, 'Were we not with you?' But if the disbelievers have a success, they say [to them], 'Did we not gain the advantage over you, but we protected you from the believers?' Allāh will judge between [all of] you on the Day of Resurrection, and never will Allāh give the disbelievers over the believers a way [to overcome them].[227]", TextGerman = "die euch gegenüber abwarten. Wenn euch ein entscheidender Sieg von Allah beschieden ist, sagen sie: Sind wir nicht mit euch gewesen? Und wenn den Ungläubigen ein Sieg beschieden ist, sagen sie: Haben wir uns nicht eurer (Angelegenheiten) bemächtigt gehabt und die Gläubigen von euch abgehalten? Aber Allah wird zwischen euch am Tag der Auferstehung richten. Und Allah wird den Ungläubigen keine Möglichkeit geben(,) gegen die Gläubigen (vorzugehen)." });
                        surah4.VerseList.Add(new Verse { Number = 142, TextBosnian = "Licemjeri, zaista, nastoje prevariti Allaha, a On će njih nadmudriti. Kada ustaju da namaz obavljaju, lijeno se dižu i prikazuju se svijetu, a Allaha samo malo spominju.", TextEnglish = "Indeed, the hypocrites [think to] deceive Allāh, but He is deceiving them. And when they stand for prayer, they stand lazily, showing [themselves to] the people and not remembering Allāh except a little,", TextGerman = "Gewiß, die Heuchler möchten Allah betrügen, doch ist Er es, der sie betrügt. Und wenn sie sich zum Gebet hinstellen, stellen sie sich schwerfällig hin, wobei sie von den Menschen gesehen werden wollen, und gedenken Allahs nur wenig;" });
                        surah4.VerseList.Add(new Verse { Number = 143, TextBosnian = "Kolebaju se između ovoga i toga - vjerovanja i nevjerovanja, niti su sa ovima niti sa onima. A onome kome Allah da da je u zabludi nećeš naći puta ka Uputi.", TextEnglish = "Wavering between them, [belonging] neither to these [i.e., the believers] nor to those [i.e., the disbelievers]. And whoever Allāh sends astray - never will you find for him a way.", TextGerman = "dazwischen hin und her schwankend - weder zu diesen noch zu jenen (gehörend). Und wen Allah in die Irre gehen läßt, für den wirst du keinen Weg finden." });
                        surah4.VerseList.Add(new Verse { Number = 144, TextBosnian = "O vi koji vjerujete, ne uzimajte za zaštitnike i prijatelje nevjernike umjesto vjernika! Zar hoćete da pružite Allahu očigledan dokaz protiv sebe?!", TextEnglish = "O you who have believed, do not take the disbelievers as allies instead of the believers. Do you wish to give Allāh against yourselves a clear case?", TextGerman = "O die ihr glaubt, nehmt nicht die Ungläubigen anstatt der Gläubigen zu Schutzherren! Wollt ihr denn Allah eine offenkundige Handhabe gegen euch liefern?" });
                        surah4.VerseList.Add(new Verse { Number = 145, TextBosnian = "Licemjeri će, doista, na samom dnu Džehennema biti i ti im nećeš pomagača naći.", TextEnglish = "Indeed, the hypocrites will be in the lowest depths of the Fire - and never will you find for them a helper -", TextGerman = "Gewiß, die Heuchler werden sich auf dem untersten Grund des (Höllen)feuers befinden, und du wirst für sie keinen Helfer finden," });
                        surah4.VerseList.Add(new Verse { Number = 146, TextBosnian = "Osim onih koji se pokaju i poprave i koji čvrsto Allaha prihvate i vjeru svoju u Allaha iskreno ispolje, oni će biti s vjernicima, a Allah će, sigurno, vjernicima veliku nagradu dati.", TextEnglish = "Except for those who repent, correct themselves, hold fast to Allāh, and are sincere in their religion for Allāh, for those will be with the believers. And Allāh is going to give the believers a great reward.", TextGerman = "- ausgenommen diejenigen, die bereuen und verbessern und an Allah festhalten und ihre Religion aufrichtig für Allah ausüben; jene werden mit den Gläubigen sein. Und Allah wird den Gläubigen großartigen Lohn geben." });
                        surah4.VerseList.Add(new Verse { Number = 147, TextBosnian = "Zašto bi vas Allah kažnjavao ako budete zahvaljivali i vjerovali!? Allah je zahvalan i Onaj Koji sve zna.", TextEnglish = "What would Allāh do with [i.e., gain from] your punishment if you are grateful and believe? And ever is Allāh Appreciative[228] and Knowing.", TextGerman = "Warum sollte Allah euch strafen, wenn ihr dankbar und gläubig seid? Allah ist Dankbar und Allwissend." });
                        surah4.VerseList.Add(new Verse { Number = 148, TextBosnian = "Allah ne voli da se o zlu glasno govori, to može samo onaj kome je zulum učinjen. A Allah je Onaj Koji sve čuje i Onaj Koji sve zna.", TextEnglish = "Allāh does not like the public mention of evil except by one who has been wronged. And ever is Allāh Hearing and Knowing.", TextGerman = "Allah liebt nicht den laut vernehmbaren Gebrauch von bösen Worten, außer durch jemanden, dem Unrecht zugefügt worden ist. Allah ist Allhörend und Allwissend." });
                        surah4.VerseList.Add(new Verse { Number = 149, TextBosnian = "Bilo da vi dobro djelo javno učinite, ili ga sakrijete, ili nepravdu oprostite - pa Allah je Onaj Koji stalno poništava grijehe i svemoćan je.", TextEnglish = "If [instead] you show [some] good or conceal it or pardon an offense - indeed, Allāh is ever Pardoning and Competent.[229]", TextGerman = "Ob ihr etwas Gutes offenlegt, oder es verbergt, oder etwas Böses verzeiht - gewiß, Allah ist Allverzeihend und Allmächtig." });
                        surah4.VerseList.Add(new Verse { Number = 150, TextBosnian = "Oni koji u Allaha i poslanike Njegove ne vjeruju i žele između Allaha i poslanika Njegovih u vjerovanju napraviti razliku, te govore: 'U neke vjerujemo, a u neke ne vjerujemo', i žele između to dvoje iznaći put neki,", TextEnglish = "Indeed, those who disbelieve in Allāh and His messengers and wish to discriminate between Allāh and His messengers and say, 'We believe in some and disbelieve in others,' and wish to adopt a way in between -", TextGerman = "Diejenigen, die Allah und Seine Gesandten verleugnen und zwischen Allah und Seinen Gesandten einen Unterschied machen wollen und sagen: Wir glauben an die einen und verleugnen die anderen, und einen Weg dazwischen einschlagen wollen," });
                        surah4.VerseList.Add(new Verse { Number = 151, TextBosnian = "oni su zbilja pravi nevjernici, a Mi smo nevjernicima pripremili ponižavajuću patnju.", TextEnglish = "Those are the disbelievers, truly. And We have prepared for the disbelievers a humiliating punishment.", TextGerman = "das sind die wahren Ungläubigen. Und für die Ungläubigen haben Wir schmachvolle Strafe bereitet." });
                        surah4.VerseList.Add(new Verse { Number = 152, TextBosnian = "A one koji u Allaha i poslanike Njegove vjeruju i ni jednoga od njih ne izdvajaju - On će, sigurno, nagraditi. A Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "But they who believe in Allāh and His messengers and do not discriminate between any of them - to those He is going to give their rewards. And ever is Allāh Forgiving and Merciful.", TextGerman = "Denjenigen aber, die an Allah und Seine Gesandten glauben und keinen Unterschied zwischen jemandem von ihnen machen, denen wird Er ihren Lohn geben. Allah ist Allvergebend und Barmherzig." });
                        surah4.VerseList.Add(new Verse { Number = 153, TextBosnian = "Oni kojima je data Knjiga traže od tebe da im s neba spustiš Knjigu. Pa od Musaa su tražili i više od toga, kad su rekli: 'Pokaži nam Allaha!' Pa ih je, zbog zuluma njihova, munja ošinula. Poslije su, kada su im očigledni dokazi pokazani, tele za božanstvo prihvatili, ali smo i to oprostili, a Musau smo protiv njih očiti dokaz dali.", TextEnglish = "The People of the Scripture ask you to bring down to them a book from the heaven. But they had asked of Moses [even] greater than that and said, 'Show us Allāh outright,' so the thunderbolt struck them for their wrongdoing. Then they took the calf [for worship] after clear evidences had come to them, and We pardoned that. And We gave Moses a clear authority.", TextGerman = "Die Leute der Schrift verlangen von dir, daß du ihnen vom Himmel ein Buch offenbaren läßt. Sie haben von Musa bereits etwas noch Größeres als dies verlangt, denn sie sagten: Zeige uns Allah unverhüllt! Da ergriff sie der Donnerschlag wegen ihrer Ungerechtigkeit. Hierauf nahmen sie das Kalb an, nachdem die klaren Beweise zu ihnen gekommen waren. Aber Wir verziehen es und gaben Musa offenkundige Gewalt." });
                        surah4.VerseList.Add(new Verse { Number = 154, TextBosnian = "I iznad njih smo brdo digli, zbog zavjeta koji su dali, i rekli smo im: 'Na kapiju pognutih glava uđite!' I još smo rekli: 'Subotu ne oskrnavite!' I od njih smo čvrsto obećanje uzeli.", TextEnglish = "And We raised over them the mount for [refusal of] their covenant; and We said to them, 'Enter the gate bowing humbly'; and We said to them, 'Do not transgress on the sabbath'; and We took from them a solemn covenant.", TextGerman = "Und Wir hoben den Berg über sie bei (der Entgegennahme von) ihrem Abkommen empor. Und Wir sagten zu ihnen: Tretet, euch niederwerfend, durch das Tor ein! Und Wir sagten zu ihnen: Übertretet nicht den Sabbat! Und Wir trafen mit ihnen ein festes Abkommen." });
                        surah4.VerseList.Add(new Verse { Number = 155, TextBosnian = "Zato što su zavjet prekršili i što u Allahove ajete i znakove nisu povjerovali, što su bez razloga vjerovjesnike ubijali i što su govorili: 'Naša srca su u omotu'', Allah je, zbog nevjerovanja, njihova srca zapečatio, pa ih samo malo vjeruje.", TextEnglish = "And [We cursed them][230] for their breaking of the covenant and their disbelief in the signs of Allāh and their killing of the prophets without right and their saying, 'Our hearts are wrapped' [i.e., sealed against reception]. Rather, Allāh has sealed them because of their disbelief, so they believe not, except for a few.[231]", TextGerman = "(Verflucht sind sie) dafür, daß sie ihr Abkommen brachen und Allahs Zeichen verleugneten und (daß sie) die Propheten zu Unrecht töteten und (daß sie) sagten: Unsere Herzen sind verhüllt. - Nein! Vielmehr hat Allah sie für ihren Unglauben versiegelt; darum glauben sie nur wenig," });
                        surah4.VerseList.Add(new Verse { Number = 156, TextBosnian = "I zbog nevjerovanja njihovoga i zbog iznošenja teške klevete protiv Merjeme.", TextEnglish = "And [We cursed them] for their disbelief and their saying against Mary a great slander[232]", TextGerman = "und daß sie ungläubig waren und gegen Maryam gewaltige Verleumdung aussprachen," });
                        surah4.VerseList.Add(new Verse { Number = 157, TextBosnian = "I zbog riječi njihovih: 'Mi smo ubili Mesiha, Isaa, sina Merjemina, Allahova poslanika.' A nisu ga ubili, ni raspeli, već im se pričinilo. Oni koji su se o njemu u mišljenju razišli, zaista su u sumnji u pogledu njega; o tome ništa pouzdano ne znaju, samo se za nagađanjem povode; a sigurno je da ga nisu ubili,", TextEnglish = "And [for] their saying, 'Indeed, we have killed the Messiah, Jesus the son of Mary, the messenger of Allāh.' And they did not kill him, nor did they crucify him; but [another] was made to resemble him to them. And indeed, those who differ over it are in doubt about it. They have no knowledge of it except the following of assumption. And they did not kill him, for certain.[233]", TextGerman = "und dafür, daß sie sagten: Gewiß, wir haben al-Masih ’Isa, den Sohn Maryams, den Gesandten Allahs getötet. - Aber sie haben ihn weder getötet noch gekreuzigt, sondern es erschien ihnen so. Und diejenigen, die sich darüber uneinig sind, befinden sich wahrlich im Zweifel darüber. Sie haben kein Wissen darüber, außer daß sie Mutmaßungen folgen. Und sie haben ihn mit Gewißheit nicht getötet." });
                        surah4.VerseList.Add(new Verse { Number = 158, TextBosnian = "već ga je Allah uzdigao Sebi. A Allah je silan i mudar.", TextEnglish = "Rather, Allāh raised him to Himself. And ever is Allāh Exalted in Might and Wise.", TextGerman = "Nein! Vielmehr hat Allah ihn zu Sich erhoben. Allah ist Allmächtig und Allweise." });
                        surah4.VerseList.Add(new Verse { Number = 159, TextBosnian = "I nema nijednog sljedbenika Knjige koji, prije njegove smrti, neće u njega kako treba povjerovati, a na Kijametskom danu on će protiv njih svjedočiti.", TextEnglish = "And there is none from the People of the Scripture but that he will surely believe in him [i.e., Jesus] before his death.[234] And on the Day of Resurrection he will be against them a witness.", TextGerman = "Es gibt keinen unter den Leuten der Schrift, der nicht noch vor dessen Tod ganz gewiß an ihn glauben wird. Und am Tag der Auferstehung wird er über sie Zeuge sein." });
                        surah4.VerseList.Add(new Verse { Number = 160, TextBosnian = "I zbog zuluma jevreja Mi smo im neka lijepa jela zabranili, koja su im bila dozvoljena, i zbog toga što su mnoge od Allahova puta odvraćali.", TextEnglish = "For wrongdoing on the part of the Jews, We made unlawful for them [certain] good foods which had been lawful to them, and for their averting from the way of Allāh many [people],", TextGerman = "Wegen Ungerechtigkeit derer, die dem Judentum angehören, hatten Wir ihnen gute Dinge verboten, die ihnen erlaubt gewesen waren, und weil sie viel von Allahs Weg abhielten," });
                        surah4.VerseList.Add(new Verse { Number = 161, TextBosnian = "I zato što su kamatu uzimali, a bilo im je zabranjeno, i zato što su tuđe imetke na nedozvoljen način jeli. A za nevjernike među njima Mi smo patnju bolnu pripremili.", TextEnglish = "And [for] their taking of usury while they had been forbidden from it, and their consuming of the people's wealth unjustly. And We have prepared for the disbelievers among them a painful punishment.", TextGerman = "und (weil sie) Zins nahmen, wo es ihnen doch verboten worden war, und den Besitz der Menschen in unrechter Weise aufzehrten. Und Wir haben den Ungläubigen unter ihnen schmerzhafte Strafe bereitet." });
                        surah4.VerseList.Add(new Verse { Number = 162, TextBosnian = "Ali oni među njima koji su u znanje duboko pronikli, i vjernici koji vjeruju u ono što se objavljuje tebi i u ono što je objavljeno prije tebe, naročito oni koji namaz obavljaju i oni koji zekat daju i u Allaha i Posljednji dan vjeruju - njima ćemo sigurno veliku nagradu dati.", TextEnglish = "But those firm in knowledge among them and the believers believe in what has been revealed to you, [O Muḥammad], and what was revealed before you. And the establishers of prayer [especially] and the givers of zakāh and the believers in Allāh and the Last Day - those We will give a great reward.", TextGerman = "Aber diejenigen unter ihnen, die im Wissen fest gegründet sind, und die Gläubigen glauben an das, was zu dir (an Offenbarung) herabgesandt worden ist, und was vor dir herabgesandt wurde, und diejenigen, die das Gebet verrichten und die Abgabe entrichten und an Allah und den Jüngsten Tag glauben. Ihnen werden Wir großartigen Lohn geben." });
                        surah4.VerseList.Add(new Verse { Number = 163, TextBosnian = "Mi objavljujemo tebi kao što smo objavljivali Nuhu i vjerovjesnicima poslije njega, objavljivali smo Ibrahimu, i Ismailu i Ishaku i Jakubu i potomcima, i Isau, i Ejjubu, i Junusu, i Harunu, i Sulejmanu, a Davudu smo dali Zebur,", TextEnglish = "Indeed, We have revealed to you, [O Muḥammad], as We revealed to Noah and the prophets after him. And We revealed to Abraham, Ishmael, Isaac, Jacob, the Descendants,[235] Jesus, Job, Jonah, Aaron, and Solomon, and to David We gave the book [of Psalms].", TextGerman = "Gewiß, Wir haben dir (Offenbarung) eingegeben, wie Wir Nuh und den Propheten nach ihm (Offenbarung) eingegeben haben. Und Wir haben Ibrahim, Isma’il, Ishaq, Ya’qub, den Stämmen, Isa, Ayyub, Yunus, Harun und Sulaiman (Offenbarung) eingegeben, und Dawud haben Wir ein Buch der Weisheit gegeben." });
                        surah4.VerseList.Add(new Verse { Number = 164, TextBosnian = "i poslali smo poslanike o kojima smo ti prije kazivali i poslanike o kojima ti nismo kazivali. A Allah je sigurno s Musaom razgovarao.", TextEnglish = "And [We sent] messengers about whom We have related [their stories] to you before and messengers about whom We have not related to you. And Allāh spoke to Moses with [direct] speech.", TextGerman = "Und (Wir sandten) Gesandte, über die Wir dir schon zuvor berichtet haben, und andere, über die Wir dir nicht berichtet haben, und zu Musa hat Allah unmittelbar gesprochen -," });
                        surah4.VerseList.Add(new Verse { Number = 165, TextBosnian = "Poslali smo poslanike koji su radosne vijesti i opomene donosili, da ljudi poslije poslanika ne bi nikakvog opravdanja pred Allahom imali. A Allah je silan i mudar.", TextEnglish = "[We sent] messengers as bringers of good tidings and warners so that mankind will have no argument against Allāh after the messengers. And ever is Allāh Exalted in Might and Wise.", TextGerman = "Gesandte als Verkünder froher Botschaft und als Überbringer von Warnungen, damit die Menschen nach den Gesandten kein Beweismittel gegen Allah haben. Und Allah ist Allmächtig und Allweise." });
                        surah4.VerseList.Add(new Verse { Number = 166, TextBosnian = "Allah svjedoči da je istina ono što ti objavljuje, objavljuje ga Svojim znanjem, a i meleci svjedoče. A dovoljan je Allah kao svjedok.", TextEnglish = "But Allāh bears witness to that which He has revealed to you. He has sent it down with His knowledge, and the angels bear witness [as well]. And sufficient is Allāh as Witness.", TextGerman = "Aber Allah bezeugt, was Er zu dir (als Offenbarung) herabgesandt hat; Er hat es mit Seinem Wissen herabgesandt. Und (auch) die Engel bezeugen es. Und Allah genügt als Zeuge." });
                        surah4.VerseList.Add(new Verse { Number = 167, TextBosnian = "Doista, oni koji ne vjeruju i koji druge od Allahova puta odvraćaju, daleko su zalutali.", TextEnglish = "Indeed, those who disbelieve and avert [people] from the way of Allāh have certainly gone far astray.", TextGerman = "Diejenigen, die ungläubig sind und von Allahs Weg abhalten, sind fürwahr weit abgeirrt." });
                        surah4.VerseList.Add(new Verse { Number = 168, TextBosnian = "Onima koji ne vjeruju i koji zulum čine, Allah, doista, neće oprostiti i neće ih na put uputiti,", TextEnglish = "Indeed, those who disbelieve and commit wrong [or injustice] - never will Allāh forgive them, nor will He guide them to a path,", TextGerman = "Für diejenigen, die ungläubig sind und Unrecht tun, ist es nicht Allahs (Wille), ihnen zu vergeben noch sie einen (rechten) Weg zu leiten," });
                        surah4.VerseList.Add(new Verse { Number = 169, TextBosnian = "osim puta u Džehennem, u kojem će vječno i zauvijek ostati; to je Allahu lahko.", TextEnglish = "Except the path of Hell; they will abide therein forever. And that, for Allāh, is [always] easy.", TextGerman = "außer den Weg zur Hölle, ewig und auf immer darin zu bleiben. Und dies ist Allah ein leichtes." });
                        surah4.VerseList.Add(new Verse { Number = 170, TextBosnian = "O ljudi, Poslanik vam je već donio Istinu od Gospodara vašeg; zato, vjerujte - bolje vam je! A ako ne budete vjerovali - pa zaista je Allahovo sve što je na nebesima i na Zemlji. Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "O mankind, the Messenger has come to you with the truth from your Lord, so believe; it is better for you. But if you disbelieve - then indeed, to Allāh belongs whatever is in the heavens and earth. And ever is Allāh Knowing and Wise.", TextGerman = "O ihr Menschen, der Gesandte ist nunmehr mit der Wahrheit von eurem Herrn zu euch gekommen, darum glaubt (an ihn), das ist besser für euch. Wenn ihr aber ungläubig seid, gewiß, so gehört Allah (alles), was in den Himmeln und was auf der Erde ist. Und Allah ist Allwissend und Allweise." });
                        surah4.VerseList.Add(new Verse { Number = 171, TextBosnian = "O vi kojima je data Knjiga, ne zastranjujte u svom vjerovanju i o Allahu govorite samo istinu! Mesih, Isa, sin Merjemin, samo je Allahov poslanik, i Njegova Riječ, koju je Merjemi dostavio, i duh Njegov; zato vjerujte u Allaha i Njegove poslanike i ne govorite: 'Trojica su!' Prestanite, bolje vam je! Allah je samo jedan Bog, Uzvišen neka je On, zar da ima dijete?! Njegovo je ono što je na nebesima i ono što je na Zemlji, i Allah je dovoljan kao zaštitnik.", TextEnglish = "O People of the Scripture, do not commit excess in your religion[236] or say about Allāh except the truth. The Messiah, Jesus the son of Mary, was but a messenger of Allāh and His word which He directed to Mary and a soul [created at a command] from Him. So believe in Allāh and His messengers. And do not say, 'Three'; desist - it is better for you. Indeed, Allāh is but one God. Exalted is He above having a son. To Him belongs whatever is in the heavens and whatever is on the earth. And sufficient is Allāh as Disposer of affairs.", TextGerman = "O Leute der Schrift, übertreibt nicht in eurer Religion und sagt gegen Allah nur die Wahrheit aus! al-Masih ’Isa, der Sohn Maryams, ist nur Allahs Gesandter und Sein Wort, das Er Maryam entbot, und Geist von Ihm. Darum glaubt an Allah und Seine Gesandten und sagt nicht Drei. Hört auf (damit), das ist besser für euch! Allah ist nur ein Einziger Gott. Preis sei Ihm (, und Erhaben ist Er darüber), daß Er ein Kind haben sollte! Ihm gehört (alles), was in den Himmeln und was auf der Erde ist, und Allah genügt als Sachwalter." });
                        surah4.VerseList.Add(new Verse { Number = 172, TextBosnian = "Mesih se neće ustezati da bude Allahov rob, pa ni meleki, Njemu bliski. A one koji se budu ustezali da Njemu ibadet čine, i one koji se budu oholili, Allah će pred Sobom sakupiti.", TextEnglish = "Never would the Messiah disdain to be a servant of Allāh, nor would the angels near [to Him]. And whoever disdains His worship and is arrogant - He will gather them to Himself all together.", TextGerman = "Al-Masih wird es nicht verschmähen, ein Diener Allahs zu sein, auch nicht die (Allah) nahegestellten Engel. Wer es aber verschmäht, Ihm zu dienen, und sich hochmütig verhält -, so wird Er sie alle zu Sich versammeln." });
                        surah4.VerseList.Add(new Verse { Number = 173, TextBosnian = "Vjernike koji su dobra djela činili On će prema zasluzi nagraditi i još će im, iz obilja Svoga, više dati; a one koji su zazorni bili i oholili se - na bolne će muke staviti i oni neće naći sebi, mimo Allaha, ni zaštitnika ni pomagača.", TextEnglish = "And as for those who believed and did righteous deeds, He will give them in full their rewards and grant them extra from His bounty. But as for those who disdained and were arrogant, He will punish them with a painful punishment, and they will not find for themselves besides Allāh any protector or helper.", TextGerman = "Was nun diejenigen angeht, die glauben und rechtschaffene Werke tun, so wird Er ihnen ihren Lohn in vollem Maß zukommen lassen und ihnen von Seiner Huld noch mehr erweisen. Was aber diejenigen angeht, die es verschmähen und sich hochmütig verhalten, so wird Er sie mit schmerzhafter Strafe strafen. Und sie werden für sich außer Allah weder Schutzherrn noch Helfer finden." });
                        surah4.VerseList.Add(new Verse { Number = 174, TextBosnian = "O ljudi, dokaz vam je već stigao od Gospodara vašeg i Mi smo vam objavili Jasno Svjetlo.", TextEnglish = "O mankind, there has come to you a conclusive proof from your Lord, and We have sent down to you a clear light.[237]", TextGerman = "O ihr Menschen, zu euch ist nunmehr ein Beweis von eurem Herrn gekommen, und Wir haben zu euch ein deutliches Licht hinabgesandt." });
                        surah4.VerseList.Add(new Verse { Number = 175, TextBosnian = "One koji budu u Allaha vjerovali i čvrsto Ga se držali On će sigurno u milost Svoju i blagodat uvesti i Pravim putem Sebi uputiti.", TextEnglish = "So those who believe in Allāh and hold fast to Him - He will admit them to mercy from Himself and bounty and guide them to Himself on a straight path.", TextGerman = "Was nun diejenigen angeht, die an Allah glauben und an Ihm festhalten, so wird Er sie in Barmherzigkeit von Ihm und Huld eingehen lassen und sie zu Sich auf einem geraden Weg leiten." });
                        surah4.VerseList.Add(new Verse { Number = 176, TextBosnian = "Oni traže od tebe tumačenje. Reci: 'Allah će vam kazati propis o onome ko nema ni roditelja ni djece: ako neko umre i ne bude imao djeteta, a ima sestru, njoj polovina njegove ostavštine pripada, a on će naslijediti nju ako ona ne bude imala dijete; a ako su dvije sestre, njima pripadaju dvije trećine njegove ostavštine. A ako su oni braća i sestre, onda će muški dio nasljedstva biti jednak iznosu dva dijela ženskog. To vam Allah objašnjava, da ne zalutate. A Allah sve dobro zna.'", TextEnglish = "They request from you a [legal] ruling. Say, 'Allāh gives you a ruling concerning one having neither descendants nor ascendants [as heirs].' If a man dies, leaving no child but [only] a sister, she will have half of what he left. And he inherits from her if she [dies and] has no child. But if there are two sisters [or more], they will have two thirds of what he left. If there are both brothers and sisters, the male will have the share of two females. Allāh makes clear to you [His law], lest you go astray. And Allāh is Knowing of all things.", TextGerman = "Sie fragen dich um Belehrung. Sag: Allah belehrt euch über den Erbanteil seitlicher Verwandtschaft. Wenn ein Mann umkommt, der keine Kinder hat, aber eine Schwester, dann steht ihr die Hälfte dessen zu, was er hinterläßt. Und er beerbt sie, wenn sie keine Kinder hat. Und wenn es zwei (Schwestern) sind, stehen ihnen (beiden) zwei Drittel dessen zu, was er hinterläßt. Und wenn es Geschwister sind, Männer und Frauen, dann kommt einem männlichen Geschlechts ebensoviel zu wie der Anteil von zwei weiblichen Geschlechts. Allah gibt euch Klarheit, damit ihr (nicht) in die Irre geht. Allah weiß über alles Bescheid." });
                        result = surah4;
                        break;
                    #endregion

                    // 
                    #region 5 Al-Maidah
                    case 5:
                        Surah surah5 = new Surah(); surah5.Name = Constants.Surah5Name; surah5.Number = 5; surah5.VerseList = new List<Verse>();
                        surah5.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah5.VerseList.Add(new Verse { Number = 1, TextBosnian = "O vi koji vjerujete, ispunjavajte preuzete obaveze! Dozvoljava vam se stoka, ali ne ona koja će vam biti spomenuta; dok obrede hadža obavljate nije vam dozvoljeno da lovite. Uistinu, Allah propisuje što On hoće.", TextEnglish = "O you who have believed, fulfill [all] contracts.[239] Lawful for you are the animals of grazing livestock except for that which is recited to you [in this Qur’ān] - hunting not being permitted while you are in the state of iḥrām.[240] Indeed, Allāh ordains what He intends.", TextGerman = "O die ihr glaubt, haltet die Abmachungen! Erlaubt (zu essen) sind euch die Vierfüßler unter dem Vieh, außer dem, was euch verlesen wird, ohne daß ihr jedoch das Jagdwild als erlaubt betrachtet, während ihr im Zustand der Pilgerweihe seid. Allah ordnet an, was Er will." });
                        surah5.VerseList.Add(new Verse { Number = 2, TextBosnian = "O vi koji vjerujete, ne skrnavite Allahove obrede hadža, ni sveti mjesec, ni kurbane, naročito one ogrlicama obilježene, ni one ljude koji su krenuli ka časnoj Kabi želeći od Gospodara svoga obilje i zadovoljstvo. A kada obrede hadža obavite, onda loviti možete. I neka vas mržnja koju prema nekim ljudima nosite, zato što su vam spriječili pristup Časnoj džamiji, nikako ne navede da pravdu skrnavite! Potpomažite se u dobročinstvu i bogobojaznosti, a ne potpomažite se u grijehu i skrnavljenju pravde i bojte se Allaha; Allah, doista, strašno kažnjava.", TextEnglish = "O you who have believed, do not violate the rites of Allāh or [the sanctity of] the sacred month or [neglect the marking of] the sacrificial animals and garlanding [them] or [violate the safety of] those coming to the Sacred House seeking bounty from their Lord and [His] approval. But when you come out of iḥrām, then [you may] hunt. And do not let the hatred of a people for having obstructed you from al-Masjid al-Ḥarām lead you to transgress. And cooperate in righteousness and piety, but do not cooperate in sin and aggression. And fear Allāh; indeed, Allāh is severe in penalty.", TextGerman = "O die ihr glaubt, entweiht nicht die Kultzeichen Allahs, noch den Schutzmonat, noch die Opfertiere, noch die Halsgehänge, noch die, die das geschützte Haus aufsuchen, indem sie nach Huld von ihrem Herrn trachten und nach Wohlgefallen. - Wenn ihr den Weihezustand abgelegt habt, dann dürft ihr jagen. - Und der Haß, den ihr gegen (bestimmte) Leute hegt, weil sie euch von der geschützten Gebetsstätte abgehalten haben, soll euch ja nicht dazu bringen zu übertreten. Helft einander zur Güte und Gottesfurcht, aber helft einander nicht zur Sünde und feindseligem Vorgehen, und fürchtet Allah! Allah ist streng im Bestrafen." });
                        surah5.VerseList.Add(new Verse { Number = 3, TextBosnian = "Zabranjena vam je strv, i krv, i svinjsko meso, i ona životinja koja je zaklana u nečije drugo, a ne u Allahovo ime, i koja je udavljena i ubijena i koja je strmoglavljena, i rogom ubodena, ili od zvijeri načeta - osim ako ste je preklali - i koja je na žtrvenicima žrtvovana i zabranjuje vam se gatanje strelicama. To je neposluh! Danas su nevjernici izgubili svaku nadu da ćete vi otpasti od svoje vjere, zato se ne bojte njih, već se bojte Mene. Danas sam vam vjeru vašu usavršio i blagodat Svoju prema vama upotpunio i zadovoljan sam da vam islam bude vjera. A ko god bude primoran, kada hara glad, bez namjere da učini grijeh, pa Allah je, zaista, Oprostitelj grijeha, Milostivi.", TextEnglish = "Prohibited to you are dead animals,[241] blood, the flesh of swine, and that which has been dedicated to other than Allāh, and [those animals] killed by strangling or by a violent blow or by a head-long fall or by the goring of horns, and those from which a wild animal has eaten, except what you [are able to] slaughter [before its death], and those which are sacrificed on stone altars,[242] and [prohibited is] that you seek decision through divining arrows. That is grave disobedience. This day those who disbelieve have despaired of [defeating] your religion; so fear them not, but fear Me. This day I have perfected for you your religion and completed My favor upon you and have approved for you Islām as religion. But whoever is forced by severe hunger with no inclination to sin - then indeed, Allāh is Forgiving and Merciful.", TextGerman = "Verboten ist euch (der Genuß von) Verendetem, Blut, Schweinefleisch und dem, worüber ein anderer (Name) als Allah(s) angerufen worden ist, und (der Genuß von) Ersticktem, Erschlagenem, zu Tode Gestürztem oder Gestoßenem, und was von einem wilden Tier gerissen worden ist - außer dem, was ihr schlachtet - und (verboten ist euch,) was auf einem Opferstein geschlachtet worden ist, und mit Pfeilen zu losen. Das ist Frevel. - Heute haben diejenigen, die ungläubig sind, hinsichtlich eurer Religion die Hoffnung aufgegeben. So fürchtet nicht sie, sondern fürchtet Mich! Heute habe Ich euch eure Religion vervollkommnet und Meine Gunst an euch vollendet, und Ich bin mit dem Islam als Religion für euch zufrieden. - Und wer sich aus Hunger in einer Zwangslage befindet, ohne zu einer Sünde hinzuneigen, so ist Allah Allvergebend und Barmherzig." });
                        surah5.VerseList.Add(new Verse { Number = 4, TextBosnian = "Pitaju te šta im je dozvoljeno. Reci: 'Dozvoljena su vam lijepa jela i ono što ulove životinje koje ste lovu podučili, od onoga čime je Allah vas naučio. Jedite ono što vam one uhvate i spomenite Allahovo ime pri tome, i bojte se Allaha; Allah, zaista, brzo sviđa račune.", TextEnglish = "They ask you, [O Muḥammad], what has been made lawful for them. Say, 'Lawful for you are [all] good foods and [game caught by] what you have trained of hunting animals[243] which you train as Allāh has taught you. So eat of what they catch for you, and mention the name of Allāh upon it, and fear Allāh.' Indeed, Allāh is swift in account.", TextGerman = "Sie fragen dich, was ihnen erlaubt ist. Sag: Erlaubt sind euch die guten Dinge. Und wenn ihr beutegreifende Tiere durch Abrichtung von dem gelehrt habt, was Allah euch gelehrt hat, dann eßt von dem, was sie für euch fassen, und sprecht den Namen Allahs darüber aus. Und fürchtet Allah. Allah ist schnell im Abrechnen." });
                        surah5.VerseList.Add(new Verse { Number = 5, TextBosnian = "Danas vam se dozvoljavaju sva lijepa jela; i jela onih kojima je data Knjiga dozvoljena su vam, i vaša su jela njima dozvoljena, i čestite žene vjernice, i čestite kćeri onih kojima je data Knjiga prije vas, kada im vjenčane darove njihove dadnete s namjerom da se njima oženite, a ne da s njima blud činite i da ih za prilježnice uzimate. A onaj ko niječe propise vjere- uzalud će mu biti djela njegova, i on će, na ahiretu, među gubitnicima biti.", TextEnglish = "This day [all] good foods have been made lawful, and the food of those who were given the Scripture is lawful for you and your food is lawful for them. And [lawful in marriage are] chaste women from among the believers and chaste women from among those who were given the Scripture before you, when you have given them their due compensation,[244] desiring chastity, not unlawful sexual intercourse or taking [secret] lovers. And whoever denies the faith - his work has become worthless, and he, in the Hereafter, will be among the losers.", TextGerman = "Heute sind euch die guten Dinge erlaubt. Und die Speise derjenigen, denen die Schrift gegeben wurde, ist euch erlaubt, und eure Speise ist ihnen erlaubt. Und die Ehrbaren von den gläubigen Frauen und die ehrbaren Frauen von denjenigen, denen vor euch die Schrift gegeben wurde, wenn ihr ihnen ihren Lohn gebt, als ehrbare Ehemänner, nicht als solche, die Hurerei treiben und sich Liebschaften halten. Wer den Glauben verleugnet, dessen Werk wird hinfällig, und im Jenseits gehört er zu den Verlierern." });
                        surah5.VerseList.Add(new Verse { Number = 6, TextBosnian = "O vi koji vjerujete, kada hoćete namaz obaviti, lica svoja i ruke svoje do iza laktova operite - a glave svoje potarite - i noge svoje do iza članaka operite. Ako ste džunupi, onda se očistite; a ako ste bolesni, ili na putu, ili ako ste izvršili prirodnu potrebu, ili ako ste imali odnos sa suprugama, a ne nađete vode, onda rukama svojim čistu zemlju dotaknite i njima preko lica svojih i ruku svojih pređite. Allah vam ne želi pričiniti poteškoće, već vas želi učiniti čistim i blagodat Svoju vam upotpuniti, da biste bili zahvalni.", TextEnglish = "O you who have believed, when you rise to [perform] prayer, wash your faces and your forearms to the elbows and wipe over your heads and wash your feet to the ankles. And if you are in a state of janābah,[245] then purify yourselves. But if you are ill or on a journey or one of you comes from the place of relieving himself or you have contacted women[246] and do not find water, then seek clean earth and wipe over your faces and hands with it. Allāh does not intend to make difficulty for you, but He intends to purify you and complete His favor upon you that you may be grateful.", TextGerman = "O die ihr glaubt, wenn ihr euch zum Gebet aufstellt, dann wascht euch das Gesicht und die Hände bis zu den Ellbogen und streicht euch über den Kopf und (wascht euch) die Füße bis zu den Knöcheln. Und wenn ihr im Zustand der Unreinheit seid, dann reinigt euch. Und wenn ihr krank seid oder auf einer Reise oder jemand von euch vom Abort kommt oder ihr Frauen berührt habt und dann kein Wasser findet, so wendet euch dem guten Erdboden zu und streicht euch damit über das Gesicht und die Hände. Allah will euch keine Bedrängnis auferlegen, sondern Er will euch reinigen und Seine Gunst an euch vollenden, auf daß ihr dankbar sein möget." });
                        surah5.VerseList.Add(new Verse { Number = 7, TextBosnian = "I sjetite se Allahove blagodati kojom vas je obasuo i zavjeta kojim vas je obavezao, kad ste rekli: 'Slušamo i pokoravamo se!' I bojte se Allaha! Zaista Allah dobro zna sve što je u grudima.", TextEnglish = "And remember the favor of Allāh upon you and His covenant with which He bound you when you said, 'We hear and we obey'; and fear Allāh. Indeed, Allāh is Knowing of that within the breasts.", TextGerman = "Und gedenkt Allahs Gunst an euch und des Abkommens, das Er mit euch getroffen hat, als ihr sagtet: Wir hören und gehorchen! Und fürchtet Allah. Gewiß, Allah weiß über das Innerste der Brüste Bescheid." });
                        surah5.VerseList.Add(new Verse { Number = 8, TextBosnian = "O vi koji vjerujete, dužnosti prema Allahu izvršavajte, i pravedno svjedočite. Neka vas mržnja koju prema nekim ljudima nosite nikako ne navede da nepravedni budete! Pravedni budite, to je najbliže bogobojaznosti, i bojte se Allaha. Allah, doista, upotpunosti zna ono što činite.", TextEnglish = "O you who have believed, be persistently standing firm for Allāh, witnesses in justice, and do not let the hatred of a people prevent you from being just. Be just; that is nearer to righteousness. And fear Allāh; indeed, Allāh is [fully] Aware of what you do.", TextGerman = "O die ihr glaubt, seid Wahrer (der Sache) Allahs als Zeugen für die Gerechtigkeit. Und der Haß, den ihr gegen (bestimmte) Leute hegt, soll euch ja nicht dazu bringen, daß ihr nicht gerecht handelt. Handelt gerecht. Das kommt der Gottesfurcht näher. Und fürchtet Allah. Gewiß, Allah ist Kundig dessen, was ihr tut." });
                        surah5.VerseList.Add(new Verse { Number = 9, TextBosnian = "Allah je onima koji vjeruju i dobra djela čine obećao oprost i nagradu veliku;", TextEnglish = "Allāh has promised those who believe and do righteous deeds [that] for them there is forgiveness and great reward.", TextGerman = "Allah hat denen, die glauben und rechtschaffene Werke tun, versprochen, daß es für sie Vergebung und großartigen Lohn geben wird." });
                        surah5.VerseList.Add(new Verse { Number = 10, TextBosnian = "a oni koji ne budu vjerovali i ajete i znakove Naše budu poricali - bit će stanovnici Džehennema.", TextEnglish = "But those who disbelieve and deny Our signs - those are the companions of Hellfire.", TextGerman = "Diejenigen aber, die ungläubig sind und Unsere Zeichen für Lüge erklären, das sind Insassen des Höllenbrandes." });
                        surah5.VerseList.Add(new Verse { Number = 11, TextBosnian = "O vi koji vjerujete, sjetite se Allahove blagodati prema vama kada su vas se neki ljudi htjeli dočepati, a On je zadržao ruke njihove. I bojte se Allaha, i neka se vjernici samo u Allaha pouzdaju!", TextEnglish = "O you who have believed, remember the favor of Allāh upon you when a people determined to extend their hands [in aggression] against you, but He withheld their hands from you; and fear Allāh. And upon Allāh let the believers rely.", TextGerman = "O ihr, die ihr glaubt, gedenkt Allahs Gunst an euch, (damals) als (gewisse) Leute vorhatten, ihre Hände nach euch auszustrecken, worauf Er ihre Hände von euch zurückhielt. Und fürchtet Allah. Und auf Allah sollen sich die Gläubigen verlassen." });
                        surah5.VerseList.Add(new Verse { Number = 12, TextBosnian = "Allah je prihvatio zavjet sinova lsrailovih - a između njih bili smo postavili dvanaest starješina - i Allah je rekao: 'Ja sam, doista, s vama! Ako budete namaz obavljali i zekat davali, i ako budete u poslanike Moje vjerovali, pomagali im i drage volje zajam Allahu davali, sigurno ću prekriti vaša loša djela i uvest ću vas u džennetske bašče kroz koje rijeke teku. A onaj među vama koji ni poslije toga ne bude vjerovao - s Puta pravoga je skrenuo.'", TextEnglish = "And Allāh had already taken a covenant from the Children of Israel, and We delegated from among them twelve leaders. And Allāh said, 'I am with you. If you establish prayer and give zakāh and believe in My messengers and support them and loan Allāh a goodly loan,[247] I will surely remove from you your misdeeds and admit you to gardens beneath which rivers flow. But whoever of you disbelieves after that has certainly strayed from the soundness of the way.'", TextGerman = "Allah hatte ja mit den Kinder Isra’ils ein Abkommen getroffen. Und Wir beriefen von ihnen zwölf Obmänner. Und Allah sagte: Ich bin mit euch. Wenn ihr das Gebet verrichtet, die Abgabe entrichtet, an Meine Gesandten glaubt und ihnen beisteht und Allah ein gutes Darlehen gebt, werde Ich euch ganz gewiß eure bösen Taten tilgen und euch ganz gewiß in Gärten eingehen lassen, durcheilt von Bächen. Wer aber von euch danach ungläubig wird, der ist wirklich vom rechten Weg abgeirrt." });
                        surah5.VerseList.Add(new Verse { Number = 13, TextBosnian = "Ali, zato što su zavjet svoj prekršili, Mi smo ih prokleli i srca njihova okrutnim učinili. Oni su riječi s mjesta na kojima su bile uklanjali, a dobar dio onoga čime su bili opominjani izostavili. I ti ćeš, osim u slučaju malo njih, neprestano njihovo vjerolomstvo otkrivati, ali ti pređi preko toga i oprosti im! Allah, uistinu, voli one koji čine dobro.", TextEnglish = "So for their breaking of the covenant We cursed them and made their hearts hardened. They distort words from their [proper] places [i.e., usages] and have forgotten a portion of that of which they were reminded.[248] And you will still observe deceit among them, except a few of them. But pardon them and overlook [their misdeeds]. Indeed, Allāh loves the doers of good.", TextGerman = "Dafür, daß sie ihr Abkommen brachen, haben Wir sie verflucht und ihre Herzen hart gemacht. Sie verdrehen den Sinn der Worte, und sie haben einen Teil von dem vergessen, womit sie ermahnt worden waren. Und du wirst immer wieder Verrat von ihnen erfahren - bis auf wenige von ihnen. Aber verzeihe ihnen und übe Nachsicht. Gewiß, Allah liebt die Gutes Tuenden." });
                        surah5.VerseList.Add(new Verse { Number = 14, TextBosnian = "Od onih koji govore: 'Mi smo kršćani', Mi smo zavjet prihvatili, ali su i oni dio onoga čime su bili opominjani izostavili, zato smo medu njih neprijateljstvo i mržnju do Kijametskog dana ubacili; a Allah će ih sigurno obavijestiti o onome što su radili.", TextEnglish = "And from those who say, 'We are Christians' We took their covenant; but they forgot a portion of that of which they were reminded.[249] So We caused among them[250] animosity and hatred until the Day of Resurrection. And Allāh is going to inform them about what they used to do.", TextGerman = "Und (auch) mit denen, die sagen: Wir sind Christen’ haben Wir ihr Abkommen getroffen. Aber dann vergaßen sie einen Teil von dem, womit sie ermahnt worden waren. So erregten Wir unter ihnen Feindschaft und Haß bis zum Tag der Auferstehung. Und Allah wird ihnen kundtun, was sie zu machen pflegten." });
                        surah5.VerseList.Add(new Verse { Number = 15, TextBosnian = "O vi kojima je data Knjiga, došao vam je Poslanik Naš da vam ukaže na mnogo šta što vi iz Knjige krijete, a preko mnogo čega će i preći. A od Allaha vam dolazi Svjetlost i Knjiga jasna.", TextEnglish = "O People of the Scripture, there has come to you Our Messenger making clear to you much of what you used to conceal of the Scripture and overlooking much.[251] There has come to you from Allāh a light and a clear Book [i.e., the Qur’ān]", TextGerman = "O Leute der Schrift, Unser Gesandter ist nunmehr zu euch gekommen, um euch vieles von dem klarzumachen, was ihr von der Schrift verborgengehalten habt, und er vieles verzeiht. Gekommen ist, nunmehr zu euch von Allah ein Licht und ein deutliches Buch," });
                        surah5.VerseList.Add(new Verse { Number = 16, TextBosnian = "Njome Allah upućuje na puteve spasa one koji nastoje steći zadovoljstvo Njegovo i izvodi ih, dozvolom Svojom, iz tmina na svjetlo i na pravi put ih upućuje.", TextEnglish = "By which Allāh guides those who pursue His pleasure to the ways of peace[252] and brings them out from darknesses into the light, by His permission, and guides them to a straight path.", TextGerman = "mit dem Allah diejenigen, die Seinem Wohlgefallen folgen, die Wege des Heils leitet und sie aus den Finsternissen hinaus mit Seiner Erlaubnis ins Licht bringt und sie zu einem geraden Weg leitet." });
                        surah5.VerseList.Add(new Verse { Number = 17, TextBosnian = "Nevjernici su oni koji govore: 'Doista je Bog Mesih sin Merjemin!' Reci: 'Ko može spriječiti Allaha da, ako hoće, uništi Mesiha, sina Merjemina, i majku njegovu, i sve one koji su na Zemlji?' Allahova je vlast na nebesima i na Zemlji i na onome što je između njih; On stvara šta hoće, i Allah nad svime ima moć.", TextEnglish = "They have certainly disbelieved who say that Allāh is Christ, the son of Mary. Say, 'Then who could prevent Allāh at all if He had intended to destroy Christ, the son of Mary, or his mother or everyone on the earth?' And to Allāh belongs the dominion of the heavens and the earth and whatever is between them. He creates what He wills, and Allāh is over all things competent.", TextGerman = "Ungläubig sind ja diejenigen, die sagen: Allah ist ja al-Masih, der Sohn Maryams. Sag: Wer vermag denn gegen Allah etwas (auszurichten), wenn Er al-Masih, den Sohn Maryams, seine Mutter und all diejenigen, die auf der Erde sind, vernichten will? Allah gehört die Herrschaft der Himmel und der Erde und dessen, was dazwischen ist. Er erschafft, was Er will. Und Allah hat zu allem die Macht." });
                        surah5.VerseList.Add(new Verse { Number = 18, TextBosnian = "Jevreji i kršćani kažu: 'Mi smo djeca Božija i miljenici Njegovi.' Reci: 'Pa zašto vas On kažnjava zbog grijeha vaših?' A nije tako! Vi ste kao i ostali ljudi koje On stvara: kome hoće On će oprostiti, a koga hoće On će patnji podvrgnuti. Allahova je vlast na nebesima i na Zemlji i na onome što je između njih, i kod Njega je konačno odredište.", TextEnglish = "But the Jews and the Christians say, 'We are the children of Allāh and His beloved.' Say, 'Then why does He punish you for your sins?' Rather, you are human beings from among those He has created. He forgives whom He wills, and He punishes whom He wills. And to Allāh belongs the dominion of the heavens and the earth and whatever is between them, and to Him is the [final] destination.", TextGerman = "Die Juden und Christen sagen: Wir sind Allahs Söhne und Seine Lieblinge. Sag: Warum bestraft Er euch dann für eure Sünden? Nein! Vielmehr seid ihr menschliche Wesen von denen, die Er erschaffen hat. Er vergibt, wem Er will, und Er straft, wen Er will. Und Allah gehört die Herrschaft der Himmel und der Erde und dessen, was dazwischen ist, und zu Ihm ist der Ausgang." });
                        surah5.VerseList.Add(new Verse { Number = 19, TextBosnian = "O vi kojima je data Knjiga, došao vam je Poslanik Naš- nakon što je neko vrijeme prekinuto slanje poslanika - da vam objasni, da ne biste rekli: 'Nije nam dolazio ni onaj koji donosi radosne vijesti ni onaj koji upozorava!' Pa, došao vam je, eto, onaj koji donosi radosne vijesti i koji upozorava. A Allah nad svime ima moć.", TextEnglish = "O People of the Scripture, there has come to you Our Messenger to make clear to you[the religion] after a period[of suspension] of messengers, lest you say, 'There came not to us any bringer of good tidings or a warner.' But there has come to you a bringer of good tidings and a warner. And Allāh is over all things competent.", TextGerman = "O Leute der Schrift, Unser Gesandter ist nunmehr zu euch gekommen, um euch nach einer Unterbrechungszeit(in der Reihe) der Gesandten Klarheit zu geben, damit ihr(nicht etwa) sagt: Zu uns ist kein Frohbote und kein Warner gekommen.Nun ist ja ein Frohbote und ein Warner zu euch gekommen.Und Allah hat zu allem die Macht." });
                        surah5.VerseList.Add(new Verse { Number = 20, TextBosnian = "A kad Musa reče narodu svome: 'O narode moj, sjetite se Allahove blagodati prema vama kada je neke od vas vjerovjesnicima učinio, i kada vas je vladarima učinio, i dao vam ono što nijednom narodu nije dao.", TextEnglish = "And [mention, O Muḥammad], when Moses said to his people, 'O my people, remember the favor of Allāh upon you when He appointed among you prophets and made you possessors[253] and gave you that which He had not given anyone among the worlds.", TextGerman = "Und als Musa zu seinem Volk sagte: O mein Volk, gedenkt der Gunst Allahs an euch, als Er unter euch Propheten einsetzte und euch zu Königen machte und euch gab, was Er niemandem (anderen) der Weltenbewohner gegeben hat." });
                        surah5.VerseList.Add(new Verse { Number = 21, TextBosnian = "O narode moj, uđite u Blagoslovljenu zemlju koju vam je Allah dodijelio, i ne uzmičite nazad pa da se vratite kao gubitnici,", TextEnglish = "O my people, enter the blessed land [i.e., Palestine] which Allāh has assigned to you and do not turn back [from fighting in Allāh's cause] and [thus] become losers.'", TextGerman = "O mein Volk, tretet in das geheiligte Land ein, das Allah für euch bestimmt hat, und kehrt nicht den Rücken, denn dann werdet ihr als Verlierer zurückkehren." });
                        surah5.VerseList.Add(new Verse { Number = 22, TextBosnian = "oni rekoše: 'O Musa! U njoj je nemilosrdan narod i mi u nju nećemo ući dok god oni iz nje ne iziđu; pa, ako oni iz nje iziđu, mi ćemo onda sigurno ući.'", TextEnglish = "They said, 'O Moses, indeed within it is a people of tyrannical strength, and indeed, we will never enter it until they leave it; but if they leave it, then we will enter.'", TextGerman = "Sie sagten: O Musa, in ihm ist ein übermächtiges Volk. Wir werden es gewiß nicht betreten, bis sie aus ihm herausgehen. Wenn sie aus ihm herausgehen, dann wollen wir (es) gewiß betreten." });
                        surah5.VerseList.Add(new Verse { Number = 23, TextBosnian = "Dva čovjeka-koja su se Allaha bojala i kojima je On darovao blagodat Svoju- rekoše: 'Navalite na njihovu kapiju, pa kad kroz nju prođete, bit ćete sigurno pobjednici; a u Allaha se pouzdajte, ako ste vjernici.'", TextEnglish = "Said two men from those who feared [to disobey] upon whom Allāh had bestowed favor, 'Enter upon them through the gate, for when you have entered it, you will be predominant.[254] And upon Allāh rely, if you should be believers.'", TextGerman = "Zwei Männer von denen, die (Allah) fürchteten und denen Allah Gunst erwiesen hatte, sagten: Tretet gegen sie durch das Tor ein; wenn ihr dadurch eintretet, dann werdet ihr Sieger sein. Und verlaßt euch auf Allah, wenn ihr gläubig seid." });
                        surah5.VerseList.Add(new Verse { Number = 24, TextBosnian = "O Musa', rekoše oni, 'dok god su oni u njoj, mi nećemo u nju ulaziti! Idite ti i Gospodar tvoj pa se borite, a mi se odavde nećemo micati!'", TextEnglish = "They said, 'O Moses, indeed we will not enter it, ever, as long as they are within it; so go, you and your Lord, and fight. Indeed, we are remaining right here.'", TextGerman = "Sie (, die anderen,) sagten: O Musa, gewiß werden wir es niemals betreten, solange sie darin sind. Geh doch du und dein Herr hin und kämpft! Wir werden hier sitzen bleiben." });
                        surah5.VerseList.Add(new Verse { Number = 25, TextBosnian = "Gospodaru moj', reče Musa, 'ja osim sebe imam moć samo nad bratom svojim; zato razluči između nas i buntovnih ljudi!'", TextEnglish = "[Moses] said, 'My Lord, indeed I do not possess [i.e., control] except myself and my brother, so part us[255] from the defiantly disobedient people.'", TextGerman = "Er sagte: Mein Herr, ich habe ja nur Macht über mich selbst und meinen Bruder. Darum (unter)scheide uns von dem Volk der Frevler." });
                        surah5.VerseList.Add(new Verse { Number = 26, TextBosnian = "Četrdeset godina oni će zemljom lutati', reče On, 'jer će im Sveta zemlja zabranjena biti, a ti ne tuguj za narodom buntovnim.'", TextEnglish = "[Allāh] said, 'Then indeed, it is forbidden to them for forty years [in which] they will wander throughout the land. So do not grieve over the defiantly disobedient people.'", TextGerman = "Er (Allah) sagte: Gewiß, so soll es ihnen denn für vierzig Jahre verwehrt sein, (während derer) sie auf der Erde umherirren. So sei nicht betrübt über das Volk der Frevler!" });
                        surah5.VerseList.Add(new Verse { Number = 27, TextBosnian = "I ispričaj im vijest o dvojici Ademovih sinova, onako kako je bilo, kad su njih dvojica kurban žrtvovali, pa je od jednog bio primljen a od drugog nije, pa je on rekao: 'Sigurno ću te ubiti!' A prvi reče: 'Allah prima samo od onih koji su bogobojazni.", TextEnglish = "And recite to them the story of Adam's two sons, in truth, when they both made an offering [to Allāh], and it was accepted from one of them but was not accepted from the other. Said [the latter], 'I will surely kill you.' Said [the former], 'Indeed, Allāh only accepts from the righteous [who fear Him].", TextGerman = "Und verlies ihnen die Kunde von den beiden Söhnen Adams, der Wahrheit entsprechend, als sie ein Opfer darbrachten. Da wurde es von dem einen von ihnen angenommen, während es vom anderen nicht angenommen wurde. Der sagte: Ich werde dich ganz gewiß töten. Der andere sagte: Allah nimmt nur von den Gottesfürchtigen an." });
                        surah5.VerseList.Add(new Verse { Number = 28, TextBosnian = "I kad bi ti pružio ruku svoju prema meni da me ubiješ, ja ne bih pružio svoju prema tebi da te ubijem. Zaista se ja bojim Allaha, Gospodara svjetova.", TextEnglish = "If you should raise your hand toward me to kill me - I shall not raise my hand toward you to kill you. Indeed, I fear Allāh, Lord of the worlds.", TextGerman = "Wenn du deine Hand nach mir ausstreckst, um mich zu töten, so werde ich meine Hand nicht nach dir ausstrecken, um dich zu töten. Ich fürchte Allah, den Herrn der Weltenbewohner." });
                        surah5.VerseList.Add(new Verse { Number = 29, TextBosnian = "Ja želim da ti poneseš i moj i svoj grijeh i da budeš stanovnik u Vatri. A ona je kazna za sve zulumćare.'", TextEnglish = "Indeed, I want you to obtain [thereby] my sin and your sin so you will be among the companions of the Fire. And that is the recompense of wrongdoers.'", TextGerman = "Ich will, daß du meine und deine Sünde auf dich lädst und so einer von den Insassen des (Höllen)feuers sein wirst. Das ist der Lohn der Ungerechten." });
                        surah5.VerseList.Add(new Verse { Number = 30, TextBosnian = "I duša ga navede da ubije brata svoga, pa ga on ubi i postade jedan od gubitnika.", TextEnglish = "And his soul permitted to him[256] the murder of his brother, so he killed him and became among the losers.", TextGerman = "Doch machte ihn seine Seele willig, seinen Bruder zu töten. Und so tötete er ihn. Und er wurde einer von den Verlierern." });
                        surah5.VerseList.Add(new Verse { Number = 31, TextBosnian = "Allah onda posla jednog gavrana da kopa po zemlji da bi mu pokazao kako da zakopa mrtvo tijelo brata svoga. 'Teško meni', povika on, 'zar i ja ne mogu, kao ovaj gavran, zakopati mrtvo tijelo brata svoga!' I pokajnik postade.", TextEnglish = "Then Allāh sent a crow searching [i.e., scratching] in the ground to show him how to hide the disgrace[257] of his brother. He said, 'O woe to me! Have I failed to be like this crow and hide the disgrace [i.e., body] of my brother?' And he became of the regretful.", TextGerman = "Da schickte Allah einen Raben, der in der Erde scharrte, um ihm zu zeigen, wie er die böse Tat an seinem Bruder verbergen könne. Er sagte: O wehe mir! War ich unfähig, zu sein wie dieser Rabe und die böse Tat an meinem Bruder zu verbergen? So wurde er von denjenigen, die bereuen." });
                        surah5.VerseList.Add(new Verse { Number = 32, TextBosnian = "Zbog toga smo Mi propisali sinovima Israilovim: ako neko ubije nekoga koji nije ubio nikoga, ili onoga koji na Zemlji nered ne čini - kao da je sve ljude poubijao; a ako neko bude uzrok da se nečiji život sačuva - kao da je svim ljudima život sačuvao. Naši su im poslanici jasne dokaze donosili, ali su mnogi od njih, i poslije toga, zaista, na Zemlji sve granice zla prelazili.", TextEnglish = "Because of that, We decreed upon the Children of Israel that whoever kills a soul unless for a soul[258] or for corruption [done] in the land[259] - it is as if he had slain mankind entirely. And whoever saves one[260] - it is as if he had saved mankind entirely. And Our messengers had certainly come to them with clear proofs. Then indeed many of them, [even] after that, throughout the land, were transgressors.[261]", TextGerman = "Aus diesem Grunde haben Wir den Kindern Isra’ils vorgeschrieben: Wer ein menschliches Wesen tötet, ohne (daß es) einen Mord (begangen) oder auf der Erde Unheil gestiftet (hat), so ist es, als ob er alle Menschen getötet hätte. Und wer es am Leben erhält, so ist es, als ob er alle Menschen am Leben erhält. Unsere Gesandten sind bereits mit klaren Beweisen zu ihnen gekommen. Danach aber sind viele von ihnen wahrlich maßlos auf der Erde geblieben." });
                        surah5.VerseList.Add(new Verse { Number = 33, TextBosnian = "Kazna za one koji protiv Allaha i Poslanika Njegova vojuju i koji nered na Zemlji čine jeste: da budu ubijeni, ili razapeti, ili da im se unakrst ruke i noge odsijeku, ili da iz zemlje budu prognani. To im je poniženje na dunjaluku, a na ahiretu čeka ih patnja velika,", TextEnglish = "Indeed, the penalty[262] for those who wage war[263] against Allāh and His Messenger and strive upon earth [to cause] corruption is none but that they be killed or crucified or that their hands and feet be cut off from opposite sides or that they be exiled from the land. That is for them a disgrace in this world; and for them in the Hereafter is a great punishment,", TextGerman = "Der Lohn derjenigen, die Krieg führen gegen Allah und Seinen Gesandten und sich bemühen, auf der Erde Unheil zu stiften, ist indessen (der), daß sie allesamt getötet oder gekreuzigt werden, oder daß ihnen Hände und Füße wechselseitig abgehackt werden, oder daß sie aus dem Land verbannt werden. Das ist für sie eine Schande im Diesseits, und im Jenseits gibt es für sie gewaltige Strafe," });
                        surah5.VerseList.Add(new Verse { Number = 34, TextBosnian = "ali ne i za one koji se pokaju prije nego što ih se domognete. I znajte da je Allah Onaj Koji opršta grijehe i milostiv je.", TextEnglish = "Except for those who return [repenting] before you overcome [i.e., apprehend] them. And know that Allāh is Forgiving and Merciful.", TextGerman = "außer denjenigen, die bereuen, bevor ihr Macht über sie habt. So wisset, daß Allah Allvergebend und Barmherzig ist." });
                        surah5.VerseList.Add(new Verse { Number = 35, TextBosnian = "O vi koji vjerujete, Allaha se bojte i tražite ono što će vas Njemu približiti, i na putu Njegovu se borite da biste uspjeli.", TextEnglish = "O you who have believed, fear Allāh and seek the means [of nearness] to Him and strive in His cause that you may succeed.", TextGerman = "O die ihr glaubt, fürchtet Allah und trachtet nach einem Mittel zu Ihm und müht euch auf Seinem Weg ab, auf daß es euch wohl ergehen möge!" });
                        surah5.VerseList.Add(new Verse { Number = 36, TextBosnian = "Zaista, kad bi sve ono što je na Zemlji bilo u posjedu nevjernika, i još toliko, i kad bi htjeli time da se otkupe od patnje na Kijametskom danu, ne bi im primljeno bilo. Njih čeka patnja bolna.", TextEnglish = "Indeed, those who disbelieve - if they should have all that is in the earth and the like of it with it by which to ransom themselves from the punishment of the Day of Resurrection, it will not be accepted from them, and for them is a painful punishment.", TextGerman = "Gewiß, wenn diejenigen, die ungläubig sind, alles hätten, was auf der Erde ist, und noch einmal das Gleiche dazu, um sich damit von der Strafe des Tages der Auferstehung loszukaufen, würde es nicht von ihnen angenommen werden, und für sie gibt es schmerzhafte Strafe." });
                        surah5.VerseList.Add(new Verse { Number = 37, TextBosnian = "Zaželjet će oni da iz Vatre iziđu, ali im iz nje izlaska neće biti, za njih će biti patnja neprestana.", TextEnglish = "They will wish to get out of the Fire, but never are they to emerge therefrom, and for them is an enduring punishment.", TextGerman = "Sie wollen aus dem (Höllen)feuer herauskommen, aber sie werden nicht aus ihm herauskommen, und für sie gibt es beständige Strafe." });
                        surah5.VerseList.Add(new Verse { Number = 38, TextBosnian = "Kradljivcu i kradljivici odsijecite ruke, neka im to bude kazna za ono što su učinili i opomena od Allaha! Allah je silan i mudar.", TextEnglish = "[As for] the thief, the male and the female, amputate their hands in recompense for what they earned [i.e., committed] as a deterrent [punishment] from Allāh. And Allāh is Exalted in Might and Wise.", TextGerman = "Der Dieb und die Diebin: trennt ihnen ihre Hände ab als Lohn für das, was sie begangen haben, und als ein warnendes Beispiel von Allah. Allah ist Allmächtig und Allweise." });
                        surah5.VerseList.Add(new Verse { Number = 39, TextBosnian = "A onome ko se poslije zuluma svoga pokaje i popravi se - Allah će sigurno oprostiti, Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "But whoever repents after his wrongdoing and reforms, indeed, Allāh will turn to him in forgiveness. Indeed, Allāh is Forgiving and Merciful.", TextGerman = "Wer aber bereut, nachdem er Unrecht getan hat, und es wieder gutmacht, so nimmt Allah seine Reue gewiß an. Allah ist Allvergebend und Barmherzig." });
                        surah5.VerseList.Add(new Verse { Number = 40, TextBosnian = "Ti sigurno znaš da samo Allah ima vlast na nebesima i na Zemlji. On patnji podvrgava onoga koga hoće, a prašta onome kome hoće. Allah nad svime ima moć.", TextEnglish = "Do you not know that to Allāh belongs the dominion of the heavens and the earth? He punishes whom He wills and forgives whom He wills, and Allāh is over all things competent.", TextGerman = "Weißt du nicht, daß Allah die Herrschaft über die Himmel und die Erde gehört? Er straft, wen Er will, und vergibt, wem Er will. Und Allah hat zu allem die Macht." });
                        surah5.VerseList.Add(new Verse { Number = 41, TextBosnian = "O Poslaniče, neka te ne zabrinjavaju oni što brzo nevjerovanje ispoljavaju, oni koji ustima svojim govore: 'Vjerujemo', a srcem ne vjeruju, i jevreji, koji izmišljotine mnogo slušaju i koji tuđe riječi rado prihvataju, a tebi ne dolaze; koji smisao riječima izvrću i govore: 'Ako vam se ovako presudi, onda pristanite na to, a ako vam se ne presudi, onda nemojte pristati! A onome kome Allah želi zabludu, ti mu, u odnosu na Allaha, ništa pomoći ne možeš. To su oni čija srca Allah ne želi očistiti; njih na dunjaluku čeka poniženje, a na ahiretu patnja golema.", TextEnglish = "O Messenger, let them not grieve you who hasten into disbelief of those who say, 'We believe' with their mouths, but their hearts believe not, and from among the Jews. [They are] avid listeners to falsehood, listening to another people who have not come to you.[264] They distort words beyond their [proper] places [i.e., usages], saying, 'If you are given this,[265] take it; but if you are not given it, then beware.' But he for whom Allāh intends fitnah[266] - never will you possess [power to do] for him a thing against Allāh. Those are the ones for whom Allāh does not intend to purify their hearts. For them in this world is disgrace, and for them in the Hereafter is a great punishment.", TextGerman = "O du Gesandter, lasse dich nicht durch jene traurig machen, die im Unglauben dahineilen, unter denen, die mit ihren Mündern sagen: Wir glauben, während ihre Herzen nicht glauben, Und unter denjenigen, die dem Judentum angehören, unter ihnen gibt es manche, die auf Lügen horchen, die auf andere Leute horchen, die nicht zu dir gekommen sind. Sie verdrehen den Sinn der Worte, nach(dem sie an) ihrer (richtigen) Stelle (waren), und sagen: Wenn euch dies gegeben wird, dann nehmt es an. Wenn euch dies aber nicht gegeben wird, dann seht euch vor. Wen Allah der Versuchung aussetzen will, für den wirst du gegen Allah nichts (auszurichten) vermögen. Das sind diejenigen, deren Herzen Allah nicht rein machen wollte. Schande gibt es für sie im Diesseits, und im Jenseits gibt es für sie gewaltige Strafe;" });
                        surah5.VerseList.Add(new Verse { Number = 42, TextBosnian = "Oni mnogo laži slušaju i mnogo ono što je zabranjeno jedu; pa ako ti dođu, ti im ili presudi ili se okreni od njih; ako se okreneš od njih, oni ti ne mogu nimalo nauditi. A ako im budeš sudio, sudi im pravo. Doista, Allah voli pravedne.", TextEnglish = "[They are] avid listeners to falsehood, devourers of [what is] unlawful. So if they come to you, [O Muḥammad], judge between them or turn away from them. And if you turn away from them - never will they harm you at all. And if you judge, judge between them with justice. Indeed, Allāh loves those who act justly.", TextGerman = "(sie), die auf Lügen horchen, und die darauf aus sind, unrechtmäßig Erworbenes zu verschlingen. Wenn sie zu dir kommen, so richte zwischen ihnen oder wende dich von ihnen ab! Wenn du dich von ihnen abwendest, werden sie dir keinen Schaden zufügen, wenn du aber richtest, dann richte zwischen ihnen in Gerechtigkeit. Gewiß, Allah liebt die Gerechten." });
                        surah5.VerseList.Add(new Verse { Number = 43, TextBosnian = "A otkud da oni traže od tebe da im sudiš kad imaju Tevrat, u kome su Allahovi propisi? Oni ni poslije presude tvoje ne bi bili zadovoljni, i oni vjernici nisu.", TextEnglish = "But how is it that they come to you for judgement while they have the Torah, in which is the judgement of Allāh? Then they turn away, [even] after that; but those are not [in fact] believers.", TextGerman = "Wie aber können sie dich richten lassen, während sie doch die Tora haben, in der das Urteil Allahs (enthalten) ist, und sich hierauf, nach alledem, abkehren? Diese sind doch keine Gläubigen." });
                        surah5.VerseList.Add(new Verse { Number = 44, TextBosnian = "Mi smo objavili Tevrat, u kome je uputa i svjetlo. Po njemu su jevrejima sudili vjerovjesnici koji su bili Allahu poslušni, i pobožni ljudi, i učeni, od kojih je traženo da čuvaju Allahovu knjigu, i oni su nad njom bdjeli. Zato se, kad budete sudili, ne bojte ljudi, već se bojte Mene, i ne zamjenjujte riječi Moje za nešto što malo vrijedi! A oni što ne sude prema onom što je Allah objavio, oni su baš nevjernici.", TextEnglish = "Indeed, We sent down the Torah, in which was guidance and light. The prophets who submitted [to Allāh] judged by it for the Jews, as did the rabbis and scholars by that with which they were entrusted of the Scripture of Allāh, and they were witnesses thereto. So do not fear the people but fear Me, and do not exchange My verses for a small price [i.e., worldly gain]. And whoever does not judge by what Allāh has revealed - then it is those who are the disbelievers.", TextGerman = "Gewiß, Wir haben die Tora hinabgesandt, in der Rechtleitung und Licht sind, womit die Propheten, die sich (Allah) ergeben hatten, für diejenigen, die dem Judentum angehören, walten, und so auch die Leute des Herrn und die Gelehrten, nach dem, was ihnen von der Schrift Allahs anvertraut worden war und worüber sie Zeugen waren. So fürchtet nicht die Menschen, sondern fürchtet Mich. Und verkauft Meine Zeichen nicht für einen geringen Preis! Wer nicht nach dem waltet, was Allah (als Offenbarung) herabgesandt hat, das sind die Ungläubigen." });
                        surah5.VerseList.Add(new Verse { Number = 45, TextBosnian = "Mi smo im u njemu propisali: glava za glavu, i oko za oko, i nos za nos, i uho za uho, i zub za zub, da za rane treba uzvratiti, te da onome ko od odmazde odustane, bit će mu to od grijeha iskupljenje. Oni koji ne sude prema onome što je Allah objavio baš su zulumćari.", TextEnglish = "And We ordained for them therein a life for a life, an eye for an eye, a nose for a nose, an ear for an ear, a tooth for a tooth, and for wounds is legal retribution. But whoever gives [up his right as] charity, it is an expiation for him. And whoever does not judge by what Allāh has revealed - then it is those who are the wrongdoers [i.e., the unjust].", TextGerman = "Und Wir haben ihnen darin vorgeschrieben: Leben um Leben, Auge um Auge, Nase um Nase, Ohr um Ohr, Zahn um Zahn; und (auch) für Verwundungen Wiedervergeltung. Wer es aber als Almosen erläßt, für den ist es eine Sühne. Wer nicht nach dem waltet, was Allah (als Offenbarung) herabgesandt hat, das sind die Ungerechten." });
                        surah5.VerseList.Add(new Verse { Number = 46, TextBosnian = "Poslije njih smo Isaa, sina Merjemina, poslali, koji je priznavao Tevrat prije njega objavljen, a njemu smo dali Indžil, u kome je bila uputa i svjetlo, i da potvrdi Tevrat, prije njega objavljen, u kome je, također, bila uputa i pouka bogobojaznima,", TextEnglish = "And We sent, following in their footsteps,[267] Jesus, the son of Mary, confirming that which came before him in the Torah; and We gave him the Gospel, in which was guidance and light and confirming that which preceded it of the Torah as guidance and instruction for the righteous.", TextGerman = "Und Wir ließen auf ihren Spuren ’Isa, den Sohn Maryams, folgen, das zu bestätigen, was von der Tora vor ihm (offenbart) war; und Wir gaben ihm das Evangelium, in dem Rechtleitung und Licht sind, und das zu bestätigen, was von der Tora vor ihm (offenbart) war, und als Rechtleitung und Ermahnung für die Gottesfürchtigen." });
                        surah5.VerseList.Add(new Verse { Number = 47, TextBosnian = "i sljedbenicima Indžila smo bili naredili da sude prema onome što je Allah objavio u njemu. Oni koji nisu sudili prema onome što je Allah objavio - baš su prema Allahu buntovni.", TextEnglish = "And let the People of the Gospel judge by what Allāh has revealed therein. And whoever does not judge by what Allāh has revealed - then it is those who are the defiantly disobedient.", TextGerman = "Und so sollen die Leute des Evangeliums nach dem walten, was Allah darin herabgesandt hat. Wer nicht nach dem waltet, was Allah (als Offenbarung) herabgesandt hat, das sind die Frevler." });
                        surah5.VerseList.Add(new Verse { Number = 48, TextBosnian = "A tebi objavljujemo Knjigu, s Istinom, da potvrdi knjige prije objavljene i da nad njima bdi. I ti im sudi prema onome što Allah objavljuje i ne povodi se za prohtjevima njihovim, i ne odstupaj od Istine koja ti dolazi; svima vama smo zakon i pravac propisali. A da je Allah htio, On bi vas sljedbenicima jedne vjere učinio, ali, On hoće da vas iskuša u onome što vam propisuje, zato se natječite ko će više dobra učiniti; Allahu ćete se svi vratiti, pa će vas On o onome u čemu ste se razilazili obavijestiti.", TextEnglish = "And We have revealed to you, [O Muḥammad], the Book [i.e., the Qur’ān] in truth, confirming that which preceded it of the Scripture and as a criterion over it. So judge between them by what Allāh has revealed and do not follow their inclinations away from what has come to you of the truth. To each of you We prescribed a law and a method.[268] Had Allāh willed, He would have made you one nation [united in religion], but [He intended] to test you in what He has given you; so race to [all that is] good.[269] To Allāh is your return all together, and He will [then] inform you concerning that over which you used to differ.", TextGerman = "Und Wir haben zu dir das Buch mit der Wahrheit hinabgesandt, das zu bestätigen, was von dem Buch vor ihm (offenbart) war, und als Wächter darüber. So richte zwischen ihnen nach dem, was Allah (als Offenbarung) herabgesandt hat, und folge nicht ihren Neigungen entgegen dem, was dir von der Wahrheit zugekommen ist. Für jeden von euch haben Wir ein Gesetz und einen deutlichen Weg festgelegt. Und wenn Allah wollte, hätte Er euch wahrlich zu einer einzigen Gemeinschaft gemacht. Aber (es ist so,) damit Er euch in dem, was Er euch gegeben hat? prüfe. So wetteifert nach den guten Dingen! Zu Allah wird euer aller Rückkehr sein, und dann wird Er euch kundtun, worüber ihr uneinig zu sein pflegtet." });
                        surah5.VerseList.Add(new Verse { Number = 49, TextBosnian = "I sudi im prema onome što Allah objavljuje i ne povodi se za prohtjevima njihovim, i čuvaj ih se da te ne odvrate od nečega što ti Allah objavljuje. A ako se okrenu, ti onda znaj da ih Allah želi zbog nekih grijeha njihovih kazniti. A mnogi ljudi su, zaista, buntovnici.", TextEnglish = "And judge, [O Muḥammad], between them by what Allāh has revealed and do not follow their inclinations and beware of them, lest they tempt you away from some of what Allāh has revealed to you. And if they turn away - then know that Allāh only intends to afflict them with some of their [own] sins. And indeed, many among the people are defiantly disobedient.", TextGerman = "Und so richte zwischen ihnen nach dem, was Allah (als Offenbarung) herabgesandt hat, und folge nicht ihren Neigungen, sondern sieh dich vor ihnen vor, daß sie dich nicht der Versuchung aussetzen (abzuweichen) von einem Teil dessen, was Allah zu dir (als Offenbarung) herabgesandt hat! Doch wenn sie sich abkehren, so wisse, daß Allah sie für einen Teil ihrer Sünden treffen will. Viele von den Menschen sind fürwahr Frevler." });
                        surah5.VerseList.Add(new Verse { Number = 50, TextBosnian = "Zar oni da traže da im se kao u pagansko doba sudi!? A ko je od Allaha bolji sudija narodu koji čvrsto vjeruje?!", TextEnglish = "Then is it the judgement of [the time of] ignorance they desire? But who is better than Allāh in judgement for a people who are certain [in faith].", TextGerman = "Begehren sie etwa das Urteil der Unwissenheit? Wer kann denn besser walten als Allah für Leute, die (in ihrem Glauben) überzeugt sind?" });
                        surah5.VerseList.Add(new Verse { Number = 51, TextBosnian = "O vi koji vjerujete, ne uzimajte za zaštitnike i prijatelje jevreje i kršćane! Oni su sami sebi zaštitnici i prijatelji! A njihov je i onaj među vama koji ih za zaštitnike i prijatelje prihvati; Allah, uistinu, neće uputiti na pravi put ljude zulumćare.", TextEnglish = "O you who have believed, do not take the Jews and the Christians as allies. They are [in fact] allies of one another. And whoever is an ally to them among you - then indeed, he is [one] of them. Indeed, Allāh guides not the wrongdoing people.", TextGerman = "O die ihr glaubt, nehmt nicht die Juden und die Christen zu Schutzherren! Sie sind einer des anderen Schutzherren. Und wer von euch sie zu Schutzherren nimmt, der gehört zu ihnen. Gewiß, Allah leitet das ungerechte Volk nicht recht." });
                        surah5.VerseList.Add(new Verse { Number = 52, TextBosnian = "Zato ti vidiš one čija su srca bolesna kako se žure da s njima prijateljstvo sklope, govoreći: 'Bojimo se da nas kakva nevolja ne zadesi.' A Allah će sigurno pobjedu ili nešto drugo od Sebe dati, pa će se oni, zbog onoga što su u dušama svojim krili, kajati,", TextEnglish = "So you see those in whose hearts is disease [i.e., hypocrisy] hastening into [association with] them, saying, 'We are afraid a misfortune may strike us.' But perhaps Allāh will bring conquest or a decision from Him, and they will become, over what they have been concealing within themselves, regretful.", TextGerman = "Und so siehst du diejenigen, in deren Herzen Krankheit ist, sich ihretwegen beeilen; sie sagen: Wir fürchten, daß uns eine Schicksalswendung treffen wird. Aber vielleicht wird Allah den entscheidenden Sieg oder eine Anordnung von Ihm herbeibringen. Dann werden sie über das, was sie in ihrem Innersten geheimgehalten haben, Reue empfinden." });
                        surah5.VerseList.Add(new Verse { Number = 53, TextBosnian = "a oni koji vjeruju reći će: 'Jesu li to oni koji su se zaklinjali Allahom, svojom najjačom zakletvom, da su, zaista, s vama?' Djela njihova bit će poništena, i oni će gubitnici biti.", TextEnglish = "And those who believe will say,[270] 'Are these the ones who swore by Allāh their strongest oaths that indeed they were with you?' Their deeds have become worthless, and they have become losers.", TextGerman = "Und diejenigen, die glauben, werden sagen: Sind dies diejenigen, die bei Allah ihren kräftigsten Eid schworen, daß sie wahrlich mit euch seien? Ihre Werke sind hinfallig, so daß sie (nun) Verlierer geworden sind." });
                        surah5.VerseList.Add(new Verse { Number = 54, TextBosnian = "O vi koji vjerujete, ako neko od vas od svoje vjere otpadne - pa, Allah će sigurno umjesto njih dovesti ljude koje On voli i koji Njega vole, prema vjernicima ponizne, a prema nevjernicima ponosite; oni će se na Allahovu putu boriti i neće se ničijeg prijekora bojati. To je Allahov dar koji On daje kome hoće - a Allah je neizmjerno darežljiv i Onaj Koji sve zna.", TextEnglish = "O you who have believed, whoever of you should revert from his religion - Allāh will bring forth [in place of them] a people He will love and who will love Him [who are] humble toward the believers, strong against the disbelievers; they strive in the cause of Allāh and do not fear the blame of a critic. That is the favor of Allāh; He bestows it upon whom He wills. And Allāh is all-Encompassing and Knowing.", TextGerman = "O die ihr glaubt, wer von euch sich von seiner Religion abkehrt -, so wird Allah Leute bringen, die Er liebt und die Ihn lieben, bescheiden gegenüber den Gläubigen, mächtig (auftretend) gegenüber den Ungläubigen, und die sich auf Allahs Weg abmühen und nicht den Tadel des Tadlers fürchten. Das ist Allahs Huld, die Er gewährt, wem Er will. Allah ist Allumfassend und Allwissend." });
                        surah5.VerseList.Add(new Verse { Number = 55, TextBosnian = "Vaši su zaštitnici samo Allah i Poslanik Njegov i vjernici koji ponizno namaz obavljaju i zekat daju.", TextEnglish = "Your ally is none but Allāh and [therefore] His Messenger and those who have believed - those who establish prayer and give zakāh, and they bow [in worship].", TextGerman = "Euer Schutzherr ist (allein) Allah und (auch) Sein Gesandter und diejenigen, die glauben, die das Gebet verrichten, die Abgabe entrichten und zu den sich Verbeugenden gehören." });
                        surah5.VerseList.Add(new Verse { Number = 56, TextBosnian = "Onaj ko za zaštitnika uzme Allaha i Poslanika Njegova i vjernike - pa, Allahova strana će, svakako, pobijediti.", TextEnglish = "And whoever is an ally of Allāh and His Messenger and those who have believed - indeed, the party of Allāh - they will be the predominant.", TextGerman = "Wer sich Allah zum Schutzherrn nimmt und (auch) Seinen Gesandten und diejenigen, die glauben, - gewiß, die Anhängerschaft Allahs wird der Sieger sein." });
                        surah5.VerseList.Add(new Verse { Number = 57, TextBosnian = "O vi koji vjerujete, ne uzimajte za zaštitnike i prijatelje one koji vašu vjeru za podsmijeh i zabavu uzimaju, bili to oni kojima je data Knjiga prije vas, ili bili nevjernici - i Allaha se bojte ako ste vjernici.", TextEnglish = "O you who have believed, take not those who have taken your religion in ridicule and amusement among the ones who were given the Scripture before you nor the disbelievers as allies. And fear Allāh, if you should [truly] be believers.", TextGerman = "O die ihr glaubt, nehmt nicht diejenigen, die sich über eure Religion lustig machen und sie zum Gegenstand des Spiels nehmen, von denjenigen, denen vor euch die Schrift gegeben wurde, und die Ungläubigen zu Schutzherren. Und fürchtet Allah, wenn ihr gläubig seid!" });
                        surah5.VerseList.Add(new Verse { Number = 58, TextBosnian = "I kad pozivate na namaz, i to za podsmijeh i zabavu uzimaju, zato što su oni ljudi koji ne razumiju.", TextEnglish = "And when you call to prayer, they take it in ridicule and amusement. That is because they are a people who do not use reason.", TextGerman = "Wenn ihr zum Gebet ruft, machen sie sich darüber lustig und nehmen es zum Gegenstand des Spiels. Dies, weil sie Leute sind, die nicht begreifen." });
                        surah5.VerseList.Add(new Verse { Number = 59, TextBosnian = "Reci: 'O vi kojima je data Knjiga, zar da nas osuđujete zato što vjerujemo u Allaha i u ono što nam se objavljuje, i u ono što je objavljeno prije - a većina vas ste prema Allahu buntovni?'", TextEnglish = "Say, 'O People of the Scripture, do you resent us except [for the fact] that we have believed in Allāh and what was revealed to us and what was revealed before and because most of you are defiantly disobedient?'", TextGerman = "Sag: O Leute der Schrift, grollt ihr uns nur (darum), daß wir an Allah glauben und an das, was zu uns (als Offenbarung) herabgesandt worden ist und was zuvor herabgesandt wurde, und daß die meisten von euch Frevler sind?" });
                        surah5.VerseList.Add(new Verse { Number = 60, TextBosnian = "Reci: 'Hoćete li da vam kažem koji su gori od njih i koje će Allah još teže kazniti? Oni koje je Allah prokleo i na koje se rasrdio i učinio od njih majmune, i svinje, i one koji su tagutu - šejtanu robovali - njih čeka najgore mjesto i oni su najdalje s Pravog puta odlutali.'", TextEnglish = "Say, 'Shall I inform you of [what is] worse than that[271] as penalty from Allāh? [It is that of] those whom Allāh has cursed and with whom He became angry and made of them apes and pigs and slaves of ṭāghūt.[272] Those are worse in position and further astray from the sound way.'", TextGerman = "Sag: Soll ich euch kundtun, was als Belohnung bei Allah (noch) schlechter ist? Diejenigen, die Allah verflucht hat und denen Er zürnt und aus denen Er Affen und Schweine gemacht hat und die falschen Göttern dienen. Diese befinden sich in einer (noch) schlechteren Lage und sind (noch) weiter vom rechten Weg abgeirrt." });
                        surah5.VerseList.Add(new Verse { Number = 61, TextBosnian = "A kada vam dolaze, oni govore: 'Vjerujemo!', ali oni dolaze kao nevjernici, a takvi i odlaze, a Allah dobro zna ono što oni kriju.", TextEnglish = "And when they come to you, they say, 'We believe.' But they have entered with disbelief [in their hearts], and they have certainly left with it. And Allāh is most knowing of what they were concealing.", TextGerman = "Wenn sie zu euch kommen, sagen sie: Wir glauben, wo sie schon mit Unglauben (behaftet) eintreten und auch noch mit ihm (behaftet wieder) herausgehen. Doch Allah weiß sehr wohl, was sie verbergen." });
                        surah5.VerseList.Add(new Verse { Number = 62, TextBosnian = "Vidiš mnoge od njih kako u grijehe i nasilje srljaju i kako ono što je zabranjeno jedu; ružno li je to kako postupaju.", TextEnglish = "And you see many of them hastening into sin and aggression and the devouring of [what is] unlawful. How wretched is what they have been doing.", TextGerman = "Und du siehst viele von ihnen in Sünde, feindseligem Vorgehen und dem Verschlingen von unrechtmäßig Erworbenem dahineilen. Fürwahr, wie schlimm ist, was sie tun!" });
                        surah5.VerseList.Add(new Verse { Number = 63, TextBosnian = "Trebalo bi da ih čestiti i učeni ljudi od lažna govora i zabranjena jela odvraćaju; ružno li je to kako postupaju.", TextEnglish = "Why do the rabbis and religious scholars not forbid them from saying what is sinful and devouring what is unlawful? How wretched is what they have been practicing.", TextGerman = "Warum verbieten ihnen nicht die Leute des Herrn und die Gelehrten die sündhaften Worte und das Verschlingen von unrechtmäßig Erworbenem? Fürwahr, wie schlimm ist, was sie machen!" });
                        surah5.VerseList.Add(new Verse { Number = 64, TextBosnian = "Jevreji govore: 'Allahova ruka je stisnuta!' Stisnute bile ruke njihove i prokleti bili zbog toga što govore! Naprotiv, obje ruke Njegove su pružene, On udjeljuje koliko hoće. A to što ti objavljuje Gospodar tvoj pojačat će kod mnogih od njih osionost i nevjerovanje. Mi smo ubacili među njih neprijateljstvo i mržnju sve do Kijametskog dana. Kad god pokušaju potpaliti ratnu vatru, Allah je ugasi. Oni nastoje na Zemlji smutnju praviti, a Allah ne voli smutljivce.", TextEnglish = "And the Jews say, 'The hand of Allāh is chained.'[273] Chained are their hands, and cursed are they for what they say. Rather, both His hands are extended; He spends however He wills. And that which has been revealed to you from your Lord will surely increase many of them in transgression and disbelief. And We have cast among them animosity and hatred until the Day of Resurrection. Every time they kindled the fire of war [against you], Allāh extinguished it. And they strive throughout the land [causing] corruption, and Allāh does not like corrupters.", TextGerman = "Und die Juden sagen: Allahs Hand ist gefesselt. Ihre (eigenen) Hände seien gefesselt und sie seien verflucht für das, was sie sagen. Nein! Vielmehr sind Seine Hände (weit) ausgestreckt; Er gibt aus, wie Er will. Was zu dir (als Offenbarung) von deinem Herrn herabgesandt worden ist, wird sicherlich bei vielen von ihnen die Auflehnung und den Unglauben noch mehren. Und Wir haben unter ihnen Feindschaft und Haß erregt bis zum Tag der Auferstehung. Jedesmal, wenn sie ein Feuer zum Krieg anzünden, löscht Allah es aus. Und sie bemühen sich, auf der Erde Unheil zu stiften. Aber Allah liebt nicht die Unheilstifter." });
                        surah5.VerseList.Add(new Verse { Number = 65, TextBosnian = "A da oni kojima je data Knjiga vjeruju i čuvaju se, Mi bismo prešli preko ružnih postupaka njihovih i sigurno bismo ih uveli u džennetske bašče uživanja.", TextEnglish = "And if only the People of the Scripture had believed and feared Allāh, We would have removed from them their misdeeds and admitted them to Gardens of Pleasure.", TextGerman = "Wenn die Leute der Schrift nur glaubten und gottesfürchtig wären, würden Wir ihnen wahrlich ihre bösen Taten tilgen und sie wahrlich in die Gärten der Wonne eingehen lassen." });
                        surah5.VerseList.Add(new Verse { Number = 66, TextBosnian = "Da se oni pridržavaju Tevrata i lndžila i onoga što im objavljuje Gospodar njihov, imali bi šta jesti, i od onoga što je iznad njih i od onoga što je ispod nogu njihovih. Ima ih i umjerenih, ali ružno je ono što radi većina njih.", TextEnglish = "And if only they had upheld [the law of] the Torah, the Gospel, and what has been revealed to them from their Lord [i.e., the Qur’ān], they would have consumed [provision] from above them and from beneath their feet.[274] Among them are a moderate [i.e., acceptable] community, but many of them - evil is that which they do.", TextGerman = "Wenn sie nur die Tora und das Evangelium und das befolgten, was zu ihnen (als Offenbarung) von ihrem Herrn herabgesandt wurde, würden sie fürwahr von (den guten Dingen) über ihnen und unter ihren Füßen essen. Unter ihnen ist eine gemäßigte Gemeinschaft; aber wie böse ist bei vielen von ihnen, was sie tun." });
                        surah5.VerseList.Add(new Verse { Number = 67, TextBosnian = "O Poslaniče, kazuj ono što ti se objavljuje od Gospodara tvoga - ako ne učiniš, onda nisi dostavio poslanicu Njegovu - a Allah će te od ljudi štititi. Allah, zaista, neće uputiti na Pravi put narod koji ne vjeruje.", TextEnglish = "O Messenger, announce that which has been revealed to you from your Lord, and if you do not, then you have not conveyed His message. And Allāh will protect you from the people. Indeed, Allāh does not guide the disbelieving people.", TextGerman = "O du Gesandter, übermittele, was zu dir (als Offenbarung) von deinem Herrn herabgesandt worden ist! Wenn du es nicht tust, so hast du Seine Botschaft nicht übermittelt. Allah wird dich vor den Menschen schützen. Gewiß, Allah leitet das ungläubige Volk nicht recht." });
                        surah5.VerseList.Add(new Verse { Number = 68, TextBosnian = "Reci: 'O vi kojima je data Knjiga, vi niste ni na čemu ako se ne budete pridržavali Tevrata i Indžila i onoga što vam objavljuje Gospodar vaš. A to što ti objavljuje Gospodar tvoj pojačat će, uistinu, kod mnogih od njih osionost i nevjerovanje, ali ti ne očajavaj zbog naroda koji ne vjeruje.'", TextEnglish = "Say, 'O People of the Scripture, you are [standing] on nothing until you uphold [the law of] the Torah, the Gospel, and what has been revealed to you from your Lord [i.e., the Qur’ān].' And that which has been revealed to you from your Lord will surely increase many of them in transgression and disbelief. So do not grieve over the disbelieving people.", TextGerman = "Sag: O Leute der Schrift, ihr fußt auf nichts, bis ihr die Tora und das Evangelium und das befolgt, was zu euch (als Offenbarung) von eurem Herrn herabgesandt worden ist. Was zu dir (als Offenbarung) von deinem Herrn herabgesandt worden ist, wird ganz gewiß bei vielen von ihnen die Auflehnung und den Unglauben noch mehren. So sei nicht betrübt über das ungläubige Volk!" });
                        surah5.VerseList.Add(new Verse { Number = 69, TextBosnian = "Oni koji su vjerovali, i oni koji su bili jevreji, te sabijci, i kršćani - oni koji su u Allaha i u ahiret vjerovali i dobra djela činili - ničega se oni uistinu neće bojati i ni za čim oni neće tugovati.", TextEnglish = "Indeed, those who have believed [in Prophet Muḥammad (ﷺ)] and those [before him (ﷺ)] who were Jews or Sabeans or Christians - those [among them] who believed in Allāh and the Last Day and did righteousness - no fear will there be concerning them, nor will they grieve.[275]", TextGerman = "Gewiß, diejenigen, die glauben, und diejenigen, die dem Judentum angehören, und die Säbier und die Christen, - wer (immer) an Allah und den Jüngsten Tag glaubt und rechtschaffen handelt, - über die soll keine Furcht kommen, noch sollen sie traurig sein." });
                        surah5.VerseList.Add(new Verse { Number = 70, TextBosnian = "Mi smo od sinova Israilovih zavjet uzeli i poslanike im slali. Kad god bi im koji poslanik donio ono što nije godilo dušama njihovim, jedne su u laž utjerivali a druge ubijali.", TextEnglish = "We had already taken the covenant of the Children of Israel and had sent to them messengers. Whenever there came to them a messenger with what their souls did not desire, a party [of messengers] they denied, and another party they killed.", TextGerman = "Und Wir trafen doch schon mit den Kindern Isra’ils ein Abkommen und sandten zu ihnen Gesandte. Jedesmal, wenn ein Gesandter zu ihnen kam mit etwas, wozu sie selbst nicht neigten, bezichtigten sie einen Teil (der Gesandten) der Lüge und töteten einen (anderen) Teil." });
                        surah5.VerseList.Add(new Verse { Number = 71, TextBosnian = "Mislili su da neće na muke biti stavljeni, pa su bili i slijepi i gluhi, i onda kad je Allah primio pokajanje njihovo, mnogi od njih su opet bili i slijepi i gluhi; a Allah dobro vidi ono šta oni rade.", TextEnglish = "And they thought there would be no [resulting] punishment, so they became blind and deaf. Then Allāh turned to them in forgiveness; then [again] many of them became blind and deaf. And Allāh is Seeing of what they do.", TextGerman = "Und sie meinten, daß es keine Versuchung gäbe. Sie waren blind und taub. Hierauf nahm Allah ihre Reue an. Dann aber wurden (wieder) viele von ihnen blind und taub. Und Allah sieht wohl, was sie tun." });
                        surah5.VerseList.Add(new Verse { Number = 72, TextBosnian = "nevjernici su oni koji govore: 'Bog je, doista, Mesih, sin Merjemin!' A Mesih je govorio: 'O sinovi lsrailovi, ibadet činite Allahu, i mome i vašem Gospodaru!' Ko drugog Allahu u obožavanju pridružuje, Allah će mu ulazak u Džennet zabraniti i boravište njegovo će Džehennem biti; a zulumćarima neće niko moći pomoći.", TextEnglish = "They have certainly disbelieved who say, 'Allāh is the Messiah, the son of Mary' while the Messiah has said, 'O Children of Israel, worship Allāh, my Lord and your Lord.' Indeed, he who associates others with Allāh - Allāh has forbidden him Paradise, and his refuge is the Fire. And there are not for the wrongdoers any helpers.", TextGerman = "Fürwahr, ungläubig sind diejenigen, die sagen: Gewiß, Allah ist al-Masih, der Sohn Maryams, wo doch al-Masih (selbst) gesagt hat: O Kinder Isra’ils, dient Allah, meinem Herrn und eurem Herrn! Wer Allah (etwas) beigesellt, dem verbietet fürwahr Allah das Paradies, und dessen Zufluchtsort wird das (Höllen)feuer sein. Die Ungerechten werden keine Helfer haben." });
                        surah5.VerseList.Add(new Verse { Number = 73, TextBosnian = "nevjernici su oni koji govore: 'Allah je, zaista, jedan od trojice!' A samo je jedan Bog! I ako se ne okane onoga što govore, nesnosna patnja će, zaista, stići svakog od njih koji nevjernik ostane.", TextEnglish = "They have certainly disbelieved who say, 'Allāh is the third of three.'[276] And there is no god except one God. And if they do not desist from what they are saying, there will surely afflict the disbelievers among them a painful punishment.", TextGerman = "Fürwahr, ungläubig sind diejenigen, die sagen: Gewiß, Allah ist einer von dreien. Es gibt aber keinen Gott außer dem Einen Einzigen. Wenn sie mit dem, was sie sagen, nicht aufhören, so wird denjenigen von ihnen, die ungläubig sind, ganz gewiß schmerzhafte Strafe widerfahren." });
                        surah5.VerseList.Add(new Verse { Number = 74, TextBosnian = "Zašto se oni ne pokaju Allahu i ne zamole Njegov oprost, a Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "So will they not repent to Allāh and seek His forgiveness? And Allāh is Forgiving and Merciful.", TextGerman = "Wenden sie sich denn nicht in Reue zu Allah und bitten Ihn um Vergebung? Allah ist Allvergebend und Barmherzig." });
                        surah5.VerseList.Add(new Verse { Number = 75, TextBosnian = "Mesih, sin Merjemin, samo je poslanik - i prije njega su dolazili i odlazili poslanici - a majka njegova je iskreno srcem vjerovala; i oboje su hranu jeli. Pogledaj kako im Mi iznosimo jasne dokaze, i pogledaj, zatim, njih kako se odmeću!", TextEnglish = "The Messiah, son of Mary, was not but a messenger; [other] messengers have passed on before him. And his mother was a supporter of truth. They both used to eat food.[277] Look how We make clear to them the signs; then look how they are deluded.", TextGerman = "Al-Masih, der Sohn Maryams, war doch nur ein Gesandter, vor dem bereits Gesandte vorübergegangen waren. Und seine Mutter war sehr wahrheitsliebend; sie (beide) pflegten Speise zu essen. Schau, wie Wir ihnen die Zeichen klar machen, und schau, wie sie sich abwendig machen lassen!" });
                        surah5.VerseList.Add(new Verse { Number = 76, TextBosnian = "Reci: 'Zar pored Allaha obožavate nešto što vam ne može nanijeti nikakvu štetu, niti vam korist neku pribaviti? A Allah je, baš, Svečujući i Sveznajući.'", TextEnglish = "Say, 'Do you worship besides Allāh that which holds for you no [power of] harm or benefit while it is Allāh who is the Hearing, the Knowing?'", TextGerman = "Sag: Wollt ihr außer Allah dem dienen, was euch weder Schaden noch Nutzen zu bringen vermag? Und Allah ist es (doch), der Allhörend und Allwissend ist." });
                        surah5.VerseList.Add(new Verse { Number = 77, TextBosnian = "Reci: 'O vi kojima je data Knjiga, ne zastranjujte u vjerovanju svome, suprotno istini, i ne povodite se za prohtjevima ljudi koji su još davno zalutali, i mnoge u zabludu odveli, i sami s pravoga puta skrenuli.'", TextEnglish = "Say, 'O People of the Scripture, do not exceed limits in your religion beyond the truth and do not follow the inclinations of a people who had gone astray before and misled many and have strayed from the soundness of the way.'", TextGerman = "Sag: O Leute der Schrift, übertreibt nicht in eurer Religion außer in (dem Rahmen) der Wahrheit und folgt nicht den Neigungen von Leuten, die schon zuvor irregegangen sind und viele (andere mit ihnen) in die Irre geführt haben und vom rechten Weg abgeirrt sind." });
                        surah5.VerseList.Add(new Verse { Number = 78, TextBosnian = "Jezikom Davuda i Isaa, sina Merjemina, prokleti su oni sinovi Israilovi koji nisu vjerovali- zato što su neposlušni bili i uvijek granice prelazili;", TextEnglish = "Cursed were those who disbelieved among the Children of Israel by the tongue of David and of Jesus, the son of Mary. That was because they disobeyed and [habitually] transgressed.", TextGerman = "Verflucht wurden diejenigen von den Kindern Isra’ils, die ungläubig waren, durch den Mund Dawuds und ’Isas, des Sohnes Maryams. Dies dafür, daß sie sich widersetzten und stets übertraten." });
                        surah5.VerseList.Add(new Verse { Number = 79, TextBosnian = "jedni druge od zla što činiše odvraćali nisu. Ružno li je, zaista, to kako su postupali!", TextEnglish = "They used not to prevent one another from wrongdoing that they did. How wretched was that which they were doing.", TextGerman = "Sie pflegten einander nichts Verwerfliches, das sie taten, zu verbieten. Fürwahr, wie schlimm ist, was sie zu tun pflegten!" });
                        surah5.VerseList.Add(new Verse { Number = 80, TextBosnian = "Ti vidiš mnoge od njih kako s višebošcima prijateljuju. Ružno je, zaista, ono što sami sebi pripremaju: da se Allah na njih rasrdi i da u patnji vječno ostanu.", TextEnglish = "You see many of them becoming allies of those who disbelieved [i.e., the polytheists]. How wretched is that which they have put forth for themselves in that Allāh has become angry with them, and in the punishment they will abide eternally.", TextGerman = "Du siehst viele von ihnen diejenigen, die ungläubig sind, zu Vertrauten nehmen. Fürwahr, wie schlimm ist, was sie sich selbst vorausgeschickt haben; (es ist,) daß Allah ihnen gegenüber Sein Mißfallen zeigt; und ewig werden sie in der Strafe bleiben." });
                        surah5.VerseList.Add(new Verse { Number = 81, TextBosnian = "A da vjeruju u Allaha i Vjerovjesnika i u ono što se njemu objavljuje, oni s njima ne bi prijateljevali, ali mnogi su od njih prema Allahu buntovni.", TextEnglish = "And if they had believed in Allāh and the Prophet and in what was revealed to him, they would not have taken them as allies; but many of them are defiantly disobedient.", TextGerman = "Wenn sie an Allah und den Propheten und das, was zu ihm (als Offenbarung) herabgesandt worden ist, glauben würden, hätten sie sie nicht zu Schutzherren genommen. Aber viele von ihnen sind Frevler." });
                        surah5.VerseList.Add(new Verse { Number = 82, TextBosnian = "Ti ćeš sigurno naći da su onima koji vjeruju najljući neprijatelji jevreji i višebošci; i svakako ćeš naći da su vjernicima najbliži prijatelji oni koji govore: 'Mi smo kršćani', zato što među njima ima svećenika i monaha i što se oni ne ohole.", TextEnglish = "You will surely find the most intense of the people in animosity toward the believers [to be] the Jews and those who associate others with Allāh; and you will find the nearest of them in affection to the believers those who say, 'We are Christians.' That is because among them are priests and monks and because they are not arrogant.", TextGerman = "Du wirst ganz gewiß finden, daß diejenigen Menschen, die den Gläubigen am heftigsten Feindschaft zeigen, die Juden und diejenigen sind, die (Allah etwas) beigesellen. Und du wirst ganz gewiß finden, daß diejenigen, die den Gläubigen in Freundschaft am nächsten stehen, die sind, die sagen: Wir sind Christen. Dies, weil es unter ihnen Priester und Mönche gibt und weil sie sich nicht hochmütig verhalten." });
                        surah5.VerseList.Add(new Verse { Number = 83, TextBosnian = "Kada slušaju ono što se objavljuje Poslaniku, vidiš kako im liju suze iz očiju jer znaju da je to Istina, pa govore: 'Gospodaru naš, mi vjerujemo, pa upiši i nas među one koji su posvjedočili.", TextEnglish = "And when they hear what has been revealed to the Messenger, you see their eyes overflowing with tears because of what they have recognized of the truth. They say, 'Our Lord, we have believed, so register us among the witnesses.", TextGerman = "Wenn sie hören, was zum Gesandten (als Offenbarung) herabgesandt worden ist, siehst du ihre Augen von Tränen überfließen wegen dessen, was sie (darin) als Wahrheit erkannt haben. Sie sagen: Unser Herr, wir glauben. Schreibe uns unter den Zeugnis Ablegenden auf." });
                        surah5.VerseList.Add(new Verse { Number = 84, TextBosnian = "Zašto da ne vjerujemo u Allaha i u Istinu koja nam dolazi, kada žudimo da i nas Gospodar naš uvede s ljudima dobrim.'", TextEnglish = "And why should we not believe in Allāh and what has come to us of the truth? And we aspire that our Lord will admit us [to Paradise] with the righteous people.'", TextGerman = "Warum sollten wir nicht an Allah glauben und an das, was von der Wahrheit zu uns gekommen ist, und begehren, daß uns unser Herr zusammen mit dem rechtschaffenen Volk (in den Paradiesgarten) eingehen lasse?" });
                        surah5.VerseList.Add(new Verse { Number = 85, TextBosnian = "I Allah će im, zbog onoga što govore, kao nagradu džennetske bašče dati, kroz koje rijeke teku, u kojima će vječno boraviti; a to će biti nagrada za sve one koji dobra djela čine.", TextEnglish = "So Allāh rewarded them for what they said[278] with gardens [in Paradise] beneath which rivers flow, wherein they abide eternally. And that is the reward of doers of good.", TextGerman = "So wird Allah sie für das, was sie gesagt haben, mit Gärten belohnen, durcheilt von Bächen, ewig darin zu bleiben. Das ist der Lohn der Gutes Tuenden." });
                        surah5.VerseList.Add(new Verse { Number = 86, TextBosnian = "A oni koji ne budu vjerovali i ajete i znakove Naše budu poricali, bit će stanovnici Džehennema.", TextEnglish = "But those who disbelieved and denied Our signs - they are the companions of Hellfire.", TextGerman = "Diejenigen aber, die ungläubig sind und Unsere Zeichen für Lüge erklären, das sind die Insassen des Höllenbrandes." });
                        surah5.VerseList.Add(new Verse { Number = 87, TextBosnian = "O vi koji vjerujete, ne zabranjujte sebi ono što je lijepo, što je Allah dozvolio, samo ne prelazite granicu! Zaista Allah ne voli one koji granicu prelaze.", TextEnglish = "O you who have believed, do not prohibit the good things which Allāh has made lawful to you and do not transgress. Indeed, Allāh does not like transgressors.", TextGerman = "O die ihr glaubt, verbietet nicht die guten Dinge, die Allah euch erlaubt hat. und übertretet nicht! Allah liebt nicht die Übertreter." });
                        surah5.VerseList.Add(new Verse { Number = 88, TextBosnian = "I jedite ono što vam Allah daje, što je dozvoljeno i lijepo; i bojte se Allaha, u Kojeg vjerujete.", TextEnglish = "And eat of what Allāh has provided for you [which is] lawful and good. And fear Allāh, in whom you are believers.", TextGerman = "Und eßt von dem, womit Allah euch versorgt hat, als etwas Erlaubtem und Gutem, und fürchtet Allah, an Den ihr glaubt!" });
                        surah5.VerseList.Add(new Verse { Number = 89, TextBosnian = "Allah vas neće kazniti ako se zakunete nenamjerno, ali će vas kazniti ako se zakunete namjerno. Otkup za prekršenu zakletvu je: da deset siromaha običnom hranom kojom hranite čeljad svoju nahranite, ili da ih odjenete, ili da roba ropstva oslobodite. A onaj ko ne bude mogao - neka tri dana posti. Tako se za zakletve svoje otkupljujte kada se zakunete; a o zakletvama svojim brinite se! Eto, tako vam Allah objašnjava propise Svoje, da biste bili zahvalni.", TextEnglish = "Allāh will not impose blame upon you for what is meaningless[279] in your oaths, but He will impose blame upon you for [breaking] what you intended of oaths. So its expiation[280] is the feeding of ten needy people from the average of that which you feed your [own] families or clothing them or the freeing of a slave. But whoever cannot find [or afford it] - then a fast of three days [is required]. That is the expiation for oaths when you have sworn. But guard your oaths.[281] Thus does Allāh make clear to you His verses [i.e., revealed law] that you may be grateful.", TextGerman = "Allah wird euch nicht für etwas Unbedachtes in euren Eiden belangen. Jedoch wird Er euch für das belangen, was ihr mit euren Eiden fest abmacht (und dieses dann nicht einhaltet). Die Sühne dafür besteht in der Speisung von zehn Armen in dem Maß, wie ihr eure Angehörigen im Durchschnitt speist, oder ihrer Bekleidung oder der Befreiung eines Sklaven. Wer aber keine (Möglichkeit) findet, (der hat) drei Tage (zu) fasten. Das ist die Sühne für eure Eide, wenn ihr schwört. Und erfüllt eure Eide. So macht Allah euch Seine Zeichen klar, auf daß ihr dankbar sein möget!" });
                        surah5.VerseList.Add(new Verse { Number = 90, TextBosnian = "O vi koji vjerujete, opojna pića i kocka i kumiri i strelice za gatanje odvratno su, šejtanovo djelo; zato se toga klonite, da biste uspjeli.", TextEnglish = "O you who have believed, indeed, intoxicants, gambling, [sacrificing on] stone alters [to other than Allāh], and divining arrows are but defilement from the work of Satan, so avoid[282] it that you may be successful.", TextGerman = "O die ihr glaubt, berauschender Trank, Glücksspiel, Opfersteine und Lospfeile sind nur ein Greuel vom Werk des Satans. So meidet ihn, auf daß es euch wohl ergehen möge!" });
                        surah5.VerseList.Add(new Verse { Number = 91, TextBosnian = "Šejtan želi pomoću opojnih pića i kocke unijeti među vas neprijateljstvo i mržnju i od sjećanja na Allaha i obavljanja namaza odvratiti vas. Pa, hoćete li se okaniti?", TextEnglish = "Satan only wants to cause between you animosity and hatred through intoxicants and gambling and to avert you from the remembrance of Allāh and from prayer. So will you not desist?", TextGerman = "Der Satan will (ja) zwischen euch nur Feindschaft und Haß säen durch berauschenden Trank und Glücksspiel und euch vom Gedenken Allahs und vom Gebet abhalten. Werdet ihr (damit) nun wohl aufhören?" });
                        surah5.VerseList.Add(new Verse { Number = 92, TextBosnian = "I budite poslušni Allahu i budite poslušni Poslaniku i oprezni budite! A ako se okrenete, onda znajte da je Poslanik Naš dužan samo da jasno obznani.", TextEnglish = "And obey Allāh and obey the Messenger and beware. And if you turn away - then know that upon Our Messenger is only [the responsibility for] clear notification.", TextGerman = "Und gehorcht Allah und gehorcht dem Gesandten und seht euch vor! Doch wenn ihr euch abkehrt, so wißt, daß Unserem Gesandten nur die deutliche Übermittelung (der Botschaft) obliegt." });
                        surah5.VerseList.Add(new Verse { Number = 93, TextBosnian = "Onima koji vjeruju i dobra djela čine nema nikakva grijeha u onome što su pojeli i popili prije zabrane, ako se klone onoga što im je zabranjeno i ako vjeruju i dobra djela čine, zatim se Allaha boje i vjeruju i onda se grijeha klone i dobro čine. A Allah voli one koji drugima dobro čine.", TextEnglish = "There is not upon those who believe and do righteousness [any] blame concerning what they have eaten [in the past] if they [now] fear Allāh and believe and do righteous deeds, and then fear Allāh and believe, and then fear Allāh and do good; and Allāh loves the doers of good.", TextGerman = "Es lastet keine Sünde auf denjenigen, die glauben und rechtschaffene Werke tun, hinsichtlich dessen, was sie (bisher) verzehrt haben, wenn sie (fortan) gottesfürchtig sind und glauben und rechtschaffene Werke tun und wiederum gottesfürchtig sind und glauben und wiederum gottesfürchtig sind und Gutes tun. Allah liebt die Gutes Tuenden." });
                        surah5.VerseList.Add(new Verse { Number = 94, TextBosnian = "O vi koji vjerujete, Allah će vas dovoditi u iskušenje sa divljači koja će biti nadohvat ruku vaših i kopalja vaših - da bi Allah znao onoga koji Ga se boji kad ga niko ne vidi. A onoga ko i poslije toga nasilje učini, bolna patnja čeka.", TextEnglish = "O you who have believed, Allāh will surely test you through something of the game that your hands and spears [can] reach, that Allāh may make evident those who fear Him unseen. And whoever transgresses after that - for him is a painful punishment.", TextGerman = "O die ihr glaubt, Allah wird euch ganz gewiß mit etwas von dem Jagdwild prüfen, das eure Hände und Lanzen erreichen (können), damit Allah denjenigen kenne, der Ihn im Verborgenen fürchtet. Wer nun danach eine Übertretung begeht, für den gibt es schmerzhafte Strafe." });
                        surah5.VerseList.Add(new Verse { Number = 95, TextBosnian = "O vi koji vjerujete, ne ubijajte divljač dok ste u ihramima! Onome od vas ko to hotimično učini kazna je da jednu domaću životinju, čiju će vrijednost procijeniti dvojica vaših pravednih ljudi, kao kurban zakolje kod Kabe, ili da se iskupi time što će, ravno tome, nahraniti siromahe ili postiti, da bi osjetio pogubnost postupka svoga. A Allah je već oprostio ono što je bilo. Onoga ko to opet uradi - Allah će kazniti. Allah je silan i strog.", TextEnglish = "O you who have believed, do not kill game while you are in the state of iḥrām.[283] And whoever of you kills it intentionally - the penalty is an equivalent from sacrificial animals to what he killed, as judged by two just men among you as an offering [to Allāh] delivered to the Kaʿbah, or an expiation: the feeding of needy people or the equivalent of that in fasting, that he may taste the consequence of his matter [i.e., deed]. Allāh has pardoned what is past; but whoever returns [to violation], then Allāh will take retribution from him. And Allāh is Exalted in Might and Owner of Retribution.[284]", TextGerman = "O die ihr glaubt, tötet nicht das Jagdwild, während ihr im Zustand der Pilgerweihe seid! Wer von euch es vorsätzlich tötet, (für den gilt es,) eine Ersatzleistung (zu zahlen), ein Gleiches, wie das, was er getötet hat, an Stück Vieh - darüber sollen zwei gerechte Personen von euch richten - (eine Ersatzleistung, die) als Opfertier die Ka’ba erreichen soll. Oder (er leistet als) eine Sühne die Speisung von Armen oder die (entsprechende) Ersatzleistung an Fasten, damit er die schlimmen Folgen seines Verhaltens koste. Allah verzeiht, was (vorher) geschehen ist. Wer aber rückfällig wird, den wird Allah der Vergeltung aussetzen. Allah ist Allmächtig und Besitzer von Vergeltungsgewalt." });
                        surah5.VerseList.Add(new Verse { Number = 96, TextBosnian = "Vama se dopušta da u moru lovite i ulov jedete, da se njime vi i putnici koristite, a zabranjuje vam se da na kopnu lovite dok obrede hadža obavljate. I bojte se Allaha, pred Kojim ćete biti sabrani.", TextEnglish = "Lawful to you is game from the sea and its food as provision for you and the travelers,[285] but forbidden to you is game from the land as long as you are in the state of iḥrām. And fear Allāh to whom you will be gathered.", TextGerman = "Erlaubt sind euch die Jagdtiere des Meeres und (all) das Eßbare aus ihm als Nießbrauch für euch und für die Reisenden; doch verboten ist euch die Jagd auf die Landtiere, solange ihr im Zustand der Pilgerweihe seid. Und fürchtet Allah, zu Dem ihr versammelt werdet." });
                        surah5.VerseList.Add(new Verse { Number = 97, TextBosnian = "Allah je učinio da Kaba, Sveta kuća, bude ljudima temelj sigurnosti i unapređenja života, a tako i sveti mjeseci i kurbani, naročito oni ogrlicama označeni. To je zato da znate da Allah zna ono što je na nebesima i ono što je na Zemlji, i da Allah sve dobro zna.", TextEnglish = "Allāh has made the Kaʿbah, the Sacred House, standing[286] for the people and [has sanctified] the sacred months and the sacrificial animals and the garlands [by which they are identified]. That is so you may know that Allāh knows what is in the heavens and what is in the earth and that Allāh is Knowing of all things.", TextGerman = "Allah hat die Ka’ba, das geschützte Haus, zu einer Stätte des Gottesdienstes für die Menschen gemacht, und (ebenso) den Schutzmonat, die Opfertiere und die (Opfertiere mit den) Halsgehänge(n). Dies, damit ihr wisset, daß Allah weiß, was in den Himmeln und was auf der Erde ist, und daß Allah über alles Bescheid weiß." });
                        surah5.VerseList.Add(new Verse { Number = 98, TextBosnian = "Znajte da Allah strogo kažnjava, ali i da je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "Know that Allāh is severe in penalty and that Allāh is Forgiving and Merciful.", TextGerman = "Wisset, daß Allah streng im Bestrafen, daß Allah (aber auch) Allvergebend und Barmherzig ist." });
                        surah5.VerseList.Add(new Verse { Number = 99, TextBosnian = "Poslaniku je jedino dužnost objaviti, a Allah zna i ono što javno činite i ono što sakrijete.", TextEnglish = "Not upon the Messenger is [responsibility] except [for] notification. And Allāh knows whatever you reveal and whatever you conceal.", TextGerman = "Dem Gesandten obliegt nur die Übermittelung (der Botschaft). Und Allah weiß, was ihr offenlegt und was ihr verbergt." });
                        surah5.VerseList.Add(new Verse { Number = 100, TextBosnian = "Reci: 'Nije isto ono što je zabranjeno i ono što je dozvoljeno', iako te iznenađuje mnoštvo onoga što je zabranjeno. Zato se Allaha bojte, o vi koji razum imate, da biste uspjeli.", TextEnglish = "Say, 'Not equal are the evil and the good, although the abundance of evil might impress you.' So fear Allāh, O you of understanding, that you may be successful.", TextGerman = "Sag: Nicht gleich sind das Schlechte und das Gute, auch wenn die Menge des Schlechten dir gefallen sollte. Darum fürchtet Allah, o die ihr Verstand besitzt, auf daß es euch wohl ergehen möge!" });
                        surah5.VerseList.Add(new Verse { Number = 101, TextBosnian = "O vi koji vjerujete, ne zapitkujte o onome što će vam pričiniti neprijatnosti ako vam bude objašnjeno; a ako budete pitali za to dok Kur'an bude objavljivan, bit će vam objašnjeno; ono ranije Allah vam je već oprostio. A Allah je Onaj Koji oprašta grijehe i Onaj Koji ne žuri s kaznom.", TextEnglish = "O you who have believed, do not ask about things which, if they are shown to you, will distress you. But if you ask about them while the Qur’ān is being revealed, they will be shown to you. Allāh has pardoned it [i.e., that which is past]; and Allāh is Forgiving and Forbearing.", TextGerman = "O die ihr glaubt, fragt nicht nach Dingen, die, wenn sie euch offengelegt werden, euch leid tun, wenn ihr nach ihnen fragt zu der Zeit, da der Qur’an offenbart wird, sie euch (gewiß) offengelegt werden, wo Allah sie übergangen hat. Und Allah ist Allvergebend und Nachsichtig." });
                        surah5.VerseList.Add(new Verse { Number = 102, TextBosnian = "Neki su ljudi prije vas pitali za to, pa poslije u to nisu povjerovali.", TextEnglish = "A people asked such [questions] before you; then they became thereby disbelievers.[287]", TextGerman = "Leute vor euch haben schon danach gefragt, doch dann wurden sie ihretwegen ungläubig." });
                        surah5.VerseList.Add(new Verse { Number = 103, TextBosnian = "Allah nije propisao ni behiru ni saibu ni vasilu a ni hama; to oni koji ne vjeruju govore o Allahu laži, i većina njih ne razumiju.", TextEnglish = "Allāh has not appointed [such innovations as] baḥīrah or sā’ibah or waṣīlah or ḥām.[288] But those who disbelieve invent falsehood about Allāh, and most of them do not reason.", TextGerman = "Allah hat keine Bahira, keine Sa’iba, keine Wasila und keine Ham bestimmt, sondern diejenigen, die ungläubig sind, ersinnen gegen Allah Lügen, und die meisten von ihnen begreifen nicht." });
                        surah5.VerseList.Add(new Verse { Number = 104, TextBosnian = "A kada im se kaže: 'Pristupite onome što Allah objavljuje, i Poslaniku!', oni odgovaraju: 'Dovoljno nam je ono što smo od predaka naših zapamtili.' Zar i ako preci njihovi nisu ništa znali i ako nisu na Pravom putu bili?!", TextEnglish = "And when it is said to them, 'Come to what Allāh has revealed and to the Messenger,' they say, 'Sufficient for us is that upon which we found our fathers.' Even though their fathers knew nothing, nor were they guided?", TextGerman = "Und wenn man zu ihnen sagt: Kommt her zu dem, was Allah (als Offenbarung) herabgesandt hat, und zum Gesandten, sagen sie: Unsere Genüge ist das, worin wir unsere Väter (vor)gefunden haben. Was denn, auch wenn ihre Väter nichts begriffen und nicht rechtgeleitet waren?" });
                        surah5.VerseList.Add(new Verse { Number = 105, TextBosnian = "O vi koji vjerujete, brinite se o sebi; ako ste na Pravom putu, neće vam nauditi onaj koji je zalutao! Allahu ćete se svi vratiti i On će vas obavijestiti o onome što ste radili.", TextEnglish = "O you who have believed, upon you is [responsibility for] yourselves. Those who have gone astray will not harm you when you have been guided. To Allāh is your return all together; then He will inform you of what you used to do.", TextGerman = "O die ihr glaubt, wacht über euch selbst! Wer abirrt, kann euch keinen Schaden zufügen, wenn ihr rechtgeleitet seid. Zu Allah wird eure aller Rückkehr sein, und dann wird Er kundtun, was ihr zu tun pflegtet." });
                        surah5.VerseList.Add(new Verse { Number = 106, TextBosnian = "O vi koji vjerujete, kada vam se približi smrt, prilikom davanja oporuke neka vam posvjedoče dvojica pravednih od vas, ili neka druga dvojica, koji nisu vaši- ako ste na putu, a pojave se znaci smrti. A ako posumnjate, zadržite ih poslije obavljenog namaza i neka se Allahom zakunu: 'Mi zakletvu ni za kakvu cijenu nećemo prodati, makar se radilo i o kakvu rođaku i svjedočenje koje je Allah propisao nećemo uskratiti, jer bismo tada doista bili grešnici.'", TextEnglish = "O you who have believed, testimony [should be taken] among you when death approaches one of you at the time of bequest - [that of] two just men from among you or two others from outside if you are traveling through the land and the disaster of death should strike you. Detain them after the prayer and let them both swear by Allāh if you doubt [their testimony, saying], 'We will not exchange it [i.e., our oath] for a price [i.e., worldly gain], even if he should be a near relative, and we will not withhold the testimony of [i.e., ordained by] Allāh. Indeed, we would then be of the sinful.'", TextGerman = "O die ihr glaubt, wenn einem von euch der Tod naht zu der Zeit, da (er sein) Vermächtnis (macht), (soll) das Zeugnis unter euch (erfolgen) durch zwei gerechte Personen von euch, oder durch zwei andere, (die) nicht von euch (sind), wenn ihr im Land umherreist und euch dann das Unglück des Todes trifft. Ihr sollt sie nach dem Gebet festhalten, und sie sollen dann, wenn ihr zweifelt, bei Allah schwören: Wir verkaufen es für keinen Preis, auch wenn es sich um einen Verwandten handelt, und verheimlichen das Zeugnis Allahs nicht; wir gehörten sonst wahrlich zu den Sündhaften." });
                        surah5.VerseList.Add(new Verse { Number = 107, TextBosnian = "A ako se dozna da su njih dvojica zgriješila, onda će njih zamijeniti druga dvojica od onih kojima je šteta nanesena, i neka se Allahom zakunu: 'Naše zakletve su sigurno vjerodostojnije od zakletvi njihovih, mi se nismo krivo zakleli, jer bismo tada, zaista, u redovima zulumćara bili.'", TextEnglish = "But if it is found that those two were guilty of sin [i.e., perjury], let two others stand in their place [who are] foremost [in claim] from those who have a lawful right. And let them swear by Allāh, 'Our testimony is truer than their testimony, and we have not transgressed. Indeed, we would then be of the wrongdoers.'", TextGerman = "Wenn man danach aber entdeckt, daß sie (beide) sich einer Sünde schuldig gemacht haben, dann (sollen) zwei andere an ihre Stelle treten von denen, zu deren Nachteil die beiden sich (einer Sünde) schuldig gemacht haben, und (sie) (beide sollen) bei Allah schwören: Unser Zeugnis ist wahrlich berechtigter als deren Zeugnis. Und wir haben nicht übertreten; wir gehörten sonst wahrlich zu den Ungerechten." });
                        surah5.VerseList.Add(new Verse { Number = 108, TextBosnian = "Time će biti najbliže ispravnom svjedočenju, i da se ne plaše da će njihove zakletve drugim zakletvama biti pobijene. I bojte se Allaha i slušajte! A Allah neće uputiti na Pravi put ljude buntovnike.", TextEnglish = "That is more likely that they will give testimony according to its [true] objective, or [at least] they would fear that [other] oaths might be taken after their oaths. And fear Allāh and listen [i.e., obey Him]; and Allāh does not guide the defiantly disobedient people.", TextGerman = "Das ist eher geeignet, daß sie das Zeugnis, wie es sein soll, ablegen oder sie befürchten (müssen), daß nach ihren Eiden wieder (andere) Eide (geschworen) werden. Und fürchtet Allah und hört! Allah leitet das Volk der Frevler nicht recht." });
                        surah5.VerseList.Add(new Verse { Number = 109, TextBosnian = "Na dan kada Allah sakupi poslanike i upita: 'Kako su vam se odazivali?', oni će reći: 'Mi znanja nemamo; zaista Ti dobro znaš sve što je čulima nedokučivo.'", TextEnglish = "[Be warned of] the Day when Allāh will assemble the messengers and say, 'What was the response you received?' They will say, 'We have no knowledge. Indeed, it is You who is Knower of the unseen' -", TextGerman = "An dem Tag, da Allah die Gesandten versammelt und dann sagt: Was wurde euch geantwortet?’, werden sie sagen: Wir haben kein Wissen (darüber). Du bist ja der Allwisser der verborgenen Dinge." });
                        surah5.VerseList.Add(new Verse { Number = 110, TextBosnian = "Kad Allah rekne: 'O Isa, sine Merjemin, sjeti se blagodati Moje prema tebi i majci tvojoj kada sam te Duhom Blagoslovljenim pomogao pa si s ljudima, u bešici i kao zreo čovjek, razgovarao; i kada sam te pismenosti i mudrosti, i Tevratu i Indžilu naučio; i kada si, dozvolom Mojom, od blata nešto poput ptice oblikovao i u nju udahnuo, i kada je ona, dozvolom Mojom, postala ptica; i kada si, dozvolom Mojom, od rođenja slijepa i gubava iscijelio; i kada si, dozvolom Mojom, mrtve dizao; i kada sam od tebe sinove Israilove odbio, kad si im ti jasne dokaze donio, pa su oni među njima koji nisu vjerovali - povikali: 'Ovo nije ništa drugo do jasna vradžbina',", TextEnglish = "[The Day] when Allāh will say, 'O Jesus, Son of Mary, remember My favor upon you and upon your mother when I supported you with the Pure Spirit [i.e., the angel Gabriel] and you spoke to the people in the cradle and in maturity; and [remember] when I taught you writing and wisdom and the Torah and the Gospel; and when you designed from clay [what was] like the form of a bird with My permission, then you breathed into it, and it became a bird with My permission; and you healed the blind [from birth] and the leper with My permission; and when you brought forth the dead with My permission; and when I restrained the Children of Israel from [killing] you when you came to them with clear proofs and those who disbelieved among them said, 'This is not but obvious magic.'", TextGerman = "Wenn Allah sagt: O ’Isa, Sohn Maryams, gedenke Meiner Gunst an dir und an deiner Mutter, als Ich dich mit dem Heiligen Geist stärkte, so daß du in der Wiege zu den Menschen sprachst und im Mannesalter; und als Ich dich die Schrift, die Weisheit, die Tora und das Evangelium lehrte; und als du aus Lehm mit Meiner Erlaubnis (etwas) schufst, (was so aussah) wie die Gestalt eines Vogels, und ihr dann einhauchtest, und sie da ein (wirklicher) Vogel wurde mit Meiner Erlaubnis; und (als) du den Blindgeborenen und den Weißgefleckten mit Meiner Erlaubnis heiltest und Tote mit Meiner Erlaubnis (aus den Gräbern) herauskommen ließest; und als Ich die Kinder Isra’ils von dir zurückhielt, als du mit den klaren Beweisen zu ihnen kamst, worauf diejenigen von ihnen, die ungläubig waren, sagten: ,Das ist nichts als deutliche Zauberei’." });
                        surah5.VerseList.Add(new Verse { Number = 111, TextBosnian = "i kada sam učenicima naredio: 'Vjerujte u Mene i Poslanika Moga', oni su odgovorili: 'Vjerujemo, a Ti budi svjedok da smo muslimani.'", TextEnglish = "And [remember] when I inspired to the disciples, 'Believe in Me and in My messenger [i.e., Jesus].' They said, 'We have believed, so bear witness that indeed we are Muslims [in submission to Allāh].'", TextGerman = "Und als Ich den Jüngern eingab: Glaubt an Mich und an Meinen Gesandten! Sie sagten: Wir glauben. Bezeuge, daß wir Dir ergeben sind!" });
                        surah5.VerseList.Add(new Verse { Number = 112, TextBosnian = "A kada učenici rekoše: 'O Isa, sine Merjemin, može li nam Gospodar tvoj trpezu s neba spustiti?', on reče: 'Bojte se Allaha, ako ste vjernici.'", TextEnglish = "[And remember] when the disciples said, 'O Jesus, Son of Mary, can your Lord[289] send down to us a table [spread with food] from the heaven?' [Jesus] said, 'Fear Allāh, if you should be believers.'", TextGerman = "Als die Jünger sagten: O ’Isa, Sohn Maryams, kann dein Herr zu uns einen Tisch (mit Speisen) vom Himmel herabsenden? Er sagte: Fürchtet Allah, wenn ihr gläubig seid!" });
                        surah5.VerseList.Add(new Verse { Number = 113, TextBosnian = "Mi želimo, rekoše oni, s nje jesti i da srca naša budu smirena i uvjeriti se da si nam istinu govorio, i da budemo oni koji će o njoj svjedočiti.", TextEnglish = "They said, 'We wish to eat from it and let our hearts be reassured and know that you have been truthful to us and be among its witnesses.'", TextGerman = "Sie sagten: Wir wollen von ihr essen und, daß unsere Herzen Ruhe finden und, daß wir wissen, daß du zu uns tatsächlich die Wahrheit gesprochen hast und, daß wir zu denen gehören, die darüber Zeugnis ablegen (können)." });
                        surah5.VerseList.Add(new Verse { Number = 114, TextBosnian = "Isa, sin Merjemin, reče: 'O Allahu, Gospodaru naš, spusti nam s neba trpezu da nam bude praznik, i prvima od nas i onima kasnijim, i znak Tvoj, i opskrbi nas, a Ti si Opskrbitelj najbolji!'", TextEnglish = "Said Jesus, the son of Mary, 'O Allāh, our Lord, send down to us a table [spread with food] from the heaven to be for us a festival for the first of us and the last of us and a sign from You. And provide for us, and You are the best of providers.'", TextGerman = "Isa, der Sohn Maryams, sagte: O Allah, unser Herr, sende zu uns einen Tisch (mit Speisen) vom Himmel herab, der für uns, für den ersten von uns und den letzten von uns, ein Fest sein soll und ein Zeichen von Dir. Und versorge uns. Du bist ja der beste Versorger." });
                        surah5.VerseList.Add(new Verse { Number = 115, TextBosnian = "Allah reče: 'Ja ću vam je, zaista, spustiti, ali ću one među vama koji i poslije budu poricali podvrgnuti patnji kakvom nikoga među svjetovima neću mučiti.'", TextEnglish = "Allāh said, 'Indeed, I will send it down to you, but whoever disbelieves afterwards from among you - then indeed will I punish him with a punishment by which I have not punished anyone among the worlds.'", TextGerman = "Allah sagte: Ich werde ihn gewiß zu euch hinabsenden. Wer von euch aber danach ungläubig ist, den werde Ich mit einer Strafe strafen, mit der Ich (sonst) niemanden (anderen) der Weltenbewohner strafe." });
                        surah5.VerseList.Add(new Verse { Number = 116, TextBosnian = "A kada Allah rekne: 'O Isa, sine Merjemin, jesi li ti govorio ljudima: 'Prihvatite mene i majku moju kao dva boga uz Allaha!,'' on će reći: 'Uzvišen si Ti od toga! Meni nije prilično da govorim ono što nemam pravo. Ako sam ja to govorio, Ti to već znaš; Ti znaš šta je u meni, a ja ne znam šta je u Tebi; baš Ti, zaista, dobro znaš ono što je čulima nedokučivo.'", TextEnglish = "And [beware the Day] when Allāh will say, 'O Jesus, Son of Mary, did you say to the people, 'Take me and my mother as deities besides Allāh?'' He will say, 'Exalted are You! It was not for me to say that to which I have no right. If I had said it, You would have known it. You know what is within myself, and I do not know what is within Yourself. Indeed, it is You who is Knower of the unseen.", TextGerman = "Und wenn Allah sagt: O ’Isa, Sohn Maryams, bist du es, der zu den Menschen gesagt hat: ,Nehmt mich und meine Mutter außer Allah zu Göttern!’?, wird er sagen: Preis sei Dir! Es steht mir nicht zu, etwas zu sagen, wozu ich kein Recht habe. Wenn ich es (tatsächlich doch) gesagt hätte, dann wüßtest Du es bestimmt. Du weißt, was in mir vorgeht, aber ich weiß nicht, was in Dir vorgeht. Du bist ja der Allwisser der verborgenen Dinge." });
                        surah5.VerseList.Add(new Verse { Number = 117, TextBosnian = "Ja sam im samo govorio ono što si im Ti naredio: 'Ibadet činite Allahu, i mome i vašem Gospodaru!' I ja sam njima svjedok bio dok sam među njima bio, a kad si me Ti k Sebi uzeo, Ti si ih jedini nadzirao; Ti nad svime bdiješ.", TextEnglish = "I said not to them except what You commanded me - to worship Allāh, my Lord and your Lord. And I was a witness over them as long as I was among them; but when You took me up, You were the Observer over them, and You are, over all things, Witness.", TextGerman = "Ich habe ihnen nur gesagt, was Du mir befohlen hast (, nämlich): ,Dient Allah, meinem und eurem Herrn! Und ich war über sie Zeuge, solange ich unter ihnen weilte. Seitdem Du mich abberufen hast, bist Du der Wächter über sie. Du bist über alles Zeuge." });
                        surah5.VerseList.Add(new Verse { Number = 118, TextBosnian = "Ako ih patnji podvrgneš, robovi su Tvoji, a ako im oprostiš, pa, uistinu si Ti Silni i Mudri.", TextEnglish = "If You should punish them - indeed they are Your servants; but if You forgive them - indeed it is You who is the Exalted in Might, the Wise.'", TextGerman = "Wenn Du sie strafst, so sind sie Deine Diener, und wenn Du ihnen vergibst, so bist Du ja der Allmächtige und Allweise." });
                        surah5.VerseList.Add(new Verse { Number = 119, TextBosnian = "Allah će reći: 'Ovo je Dan u kome će iskrenima od koristi iskrenost njihova biti; njima će džennetske bašče, kroz koje teku rijeke, pripasti; vječno i dovijeka u njima će ostati. Allah će zadovoljan njima biti, a i oni Njim. To će veliki uspjeh biti.'", TextEnglish = "Allāh will say, 'This is the Day when the truthful will benefit from their truthfulness.' For them are gardens [in Paradise] beneath which rivers flow, wherein they will abide forever, Allāh being pleased with them, and they with Him. That is the great attainment.", TextGerman = "Allah wird sagen: Dies ist der Tag, an dem den Wahrhaftigen ihre Wahrhaftigkeit nützt. Für sie sind Gärten, durcheilt von Bächen, ewig und auf immer darin zu bleiben. Allah hat Wohlgefallen an ihnen, und sie haben Wohlgefallen an Ihm. Das ist der großartige Erfolg." });
                        surah5.VerseList.Add(new Verse { Number = 120, TextBosnian = "Allahova je vlast na nebesima i na Zemlji i nad onim što je na njima; On nad svime ima moć.", TextEnglish = "To Allāh belongs the dominion of the heavens and the earth and whatever is within them. And He is over all things competent.", TextGerman = "Allah gehört die Herrschaft über die Himmel und die Erde und das, was in ihnen ist. Und Er hat zu allem die Macht." });
                        result = surah5;
                        break;
                    #endregion

                    // 
                    #region 6 Al-Anam
                    case 6:
                        Surah surah6 = new Surah(); surah6.Name = Constants.Surah6Name; surah6.Number = 6; surah6.VerseList = new List<Verse>();
                        surah6.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah6.VerseList.Add(new Verse { Number = 1, TextBosnian = "Sva hvala neka je Allahu Koji je nebesa i Zemlju stvorio i tmine i svjetlo dao, ali i pored toga oni koji ne vjeruju - druge sa Gospodarom svojim izjednačuju.", TextEnglish = "[All] praise is [due] to Allāh, who created the heavens and the earth and made the darkness and the light. Then those who disbelieve equate [others] with their Lord.", TextGerman = "(Alles) Lob gehört Allah, Der die Himmel und die Erde erschaffen und die Finsternisse und das Licht gemacht hat; dennoch setzen diejenigen, die ungläubig sind, ihrem Herrn (andere) gleich." });
                        surah6.VerseList.Add(new Verse { Number = 2, TextBosnian = "On vas je od zemlje stvorio i rok odredio, a u Njega je i rok utvrđeni, a vi i pored toga sumnjate.", TextEnglish = "It is He who created you from clay and then decreed a term[291] and a specified time [known] to Him;[292] then [still] you are in dispute.", TextGerman = "Er ist es, Der euch aus Lehm erschaffen und hierauf eine Frist bestimmt hat. Und (es gibt) eine (andere) festgesetzte Frist bei Ihm; dennoch zweifelt ihr." });
                        surah6.VerseList.Add(new Verse { Number = 3, TextBosnian = "On je Allah na nebesima i na Zemlji, On zna i što krijete i što pokazujete, i On zna ono što radite.", TextEnglish = "And He is Allāh, [the only deity] in the heavens and the earth. He knows your secret and what you make public, and He knows that which you earn.", TextGerman = "Er ist Allah in den Himmeln und auf der Erde. Er kennt euer Geheimes und euer Verlautbartes und weiß, was ihr verdient." });
                        surah6.VerseList.Add(new Verse { Number = 4, TextBosnian = "A njima - nevjernicima ne dođe nijedan dokaz od Gospodara njihova a da se od njega ne okrenu.", TextEnglish = "And no sign comes to them from the signs of their Lord except that they turn away therefrom.", TextGerman = "Kein Zeichen von den Zeichen ihres Herrn kam zu ihnen, ohne daß sie sich von ihm abzuwenden pflegten." });
                        surah6.VerseList.Add(new Verse { Number = 5, TextBosnian = "Oni su zanijekali Istinu kada im je došla, ali njima će sigurno stići vijesti o onome čemu su se rugali.", TextEnglish = "For they had denied the truth when it came to them, but there is going to reach them the news of what they used to ridicule.[293]", TextGerman = "So erklärten sie die Wahrheit für Lüge, als sie zu ihnen kam. Aber zu ihnen werden die Nachrichten kommen von dem, worüber sie sich lustig zu machen pflegten." });
                        surah6.VerseList.Add(new Verse { Number = 6, TextBosnian = "Zar oni ne znaju koliko smo Mi naraštaja prije njih uništili, kojima smo na Zemlji mogućnosti davali kakve vama nismo dali i kojima smo kišu obilatu slali i učinili da rijeke pored njih teku, pa smo ih, zbog grijeha njihovih, uništavali, i druge naraštaje, poslije njih, stvarali?!", TextEnglish = "Have they not seen how many generations We destroyed before them which We had established upon the earth as We have not established you? And We sent [rain from] the sky upon them in showers and made rivers flow beneath them; then We destroyed them for their sins and brought forth after them a generation of others.", TextGerman = "Haben sie nicht gesehen, wie viele Geschlechter Wir vor ihnen vernichteten, denen Wir auf der Erde eine feste Stellung verliehen hatten, wie Wir sie euch nicht verliehen haben, und auf die Wir den Regen ergiebig (hinab)sandten, und unterhalb derer Wir Flüsse strömen ließen? Und da haben Wir sie für ihre Sünden vernichtet und nach ihnen ein anderes Geschlecht entstehen lassen." });
                        surah6.VerseList.Add(new Verse { Number = 7, TextBosnian = "A i da ti Knjigu na papiru spustimo i da je oni rukama svojim opipaju, opet bi oni koji ne vjeruju rekli: 'Ovo zaista nije ništa drugo do jasna vradžbina.'", TextEnglish = "And even if We had sent down to you, [O Muḥammad], a written scripture on a page and they touched it with their hands, the disbelievers would say, 'This is not but obvious magic.'", TextGerman = "Wenn Wir auf dich ein Buch aus (beschriebenen) Blättern hinabgesandt hätten, so daß sie es mit ihren Händen befühlen (könnten), würden diejenigen, die ungläubig sind, dennoch sagen: Das ist ja nur deutliche Zauberei." });
                        surah6.VerseList.Add(new Verse { Number = 8, TextBosnian = "I još govore: 'Zašto mu ne bude poslan melek?!' A da meleke pošaljemo, slučaj bi određen bio, i više se na njih ne bi čekalo.", TextEnglish = "And they say, 'Why was there not sent down to him an angel?'[294] But if We had sent down an angel, the matter would have been decided;[295] then they would not be reprieved.", TextGerman = "Und sie sagen: Warum ist kein Engel zu ihm herabgesandt worden? Wenn Wir aber einen Engel herabsenden würden, dann wäre die Angelegenheit wahrlich entschieden, und ihnen würde dann kein Aufschub gewährt." });
                        surah6.VerseList.Add(new Verse { Number = 9, TextBosnian = "A da ga melekom učinimo, opet bismo ga kao čovjeka stvorili i opet bismo im učinili nejasnim ono što im jasno nije.", TextEnglish = "And if We had made him [i.e., the messenger] an angel, We would have made him [appear as] a man, and We would have covered them with that in which they cover themselves [i.e., confusion and doubt].", TextGerman = "Und wenn Wir ihn (den Gesandten auch) zu einem Engel gemacht hätten, so hätten Wir ihn (doch) wahrlich zu einem Mann gemacht, und Wir hätten ihnen wahrlich verdeckt, was sie zu verdecken suchten." });
                        surah6.VerseList.Add(new Verse { Number = 10, TextBosnian = "Poslanicima su se i prije tebe rugali, pa je one koji su im se rugali stiglo baš ono čemu su se rugali.", TextEnglish = "And already were messengers ridiculed before you, but those who mocked them were enveloped by that which they used to ridicule.", TextGerman = "Man machte sich ja schon vor dir über Gesandte lustig. Da umschloß diejenigen, die über sie spotteten, das, worüber sie sich lustig zu machen pflegten." });
                        surah6.VerseList.Add(new Verse { Number = 11, TextBosnian = "Reci: 'Putujte po Zemlji, zatim pogledajte kako su završili oni koji su poslanike lažnim smatrali!'", TextEnglish = "Say, 'Travel through the land; then observe how was the end of the deniers.'", TextGerman = "Sag: Reist auf der Erde umher und schaut, wie das Ende der Leugner war." });
                        surah6.VerseList.Add(new Verse { Number = 12, TextBosnian = "Upitaj: 'Čije je sve ono što je na nebesima i na Zemlji?', i odgovori: 'Allahovo!' On je sebi propisao da bude milostiv. On će vas na Kijametskom danu sakupiti, u to nema nikakve sumnje; oni koji su sebe upropastili, pa - oni ne vjeruju.", TextEnglish = "Say, 'To whom belongs whatever is in the heavens and earth?' Say, 'To Allāh.' He has decreed upon Himself mercy. He will surely assemble you for the Day of Resurrection, about which there is no doubt. Those who will lose themselves [that Day] do not believe.", TextGerman = "Sag: Wem gehört, was in den Himmeln und auf der Erde ist? Sag: Allah. Er hat Sich Selbst Barmherzigkeit vorgeschrieben. Er wird euch ganz gewiß zum Tag der Auferstehung versammeln, an dem es keinen Zweifel gibt. (Schaut; wie das Ende) derjenigen (war), die ihre Seelen verloren haben, denn sie glaubten nicht." });
                        surah6.VerseList.Add(new Verse { Number = 13, TextBosnian = "Njemu pripada sve što postoji u noći i danu; On je Svečujući i Sveznajući.", TextEnglish = "And to Him belongs that which reposes by night and by day, and He is the Hearing, the Knowing.", TextGerman = "Ihm gehört (alles), was in der Nacht und am Tag ruht. Und Er ist der Allhörende und Allwissende." });
                        surah6.VerseList.Add(new Verse { Number = 14, TextBosnian = "Reci: 'Zar da za zaštitnika uzmem nekog drugog osim Allaha Koji je nebesa i Zemlju, bez prethodnog primjera stvorio?! On hrani, a hranjen ne biva!' Reci: 'Meni je naređeno da budem prvi ko se pokorava', i nikako ne budi jedan od višebožaca.", TextEnglish = "Say, 'Is it other than Allāh I should take as a protector, Creator of the heavens and earth, while it is He who feeds and is not fed?' Say, [O Muḥammad], 'Indeed, I have been commanded to be the first [among you] who submit [to Allāh] and [was commanded], 'Do not ever be of the polytheists.'", TextGerman = "Sag: Sollte ich einen anderen zum Schutzherren nehmen als Allah, den Erschaffer der Himmel und der Erde, Der Speise gibt, während Ihm (selbst) keine Speise gegeben wird? Sag: Mir ist befohlen worden, der erste zu sein, der sich (Allah) ergibt, und: Gehöre ja nicht zu den Götzendienern!" });
                        surah6.VerseList.Add(new Verse { Number = 15, TextBosnian = "Reci: 'Ako budem neposlušan svome Gospodaru, ja se plašim patnje na Velikom danu.'", TextEnglish = "Say, 'Indeed I fear, if I should disobey my Lord, the punishment of a tremendous Day.'", TextGerman = "Sag: Gewiß, ich fürchte, wenn ich mich meinem Herrn widersetze, die Strafe eines gewaltigen Tages." });
                        surah6.VerseList.Add(new Verse { Number = 16, TextBosnian = "A onaj ko toga dana bude pošteđen, On mu se smilovao, i to je očiti uspjeh.", TextEnglish = "He from whom it is averted that Day - [Allāh] has granted him mercy. And that is the clear attainment.", TextGerman = "Von wem sie an jenem Tag abgewendet wird, dessen hat Er sich wirklich erbarmt; und das ist der deutliche Erfolg." });
                        surah6.VerseList.Add(new Verse { Number = 17, TextBosnian = "Ako te Allah nekom nevoljom pogodi - pa, niko je osim Njega ne može otkloniti; a ako ti kakvo dobro podari- pa, On nad svime ima moć.", TextEnglish = "And if Allāh should touch you with adversity, there is no remover of it except Him. And if He touches you with good - then He is over all things competent.", TextGerman = "Wenn Allah dir Unheil widerfahren läßt, so kann es keiner hinwegnehmen außer Ihm. Und wenn Er dir (etwas) Gutes widerfahren läßt, - so hat Er zu allem die Macht." });
                        surah6.VerseList.Add(new Verse { Number = 18, TextBosnian = "A On je iznad robova Svojih Svepotčinitelj- i On je Mudri, Onaj Koji sve u potpunosti zna.", TextEnglish = "And He is the subjugator over His servants. And He is the Wise,[296] the Aware.[297]", TextGerman = "Er ist der Bezwinger über Seinen Dienern, und Er ist der Allweise und Allkundige." });
                        surah6.VerseList.Add(new Verse { Number = 19, TextBosnian = "Reci: 'Šta je po svjedočenju najveće?', i odgovori: 'Allah, On će između mene i vas svjedok biti. A meni se ovaj Kur'an objavljuje da njime vas i one do kojih on dopre upozoravam. Zar vi, zaista, tvrdite da pored Allaha ima i drugih bogova?' Reci: 'Ja ne tvrdim.' Reci: 'On je Bog Jedini, i ja nemam ništa s time što vi Njemu pridružujete.'", TextEnglish = "Say, 'What thing is greatest in testimony?' Say, 'Allāh is witness between me and you. And this Qur’ān was revealed to me that I may warn you thereby and whomever it reaches.[298] Do you [truly] testify that with Allāh there are other deities?' Say, 'I will not testify [with you].' Say, 'Indeed, He is but one God, and indeed, I am free of what you associate [with Him].'", TextGerman = "Sag: Welches ist das größte Zeugnis? Sag: Allah (, Er) ist Zeuge zwischen mir und euch. Und dieser Qur’an ist mir eingegeben worden, damit ich euch und (jeden), den er erreicht, mit ihm warne. Wollt ihr denn wahrlich bezeugen, daß es neben Allah andere Götter gibt? Sag: Ich bezeuge (es) nicht. Sag: Er ist nur ein Einziger Gott, und ich sage mich von dem los, was ihr (Ihm) beigesellt." });
                        surah6.VerseList.Add(new Verse { Number = 20, TextBosnian = "Oni kojima smo Knjigu dali poznaju Poslanika kao što poznaju sinove svoje. Oni koji su sebe upropastili, pa - oni ne vjeruju.", TextEnglish = "Those to whom We have given the Scripture recognize it[299] as they recognize their [own] sons. Those who will lose themselves [in the Hereafter] do not believe.", TextGerman = "Diejenigen, denen Wir die Schrift gegeben haben, kennen ihn, wie sie ihre Söhne kennen. (Es sind) diejenigen, die sich selbst verloren haben, denn sie glauben nicht." });
                        surah6.VerseList.Add(new Verse { Number = 21, TextBosnian = "Ima li većeg zulumćara od onoga koji o Allahu govori laži ili ne priznaje ajete i znakove Njegove?! Zulumćari zaista neće uspjeti.", TextEnglish = "And who is more unjust than one who invents about Allāh a lie or denies His verses? Indeed, the wrongdoers will not succeed.", TextGerman = "Und wer ist ungerechter, als wer gegen Allah eine Lüge ersinnt oder Seine Zeichen für Lüge erklärt? Gewiß, den Ungerechten wird es nicht wohl ergehen." });
                        surah6.VerseList.Add(new Verse { Number = 22, TextBosnian = "A na Dan kada ih sve sakupimo, pa upitamo one koji su druge Njemu u obožavanju pridruživali: 'Gdje su vam božanstva vaša koja ste za bogove smatrali?',", TextEnglish = "And [mention, O Muḥammad], the Day We will gather them all together; then We will say to those who associated others with Allāh, 'Where are your 'partners' that you used to claim [with Him]?'", TextGerman = "Und an dem Tag, da Wir sie alle versammeln, und dann werden Wir zu denen, die (Allah etwas) beigesellt hatten, sagen: Wo sind (nun) eure Teilhaber, die ihr stets angegeben habt?" });
                        surah6.VerseList.Add(new Verse { Number = 23, TextBosnian = "neće im nikakvo opravdanje preostati nego da reknu: 'Allaha nam, Gospodara našeg, mi nismo višebošci bili.'", TextEnglish = "Then there will be no [excuse upon] examination except they will say, 'By Allāh, our Lord, we were not those who associated.'", TextGerman = "Dann wird ihre Ausrede nur sein, daß sie sagen: Bei Allah, unserem Herrn! Wir waren keine Götzendiener." });
                        surah6.VerseList.Add(new Verse { Number = 24, TextBosnian = "Gledaj kako će oni sami sebi lagati, a zagubit će im se oni koje su bili izmislili.", TextEnglish = "See how they will lie about themselves. And lost from them will be what they used to invent.", TextGerman = "Schau, wie sie gegen sich selbst lügen und (wie) ihnen das entschwunden ist, was sie zu ersinnen pflegten!" });
                        surah6.VerseList.Add(new Verse { Number = 25, TextBosnian = "Ima onih koji dolaze da te slušaju, ali Mi smo na srca njihova zastore stavili, da Kur'an ne bi razumjeli, i gluhim ih učinili, pa i ako bi sve dokaze vidjeli, opet u njih ne bi povjerovali. A kada ti dolaze da s tobom raspravljaju, govore oni koji ne vjeruju: 'To su samo izmišljotine naroda drevnih.'", TextEnglish = "And among them are those who listen to you,[300] but We have placed over their hearts coverings, lest they understand it, and in their ears deafness. And if they should see every sign, they will not believe in it. Even when they come to you arguing with you, those who disbelieve say, 'This is not but legends of the former peoples.'", TextGerman = "Und unter ihnen gibt es manche, die dir zuhören. Aber Wir haben auf ihre Herzen Hüllen gelegt, so daß sie ihn nicht verstehen, und in ihre Ohren Schwerhörigkeit. Auch wenn sie jedes Zeichen sähen, glaubten sie nicht daran. Wenn sie zu dir kommen, um mit dir zu streiten, sagen diejenigen, die ungläubig sind: Das sind nur Fabeln der Früheren." });
                        surah6.VerseList.Add(new Verse { Number = 26, TextBosnian = "Oni zabranjuju da se u Kur'an vjeruje, i sami se od njega udaljavaju, i sami sebe upropaštavaju, a da i ne primjećuju.", TextEnglish = "And they prevent [others] from him and are [themselves] remote from him. And they do not destroy except themselves, but they perceive [it] not.", TextGerman = "Sie verbieten es (anderen) und halten sich (selbst) davon fern. Sie vernichten nur sich selbst, ohne zu merken." });
                        surah6.VerseList.Add(new Verse { Number = 27, TextBosnian = "A da ti je vidjeti kako će, kad pred Vatrom budu zadržani, reći: 'Da nam je da vraćeni budemo, pa da ajete i znakove Gospodara našeg ne poričemo i da vjernici postanemo.'", TextEnglish = "If you could but see when they are made to stand before the Fire and will say, 'Oh, would that we could be returned [to life on earth] and not deny the signs of our Lord and be among the believers.'", TextGerman = "Und wenn du sehen würdest, wenn sie vor das (Höllen)feuer gestellt werden! Dann werden sie sagen: O würden wir doch zurückgebracht werden! Wir würden (dann) nicht die Zeichen unseres Herrn für Lüge erklären, sondern würden zu den Gläubigen gehören." });
                        surah6.VerseList.Add(new Verse { Number = 28, TextBosnian = "Ne, naprotiv!, njima će očevidno postati ono što su prije krili; a kada bi ponovo bili vraćeni, opet bi nastavili raditi ono što im je bilo zabranjeno, jer oni su, doista, lažljivci,", TextEnglish = "But what they concealed before has [now] appeared to them. And even if they were returned, they would return to that which they were forbidden; and indeed, they are liars.", TextGerman = "Nein! Vielmehr hat sich ihnen offenkundig gezeigt, was sie zuvor zu verbergen pflegten. Und wenn sie zurückgebracht würden, würden sie doch wieder zu dem zurückkehren, was ihnen verboten wurde. Sie sind wahrlich Lügner." });
                        surah6.VerseList.Add(new Verse { Number = 29, TextBosnian = "i rekli bi: 'Nema života osim na dunjaluku i mi oživljeni biti nećemo.'", TextEnglish = "And they say, 'There is none but our worldly life, and we will not be resurrected.'", TextGerman = "Und sie sagen: Es gibt nur unser diesseitiges Leben, und wir werden nicht auferweckt." });
                        surah6.VerseList.Add(new Verse { Number = 30, TextBosnian = "A da ti je vidjeti kako će, kada pred Gospodarem svojim budu zaustavljeni i kad ih On upita: 'Zar ovo nije istina?', odgovoriti: 'Jeste, tako nam Gospodara našeg!', a i kako će On reći: 'E, pa iskusite onda patnju zbog toga što niste vjerovali!'", TextEnglish = "If you could but see when they will be made to stand before their Lord. He will say, 'Is this not the truth?' They will say, 'Yes, by our Lord.' He will [then] say, 'So taste the punishment for what you used to deny.'", TextGerman = "Und wenn du sehen würdest, wenn sie vor ihren Herrn gestellt werden! Er wird sagen: Ist dies nicht die Wahrheit? Sie sagen: Ja doch, bei unserem Herrn! Er wird sagen: Kostet nun die Strafe dafür, daß ihr stets ungläubig wart." });
                        surah6.VerseList.Add(new Verse { Number = 31, TextBosnian = "Oni koji ne vjeruju da će pred Allaha stati nastradat će kad im iznenada Čas oživljenja dođe, i reći će: 'O žalosti naše, šta smo sve na Zemlji propustili!', i grijehe će svoje na leđima nositi, a užasno je ono što će uprtiti.", TextEnglish = "Those will have lost who deny the meeting with Allāh, until when the Hour [of resurrection] comes upon them unexpectedly, they will say, 'Oh, [how great is] our regret over what we neglected concerning it [i.e., the Hour],' while they bear their burdens [i.e., sins] on their backs. Unquestionably, evil is that which they bear.", TextGerman = "Verloren sind diejenigen, die die Begegnung mit Allah für Lüge erklären, so daß, wenn die Stunde plötzlich über sie kommt, sie sagen: O welch gramvolle Reue für uns, wegen dessen, was wir hinsichtlich ihrer vernachlässigt haben! Sie tragen ihre Lasten auf ihren Rücken. Wie übel ist das, was sie an Last auf sich nehmen!" });
                        surah6.VerseList.Add(new Verse { Number = 32, TextBosnian = "Život na dunjaluku samo je igra i zabava, a ahiret je, zaista, bolji za one koji se nastoje uščuvati. Zar ne razumijete?!", TextEnglish = "And the worldly life is not but amusement and diversion; but the home of the Hereafter is best for those who fear Allāh, so will you not reason?", TextGerman = "Das diesseitige Leben ist nur Spiel und Zerstreuung. Die jenseitige Wohnstätte ist für diejenigen, die gottesfürchtig sind, wahrlich besser. Begreift ihr denn nicht?" });
                        surah6.VerseList.Add(new Verse { Number = 33, TextBosnian = "Mi znamo da tebe zaista žalosti što oni govore. Oni, uistinu, ne smatraju da si lažac, nego zulumćari poriču Allahove riječi.", TextEnglish = "We know that you, [O Muḥammad], are saddened by what they say. And indeed, they do not call you untruthful, but it is the verses of Allāh that the wrongdoers reject.", TextGerman = "Wir wissen wohl, daß dich das, was sie sagen, in der Tat traurig macht. Aber nicht dich bezichtigen sie (in Wirklichkeit) der Lüge, sondern die Zeichen Allahs verleugnen die(se) Ungerechten." });
                        surah6.VerseList.Add(new Verse { Number = 34, TextBosnian = "A poslanici su prije tebe lažnim smatrani, pa su trpjeli što su ih u laž ugonili i mučili sve dok im ne bi došla pomoć Naša - a niko ne može Allahove riječi zamijeniti - i do tebe su doprle o poslanicima neke vijesti.", TextEnglish = "And certainly were messengers denied before you, but they were patient over the denial, and they were harmed until Our victory came to them. And none can alter the words [i.e., decrees] of Allāh. And there has certainly come to you some information about the [previous] messengers.", TextGerman = "Ja, bereits vor dir wurden Gesandte der Lüge bezichtigt. Und da ertrugen sie standhaft, daß sie der Lüge bezichtigt wurden und daß ihnen Leid zugefügt wurde, bis Unsere Hilfe zu ihnen kam. Es gibt nichts, was Allahs Worte abändern könnte. Dir ist ja bereits Kunde von den Gesandten zugekommen." });
                        surah6.VerseList.Add(new Verse { Number = 35, TextBosnian = "Ako je tebi teško to što se oni okreću, onda, ako možeš, potraži kakav otvor u Zemlji ili kakve ljestve za nebo, pa im donesi drugi znak?! Da Allah hoće, On bi ih sve na pravom putu sakupio; zato, nikako ne budi od onih koji ne znaju.", TextEnglish = "And if their evasion is difficult for you, then if you are able to seek a tunnel into the earth or a stairway into the sky to bring them a sign, [then do so]. But if Allāh had willed, He would have united them upon guidance. So never be of the ignorant.", TextGerman = "Und wenn dir ihre Ablehnung schwer zusetzt, dann, (selbst) wenn du einen Gang in die Erde oder eine Leiter in den Himmel ausfindig machen kannst, und ihnen dann ein Zeichen bringen kannst, ... Und wenn Allah gewollt hätte, hätte Er sie wahrlich unter der Rechtleitung zusammengeführt. Gehöre also ja nicht zu den Toren!" });
                        surah6.VerseList.Add(new Verse { Number = 36, TextBosnian = "Odazvat će se jedino oni koji slušaju. A Allah će umrle oživiti; zatim će se Njemu svi vratiti.", TextEnglish = "Only those who hear will respond. But the dead[301] - Allāh will resurrect them; then to Him they will be returned.", TextGerman = "Nur diejenigen können Folge leisten, die (zu)hören. Die Toten aber wird Allah auferwecken. Hierauf werden sie zu Ihm zurückgebracht." });
                        surah6.VerseList.Add(new Verse { Number = 37, TextBosnian = "Oni govore: 'Zašto mu ne bude spušten od njegova Gospodara kakav znak.' Reci: 'Allah je, zaista, kadar spustiti znak, ali većina njih ne zna.'", TextEnglish = "And they say, 'Why has a sign not been sent down to him from his Lord?' Say, 'Indeed, Allāh is Able to send down a sign, but most of them do not know.'", TextGerman = "Und sie sagen: Wenn ihm doch ein Zeichen von seinem Herrn offenbart worden wäre! Sag: Gewiß, Allah hat die Macht, ein Zeichen zu offenbaren. Aber die meisten von ihnen wissen nicht." });
                        surah6.VerseList.Add(new Verse { Number = 38, TextBosnian = "Nema ni jednog živog bića koje po zemlji hodi, niti ptice koja na krilima svojim leti, a da nisu zajednice poput vas. U Knjizi Mi ništa izostavili nismo - a naposljetku, svi će pred Gospodarom svojim biti sakupljeni.", TextEnglish = "And there is no creature on [or within] the earth or bird that flies with its wings except [that they are] communities like you. We have not neglected in the Register[302] a thing. Then unto their Lord they will be gathered.", TextGerman = "Es gibt kein Tier auf der Erde und keinen Vogel, der mit seinen Flügeln fliegt, die nicht Gemeinschaften wären gleich euch. Wir haben im Buch nichts vernachlässigt. Hierauf werden sie zu ihrem Herrn versammelt." });
                        surah6.VerseList.Add(new Verse { Number = 39, TextBosnian = "A oni koji ajete i znakove Naše poriču gluhi su i nijemi, u tminama su. Onoga koga hoće - Allah na stranputicu skrene, a onome kome hoće - da da na Pravom putu bude.", TextEnglish = "But those who deny Our verses are deaf and dumb within darknesses. Whomever Allāh wills - He sends astray; and whomever He wills - He puts him on a straight path.", TextGerman = "Und diejenigen, die Unsere Zeichen für Lüge erklären, sind taub und stumm in Finsternissen. Wen Allah will, den läßt Er in die Irre gehen, und wen Er will, den bringt Er auf einen geraden Weg." });
                        surah6.VerseList.Add(new Verse { Number = 40, TextBosnian = "Reci: 'Kažite vi meni, ako istinu govorite, kad bi vam došla Allahova kazna, ili vam dođe Čas sudnji, da li biste ikog drugog osim Allaha prizivali?'", TextEnglish = "Say, 'Have you considered:[303] if there came to you the punishment of Allāh or there came to you the Hour - is it other than Allāh you would invoke, if you should be truthful?'", TextGerman = "Sag: Was meint ihr wohl? Wenn die Strafe Allahs (über) euch kommt oder die Stunde (über) euch kommt, werdet ihr (dann) einen anderen anrufen als Allah? (So antwortet,) wenn ihr wahrhaftig seid." });
                        surah6.VerseList.Add(new Verse { Number = 41, TextBosnian = "Naprotiv, Njega samo molite pa, ako hoće, otklonit će od vas ono za što ste Ga molili, a zaboravljate one koje ste Mu u obožavanju pridruživali.", TextEnglish = "No, it is Him [alone] you would invoke, and He would remove that for which you invoked Him if He willed, and you would forget what you associate [with Him].", TextGerman = "Nein! Vielmehr Ihn werdet ihr anrufen. Dann wird Er das hinwegnehmen, weswegen ihr (Ihn) anruft, wenn Er will, und ihr werdet vergessen, was ihr (Ihm) beigesellt." });
                        surah6.VerseList.Add(new Verse { Number = 42, TextBosnian = "A poslanike smo i narodima prije tebe slali i neimaštinom i bolešću ih kažnjavali, ne bi li se ponizno molili.", TextEnglish = "And We have already sent [messengers] to nations before you, [O Muḥammad]; then We seized them with poverty and hardship that perhaps they might humble themselves [to Us].", TextGerman = "Wir haben ja schon zu Gemeinschaften vor dir (Gesandte) gesandt und über sie Not und Leid kommen lassen, auf daß sie unterwürfig flehen mögen." });
                        surah6.VerseList.Add(new Verse { Number = 43, TextBosnian = "Pa zašto se nisu ponizno molili kada bi im kazna Naša došla?! Ali, srca njihova ostajala su tvrda, a šejtan im je lijepim prikazao ono što su radili.", TextEnglish = "Then why, when Our punishment came to them, did they not humble themselves? But their hearts became hardened, and Satan made attractive to them that which they were doing.", TextGerman = "Wenn sie doch nur, als Unsere Gewalt über sie kam, unterwürfig gefleht hätten! Aber ihre Herzen verhärteten sich, und der Satan schmückte ihnen aus, was sie zu tun pflegten." });
                        surah6.VerseList.Add(new Verse { Number = 44, TextBosnian = "I kada bi zaboravili ono čime su opominjani, Mi bismo im kapije svega otvorili; a kada bi se onome što im je dato obradovali, iznenada bismo ih kaznili i oni bi, odjednom, svaku nadu izgubili,", TextEnglish = "So when they forgot that by which they had been reminded,[304] We opened to them the doors of every [good] thing until, when they rejoiced in that which they were given, We seized them suddenly, and they were [then] in despair.", TextGerman = "Als sie dann vergaßen, womit sie ermahnt worden waren, öffneten Wir ihnen die Tore zu allen Dingen. Als sie dann froh waren über das, was ihnen gegeben worden war, ergriffen Wir sie plötzlich. Da waren sie sogleich verzweifelt." });
                        surah6.VerseList.Add(new Verse { Number = 45, TextBosnian = "i do zadnjeg bi bio iskorijenjen narod koji je zulum činio; pa neka je hvaljen Allah, Gospodar svjetova.", TextEnglish = "So the people that committed wrong were eliminated. And praise to Allāh, Lord of the worlds.", TextGerman = "So soll die Rückkehr der Leute, die Unrecht taten, abgeschnitten sein. (Alles) Lob gehört Allah, dem Herrn der Weltenbewohner!" });
                        surah6.VerseList.Add(new Verse { Number = 46, TextBosnian = "Reci: 'Kažite vi meni, ako bi vam Allah sluh vaš i vid vaš oduzeo i srca vaša zapečatio, koji bi vam bog, osim Allaha, to vratio?!' Pogledaj kako ajete i znakove izlažemo, a oni se opet okreću.'", TextEnglish = "Say, 'Have you considered:[305] if Allāh should take away your hearing and your sight and set a seal upon your hearts, which deity other than Allāh could bring them [back] to you?' Look how We diversify[306] the verses; then they [still] turn away.", TextGerman = "Sag: Was meint ihr, wenn Allah euer Gehör und euer Augenlicht (weg)nähme und eure Herzen versiegelte, wer ist der Gott außer Allah, der es euch zurückgeben könnte? Schau, wie Wir die Zeichen verschiedenartig darlegen! Dennoch wenden sie sich hierauf ab." });
                        surah6.VerseList.Add(new Verse { Number = 47, TextBosnian = "Reci: 'Kažite vi meni, ako bi vas Allahova kazna stigla iznenada ili očigledno, zar bi iko drugi osim ljudi zulumćara nastradao?!'", TextEnglish = "Say, 'Have you considered: if the punishment of Allāh should come to you unexpectedly or manifestly,[307] will any be destroyed but the wrongdoing people?'", TextGerman = "Sag: Was meint ihr wohl? Wenn Allahs Strafe plötzlich oder offen (über) euch kommt, wer wird (dann) vernichtet außer dem ungerechten Volk?" });
                        surah6.VerseList.Add(new Verse { Number = 48, TextBosnian = "Mi šaljemo poslanike samo zato da donose radosne vijesti i da opominju; neka se zato oni koji vjeruju i dobra djela čine ničega ne boje i ni za čime neka ne tuguju.", TextEnglish = "And We send not the messengers except as bringers of good tidings and warners. So whoever believes and reforms - there will be no fear concerning them, nor will they grieve.", TextGerman = "Wir senden die Gesandten nur als Verkünder froher Botschaft und als Überbringer von Warnungen. Wer also glaubt und Besserung bringt, über die soll keine Furcht kommen, noch sollen sie traurig sein." });
                        surah6.VerseList.Add(new Verse { Number = 49, TextBosnian = "A one koji su ajete i znakove Naše poricali stići će kazna zato što su buntovni bili.", TextEnglish = "But those who deny Our verses - the punishment will touch[308] them for their defiant disobedience.", TextGerman = "Denjenigen aber, die Unsere Zeichen für Lüge erklären, wird die Strafe widerfahren dafür, daß sie zu freveln pflegten." });
                        surah6.VerseList.Add(new Verse { Number = 50, TextBosnian = "Reci: 'Ja vam ne kažem: 'U mene su Allahove riznice', niti: 'Ja ono što je čulima nedokučivo znam', niti vam kažem: 'Ja sam uistinu melek', ja slijedim samo ono što mi se objavljuje.' Reci: 'Zar su isto slijepac i onaj koji vidi?! Zašto ne razmislite?'", TextEnglish = "Say, [O Muḥammad], 'I do not tell you that I have the depositories [containing the provision] of Allāh or that I know the unseen, nor do I tell you that I am an angel. I only follow what is revealed to me.' Say, 'Is the blind equivalent to the seeing? Then will you not give thought?'", TextGerman = "Sag: Ich sage nicht zu euch, ich besäße die Schatzkammern Allahs, und ich weiß auch nicht das Verborgene; und ich sage nicht zu euch, ich sei ein Engel. Ich folge nur dem, was mir eingegeben wird. Sag: Sind (etwa) der Blinde und der Sehende gleich? Denkt ihr denn nicht nach?" });
                        surah6.VerseList.Add(new Verse { Number = 51, TextBosnian = "I upozoravaj Kur'anom one koji strahuju što će pred Gospodarom svojim sakupljeni biti, kad osim Njega ni zaštitnika ni zagovornika neće imati - da bi se uščuvali.", TextEnglish = "And warn by it [i.e., the Qur’ān] those who fear that they will be gathered before their Lord - for them besides Him will be no protector and no intercessor - that they might become righteous.", TextGerman = "Und warne damit diejenigen, die befürchten, daß sie zu ihrem Herrn versammelt werden - sie haben außer Ihm weder Schutzherrn noch Fürsprecher -, auf daß sie gottesfürchtig werden mögen!" });
                        surah6.VerseList.Add(new Verse { Number = 52, TextBosnian = "I ne tjeraj od sebe one koji se ujutro i navečer Gospodaru svome mole u želji za licem Njegovim; ti nećeš za njih odgovarati, a ni oni neće odgovarati za tebe, jer bi ti, ako bi ih otjerao, u zulumćare spadao.", TextEnglish = "And do not send away those who call upon their Lord morning and afternoon, seeking His face [i.e., favor]. Not upon you is anything of their account and not upon them is anything of your account.[309] So were you to send them away, you would [then] be of the wrongdoers.", TextGerman = "Und weise nicht diejenigen ab, die morgens und abends ihren Herrn anrufen im Begehren nach Seinem Angesicht! Dir obliegt in keiner Weise, sie zur Rechenschaft zu ziehen, und ihnen obliegt in keiner Weise, dich zur Rechenschaft zu ziehen. Wenn du sie abwiesest, dann würdest du zu den Ungerechten gehören." });
                        surah6.VerseList.Add(new Verse { Number = 53, TextBosnian = "I tako Mi jedne drugima iskušavamo da bi nevjernici rekli: 'Zar su to oni, među nama, kojima je Allah, milost ukazao?!' A zar Allah najbolje ne poznaje zahvalne?!", TextEnglish = "And thus We have tried some of them through others that they [i.e., the disbelievers] might say, 'Is it these whom Allāh has favored among us?' Is not Allāh most knowing of those who are grateful?[310]", TextGerman = "Und so haben Wir die einen von ihnen durch die anderen einer Prüfung unterzogen, so daß sie sagen: Hat Allah diesen da aus unserer Mitte eine Wohltat erwiesen? Weiß nicht Allah am besten über die Dankbaren Bescheid?" });
                        surah6.VerseList.Add(new Verse { Number = 54, TextBosnian = "A kada ti dođu oni koji u riječi Naše vjeruju, ti reci: 'Selamun alejkum! Gospodar vaš je sam Sebi propisao da bude milostiv: Ako neko od vas kakvo ružno djelo iz lahkomislenosti učini, pa se poslije pokaje i popravi - pa, Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.'", TextEnglish = "And when those come to you who believe in Our verses, say, 'Peace be upon you. Your Lord has decreed upon Himself mercy: that any of you who does wrong out of ignorance and then repents after that and corrects himself - indeed, He is Forgiving and Merciful.'", TextGerman = "Und wenn diejenigen, die an Unsere Zeichen glauben, zu dir kommen, dann sag: Friede sei auf euch! Euer Herr hat Sich Selbst Barmherzigkeit vorgeschrieben: Wer von euch in Unwissenheit Böses tut, aber danach dann bereut und (es) wieder gutmacht, so ist Er Allvergebend und Barmherzig." });
                        surah6.VerseList.Add(new Verse { Number = 55, TextBosnian = "Tako Mi potanko izlažemo ajete i znakove, da bi očevidan bio put kojim idu prestupnici.", TextEnglish = "And thus do We detail the verses, and [thus] the way of the criminals will become evident.", TextGerman = "So legen Wir die Zeichen ausführlich dar, - damit sich der Weg der Übeltäter klar zeigt." });
                        surah6.VerseList.Add(new Verse { Number = 56, TextBosnian = "Reci: 'Meni je zabranjeno obožavati one koje vi, pored Allaha, molite.' Reci: 'Ja se ne povodim za željama vašim, jer bih tada zalutao i ne bih među upućenima bio.'", TextEnglish = "Say, 'Indeed, I have been forbidden to worship those you invoke besides Allāh.' Say, 'I will not follow your desires, for I would then have gone astray, and I would not be of the [rightly] guided.'", TextGerman = "Sag: Mir ist verboten worden, denjenigen zu dienen, die ihr anstatt Allahs anruft. Sag: Ich folge nicht euren Neigungen; ich würde sonst nämlich fürwahr abirren und würde nicht zu den Rechtgeleiteten gehören." });
                        surah6.VerseList.Add(new Verse { Number = 57, TextBosnian = "Reci: 'Ja sam zaista na jasnoj uputi moga Gospodara, a vi je poričete. Nije u mojoj vlasti ono što vi požurujete; pita se samo Allah, On sudi po pravdi i On je Sudija najbolji.'", TextEnglish = "Say, 'Indeed, I am on clear evidence from my Lord, and you have denied it. I do not have that for which you are impatient.[311] The decision is only for Allāh. He relates the truth, and He is the best of deciders.'", TextGerman = "Sag: Ich halte mich an einen klaren Beweis von meinem Herrn, während ihr Ihn der Lüge bezichtigt. Ich verfüge nicht über das, was ihr zu beschleunigen wünscht. Das Urteil gehört allein Allah. Er berichtet die Wahrheit, und Er ist der Beste derer, die entscheiden." });
                        surah6.VerseList.Add(new Verse { Number = 58, TextBosnian = "Reci: 'Da je u mojoj vlasti ono što požurujete, između mene i vas svršeno bi bilo, a Allah najbolje zna zulumćare.'", TextEnglish = "Say, 'If I had that for which you are impatient, the matter would have been decided between me and you, but Allāh is most knowing of the wrongdoers.'", TextGerman = "Sag: Wenn ich über das verfügen würde, was ihr zu beschleunigen wünscht, wäre die Angelegenheit zwischen mir und euch wahrlich schon entschieden. Allah weiß am besten über die Ungerechten Bescheid." });
                        surah6.VerseList.Add(new Verse { Number = 59, TextBosnian = "U Njega su ključevi onoga što je čulima nedokučivo, samo ih On zna, i On jedini zna šta je na kopnu i šta je u moru, i nijedan list ne opadne a da On ne zna za njega; i nema zrna u tminama Zemlje niti ičeg svježeg niti ičeg suhog, a da nije u Jasnoj knjizi.", TextEnglish = "And with Him are the keys of the unseen; none knows them except Him. And He knows what is on the land and in the sea. Not a leaf falls but that He knows it. And no grain is there within the darknesses of the earth and no moist or dry [thing] but that it is [written] in a clear record.", TextGerman = "Er verfugt über die Schlüssel des Verborgenen; niemand kennt sie außer Ihm. Und Er weiß, was auf dem Festland und im Meer ist. Kein Blatt fällt, ohne daß Er es weiß; und (es gibt) kein Korn in den Finsternissen der Erde und nichts Feuchtes und nichts Trockenes, das nicht in einem deutlichen Buch (verzeichnet) wäre." });
                        surah6.VerseList.Add(new Verse { Number = 60, TextBosnian = "On vas noću umrtvljuje - a zna i šta ste preko dana uradili - zatim vas danju budi, kako bi rok koji je svakome određen, prošao. Na kraju, Njemu ćete se vratiti i On će vas o onome što ste radili obavijestiti.", TextEnglish = "And it is He who takes your souls by night[312] and knows what you have committed by day. Then He revives you therein [i.e., by day] that a specified term[313] may be fulfilled. Then to Him will be your return; then He will inform you about what you used to do.", TextGerman = "Er ist es, Der euch bei Nacht abberuft und weiß, was ihr bei Tag begangen habt, und euch hierauf an ihm auferweckt, damit eine festgesetzte Frist erfüllt wird. Hierauf wird eure Rückkehr zu Ihm sein, und hierauf wird Er euch kundtun, was ihr zu tun pflegtet." });
                        surah6.VerseList.Add(new Verse { Number = 61, TextBosnian = "On je iznad robova Svojih Svepotčinitelj i šalje vam čuvare; a kad nekom od vas smrt dođe, izaslanici Naši mu dušu uzmu; oni nikad propust ne prave.", TextEnglish = "And He is the subjugator over His servants, and He sends over you guardian-angels until, when death comes to one of you, Our messengers [i.e., angels of death] take him, and they do not fail [in their duties].", TextGerman = "Er ist der Bezwinger über Seinen Dienern. Und Er sendet Hüter über euch, bis, wenn dann zu einem von euch der Tod kommt, ihn Unsere Gesandten abberufen, und sie vernachlässigen nichts." });
                        surah6.VerseList.Add(new Verse { Number = 62, TextBosnian = "Oni će, poslije, Allahu biti vraćeni, svome istinskom Zaštitniku. Samo će se On pitati i On će najbrže obračun svidjeti.", TextEnglish = "Then they [i.e., His servants] are returned to Allāh, their true Lord. Unquestionably, His is the judgement, and He is the swiftest of accountants.", TextGerman = "Hierauf werden sie zu Allah, ihrem wahren Schutzherrn, zurückgebracht. Sicherlich, Sein ist das Urteil, und Er ist der schnellste Berechner." });
                        surah6.VerseList.Add(new Verse { Number = 63, TextBosnian = "Reci: 'Ko vas iz strahota na kopnu i na moru izbavlja kada mu se i javno i tajno ponizno molite: 'Ako nas On iz ovoga izbavi, sigurno ćemo biti zahvalnil''", TextEnglish = "Say, 'Who rescues you from the darknesses of the land and sea [when] you call upon Him imploring [aloud] and privately, 'If He should save us from this [crisis], we will surely be among the thankful.''", TextGerman = "Sag: Wer errettet euch von den Finsternissen des Festlandes und des Meeres, (wenn) ihr Ihn in Unterwürfigkeit und im Verborgenen flehend anruft: Wenn Er uns nur hieraus rettet, werden wir ganz gewiß zu den Dankbaren gehören?" });
                        surah6.VerseList.Add(new Verse { Number = 64, TextBosnian = "Reci: 'Allah vas iz tih i iz svake nevolje izbavlja, pa vi opet Njemu druge u obožavanju pridružujete.'", TextEnglish = "Say, 'It is Allāh who saves you from it and from every distress; then you [still] associate others with Him.'", TextGerman = "Sag: Allah errettet euch davon und von jeder Trübsal, hierauf gesellt ihr (Ihm) dennoch (andere Götter) bei." });
                        surah6.VerseList.Add(new Verse { Number = 65, TextBosnian = "Reci: 'On je kadar poslati protiv vas kaznu iznad vaših glava ili ispod vaših nogu, ili vas u stranke podijeliti i učiniti da silu jedni drugih iskusite. Pogledaj samo kako Mi potanko izlažemo ajete i znakove, ne bi li ljudi shvatili.'", TextEnglish = "Say, 'He is the [one] Able to send upon you affliction from above you or from beneath your feet or to confuse you [so you become] sects[314] and make you taste the violence of one another.' Look how We diversify the signs that they might understand.", TextGerman = "Sag: Er hat die Macht dazu, euch eine Strafe von oben oder unter euren Füßen zu schicken oder euch in (gespaltene) Lager durcheinanderzubringen und die einen von euch die Gewalt der anderen kosten zu lassen! Schau, wie Wir die Zeichen verschiedenartig darlegen, auf daß sie verstehen mögen!" });
                        surah6.VerseList.Add(new Verse { Number = 66, TextBosnian = "Tvoj narod Kur'an poriče, a on je Istina. Reci: 'Ja nisam zadužen da vam Uputu dam.", TextEnglish = "But your people have denied it while it is the truth. Say, 'I am not over you a manager [i.e., authority].'", TextGerman = "Aber dein Volk erklärt es für Lüge, obwohl es die Wahrheit ist. Sag: Ich bin nicht als Sachwalter über euch (eingesetzt)." });
                        surah6.VerseList.Add(new Verse { Number = 67, TextBosnian = "svaki nagovještaj ima svoje vrijeme, i vi ćete znati.'", TextEnglish = "For every news [i.e., happening] is a finality;[315] and you are going to know.", TextGerman = "Jede Vorankündigung hat ihre festgesetzte Zeit, und ihr werdet (es noch) erfahren." });
                        surah6.VerseList.Add(new Verse { Number = 68, TextBosnian = "Kada vidiš one koji lažima zadiru u Naše ajete i rugaju se, ti se od njih okreni sve dok drugu priču ne započnu. A ako te šejtan navede da zaboraviš, onda, nakon što se sjetiš, ne sjedi s ljudima zulumćarima.", TextEnglish = "And when you see those who engage in [offensive] discourse[316] concerning Our verses, then turn away from them until they enter into another conversation. And if Satan should cause you to forget, then do not remain after the reminder with the wrongdoing people.", TextGerman = "Und wenn du diejenigen siehst, die auf Unsere Zeichen (spottend) eingehen, so wende dich von ihnen ab, bis sie auf ein anderes Gespräch eingehen. Und wenn dich der Satan nun vergessen läßt, dann sitze nicht, nachdem du dich (daran) erinnert hast, mit dem ungerechten Volk zusammen." });
                        surah6.VerseList.Add(new Verse { Number = 69, TextBosnian = "Oni koji se nastoje uščuvati neće imati ništa s njihovim polaganjem računa, ali se, radi opomene, treba od njih da udalje, ne bi li se i oni toga okanili.", TextEnglish = "And those who fear Allāh are not held accountable for them [i.e., the disbelievers] at all, but [only for] a reminder - that perhaps they will fear Him.", TextGerman = "Denjenigen, die gottesfürchtig sind, obliegt in keiner Weise, sie zur Rechenschaft zu ziehen, sondern nur die Ermahnung, auf daß sie gottesfürchtig werden mögen." });
                        surah6.VerseList.Add(new Verse { Number = 70, TextBosnian = "Ostavi one koji vjeru svoju kao igru i zabavu uzimaju, i koje je život na ovom svijetu obmanuo, a opominji Kur'anom da niko, zbog onoga što radi, ne bi stradao, jer osim Allaha - ni zaštitnika ni posrednika neće imati i od takvog se neće nikakva otkupnina primiti. Oni će, zbog onoga što su radili, biti u muci držani; njih čeka piće od ključale vode i patnja bolna, zato što nisu vjerovali.", TextEnglish = "And leave those who take their religion as amusement and diversion and whom the worldly life has deluded. But remind with it [i.e., the Qur’ān], lest a soul be given up to destruction for what it earned; it will have other than Allāh no protector and no intercessor. And if it should offer every compensation, it would not be taken from it [i.e., that soul]. Those are the ones who are given to destruction for what they have earned. For them will be a drink of scalding water and a painful punishment because they used to disbelieve.", TextGerman = "Und lasse diejenigen allein, die ihre Religion zum Gegenstand des Spiels und der Zerstreuung nehmen und die das diesseitige Leben täuscht! Und ermahne durch ihn, damit sich keine Seele für das, was sie verdient hat, dem Verderben ausliefert. Sie hat (dann) außer Allah weder Schutzherrn noch Fürsprecher. Und wenn sie auch (als Lösegeld) jede Ersatzleistung anbietet, wird sie von ihr doch nicht angenommen. Das sind diejenigen, die dem Verderben ausgeliefert werden für das, was sie verdient haben. Für sie wird es ein Getränk aus heißem Wasser und schmerzhafte Strafe geben dafür, daß sie ungläubig gewesen sind." });
                        surah6.VerseList.Add(new Verse { Number = 71, TextBosnian = "Reci: 'Zar da, pored Allaha, molimo one koji nam ne mogu nikakvu korist pribaviti ni neku štetu otkloniti, pa da budemo vraćeni svojoj prošlosti - a Allah nas je već uputio- i da budemo kao onaj koga su na Zemlji šejtani zaveli pa ništa ne zna, a koga drugovi njegovi zovu na Pravi put: 'Hodi nama!'' Reci: 'Zaista je Allahova uputa - istinska uputa, i nama je naređeno da se Gospodaru svjetova predamo,", TextEnglish = "Say, 'Shall we invoke[317] instead of Allāh that which neither benefits us nor harms us and be turned back on our heels after Allāh has guided us? [We would then be] like one whom the devils enticed [to wander] upon the earth confused, [while] he has companions inviting him to guidance, [calling], 'Come to us.''[318] Say, 'Indeed, the guidance of Allāh is the [only] guidance; and we have been commanded to submit to the Lord of the worlds.", TextGerman = "Sag: Sollen wir anstatt Allahs anrufen, was uns nicht nützt und nicht schadet, und auf unseren Fersen kehrtmachen, nachdem Allah uns rechtgeleitet hat, wie derjenige, den die Teufel auf der Erde verführt haben, so daß er verwirrt ist? Er hat Gefährten, die ihn zur Rechtleitung einladen: Komm zu uns! Sag: Gewiß, Allahs Rechtleitung ist die (wahre) Rechtleitung. Und uns wurde befohlen, uns dem Herrn der Weltenbewohner zu ergeben," });
                        surah6.VerseList.Add(new Verse { Number = 72, TextBosnian = "i da namaz obavljamo i da se Njega bojimo'; On je Taj pred Kojim ćete biti sakupljeni,", TextEnglish = "And to establish prayer and fear Him.' And it is He to whom you will be gathered.", TextGerman = "und verrichtet das Gebet und fürchtet Ihn! Er ist es, zu Dem ihr versammelt werdet." });
                        surah6.VerseList.Add(new Verse { Number = 73, TextBosnian = "i On je Taj Koji je nebesa i Zemlju s Istinom stvorio. Čim On za nešto rekne: 'Budi!' - ono biva; riječ Njegova je Istina; samo će On imati vlast na Dan kada se u rog puhne; On zna ono što je čulima nedokučivo i ono što je pojavno; On je Mudri i Onaj Koji sve u potpunosti zna.'", TextEnglish = "And it is He who created the heavens and earth in truth. And the day [i.e., whenever] He says, 'Be,' and it is, His word is the truth.[319] And His is the dominion [on] the Day the Horn is blown. [He is] Knower of the unseen[320] and the witnessed;[321] and He is the Wise, the Aware.", TextGerman = "Und Er ist es, Der die Himmel und die Erde in Wahrheit erschaffen hat. Und an dem Tag, da Er sagt: Sei! da wird es sein. Sein Wort ist die Wahrheit. Und Er hat die Herrschaft an dem Tag, da ins Horn geblasen wird. (Er ist) der Kenner des Verborgenen und des Offenbaren, und Er ist der Allweise und Allkundige." });
                        surah6.VerseList.Add(new Verse { Number = 74, TextBosnian = "A kad Ibrahim reče ocu svome Azeru: 'Zar kumire smatraš bogovima?! Uistinu, vidim da ste i ti i narod tvoj u očitaj zabludi.'", TextEnglish = "And [mention, O Muḥammad], when Abraham said to his father Āzar, 'Do you take idols as deities? Indeed, I see you and your people to be in manifest error.'", TextGerman = "Und als Ibrahim zu seinem Vater Azar sagte: Nimmst du (denn) Götzenbilder zu Göttern? Gewiß, ich sehe dich und dein Volk in deutlichem Irrtum." });
                        surah6.VerseList.Add(new Verse { Number = 75, TextBosnian = "I Mi pokazasmo Ibrahimu carstvo nebesa i Zemlje da bi postao od onih što čvrsto vjeruju.", TextEnglish = "And thus did We show Abraham the realm of the heavens and the earth that he would be among the certain [in faith].", TextGerman = "Und so zeigten Wir Ibrahim das Reich der Himmel und der Erde, - und damit er zu den Überzeugten gehöre." });
                        surah6.VerseList.Add(new Verse { Number = 76, TextBosnian = "I kad nastupi noć, on ugleda zvijezdu i reče: 'Ovo je Gospodar moj!' A pošto zađe, reče: 'Ne volim one koji zalaze!'", TextEnglish = "So when the night covered him [with darkness], he saw a star. He said, 'This is my lord.'[322] But when it set, he said, 'I like not those that set [i.e., disappear].'", TextGerman = "Als die Nacht über ihn hereinbrach, sah er einen Himmelskörper. Er sagte: Das ist mein Herr. Als er aber unterging, sagte er: Ich liebe nicht diejenigen, die untergehen’." });
                        surah6.VerseList.Add(new Verse { Number = 77, TextBosnian = "A kad ugleda Mjesec kako izlazi, reče: 'Ovo je Gospodar moj!' A pošto zađe, on reče: 'Ako me Gospodar moj ne uputi, bit ću sigurno jedan od ljudi koji su zalutali.'", TextEnglish = "And when he saw the moon rising, he said, 'This is my lord.' But when it set, he said, 'Unless my Lord guides me, I will surely be among the people gone astray.'", TextGerman = "Als er dann den Mond aufgehen sah, sagte er: Das ist mein Herr. Als er aber unterging, sagte er: Wenn mein Herr mich nicht rechtleitet, werde ich ganz gewiß zum irregehenden Volk gehören." });
                        surah6.VerseList.Add(new Verse { Number = 78, TextBosnian = "A kad ugleda Sunce kako se rađa, on uzviknu: 'Ovo je Gospodar moj, ovo je veće!' A pošto zađe, on reče: 'Narode moj, ja zaista nemam ništa s time što vi Njemu pridružujete!", TextEnglish = "And when he saw the sun rising, he said, 'This is my lord; this is greater.' But when it set, he said, 'O my people, indeed I am free from what you associate with Allāh.", TextGerman = "Als er dann die Sonne aufgehen sah, sagte er: Das ist mein Herr. Das ist größer. Als sie aber unterging, sagte er: O mein Volk, ich sage mich ja von dem los, was ihr (Ihm) beigesellt." });
                        surah6.VerseList.Add(new Verse { Number = 79, TextBosnian = "Ja okrećem lice svoje, kao pravi vjernik, prema Onome Koji je nebesa i Zemlju, bez prethodnog primjera, stvorio; ja u višebošce ne spadam!'", TextEnglish = "Indeed, I have turned my face [i.e., self] toward He who created the heavens and the earth, inclining toward truth, and I am not of those who associate others with Allāh.'", TextGerman = "Ich wende mein Gesicht Dem zu, Der die Himmel und die Erde erschaffen hat, als Anhänger des rechten Glaubens, und ich gehöre nicht zu den Götzendienern." });
                        surah6.VerseList.Add(new Verse { Number = 80, TextBosnian = "I narod je njegov s njim raspravljao. 'Zar da sa mnom o Allahu raspravljate, a On je mene uputio?'', reče on. 'Ja se ne bojim onih koje vi Njemu pridružujete, osim što se bojim da moj Gospodar htjedne nešto. Gospodar moj znanjem svojim obuhvata sve. Zašto ne promislite i pouku ne prihvatite?", TextEnglish = "And his people argued with him. He said, 'Do you argue with me concerning Allāh while He has guided me? And I fear not what you associate with Him [and will not be harmed] unless my Lord should will something. My Lord encompasses all things in knowledge; then will you not remember?", TextGerman = "Sein Volk stritt mit ihm. Er sagte: Wollt ihr mit mir über Allah streiten, wo Er mich doch schon rechtgeleitet hat? Ich fürchte nicht, was ihr Ihm beigesellt, außer daß mein Herr will (, daß ich) es (fürchte). Mein Herr umfaßt alles mit (Seinem) Wissen. Bedenkt ihr denn nicht?" });
                        surah6.VerseList.Add(new Verse { Number = 81, TextBosnian = "A kako bih se bojao onih koje Njemu pridružujete, kada se vi ne bojite što Allahu u obožavanju pridružujete one za koje vam On nije nikakav dokaz spustio? Pa koji od dva tabora više sigurnost zaslužuje, ako vi znate?", TextEnglish = "And how should I fear what you associate while you do not fear that you have associated with Allāh that for which He has not sent down to you any authority? So which of the two parties has more right to security, if you should know?'", TextGerman = "Wie sollte ich fürchten, was ihr (Ihm) beigesellt, wo ihr euch nicht fürchtet, Allah beizugesellen, wozu Er euch keine Ermächtigung offenbart hat? Welche der beiden Gruppen hat also ein größeres Anrecht auf Sicherheit, wenn ihr wißt?" });
                        surah6.VerseList.Add(new Verse { Number = 82, TextBosnian = "Samo oni koji vjeruju i vjerovanje svoje sa zulumom ne miješaju bit će uistinu sigurni: oni su upućeni.'", TextEnglish = "They who believe and do not mix their belief with injustice[323] - those will have security, and they are [rightly] guided.", TextGerman = "Diejenigen, die glauben und ihren Glauben nicht mit Ungerechtigkeit verdecken, die haben (das Recht auf) Sicherheit, und sie sind rechtgeleitet." });
                        surah6.VerseList.Add(new Verse { Number = 83, TextBosnian = "To je čvrst dokaz Naš koji dadosmo Ibrahimu protiv naroda njegova. Mi na više stepene uzdižemo one koje hoćemo. Gospodar tvoj je, uistinu, mudar i Onaj Koji sve zna.", TextEnglish = "And that was Our [conclusive] argument which We gave Abraham against his people. We raise by degrees whom We will. Indeed, your Lord is Wise and Knowing.", TextGerman = "Das ist unser Beweismittel, das Wir Ibrahim gegen sein Volk gaben. Wir erhöhen, wen Wir wollen, um Rangstufen. Gewiß, dein Herr ist Allweise und Allwissend." });
                        surah6.VerseList.Add(new Verse { Number = 84, TextBosnian = "I Mi mu darovasmo Ishaka i Jakuba; i svakog uputismo - a Nuha smo još prije uputili - i od potomaka njegovih Davuda, i Sulejmana, i Ejjuba, i Jusufa, i Musaa, i Haruna- eto, tako Mi nagrađujemo one koji dobra djela čine;", TextEnglish = "And We gave to him [i.e., Abraham] Isaac and Jacob - all [of them] We guided. And Noah, We guided before; and among his descendants, David and Solomon and Job and Joseph and Moses and Aaron. Thus do We reward the doers of good.", TextGerman = "Und Wir schenkten ihm Ishaq und Ya’qub; jeden (von ihnen) haben Wir rechtgeleitet. Und (auch) Nuh haben Wir zuvor rechtgeleitet, und aus seiner Nachkommenschaft Dawud, Sulaiman, Ayyub, Yusuf, Musa und Harun - so vergelten Wir (es) den Gutes Tuenden -;" });
                        surah6.VerseList.Add(new Verse { Number = 85, TextBosnian = "i Zekerijjaa, i Jahjaa, i Isaa, i Il'jasa - svi su oni među čestitima bili -", TextEnglish = "And Zechariah and John and Jesus and Elias - and all were of the righteous.", TextGerman = "und Zakariyya, Yahya, ’Isa und Ilyas: jeder (von ihnen) gehört zu den Rechtschaffenen;" });
                        surah6.VerseList.Add(new Verse { Number = 86, TextBosnian = "i Ismaila, i El-Jese'a i Junusa i Luta- i svima smo prednost nad svjetovima ostalim dali -", TextEnglish = "And Ishmael and Elisha and Jonah and Lot - and all [of them] We preferred over the worlds.", TextGerman = "und lsma’il, Alyasa’, Yunus und Lut: jeden (von ihnen) haben Wir vor den (anderen) Weltenbewohnern bevorzugt;" });
                        surah6.VerseList.Add(new Verse { Number = 87, TextBosnian = "i neke pretke njihove i potomke njihove i braću njihovu - njih smo odabrali i na Pravi put ih uputili.", TextEnglish = "And [some] among their fathers and their descendants and their brothers - and We chose them and We guided them to a straight path.", TextGerman = "und (auch manche) von ihren (Vor)Vätern, ihren Nachkommen und ihren Brüdern; Wir haben sie erwählt und zu einem geraden Weg geleitet." });
                        surah6.VerseList.Add(new Verse { Number = 88, TextBosnian = "To je Allahova uputa kojom On upućuje one koje hoće od robova Svojih. A da su oni Allahu ikoga u obožavanju pridruživali, sigurno bi propalo ono što su radili.", TextEnglish = "That is the guidance of Allāh by which He guides whomever He wills of His servants. But if they had associated others with Allāh, then worthless for them would be whatever they were doing.", TextGerman = "Das ist Allahs Rechtleitung. Er leitet damit recht, wen von Seinen Dienern Er will. Wenn sie (Ihm) aber (andere) beigesellt hätten, wäre für sie wahrlich hinfällig geworden, was sie zu tun pflegten." });
                        surah6.VerseList.Add(new Verse { Number = 89, TextBosnian = "To su oni kojima smo Mi Knjigu i mudrost i vjerovjesništvo dali. Pa ako oni u to ne vjeruju, Mi smo to povjerili ljudima koji to ne niječu.", TextEnglish = "Those are the ones to whom We gave the Scripture and authority and prophethood. But if they [i.e., the disbelievers] deny it, then We have entrusted it to a people who are not therein disbelievers.", TextGerman = "Das sind diejenigen, denen Wir die Schrift, das Urteil und das Prophetentum gegeben haben. Wenn aber diese es verleugnen, so haben Wir damit schon (andere) Leute betraut, die dem gegenüber nicht ungläubig sind." });
                        surah6.VerseList.Add(new Verse { Number = 90, TextBosnian = "Njih je Allah uputio, i zato slijedi njihov put. Reci: 'Ja od vas ne tražim nagradu za Kur'an, on je samo pouka svjetovima.'", TextEnglish = "Those are the ones whom Allāh has guided, so from their guidance take an example. Say, 'I ask of you for it [i.e., this message] no payment. It is not but a reminder for the worlds.'", TextGerman = "Das sind diejenigen, die Allah rechtgeleitet hat. So nimm ihre Rechtleitung zum Vorbild! Sag: Ich verlange von euch keinen Lohn dafür. Es ist nur eine Ermahnung für die Weltenbewohner." });
                        surah6.VerseList.Add(new Verse { Number = 91, TextBosnian = "Oni ne poznaju Allaha kako treba, kad govore: 'Nijednom čovjeku Allah nije ništa objavio!' Reci: 'A ko je objavio Knjigu koju je donio Musa kao svjetlo i uputu ljudima, koju na listove stavljate i pokazujete, a mnogo i krijete, i poučeni ste onome što ni vi ni preci vaši niste znali?' Reci: 'Allah!' Zatim ih ostavi, neka se lažima svojim zabavljaju.", TextEnglish = "And they did not appraise Allāh with true appraisal[324] when they said, 'Allāh did not reveal to a human being anything.' Say, 'Who revealed the Scripture that Moses brought as light and guidance to the people? You [Jews] make it into pages, disclosing [some of] it and concealing much. And you[325] were taught that which you knew not - neither you nor your fathers.' Say, 'Allāh [revealed it].' Then leave them in their [empty] discourse, amusing themselves.", TextGerman = "Sie schätzen Allah nicht ein, wie es Ihm gebührt’, wenn sie sagen: Allah hat nichts auf ein menschliches Wesen (als Offenbarung) herabgesandt. Sag: Wer sandte (denn) die Schrift herab, die Musa als Licht und Rechtleitung für die Menschen brachte, die ihr zu (beschriebenen) Blättern macht, die ihr offen (vor)zeigt, während ihr vieles verbergt, und (sag: von wem) wurde euch gelehrt, was ihr nicht wußtet, weder ihr noch eure Väter? Sag: (Von) Allah. Sodann lasse sie mit ihren schweifenden Gesprächen ihr Spiel treiben." });
                        surah6.VerseList.Add(new Verse { Number = 92, TextBosnian = "A ova Knjiga, koju objavljujemo, blagoslovljena je, ona potvrđuje one prije nje i da upozoriš Mekku - Majku naselja i sve oko nje. A oni koji u ahiret vjeruju - vjeruju i u nju i o namazima svojim brinu.", TextEnglish = "And this is a Book which We have sent down, blessed and confirming what was before it, that you may warn the Mother of Cities [i.e., Makkah] and those around it.[326] Those who believe in the Hereafter believe in it, and they are maintaining their prayers.", TextGerman = "Und dies ist ein Buch, das Wir hinabgesandt haben, ein gesegnetes, das bestätigend, was vor ihm war, und damit du die Mutter der Städte und diejenigen rings umher (Wohnenden) warnst. Diejenigen, die an das Jenseits glauben, glauben (auch) daran, und sie halten ihr Gebet ein." });
                        surah6.VerseList.Add(new Verse { Number = 93, TextBosnian = "Ko je veći zulumćar od onoga koji laži o Allahu iznosi ili koji govori: 'Objavljuje mi se'', a ništa mu se ne objavljuje, ili koji kaže: 'I ja ću reći isto onako kao što Allah objavljuje.' A da ti je vidjeti zulumćare u smrtnim mukama, kada meleki budu ispružili ruke svoje prema njima: 'Izvadite svoje duše! Od sada ćete neizdržljivom kaznom biti kažnjeni zato što ste na Allaha ono što nije istina iznosili i što ste se prema ajetima i znakovima Njegovim oholo ponašali.'", TextEnglish = "And who is more unjust than one who invents a lie about Allāh or says, 'It has been inspired to me,' while nothing has been inspired to him, and one who says, 'I will reveal [something] like what Allāh revealed.' And if you could but see when the wrongdoers are in the overwhelming pangs of death while the angels extend their hands,[327] [saying], 'Discharge your souls! Today you will be awarded the punishment of [extreme] humiliation for what you used to say against Allāh other than the truth and [that] you were, toward His verses, being arrogant.'", TextGerman = "Und wer ist ungerechter, als wer gegen Allah eine Lüge ersinnt oder sagt: Mir ist (Offenbarung) eingegeben worden, während ihm überhaupt nichts eingegeben worden ist, und wer sagt: Ich werde hinabsenden, gleich dem, was Allah hinabgesandt hat? Und wenn du sehen würdest, wie sich die Ungerechten in den Fluten des Todes befinden und die Engel ihre Hände ausstrecken: Gebt eure Seelen heraus! Heute wird euch mit der schmählichen Strafe vergolten, daß ihr stets über Allah die Unwahrheit gesagt habt und euch gegenüber Seinen Zeichen hochmütig zu verhalten pflegtet." });
                        surah6.VerseList.Add(new Verse { Number = 94, TextBosnian = "A doći ćete nam pojedinačno, onakvi kakve smo vas prvi put stvorili, napustivši dobra koja smo vam bili darovali. Mi ne vidimo s vama zagovarače vaše za koje ste smatrali da ih Allahu treba u obožavanju pridružiti, pokidane su veze među vama i ostaviše vas oni za koje ste tako tvrdili.", TextEnglish = "[It will be said to them], 'And you have certainly come to Us alone [i.e., individually] as We created you the first time, and you have left whatever We bestowed upon you behind you. And We do not see with you your 'intercessors' which you claimed that they were among you associates [of Allāh]. It has [all] been severed between you,[328] and lost from you is what you used to claim.'", TextGerman = "Nun seid ihr einzeln zu Uns gekommen, so wie Wir euch das erste Mal erschaffen haben, und ihr habt hinter euch zurückgelassen, was Wir euch übertragen hatten. Und Wir sehen eure Fürsprecher nicht bei euch, von denen ihr behauptet habt, daß sie Teilhaber an euch waren. Nun ist (das Band) zwischen euch abgeschnitten, und entschwunden ist euch, was ihr zu behaupten pflegtet." });
                        surah6.VerseList.Add(new Verse { Number = 95, TextBosnian = "Zaista je Allah Onaj Koji čini da se raspukne zrno i košpica. On izvodi živo iz mrtvoga, i On je Taj Koji izvodi mrtvo iz živoga; to vam je, eto, Allah, pa kuda se onda odmećete!?", TextEnglish = "Indeed, Allāh is the cleaver of grain and date seeds.[329] He brings the living out of the dead and brings the dead out of the living. That is Allāh; so how are you deluded?", TextGerman = "Allah ist es, Der die Körner und die Kerne spaltet und das Lebendige aus dem Toten hervorbringt. Und (Er ist es,) Der das Tote aus dem Lebendigen hervorbringt. Dies ist doch Allah - wie laßt ihr euch also abwendig machen? -," });
                        surah6.VerseList.Add(new Verse { Number = 96, TextBosnian = "On je Taj Koji čini da zora sviće, On je noć odredio za počinak, a Sunce i Mjesec za računanje vremena; to je odredba Silnoga i Sveznajućeg.", TextEnglish = "[He is] the cleaver of daybreak and has made the night for rest and the sun and moon for calculation.[330] That is the determination of the Exalted in Might, the Knowing.", TextGerman = "Er, Der den Morgen anbrechen läßt’. Er hat die Nacht zur Ruhe(zeit) und die Sonne und den Mond als (Mittel der) Berechnung gemacht. Das ist die Anordnung des Allmächtigen und Allwissenden." });
                        surah6.VerseList.Add(new Verse { Number = 97, TextBosnian = "On vam je stvorio zvijezde da se po njima u mraku upravljate, na kopnu i moru. Mi smo obrazložili ajete i znakove ljudima koji znaju.", TextEnglish = "And it is He who placed for you the stars that you may be guided by them through the darknesses of the land and sea. We have detailed the signs for a people who know.", TextGerman = "Und Er ist es, Der euch die Sterne gemacht hat, damit ihr euch durch sie rechtleiten laßt in den Finsternissen des Festlandes und des Meeres. Wir haben ja die Zeichen ausführlich dargelegt für Leute, die Bescheid wissen." });
                        surah6.VerseList.Add(new Verse { Number = 98, TextBosnian = "On vas je stvorio od jednog čovjeka, a zatim vam slijedi boravište i spremište. Mi smo obrazložili ajete i znakove ljudima koji razumiju.", TextEnglish = "And it is He who produced you from one soul and [gave you] a place of dwelling and of storage.[331] We have detailed the signs for a people who understand.", TextGerman = "Und Er ist es, Der euch aus einem einzigen Wesen hat entstehen lassen. Dann gibt es einen Aufenthaltsort und einen Aufbewahrungsort. Wir haben die Zeichen ausführlich dargelegt für Leute, die verstehen." });
                        surah6.VerseList.Add(new Verse { Number = 99, TextBosnian = "On vodu s neba spušta, pa Mi onda činimo da pomoću nje niču sve vrste bilja i da iz njega izrasta zelenilo, a iz njega klasje gusto, i iz palmi, iz zametka njihova, grozdovi koje je lahko ubrati, i vrtovi lozom zasađeni, i naročito masline i šipci-oboje, i slično i različito. Posmatrajte, zato, plodove njihove kad se tek pojave i kad zru. U tome su, zaista, dokazi za ljude koji vjeruju.", TextEnglish = "And it is He who sends down rain from the sky, and We produce thereby the growth of all things. We produce from it greenery from which We produce grains arranged in layers. And from the palm trees - of its emerging fruit are clusters hanging low. And [We produce] gardens of grapevines and olives and pomegranates, similar yet varied. Look at [each of] its fruit when it yields and [at] its ripening. Indeed in that are signs for a people who believe.", TextGerman = "Und Er ist es, Der vom Himmel Wasser herabkommen läßt. Damit bringen Wir den Wuchs aller Arten hervor; aus ihnen bringen Wir dann Grün hervor, aus dem Wir übereinandergeschichtete Körner hervorbringen - und aus den Palmen, aus ihren Blütenscheiden (entstehen) herabhängende Dattelbüschel -, und (auch) Gärten mit Rebstöcken und die Öl- und die Granatapfelbäume, die einander ähnlich und unähnlich sind. Schaut ihre Früchte an, wenn sie Früchte tragen, und (schaut) auf deren Reife! Seht, darin sind wahrlich Zeichen für Leute, die glauben." });
                        surah6.VerseList.Add(new Verse { Number = 100, TextBosnian = "I oni drže da su džini Allahu takmaci, a On je njih stvorio, i izmislili su, ne znajući šta govore, da On ima sinove i kćeri. Uzvišen je On i vrlo visoko iznad onoga kako Ga oni opisuju!", TextEnglish = "But they have attributed to Allāh partners - the jinn, while He has created them - and have fabricated for Him sons and daughters without knowledge. Exalted is He and high above what they describe.", TextGerman = "Und sie haben Teilhaber Allah gegeben: die Ginn, wo Er sie doch erschaffen hat. Und sie haben Ihm Söhne und Töchter angedichtet, ohne Wissen. Preis sei Ihm! Erhaben ist Er über das, was sie (Ihm) zuschreiben." });
                        surah6.VerseList.Add(new Verse { Number = 101, TextBosnian = "On je nebesa i Zemlje, bez prethodnog primjera, Stvoritelj. Otkud Njemu dijete kad nema žene?! On sve stvara, i samo On sve zna.", TextEnglish = "[He is] Originator of the heavens and the earth. How could He have a son when He does not have a companion [i.e., wife] and He created all things? And He is, of all things, Knowing.", TextGerman = "(Er ist) der Schöpfer der Himmel und der Erde in ihrer schönsten Form. Wie soll Er Kinder haben, wo Er doch keine Gefährtin hat und Er (sonst) alles erschaffen hat? Und Er weiß über alles Bescheid." });
                        surah6.VerseList.Add(new Verse { Number = 102, TextBosnian = "To vam je Allah, Gospodar vaš, nema drugog boga osim Njega, Stvoritelja svega; zato Njemu ibadet činite; On nad svime bdi.", TextEnglish = "That is Allāh, your Lord; there is no deity except Him, the Creator of all things, so worship Him. And He is Disposer of all things.", TextGerman = "Dies ist doch Allah, euer Herr. Es gibt keinen Gott außer Ihm, dem Schöpfer von allem. So dient Ihm! Er ist Sachwalter über alles." });
                        surah6.VerseList.Add(new Verse { Number = 103, TextBosnian = "Pogledi do Njega ne mogu doprijeti, a On do pogleda dopire; On je Dobrostiv i Onaj Koji sve u potpunosti zna.", TextEnglish = "Vision perceives Him not,[332] but He perceives [all] vision; and He is the Subtle,[333] the Aware.[334]", TextGerman = "Die Blicke erfassen Ihn nicht, Er aber erfaßt die Blicke. Und Er ist der Feinfühlige und Allkundige." });
                        surah6.VerseList.Add(new Verse { Number = 104, TextBosnian = "Od Gospodara vašeg došli su vam jasni dokazi, pa onaj ko ih vidi - u svoju korist to čini, a onaj ko ostane na to slijep - na svoju je štetu slijep; a ja nisam taj koji vas nadzire.", TextEnglish = "There has come to you enlightenment from your Lord. So whoever will see does so for [the benefit of] his soul, and whoever is blind [does harm] against it. And [say], 'I am not a guardian over you.'[335]", TextGerman = "Zu euch sind nunmehr einsicht bringende Zeichen von eurem Herrn gekommen. Wer einsichtig wird, der ist es zu seinem eigenen Vorteil, und wer blind ist, der ist es zu seinem eigenen Nachteil. Und ich bin nicht Hüter über euch." });
                        surah6.VerseList.Add(new Verse { Number = 105, TextBosnian = "I eto, tako Mi izlažemo ajete da bi oni rekli: 'Ti si to učio', i da bismo to objasnili ljudima koji znaju.", TextEnglish = "And thus do We diversify the verses so they [i.e., the disbelievers] will say, 'You have studied,'[336] and so We may make it [i.e., the Qur’ān] clear for a people who know.", TextGerman = "So legen Wir die Zeichen verschiedenartig dar, - und damit sie sagen können: Du hast (es) erlernt und damit Wir ihn Leuten klar machen, die Bescheid wissen." });
                        surah6.VerseList.Add(new Verse { Number = 106, TextBosnian = "Ti slijedi ono što Gospodar tvoj objavljuje, drugoga boga osim Njega nema, i višebošce izbjegavaj.", TextEnglish = "Follow, [O Muḥammad], what has been revealed to you from your Lord - there is no deity except Him - and turn away from those who associate others with Allāh.", TextGerman = "Folge dem, was dir von deinem Herrn (als Offenbarung) eingegeben worden ist! Es gibt keinen Gott außer Ihm. Und wende dich von den Götzendienern ab!" });
                        surah6.VerseList.Add(new Verse { Number = 107, TextBosnian = "Da Allah hoće, oni ne bi širk činili, a Mi tebe nismo dali da ih nadzireš niti si ti za njih zadužen.", TextEnglish = "But if Allāh had willed, they would not have associated. And We have not appointed you over them as a guardian, nor are you a manager over them.[337]", TextGerman = "Wenn Allah gewollt hätte, hätten sie (Ihm) nicht(s) beigesellt. Und Wir haben dich nicht zum Hüter über sie gemacht, noch bist du (als) Sachwalter über sie (eingesetzt)." });
                        surah6.VerseList.Add(new Verse { Number = 108, TextBosnian = "Ne grdite one koje oni, pored Allaha, prizivaju, da ne bi i oni, nepravedno i ne misleći šta govore, Allaha grdili. Kao i njima, tako smo svakom narodu lijepim postupke njihove predstavljali. Oni će se, na kraju, Gospodaru svome vratiti, pa će ih On o onom što su radili obavijestiti.", TextEnglish = "And do not insult those they invoke other than Allāh, lest they insult Allāh in enmity without knowledge. Thus We have made pleasing to every community their deeds. Then to their Lord is their return, and He will inform them about what they used to do.", TextGerman = "Und schmäht nicht diejenigen, die sie außer Allah anrufen, damit sie nicht in Übertretung ohne Wissen Allah schmähen! So haben Wir jeder Gemeinschaft ihr Tun ausgeschmückt erscheinen lassen. Hierauf wird ihre Rückkehr zu ihrem Herrn sein, und Er wird ihnen kundtun, was sie zu tun pflegten." });
                        surah6.VerseList.Add(new Verse { Number = 109, TextBosnian = "Oni se zaklinju Allahom, najjačom zakletvom, da će, ako im dođe kakvo znamenje, sigurno zbog njega vjernici postati. Reci: 'Sva su znamenja samo u Allaha!' A šta vi znate, možda bi oni, kada bi im ona došla, vjernici postali?", TextEnglish = "And they swear by Allāh their strongest oaths that if a sign came to them, they would surely believe in it. Say, 'The signs are only with [i.e., from] Allāh.' And what will make you perceive that even if it [i.e., a sign] came, they would not believe.", TextGerman = "Und sie haben bei Allah ihren kräftigsten Eid geschworen, sie würden, wenn nur ein Zeichen zu ihnen käme, ganz gewiß daran glauben. Sag: Nur bei Allah sind die Zeichen. Was sollte euch auch merken lassen, daß, wenn es tatsächlich käme, sie doch nicht glauben?" });
                        surah6.VerseList.Add(new Verse { Number = 110, TextBosnian = "A mi srca njihova i oči njihove okrećemo, pa neće vjerovati kao što nisu vjerovali ni prvi put, i ostavljamo ih da u osionosti svojoj lutaju smeteni.", TextEnglish = "And We will turn away their hearts and their eyes just as they refused to believe in it [i.e., the revelation] the first time. And We will leave them in their transgression, wandering blindly.", TextGerman = "Und Wir kehren ihre Herzen und ihr Augenlicht um, so wie sie das erste Mal nicht daran geglaubt haben. Und Wir lassen sie in ihrer Auflehnung umherirren." });
                        surah6.VerseList.Add(new Verse { Number = 111, TextBosnian = "Kad bismo im meleke poslali, i kad bi im mrtvi progovorili, i kad bismo pred njih sve dokaze sabrali - oni, opet, ne bi vjerovali, osim ako bi Allah htio, ali većina njih ne zna.", TextEnglish = "And even if We had sent down to them the angels [with the message] and the dead spoke to them [of it] and We gathered together every [created] thing in front of them, they would not believe unless Allāh should will. But most of them, [of that], are ignorant.", TextGerman = "Wenn Wir auch zu ihnen die Engel hinabsendeten, die Toten zu ihnen sprächen und Wir alle Dinge vor ihren Augen versammelten, sie würden unmöglich glauben, es sei denn, Allah wollte es. Aber die meisten von ihnen wissen nicht." });
                        surah6.VerseList.Add(new Verse { Number = 112, TextBosnian = "Tako smo svakom vjerovjesniku neprijatelje određivali, šejtane u vidu ljudi i džina, koji su jedni drugima kićene besjede govorili, da bi ih obmanuli; a da je Gospodar tvoj htio, oni to ne bi učinili; zato ti ostavi njih, i ono što izmišljaju;", TextEnglish = "And thus We have made for every prophet an enemy - devils from mankind and jinn, inspiring to one another decorative speech in delusion. But if your Lord had willed, they would not have done it, so leave them and that which they invent.", TextGerman = "Und so haben Wir jedem Propheten Feinde bestimmt: die Satane der Menschen und der Ginn, von denen die einen den anderen prunkende Worte eingeben in Trug und wenn dein Herr gewollt hätte, hätten sie es nicht getan; so lasse sie (stehen) mit dem, was sie an Lügen ersinnen -," });
                        surah6.VerseList.Add(new Verse { Number = 113, TextBosnian = "i da bi srca onih koji u ahiret ne vjeruju bila sklona tome i zadovoljna time, i da bi počinili grijehe koje su počinili.", TextEnglish = "And [it is] so the hearts of those who disbelieve in the Hereafter will incline toward it [i.e., deceptive speech] and that they will be satisfied with it and that they will commit that which they are committing.", TextGerman = "und damit die Herzen derjenigen, die an das Jenseits nicht glauben, dem zuneigen, und damit sie damit zufrieden sind und begehen, was sie begehen." });
                        surah6.VerseList.Add(new Verse { Number = 114, TextBosnian = "Zar da pored Allaha tražim drugog sudiju, kad vam On objavljuje Knjigu potanko?! A oni kojima smo Mi dali knjigu dobro znaju da Kur'an objavljuje Gospodar tvoj s Istinom, zato ti ne sumnjaj nikako!", TextEnglish = "[Say], 'Then is it other than Allāh I should seek as judge while it is He who has revealed to you the Book [i.e., the Qur’ān] explained in detail?' And those to whom We [previously] gave the Scripture know that it is sent down from your Lord in truth, so never be among the doubters.", TextGerman = "Soll ich denn einen anderen Schiedsrichter als Allah begehren, wo Er es doch ist, der das Buch, ausführlich dargelegt, zu euch herabgesandt hat? Diejenigen, denen Wir die Schrift gaben, wissen, daß es von deinem Herrn mit der Wahrheit herabgesandt wurde. So gehöre ja nicht zu den Zweiflern." });
                        surah6.VerseList.Add(new Verse { Number = 115, TextBosnian = "Riječi Gospodara tvoga vrhunac su istine i pravde; Njegove riječi niko ne može promijeniti i On je Svečujući i Sveznajući.", TextEnglish = "And the word of your Lord has been fulfilled in truth and in justice. None can alter His words, and He is the Hearing, the Knowing.", TextGerman = "Vollkommen ist das Wort deines Herrn in Wahrhaftigkeit und Gerechtigkeit. Es gibt niemanden, der Seine Worte abändern könnte. Und Er ist der Allhörende und Allwissende." });
                        surah6.VerseList.Add(new Verse { Number = 116, TextBosnian = "Ako bi se ti pokoravao većini onih koji žive na Zemlji, oni bi te od Allahova puta odvratili; oni se samo za pretpostavkama povode, i oni samo laži izmišljaju.", TextEnglish = "And if you obey most of those upon the earth, they will mislead you from the way of Allāh. They follow not except assumption, and they are not but misjudging.[338]", TextGerman = "Wenn du den meisten von denen, die auf der Erde sind, gehorchst, werden sie dich von Allahs Weg ab in die Irre führen. Sie folgen nur Mutmaßungen, und sie stellen nur Schätzungen an." });
                        surah6.VerseList.Add(new Verse { Number = 117, TextBosnian = "Zaista Gospodar tvoj najbolje zna one koji su skrenuli s Njegova puta i On najbolje zna one koji su na Pravom putu.", TextEnglish = "Indeed, your Lord is most knowing of who strays from His way, and He is most knowing of the [rightly] guided.", TextGerman = "Gewiß, dein Herr kennt sehr wohl, wer von Seinem Weg abirrt, und Er kennt sehr wohl die Rechtgeleiteten." });
                        surah6.VerseList.Add(new Verse { Number = 118, TextBosnian = "Zato, jedite ono pri čijem je klanju spomenuto Allahovo ime, ako u Njegove ajete i znakove vjerujete.", TextEnglish = "So eat of that [meat] upon which the name of Allāh has been mentioned,[339] if you are believers in His verses [i.e., revealed law].", TextGerman = "Eßt von dem, worüber Allahs Name ausgesprochen worden ist, wenn ihr an Seine Zeichen glaubt." });
                        surah6.VerseList.Add(new Verse { Number = 119, TextBosnian = "A zašto da ne jedete ono pri čijem je klanju spomenuto Allahovo ime kad vam je On objasnio šta vam je zabranio - osim kad ste prisiljeni; mnogi, prema prohtjevima svojim, nemajući za to nikakva dokaza, zavode druge u zabludu. U istinu, Gospodar tvoj najbolje zna one koji granice prelaze.", TextEnglish = "And why should you not eat of that upon which the name of Allāh has been mentioned while He has explained in detail to you what He has forbidden you, excepting that to which you are compelled.[340] And indeed do many lead [others] astray through their [own] inclinations without knowledge. Indeed, your Lord - He is most knowing of the transgressors.", TextGerman = "Was ist mit euch, daß ihr nicht von dem eßt, worüber Allahs Name ausgesprochen worden ist, wo Er euch doch ausführlich dargelegt hat, was Er euch verboten hat, außer dem, wozu ihr gezwungen werdet? Viele führen wahrlich (andere) durch ihre Neigungen ohne Wissen in die Irre. Aber dein Herr weiß sehr wohl über die Übertreter Bescheid." });
                        surah6.VerseList.Add(new Verse { Number = 120, TextBosnian = "Okanite se i javnog i tajnog griješenja! Oni koji griješe sigurno će biti kažnjeni za ono što su zgriješili.", TextEnglish = "And leave [i.e., desist from] what is apparent of sin and what is concealed thereof. Indeed, those who earn [blame for] sin will be recompensed for that which they used to commit.", TextGerman = "Unterlaßt das Offenkundige an Sünde und das Verborgene! Gewiß, denen, die Sünde erwerben, wird das vergolten, was sie zu begehen pflegten." });
                        surah6.VerseList.Add(new Verse { Number = 121, TextBosnian = "Ne jedite ono pri čijem klanju nije spomenuto Allahovo ime; to je, uistinu, neposluh! A šejtani, doista, navode štićenike svoje da se s vama raspravljaju, pa ako biste im se pokorili - i vi biste, sigurno, mušrici postali.", TextEnglish = "And do not eat of that upon which the name of Allāh has not been mentioned, for indeed, it is grave disobedience. And indeed do the devils inspire their allies [among men] to dispute with you. And if you were to obey them, indeed, you would be associators [of others with Him].[341]", TextGerman = "Und eßt nicht von dem, worüber der Name Allahs nicht ausgesprochen worden ist. Das ist wahrlich Frevel. Die Satane geben ihren Schützlingen in der Tat ein, mit euch zu streiten. Wenn ihr ihnen gehorcht, seid ihr fürwahr Götzendiener." });
                        surah6.VerseList.Add(new Verse { Number = 122, TextBosnian = "Zar je onaj koji je bio u zabludi, a koga smo Mi oživili i dali mu svjetlo pomoću kojeg se među ljudima kreće, kao onaj koji je u tminama iz kojih ne izlazi? A nevjernicima je uljepšano to što rade.", TextEnglish = "And is one who was dead and We gave him life and made for him light by which to walk among the people like one who is in darkness, never to emerge therefrom? Thus it has been made pleasing to the disbelievers that which they were doing.", TextGerman = "Ist denn der, der tot war, und den Wir dann lebendig gemacht und dem Wir ein Licht gegeben haben, worin er unter den Menschen geht, wie einer, dessen Gleichnis das jemandes ist, der sich in Finsternissen befindet, aus denen er nicht herauskommen kann? So ist den Ungläubigen ausgeschmückt, was sie zu tun pflegten." });
                        surah6.VerseList.Add(new Verse { Number = 123, TextBosnian = "Isto tako, Mi učinimo da u svakom naselju velikaši postanu prestupnici i da u njemu lukavstva smišljaju, ali oni samo protiv sebe lukavstva smišljaju, a da i ne primjećuju.", TextEnglish = "And thus We have placed within every city the greatest of its criminals to conspire therein. But they conspire not except against themselves, and they perceive [it] not.", TextGerman = "Und so haben Wir in jeder Stadt ihre größten Übeltäter bestimmt, damit sie darin Ränke schmieden. Sie schmieden aber Ränke nur gegen sich selbst, ohne zu merken." });
                        surah6.VerseList.Add(new Verse { Number = 124, TextBosnian = "A kada njima kakav znak dođe, oni govore: 'Mi nećemo vjerovati sve dok se i nama ne da nešto slično onome što je Allahovim poslanicima dato.' A Allah najbolje zna kome će povjeriti poslanstvo Svoje. Prestupnike će sigurno kod Allaha zadesiti poniženje i stići će ih patnja velika zato što su spletkarili.", TextEnglish = "And when a sign comes to them, they say, 'Never will we believe until we are given like that which was given to the messengers of Allāh.' Allāh is most knowing of where [i.e., with whom] He places His message. There will afflict those who committed crimes debasement before Allāh and severe punishment for what they used to conspire.", TextGerman = "Und wenn ein Zeichen zu ihnen kommt, sagen sie: Wir werden nicht glauben, bevor uns nicht gegeben wird, was den Gesandten Allahs gegeben wurde. Allah weiß sehr wohl, wo Er Seine Botschaft anbringt. Diejenigen, die Übeltaten begehen, werden bei Allah Erniedrigung und strenge Strafe treffen dafür, daß sie Ränke zu schmieden pflegten." });
                        surah6.VerseList.Add(new Verse { Number = 125, TextBosnian = "Onome koga Allah želi uputiti -On srce njegovo prema islamu raspoloži, a onome kome hoće dati da je u zabludi - On srce njegovo tijesnim i stegnutim učini, kao kad čini napor da na nebo uzleti. Eto, tako Allah učini da gnusoba - šejtan ovlada nad onima koji ne vjeruju.", TextEnglish = "So whoever Allāh wants to guide - He expands his breast to [contain] Islām; and whoever He wants to send astray[342] - He makes his breast tight and constricted as though he were climbing into the sky. Thus does Allāh place defilement upon those who do not believe.", TextGerman = "Wen Allah rechtleiten will, dem tut Er die Brust auf für den Islam. Und wen Er in die Irre gehen lassen will, dem macht Er die Brust eng und bedrängt, so als ob er in den Himmel hochsteigen sollte. So legt Allah den Greuel auf diejenigen, die nicht glauben." });
                        surah6.VerseList.Add(new Verse { Number = 126, TextBosnian = "Ovo je pravi put Gospodara tvoga, a Mi smo obrazložili ajete i znakove ljudima koji promišljaju i pouku primaju.", TextEnglish = "And this is the path of your Lord, [leading] straight. We have detailed the verses for a people who remember.", TextGerman = "Und dies ist der Weg deines Herrn, ein gerader. Nunmehr haben Wir die Zeichen ausführlich dargelegt für Leute, die bedenken." });
                        surah6.VerseList.Add(new Verse { Number = 127, TextBosnian = "Njih čeka Kuća spasa u Gospodara njihova; On će biti zaštitnik njihov zbog onoga što su činili.", TextEnglish = "For them will be the Home of Peace [i.e., Paradise] with their Lord. And He will be their protecting friend because of what they used to do.", TextGerman = "Für sie gibt es die Wohnstätte des Friedens bei ihrem Herrn. Er ist ihr Schutzherr, für das, was sie zu tun pflegten." });
                        surah6.VerseList.Add(new Verse { Number = 128, TextBosnian = "A na Dan kada On sve sakupi: 'O skupe šejtanski, vi ste mnoge ljude zaveli!' 'Gospodaru naš', reći će ljudi, štićenici njihovi, 'mi smo jedni drugima bili od koristi i stigli smo do roka našeg koji si nam odredio Ti!' 'Vatra će biti prebivalište vaše', 'reći će On', 'u njoj ćete vječno ostati, osim ako Allah drukčije ne odredi.' Gospodar tvoj je, zaista, mudar i Onaj Koji sve zna.", TextEnglish = "And [mention, O Muḥammad], the Day when He will gather them together [and say], 'O company of jinn, you have [misled] many of mankind.' And their allies among mankind will say, 'Our Lord, some of us made use of others, and we have [now] reached our term which You appointed for us.' He will say, 'The Fire is your residence, wherein you will abide eternally, except for what Allāh wills. Indeed, your Lord is Wise and Knowing.'", TextGerman = "Und an dem Tag, da Er sie alle versammelt (wird Er sagen): O Gesellschaft der Ginn! Viele Menschen habt ihr (verführt). Und ihre Schützlinge unter den Menschen sagen: Unser Herr, wir haben voneinander (den Vorteil) genossen und haben (nun) unsere Frist erreicht, die Du uns gesetzt hast. Er wird sagen: Das (Höllen)feuer ist euer Aufenthalt, ewig darin zu bleiben, außer Allah will es (anders). Gewiß, dein Herr ist Allweise und Allwissend." });
                        surah6.VerseList.Add(new Verse { Number = 129, TextBosnian = "Tako isto Mi prepuštamo vlast jednim zulumćarima nad drugima zbog onoga što su zaradili.", TextEnglish = "And thus will We make some of the wrongdoers allies of others for what they used to earn.[343]", TextGerman = "So machen Wir die einen Ungerechten zu Beschützern der anderen für das, was sie erworben haben." });
                        surah6.VerseList.Add(new Verse { Number = 130, TextBosnian = "O skupe džinski i ljudski, zar vam iz redova vaših nisu dolazili poslanici koji su vam ajete Moje kazivali i upozoravali vas da ćete ovaj vaš dan dočekati? Oni će reći: 'Mi svjedočimo protiv sebe.' Njih je život na Zemlji bio obmanuo i oni će sami protiv sebe posvjedočiti da su bili nevjernici.", TextEnglish = "O company of jinn and mankind,[344] did there not come to you messengers from among you, relating to you My verses and warning you of the meeting of this Day of yours?' They will say, 'We bear witness against ourselves'; and the worldly life had deluded them, and they will bear witness against themselves that they were disbelievers.", TextGerman = "O Gesellschaft der Ginn und der Menschen, sind nicht zu euch Gesandte von euch (selbst) gekommen, die euch Meine Zeichen berichtet und euch vor dem Eintreffen dieses euren Tages gewarnt haben? Sie werden sagen: Wir zeugen gegen uns selbst. Das diesseitige Leben hat sie getäuscht, und sie zeugen gegen sich selbst, daß sie ungläubig waren." });
                        surah6.VerseList.Add(new Verse { Number = 131, TextBosnian = "Tako je, jer Gospodar tvoj nije uništavao naselja zbog zuluma njihova - bez prethodne opomene njihovim stanovnicima.?'", TextEnglish = "That is because your Lord would not destroy the cities for wrongdoing[345] while their people were unaware.", TextGerman = "Das (ist so), weil dein Herr die Städte nicht zu Unrecht vernichtet’, während ihre Bewohner unachtsam sind." });
                        surah6.VerseList.Add(new Verse { Number = 132, TextBosnian = "Svima će pripasti nagrada ili kazna, već prema tome kako su postupali, a Gospodar tvoj nije nemaran prema onom šta su radili.", TextEnglish = "And for all are degrees [i.e., positions resulting] from what they have done. And your Lord is not unaware of what they do.", TextGerman = "Für alle wird es Rangstufen geben je nach dem, was sie getan haben. Und dein Herr ist nicht unachtsam dessen, was sie tun." });
                        surah6.VerseList.Add(new Verse { Number = 133, TextBosnian = "Gospodar tvoj je neovisan i milostiv. Ako hoće, uklonit će vas, i poslije vas one koje On hoće dovesti, kao što je od potomstva drugih naroda vas stvorio.", TextEnglish = "And your Lord is the Free of need, the possessor of mercy. If He wills, He can do away with you and give succession after you to whomever He wills, just as He produced you from the descendants of another people.", TextGerman = "Und dein Herr ist der Unbedürftige, voll der Barmherzigkeit. Wenn Er will, läßt Er euch vergehen und nach euch folgen, was Er will, so wie Er euch aus der Nachkommenschaft anderer Leute entstehen ließ." });
                        surah6.VerseList.Add(new Verse { Number = 134, TextBosnian = "Ono što vam se prijeteći obećava doista će doći i vi nećete moći umaći.", TextEnglish = "Indeed, what you are promised is coming, and you will not cause failure [to Allāh].[346]", TextGerman = "Gewiß, was euch versprochen wird, wird sicher eintreffen. Und ihr werdet euch (Ihm) nicht entziehen können." });
                        surah6.VerseList.Add(new Verse { Number = 135, TextBosnian = "Reci: 'O narode moj, čini sve što možeš, činit ću, uistinu, i ja; a vi ćete već saznati koga čeka sretan kraj!' Zulumćari sigurno neće uspjeti.", TextEnglish = "Say, 'O my people, work according to your position; [for] indeed, I am working. And you are going to know who will have succession in the home.[347] Indeed, the wrongdoers will not succeed.'", TextGerman = "Sag: O mein Volk, handelt nach eurer Stellung! Ich werde ebenfalls (so) handeln. Dann werdet ihr (noch) erfahren, wem die letztendliche Wohnstätte gehören wird. Gewiß, den Ungerechten wird es nicht wohl ergehen." });
                        surah6.VerseList.Add(new Verse { Number = 136, TextBosnian = "Oni određuju za Allaha dio ljetine i dio stoke, koju je On stvorio, pa govore: 'Ovo je za Allaha', tvrde oni, 'a ovo za božanstva naša!' Međutim, ono što je namijenjeno božanstvima njihovim ne stiže Allahu, dok ono što je određeno za Allaha stiže božanstvima njihovim!? Kako ružno oni sude!", TextEnglish = "And they [i.e., the polytheists] assign to Allāh from that which He created of crops and livestock a share and say, 'This is for Allāh,' by their claim, 'and this is for our 'partners' [associated with Him].' But what is for their 'partners' does not reach Allāh, while what is for Allāh - this reaches their 'partners.' Evil is that which they rule.", TextGerman = "Und sie haben Allah mit dem, was Er an (Früchten auf den) Saatfeldern und Vieh wachsen läßt, einen Anteil bestimmt. Und sie sagen: Dies ist für Allah - so behaupten sie -, Und dies ist für unsere Teilhaber. Was nun für ihre Teilhaber (bestimmt) ist, gelangt nicht zu Allah, was aber für Allah (bestimmt) ist, gelangt zu ihren Teilhabern. Wie böse ist, was sie urteilen!" });
                        surah6.VerseList.Add(new Verse { Number = 137, TextBosnian = "Mnogim su mušricima isto tako šejtani njihovi ubijanje vlastite djece lijepim prikazali, da bi ih upropastili i da bi ih u vjeri njihovoj zbunili. A da je Allah htio, oni to ne bi činili. Zato, i njih i njihove izmišljotine ostavi!", TextEnglish = "And likewise, to many of the polytheists their partners[348] have made [to seem] pleasing the killing of their children in order to bring about their destruction and to cover them with confusion in their religion. And if Allāh had willed, they would not have done so. So leave them and that which they invent.", TextGerman = "Und ebenso haben vielen der Götzendiener ihre Teilhaber wohlgefällig erscheinen lassen, ihre Kinder zu töten, um sie ins Verderben zu stürzen und um ihnen ihre Religion zu verdecken. Wenn Allah wollte, hätten sie es nicht getan. So lasse sie (allein) mit dem, was sie an Lügen ersinnen!" });
                        surah6.VerseList.Add(new Verse { Number = 138, TextBosnian = "Oni govore: 'Ova stoka i plodovi zabranjeni su, smiju ih jesti samo oni kojima mi dozvoljavamo', tvrde oni, 'a ovo je stoka čija su leđa zabranjena.' Ima stoke prilikom čijeg klanja ne spominju Allahovo ime, izmišljajući o Njemu laži. A On će ih zbog onoga što izmišljaju, kazniti.", TextEnglish = "And they say, 'These animals[349] and crops are forbidden; no one may eat from them except whom we will,' by their claim. And there are those [camels] whose backs are forbidden [by them][350] and those upon which the name of Allāh is not mentioned[351] - [all of this] an invention of untruth about Him.[352] He will punish them for what they were inventing.", TextGerman = "Und sie sagen: Dies sind verwehrtes Vieh und verwehrte (Früchte auf den) Saatfelder(n). Niemand kostet davon außer, wen wir (essen lassen) wollen. - So behaupten sie. - Und (es gibt nach ihrer Behauptung) Vieh, dessen Rücken (zum Tragen von Lasten) verboten ist, und Vieh, über das sie den Namen Allahs nicht aussprechen, womit sie Lügen gegen Ihn ersinnen. Er wird ihnen vergelten, was sie stets an Lügen ersinnen." });
                        surah6.VerseList.Add(new Verse { Number = 139, TextBosnian = "Oni govore: 'Ono što je u utrobama ove i ove stoke dozvoljeno je samo muškarcima našim, a zabranjeno našim ženama. A ako se plod izjalovi, onda su u tome sudionici.' Allah će ih za neistine koje pričaju kazniti, zaista je On Mudri i Sveznajući.", TextEnglish = "And they say, 'What is in the bellies of these animals[353] is exclusively for our males and forbidden to our females. But if it is [born] dead, then all of them have shares therein.' He will punish them for their description.[354] Indeed, He is Wise and Knowing.", TextGerman = "Und sie sagen: Was in den Leibern dieses Viehs ist, ist unseren Männern vorbehalten, unseren Gattinnen aber verboten. Wenn es aber tot (geboren) ist, so haben sie (alle) daran teil. Er wird ihnen ihre (ersonnene) Zuschreibung vergelten. Er ist ja Allweise und Allwissend." });
                        surah6.VerseList.Add(new Verse { Number = 140, TextBosnian = "Gubitnici su oni koji iz lahkoumnosti i ne znajući šta rade djecu svoju ubijaju i koji ono čime ih je Allah opskrbio zabranjenim smatraju, izmišljajući o Allahu laži. Oni su zalutali i oni ne znaju šta rade.", TextEnglish = "They will have lost who killed their children in foolishness without knowledge and prohibited what Allāh had provided for them, inventing untruth about Allāh. They have gone astray and were not [rightly] guided.", TextGerman = "Verloren sind diejenigen, die ihre Kinder in Torheit und ohne Wissen töten und verbieten, womit Allah sie versorgt hat, indem sie Lügen gegen Allah ersinnen. Sie sind fürwahr irregegangen und nicht rechtgeleitet gewesen." });
                        surah6.VerseList.Add(new Verse { Number = 141, TextBosnian = "On je Taj Koji stvara vinograde, poduprte i nepoduprte, i palme i usjeve različita okusa, i masline i šipke - oboje, i slično i različito; jedite plodove njihove kad plod dadnu, i podajte na dan žetve i berbe ono na što drugi pravo imaju, i ne rasipajte, a On, zaista, ne voli rasipnike.", TextEnglish = "And He it is who causes gardens to grow, [both] trellised and untrellised, and palm trees and crops of different [kinds of] food and olives and pomegranates, similar and dissimilar. Eat of [each of] its fruit when it yields and give its due [zakāh] on the day of its harvest. And be not excessive.[355] Indeed, He does not like those who commit excess.", TextGerman = "Und Er ist es, Der Gärten mit Spalieren und ohne Spaliere entstehen läßt, sowie Palmen und (sonstige) Pflanzen mit unterschiedlichen Erntesorten und Öl- und Granatapfelbäume, die einander ähnlich und unähnlich sind. Eßt von ihren Früchten’, wenn sie Früchte tragen, und entrichtet am Tag ihrer Ernte ihre(n) Pflicht(anteil, der darauf steht), doch seid nicht maßlos! - Er liebt nicht die Maßlosen." });
                        surah6.VerseList.Add(new Verse { Number = 142, TextBosnian = "Stvara i stoku koja teret nosi i onu od koje je prostirka; jedite dio onoga čime vas Allah opskrbljuje, a ne slijedite šejtanove korake, zaista vam je on otvoreni neprijatelj,", TextEnglish = "And of the grazing livestock are carriers [of burdens] and those [too] small. Eat of what Allāh has provided for you and do not follow the footsteps of Satan.[356] Indeed, he is to you a clear enemy.", TextGerman = "Und an Vieh (läßt Allah entstehen) Lasttiere und Kleintiere. Eßt von dem, womit Allah euch versorgt hat, und folgt nicht den Fußstapfen des Satans! Er ist euch ein deutlicher Feind." });
                        surah6.VerseList.Add(new Verse { Number = 143, TextBosnian = "i to osam životinja u paru: par ovaca i par koza. Reci: 'Da li je On zabranio mužjake ili ženke, ili ono što se nalazi u utrobama ženki? Obavijestite me pouzdano, ako je istina to što govorite',", TextEnglish = "[They are] eight mates - of the sheep, two and of the goats, two. Say, 'Is it the two males He has forbidden or the two females or that which the wombs of the two females contain? Inform me with knowledge, if you should be truthful.'", TextGerman = "Acht (Tiere) zu Paaren (hat Er euch erschaffen): von den Schafen zwei und von den Ziegen zwei. - Sag: Sind es die (beiden) Männchen, die Er verboten hat, oder die (beiden) Weibchen, oder was der Mutterleib der (beiden) Weibchen umschließt? Tut (es) mir kund auf Grund von (wirklichem) Wissen, wenn ihr wahrhaftig seid!" });
                        surah6.VerseList.Add(new Verse { Number = 144, TextBosnian = "i par kamila i par goveda. Reci: 'Da li je On zabranio mužjake i ženke i ono što se nalazi u utrobama ženki? Da li ste vi bili prisutni kad vam je Allah to propisao?' Ima li onda većeg zulumćara od onoga koji, ne znajući istinu, izmišlja laži o Allahu da bi ljude u zabludu doveo? Allah sigurno neće uputiti na Pravi put ljude koji su zulumćari.", TextEnglish = "And of the camels, two and of the cattle, two. Say, 'Is it the two males He has forbidden or the two females or that which the wombs of the two females contain? Or were you witnesses when Allāh charged you with this? Then who is more unjust than one who invents a lie about Allāh to mislead the people by [something] other than knowledge? Indeed, Allāh does not guide the wrongdoing people.'", TextGerman = "Und (auch) von den Kamelen zwei und von den Rindern zwei. - Sag: Sind es die (beiden) Männchen, die Er verboten hat, oder die (beiden) Weibchen, oder was der Mutterleib der (beiden) Weibchen umschließt? Oder wart ihr Zeugen, als Allah euch dies anbefahl? Wer ist denn ungerechter, als wer gegen Allah eine Lüge ersinnt, um die Menschen ohne Wissen in die Irre zu führen? Gewiß, Allah leitet das ungerechte Volk nicht recht." });
                        surah6.VerseList.Add(new Verse { Number = 145, TextBosnian = "Reci: 'Ja ne vidim u ovome što mi se objavljuje da je ikome zabranjeno jesti ma šta drugo osim strvi, ili krvi koja istječe, ili svinjskog mesa, to je, doista, pogano, ili što je kao vid neposluha zaklano u nečije drugo, a ne u Allahovo ime.' A bude li ko primoran, a ne želi to i ne pretjeruje u tome - pa Gospodar tvoj je, doista, Oprostitelj grijeha, Milostivi.", TextEnglish = "Say, 'I do not find within that which was revealed to me [anything] forbidden to one who would eat it unless it be a dead animal or blood spilled out or the flesh of swine - for indeed, it is impure - or it be [that slaughtered in] disobedience, dedicated to other than Allāh.[357] But whoever is forced [by necessity], neither desiring [it] nor transgressing [its limit], then indeed, your Lord is Forgiving and Merciful.'", TextGerman = "Sag: Ich finde in dem, was mir (als Offenbarung) eingegeben wurde, nichts, das für den Essenden zu essen verboten wäre, außer es ist Verendetes oder ausgeflossenes Blut oder Schweinefleisch - denn das ist ein Greuel - oder ein Frevel, worüber ein anderer (Name) als Allah(s) angerufen worden ist. Wer sich aber in einer Zwangslage befindet, ohne zu begehren oder das Maß zu überschreiten, - so ist dein Herr Allvergebend und Barmherzig." });
                        surah6.VerseList.Add(new Verse { Number = 146, TextBosnian = "Jevrejima smo sve životinje koje imaju kopita ili kandže zabranili, a od goveda i brava njihov loj, osim onog s leđa ili s crijeva, ili onog pomiješanog s kostima. Time smo ih zbog zuluma njihova kaznili; i Mi, zaista, govorimo istinu.", TextEnglish = "And to those who are Jews We prohibited every animal of uncloven hoof; and of the cattle and the sheep We prohibited to them their fat, except what adheres to their backs or the entrails or what is joined with bone. [By] that We repaid them for their transgression. And indeed, We are truthful.", TextGerman = "Und denen, die dem Judentum angehören, haben Wir alles verboten, was Klauen besitzt; und von den Rindern und dem Kleinvieh haben Wir ihnen das Fett verboten, außer dem, was ihre Rücken und ihre Eingeweide tragen, oder was mit Knochen verwachsen ist. Damit haben Wir ihnen ihre Übertretung vergolten. Und Wir sagen ganz gewiß die Wahrheit." });
                        surah6.VerseList.Add(new Verse { Number = 147, TextBosnian = "A ako ti oni ne budu vjerovali, ti reci: 'Gospodar vaš je posjednik milosti neizmjerne, ali kazne Njegove ne bivaju pošteđeni ljudi prestupnici.'", TextEnglish = "So if they deny you, [O Muḥammad], say, 'Your Lord is the possessor of vast mercy; but His punishment cannot be repelled from the people who are criminals.'", TextGerman = "Wenn sie dich nun der Lüge bezichtigen, dann sag: Euer Herr ist voll umfassender Barmherzigkeit, aber vom übeltätigen Volk wird Seine Gewalt nicht abgewandt." });
                        surah6.VerseList.Add(new Verse { Number = 148, TextBosnian = "Govorit će oni koji su Allahu druge u obožavanju pridruživali: 'Da je Allah htio, mi ne bismo širk činili, a ni preci naši, niti bismo išta zabranjenim učinili.' Tako su i oni prije njih poricali, sve dok Našu kaznu nisu iskusili. Reci: 'Imate li vi kakav dokaz, da nam ga iznesete? Vi se samo za pretpostavkama povodite i vi samo neistinu govorite.'", TextEnglish = "Those who associated [others] with Allāh will say, 'If Allāh had willed, we would not have associated [anything] and neither would our fathers, nor would we have prohibited anything.' Likewise did those before deny until they tasted Our punishment. Say, 'Do you have any knowledge that you can produce for us? You follow not except assumption, and you are not but misjudging.'", TextGerman = "Diejenigen, die (Ihm) beigesellen, werden sagen: Wenn Allah gewollt hätte, hätten wir (Ihm) nichts beigesellt, und (auch) nicht unsere Väter, und wir hätten nichts verboten. Ebenso haben diejenigen vor ihnen (ihre Gesandten) der Lüge bezichtigt, bis sie Unsere Gewalt kosteten. Sag: Habt ihr (irgendein) Wissen, das ihr uns vorbringen könnt? Ihr folgt ja nur Mutmaßungen, und ihr stellt nur Schätzungen an." });
                        surah6.VerseList.Add(new Verse { Number = 149, TextBosnian = "Reci: 'Allah ima potpun dokaz, i da On hoće, sve bi vas na Pravi put uputio!'", TextEnglish = "Say, 'With Allāh is the far-reaching [i.e., conclusive] argument. If He had willed, He would have guided you all.'", TextGerman = "Sag: Allah hat das überzeugende Beweismittel. Wenn Er gewollt hätte, hätte Er euch fürwahr allesamt rechtgeleitet." });
                        surah6.VerseList.Add(new Verse { Number = 150, TextBosnian = "Reci: 'Dovedite svoje svjedoke koji će posvjedočiti da je Allah to zabranio!' Pa ako oni posvjedoče, ti im nemoj povjerovati i ne povodi se za strastima onih koji Naše ajete i znakove smatraju lažnim i koji u ahiret ne vjeruju i koji druge Gospodaru svome ravnim smatraju.", TextEnglish = "Say, [O Muḥammad], 'Bring forward your witnesses who will testify that Allāh has prohibited this.' And if they testify, do not testify with them. And do not follow the desires of those who deny Our verses and those who do not believe in the Hereafter, while they equate [others] with their Lord.", TextGerman = "Sag: Bringt eure Zeugen her, die bezeugen, daß Allah dies verboten hat! Wenn sie (es) dann bezeugen, so bezeuge (es) nicht mit ihnen. Und folge nicht den Neigungen derjenigen, die Unsere Zeichen für Lüge erklären und die nicht an das Jenseits glauben und ihrem Herrn (andere) gleichsetzen." });
                        surah6.VerseList.Add(new Verse { Number = 151, TextBosnian = "Reci: 'Dođite da vam kažem šta vam Gospodar vaš propisuje: da Mu ništa u obožavanju ne pridružujete, da roditeljima dobro činite, da djecu svoju zbog neimaštine ne ubijate, Mi i vas i njih hranimo, ne približujte se nevaljalštinama, bile javne ili tajne; ne ubijajte onog koga je Allah zabranio ubiti, osim kada to pravda zahtijeva; to vam On zapovijeda, da biste razumjeli.'", TextEnglish = "Say, 'Come, I will recite what your Lord has prohibited to you. [He commands] that you not associate anything with Him, and to parents, good treatment, and do not kill your children out of poverty; We will provide for you and them. And do not approach immoralities - what is apparent of them and what is concealed. And do not kill the soul which Allāh has forbidden [to be killed] except by [legal] right. This has He instructed you that you may use reason.'", TextGerman = "Sag: Kommt her! Ich will euch verlesen, was euer Herr euch verboten hat: Ihr sollt Ihm nichts beigesellen, und zu den Eltern gütig sein; und tötet nicht eure Kinder aus Armut - Wir versorgen euch und auch sie; und nähert euch nicht den Abscheulichkeiten, was von ihnen offen und was verborgen ist; und tötet nicht die Seele, die Allah verboten hat (zu töten), außer aus einem rechtmäßigen Grund! Dies hat Er euch anbefohlen, auf daß ihr begreifen möget." });
                        surah6.VerseList.Add(new Verse { Number = 152, TextBosnian = "I ne približavajte se imetku siročeta, osim na najljepši način, sve dok punoljetno ne postane, i mjericu i vagu pravično napunite. Mi nikoga preko njegove mogućnosti ne zadužujemo; i kad govorite, po pravdi govorite, pa makar se ticalo i srodnika, i obaveze prema Allahu ispunjavajte, eto, to vam On zapovijeda da biste se prisjetili i pouku primili.", TextEnglish = "And do not approach the orphan's property except in a way that is best [i.e., intending improvement] until he reaches maturity. And give full measure and weight in justice. We do not charge any soul except [with that within] its capacity. And when you speak [i.e., testify], be just, even if [it concerns] a near relative. And the covenant of Allāh fulfill. This has He instructed you that you may remember.", TextGerman = "Und nähert euch nicht dem Besitz des Waisenkindes, außer auf die beste Art, bis es seine Vollreife erlangt hat. Und gebt volles Maß und Gewicht in Gerechtigkeit. Wir erlegen keiner Seele mehr auf, als sie zu leisten vermag. Und wenn ihr euer Wort gebt, dann seid gerecht, auch wenn es um einen Verwandten geht. Und haltet euren Bund gegenüber Allah. Dies hat Er euch anbefohlen, auf daß ihr (es) bedenken möget!" });
                        surah6.VerseList.Add(new Verse { Number = 153, TextBosnian = "I zato što je ovo pravi put Moj, njega se držite i druge puteve ne slijedite, pa da vas odvoje od puta Njegova; eto, to vam On naređuje, da biste se uščuvali.", TextEnglish = "And, [moreover], this is My path, which is straight, so follow it; and do not follow [other] ways, for you will be separated from His way. This has He instructed you that you may become righteous.", TextGerman = "Und (Er hat euch anbefohlen:) Dies ist Mein Weg, ein gerader. So folgt ihm! Und folgt nicht den (anderen) Wegen, damit sie euch nicht von Seinem Weg auseinanderführen! Dies hat Er euch anbefohlen, auf daß ihr gottesfürchtig werden möget!" });
                        surah6.VerseList.Add(new Verse { Number = 154, TextBosnian = "Mi smo Musau Knjigu dali da učinimo potpunom blagodat onome koji će prema njoj postupati i kao objašnjenje svemu i uputu i milost, da bi oni povjerovali da će pred Gospodara svoga stati.", TextEnglish = "Then[358] We gave Moses the Scripture, making complete [Our favor] upon the one who did good [i.e., Moses] and as a detailed explanation of all things and as guidance and mercy that perhaps in the meeting with their Lord they would believe.", TextGerman = "Hierauf gaben Wir Musa die Schrift als Vollendung (und Belohnung) für das, was er an Gutem getan hatte’, als eine ausführliche Darlegung für alles und als Rechtleitung und Barmherzigkeit, auf daß sie an die Begegnung mit ihrem Herrn glauben mögen!" });
                        surah6.VerseList.Add(new Verse { Number = 155, TextBosnian = "A ova je Knjiga koju objavljujemo blagoslovljena, zato je slijedite i Allaha se bojte, da bi vam milost ukazana bila.", TextEnglish = "And this [Qur’ān] is a Book We have revealed [which is] blessed, so follow it and fear Allāh that you may receive mercy.", TextGerman = "Und dies ist ein Buch, das Wir (als Offenbarung) hinabgesandt haben, ein gesegnetes (Buch). So folgt ihm und seid gottesfürchtig, auf daß ihr Erbarmen finden möget!" });
                        surah6.VerseList.Add(new Verse { Number = 156, TextBosnian = "I zato, da ne kažete: 'Knjiga je objavljena dvjema zajednicama prije nas, ali mi je ne znamo čitati kao oni',", TextEnglish = "[We revealed it] lest you say, 'The Scripture was only sent down to two groups before us, but we were of their study unaware,'", TextGerman = "Damit ihr (nicht) sagt: Die Schrift wurde nur auf zwei Gruppen vor uns herabgesandt, und wir waren gegenüber dem, was sie erlernt haben, wahrlich unachtsam." });
                        surah6.VerseList.Add(new Verse { Number = 157, TextBosnian = "i da ne kažete: 'Da je Knjiga objavljena nama, bolje bismo se od njih držali Pravog puta.' Pa objavljuje vam se, eto, od Gospodara vašeg jasan dokaz i uputa i milost; i ima li, onda, većeg zulumćara od onoga koji Allahove ajete ne priznaje i od njih odvraća?! A Mi ćemo teškom mukom kazniti one koji od ajeta Naših odvraćaju, zato što to stalno čine.", TextEnglish = "Or lest you say, 'If only the Scripture had been revealed to us, we would have been better guided than they.' So there has [now] come to you a clear evidence from your Lord and a guidance and mercy. Then who is more unjust than one who denies the verses of Allāh and turns away from them? We will recompense those who turn away from Our verses with the worst of punishment for their having turned away.", TextGerman = "Oder damit ihr (nicht) sagt: Wenn die Schrift auf uns herabgesandt worden wäre, wären wir wahrlich eher rechtgeleitet als sie. Aber nun ist ein klarer Beweis von eurem Herrn und Rechtleitung und Barmherzigkeit zu euch gekommen. Wer ist denn ungerechter, als wer Allahs Zeichen für Lüge erklärt und sich von ihnen abwendet? Wir werden denjenigen, die sich von Unseren Zeichen abwenden, mit einer bösen Strafe vergelten, daß sie sich stets abwandten." });
                        surah6.VerseList.Add(new Verse { Number = 158, TextBosnian = "Zar oni čekaju da im meleci dođu, ili da im Gospodar tvoj dođe, ili neki predznaci od Gospodara tvoga?! Onoga dana kada neki predznaci od Gospodara tvoga dođu nijednom čovjeku neće koristiti to što će tada vjerovati, ako prije nije vjerovao, ili ako nije kao vjernik kakvo dobro uradio. Reci: 'Samo vi čekajte, i mi ćemo, doista, čekati!'", TextEnglish = "Do they [then] wait for anything except that the angels should come to them or your Lord should come or that there come some of the signs[359] of your Lord? The Day that some of the signs of your Lord will come no soul will benefit from its faith as long as it had not believed before or had earned through its faith some good. Say, 'Wait. Indeed, we [also] are waiting.'", TextGerman = "Erwarten sie (etwas anderes), als daß die Engel zu ihnen kommen oder daß dein Herr kommt oder daß etwas von den Zeichen deines Herrn kommt? An dem Tag, da etwas von den Zeichen deines Herrn kommt, nützt einer Seele ihr Glaube nicht, wenn sie nicht schon zuvor geglaubt oder in ihrem Glauben Gutes erworben hat. Sag: Wartet ab! Wir warten ebenfalls ab." });
                        surah6.VerseList.Add(new Verse { Number = 159, TextBosnian = "Zaista, s onima koji su vjeru svoju razdijelili i u stranke se podijelili ti nemaš ništa! Njihov slučaj je Allahu predat. On će ih, potom, o onome što su radili obavijestiti.", TextEnglish = "Indeed, those who have divided their religion and become sects - you, [O Muḥammad], are not [associated] with them in anything. Their affair is only [left] to Allāh; then He will inform them about what they used to do.", TextGerman = "Gewiß, mit denjenigen, die ihre Religion spalteten und zu Lagern geworden sind, hast du nichts gemein. Ihre Angelegenheit steht (allein) bei Allah. Hierauf wird Er ihnen kundtun, was sie zu tun pflegten." });
                        surah6.VerseList.Add(new Verse { Number = 160, TextBosnian = "Ko god uradi dobro djelo, bit će deseterostruko nagrađen; a ko god uradi loše djelo, bit će samo prema zasluzi kažnjen, i neće im zulum biti učinjen.", TextEnglish = "Whoever comes [on the Day of Judgement] with a good deed will have ten times the like thereof [to his credit], and whoever comes with an evil deed will not be recompensed except the like thereof; and they will not be wronged.[360]", TextGerman = "Wer mit (etwas) Gutem kommt, erhält zehnmal soviel. Und Wer mit einer bösen Tat kommt, dem wird nur gleichviel vergolten, und es wird ihnen kein Unrecht zugefügt." });
                        surah6.VerseList.Add(new Verse { Number = 161, TextBosnian = "Reci: 'Mene je Gospodar moj na Pravi put zaista uputio, u pravu vjeru, vjeru Ibrahima pravovjemika, a on višebožac nije bio.'", TextEnglish = "Say, 'Indeed, my Lord has guided me to a straight path - a correct religion - the way of Abraham, inclining toward truth. And he was not among those who associated others with Allāh.'", TextGerman = "Sag: Gewiß, mich hat mein Herr zu einem geraden Weg geleitet, einer richtigen Religion, dem Glaubensbekenntnis Ibrahims, als Anhänger des rechten Glaubens, und er war keiner der Götzendiener." });
                        surah6.VerseList.Add(new Verse { Number = 162, TextBosnian = "Reci: 'Klanjanje moje, i obredi moji, i život moj, i smrt moja doista su zarad Allaha, Gospodara svjetova,", TextEnglish = "Say, 'Indeed, my prayer, my rites of sacrifice, my living and my dying are for Allāh, Lord of the worlds.", TextGerman = "Sag: Gewiß, mein Gebet und mein (Schlacht)opfer, mein Leben und mein Sterben gehören Allah, dem Herrn der Weltenbewohner." });
                        surah6.VerseList.Add(new Verse { Number = 163, TextBosnian = "Koji nema saučesnika; to mi je naređeno i ja sam prvi musliman.'", TextEnglish = "No partner has He. And this I have been commanded, and I am the first [among you] of the Muslims.'[361]", TextGerman = "Er hat keinen Teilhaber. Dies ist mir befohlen worden, und ich bin der erste der (Ihm) Ergebenen." });
                        surah6.VerseList.Add(new Verse { Number = 164, TextBosnian = "Reci: 'Zar da za Gospodara tražim nekog drugog osim Allaha, kad je On Gospodar svega?!' Što god ko uradi, sebi uradi, i svaki grešnik samo će svoje breme nositi. Na kraju, Gospodaru svome ćete se vratiti i On će vas o onome u čemu ste se razilazili obavijestiti.", TextEnglish = "Say, 'Is it other than Allāh I should desire as a lord while He is the Lord of all things? And every soul earns not [blame] except against itself, and no bearer of burdens will bear the burden of another. Then to your Lord is your return, and He will inform you concerning that over which you used to differ.'", TextGerman = "Sag: Soll ich einen anderen Herrn als Allah begehren, wo Er doch der Herr von allem ist? Jede Seele erwirbt nur gegen sich selbst. Und keine lasttragende (Seele) nimmt die Last einer anderen auf sich. Hierauf wird eure Rückkehr zu eurem Herrn sein, und Er wird kundtun, worüber ihr uneinig zu sein pflegtet." });
                        surah6.VerseList.Add(new Verse { Number = 165, TextBosnian = "On čini da jedni druge na Zemlji smjenjujete i On vas po položajima jedne iznad drugih uzdiže, da bi vas iskušao u onome što vam daje. Gospodar tvoj, zaista, brzo kažnjava, ali On je, uistinu, i Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And it is He who has made you successors upon the earth and has raised some of you above others in degrees [of rank] that He may try you through what He has given you. Indeed, your Lord is swift in penalty; but indeed, He is Forgiving and Merciful.", TextGerman = "Er ist es, Der euch zu Nachfolgern (auf) der Erde gemacht und die einen von euch über die anderen um Rangstufen erhöht hat, damit Er euch mit dem, was Er euch gegeben hat, prüfe. Gewiß, dein Herr ist schnell im Bestrafen, aber Er ist auch wahrlich Allvergebend und Barmherzig." });
                        result = surah6;
                        break;
                    #endregion

                    // 
                    #region 7 Al-Araf
                    case 7:
                        Surah surah7 = new Surah(); surah7.Name = Constants.Surah7Name; surah7.Number = 7; surah7.VerseList = new List<Verse>();
                        surah7.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah7.VerseList.Add(new Verse { Number = 1, TextBosnian = "Elif-lam-mim-sad.", TextEnglish = "Alif, Lām, Meem, Ṣād.[363]", TextGerman = "Alif-Lam-Mim-Sad" });
                        surah7.VerseList.Add(new Verse { Number = 2, TextBosnian = "Ovo je knjiga koja ti se spušta - pa neka ti u grudima ne bude nikakve tegobe zbog nje - da njome upozoravaš, i da vjernicima bude opomena.", TextEnglish = "[This is] a Book revealed to you, [O Muḥammad] - so let there not be in your breast distress therefrom - that you may warn thereby and as a reminder to the believers.", TextGerman = "(Dies ist) ein Buch, das zu dir (als Offenbarung) herabgesandt worden ist - es soll seinetwegen in deiner Brust keine Bedrängnis sein -, damit du mit ihm warnst, und als Ermahnung für die Gläubigen." });
                        surah7.VerseList.Add(new Verse { Number = 3, TextBosnian = "Slijedite ono što vam se od Gospodara vašeg objavljuje i pored Njega ne slijedite druge kao zaštitnike! A kako se vi malo prisjećate i pouku primate?!", TextEnglish = "Follow, [O mankind], what has been revealed to you from your Lord and do not follow other than Him any allies. Little do you remember.", TextGerman = "Folgt dem, was zu euch von eurem Herrn herabgesandt worden ist, und folgt außer Ihm keinen (anderen) Schutzherren! Wie wenig ihr bedenkt!" });
                        surah7.VerseList.Add(new Verse { Number = 4, TextBosnian = "Koliko smo Mi samo naselja razorili; a kazna im je Naša dolazila noću ili danju kad bi prilegli?", TextEnglish = "And how many cities have We destroyed, and Our punishment came to them at night or while they were sleeping at noon.", TextGerman = "Wie viele Städte haben Wir vernichtet! Da kam Unsere Gewalt über sie bei Nacht, oder während sie zu Mittag ruhten." });
                        surah7.VerseList.Add(new Verse { Number = 5, TextBosnian = "A priznanje njihovo, kada bi im došla kazna Naša, bile su samo riječi: Zulumćari smo, zaista, bili!", TextEnglish = "And their declaration when Our punishment came to them was only that they said, 'Indeed, we were wrongdoers!'", TextGerman = "So war ihr Ausruf, als Unsere Gewalt über sie kam, nur, daß sie sagten: Wir haben gewiß Unrecht getan." });
                        surah7.VerseList.Add(new Verse { Number = 6, TextBosnian = "I sigurno ćemo pitati one kojima smo poslanike slali, a pitat ćemo, doista, i poslanike,", TextEnglish = "Then We will surely question those to whom [a message] was sent, and We will surely question the messengers.", TextGerman = "Ganz gewiß werden Wir diejenigen, zu denen Gesandte gesandt worden sind, fragen, und ganz gewiß werden Wir die Gesandten fragen." });
                        surah7.VerseList.Add(new Verse { Number = 7, TextBosnian = "pa ćemo im, sigurno, kazivati po znanju Našemu, sve što o njima znamo, a Mi nismo odsutni bili.", TextEnglish = "Then We will surely relate [their deeds] to them with knowledge, and We were not [at all] absent.", TextGerman = "Dann werden Wir ihnen ganz gewiß aus (vollem) Wissen berichten, denn Wir waren ja nicht abwesend." });
                        surah7.VerseList.Add(new Verse { Number = 8, TextBosnian = "Vaganje toga dana bit će pravedno: oni čiji tas dobra bude teži, oni će uspjeti;", TextEnglish = "And the weighing [of deeds] that Day will be the truth. So those whose scales are heavy - it is they who will be the successful.", TextGerman = "Das Wägen erfolgt an jenem Tag der Wahrheit (entsprechend). Wessen Waagschalen schwer sein werden, jene sind es, denen es wohl ergeht." });
                        surah7.VerseList.Add(new Verse { Number = 9, TextBosnian = "a oni čiji tas dobra bude lakši, oni će, zato što ajete i znakove Naše nisu priznavali, stradati.", TextEnglish = "And those whose scales are light - they are the ones who will lose themselves for what injustice they were doing toward Our verses.", TextGerman = "Wessen Waagschalen aber leicht sein werden, das sind diejenigen, die ihre Seelen verloren haben dafür, daß sie an Unseren Zeichen unrecht zu handeln pflegten." });
                        surah7.VerseList.Add(new Verse { Number = 10, TextBosnian = "Mi smo vas na Zemlji smjestili i na njoj vam sve što je potrebno za život dali, kako vi malo zahvaljujete?!", TextEnglish = "And We have certainly established you upon the earth and made for you therein ways of livelihood. Little are you grateful.", TextGerman = "Und Wir haben euch ja auf der Erde eine feste Stellung verliehen, und Wir haben für euch auf ihr (Möglichkeiten für den) Lebensunterhalt geschaffen. Wie wenig ihr dankbar seid!" });
                        surah7.VerseList.Add(new Verse { Number = 11, TextBosnian = "Mi smo vas stvorili i onda vam oblik dali, a poslije melecima rekli: 'Sedždu Ademu učinite!' Pa svi sedždu učiniše, osim Iblisa, on nije bio među onim koji na sedždu padoše!", TextEnglish = "And We have certainly created you, [O mankind], and given you [human] form. Then We said to the angels, 'Prostrate to Adam'; so they prostrated, except for Iblees.[364] He was not of those who prostrated.", TextGerman = "Und Wir haben euch ja erschaffen. Hierauf haben Wir euch gestaltet. Hierauf haben Wir zu den Engeln gesagt: Werft euch vor Adam nieder! Da warfen sie sich nieder, außer Iblis. Er gehörte nicht zu denjenigen, die sich niederwerfen." });
                        surah7.VerseList.Add(new Verse { Number = 12, TextBosnian = "Šta te je navelo pa da ničice ne padneš kad sam ti naredio?', upita On. 'Ja sam bolji od njega; mene si od vatre stvorio, a njega od ilovače', odgovori.", TextEnglish = "[Allāh] said, 'What prevented you from prostrating when I commanded you?' [Satan] said, 'I am better than him. You created me from fire and created him from clay [i.e., earth].'", TextGerman = "Er (Allah) sagte: Was hat dich davon abgehalten, dich niederzuwerfen, als Ich (es) dir befahl? Er sagte: Ich bin besser als er. Du hast mich aus Feuer erschaffen, ihn aber hast Du aus Lehm erschaffen." });
                        surah7.VerseList.Add(new Verse { Number = 13, TextBosnian = "E, onda silazi iz Dženneta', reče On, 'nije tvoje da se u njemu oholiš; silazi, ti si, zaista, među prezrenim!'", TextEnglish = "[Allāh] said, 'Descend from it [i.e., Paradise], for it is not for you to be arrogant therein. So get out; indeed, you are of the debased.'", TextGerman = "Er (Allah) sagte: So geh fort von ihm! Es steht dir nicht zu, darin hochmütig zu sein. So geh hinaus! Gewiß, du gehörst zu den Geringgeachteten." });
                        surah7.VerseList.Add(new Verse { Number = 14, TextBosnian = "Daj mi vremena do dana njihova oživljenja!', zamoli.", TextEnglish = "[Satan] said, 'Reprieve me until the Day they are resurrected.'", TextGerman = "Er sagte: Gewähre mir Aufschub bis zu dem Tag, da sie auferweckt werden." });
                        surah7.VerseList.Add(new Verse { Number = 15, TextBosnian = "Daje ti se vremena!', reče On.", TextEnglish = "[Allāh] said, 'Indeed, you are of those reprieved.'", TextGerman = "Er (Allah) sagte: Du sollst gewiß zu denjenigen gehören, denen Aufschub gewährt wird." });
                        surah7.VerseList.Add(new Verse { Number = 16, TextBosnian = "E, kao što si me u zabludu zaveo', reče,' tako ću i ja ljude na Tvom Pravom putu sigurno presretati,", TextEnglish = "[Satan] said, 'Because You have put me in error, I will surely sit in wait for them [i.e., mankind] on Your straight path.", TextGerman = "Er sagte: Darum, daß Du mich in Verirrung hast fallen lassen, werde ich ihnen ganz gewiß auf Deinem geraden Weg auflauern." });
                        surah7.VerseList.Add(new Verse { Number = 17, TextBosnian = "pa ću im, doista, i sprijeda, i straga, i zdesna, i slijeva prilaziti, i Ti ćeš ustanoviti da većina njih neće zahvalna biti!'", TextEnglish = "Then I will come to them from before them and from behind them and on their right and on their left, and You will not find most of them grateful [to You].'", TextGerman = "Hierauf werde ich ganz gewiß von vorn und von hinten, von ihrer Rechten und von ihrer Linken über sie kommen. Und Du wirst die meisten von ihnen nicht dankbar finden." });
                        surah7.VerseList.Add(new Verse { Number = 18, TextBosnian = "Izlazi iz njega, pokuđen i ponižen!', reče On. 'Tobom i svima koji se za tobom budu povodili doista ću Džehennem napuniti!'", TextEnglish = "[Allāh] said, 'Depart from it [i.e., Paradise], reproached and expelled. Whoever follows you among them - I will surely fill Hell with you, all together.'", TextGerman = "Er (Allah) sagte: Geh hinaus aus ihm, verachtet und verstoßen! Wer auch immer von ihnen dir folgt, Ich werde die Hölle ganz gewiß mit euch allesamt füllen." });
                        surah7.VerseList.Add(new Verse { Number = 19, TextBosnian = "ti, o Ademe, i supruga tvoja u Džennetu stanujte i odakle god hoćete jedite, samo se ovom drvetu ne približujte, pa da tako u redovima zulumćara budete!'", TextEnglish = "And 'O Adam, dwell, you and your wife, in Paradise and eat from wherever you will but do not approach this tree, lest you be among the wrongdoers.'", TextGerman = "- Und (du,) o Adam, bewohne du und deine Gattin den (Paradies)garten, und dann eßt, wo immer ihr wollt. Aber naht euch nicht diesem Baum, sonst gehört ihr zu den Ungerechten!" });
                        surah7.VerseList.Add(new Verse { Number = 20, TextBosnian = "I šejtan ih poče navraćati da bi im otkrio stidna mjesta njihova, koja su im skrivena bila, i reče: 'Gospodar vaš zabranjuje vam ovo drvo samo zato da ne biste meleci postali ili da ne biste u redovima besmrtnika bili',", TextEnglish = "But Satan whispered to them to make apparent to them that which was concealed from them of their private parts. He said, 'Your Lord did not forbid you this tree except that you become angels or become of the immortal.'", TextGerman = "Da flüsterte ihnen der Satan ein, um ihnen offen zu zeigen, was ihnen von ihrer Blöße verborgen war. Und er sagte: Unser Herr hat euch diesen Baum nur verboten, damit ihr nicht Engel werdet oder zu den Ewiglebenden gehört." });
                        surah7.VerseList.Add(new Verse { Number = 21, TextBosnian = "i zaklinjaše im se: 'Ja sam vama dvoma, zaista, savjetnik iskreni!'", TextEnglish = "And he swore [by Allāh] to them, 'Indeed, I am to you from among the sincere advisors.'", TextGerman = "Und er schwor ihnen: Ich gehöre wahrlich zu denjenigen, die euch guten Rat geben." });
                        surah7.VerseList.Add(new Verse { Number = 22, TextBosnian = "I na prevaru ih zavede. Kad oni ono drvo okusiše, stidna mjesta njihova ukazaše im se i oni po sebi džennetsko lišće stavljati počeše. 'Zar vam to drvo nisam zabranio?', zovnu ih Gospodar njihov, 'i rekao vam: 'šejtan vam je, zbilja, otvoreni neprijatelj.''", TextEnglish = "So he made them fall, through deception. And when they tasted of the tree, their private parts became apparent to them, and they began to fasten together over themselves from the leaves of Paradise. And their Lord called to them, 'Did I not forbid you from that tree and tell you that Satan is to you a clear enemy?'", TextGerman = "So verführte er sie trügerisch. Als sie dann von dem Baum gekostet hatten, zeigte sich ihnen ihre Blöße offenkundig, und sie begannen, Blätter des (Paradies)gartens auf sich zusammenzuheften. Und ihr Herr rief ihnen zu: Habe Ich euch nicht jenen Baum verboten und euch gesagt: Der Satan ist euch ein deutlicher Feind?" });
                        surah7.VerseList.Add(new Verse { Number = 23, TextBosnian = "Gospodaru naš', rekoše oni, 'sami smo sebi zulum učinili, i ako nam Ti ne oprostiš i ne smiluješ nam se, sigurno ćemo u redovima gubitnika biti.'", TextEnglish = "They said, 'Our Lord, we have wronged ourselves, and if You do not forgive us and have mercy upon us, we will surely be among the losers.'", TextGerman = "Sie sagten: Unser Herr, wir haben uns selbst Unrecht zugefügt. Wenn Du uns nicht vergibst und Dich unser erbarmst, werden wir ganz gewiß zu den Verlorenen gehören." });
                        surah7.VerseList.Add(new Verse { Number = 24, TextBosnian = "Silazite!', reče On, 'jedni drugima bit ćete neprijatelji! Na Zemlji ćete boraviti i do određenog vremena uživati.'", TextEnglish = "[Allāh] said, 'Descend, being to one another enemies. And for you on the earth is a place of settlement and enjoyment [i.e., provision] for a time.'", TextGerman = "Er (Allah) sagte: Geht fort! Einer sei des anderen Feind. Und auf der Erde sollt ihr Aufenthalt und Nießbrauch auf Zeit haben." });
                        surah7.VerseList.Add(new Verse { Number = 25, TextBosnian = "Na njoj ćete živjeti, na njoj umirati i iz nje oživljeni biti', reče On.", TextEnglish = "He said, 'Therein you will live, and therein you will die, and from it you will be brought forth.'", TextGerman = "Er (Allah) sagte: Auf ihr werdet ihr leben, und auf ihr werdet ihr sterben, und aus ihr werdet ihr hervorgebracht werden." });
                        surah7.VerseList.Add(new Verse { Number = 26, TextBosnian = "O sinovi Ademovi, dali smo vam odjeću koja će pokrivati stidna mjesta vaša, a i raskošna odijela, ali, odjeća bogobojaznosti, to je ono najbolje. To su neki Allahovi znakovi da bi se oni prisjetili i pouku prihvatili.", TextEnglish = "O children of Adam, We have bestowed upon you clothing to conceal your private parts and as adornment. But the clothing of righteousness - that is best. That is from the signs of Allāh that perhaps they will remember.", TextGerman = "O Kinder Adams, Wir haben auf euch Kleidung hinabgesandt, die eure Blöße verbirgt, und Gefieder. Aber die Kleidung der Gottesfurcht, die ist besser. Das ist (eines) von Allahs Zeichen, auf daß sie bedenken mögen." });
                        surah7.VerseList.Add(new Verse { Number = 27, TextBosnian = "O sinovi Ademovi, neka vas nikako ne zavede šejtan kao što je roditelje vaše iz Dženneta izveo, skinuvši s njih odjeću da bi im stidna mjesta njihova pokazao! On vas, doista, vidi; on i vojske njegove, odakle vi njih ne vidite. Uistinu, Mi smo učinili šejtane zaštitnicima onih koji ne vjeruju.", TextEnglish = "O children of Adam, let not Satan tempt you as he removed your parents from Paradise, stripping them of their clothing[365] to show them their private parts. Indeed, he sees you, he and his tribe, from where you do not see them. Indeed, We have made the devils allies to those who do not believe.", TextGerman = "O Kinder Adams, der Satan soll euch ja nicht der Versuchung aussetzen, wie er eure (Stamm)eltern aus dem (Paradies)garten vertrieben hat, indem er ihnen ihre Kleidung wegnahm, um ihnen ihre Blöße zu zeigen. Gewiß, er sieht euch, er und sein Stamm, von wo ihr sie nicht seht. Gewiß, Wir haben die Satane zu Schutzherren für diejenigen gemacht, die nicht glauben." });
                        surah7.VerseList.Add(new Verse { Number = 28, TextBosnian = "A kada urade neko gnusno djelo, govore: 'Zatekli smo pretke naše da to rade, a i Allah nam je to zapovijedio.' Reci: 'Allah ne zapovijeda da se rade gnusna djela! Zašto o Allahu govorite ono što ne znate?'", TextEnglish = "And when they commit an immorality, they say, 'We found our fathers doing it, and Allāh has ordered us to do it.' Say, 'Indeed, Allāh does not order immorality. Do you say about Allāh that which you do not know?'", TextGerman = "Und wenn sie eine Abscheulichkeit begehen, sagen sie: Wir haben unsere Väter darin (vorgefunden, und Allah hat es uns geboten. Sag: Allah gebietet nicht Schändliches. Wollt ihr (denn) über Allah sagen, was ihr nicht wißt?" });
                        surah7.VerseList.Add(new Verse { Number = 29, TextBosnian = "Gospodar moj naređuje pravednost. I obraćajte se samo Njemu kad god obavljate namaz, i molite se iskreno Mu ispovijedajući vjeru!, kao što vas je prvi put stvorio, tako će vas ponovo oživiti.'", TextEnglish = "Say, [O Muḥammad], 'My Lord has ordered justice and that you direct yourselves [to the Qiblah] at every place [or time] of prostration, and invoke Him, sincere to Him in religion.' Just as He originated you, you will return [to life] -", TextGerman = "Sag: Mein Herr hat Gerechtigkeit geboten, und ihr sollt euer Gesicht bei jeder Gebetsstätte aufrichten und Ihn anrufen, (wobei ihr) Ihm gegenüber aufrichtig in der Religion (sein sollt). So wie Er euch anfangs gebracht hat, werdet ihr zurückkehren." });
                        surah7.VerseList.Add(new Verse { Number = 30, TextBosnian = "On jedne na Pravi put upućuje, a drugi, s pravom, zabludu zaslužuju, jer oni, umjesto Allaha, doista šejtane za zaštitnike uzimaju i misle da su upućeni.", TextEnglish = "A group [of you] He guided, and a group deserved [to be in] error. Indeed, they [i.e., the latter] had taken the devils as allies instead of Allāh while they thought that they were guided.", TextGerman = "Einen Teil hat Er rechtgeleitet, an einem (anderen) Teil aber hat sich das Irregehen bewahrheitet, denn sie haben sich die Satane anstatt Allahs zu Schutzherren genommen und meinen, sie seien rechtgeleitet." });
                        surah7.VerseList.Add(new Verse { Number = 31, TextBosnian = "O sinovi Ademovi, kad god u mesdžid pođete odjeću pristojnu - ukras vaš - nosite! I jedite i pijte, samo ne pretjerujte; On, zaista, ne voli one koji pretjeruju.", TextEnglish = "O children of Adam, take your adornment [i.e., wear your clothing] at every masjid,[366] and eat and drink, but be not excessive. Indeed, He likes not those who commit excess.", TextGerman = "O Kinder Adams, legt euren Schmuck bei jeder Gebetsstätte an und eßt und trinkt, aber seid nicht maßlos! - Er (Allah) liebt nicht die Maßlosen." });
                        surah7.VerseList.Add(new Verse { Number = 32, TextBosnian = "Reci: 'Ko je zabranio Allahove ukrase koje je On za robove Svoje stvorio, i ukusna jela?' Reci: 'To je za vjernike na dunjaluku, a i na ahiretu jesu samo za njih.' Eto, tako Mi podrobno izlažemo ajete ljudima koji znaju.", TextEnglish = "Say, 'Who has forbidden the adornment of [i.e., from] Allāh which He has produced for His servants and the good [lawful] things of provision?' Say, 'They are for those who believed during the life of this world, exclusively [for them] on the Day of Resurrection.' Thus do We detail the verses for a people who know.", TextGerman = "Sag: Wer hat den Schmuck Allahs verboten, den Er für Seine Diener hervorgebracht hat, und (auch) die guten Dinge (aus) der Versorgung (Allahs)? Sag: Sie sind im diesseitigen Leben für diejenigen (bestimmt), die glauben, und am Tag der Auferstehung (ihnen) vorbehalten. So legen Wir die Zeichen ausführlich dar für Leute, die Bescheid wissen." });
                        surah7.VerseList.Add(new Verse { Number = 33, TextBosnian = "Reci: 'Gospodar moj zabranjuje gnusna djela, i javna i tajna, i grijehe, i bespravnu upotrebu sile, i da Allahu u obožavanju pridružujete one za koje On nikakav dokaz objavio nije, i da o Allahu govorite ono što ne znate.'", TextEnglish = "Say, 'My Lord has only forbidden immoralities - what is apparent of them and what is concealed - and sin,[367] and oppression without right, and that you associate with Allāh that for which He has not sent down authority, and that you say about Allāh that which you do not know.'", TextGerman = "Sag: Mein Herr hat nur die Abscheulichkeiten verboten, was von ihnen offen und was verborgen ist; und (auch) die Sünde und die Gewalttätigkeit ohne Recht, und, daß ihr Allah (etwas) beigesellt, wofür Er keine Ermächtigung herabgesandt hat, und daß ihr über Allah (etwas) sagt, was ihr nicht wißt." });
                        surah7.VerseList.Add(new Verse { Number = 34, TextBosnian = "Svaki narod ima svoj rok, i kada dođe njihov rok, neće ga moći ni za tren jedan ni odložiti ni ubrzati.", TextEnglish = "And for every nation is a [specified] term. So when their time has come, they will not remain behind an hour, nor will they precede [it].", TextGerman = "Jede Gemeinschaft hat eine (festgesetzte) Frist. Und wenn nun ihre Frist kommt, können sie (sie) weder um eine Stunde hinausschieben noch sie vorverlegen." });
                        surah7.VerseList.Add(new Verse { Number = 35, TextBosnian = "O sinovi Ademovi, kad vam između vas budu dolazili poslanici koji vam kazuju ajete Moje - onda za one koji se budu uščuvali i dobra djela činili nema straha niti će za bilo čim tugovati.", TextEnglish = "O children of Adam, if there come to you messengers from among you relating to you My verses [i.e., scriptures and laws], then whoever fears Allāh and reforms - there will be no fear concerning them, nor will they grieve.", TextGerman = "O Kinder Adams, wenn immer Gesandte von euch (selbst) zu euch kommen, die euch Meine Zeichen berichten - wer dann gottesfürchtig ist und Besserung bringt, über die soll keine Furcht kommen, noch sollen sie traurig sein." });
                        surah7.VerseList.Add(new Verse { Number = 36, TextBosnian = "Oni koji ajete i znakove Naše budu poricali, i od njih se budu oholo okretali, bit će stanovnici Vatre; u njoj će vječno ostati.", TextEnglish = "But the ones who deny Our verses and are arrogant toward them - those are the companions of the Fire; they will abide therein eternally.", TextGerman = "Diejenigen aber, die Unsere Zeichen für Lüge erklären und sich ihnen gegenüber hochmütig verhalten, das sind Insassen des (Höllen)feuers. Ewig werden sie darin bleiben." });
                        surah7.VerseList.Add(new Verse { Number = 37, TextBosnian = "Ima li, onda, većeg zulumćara od onoga koji o Allahu govori laži ili poriče Njegove ajete i znakove?! Takvi će dobiti sve ono što im je zapisano. Ali, kada im izaslanici Naši dođu da im duše uzmu, upitat će: 'A gdje su oni koje ste, umjesto Allaha, prizivali?' 'Izgubili su nam se', odgovorit će, i sami protiv sebe posvjedočiti da su nevjernici bili.", TextEnglish = "And who is more unjust than one who invents about Allāh a lie or denies His verses? Those will attain their portion of the decree[368] until, when Our messengers [i.e., angels] come to them to take them in death, they will say, 'Where are those you used to invoke besides Allāh?' They will say, 'They have departed from us,' and will bear witness against themselves that they were disbelievers.", TextGerman = "Wer ist denn ungerechter, als wer gegen Allah eine Lüge ersinnt oder Seine Zeichen für Lüge erklärt? Jene wird ihr Anteil vom Buch erreichen, bis daß, wenn Unsere Gesandten dann zu ihnen kommen, um sie abzuberufen, sie sagen: Wo ist das, was ihr außer Allah anzurufen pflegtet? Sie werden sagen: Sie sind uns entschwunden. Und sie zeugen gegen sich selbst, daß sie ungläubig waren." });
                        surah7.VerseList.Add(new Verse { Number = 38, TextBosnian = "Reći će On: 'Ulazite u Vatru sa zajednicama džina i ljudi koje su prije vas bile i nestale!' I kad god neka zajednica uđe, proklinjat će onu sebi sličnu - za kojom se u nevjerovanju povela. A kad se svi u njemu iskupe, tada će oni iz druge reći za one iz prve: 'Gospodaru naš, ovi su nas u zabludu odveli, zato im daj dvostruku patnju u Vatri!' 'Za sve će biti dvostruka!', reći će On, 'ali vi ne znate.'", TextEnglish = "[Allāh] will say, 'Enter among nations which had passed on before you of jinn and mankind into the Fire.' Every time a nation enters, it will curse its sister[369] until, when they have all overtaken one another therein, the last of them[370] will say about the first of them,[371] 'Our Lord, these had misled us, so give them a double punishment of the Fire.' He will say, 'For each is double, but you do not know.'", TextGerman = "Er (Allah) sagt: Tretet ein unter Gemeinschaften von den Ginn und den Menschen, die schon vor euch dahingegangen sind, ins (Höllen)feuer. Jedesmal, wenn eine Gemeinschaft eintritt, verflucht sie die vorhergehende. Wenn sie dann alle darin eingeholt werden, sagt die letzte von ihnen in Bezug auf die erste: Unser Herr, diese da haben uns in die Irre geführt. So gib ihnen eine mehrfache Strafe vom (Höllen)feuer! Er sagt: Jeder bekommt das Mehrfache, aber ihr wißt (es) nicht." });
                        surah7.VerseList.Add(new Verse { Number = 39, TextBosnian = "A oni iz prve reći će onima iz druge: 'Vi nemate nikakve prednosti nad nama! Zato iskusite patnju za ono što ste radili.'", TextEnglish = "And the first of them will say to the last of them, 'Then you had not any favor over us, so taste the punishment for what you used to earn.'", TextGerman = "Und die erste von ihnen sagt zu der letzten: Ihr habt doch keinen Vorzug gegenüber uns. So kostet die Strafe für das, was ihr verdient habt." });
                        surah7.VerseList.Add(new Verse { Number = 40, TextBosnian = "Doista, onima koji ajete i znakove Naše budu poricali i prema njima se budu oholo odnosili - kapije nebeske neće se otvoriti, niti će oni u Džennet ući sve dok se deva kroz iglene uši ne provuče!? Eto tako ćemo Mi prestupnike kazniti.", TextEnglish = "Indeed, those who deny Our verses and are arrogant toward them - the gates of Heaven will not be opened for them, nor will they enter Paradise until a camel enters into the eye of a needle [i.e., never]. And thus do We recompense the criminals.", TextGerman = "Gewiß, denjenigen, die Unsere Zeichen für Lüge erklären und sich ihnen gegenüber hochmütig verhalten, werden die Tore des Himmels nicht geöffnet, und sie werden nicht in den (Paradies)garten eingehen, bis ein Kamel durch ein Nadelöhr geht. So vergelten Wir den Übeltätern." });
                        surah7.VerseList.Add(new Verse { Number = 41, TextBosnian = "Od džehennemske vatre će im ležaji, a i pokrivači, iznad njih biti. Eto, tako ćemo Mi zulumćare kazniti.", TextEnglish = "They will have from Hell a bed and over them coverings [of fire]. And thus do We recompense the wrongdoers.", TextGerman = "Für sie wird die Hölle eine Lagerstatt sein, und über ihnen werden Decken (aus Höllenfeuer) sein. So vergelten Wir den Ungerechten." });
                        surah7.VerseList.Add(new Verse { Number = 42, TextBosnian = "Oni koji budu vjerovali i dobra djela činili - a Mi nikog ne zadužujemo mimo njegovih mogućnosti, bit će stanovnici Dženneta, u njemu će vječno ostati.", TextEnglish = "But those who believed and did righteous deeds - We charge no soul except [within] its capacity. Those are the companions of Paradise; they will abide therein eternally.", TextGerman = "Diejenigen aber, die glauben und rechtschaffene Werke tun - Wir erlegen keiner Seele mehr auf, als sie zu leisten vermag -, jene sind Insassen des (Paradies)gartens. Ewig werden sie darin bleiben." });
                        surah7.VerseList.Add(new Verse { Number = 43, TextBosnian = "Iz njihovih ćemo grudi zlobu odstraniti; ispred njih će rijeke teći, i oni će govoriti: 'Hvaljen neka je Allah, Koji nas je na Pravi put uputio; mi ne bismo na Pravom putu bili da nas Allah nije uputio, poslanici Gospodara našeg Istinu su donosili', i doviknut će im se: 'Taj ste Džennet u nasljedstvo dobili za ono što ste činili!'", TextEnglish = "And We will have removed whatever is within their breasts of resentment,[372] [while] flowing beneath them are rivers. And they will say, 'Praise to Allāh, who has guided us to this; and we would never have been guided if Allāh had not guided us. Certainly the messengers of our Lord had come with the truth.' And they will be called, 'This is Paradise, which you have been made to inherit for what you used to do.'", TextGerman = "Und Wir nehmen weg, was in ihren Brüsten an Groll ist. Unter ihnen strömen Flüsse. Und sie sagen: (Alles) Lob gehört Allah, Der uns hierher geleitet hat! Wir hätten unmöglich die Rechtleitung gefunden, wenn uns Allah nicht rechtgeleitet hätte. Die Gesandten unseres Herrn sind wirklich mit der Wahrheit gekommen. Und es wird ihnen zugerufen: Siehe, das ist der (Paradies)garten. Er ist euch zum Erbe gegeben worden für das, was ihr zu tun pflegtet." });
                        surah7.VerseList.Add(new Verse { Number = 44, TextBosnian = "I stanovnici Dženneta stanovnike Vatre dozivat će: 'Mi nađosmo da je istinito ono što nam je Gospodar naš obećao, da li ste i vi našli da je istinito ono što je vama Gospodar vaš prijeteći obećao?' 'Jesmo!', odgovorit će. A onda će jedan glasnik među njima viknuti: 'Neka Allahovo prokletstvo na zulumćarima ostane,", TextEnglish = "And the companions of Paradise will call out to the companions of the Fire, 'We have already found what our Lord promised us to be true. Have you found what your Lord promised to be true?' They will say, 'Yes.' Then an announcer will announce among them, 'The curse of Allāh shall be upon the wrongdoers", TextGerman = "Die Insassen des (Paradies)gartens rufen den Insassen des (Höllen)feuers zu: Wir haben gefunden, daß das, was uns unser Herr versprochen hat, wahr ist. Habt (auch) ihr gefunden, daß das, was euer Herr versprochen hat, wahr ist? Sie sagen: Ja! Und dann ruft ein Rufer unter ihnen aus: Allahs Fluch (kommt) über die Ungerechten," });
                        surah7.VerseList.Add(new Verse { Number = 45, TextBosnian = "koji su od Allahova puta odvraćali i nastojali da ga iskrive, i koji u ahiret nisu vjerovali!'", TextEnglish = "Who averted [people] from the way of Allāh and sought to make it [seem] deviant while they were, concerning the Hereafter, disbelievers.'", TextGerman = "die von Allahs Weg abhalten und danach trachten, ihn krumm zu machen, und die das Jenseits verleugnen." });
                        surah7.VerseList.Add(new Verse { Number = 46, TextBosnian = "Između njih bit će zid, a na vrhovima ljudi koji će svakog po obilježju njegovom poznati. I oni će stanovnicima Dženneta, prije nego uđu u njega, viknuti: 'Selamun alejkum!' a to će silno željeti.", TextEnglish = "And between them will be a partition [i.e., wall], and on [its] elevations are men[373] who recognize all[374] by their mark. And they call out to the companions of Paradise, 'Peace be upon you.' They have not [yet] entered it, but they long intensely.", TextGerman = "Und zwischen ihnen ist ein Vorhang. Und auf den Höhen sind Männer, die jeden an seinem Merkmal erkennen. Sie rufen den Insassen des (Paradies)gartens zu: Friede sei auf euch! Sie sind aber in ihn nicht eingetreten, und sie begehren (es) doch." });
                        surah7.VerseList.Add(new Verse { Number = 47, TextBosnian = "Kada im pogledi skrenu prema stanovnicima Vatre, uzviknut će: 'Gospodaru naš, ne daj nam da budemo s ljudima zulumćarima!'", TextEnglish = "And when their eyes are turned toward the companions of the Fire, they say, 'Our Lord, do not place us with the wrongdoing people.'", TextGerman = "Und wenn ihre Blicke den Insassen des (Höllen)feuers zugewendet werden, sagen sie: Unser Herr, lasse uns nicht mit dem ungerechten Volk sein!" });
                        surah7.VerseList.Add(new Verse { Number = 48, TextBosnian = "Oni koji će po vrhovima bedema biti zovnut će ljude koje će po obilježju njihovom poznati, i reći će: 'Nije vam koristilo vaše mnoštvo i to što ste se oholo držali.", TextEnglish = "And the companions of the Elevations will call to men [within Hell] whom they recognize by their mark, saying, 'Of no avail to you was your gathering[375] and [the fact] that you were arrogant.'", TextGerman = "Und die Leute der Höhen rufen Männern, die sie an ihren Merkmalen erkennen, zu. Sie sagen: Es hat euch eure Ansammlung, und daß ihr euch hochmütig zu verhalten pflegtet, nicht genützt." });
                        surah7.VerseList.Add(new Verse { Number = 49, TextBosnian = "Zar ovo nisu oni za koje ste se zaklinjali da ih Allahova milost neće stići? Uđite u Džennet, nikakva straha za vas neće biti i ni za čim vi nećete tugovati!'", TextEnglish = "[Allāh will say], 'Are these[376] the ones whom you [inhabitants of Hell] swore that Allāh would never offer them mercy? Enter Paradise, [O people of the Elevations]. No fear will there be concerning you, nor will you grieve.'", TextGerman = "Sind das diejenigen, von denen ihr geschworen habt, Allah werde ihnen keine Barmherzigkeit erweisen? - Geht in den (Paradies)garten ein! Über euch soll keine Furcht kommen, noch sollt ihr traurig sein." });
                        surah7.VerseList.Add(new Verse { Number = 50, TextBosnian = "I stanovnici Vatre dozivat će stanovnike Dženneta: 'Prolijte na nas vode ili nešto od onoga čime vas je Allah obdario!', a oni će reći: 'Allah je, doista, to dvoje nevjernicima zabranio',", TextEnglish = "And the companions of the Fire will call to the companions of Paradise, 'Pour upon us some water or from whatever Allāh has provided you.' They will say, 'Indeed, Allāh has forbidden them both to the disbelievers", TextGerman = "Und die Insassen des (Höllen)feuers rufen den Insassen des (Paradies)gartens zu: Schüttet auf uns etwas Wasser aus oder etwas von dem, womit Allah euch versorgt hat! Sie sagen: Gewiß, Allah hat beides für die Ungläubigen verboten," });
                        surah7.VerseList.Add(new Verse { Number = 51, TextBosnian = "onima kojima su vjeru svoju za zabavu i igru držali i koje je život na Zemlji bio obmanuo. Zato ćemo danas Mi njih zaboraviti, kao što su zaboravljali da će na ovaj Dan pred Nama stajati i zato što su ajete i znakove Naše poricali.", TextEnglish = "Who took their religion as distraction and amusement and whom the worldly life deluded.' So today We will forget them just as they forgot the meeting of this Day of theirs and for having rejected Our verses.", TextGerman = "die ihre Religion zum Gegenstand der Zerstreuung und des Spiels genommen haben und die das diesseitige Leben getäuscht hat. Heute werden Wir sie vergessen, so wie sie die Begegnung mit diesem ihrem Tag vergaßen und wie sie Unsere Zeichen zu verleugnen pflegten." });
                        surah7.VerseList.Add(new Verse { Number = 52, TextBosnian = "A Mi smo im donijeli Knjigu koju smo po znanju Našem objasnili, da bude uputa i milost ljudima koji vjeruju.", TextEnglish = "And We had certainly brought them a Book which We detailed by knowledge - as guidance and mercy to a people who believe.", TextGerman = "Wir haben ihnen ja ein Buch gebracht, das Wir mit Wissen ausführlich dargelegt haben, als Rechtleitung und Barmherzigkeit für Leute, die glauben." });
                        surah7.VerseList.Add(new Verse { Number = 53, TextBosnian = "Čekaju li oni da se to obistini? Onoga dana kada se obistini, reći će oni koji su je prije bili zaboravili: 'Istinu su poslanici Gospodara našeg donosili! Da nam je zagovornik kakav, pa da se za nas zauzme ili da nam je da budemo vraćeni pa da postupamo drukčije nego što smo prije postupali!' Ali, oni su sami sebe upropastili, i zagubit će im se oni koje su izmišljali.'", TextEnglish = "Do they await except its result?[377] The Day its result comes, those who had ignored it before will say, 'The messengers of our Lord had come with the truth, so are there [now] any intercessors to intercede for us or could we be sent back to do other than what we used to do?' They will have lost themselves, and lost from them is what they used to invent.", TextGerman = "Erwarten sie (etwas anderes) als seine Deutung? An dem Tag, da seine Deutung eintrifft, werden diejenigen, die es zuvor vergessen haben, sagen: Die Gesandten unseres Herrn sind wirklich mit der Wahrheit gekommen! Haben wir denn Fürsprecher, die für uns Fürsprache einlegen? Oder können wir zurückgebracht werden, daß wir anders handeln, als wir zu handeln pflegten? Sie haben wirklich ihre Seelen verloren, und ihnen ist entschwunden, was sie zu ersinnen pflegten." });
                        surah7.VerseList.Add(new Verse { Number = 54, TextBosnian = "Zaista je Gospodar vaš Allah Koji je nebesa i Zemlju u šest dana stvorio, a zatim se nad Aršom uzvisio. On tamom noći dan prekriva i ona ga ustopu prati, a Sunce i Mjesec i zvijezde pokoravaju se Njegovoj naredbi. Samo On raspolaže stvaranjem i odlučivanjem! Uzvišen neka je Allah, Gospodar svjetova!", TextEnglish = "Indeed, your Lord is Allāh, who created the heavens and earth in six days and then established Himself above the Throne.[378] He covers the night with the day, [another night] chasing it rapidly; and [He created] the sun, the moon, and the stars, subjected by His command. Unquestionably, His is the creation and the command; blessed is Allāh, Lord of the worlds.", TextGerman = "Gewiß, euer Herr ist Allah, Der die Himmel und die Erde in sechs Tagen erschuf und Sich hierauf über den Thron erhob. Er läßt die Nacht den Tag überdecken, wobei sie ihn eilig einzuholen sucht. Und (Er schuf auch) die Sonne, den Mond und die Sterne, durch Seinen Befehl dienstbar gemacht. Sicherlich, Sein ist die Schöpfung und der Befehl. Segensreich ist Allah, der Herr der Weltenbewohner." });
                        surah7.VerseList.Add(new Verse { Number = 55, TextBosnian = "Molite se ponizno i u sebi Gospodaru svome, On, doista, ne voli one koji granicu prelaze.", TextEnglish = "Call upon your Lord in humility and privately; indeed, He does not like transgressors.[379]", TextGerman = "Ruft euren Herrn in Unterwürfigkeit flehend und im Verborgenen an. Gewiß, Er liebt nicht die Übertreter." });
                        surah7.VerseList.Add(new Verse { Number = 56, TextBosnian = "I ne pravite nered na Zemlji, kada je na njoj red uspostavljen, a Njemu se molite sa strahom i nadom; milost je Allahova, doista, blizu onih koji dobra djela čine.", TextEnglish = "And cause not corruption upon the earth after its reformation. And invoke Him in fear and aspiration. Indeed, the mercy of Allāh is near to the doers of good.", TextGerman = "Und stiftet auf der Erde nicht Unheil, nachdem sie in Ordnung gebracht worden ist! Und ruft Ihn in Furcht und Begehren an. Gewiß, die Barmherzigkeit Allahs ist den Gutes Tuenden nahe." });
                        surah7.VerseList.Add(new Verse { Number = 57, TextBosnian = "On je Taj Koji šalje vjetrove kao radosnu vijest milosti Svoje; a kad oni pokrenu teške oblake, Mi ih prema mrtvom predjelu potjeramo, pa na njeg kišu spustimo i učinimo da uz njenu pomoć rastu plodovi svakovrsni; tako ćemo i mrtve oživiti da bi ste se prisjetili i pouku prihvatili!", TextEnglish = "And it is He who sends the winds as good tidings before His mercy [i.e., rainfall] until, when they have carried heavy rainclouds, We drive them to a dead land and We send down rain therein and bring forth thereby [some] of all the fruits. Thus will We bring forth the dead; perhaps you may be reminded.", TextGerman = "Und Er ist es, Der die Winde als Frohboten Seiner Barmherzigkeit (voraus)sendet, bis daß, wenn sie dann schwere Wolken herbeitragen, Wir sie zu einem toten Land treiben, dadurch Wasser hinabsenden und dann dadurch alle Früchte hervorbringen. Ebenso bringen Wir (dereinst) die Toten hervor, auf daß ihr bedenken möget!" });
                        surah7.VerseList.Add(new Verse { Number = 58, TextBosnian = "U plodnom predjelu raste bilje uz dopuštenje Gospodara njegova, a u neplodnom tek s mukom. Eto, tako Mi, na razne načine, izlažemo znakove ljudima koji zahvaljuju.", TextEnglish = "And the good land - its vegetation emerges by permission of its Lord; but that which is bad - nothing emerges except sparsely, with difficulty. Thus do We diversify the signs for a people who are grateful.", TextGerman = "Und aus dem guten Land kommt sein Pflanzenwuchs mit Allahs Erlaubnis hervor, aus dem schlechten (Land) jedoch kommt (nur) Kümmerliches hervor. So legen Wir die Zeichen verschiedenartig dar für Leute, die dankbar sind." });
                        surah7.VerseList.Add(new Verse { Number = 59, TextBosnian = "Mi smo Nuha poslali narodu njegovu, pa im je on govorio: 'O narode moj, Allahu ibadet činite, vi drugog boga osim Njega nemate! Ja se, doista, bojim za vas patnje na Velikom danu!'", TextEnglish = "We had certainly sent Noah to his people, and he said, 'O my people, worship Allāh; you have no deity other than Him. Indeed, I fear for you the punishment of a tremendous Day.'", TextGerman = "Wir sandten doch bereits Nuh zu seinem Volk, und da sagte er: O mein Volk, dient Allah! Keinen Gott habt ihr außer Ihm. Gewiß, ich fürchte für euch die Strafe eines gewaltigen Tages." });
                        surah7.VerseList.Add(new Verse { Number = 60, TextBosnian = "A glavešine naroda njegova odgovarale su: 'Mi smatramo da si ti, doista, u potpunoj zabludi.'", TextEnglish = "Said the eminent among his people, 'Indeed, we see you in clear error.'", TextGerman = "Die führende Schar aus seinem Volk sagte: Wir sehen dich wahrlich in deutlichem Irrtum." });
                        surah7.VerseList.Add(new Verse { Number = 61, TextBosnian = "A on je govorio: 'O narode moj, nisam ja ni u kakvoj zabludi, nego sam poslanik Gospodara svjetova;", TextEnglish = "[Noah] said, 'O my people, there is not error in me, but I am a messenger from the Lord of the worlds.", TextGerman = "Er sagte: O mein Volk, bei mir befindet sich kein Irrtum, sondern ich bin ein Gesandter vom Herrn der Weltenbewohner." });
                        surah7.VerseList.Add(new Verse { Number = 62, TextBosnian = "dostavljam vam poslanice Gospodara svoga i savjetujem vas; ja od Allaha znam ono što ne znate vi.", TextEnglish = "I convey to you the messages of my Lord and advise you; and I know from Allāh what you do not know.", TextGerman = "Ich übermittele euch die Botschaften meines Herrn und rate euch gut. Und ich weiß von Allah her, was ihr nicht wißt." });
                        surah7.VerseList.Add(new Verse { Number = 63, TextBosnian = "Zar vam je čudno što vam pouka od Gospodara vašeg dolazi po čovjeku, jednom od vas, da vas upozorava, da biste se Allaha bojali i da biste pomilovani bili?'", TextEnglish = "Then do you wonder that there has come to you a reminder from your Lord through a man from among you, that he may warn you and that you may fear Allāh so you might receive mercy?'", TextGerman = "Wundert ihr euch etwa (darüber), daß eine Erinnerung von eurem Herrn zu euch gekommen ist durch einen Mann von euch, damit er euch warne und damit ihr gottesfürchtig werdet, auf daß ihr Erbarmen finden möget?" });
                        surah7.VerseList.Add(new Verse { Number = 64, TextBosnian = "Ali, oni su ga lažnim smatrali, pa smo spasili njega i one koji su bili uz njega u lađi, a one koji u ajete i znakove Naše nisu vjerovali - potopili. Uistinu, oni su pravi slijepci bili.", TextEnglish = "But they denied him, so We saved him and those who were with him in the ship. And We drowned those who denied Our signs. Indeed, they were a blind people.[380]", TextGerman = "Sie aber bezichtigten ihn der Lüge. Da retteten Wir ihn und diejenigen, die mit ihm waren, im Schiff und ließen diejenigen ertrinken, die Unsere Zeichen für Lüge erklärten. Sie waren gewiß blinde Leute." });
                        surah7.VerseList.Add(new Verse { Number = 65, TextBosnian = "A Adu njihova brata Huda poslasmo. On je govorio: 'O narode moj, Allahu ibadet činite, vi drugog boga osim Njega nemate, zar se sačuvati nećete'!'", TextEnglish = "And to the ʿAad [We sent] their brother Hūd. He said, 'O my people, worship Allāh; you have no deity other than Him. Then will you not fear Him?'", TextGerman = "Und (Wir sandten) zu Ad ihren Bruder Hud. Er sagte: O mein Volk, dient Allah! Keinen Gott habt ihr außer Ihm. Wollt ihr denn nicht gottesfürchtig sein?" });
                        surah7.VerseList.Add(new Verse { Number = 66, TextBosnian = "Glavešine naroda njegova, koje nisu vjerovale, odgovarale su: 'Mi smatramo da si ti, doista, u ludilu i mi mislimo da si, zaista, lažac.'", TextEnglish = "Said the eminent ones who disbelieved among his people, 'Indeed, we see you in foolishness, and indeed, we think you are of the liars.'", TextGerman = "Die führende Schar aus seinem Volk, die ungläubig war, sagte: Wir sehen dich wahrlich in Torheit (befangen), und wir meinen wahrlich, daß du zu den Lügnern gehörst." });
                        surah7.VerseList.Add(new Verse { Number = 67, TextBosnian = "A on je govorio: 'O narode moj, nije pri meni nikakvo ludilo, nego sam Gospodara svjetova poslanik;", TextEnglish = "[Hūd] said, 'O my people, there is not foolishness in me, but I am a messenger from the Lord of the worlds.", TextGerman = "Er sagte: O mein Volk, bei mir befindet sich keine Torheit, sondern ich bin ein Gesandter vom Herrn der Weltenbewohner." });
                        surah7.VerseList.Add(new Verse { Number = 68, TextBosnian = "dostavljam vam poslanice Gospodara svoga, i ja sam vam iskreni savjetnik.'", TextEnglish = "I convey to you the messages of my Lord, and I am to you a trustworthy adviser.", TextGerman = "Ich übermittele euch die Botschaften meines Herrn, und ich bin für euch ein vertrauenswürdiger Ratgeber." });
                        surah7.VerseList.Add(new Verse { Number = 69, TextBosnian = "Zar vam je čudno što vam pouka od Gospodara vašeg dolazi po čovjeku, jednom između vas, da vas opominje? Sjetite se da vas je On nasljednicima Nuhova naroda učinio i stvorio vas krupnog rasta. I neka su vam zato uvijek na umu Allahove blagodati, da biste uspjeli.", TextEnglish = "Then do you wonder that there has come to you a reminder from your Lord through a man from among you, that he may warn you? And remember when He made you successors after the people of Noah and increased you in stature extensively. So remember the favors of Allāh that you might succeed.'", TextGerman = "Wundert ihr euch etwa (darüber), daß eine Erinnerung von eurem Herrn zu euch gekommen ist durch einen Mann von euch, damit er euch warne? Gedenkt, als Er euch zu Nachfolgern nach dem Volk Nuhs machte und euch ein Übermaß an körperlichen Vorzügen gab! Gedenkt also der Wohltaten Allahs, auf daß es euch wohl ergehen möge!" });
                        surah7.VerseList.Add(new Verse { Number = 70, TextBosnian = "Oni su govorili: 'Zar si nam došao zato da samo Allahu ibadet činimo, a da napustimo one koje su preci naši obožavali? Učini da nas snađe to što nam prijeteći obećavaš, ako je istina to što govoriš!'", TextEnglish = "They said, 'Have you come to us that we should worship Allāh alone and leave what our fathers have worshipped? Then bring us what you promise us,[381] if you should be of the truthful.'", TextGerman = "Sie sagten: Bist du zu uns gekommen, damit wir Allah allein dienen und das verlassen, dem unsere Väter immer gedient haben? Bring uns doch her, was du uns androhst, wenn du zu den Wahrhaftigen gehören solltest!" });
                        surah7.VerseList.Add(new Verse { Number = 71, TextBosnian = "Već vas je snašla kazna i gnjev Gospodara vašeg!', govorio je on. 'Zar sa mnom da se prepirete o imenima nekakvim kojima ste ih vi i preci vaši nazvali, a o kojima Allah nikakav dokaz nije objavio? Zato čekajte, i ja ću s vama sigurno čekati!'", TextEnglish = "[Hūd] said, 'Already have defilement and anger fallen upon you from your Lord. Do you dispute with me concerning [mere] names you have named them,[382] you and your fathers, for which Allāh has not sent down any authority? Then wait; indeed, I am with you among those who wait.'", TextGerman = "Er sagte: Greuel und Zorn von eurem Herrn überfallen euch nunmehr. (Wollt) ihr denn mit mir über Namen streiten, die ihr genannt habt, ihr und eure Väter, für die Allah (aber) keine Ermächtigung offenbart hat? So wartet ab! Ich gehöre mit euch zu den Abwartenden." });
                        surah7.VerseList.Add(new Verse { Number = 72, TextBosnian = "I Mi smo iz milosti Naše njega i one koji su bili uz njega spasili, a do posljednjeg istrijebili one koji dokaze Naše nisu priznavali i koji nisu vjerovali.", TextEnglish = "So We saved him and those with him by mercy from Us. And We eliminated those who denied Our signs, and they were not [at all] believers.", TextGerman = "Da retteten Wir ihn und diejenigen, die mit ihm waren, durch Barmherzigkeit von Uns. Aber Wir schnitten die Rückkehr derer, die Unsere Zeichen für Lüge erklärten, ab, und sie waren nicht gläubig." });
                        surah7.VerseList.Add(new Verse { Number = 73, TextBosnian = "A Semudu smo njegova brata Saliha poslali. 'O narode moj', govorio je on, 'Allahu ibadet činite, vi drugog boga osim Njega nemate! Evo vam znaka Gospodara vašeg: ova Allahova kamila za vas je znak. Pustite je neka pase po Allahovoj zemlji i ne zlostavljajte je da vas bolna kazna ne bi snašla!'", TextEnglish = "And to the Thamūd [We sent] their brother Ṣāliḥ. He said, 'O my people, worship Allāh; you have no deity other than Him. There has come to you clear evidence from your Lord. This is the she-camel of Allāh [sent] to you as a sign. So leave her to eat within Allāh's land and do not touch her with harm, lest there seize you a painful punishment.", TextGerman = "Und (Wir sandten) zu Tamud ihren Bruder Salih. Er sagte: O mein Volk, dient Allah! Keinen Gott habt ihr außer Ihm. Nun ist ein klarer Beweis von eurem Herrn zu euch gekommen: Dies ist die Kamelstute Allahs, euch zum Zeichen. So laßt sie auf Allahs Erde fressen und fügt ihr nichts Böses zu, sonst überkommt euch schmerzhafte Strafe." });
                        surah7.VerseList.Add(new Verse { Number = 74, TextBosnian = "I sjetite se da vas je On nasljednicima Ada učinio, i da vas je On na Zemlji nastanio: u ravnicama njezinim palače gradite, a u brdima kuće klešete. I neka su vam uvijek na umu Allahove blagodati, i ne činite zlo po Zemlji nered praveći!'", TextEnglish = "And remember when He made you successors after the ʿAad and settled you in the land, [and] you take for yourselves palaces from its plains and carve from the mountains, homes. Then remember the favors of Allāh and do not commit abuse on the earth, spreading corruption.'", TextGerman = "Und gedenkt, als Er euch zu Nachfolgern nach den ’Ad machte und euch auf der Erde (Stätten) zuwies, so daß ihr euch in ihrem Flachland Schlösser nahmt und die Berge zu Häusern aushautet. Gedenkt also der Wohltaten Allahs und richtet auf der Erde nicht unheilstiftend Verderben an!" });
                        surah7.VerseList.Add(new Verse { Number = 75, TextBosnian = "A glavešine naroda njegova, oni koji su se oholili, upitaše potlačene, one među njima koji su vjerovali: 'Vjerujete li vi da je Saliha poslao Gospodar njegov?' 'Mi, uistinu, vjerujemo u sve ono što je po njemu poslano', odgovoriše oni.", TextEnglish = "Said the eminent ones who were arrogant among his people to those who were oppressed - to those who believed among them, 'Do you [actually] know that Ṣāliḥ is sent from his Lord?' They said, 'Indeed we, in that with which he was sent, are believers.'", TextGerman = "Die führende Schar, die sich hochmütig verhielt, aus seinem Volk, sagte zu denjenigen, die unterdrückt waren, (und zwar) zu denen von ihnen, die gläubig waren: Wißt ihr (sicher), daß Salih von seinem Herrn gesandt ist? Sie sagten: Wir glauben gewiß an das, womit er gesandt worden ist." });
                        surah7.VerseList.Add(new Verse { Number = 76, TextBosnian = "A mi, doista, ne vjerujemo u to u što vi vjerujete', rekoše oni koji su bili oholi.", TextEnglish = "Said those who were arrogant, 'Indeed we, in that which you have believed, are disbelievers.'", TextGerman = "Diejenigen, die sich hochmütig verhielten, sagten: Wir verleugnen ja das, woran ihr glaubt." });
                        surah7.VerseList.Add(new Verse { Number = 77, TextBosnian = "I zaklaše oni kamilu, i zapovijed Gospodara svoga ne poslušaše i rekoše: 'O Salih, učini da nas snađe to što prijeteći obećavaš, ako si iz reda poslanih!?'", TextEnglish = "So they hamstrung the she-camel and were insolent toward the command of their Lord and said, 'O Ṣāliḥ, bring us what you promise us, if you should be of the messengers.'", TextGerman = "Dann schnitten sie der Kamelstute die Sehnen durch und lehnten sich gegen den Befehl ihres Herrn auf. Und sie sagten: O Salih, bringe uns doch her, was du uns angedroht hast, wenn du zu den Gesandten gehörst!" });
                        surah7.VerseList.Add(new Verse { Number = 78, TextBosnian = "I zadesi ih strašan potres i oni u zemlji svojoj osvanuše mrtvi, nepomični.", TextEnglish = "So the earthquake seized them, and they became within their home [corpses] fallen prone.", TextGerman = "Da ergriff sie das Zittern, und am Morgen lagen sie in ihrer Wohnstätte auf den Brüsten da." });
                        surah7.VerseList.Add(new Verse { Number = 79, TextBosnian = "A on ih napusti i reče: 'O narode moj, prenio sam vam poslanicu Gospodara svoga i savjetovao sam vas, ali vi ne volite one koji savjetuju.'", TextEnglish = "And he [i.e., Ṣāliḥ] turned away from them and said, 'O my people, I had certainly conveyed to you the message of my Lord and advised you, but you do not like advisors.'", TextGerman = "So kehrte er sich von ihnen ab und sagte: O mein Volk, ich habe euch doch die Botschaft meines Herrn ausgerichtet und euch gut geraten. Aber ihr liebt nicht die guten Ratgeber." });
                        surah7.VerseList.Add(new Verse { Number = 80, TextBosnian = "I sjeti se Luta - kad reče narodu svome: 'Zašto činite razvrat koji niko među svim svjetovima prije vas nije činio?", TextEnglish = "And [We had sent] Lot when he said to his people, 'Do you commit such immorality as no one has preceded you with from among the worlds [i.e., peoples]?", TextGerman = "Und (Wir sandten) Lut, als er zu seinem Volk sagte: Wollt ihr denn das Abscheuliche begehen, wie es vor euch niemand von den Weltenbewohnern begangen hat?" });
                        surah7.VerseList.Add(new Verse { Number = 81, TextBosnian = "Vi zaista strasno prilazite muškarcima, umjesto ženama. Štaviše, vi ste narod koji sve granice prelazi!'", TextEnglish = "Indeed, you approach men with desire, instead of women. Rather, you are a transgressing people.'", TextGerman = "Ihr laßt euch doch wahrlich in Begierde mit den Männern ein anstatt mit den Frauen. Aber nein! Ihr seid maßlose Leute." });
                        surah7.VerseList.Add(new Verse { Number = 82, TextBosnian = "A odgovor naroda njegova bio je samo to što su rekli: 'Istjerajte ih iz naselja vašeg, oni su, zaista, ljudi koji se od toga ustežu!'", TextEnglish = "But the answer of his people was only that they said, 'Evict them from your city! Indeed, they are men who keep themselves pure.'", TextGerman = "Die Antwort seines Volkes war nur, daß sie sagten: Vertreibt sie aus eurer Stadt! Das sind Menschen, die sich rein halten." });
                        surah7.VerseList.Add(new Verse { Number = 83, TextBosnian = "I Mi smo njega i porodicu njegovu spasili, osim žene njegove; ona je ostala sa onima koji su kaznu iskusili.", TextEnglish = "So We saved him and his family, except for his wife; she was of those who remained [with the evildoers].", TextGerman = "Da retteten Wir ihn und seine Angehörigen, außer seiner Frau; Sie gehörte zu denjenigen, die zurückblieben." });
                        surah7.VerseList.Add(new Verse { Number = 84, TextBosnian = "I na njih smo kišu grumenja spustili, pa pogledaj kako su prestupnici skončali.", TextEnglish = "And We rained upon them a rain [of stones]. Then see how was the end of the criminals.", TextGerman = "Und Wir ließen einen Regen auf sie niedergehen. So schau, wie das Ende der Übeltäter war!" });
                        surah7.VerseList.Add(new Verse { Number = 85, TextBosnian = "A i u Medjen- njihova brata Šuajba poslasmo. 'O narode moj', govorio je on, 'Allahu ibadet činite, vi drugog boga osim Njega nemate! Jasan dokaz od Gospodara vašeg došao vam je, zato punite mjericu i vagu, i ljude ni u čemu ne zakidajte, i red na Zemlji ne remetite kad je već na njoj red uspostavljen. To je bolje za vas, ako ste vjernici.", TextEnglish = "And to [the people of] Madyan [We sent] their brother Shuʿayb. He said, 'O my people, worship Allāh; you have no deity other than Him. There has come to you clear evidence from your Lord. So fulfill the measure and weight and do not deprive people of their due and cause not corruption upon the earth after its reformation. That is better for you, if you should be believers.", TextGerman = "Und (Wir sandten) zu Madyan ihren Bruder Su’aib. Er sagte: O mein Volk, dient Allah! Keinen Gott habt ihr außer Ihm. Nun ist ein klarer Beweis von eurem Herrn zu euch gekommen; so gebt volles Maß und Gewicht und schmälert den Menschen nicht ihre Sachen und stiftet auf der Erde nicht Unheil, nachdem sie in Ordnung gebracht worden ist! Das ist besser für euch, wenn ihr gläubig seid." });
                        surah7.VerseList.Add(new Verse { Number = 86, TextBosnian = "I ne postavljajte zasjede na putu, prijeteći i od Allahova puta odvraćajući one koji u Njega vjeruju, želeći da on kriv bude. I sjetite se da vas je bilo malo i da vas je On umnožio, a pogledajte kako su skončali oni koji su nered pravili.", TextEnglish = "And do not sit on every path, threatening and averting from the way of Allāh those who believe in Him, seeking to make it [seem] deviant. And remember when you were few and He increased you. And see how was the end of the corrupters.", TextGerman = "Und lauert nicht auf jedem Weg, indem ihr droht und von Allahs Weg denjenigen abhaltet, der an Ihn glaubt, und danach trachtet, ihn krumm zu machen. Und gedenkt (der Zeit), als ihr (nur) wenige wart und Er euch dann zu vielen machte. Und schaut, wie das Ende der Unheilstifter war!" });
                        surah7.VerseList.Add(new Verse { Number = 87, TextBosnian = "I ako jedni od vas vjeruju u ono što je po meni poslano, a drugi ne vjeruju, pa pričekajte dok nam Allah ne presudi, a On je najbolji sudija!'", TextEnglish = "And if there should be a group among you who has believed in that with which I have been sent and a group that has not believed, then be patient until Allāh judges between us. And He is the best of judges.'", TextGerman = "Und wenn ein Teil von euch an das glauben sollte, womit ich gesandt worden bin, ein anderer Teil aber nicht glauben sollte, so geduldet euch, bis Allah zwischen uns richtet. Er ist der Beste derer, die richten." });
                        surah7.VerseList.Add(new Verse { Number = 88, TextBosnian = "I Glavešine naroda njegova, oni koji su bili oholi, rekoše: 'Ili ćemo mi, o Šuajbe, i tebe i one koji s tobom vjeruju iz naselja našeg istjerati, ili ćete se vratiti u našu vjeru!' 'Zar i protiv naše volje?', reče on.", TextEnglish = "Said the eminent ones who were arrogant among his people, 'We will surely evict you, O Shuʿayb, and those who have believed with you from our city, or you must return to our religion.' He said, 'Even if we were unwilling?", TextGerman = "Die führende Schar, die sich hochmütig verhielt, aus seinem Volk, sagte: Wir werden dich, o Su’aib, und diejenigen, die mit dir glauben, ganz gewiß aus unserer Stadt vertreiben, oder aber ihr kehrt zu unserem Glaubensbekenntnis zurück! Er sagte: Was denn, auch wenn es uns zuwider ist?" });
                        surah7.VerseList.Add(new Verse { Number = 89, TextBosnian = "Ako bismo se u vjeru vašu vratili nakon što nas je Allah iz nje izbavio, na Allaha bismo laž iznijeli. Mi ne treba da se u nju vraćamo, osim ako to htjedne Allah, Gospodar naš. Gospodar naš znanjem Svojim sve obuhvata; u Allaha se uzdamo! Gospodaru naš, Ti presudi nama i narodu našem, po pravdi, Ti si Presuditelj najbolji!'", TextEnglish = "We would have invented against Allāh a lie if we returned to your religion after Allāh had saved us from it. And it is not for us to return to it except that Allāh, our Lord, should will. Our Lord has encompassed all things in knowledge. Upon Allāh we have relied. Our Lord, decide between us and our people in truth, and You are the best of those who give decision.'", TextGerman = "Wir würden ja gegen Allah eine Lüge ersinnen, wenn wir zu eurem Glaubensbekenntnis zurückkehrten, nachdem Allah uns vor ihr errettet hat. Es steht uns nicht zu, zu ihr zurückzukehren, außer daß Allah, unser Herr, (es) wollte. Unser Herr umfaßt alles mit (Seinem) Wissen. Auf Allah verlassen wir uns. Unser Herr, entscheide zwischen uns und unserem Volk der Wahrheit entsprechend! Du bist ja der beste Entscheider." });
                        surah7.VerseList.Add(new Verse { Number = 90, TextBosnian = "A glavešine naroda njegova, oni koji nisu vjerovali, rekoše: 'Ako pođete za Šuajbom, bit ćete sigurno izgubljeni.'", TextEnglish = "Said the eminent ones who disbelieved among his people, 'If you should follow Shuʿayb, indeed, you would then be losers.'", TextGerman = "Aber die führende Schar aus seinem Volk, die ungläubig war, sagte: Wenn ihr Su’aib folgt, dann werdet ihr fürwahr Verlierer sein." });
                        surah7.VerseList.Add(new Verse { Number = 91, TextBosnian = "I zadesi ih potom strašan potres i oni osvanuše u zemlji svojoj mrtvi, nepomični.", TextEnglish = "So the earthquake seized them, and they became within their home [corpses] fallen prone.", TextGerman = "Da ergriff sie das Zittern, und am Morgen lagen sie in ihrer Wohnstätte auf den Brüsten da." });
                        surah7.VerseList.Add(new Verse { Number = 92, TextBosnian = "Oni koji su smatrali Šuajba lašcem - kao da nikad nisu u njoj ni bili; oni koji su smatrali Šuajba lašcem, oni su baš gubitnici bili.", TextEnglish = "Those who denied Shuʿayb - it was as though they had never resided there. Those who denied Shuʿayb - it was they who were the losers.", TextGerman = "Diejenigen, die Su’aib der Lüge bezichtigen, waren, als hätten sie (überhaupt) nicht darin gewohnt. Diejenigen, die Su’aib der Lüge bezichtigten, waren (nun) selbst die Verlierer." });
                        surah7.VerseList.Add(new Verse { Number = 93, TextBosnian = "A on ih napusti i reče: 'O narode moj, prenio sam vam poslanice Gospodara svoga i savjetovao vas, pa zašto da tugujem za narodom nevjerničkim?!'", TextEnglish = "And he [i.e., Shuʿayb] turned away from them and said, 'O my people, I had certainly conveyed to you the messages of my Lord and advised you, so how could I grieve for a disbelieving people?'", TextGerman = "So kehrte er sich von ihnen ab und sagte: O mein Volk, ich habe euch doch die Botschaften meines Herrn ausgerichtet und euch gut geraten. Wie sollte ich betrübt sein über ungläubige Leute?" });
                        surah7.VerseList.Add(new Verse { Number = 94, TextBosnian = "I Mi nijednog vjerovjesnika u neko naselje nismo poslali, a da stanovnike njegove bolešću i neimaštinom nismo kaznili da bi se pokajnički molili.", TextEnglish = "And We sent to no city a prophet [who was denied] except that We seized its people with poverty and hardship that they might humble themselves [to Allāh].", TextGerman = "Und Wir haben in keine Stadt Propheten gesandt, ohne daß Wir über ihre Bewohner Not und Leid hätten kommen lassen, auf daß sie in Unterwürfigkeit flehen mögen." });
                        surah7.VerseList.Add(new Verse { Number = 95, TextBosnian = "Poslije bismo zlo zamijenili dobrim dok se ne bi umnožili i rekli: 'Zadesili su i naše pretke i neimaština i blagostanje!', i tada bismo ih iznenada kaznili, a oni ni predosjetili ne bi.", TextEnglish = "Then We exchanged in place of the bad [condition], good, until they increased [and prospered] and said, 'Our fathers [also] were touched with hardship and ease.'[383] So We seized them suddenly while they did not perceive.[384]", TextGerman = "Hierauf tauschten Wir anstelle des Bösen Gutes ein, bis sie (sich und ihren Besitz) vermehrten und sagten: Schon unseren Vätern ist Leid und (auch) Freude widerfahren. Da ergriffen Wir sie plötzlich, ohne daß sie merkten." });
                        surah7.VerseList.Add(new Verse { Number = 96, TextBosnian = "A da su stanovnici naselja'' vjerovali i bogobojazni bili, Mi bismo im blagoslove i s neba i iz zemlje slali, ali, oni su poricali, pa smo ih kažnjavali zbog onog što su činili.", TextEnglish = "And if only the people of the cities had believed and feared Allāh, We would have opened [i.e., bestowed] upon them blessings from the heaven and the earth; but they denied [the messengers], so We seized them for what they were earning.[385]", TextGerman = "Hätten aber die Bewohner der Städte geglaubt und wären sie gottesfürchtig gewesen, hätten Wir ihnen bestimmt Segnungen von dem Himmel und der Erde aufgetan. Aber sie erklärten (die Botschaft) für Lüge, und so ergriffen Wir sie für das, was sie erworben hatten." });
                        surah7.VerseList.Add(new Verse { Number = 97, TextBosnian = "A zar su stanovnici naselja sigurni da ih Naša kazna neće snaći noću dok budu spavali?", TextEnglish = "Then, did the people of the cities feel secure from Our punishment coming to them at night while they were asleep?", TextGerman = "Glauben denn die Bewohner der Städte, sicher davor zu sein, daß Unsere Gewalt bei Nacht über sie kommt, während sie schlafen?" });
                        surah7.VerseList.Add(new Verse { Number = 98, TextBosnian = "Ili, zar su stanovnici naselja sigurni da ih Naša kazna neće snaći danju dok se budu zabavljali?", TextEnglish = "Or did the people of the cities feel secure from Our punishment coming to them in the morning while they were at play?[386]", TextGerman = "Oder glauben die Bewohner der Städte, sicher davor zu sein, daß Unsere Gewalt am hellen Morgen über sie kommt, während sie spielen?" });
                        surah7.VerseList.Add(new Verse { Number = 99, TextBosnian = "Zar oni misle da su sigurni od Allahovog lukavstva? Da su sigurni od Allahovog lukavstva misli samo narod gubitnički.", TextEnglish = "Then, did they feel secure from the plan of Allāh? But no one feels secure from the plan of Allāh except the losing people.", TextGerman = "Glauben sie denn, sicher vor Allahs Ränken zu sein? Aber vor Allahs Ränken sicher zu sein, glaubt nur das Volk derjenigen, die Verlierer sind." });
                        surah7.VerseList.Add(new Verse { Number = 100, TextBosnian = "Zar nije jasno onima koji nasljeđuju Zemlju nakon prijašnjih stanovnika njezinih da ćemo i njih, ako budemo htjeli, zbog grijeha njihovih kazniti ili im srca zapečatiti, pa neće moći čuti?!", TextEnglish = "Has it not become clear to those who inherited the earth after its [previous] people that if We willed, We could afflict them for their sins? But We seal over their hearts so they do not hear.[387]", TextGerman = "Ist denjenigen, die die Erde nach ihren (vorherigen) Bewohnern erben, nicht deutlich geworden, daß, wenn Wir wollten, Wir sie für ihre Sünden treffen würden? Und Wir versiegeln ihre Herzen, so daß sie nicht hören." });
                        surah7.VerseList.Add(new Verse { Number = 101, TextBosnian = "O tim naseljima Mi ti neke događaje kazujemo. Poslanici njihovi jasne su im dokaze donosili, ali oni nisu povjerovali zato što ni prije nisu vjerovali. Eto, tako Allah srca nevjernika zapečati.", TextEnglish = "Those cities - We relate to you, [O Muḥammad], some of their news. And certainly did their messengers come to them with clear proofs, but they were not to believe in that which they had denied before.[388] Thus does Allāh seal over the hearts of the disbelievers.", TextGerman = "Das sind die Städte. Über sie erzählen Wir dir (einiges) von ihren Berichten. Ihre Gesandten kamen ja zu ihnen mit den klaren Beweisen, aber sie konnten nicht an das glauben, was sie zuvor für Lüge erklärt hatten. So versiegelt Allah die Herzen der Ungläubigen." });
                        surah7.VerseList.Add(new Verse { Number = 102, TextBosnian = "A Mi nismo ustanovili da se većina njih zavjeta drži, a ustanovili smo da su većina njih, doista, buntovnici.", TextEnglish = "And We did not find for most of them any covenant;[389] but indeed, We found most of them defiantly disobedient.", TextGerman = "Und Wir fanden bei den meisten von ihnen keine Vertragstreue, sondern Wir fanden (, daß) die meisten von ihnen fürwahr Frevler (waren)." });
                        surah7.VerseList.Add(new Verse { Number = 103, TextBosnian = "Zatim smo, poslije njih, poslali Musaa sa dokazima Našim faraonu i glavešinama njegovim, ali oni u njih nisu povjerovali, pa pogledaj kako su skončali oni koji su nered činili.", TextEnglish = "Then We sent after them Moses with Our signs to Pharaoh and his establishment, but they were unjust toward them.[390] So see how was the end of the corrupters.", TextGerman = "Hierauf schickten Wir nach ihnen Musa mit Unseren Zeichen zu Fir’aun und seiner führenden Schar, doch sie handelten unrecht an ihnen. Schau nur, wie das Ende der Unheilstifter war!" });
                        surah7.VerseList.Add(new Verse { Number = 104, TextBosnian = "I Musa reče: 'O faraone, ja sam, uistinu, poslanik Gospodara svjetova!'", TextEnglish = "And Moses said, 'O Pharaoh, I am a messenger from the Lord of the worlds", TextGerman = "Musa sagte: O Fir’aun, gewiß, ich bin ein Gesandter vom Herrn der Weltenbewohner," });
                        surah7.VerseList.Add(new Verse { Number = 105, TextBosnian = "Dužnost mi je da o Allahu samo istinu kažem. Donio sam vam jasan dokaz od Gospodara vašeg, zato pusti da idu sa mnom sinovi Israilovi!'", TextEnglish = "[Who is] obligated not to say about Allāh except the truth. I have come to you with clear evidence from your Lord, so send with me the Children of Israel.'[391]", TextGerman = "bestrebt’, über Allah nur die Wahrheit zu sagen. Ich bin doch mit einem klaren Beweis von eurem Herrn zu euch gekommen. So lasse die Kinder Isra’ils mit mir gehen!" });
                        surah7.VerseList.Add(new Verse { Number = 106, TextBosnian = "Ako si donio kakav dokaz', reče, 'pokaži ga, ako si od onih koji istinu govore.'", TextEnglish = "[Pharaoh] said, 'If you have come with a sign, then bring it forth, if you should be of the truthful.'", TextGerman = "Er sagte: Wenn du mit einem Zeichen gekommen bist, dann bringe es her, wenn du zu den Wahrhaftigen gehörst." });
                        surah7.VerseList.Add(new Verse { Number = 107, TextBosnian = "I on baci svoj štap - kad on prava zmijurina;", TextEnglish = "So he [i.e., Moses] threw his staff, and suddenly it was a serpent, manifest.[392]", TextGerman = "Er warf seinen Stock hin, und sogleich war er eine deutliche Schlange." });
                        surah7.VerseList.Add(new Verse { Number = 108, TextBosnian = "i izvadi ruku svoju - ona za prisutne postade bijela.", TextEnglish = "And he drew out his hand; thereupon it was white [with radiance] for the observers.", TextGerman = "Und er zog seine Hand heraus, da war sie weiß für die Betrachter." });
                        surah7.VerseList.Add(new Verse { Number = 109, TextBosnian = "Glavešine naroda faraonova povikaše: 'Ovaj je, doista, čarobnjak koji zna,", TextEnglish = "Said the eminent among the people of Pharaoh, 'Indeed, this is a learned magician", TextGerman = "Die führende Schar aus dem Volk Fir’auns sagte: Dieser ist fürwahr ein kenntnisreicher Zauberer," });
                        surah7.VerseList.Add(new Verse { Number = 110, TextBosnian = "on hoće da vas izvede iz zemlje vaše, pa šta predlažete?'", TextEnglish = "Who wants to expel you from your land [through magic], so what do you instruct?'", TextGerman = "der euch aus eurem Land vertreiben will. Was befehlt ihr nun?" });
                        surah7.VerseList.Add(new Verse { Number = 111, TextBosnian = "Zadrži njega i brata njegova', rekoše, 'a pošalji u gradove sakupljače,", TextEnglish = "They said,[393] 'Postpone [the matter of] him and his brother and send among the cities gatherers", TextGerman = "Sie sagten: Stelle ihn und seinen Bruder zurück, und sende in die Städte (Boten), die (die Leute) versammeln," });
                        surah7.VerseList.Add(new Verse { Number = 112, TextBosnian = "preda te će sve vješte čarobnjake dovesti.'", TextEnglish = "Who will bring you every learned magician.'", TextGerman = "damit sie dir jeden kenntnisreichen Zauberer herbeibringen." });
                        surah7.VerseList.Add(new Verse { Number = 113, TextBosnian = "I faraonu čarobnjaci dođoše. 'Da li ćemo, doista, nagradu dobiti ako budemo pobjednici?', upitaše.", TextEnglish = "And the magicians came to Pharaoh. They said, 'Indeed for us is a reward if we are the predominant.'", TextGerman = "Und die Zauberer kamen zu Fir’aun. Sie sagten: Wir bekommen doch sicher Lohn, wenn wir es sind, die siegen?" });
                        surah7.VerseList.Add(new Verse { Number = 114, TextBosnian = "Da', reče, 'i bit ćete, zaista, među meni bliskima.'", TextEnglish = "He said, 'Yes, and, [moreover], you will be among those made near [to me].'", TextGerman = "Er sagte: Ja. Und ihr werdet fürwahr zu den Nahegestellten gehören." });
                        surah7.VerseList.Add(new Verse { Number = 115, TextBosnian = "O Musa', rekoše onda, 'hoćeš li ti, ili ćemo mi baciti?'", TextEnglish = "They said, 'O Moses, either you throw [your staff], or we will be the ones to throw [first].'", TextGerman = "Sie sagten: O Musa, entweder wirfst du oder wir sind es, die (zuerst) werfen." });
                        surah7.VerseList.Add(new Verse { Number = 116, TextBosnian = "Bacite vi', reče on. I kad oni baciše, oči ljudima začaraše i jako ih prestrašiše, i vradžbinu veliku prirediše.", TextEnglish = "He said, 'Throw,' and when they threw, they bewitched the eyes of the people and struck terror into them, and they presented a great [feat of] magic.[394]", TextGerman = "Er sagte: Werft (ihr zuerst)! Als sie nun warfen, bezauberten sie die Augen der Menschen und flößten ihnen Furcht ein, und sie brachten einen gewaltigen Zauber vor." });
                        surah7.VerseList.Add(new Verse { Number = 117, TextBosnian = "I Mi naredismo Musau: 'Baci štap svoj!', i on odjednom proguta sve ono čime su oni obmanu bili izveli.", TextEnglish = "And We inspired to Moses, 'Throw your staff,' and at once it devoured what they were falsifying.", TextGerman = "Und Wir gaben Musa ein: Wirf deinen Stock hin! Da verschlang er sogleich, was sie vortäuschten." });
                        surah7.VerseList.Add(new Verse { Number = 118, TextBosnian = "I tako istina na vidjelo izbi i pokaza se da je bilo lažno ono što su oni priredili,", TextEnglish = "So the truth was established, and abolished was what they were doing.", TextGerman = "So bestätigte sich die Wahrheit, und zunichte wurde das, was sie taten." });
                        surah7.VerseList.Add(new Verse { Number = 119, TextBosnian = "i tu oni bijahu pobijeđeni i postadoše poniženi.", TextEnglish = "And they [i.e., Pharaoh and his people] were overcome right there and became debased.", TextGerman = "Sie wurden dort besiegt und kehrten gering geachtet zurück." });
                        surah7.VerseList.Add(new Verse { Number = 120, TextBosnian = "I čarobnjaci se licem na tlo baciše.", TextEnglish = "And the magicians fell down in prostration [to Allāh].", TextGerman = "Und die Zauberer warfen sich ehrerbietig nieder." });
                        surah7.VerseList.Add(new Verse { Number = 121, TextBosnian = "Mi vjerujemo u Gospodara svjetova', povikaše,", TextEnglish = "They said, 'We have believed in the Lord of the worlds,", TextGerman = "Sie sagten: Wir glauben an den Herrn der Weltenbewohner," });
                        surah7.VerseList.Add(new Verse { Number = 122, TextBosnian = "Gospodara Musaova i Harunova!'", TextEnglish = "The Lord of Moses and Aaron.'", TextGerman = "den Herrn von Musa und Harun." });
                        surah7.VerseList.Add(new Verse { Number = 123, TextBosnian = "Zar da mu povjerujete prije nego što vam ja dozvolim!', viknu faraon. 'Ovo je, uistinu, smicalica koju ste u gradu smislili da biste iz njega stanovnike njegove izveli. Znat ćete vi!'", TextEnglish = "Said Pharaoh, 'You believed in him[395] before I gave you permission. Indeed, this is a conspiracy which you conspired in the city to expel therefrom its people. But you are going to know.", TextGerman = "Fir’aun sagte: Ihr glaubt an Ihn, bevor ich es euch erlaube? Das sind wahrlich Ränke, die ihr in der Stadt geschmiedet habt, um ihre Bewohner daraus zu vertreiben. Aber ihr werdet (es noch) erfahren." });
                        surah7.VerseList.Add(new Verse { Number = 124, TextBosnian = "Izodsijecat ću vam, sigurno, ruke vaše i noge vaše unakrst, a onda ću vas sve zbilja razapeti!'", TextEnglish = "I will surely cut off your hands and your feet on opposite sides; then I will surely crucify you all.'", TextGerman = "Ich werde ganz gewiß eure Hände und eure Füße wechselseitig abhacken und euch hierauf ganz gewiß allesamt kreuzigen (lassen)." });
                        surah7.VerseList.Add(new Verse { Number = 125, TextBosnian = "A oni rekoše: 'Mi ćemo se, doista, Gospodaru našem vratiti!'", TextEnglish = "They said, 'Indeed, to our Lord we will return.", TextGerman = "Sie sagten: Wir werden gewiß zu unserem Herrn zurückkehren!" });
                        surah7.VerseList.Add(new Verse { Number = 126, TextBosnian = "Ti nam zamjeraš samo to što smo u dokaze Gospodara našeg povjerovali kad su nam došli. Gospodaru naš, obaspi nas strpljivošću i učini da kao predani vjernici umremo!'", TextEnglish = "And you do not resent us except because we believed in the signs of our Lord when they came to us. Our Lord, pour upon us patience[396] and let us die as Muslims [in submission to You].'", TextGerman = "Du grollst uns ja nur (darum), daß wir an die Zeichen unseres Herrn glaubten, als sie zu uns kamen. Unser Herr, überschütte uns mit Standhaftigkeit und berufe uns ab als (Dir) Ergebene!" });
                        surah7.VerseList.Add(new Verse { Number = 127, TextBosnian = "A glavešine naroda faraonovog rekoše: 'Zar ćeš ostaviti Musaa i narod njegov da nered po Zemlji pravi i da tebe i božanstva tvoja napusti?' On reče: 'Ubijat ćemo mušku djecu njihovu, a žensku ćemo im ostavljati u životu; mi, uistinu, vladamo nad njima.'", TextEnglish = "And the eminent among the people of Pharaoh said, 'Will you leave Moses and his people to cause corruption in the land and abandon you and your gods?' [Pharaoh] said, 'We will kill their sons and keep their women alive; and indeed, we are subjugators over them.'", TextGerman = "Die führende Schar aus dem Volk Fir’auns sagte: Willst du zulassen, daß Musa und sein Volk auf der Erde Unheil stiften und daß er dich und deine Götter verläßt? Er sagte: Wir werden ihre Söhne allesamt töten und (nur) ihre Frauen am Leben lassen. Wir sind ja Bezwinger über sie." });
                        surah7.VerseList.Add(new Verse { Number = 128, TextBosnian = "Musa reče narodu svome: 'Molite Allaha da vam pomogne i budite strpljivi, Zemlja je, doista, Allahova. On je daje u naslijeđe kome On hoće od robova Svojih; a lijep ishod pripada bogobojaznima.'", TextEnglish = "Said Moses to his people, 'Seek help through Allāh and be patient. Indeed, the earth belongs to Allāh. He causes to inherit it whom He wills of His servants. And the [best] outcome is for the righteous.'", TextGerman = "Musa sagte zu seinem Volk: Sucht Hilfe bei Allah, und seid standhaft! Gewiß, die Erde gehört Allah; Er gibt sie zum Erbe, wem von Seinen Dienern Er will. Und das (gute) Ende gehört den Gottesfürchtigen." });
                        surah7.VerseList.Add(new Verse { Number = 129, TextBosnian = "Zlostavljani smo', rekoše oni, 'prije nego što si nam došao, a i nakon što si došao!' A Musa reče: 'Možda će Gospodar vaš neprijatelja vašeg uništiti, a vas nasljednicima na Zemlji učiniti, da bi vidio kako ćete postupiti.'", TextEnglish = "They said, 'We have been harmed before you came to us and after you have come to us.' He said, 'Perhaps your Lord will destroy your enemy and grant you succession in the land and see how you will do.'", TextGerman = "Sie sagten: Uns ist Leid zugefügt worden, bevor du zu uns kamst und nachdem du zu uns gekommen bist. Er sagte: Vielleicht wird euer Herr euren Feind vernichten und euch zu Nachfolgern auf der Erde machen und dann schauen, wie ihr handelt." });
                        surah7.VerseList.Add(new Verse { Number = 130, TextBosnian = "I Mi smo kaznili faraonov narod gladnim godinama i oskudicom u plodovima, da bi se prisjetili i pouku uzeli.", TextEnglish = "And We certainly seized[397] the people of Pharaoh with years of famine and a deficiency in fruits that perhaps they would be reminded.", TextGerman = "Und Wir ergriffen ja schon die Leute Fir’auns mit Dürrejahren und Mangel an Früchten, auf daß sie bedenken mögen." });
                        surah7.VerseList.Add(new Verse { Number = 131, TextBosnian = "I kad bi im bilo dobro, oni bi govorili: 'Ovo smo zaslužili', a kad bi ih snašla kakva nevolja, zloslutnju su nalazili u Musau i onima koji su s njim vjerovali. Ali ne! Njihova je zloslutnja od Allaha bila, samo što većina njih nije znala!'", TextEnglish = "But when good [i.e., provision] came to them, they said, 'This is ours [by right].' And if a bad [condition] struck them, they saw an evil omen in Moses and those with him. Unquestionably, their fortune is with Allāh, but most of them do not know.", TextGerman = "Aber wenn dann etwas Gutes zu ihnen kam, sagten sie: Dies gehört uns. und wenn sie etwas Böses traf, sahen sie ein (böses) Vorzeichen in Musa und denjenigen, die mit ihm waren. Aber ihr Vorzeichen ist sicherlich bei Allah, jedoch wissen die meisten von ihnen nicht." });
                        surah7.VerseList.Add(new Verse { Number = 132, TextBosnian = "I govorili su: 'Kakav god da nam dokaz doneseš da nas njime opčaraš, mi ti nećemo vjerovati!'", TextEnglish = "And they said, 'No matter what sign you bring us with which to bewitch us, we will not be believers in you.'", TextGerman = "Und sie sagten: Welches Zeichen du uns auch vorbringen magst, um uns damit zu bezaubern, so werden wir dir nicht glauben." });
                        surah7.VerseList.Add(new Verse { Number = 133, TextBosnian = "Pa Mi na njih poslasmo i poplavu, i skakavce, i krpelje, i žabe, i krv- sve jasne znakove, ali su se oni oholili, a bijahu narod prestupnički.", TextEnglish = "So We sent upon them the flood and locusts and lice and frogs and blood as distinct signs, but they were arrogant and were a criminal people.", TextGerman = "Da sandten Wir über sie die Überschwemmung, die Heuschrecken, die Zecken, die Frösche und das Blut als einzeln vorgebrachte Zeichen. Aber sie verhielten sich hochmütig und waren ein Volk von Übeltätern." });
                        surah7.VerseList.Add(new Verse { Number = 134, TextBosnian = "I kad bi ih zadesila patnja, govorili bi: 'O Musa, moli se za nas Gospodaru svome - onako kako ti je On naredio. Ako nas oslobodiš patnje, mi ćemo zaista vjerovati i s tobom ćemo sinove Israilove zbilja poslati.'", TextEnglish = "And when the punishment descended upon them, they said, 'O Moses, invoke for us your Lord by what He has promised you. If you [can] remove the punishment from us, we will surely believe you, and we will send with you the Children of Israel.'", TextGerman = "Als nun die unheilvolle Strafe sie überfiel, sagten sie: O Musa, rufe für uns deinen Herrn an auf Grund Seines Bundes mit dir. Wenn du die unheilvolle Strafe hinwegnimmst, werden wir dir ganz gewiß glauben und die Kinder Isra’ils ganz gewiß mit dir wegschicken." });
                        surah7.VerseList.Add(new Verse { Number = 135, TextBosnian = "I pošto bismo ih patnje oslobodili - do vremena do kog im je bilo određeno da je podnose - oni bi, odjednom, obećanje prekršili.", TextEnglish = "But when We removed the punishment from them until a term which they were to reach,[398] then at once they broke their word.", TextGerman = "Als Wir dann die unheilvolle Strafe von ihnen hinwegnahmen auf eine Frist, die sie erreichen sollten, brachen sie sogleich (ihr Wort)." });
                        surah7.VerseList.Add(new Verse { Number = 136, TextBosnian = "I onda ih Mi kaznismo i u moru ih potopismo, jer su znakove Naše poricali i prema njima ravnodušni bili,", TextEnglish = "So We took retribution from them, and We drowned them in the sea because they denied Our signs and were heedless of them.", TextGerman = "Da übten Wir an ihnen Vergeltung und ließen sie im großen Gewässer ertrinken, dafür, daß sie Unsere Zeichen für Lüge erklärten und ihnen gegenüber unachtsam waren." });
                        surah7.VerseList.Add(new Verse { Number = 137, TextBosnian = "a potlačenom narodu dodadosmo u naslijeđe istočne i zapadne krajeve zemlje koju smo blagoslovili, i lijepe riječi Gospodara tvoga sinovima Israilovim bile su ispunjene - zato što su trpjeli, a sa zemljom sravnismo ono što su faraon i narod njegov bili sagradili i ono što su uvis podizali.", TextEnglish = "And We caused the people who had been oppressed to inherit the eastern regions of the land and the western ones, which We had blessed. And the good word [i.e., decree] of your Lord was fulfilled for the Children of Israel because of what they had patiently endured. And We destroyed [all] that Pharaoh and his people were producing and what they had been building.", TextGerman = "Und Wir gaben dem Volk, das unterdrückt worden war, zum Erbe die östlichen und die westlichen (Gegenden) des Landes, das Wir gesegnet haben. Und das schönste Wort deines Herrn erfüllte sich an den Kindern Isra’ils dafür, daß sie standhaft waren. Und Wir zerstörten, was Fir’aun und sein Volk zu machen und was sie zu errichten pflegten." });
                        surah7.VerseList.Add(new Verse { Number = 138, TextBosnian = "I Mi sinove Israilove preko mora prevedosmo, pa oni naiđoše na narod koji je privržen obožavanju kumira svojih bio. 'O Musa', rekoše, 'napravi i ti nama boga kao što i oni imaju bogove!' 'Vi ste, uistinu, narod koji ne zna!', reče on.", TextEnglish = "And We took the Children of Israel across the sea; then they came upon a people intent in devotion to [some] idols of theirs. They [the Children of Israel] said, 'O Moses, make for us a god just as they have gods.' He said, 'Indeed, you are a people behaving ignorantly.", TextGerman = "Und Wir ließen die Kinder lsra’ils das Meer durchschreiten. Sie kamen zu Leuten, die sich zur Andacht an ihren Götzen zurückzogen. Sie sagten: O Musa, mache uns einen Gott, so wie sie Götter haben! Er sagte: Ihr seid ja Leute, die töricht sind." });
                        surah7.VerseList.Add(new Verse { Number = 139, TextBosnian = "Zaista će biti poništeno ono u šta su ogrezli bili i beskorisno će im biti ono što su radili.", TextEnglish = "Indeed, those [worshippers] - destroyed is that in which they are [engaged], and worthless is whatever they were doing.'", TextGerman = "Gewiß, was diese da betreiben, wird zerstört, und zunichte wird, was sie zu tun pflegten." });
                        surah7.VerseList.Add(new Verse { Number = 140, TextBosnian = "On reče: 'Zar da vam, pored Allaha, tražim drugog boga, a On vas nad svim drugim odlikovao?'", TextEnglish = "He said, 'Is it other than Allāh I should desire for you as a god[399] while He has preferred you over the worlds?'", TextGerman = "Er sagte: Sollte ich für euch einen anderen Gott begehren als Allah, wo Er euch vor den (anderen) Weltenbewohnern bevorzugt hat?" });
                        surah7.VerseList.Add(new Verse { Number = 141, TextBosnian = "I pošto smo vas Mi izbavili od faraonovih ljudi, koji su vas najgorom mukom mučili: mušku su djecu vašu ubijali, a žensku vam u životu ostavljali- to je bilo teško iskušenje od Gospodara vašeg.", TextEnglish = "And [recall, O Children of Israel], when We saved you from the people of Pharaoh, [who were] afflicting you with the worst torment - killing your sons and keeping your women alive. And in that was a great trial from your Lord.", TextGerman = "Und (gedenkt,) als Wir euch vor den Leuten Fir’auns retteten, als sie euch eine böse Qual auferlegten, indem sie eure Söhne allesamt töteten und (nur) eure Frauen am Leben ließen. Darin war für euch eine gewaltige Prüfung von eurem Herrn." });
                        surah7.VerseList.Add(new Verse { Number = 142, TextBosnian = "Mi odredismo da čas susreta s Musaom bude kad se napuni trideset noći, i dopunismo ih još sa deset, pa se vrijeme koje je odredio Gospodar njegov ispuni za četrdeset noći. A Musa je bio rekao bratu svome Harunu: 'Zamijeni me u narodu mome, i red pravi i ne slijedi puteve smutljivaca!'", TextEnglish = "And We made an appointment with Moses for thirty nights and perfected them by [the addition of] ten; so the term of his Lord was completed as forty nights. And Moses said to his brother Aaron, 'Take my place among my people, do right [by them],[400] and do not follow the way of the corrupters.'", TextGerman = "Und Wir gaben Musa eine Verabredung auf dreißig Nächte und machten sie mit (weiteren) zehn voll. So vervollständigte sich die festgesetzte Zahl seines Herrn auf vierzig Nächte. Und Musa sagte zu seinem Bruder Harun: Nimm meine Stelle bei meinem Volk ein, sorge für Ordnung und folge nicht dem Weg der Unheilstifter!" });
                        surah7.VerseList.Add(new Verse { Number = 143, TextBosnian = "I kad Nam Musa dođe u određeno vrijeme, i kada mu Gospodar njegov progovori, on reče: 'Gospodaru moj, ukaži mi se da Te vidim!' 'Nećeš Me vidjeti', reče, 'ali pogledaj u ono brdo, pa ako ono ostane na svome mjestu, vidjet ćeš Me!' I kad se Gospodar njegov onom brdu otkri, On ga sa zemljom sravni, a Musa izgubi svijest i strovali se. Čim se osvijesti, reče: 'Uzvišen si Ti! Kajem Ti se, ja sam vjernik prvi!'", TextEnglish = "And when Moses arrived at Our appointed time and his Lord spoke to him, he said, 'My Lord, show me [Yourself] that I may look at You.' [Allāh] said, 'You will not see Me,[401] but look at the mountain; if it should remain in place, then you will see Me.' But when his Lord appeared to the mountain, He rendered it level,[402] and Moses fell unconscious. And when he awoke, he said, 'Exalted are You! I have repented to You, and I am the first [among my people] of the believers.'[403]", TextGerman = "Als nun Musa zu Unserer festgesetzten Zeit kam und sein Herr zu ihm sprach, sagte er: Mein Herr, zeige (Dich) mir, daß ich Dich anschaue! Er sagte: Du wirst Mich nicht sehen. Aber schau den Berg an! Wenn er fest an seiner Stelle bleibt, dann wirst du Mich sehen. Als nun sein Herr dem Berg offenbar erschien, ließ Er ihn in sich zusammensinken, und Musa fiel bewußtlos nieder’. Als er dann (wieder) zu sich kam, sagte er: Preis sei Dir! Ich wende mich in Reue Dir zu, und ich bin der erste der Gläubigen." });
                        surah7.VerseList.Add(new Verse { Number = 144, TextBosnian = "O Musa', reče On, 'Ja sam tebe, doista, nad drugima poslanicama Svojim i govorom Svojim odabranim učinio. Ono što ti dajem uzmi i zahvalan budi!'", TextEnglish = "[Allāh] said, 'O Moses, I have chosen you over the people with My messages and My words [to you]. So take what I have given you and be among the grateful.'", TextGerman = "Er sagte: O Musa, Ich habe dich durch Meine Botschaften und Mein Gespräch (mit dir) vor den Menschen auserwählt! So nimm, was Ich dir gegeben habe, und gehöre zu den Dankbaren!" });
                        surah7.VerseList.Add(new Verse { Number = 145, TextBosnian = "I Mi mu na pločama napisasmo pouku za sve, i objašnjenje za svašta. Prihvati ih snažno, a narodu svom zapovjedi da se pridržava onoga što je u njima ljepše. A pokazat ću vam i stanište buntovnika.", TextEnglish = "And We wrote for him on the tablets [something] of all things - instruction and explanation for all things, [saying], 'Take them with determination and order your people to take the best of it. I will show you the home of the defiantly disobedient.'[404]", TextGerman = "Und Wir schrieben ihm auf den Tafeln von allem eine Ermahnung und eine ausführliche Darlegung von allem. So halte sie fest und befiel deinem Volk, sich an das Schönste in ihnen zu halten! Ich werde euch die Wohnstätte der Frevler zeigen." });
                        surah7.VerseList.Add(new Verse { Number = 146, TextBosnian = "Odvratit ću od znamenja Svojih one koji se, bez ikakva osnova, na Zemlji ohole. I kakav god dokaz oni vide, neće u njega vjerovati; ako vide Pravi put- neće ga kao put prihvatiti, a ako vide stranputicu - kao put će je prihvatiti. To je zato što znakove Naše poriču i što su prema njima ravnodušni.", TextEnglish = "I will turn away from My signs those who are arrogant upon the earth without right; and if they should see every sign, they will not believe in it. And if they see the way of consciousness,[405] they will not adopt it as a way; but if they see the way of error, they will adopt it as a way. That is because they have denied Our signs and they were heedless of them.", TextGerman = "Ich werde von meinen Zeichen diejenigen abwenden, die auf der Erde ohne Recht hochmütig sind. Wenn sie auch jedes Zeichen sehen, glauben sie nicht daran. Und wenn sie den Weg der Besonnenheit sehen, nehmen sie ihn sich nicht zum Weg. Wenn sie aber den Weg der Verirrung sehen, nehmen sie ihn sich zum Weg. Dies, weil sie Unsere Zeichen für Lüge erklären und ihnen gegenüber unachtsam sind." });
                        surah7.VerseList.Add(new Verse { Number = 147, TextBosnian = "Djela će biti poništena onima koji poriču znakove Naše i susret na onom svijetu. Zar će biti drukčije kažnjeni nego kako su radili?", TextEnglish = "Those who denied Our signs and the meeting of the Hereafter - their deeds have become worthless. Are they recompensed except for what they used to do?", TextGerman = "Diejenigen, die Unsere Zeichen und die Begegnung mit dem Jenseits für Lüge erklären, deren Werke werden hinfällig. Wird ihnen (denn) etwas anderes vergolten als das, was sie zu tun pflegten?" });
                        surah7.VerseList.Add(new Verse { Number = 148, TextBosnian = "I narod Musaov, poslije odlaska njegova, izradi od nakita svoga kip teleta koje je rikalo i koje za božanstvo prihvati. Zar nisu vidjeli da im ono ne govori i da ih putem pravim ne vodi? Oni ga prihvatiše i zulumćari postaše.", TextEnglish = "And the people of Moses made, after [his departure], from their ornaments a calf - an image having a lowing sound. Did they not see that it could neither speak to them nor guide them to a way? They took it [for worship], and they were wrongdoers.", TextGerman = "Und das Volk Musas nahm sich, nachdem er (weggegangen) war, aus ihren Schmucksachen (verfertigt) ein Kalb als Leib, der blökte. Sahen sie denn nicht, daß es nicht zu ihnen spricht und sie nicht den rechten Weg leitet? Sie nahmen es sich (als Götzen) und waren ungerecht." });
                        surah7.VerseList.Add(new Verse { Number = 149, TextBosnian = "Pošto se poslije gorko pokajaše i uvidješe da su zalutali, oni rekoše: 'Ako nam se Gospodar naš ne smiluje i ako nam ne oprosti, sigurno ćemo među gubitnicima biti.'", TextEnglish = "And when regret overcame them[406] and they saw that they had gone astray, they said, 'If our Lord does not have mercy upon us and forgive us, we will surely be among the losers.'", TextGerman = "Und als sich bei ihnen die Reue einstellte und sie sahen, daß sie bereits irregegangen waren, sagten sie: Wenn sich unser Herr nicht unser erbarmt und uns vergibt, werden wir ganz gewiß zu den Verlierern gehören!" });
                        surah7.VerseList.Add(new Verse { Number = 150, TextBosnian = "A kad se Musa, srdit i bijesan, narodu svome vrati, povika: 'Kako ste tako ružno poslije odlaska moga postupili! Zar ste ubrzali naredbu Gospodara svoga?!', i ploče baci, i brata svoga za kosu dohvati i poče ga vući sebi. 'O sine majke moje', reče Harun, 'narod moj me je slabim smatrao i umalo me nije ubio; nemoj da mi se svete dušmani i ne ubrajaj mene u zulumćare.'", TextEnglish = "And when Moses returned to his people, angry and grieved, he said, 'How wretched is that by which you have replaced me after [my departure]. Were you impatient over the matter of your Lord?' And he threw down the tablets and seized his brother by [the hair of] his head, pulling him toward him. [Aaron] said, 'O son of my mother, indeed the people overpowered me and were about to kill me, so let not the enemies rejoice over me[407] and do not place me among the wrongdoing people.'", TextGerman = "Als Musa zu seinem Volk zornig und bekümmert zurückkam, sagte er: Wie schlimm ist das, was ihr nach mir an meiner Stelle begangen habt! Wollt ihr den Befehl eures Herrn beschleunigen? Er warf die Tafeln hin und ergriff seinen Bruder beim Kopf, indem er ihn an sich zog. (Dieser) sagte: Sohn meiner Mutter, das Volk unterdrückte mich und hätte mich beinahe getötet! So lasse nicht die Feinde über mich Schadenfreude empfinden und stelle mich nicht zum ungerechten Volk!" });
                        surah7.VerseList.Add(new Verse { Number = 151, TextBosnian = "Gospodaru moj', zamoli Musa, 'oprosti meni i bratu mome i učini da budemo pod okriljem Tvoje milosti, Ti si među milostivima najmilostiviji!'", TextEnglish = "[Moses] said, 'My Lord, forgive me and my brother and admit us into Your mercy, for You are the most merciful of the merciful.'", TextGerman = "Er (Musa) sagte: Mein Herr, vergib mir und meinem Bruder, und lasse uns in Deine Barmherzigkeit eingehen. Du bist ja der Barmherzigste der Barmherzigen." });
                        surah7.VerseList.Add(new Verse { Number = 152, TextBosnian = "One koji su tele prihvatili stići će doista srdžba Gospodara njihova i poniženje još na ovom svijetu. Tako Mi kažnjavamo one koji kuju laži.", TextEnglish = "Indeed, those who took the calf [for worship] will obtain anger from their Lord and humiliation in the life of this world, and thus do We recompense the inventors [of falsehood].", TextGerman = "Gewiß, diejenigen, die sich das Kalb nahmen, wird Zorn von ihrem Herrn und Erniedrigung im diesseitigen Leben ereilen. So vergelten Wir denen, die Lügen ersinnen." });
                        surah7.VerseList.Add(new Verse { Number = 153, TextBosnian = "A oni koji hrđava djela rade, pa se poslije pokaju i vjernici postanu- pa, Gospodar tvoj je, poslije toga, sigurno, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "But those who committed misdeeds and then repented after them and believed - indeed your Lord, thereafter, is Forgiving and Merciful.", TextGerman = "Aber diejenigen, die böse Taten begehen und danach bereuen und glauben-, gewiß, dein Herr ist danach wahrlich Allvergebend und Barmherzig." });
                        surah7.VerseList.Add(new Verse { Number = 154, TextBosnian = "I kada Musaova srdžba minu, on uze ploče na kojima je bila ispisana uputa i milost za one koji od Gospodara svoga strahuju.", TextEnglish = "And when the anger subsided in Moses, he took up the tablets; and in their inscription was guidance and mercy for those who are fearful of their Lord.", TextGerman = "Und als sich in Musa der Zorn beruhigt hatte, nahm er die Tafeln. In ihrer Schrift ist Rechtleitung und Barmherzigkeit für die, die vor ihrem Herrn Ehrfurcht haben." });
                        surah7.VerseList.Add(new Verse { Number = 155, TextBosnian = "I Musa odabra iz naroda svoga sedamdeset ljudi da u određeno vrijeme stanu pred Nas. A kad ih zadesi potres, on reče: 'Gospodaru moj, da si htio, mogao si i njih i mene uništiti još prije. Zar da nas uništiš zbog onoga što su uradili bezumnici naši? To je samo iskušenje Tvoje kojim Ti, koga hoćeš, na stranputicu skrećeš, a koga hoćeš, na Pravi put upućuješ; Ti si Gospodar naš, pa nam oprosti i smiluj nam se! A Ti si Oprostitelj najbolji!'", TextEnglish = "And Moses chose from his people seventy men for Our appointment.[408] And when the earthquake seized them,[409] he said, 'My Lord, if You had willed, You could have destroyed them before and me [as well]. Would You destroy us for what the foolish among us have done? This is not but Your trial by which You send astray whom You will and guide whom You will. You are our Protector, so forgive us and have mercy upon us; and You are the best of forgivers.", TextGerman = "Und Musa wählte aus seinem Volk siebzig Männer zu Unserer festgesetzten Zeit. Als sie nun das Zittern ergriff, sagte er: Mein Herr, wenn Du gewollt hättest, hättest Du sie schon zuvor vernichtet, und auch mich. Willst Du uns vernichten für das, was die Toren von uns getan haben? Es ist doch nur Deine Versuchung, mit der Du in die Irre gehen läßt, wen Du willst, und rechtleitest, wen Du willst. Du bist unser Schutzherr, so vergib uns und erbarme Dich unser! Du bist der Beste derer, die vergeben." });
                        surah7.VerseList.Add(new Verse { Number = 156, TextBosnian = "I dosudi nam dobro na dunjaluku, i na ahiretu - mi se, uistinu, vraćamo Tebi!' 'Kaznom Svojom Ja kažnjavam koga hoću', reče On, 'a milost Moja obuhvata sve; dat ću je onima koji se budu grijeha klonili i zekat davali, i onima koji u ajete i dokaze Naše budu vjerovali',", TextEnglish = "And decree for us in this world [that which is] good and [also] in the Hereafter; indeed, we have turned back to You.' [Allāh] said, 'My punishment - I afflict with it whom I will, but My mercy encompasses all things.' So I will decree it [especially] for those who fear Me and give zakāh and those who believe in Our verses-", TextGerman = "Und bestimme für uns in diesem Diesseits Gutes und auch im Jenseits! Gewiß, wir haben zu Dir zurückgefunden. Er sagte: Mit Meiner Strafe treffe Ich, wen Ich will, aber Meine Barmherzigkeit umfaßt alles. Ich werde sie für die bestimmen, die gottesfürchtig sind und die Abgabe entrichten und die an Unsere Zeichen glauben," });
                        surah7.VerseList.Add(new Verse { Number = 157, TextBosnian = "onima koji će slijediti Poslanika, vjerovjesnika, koji neće znati ni čitati ni pisati, kojeg oni kod sebe, u Tevratu i Indžilu, zapisana nalaze, koji će od njih tražiti da čine dobra djela, a od odvratnih odvraćati, koji će im lijepa jela dozvoliti, a ružna zabraniti, koji će ih tereta i teškoća koje su oni imali osloboditi. A oni koji u njega budu vjerovali, koji ga budu podržavali i pomagali i svjetlo po njemu poslano slijedili - oni će baš uspjeti.'", TextEnglish = "Those who follow the Messenger, the unlettered prophet, whom they find written [i.e., described] in what they have of the Torah and the Gospel, who enjoins upon them what is right and prohibits them from what is wrong and makes lawful for them what is good and forbids them from what is evil and relieves them of their burden[410] and the shackles which were upon them.[411] So they who have believed in him, honored him, supported him and followed the light which was sent down with him - it is those who will be the successful.", TextGerman = "die dem Gesandten, dem schriftunkundigen Propheten, folgen, den sie bei sich in der Tora und im Evangelium aufgeschrieben finden. Er gebietet ihnen das Rechte und verbietet ihnen das Verwerfliche, er erlaubt ihnen die guten Dinge und verbietet ihnen die schlechten, und er nimmt ihnen ihre Bürde und die Fesseln ab, die auf ihnen lagen. Diejenigen nun, die an ihn glauben, ihm beistehen, ihm helfen und dem Licht, das mit ihm herabgesandt worden ist, folgen, das sind diejenigen, denen es wohl ergeht." });
                        surah7.VerseList.Add(new Verse { Number = 158, TextBosnian = "Reci: 'O ljudi, ja sam zaista svima vama Allahov poslanik, Njegova vlast je i na nebesima i na Zemlji; nema drugog boga osim Njega, On život i smrt daje, i zato vjerujte u Allaha i Poslanika Njegova, vjerovjesnika, koji ne zna čitati i pisati, koji vjeruje u Allaha i riječi Njegove; njega slijedite - da biste bili upućeni!'", TextEnglish = "Say, [O Muḥammad], 'O mankind, indeed I am the Messenger of Allāh to you all, [from Him] to whom belongs the dominion of the heavens and the earth. There is no deity except Him; He gives life and causes death.' So believe in Allāh and His Messenger, the unlettered prophet, who believes in Allāh and His words, and follow him that you may be guided.", TextGerman = "Sag: O ihr Menschen, ich bin der Gesandte Allahs an euch alle, Dem die Herrschaft der Himmel und der Erde gehört. Es gibt keinen Gott außer Ihm. Er macht lebendig und läßt sterben. So glaubt an Allah und Seinen Gesandten, den schriftunkundigen Propheten, der an Allah und Seine Worte glaubt, und folgt ihm, auf daß ihr rechtgeleitet sein möget!" });
                        surah7.VerseList.Add(new Verse { Number = 159, TextBosnian = "U narodu Musaovu ima zajednica ljudi koji se napućuju Istinom i koji prema njoj pravedno sude.", TextEnglish = "And among the people of Moses is a community[412] which guides by truth and by it establishes justice.", TextGerman = "Und unter dem Volk Musas ist eine Gemeinschaft, die mit der Wahrheit rechtleitet und nach ihr gerecht handelt." });
                        surah7.VerseList.Add(new Verse { Number = 160, TextBosnian = "I Mi smo ih u dvanaest rodova podijelili, i Musau smo objavili, kad mu je narod njegov vode zatražio: 'Udari štapom svojim po stijeni!', i iz nje je dvanaest vrela provrelo, svaki rod je znao vrelo iz kog će piti. I Mi smo im od oblaka hlad pravili i manu i prepelice im davali: 'Jedite ono lijepo čime vas opskrbljujemo!' Oni nisu Nama, nego su sami sebi zulum učinili.'", TextEnglish = "And We divided them into twelve descendant tribes[413] [as distinct] nations. And We inspired to Moses when his people implored him for water, 'Strike with your staff the stone,' and there gushed forth from it twelve springs. Every people [i.e., tribe] knew its watering place. And We shaded them with clouds and sent down upon them manna and quails, [saying], 'Eat from the good things with which We have provided you.' And they wronged Us not, but they were [only] wronging themselves.", TextGerman = "Und Wir zerteilten sie in zwölf Stämme, in Gemeinschaften. Und Wir gaben Musa ein, als sein Volk ihn um Wasser bat: Schlage mit deinem Stock auf den Felsen! Da brachen aus ihm zwölf Quellen hervor. Nun wußte jedermann, wo sein Platz zum Trinken war. Und Wir ließen die Wolken sie überschatten und sandten das Manna und die Wachteln auf sie herab: Eßt von den guten Dingen, mit denen Wir euch versorgt haben! Und sie fügten nicht Uns Unrecht zu, sondern sich selbst fügten sie Unrecht zu." });
                        surah7.VerseList.Add(new Verse { Number = 161, TextBosnian = "A kada im je bilo rečeno: 'Nastanite se u ovom naselju i jedite odakle hoćete i recite: 'Oprosti!', a na kapiju uđite glava pognutih', oprostit ćemo vam grijehe vaše, a onima koji čine dobra djela dat ćemo i više.", TextEnglish = "And [mention, O Muḥammad], when it was said to them, 'Dwell in this city [i.e., Jerusalem] and eat from it wherever you will and say, 'Relieve us of our burdens [i.e., sins],' and enter the gate bowing humbly; We will [then] forgive you your sins. We will increase the doers of good [in goodness and reward].'", TextGerman = "Und als zu ihnen gesagt wurde: Bewohnt diese Stadt und eßt von (dem, was in) ihr (ist), wo immer ihr wollt! und sagt: Vergebung! und tretet, euch niederwerfend, durch das Tor ein, so vergeben Wir euch eure Verfehlungen. Und Wir werden den Gutes Tuenden noch mehr erweisen." });
                        surah7.VerseList.Add(new Verse { Number = 162, TextBosnian = "Onda su oni među njima što zulum učiniše zamijenili drugom riječi riječ koja im je bila kazana, i Mi smo na njih s neba kaznu spustili zato što su zulum činili.", TextEnglish = "But those who wronged among them changed [the words] to a statement other than that which had been said to them. So We sent upon them a punishment from the sky for the wrong that they were doing.", TextGerman = "Doch da tauschten diejenigen von ihnen, die Unrecht taten, das Wort gegen ein anderes aus, das ihnen nicht gesagt worden war. Und da sandten Wir auf sie eine unheilvolle Strafe vom Himmel hinab dafür, daß sie Unrecht zu tun pflegten." });
                        surah7.VerseList.Add(new Verse { Number = 163, TextBosnian = "I upitaj ih o naselju koje se nalazilo pored mora kad su propise o suboti kršili: kada su im ribe, na oči njihove, dolazile dok su subotu svetkovali, a kad nisu svetkovali one im nisu dolazile. Eto, tako smo ih u iskušenje dovodili zato što su buntovni bili.", TextEnglish = "And ask them about the town that was by the sea - when they transgressed in [the matter of] the sabbath - when their fish came to them openly on their sabbath day, and the day they had no sabbath they did not come to them. Thus did We give them trial because they were defiantly disobedient.", TextGerman = "Und frag sie nach der Stadt, die am Meer lag, als sie den Sabbat übertraten, als ihre Fische zu ihnen an ihrem Sabbat sichtbar (geschwommen) kamen, aber an dem Tag, an dem sie nicht Sabbat hielten, nicht zu ihnen kamen. So prüfen Wir sie dafür, daß sie zu freveln pflegten." });
                        surah7.VerseList.Add(new Verse { Number = 164, TextBosnian = "A kad neki od njih rekoše: 'Zašto opominjete narod koji će Allah uništiti ili ga teškim mukama namučiti?', oni odgovoriše: 'Da bismo se pred Gospodarom vašim opravdali i da bi se oni toga klonili.'", TextEnglish = "And when a community among them said, 'Why do you advise [or warn] a people whom Allāh is [about] to destroy or to punish with a severe punishment?' they [the advisors] said, 'To be absolved before your Lord and perhaps they may fear Him.'", TextGerman = "Und als eine Gemeinschaft von ihnen sagte: Warum ermahnt ihr Leute, die Allah vernichten oder mit strenger Strafe strafen wird?, sagten sie: Als eine Entschuldigung bei eurem Herrn und auf daß sie gottesfürchtig werden mögen." });
                        surah7.VerseList.Add(new Verse { Number = 165, TextBosnian = "I kada zaboraviše ono čime su bili upozoravani, Mi izbavismo one koji su od nevaljalih djela odvraćali, a zulumćare kaznismo teškom kaznom, zato što su stalno buntovni bili.", TextEnglish = "And when they [i.e., those advised] forgot that by which they had been reminded, We saved those who had forbidden evil and seized those who wronged, with a wretched punishment, because they were defiantly disobeying.", TextGerman = "Und als sie vergessen hatten, womit sie ermahnt worden waren, retteten Wir diejenigen, die das Böse verboten, und ergriffen diejenigen, die Unrecht taten, mit schlimmer Strafe dafür, daß sie zu freveln pflegten." });
                        surah7.VerseList.Add(new Verse { Number = 166, TextBosnian = "I pošto su oni bahato odbili da se okane onoga što im je zabranjivano, Mi smo im rekli: 'Postanite majmuni prezreni!'", TextEnglish = "So when they were insolent about that which they had been forbidden, We said to them, 'Be apes, despised.'", TextGerman = "Als sie dann das mißachteten, was ihnen verboten war, sagten Wir zu ihnen: Werdet verstoßene Affen!" });
                        surah7.VerseList.Add(new Verse { Number = 167, TextBosnian = "I Gospodar tvoj obznani da će prepuštati nad njima vlast do Kijametskog dana nekom ko će ih na najgori način tlačiti. Gospodar tvoj, uistinu, brzo kažnjava, a On je, doista, i Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And [mention] when your Lord declared that He would surely [continue to] send upon them until the Day of Resurrection those who would afflict them with the worst torment. Indeed, your Lord is swift in penalty; but indeed, He is Forgiving and Merciful.", TextGerman = "Und als dein Herr ankündigte, Er werde ganz gewiß gegen sie bis zum Tag der Auferstehung jemanden schicken, der ihnen eine böse Qual auferlegt. Dein Herr ist fürwahr schnell im Bestrafen, aber Er ist auch wahrlich Allvergebend und Barmherzig." });
                        surah7.VerseList.Add(new Verse { Number = 168, TextBosnian = "I Mi smo ih po Zemlji kao narode raspodijelili: bilo je među njima dobrih, a i onih koji to nisu; iskušavali smo ih i u dobru i u zlu, ne bi li se ispravnom putu vratili.", TextEnglish = "And We divided them throughout the earth into nations. Of them some were righteous, and of them some were otherwise. And We tested them with good [times] and bad that perhaps they would return [to obedience].", TextGerman = "Und Wir zerteilten sie auf der Erde in Gemeinschaften. Unter ihnen gab es Rechtschaffene und solche, die dies nicht waren. Und Wir prüften sie mit Gutem und Bösem, auf daß sie umkehren mögen." });
                        surah7.VerseList.Add(new Verse { Number = 169, TextBosnian = "I poslije njih ostala su pokoljenja koja su Knjigu naslijedila i koja su uzimala prolazna dobra ovoga nižeg svijeta, i govorila: 'Bit će nam oprošteno!' A ako bi im opet dopalo šaka tako nešto, opet bi to prihvatili. Zar od njih nije uzet zavjet u Knjizi da će o Allahu samo istinu govoriti - a oni čitaju ono što je u njoj. Ahiret je bolji za one koji se nastoje uščuvati; pa zar ne razumijete?", TextEnglish = "And there followed them successors who inherited the Scripture [while] taking the commodities[414] of this lower life and saying, 'It will be forgiven for us.' And if an offer like it[415] comes to them, they will [again] take it. Was not the covenant of the Scripture [i.e., the Torah] taken from them that they would not say about Allāh except the truth, and they studied what was in it? And the home of the Hereafter is better for those who fear Allāh, so will you not use reason?", TextGerman = "Dann folgten nach ihnen Nachfolger, die die Schrift erbten. Sie greifen nach den Glücksgütern des Diesseitigen und sagen: Es wird uns vergeben werden. Und wenn sich ihnen gleiche Glücksgüter bieten, greifen sie danach. Wurde nicht mit ihnen das Abkommen der Schrift getroffen, sie sollen über Allah nur die Wahrheit sagen? Sie haben doch das, was darin steht, erlernt. Und die jenseitige Wohnstätte ist besser für diejenigen, die gottesfürchtig sind. Begreift ihr denn nicht?" });
                        surah7.VerseList.Add(new Verse { Number = 170, TextBosnian = "A oni koji se čvrsto drže Knjige i koji obavljaju namaz - pa, Mi, doista, nećemo dopustiti da propadne nagrada onima koji čine dobra djela.", TextEnglish = "But those who hold fast to the Book [i.e., the Qur’ān] and establish prayer - indeed, We will not allow to be lost the reward of the reformers.", TextGerman = "Und diejenigen, die an der Schrift festhalten und das Gebet verrichten - gewiß, Wir lassen den Lohn der Heilstifter nicht verlorengehen." });
                        surah7.VerseList.Add(new Verse { Number = 171, TextBosnian = "A kada smo iznad njih brdo podigli - činilo se kao oblak - oni su bili uvjereni da će na njih pasti. Prihvatite snažno ono što smo vam dali, i neka vam je na umu ono što je u njemu - da biste se sačuvali!", TextEnglish = "And [mention] when We raised the mountain above them as if it was a dark cloud and they were certain that it would fall upon them,[416] [and Allāh said], 'Take what We have given you with determination and remember what is in it that you might fear Allāh.'", TextGerman = "Und als Wir den Berg über sie heraushoben, als wäre er ein Schattendach, und sie glaubten, er würde auf sie fallen: Haltet fest an dem, was Wir euch gegeben haben, und gedenkt dessen, was es enthält, auf daß ihr gottesfürchtig werden möget!" });
                        surah7.VerseList.Add(new Verse { Number = 172, TextBosnian = "I kad je Gospodar tvoj od Ademovih sinova, iz njihovih kičmi, izveo potomstvo njihovo i zatražio od njih da posvjedoče protiv sebe: 'Zar Ja nisam Gospodar vaš?', oni su odgovarali: 'Jesi, mi svjedočimo ', i zato da na Kijametskom danu ne reknete: 'Mi o ovome nismo ništa znali.'", TextEnglish = "And [mention] when your Lord took from the children of Adam - from their loins - their descendants and made them testify of themselves, [saying to them], 'Am I not your Lord?' They said, 'Yes, we have testified.' [This] - lest you should say on the Day of Resurrection, 'Indeed, we were of this unaware.'", TextGerman = "Und als dein Herr aus den Kindern Adams, aus ihren Rücken, ihre Nachkommenschaft nahm und sie gegen sich selbst zeugen ließ: Bin Ich nicht euer Herr? Sie sagten: Doch, wir bezeugen (es)! (Dies,) damit ihr nicht am Tag der Auferstehung sagt: Wir waren dessen unachtsam," });
                        surah7.VerseList.Add(new Verse { Number = 173, TextBosnian = "Ili da ne reknete: 'Naši su preci prije nas druge Allahu ravnim smatrali, a mi smo pokoljenje poslije njih. Zar ćeš nas kazniti za ono što su lažljivci činili?'", TextEnglish = "Or [lest] you say, 'It was only that our fathers associated [others in worship] with Allāh before, and we were but descendants after them. Then would You destroy us for what the falsifiers have done?'", TextGerman = "oder (auch) nicht sagt: Unsere Väter gesellten doch zuvor (Allah) Teilhaber zu, und wir sind (nur) eine Nachkommenschaft nach ihnen. Willst du uns vernichten für das, was diejenigen getan haben, die Nichtiges tun?" });
                        surah7.VerseList.Add(new Verse { Number = 174, TextBosnian = "I tako, eto, Mi opširno iznosimo dokaze, da bi oni došli sebi.", TextEnglish = "And thus do We [explain in] detail the verses, and perhaps they will return.[417]", TextGerman = "So legen Wir die Zeichen ausführlich dar, - auf daß sie umkehren mögen." });
                        surah7.VerseList.Add(new Verse { Number = 175, TextBosnian = "I kaži im vijest o onome kome smo dokaze Naše dali, ali koji se od njih udaljio pa ga šejtan dostigao, i on je zalutao.", TextEnglish = "And recite to them, [O Muḥammad], the news of him[418] to whom We gave [knowledge of] Our signs, but he detached himself from them; so Satan pursued him, and he became of the deviators.[419]", TextGerman = "Und verlies ihnen die Kunde von dem, dem Wir Unsere Zeichen gaben und der sich dann ihrer entledigte. Da verfolgte ihn der Satan, und so wurde er einer der Verirrten." });
                        surah7.VerseList.Add(new Verse { Number = 176, TextBosnian = "A da smo htjeli, mogli smo ga time uzvisiti, ali se on ovom svijetu priklonio i za svojom strašću krenuo. Njegov je slučaj kao slučaj psa: ako ga potjeraš, on isplažena jezika dahće, a ako ga se okaniš - on opet dahće. Takvi su ljudi koji Naše dokaze smatraju lažnim; zato, kazuj događaje da bi oni razmislili.", TextEnglish = "And if We had willed, We could have elevated him thereby,[420] but he adhered [instead] to the earth[421] and followed his own desire. So his example is like that of the dog: if you chase him, he pants, or if you leave him, he [still] pants. That is the example of the people who denied Our signs.[422] So relate the stories that perhaps they will give thought.", TextGerman = "Und wenn Wir gewollt hätten, hätten Wir ihn durch sie fürwahr erhöht. Aber er neigte zur Erde hin und folgte seiner Neigung. So ist sein Gleichnis das eines Hundes: Wenn du auf ihn losgehst, hechelt er; läßt du ihn (in Ruhe), hechelt er (auch). So ist das Gleichnis der Leute, die Unsere Zeichen für Lüge erklärten. Berichte die Geschichte, auf daß sie nachdenken mögen." });
                        surah7.VerseList.Add(new Verse { Number = 177, TextBosnian = "Loš su primjer ljudi koji ne priznaju Naše dokaze, oni zulum čine sami sebi.", TextEnglish = "How evil an example [is that of] the people who denied Our signs and used to wrong themselves.", TextGerman = "Wie böse ist das Gleichnis der Leute, die Unsere Zeichen für Lüge erklärten und sich selbst Unrecht zu tun pflegten!" });
                        surah7.VerseList.Add(new Verse { Number = 178, TextBosnian = "Koga Allah uputi na Pravi put - bit će na Pravom putu, a koga na stranputicu skrene - pa takvi su pravi gubitnici.", TextEnglish = "Whoever Allāh guides - he is the [rightly] guided; and whoever He sends astray[423] - it is those who are the losers.", TextGerman = "Wen Allah rechtleitet, der ist (in Wahrheit) rechtgeleitet. Wen Er aber in die Irre gehen läßt, das sind die Verlierer." });
                        surah7.VerseList.Add(new Verse { Number = 179, TextBosnian = "Mi smo za Džehennem mnoge džine i ljude stvorili; oni srca imaju - a njima ne shvataju, oni oči imaju - a njima ne vide, oni uši imaju - a njima ne čuju; oni su kao stoka, čak i gori- oni su baš nemarni.", TextEnglish = "And We have certainly created for Hell many of the jinn and mankind. They have hearts with which they do not understand, they have eyes with which they do not see, and they have ears with which they do not hear. Those are like livestock; rather, they are more astray.[424] It is they who are the heedless.", TextGerman = "Wir haben ja schon viele von den Ginn und den Menschen für die Hölle erschaffen. Sie haben Herzen, mit denen sie nicht verstehen; sie haben Augen, mit denen sie nicht sehen; und sie haben Ohren, mit denen sie nicht hören. Jene sind wie das Vieh. Aber nein! Sie irren noch weiter ab. Jene sind (überhaupt) die Unachtsamen." });
                        surah7.VerseList.Add(new Verse { Number = 180, TextBosnian = "Allah ima najljepša imena i vi Ga zovite tim imenima, a klonite se onih koji iskreću Njegova imena; kako budu radili, onako će biti kažnjeni!", TextEnglish = "And to Allāh belong the best names, so invoke Him by them. And leave [the company of] those who practice deviation concerning His names.[425] They will be recompensed for what they have been doing.", TextGerman = "Allahs sind die schönsten Namen; so ruft Ihn damit an und laßt diejenigen, die mit Seinen Namen abwegig umgehen. Ihnen wird das vergolten, was sie zu tun pflegten." });
                        surah7.VerseList.Add(new Verse { Number = 181, TextBosnian = "A među onima koje stvaramo ima ljudi koji druge upućuju istini i koji prema njoj pravedno sude.", TextEnglish = "And among those We created is a community[426] which guides by truth and thereby establishes justice.", TextGerman = "Und unter denjenigen, die Wir erschaffen haben, gibt es eine Gemeinschaft, die mit der Wahrheit rechtleitet und nach ihr gerecht handelt." });
                        surah7.VerseList.Add(new Verse { Number = 182, TextBosnian = "A one koji Naše ajete i znakove poriču Mi ćemo malo-pomalo, a da oni ni znati neće, u propast voditi.", TextEnglish = "But those who deny Our signs - We will progressively lead them [to destruction][427] from where they do not know.", TextGerman = "Diejenigen aber, die Unsere Zeichen für Lüge erklären, werden Wir stufenweise (dem Verderben) näherbringen, von wo sie nicht wissen." });
                        surah7.VerseList.Add(new Verse { Number = 183, TextBosnian = "I davat ću im vremena; zamka je Moja, doista čvrsta.", TextEnglish = "And I will give them time. Indeed, My plan is firm.", TextGerman = "Und Ich gewähre ihnen Aufschub. (Aber) gewiß, Meine List ist fest." });
                        surah7.VerseList.Add(new Verse { Number = 184, TextBosnian = "Pa zašto oni ne razmisle da poslanik koji im je poslan nije lud; on je samo upozoritelj jasni.", TextEnglish = "Then do they not give thought? There is in their companion [i.e., Muḥammad (ﷺ)] no madness. He is not but a clear warner.", TextGerman = "Denken sie denn nicht (darüber) nach? Ihr Gefährte leidet nicht an Besessenheit; er ist nur ein deutlicher Warner." });
                        surah7.VerseList.Add(new Verse { Number = 185, TextBosnian = "I zašto oni ne promisle o carstvu nebesa i Zemlje i o svemu onome što je On stvorio, i da im se, možda, kraj primakao? Pa u koji će govor, ako ne u Kur'an, vjerovati?", TextEnglish = "Do they not look into the realm of the heavens and the earth and everything that Allāh has created and [think] that perhaps their appointed time has come near? So in what statement [i.e., message] hereafter will they believe?", TextGerman = "Haben sie sich denn nicht im Reich der Himmel und der Erde umgeschaut und was Allah an Dingen erschaffen hat und, daß ihre Frist vielleicht nahegerückt ist? An welche Aussage nach dieser wollen sie denn (sonst) glauben?" });
                        surah7.VerseList.Add(new Verse { Number = 186, TextBosnian = "Koga Allah na stranputicu skrene, niko ga ne može na Pravi put uputiti! On će ih ostaviti da u osionosti svojoj lutaju.", TextEnglish = "Whoever Allāh sends astray - there is no guide for him. And He leaves them in their transgression, wandering blindly.", TextGerman = "Wen Allah in die Irre gehen läßt, der hat keinen, der ihn rechtleiten könnte; Er läßt sie in ihrer Auflehnung umherirren." });
                        surah7.VerseList.Add(new Verse { Number = 187, TextBosnian = "Pitaju te o Smaku svijeta, kada će se zbiti. Reci: 'To zna jedino Gospodar moj, On će ga u određeno vrijeme otkriti, a težak će biti nebesima i Zemlji, sasvim neočekivano će vam doći.' Pitaju te kao da ti o njemu nešto znaš. Reci: 'To samo Allah zna, ali većina ljudi ne zna.'", TextEnglish = "They ask you, [O Muḥammad], about the Hour: when is its arrival?[428] Say, 'Its knowledge is only with my Lord. None will reveal its time except Him. It lays heavily[429] upon the heavens and the earth. It will not come upon you except unexpectedly.' They ask you as if you are familiar with it. Say, 'Its knowledge is only with Allāh, but most of the people do not know.'", TextGerman = "Sie fragen dich nach der Stunde, für wann sie feststeht. Sag: Das Wissen darüber ist nur bei meinem Herrn. Nur Er wird sie zu ihrer Zeit erscheinen lassen. Schwer lastet sie in den Himmeln und auf der Erde. Sie wird nur plötzlich über euch kommen. Sie fragen dich, als ob du gut unterrichtet über sie seiest. Sag: Das Wissen darüber ist nur bei Allah. Aber die meisten Menschen wissen nicht." });
                        surah7.VerseList.Add(new Verse { Number = 188, TextBosnian = "Reci: 'Ja ne mogu ni samom sebi neku korist pribaviti, ni od sebe kakvu štetu otkloniti; biva onako kako Allah hoće. A da znadoh ono što je čulima nedokučivo, stekao bih mnoga dobra, a zlo bi bilo daleko od mene; ja samo donosim opomene i radosne vijesti ljudima koji vjeruju.'", TextEnglish = "Say, 'I hold not for myself [the power of] benefit or harm, except what Allāh has willed. And if I knew the unseen, I could have acquired much wealth, and no harm would have touched me. I am not except a warner and a bringer of good tidings to a people who believe.'", TextGerman = "Sag: Ich vermag mir selbst weder Nutzen noch Schaden (zu bringen), außer was Allah will. Wenn ich das Verborgene wüßte, würde ich mir wahrlich viel Gutes verschaffen, und Böses würde mir nicht widerfahren. Ich bin nur ein Warner und ein Frohbote für Leute, die glauben." });
                        surah7.VerseList.Add(new Verse { Number = 189, TextBosnian = "On je Taj Koji vas je od jednog čovjeka stvorio - a od njega je drugu njegovu stvorio da se on uz nju smiri. I kada je on nju obljubio, ona je zanijela lahko breme i nosila ga; a kad joj je ono otežalo, njih dvoje su zamolili Allaha, Gospodara svoga: 'Ako nam daruješ zdrava potomka, bit ćemo, doista, u redovima zahvalnih!'", TextEnglish = "It is He who created you from one soul and created from it its mate that he[430] might dwell in security with her. And when he [i.e., man] covers her,[431] she carries a light burden [i.e., a pregnancy] and continues therein. And when it becomes heavy, they both invoke Allāh, their Lord, 'If You should give us a good[432] [child], we will surely be among the grateful.'", TextGerman = "Er ist es, Der euch aus einem einzigen Wesen schuf, und Er hat aus ihm seine Gattin gemacht, damit er (der Mann) bei ihr Ruhe finde. Und als er über sie kam, trug sie an einer leichten Schwangerschaft und verbrachte damit (eine Zeit). Als sie dann schwer zu tragen hatte, riefen die beiden Allah, ihren Herrn, an: Wenn Du uns einen Rechtschaffenen gibst, werden wir ganz gewiß zu den Dankbaren gehören." });
                        surah7.VerseList.Add(new Verse { Number = 190, TextBosnian = "I kad im je On darovao zdrava potomka, njih dvoje su izjednačili druge s Njim u onome što im On daje, a Allah je vrlo visoko iznad onih koje Njemu u obožavanju pridružuju!", TextEnglish = "But when He gives them a good [child], they[433] ascribe partners to Him concerning that which He has given them. Exalted is Allāh above what they associate with Him.", TextGerman = "Als Er ihnen dann einen Rechtschaffenen gegeben hatte, gaben sie Ihm Teilhaber an dem, was Er ihnen gegeben hatte. Aber Allah ist Erhaben über das, was sie (Ihm) beigesellen." });
                        surah7.VerseList.Add(new Verse { Number = 191, TextBosnian = "Zar da Njemu ravnim smatraju one koji ne mogu ništa stvoriti, i sami su stvoreni,", TextEnglish = "Do they associate with Him those who create nothing and they are [themselves] created?", TextGerman = "Wollen sie (Ihm etwas) beigesellen, was nichts erschafft, während sie selbst erschaffen wurden," });
                        surah7.VerseList.Add(new Verse { Number = 192, TextBosnian = "i koji im ne mogu pomoći niti mogu sebi pomoći?", TextEnglish = "And they [i.e., the false deities] are unable to [give] them help, nor can they help themselves.", TextGerman = "und die ihnen keine Hilfe leisten, noch sich selbst helfen können?" });
                        surah7.VerseList.Add(new Verse { Number = 193, TextBosnian = "A ako ih zamolite da vas na Pravi put upute, neće vam se odazvati; isto vam je pozivali ih ili šutjeli.", TextEnglish = "And if you [believers] invite them to guidance, they will not follow you. It is all the same for you whether you invite them or you are silent.", TextGerman = "Und wenn ihr sie zur Rechtleitung aufruft, folgen sie euch nicht. Gleich ist es in Bezug auf euch, ob ihr sie aufruft oder ob ihr schweigt." });
                        surah7.VerseList.Add(new Verse { Number = 194, TextBosnian = "Oni kojima se vi, pored Allaha, molite, zaista su robovi, kao i vi. Pa, vi ih molite, i neka vam se odazovu ako istinu govorite!", TextEnglish = "Indeed, those you [polytheists] call upon besides Allāh are servants [i.e., creations] like you. So call upon them and let them respond to you, if you should be truthful.", TextGerman = "Gewiß, diejenigen, die ihr anstatt Allahs anruft, sind (nur) Diener gleich euch. So ruft sie (doch) an, und so sollen sie euch doch erhören, wenn ihr wahrhaftig seid!" });
                        surah7.VerseList.Add(new Verse { Number = 195, TextBosnian = "Imaju li oni noge da na njima hodaju, ili ruke da njima prihvataju, imaju li oči da njima gledaju, ili uši da njima čuju? Reci: 'Zovite božanstva svoja, pa protiv mene kakvo hoćete lukavstvo smislite, i ne odugovlačite.'", TextEnglish = "Do they have feet by which they walk? Or do they have hands by which they strike? Or do they have eyes by which they see? Or do they have ears by which they hear? Say, [O Muḥammad], 'Call your 'partners' and then conspire against me and give me no respite.", TextGerman = "Haben sie (etwa) Füße, mit denen sie gehen, oder haben sie Hände, mit denen sie zupacken, oder haben sie Augen, mit denen sie sehen, oder haben sie Ohren, mit denen sie hören? Sag: Ruft eure Teilhaber an, und hierauf schmiedet eure List gegen mich an und gewährt mir keinen Aufschub." });
                        surah7.VerseList.Add(new Verse { Number = 196, TextBosnian = "Moj je, zaista, zaštitnik Allah Koji Knjigu objavljuje i On se o dobrima brine.", TextEnglish = "Indeed, my protector is Allāh, who has sent down the Book; and He is an ally to the righteous.", TextGerman = "Mein Schutzherr ist Allah, Der das Buch offenbart hat, und Er macht Sich zum Schutzherrn der Rechtschaffenen." });
                        surah7.VerseList.Add(new Verse { Number = 197, TextBosnian = "A oni kojima se vi, pored Njega, molite, ne mogu ni vama, a ni sebi pomoći.", TextEnglish = "And those you call upon besides Him are unable to help you, nor can they help themselves.'", TextGerman = "Diejenigen, die ihr außer Ihm anruft, können euch keine Hilfe leisten, noch sich selbst helfen." });
                        surah7.VerseList.Add(new Verse { Number = 198, TextBosnian = "A kad ih zamolite da vas upute na pravi put, oni ne čuju; vidiš ih kao da te gledaju, ali oni ne vide.", TextEnglish = "And if you invite them to guidance, they do not hear; and you see them looking at you while they do not see.", TextGerman = "Und wenn ihr sie zur Rechtleitung aufruft, hören sie nicht; du siehst sie dich anschauen, doch sie sehen nicht." });
                        surah7.VerseList.Add(new Verse { Number = 199, TextBosnian = "Ti ono lijepo što čine prihvati!, i traži da se čine dobra djela, a neznalica se kloni!", TextEnglish = "Take what is given freely,[434] enjoin what is good, and turn away from the ignorant.", TextGerman = "Nimm den Überschuß, gebiete das allgemein Gute und wende dich von den Toren ab!" });
                        surah7.VerseList.Add(new Verse { Number = 200, TextBosnian = "A ako te šejtan pokuša na zlo navesti, ti potraži utočište u Allaha, On je, uistinu, Onaj Koji sve čuje i Onaj Koji sve zna.", TextEnglish = "And if an evil suggestion comes to you from Satan, then seek refuge in Allāh. Indeed, He is Hearing and Knowing.", TextGerman = "Und wenn dich vom Satan eine Eingebung aufstachelt, dann suche Schutz bei Allah, denn Er ist ja Allhörend und Allwissend." });
                        surah7.VerseList.Add(new Verse { Number = 201, TextBosnian = "Doista, oni koji se Allaha boje, čim ih dodirne sablazan šejtanska, sjete se, i postanu razboriti,", TextEnglish = "Indeed, those who fear Allāh - when an impulse touches them from Satan, they remember [Him] and at once they have insight.", TextGerman = "Diejenigen, die gottesfürchtig sind, - wenn ihnen eine Anwandlung vom Satan widerfährt, bedenken sie, und da werden sie sogleich einsichtig." });
                        surah7.VerseList.Add(new Verse { Number = 202, TextBosnian = "dok prijatelje šejtanove šejtani podržavaju u zabludi i nikako ne prestaju.", TextEnglish = "But their brothers[435] - they [i.e., the devils] increase them in error; then they do not stop short.", TextGerman = "Aber ihre Brüder lassen sie in ihrer Verirrung gewähren, und dann lassen sie (daran) nicht nach." });
                        surah7.VerseList.Add(new Verse { Number = 203, TextBosnian = "Kad im nikakav znak ne doneseš, oni govore: 'Zašto ga sam ne izmisliš!' Reci: 'Ja slijedim samo ono što mi Gospodar moj objavljuje.' Ovo su jasni dokazi od Gospodara vašeg i uputa i milost za ljude koji vjeruju.", TextEnglish = "And when you, [O Muḥammad], do not bring them a sign [i.e., miracle], they say, 'Why have you not contrived it?' Say, 'I only follow what is revealed to me from my Lord. This [Qur’ān] is enlightenment from your Lord and guidance and mercy for a people who believe.'", TextGerman = "Und wenn du ihnen kein Zeichen bringst, sagen sie: Hättest du es dir doch (selbst) ausgesucht! Sag: Ich folge nur dem, was mir von meinem Herrn (als Offenbarung) eingegeben wird. Dies sind einsichtbringende Zeichen von eurem Herrn und Rechtleitung und Barmherzigkeit für Leute, die glauben." });
                        surah7.VerseList.Add(new Verse { Number = 204, TextBosnian = "A kad se uči Kur'an, vi ga slušajte i šutite - da bi vam milost ukazana bila.", TextEnglish = "So when the Qur’ān is recited, then listen to it and pay attention that you may receive mercy.", TextGerman = "Und wenn der Qur’an vorgetragen wird, dann hört ihm zu und horcht hin, auf daß ihr Erbarmen finden möget!" });
                        surah7.VerseList.Add(new Verse { Number = 205, TextBosnian = "I spominji Gospodara svoga u sebi, ponizno i sa strahopoštovanjem i ne podižući jako glas - ujutro i navečer, i ne budi među nemarnima.", TextEnglish = "And remember your Lord within yourself in humility and in fear without being apparent in speech - in the mornings and the evenings. And do not be among the heedless.", TextGerman = "Und gedenke deines Herrn in deiner Seele in Unterwürfigkeit flehend und in Furcht und mit leiser Stimme, am Morgen und am Abend. Und gehöre nicht zu den Unachtsamen!" });
                        surah7.VerseList.Add(new Verse { Number = 206, TextBosnian = "Oni koji su kod Gospodara tvoga doista ne zaziru da Mu ibadet čine; samo Njega hvale i samo Njemu na sedždu padaju.", TextEnglish = "Indeed, those who are near your Lord [i.e., the angels] are not prevented by arrogance from His worship, and they exalt Him, and to Him they prostrate.", TextGerman = "Diejenigen, die bei deinem Herrn sind, sind nicht zu hochmütig dazu, Ihm zu dienen; sie preisen Ihn und werfen sich vor Ihm nieder." });
                        result = surah7;

                        break;
                    #endregion

                    // 
                    #region 8 Al-Anfal
                    case 8:
                        Surah surah8 = new Surah(); surah8.Name = Constants.Surah8Name; surah8.Number = 8; surah8.VerseList = new List<Verse>();
                        surah8.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah8.VerseList.Add(new Verse { Number = 1, TextBosnian = "Pitaju te o ratnom plijenu. Reci: 'Ratni plijen pripada Allahu i Poslaniku.' Zato se bojte Allaha i izgladite međusobne razmirice, i pokoravajte se Allahu i Njegovu Poslaniku, ako ste pravi vjernici.", TextEnglish = "They ask you, [O Muḥammad], about the bounties [of war]. Say, 'The [decision concerning] bounties is for Allāh and the Messenger.' So fear Allāh and amend that which is between you and obey Allāh and His Messenger, if you should be believers.", TextGerman = "Sie fragen dich nach der (zugedachten) Beute. Sag: Die (zugedachte) Beute gehört Allah und dem Gesandten. So fürchtet Allah und stiftet Frieden untereinander, und gehorcht Allah und Seinem Gesandten, wenn ihr gläubig seid!" });
                        surah8.VerseList.Add(new Verse { Number = 2, TextBosnian = "Pravi su vjernici samo oni čija se srca strahom ispune kad se Allah spomene, a kad im se ajeti Njegovi uče, vjerovanje im povećaju i samo se na Gospodara svoga oslanjaju,", TextEnglish = "The believers are only those who, when Allāh is mentioned, their hearts become fearful, and when His verses are recited to them, it increases them in faith; and upon their Lord they rely -", TextGerman = "Die (wahren) Gläubigen sind ja diejenigen, deren Herzen sich vor Ehrfurcht regen, wenn Allahs gedacht wird, und die, wenn ihnen Seine Zeichen verlesen werden, es ihren Glauben mehrt, und die sich auf ihren Herrn verlassen," });
                        surah8.VerseList.Add(new Verse { Number = 3, TextBosnian = "oni koji namaz obavljaju i dio od onoga što im Mi dajemo udjeljuju.", TextEnglish = "The ones who establish prayer, and from what We have provided them, they spend.", TextGerman = "die das Gebet verrichten und von dem, womit Wir sie versorgt haben, ausgeben." });
                        surah8.VerseList.Add(new Verse { Number = 4, TextBosnian = "Oni su, zbilja, pravi vjernici - njih kod Gospodara njihova čekaju počasti, i oprost, i opskrba divna.", TextEnglish = "Those are the believers, truly. For them are degrees [of high position] with their Lord and forgiveness and noble provision.", TextGerman = "Das sind die wahren Gläubigen. Für sie gibt es bei ihrem Herrn Rangstufen und Vergebung und ehrenvolle Versorgung." });
                        surah8.VerseList.Add(new Verse { Number = 5, TextBosnian = "Tako je bilo i onda kada te je Gospodar tvoj s pravom iz doma tvoga izveo, a to jednoj grupi vjernika nije nikako po volji bilo.", TextEnglish = "[It[437] is] just as when your Lord brought you out of your home [for the battle of Badr] in truth, while indeed, a party among the believers were unwilling,", TextGerman = "So wie dein Herr dich aus deinem Haus hinausziehen ließ mit der Wahrheit, während es einer Gruppe der Gläubigen wahrlich zuwider war," });
                        surah8.VerseList.Add(new Verse { Number = 6, TextBosnian = "Raspravljali su s tobom o borbi, nakon što je bilo očito da će pobijediti, kao da se naočigled svoj u smrt gone.", TextEnglish = "Arguing with you concerning the truth after it had become clear, as if they were being driven toward death while they were looking on.", TextGerman = "und sie mit dir über die Wahrheit stritten, nachdem (es) klargeworden war, als ob sie in den Tod getrieben würden, während sie zuschauten." });
                        surah8.VerseList.Add(new Verse { Number = 7, TextBosnian = "Kad vam je Allah obećao da će vaša biti jedna od dvije skupine - a vi ste više voljeli da vam padne šaka ona koja nije bila naoružana -Allah je htio riječima Svojim Istinu utvrditi i nevjernike u korijenu istrijebiti,", TextEnglish = "[Remember, O believers], when Allāh promised you one of the two groups[438] - that it would be yours - and you wished that the unarmed one would be yours. But Allāh intended to establish the truth by His words and to eliminate the disbelievers", TextGerman = "Und als Allah euch versprach, daß die eine der beiden Gruppen euch gehören sollte, und ihr (es) lieber gehabt hättet, daß diejenige ohne Kampfkraft euer sein sollte! Aber Allah will mit Seinen Worten die Wahrheit bestätigen und die Rückkehr der Ungläubigen abschneiden," });
                        surah8.VerseList.Add(new Verse { Number = 8, TextBosnian = "da Istinu utvrdi i neistinu uništi, makar to prestupnicima ne bilo po volji.", TextEnglish = "That He should establish the truth and abolish falsehood, even if the criminals disliked it.", TextGerman = "um die Wahrheit zu bestätigen und das Falsche zunichte zu machen, auch wenn es den Übeltätern zuwider ist." });
                        surah8.VerseList.Add(new Verse { Number = 9, TextBosnian = "I kada ste od Gospodara svoga spas zatražili, On vam se odazvao: 'Poslat ću vam u pomoć hiljadu meleka koji će jedni za drugima dolaziti.'", TextEnglish = "[Remember] when you were asking help of your Lord, and He answered you, 'Indeed, I will reinforce you with a thousand from the angels, following one another.'", TextGerman = "(Gedenke) als ihr euren Herrn um Hilfe anrieft. Da erhörte Er euch: Ich werde euch mit Tausend von den Engeln unterstützen, hintereinander reitend." });
                        surah8.VerseList.Add(new Verse { Number = 10, TextBosnian = "Allah je to učinio da bi vas obradovao i da bi se time srca vaša umirila; a pobjeda je samo od Allaha -Allah je zaista silan i mudar.", TextEnglish = "And Allāh made it not but good tidings and so that your hearts would be assured thereby. And victory is not but from Allāh. Indeed, Allāh is Exalted in Might and Wise.", TextGerman = "Allah machte es nur zu einer frohen Botschaft und damit eure Herzen dadurch Ruhe fänden. Der Sieg kommt ja nur von Allah. Gewiß, Allah ist Allmächtig und Allweise." });
                        surah8.VerseList.Add(new Verse { Number = 11, TextBosnian = "Kad je On učinio da vas san, kao spokojstvo od Njega, obuzme i s neba vam vodu spustio da bi vas njome očistio, i da bi od vas šejtanovo uznemiravanje odstranio, i da bi srca vaša jakim učinio i da bi njome - kišom stopala učvrstio.", TextEnglish = "[Remember] when He overwhelmed you with drowsiness [giving] security from Him and sent down upon you from the sky, rain by which to purify you and remove from you the evil [suggestions] of Satan and to make steadfast your hearts and plant firmly thereby your feet.", TextGerman = "Als Er Schläfrigkeit euch überkommen ließ als (Gefühl der) Sicherheit von Ihm und Wasser vom Himmel auf euch herabsandte, um euch damit zu reinigen und das Unheil des Satans von euch zu entfernen und um eure Herzen zu stärken und (eure) Füße damit zu festigen." });
                        surah8.VerseList.Add(new Verse { Number = 12, TextBosnian = "Kad je Gospodar tvoj nadahnuo meleke: 'Ja sam s vama, pa učvrstite one koji vjeruju! U srca nevjernika Ja ću strah uliti, pa ih vi po šijama udarite, i udarite ih po svim udovima.'", TextEnglish = "[Remember] when your Lord inspired to the angels, 'I am with you, so strengthen those who have believed. I will cast terror into the hearts of those who disbelieved, so strike [them] upon the necks and strike from them every fingertip.'[439]", TextGerman = "Als dein Herr den Engeln eingab: Gewiß, Ich bin mit euch. So festigt diejenigen, die glauben! Ich werde in die Herzen derer, die ungläubig sind, Schrecken einjagen. So schlagt oberhalb der Nacken und schlagt von ihnen jeden Finger!" });
                        surah8.VerseList.Add(new Verse { Number = 13, TextBosnian = "To je zato što su se suprotstavljali Allahu i Poslaniku Njegovu; a onoga ko se suprotstavlja Allahu i Poslaniku Njegovu, Allah će, zaista, strašno kazniti.", TextEnglish = "That is because they opposed Allāh and His Messenger. And whoever opposes Allāh and His Messenger - indeed, Allāh is severe in penalty.", TextGerman = "Dies dafür, daß sie Allah und Seinem Gesandten entgegenwirkten. Wer Allah und Seinem Gesandten entgegenwirkt, - gewiß, Allah ist streng im Bestrafen." });
                        surah8.VerseList.Add(new Verse { Number = 14, TextBosnian = "Kazna vam je to, pa je iskusite, a nevjernike čeka patnja u Vatri.", TextEnglish = "That [is yours], so taste it.' And indeed for the disbelievers is the punishment of the Fire.", TextGerman = "Das (ist eure Strafe dafür), so kostet sie! Und (wisset,) daß es für die Ungläubigen die Strafe des (Höllen)feuers geben wird." });
                        surah8.VerseList.Add(new Verse { Number = 15, TextBosnian = "O vi koji vjerujete, kada se s nevjernicima sukobite, a njih nastupa mnogo, leđa im ne okrećite;", TextEnglish = "O you who have believed, when you meet those who disbelieve advancing [in battle], do not turn to them your backs [in flight].", TextGerman = "O die ihr glaubt, wenn ihr auf die, die ungläubig sind, trefft, während sie (zur Schlacht) anrücken, dann kehrt ihnen nicht den Rücken." });
                        surah8.VerseList.Add(new Verse { Number = 16, TextBosnian = "onaj ko im tada leđa okrene - osim onog koji se povuče s namjerom da se ponovno bori ili drugoj četi pristupi - vratit će se natovaren Allahovom srdžbom; prebivalište njegovo bit će Džehennem, a užasno je to krajnje odredište.", TextEnglish = "And whoever turns his back to them on such a day, unless swerving [as a strategy] for war or joining [another] company, has certainly returned with anger [upon him] from Allāh, and his refuge is Hell - and wretched is the destination.", TextGerman = "Wer ihnen an jenem Tag den Rücken kehrt - außer, er setzt sich ab zum Kampf, oder er schließt sich einer (anderen) Schar an -, zieht sich fürwahr Zorn von Allah zu, und sein Zufluchtsort ist die Hölle - ein schlimmer Ausgang!" });
                        surah8.VerseList.Add(new Verse { Number = 17, TextBosnian = "Njih niste ubijali vi, nego Allah; i nisi ti bacio, kad si bacio, nego je Allah bacio, da bi vjernike lijepom kušnjom iskušao -Allah je, zaista, Onaj Koji sve čuje i Onaj Koji sve zna.", TextEnglish = "And you did not kill them, but it was Allāh who killed them.[440] And you threw not, [O Muḥammad], when you threw, but it was Allāh who threw[441] that He might test the believers with a good test.[442] Indeed, Allāh is Hearing and Knowing.", TextGerman = "Nicht ihr habt sie getötet, sondern Allah hat sie getötet. Und nicht du hast geworfen, als du geworfen hast, sondern Allah hat geworfen, und damit Er die Gläubigen einer schönen Prüfung von Ihm unterziehe. Gewiß, Allah ist Allhörend und Allwissend." });
                        surah8.VerseList.Add(new Verse { Number = 18, TextBosnian = "Tako je to bilo, a doista je Allah Onaj Koji oslabljuje spletke nevjernika.", TextEnglish = "That [is so], and [also] that Allāh will weaken the plot of the disbelievers.", TextGerman = "Das (ist eure Prüfung), und (wisset), daß Allah die List der Ungläubigen abschwächt!" });
                        surah8.VerseList.Add(new Verse { Number = 19, TextBosnian = "Ako ste se molili da pobijedite - pa došla vam je, eto, pobjeda! A da se okanite, bolje bi vam bilo! I ako se ponovo vratite, Mi ćemo se ponovo vratiti, i nimalo vam neće koristiti tabor vaš, ma koliko brojan bio, a Allah je, zaista, uz vjernike.", TextEnglish = "If you [disbelievers] seek the decision [i.e., victory] - the decision [i.e., defeat] has come to you. And if you desist [from hostilities], it is best for you; but if you return [to war], We will return, and never will you be availed by your [large] company at all, even if it should increase; and [that is] because Allāh is with the believers.", TextGerman = "Wenn ihr nach einer Entscheidung sucht, so ist nunmehr die Entscheidung zu euch gekommen. Und wenn ihr aufhört, so ist es besser für euch. Aber wenn ihr (dazu) zurückkehrt, kehren Wir (auch) zurück. Und eure Schar wird euch nichts nützen, auch wenn sie zahlreich sein sollte. Und (wisset,) daß Allah mit den Gläubigen ist!" });
                        surah8.VerseList.Add(new Verse { Number = 20, TextBosnian = "O vi koji vjerujete, pokoravajte se Allahu i Njegovu Poslaniku, i ne okrećite se od njega, a čujete ga,", TextEnglish = "O you who have believed, obey Allāh and His Messenger and do not turn from him while you hear [his order].", TextGerman = "O die ihr glaubt, gehorcht Allah und Seinem Gesandten, und kehrt euch nicht von ihm ab, wo ihr doch hört!" });
                        surah8.VerseList.Add(new Verse { Number = 21, TextBosnian = "i ne budite kao oni koji su rekli: 'Čuli smo!' - a ne slušaju.", TextEnglish = "And do not be like those who say, 'We have heard,' while they do not hear.", TextGerman = "Und seid nicht wie diejenigen, die sagen: Wir hören, wo sie doch nicht hören!" });
                        surah8.VerseList.Add(new Verse { Number = 22, TextBosnian = "Doista, najgora su bića kod Allaha oni koji su gluhi i nijemi, koji ne razumiju.", TextEnglish = "Indeed, the worst of living creatures in the sight of Allāh are the deaf and dumb who do not use reason [i.e., the disbelievers].", TextGerman = "Gewiß, die schlimmsten Tiere bei Allah sind die tauben und stummen, die nicht begreifen." });
                        surah8.VerseList.Add(new Verse { Number = 23, TextBosnian = "Da Allah zna da od njih može biti ikakva dobra, učinio bi da čuju, a da je učinio i da čuju, oni bi se, opet, okrećući se od toga udaljili.", TextEnglish = "Had Allāh known any good in them, He would have made them hear. And if He had made them hear, they would [still] have turned away, while they were refusing.", TextGerman = "Wenn Allah von ihnen etwas Gutes gewußt hätte, hätte Er sie wahrlich hören lassen. Und wenn Er sie hätte hören lassen, so hätten sie sich dennoch widerstrebend abgekehrt." });
                        surah8.VerseList.Add(new Verse { Number = 24, TextBosnian = "O vi koji vjerujete, odazovite se Allahu i Poslaniku kad od vas zatraži da činite ono što vas oživljava; i neka znate da se Allah upliće između čovjeka i srca njegova, i da ćete svi pred Njim biti sakupljeni.", TextEnglish = "O you who have believed, respond to Allāh and to the Messenger when he calls you to that which gives you life. And know that Allāh intervenes between a man and his heart and that to Him you will be gathered.", TextGerman = "O die ihr glaubt, leistet Allah und dem Gesandten Folge, wenn er euch zu dem aufruft, was euch Leben gibt. Und wisset, daß Allah zwischen dem Menschen und seinem Herzen trennt und daß ihr zu Ihm versammelt werdet!" });
                        surah8.VerseList.Add(new Verse { Number = 25, TextBosnian = "I čuvajte se iskušenja koja neće pogoditi samo one među vama koji su zulum činili, i znajte da Allah strašno kažnjava.", TextEnglish = "And fear a trial[443] which will not strike those who have wronged among you exclusively, and know that Allāh is severe in penalty.", TextGerman = "Und hütet euch vor einer Versuchung, die nicht nur besonders diejenigen von euch treffen wird, die Unrecht taten. Und wisset, daß Allah streng im Bestrafen ist!" });
                        surah8.VerseList.Add(new Verse { Number = 26, TextBosnian = "I sjetite se kad vas je bilo malo, kad ste na zemlji bili potlačeni - bojali ste se da vas ljudi ne pohvataju, pa vam je On sklonište dao i Svojom pomoći vas ojačao i ljepotama vas opskrbio da biste bili zahvalni.", TextEnglish = "And remember when you were few and oppressed in the land, fearing that people might abduct you, but He sheltered you, supported you with His victory, and provided you with good things - that you might be grateful.", TextGerman = "Und gedenkt, als ihr wenige wart und auf der Erde unterdrückt wurdet und fürchtetet, daß euch die Menschen wegschnappen würden! Da hat Er euch Zuflucht gewährt, euch mit Seiner Hilfe gestärkt und euch mit (einigen von) den guten Dingen versorgt, auf daß ihr dankbar sein möget." });
                        surah8.VerseList.Add(new Verse { Number = 27, TextBosnian = "O vi koji vjerujete, ne iznevjeravajte Allaha i Poslanika i ne pronevjeravajte emanete svoje, a znate - svjesni ste da to radite.", TextEnglish = "O you who have believed, do not betray Allāh and the Messenger or betray your trusts while you know [the consequence].", TextGerman = "O die ihr glaubt, verratet nicht Allah und den Gesandten, und handelt nicht verräterisch in Bezug auf die euch anvertrauten Güter, wo ihr wisset!" });
                        surah8.VerseList.Add(new Verse { Number = 28, TextBosnian = "I neka znate da su imeci vaši i djeca vaša samo iskušenje, i da je samo u Allaha nagrada velika.", TextEnglish = "And know that your properties and your children are but a trial and that Allāh has with Him a great reward.", TextGerman = "Und wißt, daß euer Besitz und eure Kinder eine Versuchung sind und daß es bei Allah großartigen Lohn gibt!" });
                        surah8.VerseList.Add(new Verse { Number = 29, TextBosnian = "O vi koji vjerujete, ako se budete Allaha bojali, On će vam ono što istinu od neistine razdvaja dati i preko ružnih postupaka vaših će preći i oprostiti vam. A Allahova je dobrota neizmjerna.", TextEnglish = "O you who have believed, if you fear Allāh, He will grant you a criterion[444] and will remove from you your misdeeds and forgive you. And Allāh is the possessor of great bounty.", TextGerman = "O die ihr glaubt, wenn ihr Allah fürchtet, bestimmt Er euch eine Unterscheidung(snorm), tilgt euch eure bösen Taten und vergibt euch. Und Allah besitzt große Huld." });
                        surah8.VerseList.Add(new Verse { Number = 30, TextBosnian = "I kad su ti nevjernici zamke razapinjali da bi te u tamnicu bacili ili da bi te ubili, ili da bi te prognali; oni su zamke pleli pa je i Allah njima zamke pleo, a Allah najbolje zamke plete.", TextEnglish = "And [remember, O Muḥammad], when those who disbelieved plotted against you to restrain you or kill you or evict you [from Makkah]. But they plan, and Allāh plans. And Allāh is the best of planners.", TextGerman = "Und als diejenigen, die ungläubig sind, gegen dich Ränke schmiedeten, um dich festzusetzen oder zu töten oder zu vertreiben. Sie schmiedeten Ränke, und (auch) Allah schmiedete Ränke. Aber Allah ist der beste Ränkeschmied." });
                        surah8.VerseList.Add(new Verse { Number = 31, TextBosnian = "Kada im se ajeti Naši uče, govore: 'Već smo čuli! Da hoćemo, i mi bismo tako nešto rekli; to su samo izmišljotine naroda drevnih.'", TextEnglish = "And when Our verses are recited to them, they say, 'We have heard. If we willed, we could say [something] like this. This is not but legends of the former peoples.'", TextGerman = "Und wenn ihnen Unsere Zeichen verlesen werden, sagen sie: Wir haben es bereits gehört. Wenn wir wollten, könnten wir fürwahr etwas Gleichartiges sagen. Das sind nur Fabeln der Früheren." });
                        surah8.VerseList.Add(new Verse { Number = 32, TextBosnian = "A kad su oni rekli: 'Bože, ako je ovo zbilja istina Tvoja, Ti pusti na nas kamenje s neba kao kišu ili nam pošalji patnju bolnu!'", TextEnglish = "And [remember] when they said, 'O Allāh, if this should be the truth from You, then rain down upon us stones from the sky or bring us a painful punishment.'", TextGerman = "Und als sie sagten: O Allah, wenn dies tatsächlich die Wahrheit von Dir ist, dann lasse auf uns Steine vom Himmel regnen, oder bringe schmerzhafte Strafe über uns!" });
                        surah8.VerseList.Add(new Verse { Number = 33, TextBosnian = "Allah ih i nije kaznio dok si ti među njima bio; i Allah ih neće kazniti sve dok oni za oprost mole.", TextEnglish = "But Allāh would not punish them while you, [O Muḥammad], are among them, and Allāh would not punish them while they seek forgiveness.", TextGerman = "Aber Allah würde sie nimmer strafen, solange du unter ihnen bist; und Allah würde sie nimmer strafen, solange sie um Vergebung bitten." });
                        surah8.VerseList.Add(new Verse { Number = 34, TextBosnian = "A zaslužuju da ih Allah kazni kad brane drugima pristup Časnoj džamiji, a oni nisu njeni čuvari. Čuvari njeni treba da budu samo oni koji se Allaha boje, ali većina njih ne zna.", TextEnglish = "But why should Allāh not punish them while they obstruct [people] from al-Masjid al-Ḥarām and they were not [fit to be] its guardians? Its [true] guardians are not but the righteous, but most of them do not know.", TextGerman = "Und was haben sie nun, daß Allah sie nicht strafen sollte, wo sie (euch) von der geschützten Gebetsstätte abhalten? Und sie waren nicht ihre Beschützer. Ihre Beschützer sind ja nur die Gottesfürchtigen! Aber die meisten von ihnen wissen nicht." });
                        surah8.VerseList.Add(new Verse { Number = 35, TextBosnian = "Molitva njihova pored Kabe svodi se samo na zviždanje i pljeskanje rukama; zato patnju iskusite jer ne vjerujete.", TextEnglish = "And their prayer at the House [i.e., the Kaʿbah] was not except whistling and handclapping. So taste the punishment for what you disbelieved [i.e., practiced of deviations].", TextGerman = "Und ihr Gebet beim Haus ist nur Pfeifen und Klatschen. Kostet nun die Strafe dafür, daß ihr stets ungläubig wart." });
                        surah8.VerseList.Add(new Verse { Number = 36, TextBosnian = "Zaista oni koji ne vjeruju troše imovinu svoju da bi od Allahova puta odvraćali. Oni će je tako trošiti, zatim će zbog toga žaliti i na kraju će pobijeđeni biti. A oni koji ne budu vjerovali - u Džehennem će biti potjerani,", TextEnglish = "Indeed, those who disbelieve spend their wealth to avert [people] from the way of Allāh. So they will spend it; then it will be for them a [source of] regret; then they will be overcome. And those who have disbelieved - unto Hell they will be gathered.", TextGerman = "Diejenigen, die ungläubig sind, geben ihren Besitz aus, um von Allahs Weg abzuhalten. Sie werden ihn ausgeben, und hierauf wird es eine gramvolle Reue für sie sein. Hierauf werden sie besiegt werden. Und diejenigen, die ungläubig sind, werden zur Hölle versammelt werden," });
                        surah8.VerseList.Add(new Verse { Number = 37, TextBosnian = "da bi Allah dobre od nevaljalih odvojio i da bi nevaljale jedne na druge naslagao, a onda ih sve u gomilu zbio i u Džehennem bacio. Oni su baš gubitnici.", TextEnglish = "[It is] so that Allāh may distinguish the wicked from the good and place the wicked some of them upon others and heap them all together and put them into Hell. It is those who are the losers.", TextGerman = "damit Allah das Schlechte vom Guten sondere und das Schlechte übereinander tue, es alles zusammenhäufe und in die Hölle bringe. Das sind die Verlierer." });
                        surah8.VerseList.Add(new Verse { Number = 38, TextBosnian = "Reci onima koji ne vjeruju: ako se okane, bit će im oprošteno ono što je prije bilo; a ako se ne okane - pa zna se šta je s drevnim narodima bilo.", TextEnglish = "Say to those who have disbelieved [that] if they cease, what has previously occurred will be forgiven for them. But if they return [to hostility] - then the precedent of the former [rebellious] peoples has already taken place.[445]", TextGerman = "Sag zu denen, die ungläubig sind: Wenn sie aufhören, wird ihnen vergeben, was bereits vergangen ist. Wenn sie aber (dazu) zurückkehren, - so hat sich schon die Gesetzmäßigkeit an den Früheren vollzogen." });
                        surah8.VerseList.Add(new Verse { Number = 39, TextBosnian = "I borite se protiv njih dok smutnja ne iščezne i dok samo Allahova vjera ne ostane. Ako se oni okane - pa zaista Allah dobro vidi šta oni rade.", TextEnglish = "And fight against them until there is no fitnah[446] and [until] the religion [i.e., worship], all of it, is for Allāh.[447] And if they cease - then indeed, Allāh is Seeing of what they do.", TextGerman = "Und kämpft gegen sie, bis es keine Verfolgung mehr gibt und (bis) die Religion gänzlich Allahs ist. Wenn sie jedoch aufhören, so sieht Allah wohl, was sie tun." });
                        surah8.VerseList.Add(new Verse { Number = 40, TextBosnian = "A ako se okrenu, znajte da je Allah vaš zaštitnik, a divan je On Zaštitnik i divan Pomagač!", TextEnglish = "But if they turn away - then know that Allāh is your protector. Excellent is the protector, and excellent is the helper.", TextGerman = "Und wenn sie sich abkehren, so wisset, daß Allah euer Schutzherr ist. Wie trefflich ist der Schutzherr, und wie trefflich ist der Helfer!" });
                        surah8.VerseList.Add(new Verse { Number = 41, TextBosnian = "I znajte da od svega što u borbi zaplijenite jedna petina pripada Allahu i Poslaniku, i rodbini njegovoj, i siročadi, i siromasima, i putnicima - namjernicima, ako vjerujete u Allaha i u ono što smo objavili robu Našem na Dan razlučenja Istine od neistine, na dan kad su se sukobile dvije vojske - a Allah nad svime ima moć –", TextEnglish = "And know that anything you obtain of war booty - then indeed, for Allāh is one fifth of it and for the Messenger[448] and for [his] near relatives[449] and the orphans, the needy, and the [stranded] traveler,[450] if you have believed in Allāh and in that which We sent down to Our Servant[451] on the day of criterion [i.e., decisive encounter] - the day when the two armies met [at Badr]. And Allāh, over all things, is competent.", TextGerman = "Und wisset: Was immer ihr erbeutet, so gehört Allah ein Fünftel davon und dem Gesandten, und den Verwandten, den Waisen, den Armen und dem Sohn des Weges, wenn ihr an Allah glaubt und an das, was Wir auf Unseren Diener am Tag der Unterscheidung (als Offenbarung) hinabgesandt haben, an dem Tag, da die beiden Heere aufeinandertrafen. Und Allah hat zu allem die Macht." });
                        surah8.VerseList.Add(new Verse { Number = 42, TextBosnian = "kada ste vi bili u bližoj strani doline, oni u daljoj strani doline, a karavana niže vas. I da ste se dogovarali o vremenu borbe, ne biste se dogovorili, ali se ona dogodila da bi Allah dao da se ispuni ono što se moralo dogoditi, da nevjernik ostane nevjernik poslije očigledna dokaza, i da vjernik ostane vjernik poslije očigledna dokaza - a Allah je, doista, Onaj Koji sve čuje i Onaj Koji sve zna.", TextEnglish = "[Remember] when you were on the near side of the valley, and they were on the farther side, and the caravan was lower [in position] than you. If you had made an appointment [to meet], you would have missed the appointment. But [it was] so that Allāh might accomplish a matter already destined - that those who perished [through disbelief] would perish upon evidence and those who lived [in faith] would live upon evidence; and indeed, Allāh is Hearing and Knowing.", TextGerman = "Als ihr auf der näheren Talseite wart, sie auf der ferneren Talseite und die Karawane unterhalb von euch. Und wenn ihr euch verabredet hättet, wäret ihr über die Verabredung fürwahr uneinig gewesen, aber (es geschah so), damit Allah eine Angelegenheit entscheide, die ausgeführt werden sollte, damit, wer umkam, auf Grund eines klaren Beweises umkäme, und wer am Leben bliebe, auf Grund eines klaren Beweises am Leben bliebe. Allah ist wahrlich Allhörend und Allwissend." });
                        surah8.VerseList.Add(new Verse { Number = 43, TextBosnian = "Kad ti je Allah u snu pokazao da je njih malo; a da ti je pokazao da ih je mnogo, vi biste duhom klonuli i o boju se raspravljali, ali je Allah spas ukazao. On, zaista, dobro zna šta svačije grudi kriju.", TextEnglish = "[Remember, O Muḥammad], when Allāh showed them to you in your dream as few; and if He had shown them to you as many, you [believers] would have lost courage and would have disputed in the matter [of whether to fight], but Allāh saved [you from that]. Indeed, He is Knowing of that within the breasts.", TextGerman = "Als Allah sie dir in deinem Schlaf als wenige zeigte -, und wenn Er sie dir als viele gezeigt hätte, hättet ihr wahrlich den Mut verloren und über die Angelegenheit miteinander gestritten. Aber Allah hat (euch) bewahrt. Gewiß, Er weiß über das Innerste der Brüste Bescheid." });
                        surah8.VerseList.Add(new Verse { Number = 44, TextBosnian = "A kad ste se sukobili, u očima vašim On ih je prikazao u malom broju, a vas u očima njihovim također u malom broju, da bi Allah dao da se ispuni ono što se moralo dogoditi - a Allahu se sve vraća.", TextEnglish = "And [remember] when He showed them to you, when you met, as few in your eyes, and He made you [appear] as few in their eyes so that Allāh might accomplish a matter already destined. And to Allāh are [all] matters returned.", TextGerman = "Und als Er sie euch, als ihr aufeinandertraft, in euren Augen als wenige erscheinen ließ, und (auch) euch in ihren Augen weniger machte, damit Allah eine Angelegenheit entscheide, die ausgeführt werden sollte. Und zu Allah werden (all) die Angelegenheiten zurückgebracht." });
                        surah8.VerseList.Add(new Verse { Number = 45, TextBosnian = "O vi koji vjerujete, kad se s kakvom četom sukobite, smjeli budite i neprestano Allaha spominjite, da biste uspjeli,", TextEnglish = "O you who have believed, when you encounter a company [from the enemy forces], stand firm and remember Allāh much that you may be successful.", TextGerman = "O die ihr glaubt, wenn ihr auf eine Schar trefft, so steht fest und gedenkt Allahs häufig, auf daß es euch wohl ergehen möge!" });
                        surah8.VerseList.Add(new Verse { Number = 46, TextBosnian = "i pokoravajte se Allahu i Poslaniku Njegovu, i ne prepirite se da ne biste klonuli i bez borbenog duha ostali; i budite strpljivi, a Allah je, zaista, uz strpljive.", TextEnglish = "And obey Allāh and His Messenger, and do not dispute and [thus] lose courage and [then] your strength would depart; and be patient. Indeed, Allāh is with the patient.", TextGerman = "Und gehorcht Allah und Seinem Gesandten, und streitet nicht miteinander, sonst würdet ihr den Mut verlieren, und eure Kraft würde vergehen! Und seid standhaft! Gewiß, Allah ist mit den Standhaften." });
                        surah8.VerseList.Add(new Verse { Number = 47, TextBosnian = "I ne budite kao oni koji su, da se pokažu svijetu, nadmeno iz grada svoga izišli da bi od Allahova puta odvraćali, a Allah sve što rade obuhvata.", TextEnglish = "And do not be like those who came forth from their homes insolently and to be seen by people and avert [them] from the way of Allāh. And Allāh is encompassing[452] of what they do.", TextGerman = "Und seid nicht wie diejenigen, die aus ihren Wohnstätten hinauszogen, in Übermut und aus Augendienerei vor den Menschen, und die von Allahs Weg abhalten. Allah umfaßt, was sie tun." });
                        surah8.VerseList.Add(new Verse { Number = 48, TextBosnian = "Sjeti se kada im je šejtan lijepim njihove postupke predstavio i rekao: 'Niko vas danas ne može pobijediti, i ja sam, uistinu, vaš zaštitnik!' - onda je on, kad su se dva protivnička tabora sukobila, natrag uzmaknuo, i rekao: 'Ja, doista, nemam ništa s vama, ja vidim ono što vi ne vidite, i ja se bojim Allaha, a Allah strašno kažnjava.'", TextEnglish = "And [remember] when Satan made their deeds pleasing to them and said, 'No one can overcome you today from among the people, and indeed, I am your protector.' But when the two armies sighted each other, he turned on his heels and said, 'Indeed, I am disassociated from you. Indeed, I see what you do not see; indeed, I fear Allāh. And Allāh is severe in penalty.'", TextGerman = "Und als der Satan ihnen ihre Werke ausschmückte und sagte: Es gibt heute keinen unter den Menschen, der euch besiegen könnte. Und ich bin euch ein Beschützer. Als aber die beiden Scharen einander sahen, machte er auf seinen Fersen kehrt und sagte: Gewiß, ich sage mich von euch los. Ich sehe, was ihr nicht seht. Ich fürchte Allah. Und Allah ist streng im Bestrafen." });
                        surah8.VerseList.Add(new Verse { Number = 49, TextBosnian = "I sjeti se kada su licemjeri i oni čija su srca bolesna govorili: 'Ove je obmanula vjera njihova!' A onaj ko se u Allaha pouzda - pa Allah je, zaista, silan i mudar.", TextEnglish = "[Remember] when the hypocrites and those in whose hearts was disease [i.e., arrogance and disbelief] said, 'Their religion has deluded those [Muslims].' But whoever relies upon Allāh - then indeed, Allāh is Exalted in Might and Wise.", TextGerman = "Als die Heuchler und diejenigen, in deren Herzen Krankheit ist, sagten: Getäuscht hat diese da ihre Religion! Wer sich aber auf Allah verläßt, - so ist Allah Allmächtig und Allwissend." });
                        surah8.VerseList.Add(new Verse { Number = 50, TextBosnian = "A da si samo vidio kad su meleci nevjernicima duše uzimali i po licima ih njihovim i straga udarali: 'Iskusite patnju u ognju!'", TextEnglish = "And if you could but see when the angels take the souls of those who disbelieved...[453] They are striking their faces and their backs and [saying], 'Taste the punishment of the Burning Fire.", TextGerman = "Und wenn du sehen würdest, wenn die Engel diejenigen abberufen, die ungläubig sind, wobei sie sie ins Gesicht und auf den Rücken schlagen und (sagen): Kostet die Strafe des Brennens!" });
                        surah8.VerseList.Add(new Verse { Number = 51, TextBosnian = "To je za ono što ste rukama svojim pripremili, a Allah, zaista, ne čini zulum robovima Svojim!", TextEnglish = "That is for what your hands have put forth [of evil] and because Allāh is not ever unjust to [His] servants.'", TextGerman = "Dies (geschieht) wegen dessen, was eure Hände vorausgeschickt haben, und deshalb, weil Allah keiner ist, der den Menschen Unrecht zufügt." });
                        surah8.VerseList.Add(new Verse { Number = 52, TextBosnian = "Tako je bilo i s faraonovim ljudima i onima prije njih - u Allahove dokaze nisu vjerovali, pa ih je Allah zbog grijeha njihovih kaznio. Allah je, uistinu, moćan i strašno kažnjava.", TextEnglish = "[Theirs is] like the custom of the people of Pharaoh and of those before them. They disbelieved in the signs of Allāh, so Allāh seized them for their sins. Indeed, Allāh is Powerful and severe in penalty.", TextGerman = "Nach der Art der Leute Fir’auns und derjenigen vor ihnen: Sie verleugneten Allahs Zeichen, und da ergriff Allah sie für ihre Sünden. Gewiß, Allah ist Stark und streng im Bestrafen." });
                        surah8.VerseList.Add(new Verse { Number = 53, TextBosnian = "To je zato što Allah neće lišiti blagostanja narod kome je blagostanje podario - sve dok se on sam ne promijeni - a Allah je Onaj Koji sve čuje i Onaj Koji sve zna.", TextEnglish = "That is because Allāh would not change a favor which He had bestowed upon a people until they change what is within themselves. And indeed, Allāh is Hearing and Knowing.", TextGerman = "Dies, weil Allah nimmer eine Gunst, die Er einem Volk erwiesen hat, ändert, bis sie das ändern, was in ihnen selbst ist, und weil Allah Allhörend und Allwissend ist." });
                        surah8.VerseList.Add(new Verse { Number = 54, TextBosnian = "Tako je bilo s faraonovim ljudima i onima prije njih: znakove Gospodara svoga nisu priznavali, pa smo ih Mi, zbog grijeha njihovih, uništili, a faraonove smo ljude potopili - svi su oni zulumćari bili.", TextEnglish = "[Theirs is] like the custom of the people of Pharaoh and of those before them. They denied the signs of their Lord, so We destroyed them for their sins, and We drowned the people of Pharaoh. And all [of them] were wrongdoers.", TextGerman = "Nach der Art der Leute Fir’auns und derjenigen vor ihnen: Sie erklärten die Zeichen ihres Herrn für Lüge, und da vernichteten Wir sie für ihre Sünden, und Wir ließen die Leute Fir’auns ertrinken. Sie alle waren ungerecht." });
                        surah8.VerseList.Add(new Verse { Number = 55, TextBosnian = "Najgora bića kod Allaha zaista su oni koji poriču; oni ne vjeruju,", TextEnglish = "Indeed, the worst of living creatures in the sight of Allāh are those who have disbelieved, and they will not [ever] believe-", TextGerman = "Gewiß, die schlimmsten Tiere bei Allah sind die, die ungläubig sind und (auch) weiterhin nicht glauben," });
                        surah8.VerseList.Add(new Verse { Number = 56, TextBosnian = "oni s kojima ti ugovore sklapaš, pa oni svaki put, ne bojeći se Allaha, krše ugovor svoj.", TextEnglish = "The ones with whom you made a treaty but then they break their pledge every time, and they do not fear Allāh.", TextGerman = "mit denen du eine Verpflichtung eingegangen bist, die aber dann ihre Verpflichtung jedesmal brechen und nicht gottesfürchtig sind." });
                        surah8.VerseList.Add(new Verse { Number = 57, TextBosnian = "Ako se u borbi s njima sukobiš, tako ih kazni da se zbog njih razbježe oni koji su iza njih, ne bi li se prisjetili i pouku uzeli.", TextEnglish = "So if you, [O Muḥammad], gain dominance over them in war, disperse by [means of] them those behind them that perhaps they will be reminded.[454]", TextGerman = "Wenn du nun auf sie im Krieg triffst, dann verscheuche mit ihnen diejenigen, die hinter ihnen stehen, auf daß sie bedenken mögen." });
                        surah8.VerseList.Add(new Verse { Number = 58, TextBosnian = "Čim primijetiš vjerolomstvo nekog naroda, i ti njima ugovor otkaži -Allah, uistinu, ne voli vjerolomnike.", TextEnglish = "If you [have reason to] fear from a people betrVersel, throw [their treaty] back to them, [putting you] on equal terms.[455] Indeed, Allāh does not like traitors.", TextGerman = "Und wenn du dann von irgendwelchen Leuten Verrat befürchtest, so verwirf ihnen (die Verpflichtung) in gleicher Weise. Gewiß, Allah liebt nicht die Verräter." });
                        surah8.VerseList.Add(new Verse { Number = 59, TextBosnian = "I neka nikako ne misle oni koji ne vjeruju da će se spasiti; oni, doista, neće moći umaći.", TextEnglish = "And let not those who disbelieve think they will escape. Indeed, they will not cause failure [to Allāh].", TextGerman = "Und diejenigen, die ungläubig sind, sollen ja nicht meinen, sie könnten zuvorkommen. Sie werden sich (Mir) gewiß nicht entziehen." });
                        surah8.VerseList.Add(new Verse { Number = 60, TextBosnian = "I protiv njih pripremite koliko god možete snage i konja za boj, da biste time zaplašili Allahove i vaše neprijatelje, i druge osim njih - vi ih ne poznajete, Allah ih zna. Sve što na Allahovu putu potrošite bit će vam nadoknađeno, i neće vam biti učinjen zulum.", TextEnglish = "And prepare against them whatever you are able of power and of steeds of war[456] by which you may terrify the enemy of Allāh and your enemy and others besides them whom you do not know [but] whom Allāh knows. And whatever you spend in the cause of Allāh will be fully repaid to you, and you will not be wronged.", TextGerman = "Und haltet für sie bereit, was ihr an Kraft und an kampfbereiten Pferden (haben) könnt, um damit den Feinden Allahs und euren Feinden Angst zu machen, sowie anderen außer ihnen, die ihr nicht kennt; Allah aber kennt sie! Und was immer ihr auf Allahs Weg ausgebt, wird euch in vollem Maß zukommen, und es wird euch kein Unrecht zugefügt." });
                        surah8.VerseList.Add(new Verse { Number = 61, TextBosnian = "Ako oni budu skloni miru, budi i ti sklon i pouzdaj se u Allaha, a On je, uistinu, Svečujući i Sveznajući.", TextEnglish = "And if they incline to peace, then incline to it [also] and rely upon Allāh. Indeed, it is He who is the Hearing, the Knowing.", TextGerman = "Und wenn sie sich dem Frieden zuneigen, dann neige auch du dich ihm zu und verlasse dich auf Allah! Gewiß, Er ist ja der Allhörende und Allwissende." });
                        surah8.VerseList.Add(new Verse { Number = 62, TextBosnian = "A ako te htjednu prevariti - pa tebi je, doista, dovoljan Allah; On te podržava Svojom pomoći i vjernicima,", TextEnglish = "But if they intend to deceive you - then sufficient for you is Allāh. It is He who supported you with His help and with the believers", TextGerman = "Und wenn sie dich betrügen wollen - gewiß, so ist deine Genüge Allah. Er ist es, Der dich mit Seiner Hilfe und mit den Gläubigen gestärkt hat." });
                        surah8.VerseList.Add(new Verse { Number = 63, TextBosnian = "i On je sjedinio srca njihova. Da si ti potrošio sve ono što na Zemlji postoji, ti ne bi sjedinio srca njihova, ali ih je Allah sjedinio. On je zaista silan i mudar.", TextEnglish = "And brought together their hearts. If you had spent all that is in the earth, you could not have brought their hearts together; but Allāh brought them together. Indeed, He is Exalted in Might and Wise.", TextGerman = "Und Er hat ihre Herzen zusammengefügt. Wenn du alles, was auf der Erde ist, (dafür) ausgegeben hättest, hättest du ihre Herzen nicht zusammenfügen können. Aber Allah hat sie zusammengefügt. Gewiß, Er ist Allmächtig und Allweise." });
                        surah8.VerseList.Add(new Verse { Number = 64, TextBosnian = "O Vjerovjesniče, Allah je dovoljan tebi i vjernicima koji te slijede.", TextEnglish = "O Prophet, sufficient for you is Allāh and for whoever follows you of the believers.", TextGerman = "O Prophet, deine Genüge ist Allah und (auch) derer von den Gläubigen, die dir folgen." });
                        surah8.VerseList.Add(new Verse { Number = 65, TextBosnian = "O Vjerovjesniče, bodri vjernike na borbu! Ako vas bude dvadeset izdržljivih, pobijedit ćete dvije stotine; a ako vas bude stotina - pobijedit ćete hiljadu onih koji ne vjeruju, zato što su oni ljudi koji ne shvataju.", TextEnglish = "O Prophet, urge the believers to battle. If there are among you twenty [who are] steadfast, they will overcome two hundred. And if there are among you one hundred [who are steadfast], they will overcome a thousand of those who have disbelieved because they are a people who do not understand.", TextGerman = "O Prophet, sporne die Gläubigen zum Kampf an! Wenn es unter euch zwanzig Standhafte gibt, werden sie zweihundert besiegen. Und wenn es unter euch hundert gibt, werden sie Tausend von denen, die ungläubig sind, besiegen, weil sie Leute sind, die nicht verstehen." });
                        surah8.VerseList.Add(new Verse { Number = 66, TextBosnian = "Sada vam Allah daje olakšicu i zna da među vama slabosti ima. Ako vas bude stotinu izdržljivih - pobijedit ćete dvije stotine, a ako vas bude hiljadu - pobijedit ćete, Allahovom dozvolom, dvije hiljade. A Allah je uz strpljive.", TextEnglish = "Now, Allāh has lightened [the hardship] for you, and He knows that among you is weakness. So if there are from you one hundred [who are] steadfast, they will overcome two hundred. And if there are among you a thousand, they will overcome two thousand by permission of Allāh. And Allāh is with the steadfast.", TextGerman = "Jetzt (aber) hat Allah es euch leicht gemacht. Er weiß ja, daß in euch Schwaches (angelegt) ist. Wenn es nun unter euch hundert Standhafte gibt, werden sie zweihundert besiegen, und wenn es unter euch Tausend gibt, werden sie Zweitausend besiegen, mit Allahs Erlaubnis. Allah ist mit den Standhaften." });
                        surah8.VerseList.Add(new Verse { Number = 67, TextBosnian = "Nijednom vjerovjesniku nije bilo dopušteno držati sužnje dok ne izvojuje pobjedu na zemlji; vi želite prolazna dobra ovoga svijeta, a Allah želi ahiret. Doista je Allah silan i mudar.", TextEnglish = "It is not for a prophet to have captives [of war] until he inflicts a massacre [upon Allāh's enemies] in the land. You [i.e., some Muslims] desire the commodities of this world,[457] but Allāh desires [for you] the Hereafter. And Allāh is Exalted in Might and Wise.", TextGerman = "Es steht keinem Propheten zu, Gefangene zu haben, bis er (den Feind überall) im Land schwer niedergekämpft hat. Ihr wollt Glücksgüter des Diesseitigen, aber Allah will das Jenseits. Allah ist Allmächtig und Allweise." });
                        surah8.VerseList.Add(new Verse { Number = 68, TextBosnian = "Da nije ranije Allahove odredbe, snašla bi vas patnja velika zbog onoga što ste uzeli.", TextEnglish = "If not for a decree from Allāh that preceded,[458] you would have been touched for what you took by a great punishment.", TextGerman = "Wenn nicht von Allah eine früher ergangene Bestimmung wäre, würde euch für das, was ihr genommen habt, wahrlich gewaltige Strafe widerfahren." });
                        surah8.VerseList.Add(new Verse { Number = 69, TextBosnian = "Sada jedite ono što ste zaplijenili kao dopušteno i lijepo i bojte se Allaha; Allah je, zaista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "So consume what you have taken of war booty [as being] lawful and good, and fear Allāh. Indeed, Allāh is Forgiving and Merciful.", TextGerman = "Eßt nun von dem, was ihr erbeutet habt, als etwas Erlaubtes und Gutes, und fürchtet Allah! Gewiß, Allah ist Allvergebend und Barmherzig." });
                        surah8.VerseList.Add(new Verse { Number = 70, TextBosnian = "O Vjerovjesniče, reci onima koji su kao zarobljenici pali u vaše ruke: 'Ako Allah zna da u srcima vašim ima bilo šta dobro, dat će vam bolje od onoga što vam je uzeto i oprostit će vam, a Allah je Onaj Koji oprašta grijehe i milostiv je.'", TextEnglish = "O Prophet, say to whoever is in your hands of the captives, 'If Allāh knows [any] good in your hearts, He will give you [something] better than what was taken from you, and He will forgive you; and Allāh is Forgiving and Merciful.'", TextGerman = "O Prophet, sag zu denen von den Gefangenen (, die sich) in euren Händen (befinden): Wenn Allah in euren Herzen etwas Gutes weiß, wird Er euch etwas Besseres geben als das, was euch genommen worden ist, und wird euch vergeben. Allah ist Allvergebend und Barmherzig." });
                        surah8.VerseList.Add(new Verse { Number = 71, TextBosnian = "A ako htjednu da te prevare, pa - oni su i prije Allaha nastojali varati i zato ti je On omogućio da ih pobijediš; a Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "But if they intend to betray you - then they have already betrayed Allāh before, and He empowered [you] over them. And Allāh is Knowing and Wise.", TextGerman = "Und wenn sie dich verraten wollen, so haben sie schon zuvor Allah verraten, und dann hat Er (euch) Macht über sie gegeben. Allah ist Allwissend und Allweise." });
                        surah8.VerseList.Add(new Verse { Number = 72, TextBosnian = "Zaista oni koji vjeruju i iseljavaju se, i u borbi na Allahovu putu zalažu imetke svoje i živote svoje, i oni koji daju utočište i pomažu, oni su jedni drugima zaštitnici. A onima koji vjeruju, a koji se nisu iselili, vi ne možete, sve dok se ne isele, zaštitnici biti. A ako vas zamole da im u vjeri pomognete, dužni ste im u pomoć priteći, osim protiv naroda s kojim o nenapadanju zaključen ugovor imate. A Allah dobro vidi ono što radite.", TextEnglish = "Indeed, those who have believed and emigrated and fought with their wealth and lives in the cause of Allāh and those who gave shelter and aided - they are allies of one another. But those who believed and did not emigrate - for you there is no support of them until they emigrate. And if they seek help of you for the religion, then you must help, except against a people between yourselves and whom is a treaty. And Allāh is Seeing of what you do.", TextGerman = "Gewiß, diejenigen, die glauben und ausgewandert sind und sich mit ihrem Besitz und ihrer eigenen Person auf Allahs Weg abgemüht haben, und diejenigen, die (jenen) Zuflucht gewährt und (ihnen) geholfen haben, sie sind einer des anderen Schutzherren. Zu denjenigen aber, die glauben und nicht ausgewandert sind, habt ihr kein Schutzverhältnis, bis sie auswandern. Wenn sie euch jedoch um der (euch gemeinsamen) Religion willen um Hilfe bitten, dann obliegt euch die Hilfe, außer gegen Leute, zwischen euch und denen ein Abkommen besteht. Und was ihr tut, sieht Allah wohl." });
                        surah8.VerseList.Add(new Verse { Number = 73, TextBosnian = "Nevjernici su jedni drugima zaštitnici. Ne postupite li tako, nastat će smutnja na Zemlji i nered veliki.", TextEnglish = "And those who disbelieved are allies of one another. If you do not do so [i.e., ally yourselves with other believers], there will be fitnah [i.e., disbelief and oppression] on earth and great corruption.", TextGerman = "Und diejenigen, die ungläubig sind, sind einer des anderen Schutzherren. - Wenn ihr es nicht tut, wird es im Land Aufruhr und großes Unheil geben." });
                        surah8.VerseList.Add(new Verse { Number = 74, TextBosnian = "Oni koji vjeruju i isele se, i bore se na Allahovu putu, i oni koji daju sklonište i pomažu - oni su zbilja pravi vjernici - njih čeka oprost i opskrba divna.", TextEnglish = "But those who have believed and emigrated and fought in the cause of Allāh and those who gave shelter and aided - it is they who are the believers, truly. For them is forgiveness and noble provision.", TextGerman = "Und diejenigen, die glauben und ausgewandert sind und sich auf Allahs Weg abgemüht haben, und diejenigen, die (jenen) Zuflucht gewährt und geholfen haben, das sind die wahren Gläubigen. Für sie gibt es Vergebung und ehrenvolle Versorgung." });
                        surah8.VerseList.Add(new Verse { Number = 75, TextBosnian = "A oni koji kasnije vjernici postanu, pa se isele i u borbi zajedno s vama učestvuju - oni su vaši. A rođaci su, prema Allahovoj Knjizi, jedni drugima preči. Allah, zaista, sve dobro zna.", TextEnglish = "And those who believed after [the initial emigration] and emigrated and fought with you - they are of you. But those of [blood] relationship are more entitled [to inheritance] in the decree of Allāh.[459] Indeed, Allāh is Knowing of all things.", TextGerman = "Und diejenigen, die danach geglaubt haben und ausgewandert sind und sich mit euch abgemüht haben, sie gehören zu euch. Aber die Blutsverwandten stehen einander am nächsten; (dies steht) im Buch Allahs. Gewiß, Allah weiß über alles Bescheid." });
                        result = surah8;
                        break;
                    #endregion

                    // 
                    #region 9 At-Tawbah
                    case 9:
                        Surah surah9 = new Surah(); surah9.Name = Constants.Surah9Name; surah9.Number = 9; surah9.VerseList = new List<Verse>();
                        surah9.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah9.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ovo je obznana o prestanku zaštite, od Allaha i Njegova Poslanika, onim višebošcima s kojima ste zaključili ugovore.", TextEnglish = "[This is a declaration of] disassociation, from Allāh and His Messenger, to those with whom you had made a treaty among the polytheists.[461]", TextGerman = "Eine Lossagung von Seiten Allahs und Seines Gesandten an diejenigen unter den Götzendienern, mit denen ihr einen Vertrag abgeschlossen habt." });
                        surah9.VerseList.Add(new Verse { Number = 2, TextBosnian = "Putujte po svijetu još četiri mjeseca, ali znajte da Allahu nećete umaći i da će Allah nevjernike osramotiti.", TextEnglish = "So travel freely, [O disbelievers], throughout the land [during] four months but know that you cannot cause failure to Allāh and that Allāh will disgrace the disbelievers.", TextGerman = "Nun zieht im Land vier Monate umher und wißt, daß ihr euch Allah nicht entziehen könnt, und daß Allah die Ungläubigen in Schande stürzt!" });
                        surah9.VerseList.Add(new Verse { Number = 3, TextBosnian = "Ovo je i proglas od Allaha i Njegova Poslanika ljudima na najveći dan hadža, da Allah i Njegov Poslanik nemaju ništa s višebošcima, pa ako se pokajete - to je za vas bolje a ako se okrenete znajte da Allahu nećete umaći. A nevjernicima navijesti patnju bolnu.", TextEnglish = "And [it is] an announcement from Allāh and His Messenger to the people on the day of the greater pilgrimage[462] that Allāh is disassociated from the disbelievers, and [so is] His Messenger. So if you repent, that is best for you; but if you turn away - then know that you will not cause failure to Allāh.[463] And give tidings to those who disbelieve of a painful punishment.", TextGerman = "Und eine Bekanntmachung von Seiten Allahs und Seines Gesandten an die Menschen am Tag der großen Pilgerfahrt, daß Allah der (Verpflichtung gegenüber den) Götzendiener(n) ledig ist, und auch Sein Gesandter. Wenn ihr nun bereut, so ist es besser für euch. Wenn ihr euch jedoch abkehrt, so wißt, daß ihr euch Allah nicht entziehen könnt! Und verkünde denen, die ungläubig sind, schmerzhafte Strafe!" });
                        surah9.VerseList.Add(new Verse { Number = 4, TextBosnian = "Izuzetak su višebošci s kojima imate zaključene ugovore koje oni nisu ni u čemu povrijedili, niti su ikoga protiv vas pomagali, ispoštujte ugovore s njima do dogovorenog roka; Allah zaista voli bogobojazne.", TextEnglish = "Excepted are those with whom you made a treaty among the polytheists and then they have not been deficient toward you in anything or supported anyone against you; so complete for them their treaty until their term [has ended]. Indeed, Allāh loves the righteous [who fear Him].", TextGerman = "Mit Ausnahme derer von den Götzendienern, mit denen ihr einen Vertrag abgeschlossen habt und die (es) euch (in) nichts haben fehlen lassen und niemandem gegen euch beigestanden haben. So erfüllt ihnen gegenüber ihren Vertrag bis zu der ihnen eingeräumten Frist! Gewiß, Allah liebt die Gottesfürchtigen." });
                        surah9.VerseList.Add(new Verse { Number = 5, TextBosnian = "Kada prođu sveti mjeseci, onda ubijajte mušrike gdje god ih nađete, zarobljavajte ih, opsjedajte i na svakom prolazu dočekujte. Pa ako se pokaju i budu namaz obavljali i zekat davali, ostavite ih na miru, a Allah je, zaista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And when the inviolable months[464] have passed, then kill the polytheists wherever you find them and capture them and besiege them and sit in wait for them at every place of ambush. But if they should repent, establish prayer, and give zakāh, let them [go] on their way. Indeed, Allāh is Forgiving and Merciful.", TextGerman = "Wenn nun die Schutzmonate abgelaufen sind, dann tötet die Götzendiener, wo immer ihr sie findet, ergreift sie, belagert sie und lauert ihnen aus jedem Hinterhalt auf! Wenn sie aber bereuen, das Gebet verrichten und die Abgabe entrichten, dann laßt sie ihres Weges ziehen! Gewiß, Allah ist Allvergebend und Barmherzig." });
                        surah9.VerseList.Add(new Verse { Number = 6, TextBosnian = "Ako te neki višebožac zamoli za zaštitu, ti ga zaštiti da bi saslušao Allahove riječi, a potom ga otpremi na mjesto sigurno za njega. To je zato što su oni narod koji ne zna.", TextEnglish = "And if any one of the polytheists seeks your protection, then grant him protection so that he may hear the words of Allāh [i.e., the Qur’ān]. Then deliver him to his place of safety. That is because they are a people who do not know.", TextGerman = "Und wenn jemand von den Götzendienern dich um Schutz bittet, dann gewähre ihm Schutz, bis er das Wort Allahs hört. Hierauf lasse ihn den Ort erreichen, wo er in Sicherheit ist. Dies, weil sie Leute sind, die nicht Bescheid wissen." });
                        surah9.VerseList.Add(new Verse { Number = 7, TextBosnian = "Kako višebožci mogu imati ugovor s Allahom i Poslanikom Njegovim?! Mogu imati samo oni s kojima ste ugovor kod Časne džamije zaključili; sve dok se oni budu ugovora pridržavali, pridržavajte se i vi, a Allah, zaista, voli bogobojazne.", TextEnglish = "How can there be for the polytheists a treaty in the sight of Allāh and with His Messenger, except for those with whom you made a treaty at al-Masjid al-Ḥarām? So as long as they are upright toward you,[465] be upright toward them. Indeed, Allāh loves the righteous [who fear Him].", TextGerman = "Wie sollte es denn für die Götzendiener bei Allah und bei Seinem Gesandten einen Vertrag geben, außer (für) diejenigen, mit denen ihr bei der geschützten Gebetsstätte einen Vertrag abgeschlossen habt? Solange sie sich euch gegenüber recht verhalten, verhaltet (auch) ihr euch ihnen gegenüber recht. Gewiß, Allah liebt die Gottesfürchtigen." });
                        surah9.VerseList.Add(new Verse { Number = 8, TextBosnian = "Kako, kada oni, ako bi vas pobijedili, ne bi, kad ste vi u pitanju, ni srodstvo ni sporazum poštivali? Oni vam se govorom usta svojih umiljavaju, ali im se srca protive; većina njih su buntovnici.", TextEnglish = "How [can there be a treaty] while, if they gain dominance over you, they do not observe concerning you any pact of kinship or covenant of protection? They satisfy you with their mouths, but their hearts refuse [compliance], and most of them are defiantly disobedient.", TextGerman = "Wie (sollte es einen Vertrag geben), wo sie doch, wenn sie die Oberhand über euch bekommen, euch gegenüber weder Verwandtschaftsbande noch (Schutz)vertrag beachten? Sie stellen euch mit ihren Mündern zufrieden, aber ihre Herzen weigern sich. Und die meisten von ihnen sind Frevler." });
                        surah9.VerseList.Add(new Verse { Number = 9, TextBosnian = "Oni Allahove ajete za ono što malo vrijedi zamjenjuju, pa od Puta Njegova odvraćaju, zaista je ružno kako postupaju.", TextEnglish = "They have exchanged the signs of Allāh for a small price and averted [people] from His way. Indeed, it was evil that they were doing.", TextGerman = "Sie haben die Zeichen Allahs für einen geringen Preis verkauft und damit von Seinem Weg abgehalten. Gewiß, wie böse ist, was sie zu tun pflegten." });
                        surah9.VerseList.Add(new Verse { Number = 10, TextBosnian = "Ni srodstvo ni sporazum, kada je vjernik u pitanju, ne poštuju, i oni su baš prestupnici.", TextEnglish = "They do not observe toward a believer any pact of kinship or covenant of protection. And it is they who are the transgressors.", TextGerman = "Sie beachten gegenüber einem Gläubigen weder Verwandtschaftsbande noch (Schutz)vertrag. Das sind die Übertreter." });
                        surah9.VerseList.Add(new Verse { Number = 11, TextBosnian = "Ali ako se oni budu pokajali i namaz obavljali i zekat davali, braća su vam po vjeri - a Mi ajete i znakove objašnjavamo ljudima koji znaju.", TextEnglish = "But if they repent, establish prayer, and give zakāh, then they are your brothers in religion; and We detail the verses for a people who know.", TextGerman = "Wenn sie aber bereuen, das Gebet verrichten und die Abgabe entrichten, dann sind sie eure Brüder in der Religion. Wir legen die Zeichen ausführlich dar für Leute, die Bescheid wissen." });
                        surah9.VerseList.Add(new Verse { Number = 12, TextBosnian = "A ako prekrše zakletve nakon što s njima ugovore sklopite, i ako vjeru vašu budu vrijeđali, onda se borite protiv kolovođa nevjerovanja - za njih, doista, ne postoje zakletve - ne bi li se okanili.", TextEnglish = "And if they break their oaths after their treaty and defame your religion, then combat the leaders of disbelief, for indeed, there are no oaths [sacred] to them; [fight them that] they might cease.", TextGerman = "Wenn sie ihre Eide aber nach Vertragsabschluß brechen und eure Religion schmähen, dann kämpft gegen die Anführer des Unglaubens - für sie gibt es ja keine Eide -, auf daß sie aufhören mögen." });
                        surah9.VerseList.Add(new Verse { Number = 13, TextBosnian = "Zar se nećete boriti protiv ljudi koji su zakletve svoje prekršili, Poslanika nastojali protjerati i prvi vas napali? Zar ih se bojite? Preče je da se Allaha bojite ako ste vjernici.", TextEnglish = "Would you not fight against a people who broke their oaths and determined to expel the Messenger, and they had begun [the attack upon] you the first time? Do you fear them? But Allāh has more right that you should fear Him, if you are [truly] believers.", TextGerman = "Wollt ihr nicht gegen Leute kämpfen, die ihre Eide gebrochen haben und vorhatten, den Gesandten zu vertreiben, wobei sie zuerst gegen euch (mit Feindseligkeiten) anfingen? Fürchtet ihr sie? Aber Allah hat ein größeres Anrecht darauf, daß ihr Ihn fürchtet, wenn ihr gläubig seid." });
                        surah9.VerseList.Add(new Verse { Number = 14, TextBosnian = "Borite se protiv njih, Allah će ih rukama vašim mučiti i poniziti, a vas će protiv njih pomoći i grudi vjernika zaliječiti,", TextEnglish = "Fight them; Allāh will punish them by your hands and will disgrace them and give you victory over them and satisfy the breasts [i.e., desires] of a believing people", TextGerman = "Kämpft gegen sie! Allah wird sie durch eure Hände strafen, sie in Schande stürzen, euch zum Sieg über sie verhelfen und die Brüste von gläubigen Leuten heilen" });
                        surah9.VerseList.Add(new Verse { Number = 15, TextBosnian = "i iz srca njihovih srdžbu odstraniti. A Allah će onome kome On hoće pokajanje primiti; Allah je Onaj Koji sve čuje i mudar je.", TextEnglish = "And remove the fury in their [i.e., the believers'] hearts. And Allāh turns in forgiveness to whom He wills; and Allāh is Knowing and Wise.", TextGerman = "und den Grimm in ihren Herzen vergehen lassen. Allah nimmt die Reue an, von wem Er will. Allah ist Allwissend und Allweise." });
                        surah9.VerseList.Add(new Verse { Number = 16, TextBosnian = "Zar mislite da ćete biti ostavljeni, a da Allah ne zna one među vama koji se bore i koji umjesto Allaha i Poslanika Njegova i vjernika nisu uzeli nikoga za prisna prijatelja?! A Allah upotpunosti zna ono što radite.", TextEnglish = "Do you think that you will be left [as you are] while Allāh has not yet made evident those among you who strive [for His cause] and do not take other than Allāh, His Messenger and the believers as intimates? And Allāh is [fully] Aware of what you do.", TextGerman = "Oder meint ihr etwa, daß ihr (in Ruhe) gelassen werdet, ohne daß Allah zuvor diejenigen kenne, die von euch sich abgemüht und außer Allah, Seinem Gesandten und den Gläubigen keinen Vertrauten genommen haben? Und Allah ist Kundig dessen, was ihr tut." });
                        surah9.VerseList.Add(new Verse { Number = 17, TextBosnian = "Višebošci nisu dostojni Allahove džamije održavati, a sami priznaju da su nevjernici. Djela njihova su poništena i u Vatri će vječno ostati.", TextEnglish = "It is not for the polytheists to maintain the mosques of Allāh [while] witnessing against themselves with disbelief. [For] those, their deeds have become worthless, and in the Fire they will abide eternally.", TextGerman = "Es steht den Götzendienern nicht zu, Allahs Gebetsstätten zu bevölkern, wo sie gegen sich selbst Zeugnis ablegen durch den Unglauben. Deren Werke werden hinfällig, und im (Höllen)feuer werden sie ewig bleiben." });
                        surah9.VerseList.Add(new Verse { Number = 18, TextBosnian = "Allahove džamije održavaju oni koji u Allaha i u ahiret vjeruju i koji namaz obavljaju i zekat daju i koji se nikoga osim Allaha ne boje, oni su na Pravom putu.", TextEnglish = "The mosques of Allāh are only to be maintained by those who believe in Allāh and the Last Day and establish prayer and give zakāh and do not fear except Allāh, for it is expected that those will be of the [rightly] guided.", TextGerman = "Gewiß, Allahs Gebetsstätten bevölkert nur, wer an Allah und den Jüngsten Tag glaubt, das Gebet verrichtet, die Abgabe entrichtet und niemanden außer Allah fürchtet. Diese aber werden vielleicht zu den Rechtgeleiteten gehören." });
                        surah9.VerseList.Add(new Verse { Number = 19, TextBosnian = "Zar smatrate da je napajanje hadžija vodom i održavanje Časne džamije poput vrijednosti svakog onog koji vjeruje u Allaha i u ahiret i koji se na Allahovu putu bori? Nisu oni jednaki pred Allahom, a Allah neće uputiti ljude zulumćare.", TextEnglish = "Have you made the providing of water for the pilgrim and the maintenance of al-Masjid al-Ḥarām equal to [the deeds of] one who believes in Allāh and the Last Day and strives in the cause of Allāh? They are not equal in the sight of Allāh. And Allāh does not guide the wrongdoing people.", TextGerman = "Stellt ihr etwa die Tränkung der Pilger und das Bevölkern der geschützten Gebetsstätte (den Werken) dessen gleich, der an Allah und den Jüngsten Tag glaubt und sich auf Allahs Weg abmüht? Sie sind nicht gleich bei Allah. Und Allah leitet das ungerechte Volk nicht recht." });
                        surah9.VerseList.Add(new Verse { Number = 20, TextBosnian = "Oni koji vjeruju, i koji se iseljavaju, i koji se na Allahovu putu bore, zalažući imetke svoje i živote svoje na većem su stepenu u Allaha; oni su baš uspjeli.", TextEnglish = "The ones who have believed, emigrated and striven in the cause of Allāh with their wealth and their lives are greater in rank in the sight of Allāh. And it is those who are the attainers [of success].", TextGerman = "Diejenigen, die glauben und ausgewandert sind und sich auf Allahs Weg mit ihrem Besitz und ihrer eigenen Person abgemüht haben, haben einen größeren Vorzug bei Allah. Das sind die Erfolgreichen." });
                        surah9.VerseList.Add(new Verse { Number = 21, TextBosnian = "Gospodar njihov im šalje radosni navještaj o milosti Njegovoj i zadovoljstvu, i o džennetskim baščama u kojima će neprekidno uživati.", TextEnglish = "Their Lord gives them good tidings of mercy from Him and approval and of gardens for them wherein is enduring pleasure.", TextGerman = "Ihr Herr verkündet ihnen Barmherzigkeit von sich und Wohlgefallen und Gärten, in denen sie beständige Wonne haben," });
                        surah9.VerseList.Add(new Verse { Number = 22, TextBosnian = "Vječno će u njima boraviti. Uistinu, u Allaha je nagrada velika.", TextEnglish = "[They will be] abiding therein forever. Indeed, Allāh has with Him a great reward.", TextGerman = "ewig und auf immer darin zu bleiben. Gewiß, bei Allah gibt es großartigen Lohn." });
                        surah9.VerseList.Add(new Verse { Number = 23, TextBosnian = "O vi koji vjerujete, ne uzimajte za prisne prijatelje ni očeve svoje ni braću svoju, ako više vole nevjerovanje od vjerovanja. A ko god od vas bude s njima prisno prijateljevao, pa takvi su pravi zulumćari.", TextEnglish = "O you who have believed, do not take your fathers or your brothers as allies if they have preferred disbelief over belief. And whoever does so among you - then it is those who are the wrongdoers.", TextGerman = "O die ihr glaubt, nehmt nicht eure Väter und eure Brüder zu Schutzherren, wenn sie den Unglauben mehr lieben als den Glauben! Wer von euch sie zu Vertrauten nimmt, das sind die Ungerechten." });
                        surah9.VerseList.Add(new Verse { Number = 24, TextBosnian = "Reci: 'Ako su vam očevi vaši, i sinovi vaši, i braća vaša, i žene vaše, i rod vaš, i imanja vaša koja ste stekli, i trgovačka roba za koju strahujete da neće prođe imati, i kuće vaše u kojima se prijatno osjećate - miliji od Allaha i Poslanika, i od borbe na Njegovom putu, onda pričekajte dok Allah Svoju odluku ne donese. A Allah neće uputiti ljude buntovnike.'", TextEnglish = "Say, [O Muḥammad], 'If your fathers, your sons, your brothers, your wives, your relatives, wealth which you have obtained, commerce wherein you fear decline, and dwellings with which you are pleased are more beloved to you than Allāh and His Messenger and jihād [i.e., striving] in His cause, then wait until Allāh executes His command. And Allāh does not guide the defiantly disobedient people.'", TextGerman = "Sag: Wenn eure Väter, eure Söhne, eure Brüder, eure Gattinnen und eure Sippemitglieder, Besitz, den ihr erworben habt, Handel, dessen Niedergang ihr fürchtet, und Wohnungen, an denen ihr Gefallen findet, euch lieber sind als Allah und Sein Gesandter und das Abmühen auf Seinem Weg, dann wartet ab, bis Allah mit Seiner Anordnung kommt! Allah leitet das Volk der Frevler nicht recht." });
                        surah9.VerseList.Add(new Verse { Number = 25, TextBosnian = "Allah vas je na mnogim bojištima pomagao, a i onoga dana na Hunejnu kad vas je mnoštvo vaše zanijelo, ali vam ono nije ni od kakve koristi bilo, nego vam je zemlja, koliko god da je bila prostrana, tijesna postala, pa ste se u bijeg dali.", TextEnglish = "Allāh has already given you victory in many regions and [even] on the day of Ḥunayn, when your great number pleased you, but it did not avail you at all, and the earth was confining for you with [i.e., in spite of] its vastness; then you turned back, fleeing.", TextGerman = "Allah hat euch doch an vielen Orten zum Sieg verholfen, und auch am Tag von Hunain, als eure große Zahl euch gefiel, euch aber nichts nutzte. Die Erde wurde euch eng bei all ihrer Weite. Hierauf kehrtet ihr den Rücken (zur Flucht)." });
                        surah9.VerseList.Add(new Verse { Number = 26, TextBosnian = "Zatim je Allah na Poslanika Svoga i na vjernike smiraj Svoj spustio, i vojske koje vi niste vidjeli poslao, i one koji nisu vjerovali na muke stavio; to je bila kazna za nevjernike.", TextEnglish = "Then Allāh sent down His tranquility upon His Messenger and upon the believers and sent down soldiers [i.e., angels] whom you did not see and punished those who disbelieved. And that is the recompense of the disbelievers.", TextGerman = "Daraufhin sandte Allah Seine innere Ruhe auf Seinen Gesandten und auf die Gläubigen herab, und Er sandte Heerscharen, die ihr nicht saht, herab und strafte diejenigen, die ungläubig waren. Das war der Lohn der Ungläubigen." });
                        surah9.VerseList.Add(new Verse { Number = 27, TextBosnian = "Allah će poslije toga onome kome hoće pokajanje primiti, a Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "Then Allāh will accept repentance after that for whom He wills; and Allāh is Forgiving and Merciful.", TextGerman = "Hierauf nimmt Allah nach alledem die Reue an, von wem Er will. Und Allah ist Allvergebend und Barmherzig." });
                        surah9.VerseList.Add(new Verse { Number = 28, TextBosnian = "O vi koji vjerujete, višebošci su sama pogan i neka se, nakon ove godine, više ne približuju Časnoj džamiji. A ako se bojite oskudice pa Allah će vas, ako hoće, iz obilja Svoga imućnim učiniti. Allah je, zaista, Onaj Koji sve zna i mudar je.", TextEnglish = "O you who have believed, indeed the polytheists are unclean, so let them not approach al-Masjid al-Ḥarām after this, their [final] year. And if you fear privation, Allāh will enrich you from His bounty if He wills. Indeed, Allāh is Knowing and Wise.", TextGerman = "O die ihr glaubt, die Götzendiener sind fürwahr unrein, so sollen sie sich der geschützten Gebetsstätte nach diesem, ihrem Jahr nicht mehr nähern! Und wenn ihr (deshalb) Armut befürchtet, so wird Allah euch durch Seine Huld reich machen, wenn Er will. Gewiß, Allah ist Allwissend und Allweise." });
                        surah9.VerseList.Add(new Verse { Number = 29, TextBosnian = "Borite se protiv onih kojima je data Knjiga, a koji ne vjeruju ni u Allaha ni u ahiret, ne smatraju zabranjenim ono što Allah i Njegov Poslanik zabranjuju i ne ispovijedaju istinsku vjeru -sve dok ne daju glavarinu poslušno i pri tom budu ponizni.", TextEnglish = "Fight against those who do not believe in Allāh or in the Last Day and who do not consider unlawful what Allāh and His Messenger have made unlawful and who do not adopt the religion of truth [i.e., Islām] from those who were given the Scripture - [fight] until they give the jizyah[466] willingly while they are humbled.", TextGerman = "Kämpft gegen diejenigen, die nicht an Allah und nicht an den Jüngsten Tag glauben und nicht verbieten, was Allah und Sein Gesandter verboten haben, und nicht die Religion der Wahrheit befolgen - von denjenigen, denen die Schrift gegeben wurde -, bis sie den Tribut aus der Hand entrichten und gefügig sind!" });
                        surah9.VerseList.Add(new Verse { Number = 30, TextBosnian = "Jevreji govore: 'Uzejr je Allahov sin', a kršćani kažu: 'Mesih je Allahov sin.' To su riječi njihove, iz usta njihovih, oponašaju riječi nevjernika prijašnjih- ubio ih Allah! Kuda se odmeću?", TextEnglish = "The Jews say, 'Ezra is the son of Allāh'; and the Christians say, 'The Messiah is the son of Allāh.' That is their statement from their mouths; they imitate the saying of those who disbelieved before [them]. May Allāh destroy them; how are they deluded?", TextGerman = "Die Juden sagen: ’Uzair ist Allahs Sohn, und die Christen sagen: Al-Masih ist Allahs Sohn. Das sind ihre Worte aus ihren (eigenen) Mündern. Sie führen ähnliche Worte wie diejenigen, die zuvor ungläubig waren. Allah bekämpfe sie! Wie sie sich (doch) abwendig machen lassen!" });
                        surah9.VerseList.Add(new Verse { Number = 31, TextBosnian = "Oni, pored Allaha, bogovima smatraju svećenike svoje, i monahe svoje i Mesiha, sina Merjemina. A naređeno im je da samo jednom Bogu ibadet čine - nema boga osim Njega. Uzvišen je On naspram onih koje Njemu u obožavanju pridružuju.", TextEnglish = "They have taken their scholars and monks as lords besides Allāh,[467] and [also] the Messiah, the son of Mary.[468] And they were not commanded except to worship one God; there is no deity except Him. Exalted is He above whatever they associate with Him.", TextGerman = "Sie haben ihre Gelehrten und ihre Mönche zu Herren genommen außer Allah, sowie al-Masih ibn Maryam, wo ihnen doch nur befohlen worden ist, einem einzigen Gott zu dienen. Es gibt keinen Gott außer Ihm. Preis sei Ihm! (Erhaben ist Er) über das, was sie (Ihm) beigesellen." });
                        surah9.VerseList.Add(new Verse { Number = 32, TextBosnian = "Oni žele ustima svojim utrnuti Allahovo svjetlo, a Allah će, svakako, upotpuniti svjetlo Svoje, makar to mrzili nevjernici.", TextEnglish = "They want to extinguish the light of Allāh with their mouths, but Allāh refuses except to perfect His light, although the disbelievers dislike it.", TextGerman = "Sie wollen Allahs Licht mit ihren Mündern auslöschen. Aber Allah besteht darauf, Sein Licht doch zu vollenden, auch wenn es den Ungläubigen zuwider ist." });
                        surah9.VerseList.Add(new Verse { Number = 33, TextBosnian = "On je poslao Poslanika Svoga s uputom i pravom vjerom, da bi je uzdigao iznad svih vjera, makar to mrzili mušrici.", TextEnglish = "It is He who has sent His Messenger with guidance and the religion of truth to manifest it over all religion, although they who associate others with Allāh dislike it.", TextGerman = "Er ist es, Der Seinen Gesandten mit der Rechtleitung und der Religion der Wahrheit gesandt hat, um ihr die Oberhand über alle Religion zu geben, auch wenn es den Götzendienern zuwider ist." });
                        surah9.VerseList.Add(new Verse { Number = 34, TextBosnian = "O vi koji vjerujete, mnogi svećenici i monasi, doista, na nedozvoljen način tuđu imovinu jedu i od Allahova puta odvraćaju. Onima koji zlato i srebro gomilaju i ne troše ga na Allahovu putu - navijesti bolnu patnju.", TextEnglish = "O you who have believed, indeed many of the scholars and the monks devour the wealth of people unjustly[469] and avert [them] from the way of Allāh. And those who hoard gold and silver and spend it not in the way of Allāh - give them tidings of a painful punishment.", TextGerman = "O die ihr glaubt, viele von den Gelehrten und den Mönchen verschlingen fürwahr den Besitz der Menschen auf nichtige Weise und halten von Allahs Weg ab. Diejenigen, die Gold und Silber horten und es nicht auf Allahs Weg ausgeben, denen verkünde schmerzhafte Strafe," });
                        surah9.VerseList.Add(new Verse { Number = 35, TextBosnian = "Na dan kad se ono u vatri džehennemskoj bude usijalo pa njime čela njihova i slabine njihove i leđa njihova budu žigosana: 'Ovo je ono što ste za sebe zgrtali, iskusite zato kaznu za ono što ste gomilali.'", TextEnglish = "The Day when it[470] will be heated in the fire of Hell and seared therewith will be their foreheads, their flanks, and their backs, [it will be said], 'This is what you hoarded for yourselves, so taste what you used to hoard.'", TextGerman = "am Tag, da im Feuer der Hölle darüber heiß gemacht wird und damit ihre Stirnen, ihre Seiten und ihre Rücken gebrandmarkt werden: Dies ist, was ihr für euch selbst gehortet habt. Nun kostet, was ihr zu horten pflegtet!" });
                        surah9.VerseList.Add(new Verse { Number = 36, TextBosnian = "Broj mjeseci u Allaha, zaista, je dvanaest, prema Allahovoj Knjizi, od dana kada je nebesa i Zemlju stvorio, a četiri su sveta. To je prava vjera! U njima sebi zulum ne činite! A borite se svi protiv mušrika kao što se svi oni bore protiv vas, i znajte da je Allah uz bogobojazne.", TextEnglish = "Indeed, the number of months with Allāh is twelve [lunar] months in the register of Allāh [from] the day He created the heavens and the earth; of these, four are sacred.[471] That is the correct religion [i.e., way], so do not wrong yourselves during them.[472] And fight against the disbelievers collectively as they fight against you collectively. And know that Allāh is with the righteous [who fear Him].", TextGerman = "Gewiß, die Anzahl der Monate bei Allah ist zwölf Monate, im Buch Allahs (festgelegt) am Tag, da Er die Himmel und die Erde schuf. Davon sind vier geschützt. Das ist die richtige Religion. So fügt euch selbst in ihnen kein Unrecht zu. Und kämpft gegen die Götzendiener allesamt wie sie gegen euch allesamt kämpfen! Und wißt, daß Allah mit den Gottesfürchtigen ist!" });
                        surah9.VerseList.Add(new Verse { Number = 37, TextBosnian = "Premještanje svetih mjeseci samo je dodatno nevjerovanje, čime oni koji ne vjeruju bivaju u zabludu dovedeni; jedne godine ga proglašavaju običnim, a druge godine ga smatraju svetim, da bi ispunili broj mjeseci koje je Allah učinio svetim, pa drže običnim one koje je Allah učinio svetim, ružni postupci njihovi predstavljeni su im kao lijepi. A Allah neće uputiti ljude nevjernike.", TextEnglish = "Indeed, the postponing [of restriction within sacred months] is an increase in disbelief by which those who have disbelieved are led [further] astray. They make it[473] lawful one year and unlawful another year to correspond to the number made unlawful by Allāh[474] and [thus] make lawful what Allāh has made unlawful. Made pleasing to them is the evil of their deeds; and Allāh does not guide the disbelieving people.", TextGerman = "Das Verschieben eines Monats ist nur eine Mehrung des Unglaubens. Damit werden diejenigen, die ungläubig sind, in die Irre geführt; sie erklären ihn in einem Jahr für ungeschützt und in einem (anderen) Jahr für geschützt, um der Anzahl dessen gleichzukommen, was Allah für geschützt erklärt hat; so erklären sie für ungeschützt, was Allah für geschützt erklärt hat. Ihre bösen Taten sind ihnen ausgeschmückt worden. Allah leitet das ungläubige Volk nicht recht." });
                        surah9.VerseList.Add(new Verse { Number = 38, TextBosnian = "O vi koji vjerujete, zašto su neki od vas oklijevali kad vam je bilo rečeno: 'Krenite u borbu na Allahovu putu!', kao da ste za zemlju prikovani? Zar vam je draži život na dunjaluku od ahireta? A uživanje na dunjaluku prema ahiretu neznatno je.", TextEnglish = "O you who have believed, what is [the matter] with you that, when you are told to go forth in the cause of Allāh, you adhere heavily to the earth?[475] Are you satisfied with the life of this world rather than the Hereafter? But what is the enjoyment of worldly life compared to the Hereafter except a [very] little.", TextGerman = "O die ihr glaubt, was ist mit euch, daß, wenn zu euch gesagt wird: Rückt aus auf Allahs Weg!, ihr euch schwer zur Erde sinken laßt? Seid ihr mit dem diesseitigen Leben mehr zufrieden als mit dem Jenseits? Aber der Genuß des diesseitigen Lebens wird im Jenseits nur gering (erscheinen)." });
                        surah9.VerseList.Add(new Verse { Number = 39, TextBosnian = "Ako ne budete u boj išli, On će vas na nesnosne muke staviti i drugim će vas narodom zamijeniti, a vi Mu nećete nimalo nauditi. A Allah nad svime ima moć.", TextEnglish = "If you do not go forth, He will punish you with a painful punishment and will replace you with another people, and you will not harm Him at all. And Allāh is over all things competent.", TextGerman = "Wenn ihr nicht ausrückt, wird Er euch mit schmerzhafter Strafe strafen und euch durch ein anderes Volk ersetzen, und ihr (könnt) Ihm keinerlei Schaden zufügen. Allah hat zu allem die Macht." });
                        surah9.VerseList.Add(new Verse { Number = 40, TextBosnian = "Ako ga vi ne pomognete, pa pomogao ga je Allah onda kad su ga oni koji ne vjeruju prisilili da ode, kad je s njim bio samo drug njegov, kad su njih dvojica bili u pećini, i kad je on rekao drugu svome: 'Ne brini, Allah je s nama!' Pa je Allah spustio smiraj Svoj na njega i pomagao ga vojskom koju vi niste vidjeli i učinio da riječ nevjernika bude donja, a Allahova riječ - ona je gornja. Allah je silan i mudar.", TextEnglish = "If you do not aid him [i.e., the Prophet (ﷺ)] - Allāh has already aided him when those who disbelieved had driven him out [of Makkah] as one of two,[476] when they were in the cave and he [i.e., Muḥammad (ﷺ)] said to his companion, 'Do not grieve; indeed Allāh is with us.' And Allāh sent down His tranquility upon him and supported him with soldiers [i.e., angels] you did not see and made the word[477] of those who disbelieved the lowest,[478] while the word of Allāh[479] - that is the highest. And Allāh is Exalted in Might and Wise.", TextGerman = "Wenn ihr ihm nicht helft, so hat Allah ihm (schon damals) geholfen, als diejenigen, die ungläubig waren, ihn als einen von Zweien vertrieben; als sie beide in der Höhle waren und als er zu seinem Gefährten sagte: Sei nicht traurig! Gewiß, Allah ist mit uns! Da sandte Allah Seine innere Ruhe auf ihn herab und stärkte ihn mit Heerscharen, die ihr nicht saht, und erniedrigte das Wort derjenigen, die ungläubig waren, während Allahs Wort (doch) das hohe ist. Allah ist Allmächtig und Allweise." });
                        surah9.VerseList.Add(new Verse { Number = 41, TextBosnian = "Krećite u boj, lahki ili teški, i borite se na Allahovu putu zalažući imetke svoje i živote svoje! To vam je, da znate, bolje!", TextEnglish = "Go forth, whether light or heavy,[480] and strive with your wealth and your lives in the cause of Allāh. That is better for you, if you only knew.", TextGerman = "Rückt aus, leicht oder schwer, und müht euch mit eurem Besitz und eurer eigenen Person auf Allahs Weg ab. Das ist besser für euch, wenn ihr (es) nur wißt." });
                        surah9.VerseList.Add(new Verse { Number = 42, TextBosnian = "Da se radilo o plijenu nadohvat ruke i ne toliko dalekom pohodu, licemjeri bi te sigurno slijedili, ali im je put izgledao dalek. I oni će se zaklinjati Allahom: 'Da smo mogli, doista bismo s vama pošli.' Oni upropaštavaju sami sebe, a Allah, zaista, zna da su oni lažljivci.", TextEnglish = "Had it been a near [i.e., easy] gain and a moderate trip, they [i.e., the hypocrites] would have followed you, but distant to them was the journey. And they will swear by Allāh,[481] 'If we were able, we would have gone forth with you,' destroying themselves [through false oaths], and Allāh knows that indeed they are liars.", TextGerman = "Wenn es um nahe Glücksgüter und eine mäßige Reise ginge, würden sie dir wahrlich folgen. Aber die Entfernung ist ihnen zu weit. Und dennoch werden sie bei Allah schwören: Wenn wir (es) könnten, würden wir fürwahr mit euch hinausziehen. Dabei vernichteten sie sich selbst. Doch Allah weiß, daß sie wahrlich lügen." });
                        surah9.VerseList.Add(new Verse { Number = 43, TextBosnian = "Neka ti Allah oprosti što si dozvolio da izostanu! Inače bi ti se obznanili oni koji govore istinu, a znao bi i one koji lažu.", TextEnglish = "Allāh has pardoned you, [O Muḥammad, but] why did you give them permission [to remain behind]? [You should not have] until it was evident to you who were truthful and you knew [who were] the liars.", TextGerman = "Allah verzeihe dir! Warum hast du ihnen erlaubt (, zurückzubleiben), bevor sich dir diejenigen klar gezeigt haben, die wahrhaftig sind, und du die Lügner kennst?" });
                        surah9.VerseList.Add(new Verse { Number = 44, TextBosnian = "Oni koji vjeruju u Allaha i u ahiret neće od tebe tražiti dozvolu da se ne bore zalažući imetke svoje i živote svoje - a Allah dobro zna bogobojazne.", TextEnglish = "Those who believe in Allāh and the Last Day would not ask permission of you to be excused from striving [i.e., fighting] with their wealth and their lives. And Allāh is Knowing of those who fear Him.", TextGerman = "Diejenigen, die an Allah und den Jüngsten Tag glauben, bitten dich nicht um Erlaubnis, sich mit ihrem Besitz und ihrer eigenen Person nicht abzumühen. Und Allah weiß über die Gottesfürchtigen Bescheid." });
                        surah9.VerseList.Add(new Verse { Number = 45, TextBosnian = "Od tebe će tražiti dozvolu samo oni koji ne vjeruju u Allaha i ahiret, i čija se srca kolebaju, pa su u sumnji svojoj neodlučni.", TextEnglish = "Only those would ask permission of you who do not believe in Allāh and the Last Day and whose hearts have doubted, and they, in their doubt, are hesitating.", TextGerman = "Um Erlaubnis bitten dich nur diejenigen, die an Allah und den Jüngsten Tag nicht glauben und deren Herzen zweifeln; so zaudern sie in ihrem Zweifel." });
                        surah9.VerseList.Add(new Verse { Number = 46, TextBosnian = "Da su imali namjeru poći, sigurno bi za to pripremili ono što je potrebno, ali Allah nije volio da idu, pa ih je zadržao i bi im rečeno: 'Sjedite sa onima koji sjede.'", TextEnglish = "And if they had intended to go forth, they would have prepared for it [some] preparation. But Allāh disliked their being sent, so He kept them back, and they were told, 'Remain [behind] with those who remain.'[482]", TextGerman = "Wenn sie (wirklich) hätten hinausziehen wollen, hätten sie fürwahr Vorbereitungen dazu getroffen. Aber Allah war ihr Ausziehen zuwider, und so hielt Er sie zurück. Und es wurde gesagt: So bleibt (daheim) mit denjenigen, die (daheim) sitzen bleiben!" });
                        surah9.VerseList.Add(new Verse { Number = 47, TextBosnian = "Da su pošli s vama, samo bi među vama razdor povećali i brzo bi među vas smutnju ubacili, a među vama ima i onih koji ih rado slušaju. A Allah dobro zna zulumćare.", TextEnglish = "Had they gone forth with you, they would not have increased you except in confusion, and they would have been active among you, seeking [to cause] you fitnah [i.e., chaos and dissension]. And among you are avid listeners to them. And Allāh is Knowing of the wrongdoers.", TextGerman = "Wenn sie mit euch hinausgezogen wären, hätten sie euch nur Verwirrung gebracht und wären unter euch fürwahr umhergelaufen im Trachten danach, euch der Versuchung auszusetzen. Und unter euch gibt es manche, die immer (wieder) auf sie horchen. Und Allah weiß über die Ungerechten Bescheid." });
                        surah9.VerseList.Add(new Verse { Number = 48, TextBosnian = "Oni su i prije smutnju priželjkivali i smicalice ti smišljali sve dok nije Istina došla i Allahova vjera zavladala, a oni to voljeli nisu.", TextEnglish = "They had already desired dissension before and had upset matters for you[483] until the truth came and the ordinance [i.e., victory] of Allāh appeared, while they were averse.", TextGerman = "Sie haben bereits zuvor danach getrachtet, euch der Versuchung auszusetzen, und haben dir die Angelegenheiten umgekehrt, bis die Wahrheit kam und die Anordnung Allahs erschien, obwohl es ihnen zuwider war." });
                        surah9.VerseList.Add(new Verse { Number = 49, TextBosnian = "Ima ih koji govore: 'Dozvoli mi i ne dovedi me u iskušenje!' Eto, baš u iskušenje su pali! A Džehennem će sigurno nevjernike skoliti.", TextEnglish = "And among them is he who says, 'Permit me [to remain at home] and do not put me to trial.' Unquestionably, into trial they have fallen.[484] And indeed, Hell will encompass the disbelievers.", TextGerman = "Unter ihnen gibt es manche, die sagen: Erlaube mir (, zurückzubleiben) und setze mich nicht der Versuchung aus! Dabei sind sie doch in Versuchung gefallen. Und wahrlich, die Hölle umfaßt die Ungläubigen." });
                        surah9.VerseList.Add(new Verse { Number = 50, TextBosnian = "Ako te zadesi dobro, to ih ojadi, a kad te pogodi nesreća - oni govore: 'Mi smo i ranije bili oprezni', i odlaze veseli.", TextEnglish = "If good befalls you, it distresses them; but if disaster strikes you, they say, 'We took our matter [in hand] before,'[485] and turn away while they are rejoicing.", TextGerman = "Wenn dich etwas Gutes trifft, tut es ihnen leid. Wenn dich jedoch ein Unglück trifft, sagen sie: Wir haben unsere Angelegenheit schon zuvor selbst übernommen und kehren sich froh ab." });
                        surah9.VerseList.Add(new Verse { Number = 51, TextBosnian = "Reci: 'Dogodit će nam se samo ono što nam Allah odredi, On je Gospodar naš, i neka se vjernici samo u Allaha pouzdaju!'", TextEnglish = "Say, 'Never will we be struck except by what Allāh has decreed for us; He is our protector.' And upon Allāh let the believers rely.", TextGerman = "Sag: Uns wird nur das treffen, was Allah für uns bestimmt hat. Er ist unser Schutzherr. Auf Allah sollen sich die Gläubigen verlassen." });
                        surah9.VerseList.Add(new Verse { Number = 52, TextBosnian = "Reci: 'Očekujete li za nas šta drugo već jedno od dva dobra?' A mi očekujemo da vas Allah kaznom od Sebe pogodi, ili da vas rukama našim kazni. Pa iščekujte! Mi ćemo, zaista, s vama čekati.", TextEnglish = "Say, 'Do you await for us except one of the two best things [i.e., martyrdom or victory] while we await for you that Allāh will afflict you with punishment from Himself or at our hands? So wait; indeed we, along with you, are waiting.'", TextGerman = "Sag: Erwartet ihr für uns etwas (anderes) als eines der beiden schönsten Dinge? Wir erwarten für euch, daß Allah euch mit einer Strafe von Ihm oder durch unsere Hände trifft. So wartet nur ab! Gewiß, Wir warten mit euch ab." });
                        surah9.VerseList.Add(new Verse { Number = 53, TextBosnian = "Reci: 'Trošili milom ili silom, od vas neće biti primljeno; vi ste, zaista, ljudi buntovnici.'", TextEnglish = "Say, 'Spend willingly or unwillingly; never will it be accepted from you. Indeed, you have been a defiantly disobedient people.'", TextGerman = "Sag: Gebt freiwillig oder widerwillig aus, es wird von euch (doch) nicht angenommen werden! Ihr seid ja frevlerische Leute." });
                        surah9.VerseList.Add(new Verse { Number = 54, TextBosnian = "A priloge njihove spriječit će da budu primljeni samo to što u Allaha i Njegova Poslanika ne vjeruju, što lijeno namaz obavljaju i što samo preko volje udjeljuju.", TextEnglish = "And what prevents their expenditures from being accepted from them but that they have disbelieved in Allāh and in His Messenger and that they come not to prayer except while they are lazy and that they do not spend except while they are unwilling.", TextGerman = "Und nichts (anderes) verhindert, daß ihre Ausgaben von ihnen angenommen werden, als daß sie Allah und Seinen Gesandten verleugnen, sich nur schwerfällig zum Gebet begeben und nur widerwillig ausgeben." });
                        surah9.VerseList.Add(new Verse { Number = 55, TextBosnian = "Neka te ne oduševljavaju imeci njihovi, a ni djeca njihova! Allah hoće da ih zbog njih kazni na ovom svijetu i da im duše, dok su nevjernici, iziđu.", TextEnglish = "So let not their wealth or their children impress you. Allāh only intends to punish them through them in worldly life and that their souls should depart [at death] while they are disbelievers.", TextGerman = "So sollen dir weder ihr Besitz noch ihre Kinder gefallen. Allah will sie damit ja nur im diesseitigen Leben strafen, und (daß) ihre Seelen (im Tod) dahingehen, während sie ungläubig sind." });
                        surah9.VerseList.Add(new Verse { Number = 56, TextBosnian = "Oni se zaklinju Allahom da su doista vaši, a oni nisu vaši, nego su narod kukavički.", TextEnglish = "And they swear by Allāh that they are from among you while they are not from among you; but they are a people who are afraid.", TextGerman = "Und sie schwören bei Allah, daß sie fürwahr zu euch gehören, während sie nicht zu euch gehören, sondern sie sind Leute, die ängstlich sind." });
                        surah9.VerseList.Add(new Verse { Number = 57, TextBosnian = "Kad bi našli kakvo sklonište, ili kakve pećine, ili kakvu jamu, oni bi tamo trkom pohrlili.", TextEnglish = "If they could find a refuge or some caves or any place to enter [and hide], they would turn to it while they run heedlessly.", TextGerman = "Wenn sie einen Zufluchtsort oder Höhlen oder (sonst) ein Schlupfloch fänden, würden sie sich wahrlich dorthin wenden, und zwar fluchtartig." });
                        surah9.VerseList.Add(new Verse { Number = 58, TextBosnian = "Ima ih koji ti prigovaraju zbog raspodjele zekata. Ako im se iz njega da, zadovolje se, a ako im se ne da, odjednom se razljute.", TextEnglish = "And among them are some who criticize you concerning the [distribution of] charities. If they are given from them, they approve; but if they are not given from them, at once they become angry.", TextGerman = "Und unter ihnen gibt es manche, die gegen dich wegen der Almosen nörgeln. Wenn ihnen davon gegeben wird, zeigen sie Wohlgefallen. Wenn ihnen davon aber nichts gegeben wird, zeigen sie sogleich Mißfallen." });
                        surah9.VerseList.Add(new Verse { Number = 59, TextBosnian = "A da se zadovolje onim što im daju Allah i Poslanik Njegov i da kažu: 'Dovoljan nam je Allah, Allah će nam dati iz obilja Svoga, a i Poslanik Njegov, i mi, zaista, ka Allahu žudimo'.", TextEnglish = "If only they had been satisfied with what Allāh and His Messenger gave them and said, 'Sufficient for us is Allāh; Allāh will give us of His bounty, and [so will] His Messenger; indeed, we are desirous toward Allāh,'[486] [it would have been better for them].", TextGerman = "Wenn sie doch mit dem zufrieden wären, was Allah und Sein Gesandter ihnen gewährten, und sagten: Unsere Genüge ist Allah. Allah wird uns (etwas) von Seiner Huld gewähren und auch Sein Gesandter. Gewiß, nach Allah steht unser Begehren." });
                        surah9.VerseList.Add(new Verse { Number = 60, TextBosnian = "Zekat pripada samo siromasima i nevoljnicima, i onima koji ga sakupljaju, i onima čija srca treba pridobiti, i za otkup iz ropstva, i prezaduženima, i u svrhe na Allahovu putu, i putniku namjerniku. Allah je odredio tako! A Allah je sveznajući i mudar je.", TextEnglish = "Zakāh expenditures are only for the poor and for the needy and for those employed for it[487] and for bringing hearts together [for Islām] and for freeing captives [or slaves] and for those in debt and for the cause of Allāh and for the [stranded] traveler - an obligation [imposed] by Allāh. And Allāh is Knowing and Wise.", TextGerman = "Die Almosen sind nur für die Armen, die Bedürftigen, diejenigen, die damit beschäftigt sind, diejenigen, deren Herzen vertraut gemacht werden sollen, (den Loskauf von) Sklaven, die Verschuldeten, auf Allahs Weg und (für) den Sohn des Weges, als Verpflichtung von Allah. Allah ist Allwissend und Allweise." });
                        surah9.VerseList.Add(new Verse { Number = 61, TextBosnian = "Ima ih koji uznemiravaju Vjerovjesnika i govore: 'On je kao uho - vjeruje u sve što čuje!' Reci: 'On je kao uho koje čuje ono što je dobro po vas, vjeruje u Allaha i vjeruje vjernicima, i milost je onima između vas koji vjeruju.' A one koji Allahova Poslanika uznemiravaju čeka patnja bolna.", TextEnglish = "And among them are those who abuse the Prophet and say, 'He is an ear.'[488] Say, '[It is] an ear of goodness for you that believes in Allāh and believes the believers and [is] a mercy to those who believe among you.' And those who abuse the Messenger of Allāh - for them is a painful punishment.", TextGerman = "Unter ihnen gibt es diejenigen, die dem Propheten Leid zufügen und sagen: Er ist ein Ohr. Sag: (Er ist) ein Ohr des Guten für euch. Er glaubt an Allah und glaubt den Gläubigen, und (er ist) eine Barmherzigkeit für diejenigen von euch, die glauben. Für diejenigen aber, die Allahs Gesandtem Leid zufügen, wird es schmerzhafte Strafe geben." });
                        surah9.VerseList.Add(new Verse { Number = 62, TextBosnian = "Zaklinju vam se Allahom da biste bili zadovoljni, a preče bi im bilo da Allah i Njegov Poslanik budu zadovoljni, ako su vjernici.", TextEnglish = "They swear by Allāh to you [Muslims] to satisfy you. But Allāh and His Messenger are more worthy for them to satisfy,[489] if they were to be believers.", TextGerman = "Sie schwören euch bei Allah, um euch zufriedenzustellen. Aber Allah - und (auch) Sein Gesandter - hat ein größeres Anrecht darauf, daß sie Ihn zufriedenstellen, wenn sie gläubig sind." });
                        surah9.VerseList.Add(new Verse { Number = 63, TextBosnian = "Zar oni ne znaju da onoga koji se suprotstavlja Allahu i Poslaniku Njegovu čeka vatra džehennemska, u kojoj će vječno ostati? To je poniženje veliko.", TextEnglish = "Do they not know that whoever opposes Allāh and His Messenger - that for him is the fire of Hell, wherein he will abide eternally? That is the great disgrace.", TextGerman = "Wissen sie denn nicht, daß es für denjenigen, der Allah und Seinem Gesandten zuwiderhandelt -, daß es für ihn das Feuer der Hölle gibt, ewig darin zu bleiben? Das ist die gewaltige Schande." });
                        surah9.VerseList.Add(new Verse { Number = 64, TextBosnian = "Licemjeri se plaše da se vjernicima ne objavi sura koja bi im otkrila ono što je u srcima njihovim. Reci: 'Samo se vi rugajte.' Allah će, doista, na vidjelo iznijeti ono čega se vi plašite.", TextEnglish = "The hypocrites are apprehensive lest a sūrah be revealed about them, informing them of[490] what is in their hearts. Say, 'Mock [as you wish]; indeed, Allāh will expose that which you fear.'", TextGerman = "Die Heuchler fürchten, daß eine Sura über sie offenbart werden könnte, die ihnen kundtut, was in ihren Herzen ist. Sag: Macht euch (nur) lustig! Allah wird herausbringen, was ihr fürchtet." });
                        surah9.VerseList.Add(new Verse { Number = 65, TextBosnian = "Ako ih zapitaš, oni će sigurno reći: 'Mi smo samo razgovarali i zabavljali se.' Reci: 'Zar se niste Allahu, ajetima i znakovima Njegovim, i Poslaniku Njegovom rugali?'", TextEnglish = "And if you ask them, they will surely say, 'We were only conversing and playing.' Say, 'Is it Allāh and His verses and His Messenger that you were mocking?'", TextGerman = "Und wenn du sie fragst, werden sie ganz gewiß sagen: Wir haben nur (schweifende) Gespräche geführt und gescherzt. Sag: Habt ihr euch denn über Allah und Seine Zeichen und Seinen Gesandten lustig gemacht?" });
                        surah9.VerseList.Add(new Verse { Number = 66, TextBosnian = "Ne ispričavajte se! Jasno je da ste postali nevjernici nakon što ste vjerovali. Ako nekima od vas i oprostimo, druge ćemo kazniti zato što su prestupnici bili.", TextEnglish = "Make no excuse; you have disbelieved [i.e., rejected faith] after your belief. If We pardon one faction of you - We will punish another faction because they were criminals.", TextGerman = "Entschuldigt euch nicht! Ihr seid ja ungläubig geworden, nachdem ihr den Glauben (angenommen) hattet. Wenn Wir (auch) einem Teil von euch verzeihen, so strafen Wir einen (anderen) Teil (dafür), daß sie Übeltäter waren." });
                        surah9.VerseList.Add(new Verse { Number = 67, TextBosnian = "Licemjeri i licemjerke slični su jedni drugima: traže da se čine nevaljala djela, a odvraćaju od dobrih, i ruke su im stisnute, zaboravljaju Allaha, pa je i On njih zaboravio. Zaista su licemjeri - baš oni - buntovnici.", TextEnglish = "The hypocrite men and hypocrite women are of one another. They enjoin what is wrong and forbid what is right and close their hands.[491] They have forgotten Allāh, so He has forgotten them [accordingly]. Indeed, the hypocrites - it is they who are the defiantly disobedient.", TextGerman = "Die Heuchler und die Heuchlerinnen stammen voneinander. Sie gebieten das Verwerfliche und verbieten das Rechte und halten ihre Hände geschlossen. Sie haben Allah vergessen, und so hat Er sie vergessen. Gewiß, die Heuchler sind die (wahren) Frevler." });
                        surah9.VerseList.Add(new Verse { Number = 68, TextBosnian = "Licemjerima i licemjerkama i nevjernicima Allah je obećao džehennemsku vatru, vječno će u njoj boraviti, dosta će im ona biti! Allah ih je prokleo, i njih čeka patnja stalna.", TextEnglish = "Allāh has promised the hypocrite men and hypocrite women and the disbelievers the fire of Hell, wherein they will abide eternally. It is sufficient for them. And Allāh has cursed them, and for them is an enduring punishment.", TextGerman = "Allah hat den Heuchlern und den Heuchlerinnen und den Ungläubigen das Feuer der Hölle versprochen, ewig darin zu bleiben. Es ist ihre Genüge. Und Allah hat sie verflucht, und für sie gibt es beständige Strafe." });
                        surah9.VerseList.Add(new Verse { Number = 69, TextBosnian = "Vi ste kao i oni prije vas. Oni su jači od vas bili i više su blaga i djece imali, i slatkim životom živjeli, a i vi slatkim životom živite, isto onako, kao što su oni prije vas živjeli, i vi se upuštate u nevaljalštine kao što su se i oni upuštali. To su oni čija će djela bili poništena i na ovom i na onom svijetu, i oni su baš gubitnici.", TextEnglish = "[You disbelievers are] like those before you; they were stronger than you in power and more abundant in wealth and children. They enjoyed their portion [of worldly enjoyment], and you have enjoyed your portion as those before you enjoyed their portion, and you have engaged [in vanities] like that in which they engaged. [It is] those whose deeds have become worthless in this world and in the Hereafter, and it is they who are the losers.", TextGerman = "(Es ist) wie mit denen, die vor euch waren. Sie hatten stärkere Kraft als ihr und mehr Besitz und Kinder. Sie genossen ihren Anteil, und dann habt ihr euren Anteil genossen, wie diejenigen, die vor euch waren, ihren Anteil genossen haben. Und ihr habt (schweifende) Gespräche geführt wie die Gespräche, die sie führten. Deren Werke werden im Diesseits und im Jenseits hinfällig. Das sind die Verlierer." });
                        surah9.VerseList.Add(new Verse { Number = 70, TextBosnian = "Zar do njih nije doprla vijest o onima prije njih: o narodu Nuhovu, i o Adu, i o Semudu, i o narodu Ibrahimovom, i o stanovnicima Medjena, i o onima čija su naselja prevrnuta? Poslanici su im njihovi jasne dokaze donosili i Allah im nije učinio nikakav zulum, nego su oni sami sebi zulum činili.", TextEnglish = "Has there not reached them the news of those before them - the people of Noah and [the tribes of] ʿAad and Thamūd and the people of Abraham and the companions [i.e., dwellers] of Madyan and the towns overturned?[492] Their messengers came to them with clear proofs. And Allāh would never have wronged them, but they were wronging themselves.", TextGerman = "Ist ihnen nicht die Kunde von denjenigen zugekommen, die vor ihnen waren, des Volkes Nuhs, der ’Ad und der Tamud, des Volkes Ibrahims, der Bewohner von Madyan und der umgestürzten Städte? Ihre Gesandten kamen zu ihnen mit den klaren Beweisen. Nimmer ist es Allah, der ihnen Unrecht getan hat, sondern sie selbst haben sich Unrecht zugefügt." });
                        surah9.VerseList.Add(new Verse { Number = 71, TextBosnian = "A vjernici i vjernice prijatelji i zaštitnici su jedni drugima: traže da se čine dobra djela, a od nevaljalih odvraćaju, i namaz obavljaju i zekat daju, i Allahu i Poslaniku Njegovu se pokoravaju. To su oni kojima će se Allah sigurno smilovati. Allah je, doista, silan i mudar.", TextEnglish = "The believing men and believing women are allies of one another. They enjoin what is right and forbid what is wrong and establish prayer and give zakāh and obey Allāh and His Messenger. Those - Allāh will have mercy upon them. Indeed, Allāh is Exalted in Might and Wise.", TextGerman = "Die gläubigen Männer und Frauen sind einer des anderen Beschützer. Sie gebieten das Rechte und verbieten das Verwerfliche, verrichten das Gebet und entrichten die Abgabe und gehorchen Allah und Seinem Gesandten. Sie sind es, derer Allah Sich erbarmen wird. Gewiß, Allah ist Allmächtig und Allweise." });
                        surah9.VerseList.Add(new Verse { Number = 72, TextBosnian = "Allah je obećao vjernicima i vjernicama džennetske bašče, kroz koje rijeke teku, u kojima će vječno boraviti, i divna boravišta u džennetima Adna. A zadovoljstvo Allahovo veće je od svega toga: to će, doista, uspjeh veliki biti!", TextEnglish = "Allāh has promised the believing men and believing women gardens beneath which rivers flow, wherein they abide eternally, and pleasant dwellings in gardens of perpetual residence; but approval from Allāh is greater. It is that which is the great attainment.", TextGerman = "Allah hat den gläubigen Männern und Frauen Gärten versprochen, durcheilt von Bächen, ewig darin zu bleiben, und gute Wohnungen in den Gärten Edens. Wohlgefallen von Allah ist aber (noch) größer. Das ist der großartige Erfolg." });
                        surah9.VerseList.Add(new Verse { Number = 73, TextBosnian = "O Vjerovjesniče, bori se protiv nevjernika i licemjera i budi prema njima strog. Prebivalište njihovo bit će Džehennem, a užasno je to krajnje odredište!", TextEnglish = "O Prophet, fight against the disbelievers and the hypocrites and be harsh upon them. And their refuge is Hell, and wretched is the destination.", TextGerman = "O Prophet, mühe dich gegen die Ungläubigen und die Heuchler ab und sei hart gegen sie. Ihr Zufluchtsort wird die Hölle sein - ein schlimmer Ausgang!" });
                        surah9.VerseList.Add(new Verse { Number = 74, TextBosnian = "Licemjeri se zaklinju Allahom da nisu govorili, a sigurno su govorili nevjerničke riječi i pokazivali da su nevjernici, nakon što su javno islam bili primili, i htjeli su učiniti ono što nisu uspjeli. A prigovaraju samo zato što su ih Allah, iz obilja Svoga, i Poslanik Njegov, imućnim učinili. Pa ako se pokaju, bit će im dobro, a ako se okrenu, Allah će ih na ovom i na drugom svijetu na bolne muke staviti, a na Zemlji ni zaštitnika ni pomagača neće imati.", TextEnglish = "They swear by Allāh that they did not say [anything against the Prophet (ﷺ)] while they had said the word of disbelief and disbelieved after their [pretense of] Islām and planned that which they were not to attain.[493] And they were not resentful except [for the fact] that Allāh and His Messenger had enriched them of His bounty.[494] So if they repent, it is better for them; but if they turn away, Allāh will punish them with a painful punishment in this world and the Hereafter. And there will not be for them on earth any protector or helper.", TextGerman = "Sie schwören bei Allah, sie hätten (es) nicht gesagt. Aber sie haben ja das Wort des Unglaubens gesagt und sind, nachdem sie den Islam (angenommen) hatten, ungläubig geworden. Sie hatten vor (, das auszuführen), was sie (doch) nicht erreicht haben. Und sie grollten darüber nur, daß Allah - und (auch) Sein Gesandter - sie von Seiner Huld reich gemacht hat. Wenn sie nun bereuen, ist es besser für sie. Wenn sie sich aber abkehren, wird Allah sie mit einer schmerzhaften Strafe im Diesseits und Jenseits strafen, und sie werden auf der Erde weder Schutzherrn noch Helfer haben." });
                        surah9.VerseList.Add(new Verse { Number = 75, TextBosnian = "Ima ih koji su se obavezali Allahu: 'Ako nam iz obilja Svoga dadne, udjeljivat ćemo, zaista, zekat i sadaku i bit ćemo, doista, dobri.'", TextEnglish = "And among them are those who made a covenant with Allāh, [saying], 'If He should give us from His bounty, we will surely spend in charity, and we will surely be among the righteous.'", TextGerman = "Unter ihnen gibt es manche, die gegenüber Allah eine Verpflichtung eingegangen sind: Wenn Er uns etwas von Seiner Huld gewährt, werden wir ganz gewiß Almosen geben und ganz gewiß zu den Rechtschaffenen gehören." });
                        surah9.VerseList.Add(new Verse { Number = 76, TextBosnian = "A kad im je On dao iz obilja Svoga, oni su u tome postali škrti i okrećući se - od toga udaljili.", TextEnglish = "But when He gave them from His bounty, they were stingy with it and turned away while they refused.", TextGerman = "Als Er ihnen aber von Seiner Huld gewährt hatte, geizten sie damit, und sie kehrten sich widerstrebend ab." });
                        surah9.VerseList.Add(new Verse { Number = 77, TextBosnian = "I nadovezao im je On na to i licemjerstvo u srcima sve do dana kada će pred Njega stati, a to stoga što su iznevjerili ono što su Allahu obećali, i zato što su stalno lagali.", TextEnglish = "So He penalized them with hypocrisy in their hearts until the Day they will meet Him - because they failed Allāh in what they promised Him and because they [habitually] used to lie.", TextGerman = "So setzte Er als Folge davon in ihre Herzen Heuchelei bis zum Tag, an dem sie Ihm begegnen werden, dafür, daß sie gegenüber Allah brachen, was sie Ihm versprochen hatten, und daß sie zu lügen pflegten." });
                        surah9.VerseList.Add(new Verse { Number = 78, TextBosnian = "Zar oni ne znaju da Allah zna ono što oni u sebi kriju, ono o čemu se sašaptavaju i da je Allah znalac svega što je čulima nedokučivo.", TextEnglish = "Did they not know that Allāh knows their secrets and their private conversations and that Allāh is the Knower of the unseen?", TextGerman = "Wissen sie denn nicht, daß Allah ihr Geheimes und ihre vertraulichen Gespräche kennt und daß Allah der Allwisser der verborgenen Dinge ist?" });
                        surah9.VerseList.Add(new Verse { Number = 79, TextBosnian = "One koji ogovaraju vjernike, koji dobrovoljno zekat i sadaku daju, a rugaju se i onima koji ga s mukom daju -Allah će poruzi izvrgnuti, i njih čeka patnja bolna.", TextEnglish = "Those who criticize the contributors among the believers concerning [their] charities and [criticize] the ones who find nothing [to spend] except their effort, so they ridicule them - Allāh will ridicule them, and they will have a painful punishment.", TextGerman = "Diejenigen, die gegen die Freiwilligen unter den Gläubigen wegen der Almosen verhöhnen und (auch) gegen diejenigen, die nichts als ihre Mühe (als Leistung zu erbringen) finden, und dann über sie spotten - Allah spottet über sie, und für sie wird es schmerzhafte Strafe geben." });
                        surah9.VerseList.Add(new Verse { Number = 80, TextBosnian = "Molio ti za njih oprost ili ne molio, molio čak i sedamdeset puta, Allah im neće oprostiti - zato što u Allaha i Njegova Poslanika ne vjeruju. A Allah neće uputiti ljude buntovnike.", TextEnglish = "Ask forgiveness for them, [O Muḥammad], or do not ask forgiveness for them. If you should ask forgiveness for them seventy times - never will Allāh forgive them. That is because they disbelieved in Allāh and His Messenger, and Allāh does not guide the defiantly disobedient people.", TextGerman = "Bitte um Vergebung für sie, oder bitte nicht um Vergebung für sie; wenn du (auch) siebzigmal um Vergebung für sie bittest, so wird Allah ihnen doch nicht vergeben. Dies (geschieht deshalb), weil sie Allah und Seinen Gesandten verleugnet haben. Allah leitet das Volk der Frevler nicht recht." });
                        surah9.VerseList.Add(new Verse { Number = 81, TextBosnian = "Oni koji su izostali iza Allahova Poslanika veselili su se kod kuća svojih - mrsko im je bilo da se bore na Allahovu putu zalažući imetke svoje i živote svoje, i jedni drugima su govorili: 'Ne krećite u boj po vrućini', reci : 'Džehennemska vatra još je vrelija!' Samo da su oni shvatali!?", TextEnglish = "Those who remained behind[495] rejoiced in their staying [at home] after [the departure of] the Messenger of Allāh and disliked to strive with their wealth and their lives in the cause of Allāh and said, 'Do not go forth in the heat.' Say, 'The fire of Hell is more intense in heat' - if they would but understand.", TextGerman = "Die Zurückgelassenen waren froh darüber, daß sie hinter Allahs Gesandtem (daheim) sitzen geblieben sind, und es war ihnen zuwider, sich mit ihrem Besitz und mit ihrer eigenen Person auf Allahs Weg abzumühen. Und sie sagten: Rückt nicht in der Hitze aus! Sag: Das Feuer der Hölle ist noch heißer; wenn sie (es doch) verstehen würden!" });
                        surah9.VerseList.Add(new Verse { Number = 82, TextBosnian = "Neka se oni malo smiju, a dugo će plakati, bit će to kazna za ono što su zaradili.", TextEnglish = "So let them laugh a little and [then] weep much as recompense for what they used to earn.", TextGerman = "So sollen sie ein wenig lachen und sollen häufig weinen als Lohn für das, was sie erworben haben." });
                        surah9.VerseList.Add(new Verse { Number = 83, TextBosnian = "I ako te Allah ponovo vrati nekima od njih, pa te zamole da im dopustiš da pođu s tobom u boj, ti im reci: 'Nikad sa mnom u boj nećete ići i nikada se sa mnom protiv neprijatelja nećete boriti! Bili ste zadovoljni što ste prvi put izostali, zato ostanite s onima koji ionako ne idu u boj.'", TextEnglish = "If Allāh should return you to a faction of them [after the expedition] and then they ask your permission to go out [to battle], say, 'You will not go out with me, ever, and you will never fight with me an enemy. Indeed, you were satisfied with sitting [at home] the first time, so sit [now] with those who stay behind.'", TextGerman = "Wenn Allah dich zu einem Teil von ihnen zurückkehren läßt und sie dich um Erlaubnis bitten, hinauszuziehen, dann sag: Ihr werdet niemals mit mir hinausziehen, und ihr werdet mit mir gegen keinen Feind kämpfen. Ihr seid (ja) das erste Mal damit zufrieden gewesen, (daheim) sitzen zu bleiben. Bleibt nun (daheim) sitzen mit denen, die zurückbleiben!" });
                        surah9.VerseList.Add(new Verse { Number = 84, TextBosnian = "I nijednom od njih, kad umre, nemoj dženazu obaviti, i nemoj nad kaburom njegovim stajati. Oni u Allaha i Njegova Poslanika, zaista, nisu vjerovali i kao buntovnici oni umiru.", TextEnglish = "And do not pray [the funeral prayer, O Muḥammad], over any of them who has died - ever - or stand at his grave. Indeed, they disbelieved in Allāh and His Messenger and died while they were defiantly disobedient.", TextGerman = "Und bete niemals über jemandem von ihnen, der gestorben ist, und stehe nicht an seinem Grab! Sie verleugneten ja Allah und Seinen Gesandten, und sie starben als Frevler." });
                        surah9.VerseList.Add(new Verse { Number = 85, TextBosnian = "Neka te ne ushićuju imeci njihovi i djeca njihova! Allah želi da ih njima na dunjaluku namuči i da im duše - dok su nevjernici - iziđu.", TextEnglish = "And let not their wealth and their children impress you. Allāh only intends to punish them through them in this world and that their souls should depart [at death] while they are disbelievers.", TextGerman = "Ihr Besitz und ihre Kinder sollen dir nicht gefallen. Allah will sie ja nur im Diesseits damit strafen, und (daß) ihre Seelen (im Tod) dahingehen, während sie ungläubig sind." });
                        surah9.VerseList.Add(new Verse { Number = 86, TextBosnian = "A kada je objavljena sura da u Allaha vjerujete i da se zajedno s Poslanikom Njegovim borite, najimućniji od njih su zatražili odobrenje od tebe i rekli: 'Ostavi nas da budemo s onima koji ne idu u boj!'", TextEnglish = "And when a sūrah was revealed [enjoining them] to believe in Allāh and to fight with His Messenger, those of wealth among them asked your permission [to stay back] and said, 'Leave us to be with them who sit [at home].'", TextGerman = "Und wenn eine Sura (als Offenbarung) hinabgesandt wird: Glaubt an Allah und müht euch (zusammen) mit Seinem Gesandten ab, (dann) bitten dich die Bemittelten unter ihnen um Erlaubnis und sagen: Lasse uns mit denen (zusammen) sein, die (daheim) sitzen bleiben!" });
                        surah9.VerseList.Add(new Verse { Number = 87, TextBosnian = "Zadovoljavaju se da budu s onima koji ne idu u boj, srca njihova su zapečaćena, pa oni ne shvaćaju.", TextEnglish = "They were satisfied to be with those who stay behind, and their hearts were sealed over, so they do not understand.", TextGerman = "Sie waren damit zufrieden, (zusammen) mit den zurückbleibenden Frauen zu sein, und ihre Herzen wurden versiegelt; so verstehen sie nicht." });
                        surah9.VerseList.Add(new Verse { Number = 88, TextBosnian = "Ali Poslanik i oni koji s njim vjeruju bore se zalažući imetke svoje i živote svoje. Njima će svako dobro pripasti i oni će uspjeti.", TextEnglish = "But the Messenger and those who believed with him fought with their wealth and their lives. Those will have [all that is] good and it is those who are the successful.", TextGerman = "Aber der Gesandte und diejenigen, die mit ihm glaubten, mühten sich mit ihrem Besitz und ihrer eigenen Person ab. Das sind die, für die es die guten Dinge geben wird, und das sind diejenigen, denen es wohl ergeht." });
                        surah9.VerseList.Add(new Verse { Number = 89, TextBosnian = "Allah im je pripremio džennetske bašče kroz koje rijeke teku, u kojima će vječno boraviti. To je veliki uspjeh.", TextEnglish = "Allāh has prepared for them gardens beneath which rivers flow, wherein they will abide eternally. That is the great attainment.", TextGerman = "Allah hat für sie Gärten bereitet, durcheilt von Bächen, ewig darin zu bleiben; das ist der großartige Erfolg." });
                        surah9.VerseList.Add(new Verse { Number = 90, TextBosnian = "Dolazili su i neki beduini koji su se izvinjavali i tražili dopuštenje da ne idu, i tako su izostali oni koji su Allahu i Njegovu Poslaniku lagali, a teška patnja pogodit će one među njima koji nisu vjerovali.", TextEnglish = "And those with excuses among the bedouins came to be permitted [to remain], and they who had lied[496] to Allāh and His Messenger sat [at home]. There will strike those who disbelieved among them a painful punishment.", TextGerman = "Und diejenigen von den Wüstenarabern, die sich entschuldigen wollten, kamen (her), um Erlaubnis zu erhalten. Und (daheim) blieben diejenigen sitzen, die Allah und Seinen Gesandten belogen. Treffen wird diejenigen von ihnen, die ungläubig sind, schmerzhafte Strafe." });
                        surah9.VerseList.Add(new Verse { Number = 91, TextBosnian = "Neće se ogriješiti nemoćni i bolesni, i oni koji ne mogu naći sredstva za borbu, samo ako su prema Allahu i Njegovu Poslaniku iskreni. Nema razloga da se išta prigovara onima koji čine dobra djela- a Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "There is not upon the weak or upon the ill or upon those who do not find anything to spend any discomfort [i.e., guilt] when they are sincere to Allāh and His Messenger. There is not upon the doers of good any cause [for blame]. And Allāh is Forgiving and Merciful.", TextGerman = "Kein Grund zur Bedrängnis (wegen des Daheimbleibens) ist es für die Schwachen, für die Kranken und für diejenigen, die nichts finden, was sie ausgeben (können), wenn sie sich gegenüber Allah und Seinem Gesandten aufrichtig verhalten. Gegen die Gutes Tuenden gibt es keine Möglichkeit (, sie zu belangen) - Allah ist Allvergebend und Barmherzig -," });
                        surah9.VerseList.Add(new Verse { Number = 92, TextBosnian = "Ni onima kojima si rekao, kad su ti došli da im daš životinje za jahanje: 'Ne mogu naći za vas životinje za jahanje', pa su se vratili suznih očiju, tužni što ne nalaze ništa što bi za borbu utrošili.", TextEnglish = "Nor [is there blame] upon those who, when they came to you for you to take them along, you said, 'I can find nothing upon which to carry you.'[497] They turned back while their eyes overflowed with tears out of grief that they could not find something to spend [for the cause of Allāh].", TextGerman = "und (auch) nicht gegen diejenigen, die, als sie zu dir kamen, damit du ihnen gäbest, (worauf) sie mitreiten (könnten), und du sagtest: Ich finde nichts, worauf ich euch mitreiten lassen kann, sich abkehrten, während ihre Augen von Tränen überflossen aus Traurigkeit darüber, daß sie nichts fanden, was sie ausgeben (konnten)." });
                        surah9.VerseList.Add(new Verse { Number = 93, TextBosnian = "A ima razloga da se prigovara onima koji od tebe traže dozvolu da izostanu, a imućni su. Zadovoljavaju se da ostanu sa onima koji ne idu u boj, a Allah je njihova srca zapečatio, pa oni ne znaju.", TextEnglish = "The cause [for blame] is only upon those who ask permission of you while they are rich. They are satisfied to be with those who stay behind, and Allāh has sealed over their hearts, so they do not know.", TextGerman = "Eine Möglichkeit (zu belangen) gibt es nur gegen diejenigen, die dich um Erlaubnis bitten, obwohl sie reich sind. Sie waren damit zufrieden, (zusammen) mit den zurückbleibenden Frauen zu sein. Allah hat ihre Herzen versiegelt, so daß sie nicht Bescheid wissen." });
                        surah9.VerseList.Add(new Verse { Number = 94, TextBosnian = "Kad se među njih vratite, oni će vam se pravdati. Reci: 'Ne pravdajte se, jer mi vama ne vjerujemo, zato što nas je Allah o vama obavijestio. Allah i Njegov Poslanik vidjet će kako ćete postupati, zatim ćete biti ponovo vraćeni Onome Kojem je poznato ono što je čulima nedokučivo i ono što je pojavno, pa će vas On o onome što ste radili obavijestiti.'", TextEnglish = "They will make excuses to you when you have returned to them. Say, 'Make no excuse - never will we believe you. Allāh has already informed us of your news [i.e., affair]. And Allāh will observe your deeds, and [so will] His Messenger; then you will be taken back to the Knower of the unseen and the witnessed,[498] and He will inform you of what you used to do.'", TextGerman = "Sie werden sich bei euch entschuldigen, wenn ihr zu ihnen zurückkehrt. Sag: Entschuldigt euch nicht! Wir werden euch (doch) nicht glauben. Allah hat uns ja von den Nachrichten über euch kundgetan. Allah wird euer Tun sehen und (auch) Sein Gesandter. Hierauf werdet ihr zum Kenner des Verborgenen und des Offenbaren zurückgebracht werden, und dann wird Er euch kundtun, was ihr zu tun pflegtet." });
                        surah9.VerseList.Add(new Verse { Number = 95, TextBosnian = "Kad se među njih vratite, zaklinjat će vam se Allahom, samo da ih se okanite, pa okanite ih se. Oni su, zaista, pogan i prebivalište njihovo bit će Džehennem, kao kazna za ono što su radili.", TextEnglish = "They will swear by Allāh to you when you return to them that you would leave them alone. So leave them alone; indeed they are evil; and their refuge is Hell as recompense for what they had been earning.", TextGerman = "Sie werden euch bei Allah schwören, wenn ihr zu ihnen zurückgekehrt seid, damit ihr von ihnen ablaßt. So laßt von ihnen ab, denn sie sind ein Greuel! Ihr Zufluchtsort ist die Hölle als Lohn für das, was sie erworben haben." });
                        surah9.VerseList.Add(new Verse { Number = 96, TextBosnian = "Oni vam se zaklinju zato da biste bili zadovoljni njima. Ako vi budete zadovoljni njima, Allah sigurno nije zadovoljan ljudima buntovnicima.", TextEnglish = "They swear to you so that you might be satisfied with them. But if you should be satisfied with them - indeed, Allāh is not satisfied with a defiantly disobedient people.", TextGerman = "Sie schwören euch, damit ihr mit ihnen zufrieden seid. Und wenn ihr auch mit ihnen zufrieden seid, so ist Allah doch nicht zufrieden mit dem Volk der Frevler." });
                        surah9.VerseList.Add(new Verse { Number = 97, TextBosnian = "Beduini su u nevjerovanju i licemjerju žešći, i najpriličnije je da ne poznaju propise koje Allah Svome Poslaniku objavljuje. A Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "The bedouins are stronger in disbelief and hypocrisy and more likely not to know the limits of what [laws] Allāh has revealed to His Messenger. And Allāh is Knowing and Wise.", TextGerman = "Die Wüstenaraber sind mehr ungläubig und heuchlerischer, und es paßt eher zu ihnen, daß sie die Grenzen dessen, was Allah auf Seinen Gesandten (als Offenbarung) herabgesandt hat, nicht kennen. Und Allah ist Allwissend und Allweise." });
                        surah9.VerseList.Add(new Verse { Number = 98, TextBosnian = "Ima beduina koji ono što daju smatraju nametom i jedva čekaju da vas nesreće stignu - neka njih pogodi nesreća, a Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "And among the bedouins are some who consider what they spend as a loss[499] and await for you turns of misfortune. Upon them will be a misfortune of evil. And Allāh is Hearing and Knowing.", TextGerman = "Unter den Wüstenarabern gibt es (auch) manche, die das, was sie (als Spende) ausgeben, als erzwungene Zahlung ansehen und für euch die Schicksalswendungen abwarten. Gegen sie wird die böse Schicksalswendung sein. Allah ist Allhörend und Allwissend." });
                        surah9.VerseList.Add(new Verse { Number = 99, TextBosnian = "A ima beduina koji vjeruju u Allaha i u ahiret i koji smatraju da je ono što daju put da se Allahu približe i da Poslanikove dove zasluže. To im je, zaista, dobro djelo. Allah će ih milošću Svojom obasuti, Allah je, zaista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "But among the bedouins are some who believe in Allāh and the Last Day and consider what they spend as means of nearness to Allāh and of [obtaining] invocations of the Messenger. Unquestionably, it is a means of nearness for them. Allāh will admit them to His mercy. Indeed, Allāh is Forgiving and Merciful.", TextGerman = "Und unter den Wüstenarabern gibt es (aber auch) manche, die an Allah und den Jüngsten Tag glauben und das, was sie (als Spende) ausgeben, als Mittel der Annäherung zu Allah ansehen, und (auch) die Gebete des Gesandten. Es ist sicherlich für sie ein Mittel der Annäherung (zu Allah). Allah wird sie in Seine Barmherzigkeit eingehen lassen. Gewiß, Allah ist Allvergebend und Barmherzig." });
                        surah9.VerseList.Add(new Verse { Number = 100, TextBosnian = "Allah je zadovoljan prvim muslimanima, muhadžirima i ensarijama, i svima onima koji ih slijede dobra djela čineći. A i oni su zadovoljni Njime. Za njih je On pripremio džennetske bašče, kroz koje rijeke teku, i oni će vječno i zauvijek u njima boraviti. To je veliki uspjeh.", TextEnglish = "And the first forerunners [in the faith] among the Muhājireen[500] and the Anṣār[501] and those who followed them with good conduct - Allāh is pleased with them and they are pleased with Him, and He has prepared for them gardens beneath which rivers flow, wherein they will abide forever. That is the great attainment.", TextGerman = "Die vorausgeeilten Ersten von den Auswanderern und den Helfern und diejenigen, die ihnen auf beste Weise gefolgt sind - Allah hat Wohlgefallen an ihnen, und sie haben Wohlgefallen an Ihm. Und Er hat für sie Gärten bereitet, durcheilt von Bächen, ewig und auf immer darin zu bleiben; das ist der großartige Erfolg." });
                        surah9.VerseList.Add(new Verse { Number = 101, TextBosnian = "Među beduinima oko vas ima licemjera, a ima ih i među stanovnicima Medine, koji su u licemjerstvu uporni- ti ih ne poznaješ, ali ih Mi poznajemo. Njih ćemo na dvostruke muke staviti, a zatim će biti u veliku patnju vraćeni.", TextEnglish = "And among those around you of the bedouins are hypocrites, and [also] from the people of Madīnah. They have persisted in hypocrisy. You, [O Muḥammad], do not know them, [but] We know them. We will punish them twice [in this world]; then they will be returned to a great punishment.", TextGerman = "Unter den Wüstenarabern in eurer Umgebung gibt es Heuchler und (auch) unter den Bewohnern von al-Madina, die in der Heuchelei geübt sind. Du kennst sie nicht, aber Wir kennen sie. Wir werden sie zweimal strafen, und hierauf werden sie einer gewaltigen Strafe zugeführt werden." });
                        surah9.VerseList.Add(new Verse { Number = 102, TextBosnian = "A ima i drugih koji su grijehe svoje priznali, i koji su djela dobra s drugim koja su loša izmiješali; njima će Allah oprostiti - Allah je, zaista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And [there are] others who have acknowledged their sins. They had mixed [i.e., polluted] a righteous deed with another that was bad.[502] Perhaps Allāh will turn to them in forgiveness. Indeed, Allāh is Forgiving and Merciful.", TextGerman = "Und (es gibt) andere, die ihre Sünden bekennen. Sie haben eine rechtschaffene Tat mit einer anderen bösen vermischt. Vielleicht wird Allah ihre Reue annehmen. Gewiß, Allah ist Allvergebend und Barmherzig." });
                        surah9.VerseList.Add(new Verse { Number = 103, TextBosnian = "Uzmi od dobara njihovih zekat i sadaku, da ih njime očistiš i blagoslovljenim ih učiniš, i pomoli se za njih, molitva tvoja će ih, zaista, smiriti - a Allah je Onaj Koji sve čuje i sve zna.", TextEnglish = "Take, [O Muḥammad], from their wealth a charity by which you purify them and cause them increase, and invoke [Allāh's blessings] upon them. Indeed, your invocations are reassurance for them. And Allāh is Hearing and Knowing.", TextGerman = "Nimm von ihrem Besitz ein Almosen, mit dem du sie rein machst und läuterst, und bete für sie, denn dein Gebet ist für sie eine Beruhigung! Allah ist Allhörend und Allwissend." });
                        surah9.VerseList.Add(new Verse { Number = 104, TextBosnian = "Zar ne znaju oni da jedino Allah prima pokajanje od robova Svojih i da samo On prihvaća zekat i sadaku, i da je samo Allah stalni Primatelj pokajanja, Milostivi?", TextEnglish = "Do they not know that it is Allāh who accepts repentance from His servants and receives charities and that it is Allāh who is the Accepting of Repentance,[503] the Merciful?", TextGerman = "Wissen sie denn nicht, daß Allah es ist, Der die Reue von Seinen Dienern annimmt und die Almosen nimmt, und daß Allah der Reue-Annehmende und Barmherzige ist?" });
                        surah9.VerseList.Add(new Verse { Number = 105, TextBosnian = "I reci: 'Trudite se! Allah će trud vaš vidjeti, a i Poslanik Njegov i vjernici, i vi ćete biti vraćeni Onome Koji zna ono što je čulima nedokučivo i ono što je pojavno, pa će vas o onome što ste radili obavijestiti.'", TextEnglish = "And say, 'Do [as you will], for Allāh will see your deeds, and [so will] His Messenger and the believers. And you will be returned to the Knower of the unseen and the witnessed, and He will inform you of what you used to do.'", TextGerman = "Und sag: Wirkt! Allah wird euer Tun sehen, und (auch) Sein Gesandter und die Gläubigen. Und ihr werdet zum Kenner des Verborgenen und des Offenbaren zurückgebracht werden, und dann wird Er euch kundtun, was ihr zu tun pflegtet." });
                        surah9.VerseList.Add(new Verse { Number = 106, TextBosnian = "Ima i drugih koji su u neizvjesnosti da li će ih Allah kazniti ili će im oprostiti. AAllah je Onaj Koji sve zna i mudar je.", TextEnglish = "And [there are] others deferred until the command of Allāh - whether He will punish them or whether He will forgive them. And Allāh is Knowing and Wise.", TextGerman = "Und (wieder) andere werden zurückgestellt, bis die Anordnung Allahs (ergeht). Entweder straft Er sie, oder Er nimmt ihre Reue an. Allah ist Allwissend und Allweise." });
                        surah9.VerseList.Add(new Verse { Number = 107, TextBosnian = "A oni koji su džamiju sagradili da bi štetu nanijeli, i nevjerovanje osnažili, i razdor među vjernike unijeli, pripremajući je za onoga koji se protiv Allaha i Njegova Poslanika još prije borio, sigurno će se zaklinjati: 'Mi smo samo najbolje željeli, a Allah je svjedok da su oni sigurno lažljivci.'", TextEnglish = "And [there are] those [hypocrites] who took for themselves a mosque for causing harm and disbelief and division among the believers and as a station for whoever had warred against Allāh and His Messenger before. And they will surely swear, 'We intended only the best.' And Allāh testifies that indeed they are liars.", TextGerman = "Und (es) gibt (auch) diejenigen, die sich eine Gebetsstätte genommen haben in der Absicht der Schädigung und aus Unglauben und zur Spaltung der Gläubigen und zur Beobachtung für denjenigen, der zuvor gegen Allah und Seinen Gesandten Krieg geführt hat. Sie werden ganz gewiß schwören: Wir haben nur das Beste gewollt. Doch Allah bezeugt, daß sie fürwahr Lügner sind." });
                        surah9.VerseList.Add(new Verse { Number = 108, TextBosnian = "Ti u njoj nemoj nikada na namaz stati! Džamija čiji su temelji, već od prvog dana, postavljeni u strahu od Allaha, zaista više zaslužuje da u njoj na namaz staješ. U njoj su ljudi koji se vole često prati, a Allah voli one koji se mnogo čiste.", TextEnglish = "Do not stand [for prayer] within it - ever. A mosque founded on righteousness from the first day[504] is more worthy for you to stand in. Within it are men who love to purify themselves; and Allāh loves those who purify themselves.", TextGerman = "Stell dich niemals in ihr (zum Gebet) hin! Eine Gebetsstätte, die vom ersten Tag an auf die Gottesfurcht gegründet worden ist, hat wahrlich ein größeres Anrecht darauf, daß du dich in ihr hinstellst. In ihr sind Männer, die es lieben, sich zu reinigen. Und Allah liebt die sich Reinigenden." });
                        surah9.VerseList.Add(new Verse { Number = 109, TextBosnian = "Da li je bolji onaj koji je temelj zgrade svoje postavio u strahu od Allaha i u Njegovom zadovoljstvu, ili onaj koji je temelj zgrade svoje postavio na rub podlokane obale koja se nagnula, da se zajedno s njim u vatru džehennemsku sruši? A Allah neće uputiti na Pravi put zulumćare.", TextEnglish = "Then is one who laid the foundation of his building on righteousness [with fear] from Allāh and [seeking] His approval better or one who laid the foundation of his building on the edge of a bank about to collapse, so it collapsed with him into the fire of Hell? And Allāh does not guide the wrongdoing people.", TextGerman = "Ist derjenige, der seinen Bau auf Furcht vor Allah und (Sein) Wohlgefallen gegründet hat, besser oder der, der seinen Bau auf den Rand eines abstürzenden Hanges gegründet hat, so daß er mit ihm ins Feuer der Hölle abstürzt? Und Allah leitet das ungerechte Volk nicht recht." });
                        surah9.VerseList.Add(new Verse { Number = 110, TextBosnian = "Zgrada koju su oni sagradili stalno će unositi nemir u srca njihova, sve dok im srca ne popucaju - a Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "Their building which they built will not cease to be a [cause of] skepticism in their hearts until their hearts are cut [i.e., stopped]. And Allāh is Knowing and Wise.", TextGerman = "Ihr Bau, den sie errichtet haben, wird nicht aufhören, Zweifel (hervorzurufen), bis ihre Herzen in Stücke gerissen sind. Allah ist Allwissend und Allweise." });
                        surah9.VerseList.Add(new Verse { Number = 111, TextBosnian = "Allah je od vjernika kupio živote njihove i imetke njihove u zamjenu za Džennet koji će im dati. Oni će se na Allahovu putu boriti, pa ubijati i ginuti. On je to, zbilja, obećao u Tevratu i Indžilu i Kur'anu. A ko od Allaha dosljednije ispunjava obećanje svoje? Zato se radujte pogodbi svojoj koju ste s Njim ugovorili, i to je zasigurno uspjeh veliki.", TextEnglish = "Indeed, Allāh has purchased from the believers their lives and their properties [in exchange] for that they will have Paradise. They fight in the cause of Allāh, so they kill and are killed. [It is] a true promise [binding] upon Him in the Torah and the Gospel and the Qur’ān. And who is truer to his covenant than Allāh? So rejoice in your transaction which you have contracted. And it is that which is the great attainment.", TextGerman = "Allah hat von den Gläubigen ihre eigene Person und ihren Besitz dafür erkauft, daß ihnen der (Paradies)garten gehört: Sie kämpfen auf Allahs Weg, und so töten sie und werden getötet. (Das ist) ein für Ihn bindendes Versprechen in Wahrheit in der Tora, dem Evangelium und dem Qur’an. Und wer ist treuer in (der Einhaltung) seiner Abmachung als Allah? So freut euch über das Kaufgeschäft, das ihr abgeschlossen habt, denn das ist der großartige Erfolg!" });
                        surah9.VerseList.Add(new Verse { Number = 112, TextBosnian = "Oni se kaju, i Njemu ibadet čine, i Njega hvale, i poste, i ruku' i sedždu obavljaju, i traže da se čine dobra djela, a od nevaljalih odvraćaju i Allahovih se propisa pridržavaju. A vjernike obraduj!", TextEnglish = "[Such believers are] the repentant, the worshippers, the praisers [of Allāh], the travelers [for His cause], those who bow and prostrate [in prayer], those who enjoin what is right and forbid what is wrong, and those who observe the limits [set by] Allāh. And give good tidings to the believers.", TextGerman = "(... für die Gläubigen,) die in Reue Umkehrenden, die (Allah) Dienenden, die Lobenden, die (auf Allahs Weg) Umherziehenden, die sich Verbeugenden, die sich Niederwerfenden, die das Rechte Gebietenden und das Verwerfliche Verbietenden, die Allahs Grenzen Beachtenden ... und verkünde den Gläubigen frohe Botschaft." });
                        surah9.VerseList.Add(new Verse { Number = 113, TextBosnian = "Vjerovjesniku i vjernicima nije dopušteno da mole oprost za višebošce, makar im bili i rod najbliži, kad im je jasno da će oni stanovnici u Džehennemu biti.", TextEnglish = "It is not for the Prophet and those who have believed to ask forgiveness for the polytheists, even if they were relatives, after it has become clear to them that they are companions of Hellfire.", TextGerman = "Dem Propheten und denjenigen, die glauben, steht es nicht zu, für die Götzendiener um Vergebung zu bitten, auch wenn es Verwandte wären, nachdem es ihnen klargeworden ist, daß sie Insassen des Höllenbrandes sein werden." });
                        surah9.VerseList.Add(new Verse { Number = 114, TextBosnian = "A što je Ibrahim tražio oprost za svoga oca, bilo je to samo zbog obećanja koje mu je dao. A čim mu je bilo jasno da je on Allahov neprijatelj, on ga se odrekao. Ibrahim je, doista, mnogo Allaha molio i obazriv bio.", TextEnglish = "And the request of forgiveness of Abraham for his father was only because of a promise he had made to him. But when it became apparent to him [i.e., Abraham] that he [i.e., the father] was an enemy to Allāh, he disassociated himself from him. Indeed was Abraham compassionate and patient.", TextGerman = "Und die Bitte Ibrahims um Vergebung für seinen Vater war nur wegen eines Versprechens, das er ihm gegeben hatte. Als es ihm aber klarwurde, daß er ein Feind Allahs war, sagte er sich von ihm los. Ibrahim war fürwahr weichherzig und nachsichtig." });
                        surah9.VerseList.Add(new Verse { Number = 115, TextBosnian = "Allah neće učiniti da zaluta narod koji je na Pravi put uputio prije nego što im objasni ono čega treba da se čuvaju; Allah, zaista, sve dobro zna.", TextEnglish = "And Allāh would not let a people stray after He has guided them until He makes clear to them what they should avoid. Indeed, Allāh is Knowing of all things.", TextGerman = "Nimmer wird Allah Leute in die Irre gehen lassen, nachdem Er sie rechtgeleitet hat, bis Er ihnen darüber Klarheit gegeben hat, wovor sie sich hüten sollen. Gewiß, Allah weiß über alles Bescheid." });
                        surah9.VerseList.Add(new Verse { Number = 116, TextBosnian = "Zaista je Allahova vlast na nebesima i na Zemlji, On život i smrt daje, i vi osim Allaha nemate ni zaštitnika ni pomagača.", TextEnglish = "Indeed, to Allāh belongs the dominion of the heavens and the earth; He gives life and causes death. And you have not besides Allāh any protector or any helper.", TextGerman = "Allah hat die Herrschaft über die Himmel und die Erde. Er macht lebendig und läßt sterben. Ihr habt außer Allah weder Schutzherrn noch Helfer." });
                        surah9.VerseList.Add(new Verse { Number = 117, TextBosnian = "Allah je oprostio Vjerovjesniku, i muhadžirima i ensarijama, koji su ga u teškom času slijedili, u vrijeme kada se srca nekih od njih zamalo nisu pokolebala: On je poslije i njima oprostio. On je prema njima, zaista, milosrdan i milostiv.", TextEnglish = "Allāh has already forgiven the Prophet and the Muhājireen and the Anṣār who followed him in the hour of difficulty after the hearts of a party of them had almost inclined [to doubt], and then He forgave them. Indeed, He was to them Kind and Merciful.", TextGerman = "Allah hat die Reue des Propheten, der Auswanderer und der Helfer angenommen, die ihm in der Stunde der Bedrängnis folgten, nachdem die Herzen einer Gruppe von ihnen beinahe abgeschweift wären. Hierauf hat Er ihre Reue angenommen - gewiß, Er ist zu ihnen Gnädig und Barmherzig -," });
                        surah9.VerseList.Add(new Verse { Number = 118, TextBosnian = "A i onoj trojici čiji je slučaj bio odgođen tako da im je zemlja, koliko god da je bila prostrana, postala tijesna, i bilo im se stisnula u dušama njihovim, i vidjeli su da nema utočišta od Allaha nego samo u Njega. On je poslije i njima oprostio da bi se i ubuduće kajali, a Allah je, uistinu, stalni Primatelj pokajanja, Milostivi.", TextEnglish = "And [He also forgave] the three who were left alone [i.e., boycotted, regretting their error] to the point that the earth closed in on them in spite of its vastness[505] and their souls confined [i.e., anguished] them and they were certain that there is no refuge from Allāh except in Him. Then He turned to them so they could repent. Indeed, Allāh is the Accepting of Repentance,[506] the Merciful.", TextGerman = "und (die Reue) der Dreien, die zurückgelassen wurden, bis die Erde ihnen eng wurde bei all ihrer Weite und ihre Seelen ihnen eng wurden und sie wußten, daß es vor Allah keine (andere) Zuflucht gibt als zu Ihm. Hierauf wandte Er Sich ihnen verzeihend zu, damit sie bereuen. Gewiß, Allah ist der Reue-Annehmende und Barmherzige." });
                        surah9.VerseList.Add(new Verse { Number = 119, TextBosnian = "O vi koji vjerujete, bojte se Allaha i budite s onima koji su iskreni!", TextEnglish = "O you who have believed, fear Allāh and be with those who are true.", TextGerman = "O ihr, die ihr glaubt, fürchtet Allah und seid mit den Wahrhaftigen!" });
                        surah9.VerseList.Add(new Verse { Number = 120, TextBosnian = "Nije trebalo da stanovnici Medine ni beduini u njegovoj blizini iza Allahova Poslanika izostanu i da svoj život njegovu životu pretpostave jer njih neće zadesiti ni žeđ, ni umor, ni glad na Allahovu putu, niti će stupiti na neko mjesto koje će nevjernike naljutiti, niti će ikakvu dobit nad neprijateljem ostvariti, a da im to sve neće kao dobro djelo upisano biti; Allah, zaista, neće dopustiti da propadne nagrada onima koji čine dobro.", TextEnglish = "It was not [proper] for the people of Madīnah and those surrounding them of the bedouins that they remain behind after [the departure of] the Messenger of Allāh or that they prefer themselves over his self.[507] That is because they are not afflicted by thirst or fatigue or hunger in the cause of Allāh, nor do they tread on any ground that enrages the disbelievers, nor do they inflict upon an enemy any infliction but that it is registered for them as a righteous deed. Indeed, Allāh does not allow to be lost the reward of the doers of good.", TextGerman = "Es steht den Bewohnern von al-Madina und den Wüstenarabern in ihrer Umgebung nicht zu, hinter dem Gesandten Allahs zurückzubleiben und sich selbst (und ihr Leben) ihm vorzuziehen. Dies (ist), weil sie weder Durst noch Mühsal noch Hunger auf Allahs Weg trifft, noch sie einen Schritt unternehmen, der die Ungläubigen ergrimmen läßt, noch einem Feind etwas Schlimmes zufügen, ohne daß ihnen dafür eine rechtschaffene Tat aufgeschrieben würde. Gewiß, Allah läßt den Lohn der Gutes Tuenden nicht verlorengehen." });
                        surah9.VerseList.Add(new Verse { Number = 121, TextBosnian = "I neće dati nikakav prilog, ni mali ni veliki, niti će ikakvu dolinu prevaliti, a da im to neće zapisano biti, da bi ih Allah nagradio za ono dobro što su radili.", TextEnglish = "Nor do they spend an expenditure, small or large, or cross a valley but that it is registered for them that Allāh may reward them for the best of what they were doing.", TextGerman = "Auch geben sie keine Spende aus, ob klein oder groß, noch durchqueren sie ein Tal, ohne daß es ihnen (als gute Tat) aufgeschrieben würde, damit Allah ihnen das Beste vergelte von dem, was sie getan haben." });
                        surah9.VerseList.Add(new Verse { Number = 122, TextBosnian = "Nije trebalo da svi vjernici odjednom idu u boj. Neka se po nekoliko njih iz svake zajednice potrudi da se upute u vjerske nauke i neka upozoravaju svoj narod kada im se vrate, da bi na oprezu bili.", TextEnglish = "And it is not for the believers to go forth [to battle] all at once. For there should separate from every division of them a group [remaining] to obtain understanding in the religion and warn [i.e., advise] their people when they return to them that they might be cautious.", TextGerman = "Es steht den Gläubigen nicht zu, allesamt auszurücken. Wenn doch von jeder Gruppe von ihnen ein Teil ausrücken würde, um (mehr) von der Religion zu erlernen und um ihre Leute zu warnen, wenn sie zu ihnen zurückkehren, auf daß sie sich vorsehen mögen." });
                        surah9.VerseList.Add(new Verse { Number = 123, TextBosnian = "O vi koji vjerujete, borite se protiv nevjernika koji su u blizini vašoj i neka oni osjete vašu strogost, i znajte da je Allah s bogobojaznima.", TextEnglish = "O you who have believed, fight against those adjacent to you of the disbelievers and let them find in you harshness. And know that Allāh is with the righteous.", TextGerman = "O die ihr glaubt, kämpft gegen diejenigen, die in eurer Nähe sind von den Ungläubigen! Sie sollen in euch Härte vorfinden. Und wisset, daß Allah mit den Gottesfürchtigen ist!" });
                        surah9.VerseList.Add(new Verse { Number = 124, TextBosnian = "A kad bude objavljena neka sura, ima ih koji govore: 'Kome je od vas ova sura povećala Vjerovanje? što se tiče Vjernika, njima je povećala vjerovanje i oni se raduju.", TextEnglish = "And whenever a sūrah is revealed, there are among them [i.e., the hypocrites] those who say, 'Which of you has this increased in faith?' As for those who believed, it has increased them in faith, while they are rejoicing.", TextGerman = "Wenn eine Sura (als Offenbarung) herabgesandt wird, dann gibt es unter ihnen manche, die sagen: Wem von euch hat dies(e Sura) seinen Glauben vermehrt? Was nun diejenigen angeht, die glauben, so hat sie ihren Glauben vermehrt, und sie freuen sich über die frohe Botschaft." });
                        surah9.VerseList.Add(new Verse { Number = 125, TextBosnian = "A što se tiče onih čija su srca bolesna, ona im je sumnju na sumnju koju već imaju dodala i oni kao nevjernici umiru.", TextEnglish = "But as for those in whose hearts is disease, it has [only] increased them in evil [in addition] to their evil.[508] And they will have died while they are disbelievers.", TextGerman = "Was aber diejenigen angeht, in deren Herzen Krankheit ist, so fügt sie ihrem Greuel noch (weiteren) Greuel hinzu, und sie sterben als Ungläubige." });
                        surah9.VerseList.Add(new Verse { Number = 126, TextBosnian = "Zar oni ne vide da svake godine jedanput ili dvaput u iskušenje padaju, pa opet, oni se ne kaju, niti se prisjećaju i pouku ne uzimaju!", TextEnglish = "Do they not see that they are tried every year once or twice but then they do not repent nor do they remember?", TextGerman = "Sehen sie denn nicht, daß sie in jedem Jahr einmal oder zweimal einer Prüfung unterzogen werden? Aber hierauf bereuen sie nicht, und sie bedenken nicht." });
                        surah9.VerseList.Add(new Verse { Number = 127, TextBosnian = "A kad bude objavljena koja sura, samo se zagledaju: 'Da li vas ko vidi?', i onda se udaljuju. Allah je srca njihova skrenuo, zato što su oni ljudi koji ne shvataju.", TextEnglish = "And whenever a sūrah is revealed, they look at each other, [as if saying], 'Does anyone see you?' and then they dismiss themselves. Allāh has dismissed their hearts because they are a people who do not understand.", TextGerman = "Wenn eine Sura (als Offenbarung) herabgesandt wird, schauen sie einander an: Sieht euch irgend jemand? Hierauf wenden sie sich ab. Allah hat ihre Herzen abgewendet, weil sie Leute sind, die nicht verstehen." });
                        surah9.VerseList.Add(new Verse { Number = 128, TextBosnian = "Došao vam je Poslanik, jedan od vas, teško mu je što ćete na muke udariti, jedva čeka da Pravim putem pođete, a prema vjernicima je milosrdan i milostiv.", TextEnglish = "There has certainly come to you a Messenger from among yourselves. Grievous to him is what you suffer; [he is] concerned over you [i.e., your guidance] and to the believers is kind and merciful.", TextGerman = "Zu euch ist nunmehr ein Gesandter aus euren eigenen Reihen gekommen. Bedrückend ist es für ihn, wenn ihr in Bedrängnis seid, (er ist) eifrig um euch bestrebt, zu den Gläubigen gnadenvoll und barmherzig." });
                        surah9.VerseList.Add(new Verse { Number = 129, TextBosnian = "A ako oni glavu okrenu, ti reci: 'Meni je dovoljan Allah, nema boga osim Njega; Samo se u Njega uzdam, On je Gospodar Arša veličanstvenoga!'", TextEnglish = "But if they turn away, [O Muḥammad], say, 'Sufficient for me is Allāh; there is no deity except Him. On Him I have relied, and He is the Lord of the Great Throne.'", TextGerman = "Wenn sie sich aber abkehren, dann sag: Meine Genüge ist Allah. Es gibt keinen Gott außer Ihm. Auf Ihn verlasse ich mich, und Er ist der Herr des gewaltigen Thrones." });
                        result = surah9;
                        break;

                    #endregion

                    // 
                    #region 10 Yunus
                    case 10:
                        Surah surah10 = new Surah(); surah10.Name = Constants.Surah10Name; surah10.Number = 10; surah10.VerseList = new List<Verse>();
                        surah10.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah10.VerseList.Add(new Verse { Number = 1, TextBosnian = "Elif-lam-ra. Ovo su ajeti Mudre Knjige.", TextEnglish = "Alif, Lām, Rā.[510] These are the verses of the wise[511] Book.", TextGerman = "Alif-Lam-Ra. Dies sind die Zeichen des weisen Buches." });
                        surah10.VerseList.Add(new Verse { Number = 2, TextBosnian = "Zašto je čudno ljudima što Mi objavljujemo jednom između njih: 'Opominji ljude! A one koji vjeruju obraduj da ih iskrena djela koja su uradili kod Gospodara njihova čekaju.' Ali, nevjernici govore: 'Ovaj je, zaista, očiti čarobnjak!'", TextEnglish = "Have the people been amazed that We revealed [revelation] to a man from among them, [saying], 'Warn mankind and give good tidings to those who believe that they will have a [firm] precedence of honor[512] with their Lord'? [But] the disbelievers say, 'Indeed, this is an obvious magician.'", TextGerman = "Ist es denn für die Menschen verwunderlich, daß Wir einem Mann von ihnen (als Offenbarung) eingegeben haben: Warne die Menschen und verkünde denen, die glauben, die frohe Botschaft, daß sie bei ihrem Herrn einen wahrhaftigen Stand haben? Die Ungläubigen sagen: Das ist wahrlich ein deutlicher Zauberer." });
                        surah10.VerseList.Add(new Verse { Number = 3, TextBosnian = "Gospodar vaš je Allah, Koji je nebesa i Zemlju za šest dana stvorio a onda se nad Aršom uzvisio i Koji sve određuje. Niko se neće moći zauzimati ni za koga bez dopuštenja Njegova. Eto, to vam je Allah, Gospodar vaš, pa Njemu ibadet činite! Zašto se ne prisjetite i pouku ne uzmete?", TextEnglish = "Indeed, your Lord is Allāh, who created the heavens and the earth in six days and then established Himself above the Throne,[513] arranging the matter [of His creation]. There is no intercessor except after His permission. That is Allāh, your Lord, so worship Him. Then will you not remember?", TextGerman = "Gewiß, euer Herr ist Allah, Der die Himmel und die Erde in sechs Tagen erschuf und Sich hierauf über den Thron erhob. Er regelt die Angelegenheit. Es gibt keinen Fürsprecher außer nach Seiner Erlaubnis. Dies ist doch Allah, euer Herr, so dient Ihm! Bedenkt ihr denn nicht?" });
                        surah10.VerseList.Add(new Verse { Number = 4, TextBosnian = "Njemu ćete se svi vratiti - Allahovo je obećanje istinito; On, doista, započinje stvaranje - iz ničega stvara, On će poslije to ponoviti, da bi pravedno nagradio one koji budu vjerovali i dobra djela činili. A one koji ne budu vjerovali čeka piće od ključale vode i patnja bolna, zato što su nevjernici bili.", TextEnglish = "To Him is your return all together. [It is] the promise of Allāh [which is] truth. Indeed, He begins the [process of] creation and then repeats it that He may reward those who have believed and done righteous deeds, in justice. But those who disbelieved will have a drink of scalding water and a painful punishment for what they used to deny.", TextGerman = "Zu Ihm wird eure aller Rückkehr sein. (Das ist) Allahs Versprechen in Wahrheit. Er macht die Schöpfung am Anfang und wiederholt sie hierauf, um (es) denjenigen, die glauben und rechtschaffene Werke tun, in Gerechtigkeit zu vergelten. Für diejenigen, die ungläubig sind, wird es ein Getränk aus heißem Wasser und schmerzhafte Strafe geben dafür, daß sie ungläubig gewesen sind." });
                        surah10.VerseList.Add(new Verse { Number = 5, TextBosnian = "On je Taj Koji je Sunce izvorom svjetlosti učinio a Mjesec sjajnim, i položaje mu odredio da biste znali broj godina i računanje. Allah je to s Istinom stvorio. On potanko izlaže ajete i znakove ljudima koji znaju.", TextEnglish = "It is He who made the sun a shining light and the moon a derived light and determined for it phases - that you may know the number of years and account [of time]. Allāh has not created this except in truth. He details the signs for a people who know.", TextGerman = "Er ist es, Der die Sonne zu einer Leuchte und den Mond zu einem Licht gemacht und ihm Himmelspunkte zugemessen hat, damit ihr die Zahl der Jahre und die (Zeit)rechnung wißt. Allah hat dies ja nur in Wahrheit erschaffen. Er legt die Zeichen ausführlich dar für Leute, die Bescheid wissen." });
                        surah10.VerseList.Add(new Verse { Number = 6, TextBosnian = "U smjeni noći i dana i u onom što je Allah na nebesima i na Zemlji stvorio zaista postoje znakovi za ljude koji se čuvaju.", TextEnglish = "Indeed, in the alternation of the night and the day and [in] what Allāh has created in the heavens and the earth are signs for a people who fear Allāh.", TextGerman = "Gewiß, in dem Unterschied von Nacht und Tag und in dem, was Allah in den Himmeln und auf der Erde erschaffen hat, sind wahrlich Zeichen für Leute, die gottesfürchtig sind." });
                        surah10.VerseList.Add(new Verse { Number = 7, TextBosnian = "Zaista onima koji ne očekuju da će pred Nas stati i koji su zadovoljni životom na ovom svijetu, koji su u njemu smireni, i onima koji su prema ajetima i znakovima našim ravnodušni,", TextEnglish = "Indeed, those who do not expect the meeting with Us and are satisfied with the life of this world and feel secure therein and those who are heedless of Our signs -", TextGerman = "Diejenigen nun, die nicht die Begegnung mit Uns erwarten und mit dem diesseitigen Leben zufrieden sind und darin Ruhe finden, und die gegenüber Unseren Zeichen unachtsam sind," });
                        surah10.VerseList.Add(new Verse { Number = 8, TextBosnian = "njima prebivalište bit će Vatra, zbog onoga što su radili.", TextEnglish = "For those their refuge will be the Fire because of what they used to earn.", TextGerman = "deren Zufluchtsort wird das (Höllen)feuer sein für das, was sie erworben haben." });
                        surah10.VerseList.Add(new Verse { Number = 9, TextBosnian = "One koji vjeruju i čine dobra djela Gospodar njihov će, zaista, zbog njihovog vjerovanja uputiti. Rijeke će ispod njih u džennetima uživanja teći.", TextEnglish = "Indeed, those who have believed and done righteous deeds - their Lord will guide them because of their faith. Beneath them rivers will flow in the Gardens of Pleasure.", TextGerman = "Diejenigen aber, die glauben und rechtschaffene Werke tun, leitet ihr Herr wegen ihres Glaubens recht. Unter ihnen werden Flüsse strömen in den Gärten der Wonne." });
                        surah10.VerseList.Add(new Verse { Number = 10, TextBosnian = "Molitva njihova bit će u njima: 'Uzvišen neka si, Allahu', pozdrav njihov: 'Selam', a zadnje što će u molitvi biti: 'Hvala Allahu, Gospodaru svjetova.'", TextEnglish = "Their call therein will be, 'Exalted are You, O Allāh,' and their greeting therein will be, 'Peace.' And the last of their call will be, 'Praise to Allāh, Lord of the worlds!'", TextGerman = "Ihr Ausruf darin wird sein: Preis sei Dir, o Allah! und ihr Gruß darin: Friede! und ihr abschließender Ausruf: (Alles) Lob gehört Allah, dem Herrn der Weltenbewohner!" });
                        surah10.VerseList.Add(new Verse { Number = 11, TextBosnian = "Da Allah ljudima daje zlo onako brzo kao što im se odaziva kad traže dobro, oni bi, uistinu, stradali. A Mi, ipak, ostavljamo da u osionosti svojoj lutaju oni koji ne vjeruju da će pred Nas stati.", TextEnglish = "And if Allāh was to hasten for the people the evil [they invoke][514] as He hastens for them the good, their term would have been ended for them.[515] But We leave the ones who do not expect the meeting with Us, in their transgression, wandering blindly.", TextGerman = "Wenn Allah den Menschen das Schlechte so beschleunigen würde, wie sie das Gute zu beschleunigen wünschen, wäre ihre Frist für sie bereits bestimmt. Aber Wir lassen diejenigen, die nicht die Begegnung mit Uns erwarten, in ihrer Auflehnung umherirren." });
                        surah10.VerseList.Add(new Verse { Number = 12, TextBosnian = "Kada čovjeka snađe nevolja, on Nam se moli ležeći, ili sjedeći, ili stojeći. A čim mu nevolju otklonimo, on nastavlja kao da Nam se nije ni obraćao molbom zbog nevolje koja ga je zadesila. Tako je onima koji pretjeruju uljepšano ono što radiše.", TextEnglish = "And when affliction touches man, he calls upon Us, whether lying on his side or sitting or standing; but when We remove from him his affliction, he continues [in disobedience] as if he had never called upon Us to [remove] an affliction that touched him. Thus is made pleasing to the transgressors that which they have been doing.", TextGerman = "Und wenn dem Menschen Unheil widerfährt, ruft er Uns auf der Seite (liegend), im Sitzen oder Stehen an. Wenn Wir ihm aber sein Unheil hinweggenommen haben, geht er vorbei, als hätte er Uns nicht wegen eines Unheils, das ihm widerfahren ist, angerufen. So ist den Maßlosen ausgeschmückt, was sie tun." });
                        surah10.VerseList.Add(new Verse { Number = 13, TextBosnian = "Mi smo narode prije vas uništavali pošto bi zulum činili i poslanici njihovi im jasne dokaze donosili. Ali, oni ionako ne bi vjerovali. Tako Mi kažnjavamo ljude prestupnike.", TextEnglish = "And We had already destroyed generations before you when they wronged, and their messengers had come to them with clear proofs, but they were not to believe. Thus do We recompense the criminal people.", TextGerman = "Wir haben bereits die Geschlechter vor euch vernichtet, als sie Unrecht taten, als ihre Gesandten mit den klaren Beweisen zu ihnen kamen, und sie nicht glauben mochten. So vergelten Wir dem übeltätigen Volk." });
                        surah10.VerseList.Add(new Verse { Number = 14, TextBosnian = "Zatim smo vas, poslije njih, nasljednicima na Zemlji učinili, da bismo vidjeli kako ćete postupati.", TextEnglish = "Then We made you successors in the land after them so that We may observe how you will do.", TextGerman = "Hierauf haben Wir euch nach ihnen zu Nachfolgern (auf) der Erde gemacht, um zu schauen, wie ihr handeln würdet." });
                        surah10.VerseList.Add(new Verse { Number = 15, TextBosnian = "A kad im se uče ajeti Naši jasni, onda govore oni koji ne vjeruju da će pred Nas stati: 'Donesi ti kakav drugi Kur'an, ili ga izmijeni!' Reci: 'Nije moje da ga ja sam od sebe mijenjam, ja slijedim samo ono što mi se objavljuje, ja se bojim, ako svome Gospodaru nepokoran budem, patnje na Velikom danu.'", TextEnglish = "And when Our verses are recited to them as clear evidences, those who do not expect the meeting with Us say, 'Bring us a Qur’ān other than this or change it.' Say, [O Muḥammad], 'It is not for me to change it on my own accord. I only follow what is revealed to me. Indeed I fear, if I should disobey my Lord, the punishment of a tremendous Day.'", TextGerman = "Wenn ihnen Unsere Zeichen als klare Beweise verlesen werden, sagen diejenigen, die nicht die Begegnung mit Uns erwarten: Bringe einen anderen Qur’an als diesen oder ändere ihn ab. Sag: Es steht mir nicht zu, ihn von mir selbst aus abzuändern. Ich folge nur dem, was mir (als Offenbarung) eingegeben wird. Gewiß, ich fürchte, wenn ich mich meinem Herrn widersetze, die Strafe eines gewaltigen Tages." });
                        surah10.VerseList.Add(new Verse { Number = 16, TextBosnian = "Reci: 'Da Allah nije htio, ja vam ga ne bih učio niti bi vas On njime podučio. Ja sam prije poslanstva dugo među vama boravio - zar ne razumijete?'", TextEnglish = "Say, 'If Allāh had willed, I would not have recited it to you, nor would He have made it known to you, for I had remained among you a lifetime before it.[516] Then will you not reason?'", TextGerman = "Sag: Wenn Allah wollte, würde ich ihn euch nicht verlesen, und Er würde euch nicht davon Kenntnis geben. Ich habe doch vordem ein Leben lang unter euch verweilt. Begreift ihr denn nicht?" });
                        surah10.VerseList.Add(new Verse { Number = 17, TextBosnian = "Pa ima li onda većeg zulumćara od onoga koji o Allahu laži iznosi ili koji Njegove ajete i znakove smatra neistinitim? Prestupnici, doista, neće uspjeti!", TextEnglish = "So who is more unjust than he who invents a lie about Allāh or denies His signs? Indeed, the criminals will not succeed.", TextGerman = "Wer ist ungerechter, als wer gegen Allah eine Lüge ersinnt oder Seine Zeichen für Lüge erklärt? Gewiß, den Übeltätern wird es nicht wohl ergehen." });
                        surah10.VerseList.Add(new Verse { Number = 18, TextBosnian = "Oni, pored Allaha, obožavaju nešto što im ne može ni nauditi niti im može kakvu korist pribaviti, i govore: 'Ovo su naši zagovornici kod Allaha.' Reci: 'Zar da Allaha obavještavate da na nebesima i na Zemlji postoji nešto što On ne zna da postoji?!' Uzvišen je On i vrlo visoko u odnosu na ono što Njemu u obožavanju pridružuju.", TextEnglish = "And they worship other than Allāh that which neither harms them nor benefits them, and they say, 'These are our intercessors with Allāh.' Say, 'Do you inform Allāh of something He does not know in the heavens or on the earth?' Exalted is He and high above what they associate with Him.", TextGerman = "Sie dienen anstatt Allahs, was ihnen weder schadet noch nützt, und sagen: Das sind unsere Fürsprecher bei Allah. Sag: Wollt ihr denn Allah kundtun, was Er nicht kennt, weder in den Himmeln noch auf der Erde? Preis sei Ihm! Erhaben ist Er über das, was sie (ihm) beigesellen." });
                        surah10.VerseList.Add(new Verse { Number = 19, TextBosnian = "Ljudi su bili samo jedna zajednica, a onda su se razdvojili. A da nije riječi ranije izrečene od Gospodara tvoga, ovima bi već bilo presuđeno o onome u čemu se razilaze.", TextEnglish = "And mankind was not but one community [united in religion], but [then] they differed. And if not for a word[517] that preceded from your Lord, it would have been judged between them [immediately] concerning that over which they differ.", TextGerman = "Die Menschen waren nur eine einzige Gemeinschaft. Dann wurden sie uneinig. Wenn es nicht ein früher ergangenes Wort von deinem Herrn gäbe, so wäre zwischen ihnen wahrlich entschieden worden über das, worüber sie uneinig sind." });
                        surah10.VerseList.Add(new Verse { Number = 20, TextBosnian = "Oni govore: 'Zašto mu Gospodar njegov ne pošalje kakav znak?' Ti reci: 'Samo Allah zna ono što je čulima nedokučivo, pa pričekajte, i ja ću, zaista, s vama ću se u one što čekaju svrstati.'", TextEnglish = "And they say, 'Why is a sign not sent down to him from his Lord?' So say, 'The unseen is only for Allāh [to administer], so wait; indeed, I am with you among those who wait.'", TextGerman = "Und sie sagen: Wenn doch ein Zeichen von seinem Herrn auf ihn herabgesandt würde! Sag: Das Verborgene ist nur Allahs. So wartet ab! Ich gehöre mit euch zu denjenigen, die abwarten." });
                        surah10.VerseList.Add(new Verse { Number = 21, TextBosnian = "A kada Mi dopustimo da osjete milost poslije nevolje koja ih snađe, oni opet u vezi s ajetima i znakovima Našim lukavstvo smišljaju. Reci: 'Allah je u lukavstvu najbrži; izaslanici Naši, ono što vi ispletkarite, doista zapisuju.'", TextEnglish = "And when We give the people a taste of mercy after adversity has touched them, at once they conspire against Our verses. Say, 'Allāh is swifter in strategy.' Indeed, Our messengers [i.e., angels] record that which you conspire.", TextGerman = "Wenn Wir die Menschen Barmherzigkeit kosten lassen nach Leid, das ihnen widerfuhr, schmieden sie gleich Ränke gegen Unsere Zeichen. Sag: Allah schmiedet noch schneller Ränke. Gewiß, Unsere Gesandten schreiben auf, was ihr an Ränken schmiedet." });
                        surah10.VerseList.Add(new Verse { Number = 22, TextBosnian = "On je Taj Koji vam omogućava da kopnom i morem putujete, pa kad ste u lađama, i kad one, uz blag povjetarac zaplove s putnicima, te se oni obraduju tome, naiđe silan vjetar i valovi navale na njih sa svih strana i oni se uvjere da će nastradati, iskreno se mole Allahu: 'Ako nas iz ovoga izbaviš, sigurno ćemo u zahvalne spadati.'", TextEnglish = "It is He who enables you to travel on land and sea until, when you are in ships and they sail with them[518] by a good wind and they rejoice therein, there comes a storm wind and the waves come upon them from every place and they expect to be engulfed, they supplicate Allāh, sincere to Him in religion, 'If You should save us from this, we will surely be among the thankful.'", TextGerman = "Er ist es, Der euch auf dem Festland und dem Meer reisen läßt. Wenn ihr dann auf den Schiffen seid und diese mit ihnen bei einem guten Wind dahinfahren und sie froh darüber sind, ein stürmischer Wind darüber kommt, die Wogen von überall über sie kommen, und sie meinen, daß sie rings umgeben werden, (dann) rufen sie Allah an, (wobei sie) Ihm gegenüber aufrichtig in der Religion (sind): Wenn Du uns nur hieraus rettest, werden wir ganz gewiß zu den Dankbaren gehören!" });
                        surah10.VerseList.Add(new Verse { Number = 23, TextBosnian = "A kad ih On izbavi, odmah potpuno neosnovano na Zemlji nasilje čine. O ljudi, vaše nasilje koje činite radi užitka u životu dunjalučkom samo vama šteti; Nama ćete se poslije vratiti i Mi ćemo vas o onom što ste radili obavijestiti.", TextEnglish = "But when He saves them, at once they commit injustice[519] upon the earth without right. O mankind, your injustice is only against yourselves, [being merely] the enjoyment of worldly life. Then to Us is your return, and We will inform you of what you used to do.", TextGerman = "Aber wenn Er sie gerettet hat, fangen sie sogleich an, ohne Recht auf der Erde gewalttätig zu handeln. O ihr Menschen, eure Gewalttätigkeit richtet sich doch nur gegen euch selbst. (Es ist doch nur) der Genuß des diesseitigen Lebens. Hierauf wird eure Rückkehr zu Uns sein, und Wir werden euch kundtun, was ihr zu tun pflegtet." });
                        surah10.VerseList.Add(new Verse { Number = 24, TextBosnian = "Život na dunjaluku sličan je bilju zemaljskom na koje Mi spustimo s neba vodu s kojim se ona izmiješa, kojim se onda hrane ljudi i stoka. Pa kad se Zemlja ukrasi svojim ruhom i okiti, i kad stanovnici njezini pomisle da oni time raspolažu, dođe odredba Naša, noću ili danju, i Mi to pokosimo kao da prije ničega nije ni bilo. Eto, tako Mi potanko izlažemo ajete i znakove ljudima koji razmišljaju.", TextEnglish = "The example of [this] worldly life is but like rain which We have sent down from the sky that the plants of the earth absorb - [those] from which men and livestock eat - until, when the earth has taken on its adornment and is beautified and its people suppose that they have capability over it, there comes to it Our command by night or by day, and We make it as a harvest,[520] as if it had not flourished yesterday. Thus do We explain in detail the signs for a people who give thought.", TextGerman = "Das Gleichnis des diesseitigen Lebens ist nur wie Wasser, das Wir vom Himmel hinabsenden, worauf das Gewächs der Erde, von dem die Menschen und das Vieh verzehren, sich damit vermischt, bis dann, wenn die Erde ihren Prunk angenommen hat und sich geschmückt hat und ihre Bewohner meinen, daß sie Macht über sie hätten, kommt Unser Befehl über sie bei Nacht oder bei Tag, und da lassen Wir sie abgemäht sein, als ob sie am Tag zuvor nicht in Blüte gestanden hätte. So legen Wir die Zeichen ausführlich dar für Leute, die nachdenken." });
                        surah10.VerseList.Add(new Verse { Number = 25, TextBosnian = "Allah poziva u Kuću spasa i upućuje na Pravi put onoga koga On hoće.", TextEnglish = "And Allāh invites to the Home of Peace [i.e., Paradise] and guides whom He wills to a straight path.", TextGerman = "Allah lädt zur Wohnstätte des Friedens ein und leitet, wen Er will, zu einem geraden Weg." });
                        surah10.VerseList.Add(new Verse { Number = 26, TextBosnian = "Onima koji čine dobra djela pripada lijepa nagrada- Džennet, a i više od toga - gledanje u lice Allahovo! Lica njihova neće tama i potištenost prekrivati, oni će stanovnici Dženneta biti, u njemu će vječno boraviti.", TextEnglish = "For them who have done good is the best [reward] - and extra.[521] No darkness will cover their faces, nor humiliation. Those are companions of Paradise; they will abide therein eternally.", TextGerman = "Für diejenigen, die Gutes tun, gibt es das Beste (an Lohn) und noch mehr. Ihre Gesichter werden weder von Dunkelheit noch Erniedrigung bedeckt. Das sind die Insassen des (Paradies)gartens; ewig werden sie darin bleiben." });
                        surah10.VerseList.Add(new Verse { Number = 27, TextBosnian = "A one koji počiniše loša djela čeka kazna srazmjerna onom što su počinili i njih će potištenost prekrivati. Nikoga neće imati da ih od Allaha zaštiti, lica će im biti tamna kao da su prekrivena dijelovima mrkle noći, stanovnici Vatre će oni biti, i u njoj će vječno boraviti.", TextEnglish = "But they who have earned [blame for] evil doings - the recompense of an evil deed is its equivalent, and humiliation will cover them. They will have from Allāh no protector. It will be as if their faces are covered with pieces of the night - so dark [are they]. Those are the companions of the Fire; they will abide therein eternally.", TextGerman = "Für diejenigen aber, die böse Taten erwerben, ist der Lohn einer bösen Tat ein Gleiches, und Erniedrigung wird sie bedecken - sie haben vor Allah nichts, das sie schützen könnte -, als ob ihre Gesichter von Stücken finsterer Nacht überdeckt wären. Das sind die Insassen des (Höllen)feuers; ewig werden sie darin bleiben." });
                        surah10.VerseList.Add(new Verse { Number = 28, TextBosnian = "A na Dan kad ih sve sakupimo, reći ćemo onima koji su Allahu druge u obožavanju pridruživali: 'Na mjestu ostanite i vi i božanstva vaša!' Pa ćemo ih razdvojiti, a božanstva njihova će reći: 'Niste vi nas obožavali.'", TextEnglish = "And [mention, O Muḥammad], the Day We will gather them all together - then We will say to those who associated others with Allāh, '[Remain in] your place, you and your 'partners.''[522] Then We will separate them,[523] and their 'partners' will say, 'You did not used to worship us,[524]", TextGerman = "Und am Tag, da Wir sie alle versammeln, und dann werden Wir zu denen, die (Allah etwas) beigesellt haben, sagen: (Bleibt) an eurem Platz, ihr und eure Teilhaber! Wir trennen sie dann voneinander. Ihre Teilhaber sagen: Nicht uns habt ihr gedient." });
                        surah10.VerseList.Add(new Verse { Number = 29, TextBosnian = "Allah je dovoljan svjedok i nama i vama, mi, doista, nismo znali da ste nas obožavali.", TextEnglish = "And sufficient is Allāh as a witness between us and you that we were of your worship unaware.'", TextGerman = "Allah genügt als Zeuge zwischen uns und euch. Wir waren gegenüber eurem Dienst (an uns) fürwahr unachtsam." });
                        surah10.VerseList.Add(new Verse { Number = 30, TextBosnian = "Tu će svako znati ono što je prije uradio - bit će vraćeni Allahu, svom istinskom Gospodaru, a neće im biti onih koje su izmišljali.", TextEnglish = "There, [on that Day], every soul will be put to trial for what it did previously, and they will be returned to Allāh, their master, the Truth, and lost from them is whatever they used to invent.", TextGerman = "Dort wird jede Seele über das, was sie früher getan hat, nachgeprüft. Und sie werden zu Allah, ihrem wahren Schutzherrn, zurückgebracht, und entschwunden ist ihnen (dann), was sie zu ersinnen pflegten." });
                        surah10.VerseList.Add(new Verse { Number = 31, TextBosnian = "Upitaj: 'Ko vas opskrbljuje s neba i iz zemlje?', ili: 'Ko vlada sluhom i vidom, ko izvodi živo iz neživog i iz živog neživo, i ko upravlja svim?' 'Allah', reći će oni. A ti reci: 'Pa zar se sačuvati nećete?!'", TextEnglish = "Say, 'Who provides for you from the heaven and the earth? Or who controls hearing and sight and who brings the living out of the dead and brings the dead out of the living and who arranges [every] matter?' They will say, 'Allāh,' so say, 'Then will you not fear Him?'", TextGerman = "Sag: Wer versorgt euch vom Himmel und von der Erde, oder wer verfügt über Gehör und Augenlicht? Und wer bringt das Lebendige aus dem Toten und bringt das Tote aus dem Lebendigen hervor? Und wer regelt die Angelegenheit? Sie werden sagen: Allah. Sag: Wollt ihr denn nicht gottesfürchtig sein?" });
                        surah10.VerseList.Add(new Verse { Number = 32, TextBosnian = "To vam je Allah, Gospodar vaš istinski! Zar poslije Istine ima išta osim zablude?! Pa kuda se onda odmećete?", TextEnglish = "For that is Allāh, your Lord, the Truth. And what can be beyond truth except error? So how are you averted?", TextGerman = "Dies ist doch Allah, euer wahrer Herr. Was gibt es denn über die Wahrheit hinaus außer dem Irrtum? Wie laßt ihr euch also abwenden?" });
                        surah10.VerseList.Add(new Verse { Number = 33, TextBosnian = "Tako se obistinila riječ Gospodara tvoga nad onima koji su buntovni, da vjerovati neće.", TextEnglish = "Thus the word [i.e., decree] of your Lord has come into effect upon those who defiantly disobeyed - that they will not believe.", TextGerman = "So hat sich das Wort deines Herrn bewahrheitet gegen die, die freveln, weil sie nicht glauben." });
                        surah10.VerseList.Add(new Verse { Number = 34, TextBosnian = "Reci: 'Ima li među vašim božanstvima ijedno koje prvobitno stvara, a zatim to ponovo učini?' Reci: 'Allah prvobitno stvara, a zatim to ponavlja! Pa kuda se onda odmećete?'", TextEnglish = "Say, 'Are there of your 'partners' any who begins creation and then repeats it?' Say, 'Allāh begins creation and then repeats it, so how are you deluded?'", TextGerman = "Sag: Ist unter euren Teilhabern jemand, der die Schöpfung am Anfang macht und sie hierauf wiederholt? Sag: Allah macht die Schöpfung am Anfang und wiederholt sie hierauf. Wie laßt ihr euch also abwendig machen?" });
                        surah10.VerseList.Add(new Verse { Number = 35, TextBosnian = "Reci: 'Može li ijedno vaše božanstvo uputiti ka Istini?' I odgovori: 'Samo Allah upućuje ka Istini! Pa, da li je onda dostojniji slijeđenja Onaj Koji ka Istini upućuje ili onaj koji ni sam nije na Pravom putu?, osim ako on upućen bude. Šta vam je, kako rasuđujete?'", TextEnglish = "Say, 'Are there of your 'partners' any who guides to the truth?' Say, 'Allāh guides to the truth. So is He who guides to the truth more worthy to be followed or he who guides not unless he is guided? Then what is [wrong] with you - how do you judge?'", TextGerman = "Sag: Ist unter euren Teilhabern jemand, der zur Wahrheit leitet? Sag: Allah leitet zur Wahrheit. Hat jemand, der zur Wahrheit leitet, ein größeres Anrecht darauf, daß man ihm folgt, oder jemand, der nur (dann) die Rechtleitung findet, wenn er (selbst) rechtgeleitet wird? Was ist denn mit euch? Wie urteilt ihr?" });
                        surah10.VerseList.Add(new Verse { Number = 36, TextBosnian = "Većina njih slijedi samo pretpostavke, ali pretpostavke nimalo nisu od koristi Istini. Allah uistinu dobro zna ono što oni rade.", TextEnglish = "And most of them follow not except assumption. Indeed, assumption avails not against the truth at all. Indeed, Allāh is Knowing of what they do.", TextGerman = "Und die meisten von ihnen folgen nur Mutmaßungen. Aber Mutmaßungen nützen nichts gegenüber der Wahrheit. Gewiß, Allah weiß Bescheid über das, was sie tun." });
                        surah10.VerseList.Add(new Verse { Number = 37, TextBosnian = "Ovaj Kur'an nije izmišljen, od Allaha je - on potvrđuje ono prije njega i objašnjava propise, u njega nema sumnje, od Gospodara svjetova je!", TextEnglish = "And it was not [possible] for this Qur’ān to be produced by other than Allāh, but [it is] a confirmation of what was before it and a detailed explanation of the [former] Scripture, about which there is no doubt,[525] from the Lord of the worlds.", TextGerman = "Dieser Qur’an kann unmöglich ohne Allah ersonnen werden. Sondern (er ist) die Bestätigung dessen, was vor ihm war, und die ausführliche Darlegung des Buches, an dem es keinen Zweifel gibt, vom Herrn der Weltenbewohner." });
                        surah10.VerseList.Add(new Verse { Number = 38, TextBosnian = "I zar oni govore: 'On ga izmišlja!' Reci: 'Pa dajte vi jednu suru kao što je njemu objavljena, i koga god hoćete, od onih u koje mimo Allaha vjerujete, u pomoć pozovite, ako istinu govorite.'", TextEnglish = "Or do they say [about the Prophet (ﷺ)], 'He invented it?' Say, 'Then bring forth a sūrah like it and call upon [for assistance] whomever you can besides Allāh, if you should be truthful.'", TextGerman = "Oder sagen sie: Er hat ihn ersonnen Sag: Dann bringt eine Sura bei, die ihm gleich ist, und ruft an, wen ihr könnt, anstatt Allahs, wenn ihr wahrhaftig seid." });
                        surah10.VerseList.Add(new Verse { Number = 39, TextBosnian = "Štaviše, oni poriču prije nego temeljito saznaju šta ima u njemu, a još im nije došlo ni tumačenje njegovo. Tako su i oni prije njih poricali, pa pogledaj kako su zulumćari završili.", TextEnglish = "Rather, they have denied that which they encompass not in knowledge and whose interpretation has not yet come to them. Thus did those before them deny. Then observe how was the end of the wrongdoers.", TextGerman = "Nein! Vielmehr erklären sie das für Lüge, wovon sie kein umfassendes Wissen haben, und schon bevor seine Deutung zu ihnen gekommen ist. So haben es auch diejenigen, die vor ihnen waren, für Lüge erklärt. Schau, wie das Ende der Ungerechten war!" });
                        surah10.VerseList.Add(new Verse { Number = 40, TextBosnian = "Ima ih koji u njega vjeruju, a ima ih koji ne vjeruju u njega. A Gospodar tvoj najbolje poznaje smutljivce.", TextEnglish = "And of them are those who believe in it, and of them are those who do not believe in it. And your Lord is most knowing of the corrupters.", TextGerman = "Und unter ihnen gibt es manche, die an ihn glauben, und unter ihnen gibt es manche, die nicht an ihn glauben. Dein Herr weiß sehr wohl über die Unheilstifter Bescheid." });
                        surah10.VerseList.Add(new Verse { Number = 41, TextBosnian = "I ako te oni budu u laž utjerivali, ti reci: 'Meni moja, a vama vaša djela; vi nećete odgovarati za ono što ja radim, a ja neću odgovarati za ono što vi radite.'", TextEnglish = "And if they deny you, [O Muḥammad], then say, 'For me are my deeds, and for you are your deeds. You are disassociated from what I do, and I am disassociated from what you do.'", TextGerman = "Und wenn sie dich der Lüge bezichtigen, dann sag: Für mich ist mein Tun, und für euch ist euer Tun. Ihr seid unschuldig an dem, was ich tue; und ich bin unschuldig an dem, was ihr tut." });
                        surah10.VerseList.Add(new Verse { Number = 42, TextBosnian = "Ima među njima onih koji te slušaju. A možeš li ti učiniti da te gluhi čuju, ako još i ne razumiju?!", TextEnglish = "And among them are those who listen to you. But can you cause the deaf to hear [i.e., benefit from this hearing], although they will not use reason?", TextGerman = "Und unter ihnen gibt es manche, die dir zuhören. Kannst du aber die Tauben hören lassen, auch wenn sie nicht begreifen (wollen)?" });
                        surah10.VerseList.Add(new Verse { Number = 43, TextBosnian = "A ima među njima i onih koji te posmatraju, a možeš li ti uputiti na Pravi put slijepe, ako još i ne uviđaju?!", TextEnglish = "And among them are those who look at you. But can you guide the blind although they will not [attempt to] see?", TextGerman = "Und unter ihnen gibt es manche, die auf dich schauen. Kannst du aber die Blinden rechtleiten, auch wenn sie nicht sehen?" });
                        surah10.VerseList.Add(new Verse { Number = 44, TextBosnian = "Allah, zaista, nikakav zulum ne čini ljudima, ljudi ga sami sebi čine.", TextEnglish = "Indeed, Allāh does not wrong the people at all, but it is the people who are wronging themselves.", TextGerman = "Gewiß, Allah fügt den Menschen kein Unrecht zu, sondern die Menschen fügen sich selbst Unrecht zu." });
                        surah10.VerseList.Add(new Verse { Number = 45, TextBosnian = "A na Dan kada ih On sakupi, učinit će im se da su boravili samo jedan kratki dio dana, i jedni druge će prepoznati. Izgubili su, zasigurno, oni koji su poricali da će pred Allaha stati i koji nisu Uputu slijedili!", TextEnglish = "And on the Day when He will gather them, [it will be] as if they had not remained [in the world] but an hour of the day, [and] they will know each other. Those will have lost who denied the meeting with Allāh and were not guided.", TextGerman = "Und an dem Tag, da Er sie versammelt, wird (ihnen) sein, als ob sie nur eine Stunde vom Tag verweilt hätten, und sie erkennen einander wieder. Verloren sind dann diejenigen, die die Begegnung mit Allah für Lüge erklärten und nicht rechtgeleitet waren." });
                        surah10.VerseList.Add(new Verse { Number = 46, TextBosnian = "Bilo da ti pokažemo dio onoga što im prijeteći obećavamo, bilo da te usmrtimo, Nama će se sve vratiti, a povrh toga Allah je svjedok onoga što oni rade.", TextEnglish = "And whether We show you some of what We promise them, [O Muḥammad], or We take you in death, to Us is their return; then, [either way], Allāh is a witness concerning what they are doing.", TextGerman = "Ob Wir dich nun einen Teil dessen, was Wir ihnen androhen, sehen lassen oder dich (vorher) abberufen, zu Uns wird (auf jeden Fall) ihre Rückkehr sein. Hierauf ist Allah Zeuge über das, was sie tun." });
                        surah10.VerseList.Add(new Verse { Number = 47, TextBosnian = "Svaki je narod imao poslanika, i kad poslanik njihov dođe među njih njima će biti pravedno presuđeno, zulum im neće biti učinjen.", TextEnglish = "And for every nation is a messenger. So when their messenger comes,[526] it will be judged between them in justice, and they will not be wronged.", TextGerman = "Jede Gemeinschaft hat einen Gesandten. Wenn nun ihr Gesandter kommt, wird zwischen ihnen in Gerechtigkeit entschieden, und es wird ihnen kein Unrecht zugefügt." });
                        surah10.VerseList.Add(new Verse { Number = 48, TextBosnian = "Oni govore: 'Kada će već jednom to prijeteće obećanje, ako istinu govorite?'", TextEnglish = "And they say, 'When is [the fulfillment of] this promise, if you should be truthful?'", TextGerman = "Und sie sagen: „Wann wird dieses Versprechen eintreten, wenn ihr wahrhaftig seid?" });
                        surah10.VerseList.Add(new Verse { Number = 49, TextBosnian = "Reci: 'Sam od sebe ne mogu nikakvu štetu otkloniti, a ni neku korist sebi pribaviti; biva onako kako Allah hoće!' Svaki narod ima rok određeni, a kad rok njegov dođe, ni za tren ga neće moći ni odložiti ni ubrzati.", TextEnglish = "Say, 'I possess not for myself any harm or benefit except what Allāh should will. For every nation is a [specified] term. When their time has come, then they will not remain behind an hour, nor will they precede [it].'", TextGerman = "Sag: Ich vermag mir selbst weder Schaden noch Nutzen (zu bringen), außer was Allah will. Jede Gemeinschaft hat eine (festgesetzte) Frist. Und wenn ihre Frist kommt, können sie (sie) weder um eine Stunde hinausschieben noch vorverlegen." });
                        surah10.VerseList.Add(new Verse { Number = 50, TextBosnian = "Reci: 'Kažite vi meni: Ako će vas kazna Njegova noću ili danju zadesiti, zašto je onda požuruju zločinitelji?'", TextEnglish = "Say, 'Have you considered: if His punishment should come to you by night or by day - for which [aspect] of it would the criminals be impatient?'[527]", TextGerman = "Sag: Was meint ihr, wenn Seine Strafe bei Nacht oder bei Tag (über) euch kommt, was werden die Übeltäter davon vorzuverlegen wünschen?" });
                        surah10.VerseList.Add(new Verse { Number = 51, TextBosnian = "Zar ćete tek onda, kad se dogodi, u nju povjerovati?! Zar tek tada, a ranije ste je požurivali?!", TextEnglish = "Then is it that when it has [actually] occurred you will believe in it? Now?[528] And you were [once] for it impatient.[529]", TextGerman = "Werdet ihr dann, wenn sie hereinbricht, daran glauben? Wie, erst jetzt, und dabei habt ihr sie doch vorzuverlegen gewünscht?" });
                        surah10.VerseList.Add(new Verse { Number = 52, TextBosnian = "Zatim će onima koji su zulum učinili rečeno biti: 'Iskusite patnju vječnu; da li ste izloženi kazni osim prema onome što ste činili?'", TextEnglish = "Then it will be said to those who had wronged, 'Taste the punishment of eternity; are you being recompensed except for what you used to earn?'", TextGerman = "Dann wird zu denen, die Unrecht getan haben, gesagt werden: Kostet die ewige Strafe! Wird euch denn etwas anderes vergolten als das, was ihr verdient habt?" });
                        surah10.VerseList.Add(new Verse { Number = 53, TextBosnian = "Oni te zapitkuju: 'Da li je istina da će to biti?' Reci: 'Jeste, Gospodara mi moga, zaista je istina, i vi je nećete moći spriječiti.'", TextEnglish = "And they ask information of you, [O Muḥammad], 'Is it true?' Say, 'Yes, by my Lord. Indeed, it is truth; and you will not cause failure [to Allāh].'", TextGerman = "Und sie erkundigen sich bei dir: Ist es wahr? Sag: Ja, bei meinem Herrn! Es ist bestimmt wahr, und ihr werdet euch (Ihm) nicht entziehen können." });
                        surah10.VerseList.Add(new Verse { Number = 54, TextBosnian = "Kad bi onaj ko je zulum činio imao sve ono što na Zemlji postoji, sve bi on to dao samo da se iskupi. A kada oni dožive patnju, sakrit će tugu, i bit će im po pravdi presuđeno i neće im zulum učinjen biti.", TextEnglish = "And if each soul that wronged had everything on earth, it would offer it in ransom. And they will confide regret when they see the punishment; and they will be judged in justice, and they will not be wronged.", TextGerman = "Und wenn jede Seele, die Unrecht getan hat, das besäße, was auf der Erde ist, würde sie sich wahrlich damit loskaufen. Sie halten Reue geheim, wenn sie die Strafe sehen. Und es wird in Gerechtigkeit zwischen ihnen entschieden, und es wird ihnen kein Unrecht zugefügt." });
                        surah10.VerseList.Add(new Verse { Number = 55, TextBosnian = "Zasigurno, Allahovo je sve što je na nebesima i na Zemlji! Zasigurno, Allahovo obećanje je istinito, ali većina njih ne zna.", TextEnglish = "Unquestionably, to Allāh belongs whatever is in the heavens and the earth. Unquestionably, the promise of Allāh is truth, but most of them do not know.", TextGerman = "Sicherlich, Allah gehört (alles), was in den Himmeln und auf der Erde ist. Sicherlich, Allahs Versprechen ist wahr. Aber die meisten von ihnen wissen nicht." });
                        surah10.VerseList.Add(new Verse { Number = 56, TextBosnian = "On život i smrt daje i Njemu ćete se vratiti.", TextEnglish = "He gives life and causes death, and to Him you will be returned.", TextGerman = "Er macht lebendig und läßt sterben. Und zu Ihm werdet ihr zurückgebracht." });
                        surah10.VerseList.Add(new Verse { Number = 57, TextBosnian = "O ljudi, već vam je stigla Opomena od Gospodara vašega, i lijek za vaša srca, i uputa i milost vjernicima.", TextEnglish = "O mankind, there has come to you instruction from your Lord and healing for what is in the breasts and guidance and mercy for the believers.", TextGerman = "O ihr Menschen, zu euch ist nunmehr eine Ermahnung von eurem Herrn gekommen und eine Heilung für das, was in den Brüsten ist, eine Rechtleitung und Barmherzigkeit für die Gläubigen." });
                        surah10.VerseList.Add(new Verse { Number = 58, TextBosnian = "Reci: 'Zbog Allahove blagodarnosti i milosti - eto, zbog toga neka se raduju; to je bolje od onoga što gomilaju.'", TextEnglish = "Say, 'In the bounty of Allāh and in His mercy - in that let them rejoice; it is better than what they accumulate.'", TextGerman = "Sag: Über die Huld Allahs und über Seine Barmherzigkeit, ja darüber sollen sie froh sein. Das ist besser als das, was sie zusammentragen." });
                        surah10.VerseList.Add(new Verse { Number = 59, TextBosnian = "Reci: 'Kažite vi meni zašto neku hranu koju vam Allah daje smatrate zabranjenom, a drugu dopuštenom?' Reci: 'Da li vam je to Allah dozvolio ili o Allahu laži iznosite?!'", TextEnglish = "Say, 'Have you seen what Allāh has sent down to you of provision of which you have made [some] lawful and [some] unlawful?' Say, 'Has Allāh permitted you [to do so], or do you invent [something] about Allāh?'", TextGerman = "Sag: Was meint ihr zu dem, was Allah für euch an Versorgung herabgesandt hat und was ihr dann als Verbotenes und Erlaubtes festgelegt habt, - sag: Hat Allah es euch tatsächlich erlaubt, oder ersinnt ihr etwas gegen Allah?" });
                        surah10.VerseList.Add(new Verse { Number = 60, TextBosnian = "I, šta misle oni koji o Allahu iznose laži, šta će na Sudnjem danu biti? Allah je, zaista, blagodaran prema ljudima, ali većina njih ne zahvaljuje.", TextEnglish = "And what will be the supposition of those who invent falsehood about Allāh on the Day of Resurrection?[530] Indeed, Allāh is the possessor of bounty for the people, but most of them are not grateful.", TextGerman = "Was wird die Meinung derer, die gegen Allah Lügen ersinnen, am Tag der Auferstehung sein? Allah ist wahrlich voll Huld gegen die Menschen. Aber die meisten von ihnen sind nicht dankbar." });
                        surah10.VerseList.Add(new Verse { Number = 61, TextBosnian = "Što god ti činio, i što god iz Kur'ana učio i kakav god vi posao radili, Mi nad vama bdijemo dok god se time zanimate. Gospodaru tvome nije ništa skriveno ni na Zemlji ni na nebu, ni koliko trun jedan, i ne postoji ništa, ni manje ni veće od toga, što nije u Knjizi jasnoj.", TextEnglish = "And, [O Muḥammad], you are not [engaged] in any matter and do not recite any of the Qur’ān and you [people] do not do any deed except that We are witness over you when you are involved in it. And not absent from your Lord is any [part] of an atom's weight[531] within the earth or within the heaven or [anything] smaller than that or greater but that it is in a clear register.", TextGerman = "Und mit keiner Angelegenheit befaßt Du dich, und nichts verliest du davon an Qur’an, und keine Tat vollbringt ihr, ohne daß Wir über euch Zeugen sind, wenn ihr euch (ausgiebig) darüber auslaßt. Und es entgeht deinem Herrn nicht das Gewicht eines Stäubchens, weder auf der Erde noch im Himmel, und nichts Kleineres als dies oder Größeres; (es gibt) nichts, das nicht in einem deutlichen Buch (verzeichnet) wäre." });
                        surah10.VerseList.Add(new Verse { Number = 62, TextBosnian = "Zaista, za Allahove štićenike nema straha i oni ni za čim ne treba da tuguju,", TextEnglish = "Unquestionably, [for] the allies of Allāh there will be no fear concerning them, nor will they grieve -", TextGerman = "Sicherlich, über Allahs Gefolgsleute soll keine Furcht kommen, noch sollen sie traurig sein," });
                        surah10.VerseList.Add(new Verse { Number = 63, TextBosnian = "oni koji budu vjerovali i koji se budu Allaha bojali,", TextEnglish = "Those who believed and were fearing Allāh.", TextGerman = "diejenigen, die glauben und gottesfürchtig sind." });
                        surah10.VerseList.Add(new Verse { Number = 64, TextBosnian = "za njih su dobre vijesti i na ovom i na drugom svijetu-Allahove riječi niko ne može izmijeniti- to je, zaista, uspjeh veliki.", TextEnglish = "For them are good tidings in the worldly life and in the Hereafter. No change is there in the words [i.e., decrees] of Allāh. That is what is the great attainment.", TextGerman = "Für sie ist die frohe Botschaft im diesseitigen Leben und im Jenseits. Keine Abänderung gibt es für die Worte Allahs. Das ist der großartige Erfolg." });
                        surah10.VerseList.Add(new Verse { Number = 65, TextBosnian = "Neka te ne žaloste riječi njihove! Uistinu, sva moć pripada Allahu; On je Svečujući i Sveznajući.", TextEnglish = "And let not their speech grieve you. Indeed, honor [due to power] belongs to Allāh entirely. He is the Hearing, the Knowing.", TextGerman = "Ihre Worte sollen dich nicht traurig machen. Gewiß, alle Macht gehört Allah. Er ist der Allhörende und Allwissende." });
                        surah10.VerseList.Add(new Verse { Number = 66, TextBosnian = "Zaista, u Allahovoj je vlasti sve što je na nebesima i na Zemlji. A oni koji se, pored Allaha, božanstvima mole, povode se samo za pretpostavkama i samo laži izmišljaju.", TextEnglish = "Unquestionably, to Allāh belongs whoever is in the heavens and whoever is on the earth. And those who invoke other than Allāh do not [actually] follow [His] 'partners.' They follow not except assumption, and they are not but misjudging.", TextGerman = "Sicherlich, Allah gehört, wer in den Himmeln und wer auf der Erde ist. Es folgen diejenigen, die anstatt Allahs Teilhaber anrufen (ihren falschen Göttern); sie folgen nur Mutmaßungen, und sie stellen nur Schätzungen an." });
                        surah10.VerseList.Add(new Verse { Number = 67, TextBosnian = "On je Taj Koji vam je dao noć da u njoj počinak imate, a dan da vidite. U tome su, zaista, znakovi za ljude koji čuju.", TextEnglish = "It is He who made for you the night to rest therein and the day, giving sight.[532] Indeed in that are signs for a people who listen.", TextGerman = "Er ist es, Der euch die Nacht gemacht hat, damit ihr in ihr ruht, und den Tag hell. Darin sind wahrlich Zeichen für Leute, die hören." });
                        surah10.VerseList.Add(new Verse { Number = 68, TextBosnian = "Neki govore: 'Allah je Sebi uzeo dijete!' Uzvišen je On! On ni o kom ovisan nije! Sve što je na nebesima i na Zemlji Njegovo je! Vi za to nikakva dokaza nemate. Zašto o Allahu govorite ono što ne znate?", TextEnglish = "They[533] have said, 'Allāh has taken a son.' Exalted is He; He is the [one] Free of need. To Him belongs whatever is in the heavens and whatever is in the earth. You have no authority[534] for this [claim]. Do you say about Allāh that which you do not know?", TextGerman = "Sie sagen: Allah hat Sich Kinder genommen. Preis sei Ihm! Er ist der Unbedürftige. Ihm gehört, was in den Himmeln und was auf der Erde ist. Keine Ermächtigung habt ihr dafür. Wollt ihr über Allah etwas sagen, was ihr nicht wißt?" });
                        surah10.VerseList.Add(new Verse { Number = 69, TextBosnian = "Reci: 'Oni koji o Allahu laži iznose neće uspjeti.'", TextEnglish = "Say, 'Indeed, those who invent falsehood about Allāh will not succeed.'", TextGerman = "Sag: Gewiß, denjenigen, die gegen Allah Lügen ersinnen, wird es nicht wohl ergehen." });
                        surah10.VerseList.Add(new Verse { Number = 70, TextBosnian = "Uživat će kratko na ovom svijetu, a zatim će se Nama vratiti i Mi ćemo dati da iskuse žestoku patnju zato što nisu vjerovali.", TextEnglish = "[For them is brief] enjoyment in this world; then to Us is their return; then We will make them taste the severe punishment because they used to disbelieve.", TextGerman = "(Für sie ist) im Diesseits Nießbrauch, hierauf wird ihre Rückkehr zu Uns sein. Hierauf lassen Wir sie die strenge Strafe kosten dafür, daß sie ungläubig waren." });
                        surah10.VerseList.Add(new Verse { Number = 71, TextBosnian = "Kaži im pripovijest o Nuhu! Kada on reče narodu svome: 'O narode moj, ako vam je težak moj boravak među vama i moje podsjećanje na Allahova znamenja, pa ja se uzdam u Allaha, a vi se, zajedno sa božanstvima svojim, odlučite, i neka vam odluka vaša ne bude nejasna; zatim je nada mnom izvršite i ne odgađajte!", TextEnglish = "And recite to them the news of Noah, when he said to his people, 'O my people, if my residence and my reminding of the signs of Allāh has become burdensome upon you - then I have relied upon Allāh. So resolve upon your plan and [call upon] your associates. Then let not your plan be obscure to you.[535] Then carry it out upon me and do not give me respite.", TextGerman = "Und verlies ihnen die Kunde von Nuh, als er zu seinem Volk sagte: O mein Volk, wenn euch mein Stand (unter euch) und meine Ermahnung mit Allahs Zeichen schwer ankommt, so verlasse ich mich auf Allah. So einigt euch über eure Angelegenheit, ihr und eure Teilhaber, und (der Entschluß in) eure(r) Angelegenheit soll für euch nicht unklar sein; hierauf führt (ihn) an mir aus und gewährt mir keinen Aufschub." });
                        surah10.VerseList.Add(new Verse { Number = 72, TextBosnian = "A ako se okrenete, pa ja od vas nikakvu nagradu ne tražim, mene će Allah nagraditi, meni je naređeno da budem među muslimanima - Njemu predanima.'", TextEnglish = "And if you turn away [from my advice] - then no payment have I asked of you. My reward is only from Allāh, and I have been commanded to be of the Muslims [i.e., those who submit to Allāh].'", TextGerman = "Doch wenn ihr euch abkehrt, so habe ich von euch keinen Lohn verlangt. Mein Lohn obliegt nur Allah. Und mir ist befohlen worden, einer der (Allah) Ergebenen zu sein ." });
                        surah10.VerseList.Add(new Verse { Number = 73, TextBosnian = "Ali, nazvaše ga lašcem, pa Mi u lađi njega i one koji bijahu uz njega spasismo i nasljednicima ih učinismo a one koji dokaze naše poricaše potopismo, pa pogledaj kakav je bio završetak onih koji su upozoravani!", TextEnglish = "And they denied him, so We saved him and those with him in the ship and made them successors, and We drowned those who denied Our signs. Then see how was the end of those who were warned.", TextGerman = "Sie bezichtigten ihn aber der Lüge. Da erretteten Wir ihn und diejenigen, die mit ihm waren, im Schiff. Und Wir machten sie zu Nachfolgern und ließen diejenigen ertrinken, die Unsere Zeichen für Lüge erklärten. So schau, wie das Ende der Gewarnten war!" });
                        surah10.VerseList.Add(new Verse { Number = 74, TextBosnian = "Zatim smo, poslije njega, poslanike narodima njihovim slali i oni su im jasne dokaze donosili, ali oni nisu htjeli vjerovati u ono što su prije poricali. Tako Mi pečatimo srca onih koji granice prelaze.", TextEnglish = "Then We sent after him messengers to their peoples, and they came to them with clear proofs. But they were not to believe in that which they had denied before.[536] Thus We seal over the hearts of the transgressors.", TextGerman = "Hierauf schickten Wir nach ihm Gesandte zu ihrem (jeweiligen) Volk; sie kamen mit den klaren Beweisen zu ihnen. Aber sie waren nicht bereit, an das zu glauben, was sie zuvor für Lüge erklärt hatten. So versiegeln Wir die Herzen der Übertreter." });
                        surah10.VerseList.Add(new Verse { Number = 75, TextBosnian = "Zatim smo, poslije njih, Musaa i Haruna poslali sa znacima Našim faraonu i glavešinama njegovim, ali su se oni uzoholili; bijahu ljudi prestupnici.", TextEnglish = "Then We sent after them Moses and Aaron to Pharaoh and his establishment with Our signs, but they behaved arrogantly and were a criminal people.", TextGerman = "Hierauf schickten Wir nach ihnen Musa und Harun mit Unseren Zeichen zu Fir’aun und seiner führenden Schar, aber sie verhielten sich hochmütig und waren ein Volk von Übeltätern." });
                        surah10.VerseList.Add(new Verse { Number = 76, TextBosnian = "A kada im je od Nas došla Istina, rekli su: 'Ovo je, zaista, prava čarolija!'", TextEnglish = "So when there came to them the truth from Us, they said, 'Indeed, this is obvious magic.'", TextGerman = "Als nun die Wahrheit von Uns zu ihnen kam, sagten sie: Das ist wahrlich deutliche Zauberei." });
                        surah10.VerseList.Add(new Verse { Number = 77, TextBosnian = "Zar za Istinu koja vam je došla kažete da je čarolija?', reče Musa, 'a čarobnjaci neće nikad uspjeti!'", TextEnglish = "Moses said, 'Do you say [thus] about the truth when it has come to you? Is this magic? But magicians will not succeed.'", TextGerman = "Musa sagte: Sagt ihr denn von der Wahrheit, nachdem sie zu euch gekommen ist: ,Ist das etwa Zauberei? Aber den Zauberern wird es nicht wohl ergehen." });
                        surah10.VerseList.Add(new Verse { Number = 78, TextBosnian = "A oni rekoše: 'Zar si došao da nas odvratiš od onoga na čemu smo zatekli pretke naše, da bi vas dvojica imali vlast na zemlji? E, nećemo mi vama dvojici vjerovati.'", TextEnglish = "They said, 'Have you come to us to turn us away from that upon which we found our fathers and so that you two may have grandeur in the land? And we are not believers in you.'", TextGerman = "Sie sagten: Bist du zu uns gekommen, um uns von dem abzubringen, worin wir unsere Väter (vor)gefunden haben, und damit euch beiden die Oberhoheit im Land werde? Wir werden euretwegen nicht gläubig werden." });
                        surah10.VerseList.Add(new Verse { Number = 79, TextBosnian = "I faraon reče: 'Dovedite mi svakog čarobnjaka znana!'", TextEnglish = "And Pharaoh said, 'Bring to me every learned magician.'", TextGerman = "Und Fir’aun sagte: Bringt mir jeden kenntnisreichen Zauberer herbei!" });
                        surah10.VerseList.Add(new Verse { Number = 80, TextBosnian = "I kad čarobnjaci dođoše, Musa im reče: 'Bacite što imate da bacite!'", TextEnglish = "So when the magicians came, Moses said to them, 'Throw down whatever you will throw.'", TextGerman = "Als die Zauberer kamen, sagte Musa zu ihnen: Werft hin, was ihr zu werfen habt." });
                        surah10.VerseList.Add(new Verse { Number = 81, TextBosnian = "I kad oni baciše, Musa uzviknu: 'Ono što ste priredili čarolija je! Allah će je zaista uništiti, Allah ne čini da djelo smutljivaca valjano bude.'", TextEnglish = "And when they had thrown, Moses said, 'What you have brought is [only] magic. Indeed, Allāh will expose its worthlessness. Indeed, Allāh does not amend the work of corrupters.", TextGerman = "Als sie geworfen hatten, sagte Musa: Was ihr vorgebracht habt, ist Zauberei. Allah wird sie zunichte machen. Gewiß, Allah läßt das Tun der Unheilstifter nicht als gut gelten." });
                        surah10.VerseList.Add(new Verse { Number = 82, TextBosnian = "Allah će Svojim riječima istinu utvrditi, makar što će to zločincima krivo biti!", TextEnglish = "And Allāh will establish the truth by His words, even if the criminals dislike it.'", TextGerman = "Allah bestätigt die Wahrheit mit Seinen Worten, auch wenn es den Übeltätern zuwider ist." });
                        surah10.VerseList.Add(new Verse { Number = 83, TextBosnian = "I ne povjerova Musau niko, osim malo njih iz naroda faraonova, iz straha da ih faraon i glavešine njegove ne počnu zlostavljati - a faraon je, zaista, na zemlji silnik bio i zaista je u one što svaku mjeru prevršavaju spadao.", TextEnglish = "But no one believed Moses, except [some] offspring [i.e., youths] among his people, for fear of Pharaoh and his establishment that they would persecute them. And indeed, Pharaoh was haughty within the land, and indeed, he was of the transgressors.", TextGerman = "Aber es glaubten Musa nur junge Leute aus seinem Volk, trotz ihrer Furcht vor Fir’aun und ihrer führenden Schar, davor, daß er sie verfolgen würde. Fir’aun war ja überheblich im Land und gehörte wahrlich zu den Maßlosen." });
                        surah10.VerseList.Add(new Verse { Number = 84, TextBosnian = "I Musa reče: 'O narode moj, ako u Allaha vjerujete, u Njega se pouzdajte ako ste muslimani!'", TextEnglish = "And Moses said, 'O my people, if you have believed in Allāh, then rely upon Him, if you should be Muslims [i.e., submitting to Him].'", TextGerman = "Und Musa sagte: O mein Volk, wenn ihr wirklich an Allah glaubt, dann verlaßt euch auf Ihn, wenn ihr (Ihm) Ergebene seid." });
                        surah10.VerseList.Add(new Verse { Number = 85, TextBosnian = "U Allaha se uzdamo', rekoše oni. 'Gospodaru naš, ne učini nas iskušenjem za ljude koji zulum čine.", TextEnglish = "So they said, 'Upon Allāh do we rely. Our Lord, make us not [objects of] trial for the wrongdoing people", TextGerman = "Sie sagten: Auf Allah verlassen wir uns. Unser Herr, mache uns nicht zu einer Versuchung für das ungerechte Volk," });
                        surah10.VerseList.Add(new Verse { Number = 86, TextBosnian = "I spasi nas milošću Svojom od naroda koji ne vjeruje!'", TextEnglish = "And save us by Your mercy from the disbelieving people.'", TextGerman = "und errette uns durch Deine Barmherzigkeit von dem ungläubigen Volk!" });
                        surah10.VerseList.Add(new Verse { Number = 87, TextBosnian = "I mi objavismo Musau i bratu njegovu: 'U Misiru narodu svome kuće izgradite i bogomoljama ih učinite i u njima namaz klanjajte! A ti, obraduj vjernike!'", TextEnglish = "And We inspired to Moses and his brother, 'Settle your people in Egypt in houses and make your houses [facing the] qiblah[537] and establish prayer and give good tidings to the believers.'", TextGerman = "Und Wir gaben Müsä und seinem Bruder ein: „Weiset eurem Volk in Ägypten Häuser zu und macht eure Häuser zu Gebetsstätten und verrichtet das Gebet. Und verkünde den Gläubigen frohe Botschaft.“" });
                        surah10.VerseList.Add(new Verse { Number = 88, TextBosnian = "I Musa reče: 'Gospodaru naš! Ti si dao faraonu i glavešinama njegovim ukrase i imetke na ovom svijetu, da oni, Gospodaru moj, zavode s puta Tvoga! Gospodaru naš, uništi imetke njihove i zapečati srca njihova, pa neka ne vjeruju dok ne dožive patnju bolnu!'", TextEnglish = "And Moses said, 'Our Lord, indeed You have given Pharaoh and his establishment splendor and wealth in the worldly life, our Lord, that they may lead [men] astray from Your way. Our Lord, obliterate their wealth and harden their hearts so that they will not believe until they see the painful punishment.'", TextGerman = "Und Musa sagte: Unser Herr, Du hast ja Fir’aun und seiner führenden Schar im diesseitigen Leben Pracht und Besitz gegeben, unser Herr, damit sie (andere) von Deinem Weg in die Irre fuhren. Unser Herr, lösche ihren Besitz aus und schnüre ihre Herzen fest, so daß sie nicht glauben, bis sie die schmerzhafte Strafe sehen!" });
                        surah10.VerseList.Add(new Verse { Number = 89, TextBosnian = "Uslišena je molba vaša!', reče On, 'a vas dvojica na Pravom putu ustrajte i nikako put neznalica ne slijedite!'", TextEnglish = "[Allāh] said, 'Your supplication has been answered.'[538] So remain on a right course and follow not the way of those who do not know.'", TextGerman = "Er sagte: Eure beider Anrufung ist erhört. So verhaltet euch recht und folgt ja nicht dem Weg derjenigen, die nicht Bescheid wissen." });
                        surah10.VerseList.Add(new Verse { Number = 90, TextBosnian = "I mi prevedosmo preko mora sinove Israilove, a za petama su im bili faraon i vojnici njegovi, progoneći ih iz zlobe i neprijateljstva. A on, kad se poče daviti, uzviknu: 'Ja vjerujem da nema boga osim Onoga u Koga vjeruju sinovi Israilovi i ja u muslimane - pokorne spadam!'", TextEnglish = "And We took the Children of Israel across the sea, and Pharaoh and his soldiers pursued them in tyranny and enmity until, when drowning overtook him, he said, 'I believe that there is no deity except that in whom the Children of Israel believe, and I am of the Muslims.'", TextGerman = "Und Wir ließen die Kinder Isra’ils das Meer durchschreiten. Da verfolgten sie Fir’aun und seine Heerscharen in Auflehnung und Übertretung, bis daß, als er vom Ertrinken erfaßt wurde, er sagte: Ich glaube, daß es keinen Gott gibt außer dem, an den die Kinder Isra’ils glauben. Und ich gehöre (nun) zu den (Allah) Ergebenen." });
                        surah10.VerseList.Add(new Verse { Number = 91, TextBosnian = "Zar sada, a prije si buntovan bio i u one si što nered šire spadao?!'", TextEnglish = "Now? And you had disobeyed [Him] before and were of the corrupters?", TextGerman = "Aber jetzt erst! Wo du dich doch zuvor widersetztest und zu den Unheilstiftern gehörtest?" });
                        surah10.VerseList.Add(new Verse { Number = 92, TextBosnian = "Danas ćemo izbaviti samo tijelo tvoje, da bi bio poučan primjer onima poslije tebe, ali mnogi ljudi su prema Našim ajetima i znakovima ravnodušni.'", TextEnglish = "So today We will save you in body[539] that you may be to those who succeed you a sign. And indeed, many among the people, of Our signs, are heedless.", TextGerman = "Heute wollen Wir dich mit deinem Leib erretten, damit du für diejenigen, die nach dir kommen, ein Zeichen seiest. Und viele von den Menschen sind gegenüber Unseren Zeichen wahrlich unachtsam." });
                        surah10.VerseList.Add(new Verse { Number = 93, TextBosnian = "I Mi smo sinove Israllove u časni predjel naselili i lijepom hranom ih opskrbili, i tek kad im je došlo pravo znanje, oni su se u mišljenju razišli. A Gospodar tvoj će im, sigurno, na Kijametskom danu presuditi o onome u čemu su se razilazili.", TextEnglish = "And We had certainly settled the Children of Israel in an agreeable settlement and provided them with good things. And they did not differ until [after] knowledge had come to them. Indeed, your Lord will judge between them on the Day of Resurrection concerning that over which they used to differ.", TextGerman = "Und Wir wiesen den Kindern Isra’ils einen wahrhaftigen Aufenthaltsort zu und versorgten sie von den guten Dingen. Sie wurden aber nicht (eher) uneinig, bis das Wissen zu ihnen gekommen war. Gewiß, dein Herr wird am Tag der Auferstehung zwischen ihnen über das entscheiden, worüber sie uneinig zu sein pflegten." });
                        surah10.VerseList.Add(new Verse { Number = 94, TextBosnian = "A ako sumnjaš u ono što ti objavljujemo, upitaj one koji čitaju Knjigu, prije tebe objavljenu. Tebi Istina od Gospodara tvoga dolazi, i nikako ne budi od onih koji sumnjaju.", TextEnglish = "So if you are in doubt, [O Muḥammad], about that which We have revealed to you, then ask those who have been reading the Scripture before you. The truth has certainly come to you from your Lord, so never be among the doubters.", TextGerman = "Wenn du über das, was Wir zu dir (als Offenbarung) hinabgesandt haben, im Zweifel bist, dann frag diejenigen, die vor dir die Schrift lesen. Dir ist ja die Wahrheit von deinem Herrn zugekommen, so gehöre nun nicht zu den Zweiflern," });
                        surah10.VerseList.Add(new Verse { Number = 95, TextBosnian = "I ne budi nikako od onih koji Allahove ajete i znakove poriču, pa da u gubitnike spadao ne bi.", TextEnglish = "And never be of those who deny the signs of Allāh and [thus] be among the losers.[540]", TextGerman = "und gehöre auch nicht zu denen, die Allahs Zeichen für Lüge erklären, sonst wirst du zu den Verlierern gehören." });
                        surah10.VerseList.Add(new Verse { Number = 96, TextBosnian = "A oni za koje se ispuni Riječ Gospodara tvoga zaista neće vjerovati,", TextEnglish = "Indeed, those upon whom the word [i.e., decree] of your Lord has come into effect will not believe,", TextGerman = "Diejenigen,gegen die sich das Wort deines Herrn bewahrheitet hat, glauben nicht -," });
                        surah10.VerseList.Add(new Verse { Number = 97, TextBosnian = "makar im došli svi dokazi, sve dok ne dožive patnju bolnu.", TextEnglish = "Even if every sign should come to them, until they see the painful punishment.", TextGerman = "auch wenn jedes Zeichen zu ihnen käme -, bis sie die schmerzhafte Strafe sehen." });
                        surah10.VerseList.Add(new Verse { Number = 98, TextBosnian = "Zašto nije bilo nijednog naselja koje je povjerovalo i kome je vjerovanje njegovo koristilo, osim naroda Junusova; kada su povjerovali, od njih smo patnju poniženja u životu na ovom svijetu otklonili, i dali da još neko vrijeme uživaju?", TextEnglish = "Then has there not been a [single] city that believed so its faith benefited it except the people of Jonah? When they believed, We removed from them the punishment of disgrace in worldly life and gave them enjoyment [i.e., provision] for a time.", TextGerman = "Wenn doch (irgend)eine Stadt geglaubt hätte, so daß ihr Glaube ihr genützt hätte! (Keine tat es), außer dem Volk des Yunus. Als diese glaubten, hoben Wir die schändliche Strafe im diesseitigen Leben von ihnen auf und gewährten ihnen Nießbrauch auf Zeit." });
                        surah10.VerseList.Add(new Verse { Number = 99, TextBosnian = "Da Gospodar tvoj hoće na Zemlji bi, baš svi, bili vjernici. Pa zašto onda ti da nagoniš ljude da budu vjernici?", TextEnglish = "And had your Lord willed, those on earth would have believed - all of them entirely. Then, [O Muḥammad], would you compel the people in order that they become believers?", TextGerman = "Und wenn dein Herr wollte, würden fürwahr alle auf der Erde zusammen gläubig werden. Willst du etwa die Menschen dazu zwingen, gläubig zu werden?" });
                        surah10.VerseList.Add(new Verse { Number = 100, TextBosnian = "Nijedna osoba ne može vjerovati osim s Allahovom dozvolom; a On spusti propast na one koji ne razumiju.", TextEnglish = "And it is not for a soul [i.e., anyone] to believe except by permission of Allāh, and He will place defilement[541] upon those who will not use reason.", TextGerman = "Keiner Seele ist es möglich zu glauben, außer mit Allahs Erlaubnis. Und Er legt den Greuel auf diejenigen, die nicht begreifen." });
                        surah10.VerseList.Add(new Verse { Number = 101, TextBosnian = "Reci: 'Posmatrajte ono što je na nebesima i na Zemlji!', a ni od kakve koristi neće biti znakovi i upozorenja narodu koji ne vjeruje.", TextEnglish = "Say, 'Observe what is in the heavens and the earth.' But of no avail will be signs or warners to a people who do not believe.", TextGerman = "Sag: Schaut, was in den Himmeln und auf der Erde ist! Aber die Zeichen und die Warnungen werden den Leuten, die nicht glauben, nicht nützen." });
                        surah10.VerseList.Add(new Verse { Number = 102, TextBosnian = "Zar oni čekaju da ih snađe nešto slično onome što je snašlo one koji su prije njih bili i nestali?! Reci: 'Pa čekajte, i ja ću s vama čekati!'", TextEnglish = "So do they wait except for like [what occurred in] the days of those who passed on before them? Say, 'Then wait; indeed, I am with you among those who wait.'", TextGerman = "Haben sie denn etwas anderes zu erwarten, als was den Tagen derer gleicht, die vor ihnen dahingegangen sind? Sag: So wartet ab! Ich bin mit euch einer von denen, die abwarten." });
                        surah10.VerseList.Add(new Verse { Number = 103, TextBosnian = "Poslije smo spasavali poslanike Naše i one koji su vjerovali. Eto, tako je dužnost Naša da spasimo vjernike.", TextEnglish = "Then We will save Our messengers and those who have believed. Thus, it is an obligation upon Us that We save the believers.[542]", TextGerman = "Dann werden Wir Unsere Gesandten und diejenigen, die glauben, erretten. So ist es Uns eine Pflicht: Wir retten die Gläubigen." });
                        surah10.VerseList.Add(new Verse { Number = 104, TextBosnian = "Reci: 'O ljudi, ako imate sumnje spram vjere moje - pa, ja neću obožavati one koje vi, mimo Allaha, obožavate, već ću ibadet činiti Allahu, Koji će vam duše uzeti. Meni je naređeno da u vjernike spadam.", TextEnglish = "Say, [O Muḥammad], 'O people, if you are in doubt as to my religion - then I do not worship those which you worship besides Allāh; but I worship Allāh, who causes your death. And I have been commanded to be of the believers", TextGerman = "Sag: O ihr Menschen, wenn ihr über meine Religion im Zweifel seid, so diene ich nicht denjenigen, denen ihr anstatt Allahs dient, sondern ich diene Allah, Der euch abberuft. Und mir ist befohlen worden, einer der Gläubigen zu sein." });
                        surah10.VerseList.Add(new Verse { Number = 105, TextBosnian = "I naređeno mi je: 'Predaj se pravoj vjeri iskreno, i nikako ne budi iz reda onih koji Allahu u obožavanju druge pridružuju,", TextEnglish = "And [commanded], 'Direct your face [i.e., self] toward the religion, inclining to truth, and never be of those who associate others with Allāh;", TextGerman = "Und: Richte dein Gesicht aufrichtig zur Religion hin als Anhänger des rechten Glaubens, und gehöre ja nicht zu den Götzendienern!" });
                        surah10.VerseList.Add(new Verse { Number = 106, TextBosnian = "i, pored Allaha, ne moli se onome ko ti ne može ni koristiti ni nauditi, jer ako bi to uradio, uistinu, bi u zulumćare spadao.'", TextEnglish = "And do not invoke[543] besides Allāh that which neither benefits you nor harms you, for if you did, then indeed you would be of the wrongdoers.''[544]", TextGerman = "Und rufe nicht außer Allah an, was dir weder nützt noch schadet! Wenn du es tust, dann gehörst du folglich zu den Ungerechten." });
                        surah10.VerseList.Add(new Verse { Number = 107, TextBosnian = "Ako ti Allah kakvu nevolju da, niko je osim Njega ne može otkloniti, a ako ti htjedne dobro, pa - niko ne može blagodat Njegovu spriječiti; On njome nagrađuje onoga koga hoće od robova Svojih; On je Oprostitelj grijeha, Milostivi.", TextEnglish = "And if Allāh should touch you with adversity, there is no remover of it except Him; and if He intends for you good, then there is no repeller of His bounty. He causes it to reach whom He wills of His servants. And He is the Forgiving, the Merciful.", TextGerman = "Wenn Allah dir Unheil widerfahren läßt, so kann es keiner hinwegnehmen außer Ihm. Und wenn Er für dich etwas Gutes will, so kann keiner Seine Huld zurückweisen. Er trifft damit, wen Er will von Seinen Dienern. Er ist der Allvergebende und Barmherzige." });
                        surah10.VerseList.Add(new Verse { Number = 108, TextBosnian = "Reci: 'O ljudi, Istina vam je došla od Gospodara vašeg, i onaj ko se uputi Pravim putem- uputio se za svoje dobro, a onaj ko krene stranputicom, krenuo je na svoju štetu, a ja nisam zadužen da vam Uputu dam.'", TextEnglish = "Say, 'O mankind, the truth has come to you from your Lord, so whoever is guided is only guided for [the benefit of] his soul, and whoever goes astray only goes astray [in violation] against it. And I am not over you a manager.'", TextGerman = "Sag: O ihr Menschen! Zu euch ist nunmehr die Wahrheit von eurem Herrn gekommen. Wer sich rechtleiten läßt, der ist nur zu seinem eigenen Vorteil rechtgeleitet. Und wer irregeht, der geht nur zu seinem Nachteil irre. Und ich bin nicht euer Sachwalter." });
                        surah10.VerseList.Add(new Verse { Number = 109, TextBosnian = "Ti slijedi ono što ti se objavljuje i budi strpljiv dok Allah ne presudi, On je sudija najbolji!", TextEnglish = "And follow what is revealed to you, [O Muḥammad], and be patient until Allāh will judge. And He is the best of judges.", TextGerman = "Und folge dem, was dir (als Offenbarung) eingegeben wird, und gedulde dich, bis Allah richtet! Er ist der Beste derer, die richten." });
                        result = surah10;
                        break;
                    #endregion

                    // 
                    #region 11 Hud
                    case 11:
                        Surah surah11 = new Surah(); surah11.Name = Constants.Surah11Name; surah11.Number = 11; surah11.VerseList = new List<Verse>();
                        surah11.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah11.VerseList.Add(new Verse { Number = 1, TextBosnian = "Elif-lam-ra, Ovo je Knjiga čiji su ajeti usavršeni a potom razjašnjeni, od mudrog i Onog Koji sve u potpunosti zna,", TextEnglish = "Alif, Lām, Rā.[546] [This is] a Book whose verses are perfected and then presented in detail from [one who is] Wise and Aware[547]", TextGerman = "Alif-Lam-Ra. (Dies ist) ein Buch, dessen Zeichen eindeutig festgefügt und hierauf ausführlich dargelegt sind von Seiten eines Allweisen und Allkundigen:" });
                        surah11.VerseList.Add(new Verse { Number = 2, TextBosnian = "da samo Allahu ibadet činite - ja sam vam od Njega upozoritelj i donositelj radosnih vijesti,", TextEnglish = "[Through a messenger, saying], 'Do not worship except Allāh. Indeed, I am to you from Him a warner and a bringer of good tidings,'", TextGerman = "Ihr sollt nur Allah dienen. Gewiß, ich bin euch von Ihm (aus) ein Warner und Frohbote -," });
                        surah11.VerseList.Add(new Verse { Number = 3, TextBosnian = "da od Gospodara svoga oprosta tražite i da se pokajete, a On će vam dati da do roka koji je određen lijepo proživite i svakom darežljivom dat će iz Svoje darežljivosti. A ako se okrenete - pa, ja se, zaista, bojim za vas patnje na Velikom danu.", TextEnglish = "And [saying], 'Seek forgiveness of your Lord and repent to Him, [and] He will let you enjoy a good provision for a specified term and give every doer of favor his favor [i.e., reward]. But if you turn away, then indeed, I fear for you the punishment of a great Day.", TextGerman = "und: Bittet euren Herrn um Vergebung, hierauf wendet euch Ihm in Reue zu, so wird Er euch einen schönen Nießbrauch auf eine festgesetzte Frist gewähren, und Er wird jedem, der voll Huld ist, Seine Huld gewähren. Wenn sie sich jedoch abkehren, (so sag:) Gewiß, ich fürchte für euch die Strafe eines schweren Tages." });
                        surah11.VerseList.Add(new Verse { Number = 4, TextBosnian = "Allahu ćete se vratiti, a On Svojom moći sve nadilazi!", TextEnglish = "To Allāh is your return, and He is over all things competent.'", TextGerman = "Zu Allah ist eure Rückkehr, und Er hat zu allem die Macht." });
                        surah11.VerseList.Add(new Verse { Number = 5, TextBosnian = "Eto, oni grudi svoje uvijaju da bi se od Njega sakrili. A i kad se u odjeću svoju zaogrću, On zna ono što skrivaju i ono što pokazuju - On, uistinu, zna šta se u grudima krije.", TextEnglish = "Unquestionably, they [i.e., the disbelievers] turn away their breasts to hide themselves from him. Unquestionably, [even] when they cover themselves in their clothing, He [i.e., Allāh] knows what they conceal and what they declare. Indeed, He is Knowing of that within the breasts.", TextGerman = "Sicherlich, sie krümmen ihre Brüste zusammen, um sich vor Ihm zu verbergen. Sicherlich, wenn sie sich (auch) mit ihren Gewändern überdecken, Er weiß doch, was sie geheimhalten und was sie offenlegen. Gewiß, Er weiß über das Innerste der Brüste Bescheid." });
                        surah11.VerseList.Add(new Verse { Number = 6, TextBosnian = "Na Zemlji nema nijednog živog bića a da ga Allah ne hrani i da ne zna gdje koje skončava i gdje se sklanja. Sve to ima u jasnoj Knjizi.", TextEnglish = "And there is no creature on earth but that upon Allāh is its provision, and He knows its place of dwelling and place of storage.[548] All is in a clear register.", TextGerman = "Und es gibt kein Tier auf der Erde, ohne daß Allah sein Unterhalt obläge. Und Er kennt seinen Aufenthaltsort und seinen Aufbewahrungsort. Alles ist in einem deutlichen Buch (verzeichnet)." });
                        surah11.VerseList.Add(new Verse { Number = 7, TextBosnian = "On je Taj Koji je za šest dana nebesa i Zemlju stvorio - a Njegov je Arš iznad vode bio - da bi vas iskušao - koji će od vas bolje postupati. Ako ti rekneš: 'Poslije smrti bit ćete doista oživljeni', nevjernici će sigurno reći: 'Ovo nije ništa drugo do očita čarolija!'", TextEnglish = "And it is He who created the heavens and the earth in six days - and His Throne had been upon water - that He might test you as to which of you is best in deed. But if you say, 'Indeed, you are resurrected after death,' those who disbelieve will surely say, 'This is not but obvious magic.'", TextGerman = "Er ist es, Der die Himmel und die Erde in sechs Tagen erschuf, und Sein Thron war auf dem Wasser, damit Er euch prüfe (und feststelle), wer von euch die besten Taten begeht. Und wenn du sagst: Ihr werdet nach dem Tod auferweckt werden, sagen diejenigen, die ungläubig sind, ganz gewiß: Das ist ja nur deutliche Zauberei." });
                        surah11.VerseList.Add(new Verse { Number = 8, TextBosnian = "Ako im Mi patnju do određenog roka odgodimo, oni će sigurno reći: 'Šta je zadržava?' A onoga dana kad im dođe, neće biti od njih otklonjena i sa svih strana bit će okruženi onim čemu su se rugali.", TextEnglish = "And if We hold back from them the punishment for a limited time, they will surely say,[549] 'What detains it?' Unquestionably, on the Day it comes to them, it will not be averted from them, and they will be enveloped by what they used to ridicule.", TextGerman = "Und wenn Wir ihnen die Strafe für einen bestimmten Zeitraum zurückstellen, sagen sie ganz gewiß: Was hält sie zurück? Wahrlich, am Tag, an dem sie über sie kommt, kann sie nicht von ihnen abgewendet werden, und es wird sie das umschließen, worüber sie sich lustig zu machen pflegten." });
                        surah11.VerseList.Add(new Verse { Number = 9, TextBosnian = "Ako čovjeku damo da osjeti milost Našu, pa mu je poslije uskratimo, tada on, zaista, pravi očajnik i nezahvalnik postaje.", TextEnglish = "And if We give man a taste of mercy from Us and then We withdraw it from him, indeed, he is despairing and ungrateful.", TextGerman = "Und wenn Wir den Menschen von Uns (aus) Barmherzigkeit kosten lassen und sie ihm hierauf entziehen, ist er fürwahr sehr verzweifelt und sehr undankbar." });
                        surah11.VerseList.Add(new Verse { Number = 10, TextBosnian = "A ako mu damo da nakon nevolje koja ga je zadesila blagostanje osjeti, on će sigurno reći: 'Nevolje su me napustile!' On je, doista, odviše radostan i hvalisav.", TextEnglish = "But if We give him a taste of favor after hardship has touched him, he will surely say, 'Bad times have left me.' Indeed, he is exultant and boastful -", TextGerman = "Und wenn Wir ihn Angenehmes kosten lassen nach Leid, das ihm widerfuhr, sagt er ganz gewiß: Das Übel ist von mir gewichen. Und er ist fürwahr froh und prahlerisch," });
                        surah11.VerseList.Add(new Verse { Number = 11, TextBosnian = "Osim onih koji su se strpili i dobra djela činili - njima pripada oprost i nagrada velika.", TextEnglish = "Except for those who are patient and do righteous deeds; those will have forgiveness and great reward.", TextGerman = "außer denjenigen, die standhaft sind und rechtschaffene Werke tun; für sie gibt es Vergebung und großen Lohn." });
                        surah11.VerseList.Add(new Verse { Number = 12, TextBosnian = "Nećeš, valjda, nešto od onoga što ti se objavljuje izostaviti, što tišti grudi tvoje, samo zato što oni govore: 'Zašto mu nije poslano kakvo blago ili, zašto s njim nije došao melek?' Ti si samo upozoritelj, a Allah je Zaštitnik svemu.", TextEnglish = "Then would you possibly leave [out] some of what is revealed to you,[550] or is your breast constrained by it because they say, 'Why has there not been sent down to him a treasure or come with him an angel?' But you are only a warner. And Allāh is Disposer of all things.", TextGerman = "Vielleicht möchtest du einen Teil von dem, was dir (als Offenbarung) eingegeben wird, auslassen und deine Brust ist dadurch beklommen, und dies, weil sie sagen: Wäre doch ein Schatz auf ihn herabgesandt worden oder ein Engel mit ihm gekommen! Du aber bist nur ein Warner. Und Allah ist Sachwalter über alles." });
                        surah11.VerseList.Add(new Verse { Number = 13, TextBosnian = "Zar oni da govore: 'On ga izmišlja!' Reci: 'Pa sačinite vi deset Kur'anu sličnih, izmišljenih sura, i koga god hoćete, od onih u koje pored Allaha vjerujete, u pomoć pozovite, ako istinu govorite!'", TextEnglish = "Or do they say, 'He invented it'? Say, 'Then bring ten sūrahs like it that have been invented and call upon [for assistance] whomever you can besides Allāh, if you should be truthful.'", TextGerman = "Oder sagen sie: Er hat ihn ersonnen Sag: Dann bringt doch zehn ersonnene Suren bei, die ihm gleich sind, und ruft an, wen ihr könnt, außer Allah, wenn ihr wahrhaftig seid." });
                        surah11.VerseList.Add(new Verse { Number = 14, TextBosnian = "A ako vam se ne odazovu, onda znajte da se on objavljuje samo s Allahovim znanjem i da nema boga osim Njega-pa jeste li muslimani?!", TextEnglish = "And if they do not respond to you - then know that it [i.e., the Qur’ān] was revealed with the knowledge of Allāh[551] and that there is no deity except Him. Then,[552] would you [not] be Muslims?", TextGerman = "Und wenn sie euch nicht erhören, dann wisset, daß er nur mit Allahs Wissen herabgesandt worden ist und daß es keinen Gott gibt außer Ihm? Werdet ihr nun (Allah) ergeben sein?" });
                        surah11.VerseList.Add(new Verse { Number = 15, TextBosnian = "Onima koji streme životu na ovome svijetu i dunjalučkim ukrasima, Mi ćemo im to zbog njihovih djela - u potpunosti - i dati, i u tome zakinuti neće biti.", TextEnglish = "Whoever desires the life of this world and its adornments - We fully repay them for their deeds therein,[553] and they therein will not be deprived.", TextGerman = "Wer immer das diesseitige Leben und seinen Schmuck will, dem lassen wir in ihm (den Lohn für) seine Werke in vollem Maß zukommen, und ihm wird darin nichts geschmälert." });
                        surah11.VerseList.Add(new Verse { Number = 16, TextBosnian = "To su oni koji će na ahiretu samo vatru imati; tamo će propasti sve ono što su na Zemlji činili i bit će uzaludno sve što su radili.", TextEnglish = "Those are the ones for whom there is not in the Hereafter but the Fire. And lost is what they did therein,[554] and worthless is what they used to do.", TextGerman = "Das sind diejenigen, für die es im Jenseits nur das (Höllen)feuer gibt. Nutzlos ist, was sie in ihm gemacht haben, und hinfallig wird, was sie zu tun pflegten." });
                        surah11.VerseList.Add(new Verse { Number = 17, TextBosnian = "Jesu li oni kao oni kojima je jasno ko je Gospodar njihov - kojima je došao Kur'an - svjedok Njegov, a još prije njega i Knjiga Musaova, putovođa i milost?! To su oni koji vjeruju u njega. A ko god ga negira, među raznim grupacijama, Vatra će mu mjesto gdje će skončati biti. Zato, ti nikako ne sumnjaj u njega, on je zaista istina od Gospodara tvoga, ali većina ljudi ne vjeruje.", TextEnglish = "So is one who [stands] upon a clear evidence from his Lord [like the aforementioned]? And a witness[555] from Him follows it,[556] and before it was the Scripture of Moses to lead and as mercy. Those [believers in the former revelations] believe in it [i.e., the Qur’ān]. But whoever disbelieves in it from the [various] factions - the Fire is his promised destination. So be not in doubt about it. Indeed, it is the truth from your Lord, but most of the people do not believe.", TextGerman = "Ist denn einer (den anderen gleich), der sich auf einen klaren Beweis von seinem Herrn stützt, und auf den ein Zeuge von Ihm folgt, dem die Schrift Musas vorausging als Vorbild und Barmherzigkeit? Diese glauben daran. Wer aber von den Gruppierungen ihn verleugnet, dessen Verabredung(sort) ist das (Höllen)feuer. So sei nicht über ihn im Zweifel, er ist die Wahrheit von deinem Herrn. Aber die meisten Menschen glauben nicht." });
                        surah11.VerseList.Add(new Verse { Number = 18, TextBosnian = "A ima li većih zulumćara od onih koji o Allahu laž iznose?! Oni će pred Gospodara svoga biti dovedeni, a svjedoci će reći: 'Ovi su izmišljali laži o Gospodaru svome!' I, Allahovo prokletstvo stiglo je zulumćare,", TextEnglish = "And who is more unjust than he who invents a lie about Allāh? Those will be presented before their Lord, and the witnesses will say, 'These are the ones who lied against their Lord.' Unquestionably, the curse of Allāh is upon the wrongdoers.", TextGerman = "Und wer ist ungerechter, als wer gegen Allah eine Lüge ersinnt? Jene werden ihrem Herrn vorgeführt, und die Zeugen werden sagen: Das sind diejenigen, die gegen ihren Herrn gelogen haben. Aber wahrlich, Allahs Fluch (kommt) über die Ungerechten," });
                        surah11.VerseList.Add(new Verse { Number = 19, TextBosnian = "koji od Allahova puta odvraćaju i željeli bi da je on krivudav, oni ni u ahiret ne vjeruju.", TextEnglish = "Who averted [people] from the way of Allāh and sought to make it [seem] deviant while they, concerning the Hereafter, were disbelievers.", TextGerman = "die von Allahs Weg abhalten und danach trachten, ihn krumm zu machen, und sie, die sie das Jenseits verleugnen." });
                        surah11.VerseList.Add(new Verse { Number = 20, TextBosnian = "Oni na Zemlji ne mogu Allahu umaći, niti, osim Allaha, drugog zaštitnika imaju. Njihova patnja bit će umnogostručena. Oni nisu mogli čuti, i nisu vidjeli.", TextEnglish = "Those were not causing failure [to Allāh] on earth, nor did they have besides Allāh any protectors. For them the punishment will be multiplied. They were not able to hear, nor did they see.[557]", TextGerman = "Jene werden sich (Allah) auf der Erde nicht entziehen können, und sie werden außer Allah keine Schutzherren haben. Die Strafe wird ihnen vervielfacht. Sie konnten nicht hören, und sie pflegten nicht zu sehen." });
                        surah11.VerseList.Add(new Verse { Number = 21, TextBosnian = "To su oni koji su sami sebe upropastili - a neće biti ni traga onog što su izmišljali.", TextEnglish = "Those are the ones who will have lost themselves, and lost from them is what they used to invent.", TextGerman = "Das sind diejenigen, die ihre Seelen verloren haben, und es ist ihnen entschwunden, was sie zu ersinnen pflegten." });
                        surah11.VerseList.Add(new Verse { Number = 22, TextBosnian = "Bez sumnje, oni će na ahiretu biti najveći gubitnici.", TextEnglish = "Assuredly, it is they in the Hereafter who will be the greatest losers.", TextGerman = "Zweifellos sind sie im Jenseits die größten Verlierer." });
                        surah11.VerseList.Add(new Verse { Number = 23, TextBosnian = "Zaista, oni koji vjeruju i dobra djela čine i koji su Gospodaru svome ponizni - to su stanovnici Dženneta, u njemu će vječno boraviti.", TextEnglish = "Indeed, they who have believed and done righteous deeds and humbled themselves to their Lord - those are the companions of Paradise; they will abide eternally therein.", TextGerman = "Gewiß, diejenigen, die glauben und rechtschaffene Werke tun und sich vor ihrem Herrn demütigen, das sind die Insassen des (Paradies)gartens. Ewig werden sie darin bleiben." });
                        surah11.VerseList.Add(new Verse { Number = 24, TextBosnian = "Te dvije vrste su kao slijep i gluh i kao onaj koji vidi i čuje. A jesu li ta dvojica jednaki?! Pa zašto se ne prisjetite i pouku ne uzmete'!", TextEnglish = "The example of the two parties is like the blind and deaf, and the seeing and hearing. Are they equal in comparison? Then, will you not remember?", TextGerman = "Das Gleichnis der beiden Gruppen ist wie der Blinde und der Taube und der Sehende und der Hörende. Sind die beiden (etwa) einander gleich? Bedenkt ihr denn nicht?" });
                        surah11.VerseList.Add(new Verse { Number = 25, TextBosnian = "I Nuha poslasmo narodu njegovu: 'Ja sam vam, uistinu, jasni upozoritelj,", TextEnglish = "And We had certainly sent Noah to his people, [saying], 'Indeed, I am to you a clear warner", TextGerman = "Und Wir sandten ja bereits Nuh zu seinem Volk: Ich bin euch ja ein deutlicher Warner:" });
                        surah11.VerseList.Add(new Verse { Number = 26, TextBosnian = "da samo Allahu u ibadetu budete; ja se, zaista, plašim za vas patnje na danu bolnome.'", TextEnglish = "That you not worship except Allāh. Indeed, I fear for you the punishment of a painful day.'", TextGerman = "Ihr sollt keinem außer Allah dienen. Gewiß, ich fürchte für euch die Strafe eines schmerzhaften Tages." });
                        surah11.VerseList.Add(new Verse { Number = 27, TextBosnian = "Glavešine naroda njegova, oni koji nisu vjerovali, rekoše: 'Vidimo da si i ti čovjek kao i mi i da te, bez ikakva razmišljanja, slijede samo oni koji su naš najniži sloj; vidimo da vi nikakve prednosti nad nama nemate. Štaviše, mislimo da ste lažljivci.'", TextEnglish = "So the eminent among those who disbelieved from his people said, 'We do not see you but as a man like ourselves, and we do not see you followed except by those who are the lowest of us [and] at first suggestion.[558] And we do not see in you over us any merit; rather, we think you are liars.'", TextGerman = "Da sagte die führende Schar aus seinem Volk, die ungläubig war: Wir sehen in dir nur einen Menschen unseresgleichen. Und wir sehen, daß nur die dir folgen, die unsere Niedrigsten sind, und zwar ohne reifliche Überlegung. Und wir sehen bei euch keinen Vorzug gegenüber uns. Vielmehr meinen wir, daß ihr Lügner seid." });
                        surah11.VerseList.Add(new Verse { Number = 28, TextBosnian = "O narode moj', reče on, 'recite mi, ako mi je Gospodar moj jasan dokaz dao i ako mi je On od Sebe milost-vjerovjesništvo dao, a vi to ne vidite, da li vas možemo prisiliti na to kad vam to po volji nije?!", TextEnglish = "He said, 'O my people, have you considered: if I should be upon clear evidence from my Lord while He has given me mercy from Himself but it has been made unapparent to you, should we force it upon you while you are averse to it?", TextGerman = "Er sagte: O mein Volk, was meint ihr, wenn ich mich auf einen klaren Beweis von meinem Herrn stütze und Er mir eine Barmherzigkeit von Sich hat zukommen lassen, die aber eurem Blick entzogen wurde, sollen wir sie euch da aufzwingen, wo sie euch zuwider ist?" });
                        surah11.VerseList.Add(new Verse { Number = 29, TextBosnian = "O narode moj! Za ovo od vas imetak ne tražim; samo od Allaha ja nagradu želim. I ja neću otjerati one koji vjeruju, oni će sigurno svoga Gospodara sresti; ali, ja vidim da ste vi neuk narod.", TextEnglish = "And O my people, I ask not of you for it any wealth. My reward is not but from Allāh. And I am not one to drive away those who have believed. Indeed, they will meet their Lord, but I see that you are a people behaving ignorantly.", TextGerman = "Und, o mein Volk, ich verlange von euch keinen Besitz dafür. Mein Lohn obliegt nur Allah. Und ich werde nicht diejenigen, die glauben, abweisen; sie werden ihrem Herrn begegnen. Aber ich sehe, ihr seid Leute, die töricht sind." });
                        surah11.VerseList.Add(new Verse { Number = 30, TextBosnian = "O narode moj! Ko bi me od Allaha odbranio kad bih ih otjerao? Zašto se ne prisjetite?!", TextEnglish = "And O my people, who would protect me from Allāh if I drove them away? Then will you not be reminded?", TextGerman = "Und, o mein Volk, wer wird mir gegen Allah helfen, wenn ich sie abweise? Bedenkt ihr denn nicht?" });
                        surah11.VerseList.Add(new Verse { Number = 31, TextBosnian = "Ja vam ne kažem: 'U mene su Allahove riznice' - niti: 'Meni je poznato ono što je čulima nedokučivo' - niti kažem: 'Ja sam melek' - a ne govorim ni o onima koje vaše oči s prezirom gledaju: 'Allah im nikakvo dobro neće dati', Allah najbolje zna šta je u dušama njihovim - a ja bih se tada, sigurno, među zulumćare svrstao.'", TextEnglish = "And I do not tell you that I have the depositories [containing the provision] of Allāh or that I know the unseen, nor do I tell you that I am an angel, nor do I say of those upon whom your eyes look down that Allāh will never grant them any good. Allāh is most knowing of what is within their souls. Indeed, I would then be among the wrongdoers [i.e., the unjust].'", TextGerman = "Und ich sage nicht zu euch, ich besäße die Schatzkammern Allahs, und ich weiß auch nicht das Verborgene; und ich sage nicht, ich sei ein Engel. Noch sage ich von denen, die eure Augen verachten, Allah werde ihnen niemals etwas Gutes zukommen lassen - Allah weiß am besten, was in ihrem Innersten ist -, sonst gehörte ich wahrlich zu den Ungerechten." });
                        surah11.VerseList.Add(new Verse { Number = 32, TextBosnian = "O Nuh', rekoše oni, 'ti si želio da se s nama raspravljaš i dugo si se raspravljao. Daj, neka se ostvari ono što nam prijeteći obećavaš, ako si jedan od iskrenih!'", TextEnglish = "They said, 'O Noah, you have disputed [i.e., opposed] us and been frequent in dispute of us. So bring us what you threaten us, if you should be of the truthful.'", TextGerman = "Sie sagten: O Nuh, du hast bereits mit uns gestritten und dabei den Streit mit uns übertrieben. Bring uns doch her, was du uns androhst, wenn du zu den Wahrhaftigen gehörst!" });
                        surah11.VerseList.Add(new Verse { Number = 33, TextBosnian = "To će vam učiniti samo Allah, ako bude htio', reče on, 'i vi nećete moći umaći.'", TextEnglish = "He said, 'Allāh will only bring it to you if He wills, and you will not cause [Him] failure.", TextGerman = "Er sagte: Nur Allah (allein) wird es euch bringen, wenn Er will, und ihr werdet euch (Ihm) nicht entziehen können." });
                        surah11.VerseList.Add(new Verse { Number = 34, TextBosnian = "Neće vam savjet moj koristiti, ma koliko ja želio da vas savjetujem, ako Allah bude htio da vas u zabludu okrene. On je Gospodar vaš i Njemu ćete se vratiti.'", TextEnglish = "And my advice will not benefit you - although I wished to advise you - if Allāh should intend to put you in error. He is your Lord, and to Him you will be returned.'", TextGerman = "Und mein guter Rat wird, wenn ich euch gut raten will, euch nicht nützen, wenn Allah euch in Verirrung fallen lassen will. Er ist euer Herr, und zu Ihm werdet ihr zurückgebracht." });
                        surah11.VerseList.Add(new Verse { Number = 35, TextBosnian = "Zar da govore: 'On ga izmišlja!' Reci: 'Ako ga izmišljam, grijeh svoj ja ću nositi, a ja nemam ništa s vašim grijesima.'", TextEnglish = "Or do they say [about Prophet Muḥammad (ﷺ)], 'He invented it'? Say, 'If I have invented it, then upon me is [the consequence of] my crime; but I am innocent of what [crimes] you commit.'", TextGerman = "Oder sagen sie: Er hat ihn ersonnen Sag: Wenn ich ihn ersonnen habe, so lastet meine Übeltat auf mir, und ich bin unschuldig an dem, was ihr an Übeltaten begeht." });
                        surah11.VerseList.Add(new Verse { Number = 36, TextBosnian = "I Nuhu bi objavljeno: 'Iz naroda tvoga vjerovat će samo oni koji otprije vjeruju; zato se ne žalosti zbog onoga što oni stalno čine,", TextEnglish = "And it was revealed to Noah that, 'No one will believe from your people except those who have already believed, so do not be distressed by what they have been doing.", TextGerman = "Und Nuh wurde (als Offenbarung) eingegeben: Niemand aus deinem Volk wird glauben außer denen, die bereits geglaubt haben. So sei nicht bekümmert wegen dessen, was sie getan haben." });
                        surah11.VerseList.Add(new Verse { Number = 37, TextBosnian = "I gradi lađu pred Našim očima, i po Našem uputstvu, i ne obraćaj Mi se više zbog onih koji su zulum činili – oni će sigurno biti potopljeni!'", TextEnglish = "And construct the ship under Our observation and Our inspiration and do not address Me concerning those who have wronged; indeed, they are [to be] drowned.'", TextGerman = "Und verfertige das Schiff vor Unseren Augen und nach Unserer Eingebung. Und sprich Mich nicht an zugunsten derer, die Unrecht getan haben; sie werden ertränkt werden." });
                        surah11.VerseList.Add(new Verse { Number = 38, TextBosnian = "I on je gradio lađu. I kad god bi pored njega prolazile glavešine iz naroda njegova, rugali bi mu se. 'Ako se vi rugate nama', govorio je on, 'rugat ćemo se i mi vama, onako kako se vi rugate,", TextEnglish = "And he constructed the ship, and whenever an assembly of the eminent of his people passed by him, they ridiculed him. He said, 'If you ridicule us, then we will ridicule you just as you ridicule.", TextGerman = "Er verfertigte das Schiff, wobei jedesmal, wenn eine führende Schar aus seinem Volk an ihm vorbeikam, sie über ihn spotteten. Er sagte: Wenn ihr über uns spottet, werden auch wir über euch spotten, so wie ihr (jetzt über uns) spottet." });
                        surah11.VerseList.Add(new Verse { Number = 39, TextBosnian = "i saznat ćete, zaista, kome će doći kazna koja će ga poniziti i koga će vječna patnja snaći.'", TextEnglish = "And you are going to know who will get a punishment that will disgrace him [on earth] and upon whom will descend an enduring punishment [in the Hereafter].'", TextGerman = "Dann werdet ihr (noch) erfahren, über wen eine Strafe kommen, die ihn in Schande stürzt, und (über wen) eine beständige Strafe hereinbrechen wird." });
                        surah11.VerseList.Add(new Verse { Number = 40, TextBosnian = "I kad je stigla odredba Naša i voda s površine zemlje pokuljala, Mi smo rekli: 'Ukrcaj u lađu od svake životinjske vrste po jedan par, i porodicu svoju - osim onih o čijoj je kazni prethodila Riječ odredbe - i vjernike', a malo je bilo onih koji su s njim vjerovali.", TextEnglish = "[So it was], until when Our command came and the oven overflowed,[559] We said, 'Load upon it [i.e., the ship] of each [creature] two mates and your family, except those about whom the word [i.e., decree] has preceded, and [include] whoever has believed.' But none had believed with him, except a few.", TextGerman = "(So ging es,) bis nun Unser Befehl kam und der Ofen brodelte; Wir sagten: Lade darin von jeder (Art) zwei, ein Paar, und deine Angehörigen außer demjenigen, gegen den das Wort vorher ergangen ist, und diejenigen, die glauben! Mit ihm glaubten aber nur wenige." });
                        surah11.VerseList.Add(new Verse { Number = 41, TextBosnian = "I on reče: 'Ukrcajte se u nju, u ime Allaha, neka plovi i neka pristane! Gospodar moj uistinu je Oprostitelj grijeha, Milostivi.'", TextEnglish = "And [Noah] said, 'Embark therein; in the name of Allāh [are] its course and its anchorage. Indeed, my Lord is Forgiving and Merciful.'", TextGerman = "Und er sagte: Steigt hinein! Im Namen Allahs erfolge seine Fahrt und seine Landung. Mein Herr ist fürwahr Allvergebend und Barmherzig." });
                        surah11.VerseList.Add(new Verse { Number = 42, TextBosnian = "I ona ih je ponijela na valovima velikim kao brda; i Nuh zovnu sina svoga koji je bio izdvojen: 'O sinko moj, ukrcaj se s nama, ne budi s nevjernicima!'", TextEnglish = "And it sailed with them through waves like mountains, and Noah called to his son who was apart [from them], 'O my son, come aboard with us and be not with the disbelievers.'", TextGerman = "Es fuhr mit ihnen dahin inmitten von Wogen wie Berge. Nuh rief seinem Sohn zu, der sich abseits hielt: O mein lieber Sohn, steig mit uns ein und sei nicht mit den Ungläubigen!" });
                        surah11.VerseList.Add(new Verse { Number = 43, TextBosnian = "A on reče: 'Sklonit ću se na kakvo brdo koje će me od vode zaštititi. Od Allahove odredbe danas nema spasa, osim onome kome se On smilovao!', reče Nuh, i val ih razdvoji, i on potopljen bi.", TextEnglish = "[But] he said, 'I will take refuge on a mountain to protect me from the water.' [Noah] said, 'There is no protector today from the decree of Allāh, except for whom He gives mercy.' And the waves came between them, and he was among the drowned.", TextGerman = "Er sagte: Ich werde auf einem Berg Zuflucht suchen, der mich vor dem Wasser schützt. Er sagte: Es gibt heute nichts, das vor dem Befehl Allahs schützen könnte, außer für den, dessen Er Sich erbarmt. Die Wogen trennten sie beide, und so gehörte er zu denjenigen, die ertränkt wurden." });
                        surah11.VerseList.Add(new Verse { Number = 44, TextBosnian = "I bi rečeno: 'O Zemljo, gutaj vodu svoju, a ti, o nebo, prestani!' I voda se povuče i ispuni se odredba, a lađa pristade na planini El-Džudijj i bi rečeno: 'Daleko neka je narod koji zulum čini!'", TextEnglish = "And it was said, 'O earth, swallow your water, and O sky, withhold [your rain].' And the water subsided, and the matter was accomplished, and it [i.e., the ship] came to rest on the [mountain of] Jūdiyy. And it was said, 'Away with the wrongdoing people.'", TextGerman = "Und es wurde gesagt: O Erde, schlucke dein Wasser! O Himmel, halt ein! Das Wasser nahm ab, und die Angelegenheit war entschieden. Es saß auf dem (Berg) Gudi auf. Und es wurde gesagt: Weg mit dem ungerechten Volk!" });
                        surah11.VerseList.Add(new Verse { Number = 45, TextBosnian = "A Nuh se prije toga obrati Gospodaru svome i reče: 'Gospodaru moj, sin moj je, uistinu, iz porodice moje, a obećanje Tvoje je, zaista, istinito i Ti si Sudija najpravedniji!'", TextEnglish = "And Noah called to his Lord and said, 'My Lord, indeed my son is of my family; and indeed, Your promise is true; and You are the most just of judges!'", TextGerman = "Und Nuh rief zu seinem Herrn. Er sagte: Mein Herr, mein Sohn gehört zu meinen Angehörigen, und Dein Versprechen ist die Wahrheit, und Du bist der Weiseste derer, die richten." });
                        surah11.VerseList.Add(new Verse { Number = 46, TextBosnian = "O Nuh', odgovori mu, 'on nije od onih iz porodice tvoje koje sam obećao spasiti; on, doista, radi ono što ne valja, zato Me ne moli za ono što ne znaš! Upozoravam te da ne budeš među neznalicama.'", TextEnglish = "He said, 'O Noah, indeed he is not of your family; indeed, he is [one whose] work was other than righteous, so ask Me not for that about which you have no knowledge. Indeed, I advise you, lest you be among the ignorant.'", TextGerman = "Er sagte: O Nuh, er gehört nicht zu deinen Angehörigen. Er ist eine Tat, die nicht rechtschaffen ist. So bitte Mich nicht um etwas, wovon du kein Wissen hast! Ich ermahne dich, nicht zu den Toren zu gehören." });
                        surah11.VerseList.Add(new Verse { Number = 47, TextBosnian = "Gospodaru moj', reče, 'Tebi se ja utječem da Te više nikada ne zamolim za ono što ne znam! Ako mi ne oprostiš i ne smiluješ mi se, sigurno ću biti među gubitnicima.'", TextEnglish = "[Noah] said, 'My Lord, I seek refuge in You from asking that of which I have no knowledge. And unless You forgive me and have mercy upon me, I will be among the losers.'", TextGerman = "Er sagte: Mein Herr, ich suche Schutz bei Dir (davor), daß ich Dich um etwas bitte, wovon ich kein Wissen habe! Wenn Du mir nicht vergibst und Dich meiner nicht erbarmst, werde ich zu den Verlierern gehören." });
                        surah11.VerseList.Add(new Verse { Number = 48, TextBosnian = "O Nuh', bi rečeno 'iskrcaj se, sa selamom Našim i blagoslovima tebi i narodima koji će se izroditi od ovih koji su s tobom! Bit će naroda kojima ćemo davati da uživaju, a kojima ćemo Mi dati da osjete bolnu patnju!'", TextEnglish = "It was said, 'O Noah, disembark in security from Us and blessings upon you and upon nations [descending] from those with you. But other nations [of them] We will grant enjoyment; then there will touch them from Us a painful punishment.'", TextGerman = "Es wurde gesagt: O Nuh, steige hinunter in Frieden von Uns und mit Segnungen über dich und über Gemeinschaften von denen, die mit dir sind. Es gibt (aber auch) Gemeinschaften, denen Wir einen Nießbrauch geben werden, hierauf wird ihnen schmerzhafte Strafe von Uns widerfahren." });
                        surah11.VerseList.Add(new Verse { Number = 49, TextBosnian = "Taj slučaj u vijesti iz onog što je čulima nedokučivo spada, koje ti Mi objavljujemo; ni ti ni narod tvoj niste prije ovoga o tome ništa znali. Zato budi strpljiv, ishod će, zaista, u korist bogobojaznih biti.", TextEnglish = "That is from the news of the unseen which We reveal to you, [O Muḥammad]. You knew it not, neither you nor your people, before this. So be patient; indeed, the [best] outcome is for the righteous.", TextGerman = "Diese (Nachrichten) gehören zu den Nachrichten vom Verborgenen, die Wir dir (als Offenbarung) eingeben. Du wußtest sie zuvor nicht, weder du noch dein Volk. Sei nun standhaft! Das (gute) Ende gehört den Gottesfürchtigen." });
                        surah11.VerseList.Add(new Verse { Number = 50, TextBosnian = "I Adu - brata njihova Huda poslasmo. 'O narode moj', govorio je on, 'Allahu ibadetu činite, vi drugog boga osim Njega nemate, vi samo neistine iznosite.", TextEnglish = "And to ʿAad [We sent] their brother Hūd. He said, 'O my people, worship Allāh; you have no deity other than Him. You are not but inventors [of falsehood].", TextGerman = "Und (Wir sandten) zu ’Ad ihren Bruder Hud. Er sagte: O mein Volk, dient Allah! Keinen Gott habt ihr außer Ihm. Ihr ersinnt ja nur Lügen." });
                        surah11.VerseList.Add(new Verse { Number = 51, TextBosnian = "O narode moj, ja ne tražim od vas nagradu za ovo; nagradu želim samo od Onoga Koji me je stvorio! Zar ne razumijete?!", TextEnglish = "O my people, I do not ask you for it [i.e., my advice] any reward. My reward is only from the one who created me. Then will you not reason?", TextGerman = "O mein Volk, ich verlange von euch keinen Lohn dafür. Mein Lohn obliegt nur demjenigen, Der mich erschaffen hat. Begreift ihr denn nicht?" });
                        surah11.VerseList.Add(new Verse { Number = 52, TextBosnian = "O narode moj, molite Gospodara svoga da vam oprosti, i pokajte Mu se, a On će vam slati kišu obilnu i dat će vam još veću snagu, uz onu koju imate, i ne odlazite kao oni koji zlo čine!'", TextEnglish = "And O my people, ask forgiveness of your Lord and then repent to Him. He will send [rain from] the sky upon you in showers and increase you in strength [added] to your strength. And do not turn away, [being] criminals.'", TextGerman = "Und, o mein Volk, bittet euren Herrn um Vergebung, hierauf bereut vor Ihm, so wird Er den Regen auf euch ergiebig (herab)senden und euch (noch mehr) Kraft zu eurer Kraft hinzufügen. Und kehrt euch nicht als Übeltäter ab!" });
                        surah11.VerseList.Add(new Verse { Number = 53, TextBosnian = "O Hud', govorili su oni, 'nisi nam nikakav dokaz donio i mi na samu tvoju riječ nećemo napustiti božanstva svoja, mi tebi ne vjerujemo.", TextEnglish = "They said, 'O Hūd, you have not brought us clear evidence, and we are not ones to leave our gods on your say-so. Nor are we believers in you.", TextGerman = "Sie sagten: O Hud, du hast uns keinen klaren Beweis gebracht. Wir wollen nicht unsere Götter auf dein Wort hin verlassen, und wir werden deinetwegen nicht gläubig werden." });
                        surah11.VerseList.Add(new Verse { Number = 54, TextBosnian = "Mi kažemo samo to da te je neko božanstvo naše zlom pogodilo.' 'Ja pozivam Allaha za svjedoka', reče on, 'a i vi posvjedočite da ja nemam ništa s tim što vi u obožavanju pridružujete –", TextEnglish = "We only say that some of our gods have possessed you with evil [i.e., insanity].' He said, 'Indeed, I call Allāh to witness, and witness [yourselves] that I am free from whatever you associate with Allāh", TextGerman = "Wir können nur sagen, daß einige unserer Götter dich mit etwas Bösem heimgesucht haben. Er sagte: Ich nehme Allah zum Zeugen, und bezeugt auch ihr, daß ich mich lossage von dem, was ihr (Ihm) beigesellt" });
                        surah11.VerseList.Add(new Verse { Number = 55, TextBosnian = "Njemu; i zato svi zajedno protiv mene lukavstvo smislite i nimalo mi vremena ne dajte,", TextEnglish = "Other than Him. So plot against me all together; then do not give me respite.", TextGerman = "außer Ihm. So schmiedet alle gegen mich eure List und gewährt mir hierauf keinen Aufschub!" });
                        surah11.VerseList.Add(new Verse { Number = 56, TextBosnian = "ja se uzdam u Allaha, u moga i vašeg Gospodara! Sve živo samo je Njemu potčinjeno; Gospodar moj je, zaista, na putu pravome.'", TextEnglish = "Indeed, I have relied upon Allāh, my Lord and your Lord. There is no creature but that He holds it by its forelock [i.e., controls it]. Indeed, my Lord is on a path [that is] straight.", TextGerman = "Ich verlasse mich auf Allah, meinen Herrn und euren Herrn. Es gibt kein Tier, das Er nicht an seiner Stirnlocke hielte. Gewiß, mein Herr befindet sich auf einem geraden Weg." });
                        surah11.VerseList.Add(new Verse { Number = 57, TextBosnian = "Pa ako se okrenete - a ja sam vam saopćio ono što vam je po meni poslano - Gospodar moj će umjesto vas drugi narod dati, i vi Mu nećete ničim nauditi; Gospodar moj zaista bdije nad svim.'", TextEnglish = "But if you turn away, then I have already conveyed that with which I was sent to you. My Lord will give succession to a people other than you, and you will not harm Him at all. Indeed my Lord is, over all things, Guardian.'[560]", TextGerman = "Wenn ihr euch abkehrt, so habe ich euch ja das (von der Botschaft) ausgerichtet, womit ich zu euch gesandt wurde. Mein Herr wird ein anderes Volk nach euch folgen lassen, und ihr könnt Ihm keinerlei Schaden zufügen. Gewiß, mein Herr ist Hüter über alles." });
                        surah11.VerseList.Add(new Verse { Number = 58, TextBosnian = "I kad je došla odredba Naša, Mi smo, milošću Našom, Huda i vjernike s njim spasili i patnje surove ih poštedjeli.", TextEnglish = "And when Our command came, We saved Hūd and those who believed with him, by mercy from Us; and We saved them from a harsh punishment.", TextGerman = "Als nun Unser Befehl kam, erretteten Wir Hud und diejenigen, die mit ihm glaubten, durch Barmherzigkeit von Uns; und Wir erretteten sie vor harter Strafe." });
                        surah11.VerseList.Add(new Verse { Number = 59, TextBosnian = "Eto, to je bio narod Ad, oni su dokaze Gospodara svoga poricali i bili neposlušni poslanicima svojim, i slijedili su zapovijed svakog silnika, inadžije.", TextEnglish = "And that was ʿAad, who rejected the signs of their Lord and disobeyed His messengers and followed the order of every obstinate tyrant.", TextGerman = "Das waren die ’Ad. Sie verleugneten die Zeichen ihres Herrn und widersetzten sich Seinen Gesandten und folgten dem Befehl eines jeden trotzigen Gewalthabers." });
                        surah11.VerseList.Add(new Verse { Number = 60, TextBosnian = "I prokletstvo je na ovom svijetu stalno bilo s njim, a bit će i na Sudnjem danu. Ad, doista, nije vjerovao u Gospodara svoga; daleko neka je Ad, narod Hudov!", TextEnglish = "And they were [therefore] followed in this world with a curse and [as well] on the Day of Resurrection. Unquestionably, ʿAad denied their Lord; then away with ʿAad, the people of Hūd.", TextGerman = "Aber ein Fluch folgte ihnen im Diesseits nach und (wird ihnen) am Tag der Auferstehung (nachfolgen). Sicherlich, die ’Ad verleugneten ihren Herrn. Aber ja, weg mit ’Ad, dem Volk von Hud!" });
                        surah11.VerseList.Add(new Verse { Number = 61, TextBosnian = "I Semudu - brata njihova Saliha poslasmo. 'O narode moj', govorio je on, 'ibadet samo Allahu činite, vi drugog boga osim Njega nemate! On vas je od zemlje stvorio i dao vam da živite na njoj! Zato Ga molite da vam oprosti i pokajte Mu se! Gospodar moj je, zaista, blizu i odaziva se.'", TextEnglish = "And to Thamūd [We sent] their brother Ṣāliḥ. He said, 'O my people, worship Allāh; you have no deity other than Him. He has produced you from the earth and settled you in it, so ask forgiveness of Him and then repent to Him. Indeed, my Lord is near and responsive.'", TextGerman = "Und (Wir sandten) zu Tamud ihren Bruder Salih. Er sagte: O mein Volk, dient Allah! Keinen Gott habt ihr außer Ihm. Er hat euch aus der Erde entstehen lassen und sie euch zu besiedeln gegeben. So bittet Ihn um Vergebung, hierauf bereut vor Ihm. Mein Herr ist nahe und erhört (die Gebete)." });
                        surah11.VerseList.Add(new Verse { Number = 62, TextBosnian = "O Salih', govorili su oni, 'ti si među nama prije ovoga cijenjen bio. Zašto braniš da obožavamo ono što su preci naši obožavali? Mi uveliko sumnjamo u ono čemu nas ti pozivaš.'", TextEnglish = "They said, 'O Ṣāliḥ, you were among us a man of promise before this. Do you forbid us to worship what our fathers worshipped? And indeed we are, about that to which you invite us, in disquieting doubt.'", TextGerman = "Sie sagten: O Salih, du warst zuvor unter uns einer, auf den man Hoffnung setzte. Willst du uns denn verbieten, dem zu dienen, dem unsere Väter dienen? Wir sind fürwahr über das, wozu du uns aufrufst, in einem starken Zweifel." });
                        surah11.VerseList.Add(new Verse { Number = 63, TextBosnian = "O narode moj', govorio je on, 'šta mislite: ako je meni jasno ko je Gospodar moj i ako mi je On sam milost - vjerovjesništvo dao, pa ko će me od Allaha odbraniti ako Mu ne budem poslušan? Vi možete samo uvećati propast moju.", TextEnglish = "He said, 'O my people, have you considered: if I should be upon clear evidence from my Lord and He has given me mercy from Himself, who would protect me from Allāh if I disobeyed Him? So you would not increase me except in loss.", TextGerman = "Er sagte: O mein Volk, was meint ihr, wenn ich mich auf einen klaren Beweis von meinem Herrn stütze und Er mir Barmherzigkeit von Sich hat zukommen lassen, wer wird mir gegen Allah helfen, wenn ich mich Ihm widersetze? Ihr würdet mir nur (meinen) Verlust mehren." });
                        surah11.VerseList.Add(new Verse { Number = 64, TextBosnian = "O narode moj, evo, ova Allahova kamila je znak za vas, pa je pustite neka pase po Allahovoj zemlji i ne činite joj nikakvo zlo, da vas ne bi zadesila kazna bliska.'", TextEnglish = "And O my people, this is the she-camel of Allāh - [she is] to you a sign. So let her feed upon Allāh's earth and do not touch her with harm, or you will be taken by an impending punishment.'", TextGerman = "Und, o mein Volk, dies ist die Kamelstute Allahs, euch zum Zeichen. Laßt sie auf Allahs Erde weiden und fügt ihr nichts Böses zu, sonst überkommt euch eine baldige Strafe." });
                        surah11.VerseList.Add(new Verse { Number = 65, TextBosnian = "Ali, oni je zaklaše, pa on reče: 'Uživajte u zemlji svojoj još samo tri dana, to nije lažna prijetnja.'", TextEnglish = "But they hamstrung her, so he said, 'Enjoy yourselves in your homes for three days. That is a promise not to be denied [i.e., unfailing].'", TextGerman = "Aber sie schnitten ihr die Sehnen durch. Da sagte er: Genießt (euer Leben) in eurer Wohnstätte noch drei Tage lang. Dies ist ein Versprechen, das nicht erlogen ist." });
                        surah11.VerseList.Add(new Verse { Number = 66, TextBosnian = "I kad je došla odredba Naša, Mi smo, milošću Našom, Saliha i vjernike s njim spasili od sramote toga dana - Gospodar tvoj je, uistinu, Moćni i Silni,", TextEnglish = "So when Our command came, We saved Ṣāliḥ and those who believed with him, by mercy from Us, and [saved them] from the disgrace of that day.[561] Indeed, it is your Lord who is the Powerful, the Exalted in Might.", TextGerman = "Als nun Unser Befehl kam, erretteten Wir Salih und diejenigen, die mit ihm glaubten, durch Barmherzigkeit von Uns, auch vor der Schande jenes Tages. Gewiß, dein Herr ist ja der Starke und Allmächtige." });
                        surah11.VerseList.Add(new Verse { Number = 67, TextBosnian = "a one koji su činili zulum pogodio je strašan glas i oni su u svojim domovima osvanuli mrtvi, nepomični,", TextEnglish = "And the shriek[562] seized those who had wronged, and they became within their homes [corpses] fallen prone", TextGerman = "Und es ergriff diejenigen, die Unrecht taten, der Schrei, und so lagen sie morgens in ihren Wohnstätten auf den Brüsten da," });
                        surah11.VerseList.Add(new Verse { Number = 68, TextBosnian = "kao da na njoj nikad nisu ni živjeli. Narod Semud, doista, u Gospodara svoga nije vjerovao; daleko neka je Semud!", TextEnglish = "As if they had never prospered therein. Unquestionably, Thamūd denied their Lord; then, away with Thamūd.", TextGerman = "als hätten sie (überhaupt) nicht darin gewohnt. Sicherlich, die Tamud verleugneten ihren Herrn. Aber ja, weg mit Tamud!" });
                        surah11.VerseList.Add(new Verse { Number = 69, TextBosnian = "I Ibrahimu su izaslanici Naši došli da mu donesu radosnu vijest. 'Selam!', rekoše. 'Selam!', odgovori on, i ubrzo im donese pečeno tele.", TextEnglish = "And certainly did Our messengers [i.e., angels] come to Abraham with good tidings; they said, 'Peace.' He said, 'Peace,' and did not delay in bringing [them] a roasted calf.", TextGerman = "Unsere Gesandten kamen bereits zu Ibrahim mit der frohen Botschaft. Sie sagten: Frieden! Er sagte: Friede! Er verweilte nicht lange, da brachte er ein gebratenes Kalb herbei." });
                        surah11.VerseList.Add(new Verse { Number = 70, TextBosnian = "A kad vidje da ga se ruke njihove ne dotiču, on shvati da su neobični i osjeti strah od njih. Ne boj se!, rekoše oni, mi smo i Lutovu narodu poslani.", TextEnglish = "But when he saw their hands not reaching for it, he distrusted them and felt from them apprehension.[563] They said, 'Fear not. We have been sent to the people of Lot.'", TextGerman = "Als er aber sah, daß ihre Hände nicht danach hinlangten, fand er es von ihnen befremdlich, und er empfand Furcht vor ihnen. Sie sagten: Fürchte dich nicht! Wir sind zu dem Volk Luts gesandt." });
                        surah11.VerseList.Add(new Verse { Number = 71, TextBosnian = "A žena njegova stajaše tu, nasmija se, i Mi je obradovasmo Ishakom, a poslije Ishaka Jakubom.", TextEnglish = "And his wife was standing, and she smiled.[564] Then We gave her good tidings of Isaac and after Isaac, Jacob.", TextGerman = "Seine Frau stand dabei. Sie lachte, und da verkündeten Wir ihr Ishaq, und nach Ishaq Ya’qub." });
                        surah11.VerseList.Add(new Verse { Number = 72, TextBosnian = "Jadna ja!', reče, 'zar da rodim ovako stara, a i ovaj moj muž je star?! Ovo je zaista nešto neobično!'", TextEnglish = "She said, 'Woe to me![565] Shall I give birth while I am an old woman and this, my husband, is an old man? Indeed, this is an amazing thing!'", TextGerman = "Sie sagte: O wehe mir, soll ich noch gebären, wo ich doch alt bin, und dies ist doch mein Ehemann, schon ein Greis? Das ist fürwahr eine verwunderliche Sache." });
                        surah11.VerseList.Add(new Verse { Number = 73, TextBosnian = "Zar se čudiš Allahovoj odredbi?', rekoše oni, 'Allahova milost i blagoslovi Njegovi neka su na vama, obitelji vjerovjesnička. Zaista je On hvaljen i slavljen!'", TextEnglish = "They said, 'Are you amazed at the decree of Allāh? May the mercy of Allāh and His blessings be upon you, people of the house. Indeed, He is Praiseworthy and Honorable.'", TextGerman = "Sie sagten: Wunderst du dich über den Befehl Allahs? Die Barmherzigkeit Allahs und Seine Segnungen seien auf euch, Angehörige des Hauses! Er ist Lobenswürdig und Ruhmvoll." });
                        surah11.VerseList.Add(new Verse { Number = 74, TextBosnian = "I pošto Ibrahima minu strah i dođe mu radosna vijest, on poče raspravljati s Nama o narodu Lutovu;", TextEnglish = "And when the fright had left Abraham and the good tidings had reached him, he began to argue [i.e., plead] with Us[566] concerning the people of Lot.", TextGerman = "Als die Angst von Ibrahim gewichen und die frohe Botschaft zu ihm gekommen war, begann er mit Uns über das Volk Luts zu streiten." });
                        surah11.VerseList.Add(new Verse { Number = 75, TextBosnian = "Ibrahim je, zaista, obazriv bio, Allahu je mnogo dovu činio i Njemu se vraćao.", TextEnglish = "Indeed, Abraham was forbearing, grieving[567] and [frequently] returning [to Allāh].", TextGerman = "Ibrahim war wahrlich nachsichtig, weichherzig und reuig." });
                        surah11.VerseList.Add(new Verse { Number = 76, TextBosnian = "O Ibrahime, prođi se toga, naređenje od Gospodara tvoga je stiglo; njih će stići patnja neizbježna!'", TextEnglish = "[The angels said], 'O Abraham, give up this [plea]. Indeed, the command of your Lord has come, and indeed, there will reach them a punishment that cannot be repelled.'", TextGerman = "O Ibrahim, lasse davon ab! Der Befehl deines Herrn ist nun gekommen. Über sie kommt eine Strafe, die unwiderruflich ist." });
                        surah11.VerseList.Add(new Verse { Number = 77, TextBosnian = "I kada izaslanici Naši dođoše Lutu, on se zbog njih nađe u neprilici i to mu teško pade, pa reče: 'Ovo je mučan dan!'", TextEnglish = "And when Our messengers, [the angels], came to Lot, he was anguished for them and felt for them great discomfort[568] and said, 'This is a trying day.'", TextGerman = "Und als Unsere Gesandten zu Lut kamen, geriet er ihretwegen in eine böse Lage und war durch ihre Anwesenheit beklommen. Er sagte: Das ist ein drangsalvoller Tag." });
                        surah11.VerseList.Add(new Verse { Number = 78, TextBosnian = "I narod njegov pohrli njemu- a prije su nevaljaštine činili. 'O narode moj', reče on, 'eto vam za brak mojih kćeri, one su vam čistije! Bojte se Allaha i pred gostima mojim ne sramotite me! Zar među vama nema razumna čovjeka?'", TextEnglish = "And his people came hastening to him, and before [this] they had been doing evil deeds.[569] He said, 'O my people, these are my daughters;[570] they are purer for you. So fear Allāh and do not disgrace me concerning my guests. Is there not among you a man of reason?'", TextGerman = "Seine Leute kamen eilig zu ihm getrieben. Zuvor pflegten sie böse Taten zu begehen. Er sagte: O mein Volk, dies hier sind meine Töchter, sie sind reiner für euch. So fürchtet Allah und stürzt mich nicht um meiner Gäste willen in Schande! Gibt es denn unter euch keinen besonnenen Mann?" });
                        surah11.VerseList.Add(new Verse { Number = 79, TextBosnian = "Tebi je svakako poznato da nam kćeri tvoje nisu potrebne', rekoše oni, 'ti, doista, znaš šta mi hoćemo.'", TextEnglish = "They said, 'You have already known that we have not concerning your daughters [i.e., women] any claim [i.e., desire], and indeed, you know what we want.'", TextGerman = "Sie sagten: Du weißt sehr wohl, daß wir kein Recht auf deine Töchter haben, und du weißt fürwahr, was wir wollen." });
                        surah11.VerseList.Add(new Verse { Number = 80, TextBosnian = "Kad bih samo imao kakvu moć nad vama', reče on, 'ili da mogu kod zaštitnika snažnog utočište naći!'", TextEnglish = "He said, 'If only I had against you some power or could take refuge in a strong support.'", TextGerman = "Er sagte: Hätte ich doch Kraft (genug), um euch zu widerstehen, oder könnte ich nur bei einer starken Stütze Zuflucht finden!" });
                        surah11.VerseList.Add(new Verse { Number = 81, TextBosnian = "Odgovoriše: 'O Lut, mi smo izaslanici Gospodara tvoga, oni tebi ne mogu nauditi. Ti kreni sa porodicom svojom u gluho doba noći bez žene svoje, nju će zadesiti isto što i njih, i neka se niko od vas ne obazire! Rok im je praskozorje, a zar praskozorje nije blizu?'", TextEnglish = "They [the angels] said, 'O Lot, indeed we are messengers of your Lord; [therefore], they will never reach you. So set out with your family during a portion of the night[571] and let not any among you look back - except your wife; indeed, she will be struck by that which strikes them. Indeed, their appointment is [for] the morning. Is not the morning near?'", TextGerman = "Sie sagten: O Lut, wir sind die Gesandten deines Herrn. Sie werden nicht zu dir gelangen. So zieh mit deinen Angehörigen in einem Teil der Nacht fort, und niemand von euch soll sich umwenden - außer deiner Frau! Gewiß, es wird sie treffen, was jene trifft. Der ihnen versprochene Zeitpunkt ist der Tagesanbruch. Ist nicht der Tagesanbruch schon nahe?" });
                        surah11.VerseList.Add(new Verse { Number = 82, TextBosnian = "I kada dođe naredba Naša, učinismo da, ono što je bilo gore - bi dolje, i na njih kišu grumenja od pečena blata spustismo, koje je neprekidno sipalo,", TextEnglish = "So when Our command came, We made the highest part [of the city] its lowest and rained upon them stones of layered hard clay, [which were]", TextGerman = "Als nun Unser Befehl kam, kehrten Wir das Oberste von ihr zuunterst und ließen auf sie Steine aus vorbereitetem gebrannten Lehm regnen," });
                        surah11.VerseList.Add(new Verse { Number = 83, TextBosnian = "obilježeno od Gospodara tvoga, a ono od zulumćara nije daleko.", TextEnglish = "Marked from your Lord. And it [i.e., Allāh's punishment] is not from the wrongdoers [very] far.", TextGerman = "bei deinem Herrn gekennzeichnete (Steine). Und sie liegt den Ungerechten nicht fern." });
                        surah11.VerseList.Add(new Verse { Number = 84, TextBosnian = "I Medjenu- brata njihova Šu'ajba poslasmo. 'O narode moj', govorio je on, 'Allahu ibadet činite, vi drugog boga osim Njega nemate, i nemojte iz posude za mjerenje i sa vage ništa umanjivati. Vidim da zaista u obilju živite i bojim se za vas kazne na Dan koji će sve skoliti.", TextEnglish = "And to Madyan [We sent] their brother Shuʿayb. He said, 'O my people, worship Allāh; you have no deity other than Him. And do not decrease from the measure and the scale. Indeed, I see you in prosperity, but indeed, I fear for you the punishment of an all-encompassing Day.", TextGerman = "Und (Wir sandten) zu Madyan ihren Bruder Su’aib. Er sagte: O mein Volk, dient Allah! Keinen Gott habt ihr außer Ihm. Laßt an Maß und Gewicht nichts fehlen! Ich sehe, es geht euch gut. Aber ich fürchte für euch die Strafe eines umfassenden Tages." });
                        surah11.VerseList.Add(new Verse { Number = 85, TextBosnian = "O narode moj! Posudu za mjerenje i vagu pravedno ispunite, i ne zakidajte ljude ni u čemu što je njihovo, i ne činite zlo po zemlji nered praveći.", TextEnglish = "And O my people, give full measure and weight in justice and do not deprive the people of their due and do not commit abuse on the earth, spreading corruption.", TextGerman = "Und, o mein Volk, gebt volles Maß und Gewicht in Gerechtigkeit und schmälert den Menschen nicht ihre Sachen und richtet auf der Erde nicht unheilstiftend Verderben an!" });
                        surah11.VerseList.Add(new Verse { Number = 86, TextBosnian = "Ono što vam pretekne, a što vam je Allah dozvolio, bolje vam je, ako hoćete biti vjernici; a ja nisam vaš čuvar.'", TextEnglish = "What remains [lawful] from Allāh is best for you, if you would be believers. But I am not a guardian over you.'", TextGerman = "Das Bleibende (an Lohn) von Allah ist besser für euch, wenn ihr gläubig seid. Und ich bin nicht Hüter über euch." });
                        surah11.VerseList.Add(new Verse { Number = 87, TextBosnian = "O Šuajbe', govorili su oni, 'da li molitva tvoja traži od tebe da napustimo ono što su preci naši obožavali ili da ne postupamo sa imanjima našim onako kako nam je volja? E baš si 'obazriv' i 'razuman'!'", TextEnglish = "They said, 'O Shuʿayb, does your prayer [i.e., religion] command you that we should leave what our fathers worship or not do with our wealth what we please? Indeed, you are the forbearing, the discerning!'[572]", TextGerman = "Sie sagten: O Su’aib, befiehlt dir denn dein Gebet, daß wir das verlassen, dem unsere Väter dienen, oder (davon absehen), mit unserem Besitz zu tun, was wir wollen? Du bist fürwahr der Nachsichtige und Besonnene!" });
                        surah11.VerseList.Add(new Verse { Number = 88, TextBosnian = "O narode moj', govorio je on, 'šta mislite, ako sam pri jasnom dokazu od Gospodara moga i On mi dao lijepu opskrbu?! Ja ne želim činiti ono što vama zabranjujem; jedino želim učiniti dobro koliko mogu, a uspjeh moj zavisi samo od Allaha; u Njega se uzdam i Njemu se obraćam.", TextEnglish = "He said, 'O my people, have you considered: if I am upon clear evidence from my Lord and He has provided me with a good provision from Him...?[573] And I do not intend to differ from you in that which I have forbidden you; I only intend reform as much as I am able. And my success is not but through Allāh. Upon Him I have relied, and to Him I return.[574]", TextGerman = "Er sagte: O mein Volk, was meint ihr, wenn ich mich auf einen klaren Beweis von meinem Herrn stütze und Er mir eine schöne Versorgung gewährt hat? Und ich will mich (dann in meinem Vorhaben) von euch nicht unterscheiden, indem ich das tue, was ich euch verbiete. Ich will nur Besserung, soweit ich (sie erreichen) kann. Das Gelingen wird mir nur durch Allah (allein) beschieden. Auf Ihn verlasse ich mich, und Ihm wende ich mich reuig zu." });
                        surah11.VerseList.Add(new Verse { Number = 89, TextBosnian = "O narode moj! Neka vas neslaganje sa mnom nikako ne dovede do toga da vas zadesi ono što je zadesilo Nuhov narod, ili Hudov narod, ili Salihov narod. A i Lutov narod nije od vas daleko.", TextEnglish = "And O my people, let not [your] dissension from me cause you to be struck by that similar to what struck the people of Noah or the people of Hūd or the people of Ṣāliḥ. And the people of Lot are not from you far away.", TextGerman = "Und, o mein Volk, meine Feindseligkeit soll euch ja nicht dazu bringen, daß euch das Gleiche trifft, was das Volk Nuhs oder das Volk Huds oder das Volk Salihs traf. Und das Volk Luts liegt euch nicht fern." });
                        surah11.VerseList.Add(new Verse { Number = 90, TextBosnian = "I tražite oprost od Gospodara svoga, i onda Mu se pokajte! Gospodar je moj, uistinu, milostiv i Onaj Koji voli.'", TextEnglish = "And ask forgiveness of your Lord and then repent to Him. Indeed, my Lord is Merciful and Affectionate.'", TextGerman = "Und bittet euren Herrn um Vergebung und hierauf bereut vor Ihm! Gewiß, mein Herr ist Barmherzig und Liebevoll." });
                        surah11.VerseList.Add(new Verse { Number = 91, TextBosnian = "O Šuajbe', rekoše oni, 'Mi ne shvatamo mnogo toga što ti govoriš, a vidimo da si ti među nama jadan; da nije rodbine tvoje, mi bismo te kamenovali, ti nad nama nemaš moć i ne uživaš nikakav ugled.'", TextEnglish = "They said, 'O Shuʿayb, we do not understand much of what you say, and indeed, we consider you among us as weak. And if not for your family, we would have stoned you [to death]; and you are not to us one respected.'", TextGerman = "Sie sagten: O Su’aib, wir verstehen nicht viel von dem, was du sagst. Und wir sehen wahrlich, daß du unter uns nicht wehrhaft bist. Wenn deine Sippschaft nicht wäre, hätten wir dich fürwahr gesteinigt. Du hast ja bei uns kein Ansehen." });
                        surah11.VerseList.Add(new Verse { Number = 92, TextBosnian = "O narode moj', reče on, 'zar rodbina moja kod vas ima veći ugled od Allaha, a Njega ste iza sebe odbacili?! Uistinu Gospodar moj ono što radite znanjem obuhvata.", TextEnglish = "He said, 'O my people, is my family more respected for power by you than Allāh? But you put Him behind your backs [in neglect]. Indeed, my Lord is encompassing of what you do.", TextGerman = "Er sagte: O mein Volk, hat meine Sippschaft bei euch mehr Ansehen als Allah, und habt ihr Ihn hinter eurem Rücken gelassen? Gewiß, mein Herr umfaßt, was ihr tut." });
                        surah11.VerseList.Add(new Verse { Number = 93, TextBosnian = "O narode moj, činite sve što možete, a činit ću i ja. Vi ćete sigurno saznati koga će kazna stići koja će ga osramotiti i ko je lažac. Pa, čekajte, i ja ću s vama čekati!'", TextEnglish = "And O my people, work according to your position; indeed, I am working. You are going to know to whom will come a punishment that will disgrace him and who is a liar. So watch; indeed, I am with you a watcher, [awaiting the outcome].'", TextGerman = "Und, o mein Volk, handelt nach eurer Stellung! Ich werde (ebenfalls so) handeln. Ihr werdet (noch) erfahren, über wen eine Strafe kommen wird, die ihn in Schande stürzt, und wer ein Lügner ist. Und wartet (es) ab! Ich bin mit euch ein Beobachter." });
                        surah11.VerseList.Add(new Verse { Number = 94, TextBosnian = "I kada je nastupila naredba Naša, Mi smo, iz milosti Naše, Šuajba i one koji su uz njega vjerovali spasili, a one koji su zulum činili pogodio je užasan glas i oni su u zemlji svojoj mrtvi, nepomični osvanuli,", TextEnglish = "And when Our command came, We saved Shuʿayb and those who believed with him, by mercy from Us. And the shriek seized those who had wronged, and they became within their homes [corpses] fallen prone", TextGerman = "Als nun Unser Befehl kam, erretteten Wir Su’aib und diejenigen, die mit ihm glaubten, durch Barmherzigkeit von Uns. Da ergriff diejenigen, die Unrecht taten, der Schrei, und so lagen sie morgens in ihren Wohnstätten auf den Brüsten da," });
                        surah11.VerseList.Add(new Verse { Number = 95, TextBosnian = "kao da na njoj nikad nisu ni postojali. Daleko bio Medjen, kao i Semud!", TextEnglish = "As if they had never prospered therein. Then, away with Madyan as Thamūd was taken away.", TextGerman = "als hätten sie (überhaupt) nicht darin gewohnt. Aber ja, weg mit Madyan, wie auch die Tamud entfernt wurden!" });
                        surah11.VerseList.Add(new Verse { Number = 96, TextBosnian = "I Musaa smo poslali sa znakovima Našim i dokazom jasnim,", TextEnglish = "And We did certainly send Moses with Our signs and a clear authority[575]", TextGerman = "Und Wir sandten bereits Musa mit Unseren Zeichen und mit einer deutlichen Ermächtigung" });
                        surah11.VerseList.Add(new Verse { Number = 97, TextBosnian = "faraonu i glavešinama njegovim, ali su oni slijedili faraonovu praksu, a faraonova praksa bila je zabluda.", TextEnglish = "To Pharaoh and his establishment, but they followed the command of Pharaoh, and the command of Pharaoh was not [at all] discerning.", TextGerman = "zu Fir’aun und seiner führenden Schar. Diese folgten dem Befehl Fir’auns; der Befehl Fir’auns aber war nicht rechtweisend." });
                        surah11.VerseList.Add(new Verse { Number = 98, TextBosnian = "Na Kijametskom danu on će svoj narod predvoditi i u Vatru ga uvesti, a užasno je mjesto u koje će doveden biti!", TextEnglish = "He will precede his people on the Day of Resurrection and lead them into the Fire; and wretched is the place to which they are led.", TextGerman = "Er wird seinem Volk am Tag der Auferstehung vorangehen. Er führt sie wie zur Tränke ins (Höllen)feuer hinab - eine schlimme Tränke, zu der sie hinabgeführt werden!" });
                        surah11.VerseList.Add(new Verse { Number = 99, TextBosnian = "Na ovom svijetu ih je pratilo prokletstvo a pratit će ih i na Kijametskom danu; strašan će biti dar kojim će darivani biti.", TextEnglish = "And they were followed in this [world] with a curse and on the Day of Resurrection. And wretched is the gift[576] which is given.", TextGerman = "Und ein Fluch folgte ihnen im Diesseits nach und (wird ihnen) am Tag der Auferstehung (nachfolgen) - eine schlimme Beigabe, die (ihnen) gegeben wird!" });
                        surah11.VerseList.Add(new Verse { Number = 100, TextBosnian = "To su neke vijesti koje ti o naseljima kazujemo; neka od njih još postoje, a neka su sa zemljom sravnjena.", TextEnglish = "That is from the news of the cities, which We relate to you; of them, some are [still] standing and some are [as] a harvest [mowed down].[577]", TextGerman = "Dies gehört zu den Nachrichten von den Städten; Wir erzählen es dir. Einige von ihnen stehen noch, andere sind abgemäht." });
                        surah11.VerseList.Add(new Verse { Number = 101, TextBosnian = "Mi im nismo zulum učinili. Oni su sami sebi zulum učinili. Ništa im nisu pomogla božanstva njihova koja su mimo Allaha molili, kad bi naredba Gospodara tvoga nastupila, samo bi im propast njihovu povećala.", TextEnglish = "And We did not wrong them, but they wronged themselves. And they were not availed at all by their gods which they invoked other than Allāh when there came the command of your Lord. And they did not increase them in other than ruin.", TextGerman = "Nicht Wir haben ihnen Unrecht getan, sondern sie selbst haben sich Unrecht zugefügt. Ihre Götter, die sie anstatt Allahs anrufen, haben ihnen nichts genützt, als der Befehl deines Herrn kam, und sie richteten sie nur noch mehr zugrunde." });
                        surah11.VerseList.Add(new Verse { Number = 102, TextBosnian = "Eto, tako Gospodar tvoj kažnjava kad kažnjava naselja koja zulum čine. Njegova kazna zaista je bolna i žestoka.", TextEnglish = "And thus is the seizure of your Lord when He seizes the cities while they are committing wrong. Indeed, His seizure is painful and severe.", TextGerman = "So ist der Griff deines Herrn, wenn Er die Städte ergreift, während sie Unrecht tun. Gewiß, Sein Griff ist schmerzhaft und hart." });
                        surah11.VerseList.Add(new Verse { Number = 103, TextBosnian = "U tome je, zaista, pouka za one koji se plaše patnje na ahiretu; to je Dan kada će svi ljudi biti sabrani i to je Dan kad će svi biti prisutni,", TextEnglish = "Indeed in that is a sign for those who fear the punishment of the Hereafter. That is a Day for which the people will be collected, and that is a Day [which will be] witnessed.", TextGerman = "Darin ist wahrlich ein Zeichen für jemanden, der die Strafe des Jenseits fürchtet. Das ist ein Tag, zu dem die Menschen versammelt werden, und das ist ein Tag, an dem sie (alle) anwesend sein werden." });
                        surah11.VerseList.Add(new Verse { Number = 104, TextBosnian = "a Mi ga odgađamo samo za neko vrijeme.", TextEnglish = "And We do not delay it except for a limited term.", TextGerman = "Und Wir stellen ihn nur auf eine bestimmte Frist zurück." });
                        surah11.VerseList.Add(new Verse { Number = 105, TextBosnian = "A onda kad dođe, bez dopuštenja Njegova niko ni riječ neće izustiti, a među njima bit će nesretnih i sretnih.", TextEnglish = "The Day it comes no soul will speak except by His permission. And among them will be the wretched and the prosperous.", TextGerman = "An dem Tag, da er eintrifft, wird keine Seele sprechen, außer mit Seiner Erlaubnis. Dann werden einige von ihnen unglücklich und andere glückselig sein." });
                        surah11.VerseList.Add(new Verse { Number = 106, TextBosnian = "I nesretni će u Vatru, u njoj će teško izdisati i udisati;", TextEnglish = "As for those who were [destined to be] wretched, they will be in the Fire. For them therein is [violent] exhaling and inhaling.[578]", TextGerman = "Was nun diejenigen angeht, die unglücklich sind, so werden sie dann im (Höllen)feuer sein; darin werden sie seufzen und schluchzen," });
                        surah11.VerseList.Add(new Verse { Number = 107, TextBosnian = "U njoj će ostati dok je nebesa i Zemlje, osim ako drukčije Gospodar tvoj ne odredi. Gospodar tvoj, zaista, radi ono što želi.", TextEnglish = "[They will be] abiding therein as long as the heavens and the earth endure, except what your Lord should will. Indeed, your Lord is an effecter of what He intends.", TextGerman = "ewig darin zu bleiben, solange die Himmel und die Erde währen, außer was dein Herr will. Dein Herr tut immer, was Er will." });
                        surah11.VerseList.Add(new Verse { Number = 108, TextBosnian = "A sretni će u Džennet; u njemu će boraviti, dok je nebesa i Zemlje, osim ako drukčije Gospodar tvoj ne odredi; bit će to dar koji će neprekidno trajati.", TextEnglish = "And as for those who were [destined to be] prosperous, they will be in Paradise, abiding therein as long as the heavens and the earth endure, except what your Lord should will - a bestowal uninterrupted.", TextGerman = "Was aber diejenigen angeht, die glückselig sind, so werden sie im (Paradies)garten sein, ewig darin zu bleiben, solange die Himmel und die Erde währen, außer was dein Herr will, als eine unverkürzte Gabe." });
                        surah11.VerseList.Add(new Verse { Number = 109, TextBosnian = "Zato ne sumnjaj da je lažno ono što oni obožavaju; oni obožavaju upravo ono što su, još prije, obožavali preci njihovi. Mi ćemo im, doista, ono što su zaslužili - bez odbitka dati!", TextEnglish = "So do not be in doubt, [O Muḥammad], as to what these [polytheists] are worshipping. They worship not except as their fathers worshipped before. And indeed, We will give them their share undiminished.", TextGerman = "So sei nicht im Zweifel über das, dem diese dienen. Sie dienen nur, wie ihre Väter zuvor gedient haben. Und Wir werden ihnen ihren Anteil fürwahr unverringert zukommen lassen." });
                        surah11.VerseList.Add(new Verse { Number = 110, TextBosnian = "I Musau smo Knjigu dali, pa su se o njoj u mišljenju podvojili. I da nije riječi Gospodara tvoga ranije izrečene, bilo bi s njima svršeno, jer oni u nju sumnjaju mnogo.", TextEnglish = "And We had certainly given Moses the Scripture, but it came under disagreement. And if not for a word[579] that preceded from your Lord, it would have been judged between them. And indeed they are, concerning it [i.e., the Qur’ān], in disquieting doubt.", TextGerman = "Und Wir gaben bereits Musa die Schrift, doch wurde man darüber uneinig. Und wenn es nicht ein früher ergangenes Wort von deinem Herrn gegeben hätte, so wäre zwischen ihnen wahrlich entschieden worden. Und sie sind darüber fürwahr in starkem Zweifel." });
                        surah11.VerseList.Add(new Verse { Number = 111, TextBosnian = "I svima njima će Gospodar tvoj prema djelima platiti, On u potpunosti zna ono što su radili.", TextEnglish = "And indeed, each [of the believers and disbelievers] - your Lord will fully compensate them for their deeds. Indeed, He is Aware of what they do.", TextGerman = "Und allen wird dein Herr ihre Werke in vollem Maß ganz sicher erstatten. Gewiß, Er ist dessen, was sie tun, Kundig." });
                        surah11.VerseList.Add(new Verse { Number = 112, TextBosnian = "Budi ustrajan na Pravom putu, kao što ti je naređeno, i neka tako postupe i vjernici koji su uz tebe, i obijesni ne budite, On dobro vidi ono što radite.", TextEnglish = "So remain on a right course as you have been commanded, [you] and those who have turned back with you [to Allāh], and do not transgress. Indeed, He is Seeing of what you do.", TextGerman = "So verhalte dich recht, wie dir befohlen wurde, (du) und diejenigen, die mit dir bereuen, und lehnt euch nicht auf. Gewiß, was ihr tut, sieht Er wohl." });
                        surah11.VerseList.Add(new Verse { Number = 113, TextBosnian = "I ne naginjite onima koji zulum čine, pa da vas Vatra prži, da nemate drugih zaštitnika osim Allaha, i da zatim nemate pomoći!", TextEnglish = "And do not incline toward those who do wrong, lest you be touched by the Fire, and you would not have other than Allāh any protectors; then you would not be helped.", TextGerman = "Und sucht nicht eine Stütze bei denen, die Unrecht tun, sonst berührt euch das (Höllen)feuer; ihr habt außer Allah keine Schutzherren. Dann wird euch keine Hilfe zuteil werden." });
                        surah11.VerseList.Add(new Verse { Number = 114, TextBosnian = "I obavljaj namaz početkom i krajem dana, i u prvim časovima noći! Dobra djela zaista poništavaju hrđava. To je opomena za one koji se opomene prisjećaju.", TextEnglish = "And establish prayer at the two ends of the day and at the approach of the night. Indeed, good deeds do away with misdeeds. That is a reminder for those who remember.", TextGerman = "Und verrichte das Gebet an beiden Enden des Tages und in Stunden der Nacht. Die guten Taten lassen die bösen Taten vergehen. Das ist eine Ermahnung für diejenigen, die (Allahs) gedenken." });
                        surah11.VerseList.Add(new Verse { Number = 115, TextBosnian = "I strpljiv budi! Allah, doista, neće uskratiti nagradu onima koji dobra djela čine.", TextEnglish = "And be patient, for indeed, Allāh does not allow to be lost the reward of those who do good.", TextGerman = "Und sei standhaft; denn Allah läßt den Lohn der Gutes Tuenden nicht verlorengehen." });
                        surah11.VerseList.Add(new Verse { Number = 116, TextBosnian = "A da je barem među narodima prije vas bilo čestitih koji su branili da se na Zemlji nered čini, osim malo njih koje smo Mi spasili! A oni koji su zulum činili odavali su se onome u čemu su uživali, a prestupnici su bili.", TextEnglish = "So why were there not[580] among the generations before you those of enduring discrimination forbidding corruption on earth - except a few of those We saved from among them? But those who wronged pursued what luxury they were given therein, and they were criminals.", TextGerman = "Wenn es unter den Geschlechtern vor euch doch nur Leute mit einem Rest (von Tugend) gegeben hätte, die verbieten, auf der Erde Unheil zu stiften - bis auf wenige unter denen, die Wir von ihnen retteten! Diejenigen, die Unrecht taten, folgten dem ihnen verliehenen üppigen Leben und wurden Übeltäter." });
                        surah11.VerseList.Add(new Verse { Number = 117, TextBosnian = "Gospodar tvoj ne bi nikada uništavao naselja zbog zuluma ako su stanovnici njihovi dobri.", TextEnglish = "And your Lord would not have destroyed the cities unjustly while their people were reformers.", TextGerman = "Und dein Herr hätte die Städte nimmer zu Unrecht vernichtet, während ihre Bewohner Heilstifter waren." });
                        surah11.VerseList.Add(new Verse { Number = 118, TextBosnian = "A da je Gospodar tvoj htio, sve bi ljude sljedbenicima jedne vjere učinio. Međutim, oni će se uvijek u vjerovanju razilaziti,", TextEnglish = "And if your Lord had willed, He could have made mankind one community; but they will not cease to differ,", TextGerman = "Und wenn dein Herr wollte, hätte Er die Menschen wahrlich zu einer einzigen Gemeinschaft gemacht. Aber sie bleiben doch uneinig," });
                        surah11.VerseList.Add(new Verse { Number = 119, TextBosnian = "osim onih kojima se Gospodar tvoj smiluje. A zato ih je i stvorio. I ispunit će se riječ Gospodara tvoga: 'Napunit ću, zaista, Džehennem džinima i ljudima - zajedno!'", TextEnglish = "Except whom your Lord has given mercy, and for that He created them. But the word of your Lord is to be fulfilled that, 'I will surely fill Hell with jinn and men all together.'", TextGerman = "außer denen, derer Sich dein Herr erbarmt hat. Dazu hat Er sie erschaffen. Und so erfüllt sich das Wort deines Herrn: Ich werde die Hölle ganz gewiß mit den Ginn und den Menschen allesamt füllen." });
                        surah11.VerseList.Add(new Verse { Number = 120, TextBosnian = "A sve ove vijesti koje ti o pojedinim događajima o poslanicima kazujemo zato su da njima srce tvoje učvrstimo. I u ovima došla ti je prava istina, i pouka, i vjernicima opomena.", TextEnglish = "And each [story] We relate to you from the news of the messengers is that by which We make firm your heart. And there has come to you, in this, the truth and an instruction and a reminder for the believers.", TextGerman = "Alles berichten Wir dir von den Nachrichten über die Gesandten, womit Wir dein Herz festigen. Darin ist dir die Wahrheit zugekommen, und eine Ermahnung und Erinnerung für die Gläubigen." });
                        surah11.VerseList.Add(new Verse { Number = 121, TextBosnian = "I reci onima koji neće da vjeruju: 'Radite što god možete, i mi ćemo raditi;", TextEnglish = "And say to those who do not believe, 'Work according to your position; indeed, we are working.", TextGerman = "Und sag zu denen, die nicht glauben: Handelt nach eurer Stellung, wir werden (ebenfalls so) handeln." });
                        surah11.VerseList.Add(new Verse { Number = 122, TextBosnian = "i čekajte, i mi ćemo, sigurno, čekati!'", TextEnglish = "And wait; indeed, we are waiting.'", TextGerman = "Und wartet ab, wir warten ebenfalls ab." });
                        surah11.VerseList.Add(new Verse { Number = 123, TextBosnian = "Allah zna tajne nebesa i Zemlje i Njemu se sve vraća, zato samo Njemu ibadet čini i samo se u Njega uzdaj! A Gospodar tvoj nije nemaran spram onoga što radite.", TextEnglish = "And to Allāh belong the unseen [aspects] of the heavens and the earth and to Him will be returned the matter, all of it, so worship Him and rely upon Him. And your Lord is not unaware of that which you do.", TextGerman = "Und Allahs ist das Verborgene der Himmel und der Erde, und zu Ihm wird die ganze Angelegenheit zurückgebracht. So diene Ihm und verlasse dich auf Ihn. Und dein Herr ist nicht unachtsam dessen, was ihr tut." });
                        result = surah11;
                        break;
                    #endregion

                    // 
                    #region 12 Yusuf
                    case 12:
                        Surah surah12 = new Surah(); surah12.Name = Constants.Surah12Name; surah12.Number = 12; surah12.VerseList = new List<Verse>();
                        surah12.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah12.VerseList.Add(new Verse { Number = 1, TextBosnian = "Elif-lam-ra, Ovo su ajeti Knjige jasne!", TextEnglish = "Alif, Lām, Rā.[582] These are the verses of the clear Book.", TextGerman = "Alif-Lam-Ra. Dies sind die Zeichen des deutlichen Buches." });
                        surah12.VerseList.Add(new Verse { Number = 2, TextBosnian = "Objavljujemo je kao Kur'an na arapskom jeziku, da biste razumjeli.", TextEnglish = "Indeed, We have sent it down as an Arabic Qur’ān[583] that you might understand.", TextGerman = "Wir haben es als einen arabischen Qur’an hinabgesandt, auf daß ihr begreifen möget." });
                        surah12.VerseList.Add(new Verse { Number = 3, TextBosnian = "Mi tebi o najljepšim događajima kazujemo tako što ti ovaj Kur'an objavljujemo, iako si prije njega, jedan od neukih bio.", TextEnglish = "We relate to you, [O Muḥammad], the best of stories in what We have revealed to you of this Qur’ān although you were, before it, among the unaware.", TextGerman = "Wir berichten dir die schönsten Geschichten dadurch, daß Wir dir diesen Qur’an (als Offenbarung) eingegeben haben, obgleich du zuvor wahrlich zu den Unachtsamen gehörtest." });
                        surah12.VerseList.Add(new Verse { Number = 4, TextBosnian = "Kad Jusuf reče svome ocu: 'O oče moj, sanjao sam jedanaest zvijezda, i Sunce i Mjesec, i u snu sam ih vidio kako mi se pokloniše.'", TextEnglish = "[Of these stories mention] when Joseph said to his father,[584] 'O my father, indeed I have seen [in a dream] eleven stars and the sun and the moon; I saw them prostrating to me.'", TextGerman = "Als Yusuf zu seinem Vater sagte: O mein Vater, ich sah elf Sterne und die Sonne und den Mond, ich sah sie sich vor mir niederwerfen." });
                        surah12.VerseList.Add(new Verse { Number = 5, TextBosnian = "On reče: 'O sinko moj, ne kazuj svoj san braći svojoj, da ti ne učine kakvu spletku; šejtan je, doista, čovjeku neprijatelj otvoreni.'", TextEnglish = "He said, 'O my son, do not relate your vision to your brothers or they will contrive against you a plan. Indeed Satan, to man, is a manifest enemy.", TextGerman = "Er sagt: O mein lieber Sohn, erzähle dein (Traum)gesicht nicht deinen Brüdern, sonst werden sie eine List gegen dich schmieden. Gewiß, der Satan ist dem Menschen ein deutlicher Feind." });
                        surah12.VerseList.Add(new Verse { Number = 6, TextBosnian = "I, eto tako, Gospodar tvoj će tebe odabrati, i tumačenju snova te naučiti, i blagodat Svoju tebi i Jakubovoj porodici upotpuniti, kao što je prije upotpunio precima tvojim, Ibrahimu i Ishaku, Gospodar tvoj je, zaista, Onaj Koji sve zna i mudar je.", TextEnglish = "And thus will your Lord choose you and teach you the interpretation of narratives [i.e., events or dreams] and complete His favor upon you and upon the family of Jacob, as He completed it upon your fathers before, Abraham and Isaac. Indeed, your Lord is Knowing and Wise.'", TextGerman = "Und so wird dein Herr dich erwählen und dich etwas von der Deutung der Geschichten lehren und Seine Gunst an dir und an der Sippe Ya’qubs vollenden, wie Er sie zuvor an deinen beiden Vätern Ibrahim und lshaq vollendet hat. Gewiß, dein Herr ist Allwissend und Allweise." });
                        surah12.VerseList.Add(new Verse { Number = 7, TextBosnian = "U Jusufu i braći njegovoj nalaze se pouke za sve koji se raspituju.", TextEnglish = "Certainly were there in Joseph and his brothers signs for those who ask, [such as]", TextGerman = "Wahrlich, in Yusuf und seinen Brüdern sind Zeichen für diejenigen, die (nach der Wahrheit) fragen." });
                        surah12.VerseList.Add(new Verse { Number = 8, TextBosnian = "Kada oni rekoše: 'Jusuf i brat njegov draži su našem ocu od nas, a mi smo grupa. Naš otac je, zaista, u zabludi očitaj.", TextEnglish = "When they said, 'Joseph and his brother[585] are more beloved to our father than we, while we are a clan. Indeed, our father is in clear error.", TextGerman = "Als sie sagten: Wahrlich, Yusuf und sein Bruder sind unserem Vater lieber als wir, obwohl wir eine (ansehnliche) Schar sind. Unser Vater befindet sich fürwahr in deutlichem Irrtum." });
                        surah12.VerseList.Add(new Verse { Number = 9, TextBosnian = "Ubijte Jusufa ili ga u nekoj zabiti ostavite, otac vaš će se vama okrenuti, i poslije toga ćete dobri ljudi biti.'", TextEnglish = "Kill Joseph or cast him out to [another] land; the countenance [i.e., attention] of your father will [then] be only for you, and you will be after that a righteous people.'[586]", TextGerman = "Tötet Yusuf oder werft ihn ins Land hinaus, so wird das Gesicht eures Vaters sich nur noch euch zuwenden, und danach werdet ihr rechtschaffene Leute sein." });
                        surah12.VerseList.Add(new Verse { Number = 10, TextBosnian = "Jedan od njih reče: 'Ako baš hoćete nešto učiniti, onda Jusufa ne ubijte, već ga na dno nekog bunara bacite, uzet će ga kakav karavan.'", TextEnglish = "Said a speaker among them, 'Do not kill Joseph but throw him into the bottom of the well; some travelers will pick him up - if you would do [something].'", TextGerman = "Einer von ihnen sagte: Tötet Yusuf nicht, sondern werft ihn in die verborgene Tiefe des Brunnenlochs, so werden ihn schon einige der Reisenden auflesen, wenn ihr doch etwas tun wollt." });
                        surah12.VerseList.Add(new Verse { Number = 11, TextBosnian = "O oče naš', rekoše oni, 'zašto nam ne povjeriš Jusufa? Mi mu, zaista, želimo dobro.", TextEnglish = "They said, 'O our father, why do you not entrust us with Joseph while indeed, we are to him sincere counselors?", TextGerman = "Sie sagten: O unser Vater, warum vertraust du uns Yusuf nicht an? Wir werden ihm wahrlich aufrichtig zugetan sein." });
                        surah12.VerseList.Add(new Verse { Number = 12, TextBosnian = "Pošalji ga sutra s nama da se zabavi i razonodi, mi ćemo ga, sigurno, čuvati.'", TextEnglish = "Send him with us tomorrow that he may eat well and play. And indeed, we will be his guardians.'", TextGerman = "Sende ihn morgen mit uns, daß er Spaß und Spiel finde; wir werden ihn fürwahr behüten." });
                        surah12.VerseList.Add(new Verse { Number = 13, TextBosnian = "On odgovori: 'Bit će mi, doista, žao ako ga odvedete, a plašim se da ga vuk ne pojede kad vi na njega ne budete pazili.'", TextEnglish = "[Jacob] said, 'Indeed, it saddens me that you should take him, and I fear that a wolf would eat him while you are of him unaware.'", TextGerman = "Er sagte: Es macht mich fürwahr traurig, daß ihr ihn mitnehmt. Und ich fürchte, daß ihn der Wolf frißt, während ihr seiner unachtsam seid." });
                        surah12.VerseList.Add(new Verse { Number = 14, TextBosnian = "Ako bi ga vuk pojeo, a mi smo grupa!', rekoše oni, 'bili bismo zaista gubitnici.'", TextEnglish = "They said, 'If a wolf should eat him while we are a [strong] clan, indeed, we would then be losers.'", TextGerman = "Sie sagten: Wenn ihn der Wolf fressen sollte, wo wir doch eine (ansehnliche) Schar sind, dann wären wir fürwahr Verlierer." });
                        surah12.VerseList.Add(new Verse { Number = 15, TextBosnian = "I kada ga odvedoše i odlučiše da ga bace na dno bunara, Mi mu objavismo: 'Ti ćeš ih o ovom njihovom postupku sigurno obavijestiti, a oni tebe neće ni prepoznati.'", TextEnglish = "So when they took him [out] and agreed to put him into the bottom of the well...[587] But We inspired to him, 'You will surely inform them [someday] about this affair of theirs while they do not perceive [your identity].'", TextGerman = "Als sie ihn mitnahmen und sich geeinigt hatten, ihn in die verborgene Tiefe des Brunnenlochs zu stecken, gaben Wir ihm ein: Du wirst ihnen ganz gewiß noch diese ihre Tat kundtun, ohne daß sie merken." });
                        surah12.VerseList.Add(new Verse { Number = 16, TextBosnian = "I uvečer dođoše svom ocu plačući.", TextEnglish = "And they came to their father at night, weeping.", TextGerman = "Und am Abend kamen sie weinend zu ihrem Vater." });
                        surah12.VerseList.Add(new Verse { Number = 17, TextBosnian = "O oče naš', rekoše, 'bili smo otišli da se nadmećemo, a Jusufa smo ostavili kod naših stvari, pa ga je vuk pojeo. A ti nam nećeš vjerovati iako istinu govorimo.'", TextEnglish = "They said, 'O our father, indeed we went racing each other and left Joseph with our possessions, and a wolf ate him. But you would not believe us, even if we were truthful.'", TextGerman = "Sie sagten: O unser Vater, wir gingen, um einen Wettlauf zu machen, und ließen Yusuf bei unseren Sachen zurück. Da fraß ihn der Wolf. Aber du glaubst uns wohl nicht, auch wenn wir die Wahrheit sagen." });
                        surah12.VerseList.Add(new Verse { Number = 18, TextBosnian = "I košulju njegovu lažnom krvlju okrvaviše. 'U vašim dušama je ponikla zla misao', reče on, 'a strpljivost je lijepa; od Allaha tražim pomoć u onome što vi govorite.'", TextEnglish = "And they brought upon his shirt false blood.[588] [Jacob] said, 'Rather, your souls have enticed you to something, so patience is most fitting. And Allāh is the one sought for help against that which you describe.'", TextGerman = "Sie brachten falsches Blut auf seinem Hemd. Er sagte: Nein! Vielmehr habt ihr selbst euch etwas eingeredet. (So gilt es,) schöne Geduld (zu üben). Allah ist Derjenige, bei Dem Hilfe zu suchen ist gegen das, was ihr beschreibt." });
                        surah12.VerseList.Add(new Verse { Number = 19, TextBosnian = "I dođe jedan karavan, te poslaše vodonošu svoga i on spusti vedro svoje. 'Gle sreće!', viknu on, 'evo jednog dječaka!' I oni su ga kao trgovačku robu sakrili. a Allah dobro zna šta su činili.", TextEnglish = "And there came a company of travelers; then they sent their water drawer, and he let down his bucket. He said, 'Good news! Here is a boy.' And they concealed him, [taking him] as merchandise;[589] and Allāh was Knowing of what they did.", TextGerman = "Reisende kamen vorbei. Sie sandten ihren Wasserschöpfer, und er ließ seinen Eimer hinunter. Er sagte: O frohe Botschaft! Da ist ein Junge. Sie verbargen ihn als Ware. Und Allah wußte wohl, was sie taten." });
                        surah12.VerseList.Add(new Verse { Number = 20, TextBosnian = "I prodadoše ga za malu nadoknadu, za nekoliko srebrenjaka; u vezi s njim bili su ravnodušni.", TextEnglish = "And they sold him for a reduced price - a few dirhams - and they were, concerning him, of those content with little.", TextGerman = "Und sie verkauften ihn für einen zu niedrigen Preis, einige gezählte Dirhams. Und sie übten Verzicht in Bezug auf ihn." });
                        surah12.VerseList.Add(new Verse { Number = 21, TextBosnian = "I onda onaj iz Misira, koji ga je kupio, reče ženi svojoj: 'Učini mu boravak prijatnim! Može nam koristan biti, a možemo ga i posiniti!' I eto tako Mi Jusufu dadosmo lijepo mjesto na Zemlji i naučismo ga tumačenju snova - a Allah čini šta hoće, ali većina ljudi ne zna.", TextEnglish = "And the one from Egypt[590] who bought him said to his wife, 'Make his residence comfortable. Perhaps he will benefit us, or we will adopt him as a son.' And thus, We established Joseph in the land that We might teach him the interpretation of events [i.e., dreams]. And Allāh is predominant over His affair, but most of the people do not know.", TextGerman = "Und derjenige aus Ägypten, der ihn gekauft hatte, sagte zu seiner Frau: Bereite ihm einen gastfreundlichen Aufenthalt. Vielleicht wird er uns nützlich sein, oder nehmen wir ihn als Kind an. So verliehen Wir Yusuf eine feste Stellung im Land. Und Wir wollten ihn etwas von der Deutung der Geschichten lehren. Und Allah ist in Seiner Angelegenheit überlegen. Aber die meisten Menschen wissen nicht." });
                        surah12.VerseList.Add(new Verse { Number = 22, TextBosnian = "I kad on stasa, Mi mu podarismo mudrost i znanje; tako Mi nagrađujemo dobročinitelje.", TextEnglish = "And when he [i.e., Joseph] reached maturity, We gave him judgement and knowledge. And thus We reward the doers of good.", TextGerman = "Als er seine Vollreife erlangt hatte, gaben Wir ihm Urteil(skraft) und Wissen. So vergelten Wir den Gutes Tuenden." });
                        surah12.VerseList.Add(new Verse { Number = 23, TextBosnian = "I poče ga na grijeh navoditi ona u čijoj je kući bio, pa zaključa sva vrata i reče: 'Hodi!' 'Allahovu zaštitu tražim!', uzviknu on, 'gospodar me moj lijepo pazi; a zulumćari neće nikad uspjeti.'", TextEnglish = "And she, in whose house he was, sought to seduce him. She closed the doors and said, 'Come, you.' He said, '[I seek] the refuge of Allāh. Indeed, he[591] is my master, who has made good my residence. Indeed, wrongdoers will not succeed.'", TextGerman = "Und diejenige, in deren Haus er war, versuchte, ihn zu verführen. Sie schloß die Türen ab und sagte: Da bin ich für dich! Er sagte: Allah schütze mich (davor)! Er, mein Herr, hat mir einen schönen Aufenthalt bereitet. Gewiß, den Ungerechten wird es nicht wohl ergehen." });
                        surah12.VerseList.Add(new Verse { Number = 24, TextBosnian = "I ona je bila poželjela njega, a i on bi nju bio poželio da od Gospodara svoga nije jasan znak ugledao - tako bi, da odvratimo od njega zlo i blud. Uistinu je on među Našim odabranim robovima.", TextEnglish = "And she certainly determined [to seduce] him, and he would have inclined to her had he not seen the proof [i.e., sign] of his Lord. And thus [it was] that We should avert from him evil and immorality. Indeed, he was of Our chosen servants.", TextGerman = "Es verlangte sie nach ihm, und es hätte ihn nach ihr verlangt, wenn er nicht den Beweis seines Herrn gesehen hätte. Dies (geschah), damit Wir das Böse und das Schändliche von ihm abwendeten. Er gehört ja zu Unseren auserlesenen Dienern." });
                        surah12.VerseList.Add(new Verse { Number = 25, TextBosnian = "I njih dvoje prema vratima potrčaše - a ona razdera straga košulju njegovu - i muža njezina kraj vrata zatekoše. 'Kakvu kaznu zaslužuje onaj koji je htio ženi tvojoj zlo učiniti', reče ona, 'ako ne tamnicu ili kaznu bolnu?'", TextEnglish = "And they both raced to the door, and she tore his shirt from the back, and they found her husband at the door. She said, 'What is the recompense of one who intended evil for your wife but that he be imprisoned or a painful punishment?'", TextGerman = "Sie versuchten beide als erster zur Tür zu gelangen. Sie zerriß ihm von hinten das Hemd. Und sie fanden ihren Herrn bei der Tür vor. Sie sagte: Der Lohn dessen, der deiner Familie Böses (antun) wollte, ist nur, daß er ins Gefängnis gesteckt wird, oder schmerzhafte Strafe." });
                        surah12.VerseList.Add(new Verse { Number = 26, TextBosnian = "Ona je pokušala da me zavede', reče Jusuf. I posvjedoči jedan svjedok iz porodice njene: 'Ako je košulja njegova sprijeda razderana, onda ona istinu govori, a on je među lažljivcima,", TextEnglish = "[Joseph] said, 'It was she who sought to seduce me.' And a witness from her family testified, 'If his shirt is torn from the front, then she has told the truth, and he is of the liars.", TextGerman = "Er sagte: Sie war es, die versucht hat, mich zu verführen. Und ein Zeuge aus ihrer Familie bezeugte: Wenn sein Hemd vorn zerrissen ist, dann hat sie die Wahrheit gesprochen, und er gehört zu den Lügnern." });
                        surah12.VerseList.Add(new Verse { Number = 27, TextBosnian = "a ako je košulja njegova straga razderana, onda ona laže, a on govori istinu.'", TextEnglish = "But if his shirt is torn from the back, then she has lied, and he is of the truthful.'", TextGerman = "Wenn sein Hemd aber hinten zerrissen ist, dann hat sie gelogen, und er gehört zu den Wahrhaftigen." });
                        surah12.VerseList.Add(new Verse { Number = 28, TextBosnian = "I kad on vidje da je košulja njegova straga razderana, reče: 'To je jedno od vaših - ženskih lukavstava; vaša lukavstva su, zaista, velika!", TextEnglish = "So when he [i.e., her husband] saw his shirt torn from the back, he said, 'Indeed, it is of your [i.e., women's] plan. Indeed, your plan is great [i.e., vehement].", TextGerman = "Als er nun sah, daß sein Hemd hinten zerrissen war, sagte er: Das gehört zu euren Listen. Eure List ist gewaltig." });
                        surah12.VerseList.Add(new Verse { Number = 29, TextBosnian = "Ti, Jusufe, zaboravi i ne spominji ovo, a ti traži oproštenje za grijeh svoj; ti si, uistinu, jedna od grešnica bila!'", TextEnglish = "Joseph, ignore this.[592] And, [my wife], ask forgiveness for your sin. Indeed, you were of the sinful.'", TextGerman = "Yusuf, lasse davon ab! Und (du, jene,) bitte um Vergebung für deine Sünde! Du gehörst ja zu denjenigen, die Verfehlungen begangen haben." });
                        surah12.VerseList.Add(new Verse { Number = 30, TextBosnian = "I žene u gradu počeše govorkati: 'Velikaševa žena zavodi mladića, slugu svoga, u njega se ludo zaljubila! Mi mislimo da je ona u zabludi očitaj.'", TextEnglish = "And women in the city said, 'The wife of al-ʿAzeez is seeking to seduce her slave boy; he has impassioned her with love. Indeed, we see her [to be] in clear error.'", TextGerman = "(Einige) Frauen in der Stadt sagten: Die Frau des hohen Herrn versucht, ihren Burschen zu verführen. Er hat sie in leidenschaftliche Liebe versetzt. Wir sehen wahrlich, sie befindet sich in deutlichem Irrtum." });
                        surah12.VerseList.Add(new Verse { Number = 31, TextBosnian = "I kad ona ču za ogovaranja njihova, posla po njih, te im naslone pripremi, dade svakoj od njih po nož i reče: 'Izađi pred njih!' A kad ga one ugledaše, njegova ljepota ih zanese i po rukama svojim se porezaše: 'Sačuvaj Allahu!', uskliknuše, 'ovo nije čovjek, ovo je melek plemeniti!'", TextEnglish = "So when she heard of their scheming, she sent for them and prepared for them a banquet and gave each one of them a knife and said [to Joseph], 'Come out before them.' And when they saw him, they greatly admired him and cut their hands[593] and said, 'Perfect is Allāh![594] This is not a man; this is none but a noble angel.'", TextGerman = "Als sie nun von ihren Ränken hörte, sandte sie zu ihnen und bereitete ihnen ein Gastmahl. Sie gab einer jeden von ihnen ein Messer und sagte (zu Yusuf): Komm zu ihnen heraus. Als sie ihn sahen, fanden sie ihn groß(artig), und sie zerschnitten sich ihre Hände und sagten: Allah behüte! Das ist kein Mensch, das ist nur ein ehrenvoller Engel." });
                        surah12.VerseList.Add(new Verse { Number = 32, TextBosnian = "E, to vam je onaj zbog koga ste me korile', reče ona. 'Istina je da sam ga zavodila, ali se on uščuvao. Ako ne učini ono što od njega tražim, sigurno će u zatvoru završiti i među poniženim biti.'", TextEnglish = "She said, 'That is the one about whom you blamed me. And I certainly sought to seduce him, but he firmly refused; and if he will not do what I order him, he will surely be imprisoned and will be of those debased.'", TextGerman = "Sie sagte: Seht, das ist der, dessentwegen ihr mich getadelt habt. Ich habe allerdings versucht, ihn zu verführen, doch er widerstand. Und wenn er nicht tut, was ich ihm befehle, wird er ganz gewiß ins Gefängnis gesteckt werden, und er wird gewiß zu den Geringgeachteten gehören." });
                        surah12.VerseList.Add(new Verse { Number = 33, TextBosnian = "Gospodaru moj', rekao je on, 'draža mi je tamnica od ovoga na što me one navraćaju! I ako Ti ne odvratiš od mene lukavstva njihova, ja mogu prema njima naklonost osjetiti i jedan od lahkomislenih postati.'", TextEnglish = "He said, 'My Lord, prison is more to my liking than that to which they invite me. And if You do not avert from me their plan, I might incline toward them and [thus] be of the ignorant.'", TextGerman = "Er sagte: Mein Herr, das Gefängnis ist mir lieber als das, wozu sie mich auffordern. Und wenn Du ihre List von mir nicht abwendest, werde ich mich zu ihnen hingezogen fühlen und zu den Toren gehören." });
                        surah12.VerseList.Add(new Verse { Number = 34, TextBosnian = "I Gospodar mu njegov molbu usliša i spasi ga lukavstva njihova; On je, uistinu, Svečujući i Sveznajući.", TextEnglish = "So his Lord responded to him and averted from him their plan. Indeed, He is the Hearing, the Knowing.", TextGerman = "Da erhörte ihn sein Herr und wendete ihre List von ihm ab. Er ist ja der Allhörende und Allwissende." });
                        surah12.VerseList.Add(new Verse { Number = 35, TextBosnian = "A potom im, kada su vidjeli dokaze da je nedužan, naumpade da ga za neko vrijeme smjeste u tamnicu.", TextEnglish = "Then it appeared to them after they had seen the signs[595] that he [i.e., al-ʿAzeez] should surely imprison him for a time.[596]", TextGerman = "Hierauf schien es ihnen angebracht, nachdem sie die Zeichen (seiner Unschuld) gesehen hatten, ihn für eine gewisse Zeit ins Gefängnis zu stecken." });
                        surah12.VerseList.Add(new Verse { Number = 36, TextBosnian = "S njim su u tamnicu ušla još dva momka. 'Ja sam sanjao da cijedim vino', reče jedan od njih. 'A ja, opet', reče drugi, 'kako na glavi nosim hljeb koji ptice kljuju. Protumači nam to, jer vidimo da si, zaista, jedan od dobročinitelja.'", TextEnglish = "And there entered the prison with him two young men. One of them said, 'Indeed, I have seen myself [in a dream] pressing [grapes for] wine.' The other said, 'Indeed, I have seen myself carrying upon my head [some] bread, from which the birds were eating. Inform us of its interpretation; indeed, we see you to be of those who do good.'", TextGerman = "Mit ihm kamen zwei Burschen ins Gefängnis. Der eine von ihnen sagte: Ich sah mich Wein auspressen. Der andere sagte: Ich sah mich auf dem Kopf Brot tragen, von dem die Vögel fraßen. Tue uns die Deutung hiervon kund. Wir sehen, daß du zu den Gutes Tuenden gehörst." });
                        surah12.VerseList.Add(new Verse { Number = 37, TextBosnian = "Ništa od onoga što jedete neće vam doneseno biti, a da vam ja prije ne kažem što ćete dobiti', reče Jusuf. 'To je samo dio onoga čemu me naučio Gospodar moj, ja se klonim vjere naroda koji u Allaha ne vjeruje i koji i ahiret negira,", TextEnglish = "He said, 'You will not receive food that is provided to you except that I will inform you of its interpretation before it comes to you. That is from what my Lord has taught me. Indeed, I have left the religion of a people who do not believe in Allāh, and they, in the Hereafter, are disbelievers.", TextGerman = "Er sagte: Es wird euch kein Essen gebracht, mit dem ihr versorgt werdet, ohne daß ich euch die Deutung davon kundgetan habe, bevor es euch gebracht wird. Seht, das ist etwas von dem, was mich mein Herr gelehrt hat. Verlassen habe ich das Glaubensbekenntnis von Leuten, die nicht an Allah glauben, und (verlassen habe, ich) sie, die sie das Jenseits verleugnen," });
                        surah12.VerseList.Add(new Verse { Number = 38, TextBosnian = "i slijedim vjeru predaka svojih, Ibrahima i Ishaka i Jakuba; nama ne priliči da ikoga Allahu u obožavanju pridružujemo. To je iz Allahove darežljivosti prema nama i ostalim ljudima, ali većina ljudi ne zahvaljuje.'", TextEnglish = "And I have followed the religion of my fathers, Abraham, Isaac and Jacob. And it was not for us to associate anything with Allāh. That is from the favor of Allāh upon us and upon the people, but most of the people are not grateful.", TextGerman = "und ich bin dem Glaubensbekenntnis meiner Väter Ibrahim, Ishaq und Ya’qub gefolgt. Es steht uns nicht zu, Allah etwas beizugesellen. Das ist etwas von Allahs Huld gegen uns und gegen die Menschen. Aber die meisten Menschen sind nicht dankbar." });
                        surah12.VerseList.Add(new Verse { Number = 39, TextBosnian = "O drugovi moji u tamnici, da li su raznorazna božanstva bolja ili Allah, Jedini i Potčinitelj svega?", TextEnglish = "O [my] two companions of prison, are separate lords better or Allāh, the One,[597] the Prevailing?[598]", TextGerman = "O meine beiden Gefängnisgefährten! Sind verschiedene Herren besser oder Allah, der Eine, der Allbezwinger?" });
                        surah12.VerseList.Add(new Verse { Number = 40, TextBosnian = "Oni koje, mimo Njega, obožavate - samo su imena koja ste im nadjenuli vi i preci vaši - Allah o njima nikakva dokaza nije objavio. Sud pripada jedino Allahu, a On je naredio da samo Njemu u ibadetu budete. To je jedino prava vjera, ali većina ljudi ne zna.", TextEnglish = "You worship not besides Him except [mere] names you have named them,[599] you and your fathers, for which Allāh has sent down no evidence. Legislation is not but for Allāh. He has commanded that you worship not except Him. That is the correct religion, but most of the people do not know.", TextGerman = "Ihr dient außer Ihm nur Namen, die ihr genannt habt, ihr und eure Väter, für die Allah (jedoch) keine Ermächtigung herabgesandt hat. Das Urteil ist allein Allahs. Er hat befohlen, daß ihr nur Ihm dienen sollt. Das ist die richtige Religion. Aber die meisten Menschen wissen nicht." });
                        surah12.VerseList.Add(new Verse { Number = 41, TextBosnian = "O drugovi moji u tamnici, jedan će od vas gospodara svoga vinom pojiti, a drugi će raspet biti, pa će mu ptice glavu kljuvati. Ono što ste pitali već je određeno!'", TextEnglish = "O two companions of prison, as for one of you, he will give drink to his master of wine; but as for the other, he will be crucified, and the birds will eat from his head. The matter has been decreed about which you both inquire.'", TextGerman = "O meine beiden Gefangnisgefährten! Was den einen von euch angeht, so wird er seinem Herrn Wein zu trinken geben. Was aber den anderen angeht, so wird er gekreuzigt, und die Vögel werden von seinem Kopf fressen. Entschieden ist die Angelegenheit, über die ihr um Auskunft fragt." });
                        surah12.VerseList.Add(new Verse { Number = 42, TextBosnian = "A onome od njih dvojice za kojeg je znao da će spasen biti reče: 'Spomeni me gospodaru svome!', ali šejtan učini te on zaboravi da ga spomene gospodaru svome, i Jusuf ostade u tamnici nekoliko godina.", TextEnglish = "And he said to the one whom he knew would go free, 'Mention me before your master.' But Satan made him forget the mention [to] his master, and he [i.e., Joseph] remained in prison several years.", TextGerman = "Und er sagte zu dem von beiden, von dem er glaubte, daß er entkommen werde: Erwähne mich bei deinem Herrn. Aber der Satan ließ ihn vergessen, ihn bei seinem Herrn zu erwähnen, (und) so blieb er noch einige Jahre im Gefängnis." });
                        surah12.VerseList.Add(new Verse { Number = 43, TextBosnian = "I vladar reče: 'Sanjao sam kako sedam mršavih krava pojede sedam debelih, i sanjao sam sedam klasova zelenih i sedam drugih, sasušenih. O velikaši, protumačite mi san moj, ako snove znate tumačiti?'", TextEnglish = "And [subsequently] the king said, 'Indeed, I have seen [in a dream] seven fat cows being eaten by seven [that were] lean, and seven green spikes [of grain] and others [that were] dry. O eminent ones, explain to me my vision, if you should interpret visions.'", TextGerman = "Und der König sagte: Ich sah sieben fette Kühe, die von sieben mageren gefressen wurden, und sieben grüne Ähren und (sieben) andere dürre. O ihr führende Schar, gebt mir Auskunft über mein (Traum)gesicht, wenn ihr ein (Traum)gesicht auslegen könnt." });
                        surah12.VerseList.Add(new Verse { Number = 44, TextBosnian = "Zbrkanih li snova!', rekoše oni, 'mi snove ne znamo tumačiti.'", TextEnglish = "They said, '[It is but] a mixture of false dreams, and we are not learned in the interpretation of dreams.'", TextGerman = "Sie sagten: (Das ist) ein Bündel von wirren Träumen. Wir wissen über die Deutung der Träume nicht Bescheid." });
                        surah12.VerseList.Add(new Verse { Number = 45, TextBosnian = "I tada jedan od one dvojice koji se spasio, koji se poslije toliko vremena sjetio, reče: 'Ja ću vam protumačiti san, samo me pošaljite!'", TextEnglish = "But the one who was freed and remembered after a time said, 'I will inform you of its interpretation, so send me forth.'", TextGerman = "Derjenige von beiden, der entkommen war und sich nach einiger Zeit erinnerte, sagte: Ich werde euch seine Deutung kundtun. Darum entsendet mich." });
                        surah12.VerseList.Add(new Verse { Number = 46, TextBosnian = "Jusufe, o ti koji si uvijek iskren, protumači nam šta znači: sedam mršavih krava pojede sedam debelih; i sedam klasova zelenih i sedam drugih sasušenih - pa da se vratim ljudima, da bi oni saznali.'", TextEnglish = "[He said], 'Joseph, O man of truth, explain to us about seven fat cows eaten by seven [that were] lean, and seven green spikes [of grain] and others [that were] dry - that I may return to the people [i.e., the king and his court]; perhaps they will know [about you].'", TextGerman = "Yusuf, du Wahrhaftiger, gib uns Auskunft über sieben fette Kühe, die von sieben mageren gefressen werden, und über sieben grüne Ähren und (sieben) andere dürre, auf daß ich zu den Leuten zurückkehre, auf daß sie wissen mögen." });
                        surah12.VerseList.Add(new Verse { Number = 47, TextBosnian = "Sijat ćete sedam godina uzastopno', reče, 'pa ono što požanjete u klasu ostavite, osim ono malo što ćete jesti.", TextEnglish = "[Joseph] said, 'You will plant for seven years consecutively; and what you harvest leave in its spikes, except a little from which you will eat.", TextGerman = "Er sagte: Ihr werdet unablässig sieben Jahre wie gewohnt säen. Was ihr erntet, das laßt in seinen Ähren, bis auf ein weniges, wovon ihr eßt." });
                        surah12.VerseList.Add(new Verse { Number = 48, TextBosnian = "Zatim će poslije doći sedam teških koje će pojesti ono što ste za njih pripremili, ostat će jedino ono malo što ćete za sjetvu sačuvati.", TextEnglish = "Then will come after that seven difficult [years] which will consume what you advanced [i.e., saved] for them, except a little from which you will store.", TextGerman = "Hierauf werden nach alledem sieben harte (Jahre) kommen, die das aufzehren werden, was ihr für sie vorbereitet habt, bis auf ein weniges von dem, was ihr aufbewahrt." });
                        surah12.VerseList.Add(new Verse { Number = 49, TextBosnian = "Zatim će, poslije toga, doći godina u kojoj će ljudima kiše u obilju biti i u kojoj će cijediti.'", TextEnglish = "Then will come after that a year in which the people will be given rain and in which they will press [olives and grapes].'", TextGerman = "Hierauf wird nach alledem ein Jahr kommen, in dem die Menschen Regen haben und in dem sie (Früchte) auspressen werden." });
                        surah12.VerseList.Add(new Verse { Number = 50, TextBosnian = "I vladar reče: 'Dovedite mi ga!' I kad Jusufu izaslanik dođe, on reče: 'Vrati se gospodaru svome i upitaj ga: 'Šta je s onim ženama koje su svoje ruke porezale? Gospodar moj dobro zna spletke njihove!''", TextEnglish = "And the king said, 'Bring him to me.' But when the messenger came to him, [Joseph] said, 'Return to your master and ask him what is the case of the women who cut their hands. Indeed, my Lord is Knowing of their plan.'", TextGerman = "Der König sagte: Bringt ihn zu mir. Als der Bote zu ihm kam, sagte er: Kehr zu deinem Herrn zurück und frag ihn, wie es mit den Frauen steht, die sich ihre Hände zerschnitten. Mein Herr weiß doch über ihre List Bescheid." });
                        surah12.VerseList.Add(new Verse { Number = 51, TextBosnian = "Šta je s vama bilo kad ste Jusufa zavodile?', upita velikaš. 'Sačuvaj Allahu!', rekoše one, 'mi o njemu ništa ružno ne znamo!' 'Sad će istina izaći na vidjelo', reče velikaševa žena, 'ja sam njega zavodila, a on je, doista, jedan od iskrenih.", TextEnglish = "Said [the king to the women], 'What was your condition when you sought to seduce Joseph?' They said, 'Perfect is Allāh![600] We know about him no evil.' The wife of al-ʿAzeez said, 'Now the truth has become evident. It was I who sought to seduce him, and indeed, he is of the truthful.", TextGerman = "Er sagte: Was war da mit euch, als ihr versuchtet, Yusuf zu verführen? Sie sagten: Allah behüte! Wir wissen nichts Böses gegen ihn (anzugeben). Die Frau des hohen Herrn sagte: Jetzt ist die Wahrheil ans Licht gekommen. Ich habe versucht, ihn zu verführen. Und er gehört fürwahr zu den Wahrhaftigen." });
                        surah12.VerseList.Add(new Verse { Number = 52, TextBosnian = "To je zato da on zna da ga ja nisam tajno iznevjerila i da Allah ne da da se ostvare spletke izdajnika.", TextEnglish = "That is so he [i.e., al-ʿAzeez] will know that I did not betray him in [his] absence and that Allāh does not guide the plan of betrayers.", TextGerman = "Dies ist, damit er weiß, daß ich ihn nicht in seiner Abwesenheit verraten habe und daß Allah die List der Verräter nicht gelingen läßt." });
                        surah12.VerseList.Add(new Verse { Number = 53, TextBosnian = "Ja ne pravdam sebe, duša je zaista sklona zlu, osim one kojoj se Gospodar moj smiluje. Gospodar moj je, zaista, Onaj Koji oprašta grijehe i milostiv je.'", TextEnglish = "And I do not acquit myself. Indeed, the soul is a persistent enjoiner of evil, except those upon which my Lord has mercy. Indeed, my Lord is Forgiving and Merciful.'[601]", TextGerman = "Und ich spreche mich nicht selbst frei. Die Seele gebietet fürwahr mit Nachdruck das Böse, außer daß mein Herr Sich erbarmt. Mein Herr ist Allvergebend und Barmherzig." });
                        surah12.VerseList.Add(new Verse { Number = 54, TextBosnian = "I velikaš reče: 'Dovedite mi ga, uzet ću ga u svoju svitu', i pošto porazgovara s njim, reče mu: 'Ti ćeš od danas kod nas utjecajan i od povjerenja biti.'", TextEnglish = "And the king said, 'Bring him to me; I will appoint him exclusively for myself.' And when he spoke to him, he said, 'Indeed, you are today established [in position] and trusted.'", TextGerman = "Und der König sagte: Bringt ihn zu mir. Ich will ihn mir selbst vorbehalten. Als er mit ihm sprach, sagte er: Du bist (von) heute (an) bei uns in fester Stellung und vertrauenswürdig." });
                        surah12.VerseList.Add(new Verse { Number = 55, TextBosnian = "Postavi me', reče, 'da vodim brigu o zalihama u zemlji, ja sam zaista čuvaran i znan.'", TextEnglish = "[Joseph] said, 'Appoint me over the storehouses of the land. Indeed, I will be a knowing guardian.'", TextGerman = "Er sagte: Setze mich über die Vorratskammern des Landes ein; ich bin ein kenntnisreicher Hüter." });
                        surah12.VerseList.Add(new Verse { Number = 56, TextBosnian = "I tako Mi Jusufu dadosmo vlast u zemlji; boravio je ondje gdje je htio; milost svoju Mi dajemo onome kome hoćemo i ne uništavamo nagradu onima koji dobra djela čine.", TextEnglish = "And thus We established Joseph in the land to settle therein wherever he willed. We touch with Our mercy whom We will, and We do not allow to be lost the reward of those who do good.", TextGerman = "So verliehen Wir Yusuf eine feste Stellung im Land, so daß er sich darin aufhalten konnte, wo immer er wollte. Wir treffen mit Unserer Barmherzigkeit, wen Wir wollen, und Wir lassen den Lohn der Gutes Tuenden nicht verlorengehen." });
                        surah12.VerseList.Add(new Verse { Number = 57, TextBosnian = "A nagrada na ahiretu bolja je za one koji vjeruju i koji se Allaha boje i grijeha klone.", TextEnglish = "And the reward of the Hereafter is better for those who believed and were fearing Allāh.", TextGerman = "Aber der Lohn des Jenseits ist wahrlich besser für diejenigen, die glauben und gottesfürchtig sind." });
                        surah12.VerseList.Add(new Verse { Number = 58, TextBosnian = "I dođoše braća Jusufova i uđoše k njemu, pa ih on poznade, a oni njega ne poznadoše.", TextEnglish = "And the brothers of Joseph came [seeking food], and they entered upon him; and he recognized them, but he was to them unknown.[602]", TextGerman = "Und da kamen Yusufs Brüder und traten bei ihm ein. Er erkannte sie, während sie ihn nicht erkannten." });
                        surah12.VerseList.Add(new Verse { Number = 59, TextBosnian = "I kad ih podmiri namirnicama i tovare im pripremi, reče: 'Dovedite mi svoga brata po ocu, zar ne vidite da punu mjeru dajem i da najbolje ugošćujem.", TextEnglish = "And when he had furnished them with their supplies, he said, 'Bring me a brother of yours from your father.[603] Do you not see that I give full measure and that I am the best of accommodators?", TextGerman = "Als er sie nun mit ihrem Bedarf ausgestattet hatte, sagte er: Bringt mir einen (Halb)bruder von euch väterlicherseits. Seht ihr denn nicht, daß ich volles Maß gebe und daß ich der beste Obdachgeber bin?" });
                        surah12.VerseList.Add(new Verse { Number = 60, TextBosnian = "Ako mi ga ne dovedete, nećete više od mene mjeru s namirnicama dobiti i blizu mi ne dolazite!'", TextEnglish = "But if you do not bring him to me, no measure will there be [hereafter] for you from me, nor will you approach me.'", TextGerman = "Wenn ihr ihn mir nicht bringt, so bekommt ihr bei mir kein Maß mehr, und ihr sollt nicht in meine Nähe kommen." });
                        surah12.VerseList.Add(new Verse { Number = 61, TextBosnian = "Pobrinut ćemo se da ga nekako od oca njegova izmamimo, zaista ćemo tako postupiti', rekoše oni.", TextEnglish = "They said, 'We will attempt to dissuade his father from [keeping] him, and indeed, we will do [it].'", TextGerman = "Sie sagten: Wir werden versuchen, seinen Vater in Bezug auf ihn zu bewegen, und wir werden es bestimmt tun." });
                        surah12.VerseList.Add(new Verse { Number = 62, TextBosnian = "A Jusuf reče momcima svojim: 'Stavite njihove stvari za koje su kupili namirnice u tovare njihove, oni će ih, kad se vrate svojima, prepoznati i opet će se vratiti.'", TextEnglish = "And [Joseph] said to his servants, 'Put their merchandise[604] into their saddlebags so they might recognize it when they have gone back to their people that perhaps they will [again] return.'", TextGerman = "Und er sagte zu seinen Burschen: Steckt ihre (Tausch)ware (zurück) in ihr Gepäck, so daß sie sie (wieder)erkennen, wenn sie zu ihren Angehörigen zurückgekehrt sind, auf daß sie wiederkommen mögen." });
                        surah12.VerseList.Add(new Verse { Number = 63, TextBosnian = "I pošto se vratiše ocu svome, rekoše: 'O oče naš, uskraćeni ćemo za mjeru s namirnicama biti. Zato pošalji s nama brata našeg da bismo dobili mjeru s namirnicama, a mi ćemo ga, zaista, čuvati.'", TextEnglish = "So when they returned to their father, they said, 'O our father, [further] measure has been denied to us, so send with us our brother [that] we will be given measure. And indeed, we will be his guardians.'", TextGerman = "Als sie zu ihrem Vater zurückkamen, sagten sie: O unser Vater, ein (weiteres) Maß ist uns verweigert worden. Darum lasse unseren Bruder mit uns gehen, so bekommen wir ein (weiteres) Maß zugeteilt; wir werden ihn fürwahr behüten." });
                        surah12.VerseList.Add(new Verse { Number = 64, TextBosnian = "Zar da vam ga povjerim kao što sam prije povjerio brata njegova?', reče on. 'Ali, Allah je Čuvar najbolji i On je Najmilostiviji!'", TextEnglish = "He said, 'Should I entrust you with him except [under coercion] as I entrusted you with his brother before? But Allāh is the best guardian, and He is the most merciful of the merciful.'", TextGerman = "Er sagte: Kann ich ihn euch etwa anders anvertrauen, als ich euch zuvor seinen Bruder anvertraut habe? Allah ist besser als Behütender, und Er ist der Barmherzigste der Barmherzigen." });
                        surah12.VerseList.Add(new Verse { Number = 65, TextBosnian = "A kada otvoriše tovare svoje, nađoše da su im vraćene stvari njihove, i rekoše: 'O oče naš, šta možemo više poželjeti? Evo, vraćene su nam stvari naše, i hranom ćemo porodicu našu namiriti, i brata našeg ćemo čuvati, a dobit ćemo i dodatni tovar koji kamila može ponijeti; to je za njega neznatan iznos.'", TextEnglish = "And when they opened their baggage, they found their merchandise returned to them. They said, 'O our father, what [more] could we desire? This is our merchandise returned to us. And we will obtain supplies [i.e., food] for our family and protect our brother and obtain an increase of a camel's load; that is an easy measurement.'[605]", TextGerman = "Und als sie ihre Sachen öffneten, fanden sie, daß ihre Ware ihnen zurückgegeben worden war. Sie sagten: O unser Vater, was begehren wir (mehr)? Das ist unsere Ware, sie ist uns zurückgegeben worden. Wir werden Vorrat für unsere Angehörigen bringen, unseren Bruder behüten und das Maß (der Last) eines Kamels mehr haben. Das ist ein leicht(erhältlich)es Maß." });
                        surah12.VerseList.Add(new Verse { Number = 66, TextBosnian = "Ja ga s vama neću poslati', reče, 'dok se čvrsto Allahom ne zakunete da ćete mi ga doista vratiti, osim ako onemogućeni budete.' I pošto mu se oni zakleše, on reče: 'Allah je za ono što govorimo jamac!'", TextEnglish = "[Jacob] said, 'Never will I send him with you until you give me a promise [i.e., oath] by Allāh that you will bring him [back] to me, unless you should be surrounded [i.e., overcome by enemies].' And when they had given their promise, he said, 'Allāh, over what we say, is Entrusted.'[606]", TextGerman = "Er sagte: Ich werde ihn nicht mit euch senden, bis ihr mir ein verbindliches Versprechen vor Allah gebt, daß ihr ihn mir ganz gewiß wiederbringt, es sei denn, ihr werdet umringt. Als sie ihm ihr verbindliches Versprechen gegeben hatten, sagte er: Allah ist Sachwalter über das, was wir (hier) sagen." });
                        surah12.VerseList.Add(new Verse { Number = 67, TextBosnian = "I reče: 'O sinovi moji, ne ulazite na jednu kapiju, već na razne kapije, a ja vas ne mogu spasiti od onoga što vam Allah odredi; vlast pripada jedino Njemu, ja se u Njega uzdam, i neka se samo u Njega uzdaju oni koji se uzdaju!'", TextEnglish = "And he said, 'O my sons, do not enter from one gate but enter from different gates; and I cannot avail you against [the decree of] Allāh at all. The decision is only for Allāh; upon Him I have relied, and upon Him let those who would rely [indeed] rely.'", TextGerman = "Und er sagte: O meine Kinder, geht nicht (alle) durch ein einziges Tor hinein, sondern geht durch verschiedene Tore hinein. Ich kann euch gegen Allah nichts nützen. Das Urteil ist allein Allahs. Auf Ihn verlasse ich mich; und auf Ihn sollen sich diejenigen verlassen, die sich (überhaupt auf jemanden) verlassen (wollen)." });
                        surah12.VerseList.Add(new Verse { Number = 68, TextBosnian = "I kad uđoše onako kako im je otac njihov naredio, to im nimalo nije pomoglo da budu pošteđeni onoga što im je Allah bio odredio, jedino se ostvarila želja Jakubova, koju je izvršio, a on je, uistinu, znanje imao, zato što smo ga Mi naučili, ali većina ljudi ne zna.", TextEnglish = "And when they entered from where their father had ordered them, it did not avail them against Allāh at all except [it was] a need [i.e., concern] within the soul of Jacob, which he satisfied. And indeed, he was a possessor of knowledge because of what We had taught him, but most of the people do not know.", TextGerman = "Als sie hineingingen, wie ihr Vater ihnen befohlen hatte, konnte es ihnen vor Allah nichts nützen. (Es war) nur ein Bedürfnis in der Seele Ya’qubs, das er (damit) erfüllte. Und er besaß fürwahr Wissen, weil Wir es ihn gelehrt hatten. Aber die meisten Menschen wissen nicht." });
                        surah12.VerseList.Add(new Verse { Number = 69, TextBosnian = "I kad dođoše kod Jusufa, on privi na grudi brata svoga i reče: 'Ja sam, doista, brat tvoj i ne žalosti se zbog onog što su oni činili.'", TextEnglish = "And when they entered upon Joseph, he took his brother to himself; he said, 'Indeed, I am your brother, so do not despair over what they used to do [to me].'", TextGerman = "Als sie bei Yusuf eintraten, zog er seinen Bruder zu sich. Er sagte: Gewiß, ich bin dein Bruder. So sei nicht bekümmert wegen dessen, was sie getan haben." });
                        surah12.VerseList.Add(new Verse { Number = 70, TextBosnian = "I pošto ih namiri potrebnom hranom, stavi pehar u tovar brata svoga, a poslije jedan glasnik stade vikati: 'O karavanu, vi ste, doista, kradljivci!'", TextEnglish = "So when he had furnished them with their supplies, he put the [gold measuring] bowl into the bag of his brother. Then an announcer called out, 'O caravan, indeed you are thieves.'", TextGerman = "Als er sie nun mit ihrem Bedarf ausgestattet hatte, steckte er das Trinkgefäß in das Gepäck seines Bruders. Hierauf rief ein Rufer aus: Ihr (da, von der) Karawane, ihr seid fürwahr Diebe." });
                        surah12.VerseList.Add(new Verse { Number = 71, TextBosnian = "Oni im pristupiše i upitaše: 'Šta ste to izgubili?'", TextEnglish = "They said while approaching them, 'What is it you are missing?'", TextGerman = "Sie sagten, indem sie sich ihnen zuwandten: Was vermißt ihr?" });
                        surah12.VerseList.Add(new Verse { Number = 72, TextBosnian = "Izgubili smo vladarevu posudu', odgovoriše. 'Ko je donese, dobit će tovar hrane koji kamila može ponijeti. Ja za to garantujem.'", TextEnglish = "They said, 'We are missing the measure of the king. And for he who produces it is [the reward of] a camel's load, and I am responsible for it.'", TextGerman = "Sie sagten: Wir vermissen den Kelch des Königs. Wer ihn wiederbringt, erhält die Last eines Kamels, und dafür bin ich Bürge." });
                        surah12.VerseList.Add(new Verse { Number = 73, TextBosnian = "Allaha nam', rekoše oni, 'vi znate da mi nismo došli da činimo nered u zemlji, i mi nismo kradljivci.'", TextEnglish = "They said, 'By Allāh, you have certainly known that we did not come to cause corruption in the land, and we have not been thieves.'", TextGerman = "Sie sagten: Bei Allah, ihr wißt doch, wir sind nicht gekommen, um im Land Unheil zu stiften, und wir sind keine Diebe." });
                        surah12.VerseList.Add(new Verse { Number = 74, TextBosnian = "A kakva je takvome kazna ako budete lagali?', upitaše.", TextEnglish = "They [the accusers] said, 'Then what would be its recompense[607] if you should be liars?'", TextGerman = "Sie sagten: Was soll dann die Vergeltung dafür sein, wenn ihr Lügner seid?" });
                        surah12.VerseList.Add(new Verse { Number = 75, TextBosnian = "Kazna je onome u čijem se tovaru nađe - sam on', odgovoriše. 'Eto tako mi kažnjavamo zulumćare.'", TextEnglish = "[The brothers] said, 'Its recompense is that he in whose bag it is found - he [himself] will be its recompense.[608] Thus do we recompense the wrongdoers.'", TextGerman = "Sie (die Brüder) sagten: Die Vergeltung dafür soll sein, daß derjenige, in dessen Gepäck er gefunden wird, selbst das Entgelt dafür sein soll. So vergelten wir den Ungerechten." });
                        surah12.VerseList.Add(new Verse { Number = 76, TextBosnian = "I on poče s vrećama njihovim, prije vreće brata svoga, a onda izvadi pehar iz vreće brata svoga. Tako smo, eto, u Jusufovu korist varku napravili. On, po vladarevu zakonu, nije mogao uzeti kao roba brata svoga, izuzev da to Allah hoće. Mi uzdižemo na veće položaje onoga koga hoćemo. Sve znaniji od znanijeg ima, a Sveznajući je iznad svakog.", TextEnglish = "So he began [the search] with their bags before the bag of his brother; then he extracted it from the bag of his brother. Thus did We plan for Joseph. He could not have taken his brother within the religion [i.e., law] of the king except that Allāh willed. We raise in degrees whom We will, but over every possessor of knowledge is one [more] knowing.[609]", TextGerman = "Er begann (zu suchen) in ihren Behältern vor dem Behälter seines Bruders. Hierauf holte er es aus dem Behälter seines Bruders hervor. So führten Wir für Yusuf eine List aus. Nach dem Gesetz des Königs hätte es ihm nicht zugestanden, seinen Bruder (als Sklaven) zu nehmen, außer daß Allah es wollte. Wir erhöhen, wen Wir wollen, um Rangstufen. Und über jedem, der Wissen besitzt, steht einer, der (noch mehr) weiß." });
                        surah12.VerseList.Add(new Verse { Number = 77, TextBosnian = "Ako je on ukrao', rekoše oni, 'pa i prije je brat njegov krao!' I Jusuf to u sebi zataji i to im ne otkri. 'Vi ste u gorem položaju', pomisli u sebi, 'Allah najbolje zna ono što vi spominjete.'", TextEnglish = "They said, 'If he steals - a brother of his has stolen before.' But Joseph kept it within himself and did not reveal it to them.[610] He said, 'You are worse in position, and Allāh is most knowing of what you describe.'", TextGerman = "Sie sagten: Wenn er stiehlt, so hat ein Bruder von ihm schon zuvor gestohlen. Aber Yusuf hielt es in seinem Innersten geheim und legte es ihnen nicht offen. Er sagte: Ihr befindet euch in einer (noch) schlechteren Lage. Und Allah weiß sehr wohl, was ihr beschreibt." });
                        surah12.VerseList.Add(new Verse { Number = 78, TextBosnian = "O velikašu', rekoše oni, 'on ima vrlo stara oca, pa uzmi jednog od nas umjesto njega! Mi uistinu vidimo da si jedan od dobročinitelja.'", TextEnglish = "They said, 'O ʿAzeez,[611] indeed he has a father [who is] an old man, so take one of us in place of him. Indeed, we see you as a doer of good.'", TextGerman = "Sie sagten: O hoher Herr, er hat einen Vater, einen hochbetagten Greis. So nimm einen von uns an seiner Stelle. Wir sehen, daß du zu den Gutes Tuenden gehörst." });
                        surah12.VerseList.Add(new Verse { Number = 79, TextBosnian = "Sačuvaj Allahu', reče, 'da uzmemo nekog drugog do onoga u koga smo našli ono što je naše! Tada bismo zaista zulumćari bili!'", TextEnglish = "He said, '[I seek] the refuge of Allāh [to prevent] that we take except him with whom we found our possession. Indeed, we would then be unjust.'", TextGerman = "Er sagte: Allah schütze uns (davor), daß wir einen anderen nehmen als denjenigen, bei dem wir unsere Sachen gefunden haben! Wir würden sonst wahrlich zu den Ungerechten gehören." });
                        surah12.VerseList.Add(new Verse { Number = 80, TextBosnian = "I pošto u njemu izgubiše svaku nadu da će brata spasiti, odvojiše se ustranu da se posavjetuju. 'Zar ne znate', reče najstariji među njima, 'da ste se svome ocu Allahom zakleli, a i da ste prije toga u vezi s Jusufom propust učinili?! Neću napustiti ovu zemlju dok mi to moj otac ne dozvoli ili dok Allah u moju korist ne presudi, a On je Sudija najbolji.'", TextEnglish = "So when they had despaired of him, they secluded themselves in private consultation. The eldest of them said, 'Do you not know that your father has taken upon you an oath by Allāh and [that] before you failed in [your duty to] Joseph? So I will never leave [this] land until my father permits me or Allāh decides for me,[612] and He is the best of judges.", TextGerman = "Als sie die Hoffnung an ihm aufgegeben hatten, zogen sie sich zurück zu einem vertraulichen Gespräch. Der Älteste von ihnen sagte: Wißt ihr (denn) nicht, daß euer Vater von euch ein verbindliches Versprechen vor Allah entgegengenommen hat und daß ihr zuvor (eure Pflicht) gegenüber Yusuf vernachlässigt habt? Ich werde das Land nicht verlassen, bis mein Vater es mir erlaubt oder Allah ein Urteil für mich fällt. Er ist der Beste derer, die Urteile fällen." });
                        surah12.VerseList.Add(new Verse { Number = 81, TextBosnian = "Vratite se svom ocu pa recite: 'O oče naš, sin tvoj je ukrao; mi tvrdimo samo ono što smo vidjeli, a mi nismo mogli neviđeno znati.:'", TextEnglish = "Return to your father and say, 'O our father, indeed your son has stolen, and we did not testify except to what we knew. And we were not witnesses of the unseen.[613]", TextGerman = "Kehrt zu eurem Vater zurück und sagt: ,O unser Vater, dein Sohn hat gestohlen, und wir bezeugen nur das, was wir wissen, und wir sind nicht Hüter über das Verborgene." });
                        surah12.VerseList.Add(new Verse { Number = 82, TextBosnian = "Pitaj naselje u kome smo boravili i karavan s kojim smo došli. Mi, zaista, govorimo istinu!", TextEnglish = "And ask the city in which we were and the caravan in which we came - and indeed, we are truthful.''", TextGerman = "Frag die Stadt, in der wir waren, und die Karawane, mit der wir angekommen sind. Wir sagen gewiß die Wahrheit’." });
                        surah12.VerseList.Add(new Verse { Number = 83, TextBosnian = "Nije tako', reče Jakub, 'u dušama vašim ponikla je zla misao, a lijepa je strpljivost. Nadam se da će mi ih Allah sve vratiti; uistinu je On Sveznajući i Mudri.'", TextEnglish = "[Jacob] said, 'Rather, your souls have enticed you to something, so patience is most fitting. Perhaps Allāh will bring them to me all together. Indeed, it is He who is the Knowing, the Wise.'", TextGerman = "Er sagte: Nein! Vielmehr habt ihr selbst euch etwas eingeredet. (Es gilt) schöne Geduld (zu üben). Aber vielleicht wird Allah sie mir alle wiederbringen. Er ist ja der Allwissende und Allweise." });
                        surah12.VerseList.Add(new Verse { Number = 84, TextBosnian = "I okrenu se od njih i reče: 'O Jusufe, tugo moja!', i oči mu pobijelješe od jada, bio je vrlo potišten.", TextEnglish = "And he turned away from them and said, 'Oh, my sorrow over Joseph,' and his eyes became white[614] from grief, for he was [of that] a suppressor.[615]", TextGerman = "Und er kehrte sich von ihnen ab und sagte: O mein Kummer um Yusuf! Und seine Augen wurden weiß vor Trauer, und so hielt er (seinen Kummer) zurück." });
                        surah12.VerseList.Add(new Verse { Number = 85, TextBosnian = "Allaha nam', rekoše oni, 'ti toliko spominješ Jusufa da ćeš teško oboljeti ili umrijeti!'", TextEnglish = "They said, 'By Allāh, you will not cease remembering Joseph until you become fatally ill or become of those who perish.'", TextGerman = "Sie sagten: Bei Allah, du hörst nicht auf, Yusufs zu gedenken, bis du hinfällig geworden bist oder zu denen gehörst, die umkommen." });
                        surah12.VerseList.Add(new Verse { Number = 86, TextBosnian = "Ja tugu svoju i jad svoj pred Allaha iznosim, a Allah mi je dao da znam ono što vi ne znate', reče on.", TextEnglish = "He said, 'I only complain of my suffering and my grief to Allāh, and I know from Allāh that which you do not know.", TextGerman = "Er sagte: Ich klage meinen unerträglichen Kummer und meine Trauer nur Allah (allein), und ich weiß von Allah her, was ihr nicht wißt." });
                        surah12.VerseList.Add(new Verse { Number = 87, TextBosnian = "O sinovi moji, idite i raspitajte se za Jusufa i brata njegova, i ne gubite nadu u milost Allahovu; doista, nadu u Allahovu milost gube samo ljudi koji su nevjernici.'", TextEnglish = "O my sons, go and find out about Joseph and his brother and despair not of relief from Allāh. Indeed, no one despairs of relief from Allāh except the disbelieving people.'", TextGerman = "O meine Kinder, geht und erkundigt euch über Yusuf und seinen Bruder. Und gebt nicht die Hoffnung auf das Erbarmen Allahs auf. Es gibt die Hoffnung auf das Erbarmen Allahs nur das ungläubige Volk auf." });
                        surah12.VerseList.Add(new Verse { Number = 88, TextBosnian = "I kad oni uđoše kod njega, rekoše: 'O velikašu, i nas i porodicu našu pritisla je nevolja; donijeli smo malo vrijedne stvari, ali ti nam podaj punu mjeru i udijeli nam milostinju, jer Allah, doista, nagrađuje one koji milostinju udjeljuju.'", TextEnglish = "So when they entered upon him [i.e., Joseph], they said, 'O ʿAzeez, adversity has touched us and our family, and we have come with goods poor in quality, but give us full measure and be charitable to us. Indeed, Allāh rewards the charitable.'", TextGerman = "Als sie (wieder) bei ihm eintraten, sagten sie: O hoher Herr, Unheil ist uns und unseren Angehörigen widerfahren. Und wir haben (nur) Ware von geringem Wert gebracht. So gib uns (dennoch) volles Maß und gib (es) uns als Almosen. Allah vergilt denjenigen, die Almosen geben." });
                        surah12.VerseList.Add(new Verse { Number = 89, TextBosnian = "A znate li', upita on, 'šta ste uradili s Jusufom i bratom njegovim kada ste lahkomisleni bili?'", TextEnglish = "He said, 'Do you know what you did with Joseph and his brother when you were ignorant?'", TextGerman = "Er sagte: Wißt ihr (noch), was ihr Yusuf und seinem Bruder damals in eurer Torheit antatet?" });
                        surah12.VerseList.Add(new Verse { Number = 90, TextBosnian = "A da ti nisi, uistinu, Jusuf?', povikaše oni. 'Da, ja sam Jusuf, a ovo je brat moj, Allah nam je milost darovao; a ko god se uščuva i strpljiv bude bio - pa, Allah, uistinu, neće dopustiti da propadne nagrada onima koji dobra djela čine.'", TextEnglish = "They said, 'Are you indeed Joseph?' He said, 'I am Joseph, and this is my brother. Allāh has certainly favored us. Indeed, he who fears Allāh and is patient, then indeed, Allāh does not allow to be lost the reward of those who do good.'", TextGerman = "Sie sagten: Bist du denn wirklich Yusuf? Er sagte: Ich bin Yusuf, und das ist mein Bruder. Allah hat uns eine Wohltat erwiesen. Gewiß, wer gottesfürchtig und standhaft ist -- gewiß, Allah läßt den Lohn der Gutes Tuenden nicht verlorenge hen." });
                        surah12.VerseList.Add(new Verse { Number = 91, TextBosnian = "Allaha nam', rekoše oni, 'Allah ti je nad nama prednost dao, mi smo, zaista, griješili!'", TextEnglish = "They said, 'By Allāh, certainly has Allāh preferred you over us, and indeed, we have been sinners.'", TextGerman = "Sie sagten: Bei Allah, Allah hat dich uns vorgezogen. Und wir haben wahrlich Verfehlungen begangen." });
                        surah12.VerseList.Add(new Verse { Number = 92, TextBosnian = "Danas vam neće biti ukora',reče, 'Allah će vam oprostiti, On je najmilostiviji!'", TextEnglish = "He said, 'No blame will there be upon you today. May Allāh forgive you; and He is the most merciful of the merciful.", TextGerman = "Er sagte: Keine Schelte soll heute über euch kommen. Allah vergibt euch, Er ist ja der Barmherzigste der Barmherzigen." });
                        surah12.VerseList.Add(new Verse { Number = 93, TextBosnian = "Ponesite ovu košulju moju i bacite je na lice moga oca; on će progledati; i svu porodicu svoju mi dovedite!'", TextEnglish = "Take this, my shirt, and cast it over the face of my father; he will become seeing. And bring me your family, all together.'", TextGerman = "Geht mit diesem meinem Hemd und legt es auf das Gesicht meines Vaters, so wird er sein Augenlicht (wieder)erlangen. Und bringt eure Angehörigen allesamt zu mir." });
                        surah12.VerseList.Add(new Verse { Number = 94, TextBosnian = "I kad karavan napusti Misir, otac njihov reče: 'Ja zbilja osjećam miris Jusufov, samo ne recite da sam omatuhio.'", TextEnglish = "And when the caravan departed [from Egypt], their father said,[616] 'Indeed, I find the smell of Joseph [and would say that he was alive] if you did not think me weakened in mind.'", TextGerman = "Als nun die Karawane aufgebrochen war, sagte ihr Vater: Wahrlich, ich nehme Yusufs Geruch war. Wenn ihr mich nur nicht bezichtigen würdet, Unsinn zu reden!" });
                        surah12.VerseList.Add(new Verse { Number = 95, TextBosnian = "Allaha nam', rekoše oni, 'doista si ti još uvijek u prijašnjoj zabludi.'", TextEnglish = "They said, 'By Allāh, indeed you are in your [same] old error.'", TextGerman = "Sie sagten: Bei Allah, du befindest dich fürwahr in deinem alten Irrtum." });
                        surah12.VerseList.Add(new Verse { Number = 96, TextBosnian = "A kad glasonoša s radosnom vijesti dođe, on stavi košulju na lice njegovo i on progleda. 'Zar vam ne rekoh', reče, 'da ja znam od Allaha ono što vi ne znate.'", TextEnglish = "And when the bearer of good tidings[617] arrived, he cast it over his face, and he returned [once again] seeing. He said, 'Did I not tell you that I know from Allāh that which you do not know?'", TextGerman = "Als nun der Frohbote kam, legte er es auf sein Gesicht, und da hatte er sein Augenlicht wiedererlangt. Er sagte: Habe ich euch nicht gesagt, daß ich von Allah her weiß, was ihr nicht wißt?" });
                        surah12.VerseList.Add(new Verse { Number = 97, TextBosnian = "O oče naš', rekoše oni, 'zamoli za oprost grijeha naših, mi smo, zaista, griješili.'", TextEnglish = "They said, 'O our father, ask for us forgiveness of our sins; indeed, we have been sinners.'", TextGerman = "Sie sagten: O unser Vater, bitte für uns um Vergebung unserer Sünden, denn wir haben gewiß Verfehlungen begangen." });
                        surah12.VerseList.Add(new Verse { Number = 98, TextBosnian = "Zamolit ću Gospodara svoga da vam oprosti', odgovori on, 'jer On je Oprostitelj grijeha, Milostivi.'", TextEnglish = "He said, 'I will ask forgiveness for you from my Lord. Indeed, it is He who is the Forgiving, the Merciful.'", TextGerman = "Er sagte: Ich werde meinen Herrn um Vergebung für euch bitten. Er ist ja der Allvergebende und Barmherzige." });
                        surah12.VerseList.Add(new Verse { Number = 99, TextBosnian = "I kad dođoše kod Jusufa, on privi roditelje svoje na grudi i reče: 'Nastanite se u Misiru, od svega, ako Allah da, sigurni!'", TextEnglish = "And when they entered upon Joseph, he took his parents to himself [i.e., embraced them] and said, 'Enter Egypt, Allāh willing, safe [and secure].'", TextGerman = "Als sie nun bei Yusuf eintraten, zog er seine Eltern an sich und sagte: Betretet Ägypten, wenn Allah will, in Sicherheit." });
                        surah12.VerseList.Add(new Verse { Number = 100, TextBosnian = "I on roditelje svoje postavi na prijesto i svi mu se nakloniše, pa on reče: 'O oče moj, ovo je ostvarenje moga sna nekadašnjeg. Gospodar moj ga je istinitim učinio. On je bio dobar prema meni kad me je iz tamnice izbavio i vas iz pustinje doveo, nakon što je šejtan između mene i braće moje bio razdor posijao. Gospodar moj je, zaista, dobrostiv onome kome On hoće, i On je, zaista, Sveznajući i Mudri!", TextEnglish = "And he raised his parents upon the throne, and they bowed to him in prostration.[618] And he said, 'O my father, this is the explanation of my vision of before. My Lord has made it reality. And He was certainly good to me when He took me out of prison and brought you [here] from bedouin life after Satan had induced [estrangement] between me and my brothers. Indeed, my Lord is Subtle[619] in what He wills. Indeed, it is He who is the Knowing, the Wise.", TextGerman = "Und er hob seine Eltern auf den Thron empor. Und sie fielen vor ihm ehrerbietig nieder. Er sagte: O mein lieber Vater, das ist die Deutung meines (Traum)gesichts von zuvor. Mein Herr hat es wahrgemacht. Und Er hat mir Gutes erwiesen, als Er mich aus dem Gefängnis herauskommen ließ und euch aus dem nomadischen Leben hierherbrachte, nachdem der Satan zwischen mir und meinen Brüdern (zu Zwietracht) aufgestachelt hatte. Gewiß, mein Herr ist feinfühlig (in der Durchführung dessen), was Er will. Er ist ja der Allwissende und Allweise." });
                        surah12.VerseList.Add(new Verse { Number = 101, TextBosnian = "Gospodaru moj, Ti si mi dao dio vlasti i naučio me tumačenju snova! O Ti Koji si nebesa i Zemlju bez prethodnog primjera stvorio, Ti si Zaštitnik moj i na dunjaluku i na ahiretu; daj da umrem kao musliman i pridruži me čestitima!'", TextEnglish = "My Lord, You have given me [something] of sovereignty and taught me of the interpretation of dreams. Creator of the heavens and earth, You are my protector in this world and the Hereafter. Cause me to die a Muslim and join me with the righteous.'", TextGerman = "Mein Herr, Du hast mir etwas von der Herrschaft gegeben und mich etwas von der Deutung der Sprüche gelehrt. (O Du) Erschaffer der Himmel und der Erde, Du bist mein Schutzherr im Diesseits und Jenseits. Berufe mich als (Dir) ergeben ab und nimm mich unter die Rechtschaffenen auf." });
                        surah12.VerseList.Add(new Verse { Number = 102, TextBosnian = "Eto, to su neke vijesti iz onoga što je čulima nedokučivo i koje tebi objavljujemo, a ti nisi bio s njima kada su se oni odlučili, i kada su spletke smislili.", TextEnglish = "That is from the news of the unseen which We reveal, [O Muḥammad], to you. And you were not with them when they put together their plan while they conspired.", TextGerman = "Dies gehört zu den Nachrichten vom Verborgenen, das Wir dir (als Offenbarung) eingeben. Denn du warst nicht bei ihnen, als sie sich einigten, indem sie Ränke schmiedeten." });
                        surah12.VerseList.Add(new Verse { Number = 103, TextBosnian = "A većina ljudi, ma koliko ti nastojao, nisu vjernici.", TextEnglish = "And most of the people, although you strive [for it], are not believers.", TextGerman = "Aber die meisten Menschen werden, auch wenn du noch so sehr (danach) trachtest, nicht gläubig sein." });
                        surah12.VerseList.Add(new Verse { Number = 104, TextBosnian = "Ti od njih ne tražiš nagradu za to, to je samo opomena svim svjetovima.", TextEnglish = "And you do not ask of them for it any payment. It is not except a reminder to the worlds.", TextGerman = "Und du verlangst von ihnen keinen Lohn dafür. Es ist nur eine Ermahnung für die Weltenbewohner." });
                        surah12.VerseList.Add(new Verse { Number = 105, TextBosnian = "A koliko ima znakova na nebesima i na Zemlji pored kojih prolaze, i tom prilikom se od njih okreću!", TextEnglish = "And how many a sign within the heavens and earth do they pass over while they, therefrom, are turning away.", TextGerman = "Wie viele Zeichen gibt es in den Himmeln und auf der Erde, an denen sie vorbeigehen, wobei sie sie unbeachtet lassen!" });
                        surah12.VerseList.Add(new Verse { Number = 106, TextBosnian = "Većina njih ne vjeruje u Allaha, osim tako što Mu druge pridružuju.", TextEnglish = "And most of them believe not in Allāh except while they associate others with Him.", TextGerman = "Und die meisten von ihnen glauben nicht an Allah, ohne (Ihm andere) beizugesellen." });
                        surah12.VerseList.Add(new Verse { Number = 107, TextBosnian = "Zar mogu biti sigurni da ih nevolja, kao Allahova kazna, neće stići ili da ih čas suđeni neće iznenaditi, a da oni to neće ni primijetiti?", TextEnglish = "Then do they feel secure that there will not come to them an overwhelming [aspect] of the punishment of Allāh or that the Hour will not come upon them suddenly while they do not perceive?", TextGerman = "Glauben sie denn, sicher zu sein davor, daß eine überdeckende Strafe von Allah über sie kommt oder daß plötzlich die Stunde über sie kommt, ohne daß sie merken?" });
                        surah12.VerseList.Add(new Verse { Number = 108, TextBosnian = "Reci: 'Ovo je put moj, ja pozivam k Allahu, imajući jasne dokaze, ja, i svaki onaj koji me slijedi, i neka je Uzvišen Allah, ja nisam od onih koji Mu druge pridružuju.'", TextEnglish = "Say, 'This is my way; I invite to Allāh with insight, I and those who follow me. And exalted is Allāh; and I am not of those who associate others with Him.'", TextGerman = "Sag: Das ist mein Weg: Ich rufe zu Allah aufgrund eines sichtbaren Hinweises, ich und diejenigen, die mir folgen. Preis sei Allah! Und ich gehöre nicht zu den Götzendienern." });
                        surah12.VerseList.Add(new Verse { Number = 109, TextBosnian = "A Mi smo i prije tebe samo muškarce slali, koji su iz naseljenih mjesta bili i kojima smo objave dostavljali. Zašto ovi ne putuju po svijetu, pa da vide kako su skončali oni prije njih- a ahiret je zaista bolji za one koji se uščuvaju - zar ne razumijete?'", TextEnglish = "And We sent not before you [as messengers] except men to whom We revealed from among the people of cities. So have they[620] not traveled through the earth and observed how was the end of those before them? And the home of the Hereafter is best for those who fear Allāh; then will you not reason?", TextGerman = "Und Wir haben vor dir nur Männer gesandt von den Bewohnern der Städte, denen Wir (Offenbarungen) eingaben. Sind sie denn nicht auf der Erde umhergereist, so daß sie schauen (konnten), wie das Ende derjenigen war, die vor ihnen waren? Die Wohnstätte des Jenseits ist wahrlich besser für diejenigen, die gottesfürchtig sind. Begreift ihr denn nicht?" });
                        surah12.VerseList.Add(new Verse { Number = 110, TextBosnian = "I kad bi poslanici gotovo nadu gubili i pomišljali da će ih za lašce proglasiti, pomoć Naša bi im došla. Spasili bismo one koje smo Mi htjeli, a kazna Naša ne bi mimoišla ljude prestupnike.", TextEnglish = "[They continued] until, when the messengers despaired and were certain that they had been denied, there came to them Our victory, and whoever We willed was saved. And Our punishment cannot be repelled from the people who are criminals.", TextGerman = "Erst dann, als die Gesandten die Hoffnung aufgegeben hatten und sie meinten, daß sie belogen worden seien, kam Unsere Hilfe zu ihnen. Und so wird errettet, wen Wir wollen. Aber vom übeltätigen Volk wird Unsere Gewalt nicht abgewandt." });
                        surah12.VerseList.Add(new Verse { Number = 111, TextBosnian = "U kazivanju o njima pouka je za one koji su razumom obdareni. Kur'an nije izmišljena besjeda, on priznaje knjige prije njega objavljene, i objašnjava sve, i uputa je i milost narodu koji vjeruje.", TextEnglish = "There was certainly in their stories a lesson for those of understanding. Never was it [i.e., the Qur’ān] a narration invented, but a confirmation of what was before it and a detailed explanation of all things and guidance and mercy for a people who believe.", TextGerman = "In ihren Geschichten ist wahrlich eine Lehre für diejenigen, die Verstand besitzen. Es ist keine Aussage, die ersonnen wird, sondern die Bestätigung dessen, was vor ihm war, und die ausführliche Darlegung aller Dinge und eine Rechtleitung und Barmherzigkeit für Leute, die glauben." });
                        result = surah12;
                        break;
                    #endregion

