                    #region 13 Ar-Rad
                    case 13:
                        Surah surah13 = new Surah(); surah13.Name = Constants.Surah13Name; surah13.Number = 13; surah13.VerseList = new List<Verse>();
                        surah13.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah13.VerseList.Add(new Verse { Number = 1, TextBosnian = "Elif-lam-mim-ra, Ovo su ajeti Knjige! A ono što ti se objavljuje od Gospodara tvoga istina je, ali većina ljudi ne vjeruje.", TextEnglish = "Alif, Lām, Meem, Rā.[622] These are the verses of the Book; and what has been revealed to you from your Lord is the truth, but most of the people do not believe.", TextGerman = "Alif-Lam-Mim-Ra. Dies sind die Zeichen des Buches. Was zu dir von deinem Herrn herabgesandt worden ist, ist die Wahrheit. Aber die meisten Menschen glauben nicht." });
                        surah13.VerseList.Add(new Verse { Number = 2, TextBosnian = "Allah je nebesa, vidite ih, bez stupova podigao, i onda se nad Aršom uzvisio, i Sunce i Mjesec potčinio; svako se kreće do roka određenog. On upravlja svim i potanko izlaže dokaze kako biste u susret s Gospodarom svojim čvrsto bili uvjereni.", TextEnglish = "It is Allāh who erected the heavens without pillars that you [can] see; then He established Himself above the Throne[623] and made subject[624] the sun and the moon, each running [its course] for a specified term. He arranges [each] matter; He details the signs that you may, of the meeting with your Lord, be certain.", TextGerman = "Allah ist es, Der die Himmel ohne Stützen, die ihr sehen könnt, emporgehoben und Sich hierauf über den Thron erhoben hat. Er hat die Sonne und den Mond dienstbar gemacht - jedes läuft auf eine festgesetzte Frist zu. Er regelt die Angelegenheit, er legt die Zeichen ausführlich dar, auf daß ihr von der Begegnung mit eurem Herrn überzeugt seiet." });
                        surah13.VerseList.Add(new Verse { Number = 3, TextBosnian = "I On je Taj Koji je Zemlju rasprostro i na njoj nepomične planine i rijeke stvorio i od svakog ploda po par, muško i žensko dao; On dan zastire noću. U tome, doista, ima dokaza za ljude koji razmišljaju.", TextEnglish = "And it is He who spread the earth and placed therein firmly set mountains and rivers; and from all of the fruits He made therein two mates; He causes the night to cover the day. Indeed in that are signs for a people who give thought.", TextGerman = "Und Er ist es, Der die Erde gedehnt und auf ihr festgegründete Berge und Flüsse gemacht hat. Und von allen Früchten hat Er auf ihr zwei, ein Paar, gemacht. Er läßt die Nacht den Tag überdecken. Darin sind wahrlich Zeichen für Leute, die nachdenken." });
                        surah13.VerseList.Add(new Verse { Number = 4, TextBosnian = "Na Zemlji ima predjela koji jedni s drugima graniče, i bašča s grožđem, i njiva, i palmi; sa više izdanaka i samo s jednim, i jednom te istom vodom se napajaju; ipak plod nekih činimo ukusnijim od drugih. U tome, doista, ima znakova za ljude koji razumiju.", TextEnglish = "And within the land are neighboring plots and gardens of grapevines and crops and palm trees, [growing] several from a root or otherwise,[625] watered with one water; but We make some of them exceed others in [quality of] fruit. Indeed in that are signs for a people who reason.", TextGerman = "Und auf der Erde sind nebeneinanderliegende Landstriche und Gärten mit Rebstöcken und (sonstige) Pflanzen und Palmen, mehrstämmig und einzelstämmig, die (alle) mit demselben Wasser bewässert werden. Wir zeichnen die einen von ihnen vor den anderen im Ernteertrag aus. Darin sind wahrlich Zei chen für Leute, die begreifen." });
                        surah13.VerseList.Add(new Verse { Number = 5, TextBosnian = "A ako se čudiš, pa - čudo su riječi njihove: 'Zar ćemo kad prah budemo, zaista ponovo biti stvoreni?' To su oni što ne vjeruju Gospodara svoga. To su oni na čijim vratima će biti sindžiri. To su oni što im Vatra pripada; u njoj će vječno ostati.", TextEnglish = "And if you are astonished,[626] [O Muḥammad] - then astonishing is their saying, 'When we are dust, will we indeed be [brought] into a new creation?' Those are the ones who have disbelieved in their Lord, and those will have shackles[627] upon their necks, and those are the companions of the Fire; they will abide therein eternally.", TextGerman = "Und wenn du dich (schon) wunderst, so sind ihre Worte wunderlich: Sollen wir etwa, wenn wir zu Erde geworden sind, denn wirklich in neuer Schöpfung (erstehen)? Das sind diejenigen, die ihren Herrn verleugnen; das sind diejenigen, die Fesseln um ihre Hälse haben; das sind die Insassen des (Höllen)feuers. Ewig werden sie darin bleiben." });
                        surah13.VerseList.Add(new Verse { Number = 6, TextBosnian = "Oni te požuruju sa zlom, prije nego s dobrim, a bilo je kazni i prije njih. Gospodar tvoj ljudima prašta i uprkos zulumu njihovom, ali Gospodar tvoj, doista, i kažnjava žestoko.", TextEnglish = "They impatiently urge you to bring about evil before good,[628] while there has already occurred before them similar punishments [to what they demand]. And indeed, your Lord is the possessor of forgiveness for the people despite their wrongdoing, and indeed, your Lord is severe in penalty.", TextGerman = "Sie wünschen von dir, ihnen das Schlechte vor dem Guten zu beschleunigen, obwohl doch vor ihnen beispielgebende Strafen ergangen sind. Dein Herr ist gegen die Menschen wahrlich voll der Vergebung, trotz ihres Unrechts. Aber dein Herr ist wahrlich (auch) streng im Bestrafen." });
                        surah13.VerseList.Add(new Verse { Number = 7, TextBosnian = "A oni koji ne vjeruju govore: 'Zašto mu Gospodar njegov ne pošalje kakav znak?' Ti si, međutim, samo upozoritelj, a svaki je narod imao onoga koji ga je na Pravi put usmjeravao.", TextEnglish = "And those who disbelieved say, 'Why has a sign not been sent down to him from his Lord?' You are only a warner, and for every people is a guide.", TextGerman = "Diejenigen, die ungläubig sind, sagen: Wenn doch ein Zeichen von seinem Herrn auf ihn herabgesandt würde! Du bist aber nur ein Überbringer von Warnungen. Und jedes Volk hat einen, der es rechtleitet." });
                        surah13.VerseList.Add(new Verse { Number = 8, TextBosnian = "Allah zna šta svako žensko nosi i šta materice kraće nose, a šta duže; u Njega sve ima mjeru.", TextEnglish = "Allāh knows what every female carries[629] and what the wombs lose [prematurely] or exceed.[630] And everything with Him is by due measure.", TextGerman = "Allah weiß, womit jedes weibliche Wesen schwanger ist und wie der Mutterleib abnimmt und wie er zunimmt. Und alles hat bei Ihm ein Maß." });
                        surah13.VerseList.Add(new Verse { Number = 9, TextBosnian = "On je Znalac i onog što je čulima nedokučivo i onog što je pojavno, On je Veličanstveni i Uzvišeni.", TextEnglish = "[He is] Knower of the unseen and the witnessed, the Grand, the Exalted.", TextGerman = "(Er ist) der Kenner des Verborgenen und des Offenbaren, der Große und hoch Erhabene." });
                        surah13.VerseList.Add(new Verse { Number = 10, TextBosnian = "Za Njega je jednak onaj od vas koji tiho govori i onaj koji to glasno čini, onaj koji se noću skriva i onaj koji po danu hodi.", TextEnglish = "It is the same [to Him] concerning you whether one conceals [his] speech or publicizes it and whether one is hidden by night or conspicuous [among others] by day.", TextGerman = "Es ist gleich, ob jemand von euch seine Worte geheimhält oder sie laut vernehmbar äußert und ob jemand sich bei Nacht verbirgt oder bei Tag offen hervortritt." });
                        surah13.VerseList.Add(new Verse { Number = 11, TextBosnian = "Uz svakog od vas su meleki, koji se smjenjuju ispred njega i iza njega - po Allahovu naređenju nad njim bdiju. Sigurno je da Allah neće izmijeniti stanje u kojem se nalazi određeni narod sve dok se taj narod ne promijeni. A kad Allah htjedne nekom narodu nešto što je loše, niko to ne može spriječiti; bez Njega im nema zaštitnika.", TextEnglish = "For him [i.e., each one] are successive [angels][631] before and behind him who protect him by the decree of Allāh.[632] Indeed, Allāh will not change the condition of a people until they change what is in themselves. And when Allāh intends for a people ill,[633] there is no repelling it. And there is not for them besides Him any patron.", TextGerman = "Er hat vor sich und hinter sich Begleiter, die ihn auf Allahs Befehl beschützen. Allah ändert nicht den Zustand eines Volkes, bis sie das ändern, was in ihnen selbst ist. Und wenn Allah einem Volk Böses will, so kann es nicht zurückgewiesen werden. Und sie haben außer Ihm keinen Schutzherrn." });
                        surah13.VerseList.Add(new Verse { Number = 12, TextBosnian = "On je Taj Koji vam pokazuje munju, da se uplašite i ponadate, i On daje da iznova nastaju teški oblaci.", TextEnglish = "It is He who shows you lightning, [causing] fear and aspiration, and generates the heavy clouds.", TextGerman = "Er ist es, Der euch den Blitz (als Grund) zur Angst und zum Begehren sehen und die schweren Wolken entstehen läßt." });
                        surah13.VerseList.Add(new Verse { Number = 13, TextBosnian = "I grmljavina veliča i hvali Njega, a i meleki, iz strahopoštovanja prema Njemu; On šalje gromove i njima pogađa koga hoće, a oni, i pored toga, raspravljaju o Allahu, a On žestoko kažnjava.", TextEnglish = "And the thunder exalts [Allāh] with praise of Him - and the angels [as well] from fear of Him - and He sends thunderbolts and strikes therewith whom He wills while they dispute about Allāh; and He is severe in assault.", TextGerman = "Und der Donner lobpreist Ihn und (desgleichen) die Engel aus Furcht vor Ihm. Und Er sendet die Donnerschläge und trifft damit, wen Er will. Dabei streiten sie über Allah, wo Er doch stark im Streiten ist." });
                        surah13.VerseList.Add(new Verse { Number = 14, TextBosnian = "Samo se Njemu može istinski moliti. A oni kojima se pored Njega mole - neće im se odazvati, kao što ni voda neće stići u usta onome koji prema njoj samo dlanove svoje pruži; dova nevjernika zasigurno je beskorisna.", TextEnglish = "To Him [alone] is the supplication of truth. And those they call upon besides Him do not respond to them with a thing, except as one who stretches his hands toward water [from afar, calling it] to reach his mouth, but it will not reach it [thus].[634] And the supplication of the disbelievers is not but in error [i.e., futility].", TextGerman = "Ihm gebührt die wahre Anrufung. Diejenigen, die sie außer Ihm anrufen, erhören sie in nichts. Sie sind nur wie einer, der seine Hände nach Wasser ausstreckt, damit es seinen Mund erreiche, aber es erreicht ihn nicht. Aber das Bittgebet der Ungläubigen geht nur ins Leere." });
                        surah13.VerseList.Add(new Verse { Number = 15, TextBosnian = "Allahu sedždu čini sve što je na nebesima i na Zemlji, svojom voljom ili pod moranje, a i sjene njihove - ujutro i na kraju dana.", TextEnglish = "And to Allāh prostrates whoever is within the heavens and the earth, willingly or by compulsion, and their shadows [as well] in the mornings and the afternoons.", TextGerman = "Und vor Allah wirft sich nieder, wer in den Himmeln und auf der Erde ist, ob freiwillig oder widerwillig, und (auch) ihre Schatten, am Morgen und am Abend." });
                        surah13.VerseList.Add(new Verse { Number = 16, TextBosnian = "Reci: 'Ko je Gospodar nebesa i Zemlje?' - i odgovori: 'Allah!' 'Reci: 'Pa zašto ste onda umjesto Njega kao zaštitnike prihvatili one koji sami sebi ne mogu neku korist pribaviti niti od sebe kakvu štetu otkloniti?' Reci: 'Zar su jednaki slijepac i onaj koji vidi, ili, zar su isto tmine i svjetlo, ili, možda misle da oni koje su Allahu u obožavanju pridruživali stvaraju kao što On stvara, pa im se stvaranje čini sličnim'!' Reci: 'Allah je Stvoritelj svega i On je, Jedini, Potčinitelj svega.'", TextEnglish = "Say, 'Who is Lord of the heavens and earth?' Say, 'Allāh.' Say, 'Have you then taken besides Him allies not possessing [even] for themselves any benefit or any harm?' Say, 'Is the blind equivalent to the seeing? Or is darkness equivalent to light? Or have they attributed to Allāh partners who created like His creation so that the creation [of each] seemed similar to them?'[635] Say, 'Allāh is the Creator of all things, and He is the One, the Prevailing.'[636]", TextGerman = "Sag: Wer ist der Herr der Himmel und der Erde? Sag: Allah. Sag: Nehmt ihr euch denn außer Ihm Schutzherren, die sich selbst weder Nutzen noch Schaden zu bringen vermögen? Sag: Sind (etwa) der Blinde und der Sehende gleich? Oder sind (etwa) die Finsternisse und das Licht gleich? Oder haben sie Allah (solche) Teilhaber gegeben, die eine Schöpfung wie die Seinige schufen, so daß ihnen die Schöpfung ähnlich erscheint? Sag: Allah ist der Schöpfer von allem, und Er ist der Eine, der Allbezwinger." });
                        surah13.VerseList.Add(new Verse { Number = 17, TextBosnian = "On vodu s neba spusti, pa svakom udolinom poteče onoliko koliko može da prihvati i bujica nosi pjenu koja narasta. I ono što ljudi tope na vatri u želji da dobiju nakit ili oruđe ima također otpad, sličan pjeni. Tako Allah navodi primjer za istinu i neistinu: pjena se raspršuje, dok ono što koristi ljudima ostaje na Zemlji. Tako, eto, Allah navodi primjere.", TextEnglish = "He sends down from the sky, rain, and valleys flow according to their capacity, and the torrent carries a rising foam. And from that [ore] which they heat in the fire, desiring adornments and utensils, is a foam like it. Thus Allāh presents [the example of] truth and falsehood. As for the foam, it vanishes, [being] cast off; but as for that which benefits the people, it remains on the earth. Thus does Allāh present examples.", TextGerman = "Er läßt vom Himmel Wasser herabkommen, und dann fließen Täler entsprechend ihrem Maß, daraufhin trägt die Flut aufschwellenden Schaum. Und aus dem, worüber man das Feuer anzündet, im Trachten (da)nach(,) Schmuck oder Gerät (anzufertigen, entsteht) ein ähnlicher Schaum. So prägt Allah (im Gleichnis) das Wahre und das Falsche. Was nun den Schaum angeht, so vergeht er nutzlos. Was aber den Menschen nützt, das bleibt in der Erde. So prägt Allah die Gleichnisse." });
                        surah13.VerseList.Add(new Verse { Number = 18, TextBosnian = "Onima koji se Gospodaru svome odazovu - nagrada najljepša pripada; a oni koji Mu se ne odazovu - kad bi sve što je na Zemlji njihovo bilo, i još toliko, rado bi se time otkupili. Njih čeka užas polaganja računa, prebivalište njihovo bit će Džehennem, a grozna li je on postelja!", TextEnglish = "For those who have responded to their Lord is the best [reward], but those who did not respond to Him - if they had all that is in the earth entirely and the like of it with it, they would [attempt to] ransom themselves thereby. Those will have the worst account, and their refuge is Hell, and wretched is the resting place.", TextGerman = "Für diejenigen, die auf ihren Herrn hören, ist das Beste. Diejenigen aber, die nicht auf Ihn hören, wenn sie alles hätten, was auf der Erde ist, und noch einmal das Gleiche dazu, würden sie sich sicherlich damit loskaufen. Für sie wird es eine böse Abrechnung geben, und ihr Zufluchtsort ist die Hölle - eine schlimme Lagerstatt!" });
                        surah13.VerseList.Add(new Verse { Number = 19, TextBosnian = "Zar je onaj koji zna da je istina ono što ti se objavljuje od Gospodara tvoga kao onaj koji je slijep?! A pouku primaju samo oni koji razum imaju,", TextEnglish = "Then is he who knows that what has been revealed to you from your Lord is the truth like one who is blind? They will only be reminded who are people of understanding -", TextGerman = "Ist etwa jemand, der weiß, daß das, was zu dir von deinem Herrn (als Offenbarung) herabgesandt worden ist, die Wahrheit ist, wie jemand, der blind ist? Jedoch bedenken nur diejenigen, die Verstand besitzen." });
                        surah13.VerseList.Add(new Verse { Number = 20, TextBosnian = "oni koji obavezu prema Allahu ispunjavaju i ne krše obećanje;", TextEnglish = "Those who fulfill the covenant of Allāh and do not break the contract,", TextGerman = "Diejenigen, die Allahs Bund halten und das Abkommen nicht brechen" });
                        surah13.VerseList.Add(new Verse { Number = 21, TextBosnian = "i oni koji održavaju ono što je Allah naredio da se održava i Gospodara svoga se boje i užasa obračuna se plaše.", TextEnglish = "And those who join that which Allāh has ordered to be joined[637] and fear their Lord and are afraid of the evil of [their] account,", TextGerman = "und die verbinden, was Allah befohlen hat zu verbinden, ihren Herrn fürchten und Angst vor einer bösen Abrechnung haben" });
                        surah13.VerseList.Add(new Verse { Number = 22, TextBosnian = "I oni koji trpe u želji za licem Gospodara svoga, i oni koji namaz obavljaju, i koji od onoga što im dajemo i tajno i javno udjeljuju, i koji dobrim na zlo uzvraćaju - njima će lijep ishod dunjaluka pripasti, njih čeka najljepše prebivalište.", TextEnglish = "And those who are patient, seeking the face [i.e., acceptance] of their Lord, and establish prayer and spend from what We have provided for them secretly and publicly and prevent evil with good - those will have the good consequence of [this] home[638] -", TextGerman = "und die geduldig sind im Trachten nach dem Angesicht ihres Herrn, das Gebet verrichten und von dem, womit Wir sie versorgt haben, heimlich und öffentlich ausgeben und mit dem Guten das Böse abwehren, für sie gibt es letztendlich die Wohnstätte," });
                        surah13.VerseList.Add(new Verse { Number = 23, TextBosnian = "Vrtovi Adna u koje će ući i oni i roditelji njihovi i žene njihove i porod njihov - oni koji su bili čestiti - meleci će im ulaziti na vrata svaka.", TextEnglish = "Gardens of perpetual residence; they will enter them with whoever were righteous among their forefathers, their spouses and their descendants. And the angels will enter upon them from every gate, [saying],", TextGerman = "die Gärten Edens, in die sie eingehen werden, (sie) und diejenigen, die rechtschaffen waren von ihren Vätern, ihren Gattinnen und ihren Nachkommenschaften. Und die Engel treten zu ihnen ein durch alle Tore:" });
                        surah13.VerseList.Add(new Verse { Number = 24, TextBosnian = "Selam neka je vama, zato što ste trpjeli, a divan li je ishod nakon dunjaluka!", TextEnglish = "Peace [i.e., security] be upon you for what you patiently endured. And excellent is the final home.'", TextGerman = "Friede sei auf euch dafür, daß ihr geduldig wart! Wie trefflich ist die endgültige Wohnstätte!" });
                        surah13.VerseList.Add(new Verse { Number = 25, TextBosnian = "A oni koji krše obećanje prema Allahu, nakon što su se na to čvrsto obavezali, i kidaju ono što je Allah naredio da se održava, i čine nered na Zemlji - njih čekaju prokletstvo i užas boravišta.", TextEnglish = "But those who break the covenant of Allāh after contracting it and sever that which Allāh has ordered to be joined and spread corruption on earth - for them is the curse, and they will have the worst home.[639]", TextGerman = "Diejenigen, die den Bund Allahs nach seiner Abmachung brechen und das trennen, was Allah befohlen hat, daß (es) verbunden werden soll, und auf der Erde Unheil stiften, für sie gibt es den Fluch und eine schlimme Wohnstätte." });
                        surah13.VerseList.Add(new Verse { Number = 26, TextBosnian = "Kome hoće, Allah daje opskrbu u obilju, i uskraćuje. Oni se raduju životu na ovom svijetu, a život na ovom svijetu prema ahiretu samo je prolazno uživanje.", TextEnglish = "Allāh extends provision for whom He wills and restricts [it]. And they rejoice in the worldly life, while the worldly life is not, compared to the Hereafter, except [brief] enjoyment.", TextGerman = "Allah gewährt die Versorgung großzügig, wem Er will, und bemißt auch. Und sie sind froh über das diesseitige Leben; aber das diesseitige Leben (zählt) im Jenseits nur als (vergänglicher) Nießbrauch." });
                        surah13.VerseList.Add(new Verse { Number = 27, TextBosnian = "Oni koji ne vjeruju govore: 'Zašto mu od Gospodara njegova nije spušten kakav znak?' Reci: 'Allah odvodi na stranputicu onoga koga On hoće, a k Sebi upućuje onoga ko Mu se pokajnički obraća.'", TextEnglish = "And those who disbelieved say, 'Why has a sign not been sent down to him from his Lord?' Say, [O Muḥammad], 'Indeed, Allāh leaves astray whom He wills and guides to Himself whoever turns back [to Him] -", TextGerman = "Und diejenigen, die ungläubig sind, sagen: Wenn doch ein Zeichen von seinem Herrn auf ihn herabgesandt würde! Sag: Allah läßt in die Irre gehen, wen Er will, und leitet zu Sich, wer sich Ihm reuig zuwendet." });
                        surah13.VerseList.Add(new Verse { Number = 28, TextBosnian = "One koji vjeruju i čija se srca, kad se Allah spomene, smiruju - a srca se, doista, kad se Allah spomene, smiruju.", TextEnglish = "Those who have believed and whose hearts are assured by the remembrance of Allāh. Unquestionably, by the remembrance of Allāh hearts are assured.'", TextGerman = "(Es sind) diejenigen, die glauben und deren Herzen im Gedenken Allahs Ruhe finden. Sicherlich, im Gedenken Allahs finden die Herzen Ruhe!" });
                        surah13.VerseList.Add(new Verse { Number = 29, TextBosnian = "Onima koji vjeruju i čine dobra djela - blago njima, njih čeka divno prebivalište.", TextEnglish = "Those who have believed and done righteous deeds - a good state is theirs and a good return.", TextGerman = "Diejenigen, die glauben und rechtschaffene Werke tun - für sie wird es Tuba geben, und für sie wird eine schöne Heimstatt (da)sein." });
                        surah13.VerseList.Add(new Verse { Number = 30, TextBosnian = "I tako smo te poslali narodu prije kojeg su bili i nestali drugi narodi, da im kazuješ ono što ti objavljujemo, a oni u Svemilosnog ne vjeruju. Reci: 'On je Gospodar moj, nema boga osim Njega, u Njega se uzdam i Njemu se obraćam.'", TextEnglish = "Thus have We sent you to a community before which [other] communities have passed on so you might recite to them that which We revealed to you, while they disbelieve in the Most Merciful. Say, 'He is my Lord; there is no deity except Him. Upon Him I rely, and to Him is my return.'", TextGerman = "So haben Wir dich in eine Gemeinschaft gesandt, vor der Gemeinschaften dahingegangen sind, damit du ihnen verliest, was Wir dir (als Offenbarung) eingegeben haben; und doch verleugnen sie den Allerbarmer. Sag: Er ist mein Herr. Es gibt keinen Gott außer Ihm. Auf Ihn verlasse ich mich, und zu Ihm bereue ich." });
                        surah13.VerseList.Add(new Verse { Number = 31, TextBosnian = "Kad bi se kakvom knjigom brda pokrenula ili zemlja iskomadala ili mrtvi dozvali, Kur'an bi to bio! Štaviše, Allahu pripada sve! A zar nemaju nade i ne znaju oni koji vjeruju da bi Allah, kad bi samo htio, sve ljude uputio. A nevjernike će nesreća neprestano pogađati ili će u blizini mjesta njihova doći, zbog onoga što rade-sve dok se Allahovo obećanje ne ostvari. Allah, zaista, ne krši obećanje.", TextEnglish = "And if there was any Qur’ān [i.e., recitation] by which the mountains would be removed or the earth would be broken apart or the dead would be made to speak,[640] [it would be this Qur’ān], but to Allāh belongs the affair entirely. Then have those who believed not accepted that had Allāh willed, He would have guided the people, all of them? And those who disbelieve do not cease to be struck, for what they have done, by calamity - or it will descend near their home - until there comes the promise of Allāh. Indeed, Allāh does not fail in [His] promise.", TextGerman = "Wenn es auch einen Qur’an gäbe, mit dem die Berge versetzt oder die Erde in Stücke gerissen oder zu den Toten gesprochen werden könnte ... (würden sie doch nicht glauben). Nein! Vielmehr steht die Angelegenheit ganz bei Allah. Wissen denn nicht diejenigen, die glauben, daß wenn Allah wollte, Er die Menschen wahrlich alle rechtleiten würde? Diejenigen, die ungläubig sind, wird immer wieder ein Verhängnis treffen für das, was sie gemacht haben, oder es wird in der Nähe ihrer Wohnstätten geschehen, bis Allahs Versprechen eintrifft. Gewiß, Allah bricht nicht, was Er versprochen hat." });
                        surah13.VerseList.Add(new Verse { Number = 32, TextBosnian = "I prije tebe su poslanici ismijavani bili, pa sam Ja onima koji nisu vjerovali vremena ostavljao, i poslije sam ih kažnjavao, a kakva je samo bila kazna Moja!", TextEnglish = "And already were [other] messengers ridiculed before you, and I extended the time of those who disbelieved; then I seized them, and how [terrible] was My penalty.", TextGerman = "Man machte sich ja bereits über Gesandte vor dir lustig. Da gewährte Ich denen, die ungläubig waren, Aufschub; hierauf ergriff Ich sie. Wie war da Meine Bestrafung!" });
                        surah13.VerseList.Add(new Verse { Number = 33, TextBosnian = "Pa zar je Onaj Koji nad svakim bdi, što god da uradi, kao onaj ko to nije u stanju uraditi?! A Allahu su pridružili ortake. Reci: 'Imenujte ih!' Zar da Ga vi obavještavate o nečemu što se na Zemlji nalazi - što on zna da postoji, ili ih vi po svome nahođenju tako nazivate?! Ustvari, predstavljeno je lijepim onima koji ne vjeruju spletkarenje njihovo, i oni su od Pravog puta odvraćeni. A onaj koga Allah na stranputicu okrene neće imati nikoga da ga na Pravi put uputi.", TextEnglish = "Then is He who is a maintainer of every soul, [knowing] what it has earned, [like any other]? But to Allāh they have attributed partners. Say, 'Name them. Or do you inform Him of that[641] which He knows not upon the earth or of what is apparent [i.e., alleged] of speech?'[642] Rather, their [own] plan has been made attractive to those who disbelieve, and they have been averted from the way. And whomever Allāh sends astray - there will be for him no guide.", TextGerman = "Ist denn Derjenige, Der über jeder Seele steht, (um ihr zu vergelten) für das, was sie verdient hat, (den Götzen gleich)? Und dennoch geben sie Allah Teilhaber. Sag: Nennt sie. Oder wollt ihr Ihm etwas kundtun, das Er auf der Erde nicht kennt, oder über offenkundige Worte? Nein! Vielmehr sind denjenigen, die ungläubig sind, ihre Ränke ausgeschmückt worden, und sie sind vom (rechten) Weg abgehalten worden. Wen Allah in die Irre gehen läßt, der hat niemanden, der ihn rechtleitet." });
                        surah13.VerseList.Add(new Verse { Number = 34, TextBosnian = "Oni će u životu na dunjaluku patiti, ali će patnja na ahiretu sigurno teža biti; od Allaha ih niko neće odbraniti.", TextEnglish = "For them will be punishment in the life of [this] world, and the punishment of the Hereafter is more severe. And they will not have from Allāh any protector.", TextGerman = "Für sie gibt es Strafe im diesseitigen Leben; aber die Strafe des Jenseits ist wahrlich härter. Und sie werden vor Allah keinen Behüter haben." });
                        surah13.VerseList.Add(new Verse { Number = 35, TextBosnian = "Ovakav je Džennet obećan onima koji se budu Allaha bojali: vrtovi s rijekama, kroz njega rijeke teku, njegovi plodovi i hladovina trajni su; to će, na kraju, slijediti onima koji se Allaha boje i grijeha klone, a ono što nevjernicima na kraju slijedi Vatra će biti.", TextEnglish = "The example [i.e., description] of Paradise, which the righteous have been promised, is [that] beneath it rivers flow. Its fruit is lasting, and its shade. That is the consequence for the righteous, and the consequence for the disbelievers is the Fire.", TextGerman = "(Dies ist) das Gleichnis des (Paradies)gartens, der den Gottesfürchtigen versprochen ist: Er ist durcheilt von Bächen; sein Ernteertrag ist immerwährend und (auch) sein Schatten. Das ist die letztendliche (Taten)folge derjenigen, die gottesfürchtig sind, während das endgültige Ziel der Ungläubigen das (Höllen)feuer ist." });
                        surah13.VerseList.Add(new Verse { Number = 36, TextBosnian = "Oni kojima smo dali Knjigu raduju se svemu što objavljujemo tebi, ali u urotničkim grupama ima onih koji poriču nešto od toga. Reci: 'Meni je naređeno da samo Allahu ibadet činim i da Njemu nikoga u obožavanju ne pridružujem; Njemu ja pozivam i Njemu se vraćam.'", TextEnglish = "And [the believers among] those to whom We have given the [previous] Scripture rejoice at what has been revealed to you, [O Muḥammad], but among the [opposing] factions are those who deny part of it [i.e., the Qur’ān]. Say, 'I have only been commanded to worship Allāh and not associate [anything] with Him. To Him I invite, and to Him is my return.'", TextGerman = "Diejenigen, denen Wir die Schrift gegeben haben, sind froh über das, was (als Offenbarung) zu dir herabgesandt worden ist. Unter den Gruppierungen gibt es manche, die einen Teil davon nicht anerkennen. Sag: Mir ist befohlen worden, Allah zu dienen und Ihm nicht(s) beizugesellen. Zu Ihm rufe ich, und zu Ihm ist meine Heimkehr." });
                        surah13.VerseList.Add(new Verse { Number = 37, TextBosnian = "I Mi ga tako objavljujemo usavršenog na arapskom jeziku. A ako bi se ti za ćudima njihovim poveo, nakon što ti je znanje došlo, ti ne bi imao nikoga ko bi te od Allaha zaštitio ni odbranio.", TextEnglish = "And thus We have revealed it as an Arabic legislation.[643] And if you should follow their inclinations after what has come to you of knowledge, you would not have against Allāh any ally or any protector.", TextGerman = "Und so haben Wir ihn als ein Urteil auf arabisch hinabgesandt. Wenn du ihren Neigungen folgst nach dem, was dir an Wissen zugekommen ist, wirst du vor Allah weder Schutzherrn noch Behüter haben." });
                        surah13.VerseList.Add(new Verse { Number = 38, TextBosnian = "I prije tebe smo poslanike slali, i supruge i potomke im davali. I nijedan poslanik nije mogao s nekim znakom istupiti, osim s Allahovom dozvolom. Svako doba imalo je Knjigu.", TextEnglish = "And We have already sent messengers before you and assigned to them wives and descendants. And it was not for a messenger to come with a sign except by permission of Allāh. For every term is a decree.", TextGerman = "Und Wir haben ja bereits Gesandte vor dir gesandt und ihnen Gattinnen und Nachkommenschaft gegeben. Kein Gesandter kann ein Zeichen bringen außer mit Allahs Erlaubnis. Jede Frist hat eine Vorbestimmung." });
                        surah13.VerseList.Add(new Verse { Number = 39, TextBosnian = "Allah uklanja šta hoće, a ostavlja šta hoće; u Njega je glavna Knjiga.", TextEnglish = "Allāh eliminates what He wills or confirms, and with Him is the Mother of the Book.[644]", TextGerman = "Allah löscht aus, was Er will, und läßt bestehen; und bei Ihm ist der Kern des Buches." });
                        surah13.VerseList.Add(new Verse { Number = 40, TextBosnian = "Bilo da ti pokažemo dio onoga što im prijeteći obećavamo, bilo da ti život oduzmemo, tvoje je da obznanjuješ, a Naše da račune svodimo.", TextEnglish = "And whether We show you part of what We promise them or take you in death, upon you is only the [duty of] notification, and upon Us is the account.", TextGerman = "Ob Wir dich nun einen Teil dessen, was Wir ihnen androhen, sehen lassen oder dich nun (zuvor) abberufen, so obliegt dir nur die Übermittlung (der Botschaft), und Uns obliegt die Abrechnung." });
                        surah13.VerseList.Add(new Verse { Number = 41, TextBosnian = "Zar oni ne vide da Mi Zemlju sužavamo umanjujući krajeve njene?! A Allah sudi i niko ne može presudu Njegovu pobiti, i On brzo račun svodi.", TextEnglish = "Have they not seen that We set upon the land, reducing it from its borders?[645] And Allāh decides; there is no adjuster of His decision. And He is swift in account.", TextGerman = "Sehen sie denn nicht, daß Wir über das Land kommen und es an seinen Enden kürzen? Allah (allein) richtet, und es gibt niemanden, der letztendlich Sein Urteil rückgängig machen kann. Und Er ist schnell im Abrechnen." });
                        surah13.VerseList.Add(new Verse { Number = 42, TextBosnian = "Lukavstva su smišljali i oni prije njih, ali svako lukavstvo pripada Allahu, i On zna šta svako zaslužuje. A nevjernici će sigurno saznati kome će lijep ishod na kraju pripasti.", TextEnglish = "And those before them had plotted, but to Allāh belongs the plan entirely. He knows what every soul earns, and the disbelievers will know for whom is the final home.", TextGerman = "Ränke haben bereits diejenigen vor ihnen geschmiedet. Aber alles an Ränken ist Allahs. Er weiß, was jede Seele erwirbt. Und die Ungläubigen werden zu wissen bekommen, für wen die letztendliche Wohnstätte sein wird." });
                        surah13.VerseList.Add(new Verse { Number = 43, TextBosnian = "Oni koji ne vjeruju govore: 'Ti nisi poslanik.' Reci: 'Meni i vama dovoljan će kao svjedok biti Allah, i oni koji znanje iz Knjige imaju.'", TextEnglish = "And those who have disbelieved say, 'You are not a messenger.' Say, [O Muḥammad], 'Sufficient is Allāh as Witness between me and you, and [the witness of] whoever has knowledge of the Scripture.'[646]", TextGerman = "Diejenigen, die ungläubig sind, sagen: Du bist nicht gesandt. Sag: Allah genügt als Zeuge zwischen mir und euch und derjenige, der das Wissen der Schrift hat." });
                        result = surah13;
                        break;
                    #endregion

                    // 
                    #region 14 Ibrahim
                    case 14:
                        Surah surah14 = new Surah(); surah14.Name = Constants.Surah14Name; surah14.Number = 14; surah14.VerseList = new List<Verse>();
                        surah14.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah14.VerseList.Add(new Verse { Number = 1, TextBosnian = "Elif-lam-ra, Ovo je knjiga koju ti objavljujemo da ljude dozvolom njihova Gospodara izvedeš iz tmina na svjetlo, na put Silnoga i Hvaljenoga", TextEnglish = "Alif, Lām, Rā.[648] [This is] a Book which We have revealed to you, [O Muḥammad], that you might bring mankind out of darknesses into the light by permission of their Lord - to the path of the Exalted in Might, the Praiseworthy -", TextGerman = "Alif-Lam-Ra. Dies ist ein Buch, das Wir zu dir hinabgesandt haben, damit du die Menschen mit der Erlaubnis ihres Herrn aus den Finsternissen hinaus ins Licht bringst, auf den Weg des Allmächtigen und Lobenswürdigen," });
                        surah14.VerseList.Add(new Verse { Number = 2, TextBosnian = "Allaha, čije je ono što je na nebesima i ono što je na Zemlji. A teško nevjernicima od patnje žestoke,", TextEnglish = "Allāh, to whom belongs whatever is in the heavens and whatever is on the earth. And woe [i.e., destruction] to the disbelievers from a severe punishment -", TextGerman = "(den Weg) Allahs, Dessen ist, was in den Himmeln und was auf der Erde. Und wehe den Ungläubigen vor einer strengen Strafe!" });
                        surah14.VerseList.Add(new Verse { Number = 3, TextBosnian = "onima koji život na dunjaluku više vole od ahireta i koji od Allahova puta odvraćaju i nastoje ga krivim prikazati! Oni su daleko zalutali.", TextEnglish = "The ones who prefer the worldly life over the Hereafter and avert [people] from the way of Allāh, seeking to make it [seem] deviant. Those are in extreme error.", TextGerman = "Diejenigen, die das diesseitige Leben mehr lieben als das Jenseits und von Allahs Weg abhalten und danach trachten, ihn krumm zu machen; sie befinden sich in weit(reichend)em Irrtum." });
                        surah14.VerseList.Add(new Verse { Number = 4, TextBosnian = "Mi nismo poslali nijednog poslanika koji nije govorio jezikom naroda svoga, da bi mu objasnio. A Allah u zabludu okreće onoga koga hoće i upućuje na Pravi put onoga koga hoće; On je Silni i Mudri.", TextEnglish = "And We did not send any messenger except [speaking] in the language of his people to state clearly for them, and Allāh sends astray [thereby] whom He wills[649] and guides whom He wills. And He is the Exalted in Might, the Wise.", TextGerman = "Und Wir haben keinen Gesandten gesandt, außer in der Sprache seines Volkes, damit er ihnen (die Botschaft) klar macht. Allah läßt dann in die Irre gehen, wen Er will, und leitet recht, wen Er will. Und Er ist der Allmächtige und Allweise." });
                        surah14.VerseList.Add(new Verse { Number = 5, TextBosnian = "I Musaa smo poslali s dokazima Našim: 'Izvedi narod svoj iz tmina na svjetlo i podsjeti ga na Allahove blagodati.' To su, uistinu, znakovi za svakog ko je strpljiv i zahvalan.", TextEnglish = "And We certainly sent Moses with Our signs, [saying], 'Bring out your people from darknesses into the light and remind them of the days[650] of Allāh.' Indeed in that are signs for everyone patient and grateful.", TextGerman = "Und Wir sandten ja bereits Musa mit Unseren Zeichen: Bringe dein Volk aus den Finsternissen hinaus ins Licht, und erinnere sie an die Tage Allahs. Darin sind wahrlich Zeichen für jeden sehr Standhaften und sehr Dankbaren." });
                        surah14.VerseList.Add(new Verse { Number = 6, TextBosnian = "I kad Musa reče narodu svom: 'Sjetite se Allahove blagodati kad vas je izbavio od faraonovih ljudi koji su vas najgorim mukama mučili, koji su vam mušku djecu klali, a žensku u životu ostavljali - to je bilo veliko iskušenje od Gospodara vašega.'", TextEnglish = "And [recall, O Children of Israel], when Moses said to his people, 'Remember the favor of Allāh upon you when He saved you from the people of Pharaoh, who were afflicting you with the worst torment and were slaughtering your [newborn] sons and keeping your females alive. And in that was a great trial from your Lord.", TextGerman = "Und als Musa zu seinem Volk sagte: Gedenkt der Gunst Allahs an euch, als Er euch vor den Leuten Fir’auns rettete, die euch eine böse Qual auferlegten, eure Söhne abschlachteten und (nur) eure Frauen am Leben ließen. Seht, darin war für euch eine gewaltige Prüfung von eurem Herrn." });
                        surah14.VerseList.Add(new Verse { Number = 7, TextBosnian = "I kad je Gospodar vaš objavio: 'Ako budete zahvalni, Ja ću vam sigurno još više dati; budete li nezahvalni, žestoka će, doista, biti kazna Moja.'", TextEnglish = "And [remember] when your Lord proclaimed, 'If you are grateful, I will surely increase you [in favor]; but if you deny, indeed, My punishment is severe.''", TextGerman = "Und als euer Herr ankündigte: ,Wenn ihr dankbar seid, werde Ich euch ganz gewiß noch mehr (Gunst) erweisen. Wenn ihr jedoch undankbar seid, dann ist meine Strafe fürwahr streng." });
                        surah14.VerseList.Add(new Verse { Number = 8, TextBosnian = "I Musa još reče: 'Ako budete nezahvalni i vi i svi drugi na Zemlji, pa, Allah je, doista, neovisan i hvaljen.'", TextEnglish = "And Moses said, 'If you should disbelieve, you and whoever is on the earth entirely - indeed, Allāh is Free of need and Praiseworthy.'", TextGerman = "Und Musa sagte: Wenn ihr undankbar seid, ihr und alle, die auf der Erde sind, so ist Allah wahrlich Unbedürftig und Lobenswürdig." });
                        surah14.VerseList.Add(new Verse { Number = 9, TextBosnian = "Zar do vas nije doprla vijest o onima prije vas, o narodu Nuhovu, i o Adu, i o Semudu, i o onima poslije njih? Samo ih Allah zna! Poslanici su im njihovi jasne dokaze donosili, ali oni su ruke svoje na usta stavljali i govorili: 'Mi ne vjerujemo u ono što se po vama šalje i mi itekako sumnjamo u ono u što nas pozivate.'", TextEnglish = "Has there not reached you the news of those before you - the people of Noah and ʿAad and Thamūd and those after them? No one knows them [i.e., their number] but Allāh. Their messengers brought them clear proofs, but they returned their hands to their mouths[651] and said, 'Indeed, we disbelieve in that with which you have been sent, and indeed we are, about that to which you invite us, in disquieting doubt.'", TextGerman = "Ist zu euch nicht die Kunde von denjenigen vor euch gekommen, des Volkes Nuhs, der ’Ad und der Tamud und derjenigen nach ihnen? Nur Allah kennt sie. Ihre Gesandten kamen zu ihnen mit den klaren Beweisen; sie aber steckten (vor Grimm) ihre Hände in den Mund und sagten: Wir verleugnen das, womit ihr gesandt seid, und wir sind über das, wozu ihr uns aufruft, fürwahr in einem starken Zweifel." });
                        surah14.VerseList.Add(new Verse { Number = 10, TextBosnian = "Poslanici su njihovi govorili: 'Zar se može sumnjati u Allaha, Koji je nebesa i Zemlju bez prethodnog primjera-iz ničega stvorio? On vas poziva da bi vam neke grijehe oprostio i da bi vas do roka određenog ostavio.' Oni su odgovorili: 'Vi ste ljudi kao i mi; hoćete da nas odvratite od onih koje su naši preci obožavali - pa, donesite nam dokaz očevidni.'", TextEnglish = "Their messengers said, 'Can there be doubt about Allāh, Creator of the heavens and earth? He invites you that He may forgive you of your sins, and He delays you [i.e., your death] for a specified term.' They said, 'You are not but men like us who wish to avert us from what our fathers were worshipping. So bring us a clear authority [i.e., evidence].'", TextGerman = "Ihre Gesandten sagten: Gibt es denn einen Zweifel über Allah, den Erschaffer der Himmel und der Erde, Der euch ruft, um euch (etwas) von euren Sünden zu vergeben und euch auf eine festgesetzte Frist zurückzustellen? Sie sagten: Ihr seid nur menschliche Wesen wie wir. Ihr wollt uns von dem abhalten, dem unsere Väter dienten. So bringt uns eine deutliche Ermächtigung." });
                        surah14.VerseList.Add(new Verse { Number = 11, TextBosnian = "Mi jesmo ljudi kao i vi', govorili su im poslanici njihovi, 'ali, Allah dariva samo one robove Svoje koje On hoće; mi vam ne možemo donijeti dokaz osim s dozvolom Allahovom - a vjernici neka se samo u Allaha uzdaju.'", TextEnglish = "Their messengers said to them, 'We are only men like you, but Allāh confers favor upon whom He wills of His servants. It has never been for us to bring you evidence except by permission of Allāh. And upon Allāh let the believers rely.", TextGerman = "Ihre Gesandten sagten zu ihnen: Wir sind (zwar) nur menschliche Wesen wie ihr, aber Allah erweist Wohltaten, wem von Seinen Dienern Er will. Es steht uns nicht zu, eine Ermächtigung zu bringen - außer mit der Erlaubnis Allahs. Und auf Allah sollen sich die Gläubigen verlassen." });
                        surah14.VerseList.Add(new Verse { Number = 12, TextBosnian = "Zašto da se ne uzdamo u Allaha kad nas je On putevima kojim idemo uputio? Mi ćemo, doista, strpljivo podnositi ono čime nas uznemiravate - a oni koji se uzdaju, neka se samo u Allaha uzdaju!'", TextEnglish = "And why should we not rely upon Allāh while He has guided us to our [good] ways. And we will surely be patient against whatever harm you should cause us. And upon Allāh let those who would rely [indeed] rely.'", TextGerman = "Warum sollten wir uns nicht auf Allah verlassen, wo Er uns doch unsere Wege geleitet hat? Wir werden das, was ihr uns an Leid zufügt, ganz gewiß geduldig ertragen, und auf Allah sollen sich diejenigen verlassen, die sich (überhaupt auf jemanden) verlassen (wollen)." });
                        surah14.VerseList.Add(new Verse { Number = 13, TextBosnian = "Oni koji nisu vjerovali govorili su poslanicima svojim: 'Ili ćete se u vjeru našu vratiti ili ćemo vas, doista, iz zemlje naše protjerati!' A poslanicima je Gospodar njihov objavljivao: 'Mi ćemo zulumćare sigurno uništiti.", TextEnglish = "And those who disbelieved said to their messengers, 'We will surely drive you out of our land, or you must return to our religion.' So their Lord inspired to them, 'We will surely destroy the wrongdoers.", TextGerman = "Diejenigen, die ungläubig waren, sagten zu ihren Gesandten: Wir werden euch ganz gewiß aus unserem Land vertreiben, oder aber ihr kehrt zu unserem Glaubensbekenntnis zurück! Da gab ihnen ihr Herr (als Offenbarung) ein: Ganz gewiß werden Wir die Ungerechten vernichten." });
                        surah14.VerseList.Add(new Verse { Number = 14, TextBosnian = "I poslije njih vas na Zemlji nastaniti. Bit će to za one koji će se polaganja računa preda Mnom bojati i koji će od prijetnje Moje strahovati.'", TextEnglish = "And We will surely cause you to dwell in the land after them. That is for he who fears My position[652] and fears My threat.'", TextGerman = "Und Wir werden euch ganz gewiß nach ihnen das Land bewohnen lassen. Dies (gilt) für denjenigen, der Meinen Stand fürchtet und Meine Androhung fürchtet." });
                        surah14.VerseList.Add(new Verse { Number = 15, TextBosnian = "I poslanici su pomoć tražili, pa je svaki oholi inadžija nastradao.", TextEnglish = "And they requested decision [i.e., victory from Allāh], and disappointed, [therefore], was every obstinate tyrant.", TextGerman = "Und sie riefen (Allah) um Sieg (über die Ungläubigen) an. Und enttäuscht wurde jeder widerspenstige Gewalthaber." });
                        surah14.VerseList.Add(new Verse { Number = 16, TextBosnian = "Pred njim će Džehennem biti - i on će biti napajan tekućinom od sukrvice.", TextEnglish = "Before him[653] is Hell, and he will be given a drink of purulent water.[654]", TextGerman = "Hinterdrein kommt für ihn die Hölle, und er bekommt eitriges Wasser zu trinken," });
                        surah14.VerseList.Add(new Verse { Number = 17, TextBosnian = "Mučit će se daje proguta, ali je nikako neće moći proždrijeti i smrt će mu sa svih strana prilaziti, ali on neće umrijeti; njega poslije toga tek čeka patnja teška.", TextEnglish = "He will gulp it but will hardly [be able to] swallow it. And death will come to him from everywhere, but he is not to die. And before him is a massive punishment.", TextGerman = "das er schluckt, aber beinahe nicht hinunterbringt. Und der Tod kommt zu ihm von überall her, doch kann er nicht sterben. Und hinterdrein kommt harte Strafe." });
                        surah14.VerseList.Add(new Verse { Number = 18, TextBosnian = "Djela onih koji ne vjeruju u Gospodara svoga nalik su na pepeo koji vihor u olujnom danu raznese; neće moći očekivati nikakvu nagradu za djela koja su učinili, to je stranputica daleka.", TextEnglish = "The example of those who disbelieve in their Lord is [that] their deeds are like ashes which the wind blows forcefully on a stormy day; they are unable [to keep] from what they earned a [single] thing. That is what is extreme error.", TextGerman = "(Dies ist) das Gleichnis derjenigen, die ihren Herrn verleugnen: Ihre Werke sind wie Asche, auf die der Wind an einem stürmischen Tag heftig bläst. Sie haben keine Macht über etwas von dem, was sie erworben haben. Das ist wirklich der tiefe Irrtum." });
                        surah14.VerseList.Add(new Verse { Number = 19, TextBosnian = "Zar ne vidiš da je Allah nebesa i Zemlju s Istinom stvorio? Ako htjedne vas će ukloniti i nova stvorenja dovesti.", TextEnglish = "Have you not seen [i.e., considered] that Allāh created the heavens and the earth in truth? If He wills, He can do away with you and produce a new creation.", TextGerman = "Siehst du nicht, daß Allah die Himmel und die Erde in Wahrheit erschaffen hat? Wenn Er will, läßt Er euch vergehen und bringt eine neue Schöpfung." });
                        surah14.VerseList.Add(new Verse { Number = 20, TextBosnian = "To Allahu nije teško.", TextEnglish = "And that is not difficult for Allāh.", TextGerman = "Und dies ist für Allah keineswegs schwer." });
                        surah14.VerseList.Add(new Verse { Number = 21, TextBosnian = "I izići će svi pred Allaha, pa će oni koji su bili nemoćni reći svojim glavešinama, onima koji su osioni bili: 'Mi smo bili vaše pristalice, pa da li ste u stanju da nas bar malo od Allahove kazne zaštitite?' 'Da je nas Allah uputio', odgovorit će oni, 'i mi bismo bili vas uputili. Žalili se mi ili trpjeli, svejedno nam je, spasa nama više neće biti.'", TextEnglish = "And they will come out [for judgement] before Allāh all together, and the weak will say to those who were arrogant, 'Indeed, we were your followers, so can you avail us anything against the punishment of Allāh?' They will say, 'If Allāh had guided us, we would have guided you. It is all the same for us whether we show intolerance or are patient: there is for us no place of escape.'", TextGerman = "Und sie erscheinen alle vor Allah. Dann sagen die Schwachen zu denjenigen, die sich hochmütig verhielten: Wir waren doch eure Gefolgsleute; könnt ihr uns nun vor Allahs Strafe etwas nützen? Sie sagen: Wenn Allah uns rechtgeleitet hätte, fürwahr, hätten (auch) wir euch rechtgeleitet. Gleich ist es in Bezug auf uns, ob wir ängstlich sind oder standhaft bleiben; es gibt für uns kein Entrinnen." });
                        surah14.VerseList.Add(new Verse { Number = 22, TextBosnian = "I kada bude sve riješeno, šejtan će reći: 'Allah vam je istinsko obećanje dao, a ja sam vam obećavao i svoja obećanja iznevjerio; ali, ja nisam nikakvog dokaza nad vama imao, samo sam vas pozivao i vi ste mi se odazivali; zato ne korite mene, već sami sebe; niti ja mogu vama pomoći, niti vi možete pomoći meni. Ja nemam ništa s tim što ste me prije Njemu u obožavanju pridruživali. Zulumćare sigurno čeka bolna patnja.'", TextEnglish = "And Satan will say when the matter has been concluded, 'Indeed, Allāh had promised you the promise of truth. And I promised you, but I betrayed you. But I had no authority over you except that I invited you, and you responded to me. So do not blame me; but blame yourselves. I cannot be called to your aid, nor can you be called to my aid. Indeed, I deny your association of me [with Allāh] before.[655] Indeed, for the wrongdoers is a painful punishment.'", TextGerman = "Und der Satan sagt, nachdem die Angelegenheit entschieden ist: Gewiß, Allah hat euch ein wahres Versprechen gegeben, und ich habe euch (etwas) versprochen, es aber dann gebrochen. Und ich hatte keine Macht über euch, außer daß ich euch gerufen habe und ihr auf mich gehört habt. So tadelt mich nicht, sondern tadelt euch selbst. Ich kann euch nicht zu Hilfe kommen, und ihr könnt mir nicht zu Hilfe kommen. Ich weise es ja von mir, daß ihr mich zuvor (Allah) beigesellt habt. Gewiß, für die Ungerechten gibt es schmerzhafte Strafe." });
                        surah14.VerseList.Add(new Verse { Number = 23, TextBosnian = "A oni koji su vjerovali i dobra djela činili bit će uvedeni, s dozvolom Gospodara njihova, u džennetske bašče kroz koje rijeke teku i u njima će vječno boraviti, njihov pozdrav će u njima selam biti.", TextEnglish = "And those who believed and did righteous deeds will be admitted to gardens beneath which rivers flow, abiding eternally therein by permission of their Lord; and their greeting therein will be, 'Peace!'", TextGerman = "Aber diejenigen, die glauben und rechtschaffene Werke tun, werden in Gärten eingelassen, durcheilt von Bächen, ewig darin zu bleiben, mit der Erlaubnis ihres Herrn. Ihr Gruß darin wird sein: Friede!" });
                        surah14.VerseList.Add(new Verse { Number = 24, TextBosnian = "Zar ne vidiš kako Allah navodi primjer - lijepa je riječ kao lijepo drvo: korijen mu je čvrsto u zemlji a grane prema nebu.", TextEnglish = "Have you not considered how Allāh presents an example, [making] a good word like a good tree, whose root is firmly fixed and its branches [high] in the sky?", TextGerman = "Siehst du nicht, wie Allah ein Gleichnis von einem guten Wort geprägt hat? (Es ist) wie ein guter Baum, dessen Wurzeln fest sitzen und dessen Zweige in den Himmel (reichen)." });
                        surah14.VerseList.Add(new Verse { Number = 25, TextBosnian = "Ono plod svoj u svakom momentu daje s dozvolom Gospodara svoga- a Allah ljudima navodi primjere da bi se prisjetili i pouku uzeli,", TextEnglish = "It produces its fruit all the time, by permission of its Lord. And Allāh presents examples for the people that perhaps they will be reminded.", TextGerman = "Er bringt seinen Ernteertrag zu jeder Zeit (hervor) - mit der Erlaubnis seines Herrn. Und Allah prägt für die Menschen Gleichnisse, auf daß sie bedenken mögen." });
                        surah14.VerseList.Add(new Verse { Number = 26, TextBosnian = "a ružna je riječ kao ružno drvo koje je s površine zemlje iščupano. Njemu nema opstanka.", TextEnglish = "And the example of a bad word is like a bad tree, uprooted from the surface of the earth, not having any stability.", TextGerman = "Und das Gleichnis eines schlechten Wortes ist wie ein schlechter Baum, der aus der Erde herausgerissen worden ist und keinen festen Grund (mehr) hat." });
                        surah14.VerseList.Add(new Verse { Number = 27, TextBosnian = "Allah vjernike postojanom riječju učvršćuje u životu na dunjaluku, a i na ahiretu, a zulumćare u zabludu odvodi; Allah radi što hoće.", TextEnglish = "Allāh keeps firm those who believe, with the firm word,[656] in worldly life and in the Hereafter.[657] And Allāh sends astray the wrongdoers. And Allāh does what He wills.", TextGerman = "Allah festigt diejenigen, die glauben, durch das beständige Wort im diesseitigen Leben und im Jenseits. Doch Allah läßt die Ungerechten in die Irre gehen. Allah tut, was Er will." });
                        surah14.VerseList.Add(new Verse { Number = 28, TextBosnian = "Zar ne vidiš one koji su umjesto zahvalnosti Allahu na blagodatima - nezahvalnošću uzvratili i narod svoj u Kuću propasti doveli,", TextEnglish = "Have you not considered those who exchanged the favor of Allāh for disbelief[658] and settled their people [in] the home of ruin?", TextGerman = "Siehst du nicht jene, die Allahs Gunst gegen Undankbarkeit eingetauscht und ihr Volk in die Wohnstätte des Niedergangs versetzt haben," });
                        surah14.VerseList.Add(new Verse { Number = 29, TextBosnian = "u Džehennem, u kome će gorjeti - a užasno je on prebivalište!", TextEnglish = "[It is] Hell, which they will [enter to] burn, and wretched is the settlement.", TextGerman = "in die Hölle, der sie ausgesetzt sind? - Ein schlimmer Aufenthalt!" });
                        surah14.VerseList.Add(new Verse { Number = 30, TextBosnian = "I koji su Allahu druge kao ravne dodavali da bi zavodili s puta Njegova? Reci: 'Naslađujte se, završit ćete, sigurno, u Vatri!'", TextEnglish = "And they have attributed to Allāh equals to mislead [people] from His way. Say, 'Enjoy yourselves, for indeed, your destination is the Fire.'", TextGerman = "Und sie haben Allah andere als Seinesgleichen zur Seite gestellt, um (die Menschen) von Seinem Weg abirren zu lassen. Sag: Genießt nur, euer Ausgang wird ja in das (Höllen)feuer sein." });
                        surah14.VerseList.Add(new Verse { Number = 31, TextBosnian = "Reci Mojim robovima koji vjeruju da namaz obavljaju i da udjeljuju i tajno i javno iz onoga što im Mi darujemo, prije nego što nastupi Dan u kome neće biti ni trgovanja ni prijateljstva.", TextEnglish = "[O Muḥammad], tell My servants who have believed to establish prayer and spend from what We have provided them, secretly and publicly, before a Day comes in which there will be no exchange [i.e., ransom], nor any friendships.", TextGerman = "Sag zu Meinen Dienern, die glauben, sie sollen das Gebet verrichten und von dem, womit Wir sie versorgt haben, heimlich und öffentlich (als Spende) ausgeben, bevor ein Tag kommt, an dem es weder Verkaufen noch Freundschaften gibt." });
                        surah14.VerseList.Add(new Verse { Number = 32, TextBosnian = "Allah je Taj Koji je stvorio nebesa i Zemlju; On spušta s neba vodu i čini da pomoću nje rađaju plodovi kojima se hranite; i potčinio vam je lađe da morem plove po odredbi Njegovoj, i potčinio vam je rijeke,", TextEnglish = "It is Allāh who created the heavens and the earth and sent down rain from the sky and produced thereby some fruits as provision for you and subjected for you[659] the ships to sail through the sea by His command and subjected for you the rivers.", TextGerman = "Allah ist es, Der die Himmel und die Erde erschaffen hat und vom Himmel Wasser herabkommen läßt, durch das Er dann für euch Früchte als Versorgung hervorbringt. Und Er hat euch die Schiffe dienstbar gemacht, damit sie auf dem Meer auf Seinen Befehl fahren, und Er hat euch die Flüsse dienstbar gemacht." });
                        surah14.VerseList.Add(new Verse { Number = 33, TextBosnian = "i potčinio vam je Sunce i Mjesec, koji se stalno kreću, i potčinio vam je noć i dan.", TextEnglish = "And He subjected for you the sun and the moon, continuous [in orbit], and subjected for you the night and the day.", TextGerman = "Er hat euch die Sonne und den Mond in ihrem unablässigen Lauf dienstbar gemacht, und Er hat euch die Nacht und den Tag dienstbar gemacht." });
                        surah14.VerseList.Add(new Verse { Number = 34, TextBosnian = "I daje vam od svega onoga što od Njega tražite, i ako biste Allahove blagodati brojali, ne biste ih nabrojali. Čovjek je, uistinu, itekako nepravedan i nezahvalan.", TextEnglish = "And He gave you from all you asked of Him.[660] And if you should count the favor [i.e., blessings] of Allāh, you could not enumerate them. Indeed, mankind is [generally] most unjust and ungrateful.[661]", TextGerman = "Und Er gewährte euch von allem, worum ihr batet. Wenn ihr die Gunst(erweise) Allahs aufzählen wolltet, könntet ihr sie nicht erfassen. Gewiß, der Mensch ist wahrlich sehr oft ungerecht und sehr oft undankbar." });
                        surah14.VerseList.Add(new Verse { Number = 35, TextBosnian = "A kada Ibrahim reče: 'Gospodaru moj, učini ovaj grad sigurnim i sačuvaj i mene i sinove moje od toga da obožavamo kumire.", TextEnglish = "And [mention, O Muḥammad], when Abraham said, 'My Lord, make this city [i.e., Makkah] secure and keep me and my sons away from worshipping idols.", TextGerman = "Und als Ibrahim sagte: Mein Herr, mache diese Ortschaft sicher, und lasse mich und meine Kinder es meiden, Götzen zu dienen." });
                        surah14.VerseList.Add(new Verse { Number = 36, TextBosnian = "Oni su, Gospodaru moj, mnoge ljude na stranputicu naveli. Onaj ko bude mene slijedio - moje je vjere, a onaj ko bude protiv mene ustajao, pa - Ti si, uistinu, Onaj Koji oprašta grijehe i milostiv si.", TextEnglish = "My Lord, indeed they have led astray many among the people. So whoever follows me - then he is of me;[662] and whoever disobeys me - indeed, You are [yet] Forgiving and Merciful.", TextGerman = "Mein Herr, sie haben viele von den Menschen in die Irre geführt. Wer mir nun folgt, der gehört zu mir; und wer sich mir widersetzt, so bist Du ja Allvergebend und Barmherzig." });
                        surah14.VerseList.Add(new Verse { Number = 37, TextBosnian = "Gospodaru naš, ja sam neke potomke svoje naselio u kotlini u kojoj se ništa ne sije, kod Tvoje časne Kabe, da bi, Gospodaru naš, namaz obavljali; zato učini da srca nekih ljudi čeznu za njima i opskrbi ih raznim plodovima da bi zahvalni bili.", TextEnglish = "Our Lord, I have settled some of my descendants in an uncultivated valley near Your sacred House, our Lord, that they may establish prayer. So make hearts among the people incline toward them and provide for them from the fruits that they might be grateful.", TextGerman = "Unser Herr, ich habe (einige) aus meiner Nachkommenschaft in einem Tal ohne Pflanzungen bei Deinem geschützten Haus wohnen lassen, unser Herr, damit sie das Gebet verrichten. So lasse die Herzen einiger Menschen sich ihnen zuneigen und versorge sie mit Früchten, auf daß sie dankbar sein mögen." });
                        surah14.VerseList.Add(new Verse { Number = 38, TextBosnian = "Gospodaru naš, Ti sigurno znaš šta mi tajimo, a šta na javu iznosimo. A Allahu ništa nije skriveno; ni na Zemlji, ni na nebu.", TextEnglish = "Our Lord, indeed You know what we conceal and what we declare, and nothing is hidden from Allāh on the earth or in the heaven.", TextGerman = "Unser Herr, Du weißt ja, was wir verbergen und was wir offenlegen; vor Allah ist nichts verborgen, weder auf der Erde noch im Himmel." });
                        surah14.VerseList.Add(new Verse { Number = 39, TextBosnian = "Hvala Allahu, Koji mi je u starosti podario Ismaila i Ishaka; Gospodar moj dove, uistinu, uslišava.", TextEnglish = "Praise to Allāh, who has granted to me in old age Ishmael and Isaac. Indeed, my Lord is the Hearer of supplication.", TextGerman = "(Alles) Lob gehört Allah, Der mir trotz meines hohen Alters Ismail und Ishaq geschenkt hat! Gewiß, mein Herr ist wahrlich der Erhörer des Gebets." });
                        surah14.VerseList.Add(new Verse { Number = 40, TextBosnian = "Gospodaru moj, daj da ja i potomci moji u obavljanju namaza budemo ustrajni; Gospodaru naš, Ti usliši dovu moju!", TextEnglish = "My Lord, make me an establisher of prayer, and [many] from my descendants. Our Lord, and accept my supplication.", TextGerman = "Mein Herr, mach, daß ich das Gebet verrichte, (ich) und (auch einige) aus meiner Nachkommenschaft. Unser Herr, und nimm mein Gebet an." });
                        surah14.VerseList.Add(new Verse { Number = 41, TextBosnian = "Gospodaru naš, oprosti meni, i roditeljima mojim i svim vjernicima- na Dan kad se bude polagao račun!'", TextEnglish = "Our Lord, forgive me and my parents and the believers the Day the account is established.'", TextGerman = "Unser Herr, vergib mir und meinen Eltern und den Gläubigen an dem Tag, da die Abrechnung stattfinden wird." });
                        surah14.VerseList.Add(new Verse { Number = 42, TextBosnian = "A ti nikako ne misli da Allah ne mari za onim što rade zulumćari! On ih samo pušta do Dana kada će im oči ostati otvorene!", TextEnglish = "And never think that Allāh is unaware of what the wrongdoers do. He only delays them [i.e., their account] for a Day when eyes will stare [in horror].", TextGerman = "Und meine ja nicht, Allah sei unachtsam dessen, was die Ungerechten tun. Er stellt sie nur zurück bis zu einem Tag, an dem die Blicke starr werden," });
                        surah14.VerseList.Add(new Verse { Number = 43, TextBosnian = "Žurit će, glave im uzdignute, pogled ukočen; a srca će im prazna biti.", TextEnglish = "Racing ahead, their heads raised up, their glance does not come back to them,[663] and their hearts are void.", TextGerman = "(sie kommen) hastend, die Köpfe hochhebend; ihr Blick kehrt nicht zu ihnen zurück, und ihre Herzen sind leer." });
                        surah14.VerseList.Add(new Verse { Number = 44, TextBosnian = "Ti opominji ljude Danom kada će im kazna doći, kad će oni koji su zulum činili - govoriti: 'Gospodaru naš, ostavi nas još samo kratko vrijeme, odazvat ćemo se pozivu Tvome i slijedit ćemo poslanike!', a zar se prije niste zaklinjali da s ovog svijeta nećete otići?", TextEnglish = "And, [O Muḥammad], warn the people of a Day when the punishment will come to them and those who did wrong will say, 'Our Lord, delay us for a short term; we will answer Your call and follow the messengers.' [But it will be said], 'Had you not sworn, before, that for you there would be no cessation?[664]", TextGerman = "Und warne die Menschen vor dem Tag, an dem die Strafe über sie kommt. Da werden diejenigen sagen, die Unrecht taten: Unser Herr, stelle uns auf eine kurze Frist zurück, so werden wir Deinen Ruf erhören und den Gesandten folgen. Hattet ihr denn nicht zuvor geschworen, es würde für euch keinen Untergang geben?" });
                        surah14.VerseList.Add(new Verse { Number = 45, TextBosnian = "I nastanili ste se bili u mjestima gdje su živjeli oni koji su prema sebi zulum učinili, a bilo vam je poznato kako smo s njima postupili i primjere smo vam navodili.", TextEnglish = "And you lived among the dwellings of those who wronged themselves, and it had become clear to you how We dealt with them. And We presented for you [many] examples.'", TextGerman = "Ihr habt noch in den Wohnorten derer gewohnt, die sich selbst Unrecht zugefügt haben, und es ist euch klargeworden, wie Wir an ihnen gehandelt haben. Wir haben euch doch Beispiele geprägt." });
                        surah14.VerseList.Add(new Verse { Number = 46, TextBosnian = "I oni lukavstva svoja pletu, a Allah zna za lukavstva njihova, samo što lukavstva njihova ne mogu brda pokrenuti.", TextEnglish = "And they had planned their plan, but with Allāh is [recorded] their plan, even if their plan had been [sufficient] to do away with the mountains.[665]", TextGerman = "Und sie haben bereits ihre Ränke geschmiedet, aber ihre Ränke sind bei Allah, auch wenn ihre Ränke derart sind, daß davor die Berge vergehen." });
                        surah14.VerseList.Add(new Verse { Number = 47, TextBosnian = "Nemoj ni pomisliti da Allah neće održati obećanje Svoje poslanicima Svojim-Allah je, uistinu, silan i strog.", TextEnglish = "So never think that Allāh will fail in His promise to His messengers. Indeed, Allāh is Exalted in Might and Owner of Retribution.[666]", TextGerman = "So meine ja nicht, Allah würde Sein Versprechen gegenüber Seinen Gesandten brechen. Gewiß, Allah ist Allmächtig und Besitzer von Vergeltungsgewalt." });
                        surah14.VerseList.Add(new Verse { Number = 48, TextBosnian = "Na Dan kad Zemlja bude zamijenjena drugom zemljom, a i nebesa, i kad svi iziđu pred Allaha, Jedinoga, Potčinitelja svega.", TextEnglish = "[It will be] on the Day the earth will be replaced by another earth, and the heavens [as well], and they [i.e., all creatures] will come out before Allāh, the One, the Prevailing,", TextGerman = "An dem Tag, da die Erde zu einer anderen Erde verändert werden wird, und (ebenso) die Himmel, und da sie vor Allah erscheinen werden, dem Einen, dem Allbezwinger." });
                        surah14.VerseList.Add(new Verse { Number = 49, TextBosnian = "Taj dan ćeš vidjeti prestupnike u okovima zajedno povezane.", TextEnglish = "And you will see the criminals that Day bound together in irons,", TextGerman = "An jenem Tag wirst du die Übeltäter in Ketten zusammengebunden sehen." });
                        surah14.VerseList.Add(new Verse { Number = 50, TextBosnian = "Košulje će im od katrana biti, a vatra će lica njihova obaviti.", TextEnglish = "Their garments of liquid pitch and their faces covered by the Fire", TextGerman = "Ihre Kleider werden aus Pech sein, und das Feuer wird ihre Gesichter überdecken," });
                        surah14.VerseList.Add(new Verse { Number = 51, TextBosnian = "To je zato da Allah svakome da što je zaslužio - Allah zaista brzo račun svodi.", TextEnglish = "So that Allāh will recompense every soul for what it earned. Indeed, Allāh is swift in account.", TextGerman = "damit Allah jeder Seele vergelte, was sie erworben hat. Gewiß, Allah ist schnell im Abrechnen." });
                        surah14.VerseList.Add(new Verse { Number = 52, TextBosnian = "Ovo je obznana ljudima, i da njome budu opomenuti, i da znaju da je samo On Jedan Bog, i da prime pouku oni koji razum imaju!", TextEnglish = "This [Qur’ān] is notification for the people that they may be warned thereby and that they may know that He is but one God and that those of understanding will be reminded.", TextGerman = "Dies ist eine Botschaft an die Menschen, damit sie dadurch gewarnt werden und damit sie wissen, daß Er nur ein Einziger Gott ist, und damit diejenigen bedenken, die Verstand besitzen." });
                        result = surah14;
                        break;
                    #endregion

                    // 
                    #region 15 Al-Hijr
                    case 15:
                        Surah surah15 = new Surah(); surah15.Name = Constants.Surah15Name; surah15.Number = 15; surah15.VerseList = new List<Verse>();
                        surah15.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah15.VerseList.Add(new Verse { Number = 1, TextBosnian = "Elif-lam-ra. Ovo su ajeti Knjige, Kur'ana jasnog!", TextEnglish = "Alif, Lām, Rā.[668] These are the verses of the Book and a clear Qur’ān [i.e., recitation].", TextGerman = "Alif-Lam-Ra. Dies sind die Zeichen des Buches und eines deutlichen Qur’ans." });
                        surah15.VerseList.Add(new Verse { Number = 2, TextBosnian = "Oni koji ne vjeruju često će žaliti što muslimani nisu bili.", TextEnglish = "Perhaps those who disbelieve will wish[669] that they had been Muslims.", TextGerman = "Vielleicht werden diejenigen, die ungläubig sind, wünschen, Muslime gewesen zu sein." });
                        surah15.VerseList.Add(new Verse { Number = 3, TextBosnian = "Pusti ih neka jedu i naslađuju se, i neka ih zavarava nada - znat će oni!", TextEnglish = "Let them eat and enjoy themselves and be diverted by [false] hope, for they are going to know.", TextGerman = "Lasse sie nur essen und genießen und sich durch (falsche) Hoffnung ablenken lassen. Sie werden (es noch) erfahren." });
                        surah15.VerseList.Add(new Verse { Number = 4, TextBosnian = "A nijedno naselje nismo uništili a da mu nije propisan određeni rok.", TextEnglish = "And We did not destroy any city but that for it was a known decree.", TextGerman = "Und wir haben keine Stadt vernichtet, ohne daß sie eine festgelegte Frist gehabt hätte." });
                        surah15.VerseList.Add(new Verse { Number = 5, TextBosnian = "Nijedan narod ne može ni ubrzati ni usporiti vrijeme mu određeno.", TextEnglish = "No nation will precede its term, nor will they remain thereafter.", TextGerman = "Keine Gemeinschaft kann ihrer Frist vorausgehen, noch sie hinausschieben." });
                        surah15.VerseList.Add(new Verse { Number = 6, TextBosnian = "Oni govore: 'Hej ti kome se Opomena objavljuje, ti si uistinu lud!", TextEnglish = "And they say, 'O you upon whom the message has been sent down, indeed you are mad.[670]", TextGerman = "Und sie sagen: O du, dem (angeblich) die Ermahnung offenbart worden ist, du bist ja fürwahr besessen." });
                        surah15.VerseList.Add(new Verse { Number = 7, TextBosnian = "Zašto nam meleke ne dovedeš, ako si jedan od iskrenih?!", TextEnglish = "Why do you not bring us the angels, if you should be among the truthful?'", TextGerman = "Warum bringst du uns nicht die Engel, wenn du zu den Wahrhaftigen gehörst?" });
                        surah15.VerseList.Add(new Verse { Number = 8, TextBosnian = "Mi meleke šaljemo samo s Istinom, a da ih pošaljemo, tada se njima ne bi vremena dalo.", TextEnglish = "We do not send down the angels except with truth;[671] and they [i.e., the disbelievers] would not then be reprieved.", TextGerman = "Wir senden die Engel nur mit der Wahrheit hinab. Dann wird ihnen kein Aufschub gewährt." });
                        surah15.VerseList.Add(new Verse { Number = 9, TextBosnian = "Mi, uistinu, Opomenu objavljujemo i zaista ćemo Mi nad njim bdjeti.", TextEnglish = "Indeed, it is We who sent down the message [i.e., the Qur’ān], and indeed, We will be its guardian.", TextGerman = "Gewiß, Wir sind es, die Wir die Ermahnung offenbart haben, und Wir werden wahrlich ihr Hüter sein." });
                        surah15.VerseList.Add(new Verse { Number = 10, TextBosnian = "I prije tebe poslanike smo narodima prijašnjim slali.", TextEnglish = "And We had certainly sent [messengers] before you, [O Muḥammad], among the sects of the former peoples.", TextGerman = "Wir haben ja bereits vor dir unter den Lagern der Früheren entsandt." });
                        surah15.VerseList.Add(new Verse { Number = 11, TextBosnian = "I nijedan im poslanik nije došao a da mu se nisu izrugivali.", TextEnglish = "And no messenger would come to them except that they ridiculed him.", TextGerman = "Und kein Gesandter kam zu ihnen, ohne daß sie sich über ihn lustig gemacht hätten." });
                        surah15.VerseList.Add(new Verse { Number = 12, TextBosnian = "Eto, tako dajemo da to uđe u srca prestupnika.", TextEnglish = "Thus do We insert it [i.e., denial] into the hearts of the criminals.", TextGerman = "So lassen Wir ihn in die Herzen der Übeltäter einziehen." });
                        surah15.VerseList.Add(new Verse { Number = 13, TextBosnian = "Oni u njega neće vjerovati, a već je primijenjena zakonitost kažnjavanja na narodima prijašnjim.", TextEnglish = "They will not believe in it, while there has already occurred the precedent of the former peoples.", TextGerman = "Sie glauben nicht daran, obwohl bereits die Gesetzmäßigkeit an den Früheren ergangen ist." });
                        surah15.VerseList.Add(new Verse { Number = 14, TextBosnian = "Kad bismo im kapiju na nebu otvorili i oni se kroz nju uspinjali,", TextEnglish = "And [even] if We opened to them a gate from the heaven and they continued therein to ascend,", TextGerman = "Selbst wenn Wir ihnen ein Tor vom Himmel öffneten und sie dauernd dadurch hinaufstiegen," });
                        surah15.VerseList.Add(new Verse { Number = 15, TextBosnian = "opet bi oni, sigurno, rekli: 'Pogledi očiju su nam opijeni, štaviše, mi smo ljudi opčinjeni.", TextEnglish = "They would say, 'Our eyes have only been dazzled. Rather, we are a people affected by magic.'", TextGerman = "würden sie dennoch sagen: Unsere Blicke sind verschlossen. Nein! Vielmehr sind wir Leute, die einem Zauber verfallen sind." });
                        surah15.VerseList.Add(new Verse { Number = 16, TextBosnian = "Mi smo na nebu velike zvijezde stvorili i okitili ga za one koji posmatraju.", TextEnglish = "And We have placed within the heaven great stars and have beautified it for the observers.", TextGerman = "Und Wir haben ja im Himmel Türme gesetzt und ihn für die Betrachter ausgeschmückt," });
                        surah15.VerseList.Add(new Verse { Number = 17, TextBosnian = "I zaštitili smo ga od svakog šejtana prokletog.", TextEnglish = "And We have protected it from every devil expelled [from the mercy of Allāh]", TextGerman = "und Wir haben ihn vor jedem gesteinigten Satan behütet," });
                        surah15.VerseList.Add(new Verse { Number = 18, TextBosnian = "A onoga koji kradom prisluškuje stiže plamen vidljivi.", TextEnglish = "Except one who steals a hearing and is pursued by a clear burning flame.", TextGerman = "außer demjenigen, der verstohlen zuhört, worauf ihn ein deutlich erkennbarer Leuchtkörper verfolgt." });
                        surah15.VerseList.Add(new Verse { Number = 19, TextBosnian = "A Zemlju smo prostrli i po njoj nepomične planine postavili i učinili da na njoj sve s mjerom raste.", TextEnglish = "And the earth - We have spread it and cast therein firmly set mountains and caused to grow therein [something] of every well-balanced thing.", TextGerman = "Und die Erde haben Wir gedehnt und darauf festgegründete Berge gesetzt und auf ihr von allen zu wiegenden Dingen wachsen lassen." });
                        surah15.VerseList.Add(new Verse { Number = 20, TextBosnian = "I tu smo vam dali sve što je za život potrebno, a i onima koje vi ne hranite.", TextEnglish = "And We have made for you therein means of living and [for] those for whom you are not providers.[672]", TextGerman = "Und Wir haben auf ihr für euch (Möglichkeiten für den) Lebensunterhalt geschaffen und (auch) für diejenigen, die ihr nicht versorgt." });
                        surah15.VerseList.Add(new Verse { Number = 21, TextBosnian = "I ne postoji ništa čije riznice ne posjedujemo, a od toga Mi dajemo samo po mjeri određenoj.", TextEnglish = "And there is not a thing but that with Us are its depositories, and We do not send it down except according to a known [i.e., specified] measure.", TextGerman = "Und es gibt nichts, dessen Schatzkammern nicht bei Uns wären. Und Wir senden es nur in bestimmtem Maß hinab." });
                        surah15.VerseList.Add(new Verse { Number = 22, TextBosnian = "Mi šaljemo vjetrove da oplođuju, a iz neba spuštamo vodu pa vas njome napajamo - vi je niste u stanju zadržati.", TextEnglish = "And We have sent the fertilizing winds[673] and sent down water from the sky and given you drink from it. And you are not its retainers.", TextGerman = "Und Wir senden die Winde zur Befruchtung. Und Wir lassen dann vom Himmel Wasser hinabkommen und geben es euch zu trinken, doch ihr könnt es nicht (alles davon) als Vorrat lagern." });
                        surah15.VerseList.Add(new Verse { Number = 23, TextBosnian = "I samo Mi dajemo život i smrt, i samo Mi sve nasljeđujemo.", TextEnglish = "And indeed, it is We who give life and cause death, and We are the Inheritor.[674]", TextGerman = "Und fürwahr, Wir sind es, die lebendig machen und sterben lassen, und Wir sind die (alles) Erbenden." });
                        surah15.VerseList.Add(new Verse { Number = 24, TextBosnian = "I samo Mi znamo one koji su vam prethodili, i samo Mi znamo one koji će poslije doći.", TextEnglish = "And We have already known the preceding [generations] among you, and We have already known the later [ones to come].", TextGerman = "Und Wir kennen ja diejenigen unter euch, die vorausgehen, und Wir kennen ja (auch) diejenigen, die zurückbleiben." });
                        surah15.VerseList.Add(new Verse { Number = 25, TextBosnian = "A On, Gospodar tvoj, zaista će ih sabrati. On je Mudri i Sveznajući.", TextEnglish = "And indeed, your Lord will gather them; indeed, He is Wise and Knowing.", TextGerman = "Und gewiß, dein Herr wird sie versammeln. Er ist Allweise und Allwissend." });
                        surah15.VerseList.Add(new Verse { Number = 26, TextBosnian = "Mi smo stvorili Adema od suhe gline; od blata glatkoga.", TextEnglish = "And We did certainly create man out of clay from an altered black mud.", TextGerman = "Und Wir haben ja den Menschen aus trockenem Ton, aus fauligem schwarzen Schlamm erschaffen." });
                        surah15.VerseList.Add(new Verse { Number = 27, TextBosnian = "A još prije stvorili smo džine od vatre užarene.", TextEnglish = "And the jinn We created before from scorching fire.", TextGerman = "Und die Ginn haben Wir zuvor aus dem Feuer des Glutwindes erschaffen." });
                        surah15.VerseList.Add(new Verse { Number = 28, TextBosnian = "I kad Gospodar tvoj reče melecima: 'Ja ću stvoriti čovjeka od suhe gline, od blata glatkoga.", TextEnglish = "And [mention, O Muḥammad], when your Lord said to the angels, 'I will create a human being out of clay from an altered black mud.", TextGerman = "Und als dein Herr zu den Engeln sagte: Ich bin dabei, ein menschliches Wesen aus trockenem Ton, aus fauligem schwarzen Schlamm zu erschaffen." });
                        surah15.VerseList.Add(new Verse { Number = 29, TextBosnian = "I kad mu dam lik i u njega udahnem dušu, vi mu na sedždu padnite!'", TextEnglish = "And when I have proportioned him and breathed into him of My [created] soul,[675] then fall down to him in prostration.'", TextGerman = "Wenn Ich es zurechtgeformt und ihm von Meinem Geist eingehaucht habe, dann fallt und werft euch vor ihm nieder." });
                        surah15.VerseList.Add(new Verse { Number = 30, TextBosnian = "Svi meleki su skupa sedždu učinili,", TextEnglish = "So the angels prostrated - all of them entirely,", TextGerman = "Da warfen sich die Engel alle zusammen nieder," });
                        surah15.VerseList.Add(new Verse { Number = 31, TextBosnian = "osim Iblisa; on je odbio da bude s onim koji su sedždu učinili.", TextEnglish = "Except Iblees;[676] he refused to be with those who prostrated.", TextGerman = "außer Iblis; er weigerte sich, mit denen zu sein, die sich niederwerfen." });
                        surah15.VerseList.Add(new Verse { Number = 32, TextBosnian = "O Iblise', reče On, 'zašto nisi bio s onima koji su sedždu učinili?'", TextEnglish = "[Allāh] said, 'O Iblees, what is [the matter] with you that you are not with those who prostrate?'", TextGerman = "Er sagte: O lblis, was ist mit dir, daß du nicht mit denen bist, die sich niederwerfen?" });
                        surah15.VerseList.Add(new Verse { Number = 33, TextBosnian = "Nije moje da učinim sedždu čovjeku koga si stvorio od suhe gline, od blata glatkoga.'", TextEnglish = "He said, 'Never would I prostrate to a human whom You created out of clay from an altered black mud.'", TextGerman = "Er sagte: Ich kann mich unmöglich vor einem menschlichen Wesen niederwerfen, das Du aus trockenem Ton, aus fauligem schwarzen Schlamm erschaffen hast." });
                        surah15.VerseList.Add(new Verse { Number = 34, TextBosnian = "Onda izlazi iz Dženneta', reče On, 'zaista si proklet!", TextEnglish = "[Allāh] said, 'Then depart from it,[677] for indeed, you are expelled.", TextGerman = "Er sagte: Dann geh aus ihm hinaus, denn du bist der Steinigung würdig," });
                        surah15.VerseList.Add(new Verse { Number = 35, TextBosnian = "I neka se prokletstvo zadrži na tebi do Dana sudnjega!'", TextEnglish = "And indeed, upon you is the curse until the Day of Recompense.'", TextGerman = "Und auf dir liegt der Fluch bis zum Tag des Gerichts." });
                        surah15.VerseList.Add(new Verse { Number = 36, TextBosnian = "Gospodaru moj', reče on, 'daj mi vremena do Dana kad će oni biti oživljeni!'", TextEnglish = "He said, 'My Lord, then reprieve me until the Day they are resurrected.'", TextGerman = "Er sagte: Mein Herr, so gewähre mir Aufschub bis zu dem Tag, da sie auferweckt werden." });
                        surah15.VerseList.Add(new Verse { Number = 37, TextBosnian = "Daje ti se vrijeme', reče On,", TextEnglish = "[Allāh] said, 'So indeed, you are of those reprieved", TextGerman = "Er sagte: Gewiß, du gehörst zu denjenigen, denen Aufschub gewährt wird" });
                        surah15.VerseList.Add(new Verse { Number = 38, TextBosnian = "do Dana već određenog.'", TextEnglish = "Until the Day of the time well-known.'", TextGerman = "bis zum Tag der (wohl)bekannten Zeit." });
                        surah15.VerseList.Add(new Verse { Number = 39, TextBosnian = "Gospodaru moj', reče, 'kao što si me u zabludu doveo, tako ću i ja njima na Zemlji poroke lijepim činiti i sve ću ih zavesti,", TextEnglish = "[Iblees] said, 'My Lord, because You have put me in error, I will surely make [disobedience] attractive to them [i.e., mankind] on earth, and I will mislead them all", TextGerman = "Er sagte: Mein Herr, darum, daß Du mich in Verirrung hast fallen lassen, werde ich ihnen ganz gewiß auf der Erde (das Böse) ausschmücken und sie ganz gewiß allesamt in Verirrung fallen lassen," });
                        surah15.VerseList.Add(new Verse { Number = 40, TextBosnian = "osim Tvojih iskrenih robova među njima.'", TextEnglish = "Except, among them, Your chosen servants.'", TextGerman = "außer Deinen Dienern, den auserlesenen unter ihnen." });
                        surah15.VerseList.Add(new Verse { Number = 41, TextBosnian = "Ovo je Pravi put k Meni', reče On.", TextEnglish = "[Allāh] said, 'This is a path [of return] to Me [that is] straight.", TextGerman = "Er sagte: Das ist ein gerader Weg, der (einzuhalten) Mir obliegt." });
                        surah15.VerseList.Add(new Verse { Number = 42, TextBosnian = "Ti nećeš imati nikakve vlasti nad robovima Mojim, osim nad onima koji te budu slijedili, od onih zalutalih;", TextEnglish = "Indeed, My servants - no authority will you have over them, except those who follow you of the deviators.", TextGerman = "Gewiß, über Meine Diener hast du keine Macht, außer wer dir von den Verirrten folgt." });
                        surah15.VerseList.Add(new Verse { Number = 43, TextBosnian = "za sve njih Džehennem će odredište biti,", TextEnglish = "And indeed, Hell is the promised place for them all.", TextGerman = "Und die Hölle ist wahrlich ihrer aller Verabredung(sort)." });
                        surah15.VerseList.Add(new Verse { Number = 44, TextBosnian = "on sedam kapija ima i kroz svaku će određen broj njih proći.'", TextEnglish = "It has seven gates; for every gate is of them [i.e., Satan's followers] a portion designated.'", TextGerman = "Sie hat sieben Tore, und jedem Tor wird ein Teil von ihnen zugewiesen." });
                        surah15.VerseList.Add(new Verse { Number = 45, TextBosnian = "Zaista će bogobojazni u džennetskim baščama i pored izvora biti.", TextEnglish = "Indeed, the righteous will be within gardens and springs,", TextGerman = "Die Gottesfürchtigen aber werden in Gärten und an Quellen sein:" });
                        surah15.VerseList.Add(new Verse { Number = 46, TextBosnian = "Uđite u njih sigurni, straha oslobođeni!'", TextEnglish = "[Having been told], 'Enter it in peace, safe [and secure].'", TextGerman = "Betretet sie in Frieden und in Sicherheit." });
                        surah15.VerseList.Add(new Verse { Number = 47, TextBosnian = "Mi ćemo zlobu iz grudi njihovih istisnuti; kao braća će biti, jedni naspram drugih oni će na divanima sjediti.", TextEnglish = "And We will remove whatever is in their breasts of resentment,[678] [so they will be] brothers, on thrones facing each other.", TextGerman = "Und Wir nehmen weg, was in ihren Brüsten an Groll ist, als Brüder auf Liegen (ruhend), einander gegenüber." });
                        surah15.VerseList.Add(new Verse { Number = 48, TextBosnian = "Tu ih umor neće doticati, oni odatle nikada neće izvedeni biti.", TextEnglish = "No fatigue will touch them therein, nor from it will they [ever] be removed.", TextGerman = "Darin widerfährt ihnen weder Mühsal, noch werden sie daraus vertrieben." });
                        surah15.VerseList.Add(new Verse { Number = 49, TextBosnian = "Kaži robovima Mojim da sam Ja, zaista, Oprostitelj grijeha, Milostivi,", TextEnglish = "[O Muḥammad], inform My servants that it is I who am the Forgiving, the Merciful,", TextGerman = "Tue Meinen Dienern kund, daß Ich es bin, der Allvergebend und Barmherzig ist," });
                        surah15.VerseList.Add(new Verse { Number = 50, TextBosnian = "ali da je i kazna Moja, doista, bolna kazna!", TextEnglish = "And that it is My punishment which is the painful punishment.", TextGerman = "und daß Meine Strafe die schmerzhafte Strafe ist." });
                        surah15.VerseList.Add(new Verse { Number = 51, TextBosnian = "I obavijesti ih o gostima Ibrahimovim,", TextEnglish = "And inform them about the guests of Abraham,", TextGerman = "Und gib ihnen Kunde über die Gäste Ibrahims." });
                        surah15.VerseList.Add(new Verse { Number = 52, TextBosnian = "kada su mu ušli i rekli: 'Selam!', on je rekao: 'Prepali smo vas se!'", TextEnglish = "When they entered upon him and said, 'Peace.' [Abraham] said, 'Indeed, we are fearful [i.e., apprehensive] of you.'", TextGerman = "Als sie bei ihm eintraten und sagten: Frieden!, sagte er: Wir ängstigen uns vor euch." });
                        surah15.VerseList.Add(new Verse { Number = 53, TextBosnian = "Ne plaši se!', rekoše, 'donosimo ti radosnu vijest, imat ćeš sina koji će znan biti.'", TextEnglish = "[The angels] said, 'Fear not. Indeed, we give you good tidings of a learned boy.'", TextGerman = "Sie sagten: Ängstige dich nicht. Wir verkünden dir einen kenntnisreichen Jungen." });
                        surah15.VerseList.Add(new Verse { Number = 54, TextBosnian = "Donosite radosnu vijest sada kad me je starost ophrvala?', reče on, 'čime ćete me obradovati?'", TextEnglish = "He said, 'Have you given me good tidings although old age has come upon me? Then of what [wonder] do you inform?'", TextGerman = "Er sagte: Ihr verkündet (es) mir, obwohl mir das hohe Alter widerfahren ist! Was verkündet ihr mir denn da?" });
                        surah15.VerseList.Add(new Verse { Number = 55, TextBosnian = "Donosimo ti radosnu vijest koja će se doista obistiniti', rekoše oni, 'zato ne budi među očajnicima!'", TextEnglish = "They said, 'We have given you good tidings in truth, so do not be of the despairing.'", TextGerman = "Sie sagten: Wir verkünden (es) dir der Wahrheit entsprechend. So gehöre nicht zu denen, die die Hoffnung verlieren." });
                        surah15.VerseList.Add(new Verse { Number = 56, TextBosnian = "A ko osim oni koji su zabludjeli gubi nadu u milost svoga Gospodara?!', reče on.", TextEnglish = "He said, 'And who despairs of the mercy of his Lord except for those astray?'", TextGerman = "Er sagte: Wer verliert die Hoffnung auf die Barmherzigkeit seines Herrn außer den Irregehenden?" });
                        surah15.VerseList.Add(new Verse { Number = 57, TextBosnian = "I upita: 'A koji je tačno razlog vašeg dolaska, o izaslanici?'", TextEnglish = "[Abraham] said, 'Then what is your business [here], O messengers?'", TextGerman = "Er sagte: Was ist nun euer Auftrag, ihr Boten?" });
                        surah15.VerseList.Add(new Verse { Number = 58, TextBosnian = "Mi smo', rekoše, 'zaista, bili poslani i narodu koji je prestupe činio,", TextEnglish = "They said, 'Indeed, we have been sent to a people of criminals,", TextGerman = "Sie sagten: Wir sind zu einem Volk von Übeltätern gesandt," });
                        surah15.VerseList.Add(new Verse { Number = 59, TextBosnian = "izuzev Lutove porodice, njih ćemo, zaista, sve spasiti,", TextEnglish = "Except the family of Lot; indeed, we will save them all", TextGerman = "ausgenommen die Sippe Luts. Diese werden Wir wahrlich allesamt erretten," });
                        surah15.VerseList.Add(new Verse { Number = 60, TextBosnian = "osim žene njegove, ona će, odredili smo, biti od onih koji će ostati i kaznu iskusiti.'", TextEnglish = "Except his wife.' We [i.e., Allāh] decreed that she is of those who remain behind.[679]", TextGerman = "außer seiner Frau. Wir haben (es so) bestimmt, sie gehörte fürwahr zu denen, die zurückbleiben." });
                        surah15.VerseList.Add(new Verse { Number = 61, TextBosnian = "I kad izaslanici dođoše do Lutove porodice,", TextEnglish = "And when the messengers came to the family of Lot,", TextGerman = "Als nun die Gesandten zu der Sippe Luts kamen," });
                        surah15.VerseList.Add(new Verse { Number = 62, TextBosnian = "on reče: 'Vi ste, doista, ljudi neznani!'", TextEnglish = "He said, 'Indeed, you are people unknown.'", TextGerman = "sagte er: Ihr seid ja fremde Leute." });
                        surah15.VerseList.Add(new Verse { Number = 63, TextBosnian = "Ne', rekoše oni. 'Donosimo ti ono u što ovi stalno sumnjaju,", TextEnglish = "They said, 'But we have come to you with that about which they were disputing,", TextGerman = "Sie sagten: Nein! Vielmehr kommen wir zu dir mit dem, woran sie zu zweifeln pflegten." });
                        surah15.VerseList.Add(new Verse { Number = 64, TextBosnian = "donosimo ti ono što će se sigurno dogoditi, a mi, zaista, istinu govorimo.", TextEnglish = "And we have come to you with truth, and indeed, we are truthful.", TextGerman = "Und wir kommen zu dir mit der Gewißheit, und wir sagen gewiß die Wahrheit." });
                        surah15.VerseList.Add(new Verse { Number = 65, TextBosnian = "Izvedi porodicu svoju u gluho doba noći, a ti budi na začelju njihovu, i neka se niko od vas ne osvrće, i idite tamo gdje vam se naređuje!'", TextEnglish = "So set out with your family during a portion of the night and follow behind them and let not anyone among you look back and continue on to where you are commanded.'", TextGerman = "So zieh mit deinen Angehörigen in einem Teil der Nacht fort, und folge du hinterdrein. Und niemand von euch soll sich umwenden, geht weiter, wohin euch befohlen wird." });
                        surah15.VerseList.Add(new Verse { Number = 66, TextBosnian = "I Mi smo mu objavili ono što će se zbiti: da će oni, svi do posljednjeg, u svitanje uništeni biti.", TextEnglish = "And We conveyed to him [the decree] of that matter: that those [sinners] would be eliminated by early morning.", TextGerman = "Und Wir haben ihm diese Angelegenheit mitgeteilt, daß der letzte Rest dieser Leute bei Tagesanbruch ausgetilgt werde." });
                        surah15.VerseList.Add(new Verse { Number = 67, TextBosnian = "I dodoše stanovnici grada, veseli.", TextEnglish = "And the people of the city came rejoicing.[680]", TextGerman = "Und die Bewohner der Stadt kamen voller Freude." });
                        surah15.VerseList.Add(new Verse { Number = 68, TextBosnian = "Doista su ovo moji gosti', reče, 'pa me ne sramotite,", TextEnglish = "[Lot] said, 'Indeed, these are my guests, so do not shame me.", TextGerman = "Er sagte: Das sind meine Gäste, so stellt mich nicht bloß." });
                        surah15.VerseList.Add(new Verse { Number = 69, TextBosnian = "i bojte se Allaha, i mene ne ponižavajte!'", TextEnglish = "And fear Allāh and do not disgrace me.'", TextGerman = "Und fürchtet Allah und stürzt mich nicht in Schande." });
                        surah15.VerseList.Add(new Verse { Number = 70, TextBosnian = "A zar ti nismo zabranili da muški svijet ugošćavaš?'", TextEnglish = "They said, 'Have we not forbidden you from [protecting] people?'", TextGerman = "Sie sagten: Haben wir dir nicht die Weltenbewohner verboten (als Gäste aufzunehmen)?" });
                        surah15.VerseList.Add(new Verse { Number = 71, TextBosnian = "On reče: 'Evo vam za brak kćeri mojih, ako to hoćete učiniti!'", TextEnglish = "[Lot] said, 'These are my daughters[681] - if you would be doers [of lawful marriage].'", TextGerman = "Er sagte: Hier sind meine Töchter, wenn ihr etwas tun wollt." });
                        surah15.VerseList.Add(new Verse { Number = 72, TextBosnian = "A života mi tvoga, oni su u pijanstvu svom lutali.", TextEnglish = "By your life, [O Muḥammad], indeed they were, in their intoxication, wandering blindly.", TextGerman = "Bei deinem Leben, sie irrten wahrlich in ihrer Trunkenheit umher." });
                        surah15.VerseList.Add(new Verse { Number = 73, TextBosnian = "I njih je zadesio strašan glas kad je Sunce izlazilo,", TextEnglish = "So the shriek[682] seized them at sunrise.", TextGerman = "Da ergriff sie der Schrei bei Sonnenaufgang," });
                        surah15.VerseList.Add(new Verse { Number = 74, TextBosnian = "i Mi smo učinili da ono što je bilo gore bude dolje, i na njih smo kišu grumenja od skamenjene gline sručili.", TextEnglish = "And We made the highest part [of the city] its lowest and rained upon them stones of hard clay.", TextGerman = "und Wir kehrten das Oberste von ihr zuunterst und ließen auf sie Steine aus gebranntem Lehm regnen." });
                        surah15.VerseList.Add(new Verse { Number = 75, TextBosnian = "U tome su, zaista, znaci za one koji o tome pronicljivo razmišljaju,", TextEnglish = "Indeed in that are signs for those who discern.", TextGerman = "Darin sind wahrlich Zeichen für die Betrachtenden." });
                        surah15.VerseList.Add(new Verse { Number = 76, TextBosnian = "a naselje je baš uz postojeći put.", TextEnglish = "And indeed, they [i.e., those cities] are [situated] on an established road.", TextGerman = "Und sie liegt fürwahr an einem noch bestehenden Weg." });
                        surah15.VerseList.Add(new Verse { Number = 77, TextBosnian = "U tome su, zaista, znaci za one koji vjeruju.", TextEnglish = "Indeed in that is a sign for the believers.", TextGerman = "Darin ist wahrlich ein Zeichen für die Gläubigen." });
                        surah15.VerseList.Add(new Verse { Number = 78, TextBosnian = "A i stanovnici Ejke bili su zulumćari,", TextEnglish = "And the companions of the thicket [i.e., the people of Madyan] were [also] wrongdoers,", TextGerman = "Und die Bewohner des Dickichts taten fürwahr Unrecht," });
                        surah15.VerseList.Add(new Verse { Number = 79, TextBosnian = "pa smo ih kaznili, i oba naselja su uz put koji se vidi.", TextEnglish = "So We took retribution from them, and indeed, both [cities] are on a clear highway.", TextGerman = "so übten Wir an ihnen Vergeltung. Beide liegen fürwahr an einem deutlichen Weg." });
                        surah15.VerseList.Add(new Verse { Number = 80, TextBosnian = "I stanovnici Hidžra su poslanike lažnim smatrali,", TextEnglish = "And certainly did the companions of al-Ḥijr[683] [i.e., the Thamūd] deny the messengers.", TextGerman = "Und die Bewohner von al-Higr bezichtigten die Gesandten der Lüge." });
                        surah15.VerseList.Add(new Verse { Number = 81, TextBosnian = "a Mi smo im znakove Naše bili dali, ali su se oni od njih okretali.", TextEnglish = "And We gave them Our signs, but from them they were turning away.", TextGerman = "Wir ließen ihnen Unsere Zeichen zukommen, aber sie wandten sich von ihnen ab." });
                        surah15.VerseList.Add(new Verse { Number = 82, TextBosnian = "Oni su kuće u brdima klesali, vjerujući da su sigurni,", TextEnglish = "And they used to carve from the mountains, houses, feeling secure.", TextGerman = "Und sie hauten aus den Bergen Häuser aus, im Trachten nach Sicherheit." });
                        surah15.VerseList.Add(new Verse { Number = 83, TextBosnian = "pa i njih u svitanje strašan glas zadesi,", TextEnglish = "But the shriek seized them at early morning,", TextGerman = "Da ergriff sie der Schrei bei Tagesanbruch;" });
                        surah15.VerseList.Add(new Verse { Number = 84, TextBosnian = "i ne bijaše im ni od kakve koristi ono što su stjecali.", TextEnglish = "So nothing availed them [from] what they used to earn.", TextGerman = "so nützte ihnen nicht, was sie erworben hatten." });
                        surah15.VerseList.Add(new Verse { Number = 85, TextBosnian = "Mi smo nebesa i Zemlju i ono između njih s Istinom stvorili. Čas oživljenja će zacijelo doći, zato ti velikodušno oprosti,", TextEnglish = "And We have not created the heavens and earth and that between them except in truth. And indeed, the Hour is coming; so forgive with gracious forgiveness.", TextGerman = "Wir haben die Himmel und die Erde und was dazwischen ist nur in Wahrheit erschaffen. Gewiß, die Stunde wird sicher eintreffen. So übe schöne Nachsicht." });
                        surah15.VerseList.Add(new Verse { Number = 86, TextBosnian = "Gospodar tvoj je, zaista, Stvoritelj svega, Sveznajući.", TextEnglish = "Indeed, your Lord - He is the Knowing Creator.", TextGerman = "Dein Herr ist der Allerschaffer und der Allwissende." });
                        surah15.VerseList.Add(new Verse { Number = 87, TextBosnian = "Mi smo ti dali sedam ajeta, koji se ponavljaju a i Kur'an veličanstveni.", TextEnglish = "And We have certainly given you, [O Muḥammad], seven of the often repeated [verses][684] and the great Qur’ān.", TextGerman = "Und Wir haben dir doch sieben von den sich wiederholenden Versen gegeben, und (auch) den großartigen Qur’an." });
                        surah15.VerseList.Add(new Verse { Number = 88, TextBosnian = "Ne pružaj poglede svoje na ono što Mi dajemo na uživanje nekima od njih i ne budi tužan zbog njih, a prema vjernicima blag budi,", TextEnglish = "Do not extend your eyes toward that by which We have given enjoyment to [certain] categories of them [i.e., the disbelievers], and do not grieve over them. And lower your wing [i.e., show kindness] to the believers.", TextGerman = "Richte ja nicht deine Augen auf das, was Wir manchen von ihnen paarweise als Nießbrauch gewähren. Und sei nicht traurig über sie. Und senke deinen Flügel für die Gläubigen," });
                        surah15.VerseList.Add(new Verse { Number = 89, TextBosnian = "i reci: 'Ja sam samo upozoritelj jasni.'", TextEnglish = "And say, 'Indeed, I am the clear warner' -", TextGerman = "und sag: Ich bin ja der deutliche Warner." });
                        surah15.VerseList.Add(new Verse { Number = 90, TextBosnian = "Isto tako smo i urotitelje opomenuli,", TextEnglish = "Just as We had revealed [scriptures] to the separators[685]", TextGerman = "Wie Wir (die Strafe) auf diejenigen hinabgesandt haben, die aufteilten," });
                        surah15.VerseList.Add(new Verse { Number = 91, TextBosnian = "one koji Kur'an na dijelove podijeliše.", TextEnglish = "Who have made the Qur’ān into portions.[686]", TextGerman = "die den Qur’an (in einzelne Teile) zergliedert haben." });
                        surah15.VerseList.Add(new Verse { Number = 92, TextBosnian = "I tako Mi Gospodara tvoga, njih ćemo sve na odgovornost pozvati", TextEnglish = "So by your Lord, We will surely question them all", TextGerman = "Ja, bei deinem Herrn! Wir werden sie allesamt ganz gewiß befragen" });
                        surah15.VerseList.Add(new Verse { Number = 93, TextBosnian = "za ono što su radili!", TextEnglish = "About what they used to do.", TextGerman = "über das, was sie zu tun pflegten." });
                        surah15.VerseList.Add(new Verse { Number = 94, TextBosnian = "Ti javno ispovijedaj ono što ti se naređuje i višebožaca se okani.", TextEnglish = "Then declare what you are commanded[687] and turn away from the polytheists.[688]", TextGerman = "So verkünde denn laut, was dir befohlen wird, und wende dich von den Götzendienern ab." });
                        surah15.VerseList.Add(new Verse { Number = 95, TextBosnian = "Zaista ćemo te sačuvati onih koji se rugaju,", TextEnglish = "Indeed, We are sufficient for you against the mockers", TextGerman = "Wir genügen dir (als Schutz) vor den Spöttern," });
                        surah15.VerseList.Add(new Verse { Number = 96, TextBosnian = "koji pored Allaha drugog boga uzimaju; i znat će oni!", TextEnglish = "Who make [equal] with Allāh another deity. But they are going to know.", TextGerman = "die neben Allah einen anderen Gott setzen. Aber sie werden (es noch) erfahren." });
                        surah15.VerseList.Add(new Verse { Number = 97, TextBosnian = "Mi dobro znamo da ti se u prsima steže zbog onoga što oni govore,", TextEnglish = "And We already know that your breast is constrained by what they say.", TextGerman = "Wir wissen ja, daß deine Brust beklommen ist wegen dessen, was sie sagen." });
                        surah15.VerseList.Add(new Verse { Number = 98, TextBosnian = "zato, veličaj Gospodara svoga i hvali Ga, i budi od onih što na sedždu padaju,", TextEnglish = "So exalt [Allāh] with praise of your Lord and be of those who prostrate [to Him].", TextGerman = "Aber lobpreise deinen Herrn und gehöre zu denen, die sich niederwerfen," });
                        surah15.VerseList.Add(new Verse { Number = 99, TextBosnian = "i Gospodaru svome ibadet čini sve dok ti smrt - ono što je neupitno - ne dođe!", TextEnglish = "And worship your Lord until there comes to you the certainty [i.e., death].", TextGerman = "und diene deinem Herrn, bis die Gewißheit zu dir kommt." });
                        result = surah15;
                        break;
                    #endregion

                    // 
                    #region 16 An-Nahl
                    case 16:
                        Surah surah16 = new Surah(); surah16.Name = Constants.Surah16Name; surah16.Number = 16; surah16.VerseList = new List<Verse>();
                        surah16.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah16.VerseList.Add(new Verse { Number = 1, TextBosnian = "Allahova odredba stiže, i ne požurujte je! Uzvišen je On i vrlo visoko iznad onoga što Mu pridružuju!", TextEnglish = "The command of Allāh is coming,[690] so be not impatient for it. Exalted is He and high above what they associate with Him.", TextGerman = "Der Befehl Allahs ist (so gut wie) eingetroffen, so wünscht nicht, ihn zu beschleunigen. Preis sei Ihm! Erhaben ist Er über das, was sie (Ihm) beigesellen." });
                        surah16.VerseList.Add(new Verse { Number = 2, TextBosnian = "On meleke s Objavom, po odredbi Svojoj, šalje onim robovima Svojim kojima hoće: 'Upozoravajte da nema drugog boga osim Mene i bojte Me se!'", TextEnglish = "He sends down the angels, with the inspiration [i.e., revelation] of His command, upon whom He wills of His servants, [telling them], 'Warn that there is no deity except Me; so fear Me.'", TextGerman = "Er sendet die Engel mit dem Geist von Seinem Befehl herab, auf wen von Seinen Dienern Er will: Warnt (und verkündet), daß es keinen Gott gibt außer Mir; darum fürchtet Mich (allein)." });
                        surah16.VerseList.Add(new Verse { Number = 3, TextBosnian = "On je s Istinom nebesa i Zemlju stvorio, neka je On vrlo visoko iznad onoga što Mu pridružuju.", TextEnglish = "He created the heavens and earth in truth. High is He above what they associate with Him.", TextGerman = "Er hat die Himmel und die Erde in Wahrheit erschaffen. Erhaben ist Er über das, was sie (Ihm) beigesellen." });
                        surah16.VerseList.Add(new Verse { Number = 4, TextBosnian = "On stvara čovjeka od kapi sjemena, a on poslije toga otvoreno, svađalački osporava!", TextEnglish = "He created man from a sperm-drop; then at once[691] he is a clear adversary.", TextGerman = "Er hat den Menschen aus einem Samentropfen erschaffen, und doch ist er sogleich ein deutlicher Widersacher." });
                        surah16.VerseList.Add(new Verse { Number = 5, TextBosnian = "I stoku On za vas stvara; njome se od hladnoće štitite, a i drugih koristi imate, njome se i hranite;", TextEnglish = "And the grazing livestock He has created for you; in them is warmth[692] and [numerous] benefits, and from them you eat.", TextGerman = "Und (auch) das Vieh hat Er erschaffen. An ihm habt ihr Wärme und (allerlei anderen) Nutzen; und davon eßt ihr." });
                        surah16.VerseList.Add(new Verse { Number = 6, TextBosnian = "ona vam je ukras kad je sa ispaše vraćate i kad je na pašu izgonite.", TextEnglish = "And for you in them is [the enjoyment of] beauty when you bring them in [for the evening] and when you send them out [to pasture].", TextGerman = "Und ihr habt an ihnen Schönes, wenn ihr (sie abends) eintreibt und wenn ihr (sie) morgens austreibt." });
                        surah16.VerseList.Add(new Verse { Number = 7, TextBosnian = "A nosi vam i terete u mjesta u koja, osim uz velike muke, ne biste stigli. Gospodar vaš je, uistinu, milosrdan i milostiv.", TextEnglish = "And they carry your loads to a land you could not have reached except with difficulty to yourselves. Indeed, your Lord is Kind and Merciful.", TextGerman = "Und sie tragen eure Lasten in ein Land, das ihr (sonst) nur mit größter Mühe hättet erreichen können. Euer Herr ist wahrlich Gnädig und Barmherzig." });
                        surah16.VerseList.Add(new Verse { Number = 8, TextBosnian = "I konje, i mazge, i magarce - da ih jašete, i kao ukras - a stvorit će i ono što ne znate.", TextEnglish = "And [He created] the horses, mules and donkeys for you to ride and [as] adornment. And He creates that which you do not know.", TextGerman = "Und (erschaffen hat Er) die Pferde, die Maultiere und die Esel, damit ihr auf ihnen reitet, und (auch) als Schmuck. Und Er erschafft, was ihr nicht wißt." });
                        surah16.VerseList.Add(new Verse { Number = 9, TextBosnian = "Ka Allahu vodi samo Pravi put - put Istine, a ima ih i krivih, a da On hoće, sve bi vas uputio.", TextEnglish = "And upon Allāh[693] is the direction of the [right] way, and among them [i.e., the various paths] are those deviating. And if He willed, He could have guided you all.", TextGerman = "Allah obliegt es, (euch) auf dem richtigen Weg zu halten. Es gibt ja (auch) manche, die abweichen. Wenn Er gewollt hätte, hätte Er euch fürwahr allesamt rechtgeleitet." });
                        surah16.VerseList.Add(new Verse { Number = 10, TextBosnian = "On je Taj Koji s neba spušta vodu koju pijete i ona pomaže da iznikne bilje kojim stoku napasate.", TextEnglish = "It is He who sends down rain from the sky; from it is drink and from it is foliage in which you pasture [animals].", TextGerman = "Er ist es, Der vom Himmel Wasser herabkommen läßt; davon habt ihr zu trinken, und davon (wachsen) Bäume, unter denen ihr (euer Vieh) frei weiden laßt." });
                        surah16.VerseList.Add(new Verse { Number = 11, TextBosnian = "On vam daje da pomoću nje iznikne žito, i masline, i palme, i grožđe, i svakovrsni plodovi - u tome je, zaista, znak za ljude koji razmišljaju.", TextEnglish = "He causes to grow for you thereby the crops, olives, palm trees, grapevines, and of all the fruits. Indeed in that is a sign for a people who give thought.", TextGerman = "Er läßt euch damit Getreide wachsen, und Ölbäume, Palmen, Rebstöcke und von allen Früchten. Darin ist wahrlich ein Zeichen für Leute, die nachdenken." });
                        surah16.VerseList.Add(new Verse { Number = 12, TextBosnian = "On vam je potčinio noć i dan, i Sunce i Mjesec, i zvijezde su po odredbi Njegovoj potčinjene. U tome su, uistinu, znakovi za ljude koji razumiju;", TextEnglish = "And He has subjected for you the night and day and the sun and moon, and the stars are subjected by His command. Indeed in that are signs for a people who reason.", TextGerman = "Und Er hat euch die Nacht und den Tag, die Sonne und den Mond dienstbar gemacht; und (auch) die Sterne sind durch Seinen Befehl dienstbar gemacht worden. Darin sind wahrlich Zeichen für Leute, die begreifen." });
                        surah16.VerseList.Add(new Verse { Number = 13, TextBosnian = "- i sve što vam je na Zemlji šaroliko stvorio - u tome je, doista, znak ljudima koji se prisjećaju i pouku uzimaju.", TextEnglish = "And [He has subjected] whatever He multiplied for you on the earth of varying colors. Indeed in that is a sign for a people who remember.", TextGerman = "Und (dienstbar gemacht ist auch,) was Er euch auf der Erde in unterschiedlichen Farben hat wachsen lassen. Darin ist wahrlich ein Zeichen für Leute, die bedenken." });
                        surah16.VerseList.Add(new Verse { Number = 14, TextBosnian = "I On je Taj Koji je more potčinio, da iz njega svježe meso jedete i da vadite nakit koji nosite - ti vidiš lađe kako ga sijeku da biste tragali za Njegovim obiljem i da biste zahvaljivali.", TextEnglish = "And it is He who subjected the sea for you to eat from it tender meat and to extract from it ornaments which you wear. And you see the ships plowing through it, and [He subjected it] that you may seek of His bounty; and perhaps you will be grateful.", TextGerman = "Und Er ist es, Der euch das Meer dienstbar gemacht hat, damit ihr frisches Fleisch daraus eßt und Schmuck aus ihm hervorholt, den ihr anlegt. Und du siehst die Schiffe es durchpflügen, damit ihr nach etwas von Seiner Huld trachtet und auf daß ihr dankbar sein möget!" });
                        surah16.VerseList.Add(new Verse { Number = 15, TextBosnian = "I po Zemlji je nepomična brda postavio da vas ona ne potrese, i rijeke, a i puteve da se usmjeravate,", TextEnglish = "And He has cast into the earth firmly set mountains, lest it shift with you, and [made] rivers and roads, that you may be guided,", TextGerman = "Und Er hat auf der Erde festgegründete Berge gesetzt, daß sie nicht mit euch wanke, und Flüsse und Wege (geschaffen) - auf daß ihr rechtgeleitet werden möget" });
                        surah16.VerseList.Add(new Verse { Number = 16, TextBosnian = "a i putokaze, a i po zvijezdama se oni upravljaju:", TextEnglish = "And landmarks. And by the stars they are [also] guided.[694]", TextGerman = "und als (Weg)zeichen. Und mit Hilfe der Sterne werden sie geleitet." });
                        surah16.VerseList.Add(new Verse { Number = 17, TextBosnian = "Pa da li je onda Onaj Koji stvara kao onaj koji ne stvara?! Zašto se ne prisjećate i pouku ne uzmete?!", TextEnglish = "Then is He who creates like one who does not create? So will you not be reminded?", TextGerman = "Ist denn Derjenige, Der erschafft, wie derjenige, der nicht erschafft? Bedenkt ihr denn nicht?" });
                        surah16.VerseList.Add(new Verse { Number = 18, TextBosnian = "Ako vi budete brojili Allahove blagodati, nećete ih nabrojati; Allah je, uistinu, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And if you should count the favors of Allāh, you could not enumerate them. Indeed, Allāh is Forgiving and Merciful.", TextGerman = "Und wenn ihr die Gunst(erweise) Allahs aufzählen wolltet, könntet ihr sie nicht erfassen. Allah ist wahrlich Allvergebend und Barmherzig." });
                        surah16.VerseList.Add(new Verse { Number = 19, TextBosnian = "Allah zna šta tajite, a šta javno iznosite.", TextEnglish = "And Allāh knows what you conceal and what you declare.", TextGerman = "Und Allah weiß, was ihr geheimhaltet und was ihr offenlegt." });
                        surah16.VerseList.Add(new Verse { Number = 20, TextBosnian = "A oni koje oni mjesto Allaha mole - ništa ne stvaraju; oni su sami stvoreni;", TextEnglish = "And those they invoke other than Allāh create nothing, and they [themselves] are created.", TextGerman = "Und diejenigen, die sie anstatt Allahs anrufen, erschaffen nichts, während sie selbst erschaffen werden." });
                        surah16.VerseList.Add(new Verse { Number = 21, TextBosnian = "mrtvi su, nisu živi i ne znaju kada će biti oživljeni.", TextEnglish = "They are [in fact] dead,[695] not alive, and they do not perceive when they will be resurrected.", TextGerman = "Tot (sind sie), nicht lebendig; und sie merken nicht, wann sie auferweckt werden." });
                        surah16.VerseList.Add(new Verse { Number = 22, TextBosnian = "Vaš Bog je- jedan Bog! Srca onih koji u ahiret ne vjeruju, poriču; oni su oholi.", TextEnglish = "Your god is one God. But those who do not believe in the Hereafter - their hearts are disapproving, and they are arrogant.", TextGerman = "Euer Gott ist ein Einziger Gott. Diejenigen aber, die nicht an das Jenseits glauben, deren Herzen weisen (es) ab, und sie verhalten sich hochmütig." });
                        surah16.VerseList.Add(new Verse { Number = 23, TextBosnian = "Nema sumnje da Allah zna i ono što oni taje, a i ono što javno iznose; On, doista, ne voli one koji se ohole.", TextEnglish = "Assuredly, Allāh knows what they conceal and what they declare. Indeed, He does not like the arrogant.", TextGerman = "Zweifellos weiß Allah, was sie geheimhalten und was sie offenlegen. Gewiß, Er liebt nicht die Hochmütigen." });
                        surah16.VerseList.Add(new Verse { Number = 24, TextBosnian = "A kada ih neko upita: 'Šta je to Gospodar vaš objavio?', oni odgovaraju: 'Izmišljotine naroda nekadašnjih!'", TextEnglish = "And when it is said to them, 'What has your Lord sent down?' they say, 'Legends of the former peoples,'", TextGerman = "Und wenn zu ihnen gesagt wird: Was hat euer Herr (als Offenbarung) herabgesandt?, sagen sie: (Es sind) Fabeln der Früheren." });
                        surah16.VerseList.Add(new Verse { Number = 25, TextBosnian = "To je zato da bi na Kijametskom danu nosili grijehe - terete svoje cijele i dio grijeha onih koje su, a da oni nisu bili svjesni, u zabludu doveli.:' Užasan je teret koji će oni nositi!", TextEnglish = "That they may bear their own burdens [i.e., sins] in full on the Day of Resurrection and some of the burdens of those whom they misguide without [i.e., by lack of] knowledge. Unquestionably, evil is that which they bear.", TextGerman = "Deshalb sollen sie am Tag der Auferstehung ihre Lasten vollständig tragen, und (auch etwas) von den Lasten derjenigen, die sie ohne (richtiges) Wissen in die Irre führten. Wie böse ist das, was sie an Last auf sich nehmen!" });
                        surah16.VerseList.Add(new Verse { Number = 26, TextBosnian = "I oni prije njih spletke su pravili, pa je Allah iz temelja zgrade njihove porušio, i krov se na njih srušio - stigla ih je kazna odakle nisu ni očekivali.", TextEnglish = "Those before them had already plotted, but Allāh came at [i.e., uprooted] their building from the foundations, so the roof fell upon them from above them,[696] and the punishment came to them from where they did not perceive.", TextGerman = "Ränke schmiedeten bereits diejenigen, die vor ihnen waren. Da ging Allah ihren Bau an den Grundmauern an, so daß das Dach über ihnen auf sie herabfiel, und die Strafe über sie kam, von wo sie nicht merkten." });
                        surah16.VerseList.Add(new Verse { Number = 27, TextBosnian = "A na Kijametskom danu On će ih osramotiti i upitati: 'Gdje su oni koje ste Meni u obožavanju pridruživali, oni zbog kojih ste se suprotstavljali?' Oni kojima je dato znanje reći će: 'Danas će sramota i muka nevjernike, zaista, snaći,", TextEnglish = "Then on the Day of Resurrection He will disgrace them and say, 'Where are My 'partners' for whom you used to oppose [the believers]?' Those who were given knowledge will say, 'Indeed disgrace, this Day, and evil are upon the disbelievers' -", TextGerman = "Hierauf, am Tag der Auferstehung, wird Er sie in Schande stürzen und sagen: Wo sind Meine Teilhaber, derentwegen ihr (Mir) entgegenwirktet? Diejenigen, denen das Wissen gegeben wurde, sagen: Schande kommt heute und Böses über die Ungläubigen," });
                        surah16.VerseList.Add(new Verse { Number = 28, TextBosnian = "kojima su meleci duše uzeli u vrijeme kada su oni sebi zulum činili.' I oni će se pokoriti i reći: 'Mi nismo nikakvo zlo činili!' 'Jeste, Allah, doista dobro zna ono što ste radili.", TextEnglish = "The ones whom the angels take in death [while] wronging themselves,[697] and [who] then offer submission, [saying], 'We were not doing any evil.' But, yes! Indeed, Allāh is Knowing of what you used to do.", TextGerman = "die die Engel abberufen, während sie sich selbst Unrecht zugefügt haben. Sie werden Frieden anbieten: Wir pflegten nichts Böses zu tun. - Doch, Allah weiß Bescheid über das, was ihr zu tun pflegtet." });
                        surah16.VerseList.Add(new Verse { Number = 29, TextBosnian = "Zato, ulazite na kapije Džehennema, u njemu ćete vječno ostati! O kako će prebivalište onih koji su se oholili- grozno biti!'", TextEnglish = "So enter the gates of Hell to abide eternally therein, and how wretched is the residence of the arrogant.", TextGerman = "So betretet die Tore der Hölle, ewig darin zu bleiben. Schlimm ist fürwahr der Aufenthaltsort der Hochmütigen." });
                        surah16.VerseList.Add(new Verse { Number = 30, TextBosnian = "A onima koji su se Allaha bojali i grijeha klonili reći će se: 'Šta je objavljivao Gospodar vaš?' 'Dobro!', odgovorit će. Oni koji čine dobra djela imat će već na dunjaluku lijepu nagradu, a ahiret je, sigurno, još bolji. O kako će divno biti boravište onih koji su se Allaha bojali:", TextEnglish = "And it will be said to those who feared Allāh, 'What did your Lord send down?' They will say, '[That which is] good.' For those who do good in this world is good; and the home of the Hereafter is better. And how excellent is the home of the righteous -", TextGerman = "Zu denjenigen, die gottesfürchtig sind, wird gesagt: Was hat euer Herr herabgesandt? Sie sagen: Gutes. Für diejenigen, die Gutes tun, gibt es hier im Diesseits Gutes; aber die Wohnstätte des Jenseits ist wahrlich besser. Wie trefflich ist fürwahr die Wohnstätte der Gottesfürchtigen:" });
                        surah16.VerseList.Add(new Verse { Number = 31, TextBosnian = "dženneti Adna u koje će ući, gdje rijeke teku, i gdje će sve što zažele imati. Tako će Allah one koji Ga se budu bojali nagraditi,", TextEnglish = "Gardens of perpetual residence, which they will enter, beneath which rivers flow. They will have therein whatever they wish. Thus does Allāh reward the righteous -", TextGerman = "die Gärten Edens, in die sie eingehen, durcheilt von Bächen, und in denen sie (alles) haben, was sie wollen. So vergilt (es) Allah den Gottesfürchtigen," });
                        surah16.VerseList.Add(new Verse { Number = 32, TextBosnian = "one kojima meleki dušu uzimaju dok su čestiti, i kojima govore: 'Selam vama! Uđite u Džennet zbog onoga što ste činili!'", TextEnglish = "The ones whom the angels take in death, [being] good and pure; [the angels] will say, 'Peace be upon you. Enter Paradise for what you used to do.'", TextGerman = "die die Engel abberufen, während sie gute (Menschen) gewesen sind. Sie sagen: Friede sei auf euch! Geht in den (Paradies)garten ein für das, was ihr zu tun pflegtet." });
                        surah16.VerseList.Add(new Verse { Number = 33, TextBosnian = "Šta li višebošci čekaju: da im dođu meleci, ili da dođe odredba Gospodara tvoga?! Tako su postupili i oni prije njih; njima nije zulum učinio Allah, oni su sami sebi zulum učinili.", TextEnglish = "Do they [i.e., the disbelievers] await except that the angels should come to them or there comes the command of your Lord? Thus did those do before them. And Allāh wronged them not, but they had been wronging themselves.", TextGerman = "Erwarten sie denn (etwas anderes), als daß die Engel zu ihnen kommen oder daß der Befehl deines Herrn eintrifft? So handelten (auch) diejenigen, die vor ihnen waren. Allah fügte ihnen kein Unrecht zu, sondern sie selbst fügten sich Unrecht zu." });
                        surah16.VerseList.Add(new Verse { Number = 34, TextBosnian = "I stigla ih je kazna za ono što su činili, i sa svih strana ih je obuzelo ono čemu su se rugali.", TextEnglish = "So they were struck by the evil consequences of what they did and were enveloped by what they used to ridicule.", TextGerman = "So trafen sie (schließlich) die bösen Taten, die sie begangen hatten, und es umschloß sie das, worüber sie sich lustig zu machen pflegten." });
                        surah16.VerseList.Add(new Verse { Number = 35, TextBosnian = "I rekli su oni koji su druge Njemu u obožavanju pridruživali: 'Da je Allah htio, ne bismo ni mi ni preci naši, pored Allaha, nikoga obožavali i ne bismo ništa, bez Njegove volje, zabranjenim smatrali. Baš tako su i oni prije njih postupali. A zar su poslanici bili dužni šta drugo već da jasno obznane?!", TextEnglish = "And those who associate others with Allāh say, 'If Allāh had willed, we would not have worshipped anything other than Him, neither we nor our fathers, nor would we have forbidden anything through other than Him.' Thus did those do before them. So is there upon the messengers except [the duty of] clear notification?", TextGerman = "Diejenigen, die (Allah etwas) beigesellen, sagen: Wenn Allah gewollt hätte, hätten wir nichts anstatt Seiner gedient, weder wir noch unsere Vater, und wir (selbst) hätten anstatt Seiner nichts verboten. So handelten (auch) diejenigen, die vor ihnen waren. Obliegt denn den Gesandten etwas anderes als die deutliche Übermittlung (der Botschaft)?" });
                        surah16.VerseList.Add(new Verse { Number = 36, TextBosnian = "Mi smo svakom narodu poslanika poslali: 'Allahu ibadet činite, a šejtana - taguta se klonite!' I bilo je među njima onih koje je Allah uputio, a i onih koji su zaslužili zabludu. Zato putujte Zemljom da vidite kakav je bio svršetak onih koji su poslanike u laž utjerivali.", TextEnglish = "And We certainly sent into every nation a messenger, [saying], 'Worship Allāh and avoid ṭāghūt.'[698] And among them were those whom Allāh guided, and among them were those upon whom error was [deservedly] decreed. So proceed [i.e., travel] through the earth and observe how was the end of the deniers.", TextGerman = "Und Wir haben ja bereits in jeder Gemeinschaft einen Gesandten erweckt: Dient Allah und meidet die falschen Götter. Unter ihnen gibt es manche, die Allah rechtgeleitet hat, und unter ihnen gibt es manche, an denen sich das Irregehen bewahrheitet hat. So reist auf der Erde umher und schaut, wie das Ende der Leugner war." });
                        surah16.VerseList.Add(new Verse { Number = 37, TextBosnian = "Ma koliko ti želio da im uputa bude data, Allah, zaista, neće uputiti nikoga koga u zabludu okrene i njima niko neće pomoći.", TextEnglish = "[Even] if you should strive for their guidance, [O Muḥammad], indeed, Allāh does not guide those He sends astray,[699] and they will have no helpers.", TextGerman = "Selbst wenn du noch so (danach) trachtest, sie rechtzuleiten, so leitet Allah nicht recht, wen Er (nun einmal) in die Irre gehen läßt; und sie werden keine Helfer haben." });
                        surah16.VerseList.Add(new Verse { Number = 38, TextBosnian = "Oni se zaklinju Allahom, učvršćujući zakletve svoje: 'Allah neće oživiti onoga koji umre!' Ali hoće! To Njegovo obećanje doista će se ispuniti, ali to većina ljudi ne zna.", TextEnglish = "And they swear by Allāh their strongest oaths [that] Allāh will not resurrect one who dies. But yes - [it is] a true promise [binding] upon Him, but most of the people do not know.", TextGerman = "Und sie schwören bei Allah ihren kräftigsten Eid, Allah werde denjenigen nicht auferwecken, der stirbt. Doch (das ist) ein für Ihn bindendes Versprechen in Wahrheit - aber die meisten Menschen wissen nicht -," });
                        surah16.VerseList.Add(new Verse { Number = 39, TextBosnian = "Da bi im objasnio ono u čemu su se razilazili, i da bi saznali oni koji nisu vjerovali da su lašci bili.", TextEnglish = "[It is] so He will make clear to them [the truth of] that wherein they differ and so those who have disbelieved may know that they were liars.", TextGerman = "damit Er ihnen das klar macht, worüber sie uneinig sind, und damit diejenigen, die ungläubig sind, wissen, daß sie Lügner waren." });
                        surah16.VerseList.Add(new Verse { Number = 40, TextBosnian = "Ako nešto hoćemo, Mi samo za to reknemo: 'Budi!', i ono bude.", TextEnglish = "Indeed, Our word to a thing when We intend it is but that We say to it, 'Be,' and it is.", TextGerman = "Unser Wort zu etwas, wenn Wir es wollen, ist, dazu nur zu sagen: Sei!, und so ist es." });
                        surah16.VerseList.Add(new Verse { Number = 41, TextBosnian = "One koji su se iselili radi Allaha, nakon što su bili tlačeni, Mi ćemo još na ovom svijetu na lijepo mjesto smjestiti; a nagrada na ahiretu bit će još veća - kad bi samo oni što su izostali znali.'", TextEnglish = "And those who emigrated for [the cause of] Allāh after they had been wronged - We will surely settle them in this world in a good place; but the reward of the Hereafter is greater, if only they could know.", TextGerman = "Und diejenigen, die um Allahs willen ausgewandert sind, nachdem ihnen Unrecht zugefügt wurde, denen werden Wir ganz gewiß im Diesseits Gutes zuweisen. Aber der Lohn des Jenseits ist wahrlich (noch) größer, wenn sie (es) nur wüßten!" });
                        surah16.VerseList.Add(new Verse { Number = 42, TextBosnian = "Iselili su se oni koji su, oslanjajući se na Gospodara svoga, strpljivi bili.", TextEnglish = "[They are] those who endured patiently and upon their Lord relied.", TextGerman = "(Sie), die standhaft sind und sich auf ihren Herrn verlassen." });
                        surah16.VerseList.Add(new Verse { Number = 43, TextBosnian = "Mi smo i prije tebe samo ljude kao poslanike s objavom slali; ako za to ne znate, pitajte sljedbenike Knjige!", TextEnglish = "And We sent not before you except men to whom We revealed [Our message]. So ask the people of the message [i.e., former scriptures] if you do not know.", TextGerman = "Und Wir haben vor dir nur Männer gesandt, denen Wir (Offenbarungen) eingegeben haben. So fragt die Leute der Ermahnung, wenn ihr (etwas) nicht wißt." });
                        surah16.VerseList.Add(new Verse { Number = 44, TextBosnian = "Slali smo ih s jasnim dokazima i knjigama, a tebi spuštamo Opomenu, da bi objasnio ljudima ono što im se objavljuje, i da bi oni razmislili.", TextEnglish = "[We sent them] with clear proofs and written ordinances. And We revealed to you the message [i.e., the Qur’ān] that you may make clear to the people what was sent down to them and that they might give thought.", TextGerman = "(Wir haben sie gesandt) mit den klaren Beweisen und den Büchern der Weisheit. Und Wir haben zu dir die Ermahnung hinabgesandt, damit du den Menschen klar machst, was ihnen offenbart worden ist, und auf daß sie nachdenken mögen." });
                        surah16.VerseList.Add(new Verse { Number = 45, TextBosnian = "A zar su sigurni oni koji podmuklo prave zlo - da ih Allah neće u zemlju utjerati, ili da im neće, odakle ne mogu ni pomisliti, kazna doći,", TextEnglish = "Then, do those who have planned evil deeds feel secure that Allāh will not cause the earth to swallow them or that the punishment will not come upon them from where they do not perceive?", TextGerman = "Glauben denn diejenigen, die böse Ränke schmieden, sicher davor zu sein, daß Allah mit ihnen die Erde versinken läßt oder (daß) die Strafe über sie kommt, von wo sie nicht merken," });
                        surah16.VerseList.Add(new Verse { Number = 46, TextBosnian = "ili da ih neće na putovanjima njihovim kazniti, pa da neće umaći,", TextEnglish = "Or that He would not seize them during their [usual] activity, and they could not cause failure [i.e., escape from Him]?", TextGerman = "oder daß Er sie in ihrem Umherziehen ergreift, ohne daß sie sich (Ihm) entziehen können," });
                        surah16.VerseList.Add(new Verse { Number = 47, TextBosnian = "ili da ih neće, dok su u strahu, kažnjavati? Ali Gospodar vaš je, doista, milosrdan i milostiv.", TextEnglish = "Or that He would not seize them gradually [in a state of dread]?[700] But indeed, your Lord is Kind and Merciful.[701]", TextGerman = "oder daß Er sie in Verängstigung ergreift? Euer Herr ist wahrlich Gnädig und Barmherzig." });
                        surah16.VerseList.Add(new Verse { Number = 48, TextBosnian = "Zar oni ne vide da sve ono što je Allah stvorio sad desno, sad lijevo, pruža sjene svoje Allahu sedždu čineći i da je sve to pokorno?", TextEnglish = "Have they not considered what things Allāh has created? Their shadows incline to the right and to the left, prostrating to Allāh, while they [i.e., those creations] are humble.", TextGerman = "Haben sie nicht auf (all) die Dinge gesehen, die Allah erschaffen hat, wie sich ihre Schatten nach rechts und links wenden, indem sie sich demütigend vor Allah niederwerfen?" });
                        surah16.VerseList.Add(new Verse { Number = 49, TextBosnian = "Allahu sedždu čini sve živo na nebesima i na Zemlji, i meleci, a oni se ne ohole,", TextEnglish = "And to Allāh prostrates whatever is in the heavens and whatever is on the earth of creatures, and the angels [as well], and they are not arrogant.", TextGerman = "Vor Allah wirft sich (alles) nieder, was in den Himmeln und was auf der Erde an Tieren ist, und (auch) die Engel, und sie verhalten sich nicht hochmütig." });
                        surah16.VerseList.Add(new Verse { Number = 50, TextBosnian = "boje se Gospodara svoga koji je iznad njih i čine ono što im se naredi.", TextEnglish = "They fear their Lord above them, and they do what they are commanded.", TextGerman = "Sie fürchten ihren Herrn über sich und tun, was ihnen befohlen wird." });
                        surah16.VerseList.Add(new Verse { Number = 51, TextBosnian = "Allah kaže: 'Dvojicu bogova za obožavanje ne uzimajte!', samo je jedan Bog, 'i samo od Mene strahujte!'", TextEnglish = "And Allāh has said, 'Do not take for yourselves two[702] deities. He [i.e., Allāh] is but one God, so fear only Me.'", TextGerman = "Allah hat gesagt: Nehmt euch nicht zwei Götter. Er ist nur ein Einziger Gott. Vor Mir (allein) sollt ihr Ehrfurcht haben." });
                        surah16.VerseList.Add(new Verse { Number = 52, TextBosnian = "Allahu pripada sve što je na nebesima i na Zemlji i Njemu treba uvijek poslušan biti. Zar nekoga drugog osim Allaha da se bojite?!", TextEnglish = "And to Him belongs whatever is in the heavens and the earth, and to Him is [due] worship constantly. Then is it other than Allāh that you fear?", TextGerman = "Ihm gehört (alles), was in den Himmeln und auf der Erde ist, und Sein ist immerwährend die Religion. Wollt ihr denn einen anderen als Allah fürchten?" });
                        surah16.VerseList.Add(new Verse { Number = 53, TextBosnian = "Svaka blagodat koju uživate od Allaha je, a čim vas nevolja kakva zadesi, opet od Njega glasno pomoć tražite.", TextEnglish = "And whatever you have of favor - it is from Allāh. Then when adversity touches you, to Him you cry for help.", TextGerman = "Was ihr an Gunst erfahrt, ist von Allah. Wenn euch hierauf ein Unheil widerfährt, so fleht ihr Ihn laut um Hilfe an." });
                        surah16.VerseList.Add(new Verse { Number = 54, TextBosnian = "A potom, kad vam On nevolju otkloni, neki od vas Gospodaru svome druge u obožavanju pridružuju,", TextEnglish = "Then when He removes the adversity from you, at once a party of you associates others with their Lord", TextGerman = "Doch wenn Er hierauf das Unheil von euch hinweggenommen hat, ist da gleich eine Gruppe von euch dabei, ihrem Herrn (andere) beizugesellen," });
                        surah16.VerseList.Add(new Verse { Number = 55, TextBosnian = "da bi zanijekali ono što smo im Mi dali. Pa uživajte, ali zbilja, znat ćete!", TextEnglish = "So they will deny what We have given them. Then enjoy yourselves, for you are going to know.", TextGerman = "um undankbar zu sein für das, was Wir ihnen gaben. So genießt nur; ihr werdet (es noch) erfahren." });
                        surah16.VerseList.Add(new Verse { Number = 56, TextBosnian = "Višebošci onima koje kako treba ne poznaju, određuju dio onoga čime smo ih opskrbili. Allaha Mi, bit ćete sigurno pitani za ono što ste stalno izmišljali!", TextEnglish = "And they assign to what they do not know[703] [i.e., false deities] a portion of that which We have provided them. By Allāh, you will surely be questioned about what you used to invent.", TextGerman = "Und sie bestimmen für das, was sie nicht kennen, einen Anteil von dem, womit Wir sie versorgt haben. Bei Allah, ihr werdet ganz gewiß danach befragt werden, was ihr zu ersinnen pflegtet." });
                        surah16.VerseList.Add(new Verse { Number = 57, TextBosnian = "Oni Allahu kćeri pripisuju - Uzvišen je On! - a sebi ono što priželjkuju.", TextEnglish = "And they attribute to Allāh daughters[704] - exalted is He - and for them is what they desire [i.e., sons].", TextGerman = "Und sie bestimmen für Allah die Töchter - Preis sei Ihm! - und für sich (selbst), was sie begehren." });
                        surah16.VerseList.Add(new Verse { Number = 58, TextBosnian = "I kad nekom od njih bude donesena vijest da mu se rodila kći, lice mu se ojađenom smrkne,", TextEnglish = "And when one of them is informed of [the birth of] a female, his face becomes dark, and he suppresses grief.", TextGerman = "Wenn einem von ihnen die frohe Botschaft (von der Geburt) eines Mädchens verkündet wird, bleibt sein Gesicht finster, und er hält (seinen Grimm) zurück." });
                        surah16.VerseList.Add(new Verse { Number = 59, TextBosnian = "krije se od ljudi zbog loše vijesti koja mu je dojavljena: da li da je uz poniženje zadrži, ili u prašinu da je zarovi? Kako ružno oni prosuđuju!", TextEnglish = "He hides himself from the people because of the ill of which he has been informed. Should he keep it in humiliation or bury it in the ground? Unquestionably, evil is what they decide.", TextGerman = "Er verbirgt sich vor den Leuten wegen der schlimmen Nachricht, die ihm verkündet worden ist. Soll er es trotz der Schmach behalten oder es in die Erde stecken? Wie böse ist, was sie urteilen!" });
                        surah16.VerseList.Add(new Verse { Number = 60, TextBosnian = "Oni koji u ahiret ne vjeruju loša svojstva imaju, a Allah ima svojstva najuzvišenija: On je Silni i Mudri.", TextEnglish = "For those who do not believe in the Hereafter is the description [i.e., an attribute] of evil;[705] and for Allāh is the highest attribute. And He is Exalted in Might, the Wise.", TextGerman = "Diejenigen, die an das Jenseits nicht glauben, haben die Eigenschaft des Bösen. Allah aber hat die höchste Eigenschaft, und Er ist der Allmächtige und Allweise." });
                        surah16.VerseList.Add(new Verse { Number = 61, TextBosnian = "Kad bi Allah ljude zbog zuluma njihovog kažnjavao, ništa živo na Zemlji ne bi ostavio, ali, On ih do roka određenog ostavlja, i kad rok njihov prođe, ni za tren ga jedan ne mogu ni odgoditi ni ubrzati.", TextEnglish = "And if Allāh were to impose blame on the people for their wrongdoing, He would not have left upon it [i.e., the earth] any creature, but He defers them for a specified term. And when their term has come, they will not remain behind an hour, nor will they precede [it].", TextGerman = "Wenn Allah die Menschen für ihre Ungerechtigkeit belangen wollte, würde Er auf ihr kein Tier übriglassen. Aber Er stellt sie auf eine festgesetzte Frist zurück. Und wenn dann ihre Frist kommt, können sie (sie) weder um eine Stunde hinausschieben noch vorverlegen." });
                        surah16.VerseList.Add(new Verse { Number = 62, TextBosnian = "Oni Allahu pripisuju ono prema čemu sami odvratnost osjećaju i njihovi jezici govore laž da njih čeka najljepša nagrada; a njih, nema sumnje, Vatra čeka: oni će u nju prvi potjerani biti.", TextEnglish = "And they attribute to Allāh that which they dislike [i.e., daughters], and their tongues assert the lie that they will have the best [from Him]. Assuredly, they will have the Fire, and they will be [therein] neglected.[706]", TextGerman = "Und sie geben Allah, was ihnen (selbst) zuwider ist, und ihre Zungen behaupten die Lüge, daß für sie (nur) das Beste ist. Zweifellos ist für sie das (Höllen)feuer und daß sie vernachlässigt werden." });
                        surah16.VerseList.Add(new Verse { Number = 63, TextBosnian = "Allaha Mi, Mi smo i prije tebe narodima poslanike slali, ali im je šejtan uljepšao postupke njihove, i on je sada drug njihov, njih čeka bolna patnja.", TextEnglish = "By Allāh, We did certainly send [messengers] to nations before you, but Satan made their deeds attractive to them. And he is their [i.e., the disbelievers] ally today [as well], and they will have a painful punishment.", TextGerman = "Bei Allah, Wir haben bereits (Propheten) zu Gemeinschaften vor dir gesandt. Da schmückte ihnen der Satan ihre Taten aus. So ist er heute ihr Schutzherr, und für sie wird es schmerzhafte Strafe geben." });
                        surah16.VerseList.Add(new Verse { Number = 64, TextBosnian = "Mi tebi objavljujemo Knjigu, samo da bi im ti objasnio ono u čemu se razilaze i da bude uputa i milost ljudima koji vjeruju.", TextEnglish = "And We have not revealed to you the Book, [O Muḥammad], except for you to make clear to them that wherein they have differed and as guidance and mercy for a people who believe.", TextGerman = "Und Wir haben auf dich das Buch nur hinabgesandt, damit du ihnen das klar machst, worüber sie uneinig gewesen sind, und als Rechtleitung und Barmherzigkeit für Leute, die glauben." });
                        surah16.VerseList.Add(new Verse { Number = 65, TextBosnian = "Allah vodu s neba spušta i njome život mrtvoj zemlji vraća! Tu je, zaista, dokaz za ljude koji čuju.", TextEnglish = "And Allāh has sent down rain from the sky and given life thereby to the earth after its lifelessness. Indeed in that is a sign for a people who listen.", TextGerman = "Und Allah läßt vom Himmel Wasser herabkommen und macht dann damit die Erde nach ihrem Tod wieder lebendig. Darin ist wahrlich ein Zeichen für Leute, die hören (können)." });
                        surah16.VerseList.Add(new Verse { Number = 66, TextBosnian = "Vi imate pouku i u stoci: 'Mi vam dajemo da pijete ono što iz njene utrobe dolazi, što nastaje između grizina i krvi; mlijeko je to, čisto i ukusno onima koji ga piju.", TextEnglish = "And indeed, for you in grazing livestock is a lesson. We give you drink from what is in their bellies - between excretion and blood - pure milk, palatable to drinkers.", TextGerman = "Gewiß, auch im Vieh habt ihr wahrlich eine Lehre. Wir geben euch von dem, was in ihren Leibern zwischen Kot und Blut ist, zu trinken, reine Milch, angenehm für diejenigen, die (sie) trinken." });
                        surah16.VerseList.Add(new Verse { Number = 67, TextBosnian = "A od plodova palmi i loze pripremate opojno piće i hranu prijatnu. Tu je, doista, dokaz za ljude koji shvataju.'", TextEnglish = "And from the fruits of the palm trees and grapevines you take intoxicant and good provision.[707] Indeed in that is a sign for a people who reason.", TextGerman = "Und (Wir geben euch) von den Früchten der Palmen und der Rebstöcke (zu trinken), woraus ihr euch Rauschgetränk und eine schöne Versorgung nehmt. Darin ist wahrlich ein Zeichen für Leute, die begreifen." });
                        surah16.VerseList.Add(new Verse { Number = 68, TextBosnian = "Gospodar tvoj pčelu je nadahnuo: 'Pravi sebi kuće u brdima, na drveću i u onome što naprave ljudi,", TextEnglish = "And your Lord inspired to the bee, 'Take for yourself among the mountains, houses [i.e., hives], and among the trees and [in] that which they construct.", TextGerman = "Und dein Herr hat der Biene eingegeben: Nimm dir in den Bergen Häuser, in den Bäumen und in dem, was sie an Spalieren errichten." });
                        surah16.VerseList.Add(new Verse { Number = 69, TextBosnian = "zatim, hrani se svakovrsnim plodovima, pa onda idi stazama Gospodara svoga, poslušno!' Iz utroba njihovih izlazi piće različitih boja, u njemu je lijek ljudima. Tu je, uistinu, dokaz za ljude koji razmišljaju.", TextEnglish = "Then eat from all the fruits[708] and follow the ways of your Lord laid down [for you].' There emerges from their bellies a drink, varying in colors, in which there is healing for people. Indeed in that is a sign for a people who give thought.", TextGerman = "Hierauf iß von allen Früchten, ziehe auf den Wegen deines Herrn dahin, die (dir) geebnet sind. Aus ihren Leibern kommt ein Getränk von unterschiedlichen Farben, in dem Heilung für die Menschen ist. Darin ist wahrlich ein Zeichen für Leute, die nachdenken." });
                        surah16.VerseList.Add(new Verse { Number = 70, TextBosnian = "Allah vas stvara a poslije vam duše uzima; među vama ima onih koji senilnost - duboku starost dožive, pa opet ne znaju nakon što su znanje imali. Allah je, zaista, Onaj Koji sve zna i svemoćan je.", TextEnglish = "And Allāh created you; then He will take you in death. And among you is he who is reversed to the most decrepit [old] age so that he will not know, after [having had] knowledge, a thing. Indeed, Allāh is Knowing and Competent.", TextGerman = "Und Allah hat euch erschaffen, hierauf beruft Er euch ab. Und manch einer von euch wird in das niedrigste (Greisen)alter zurückgebracht, so daß er nach (vorherigem) Wissen nichts (mehr) weiß. Gewiß, Allah ist Allwissend und hat Macht." });
                        surah16.VerseList.Add(new Verse { Number = 71, TextBosnian = "Allah vas je u opskrbi jedne nad drugima odlikovao. Ali, oni kojima je dato više ne daju onima koji su u njihovoj vlasti, a potrebe su im jednake. Pa zar Allahove blagodati niječu?!", TextEnglish = "And Allāh has favored some of you over others in provision. But those who were favored [i.e., given more] would not hand over their provision to those whom their right hands possess [i.e., slaves] so they would be equal to them therein.[709] Then is it the favor of Allāh they reject?", TextGerman = "Und Allah hat die einen von euch vor den anderen in der Versorgung bevorzugt. Doch geben diejenigen, die bevorzugt werden, ihre Versorgung nicht an diejenigen zurück, die ihre rechte Hand besitzt, so daß sie darin gleich wären. Wollen sie denn die Gunst Allahs verleugnen?" });
                        surah16.VerseList.Add(new Verse { Number = 72, TextBosnian = "Allah vam stvara žene od vaše, ljudske, vrste, a od žena vaših daje vam sinove i unuke; i opskrbljuje vas dobrima. Pa zar u laž oni vjeruju, a Allahove blagodati poriču?!", TextEnglish = "And Allāh has made for you from yourselves mates and has made for you from your mates sons and grandchildren and has provided for you from the good things. Then in falsehood do they believe and in the favor of Allāh they disbelieve?", TextGerman = "Und Allah hat euch aus euch selbst Gattinnen gemacht und von euren Gattinnen Söhne und Enkel gemacht und euch von den guten Dingen versorgt. Wollen sie denn an das Falsche glauben und Allahs Gunst verleugnen," });
                        surah16.VerseList.Add(new Verse { Number = 73, TextBosnian = "I obožavaju, pored Allaha, one koji im nisu u stanju bilo kakvu opskrbu dati, ni iz nebesa ni iz zemlje, i koji ništa ne mogu!", TextEnglish = "And they worship besides Allāh that which does not possess for them [the power of] provision from the heavens and the earth at all, and [in fact], they are unable.", TextGerman = "und (wollen sie) anstatt Allahs dienen, was ihnen keine Versorgung in den Himmeln und auf der Erde (zu geben) vermag und (auch) nicht (dazu) in der Lage sein wird?" });
                        surah16.VerseList.Add(new Verse { Number = 74, TextBosnian = "Zato s Allahom ništa ne poredite! Allah, doista, zna, a vi ne znate!", TextEnglish = "So do not assert similarities to Allāh.[710] Indeed, Allāh knows and you do not know.", TextGerman = "So prägt Allah keine Gleichnisse! Allah weiß, ihr aber wißt nicht." });
                        surah16.VerseList.Add(new Verse { Number = 75, TextBosnian = "Allah navodi primjer roba u tuđem vlasništvu, koji ništa nema, i onoga kome smo Mi lijepu opskrbu dali i koji udjeljuje iz toga, i tajno i javno - zar su oni jednaki?! Neka je hvaljen Allah! Ali, većina njih ne zna!", TextEnglish = "Allāh presents an example: a slave [who is] owned and unable to do a thing and he to whom We have provided from Us good provision, so he spends from it secretly and publicly. Can they be equal? Praise to Allāh! But most of them do not know.", TextGerman = "Allah prägt das Gleichnis eines leibeigenen Dieners, der über nichts Macht hat, und jemandes, dem Wir von Uns her eine schöne Versorgung gewährt haben, so daß er davon heimlich und offen ausgibt. Sind sie (etwa) gleich? (Alles) Lob gehört Allah! Nein! Vielmehr wissen die meisten von ihnen nicht." });
                        surah16.VerseList.Add(new Verse { Number = 76, TextBosnian = "Allah vam navodi kao primjer dvojicu ljudi, od kojih je jedan gluhonijem, koji ni za šta nije sposoban, i koji je na teretu gospodaru svome - kud god ga pošalje, nikakva dobra ne donese. Da li je on jednak onom koji traži da se pravedno postupa, a i sam je na Pravom putu?!", TextEnglish = "And Allāh presents an example of two men, one of them dumb and unable to do a thing, while he is a burden to his guardian. Wherever he directs him, he brings no good. Is he equal to one who commands justice, while he is on a straight path?", TextGerman = "Und Allah prägt das Gleichnis zweier Männer. Der eine von beiden ist stumm und hat über nichts Macht; er ist seinem Herrn eine Last; wo auch immer er ihn hinschickt, bringt er nichts Gutes. Ist er etwa jemandem gleich, der Gerechtigkeit gebietet und sich auf einem geraden Weg befindet?" });
                        surah16.VerseList.Add(new Verse { Number = 77, TextBosnian = "Allah zna sve tajne nebesa i Zemlje! A Smak svijeta će kao treptaj oka, ili još brže, doći - zaista Allah nad svime ima moć!", TextEnglish = "And to Allāh belongs the unseen [aspects] of the heavens and the earth. And the command for the Hour is not but as a glance of the eye or even nearer. Indeed, Allāh is over all things competent.", TextGerman = "Und Allahs ist das Verborgene der Himmel und der Erde. Und die Angelegenheit der Stunde ist nur wie ein Augenblick, oder sie ist noch näher. Gewiß, Allah hat zu allem die Macht." });
                        surah16.VerseList.Add(new Verse { Number = 78, TextBosnian = "Allah vas iz nutrina majki vaših izvodi dok još ništa ne znate, i daje vam sluh i vid i razum - da biste se zahvaljivali.", TextEnglish = "And Allāh has extracted you from the wombs of your mothers not knowing a thing, and He made for you hearing and vision and hearts [i.e., intellect] that perhaps you would be grateful.", TextGerman = "Und Allah hat euch aus den Leibern eurer Mütter hervorgebracht, während ihr nichts wußtet. Und Er hat euch Gehör, Augenlicht und Herzen gegeben, auf daß ihr dankbar sein möget." });
                        surah16.VerseList.Add(new Verse { Number = 79, TextBosnian = "Zar oni ne vide ptice kako su potčinjene u prostranstvu nebeskom?!, njih samo Allah drži. Tu su, zaista, dokazi ljudima koji vjeruju.", TextEnglish = "Do they not see the birds controlled in the atmosphere of the sky? None holds them up except Allāh. Indeed in that are signs for a people who believe.", TextGerman = "Haben sie nicht auf die Vögel gesehen, die im Luftraum des Himmels dienstbar gemacht worden sind? Nur Allah hält sie (oben). Darin sind wahrlich Zeichen für Leute, die glauben." });
                        surah16.VerseList.Add(new Verse { Number = 80, TextBosnian = "Allah vam daje smiraj u vašim kućama, i daje vam od kože stoke šatore, koje lahko nosite dok putujete i kad se smjestite, a od vune njihove, i dlake njihove, i kostrijeti njihove imovinu i odjeću, sve do određenog roka.", TextEnglish = "And Allāh has made for you from your homes a place of rest and made for you from the hides of the animals tents which you find light on your day of travel and your day of encampment; and from their wool, fur and hair is furnishing and enjoyment [i.e., provision] for a time.", TextGerman = "Und Allah hat euch aus euren Häusern eine Ruhestätte gemacht, und Er hat euch aus den Häuten des Viehs Behausungen gemacht, die ihr am Tag eures Aufbrechens und am Tag eures Aufenthaltes leicht benutzen könnt, und aus ihrer Wolle, ihren Fellhärchen und ihrem Haar Ausstattung und Nießbrauch auf Zeit." });
                        surah16.VerseList.Add(new Verse { Number = 81, TextBosnian = "Allah vam u onome što je stvorio hlad daje, i skloništa u brdima vam daje, i odjeću koja vas čuva od vrućine daje vam, a i odjeću koja vas od vlastite sile štiti - tako vam upotpunjava blagodat Svoju da biste bili predani.", TextEnglish = "And Allāh has made for you, from that which He has created, shadows [i.e., shade] and has made for you from the mountains, shelters and has made for you garments which protect you from the heat and garments [i.e., coats of mail] which protect you from your [enemy in] battle. Thus does He complete His favor upon you that you might submit [to Him].", TextGerman = "Und Allah hat euch aus dem, was Er erschaffen hat, Schattenspender gemacht. Und Er hat euch in den Bergen Deckung gegeben. Und Er hat euch Kleider gemacht, die euch vor der Hitze schützen, und Kleider, die euch vor eurer Gewalt (gegeneinander) schützen. So vollendet Er Seine Gunst an euch, auf daß ihr (Ihm) ergeben sein möget." });
                        surah16.VerseList.Add(new Verse { Number = 82, TextBosnian = "A ako se oni okrenu - pa tvoja je dužnost samo da jasno saopćiš.", TextEnglish = "But if they turn away, [O Muḥammad] - then only upon you is [responsibility for] clear notification.", TextGerman = "Wenn sie sich abkehren, so obliegt dir nur die deutliche Übermittlung (der Botschaft)." });
                        surah16.VerseList.Add(new Verse { Number = 83, TextBosnian = "Iako znaju za blagodat Allahovu, opet je poriču i većina njih nevjernici su.", TextEnglish = "They recognize the favor of Allāh; then they deny it. And most of them are disbelievers.", TextGerman = "Sie erkennen die Gunst Allahs, und sie erkennen sie hierauf nicht an. Die meisten von ihnen sind undankbar." });
                        surah16.VerseList.Add(new Verse { Number = 84, TextBosnian = "A na Dan kada iz svakog naroda svjedoka podignemo, nevjernicima neće biti dopušteno da se pravdaju, niti će im biti udovoljeno da blagonaklonost za povratak traže.", TextEnglish = "And [mention] the Day when We will resurrect from every nation a witness [i.e., their prophet]. Then it will not be permitted to the disbelievers [to apologize or make excuses], nor will they be asked to appease [Allāh].", TextGerman = "Und (gedenke) des Tages, da Wir von jeder Gemeinschaft einen Zeugen erwecken. Dann wird denjenigen, die ungläubig sind, keine Erlaubnis gegeben (, sich zu entschuldigen), und es wird ihnen keine Gnade gewährt." });
                        surah16.VerseList.Add(new Verse { Number = 85, TextBosnian = "Kad se oni koji su zulum činili osvjedoče u patnju, u tome olakšanja niti odgode neće imati.", TextEnglish = "And when those who wronged see the punishment, it will not be lightened for them, nor will they be reprieved.", TextGerman = "Und wenn diejenigen, die Unrecht taten, die Strafe sehen, dann wird sie ihnen weder erleichtert noch wird ihnen Aufschub gewährt." });
                        surah16.VerseList.Add(new Verse { Number = 86, TextBosnian = "A kad oni koji su Njemu druge u obožavanju pridruživali vide svoja božanstva, reći će:'Gospodaru naš, ovo su naša božanstva koja smo, mimo Tebe, molili.' Ona će im dobaciti: 'Vi ste, uistinu, lažljivci!'", TextEnglish = "And when those who associated others with Allāh see their 'partners,' they will say, 'Our Lord, these are our partners [to You] whom we used to invoke [in worship] besides You.' But they will throw at them the statement, 'Indeed, you are liars.'", TextGerman = "Und wenn diejenigen, die (Allah andere) beigesellten, ihre Teilhaber sehen, sagen sie: Unser Herr, das sind unsere Teilhaber, die wir anstatt Deiner anzurufen pflegten. Sie aber richteten an sie das Wort: Ihr seid fürwahr Lügner." });
                        surah16.VerseList.Add(new Verse { Number = 87, TextBosnian = "I oni će se toga dana Allahu pokoriti, i nestat će ono što su lažno obožavali.", TextEnglish = "And they will impart to Allāh that Day [their] submission, and lost from them is what they used to invent.", TextGerman = "Sie bieten Allah an jenem Tag Ergebenheit an, und es ist ihnen entschwunden, was sie zu ersinnen pflegten." });
                        surah16.VerseList.Add(new Verse { Number = 88, TextBosnian = "Onima koji nisu vjerovali i koji su od Allahova puta odvraćali, povećat ćemo patnju preko patnje, zato što su smutnju pravili.", TextEnglish = "Those who disbelieved and averted [others] from the way of Allāh - We will increase them in punishment over [their] punishment for what corruption they were causing.", TextGerman = "Denjenigen, die ungläubig sind und von Allahs Weg abhalten, fügen Wir noch Strafe zu über ihre Strafe hinaus dafür, daß sie Unheil zu stiften pflegten." });
                        surah16.VerseList.Add(new Verse { Number = 89, TextBosnian = "I sjeti se Dana kada protiv svakog naroda oživimo po jednog svjedoka, iz njega samog, i tebe dovedemo kao svjedoka protiv ovih! Mi tebi objavljujemo Knjigu kao objašnjenje za sve - kao uputu, i milost, i radosnu vijest za muslimane.", TextEnglish = "And [mention] the Day when We will resurrect among every nation a witness over them from themselves [i.e., their prophet]. And We will bring you, [O Muḥammad], as a witness over these [i.e., your nation]. And We have sent down to you the Book as clarification for all things and as guidance and mercy and good tidings for the Muslims.[711]", TextGerman = "Und (gedenke) des Tages, da Wir in jeder Gemeinschaft einen Zeugen über sie aus ihren eigenen Reihen erwecken. Dich bringen Wir als Zeugen über diese da herbei. Und Wir haben dir das Buch offenbart als klare Darlegung von allem und als Rechtleitung, Barmherzigkeit und frohe Botschaft für die (Allah) Ergebenen." });
                        surah16.VerseList.Add(new Verse { Number = 90, TextBosnian = "Allah, zaista, naređuje pravdu, dobročinstvo i pomaganje bližnjih, a razvrat, sve što je odvratno i nasilje zabranjuje; On vas savjetuje ne bi li ste se prisjetili i pouku uzeli.", TextEnglish = "Indeed, Allāh orders justice and good conduct and giving [help] to relatives and forbids immorality and bad conduct and oppression. He admonishes you that perhaps you will be reminded.", TextGerman = "Allah gebietet Gerechtigkeit, gütig zu sein und den Verwandten zu geben; Er verbietet das Schändliche, das Verwerfliche und die Gewalttätigkeit. Er ermahnt euch, auf daß ihr bedenken möget." });
                        surah16.VerseList.Add(new Verse { Number = 91, TextBosnian = "I ispunjavajte obaveze na koje ste se Allahu obavezali i ne kršite zakletve kad ste ih čvrsto dali, a Allaha kao jamca sebi uzeli, jer Allah zna ono što radite.", TextEnglish = "And fulfill the covenant of Allāh when you have taken it, [O believers], and do not break oaths after their confirmation while you have made Allāh, over you, a security [i.e., witness]. Indeed, Allāh knows what you do.", TextGerman = "Und haltet den Bund Allahs, wenn ihr einen Bund geschlossen habt, und brecht nicht die Eide nach ihrer Bekräftigung, wo ihr doch Allah zum Bürgen über euch gemacht habt. Gewiß, Allah weiß, was ihr tut." });
                        surah16.VerseList.Add(new Verse { Number = 92, TextBosnian = "I ne budite kao ona koja bi svoju pređu rasprela kad bi je već bila čvrsto oprela, služeći se zakletvama svojim za međusobnu prevaru, samo zato što je jedno pleme brojnije od drugog. Allah vas time samo iskušava, a na Sudnjem danu će vam, doista, objasniti ono u čemu ste se razilazili.", TextEnglish = "And do not be like she who untwisted her spun thread after it was strong [by] taking your oaths as [means of] deceit between you because one community is more plentiful [in number or wealth] than another community.[712] Allāh only tries you thereby. And He will surely make clear to you on the Day of Resurrection that over which you used to differ.", TextGerman = "Und seid nicht wie jene, die ihr Garn, nachdem es fest gesponnen war, wieder in aufgelöste Strähnen bricht, indem ihr eure Eide untereinander als Mittel des Betrugs nehmt, weil eine Gemeinschaft zahlreicher ist als eine andere Gemeinschaft. Allah prüft euch damit nur, und Er wird euch am Tag der Auferstehung ganz gewiß über das Klarheit geben, worüber ihr uneinig zu sein pflegtet." });
                        surah16.VerseList.Add(new Verse { Number = 93, TextBosnian = "Da Allah hoće, učinio bi vas sljedbenicima jedne vjere, ali On onome kome hoće daje da je u zabludi, a na Pravi put upućuje onoga koga On hoće; i vi ćete, doista, biti pitani za ono što ste radili.", TextEnglish = "And if Allāh had willed, He could have made you [of] one religion, but He sends astray whom He wills and guides whom He wills.[713] And you will surely be questioned about what you used to do.", TextGerman = "Und wenn Allah gewollt hätte, hätte Er euch wahrlich zu einer einzigen Gemeinschaft gemacht. Aber Er läßt in die Irre gehen, wen Er will, und Er leitet recht, wen Er will, und ihr werdet ganz gewiß danach befragt werden, was ihr zu tun pflegtet." });
                        surah16.VerseList.Add(new Verse { Number = 94, TextBosnian = "I ne služite se zakletvama zato da biste jedni druge varali, pa da posrne noga nakon što je stabilna bila, i da zlo iskusite zato što ste od Allahova puta odvraćali; a patnja velika vas još čeka.", TextEnglish = "And do not take your oaths as [means of] deceit between you, lest a foot slip after it was [once] firm, and you would taste evil [in this world] for what [people] you diverted from the way of Allāh,[714] and you would have [in the Hereafter] a great punishment.", TextGerman = "Und nehmt euch nicht eure Eide als Mittel des Betrugs untereinander; sonst würde ein Fuß straucheln, nachdem er fest gestanden hat, und ihr würdet Böses erleiden dafür, daß ihr von Allahs Weg abgehalten habt, und für euch wird es gewaltige Strafe geben." });
                        surah16.VerseList.Add(new Verse { Number = 95, TextBosnian = "I ne zamjenjujte obavezu datu Allahu za nešto što malo vrijedi - ono što je u Allaha za vas je bolje, ako znate!", TextEnglish = "And do not exchange the covenant of Allāh for a small price. Indeed, what is with Allāh is best for you, if only you could know.", TextGerman = "Und verkauft den Bund Allahs nicht für einen geringen Preis. Gewiß, was bei Allah ist, ist besser für euch, wenn ihr (es) nur wißt." });
                        surah16.VerseList.Add(new Verse { Number = 96, TextBosnian = "Ono što je u vas-prolazno je, a ono što je u Allaha-vječno je. Mi ćemo, sigurno, one koji su strpljivi bili prema onom boljem što su činili nagraditi.", TextEnglish = "Whatever you have will end, but what Allāh has is lasting. And We will surely give those who were patient their reward according to the best of what they used to do.", TextGerman = "Was bei euch ist, geht zu Ende; was aber bei Allah ist, ist bleibend. Wir werden denjenigen, die standhaft sind, ganz gewiß mit ihrem Lohn das Beste vergelten von dem, was sie taten." });
                        surah16.VerseList.Add(new Verse { Number = 97, TextBosnian = "Ko god činio dobro, bio muškarac ili žena, a vjernik je, Mi ćemo dati da proživi lijep život i doista ćemo ih u skladu s najboljim što su činili nagraditi.", TextEnglish = "Whoever does righteousness, whether male or female, while he is a believer - We will surely cause him to live a good life, and We will surely give them their reward [in the Hereafter] according to the best of what they used to do.", TextGerman = "Wer rechtschaffen handelt, sei es Mann oder Frau, und dabei gläubig ist, den werden Wir ganz gewiß ein gutes Leben leben lassen. Und Wir werden ihnen ganz gewiß mit ihrem Lohn das Beste von dem vergelten, was sie taten." });
                        surah16.VerseList.Add(new Verse { Number = 98, TextBosnian = "Kada budeš htio učiti Kur'an, zatraži Allahovu zaštitu od šejtana prokletog,", TextEnglish = "So when you recite the Qur’ān, [first] seek refuge in Allāh from Satan, the expelled [from His mercy].", TextGerman = "Wenn du nun den Qur’an vorträgst, so suche Schutz bei Allah vor dem gesteinigten Satan." });
                        surah16.VerseList.Add(new Verse { Number = 99, TextBosnian = "on doista nema nikakve moći nad onima koji vjeruju i koji se u Gospodara svoga uzdaju,", TextEnglish = "Indeed, there is for him no authority over those who have believed and rely upon their Lord.", TextGerman = "Er hat gewiß keine Macht über diejenigen, die glauben und sich auf ihren Herrn verlassen." });
                        surah16.VerseList.Add(new Verse { Number = 100, TextBosnian = "njegova je moć jedino nad onima koji njega za zaštitnika uzimaju i koji druge Allahu pridružuju.", TextEnglish = "His authority is only over those who take him as an ally and those who through him associate others with Allāh.", TextGerman = "Seine Macht erstreckt sich nur über diejenigen, die ihn zum Schutzherrn nehmen und die ihn (Allah) beigesellen." });
                        surah16.VerseList.Add(new Verse { Number = 101, TextBosnian = "Kada Mi ajet zamijemo drugim - a Allah najbolje zna što objavljuje - oni govore: 'Ti samo izmišljaš!' A nije tako, nego većina njih ne zna.", TextEnglish = "And when We substitute a verse in place of a verse - and Allāh is most knowing of what He sends down - they say, 'You, [O Muḥammad], are but an inventor [of lies].' But most of them do not know.", TextGerman = "Und wenn Wir einen Vers anstelle eines (anderen) Verses austauschen - und Allah weiß sehr wohl, was Er offenbart -, sagen sie: Du ersinnst nur Lügen. Aber nein! Die meisten von ihnen wissen nicht." });
                        surah16.VerseList.Add(new Verse { Number = 102, TextBosnian = "Reci: 'Od Gospodara tvoga spušta ga Džibril, Duh Blagoslovljeni s Istinom, da još više učvrsti vjernike, i da bude uputa i radosna vijest muslimanima.'", TextEnglish = "Say, [O Muḥammad], 'The Pure Spirit [i.e., Gabriel] has brought it down from your Lord in truth to make firm those who believe and as guidance and good tidings to the Muslims.'", TextGerman = "Sag: Offenbart hat ihn der Heilige Geist von deinem Herrn mit der Wahrheit, um diejenigen, die glauben, zu festigen, und als Rechtleitung und frohe Botschaft für die (Allah) Ergebenen." });
                        surah16.VerseList.Add(new Verse { Number = 103, TextBosnian = "Dobro znamo da oni govore: 'Poučava ga jedan čovjek! Jezik onoga na koga su skloni tako misliti, jezik je nearapa, a ovaj Kur'an na jasnom je arapskom jeziku.", TextEnglish = "And We certainly know that they say, 'It is only a human being who teaches him [i.e., the Prophet (ﷺ)].' The tongue of the one they refer to is foreign,[715] and this [recitation, i.e., Qur’ān] is [in] a clear Arabic language.", TextGerman = "Und Wir wissen sehr wohl, daß sie sagen: Es lehrt ihn nur ein menschliches Wesen. Die Sprache dessen, auf den sie hinweisen, ist eine fremde, während dies hier deutliche arabische Sprache ist." });
                        surah16.VerseList.Add(new Verse { Number = 104, TextBosnian = "One koji ne vjeruju u Allahove ajete i znakove Allah sigurno neće uputiti, i njih čeka patnja bolna.", TextEnglish = "Indeed, those who do not believe in the verses of Allāh - Allāh will not guide them, and for them is a painful punishment.", TextGerman = "Diejenigen, die nicht an Allahs Zeichen glauben, leitet Allah nicht recht, und für sie wird es schmerzhafte Strafe geben." });
                        surah16.VerseList.Add(new Verse { Number = 105, TextBosnian = "Laži izmišljaju samo oni koji u Allahove ajete i znakove ne vjeruju i to su baš lažljivci.", TextEnglish = "They only invent falsehood who do not believe in the verses of Allāh, and it is those who are the liars.", TextGerman = "Lügen ersinnen ja nur diejenigen, die nicht an Allahs Zeichen glauben. Das sind die (wahren) Lügner." });
                        surah16.VerseList.Add(new Verse { Number = 106, TextBosnian = "Onaj ko zaniječe Allaha, nakon što je u Njega vjerovao - ne i onaj ko na to bude primoran, već onaj koji nevjerovanje širokogrudo prihvati - Allah se na takve srdi i njih čeka patnja velika,", TextEnglish = "Whoever disbelieves in [i.e., denies] Allāh after his belief...[716] except for one who is forced [to renounce his religion] while his heart is secure in faith. But those who [willingly] open their breasts to disbelief, upon them is wrath from Allāh, and for them is a great punishment;", TextGerman = "Wer Allah verleugnet, nachdem er den Glauben (angenommen) hatte - außer demjenigen, der gezwungen wird, während sein Herz im Glauben Ruhe gefunden hat -, doch wer aber seine Brust dem Unglauben auftut, über diejenigen kommt Zorn von Allah, und für sie wird es gewaltige Strafe geben." });
                        surah16.VerseList.Add(new Verse { Number = 107, TextBosnian = "zato što su više voljeli život na dunjaluku nego na ahiretu, a Allah neće uputiti narod koji ne vjeruje.", TextEnglish = "That is because they preferred the worldly life over the Hereafter and that Allāh does not guide the disbelieving people.", TextGerman = "Dies (wird sein), weil sie das diesseitige Leben mehr lieben als das Jenseits und weil Allah das ungläubige Volk nicht rechtleitet." });
                        surah16.VerseList.Add(new Verse { Number = 108, TextBosnian = "To su oni čija je srca i sluh i vid Allah zapečatio, i oni su itekako nemarni;", TextEnglish = "Those are the ones over whose hearts and hearing and vision Allāh has sealed, and it is those who are the heedless.", TextGerman = "Das sind diejenigen, deren Herzen, Gehör und Augenlicht Allah versiegelt hat; und das sind (überhaupt) die Unachtsamen." });
                        surah16.VerseList.Add(new Verse { Number = 109, TextBosnian = "nema sumnje da će oni na ahiretu baš biti gubitnici.", TextEnglish = "Assuredly, it is they, in the Hereafter, who will be the losers.", TextGerman = "Zweifellos sind sie im Jenseits die Verlierer." });
                        surah16.VerseList.Add(new Verse { Number = 110, TextBosnian = "Onima koji su se, nakon što su u iskušenje dovedeni pa nevjerovanje izjavili, iselili, pa se onda borili i strpljivi bili - Gospodar tvoj će, poslije toga, doista, oprostiti i milostiv biti", TextEnglish = "Then, indeed your Lord, to those who emigrated after they had been compelled [to say words of disbelief] and thereafter fought [for the cause of Allāh] and were patient - indeed, your Lord, after that, is Forgiving and Merciful", TextGerman = "Hierauf wird dein Herr zu denjenigen, die ausgewandert sind, nachdem sie der Versuchung ausgesetzt wurden, und sich hierauf abgemüht haben und standhaft gewesen sind - gewiß, dein Herr wird nach (all) diesem (zu ihnen) wahrlich Allvergebend und Barmherzig sein." });
                        surah16.VerseList.Add(new Verse { Number = 111, TextBosnian = "na Dan kada će svako samo o sebi brinuti, i u kome će se svakoj osobi za djela njena puna nagrada ili kazna dati, i nepravda im neće biti učinjena!", TextEnglish = "On the Day when every soul will come disputing [i.e., pleading] for itself, and every soul will be fully compensated for what it did, and they will not be wronged [i.e., treated unjustly].", TextGerman = "Am Tag, da jede Seele kommt und für sich selbst streitet, und jeder Seele in vollem Maß zukommen wird, was sie getan hat. Und es wird ihnen kein Unrecht zugefügt." });
                        surah16.VerseList.Add(new Verse { Number = 112, TextBosnian = "Allah navodi kao primjer jedno naselje''' koje je bilo sigurno i spokojno, u koje je u izobilju dolazila ukusna hrana sa svih strana, pa je zanijekalo Allahove blagodati, pa mu je Allah dao da iskusi i glad i strah zbog onoga što su stanovnici njegovi činili.", TextEnglish = "And Allāh presents an example: a city [i.e., Makkah] which was safe and secure, its provision coming to it in abundance from every location, but it denied the favors of Allāh. So Allāh made it taste the envelopment of hunger and fear for what they had been doing.", TextGerman = "Allah prägt das Gleichnis einer Stadt, die Sicherheit und Ruhe genoß; ihre Versorgung kam zu ihr reichlich von überall her. Da wurde sie gegenüber den Gnaden Allahs undankbar. So ließ sie Allah das Kleid des Hungers und der Angst erleiden für das, was sie machten." });
                        surah16.VerseList.Add(new Verse { Number = 113, TextBosnian = "I njima je došao poslanik, jedan od njih, ali su ga oni lažljivcem nazvali, i stigla ih je kazna dok su zulumćari bili.", TextEnglish = "And there had certainly come to them a Messenger from among themselves, but they denied him; so punishment overtook them while they were wrongdoers.", TextGerman = "Ein Gesandter von ihnen kam doch zu ihnen, sie bezichtigten ihn aber der Lüge; so ergriff sie die Strafe, während sie Unrecht taten." });
                        surah16.VerseList.Add(new Verse { Number = 114, TextBosnian = "Hranite se dopuštenim i lijepim jelima koja vam Allah daruje i budite zahvalni Allahu na blagodatima Njegovim, ako samo Njemu ibadet činite.", TextEnglish = "Then eat of what Allāh has provided for you [which is] lawful and good. And be grateful for the favor of Allāh, if it is [indeed] Him that you worship.", TextGerman = "So eßt von dem, womit Allah euch versorgt hat, als etwas Erlaubtes und Gutes, und seid dankbar für die Gunst Allahs, wenn ihr Ihm allein dient." });
                        surah16.VerseList.Add(new Verse { Number = 115, TextBosnian = "On vam zabranjuje jedino strv, i krv, i svinjsko meso, i ono što je zaklano u nečije drugo, a ne u Allahovo ime. A onome ko bude primoran, ali ne iz želje, samo toliko da utoli glad- pa, Gospodar tvoj je, zaista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "He has only forbidden to you dead animals,[717] blood, the flesh of swine, and that which has been dedicated to other than Allāh. But whoever is forced [by necessity], neither desiring [it] nor transgressing [its limit] - then indeed, Allāh is Forgiving and Merciful.", TextGerman = "Verboten hat Er euch nur (den Genuß von) Verendetem, Blut, Schweinefleisch und dem, worüber ein anderer (Name) als Allah(s) angerufen worden ist. Wer sich aber in einer Zwangslage befindet, ohne zu begehren oder das Maß zu überschreiten, so ist Allah Allvergebend und Barmherzig." });
                        surah16.VerseList.Add(new Verse { Number = 116, TextBosnian = "I ne govorite laži jezicima svojim: 'Ovo je dopušteno, a ovo zabranjeno', da biste tako o Allahu laži izmišljali. Oni koji o Allahu laži izmišljaju - neće uspjeti,", TextEnglish = "And do not say about what your tongues assert of untruth, 'This is lawful and this is unlawful,' to invent falsehood about Allāh. Indeed, those who invent falsehood about Allāh will not succeed.", TextGerman = "Und sagt nicht von dem, was eure Zungen als Lüge behaupten: Das ist erlaubt, und das ist verboten, um gegen Allah eine Lüge zu ersinnen. Gewiß, denjenigen, die gegen Allah eine Lüge ersinnen, wird es nicht wohl ergehen." });
                        surah16.VerseList.Add(new Verse { Number = 117, TextBosnian = "kratko će uživati, a onda će patnju bolnu imati.", TextEnglish = "[It is but] a brief enjoyment, and they will have a painful punishment.", TextGerman = "(Es ist nur) ein geringer Genuß, und für sie wird es schmerzhafte Strafe geben." });
                        surah16.VerseList.Add(new Verse { Number = 118, TextBosnian = "Jevrejima smo zabranili ono o čemu smo ti prije kazivali; Mi njima nismo zulum učinili, oni su sami sebi zulum nanosili.", TextEnglish = "And to those who are Jews We have prohibited that which We related to you before.[718] And We did not wrong them [thereby], but they were wronging themselves.", TextGerman = "Und denjenigen, die dem Judentum angehören, haben Wir das verboten, worüber Wir dir zuvor berichtet haben. Und nicht Wir haben ihnen Unrecht getan, sondern sie haben sich selbst Unrecht zugefügt." });
                        surah16.VerseList.Add(new Verse { Number = 119, TextBosnian = "Onima koji urade zlo iz neznanja, pa se kasnije pokaju i poprave, Gospodar tvoj će, poslije toga, sigurno oprostiti i milostiv biti.", TextEnglish = "Then, indeed your Lord, to those who have done wrong out of ignorance and then repent after that and correct themselves - indeed, your Lord, thereafter, is Forgiving and Merciful.", TextGerman = "Hierauf wird dein Herr zu denjenigen, die in Unwissenheit Böses tun, aber nach alledem bereuen und (es) wieder gutmachen - gewiß, dein Herr wird danach (zu ihnen) wahrlich Allvergebend und Barmherzig sein." });
                        surah16.VerseList.Add(new Verse { Number = 120, TextBosnian = "Ibrahim je doista bio predvodnik, pokoran Allahu, pravi vjernik, nije bio od onih koji Allahu druge u obožavanju pridružuju.", TextEnglish = "Indeed, Abraham was a [comprehensive] leader,[719] devoutly obedient to Allāh, inclining toward truth, and he was not of those who associate others with Allāh.", TextGerman = "Ibrahim war eine Gemeinschaft, Allah demütig ergeben und einer, der Anhänger des rechten Glaubens war, und er gehörte nicht zu den Götzendienern;" });
                        surah16.VerseList.Add(new Verse { Number = 121, TextBosnian = "I bio je zahvalan na blagodatima Njegovim; njega je Allah izabrao i na Pravi put uputio.", TextEnglish = "[He was] grateful for His favors. He [i.e., Allāh] chose him and guided him to a straight path.", TextGerman = "dankbar (war er) für Seine Gnaden. Er hatte ihn erwählt und zu einem geraden Weg geleitet." });
                        surah16.VerseList.Add(new Verse { Number = 122, TextBosnian = "I dali smo mu dobro na dunjaluku, a na ahiretu doista će biti među dobrima.", TextEnglish = "And We gave him good in this world, and indeed, in the Hereafter he will be among the righteous.", TextGerman = "Und Wir hatten ihm im Diesseits Gutes gegeben, und im Jenseits gehört er fürwahr zu den Rechtschaffenen." });
                        surah16.VerseList.Add(new Verse { Number = 123, TextBosnian = "Poslije smo tebi objavili: 'Slijedi vjeru Ibrahimovu, on je bio vjere čiste, on nije bio od onih koji Allahu druge u obožavanju pridružuju!'", TextEnglish = "Then We revealed to you, [O Muḥammad], to follow the religion of Abraham, inclining toward truth; and he was not of those who associate with Allāh.", TextGerman = "Und hierauf haben Wir dir (als Offenbarung) eingegeben: Folge dem Glaubensbekenntnis Ibrahims, (als) Anhänger des rechten Glaubens, und er gehörte nicht zu den Götzendienern." });
                        surah16.VerseList.Add(new Verse { Number = 124, TextBosnian = "Svetkovanje subote propisano je onima koji su imali različita mišljenja o njoj, i Gospodar tvoj će im na Kijametskom danu presuditi u vezi s onim u čemu su se razilazili.", TextEnglish = "The sabbath was only appointed for those who differed over it. And indeed, your Lord will judge between them on the Day of Resurrection concerning that over which they used to differ.", TextGerman = "Der Sabbat ist nur denjenigen auferlegt worden, die über ihn uneinig waren. Und dein Herr wird wahrlich am Tag der Auferstehung zwischen ihnen über das richten, worüber sie uneinig zu sein pflegten." });
                        surah16.VerseList.Add(new Verse { Number = 125, TextBosnian = "Na put Gospodara svoga mudro i lijepim savjetom pozivaj, i s njima na najljepši način raspravljaj! Gospodar tvoj zna one koji su zalutali s puta Njegova, i On zna one koji Uputu slijede.", TextEnglish = "Invite to the way of your Lord with wisdom and good instruction, and argue with them in a way that is best. Indeed, your Lord is most knowing of who has strayed from His way, and He is most knowing of who is [rightly] guided.", TextGerman = "Rufe zum Weg deines Herrn mit Weisheit und schöner Ermahnung, und streite mit ihnen in bester Weise. Gewiß, dein Herr kennt sehr wohl, wer von Seinem Weg abirrt, und Er kennt sehr wohl die Rechtgeleiteten." });
                        surah16.VerseList.Add(new Verse { Number = 126, TextBosnian = "A ako budete kažnjavali, onda kažnjavajte samo onako kako ste i sami kažnjeni; a ako otrpite - to je, doista, bolje za strpljive.", TextEnglish = "And if you punish [an enemy, O believers], punish with an equivalent of that with which you were harmed.[720] But if you are patient - it is better for those who are patient.", TextGerman = "Und wenn ihr bestraft, so bestraft im gleichen Maß, wie ihr bestraft wurdet. Wenn ihr aber geduldig seid, so ist das wahrlich besser für die Geduldigen." });
                        surah16.VerseList.Add(new Verse { Number = 127, TextBosnian = "Strpljiv budi! Ali, strpljiv ćeš biti samo uz Allahovu pomoć. I ne žalosti se za njima, i neka ti nije teško zbog spletkarenja njihova.", TextEnglish = "And be patient, [O Muḥammad], and your patience is not but through Allāh. And do not grieve over them and do not be in distress over what they conspire.", TextGerman = "Sei standhaft; deine Standhaftigkeit ist nur durch Allah (möglich). Sei nicht traurig über sie, und sei nicht in Beklommenheit wegen der Ränke, die sie schmieden." });
                        surah16.VerseList.Add(new Verse { Number = 128, TextBosnian = "Allah je zaista uz one koji se uščuvaju i koji dobra djela čine.", TextEnglish = "Indeed, Allāh is with those who fear Him and those who are doers of good.", TextGerman = "Gewiß, Allah ist mit denjenigen, die gottesfürchtig sind und Gutes tun." });
                        result = surah16;
                        break;
                    #endregion

                    // 
                    #region 17 Al-Isra
                    case 17:
                        Surah surah17 = new Surah(); surah17.Name = Constants.Surah17Name; surah17.Number = 17; surah17.VerseList = new List<Verse>();
                        surah17.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah17.VerseList.Add(new Verse { Number = 1, TextBosnian = "Uzvišen neka je Onaj Koji je u jednom času noći preveo Svoga roba od Časne džamije do Najdalje džamije, čiju smo okolicu blagoslovili kako bismo mu neka znamenja Naša pokazali. On je, uistinu, Svečujući i Svevideći.", TextEnglish = "Exalted[722] is He who took His Servant [i.e., Prophet Muḥammad (ﷺ)] by night from al-Masjid al-Ḥarām to al-Masjid al-Aqṣā,[723] whose surroundings We have blessed, to show him of Our signs. Indeed, He is the Hearing,[724] the Seeing.[725]", TextGerman = "Preis sei Dem, Der Seinen Diener bei Nacht von der geschützten Gebetsstätte zur fernsten Gebetsstätte, deren Umgebung Wir gesegnet haben, reisen ließ, damit Wir ihm (etwas) von Unseren Zeichen zeigen. Er ist ja der Allhörende, der Allsehende." });
                        surah17.VerseList.Add(new Verse { Number = 2, TextBosnian = "A Musau smo Knjigu dali i uputom je sinovima Israilovim učinili: 'Osim Mene zaštitinika ne tražite,", TextEnglish = "And We gave Moses the Scripture and made it a guidance for the Children of Israel that you not take other than Me as Disposer of affairs,[726]", TextGerman = "Und Wir gaben Musa die Schrift und machten sie zu einer Rechtleitung für die Kinder Isra’ils: Nehmt euch außer Mir keinen Sachwalter," });
                        surah17.VerseList.Add(new Verse { Number = 3, TextBosnian = "o potomci onih koje smo sa Nuhom u lađi prevezli! On je, doista, bio rob zahvalni.", TextEnglish = "O descendants of those We carried [in the ship] with Noah. Indeed, he was a grateful servant.", TextGerman = "(o ihr,) die Nachkommenschaft derer, die Wir mit Nuh trugen. Gewiß, er war ein dankbarer Diener." });
                        surah17.VerseList.Add(new Verse { Number = 4, TextBosnian = "I Mi smo u Knjizi objavili sinovima Israilovim: 'Vi ćete doista dva puta nered na Zemlji učiniti i uveliko se oholiti.'", TextEnglish = "And We conveyed[727] to the Children of Israel in the Scripture that, 'You will surely cause corruption on the earth twice, and you will surely reach [a degree of] great haughtiness.'", TextGerman = "Und Wir haben für die Kinder Isra’ils im Buch entschieden: Ihr werdet ganz gewiß zweimal auf der Erde Unheil stiften, und ihr werdet ganz gewiß mächtige Überheblichkeit erlangen." });
                        surah17.VerseList.Add(new Verse { Number = 5, TextBosnian = "Kada dođe vrijeme prvoga, poslat ćemo protiv vas robove Naše snage žestoke, oni će uzduž i poprijeko zemlju vašu pregaziti, i obećanje o kazni će se ispuniti.", TextEnglish = "So when the [time of] promise came for the first of them,[728] We sent against you servants of Ours - those of great military might, and they probed [even] into the homes,[729] and it was a promise fulfilled.", TextGerman = "Wenn nun das Versprechen vom ersten der beiden (Male) eintrifft, schicken Wir gegen euch Diener von Uns, die eine starke Gewalt besitzen. Sie dringen zwischen den Wohnstätten hindurch ein, und das ist ein Versprechen, das sicher ausgeführt wird." });
                        surah17.VerseList.Add(new Verse { Number = 6, TextBosnian = "Zatim ćemo vam dati pobjedu protiv njih i povećati vam imetke i sinove i učinit ćemo vas brojnijim.", TextEnglish = "Then We gave back to you a return victory over them. And We reinforced you with wealth and sons and made you more numerous in manpower.", TextGerman = "Hierauf geben Wir euch wiederum die Oberhand über sie, und Wir unterstützen euch mit Besitz und Söhnen und machen euch zahlreicher." });
                        surah17.VerseList.Add(new Verse { Number = 7, TextBosnian = "Pa, ako dobro budete činili - sebi ste ga činili, a ako zlo budete činili - protiv sebe ste ga činili!A kad dođe vrijeme drugog nereda, poslat ćemo ih da na licima vašim tugu i jad ostave i da u Mesdžid uđu, kao što su u njega ušli i prvi put, i da do temelja, sve nad čime zavladaju, poruše.", TextEnglish = "[And said], 'If you do good, you do good for yourselves; and if you do evil, [you do it] to them [i.e., yourselves].' Then when the final[i.e., second] promise came, [We sent your enemies] to sadden your faces and to enter the masjid[i.e., the temple in Jerusalem], as they entered it the first time, and to destroy what they had taken over with[total] destruction.", TextGerman = "Wenn ihr Gutes tut, tut ihr Gutes für euch selbst; und wenn ihr Böses tut, ist es(auch) für euch selbst. - Wenn nun das Versprechen vom letzten(Mal) eintrifft, so sollen sie eure Gesichter entstellen und die Gebetsstätte betreten, wie sie diese das erste Mal betraten, und das, worüber sie Macht erlangt haben, völlig zerstören." });
                        surah17.VerseList.Add(new Verse { Number = 8, TextBosnian = "I možda vam se Gospodar vaš opet smiluje; ali ako se vratite neredu, vratit ćemo se i Mi kazni. A Džehennem smo za nevjernike tamnicom učinili.", TextEnglish = "[Then Allāh said], 'It is expected, [if you repent], that your Lord will have mercy upon you. But if you return [to sin], We will return [to punishment]. And We have made Hell, for the disbelievers, a prison-bed.'", TextGerman = "Vielleicht erbarmt euer Herr Sich eurer; doch wenn ihr (dazu) zurückkehrt, kehren (auch) Wir zurück. Und Wir haben die Hölle für die Ungläubigen zum Gefängnis gemacht." });
                        surah17.VerseList.Add(new Verse { Number = 9, TextBosnian = "Zaista je ovaj Kur'an uputa ka Putu koji je najispravniji, i vjernicima koji čine dobra djela donosi radosnu vijest da ih čeka nagrada velika,", TextEnglish = "Indeed, this Qur’ān guides to that which is most suitable and gives good tidings to the believers who do righteous deeds that they will have a great reward", TextGerman = "Gewiß, dieser Qur’an leitet zu dem, was richtiger ist, und verkündet den Gläubigen, die rechtschaffene Werke tun, daß es für sie großen Lohn geben wird" });
                        surah17.VerseList.Add(new Verse { Number = 10, TextBosnian = "a da smo za one koji u ahiret ne vjeruju-bolnu patnju pripremili.", TextEnglish = "And that those who do not believe in the Hereafter - We have prepared for them a painful punishment.", TextGerman = "und daß Wir denjenigen, die an das Jenseits nicht glauben, schmerzhafte Strafe bereitet haben." });
                        surah17.VerseList.Add(new Verse { Number = 11, TextBosnian = "Baš kao što moli za ono što je dobro, čovjek nekada moli i za ono što ne valja. Čovjek je zaista sklon da ishitren bude.", TextEnglish = "And man supplicates for evil [when angry] as he supplicates for good, and man is ever hasty.[730]", TextGerman = "Der Mensch ruft (zu Allah) um das Schlechte, wie er um das Gute ruft; der Mensch ist ja stets voreilig." });
                        surah17.VerseList.Add(new Verse { Number = 12, TextBosnian = "I Mi smo noć i dan kao dva znamenja učinili: znak noći smo uklonili, a znak dana smo svijetlim učinili kako biste mogli da tražite blagodati Gospodara svoga i da biste broj godina znali i da biste vrijeme računali i sve smo potanko objasnili.", TextEnglish = "And We have made the night and day two signs, and We erased the sign of the night and made the sign of the day visible[731] that you may seek bounty from your Lord and may know the number of years and the account [of time]. And everything We have set out in detail.", TextGerman = "Und Wir haben die Nacht und den Tag zu zwei Zeichen gemacht. Dann haben Wir das Zeichen der Nacht ausgelöscht und das Zeichen des Tages hell gemacht, damit ihr nach Huld von eurem Herrn trachtet und damit ihr die Zahl der Jahre und die (Zeit)rechnung wißt. Und alles haben Wir ganz ausführlich dargelegt." });
                        surah17.VerseList.Add(new Verse { Number = 13, TextBosnian = "I svakom čovjeku Mi ono što uradi o vrat privezujemo, a na Kijametskom danu pokazat ćemo mu knjigu koju će otvorenu zateći:", TextEnglish = "And [for] every person We have imposed his fate upon his neck,[732] and We will produce for him on the Day of Resurrection a record which he will encounter spread open.", TextGerman = "Jedem Menschen haben Wir sein Vorzeichen an seinem Hals befestigt. Und am Tag der Auferstehung bringen Wir ihm ein Buch heraus, das er aufgeschlagen vorfinden wird:" });
                        surah17.VerseList.Add(new Verse { Number = 14, TextBosnian = "Čitaj knjigu svoju, dosta ti je danas to što ćeš svoj račun polagati!'", TextEnglish = "[It will be said], 'Read your record. Sufficient is yourself against you this Day as accountant.'", TextGerman = "Lies dein Buch! Du selbst genügst heute als Abrechner über dich." });
                        surah17.VerseList.Add(new Verse { Number = 15, TextBosnian = "Onaj ko slijedi Uputu pomoći će sam sebi time što je slijedi, a onaj ko zaluta - sebi će štetu nanijeti time što luta, i nijedna duša neće nositi grijehe druge. A Mi nijedan narod nismo kaznili dok poslanika nismo poslali!", TextEnglish = "Whoever is guided is only guided for [the benefit of] his soul. And whoever errs only errs against it. And no bearer of burdens will bear the burden of another. And never would We punish until We sent a messenger.", TextGerman = "Wer der Rechtleitung folgt, der ist nur zu seinem eigenen Vorteil rechtgeleitet. Und wer irregeht, der geht nur zu seinem Nachteil irre. Und keine lasttragende (Seele) nimmt die Last einer anderen auf sich. Wir strafen nicht eher, bis Wir einen Gesandten geschickt haben." });
                        surah17.VerseList.Add(new Verse { Number = 16, TextBosnian = "Kad hoćemo da neko naselje uništimo, onima koji su u njemu na raskoš navikli naredimo pokornost a oni u njemu buntovni budu i tako se nad njima obistini Riječ kazne, pa ga do temelja razrušimo.", TextEnglish = "And when We intend to destroy a city, We command its affluent[733] but they defiantly disobey therein; so the word [i.e., deserved decree] comes into effect upon it, and We destroy it with [complete] destruction.", TextGerman = "Und wenn Wir eine Stadt vernichten wollen, befehlen Wir denjenigen, die in ihr üppig leben (, zu freveln), und dann freveln sie in ihr. So bewahrheitet sich das Wort gegen sie, und dann zerstören Wir sie vollständig." });
                        surah17.VerseList.Add(new Verse { Number = 17, TextBosnian = "I koliko smo samo naroda poslije Nuha uništili! A dovoljno je to što Gospodar tvoj u potpunosti zna i vidi grijehe robova Svojih.", TextEnglish = "And how many have We destroyed from the generations after Noah. And sufficient is your Lord, concerning the sins of His servants, as Aware and Seeing.", TextGerman = "Wie viele Geschlechter nach Nuh haben Wir vernichtet! Und es genügt, daß dein Herr die Sünden Seiner Diener wohl kennt und sieht." });
                        surah17.VerseList.Add(new Verse { Number = 18, TextBosnian = "Onome koji želi ovaj - kratkotrajni svijet, Mi mu na njemu brzo dajemo - što hoćemo i kome hoćemo, ali ćemo mu poslije Džehennem pripremiti, u kome će se prezren i odbačen peći.", TextEnglish = "Whoever should desire the immediate[734] - We hasten for him from it what We will to whom We intend. Then We have made for him Hell, which he will [enter to] burn, censured and banished.", TextGerman = "Wer immer das schnell Eintreffende will, dem gewähren Wir darin schnell, was Wir wollen - demjenigen, den Wir wollen; hierauf haben Wir für ihn die Hölle bestimmt, der er ausgesetzt sein wird, mit Vorwürfen behaftet und verstoßen." });
                        surah17.VerseList.Add(new Verse { Number = 19, TextBosnian = "A svako ko za ahiretom teži i zbog njega se trudi, a vjernik je - pa trud takvih će pohvalan biti.", TextEnglish = "But whoever desires the Hereafter and exerts the effort due to it while he is a believer - it is those whose effort is ever appreciated [by Allāh].", TextGerman = "Wer das Jenseits will und sich darum bemüht, wie es ihm zusteht, wobei er gläubig ist, - denen wird für ihr Bemühen gedankt." });
                        surah17.VerseList.Add(new Verse { Number = 20, TextBosnian = "Svima njima, ijednima i drugima, dajemo darove Gospodara tvoga; a darovi Gospodara tvoga nisu nikome zabranjeni.", TextEnglish = "To each [category] We extend - to these and to those - from the gift of your Lord. And never has the gift of your Lord been restricted.", TextGerman = "Sie alle, diese und jene, unterstützen Wir mit etwas von der Gabe deines Herrn. Und die Gabe deines Herrn wird nicht verwehrt." });
                        surah17.VerseList.Add(new Verse { Number = 21, TextBosnian = "Gledaj kako jednima dajemo prednost nad drugima; a na ahiretu će razlika u stepenima i prednostima biti, doista, veća.", TextEnglish = "Look how We have favored [in provision] some of them over others. But the Hereafter is greater in degrees [of difference] and greater in distinction.", TextGerman = "Schau, wie Wir die einen von ihnen vor den anderen auszeichnen; aber das Jenseits ist wahrlich größer an Rangstufen und größer an Auszeichnungen." });
                        surah17.VerseList.Add(new Verse { Number = 22, TextBosnian = "Ne uzimaj uz Allaha nekog drugog boga, pa da prezren i bez podrške budeš!", TextEnglish = "Do not make [as equal] with Allāh another deity and [thereby] become censured and forsaken.", TextGerman = "Setze neben Allah keinen anderen Gott, sonst wirst du gescholten und im Stich gelassen dasitzen." });
                        surah17.VerseList.Add(new Verse { Number = 23, TextBosnian = "Gospodar tvoj zapovijeda da samo Njemu ibadet činite i da roditeljima dobročinstvo činite. Kad jedno od njih dvoje, ili oboje, kod tebe starost dožive, ne reci im ni: 'Uh!', i ne odbij ih od sebe, i obraćaj im se riječima poštovanja punim.", TextEnglish = "And your Lord has decreed that you worship not except Him, and to parents, good treatment. Whether one or both of them reach old age [while] with you, say not to them [so much as], 'uff,'[735] and do not repel them but speak to them a noble word.", TextGerman = "Und dein Herr hat bestimmt, daß ihr nur Ihm dienen und zu den Eltern gütig sein sollt. Wenn nun einer von ihnen oder beide bei dir ein hohes Alter erreichen, so sag nicht zu ihnen: Pfui! und fahre sie nicht an, sondern sag zu ihnen ehrerbietige Worte." });
                        surah17.VerseList.Add(new Verse { Number = 24, TextBosnian = "Iz samilosti prema njima svoje krilo poniznosti spusti i reci: 'Gospodaru moj, smiluj im se, kao što su oni mene, kad sam bio dijete, podizali!'", TextEnglish = "And lower to them the wing of humility out of mercy and say, 'My Lord, have mercy upon them as they brought me up [when I was] small.'", TextGerman = "Und senke für sie aus Barmherzigkeit den Flügel der Demut und sag: Mein Herr, erbarme Dich ihrer, wie sie mich aufgezogen haben, als ich klein war." });
                        surah17.VerseList.Add(new Verse { Number = 25, TextBosnian = "Gospodar vaš najbolje zna šta je u dušama vašim: ako budete poslušni, pa Allah će, doista, oprostiti onima koji se kaju.", TextEnglish = "Your Lord is most knowing of what is within yourselves. If you should be righteous [in intention] - then indeed He is ever, to the often returning [to Him], Forgiving.[736]", TextGerman = "Euer Herr weiß sehr wohl, was in eurem Innersten ist. Wenn ihr rechtschaffen seid, so ist Er gewiß für die sich (zu Ihm) stets Bekehrenden Allvergebend." });
                        surah17.VerseList.Add(new Verse { Number = 26, TextBosnian = "Daj bližnjemu svome pravo njegovo, i siromahu, i putniku namjerniku, ali nipošto ne rasipaj.", TextEnglish = "And give the relative his right, and [also] the poor and the traveler, and do not spend wastefully.[737]", TextGerman = "Und gib dem Verwandten sein Recht, ebenso dem Armen und dem Sohn des Weges. Und handle nicht ganz verschwenderisch." });
                        surah17.VerseList.Add(new Verse { Number = 27, TextBosnian = "Zaista su rasipnici braća šejtanova, a šejtan je Gospodaru svome itekako nezahvalan.", TextEnglish = "Indeed, the wasteful are brothers of the devils, and ever has Satan been to his Lord ungrateful.", TextGerman = "Gewiß, die Verschwender sind die Brüder der Satane; und der Satan ist gegenüber seinem Herrn sehr undankbar." });
                        surah17.VerseList.Add(new Verse { Number = 28, TextBosnian = "A ako moraš da se od njih okreneš, jer i sam od Gospodara svoga milost tražiš i njoj se nadaš, onda im bar birane riječi obećanja uputi.", TextEnglish = "And if you [must] turn away from them [i.e., the needy] awaiting mercy from your Lord which you expect,[738] then speak to them a gentle word.", TextGerman = "Doch wenn du dich nun von ihnen abwendest - im Trachten nach einer Barmherzigkeit von deinem Herrn, die du dir erhoffst -, so sag zu ihnen milde Worte." });
                        surah17.VerseList.Add(new Verse { Number = 29, TextBosnian = "Ne drži ruku svoju stisnutu kao da je za vrat privezana, a nemoj je ni posve pružati, pa da ukoren budeš i kloneš - bez ičega ostaneš.", TextEnglish = "And do not make your hand [as] chained to your neck[739] or extend it completely[740] and [thereby] become blamed and insolvent.", TextGerman = "Und lasse deine Hand nicht an deinem Hals gefesselt sein, strecke sie aber auch nicht vollständig aus, sonst würdest du getadelt und (aller Mittel) entblößt dasitzen." });
                        surah17.VerseList.Add(new Verse { Number = 30, TextBosnian = "Gospodar tvoj, zaista, pruža obilnu opskrbu onome kome hoće, a i uskraćuje je. On, uistinu, u potpunosti zna i vidi robove Svoje.", TextEnglish = "Indeed, your Lord extends provision for whom He wills and restricts [it]. Indeed He is ever, concerning His servants, Aware and Seeing.", TextGerman = "Gewiß, dein Herr gewährt die Versorgung großzügig, wem Er will, und bemißt auch. Gewiß, Er kennt und sieht Seine Diener wohl." });
                        surah17.VerseList.Add(new Verse { Number = 31, TextBosnian = "Ne ubijajte djecu svoju iz straha od neimaštine, i njih i vas Mi hranimo, a ubijati njih doista je veliki grijeh!", TextEnglish = "And do not kill your children for fear of poverty. We provide for them and for you. Indeed, their killing is ever a great sin.", TextGerman = "Und tötet nicht eure Kinder aus Furcht vor Verarmung; Wir versorgen sie und auch euch. Gewiß, sie zu töten ist ein großes Vergehen." });
                        surah17.VerseList.Add(new Verse { Number = 32, TextBosnian = "I ne približavajte se bludu! Doista je to razvrat, i to je ružan put!", TextEnglish = "And do not approach unlawful sexual intercourse.[741] Indeed, it is ever an immorality and is evil as a way.", TextGerman = "Und nähert euch nicht der Unzucht. Gewiß, sie ist etwas Abscheuliches - und wie böse ist der Weg." });
                        surah17.VerseList.Add(new Verse { Number = 33, TextBosnian = "I ne ubijajte nikoga koga je Allah zabranio, osim kada je to zakonom opravdano! A ako je neko, ni kriv ni dužan, ubijen, onda njegovom nasljedniku ili staratelju dajemo vlast, ali neka ni on ne prekoračuje granicu u ubijanju. On će, doista, biti pomognut.", TextEnglish = "And do not kill the soul [i.e., person] which Allāh has forbidden, except by right.[742] And whoever is killed unjustly - We have given his heir authority,[743] but let him not exceed limits in [the matter of] taking life. Indeed, he has been supported [by the law].", TextGerman = "Und tötet nicht die Seele, die Allah verboten hat (zu töten), außer aus einem rechtmäßigen Grund. Wer ungerechterweise getötet wird, dessen nächstem Verwandten haben Wir Ermächtigung erteilt (, Recht einzufordern); doch soll er nicht maßlos im Töten sein, denn ihm wird gewiß geholfen." });
                        surah17.VerseList.Add(new Verse { Number = 34, TextBosnian = "I ne približavajte se imetku siročeta, osim ako želite da ga unaprijedite, sve dok ne postane punoljetno. I ispunjavajte ono na šta ste se obavezali, jer će se za ugovore zaista odgovarati!", TextEnglish = "And do not approach the property of an orphan, except in the way that is best,[744] until he reaches maturity. And fulfill [every] commitment. Indeed, the commitment is ever [that about which one will be] questioned.", TextGerman = "Und nähert euch nicht dem Besitz des Waisenkindes, außer auf die beste Art, bis es seine Vollreife erlangt hat. Und erfüllt die (eingegangene) Verpflichtung. Gewiß, nach der (Erfüllung der) Verpflichtung wird gefragt werden." });
                        surah17.VerseList.Add(new Verse { Number = 35, TextBosnian = "Napunite mjeru kada mjerite i vagajte vagom ispravnom! To je bolje i posljedice su ljepše.", TextEnglish = "And give full measure when you measure, and weigh with an even [i.e., honest] balance. That is the best [way] and best in result.", TextGerman = "Und gebt volles Maß, wenn ihr meßt, und wägt mit der richtigen Waage; das ist besser und eher ein guter Ausgang." });
                        surah17.VerseList.Add(new Verse { Number = 36, TextBosnian = "Ne govori ono što ne znaš! I sluh, i vid, i razum - za sve to će se, zaista, odgovarati.", TextEnglish = "And do not pursue[745] that of which you have no knowledge. Indeed, the hearing, the sight and the heart - about all those [one] will be questioned.", TextGerman = "Und verfolge nicht das, wovon du kein Wissen hast. Gewiß, Gehör, Augenlicht und Herz, - all diese -, danach wird gefragt werden." });
                        surah17.VerseList.Add(new Verse { Number = 37, TextBosnian = "Ne hodi po zemlji nadmeno! Zemlju sigurno ne možeš probiti ni brda visinom dostići.", TextEnglish = "And do not walk upon the earth exultantly. Indeed, you will never tear the earth [apart], and you will never reach the mountains in height.[746]", TextGerman = "Und gehe nicht übermütig auf der Erde einher. Du wirst ja die Erde nicht aufreißen noch die Berge an Höhe erreichen (können)." });
                        surah17.VerseList.Add(new Verse { Number = 38, TextBosnian = "Sve ogavnosti što su u tome, tvome su Gospodaru mrske.", TextEnglish = "All that [i.e., the aforementioned] - its evil is ever, in the sight of your Lord, detested.", TextGerman = "Das schlechte (Verhalten) in alledem ist bei deinem Herrn verabscheut." });
                        surah17.VerseList.Add(new Verse { Number = 39, TextBosnian = "To je mudrost koju ti Gospodar tvoj objavljuje. I ne pridodaji Allahu drugog boga da ne bi u Džehennem bio bačen, prekoren i odbačen.", TextEnglish = "That is from what your Lord has revealed to you, [O Muḥammad], of wisdom. And, [O mankind], do not make [as equal] with Allāh another deity, lest you be thrown into Hell, blamed and banished.", TextGerman = "Das ist etwas von dem, was dir dein Herr an Weisheit (als Offenbarung) eingegeben hat. Und setze neben Allah keinen anderen Gott, sonst wirst du in die Hölle geworfen, getadelt und verstoßen." });
                        surah17.VerseList.Add(new Verse { Number = 40, TextBosnian = "Zar je vaš Gospodar vas sinovima obdario, a Sebi, kao kćeri, meleke uzeo?! Vi, zaista, izgovarate krupne riječi.", TextEnglish = "Then, has your Lord chosen you for [having] sons and taken [i.e., adopted] from among the angels daughters? Indeed, you say a grave saying.", TextGerman = "Hat denn euer Herr für euch die Söhne erwählt und Sich selbst unter den Engeln Töchter genommen? Ihr sagt da fürwahr ein gewaltiges Wort." });
                        surah17.VerseList.Add(new Verse { Number = 41, TextBosnian = "U Kur'anu smo mnoštvo opomena naveli da bi oni pouku izvukli, ali njima on samo odbojnost povećava.", TextEnglish = "And We have certainly diversified [the contents] in this Qur’ān that they [i.e., mankind] may be reminded, but it does not increase them [i.e., the disbelievers] except in aversion.", TextGerman = "Wir haben doch in diesem Qur’an (die Botschaft) verschiedenartig dargelegt, damit sie bedenken, doch mehrt dies ihnen nur die Abneigung." });
                        surah17.VerseList.Add(new Verse { Number = 42, TextBosnian = "Reci: 'Da pored Njega postoje drugi bogovi, kao što oni govore, oni bi onda potražili put do Vlasnika Arša.'", TextEnglish = "Say, [O Muḥammad], 'If there had been with Him [other] gods, as they say, then they [each] would have sought to the Owner of the Throne a way.'[747]", TextGerman = "Sag: Wenn es neben Ihm noch (andere) Götter gäbe, wie sie sagen, dann würden sie wahrlich nach einem Weg zum Besitzer des Thrones trachten." });
                        surah17.VerseList.Add(new Verse { Number = 43, TextBosnian = "Slavljen je On i visinom velikom uzvišen naspram onoga što oni govore!", TextEnglish = "Exalted is He and high above what they say by great sublimity.", TextGerman = "Preis sei Ihm! Überaus hoch Erhaben ist Er über das, was sie sagen." });
                        surah17.VerseList.Add(new Verse { Number = 44, TextBosnian = "Njega veličaju sedmera nebesa, i Zemlja, i oni na njima; i ne postoji ništa što Ga ne veliča, hvaleći Ga; ali vi ne razumijete veličanje njihovo. On je, doista, Onaj Koji ne žuri s kaznom i Onaj Koji oprašta grijehe.", TextEnglish = "The seven heavens and the earth and whatever is in them exalt Him. And there is not a thing except that it exalts [Allāh] by His praise, but you do not understand their [way of] exalting. Indeed, He is ever Forbearing[748] and Forgiving.", TextGerman = "Ihn preisen die sieben Himmel und die Erde, und wer in ihnen ist. Es gibt nichts, was Ihn nicht lobpreist; ihr aber versteht ihr Preisen nicht. Gewiß, Er ist Nachsichtig und Allvergebend." });
                        surah17.VerseList.Add(new Verse { Number = 45, TextBosnian = "A kada učiš Kur'an, između tebe i onih koji u ahiret ne vjeruju Mi zastor nevidljivi koji razdvaja stavimo.", TextEnglish = "And when you recite the Qur’ān, We put between you and those who do not believe in the Hereafter a concealed partition.[749]", TextGerman = "Wenn du den Qur’an vorträgst, machen Wir zwischen dir und denjenigen, die an das Jenseits nicht glauben, einen unsichtbaren Vorhang." });
                        surah17.VerseList.Add(new Verse { Number = 46, TextBosnian = "a na srca njihova pokrivače da ga ne bi razumjeli, i gluhim ih učinimo. I kada ti u Kur'anu spomeneš Gospodara svoga da Jedini je, oni iz odbojnosti okrenu leđa.", TextEnglish = "And We have placed over their hearts coverings, lest they understand it, and in their ears deafness. And when you mention your Lord alone in the Qur’ān, they turn back in aversion.", TextGerman = "Und Wir legen auf ihre Herzen Hüllen, so daß sie ihn nicht verstehen, und in ihre Ohren Schwerhörigkeit. Und wenn du im Qur’an deinen Herrn allein (ohne ihre Götter) erwähnst, kehren sie aus Abneigung den Rücken." });
                        surah17.VerseList.Add(new Verse { Number = 47, TextBosnian = "Mi dobro znamo šta oni žele da čuju kada dolaze da te prisluškuju, i o čemu se sašaptavaju kad zulumćari govore: 'Vi slijedite samo opčinjena čovjeka!'", TextEnglish = "We are most knowing of how they listen to it when they listen to you and [of] when they are in private conversation, when the wrongdoers say, 'You follow not but a man affected by magic.'", TextGerman = "Wir wissen sehr wohl, worauf sie hören, wenn sie dir zuhören und (auch) wenn sie sich in vertraulichen Gesprächen befinden, wenn die Ungerechten sagen: „Ihr folgt doch nur einem Mann, der einem Zauber verfallen ist." });
                        surah17.VerseList.Add(new Verse { Number = 48, TextBosnian = "Pogledaj kako oni tebe opisuju, pa onda lutaju i ne mogu da nađu Pravi put.", TextEnglish = "Look how they strike for you comparisons;[750] but they have strayed, so they cannot [find] a way.", TextGerman = "Schau, wie sie dir Gleichnisse prägen, und so sind sie abgeirrt, daß sie keinen Weg (mehr) finden können." });
                        surah17.VerseList.Add(new Verse { Number = 49, TextBosnian = "Oni govore: 'Zar kad se u kosti i prašinu pretvorimo, zar ćemo novim stvaranjem, doista, oživljeni biti?!'", TextEnglish = "And they say, 'When we are bones and crumbled particles, will we [truly] be resurrected as a new creation?'", TextGerman = "Und sie sagen: „Sollen wir etwa, wenn wir (bereits) Knochen und Überreste geworden sind, denn wirklich als neue Schöpfung auferweckt werden?" });
                        surah17.VerseList.Add(new Verse { Number = 50, TextBosnian = "Reci: 'Budite, ako možete, kamenje ili gvožđe", TextEnglish = "Say, 'Be you stones or iron[751]", TextGerman = "Sag: „Seid Steine oder Eisen" });
                        surah17.VerseList.Add(new Verse { Number = 51, TextBosnian = "ili bilo kakvo stvorenje za koje mislite da ne može biti oživljeno!' 'A ko će nas u život vratiti?', upitat će oni. Ti reci: 'Onaj Koji vas je i prvi put stvorio', a oni će prema tebi odmahnuti glavama svojim i upitati: 'Kada to?' Ti reci: 'Možda ubrzo!'", TextEnglish = "Or [any] creation of that which is great[752] within your breasts.' And they will say, 'Who will restore us?' Say, 'He who brought you forth the first time.' Then they will nod their heads toward you[753] and say, 'When is that?' Say, 'Perhaps it will be soon -", TextGerman = "oder etwas Erschaffenes von der Art, die in eurer Vorstellung noch schwerwiegender wäre. Dann werden sie sagen: „Wer wird uns (ins Leben) zurückbringen? Sag: „Derjenige, Der euch das erste Mal erschaffen hat. Dann werden sie vor dir die Köpfe schütteln und sagen: ,,Wann wird das sein? Sag: „Vielleicht wird es bald sein." });
                        surah17.VerseList.Add(new Verse { Number = 52, TextBosnian = "Bit će Onog dana kad vas On pozove, i vi ćete se, s hvalom Njemu, odazvati, i pomisliti da ste samo malo vremena ostali.", TextEnglish = "On the Day He will call you and you will respond with praise of Him and think that you had not remained [in the world] except for a little.'", TextGerman = "Am Tag, da Er euch rufen wird und da ihr mit Seinem Lob antworten und meinen werdet, ihr hättet nur ein wenig verweilt." });
                        surah17.VerseList.Add(new Verse { Number = 53, TextBosnian = "Reci robovima Mojim da govore samo lijepe riječi, ili će šejtan posijati neprijateljstvo među njima; šejtan je, doista, čovjekov otvoreni neprijatelj.", TextEnglish = "And tell My servants to say that which is best. Indeed, Satan induces [dissension] among them. Indeed Satan is ever, to mankind, a clear enemy.", TextGerman = "Und sag Meinen Dienern, sie sollen das, was am besten ist, sagen. Gewiß, der Satan stachelt zwischen ihnen (zu Zwietracht) auf. Der Satan ist ja dem Menschen ein deutlicher Feind." });
                        surah17.VerseList.Add(new Verse { Number = 54, TextBosnian = "Gospodar vaš najbolje vas poznaje: ako hoće, On će vam se smilovati ili će vas, ako hoće, na muke staviti. A nismo te poslali sa zaduženjem da im Uputu daš.", TextEnglish = "Your Lord is most knowing of you. If He wills, He will have mercy upon you; or if He wills, He will punish you. And We have not sent you, [O Muḥammad], over them as a manager.", TextGerman = "Euer Herr kennt euch sehr wohl; wenn Er will, erbarmt Er Sich eurer, oder wenn Er will, straft Er euch. Und Wir haben dich nicht als Sachwalter über sie gesandt." });
                        surah17.VerseList.Add(new Verse { Number = 55, TextBosnian = "Gospodar tvoj najbolje zna one na nebesima i one na Zemlji; Mi smo jedne vjerovjesnike nad drugima odlikovali, a Davudu smo Zebur dali.", TextEnglish = "And your Lord is most knowing of whoever is in the heavens and the earth. And We have made some of the prophets exceed others [in various ways], and to David We gave the book [of Psalms].", TextGerman = "Und dein Herr kennt diejenigen sehr wohl, die in den Himmeln und auf der Erde sind. Und Wir haben ja einige der Propheten vor anderen bevorzugt. Und Dawud haben Wir ein Buch der Weisheit gegeben." });
                        surah17.VerseList.Add(new Verse { Number = 56, TextBosnian = "Reci: 'Molite se onima koje, pored Njega, smatrate bogovima, pa, oni od vas neće moći nevolju otkloniti niti je preusmjeriti.'", TextEnglish = "Say, 'Invoke those you have claimed [as gods] besides Him, for they do not possess the [ability for] removal of adversity from you or [for its] transfer [to someone else].'", TextGerman = "Sag: Ruft diejenigen an, die ihr außer Ihm angebt; sie vermögen doch von euch das Unheil weder hinwegzunehmen noch abzuwenden." });
                        surah17.VerseList.Add(new Verse { Number = 57, TextBosnian = "Oni kojima se oni mole i sami traže načina kako će se što više Gospodaru svome približiti, i nadaju se milosti Njegovoj i plaše se kazne Njegove. A kazne Gospodara tvoga treba se čuvati.", TextEnglish = "Those whom they invoke[754] seek means of access to their Lord, [striving as to] which of them would be nearest, and they hope for His mercy and fear His punishment. Indeed, the punishment of your Lord is ever feared.", TextGerman = "Diejenigen, die sie anrufen, trachten nach einem Mittel zu ihrem Herrn (und wetteifern), wer von ihnen (Ihm) am nächsten sei, und hoffen auf Seine Barmherzigkeit und fürchten Seine Strafe. Gewiß, die Strafe deines Herrn ist furchtbar." });
                        surah17.VerseList.Add(new Verse { Number = 58, TextBosnian = "I ne postoji nijedno naselje koje Mi prije Kijametskog dana nećemo uništiti ili ga teškoj muci izložiti; to je u Knjizi zapisano.", TextEnglish = "And there is no city but that We will destroy it[755] before the Day of Resurrection or punish it with a severe punishment. That has ever been in the Register[756] inscribed.", TextGerman = "Und es gibt keine Stadt, die Wir nicht vor dem Tag der Auferstehung vernichten oder mit strenger Strafe strafen werden. Dies steht im Buch (der Vorsehung) verzeichnet." });
                        surah17.VerseList.Add(new Verse { Number = 59, TextBosnian = "A da ti pošaljemo velike znakove spriječilo Nas je samo to što su ih prijašnji narodi lažnim smatrali; Semudu smo kao očiti znak kamilu dali, pa su u vezi s njom zulum učinili. A velike znakove šaljemo samo za zastrašivanje.", TextEnglish = "And nothing has prevented Us from sending signs [i.e., miracles] except that the former peoples denied them. And We gave Thamūd the she-camel as a visible sign, but they wronged her. And We send not the signs except as a warning.", TextGerman = "Und nichts (anderes) hat Uns davon abgehalten, die Zeichen (mit den Propheten) zu senden, als daß die Früheren sie für Lüge erklärten. Und Wir gaben den Tamud die deutlich sichtbare Kamelstute, sie aber taten an ihr Unrecht. Und Wir senden die Zeichen (mit den Propheten) nur, um Furcht einzuflößen." });
                        surah17.VerseList.Add(new Verse { Number = 60, TextBosnian = "I kada smo ti rekli: 'Sve ljude Gospodar tvoj, zaista, obuhvaća! A prizor koji smo ti pokazali i drvo prokleto, u Kur'anu spomenuto, iskušenje su za ljude. Mi ih zastrašujemo, ali to samo njihovu osionost pojačava.'", TextEnglish = "And [remember, O Muḥammad], when We told you, 'Indeed, your Lord has encompassed the people.'[757] And We did not make the sight which We showed you[758] except as a trial for the people, as was the accursed tree [mentioned] in the Qur’ān. And We threaten [i.e., warn] them, but it increases them not except in great transgression.", TextGerman = "Und als Wir zu dir sagten: „Gewiß, dein Herr umfaßt die Menschen. Und Wir haben das Gesicht, das Wir dich sehen ließen, nur zu einer Versuchung für die Menschen gemacht, und (ebenso) den verfluchten Baum im Qur’an. Wir flößen ihnen Furcht ein, doch mehrt ihnen dies nur das Übermaß an Auflehnung." });
                        surah17.VerseList.Add(new Verse { Number = 61, TextBosnian = "I kada rekosmo melecima: 'Učinite sedždu Ademu!' - oni svi, osim Iblisa, sedždu učiniše. 'Zar da učinim sedždu onome koga si Ti od gline stvorio?', reče.", TextEnglish = "And [mention] when We said to the angels, 'Prostrate to Adam,' and they prostrated, except for Iblees.[759] He said, 'Should I prostrate to one You created from clay?'", TextGerman = "Und als Wir zu den Engeln sagten: „Werft euch vor Adam nieder. Da warfen sie sich nieder, außer Iblis. Er sagte: „Soll ich mich vor jemandem niederwerfen, den Du aus Lehm erschaffen hast?" });
                        surah17.VerseList.Add(new Verse { Number = 62, TextBosnian = "Reci mi', reče onda, 'evo ovoga koga si iznad mene uzdigao: ako me ostaviš do Kijametskog dana sigurno ću, osim malobrojnih, nad potomstvom njegovim zagospodariti.'", TextEnglish = "[Iblees] said, 'Do You see this one whom You have honored above me? If You delay me [i.e., my death] until the Day of Resurrection, I will surely destroy[760] his descendants, except for a few.'", TextGerman = "Er sagte: „Was meinst Du wohl von diesem, den Du höher geehrt hast als mich? Wenn Du mich bis zum Tag der Auferstehung zurückstellst, werde ich seiner Nachkommenschaft bis auf wenige ganz gewiß die Zügel anlegen." });
                        surah17.VerseList.Add(new Verse { Number = 63, TextBosnian = "Odlazi!', reče On. 'A svakom od njih ko te bude slijedio - Džehennem će svima vama biti obilna kazna.", TextEnglish = "[Allāh] said, 'Go, for whoever of them follows you, indeed Hell will be the recompense of [all of] you - an ample recompense.", TextGerman = "Er sagte: „Geh (deines Weges)! Wer von ihnen dir folgt, gewiß, so ist die Hölle euer Lohn, ein reichlicher Lohn." });
                        surah17.VerseList.Add(new Verse { Number = 64, TextBosnian = "I zavodi glasom svojim koga možeš i potjeraj na njih svoju konjicu i svoju pješadiju, i budi im ortak u imecima, i u djeci, i daji im obećanja - a samo je obmana to što im šejtan obećava,", TextEnglish = "And incite [to senselessness] whoever you can among them with your voice and assault them with your horses and foot soldiers and become a partner in their wealth and their children and promise them.' But Satan does not promise them except delusion.", TextGerman = "Und errege, wen von ihnen du (erregen) kannst, mit deiner Stimme, und biete gegen sie deine Reiterei und dein Fußvolk auf, und habe Anteil an ihrem Besitz und ihren Kindern, und mache ihnen Versprechungen. - Aber der Satan macht ihnen nur Versprechungen in Trug. -" });
                        surah17.VerseList.Add(new Verse { Number = 65, TextBosnian = "ali, ti, doista, nećeš imati nikakve vlasti nad robovima Mojim!' A Gospodar tvoj je dovoljan kao zaštitnik!", TextEnglish = "Indeed, over My [believing] servants there is for you no authority. And sufficient is your Lord as Disposer of affairs.[761]", TextGerman = "„Gewiß, über Meine Diener hast du keine Macht. Und dein Herr genügt als Sachwalter." });
                        surah17.VerseList.Add(new Verse { Number = 66, TextBosnian = "Gospodar vaš je Taj Koji radi vas pokreće lađe po moru da biste tragali za Njegovim obiljem. On je, zbilja, prema vama milostiv.", TextEnglish = "It is your Lord who drives the ship for you through the sea that you may seek of His bounty. Indeed, He is ever, to you, Merciful.", TextGerman = "Euer Herr ist es, der für euch die Schiffe auf dem Meer sanft bewegt, damit ihr nach etwas von Seiner Huld trachtet. Gewiß, Er ist zu euch Barmherzig." });
                        surah17.VerseList.Add(new Verse { Number = 67, TextBosnian = "Kad vas na moru nevolja zadesi, tada nestanu oni koje inače prizivate, postoji samo On. A kad vas On na kopno spasi, vi se okrećete, a čovjek je itekakav nezahvalnik!", TextEnglish = "And when adversity touches you at sea, lost are [all] those you invoke except for Him. But when He delivers you to the land, you turn away [from Him]. And ever is man ungrateful.", TextGerman = "Und wenn euch auf dem Meer ein Unheil widerfährt, entschwinden (euch) diejenigen, die ihr außer Ihm anruft. Aber nachdem Er euch ans (Fest)land errettet hat, wendet ihr euch (von Ihm) ab; der Mensch ist eben sehr undankbar." });
                        surah17.VerseList.Add(new Verse { Number = 68, TextBosnian = "Zar ste sigurni da vas On neće u zemlju utjerati ili da na vas pješčanu oluju neće poslati, pa da onda sebi nikakva zaštitnika nećete naći?!", TextEnglish = "Then do you feel secure that [instead] He will not cause a part of the land to swallow you or send against you a storm of stones? Then you would not find for yourselves an advocate.", TextGerman = "Glaubt ihr denn in Sicherheit davor zu sein, daß Er die Seiten des Festlandes mit euch versinken läßt oder einen Sturm von Steinchen gegen euch sendet und (daß) ihr dann für euch keinen Sachwalter findet?" });
                        surah17.VerseList.Add(new Verse { Number = 69, TextBosnian = "Ili ste možda sigurni da vas On ponovo neće na more izvesti, i buru na vas poslati i potopiti vas zbog neposlušnosti vaše?! Tada ne biste nikoga našli ko bi Nas zbog vas na odgovornost pozvao.", TextEnglish = "Or do you feel secure that He will not send you back into it [i.e., the sea] another time and send upon you a hurricane of wind and drown you for what you denied?[762] Then you would not find for yourselves against Us an avenger.[763]", TextGerman = "Oder glaubt ihr in Sicherheit davor zu sein, daß Er euch ein anderes Mal aufs Meer zurückbringt und einen verheerenden Wind gegen euch sendet und euch ertrinken läßt, weil ihr ungläubig seid, und (daß) ihr dann für euch keinen Gefolgsmann findet, der Uns deswegen belangen könnte?" });
                        surah17.VerseList.Add(new Verse { Number = 70, TextBosnian = "Mi smo sinovima Ademovim počast ukazali: dali smo im da kopnom i morem putuju, i opskrbili ih ukusnim jelima, i nad mnogima koje smo stvorili posebnu im prednost dali.", TextEnglish = "And We have certainly honored the children of Adam and carried them on the land and sea and provided for them of the good things and preferred them over much of what We have created, with [definite] preference.", TextGerman = "Und Wir haben ja die Kinder Adams geehrt; Wir haben sie auf dem Festland und auf dem Meer getragen und sie von den guten Dingen versorgt, und Wir haben sie vor vielen von denen, die Wir erschaffen haben, eindeutig bevorzugt." });
                        surah17.VerseList.Add(new Verse { Number = 71, TextBosnian = "A na Dan kad pozovemo sve ljude s predvodnikom njihovim, oni kojima knjiga njihova u desnu ruku data bude, čitat će knjige svoje i neće im, ni koliko končić na hurminoj košpici, zulum učinjen biti.", TextEnglish = "[Mention, O Muḥammad], the Day We will call forth every people with their record [of deeds].[764] Then whoever is given his record in his right hand - those will read their records, and injustice will not be done to them, [even] as much as a thread [inside the date seed].", TextGerman = "(Gedenke) des Tages, da Wir alle Menschen mit ihrem (jeweiligen) Anführer rufen werden. Wem dann sein Buch in seine rechte Hand gegeben wird, jene werden ihr Buch (ohne weiteres) lesen, und ihnen wird nicht um ein Fädchen Unrecht zugefügt." });
                        surah17.VerseList.Add(new Verse { Number = 72, TextBosnian = "Onaj ko je na ovom svijetu bio slijep, bit će slijep i na ahiretu, i još dalje će zalutati.", TextEnglish = "And whoever is blind[765] in this [life] will be blind in the Hereafter and more astray in way.", TextGerman = "Und wer in diesem (Leben) blind ist, der wird (auch) im Jenseits blind und noch weiter vom Weg abgeirrt sein." });
                        surah17.VerseList.Add(new Verse { Number = 73, TextBosnian = "I zamalo da te oni odvrate od onoga što ti Mi objavljujemo, da bi protiv Nas nešto drugo iznio, i tada bi te oni kao prijatelja prihvatili.", TextEnglish = "And indeed, they were about to tempt you away from that which We revealed to you in order to [make] you invent about Us something else; and then they would have taken you as a friend.", TextGerman = "Beinahe hätten sie dich fürwahr verführt (und) von dem (abgebracht), was Wir dir (als Offenbarung) eingegeben haben, damit du gegen Uns etwas anderes ersinnst, und dann hätten sie dich wahrlich zum Freund genommen." });
                        surah17.VerseList.Add(new Verse { Number = 74, TextBosnian = "A da te nismo učinili čvrstim, gotovo da bi im se malo priklonio,", TextEnglish = "And if We had not strengthened you, you would have almost inclined to them a little.", TextGerman = "Wenn Wir dich nicht gefestigt hätten, hättest du wohl beinahe bei ihnen ein wenig Stütze gesucht." });
                        surah17.VerseList.Add(new Verse { Number = 75, TextBosnian = "i tada bismo ti, doista, dali da iskusiš dvostruku patnju u životu i dvostruku patnju poslije smrti; tada ne bi nikoga našao ko bi ti protiv Nas pomogao.", TextEnglish = "Then [if you had], We would have made you taste double [punishment in] life and double [after] death. Then you would not find for yourself against Us a helper.", TextGerman = "Dann hätten Wir dich fürwahr ein Mehrfaches (an Strafe) im Leben und ein Mehrfaches (an Strafe) im Tod kosten lassen, und hierauf würdest du für dich keinen Helfer gegen Uns finden." });
                        surah17.VerseList.Add(new Verse { Number = 76, TextBosnian = "Pa iako su te umalo iz zemlje pokrenuli da bi te iz nje istjerali, ipak ni oni tada u njoj ne bi dugo, poslije tebe, ostali.", TextEnglish = "And indeed, they were about to provoke [i.e., drive] you from the land [i.e., Makkah] to evict you therefrom. And then [when they do], they will not remain [there] after you, except for a little.[766]", TextGerman = "Und beinahe hätten sie dich fürwahr aus dem Land aufgestört, um dich daraus zu vertreiben; aber dann würden sie nach dir nur ein wenig verweilen." });
                        surah17.VerseList.Add(new Verse { Number = 77, TextBosnian = "To pravilo je važilo za sve one kojima smo prije tebe poslanike poslali; nećeš vidjeti da je naše pravilo promijenjeno.", TextEnglish = "[That is Our] established way for those We had sent before you of Our messengers; and you will not find in Our way any alteration.", TextGerman = "(Das ist) die Gesetzmäßigkeit (, mit der) an denjenigen von Unseren Gesandten (verfahren wurde), die Wir bereits vor dir sandten. Und du wirst bei Unserer Gesetzmäßigkeit keine Abwandlung finden." });
                        surah17.VerseList.Add(new Verse { Number = 78, TextBosnian = "Obavljaj namaz kad Sunce s polovine neba krene, pa do noćne tmine, i Kur'an u namazu u zoru uči, jer učenju Kur'ana u namazu u zoru mnogi prisustvuju.", TextEnglish = "Establish prayer at the decline of the sun [from its meridian] until the darkness of the night[767] and [also] the Qur’ān [i.e., recitation] of dawn.[768] Indeed, the recitation of dawn is ever witnessed.", TextGerman = "Verrichte das Gebet beim Neigen der Sonne bis zum Dunkel der Nacht, und (auch) die (Qur’an-)Lesung (in) der Morgendämmerung. Gewiß, die (Qur’an-)Lesung (in) der Morgendämmerung wird (von den Engeln) bezeugt." });
                        surah17.VerseList.Add(new Verse { Number = 79, TextBosnian = "I bdij dio noći u namazu - tebi dodatnom; na ahiretu će te tvoj Gospodar na hvaljeni položaj postaviti.", TextEnglish = "And from [part of] the night, pray[769] with it [i.e., recitation of the Qur’ān] as additional [worship] for you; it is expected that[770] your Lord will resurrect you to a praised station.[771]", TextGerman = "Und (einen Teil) der Nacht, verbringe ihn damit, zusätzlich für dich. Vielleicht wird dich dein Herr zu einer lobenswerten (Rang)stellung erwecken." });
                        surah17.VerseList.Add(new Verse { Number = 80, TextBosnian = "I reci: 'Gospodaru moj, uvedi me uvodom Istine i izvedi me ishodom Istine! I daruj mi od Sebe moć koja će mi pomoći!'", TextEnglish = "And say, 'My Lord, cause me to enter a sound entrance[772] and to exit a sound exit[773] and grant me from Yourself a supporting authority.'", TextGerman = "Und sag: Mein Herr, gewähre mir einen wahrhaftigen Eingang, und gewähre mir einen wahrhaftigen Ausgang, und schaffe mir von Dir aus eine hilfreiche Macht." });
                        surah17.VerseList.Add(new Verse { Number = 81, TextBosnian = "I reci:'Došla je istina, a nestalo je laži; laž, zaista, nestaje!'", TextEnglish = "And say, 'Truth has come, and falsehood has departed. Indeed is falsehood, [by nature], ever bound to depart.'", TextGerman = "Und sag: Die Wahrheit ist gekommen, und das Falsche geht dahin; das Falsche ist ja dazu bestimmt, dahinzugehen." });
                        surah17.VerseList.Add(new Verse { Number = 82, TextBosnian = "Mi objavljujemo u Kur'anu ono što je lijek i milost vjernicima, a zulumćarima on samo povećava propast.", TextEnglish = "And We send down of the Qur’ān that which is healing and mercy for the believers, but it does not increase the wrongdoers except in loss.", TextGerman = "Und Wir offenbaren vom Qur’an, was für die Gläubigen Heilung und Barmherzigkeit ist; den Ungerechten aber mehrt es nur den Verlust." });
                        surah17.VerseList.Add(new Verse { Number = 83, TextBosnian = "Kad čovjeku kakvu blagodat darujemo, on se okreće i oholo udaljava, a kad ga zadesi zlo, onda očajava.", TextEnglish = "And when We bestow favor upon man [i.e., the disbeliever], he turns away and distances himself; and when evil touches him, he is ever despairing.", TextGerman = "Wenn Wir dem Menschen Gunst erweisen, wendet er sich ab und entfernt sich zur Seite. Wenn ihm aber Schlechtes widerfährt, ist er sehr verzweifelt." });
                        surah17.VerseList.Add(new Verse { Number = 84, TextBosnian = "Reci: 'Svako na svoj način?' postupa, a Gospodar vaš najbolje zna ko je na Pravom putu.'", TextEnglish = "Say, 'Each works according to his manner, but your Lord is most knowing of who is best guided in way.'", TextGerman = "Sag: Jeder handelt nach seiner Weise. Euer Herr weiß sehr wohl, wessen Weg der Rechtleitung eher entspricht." });
                        surah17.VerseList.Add(new Verse { Number = 85, TextBosnian = "Pitaju te o duhu. Reci: 'Duh je nešto što samo Gospodar moj zna, a vama je dato samo malo znanja.'", TextEnglish = "And they ask you, [O Muḥammad], about the soul. Say, 'The soul is of the affair [i.e., concern] of my Lord. And you [i.e., mankind] have not been given of knowledge except a little.'", TextGerman = "Sie fragen dich nach dem Geist. Sag: Der Geist ist vom Befehl meines Herrn, euch aber ist vom Wissen gewiß nur wenig gegeben." });
                        surah17.VerseList.Add(new Verse { Number = 86, TextBosnian = "A da hoćemo, Mi bismo učinili da iščezne ono što smo ti objavili, i ti, poslije, ne bi nikoga našao ko bi ti protiv Nas pomogao,", TextEnglish = "And if We willed, We could surely do away with that which We revealed to you. Then you would not find for yourself concerning it an advocate against Us.", TextGerman = "Und wenn Wir wollten, würden Wir ganz gewiß wegnehmen, was Wir dir (als Offenbarung) eingegeben haben. Hierauf würdest du für dich in dieser (Sache) keinen Sachwalter gegen Uns finden," });
                        surah17.VerseList.Add(new Verse { Number = 87, TextBosnian = "ali, Gospodar tvoj je tebi milostiv i Njegova dobrota prema tebi zaista je velika.", TextEnglish = "Except [We have left it with you] as a mercy from your Lord. Indeed, His favor upon you has ever been great.", TextGerman = "außer Barmherzigkeit von deinem Herrn. Gewiß, Seine Huld zu dir ist ja groß." });
                        surah17.VerseList.Add(new Verse { Number = 88, TextBosnian = "Reci: 'Kad bi se svi ljudi i džini udružili da sačine jedan ovakav Kur'an, oni, kao što je on, ne bi sačinili, pa makar jedni drugima pomagali.'", TextEnglish = "Say, 'If mankind and the jinn gathered in order to produce the like of this Qur’ān, they could not produce the like of it, even if they were to each other assistants.'", TextGerman = "Sag: Wenn sich die Menschen und die Ginn zusammentäten, um etwas beizubringen, was diesem Qur’an gleich wäre, sie brächten nicht seinesgleichen bei, auch wenn sie einander Beistand leisten würden." });
                        surah17.VerseList.Add(new Verse { Number = 89, TextBosnian = "Mi u ovom Kur'anu objašnjavamo ljudima svakojake primjere, ali većina ljudi samo poricanje prihvata.", TextEnglish = "And We have certainly diversified for the people in this Qur’ān from every [kind of] example, but most of the people refused except disbelief.", TextGerman = "Wir haben ja den Menschen in diesem Qur’an ein jedes Gleichnis verschiedenartig dargelegt, doch die meisten Menschen weigern sich (und wollen nichts) außer dem Unglauben." });
                        surah17.VerseList.Add(new Verse { Number = 90, TextBosnian = "I govore: 'Nećemo ti vjerovati sve dok nam iz zemlje izvor-vodu ne izvedeš;", TextEnglish = "And they say, 'We will not believe you until you break open for us from the ground a spring", TextGerman = "Und sie sagen: „Wir werden dir nicht glauben, bis du uns aus der Erde eine Quelle hervorströmen läßt" });
                        surah17.VerseList.Add(new Verse { Number = 91, TextBosnian = "ili dok ne budeš imao vrt od palmi i loze, pa da kroz njeg svukuda rijeke provedeš;", TextEnglish = "Or [until] you have a garden of palm trees and grapes and make rivers gush forth within them in force [and abundance]", TextGerman = "oder (bis) du einen Garten mit Palmen und Rebstöcken hast, in dem du dazwischen Flüsse ausgiebig hervorströmen läßt" });
                        surah17.VerseList.Add(new Verse { Number = 92, TextBosnian = "ili dok na nas nebo u parčadima ne oboriš, kao što tvrdiš; ili dok Allaha i meleke nama sučelice ne dovedeš;", TextEnglish = "Or you make the heaven fall upon us in fragments as you have claimed or you bring Allāh and the angels before [us]", TextGerman = "oder (bis) du den Himmel, wie du behauptet hast, auf uns in Stücken herabfallen läßt oder Allah und die Engel vor unsere Augen bringst" });
                        surah17.VerseList.Add(new Verse { Number = 93, TextBosnian = "ili dok ne budeš imao kuću od zlata, ili dok se na nebo ne uspenješ; a nećemo vjerovati ni da si se uspeo sve dok nam ne doneseš Knjigu da je čitamo.' Reci: 'Uzvišen neka je Gospodar moj!, zar ja nisam samo čovjek, poslanik?!'", TextEnglish = "Or you have a house of ornament [i.e., gold] or you ascend into the sky. And [even then], we will not believe in your ascension until you bring down to us a book we may read.' Say, 'Exalted is my Lord! Was I ever but a human messenger?'", TextGerman = "oder (bis) du ein Haus aus Gold hast oder in den Himmel aufsteigst. Und wir werden nicht an deinen Aufstieg glauben, bis du auf uns ein Buch herabsendest, das wir (selbst) lesen (können). Sag: Preis sei meinem Herrn! Bin ich etwas anderes als ein menschliches Wesen und ein Gesandter?" });
                        surah17.VerseList.Add(new Verse { Number = 94, TextBosnian = "A ljude je, kad im je dolazila objava, odvraćalo od vjerovanja samo to što su govorili: 'Zar je Allah kao poslanika čovjeka poslao?!'", TextEnglish = "And what prevented the people from believing when guidance came to them except that they said, 'Has Allāh sent a human messenger?'", TextGerman = "Und nichts anderes hielt die Menschen davon ab zu glauben, als die Rechtleitung zu ihnen kam, außer daß sie sagten: „Hat denn Allah ein menschliches Wesen als Gesandten geschickt?" });
                        surah17.VerseList.Add(new Verse { Number = 95, TextBosnian = "Reci: 'Kad bi na Zemlji meleki smireno hodili, Mi bismo im s neba meleka za poslanika poslali.'", TextEnglish = "Say, 'If there were upon the earth angels walking securely,[774] We would have sent down to them from the heaven an angel [as a] messenger.'", TextGerman = "Sag: Wenn es auf der Erde Engel gäbe, die (da) in Ruhe umhergingen, hätten Wir ihnen vom Himmel wahrlich einen Engel als Gesandten hinabgesandt." });
                        surah17.VerseList.Add(new Verse { Number = 96, TextBosnian = "Reci: 'Allah je dovoljan da svjedoči za ovo između mene i vas, a zaista On dobro zna i vidi robove Svoje.'", TextEnglish = "Say, 'Sufficient is Allāh as Witness between me and you. Indeed He is ever, concerning His servants, Aware and Seeing.'", TextGerman = "Sag: Allah genügt als Zeuge zwischen mir und euch. Gewiß, Er kennt und sieht Seine Diener wohl." });
                        surah17.VerseList.Add(new Verse { Number = 97, TextBosnian = "Onaj koga Allah uputi - on je upućen, a onoga koga na stranputicu odvede, tome, mimo Njega, nećeš naći zaštitnika. Mi ćemo ih, na Kijametskom danu, sakupiti, licem zemlji okrenute, slijepe, nijeme i gluhe; boravište njihovo bit će Džehennem; kad god mu plamen jenja, pojačat ćemo im oganj.", TextEnglish = "And whoever Allāh guides - he is the [rightly] guided; and whoever He sends astray[775] - you will never find for them protectors besides Him, and We will gather them on the Day of Resurrection [fallen] on their faces - blind, dumb and deaf. Their refuge is Hell; every time it subsides, We increase [for] them blazing fire.", TextGerman = "Wen Allah rechtleitet, der ist (in Wahrheit) rechtgeleitet; wen Er aber in die Irre gehen läßt, für diejenigen wirst du außer Ihm keine Schutzherren finden. Und Wir werden sie am Tag der Auferstehung auf ihren Gesichtern versammeln, (sie wandeln) vor sich hin, blind, stumm, taub. Ihr Zufluchtsort ist die Hölle. Jedesmal, wenn sie schwächer wird, mehren Wir ihnen die Feuerglut." });
                        surah17.VerseList.Add(new Verse { Number = 98, TextBosnian = "To će im biti kazna zato što u znakove i ajete Naše nisu vjerovali i što su govorili: 'Zar kad postanemo kosti i prah, zar ćemo kao nova stvorenja doista biti oživljeni?!'", TextEnglish = "That is their recompense because they disbelieved in Our verses and said, 'When we are bones and crumbled particles, will we [truly] be resurrected [in] a new creation?'", TextGerman = "Das ist ihr Lohn dafür, daß sie Unsere Zeichen verleugnet und gesagt haben: „Sollen wir denn, wenn wir (bereits) Knochen und Überreste geworden sind, fürwahr wieder als neue Schöpfung auferweckt werden?" });
                        surah17.VerseList.Add(new Verse { Number = 99, TextBosnian = "Zar oni ne znaju da je Allah, Stvoritelj nebesa i Zemlje, kadar da stvori slične njima i da im je već odredio čas oživljenja, u koji nema sumnje? Ali zulumćari samo poriču.", TextEnglish = "Do they not see that Allāh, who created the heavens and earth, is [the one] Able to create the likes of them? And He has appointed for them a term, about which there is no doubt. But the wrongdoers refuse except disbelief.", TextGerman = "Sehen sie denn nicht, daß Allah, Der die Himmel und die Erde erschaffen hat, (auch) die Macht hat, ihresgleichen zu erschaffen? Und Er hat ihnen eine Frist gesetzt, an der es keinen Zweifel gibt, doch die Ungerechten weigern sich (und wollen nichts) außer dem Unglauben." });
                        surah17.VerseList.Add(new Verse { Number = 100, TextBosnian = "Reci: 'Da vi posjedujete riznice milosti Gospodara moga, i tada biste škrtarili iz straha da ne potrošite; čovjek je baš tvrdica.", TextEnglish = "Say [to them], 'If you possessed the depositories of the mercy of my Lord, then you would withhold out of fear of spending.' And ever has man been stingy.", TextGerman = "Sag: Wenn ihr über die Schatzkammern der Barmherzigkeit meines Herrn verfügtet, dann würdet ihr aus Furcht vor dem Ausgeben wahrlich zurückhaltend sein, denn der Mensch ist knauserig." });
                        surah17.VerseList.Add(new Verse { Number = 101, TextBosnian = "Mi smo Musau devet očevidnih znamenja dali, pa upitaj sinove Israilove kad je precima njihovim došao i kada mu je faraon rekao: 'Ja mislim, o Musa, da si ti doista opčinjen',", TextEnglish = "And We had certainly given Moses nine evident signs, so ask the Children of Israel [about] when he came to them and Pharaoh said to him, 'Indeed I think, O Moses, that you are affected by magic.'", TextGerman = "Und Wir gaben Musa ja neun klare Zeichen. So frage die Kinder Isra’ils (danach). Als er zu ihnen kam, da sagte Fir’aun zu ihm: „Ich glaube fürwahr, o Musa, daß du einem Zauber verfallen bist." });
                        surah17.VerseList.Add(new Verse { Number = 102, TextBosnian = "a Musa odgovori: 'Ti znaš da ovo nije dao niko drugi nego Gospodar nebesa i Zemlje, kao očigledna znamenja, i ja mislim da ćeš ti, o faraone, sigurno nastradati.'", TextEnglish = "[Moses] said, 'You have already known that none has sent down these [signs] except the Lord of the heavens and the earth as evidence, and indeed I think,[776] O Pharaoh, that you are destroyed.'", TextGerman = "Er sagte: „Du weißt ja, niemand außer dem Herrn der Himmel und der Erde hat diese als einsichtbringende Zeichen herabgesandt. Und ich glaube fürwahr, o Fir’aun, daß du der Vernichtung verfallen bist." });
                        surah17.VerseList.Add(new Verse { Number = 103, TextBosnian = "Faraon odluči da ih iz zemlje istjera, pa mi potopismo i njega i sve one koji su bili s njim,", TextEnglish = "So he intended to drive them from the land, but We drowned him and those with him all together.", TextGerman = "Da wollte er sie aus dem Land aufstören. Wir aber ließen ihn ertrinken, und (auch) alle, die mit ihm waren." });
                        surah17.VerseList.Add(new Verse { Number = 104, TextBosnian = "i poslije toga rekosmo sinovima Israilovim: 'Naselite se u zemlji, pa kad vrijeme ahireta nastupi, sve ćemo vas zajedno dovesti.'", TextEnglish = "And We said after him [i.e., Pharaoh] to the Children of Israel, 'Dwell in the land, and when there comes the promise [i.e., appointment] of the Hereafter, We will bring you forth in [one] gathering.'", TextGerman = "Und Wir sagten nach seiner Vernichtung zu den Kindern Isra’ils: „Bewohnt das Land. Wenn dann das Versprechen vom letzten Mal eintrifft, bringen Wir euch in buntgemischten Gruppen herbei." });
                        surah17.VerseList.Add(new Verse { Number = 105, TextBosnian = "Mi Kur'an s Istinom spuštamo i s Istinom on silazi. A tebe smo poslali samo kao donositelja radosnih vijesti i upozoritelja.", TextEnglish = "And with the truth We have sent it [i.e., the Qur’ān] down, and with the truth it has descended. And We have not sent you, [O Muḥammad], except as a bringer of good tidings and a warner.", TextGerman = "Mit der Wahrheit haben Wir ihn (als Offenbarung) hinabgesandt, und mit der Wahrheit ist er hinabgekommen. Und Wir haben dich nur als Verkünder froher Botschaft und Warner gesandt." });
                        surah17.VerseList.Add(new Verse { Number = 106, TextBosnian = "I kao Kur'an, sve dio po dio ga objavljujemo da bi ga ti ljudima malo-pomalo kazivao, i prema potrebi ga objavljujemo.", TextEnglish = "And [it is] a Qur’ān which We have separated [by intervals] that you might recite it to the people over a prolonged period. And We have sent it down progressively.", TextGerman = "Einen Qur’an haben Wir (offenbart, den Wir in Abschnitte) unterteilt (haben), damit du ihn den Menschen in Abständen vorträgst; und Wir haben ihn wahrlich nach und nach offenbart." });
                        surah17.VerseList.Add(new Verse { Number = 107, TextBosnian = "Reci: 'Vjerovali u njega ili ne vjerovali, oni kojima je još prije objavljivanja njegova dato znanje padaju licem na tlo, čineći sedždu, kad im se on čita.", TextEnglish = "Say, 'Believe in it or do not believe.' Indeed, those who were given knowledge before it[777] - when it is recited to them, they fall upon their faces in prostration,", TextGerman = "Sag: Glaubt daran oder glaubt (eben) nicht. Diejenigen, denen vor ihm das Wissen gegeben wurde, fallen, wenn er ihnen verlesen wird, ehrerbietig auf das Kinn nieder" });
                        surah17.VerseList.Add(new Verse { Number = 108, TextBosnian = "I govore: 'Uzvišen neka je Gospodar naš, obećanje Gospodara našeg se ispunilo!''", TextEnglish = "And they say, 'Exalted is our Lord! Indeed, the promise of our Lord has been fulfilled.'", TextGerman = "und sagen: „Preis sei unserem Herrn! Das Versprechen unseres Herrn ist wahrlich ausgeführt." });
                        surah17.VerseList.Add(new Verse { Number = 109, TextBosnian = "i padaju licem na tle plačući, i on im uvećava strahopoštovanje,", TextEnglish = "And they fall upon their faces weeping, and it [i.e., the Qur’ān] increases them in humble submission.", TextGerman = "Und sie fallen auf das Kinn weinend nieder, und es mehrt ihnen die Demut." });
                        surah17.VerseList.Add(new Verse { Number = 110, TextBosnian = "Reci: 'Zovite Ga: 'Allahu', ili zovite: 'Svemilosni', a kako Ga god budete zvali, Njegova su imena najljepša. Ne izgovaraj na sav glas Kur'an kad molitvu obavljaš, a i ne prigušuj ga; traži sredinu između toga'", TextEnglish = "Say, 'Call upon Allāh or call upon the Most Merciful [ar-Raḥmān]. Whichever [name] you call - to Him belong the best names.' And do not recite [too] loudly in your prayer or [too] quietly but seek between that an [intermediate] way.", TextGerman = "Sag: Ruft Allah oder ruft den Allerbarmer an; welchen ihr auch ruft, Sein sind die schönsten Namen. Und sei nicht zu laut beim Gebet, und sei auch nicht zu leise dabei, sondern suche einen Weg dazwischen." });
                        surah17.VerseList.Add(new Verse { Number = 111, TextBosnian = "I reci: 'Sva hvala Allahu Koji Sebi nije uzeo djeteta i Koji u vlasti nema ortaka, i Kome ne treba zaštitnik zbog nemoći' - i istinski Ga veličaj!", TextEnglish = "And say, 'Praise to Allāh, who has not taken a son and has had no partner in [His] dominion and has no [need of a] protector out of weakness; and glorify Him with [great] glorification.'", TextGerman = "Und sag: (Alles) Lob gehört Allah, Der Sich keine Kinder genommen hat, und es gibt weder einen Teilhaber an Seiner Herrschaft, noch benötigt Er einen Beschützer vor Demütigung. Und verherrliche Ihn doch als den Größten!" });
                        result = surah17;
                        break;
                    #endregion

                    // 
                    #region 18 Al-Kahf
                    case 18:
                        Surah surah18 = new Surah(); surah18.Name = Constants.Surah18Name; surah18.Number = 18; surah18.VerseList = new List<Verse>();
                        surah18.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah18.VerseList.Add(new Verse { Number = 1, TextBosnian = "Sva hvala neka je Allahu Koji Svome robu objavljuje Knjigu, i nije je dao iskrivljenu,", TextEnglish = "[All] praise is [due] to Allāh, who has sent down upon His Servant [Muḥammad (ﷺ)] the Book and has not made therein any deviance.[779]", TextGerman = "(Alles) Lob gehört Allah, Der das Buch (als Offenbarung) auf Seinen Diener herabgesandt und daran nichts Krummes gemacht hat," });
                        surah18.VerseList.Add(new Verse { Number = 2, TextBosnian = "nego ispravnu, da teškom kaznom, koju će On dati, opomene, a da vjernike koji čine dobra djela divnom nagradom obraduje.", TextEnglish = "[He has made it] straight, to warn of severe punishment from Him and to give good tidings to the believers who do righteous deeds that they will have a good reward [i.e., Paradise].", TextGerman = "(ein Buch,) das richtig ist, damit er vor harter Gewalt von Ihm aus warne und den Gläubigen, die rechtschaffene Werke tun, verkünde, daß es für sie schönen Lohn geben wird -," });
                        surah18.VerseList.Add(new Verse { Number = 3, TextBosnian = "U njoj će vječno boraviti,", TextEnglish = "In which they will remain forever", TextGerman = "auf immer darin zu verbleiben -," });
                        surah18.VerseList.Add(new Verse { Number = 4, TextBosnian = "i da upozori one koji govore: 'Allah je Sebi uzeo sina.'", TextEnglish = "And to warn those who say, 'Allāh has taken a son'.", TextGerman = "und (damit er) diejenigen warne, die sagen: „Allah hat Sich Kinder genommen." });
                        surah18.VerseList.Add(new Verse { Number = 5, TextBosnian = "O tome oni ništa ne znaju, a ni preci njihovi. Kako krupna riječ izlazi iz usta njihovih! Oni ne govore drugo do laži!", TextEnglish = "They have no knowledge of it,[780] nor had their fathers. Grave is the word that comes out of their mouths; they speak not except a lie.", TextGerman = "Sie haben kein Wissen davon, und auch nicht ihre Väter. Welch schwerwiegendes Wort kommt aus ihren Mündern heraus. Sie sagen nichts als Lüge." });
                        surah18.VerseList.Add(new Verse { Number = 6, TextBosnian = "Pa zar ćeš ti za njima od tuge svisnuti, ako oni u ovaj govor neće da povjeruju?!", TextEnglish = "Then perhaps you would kill yourself through grief over them, [O Muḥammad], if they do not believe in this message, [and] out of sorrow.", TextGerman = "Vielleicht magst du (aus Gram) noch dich selbst umbringen, wenn sie an diese Botschaft nicht glauben, nachdem sie sich abgewandt haben." });
                        surah18.VerseList.Add(new Verse { Number = 7, TextBosnian = "Sve što je na Zemlji Mi smo kao ukras njoj stvorili da iskušamo ljude ko će od njih bolje postupati", TextEnglish = "Indeed, We have made that which is on the earth adornment for it that We may test them [as to] which of them is best in deed.", TextGerman = "Gewiß, Wir haben (alles), was auf der Erde ist, zu einem Schmuck für sie gemacht, um sie zu prüfen (und festzustellen), wer von ihnen die besten Taten begeht." });
                        surah18.VerseList.Add(new Verse { Number = 8, TextBosnian = "i zaista ćemo Mi sve što je na njoj u opustošeno tlo pretvoriti!", TextEnglish = "And indeed, We will make that which is upon it [into] a barren ground.", TextGerman = "Und Wir werden das, was auf ihr ist, wahrlich zu unfruchtbarem Erdboden machen." });
                        surah18.VerseList.Add(new Verse { Number = 9, TextBosnian = "Misliš li ti da su stanovnici pećine i Rekima bili čudo među znakovima Našim?!", TextEnglish = "Or have you thought that the companions of the cave and the inscription were, among Our signs, a wonder?[781]", TextGerman = "Oder meinst du etwa, daß die Leute der Höhle und der Inschrift ein (besonders) verwunderliches unter Unseren Zeichen sind?" });
                        surah18.VerseList.Add(new Verse { Number = 10, TextBosnian = "Kad se nekoliko momaka u pećinu sklonilo pa reklo: 'Gospodaru naš, daruj nam Svoju milost i daj da ovo naše stanje na dobro izađe.'", TextEnglish = "[Mention] when the youths retreated to the cave and said, 'Our Lord, grant us from Yourself mercy and prepare for us from our affair right guidance.'", TextGerman = "Als die Jünglinge in der Höhle Zuflucht suchten und sagten: „Unser Herr, gib uns Barmherzigkeit von Dir aus, und bereite uns in unserer Angelegenheit einen rechten (Aus)weg." });
                        surah18.VerseList.Add(new Verse { Number = 11, TextBosnian = "I Mi smo ih u pećini dubokim snom za dugo godina uspavali,", TextEnglish = "So We cast [a cover of sleep] over their ears within the cave for a number of years.", TextGerman = "Da ließen Wir sie in der Höhle für eine Anzahl von Jahren in Dauerschlaf fallen." });
                        surah18.VerseList.Add(new Verse { Number = 12, TextBosnian = "poslije smo ih probudili da znamo koja će od dvije skupine bolje izračunati koliko su vremena proboravili.", TextEnglish = "Then We awakened them that We might show which of the two factions was most precise in calculating what [extent] they had remained in time.", TextGerman = "Hierauf erweckten Wir sie auf, um zu wissen, welche der beiden Gruppierungen am richtigsten die Dauer ihres Verweilens erfaßt hat." });
                        surah18.VerseList.Add(new Verse { Number = 13, TextBosnian = "A Mi tebi kazujemo vijest njihovu po Istini. Tu su bili mladići, vjerovali su u Gospodara svoga, a Mi smo im Uputu još više povećali.", TextEnglish = "It is We who relate to you, [O Muḥammad], their story in truth. Indeed, they were youths who believed in their Lord, and We increased them in guidance.", TextGerman = "Wir berichten dir ihre Geschichte der Wahrheit entsprechend. Sie waren Jünglinge, die an ihren Herrn glaubten und denen Wir ihre Rechtleitung mehrten." });
                        surah18.VerseList.Add(new Verse { Number = 14, TextBosnian = "Osnažili smo bili njihova srca kad su se digli i rekli: 'Gospodar naš - Gospodar je nebesa i Zemlje, mi se nećemo pored Njega drugom bogu moliti, jer bismo tada ono što je daleko od istine govorili.", TextEnglish = "And We bound [i.e., made firm] their hearts when they stood up and said, 'Our Lord is the Lord of the heavens and the earth. Never will we invoke besides Him any deity. We would have certainly spoken, then, an excessive transgression.", TextGerman = "Und Wir stärkten ihre Herzen, als sie aufstanden und sagten: „Unser Herr ist der Herr der Himmel und der Erde. Wir werden außer Ihm keinen (anderen) Gott anrufen, sonst würden wir ja etwas Unrechtes sagen." });
                        surah18.VerseList.Add(new Verse { Number = 15, TextBosnian = "Narod ovaj naš je, mimo Njega, druge bogove prihvatio, zašto o njima jasan dokaz ne donesu? A ima li većeg zulumćara od onoga koji o Allahu laž iznosi?", TextEnglish = "These, our people, have taken besides Him deities. Why do they not bring for [worship of] them a clear evidence? And who is more unjust than one who invents about Allāh a lie?'", TextGerman = "Dieses, unser Volk hat sich außer Ihm (andere) Götter genommen. Wenn sie doch für sie eine deutliche Ermächtigung bringen würden! Wer ist denn ungerechter, als wer gegen Allah eine Lüge ersinnt?" });
                        surah18.VerseList.Add(new Verse { Number = 16, TextBosnian = "Kad napustite njih i one koje mimo Allaha obožavaju, sklonite se u pećinu, Gospodar vaš će vas milošću Svojom obasuti i za vas će ono što će vam korisno biti pripremiti.", TextEnglish = "[The youths said to one another], 'And when you have withdrawn from them and that which they worship other than Allāh, retreat to the cave. Your Lord will spread out for you of His mercy and will prepare for you from your affair facility.'", TextGerman = "Und da ihr euch nun von ihnen und von demjenigen, dem sie außer Allah dienen, fernhaltet, so sucht Zuflucht in der Höhle; euer Herr wird über euch (einiges) von Seiner Barmherzigkeit ausbreiten und euch in eurer Angelegenheit eine milde Behandlung bereiten." });
                        surah18.VerseList.Add(new Verse { Number = 17, TextBosnian = "I ti si mogao vidjeti kako Sunce, kada se rađa - obilazi pećinu s desne strane, a kada zalazi - zaobilazi je s lijeve strane, a oni su bili u sredini njezinoj. To je znak Allahov! Koga Allah uputi - on je i upućen, a koga na stranputicu odvede, ti mu nećeš naći zaštitnika koji će ga naputiti.", TextEnglish = "And [had you been present], you would see the sun when it rose, inclining away from their cave on the right, and when it set, passing away from them on the left, while they were [lying] within an open space thereof. That was from the signs of Allāh. He whom Allāh guides is the [rightly] guided, but he whom He sends astray - never will you find for him a protecting guide.", TextGerman = "Und du siehst die Sonne, wenn sie aufgeht, sich von ihrer Höhle zur Rechten wegneigen, und wenn sie untergeht, an ihnen zur Linken vorbeigehen, während sie sich darin in einem Raum befinden. Das gehört zu Allahs Zeichen. Wen Allah rechtleitet, der ist (in Wahrheit) rechtgeleitet; wen Er aber in die Irre gehen läßt, für den wirst du keinen Schutzherrn finden, der ihn den rechten Weg führt." });
                        surah18.VerseList.Add(new Verse { Number = 18, TextBosnian = "I pomislio bi da su budni, ali oni su spavali; i Mi smo ih prevrtali sad na desnu, sad na lijevu stranu, a pas njihov, opruženih prednjih šapa, na ulazu je ležao; da si ih vidio, od njih bi pobjegao i strah bi te uhvatio.", TextEnglish = "And you would think them awake, while they were asleep. And We turned them to the right and to the left, while their dog stretched his forelegs at the entrance. If you had looked at them, you would have turned from them in flight and been filled by them with terror.", TextGerman = "Du meinst, sie seien wach, obwohl sie schlafen. Und Wir drehen sie nach rechts und nach links um, während ihr Hund seine Vorderbeine im Vorraum ausstreckt. Wenn du sie erblicktest, würdest du dich vor ihnen fürwahr zur Flucht kehren und vor ihnen fürwahr mit Schrecken erfüllt sein." });
                        surah18.VerseList.Add(new Verse { Number = 19, TextBosnian = "I Mi smo ih, isto tako, probudili da bi jedni druge pitali. 'Koliko ste ovdje ostali?', upita jedan od njih. 'Ostali smo dan ili dio dana', odgovoriše. 'Gospodar vaš najbolje zna koliko ste ostali', rekoše. 'Pošaljite jednog od vas s ovim srebrenjacima vašim u grad, pa nek vidi u koga je najčistije jelo i neka vam od njega donese hrane i neka bude pažljiv, neka nikome ne govori ništa o vama,", TextEnglish = "And similarly,[782] We awakened them that they might question one another. Said a speaker from among them, 'How long have you remained [here]?' They said, 'We have remained a day or part of a day.' They said, 'Your Lord is most knowing of how long you remained. So send one of you with this silver coin of yours to the city and let him look to which is the best of food and bring you provision from it and let him be cautious. And let no one be aware of you.", TextGerman = "Und so erweckten Wir sie auf, damit sie sich gegenseitig fragten. Einer von ihnen sagte: „Wie lange habt ihr verweilt? Sie sagten: „Verweilt haben wir einen Tag oder den Teil eines Tages. Sie sagten: „Euer Herr weiß am besten, wie (lange) ihr verweilt habt. So schickt einen von euch mit diesen euren Silbermünzen in die Stadt; er soll sehen, welche ihre reinste Speise ist, und euch davon eine Versorgung bringen. Er soll behutsam sein und ja niemanden etwas von euch merken lassen." });
                        surah18.VerseList.Add(new Verse { Number = 20, TextBosnian = "jer, ako oni doznaju za vas, kamenovat će vas ili će vas nasilu u svoju vjeru obratiti, i tada nikad nećete uspjeti!'", TextEnglish = "Indeed, if they come to know of you, they will stone you or return you to their religion. And never would you succeed, then - ever.'", TextGerman = "Denn wenn sie von euch erfahren’, werden sie euch steinigen oder euch (zwangsweise) zu ihrer Glaubensrichtung zurückbringen; dann wird es euch niemals mehr wohl ergehen." });
                        surah18.VerseList.Add(new Verse { Number = 21, TextBosnian = "I Mi smo, isto tako, učinili da oni za njih saznaju, da bi se uvjerili da je istinito Allahovo obećanje i da u čas oživljenja nema nikakve sumnje, kada su se između sebe o njima raspravljali, i rekli: 'Sagradite nad njima kakvu ogradu, Gospodar njihov najbolje zna ko su oni.' A onda oni do čijih se riječi najviše držalo rekoše: 'Napravit ćemo nad njima bogomolju!'", TextEnglish = "And similarly, We caused them to be found that they [who found them] would know that the promise of Allāh is truth and that of the Hour there is no doubt. [That was] when they[783] disputed among themselves about their affair and [then] said, 'Construct over them a structure. Their Lord is most knowing about them.' Said those who prevailed in the matter, 'We will surely take [for ourselves] over them a masjid.'[784]", TextGerman = "So ließen Wir (die Menschen) sie doch entdecken, damit sie wissen, daß Allahs Versprechen wahr ist und daß es an der Stunde keinen Zweifel gibt. Als sie untereinander über ihre Angelegenheit stritten, da sagten sie: „Errichtet über ihnen einen Bau. Ihr Herr weiß am besten über sie Bescheid. Diejenigen, die in ihrer Angelegenheit siegten, sagten: „Wir werden uns über ihnen ganz gewiß eine Gebetsstätte einrichten." });
                        surah18.VerseList.Add(new Verse { Number = 22, TextBosnian = "Neki će reći: 'Bila su trojica, pas njihov je bio četvrti', a neki će govoriti: 'Bila su peterica, pas njihov je bio šesti', nagađajući o onome što je čulima nedokučivo, dok će neki reći: 'Bila su sedmerica, a pas njihov bio je osmi.' Reci: 'Gospodar moj najbolje zna njihov broj, samo malo njih to zna. Zato ne raspravljaj o njima osim površno i lijepo, i ne pitaj o njima od njih nikoga!'", TextEnglish = "They [i.e., people] will say there were three, the fourth of them being their dog; and they will say there were five, the sixth of them being their dog - guessing at the unseen; and they will say there were seven, and the eighth of them was their dog. Say, [O Muḥammad], 'My Lord is most knowing of their number. None knows them except a few. So do not argue about them except with an obvious argument[785] and do not inquire about them among [the speculators] from anyone.'", TextGerman = "(Manche) werden sagen: „(Es waren ihrer) drei, ihr Hund war der vierte von ihnen. Und (manche) sagen: „(Es waren ihrer) fünf, der sechste von ihnen war ihr Hund. - Ein Herumraten über das Verborgene. Und (manche) sagen: „(Es waren ihrer) sieben, und der achte von ihnen war ihr Hund. Sag: Mein Herr kennt ihre Zahl am besten; nur wenige kennen sie. Darum streite über sie nur in offensichtlichem Streit, und frage niemanden von ihnen um Auskunft über sie." });
                        surah18.VerseList.Add(new Verse { Number = 23, TextBosnian = "I ni u kom slučaju ne reci: 'Uradit ću to sigurno sutra!',", TextEnglish = "And never say of anything, 'Indeed, I will do that tomorrow,'", TextGerman = "Und sag ja nur nicht von einer Sache: „Ich werde dies morgen tun," });
                        surah18.VerseList.Add(new Verse { Number = 24, TextBosnian = "ne dodavši: 'Ako Allah da!' A kada zaboraviš, sjeti se Gospodara svoga i reci: 'Gospodar moj će me uputiti na ono što je bliže ispravnom od ovoga.'", TextEnglish = "Except [when adding], 'If Allāh wills.' And remember your Lord when you forget [it] and say, 'Perhaps my Lord will guide me to what is nearer than this to right conduct.'", TextGerman = "außer (du fügst hinzu): „Wenn Allah will. Und gedenke deines Herrn, wenn du (etwas) vergessen hast, und sag: „Vielleicht leitet mich mein Herr zu etwas, was dem rechten Ausweg näher kommt als dies." });
                        surah18.VerseList.Add(new Verse { Number = 25, TextBosnian = "A oni su ostali u pećini svojoj tri stotine godina, a k tome dodajte još devet.", TextEnglish = "And they remained in their cave for three hundred years and exceeded by nine.[786]", TextGerman = "Und sie verweilten in ihrer Höhle dreihundert Jahre und noch neun dazu." });
                        surah18.VerseList.Add(new Verse { Number = 26, TextBosnian = "Reci: 'Allah najbolje zna koliko su ostali; ono što je čulima nedokučivo samo On zna. Kako On sve vidi, kako On sve čuje! Oni nemaju drugog zaštitnika osim Njega, a On ne pridružuje nikoga u vlasti Svojoj.", TextEnglish = "Say, 'Allāh is most knowing of how long they remained. He has [knowledge of] the unseen [aspects] of the heavens and the earth. How Seeing is He and how Hearing! They have not besides Him any protector, and He shares not His legislation with anyone.'", TextGerman = "Sag: Allah weiß am besten, wie (lange) sie verweilten. Sein ist das Verborgene der Himmel und der Erde. Wie vorzüglich ist Er als Allsehender, und wie vorzüglich ist Er als Allhörender! Sie haben außer Ihm keinen Schutzherrn, und Er beteiligt an Seiner Urteilsgewalt niemanden." });
                        surah18.VerseList.Add(new Verse { Number = 27, TextBosnian = "A ti kazuj iz Knjige Gospodara svoga ono što ti se objavljuje, Njegovim riječima nema preinačitelja, i ti, osim kod Njega, nećeš naći utočišta nikakva.", TextEnglish = "And recite, [O Muḥammad], what has been revealed to you of the Book of your Lord. There is no changer of His words, and never will you find in other than Him a refuge.", TextGerman = "Und verlies, was dir vom Buch deines Herrn (als Offenbarung) eingegeben worden ist. Es gibt niemanden, der Seine Worte abändern könnte, und du wirst außer (bei) Ihm keine Zuflucht finden." });
                        surah18.VerseList.Add(new Verse { Number = 28, TextBosnian = "Budi strpljiv uz one koji se Gospodaru svome mole ujutro i navečer u želji za Licem Njegovim, i ne skidaj očiju svojih s njih iz želje za sjajem u životu na ovome svijetu, i ne slušaj onoga čije smo srce da Nas spominje nehajnim učinili, koji strast svoju slijedi i čiji su postupci propali.", TextEnglish = "And keep yourself patient [by being] with those who call upon their Lord in the morning and the evening, seeking His face [i.e., acceptance]. And let not your eyes pass beyond them, desiring adornments of the worldly life, and do not obey one whose heart We have made heedless of Our remembrance and who follows his desire and whose affair is ever [in] neglect.[787]", TextGerman = "Halte dich geduldig zurück zusammen mit denen, die ihren Herrn morgens und abends anrufen, im Begehren nach Seinem Angesicht. Und deine Augen sollen nicht über sie hinwegsehen, indem du den Schmuck des diesseitigen Lebens begehrst’. Und gehorche nicht jemandem, dessen Herz Wir Unserem Gedenken gegenüber unachtsam gemacht haben, der seiner Neigung folgt und dessen Angelegenheit (durch) Maßlosigkeit (ausgezeichnet) ist." });
                        surah18.VerseList.Add(new Verse { Number = 29, TextBosnian = "I reci: 'Istina dolazi od Gospodara vašeg, pa ko hoće - neka vjeruje, a ko hoće - neka ne vjeruje!' Mi smo zulumćarima pripremili Vatru čiji će ih plamen sa svih strana obuhvatiti; ako zamole pomoć, pomoći će im se tekućinom poput rastopljene kovine koja će lica ispeći. Užasna li pića i grozna li boravišta!", TextEnglish = "And say, 'The truth is from your Lord, so whoever wills - let him believe; and whoever wills - let him disbelieve.' Indeed, We have prepared for the wrongdoers a fire whose walls will surround them. And if they call for relief, they will be relieved with water like murky oil, which scalds [their] faces. Wretched is the drink, and evil is the resting place.", TextGerman = "Und sag: (Es ist) die Wahrheit von eurem Herrn. Wer nun will, der soll glauben, und wer will, der soll ungläubig sein. Gewiß, Wir haben den Ungerechten ein Feuer bereitet, dessen Zeltdecke sie umfangen hält. Und wenn sie um Hilfe rufen, wird ihnen mit Wasser wie geschmolzenem Erz geholfen, das die Gesichter versengt - ein schlimmes Getränk und ein böser Rastplatz!" });
                        surah18.VerseList.Add(new Verse { Number = 30, TextBosnian = "One koji budu vjerovali i dobra djela činili - a Mi doista ne uništavamo nagradu onome koji je dobra djela činio –", TextEnglish = "Indeed, those who have believed and done righteous deeds - indeed, We will not allow to be lost the reward of any who did well in deeds.", TextGerman = "Diejenigen, die glauben und rechtschaffene Werke tun - gewiß, Wir lassen den Lohn derer nicht verlorengehen, die die besten Taten begehen." });
                        surah18.VerseList.Add(new Verse { Number = 31, TextBosnian = "njih čekaju sigurno vrtovi Adna, kroz koje rijeke teku, u njima će se narukvicama od zlata kititi i u zelena odijela od dibe i kadife oblačiti, na divanima će u njima naslonjeni biti. Divne li nagrade i krasna li boravišta!", TextEnglish = "Those will have gardens of perpetual residence; beneath them rivers will flow. They will be adorned therein with bracelets of gold and will wear green garments of fine silk and brocade, reclining therein on adorned couches. Excellent is the reward, and good is the resting place.", TextGerman = "Für jene wird es die Gärten Edens geben, wo unter(halb von) ihnen Flüsse strömen. Geschmückt sind sie darin mit Armreifen aus Gold, und sie tragen grüne Gewänder aus Seidenbrokat und schwerem Brokat, indem sie sich darin auf überdachten Liegen lehnen. Wie trefflich ist die Belohnung und wie schön der Rastplatz!" });
                        surah18.VerseList.Add(new Verse { Number = 32, TextBosnian = "I navedi im kao primjer dva čovjeka: jednom od njih smo dva vrta lozom zasađena dali i palmama ih opasali, a između njih njive postavili.", TextEnglish = "And present to them an example of two men: We granted to one of them two gardens of grapevines, and We bordered them with palm trees and placed between them [fields of] crops.", TextGerman = "Und präge ihnen das Gleichnis von zwei Männern. Dem einen der beiden gaben Wir zwei Gärten mit Rebstöcken, und Wir umgaben sie mit Palmen und legten dazwischen (sonstige) Pflanzungen an." });
                        surah18.VerseList.Add(new Verse { Number = 33, TextBosnian = "Oba vrta davala su svoj plod, ničega nije manjkalo, a kroz sredinu njihovu smo rijeku proveli.", TextEnglish = "Each of the two gardens produced its fruit and did not fall short thereof in anything. And We caused to gush forth within them a river.", TextGerman = "Beide Gärten brachten ihren Ernteertrag hervor und ließen es in nichts davon fehlen. Und Wir ließen dazwischen einen Fluß hervorströmen." });
                        surah18.VerseList.Add(new Verse { Number = 34, TextBosnian = "On je i drugih plodova imao, i reče drugu svome, dok je s njim razgovarao: 'Od tebe sam bogatiji i jačeg sam roda!'", TextEnglish = "And he had fruit, so he said to his companion while he was conversing with him, 'I am greater than you in wealth and mightier in [numbers of] men.'", TextGerman = "Er hatte (daraus reichlich) Früchte. Da sagte er zu seinem Gefährten, während er mit ihm einen Wortwechsel führte: „Ich habe mehr Besitz als du und auch eine mächtigere Schar." });
                        surah18.VerseList.Add(new Verse { Number = 35, TextBosnian = "I uđe u vrt svoj, čineći zulum sebi, govoreći: 'Ne mislim da će ovaj ikada propasti,", TextEnglish = "And he entered his garden while he was unjust to himself.[788] He said, 'I do not think that this will perish - ever.", TextGerman = "Und er betrat seinen Garten, während er sich selbst Unrecht tat. Er sagte: „Ich glaube nicht, daß dieser (Garten) jemals zugrunde gehen wird," });
                        surah18.VerseList.Add(new Verse { Number = 36, TextBosnian = "i ne mislim da će Smak svijeta ikada doći; a ako budem vraćen Gospodaru svome, sigurno ću nešto bolje od ovoga naći.'", TextEnglish = "And I do not think the Hour will occur. And even if I should be brought back to my Lord, I will surely find better than this as a return.'", TextGerman = "und ich glaube nicht, daß die Stunde (des Gerichts) sich einstellen wird. Und wenn ich zu meinem Herrn zurückgebracht werde, werde ich ganz gewiß etwas Besseres als ihn als Rückzugsort finden." });
                        surah18.VerseList.Add(new Verse { Number = 37, TextBosnian = "I reče mu drug njegov, dok je s njim razgovarao: 'Zar ne vjeruješ u Onoga Koji te je od Zemlje stvorio, zatim od kapi sjemena, i najzad te potpunim čovjekom učinio?", TextEnglish = "His companion said to him while he was conversing with him, 'Have you disbelieved in He who created you from dust and then from a sperm-drop and then proportioned you [as] a man?", TextGerman = "Sein Gefährte sagte zu ihm, während er sich mit ihm unterhielt: „Verleugnest du denn Denjenigen, Der dich aus Erde, hierauf aus einem Samentropfen erschaffen und hierauf dich zu einem Mann geformt hat?" });
                        surah18.VerseList.Add(new Verse { Number = 38, TextBosnian = "Što se mene tiče, On, Allah, moj je Gospodar i ja Gospodaru svome ne pridružujem u obožavanju nikoga.", TextEnglish = "But as for me, He is Allāh, my Lord, and I do not associate with my Lord anyone.", TextGerman = "Aber, was mich betrifft: Er, Allah, ist mein Herr, und ich geselle meinem Herrn niemanden bei." });
                        surah18.VerseList.Add(new Verse { Number = 39, TextBosnian = "A zašto nisi, kad si u vrt ušao, rekao: 'Mašallah! Snaga je samo u Allaha!' Ako vidiš da je u mene manje blaga i manje roda nego u tebe,", TextEnglish = "And why did you, when you entered your garden, not say, 'What Allāh willed [has occurred]; there is no power except in Allāh'? Although you see me less than you in wealth and children,", TextGerman = "Würdest du doch, wenn du deinen Garten betrittst, sagen: ,(Es sei,) was Allah will; es gibt keine Kraft außer durch Allah’! Wenn du auch siehst, daß ich weniger Besitz und Kinder habe als du," });
                        surah18.VerseList.Add(new Verse { Number = 40, TextBosnian = "pa Gospodar moj može mi bolji vrt od tvoga dati, a na tvoj nepogodu s neba poslati, pa da ostane samo klizava ledina bez ičega,", TextEnglish = "It may be that my Lord will give me [something] better than your garden and will send upon it a [disastrous] penalty from the sky, and it will become a smooth, dusty ground,", TextGerman = "so wird mein Herr mir vielleicht etwas Besseres als deinen Garten geben und über ihn aufeinanderfolgende Strafe vom Himmel senden, so daß er zu schlüpfrigem Erdboden wird," });
                        surah18.VerseList.Add(new Verse { Number = 41, TextBosnian = "ili da mu voda u ponor ode pa da je ne mogneš pronaći nikada.'", TextEnglish = "Or its water will become sunken [into the earth], so you would never be able to seek it.'", TextGerman = "oder daß sein Wasser versickert sein wird, so daß du es nicht mehr wirst ausfindig machen können." });
                        surah18.VerseList.Add(new Verse { Number = 42, TextBosnian = "I propadoše plodovi njegovi i on poče pljeskati rukama svojim žaleći za onim što je na njega utrošio a loza se bijaše povaljala po podupiračima svojim - i govoraše: 'Kamo sreće da Gospodaru svome nisam nikoga u obožavanju pridruživao!'", TextEnglish = "And his fruits were encompassed [by ruin], so he began to turn his hands about [in dismay] over what he had spent on it, while it had collapsed upon its trellises, and said, 'Oh, I wish I had not associated with my Lord anyone.'[789]", TextGerman = "Seine Früchte wurden ringsum erfaßt. Da begann er, seine Handflächen umzudrehen wegen dessen, was er für ihn ausgegeben hatte, während er wüst in Trümmern lag, und zu sagen: „O hätte ich doch meinem Herrn niemanden beigesellt!" });
                        surah18.VerseList.Add(new Verse { Number = 43, TextBosnian = "I nije imao nikoga ko bi mu mogao pomoći, osim Allaha; a sam sebi nije mogao pomoći.", TextEnglish = "And there was for him no company to aid him other than Allāh, nor could he defend himself.", TextGerman = "Und er hatte weder eine Schar, die ihm anstatt Allahs half, noch konnte er sich selbst helfen." });
                        surah18.VerseList.Add(new Verse { Number = 44, TextBosnian = "Tada može pomoći samo Allah, Istiniti, On daje najbolju nagradu i čini da se sve na najbolji način okonča.", TextEnglish = "There[790] the authority is [completely] for Allāh, the Truth. He is best in reward and best in outcome.", TextGerman = "In dem Fall gehört (all) die Schutzherrschaft Allah, dem Wahren. Er ist der Beste im Belohnen, und Er verschafft den besten Ausgang." });
                        surah18.VerseList.Add(new Verse { Number = 45, TextBosnian = "Navedi im kao primjer da je život na ovom svijetu kao bilje, koje i poslije natapanja vodom, koju Mi s neba spuštamo, ipak postane suho i vjetrovi ga raznesu. A Allah nad svime ima moć.", TextEnglish = "And present to them the example of the life of this world, [its being] like rain which We send down from the sky, and the vegetation of the earth mingles with it[791] and [then] it becomes dry remnants, scattered by the winds. And Allāh is ever, over all things, Perfect in Ability.", TextGerman = "Und präge ihnen das Gleichnis vom diesseitigen Leben. (Es ist) wie Wasser, das Wir vom Himmel hinabkommen lassen, worauf sich damit das Gewächs der Erde vermischt. Dann wird es zu vertrocknetem Zeug, das die Winde verwehen. Und Allah hat ja zu allem völlig die Macht." });
                        surah18.VerseList.Add(new Verse { Number = 46, TextBosnian = "Imetak i sinovi ukras su u životu na Ovom svijetu, a dobra djela koja ostaju, bolja su kod Gospodara tvoga po nagradi i bolja po nadi.", TextEnglish = "Wealth and children are [but] adornment of the worldly life. But the enduring good deeds are better to your Lord[792] for reward and better for [one's] hope.", TextGerman = "Der Besitz und die Söhne sind der Schmuck des diesseitigen Lebens. Das Bleibende aber, die rechtschaffenen Werke -, sie sind bei deinem Herrn besser hinsichtlich der Belohnung und besser hinsichtlich der Hoffnung." });
                        surah18.VerseList.Add(new Verse { Number = 47, TextBosnian = "Na dan kada planine uklonimo, i kada vidiš da je površina Zemlje pregledna - a njih smo već sakupili, ni jednog nismo izostavili,", TextEnglish = "And [warn of] the Day when We will remove the mountains and you will see the earth exposed,[793] and We will gather them and not leave behind from them anyone.", TextGerman = "Und (gedenke) des Tages, da Wir die Berge versetzen und du die Erde (kahl) hervortreten siehst und Wir sie versammeln, ohne jemanden von ihnen auszulassen," });
                        surah18.VerseList.Add(new Verse { Number = 48, TextBosnian = "pred Gospodarom tvojim bit će oni u redove poredani: 'Došli ste nam onako kako smo vas prvi put stvorili, a tvrdili ste da vam nećemo vrijeme za oživljavanje odrediti.'", TextEnglish = "And they will be presented before your Lord in rows, [and He will say], 'You have certainly come to Us just as We created you the first time. But you claimed that We would never make for you an appointment.'", TextGerman = "und (da) sie deinem Herrn in Reihen vorgeführt werden: „Nun seid ihr zu Uns gekommen, so wie Wir euch das erste Mal erschaffen haben. Ihr aber habt behauptet, Wir würden für euch keine (letzte) Verabredung festlegen." });
                        surah18.VerseList.Add(new Verse { Number = 49, TextBosnian = "I Knjiga će biti postavljena i vidjet ćete prestupnike prestravljene zbog onoga što je u njoj. 'Teško nama!', govorit će, 'kakva je ovo knjiga, ni mali ni veliki grijeh nije propustila, sve je nabrojala!' I naći će upisano ono što su radili. Gospodar tvoj neće nikome zulum učiniti.", TextEnglish = "And the record [of deeds] will be placed [open], and you will see the criminals fearful of that within it, and they will say, 'Oh, woe to us! What is this book that leaves nothing small or great except that it has enumerated it?' And they will find what they did present [before them]. And your Lord does injustice to no one.", TextGerman = "Und das Buch wird hingelegt. Dann siehst du die Übeltäter besorgt wegen dessen, was darin steht. Sie sagen: „O wehe uns! Was ist mit diesem Buch? Es läßt nichts aus, weder klein noch groß, ohne es zu erfassen. Sie finden (alles), was sie taten, gegenwärtig, und dein Herr tut niemandem Unrecht." });
                        surah18.VerseList.Add(new Verse { Number = 50, TextBosnian = "I kada smo rekli melecima: 'Sedždu Ademu učinite!', svi sedždu učiniše osim Iblisa, on je bio jedan od džina, pa je prema zapovijesti Gospodara svoga buntovan bio. Pa zar ćete njega i porod njegov, pored Mene, kao prijatelja prihvatiti, kad su vam oni neprijatelji? Kako je šejtan loša zamjena zulumćarima!", TextEnglish = "And [mention] when We said to the angels, 'Prostrate to Adam,' and they prostrated, except for Iblees. He was of the jinn and departed from [i.e., disobeyed] the command of his Lord. Then will you take him and his descendants as allies other than Me while they are enemies to you? Wretched it is for the wrongdoers as an exchange.", TextGerman = "Und als Wir zu den Engeln sagten: „Werft euch vor Adam nieder. Da warfen sie sich nieder, außer Iblis; er gehörte zu den Ginn. So frevelte er gegen den Befehl seines Herrn. Wollt ihr euch denn ihn und seine Nachkommenschaft zu Schutzherren anstatt Meiner nehmen, wo sie euch doch feind sind? - Ein schlimmer Tausch für die Ungerechten!" });
                        surah18.VerseList.Add(new Verse { Number = 51, TextBosnian = "Ja nisam uzimao njih za svjedoke prilikom stvaranja nebesa i Zemlje niti prilikom stvaranja njih samih, i za pomagače nisam uzimao one koji na krivi put upućuju.", TextEnglish = "I did not make them witness to the creation of the heavens and the earth or to the creation of themselves, and I would not have taken the misguiders as assistants.", TextGerman = "Ich habe sie weder bei der Erschaffung der Himmel und der Erde noch bei ihrer eigenen Erschaffung zu Zeugen genommen. Ich nehme Mir niemals die Irreführenden als Beistand." });
                        surah18.VerseList.Add(new Verse { Number = 52, TextBosnian = "I na Dan kada On rekne: 'Pozovite one za koje ste tvrdili da su ortaci Moji', i kad ih pozovu, oni im se neće odazvati, a između njih ćemo mjesto propasti postaviti.", TextEnglish = "And [warn of] the Day when He will say, 'Call My 'partners' whom you claimed,' and they will invoke them, but they will not respond to them. And We will put between them [a valley of] destruction.", TextGerman = "Und an dem Tag, da Er sagen wird: „Ruft Meine Teilhaber, die ihr angegeben habt!, werden sie sie anrufen, aber sie werden ihnen nicht antworten. Und Wir werden zwischen ihnen einen Ort der Vernichtung einrichten’." });
                        surah18.VerseList.Add(new Verse { Number = 53, TextBosnian = "I ugledat će prestupnici Vatru i uvjerit će se da će u nju pasti i neće je moći izbjeći.", TextEnglish = "And the criminals will see the Fire and will be certain that they are to fall therein. And they will not find from it a way elsewhere.", TextGerman = "Und die Übeltäter werden das (Höllen)feuer sehen und überzeugt sein, daß sie hineinfallen, und kein Mittel finden, es abzuwenden." });
                        surah18.VerseList.Add(new Verse { Number = 54, TextBosnian = "U ovom Kur'anu Mi na razne načine objašnjavamo ljudima svakovrsne primjere, ali je čovjek, više nego iko, spreman da raspravlja.", TextEnglish = "And We have certainly diversified in this Qur’ān for the people from every [kind of] example; but man has ever been, most of anything, [prone to] dispute.", TextGerman = "Und Wir haben ja in diesem Qur’an den Menschen ein jedes Gleichnis verschiedenartig dargelegt; aber der Mensch ist von allen Wesen am streitsüchtigsten." });
                        surah18.VerseList.Add(new Verse { Number = 55, TextBosnian = "A ljude, kada im dolazi uputa, odvraća od vjerovanja i od toga da od Gospodara svoga mole oprost samo to što traže da ih zadesi kazna koja je bila pravilo za prethodnike ili što čekaju da im dođe kazna sučelice.", TextEnglish = "And nothing has prevented the people from believing when guidance came to them and from asking forgiveness of their Lord except that there [must] befall them the [accustomed] precedent of the former peoples[794] or that the punishment should come [directly] before them.", TextGerman = "Und nichts anderes hielt die Menschen davon ab, zu glauben, als die Rechtleitung zu ihnen kam, und ihren Herrn um Vergebung zu bitten, außer (ihrer Forderung), daß an ihnen nach der Gesetzmäßigkeit der Früheren verfahren werde oder daß die Strafe vor ihren Augen über sie komme." });
                        surah18.VerseList.Add(new Verse { Number = 56, TextBosnian = "Mi šaljemo poslanike samo zato da donose radosne vijesti i da upozoravaju. A nevjernici se raspravljaju, služeći se neistinama, da bi time opovrgli Istinu, i znakove Moje i ono na što su upozoreni uzimaju za podrugivanje.", TextEnglish = "And We send not the messengers except as bringers of good tidings and warners. And those who disbelieve dispute by [using] falsehood to [attempt to] invalidate thereby the truth and have taken My verses, and that of which they are warned, in ridicule.", TextGerman = "Wir senden die Gesandten nur als Verkünder froher Botschaft und als Überbringer von Warnungen. Aber diejenigen, die ungläubig sind, streiten mit dem Falschen, um damit die Wahrheit zu widerlegen. Und sie machen sich über Meine Zeichen und das, womit sie gewarnt wurden, lustig." });
                        surah18.VerseList.Add(new Verse { Number = 57, TextBosnian = "Ima li većeg zulumćara od onoga koji, kad na znakove i ajete Gospodara svoga bude podsjećen, za njih ne haje, a zaboravlja ono što su ruke njegove pripravile?! Mi na srca njihova pokrivače stavljamo da Kur'an ne shvate i gluhim ih učinimo; i ako ih ti na Pravi put pozoveš oni, kad su takvi, nikad neće Pravim putem poći.", TextEnglish = "And who is more unjust than one who is reminded of the verses of his Lord but turns away from them and forgets what his hands have put forth? Indeed, We have placed over their hearts coverings, lest they understand it, and in their ears deafness. And if you invite them to guidance - they will never be guided, then - ever.", TextGerman = "Wer ist denn ungerechter als jemand, der mit den Zeichen seines Herrn ermahnt wird und sich dann von ihnen abwendet und vergißt, was seine Hände vorausgeschickt haben. Gewiß, Wir haben auf ihre Herzen Hüllen gelegt, so daß sie ihn nicht verstehen, und in ihre Ohren Schwerhörigkeit; und wenn du sie zur Rechtleitung rufst, dann werden sie sich also niemals rechtleiten lassen." });
                        surah18.VerseList.Add(new Verse { Number = 58, TextBosnian = "Gospodar tvoj je Oprostitelj grijeha i milostiv je; da ih On za ono što zaslužuju kažnjava, odmah bi ih na muke stavio. Ali, njih čeka čas određeni od koga utočište neće naći.", TextEnglish = "And your Lord is the Forgiving, the possessor of mercy. If He were to impose blame upon them for what they earned, He would have hastened for them the punishment. Rather, for them is an appointment from which they will never find an escape.", TextGerman = "Und dein Herr ist der Allvergebende, voll der Barmherzigkeit. Wenn Er sie für das belangen würde, was sie erworben haben, würde Er für sie die Strafe wahrlich beschleunigen. Aber sie haben eine Verabredung, vor der sie keine Zuflucht finden werden." });
                        surah18.VerseList.Add(new Verse { Number = 59, TextBosnian = "A ona naselja smo razorili zato što su stanovnici njihovi zulum učinili i za propast njihovu smo tačno vrijeme odredili.", TextEnglish = "And those cities - We destroyed them when they wronged, and We made for their destruction an appointed time.", TextGerman = "Und diese Städte da vernichteten Wir, als sie Unrecht taten. Und Wir haben für ihre Vernichtung eine Verabredung festgelegt." });
                        surah18.VerseList.Add(new Verse { Number = 60, TextBosnian = "I kada Musa reče momku svome: 'Sve ću ići dok ne stignem do mjesta gdje se sastaju dva mora, ili ću dugo, dugo ići.'", TextEnglish = "And [mention] when Moses said to his boy [i.e., servant], 'I will not cease [traveling] until I reach the junction of the two seas or continue for a long period.'", TextGerman = "Und als Musa zu seinem Burschen sagte: „Ich lasse nicht ab, bis ich die Stelle erreicht habe, an der die Meere zusammenkommen, und sollte ich lange Zeit weiter gehen." });
                        surah18.VerseList.Add(new Verse { Number = 61, TextBosnian = "I kad njih dvojica stigoše do mjesta na kome se ona sastaju, zaboraviše na ribu svoju, pa ona u moru sebi put zasiječe.", TextEnglish = "But when they reached the junction between them, they forgot their fish, and it took its course into the sea, slipping away.", TextGerman = "Als sie die Stelle erreicht hatten, an der sie zusammenkommen, vergaßen sie ihren Fisch, so nahm er seinen Weg im Meer, (auf und davon) schwimmend." });
                        surah18.VerseList.Add(new Verse { Number = 62, TextBosnian = "A kad se udaljiše, Musa reče momku svome: 'Daj nam užinu našu, umorili smo se od ovog našeg putovanja.'", TextEnglish = "So when they had passed beyond it, [Moses] said to his boy, 'Bring us our morning meal. We have certainly suffered in this, our journey, [much] fatigue.'", TextGerman = "Als sie vorbeigegangen waren, sagte er zu seinem Burschen: „Bringe uns unser Mittagessen. Wir haben ja durch diese unsere Reise (viel) Mühsal erlitten." });
                        surah18.VerseList.Add(new Verse { Number = 63, TextBosnian = "Vidi!', reče on, 'kad smo kod one stijene svratili, ja sam zaboravio onu ribu - sam šejtan je učinio da je zaboravim, da ti je ne spomenem - pa se ona prihvati svoga puta u moru, čudnovato!'", TextEnglish = "He said, 'Did you see when we retired to the rock? Indeed, I forgot [there] the fish. And none made me forget it except Satan - that I should mention it. And it took its course into the sea amazingly.'", TextGerman = "Er sagte: „Siehst du! Als wir beim Felsen Rast gemacht haben, gewiß, da habe ich den Fisch vergessen. Und es ließ mich ihn nur der Satan vergessen, ihn (dir) zu erwähnen. Und er nahm seinen Weg im Meer auf wunderliche Weise." });
                        surah18.VerseList.Add(new Verse { Number = 64, TextBosnian = "E, to je ono što tražimo!', reče Musa, i njih dvojica se vratiše putem kojim su bili došli,", TextEnglish = "[Moses] said, 'That is what we were seeking.' So they returned, following their footprints.", TextGerman = "Er sagte: „Das ist es, was wir suchten. Da kehrten sie beide zurück, indem sie ihren eigenen Spuren folgten." });
                        surah18.VerseList.Add(new Verse { Number = 65, TextBosnian = "i nađoše jednoga Našeg roba kojem smo milost Našu darovali i onome što samo Mi znamo, naučili.", TextEnglish = "And they found a servant from among Our servants [i.e., al-Khiḍr] to whom We had given mercy from Us and had taught him from Us a [certain] knowledge.", TextGerman = "Sie trafen einen von Unseren Dienern, dem Wir Barmherzigkeit von Uns aus hatten zukommen lassen und den Wir Wissen von Uns her gelehrt hatten." });
                        surah18.VerseList.Add(new Verse { Number = 66, TextBosnian = "Mogu li da te pratim?', upita Musa, 'ali da me poučiš onome čemu si ti ispravno poučen.'", TextEnglish = "Moses said to him, 'May I follow you on [the condition] that you teach me from what you have been taught of sound judgement?'", TextGerman = "Musa sagte zu ihm: „Darf ich dir folgen, auf daß du mich von dem lehrst, was dir an Besonnenheit gelehrt worden ist?" });
                        surah18.VerseList.Add(new Verse { Number = 67, TextBosnian = "Ti se sigurno nećeš moći sa mnom strpjeti', reče onaj,", TextEnglish = "He said, 'Indeed, with me you will never be able to have patience.", TextGerman = "Er sagte: „Du wirst (es) bei mir nicht aushalten können." });
                        surah18.VerseList.Add(new Verse { Number = 68, TextBosnian = "a i kako bi se strpio u onome o čemu ništa ne znaš?'", TextEnglish = "And how can you have patience for what you do not encompass in knowledge?'", TextGerman = "Wie willst du das auch aushalten, wovon du keine umfassende Kenntnis hast?" });
                        surah18.VerseList.Add(new Verse { Number = 69, TextBosnian = "Vidjet ćeš da ću biti strpljiv, ako Allah da!', reče Musa, 'i da ti se neću ni u čemu suprotstavljati.'", TextEnglish = "[Moses] said, 'You will find me, if Allāh wills, patient, and I will not disobey you in [any] order.'", TextGerman = "Er sagte: „Du wirst mich, wenn Allah will, standhaft finden, und ich werde mich keinem Befehl von dir widersetzen." });
                        surah18.VerseList.Add(new Verse { Number = 70, TextBosnian = "Ako ćeš me već pratiti', reče onaj, 'onda me ni o čemu ne pitaj dok ti ja o tome prvi ne kažem.'", TextEnglish = "He said, 'Then if you follow me, do not ask me about anything until I make to you about it mention [i.e., explanation].'", TextGerman = "Er sagte: „Wenn du mir denn folg(en will)st, dann frage mich nach nichts, bis ich selbst es dir gegenüber zuerst erwähne." });
                        surah18.VerseList.Add(new Verse { Number = 71, TextBosnian = "I njih dvojica krenuše. I kad se u lađu ukrcaše, onaj je probuši. 'Zar je probuši da potopiš one koji na njoj plove? Učinio si nešto, doista, krupno.'", TextEnglish = "So they set out, until when they had embarked on the ship, he [i.e., al-Khiḍr] tore it open. [Moses] said, 'Have you torn it open to drown its people? You have certainly done a grave thing.'", TextGerman = "Da zogen sie beide los, bis, als sie ein Schiff bestiegen, er darin ein Loch machte. Er (Musa) sagte: „Hast du ein Loch darin gemacht, um seine Besatzung ertrinken zu lassen? Du hast da ja eine grauenhafte Sache begangen." });
                        surah18.VerseList.Add(new Verse { Number = 72, TextBosnian = "Ne rekoh li ja', reče onaj, 'da se ti, doista, nećeš moći strpjeti sa mnom?'", TextEnglish = "[Al-Khiḍr] said, 'Did I not say that with me you would never be able to have patience?'", TextGerman = "Er sagte: „Habe ich nicht gesagt, daß du (es) bei mir nicht wirst aushalten können?" });
                        surah18.VerseList.Add(new Verse { Number = 73, TextBosnian = "Ne zamjeraj mi što sam zaboravio', reče, 'i ne čini moje stanje još težim.'", TextEnglish = "[Moses] said, 'Do not blame me for what I forgot and do not overwhelm me in my matter with difficulty.'", TextGerman = "Er (Musa) sagte: „Belange mich nicht dafür, daß ich vergessen habe, und bedrücke mich in meiner Angelegenheit nicht mit einer Erschwernis." });
                        surah18.VerseList.Add(new Verse { Number = 74, TextBosnian = "I njih dvojica krenuše. I kad sretoše jednog dječaka pa ga onaj ubi, Musa reče: 'Zašto ubi dijete bezgrešno, koje nije nikoga ubilo! Učinio si, zaista, nešto vrlo ružno.'", TextEnglish = "So they set out, until when they met a boy, he [i.e., al-Khiḍr] killed him. [Moses] said, 'Have you killed a pure soul for other than [having killed] a soul? You have certainly done a deplorable thing.'", TextGerman = "Da zogen sie beide weiter, bis, als sie dann einen Jungen trafen, er ihn tötete. Er (Musa) sagte: „Hast du eine unschuldige Seele getötet (und zwar) nicht (als Wiedervergeltung) für eine (andere) Seele? Du hast da ja eine verwerfliche Sache begangen." });
                        surah18.VerseList.Add(new Verse { Number = 75, TextBosnian = "Ne rekoh li ja tebi', reče onaj, 'da se ti, doista, nećeš moći strpjeti sa mnom.'", TextEnglish = "[Al-Khiḍr] said, 'Did I not tell you that with me you would never be able to have patience?'", TextGerman = "Er sagte: „Habe ich dir nicht gesagt, daß du (es) bei mir nicht wirst aushalten können?" });
                        surah18.VerseList.Add(new Verse { Number = 76, TextBosnian = "Ako te i poslije ovoga za bilo šta upitam', reče, 'onda se nemoj sa mnom družiti. Već si prije moje opravdanje prihvatio.", TextEnglish = "[Moses] said, 'If I should ask you about anything after this, then do not keep me as a companion. You have obtained from me an excuse.'", TextGerman = "Er (Musa) sagte: „Wenn ich dich danach (noch einmal) nach irgend etwas frage, dann lasse mich dich nicht mehr begleiten. Dich trifft in Bezug auf mich kein Tadel." });
                        surah18.VerseList.Add(new Verse { Number = 77, TextBosnian = "I njih dvojica krenuše; i kad dođoše do jednog grada, zamoliše stanovnike njegove da ih nahrane, ali oni odbiše da ih ugoste. U gradu njih dvojica naiđoše na jedan zid koji samo što se nije srušio, pa ga onaj uspravi. 'Mogao si', reče Musa, 'za njega uzeti nagradu.'", TextEnglish = "So they set out, until when they came to the people of a town, they asked its people for food, but they refused to offer them hospitality. And they found therein a wall about to collapse, so he [i.e., al-Khiḍr] restored it. [Moses] said, 'If you wished, you could have taken for it a payment.'", TextGerman = "Da zogen sie beide weiter, bis, als sie dann zu den Bewohnern einer Stadt kamen, sie ihre Bewohner um etwas zu essen baten; diese aber weigerten sich, sie gastlich aufzunehmen. Da fanden sie in ihr eine Mauer, die einzustürzen drohte, und so richtete er sie auf. Er (Musa) sagte: „Wenn du wolltest, hättest du dafür wahrlich Lohn nehmen können." });
                        surah18.VerseList.Add(new Verse { Number = 78, TextBosnian = "Ovo je rastanak između mene i tebe', reče onaj, 'obavijestit ću te o tumačenju onoga zbog čega nisi mogao da se strpiš.", TextEnglish = "[Al-Khiḍr] said, 'This is parting between me and you. I will inform you of the interpretation of that about which you could not have patience.", TextGerman = "Er sagte: „Das ist die Trennung zwischen mir und dir. Ich werde dir jetzt die Deutung dessen kundtun, was du nicht aushalten konntest." });
                        surah18.VerseList.Add(new Verse { Number = 79, TextBosnian = "Što se one lađe tiče, ona je pripadala siromasima koji rade na moru, i ja sam htio da je oštetim jer je pred njima bio jedan vladar koji je svaku ispravnu lađu otimao.", TextEnglish = "As for the ship, it belonged to poor people working at sea. So I intended to cause defect in it as there was after them a king who seized every [good] ship by force.", TextGerman = "Was das Schiff angeht, so gehörte es Armen, die auf dem Meer arbeiteten. Ich wollte es schadhaft machen, denn ein König war hinter ihnen her, der jedes Schiff mit Gewalt wegnahm." });
                        surah18.VerseList.Add(new Verse { Number = 80, TextBosnian = "Što se onog dječaka tiče, roditelji njegovi bili su vjernici; pobojali smo se da ih on neće na osionost i nevjerovanje navratiti,", TextEnglish = "And as for the boy, his parents were believers, and we feared that he would overburden them by transgression and disbelief.", TextGerman = "Was den Jungen angeht, so waren seine Eltern gläubige (Menschen). Da fürchteten wir, daß er sie durch (seine) Auflehnung und durch (seinen) Unglauben bedrücken würde." });
                        surah18.VerseList.Add(new Verse { Number = 81, TextBosnian = "pa smo htjeli da im Gospodar njihov, umjesto njega, da boljeg i čestitijeg, i samilosti približnijeg.", TextEnglish = "So we intended that their Lord should substitute for them one better than him in purity and nearer to mercy.", TextGerman = "So wollten wir, daß ihr Herr ihnen zum Tausch einen gebe, - besser als er an Lauterkeit und näher kommend an Güte." });
                        surah18.VerseList.Add(new Verse { Number = 82, TextBosnian = "A što se onoga zida tiče, to je bio zid dvojice dječaka, siročadi iz grada, a pod njim je zakopano njihovo blago. Otac njihov je bio dobar čovjek i Gospodar tvoj želi, iz milosti Svoje, da oni odrastu i izvade blago svoje. Sve to ja nisam uradio po nahođenju svome. Eto to je tumačenje onoga za što se ti nisi mogao strpjeti.'", TextEnglish = "And as for the wall, it belonged to two orphan boys in the city, and there was beneath it a treasure for them, and their father had been righteous. So your Lord intended that they reach maturity and extract their treasure, as a mercy from your Lord. And I did it not of my own accord. That is the interpretation of that about which you could not have patience.'", TextGerman = "Was aber die Mauer angeht, so gehörte sie zwei Waisenjungen in der Stadt, und unter ihr befand sich ein für sie bestimmter Schatz. Ihr Vater war rechtschaffen, und da wollte dein Herr, daß sie (erst) ihre Vollreife erlangen und (dann) ihren Schatz hervorholen - aus Barmherzigkeit von deinem Herrn. Ich tat es ja nicht aus eigenem Ermessen. Das ist die Deutung dessen, was du nicht aushalten konntest." });
                        surah18.VerseList.Add(new Verse { Number = 83, TextBosnian = "I pitaju te o Zulkarnejnu. Reci: 'Kazat ću vam o njemu neke vijesti.", TextEnglish = "And they ask you, [O Muḥammad], about Dhul-Qarnayn. Say, 'I will recite to you about him a report.'", TextGerman = "Und sie fragen dich nach Du ’l-Qarnain. Sag: Ich werde euch über ihn eine Geschichte verlesen." });
                        surah18.VerseList.Add(new Verse { Number = 84, TextBosnian = "Mi smo mu omogućili da ima vlast na Zemlji i dali smo mu sredstva da uradi sve što želi.'", TextEnglish = "Indeed, We established him upon the earth, and We gave him from everything a way [i.e., means].", TextGerman = "Wir verliehen ihm auf der Erde eine feste Stellung und eröffneten ihm zu allem einen Weg." });
                        surah18.VerseList.Add(new Verse { Number = 85, TextBosnian = "I on pođe putem.", TextEnglish = "So he followed a way", TextGerman = "Da verfolgte er einen Weg," });
                        surah18.VerseList.Add(new Verse { Number = 86, TextBosnian = "Kada stiže do mjesta gdje Sunce zalazi, nađe kao da zalazi u jedan izvor mutni i nađe u blizini njegovoj jedan narod. Rekosmo Mi: 'Ili ćeš da ih kazniš ili ćeš sa njima blago da postupiš?'", TextEnglish = "Until, when he reached the setting of the sun [i.e., the west], he found it [as if] setting in a body of dark water,[795] and he found near it a people. We [i.e., Allāh] said, 'O Dhul-Qarnayn, either you punish [them] or else adopt among them [a way of] goodness.'", TextGerman = "bis, als er den Ort des Sonnenuntergangs erreichte, er fand, daß sie in einer schlammigen Quelle unterging, und er fand bei ihr ein Volk. Wir sagten: „O Du’l- Qarnain, entweder strafst du (sie), oder du behandelst sie mit Güte." });
                        surah18.VerseList.Add(new Verse { Number = 87, TextBosnian = "Onoga ko zulum čini', reče, 'kaznit ćemo, a poslije će se svome Gospodaru vratiti, pa će ga i On teškoj patnji podvrgnuti.'", TextEnglish = "He said, 'As for one who wrongs,[796] we will punish him. Then he will be returned to his Lord, and He will punish him with a terrible punishment [i.e., Hellfire].", TextGerman = "Er sagte: „Was nun jemanden angeht, der Unrecht tut, so werden wir ihn strafen; hierauf wird er zu seinem Herrn zurückgebracht, und Er straft ihn dann mit entsetzlicher Strafe." });
                        surah18.VerseList.Add(new Verse { Number = 88, TextBosnian = "A onome ko bude vjerovao i dobra djela činio pripada nagrada najljepša, i njemu ćemo samo naredbe koje su lahke upućivati.'", TextEnglish = "But as for one who believes and does righteousness, he will have a reward of the best [i.e., Paradise], and we [i.e., Dhul-Qarnayn] will speak to him from our command with ease.'", TextGerman = "Was aber jemanden angeht, der glaubt und rechtschaffen handelt, für den wird es als Lohn das Beste geben, und Wir werden ihm von unserem Befehl etwas sagen, was Erleichterung bringt." });
                        surah18.VerseList.Add(new Verse { Number = 89, TextBosnian = "I on pođe dalje.", TextEnglish = "Then he followed a way", TextGerman = "Hierauf verfolgte er einen Weg," });
                        surah18.VerseList.Add(new Verse { Number = 90, TextBosnian = "I kad dođe do mjesta gdje Sunce izlazi, on nađe da ono izlazi iznad jednog naroda kome Mi nismo dali da se od njega bilo čim zakloni.", TextEnglish = "Until, when he came to the rising of the sun [i.e., the east], he found it rising on a people for whom We had not made against it any shield.", TextGerman = "bis, als er den Ort des Sonnenaufgangs erreichte, er fand, daß sie über einem Volk aufgeht, denen Wir keine Deckung vor ihr gegeben hatten." });
                        surah18.VerseList.Add(new Verse { Number = 91, TextBosnian = "Tako to bi. A Mi obuhvatamo znanjem sve što je imao on.", TextEnglish = "Thus.[797] And We had encompassed [all] that he had in knowledge.", TextGerman = "So war es. Und Wir haben ja umfassende Kenntnis von dem, was ihn betrifft." });
                        surah18.VerseList.Add(new Verse { Number = 92, TextBosnian = "I on opet pođe.", TextEnglish = "Then he followed a way.", TextGerman = "Hierauf verfolgte er einen Weg," });
                        surah18.VerseList.Add(new Verse { Number = 93, TextBosnian = "Kad stiže između dvije planine, nađe ispred njih narod koji je jedva govor razumijevao.", TextEnglish = "Until, when he reached [a pass] between two mountains, he found beside them a people who could hardly understand [his] speech.", TextGerman = "bis, als er den Ort zwischen den beiden Bergen erreichte, er diesseits von ihnen ein Volk fand, das beinahe kein Wort verstand." });
                        surah18.VerseList.Add(new Verse { Number = 94, TextBosnian = "O Zulkarnejne', rekoše oni, 'Je'džudž i Me'džudž' čine nered po Zemlji, pa hoćeš li da ti sakupimo naknadu da između nas i njih podigneš pregradu?'", TextEnglish = "They said, 'O Dhul-Qarnayn, indeed Gog and Magog[798] are [great] corrupters in the land. So may we assign for you an expenditure that you might make between us and them a barrier?'", TextGerman = "Sie sagten: „O Du ’l-Qarnain, Ya’gug und Ma’gug stiften Unheil auf der Erde. Sollen wir dir eine Gebühr dafür aussetzen, daß du zwischen uns und ihnen eine Sperrmauer errichtest?" });
                        surah18.VerseList.Add(new Verse { Number = 95, TextBosnian = "Bolje je ono što mi je moj Gospodar dao', reče on. 'Nego, samo vi pomozite meni što više možete, i ja ću između vas i njih pregradu podići.", TextEnglish = "He said, 'That in which my Lord has established me is better [than what you offer], but assist me with strength [i.e., manpower]; I will make between you and them a dam.", TextGerman = "Er sagte: „Was mir mein Herr an fester Stellung verliehen hat, ist besser (als eure Gebühr). Doch helft mir mit (eurer Arbeits)kraft, damit ich zwischen euch und ihnen einen aufgeschütteten Wall errichte." });
                        surah18.VerseList.Add(new Verse { Number = 96, TextBosnian = "Donesite mi velike komade gvožđa.' I kad on izravna dvije strane brda, reče: 'Pušite!' A kad ga usija, reče: 'Donesite mi rastopljeni bakar da ga zalijem.'", TextEnglish = "Bring me bars of iron' - until, when he had leveled [them] between the two mountain walls, he said, 'Blow [with bellows],' until when he had made it [like] fire, he said, 'Bring me, that I may pour over it molten copper.'", TextGerman = "Bringt mir die Eisenstücke. Als er nun zwischen den beiden Berghängen gleich hoch (aufgeschüttet) hatte, sagte er: „Blast (jetzt). Als er es zum Glühen gebracht hatte, sagte er: „Bringt mir, damit ich (es) darüber gieße, geschmolzenes Kupfer." });
                        surah18.VerseList.Add(new Verse { Number = 97, TextBosnian = "I tako oni nisu mogli ni da je pređu niti su mogli da je prokopaju.", TextEnglish = "So they [i.e., Gog and Magog] were unable to pass over it, nor were they able [to effect] in it any penetration.", TextGerman = "So konnten sie ihn weder überwinden, noch konnten sie ihn durchbrechen." });
                        surah18.VerseList.Add(new Verse { Number = 98, TextBosnian = "Ovo je blagodat Gospodara moga!', reče on. 'A kad se prijeteće obećanje Gospodara moga ispuni, On će je sa zemljom sravniti, a obećanje Gospodara moga će se sigurno ispuniti.'", TextEnglish = "[Dhul-Qarnayn] said, 'This is a mercy from my Lord; but when the promise of my Lord[799] comes [i.e., approaches], He will make it level, and ever is the promise of my Lord true.'", TextGerman = "Er sagte: „Das ist eine Barmherzigkeit von meinem Herrn. Wenn dann das Versprechen meines Herrn eintrifft, läßt Er ihn in sich zusammensinken; und das Versprechen meines Herrn ist wahr." });
                        surah18.VerseList.Add(new Verse { Number = 99, TextBosnian = "I Mi ćemo tada ostaviti da se jedni od njih kao talasi sudaraju s drugima. I puhnut će se u rog, pa ćemo ih sve skupiti.", TextEnglish = "And We will leave them that day[800] surging over each other, and [then] the Horn will be blown, and We will assemble them in [one] assembly.", TextGerman = "Wir lassen die einen von ihnen an jenem Tag wie Wogen unter die anderen geraten, und es wird ins Horn geblasen. Dann versammeln Wir sie vollständig." });
                        surah18.VerseList.Add(new Verse { Number = 100, TextBosnian = "Taj dan ćemo nevjernicima Džehennem jasno pokazati,", TextEnglish = "And We will present Hell that Day to the disbelievers, on display -", TextGerman = "Und Wir führen die Hölle den Ungläubigen an jenem Tag in aller Deutlichkeit vor," });
                        surah18.VerseList.Add(new Verse { Number = 101, TextBosnian = "onima čije su oči bile koprenom zastrte od Moje Opomene, i koji nisu mogli da slušaju.", TextEnglish = "Those whose eyes had been within a cover [removed] from My remembrance,[801] and they were not able to hear.[802]", TextGerman = "(ihnen,) deren Augen vor Meiner Mahnung (wie) unter einer Decke lagen und die (auch) nicht hören konnten." });
                        surah18.VerseList.Add(new Verse { Number = 102, TextBosnian = "Zar nevjernici misle da pored Mene za bogove mogu uzimati robove Moje?! Mi smo, doista, nevjernicima Džehennem kao mjesto boravka pripremili.", TextEnglish = "Then do those who disbelieve think that they can take My servants instead of Me as allies? Indeed, We have prepared Hell for the disbelievers as a lodging.", TextGerman = "Meinen denn diejenigen, die ungläubig sind, daß sie sich Meine Diener anstatt Meiner zu Schutzherren nehmen (können)? Gewiß, Wir haben die Hölle den Ungläubigen zur gastlichen Aufnahme bereitet." });
                        surah18.VerseList.Add(new Verse { Number = 103, TextBosnian = "Reci: 'Hoćete li da vas obavijestimo o onima koji će u svojim djelima najviše gubitka imati,", TextEnglish = "Say, [O Muḥammad], 'Shall we [believers] inform you of the greatest losers as to [their] deeds?", TextGerman = "Sag: Sollen Wir euch Kunde geben von denjenigen, die an ihren Werken am meisten verlieren," });
                        surah18.VerseList.Add(new Verse { Number = 104, TextBosnian = "čiji će trud u životu na ovom svijetu uzaludan biti, a koji će misliti da je dobro ono što rade?'", TextEnglish = "[They are] those whose effort is lost in worldly life, while they think that they are doing well in work.'", TextGerman = "deren Bemühen im diesseitigen Leben fehlgeht, während sie meinen, daß sie gut handeln würden?" });
                        surah18.VerseList.Add(new Verse { Number = 105, TextBosnian = "To su oni koji u dokaze Gospodara svoga ne budu vjerovali i koji budu poricali da će pred Njega izaći; zbog toga će trud njihov uzaludan biti i na Kijametskom im danu ni vagu nećemo postaviti!", TextEnglish = "Those are the ones who disbelieve in the verses of their Lord and in [their] meeting Him, so their deeds have become worthless; and We will not assign to them on the Day of Resurrection any weight [i.e., importance].", TextGerman = "Das sind diejenigen, die die Zeichen ihres Herrn und die Begegnung mit Ihm leugnen. So werden ihre Werke hinfällig, und so werden Wir ihnen am Tag der Auferstehung kein Gewicht beimessen." });
                        surah18.VerseList.Add(new Verse { Number = 106, TextBosnian = "Njima će kazna Džehennem biti, zato što su nevjernici bili i što su se ajetima i znakovima Mojim i poslanicima Mojim rugali.", TextEnglish = "That is their recompense - Hell - for what they denied and [because] they took My signs and My messengers in ridicule.", TextGerman = "Das ist ihr Lohn - die Hölle - dafür, daß sie ungläubig waren und sich über Meine Zeichen und Meine Gesandten lustig machten." });
                        surah18.VerseList.Add(new Verse { Number = 107, TextBosnian = "Onima koji budu vjerovali i dobra djela činili - bašče Firdevsa će ugošćenje biti,", TextEnglish = "Indeed, those who have believed and done righteous deeds - they will have the Gardens of Paradise[803] as a lodging,", TextGerman = "Gewiß, für diejenigen, die glauben und rechtschaffene Werke tun, wird es die Gärten des Paradieses zur gastlichen Aufnahme geben," });
                        surah18.VerseList.Add(new Verse { Number = 108, TextBosnian = "vječno će u njima boraviti i neće poželjeti da ih nečim drugim zamijene.", TextEnglish = "Wherein they abide eternally. They will not desire from it any transfer.", TextGerman = "ewig darin zu bleiben, und sie werden nicht begehren, davon wegzugehen’." });
                        surah18.VerseList.Add(new Verse { Number = 109, TextBosnian = "Reci: 'Kada bi more bilo mastilo da se ispišu riječi Gospodara moga, more bi presahlo, ali ne i riječi Gospodara moga, pa i kad bismo ga proširili morem poput njega.'", TextEnglish = "Say, 'If the sea were ink for [writing] the words[804] of my Lord, the sea would be exhausted before the words of my Lord were exhausted, even if We brought the like of it in [continual] supplement.'", TextGerman = "Sag: Wenn das Meer Tinte für die Worte meines Herrn wäre, würde das Meer wahrlich zu Ende gehen, bevor die Worte meines Herrn zu Ende gingen, auch wenn Wir als Nachschub noch einmal seinesgleichen hinzubrächten." });
                        surah18.VerseList.Add(new Verse { Number = 110, TextBosnian = "Reci: 'Ja sam čovjek kao i vi, meni se objavljuje da je vaš Bog- jedan Bog. Pa ko se nada susretu s Gospodarom svojim, neka čini dobra djela i neka u ibadetu Gospodaru svome nikoga ne pridružuje.'", TextEnglish = "Say, 'I am only a man like you, to whom has been revealed that your god is one God. So whoever would hope for the meeting with his Lord - let him do righteous work and not associate in the worship of his Lord anyone.'", TextGerman = "Sag: Gewiß, ich bin ja nur ein menschliches Wesen gleich euch; mir wird (als Offenbarung) eingegeben, daß euer Gott ein Einziger Gott ist. Wer nun auf die Begegnung mit seinem Herrn hofft, der soll rechtschaffen handeln und beim Dienst an seinem Herrn (Ihm) niemanden beigesellen." });
                        result = surah18;
                        break;
                    #endregion

                    // 
                    #region 19 Maryam
                    case 19:
                        Surah surah19 = new Surah(); surah19.Name = Constants.Surah19Name; surah19.Number = 19; surah19.VerseList = new List<Verse>();
                        surah19.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah19.VerseList.Add(new Verse { Number = 1, TextBosnian = "Kāf Hā Jā Ajīn Sād.", TextEnglish = "Kāf, Hā, Yā, ʿAyn, Ṣād.[806]", TextGerman = "Kaf-Ha-Ya-’Ayn-Sad." });
                        surah19.VerseList.Add(new Verse { Number = 2, TextBosnian = "Ovo je spomen o milosti Gospodara tvoga prema robu Njegovu Zekerijjau,", TextEnglish = "[This is] a mention of the mercy of your Lord to His servant Zechariah", TextGerman = "(Diese Verse sind) zum Gedenken an die Barmherzigkeit deines Herrn zu Seinem Diener Zakariyya." });
                        surah19.VerseList.Add(new Verse { Number = 3, TextBosnian = "kad je on Gospodara svoga tiho zovnuo,", TextEnglish = "When he called to his Lord a private call [i.e., supplication].", TextGerman = "Als er zu seinem Herrn im Verborgenen rief" });
                        surah19.VerseList.Add(new Verse { Number = 4, TextBosnian = "i rekao: 'Gospodaru moj, kosti su mi oronule i glava osijedjela, a nikada nisam, kad sam Ti, Gospodaru moj, molbu uputio, razočaran ostao.", TextEnglish = "He said, 'My Lord, indeed my bones have weakened, and my head has filled[807] with white, and never have I been in my supplication to You, my Lord, unhappy [i.e., disappointed].", TextGerman = "(und) sagte: „Mein Herr, schwach sind mir die Knochen geworden, und in Altersgrauheit entfacht ist der Kopf. Und ich war im Bittgebet zu Dir, mein Herr, (noch) nie unglücklich." });
                        surah19.VerseList.Add(new Verse { Number = 5, TextBosnian = "Bojim se rođaka mojih po krvi poslije mene, a žena mi je nerotkinja, zato mi pokloni od Sebe nasljednika,", TextEnglish = "And indeed, I fear the successors[808] after me, and my wife has been barren, so give me from Yourself an heir", TextGerman = "Gewiß, ich fürchte die Verwandten nach mir, und meine Frau ist unfruchtbar. So schenke mir von Dir aus einen nahen Verwandten," });
                        surah19.VerseList.Add(new Verse { Number = 6, TextBosnian = "da naslijedi mene i porodicu Jakubovu, i učini, Gospodaru moj, da se s njime bude zadovoljno.'", TextEnglish = "Who will inherit me[809] and inherit from the family of Jacob. And make him, my Lord, pleasing [to You].'", TextGerman = "der mich beerbt und von der Sippe Ya’qubs erbt, und mach ihn, mein Herr, (Dir) wohlgefällig." });
                        surah19.VerseList.Add(new Verse { Number = 7, TextBosnian = "O Zekerijja, javljamo ti radosnu vijest da će ti se dječak roditi, ime će mu biti Jahja, nikoga prije njemu sličnim nismo učinili.'", TextEnglish = "[He was told],[810] 'O Zechariah, indeed We give you good tidings of a boy whose name will be John. We have not assigned to any before [this] name.'", TextGerman = "„O Zakariyya, Wir verkünden dir einen Jungen, dessen Name Yahya ist, wie Wir zuvor noch niemandem Kennzeichen gleich den seinen gegeben haben." });
                        surah19.VerseList.Add(new Verse { Number = 8, TextBosnian = "Gospodaru moj', reče on, 'kako ću imati sina kad mi je žena nerotkinja, a već sam duboku starost doživio?'", TextEnglish = "He said, 'My Lord, how will I have a boy when my wife has been barren and I have reached extreme old age?'", TextGerman = "Er sagte: „Mein Herr, wie soll ich einen Jungen haben, wo meine Frau unfruchtbar ist und ich vom hohen Alter bereits einen Dürrezustand erreicht habe?" });
                        surah19.VerseList.Add(new Verse { Number = 9, TextBosnian = "Eto tako', reče. Gospodar tvoj je rekao: 'To je meni lahko, i tebe sam ranije stvorio, a nisi ništa bio.'", TextEnglish = "[An angel] said, 'Thus [it will be]; your Lord says, 'It is easy for Me, for I created you before, while you were nothing.''", TextGerman = "Er sagte: „So wird es sein. Dein Herr sagt: ,Das ist Mir ein leichtes; und auch dich habe Ich ja zuvor erschaffen, als du noch nichts warst’." });
                        surah19.VerseList.Add(new Verse { Number = 10, TextBosnian = "Gospodaru moj', reče, 'daj mi neki znak.' 'Znak će ti biti to što tri noći nećeš s ljudima razgovarati, a zdrav ćeš biti.'", TextEnglish = "[Zechariah] said, 'My Lord, make for me a sign.' He said, 'Your sign is that you will not speak to the people for three nights, [being] sound.'[811]", TextGerman = "Er sagte: „Mein Herr, setze mir ein Zeichen. Er sagte: „Dein Zeichen ist, daß du drei Nächte lang, obwohl gesund, nicht zu den Menschen sprechen wirst." });
                        surah19.VerseList.Add(new Verse { Number = 11, TextBosnian = "I on izađe iz odaje svoje u kojoj se molio pred narod svoj i znakom im dade na znanje: 'Veličajte Ga i slavite ujutro i navečer.'", TextEnglish = "So he came out to his people from the prayer chamber and signaled to them to exalt[Allāh] in the morning and afternoon.", TextGerman = "So kam er zu seinem Volk aus dem Gebetsraum heraus und gab ihnen dann zu verstehen: „Preist morgens und abends." });
                        surah19.VerseList.Add(new Verse { Number = 12, TextBosnian = "O Jahja, prihvati Knjigu odlučno!', a dadosmo mu mudrost još dok je dječak bio,", TextEnglish = "[Allāh said], 'O John, take the Scripture [i.e., adhere to it] with determination.' And We gave him judgement [while yet] a boy", TextGerman = "„O Yahya, nimm die Schrift mit (aller) Kraft. Und Wir gaben ihm schon als Kind die Urteilskraft" });
                        surah19.VerseList.Add(new Verse { Number = 13, TextBosnian = "i nježnost i čednost, i bogobojazan je bio,", TextEnglish = "And affection from Us and purity, and he was fearing of Allāh", TextGerman = "und Mitgefühl von Uns aus und Lauterkeit. Er war gottesfürchtig" });
                        surah19.VerseList.Add(new Verse { Number = 14, TextBosnian = "i roditeljima svojim bio je dobar, i nije bio drzak i neposlušan.", TextEnglish = "And dutiful to his parents, and he was not a disobedient tyrant.", TextGerman = "und gütig gegen seine Eltern, und er war weder gewalttätig noch widerspenstig." });
                        surah19.VerseList.Add(new Verse { Number = 15, TextBosnian = "I sigurnost je imao na dan kada se rodio i na dan kada je umro, a imat će je i na dan kada bude proživljen!", TextEnglish = "And peace be upon him the day he was born and the day he dies and the day he is raised alive.", TextGerman = "Und Friede sei auf ihm am Tag, da er geboren wurde, und am Tag, da er stirbt, und am Tag, da er wieder zum Leben auferweckt wird." });
                        surah19.VerseList.Add(new Verse { Number = 16, TextBosnian = "I spomeni u Knjizi Merjemu: kada se od ukućana svojih na istočnu stranu povukla,", TextEnglish = "And mention, [O Muḥammad], in the Book [the story of] Mary, when she withdrew from her family to a place toward the east.", TextGerman = "Und gedenke im Buch Maryams, als sie sich von ihren Angehörigen an einen östlichen Ort zurückzog." });
                        surah19.VerseList.Add(new Verse { Number = 17, TextBosnian = "i od njih zaklon uzela, Mi smo k njoj Našeg meleka poslali i on joj se pokazao u ljudskom potpunom obliku.", TextEnglish = "And she took, in seclusion from them, a screen. Then We sent to her Our Angel [i.e., Gabriel], and he represented himself to her as a well-proportioned man.", TextGerman = "Sie nahm sich einen Vorhang vor ihnen. Da sandten Wir Unseren Geist zu ihr. Er stellte sich ihr als wohlgestaltetes menschliches Wesen dar." });
                        surah19.VerseList.Add(new Verse { Number = 18, TextBosnian = "Utječem se Svemilosnom od tebe, ako se Njega bojiš!', uzviknu ona.", TextEnglish = "She said, 'Indeed, I seek refuge in the Most Merciful from you, [so leave me], if you should be fearing of Allāh.'", TextGerman = "Sie sagte: „Ich suche beim Allerbarmer Schutz vor dir, wenn du gottesfürchtig bist." });
                        surah19.VerseList.Add(new Verse { Number = 19, TextBosnian = "A ja sam upravo izaslanik Gospodara tvoga'', reče on, 'da ti poklonim dječaka čista!'", TextEnglish = "He said, 'I am only the messenger of your Lord to give you [news of] a pure boy [i.e., son].'", TextGerman = "Er sagte: „Ich bin nur der Gesandte deines Herrn, um dir einen lauteren Jungen zu schenken." });
                        surah19.VerseList.Add(new Verse { Number = 20, TextBosnian = "Kako ću imati dječaka?', reče ona, 'kad me ni jedan muškarac dodirnuo nije, a ja nisam bludnica.'", TextEnglish = "She said, 'How can I have a boy while no man has touched me and I have not been unchaste?'", TextGerman = "Sie sagte: „Wie soll mir ein Junge gegeben werden, wo mich doch kein menschliches Wesen berührt hat und ich keine Hure bin." });
                        surah19.VerseList.Add(new Verse { Number = 21, TextBosnian = "A on reče: 'Tako je Gospodar tvoj rekao: 'To je meni lahko, i zato da ga učinimo da bude znak ljudima i milost naša.' Tako je unaprijed određeno.'", TextEnglish = "He said, 'Thus [it will be]; your Lord says, 'It is easy for Me, and We will make him a sign to the people and a mercy from Us. And it is a matter [already] decreed.''", TextGerman = "Er sagte: „So wird es sein. Dein Herr sagt: ,Das ist Mir ein leichtes, und damit Wir ihn zu einem Zeichen für die Menschen und zu einer Barmherzigkeit von Uns machen’. Und es ist eine beschlossene Angelegenheit." });
                        surah19.VerseList.Add(new Verse { Number = 22, TextBosnian = "I ona zanese i bremenita se skloni daleko negdje.", TextEnglish = "So she conceived him, and she withdrew with him to a remote place.", TextGerman = "So empfing sie ihn und zog sich mit ihm zu einem fernen Ort zurück." });
                        surah19.VerseList.Add(new Verse { Number = 23, TextBosnian = "I porođajni bolovi prisiliše je da dođe do stabla jedne palme. 'Kamo sreće da sam ranije umrla i da sam potpuno u zaborav pala!', povika ona.", TextEnglish = "And the pains of childbirth drove her to the trunk of a palm tree. She said, 'Oh, I wish I had died before this and was in oblivion, forgotten.'", TextGerman = "Die Wehen ließen sie zum Palmenstamm gehen. Sie sagte: „O wäre ich doch zuvor gestorben und ganz und gar in Vergessenheit geraten!" });
                        surah19.VerseList.Add(new Verse { Number = 24, TextBosnian = "I on je, niže nje, zovnu: 'Ne žalosti se, Gospodar tvoj dao je da niže tebe potok poteče.", TextEnglish = "But he[812] called her from below her, 'Do not grieve; your Lord has provided beneath you a stream.", TextGerman = "Da rief er ihr von unten her zu: „Sei nicht traurig; dein Herr hat ja unter dir ein Bächlein geschaffen." });
                        surah19.VerseList.Add(new Verse { Number = 25, TextBosnian = "Zatresi palmino stablo, posut će po tebi datule svježe,", TextEnglish = "And shake toward you the trunk of the palm tree; it will drop upon you ripe, fresh dates.", TextGerman = "Und schüttle zu dir den Palmenstamm, so läßt er frische, reife Datteln auf dich herabfallen." });
                        surah19.VerseList.Add(new Verse { Number = 26, TextBosnian = "pa jedi i pij i budi vesela! A ako vidiš čovjeka kakva, ti reci: 'Ja sam se zavjetovala Svemilosnom da ću šutjeti, i danas ni s kim neću govoriti.''", TextEnglish = "So eat and drink and be contented. And if you see from among humanity anyone, say, 'Indeed, I have vowed to the Most Merciful abstention, so I will not speak today to [any] man.''", TextGerman = "So iß und trink und sei frohen Mutes’. Und wenn du nun jemanden von den Menschen sehen solltest, dann sag: Ich habe dem Allerbarmer Fasten gelobt, so werde ich heute mit keinem Menschenwesen sprechen." });
                        surah19.VerseList.Add(new Verse { Number = 27, TextBosnian = "I dođe ona s njim narodu svome, noseći ga. 'O Merjema!'', rekoše oni, 'učinila si nešto nečuveno!", TextEnglish = "Then she brought him to her people, carrying him. They said, 'O Mary, you have certainly done a thing unprecedented.", TextGerman = "Dann kam sie mit ihm zu ihrem Volk, ihn (mit sich) tragend. Sie sagten: „O Maryam, du hast da ja etwas Unerhörtes begangen." });
                        surah19.VerseList.Add(new Verse { Number = 28, TextBosnian = "Hej ti, koja ličiš Harunu otac ti nije bio nevaljao, a ni mati tvoja nije bila bludnica!'", TextEnglish = "O sister [i.e., descendant] of Aaron, your father was not a man of evil, nor was your mother unchaste.'", TextGerman = "O Schwester Haruns, dein Vater war doch kein sündiger Mann, noch war deine Mutter eine Hure." });
                        surah19.VerseList.Add(new Verse { Number = 29, TextBosnian = "Ona im na njega pokaza. 'Kako da govorimo djetetu u bešici?', rekoše oni.", TextEnglish = "So she pointed to him. They said, 'How can we speak to one who is in the cradle a child?'", TextGerman = "Da zeigte sie auf ihn. Sie sagten: „Wie können wir mit jemandem sprechen, der noch ein Kind in der Wiege ist?" });
                        surah19.VerseList.Add(new Verse { Number = 30, TextBosnian = "Ja sam Allahov rob', reče on, 'meni će On Knjigu dati i vjerovjesnikom me učiniti,", TextEnglish = "[Jesus] said, 'Indeed, I am the servant of Allāh. He has given me the Scripture and made me a prophet.", TextGerman = "Er sagte: „Ich bin wahrlich Allahs Diener; Er hat mir die Schrift gegeben und mich zu einem Propheten gemacht." });
                        surah19.VerseList.Add(new Verse { Number = 31, TextBosnian = "i učinit će me, gdje god budem, blagoslovljenim, i naredit će mi da dok god sam živ, namaz obavljam i zekat udjeljujem,", TextEnglish = "And He has made me blessed wherever I am and has enjoined upon me prayer and zakāh as long as I remain alive", TextGerman = "Und gesegnet hat Er mich gemacht, wo immer ich bin, und angeordnet hat Er mir, das Gebet (zu verrichten) und die Abgabe (zu entrichten), solange ich lebe," });
                        surah19.VerseList.Add(new Verse { Number = 32, TextBosnian = "i da majci svojoj dobar budem, a neće me učiniti silnikom, nesretnikom.", TextEnglish = "And [made me] dutiful to my mother, and He has not made me a wretched tyrant.", TextGerman = "und gütig gegen meine Mutter zu sein. Und Er hat mich weder gewalttätig noch unglücklich gemacht." });
                        surah19.VerseList.Add(new Verse { Number = 33, TextBosnian = "I neka je sigurnost nada mnom na dan kada sam se rodio i na dan kada budem umro i na dan kad budem iz mrtvih proživljen.'", TextEnglish = "And peace is on me the day I was born and the day I will die and the day I am raised alive.'", TextGerman = "Und der Friede sei auf mir am Tag, da ich geboren wurde, und am Tag, da ich sterbe, und am Tag, da ich wieder zum Leben auferweckt werde." });
                        surah19.VerseList.Add(new Verse { Number = 34, TextBosnian = "To je Isa, sin Merjemin, to je prava istina o njemu, onaj u koga oni sumnjaju.", TextEnglish = "That is Jesus, the son of Mary - the word of truth about which they are in dispute.", TextGerman = "Das ist ’Isa, der Sohn Maryams: (Es ist) das Wort der Wahrheit, woran sie zweifeln." });
                        surah19.VerseList.Add(new Verse { Number = 35, TextBosnian = "Nezamislivo je da Allah ima dijete, Uzvišen je On! Kad nešto odredi, On za to rekne samo: 'Budi!' - i ono bude.", TextEnglish = "It is not [befitting] for Allāh to take a son; exalted is He![813] When He decrees an affair, He only says to it, 'Be,' and it is.", TextGerman = "Es steht Allah nicht an, Sich ein Kind zu nehmen. Preis sei Ihm! Wenn Er eine Angelegenheit bestimmt, so sagt Er dazu nur: ,Sei!’, und so ist es." });
                        surah19.VerseList.Add(new Verse { Number = 36, TextBosnian = "Allah je, uistinu, i moj i vaš Gospodar, zato u ibadetu budite samo Njemu! To je Pravi put.", TextEnglish = "[Jesus said], 'And indeed, Allāh is my Lord and your Lord, so worship Him. That is a straight path.'", TextGerman = "(’Isa sagte:) „Und gewiß, Allah ist mein Herr und euer Herr; so dient Ihm. Das ist ein gerader Weg." });
                        surah19.VerseList.Add(new Verse { Number = 37, TextBosnian = "I grupacije među sljedbenicima Knjige su se o njemu podvojile, pa teško onima koji ne vjeruju kad budu na Danu velikom prisutni.", TextEnglish = "Then the factions differed [concerning Jesus] from among them, so woe to those who disbelieved - from the scene of a tremendous Day.", TextGerman = "Doch die Gruppierungen wurden uneinig untereinander; dann wehe denjenigen, die ungläubig sind, vor dem Erleben eines gewaltigen Tages!" });
                        surah19.VerseList.Add(new Verse { Number = 38, TextBosnian = "Kako će dobro čuti i kako će dobro vidjeti na Dan kada Nam dođu! A zulumćari su sada u očitaj zabludi!", TextEnglish = "How [clearly] they will hear and see the Day they come to Us, but the wrongdoers today are in clear error.", TextGerman = "Wie vorzüglich werden sie hören und sehen am Tag, da sie zu Uns kommen werden! Aber die Ungerechten befinden sich heute in einem deutlichen Irrtum." });
                        surah19.VerseList.Add(new Verse { Number = 39, TextBosnian = "I upozori ih na Dan tuge kada će biti s polaganjem računa završeno, a oni su u nemaru i oni ne vjeruju.", TextEnglish = "And warn them, [O Muḥammad], of the Day of Regret, when the matter will be concluded;[814] and [yet], they are in [a state of] heedlessness, and they do not believe.", TextGerman = "Warne sie vor dem Tag der gramvollen Reue, wenn die Angelegenheit entschieden sein wird, während sie (all dessen) unachtsam sind, und während sie (noch) nicht glauben." });
                        surah19.VerseList.Add(new Verse { Number = 40, TextBosnian = "Mi ćemo Zemlju i one koji žive na njoj naslijediti i Nama će se oni vratiti.", TextEnglish = "Indeed, it is We who will inherit the earth and whoever is on it, and to Us they will be returned.", TextGerman = "Gewiß, Wir sind es, die Wir die Erde und (all die,) die auf ihr sind, erben werden. Und zu Uns werden sie zurückgebracht." });
                        surah19.VerseList.Add(new Verse { Number = 41, TextBosnian = "Spomeni u Knjizi Ibrahima! On je bio istinoljubiv, vjerovjesnik.", TextEnglish = "And mention in the Book [the story of] Abraham. Indeed, he was a man of truth and a prophet.", TextGerman = "Und gedenke im Buch Ibrahims. Er war ein Wahrhaftiger und Prophet." });
                        surah19.VerseList.Add(new Verse { Number = 42, TextBosnian = "Kada je rekao ocu svome: 'O oče moj, zašto obožavaš onoga koji niti čuje niti vidi, niti ti može od kakve koristi biti?", TextEnglish = "[Mention] when he said to his father, 'O my father, why do you worship that which does not hear and does not see and will not benefit you at all?", TextGerman = "Als er zu seinem Vater sagte: „O mein lieber Vater, warum dienst du dem, was nicht hört und nicht sieht und dir nichts nützt?" });
                        surah19.VerseList.Add(new Verse { Number = 43, TextBosnian = "O oče moj, meni dolazi znanje, a ne tebi; zato mene slijedi, i ja ću te na Pravi put uputiti.", TextEnglish = "O my father, indeed there has come to me of knowledge that which has not come to you, so follow me; I will guide you to an even path.", TextGerman = "O mein lieber Vater, gewiß, zu mir ist vom Wissen gekommen, was nicht zu dir gekommen ist. So folge mir, dann leite ich dich einen ebenen Weg." });
                        surah19.VerseList.Add(new Verse { Number = 44, TextBosnian = "Oče moj, ne pokoravaj se šejtanu, šejtan je Svemilosnom uvijek neposlušan;", TextEnglish = "O my father, do not worship [i.e., obey] Satan. Indeed Satan has ever been, to the Most Merciful, disobedient.", TextGerman = "O mein lieber Vater, diene nicht dem Satan. Gewiß, der Satan ist gegen den Allerbarmer widerspenstig." });
                        surah19.VerseList.Add(new Verse { Number = 45, TextBosnian = "oče moj, bojim se da te od Svemilosnog ne stigne kazna, pa da budeš šejtanu drug.'", TextEnglish = "O my father, indeed I fear that there will touch you a punishment from the Most Merciful so you would be to Satan a companion [in Hellfire].'", TextGerman = "O mein lieber Vater, ich fürchte, daß dir Strafe vom Allerbarmer widerfährt, so daß du zu einem Gefolgsmann des Satans wirst." });
                        surah19.VerseList.Add(new Verse { Number = 46, TextBosnian = "Otac njegov je rekao: 'Zar mrziš božanstva moja, o Ibrahime? Ako se ne okaniš, zbilja ću te kamenjem potjerati, zato me zadugo napusti.'", TextEnglish = "[His father] said, 'Have you no desire for my gods, O Abraham? If you do not desist, I will surely stone you, so avoid me a prolonged time.'", TextGerman = "Er sagte: „Verschmähst du meine Götter, o Ibrahim? Wenn du nicht aufhörst, werde ich dich ganz gewiß steinigen. Und meide mich eine Zeitlang." });
                        surah19.VerseList.Add(new Verse { Number = 47, TextBosnian = "Selam tebi!', reče Ibrahim. 'Molit ću Gospodara svog da ti oprosti, jer On je vrlo dobar prema meni.", TextEnglish = "[Abraham] said, 'Peace [i.e., safety] will be upon you.[815] I will ask forgiveness for you of my Lord. Indeed, He is ever gracious to me.", TextGerman = "Er sagte: „Friede sei auf dir! Ich werde meinen Herrn für dich um Vergebung bitten; gewiß, Er ist zu mir sehr entgegenkommend." });
                        surah19.VerseList.Add(new Verse { Number = 48, TextBosnian = "I napustit ću i vas i sve one kojima se mimo Allaha molite, i Gospodara svoga ću moliti; nadam se da dovom koju uputim Gospodaru mome neću razočaran biti.'", TextEnglish = "And I will leave you and those you invoke other than Allāh and will invoke[816] my Lord. I expect that I will not be in invocation to my Lord unhappy [i.e., disappointed].'", TextGerman = "Ich werde mich von euch und von dem, was ihr anstatt Allahs anruft, fernhalten und nur meinen Herrn anrufen. Vielleicht werde ich im Bittgebet zu meinem Herrn nicht unglücklich sein." });
                        surah19.VerseList.Add(new Verse { Number = 49, TextBosnian = "Pošto ih je napustio i one koje su, mimo Allaha, obožavali, Mi mu Ishaka i Jakuba darovasmo, i obojicu vjerovjesnicima učinismo,", TextEnglish = "So when he had left them and those they worshipped other than Allāh, We gave him Isaac and Jacob, and each [of them] We made a prophet.", TextGerman = "Als er sich nun von ihnen und von dem, dem sie anstatt Allahs dienten, fernhielt, schenkten Wir ihm Ishaq und Ya’qub; und alle (beide) machten Wir zu Propheten." });
                        surah19.VerseList.Add(new Verse { Number = 50, TextBosnian = "i darovasmo im svako dobro i učinismo da budu hvaljeni i po dobru spominjani.", TextEnglish = "And We gave them of Our mercy, and We made for them a mention [i.e., reputation] of high honor.", TextGerman = "Und Wir schenkten ihnen (etwas) von Unserer Barmherzigkeit, und Wir verliehen ihnen einen hohen Ruf an Wahrhaftigkeit." });
                        surah19.VerseList.Add(new Verse { Number = 51, TextBosnian = "I spomeni u Knjizi Musaa! On je bio odabran i bio je poslanik, vjerovjesnik.", TextEnglish = "And mention in the Book, Moses. Indeed, he was chosen, and he was a messenger and a prophet.[817]", TextGerman = "Und gedenke im Buch Musas. Gewiß, er war auserlesen, und er war ein Gesandter und Prophet." });
                        surah19.VerseList.Add(new Verse { Number = 52, TextBosnian = "Mi smo ga s desne strane Tura zovnuli i Sebi ga približili da čuje riječi Naše,", TextEnglish = "And We called him from the side of the mount[818] at [his] right and brought him near, confiding [to him].", TextGerman = "Wir riefen ihn von der rechten Seite des Berges und ließen ihn zu vertraulichem Gespräch näherkommen." });
                        surah19.VerseList.Add(new Verse { Number = 53, TextBosnian = "i darovali mu, iz milosti Naše, kao vjerovjesnika brata njegova Haruna.", TextEnglish = "And We gave him out of Our mercy his brother Aaron as a prophet.", TextGerman = "Und Wir schenkten ihm aus Unserer Barmherzigkeit seinen Bruder Harun als Propheten." });
                        surah19.VerseList.Add(new Verse { Number = 54, TextBosnian = "I spomeni u Knjizi Ismaila! On je ispunjavao dato obećanje i bio poslanik, vjerovjesnik.", TextEnglish = "And mention in the Book, Ishmael. Indeed, he was true to his promise, and he was a messenger and a prophet.", TextGerman = "Und gedenke im Buch Isma’ils. Gewiß, er war wahrhaftig in seinem Versprechen, und er war ein Gesandter und Prophet." });
                        surah19.VerseList.Add(new Verse { Number = 55, TextBosnian = "Naređivao je porodici svojoj da namaz obavljaju, i da zekat udjeljuju, i Gospodar njegov je bio njime zadovoljan.", TextEnglish = "And he used to enjoin on his people prayer and zakāh and was to his Lord pleasing [i.e., accepted by Him].", TextGerman = "Und er pflegte seinen Angehörigen das Gebet und die Abgabe zu befehlen, und er war seinem Herrn wohlgefällig." });
                        surah19.VerseList.Add(new Verse { Number = 56, TextBosnian = "I spomeni u Knjizi Idrisa! On je bio istinoljubiv, vjerovjesnik,", TextEnglish = "And mention in the Book, Idrees. Indeed, he was a man of truth and a prophet.", TextGerman = "Und gedenke im Buch Idris’. Er war ein Wahrhaftiger und Prophet." });
                        surah19.VerseList.Add(new Verse { Number = 57, TextBosnian = "i Mi smo ga na visoko mjesto digli.", TextEnglish = "And We raised him to a high station.", TextGerman = "Und Wir hoben ihn zu einem hohen Ort empor." });
                        surah19.VerseList.Add(new Verse { Number = 58, TextBosnian = "To su vjerovjesnici koje je Allah blagodatima obasuo, potomci Ademovi i onih koje smo sa Nuhom nosili, i potomci Ibrahimovi i Israilovi, i onih koje smo uputili i odabrali. Kad bi im se ajeti Svemilosnog učili, oni bi na sedždu padali i plakali.", TextEnglish = "Those were the ones upon whom Allāh bestowed favor from among the prophets of the descendants of Adam and of those We carried [in the ship] with Noah, and of the descendants of Abraham and Israel [i.e., Jacob], and of those whom We guided and chose. When the verses of the Most Merciful were recited to them, they fell in prostration and weeping.", TextGerman = "Das sind diejenigen, denen Allah Gunst erwiesen hat, unter den Propheten aus der Nachkommenschaft Adams und von denjenigen, die Wir mit Nuh trugen, und aus der Nachkommenschaft Ibrahims und Isra’ils, und von denjenigen, die Wir rechtgeleitet und erwählt haben. Als ihnen die Zeichen des Allerbarmers verlesen wurden, fielen sie ehrerbietig und weinend nieder." });
                        surah19.VerseList.Add(new Verse { Number = 59, TextBosnian = "Njih smijeniše naraštaji koji namaz napustiše i za požudama pođoše; oni će na propast naići;", TextEnglish = "But there came after them successors [i.e., later generations] who neglected prayer and pursued desires; so they are going to meet evil[819] -", TextGerman = "Dann folgten nach ihnen Nachfolger, die das Gebet vernachlässigten und den Begierden folgten. So werden sie (den Lohn für ihre) Verirrung vorfinden," });
                        surah19.VerseList.Add(new Verse { Number = 60, TextBosnian = "izuzev onih koji su se pokajali, i vjerovali, i dobro činili, njima se neće nikakva nepravda učiniti, oni će u Džennet ući.", TextEnglish = "Except those who repent, believe and do righteousness; for those will enter Paradise and will not be wronged at all.", TextGerman = "außer demjenigen, der bereut und glaubt und rechtschaffen handelt. Jene werden in den (Paradies)garten eingehen und ihnen wird in nichts Unrecht zugefügt" });
                        surah19.VerseList.Add(new Verse { Number = 61, TextBosnian = "U vrtove Adna koje je Svemilosni robovima Svojim kao nešto što je čulima nedokučivo obećao, a obećanje Njegovo će se, doista, ispuniti,", TextEnglish = "[Therein are] gardens of perpetual residence which the Most Merciful has promised His servants in the unseen. Indeed, His promise has ever been eminent.[820]", TextGerman = "in die Gärten Edens, die der Allerbarmer Seinen Dienern im Verborgenen versprochen hat. Sein Versprechen wird bestimmt erfüllt." });
                        surah19.VerseList.Add(new Verse { Number = 62, TextBosnian = "u njima prazne besjede neće slušati, već samo: 'Selam!' -i u njima će ujutro i navečer opskrbljeni biti.", TextEnglish = "They will not hear therein any ill speech - only [greetings of] peace - and they will have their provision therein, morning and afternoon.", TextGerman = "Sie hören darin keine unbedachte Rede, sondern nur: Frieden! Und sie haben darin ihre Versorgung morgens und abends." });
                        surah19.VerseList.Add(new Verse { Number = 63, TextBosnian = "Dat ćemo da takav Džennet naslijedi onaj od robova Naših koji se bude Allaha bojao i grijeha klonio.", TextEnglish = "That is Paradise, which We give as inheritance to those of Our servants who were fearing of Allāh.", TextGerman = "Das ist der (Paradies)garten, den Wir denjenigen von Unseren Dienern zum Erbe geben, die gottesfürchtig sind." });
                        surah19.VerseList.Add(new Verse { Number = 64, TextBosnian = "A mi silazimo samo po naredbi Gospodara tvoga. Njemu pripada ono što je pred nama i ono što je za nama, i što je između toga. A Gospodar tvoj nije zaboravan.'", TextEnglish = "[Gabriel said],[821] 'And we [angels] descend not except by the order of your Lord. To Him belongs that before us and that behind us and what is in between. And never is your Lord forgetful -", TextGerman = "(Und die Engel sagen:) „Wir kommen nur auf Befehl deines Herrn herab. Ihm gehört (alles), was vor uns und was hinter uns und was dazwischen ist; und dein Herr ist gewiß nicht vergeßlich,’" });
                        surah19.VerseList.Add(new Verse { Number = 65, TextBosnian = "On je Gospodar nebesa i Zemlje i onoga što je između njih, zato samo Njemu ibadet čini i u tome budi ustrajan! Znaš li da ima neko Njemu sličan?", TextEnglish = "Lord of the heavens and the earth and whatever is between them - so worship Him and have patience for His worship. Do you know of any similarity to Him?'", TextGerman = "(Er), der Herr der Himmel und der Erde und dessen, was dazwischen ist. So diene Ihm und sei beharrlich in Seinem Dienst. Weißt du (etwa) einen, der Kennzeichen gleich den Seinen besäße?" });
                        surah19.VerseList.Add(new Verse { Number = 66, TextBosnian = "Čovjek govori: 'Zar ću, kad umrem, zbilja biti oživljen?'", TextEnglish = "And man [i.e., the disbeliever] says, 'When I have died, am I going to be brought forth alive?'", TextGerman = "Der Mensch sagt: „Werde ich, wenn ich erst einmal gestorben bin, wirklich wieder lebendig hervorgebracht werden?" });
                        surah19.VerseList.Add(new Verse { Number = 67, TextBosnian = "A zar se čovjek ne sjeća da smo ga još prije stvorili, a da nije bio ništa?", TextEnglish = "Does man not remember that We created him before, while he was nothing?", TextGerman = "Gedenkt der Mensch denn nicht, daß Wir ihn zuvor erschaffen haben, da er (noch) nichts war?" });
                        surah19.VerseList.Add(new Verse { Number = 68, TextBosnian = "Tako mi Gospodara tvoga, Mi ćemo i njih i šejtane skupiti, zatim ćemo ih dovesti da oko Džehennema na koljenima kleče,", TextEnglish = "So by your Lord, We will surely gather them and the devils; then We will bring them to be present around Hell upon their knees.[822]", TextGerman = "Bei deinem Herrn, Wir werden sie ganz gewiß versammeln, (sie) und die Satane. Hierauf werden Wir sie ganz gewiß rings um die Hölle herum auf den Knien herbeibringen." });
                        surah19.VerseList.Add(new Verse { Number = 69, TextBosnian = "a onda ćemo iz svake skupine izdvojiti one koji su prema Svemilosnom najdrskiji bili,", TextEnglish = "Then We will surely extract from every sect those of them who were worst against the Most Merciful in insolence.", TextGerman = "Hierauf werden Wir aus jedem Lager ganz gewiß denjenigen herausnehmen, der sich dem Allerbarmer am heftigsten widersetzt hat." });
                        surah19.VerseList.Add(new Verse { Number = 70, TextBosnian = "jer Mi najbolje znamo one koji su najviše zaslužili da u njemu gore.", TextEnglish = "Then, surely it is We who are most knowing of those most worthy of burning therein.", TextGerman = "Dann wissen Wir wahrlich am besten über diejenigen Bescheid, die es am ehesten verdienen, ihr ausgesetzt zu sein." });
                        surah19.VerseList.Add(new Verse { Number = 71, TextBosnian = "I svaki od vas će do njega stići! Gospodar tvoj se sigurno tako obavezao!", TextEnglish = "And there is none of you except he will come to it.[823] This is upon your Lord an inevitability decreed.", TextGerman = "Und es gibt keinen unter euch, der nicht daran vorbeigehen würde. Dies obliegt deinem Herrn unabänderlich beschlossen." });
                        surah19.VerseList.Add(new Verse { Number = 72, TextBosnian = "Zatim ćemo one koji su se Allaha bojali i grijeha klonili spasiti, a zulumćare ćemo da u njemu na koljenima kleče ostaviti.", TextEnglish = "Then We will save those who feared Allāh and leave the wrongdoers within it, on their knees.", TextGerman = "Hierauf erretten Wir diejenigen, die gottesfürchtig waren, und lassen die Ungerechten in ihr auf den Knien zurück." });
                        surah19.VerseList.Add(new Verse { Number = 73, TextBosnian = "Kada su im se Naši razgovijetni ajeti učili, onda su oni koji nisu vjerovali govorili onima koji su vjerovali: 'Koja od ove dvije skupine ima bolje nastanbe i ljepša sastajališta?'", TextEnglish = "And when Our verses are recited to them as clear evidences, those who disbelieve say to those who believe, 'Which of [our] two parties is best in position and best in association?'[824]", TextGerman = "Und wenn ihnen Unsere Zeichen als klare Beweise verlesen werden, sagen diejenigen, die ungläubig sind, zu denjenigen, die glauben: „Welcher der beiden Gruppen hat nun eine bessere Stellung und eine schönere Gesellschaft?" });
                        surah19.VerseList.Add(new Verse { Number = 74, TextBosnian = "A koliko smo Mi prije njih naroda uništili koji su blagom i izgledom bolji bili?!", TextEnglish = "And how many a generation have We destroyed before them who were better in possessions and [outward] appearance?", TextGerman = "Aber wie viele Geschlechter vernichteten Wir vor ihnen, die besser an Ausstattung und Aussehen waren!" });
                        surah19.VerseList.Add(new Verse { Number = 75, TextBosnian = "Reci: 'Onome ko je u zabludi, neka Svemilosni produži zabludu!' Ali kad takvi dožive da se opomene ostvare, bilo kazna, bilo Smak svijeta, zbilja će saznati ko je u gorem položaju i ko ima pobornika manje.", TextEnglish = "Say, 'Whoever is in error - let the Most Merciful extend for him an extension [in wealth and time] until, when they see that which they were promised - either punishment [in this world] or the Hour [of resurrection] - they will come to know who is worst in position and weaker in soldiers.'", TextGerman = "Sag: Wer sich im Irrtum befindet, den möge der Allerbarmer lange darin gewähren lassen. Wenn sie dann sehen, was ihnen angedroht ist, entweder die Strafe oder die Stunde, da werden sie wissen, wer sich in einer (noch) schlechteren Lage befindet und die schwächere Heerschar hat." });
                        surah19.VerseList.Add(new Verse { Number = 76, TextBosnian = "Allah će povećati uputu onima koji su na Pravom putu! A dobra djela, koja vječno ostaju - od Gospodara tvoga bit će bolje nagrađena i ljepše uzvraćena.", TextEnglish = "And Allāh increases those who were guided, in guidance, and the enduring good deeds are better to your Lord[825] for reward and better for recourse.", TextGerman = "Und Allah mehrt denjenigen, die rechtgeleitet sind, ihre Rechtleitung. Das Bleibende aber, die rechtschaffenen Werke - sie sind bei deinem Herrn besser in der Belohnung und besser als Rückkehr." });
                        surah19.VerseList.Add(new Verse { Number = 77, TextBosnian = "Zar nisi vidio onoga koji u ajete Naše ne vjeruje i govori: 'Zacijelo će mi biti dat imetak i djeca!'", TextEnglish = "Then, have you seen he who disbelieved in Our verses and said, 'I will surely be given wealth and children [in the next life]'?", TextGerman = "Was meinst du wohl zu demjenigen, der Unsere Zeichen verleugnet und sagt: „Mir werden ganz gewiß Besitz und Kinder gegeben?" });
                        surah19.VerseList.Add(new Verse { Number = 78, TextBosnian = "Ili je on u ono što je čulima nedokučivo prodro ili je od Svemilosnog obećanje primio?!", TextEnglish = "Has he looked into the unseen, or has he taken from the Most Merciful a promise?", TextGerman = "Hat er etwa Einblick in das Verborgene gewonnen, oder hat er vom Allerbarmer eine Verpflichtung entgegengenommen?" });
                        surah19.VerseList.Add(new Verse { Number = 79, TextBosnian = "Nijedno! Mi ćemo ono što on govori zapisati i patnju mu znatno produžiti,", TextEnglish = "No! We will record what he says and extend [i.e., increase] for him from the punishment extensively.", TextGerman = "Keineswegs! Wir werden aufschreiben, was er sagt, und ihm die Strafe noch verlängern." });
                        surah19.VerseList.Add(new Verse { Number = 80, TextBosnian = "a ono o čemu on govori - naslijediti i sam samcat će Nam se vratiti.", TextEnglish = "And We will inherit him [in] what he mentions,[826] and he will come to Us alone.", TextGerman = "Und Wir erben von ihm das, was er sagt, während er einzeln zu Uns kommt." });
                        surah19.VerseList.Add(new Verse { Number = 81, TextBosnian = "I oni mimo Allaha prihvataju božanstva da im budu snaga.", TextEnglish = "And they have taken besides Allāh [false] deities that they would be for them [a source of] honor.", TextGerman = "Und sie haben sich anstatt Allahs Götter genommen, damit sie ihnen zu Macht gereichen." });
                        surah19.VerseList.Add(new Verse { Number = 82, TextBosnian = "Ali, ne! Božanstva će poreći obožavanje njihovo, i bit će im protivnici.", TextEnglish = "No! They [i.e., those 'gods'] will deny their worship of them and will be against them opponents [on the Day of Judgement].", TextGerman = "Keineswegs! Sie werden den von ihnen (empfangenen) Dienst verleugnen und werden ihnen Gegner sein." });
                        surah19.VerseList.Add(new Verse { Number = 83, TextBosnian = "Zar ne vidiš da Mi šejtane na nevjernike šaljemo da ih što više na zlo navraćaju?", TextEnglish = "Do you not see that We have sent the devils upon the disbelievers, inciting them [to evil] with [constant] incitement?", TextGerman = "Siehst du nicht, daß Wir die Satane gegen die Ungläubigen gesandt haben, damit sie sie heftig aufreizen?" });
                        surah19.VerseList.Add(new Verse { Number = 84, TextBosnian = "Zato ne traži da što prije stradaju, Mi im polahko dane odbrojavamo.", TextEnglish = "So be not impatient over them. We only count out [i.e., allow] to them a [limited] number.[827]", TextGerman = "So wünsche nichts gegen sie zu beschleunigen; Wir zählen ihnen schon (die Tage) ab." });
                        surah19.VerseList.Add(new Verse { Number = 85, TextBosnian = "Na Dan kada bogobojazne, kao uzvanike, pred Svemilosnim sakupimo,", TextEnglish = "On the Day We will gather the righteous to the Most Merciful as a delegation", TextGerman = "An einem Tag, da Wir die Gottesfürchtigen als eine (geehrte) Abordnung zum Allerbarmer versammeln" });
                        surah19.VerseList.Add(new Verse { Number = 86, TextBosnian = "a kad u Džehennem žedne grešnike potjeramo,", TextEnglish = "And will drive the criminals to Hell in thirst", TextGerman = "und die Übeltäter, wie eine durstige Herde zur Tränke zur Hölle treiben," });
                        surah19.VerseList.Add(new Verse { Number = 87, TextBosnian = "niko se ni za koga neće moći zauzimati, osim onoga ko je obećanje od Svemilosnog uzeo!", TextEnglish = "None will have [power of] intercession except he who had taken from the Most Merciful a covenant.[828]", TextGerman = "verfugen sie nicht über die Fürsprache, außer demjenigen, der vom Allerbarmer eine (diesbezügliche) Verpflichtung entgegengenommen hat." });
                        surah19.VerseList.Add(new Verse { Number = 88, TextBosnian = "Oni govore: 'Svemilosni je uzeo dijete!'", TextEnglish = "And they say, 'The Most Merciful has taken [for Himself] a son.'", TextGerman = "Und sie sagen: „Der Allerbarmer hat Sich Kinder genommen." });
                        surah19.VerseList.Add(new Verse { Number = 89, TextBosnian = "Vi, doista, nešto krupno govorite!", TextEnglish = "You have done an atrocious thing.", TextGerman = "Ihr habt ja eine abscheuliche Sache begangen." });
                        surah19.VerseList.Add(new Verse { Number = 90, TextBosnian = "Gotovo da se od toga nebesa raspadnu, a Zemlja provali i planine zdrobe,", TextEnglish = "The heavens almost rupture therefrom and the earth splits open and the mountains collapse in devastation", TextGerman = "Beinahe brechen davon die Himmel auseinander, und (beinahe) spaltet sich die Erde, und (beinahe) stürzen die Berge in Trümmern zusammen," });
                        surah19.VerseList.Add(new Verse { Number = 91, TextBosnian = "što Svemilosnom pripisuju dijete!", TextEnglish = "That they attribute to the Most Merciful a son.", TextGerman = "daß sie dem Allerbarmer Kinder zuschreiben." });
                        surah19.VerseList.Add(new Verse { Number = 92, TextBosnian = "A Svemilosnom ne treba to da uzima dijete!", TextEnglish = "And it is not appropriate for the Most Merciful that He should take a son.", TextGerman = "Es ziemt dem Allerbarmer nicht, Sich Kinder zu nehmen." });
                        surah19.VerseList.Add(new Verse { Number = 93, TextBosnian = "Ta, svi će oni, i oni na nebesima i oni na Zemlji, doći Svemilosnom kao robovi.", TextEnglish = "There is no one in the heavens and earth but that he comes to the Most Merciful as a servant.", TextGerman = "Niemand in den Himmeln und auf der Erde wird zum Allerbarmer anders denn als Diener kommen (können)." });
                        surah19.VerseList.Add(new Verse { Number = 94, TextBosnian = "On ih je sve znanjem obuhvatio i svakoga napose izbrojio,", TextEnglish = "He has enumerated them and counted them a [full] counting.", TextGerman = "Er hat sie erfaßt und sie genau gezählt." });
                        surah19.VerseList.Add(new Verse { Number = 95, TextBosnian = "i svi će Mu na Kijametskom danu doći pojedinačno.", TextEnglish = "And all of them are coming to Him on the Day of Resurrection alone.", TextGerman = "Und sie alle werden zu Ihm am Tag der Auferstehung einzeln kommen." });
                        surah19.VerseList.Add(new Verse { Number = 96, TextBosnian = "One koji su vjerovali i dobra djela činili Svemilosni će sigurno voljenim učiniti.", TextEnglish = "Indeed, those who have believed and done righteous deeds - the Most Merciful will appoint for them affection.[829]", TextGerman = "Gewiß, denjenigen, die glauben und rechtschaffene Werke tun, wird der Allerbarmer Liebe bereiten." });
                        surah19.VerseList.Add(new Verse { Number = 97, TextBosnian = "Mi smo Kur'an učinili lahkim, na tvome jeziku, da bi njime bogobojazne obradovao, a iskvarene ljude upozorio.", TextEnglish = "So, [O Muḥammad], We have only made it [i.e., the Qur’ān] easy in your tongue [i.e., the Arabic language] that you may give good tidings thereby to the righteous and warn thereby a hostile people.", TextGerman = "Wir haben ihn eigens leicht gemacht in deiner Sprache, damit du durch ihn den Gottesfürchtigen frohe Botschaft verkündest und durch ihn hartnäckige Leute warnst." });
                        surah19.VerseList.Add(new Verse { Number = 98, TextBosnian = "A koliko smo samo naroda prije njih uništili! Da li ijednog od njih vidiš i da li i najslabiji glas njihov čuješ?!", TextEnglish = "And how many have We destroyed before them of generations? Do you perceive of them anyone or hear from them a sound?", TextGerman = "Und wie viele Geschlechter haben Wir vor ihnen vernichtet! Nimmst du noch irgend jemanden von ihnen wahr, oder hörst du von ihnen noch den geringsten Laut?" });
                        result = surah19;
                        break;
                    #endregion

                    // 
                    #region 20 Taha
                    case 20:
                        Surah surah20 = new Surah(); surah20.Name = Constants.Surah20Name; surah20.Number = 20; surah20.VerseList = new List<Verse>();
                        surah20.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah20.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ta-ha", TextEnglish = "Ṭā, Hā.[831]", TextGerman = "Ta-Ha." });
                        surah20.VerseList.Add(new Verse { Number = 2, TextBosnian = "Ne objavljujemo ti Kur'an da se mučiš.", TextEnglish = "We have not sent down to you the Qur’ān that you be distressed", TextGerman = "Wir haben den Qur’an nicht auf dich (als Offenbarung) hinabgesandt, damit du unglücklich bist," });
                        surah20.VerseList.Add(new Verse { Number = 3, TextBosnian = "već da bude opomena onome koji se boji;", TextEnglish = "But only as a reminder for those who fear [Allāh] -", TextGerman = "sondern als Erinnerung für denjenigen, der gottesfürchtig ist," });
                        surah20.VerseList.Add(new Verse { Number = 4, TextBosnian = "objavljuje ga Stvoritelj Zemlje i nebesa visokih,", TextEnglish = "A revelation from He who created the earth and highest heavens,", TextGerman = "eine Offenbarung von Demjenigen, Der die Erde und die hohen Himmel erschaffen hat." });
                        surah20.VerseList.Add(new Verse { Number = 5, TextBosnian = "Svemilosni, Koji se nad Aršom uzvisio.", TextEnglish = "The Most Merciful [who is] above the Throne established.[832]", TextGerman = "Der Allerbarmer ist über dem Thron erhaben." });
                        surah20.VerseList.Add(new Verse { Number = 6, TextBosnian = "Njegovo je što je na nebesima, i na Zemlji, i što je između njih, i što je pod zemljom.", TextEnglish = "To Him belongs what is in the heavens and what is on the earth and what is between them and what is under the soil.", TextGerman = "Ihm gehört (alles), was in den Himmeln und was auf der Erde ist und was dazwischen und was unter dem (feuchten) Erdreich ist." });
                        surah20.VerseList.Add(new Verse { Number = 7, TextBosnian = "Ako se ti glasno moliš, pa On zna i što je tajno i što je još skrovitije.", TextEnglish = "And if you speak aloud - then indeed, He knows the secret and what is [even] more hidden.", TextGerman = "Und wenn du deine Worte laut vernehmbar äußerst, gewiß so weiß Er ja das Geheime und was noch verborgener ist." });
                        surah20.VerseList.Add(new Verse { Number = 8, TextBosnian = "Allah, istinskog boga osim Njega nema, najljepša imena ima.", TextEnglish = "Allāh - there is no deity except Him. To Him belong the best names.", TextGerman = "Allah - es gibt keinen Gott außer Ihm. Sein sind die schönsten Namen." });
                        surah20.VerseList.Add(new Verse { Number = 9, TextBosnian = "A da li je do tebe došla vijest o Musau,", TextEnglish = "And has the story of Moses reached you? -", TextGerman = "Ist zu dir die Geschichte Musas gekommen?" });
                        surah20.VerseList.Add(new Verse { Number = 10, TextBosnian = "kada je vatru ugledao, pa porodici svojoj rekao: 'Ostanite vi tu, ja sam vatru vidio, možda ću vam nekakvu glavnju donijeti ili ću pored vatre naći nekoga ko će mi put pokazati.'", TextEnglish = "When he saw a fire and said to his family, 'Stay here; indeed, I have perceived a fire; perhaps I can bring you a torch or find at the fire some guidance.'", TextGerman = "Als er ein Feuer sah und zu seinen Angehörigen sagte: „Bleibt hier. Ich habe ein Feuer wahrgenommen; vielleicht kann ich euch davon ein brennendes Stück Holz bringen oder am Feuer eine Wegweisung finden." });
                        surah20.VerseList.Add(new Verse { Number = 11, TextBosnian = "A kad do nje dođe, neko ga zovnu: 'O Musa,", TextEnglish = "And when he came to it, he was called, 'O Moses,", TextGerman = "Als er dort hinkam, wurde ihm zugerufen: „O Musa!" });
                        surah20.VerseList.Add(new Verse { Number = 12, TextBosnian = "Ja sam, uistinu, Gospodar tvoj! Izuj, zato, obuću svoju, ti si, doista, u blagoslovljenoj dolini Tuva.", TextEnglish = "Indeed, I am your Lord, so remove your sandals. Indeed, you are in the blessed valley of Ṭuwā.", TextGerman = "Gewiß, Ich bin dein Herr, so ziehe deine Schuhe aus. Du befindest dich im geheiligten Tal Tuwa." });
                        surah20.VerseList.Add(new Verse { Number = 13, TextBosnian = "Tebe sam izabrao, zato ono što ti se objavljuje slušaj!", TextEnglish = "And I have chosen you, so listen to what is revealed [to you].", TextGerman = "Und Ich habe dich erwählt. So höre auf das, was (als Offenbarung) eingegeben wird." });
                        surah20.VerseList.Add(new Verse { Number = 14, TextBosnian = "Ja sam, uistinu, Allah, drugog boga osim Mene nema; pa Meni ibadet čini i kad god Me se sjetiš, namaz obavi!", TextEnglish = "Indeed, I am Allāh. There is no deity except Me, so worship Me and establish prayer for My remembrance.", TextGerman = "Gewiß, Ich bin Allah. Es gibt keinen Gott außer Mir. So diene Mir und verrichte das Gebet zu Meinem Gedenken." });
                        surah20.VerseList.Add(new Verse { Number = 15, TextBosnian = "čas oživljenja sigurno će doći, skoro da ga i od Sebe tajim, kad će svaki čovjek prema trudu svome nagrađen ili kažnjen biti.", TextEnglish = "Indeed, the Hour is coming - I almost conceal it[833] - so that every soul may be recompensed according to that for which it strives.", TextGerman = "Gewiß, die Stunde kommt - Ich hielte sie beinahe (ganz) verborgen -, damit jeder Seele das vergolten wird, worum sie sich bemüht." });
                        surah20.VerseList.Add(new Verse { Number = 16, TextBosnian = "I neka te zato od vjerovanja u njeg nikako ne odvrati onaj koji u njega ne vjeruje i koji slijedi strast svoju, pa da budeš izgubljen.", TextEnglish = "So do not let one avert you from it[834] who does not believe in it and follows his desire, for you [then] would perish.", TextGerman = "So lasse denn nicht denjenigen dich von ihr abhalten, der nicht an sie glaubt und seiner Neigung folgt, sonst würdest du dich ins Verderben stürzen." });
                        surah20.VerseList.Add(new Verse { Number = 17, TextBosnian = "A šta ti je to u desnoj ruci, o Musa?'", TextEnglish = "And what is that in your right hand, O Moses?'", TextGerman = "Und was ist das da in deiner Rechten, o Musa?" });
                        surah20.VerseList.Add(new Verse { Number = 18, TextBosnian = "Ovo je moj štap', odgovori on, 'kojim se poštapam i kojim lišće ovcama svojim skidam, a služi mi i za druge potrebe.'", TextEnglish = "He said, 'It is my staff; I lean upon it, and I bring down leaves for my sheep and I have therein other uses.'", TextGerman = "Er sagte: „Es ist mein Stock, auf den ich mich stütze und mit dem ich für meine Schafe Blätter abschlage; und ich gebrauche ihn auch noch zu anderen Zwecken." });
                        surah20.VerseList.Add(new Verse { Number = 19, TextBosnian = "Baci ga, o Musa!', reče On.", TextEnglish = "[Allāh] said, 'Throw it down, O Moses.'", TextGerman = "Er sagte: „Wirf ihn hin, o Musa!" });
                        surah20.VerseList.Add(new Verse { Number = 20, TextBosnian = "I on ga baci, kad on, zmija koja hmili.", TextEnglish = "So he threw it down, and thereupon it was a snake, moving swiftly.", TextGerman = "Er warf ihn hin, und sogleich war er eine Schlange, die sich rasch bewegte." });
                        surah20.VerseList.Add(new Verse { Number = 21, TextBosnian = "Uzmi je i ne boj se!', reče On, 'Mi ćemo je vratiti u ono što je prije bila.", TextEnglish = "[Allāh] said, 'Seize it and fear not; We will return it to its former condition.", TextGerman = "Er sagte: „Nimm sie, und fürchte dich nicht. Wir werden sie in ihren früheren Zustand zurückbringen." });
                        surah20.VerseList.Add(new Verse { Number = 22, TextBosnian = "I uvuci ruku pod pazuho svoje, ruka će se ukazati bijela, bez ikakve mahane; kao znak drugi.", TextEnglish = "And draw in your hand to your side; it will come out white without disease - another sign,", TextGerman = "Und lege deine Hand dicht an deine Seite (, unter dem Oberarm), so kommt sie weiß heraus, jedoch nicht von Übel befallen. (Nimm dies) als weiteres Zeichen," });
                        surah20.VerseList.Add(new Verse { Number = 23, TextBosnian = "To je zato da ti pokažemo neke od Naših najvećih znakova.'", TextEnglish = "That We may show you [some] of Our greater signs.", TextGerman = "auf daß Wir dich etwas von Unseren größten Zeichen sehen lassen." });
                        surah20.VerseList.Add(new Verse { Number = 24, TextBosnian = "Idi faraonu, on se, zaista, osilio!'", TextEnglish = "Go to Pharaoh. Indeed, he has transgressed [i.e., tyrannized].'", TextGerman = "Geh zu Fir’aun, denn gewiß, er überschreitet das Maß (an Frevel)." });
                        surah20.VerseList.Add(new Verse { Number = 25, TextBosnian = "Gospodaru moj', reče Musa, 'učini prostranim prsa moja,", TextEnglish = "[Moses] said, 'My Lord, expand [i.e., relax] for me my breast [with assurance]", TextGerman = "Er sagte: „Mein Herr, weite mir meine Brust," });
                        surah20.VerseList.Add(new Verse { Number = 26, TextBosnian = "i olakšaj zadatak moj:", TextEnglish = "And ease for me my task", TextGerman = "und mache mir meine Angelegenheit leicht." });
                        surah20.VerseList.Add(new Verse { Number = 27, TextBosnian = "odriješi čvor s jezika mog", TextEnglish = "And untie the knot from my tongue", TextGerman = "Und löse den Knoten in meiner Zunge," });
                        surah20.VerseList.Add(new Verse { Number = 28, TextBosnian = "da bi razumjeli govor moj", TextEnglish = "That they may understand my speech.", TextGerman = "so daß sie meine Worte verstehen." });
                        surah20.VerseList.Add(new Verse { Number = 29, TextBosnian = "i daj mi pomoćnika iz porodice moje,", TextEnglish = "And appoint for me a minister [i.e., assistant] from my family -", TextGerman = "Und bestelle mir von meinen Angehörigen einen, der (die Last) mitträgt," });
                        surah20.VerseList.Add(new Verse { Number = 30, TextBosnian = "Haruna, brata mog;", TextEnglish = "Aaron, my brother.", TextGerman = "Harun, meinen Bruder." });
                        surah20.VerseList.Add(new Verse { Number = 31, TextBosnian = "osnaži me njime", TextEnglish = "Increase through him my strength", TextGerman = "Festige durch ihn meine Stärke," });
                        surah20.VerseList.Add(new Verse { Number = 32, TextBosnian = "i pridruži mi ga u zadatku mome", TextEnglish = "And let him share my task", TextGerman = "und lasse ihn an meiner Angelegenheit teilhaben," });
                        surah20.VerseList.Add(new Verse { Number = 33, TextBosnian = "da bismo Te mnogo veličanjem slavili", TextEnglish = "That we may exalt You much", TextGerman = "damit wir Dich häufig preisen" });
                        surah20.VerseList.Add(new Verse { Number = 34, TextBosnian = "i da bismo Te mnogo spominjali,", TextEnglish = "And remember You much.", TextGerman = "und Deiner häufig gedenken." });
                        surah20.VerseList.Add(new Verse { Number = 35, TextBosnian = "Ti, uistinu, sve u vezi s nama uviđaš.'", TextEnglish = "Indeed, You are of us ever Seeing.'", TextGerman = "Gewiß, Du siehst uns wohl." });
                        surah20.VerseList.Add(new Verse { Number = 36, TextBosnian = "Udovoljeno je molbi tvojoj, o Musa', reče On,", TextEnglish = "[Allāh] said, 'You have been granted your request, O Moses.", TextGerman = "Er sagte: „Deine Bitte ist dir ja gewährt, o Musa!" });
                        surah20.VerseList.Add(new Verse { Number = 37, TextBosnian = "a ukazali smo ti milost Svoju još jednom,", TextEnglish = "And We had already conferred favor upon you another time,", TextGerman = "Und Wir erwiesen dir bereits ein anderes Mal eine Wohltat," });
                        surah20.VerseList.Add(new Verse { Number = 38, TextBosnian = "kada smo majku tvoju nadahnuli onim što se samo nadahnućem zadobija:", TextEnglish = "When We inspired to your mother what We inspired,", TextGerman = "als Wir deiner Mutter eingaben, was (als Weisung) eingegeben werden sollte:" });
                        surah20.VerseList.Add(new Verse { Number = 39, TextBosnian = "Metni ga u sanduk i u rijeku baci, rijeka će ga na obalu izbaciti, pa će ga i Moj i njegov neprijatelj prihvatiti.' I na tebe sam prosuo ljubav od Sebe i da budeš othranjen pod okom Mojim.", TextEnglish = "[Saying], 'Cast him into the chest and cast it into the river, and the river will throw it onto the bank; there will take him an enemy to Me and an enemy to him.' And I bestowed upon you love from Me[835] that you would be brought up under My eye [i.e., observation and care].", TextGerman = ",Wirf ihn in den Kasten und wirf ihn ins Wasser’, und das Wasser soll ihn ans Ufer setzen, so daß ihn ein Feind von Mir und ein Feind von ihm aufnimmt. Und Ich habe auf dich Liebe von Mir gelegt - und damit du vor Meinem Auge aufgezogen würdest." });
                        surah20.VerseList.Add(new Verse { Number = 40, TextBosnian = "Kada je sestra tvoja otišla i rekla: 'Hoćete li da vam ja pokažem onu koja će se o njemu brinuti?' Mi smo te majci tvojoj opet vratili da se raduje i da više ne tuguje. A ti si ubio jednog čovjeka, pa smo te Mi brige oslobodili i raznim nevoljama te iskušali pa spasili. I ti si ostao godinama među stanovnicima Medjena, zatim si, o Musa, u određeno vrijeme došao,", TextEnglish = "[And We favored you] when your sister went and said, 'Shall I direct you to someone who will be responsible for him?' So We restored you to your mother that she might be content and not grieve. And you killed someone,[836] but We saved you from retaliation and tried you with a [severe] trial. And you remained [some] years among the people of Madyan. Then you came [here] at the decreed time, O Moses.", TextGerman = "Als deine Schwester hinging und sagte: ,Soll ich euch auf jemanden hinweisen, der ihn betreuen würde?’ So gaben Wir dich deiner Mutter wieder, damit sie frohen Mutes und nicht traurig sei. Und du tötetest eine Seele, und da erretteten Wir dich aus dem Kummer, und Wir unterzogen dich einer harten Prüfung. So verweiltest du jahrelang unter den Leuten von Madyan. Hierauf kamst du zu einer vorausbestimmten Zeit, o Musa." });
                        surah20.VerseList.Add(new Verse { Number = 41, TextBosnian = "i za Sebe sam te pripravio.", TextEnglish = "And I produced you for Myself.[837]", TextGerman = "Und Ich habe dich für Mich auserwählt." });
                        surah20.VerseList.Add(new Verse { Number = 42, TextBosnian = "Idite ti i tvoj brat, sa znakovima Mojim, i nemojte malaksati u sjećanju na Mene.", TextEnglish = "Go, you and your brother, with My signs and do not slacken in My remembrance.", TextGerman = "Geh, du und dein Bruder, mit Meinen Zeichen, und laßt nicht nach in Meinem Gedenken." });
                        surah20.VerseList.Add(new Verse { Number = 43, TextBosnian = "Idite faraonu, on se, doista, osilio,", TextEnglish = "Go, both of you, to Pharaoh. Indeed, he has transgressed.", TextGerman = "Geht zu Fir’aun, denn er lehnt sich auf." });
                        surah20.VerseList.Add(new Verse { Number = 44, TextBosnian = "pa mu blagim riječima govorite, ne bi li razmislio ili se pobojao.'", TextEnglish = "And speak to him with gentle speech that perhaps he may be reminded or fear [Allāh].'", TextGerman = "Und so redet mit ihm in sanften Worten, auf daß er bedenken oder sich fürchten möge." });
                        surah20.VerseList.Add(new Verse { Number = 45, TextBosnian = "Gospodaru naš', rekoše oni, 'bojimo se da nas odmah na muke ne stavi ili da svaku mjeru zla ne prekorači.'", TextEnglish = "They said, 'Our Lord, indeed we are afraid that he will hasten [punishment] against us or that he will transgress.'", TextGerman = "Sie sagten: „Unser Herr, gewiß, wir fürchten, daß er übereilig gegen uns vorgeht oder daß er das Maß überschreitet." });
                        surah20.VerseList.Add(new Verse { Number = 46, TextBosnian = "Ne bojte se!', reče On. 'Ja sam s vama; čujem i vidim?'", TextEnglish = "[Allāh] said, 'Fear not. Indeed, I am with you both; I hear and I see.", TextGerman = "Er sagte: „Fürchtet euch nicht. Ich bin gewiß mit euch, Ich höre und Ich sehe (, was geschieht)." });
                        surah20.VerseList.Add(new Verse { Number = 47, TextBosnian = "Idite k njemu i recite: 'Mi smo poslanici Gospodara tvoga, pusti sinove Israilove da idu s nama i nemoj ih mučiti! Donijeli smo ti znak od Gospodara tvoga, pa neka je selam - spas onome ko Uputu slijedi!", TextEnglish = "So go to him and say, 'Indeed, we are messengers of your Lord, so send with us the Children of Israel and do not torment them. We have come to you with a sign from your Lord. And peace[838] will be upon he who follows the guidance.", TextGerman = "So kommt denn zu ihm und sagt: ,Wir beide sind Gesandte deines Herrn. Lasse die Kinder Isra’ils mit uns gehen, und strafe sie nicht. Wir sind ja mit einem Zeichen von deinem Herrn zu dir gekommen. Und Friede sei auf demjenigen, der der Rechtleitung folgt." });
                        surah20.VerseList.Add(new Verse { Number = 48, TextBosnian = "Nama se objavljuje da će patnja sigurno zadesiti onoga koji niječe i koji se okreće.'", TextEnglish = "Indeed, it has been revealed to us that the punishment will be upon whoever denies and turns away.''", TextGerman = "Uns ist ja (als Offenbarung) eingegeben worden, daß die Strafe denjenigen überkommt, der (die Botschaft) für Lüge erklärt und sich abkehrt’." });
                        surah20.VerseList.Add(new Verse { Number = 49, TextBosnian = "Pa ko je Gospodar vaš, o Musa?', upita faraon.", TextEnglish = "[Pharaoh] said, 'So who is the Lord of you two, O Moses?'", TextGerman = "Er sagte: „Wer ist denn euer beider Herr, o Musa?" });
                        surah20.VerseList.Add(new Verse { Number = 50, TextBosnian = "Gospodar naš je Onaj Koji je svemu dao oblik a zatim ga uputio.'", TextEnglish = "He said, 'Our Lord is He who gave each thing its form and then guided [it].'", TextGerman = "Er sagte: „Unser Herr ist derjenige, der allem seine Natur gegeben und (es) hierauf rechtgeleitet hat." });
                        surah20.VerseList.Add(new Verse { Number = 51, TextBosnian = "A što je s narodima davnašnjim?', upita on.", TextEnglish = "[Pharaoh] said, 'Then what is the case of the former generations?'", TextGerman = "Er sagte: „Wie steht es denn mit den früheren Geschlechtern?" });
                        surah20.VerseList.Add(new Verse { Number = 52, TextBosnian = "Znanje o njima kod Gospodara je moga, u Knjizi!', reče Musa, 'Gospodaru mome ništa skriveno nije i On ne zaboravlja.'", TextEnglish = "[Moses] said, 'The knowledge thereof is with my Lord in a record. My Lord neither errs nor forgets.'", TextGerman = "Er sagte: „Das Wissen um sie ist bei meinem Herrn in einem Buch. Mein Herr irrt nicht, noch vergißt Er." });
                        surah20.VerseList.Add(new Verse { Number = 53, TextBosnian = "Onaj Koji je za vas Zemlju posteljom učinio i po njoj vam prolaze utro, i On spušta s neba kišu, pa Mi dajemo da uz njenu pomoć u parovima niče bilje raznovrsno.", TextEnglish = "[It is He] who has made for you the earth as a bed [spread out] and inserted therein for you roadways and sent down from the sky, rain and produced thereby categories of various plants.", TextGerman = "(Er ist es,) Der euch die Erde zu einer Lagerstatt gemacht und für euch auf ihr Wege sich hinziehen und vom Himmel Wasser herabkommen läßt, womit Wir dann Arten verschiedener Gewächse hervorbringen." });
                        surah20.VerseList.Add(new Verse { Number = 54, TextBosnian = "Jedite i napasajte stoku svoju! Tu su znakovi za one koji pameti imaju.", TextEnglish = "Eat [therefrom] and pasture your livestock. Indeed in that are signs for those of intelligence.", TextGerman = "Eßt und weidet euer Vieh. Darin sind wahrlich Zeichen für Leute von Verstand." });
                        surah20.VerseList.Add(new Verse { Number = 55, TextBosnian = "Od zemlje vas stvaramo i u nju vas vraćamo i iz nje ćemo vas i drugi put izvesti.", TextEnglish = "From it [i.e., the earth] We created you, and into it We will return you, and from it We will extract you another time.", TextGerman = "Aus ihr haben Wir euch erschaffen, und in sie bringen Wir euch zurück, und aus ihr bringen Wir euch ein anderes Mal hervor." });
                        surah20.VerseList.Add(new Verse { Number = 56, TextBosnian = "I Mi smo faraonu sve znakove Naše pokazali, ali je on ipak porekao i da povjeruje odbio.", TextEnglish = "And We certainly showed him [i.e., Pharaoh] Our signs - all of them - but he denied and refused.", TextGerman = "Und Wir zeigten ihm ja Unsere Zeichen alle, aber er erklärte (sie) für Lüge und weigerte sich." });
                        surah20.VerseList.Add(new Verse { Number = 57, TextBosnian = "Zar si došao da nas pomoću vradžbine svoje iz zemlje naše izvedeš, o Musa!', upitao je.", TextEnglish = "He said, 'Have you come to us to drive us out of our land with your magic, O Moses?", TextGerman = "Er sagte: „Bist du zu uns gekommen, um uns aus unserem Land mit deiner Zauberei zu vertreiben, o Musa?" });
                        surah20.VerseList.Add(new Verse { Number = 58, TextBosnian = "I mi ćemo tebi vradžbinu sličnu ovoj, doista, pripremiti! Zakaži nam mjesto i vrijeme susreta koji nećemo prekršiti ni mi, a ni ti, i neka to bude na mjestu vidnom!'", TextEnglish = "Then we will surely bring you magic like it, so make between us and you an appointment, which we will not fail to keep and neither will you, in a place assigned.'[839]", TextGerman = "Aber wir werden dir ganz gewiß Zauberei gleicher Art bringen. So setze zwischen uns und dir eine Verabredung(szeit) fest, die weder wir noch du verfehlen werden, an einem Ort, der (uns allen) gleichermaßen recht ist." });
                        surah20.VerseList.Add(new Verse { Number = 59, TextBosnian = "Neka mjesto i vrijeme susreta bude na dan gizdanja?' reče Musa, 'i nek se narod izjutra sakupi.'", TextEnglish = "[Moses] said, 'Your appointment is on the day of the festival when the people assemble at mid-morning.'[840]", TextGerman = "Er sagte: „Eure Verabredung(szeit) soll der Tag des Schmuckfestes sein, und die Menschen sollen am hellen Morgen versammelt werden." });
                        surah20.VerseList.Add(new Verse { Number = 60, TextBosnian = "I faraon ode, sakupi čarobnjake svoje i poslije dođe.", TextEnglish = "So Pharaoh went away, put together his plan, and then came [to Moses].", TextGerman = "Und so kehrte sich Fir’aun ab, und dann nahm er seine ganze List zusammen. Hierauf kam er." });
                        surah20.VerseList.Add(new Verse { Number = 61, TextBosnian = "Teško vama!', reče Musa. 'Ne iznosite laži o Allahu, pa da vas On kaznom uništi, a sigurno neće uspjeti onaj koji laži iznosi.'", TextEnglish = "Moses said to them [i.e., the magicians summoned by Pharaoh], 'Woe to you! Do not invent a lie against Allāh or He will exterminate you with a punishment; and he has failed who invents [such falsehood].'", TextGerman = "Musa sagte zu ihnen: „Wehe euch! Ersinnt gegen Allah keine Lüge, sonst vertilgt Er euch durch eine Strafe. Enttäuscht wird ja, wer Lügen ersinnt." });
                        surah20.VerseList.Add(new Verse { Number = 62, TextBosnian = "I oni se, o tome šta im je činiti, stadoše između sebe raspravljati, a tiho su šaptali.", TextEnglish = "So they disputed over their affair among themselves and concealed their private conversation.", TextGerman = "Da stritten sie untereinander über ihre Angelegenheit und führten insgeheim vertrauliche Gespräche." });
                        surah20.VerseList.Add(new Verse { Number = 63, TextBosnian = "Ova dvojica su čarobnjaci'', rekoše jedni drugima, 'hoće da vas vradžbinama svojim iz zemlje vaše izvedu i da unište vaš pravac najčasniji?'", TextEnglish = "They said, 'Indeed, these are two magicians who want to drive you out of your land with their magic and do away with your most exemplary way [i.e., religion or tradition].", TextGerman = "Sie sagten: „Diese beiden sind wahrlich nur Zauberer, die euch aus eurem Land mit ihrem Zauber vertreiben und eure vorbildliche Lebensweise beseitigen wollen." });
                        surah20.VerseList.Add(new Verse { Number = 64, TextBosnian = "zato lukavstvo svoje pametno pripremite, a onda u red stanite. Ko danas pobijedi, sigurno će postići što želi.'", TextEnglish = "So resolve upon your plan and then come [forward] in line. And he has succeeded today who overcomes.'", TextGerman = "So einigt euch auf eure List, hierauf kommt in Reihen. Und wohl ergehen wird es ja heute demjenigen, der die Oberhand gewinnt." });
                        surah20.VerseList.Add(new Verse { Number = 65, TextBosnian = "O Musa', rekoše oni, 'hoćeš li ti ili ćemo najprije mi baciti?'", TextEnglish = "They said, 'O Moses, either you throw or we will be the first to throw.'", TextGerman = "Sie sagten: „O Musa, entweder wirfst du, oder wir werden es sein, die zuerst werfen." });
                        surah20.VerseList.Add(new Verse { Number = 66, TextBosnian = "Bacite vi!', reče on, i odjednom mu se pričini da se konopi njihovi i štapovi njihovi, zbog vradžbine njihove, kreću.", TextEnglish = "He said, 'Rather, you throw.' And suddenly their ropes and staffs seemed to him from their magic that they were moving [like snakes].", TextGerman = "Er sagte: „Nein! Vielmehr werft ihr (zuerst). Und sogleich kamen ihm ihre Stricke und Stöcke durch ihre Zauberei so vor, als ob sie sich rasch bewegten." });
                        surah20.VerseList.Add(new Verse { Number = 67, TextBosnian = "I Musa u sebi osjeti zebnju.", TextEnglish = "And he sensed within himself apprehension, did Moses.", TextGerman = "Und er, Musa, empfand Furcht in seiner Seele." });
                        surah20.VerseList.Add(new Verse { Number = 68, TextBosnian = "Ne boj se!', rekosmo Mi, 'ti ćeš, doista, pobijediti!", TextEnglish = "We [i.e., Allāh] said, 'Fear not. Indeed, it is you who are superior.", TextGerman = "Wir sagten: „Fürchte dich nicht; du, ja gewiß du, wirst die Oberhand gewinnen." });
                        surah20.VerseList.Add(new Verse { Number = 69, TextBosnian = "Samo baci to što ti je u desnoj ruci, progutat će ono što su oni napravili, jer je ono što su oni napravili samo varka čarobnjaka, a čarobnjak neće, ma gdje došao, uspjeti.'", TextEnglish = "And throw what is in your right hand; it will swallow up what they have crafted. What they have crafted is but the trick of a magician, and the magician will not succeed wherever he is.'", TextGerman = "Wirf hin, was in deiner Rechten ist, so verschlingt es das, was sie gemacht haben. Was sie gemacht haben, ist nur die List eines Zauberers, und dem Zauberer wird es nicht wohl ergehen, wohin er auch kommen mag ." });
                        surah20.VerseList.Add(new Verse { Number = 70, TextBosnian = "I čarobnjaci se baciše licem na tlo govoreći: 'Mi vjerujemo u Harunova i Musaova Gospodara!'", TextEnglish = "So the magicians fell down in prostration.[841] They said, 'We have believed in the Lord of Aaron and Moses.'", TextGerman = "Da warfen sich die Zauberer ehrerbietig nieder. Sie sagten: „Wir glauben an den Herrn Haruns und Musas." });
                        surah20.VerseList.Add(new Verse { Number = 71, TextBosnian = "Vi ste mu povjerovali', viknu faraon, 'prije nego što sam vam ja dopustio! On je učitelj vaš, on vas je vradžbini naučio i ja ću vam, zacijelo, unakrst ruke i noge vaše odsjeći i po stablima palmi vas razapeti i sigurno ćete znati ko je od nas u mučenju strašniji i ustrajniji.'", TextEnglish = "[Pharaoh] said, 'You believed him [i.e., Moses] before I gave you permission. Indeed, he is your leader who has taught you magic. So I will surely cut off your hands and your feet on opposite sides, and I will crucify you on the trunks of palm trees, and you will surely know which of us is more severe in [giving] punishment and more enduring.'", TextGerman = "Er (Fir’aun) sagte: „Ihr glaubt an ihn, bevor ich es euch erlaube? Er ist wahrlich euer Ältester, der euch die Zauberei gelehrt hat. So werde ich ganz gewiß eure Hände und eure Füße wechselseitig abhacken und euch ganz gewiß an Palmstämmen kreuzigen (lassen). Und ihr werdet ganz gewiß erfahren, wer von uns strenger im Strafen und beständiger ist." });
                        surah20.VerseList.Add(new Verse { Number = 72, TextBosnian = "Mi nećemo tebe staviti iznad jasnih dokaza koji su nam došli i iznad Onoga Koji nas je stvorio', odgovoriše oni, 'pa čini što hoćeš, to možeš učiniti samo u životu dunjalučkom.", TextEnglish = "They said, 'Never will we prefer you over what has come to us of clear proofs and [over] He who created us.[842] So decree whatever you are to decree. You can only decree for this worldly life.", TextGerman = "Sie sagten: „Wir werden dich nicht dem vorziehen, was an klaren Beweisen zu uns gekommen ist, und (vor) Demjenigen, Der uns erschaffen hat. So entscheide, was du entscheiden magst; du entscheidest nur über dieses irdische Leben." });
                        surah20.VerseList.Add(new Verse { Number = 73, TextBosnian = "Mi vjerujemo u Gospodara našeg da bi nam grijehe naše oprostio i vradžbine na koje si nas ti primorao. A Allah je bolji i trajnije nagrađuje.'", TextEnglish = "Indeed, we have believed in our Lord that He may forgive us our sins and what you compelled us [to do] of magic. And Allāh is better and more enduring.'[843]", TextGerman = "Wir glauben an unseren Herrn, damit Er uns unsere Verfehlungen vergebe und (auch) die Zauberei, zu der du uns gezwungen hast. Allah ist besser und be ständiger." });
                        surah20.VerseList.Add(new Verse { Number = 74, TextBosnian = "Zaista, onoga koji pred Gospodara svoga kao prestupnik dođe - čeka Džehennem; u njemu neće ni umrijeti ni živjeti;", TextEnglish = "Indeed, whoever comes to his Lord as a criminal - indeed, for him is Hell; he will neither die therein nor live.", TextGerman = "Gewiß, wer zu seinem Herrn als Übeltäter kommt, für den gibt es die Hölle; darin wird er weder sterben noch leben." });
                        surah20.VerseList.Add(new Verse { Number = 75, TextBosnian = "a one koji pred Njega dođu kao vjernici, a koji su dobra djela činili, njih čekaju visoki stepeni,", TextEnglish = "But whoever comes to Him as a believer having done righteous deeds - for those will be the highest degrees [in position]:", TextGerman = "Wer zu Ihm als Gläubiger kommt, der rechtschaffene Werke getan hat, für jene gibt es die höchsten Rangstufen," });
                        surah20.VerseList.Add(new Verse { Number = 76, TextBosnian = "vrtovi Adna kroz koje rijeke teku, u njima će oni vječno ostati, i to će biti nagrada za one koji se budu očistili.", TextEnglish = "Gardens of perpetual residence beneath which rivers flow, wherein they abide eternally. And that is the reward of one who purifies himself.[844]", TextGerman = "die Gärten Edens, durcheilt von Bächen; ewig darin zu bleiben. Das ist der Lohn desjenigen, der sich läutert." });
                        surah20.VerseList.Add(new Verse { Number = 77, TextBosnian = "I Mi objavismo Musau: 'Noću izvedi robove Moje i pronađi im suhi put kroz more, ne strahujući da će te oni stići i ne bojeći se da ćeš se utopiti.'", TextEnglish = "And We had inspired to Moses, 'Travel by night with My servants and strike for them a dry path through the sea; you will not fear being overtaken [by Pharaoh] nor be afraid [of drowning].'", TextGerman = "Und Wir gaben Musa ja (als Offenbarung) ein: „Zieh bei Nacht mit Meinen Dienern fort und schlage ihnen einen trockenen Weg durch das Meer; du befürchtest weder, eingeholt zu werden, noch hast du Angst’." });
                        surah20.VerseList.Add(new Verse { Number = 78, TextBosnian = "A faraon je za njima s vojskama svojim krenuo, pa se na njih more sručilo;", TextEnglish = "So Pharaoh pursued them with his soldiers, and there covered them from the sea that which covered them,[845]", TextGerman = "Da verfolgte sie Fir’aun mit seinen Heerscharen, und es überdeckte sie vom Meer, was sie überdeckte." });
                        surah20.VerseList.Add(new Verse { Number = 79, TextBosnian = "I faraon je narod svoj u zabludu doveo, a nije na Put pravi izveo.", TextEnglish = "And Pharaoh led his people astray and did not guide [them].", TextGerman = "Fir’aun hatte sein Volk in die Irre geführt und nicht rechtgeleitet." });
                        surah20.VerseList.Add(new Verse { Number = 80, TextBosnian = "O sinovi Israilovi, Mi smo vas od neprijatelja vašeg izbavili, i na desnoj strani Tura rok vam zakazali, i manu i prepelice vam slali.", TextEnglish = "O Children of Israel, We delivered you from your enemy, and We made an appointment with you[846] at the right side of the mount, and We sent down to you manna and quails,", TextGerman = "O Kinder Isra’ils, Wir retteten euch ja vor eurem Feind, verabredeten Uns mit euch auf der rechten Seite des Berges und sandten das Manna und die Wachteln auf euch hinab:" });
                        surah20.VerseList.Add(new Verse { Number = 81, TextBosnian = "Jedite ukusna jela kojima vas opskrbljujemo i ne budite u tome obijesni da vas ne bi snašla srdžba Moja; a koga snađe srdžba Moja - nastradao je!", TextEnglish = "[Saying], 'Eat from the good things with which We have provided you and do not transgress [or oppress others] therein, lest My anger should descend upon you. And he upon whom My anger descends has certainly fallen [i.e., perished].'", TextGerman = "„Eßt von den guten Dingen, mit denen Wir euch versorgt haben, und lehnt euch dabei nicht (durch Undankbarkeit) auf, sonst bricht Mein Zorn über euch herein; denn derjenige, über den Mein Zorn hereinbricht, wird sicherlich stürzen." });
                        surah20.VerseList.Add(new Verse { Number = 82, TextBosnian = "Ja ću sigurno oprostiti onome ko se pokaje i uzvjeruje i dobra djela čini, i koji zatim na Pravom putu ustraje.'", TextEnglish = "But indeed, I am the Perpetual Forgiver of whoever repents and believes and does righteousness and then continues in guidance.", TextGerman = "Und Ich bin wahrlich Allvergebend für denjenigen, der bereut und glaubt und rechtschaffen handelt und sich hierauf rechtleiten läßt." });
                        surah20.VerseList.Add(new Verse { Number = 83, TextBosnian = "A zašto si prije naroda svoga požurio, o Musa?'", TextEnglish = "[Allāh said], 'And what made you hasten from your people, O Moses?'", TextGerman = "„Und was hat dich veranlaßt, von deinem Volk fortzueilen, o Musa?" });
                        surah20.VerseList.Add(new Verse { Number = 84, TextBosnian = "Evo idu za mnom', odgovori on, 'a požurio sam k Tebi, Gospodaru moj, da budeš zadovoljan.'", TextEnglish = "He said, 'They are close upon my tracks, and I hastened to You, my Lord, that You be pleased.'", TextGerman = "Er sagte: „Sie folgen mir doch auf der Spur. Und ich bin zu Dir geeilt, mein Herr, damit Du (mit mir) zufrieden seiest." });
                        surah20.VerseList.Add(new Verse { Number = 85, TextBosnian = "Mi smo narod tvoj, poslije tvog odlaska, u iskušenje stavili', reče On, 'i njih je zaveo Samirija.'", TextEnglish = "[Allāh] said, 'But indeed, We have tried your people after you [departed], and the Sāmirī[847] has led them astray.'", TextGerman = "Er sagte: „Wir haben dein Volk der Versuchung ausgesetzt, nachdem du (weggegangen) warst, und der Samiri hat sie in die Irre geführt." });
                        surah20.VerseList.Add(new Verse { Number = 86, TextBosnian = "I Musa se narodu svome vrati srdit i žalostan. 'O narode moj', reče, 'zar vam Gospodar vaš nije dao lijepo obećanje? Zar vam se vrijeme oduljilo, ili hoćete da vas stigne srdžba Gospodara vašeg, pa se zato niste držali obećanja koje ste mi dali!'", TextEnglish = "So Moses returned to his people, angry and grieved.[848] He said, 'O my people, did your Lord not make you a good promise?[849] Then, was the time [of its fulfillment] too long for you, or did you wish that wrath from your Lord descend upon you, so you broke your promise [of obedience] to me?'", TextGerman = "Da kam Musa zu seinem Volk zornig und bekümmert zurück. Er sagte: „O mein Volk, hat euch euer Herr nicht ein schönes Versprechen gegeben? Dauerte es euch mit dem Bund zu lange, oder wolltet ihr, daß Zorn von eurem Herrn über euch hereinbricht, so daß ihr die Vereinbarung mit mir gebrochen habt?" });
                        surah20.VerseList.Add(new Verse { Number = 87, TextBosnian = "Nismo od svoje volje prekršili obećanje koje smo ti dali', odgovoriše. 'Bili smo natovareni teretima, nakitom faraonova naroda, pa smo to pobacali.' A to je uradio i Samirija,", TextEnglish = "They said, 'We did not break our promise to you by our will, but we were made to carry burdens from the ornaments of the people [of Pharaoh], so we threw them [into the fire], and thus did the Sāmirī throw.'", TextGerman = "Sie sagten: „Wir haben die Vereinbarung mit dir nicht aus unserem (eigenen) Willen gebrochen, sondern wir trugen (ganze) Lasten von den Schmucksachen des Volkes, und dann haben wir sie geworfen, und ebenso hat der Samiri (welche) hineingelegt." });
                        surah20.VerseList.Add(new Verse { Number = 88, TextBosnian = "pa im izlio tele koje kao da je mukalo, i oni su onda rekli: 'Ovo je vaš bog i Musaov bog, a on ga je zaboravio.'", TextEnglish = "And he extracted for them [the statue of] a calf which had a lowing sound, and they said, 'This is your god and the god of Moses, but he forgot.'", TextGerman = "So brachte er ihnen ein Kalb hervor als Leib, das blökte. Sie sagten: ,Das ist euer Gott und der Gott Musas, aber er hat (es) vergessen’." });
                        surah20.VerseList.Add(new Verse { Number = 89, TextBosnian = "Zar oni nisu vidjeli da im ono ni riječi ne odgovara i da od njih ne može nikakvu nevolju otkloniti niti im može ikakvu korist pribaviti?", TextEnglish = "Did they not see that it could not return to them any speech [i.e., response] and that it did not possess for them any harm or benefit?", TextGerman = "Sehen sie denn nicht, daß er ihnen kein Wort erwidert und ihnen weder Schaden noch Nutzen zu bringen vermag?" });
                        surah20.VerseList.Add(new Verse { Number = 90, TextBosnian = "A njima je Harun još prije govorio: 'O narode moj, vi ste njime samo u iskušenje dovedeni; Gospodar vaš je Svemilosni, zato slijedite mene i slušajte naređenje moje.'", TextEnglish = "And Aaron had already told them before [the return of Moses], 'O my people, you are only being tested by it, and indeed, your Lord is the Most Merciful, so follow me and obey my order.'", TextGerman = "Harun hatte ihnen ja bereits zuvor gesagt: „O mein Volk, ihr seid damit nur der Versuchung ausgesetzt worden. Gewiß, euer Herr ist der Allerbarmer; so folgt mir und gehorcht meinem Befehl." });
                        surah20.VerseList.Add(new Verse { Number = 91, TextBosnian = "Mi ćemo mu u obožavanju privrženi biti sve dok nam se ne vrati Musa', rekoše oni.", TextEnglish = "They said, 'We will never cease being devoted to it [i.e., the calf] until Moses returns to us.'", TextGerman = "Sie sagten: „Wir werden nicht davon ablassen, uns seiner Andacht hinzugeben, bis Musa zu uns zurückkehrt." });
                        surah20.VerseList.Add(new Verse { Number = 92, TextBosnian = "O Harune!', povika Musa. 'Šta te je spriječilo, kad si ih vidio da su zalutali,", TextEnglish = "[Moses] said, 'O Aaron, what prevented you, when you saw them going astray,", TextGerman = "Er (Musa) sagte: „O Harun, was hat dich, als du sie irregehen sahst, davon abgehalten," });
                        surah20.VerseList.Add(new Verse { Number = 93, TextBosnian = "pa za mnom nisi pošao? Zašto nisi naređenje moje poslušao?'", TextEnglish = "From following me? Then have you disobeyed my order?'", TextGerman = "mir zu folgen? Hast du dich denn meinem Befehl widersetzt?" });
                        surah20.VerseList.Add(new Verse { Number = 94, TextBosnian = "O sine moje majke', reče Harun, 'ne hvataj me za bradu i za kosu! Ja sam se plašio da ti ne rekneš: 'Razdor si među sinovima Israilovim posijao i nisi postupio onako kako sam ti rekao.'", TextEnglish = "[Aaron] said, 'O son of my mother, do not seize [me] by my beard or by my head. Indeed, I feared that you would say, 'You caused division among the Children of Israel, and you did not observe [or await] my word.''", TextGerman = "Er sagte: „O Sohn meiner Mutter, pack mich nicht am Bart und nicht am Kopf. Ich fürchtete, du würdest sagen: ,Du hast unter den Kindern Isra’ils Zwietracht gestiftet und mein Wort nicht beachtet’." });
                        surah20.VerseList.Add(new Verse { Number = 95, TextBosnian = "A šta si ti to htio, o Samirija?', upita Musa.", TextEnglish = "[Moses] said, 'And what is your case, O Sāmirī?'", TextGerman = "Er sagte: „Doch was ist mit dir, o Samiri?" });
                        surah20.VerseList.Add(new Verse { Number = 96, TextBosnian = "Ja sam vidio ono što oni nisu vidjeli', odgovori on, 'pa sam šaku zemlje sa traga Izaslanikova uzeo i to bacio, i eto, tako mi to uljepša moja duša.'", TextEnglish = "He said, 'I saw what they did not see, so I took a handful [of dust] from the track of the messenger[850] and threw it,[851] and thus did my soul entice me.'", TextGerman = "Er sagte: „Ich gewahrte, was sie nicht gewahrten, und so faßte ich eine Handvoll (Erde) von der Spur des Gesandten und warf sie dann hin (ins Feuer). So habe ich es mir selbst eingeredet." });
                        surah20.VerseList.Add(new Verse { Number = 97, TextBosnian = "E onda se gubi!', reče Musa, 'čitavog svog života ćeš govoriti: 'Nema doticanja!', a čeka te još i Čas određeni koji te neće mimoići. Pogledaj samo ovoga tvog boga kojem si u obožavanju privržen bio: mi ćemo ga sigurno spaliti i po moru mu prah rasuti.", TextEnglish = "[Moses] said, 'Then go. And indeed, it is [decreed] for you in [this] life to say, 'No contact.'[852] And indeed, you have an appointment [in the Hereafter] you will not fail to keep. And look at your 'god' to which you remained devoted. We will surely burn it and blow it [i.e., its ashes] into the sea with a blast.", TextGerman = "Er sagte:„Geh weg! Es ist dir im Leben beschieden, zu sagen: ,Berührt mich nicht! Und du hast eine Verabredung, die dir nicht gebrochen wird. Und schau auf deinen Gott, dessen Andacht du dich dauernd hingegeben hast. Wir werden ihn ganz gewiß verbrennen, und hierauf werden wir ihn ganz gewiß in das große Gewässer streuen." });
                        surah20.VerseList.Add(new Verse { Number = 98, TextBosnian = "Vaš Bog je, Allah, drugog boga osim Njega nema! On znanjem sve obuhvata.'", TextEnglish = "Your god is only Allāh, except for whom there is no deity. He has encompassed all things in knowledge.'", TextGerman = "Euer Gott ist allein Allah, außer Dem es keinen Gott gibt. Er umfaßt alles mit (Seinem) Wissen." });
                        surah20.VerseList.Add(new Verse { Number = 99, TextBosnian = "I tako, eto, kazujemo ti neke vijesti o onome što je već prošlo i dali smo ti od Nas Opomenu.", TextEnglish = "Thus, [O Muḥammad], We relate to you from the news of what has preceded. And We have certainly given you from Us a message [i.e., the Qur’ān].", TextGerman = "Auf diese Weise erzählen Wir dir (einiges) von den Berichten dessen, was früher geschah. Und Wir haben dir ja von Uns aus eine Ermahnung gegeben." });
                        surah20.VerseList.Add(new Verse { Number = 100, TextBosnian = "Ko se od nje okrene, na Kijametskom danu će doista teško breme ponijeti;", TextEnglish = "Whoever turns away from it - then indeed, he will bear on the Day of Resurrection a burden [i.e., great sin],", TextGerman = "Wer sich davon abwendet, der wird am Tag der Auferstehung eine (drückende) Last tragen," });
                        surah20.VerseList.Add(new Verse { Number = 101, TextBosnian = "vječno će tako ostati, a jeziv tovar će im na Kijametskom danu biti.", TextEnglish = "[Abiding] eternally therein,[853] and evil it is for them on the Day of Resurrection as a load -", TextGerman = "ewig darin zu bleiben. Wie böse wird (dies) für sie am Tag der Auferstehung als Last sein!" });
                        surah20.VerseList.Add(new Verse { Number = 102, TextBosnian = "Na Dan kada će se u rog puhnuti - Taj dan ćemo prestupnike modre sakupiti,", TextEnglish = "The Day the Horn will be blown. And We will gather the criminals, that Day, blue-eyed.[854]", TextGerman = "Am Tag, da ins Horn geblasen wird und Wir die Übeltäter als Leute mit blauen Augen versammeln." });
                        surah20.VerseList.Add(new Verse { Number = 103, TextBosnian = "i jedan drugom će tiho govoriti: 'Niste ostali više od deset dana.'", TextEnglish = "They will murmur among themselves, 'You remained not but ten [days in the world].'", TextGerman = "Sie flüstern einander zu: „Ihr habt nur zehn (Nächte) verweilt." });
                        surah20.VerseList.Add(new Verse { Number = 104, TextBosnian = "Mi dobro znamo o čemu će oni govoriti kad najrazboritiji između njih rekne: 'Ostali ste samo dan jedan.'", TextEnglish = "We are most knowing of what they say when the best of them in manner [i.e., wisdom or speech] will say, 'You remained not but one day.'", TextGerman = "Wir wissen sehr wohl, was sie sagen, wenn der Vorbildlichste von ihnen in seinem Verhalten sagen wird: „Ihr habt nur einen Tag verweilt." });
                        surah20.VerseList.Add(new Verse { Number = 105, TextBosnian = "A pitaju te o planinama, pa ti reci: 'Gospodar moj će ih u prah pretvoriti i zasuti,", TextEnglish = "And they ask you about the mountains, so say, 'My Lord will blow them away with a blast.[855]", TextGerman = "Sie fragen dich nach den Bergen. Sag: Mein Herr wird sie wie Staub zerstreuen" });
                        surah20.VerseList.Add(new Verse { Number = 106, TextBosnian = "a mjesta na kojima su bile ravnom ledinom ostaviti,", TextEnglish = "And He will leave it [i.e., the earth] a level plain;", TextGerman = "und sie dann als leere Ebene zurücklassen," });
                        surah20.VerseList.Add(new Verse { Number = 107, TextBosnian = "ni udubina ni uzvisina na Zemlji nećeš vidjeti.", TextEnglish = "You will not see therein a depression or an elevation.'", TextGerman = "worauf du weder eine Vertiefung noch Erhebung siehst." });
                        surah20.VerseList.Add(new Verse { Number = 108, TextBosnian = "Taj dan će se oni glasniku odazvati, od koga odstupanja nema i pred Svemilosnim će se glasovi stišati i ti ćeš samo šapat čuti.", TextEnglish = "That Day, they [i.e., everyone] will follow [the call of] the Caller[856] [with] no deviation therefrom, and [all] voices will be stilled before the Most Merciful, so you will not hear except a whisper [of footsteps].", TextGerman = "An jenem Tag folgen sie dem Rufer, bei dem es nichts Krummes gibt; die Stimmen senken sich in Demut vor dem Allerbarmer, so daß du nichts hörst außer Flüstern." });
                        surah20.VerseList.Add(new Verse { Number = 109, TextBosnian = "Taj dan neće biti od koristi zauzimanje osim onoga kome Svemilosni dopusti i čijim riječima je zadovoljan.", TextEnglish = "That Day, no intercession will benefit except [that of] one to whom the Most Merciful has given permission and has accepted his word.", TextGerman = "An jenem Tag nützt die Fürsprache nicht, außer durch denjenigen, dem es der Allerbarmer erlaubt und mit dessen Worten Er zufrieden ist." });
                        surah20.VerseList.Add(new Verse { Number = 110, TextBosnian = "On zna šta su radili i šta ih čeka, a oni znanjem ne mogu Njega obuhvatiti.", TextEnglish = "He [i.e., Allāh] knows what is [presently] before them and what will be after them,[857] but they do not encompass it [i.e., what He knows] in knowledge.", TextGerman = "Er weiß, was vor ihnen und was hinter ihnen liegt, sie aber umfassen es nicht mit (ihrem) Wissen." });
                        surah20.VerseList.Add(new Verse { Number = 111, TextBosnian = "Lica će se Živom i Samoopstojećem Održavatelju poniziti, a onaj ko se zulumom bude natovario sigurno će na gubitku biti.", TextEnglish = "And [all] faces will be humbled before the Ever-Living, the Self-Sustaining.[858] And he will have failed who carries injustice.[859]", TextGerman = "Demütig werden sich die Gesichter vor dem Lebendigen und Beständigen senken. Und wirklich enttäuscht wird, wer (als Last begangenes) Unrecht trägt." });
                        surah20.VerseList.Add(new Verse { Number = 112, TextBosnian = "A onaj ko je dobra djela činio, a vjernik je bio, neće se zuluma ni zakidanja plašiti.", TextEnglish = "But he who does of righteous deeds while he is a believer - he will neither fear injustice nor deprivation.", TextGerman = "Wer aber etwas an rechtschaffenen Werken tut und dabei gläubig ist, der wird kein Unrecht und keine (Lohn)minderung befürchten." });
                        surah20.VerseList.Add(new Verse { Number = 113, TextBosnian = "I, eto, tako Mi Kur'an na arapskom jeziku objavljujemo i u njemu prijetnje izlažemo da bi se oni grijeha klonili ili da bi ih na poslušnost pobudio.", TextEnglish = "And thus We have sent it down as an Arabic Qur’ān[860] and have diversified therein the warnings that perhaps they will avoid [sin] or it would cause them remembrance.", TextGerman = "Und so haben Wir ihn als einen arabischen Qur’an hinabgesandt. Und Wir haben darin verschiedene Androhungen dargelegt, auf daß sie gottesfürchtig werden mögen oder er bei ihnen Gedenken (Allahs) hervorrufe." });
                        surah20.VerseList.Add(new Verse { Number = 114, TextBosnian = "Neka je uzvišen Allah, Vladar Istiniti! I ne žuri s čitanjem Kur'ana prije nego što ti se objavljivanje njegovo završi, i reci: 'Gospodaru moj, Ti znanje moje povećaj!'", TextEnglish = "So high [above all] is Allāh, the Sovereign,[861] the Truth.[862] And, [O Muḥammad], do not hasten with [recitation of] the Qur’ān before its revelation is completed to you, and say, 'My Lord, increase me in knowledge.'", TextGerman = "Erhaben ist Allah, der König, der Wahre! Und übereile dich nicht mit dem Qur’an, bevor dir seine Offenbarung vollständig eingegeben worden ist. Und sag: Mein Herr, lasse mich an Wissen zunehmen." });
                        surah20.VerseList.Add(new Verse { Number = 115, TextBosnian = "A Adema smo još prije obavezali, pa on zaboravi i ne nađosmo da je odlučan!", TextEnglish = "And We had already taken a promise from Adam before, but he forgot; and We found not in him determination.[863]", TextGerman = "Und Wir hatten bereits zuvor Adam eine Verpflichtung auferlegt. Aber er vergaß (sie), und Wir fanden bei ihm keine Entschlossenheit." });
                        surah20.VerseList.Add(new Verse { Number = 116, TextBosnian = "A kad smo melecima rekli: 'Sedždu Ademu učinite!', oni sedždu učiniše, samo Iblis nije htio.", TextEnglish = "And [mention] when We said to the angels, 'Prostrate to Adam,' and they prostrated, except Iblees;[864] he refused.", TextGerman = "Und als Wir zu den Engeln sagten: „Werft euch vor Adam nieder. Da warfen sie sich nieder, außer Iblis. Er weigerte sich." });
                        surah20.VerseList.Add(new Verse { Number = 117, TextBosnian = "O Ademe!', rekli smo, 'ovo je zaista neprijatelj tebi i tvojoj ženi, zato nikako ne dozvoli da vas on izvede iz Dženneta, pa da se onda mučiš.", TextEnglish = "So We said, 'O Adam, indeed this is an enemy to you and to your wife. Then let him not remove you from Paradise so you would suffer.", TextGerman = "Da sagten Wir: „O Adam, dieser (da) ist dir und deiner Gattin gewiß ein Feind. Daß er euch beide ja nicht aus dem (Paradies)garten vertreibt! Sonst wirst du unglücklich sein." });
                        surah20.VerseList.Add(new Verse { Number = 118, TextBosnian = "U njemu nećeš ni ogladnjeti ni go biti,", TextEnglish = "Indeed, it is [promised] for you not to be hungry therein or be unclothed.", TextGerman = "Gewiß, es ist dir gewährt, daß du darin weder hungerst noch nackt bist," });
                        surah20.VerseList.Add(new Verse { Number = 119, TextBosnian = "u njemu nećeš ni ožednjeti, ni žegu osjetiti.'", TextEnglish = "And indeed, you will not be thirsty therein or be hot from the sun.'", TextGerman = "und daß du darin weder dürstest noch Sonnenhitze erleidest." });
                        surah20.VerseList.Add(new Verse { Number = 120, TextBosnian = "Ali šejtan ga poče nagovarati i govoriti: 'O Ademe, hoćeš li da ti pokažem drvo besmrtnosti i carstvo koje neće nestati?'", TextEnglish = "Then Satan whispered to him; he said, 'O Adam, shall I direct you to the tree of eternity and possession that will not deteriorate?'", TextGerman = "Aber da flüsterte ihm der Satan ein und sagte: „O Adam, soll ich dich auf den Baum der Ewigkeit hinweisen und auf eine Herrschaft, die nicht vergeht?" });
                        surah20.VerseList.Add(new Verse { Number = 121, TextBosnian = "I njih dvoje pojedoše s njega i ukazaše im se stidna mjesta njihova pa počeše po njima lišće džennetsko stavljati, tako Adem nije Gospodara svoga poslušao, pa je zabasao.", TextEnglish = "And they [i.e., Adam and his wife] ate of it, and their private parts became apparent to them, and they began to fasten over themselves from the leaves of Paradise. And Adam disobeyed his Lord and erred.", TextGerman = "So aßen sie beide davon, und da zeigte sich ihnen ihre Blöße offenkundig, und sie begannen, Blätter des (Paradies)gartens auf sich zusammenzuheften. So widersetzte Adam sich seinem Herrn, und da fiel er in Verirrung." });
                        surah20.VerseList.Add(new Verse { Number = 122, TextBosnian = "Poslije ga je Gospodar njegov odabrao, pa mu oprostio i na Pravi put ga uputio.", TextEnglish = "Then his Lord chose him and turned to him in forgiveness and guided [him].", TextGerman = "Hierauf erwählte ihn sein Herr, und so wandte Er Sich ihm Reue annehmend zu und leitete (ihn) recht." });
                        surah20.VerseList.Add(new Verse { Number = 123, TextBosnian = "Izlazite iz njega svi', reče On, 'jedni drugima neprijatelji ćete biti! Od Mene će vam Uputa dolaziti, i onaj ko bude slijedio Uputu Moju neće zalutati i neće nesretan biti.", TextEnglish = "[Allāh] said, 'Descend from it [i.e., Paradise] - all, [your descendants] being enemies to one another. And if there should come to you guidance from Me - then whoever follows My guidance will neither go astray [in the world] nor suffer [in the Hereafter].", TextGerman = "Er sagte: „Geht alle fort von ihm. Einige von euch seien des anderen Feind. Doch wenn dann von Mir Rechtleitung zu euch kommt, dann wird derjenige, der Meiner Rechtleitung folgt, nicht irregehen und nicht unglücklich sein." });
                        surah20.VerseList.Add(new Verse { Number = 124, TextBosnian = "A onaj ko se okrene od Opomene Moje, taj će teškim životom živjeti i na Kijametskom danu slijepim ćemo ga oživiti.'", TextEnglish = "And whoever turns away from My remembrance - indeed, he will have a depressed [i.e., difficult] life, and We will gather [i.e., raise] him on the Day of Resurrection blind.'", TextGerman = "Wer sich aber von Meiner Ermahnung abwendet, der wird ein beengtes Leben führen, und Wir werden ihn am Tag der Auferstehung blind (zu den anderen) versammeln." });
                        surah20.VerseList.Add(new Verse { Number = 125, TextBosnian = "Gospodaru moj', reći će, 'zašto si me slijepa oživio kad sam vid imao?'", TextEnglish = "He will say, 'My Lord, why have you raised me blind while I was [once] seeing?'", TextGerman = "Er wird sagen: „Mein Herr, warum hast Du mich blind (zu den anderen) versammelt, wo ich doch sehen konnte?" });
                        surah20.VerseList.Add(new Verse { Number = 126, TextBosnian = "Evo zašto', reći će On. 'Ajeti i znakovi naši su ti dolazili, ali si ih zaboravljao, pa ćeš danas tako i ti biti zaboravljen.'", TextEnglish = "[Allāh] will say, 'Thus did Our signs come to you, and you forgot [i.e., disregarded] them; and thus will you this Day be forgotten.'", TextGerman = "Er sagt: „So sind auch zu dir Unsere Zeichen gekommen, und doch hast du sie vergessen. Ebenso wirst du heute vergessen." });
                        surah20.VerseList.Add(new Verse { Number = 127, TextBosnian = "I tako ćemo Mi kazniti sve one koji su pretjerivali i u ajete i znakove Gospodara svoga nisu vjerovali. A patnja na onom svijetu bit će, uistinu, bolnija i vječna.", TextEnglish = "And thus do We recompense he who transgressed and did not believe in the signs of his Lord. And the punishment of the Hereafter is more severe and more enduring.[865]", TextGerman = "So vergelten Wir demjenigen, der maßlos ist und nicht an die Zeichen seines Herrn glaubt. Die Strafe des Jenseits ist wahrlich strenger und nachhaltiger." });
                        surah20.VerseList.Add(new Verse { Number = 128, TextBosnian = "Zar njima nije poznato koliko smo naroda prije njih uništili, onih po čijim nastambama oni hode! Tu su, zaista, znakovi za ljude pametne.", TextEnglish = "Then, has it not become clear to them how many generations We destroyed before them as they walk among their dwellings? Indeed in that are signs for those of intelligence.", TextGerman = "Ist ihnen denn nicht deutlich geworden, wie viele Geschlechter Wir vor ihnen vernichtet haben, in deren Wohnorten sie (nun) umhergehen? Darin sind wahrlich Zeichen für Leute von Verstand." });
                        surah20.VerseList.Add(new Verse { Number = 129, TextBosnian = "I da nije prije izrečene riječi Gospodara tvoga, kazna bi već bila neminovna.", TextEnglish = "And if not for a word[866] that preceded from your Lord, it [i.e., punishment] would have been an obligation [due immediately],[867] and [if not for] a specified term [decreed].", TextGerman = "Wenn es nicht ein schon früher ergangenes Wort von deinem Herrn und eine festgesetzte Frist gäbe, wäre (die sofortige Strafe) fürwahr unabwendbar." });
                        surah20.VerseList.Add(new Verse { Number = 130, TextBosnian = "Zato, otrpi njihove riječi i Gospodara svoga slavi, hvaleći Ga, prije izlaska Sunca i prije zalaska njegova, i slavi Ga u noćnim časovima, i na krajevima dana, da bi bio zadovoljan.", TextEnglish = "So be patient over what they say and exalt [Allāh] with praise of your Lord before the rising of the sun and before its setting; and during periods of the night [exalt Him] and at the ends of the day, that you may be satisfied.", TextGerman = "So ertrage standhaft, was sie sagen, und lobpreise deinen Herrn vor dem Aufgang der Sonne und vor ihrem Untergang, und zu (verschiedenen) Stunden der Nacht preise (Ihn) und (ebenso) an den Enden des Tages, auf daß du zufrieden sein mögest." });
                        surah20.VerseList.Add(new Verse { Number = 131, TextBosnian = "I nikako ne gledaj dugo ljepote dunjalučkog života koje Mi kao užitak nekima od njih pružamo, da ih time na kušnju stavimo, a opskrba Gospodara tvoga bolja je i vječna.", TextEnglish = "And do not extend your eyes toward that by which We have given enjoyment to [some] categories of them, [its being but] the splendor of worldly life by which We test them. And the provision of your Lord is better and more enduring.", TextGerman = "Und richte nur nicht deine Augen auf das, was Wir manchen von ihnen paarweise als Nießbrauch gewähren - den Glanz des diesseitigen Lebens -, um sie darin der Versuchung auszusetzen. Die Versorgung deines Herrn ist besser und beständiger." });
                        surah20.VerseList.Add(new Verse { Number = 132, TextBosnian = "Naredi porodici svojoj da obavlja namaz i ustraj u tome! Mi ne tražimo od tebe opskrbu, Mi tebe opskrbljujemo! A samo za bogobojaznost lijep ishod sljeduje.", TextEnglish = "And enjoin prayer upon your family [and people] and be steadfast therein. We ask you not for provision; We provide for you, and the [best] outcome is for [those of] righteousness.", TextGerman = "Und befiehl deinen Angehörigen, das Gebet (zu verrichten), und sei beharrlich darin. Wir fordern keine Versorgung von dir; Wir versorgen dich. Und das (gute) Ende gehört der Gottesfurcht." });
                        surah20.VerseList.Add(new Verse { Number = 133, TextBosnian = "Nevjernici govore: 'Zašto nam ne donese kakav znak od Gospodara svoga?' A zar im ne dolazi objašnjenje o onome što ima u prijašnjim listovima?", TextEnglish = "And they say, 'Why does he not bring us a sign from his Lord?' Has there not come to them evidence of what was in the former scriptures?[868]", TextGerman = "Und sie sagen: „Wenn er uns doch ein Zeichen von seinem Herrn bringen würde! Ist nicht zu ihnen der klare Beweis dessen gekommen, was auf den früheren Blättern steht?" });
                        surah20.VerseList.Add(new Verse { Number = 134, TextBosnian = "A da smo ih kakvom kaznom prije njega uništili, sigurno bi rekli: 'Gospodaru naš, zašto nam nisi poslanika poslao, pa bismo riječi Tvoje slijedili prije nego što smo poniženi i osramoćeni postali.'", TextEnglish = "And if We had destroyed them with a punishment before him,[869] they would have said, 'Our Lord, why did You not send to us a messenger so we could have followed Your verses [i.e., teachings] before we were humiliated and disgraced?'", TextGerman = "Hätten Wir sie vor ihm durch eine Strafe vernichtet, hätten sie fürwahr gesagt: „Unser Herr, hättest Du doch einen Gesandten zu uns gesandt, so daß wir Deinen Zeichen hätten folgen können, bevor wir erniedrigt und in Schande gestürzt würden!" });
                        surah20.VerseList.Add(new Verse { Number = 135, TextBosnian = "Reci: 'Svako iščekuje, pa iščekujte i vi, a sigurno ćete saznati ko su bili sljedbenici ispravnog puta i ko je i sam za uputom tragao.'", TextEnglish = "Say, 'Each [of us] is waiting;[870] so wait. For you will know who are the companions of the sound path and who is guided.'", TextGerman = "Sag: Jeder wartet ab; so wartet auch (ihr) ab. Dann werdet ihr (noch) erfahren, wer die Leute des ebenen Weges sind und wer rechtgeleitet ist." });
                        result = surah20;
                        break;
                    #endregion

                    // 
                    #region 21 Al-Anbiya
                    case 21:
                        Surah surah21 = new Surah(); surah21.Name = Constants.Surah21Name; surah21.Number = 21; surah21.VerseList = new List<Verse>();
                        surah21.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah21.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ljudima se bliži čas polaganja računa, a oni u gafletu, okreću se.", TextEnglish = "[The time of] their account has approached for the people, while they are in heedlessness turning away.", TextGerman = "Nahegerückt ist den Menschen ihre Abrechnung, während sie sich in Unachtsamkeit abwenden." });
                        surah21.VerseList.Add(new Verse { Number = 2, TextBosnian = "I ne dođe im ništa novo od Opomene od Gospodara njihova čemu se, slušajući to, ne podsmjehuju", TextEnglish = "No mention [i.e., revelation] comes to them anew from their Lord except that they listen to it while they are at play", TextGerman = "Keine neuerlich offenbarte Ermahnung kommt von ihrem Herrn zu ihnen, ohne daß sie sie anhören, während sie spielen" });
                        surah21.VerseList.Add(new Verse { Number = 3, TextBosnian = "srca nehajnih. Oni koji su zulum počinili tajno se sašaptavaše: 'Da li je ovaj nešto drugo do čovjek kao i vi?! Zar ćete se prikloniti vradžbini, a vidite da to jest?!'", TextEnglish = "With their hearts distracted. And those who do wrong conceal their private conversation, [saying], 'Is this [Prophet] except a human being like you? So would you approach magic while you are aware [of it]?'", TextGerman = "und ihre Herzen zerstreut sind. Und sie führen insgeheim vertrauliche Gespräche -sie, die Unrecht tun: „Ist dieser etwas anderes als ein menschliches Wesen wie ihr? Wollt ihr denn sehend(en Auges) Zauberei begehen?" });
                        surah21.VerseList.Add(new Verse { Number = 4, TextBosnian = "Gospodar moj zna', reče on, 'šta se govori na nebu i na Zemlji; On je Svečujući i Sveznajući!'", TextEnglish = "He [the Prophet (ﷺ)] said, 'My Lord knows whatever is said throughout the heaven and earth, and He is the Hearing, the Knowing.'", TextGerman = "Sag: Mein Herr weiß (alles), was im Himmel und auf der Erde gesagt wird. Er ist der Allhörende und Allwissende." });
                        surah21.VerseList.Add(new Verse { Number = 5, TextBosnian = "Oni čak, govore: 'To su samo zbrkani snovi; on ga izmišlja; on je pjesnik; neka nam donese kakav znak kao što su i prijašnji poslanici s time slati!'", TextEnglish = "But they say, '[The revelation is but] a mixture of false dreams; rather, he has invented it; rather, he is a poet. So let him bring us a sign just as the previous [messengers] were sent [with miracles].'", TextGerman = "Aber nein! Vielmehr sagen sie: „(Das ist) ein Bündel von wirren Träumen. Nein! Vielmehr hat er ihn ersonnen. Nein! Vielmehr ist er ein Dichter. So soll er uns ein Zeichen bringen, so wie die Früheren gesandt wurden." });
                        surah21.VerseList.Add(new Verse { Number = 6, TextBosnian = "Nijedno naselje koje smo Mi prije njih uništili nije povjerovalo, pa zar će oni povjerovati?", TextEnglish = "Not a [single] city which We destroyed believed before them,[872] so will they believe?", TextGerman = "Vor ihnen hat keine Stadt geglaubt, die Wir vernichteten. Sollten gerade sie nun glauben?" });
                        surah21.VerseList.Add(new Verse { Number = 7, TextBosnian = "I prije tebe smo samo ljude - muškarce kao poslanike slali i objavljivali im, zato pitajte ljude od znanja, ako vi ne znate!", TextEnglish = "And We sent not before you, [O Muḥammad], except men to whom We revealed [the message], so ask the people of the message [i.e., former scriptures] if you do not know.", TextGerman = "Und Wir haben vor dir nur Männer gesandt, denen Wir (Offenbarungen) eingaben. So fragt die Leute der Ermahnung, wenn ihr (es) nicht wißt." });
                        surah21.VerseList.Add(new Verse { Number = 8, TextBosnian = "Mi ih nismo stvorili kao tijela koja žive bez hrane, niti su oni besmrtni bili.", TextEnglish = "And We did not make them [i.e., the prophets] forms not eating food,[873] nor were they immortal [on earth].", TextGerman = "Wir machten sie nicht zu Körpern, die keine Speise essen, und sie waren nicht unsterblich." });
                        surah21.VerseList.Add(new Verse { Number = 9, TextBosnian = "Poslije smo im obećanje ispunjavali, i njih, i one koje smo htjeli spasavali, a one koji su pretjerali, uništavali.", TextEnglish = "Then[874] We fulfilled for them the promise, and We saved them and whom We willed and destroyed the transgressors.", TextGerman = "Hierauf hielten Wir ihnen das Versprechen. Wir retteten sie und diejenigen, die Wir (erretten) wollen, und vernichteten die Maßlosen." });
                        surah21.VerseList.Add(new Verse { Number = 10, TextBosnian = "Mi vam Knjigu objavljujemo u kojoj vam je opomena, pa zašto ne razumijete?", TextEnglish = "We have certainly sent down to you a Book [i.e., the Qur’ān] in which is your mention.[875] Then will you not reason?", TextGerman = "Wir haben ja ein Buch zu euch hinabgesandt, in dem eure Ehre liegt . Begreift ihr denn nicht?" });
                        surah21.VerseList.Add(new Verse { Number = 11, TextBosnian = "A koliko smo samo uništili naselja koja su zulum činila i poslije kojih smo druge narode podigli!", TextEnglish = "And how many a city which was unjust[876] have We shattered and produced after it another people.", TextGerman = "Wie viele Städte haben Wir zerschmettert, die Unrecht taten, und haben nach ihnen ein anderes Volk entstehen lassen!" });
                        surah21.VerseList.Add(new Verse { Number = 12, TextBosnian = "I čim bi silu našu osjetili, iz njih bi se razbježali.", TextEnglish = "And when they [i.e., its inhabitants] perceived Our punishment, at once they fled from it.", TextGerman = "Als sie dann Unsere Gewalt spürten, liefen sie sogleich davor weg." });
                        surah21.VerseList.Add(new Verse { Number = 13, TextBosnian = "Ne bježite, vratite se uživanjima vašim i domovima vašim, možda ćete biti priupitani?!'", TextEnglish = "[Some angels said], 'Do not flee but return to where you were given luxury and to your homes - perhaps you will be questioned.'[877]", TextGerman = "„Lauft nicht weg, sondern kehrt zu dem euch verliehenen üppigen Leben und zu euren Wohnungen zurück, auf daß ihr befragt werden möget." });
                        surah21.VerseList.Add(new Verse { Number = 14, TextBosnian = "Teško nama', oni bi govorili, 'mi smo, zaista, zulumćari bili.'", TextEnglish = "They said, 'O woe to us! Indeed, we were wrongdoers.'", TextGerman = "Sie sagten: „O Wehe uns! Wir pflegten ja Unrecht zu tun." });
                        surah21.VerseList.Add(new Verse { Number = 15, TextBosnian = "I kukali bi tako sve dok ih ne bismo učinili, kao žito požnjeveno, nepomičnim.", TextEnglish = "And that declaration of theirs did not cease until We made them [as] a harvest [mowed down], extinguished [like a fire].", TextGerman = "So war dies stets ihr Ausruf, bis Wir sie (wie) abgemäht und ausgelöscht sein ließen." });
                        surah21.VerseList.Add(new Verse { Number = 16, TextBosnian = "Mi nismo stvorili nebo i Zemlju i ono što je između njih, igrajući se.", TextEnglish = "And We did not create the heaven and earth and that between them in play.", TextGerman = "Und Wir haben den Himmel und die Erde und was dazwischen ist, nicht zum Spiel erschaffen." });
                        surah21.VerseList.Add(new Verse { Number = 17, TextBosnian = "Da smo htjeli uzeti neku zabavu, uzeli bismo je od Nas, ali Mi to ne činimo.", TextEnglish = "Had We intended to take a diversion,[878] We could have taken it from [what is] with Us - if [indeed] We were to do so.", TextGerman = "Wenn Wir Uns eine Zerstreuung hätten nehmen wollen, dann hätten Wir sie wahrlich von Uns aus genommen - wenn Wir das (überhaupt) hätten tun wollen." });
                        surah21.VerseList.Add(new Verse { Number = 18, TextBosnian = "Međutim, Istinom suzbijamo laž, Istina je uguši i laži nestane; a teško vama zbog toga što Ga lažno opisujete.", TextEnglish = "Rather, We dash the truth upon falsehood, and it destroys it,[879] and thereupon it departs. And for you is destruction from that which you describe.[880]", TextGerman = "Aber nein! Wir schleudern die Wahrheit gegen das Falsche, und da zerschmettert sie ihm das Haupt, und sogleich geht es dahin. Und wehe euch wegen dessen, was ihr (da fälschlich) zuschreibt!" });
                        surah21.VerseList.Add(new Verse { Number = 19, TextBosnian = "Njegovo je ono što je na nebesima i na Zemlji! A oni koji su kod Njega ne zaziru da Mu budu pokorni, i ne zamaraju se,", TextEnglish = "To Him belongs whoever is in the heavens and the earth. And those near Him [i.e., the angels] are not prevented by arrogance from His worship, nor do they tire.", TextGerman = "Ihm gehört, wer in den Himmeln und auf der Erde ist. Und diejenigen, die bei Ihm sind, sind weder zu hochmütig, Ihm zu dienen, noch werden sie (dessen) müde." });
                        surah21.VerseList.Add(new Verse { Number = 20, TextBosnian = "hvale Ga noću i danju, ne malaksavaju.", TextEnglish = "They exalt [Him] night and day [and] do not slacken.", TextGerman = "Sie preisen (Ihn) Nacht und Tag und lassen (darin) nicht nach." });
                        surah21.VerseList.Add(new Verse { Number = 21, TextBosnian = "Zar božanstva koja oni od zemlje pripravljaju mogu mrtve oživiti?!", TextEnglish = "Or have they [i.e., men] taken for themselves gods from the earth who resurrect [the dead]?", TextGerman = "Oder haben sie sich Götter aus der Erde genommen, die (die Toten) auferstehen lassen (können)?" });
                        surah21.VerseList.Add(new Verse { Number = 22, TextBosnian = "A da na njima - Zemlji i nebesima ima drugih istinskih božanstava osim Allaha, i na jednom i na drugom reda bi nestalo. Pa neka je uzvišen Allah, Gospodar Arša, od onoga što Mu u obožavanju pridružuju!", TextEnglish = "Had there been within them [i.e., the heavens and earth] gods besides Allāh, they both would have been ruined. So exalted is Allāh, Lord of the Throne, above what they describe.", TextGerman = "Wenn es in ihnen beiden andere Götter als Allah gäbe, gerieten sie (beide) wahrlich ins Verderben. Preis sei Allah, dem Herrn des Thrones! (Erhaben ist Er) über das, was sie (Ihm) zuschreiben." });
                        surah21.VerseList.Add(new Verse { Number = 23, TextBosnian = "On neće biti pitan za ono što radi, a oni će biti pitani.", TextEnglish = "He is not questioned about what He does, but they will be questioned.", TextGerman = "Er wird nicht befragt nach dem, was Er tut; sie aber werden befragt." });
                        surah21.VerseList.Add(new Verse { Number = 24, TextBosnian = "Zar da pored Njega oni uzimaju bogove?! Reci: 'Dajte vaš dokaz! Ovo je opomena za one koji su sa mnom, a i opomena za one koji su bili prije mene.' Međutim, većina njih ne zna Istinu, pa se okreću.", TextEnglish = "Or have they taken gods besides Him? Say, [O Muḥammad], 'Produce your proof. This [Qur’ān] is the message for those with me and the message of those before me.'[881] But most of them do not know the truth, so they are turning away.", TextGerman = "Oder haben sie sich außer Ihm Götter genommen? Sag: Bringt euren Beweis vor! Das ist die Ermahnung derjenigen, die mit mir sind, und die Ermahnung derer, die vor mir waren. Aber nein! Die meisten von ihnen kennen die Wahrheit nicht; so wenden sie sich ab." });
                        surah21.VerseList.Add(new Verse { Number = 25, TextBosnian = "Prije tebe nijednog poslanika nismo poslali, a da mu nismo objavili: 'Nema istinskog boga osim Mene, zato Meni ibadet činite!'", TextEnglish = "And We sent not before you any messenger except We revealed to him that, 'There is no deity except Me, so worship Me.'", TextGerman = "Und Wir haben vor dir keinen Gesandten gesandt, dem Wir nicht (die Weisung) eingegeben hätten: „Es gibt keinen Gott außer Mir, so dient Mir!" });
                        surah21.VerseList.Add(new Verse { Number = 26, TextBosnian = "Oni govore: 'Svemilosni je uzeo dijete!', Uzvišen neka je On! A meleci su samo robovi poštovani.", TextEnglish = "And they say, 'The Most Merciful has taken a son.' Exalted is He! Rather, they[882] are [but] honored servants.", TextGerman = "Und sie sagen: „Der Allerbarmer hat Sich Kinder genommen. Preis sei Ihm! Nein! Vielmehr sind es geehrte Diener." });
                        surah21.VerseList.Add(new Verse { Number = 27, TextBosnian = "Oni ne govore dok to On ne odobri i postupaju onako kako On naredi.", TextEnglish = "They cannot precede Him in word, and they act by His command.", TextGerman = "Sie kommen Ihm im Reden nicht zuvor, und nur nach Seinem Befehl handeln sie." });
                        surah21.VerseList.Add(new Verse { Number = 28, TextBosnian = "On zna šta su radili i šta će raditi, i oni će se samo za onoga s kim On bude zadovoljan zauzimati, a oni i sami, iz straha prema Njemu, strepe.", TextEnglish = "He knows what is [presently] before them and what will be after them,[883] and they cannot intercede except on behalf of one whom He approves. And they, from fear of Him, are apprehensive.", TextGerman = "Er weiß, was vor ihnen und was hinter ihnen liegt, und sie legen Fürsprache nur für denjenigen ein, dem Er zustimmt. Und sie sind aus Furcht vor Ihm besorgt." });
                        surah21.VerseList.Add(new Verse { Number = 29, TextBosnian = "A onoga od njih koji bi rekao: 'Ja sam, doista, pored Njega, bog!', kaznili bismo Džehennemom, jer Mi tako kažnjavamo zulumćare.", TextEnglish = "And whoever of them should say, 'Indeed, I am a god besides Him' - that one We would recompense with Hell. Thus do We recompense the wrongdoers.", TextGerman = "Und wer von ihnen sagen sollte: „Ich bin ein Gott außer Ihm, dem vergelten Wir mit der Hölle. So vergelten Wir den Ungerechten." });
                        surah21.VerseList.Add(new Verse { Number = 30, TextBosnian = "Zar ne znaju nevjernici da su nebesa i Zemlja bili spojeni, pa smo ih Mi razdvojili, i da Mi od vode sve živo stvaramo?! Pa zar neće vjerovati?!", TextEnglish = "Have those who disbelieved not considered that the heavens and the earth were a joined entity, and then We separated them and made from water every living thing? Then will they not believe?", TextGerman = "Sehen denn diejenigen, die ungläubig sind, nicht, daß die Himmel und die Erde eine zusammenhängende Masse waren? Da haben Wir sie getrennt und aus dem Wasser alles Lebendige gemacht. Wollen sie denn nicht glauben?" });
                        surah21.VerseList.Add(new Verse { Number = 31, TextBosnian = "Mi smo po Zemlji nepomične planine razmjestili da ih ona ne potrese, i po njima smo prolaze i staze učinili da bi oni kuda žele stizali.", TextEnglish = "And We placed within the earth firmly set mountains, lest it should shift with them, and We made therein [mountain] passes [as] roads that they might be guided.", TextGerman = "Und Wir haben auf der Erde festgegründete Berge gemacht, daß sie nicht mit ihnen wanke. Und Wir haben auf ihr breite Durchgänge als Wege gemacht, auf daß sie rechtgeleitet werden mögen." });
                        surah21.VerseList.Add(new Verse { Number = 32, TextBosnian = "A nebo smo učinili svodom čuvanim, a oni se od Njegovih znakova okreću.", TextEnglish = "And We made the sky a protected ceiling, but they, from its signs,[884] are turning away.", TextGerman = "Und Wir haben den Himmel zu einem wohlbehüteten Dach gemacht. Dennoch wenden sie sich von seinen Zeichen ab." });
                        surah21.VerseList.Add(new Verse { Number = 33, TextBosnian = "I On je Taj Koji je stvorio noć i dan, i Sunce i Mjesec, i svi oni u svemiru plove.", TextEnglish = "And it is He who created the night and the day and the sun and the moon; all [heavenly bodies] in an orbit are swimming.", TextGerman = "Und Er ist es, Der die Nacht und den Tag, die Sonne und den Mond erschaffen hat; alles läuft in einer (jeweils eigenen) Umlaufbahn." });
                        surah21.VerseList.Add(new Verse { Number = 34, TextBosnian = "I Mi nismo nijednom čovjeku prije tebe dali vječnost; ako ti umreš, zar će oni dovijeka živjeti?", TextEnglish = "And We did not grant to any man before you eternity [on earth]; so if you die - would they be eternal?", TextGerman = "Und Wir haben für kein menschliches Wesen vor dir ewiges Leben bestimmt. Wenn du nun stirbst, werden sie dann ewig leben?" });
                        surah21.VerseList.Add(new Verse { Number = 35, TextBosnian = "Svako živo biće smrt će okusiti! Mi vas stavljamo na kušnju i u dobru i u zlu, i Nama ćete se vratiti.", TextEnglish = "Every soul will taste death. And We test you with evil and with good as trial; and to Us you will be returned.", TextGerman = "Jede Seele wird den Tod kosten. Und Wir prüfen euch mit Schlechtem und Gutem als Versuchung. Und zu Uns werdet ihr zurückgebracht." });
                        surah21.VerseList.Add(new Verse { Number = 36, TextBosnian = "Kada te vide oni koji ne vjeruju, samo ti se rugaju: 'Je li to onaj koji vaše bogove huli?' A oni sami ne vjeruju kad se spomene Svemilosni.", TextEnglish = "And when those who disbelieve see you, [O Muḥammad], they take you not except in ridicule, [saying], 'Is this the one who mentions [i.e., insults] your gods?' And they are, at the mention of the Most Merciful, disbelievers.", TextGerman = "Und wenn diejenigen, die ungläubig sind, dich sehen, machen sie sich über dich nur lustig: „Ist das derjenige, der eure Götter (in abfälliger Weise) erwähnt?, während sie es sind, die die Ermahnung des Allerbarmers verleugnen." });
                        surah21.VerseList.Add(new Verse { Number = 37, TextBosnian = "Čovjek je stvoren sklon žurbi. Pokazat ću vam znakove Svoje, zato Me ne požurujte.", TextEnglish = "Man was created of haste [i.e., impatience]. I will show you My signs [i.e., vengeance], so do not impatiently urge Me.", TextGerman = "Der Mensch ist (seiner Natur nach) aus Voreiligkeit erschaffen worden. Ich werde euch (schon) Meine Zeichen zeigen; so wünscht nicht von Mir, (sie) zu beschleunigen." });
                        surah21.VerseList.Add(new Verse { Number = 38, TextBosnian = "Oni govore: 'Kada će već jednom to prijeteće obećanje, ako istinu govorite?'", TextEnglish = "And they say, 'When is this promise, if you should be truthful?'", TextGerman = "Und sie sagen: „Wann wird dieses Versprechen eintreten, wenn ihr wahrhaftig seid?" });
                        surah21.VerseList.Add(new Verse { Number = 39, TextBosnian = "A kad bi nevjernici samo znali vrijeme kada neće moći otkloniti vatru od lica svojih i leđa svojih, i da im niko neće moći pružiti pomoć.", TextEnglish = "If those who disbelieved but knew the time when they will not avert the Fire from their faces or from their backs and they will not be aided...[885]", TextGerman = "Wenn diejenigen, die ungläubig sind, (es) nur wüßten, (wie es ist,) wenn sie das (Höllen)feuer weder von ihren Gesichtern noch von ihren Rücken zurückhalten können und ihnen auch keine Hilfe zuteil wird!" });
                        surah21.VerseList.Add(new Verse { Number = 40, TextBosnian = "Naprotiv, ona će im nenadano doći i zaprepastiti ih i oni je neće moći odagnati, a neće im se ni vremena dati.", TextEnglish = "Rather, it will come to them unexpectedly and bewilder them, and they will not be able to repel it, nor will they be reprieved.", TextGerman = "Aber nein! Es wird plötzlich über sie kommen und sie verblüffen. Dann werden sie es nicht abwenden können, und es wird ihnen kein Aufschub gewährt." });
                        surah21.VerseList.Add(new Verse { Number = 41, TextBosnian = "I prije tebe su poslanike ruglu izvrgavali, pa je one koji su im se rugali stiglo baš ono čemu su se rugali.", TextEnglish = "And already were messengers ridiculed before you, but those who mocked them were enveloped by what they used to ridicule.", TextGerman = "Man machte sich ja schon vor dir über Gesandte lustig. Da umschloß diejenigen, die über sie spotteten, das, worüber sie sich lustig zu machen pflegten." });
                        surah21.VerseList.Add(new Verse { Number = 42, TextBosnian = "Reci: 'Ko će vas umjesto Svemilosnog noću i danju štititi?!' Pa, ipak, oni se od Opomene Gospodara svoga okreću.", TextEnglish = "Say, 'Who can protect you at night or by day from the Most Merciful?' But they are, from the remembrance of their Lord, turning away.", TextGerman = "Sag: Wer beschützt euch bei Nacht und Tag vor dem Allerbarmer? Aber nein! Sie wenden sich von der Ermahnung ihres Herrn ab." });
                        surah21.VerseList.Add(new Verse { Number = 43, TextBosnian = "Zar oni imaju božanstva, osim Nas, koja će ih odbraniti?! Ona sama sebi ne mogu pomoći, niti se od Nas mogu zaštititi.", TextEnglish = "Or do they have gods to defend them other than Us? They are unable [even] to help themselves, nor can they be protected from Us.", TextGerman = "Oder haben sie (etwa) Götter, die sie von Uns abhalten (sollten)? Sie können doch weder sich selbst helfen, noch werden sie einen Beschützer vor Uns erhalten." });
                        surah21.VerseList.Add(new Verse { Number = 44, TextBosnian = "Mi smo ovima, a i precima njihovim, dali da uživaju tako da im se život u tome oduljio, A zar oni ne vide da Mi Zemlju s krajeva njezinih umanjujemo, pa kako da oni budu pobjednici?!", TextEnglish = "But, [on the contrary], We have provided good things for these [disbelievers] and their fathers until life was prolonged for them. Then do they not see that We set upon the land, reducing it from its borders?[886] Is it they who will overcome?", TextGerman = "Nein! Vielmehr haben Wir diesen da und ihren Vätern Nießbrauch gewährt, bis sie ein langes Leben geführt haben. Sehen sie denn nicht, daß Wir über das Land kommen und es an seinen Enden vermindern? Werden denn sie die Sieger sein?" });
                        surah21.VerseList.Add(new Verse { Number = 45, TextBosnian = "Reci: 'Ja vas upozoravam Objavom!', ali, gluhi ne čuju poziv kada ih se upozorava.", TextEnglish = "Say, 'I only warn you by revelation.' But the deaf do not hear the call when they are warned.", TextGerman = "Sag: Ich warne euch nur mit der Eingebung. Aber die Tauben hören nicht den Ruf, wenn sie gewarnt werden." });
                        surah21.VerseList.Add(new Verse { Number = 46, TextBosnian = "A da ih samo dašak kazne Gospodara tvoga dotakne, sigurno bi povikali: 'Teško nama, doista smo zulumćari bili!'", TextEnglish = "And if [as much as] a whiff of the punishment of your Lord should touch them, they would surely say, 'O woe to us! Indeed, we have been wrongdoers.'", TextGerman = "Aber wenn ein Hauch von der Strafe deines Herrn sie berührt, sagen sie ganz gewiß: „O wehe uns! Wir pflegten ja Unrecht zu tun." });
                        surah21.VerseList.Add(new Verse { Number = 47, TextBosnian = "I postavit ćemo tačne tezulje za vaganje kad Kijametski dan bude, pa se nikome neće zulum učiniti. A ako nešto bude teško koliko zrno gorušice, Mi ćemo to iznijeti na vidjelo. Dovoljni smo da račune svodimo.", TextEnglish = "And We place the scales of justice for the Day of Resurrection, so no soul will be treated unjustly at all. And if there is [even] the weight of a mustard seed,[887] We will bring it forth. And sufficient are We as accountant.", TextGerman = "Und Wir stellen die gerechten Waagen für den Tag der Auferstehung auf. So wird keiner Seele um irgend etwas Unrecht zugefügt; und wäre es auch das Gewicht eines Senfkorns, Wir bringen es bei. Und Wir genügen als Berechner." });
                        surah21.VerseList.Add(new Verse { Number = 48, TextBosnian = "Mi smo Musau i Haranu dali Furkan, svjetlo i opomenu za bogobojazne.", TextEnglish = "And We had already given Moses and Aaron the criterion and a light and a reminder[888] for the righteous", TextGerman = "Und Wir gaben ja Musa und Harun die Unterscheidung(snorm) und eine Erleuchtung und eine Ermahnung für die Gottesfürchtigen," });
                        surah21.VerseList.Add(new Verse { Number = 49, TextBosnian = "za one koji se budu Gospodara svoga bojali i u osami, i koji od časa oživljavanja budu strepjeli.", TextEnglish = "Who fear their Lord unseen,[889] while they are of the Hour apprehensive.", TextGerman = "die ihren Herrn im Verborgenen fürchten und die wegen der Stunde besorgt sind." });
                        surah21.VerseList.Add(new Verse { Number = 50, TextBosnian = "I ovo je Opomena blagoslovljena koju objavljujemo, pa zar da je vi poričete?!", TextEnglish = "And this [Qur’ān] is a blessed message which We have sent down. Then are you with it unacquainted?[890]", TextGerman = "Und dies ist eine gesegnete Ermahnung, die Wir hinabgesandt haben. Wollt ihr sie denn verwerfen?" });
                        surah21.VerseList.Add(new Verse { Number = 51, TextBosnian = "Mi smo još prije Ibrahimu razboritost dali i dobro smo ga poznavali.", TextEnglish = "And We had certainly given Abraham his sound judgement before,[891] and We were of him well-Knowing", TextGerman = "Wir verliehen bereits zuvor Ibrahim seine Besonnenheit; und Wir wußten über ihn Bescheid." });
                        surah21.VerseList.Add(new Verse { Number = 52, TextBosnian = "Kad on ocu svome i narodu svome reče: 'Kakvi su ovo kumiri kojima se tako predano klanjate?',", TextEnglish = "When he said to his father and his people, 'What are these statues to which you are devoted?'", TextGerman = "Als er zu seinem Vater und seinem Volk sagte: „Was sind das für Bildwerke, deren Andacht ihr euch hingebt?" });
                        surah21.VerseList.Add(new Verse { Number = 53, TextBosnian = "oni odgovoriše: 'Našli smo naše pretke da ih obožavaju.'", TextEnglish = "They said, 'We found our fathers worshippers of them.'", TextGerman = "Sie sagten: „Wir haben (schon) unsere Väter (vor)gefunden, wie sie ihnen dienten." });
                        surah21.VerseList.Add(new Verse { Number = 54, TextBosnian = "I vi ste, a i preci vaši, bili u očitaj zabludi', reče.", TextEnglish = "He said, 'You were certainly, you and your fathers, in manifest error.'", TextGerman = "Er sagte: „Ihr und eure Väter befindet euch ja in einem deutlichen Irrtum." });
                        surah21.VerseList.Add(new Verse { Number = 55, TextBosnian = "Govoriš li ti to ozbiljno ili se samo šališ?', upitaše oni.", TextEnglish = "They said, 'Have you come to us with truth, or are you of those who jest?'", TextGerman = "Sie sagten: „Bringst du uns die Wahrheit, oder gehörst du zu denjenigen, die ihr Spiel treiben?" });
                        surah21.VerseList.Add(new Verse { Number = 56, TextBosnian = "Ne', reče. 'Gospodar vaš je Gospodar nebesa i Zemlje. On je njih stvorio, i ja sam vam za to jedan od svjedoka.", TextEnglish = "He said, '[No], rather, your Lord is the Lord of the heavens and the earth who created them, and I, to that, am of those who testify.", TextGerman = "Er sagte: „Nein! Vielmehr ist euer Herr, der Herr der Himmel und der Erde, Der sie erschaffen hat. Und ich gehöre zu denjenigen, die euch darüber Zeugnis ablegen." });
                        surah21.VerseList.Add(new Verse { Number = 57, TextBosnian = "Tako mi Allaha, ja ću, doista, vašim kumirima napakostiti nakon što se okrenete i udaljite!'", TextEnglish = "And [I swear] by Allāh, I will surely plan against your idols after you have turned and gone away.'", TextGerman = "Und bei Allah, ich werde ganz gewiß gegen eure Götzen eine List anwenden, nachdem ihr den Rücken gekehrt habt." });
                        surah21.VerseList.Add(new Verse { Number = 58, TextBosnian = "I porazbija ih on u komade, osim onoga najvećeg, da bi se njemu obratili.", TextEnglish = "So he made them into fragments, except a large one among them, that they might return to it [and question].", TextGerman = "Da schlug er sie in Stücke, außer einem großen von ihnen, auf daß sie sich an ihn wenden möchten." });
                        surah21.VerseList.Add(new Verse { Number = 59, TextBosnian = "Ko uradi ovo s bogovima našim?', povikaše oni, 'zaista u zulumćare spada.'", TextEnglish = "They said, 'Who has done this to our gods? Indeed, he is of the wrongdoers.'", TextGerman = "Sie sagten: „Wer hat dies mit unseren Göttern getan? Er gehört wahrlich zu den Ungerechten." });
                        surah21.VerseList.Add(new Verse { Number = 60, TextBosnian = "Čuli smo jednog momka kako ih huli', rekoše, 'ime mu je Ibrahim.'", TextEnglish = "They said, 'We heard a young man mention them who is called Abraham.'", TextGerman = "Sie sagten: „Wir hörten einen Jüngling sie (in abfälliger Weise) erwähnen; man nennt ihn Ibrahim." });
                        surah21.VerseList.Add(new Verse { Number = 61, TextBosnian = "Dovedite ga da ga ljudi vide', rekoše, 'da posvjedoče.'", TextEnglish = "They said, 'Then bring him before the eyes of the people that they may testify.'[892]", TextGerman = "Sie sagten: „So bringt ihn her vor die Augen der Menschen, auf daß sie es bezeugen mögen." });
                        surah21.VerseList.Add(new Verse { Number = 62, TextBosnian = "Jesi li ti uradio ovo s bogovima našim, o Ibrahime?', upitaše.", TextEnglish = "They said, 'Have you done this to our gods, O Abraham?'", TextGerman = "Sie sagten: „Hast du dies mit unseren Göttern getan, o Ibrahim?" });
                        surah21.VerseList.Add(new Verse { Number = 63, TextBosnian = "To je učinio ovaj najveći od njih; pitajte ih ako umiju govoriti', reče on.", TextEnglish = "He said, 'Rather, this - the largest of them - did it, so ask them, if they should [be able to] speak.'", TextGerman = "Er sagte: „Nein! Vielmehr hat das dieser Große unter ihnen getan. Fragt sie doch, wenn sie reden können." });
                        surah21.VerseList.Add(new Verse { Number = 64, TextBosnian = "I oni sebi dođoše i rekoše: 'Vi ste, zaista, zulumćari!'", TextEnglish = "So they returned to [blaming] themselves and said [to each other], 'Indeed, you are the wrongdoers.'", TextGerman = "Da kamen sie wieder zu sich und sagten: „Ihr seid ja die Ungerechten." });
                        surah21.VerseList.Add(new Verse { Number = 65, TextBosnian = "Zatim glave oboriše i rekoše: 'Ti znaš da ovi ne govore!'", TextEnglish = "Then they reversed themselves,[893] [saying], 'You have already known that these do not speak!'", TextGerman = "Hierauf machten sie eine Kehrtwendung: „Du weißt doch, daß diese nicht reden können." });
                        surah21.VerseList.Add(new Verse { Number = 66, TextBosnian = "Pa zašto onda, umjesto Allaha, obožavate one koji vam ne mogu ni koristiti niti od vas kakvu štetu otkloniti?', upita on.", TextEnglish = "He said, 'Then do you worship instead of Allāh that which does not benefit you at all or harm you?", TextGerman = "Er sagte: „Dient ihr denn anstatt Allahs dem, was euch nichts nützt und nicht schadet?" });
                        surah21.VerseList.Add(new Verse { Number = 67, TextBosnian = "Pa zašto onda, umjesto Allaha, obožavate one koji vam ne mogu ni koristiti niti od vas kakvu štetu otkloniti?', upita on.", TextEnglish = "Uff[894] to you and to what you worship instead of Allāh. Then will you not use reason?'", TextGerman = "Pfui über euch und über das, dem ihr anstatt Allahs dient! Begreift ihr denn nicht?" });
                        surah21.VerseList.Add(new Verse { Number = 68, TextBosnian = "Spalite ga i bogove svoje pomozite, ako hoćete to da učinite!', povikaše.", TextEnglish = "They said, 'Burn him and support your gods - if you are to act.'", TextGerman = "Sie sagten: „Verbrennt ihn und helft euren Göttern, wenn ihr etwas tun wollt." });
                        surah21.VerseList.Add(new Verse { Number = 69, TextBosnian = "O vatro!', rekosmo Mi, 'postani hladna, i spas Ibrahimu!'", TextEnglish = "We [i.e., Allāh] said, 'O fire, be coolness and safety upon Abraham.'", TextGerman = "Wir sagten: „O Feuer, sei Kühlung und Unversehrtheit für Ibrahim." });
                        surah21.VerseList.Add(new Verse { Number = 70, TextBosnian = "I htjedoše mu spletku skovati, ali ih Mi učinismo gubitnicima najvećim!", TextEnglish = "And they intended for him a plan [i.e., harm], but We made them the greatest losers.", TextGerman = "Sie wollten gegen ihn mit einer List vorgehen. Da machten Wir sie zu den größten Verlierern." });
                        surah21.VerseList.Add(new Verse { Number = 71, TextBosnian = "I spasismo i njega i Luta u zemlju koju smo za ljude blagoslovili,", TextEnglish = "And We delivered him and Lot to the land which We had blessed for the worlds [i.e., peoples].", TextGerman = "Und Wir erretteten ihn und Lut in das Land, das Wir für (all) die Weltenbewohner gesegnet haben." });
                        surah21.VerseList.Add(new Verse { Number = 72, TextBosnian = "i poklonismo mu Ishaka, i Jakuba povrh toga, i sve učinismo čestitim.", TextEnglish = "And We gave him Isaac and Jacob in addition, and all [of them] We made righteous.", TextGerman = "Und Wir schenkten ihm lshaq und Ya’qub zusätzlich; und alle machten Wir rechtschaffen." });
                        surah21.VerseList.Add(new Verse { Number = 73, TextBosnian = "I učinismo ih vođama da upućuju prema zapovijedi Našoj, i objavismo im da čine dobra djela, i da namaz obavljaju, i da zekat udjeljuju, i samo su Nama ibadet činili.", TextEnglish = "And We made them leaders guiding by Our command. And We inspired to them the doing of good deeds, establishment of prayer, and giving of zakāh; and they were worshippers of Us.", TextGerman = "Und Wir machten sie zu Vorbildern, die nach Unserem Befehl leiteten. Und Wir gaben ihnen ein, gute Werke zu tun, das Gebet zu verrichten und die Abgabe zu entrichten. Und sie pflegten Uns zu dienen." });
                        surah21.VerseList.Add(new Verse { Number = 74, TextBosnian = "I Lutu mudrost i znanje dadosmo i iz grada ga, u kome su stanovnici njegovi nevaljalštine činili, izbavismo; to, uistinu, bijaše narod buntovan i zao,", TextEnglish = "And to Lot We gave judgement and knowledge, and We saved him from the city that was committing wicked deeds. Indeed, they were a people of evil, defiantly disobedient.", TextGerman = "Und Lut gaben Wir Urteil(skraft) und Wissen und erretteten ihn aus der Stadt, die schlechte Taten zu begehen pflegte. Sie waren ja böse Leute, sie waren Frevler." });
                        surah21.VerseList.Add(new Verse { Number = 75, TextBosnian = "i u milost Našu ga uvedosmo; on je, doista, od onih dobrih.", TextEnglish = "And We admitted him into Our mercy. Indeed, he was of the righteous.", TextGerman = "Und Wir ließen ihn in Unsere Barmherzigkeit eingehen. Gewiß, er gehört zu den Rechtschaffenen." });
                        surah21.VerseList.Add(new Verse { Number = 76, TextBosnian = "I spomeni Nuha - kad u davno vrijeme zavapi, pa Mu se odazvasmo, i njega i njegovu porodicu od jada velikog spasismo,", TextEnglish = "And [mention] Noah, when he called [to Allāh][895] before [that time], so We responded to him and saved him and his family from the great affliction [i.e., the flood].", TextGerman = "Und (auch) Nuh, als er zuvor rief. Da erhörten Wir ihn und erretteten darauf ihn und seine Angehörigen aus der großen Trübsal." });
                        surah21.VerseList.Add(new Verse { Number = 77, TextBosnian = "i od naroda ga, koji je smatrao neistinitim znakove Naše, zaštitismo. To bijahu opaki ljudi, pa ih sve potopismo.", TextEnglish = "And We aided [i.e., saved] him from the people who denied Our signs. Indeed, they were a people of evil, so We drowned them, all together.", TextGerman = "Und Wir halfen ihm vor den Leuten, die Unsere Zeichen für Lüge erklärten. Sie waren ja böse Leute, und so ließen Wir sie allesamt ertrinken." });
                        surah21.VerseList.Add(new Verse { Number = 78, TextBosnian = "I spomeni Davuda i Sulejmana, kada su sudili o usjevu što su ga noću ovce nečije opasle, i Mi smo bili svjedoci suđenju njihovu,", TextEnglish = "And [mention] David and Solomon, when they judged concerning the field - when the sheep of a people overran it [at night],[896] and We were witness to their judgement.", TextGerman = "Und (auch) Dawud und Sulaiman, als sie über das Saatfeld urteilten. Darin hatte Kleinvieh von (fremden) Leuten nachts geweidet. Und Wir waren für ihr Urteil Zeugen." });
                        surah21.VerseList.Add(new Verse { Number = 79, TextBosnian = "i učinismo da Sulejman pronikne u to, a obojici smo mudrost i znanje dali. I potčinismo planine i ptice da s Davudom Allaha slave i veličaju: sve to Mi smo učinili.", TextEnglish = "And We gave understanding of it [i.e., the case] to Solomon, and to each [of them] We gave judgement and knowledge. And We subjected the mountains to exalt [Us], along with David and [also] the birds. And We were doing [that].[897]", TextGerman = "Und Wir ließen Sulaiman es begreifen. Und allen gaben Wir Urteil(skraft) und Wissen. Und Wir machten die Berge zusammen mit Dawud dienstbar, daß sie (Uns) preisen, und (auch) die Vögel. Wir taten (es wirklich)." });
                        surah21.VerseList.Add(new Verse { Number = 80, TextBosnian = "I naučismo ga da izrađuje pancire za vas da vas štite u borbi sa neprijateljem, pa zašto niste zahvalni?", TextEnglish = "And We taught him the fashioning of coats of armor to protect you from your [enemy in] battle. So will you then be grateful?", TextGerman = "Und Wir lehrten ihn die Verfertigung von (Panzer)kleidung für euch, damit sie euch vor eurer (gegenseitigen) Gewalt schütze. Wollt ihr nun dankbar sein?" });
                        surah21.VerseList.Add(new Verse { Number = 81, TextBosnian = "A Sulejmanu vjetar jaki potčinismo, po zapovijedi njegovoj je puhao prema zemlji koju smo blagoslovili; a Mi sve dobro znamo,", TextEnglish = "And to Solomon [We subjected] the wind, blowing forcefully, proceeding by his command toward the land which We had blessed. And We are ever, of all things, Knowing.", TextGerman = "Und Sulaiman (machten Wir) den Sturmwind (dienstbar), daß er auf seinen Befehl zum Land wehe, das Wir gesegnet haben - und Wir wußten über alles Bescheid -," });
                        surah21.VerseList.Add(new Verse { Number = 82, TextBosnian = "i šejtane neke da zbog njega rone, a radili su i poslove druge, i nad njima smo Mi bdjeli.", TextEnglish = "And of the devils [i.e., jinn] were those who dived for him and did work other than that. And We were of them a guardian.[898]", TextGerman = "und auch unter den Satanen manche, die für ihn tauchten und (auch noch andere) Arbeiten außer dieser verrichteten; und Wir bewachten sie." });
                        surah21.VerseList.Add(new Verse { Number = 83, TextBosnian = "I Ejjuba, kada je Gospodaru svome zavapio: 'Mene je nevolja snašla, a Ti si Milosnik najmilostiviji.'", TextEnglish = "And [mention] Job, when he called to his Lord, 'Indeed, adversity has touched me, and You are the most merciful of the merciful.'", TextGerman = "Und (auch) Ayyub, als er zu seinem Herrn rief: „Mir ist gewiß Unheil widerfahren, doch Du bist der Barmherzigste der Barmherzigen." });
                        surah21.VerseList.Add(new Verse { Number = 84, TextBosnian = "I Mi mu se odazvasmo i nevolju mu koja ga je morila otklonismo i vratismo mu, milošću Našom, porodicu njegovu, i uz njih još toliko, i da bude opomena onima koji su Nama pokorni.", TextEnglish = "So We responded to him and removed what afflicted him of adversity. And We gave him [back] his family and the like thereof with them as mercy from Us and a reminder for the worshippers [of Allāh].", TextGerman = "Da erhörten Wir ihn und nahmen das Unheil, das auf ihm war, von ihm hinweg, und gaben ihm seine Angehörigen und noch einmal die gleiche Zahl dazu, aus Barmherzigkeit von Uns und als Ermahnung für diejenigen, die (Uns) dienen." });
                        surah21.VerseList.Add(new Verse { Number = 85, TextBosnian = "I spomeni Ismaila, i Idrisa, i Zulkifla, a svi su oni bili strpljivi.", TextEnglish = "And [mention] Ishmael and Idrees and Dhul-Kifl; all were of the patient.", TextGerman = "Und (auch) Isma’il und Idris und Du’l-Kifl. Jeder gehörte zu den Standhaften." });
                        surah21.VerseList.Add(new Verse { Number = 86, TextBosnian = "I uvedosmo ih u milost Našu, oni doista bijahu čestiti.", TextEnglish = "And We admitted them into Our mercy. Indeed, they were of the righteous.", TextGerman = "Und Wir ließen sie in Unsere Barmherzigkeit eingehen. Gewiß, sie gehören zu den Rechtschaffenen." });
                        surah21.VerseList.Add(new Verse { Number = 87, TextBosnian = "I spomeni Zunnuna kada srdit ode i pomisli da ga nećemo usmrtiti, pa poslije u tminama zavapi. 'Nema istinskog boga osim Tebe, Uzvišen neka si! Ja sam, zaista, bio od onih koji su zulum učinili!'", TextEnglish = "And [mention] the man of the fish [i.e., Jonah], when he went off in anger[899] and thought that We would not decree [anything] upon him.[900] And he called out within the darknesses,[901] 'There is no deity except You; exalted are You. Indeed, I have been of the wrongdoers.'", TextGerman = "Und (auch) dem Mann mit dem Fisch’, als er erzürnt wegging. Da meinte er, Wir würden ihm nicht (den Lebensunterhalt) bemessen. Dann rief er in den Finsternissen: „Es gibt keinen Gott außer Dir! Preis sei Dir! Gewiß, ich gehöre zu den Ungerechten." });
                        surah21.VerseList.Add(new Verse { Number = 88, TextBosnian = "Pa mu se odazvasmo i tegobe ga spasismo; eto tako Mi spašavamo vjernike.", TextEnglish = "So We responded to him and saved him from the distress. And thus do We save the believers.", TextGerman = "Da erhörten Wir ihn und erretteten ihn aus dem Kummer. So retten Wir die Gläubigen." });
                        surah21.VerseList.Add(new Verse { Number = 89, TextBosnian = "I spomeni Zekerijjaa, kada pozva Gospodara svoga: 'Gospodaru moj, ne ostavljaj me samog, Ti si Najbolji Nasljednik!'", TextEnglish = "And [mention] Zechariah, when he called to his Lord, 'My Lord, do not leave me alone [with no heir], while You are the best of inheritors.'", TextGerman = "Und (auch) Zakariyya, als er zu seinem Herrn rief: „Mein Herr, lasse mich nicht kinderlos bleiben, und Du bist der beste Erbe." });
                        surah21.VerseList.Add(new Verse { Number = 90, TextBosnian = "Pa smo mu se odazvali i poklonili mu Jahjaa i suprugu mu izliječili. Oni su se trudili da što više dobra učine i molili su Nam se u nadi i strahu, i bili su prema Nama ponizni.", TextEnglish = "So We responded to him, and We gave to him John, and amended for him his wife. Indeed, they used to hasten to good deeds and supplicate[902] Us in hope and fear, and they were to Us humbly submissive.", TextGerman = "Da erhörten Wir ihn und schenkten ihm Yahya und besserten ihm seine Gattin. Sie pflegten sich ja mit den guten Dingen zu beeilen und Uns in Begehren und Ehrfurcht anzurufen, und sie pflegten vor Uns demütig zu sein." });
                        surah21.VerseList.Add(new Verse { Number = 91, TextBosnian = "I spomeni onu koja je sačuvala čednost - djevičanstvo svoje, pa smo u nju Naš duh udahnuli, i nju i sina njezina učinili da budu svim svjetovima znamenje.", TextEnglish = "And [mention] the one who guarded her chastity [i.e., Mary], so We blew into her [garment] through Our angel [i.e., Gabriel], and We made her and her son a sign for the worlds.", TextGerman = "Und (auch) diejenige, die ihre Scham unter Schutz stellte. Da hauchten Wir ihr von Unserem Geist ein und machten sie und ihren Sohn zu einem Zeichen für die Weltenbewohner." });
                        surah21.VerseList.Add(new Verse { Number = 92, TextBosnian = "Doista, ova vaša vjera - jedna je vjera, a Ja sam - vaš Gospodar, pa Meni u ibadetu budite!", TextEnglish = "Indeed this, your religion, is one religion,[903] and I am your Lord, so worship Me.", TextGerman = "„Gewiß, diese ist eure Gemeinschaft, eine einzige Gemeinschaft, und Ich bin euer Herr; so dient Mir!" });
                        surah21.VerseList.Add(new Verse { Number = 93, TextBosnian = "I oni su se između sebe u vjeri podvojili, a svi će se Nama vratiti.", TextEnglish = "And [yet] they divided their affair [i.e., that of their religion] among themselves,[904] [but] all to Us will return.", TextGerman = "Aber sie spalteten sich in ihrer Angelegenheit untereinander; doch sie werden alle zu Uns zurückkehren." });
                        surah21.VerseList.Add(new Verse { Number = 94, TextBosnian = "Ko bude dobra djela činio i uz to vjernik bio, trud mu neće lišen nagrade ostati, jer smo ga sigurno Mi pribilježili.", TextEnglish = "So whoever does righteous deeds while he is a believer - no denial will there be for his effort,[905] and indeed We [i.e., Our angels], of it, are recorders.", TextGerman = "Wer etwas an rechtschaffenen Werken tut und dabei gläubig ist, der wird für sein Bemühen nicht Undank ernten; Wir schreiben es ihm gut." });
                        surah21.VerseList.Add(new Verse { Number = 95, TextBosnian = "A dopušteno nije da se stanovnici bilo kojeg naselja koje smo Mi uništili vrate!", TextEnglish = "And it is prohibited to [the people of] a city which We have destroyed that they will [ever] return[906]", TextGerman = "Und verboten ist es für (die Bewohner) eine(r jeden) Stadt, die Wir vernichteten, daß sie (ins Diesseits) zurückkehren," });
                        surah21.VerseList.Add(new Verse { Number = 96, TextBosnian = "I kad se oslobode Je'džudž i Me'džudž i kad se budu niz sve strmine žurno spuštali,", TextEnglish = "Until when [the dam of] Gog and Magog has been opened and they, from every elevation, descend", TextGerman = "bis die (Sperrmauer von) Ya’gug und Ma’güg geöffnet wird und sie von jeder Anhöhe schnell herbeilaufen" });
                        surah21.VerseList.Add(new Verse { Number = 97, TextBosnian = "i približi se Istinita prijetnja, tada će se pogledi nevjernika ukočiti. 'Teško nama, mi smo prema ovome ravnodušni bili, pa čak smo i zulumćari bili!'", TextEnglish = "And [when] the true promise [i.e., the resurrection] has approached; then suddenly the eyes of those who disbelieved will be staring [in horror, while they say], 'O woe to us; we had been unmindful of this; rather, we were wrongdoers.'", TextGerman = "und das wahre Versprechen nahegerückt ist, dann werden sogleich die Blicke derjenigen, die ungläubig sind, starr werden: „O wehe uns! Wir waren dessen ja unachtsam. Nein! Vielmehr pflegten wir Unrecht zu tun." });
                        surah21.VerseList.Add(new Verse { Number = 98, TextBosnian = "I vi, i oni koje pored Allaha obožavate, bit ćete gorivo u Džehennemu, a u njega ćete doista ući.", TextEnglish = "Indeed, you [disbelievers] and what you worship other than Allāh are the firewood of Hell. You will be coming to [enter] it.", TextGerman = "„Gewiß, ihr und das, dem ihr anstatt Allahs dient, seid Brennstoff der Hölle. Ihr werdet dahin hinabgehen." });
                        surah21.VerseList.Add(new Verse { Number = 99, TextBosnian = "Da su oni bogovi, ne bi u njega ušli, i svi će u njemu vječno boraviti.", TextEnglish = "Had these [false deities] been [actual] gods, they would not have come to it, but all are eternal therein.", TextGerman = "Wenn diese da Götter wären, gingen sie nicht zu ihr hinab. Und alle werden ewig darin bleiben." });
                        surah21.VerseList.Add(new Verse { Number = 100, TextBosnian = "U njemu će prigušeno uzdisati, u njemu ništa radosno neće čuti.", TextEnglish = "For them therein is heavy sighing, and they therein will not hear.", TextGerman = "Sie werden darin seufzen und selbst darin nicht hören." });
                        surah21.VerseList.Add(new Verse { Number = 101, TextBosnian = "A oni kojima smo još prije lijepu nagradu obećali, oni će od njega daleko biti.", TextEnglish = "Indeed, those for whom the best [reward] has preceded from Us - they are from it far removed.", TextGerman = "Gewiß, diejenigen, an die von Uns (das Versprechen für) die beste Behandlung vorausgegangen ist, sie werden von ihr ferngehalten," });
                        surah21.VerseList.Add(new Verse { Number = 102, TextBosnian = "Huku njegovu neće čuti, i vječno će u onom što im budu duše željele uživati;", TextEnglish = "They will not hear its sound, while they are, in that which their souls desire, abiding eternally.", TextGerman = "sie hören von ihr nicht (einmal) das leiseste Geräusch. Und sie werden in dem, was ihre Seelen begehrt haben, ewig bleiben." });
                        surah21.VerseList.Add(new Verse { Number = 103, TextBosnian = "neće ih brinuti najveći užas, nego će ih meleki dočekati: 'Evo, ovo je Dan vaš koji vam je obećavan!'", TextEnglish = "They will not be grieved by the greatest terror,[907] and the angels will meet them, [saying], 'This is your Day which you have been promised' -", TextGerman = "Der größte Schrecken macht sie nicht traurig, und die Engel empfangen sie: „Das ist euer Tag, der euch versprochen wurde." });
                        surah21.VerseList.Add(new Verse { Number = 104, TextBosnian = "Na Dan kad smotamo nebesa kao što se smota list papira za pisanje, onako kako smo prvi put iz ništa stvorili, tako ćemo ponovo iz ništa stvoriti, to je obećanje Naše, Mi ćemo, doista, to učiniti.", TextEnglish = "The Day when We will fold the heaven like the folding of a [written] sheet for the records. As We began the first creation, We will repeat it. [That is] a promise binding upon Us. Indeed, We will do it.[908]", TextGerman = "An dem Tag, da Wir den Himmel zusammenfalten, wie der Urkundenschreiber die Schriftstücke zusammenfaltet. Wie Wir eine erste Schöpfung am Anfang gemacht haben, wiederholen Wir sie; (das ist) ein für Uns bindendes Versprechen. Wir werden es bestimmt tun." });
                        surah21.VerseList.Add(new Verse { Number = 105, TextBosnian = "Mi smo u Zeburu, poslije Opomene, napisali da će Zemlju Moji čestiti robovi naslijediti.", TextEnglish = "And We have already written in the book [of Psalms][909] after the [previous] mention[910] that the land [of Paradise] is inherited by My righteous servants.", TextGerman = "Und Wir haben bereits im Buch der Weisheit nach der Ermahnung geschrieben, daß Meine rechtschaffenen Diener das Land erben werden." });
                        surah21.VerseList.Add(new Verse { Number = 106, TextBosnian = "U ovom je doista saopćenje za ljude koji budu Allahu robovali,", TextEnglish = "Indeed, in this [Qur’ān] is notification for a worshipping people.", TextGerman = "In diesem ist fürwahr eine Botschaft an Leute, die (Uns) dienen." });
                        surah21.VerseList.Add(new Verse { Number = 107, TextBosnian = "a tebe smo samo kao milost svjetovima poslali.", TextEnglish = "And We have not sent you, [O Muḥammad], except as a mercy to the worlds.", TextGerman = "Und Wir haben dich nur als Barmherzigkeit für die Weltenbewohner gesandt." });
                        surah21.VerseList.Add(new Verse { Number = 108, TextBosnian = "Reci: 'Meni se objavljuje da je vaš Bog- Jedan Bog, pa samo Njemu budite predani!'", TextEnglish = "Say, 'It is only revealed to me that your god is but one God; so will you be Muslims [in submission to Him]?'", TextGerman = "Sag: Mir wird (als Offenbarung) nur eingegeben, daß euer Gott nur ein einziger Gott ist. Werdet ihr nun (Allah) ergeben sein?" });
                        surah21.VerseList.Add(new Verse { Number = 109, TextBosnian = "A ako se oni okrenu, ti reci: 'Ja sam vas sve, bez razlike, obavijestio, a ne znam da li je blizu ili daleko Ono što vam se kao prijetnja obećava.'", TextEnglish = "But if they turn away, then say, 'I have announced to [all of] you equally.[911] And I know not whether near or far is that which you are promised.", TextGerman = "Wenn sie sich abkehren, dann sag: Ich habe es euch (allen) gleichermaßen angekündigt, und ich weiß nicht, ob das nahe ist oder fernliegt, was euch versprochen wird." });
                        surah21.VerseList.Add(new Verse { Number = 110, TextBosnian = "On zna glasno izgovorene riječi, zna i ono što krijete,", TextEnglish = "Indeed, He knows what is declared of speech, and He knows what you conceal.", TextGerman = "Gewiß, Er weiß, was an Worten laut vernehmbar geäußert wird, und Er weiß, was ihr verheimlicht." });
                        surah21.VerseList.Add(new Verse { Number = 111, TextBosnian = "a ja ne znam da nije to vama iskušenje, i pružanje uživanja još za izvjesno vrijeme.", TextEnglish = "And I know not; perhaps it[912] is a trial for you and enjoyment for a time.'", TextGerman = "Und ich weiß (auch) nicht, ob es vielleicht nur eine Versuchung für euch ist und ein Nießbrauch auf Zeit." });
                        surah21.VerseList.Add(new Verse { Number = 112, TextBosnian = "Gospodaru moj, presudi onako kako su zaslužili!', reče on, 'a Gospodar naš Svemilosni jeste Onaj od Kojega treba tražiti pomoć protiv onoga čime Ga opisujete.'", TextEnglish = "[The Prophet (ﷺ)] has said, 'My Lord, judge [between us] in truth. And our Lord is the Most Merciful, the one whose help is sought against that which you describe.'[913]", TextGerman = "Er sagte: Mein Herr, richte der Wahrheit entsprechend. Und unser Herr ist der Allerbarmer, bei Dem Hilfe zu suchen ist gegen das, was ihr beschreibt." });
                        result = surah21;
                        break;
                    #endregion

                    // 
                    #region 22 Al-Haj
                    case 22:
                        Surah surah22 = new Surah(); surah22.Name = Constants.Surah22Name; surah22.Number = 22; surah22.VerseList = new List<Verse>();
                        surah22.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah22.VerseList.Add(new Verse { Number = 1, TextBosnian = "O ljudi, bojte se Gospodara svoga! Zaista je potres Časa kijametskog nešto veliko!", TextEnglish = "O mankind, fear your Lord. Indeed, the convulsion of the [final] Hour is a terrible thing.", TextGerman = "O ihr Menschen, fürchtet euren Herrn. Gewiß, das Beben der Stunde ist eine gewaltige Sache." });
                        surah22.VerseList.Add(new Verse { Number = 2, TextBosnian = "Na Dan kada ga vidite vi, svaka dojilja će ono što doji zapanjena zaboraviti, a svaka trudnica će svoj plod pobaciti i ti ćeš vidjeti ljude pijane, a oni neće pijani biti, nego će kazna Allahova žestoka biti!", TextEnglish = "On the Day you see it every nursing mother will be distracted from that [child] she was nursing, and every pregnant woman will abort her pregnancy, and you will see the people [appearing] intoxicated while they are not intoxicated; but the punishment of Allāh is severe.", TextGerman = "An dem Tag, da ihr es seht, wird jede Stillende (aus Entsetzen) übersehen, was sie (soeben) stillt, und jede Schwangere wird mit dem niederkommen, was sie trägt. Und du siehst die Menschen trunken, obwohl sie nicht betrunken sind; aber die Strafe Allahs ist streng." });
                        surah22.VerseList.Add(new Verse { Number = 3, TextBosnian = "Ima ljudi kao onaj koji se bez znanja raspravlja o Allahu i koji slijedi svakog šejtana buntovnog.", TextEnglish = "And of the people is he who disputes about Allāh without knowledge and follows every rebellious devil.", TextGerman = "Und unter den Menschen gibt es manch einen, der über Allah ohne Wissen streitet und jedem rebellischen Satan folgt," });
                        surah22.VerseList.Add(new Verse { Number = 4, TextBosnian = "Za njega je već propisano da će svakog onog ko ga uzme za zaštitnika na stranputicu zavesti i u patnju ga ognjenu odvesti.", TextEnglish = "It has been decreed for him [i.e., every devil] that whoever turns to him - he will misguide him and will lead him to the punishment of the Blaze.", TextGerman = "gegen den vorgezeichnet ist, daß er denjenigen, der ihn zum Schutzherr nimmt, daß er ihn in die Irre führen und zur Strafe der Feuerglut leiten wird." });
                        surah22.VerseList.Add(new Verse { Number = 5, TextBosnian = "O ljudi! Ako ste u sumnji spram proživljenja - pa Mi smo vas, zaista, stvorili od prašine, zatim od kapi sjemena, potom od zakvačka, zatim od grude mesa, oblikovanog i neoblikovanog, da bismo vama objasnili! A u matericama zadržavamo šta hoćemo, do roka određenog, zatim činimo da se kao dojenčad rađate i da poslije do muževnog doba uzrastete; neko od vas umire, a neko duboku starost doživljava, tako da, nakon što je znao, ne zna ništa. I ti vidiš zemlju kako je zamrla, ali kad na nju vodu spustimo, ona ustrepće i uzbuja, i iz nje iznikne svakovrsno bilje prekrasno.", TextEnglish = "O people, if you should be in doubt about the Resurrection, then [consider that] indeed, We created you from dust, then from a sperm-drop, then from a clinging clot, and then from a lump of flesh, formed and unformed[915] - that We may show you.[916] And We settle in the wombs whom We will for a specified term, then We bring you out as a child, and then [We develop you] that you may reach your [time of] maturity. And among you is he who is taken in [early] death, and among you is he who is returned to the most decrepit [old] age so that he knows, after [once having] knowledge, nothing. And you see the earth barren, but when We send down upon it rain, it quivers and swells and grows [something] of every beautiful kind.", TextGerman = "O ihr Menschen, wenn ihr über die Auferweckung im Zweifel seid, so haben Wir euch aus Erde erschaffen, hierauf aus einem Samentropfen, hierauf aus einem Anhängsel, hierauf aus einem kleinen Klumpen, gestaltet und ungestaltet, um (es) euch klarzumachen. Und Wir lassen, was Wir wollen, im Mutterleib auf eine festgesetzte Frist untergebracht. Danach lassen Wir euch als kleine Kinder hervorkommen. Hierauf (lassen Wir euch heranwachsen), damit ihr eure Vollreife erlangt. Und mancher von euch wird (frühzeitig) abberufen, und manch einer von euch wird in das niedrigste (Greisen)alter gebracht, so daß er nach (dem vorherigen) Wissen nichts (mehr) weiß. Und du siehst die Erde regungslos, doch wenn Wir Wasser auf sie herabkommen lassen, regt sie sich, schwillt und läßt von jeder entzückenden (Pflanzen)art wachsen." });
                        surah22.VerseList.Add(new Verse { Number = 6, TextBosnian = "To je zato što je Allah, baš On, zaista, Istina i što On oživljava mrtve, i što On nad svime ima moć,", TextEnglish = "That is because Allāh is the True Reality and because He gives life to the dead and because He is over all things competent", TextGerman = "Dies, weil Allah die Wahrheit ist und weil Er die Toten wieder lebendig macht und weil Er zu allem die Macht hat" });
                        surah22.VerseList.Add(new Verse { Number = 7, TextBosnian = "i što će Čas oživljenja, u to nema sumnje, doći i što će Allah one u grobovima oživiti.", TextEnglish = "And [that they may know] that the Hour is coming - no doubt about it - and that Allāh will resurrect those in the graves.", TextGerman = "und weil die Stunde kommt, an der es keinen Zweifel gibt, und weil Allah (all) diejenigen auferwecken wird, die in den Gräbern sind." });
                        surah22.VerseList.Add(new Verse { Number = 8, TextBosnian = "Ima ljudi kao onaj koji o Allahu raspravlja bez znanja i bez Upute i bez Knjige svjetilje,", TextEnglish = "And of the people is he who disputes about Allāh without knowledge or guidance or an enlightening book [from Him],", TextGerman = "Doch gibt es unter den Menschen manchen, der über Allah ohne Wissen, ohne Rechtleitung und ohne erleuchtendes Buch streitet," });
                        surah22.VerseList.Add(new Verse { Number = 9, TextBosnian = "savijajući svoj vrat da bi s Allahova Puta odvraćao. Njega na dunjaluku čeka poniženje, a na Kijametskom danu dat ćemo mu da iskusi patnju vatrom.", TextEnglish = "Twisting his neck [in arrogance] to mislead [people] from the way of Allāh. For him in the world is disgrace, and We will make him taste on the Day of Resurrection the punishment of the Burning Fire [while it is said],", TextGerman = "indem er sich (hochmütig) zur Seite wendet, um von Allahs Weg in die Irre zu führen. Schande gibt es für ihn im Diesseits, und Wir lassen ihn am Tag der Auferstehung die Strafe des Brennens kosten." });
                        surah22.VerseList.Add(new Verse { Number = 10, TextBosnian = "Eto, to je zbog djela ruku tvojih, a Allah, zaista, ne čini zulum robovima Svojim.'", TextEnglish = "That is for what your hands have put forth and because Allāh is not ever unjust to [His] servants.'", TextGerman = "„Dies (geschieht) wegen dessen, was deine Hände vorausgeschickt haben, und deshalb, weil Allah keiner ist, der den Menschen Unrecht zufügt." });
                        surah22.VerseList.Add(new Verse { Number = 11, TextBosnian = "Ima ljudi kao onaj koji Allaha obožava kolebljivo, na ivici, ako ga zadesi dobro, smiri se time, a ako ga zadesi iskušenje, on se potpuno okrene, pa tako izgubi i dunjaluk i ahiret. To je, uistinu, očiti gubitak.", TextEnglish = "And of the people is he who worships Allāh on an edge.[917] If he is touched by good, he is reassured by it; but if he is struck by trial, he turns on his face [to unbelief]. He has lost [this] world and the Hereafter. That is what is the manifest loss.", TextGerman = "Und unter den Menschen gibt es manchen, der Allah nur am Rande dient. Wenn ihn etwas Gutes trifft, ist er damit beruhigt, doch wenn ihn eine Versuchung trifft, macht er eine Kehrtwende. Er verliert das Diesseits und das Jenseits. Das ist der deutliche Verlust." });
                        surah22.VerseList.Add(new Verse { Number = 12, TextBosnian = "On, pored Allaha, moli onoga koji od njega ne može nikakvu štetu otkloniti niti mu može bilo kakvu korist pribaviti; to je, zaista, daleka stranputica;", TextEnglish = "He invokes instead of Allāh that which neither harms him nor benefits him. That is what is the extreme error.", TextGerman = "Er ruft anstatt Allahs das an, was ihm weder schadet noch nützt. Das ist wirklich der tiefe Irrtum." });
                        surah22.VerseList.Add(new Verse { Number = 13, TextBosnian = "moli onoga što će mu prije nauditi nego od koristi biti; takav je, doista, loš zaštitnik i zao drug!", TextEnglish = "He invokes one whose harm is closer than his benefit - how wretched the protector and how wretched the associate.", TextGerman = "Er ruft denjenigen an, von dem eher Schaden (zu erwarten) ist als Nutzen - ein wahrlich schlimmer Schutzherr und ein wahrlich schlimmer Gefährte!" });
                        surah22.VerseList.Add(new Verse { Number = 14, TextBosnian = "Allah će one koji vjeruju i čine dobra djela, zaista, uvesti u džennetske bašče, kroz koje rijeke teku. Allah radi ono što hoće!", TextEnglish = "Indeed, Allāh will admit those who believe and do righteous deeds to gardens beneath which rivers flow. Indeed, Allāh does what He intends.", TextGerman = "Gewiß, Allah läßt diejenigen, die glauben und rechtschaffene Werke tun, in Gärten eingehen, durcheilt von Bächen. Gewiß, Allah tut, was Er will." });
                        surah22.VerseList.Add(new Verse { Number = 15, TextBosnian = "Onaj ko misli da Poslaniku Allah neće pomoći ni na dunjaluku ni na ahiretu, neka rastegne uže do tavanice i neka ga presiječe, pa nek vidi da li će njegov plan otkloniti ono što ga srdi.", TextEnglish = "Whoever should think that Allāh will not support him [i.e., Prophet Muḥammad (ﷺ)] in this world and the Hereafter - let him extend a rope to the ceiling, then cut off [his breath],[918] and let him see: will his effort remove that which enrages [him]?", TextGerman = "Wer meint, daß Allah ihm im Diesseits und Jenseits nicht helfen werde, der strecke doch ein Seil zum Himmel, dann schneide er (es) ab. So schaue er, ob nun seine List das wegnimmt, was (ihn) ergrimmen läßt." });
                        surah22.VerseList.Add(new Verse { Number = 16, TextBosnian = "I tako isto, Mi ga u vidu jasnih ajeta objavljujemo, a Allah upućuje koga hoće.", TextEnglish = "And thus have We sent it [i.e., the Qur’ān] down as verses of clear evidence and because Allāh guides whom He intends.", TextGerman = "So haben Wir ihn als klare Zeichen hinabgesandt, und (es ist so,) daß Allah rechtleitet, wen Er will." });
                        surah22.VerseList.Add(new Verse { Number = 17, TextBosnian = "Zaista će onima koji vjeruju, jevrejima, sabijcima, kršćanima, poklonicima vatre i višebošcima, zaista će im Allah presuditi na Kijametskom danu. Allah je, zaista, svemu svjedok.", TextEnglish = "Indeed, those who have believed and those who were Jews and the Sabeans and the Christians and the Magians and those who associated with Allāh - Allāh will judge between them on the Day of Resurrection. Indeed Allāh is, over all things, Witness.[919]", TextGerman = "Gewiß, diejenigen, die glauben, und diejenigen, die dem Judentum angehören, und die Säbier und die Christen und die Magus und diejenigen, die Götzendiener sind -gewiß, Allah wird am Tag der Auferstehung zwischen ihnen entscheiden; Allah ist ja über alles Zeuge." });
                        surah22.VerseList.Add(new Verse { Number = 18, TextBosnian = "Zar ne vidiš da Allahu sedždu čine i oni na nebesima i oni na Zemlji, a i Sunce, i Mjesec, i zvijezde, i planine, i drveće, i životinje, i mnogi ljudi, a mnogi i kaznu zaslužuju. A koga Allah ponizi, niko ga ne može poštovanim učiniti; Allah, zaista, radi što hoće.", TextEnglish = "Do you not see [i.e., know] that to Allāh prostrates whoever is in the heavens and whoever is on the earth and the sun, the moon, the stars, the mountains, the trees, the moving creatures and many of the people? But upon many the punishment has been justified.[920] And he whom Allāh humiliates - for him there is no bestower of honor. Indeed, Allāh does what He wills.", TextGerman = "Siehst du nicht, daß sich vor Allah (jeder) niederwirft, wer in den Himmeln und wer auf der Erde ist, und (auch) die Sonne, der Mond und die Sterne, die Berge, die Bäume und die Tiere und viele von den Menschen? Und gegen viele ist die Strafe unvermeidlich geworden. Und wen Allah der Schmach aussetzt, der hat niemanden, der ihm Ehre erweist. Gewiß, Allah tut, was Er will." });
                        surah22.VerseList.Add(new Verse { Number = 19, TextBosnian = "Ta dva protivnička tabora spore se o Gospodaru svome; onima koji ne budu vjerovali bit će odijela od Vatre skrojena, a ključala voda bit će na glave njihove sipana;", TextEnglish = "These[921] are two adversaries who have disputed over their Lord. But those who disbelieved will have cut out for them garments of fire. Poured upon their heads will be scalding water", TextGerman = "Das sind zwei Widersacher, die miteinander über ihren Herrn streiten. Für diejenigen nun, die ungläubig sind, werden Gewänder aus Feuer zugeschnitten; über ihre Köpfe wird heißes Wasser gegossen." });
                        surah22.VerseList.Add(new Verse { Number = 20, TextBosnian = "od nje će se istopiti ono što je u trbusima njihovim, i koža,", TextEnglish = "By which is melted that within their bellies and [their] skins.", TextGerman = "Dadurch wird zum Schmelzen gebracht, was sie in ihrem Bauch haben, und ebenso die Haut." });
                        surah22.VerseList.Add(new Verse { Number = 21, TextBosnian = "a imat će oni i gvozdene kukaste štapove", TextEnglish = "And for [striking] them are maces of iron.", TextGerman = "Und für sie gibt es Keulen aus Eisen." });
                        surah22.VerseList.Add(new Verse { Number = 22, TextBosnian = "kad god htjednu da zbog teškog jada iz nje iziđu, bit će u nju vraćeni: 'Iskusite patnju vatrenu!'", TextEnglish = "Every time they want to get out of it [i.e., Hellfire] from anguish, they will be returned to it, and [it will be said], 'Taste the punishment of the Burning Fire!'", TextGerman = "Jedesmal, wenn sie vor Kummer aus ihm herauskommen wollen, werden sie dahin zurückgebracht, und (es wird zu ihnen gesagt): „Kostet die Strafe des Brennens!" });
                        surah22.VerseList.Add(new Verse { Number = 23, TextBosnian = "A one koji budu vjerovali i dobra djela činili-Allah će sigurno uvesti u džennetske bašče kroz koje rijeke teku; u njima će se narukvicama od zlata i biserom kititi a odjeća će im svilena biti.", TextEnglish = "Indeed, Allāh will admit those who believe and do righteous deeds to gardens beneath which rivers flow. They will be adorned therein with bracelets of gold and pearl, and their garments therein will be silk.", TextGerman = "Gewiß, Allah läßt diejenigen, die glauben und rechtschaffene Werke tun, in Gärten eingehen, durcheilt von Bächen, worin sie mit Armreifen aus Gold und mit Perlen geschmückt sein werden und worin ihre Kleidung aus Seide sein wird." });
                        surah22.VerseList.Add(new Verse { Number = 24, TextBosnian = "I napućeni su da govore lijepe riječi i napućeni su na Put Hvaljenoga.", TextEnglish = "And they had been guided [in worldly life] to good speech, and they were guided to the path of the Praiseworthy.", TextGerman = "Rechtgeleitet sind sie zu dem Wort, das gut ist; und rechtgeleitet sind sie zum Weg des Lobenswürdigen." });
                        surah22.VerseList.Add(new Verse { Number = 25, TextBosnian = "Zaista će oni koji ne vjeruju i odvraćaju od Allahova Puta i Časne džamije koju smo namijenili za sve ljude, jednako za mještanina i za beduina, biti kažnjeni. A onome ko u njemu naumi zastraniti zulum čineći, dat ćemo da patnje bolne kuša.", TextEnglish = "Indeed, those who have disbelieved and avert [people] from the way of Allāh and [from] al-Masjid al-Ḥarām, which We made for the people - equal are the resident therein and one from outside - and [also] whoever intends [a deed] therein[922] of deviation [in religion] by wrongdoing - We will make him taste of a painful punishment.", TextGerman = "Gewiß, diejenigen, die ungläubig sind und von Allahs Weg abhalten und (auch) von der geschützten Gebetsstätte, die Wir für die Menschen bestimmt haben, gleich ob sie dort ansässig oder nur vorübergehend anwesend sind (, werden ihre Strafe bekommen). Wer aber beabsichtigt, dort etwas Abwegiges zu Unrecht (zu begehen), den werden Wir von einer schmerzhaften Strafe kosten lassen." });
                        surah22.VerseList.Add(new Verse { Number = 26, TextBosnian = "I kada smo Ibrahimu pokazali mjesto Kuće, rekavši: 'Meni ništa ne pridružuj, i očisti Kuću Moju za one koji će je obilaziti, koji će stajati i koji će na ruku'u i sedždi biti.'", TextEnglish = "And [mention, O Muḥammad], when We designated for Abraham the site of the House, [saying], 'Do not associate anything with Me and purify My House for those who perform ṭawāf[923] and those who stand [in prayer] and those who bow and prostrate.", TextGerman = "Und als Wir Ibrahim die Stelle des Hauses zuwiesen: „Geselle Mir nichts bei und reinige Mein Haus für die den Umlauf Vollziehenden, die aufrecht Stehenden, sich Verbeugenden und die sich Niederwerfenden." });
                        surah22.VerseList.Add(new Verse { Number = 27, TextBosnian = "I oglasi ljudima hadž - i dolazit će ti pješke i na kamilama iznurenim koje dolaze sa dalekog puta,", TextEnglish = "And proclaim to the people the ḥajj [pilgrimage]; they will come to you on foot and on every lean camel; they will come from every distant pass -", TextGerman = "Und rufe unter den Menschen die Pilgerfahrt aus, so werden sie zu dir kommen zu Fuß und auf vielen hageren (Reittieren), die aus jedem tiefen Paßweg da herkommen," });
                        surah22.VerseList.Add(new Verse { Number = 28, TextBosnian = "I da bi prisustvovali onome od čega će koristi imati i da bi ime Allahovo spominjali u danima poznatim zato što ih je oskrbio životinjama domaćim. Jedite meso njihovo i nahranite siromaha ubogog!", TextEnglish = "That they may witness [i.e., attend] benefits for themselves and mention the name of Allāh on known [i.e., specific] days over what He has provided for them of [sacrificial] animals.[924] So eat of them and feed the miserable and poor.", TextGerman = "damit sie (allerlei) Nutzen für sich erfahren und den Namen Allahs an wohlbekannten Tagen über den aussprechen, womit Er sie an den Vierfüßlern unter dem Vieh versorgt hat. - Eßt (selbst) davon und gebt dem Elenden, dem Armen zu essen." });
                        surah22.VerseList.Add(new Verse { Number = 29, TextBosnian = "Neka potom sa sebe prljavštinu uklone, neka svoje zavjete ispune i neka oko Kuće drevne obilaze.", TextEnglish = "Then let them end their untidiness and fulfill their vows and perform ṭawāf around the ancient House.'", TextGerman = "Hierauf sollen sie ihre Ungepflegtheit beenden, ihre Gelübde erfüllen und den Umlauf um das alt(ehrwürdig)e Haus vollziehen." });
                        surah22.VerseList.Add(new Verse { Number = 30, TextBosnian = "To je tako! A ko Allahove zabrane smatra velikim, pa to će mu biti dobro kod Gospodara njegova - a dozvoljena vam je stoka, osim one koja vam se spominje - pa klonite se prljavštine kumira i klonite se lažna govora!", TextEnglish = "That [has been commanded], and whoever honors the sacred ordinances of Allāh - it is best for him in the sight of his Lord. And permitted to you are the grazing livestock, except what is recited to you.[925] So avoid the uncleanliness of idols and avoid false statement,", TextGerman = "So (ist es). Und wenn einer die unantastbaren Dinge Allahs hoch ehrt, so ist es besser für ihn bei seinem Herrn. Erlaubt ist euch das Vieh, außer dem, was euch verlesen wird. So meidet den Greuel der Götzenbilder, und meidet die falsche Aussage," });
                        surah22.VerseList.Add(new Verse { Number = 31, TextBosnian = "iskazujući Allahu čistu predanost, ne pridružujući Mu u obožavanju nikoga! A onaj ko Allahu pridružuje druge - kao da je s neba pao, pa ga ptice razgrabe, ili ga vjetar u daleki predio otpuše.", TextEnglish = "Inclining [only] to Allāh, not associating [anything] with Him. And he who associates with Allāh - it is as though he had fallen from the sky and was snatched by the birds or the wind carried him down into a remote place.", TextGerman = "als Anhänger des rechten Glaubens gegenüber Allah, die Ihm nichts beigesellen. Und wenn einer Allah (etwas) beigesellt, so ist es, als ob er vom Himmel herunterfiele und er dann von den Vögeln fortgerissen oder vom Wind an einen fernen Ort hinabgeweht würde." });
                        surah22.VerseList.Add(new Verse { Number = 32, TextBosnian = "Tako je to! Pa ko poštuje Allahova znamenja - znak je to bogobojaznosti srca.", TextEnglish = "That [is so]. And whoever honors the symbols [i.e., rites] of Allāh - indeed, it is from the piety of hearts.", TextGerman = "So ist es. Und wenn einer die Kulthandlungen Allahs hoch ehrt, so ist es (ein Ausdruck, der) von der Gottesfurcht der Herzen (herrührt)." });
                        surah22.VerseList.Add(new Verse { Number = 33, TextBosnian = "Vi imate u njima koristi do određenog roka, a poslije je mjesto njihovo kraj Kuće drevne.", TextEnglish = "For you therein [i.e., the animals marked for sacrifice] are benefits for a specified term;[926] then their place of sacrifice is at the ancient House.[927]", TextGerman = "An ihnen habt ihr (allerlei) Nutzen auf eine festgesetzte Frist, hierauf liegt ihr Zielort beim alt(ehrwürdig)en Haus." });
                        surah22.VerseList.Add(new Verse { Number = 34, TextBosnian = "Svakoj vjerskoj zajednici propisali smo klanje kurbana da bi spominjali Allahovo ime prilikom klanja stoke koju im On daje. Vaš Bog je jedan Bog, zato se samo Njemu iskreno predajte! A radosnom viješću obraduj ponizne,", TextEnglish = "And for every [religious] community We have appointed a rite [of sacrifice][928] that they may mention the name of Allāh over what He has provided for them of [sacrificial] animals. For your god is one God, so to Him submit. And, [O Muḥammad], give good tidings to the humble [before their Lord]", TextGerman = "Und für jede Gemeinschaft haben Wir einen Ritus festgelegt, damit sie den Namen Allahs über den aussprechen, womit Er sie an den Vierfüßlern unter dem Vieh versorgt hat. Euer Gott ist ein Einziger Gott, so seid Ihm ergeben. Und verkünde frohe Botschaft denjenigen, die sich demütigen," });
                        surah22.VerseList.Add(new Verse { Number = 35, TextBosnian = "čija srca, kad se Allah spomene, strah obuzme, i one koji strpljivo podnose nevolje koje ih zadese, i one koji namaz obavljaju i koji, od onoga što im Mi dajemo, udjeljuju.", TextEnglish = "Who, when Allāh is mentioned, their hearts are fearful, and [to] the patient over what has afflicted them, and the establishers of prayer and those who spend from what We have provided them.", TextGerman = "denjenigen, deren Herzen sich vor Ehrfurcht regen, wenn Allahs gedacht wird, die das standhaft ertragen, was sie trifft, das Gebet verrichten und von dem, womit Wir sie versorgt haben, ausgeben." });
                        surah22.VerseList.Add(new Verse { Number = 36, TextBosnian = "A kamile smo vam učinili jednim od Allahovih znamenja i vi u njima imate dobro, zato spominjite Allahovo ime nad njima, dok stoje sa jednom podvijenom nogom, a kad njihovi bokovi padnu na zemlju. Jedite ih, a nahranite i onoga koji se zadovolji s onim što mu se da, a i onog koji dođe da mu se da; tako smo vam ih potčinili da biste zahvalni bili.", TextEnglish = "And the camels and cattle We have appointed for you as among the symbols [i.e., rites] of Allāh; for you therein is good. So mention the name of Allāh upon them when lined up [for sacrifice]; and when they are [lifeless] on their sides, then eat from them and feed the needy [who does not seek aid] and the beggar. Thus have We subjected them to you that you may be grateful.", TextGerman = "Und die Opferkamele haben Wir euch zu Kultzeichen Allahs gemacht. An ihnen habt ihr (etwas) Gutes. So sprecht den Namen Allahs über sie aus, wenn sie mit gebundenen Beinen dastehen. Wenn sie nun auf die Seite umgefallen sind, dann eßt davon und gebt dem bescheidenen und dem fordernden (Armen) zu essen. So haben Wir sie euch dienstbar gemacht, auf daß ihr dankbar sein möget." });
                        surah22.VerseList.Add(new Verse { Number = 37, TextBosnian = "Do Allaha neće doprijeti meso njihovo i krv njihova, ali će Mu stići bogogojaznost vaša. Tako vam ih je potčinio da biste Allaha veličali zato što vas je uputio. I obraduj one koji dobra djela čine!", TextEnglish = "Their meat will not reach Allāh, nor will their blood, but what reaches Him is piety from you. Thus have We subjected them to you that you may glorify Allāh for that [to] which He has guided you; and give good tidings to the doers of good.", TextGerman = "Weder ihr Fleisch noch ihr Blut werden Allah erreichen, aber Ihn erreicht die Gottesfurcht von euch. So hat Er sie euch dienstbar gemacht, damit ihr Allah als den Größten preist, daß Er euch rechtgeleitet hat. Und verkünde frohe Botschaft den Gutes Tuenden." });
                        surah22.VerseList.Add(new Verse { Number = 38, TextBosnian = "I Allah, doista, štiti vjernike, Allah sigurno ne voli nijednog izdajnika, nezahvalnika.", TextEnglish = "Indeed, Allāh defends those who have believed. Indeed, Allāh does not like everyone treacherous and ungrateful.", TextGerman = "Gewiß, Allah verteidigt diejenigen, die glauben. Gewiß, Allah liebt keinen undankbaren Verräter." });
                        surah22.VerseList.Add(new Verse { Number = 39, TextBosnian = "Dopušteno je onima koji su napadnuti da se bore, zato što im je zulum učinjen - a Allah je, doista, kadar da ih pomogne.", TextEnglish = "Permission [to fight] has been given to those who are being fought,[929] because they were wronged. And indeed, Allāh is competent to give them victory.", TextGerman = "Erlaubnis (zum Kampf) ist denjenigen gegeben, die bekämpft werden, weil ihnen ja Unrecht zugefügt wurde - und Allah hat wahrlich die Macht, ihnen zu helfen -," });
                        surah22.VerseList.Add(new Verse { Number = 40, TextBosnian = "Onima koji su bespravno iz zavičaja svoga prognani samo zato što su govorili: 'Gospodar naš je Allah!' A da Allah ne suzbije neke ljude drugima, do temelja bi bile porušene monaške ćelije, i crkve, i sinagoge, a i džamije, u kojima se mnogo spominje Allahovo ime. A Allah će sigurno pomoći one koji Njega pomažuv -Allah je, zaista, moćan i silan.", TextEnglish = "[They are] those who have been evicted from their homes without right - only because they say, 'Our Lord is Allāh.' And were it not that Allāh checks the people, some by means of others, there would have been demolished monasteries, churches, synagogues, and mosques in which the name of Allāh is much mentioned [i.e., praised]. And Allāh will surely support those who support Him [i.e., His cause]. Indeed, Allāh is Powerful and Exalted in Might.", TextGerman = "(ihnen), die zu Unrecht aus ihren Wohnstätten vertrieben wurden, nur weil sie sagen: Unser Herr ist Allah. Und wenn Allah nicht die einen Menschen durch die anderen abgewehrt hätte, so wären fürwahr Mönchsklausen, Kirchen, Bethäuser und Gebetsstätten zerstört worden, in denen Allahs Name häufig genannt wird. Und Allah wird ganz gewiß denjenigen helfen, die Ihm helfen. Allah ist wahrlich Stark und Allmächtig." });
                        surah22.VerseList.Add(new Verse { Number = 41, TextBosnian = "I oni koji će, ako im damo vlast na Zemlji, namaz obavljati i zekat udjeljivati i koji će tražiti da se čine dobra djela, a odvraćati od nevaljalih- a Allahu se, na kraju, sve vraća.", TextEnglish = "[And they are] those who, if We give them authority in the land, establish prayer and give zakāh and enjoin what is right and forbid what is wrong. And to Allāh belongs the outcome of [all] matters.", TextGerman = "(Ihnen), die, wenn Wir Ihnen eine feste Stellung auf der Erde verleihen, das Gebet verrichten und die Abgabe entrichten, das Rechte gebieten und das Verwerfliche verbieten. und Allah gehört das Ende der Angelegenheiten." });
                        surah22.VerseList.Add(new Verse { Number = 42, TextBosnian = "A to što te oni smatraju lažnim, pa i prije njih su narod Nuhov, i Ad, i Semud poslanike smatrali lažnim,", TextEnglish = "And if they deny you, [O Muḥammad] - so, before them, did the people of Noah and ʿAad and Thamūd deny [their prophets],", TextGerman = "Wenn sie dich der Lüge bezichtigen, so haben auch schon vor ihnen das Volk Nuhs, die ´Ad und die Tamud (ihre Gesandten) der Lüge bezichtigt," });
                        surah22.VerseList.Add(new Verse { Number = 43, TextBosnian = "i narod Ibrahimov, i narod Lutov,", TextEnglish = "And the people of Abraham and the people of Lot.", TextGerman = "und (auch) das Volk Ibrahims und das Volk Luts" });
                        surah22.VerseList.Add(new Verse { Number = 44, TextBosnian = "i stanovnici Medjena; a lažnim je smatran i Musa, i Ja sam nevjernicima vremena davao, zatim bi ih dohvatio kaznom, pa kakva je bila osuda Moja?!", TextEnglish = "And the inhabitants of Madyan. And Moses was denied, so I prolonged enjoyment for the disbelievers; then I seized them, and how [terrible] was My reproach.", TextGerman = "und die Bewohner von Madyan. Auch Musa wurde der Lüge bezichtigt. Da gewährte Ich den Ungläubigen Aufschub, hierauf ergriff Ich sie. Wie war da Meine Mißbilligung!" });
                        surah22.VerseList.Add(new Verse { Number = 45, TextBosnian = "I koliko samo ima naselja koja smo uništili, a zulum su činila, pa su pusta i srušena na krovove svoje! I koliko ima bunara zapuštenih i palača visokih!", TextEnglish = "And how many a city did We destroy while it was committing wrong - so it is [now] fallen into ruin[930] - and [how many] an abandoned well and [how many] a lofty palace.[931]", TextGerman = "Wie viele Städte, die Unrecht taten, vernichteten Wir, so daß sie wüst in Trümmern lagen, und (wie viele nun) verlassene Brunnen und hochragende Schlösser!" });
                        surah22.VerseList.Add(new Verse { Number = 46, TextBosnian = "Zašto oni ne putuju po Zemlji pa da srca njihova shvate ono što treba da shvate, i da uši njihove čuju ono što treba da čuju, ali, oči nisu slijepe, već srca u grudima.", TextEnglish = "So have they not traveled through the earth and have hearts by which to reason and ears by which to hear? For indeed, it is not eyes that are blinded, but blinded are the hearts which are within the breasts.", TextGerman = "Reisen sie denn nicht auf der Erde umher, so daß sie Herzen bekommen, mit denen sie begreifen, oder Ohren, mit denen sie hören? Denn nicht die Blicke sind blind, sondern blind sind die Herzen, die in den Brüsten sind." });
                        surah22.VerseList.Add(new Verse { Number = 47, TextBosnian = "Oni od tebe traže da se patnja ubrza, a Allah neće iznevjeriti obećanje Svoje; samo jedan dan u Gospodara tvoga traje koliko hiljadu godina po vašem računanju.", TextEnglish = "And they urge you to hasten the punishment. But Allāh will never fail in His promise. And indeed, a day with your Lord is like a thousand years of those which you count.", TextGerman = "Und sie wünschen von dir, die Strafe zu beschleunigen. Allah wird Sein Versprechen nicht brechen. Und gewiß, ein Tag bei deinem Herrn ist wie tausend Jahre nach eurer Berechnung." });
                        surah22.VerseList.Add(new Verse { Number = 48, TextBosnian = "I koliko je bilo naselja kojima sam vremena davao, a zulum su činila, da bih ih najzad, kaznom dohvatio! A kod Mene je konačno odredište!", TextEnglish = "And for how many a city did I prolong enjoyment while it was committing wrong. Then I seized it, and to Me is the [final] destination.", TextGerman = "Und wie vielen Städten, die Unrecht taten, gewährte Ich Aufschub! Hierauf ergriff Ich sie, und zu Mir ist der Ausgang." });
                        surah22.VerseList.Add(new Verse { Number = 49, TextBosnian = "Reci: 'O ljudi, ja sam vam samo upozoritelj jasni!',", TextEnglish = "Say, 'O people, I am only to you a clear warner.'", TextGerman = "Sag: O ihr Menschen, ich bin euch nur ein deutlicher Warner." });
                        surah22.VerseList.Add(new Verse { Number = 50, TextBosnian = "oni koji budu vjerovali i dobra djela činili imat će oprost i opskrbu plemenitu;", TextEnglish = "And those who have believed and done righteous deeds - for them is forgiveness and noble provision.", TextGerman = "Diejenigen nun, die glauben und rechtschaffene Werke tun, für sie wird es Vergebung und ehrenvolle Versorgung geben." });
                        surah22.VerseList.Add(new Verse { Number = 51, TextBosnian = "a oni koji se budu trudili da ajete Naše poreknu, odvraćajući od njih, bit će stanovnici Džehennema.", TextEnglish = "But the ones who strove against Our verses, [seeking] to cause failure[932] - those are the companions of Hellfire.", TextGerman = "Diejenigen hingegen, die ständig gegen Unsere Zeichen eifern und ihnen Machtlosigkeit nachweisen wollen, das sind Insassen des Höllenbrandes." });
                        surah22.VerseList.Add(new Verse { Number = 52, TextBosnian = "Prije tebe, Mi nijednog poslanika i vjerovjesnika nismo poslali a da šejtan nije, kad bi on kazivao, u kazivanje njegovo nešto ubacio. Allah bi ono što bi šejtan ubacio uklonio, a zatim bi ajete Svoje učvrstio, a Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "And We did not send before you any messenger or prophet except that when he spoke [or recited], Satan threw into it [some misunderstanding]. But Allāh abolishes that which Satan throws in; then Allāh makes precise His verses.[933] And Allāh is Knowing and Wise.", TextGerman = "Und Wir sandten vor dir keinen Gesandten oder Propheten, ohne daß ihm, wenn er etwas wünschte, der Satan in seinen Wunsch etwas dazwischen geworfen hätte. Aber Allah hebt auf, was der Satan dazwischenwirft. Hierauf legt Allah Seine Zeichen eindeutig fest. Und Allah ist Allwissend und Allweise." });
                        surah22.VerseList.Add(new Verse { Number = 53, TextBosnian = "To je zato da ono što šejtan ubaci učini iskušenjem za one u čijim srcima je bolest i za one čija su srca okrutna - a zulumćari su, zaista, u rascjepu i na stranputici dalekoj,", TextEnglish = "[That is] so He may make what Satan throws in [i.e., asserts] a trial for those within whose hearts is disease[934] and those hard of heart. And indeed, the wrongdoers are in extreme dissension.", TextGerman = "(Das ist so,) damit Er das, was der Satan dazwischenwirft, zu einer Versuchung für diejenigen macht, in deren Herzen Krankheit ist und deren Herzen verhärtet sind -die Ungerechten befinden sich wahrlich in tiefem Widerstreit -," });
                        surah22.VerseList.Add(new Verse { Number = 54, TextBosnian = "i da bi oni koji su znanjem obdareni spoznali da je Kur 'an Istina od Gospodara tvoga, pa u njega povjerovali i da mu srca njihova budu ponizna. Zaista je onima koji vjeruju Allah Uputitelj na Pravi put.", TextEnglish = "And so those who were given knowledge may know that it is the truth from your Lord and [therefore] believe in it, and their hearts humbly submit to it. And indeed is Allāh the Guide of those who have believed to a straight path.", TextGerman = "und damit diejenigen, denen das Wissen gegeben wurde, wissen, daß dies die Wahrheit von deinem Herrn ist, so daß sie daran glauben und ihre Herzen sich dann davor demütigen. Und Allah leitet wahrlich diejenigen, die glauben, zu einem geraden Weg." });
                        surah22.VerseList.Add(new Verse { Number = 55, TextBosnian = "A oni koji ne vjeruju neće prestati da u njega sumnjaju, sve dok im Čas iznenada ne dođe ili dok im ne dođe patnja u danu nerodnom!", TextEnglish = "But those who disbelieve will not cease to be in doubt of it until the Hour comes upon them unexpectedly or there comes to them the punishment of a barren Day.[935]", TextGerman = "Und diejenigen, die ungläubig sind, werden nicht aufhören, daran zu zweifeln, bis plötzlich die Stunde über sie kommt oder die Strafe eines unheilvollen Tages über sie kommt." });
                        surah22.VerseList.Add(new Verse { Number = 56, TextBosnian = "Taj dan vlast će samo Allah imati, On će im suditi. A vjernici i oni koji su dobra djela činili bit će u baščama u kojima se uživa,", TextEnglish = "[All] sovereignty that Day is for Allāh;[936] He will judge between them. So they who believed and did righteous deeds will be in the Gardens of Pleasure.", TextGerman = "Die Herrschaft wird an jenem Tag Allah (allein) gehören. Er wird zwischen ihnen richten. Dann werden diejenigen, die glauben und rechtschaffene Werke tun, in den Gärten der Wonne sein." });
                        surah22.VerseList.Add(new Verse { Number = 57, TextBosnian = "a oni koji nisu vjerovali i ajete i znakove Naše poricali, oni će patnju ponižavajuću imati.", TextEnglish = "And they who disbelieved and denied Our signs - for those there will be a humiliating punishment.", TextGerman = "Diejenigen aber, die ungläubig sind und Unsere Zeichen für Lüge erklären, für sie wird es schmachvolle Strafe geben." });
                        surah22.VerseList.Add(new Verse { Number = 58, TextBosnian = "Allah će, zaista, lijepo opskrbiti one koji su svoj rodni kraj napustili da bi se na Allahovu putu borili, pa zatim poginuli ili umrli, a zaista je Allah, baš On, najbolji Opskrbitelj.", TextEnglish = "And those who emigrated for the cause of Allāh and then were killed or died - Allāh will surely provide for them a good provision. And indeed, it is Allāh who is the best of providers.", TextGerman = "Denjenigen, die auf Allahs Weg auswandern, hierauf getötet werden oder sterben, wird Allah ganz gewiß eine schöne Versorgung gewähren. Allah ist wahrlich der beste Versorger." });
                        surah22.VerseList.Add(new Verse { Number = 59, TextBosnian = "On će ih u mjesto kojim će oni biti zadovoljni, sigurno, uvesti. A Allah je, doista, Onaj Koji sve zna i Onaj Koji ne žuri s kaznom.", TextEnglish = "He will surely cause them to enter an entrance with which they will be pleased, and indeed, Allāh is Knowing and Forbearing.", TextGerman = "Er wird ihnen ganz gewiß einen Eingang gewähren, mit dem sie zufrieden sind. Und Allah ist wahrlich Allwissend und Nachsichtig." });
                        surah22.VerseList.Add(new Verse { Number = 60, TextBosnian = "Eto tako! A onome ko kazni isto onako kako je kažnjen, i kome opet nepravda bude učinjena, Allah će mu, sigurno, pomoći - Allah je, zaista, Onaj Koji stalno poništava grijehe i Onaj Koji oprašta.", TextEnglish = "That [is so]. And whoever responds [to injustice] with the equivalent of that with which he was harmed and then is tyrannized - Allāh will surely aid him. Indeed, Allāh is Pardoning and Forgiving.[937]", TextGerman = "Dies, und wer im gleichen Maß bestraft, wie er bestraft wurde, hierauf aber (wieder zu Unrecht) angegriffen wird, dem wird Allah ganz gewiß helfen. Allah ist wahrlich Allverzeihend und Allvergebend." });
                        surah22.VerseList.Add(new Verse { Number = 61, TextBosnian = "Eto zato, što Allah uvodi noć u dan, i uvodi dan u noć, i što je Allah, doista, Onaj Koji sve čuje i Onaj Koji sve vidi.", TextEnglish = "That[938] is because Allāh causes the night to enter the day and causes the day to enter the night and because Allāh is Hearing and Seeing.", TextGerman = "Dies, weil Allah die Nacht in den Tag eindringen und den Tag in die Nacht eindringen läßt und weil Allah Allhörend und Allsehend ist." });
                        surah22.VerseList.Add(new Verse { Number = 62, TextBosnian = "To je zato što je Allah - Istina, a oni koje oni, pored Allaha, mole - laž, i zato što je Allah Svevišnji i Veliki.", TextEnglish = "That is because Allāh is the True Reality, and that which they call upon other than Him is falsehood, and because Allāh is the Most High, the Grand.", TextGerman = "Dies, weil Allah die Wahrheit ist und weil das, was sie anstatt Seiner anrufen, das Falsche ist, und weil Allah der Erhabene, der Große ist." });
                        surah22.VerseList.Add(new Verse { Number = 63, TextBosnian = "Zar ne vidiš da Allah s neba spušta vodu od koje zemlja zazeleni!? Allah je, zaista, znalac i onoga što je najsitnije i On sve u potpunosti zna.", TextEnglish = "Do you not see that Allāh has sent down rain from the sky and the earth becomes green? Indeed, Allāh is Subtle[939] and Aware.[940]", TextGerman = "Siehst du nicht, daß Allah vom Himmel Wasser herabkommen läßt, so daß die Erde grün wird? Gewiß, Allah ist Feinfühlig und Allkundig." });
                        surah22.VerseList.Add(new Verse { Number = 64, TextBosnian = "Njegovo je ono što je na nebesima i ono što je na Zemlji? Allah je, doista, Neovisni i Hvaljeni!", TextEnglish = "To Him belongs what is in the heavens and what is on the earth. And indeed, Allāh is the Free of need, the Praiseworthy.", TextGerman = "Ihm gehört (alles), was in den Himmeln und was auf der Erde ist; Allah ist wahrlich der Unbedürftige und Lobenswürdige." });
                        surah22.VerseList.Add(new Verse { Number = 65, TextBosnian = "Zar ne vidiš da je Allah sve što je na Zemlji vama podredio, pa i lađe koje, odredbom Njegovom, morem plove?! On drži ono što je na nebu da ne bi palo na Zemlju, osim ako On to dopusti. Allah je, uistinu, prema ljudima milosrdan i milostiv.", TextEnglish = "Do you not see that Allāh has subjected to you whatever is on the earth and the ships which run through the sea by His command? And He restrains the sky from falling upon the earth, unless by His permission. Indeed Allāh, to the people, is Kind and Merciful.", TextGerman = "Siehst du nicht, daß Allah euch (all) das, was auf der Erde ist, dienstbar gemacht hat, und (ebenso) die Schiffe, die nach Seinem Befehl auf dem Meer fahren? Und Er hält den Himmel, daß er nicht auf die Erde fällt, außer mit Seiner Erlaubnis. Gewiß, Allah ist zu den Menschen wahrlich Gnädig und Barmherzig." });
                        surah22.VerseList.Add(new Verse { Number = 66, TextBosnian = "On vam daje život i zatim će vam ga oduzeti i poslije vas opet oživiti; čovjek je, zaista, poricatelj pravi!", TextEnglish = "And He is the one who gave you life; then He causes you to die and then will [again] give you life. Indeed, mankind is ungrateful.", TextGerman = "Und Er ist es, Der euch lebendig gemacht hat, euch hierauf sterben läßt, euch hierauf wieder lebendig macht. Der Mensch ist wahrlich sehr undankbar." });
                        surah22.VerseList.Add(new Verse { Number = 67, TextBosnian = "Svakoj vjerskoj zajednici propisali smo vjerozakon prema kome je trebalo da se vlada; i ne dozvoli, nikako, da s tobom o tome raspravljaju. Ti pozivaj svome Gospodaru, ti si, uistinu, na Pravom putu.", TextEnglish = "For every [religious] community We have appointed rites which they perform. So, [O Muḥammad], let them [i.e., the disbelievers] not contend with you over the matter but invite [them] to your Lord. Indeed, you are upon straight guidance.", TextGerman = "Für jede Gemeinschaft haben Wir einen Ritus festgelegt, den sie (zu) vollziehen (haben); so sollen sie mit dir nicht über die Angelegenheit streiten. Und rufe zu deinem Herrn. Du verfährst fürwahr nach einer geraden Rechtleitung." });
                        surah22.VerseList.Add(new Verse { Number = 68, TextBosnian = "A ako se oni budu s tobom prepirali, reci: 'Allah najbolje zna ono što vi radite!'", TextEnglish = "And if they dispute with you, then say, 'Allāh is most knowing of what you do.", TextGerman = "Und wenn sie (doch) mit dir streiten, dann sag: Allah weiß sehr wohl, was ihr tut." });
                        surah22.VerseList.Add(new Verse { Number = 69, TextBosnian = "Allah će vam na Kijametskom danu presuditi o onome u čemu se razilazite.", TextEnglish = "Allāh will judge between you on the Day of Resurrection concerning that over which you used to differ.'", TextGerman = "Allah wird zwischen euch am Tag der Auferstehung über das richten, worüber ihr uneinig zu sein pflegtet." });
                        surah22.VerseList.Add(new Verse { Number = 70, TextBosnian = "Zar ne znaš da Allah zna sve što je na nebu i na Zemlji?! To je sve u Knjizi, to je, uistinu, Allahu lahko!", TextEnglish = "Do you not know that Allāh knows what is in the heaven and earth? Indeed, that is in a Record.[941] Indeed that, for Allāh, is easy.", TextGerman = "Weißt du denn nicht, daß Allah weiß, was im Himmel und auf der Erde ist? Gewiß, das steht in einem Buch; gewiß, das ist Allah ein leichtes." });
                        surah22.VerseList.Add(new Verse { Number = 71, TextBosnian = "Višebošci, pored Allaha, obožavaju one za koje im On nije poslao nikakav dokaz, i o njima sami ništa ne znaju. A zulumćarima nema pomagača.", TextEnglish = "And they worship besides Allāh that for which He has not sent down authority and that of which they have no knowledge. And there will not be for the wrongdoers any helper.", TextGerman = "Und sie dienen anstatt Allahs dem, wofür Er keine Ermächtigung herabgesandt hat und wovon sie kein Wissen haben. Und es wird für die Ungerechten keinen Helfer geben." });
                        surah22.VerseList.Add(new Verse { Number = 72, TextBosnian = "A kad im se Naši jasni ajeti kazuju, ti na licima onih koji ne vjeruju razaznaješ zlo. Oni umalo da ne nasrnu na one koji im riječi Naše kazuju. Reci: 'Hoćete li da vam kažem šta je gore od toga? Vatra koju je Allah onima koji ne vjeruju obećao, a užasno je ona krajnje odredište!'", TextEnglish = "And when Our verses are recited to them as clear evidences, you recognize in the faces of those who disbelieve disapproval. They are almost on the verge of assaulting those who recite to them Our verses. Say, 'Then shall I inform you of [what is] worse than that?[942] [It is] the Fire which Allāh has promised those who disbelieve, and wretched is the destination.'", TextGerman = "Und wenn ihnen Unsere Zeichen als klare Beweise verlesen werden, erkennst du in den Gesichtern derjenigen, die ungläubig sind, Mißbilligung. Beinahe würden sie über diejenigen herfallen, die ihnen Unsere Zeichen verlesen. Sag: Soll ich euch denn kundtun, was schlechter ist als dies? (Es ist) das (Höllen)feuer; Allah hat es denjenigen angedroht, die ungläubig sind - ein schlimmer Ausgang!" });
                        surah22.VerseList.Add(new Verse { Number = 73, TextBosnian = "O ljudi, evo jednog primjera, pa ga poslušajte: 'Oni koje vi, pored Allaha molite, ne mogu nikako ni mušicu stvoriti, makar se radi nje sakupili. A ako bi im mušica nešto ugrabila, oni to ne bi mogli od nje izbaviti, nejak je i onaj koji traži i ono što se traži!", TextEnglish = "O people, an example is presented, so listen to it. Indeed, those you invoke besides Allāh will never create [as much as] a fly, even if they gathered together for it [i.e., that purpose]. And if the fly should steal from them a [tiny] thing, they could not recover it from him. Weak are the pursuer and pursued.[943]", TextGerman = "O ihr Menschen, ein Gleichnis wird (euch) angeführt, so hört darauf. Gewiß, diejenigen, die ihr anstatt Allahs anruft, werden nicht (einmal) eine Fliege erschaffen (können), auch wenn sie sich dafür zusammentäten. Und wenn die Fliege ihnen etwas raubte, könnten sie es ihr nicht entreißen. Schwach ist (hier) derjenige, der ersucht, und derjenige, der ersucht wird." });
                        surah22.VerseList.Add(new Verse { Number = 74, TextBosnian = "Oni ne veličaju i ne poznaju Allaha kako treba, a Allah je, uistinu, moćan i silan.", TextEnglish = "They have not appraised Allāh with true appraisal.[944] Indeed, Allāh is Powerful and Exalted in Might.", TextGerman = "Sie schätzen Allah nicht ein, wie es Ihm gebührt. Allah ist wahrlich Stark und Allmächtig." });
                        surah22.VerseList.Add(new Verse { Number = 75, TextBosnian = "Allah odabire izaslanike među melecima i ljudima-Allah je, zaista, Onaj Koji sve čuje i Onaj Koji sve vidi.", TextEnglish = "Allāh chooses from the angels messengers and from the people. Indeed, Allāh is Hearing and Seeing.", TextGerman = "Allah erwählt Sich aus den Engeln Gesandte, und (auch) aus den Menschen. Gewiß, Allah ist Allhörend und Allsehend." });
                        surah22.VerseList.Add(new Verse { Number = 76, TextBosnian = "On zna šta su uradili i šta će uraditi. A Allahu se sve vraća.", TextEnglish = "He knows what is [presently] before them and what will be after them.[945] And to Allāh will be returned [all] matters.", TextGerman = "Er weiß, was vor ihnen und was hinter ihnen liegt. Und zu Allah werden (all) die Angelegenheiten zurückgebracht." });
                        surah22.VerseList.Add(new Verse { Number = 77, TextBosnian = "O vjernici, ruku' i sedždu obavljajte i Gospodaru svome ibadet činite, i dobra djela činite da biste postigli ono što želite.", TextEnglish = "O you who have believed, bow and prostrate and worship your Lord and do good - that you may succeed.", TextGerman = "O die ihr glaubt, verbeugt euch, werft euch nieder und dient eurem Herrn und tut das Gute, auf daß es euch wohl ergehen möge!" });
                        surah22.VerseList.Add(new Verse { Number = 78, TextBosnian = "I borite se, Allaha radi, istinski, onako kako se treba boriti! On je vas izabrao i u vjeri vam nije ništa teško propisao, u vjeri pretka vašeg Ibrahima. Allah vas je otprije muslimanima nazvao, i u ovom Kur'anu, da bi Poslanik bio svjedok protiv vas, i da biste vi bili svjedoci protiv ostalih ljudi. Zato, namaz obavljajte i zekat dajite i Allaha se čvrsto držite. On je Gospodar vaš. Divnog li Gospodara i divnog li Pomagača!", TextEnglish = "And strive for Allāh with the striving due to Him. He has chosen you and has not placed upon you in the religion any difficulty. [It is] the religion of your father, Abraham. He [i.e., Allāh] named you 'Muslims' before [in former scriptures] and in this [revelation] that the Messenger may be a witness over you and you may be witnesses over the people. So establish prayer and give zakāh and hold fast to Allāh. He is your protector; and excellent is the protector, and excellent is the helper.", TextGerman = "Und müht euch für Allah ab, wie der wahre Einsatz für Ihn sein soll. Er hat euch erwählt und euch in der Religion keine Bedrängnis auferlegt, dem Glaubensbekenntnis eures Vaters Ibrahim: Er hat euch Muslime genannt, zuvor und (nunmehr) in diesem (Qur’an), damit der Gesandte Zeuge über euch sei und ihr Zeugen über die Menschen seid. So verrichtet das Gebet, entrichtet die Abgabe und haltet an Allah fest. Er ist euer Schutzherr. Wie trefflich ist doch der Schutzherr, und wie trefflich ist der Helfer!" });
                        result = surah22;
                        break;
                    #endregion

                    // 
                    #region
                    case 23:
                        Surah surah23 = new Surah(); surah23.Name = Constants.Surah23Name; surah23.Number = 23; surah23.VerseList = new List<Verse>();
                        surah23.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah23.VerseList.Add(new Verse { Number = 1, TextBosnian = "Uspjeli su vjernici!", TextEnglish = "Certainly will the believers have succeeded:", TextGerman = "Den Gläubigen wird es ja wohl ergehen," });
                        surah23.VerseList.Add(new Verse { Number = 2, TextBosnian = "oni koji su u svom namazu skrušeni,", TextEnglish = "They who are during their prayer humbly intent", TextGerman = "denjenigen, die in ihrem Gebet demütig sind," });
                        surah23.VerseList.Add(new Verse { Number = 3, TextBosnian = "i koji se od besmislica okreću,", TextEnglish = "And they who turn away from ill speech", TextGerman = "und denjenigen, die sich von unbedachter Rede abwenden," });
                        surah23.VerseList.Add(new Verse { Number = 4, TextBosnian = "i koji zekat - čišćenje izvršavaju,", TextEnglish = "And they who are observant of zakāh", TextGerman = "und denjenigen, die die (Zahlung der) Abgabe anwenden," });
                        surah23.VerseList.Add(new Verse { Number = 5, TextBosnian = "i koji stidna mjesta svoja čuvaju,", TextEnglish = "And they who guard their private parts", TextGerman = "und diejenigen, die ihre Scham hüten," });
                        surah23.VerseList.Add(new Verse { Number = 6, TextBosnian = "osim od supruga svojih ili onih koje su u posjedu njihovu; oni, doista, neće biti prekoreni;", TextEnglish = "Except from their wives or those their right hands possess,[947] for indeed, they will not be blamed -", TextGerman = "außer gegenüber ihren Gattinnen oder was ihre rechte Hand (an Sklavinnen) besitzt, denn sie sind (hierin) nicht zu tadeln," });
                        surah23.VerseList.Add(new Verse { Number = 7, TextBosnian = "a oni koji i pored toga nešto traže, to su baš oni koji prelaze granicu;", TextEnglish = "But whoever seeks beyond that, then those are the transgressors -", TextGerman = "- wer aber darüber hinaus (etwas) begehrt, das sind die Übertreter-," });
                        surah23.VerseList.Add(new Verse { Number = 8, TextBosnian = "i koji na povjerene im amanete i obaveze svoje paze,", TextEnglish = "And they who are to their trusts and their promises attentive", TextGerman = "und diejenigen, die auf die ihnen anvertrauten Güter und ihre Verpflichtung achtgeben," });
                        surah23.VerseList.Add(new Verse { Number = 9, TextBosnian = "i koji namaze svoje ustrajno čuvaju", TextEnglish = "And they who carefully maintain their prayers -", TextGerman = "und denjenigen, die ihre Gebete einhalten." });
                        surah23.VerseList.Add(new Verse { Number = 10, TextBosnian = "oni su baš nasljednici,", TextEnglish = "Those are the inheritors", TextGerman = "Das sind die Erben," });
                        surah23.VerseList.Add(new Verse { Number = 11, TextBosnian = "koji će Firdevs naslijediti, oni će u njemu vječno boraviti.", TextEnglish = "Who will inherit al-Firdaus.[948] They will abide therein eternally.", TextGerman = "die das Paradies erben werden; ewig werden sie darin bleiben." });
                        surah23.VerseList.Add(new Verse { Number = 12, TextBosnian = "Mi smo, zaista, čovjeka od uzorka gline stvorili,", TextEnglish = "And certainly did We create man from an extract of clay.", TextGerman = "Wir schufen den Menschen ja aus einem Auszug aus Lehm." });
                        surah23.VerseList.Add(new Verse { Number = 13, TextBosnian = "zatim činimo da kao kap sjemena u stanište pripremljeno dospije,", TextEnglish = "Then We placed him as a sperm-drop[949] in a firm lodging [i.e., the womb].", TextGerman = "Hierauf machten Wir ihn zu einem Samentropfen in einem festen Aufenthaltsort." });
                        surah23.VerseList.Add(new Verse { Number = 14, TextBosnian = "pa onda kap sjemena ugruškom stvorimo, zatim od ugruška zakvačak stvorimo, pa od zakvačka kosti stvorimo, a onda kosti mesom zaodjenemo, i poslije ga, kao drugo stvorenje, oformimo, pa neka je uzvišen Allah, Najbolji Stvoritelj!", TextEnglish = "Then We made the sperm-drop into a clinging clot, and We made the clot into a lump [of flesh], and We made [from] the lump, bones, and We covered the bones with flesh; then We developed him into another creation. So blessed is Allāh, the best of creators.[950]", TextGerman = "Hierauf schufen Wir den Samentropfen zu einem Anhängsel, dann schufen Wir das Anhängsel zu einem kleinen Klumpen, dann schufen Wir den kleinen Klumpen zu Knochen, dann bekleideten Wir die Knochen mit Fleisch. Hierauf ließen Wir ihn als eine weitere Schöpfung entstehen. Segensreich ist Allah, der beste Schöpfer." });
                        surah23.VerseList.Add(new Verse { Number = 15, TextBosnian = "Vi ćete, poslije toga, zaista, pomrijeti,", TextEnglish = "Then indeed, after that you are to die.", TextGerman = "Hierauf werdet ihr gewiß nach (all) diesem sterben." });
                        surah23.VerseList.Add(new Verse { Number = 16, TextBosnian = "zatim ćete, na Kijametskom danu, oživljeni biti.", TextEnglish = "Then indeed you, on the Day of Resurrection, will be resurrected.", TextGerman = "Hierauf werdet ihr gewiß am Tag der Auferstehung auferweckt werden." });
                        surah23.VerseList.Add(new Verse { Number = 17, TextBosnian = "Mi smo sedam slojeva nebeskih iznad vas stvorili, i Mi prema stvorenjima nismo nemarni.", TextEnglish = "And We have created above you seven layered heavens, and never have We been of [Our] creation unaware.", TextGerman = "Und Wir schufen ja über euch sieben Lagen, und Wir sind der Schöpfung nicht unachtsam." });
                        surah23.VerseList.Add(new Verse { Number = 18, TextBosnian = "Mi s neba s mjerom vodu spuštamo, i u zemlji je zadržavamo - a kadri smo i da je odvedemo", TextEnglish = "And We have sent down rain from the sky in a measured amount and settled it in the earth. And indeed, We are Able to take it away.", TextGerman = "Und Wir lassen vom Himmel Wasser in (bestimmtem) Maß herabkommen und lassen es sich dann in der Erde aufhalten. Und Wir haben fürwahr die Macht, es (wieder) wegzunehmen." });
                        surah23.VerseList.Add(new Verse { Number = 19, TextBosnian = "i pomoću nje bašče za vas podižemo od palmi i loze vinove - u njima mnogo voća imate, i vi ga jedete", TextEnglish = "And We brought forth for you thereby gardens of palm trees and grapevines in which for you are abundant fruits and from which you eat.", TextGerman = "Dann lassen Wir euch dadurch Gärten mit Palmen und Rebstöcken entstehen, in denen ihr vielerlei Früchte habt und von denen ihr eßt," });
                        surah23.VerseList.Add(new Verse { Number = 20, TextBosnian = "i drvo koje na Sinajskoj gori raste, koje zejtin daje, i začin je onima koji jedu.", TextEnglish = "And [We brought forth] a tree issuing from Mount Sinai which produces oil and food [i.e., olives] for those who eat.", TextGerman = "und einen Baum, der aus dem Berg Saina herauskommt, der Öl hervorbringt und auch Tunke für diejenigen, die essen." });
                        surah23.VerseList.Add(new Verse { Number = 21, TextBosnian = "I stoka vam je pouka: Mi vam dajemo da pijete ono što se nalazi u utrobama njezinim, i vi od nje mnogo koristi imate i vi se njome hranite,", TextEnglish = "And indeed, for you in livestock is a lesson. We give you drink from that which is in their bellies, and for you in them are numerous benefits, and from them you eat.", TextGerman = "Und (auch) im Vieh habt ihr wahrlich eine Lehre. Wir geben euch von dem, was in ihren Leibern ist, zu trinken. An ihnen habt ihr vielerlei Nutzen, und davon eßt ihr." });
                        surah23.VerseList.Add(new Verse { Number = 22, TextBosnian = "i na njima, i na lađama se vozite.", TextEnglish = "And upon them and on ships you are carried.", TextGerman = "Und auf ihnen und auf den Schiffen werdet ihr getragen." });
                        surah23.VerseList.Add(new Verse { Number = 23, TextBosnian = "Mi smo poslali Nuha narodu njegovu, i on je govorio: 'O narode moj, samo Allahu ibadet činite, vi drugog boga osim Njega nemate; zar se sačuvati nećete?!'", TextEnglish = "And We had certainly sent Noah to his people, and he said, 'O my people, worship Allāh; you have no deity other than Him; then will you not fear Him?'", TextGerman = "Und Wir sandten bereits Nuh zu seinem Volk. Da sagte er: „O mein Volk, dient Allah. Keinen Gott habt ihr außer Ihm. Wollt ihr denn nicht gottesfürchtig sein?" });
                        surah23.VerseList.Add(new Verse { Number = 24, TextBosnian = "Ali su glavešine naroda njegova, koje nisu vjerovale, govorile: 'Ovo je čovjek kao i vi, samo hoće da nad vama bude odlikovan. Da je Allah htio, meleke bi poslao; ovako nešto nismo čuli od naših predaka davnih,", TextEnglish = "But the eminent among those who disbelieved from his people said, 'This is not but a man like yourselves who wishes to take precedence over you; and if Allāh had willed [to send a messenger], He would have sent down angels. We have not heard of this among our forefathers.", TextGerman = "Da sagte die aus seinem Volk führende Schar, die ungläubig war: „Dieser ist nur ein menschliches Wesen wie ihr, das einen Vorzug euch gegenüber haben will. Und wenn Allah gewollt hätte, hätte Er wahrlich Engel herabgesandt. Wir haben so etwas bei unseren Vorvätern nicht gehört." });
                        surah23.VerseList.Add(new Verse { Number = 25, TextBosnian = "on nije ništa doli čovjek u ludilu, pa sačekajte s njim vrijeme neko!'", TextEnglish = "He is not but a man possessed with madness, so wait concerning him for a time.'", TextGerman = "Er ist nur ein Mann, der an Besessenheit leidet; so wartet mit ihm für eine gewisse Zeit ab." });
                        surah23.VerseList.Add(new Verse { Number = 26, TextBosnian = "Gospodaru moj', reče on, 'pomozi mi, zato što me u laž utjeruju!'", TextEnglish = "[Noah] said, 'My Lord, support me because they have denied me.'", TextGerman = "Er sagte: „Mein Herr, hilf mir, wo sie mich der Lüge bezichtigen." });
                        surah23.VerseList.Add(new Verse { Number = 27, TextBosnian = "Pa mu Mi objavismo: 'Lađu pred očima Našim i po objavi Našoj pravi, pa kad zapovijed Naša dođe i voda na površinu zemlje izbije, ti u nju ukrcaj od svake vrste po dvoje - mužjaka i ženku, i porodicu svoju, ali ne i one od njih za koje je prethodila Riječ, i ne obraćaj Mi se za one koji su zulum učinili, jer će, doista, biti potopljeni.", TextEnglish = "So We inspired to him, 'Construct the ship under Our observation and Our inspiration, and when Our command comes and the oven overflows,[951] put into it [i.e., the ship] from each [creature] two mates and your family, except him for whom the decree [of destruction] has proceeded. And do not address Me concerning those who have wronged; indeed, they are to be drowned.", TextGerman = "Da gaben Wir ihm ein: „Verfertige das Schiff vor Unseren Augen und nach Unserer Eingebung. Wenn dann Unser Befehl kommt und der Ofen brodelt, so führe von jeder (Art) ein Paar und deine Angehörigen hinein, außer gegen wen das Wort vorher ergangen ist. Und sprich Mich nicht an zugunsten derjenigen, die Unrecht getan haben; sie werden ertränkt werden." });
                        surah23.VerseList.Add(new Verse { Number = 28, TextBosnian = "Pa kad se smjestiš u lađu, ti i oni koji su uz tebe, reci: 'Hvala Allahu, Koji nas je spasio naroda zulumćarskog!'", TextEnglish = "And when you have boarded the ship, you and those with you, then say, 'Praise to Allāh who has saved us from the wrongdoing people.'", TextGerman = "Und wenn du dich nun auf dem Schiff eingerichtet hast, du und diejenigen, die mit dir sind, dann sag: (Alles) Lob gehört Allah, Der uns von dem ungerechten Volk errettet hat!" });
                        surah23.VerseList.Add(new Verse { Number = 29, TextBosnian = "i reci: 'Gospodaru moj, spusti me na blagoslovljeno mjesto, Ti si najbolji Nastanitelj!''", TextEnglish = "And say, 'My Lord, let me land at a blessed landing place, and You are the best to accommodate [us].''", TextGerman = "Und sag: Mein Herr, gewähre mir einen gesegneten Abstieg’, denn Du bist der Beste derjenigen, die Abstieg gewähren." });
                        surah23.VerseList.Add(new Verse { Number = 30, TextBosnian = "U tome su, zaista, znakovi, a Mi smo, doista, stavljali na kušnju", TextEnglish = "Indeed in that are signs, and indeed, We are ever testing [Our servants].", TextGerman = "Darin sind wahrlich Zeichen. Wir haben (sie damit) fürwahr geprüft." });
                        surah23.VerseList.Add(new Verse { Number = 31, TextBosnian = "Poslije njih smo druge ljude stvorili", TextEnglish = "Then We produced after them a generation of others.", TextGerman = "Hierauf ließen Wir nach ihnen ein anderes Geschlecht entstehen." });
                        surah23.VerseList.Add(new Verse { Number = 32, TextBosnian = "i jednog između njih smo im kao poslanika poslali: 'Samo Allahu ibadet činite, vi drugog boga osim Njega nemate; zar se sačuvati nećete?!'", TextEnglish = "And We sent among them a messenger[952] from themselves, [saying], 'Worship Allāh; you have no deity other than Him; then will you not fear Him?'", TextGerman = "Dann schickten Wir zu ihnen einen Gesandten von ihnen: „Dient Allah! Keinen Gott habt ihr außer Ihm. Wollt ihr denn nicht gottesfürchtig sein?" });
                        surah23.VerseList.Add(new Verse { Number = 33, TextBosnian = "Ali su glavešine naroda njegova, koje nisu vjerovale, koje su poricale da će na ahiretu biti oživljeni, i kojima smo dali da u životu na dunjaluku raskošno žive, govorile: 'On je čovjek kao i vi; jede ono što i vi jedete, i pije ono što i vi pijete;", TextEnglish = "And the eminent among his people who disbelieved and denied the meeting of the Hereafter while We had given them luxury in the worldly life said, 'This is not but a man like yourselves. He eats of that from which you eat and drinks of what you drink.", TextGerman = "Und die führende Schar aus seinem Volk, die ungläubig war und die Begegnung mit dem Jenseits für Lüge erklärte und der Wir im diesseitigen Leben üppigen Wohlstand verliehen hatten, sagte: „Dieser ist nur ein menschliches Wesen wie ihr, das von dem ißt, was ihr eßt, und von dem trinkt, was ihr trinkt." });
                        surah23.VerseList.Add(new Verse { Number = 34, TextBosnian = "i ako se budete pokoravali čovjeku kao što ste vi, sigurno ćete biti gubitnici.", TextEnglish = "And if you should obey a man like yourselves, indeed, you would then be losers.", TextGerman = "Wenn ihr einem menschlichen Wesen euresgleichen gehorcht, dann werdet ihr fürwahr Verlierer sein." });
                        surah23.VerseList.Add(new Verse { Number = 35, TextBosnian = "Zar vama on da obećava da ćete, pošto pomrete i zemlja i kosti postanete, doista, oživljeni biti?!", TextEnglish = "Does he promise you that when you have died and become dust and bones that you will be brought forth [once more]?", TextGerman = "Verspricht er euch etwa, daß ihr, wenn ihr gestorben und zu Erde und Knochen geworden seid, (wieder) hervorgebracht werdet?" });
                        surah23.VerseList.Add(new Verse { Number = 36, TextBosnian = "Daleko je, daleko ono što vam se obećava!", TextEnglish = "How far, how far, is that which you are promised.[953]", TextGerman = "Weit, weit entfernt ist das, was euch versprochen wird." });
                        surah23.VerseList.Add(new Verse { Number = 37, TextBosnian = "Postoji samo naš život dunjalučki, mi živimo i umiremo, a oživljeni nećemo biti.", TextEnglish = "It [i.e., life] is not but our worldly life - we die and live, but we will not be resurrected.", TextGerman = "Es gibt nur unser diesseitiges Leben: Wir sterben und wir leben, und wir werden nicht auferweckt." });
                        surah23.VerseList.Add(new Verse { Number = 38, TextBosnian = "On je čovjek koji o Allahu iznosi laži, i mi mu ne vjerujemo.'", TextEnglish = "He is not but a man who has invented a lie about Allāh, and we will not believe him.'", TextGerman = "Er ist nur ein Mann, der gegen Allah eine Lüge ersonnen hat; und wir wollen ihm nicht glauben." });
                        surah23.VerseList.Add(new Verse { Number = 39, TextBosnian = "Gospodaru moj', zamolio je on, 'pomozi mi, oni me u laž utjeruju!'", TextEnglish = "He said, 'My Lord, support me because they have denied me.'", TextGerman = "Er sagte: „Mein Herr, hilf mir, wo sie mich der Lüge bezichtigen." });
                        surah23.VerseList.Add(new Verse { Number = 40, TextBosnian = "Uskoro će se oni kajati!', odgovorio je On,", TextEnglish = "[Allāh] said, 'After a little, they will surely become regretful.'", TextGerman = "Er sagte: Über ein kleines werden sie wahrlich Reue empfinden." });
                        surah23.VerseList.Add(new Verse { Number = 41, TextBosnian = "i zasluženo ih je pogodio strašan glas, i Mi bismo ih kao što je nanos riječni učinili, pa daleko bio narod zulumćarski!", TextEnglish = "So the shriek[954] seized them in truth,[955] and We made them as [plant] stubble. Then away with the wrongdoing people.", TextGerman = "Da ergriff sie zu Recht der Schrei, und so machten Wir sie zu Abschaum. Weg darum mit dem ungerechten Volk!" });
                        surah23.VerseList.Add(new Verse { Number = 42, TextBosnian = "A zatim smo, poslije njih, druga pokoljenja stvorili,", TextEnglish = "Then We produced after them other generations.", TextGerman = "Hierauf ließen Wir nach ihnen andere Geschlechter entstehen." });
                        surah23.VerseList.Add(new Verse { Number = 43, TextBosnian = "nijedan narod ne može ubrzati rok svoj određeni, a ne može ga ni odgoditi,", TextEnglish = "No nation will precede its time [of termination], nor will they remain [thereafter].", TextGerman = "Keine Gemeinschaft kann ihrer Frist vorausgehen, noch sie hinausschieben." });
                        surah23.VerseList.Add(new Verse { Number = 44, TextBosnian = "i poslanike, jedne za drugim slali. Kad god bi jednom narodu došao njegov poslanik, u laž bi ga utjerivali, i Mi smo ih zato jedne drugima smjenjivali, i samo u pričama o njima spomen sačuvali; pa daleko bili ljudi koji ne vjeruju!", TextEnglish = "Then We sent Our messengers in succession. Every time there came to a nation its messenger, they denied him, so We made them follow one another [to destruction], and We made them narrations.[956] So away with a people who do not believe.", TextGerman = "Hierauf sandten Wir Unsere Gesandten, einen nach dem anderen. Jedesmal, wenn zu einer Gemeinschaft ihr Gesandter kam, bezichtigten sie ihn der Lüge. So ließen Wir die einen von ihnen auf die anderen folgen und machten sie zu(m Gegenstand von) Geschichten. Weg darum mit Leuten, die nicht glauben!" });
                        surah23.VerseList.Add(new Verse { Number = 45, TextBosnian = "Poslije smo poslali Musaa i brata mu Haruna sa znakovima Našim i dokazom očiglednim", TextEnglish = "Then We sent Moses and his brother Aaron with Our signs and a clear authority[957]", TextGerman = "Hierauf sandten Wir Musa und seinen Bruder Harun mit Unseren Zeichen und einer deutlichen Ermächtigung" });
                        surah23.VerseList.Add(new Verse { Number = 46, TextBosnian = "faraonu i glavešinama njegovim, ali su se oni uzoholili, bili su to ljudi nadmeni.", TextEnglish = "To Pharaoh and his establishment, but they were arrogant and were a haughty people.", TextGerman = "zu Fir’aun und seiner führenden Schar. Sie aber verhielten sich hochmütig und waren überhebliche Leute." });
                        surah23.VerseList.Add(new Verse { Number = 47, TextBosnian = "Zar da povjerujemo dvojici ljudi koji su kao i mi, a narod njihov je roblje naše?', govorili su,", TextEnglish = "They said, 'Should we believe two men like ourselves while their people are for us in servitude?'", TextGerman = "Sie sagten: „Sollen wir denn zwei menschlichen Wesen unseresgleichen glauben, wo doch ihr Volk in unserem Dienst steht?" });
                        surah23.VerseList.Add(new Verse { Number = 48, TextBosnian = "i njih dvojicu su lažljivcima proglasili, pa su zato uništeni bili.", TextEnglish = "So they denied them and were of those destroyed.", TextGerman = "Sie bezichtigten sie der Lüge, und so gehörten sie zu denen, die vernichtet wurden." });
                        surah23.VerseList.Add(new Verse { Number = 49, TextBosnian = "Musau smo onda Knjigu dali da bi sinovi Israilovi Pravim putem išli.", TextEnglish = "And We certainly gave Moses the Scripture that perhaps they[958] would be guided.", TextGerman = "Und Wir gaben bereits Musa die Schrift, auf daß sie rechtgeleitet werden mögen." });
                        surah23.VerseList.Add(new Verse { Number = 50, TextBosnian = "A sina Merjemina i majku njegovu smo znakom učinili i sklonili ih na jednu plodnu uzvišicu sa staništem i tekućom vodom.", TextEnglish = "And We made the son of Mary and his mother a sign and sheltered them within a high ground having level [areas] and flowing water.", TextGerman = "Und Wir machten den Sohn Maryams und seine Mutter zu einem Zeichen und gewährten ihnen Zuflucht auf einer Anhöhe mit festem Grund und Quellwasser." });
                        surah23.VerseList.Add(new Verse { Number = 51, TextBosnian = "O poslanici, lijepim - dozvoljenim jelima se hranite i dobra djela činite; Ja, zaista, dobro znam šta vi radite!", TextEnglish = "[Allāh said], 'O messengers, eat from the good foods and work righteousness. Indeed I, of what you do, am Knowing.", TextGerman = "„O ihr Gesandten, eßt von den guten Dingen und handelt rechtschaffen; gewiß, Ich weiß über das, was ihr tut, Bescheid." });
                        surah23.VerseList.Add(new Verse { Number = 52, TextBosnian = "Ova vaša vjera - jedna je vjera a Ja sam Gospodar vaš, pa Me se bojte!", TextEnglish = "And indeed this, your religion, is one religion,[959] and I am your Lord, so fear Me.'", TextGerman = "Gewiß, diese ist eure Gemeinschaft, eine einzige Gemeinschaft, und Ich bin euer Herr; so fürchtet Mich!" });
                        surah23.VerseList.Add(new Verse { Number = 53, TextBosnian = "A oni su se u pitanjima vjere svoje podijelili na skupine, svaka stranka s onim što imaju radosna.", TextEnglish = "But they [i.e., the people] divided their religion among them into portions [i.e., sects] - each faction, in what it has,[960] rejoicing.", TextGerman = "Aber sie spalteten sich in ihrer Angelegenheit untereinander nach (verschiedenen) Büchern; und jede Gruppierung ist froh über das, was sie bei sich hat." });
                        surah23.VerseList.Add(new Verse { Number = 54, TextBosnian = "Pa, ostavi ih u zabludi njihovoj do vremena nekog!", TextEnglish = "So leave them in their confusion for a time.", TextGerman = "So lasse sie in ihrer Verwirrung für eine gewisse Zeit" });
                        surah23.VerseList.Add(new Verse { Number = 55, TextBosnian = "Misle li oni - da je to što ih opskrbljujemo imetkom i sinovima", TextEnglish = "Do they think that what We extend to them of wealth and children", TextGerman = "Meinen sie etwa, daß Wir, wenn Wir sie mit Besitz und Söhnen unterstützen," });
                        surah23.VerseList.Add(new Verse { Number = 56, TextBosnian = "zato što im s dobrima žurimo? Naprotiv, oni ne opažaju!", TextEnglish = "Is [because] We hasten for them good things? Rather, they do not perceive.[961]", TextGerman = "Uns beeilen, ihnen gute Dinge (zu gewähren)? Aber nein! Sie merken nicht." });
                        surah23.VerseList.Add(new Verse { Number = 57, TextBosnian = "Oni koji iz bojazni prema Gospodaru svome strahuju,", TextEnglish = "Indeed, they who are apprehensive from fear of their Lord", TextGerman = "Gewiß, diejenigen, die aus Furcht vor ihrem Herrn besorgt sind" });
                        surah23.VerseList.Add(new Verse { Number = 58, TextBosnian = "i oni koji u ajete i znakove Gospodara svoga vjeruju,", TextEnglish = "And they who believe in the signs of their Lord", TextGerman = "und die an die Zeichen ihres Herrn glauben" });
                        surah23.VerseList.Add(new Verse { Number = 59, TextBosnian = "i oni koji druge Gospodaru svome nikoga u obožavanju ne pridružuju,", TextEnglish = "And they who do not associate anything with their Lord", TextGerman = "und die ihrem Herrn nicht(s) beigesellen" });
                        surah23.VerseList.Add(new Verse { Number = 60, TextBosnian = "i oni koji od onoga što im je dato udjeljuju, a srca su im puna straha, zato što će se vratiti svome Gospodaru", TextEnglish = "And they who give what they give while their hearts are fearful[962] because they will be returning to their Lord -", TextGerman = "und die geben, was sie geben, während ihre Herzen sich (davor) ängstigen, weil sie zu ihrem Herrn zurückkehren werden," });
                        surah23.VerseList.Add(new Verse { Number = 61, TextBosnian = "oni hitaju da čine dobra djela, i u njima druge pretiču!", TextEnglish = "It is those who hasten to good deeds, and they outstrip [others] therein.", TextGerman = "diese beeilen sich mit den guten Dingen, und sie werden sie erreichen." });
                        surah23.VerseList.Add(new Verse { Number = 62, TextBosnian = "Mi nikoga ne zadužujemo preko njegovih mogućnosti; u Nas je Knjiga koja istinu govori, i njima zulum neće biti učinjen!", TextEnglish = "And We charge no soul except [with that within] its capacity, and with Us is a record which speaks with truth; and they will not be wronged.", TextGerman = "Wir erlegen keiner Seele mehr auf, als sie zu leisten vermag. Und bei Uns ist ein Buch, das die Wahrheit redet, und es wird ihnen kein Unrecht zugefügt." });
                        surah23.VerseList.Add(new Verse { Number = 63, TextBosnian = "Ali, srca njihova su u nemaru prema ovom, a pored toga oni imaju djela koja će sigurno uraditi.", TextEnglish = "But their hearts are covered with confusion over this, and they have [evil] deeds besides that [i.e., disbelief] which they are doing,", TextGerman = "Aber nein! Ihre Herzen befinden sich in tiefer Achtlosigkeit davor. Und es gibt bei ihnen (andere) Taten außer diesen, die sie ausführen." });
                        surah23.VerseList.Add(new Verse { Number = 64, TextBosnian = "A kad smo patnjom dohvatili one među njima koji su raskošnim životom živjeli, oni su odmah zapomagali.", TextEnglish = "Until when We seize their affluent ones with punishment,[963] at once they are crying [to Allāh] for help.", TextGerman = "Wenn Wir dann diejenigen von ihnen, die üppig leben, mit der Strafe ergreifen, flehen sie sogleich laut um Hilfe." });
                        surah23.VerseList.Add(new Verse { Number = 65, TextBosnian = "Sada ne zapomažite, Mi vam nećemo pomoć ukazati,", TextEnglish = "Do not cry out today. Indeed, by Us you will not be helped.", TextGerman = "„Fleht nicht um Hilfe heute, denn euch wird von Uns nicht geholfen." });
                        surah23.VerseList.Add(new Verse { Number = 66, TextBosnian = "vama su ajeti Moji kazivani, ali ste vi natrag uzmicali;", TextEnglish = "My verses had already been recited to you, but you were turning back on your heels.", TextGerman = "Meine Zeichen wurden euch bereits verlesen. Aber ihr pflegtet auf euren Fersen kehrtzumachen," });
                        surah23.VerseList.Add(new Verse { Number = 67, TextBosnian = "oholeći se, i sijeleći o njemu ste buncali.'", TextEnglish = "In arrogance regarding it,[964] conversing by night, speaking evil.", TextGerman = "indem ihr euch davon hochmütig in eurem Nachtgeplauder abwandtet." });
                        surah23.VerseList.Add(new Verse { Number = 68, TextBosnian = "Zašto oni o Govoru ne promisle, ili im je došlo nešto što nije dolazilo njihovim precima davnim?!", TextEnglish = "Then have they not reflected over the word [i.e., the Qur’ān], or has there come to them that which had not come to their forefathers?", TextGerman = "Haben sie denn nicht über das Wort nachgedacht, oder ist zu ihnen gekommen, was nicht zu ihren Vorvätern kam?" });
                        surah23.VerseList.Add(new Verse { Number = 69, TextBosnian = "Zar oni ne poznaju Poslanika svoga, pa ga zato poriču?!", TextEnglish = "Or did they not know their Messenger, so they are toward him disacknowledging?", TextGerman = "Oder haben sie ihren Gesandten nicht erkannt, so daß sie ihn (nun) verwerfen?" });
                        surah23.VerseList.Add(new Verse { Number = 70, TextBosnian = "Zar govore: 'Lud je!' Međutim, on im Istinu donosi, ali većina njih prezire Istinu.", TextEnglish = "Or do they say, 'In him is madness'? Rather, he brought them the truth, but most of them, to the truth, are averse.", TextGerman = "Oder sagen sie: „Er leidet an Besessenheit? Nein! Vielmehr kam er zu ihnen mit der Wahrheit, aber den meisten von ihnen ist die Wahrheit zuwider." });
                        surah23.VerseList.Add(new Verse { Number = 71, TextBosnian = "Da se Allah za hirovima njihovim povodi, sigurno bi nestalo poretka na nebesima i Zemlji i u onom što je na njima; Mi smo im dali Opomenu ali se oni od Opomene svoje okreću.", TextEnglish = "But if the Truth [i.e., Allāh] had followed their inclinations, the heavens and the earth and whoever is in them would have been ruined. Rather, We have brought them their message,[965] but they, from their message, are turning away.", TextGerman = "Wenn die Wahrheit ihren Neigungen gefolgt wäre, gerieten die Himmel und die Erde und wer in ihnen ist wahrlich ins Verderben. Aber nein! Wir kamen zu ihnen mit ihrer Ermahnung, sie aber wenden sich von ihrer Ermahnung ab." });
                        surah23.VerseList.Add(new Verse { Number = 72, TextBosnian = "Ili možda od njih tražiš naknadu?! Pa, naknada Gospodara tvoga bolja je, On je najbolji Opskrbitelj.", TextEnglish = "Or do you, [O Muḥammad], ask them for payment? But the reward of your Lord is best, and He is the best of providers.", TextGerman = "Oder verlangst du von ihnen eine Entlohnung? Aber die Entlohnung deines Herrn ist besser, und Er ist der beste Versorger." });
                        surah23.VerseList.Add(new Verse { Number = 73, TextBosnian = "Ti ih, zaista, pozivaš na Pravi put,", TextEnglish = "And indeed, you invite them to a straight path.", TextGerman = "Und du lädst sie wahrlich zu einem geraden Weg ein." });
                        surah23.VerseList.Add(new Verse { Number = 74, TextBosnian = "ali oni koji u ahiret neće da vjeruju s Pravog puta, doista, skreću.", TextEnglish = "But indeed, those who do not believe in the Hereafter are deviating from the path.", TextGerman = "Jene, die nicht an das Jenseits glauben, weichen fürwahr vom Weg ab." });
                        surah23.VerseList.Add(new Verse { Number = 75, TextBosnian = "I kad bismo im se smilovali i nevolju u kojoj se nalaze od njih otklonili, opet bi oni, sigurno, nastavili u osionosti svojoj lutati.", TextEnglish = "And even if We gave them mercy and removed what was upon them of affliction, they would persist in their transgression, wandering blindly.", TextGerman = "Wenn Wir Uns ihrer erbarmten und das Unheil,das auf ihnen ist, hinwegnähmen, würden sie wahrlich in ihrer Auflehnung verharren und umherirren." });
                        surah23.VerseList.Add(new Verse { Number = 76, TextBosnian = "Mi smo ih patnjom dohvatili, ali se oni Gospodaru nisu pokorili, niti su se usrdno molili.", TextEnglish = "And We had gripped them with suffering [as a warning], but they did not yield to their Lord, nor did they humbly supplicate, [and will continue thus]", TextGerman = "Wir haben sie ja mit der Strafe ergriffen. Aber weder unterwarfen sie sich ihrem Herrn noch flehten sie demütig." });
                        surah23.VerseList.Add(new Verse { Number = 77, TextBosnian = "tek kad im kapiju teške patnje otvorimo oni će nadu izgubiti i u očajanje zapasti.", TextEnglish = "Until when We have opened before them a door of severe punishment, immediately they will be therein in despair.", TextGerman = "Wenn Wir ihnen dann ein Tor zu strenger Strafe öffnen, sind sie darüber sogleich ganz verzweifelt." });
                        surah23.VerseList.Add(new Verse { Number = 78, TextBosnian = "On je Taj Koji vam stvori sluh, i vid, i pamet; a kako malo vi zahvaljujete!", TextEnglish = "And it is He who produced for you hearing and vision and hearts [i.e., intellect]; little are you grateful.", TextGerman = "Er ist es, Der euch Gehör, Augenlicht und Herzen hat entstehen lassen. Wie wenig dankbar ihr seid!" });
                        surah23.VerseList.Add(new Verse { Number = 79, TextBosnian = "On je Taj Koji vas rasija po Zemlji, i k Njemu ćete biti sakupljeni,", TextEnglish = "And it is He who has multiplied you throughout the earth, and to Him you will be gathered.", TextGerman = "Und Er ist es, Der euch auf der Erde (an Zahl) hat anwachsen lassen, und zu Ihm werdet ihr versammelt werden." });
                        surah23.VerseList.Add(new Verse { Number = 80, TextBosnian = "On je Taj Koji oživljava i usmrćuje, i samo On noć i dan smjenjuje, pa zar ne razumijete?!", TextEnglish = "And it is He who gives life and causes death, and His is the alternation of the night and the day. Then will you not reason?", TextGerman = "Und Er ist es, Der lebendig macht und sterben läßt; und auf Ihn geht der Unterschied von Nacht und Tag zurück. Begreift ihr denn nicht?" });
                        surah23.VerseList.Add(new Verse { Number = 81, TextBosnian = "Ali, oni govore kao što su govorili oni prije njih.", TextEnglish = "Rather,[966] they say like what the former peoples said.", TextGerman = "Aber nein! Sie sagen das gleiche, was die Früheren gesagt haben." });
                        surah23.VerseList.Add(new Verse { Number = 82, TextBosnian = "Govorili su: 'Zar kad pomremo i kad prah i kosti postanemo, zar ćemo, zaista, biti oživljeni?", TextEnglish = "They said, 'When we have died and become dust and bones, are we indeed to be resurrected?", TextGerman = "Sie sagen: „Wenn wir gestorben und zu Erde und Knochen geworden sind, sollen wir denn dann wirklich auferweckt werden?" });
                        surah23.VerseList.Add(new Verse { Number = 83, TextBosnian = "I nama i, još davno, precima našim to je obećavano, ali, to su samo izmišljotine naroda drevnih.'", TextEnglish = "We have been promised this, we and our forefathers, before; this is not but legends of the former peoples.'", TextGerman = "Dies ist bereits zuvor uns und unseren Vätern versprochen worden. Das sind nur Fabeln der Früheren." });
                        surah23.VerseList.Add(new Verse { Number = 84, TextBosnian = "Upitaj: 'Čija je Zemlja i sve ono što je na njoj, znate li?'", TextEnglish = "Say, [O Muḥammad], 'To whom belongs the earth and whoever is in it, if you should know?'", TextGerman = "Sag: Wem gehört die Erde und wer auf ihr ist, wenn ihr (es) wißt?" });
                        surah23.VerseList.Add(new Verse { Number = 85, TextBosnian = "Allahova!', odgovorit će, a ti reci: 'Pa zašto se onda ne prisjetite?'", TextEnglish = "They will say, 'To Allāh.' Say, 'Then will you not remember?'", TextGerman = "Sie werden sagen: „(Alles) gehört Allah. Bedenkt ihr denn nicht?" });
                        surah23.VerseList.Add(new Verse { Number = 86, TextBosnian = "Upitaj: 'Ko je Gospodar sedam nebesa i ko je Gospodar Arša veličanstvenog?'", TextEnglish = "Say, 'Who is Lord of the seven heavens and Lord of the Great Throne?'", TextGerman = "Sag: Wer ist der Herr der sieben Himmel und der Herr des gewaltigen Thrones?" });
                        surah23.VerseList.Add(new Verse { Number = 87, TextBosnian = "Allah!', odgovorit će, a ti reci: 'Pa zašto se onda ne čuvate?!'", TextEnglish = "They will say, '[They belong] to Allāh.' Say, 'Then will you not fear Him?'", TextGerman = "Sie werden sagen: „(Alles) gehört Allah. Sag: Wollt ihr denn nicht gottesfürchtig sein?" });
                        surah23.VerseList.Add(new Verse { Number = 88, TextBosnian = "Upitaj: 'U čijoj je ruci vlast nad svim - a On uzima u zaštitu i od Njega niko ne može zaštićen biti - znate li?'", TextEnglish = "Say, 'In whose hand is the realm of all things - and He protects while none can protect against Him - if you should know?'", TextGerman = "Sag: In wessen Hand ist die Herrschaftsgewalt über alles, Der Schutz gewährt und gegen Den kein Schutz gewährt werden kann, wenn ihr wißt?" });
                        surah23.VerseList.Add(new Verse { Number = 89, TextBosnian = "To je Allahovo!', odgovorit će, a ti reci: 'Pa kako ste onda opčinjeni!?'", TextEnglish = "They will say, '[All belongs] to Allāh.' Say, 'Then how are you deluded?'", TextGerman = "Sie werden sagen: „(Alles) gehört Allah. Wieso seid ihr denn einem Zauber verfallen?" });
                        surah23.VerseList.Add(new Verse { Number = 90, TextBosnian = "Štaviše, Mi im Istinu donosimo, a oni su, baš, lažljivci.", TextEnglish = "Rather, We have brought them the truth, and indeed they are liars.", TextGerman = "Nein! Vielmehr sind Wir zu ihnen mit der Wahrheit gekommen, aber sie sind wahrlich Lügner." });
                        surah23.VerseList.Add(new Verse { Number = 91, TextBosnian = "Allah nije uzeo Sebi sina i uz Njega nema drugog boga! lnače,svaki bi bog, s onim što je stvorio - radio što bi htio, i jedan drugog bi nadvlađivao. Uzvišenje Allah od onoga čime Ga opisuju;", TextEnglish = "Allāh has not taken any son, nor has there ever been with Him any deity. [If there had been], then each deity would have taken what it created, and some of them would have [sought to] overcome others. Exalted is Allāh above what they describe [concerning Him].", TextGerman = "Allah hat Sich keine Kinder genommen, und keinen Gott gibt es neben Ihm, sonst würde fürwahr jeder Gott das wegnehmen, was er erschaffen hat, und die einen von ihnen würden sich den anderen gegenüber wahrlich überheblich zeigen. Preis sei Allah, (und Erhaben ist Er) über das, was sie (Ihm) zuschreiben," });
                        surah23.VerseList.Add(new Verse { Number = 92, TextBosnian = "Znalac i onog što je čulima nedokučivo i onog što je pojavno, pa neka je visoko iznad onih koje Mu pridružuju!", TextEnglish = "[He is] Knower of the unseen and the witnessed, so high is He above what they associate [with Him].", TextGerman = "Dem Kenner des Verborgenen und des Offenbaren! Erhaben ist Er über das, was sie (Ihm) beigesellen." });
                        surah23.VerseList.Add(new Verse { Number = 93, TextBosnian = "Reci: 'Gospodaru moj, ako ćeš mi pokazati ono što im se prijeteći obećava,", TextEnglish = "Say, [O Muḥammad], 'My Lord, if You should show me that which they are promised,", TextGerman = "Sag: Mein Herr, wenn immer Du mir zeigst, was ihnen angedroht wird," });
                        surah23.VerseList.Add(new Verse { Number = 94, TextBosnian = "onda, Gospodaru moj, ne učini da sam u narodu zulumćarskom.'", TextEnglish = "My Lord, then do not place me among the wrongdoing people.'", TextGerman = "mein Herr, dann stelle mich nicht unter das ungerechte Volk." });
                        surah23.VerseList.Add(new Verse { Number = 95, TextBosnian = "A Mi, zaista, možemo da ti pokažemo ono što im prijeteći obećavamo.", TextEnglish = "And indeed, We are Able to show you what We have promised them.", TextGerman = "Wir haben fürwahr die Macht dazu, dir zu zeigen, was Wir ihnen androhen." });
                        surah23.VerseList.Add(new Verse { Number = 96, TextBosnian = "Ti lijepim na zlo uzvrati, Mi najbolje znamo čime Nas opisuju,", TextEnglish = "Repel, by [means of] what is best, [their] evil. We are most knowing of what they describe.", TextGerman = "Wehre mit dem, was besser ist, das Böse ab. Wir wissen sehr wohl, was sie da zuschreiben." });
                        surah23.VerseList.Add(new Verse { Number = 97, TextBosnian = "i reci: 'Gospodaru moj, utječem Ti se od nagovaranja šejtanskih,", TextEnglish = "And say, 'My Lord, I seek refuge in You from the incitements of the devils,", TextGerman = "Und sag: Mein Herr, ich nehme Zuflucht bei Dir vor den Aufstachelungen der Satane." });
                        surah23.VerseList.Add(new Verse { Number = 98, TextBosnian = "i Tebi se, Gospodaru moj, utječem od toga da su uz mene prisutni!'", TextEnglish = "And I seek refuge in You, my Lord, lest they be present with me.'", TextGerman = "Und ich nehme Zuflucht bei Dir davor, mein Herr, daß sie mich aufsuchen." });
                        surah23.VerseList.Add(new Verse { Number = 99, TextBosnian = "I tako, kad nekom od njih smrt dođe, on uzvikne: 'Gospodaru moj, vrati me", TextEnglish = "[For such is the state of the disbelievers] until, when death comes to one of them, he says, 'My Lord, send me back", TextGerman = "Wenn dann der Tod zu einem von ihnen kommt, sagt er: „Mein Herr, bringt mich zurück," });
                        surah23.VerseList.Add(new Verse { Number = 100, TextBosnian = "da uradim kakvo dobro u onome što sam propustio!' Nikako!' To su, zaista, riječi koje će on neminovno reći'' - pred njima će prepreka biti sve do Dana kada će oživljeni biti.", TextEnglish = "That I might do righteousness in that which I left behind.'[967] No! It is only a word he is saying; and behind them is a barrier until the Day they are resurrected.", TextGerman = "auf daß ich rechtschaffen handele in dem, was ich hinterlassen habe. Keineswegs! Es ist nur ein Wort, das er (so) sagt; hinter ihnen wird ein trennendes Hindernis sein bis zu dem Tag, da sie auferweckt werden." });
                        surah23.VerseList.Add(new Verse { Number = 101, TextBosnian = "Pa kad u rog bude puhnuto, tada rodbinskih veza među njima neće biti i jedni druge neće ništa pitati.", TextEnglish = "So when the Horn is blown, no relationship will there be among them that Day, nor will they ask about one another.", TextGerman = "Wenn dann ins Horn geblasen wird, dann wird es zwischen ihnen keine Verwandtschaft mehr geben an jenem Tag, und sie fragen sich nicht mehr gegenseitig." });
                        surah23.VerseList.Add(new Verse { Number = 102, TextBosnian = "Oni čiji tas s dobrim djelima pretegne, oni će se spasiti,", TextEnglish = "And those whose scales are heavy [with good deeds] - it is they who are the successful.", TextGerman = "Wessen Waagschalen schwer sein werden, das sind diejenigen, denen es wohl ergeht." });
                        surah23.VerseList.Add(new Verse { Number = 103, TextBosnian = "a oni čiji tas dobra bude lakši, oni će posve izgubljeni biti, u Džehennemu će vječno boraviti,", TextEnglish = "But those whose scales are light - those are the ones who have lost their souls, [being] in Hell, abiding eternally.", TextGerman = "Wessen Waagschalen aber leicht sein werden, das sind diejenigen, die ihre Seelen verloren haben; in der Hölle werden sie ewig bleiben." });
                        surah23.VerseList.Add(new Verse { Number = 104, TextBosnian = "Vatra će im lica pržiti i iskeženih zuba će u njemu biti.", TextEnglish = "The Fire will sear their faces, and they therein will have taut smiles.[968]", TextGerman = "Das Feuer schlägt in ihre Gesichter, und sie werden darin erblaßt sein." });
                        surah23.VerseList.Add(new Verse { Number = 105, TextBosnian = "Zar vam ajeti Moji nisu kazivani, a vi ste ih poricali?!", TextEnglish = "[It will be said], 'Were not My verses recited to you and you used to deny them?'", TextGerman = "„Wurden euch nicht Meine Zeichen verlesen, ihr aber pflegtet sie für Lüge zu erklären?" });
                        surah23.VerseList.Add(new Verse { Number = 106, TextBosnian = "Gospodaru naš', reći će, 'nesreća nas naša nadvladala i bijasmo narod zalutali.", TextEnglish = "They will say, 'Our Lord, our wretchedness overcame us, and we were a people astray.", TextGerman = "Sie werden sagen: „Unser Herr, unser Unglück hat uns besiegt, und wir waren irregehende Leute." });
                        surah23.VerseList.Add(new Verse { Number = 107, TextBosnian = "Gospodaru naš, izbavi nas iz nje; ako bismo ponovo zlo radili, bili bismo, zaista, zulumćari.'", TextEnglish = "Our Lord, remove us from it, and if we were to return [to evil], we would indeed be wrongdoers.'", TextGerman = "Unser Herr, bringe uns aus ihr heraus. Wenn wir rückfällig werden, dann sind wir (wirklich) ungerecht." });
                        surah23.VerseList.Add(new Verse { Number = 108, TextBosnian = "Ostanite u njoj prezreni i ništa Mi ne govorite!', reći će On.", TextEnglish = "He will say, 'Remain despised therein and do not speak to Me.", TextGerman = "Er sagt: „Seid darin verstoßen, und sprecht nicht mit Mir." });
                        surah23.VerseList.Add(new Verse { Number = 109, TextBosnian = "Kad su neki robovi Moji govorili: 'Gospodaru naš, mi vjerujemo, zato nam oprosti i smiluj nam se, a Ti si Milosnik najbolji!'", TextEnglish = "Indeed, there was a party of My servants who said, 'Our Lord, we have believed, so forgive us and have mercy upon us, and You are the best of the merciful.'", TextGerman = "Gewiß, eine Gruppe von Meinen Dienern pflegte zu sagen: ,Unser Herr, wir glauben, so vergib uns und erbarme Dich unser, denn Du bist der Beste der Barmherzigen.’" });
                        surah23.VerseList.Add(new Verse { Number = 110, TextBosnian = "vi ste im se toliko rugali da ste zbog toga na Mene zaboravljali i uvijek ste ih ismijavati.", TextEnglish = "But you took them in mockery to the point that they made you forget My remembrance, and you used to laugh at them.", TextGerman = "Aber ihr nahmt sie zum Gegenstand des Spottes, bis ihr ihretwegen Meine Ermahnung vergaßt, und ihr pflegtet über sie zu lachen." });
                        surah23.VerseList.Add(new Verse { Number = 111, TextBosnian = "Njih sam Ja danas nagradio za ono što su trpjeli, oni su, doista, uspjeli.", TextEnglish = "Indeed, I have rewarded them this Day for their patient endurance - that they are the attainers [of success].'", TextGerman = "Ich habe ihnen heute, daß sie standhaft waren, damit vergolten, daß sie die Erfolgreichen sind." });
                        surah23.VerseList.Add(new Verse { Number = 112, TextBosnian = "A koliko ste godina na Zemlji proveli?', upitat će On.", TextEnglish = "[Allāh] will say, 'How long did you remain on earth in number of years?'", TextGerman = "Er wird sagen: „Wie viele Jahre habt ihr in der Erde verweilt?" });
                        surah23.VerseList.Add(new Verse { Number = 113, TextBosnian = "Proveli smo, dan ili samo dio dana', odgovorit će, 'pitaj one koji su brojali.'", TextEnglish = "They will say, 'We remained a day or part of a day; ask those who enumerate.'", TextGerman = "Sie werden sagen: „Verweilt haben wir einen Tag oder den Teil von einem Tag; doch frage diejenigen, die zählen (können)." });
                        surah23.VerseList.Add(new Verse { Number = 114, TextBosnian = "Pa da, malo ste proveli', reći će On, 'da ste samo znali!'", TextEnglish = "He will say, 'You stayed not but a little - if only you had known.", TextGerman = "Er wird sagen: „Ihr habt nur ein wenig verweilt, wenn ihr doch nur wüßtet!" });
                        surah23.VerseList.Add(new Verse { Number = 115, TextBosnian = "Zar ste mislili da smo vas uzalud stvorili i da Nam se nećete vratiti'", TextEnglish = "Then did you think that We created you uselessly and that to Us you would not be returned?'", TextGerman = "Meint ihr denn, daß Wir euch zum sinnlosen Spiel erschaffen hätten und daß ihr nicht zu Uns zurückgebracht würdet?" });
                        surah23.VerseList.Add(new Verse { Number = 116, TextBosnian = "I neka je Uzvišen Allah, Vladar Istiniti, nema drugog boga osim Njega, Gospodara Arša divnoga!", TextEnglish = "So exalted is Allāh, the Sovereign,[969] the Truth;[970] there is no deity except Him, Lord of the Noble Throne.", TextGerman = "Doch Erhaben ist Allah, der König, der Wahre. Es gibt keinen Gott außer Ihm, dem Herrn des ehrwürdigen Thrones." });
                        surah23.VerseList.Add(new Verse { Number = 117, TextBosnian = "A onaj koji se, pored Allaha, moli drugom bogu, bez ikakva dokaza o njemu, pred Gospodarom svojim račun će polagati, a nevjernici neće uspjeti.", TextEnglish = "And whoever invokes besides Allāh another deity for which he has no proof - then his account is only with his Lord. Indeed, the disbelievers will not succeed.", TextGerman = "Und wer neben Allah einen anderen Gott anruft, für den er keinen Beweis hat, dessen Abrechnung liegt nur bei seinem Herrn. Gewiß, den Ungläubigen wird es nicht wohl ergehen." });
                        surah23.VerseList.Add(new Verse { Number = 118, TextBosnian = "I reci: 'Gospodaru moj, oprosti i smiluj se, Ti si Milosnik najbolji!'", TextEnglish = "And, [O Muḥammad], say, 'My Lord, forgive and have mercy, and You are the best of the merciful.'", TextGerman = "Und sag: Mein Herr, vergib und erbarme Dich, denn Du bist der Beste der Barmherzigen." });
                        result = surah23;
                        break;
                    #endregion

                    // 
                    #region
                    case 24:
                        Surah surah24 = new Surah(); surah24.Name = Constants.Surah24Name; surah24.Number = 24; surah24.VerseList = new List<Verse>();
                        surah24.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah24.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ovo je sura koju objavljujemo i u obavezu je stavljamo i u njoj objavljujemo jasne ajete da biste se prisjećali i pouku primili.", TextEnglish = "[This is] a sūrah which We have sent down and made [that within it] obligatory and revealed therein verses of clear evidence that you might remember.", TextGerman = "(Dies ist) eine Sura, die Wir (als Offenbarung) hinabgesandt und verpflichtend gemacht und in der Wir klare Zeichen hinabgesandt haben, auf daß ihr bedenken möget." });
                        surah24.VerseList.Add(new Verse { Number = 2, TextBosnian = "A bludnik i bludnica-pa svakom od njih dvoje udarite stotinu udaraca po koži, i neka vas prema njima u vjeri Allahovoj ne obuzima nikakvo sažaljenje, ako u Allaha i u Posljednji dan vjerujete, i neka kažnjavanju njihovu jedna skupina vjernika prisustvuje!", TextEnglish = "The [unmarried] woman or [unmarried] man found guilty of sexual intercourse[972] - lash each one of them with a hundred lashes,[973] and do not be taken by pity for them in the religion [i.e., law] of Allāh,[974] if you should believe in Allāh and the Last Day. And let a group of the believers witness their punishment.", TextGerman = "Eine Frau und ein Mann, die Unzucht begehen, geißelt jeden von ihnen mit hundert Hieben. Laßt euch nicht von Mitleid mit ihnen beiden angesichts (der Rechtsbestimmungen) der Religion Allahs ergreifen, wenn ihr an Allah und den Jüngsten Tag glaubt. Und es soll bei (der Vollstreckung) der Strafe an ihnen ein Teil von den Gläubigen zugegen sein." });
                        surah24.VerseList.Add(new Verse { Number = 3, TextBosnian = "Bludnik ne opći osim s bludnicom ili višeboškinjom, a s bludnicom opći samo bludnik ili višebožac, a vjernicima je to zabranjeno.", TextEnglish = "The fornicator does not marry except a [female] fornicator or polytheist, and none marries her except a fornicator[975] or a polytheist, and that [i.e., marriage to such persons] has been made unlawful to the believers.", TextGerman = "Ein Unzuchttreiber heiratet keine andere als eine Frau, die Unzucht begeht oder eine Götzendienerin. Und eine Unzuchttreiberin heiratet kein anderer als ein Mann, der Unzucht begeht oder ein Götzendiener. Den Gläubigen ist dies verboten." });
                        surah24.VerseList.Add(new Verse { Number = 4, TextBosnian = "Onima koji optuže čedne žene, a potom ne dovedu četiri svjedoka, udarite osamdeset udaraca po koži i nikada više svjedočenje njihovo ne primajte; oni su baš buntovnici,", TextEnglish = "And those who accuse chaste women and then do not produce four witnesses - lash them with eighty lashes and do not accept from them testimony ever after. And those are the defiantly disobedient,", TextGerman = "Diejenigen, die den ehrbaren Ehefrauen (Untreue) vorwerfen und hierauf nicht vier Zeugen beibringen, die geißelt mit achtzig Hieben und nehmt von ihnen niemals mehr eine Zeugenaussage an - das sind die (wahren) Frevler -," });
                        surah24.VerseList.Add(new Verse { Number = 5, TextBosnian = "osim onih koji se poslije toga pokaju i poprave, jer Allah je, zaista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "Except for those who repent thereafter and reform, for indeed, Allāh is Forgiving and Merciful.", TextGerman = "außer denjenigen, die nach alledem bereuen und rechtschaffen werden, denn Allah ist Allvergebend und Barmherzig." });
                        surah24.VerseList.Add(new Verse { Number = 6, TextBosnian = "A oni koji optuže svoje supruge, a ne budu imali drugih svjedoka, osim samih sebe- pa svjedočenje svakog od njih je u četiri svjedočenja uz zaklinjanje Allahom, da je, zaista, od onih koji govore istinu,", TextEnglish = "And those who accuse their wives [of adultery] and have no witnesses except themselves - then the witness of one of them[976] [shall be] four testimonies [swearing] by Allāh that indeed, he is of the truthful.", TextGerman = "Für diejenigen, die ihren Gattinnen (Untreue) vorwerfen, aber keine Zeugen haben außer sich selbst, besteht die Zeugenaussage eines (solchen) von ihnen darin, daß er viermal bei Allah bezeugt, er gehöre wahrlich zu denen, die die Wahrheit sagen," });
                        surah24.VerseList.Add(new Verse { Number = 7, TextBosnian = "a peto je da ga zadesi Allahovo prokletstvo, ako je od onih koji lažu!", TextEnglish = "And the fifth [oath will be] that the curse of Allāh be upon him if he should be of the liars.", TextGerman = "und zum fünften Mal (bezeugt), der Fluch Allahs komme auf ihn, wenn er zu den Lügnern gehören sollte." });
                        surah24.VerseList.Add(new Verse { Number = 8, TextBosnian = "A ono što će od nje otkloniti kaznu jeste da posvjedoči u četiri svjedočenja zaklinjući se Allahom da je on, doista, od onih koji lažu,", TextEnglish = "But it will prevent punishment from her if she gives four testimonies [swearing] by Allāh that indeed, he is of the liars.", TextGerman = "Und es wehrt von ihr die Strafe ab, daß sie viermal bei Allah bezeugt, er gehöre wahrlich zu den Lügnern," });
                        surah24.VerseList.Add(new Verse { Number = 9, TextBosnian = "a peti put da je stigne Allahova srdžba, ako je on od onih koji govore istinu!", TextEnglish = "And the fifth [oath will be] that the wrath of Allāh be upon her if he was of the truthful.", TextGerman = "und das fünfte Mal (bezeugt sie), der Zorn Allahs komme über sie, wenn er zu denjenigen gehören sollte, die die Wahrheit sagen." });
                        surah24.VerseList.Add(new Verse { Number = 10, TextBosnian = "A da nije Allahove dobrote prema vama i milosti Njegove i da Allah nije Onaj Koji stalno prima pokajanje i mudar je…", TextEnglish = "And if not for the favor of Allāh upon you and His mercy...[977] and because Allāh is Accepting of Repentance and Wise.", TextGerman = "Und ohne die Huld Allahs gegen euch und Seine Barmherzigkeit, und daß Allah Reue annehmend und Allweise wäre,..." });
                        surah24.VerseList.Add(new Verse { Number = 11, TextBosnian = "Zbilja, oni koji su iznijeli potvoru jesu jedna skupina između vas! Vi ne smatrajte to nekim zlom po vas; ne, to je dobro po vas. Svaki od njih bit će kažnjen prema grijehu koji je zaslužio, a onoga od njih koji je to najviše činio čeka patnja velika.", TextEnglish = "Indeed, those who came with falsehood[978] are a group among you. Do not think it bad for you; rather, it is good for you. For every person among them is what [punishment] he has earned from the sin, and he who took upon himself the greater portion thereof[979] - for him is a great punishment [i.e., Hellfire].", TextGerman = "Diejenigen, die die ungeheuerliche Lüge vorgebracht haben, sind eine (gewisse) Schar von euch. Meint nicht, es sei schlecht für euch; nein! Vielmehr ist es gut für euch. Jedermann von ihnen wird zuteil, was er an Sünde erworben hat. Und für denjenigen unter ihnen, der den Hauptanteil daran auf sich genommen hat, wird es gewaltige Strafe geben." });
                        surah24.VerseList.Add(new Verse { Number = 12, TextBosnian = "Zašto, čim ste to čuli, vjernici i vjernice nisu jedni o drugima dobro pomislili i rekli: 'Ovo je očita potvora!'", TextEnglish = "Why, when you heard it, did not the believing men and believing women think good of themselves [i.e., one another] and say, 'This is an obvious falsehood'?", TextGerman = "Hätten doch, als ihr es hörtet, die gläubigen Männer und Frauen eine gute Meinung voneinander gehabt und gesagt: „Das ist deutlich eine ungeheuerliche Lüge!" });
                        surah24.VerseList.Add(new Verse { Number = 13, TextBosnian = "Zašto nisu četvericu svjedoka doveli? A pošto svjedoke nisu doveli, oni su onda kod Allaha lažljivci.", TextEnglish = "Why did they [who slandered] not produce for it four witnesses? And when they do not produce the witnesses, then it is they, in the sight of Allāh, who are the liars.", TextGerman = "Hätten sie doch darüber vier Zeugen beigebracht! Da sie aber die Zeugen nicht beigebracht haben, so sind diese bei Allah die Lügner." });
                        surah24.VerseList.Add(new Verse { Number = 14, TextBosnian = "A da nije Allahove dobrote prema vama i milosti Njegove i na dunjaluku i na ahiretu, već bi vas snašla patnja velika zbog onoga u što ste se upustili", TextEnglish = "And if it had not been for the favor of Allāh upon you and His mercy in this world and the Hereafter, you would have been touched for that [lie] in which you were involved by a great punishment", TextGerman = "Und ohne Allahs Huld gegen euch und Seine Barmherzigkeit im Diesseits und Jenseits würde euch für das, worin ihr euch (ausgiebig) ausgelassen habt, gewaltige Strafe widerfahren," });
                        surah24.VerseList.Add(new Verse { Number = 15, TextBosnian = "kad ste to jezicima svojim prepričavati stali i kad ste na sva usta govorili ono o čemu niste ništa znali, vi ste to sitnicom smatrali, ali je to kod Allaha krupno.", TextEnglish = "When you received it with your tongues[980] and said with your mouths that of which you had no knowledge and thought it was insignificant while it was, in the sight of Allāh, tremendous.", TextGerman = "als ihr es mit euren Zungen aufgegriffen und mit euren Mündern das gesagt habt, wovon ihr kein Wissen hattet, und es für eine leichte Sache gehalten habt, während es bei Allah eine ungeheuerliche Sache ist." });
                        surah24.VerseList.Add(new Verse { Number = 16, TextBosnian = "Zašto niste, čim ste to čuli, rekli: 'Ne dolikuje nam da o tome govorimo, Uzvišen neka si Ti! To je velika kleveta!'", TextEnglish = "And why, when you heard it, did you not say, 'It is not for us to speak of this. Exalted are You, [O Allāh]; this is a great slander'?", TextGerman = "Und hättet ihr doch, als ihr es hörtet, gesagt: „Es steht uns nicht zu, darüber zu sprechen. Preis sei Dir! Das ist eine gewaltige Verleumdung!" });
                        surah24.VerseList.Add(new Verse { Number = 17, TextBosnian = "Allah vas opominje da više nikad tako nešto ne ponovite, ako ste vjernici,", TextEnglish = "Allāh warns you against returning to the likes of this [conduct], ever, if you should be believers.", TextGerman = "Allah ermahnt euch, niemals wieder dergleichen zu tun, wenn ihr gläubig seid." });
                        surah24.VerseList.Add(new Verse { Number = 18, TextBosnian = "i Allah vam ajete objašnjava; a Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "And Allāh makes clear to you the verses [i.e., His rulings], and Allāh is Knowing and Wise.", TextGerman = "Und Allah macht euch die Zeichen klar, und Allah ist Allwissend und Allweise." });
                        surah24.VerseList.Add(new Verse { Number = 19, TextBosnian = "Zaista onima koji vole da se među vjernicima širi gnusoba pripada bolna patnja i na dunjaluku i na ahiretu; Allah zna, a vi ne znate.", TextEnglish = "Indeed, those who like that immorality[981] should be spread [or publicized] among those who have believed will have a painful punishment in this world and the Hereafter. And Allāh knows[982] and you do not know.", TextGerman = "Diejenigen, die es lieben, daß sich das Abscheuliche unter denjenigen, die glauben, verbreitet, für sie wird es schmerzhafte Strafe geben im Diesseits und Jenseits. Allah weiß, ihr aber wißt nicht." });
                        surah24.VerseList.Add(new Verse { Number = 20, TextBosnian = "A da nije Allahove dobrote prema vama i milosti Njegove i da Allah nije milosrdan i milostiv ...", TextEnglish = "And if it had not been for the favor of Allāh upon you and His mercy...[983] and because Allāh is Kind and Merciful.", TextGerman = "Und ohne die Huld Allahs gegen euch und Seine Barmherzigkeit, und daß Allah nicht Gnädig und Barmherzig wäre ..." });
                        surah24.VerseList.Add(new Verse { Number = 21, TextBosnian = "O vi koji vjerujete, ne slijedite korake šejtanove! A onaj ko slijedi korake šejtanove - pa, zaista, on naređuje gnusobu i odvratna djela. A da nije Allahove dobrote prema vama i milosti Njegove, nijedan se od vas ne bi nikad od grijeha očistio; ali Allah čisti onoga koga On hoće. Allah je Onaj Koji sve čuje i Onaj Koji sve zna.", TextEnglish = "O you who have believed, do not follow the footsteps of Satan. And whoever follows the footsteps of Satan - indeed, he enjoins immorality and wrongdoing. And if not for the favor of Allāh upon you and His mercy, not one of you would have been pure, ever, but Allāh purifies whom He wills, and Allāh is Hearing and Knowing.", TextGerman = "O die ihr glaubt, folgt nicht den Fußstapfen des Satans, denn wer den Fußstapfen des Satans folgt, der gebietet das Schändliche und Verwerfliche. Und ohne die Huld Allahs gegen euch und Seine Barmherzigkeit würde niemand von euch jemals lauter sein. Allah aber läutert, wen Er will. Und Allah ist Allhörend und Allwissend." });
                        surah24.VerseList.Add(new Verse { Number = 22, TextBosnian = "Neka se milodarni i imućni među vama ne zaklinju da više neće pomagati rođake i siromahe, i one koji su na Allahovu putu rodni kraj svoj napustili; neka pređu preko toga i nek praštaju! Zar ne biste voljeli da i vama Allah oprosti?! A Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And let not those of virtue among you and wealth swear not to give [aid] to their relatives and the needy and the emigrants for the cause of Allāh, and let them pardon and overlook. Would you not like that Allāh should forgive you? And Allāh is Forgiving and Merciful.", TextGerman = "Und es sollen diejenigen von euch, die Überfluß und Wohlstand besitzen, nicht schwören, sie würden den Verwandten, den Armen und denjenigen, die auf Allahs Weg ausgewandert sind, nichts mehr geben, sondern sie sollen verzeihen und nachsichtig sein. Liebt ihr es (selbst) nicht, daß Allah euch vergibt? Allah ist Allvergebend und Barmherzig." });
                        surah24.VerseList.Add(new Verse { Number = 23, TextBosnian = "Zaista su oni koji potvore čestite, bezazlene vjernice prokleti i na dunjaluku i na ahiretu i njima pripada patnja bolna", TextEnglish = "Indeed, those who [falsely] accuse chaste, unaware and believing women are cursed in this world and the Hereafter; and they will have a great punishment", TextGerman = "Gewiß, diejenigen, die den ehrbaren, unachtsamen gläubigen Frauen (Untreue) vorwerfen, sind verflucht im Diesseits und Jenseits. Für sie wird es gewaltige Strafe geben," });
                        surah24.VerseList.Add(new Verse { Number = 24, TextBosnian = "na Dan kada protiv njih budu svjedočili jezici njihovi, i ruke njihove, i noge njihove za ono što su radili.", TextEnglish = "On a Day when their tongues, their hands and their feet will bear witness against them as to what they used to do.", TextGerman = "am Tag, da ihre Zungen und ihre Hände und ihre Füße gegen sie Zeugnis ablegen werden über das, was sie zu tun pflegten." });
                        surah24.VerseList.Add(new Verse { Number = 25, TextBosnian = "Taj dan Allah će im isplatiti račun istiniti - kaznit će ih onako kako su zaslužili i oni će saznati da je Allah, baš On, očigledna Istina.", TextEnglish = "That Day, Allāh will pay them in full their true [i.e., deserved] recompense, and they will know that it is Allāh who is the manifest Truth [i.e., perfect in justice].", TextGerman = "An jenem Tag wird Allah ihr wahres Gericht(surteil) in vollem Maß zukommen lassen. Und sie werden erfahren, daß Allah die offenkundige Wahrheit ist." });
                        surah24.VerseList.Add(new Verse { Number = 26, TextBosnian = "Nevaljalima pripadaju nevaljale, a nevaljalim pripadaju nevaljali; dobrim pripadaju dobre, a dobrim pripadaju dobri. Oni nemaju ništa s tim što o njima govore; njima pripada oprost i opskrba plemenita.", TextEnglish = "Evil words are for evil men, and evil men are [subjected] to evil words. And good words are for good men, and good men are [an object] of good words.[984] Those [good people] are declared innocent of what they [i.e., slanderers] say. For them is forgiveness and noble provision.", TextGerman = "Schlechte Frauen gehören zu schlechten Männern, und schlechte Männer gehören zu schlechten Frauen. Gute Frauen gehören zu guten Männern, und gute Männer gehören zu guten Frauen. Freigesprochen sind diese von dem, was man (über sie) redet. Für sie wird es Vergebung und ehrenvolle Versorgung geben." });
                        surah24.VerseList.Add(new Verse { Number = 27, TextBosnian = "O vi koji vjerujete, u tuđe kuće ne ulazite dok dopuštenje ne zatražite i dok ukućane ne poselamite, to vam je bolje, da biste se prisjetili i pouku primili!", TextEnglish = "O you who have believed, do not enter houses other than your own houses until you ascertain welcome and greet[985] their inhabitants. That is best for you; perhaps you will be reminded [i.e., advised].", TextGerman = "O die ihr glaubt, betretet nicht andere Häuser, die nicht eure (eigenen) Häuser sind, bis ihr euch bemerkbar gemacht und ihre Bewohner begrüßt habt. Das ist besser für euch, auf daß ihr bedenken möget!" });
                        surah24.VerseList.Add(new Verse { Number = 28, TextBosnian = "A ako u njima nikoga ne nađete, ne ulazite u njih dok vam se ne dopusti; a ako vam se rekne: 'Vratite se!', vi se vratite, to je vama čednije, a Allah ono što radite dobro zna.", TextEnglish = "And if you do not find anyone therein, do not enter them until permission has been given you. And if it is said to you, 'Go back,'[986] then go back; it is purer for you. And Allāh is Knowing of what you do.", TextGerman = "Wenn ihr niemanden darin findet, dann tretet nicht ein, bis man (es) euch erlaubt. Und wenn man zu euch sagt: „Kehrt um, dann kehrt um. Das ist lauterer für euch. Und Allah weiß über das, was ihr tut, Bescheid." });
                        surah24.VerseList.Add(new Verse { Number = 29, TextBosnian = "Nije vam grijeh da ulazite u nenastanjene kuće u kojima se nalaze stvari vaše, a Allah zna ono što javno pokazujete i ono što krijete.", TextEnglish = "There is no blame upon you for entering houses not inhabited in which there is convenience[987] for you. And Allāh knows what you reveal and what you conceal.", TextGerman = "Es ist für euch keine Sünde, unbewohnte Häuser zu betreten, in denen sich ein Nießbrauch für euch befindet. Allah weiß, was ihr offen tut und was ihr heimlich tut." });
                        surah24.VerseList.Add(new Verse { Number = 30, TextBosnian = "Reci vjernicima neka obore poglede svoje i neka čuvaju stidna mjesta svoja; to im je čednije. Allah u potpunosti zna ono što oni čine.", TextEnglish = "Tell the believing men to reduce [some] of their vision[988] and guard their private parts.[989] That is purer for them. Indeed, Allāh is [fully] Aware of what they do.", TextGerman = "Sag zu den gläubigen Männern, sie sollen ihre Blicke senken und ihre Scham hüten. Das ist lauterer für sie. Gewiß, Allah ist Kundig dessen, was sie machen." });
                        surah24.VerseList.Add(new Verse { Number = 31, TextBosnian = "A reci vjernicama neka obore poglede svoje i neka čuvaju stidna mjesta svoja, i neka ne pokazuju ukrase svoje osim onoga što je ionako spoljašnje, i neka pokrivače svoje spuste preko razreza na grudima svojim, neka ukrase svoje ne pokazuju drugima; to mogu samo muževima svojim, ili očevima svojim, ili očevima muževa svojih, ili sinovima svojim, ili sinovima muževa svojih, ili braći svojoj, ili sinovima braće svoje, ili sinovima sestara svojih, ili ženama svojim, ili onim što su u posjedu njihovu, ili onim koji slijede uz njih - muškarcima koji nemaju prohtjeva za ženama, ili djeci koja još ne raspoznaju stidna mjesta žena; i neka ne udaraju nogama svojim kako bi se znalo za njihove ukrase skrivene. I svi se Allahu pokajte, o vjernici, da biste uspjeli.", TextEnglish = "And tell the believing women to reduce [some] of their vision[990] and guard their private parts and not expose their adornment[991] except that which [necessarily] appears thereof[992] and to wrap [a portion of] their headcovers over their chests and not expose their adornment [i.e., beauty] except to their husbands, their fathers, their husbands' fathers, their sons, their husbands' sons, their brothers, their brothers' sons, their sisters' sons, their women, that which their right hands possess [i.e., slaves], or those male attendants having no physical desire,[993] or children who are not yet aware of the private aspects of women. And let them not stamp their feet to make known what they conceal of their adornment. And turn to Allāh in repentance, all of you, O believers, that you might succeed.", TextGerman = "Und sag zu den gläubigen Frauen, sie sollen ihre Blicke senken und ihre Scham hüten, ihren Schmuck nicht offen zeigen, außer dem, was (sonst) sichtbar ist. Und sie sollen ihre Kopftücher auf den Brustschlitz ihres Gewandes schlagen und ihren Schmuck nicht offen zeigen, außer ihren Ehegatten, ihren Vätern, den Vätern ihrer Ehegatten, ihren Söhnen, den Söhnen ihrer Ehegatten, ihren Brüdern, den Söhnen ihrer Brüder und den Söhnen ihrer Schwestern, ihren Frauen, denen, die ihre rechte Hand besitzt, den männlichen Gefolgsleuten, die keinen (Geschlechts)trieb (mehr) haben, den Kindern, die auf die Blöße der Frauen (noch) nicht aufmerksam geworden sind. Und sie sollen ihre Füße nicht aneinanderschlagen, damit (nicht) bekannt wird, was sie von ihrem Schmuck verborgen tragen. Wendet euch alle reumütig Allah zu, ihr Gläubigen, auf daß es euch wohl ergehen möge!" });
                        surah24.VerseList.Add(new Verse { Number = 32, TextBosnian = "Udajite neudate i ženite neoženjene, i čestite robove i robinje svoje; ako su siromašni, Allah će im iz obilja Svoga dati. Allah je neizmjerno darežljiv i Onaj Koji sve zna.", TextEnglish = "And marry the unmarried among you and the righteous among your male slaves and female slaves. If they should be poor, Allāh will enrich them from His bounty, and Allāh is all-Encompassing and Knowing.", TextGerman = "Und verheiratet die noch ledigen (Männer und Frauen) unter euch und die Rechtschaffenen von euren Sklaven und euren Sklavinnen. Wenn sie arm sind, wird Allah sie durch Seine Huld reich machen. Allah ist Allumfassend und Allwissend." });
                        surah24.VerseList.Add(new Verse { Number = 33, TextBosnian = "I neka se suzdrže oni koji nemaju mogućnosti da se ožene, dok im Allah iz obilja Svoga ne pomogne! A s onima u posjedu vašem koji žele da se otkupe, ako znate da su u stanju da to učine, o otkupu se dogovorite. I dajte im nešto od Allahovog imetka koji je vama dao. I ne nagonite djevojke - robinje svoje da se bludom bave - ako one žele da budu čestite - da biste stekli prolazna dobra ovoga svijeta; a ako ih neko na to prisili, Allah će im, nakon što su bile primorane, oprostiti i prema njima milostiv biti.", TextEnglish = "But let them who find not [the means for] marriage abstain [from sexual relations] until Allāh enriches them from His bounty. And those who seek a contract [for eventual emancipation] from among whom your right hands possess[994] - then make a contract with them if you know there is within them goodness and give them from the wealth of Allāh which He has given you. And do not compel your slave girls to prostitution, if they desire chastity, to seek [thereby] the temporary interests of worldly life. And if someone should compel them, then indeed, Allāh is [to them], after their compulsion, Forgiving and Merciful.", TextGerman = "Diejenigen, die keine (Möglichkeit zum) Heirat(en) finden, sollen keusch bleiben, bis Allah sie durch Seine Huld reich macht. Und denjenigen von denen, die eure rechte Hand besitzt, die einen Freibrief begehren, sollt ihr einen Freibrief ausstellen, wenn ihr an ihnen etwas Gutes wißt. Und gebt ihnen (etwas) vom Besitz Allahs, den Er euch gegeben hat. Und zwingt nicht eure Sklavinnen, wo sie ehrbar sein wollen, zur Hurerei im Trachten nach den Glücksgütern des diesseitigen Lebens. Wenn aber einer sie dazu zwingt, so ist Allah, nachdem sie gezwungen worden sind, Allvergebend und Barmherzig." });
                        surah24.VerseList.Add(new Verse { Number = 34, TextBosnian = "Mi vam objavljujemo jasne ajete i primjere iz života onih koji su prije vas bili i nestali i pouku bogobojaznim.", TextEnglish = "And We have certainly sent down to you distinct verses[995] and examples from those who passed on before you and an admonition for those who fear Allāh.", TextGerman = "Wir haben ja zu euch Zeichen hinabgesandt, die (alles) klar machen, und ein Beispiel (aus den Berichten) von denjenigen, die vor euch dahingegangen sind, und eine Ermahnung für die Gottesfürchtigen." });
                        surah24.VerseList.Add(new Verse { Number = 35, TextBosnian = "Allah je svjetlo nebesa i Zemlje! Primjer svjetla Njegova je kao udubina u kojoj je fitilj kandilja; fitilj je u staklenki, a staklenka je kao zvijezda blistava; užiže se blagoslovljenim drvetom maslinovim, ni istočnim ni zapadnim, čije ulje gotovo da sija kad ga vatra i ne dotakne; sama svjetlost iznad svjetlosti! Allah vodi ka svjetlosti Svojoj onoga koga On hoće. Allah navodi primjere ljudima, Allah sve dobro zna.", TextEnglish = "Allāh is the Light[996] of the heavens and the earth. The example of His light[997] is like a niche within which is a lamp;[998] the lamp is within glass, the glass as if it were a pearly [white] star lit from [the oil of] a blessed olive tree, neither of the east nor of the west, whose oil would almost glow even if untouched by fire. Light upon light. Allāh guides to His light whom He wills. And Allāh presents examples for the people, and Allāh is Knowing of all things.", TextGerman = "Allah ist das Licht der Himmel und der Erde. Das Gleichnis seines Lichtes ist das einer Nische, in der eine Lampe ist. Die Lampe ist in einem Glas. Das Glas ist, als wäre es ein funkelnder Stern. Ihr Brennstoff kommt von einem gesegneten Baum, einem Ölbaum, weder östlich noch westlich, dessen Öl beinahe schon Helligkeit verbreitete, auch wenn das Feuer es nicht berührte. Licht über Licht. Allah führt zu Seinem Licht, wen Er will. Allah prägt den Menschen die Gleichnisse, und Allah weiß über alles Bescheid." });
                        surah24.VerseList.Add(new Verse { Number = 36, TextBosnian = "U džamijama, koje se voljom Njegovom podižu i u kojima se spominje Njegovo ime- slave i veličaju Njega, ujutro i navečer", TextEnglish = "[Such niches are] in houses [i.e., mosques] which Allāh has ordered to be raised and that His name be mentioned [i.e., praised] therein; exalting Him within them in the morning and the evenings[999]", TextGerman = "(Solche Lampen gibt es) in Häusern, für die Allah erlaubt hat, daß sie errichtet werden und daß darin Sein Name genannt wird. Ihn preisen darin, am Morgen und am Abend," });
                        surah24.VerseList.Add(new Verse { Number = 37, TextBosnian = "ljudi koje kupovina i prodaja ne ometaju da Allaha spominju i koji namaz obavljaju i zekat udjeljuju, i koji strepe od Dana u kome će srca i pogledi biti uznemireni,", TextEnglish = "[Are] men whom neither commerce nor sale distracts from the remembrance of Allāh and performance of prayer and giving of zakāh. They fear a Day in which the hearts and eyes will [fearfully] turn about -", TextGerman = "Männer, die weder Handel noch Kaufgeschäft ablenken vom Gedenken Allahs, von der Verrichtung des Gebets und der Entrichtung der Abgabe, die einen Tag fürchten, an dem Herzen und Augenlicht umgekehrt werden," });
                        surah24.VerseList.Add(new Verse { Number = 38, TextBosnian = "da bi ih Allah za ono bolje što su radili nagradio i da bi im iz obilja Svoga i više dao. A Allah daje opskrbu kome hoće, bez računa.", TextEnglish = "That Allāh may reward them [according to] the best of what they did and increase them from His bounty. And Allāh gives provision to whom He wills without account [i.e., limit].", TextGerman = "damit Allah ihnen das Beste von dem vergelte, was sie getan haben, und ihnen von Seiner Huld noch mehr gebe. Und Allah versorgt, wen Er will, ohne zu rechnen." });
                        surah24.VerseList.Add(new Verse { Number = 39, TextBosnian = "A djela nevjernika su kao priviđenje u ravnici u kojoj žedan vidi vodu, ali kad do tog mjesta dođe, ništa ne nađe - a zateći će kod njega Allaha i On će mu potpuno isplatiti račun njegov; Allah veoma brzo obračunava,", TextEnglish = "But those who disbelieved - their deeds are like a mirage in a lowland which a thirsty one thinks is water until, when he comes to it, he finds it is nothing but finds Allāh before him, and He will pay him in full his due; and Allāh is swift in account.", TextGerman = "Die Werke derjenigen aber, die ungläubig sind, sind wie eine Luftspiegelung in einer Ebene, die der Durstige für Wasser hält. Wenn er dann dorthin kommt, findet er, daß es nichts ist; aber er findet Allah da, Der ihm dann seine Abrechnung in vollem Maß zukommen läßt. Allah ist schnell im Abrechnen." });
                        surah24.VerseList.Add(new Verse { Number = 40, TextBosnian = "ili su kao tmine u dubokom moru koje prekrivaju talasi iznad kojih su drugi talasi, a iznad kojih su oblaci; sve tmine jedne iznad drugih. Ako bi izvadio ruku skoro da je ne bi vidio - a onaj kome Allah ne da svjetla neće svjetla ni imati.", TextEnglish = "Or [they are] like darknesses within an unfathomable sea which is covered by waves, upon which are waves, over which are clouds - darknesses, some of them upon others. When one puts out his hand [therein], he can hardly see it. And he to whom Allāh has not granted light - for him there is no light.", TextGerman = "Oder (sie sind) wie Finsternisse in einem abgrundtiefen Meer, das von Wogen überdeckt ist, über denen (nochmals) Wogen sind, über denen (wiederum) Wolken sind: Finsternisse, eine über der anderen. Wenn er seine Hand ausstreckt, kann er sie kaum sehen. Und wem Allah kein Licht schafft, für den gibt es kein Licht." });
                        surah24.VerseList.Add(new Verse { Number = 41, TextBosnian = "Zar ne znaš da Allaha slave i veličaju svi koji su na nebesima i na Zemlji, a i ptice širenjem krila svojih; svi znaju kako će Mu se moliti i kako će Ga slaviti i veličati. A Allah dobro zna ono što oni rade.", TextEnglish = "Do you not see that Allāh is exalted by whomever is within the heavens and the earth and [by] the birds with wings spread [in flight]? Each [of them] has known his [means of] prayer and exalting [Him], and Allāh is Knowing of what they do.", TextGerman = "Siehst du nicht, daß (alle) Allah preisen, die in den Himmeln und auf der Erde sind, und (auch) die Vögel mit ausgebreiteten Flügeln? Jeder kennt ja sein Gebet und sein Preisen. Und Allah weiß Bescheid über das, was sie tun." });
                        surah24.VerseList.Add(new Verse { Number = 42, TextBosnian = "Allahova je vlast i na nebesima i na Zemlji, i kod Allaha je konačno odredište.", TextEnglish = "And to Allāh belongs the dominion of the heavens and the earth, and to Allāh is the destination.", TextGerman = "Allah gehört die Herrschaft der Himmel und der Erde. Und zu Allah ist der Ausgang." });
                        surah24.VerseList.Add(new Verse { Number = 43, TextBosnian = "Zar ne vidiš da Allah polahko pokreće oblake, a onda ih spaja i jedne nad drugima gomila, pa ti vidiš kišu kako iz njih pada: On s neba - iz brda na njemu - spušta led, pa njime koga hoće pogodi, a koga hoće poštedi - blijesak munje Njegove gotovo da oduzima vid.", TextEnglish = "Do you not see that Allāh drives clouds? Then He brings them together; then He makes them into a mass, and you see the rain emerge from within it. And He sends down from the sky, mountains [of clouds] within which is hail, and He strikes with it whom He wills and averts it from whom He wills. The flash of its lightning almost takes away the eyesight.", TextGerman = "Siehst du nicht, daß Allah die Wolken sanft bewegt, sie hierauf zusammenfügt und hierauf zu einem Haufen macht? Dann siehst du den (Platz)regen dazwischen herauskommen. Und Er sendet vom Himmel (Wolken)berge herab, mit Hagel darin, dann trifft Er damit, wen Er will, und wendet ihn ab, von wem Er will. Das Aufleuchten Seines Blitzes nimmt beinahe das Augenlicht." });
                        surah24.VerseList.Add(new Verse { Number = 44, TextBosnian = "On čini da noć i dan naizmjenice nastupaju i u tome je, doista, pouka za one koji razbor imaju.", TextEnglish = "Allāh alternates the night and the day. Indeed in that is a lesson for those who have vision.", TextGerman = "Allah läßt Nacht und Tag sich abwechseln. Darin ist wahrlich eine Lehre für diejenigen, die Einsicht besitzen." });
                        surah24.VerseList.Add(new Verse { Number = 45, TextBosnian = "Allah sva živa bića stvara od vode, neka od njih na trbuhu puze, neka idu na dvije noge, a neka, opet hode na četiri; Allah stvara što hoće, Allah, zaista, nad svime ima moć.", TextEnglish = "Allāh has created every [living] creature from water. And of them are those that move on their bellies, and of them are those that walk on two legs, and of them are those that walk on four. Allāh creates what He wills. Indeed, Allāh is over all things competent.", TextGerman = "Und Allah hat jedes Tier aus Wasser erschaffen. So gibt es unter ihnen solche, die auf ihrem Bauch kriechen, und solche, die auf zwei Beinen gehen, und (wieder) solche, die auf Vieren gehen. Allah erschafft, was Er will. Gewiß, Allah hat zu allem die Macht." });
                        surah24.VerseList.Add(new Verse { Number = 46, TextBosnian = "Mi smo objavili ajete razjašnjene; a Allah upućuje na Pravi put onoga koga On hoće.", TextEnglish = "We have certainly sent down distinct verses. And Allāh guides whom He wills to a straight path.", TextGerman = "Wir haben ja Zeichen hinabgesandt, die (alles) klar machen. Und Allah leitet, wen Er will, zu einem geraden Weg." });
                        surah24.VerseList.Add(new Verse { Number = 47, TextBosnian = "Oni govore: 'Mi vjerujemo u Allaha i Poslanika, i pokoravamo se', a potom se grupa njih okreće; nisu oni vjernici.", TextEnglish = "But they [i.e., the hypocrites] say, 'We have believed in Allāh and in the Messenger, and we obey'; then a party of them turns away after that. And those are not believers.", TextGerman = "Und sie sagen: „Wir glauben an Allah und an den Gesandten, und wir gehorchen. Hierauf, nach diesem (Wort), kehrt sich eine Gruppe von ihnen ab. Diese da sind nicht die Gläubigen." });
                        surah24.VerseList.Add(new Verse { Number = 48, TextBosnian = "Kad budu pozvani Allahu i Poslaniku Njegovu da im presudi, grupa njih odjednom leđa okrene;", TextEnglish = "And when they are called to [the words of] Allāh and His Messenger to judge between them, at once a party of them turns aside [in refusal].", TextGerman = "Und wenn sie zu Allah und Seinem Gesandten gerufen werden, damit er zwischen ihnen richte, wendet sich sogleich eine Gruppe von ihnen ab." });
                        surah24.VerseList.Add(new Verse { Number = 49, TextBosnian = "a ako je pravda na njihovoj strani, dolaze mu poslušno.", TextEnglish = "But if the right is theirs, they come to him in prompt obedience.", TextGerman = "Wenn aber das Recht auf ihrer Seite ist, dann kommen sie zu ihm, bereit, sich zu unterwerfen." });
                        surah24.VerseList.Add(new Verse { Number = 50, TextBosnian = "Da li je u srcima njihovim bolest, ili sumnjaju, ili strahuju da će Allah i Poslanik Njegov prema njima nepravedno postupiti? Naprotiv, oni su baš zulumćari.", TextEnglish = "Is there disease in their hearts? Or have they doubted? Or do they fear that Allāh will be unjust to them, or His Messenger? Rather, it is they who are the wrongdoers [i.e., the unjust].", TextGerman = "Ist denn in ihren Herzen Krankheit, oder haben sie etwa Zweifel, oder befürchten sie, daß Allah gegen sie ungerecht sein könnte, und (auch) Sein Gesandter? Nein! Vielmehr sind eben sie die Ungerechten." });
                        surah24.VerseList.Add(new Verse { Number = 51, TextBosnian = "Kad vjernici Allahu i Poslaniku Njegovu budu pozvani da im on presudi, samo reknu: 'Slušamo i pokoravamo se!' To su oni koji su uspjeli.", TextEnglish = "The only statement of the [true] believers when they are called to Allāh and His Messenger to judge between them is that they say, 'We hear and we obey.' And those are the successful.", TextGerman = "Die Rede der Gläubigen, wenn sie zu Allah und Seinem Gesandten gerufen werden, damit er zwischen ihnen richte, besteht nur darin, daß sie sagen: „Wir hören und gehorchen. Das sind diejenigen, denen es wohl ergeht." });
                        surah24.VerseList.Add(new Verse { Number = 52, TextBosnian = "Oni koji se Allahu i Poslaniku Njegovu budu pokoravali, koji se Allaha budu bojali i Njega se čuvali-to su oni koji su uspjeli i spašeni.", TextEnglish = "And whoever obeys Allāh and His Messenger and fears Allāh and is conscious of Him - it is those who are the attainers.", TextGerman = "Wer Allah und Seinem Gesandten gehorcht, Allah fürchtet und sich vor Ihm hütet, das sind die Erfolgreichen." });
                        surah24.VerseList.Add(new Verse { Number = 53, TextBosnian = "Licemjeri se zaklinju Allahom, najčvršćom zakletvom, da će sigurno ići u boj ako im ti narediš. Reci: 'Ne zaklinjite se! Vaša pokornost - poznata je! Allah, zaista, u potpunosti zna ono što radite.'", TextEnglish = "And they swear by Allāh their strongest oaths that if you ordered them, they would go forth [in Allāh's cause]. Say, 'Do not swear. [Such] obedience is known.[1000] Indeed, Allāh is [fully] Aware of that which you do.'", TextGerman = "Und sie haben bei Allah ihren kräftigsten Eid geschworen, sie würden, wenn du ihnen befiehlst, ganz gewiß hinausziehen. Sag: Schwört nicht, geziemender Gehorsam (ist gewiß besser). Gewiß, Allah ist Kundig dessen, was ihr tut." });
                        surah24.VerseList.Add(new Verse { Number = 54, TextBosnian = "Reci: 'Pokoravajte se Allahu i pokoravajte se Poslaniku!' A ako se okrenete, on je dužan raditi ono što se njemu naređuje, a vi ste dužni ono što se vama naređuje, pa ako mu budete poslušni, bit ćete na Pravom putu - a Poslanikova dužnost samo je jasna obznana.", TextEnglish = "Say, 'Obey Allāh and obey the Messenger; but if you turn away - then upon him is only that [duty] with which he has been charged, and upon you is that with which you have been charged. And if you obey him, you will be [rightly] guided. And there is not upon the Messenger except the [responsibility for] clear notification.'", TextGerman = "Sag: Gehorcht Allah und gehorcht dem Gesandten. Doch wenn ihr euch abkehrt, dann obliegt ihm nur das, was ihm auferlegt ist, und euch obliegt, was euch auferlegt ist. Wenn ihr ihm (aber) gehorcht, seid ihr rechtgeleitet. Und dem Gesandten obliegt nur die deutliche Übermittlung (der Botschaft)." });
                        surah24.VerseList.Add(new Verse { Number = 55, TextBosnian = "Allah obećava da će one među vama koji budu vjerovali i dobra djela činili sigurno nasljednicima na Zemlji postaviti, kao što je postavio namjesnicima one prije njih, i da će im zacijelo vjeru njihovu učvrstiti, onu s kojom je zadovoljan da bude njihova, i da će im sigurno strah sigurnošću zamijeniti: 'Oni će samo Meni ibadet činiti, i neće druge Meni u obožavanju pridruživati. A oni koji i poslije toga budu nezahvalni-oni su baš buntovnici.'", TextEnglish = "Allāh has promised those who have believed among you and done righteous deeds that He will surely grant them succession [to authority] upon the earth just as He granted it to those before them and that He will surely establish for them [therein] their religion which He has preferred for them and that He will surely substitute for them, after their fear, security, [for] they worship Me, not associating anything with Me. But whoever disbelieves[1001] after that - then those are the defiantly disobedient.", TextGerman = "Allah hat denjenigen von euch, die glauben und rechtschaffene Werke tun, versprochen, daß Er sie ganz gewiß als Statthalter auf der Erde einsetzen wird, so wie Er diejenigen, die vor ihnen waren, als Statthalter einsetzte, daß Er für sie ihrer Religion, der Er für sie zugestimmt hat, ganz gewiß eine feste Stellung verleihen wird, und daß Er ihnen nach ihrer Angst (, in der sie gelebt haben,) statt dessen ganz gewiß Sicherheit gewähren wird. Sie dienen Mir und gesellen Mir nichts bei. Wer aber danach ungläubig ist, jene sind die (wahren) Frevler." });
                        surah24.VerseList.Add(new Verse { Number = 56, TextBosnian = "A vi namaz obavljajte i zekat dajite i Poslaniku poslušni budite da biste milost zadobili.", TextEnglish = "And establish prayer and give zakāh and obey the Messenger - that you may receive mercy.", TextGerman = "Und verrichtet das Gebet und entrichtet die Abgabe und gehorcht dem Gesandten, auf daß ihr Erbarmen finden möget!" });
                        surah24.VerseList.Add(new Verse { Number = 57, TextBosnian = "Nikako ne misli da će se nevjernici moći suprotstaviti Njemu na Zemlji; njihovo boravište bit će Vatra, a ona je, zaista, užasno krajnje odredište.", TextEnglish = "Never think that the disbelievers are causing failure [to Allāh] upon the earth. Their refuge will be the Fire - and how wretched the destination.", TextGerman = "Meine ja nicht, daß diejenigen, die ungläubig sind, sich (Allah) auf der Erde entziehen können. Ihr Zufluchtsort wird das (Höllen)feuer sein - ein wahrlich schlimmer Ausgang!" });
                        surah24.VerseList.Add(new Verse { Number = 58, TextBosnian = "O vjernici, neka od vas u tri slučaja zatraže dopuštenje da vam uđu oni koji su u posjedu vašem i oni koji još nisu spolno zreli: prije jutarnjeg namaza, i kada u podne odložite odjeću svoju, i poslije obavljanja noćnog namaza. To su tri doba kada niste obučeni, a u drugo doba nije ni vama ni njima grijeh, ta oni kod vas često ulaze, a i vi jedni kod drugih ulazite. Tako vam Allah objašnjava propise! A Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "O you who have believed, let those whom your right hands possess and those who have not [yet] reached puberty among you ask permission of you [before entering] at three times: before the dawn prayer and when you put aside your clothing [for rest] at noon and after the night prayer. [These are] three times of privacy[1002] for you. There is no blame upon you nor upon them beyond these [periods], for they continually circulate among you - some of you, among others. Thus does Allāh make clear to you the verses [i.e., His ordinances]; and Allāh is Knowing and Wise.", TextGerman = "O die ihr glaubt, diejenigen, die eure rechte Hand (an Sklaven) besitzt, und diejenigen von euch, die noch nicht die Geschlechtsreife erreicht haben, sollen euch zu drei Zeiten um Erlaubnis bitten: vor dem Gebet (in) der Morgendäm merung, wenn ihr zur Zeit der Mittagshitze eure Gewänder ablegt, und nach dem Abendgebet. Das sind drei (Zeiten, in denen die) Blößen von euch (sichtbar sein könnten). Es ist außerhalb dieser (Zeiten) weder für euch noch für sie eine Sünde. Sie gehen oft unter euch umher, und das tut ihr untereinander. So macht Allah euch die Zeichen klar. Und Allah ist Allwissend und Allweise." });
                        surah24.VerseList.Add(new Verse { Number = 59, TextBosnian = "A kad djeca vaša dostignu spolnu zrelost, neka onda uvijek traže dopuštenje za ulazak, kao što su tražili dopuštenje oni prije njih; tako vam Allah objašnjava ajete Svoje! A Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "And when the children among you reach puberty, let them ask permission [at all times] as those before them have done. Thus does Allāh make clear to you His verses; and Allāh is Knowing and Wise.", TextGerman = "Und wenn die Kinder unter euch die Geschlechtsreife erreicht haben, dann sollen sie um Erlaubnis bitten, wie diejenigen um Erlaubnis gebeten haben, die vor ihnen waren. So macht Allah euch Seine Zeichen klar. Und Allah ist Allwissend und Allweise." });
                        surah24.VerseList.Add(new Verse { Number = 60, TextBosnian = "A starim ženama koje ne žude više za udajom nije grijeh da odlože ogrtače svoje, ali ne pokazujući ukrase svoje; a bolje im je da budu kreposne. Allah je Onaj Koji sve čuje i Onaj Koji sve zna.", TextEnglish = "And women of post-menstrual age who have no desire for marriage - there is no blame upon them for putting aside their outer garments [but] not displaying adornment. But to modestly refrain [from that] is better for them. And Allāh is Hearing and Knowing.", TextGerman = "Und für diejenigen unter den Frauen, die sich zur Ruhe gesetzt haben und nicht mehr zu heiraten hoffen, ist es keine Sünde, wenn sie ihre Gewänder ablegen, ohne jedoch ihren Schmuck zur Schau zu stellen. Doch sich (dessen) zu enthalten, ist besser für sie. Und Allah ist Allhörend und Allwissend." });
                        surah24.VerseList.Add(new Verse { Number = 61, TextBosnian = "Nije grijeh slijepom, niti je grijeh hromom, niti je grijeh bolesnom, a niti vama samima da jedete u kućama vašim, ili u kućama očeva vaših, ili u kućama majki vaših, ili u kućama braće vaše, ili u kućama sestara vaših, ili u kućama amidža vaših, ili u kućama tetaka vaših po ocu, ili u kućama daidža vaših, ili u kućama tetaka vaših po majci, ili onih čiji su ključevi u vas ili u prijatelja vašeg; nije vam grijeh da jedete zajednički ili pojedinačno. A kad ulazite u kuće, vi ukućane njene pozdravite pozdravom od Allaha propisanim, blagoslovljenim i lijepim. Tako vam Allah objašnjava ajete da biste razumjeli!", TextEnglish = "There is not upon the blind [any] constraint nor upon the lame constraint nor upon the ill constraint nor upon yourselves when you eat from your [own] houses or the houses of your fathers or the houses of your mothers or the houses of your brothers or the houses of your sisters or the houses of your father's brothers or the houses of your father's sisters or the houses of your mother's brothers or the houses of your mother's sisters or [from houses] whose keys you possess or [from the house] of your friend. There is no blame upon you whether you eat together or separately. But when you enter houses, give greetings of peace[1003] upon each other - a greeting from Allāh, blessed and good. Thus does Allāh make clear to you the verses [of ordinance] that you may understand.", TextGerman = "Es gibt für den Blinden keinen Grund zur Bedrängnis, es gibt für den Hinkenden keinen Grund zur Bedrängnis, es gibt für den Kranken keinen Grund zur Bedrängnis, sowie auch für euch nicht, in euren (eigenen) Häusern zu essen oder in den Häusern eurer Väter, den Häusern eurer Mütter, den Häusern eurer Brüder, den Häusern eurer Schwestern, den Häusern eurer Onkel väterlicherseits, den Häusern eurer Tanten väterlicherseits, den Häusern eurer Onkel mütterlicherseits, den Häusern eurer Tanten mütterlicherseits, (in einem Haus,) dessen Schlüssel ihr besitzt, oder (im Haus) eures Freundes. Es ist für euch keine Sünde, gemeinsam oder getrennt zu essen. Wenn ihr nun Häuser betretet, so grüßt einander mit einem Gruß, der von Allah kommt, (mit) einem gesegneten, guten (Gruß). So macht Allah euch die Zeichen klar, auf daß ihr begreifen möget." });
                        surah24.VerseList.Add(new Verse { Number = 62, TextBosnian = "Pravi vjernici samo su oni koji u Allaha i Njegova Poslanika vjeruju, a koji se, kad su s njim na kakvom važnom sastanku, ne udaljuju dok od njega dopuštenje ne dobiju. Oni koji od tebe traže dopuštenje, u Allaha i Poslanika Njegova, doista, vjeruju. I kad oni zatraže dopuštenje od tebe zbog kakva posla svoga, dopusti kome hoćeš od njih, i zamoli Allaha da im oprosti - zaista je Allah Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "The believers are only those who believe in Allāh and His Messenger and, when they are [meeting] with him for a matter of common interest, do not depart until they have asked his permission. Indeed, those who ask your permission, [O Muḥammad] - those are the ones who believe in Allāh and His Messenger. So when they ask your permission due to something of their affairs, then give permission to whom you will among them and ask forgiveness for them of Allāh. Indeed, Allāh is Forgiving and Merciful.", TextGerman = "Die (wahren) Gläubigen sind ja diejenigen, die an Allah und Seinen Gesandten glauben und, wenn sie bei ihm in einer für alle wichtigen Angelegenheit versammelt sind, nicht eher weggehen, als bis sie ihn um Erlaubnis gebeten haben. Diejenigen, die dich um Erlaubnis bitten, das sind diejenigen, die an Allah und Seinen Gesandten glauben. Wenn sie dich nun wegen manch eines eigenen Anliegens um Erlaubnis bitten, so gib Erlaubnis, wem von ihnen du willst. Und bitte Allah um Vergebung für sie, denn gewiß, Allah ist Allvergebend und Barmherzig." });
                        surah24.VerseList.Add(new Verse { Number = 63, TextBosnian = "Neka vaše dozivanje Poslanika ne bude kao kad vi jedni druge dozivate; Allah sigurno zna one među vama koji se kradom izvlače. Neka se pripaze oni koji postupaju suprotno naređenju njegovu da ih iskušenje kakvo ne stigne, ili da ih patnja bolna ne snađe.", TextEnglish = "Do not make [your] calling of the Messenger among yourselves as the call of one of you to another. Already Allāh knows those of you who slip away, concealed by others. So let those beware who dissent from his [i.e., the Prophet's] order,[1004] lest fitnah[1005] strike them or a painful punishment.", TextGerman = "Erachtet nicht den Aufruf des Gesandten unter euch wie den Aufruf eines von euch an die anderen. Allah kennt ja diejenigen von euch, die sich (unbemerkt) davonstehlen, indem sie sich hinter anderen verstecken. So sollen diejenigen, die Seinem Befehl zuwiderhandeln, sich vorsehen, daß nicht eine Versuchung sie trifft oder schmerzhafte Strafe sie trifft." });
                        surah24.VerseList.Add(new Verse { Number = 64, TextBosnian = "Allahovo je sve što je na nebesima i na Zemlji, On sigurno zna kakvi ste vi, i On će ih obavijestiti o svemu što su uradili na Dan kad se budu Njemu vratili. A Allah sve dobro zna!", TextEnglish = "Unquestionably, to Allāh belongs whatever is in the heavens and earth. Already He knows that upon which you [stand][1006] and [knows] the Day[1007] when they will be returned to Him and He will inform them of what they have done. And Allāh is Knowing of all things.", TextGerman = "Sicherlich, Allah gehört, was in den Himmeln und auf der Erde ist. Er weiß ja, in welchem Zustand ihr euch befindet und den Tag, da sie zu Ihm zurückgebracht werden. Dann wird Er ihnen kundtun, was sie getan haben. Und Allah weiß über alles Bescheid." });
                        result = surah24;
                        break;
                    #endregion

                    // 
                    #region
                    case 25:
                        Surah surah25 = new Surah(); surah25.Name = Constants.Surah25Name; surah25.Number = 25; surah25.VerseList = new List<Verse>();
                        surah25.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah25.VerseList.Add(new Verse { Number = 1, TextBosnian = "Neizmjerno je Blagoslovljen Onaj Koji robu Svome objavljuje Furkan da bi svjetovima bio upozoritelj,", TextEnglish = "Blessed is He who sent down the Criterion upon His Servant that he may be to the worlds a warner -", TextGerman = "Segensreich ist Derjenige, Der Seinem Diener die Unterscheidung offenbart hat, damit er für die Weltenbewohner ein Warner sei," });
                        surah25.VerseList.Add(new Verse { Number = 2, TextBosnian = "Onaj Kome pripada vlast na nebesima i na Zemlji, Koji nema djeteta, Koji u vlasti nema ortaka i Koji je sve stvorio i kako treba uredio!", TextEnglish = "He to whom belongs the dominion of the heavens and the earth and who has not taken a son and has not had a partner in dominion and has created each thing and determined it with [precise] determination.", TextGerman = "Er, Dem die Herrschaft der Himmel und der Erde gehört, Der Sich kein Kind genommen hat und Der keinen Teilhaber an der Herrschaft hat und alles erschaffen und ihm dabei sein rechtes Maß gegeben hat." });
                        surah25.VerseList.Add(new Verse { Number = 3, TextBosnian = "A oni su uzeli mimo Njega božanstva koja ništa ne stvaraju, a koja su sama stvorena, koja nisu u stanju da od sebe neku štetu otklone ni da sebi kakvu korist pribave, i koja ne posjeduju ni smrt, ni život, a ni proživljenje.", TextEnglish = "But they have taken besides Him gods which create nothing, while they are created, and possess not for themselves any harm or benefit and possess not [power to cause] death or life or resurrection.", TextGerman = "Und sie haben sich außer Ihm Götter genommen, die nichts erschaffen, während sie (selbst) erschaffen werden, und die sich selbst weder Schaden noch Nutzen zu bringen vermögen und die weder über Tod noch über Leben noch über Auferstehung verfügen." });
                        surah25.VerseList.Add(new Verse { Number = 4, TextBosnian = "Oni koji ne vjeruju rekoše: 'Ovo nije ništa drugo do velika laž koju on izmišlja, a u tome mu i drugi ljudi pomažu.' Počiniše oni zulum i potvoru.", TextEnglish = "And those who disbelieve say, 'This [Qur’ān] is not except a falsehood he invented, and another people assisted him in it.' But they have committed an injustice and a lie.", TextGerman = "Diejenigen, die ungläubig sind, sagen: „Das ist nur eine ungeheuerliche Lüge, die er ersonnen hat und bei der andere Leute ihm geholfen haben. Sie begehen da ja Ungerechtigkeit und Falschaussage." });
                        surah25.VerseList.Add(new Verse { Number = 5, TextBosnian = "I govore: 'To su izmišljotine naroda drevnih koje je on prepisao, pa mu se čitaju jutrom i večeri.'", TextEnglish = "And they say, 'Legends of the former peoples which he has written down, and they are dictated to him morning and afternoon.'", TextGerman = "Und sie sagen: „(Es sind) Fabeln der Früheren, die er sich aufgeschrieben hat. So werden sie ihm morgens und abends vorgesagt." });
                        surah25.VerseList.Add(new Verse { Number = 6, TextBosnian = "Reci: 'Objavljuje ga Onaj Koji zna tajne nebesa i Zemlje; On je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "Say, [O Muḥammad], 'It has been revealed by He who knows [every] secret within the heavens and the earth. Indeed, He is ever Forgiving and Merciful.'", TextGerman = "Sag: Herabgesandt hat ihn Derjenige, Der das Geheime in den Himmeln und auf der Erde kennt. Gewiß, Er ist Allvergebend und Barmherzig." });
                        surah25.VerseList.Add(new Verse { Number = 7, TextBosnian = "I još govore: 'Šta je ovom poslaniku, on hranu jede i po trgovima hoda; zašto mu nije poslan jedan melek da zajedno s njim bude upozoritelj,", TextEnglish = "And they say, 'What is this messenger that eats food and walks in the markets? Why was there not sent down to him an angel so he would be with him a warner?", TextGerman = "Und sie sagen: „Was ist mit diesem Gesandten, daß er Speise ißt und auf den Märkten umhergeht? O würde doch zu ihm ein Engel herabgesandt, um mit ihm ein Warner zu sein!" });
                        surah25.VerseList.Add(new Verse { Number = 8, TextBosnian = "ili da mu se spusti riznica, ili da ima bašču iz koje bi se hranio' I zulumćari još govore: 'Vi samo opčinjena čovjeka slijedite!'", TextEnglish = "Or [why is not] a treasure presented to him [from heaven], or does he [not] have a garden from which he eats?' And the wrongdoers say, 'You follow not but a man affected by magic.'", TextGerman = "Oder würde doch zu ihm ein Schatz herabgeworfen, oder hätte er doch einen Garten, von dem er essen könnte! Und die Ungerechten sagen: „Ihr folgt ja nur ei nem Mann, der einem Zauber verfallen ist." });
                        surah25.VerseList.Add(new Verse { Number = 9, TextBosnian = "Vidi šta o tebi oni govore, pa onda lutaju i Pravi put ne mogu da nađu.", TextEnglish = "Look how they strike for you comparisons;[1009] but they have strayed, so they cannot [find] a way.", TextGerman = "Schau, wie sie dir Gleichnisse prägen! Dabei sind sie abgeirrt, und so können sie keinen (Aus)weg mehr (finden)." });
                        surah25.VerseList.Add(new Verse { Number = 10, TextBosnian = "Blagoslovljen je Onaj Koji ti, ako hoće, može dati bolje od toga: bašče kroz koje teku rijeke i dvorce.", TextEnglish = "Blessed is He who, if He willed, could have made for you [something] better than that - gardens beneath which rivers flow - and could make for you palaces.", TextGerman = "Segensreich ist Derjenige, Der dir, wenn Er will, etwas Besseres als dies zuteilen kann: Gärten, durcheilt von Bächen, und Der dir Schlösser zuteilen kann." });
                        surah25.VerseList.Add(new Verse { Number = 11, TextBosnian = "Oni, čak, i Čas kijametski poriču, a Mi smo za one koji Čas kijametski poriču pripremili Vatru razbuktalu.", TextEnglish = "But they have denied the Hour, and We have prepared for those who deny the Hour a Blaze.", TextGerman = "Aber nein! Sie erklären die Stunde für Lüge. Doch haben Wir für diejenigen, die die Stunde für Lüge erklären, eine Feuerglut bereitet." });
                        surah25.VerseList.Add(new Verse { Number = 12, TextBosnian = "Kada ih ona vidi iz daljine, čut će kako gnjevna ključa i od bijesa huči,", TextEnglish = "When it [i.e., the Hellfire] sees them from a distant place, they will hear its fury and roaring.", TextGerman = "Wenn sie sie aus der Ferne sieht, hören sie (bereits) von ihr Grollen und Fauchen." });
                        surah25.VerseList.Add(new Verse { Number = 13, TextBosnian = "a kad budu bačeni u nju tamo gdje je tijesno, ruku privezanih za vrat, propast će tamo prizivati.", TextEnglish = "And when they are thrown into a narrow place therein bound in chains, they will cry out thereupon for destruction.", TextGerman = "Und wenn sie da in einen engen Ort zusammengebunden geworfen werden, rufen sie dort nach Vernichtung." });
                        surah25.VerseList.Add(new Verse { Number = 14, TextBosnian = "Ne prizivajte danas jednu propast, nego prizivajte propast mnogostruku!'", TextEnglish = "[They will be told], 'Do not cry this Day for one destruction but cry for much destruction.'", TextGerman = "„Ruft heute nicht nach (nur) einer einzigen Vernichtung, sondern ruft nach vielen Vernichtungen!" });
                        surah25.VerseList.Add(new Verse { Number = 15, TextBosnian = "Reci: 'Da li je bolje to ili vječni Džennet, koji je obećan bogobojaznima? On će im nagrada i prebivalište biti.'", TextEnglish = "Say, 'Is that better or the Garden of Eternity which is promised to the righteous? It will be for them a reward and destination.", TextGerman = "Sag: Ist das besser oder der Garten der Ewigkeit, der den Gottesfürchtigen versprochen ist? Er ist für sie Lohn und Reiseziel." });
                        surah25.VerseList.Add(new Verse { Number = 16, TextBosnian = "U njemu će sve što zažele imati, vječno će u tome biti; to je Gospodaru tvome obećanje obavezno.", TextEnglish = "For them therein is whatever they wish, [while] abiding eternally. It is ever upon your Lord a promise [worthy to be] requested.[1010]", TextGerman = "Sie haben darin, was sie wollen, und werden ewig darin bleiben. Das ist ein für deinen Herrn bindendes Versprechen, nach dem gefragt werden wird." });
                        surah25.VerseList.Add(new Verse { Number = 17, TextBosnian = "A na Dan kada ih On sakupi, a i one koje su pored Allaha obožavali, te upita: 'Jeste li vi ove robove Moje u zabludu zaveli, ili su oni sami s Pravog puta zalutali?'", TextEnglish = "And [mention] the Day He will gather them and that which they worship besides Allāh and will say, 'Did you mislead these, My servants, or did they [themselves] stray from the way?'", TextGerman = "Und am Tag, da Er sie und das, dem sie anstatt Allahs dienen, versammeln wird, sagt Er: „Wart ihr es, die diese Meine Diener in die Irre geführt haben, oder sind sie selbst vom Weg abgeirrt?" });
                        surah25.VerseList.Add(new Verse { Number = 18, TextBosnian = "oni će reći: 'Uzvišen nek si Ti, nezamislivo je da smo mi pored Tebe ikakve zaštitnike uzimali, nego, Ti si ovima i precima njihovim dao da uživaju, pa su zaboravili opomenu; oni su propast zaslužili!'", TextEnglish = "They will say, 'Exalted are You! It was not for us to take besides You any allies [i.e., protectors]. But You provided comforts for them and their fathers until they forgot the message and became a people ruined.'", TextGerman = "Sie werden sagen: „Preis sei Dir! Es ziemte uns nicht, uns anstatt Deiner (andere) Schutzherren zu nehmen. Aber Du ließt sie und ihre Väter genießen, bis sie die Ermahnung vergaßen und zu einem Volk des Niedergangs wurden." });
                        surah25.VerseList.Add(new Verse { Number = 19, TextBosnian = "Oni će poreći to što vi govorite, a vi nećete biti u mogućnosti da patnju otklonite niti da pomoć nađete. A onome od vas koji je zulum činio dat ćemo da patnju veliku iskusi.", TextEnglish = "So they will deny you, [disbelievers], in what you say,[1011] and you cannot avert [punishment] or [find] help. And whoever commits injustice[1012] among you - We will make him taste a great punishment.", TextGerman = "- „Nun haben sie euch in dem, was ihr sagt, der Lüge bezichtigt. Da könnt ihr weder (etwas) abwenden noch Hilfe erfahren. Und wer von euch Unrecht tut, den lassen Wir große Strafe kosten" });
                        surah25.VerseList.Add(new Verse { Number = 20, TextBosnian = "Mi prije tebe nismo poslali nijednog poslanika koji nije jeo i po trgovima hodao. Mi činimo da jedni drugima iskušenje budete, da vidimo hoćete li biti strpljivi. A Gospodar tvoj vidi sve.", TextEnglish = "And We did not send before you, [O Muḥammad], any of the messengers except that they ate food and walked in the markets. And We have made some of you [people] as trial for others - will you have patience? And ever is your Lord, Seeing.", TextGerman = "Und Wir sandten vor dir keine Gesandten, die nicht Speise aßen und auf den Märkten umhergingen. Und Wir haben die einen von euch zur Versuchung für die anderen gemacht, (um festzustellen,) ob ihr standhaft seid. Und dein Herr sieht sehr wohl (alles)." });
                        surah25.VerseList.Add(new Verse { Number = 21, TextBosnian = "I govore oni koji se ne nadaju susretu s Nama: 'Zašto nam se ne pošalju meleki, ili zašto Gospodara svoga ne vidimo O sebi su oni visoko mislili i veoma se osilili.", TextEnglish = "And those who do not expect the meeting with Us say, 'Why were not angels sent down to us, or [why] do we [not] see our Lord?' They have certainly become arrogant within themselves[1013] and [become] insolent with great insolence.", TextGerman = "Und diejenigen, die nicht die Begegnung mit Uns erwarten, sagen: „O würden doch die Engel auf uns herabgesandt, oder könnten wir doch unseren Herrn sehen! Sie verhalten sich ja hochmütig in Bezug auf sich selbst, und sie lehnen sich in großer Mißachtung auf." });
                        surah25.VerseList.Add(new Verse { Number = 22, TextBosnian = "Onaj dan kad ugledaju meleke, neće biti tada radosnog navještaja prestupnicima. I govorit će meleki: 'Vama je to potpuno uskraćeno.'", TextEnglish = "The day they see the angels[1014] - no good tidings will there be that day for the criminals, and [the angels] will say, 'Prevented and inaccessible.'[1015]", TextGerman = "Am Tag, da sie die Engel sehen, an dem Tag wird es für die Übeltäter keine frohe Botschaft geben, und sie werden sagen: „Das sei (euch) verwehrt und versperrt!" });
                        surah25.VerseList.Add(new Verse { Number = 23, TextBosnian = "I Mi ćemo pristupiti onome što počiniše i u prah i pepeo ih pretvoriti.", TextEnglish = "And We will approach [i.e., regard][1016] what they have done of deeds and make them as dust dispersed.", TextGerman = "Und Wir werden Uns den Werken, die sie getan haben, zuwenden und sie zu verwehtem Staub machen." });
                        surah25.VerseList.Add(new Verse { Number = 24, TextBosnian = "Stanovnici Dženneta će taj dan najbolje prebivalište i najljepše odmaralište imati.", TextEnglish = "The companions of Paradise, that Day, are [in] a better settlement and better resting place.", TextGerman = "Die Insassen des (Paradies)gartens werden an jenem Tag einen besseren Aufenthalt und einen schöneren Ruheplatz haben." });
                        surah25.VerseList.Add(new Verse { Number = 25, TextBosnian = "A na Dan kad se nebo rascijepi i iz njega se oblak pojavi i kad se meleki sigurno spuste,", TextEnglish = "And [mention] the Day when the heaven will split open with [emerging] clouds,[1017] and the angels will be sent down in successive descent.", TextGerman = "Und am Tag, da sich der Himmel, Wolken hervorbringend, aufspaltet und die Engel eindrucksvoll herabgesandt werden," });
                        surah25.VerseList.Add(new Verse { Number = 26, TextBosnian = "taj dan će istinska vlast biti samo u Svemilosnog, a bit će to mučan dan za nevjernike.", TextEnglish = "True sovereignty, that Day, is for the Most Merciful. And it will be upon the disbelievers a difficult Day.", TextGerman = "an jenem Tag gehört die Herrschaft, die wahre (Herrschaft) dem Allerbanner, und es wird für die Ungläubigen ein schwerer Tag sein." });
                        surah25.VerseList.Add(new Verse { Number = 27, TextBosnian = "Na Dan kad zulumćar prste svoje bude grizao govoreći: 'Kamo sreće da sam se uz Poslanika Pravog puta držao!", TextEnglish = "And the Day the wrongdoer will bite on his hands [in regret] he will say, 'Oh, I wish I had taken with the Messenger a way.[1018]", TextGerman = "Und an dem Tag wird der Ungerechte sich in die Hände beißen und sagen: „O hätte ich doch mit dem Gesandten einen Weg eingeschlagen!" });
                        surah25.VerseList.Add(new Verse { Number = 28, TextBosnian = "Kamo sreće, teško meni, da toga i toga za prijatelja nisam uzeo!", TextEnglish = "Oh, woe to me! I wish I had not taken that one[1019] as a friend.", TextGerman = "O wehe mir! Hätte ich doch nicht den Soundso zum Freund genommen!" });
                        surah25.VerseList.Add(new Verse { Number = 29, TextBosnian = "On me od Opomene - Kur'ana odvratio nakon što mi je došao!' - a šejtan čovjeka uvijek ostavlja na cjedilu.", TextEnglish = "He led me away from the remembrance[1020] after it had come to me. And ever is Satan, to man, a deserter.'[1021]", TextGerman = "Er hat mich ja von der Ermahnung abirren lassen, nachdem sie zu mir gekommen war. Der Satan pflegt den Menschen stets im Stich zu lassen." });
                        surah25.VerseList.Add(new Verse { Number = 30, TextBosnian = "Poslanik je rekao: 'Gospodaru moj, zaista narod moj ovaj Kur'an izbjegava!'", TextEnglish = "And the Messenger has said, 'O my Lord, indeed my people have taken this Qur’ān as [a thing] abandoned.'[1022]", TextGerman = "Und der Gesandte sagt: „O mein Herr, mein Volk mied diesen Qur’an unter Mißachtung." });
                        surah25.VerseList.Add(new Verse { Number = 31, TextBosnian = "Isto tako smo Mi dali da svakom vjerovjesniku prestupnici neprijatelji budu. A tebi je Gospodar tvoj dovoljan kao uputitelj i pomagač!", TextEnglish = "And thus have We made for every prophet an enemy from among the criminals. But sufficient is your Lord as a guide and a helper.", TextGerman = "So haben Wir für jeden Propheten einen Feind aus den Reihen der Übeltäter bestellt. Und dein Herr genügt als Führer und Helfer." });
                        surah25.VerseList.Add(new Verse { Number = 32, TextBosnian = "Oni koji ne vjeruju govore: 'Zašto mu Kur'an nije odjednom cijeli objavljen A tako se objavljuje da bismo njime srce tvoje učvrstili, i Mi ga činimo razgovijetnim.", TextEnglish = "And those who disbelieve say, 'Why was the Qur’ān not revealed to him all at once?' Thus [it is] that We may strengthen thereby your heart. And We have spaced it distinctly.[1023]", TextGerman = "Und diejenigen, die ungläubig sind, sagen: „O würde ihm doch der Qur’an als Ganzes offenbart! So (geschieht es aber), damit Wir dein Herz mit ihm festigen. Und Wir haben ihn wohlgeordnet vorgetragen." });
                        surah25.VerseList.Add(new Verse { Number = 33, TextBosnian = "Oni ti neće nijedan prigovor uputiti, a da ti Mi nećemo odgovor i najljepše objašnjenje navesti.", TextEnglish = "And they do not come to you with an example [i.e., argument] except that We bring you the truth and the best explanation.", TextGerman = "Und sie bringen dir kein Beispiel, ohne daß Wir dir die Wahrheit und eine bessere Erklärung brächten." });
                        surah25.VerseList.Add(new Verse { Number = 34, TextBosnian = "Oni koji će biti vučeni na svojim licima u Džehennem bit će u najgorem položaju i najdalje će zalutati.", TextEnglish = "The ones who are gathered on their faces to Hell - those are the worst in position and farthest astray in [their] way.", TextGerman = "Diejenigen, die auf ihren Gesichtern zur Hölle versammelt werden, jene sind in der schlechtesten Lage und sind am weitesten vom (rechten) Weg abgeirrt." });
                        surah25.VerseList.Add(new Verse { Number = 35, TextBosnian = "Mi smo Musau Knjigu dali i brata mu Haruna pomoćnikom učinili,", TextEnglish = "And We had certainly given Moses the Scripture and appointed with him his brother Aaron as an assistant.", TextGerman = "Und Wir gaben bereits Musa die Schrift und bestellten ihm seinen Bruder Harun, (die Last) mitzutragen." });
                        surah25.VerseList.Add(new Verse { Number = 36, TextBosnian = "pa rekli: 'Otiđite vas dvojica narodu koji znakove Naše poriče', i poslije smo taj narod potpuno uništili.", TextEnglish = "And We said, 'Go both of you to the people who have denied Our signs.' Then We destroyed them with [complete] destruction.", TextGerman = "Da sagten Wir: „Geht zu dem Volk, das Unsere Zeichen für Lüge erklärt hat. Dann zerstörten Wir sie vollständig." });
                        surah25.VerseList.Add(new Verse { Number = 37, TextBosnian = "A i narod Nuhov smo, kad su poslanike u laž utjerivali, potopili i ljudima ih kao znak učinili, a zulumćarima smo patnju bolnu pripremili.", TextEnglish = "And the people of Noah - when they denied the messengers,[1024] We drowned them, and We made them for mankind a sign. And We have prepared for the wrongdoers a painful punishment.", TextGerman = "Und auch das Volk Nuhs, als sie die Gesandten der Lüge bezichtigten, ließen Wir ertrinken und machten sie für die Menschen zu einem Zeichen. Und Wir haben für die Ungerechten schmerzhafte Strafe bereitet." });
                        surah25.VerseList.Add(new Verse { Number = 38, TextBosnian = "I Adu i Semudu i stanovnicima Ressa i mnogim narodima između njih,", TextEnglish = "And [We destroyed] ʿAad and Thamūd and the companions of the well[1025] and many generations between them.", TextGerman = "Und (desgleichen auch) die ’Ad, die Tamud, die Leute von ar-Rass und viele Geschlechter dazwischen." });
                        surah25.VerseList.Add(new Verse { Number = 39, TextBosnian = "svima smo primjere za pouku navodili, i sve smo ih poslije sasvim uništili.", TextEnglish = "And for each We presented examples [as warnings], and each We destroyed with [total] destruction.", TextGerman = "(Ihnen) allen prägten Wir Gleichnisse, und (sie) alle zerstörten Wir vollständig." });
                        surah25.VerseList.Add(new Verse { Number = 40, TextBosnian = "A oni prolaze pored naselja na koje se sručila kobna kiša- zar ga ne vide? Naprotiv, oni ni proživljenje ne očekuju.", TextEnglish = "And they have already come upon the town which was showered with a rain of evil [i.e., stones]. So have they not seen it? But they are not expecting resurrection.[1026]", TextGerman = "Sie kamen doch an der Stadt vorbei, auf die der Unheilsregen niederging. Haben sie sie denn nicht gesehen? Nein! Vielmehr erwarten sie keine Auferstehung." });
                        surah25.VerseList.Add(new Verse { Number = 41, TextBosnian = "Kad te vide, rugaju ti se: 'Je li ovo onaj kojeg je Allah kao poslanika poslao?!", TextEnglish = "And when they see you, [O Muḥammad], they take you not except in ridicule, [saying], 'Is this the one whom Allāh has sent as a messenger?", TextGerman = "Und wenn sie dich sehen, machen sie sich über dich nur lustig: „Ist das derjenige, den Allah als Gesandten geschickt hat?" });
                        surah25.VerseList.Add(new Verse { Number = 42, TextBosnian = "Umalo da nas od božanstava naših nije odvratio, ali mi im vjerni ostadosmo.' A kad dožive patnju, saznat će ko je dalje s Pravog puta bio skrenuo!", TextEnglish = "He almost would have misled us from our gods had we not been steadfast in [worship of] them.' But they are going to know, when they see the punishment, who is farthest astray in [his] way.", TextGerman = "Beinahe hätte er uns fürwahr von unseren Göttern abirren lassen, wenn wir nicht (beharrlich) an ihnen festgehalten hätten. Sie werden, wenn sie die Strafe sehen, erfahren, wer weiter vom Weg abgeirrt ist." });
                        surah25.VerseList.Add(new Verse { Number = 43, TextBosnian = "Kaži ti Meni, hoćeš li ti biti zaštitnik onome koji je za svoga boga svoje hirove uzeo?", TextEnglish = "Have you seen the one who takes as his god his own desire? Then would you be responsible for him?", TextGerman = "Was meinst du wohl zu einem, der sich seine Neigung zu seinem Gott nimmt? Würdest du denn Sachwalter über ihn sein können?" });
                        surah25.VerseList.Add(new Verse { Number = 44, TextBosnian = "Misliš li ti da većina njih čuje ili razumije?! Oni su kao stoka, čak su još dalje s Puta pravog skrenuli!", TextEnglish = "Or do you think that most of them hear or reason? They are not except like livestock.[1027] Rather, they are [even] more astray in [their] way.", TextGerman = "Oder meinst du, daß die meisten von ihnen hören oder begreifen? Sie sind doch nur wie das Vieh. Aber nein! Sie irren noch weiter vom Weg ab." });
                        surah25.VerseList.Add(new Verse { Number = 45, TextBosnian = "Zar ne vidiš kako Gospodar tvoj sjenu rasprostire - a da hoće, ostavio bi je da miruje. A zatim smo učinili da na nju Sunce ukazuje.", TextEnglish = "Have you not considered your Lord - how He extends the shadow, and if He willed, He could have made it stationary? Then We made the sun for it an indication.[1028]", TextGerman = "Siehst du nicht deinen Herrn, wie Er den Schatten lang werden läßt? Wenn Er wollte, würde Er ihn fürwahr stillstehen lassen. Hierauf machen Wir die Sonne zu einem Hinweis auf ihn." });
                        surah25.VerseList.Add(new Verse { Number = 46, TextBosnian = "A poslije je malo-pomalo Sebi privlačimo.", TextEnglish = "Then We [retract and] hold it with Us for a brief grasp.[1029]", TextGerman = "Hierauf ziehen Wir ihn leicht zu Uns ein." });
                        surah25.VerseList.Add(new Verse { Number = 47, TextBosnian = "On je Taj Koji vam je noć učinio pokrivkom, san počinkom, a dan da se svuda raziđete.", TextEnglish = "And it is He who has made the night for you as clothing[1030] and sleep [a means for] rest and has made the day a resurrection.[1031]", TextGerman = "Und Er ist es, Der euch die Nacht zum Kleid und den Schlaf zum Ausruhen macht; und Er macht den Tag zum Aufstehen." });
                        surah25.VerseList.Add(new Verse { Number = 48, TextBosnian = "I On je Taj Koji šalje vjetrove kao radosnu vijest, kao prethodnicu Milosti Svoje; i Mi s neba čistu vodu spuštamo", TextEnglish = "And it is He who sends the winds as good tidings before His mercy [i.e., rainfall], and We send down from the sky pure water", TextGerman = "Und Er ist es, Der die Winde als Frohboten Seiner Barmherzigkeit (voraus)sendet. Und Wir lassen vom Himmel reines Wasser herabkommen," });
                        surah25.VerseList.Add(new Verse { Number = 49, TextBosnian = "da njome već mrtav predio oživimo i da, od stvorenja Naših, mnogu stoku i mnoge ljude napojimo.", TextEnglish = "That We may bring to life thereby a dead land and give it as drink to those We created of numerous livestock and men.", TextGerman = "um damit (manch) totes Land wieder lebendig zu machen und um es Vieh und Menschen in großer Zahl, die Wir erschaffen haben, zu trinken zu geben." });
                        surah25.VerseList.Add(new Verse { Number = 50, TextBosnian = "I Mi je raspodjeljujemo među njima da bi se prisjetili ali većina ljudi odbija sve osim nevjerovanja i nezahvalnosti.", TextEnglish = "And We have certainly distributed it among them that they might be reminded,[1032] but most of the people refuse except disbelief.", TextGerman = "Und Wir verteilen es ja unter ihnen auf verschiedene Weise, damit sie bedenken. Aber die meisten Menschen weisen (alles) zurück außer dem Unglauben." });
                        surah25.VerseList.Add(new Verse { Number = 51, TextBosnian = "Da hoćemo, u svako naselje poslali bismo upozoritelja;", TextEnglish = "And if We had willed, We could have sent into every city a warner.[1033]", TextGerman = "Wenn Wir wollten, würden Wir wahrlich in jeder Stadt einen Warner erstehen lassen." });
                        surah25.VerseList.Add(new Verse { Number = 52, TextBosnian = "zato ne budi poslušan nevjernicima i njime - Kur'anom se protiv njih uveliko bori.", TextEnglish = "So do not obey the disbelievers, and strive against them with it [i.e., the Qur’ān] a great striving.", TextGerman = "So gehorche nicht den Ungläubigen und mühe dich damit gegen sie ab mit großem Einsatz." });
                        surah25.VerseList.Add(new Verse { Number = 53, TextBosnian = "On je Taj Koji je dva mora ostavio jedno pored drugog - jedno je pitko i slatko, drugo slano i gorko, a između njih je pregradu i neprobojnu branu postavio,", TextEnglish = "And it is He who has released [simultaneously] the two seas [i.e., bodies of water], one fresh and sweet and one salty and bitter, and He placed between them a barrier and prohibiting partition.", TextGerman = "Und Er ist es, Der den beiden Meeren freien Lauf läßt: Das eine ist süß und erfrischend, das andere salzig und (auf der Zunge) brennend. Und Er hat zwischen ihnen beiden ein trennendes Hindernis und eine verwehrte Absperrung errichtet." });
                        surah25.VerseList.Add(new Verse { Number = 54, TextBosnian = "On od vode stvara ljude i čini da su rod po krvi i tazbini. Gospodar tvoj je svemoćan.", TextEnglish = "And it is He who has created from water [i.e., semen] a human being and made him [a relative by] lineage and marriage. And ever is your Lord competent [concerning creation].", TextGerman = "Und Er ist es, Der aus Wasser menschliche Wesen erschafft und sie dann zu (Bluts)verwandschaft und Schwägerschaft bestimmt; dein Herr ist Allmächtig." });
                        surah25.VerseList.Add(new Verse { Number = 55, TextBosnian = "Umjesto Allaha oni obožavaju one koji nisu u stanju neku im korist pribaviti niti od njih kakvu štetu otkloniti. Nevjernik je šejtanov pomagač protiv njegovog Gospodara.", TextEnglish = "But they worship rather than Allāh that which does not benefit them or harm them, and the disbeliever is ever, against his Lord, an assistant [to Satan].", TextGerman = "Aber sie dienen anstatt Allahs, was ihnen weder nützt noch schadet. Und der Ungläubige leistet stets Beistand gegen seinen Herrn." });
                        surah25.VerseList.Add(new Verse { Number = 56, TextBosnian = "A tebe smo poslali samo kao radosnih vijesti navjestitelja i upozoritelja.", TextEnglish = "And We have not sent you, [O Muḥammad], except as a bringer of good tidings and a warner.", TextGerman = "Und Wir haben dich nur als Verkünder froher Botschaft und Warner gesandt." });
                        surah25.VerseList.Add(new Verse { Number = 57, TextBosnian = "Reci: 'Za ovo od vas ne tražim druge nagrade osim da onaj koji hoće pođe Putem koji vodi njegovu Gospodaru.'", TextEnglish = "Say, 'I do not ask of you for it any payment - only that whoever wills might take to his Lord a way.'", TextGerman = "Sag: Ich verlange von euch (für mich selbst) keinen Lohn dafür. Wer aber einen Weg zu seinem Herrn einschlagen will (, bekommt selbst seinen Lohn)." });
                        surah25.VerseList.Add(new Verse { Number = 58, TextBosnian = "Ti se pouzdaj u Živog, koji ne može umrijeti, i veličaj Ga, i hvali! A dovoljno je to što grijehe robova Svojih potpuno zna,", TextEnglish = "And rely upon the Ever-Living who does not die, and exalt [Allāh] with His praise. And sufficient is He to be, with the sins of His servants, [fully] Aware -", TextGerman = "Und verlasse dich auf den Lebendigen, Der nicht stirbt, und lobpreise Ihn. Er genügt als Kenner der Sünden Seiner Diener," });
                        surah25.VerseList.Add(new Verse { Number = 59, TextBosnian = "Onaj Koji je za šest Dana nebesa i Zemlju i ono što je među njima stvorio, a onda se nad Aršom uzvisio. On je Svemilosni, i upitaj o Njemu onoga koji dobro zna.", TextEnglish = "He who created the heavens and the earth and what is between them in six days and then established Himself above the Throne[1034] - the Most Merciful, so ask about Him one well informed [i.e., the Prophet (ﷺ)].", TextGerman = "Der die Himmel und die Erde und was dazwischen ist, in sechs Tagen erschuf und Sich hierauf über den Thron erhob’, (Er ist) der Allerbarmer. So frag einen, der von Ihm Kenntnis hat." });
                        surah25.VerseList.Add(new Verse { Number = 60, TextBosnian = "A kad im se rekne: 'Učinite sedždu Svemilosnom!', oni pitaju: 'A ko je Svemilosni? Zar da činimo sedždu samo zato što nam ti naređuješ I to im još više odbojnost poveća.", TextEnglish = "And when it is said to them, 'Prostrate to the Most Merciful,' they say, 'And what is the Most Merciful? Should we prostrate to that which you order us?' And it increases them in aversion.", TextGerman = "Und wenn zu ihnen gesagt wird: „Werft euch vor dem Allerbarmer nieder sagen sie: „Was ist denn der Allerbarmer? Sollen wir uns (einfach) vor dem niederwerfen, was du uns befiehlst? Und das mehrt ihnen (nur) die Abneigung." });
                        surah25.VerseList.Add(new Verse { Number = 61, TextBosnian = "Neka je Uzvišen Onaj Koji je na nebu velike zvijezde stvorio i na njemu dao Svjetiljku i Mjesec koji sja.", TextEnglish = "Blessed is He who has placed in the sky great stars and placed therein a [burning] lamp and luminous moon.", TextGerman = "Segensreich ist Derjenige, Der im Himmel Türme gesetzt und darin einen Lichtkörper und einen leuchtenden Mond gesetzt hat!" });
                        surah25.VerseList.Add(new Verse { Number = 62, TextBosnian = "On je Taj Koji je učinio da se noć i dan smjenjuju, što je prilika za onoga koji hoće da se prisjeti i pouku primi ili želi biti zahvalan.", TextEnglish = "And it is He who has made the night and the day in succession for whoever desires to remember or desires gratitude.", TextGerman = "Und Er ist es, Der die Nacht und den Tag so gemacht hat, daß sie sich voneinander für jemanden unterscheiden, der bedenken oder Dankbarkeit zeigen will." });
                        surah25.VerseList.Add(new Verse { Number = 63, TextBosnian = "A robovi Svemilosnog su oni koji po Zemlji smireno hodaju, a kada im se neznalice obrate, odgovaraju: 'Mir vama!',", TextEnglish = "And the servants of the Most Merciful are those who walk upon the earth easily,[1035] and when the ignorant address them [harshly], they say [words of] peace,[1036]", TextGerman = "Die Diener des Allerbarmers sind diejenigen, die maßvoll auf der Erde umhergehen und die, wenn die Toren sie ansprechen, sagen: „Frieden!" });
                        surah25.VerseList.Add(new Verse { Number = 64, TextBosnian = "i oni koji provode noći pred Gospodarom svojim na sedždu padajući i stojeći,", TextEnglish = "And those who spend [part of] the night to their Lord prostrating and standing [in prayer]", TextGerman = "Und diejenigen, die die Nacht verbringen, indem sie sich (im Gebet) vor ihrem Herrn niederwerfen und aufrecht stehen." });
                        surah25.VerseList.Add(new Verse { Number = 65, TextBosnian = "i oni koji govore: 'Gospodaru naš, skloni od nas patnju u Džehennemu jer je patnja u njemu, doista, neprestana.", TextEnglish = "And those who say, 'Our Lord, avert from us the punishment of Hell. Indeed, its punishment is ever adhering;", TextGerman = "Und diejenigen, die sagen: „Unser Herr, wende von uns die Strafe der Hölle ab. Ihre Strafe ist ja bedrängend;" });
                        surah25.VerseList.Add(new Verse { Number = 66, TextBosnian = "On je ružno prebivalište i boravište',", TextEnglish = "Indeed, it is evil as a settlement and residence.'", TextGerman = "gewiß, sie ist böse als Aufenthaltsort und Bleibe." });
                        surah25.VerseList.Add(new Verse { Number = 67, TextBosnian = "i oni koji, kad udjeljuju, ne rasipaju i ne škrtare, već se u tome drže sredine;", TextEnglish = "And [they are] those who, when they spend, do so not excessively or sparingly but are ever, between that, [justly] moderate", TextGerman = "Und diejenigen, die, wenn sie ausgeben, weder maßlos noch knauserig sind, sondern den Mittelweg dazwischen (einhalten)." });
                        surah25.VerseList.Add(new Verse { Number = 68, TextBosnian = "i oni koji mimo Allaha drugog boga ne mole, i koji, one koje je Allah zabranio, ne ubijaju, osim kad se na to ima pravo, i koji ne bludniče; a ko to radi, iskusit će kaznu.", TextEnglish = "And those who do not invoke[1037] with Allāh another deity or kill the soul which Allāh has forbidden [to be killed], except by right, and do not commit unlawful sexual intercourse. And whoever should do that will meet a penalty.", TextGerman = "Und diejenigen, die neben Allah keinen anderen Gott anrufen und nicht die Seele töten, die Allah (zu töten) verboten hat, außer aus einem rechtmäßigen Grund, und die keine Unzucht begehen. - Wer das tut, hat die Folge der Sünde zu erleiden;" });
                        surah25.VerseList.Add(new Verse { Number = 69, TextBosnian = "Patnja će mu na ahiretu udvostručena biti i vječno će u njoj ponižen ostati,", TextEnglish = "Multiplied for him is the punishment on the Day of Resurrection, and he will abide therein humiliated -", TextGerman = "die Strafe wird ihm am Tag der Auferstehung vervielfacht, und ewig wird er darin in Schmach bleiben," });
                        surah25.VerseList.Add(new Verse { Number = 70, TextBosnian = "osim onih koji se pokaju i uzvjeruju i dobra djela čine; Allah će njihova loša djela u dobra promijeniti, a Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "Except for those who repent, believe and do righteous work. For them Allāh will replace their evil deeds with good. And ever is Allāh Forgiving and Merciful.", TextGerman = "außer demjenigen, der bereut, glaubt und rechtschaffene Werke tut; jenen wird Allah ihre bösen Taten gegen gute eintauschen; und Allah ist stets Allvergebend und Barmherzig." });
                        surah25.VerseList.Add(new Verse { Number = 71, TextBosnian = "A onaj koji se bude pokajao i dobra djela činio, on se, uistinu, Allahu iskreno vratio;", TextEnglish = "And he who repents and does righteousness does indeed turn to Allāh with [accepted] repentance.", TextGerman = "Und wer bereut und rechtschaffen handelt, der wendet sich in wahrhaftiger Reue Allah zu." });
                        surah25.VerseList.Add(new Verse { Number = 72, TextBosnian = "i oni koji ne prisustvuju laži, i koji, prolazeći pored besmislica, prolaze dostojanstveno;", TextEnglish = "And [they are] those who do not testify to falsehood, and when they pass near ill speech, they pass by with dignity.", TextGerman = "Und (auch) diejenigen, die keine Falschaussage bezeugen und, wenn sie im Vorbeigehen unbedachte Rede (hören), würdevoll weitergehen." });
                        surah25.VerseList.Add(new Verse { Number = 73, TextBosnian = "i oni koji, kada budu opomenuti ajetima Gospodara svoga, ni gluhi ni slijepi na njih ne ostanu;", TextEnglish = "And those who, when reminded of the verses of their Lord, do not fall upon them deaf and blind.", TextGerman = "Und diejenigen, die, wenn sie mit den Zeichen ihres Herrn ermahnt werden, ihnen gegenüber nicht taub und blind niederfallen ." });
                        surah25.VerseList.Add(new Verse { Number = 74, TextBosnian = "i oni koji govore: 'Gospodaru naš, podari nam u suprugama našim i djeci našoj radost i učini nas predvodnicima bogobojaznih!'", TextEnglish = "And those who say, 'Our Lord, grant us from among our wives and offspring comfort to our eyes[1038] and make us a leader [i.e., example] for the righteous.'", TextGerman = "Und diejenigen, die sagen: „Unser Herr, schenke uns an unseren Gattinnen und unseren Nachkommenschaften Grund zur Freude, und mache uns für die Rechtschaffenen zu einem Vorbild." });
                        surah25.VerseList.Add(new Verse { Number = 75, TextBosnian = "Oni će biti, za ono što su trpjeli, nastambom visokom nagrađeni i u njima će pozdravom dobrodošlice i selamom biti dočekivani.", TextEnglish = "Those will be awarded the Chamber[1039] for what they patiently endured, and they will be received therein with greetings and [words of] peace,", TextGerman = "(All) diesen wird mit dem Obergemach vergolten werden, daß sie standhaft waren; und ihnen wird Gruß und Friede entgegengebracht," });
                        surah25.VerseList.Add(new Verse { Number = 76, TextBosnian = "U njima će vječno ostati, a kako je to divno prebivalište i boravište!", TextEnglish = "Abiding eternally therein. Good is the settlement and residence.", TextGerman = "ewig darin zu bleiben. Schön ist es als Aufenthaltsort und Bleibe." });
                        surah25.VerseList.Add(new Verse { Number = 77, TextBosnian = "Reci: 'Moj Gospodar ne mari za vas ako nema dove vaše. Ali, porekli ste i neminovno vas čeka patnja.'", TextEnglish = "Say, 'What would my Lord care for you if not for your supplication?'[1040] For you [disbelievers] have denied, so it [i.e., your denial] is going to be adherent.[1041]", TextGerman = "Sag: Mein Herr kümmerte Sich nicht um euch, wenn nicht euer Bittgebet (zu Ihm) wäre. Ihr habt doch (alles) für Lüge erklärt, und so wird (eure Strafe hierfür) unabwendbar sein." });
                        result = surah25;
                        break;
                    #endregion

                    // 
                    #region
                    case 26:
                        Surah surah26 = new Surah(); surah26.Name = Constants.Surah26Name; surah26.Number = 26; surah26.VerseList = new List<Verse>();
                        surah26.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah26.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ta-sin-mim.", TextEnglish = "Ṭā, Seen, Meem.[1043]", TextGerman = "Im Namen Allahs, des Allerbarmers, des Barmherzigen:Ta-Sin-Mim." });
                        surah26.VerseList.Add(new Verse { Number = 2, TextBosnian = "Ovo su ajeti Knjige jasne!", TextEnglish = "These are the verses of the clear Book.", TextGerman = "Dies sind die Zeichen des deutlichen Buches." });
                        surah26.VerseList.Add(new Verse { Number = 3, TextBosnian = "Zar ćeš ti sebe uništiti zato što oni neće da budu vjernici?!", TextEnglish = "Perhaps, [O Muḥammad], you would kill yourself with grief that they will not be believers.", TextGerman = "Vielleicht magst du dich noch selbst umbringen aus Gram (darüber), daß sie nicht gläubig sind." });
                        surah26.VerseList.Add(new Verse { Number = 4, TextBosnian = "Kad bismo htjeli, Mi bismo im s neba znak poslali pred kojim bi oni svoje šije povili", TextEnglish = "If We willed, We could send down to them from the sky a sign for which their necks would remain humbled.[1044]", TextGerman = "Wenn Wir woll(t)en, könn(t)en Wir vom Himmel ein Zeichen auf sie hinabsenden, so daß sich ihre Nacken dauernd davor unterwerfen (würden)." });
                        surah26.VerseList.Add(new Verse { Number = 5, TextBosnian = "I njima ne dođe nijedna nova opomena od Svemilosnoga, a da se od nje ne okrenu.", TextEnglish = "And no mention [i.e., revelation] comes to them anew from the Most Merciful except that they turn away from it.", TextGerman = "Keine neuerlich offenbarte Ermahnung kommt zu ihnen vom Allerbarmer, ohne daß sie sich davon abwenden." });
                        surah26.VerseList.Add(new Verse { Number = 6, TextBosnian = "I oni poriču, a doći će im vijesti o onome čemu se izruguju.", TextEnglish = "For they have already denied, but there will come to them the news of that which they used to ridicule.", TextGerman = "Sie haben (sie) ja für Lüge erklärt. So werden zu ihnen die Nachrichten kommen von dem, worüber sie sich lustig zu machen pflegten." });
                        surah26.VerseList.Add(new Verse { Number = 7, TextBosnian = "Zar oni ne vide kako činimo da iz zemlje niče svakovrsno bilje plemenito?", TextEnglish = "Did they not look at the earth - how much We have produced therein from every noble kind?", TextGerman = "Haben sie nicht auf die Erde gesehen, wie viele edle Arten Wir auf ihr haben wachsen lassen?" });
                        surah26.VerseList.Add(new Verse { Number = 8, TextBosnian = "Zbilja, u tome je Znak, ali većina njih ne vjeruje,", TextEnglish = "Indeed in that is a sign, but most of them were not to be believers.", TextGerman = "Darin ist wahrlich ein Zeichen, doch sind die meisten von ihnen nicht gläubig." });
                        surah26.VerseList.Add(new Verse { Number = 9, TextBosnian = "a Gospodar tvoj je, zaista, Silni i Milostivi.", TextEnglish = "And indeed, your Lord - He is the Exalted in Might, the Merciful.", TextGerman = "Und dein Herr ist fürwahr der Allmächtige und Barmherzige." });
                        surah26.VerseList.Add(new Verse { Number = 10, TextBosnian = "A kad je Gospodar tvoj Musaa zovnuo: 'Idi narodu zulumćara,", TextEnglish = "And [mention] when your Lord called Moses, [saying], 'Go to the wrongdoing people -", TextGerman = "Als dein Herr Musa zurief: „Begib dich zum ungerechten Volk," });
                        surah26.VerseList.Add(new Verse { Number = 11, TextBosnian = "narodu faraonovu, ne bi li se Allaha pobojao',", TextEnglish = "The people of Pharaoh. Will they not fear Allāh?'", TextGerman = "zum Volk Fir’auns, ob sie nicht gottesfürchtig sein wollen." });
                        surah26.VerseList.Add(new Verse { Number = 12, TextBosnian = "on je rekao: 'Gospodaru moj, bojim se da me oni u laž ne utjeraju.", TextEnglish = "He said, 'My Lord, indeed I fear that they will deny me", TextGerman = "Er sagte: „Mein Herr, ich fürchte, daß sie mich der Lüge bezichtigen." });
                        surah26.VerseList.Add(new Verse { Number = 13, TextBosnian = "Da mi ne postane teško u duši i da mi se jezik ne saplete; pa daj poslanstvo i Harunu, da mi pomogne;", TextEnglish = "And that my breast will tighten and my tongue will not be fluent, so send for Aaron.", TextGerman = "Auch ist meine Brust beklommen, und meine Zunge ist nicht gelöst. Darum entsende (auch) Harun." });
                        surah26.VerseList.Add(new Verse { Number = 14, TextBosnian = "a oni protiv mene optužbu za zlodjela imaju, pa se plašim da me ne ubiju.'", TextEnglish = "And they have upon me a [claim due to] sin, so I fear that they will kill me.'", TextGerman = "Und sie haben gegen mich eine Sünde geltend zu machen; so fürchte ich, daß sie mich töten." });
                        surah26.VerseList.Add(new Verse { Number = 15, TextBosnian = "Neće!', reče On. 'Idite obojica sa znakovima Našim, Mi ćemo s vama biti i slušati.", TextEnglish = "[Allāh] said, 'No. Go both of you with Our signs; indeed, We are with you, listening.", TextGerman = "Er sagte: „Keineswegs! So geht denn beide hin mit Unseren Zeichen. Wir sind mit euch und hören zu." });
                        surah26.VerseList.Add(new Verse { Number = 16, TextBosnian = "Otiđite faraonu i recite: 'Mi smo poslanici Gospodara svjetova.", TextEnglish = "Go to Pharaoh and say, 'We are the messengers[1045] of the Lord of the worlds,", TextGerman = "Begebt euch zu Fir’aun und sagt: ,Wir sind der Gesandte des Herrn der Weltenbewohner:" });
                        surah26.VerseList.Add(new Verse { Number = 17, TextBosnian = "Dopusti da sinovi Israilovi pođu s nama.''", TextEnglish = "[Commanded to say], 'Send with us the Children of Israel.'''", TextGerman = "Lasse die Kinder Isra’ils mit uns gehen’." });
                        surah26.VerseList.Add(new Verse { Number = 18, TextBosnian = "Faraon reče: 'Zar te među nama nismo odgajali dok si dijete bio i zar među nama tolike godine života svoga nisi proveo?", TextEnglish = "[Pharaoh] said, 'Did we not raise you among us as a child, and you remained among us for years of your life?", TextGerman = "Er (Fir’aun) sagte: „Haben wir dich nicht als kleines Kind unter uns aufgezogen, und hast du dich nicht (viele) Jahre deines Lebens unter uns aufgehalten?" });
                        surah26.VerseList.Add(new Verse { Number = 19, TextBosnian = "I uradio si ono što si uradio i uz to si poricatelj bio!'", TextEnglish = "And [then] you did your deed which you did,[1046] and you were of the ungrateful.'", TextGerman = "Und du hast deine Tat, die du (damals) getan hast, verübt und gehörst zu den Undankbaren." });
                        surah26.VerseList.Add(new Verse { Number = 20, TextBosnian = "Ja sam onda ono uradio a u zalutale sam spadao', reče", TextEnglish = "[Moses] said, 'I did it, then, while I was of those astray [i.e., ignorant].", TextGerman = "Er sagte: „Ich habe sie da(mals) verübt, als ich (noch) zu den Irregehenden gehörte." });
                        surah26.VerseList.Add(new Verse { Number = 21, TextBosnian = "a od vas sam pobjegao zato što sam se vas bojao, pa mi je Gospodar moj mudrost darovao i poslanikom me učinio.", TextEnglish = "So I fled from you when I feared you. Then my Lord granted me judgement [i.e., wisdom and prophet hood] and appointed me [as one] of the messengers.", TextGerman = "Und so floh ich vor euch, als ich Angst vor euch bekommen hatte. (Doch) dann schenkte mir mein Herr Urteil(skraft) und machte mich zu einem der Gesandten." });
                        surah26.VerseList.Add(new Verse { Number = 22, TextBosnian = "A dobročinstvo koje mi prebacuješ – da nije to što si sinove Israilove robljem učinio?'", TextEnglish = "And is this a favor of which you remind me - that you have enslaved the Children of Israel?'", TextGerman = "Ist das (etwa) eine Gunst, die du mir vorhältst, daß du die Kinder lsra’ils geknechtet hast?" });
                        surah26.VerseList.Add(new Verse { Number = 23, TextBosnian = "A ko je Gospodar svjetova?', upita faraon.", TextEnglish = "Said Pharaoh, 'And what is the Lord of the worlds?'", TextGerman = "Fir’aun sagte: „Was ist denn der Herr der Weltenbewohner?" });
                        surah26.VerseList.Add(new Verse { Number = 24, TextBosnian = "Gospodar nebesa i Zemlje i onoga što je između njih, ako čvrsto vjerujete', odgovori on.", TextEnglish = "[Moses] said, 'The Lord of the heavens and earth and that between them, if you should be convinced.'", TextGerman = "Er (Musa) sagte: „Der Herr der Himmel und der Erde und dessen, was dazwischen ist, wenn ihr überzeugt seid." });
                        surah26.VerseList.Add(new Verse { Number = 25, TextBosnian = "Čujete li?', reče faraon onima oko sebe.", TextEnglish = "[Pharaoh] said to those around him, 'Do you not hear?'", TextGerman = "Er (Fir’aun) sagte zu denjenigen in seiner Umgebung: „Hört ihr nicht zu?" });
                        surah26.VerseList.Add(new Verse { Number = 26, TextBosnian = "Gospodar vaš i Gospodar vaših davnih predaka', reče Musa.", TextEnglish = "[Moses] said, 'Your Lord and the Lord of your first forefathers.'", TextGerman = "Er (Musa) sagte: „(Er ist) euer Herr und der Herr eurer Vorväter." });
                        surah26.VerseList.Add(new Verse { Number = 27, TextBosnian = "Poslanik koji vam je poslan, uistinu je lud', reče faraon.", TextEnglish = "[Pharaoh] said,[1047] 'Indeed, your 'messenger' who has been sent to you is mad.'", TextGerman = "Er (Fir’aun) sagte: „Euer Gesandter, der zu euch gesandt wurde, ist fürwahr besessen." });
                        surah26.VerseList.Add(new Verse { Number = 28, TextBosnian = "I 'Gospodar istoka i zapada i onoga što je između njih, ako razumijete?', reče Musa.", TextEnglish = "[Moses] said, 'Lord of the east and the west and that between them, if you were to reason.'", TextGerman = "Er (Musa) sagte: „(Er ist) der Herr des Ostens und des Westens und dessen, was dazwischen ist, wenn ihr (nur) begreifen würdet." });
                        surah26.VerseList.Add(new Verse { Number = 29, TextBosnian = "A faraon reče: 'Ako budeš kao boga nekoga drugog osim mene priznavao, sigurno ću te u tamnicu baciti!'", TextEnglish = "[Pharaoh] said, 'If you take a god other than me, I will surely place you among those imprisoned.'", TextGerman = "Er (Fir’aun) sagte: „Wenn du dir einen anderen Gott als mich nimmst, werde ich dich ganz gewiß zu einem der Gefangenen machen." });
                        surah26.VerseList.Add(new Verse { Number = 30, TextBosnian = "Zar i onda kad ti budem nešto što je očito donio?', upita on.", TextEnglish = "[Moses] said, 'Even if I brought you something [i.e., proof] manifest?'", TextGerman = "Er (Musa) sagte: „Etwa auch, wenn ich dir etwas Deutliches bringe?" });
                        surah26.VerseList.Add(new Verse { Number = 31, TextBosnian = "Pa daj to, ako istinu govoriš!', reče faraon.", TextEnglish = "[Pharaoh] said, 'Then bring it, if you should be of the truthful.'", TextGerman = "Er (Fir’aun) sagte: „Dann bringe es her, wenn du zu den Wahrhaftigen gehörst." });
                        surah26.VerseList.Add(new Verse { Number = 32, TextBosnian = "I Musa baci štap svoj, kad on - zmija očita.", TextEnglish = "So [Moses] threw his staff, and suddenly it was a serpent manifest.[1048]", TextGerman = "Da warf er seinen Stock hin, und sogleich war er eine deutliche Schlange." });
                        surah26.VerseList.Add(new Verse { Number = 33, TextBosnian = "I ruku svoju izvuče, kad ona, onima koji su gledali - bijela.", TextEnglish = "And he drew out his hand; thereupon it was white for the observers.", TextGerman = "Und er zog seine Hand heraus, da war sie weiß für die Betrachter." });
                        surah26.VerseList.Add(new Verse { Number = 34, TextBosnian = "Ovaj je, zaista, vješt čarobnjak', reče glavešinama oko sebe faraon,", TextEnglish = "[Pharaoh] said to the eminent ones around him, 'Indeed, this is a learned magician.", TextGerman = "Er (Fir’aun) sagte zu der führenden Schar in seiner Umgebung: „Dieser ist fürwahr ein kenntnisreicher Zauberer," });
                        surah26.VerseList.Add(new Verse { Number = 35, TextBosnian = "hoće da vas čarolijom svojom iz zemlje vaše izvede. Pa šta savjetujete?'", TextEnglish = "He wants to drive you out of your land by his magic, so what do you advise?'", TextGerman = "der euch mit seiner Zauberei aus eurem Land vertreiben will. Was befehlt ihr nun?" });
                        surah26.VerseList.Add(new Verse { Number = 36, TextBosnian = "Ostavi njega i brata njegova!', rekoše, 'a u gradove pošalji sakupljače,", TextEnglish = "They said, 'Postpone [the matter of] him and his brother and send among the cities gatherers", TextGerman = "Sie sagten: „Stelle ihn und seinen Bruder zurück und schicke in die Städte (Boten), die (die Leute) versammeln," });
                        surah26.VerseList.Add(new Verse { Number = 37, TextBosnian = "sve čarobnjake vješte će ti dovesti.'", TextEnglish = "Who will bring you every learned, skilled magician.'", TextGerman = "damit sie dir jeden kenntnisreichen Zauberer herbringen." });
                        surah26.VerseList.Add(new Verse { Number = 38, TextBosnian = "I čarobnjake u određeno vrijeme i označenog dana sabraše,", TextEnglish = "So the magicians were assembled for the appointment of a well-known day.[1049]", TextGerman = "So wurden die Zauberer auf die festgesetzte Zeit eines (wohl)bekannten Tages versammelt." });
                        surah26.VerseList.Add(new Verse { Number = 39, TextBosnian = "a narodu bi rečeno: 'Hoćete li se sakupiti,", TextEnglish = "And it was said to the people, 'Will you congregate.", TextGerman = "Und es wurde zu den Menschen gesagt: „Werdet (auch) ihr euch nun wohl versammeln?" });
                        surah26.VerseList.Add(new Verse { Number = 40, TextBosnian = "da budemo uz čarobnjake, ako oni budu pobjednici?'", TextEnglish = "That we might follow the magicians if they are the predominant?'", TextGerman = "Vielleicht werden wir den Zauberern folgen, wenn sie es sind, die siegen." });
                        surah26.VerseList.Add(new Verse { Number = 41, TextBosnian = "A kad dođoše, čarobnjaci faraona upitaše: 'Da li će nama, doista, pripasti nagrada ako mi budemo pobjednici?'", TextEnglish = "And when the magicians arrived, they said to Pharaoh, 'Is there indeed for us a reward if we are the predominant?'", TextGerman = "Als die Zauberer kamen, sagten sie zu Fir’aun: „Wir bekommen doch sicher einen Lohn, wenn wir es sind, die siegen?" });
                        surah26.VerseList.Add(new Verse { Number = 42, TextBosnian = "Hoće', odgovori on, 'i bit ćete tada sigurno među meni bliskim.'", TextEnglish = "He said, 'Yes, and indeed, you will then be of those near [to me].'", TextGerman = "Er sagte: „Ja. Und ihr werdet dann also fürwahr zu den Nahegestellten gehören." });
                        surah26.VerseList.Add(new Verse { Number = 43, TextBosnian = "Musa im reče: 'Bacite ono što želite baciti!'", TextEnglish = "Moses said to them, 'Throw whatever you will throw.'", TextGerman = "Musa sagte zu ihnen: „Werft hin, was ihr zu werfen habt!" });
                        surah26.VerseList.Add(new Verse { Number = 44, TextBosnian = "I oni pobacaše konope svoje i štapove svoje i rekoše: 'S faraonovom moći, mi ćemo svakako pobijediti!'", TextEnglish = "So they threw their ropes and their staffs and said, 'By the might of Pharaoh, indeed it is we who are predominant.'", TextGerman = "Da warfen sie ihre Stricke und Stöcke hin und sagten: „Bei der Macht Fir’auns, wir werden ganz gewiß Sieger sein." });
                        surah26.VerseList.Add(new Verse { Number = 45, TextBosnian = "Zatim Musa baci svoj štap, koji odjednom proguta ono što su oni lažno izveli.", TextEnglish = "Then Moses threw his staff, and at once it devoured what they falsified.", TextGerman = "So warf Musa seinen Stock hin, und da verschlang er sogleich, was sie vortäuschten." });
                        surah26.VerseList.Add(new Verse { Number = 46, TextBosnian = "Čarobnjaci onda na sedždu padoše", TextEnglish = "So the magicians fell down in prostration [to Allāh].", TextGerman = "Da warfen sich die Zauberer ehrerbietig nieder." });
                        surah26.VerseList.Add(new Verse { Number = 47, TextBosnian = "i rekoše: 'Mi vjerujemo u Gospodara svjetova,", TextEnglish = "They said, 'We have believed in the Lord of the worlds,", TextGerman = "Sie sagten: „Wir glauben an den Herrn der Weltenbewohner," });
                        surah26.VerseList.Add(new Verse { Number = 48, TextBosnian = "Gospodara Musaova i Harunova.'", TextEnglish = "The Lord of Moses and Aaron.'", TextGerman = "den Herrn von Musa und Harun." });
                        surah26.VerseList.Add(new Verse { Number = 49, TextBosnian = "Zar povjerovaste njemu prije dopuštenja moga', viknu faraon, 'on je vaš učitelj, on vas je čarobnjaštvu naučio - a vi ćete zapamtiti: poodsijecat ću vam ruke i noge unakrst i sve ću vas porazapinjati!'", TextEnglish = "[Pharaoh] said, 'You believed him [i.e., Moses] before I gave you permission. Indeed, he is your leader who has taught you magic, but you are going to know. I will surely cut off your hands and your feet on opposite sides, and I will surely crucify you all.'", TextGerman = "Er (Fir’aun) sagte: „Ihr glaubt ihm, bevor ich es euch erlaube? Er ist wahrlich euer Ältester, der euch die Zauberei gelehrt hat. Ihr werdet (es) wahrlich erfahren. Ganz gewiß werde ich eure Hände und eure Füße wechselseitig abhacken und euch allesamt ganz gewiß kreuzigen (lassen)." });
                        surah26.VerseList.Add(new Verse { Number = 50, TextBosnian = "Ne šteti!', rekoše oni, 'mi ćemo se Gospodaru svome vratiti.", TextEnglish = "They said, 'No harm. Indeed, to our Lord we will return.", TextGerman = "Sie sagten: „Kein Schaden! Wir werden ganz gewiß zu unserem Herrn zurückkehren." });
                        surah26.VerseList.Add(new Verse { Number = 51, TextBosnian = "Mi se nadamo da će nam Gospodar naš grijehe oprostiti zato što smo prvi vjernici.'", TextEnglish = "Indeed, we aspire that our Lord will forgive us our sins because we were the first of the believers.'", TextGerman = "Wir erhoffen ja, daß unser Herr uns unsere Verfehlungen vergebe dafür, daß wir die ersten (der) Gläubigen sind." });
                        surah26.VerseList.Add(new Verse { Number = 52, TextBosnian = "I Mi objavismo Musau: 'Kreni noću s robovima Mojim, ali bit ćete gonjeni.'", TextEnglish = "And We inspired to Moses, 'Travel by night with My servants; indeed, you will be pursued.'", TextGerman = "Und Wir gaben Musa (als Offenbarung) ein: „Zieh bei Nacht mit Meinen Dienern fort; ihr werdet ja verfolgt werden." });
                        surah26.VerseList.Add(new Verse { Number = 53, TextBosnian = "I faraon posla po gradovima sakupljače.", TextEnglish = "Then Pharaoh sent among the cities gatherers[1050]", TextGerman = "Da sandte Fir’aun in die Städte (Boten), die (die Leute) versammelten:" });
                        surah26.VerseList.Add(new Verse { Number = 54, TextBosnian = "Doista su ovi skupina mala,", TextEnglish = "[And said], 'Indeed, those are but a small band,", TextGerman = "„Diese sind fürwahr (nur) eine kleine Schar;" });
                        surah26.VerseList.Add(new Verse { Number = 55, TextBosnian = "i oni nas doista srde,", TextEnglish = "And indeed, they are enraging us,", TextGerman = "dennoch lassen sie uns fürwahr ergrimmen." });
                        surah26.VerseList.Add(new Verse { Number = 56, TextBosnian = "a mi smo svi oprezni!'", TextEnglish = "And indeed, we are a cautious society...'", TextGerman = "Aber wir sind alle wahrlich auf der Hut." });
                        surah26.VerseList.Add(new Verse { Number = 57, TextBosnian = "Mi ih izvedosmo iz vrtova i rijeka,", TextEnglish = "So We removed them from gardens and springs", TextGerman = "So ließen Wir sie fortziehen von Gärten und Quellen," });
                        surah26.VerseList.Add(new Verse { Number = 58, TextBosnian = "iz riznica i dvoraca divnih.", TextEnglish = "And treasures and honorable station[1051] -", TextGerman = "von Schätzen und von trefflicher Stätte." });
                        surah26.VerseList.Add(new Verse { Number = 59, TextBosnian = "Eto, tako je bilo, i Mi dadosmo da to naslijede sinovi Israilovi.", TextEnglish = "Thus. And We caused to inherit it the Children of Israel.", TextGerman = "So war es. Und Wir gaben sie den Kindern lsra’ils zum Erbe." });
                        surah26.VerseList.Add(new Verse { Number = 60, TextBosnian = "I oni ih, kad se sunce rađalo, sustigoše,", TextEnglish = "So they pursued them at sunrise.", TextGerman = "Dann folgten sie ihnen bei Sonnenaufgang." });
                        surah26.VerseList.Add(new Verse { Number = 61, TextBosnian = "pa kad jedni druge ugledaše, drugovi Musaovi povikaše: 'Doista smo gotovi!'", TextEnglish = "And when the two companies saw one another, the companions of Moses said, 'Indeed, we are to be overtaken!'", TextGerman = "Als die beiden Heere einander sahen, sagten die Gefährten Musas: „Wir werden fürwahr eingeholt." });
                        surah26.VerseList.Add(new Verse { Number = 62, TextBosnian = "Ne!', reče on. 'Gospodar moj je sa mnom, On će me uputiti.'", TextEnglish = "[Moses] said, 'No! Indeed, with me is my Lord; He will guide me.'", TextGerman = "Er sagte: „Keineswegs! Denn mit mir ist mein Herr; Er wird mich leiten." });
                        surah26.VerseList.Add(new Verse { Number = 63, TextBosnian = "I Mi objavismo Musau: 'Udari štapom svojim po moru!', i ono se rastavi i svaki bok njegov bijaše kao veliko brdo,", TextEnglish = "Then We inspired to Moses, 'Strike with your staff the sea,' and it parted, and each portion was like a great towering mountain.", TextGerman = "Da gaben Wir Musa ein: „Schlag mit deinem Stock auf das Meer." });
                        surah26.VerseList.Add(new Verse { Number = 64, TextBosnian = "i Mi onda tamo one druge približismo,", TextEnglish = "And We advanced thereto the others [i.e., the pursuers].", TextGerman = "Und Wir ließen die anderen dort nahe herankommen." });
                        surah26.VerseList.Add(new Verse { Number = 65, TextBosnian = "Musaa i sve one koji bijahu s njim spasismo,", TextEnglish = "And We saved Moses and those with him, all together.", TextGerman = "Und Wir retteten Musa und diejenigen, die mit ihm waren, allesamt." });
                        surah26.VerseList.Add(new Verse { Number = 66, TextBosnian = "a one druge potopismo.", TextEnglish = "Then We drowned the others.", TextGerman = "Hierauf ließen Wir die anderen ertrinken." });
                        surah26.VerseList.Add(new Verse { Number = 67, TextBosnian = "U tome je, zaista, znak, ali većina njih nisu bili vjernici,", TextEnglish = "Indeed in that is a sign, but most of them were not to be believers.", TextGerman = "Darin ist wahrlich ein Zeichen, doch sind die meisten von ihnen nicht gläubig." });
                        surah26.VerseList.Add(new Verse { Number = 68, TextBosnian = "A, doista, Gospodar tvoj - baš On - je Silni i Milostivi.", TextEnglish = "And indeed, your Lord - He is the Exalted in Might, the Merciful.", TextGerman = "Und dein Herr ist fürwahr der Allmächtige und Barmherzige." });
                        surah26.VerseList.Add(new Verse { Number = 69, TextBosnian = "I kaži im vijest o Ibrahimu,", TextEnglish = "And recite to them the news of Abraham,", TextGerman = "Und verlies ihnen die Kunde von Ibrahim." });
                        surah26.VerseList.Add(new Verse { Number = 70, TextBosnian = "kad je oca svoga i narod svoj upitao: 'Šta to vi obožavate?',", TextEnglish = "When he said to his father and his people, 'What do you worship?'", TextGerman = "Als er zu seinem Vater und seinem Volk sagte: „Wem dient ihr?" });
                        surah26.VerseList.Add(new Verse { Number = 71, TextBosnian = "a oni odgovorili: 'Obožavamo kumire i povazdan im se molimo',", TextEnglish = "They said, 'We worship idols and remain to them devoted.'", TextGerman = "Sie sagten: „Wir dienen Götzen, und so geben wir uns dauernd ihrer Andacht hin." });
                        surah26.VerseList.Add(new Verse { Number = 72, TextBosnian = "on je rekao: 'Da li vas oni čuju kada se molite,", TextEnglish = "He said, 'Do they hear you when you supplicate?", TextGerman = "Er sagte: „Hören sie euch, wenn ihr sie anruft?" });
                        surah26.VerseList.Add(new Verse { Number = 73, TextBosnian = "ili, da li vam mogu koristiti ili naškoditi", TextEnglish = "Or do they benefit you, or do they harm?'", TextGerman = "Oder nützen sie euch, oder schaden?" });
                        surah26.VerseList.Add(new Verse { Number = 74, TextBosnian = "Naproti, odgovoriše, 'ali mi smo upamtili pretke naše kako tako postupaju.'", TextEnglish = "They said, 'But we found our fathers doing thus.'", TextGerman = "Sie sagten: „Nein! Vielmehr fanden wir (bereits) unsere Väter desgleichen tun." });
                        surah26.VerseList.Add(new Verse { Number = 75, TextBosnian = "A da li ste razmišljali', upita on, 'da su oni koje obožavate vi,", TextEnglish = "He said, 'Then do you see what you have been worshipping,", TextGerman = "Er sagte: „Was meint ihr wohl zu dem, was ihr zu verehren pflegt," });
                        surah26.VerseList.Add(new Verse { Number = 76, TextBosnian = "i koje su obožavali davni preci vaši,", TextEnglish = "You and your ancient forefathers?", TextGerman = "ihr und eure Vorväter?" });
                        surah26.VerseList.Add(new Verse { Number = 77, TextBosnian = "doista, neprijatelji moji? Ali, to nije Gospodar svjetova", TextEnglish = "Indeed, they are enemies to me, except the Lord of the worlds,[1052]", TextGerman = "Gewiß, sie sind mir (alle) Feinde, außer dem Herrn der Weltenbewohner," });
                        surah26.VerseList.Add(new Verse { Number = 78, TextBosnian = "Koji me je stvorio i On me na Pravi put upućuje,", TextEnglish = "Who created me, and He [it is who] guides me.", TextGerman = "Der mich erschaffen hat und mich (nun) rechtleitet," });
                        surah26.VerseList.Add(new Verse { Number = 79, TextBosnian = "i Koji me hrani i poji,", TextEnglish = "And it is He who feeds me and gives me drink.", TextGerman = "und Der mir zu essen und zu trinken gibt" });
                        surah26.VerseList.Add(new Verse { Number = 80, TextBosnian = "i Koji me, kad se razbolim, liječi,", TextEnglish = "And when I am ill, it is He who cures me", TextGerman = "und Der, wenn ich krank bin, mich heilt," });
                        surah26.VerseList.Add(new Verse { Number = 81, TextBosnian = "i Koji će mi život oduzeti, i Koji će me poslije oživiti,", TextEnglish = "And who will cause me to die and then bring me to life", TextGerman = "und Der mich sterben läßt und hierauf wieder lebendig macht," });
                        surah26.VerseList.Add(new Verse { Number = 82, TextBosnian = "i Koji će mi, žudim za tim, pogreške na Sudnjem danu oprostiti!", TextEnglish = "And who I aspire that He will forgive me my sin on the Day of Recompense.'", TextGerman = "und von Dem ich erhoffe, daß Er mir am Tag des Gerichts meine Verfehlung vergeben wird." });
                        surah26.VerseList.Add(new Verse { Number = 83, TextBosnian = "Gospodaru moj, podari mi mudrost i pridruži me onima koji su dobri", TextEnglish = "[And he said], 'My Lord, grant me authority and join me with the righteous.", TextGerman = "Mein Herr, schenke mir Urteil(skraft), und nimm mich unter die Rechtschaffenen auf" });
                        surah26.VerseList.Add(new Verse { Number = 84, TextBosnian = "i učini da me po lijepom spominju oni što će poslije mene doći,", TextEnglish = "And grant me a mention [i.e., reputation] of honor among later generations.", TextGerman = "Und verleihe mir einen Ruf an Wahrhaftigkeit unter den späteren (Geschlechtern)." });
                        surah26.VerseList.Add(new Verse { Number = 85, TextBosnian = "i učini me jednim od onih koji će džennetske blagodati naslijediti,", TextEnglish = "And place me among the inheritors of the Garden of Pleasure.", TextGerman = "Und mach mich zu einem (der) Erben des Gartens der Wonne." });
                        surah26.VerseList.Add(new Verse { Number = 86, TextBosnian = "i ocu mome oprosti, zaista je među zalutale spadao,", TextEnglish = "And forgive my father. Indeed, he has been of those astray.", TextGerman = "Und vergib meinem Vater, denn er gehört zu den Irregehenden." });
                        surah26.VerseList.Add(new Verse { Number = 87, TextBosnian = "i ne ponizi me na Dan kad će ljudi oživljeni biti,", TextEnglish = "And do not disgrace me on the Day they are [all] resurrected -", TextGerman = "Und stürze mich nicht in Schande am Tag, da sie auferweckt werden," });
                        surah26.VerseList.Add(new Verse { Number = 88, TextBosnian = "na Dan kada neće nikakvo blago, a ni sinovi, od koristi biti,", TextEnglish = "The Day when there will not benefit [anyone] wealth or children", TextGerman = "an dem Tag, da weder Besitz noch Söhne (jemandem) nützen," });
                        surah26.VerseList.Add(new Verse { Number = 89, TextBosnian = "samo će onaj koji Allahu srca čista dođe spašen biti.'", TextEnglish = "But only one who comes to Allāh with a sound heart.'", TextGerman = "außer, wer zu Allah mit heilem Herzen kommt." });
                        surah26.VerseList.Add(new Verse { Number = 90, TextBosnian = "Džennet će se bogobojaznima približiti,", TextEnglish = "And Paradise will be brought near [that Day] to the righteous.", TextGerman = "Und der (Paradies)garten wird an die Gottesfürchtigen nahe herangebracht." });
                        surah26.VerseList.Add(new Verse { Number = 91, TextBosnian = "a Džehennem zalutalima ukazati,", TextEnglish = "And Hellfire will be brought forth for the deviators,", TextGerman = "Und zum Erscheinen gebracht wird der Höllenbrand den Verirrten." });
                        surah26.VerseList.Add(new Verse { Number = 92, TextBosnian = "i reći će im se: 'Gdje su oni koje ste obožavali,", TextEnglish = "And it will be said to them, 'Where are those you used to worship", TextGerman = "Und es wird zu ihnen gesagt: „Wo ist denn das, dem ihr zu dienen pflegtet" });
                        surah26.VerseList.Add(new Verse { Number = 93, TextBosnian = "a niste Allaha: mogu li vam oni pomoći, a mogu li i sebi pomoći", TextEnglish = "Other than Allāh? Can they help you or help themselves?'", TextGerman = "anstatt Allahs? Können sie euch helfen oder sich selbst helfen?" });
                        surah26.VerseList.Add(new Verse { Number = 94, TextBosnian = "Pa će i oni i oni koji su ih u zabludu doveli u njega biti bačeni,", TextEnglish = "So they will be overturned into it [i.e., Hellfire], they and the deviators", TextGerman = "Dann werden sie kopfüber hineingestürzt, sie und die Verirrten," });
                        surah26.VerseList.Add(new Verse { Number = 95, TextBosnian = "i vojske Iblisove - svi zajedno.", TextEnglish = "And the soldiers of Iblees, all together.", TextGerman = "und die Heerscharen Iblis allesamt." });
                        surah26.VerseList.Add(new Verse { Number = 96, TextBosnian = "I oni će, svađajući se u njemu, govoriti:", TextEnglish = "They will say while they dispute therein,", TextGerman = "Sie sagen, während sie darin miteinander streiten:" });
                        surah26.VerseList.Add(new Verse { Number = 97, TextBosnian = "Tako nam Allaha, bili smo, doista, u očitaj zabludi", TextEnglish = "By Allāh, we were indeed in manifest error", TextGerman = "„Bei Allah, wir befanden uns wahrlich in deutlichem Irrtum," });
                        surah26.VerseList.Add(new Verse { Number = 98, TextBosnian = "kad smo vas sa Gospodarom svjetova izjednačavali,", TextEnglish = "When we equated you with the Lord of the worlds.", TextGerman = "als wir euch dem Herrn der Weltenbewohner gleichsetzten." });
                        surah26.VerseList.Add(new Verse { Number = 99, TextBosnian = "a na stranputicu su nas naveli samo prestupnici,", TextEnglish = "And no one misguided us except the criminals.", TextGerman = "Es waren nur die Übeltäter, die uns in die Irre geführt haben." });
                        surah26.VerseList.Add(new Verse { Number = 100, TextBosnian = "pa nemamo ni zagovornika,", TextEnglish = "So now we have no intercessors", TextGerman = "So haben wir nun niemanden, der Fürsprache einlegt," });
                        surah26.VerseList.Add(new Verse { Number = 101, TextBosnian = "ni prisna prijatelja,", TextEnglish = "And not a devoted friend.", TextGerman = "und auch keinen warmherzigen Freund." });
                        surah26.VerseList.Add(new Verse { Number = 102, TextBosnian = "da nam je samo da se povratimo, pa da postanemo vjernici!'", TextEnglish = "Then if we only had a return [to the world] and could be of the believers...'[1053]", TextGerman = "Hätten wir doch eine (Möglichkeit zur) Wiederholung, damit wir zu den Gläubigen gehörten!" });
                        surah26.VerseList.Add(new Verse { Number = 103, TextBosnian = "U tome je, zaista, znak, ali većina njih nisu bili vjernici,", TextEnglish = "Indeed in that is a sign, but most of them were not to be believers.", TextGerman = "Darin ist wahrlich ein Zeichen, doch sind die meisten von ihnen nicht gläubig." });
                        surah26.VerseList.Add(new Verse { Number = 104, TextBosnian = "A, zaista, Gospodar tvoj - baš On - Silni je i Milostivi!", TextEnglish = "And indeed, your Lord - He is the Exalted in Might, the Merciful.", TextGerman = "Und dein Herr ist fürwahr der Allmächtige und Barmherzige." });
                        surah26.VerseList.Add(new Verse { Number = 105, TextBosnian = "I Nuhov narod je smatrao lažnim poslanike.", TextEnglish = "The people of Noah denied the messengers[1054]", TextGerman = "Das Volk Nuhs bezichtigte die Gesandten der Lüge." });
                        surah26.VerseList.Add(new Verse { Number = 106, TextBosnian = "Kad im brat njihov Nuh reče: 'Zar se sačuvati nećete?!", TextEnglish = "When their brother Noah said to them, 'Will you not fear Allāh?", TextGerman = "Als ihr Bruder Nuh zu ihnen sagte: „Wollt ihr nicht gottesfürchtig sein?" });
                        surah26.VerseList.Add(new Verse { Number = 107, TextBosnian = "Ja sam vam, sigurno, poslanik pouzdani,", TextEnglish = "Indeed, I am to you a trustworthy messenger.", TextGerman = "Gewiß, ich bin für euch ein vertrauenswürdiger Gesandter." });
                        surah26.VerseList.Add(new Verse { Number = 108, TextBosnian = "zato se bojte Allaha i budite poslušni meni!", TextEnglish = "So fear Allāh and obey me.", TextGerman = "So fürchtet Allah und gehorcht mir." });
                        surah26.VerseList.Add(new Verse { Number = 109, TextBosnian = "Za ovo od vas ne tražim nikakve nagrade, mene će Gospodar svjetova nagraditi.", TextEnglish = "And I do not ask you for it any payment. My payment is only from the Lord of the worlds.", TextGerman = "Ich verlange von euch keinen Lohn dafür. Mein Lohn obliegt nur dem Herrn der Weltenbewohner." });
                        surah26.VerseList.Add(new Verse { Number = 110, TextBosnian = "Zato se bojte Allaha i budite poslušni meni!',", TextEnglish = "So fear Allāh and obey me.'", TextGerman = "So fürchtet Allah und gehorcht mir." });
                        surah26.VerseList.Add(new Verse { Number = 111, TextBosnian = "oni rekoše: 'Kako da te poslušamo kad te najniži sloj ljudi slijedi", TextEnglish = "They said, 'Should we believe you while you are followed by the lowest [class of people]?'", TextGerman = "Sie sagten: „Sollen wir dir glauben, wo dir (nur) die Niedrigsten folgen?" });
                        surah26.VerseList.Add(new Verse { Number = 112, TextBosnian = "Ne znam ja šta oni rade', reče on,", TextEnglish = "He said, 'And what is my knowledge of what they used to do?", TextGerman = "Er sagte: „Welches Wissen sollte ich darüber haben, was sie zu tun pflegten?" });
                        surah26.VerseList.Add(new Verse { Number = 113, TextBosnian = "svi će pred Gospodarom mojim, da znate, račun polagati,", TextEnglish = "Their account is only upon my Lord, if you [could] perceive.", TextGerman = "Ihre Abrechnung obliegt nur meinem Herrn, wenn ihr nur merken würdet!" });
                        surah26.VerseList.Add(new Verse { Number = 114, TextBosnian = "a ja vjernike neću otjerati,", TextEnglish = "And I am not one to drive away the believers.", TextGerman = "Und ich werde die Gläubigen nicht abweisen." });
                        surah26.VerseList.Add(new Verse { Number = 115, TextBosnian = "ja sam samo upozoritelj jasni!'", TextEnglish = "I am only a clear warner.'", TextGerman = "Ich bin nur ein deutlicher Warner." });
                        surah26.VerseList.Add(new Verse { Number = 116, TextBosnian = "Ako se ne okaniš, o Nuh!', rekoše oni, 'bit ćeš sigurno kamenovan!'", TextEnglish = "They said, 'If you do not desist, O Noah, you will surely be of those who are stoned.'", TextGerman = "Sie sagten: „Wenn du nicht aufhörst, o Nuh, wirst du ganz gewiß zu den Gesteinigten gehören." });
                        surah26.VerseList.Add(new Verse { Number = 117, TextBosnian = "Gospodaru moj', reče on, 'narod moj me u laž utjeruje,", TextEnglish = "He said, 'My Lord, indeed my people have denied me.", TextGerman = "Er sagte: „Mein Herr, gewiß mein Volk hat mich der Lüge bezichtigt." });
                        surah26.VerseList.Add(new Verse { Number = 118, TextBosnian = "pa Ti meni i njima presudi i mene i vjernike koji su sa mnom spasi!'", TextEnglish = "Then judge between me and them with decisive judgement and save me and those with me of the believers.'", TextGerman = "So triff zwischen mir und ihnen eine klare Entscheidung und errette mich und wer von den Gläubigen mit mir ist." });
                        surah26.VerseList.Add(new Verse { Number = 119, TextBosnian = "I Mi smo njega i one koji su bili uz njega u lađi krcatoj spasili,", TextEnglish = "So We saved him and those with him in the laden ship.", TextGerman = "Da retteten Wir ihn und wer mit ihm war im vollbeladenen Schiff." });
                        surah26.VerseList.Add(new Verse { Number = 120, TextBosnian = "a ostale potopili.", TextEnglish = "Then We drowned thereafter the remaining ones.", TextGerman = "Hierauf ließen Wir alsdann die übrigen ertrinken." });
                        surah26.VerseList.Add(new Verse { Number = 121, TextBosnian = "U tome je, zaista, znak, ali većina njih nisu vjernici.", TextEnglish = "Indeed in that is a sign, but most of them were not to be believers.", TextGerman = "Darin ist wahrlich ein Zeichen, doch sind die meisten von ihnen nicht gläubig." });
                        surah26.VerseList.Add(new Verse { Number = 122, TextBosnian = "A, zaista, Gospodar tvoj- baš On-Silni je i Milostivi!", TextEnglish = "And indeed, your Lord - He is the Exalted in Might, the Merciful.", TextGerman = "Und dein Herr ist fürwahr der Allmächtige und Barmherzige." });
                        surah26.VerseList.Add(new Verse { Number = 123, TextBosnian = "I narod Ad je smatrao lažnim poslanike.", TextEnglish = "ʿAad denied the messengers", TextGerman = "Die ’Ad bezichtigten die Gesandten der Lüge." });
                        surah26.VerseList.Add(new Verse { Number = 124, TextBosnian = "Kad im brat njihov Hud reče: 'Kako to da se Allaha ne bojite?", TextEnglish = "When their brother Hūd said to them, 'Will you not fear Allāh?", TextGerman = "Als ihr Bruder Hud zu ihnen sagte: „Wollt ihr nicht gottesfürchtig sein?" });
                        surah26.VerseList.Add(new Verse { Number = 125, TextBosnian = "Ja sam vam, sigurno, poslanik pouzdani,", TextEnglish = "Indeed, I am to you a trustworthy messenger.", TextGerman = "Gewiß, ich bin für euch ein vertrauenswürdiger Gesandter." });
                        surah26.VerseList.Add(new Verse { Number = 126, TextBosnian = "zato se bojte Allaha i budite poslušni meni!", TextEnglish = "So fear Allāh and obey me.", TextGerman = "So fürchtet Allah und gehorcht mir." });
                        surah26.VerseList.Add(new Verse { Number = 127, TextBosnian = "Za ovo od vas ne tražim nikakve nagrade, mene će Gospodar svjetova nagraditi.", TextEnglish = "And I do not ask you for it any payment. My payment is only from the Lord of the worlds.", TextGerman = "Ich verlange von euch keinen Lohn dafür. Mein Lohn obliegt nur dem Herrn der Weltenbewohner." });
                        surah26.VerseList.Add(new Verse { Number = 128, TextBosnian = "Zašto na svakoj uzvišici, zabavljajući se, palate zidate", TextEnglish = "Do you construct on every elevation a sign,[1055] amusing yourselves,", TextGerman = "Baut ihr denn auf jeder Anhöhe ein Wahrzeichen und treibt ein sinnloses Spiel" });
                        surah26.VerseList.Add(new Verse { Number = 129, TextBosnian = "i podižete utvrde kao da ćete vječno živjeti,", TextEnglish = "And take for yourselves constructions [i.e., palaces and fortresses] that you might abide eternally?", TextGerman = "und nehmt euch Bauwerke (in der Hoffnung), auf daß ihr ewig leben würdet?" });
                        surah26.VerseList.Add(new Verse { Number = 130, TextBosnian = "a kad kažnjavate, kažnjavate kao silnici?", TextEnglish = "And when you strike, you strike as tyrants.", TextGerman = "Und, wenn ihr zupackt, packt ihr gewalttätig zu." });
                        surah26.VerseList.Add(new Verse { Number = 131, TextBosnian = "Bojte se Allaha i meni budite poslušni!", TextEnglish = "So fear Allāh and obey me.", TextGerman = "So fürchtet Allah und gehorcht mir." });
                        surah26.VerseList.Add(new Verse { Number = 132, TextBosnian = "Bojte se Onoga Koji vas je opskrbio onim što znate,", TextEnglish = "And fear He who provided you with that which you know,", TextGerman = "Und fürchtet Denjenigen, Der euch unterstützt hat mit dem, was ihr wißt," });
                        surah26.VerseList.Add(new Verse { Number = 133, TextBosnian = "opskrbio vas je stokom i sinovima,", TextEnglish = "Provided you with grazing livestock and children", TextGerman = "(Der) euch unterstützt hat mit Vieh und Söhnen" });
                        surah26.VerseList.Add(new Verse { Number = 134, TextBosnian = "i baščama i izvorima.", TextEnglish = "And gardens and springs.", TextGerman = "und Gärten und Quellen." });
                        surah26.VerseList.Add(new Verse { Number = 135, TextBosnian = "Ja se, doista, za vas bojim patnje na Velikom danu.'", TextEnglish = "Indeed, I fear for you the punishment of a terrible day.'", TextGerman = "Gewiß, ich fürchte für euch die Strafe eines gewaltigen Tages." });
                        surah26.VerseList.Add(new Verse { Number = 136, TextBosnian = "Rekoše oni: 'Nama je svejedno savjetovao ti ili ne bio savjetnik,", TextEnglish = "They said, 'It is all the same to us whether you advise or are not of the advisors.", TextGerman = "Sie sagten: „Gleich ist es in Bezug auf uns, ob du ermahnst oder ob du nicht zu denjenigen gehörst, die ermahnen." });
                        surah26.VerseList.Add(new Verse { Number = 137, TextBosnian = "ovako su i narodi davnašnji vjerovali,", TextEnglish = "This is not but the custom of the former peoples,", TextGerman = "Das hier ist nur die Sitte der Früheren." });
                        surah26.VerseList.Add(new Verse { Number = 138, TextBosnian = "i mi nećemo biti patnji podvrgnuti.'", TextEnglish = "And we are not to be punished.'", TextGerman = "Und wir werden nicht gestraft werden." });
                        surah26.VerseList.Add(new Verse { Number = 139, TextBosnian = "I oni su nastavili da ga u laž utjeruju, pa smo ih Mi uništili. U tome je, zaista, znak, ali većina njih nisu vjernici,", TextEnglish = "And they denied him, so We destroyed them. Indeed in that is a sign, but most of them were not to be believers.", TextGerman = "So bezichtigten sie ihn der Lüge, und da vernichteten Wir sie. Darin ist wahrlich ein Zeichen, doch sind die meisten von ihnen nicht gläubig." });
                        surah26.VerseList.Add(new Verse { Number = 140, TextBosnian = "A, zaista, Gospodar tvoj - baš On - Silni je i Milostivi!", TextEnglish = "And indeed, your Lord - He is the Exalted in Might, the Merciful.", TextGerman = "Und dein Herr ist fürwahr der Allmächtige und Barmherzige." });
                        surah26.VerseList.Add(new Verse { Number = 141, TextBosnian = "I Semud je smatrao lažnim poslanike.", TextEnglish = "Thamūd denied the messengers", TextGerman = "Die Tamud bezichtigten die Gesandten der Lüge." });
                        surah26.VerseList.Add(new Verse { Number = 142, TextBosnian = "Kad im brat njihov Salih reče: 'Kako to da se Allaha ne bojite?", TextEnglish = "When their brother Ṣāliḥ said to them, 'Will you not fear Allāh?", TextGerman = "Als ihr Bruder Salih zu ihnen sagte: „Wollt ihr nicht gottesfürchtig sein?" });
                        surah26.VerseList.Add(new Verse { Number = 143, TextBosnian = "Ja sam vam, sigurno, poslanik pouzdani.", TextEnglish = "Indeed, I am to you a trustworthy messenger.", TextGerman = "Gewiß, ich bin für euch ein vertrauenswürdiger Gesandter." });
                        surah26.VerseList.Add(new Verse { Number = 144, TextBosnian = "Zato se bojte Allaha i budite poslušni meni!", TextEnglish = "So fear Allāh and obey me.", TextGerman = "So fürchtet Allah und gehorcht mir." });
                        surah26.VerseList.Add(new Verse { Number = 145, TextBosnian = "Za to od vas ne tražim nikakve nagrade, mene će Gospodar svjetova nagraditi.", TextEnglish = "And I do not ask you for it any payment. My payment is only from the Lord of the worlds.", TextGerman = "Ich verlange von euch keinen Lohn dafür. Mein Lohn obliegt nur dem Herrn der Weltenbewohner." });
                        surah26.VerseList.Add(new Verse { Number = 146, TextBosnian = "Zar mislite da ćete ovdje biti ostavljeni sigurni,", TextEnglish = "Will you be left in what is here, secure [from death],", TextGerman = "Werdet ihr etwa in Sicherheit belassen in dem, was hier ist," });
                        surah26.VerseList.Add(new Verse { Number = 147, TextBosnian = "u vrtovima i među izvorima,", TextEnglish = "Within gardens and springs", TextGerman = "in Gärten und an Quellen." });
                        surah26.VerseList.Add(new Verse { Number = 148, TextBosnian = "u usjevima i među palmama sa plodovima zrelim?", TextEnglish = "And fields of crops and palm trees with softened fruit?", TextGerman = "In Getreidefeldern und unter Palmen, deren Blütenscheiden zart sind?" });
                        surah26.VerseList.Add(new Verse { Number = 149, TextBosnian = "Vi u brdima vrlo spretno kuće klešete,", TextEnglish = "And you carve out of the mountains, homes, with skill.", TextGerman = "Und werdet ihr (weiter) voller Stolz aus den Bergen Häuser aushauen?" });
                        surah26.VerseList.Add(new Verse { Number = 150, TextBosnian = "pa bojte se Allaha i poslušni meni budite,", TextEnglish = "So fear Allāh and obey me.", TextGerman = "So fürchtet Allah und gehorcht mir." });
                        surah26.VerseList.Add(new Verse { Number = 151, TextBosnian = "i ne slušajte naredbe onih koji u zlu pretjeruju,", TextEnglish = "And do not obey the order of the transgressors,", TextGerman = "Und gehorcht nicht dem Befehl der Maßlosen," });
                        surah26.VerseList.Add(new Verse { Number = 152, TextBosnian = "koji na Zemlji nered, a ne red, uspostavljaju.'", TextEnglish = "Who cause corruption in the land and do not amend.'", TextGerman = "die auf der Erde Unheil stiften und keine Besserung bringen." });
                        surah26.VerseList.Add(new Verse { Number = 153, TextBosnian = "Rekoše oni: 'Ti si samo opčinjen,", TextEnglish = "They said, 'You are only of those affected by magic.", TextGerman = "Sie sagten: „Du gehörst ja nur zu denjenigen, die einem Zauber verfallen sind." });
                        surah26.VerseList.Add(new Verse { Number = 154, TextBosnian = "ti si čovjek, kao i mi; zato nam donesi jedno čudo ako je istina to što govoriš!'", TextEnglish = "You are but a man like ourselves, so bring a sign, if you should be of the truthful.'", TextGerman = "Du bist nur ein menschliches Wesen wie wir. So bringe doch ein Zeichen her, wenn du zu den Wahrhaftigen gehörst." });
                        surah26.VerseList.Add(new Verse { Number = 155, TextBosnian = "Evo, to je kamila'„ reče on, 'u određeni dan ona će piti, a u određeni dan vi,", TextEnglish = "He said, 'This is a she-camel.[1056] For her is a [time of] drink, and for you is a [time of] drink, [each] on a known day.", TextGerman = "Er sagte: „Dies ist eine Kamelstute; sie hat eine Trinkzeit, und ihr habt eine Trinkzeit an einem bestimmten Tag." });
                        surah26.VerseList.Add(new Verse { Number = 156, TextBosnian = "i ne činite joj nikakvo zlo da vas ne bi stigla patnja na Velikom danu!'", TextEnglish = "And do not touch her with harm, lest you be seized by the punishment of a terrible day.'", TextGerman = "Fügt ihr nichts Böses zu, sonst überkommt euch die Strafe eines gewaltigen Tages." });
                        surah26.VerseList.Add(new Verse { Number = 157, TextBosnian = "Ali, oni je zaklaše i potom se pokajaše,", TextEnglish = "But they hamstrung her and so became regretful.", TextGerman = "Aber sie schnitten ihr die Sehnen durch . So wurden sie zu Leuten, die Reue empfinden." });
                        surah26.VerseList.Add(new Verse { Number = 158, TextBosnian = "i stiže ih kazna. U tome je, zaista, znak, ali većina njih ne vjeruje.", TextEnglish = "And the punishment seized them. Indeed in that is a sign, but most of them were not to be believers.", TextGerman = "Da ergriff sie die Strafe. Darin ist wahrlich ein Zeichen, doch sind die meisten von ihnen nicht gläubig." });
                        surah26.VerseList.Add(new Verse { Number = 159, TextBosnian = "A, zaista, Gospodar tvoj - baš On - Silni je i Milostivi!", TextEnglish = "And indeed, your Lord - He is the Exalted in Might, the Merciful.", TextGerman = "Und dein Herr ist fürwahr der Allmächtige und Barmherzige." });
                        surah26.VerseList.Add(new Verse { Number = 160, TextBosnian = "I Lutov je narod smatrao lažnim poslanike.", TextEnglish = "The people of Lot denied the messengers", TextGerman = "Das Volk Luts bezichtigte die Gesandten der Lüge." });
                        surah26.VerseList.Add(new Verse { Number = 161, TextBosnian = "Kad im njihov brat Lut reče: 'Kako to da se ne bojite?", TextEnglish = "When their brother Lot said to them, 'Will you not fear Allāh?", TextGerman = "Als ihr Bruder Lut zu ihnen sagte: „Wollt ihr nicht gottesfürchtig sein?" });
                        surah26.VerseList.Add(new Verse { Number = 162, TextBosnian = "Ja sam vam, sigurno, poslanik pouzdani,", TextEnglish = "Indeed, I am to you a trustworthy messenger.", TextGerman = "Gewiß, ich bin für euch ein vertrauenswürdiger Gesandter." });
                        surah26.VerseList.Add(new Verse { Number = 163, TextBosnian = "zato se bojte Allaha i budite poslušni meni!", TextEnglish = "So fear Allāh and obey me.", TextGerman = "So fürchtet Allah und gehorcht mir." });
                        surah26.VerseList.Add(new Verse { Number = 164, TextBosnian = "Za to od vas ne tražim nikakve nagrade, mene će Gospodar svjetova nagraditi.", TextEnglish = "And I do not ask you for it any payment. My payment is only from the Lord of the worlds.", TextGerman = "Ich verlange von euch keinen Lohn dafür. Mein Lohn obliegt nur dem Herrn der Weltenbewohner." });
                        surah26.VerseList.Add(new Verse { Number = 165, TextBosnian = "Zašto vi, mimo svih svjetova, s muškarcima općite,", TextEnglish = "Do you approach males among the worlds[1057]", TextGerman = "Laßt ihr euch etwa mit Männern unter den Weltenbewohnern ein" });
                        surah26.VerseList.Add(new Verse { Number = 166, TextBosnian = "a žene svoje, koje je za vas Gospodar vaš stvorio, ostavljate? Vi ste ljudi koji svaku granicu zla prelaze.'", TextEnglish = "And leave what your Lord has created for you as mates? But you are a people transgressing.'", TextGerman = "und laßt, was euer Herr für euch an Gattinnen erschaffen hat, stehen? Aber nein! Ihr seid Leute, die Übertretungen begehen." });
                        surah26.VerseList.Add(new Verse { Number = 167, TextBosnian = "Rekoše oni: 'Ako se ne okaniš, o Lut, bit ćeš sigurno prognan!'", TextEnglish = "They said, 'If you do not desist, O Lot, you will surely be of those evicted.'", TextGerman = "Sie sagten: „Wenn du nicht aufhörst, o Lut, wirst du ganz gewiß zu den Vertriebenen gehören." });
                        surah26.VerseList.Add(new Verse { Number = 168, TextBosnian = "Ja se gnušam toga što vi radite!', reče on.", TextEnglish = "He said, 'Indeed, I am, toward your deed, of those who detest [it].", TextGerman = "Er sagte: „Ich gehöre gewiß zu denjenigen, die euer Tun verabscheuen." });
                        surah26.VerseList.Add(new Verse { Number = 169, TextBosnian = "Gospodaru moj, sačuvaj mene i porodicu moju od onoga što oni rade!'", TextEnglish = "My Lord, save me and my family from [the consequence of] what they do.'", TextGerman = "Mein Herr, errette mich und meine Angehörigen von dem, was sie tun." });
                        surah26.VerseList.Add(new Verse { Number = 170, TextBosnian = "I Mi smo sačuvali njega i porodicu njegovu - sve,", TextEnglish = "So We saved him and his family, all,", TextGerman = "Da erretteten Wir ihn und seine Angehörigen allesamt," });
                        surah26.VerseList.Add(new Verse { Number = 171, TextBosnian = "osim starice koja je ostala s onima koji su kaznu iskusili,", TextEnglish = "Except an old woman[1058] among those who remained behind.", TextGerman = "außer einer alten Frau unter denjenigen, die zurückblieben." });
                        surah26.VerseList.Add(new Verse { Number = 172, TextBosnian = "a zatim smo ostale uništili", TextEnglish = "Then We destroyed the others.", TextGerman = "Hierauf vernichteten Wir die anderen." });
                        surah26.VerseList.Add(new Verse { Number = 173, TextBosnian = "spustivši na njih kišu - a strašne li kiše za one koji su upozoreni bili!", TextEnglish = "And We rained upon them a rain [of stones], and evil was the rain of those who were warned.", TextGerman = "Und Wir ließen einen Regen auf sie niedergehen; wie böse war der Regen für diejenigen, die gewarnt worden waren!" });
                        surah26.VerseList.Add(new Verse { Number = 174, TextBosnian = "U tome je, zaista, znak, ali većina njih ne vjeruje,", TextEnglish = "Indeed in that is a sign, but most of them were not to be believers.", TextGerman = "Darin ist wahrlich ein Zeichen, doch sind die meisten von ihnen nicht gläubig." });
                        surah26.VerseList.Add(new Verse { Number = 175, TextBosnian = "A, zaista, Gospodar tvoj-baš On- Silni je i Milostivi!", TextEnglish = "And indeed, your Lord - He is the Exalted in Might, the Merciful.", TextGerman = "Und dein Herr ist fürwahr der Allmächtige und Barmherzige." });
                        surah26.VerseList.Add(new Verse { Number = 176, TextBosnian = "I stanovnici Ejke su u laž ugonili poslanike.", TextEnglish = "The companions of the thicket [i.e., the people of Madyan] denied the messengers", TextGerman = "Die Bewohner des Dickichts bezichtigte die Gesandten der Lüge." });
                        surah26.VerseList.Add(new Verse { Number = 177, TextBosnian = "Kad im Šuajb reče: 'Kako to da se Allaha ne bojite?", TextEnglish = "When Shuʿayb said to them, 'Will you not fear Allāh?", TextGerman = "Als Su’aib zu ihnen sagte: „Wollt ihr nicht gottesfürchtig sein?" });
                        surah26.VerseList.Add(new Verse { Number = 178, TextBosnian = "Ja sam vam, sigurno, poslanik pouzdani,", TextEnglish = "Indeed, I am to you a trustworthy messenger.", TextGerman = "Gewiß, ich bin für euch ein vertrauenswürdiger Gesandter." });
                        surah26.VerseList.Add(new Verse { Number = 179, TextBosnian = "zato se bojte Allaha i budite poslušni meni!", TextEnglish = "So fear Allāh and obey me.", TextGerman = "So fürchtet Allah und gehorcht mir." });
                        surah26.VerseList.Add(new Verse { Number = 180, TextBosnian = "Za to od vas ne tražim nikakve nagrade, mene će Gospodar svjetova nagraditi.", TextEnglish = "And I do not ask you for it any payment. My payment is only from the Lord of the worlds.", TextGerman = "Ich verlange von euch keinen Lohn dafür. Mein Lohn obliegt nur dem Herrn der Weltenbewohner." });
                        surah26.VerseList.Add(new Verse { Number = 181, TextBosnian = "Pravo mjerite na litru i ne zakidajte,", TextEnglish = "Give full measure and do not be of those who cause loss.", TextGerman = "Gebt volles Maß und gehört nicht zu denjenigen, die (anderen) Verlust verursachen." });
                        surah26.VerseList.Add(new Verse { Number = 182, TextBosnian = "a na kantaru ispravnom mjerom mjerite,", TextEnglish = "And weigh with an even [i.e., honest] balance.", TextGerman = "Und wägt mit der richtigen Waage." });
                        surah26.VerseList.Add(new Verse { Number = 183, TextBosnian = "i ljudima prava njihova ne umanjujte i zlo po Zemlji, nered praveći, ne činite,", TextEnglish = "And do not deprive people of their due and do not commit abuse on earth, spreading corruption.", TextGerman = "Und schmälert den Menschen nicht ihre Sachen und richtet auf der Erde nicht unheilstiftend Verderben an." });
                        surah26.VerseList.Add(new Verse { Number = 184, TextBosnian = "i Onoga Koji je stvorio i vas i narode davnašnje - bojte se.'", TextEnglish = "And fear He who created you and the former creation.'[1059]", TextGerman = "Und fürchtet Denjenigen, Der euch und die früheren Geschöpfe erschaffen hat." });
                        surah26.VerseList.Add(new Verse { Number = 185, TextBosnian = "Rekoše oni: 'Ti si samo opčinjen;", TextEnglish = "They said, 'You are only of those affected by magic.", TextGerman = "Sie sagten: „Du gehörst ja nur zu denjenigen, die einem starken Zauber verfallen sind." });
                        surah26.VerseList.Add(new Verse { Number = 186, TextBosnian = "i ti si samo čovjek kao i mi; za nas si ti, doista, lažac pravi;", TextEnglish = "You are but a man like ourselves, and indeed, we think you are among the liars.", TextGerman = "Du bist nur ein menschliches Wesen wie wir. Wir meinen wahrlich, daß du zu den Lügnern gehörst." });
                        surah26.VerseList.Add(new Verse { Number = 187, TextBosnian = "zato spusti na nas komad neba, ako u one koji istinu govore spadaš!'", TextEnglish = "So cause to fall upon us fragments of the sky, if you should be of the truthful.'", TextGerman = "Lasse doch Stücke vom Himmel auf uns herabfallen, wenn du zu den Wahrhaftigen gehörst." });
                        surah26.VerseList.Add(new Verse { Number = 188, TextBosnian = "Gospodar moj dobro zna šta vi radite', reče on.", TextEnglish = "He said, 'My Lord is most knowing of what you do.'", TextGerman = "Er sagte: „Mein Herr weiß am besten, was ihr tut." });
                        surah26.VerseList.Add(new Verse { Number = 189, TextBosnian = "I oni su nastavili da ga u laž utjeruju pa ih je stigla kazna u Danu tamnog oblaka; a to je bila kazna jednog strašnog dana.", TextEnglish = "And they denied him, so the punishment of the day of the black cloud seized them. Indeed, it was the punishment of a terrible day.", TextGerman = "Aber sie bezichtigten ihn der Lüge. Da ergriff sie die Strafe des Tages der schattenspendenden Wolke; gewiß es war die Strafe eines gewaltigen Tages." });
                        surah26.VerseList.Add(new Verse { Number = 190, TextBosnian = "U tome je, zaista, znak, ali većina njih neće da vjeruje.", TextEnglish = "Indeed in that is a sign, but most of them were not to be believers.", TextGerman = "Darin ist wahrlich ein Zeichen, doch sind die meisten von ihnen nicht gläubig." });
                        surah26.VerseList.Add(new Verse { Number = 191, TextBosnian = "A, zaista, Gospodar tvoj - baš On - Silnije i Milostivi!", TextEnglish = "And indeed, your Lord - He is the Exalted in Might, the Merciful.", TextGerman = "Und dein Herr ist fürwahr der Allmächtige und Barmherzige." });
                        surah26.VerseList.Add(new Verse { Number = 192, TextBosnian = "Kur'an je sigurno objava Gospodara svjetova;", TextEnglish = "And indeed, it [i.e., the Qur’ān] is the revelation of the Lord of the worlds.", TextGerman = "Und er ist ganz sicher eine Offenbarung des Herrn der Weltenbewohner;" });
                        surah26.VerseList.Add(new Verse { Number = 193, TextBosnian = "donosi ga povjerljivi Duh,", TextEnglish = "The Trustworthy Spirit [i.e., Gabriel] has brought it down", TextGerman = "mit dem der vertrauenswürdige Geist herabgekommen ist" });
                        surah26.VerseList.Add(new Verse { Number = 194, TextBosnian = "na srce tvoje, da budeš upozoritelj,", TextEnglish = "Upon your heart, [O Muḥammad] - that you may be of the warners -", TextGerman = "auf dein Herz, damit du zu den Überbringern von Warnung gehörst," });
                        surah26.VerseList.Add(new Verse { Number = 195, TextBosnian = "na jasnom arapskom jeziku.", TextEnglish = "In a clear Arabic language.", TextGerman = "in deutlicher arabischer Sprache." });
                        surah26.VerseList.Add(new Verse { Number = 196, TextBosnian = "On je spomenut u knjigama poslanika prijašnjih,", TextEnglish = "And indeed, it is [mentioned] in the scriptures of former peoples.", TextGerman = "Und er ist wahrlich in den Schriften der Früheren (erwähnt)." });
                        surah26.VerseList.Add(new Verse { Number = 197, TextBosnian = "zar njima nije znak to što za njega znaju učeni ljudi sinova Israilovih?", TextEnglish = "And has it not been a sign to them that it is recognized by the scholars of the Children of Israel?", TextGerman = "War es denn kein Zeichen für sie, daß die Gelehrten der Kinder Isra’ils darüber Bescheid wissen?" });
                        surah26.VerseList.Add(new Verse { Number = 198, TextBosnian = "A da ga objavljujemo i nekom nearapu,", TextEnglish = "And even if We had revealed it to one among the foreigners[1060]", TextGerman = "Wenn Wir ihn einem der Nichtaraber offenbart hätten" });
                        surah26.VerseList.Add(new Verse { Number = 199, TextBosnian = "pa da im ga on čita, opet u njega ne bi povjerovali.", TextEnglish = "And he had recited it to them [perfectly],[1061] they would [still] not have been believers in it.", TextGerman = "und er ihn ihnen vorgelesen hätte, hätten sie nicht daran geglaubt." });
                        surah26.VerseList.Add(new Verse { Number = 200, TextBosnian = "Eto, tako ga Mi u srca prestupnika uvodimo,", TextEnglish = "Thus have We inserted it [i.e., disbelief] into the hearts of the criminals.", TextGerman = "So haben Wir ihn in die Herzen der Übeltäter einziehen lassen." });
                        surah26.VerseList.Add(new Verse { Number = 201, TextBosnian = "oni u njega neće vjerovati dok ne vide patnju bolnu,", TextEnglish = "They will not believe in it until they see the painful punishment.", TextGerman = "Sie glauben nicht eher daran, bis sie die schmerzhafte Strafe sehen" });
                        surah26.VerseList.Add(new Verse { Number = 202, TextBosnian = "koja će im iznenada doći, a oni neće ni osjetiti,", TextEnglish = "And it will come to them suddenly while they perceive [it] not.", TextGerman = "und diese plötzlich über sie kommt, ohne daß sie merken," });
                        surah26.VerseList.Add(new Verse { Number = 203, TextBosnian = "pa će reći: 'Hoće li nam se imalo vremena dati?'", TextEnglish = "And they will say, 'May we be reprieved?'", TextGerman = "bis sie dann sagen: „Wird uns Aufschub gewährt?" });
                        surah26.VerseList.Add(new Verse { Number = 204, TextBosnian = "Pa zar oni kaznu Našu požuruju?!", TextEnglish = "So for Our punishment are they impatient?", TextGerman = "Wünschen sie denn Unsere Strafe zu beschleunigen?" });
                        surah26.VerseList.Add(new Verse { Number = 205, TextBosnian = "Šta ti misliš, ako im Mi dopuštamo da godinama uživaju,", TextEnglish = "Then have you considered if We gave them enjoyment for years", TextGerman = "Was meinst du wohl? Wenn Wir sie über Jahre hinweg genießen lassen," });
                        surah26.VerseList.Add(new Verse { Number = 206, TextBosnian = "i naposljetku ih snađe ono što im se kao prijetnja obećava,", TextEnglish = "And then there came to them that which they were promised?", TextGerman = "und hierauf über sie kommt, was ihnen stets angedroht wurde," });
                        surah26.VerseList.Add(new Verse { Number = 207, TextBosnian = "zar će imati koristi od slatkog života koji su provodili?", TextEnglish = "They would not be availed by the enjoyment with which they were provided.", TextGerman = "nicht nützen wird ihnen (dann), was ihnen an Nießbrauch gewährt wurde." });
                        surah26.VerseList.Add(new Verse { Number = 208, TextBosnian = "Mi nijedno naselje nismo razorili prije nego su im došli oni koji su ih upozoravali,", TextEnglish = "And We did not destroy any city except that it had warners", TextGerman = "Und Wir vernichteten keine Stadt, ohne daß sie Überbringer von Warnungen gehabt hätte," });
                        surah26.VerseList.Add(new Verse { Number = 209, TextBosnian = "radi opomene, a Mi nismo zulumćari bili.", TextEnglish = "As a reminder; and never have We been unjust.", TextGerman = "(dies) als Ermahnung. Und nie sind Wir ungerecht." });
                        surah26.VerseList.Add(new Verse { Number = 210, TextBosnian = "Kur'an ne donose šejtani,", TextEnglish = "And the devils have not brought it [i.e., the revelation] down.[1062]", TextGerman = "Nicht die Satane sind mit ihm herabgekommen;" });
                        surah26.VerseList.Add(new Verse { Number = 211, TextBosnian = "nezamislivo je da to oni čine; oni to ne mogu,", TextEnglish = "It is not allowable for them, nor would they be able.", TextGerman = "es ziemt ihnen nicht, und sie können es (auch) nicht." });
                        surah26.VerseList.Add(new Verse { Number = 212, TextBosnian = "da ga prisluškuju - oni su potpuno spriječeni.", TextEnglish = "Indeed they, from [its] hearing, are removed.[1063]", TextGerman = "Sie sind vom Hören fürwahr ausgeschlossen." });
                        surah26.VerseList.Add(new Verse { Number = 213, TextBosnian = "Zato se, mimo Allaha, ne moli drugom bogu - da ne bi bio jedan od onih koji će biti patnji podvrgnuti!", TextEnglish = "So do not invoke[1064] with Allāh another deity and [thus] be among the punished.", TextGerman = "So rufe neben Allah keinen anderen Gott an, sonst wirst du zu den Gestraften gehören." });
                        surah26.VerseList.Add(new Verse { Number = 214, TextBosnian = "I upozoravaj rodbinu svoju najbližu,", TextEnglish = "And warn, [O Muḥammad], your closest kindred.", TextGerman = "Und warne die Nächsten deiner Sippe." });
                        surah26.VerseList.Add(new Verse { Number = 215, TextBosnian = "i budi ponizan prema vjernicima koji te slijede!", TextEnglish = "And lower your wing [i.e., show kindness] to those who follow you of the believers.", TextGerman = "Und senke deinen Flügel für diejenigen von den Gläubigen, die dir folgen." });
                        surah26.VerseList.Add(new Verse { Number = 216, TextBosnian = "A ako te ne budu poslušali, ti reci: 'Ja nemam ništa s tim što vi radite.'", TextEnglish = "And if they disobey you, then say, 'Indeed, I am disassociated from what you are doing.'", TextGerman = "Wenn sie sich dir widersetzen, dann sag: „Ich sage mich los von dem, was ihr tut." });
                        surah26.VerseList.Add(new Verse { Number = 217, TextBosnian = "I pouzdaj se u Silnoga i Milostivoga,", TextEnglish = "And rely upon the Exalted in Might, the Merciful,", TextGerman = "Und verlasse dich auf den Allmächtigen und Barmherzigen," });
                        surah26.VerseList.Add(new Verse { Number = 218, TextBosnian = "Koji te vidi kada na noćnom namazu stojiš,", TextEnglish = "Who sees you when you arise[1065]", TextGerman = "Der dich sieht, wenn du aufrecht stehst," });
                        surah26.VerseList.Add(new Verse { Number = 219, TextBosnian = "i tvoje pregibanje među onima koji čine sedždu.", TextEnglish = "And your movement among those who prostrate.[1066]", TextGerman = "und deine Stellungswechsel unter denjenigen, die sich niederwerfen." });
                        surah26.VerseList.Add(new Verse { Number = 220, TextBosnian = "A, zaista, je baš On - Silni i Milostivi!", TextEnglish = "Indeed, He is the Hearing, the Knowing.", TextGerman = "Er ist ja der Allhörende und Allwissende." });
                        surah26.VerseList.Add(new Verse { Number = 221, TextBosnian = "Hoćete li da vas obavijestim kome dolaze šejtani?", TextEnglish = "Shall I inform you upon whom the devils descend?", TextGerman = "Soll ich euch kundtun, auf wen die Satane herabkommen?" });
                        surah26.VerseList.Add(new Verse { Number = 222, TextBosnian = "Oni dolaze svakom lašcu, grešniku,", TextEnglish = "They descend upon every sinful liar.", TextGerman = "Sie kommen auf jeden ungeheuerlichen Lügner und Sünder herab." });
                        surah26.VerseList.Add(new Verse { Number = 223, TextBosnian = "i prisluškuju, a većina njih su lažljivci.", TextEnglish = "They pass on what is heard,[1067] and most of them are liars.", TextGerman = "Sie hören hin, und die meisten von ihnen sind Lügner." });
                        surah26.VerseList.Add(new Verse { Number = 224, TextBosnian = "A pjesnici - njih zavedeni slijede.", TextEnglish = "And the poets - [only] the deviators follow them;", TextGerman = "Und die Dichter - es folgen ihnen die Verirrten." });
                        surah26.VerseList.Add(new Verse { Number = 225, TextBosnian = "Zar ne znaš da oni svakom dolinom tumaraju", TextEnglish = "Do you not see that in every valley they roam[1068]", TextGerman = "Siehst du nicht, daß sie in jedem Tal ziellos umherwandern" });
                        surah26.VerseList.Add(new Verse { Number = 226, TextBosnian = "i da govore ono što ne rade,", TextEnglish = "And that they say what they do not do? -", TextGerman = "und daß sie sagen, was sie nicht tun?" });
                        surah26.VerseList.Add(new Verse { Number = 227, TextBosnian = "osim onih koji vjeruju i dobra djela čine, i koji često Allaha spominju, i koji uzvrate kada im zulum bude učinjen. A oni koji su zulum činili sigurno će saznati u kakvo će se mjesto vratiti!", TextEnglish = "Except those [poets] who believe and do righteous deeds and remember Allāh often and defend [the Muslims][1069] after they were wronged. And those who have wronged are going to know to what [kind of] return they will be returned.", TextGerman = "Außer denjenigen, die glauben, rechtschaffene Werke tun und Allahs häufig gedenken und sich (erst) selbst helfen, nachdem ihnen ja Unrecht zugefügt wurde. Und diejenigen, die Unrecht tun, werden erfahren, was für eine Rückkehr sie haben werden." });
                        result = surah26;
                        break;
                    #endregion

                    // 
                    #region
                    case 27:
                        Surah surah27 = new Surah(); surah27.Name = Constants.Surah27Name; surah27.Number = 27; surah27.VerseList = new List<Verse>();
                        surah27.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah27.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ta-sin. Ovo su ajeti Kur' ana i Knjige jasne,", TextEnglish = "Ṭā, Seen.[1071] These are the verses of the Qur’ān [i.e., recitation] and a clear Book", TextGerman = "Ta-Sin. Dies sind die Zeichen des Qur’ans und eines deutlichen Buches," });
                        surah27.VerseList.Add(new Verse { Number = 2, TextBosnian = "upute i radosne vijesti onima koji vjeruju,", TextEnglish = "As guidance and good tidings for the believers", TextGerman = "als Rechtleitung und frohe Botschaft für die Gläubigen," });
                        surah27.VerseList.Add(new Verse { Number = 3, TextBosnian = "koji namaz obavljaju i zekat udjeljuju, i oni - baš oni - u ahiret čvrsto vjeruju.", TextEnglish = "Who establish prayer and give zakāh, and of the Hereafter they are certain [in faith].", TextGerman = "die das Gebet verrichten und die Abgabe entrichten, und sie, die sie vom Jenseits überzeugt sind." });
                        surah27.VerseList.Add(new Verse { Number = 4, TextBosnian = "Zaista onima koji u ahiret ne vjeruju Mi prikazujemo kao lijepe postupke njihove-pa oni lutaju;", TextEnglish = "Indeed, for those who do not believe in the Hereafter, We have made pleasing to them their deeds, so they wander blindly.", TextGerman = "Gewiß, denjenigen, die nicht an das Jenseits glauben, haben Wir ihre Taten ausgeschmückt, so daß sie blind umherirren." });
                        surah27.VerseList.Add(new Verse { Number = 5, TextBosnian = "to su oni kojima pripada patnja žestoka, a na ahiretu oni će - baš oni - biti najveći gubitnici,", TextEnglish = "Those are the ones for whom there will be the worst of punishment, and in the Hereafter they are the greatest losers.", TextGerman = "Das sind diejenigen, für die es böse Strafe geben wird, und im Jenseits sind sie die größten Verlierer." });
                        surah27.VerseList.Add(new Verse { Number = 6, TextBosnian = "a ti, zaista, primaš Kur'an od Onog Koji je mudar i Koji sve zna!", TextEnglish = "And indeed, [O Muḥammad], you receive the Qur’ān from one Wise and Knowing.", TextGerman = "Dir wird der Qur’an wahrlich von Seiten eines Allweisen und Allwissenden zum Empfang überbracht." });
                        surah27.VerseList.Add(new Verse { Number = 7, TextBosnian = "Kada Musa reče porodici svojoj: 'Vidio sam vatru, donijet ću vam otuda vijest kakvu, ili ću vam donijeti plamen - glavnju da biste se ogrijali.'", TextEnglish = "[Mention] when Moses said to his family, 'Indeed, I have perceived a fire. I will bring you from there information or will bring you a burning torch that you may warm yourselves.'", TextGerman = "Als Musa zu seinen Angehörigen sagte: „Ich habe ein Feuer wahrgenommen. Ich werde euch davon eine Nachricht bringen, oder ich bringe euch einen Leuchtkörper, ein brennendes Stück Holz, auf daß ihr euch (daran) wärmen könnt." });
                        surah27.VerseList.Add(new Verse { Number = 8, TextBosnian = "Kad je do nje došao, neki glas je dozivao: 'Neka je blagoslovljen ko u vatri je i oni oko nje, i neka je slavljen i uzvišen Allah, Gospodar svjetova!", TextEnglish = "But when he came to it, he was called, 'Blessed is whoever is at the fire and whoever is around it. And exalted is Allāh, Lord of the worlds.", TextGerman = "Als er dort ankam, wurde ihm zugerufen: „Gesegnet ist wer im Feuer und wer in dessen Umgebung ist, und Preis sei Allah, dem Herrn der Weltenbewohner!" });
                        surah27.VerseList.Add(new Verse { Number = 9, TextBosnian = "O Musa, Ja sam-Allah, Silni i Mudri!", TextEnglish = "O Moses, indeed it is I - Allāh, the Exalted in Might, the Wise.'", TextGerman = "O Musa, Ich bin es, gewiß, Ich bin Allah, der Allmächtige und Allweise." });
                        surah27.VerseList.Add(new Verse { Number = 10, TextBosnian = "Baci svoj štap!' Pa kad je vidje da se tresući kreće, kao da je ona džann-zmija, on okrenut leđima uzmače i ne osvrnu se. 'O Musa, ne boj se! Poslanici se kod Mene ne treba ničega da boje;", TextEnglish = "And [he was told], 'Throw down your staff.' But when he saw it writhing as if it were a snake, he turned in flight and did not return.[1072] [Allāh said], 'O Moses, fear not. Indeed, in My presence the messengers do not fear.", TextGerman = "Und: „Wirf deinen Stock hin. Doch als er sah, daß er sich hin und her bewegte, als wäre er eine flinke Schlange, kehrte er den Rücken und wandte sich nicht mehr um. „O Musa, fürchte dich nicht, denn bei Mir brauchen sich die Gesandten nicht zu fürchten." });
                        surah27.VerseList.Add(new Verse { Number = 11, TextBosnian = "Međutim, onaj koji zulum počini, a onda zlo dobrim djelom zamijeni - pa, doista sam Ja Oprostitelj grijeha, Milostivi!", TextEnglish = "Otherwise, he who wrongs, then substitutes good after evil - indeed, I am Forgiving and Merciful.", TextGerman = "Wer aber Unrecht getan und hierauf nach Bösem Gutes eingetauscht hat, so bin Ich Allvergebend und Barmherzig." });
                        surah27.VerseList.Add(new Verse { Number = 12, TextBosnian = "Uvuci ruku svoju u njedra svoja, pojavit će se bijela, ali neće biti bolesna - bit će jedan od devet znakova faraonu i narodu njegovu; oni su, doista, bili narod buntovni.'", TextEnglish = "And put your hand into the opening of your garment [at the breast]; it will come out white without disease. [These are] among the nine signs [you will take] to Pharaoh and his people. Indeed, they have been a people defiantly disobedient.'", TextGerman = "Und stecke deine Hand in deinen Brustschlitz, so kommt sie weiß, jedoch nicht von Übel befallen, heraus. (Dies zählt) zu neun Zeichen, (gerichtet) an Fir’aun und sein Volk. Gewiß, sie sind ein Volk von Frevlern." });
                        surah27.VerseList.Add(new Verse { Number = 13, TextBosnian = "I kad im jasno dođoše znakovi Naši, oni rekoše: 'Ovo je prava čarolija!'", TextEnglish = "But when there came to them Our visible signs, they said, 'This is obvious magic.'", TextGerman = "Als dann Unsere Zeichen offenkundig sichtbar zu ihnen kamen, sagten sie: „Das ist deutliche Zauberei." });
                        surah27.VerseList.Add(new Verse { Number = 14, TextBosnian = "I oni ih, čineći zulum i oholeći se porekoše, ali su dušama svojim vjerovali da su istiniti, pa pogledaj kako su skončali smutljivci.", TextEnglish = "And they rejected them, while their [inner] selves were convinced thereof, out of injustice and haughtiness. So see how was the end of the corrupters.", TextGerman = "Und sie verleugneten sie, obwohl sie selbst davon überzeugt waren, aus Ungerechtigkeit und Überheblichkeit. So schau, wie das Ende der Unheilstifter war." });
                        surah27.VerseList.Add(new Verse { Number = 15, TextBosnian = "Davudu i Sulejmanu znanje smo dali i oni su govorili: 'Hvala Allahu Koji nas je odlikovao nad mnogim Svojim robovima vjernicima!'", TextEnglish = "And We had certainly given to David and Solomon knowledge, and they said, 'Praise [is due] to Allāh, who has favored us over many of His believing servants.'", TextGerman = "Und Wir gaben bereits Dawud und Sulaiman Wissen. Und sie sagten: „(Alles) Lob gehört Allah, der uns vor vielen Seiner gläubigen Diener bevorzugt hat!" });
                        surah27.VerseList.Add(new Verse { Number = 16, TextBosnian = "I Sulejman naslijedi Davuda i reče: 'O ljudi, dato nam je da razumijevamo ptičije glasove i svašta nam je dato: ovo je, zaista, blagodat očita!'", TextEnglish = "And Solomon inherited David. He said, 'O people, we have been taught the language of birds, and we have been given from all things. Indeed, this is evident bounty.'", TextGerman = "Und Sulaiman beerbte Dawud und sagte„O ihr Menschen, uns ist die Sprache der Vögel gelehrt worden, und uns wurde von allem gegeben. Das ist wahrlich die deutliche Huld." });
                        surah27.VerseList.Add(new Verse { Number = 17, TextBosnian = "I Sulejmanu bijahu sakupljene vojske njegove, činili su ih džini i ljudi i ptice, i bili su u stroju vođeni,", TextEnglish = "And gathered for Solomon were his soldiers of the jinn and men and birds, and they were [marching] in rows", TextGerman = "Und versammelt wurden für Sulaiman seine Heerscharen - unter den Ginn, Menschen und Vögeln -, und so wurden sie in Reihen geordnet." });
                        surah27.VerseList.Add(new Verse { Number = 18, TextBosnian = "i kad stigoše do mravlje doline, jedan mrav reče: 'O mravi, ulazite u staništa svoja da vas ne pogaze Sulejman i vojske njegove, a da to i ne primijete!'", TextEnglish = "Until, when they came upon the valley of the ants, an ant said, 'O ants, enter your dwellings that you not be crushed by Solomon and his soldiers while they perceive not.'", TextGerman = "Als sie dann zum Ameisental kamen, sagte eine Ameise: „O ihr Ameisen, geht in eure Wohnungen hinein, damit euch ja nicht Sulaiman und seine Heerscharen niederwalzen, ohne daß sie merken." });
                        surah27.VerseList.Add(new Verse { Number = 19, TextBosnian = "I on se nasmiješi pa nasmija riječima njegovim i reče: 'Gospodaru moj, nadahni me da budem zahvalan na blagodati Tvojoj, koju si ukazao meni i roditeljima mojim, i da činim dobra djela na zadovoljstvo Tvoje, i uvedi me, milošću Svojom, među dobre robove Svoje!'", TextEnglish = "So [Solomon] smiled, amused at her speech, and said, 'My Lord, enable me[1073] to be grateful for Your favor which You have bestowed upon me and upon my parents and to do righteousness of which You approve. And admit me by Your mercy into [the ranks of] Your righteous servants.'[1074]", TextGerman = "Da lächelte er erheitert über ihre Worte und sagte: „Mein Herr, veranlasse mich, für Deine Gunst zu danken, die Du mir und meinen Eltern erwiesen hast, und rechtschaffen zu handeln, womit Du zufrieden bist. Und lasse mich durch Deine Barmherzigkeit eingehen in die Reihen Deiner rechtschaffenen Diener." });
                        surah27.VerseList.Add(new Verse { Number = 20, TextBosnian = "I on izvrši smotru ptica, pa reče: 'Zašto ne vidim pupavca, da nije odsutan?", TextEnglish = "And he took attendance of the birds and said, 'Why do I not see the hoopoe - or is he among the absent?", TextGerman = "Und er schaute bei den Vögeln nach. Da sagte er: „Wie kommt es, daß ich den Wiedehopf nicht sehe? Befindet er sich etwa unter den Abwesenden?" });
                        surah27.VerseList.Add(new Verse { Number = 21, TextBosnian = "Žestokoj ću ga patnji podvrgnuti ili ću ga zaklati, ili će mi jasan argument opravdanja donijeti!'", TextEnglish = "I will surely punish him with a severe punishment or slaughter him unless he brings me clear authorization.'[1075]", TextGerman = "Ich werde ihn ganz gewiß strengstens strafen, oder ich werde ihn ganz gewiß hinrichten (lassen), es sei denn, er bringt mir fürwahr eine deutliche Ermächtigung." });
                        surah27.VerseList.Add(new Verse { Number = 22, TextBosnian = "Izbivao je nedugo, a onda dođe, pa reče: 'Doznao sam ono što ti ne znaš, iz Sabe ti donosim pouzdanu vijest.", TextEnglish = "But he [i.e., the hoopoe] stayed not long and said, 'I have encompassed [in knowledge] that which you have not encompassed, and I have come to you from Sheba with certain news.", TextGerman = "Aber er blieb nicht lange aus. Er sagte: „Ich habe (an Wissen) erfaßt, was du nicht erfaßt hast. Und ich bringe dir aus Saba sichere Kunde." });
                        surah27.VerseList.Add(new Verse { Number = 23, TextBosnian = "Vidio sam da jedna žena njima vlada i da joj je svega i svačega dato, a ima i prijesto veličanstveni,", TextEnglish = "Indeed, I found [there] a woman ruling them, and she has been given of all things, and she has a great throne.", TextGerman = "Gewiß, ich habe herausgefunden, daß eine Frau über sie herrscht, daß ihr von allem gegeben worden ist und daß sie einen gewaltigen Thron hat." });
                        surah27.VerseList.Add(new Verse { Number = 24, TextBosnian = "vidio sam da se i ona i narod njen Suncu klanjaju, a ne Allahu, šejtan im je prikazivao lijepim postupke njihove i od pravog puta ih odvratio, te se oni ne napućuju Pravim putem,", TextEnglish = "I found her and her people prostrating to the sun instead of Allāh, and Satan has made their deeds pleasing to them and averted them from [His] way, so they are not guided,", TextGerman = "Ich habe herausgefunden, daß sie und ihr Volk sich vor der Sonne niederwerfen, anstatt vor Allah. Und der Satan hat ihnen ihre Taten ausgeschmückt und sie dann vom Weg abgehalten, so daß sie nicht rechtgeleitet sind," });
                        surah27.VerseList.Add(new Verse { Number = 25, TextBosnian = "da sedždu čine Allahu, Koji izvodi ono što je skriveno na nebesima i na Zemlji i Koji zna ono što krijete i ono što na javu iznosite.", TextEnglish = "[And] so they do not prostrate to Allāh, who brings forth what is hidden within the heavens and the earth and knows what you conceal and what you declare -", TextGerman = "(dies), damit sie sich nicht vor Allah niederwerfen, Der das Versteckte in den Himmeln und auf der Erde herausbringt und weiß, was ihr verbergt, und was ihr offenlegt." });
                        surah27.VerseList.Add(new Verse { Number = 26, TextBosnian = "Allah je, nema boga osim Njega, Gospodar Arša Veličanstvenoga!'", TextEnglish = "Allāh - there is no deity except Him, Lord of the Great Throne.'", TextGerman = "Allah - es gibt keinen Gott außer Ihm, dem Herrn des gewaltigen Thrones." });
                        surah27.VerseList.Add(new Verse { Number = 27, TextBosnian = "Vidjet ćemo', reče Sulejman, 'da li govoriš istinu ili ne.", TextEnglish = "[Solomon] said, 'We will see whether you were truthful or were of the liars.", TextGerman = "Er sagte: „Wir werden schauen, ob du die Wahrheit sagst oder ob du zu den Lügnern gehörst." });
                        surah27.VerseList.Add(new Verse { Number = 28, TextBosnian = "Odnesi ovo moje pismo pa im ga baci, a onda se od njih malo izmakni i pogledaj šta će odgovoriti!'", TextEnglish = "Take this letter of mine and deliver it to them. Then leave them and see what [answer] they will return.'", TextGerman = "Geh mit diesem meinem Schreiben, überbringe es ihnen und kehre dich hierauf von ihnen ab. Dann schau, was sie erwidern." });
                        surah27.VerseList.Add(new Verse { Number = 29, TextBosnian = "O velikaši', reče ona, 'meni je dostavljeno jedno poštovanja vrijedno pismo!", TextEnglish = "She said, 'O eminent ones, indeed, to me has been delivered a noble letter.", TextGerman = "Sie sagte: „O ihr führende Schar, mir ist ein edles Schreiben zugeworfen worden." });
                        surah27.VerseList.Add(new Verse { Number = 30, TextBosnian = "Zbilja je od Sulejmana i zbilja je ono: 'S imenom Allaha, Svemilosnog, Milostivog!", TextEnglish = "Indeed, it is from Solomon, and indeed, it is [i.e., reads]: 'In the name of Allāh, the Entirely Merciful, the Especially Merciful,", TextGerman = "Gewiß, es ist von Sulaiman, und es lautet: ,Im Namen Allahs, des Allerbarmers, des Barmherzigen." });
                        surah27.VerseList.Add(new Verse { Number = 31, TextBosnian = "Ne pravite se većim od mene i dođite mi kao muslimani.'", TextEnglish = "Be not haughty with me but come to me in submission [as Muslims].''", TextGerman = "Seid mir gegenüber nicht überheblich und kommt als (Allah) Ergebene zu mir’." });
                        surah27.VerseList.Add(new Verse { Number = 32, TextBosnian = "O velikaši', reče ona, 'savjetujte mi šta treba u ovom slučaju raditi; ja bez vas ništa neću odlučiti!'", TextEnglish = "She said, 'O eminent ones, advise me in my affair. I would not decide a matter until you witness [for] me.'[1076]", TextGerman = "Sie sagte: „O ihr führende Schar, gebt mir eure Meinung über meine Angelegenheit bekannt; ich pflege ja keine Angelegenheit zu entscheiden, solange ihr nicht bei mir anwesend seid." });
                        surah27.VerseList.Add(new Verse { Number = 33, TextBosnian = "Mi smo vrlo jaki i hrabri', rekoše oni, 'a ti odlučuješ. Pa, gledaj šta ćeš narediti!'", TextEnglish = "They said, 'We are men of strength and of great military might, but the command is yours, so see what you will command.'", TextGerman = "Sie sagten: „Wir besitzen eine Streitmacht und besitzen eine starke Gewalt, doch es steht dir zu, zu befehlen. So sieh zu, was du befehlen willst." });
                        surah27.VerseList.Add(new Verse { Number = 34, TextBosnian = "Kad carevi osvoje neki grad', reče ona, 'oni ga razore, a ugledne stanovnike njegove učine poniženim.' Eto, tako oni rade.", TextEnglish = "She said, 'Indeed kings - when they enter a city, they ruin it and render the honored of its people humbled. And thus do they do.", TextGerman = "Sie sagte: „Gewiß, wenn Könige eine Stadt betreten, verderben sie sie und machen die Mächtigen ihrer Bewohner zu Erniedrigten, und so handeln sie." });
                        surah27.VerseList.Add(new Verse { Number = 35, TextBosnian = "Poslat ću im jedan dar i vidjet ću sa čime će se izaslanici vratiti.'", TextEnglish = "But indeed, I will send to them a gift and see with what [reply] the messengers will return.'", TextGerman = "Gewiß, ich werde (Boten) zu ihnen senden mit einem Geschenk und dann abwarten, was (für eine Antwort) die Gesandten zurückbringen." });
                        surah27.VerseList.Add(new Verse { Number = 36, TextBosnian = "I kad izaslanik pred Sulejmana iziđe, ovaj mu reče: 'Zar meni da blago nudite?! Ono što je Allah dao bolje je od onoga što je dao vama. Vi se onome što vam se daruje radujete!", TextEnglish = "So when they came to Solomon, he said, 'Do you provide me with wealth? But what Allāh has given me is better than what He has given you. Rather, it is you who rejoice in your gift.", TextGerman = "Als er zu Sulaiman kam, sagte dieser : „Wollt ihr mich mit Besitz unterstützen? Aber das, was mir Allah gegeben hat, ist besser als das, was Er euch gegeben hat. Nein! Vielmehr seid ihr es, die ihr über euer Geschenk froh seid." });
                        surah27.VerseList.Add(new Verse { Number = 37, TextBosnian = "Vrati se njima! Mi ćemo dovesti vojske kojima se neće moći oduprijeti i istjerat ćemo ih iz Sabe ponižene i pokorene.", TextEnglish = "Return to them, for we will surely come to them with soldiers that they will be powerless to encounter, and we will surely expel them therefrom in humiliation, and they will be debased.'", TextGerman = "Kehr zu ihnen zurück. Wir werden ganz gewiß mit Heerscharen über sie kommen, denen sie nichts entgegenzusetzen haben. Und wir werden sie ganz gewiß erniedrigt daraus vertreiben, als Geringgeachtete." });
                        surah27.VerseList.Add(new Verse { Number = 38, TextBosnian = "O dostojanstvenici, ko će mi od vas donijeti njezin prijesto prije nego što mi dođu kao muslimani.'", TextEnglish = "[Solomon] said, 'O assembly [of jinn], which of you will bring me her throne before they come to me in submission?'", TextGerman = "Er sagte: „O ihr führende Schar, wer von euch bringt mir ihren Thron, bevor sie als (Allah) Ergebene zu mir kommen?" });
                        surah27.VerseList.Add(new Verse { Number = 39, TextBosnian = "Ja ću ti ga donijeti', reče Ifrit, jedan od džina, 'prije nego sa svog mjesta ustaneš, ja sam za to, zaista, snažan i pouzdan.'", TextEnglish = "A powerful one from among the jinn said, 'I will bring it to you before you rise from your place, and indeed, I am for this [task] strong and trustworthy.'", TextGerman = "Ein unüberwindlicher von den Ginn sagte: „Ich bringe ihn dir, bevor du dich von deiner Stelle erhebst. Und ich bin wahrlich stark (genug) dazu und vertrauenswürdig." });
                        surah27.VerseList.Add(new Verse { Number = 40, TextBosnian = "A ja ću ga donijeti', reče onaj koji je imao znanje iz Knjige 'prije nego što vratiš pogled svoj.' I kad Sulejman vidje da je prijesto već pred njega postavljen, uzviknu: 'Ovo je blagodat Gospodara moga, Koji me iskušava da li ću zahvalan ili nezahvalan biti. A ko je zahvalan - u svoju je korist zahvalan, a ko je nezahvalan - pa, Gospodar moj je neovisan i plemenit.", TextEnglish = "Said one who had knowledge from the Scripture, 'I will bring it to you before your glance returns to you.' And when [Solomon] saw it placed before him, he said, 'This is from the favor of my Lord to test me whether I will be grateful or ungrateful. And whoever is grateful - his gratitude is only for [the benefit of] himself. And whoever is ungrateful - then indeed, my Lord is Free of need and Generous.'", TextGerman = "Derjenige, der Wissen aus der Schrift hatte, sagte: „Ich bringe ihn dir, bevor dein Blick zu dir zurückkehrt. Als er ihn unbeweglich bei sich stehen sah, sagte er: „Dies ist von der Huld meines Herrn, damit Er mich prüft, ob ich dankbar oder undankbar bin. Wer dankbar ist, der ist nur zu seinem eigenen Vorteil dankbar; wer aber undankbar ist - so ist mein Herr unbedürftig und freigebig." });
                        surah27.VerseList.Add(new Verse { Number = 41, TextBosnian = "Promijenite izgled njezina prijestolja da vidimo hoće li se naputiti ili će biti među onima koji za uputom ne idu!'", TextEnglish = "He said, 'Disguise for her her throne; we will see whether she will be guided [to truth] or will be of those who is not guided.'", TextGerman = "Er sagte: „Macht ihr ihren Thron unkenntlich, wir wollen schauen, ob sie rechtgeleitet wird oder ob sie zu denjenigen gehört, die nicht rechtgeleitet werden." });
                        surah27.VerseList.Add(new Verse { Number = 42, TextBosnian = "I kad ona dođe, bi joj rečeno: 'Je li ovakav prijesto tvoj 'Kao da jest!', uzviknu ona. 'A nama je prije nego njoj dato znanje, i mi smo muslimani.'", TextEnglish = "So when she arrived, it was said [to her], 'Is your throne like this?' She said, '[It is] as though it was it.' [Solomon said], 'And we were given knowledge before her, and we have been Muslims [in submission to Allāh].", TextGerman = "Als sie kam, wurde (zu ihr) gesagt: „Ist dein Thron so (wie dieser hier)? Sie sagte: „Es ist so, als ob er es sei. (Sulaiman sagte:) „Und uns wurde schon davor das Wissen gegeben, und wir waren (Allah) ergeben." });
                        surah27.VerseList.Add(new Verse { Number = 43, TextBosnian = "A od ispravna vjerovanja spriječilo je ono što je, mimo Allaha, obožavala. Zaista je ona bila iz reda nevjerničkog naroda!", TextEnglish = "And that which she was worshipping other than Allāh had averted her [from submission to Him]. Indeed, she was from a disbelieving people.'", TextGerman = "Aber abgehalten (vom Glauben) hat sie das, dem sie anstatt Allahs diente, denn sie gehörte zu ungläubigen Leuten." });
                        surah27.VerseList.Add(new Verse { Number = 44, TextBosnian = "Uđi u dvorac!', bi joj rečeno. Kad ga ona pogleda, pomisli da je duboka voda, pa zadiže haljinu uz noge svoje. 'Ovaj je dvorac uglačanim staklom popločan!', reče on. 'Gospodaru moj!', uzviknu ona, 'ja sam prema sebi zulum učinila i u društvu sa Sulejmanom predajem se Allahu, Gospodaru svjetova!'", TextEnglish = "She was told, 'Enter the palace.' But when she saw it, she thought it was a body of water[1077] and uncovered her shins [to wade through]. He said, 'Indeed, it is a palace [whose floor is] made smooth with glass.' She said, 'My Lord, indeed I have wronged myself, and I submit with Solomon to Allāh, Lord of the worlds.'", TextGerman = "Es wurde zu ihr gesagt: „Tritt in den Prachtbau ein. Als sie ihn sah, hielt sie ihn für ein tiefes Wasser und entblößte ihre Unterschenkel. Er sagte: „Es ist ein mit Glas ausgelegter Prachtbau. Sie sagte: „Mein Herr, ich habe mir selbst Unrecht zugefügt, aber ich ergebe mich (nun), zusammen mit Sulaiman, Allah, dem Herrn der Weltenbewohner." });
                        surah27.VerseList.Add(new Verse { Number = 45, TextBosnian = "A narodu Semud poslali smo brata njihova Saliha: 'Ibadet činite jedino Allahu', a oni se podijeliše u dvije skupine koje su se međusobno prepirale.", TextEnglish = "And We had certainly sent to Thamūd their brother Ṣāliḥ, [saying], 'Worship Allāh,' and at once they were two parties conflicting.", TextGerman = "Und Wir sandten bereits zu den Tamud ihren Bruder Salih: „Dient Allah. Da waren es sogleich zwei Gruppen, die miteinander stritten." });
                        surah27.VerseList.Add(new Verse { Number = 46, TextBosnian = "O narode moj', govorio im je on, 'zašto tražite zlo prije dobra? Zašto od Allaha oprost ne tražite, ne bi li ste milost zadobili?'", TextEnglish = "He said, 'O my people, why are you impatient for evil before [i.e., instead of] good?[1078] Why do you not seek forgiveness of Allāh that you may receive mercy?'", TextGerman = "Er sagte: „O mein Volk, warum wünscht ihr, das Schlechte vor dem Guten zu beschleunigen? Würdet ihr doch Allah um Vergebung bitten, auf daß ihr Erbarmen finden möget!" });
                        surah27.VerseList.Add(new Verse { Number = 47, TextBosnian = "Mi zloslutnju nalazimo u tebi i onima koji su s tobom!', rekoše oni. 'Zloslutnja vaša kod Allaha je', reče on, 'vi ste narod koji je stavljen u iskušenje.'", TextEnglish = "They said, 'We consider you a bad omen, you and those with you.' He said, 'Your omen [i.e., fate] is with Allāh. Rather, you are a people being tested.'[1079]", TextGerman = "Sie sagten: „Wir sehen ein böses Vorzeichen in dir und in denjenigen, die mit dir sind. Er sagte: „Euer Vorzeichen ist bei Allah. Nein! Vielmehr seid ihr Leute, die der Versuchung ausgesetzt werden." });
                        surah27.VerseList.Add(new Verse { Number = 48, TextBosnian = "U gradu je bilo devet osoba koje su po zemlji, ne red, nego nered činile.", TextEnglish = "And there were in the city nine family heads causing corruption in the land and not amending [its affairs].", TextGerman = "Nun befanden sich in der Stadt neun Mitglieder einer Sippschaft, die auf der Erde Unheil stifteten und keine Besserung brachten." });
                        surah27.VerseList.Add(new Verse { Number = 49, TextBosnian = "Allahom se zakunite', rekoše, 'da ćemo noću i njega i porodicu njegovu ubiti, a onda njegovom staratelju reći: 'Mi nismo prisustvovali pogibiji porodice njegove, mi, zaista, istinu govorimo.''", TextEnglish = "They said, 'Take a mutual oath by Allāh that we will kill him by night, he and his family. Then we will say to his executor,[1080] 'We did not witness the destruction of his family, and indeed, we are truthful.''", TextGerman = "Sie sagten: „Schwört einander bei Allah: Wir werden ganz gewiß ihn und seine Angehörigen nachts überfallen, und hierauf werden wir zu seinem nächsten Verwandten sagen: ,Wir waren bei der Vernichtung seiner Angehörigen nicht zugegen, und wir sagen ganz gewiß die Wahrheit’." });
                        surah27.VerseList.Add(new Verse { Number = 50, TextBosnian = "I spleli su spletku, pa smo i Mi spleli njima spletku, a oni to nisu osjećali,", TextEnglish = "And they planned a plan, and We planned a plan, while they perceived not.", TextGerman = "Sie schmiedeten Ränke, und Wir schmiedeten Ränke, ohne daß sie merkten." });
                        surah27.VerseList.Add(new Verse { Number = 51, TextBosnian = "pa pogledaj kakva je bila posljedica spletkarenja njihova: uništili smo sve- i njih i narod njihov.", TextEnglish = "Then look how was the outcome of their plan - that We destroyed them and their people, all.", TextGerman = "Schau nur, wie die Folge ihrer Ränke war: Wir zerstörten sie und ihr Volk allesamt." });
                        surah27.VerseList.Add(new Verse { Number = 52, TextBosnian = "Eno kuća njihovih, puste su zbog zuluma koji su činili - u tome je, zaista, znak narodu koji zna,", TextEnglish = "So those are their houses, desolate because of the wrong they had done. Indeed in that is a sign for people who know.", TextGerman = "Dies sind nun ihre Häuser, wüst dafür, daß sie Unrecht taten. Darin ist wahrlich ein Zeichen für Leute, die Bescheid wissen." });
                        surah27.VerseList.Add(new Verse { Number = 53, TextBosnian = "a spasili smo one koji su vjerovali i još su se nastojali uščuvati.", TextEnglish = "And We saved those who believed and used to fear Allāh.", TextGerman = "Und Wir retteten diejenigen, die glaubten und gottesfürchtig waren." });
                        surah27.VerseList.Add(new Verse { Number = 54, TextBosnian = "I Luta smo poslali kada reče narodu svome: 'Zar činite razvrat i pritom jedni druge vidite?!", TextEnglish = "And [mention] Lot, when he said to his people, 'Do you commit immorality[1081] while you are seeing?[1082]", TextGerman = "Und (Wir sandten) Lut. Als er zu seinem Volk sagte: „Wollt ihr denn sehend(en Auges) das Abscheuliche begehen?" });
                        surah27.VerseList.Add(new Verse { Number = 55, TextBosnian = "Zar vi zbilja strasno prilazite muškarcima umjesto ženama?! Štaviše, vi ste narod koji ne zna!'", TextEnglish = "Do you indeed approach men with desire instead of women? Rather, you are a people behaving ignorantly.'", TextGerman = "Laßt ihr euch denn wahrlich in Begierde mit den Männern ein anstatt mit den Frauen? Aber nein! Ihr seid Leute, die töricht sind." });
                        surah27.VerseList.Add(new Verse { Number = 56, TextBosnian = "A odgovor naroda njegova samo je glasio: 'Istjerajte Lutovu porodicu iz grada vašeg, oni su, zaista, ljudi koji se od toga ustežu!'", TextEnglish = "But the answer of his people was not except that they said, 'Expel the family of Lot from your city. Indeed, they are people who keep themselves pure.'", TextGerman = "Doch die Antwort seines Volkes war nur, daß sie sagten: „Vertreibt die Sippe Luts aus eurer Stadt! Das sind Menschen, die sich rein halten." });
                        surah27.VerseList.Add(new Verse { Number = 57, TextBosnian = "Mi smo spasili njega i porodicu njegovu, osim žene njegove. Mi smo odredili da bude među onima koji su ostali i nastradali,", TextEnglish = "So We saved him and his family, except for his wife; We destined her to be of those who remained behind.", TextGerman = "Da retteten Wir ihn und seine Angehörigen, außer seiner Frau; Wir bestimmten, daß sie zu denjenigen gehörte, die zurückblieben." });
                        surah27.VerseList.Add(new Verse { Number = 58, TextBosnian = "i pustili smo na njih kamenu kišu, a strašne li kiše za one koji su bili opomenuti!", TextEnglish = "And We rained upon them a rain [of stones], and evil was the rain of those who were warned.", TextGerman = "Und Wir ließen einen Regen auf sie niedergehen; wie böse war der Regen der Gewarnten!" });
                        surah27.VerseList.Add(new Verse { Number = 59, TextBosnian = "Reci: 'Hvala Allahu i selam robovima Njegovim koje je On odabrao!' Šta je bolje: Allah ili oni koje Njemu u obožavanju pridružuju?", TextEnglish = "Say, [O Muḥammad], 'Praise be to Allāh, and peace upon His servants whom He has chosen. Is Allāh better or what they associate with Him?'", TextGerman = "Sag: (Alles) Lob gehört Allah, und Friede sei auf Seinen Dienern, die Er Sich auserwählt hat! Ist denn Allah besser oder das, was sie (Ihm) beigesellen?" });
                        surah27.VerseList.Add(new Verse { Number = 60, TextBosnian = "Onaj Koji je nebesa i Zemlju stvorio i Koji vam spušta s neba kišu pomoću koje Mi dajemo da ozelene bašče prekrasne, nemoguće je da vi učinite da izraste drveće njihovo. Zar pored Allaha postoji ijedan drugi bog?! Ne postoji, ali su oni narod koji druge s Njim izjednačuje.", TextEnglish = "[More precisely], is He [not best] who created the heavens and the earth and sent down for you rain from the sky, causing to grow thereby gardens of joyful beauty which you could not [otherwise] have grown the trees thereof? Is there a deity with Allāh?[1083] [No], but they are a people who ascribe equals [to Him].", TextGerman = "Oder (ist besser) Wer die Himmel und die Erde erschaffen hat und für euch vom Himmel Wasser herabkommen läßt, durch das Wir in Entzücken versetzende umfriedete Gärten wachsen lassen, deren Bäume unmöglich ihr wachsen lassen könntet. Gibt es denn einen (anderen) Gott neben Allah? Nein! Vielmehr sind sie Leute, die (Allah andere) gleichsetzen." });
                        surah27.VerseList.Add(new Verse { Number = 61, TextBosnian = "Onaj Koji je Zemlju prebivalištem učinio i kroz nju rijeke proveo i na njoj brda nepomična postavio i između dva mora učinio pregradu. Zar pored Allaha postoji ijedan drugi bog?! Ne postoji, nego većina njih ne zna.", TextEnglish = "Is He [not best] who made the earth a stable ground and placed within it rivers and made for it firmly set mountains and placed between the two seas a barrier? Is there a deity with Allāh? [No], but most of them do not know.", TextGerman = "Oder (ist besser) Wer die Erde zu einem festen Grund gemacht und Flüsse durch sie gemacht und festgegründete Berge (auf) ihr gemacht und zwischen den beiden Meeren eine Schranke gemacht hat? Gibt es denn einen (anderen) Gott neben Allah? Aber nein! Die meisten von ihnen wissen nicht." });
                        surah27.VerseList.Add(new Verse { Number = 62, TextBosnian = "Onaj Koji se nevoljniku, kad Mu se obrati, odaziva, i Koji zlo otklanja i Koji vas na Zemlji jedne drugima smjenjuje. Zar pored Allaha postoji ijedan drugi bog?! Tako se rijetko prisjećate i pouku primate!", TextEnglish = "Is He [not best] who responds to the desperate one when he calls upon Him and removes evil and makes you inheritors of the earth?[1084] Is there a deity with Allāh? Little do you remember.", TextGerman = "Oder (ist besser) Wer den in einer Notlage Befindlichen erhört, wenn er Ihn anruft, und das Böse hinwegnimmt und euch zu Nachfolgern (auf) der Erde macht? Gibt es denn einen (anderen) Gott neben Allah? Wie wenig ihr bedenkt!" });
                        surah27.VerseList.Add(new Verse { Number = 63, TextBosnian = "Onaj Koji vam u tminama, na kopnu i na moru, put pokazuje i Koji vjetrove kao radosnu vijest ispred milosti Svoje šalje. Zar pored Allaha postoji ijedan drugi bog?! Kako je Allah visoko iznad onih koje druge Njemu u obožavanju pridružuju!", TextEnglish = "Is He [not best] who guides you through the darknesses of the land and sea and who sends the winds as good tidings before His mercy? Is there a deity with Allāh? High is Allāh above whatever they associate with Him.", TextGerman = "Oder (ist besser) Wer euch in den Finsternissen des Festlandes und des Meeres (den rechten Weg) leitet und Wer die Winde als Frohboten Seiner Barmherzigkeit (voraus)sendet? Gibt es denn einen (anderen) Gott neben Allah? Erhaben ist Allah über das, was sie (Ihm) beigesellen." });
                        surah27.VerseList.Add(new Verse { Number = 64, TextBosnian = "Onaj Koji sve iz ničega stvara, Koji će zatim to ponovo učiniti, i Koji vam opskrbu s neba i iz zemlje daje. Zar pored Allaha postoji ijedan drugi bog?! Reci: 'Dokažite, ako istinu govorite!'", TextEnglish = "Is He [not best] who begins creation and then repeats it and who provides for you from the heaven and earth? Is there a deity with Allāh? Say, 'Produce your proof, if you should be truthful.'", TextGerman = "Oder (ist besser) Wer die Schöpfung am Anfang macht und sie hierauf wiederholt und Wer euch vom Himmel und von der Erde versorgt? Gibt es denn einen (anderen) Gott neben Allah? Sag: Bringt euren Beweis vor, wenn ihr wahrhaftig seid." });
                        surah27.VerseList.Add(new Verse { Number = 65, TextBosnian = "Reci: 'Niko, osim Allaha, ni na nebu ni na Zemlji, ne zna ono što je čulima nedokučivo; i oni ne znaju kada će oživljeni biti.'", TextEnglish = "Say, 'None in the heavens and earth knows the unseen except Allāh, and they do not perceive when they will be resurrected.'", TextGerman = "Sag: Über das Verborgene weiß nicht Bescheid wer in den Himmeln und auf der Erde ist, außer Allah; und sie merken nicht, wann sie auferweckt werden." });
                        surah27.VerseList.Add(new Verse { Number = 66, TextBosnian = "Štaviše, oni o ahiretu ništa ne znaju! Oni u njega još i sumnjaju, pa čak su i slijepi u vezi s tim!", TextEnglish = "Rather, their knowledge is arrested concerning the Hereafter. Rather, they are in doubt about it. Rather, they are, concerning it, blind.", TextGerman = "Nein! Vielmehr hat ihr Wissen über das Jenseits versagt. Nein! Vielmehr sind sie darüber im Zweifel. Nein! Vielmehr sind sie ihm gegenüber blind." });
                        surah27.VerseList.Add(new Verse { Number = 67, TextBosnian = "Nevjernici govore: 'Zar ćemo, kad postanemo zemlja, i mi i preci naši, zaista, biti oživljeni?", TextEnglish = "And those who disbelieve say, 'When we have become dust as well as our forefathers, will we indeed be brought out [of the graves]?", TextGerman = "Und diejenigen, die ungläubig sind, sagen: „Wenn wir zu Erde geworden sind, und (auch) unsere Väter, sollen wir denn dann wirklich wieder hervorgebracht werden?" });
                        surah27.VerseList.Add(new Verse { Number = 68, TextBosnian = "Ovo nam se već odavno obećava, i nama i precima našim, a ovo su samo izmišljotine naroda drevnih.'", TextEnglish = "We have been promised this, we and our forefathers, before. This is not but legends of the former peoples.'", TextGerman = "Dies ist zuvor bereits uns und unseren Vätern versprochen worden. Das sind nur Fabeln der Früheren." });
                        surah27.VerseList.Add(new Verse { Number = 69, TextBosnian = "Reci: 'Putujte po svijetu i vidite kako su završili prestupnici!'", TextEnglish = "Say, [O Muḥammad], 'Proceed [i.e., travel] through the land and observe how was the end of the criminals.'", TextGerman = "Sag: Reist auf der Erde umher und schaut, wie das Ende der Übeltäter war." });
                        surah27.VerseList.Add(new Verse { Number = 70, TextBosnian = "I ne žalosti se zbog njih i neka ti nije u duši teško zbog spletki njihovih.", TextEnglish = "And grieve not over them or be in distress from what they conspire.", TextGerman = "Sei nicht traurig über sie, und sei nicht in Beklommenheit wegen der Ränke, die sie schmieden." });
                        surah27.VerseList.Add(new Verse { Number = 71, TextBosnian = "Kad će se već jednom obistiniti to obećanje, ako istinu govorite?', pitaju oni.", TextEnglish = "And they say, 'When is [the fulfillment of] this promise, if you should be truthful?'", TextGerman = "Und sie sagen: „Wann wird dieses Versprechen eintreten, wenn ihr wahrhaftig seid?" });
                        surah27.VerseList.Add(new Verse { Number = 72, TextBosnian = "Reci: 'Stići će vas sigurno nešto od onoga što požurujete!'", TextEnglish = "Say, 'Perhaps it is close behind you [i.e., very near] - some of that for which you are impatient.", TextGerman = "Sag: Vielleicht ist dicht hinter euch einiges von dem, was ihr zu beschleunigen wünscht." });
                        surah27.VerseList.Add(new Verse { Number = 73, TextBosnian = "A Gospodar tvoj je neizmjerno dobar prema ljudima, ali većina njih nije zahvalna.", TextEnglish = "And indeed, your Lord is the possessor of bounty for the people, but most of them are not grateful.'", TextGerman = "Dein Herr ist wahrlich voll Huld gegen die Menschen. Aber die meisten von ihnen sind nicht dankbar." });
                        surah27.VerseList.Add(new Verse { Number = 74, TextBosnian = "Gospodar tvoj dobro zna ono što grudi njihove taje i ono što oni na javu iznose.", TextEnglish = "And indeed, your Lord knows what their breasts conceal and what they declare.", TextGerman = "Und dein Herr weiß fürwahr, was ihre Brüste verhehlen und was sie offenlegen." });
                        surah27.VerseList.Add(new Verse { Number = 75, TextBosnian = "Nema ničeg skrivenog ni na nebu ni na Zemlji, a da nije u Knjizi jasnoj.", TextEnglish = "And there is nothing concealed[1085] within the heaven and the earth except that it is in a clear Register.[1086]", TextGerman = "Und es gibt nichts Verborgenes im Himmel und auf der Erde, das nicht in einem deutlichen Buch (verzeichnet) wäre." });
                        surah27.VerseList.Add(new Verse { Number = 76, TextBosnian = "Ovaj Kur'an sinovima Israilovim kazuje najviše o onome u čemu se oni razilaze,", TextEnglish = "Indeed, this Qur’ān relates to the Children of Israel most of that over which they disagree.", TextGerman = "Gewiß, dieser Qur’an erzählt den Kindern Isra’ils das meiste von dem, worüber sie uneinig sind." });
                        surah27.VerseList.Add(new Verse { Number = 77, TextBosnian = "i on je, uistinu, uputa i milost vjernicima.", TextEnglish = "And indeed, it is guidance and mercy for the believers.", TextGerman = "Und es ist wahrlich eine Rechtleitung und Barmherzigkeit für die Gläubigen." });
                        surah27.VerseList.Add(new Verse { Number = 78, TextBosnian = "Gospodar tvoj će im po pravdi Svojoj presuditi; On je Silni i Sveznajući,", TextEnglish = "Indeed, your Lord will judge between them by His [wise] judgement. And He is the Exalted in Might, the Knowing.", TextGerman = "Gewiß, dein Herr wird zwischen ihnen durch Sein Urteil entscheiden. Und Er ist der Allmächtige und Allwissende." });
                        surah27.VerseList.Add(new Verse { Number = 79, TextBosnian = "zato se pouzdaj u Allaha, jer ti, doista, slijediš Jasnu Istinu!", TextEnglish = "So rely upon Allāh; indeed, you are upon the clear truth.", TextGerman = "So verlasse dich auf Allah, denn du verfährst nach der offenkundigen Wahrheit." });
                        surah27.VerseList.Add(new Verse { Number = 80, TextBosnian = "Ti ne možeš mrtve dozvati niti gluhe dovikati kada se okrenu udaljavajući se,", TextEnglish = "Indeed, you will not make the dead hear, nor will you make the deaf hear the call when they have turned their backs retreating.", TextGerman = "Du kannst weder die Toten hören lassen noch die Tauben den Ruf hören lassen, wenn sie den Rücken kehren." });
                        surah27.VerseList.Add(new Verse { Number = 81, TextBosnian = "niti možeš uputiti slijepe i iz zablude ih njihove izbaviti; možeš jedino dozvati one koji u ajete Naše vjeruju. Oni su predani!", TextEnglish = "And you cannot guide the blind away from their error. You will only make hear those who believe in Our verses so they are Muslims [i.e., submitting to Allāh].", TextGerman = "Noch kannst du die Blinden aus ihrem Irrtum heraus rechtleiten. Hören lassen kannst du nur, wer an Unsere Zeichen glaubt und die (Uns) somit ergeben sind." });
                        surah27.VerseList.Add(new Verse { Number = 82, TextBosnian = "I kada se ostvare riječi o kazni njihovoj, Mi ćemo učiniti da iz zemlje iziđe jedna životinja koja će im reći da ljudi u znakove i ajete Naše nisu bili uvjereni.", TextEnglish = "And when the word [i.e., decree] befalls them,[1087] We will bring forth for them a creature from the earth speaking to them, [saying] that the people were, of Our verses, not certain [in faith].", TextGerman = "Und wenn das Wort über sie fällig wird, bringen Wir ihnen ein Tier aus der Erde hervor, das zu ihnen spricht; (dies), weil die Menschen von Unseren Zeichen nicht überzeugt sind." });
                        surah27.VerseList.Add(new Verse { Number = 83, TextBosnian = "A na Dan kada od svakog naroda sakupimo gomilu onih koji su ajete i znakove Naše poricali - oni će biti u stroju vođeni.", TextEnglish = "And [warn of] the Day when We will gather from every nation a company of those who deny Our signs, and they will be [driven] in rows", TextGerman = "Und (denke an) den Tag, da Wir aus jeder Gemeinschaft eine Schar von denjenigen versammeln werden, die Unsere Zeichen für Lüge erklären, und sie dann in Reihen geordnet werden." });
                        surah27.VerseList.Add(new Verse { Number = 84, TextBosnian = "A kad dođu, On će upitati: 'Jeste li vi ajete i znakove Moje poricali i niste ih znanjem obuhvatili, ili šta ste to radili?'", TextEnglish = "Until, when they arrive [at the place of Judgement], He will say, 'Did you deny My signs while you encompassed them not in knowledge, or what [was it that] you were doing?'", TextGerman = "Wenn sie dann kommen, sagt Er: „Habt ihr denn Meine Zeichen für Lüge erklärt und davon kein umfassendes Wissen gehabt, oder was habt ihr denn sonst getan?" });
                        surah27.VerseList.Add(new Verse { Number = 85, TextBosnian = "I riječi o kazni njima namijenjenoj ostvarit će se zato što su zulumćari bili, pa neće moći ni riječi izustiti.", TextEnglish = "And the decree will befall them[1088] for the wrong they did, and they will not [be able to] speak.", TextGerman = "Und das Wort wird über sie fällig werden dafür, daß sie Unrecht taten, und da werden sie nicht reden." });
                        surah27.VerseList.Add(new Verse { Number = 86, TextBosnian = "Zar nisu vidjeli da smo učinili noć da u njoj otpočinu, a dan vidnim?! Tu su, zaista, znakovi za narod koji vjeruje.", TextEnglish = "Do they not see that We made the night that they may rest therein and the day giving sight? Indeed in that are signs for a people who believe.", TextGerman = "Sehen sie nicht, daß Wir die Nacht gemacht haben, damit sie in ihr ruhen, und den Tag hell? Darin sind wahrlich Zeichen für Leute, die glauben." });
                        surah27.VerseList.Add(new Verse { Number = 87, TextBosnian = "A na Dan kad se u rog puhne pa se smrtno istrave i oni na nebesima i oni na Zemlji, izuzev onih koje Allah bude htio da poštedi, svi će Mu ponizno doći.", TextEnglish = "And [warn of] the Day the Horn will be blown, and whoever is in the heavens and whoever is on the earth will be terrified except whom Allāh wills. And all will come to Him humbled.", TextGerman = "Und (denke an) den Tag, da ins Horn geblasen wird und da (all) diejenigen erschrecken, die in den Himmeln und die auf der Erde sind, außer wer Allah will. Und alle kommen in Demut zu Ihm." });
                        surah27.VerseList.Add(new Verse { Number = 88, TextBosnian = "I Ti vidiš planine i misliš da su nepomične, a one promiču kao što promiču oblaci, to je djelo Allahovo - Koji je sve savršeno stvorio; On, doista, u potpunosti zna ono što radite.", TextEnglish = "And you see the mountains, thinking them motionless,[1089] while they will pass as the passing of clouds. [It is] the work of Allāh, who perfected all things. Indeed, He is Aware of that which you do.", TextGerman = "Und du siehst die Berge, von denen du meinst, sie seien unbeweglich, während sie wie Wolken vorbeiziehen. - (Es ist) das Werk Allahs, Der alles sehr geschickt gemacht hat. Gewiß, Er ist Kundig dessen, was ihr tut." });
                        surah27.VerseList.Add(new Verse { Number = 89, TextBosnian = "Oni koji učine dobro djelo, imat će bolje od toga i bit će straha na Sudnjem danu pošteđeni,", TextEnglish = "Whoever comes [at Judgement] with a good deed will have better than it, and they, from the terror of that Day, will be safe.", TextGerman = "Wer mit einer guten Tat kommt, wird etwas Besseres als sie erhalten. Und sie sind vor (jedem) Schrecken an jenem Tag in Sicherheit." });
                        surah27.VerseList.Add(new Verse { Number = 90, TextBosnian = "a oni koji budu zlo činili, u Vatru će naglavačke biti gurnuti. 'Pa zar bivate kažnjeni osim za ono što ste radili?!'", TextEnglish = "And whoever comes with an evil deed[1090] - their faces will be overturned into the Fire, [and it will be said], 'Are you recompensed except for what you used to do?'", TextGerman = "Wer aber mit einer schlechten Tat kommt, - diese werden mit ihren Gesichtern (voran) ins (Höllen)feuer gestürzt: „Wird euch denn etwas anderes vergolten als das, was ihr zu tun pflegtet?" });
                        surah27.VerseList.Add(new Verse { Number = 91, TextBosnian = "Ja sam primio zapovijest da ibadet činim jedino Gospodaru ovoga grada, Koji ga je zabranio skrnaviti - a Njemu sve pripada - i naređeno mi je da iz reda muslimana - Allahu predanih budem.", TextEnglish = "[Say, O Muḥammad], 'I have only been commanded to worship the Lord of this city,[1091] who made it sacred and to whom [belongs] all things. And I am commanded to be of the Muslims [i.e., those who submit to Allāh].", TextGerman = "Mir ist nur befohlen worden, dem Herrn dieser Ortschaft zu dienen, Der sie geschützt hat und Dem alles gehört. Und mir ist befohlen worden, einer der (Ihm) Ergebenen zu sein" });
                        surah27.VerseList.Add(new Verse { Number = 92, TextBosnian = "i da Kur'an učim; onaj ko bude išao Pravim putem na Pravom putu je za svoje dobro, a onome koji je u zabludi ti reci: 'Ja sam samo iz reda upozoritelja.'", TextEnglish = "And to recite the Qur’ān.' And whoever is guided is only guided for [the benefit of] himself; and whoever strays - say, 'I am only [one] of the warners.'", TextGerman = "und den Qur’an zu verlesen. Wer sich nun rechtleiten läßt, der ist nur zu seinem eigenen Vorteil rechtgeleitet. Und wenn einer irregeht, dann sag: Ich gehöre ja nur zu den Überbringern von Warnungen." });
                        surah27.VerseList.Add(new Verse { Number = 93, TextBosnian = "I reci: 'Hvala Allahu, On će vam znakove Svoje pokazati, pa ćete ih vi poznati!' A Gospodar tvoj nije nemaran spram onog što radite.", TextEnglish = "And say, '[All] praise is [due] to Allāh. He will show you His signs, and you will recognize them. And your Lord is not unaware of what you do.'", TextGerman = "Und sag: (Alles) Lob gehört Allah! Er wird euch Seine Zeichen zeigen, und dann werdet ihr sie erkennen. Allah ist nicht unachtsam dessen, was ihr tut." });
                        result = surah27;
                        break;
                    #endregion

                    // 
                    #region
                    case 28:
                        Surah surah28 = new Surah(); surah28.Name = Constants.Surah28Name; surah28.Number = 28; surah28.VerseList = new List<Verse>();
                        surah28.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah28.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ta-sin-mim,", TextEnglish = "Ṭā, Seen, Meem.[1093]", TextGerman = "Ta-Sin-Mim." });
                        surah28.VerseList.Add(new Verse { Number = 2, TextBosnian = "Ovo su ajeti Knjige jasne!", TextEnglish = "These are verses of the clear Book.", TextGerman = "Dies sind die Zeichen des deutlichen Buches." });
                        surah28.VerseList.Add(new Verse { Number = 3, TextBosnian = "Mi ćemo ti po Istini kazati neke vijesti o Musau i faraonu, i to za one ljude koji vjeruju.", TextEnglish = "We recite to you from the news of Moses and Pharaoh in truth for a people who believe.", TextGerman = "Wir verlesen dir von der Kunde über Musa und Fir’aun der Wahrheit entsprechend, für Leute, die glauben." });
                        surah28.VerseList.Add(new Verse { Number = 4, TextBosnian = "Zaista se faraon u zemlji uznosio, i od stanovnika njenih načinio poklonike raznovrsne; jednu zajednicu je tlačio mušku im djecu klao, a žensku u životu ostavljao, doista je jedan od remetitelja poretka bio.", TextEnglish = "Indeed, Pharaoh exalted himself in the land and made its people into factions, oppressing a sector among them, slaughtering their [newborn] sons and keeping their females alive. Indeed, he was of the corrupters.", TextGerman = "Gewiß, Fir’aun zeigte sich überheblich im Land und machte seine Bewohner zu Lagern, von denen er einen Teil unterdrückte, indem er ihre Söhne abschlachtete und (nur) ihre Frauen am Leben ließ. Gewiß, er gehörte zu den Unheilstiftern." });
                        surah28.VerseList.Add(new Verse { Number = 5, TextBosnian = "A Mi smo htjeli da one koji su na zemlji tlačeni obdarimo i da ih vođama i nasljednicima učinimo.", TextEnglish = "And We wanted to confer favor upon those who were oppressed in the land and make them leaders and make them inheritors", TextGerman = "Wir aber wollten denjenigen, die im Land unterdrückt wurden, eine Wohltat erweisen und sie zu Vorbildern machen und zu Erben machen," });
                        surah28.VerseList.Add(new Verse { Number = 6, TextBosnian = "I da im omogućimo da zemljom upravljaju, a da faraonu i Hamanu i vojskama njihovim učinimo da od njih dožive baš ono čega su se pribojavali.", TextEnglish = "And establish them in the land and show Pharaoh and [his minister] Hāmān and their soldiers through them[1094] that which they had feared.", TextGerman = "ihnen eine feste Stellung im Land verleihen und Fir’aun, Haman und deren Heerscharen durch sie das erfahren lassen, wovor sie sich immer vorzusehen suchten." });
                        surah28.VerseList.Add(new Verse { Number = 7, TextBosnian = "I mi nadahnusmo Musaovu majku: 'Doji ga, a kad se uplašiš za njegov život, baci ga u rijeku, i ne strahuj i ne tuguj, Mi ćemo ti ga, doista, vratiti i jednim od poslanika ga učiniti.'", TextEnglish = "And We inspired to the mother of Moses, 'Suckle him; but when you fear for him, cast him into the river and do not fear and do not grieve. Indeed, We will return him to you and will make him [one] of the messengers.'", TextGerman = "Und Wir gaben der Mutter Musas ein: „Stille ihn. Und wenn du um ihn fürchtest, dann setze ihn in das Wasser und fürchte dich nicht und sei nicht traurig. Wir werden ihn dir zurückbringen und ihn zu einem der Gesandten machen." });
                        surah28.VerseList.Add(new Verse { Number = 8, TextBosnian = "I izvadiše ga ukućani faraonovi, da im postane dušmanin i jad; zaista su faraon i Haman i vojske njihove bili oni koji griješe.", TextEnglish = "And the family of Pharaoh picked him up [out of the river] so that he would become to them an enemy and a [cause of] grief. Indeed, Pharaoh and Hāmān and their soldiers were deliberate sinners.", TextGerman = "Da lasen ihn die Angehörigen Fir’auns auf, damit er ihnen zum Feind und (Grund zum) Kummer werde. Gewiß, Fir’aun, Haman und deren Heerscharen pflegten verfehlt zu handeln." });
                        surah28.VerseList.Add(new Verse { Number = 9, TextBosnian = "I žena faraonova reče: 'On će biti radost i meni i tebi! Ne ubijte ga, možda će nam od koristi biti, a možemo ga i posiniti'; a oni ništa ne predosjetiše.", TextEnglish = "And the wife of Pharaoh said, '[He will be] a comfort of the eye [i.e., pleasure] for me and for you. Do not kill him; perhaps he may benefit us, or we may adopt him as a son.' And they perceived not.[1095]", TextGerman = "Die Frau Fir’auns sagte: „(Er wird) für mich und dich ein Grund zur Freude sein. Tötet ihn nicht. Vielleicht nützt er uns oder wir nehmen ihn als Kind an. Dabei merkten sie nicht (, was sie taten)." });
                        surah28.VerseList.Add(new Verse { Number = 10, TextBosnian = "I srce Musaove majke postade prazno, i gotovo bi ga prokazala da Mi srce njeno nismo učvrstili i vjernicom je učinili.", TextEnglish = "And the heart of Moses' mother became empty [of all else]. She was about to disclose [the matter concerning] him had We not bound fast her heart that she would be of the believers.", TextGerman = "Und das Herz der Mutter Musas wurde leer. Beinahe hätte sie ihn fürwahr offen bekanntgegeben, wenn Wir nicht ihr Herz gestärkt hätten, damit sie zu den Gläubigen gehöre." });
                        surah28.VerseList.Add(new Verse { Number = 11, TextBosnian = "I ona reče sestri njegovoj: 'Idi za njim!' I ona ga je gledala izdaleka, a oni nisu ništa primijetili.", TextEnglish = "And she said to his sister, 'Follow him'; so she watched him from a distance while they perceived not.", TextGerman = "Und sie sagte zu seiner Schwester: „Folge seiner Spur. Sie beobachtete ihn von fern, ohne daß sie merkten." });
                        surah28.VerseList.Add(new Verse { Number = 12, TextBosnian = "A Mi smo mu već otprije zabranili dojilje, pa ona reče: 'Hoćete li da vam ja pokažem porodicu koja će se o njemu brinuti i koja će mu dobro željeti?'", TextEnglish = "And We had prevented from him [all] wet nurses before,[1096] so she said, 'Shall I direct you to a household that will be responsible for him for you while they are to him [for his upbringing] sincere?'", TextGerman = "Nun hatten Wir ihm zuvor die Ammenbrüste verwehrt. Da sagte sie: „Soll ich euch auf Hausleute hinweisen, die ihn für euch betreuen und ihm aufrichtig zugetan sein würden?" });
                        surah28.VerseList.Add(new Verse { Number = 13, TextBosnian = "I vratismo ga majci njegovoj da se raduje i da ne tuguje, i da se uvjeri da je Allahovo obećanje istinito; ali većina njih ne zna.", TextEnglish = "So We restored him to his mother that she might be content and not grieve and that she would know that the promise of Allāh is true. But most of them [i.e., the people] do not know.", TextGerman = "So brachten Wir ihn zu seiner Mutter zurück, damit sie frohen Mutes und nicht mehr traurig sei und damit sie wissen sollte, daß Allahs Versprechen wahr ist. Aber die meisten von ihnen wissen nicht." });
                        surah28.VerseList.Add(new Verse { Number = 14, TextBosnian = "I kad on dostiže vrhunac svoje snage i potpuno stasa, dadosmo mu mudrost i znanje; tako Mi nagrađujemo one koji dobra djela čine.", TextEnglish = "And when he attained his full strength and was [mentally] mature, We bestowed upon him judgement and knowledge. And thus do We reward the doers of good.", TextGerman = "Und als er seine Vollreife und sein Ebenmaß erlangt hatte, gaben Wir ihm Urteil(skraft) und Wissen. So vergelten Wir den Gutes Tuenden." });
                        surah28.VerseList.Add(new Verse { Number = 15, TextBosnian = "I on uđe u grad u vrijeme nepažnje stanovnika njegovih i u njemu zateče dvojicu ljudi kako se tuku, jedan je bio iz reda njegovih pristalica a drugi iz reda njegovih neprijatelja, pa ga zovnu upomoć onaj iz reda njegovih pristalica protiv onoga iz reda njegovih neprijatelja i Musa ga udari i dokrajči ga. 'Ovo je šejtanov posao!', uzviknu, 'on je, zaista, neprijatelj i zabluđivač očiti!'", TextEnglish = "And he entered the city at a time of inattention by its people[1097] and found therein two men fighting: one from his faction and one from among his enemy. And the one from his faction called for help to him against the one from his enemy, so Moses struck him and [unintentionally] killed him. [Moses] said, 'This is from the work of Satan. Indeed, he is a manifest, misleading enemy.'", TextGerman = "Und er betrat die Stadt zu einer Zeit, als ihre Bewohner unachtsam waren. Da fand er darin zwei Männer, die miteinander kämpften, der eine war von seinem (eigenen) Lager, der andere von seinen Feinden. Da rief ihn derjenige, der von seinem Lager war, zu Hilfe gegen denjenigen, der von den Feinden war. Dann schlug ihn Musa mit der Faust und brachte ihn so um. Er sagte: „Das gehört zum Werk des Satans. Gewiß, er ist ein deutlicher Feind, der in die Irre führt." });
                        surah28.VerseList.Add(new Verse { Number = 16, TextBosnian = "I reče: 'Ja sam sam sebi zulum učinio, pa oprosti mi!' I On mu oprosti, On je, zaista, Oprostitelj grijeha, Milostivi.", TextEnglish = "He said, 'My Lord, indeed I have wronged myself, so forgive me,' and He forgave him. Indeed, He is the Forgiving, the Merciful.", TextGerman = "Er sagte: „Mein Herr, ich habe mir selbst Unrecht zugefügt; so vergib mir. Da vergab Er ihm, denn Er ist ja der Allvergebende und Barmherzige." });
                        surah28.VerseList.Add(new Verse { Number = 17, TextBosnian = "O Gospodaru moj', reče, 'zato što si mi blagodati dao, nikada prestupnicima neću biti od pomoći.'", TextEnglish = "He said, 'My Lord, for the favor You bestowed upon me, I will never be an assistant to the criminals.'", TextGerman = "Er sagte: „Mein Herr, darum, daß Du mir Gunst erwiesen hast, werde ich den Übeltätern nicht mehr Beistand leisten." });
                        surah28.VerseList.Add(new Verse { Number = 18, TextBosnian = "I Musa u gradu osvanu prestrašen, očekujući šta će biti, kad ono onaj koji je jučer od njega tražio pomoć, ponovo ga doziva. 'Ti si, zbilja, očigledno zabludjeli!', reče mu Musa.", TextEnglish = "And he became inside the city fearful and anticipating [exposure], when suddenly the one who sought his help the previous day cried out to him [once again]. Moses said to him, 'Indeed, you are an evident, [persistent] deviator.'", TextGerman = "Am Morgen war er in der Stadt furchtsam und hielt (immer wieder) Ausschau. Auf einmal schrie derjenige, der ihn am Tag zuvor um Hilfe gebeten hatte, zu ihm um Beistand. Musa sagte zu ihm: „Du bist offenkundig einer, der wahrlich (zu Gewalttaten) verleitet." });
                        surah28.VerseList.Add(new Verse { Number = 19, TextBosnian = "I kad htjede ščepati onoga koji im je obojici neprijatelj, reče mu onaj: 'O Musa, zar hoćeš da me ubiješ, kao što si jučer ubio čovjeka?! Ti hoćeš na Zemlji silnik da budeš, a ne želiš da budeš od onih koji izmiruju!'", TextEnglish = "And when he wanted to strike the one who was an enemy to both of them, he[1098] said, 'O Moses, do you intend to kill me as you killed someone yesterday? You only want to be a tyrant in the land and do not want to be of the amenders.'", TextGerman = "Als er nun mit Gewalt denjenigen packen wollte, der ihrer beider Feind war, sagte dieser: „O Musa, willst du denn mich töten, wie du gestern eine (Menschen)seele getötet hast? Du willst ja nur ein Gewalttäter im Land sein, und du willst nicht zu den Heilstiftern gehören." });
                        surah28.VerseList.Add(new Verse { Number = 20, TextBosnian = "I jedan čovjek s kraja grada dotrča: 'O Musa', reče, 'glavešine se dogovaraju da te ubiju, zato odlazi, ja sam ti zbilja iskren savjetnik!'", TextEnglish = "And a man came from the farthest end of the city, running. He said, 'O Moses, indeed the eminent ones are conferring over you [intending] to kill you, so leave [the city]; indeed, I am to you of the sincere advisors.'", TextGerman = "Und es kam ein Mann vom äußersten Ende der Stadt gelaufen. Er sagte: „O Musa, die führende Schar berät über dich, um dich zu töten. So geh fort, gewiß, ich gehöre zu denjenigen, die dir guten Rat geben." });
                        surah28.VerseList.Add(new Verse { Number = 21, TextBosnian = "I Musa uplašen iziđe iz grada, obzirući se. 'Gospodaru moj', reče, 'spasi me naroda zulumćarskog.'", TextEnglish = "So he left it, fearful and anticipating [apprehension]. He said, 'My Lord, save me from the wrongdoing people.'", TextGerman = "So ging er furchtsam aus ihr fort und hielt (immer wieder) Ausschau. Er sagte: „Mein Herr, errette mich von dem ungerechten Volk." });
                        surah28.VerseList.Add(new Verse { Number = 22, TextBosnian = "I kada se uputi prema Medjenu, on reče: 'Gospodar moj će me na Pravi put uputiti!'", TextEnglish = "And when he directed himself toward Madyan, he said, 'Perhaps my Lord will guide me to the sound way.'", TextGerman = "Und als er sich Madyan zuwandte, sagte er: „Vielleicht wird mein Herr mich den rechten Weg leiten." });
                        surah28.VerseList.Add(new Verse { Number = 23, TextBosnian = "A kad stiže do vode medjenske, zateče oko nje grupu ljudi kako napajaju stoku a malo dalje od njih ugleda dvije žene koje su je od vode odbijale. 'Šta je s vama?', upita on. 'Mi ne napajamo dok čobani ne otjeraju stoku!', odgovoriše one, 'a otac nam je veoma star.'", TextEnglish = "And when he came to the water [i.e., well] of Madyan, he found there a crowd of people watering [their flocks], and he found aside from them two women holding back [their flocks]. He said, 'What is your circumstance?' They said, 'We do not water until the shepherds dispatch [their flocks]; and our father is an old man.'", TextGerman = "Als er nun zum Wasser von Madyan kam, fand er dort eine (ganze) Gemeinschaft von Menschen, die (ihr Vieh) tränkten. Und er fand außer ihnen zwei Frauen, die (ihre Tiere) fernhielten. Er sagte: „Was ist mit euch beiden? Sie sagten: „Wir tränken (unsere Tiere) nicht, bis die Hirten (ihr Vieh) weggetrieben haben. Und unser Vater ist ein hochbetagter Greis." });
                        surah28.VerseList.Add(new Verse { Number = 24, TextBosnian = "I on im je napoji, a onda ode u hlad i reče: 'Gospodaru moj, šta god mi od dobra spustio, zaista mi je to potrebno!'", TextEnglish = "So he watered [their flocks] for them; then he went back to the shade and said, 'My Lord, indeed I am, for whatever good You would send down to me, in need.'", TextGerman = "Da tränkte er ihnen (ihre Tiere). Hierauf zog er sich zurück in den Schatten und sagte: „Mein Herr, ich bin dessen bedürftig, was Du auch immer an Gutem zu mir herabsendest." });
                        surah28.VerseList.Add(new Verse { Number = 25, TextBosnian = "I jedna od njih dvije dođe mu, poslije, idući stidljivo i reče: 'Otac moj te zove da te nagradi za to što si nam je napojio!' I kad mu on dođe i kaza mu šta je doživio, on reče: 'Ne strahuj, spasio si se naroda nasilničkog!'", TextEnglish = "Then one of the two women came to him walking with shyness. She said, 'Indeed, my father invites you that he may reward you for having watered for us.' So when he came to him[1099] and related to him the story, he said, 'Fear not. You have escaped from the wrongdoing people.'", TextGerman = "Da kam die eine von den beiden zu ihm, indem sie verschämt einherging. Sie sagte: „Mein Vater ruft dich, um dir den Lohn dafür zu entrichten, daß du uns (die Tiere) getränkt hast. Nachdem er zu ihm gekommen war und ihm die Geschichte berichtet hatte, sagte er: „Fürchte dich nicht; du bist dem ungerechten Volk entkommen." });
                        surah28.VerseList.Add(new Verse { Number = 26, TextBosnian = "O oče moj', reče jedna od njih, 'uzmi ga u najam, najbolje je da unajmiš onog ko je snažan i pouzdan.'", TextEnglish = "One of the women said, 'O my father, hire him. Indeed, the best one you can hire is the strong and the trustworthy.'", TextGerman = "Die eine von den beiden sagte: „O mein lieber Vater, nimm ihn in Dienst, denn der Beste, den du in Dienst nehmen kannst, ist der Starke und Vertrauenswürdige." });
                        surah28.VerseList.Add(new Verse { Number = 27, TextBosnian = "Ja te želim oženiti jednom od ove dvije kćeri moje', reče on, 'ali treba da me osam godina služiš; a ako deset napuniš bit će dobra volja tvoja, a ja ne želim da te na to silim; ti ćeš vidjeti, ako Allah da, da sam dobar.'", TextEnglish = "He said, 'Indeed, I wish to wed you one of these, my two daughters, on [the condition] that you serve me for eight years; but if you complete ten, it will be [as a favor] from you. And I do not wish to put you in difficulty. You will find me, if Allāh wills, from among the righteous.'", TextGerman = "Er sagte: „Ich will dich mit einer dieser meiner beiden Töchter verheiraten unter der Bedingung, daß du acht Jahre in meinen Dienst trittst. Wenn du sie aber auf zehn vollmachst, so steht es bei dir. Ich will dir keine Härte auferlegen. Du wirst mich, wenn Allah will, als einen der Rechtschaffenen finden." });
                        surah28.VerseList.Add(new Verse { Number = 28, TextBosnian = "Neka bude tako između mene i tebe!', reče Musa, 'koji god od ta dva roka ispunim nema mi se šta prigovoriti, a Allah je jamac za ono što smo utanačili!'", TextEnglish = "[Moses] said, 'That is [established] between me and you. Whichever of the two terms I complete - there is no injustice to me, and Allāh, over what we say, is Witness.'", TextGerman = "Er sagte: „Dies sei zwischen mir und dir (abgemacht). Welche der beiden Fristen ich auch erfülle, so darf es keine Bedrängnis gegen mich geben. Und Allah ist Sachwalter über das, was wir (hier) sagen." });
                        surah28.VerseList.Add(new Verse { Number = 29, TextBosnian = "I kada Musa ispuni ugovoreni rok i krenu s porodicom svojom, on ugleda vatru na jednoj strani brda i reče porodici svojoj: 'Pričekajte, ja sam, zbilja, ugledao vatru, možda ću vam od nje kakvu vijest donijeti, ili zapaljenu glavnju, da se ogrijete.'", TextEnglish = "And when Moses had completed the term and was traveling with his family, he perceived from the direction of the mount a fire. He said to his family, 'Stay here; indeed, I have perceived a fire. Perhaps I will bring you from there [some] information or burning wood from the fire that you may warm yourselves.'", TextGerman = "Als Musa die Frist erfüllt hatte und mit seinen Angehörigen fortzog, nahm er auf der Seite des Berges ein Feuer wahr. Er sagte zu seinen Angehörigen: „Bleibt hier. Ich habe ein Feuer wahrgenommen; vielleicht kann ich euch davon eine Nachricht oder ein Stück brennendes Holz aus dem Feuer bringen, auf daß ihr euch (daran) wärmen könnt." });
                        surah28.VerseList.Add(new Verse { Number = 30, TextBosnian = "A kad dođe do vatre, neko ga zovnu s desne strane doline, iz stabla, u blagoslovljenom kraju: 'O Musa, Ja sam, zbilja, Allah, Gospodar svjetova!", TextEnglish = "But when he came to it, he was called from the right side of the valley in a blessed spot - from the tree,[1100] 'O Moses, indeed I am Allāh, Lord of the worlds.'", TextGerman = "Als er dort ankam, wurde ihm vom rechten Ufer des Tales im gesegneten Fleck (Land) aus dem Baum zugerufen: „O Musa, gewiß, Ich bin Allah, der Herr der Weltenbewohner." });
                        surah28.VerseList.Add(new Verse { Number = 31, TextBosnian = "I baci svoj štap!' I kad je vidje da se tresući kreće, kao da je to džann-zmija, on okrenut leđima uzmače i ne osvrnu se. 'O Musa, priđi i ne boj se! Ti si od onih sigurnih!", TextEnglish = "And [he was told], 'Throw down your staff.' But when he saw it writhing as if it was a snake, he turned in flight and did not return.[1101] [Allāh said], 'O Moses, approach and fear not. Indeed, you are of the secure.", TextGerman = "Und: „Wirf deinen Stock hin. Doch als er sah, daß er sich hin und her bewegte, als wäre er eine flinke Schlange, kehrte er den Rücken und wandte sich nicht mehr um. - „O Musa, komm her und fürchte dich nicht, denn du sollst zu denjenigen gehören, die in Sicherheit sind." });
                        surah28.VerseList.Add(new Verse { Number = 32, TextBosnian = "Uvuci ruku u njedra svoja, pojavit će se bijela, a bez mahane, i pribij uza se ruku svoju od straha! Eto, to su dva jasna dokaza od Gospodara tvoga faraonu i glavešinama njegovim, oni su, doista, narod buntovni!'", TextEnglish = "Insert your hand into the opening of your garment; it will come out white, without disease. And draw in your arm close to you [as prevention] from fear, for those are two proofs from your Lord to Pharaoh and his establishment. Indeed, they have been a people defiantly disobedient.'", TextGerman = "(Und) stecke die Hand in deinen Brustschlitz, so kommt sie weiß, jedoch nicht von Übel befallen, heraus. - Und zieh deinen Arm an dich gegen den Schreck. - Dies sind zwei Beweise von deinem Herrn, (gerichtet) an Fir’aun und seine führende Schar. Gewiß, sie sind ein Volk von Frevlern." });
                        surah28.VerseList.Add(new Verse { Number = 33, TextBosnian = "Gospodaru moj', reče, 'ja sam ubio jednog njihovog čovjeka, pa se bojim da i oni mene ne ubiju.", TextEnglish = "He said, 'My Lord, indeed I killed from among them someone, and I fear they will kill me.", TextGerman = "Er sagte: „Mein Herr, ich habe eine (Menschen)seele von ihnen getötet, und so fürchte ich, daß sie mich töten." });
                        surah28.VerseList.Add(new Verse { Number = 34, TextBosnian = "A moj brat Harun rječitiji je od mene, pa pošalji sa mnom i njega, kao mog pomoćnika, da potvrđuje riječi moje, jer se bojim da me ne nazovu lašcem.'", TextEnglish = "And my brother Aaron is more fluent than me in tongue, so send him with me as support, verifying me. Indeed, I fear that they will deny me.'", TextGerman = "Auch hat mein Bruder Harun eine redegewandtere Zunge. So sende ihn mit mir zur Unterstützung, mich zu bestätigen, denn ich fürchte, daß sie mich der Lüge bezichtigen." });
                        surah28.VerseList.Add(new Verse { Number = 35, TextBosnian = "Pomoći ćemo te bratom tvojim', reče On, 'i obojici ćemo nepobitni dokaz dati, pa do vas oni neće moći doprijeti zbog znakova Naših. Vas dvojica ćete, i oni koji vas budu slijedili, biti pobjednici.'", TextEnglish = "[Allāh] said, 'We will strengthen your arm through your brother and grant you both supremacy so they will not reach you. [It will be] through Our signs; you and those who follow you will be the predominant.'", TextGerman = "Er sagte: „Wir werden deinen Arm durch deinen Bruder festigen und euch beiden eine Ermächtigung erteilen, so daß sie nicht zu euch gelangen (können). Mit Unseren Zeichen werdet ihr und diejenigen, die euch folgen, Sieger sein." });
                        surah28.VerseList.Add(new Verse { Number = 36, TextBosnian = "I kad im Musa donese Naše jasne znakove, oni povikaše: 'Ovo je samo smišljena magija, nismo čuli da se ovako nešto dešavalo u doba predaka naših!'", TextEnglish = "But when Moses came to them with Our signs as clear evidences, they said, 'This is not except invented magic, and we have not heard of this [religion] among our forefathers.'", TextGerman = "Als nun Musa mit Unseren Zeichen als klare Beweise kam, sagten sie: „Das ist nur ersonnene Zauberei. Und wir haben so etwas bei unseren Vorvätern nicht gehört." });
                        surah28.VerseList.Add(new Verse { Number = 37, TextBosnian = "Gospodar moj najbolje zna onoga koji donosi uputu od Njega', reče Musa, 'i onoga koji će sretan kraj imati, a zulumćari, doista, neće uspjeti!'", TextEnglish = "And Moses said, 'My Lord is more knowing [than we or you] of who has come with guidance from Him and to whom will be succession in the home.[1102] Indeed, wrongdoers do not succeed.'", TextGerman = "Musa sagte: „Mein Herr weiß besser, wer von Ihm her die Rechtleitung bringt und wem die letztendliche Wohnstätte gehören wird. Gewiß, den Ungerechten wird es nicht wohl ergehen." });
                        surah28.VerseList.Add(new Verse { Number = 38, TextBosnian = "O velikaši', reče faraon, 'ja ne znam da vi imate drugog boga osim mene a ti, o Hamane, peci za me opeke i sagradi mi toranj da se popnem k Musaovu Bogu, jer ja mislim da je on, zaista, jedan od lažaca!'", TextEnglish = "And Pharaoh said, 'O eminent ones, I have not known you to have a god other than me. Then ignite for me, O Hāmān, [a fire] upon the clay[1103] and make for me a tower that I may look at the God of Moses. And indeed, I do think he is among the liars.'", TextGerman = "Und Fir’aun sagte: „O ihr führende Schar, keinen anderen Gott weiß ich für euch als mich (selbst). So entfache mir, o Haman, einen Brand auf Lehm, und mache mir einen Hochbau, auf daß ich zum Gott Musas emporsteige. Ich glaube fürwahr, daß er zu den Lügnern gehört." });
                        surah28.VerseList.Add(new Verse { Number = 39, TextBosnian = "A on i vojske njegove bijahu se, bez ikakvog prava, oholili na Zemlji i mislili su da nam neće biti vraćeni.", TextEnglish = "And he was arrogant, he and his soldiers, in the land, without right, and they thought that they would not be returned to Us.", TextGerman = "Er und seine Heerscharen verhielten sich ohne Recht hochmütig auf der Erde und glaubten, daß sie (dereinst) nicht zu Uns zurückgebracht würden." });
                        surah28.VerseList.Add(new Verse { Number = 40, TextBosnian = "Pa mi dohvatismo i njega i vojske njegove i u more ih bacismo; pogledaj kako su skončali zulumćari!", TextEnglish = "So We took him and his soldiers and threw them into the sea.[1104] So see how was the end of the wrongdoers.", TextGerman = "Da ergriffen Wir ihn und seine Heerscharen und warfen sie dann in das große Gewässer. So schau, wie das Ende der Ungerechten war." });
                        surah28.VerseList.Add(new Verse { Number = 41, TextBosnian = "A bili smo ih učinili vođama, koji su pozivali u Vatru, a na Kijametskom danu niko im neće pomoći.", TextEnglish = "And We made them leaders[1105] inviting to the Fire, and on the Day of Resurrection they will not be helped.", TextGerman = "Und Wir machten sie zu Anführern, die zum (Höllen)feuer einladen. Und am Tag der Auferstehung wird ihnen keine Hilfe zuteil werden." });
                        surah28.VerseList.Add(new Verse { Number = 42, TextBosnian = "I popratismo ih prokletstvom na ovom dunjaluku, a na Kijametskom danu bit će među onima koji su od svakog dobra udaljeni.", TextEnglish = "And We caused to overtake them in this world a curse, and on the Day of Resurrection they will be of the despised.[1106]", TextGerman = "Und Wir ließen ihnen einen Fluch im Diesseits nachfolgen, und am Tag der Auferstehung werden sie zu den Verabscheuten gehören." });
                        surah28.VerseList.Add(new Verse { Number = 43, TextBosnian = "I Mi smo Musau Knjigu dali, nakon što smo drevne narode uništili, da bude svjetlo ljudima, i uputa, i milost, da bi se prisjetili i pouku primili.'", TextEnglish = "And We gave Moses the Scripture, after We had destroyed the former generations, as enlightenment for the people and guidance and mercy that they might be reminded.", TextGerman = "Und Wir gaben ja Musa die Schrift -, nachdem Wir die früheren Geschlechter vernichteten, - als einsichtbringende Zeichen für die Menschen und als Rechtleitung und Barmherzigkeit, auf daß sie bedenken mögen." });
                        surah28.VerseList.Add(new Verse { Number = 44, TextBosnian = "Ti nisi bio na zapadnoj strani kada smo Musau poslanstvo povjerili, a nisi bio ni svjedok tome.", TextEnglish = "And you, [O Muḥammad], were not on the western side [of the mount] when We revealed to Moses the command, and you were not among the witnesses [to that].", TextGerman = "Du warst nicht auf der westlichen Seite, als Wir Musa die Angelegenheit auftrugen, und du warst nicht zugegen." });
                        surah28.VerseList.Add(new Verse { Number = 45, TextBosnian = "Mi smo mnoge narode podigli i oni su dugo živjeli, a ti nisi boravio među stanovnicima Medjena i ajete im Naše kazivao nego smo Mi bili Pošiljatelji,", TextEnglish = "But We produced [many] generations [after Moses], and prolonged was their duration.[1107] And you were not a resident among the people of Madyan, reciting to them Our verses, but We were senders [of this message].[1108]", TextGerman = "Wir aber ließen Geschlechter entstehen, und es war ihnen ein langes Leben beschieden. Und du hattest dich nicht unter den Leuten von Madyan niedergelassen, um ihnen Unsere Zeichen zu verlesen. Wir aber entsandten immer wieder (Propheten)." });
                        surah28.VerseList.Add(new Verse { Number = 46, TextBosnian = "i nisi bio pored brda kada smo dozivali, ali Mi smo te poslali kao milost Gospodara tvoga da upozoravaš ljude kojima prije tebe nije došao nikakav upozoritelj, da bi se prisjetili i pouku primili.", TextEnglish = "And you were not at the side of the mount when We called [Moses] but [were sent] as a mercy from your Lord to warn a people to whom no warner had come before you that they might be reminded.", TextGerman = "Und du warst nicht auf der Seite des Berges, als Wir zuriefen. Aber (die Offenbarung ist) als Barmherzigkeit von deinem Herrn (zu dir gekommen), damit du Leute warnst, zu denen noch kein Warner vor dir gekommen ist, auf daß sie bedenken mögen." });
                        surah28.VerseList.Add(new Verse { Number = 47, TextBosnian = "I da ne reknu, kada ih zadesi nedaća zbog onoga što su počinili: 'Gospodaru naš, zašto nam nisi poslao poslanika, pa da dokaze Tvoje slijedimo i vjernici budemo", TextEnglish = "And if not that a disaster should strike them for what their hands put forth [of sins] and they would say, 'Our Lord, why did You not send us a messenger so we could have followed Your verses and been among the believers?'...[1109]", TextGerman = "(Hätten Wir dich nicht entsandt), würden sie sagen, wenn sie ein Unglück trifft wegen dessen, was ihre Hände vorausgeschickt haben: „Unser Herr, hättest Du doch einen Gesandten zu uns gesandt, so daß wir Deinen Zeichen hätten folgen können und zu den Gläubigen hätten gehören können!" });
                        surah28.VerseList.Add(new Verse { Number = 48, TextBosnian = "A kad im dolazi Istina od nas, oni govore: 'Zašto mu nije dato isto kao što je dato Musau?' A zar oni nisu porekli ono što je dato Musau ranije?! Oni govore: 'Dvije čarolije, jedna drugu podržava', i govore: 'Mi ni u jednu ne vjerujemo!'", TextEnglish = "But when the truth came to them from Us, they said, 'Why was he not given like that which was given to Moses?' Did they not disbelieve in that which was given to Moses before? They said, '[They are but] two works of magic supporting each other,[1110] and indeed we are, in both, disbelievers.'", TextGerman = "Als nun die Wahrheit von Uns her zu ihnen kam, sagten sie: „Wäre ihm doch das gleiche gegeben worden, was Musa gegeben wurde! Haben sie denn nicht das verleugnet, was zuvor Musa gegeben wurde? Sie sagen: „Zwei Zauberwerke, die einander beistehen. Und sie sagen: „Wir verleugnen sie alle (beide)." });
                        surah28.VerseList.Add(new Verse { Number = 49, TextBosnian = "Reci: 'Pa donesite vi od Allaha knjigu koja bolje, nego ove dvije, na Pravi put upućuje, nju bih slijedio, ako ste iz reda onih koji istinu govore!'", TextEnglish = "Say, 'Then bring a scripture from Allāh which is more guiding than either of them that I may follow it, if you should be truthful.'", TextGerman = "Sag: Bringt doch eine Schrift von Allah bei, die eine bessere Rechtleitung enthält als diese beiden, so will ich ihr folgen, wenn ihr wahrhaftig seid." });
                        surah28.VerseList.Add(new Verse { Number = 50, TextBosnian = "Pa ako ti se ne odazovu, onda znaj da se oni povode jedino za hirovima svojim, a ne za Allahovom uputom?! Allah, doista, neće uputiti narod zulumćarski.", TextEnglish = "But if they do not respond to you - then know that they only follow their [own] desires. And who is more astray than one who follows his desire without guidance from Allāh? Indeed, Allāh does not guide the wrongdoing people.", TextGerman = "Wenn sie nicht auf dich hören, so wisse, daß sie nur ihren Neigungen folgen. Und wer ist weiter abgeirrt als jemand, der seiner Neigung folgt ohne Rechtleitung von Allah. Gewiß, Allah leitet das ungerechte Volk nicht recht." });
                        surah28.VerseList.Add(new Verse { Number = 51, TextBosnian = "A Mi smo im Riječ podrobno dostavili ne bi li se prisjetili i pouku primili.", TextEnglish = "And We have [repeatedly] conveyed to them the word [i.e., the Qur’ān] that they might be reminded.", TextGerman = "Und Wir haben ihnen ja das Wort aufeinanderfolgend übermittelt, auf daß sie bedenken mögen." });
                        surah28.VerseList.Add(new Verse { Number = 52, TextBosnian = "Oni kojima smo dali Knjigu prije Kur'ana, vjeruju u njega,", TextEnglish = "Those to whom We gave the Scripture before it - they[1111] are believers in it.", TextGerman = "Diejenigen, denen Wir vor ihm die Schrift gaben, glauben an ihn." });
                        surah28.VerseList.Add(new Verse { Number = 53, TextBosnian = "a kada im se on uči, govore: 'Mi vjerujemo u njega, on je Istina od Gospodara našeg, mi smo i prije bili muslimani.'", TextEnglish = "And when it is recited to them, they say, 'We have believed in it; indeed, it is the truth from our Lord. Indeed we were, [even] before it, Muslims [i.e., submitting to Allāh].'", TextGerman = "Und wenn er ihnen verlesen wird, sagen sie: „Wir glauben an ihn. Gewiß, es ist die Wahrheit von unserem Herrn. Wir waren ja schon vor ihm (Allah) ergeben." });
                        surah28.VerseList.Add(new Verse { Number = 54, TextBosnian = "Oni će dobiti dvostruku nagradu za to što trpe i što lijepim na zlo uzvraćaju i što od onoga što im dajemo udjeljuju;", TextEnglish = "Those will be given their reward twice for what they patiently endured and [because] they avert evil through good, and from what We have provided them they spend.", TextGerman = "Diese erhalten ihren Lohn zweimal dafür, daß sie standhaft waren. Und sie wehren mit dem Guten das Böse ab und geben von dem aus, womit Wir sie versorgt haben." });
                        surah28.VerseList.Add(new Verse { Number = 55, TextBosnian = "a kad čuju besmislicu kakvu, od nje se okrenu i reknu: 'Nama naša, a vama vaša djela, od nas mirni ste! Mi ne želimo društvo neukih.'", TextEnglish = "And when they hear ill speech, they turn away from it and say, 'For us are our deeds, and for you are your deeds. Peace will be upon you;[1112] we seek not the ignorant.'", TextGerman = "Und wenn sie unbedachte Rede hören, wenden sie sich davon ab und sagen: „Wir haben unsere Taten und ihr habt eure Taten (zu verantworten). Friede sei auf euch! Wir trachten nicht nach (dem Umgang mit) den Toren." });
                        surah28.VerseList.Add(new Verse { Number = 56, TextBosnian = "Ti, doista, ne možeš dati Uputu kome bi ti volio, jer Allah upućuje koga On hoće, i On najbolje zna one koji će Uputu slijediti.", TextEnglish = "Indeed, [O Muḥammad], you do not guide whom you like, but Allāh guides whom He wills. And He is most knowing of the [rightly] guided.", TextGerman = "Gewiß, du kannst nicht rechtleiten, wen du gern (rechtgeleitet sehen) möchtest. Allah aber leitet recht, wen Er will. Er kennt sehr wohl die Rechtgeleiteten." });
                        surah28.VerseList.Add(new Verse { Number = 57, TextBosnian = "Oni govore: 'Ako s tobom budemo Uputu slijedili, bit ćemo brzo iz rodnog kraja protjerani!' Zar im nismo uspostavili Harem sigurnim gdje se, kao opskrba od Nas, slivaju plodovi svakovrsni, međutim, većina njih ne zna.", TextEnglish = "And they [i.e., the Quraysh] say, 'If we were to follow the guidance with you, we would be swept[1113] from our land.' Have We not established for them a safe sanctuary to which are brought the fruits of all things as provision from Us? But most of them do not know.", TextGerman = "Und sie sagen: „Wenn wir der Rechtleitung mit dir folgen, werden wir von unserem Land fortgerissen. Haben Wir ihnen denn nicht eine feste Stellung in einem sicheren geschützten Bezirk verliehen, zu dem die Früchte jeder Art zusammengetragen werden als Versorgung von Uns aus? Aber die meisten von ihnen wissen nicht." });
                        surah28.VerseList.Add(new Verse { Number = 58, TextBosnian = "A koliko smo Mi naselja uništili, čiji su stanovnici naspram onoga što im je za življenje dato obijesni bili! Eno domova njihovih, samo su malo, poslije njih, nastanjeni bili, Mi smo ih naslijedili.", TextEnglish = "And how many a city have We destroyed that was insolent in its [way of] living, and those are their dwellings which have not been inhabited after them except briefly.[1114] And it is We who were the inheritors.", TextGerman = "Und wie (so) manche Stadt, die sich übermütig ihres Lebensunterhaltes erfreute, haben Wir vernichtet! Da sind nun ihre Wohnorte, die nach ihnen nicht mehr bewohnt wurden, bis auf wenige. Und Wir sind es, die Erben (all dessen) geworden sind." });
                        surah28.VerseList.Add(new Verse { Number = 59, TextBosnian = "A Gospodar tvoj ne bi naselja uništio dok u njihov glavni grad poslanika ne bi poslao, i ajete im Naše kazivao. I Mi ne bismo naselja uništavali, osim kada su stanovnici njihovi zulumćari bili.", TextEnglish = "And never would your Lord have destroyed the cities until He had sent to their mother [i.e., principal city] a messenger reciting to them Our verses. And We would not destroy the cities except while their people were wrongdoers.", TextGerman = "Und dein Herr hätte nimmer die Städte vernichtet, bevor Er nicht in ihrem Mittelpunkt einen Gesandten hätte erstehen lassen, der ihnen Unsere Zeichen verliest. Und Wir hätten die Städte nimmer vernichtet, wenn ihre Bewohner nicht ungerecht gewesen wären." });
                        surah28.VerseList.Add(new Verse { Number = 60, TextBosnian = "Sve što vam je darovano samo su naslade i ukrasi u životu dunjalučkom, a ono što je u Allaha bolje je i trajno je. Zar ne razumijete?!", TextEnglish = "And whatever thing you [people] have been given - it is [only for] the enjoyment of worldly life and its adornment. And what is with Allāh is better and more lasting; so will you not use reason?", TextGerman = "Und was immer euch gegeben geworden ist, ist Genuß und Schmuck des diesseitigen Lebens. Was aber bei Allah ist, ist besser und beständiger. Begreift ihr denn nicht?" });
                        surah28.VerseList.Add(new Verse { Number = 61, TextBosnian = "Kako može biti jednak onaj kome smo lijepu nagradu obećali, i koju će dobiti, i onaj kome smo dali da se naslađuje u životu dunjalučkom, a koji će poslije, na Kijametskom danu, Vatri priveden biti?!", TextEnglish = "Then is he whom We have promised a good promise which he will meet [i.e., obtain] like he for whom We provided enjoyment of worldly life [but] then he is, on the Day of Resurrection, among those presented [for punishment in Hell]?", TextGerman = "Ist denn einer, dem Wir ein schönes Versprechen gegeben haben und der es auch vorfinden wird, einem gleich, den Wir den Genuß des diesseitigen Lebens genießen lassen, der hierauf aber am Tag der Auferstehung zu den Vorgeführten gehören wird?" });
                        surah28.VerseList.Add(new Verse { Number = 62, TextBosnian = "A na Dan kad ih On pozove i upita: 'Gdje su oni koje ste Mojim ortacima smatrali?',", TextEnglish = "And [warn of] the Day He will call them and say, 'Where are My 'partners' which you used to claim?'", TextGerman = "Und am Tag, da Er ihnen zurufen wird und dann sagt: „Wo sind denn Meine Teilhaber, die ihr stets angegeben habt?" });
                        surah28.VerseList.Add(new Verse { Number = 63, TextBosnian = "reći će oni na kojima će se Riječ obistiniti: 'Gospodaru naš, ove što smo na stranputicu naveli, naveli smo zato što smo i sami na stranputici bili, mi ih se pred Tobom odričemo, oni nas nisu obožavali.'", TextEnglish = "Those upon whom the word[1115] will have come into effect will say, 'Our Lord, these are the ones we led to error. We led them to error just as we were in error. We declare our disassociation [from them] to You. They did not used to worship [i.e., obey] us.'[1116]", TextGerman = "Diejenigen, gegen die sich das Wort bewahrheitet hat, sagen: „Unser Herr, diese, die wir in Verirrung haben fallen lassen, haben wir (nur) in Verirrung fallen lassen, wie wir selbst in Verirrung gefallen waren. (Von ihnen) sagen wir uns vor Dir los. Nicht uns dienten sie ja." });
                        surah28.VerseList.Add(new Verse { Number = 64, TextBosnian = "Pozovite božanstva vaša!', reći će im se, pa će ih oni pozvati, ali im se ona neće odazvati i patnju će vidjeti - a da su samo Uputu slijedili!", TextEnglish = "And it will be said, 'Invoke your 'partners,'' and they will invoke them; but they will not respond to them, and they will see the punishment. If only they had followed guidance!", TextGerman = "Und es wird gesagt werden: „Ruft eure Teilhaber. Sie werden sie anrufen, aber sie werden ihnen nicht antworten. Und sie werden die Strafe sehen. Wenn sie sich hätten rechtleiten lassen (, wären sie gerettet gewesen)." });
                        surah28.VerseList.Add(new Verse { Number = 65, TextBosnian = "A na Dan kad ih On pozove i upita: 'Šta ste poslanicima odgovorili?',", TextEnglish = "And [mention] the Day He will call them and say, 'What did you answer the messengers?'", TextGerman = "Und am Tag, da Er ihnen zurufen wird und sagt: „Was habt ihr den Gesandten geantwortet?" });
                        surah28.VerseList.Add(new Verse { Number = 66, TextBosnian = "taj Dan oni neće znati šta će odgovoriti, pa ni jedan drugog neće ništa pitati.", TextEnglish = "But the information[1117] will be unapparent to them that Day, so they will not [be able to] ask one another.", TextGerman = "Ihren Blicken werden an jenem Tag die Beweise entzogen sein, und so fragen sie sich nicht mehr gegenseitig." });
                        surah28.VerseList.Add(new Verse { Number = 67, TextBosnian = "A onaj koji se bude pokajao, i koji bude vjerovao, i koji bude dobra djela činio, on će uspjeti.", TextEnglish = "But as for one who had repented, believed, and done righteousness, it is expected [i.e., promised by Allāh] that he will be among the successful.", TextGerman = "Was aber jemanden angeht, der bereut und glaubt und rechtschaffen handelt, der wird vielleicht zu denjenigen gehören, denen es wohl ergeht." });
                        surah28.VerseList.Add(new Verse { Number = 68, TextBosnian = "Gospodar tvoj stvara šta hoće, i On odabire. Oni nemaju pravo birati. Uzvišen neka je Allah i vrlo visoko iznad onih koje Njemu pridružuju!", TextEnglish = "And your Lord creates what He wills and chooses; not for them was the choice. Exalted is Allāh and high above what they associate with Him.", TextGerman = "Und dein Herr erschafft, was Er will, und wählt. Ihnen aber steht es nicht zu, zu wählen. Preis sei Allah! Erhaben ist Er über das, was sie (Ihm) beigesellen." });
                        surah28.VerseList.Add(new Verse { Number = 69, TextBosnian = "Gospodar tvoj zna ono što grudi njihove kriju i ono što oni obznanjuju.", TextEnglish = "And your Lord knows what their breasts conceal and what they declare.", TextGerman = "Und dein Herr weiß, was ihre Brüste verhehlen und was sie offenlegen." });
                        surah28.VerseList.Add(new Verse { Number = 70, TextBosnian = "On je Allah, drugog boga osim Njega nema; Njemu neka je sva hvala i na ovom- prvom i na drugom svijetu! Presuda samo Njemu pripada i Njemu ćete se vratiti!", TextEnglish = "And He is Allāh; there is no deity except Him. To Him is [due all] praise in the first [life] and the Hereafter. And His is the [final] decision, and to Him you will be returned.", TextGerman = "Und Er ist Allah. Es gibt keinen Gott außer Ihm. (Alles) Lob gehört Ihm in der diesseitigen und in der jenseitigen Welt! Ihm gehört das Urteil, und zu Ihm werdet ihr zurückgebracht." });
                        surah28.VerseList.Add(new Verse { Number = 71, TextBosnian = "Reci: 'Šta mislite, ako bi Allah dao da vam noć potraje vječno, do Kijametskog dana, koji bog bi vam, osim Allaha, svjetlost dao?! Zar ne čujete'", TextEnglish = "Say, 'Have you considered:[1118] if Allāh should make for you the night continuous until the Day of Resurrection, what deity other than Allāh could bring you light? Then will you not hear?'", TextGerman = "Sag: Was meint ihr, wenn Allah auf euch die Nacht als Dauerzustand legen würde bis zum Tag der Auferstehung, wer wäre (dann) Gott außer Allah, Der euch Licht bringen würde? Wollt ihr denn nicht hören?" });
                        surah28.VerseList.Add(new Verse { Number = 72, TextBosnian = "I Reci: 'Kažite vi meni, ako bi Allah dao da vam dan potraje vječno, do Kijametskog dana, koji bi bog vama, osim Allaha, noć dao da u njoj smiraj nađete?! Zar ne vidite'", TextEnglish = "Say, 'Have you considered: if Allāh should make for you the day continuous until the Day of Resurrection, what deity other than Allāh could bring you a night in which you may rest? Then will you not see?'", TextGerman = "Sag: Was meint ihr, wenn Allah auf euch den Tag als Dauerzustand legen würde bis zum Tag der Auferstehung, wer wäre (dann) Gott außer Allah, Der euch eine Nacht bringen würde, in der ihr ruht? Wollt ihr denn nicht einsichtig sein?" });
                        surah28.VerseList.Add(new Verse { Number = 73, TextBosnian = "Iz milosti Svoje On vam je dao noć i dan - da se noću odmarate a da danju za dobrima Njegovim tragate i da zahvalni budete.", TextEnglish = "And out of His mercy He made for you the night and the day that you may rest therein and [by day] seek from His bounty and [that] perhaps you will be grateful.", TextGerman = "In Seiner Barmherzigkeit hat Er euch die Nacht und den Tag gemacht, damit ihr darin ruht und damit ihr (auch) nach etwas von Seiner Huld trachtet, und auf daß ihr dankbar sein möget." });
                        surah28.VerseList.Add(new Verse { Number = 74, TextBosnian = "A na Dan kad ih on pozove i upita: 'Gdje su oni koje ste Mojim ortacima smatrali',", TextEnglish = "And [warn of] the Day He will call them and say, 'Where are My 'partners' which you used to claim?'", TextGerman = "Und am Tag, da Er ihnen zurufen wird und dann sagt: „Wo sind denn Meine Teilhaber, die ihr stets angegeben habt?" });
                        surah28.VerseList.Add(new Verse { Number = 75, TextBosnian = "iz svakog naroda dovest ćemo svjedoka i reći: 'Dajte svoj dokaz!', i oni će saznati da Istina pripada jedino Allahu, a neće im biti onih koje su izmišljali.", TextEnglish = "And We will extract from every nation a witness and say, 'Produce your proof,' and they will know that the truth belongs to Allāh, and lost from them is that which they used to invent.", TextGerman = "Und Wir nehmen aus jeder Gemeinschaft einen Zeugen heraus, und da sagen Wir: „Bringt euren Beweis vor. Dann werden sie wissen, daß die Wahrheit Allah gehört. Und entschwunden wird ihnen sein, was sie zu ersinnen pflegten." });
                        surah28.VerseList.Add(new Verse { Number = 76, TextBosnian = "Karun je iz Musaova naroda bio, pa ih je tlačio, a bili smo mu dali toliko blaga da je ključeve njegove teško mogla nositi grupa snažnih ljudi. Pa mu narod njegov reče: 'Ne budi obijestan jer Allah ne voli one koji su obijesni!',", TextEnglish = "Indeed, Qārūn was from the people of Moses, but he tyrannized them. And We gave him of treasures whose keys would burden a band of strong men; thereupon his people said to him, 'Do not exult. Indeed, Allāh does not like the exultant.", TextGerman = "Gewiß, Qarun gehörte zum Volk Musas, doch unterdrückte er sie. Und Wir gaben ihm solche Schätze, daß deren Schlüssel wahrlich eine schwere Last für eine (ganze) Schar kräftiger Männer gewesen wären. Als sein Volk zu ihm sagte: „Sei nicht (übermütig) froh, denn Allah liebt nicht diejenigen (Unterdrücker), die (zu übermütig) froh sind," });
                        surah28.VerseList.Add(new Verse { Number = 77, TextBosnian = "i nastoj da time što ti je Allah dao stekneš Dom ahiretski, a ne zaboravi ni svoj udio na dunjaluku i čini dobro, kao što je Allah tebi dobro učinio, i nastoj da ne činiš nered po Zemlji, zaista Allah ne voli one koji nered čine.'", TextEnglish = "But seek, through that which Allāh has given you, the home of the Hereafter; and [yet], do not forget your share of the world. And do good as Allāh has done good to you. And desire not corruption in the land. Indeed, Allāh does not like corrupters.'", TextGerman = "sondern trachte mit dem, was Allah dir gegeben hat, nach der jenseitigen Wohnstätte, vergiß aber auch nicht deinen Anteil am Diesseits. Und tu Gutes, so wie Allah dir Gutes getan hat. Und trachte nicht nach Unheil auf der Erde, denn Allah liebt nicht die Unheilstifter." });
                        surah28.VerseList.Add(new Verse { Number = 78, TextBosnian = "Ovo što imam dato mi je samo na osnovu Allahovog znanja o meni, tako ja mislim', govorio je on. A zar nije znao da je Allah prije njega već uništio neke narode koji su bili od njega jači i koji su bili više nakupili?! A o grijesima svojim, prestupnici ni ispitivani neće biti.", TextEnglish = "He said, 'I was only given it because of knowledge I have.' Did he not know that Allāh had destroyed before him of generations those who were greater than him in power and greater in accumulation [of wealth]? But the criminals, about their sins, will not be asked.[1119]", TextGerman = "Er sagte: „Es ist mir nur gegeben worden aufgrund von Wissen, das ich besitze. Wußte er denn nicht, daß Allah bereits vor ihm solche Geschlechter vernichtet hatte, die eine stärkere Kraft als er besaßen und eine größere Ansammlung (an Helfern) hatten? Und die Übeltäter werden nicht nach ihren Sünden befragt." });
                        surah28.VerseList.Add(new Verse { Number = 79, TextBosnian = "I iziđe on pred narod svoj u punom sjaju. 'Ah, da je i nama ono što je dato Karunu!', govorili su oni koji su čeznuli za životom dunjalučkim, 'on, uistinu, veliki udio u dunjaluku ima!'", TextEnglish = "So he came out before his people in his adornment. Those who desired the worldly life said, 'Oh, would that we had like what was given to Qārūn. Indeed, he is one of great fortune.'", TextGerman = "Und so trat er zu seinem Volk in seinem Schmuck heraus. Diejenigen, die das diesseitige Leben begehrten, sagten: „O hätten doch (auch) wir das gleiche wie das, was Qarun gegeben worden ist! Er hat wahrlich gewaltiges Glück." });
                        surah28.VerseList.Add(new Verse { Number = 80, TextBosnian = "Oni kojima je dato znanje rekoše: 'Teško vama! Onima koji vjeruju i čine dobra djela bolja je Allahova nagrada, a nju će samo strpljivi zadobiti.'", TextEnglish = "But those who had been given knowledge said, 'Woe to you! The reward of Allāh is better for he who believes and does righteousness. And none are granted it except the patient.'", TextGerman = "Aber diejenigen, denen das Wissen gegeben worden war, sagten: „Wehe euch! Allahs Belohnung ist besser für jemanden, der glaubt und rechtschaffen handelt. Aber es wird nur den Standhaften dargeboten." });
                        surah28.VerseList.Add(new Verse { Number = 81, TextBosnian = "I Mi smo i njega i dvorac njegov u zemlju utjerali, i nikakva ga grupa od Allaha nije mogla odbraniti, a ni sam sebi nije mogao pomoći.", TextEnglish = "And We caused the earth to swallow him and his home. And there was for him no company to aid him other than Allāh, nor was he of those who [could] defend themselves.", TextGerman = "Da ließen Wir mit ihm und mit seiner Wohnstätte die Erde versinken. Und da hatte er weder eine Schar, die ihm vor Allah half, noch konnte er sich selbst helfen." });
                        surah28.VerseList.Add(new Verse { Number = 82, TextBosnian = "A oni koji su ranije priželjkivali da su na njegovom mjestu, stadoše govoriti: 'Zar ne vidite da Allah daje obilje onom od robova Svojih kome On hoće, a i da uskraćuje?! Da nam Allah nije milost Svoju ukazao i nas bi u zemlju utjerao! Zar ne vidite da nevjernici nikad neće uspjeti?!'", TextEnglish = "And those who had wished for his position the previous day began to say, 'Oh, how Allāh extends provision to whom He wills of His servants and restricts it! If not that Allāh had conferred favor on us, He would have caused it to swallow us. Oh, how the disbelievers do not succeed!'", TextGerman = "Und diejenigen, die sich am Tag zuvor (an) seine(r) Stelle (zu sein) gewünscht hatten, begannen zu sagen: „Ah sieh! Allah gewährt die Versorgung großzügig, wem von Seinen Dienern Er will, und bemißt auch. Wenn Allah uns nicht eine Wohltat erwiesen hätte, hätte Er uns wahrlich (auch) versinken lassen. Ah sieh! Den Ungläubigen wird es nicht wohl ergehen." });
                        surah28.VerseList.Add(new Verse { Number = 83, TextBosnian = "Taj Dom ahiretski dat ćemo onima koji ne žele da se na Zemlji ohole i da nered čine, a one koji se Allaha boje čeka sretan kraj.", TextEnglish = "That home of the Hereafter We assign to those who do not desire exaltedness upon the earth or corruption. And the [best] outcome is for the righteous.", TextGerman = "Das ist die jenseitige Wohnstätte. Wir bestimmen sie für diejenigen, die weder Überheblichkeit auf der Erde noch Unheil begehren. Und das (gute) Ende gehört den Gottesfürchtigen." });
                        surah28.VerseList.Add(new Verse { Number = 84, TextBosnian = "Onaj ko učini dobro djelo, dobit će bolju nagradu za njega, a onaj ko uradi zlo, pa oni koji budu zlo radili, bit će kažnjeni samo u srazmjeri s onim što su radili.", TextEnglish = "Whoever comes [on the Day of Judgement] with a good deed will have better than it; and whoever comes with an evil deed - then those who did evil deeds will not be recompensed except [as much as] what they used to do.", TextGerman = "Wer mit einer guten Tat kommt, für den wird es etwas Besseres als sie geben. Wer aber mit einer bösen Tat kommt, so wird denjenigen, die böse Taten begehen, nur das vergolten, was sie zu tun pflegten." });
                        surah28.VerseList.Add(new Verse { Number = 85, TextBosnian = "Onaj Koji ti je Kur'an u dužnost stavio sigurno će te na povratište vratiti. Reci: 'Gospodar moj najbolje zna onoga ko je Uputu donio, a ko je u očitoj zabludi. '", TextEnglish = "Indeed, [O Muḥammad], He who imposed upon you the Qur’ān will take you back to a place of return.[1120] Say, 'My Lord is most knowing of who brings guidance and who is in clear error.'", TextGerman = "Derjenige, Der dir den Qur’an verpflichtend gemacht hat, wird dich sicher zu einem Ort der Wiederkehr zurückkehren lassen. Sag: Mein Herr weiß besser, wer die Rechtleitung bringt und wer sich in deutlichem Irrtum befindet." });
                        surah28.VerseList.Add(new Verse { Number = 86, TextBosnian = "Ti nisi očekivao da će ti Knjiga biti objavljena, ali ona ti je objavljena kao milost Gospodara tvoga, zato nikako ne budi nevjernicima pomagač!", TextEnglish = "And you were not expecting that the Book would be conveyed to you, but [it is] a mercy from your Lord. So do not be an assistant to the disbelievers.[1121]", TextGerman = "Und du hattest nicht erwartet, daß das Buch an dich gerichtet würde. Es ist nur aus Barmherzigkeit von deinem Herrn. Darum leiste den Ungläubigen ja keinen Beistand." });
                        surah28.VerseList.Add(new Verse { Number = 87, TextBosnian = "I neka te oni nikako ne odvrate od Allahovih ajeta, kada ti se objave, i pozivaj Gospodaru svome i nikako ne budi iz reda višebožaca!", TextEnglish = "And never let them avert you from the verses of Allāh after they have been revealed to you. And invite [people] to your Lord. And never be of those who associate others with Allāh.", TextGerman = "Und lasse sie dich nur nicht von den Zeichen Allahs abhalten, nachdem sie nun zu dir herabgesandt worden sind. Und rufe zu deinem Herrn, und gehöre ja nicht zu den Götzendienern." });
                        surah28.VerseList.Add(new Verse { Number = 88, TextBosnian = "I ne moli, pored Allaha, drugog boga! Nema boga osim Njega! Sve će, osim Njegovog lica, propasti! Njemu sud pripada i Njemu ćete se vratiti!", TextEnglish = "And do not invoke with Allāh another deity. There is no deity except Him. Everything will be destroyed except His Face.[1122] His is the judgement, and to Him you will be returned.", TextGerman = "Und rufe neben Allah keinen anderen Gott an. Es gibt keinen Gott außer Ihm. Alles wird untergehen - außer Seinem Angesicht. Ihm gehört das Urteil, und zu Ihm werdet ihr zurückgebracht." });
                        result = surah28;
                        break;
                    #endregion

                    // 
                    #region
                    case 29:
                        Surah surah29 = new Surah(); surah29.Name = Constants.Surah29Name; surah29.Number = 29; surah29.VerseList = new List<Verse>();
                        surah29.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah29.VerseList.Add(new Verse { Number = 1, TextBosnian = "Elif-lam-mim.", TextEnglish = "Alif, Lām, Meem.[1124]", TextGerman = "Alif-Lam-Mim." });
                        surah29.VerseList.Add(new Verse { Number = 2, TextBosnian = "Zar misle ljudi da će ako govore: 'Mi vjerujemo!' biti ostavljeni a da iskušavani neće biti?!", TextEnglish = "Do the people think that they will be left to say, 'We believe' and they will not be tried?", TextGerman = "Meinen die Menschen, daß sie in Ruhe gelassen werden, (nur) weil sie sagen: „Wir glauben, ohne daß sie geprüft werden?" });
                        surah29.VerseList.Add(new Verse { Number = 3, TextBosnian = "A mi smo iskušavali i one prije njih, pa će Allah, sigurno, znati one koji govore istinu i one koji lažu.", TextEnglish = "But We have certainly tried those before them, and Allāh will surely make evident those who are truthful, and He will surely make evident the liars.", TextGerman = "Wir haben bereits diejenigen vor ihnen geprüft. Allah wird ganz gewiß diejenigen kennen, die die Wahrheit sprechen, und Er wird ganz gewiß die Lügner kennen." });
                        surah29.VerseList.Add(new Verse { Number = 4, TextBosnian = "Zar misle oni koji zla djela rade daće Nama umaći?! Loše li je ono što prosuđuju!", TextEnglish = "Or do those who do evil deeds think they can outrun [i.e., escape] Us? Evil is what they judge.", TextGerman = "Oder meinen diejenigen, die böse Taten begehen, daß sie Uns entkommen (können)? Wie böse ist, was sie urteilen!" });
                        surah29.VerseList.Add(new Verse { Number = 5, TextBosnian = "A ko se nada susretu s Allahom, pa od Allaha određeno vrijeme će doći, a On je Svečujući i Sveznajući!", TextEnglish = "Whoever should hope for the meeting with Allāh - indeed, the term [decreed by] Allāh is coming. And He is the Hearing, the Knowing.", TextGerman = "Wer die Begegnung mit Allah erwartet-, so wird Allahs Frist sicher kommen. Und Er ist der Allhörende und Allwissende." });
                        surah29.VerseList.Add(new Verse { Number = 6, TextBosnian = "A onaj ko se bori - bori se samo za sebe, doista, Allah je neovisan od svih svjetova.", TextEnglish = "And whoever strives only strives for [the benefit of] himself. Indeed, Allāh is Free from need of the worlds.", TextGerman = "Und wer sich abmüht, der müht sich nur zu seinem eigenen Vorteil ab, denn Allah ist der Weltenbewohner fürwahr unbedürftig." });
                        surah29.VerseList.Add(new Verse { Number = 7, TextBosnian = "Preći ćemo, doista, preko loših postupaka onih koji vjeruju i dobra djela čine i prema onom boljem što su radili sigurno ćemo ih nagraditi.", TextEnglish = "And those who believe and do righteous deeds - We will surely remove from them their misdeeds and will surely reward them according to the best of what they used to do.", TextGerman = "Denjenigen, die glauben und rechtschaffene Werke tun, werden Wir ganz gewiß ihre bösen Taten tilgen und ihnen ganz gewiß das Beste vergelten von dem, was sie taten." });
                        surah29.VerseList.Add(new Verse { Number = 8, TextBosnian = "I Mi smo svakog čovjeka zadužili da čini dobro roditeljima svojim. Ali, ako se oni oko tebe budu trudili kako bi Meni bilo šta pridružio, o čemu ti nemaš znanja, onda im se ne pokoravaj. Meni ćete se vratiti, pa ću vas Ja o onome što ste radili obavijestiti.", TextEnglish = "And We have enjoined upon man goodness to parents. But if they endeavor to make you associate with Me that of which you have no knowledge,[1125] do not obey them. To Me is your return, and I will inform you about what you used to do.", TextGerman = "Und Wir haben dem Menschen anbefohlen, seine Eltern mit Güte zu behandeln. Wenn sie sich aber darum bemühen, daß Du Mir das beigesellst, wovon du kein Wissen hast, dann gehorche ihnen nicht. Zu Mir wird eure Rückkehr sein, da werde Ich euch kundtun, was ihr zu tun pflegtet." });
                        surah29.VerseList.Add(new Verse { Number = 9, TextBosnian = "One koji vjeruju i dobra djela čine sigurno ćemo među one koji su dobri uvrstiti.", TextEnglish = "And those who believe and do righteous deeds - We will surely admit them among the righteous [into Paradise].", TextGerman = "Diejenigen, die glauben und rechtschaffene Werke tun, lassen Wir in die Reihen der Rechtschaffenen eingehen." });
                        surah29.VerseList.Add(new Verse { Number = 10, TextBosnian = "Ima ljudi koji govore: 'Vjerujemo u Allaha!', a kad neki od njih radi Allaha bude na muke stavljen, on drži da je iskušenje od ljudi isto kao i Allahova kazna. A ako pobjeda dođe od Gospodara tvoga, sigurno će oni reći: 'Bili smo uz vas!' A zar Allah ne zna najbolje ono što je u grudima svačijim?", TextEnglish = "And of the people are some who say, 'We believe in Allāh,' but when one [of them] is harmed for [the cause of] Allāh, he considers the trial [i.e., harm] of the people as [if it were] the punishment of Allāh. But if victory comes from your Lord, they say, 'Indeed, We were with you.' Is not Allāh most knowing of what is within the breasts of the worlds [i.e., all creatures]?", TextGerman = "Und unter den Menschen gibt es manchen, der sagt: „Wir glauben an Allah. Wenn ihm aber um Allahs willen Leid zugefügt wird, setzt er die Anfechtung durch die Menschen der Strafe Allahs gleich. Wenn jedoch Hilfe von deinem Herrn kommt, sagen sie ganz gewiß: „Wir sind ja mit euch gewesen. Weiß Allah denn nicht besser Bescheid über das, was in den Brüsten der Weltenbewohner steckt?" });
                        surah29.VerseList.Add(new Verse { Number = 11, TextBosnian = "Allah će sigurno znati one koji vjeruju i sigurno će znati one koji su dvolični.", TextEnglish = "And Allāh will surely make evident those who believe, and He will surely make evident the hypocrites.", TextGerman = "Und Allah wird ganz gewiß diejenigen kennen, die glauben, und Er wird ganz gewiß die Heuchler kennen." });
                        surah29.VerseList.Add(new Verse { Number = 12, TextBosnian = "Oni koji ne vjeruju govore onima koji vjeruju: 'Slijedite naš put, a mi ćemo nositi grijehe vaše!', a ne bi ponijeli nijedan grijeh njihov. Oni su, zaista, lažljivci.", TextEnglish = "And those who disbelieve say to those who believe, 'Follow our way, and we will carry your sins.'[1126] But they will not carry anything of their sins. Indeed, they are liars.", TextGerman = "Und diejenigen, die ungläubig sind, sagen zu denjenigen, die glauben: „Folgt unserem Weg. Laßt uns eure Verfehlungen tragen. Tragen werden sie aber nichts von ihren Verfehlungen, denn sie sind fürwahr Lügner." });
                        surah29.VerseList.Add(new Verse { Number = 13, TextBosnian = "A sigurno će vlastite terete i terete onih koje su u zabludu odveli nositi, i sigurno će za ono što su izmišljali na Kijametskom danu odgovarati.", TextEnglish = "But they will surely carry their [own] burdens and [other] burdens along with their burdens,[1127] and they will surely be questioned on the Day of Resurrection about what they used to invent.", TextGerman = "Ganz gewiß werden sie ihre (eigenen) Lasten tragen, und auch (weitere) Lasten zu ihren (eigenen) Lasten hinzu. Und sie werden am Tag der Auferstehung ganz gewiß befragt werden nach dem, was sie zu ersinnen pflegten." });
                        surah29.VerseList.Add(new Verse { Number = 14, TextBosnian = "Mi smo Nuha narodu njegovu poslali i on je među njima ostao hiljadu, manje pedeset, godina, pa ih je potom zadesio potop, a bili su zulumćari.", TextEnglish = "And We certainly sent Noah to his people, and he remained among them a thousand years minus fifty years, and the flood seized them while they were wrongdoers.", TextGerman = "Und Wir sandten bereits Nuh zu seinem Volk. Er verweilte unter ihnen tausend Jahre weniger fünfzig Jahre. Da ergriff sie die Überschwemmung, während sie Unrecht taten." });
                        surah29.VerseList.Add(new Verse { Number = 15, TextBosnian = "I Mi smo njega i one što su bili u lađi - spasili, i znakom je svjetovima učinili.", TextEnglish = "But We saved him and the companions of the ship, and We made it[1128] a sign for the worlds.", TextGerman = "Da retteten Wir ihn und die Insassen des Schiffes und machten es zu einem Zeichen für die Weltenbewohner." });
                        surah29.VerseList.Add(new Verse { Number = 16, TextBosnian = "A sjeti se i Ibrahima, kad je rekao narodu svome: 'Allahu ibadet činite i Njega se bojte, to vam je bolje, ako znate!'", TextEnglish = "And [We sent] Abraham, when he said to his people, 'Worship Allāh and fear Him. That is best for you, if you should know.", TextGerman = "Und (Wir sandten) Ibrahim. Als er zu seinem Volk sagte: „Dient Allah und fürchtet Ihn. Das ist besser für euch, wenn ihr (es) nur wißt." });
                        surah29.VerseList.Add(new Verse { Number = 17, TextBosnian = "Vi, mimo Allaha, kumire obožavate i laži smišljate. Oni koje vi, mimo Allaha, obožavate ne mogu vas nikakvom opskrbom opskrbiti; vi opskrbu od Allaha tražite i Njemu ibadet činite i Njemu zahvalni budite! Njemu ćete se vratiti.", TextEnglish = "You only worship, besides Allāh, idols, and you produce a falsehood. Indeed, those you worship besides Allāh do not possess for you [the power of] provision. So seek from Allāh provision and worship Him and be grateful to Him. To Him you will be returned.'", TextGerman = "Ihr dient anstatt Allahs nur Götzen und schafft (dabei nur) ungeheuerliche Lüge. Gewiß, diejenigen, denen ihr anstatt Allahs dient, vermögen euch nicht zu versorgen. Sucht darum bei Allah die Versorgung und dient Ihm und dankt Ihm. Zu Ihm werdet ihr zurückgebracht." });
                        surah29.VerseList.Add(new Verse { Number = 18, TextBosnian = "A ako vi poričete, pa već su narodi prije vas poricali; a poslanik je jedino dužan da jasno obznani. '", TextEnglish = "And if you [people] deny [the message] - already nations before you have denied. And there is not upon the Messenger except [the duty of] clear notification.[1129]", TextGerman = "Und wenn ihr (die Botschaft) für Lüge erklärt, so haben bereits vor euch (andere) Gemeinschaften (sie) für Lüge erklärt. Und dem Gesandten obliegt nur die deutliche Übermittlung (der Botschaft)." });
                        surah29.VerseList.Add(new Verse { Number = 19, TextBosnian = "Zar oni ne vide kako Allah započinje stvaranje?! On će to opet učiniti; Allahu je to, zaista, lahko.", TextEnglish = "Have they not considered how Allāh begins creation and then repeats it? Indeed that, for Allāh, is easy.", TextGerman = "Haben sie denn nicht gesehen, wie Allah die Schöpfung am Anfang macht und sie hierauf wiederholt? Gewiß, das ist für Allah ein leichtes." });
                        surah29.VerseList.Add(new Verse { Number = 20, TextBosnian = "Reci: 'Putujte po Zemlji pa da vidite kako je On započeo stvaranje! Potom će Allah stvaranje ponoviti. Allah, zaista, nad svime ima moć.'", TextEnglish = "Say, [O Muḥammad], 'Travel through the land and observe how He began creation. Then Allāh will produce the final creation [i.e., development]. Indeed Allāh, over all things, is competent.'", TextGerman = "Sag: Reist auf der Erde umher und schaut, wie Er die Schöpfung am Anfang gemacht hat. Hierauf läßt Allah die letzte Schöpfung entstehen. Gewiß, Allah hat zu allem die Macht." });
                        surah29.VerseList.Add(new Verse { Number = 21, TextBosnian = "On patnji podvrgava onoga koga On hoće i milost ukazuje onome kome On hoće, Njemu ćete biti vraćeni.", TextEnglish = "He punishes whom He wills and has mercy upon whom He wills, and to Him you will be returned.", TextGerman = "Er straft, wen Er will, und Er erbarmt Sich, wessen Er will. Und zu Ihm werdet ihr zurückgebracht." });
                        surah29.VerseList.Add(new Verse { Number = 22, TextBosnian = "A vi nećete umaći ni na Zemlji ni na nebu, i nemate, mimo Allaha, ni pomagača ni zaštitnika.", TextEnglish = "And you will not cause failure [to Allāh] upon the earth or in the heaven. And you have not other than Allāh any protector or any helper.", TextGerman = "Und ihr werdet euch (Ihm) weder auf der Erde noch im Himmel entziehen können, und ihr habt außer Allah weder Schutzherrn noch Helfer." });
                        surah29.VerseList.Add(new Verse { Number = 23, TextBosnian = "A oni koji ne vjeruju u Allahove ajete i znakove i u susret s Njim - to su oni koji su izgubili nadu u Moju milost i to su oni kojima pripada patnja bolna.", TextEnglish = "And the ones who disbelieve in the signs of Allāh and the meeting with Him - those have despaired of My mercy, and they will have a painful punishment.", TextGerman = "Diejenigen, die Allahs Zeichen und die Begegnung mit Ihm ableugnen, diese haben die Hoffnung auf Meine Barmherzigkeit aufgegeben, und für diese wird es schmerzhafte Strafe geben." });
                        surah29.VerseList.Add(new Verse { Number = 24, TextBosnian = "Odgovor naroda njegova bijaše samo to što rekoše: 'Ubijte ga ili ga spalite!', ali, Allah ga je iz vatre izbavio. U tome su, zaista, znakovi za narod koji vjeruje.", TextEnglish = "And the answer of his [i.e., Abraham's] people was not but that they said, 'Kill him or burn him,' but Allāh saved him from the fire. Indeed in that are signs for a people who believe.", TextGerman = "Aber die Antwort seines Volkes war nur, daß sie sagten: „Tötet ihn oder verbrennt ihn. Da rettete Allah ihn aus dem Feuer. Darin sind wahrlich Zeichen für Leute, die glauben." });
                        surah29.VerseList.Add(new Verse { Number = 25, TextBosnian = "Vi ste', reče on, 'mimo Allaha kumire prihvatili da biste u životu dunjalučkom međusobne prijateljske odnose održavali, a poslije, na Kijametskom danu, jedni drugih ćete se odricati i jedni druge ćete proklinjati, Vatra će vaše boravište biti, i vi nikakvih pomagača nećete imati.'", TextEnglish = "And [Abraham] said, 'You have only taken, other than Allāh, idols as [a bond of] affection among you in worldly life. Then on the Day of Resurrection you will deny one another and curse one another, and your refuge will be the Fire, and you will not have any helpers.'", TextGerman = "Und er sagte: „Ihr habt euch ja anstatt Allahs nur Götzen genommen aus Freundschaft zueinander im diesseitigen Leben. Aber dereinst, am Tag der Auferstehung, werdet ihr einander verleugnen und einander verfluchen. Euer Zufluchtsort ist das (Höllen)feuer, und ihr werdet keine Helfer haben." });
                        surah29.VerseList.Add(new Verse { Number = 26, TextBosnian = "I Lut mu jedini povjerova! A Ibrahim reče: 'Ja se, uistinu, selim Gospodaru svome. On je, baš, Silni i Mudri.'", TextEnglish = "And Lot believed him. [Abraham] said, 'Indeed, I will emigrate to [the service of] my Lord. Indeed, He is the Exalted in Might, the Wise.'", TextGerman = "Da glaubte Lut ihm und sagte: „Ich werde zu meinem Herrn auswandern. Er ist ja der Allmächtige und Allweise." });
                        surah29.VerseList.Add(new Verse { Number = 27, TextBosnian = "I Mi smo mu Ishaka i Jakuba podarili i među potomcima njegovim vjerovjesništvo i Knjigu dali; njemu smo na dunjaluku njegovu nagradu dali, a na Ahiretu će, doista, jedan od dobrih biti.", TextEnglish = "And We gave to him Isaac and Jacob and placed in his descendants prophethood and scripture. And We gave him his reward in this world, and indeed, he is in the Hereafter among the righteous.", TextGerman = "Und Wir schenkten ihm Ishaq und Ya’qub und richteten unter seiner Nachkommenschaft das Prophetentum und die Schrift ein und gaben ihm seinen Lohn im Diesseits. Und im Jenseits gehört er fürwahr zu den Rechtschaffenen." });
                        surah29.VerseList.Add(new Verse { Number = 28, TextBosnian = "I sjeti se Luta. Kada narodu svome reče: 'Vi činite takav razvrat kakav prije vas niko od svih svjetova nije činio:", TextEnglish = "And [mention] Lot, when he said to his people, 'Indeed, you commit such immorality as no one has preceded you with from among the worlds.", TextGerman = "Und (Wir sandten) Lut. Als er zu seinem Volk sagte: „Ihr begeht fürwahr das Abscheuliche, wie es vor euch niemand von den Weltenbewohnern getan hat." });
                        surah29.VerseList.Add(new Verse { Number = 29, TextBosnian = "s muškarcima općite, po drumovima presrećete i na sastajalištima svojim ono što je odvratno činite.' A odgovor naroda njegova bijaše samo to što rekoše: 'Učini da nas Allahova kazna stigne ako si od onih koji istinu govore!'", TextEnglish = "Indeed, you approach men and obstruct the road[1130] and commit in your meetings [every] evil.' And the answer of his people was not but that they said, 'Bring us the punishment of Allāh, if you should be of the truthful.'", TextGerman = "Laßt ihr euch denn wahrlich (in Begierde) mit den Männern ein, und schneidet den (natürlichen) Weg ab und begeht in euren Zusammenkünften das Verwerfliche? Aber die Antwort seines Volkes war nur, daß sie sagten: „Bringe uns doch die Strafe Allahs her, wenn du zu den Wahrhaftigen gehörst." });
                        surah29.VerseList.Add(new Verse { Number = 30, TextBosnian = "Gospodaru moj', reče on, 'pomozi mi protiv naroda koji nered čini!'", TextEnglish = "He said, 'My Lord, support me against the corrupting people.'", TextGerman = "Er sagte: „Mein Herr, hilf mir gegen das Volk, das Unheil stiftet." });
                        surah29.VerseList.Add(new Verse { Number = 31, TextBosnian = "I kad izaslanici Naši Ibrahimu radosnu vijest donesoše, rekoše: 'Mi ćemo uništiti stanovnike onoga naselja! Njegovi stanovnici zaista su zulumćari! '", TextEnglish = "And when Our messengers [i.e., angels] came to Abraham with the good tidings,[1131] they said, 'Indeed, we will destroy the people of that [i.e., Lot's] city. Indeed, its people have been wrongdoers.'", TextGerman = "Und als Unsere Gesandten zu Ibrahim mit der frohen Botschaft kamen, sagten sie: „Wir werden die Bewohner dieser Stadt vernichten, denn ihre Bewohner sind ungerecht." });
                        surah29.VerseList.Add(new Verse { Number = 32, TextBosnian = "U njemu je Lut'', reče Ibrahim. 'Mi dobro znamo ko je u njemu', rekoše oni, 'mi ćemo njega i porodicu njegovu spasiti, osim žene njegove. Ona će ostati s onima koji će kaznu iskusiti.'", TextEnglish = "[Abraham] said, 'Indeed, within it is Lot.' They said, 'We are more knowing of who is within it. We will surely save him and his family, except his wife. She is to be of those who remain behind.'", TextGerman = "Er sagte: Aber Lut befindet sich in ihr. Sie sagten: Wir wissen sehr wohl, wer sich in ihr befindet. Wir werden ihn ganz gewiß erretten, (ihn) und seine Angehörigen, außer seiner Frau; sie gehört zu denjenigen, die zurückbleiben." });
                        surah29.VerseList.Add(new Verse { Number = 33, TextBosnian = "I kad izaslanici Naši dođoše Lutu, on se zbog njih sneveseli i uznemiri. 'Ne boj se i ne brini se', rekoše oni, 'mi ćemo tebe i porodicu spasiti, osim žene tvoje, ona će ostati s onima koji će kaznu iskusiti.", TextEnglish = "And when Our messengers [i.e., angels] came to Lot, he was distressed for them and felt for them great discomfort.[1132] They said, 'Fear not, nor grieve. Indeed, we will save you and your family, except your wife; she is to be of those who remain behind.", TextGerman = "Und als nun Unsere Gesandten zu Lut kamen, geriet er ihretwegen in eine böse Lage und war durch ihre Anwesenheit beklommen. Sie sagten: „Fürchte dich nicht und sei nicht traurig, denn wir werden dich erretten, (dich) und deine Angehörigen, außer deiner Frau; sie gehört zu denjenigen, die zurückbleiben." });
                        surah29.VerseList.Add(new Verse { Number = 34, TextBosnian = "A na stanovnike ovog grada spustit ćemo strašnu kaznu s neba zbog toga što su buntovni bili.", TextEnglish = "Indeed, we will bring down on the people of this city punishment from the sky because they have been defiantly disobedient.'", TextGerman = "Wir werden auf die Bewohner dieser Stadt eine unheilvolle Strafe vom Himmel herabkommen lassen dafür, daß sie gefrevelt haben," });
                        surah29.VerseList.Add(new Verse { Number = 35, TextBosnian = "I od njega smo ostavili jasan znak ljudima koji razumiju.'", TextEnglish = "And We have certainly left of it a sign as clear evidence for a people who use reason.", TextGerman = "Und Wir ließen ja von ihr ein klares Zeichen zurück für Leute, die begreifen." });
                        surah29.VerseList.Add(new Verse { Number = 36, TextBosnian = "A u Medjen poslasmo brata njihova Šuajba: 'O narode moj', govorio je on, 'Allahu ibadet činite, i Posljednjem danu se nadajte, i ne činite zlo po Zemlji nered praveći!'", TextEnglish = "And to Madyan [We sent] their brother Shuʿayb, and he said, 'O my people, worship Allāh and expect the Last Day and do not commit abuse on the earth, spreading corruption.'", TextGerman = "Und (Wir sandten) zu Madyan ihren Bruder Su’aib. Da sagte er: „O mein Volk, dient Allah, erwartet den Jüngsten Tag und richtet auf der Erde nicht unheilstiftend Verderben an." });
                        surah29.VerseList.Add(new Verse { Number = 37, TextBosnian = "Ali mu oni ne povjerovaše, pa ih zadesi strašan potres i oni u svojoj domovini osvanuše mrtvi, nepomični.", TextEnglish = "But they denied him, so the earthquake seized them, and they became within their home [corpses] fallen prone.", TextGerman = "Aber sie bezichtigten ihn der Lüge. Da ergriff sie das Zittern, und am Morgen lagen sie in ihrer Wohnstätte auf den Brüsten da." });
                        surah29.VerseList.Add(new Verse { Number = 38, TextBosnian = "A i Ada i Semuda - ostaci domova njihovih vidljivi su vam - a šejtan im je lijepim postupke njihove predočio, pa ih, od Pravog puta odvratio, a oni su razumni bili.", TextEnglish = "And [We destroyed] ʿAad and Thamūd, and it has become clear to you from their [ruined] dwellings. And Satan had made pleasing to them their deeds and averted them from the path, and they were endowed with perception.", TextGerman = "Und (erwähne auch) die ’Ad und die Tamud. (Ihr Untergang) ist euch ja deutlich geworden an ihren Wohnorten. Der Satan schmückte ihnen ihre Taten aus und hielt sie so vom Weg ab, obwohl sie Einsicht besaßen." });
                        surah29.VerseList.Add(new Verse { Number = 39, TextBosnian = "I Karuna i faraona i Hamana: Musa im je jasne dokaze donio, ali su se oni na Zemlji oholili i nisu mogli umaći,", TextEnglish = "And [We destroyed] Qārūn and Pharaoh and Hāmān. And Moses had already come to them with clear evidences, and they were arrogant in the land, but they were not outrunners [of Our punishment].", TextGerman = "Und (erwähne weiterhin) Qarun und Fir’aun und Haman. Musa kam ja zu ihnen mit den klaren Beweisen. Aber sie verhielten sich hochmütig auf der Erde; doch konnten sie (Uns) nicht zuvorkommen." });
                        surah29.VerseList.Add(new Verse { Number = 40, TextBosnian = "i sve smo prema grijesima njihovim kaznili: na neke vjetar pun pijeska poslali, a neke strašnim glasom uništili, neke u zemlju utjerali, a neke potopili. Allah im nije zulum učinio, sami su sebi zulum nanijeli", TextEnglish = "So each We seized for his sin; and among them were those upon whom We sent a storm of stones, and among them were those who were seized by the blast [from the sky], and among them were those whom We caused the earth to swallow, and among them were those whom We drowned. And Allāh would not have wronged them, but it was they who were wronging themselves.", TextGerman = "Einen jeden ergriffen Wir für seine Sünde; so sandten Wir gegen einige von ihnen einen Sturm von Steinchen, andere ergriff der Schrei, mit anderen ließen Wir die Erde versinken, andere ließen Wir ertrinken. Und nimmer ist es Allah, der ihnen Unrecht getan hat, sondern sie selbst haben sich Unrecht zugefügt." });
                        surah29.VerseList.Add(new Verse { Number = 41, TextBosnian = "Oni koji, mimo Allaha, zaštitnike uzimaju slični su pauku koji sebi isplete kuću, a najslabija kuća, zasigurno, je paukova kuća, kad bi samo znali.", TextEnglish = "The example of those who take allies other than Allāh is like that of the spider who takes [i.e., constructs] a home. And indeed, the weakest of homes is the home of the spider, if they only knew.", TextGerman = "Das Gleichnis derjenigen, die sich anstatt Allahs Schutzherren nehmen, ist das der Spinne, die sich ein Haus genommen hat; das schwächste Haus ist fürwahr das Haus der Spinne, wenn sie (es) nur wüßten!" });
                        surah29.VerseList.Add(new Verse { Number = 42, TextBosnian = "Allah, zaista, zna sve ono što oni mimo Njega zazivaju, a On je Silni i Mudri.", TextEnglish = "Indeed, Allāh knows whatever thing they call upon[1133] other than Him. And He is the Exalted in Might, the Wise.", TextGerman = "Gewiß, Allah weiß all das, was sie anstatt Seiner anrufen. Und Er ist der Allmächtige und Allweise." });
                        surah29.VerseList.Add(new Verse { Number = 43, TextBosnian = "To su primjeri koje Mi ljudima navodimo, ali ih samo učeni razumiju.", TextEnglish = "And these examples We present to the people, but none will understand them except those of knowledge.", TextGerman = "Diese Gleichnisse prägen Wir für die Menschen. Aber nur diejenigen verstehen sie, die Wissen besitzen." });
                        surah29.VerseList.Add(new Verse { Number = 44, TextBosnian = "Allah je nebesa i Zemlju s Istinom stvorio; u tome je, zaista, znak za vjernike.", TextEnglish = "Allāh created the heavens and the earth in truth. Indeed in that is a sign for the believers.", TextGerman = "Allah hat die Himmel und die Erde in Wahrheit erschaffen. Darin ist wahrlich ein Zeichen für die Gläubigen." });
                        surah29.VerseList.Add(new Verse { Number = 45, TextBosnian = "Kazuj Knjigu koja ti se objavljuje i obavljaj namaz - namaz, zaista, odvraća od razvrata i od svega što je ružno; a Allahov spomen na vas veći je od vašeg spomena na Njega! A Allah zna šta činite!", TextEnglish = "Recite, [O Muḥammad], what has been revealed to you of the Book and establish prayer. Indeed, prayer prohibits immorality and wrongdoing, and the remembrance of Allāh is greater. And Allāh knows that which you do.", TextGerman = "Verlies, was dir vom Buch (als Offenbarung) eingegeben wird, und verrichte das Gebet. Gewiß, das Gebet hält davon ab, das Schändliche und das Verwerfliche (zu tun). Und das Gedenken Allahs ist wahrlich größer. Und Allah weiß, was ihr macht." });
                        surah29.VerseList.Add(new Verse { Number = 46, TextBosnian = "I sa sljedbenicima Knjige raspravljajte samo na najljepši način - osim onih među njima koji su zulum učinili - i recite: 'Mi vjerujemo u ono što se objavljuje nama i u ono što je objavljeno vama, a naš Bog i vaš Bog - jedan je i Njemu smo mi predani!'", TextEnglish = "And do not argue with the People of the Scripture except in a way that is best, except for those who commit injustice among them, and say, 'We believe in that which has been revealed to us and revealed to you. And our God and your God is one; and we are Muslims [in submission] to Him.'", TextGerman = "Und streitet mit den Leuten der Schrift nur in bester Weise, außer denjenigen von ihnen, die Unrecht tun. Und sagt: „Wir glauben an das, was (als Offenbarung) zu uns herabgesandt worden ist und zu euch herabgesandt worden ist; unser Gott und euer Gott ist Einer, und wir sind Ihm ergeben’." });
                        surah29.VerseList.Add(new Verse { Number = 47, TextBosnian = "Tako isto i tebi objavljujemo Knjigu, a neki od onih kojima smo već dali Knjigu-i u nju vjeruju, a i među ovima ima onih koji u nju vjeruju, a ajete i dokaze Naše samo nevjernici negiraju.", TextEnglish = "And thus We have sent down to you the Book [i.e., the Qur’ān]. And those to whom We [previously] gave the Scripture believe in it. And among these [people of Makkah] are those who believe in it. And none reject Our verses except the disbelievers.", TextGerman = "Und so haben Wir das Buch zu dir hinabgesandt. Diejenigen, denen Wir die Schrift gaben, glauben daran. Und auch unter diesen da gibt es manche, die daran glauben. Nur die Ungläubigen verleugnen Unsere Zeichen." });
                        surah29.VerseList.Add(new Verse { Number = 48, TextBosnian = "Ti prije nje nijednu knjigu nisi čitao, a nisi je ni desnom rukom svojom pisao, inače, posumnjali bi oni što laži govore.", TextEnglish = "And you did not recite before it any scripture, nor did you inscribe one with your right hand. Then [i.e., otherwise] the falsifiers would have had [cause for] doubt.", TextGerman = "Und du hast vordem kein Buch verlesen und es auch nicht mit deiner rechten Hand niedergeschrieben. Sonst würden wahrlich diejenigen zweifeln, die (es) für falsch erklären." });
                        surah29.VerseList.Add(new Verse { Number = 49, TextBosnian = "Međutim, on - Kur'an je sačinjen od ajeta jasnih; u grudima su onih kojima je znanje dato, a Naše ajete samo zulumćari negiraju,", TextEnglish = "Rather, it [i.e., the Qur’ān] is distinct verses [preserved] within the breasts of those who have been given knowledge. And none reject Our verses except the wrongdoers.", TextGerman = "Nein! Vielmehr sind es klare Zeichen in den Brüsten derjenigen, denen das Wissen gegeben worden ist. Und nur die Ungerechten verleugnen Unsere Zeichen." });
                        surah29.VerseList.Add(new Verse { Number = 50, TextBosnian = "i govore: 'Zašto mu od Gospodara njegova nisu neki znaci poslani?' Reci: 'Znaci su jedino u Allaha, a ja sam samo upozoritelj jasni.'", TextEnglish = "But they say, 'Why are not signs sent down to him from his Lord?' Say, 'The signs are only with Allāh, and I am only a clear warner.'", TextGerman = "Und sie sagen: „Wenn doch Zeichen von seinem Herrn auf ihn herabgesandt würden! Sag: Über die Zeichen verfügt (allein) Allah . Und ich bin nur ein deutlicher Warner." });
                        surah29.VerseList.Add(new Verse { Number = 51, TextBosnian = "A zar im nije dosta to što Mi tebi objavljujemo Knjigu koja im se kazuje: u njoj je, doista, milost i pouka ljudima koji vjeruju.", TextEnglish = "And is it not sufficient for them that We revealed to you the Book [i.e., the Qur’ān] which is recited to them? Indeed in that is a mercy and reminder for a people who believe.", TextGerman = "Genügt es ihnen denn nicht, daß Wir das Buch auf dich hinabgesandt haben, das ihnen verlesen wird? Darin ist wahrlich eine Barmherzigkeit und eine Ermahnung für Leute, die glauben." });
                        surah29.VerseList.Add(new Verse { Number = 52, TextBosnian = "Reci: 'Allah je dovoljan svjedok meni i vama; On zna sve što je na nebesima i na Zemlji. A oni koji u laž vjeruju, a u Allaha ne vjeruju, oni su baš gubitnici.'", TextEnglish = "Say, 'Sufficient is Allāh between me and you as Witness. He knows what is in the heavens and earth. And they who have believed in falsehood and disbelieved in Allāh - it is those who are the losers.'", TextGerman = "Sag: Allah genügt zwischen mir und euch als Zeuge. Er weiß, was in den Himmeln und auf der Erde ist. Und diejenigen, die an das Falsche glauben und Allah verleugnen, das sind die Verlierer." });
                        surah29.VerseList.Add(new Verse { Number = 53, TextBosnian = "Oni traže od tebe da ih što prije stigne kazna. A da nije određenog roka, kazna bi im došla, a doći će im, sigurno, iznenada, a oni je neće predosjetiti.", TextEnglish = "And they urge you to hasten the punishment. And if not for [the decree of] a specified term, punishment would have reached them. But it will surely come to them suddenly while they perceive not.", TextGerman = "Und sie wünschen von dir, die Strafe zu beschleunigen. Wenn es nicht eine festgesetzte Frist gäbe, wäre die Strafe wahrlich zu ihnen gekommen. Aber ganz gewiß wird sie plötzlich über sie kommen, ohne daß sie merken." });
                        surah29.VerseList.Add(new Verse { Number = 54, TextBosnian = "Oni traže od tebe da ih što prije stigne kazna, a Džehennem će sigurno sve nevjernike skoliti", TextEnglish = "They urge you to hasten the punishment. And indeed, Hell will be encompassing of the disbelievers", TextGerman = "Sie wünschen von dir, die Strafe zu beschleunigen, doch wahrlich, die Hölle umfaßt die Ungläubigen," });
                        surah29.VerseList.Add(new Verse { Number = 55, TextBosnian = "na Dan kad ih patnja i odozgo i odozdo obuzme, a On rekne: 'Iskusite ono što ste radili!'", TextEnglish = "On the Day the punishment will cover them from above them and from below their feet and it is said, 'Taste [the result of] what you used to do.'", TextGerman = "am Tag, da die Strafe sie von oben und von unterhalb ihrer Füße her überdeckt, und Er sagt: „Kostet, was ihr zu tun pflegtet." });
                        surah29.VerseList.Add(new Verse { Number = 56, TextBosnian = "O robovi Moji koji vjerujete, Moja je Zemlja prostrana, zato samo Meni ibadet činite!", TextEnglish = "O My servants who have believed, indeed My earth is spacious, so worship only Me.", TextGerman = "O Meine Diener, die ihr glaubt, gewiß, Meine Erde ist weit. So dient Mir, ja, allein Mir." });
                        surah29.VerseList.Add(new Verse { Number = 57, TextBosnian = "Svaka duša smrt će okusiti, a onda ćete Nama biti vraćeni.", TextEnglish = "Every soul will taste death. Then to Us will you be returned.", TextGerman = "Jede Seele wird den Tod kosten. Hierauf werdet ihr zu Uns zurückgebracht." });
                        surah29.VerseList.Add(new Verse { Number = 58, TextBosnian = "I One koji budu vjerovali i dobra djela činili smjestit ćemo u džennetske odaje, ispod njih rijeke teku, u njima će vječno boraviti. Divna li će biti nagrada onima koji su se trudili,", TextEnglish = "And those who have believed and done righteous deeds - We will surely assign to them of Paradise [elevated] chambers beneath which rivers flow, wherein they abide eternally. Excellent is the reward of the [righteous] workers", TextGerman = "Denjenigen, die glauben und rechtschaffene Werke tun, werden Wir im (Paradies)garten ganz gewiß Obergemächer zuweisen, durcheilt von Bächen; ewig darin zu bleiben. Wie trefflich ist der Lohn derjenigen, die (gut) handeln," });
                        surah29.VerseList.Add(new Verse { Number = 59, TextBosnian = "onima koji su strpljivi bili i u Gospodara Svoga se uzdali!", TextEnglish = "Who have been patient and upon their Lord rely.", TextGerman = "die standhaft sind und sich auf ihren Herrn verlassen." });
                        surah29.VerseList.Add(new Verse { Number = 60, TextBosnian = "A koliko ima živih bića koja ne mogu skupljati opskrbu sebi, Allah ih opskrbljuje, a i vas! On je Svečujući i Svevideći.", TextEnglish = "And how many a creature carries not its [own] provision. Allāh provides for it and for you. And He is the Hearing, the Knowing.", TextGerman = "Und wie viele Tiere tragen ihre (eigene) Versorgung nicht herbei! Allah versorgt sie und euch. Und Er ist der Allhörende und Allwissende." });
                        surah29.VerseList.Add(new Verse { Number = 61, TextBosnian = "A da ih upitaš: 'Ko je nebesa i Zemlju stvorio i ko je Sunce i Mjesec potčinio?', sigurno bi rekli: 'Allah!', pa kuda se onda odmeću!?", TextEnglish = "If you asked them, 'Who created the heavens and earth and subjected the sun and the moon?' they would surely say, 'Allāh.' Then how are they deluded?", TextGerman = "Und wenn du sie fragst, wer die Himmel und die Erde erschaffen und die Sonne und den Mond dienstbar gemacht hat, sagen sie ganz gewiß: Allah. Wie lassen sie sich also abwendig machen?" });
                        surah29.VerseList.Add(new Verse { Number = 62, TextBosnian = "Allah u izobilju daje opskrbu onome kome hoće od robova Svojih, a i uskraćuje kome hoće: Allah, zaista, zna sve.", TextEnglish = "Allāh extends provision for whom He wills of His servants and restricts for him. Indeed Allāh is, of all things, Knowing.", TextGerman = "Allah gewährt die Versorgung großzügig, wem von Seinen Dienern Er will, und bemißt ihm auch. Gewiß, Allah weiß über alles Bescheid." });
                        surah29.VerseList.Add(new Verse { Number = 63, TextBosnian = "A ako ih upitaš: 'Ko s neba spušta vodu i njom mrtvu Zemlju oživljava?', sigurno će reći: 'Allah!', a ti reci: 'Hvala Allahu!', ali većina njih ne razumije.", TextEnglish = "And if you asked them, 'Who sends down rain from the sky and gives life thereby to the earth after its lifelessness?' they would surely say, 'Allāh.' Say, 'Praise to Allāh'; but most of them do not reason.", TextGerman = "Und wenn du sie fragst, wer vom Himmel Wasser herabkommen läßt und dann damit die Erde nach ihrem Tod wieder lebendig macht, sagen sie ganz gewiß: „Allah. Sag: (Alles) Lob gehört Allah! Aber nein! Die meisten von ihnen begreifen nicht." });
                        surah29.VerseList.Add(new Verse { Number = 64, TextBosnian = "Ovaj život dunjalučki nije ništa drugo do zabava i igra, a samo Dom ahiretski - to je život pravi, kad bi oni samo znali!", TextEnglish = "And this worldly life is not but diversion and amusement. And indeed, the home of the Hereafter - that is the [eternal] life, if only they knew.", TextGerman = "Dieses irdische Leben ist nur Zerstreuung und Spiel. Die jenseitige Wohnstätte aber ist wahrlich das eigentliche Leben, wenn sie (es) nur wüßten!" });
                        surah29.VerseList.Add(new Verse { Number = 65, TextBosnian = "Kad se u lađe ukrcaju, Allaha mole, iskreno Mu se predajući, a kada ih On do kopna dovede, odjednom druge Njemu ravnim smatraju", TextEnglish = "And when they board a ship, they supplicate Allāh, sincere to Him in religion [i.e., faith and hope]. But when He delivers them to the land, at once they associate others with Him", TextGerman = "Wenn sie ein Schiff besteigen, rufen sie Allah an, (wobei sie) Ihm gegenüber aufrichtig in der Religion (sind). Wenn Er sie nun ans Land errettet, gesellen sie sogleich (Ihm wieder andere) bei," });
                        surah29.VerseList.Add(new Verse { Number = 66, TextBosnian = "da bi poricali ono što im Mi dajemo, i da bi uživali. A znat će oni!", TextEnglish = "So that they will deny what We have granted them, and they will enjoy themselves. But they are going to know.[1134]", TextGerman = "um undankbar zu sein für das, was Wir ihnen gegeben haben, und um zu genießen; sie werden (es noch) erfahren." });
                        surah29.VerseList.Add(new Verse { Number = 67, TextBosnian = "Zar ne vide da smo Harem sigurnim učinili, dok se ljude oko njih otima i pljačka?! Zar u laž vjeruju, a Allahove blagodati poriču?!", TextEnglish = "Have they not seen that We made [Makkah] a safe sanctuary, while people are being taken away[1135] all around them? Then in falsehood do they believe, and in the favor of Allāh they disbelieve?", TextGerman = "Sehen sie denn nicht, daß Wir einen sicheren geschützten Bezirk gemacht haben, während die Menschen in ihrer Umgebung fortgerissen werden? Wollen sie denn an das Falsche glauben und Allahs Gunst verleugnen?" });
                        surah29.VerseList.Add(new Verse { Number = 68, TextBosnian = "Ima li onda većeg zulumćara od onoga koji o Allahu izmišlja laži ili poriče Istinu koja mu dolazi?! Zar nevjernicima nije boravište u Džehennemu?!", TextEnglish = "And who is more unjust than one who invents a lie about Allāh or denies the truth when it has come to him? Is there not in Hell a [sufficient] residence for the disbelievers?", TextGerman = "Und wer ist ungerechter als jemand, der gegen Allah eine Lüge ersinnt oder die Wahrheit, nachdem sie zu ihm gekommen ist, für Lüge erklärt? Ist nicht in der Hölle ein Aufenthaltsort für die Ungläubigen?" });
                        surah29.VerseList.Add(new Verse { Number = 69, TextBosnian = "One koji se budu radi Nas borili, Mi ćemo, sigurno, putevima Našim uputiti, a Allah je, zaista, s onima koji dobra djela čine!", TextEnglish = "And those who strive for Us - We will surely guide them to Our ways.[1136] And indeed, Allāh is with the doers of good.", TextGerman = "Diejenigen aber, die sich um Unsertwillen abmühen, werden Wir ganz gewiß Unsere Wege leiten. Und Allah ist wahrlich mit den Gutes Tuenden." });
                        result = surah29;
                        break;
                    #endregion

                    // 
                    #region
                    case 30:
                        Surah surah30 = new Surah(); surah30.Name = Constants.Surah30Name; surah30.Number = 30; surah30.VerseList = new List<Verse>();
                        surah30.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah30.VerseList.Add(new Verse { Number = 1, TextBosnian = "Elif-lam-mim.", TextEnglish = "Alif, Lām, Meem.[1138]", TextGerman = "Alif-Lam-Mim." });
                        surah30.VerseList.Add(new Verse { Number = 2, TextBosnian = "Bizantinci su pobijeđeni", TextEnglish = "The Byzantines have been defeated[1139]", TextGerman = "Die Römer sind besiegt worden" });
                        surah30.VerseList.Add(new Verse { Number = 3, TextBosnian = "u najbližoj zemlji, ali oni će, poslije poraza, sigurno pobijediti", TextEnglish = "In the nearest land.[1140] But they, after their defeat, will overcome", TextGerman = "im nächstliegenden Land. Aber sie werden nach ihrer Niederlage (selbst) siegen," });
                        surah30.VerseList.Add(new Verse { Number = 4, TextBosnian = "za nekoliko godina. Allahu pripada odluka i prije i poslije. Taj dan će se vjernici radovati", TextEnglish = "Within three to nine years. To Allāh belongs the command [i.e., decree] before and after. And that day the believers will rejoice", TextGerman = "in einigen Jahren. Allah gehört der Befehl vorher und nachher. An jenem Tag werden die Gläubigen froh sein" });
                        surah30.VerseList.Add(new Verse { Number = 5, TextBosnian = "Allahovoj pomoći. On pomaže kome hoće i On je Silni i Milostivi.", TextEnglish = "In the victory of Allāh.[1141] He gives victory to whom He wills, and He is the Exalted in Might, the Merciful.", TextGerman = "über Allahs Hilfe. Er hilft, wem Er will, und Er ist der Allmächtige und Barmherzige." });
                        surah30.VerseList.Add(new Verse { Number = 6, TextBosnian = "To je obećanje Allahovo. Allah ne krši obećanje svoje, ali većina ljudi ne zna.", TextEnglish = "[It is] the promise of Allāh. Allāh does not fail in His promise, but most of the people do not know.", TextGerman = "Das (ist) Allahs Versprechen. Allah bricht Sein Versprechen nicht, aber die meisten Menschen wissen nicht." });
                        surah30.VerseList.Add(new Verse { Number = 7, TextBosnian = "Oni znaju samo spoljašnju stranu života dunjalučkog, a prema ahiretu su nemarni.", TextEnglish = "They know what is apparent of the worldly life, but they, of the Hereafter, are unaware.", TextGerman = "Sie kennen nur das Äußerliche vom diesseitigen Leben, während sie des Jenseits unachtsam sind." });
                        surah30.VerseList.Add(new Verse { Number = 8, TextBosnian = "A zašto ne razmisle sami o sebi?! Allah je stvorio nebesa i Zemlju i ono što je između njih samo s Istinom i s rokom određenim. A zaista ljudi mnogi u susret s Gospodarom svojim ne vjeruju.", TextEnglish = "Do they not contemplate within themselves?[1142] Allāh has not created the heavens and the earth and what is between them except in truth and for a specified term. And indeed, many of the people, in the meeting with their Lord, are disbelievers.", TextGerman = "Denken sie denn nicht in ihrem Inneren (darüber) nach? Allah hat die Himmel und die Erde und was dazwischen ist nur in Wahrheit und (auf) eine festgesetzte Frist erschaffen. Aber viele von den Menschen verleugnen fürwahr die Begegnung mit ihrem Herrn." });
                        surah30.VerseList.Add(new Verse { Number = 9, TextBosnian = "Zašto ne putuju po Zemlji pa da vide kakav je bio završetak onih prije njih?! Oni su bili od njih jači i zemlju su obrađivali i izgrađivali je više nego što je izgrađuju ovi, i poslanici su im donosili jasne dokaze: Allah im sigurno nije zulum činio, međutim, sami su sebi zulum činili.", TextEnglish = "Have they not traveled through the earth and observed how was the end of those before them? They were greater than them in power, and they plowed [or excavated] the earth and built it up more than they [i.e., the Makkans] have built it up, and their messengers came to them with clear evidences. And Allāh would not ever have wronged them, but they were wronging themselves.", TextGerman = "Sind sie denn nicht auf der Erde umhergereist, so daß sie schauen (konnten), wie das Ende derjenigen war, die vor ihnen waren? Sie hatten eine stärkere Kraft als sie, pflügten und bevölkerten das Land noch mehr, als sie es bevölkerten. Und ihre Gesandten kamen zu ihnen mit den klaren Beweisen. Aber nimmer ist es Allah, der ihnen Unrecht getan hat, sondern sie selbst haben sich Unrecht zugefügt." });
                        surah30.VerseList.Add(new Verse { Number = 10, TextBosnian = "A onda, završetak onih koji su zlo činili bio je- i bit će- najužasnija kazna, zato što su Allahove riječi poricali i što su ih ruglu izvrgavali.", TextEnglish = "Then the end of those who did evil was the worst [consequence] because they denied the signs of Allāh and used to ridicule them.", TextGerman = "Hierauf war das Ende derjenigen, die Böses taten, das Schlechteste dafür, daß sie Allahs Zeichen für Lüge erklärt hatten und sich über sie lustig zu machen pflegten." });
                        surah30.VerseList.Add(new Verse { Number = 11, TextBosnian = "Allah započinje stvaranje; stvara iz ničega, zatim će to ponovo učiniti, a onda ćete Njemu biti vraćeni.", TextEnglish = "Allāh begins creation; then He will repeat it; then to Him you will be returned.", TextGerman = "Allah macht die Schöpfung am Anfang und wiederholt sie hierauf. Dann werdet ihr zu Ihm zurückgebracht." });
                        surah30.VerseList.Add(new Verse { Number = 12, TextBosnian = "Na Dan kad nastupi Čas suda, prestupnici će očajavati.", TextEnglish = "And the Day the Hour appears the criminals will be in despair.", TextGerman = "Und am Tag, da sich die Stunde erhebt, werden die Übeltäter ganz verzweifelt sein." });
                        surah30.VerseList.Add(new Verse { Number = 13, TextBosnian = "I oni neće imati među svojim božanstvima zagovornika i oni će svoja božanstva nijekati.", TextEnglish = "And there will not be for them among their [alleged] partners any intercessors, and they will [then] be disbelievers in their partners.", TextGerman = "Sie haben dann an ihren Teilhabern keine Fürsprecher und werden ihre Teilhaber verleugnen." });
                        surah30.VerseList.Add(new Verse { Number = 14, TextBosnian = "Na Dan kada nastupi Čas suda, ljudi će se razdvojiti:", TextEnglish = "And the Day the Hour appears - that Day they will become separated.", TextGerman = "Am Tag, da sich die Stunde erhebt, an jenem Tag werden sie sich in (Gruppen) teilen." });
                        surah30.VerseList.Add(new Verse { Number = 15, TextBosnian = "oni koji su vjerovali i dobra djela činili radovat će se u džennetskom perivoju,", TextEnglish = "And as for those who had believed and done righteous deeds, they will be in a garden [of Paradise], delighted.", TextGerman = "Was nun diejenigen angeht, die geglaubt und rechtschaffene Werke getan haben, so wird ihnen in einem Lustgarten Freude bereitet." });
                        surah30.VerseList.Add(new Verse { Number = 16, TextBosnian = "a oni koji nisu vjerovali i koji su ajete i dokaze Naše i susret na ahiretu poricali - u Vatru privedeni bit će.", TextEnglish = "But as for those who disbelieved and denied Our verses and the meeting of the Hereafter, those will be brought into the punishment [to remain].", TextGerman = "Was aber diejenigen angeht, die ungläubig waren und Unsere Zeichen und die Begegnung mit dem Jenseits für Lüge erklärt haben, sie werden zur Strafe vorgeführt werden." });
                        surah30.VerseList.Add(new Verse { Number = 17, TextBosnian = "Pa, slavljen i uzvišen neka je Allah kad god omrknete i kad god osvanete.", TextEnglish = "So exalted is Allāh when you reach the evening and when you reach the morning.", TextGerman = "Preis sei daher Allah, wenn ihr den Abend erreicht und auch wenn ihr den Morgen erreicht!" });
                        surah30.VerseList.Add(new Verse { Number = 18, TextBosnian = "Njemu neka je sva hvala i na nebesima i na Zemlji, i predvečer i u podne.", TextEnglish = "And to Him is [due all] praise throughout the heavens and the earth. And [exalted is He] at night and when you are at noon.", TextGerman = "Und Ihm gehört (alles) Lob in den Himmeln und auf der Erde, und abends, und wenn ihr den Mittag erreicht!" });
                        surah30.VerseList.Add(new Verse { Number = 19, TextBosnian = "On živo izvodi iz mrtvog i izvodi mrtvo iz živog. On oživljava Zemlju nakon mrtvila njezina; isto tako ćete i vi biti iz nje izvedeni.", TextEnglish = "He brings the living out of the dead and brings the dead out of the living and brings to life the earth after its lifelessness. And thus will you be brought out.[1143]", TextGerman = "Er bringt das Lebendige aus dem Toten und bringt das Tote aus dem Lebendigen hervor und macht die Erde nach ihrem Tod wieder lebendig. Und so werdet auch ihr hervorgebracht werden." });
                        surah30.VerseList.Add(new Verse { Number = 20, TextBosnian = "Jedan od znakova Njegovih je to što vas je od prašine stvorio, a zatim se kao ljudska bića svuda razilazite.", TextEnglish = "And of His signs is that He created you from dust; then, suddenly you were human beings dispersing [throughout the earth].", TextGerman = "Es gehört zu Seinen Zeichen, daß Er euch aus Erde erschaffen hat, hierauf wart ihr auf einmal menschliche Wesen, die sich ausbreiten." });
                        surah30.VerseList.Add(new Verse { Number = 21, TextBosnian = "I jedan od znakova Njegovih je to što vam je, od vas samih, parice stvorio da se uz njih smirite, i što između vas uspostavlja ljubav i samilost. U tome, zbilja, ima znakova za ljude koji razmišljaju.", TextEnglish = "And of His signs is that He created for you from yourselves mates that you may find tranquility in them; and He placed between you affection and mercy. Indeed in that are signs for a people who give thought.", TextGerman = "Und es gehört zu Seinen Zeichen, daß Er euch aus euch selbst Gattinnen erschaffen hat, damit ihr bei ihnen Ruhe findet; und Er hat Zuneigung und Barmherzigkeit zwischen euch gesetzt. Darin sind wahrlich Zeichen für Leute, die nachdenken." });
                        surah30.VerseList.Add(new Verse { Number = 22, TextBosnian = "I jedan od znakova Njegovih je stvaranje nebesa i Zemlje, i raznovrsnost jezika vaših i boja vaših; u tome, zaista, ima znakova za one koji znaju.", TextEnglish = "And of His signs is the creation of the heavens and the earth and the diversity of your languages and your colors. Indeed in that are signs for those of knowledge.", TextGerman = "Und zu Seinen Zeichen gehört die Erschaffung der Himmel und der Erde und (auch) die Verschiedenheit eurer Sprachen und Farben. Darin sind wahrlich Zeichen für die Wissenden." });
                        surah30.VerseList.Add(new Verse { Number = 23, TextBosnian = "I jedan od znakova Njegovih je san vaš noću i danju, i nastojanje vaše da steknete nešto iz obilja Njegova; u tome, zaista, ima znakova za ljude koji čuju.", TextEnglish = "And of His signs is your sleep by night and day and your seeking of His bounty. Indeed in that are signs for a people who listen.", TextGerman = "Und zu Seinen Zeichen gehört euer Schlaf bei Nacht und Tag und auch euer Trachten nach etwas von Seiner Huld. Darin sind wahrlich Zeichen für Leute, die hören." });
                        surah30.VerseList.Add(new Verse { Number = 24, TextBosnian = "I jedan od znakova Njegovih je to što vam pokazuje munju, da se pobojite i ponadate, i to što spušta s neba kišu i oživljava njome zemlju poslije mrtvila njezina; u tome, zaista, ima znakova za ljude koji razumiju.", TextEnglish = "And of His signs is [that] He shows you the lightning [causing] fear and aspiration, and He sends down rain from the sky by which He brings to life the earth after its lifelessness. Indeed in that are signs for a people who use reason.", TextGerman = "Und es gehört zu Seinen Zeichen, daß Er euch den Blitz (als Grund) zur Furcht und zum Begehren sehen läßt und vom Himmel Wasser herabkommen läßt und mit ihm dann die Erde nach ihrem Tod wieder lebendig macht. Darin sind wahrlich Zeichen für Leute, die begreifen." });
                        surah30.VerseList.Add(new Verse { Number = 25, TextBosnian = "I jedan od znakova Njegovih je i to što nebo i Zemlja opstoje odredbom Njegovom. Zatim, ćete, čim vas On samo jednom pozove, iz zemlje brzo izaći.", TextEnglish = "And of His signs is that the heaven and earth stand [i.e., remain] by His command. Then when He calls you with a [single] call from the earth, immediately you will come forth.", TextGerman = "Und es gehört zu Seinen Zeichen, daß der Himmel und die Erde durch Seinen Befehl bestehen. Wenn Er euch hierauf ein (einziges Mal) ruft, da kommt ihr sogleich aus der Erde hervor." });
                        surah30.VerseList.Add(new Verse { Number = 26, TextBosnian = "Njemu pripada sve što je na nebesima i na Zemlji, sve je Njemu poslušno.", TextEnglish = "And to Him belongs whoever is in the heavens and earth. All are to Him devoutly obedient.", TextGerman = "Ihm gehört, wer in den Himmeln und auf der Erde ist. Alle sind Ihm demütig ergeben." });
                        surah30.VerseList.Add(new Verse { Number = 27, TextBosnian = "On je Taj Koji započinje stvaranje; stvara iz ničega i On će to ponovo učiniti, to je Njemu lahko; Njemu pripada primjer najuzvišeniji i na nebesima i na Zemlji; On je Silni i Mudri.", TextEnglish = "And it is He who begins creation; then He repeats it, and that is [even] easier for Him. To Him belongs the highest description [i.e., attribute] in the heavens and earth. And He is the Exalted in Might, the Wise.", TextGerman = "Und Er ist es, Der die Schöpfung am Anfang macht und sie hierauf wiederholt; das ist für Ihn noch leichter. Er hat die höchste Eigenschaft in den Himmeln und auf der Erde, und Er ist der Allmächtige und Allweise." });
                        surah30.VerseList.Add(new Verse { Number = 28, TextBosnian = "On vam kao primjer navodi vas same: da li među onima koji su u posjedu vašem imate suvlasnike u onome čime smo vas opskrbili, pa ste u tome isti, i bojite ih se kao što se jedni drugih bojite? Eto, tako Mi podrobno izlažemo znakove Naše ljudima koji razumiju.", TextEnglish = "He presents to you an example from yourselves. Do you have among those whom your right hands possess [i.e., slaves] any partners in what We have provided for you so that you are equal therein [and] would fear them as your fear of one another [within a partnership]?[1144] Thus do We detail the verses for a people who use reason.", TextGerman = "Er prägt euch aus eurem eigenen (Lebens)bereich ein Gleichnis: Habt ihr denn (aus der Schar) unter denjenigen, die eure rechte Hand (an Sklaven) besitzt, Teilhaber an dem, womit Wir euch versorgt haben, so daß ihr darin gleich wäret und ihr sie fürchten müßtet, wie ihr einander fürchtet? So legen Wir die Zeichen ausführlich dar für Leute, die begreifen." });
                        surah30.VerseList.Add(new Verse { Number = 29, TextBosnian = "Ali, oni koji zulum učine, bez znanja slijede hirove svoje, a ko će na Pravi put uputiti onoga kome je Allah dao da u zabludi je?! Oni pomagača nemaju.", TextEnglish = "But those who wrong follow their [own] desires without knowledge. Then who can guide one whom Allāh has sent astray? And for them there are no helpers.", TextGerman = "Aber nein! Diejenigen, die Unrecht tun, folgen ihren Neigungen ohne (richtiges) Wissen. Wer sollte rechtleiten, wen Allah in die Irre gehen läßt? Und sie haben keine Helfer." });
                        surah30.VerseList.Add(new Verse { Number = 30, TextBosnian = "Ti upravi lice svoje vjeri iskreno; prirodnoj vjeri Allahovoj prema kojoj je ljude stvorio. Nema izmjene Allahovog stvaranja, to je prava vjera, ali većina ljudi to ne zna.", TextEnglish = "So direct your face [i.e., self] toward the religion, inclining to truth. [Adhere to] the fiṭrah[1145] of Allāh upon which He has created [all] people. No change should there be in the creation of Allāh.[1146] That is the correct religion, but most of the people do not know.", TextGerman = "So richte dein Gesicht aufrichtig zur Religion hin als Anhänger des rechten Glaubens, - (gemäß) der natürlichen Anlage Allahs, in der Er die Menschen erschaffen hat. Keine Abänderung gibt es für die Schöpfung Allahs. Das ist die richtige Religion. Aber die meisten Menschen wissen nicht." });
                        surah30.VerseList.Add(new Verse { Number = 31, TextBosnian = "Pokajnički Mu se obraćajte! Bojte se Njega i obavljajte namaz, i ne budite višebošci,", TextEnglish = "[Adhere to it], turning in repentance to Him, and fear Him and establish prayer and do not be of those who associate others with Allāh", TextGerman = "(Haltet daran fest), indem ihr euch Ihm reuig zuwendet, und fürchtet Ihn und verrichtet das Gebet und gehört nicht zu den Götzendienern," });
                        surah30.VerseList.Add(new Verse { Number = 32, TextBosnian = "oni koji su vjeru svoju rascijepili i u stranke se podijelili, svaka stranka radosna zbog onog što ima!", TextEnglish = "[Or] of those who have divided their religion and become sects, every faction rejoicing in what it has.[1147]", TextGerman = "zu denjenigen, die ihre Religion spalteten und zu Lagern geworden sind, wobei jede Gruppierung froh ist über das, was sie bei sich hat." });
                        surah30.VerseList.Add(new Verse { Number = 33, TextBosnian = "A kad ljude nevolja snađe, oni Gospodara svoga mole, vraćajući Mu se pokajnički, a poslije, kad On učini da okuse milost Njegovu, odjednom neki od njih Gospodaru svome druge u obožavanju pridružuju,", TextEnglish = "And when adversity touches the people, they call upon their Lord, turning in repentance to Him. Then when He lets them taste mercy from Him, at once a party of them associate others with their Lord,", TextGerman = "Und wenn den Menschen Unheil widerfahrt, rufen sie ihren Herrn an, indem sie sich Ihm reuig zuwenden. Wenn Er sie hierauf Barmherzigkeit von Sich kosten läßt, gesellt sogleich eine Gruppe von ihnen ihrem Herrn (andere) bei," });
                        surah30.VerseList.Add(new Verse { Number = 34, TextBosnian = "da bi poricali ono što im Mi dajemo, pa uživajte, a saznat ćete!", TextEnglish = "So that they will deny what We have granted them.[1148] Then enjoy yourselves, for you are going to know.", TextGerman = "um undankbar zu sein für das, was Wir ihnen gegeben haben. - Genießt nur; ihr werdet (es noch) erfahren." });
                        surah30.VerseList.Add(new Verse { Number = 35, TextBosnian = "Štaviše, da im nismo poslali kakav dokaz koji govori u prilog onima koje Njemu ravnim smatraju?", TextEnglish = "Or have We sent down to them an authority [i.e., a proof or scripture], and it speaks of what they have been associating with Him?", TextGerman = "Oder haben Wir ihnen etwa eine Ermächtigung offenbart, die dann von dem spricht, was sie Ihm beigesellen?" });
                        surah30.VerseList.Add(new Verse { Number = 36, TextBosnian = "A kad učinimo da ljudi milost našu okuse, oni joj se obraduju, a kad ih pogodi nevolja zbog onoga što su njihove ruke činile, odjednom očajavaju.", TextEnglish = "And when We let the people taste mercy, they rejoice therein, but if evil afflicts them for what their hands have put forth, immediately they despair.", TextGerman = "Und wenn Wir die Menschen Barmherzigkeit kosten lassen, sind sie froh darüber. Wenn sie aber etwas Böses trifft für das, was ihre Hände vorausgeschickt haben, verlieren sie sogleich die Hoffnung." });
                        surah30.VerseList.Add(new Verse { Number = 37, TextBosnian = "Zar oni ne vide da Allah u obilju daje opskrbu onome kome On hoće, i da je uskraćuje?! Tu su, uistinu, znakovi za ljude koji vjeruju.", TextEnglish = "Do they not see that Allāh extends provision for whom He wills and restricts [it]? Indeed in that are signs for a people who believe.", TextGerman = "Sehen sie denn nicht, daß Allah die Versorgung großzügig gewährt, wem Er will, und auch bemißt? Darin sind wahrlich Zeichen für Leute, die glauben." });
                        surah30.VerseList.Add(new Verse { Number = 38, TextBosnian = "Pa daj bližnjem pravo njegovo, i siromahu, i putniku namjerniku. To je bolje za one koji žele lice Allahovo; baš to su oni uspješni.", TextEnglish = "So give the relative his right, as well as the needy and the traveler. That is best for those who desire the face [i.e., approval] of Allāh, and it is they who will be the successful.", TextGerman = "So gib dem Verwandten sein Recht, ebenso dem Armen und dem Sohn des Weges. Das ist besser für diejenigen, die Allahs Angesicht begehren. Und jene sind es, denen es wohl ergeht." });
                        surah30.VerseList.Add(new Verse { Number = 39, TextBosnian = "A ono što dajete da se uveća imovinom drugih ljudi neće se kod Allaha uvećati, a zekat koji udijelite iz želje za licem Allahovim - upravo takvi će nagradu svoju umnogostručiti.", TextEnglish = "And whatever you give for interest [i.e., advantage] to increase within the wealth of people[1149] will not increase with Allāh. But what you give in zakāh,[1150] desiring the face [i.e., approval] of Allāh - those are the multipliers.[1151]", TextGerman = "Und was ihr an Ausgaben aufwendet, damit sie sich aus dem Besitz der Menschen vermehren, sie vermehren sich nicht bei Allah. Was ihr aber an Abgaben entrichtet im Begehren nach Allahs Angesicht ... - das sind diejenigen, die das Vielfache erzielen." });
                        surah30.VerseList.Add(new Verse { Number = 40, TextBosnian = "Allah je Onaj Koji vas je stvorio, pa vas opskrbio, zatim će vas usmrtiti i potom oživiti. Postoji li ijedno božanstvo koje bilo šta od toga čini?! Čist je i Uzvišen On od onoga što Njemu ravnim smatraju.", TextEnglish = "Allāh is the one who created you, then provided for you, then will cause you to die, and then will give you life. Are there any of your 'partners' who does anything of that? Exalted is He and high above what they associate with Him.", TextGerman = "Allah ist es, Der euch erschaffen und dann versorgt hat. Hierauf läßt Er euch sterben, hierauf macht Er euch wieder lebendig. Gibt es unter euren Teilhabern einen, der irgend etwas von alledem tut? Preis sei Ihm! Erhaben ist Er über das, was sie (Ihm) beigesellen." });
                        surah30.VerseList.Add(new Verse { Number = 41, TextBosnian = "Nered se pojavio na kopnu i na moru zbog onog što ljudi rade, da bi im On dao da iskuse kaznu za nešto od onoga što urade, ne bi li se povratili.", TextEnglish = "Corruption has appeared throughout the land and sea by [reason of] what the hands of people have earned so He [i.e., Allāh] may let them taste part of [the consequence of] what they have done that perhaps they will return [to righteousness].", TextGerman = "Unheil ist auf dem Festland und im Meer erschienen wegen dessen, was die Hände der Menschen erworben haben, damit Er sie einiges von dem kosten läßt, was sie getan haben, auf daß sie umkehren mögen." });
                        surah30.VerseList.Add(new Verse { Number = 42, TextBosnian = "Reci: 'Putujte po Zemlji pa pogledajte kakav je bio kraj onih što su bili prije; većinom su oni višebošci bili.'", TextEnglish = "Say, [O Muḥammad], 'Travel through the land and observe how was the end of those before. Most of them were associators [of others with Allāh].", TextGerman = "Sag: Reist auf der Erde umher und schaut, wie das Ende derjenigen zuvor war. Die meisten von ihnen waren Götzendiener." });
                        surah30.VerseList.Add(new Verse { Number = 43, TextBosnian = "Zato ti upravi lice svoje pravoj vjeri, prije nego što od Allaha dođe Dan koji niko neće moći odbiti. Taj dan će se oni razdvojiti.", TextEnglish = "So direct your face [i.e., self] toward the correct religion before a Day comes from Allāh of which there is no repelling. That Day, they will be divided.[1152]", TextGerman = "So richte dein Gesicht aufrichtig zur richtigen Religion hin, bevor von Allah ein Tag kommt, der nicht zurückgewiesen wird. An jenem Tag werden sie sich (in Gruppen) spalten." });
                        surah30.VerseList.Add(new Verse { Number = 44, TextBosnian = "Oni koji nisu vjerovali - na svoju štetu nisu vjerovali, a oni koji su dobra djela činili - sebi su Džennet pripremili,", TextEnglish = "Whoever disbelieves - upon him is [the consequence of] his disbelief. And whoever does righteousness - they are for themselves preparing,", TextGerman = "Wer ungläubig ist, dessen Unglaube geht zu seinem (eigenen) Nachteil. Und wer rechtschaffen handelt, bereitet sich selbst die Lagerstatt vor," });
                        surah30.VerseList.Add(new Verse { Number = 45, TextBosnian = "da On iz obilja Svoga nagradi one koji su vjerovali i dobra djela činili; a On, zaista, ne voli nevjernike.", TextEnglish = "That He may reward those who have believed and done righteous deeds out of His bounty. Indeed, He does not like the disbelievers.", TextGerman = "damit Er denjenigen, die glauben und rechtschaffene Werke tun, aus Seiner Huld vergelte. Gewiß, Er liebt nicht die Ungläubigen." });
                        surah30.VerseList.Add(new Verse { Number = 46, TextBosnian = "Jedan od znakova Njegovih je to što On šalje vjetrove kao nosioce radosnih vijesti da bi vam dao da milost Njegovu osjetite i da lađe odredbom Njegovom plove i da iz obilja Njegova stječete, i da biste zahvalni bili.", TextEnglish = "And of His signs is that He sends the winds as bringers of good tidings and to let you taste His mercy [i.e., rain] and so the ships may sail at His command and so you may seek of His bounty, and perhaps you will be grateful.", TextGerman = "Und es gehört zu Seinen Zeichen, daß Er die Winde als Verkünder froher Botschaft (voraus)sendet, und damit Er euch etwas von Seiner Barmherzigkeit kosten läßt und damit die Schiffe auf Seinen Befehl fahren und damit ihr nach etwas von Seiner Huld trachtet, und auf daß ihr dankbar sein möget." });
                        surah30.VerseList.Add(new Verse { Number = 47, TextBosnian = "I prije tebe smo poslanike narodima njihovim slali i oni su im jasne dokaze donosili, pa smo one koji su griješili kažnjavali, a uistinu Nam je dužnost bila vjernike pomoći.", TextEnglish = "And We have already sent messengers before you to their peoples, and they came to them with clear evidences; then We took retribution from those who committed crimes, and incumbent upon Us was support[1153] of the believers.", TextGerman = "Und Wir haben bereits vor dir Gesandte zu ihren (jeweiligen) Völkern gesandt. Sie kamen zu ihnen mit den klaren Beweisen. Da übten Wir Vergeltung an denjenigen, die Übeltaten begingen; und es war für Uns eine Pflicht, den Gläubigen zu helfen." });
                        surah30.VerseList.Add(new Verse { Number = 48, TextBosnian = "Allah je Taj Koji vjetrove šalje, pa oni oblake tjeraju i On ih po nebu, kako On hoće, rasprostire i na komade dijeli, pa ti vidiš kišu kako iz njih pada, i kad je On na one robove Svoje, na koje želi, prolije, oni se odjednom vesele,", TextEnglish = "It is Allāh who sends the winds, and they stir the clouds and spread them in the sky however He wills, and He makes them fragments so you see the rain emerge from within them. And when He causes it to fall upon whom He wills of His servants, immediately they rejoice", TextGerman = "Allah ist es, der die Winde sendet, und da wühlen sie die Wolken auf. Dann breitet Er sie im Himmel aus, wie Er will, und macht sie zu Stücken. Da siehst du den (Platz)regen dazwischen herauskommen. Wenn Er damit von Seinen Dienern, wen Er will, trifft, freuen sie sich sogleich," });
                        surah30.VerseList.Add(new Verse { Number = 49, TextBosnian = "iako su prije nego što se spustila na njih bili očajni.", TextEnglish = "Although they were, before it was sent down upon them - before that, in despair.", TextGerman = "obwohl sie vorher, bevor (er) auf sie herabgesandt wurde, wahrlich ganz verzweifelt waren." });
                        surah30.VerseList.Add(new Verse { Number = 50, TextBosnian = "Zato, pogledaj tragove Allahove milosti - kako On oživi zemlju nakon mrtvila njezina! On će, uistinu, i mrtve oživiti, On nad svime ima moć.", TextEnglish = "So observe the effects of the mercy of Allāh - how He gives life to the earth after its lifelessness. Indeed, that [same one] will give life to the dead, and He is over all things competent.", TextGerman = "Schau doch auf die Spuren von Allahs Barmherzigkeit, wie Er die Erde nach ihrem Tod wieder lebendig macht. Er ist wahrlich Derjenige, Der (auch) die Toten wieder lebendig macht, und Er hat zu allem die Macht." });
                        surah30.VerseList.Add(new Verse { Number = 51, TextBosnian = "A da pošaljemo vjetar i da oni vide da je sve požutjelo, oni bi, i poslije toga, blagodati poricali.", TextEnglish = "But if We should send a [bad] wind and they saw [their crops] turned yellow, they would remain thereafter disbelievers.[1154]", TextGerman = "Und wenn Wir einen Wind sendeten und sie dann alles gelb werden sähen, würden sie (trotzdem) danach fortfahren, ungläubig zu sein." });
                        surah30.VerseList.Add(new Verse { Number = 52, TextBosnian = "Ti ne možeš mrtve dozvati, ni gluhe, kad se od tebe okrenu, dovikati,", TextEnglish = "So indeed, you will not make the dead hear, nor will you make the deaf hear the call when they turn their backs, retreating.", TextGerman = "(Sei nicht traurig,) denn du kannst nicht die Toten hören lassen noch die Tauben den Ruf hören lassen, wenn sie den Rücken kehren." });
                        surah30.VerseList.Add(new Verse { Number = 53, TextBosnian = "niti možeš zaslijepljene od zablude njihove odvratiti; možeš jedino dozvati one koji u ajete Naše vjeruju, jedino oni su pokorni.", TextEnglish = "And you cannot guide the blind away from their error. You will only make hear those who believe in Our verses so they are Muslims [in submission to Allāh].", TextGerman = "Noch kannst du die Blinden aus ihrem Irrtum heraus rechtleiten. Hören lassen kannst du nur, wer an Unsere Zeichen glaubt und die somit (Allah) ergeben sind." });
                        surah30.VerseList.Add(new Verse { Number = 54, TextBosnian = "Allah je Taj Koji vas nejakim stvara, i onda vam, poslije nejakosti, snagu daje, a poslije snage iznemoglost i sijede vlasi; On stvara što hoće, On je Sveznajući i Svemoćni.", TextEnglish = "Allāh is the one who created you from weakness, then made after weakness strength, then made after strength weakness and white hair. He creates what He wills, and He is the Knowing, the Competent.", TextGerman = "Allah ist es, der euch (zuerst) in Schwäche erschafft. Hierauf verleiht Er nach der Schwäche Kraft. Hierauf verleiht Er nach der Kraft Schwäche und weißes Haar. Er erschafft, was Er will, und Er ist der Allwissende und Allmächtige." });
                        surah30.VerseList.Add(new Verse { Number = 55, TextBosnian = "A na Dan kad nastupi Čas oživljenja, prestupnici će se zaklinjati da su samo jedan čas ostali; a i prije su Istinu izbjegavali.", TextEnglish = "And the Day the Hour appears the criminals will swear they had remained but an hour. Thus they were deluded.", TextGerman = "Und am Tag, da sich die Stunde erhebt, schwören die Übeltäter, sie hätten nicht länger als eine Stunde verweilt. So pflegten sie sich (von der Wahrheit) abwendig machen zu lassen." });
                        surah30.VerseList.Add(new Verse { Number = 56, TextBosnian = "A reći će oni kojima je dato znanje i vjerovanje: 'Vi ste ostali, prema Allahovoj Knjizi, sve do Dana oživljenja, a ovo je Dan oživljenja, samo što vi niste znali.'", TextEnglish = "But those who were given knowledge and faith will say, 'You remained the extent of Allāh's decree until the Day of Resurrection, and this is the Day of Resurrection, but you did not used to know.'[1155]", TextGerman = "Und diejenigen, denen Wissen und Glauben gegeben worden ist, sagen: „Nach Allahs Buch habt ihr bis zum Tag der Auferweckung verweilt. Das ist nun der Tag der Auferweckung, aber ihr wußtet (es) nicht." });
                        surah30.VerseList.Add(new Verse { Number = 57, TextBosnian = "Taj dan neće ničemu poslužiti pravdanja onima koji su prema sebi zulum učinili i neće im biti udovoljeno da blagonaklonost za povratak traže.", TextEnglish = "So that Day, their excuse will not benefit those who wronged, nor will they be asked to appease [Allāh].", TextGerman = "So wird an jenem Tag denjenigen, die Unrecht tun, ihre Entschuldigung nicht nützen, und es wird ihnen keine Gnade erwiesen." });
                        surah30.VerseList.Add(new Verse { Number = 58, TextBosnian = "U ovom Kur'anu Mi navodimo ljudima svakovrsne primjere. A kad bi im kakav znak donio, opet bi rekli oni koji ne vjeruju: 'Vi samo iznosite laži!'", TextEnglish = "And We have certainly presented to the people in this Qur’ān from every [kind of] example. But, [O Muḥammad], if you should bring them a sign, the disbelievers will surely say, 'You [believers] are but falsifiers.'", TextGerman = "Wir haben ja den Menschen in diesem Qur’an allerlei Gleichnisse geprägt. Und wenn du zu ihnen mit einem Zeichen kommst, sagen ganz gewiß diejenigen, die ungläubig sind: „Ihr bringt nur Falsches." });
                        surah30.VerseList.Add(new Verse { Number = 59, TextBosnian = "Eto, tako Allah pečati srca onima koji ne znaju.", TextEnglish = "Thus does Allāh seal the hearts of those who do not know.[1156]", TextGerman = "So versiegelt Allah die Herzen derjenigen, die nicht Bescheid wissen." });
                        surah30.VerseList.Add(new Verse { Number = 60, TextBosnian = "A ti budi strpljiv! Allahovo obećanje je, zaista, istina i neka te nikako ne usplahire oni koji čvrsto ne vjeruju.", TextEnglish = "So be patient. Indeed, the promise of Allāh is truth. And let them not disquiet you who are not certain [in faith].", TextGerman = "So sei standhaft. Gewiß, Allahs Versprechen ist wahr. Und diejenigen, die nicht überzeugt sind, sollen dich ja nicht ins Wanken bringen." });
                        result = surah30;
                        break;
                    #endregion

