                    case 31:
                        Surah surah31 = new Surah(); surah31.Name = Constants.Surah31Name; surah31.Number = 31; surah31.VerseList = new List<Verse>();
                        surah31.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah31.VerseList.Add(new Verse { Number = 1, TextBosnian = "Elif-lam-mim.", TextEnglish = "Alif, Lām, Meem.[1158]", TextGerman = "Alif-Lam-Mim." });
                        surah31.VerseList.Add(new Verse { Number = 2, TextBosnian = "Ovo su ajeti Knjige usavršene,", TextEnglish = "These are verses of the wise[1159] Book,", TextGerman = "Dies sind die Zeichen des weisen Buches," });
                        surah31.VerseList.Add(new Verse { Number = 3, TextBosnian = "upute i milosti onima koji dobro čine,", TextEnglish = "As guidance and mercy for the doers of good", TextGerman = "eine Rechtleitung und Barmherzigkeit für die Gutes Tuenden," });
                        surah31.VerseList.Add(new Verse { Number = 4, TextBosnian = "onima koji namaz obavljaju i zekat daju i koji u ahiret čvrsto vjeruju;", TextEnglish = "Who establish prayer and give zakāh, and they, of the Hereafter, are certain [in faith].", TextGerman = "die das Gebet verrichten und die Abgabe entrichten, und sie, die sie vom Jenseits überzeugt sind." });
                        surah31.VerseList.Add(new Verse { Number = 5, TextBosnian = "takvi su u Uputi Gospodara svoga i oni će uspjeti.", TextEnglish = "Those are on [right] guidance from their Lord, and it is those who are the successful.", TextGerman = "Jene verfahren nach einer Rechtleitung von ihrem Herrn, und das sind diejenigen, denen es wohl ergeht." });
                        surah31.VerseList.Add(new Verse { Number = 6, TextBosnian = "Ima ljudi koji kupuju zaludan govor, da bi, bez znanja, s Allahova puta odvodili i da bi ga za ismijavanje uzimali. Njima ponižavajuća patnja pripada.", TextEnglish = "And of the people is he who buys the amusement of speech[1160] to mislead [others] from the way of Allāh without knowledge and who takes it [i.e., His way] in ridicule. Those will have a humiliating punishment.", TextGerman = "Unter den Menschen gibt es manchen, der zerstreuende Unterhaltung erkauft, um (die Menschen) von Allahs Weg ohne (richtiges) Wissen in die Irre zu führen und sich über ihn lustig zu machen. Für solche wird es schmachvolle Strafe geben." });
                        surah31.VerseList.Add(new Verse { Number = 7, TextBosnian = "Kad se nekom od njih ajeti Naši kazuju, on oholo glavu okreće, kao da ih nije ni čuo, kao da su gluhe uši njegove - zato mu navijesti patnju bolnu.", TextEnglish = "And when Our verses are recited to him, he turns away arrogantly as if he had not heard them, as if there was in his ears deafness. So give him tidings of a painful punishment.", TextGerman = "Und wenn ihm Unsere Zeichen verlesen werden, kehrt er sich hochmütig ab, als ob er sie nicht gehört hätte, als ob in seinen Ohren Schwerhörigkeit wäre. So verkünde ihm schmerzhafte Strafe." });
                        surah31.VerseList.Add(new Verse { Number = 8, TextBosnian = "Onima koji vjeruju i dobra djela čine, doista, pripadaju bašče uživanja,", TextEnglish = "Indeed, those who believe and do righteous deeds - for them are the Gardens of Pleasure,", TextGerman = "Gewiß, diejenigen, die glauben und rechtschaffene Werke tun, für sie wird es die Gärten der Wonne geben;" });
                        surah31.VerseList.Add(new Verse { Number = 9, TextBosnian = "u njima će vječno boraviti - obećanje je Allahovo istinito, a On je Silni i Mudri.", TextEnglish = "Wherein they abide eternally; [it is] the promise of Allāh [which is] truth. And He is the Exalted in Might, the Wise.", TextGerman = "ewig darin zu bleiben. (Das ist) Allahs Versprechen in Wahrheit. Und Er ist der Allmächtige und Allweise." });
                        surah31.VerseList.Add(new Verse { Number = 10, TextBosnian = "Stvorio je nebesa koja vi vidite bez stupova, a po Zemlji planine nepomične postavio da se zajedno s vama ne potresa, i po njoj životinje svih vrsta razasuo. Mi s neba vodu spuštamo i činimo da po njoj izniču razne vrste plemenite.", TextEnglish = "He created the heavens without pillars that you see and has cast into the earth firmly set mountains, lest it should shift with you, and dispersed therein from every creature. And We sent down rain from the sky and made grow therein [plants] of every noble kind.", TextGerman = "Er hat die Himmel ohne Stützen, die ihr sehen könnt, erschaffen und auf der Erde festgegründete Berge gesetzt, daß sie nicht mit euch wanke, und auf ihr allerlei Tiere sich ausbreiten lassen. Und Wir lassen Wasser vom Himmel herabkommen und dann viele edle Arten auf ihr wachsen." });
                        surah31.VerseList.Add(new Verse { Number = 11, TextBosnian = "To je Allahovo stvaranje, a pokažite Mi šta su drugi, mimo Njega, stvorili! Naprotiv, zulumćari su u očitaj zabludi!", TextEnglish = "This is the creation of Allāh. So show Me what those other than Him have created. Rather, the wrongdoers are in clear error.", TextGerman = "Das ist Allahs Schöpfung. Zeigt mir nun, was diejenigen außer Ihm erschaffen haben. Aber nein! Die Ungerechten befinden sich in deutlichem Irrtum." });
                        surah31.VerseList.Add(new Verse { Number = 12, TextBosnian = "A Mi smo Lukmanu mudrost darovali: 'Budi zahvalan Allahu! Ko je zahvalan, čini to u svoju korist, a ko je nezahvalan - pa, Allah je, zaista, neovisan i hvaljen.'", TextEnglish = "And We had certainly given Luqmān wisdom [and said], 'Be grateful to Allāh.' And whoever is grateful is grateful for [the benefit of] himself. And whoever denies [His favor] - then indeed, Allāh is Free of need and Praiseworthy.", TextGerman = "Und Wir gaben ja Luqman Weisheit: „Sei Allah dankbar. Und wer dankbar ist, der ist nur zu seinem eigenen Vorteil dankbar. Und wer undankbar ist, - so ist Allah Unbedürftig und Lobenswürdig." });
                        surah31.VerseList.Add(new Verse { Number = 13, TextBosnian = "Kad Lukman reče sinu svome, savjetujući ga: 'O sinko moj, nemoj Allahu u obožavanju nikoga pridruživati; širk je, zbilja, zulum veliki.'", TextEnglish = "And [mention, O Muḥammad], when Luqmān said to his son while he was instructing him, 'O my son, do not associate [anything] with Allāh. Indeed, association [with Him] is great injustice.'", TextGerman = "Und (gedenke,) als Luqman zu seinem Sohn sagte, indem er ihn ermahnte: „O mein lieber Sohn, geselle Allah nicht(s) bei, denn Götzendienst ist fürwahr ein gewaltiges Unrecht." });
                        surah31.VerseList.Add(new Verse { Number = 14, TextBosnian = "Mi smo zapovjedili čovjeku da bude poslušan roditeljima svojim. Majka ga nosi, iz slabosti u slabost, i doji ga u toku dvije godine. Budi zahvalan Meni i roditeljima svojim, kod Mene je krajnje odredište.", TextEnglish = "And We have enjoined upon man [care] for his parents. His mother carried him, [increasing her] in weakness upon weakness, and his weaning is in two years. Be grateful to Me and to your parents; to Me is the [final] destination.", TextGerman = "Und Wir haben dem Menschen seine Eltern anbefohlen - seine Mutter hat ihn unter wiederholter Schwäche getragen, und seine Entwöhnung (erfolgt) innerhalb von zwei Jahren -: „Sei Mir und deinen Eltern dankbar. Zu Mir ist der Ausgang." });
                        surah31.VerseList.Add(new Verse { Number = 15, TextBosnian = "A ako budu nastojali da te nagovore da Meni u obožavanju pridružuješ ono o čemu ništa ne znaš, ti ih ne slušaj i prema njima se, na dunjaluku, velikodušno ponašaj. I slijedi Put onoga koji se iskreno Meni obraća; Meni ćete se poslije vratiti i Ja ću vas o onome što ste radili obavijestiti.", TextEnglish = "But if they endeavor to make you associate with Me that of which you have no knowledge,[1161] do not obey them but accompany them in [this] world with appropriate kindness and follow the way of those who turn back to Me [in repentance]. Then to Me will be your return, and I will inform you about what you used to do.", TextGerman = "Wenn sie sich aber darum bemühen, daß du Mir das beigesellst, wovon du kein Wissen hast, dann gehorche ihnen nicht, doch geh mit ihnen im Diesseits in rechtlicher Weise um. Und folge dem Weg dessen, der sich Mir reuig zuwendet. Zu Mir wird hierauf eure Rückkehr sein, da werde Ich euch kundtun, was ihr zu tun pflegtet." });
                        surah31.VerseList.Add(new Verse { Number = 16, TextBosnian = "O sinko moj, dobro i zlo, teško koliko zrno gorušice, bilo u stijeni ili na nebesima ili u zemlji, Allah će na vidjelo iznijeti. Doista, Allah zna ono što je najsitnije i On je Onaj Koji sve u potpunosti zna.", TextEnglish = "[And Luqmān said], 'O my son, indeed if it [i.e., a wrong] should be the weight of a mustard seed and should be within a rock or [anywhere] in the heavens or in the earth, Allāh will bring it forth. Indeed, Allāh is Subtle[1162] and Aware.", TextGerman = "O mein lieber Sohn, gewiß, wäre es auch das Gewicht eines Senfkorns und befände es sich in einem Felsen oder in den Himmeln oder in der Erde, bringt es Allah bei. Gewiß, Allah ist Feinfühlig und Allkundig." });
                        surah31.VerseList.Add(new Verse { Number = 17, TextBosnian = "O sinko moj, obavljaj namaz i traži da se čine dobra djela, a odvraćaj od hrđavih i strpljivo podnosi ono što te zadesi - to, doista, u istinske postupke spada.", TextEnglish = "O my son, establish prayer, enjoin what is right, forbid what is wrong, and be patient over what befalls you. Indeed, [all] that is of the matters [requiring] resolve.[1163]", TextGerman = "O mein lieber Sohn, verrichte das Gebet, gebiete das Rechte und verbiete das Verwerfliche und ertrage standhaft, was dich trifft. Gewiß, dies gehört zur Entschlossenheit (in der Handhabung) der Angelegenheiten." });
                        surah31.VerseList.Add(new Verse { Number = 18, TextBosnian = "I, iz oholosti, ne okreći od ljudi lice svoje i ne idi Zemljom nadmeno. Zaista Allah ne voli ni gordog ni hvalisavog.", TextEnglish = "And do not turn your cheek [in contempt] toward people[1164] and do not walk through the earth exultantly. Indeed, Allāh does not like everyone self-deluded and boastful.", TextGerman = "Und zeige den Menschen nicht geringschätzig die Wange und gehe nicht übermütig auf der Erde einher, denn Allah liebt niemanden, der eingebildet und prahlerisch ist." });
                        surah31.VerseList.Add(new Verse { Number = 19, TextBosnian = "U hodu budi odmjeren, a u govoru ne budi grlat; zbilja, najneprijatniji glas jeste glas magarca!", TextEnglish = "And be moderate in your pace and lower your voice; indeed, the most disagreeable of sounds is the voice of donkeys.'", TextGerman = "Halte das rechte Maß in deinem Gang und dämpfe deine Stimme, denn die widerwärtigste der Stimmen ist wahrlich die Stimme der Esel." });
                        surah31.VerseList.Add(new Verse { Number = 20, TextBosnian = "Kako ne vidite da vam je Allah potčinio sve što postoji na nebesima i na Zemlji i da vas darežljivo obasipa blagodatima Svojim, i vidljivim i nevidljivim?! A ima ljudi koji raspravljaju o Allahu bez ikakva znanja, bez ikakve upute i bez knjige svjetilje.", TextEnglish = "Do you not see that Allāh has made subject to you whatever is in the heavens and whatever is in the earth and amply bestowed upon you His favors, [both] apparent and unapparent? But of the people is he who disputes about Allāh without knowledge or guidance or an enlightening Book [from Him].", TextGerman = "Seht ihr nicht, daß Allah euch das, was in den Himmeln und was auf der Erde ist, dienstbar gemacht hat, und euch mit Seinen Gunsterweisen überhäuft hat - äußerlich und innerlich? Doch gibt es unter den Menschen manchen, der über Allah ohne (richtiges) Wissen, ohne Rechtleitung und ohne erleuchtendes Buch streitet." });
                        surah31.VerseList.Add(new Verse { Number = 21, TextBosnian = "A kad im se govori: 'Slijedite ono što vam Allah objavljuje!', odgovaraju: 'Ne, mi slijedimo ono što smo zapamtili od predaka naših.' Zar i onda kad ih šejtan poziva na patnju u Ognju?!", TextEnglish = "And when it is said to them, 'Follow what Allāh has revealed,' they say, 'Rather, we will follow that upon which we found our fathers.' Even if Satan was inviting them to the punishment of the Blaze?", TextGerman = "Und wenn zu ihnen gesagt wird: „Folgt dem, was Allah herabgesandt hat, sagen sie: „Nein! Vielmehr folgen wir dem, worin wir unsere Väter vorgefunden haben. Was denn, auch wenn der Satan sie zur Strafe der Feuerglut einlädt?" });
                        surah31.VerseList.Add(new Verse { Number = 22, TextBosnian = "Onaj ko se sasvim preda Allahu, a uz to još i dobro čini, uhvatio se za najčvršću vezu. Allahu se na kraju sve vraća.", TextEnglish = "And whoever submits his face [i.e., self] to Allāh while he is a doer of good - then he has grasped the most trustworthy handhold. And to Allāh will be the outcome of [all] matters.", TextGerman = "Wer sich Allah völlig hingibt und dabei Gutes tut, der hält sich an die festeste Handhabe. Und zu Allah (führt) das Ende der Angelegenheiten." });
                        surah31.VerseList.Add(new Verse { Number = 23, TextBosnian = "A onaj koji ne vjeruje, pa, neka te ne zabrinjava nevjerovanje njegovo. Nama će se svi vratiti i Mi ćemo ih o onome šta su radili obavijestiti; Allah, uistinu, dobro zna sve što u grudima je.", TextEnglish = "And whoever has disbelieved - let not his disbelief grieve you. To Us is their return, and We will inform them of what they did. Indeed, Allāh is Knowing of that within the breasts.", TextGerman = "Und wer ungläubig ist, dessen Unglaube soll dich nicht traurig machen. Zu Uns wird ihre Rückkehr sein, dann werden Wir ihnen kundtun, was sie taten. Gewiß, Allah weiß über das Innerste der Brüste Bescheid." });
                        surah31.VerseList.Add(new Verse { Number = 24, TextBosnian = "Mi im dajemo da kratko uživaju, a onda ćemo ih natjerati u patnju žestoku.", TextEnglish = "We grant them enjoyment for a little; then We will force them to a massive punishment.", TextGerman = "Wir lassen sie ein wenig genießen, dann zwingen Wir sie in eine harte Strafe." });
                        surah31.VerseList.Add(new Verse { Number = 25, TextBosnian = "A da ih upitaš: 'Ko je stvorio nebesa i Zemlju?', sigurno bi rekli: 'Allah!', a ti reci: 'Hvaljen nekaje Allah!', samo što većina njih ne zna.", TextEnglish = "And if you asked them, 'Who created the heavens and earth?' they would surely say, 'Allāh.' Say, '[All] praise is [due] to Allāh'; but most of them do not know.", TextGerman = "Und wenn du sie fragst, wer die Himmel und die Erde erschaffen hat, sagen sie ganz gewiß: „Allah. Sag: (Alles) Lob gehört Allah! Aber nein! Die meisten von ihnen wissen nicht." });
                        surah31.VerseList.Add(new Verse { Number = 26, TextBosnian = "Allahovo je sve na nebesima i na Zemlji! Allah je, uistinu, Neovisni i Hvaljeni.", TextEnglish = "To Allāh belongs whatever is in the heavens and earth. Indeed, Allāh is the Free of need, the Praiseworthy.", TextGerman = "Allah gehört, was in den Himmeln und auf der Erde ist. Gewiß, Allah ist der Unbedürftige und Lobenswürdige." });
                        surah31.VerseList.Add(new Verse { Number = 27, TextBosnian = "Da su sva stabla na Zemlji pisaljke, a more, uz pomoć još sedam mora, tinta, ne bi se ispisale Allahove riječi; Allah je, uistinu, silan i mudar.", TextEnglish = "And if whatever trees upon the earth were pens and the sea [was ink], replenished thereafter by seven [more] seas, the words[1165] of Allāh would not be exhausted. Indeed, Allāh is Exalted in Might and Wise.", TextGerman = "Und wenn auch das, was es auf der Erde an Bäumen gibt, Schreibrohre wären und das (gesamte) Meer und danach sieben weitere Meere als Nachschub (Tinte wären), würden die Worte Allahs nicht zu Ende gehen, denn Allah ist Allmächtig und Allweise." });
                        surah31.VerseList.Add(new Verse { Number = 28, TextBosnian = "I Stvoriti sve vas i sve vas oživiti, isto je kao stvoriti i oživiti jednoga čovjeka; Allah je, zaista, Onaj Koji sve čuje i Onaj Koji sve vidi.", TextEnglish = "Your creation and your resurrection will not be but as that of a single soul.[1166] Indeed, Allāh is Hearing and Seeing.", TextGerman = "Die Erschaffung und die Auferweckung von euch (allen) ist nur (so) wie bei einer einzigen Seele. Gewiß, Allah ist Allhörend und Allsehend." });
                        surah31.VerseList.Add(new Verse { Number = 29, TextBosnian = "Zar ne vidiš da Allah uvodi noć u dan i dan u noć, i da je potčinio Sunce i Mjesec - svako se kreće do roka određenog - i da Allah dobro zna ono što radite?!", TextEnglish = "Do you not see [i.e., know] that Allāh causes the night to enter the day and causes the day to enter the night and has subjected the sun and the moon, each running [its course] for a specified term, and that Allāh, of whatever you do, is Aware?", TextGerman = "Siehst du denn nicht, daß Allah die Nacht in den Tag eindringen und den Tag in die Nacht eindringen läßt und die Sonne und den Mond dienstbar gemacht hat - ein jedes läuft auf eine festgesetzte Frist zu -, und daß Allah dessen Kundig ist, was ihr tut?" });
                        surah31.VerseList.Add(new Verse { Number = 30, TextBosnian = "To je zato što je Allah- Istina, a oni kojima oni, pored Njega, dove upućuju - neistina, i što je Allah Uzvišeni i Veliki.", TextEnglish = "That is because Allāh is the True Reality, and that what they call upon[1167] other than Him is falsehood, and because Allāh is the Most High, the Grand.", TextGerman = "Dies, weil Allah die Wahrheit ist und weil das, was sie anstatt Seiner anrufen, das Falsche ist und weil Allah der Erhabene, der Große ist." });
                        surah31.VerseList.Add(new Verse { Number = 31, TextBosnian = "Zar ne vidiš da lađe Allahovom blagodati morem plove da bi vam pokazao neke znake Svoje?! U tome su, zaista, znaci za svakog ko strpljiv i zahvalan je.", TextEnglish = "Do you not see that ships sail through the sea by the favor of Allāh that He may show you of His signs? Indeed in that are signs for everyone patient and grateful.", TextGerman = "Siehst du nicht, daß die Schiffe durch die Gunst Allahs auf dem Meer fahren, damit Er euch etwas von Seinen Zeichen zeigt? Darin sind wahrlich Zeichen für jeden sehr Standhaften und sehr Dankbaren." });
                        surah31.VerseList.Add(new Verse { Number = 32, TextBosnian = "A kad ih talas, kao oblak, prekrije, mole se Allahu, iskreno Mu vjeru ispovijedajući; a čim ih On do kopna dovede, među njima osrednjih bude. A ajete i znakove Naše samo izdajnik, nezahvalnik poriče.", TextEnglish = "And when waves come over them like canopies, they supplicate Allāh, sincere to Him in religion [i.e., faith]. But when He delivers them to the land, there are [some] of them who are moderate [in faith]. And none rejects Our signs except everyone treacherous and ungrateful.", TextGerman = "Und wenn Wogen wie Schattendächer sie überdecken, rufen sie Allah an, (wobei sie) Ihm gegenüber aufrichtig in der Religion (sind). Wenn Er sie nun ans Land errettet, zeigen einige von ihnen ein gemäßigtes Verhalten. Und Unsere Zeichen verleugnet nur jeder sehr Treulose und sehr Undankbare." });
                        surah31.VerseList.Add(new Verse { Number = 33, TextBosnian = "O ljudi, bojte se Gospodara svoga i strahujte od Dana kad roditelj djetetu svome neće moći nimalo pomoći, niti će dijete moći svome roditelju imalo pomoći! Allahovo prijeteće obećanje je istinito, pa neka vas nikako život dunjalučki ne zavara i neka vas obmanitelj u vezi s Allahom ne pokoleba.", TextEnglish = "O mankind, fear your Lord and fear a Day when no father will avail his son, nor will a son avail his father at all. Indeed, the promise of Allāh is truth, so let not the worldly life delude you and be not deceived about Allāh by the Deceiver [i.e., Satan].", TextGerman = "O ihr Menschen, fürchtet euren Herrn und habt Angst vor einem Tag, an dem weder ein Vater etwas für sein Kind begleichen kann, noch ein Kind für seinen Vater etwas wird begleichen können. Gewiß, Allahs Versprechen ist wahr. So soll euch das diesseitige Leben nicht täuschen, und nicht täuschen soll euch hinsichtlich Allahs der Täuscher." });
                        surah31.VerseList.Add(new Verse { Number = 34, TextBosnian = "Samo Allah zna kad će Čas kijameta nastupiti, samo On spušta kišu i samo On zna šta je u matericama, a čovjek ne zna šta će sutra zaraditi i ne zna niko u kojoj će zemlji umrijeti; Allah je, uistinu, Onaj Koji sve, i u potpunosti, zna.", TextEnglish = "Indeed, Allāh [alone] has knowledge of the Hour and sends down the rain and knows what is in the wombs.[1168] And no soul perceives what it will earn tomorrow, and no soul perceives in what land it will die. Indeed, Allāh is Knowing and Aware.", TextGerman = "Gewiß, Allah (allein) besitzt das Wissen über die Stunde, läßt den Regen herabkommen und weiß, was im Mutterleib ist. Niemand weiß, was er morgen erwerben wird, und niemand weiß, in welchem Land er sterben wird. Gewiß, Allah ist Allwissend und Allkundig." });
                        result = surah31;
                        break;
                    #endregion

