                        surah32.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah32.VerseList.Add(new Verse { Number = 1, TextBosnian = "Elif-lam-mim.", TextEnglish = "Alif, Lām, Meem.[1170]", TextGerman = "Alif-Lam-Mim." });
                        surah32.VerseList.Add(new Verse { Number = 2, TextBosnian = "Objavljivanje Knjige, u koju nema sumnje, od Gospodara je svjetova.", TextEnglish = "[This is] the revelation of the Book about which there is no doubt from the Lord of the worlds.", TextGerman = "Die Offenbarung des Buches, an dem es keinen Zweifel gibt, ist vom Herrn der Weltenbewohner." });
                        surah32.VerseList.Add(new Verse { Number = 3, TextBosnian = "Međutim, oni govore: 'On je izmišlja!' Naprotiv, ona je Istina od Gospodara tvoga da upozoravaš narod kojem prije tebe nije došao nikakav upozoritelj, da bi se oni naputili.", TextEnglish = "Or do they say, 'He invented it'? Rather, it is the truth from your Lord, [O Muḥammad], that you may warn a people to whom no warner has come before you [so] perhaps they will be guided.", TextGerman = "Oder sagen sie: „Er hat es ersonnen? Nein! Vielmehr ist es die Wahrheit von deinem Herrn, damit du ein Volk warnst, zu denen noch kein Warner vor dir gekommen ist, auf daß sie rechtgeleitet werden mögen." });
                        surah32.VerseList.Add(new Verse { Number = 4, TextBosnian = "Allah je nebesa i Zemlju i ono što je između njih u šest dana stvorio, a zatim se nad Aršom uzvisio; vi, osim Njega, ni zaštitnika ni zauzimatelja nemate, pa zašto se ne prisjećate i pouku ne uzmete?!", TextEnglish = "It is Allāh who created the heavens and the earth and whatever is between them in six days; then He established Himself above the Throne.[1171] You have not besides Him any protector or any intercessor; so will you not be reminded?", TextGerman = "Allah ist es, Der die Himmel und die Erde und was dazwischen ist in sechs Tagen erschuf und Sich hierauf über den Thron erhob. Ihr habt außer Ihm weder Schutzherrn noch Fürsprecher. Bedenkt ihr denn nicht?" });
                        surah32.VerseList.Add(new Verse { Number = 5, TextBosnian = "On uređuje i sprovodi odredbu Svoju, od neba do Zemlje, a onda se to k Njemu penje u Danu koji hiljadu godina traje, prema vašem računanju vremena.", TextEnglish = "He arranges [each] matter from the heaven to the earth; then it will ascend to Him in a Day, the extent of which is a thousand years of those which you count.", TextGerman = "Er regelt die Angelegenheit vom Himmel bis zur Erde. Hierauf steigt sie zu Ihm auf an einem Tag, dessen Maß tausend Jahre nach eurer Berechnung sind." });
                        surah32.VerseList.Add(new Verse { Number = 6, TextBosnian = "To je Onaj Koji zna i ono što je čulima nedokučivo, a i ono što je pojavno, Silni i Milostivi.", TextEnglish = "That is the Knower of the unseen and the witnessed, the Exalted in Might, the Merciful,", TextGerman = "Jener ist der Kenner des Verborgenen und des Offenbaren, der Allmächtige und Barmherzige," });
                        surah32.VerseList.Add(new Verse { Number = 7, TextBosnian = "Koji je sve što je stvorio skladnim učinio, a stvaranje čovjeka od gline je započeo,", TextEnglish = "Who perfected everything which He created and began the creation of man from clay.", TextGerman = "Der alles gut macht, was Er erschafft. Und Er machte die Schöpfung des Menschen am Anfang aus Lehm," });
                        surah32.VerseList.Add(new Verse { Number = 8, TextBosnian = "a onda je učinio da potomstvo njegovo od uzorka, beznačajne tekućine nastaje,", TextEnglish = "Then He made his posterity out of the extract of a liquid disdained.", TextGerman = "hierauf machte Er seine Nachkommenschaft aus einem Auszug aus verächtlichem Wasser." });
                        surah32.VerseList.Add(new Verse { Number = 9, TextBosnian = "zatim ga skladno uobličuje i u njega Svoga duha udahnjuje, i On vam je sluh i vid i razum dao, a kako vi malo zahvaljujete!", TextEnglish = "Then He proportioned him and breathed into him from His [created] soul[1172] and made for you hearing and vision and hearts [i.e., intellect]; little are you grateful.", TextGerman = "Hierauf formte Er ihn zurecht und hauchte ihm von Seinem Geist ein, und Er hat euch Gehör, Augenlicht und Herzen gemacht. Wie wenig ihr dankbar seid!" });
                        surah32.VerseList.Add(new Verse { Number = 10, TextBosnian = "Oni govore: 'Zar ćemo, kad nestanemo pod zemljom, ponovo stvoreni biti' Štaviše, oni susret s Gospodarom svojim poriču.", TextEnglish = "And they say, 'When we are lost [i.e., disintegrated] within the earth, will we indeed be [recreated] in a new creation?' Rather, they are, in the meeting with their Lord, disbelievers.", TextGerman = "Und sie sagen: „Sollen wir etwa, wenn wir uns in der Erde verloren haben, denn wirklich wieder in neuer Schöpfung (erstehen)? Aber nein! Sie verleugnen die Begegnung mit ihrem Herrn." });
                        surah32.VerseList.Add(new Verse { Number = 11, TextBosnian = "Reci: 'Melek smrti, koji je za vas zadužen, duše će vam uzeti, a poslije ćete Gospodaru svome vraćeni biti.'", TextEnglish = "Say, 'The angel of death who has been entrusted with you will take you. Then to your Lord you will be returned.'", TextGerman = "Sag: Abberufen wird euch der Engel des Todes, der mit euch betraut ist, hierauf werdet ihr zu eurem Herrn zurückgebracht." });
                        surah32.VerseList.Add(new Verse { Number = 12, TextBosnian = "A da ti je vidjeti kako će prestupnici, oborenih glava, pred Gospodarom svojim biti: 'Gospodaru naš, vidjeli smo i čuli smo, pa nas vrati da dobra djela činimo; mi doista, čvrsto vjerujemo?'", TextEnglish = "If you could but see when the criminals are hanging their heads before their Lord, [saying], 'Our Lord, we have seen and heard, so return us [to the world]; we will work righteousness. Indeed, we are [now] certain.'", TextGerman = "Könntest du nur sehen, wenn die Übeltäter vor ihrem Herrn die Köpfe hängenlassen: „Unser Herr, jetzt haben wir gesehen und gehört. Bringe uns zurück, so wollen wir rechtschaffen handeln. Gewiß, wir sind nun überzeugt!" });
                        surah32.VerseList.Add(new Verse { Number = 13, TextBosnian = "A kad bismo htjeli, svakom čovjeku bismo Uputu dali, ali obistinile su se Moje riječi: 'Napunit ću, zaista, Džehennem džinima i ljudima zajedno!'", TextEnglish = "And if We had willed, We could have given every soul its guidance, but the word[1173] from Me will come into effect [that] 'I will surely fill Hell with jinn and people all together.", TextGerman = "Und wenn Wir gewollt hätten, hätten Wir jeder Seele ihre Rechtleitung gegeben. Aber (nun) ist das Wort von Mir unvermeidlich fällig geworden: „Ganz gewiß werde Ich die Hölle mit den Ginn und den Menschen allesamt füllen." });
                        surah32.VerseList.Add(new Verse { Number = 14, TextBosnian = "Pa iskusite patnju zato što ste zaboravljali da ćete ovaj Dan doživjeti - i Mi ćemo vas zaboraviti - i vječnu patnju iskusite zbog onoga što ste radili.", TextEnglish = "So taste [punishment] because you forgot the meeting of this, your Day; indeed, We have [accordingly] forgotten you. And taste the punishment of eternity for what you used to do.'", TextGerman = "So kostet (es) dafür, daß ihr die Begegnung mit diesem eurem Tag vergessen habt. Gewiß, Wir haben euch (auch) vergessen. Kostet die ewige Strafe für das, was ihr zu tun pflegtet." });
                        surah32.VerseList.Add(new Verse { Number = 15, TextBosnian = "U Naše riječi vjeruju samo oni koji, kad njima budu opomenuti, licem na tlo padaju, i koji Gospodara svoga veličaju i hvale i koji se ne ohole.", TextEnglish = "Only those believe in Our verses who, when they are reminded by them, fall down in prostration and exalt [Allāh] with praise of their Lord, and they are not arrogant.", TextGerman = "Nur diejenigen glauben an Unsere Zeichen, die, wenn sie damit ermahnt werden, ehrerbietig niederfallen und ihren Herrn lobpreisen und sich nicht hochmütig verhalten." });
                        surah32.VerseList.Add(new Verse { Number = 16, TextBosnian = "Podižu se bokovi njihovi iz postelja i mole svoga Gospodara iz straha i želje, a dio onog što im Mi dajemo udjeljuju.", TextEnglish = "Their sides part [i.e., they arise] from [their] beds; they supplicate their Lord in fear and aspiration, and from what We have provided them, they spend.[1174]", TextGerman = "Ihre Seiten weichen vor den Schlafstätten zurück; sie rufen ihren Herrn in Furcht und Begehren an und geben von dem, womit Wir sie versorgt haben, aus." });
                        surah32.VerseList.Add(new Verse { Number = 17, TextBosnian = "I niko ne zna šta je za njih skriveno, a što će oči njihove obradovati, kao nagrada za ono što su činili.", TextEnglish = "And no soul knows what has been hidden for them of comfort for eyes [i.e., satisfaction] as reward for what they used to do.", TextGerman = "Keine Seele weiß, welche Freuden im Verborgenen für sie bereitgehalten werden als Lohn für das, was sie zu tun pflegten." });
                        surah32.VerseList.Add(new Verse { Number = 18, TextBosnian = "Zar da isti budu vjernik i onaj ko je buntovan?! Ne, oni nisu jednaki!", TextEnglish = "Then is one who was a believer like one who was defiantly disobedient? They are not equal.", TextGerman = "Ist denn jemand, der gläubig ist, wie jemand, der ein Frevler ist? Sie sind nicht gleich(zustellen)." });
                        surah32.VerseList.Add(new Verse { Number = 19, TextBosnian = "One koji su vjerovali i dobra djela činili - čekaju džennetske bašče u kojima će boraviti, kao nagrada za ono što su radili.", TextEnglish = "As for those who believed and did righteous deeds, for them will be the Gardens of Refuge as accommodation for what they used to do.", TextGerman = "Was nun diejenigen angeht, die glauben und rechtschaffene Werke tun, so wird es für sie die Gärten der Zuflucht zur gastlichen Aufnahme geben für das, was sie zu tun pflegten." });
                        surah32.VerseList.Add(new Verse { Number = 20, TextBosnian = "A one koji su bili buntovni - čeka Vatra, u kojoj će prebivati; kad god pokušaju da iz nje iziđu, bit će u nju vraćeni i bit će im rečeno: 'Iskusite kaznu u Vatri koju ste poricali!'", TextEnglish = "But as for those who defiantly disobeyed, their refuge is the Fire. Every time they wish to emerge from it, they will be returned to it while it is said to them, 'Taste the punishment of the Fire which you used to deny.'", TextGerman = "Was aber diejenigen angeht, die freveln, so wird das (Höllen)feuer ihr Zufluchtsort sein. Jedesmal, wenn sie aus ihm herauskommen wollen, werden sie dahinein zurückgebracht, und es wird zu ihnen gesagt: „Kostet die Strafe des (Höllen)feuers, die ihr für Lüge zu erklären pflegtet." });
                        surah32.VerseList.Add(new Verse { Number = 21, TextBosnian = "A mi ćemo, doista, dati da iskuse patnju manju prije patnje velike, ne bi li se oni povratili.", TextEnglish = "And We will surely let them taste the nearer punishment[1175] short of the greater punishment that perhaps they will return [i.e., repent].", TextGerman = "Wir werden sie ganz gewiß etwas von der diesseitigen Strafe vor der größeren Strafe kosten lassen, auf daß sie umkehren mögen." });
                        surah32.VerseList.Add(new Verse { Number = 22, TextBosnian = "Ima li većega zulumćara od onoga koji je opomenut ajetima i znacima svoga Gospodara, pa se potom okreće od njih?! Mi ćemo, zaista, kazniti prestupnike!", TextEnglish = "And who is more unjust than one who is reminded of the verses of his Lord; then he turns away from them? Indeed We, from the criminals, will take retribution.", TextGerman = "Und wer ist ungerechter als jemand, der mit den Zeichen seines Herrn ermahnt wird und sich hierauf von ihnen abwendet? Gewiß, Wir werden an den Übeltätern Vergeltung üben." });
                        surah32.VerseList.Add(new Verse { Number = 23, TextBosnian = "Musau smo Mi Knjigu dali - pa ne sumnjaj nimalo u susret s njim - i uputom je sinovima Israilovim učinili.", TextEnglish = "And We certainly gave Moses the Scripture, so do not be in doubt over his meeting.[1176] And We made it [i.e., the Torah] guidance for the Children of Israel.", TextGerman = "Wir gaben bereits Musa die Schrift - so sei nicht im Zweifel über die Begegnung mit Ihm - und machten sie zu einer Rechtleitung für die Kinder Isra’ils." });
                        surah32.VerseList.Add(new Verse { Number = 24, TextBosnian = "Između njih Mi smo vođe određivali i oni su, odazivajući se zapovijedi Našoj, na Pravi put upućivali, jer su strpljivi bili i u ajete i znakove Naše čvrsto vjerovali.", TextEnglish = "And We made from among them leaders guiding by Our command when they were patient and [when] they were certain of Our signs.", TextGerman = "Und Wir bestellten unter ihnen Vorbilder, die (sie) nach Unserem Befehl leiteten, als sie sich standhaft gezeigt hatten und von Unseren Zeichen überzeugt waren." });
                        surah32.VerseList.Add(new Verse { Number = 25, TextBosnian = "Zaista će Gospodar tvoj - baš On - među njima na Kijametskom danu presuditi o onome u čemu su se razilazili.", TextEnglish = "Indeed, your Lord will judge between them on the Day of Resurrection concerning that over which they used to differ.", TextGerman = "Gewiß, dein Herr wird zwischen ihnen am Tag der Auferstehung über das entscheiden, worüber sie uneinig waren." });
                        surah32.VerseList.Add(new Verse { Number = 26, TextBosnian = "Zar im nije jasno koliko smo Mi prije njih naroda uništili, po čijim oni nastambama hodaju?! U tome su, zaista, znakovi, pa zašto ne čuju?!", TextEnglish = "Has it not become clear to them how many generations We destroyed before them, [as] they walk among their dwellings? Indeed in that are signs; then do they not hear?", TextGerman = "Ist ihnen nicht deutlich geworden, wie viele Geschlechter Wir vor ihnen vernichtet haben, in deren Wohnorten sie (nun) umhergehen? Darin sind wahrlich Zeichen. Wollen sie denn nicht hören?" });
                        surah32.VerseList.Add(new Verse { Number = 27, TextBosnian = "Kako oni ne vide da Mi tjeramo kišu u ogoljelu zemlju, i činimo da, uz pomoć njenu, niče rastinje kojim se hrani stoka njihova, a i oni sami - pa zašto ne vide?!", TextEnglish = "Have they not seen that We drive water [in clouds] to barren land and bring forth thereby crops from which their livestock eat and [they] themselves? Then do they not see?", TextGerman = "Sehen sie denn nicht, daß Wir das Wasser zum dürren Land treiben und dann dadurch Pflanzen hervorbringen, von denen ihr Vieh und sie selbst essen? Wollen sie denn nicht einsichtig sein?" });
                        surah32.VerseList.Add(new Verse { Number = 28, TextBosnian = "I oni govore: 'Kad će već jednom ta pobjeda, ako istinu govorite?'", TextEnglish = "And they say, 'When will be this conquest,[1177] if you should be truthful?'", TextGerman = "Und sie sagen: „Wann wird diese Entscheidung eintreten, wenn ihr wahrhaftig seid?" });
                        surah32.VerseList.Add(new Verse { Number = 29, TextBosnian = "Reci: 'Na Dan pobjede nevjernicima neće nikako koristiti to što će tada vjerovati i nimalo im se vremena neće dati.'", TextEnglish = "Say, [O Muḥammad], 'On the Day of Conquest the belief of those who had disbelieved will not benefit them, nor will they be reprieved.'", TextGerman = "Sag: Am Tag der Entscheidung wird denjenigen, die ungläubig waren, ihr Glaube nicht (mehr) nützen, noch wird ihnen Aufschub gewährt." });
                        surah32.VerseList.Add(new Verse { Number = 30, TextBosnian = "A ti se okreni od njih i čekaj, i oni, doista, čekaju!", TextEnglish = "So turn away from them and wait. Indeed, they are waiting.", TextGerman = "So wende dich ab von ihnen und warte ab; sie warten ebenfalls ab." });
                        result = surah32;
                        break;
                    #endregion

                    // 
                    #region
                    case 33:
                        Surah surah33 = new Surah(); surah33.Name = Constants.Surah33Name; surah33.Number = 33; surah33.VerseList = new List<Verse>();
                        surah33.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah33.VerseList.Add(new Verse { Number = 1, TextBosnian = "O Vjerovjesniče, Allaha se boj, a nevjernike i licemjere ne slušaj - Allah je, uistinu, Onaj Koji sve zna i mudar je!", TextEnglish = "O Prophet, fear Allāh and do not obey the disbelievers and the hypocrites. Indeed, Allāh is ever Knowing and Wise.", TextGerman = "O Prophet, fürchte Allah und gehorche nicht den Ungläubigen und den Heuchlern. Gewiß, Allah ist Allwissend und Allweise." });
                        surah33.VerseList.Add(new Verse { Number = 2, TextBosnian = "I slijedi ono što ti se od Gospodara tvoga objavljuje - Allah, zaista, u potpunosti zna ono što radite!", TextEnglish = "And follow that which is revealed to you from your Lord. Indeed Allāh is ever, of what you do, Aware.", TextGerman = "Und folge dem, was dir (als Offenbarung) von deinem Herrn eingegeben wird. Gewiß, Allah ist dessen, was ihr tut, Kundig." });
                        surah33.VerseList.Add(new Verse { Number = 3, TextBosnian = "I na Allaha se osloni, a Allah je kao zaštitinik dovoljan!", TextEnglish = "And rely upon Allāh; and sufficient is Allāh as Disposer of affairs.[1179]", TextGerman = "Und verlasse dich auf Allah. Allah genügt als Sachwalter." });
                        surah33.VerseList.Add(new Verse { Number = 4, TextBosnian = "Allah nijednom čovjeku dva srca u nutrini nije dao, a ni supruge vaše, od kojih se rastavljate govoreći da su vam njihova leđa poput leđa vaših majki, majkama vašim nije učinio, niti je posinke vaše sinovima vašim učinio. To su samo vaše riječi, iz vaših usta, a Allah Istinu govori i na Pravi put upućuje.", TextEnglish = "Allāh has not made for a man two hearts in his interior. And He has not made your wives whom you declare unlawful[1180] your mothers. And He has not made your claimed [i.e., adopted] sons your [true] sons. That is [merely] your saying by your mouths, but Allāh says the truth, and He guides to the [right] way.", TextGerman = "Allah hat keinem Mann zwei Herzen in seinem Inneren gemacht. Und Er hat eure Gattinnen, von denen ihr euch durch den Rückenschwur trennt, nicht (wirklich) zu euren Müttern gemacht. Und Er hat eure angenommenen Söhne nicht (wirklich) zu euren Söhnen gemacht. Das sind eure Worte aus eurem (eigenen) Mund. Aber Allah sagt die Wahrheit, und Er leitet den (rechten) Weg." });
                        surah33.VerseList.Add(new Verse { Number = 5, TextBosnian = "Zovite ih po očevima njihovim, to je kod Allaha pravednije! A ako ne znate očeve njihove, pa oni su braća vaša po vjeri i štićenici su vaši. Nije vam grijeh ako u tome pogriješite, grijeh je ako to vaša srca smjeraju; a Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "Call them[1181] by [the names of] their fathers; it is more just in the sight of Allāh. But if you do not know their fathers - then they are [still] your brothers in religion and those entrusted to you. And there is no blame upon you for that in which you have erred but [only for] what your hearts intended. And ever is Allāh Forgiving and Merciful.", TextGerman = "Nennt sie nach ihren Vätern; das ist gerechter vor Allah. Wenn ihr ihre Väter nicht kennt, dann sind sie eure Brüder in der Religion und eure Schützlinge. Es ist für euch keine Sünde in dem, was ihr an Fehlern begeht, sondern was eure Herzen vorsätzlich anstreben. Und Allah ist Allvergebend und Barmherzig." });
                        surah33.VerseList.Add(new Verse { Number = 6, TextBosnian = "Vjerovjesnik je bliži vjernicima nego oni sami sebi, a supruge njegove su - kao majke njihove. A srodnici, po Allahovoj Knjizi, bliži su jedni drugima od ostalih vjernika i muhadžira, osim što prijateljima svojim možete dobro učiniti. To je u Knjizi zapisano.", TextEnglish = "The Prophet is more worthy of the believers than themselves,[1182] and his wives are [in the position of] their mothers. And those of [blood] relationship are more entitled [to inheritance] in the decree of Allāh than the [other] believers and the emigrants, except that you may do to your close associates a kindness [through bequest]. That was in the Book[1183] inscribed.", TextGerman = "Der Prophet steht den Gläubigen näher als sie sich selbst, und seine Gattinnen sind ihre Mütter. Und die Blutsverwandten stehen nach dem Buch Allahs einander näher als die Gläubigen und Auswanderer, außer, daß ihr euren Schützlingen Gutes tun solltet. Dies steht im Buch verzeichnet." });
                        surah33.VerseList.Add(new Verse { Number = 7, TextBosnian = "Sjeti se kad smo od vjerovjesnika zavjet njihov uzeli, i od tebe, i od Nuha, i od Ibrahima, i od Musaa, i od Isaa, sina Merjemina. Od njih smo čvrst zavjet uzeli,", TextEnglish = "And [mention, O Muḥammad], when We took from the prophets their covenant and from you and from Noah and Abraham and Moses and Jesus, the son of Mary; and We took from them a solemn covenant", TextGerman = "Und (gedenke,) als Wir von den Propheten ihr Versprechen abnahmen, und auch von dir und von Nuh, Ibrahim, Musa und ’Isa, dem Sohn Maryams; Wir nahmen ihnen ein festes Versprechen ab," });
                        surah33.VerseList.Add(new Verse { Number = 8, TextBosnian = "da bi On pitao iskrene o njihovoj iskrenosti; a nevjernicima je On pripremio bolnu patnju.", TextEnglish = "That He may question the truthful about their truth.[1184] And He has prepared for the disbelievers a painful punishment.", TextGerman = "damit Er die Wahrhaftigen nach ihrer Wahrhaftigkeit frage. Und Er hat für die Ungläubigen schmerzhafte Strafe bereitet." });
                        surah33.VerseList.Add(new Verse { Number = 9, TextBosnian = "O vjernici, sjetite se Allahove blagodati prema vama kada su do vas vojske došle, pa smo Mi protiv njih vjetar poslali, i vojske koje vi niste vidjeli- a Allah dobro vidi šta vi radite.", TextEnglish = "O you who have believed, remember the favor of Allāh upon you when armies came to [attack] you and We sent upon them a wind and armies [of angels] you did not see. And ever is Allāh, of what you do, Seeing.", TextGerman = "O die ihr glaubt, gedenkt der Gunst Allahs an euch, als Heerscharen zu euch kamen. Da sandten Wir gegen sie einen Wind und Heerscharen, die ihr nicht saht. Und was ihr tut, sieht Allah wohl." });
                        surah33.VerseList.Add(new Verse { Number = 10, TextBosnian = "Kad su vam došle i odozgo i odozdo, kad su vam oči razrogačene bile, a duša došla do grkljana, i kad ste o Allahu svašta pomišljali.", TextEnglish = "[Remember] when they came at you from above you and from below you, and when eyes shifted [in fear], and hearts reached the throats, and you assumed about Allāh [various] assumptions.", TextGerman = "Als sie von oben zu euch kamen und von unten her, und als die Blicke unsicher wurden und die Herzen die Kehle erreichten und ihr von Allah verschiedene Meinungen hattet." });
                        surah33.VerseList.Add(new Verse { Number = 11, TextBosnian = "Tada su vjernici bili u iskušenje stavljeni i žestoko su bili uzdrmani.", TextEnglish = "There the believers were tested and shaken with a severe shaking.", TextGerman = "Dort wurden die Gläubigen geprüft und heftig erschüttert." });
                        surah33.VerseList.Add(new Verse { Number = 12, TextBosnian = "I kad su licemjeri i oni u čijim srcima je bolest govorili: 'Allah i Poslanik Njegov su nam samo obmanu obećavali!'", TextEnglish = "And [remember] when the hypocrites and those in whose hearts is disease said, 'Allāh and His Messenger did not promise us except delusion,'", TextGerman = "Als die Heuchler und diejenigen, in deren Herzen Krankheit ist, sagten: „Allah und Sein Gesandter haben uns nur Trügerisches versprochen." });
                        surah33.VerseList.Add(new Verse { Number = 13, TextBosnian = "Kad su neki među njima rekli: 'O stanovnici Jesriba, ovdje vam nema stanka, zato se vratite', a drugi među njima tražili su dopuštenje od Vjerovjesnika i govorili: 'Kuće su naše nezaštićene!', a nisu bile nezaštićene, već su oni samo umaći htjeli.", TextEnglish = "And when a faction of them said, 'O people of Yathrib,[1185] there is no stability for you [here], so return [home].' And a party of them asked permission of the Prophet, saying, 'Indeed, our houses are exposed [i.e., unprotected],' while they were not exposed. They did not intend except to flee.", TextGerman = "Und als eine Gruppe von ihnen sagte: „O ihr Leute von Yatrib, ihr könnt euch (hier) nicht aufhalten. Kehrt zurück. Und ein Teil von ihnen bat den Propheten um Erlaubnis, (heimzukehren,) indem sie sagten: „Unsere Häuser sind ohne Schutz. Dabei waren sie nicht ohne Schutz, sie wollten nur fliehen." });
                        surah33.VerseList.Add(new Verse { Number = 14, TextBosnian = "A da se do njih s raznih medinskih strana prodrlo i da je potom od njih zatražena smutnja - nevjerovanje, oni bi to, ne dvoumeći se o tome, brzo učinili,", TextEnglish = "And if they had been entered upon from all its [surrounding] regions and fitnah [i.e., disbelief] had been demanded of them, they would have done it and not hesitated over it except briefly.", TextGerman = "Wäre man von ihren (verschiedenen) Bezirken her zu ihnen eingedrungen und wären sie dann aufgefordert worden, der Versuchung nachzugeben, sie hätten es begangen, und sie hätten dabei nur kurze Zeit gezögert." });
                        surah33.VerseList.Add(new Verse { Number = 15, TextBosnian = "a bili su se još prije Allahu obavezali da neće uzmicati - a za Allahu datu obavezu odgovarat će se.", TextEnglish = "And they had already promised Allāh before not to turn their backs [i.e., flee]. And ever is the promise to Allāh [that about which one will be] questioned.", TextGerman = "Dabei hatten sie sich zuvor gegenüber Allah verpflichtet, sie würden nicht den Rücken kehren. Nach der (Erfüllung der) Verpflichtung gegenüber Allah wird gefragt werden." });
                        surah33.VerseList.Add(new Verse { Number = 16, TextBosnian = "Reci: 'Bježanje vam neće koristiti ako i pobjegnete iz straha od smrti ili pogibije, jer i poslije toga, samo ćete kratko uživati.", TextEnglish = "Say, [O Muḥammad], 'Never will fleeing benefit you if you should flee from death or killing; and then [if you did], you would not be given enjoyment [of life] except for a little.'", TextGerman = "Sag: Die Flucht wird euch nicht nützen, wenn ihr davor flieht, zu sterben oder getötet zu werden. Und dann wird euch nur noch ein wenig Nießbrauch gewährt." });
                        surah33.VerseList.Add(new Verse { Number = 17, TextBosnian = "Reci: 'Ko će vas od Allaha zaštititi ako On hoće da vas zlo snađe, ili, ko vam može nauditi ako On želi da vam milost ukaže' Osim Allaha, oni neće naći sebi ni zaštitnika ni pomagača.", TextEnglish = "Say, 'Who is it that can protect you from Allāh[1186] if He intends for you an ill or intends for you a mercy?' And they will not find for themselves besides Allāh any protector or any helper.", TextGerman = "Sag: Wer ist es denn, der euch vor Allah schützen könnte, wenn Er euch Böses will oder wenn Er euch Barmherzigkeit (erweisen) will? Und sie werden für sich vor Allah weder Schutzherrn noch Helfer finden." });
                        surah33.VerseList.Add(new Verse { Number = 18, TextBosnian = "Allah dobro zna one među vama koji su druge zadržali i prijateljima svojim govorili: 'Priključite nam se!', a inače oni u borbu rijetko idu,", TextEnglish = "Already Allāh knows the hinderers[1187] among you and those [hypocrites] who say to their brothers, 'Come to us,'[1188] and do not go to battle, except for a few,[1189]", TextGerman = "Allah kennt wohl diejenigen von euch, die (die anderen) behindern und die zu ihren Brüdern sagen: „Kommt her zu uns. Und sie lassen sich nur wenig auf den Kampf ein" });
                        surah33.VerseList.Add(new Verse { Number = 19, TextBosnian = "škrtareći prema vama. A kad zavlada strah, vidiš ih kako gledaju u tebe kolutajući očima kao kad pred smrt izbezumljeni svijest izgubi. Čim strah mine, oni vas 'sijeku svojim oštrim jezicima', škrti da učine bilo kakvo dobro. Oni ne vjeruju, i zato je Allah djela njihova poništio; a to je Allahu lahko.", TextEnglish = "Indisposed[1190] toward you. And when fear comes, you see them looking at you, their eyes revolving like one being overcome by death. But when fear departs, they lash you with sharp tongues, indisposed toward [any] good. Those have not believed, so Allāh has rendered their deeds worthless, and ever is that, for Allāh, easy.", TextGerman = "und sind dabei habsüchtig euch gegenüber. Wenn aber die Angst sich einstellt, siehst du, wie sie dich mit kreisenden Augen anschauen wie einer, der vor dem Tod ohnmächtig wird. Wenn dann die Angst gewichen ist, verletzen sie euch mit scharfen Zungen aus Habsucht nach dem (erbeuteten) Gut. Jene haben nicht (wirklich) geglaubt; darum läßt Allah ihre Werke hinfällig werden; und dies ist Allah ein leichtes." });
                        surah33.VerseList.Add(new Verse { Number = 20, TextBosnian = "Oni misle da saveznici još nisu otišli. A kad bi saveznici opet došli, najdraže bi im bilo da su među beduinima u pustinji i da se raspituju za vas; a da s vama ostanu, malo bi se borili.", TextEnglish = "They think the companies have not [yet] withdrawn.[1191] And if the companies should come [again], they would wish they were in the desert among the bedouins, inquiring [from afar] about your news. And if they should be among you, they would not fight except for a little.", TextGerman = "Sie meinen, die Gruppierungen seien nicht weggegangen. Und wenn die Gruppierungen (wieder)kommen sollten, möchten sie gern, sie wären in der Wüste unter den Wüstenarabern und würden sich (nur) über euch erkundigen. Und wenn sie unter euch wären, würden sie nur wenig kämpfen." });
                        surah33.VerseList.Add(new Verse { Number = 21, TextBosnian = "Vi u Allahovom Poslaniku imate divan uzor za onoga koji nadu polaže u Allaha i Posljednji dan, i koji često Allaha spominje.", TextEnglish = "There has certainly been for you in the Messenger of Allāh an excellent pattern[1192] for anyone whose hope is in Allāh and the Last Day and [who] remembers Allāh often.", TextGerman = "Ihr habt ja im Gesandten Allahs ein schönes Vorbild, (und zwar) für einen jeden, der auf Allah und den Jüngsten Tag hofft und Allahs viel gedenkt." });
                        surah33.VerseList.Add(new Verse { Number = 22, TextBosnian = "A kad su vjernici saveznike ugledali, rekli su: 'Ovo je ono što su nam Allah i Poslanik Njegov obećali, i Allah i Poslanik Njegov istinu su govorili!' - i to im je samo povećalo vjerovanje i predanost.", TextEnglish = "And when the believers saw the companies, they said, 'This is what Allāh and His Messenger had promised us, and Allāh and His Messenger spoke the truth.' And it increased them only in faith and acceptance.", TextGerman = "Und als die Gläubigen die Gruppierungen sahen, sagten sie: „Das ist, was Allah und Sein Gesandter uns versprochen haben; Allah und Sein Gesandter sagen die Wahrheit. Und es mehrte ihnen nur den Glauben und die Ergebung." });
                        surah33.VerseList.Add(new Verse { Number = 23, TextBosnian = "Među vjernicima ima ljudi koji su ispunili ono na što su se Allahu obavezali; neko od njih okonča svoj rok, a neko od njih to još iščekuje, i nisu ništa izmijenili.", TextEnglish = "Among the believers are men true to what they promised Allāh. Among them is he who has fulfilled his vow [to the death], and among them is he who awaits [his chance]. And they did not alter [the terms of their commitment] by any alteration -", TextGerman = "Unter den Gläubigen gibt es Männer, die das wahr gemacht haben, wozu sie sich Allah gegenüber verpflichteten. Unter ihnen gibt es manche, die ihr Gelübde erfüllt haben; und unter ihnen gibt es manche, die noch warten (müssen). Und sie haben keine Änderung vorgenommen;" });
                        surah33.VerseList.Add(new Verse { Number = 24, TextBosnian = "To je zato da bi Allah nagradio iskrene za njihovu iskrenost, a patnji podvrgao licemjere, ako hoće, ili im oprostio. Allah je, zaista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "That Allāh may reward the truthful for their truth and punish the hypocrites if He wills or accept their repentance. Indeed, Allāh is ever Forgiving and Merciful.", TextGerman = "damit Allah den Wahrhaftigen ihre Wahrhaftigkeit vergelte und die Heuchler strafe, wenn Er will, oder sich ihnen Reue-Annehmend zuwende. Gewiß, Allah ist Allvergebend und Barmherzig." });
                        surah33.VerseList.Add(new Verse { Number = 25, TextBosnian = "Allah je nevjernike pune srdžbe odbio, nisu dobra postigli, a vjernike je Allah borbe poštedio. Allah je, uistinu, moćan i silan.", TextEnglish = "And Allāh repelled those who disbelieved, in their rage, not having obtained any good. And sufficient was Allāh for the believers in battle, and ever is Allāh Powerful and Exalted in Might.", TextGerman = "Und Allah wies diejenigen, die ungläubig waren, mit ihrem Grimm zurück, ohne daß sie etwas Gutes erlangt hätten. Und Allah ersparte den Gläubigen den Kampf. Allah ist Stark und Allmächtig." });
                        surah33.VerseList.Add(new Verse { Number = 26, TextBosnian = "A sljedbenike Knjige, koji su ih pomagali, iz utvrda njihovih je izveo, i strah u srca njihova ulio, pa ste jedne pobili, a druge kao sužnje uzeli.", TextEnglish = "And He brought down those who supported them among the People of the Scripture[1193] from their fortresses and cast terror into their hearts [so that] a party [i.e., their men] you killed, and you took captive a party [i.e., the women and children].", TextGerman = "Und Er ließ diejenigen von den Leuten der Schrift, die ihnen beigestanden hatten, aus ihren Burgen heruntersteigen. Und Er jagte in ihre Herzen Schrecken; eine Gruppe (von ihnen) habt ihr getötet und eine Gruppe gefangengenommen." });
                        surah33.VerseList.Add(new Verse { Number = 27, TextBosnian = "I dao vam je da naslijedite zemlje njihove i domove njihove i bogatstva njihova, i zemlju kojom prije niste hodili; Allah nad svime ima moć.", TextEnglish = "And He caused you to inherit their land and their homes and their properties and a land which you have not trodden.[1194] And ever is Allāh, over all things, competent.", TextGerman = "Und Er gab euch zum Erbe ihr Land, ihre Wohnstätten und ihren Besitz und auch ein Land, das ihr (vorher) nicht betreten hattet. Und Allah hat zu allem die Macht." });
                        surah33.VerseList.Add(new Verse { Number = 28, TextBosnian = "O Vjerovjesniče, reci ženama svojim: 'Ako želite život dunjalučki i njegov sjaj, onda dođite, dat ću vam pristojnu otpremu i lijepo ću vas otpustiti.", TextEnglish = "O Prophet, say to your wives, 'If you should desire the worldly life and its adornment, then come, I will provide for you and give you a gracious release.", TextGerman = "O Prophet, sag zu deinen Gattinnen: Wenn ihr das diesseitige Leben und seinen Schmuck haben wollt, dann kommt her, ich werde euch eine Abfindung gewähren und euch auf schöne Weise freigeben." });
                        surah33.VerseList.Add(new Verse { Number = 29, TextBosnian = "A ako želite Allaha i Poslanika Njegova i drugi svijet, pa, Allah je, doista, onima među vama koji čine dobra djela pripremio nagradu veliku.", TextEnglish = "But if you should desire Allāh and His Messenger and the home of the Hereafter - then indeed, Allāh has prepared for the doers of good among you a great reward.'", TextGerman = "Wenn ihr aber Allah und Seinen Gesandten und die jenseitige Wohnstätte haben wollt, so hat Allah für diejenigen von euch, die Gutes tun, großartigen Lohn berei tet." });
                        surah33.VerseList.Add(new Verse { Number = 30, TextBosnian = "O žene Vjerovjesnikove, ako bi koja od vas očiti razvrat učinila, kazna bi joj udvostručena bila, a to je Allahu lahko;", TextEnglish = "O wives of the Prophet, whoever of you should commit a clear immorality - for her the punishment would be doubled two fold, and ever is that, for Allāh, easy.", TextGerman = "O Frauen des Propheten, wer von euch etwas klar Abscheuliches begeht, derjenigen wird die Strafe verzweifacht. Und das ist Allah ein leichtes." });
                        surah33.VerseList.Add(new Verse { Number = 31, TextBosnian = "a onoj koja se bude Allahu i Poslaniku Njegovu pokoravala i dobra djela činila - dat ćemo nagradu dvostruku i njoj smo opskrbu plemenitu pripremili.", TextEnglish = "And whoever of you devoutly obeys Allāh and His Messenger and does righteousness - We will give her her reward twice; and We have prepared for her a noble provision.", TextGerman = "Und wer von euch Allah und Seinem Gesandten demütig ergeben ist und rechtschaffen handelt, der geben Wir ihren Lohn zweimal. Und Wir haben für sie ehrenvolle Versorgung bereitet." });
                        surah33.VerseList.Add(new Verse { Number = 32, TextBosnian = "O žene Vjerovjesnikove, vi niste kao druge žene! Ako se Allaha bojite, nemojte zavodljivo govoriti, pa da tada žudnju osjeti onaj u čijem je srcu bolest, i govorite riječi koje su kao lijepe poznate!", TextEnglish = "O wives of the Prophet, you are not like anyone among women. If you fear Allāh, then do not be soft in speech [to men],[1195] lest he in whose heart is disease should covet, but speak with appropriate speech.", TextGerman = "O Frauen des Propheten, ihr seid nicht wie irgendeine von den (übrigen) Frauen. Wenn ihr gottesfürchtig seid, dann seid nicht unterwürfig im Reden, damit nicht derjenige, in dessen Herzen Krankheit ist, begehrlich wird, sondern sagt geziemende Worte." });
                        surah33.VerseList.Add(new Verse { Number = 33, TextBosnian = "U kućama svojim boravite i ljepotu svoju, kao što je bilo u prvo pagansko doba, ne pokazujte, i namaz obavljajte i zekat dajite, i pokoravajte se Allahu i Njegovom Poslaniku! Allah želi da od vas, Porodico Poslanikova, grijehe odstrani, i da vas potpuno očisti.", TextEnglish = "And abide in your houses and do not display yourselves as [was] the display of the former times of ignorance. And establish prayer and give zakāh and obey Allāh and His Messenger. Allāh intends only to remove from you the impurity [of sin], O people of the [Prophet's] household, and to purify you with [extensive] purification.", TextGerman = "Haltet euch in euren Häusern auf; und stellt euch nicht zur Schau wie in der Zeit der früheren Unwissenheit. Verrichtet das Gebet und entrichtet die Abgabe und gehorcht Allah und Seinem Gesandten. Allah will gewiß nur den Makel von euch entfernen, ihr Angehörigen des Hauses, und euch völlig rein machen." });
                        surah33.VerseList.Add(new Verse { Number = 34, TextBosnian = "I pamtite Allahove ajete i mudrost koja se kazuje u domovima vašim; Allah je, uistinu, dobrostiv i Onaj Koji sve u potpunosti zna.", TextEnglish = "And remember what is recited in your houses of the verses of Allāh and wisdom.[1196] Indeed, Allāh is ever Subtle[1197] and Aware.", TextGerman = "Und gedenkt dessen, was in euren Häusern von den Zeichen Allahs und von der Weisheit verlesen wird. Gewiß, Allah ist Feinfühlig und Allkundig." });
                        surah33.VerseList.Add(new Verse { Number = 35, TextBosnian = "Muslimanima i muslimankama, i vjernicima i vjernicama, i poslušnim muškarcima i poslušnim ženama, i iskrenim muškarcima i iskrenim ženama, i strpljivim muškarcima i strpljivim ženama, i poniznim muškarcima i poniznim ženama, i muškarcima koji dijele zekat i sadaku i ženama koje dijele zekat i sadaku, i muškarcima koji poste i ženama koje poste, i muškarcima koji o svojim stidnim mjestima vode brigu i ženama koje o svojim stidnim mjestima vode brigu, i muškarcima koji često spominju Allaha i ženama koje često spominju Allaha - Allah je, doista, za sve njih oprost i veliku nagradu pripremio.", TextEnglish = "Indeed, the Muslim men and Muslim women, the believing men and believing women, the obedient men and obedient women, the truthful men and truthful women, the patient men and patient women, the humble men and humble women, the charitable men and charitable women, the fasting men and fasting women, the men who guard their private parts and the women who do so, and the men who remember Allāh often and the women who do so - for them Allāh has prepared forgiveness and a great reward.", TextGerman = "Gewiß, muslimische Männer und muslimische Frauen, gläubige Männer und gläubige Frauen, ergebene Männer und ergebene Frauen, wahrhaftige Männer und wahrhaftige Frauen, standhafte Männer und standhafte Frauen, demütige Männer und demütige Frauen, Almosen gebende Männer und Almosen gebende Frauen, fastende Männer und fastende Frauen, Männer, die ihre Scham hüten und Frauen, die (ihre Scham) hüten, und Allahs viel gedenkende Männer und gedenkende Frauen für (all) sie hat Allah Vergebung und großartigen Lohn bereitet." });
                        surah33.VerseList.Add(new Verse { Number = 36, TextBosnian = "Ni vjernik ni vjernica nemaju izbora da, kada Allah i Poslanik Njegov nešto odrede, po svom nahođenju postupe. A ko Allaha i Njegova Poslanika ne posluša, taj je očito zalutao.", TextEnglish = "It is not for a believing man or a believing woman, when Allāh and His Messenger have decided a matter, that they should [thereafter] have any choice about their affair. And whoever disobeys Allāh and His Messenger has certainly strayed into clear error.", TextGerman = "Weder für einen gläubigen Mann noch für eine gläubige Frau gibt es, wenn Allah und Sein Gesandter eine Angelegenheit entschieden haben, die Möglichkeit, in ihrer Angelegenheit zu wählen. Und wer sich Allah und Seinem Gesandten widersetzt, der befindet sich ja in deutlichem Irrtum." });
                        surah33.VerseList.Add(new Verse { Number = 37, TextBosnian = "A kad ti reče onome kome je Allah bio blagodaran, i kome si i ti bio blagodaran: 'Zadrži suprugu svoju i boj se Allaha!' - u sebi si skrivao ono što će Allah objelodaniti i ljudi si se bojao, a preče je da se Allaha bojiš. I pošto je Zejdova potreba za njom prestala i on se od nje razveo, Mi smo je za tebe udali kako se vjernici ne bi ustručavali više da se žene ženama posinaka svojih kad se oni od njih razvedu. A Allahova se zapovijed izvršava!", TextEnglish = "And [remember, O Muḥammad], when you said to the one on whom Allāh bestowed favor and you bestowed favor,[1198] 'Keep your wife and fear Allāh,' while you concealed within yourself that which Allāh is to disclose.[1199] And you feared the people,[1200] while Allāh has more right that you fear Him.[1201] So when Zayd had no longer any need for her, We married her to you in order that there not be upon the believers any discomfort [i.e., guilt] concerning the wives of their claimed [i.e., adopted] sons when they no longer have need of them. And ever is the command [i.e., decree] of Allāh accomplished.", TextGerman = "Und als du zu demjenigen sagtest, dem Allah Gunst erwiesen hatte und dem auch du Gunst erwiesen hattest: „Behalte deine Gattin für dich und fürchte Allah, und in deinem Inneren verborgen hieltest, was Allah doch offenlegen wird, und die Menschen fürchtetest, während Allah ein größeres Anrecht darauf hat, daß du Ihn fürchtest. Als dann Zaid keinen Wunsch mehr an ihr hatte, gaben Wir sie dir zur Gattin, damit für die Gläubigen kein Grund zur Bedrängnis bestehe hinsichtlich der Gattinnen ihrer angenommenen Söhne, wenn diese keinen Wunsch mehr an ihnen haben. Und Allahs Anordnung wird (stets) ausgeführt." });
                        surah33.VerseList.Add(new Verse { Number = 38, TextBosnian = "I Vjerovjesniku nije teško da čini ono što mu Allah odredi jer takav je bio Allahov propis i za one koji su prije bili i nestali, a Allahova odredba je neminovna.", TextEnglish = "There is not to be upon the Prophet any discomfort concerning that which Allāh has imposed upon him.[1202] [This is] the established way of Allāh with those [prophets] who have passed on before. And ever is the command of Allāh a destiny decreed.", TextGerman = "Es besteht für den Propheten kein Grund zur Bedrängnis in dem, was Allah für ihn verpflichtend gemacht hat. (So war) Allahs Gesetzmäßigkeit mit denjenigen, die zuvor dahingegangen sind - und Allahs Anordnung ist ein fest gefaßter Beschluß -" });
                        surah33.VerseList.Add(new Verse { Number = 39, TextBosnian = "Za one koji su Allahove poslanice dostavljali i od Njega strahovali, i koji se nikoga, osim Allaha, nisu bojali. A dovoljan je Allah da svodi račune!", TextEnglish = "[Allāh praises] those who convey the messages of Allāh[1203] and fear Him and do not fear anyone but Allāh. And sufficient is Allāh as Accountant.", TextGerman = "die Allahs Botschaften übermitteln, Ihn fürchten und niemanden fürchten außer Allah. Und Allah genügt als Abrechner." });
                        surah33.VerseList.Add(new Verse { Number = 40, TextBosnian = "Muhammed nije otac nijednom od vaših ljudi, nego je Allahov poslanik i pečat, posljednji vjerovjesnik- a Allah sve dobro zna.", TextEnglish = "Muḥammad is not the father of [any] one of your men, but [he is] the Messenger of Allāh and seal [i.e., last] of the prophets. And ever is Allāh, of all things, Knowing.", TextGerman = "Muhammad ist nicht der Vater irgend jemandes von euren Männern, sondern Allahs Gesandter und das Siegel der Propheten. Und Allah weiß über alles Bescheid." });
                        surah33.VerseList.Add(new Verse { Number = 41, TextBosnian = "O vi koji vjerujete, često se Allaha sjećajte i spominjite Ga,", TextEnglish = "O you who have believed, remember Allāh with much remembrance", TextGerman = "O die ihr glaubt, gedenkt Allahs in häufigem Gedenken" });
                        surah33.VerseList.Add(new Verse { Number = 42, TextBosnian = "i ujutro i navečer Ga slavite i veličajte;", TextEnglish = "And exalt Him morning and afternoon.", TextGerman = "und preist Ihn morgens und abends." });
                        surah33.VerseList.Add(new Verse { Number = 43, TextBosnian = "On je Taj Koji vas blagosilja, a i meleki Njegovi, da bi vas iz tmina na svjetlo izveo - On je prema vjernicima milostiv.", TextEnglish = "It is He who confers blessing upon you,[1204] and His angels [ask Him to do so] that He may bring you out from darknesses into the light. And ever is He, to the believers, Merciful.", TextGerman = "Er ist es, Der über euch den Segen spricht - und auch Seine Engel -, damit Er euch aus den Finsternissen ins Licht hinausbringt; und Er ist zu den Gläubigen Barmherzig." });
                        surah33.VerseList.Add(new Verse { Number = 44, TextBosnian = "Pozdrav će im, na Dan kad Ga oni susretnu, selam biti, i On im je pripremio nagradu plemenitu.", TextEnglish = "Their greeting the Day they meet Him will be, 'Peace.' And He has prepared for them a noble reward.", TextGerman = "Ihr Gruß am Tag, da sie Ihm begegnen, wird sein: „Friede ! Und Er hat für sie trefflichen Lohn bereitet." });
                        surah33.VerseList.Add(new Verse { Number = 45, TextBosnian = "O Vjerovjesniče, zaista smo te poslali kao svjedoka i kao donositelja radosnih vijesti i kao upozoritelja,", TextEnglish = "O Prophet, indeed We have sent you as a witness and a bringer of good tidings and a warner", TextGerman = "O Prophet, Wir haben dich gesandt als Zeugen, als Verkünder froher Botschaft und als Warner" });
                        surah33.VerseList.Add(new Verse { Number = 46, TextBosnian = "da - po Njegovom naređenju - pozivaš k Allahu, i kao svjetiljku koja sija.", TextEnglish = "And one who invites to Allāh, by His permission, and an illuminating lamp.", TextGerman = "und als einen, der zu Allah mit Seiner Erlaubnis ruft und als eine lichtspendende Leuchte." });
                        surah33.VerseList.Add(new Verse { Number = 47, TextBosnian = "I obraduj vjernike da će Allah na njih veliku milost prosuti,", TextEnglish = "And give good tidings to the believers that they will have from Allāh great bounty.", TextGerman = "Und verkünde den Gläubigen, daß es für sie von Allah große Huld geben wird." });
                        surah33.VerseList.Add(new Verse { Number = 48, TextBosnian = "a ne slušaj nevjernike ni licemjere, na uvrede njihove pažnju ne obraćaj i u Allaha se pouzdaj, Allah je dovoljan kao zaštitnik.", TextEnglish = "And do not obey the disbelievers and the hypocrites and disregard their annoyance, and rely upon Allāh. And sufficient is Allāh as Disposer of affairs.", TextGerman = "Und gehorche nicht den Ungläubigen und den Heuchlern; beachte nicht die Beleidigungen, die sie dir zufügen, und verlasse dich auf Allah. Und Allah genügt als Sachwalter." });
                        surah33.VerseList.Add(new Verse { Number = 49, TextBosnian = "O vjernici, kad se vjernicama oženite, a onda ih, prije stupanja u bračni odnos, pustite, one nisu dužne čekati određeno vrijeme koje ćete vi brojati, već ih darujte i lijepo ih otpremite.", TextEnglish = "O you who have believed, when you marry believing women and then divorce them before you have touched them [i.e., consummated the marriage], then there is not for you any waiting period to count concerning them. So provide for them and give them a gracious release.", TextGerman = "O die ihr glaubt, wenn ihr gläubige Frauen heiratet und euch hierauf von ihnen scheidet, bevor ihr sie berührt habt, dann dürft ihr für sie keine von euch berechnete Wartezeit aussetzen; gewährt ihnen eine Abfindung und gebt sie auf schöne Weise frei." });
                        surah33.VerseList.Add(new Verse { Number = 50, TextBosnian = "O Vjerovjesniče, Mi smo ti dozvolili da budu žene tvoje one kojima si dao vjenčane darove njihove, i one u vlasti tvojoj koje ti je Allah dao iz plijena, i kćeri amidža tvojih, i kćeri tetaka tvojih po ocu, i kćeri daidža tvojih, i kćeri tetaka tvojih po majci koje su se s tobom iselile, i ženu - vjernicu koja sebe pokloni Vjerovjesniku, ako Vjerovjesnik hoće da se njome oženi, samo tebi, a ne ostalim vjernicima. Mi znamo šta smo propisali vjernicima u vezi sa suprugama njihovim i onima koje su u vlasništvu njihovu, a i da ti ne bi bilo teško. A Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "O Prophet, indeed We have made lawful to you your wives to whom you have given their due compensation[1205] and those your right hand possesses from what Allāh has returned to you [of captives] and the daughters of your paternal uncles and the daughters of your paternal aunts and the daughters of your maternal uncles and the daughters of your maternal aunts who emigrated with you and a believing woman if she gives herself to the Prophet [and] if the Prophet wishes to marry her; [this is] only for you, excluding the [other] believers. We certainly know what We have made obligatory upon them concerning their wives and those their right hands possess, [but this is for you] in order that there will be upon you no discomfort [i.e., difficulty]. And ever is Allāh Forgiving and Merciful.", TextGerman = "O Prophet, Wir haben dir (zu heiraten) erlaubt: deine Gattinnen, denen du ihren Lohn gegeben hast, das, was deine rechte Hand (an Sklavinnen) besitzt von dem, was Allah dir als Beute zugeteilt hat, die Töchter deiner Onkel väterlicherseits und die Töchter deiner Tanten väterlicherseits, die Töchter deiner Onkel mütterlicherseits und die Töchter deiner Tanten mütterlicherseits, die mit dir ausgewandert sind; auch eine (jede) gläubige Frau, wenn sie sich dem Propheten (ohne Gegenforderung) schenkt und falls der Prophet sie heiraten will: Dies ist dir vorbehalten unter Ausschluß der (übrigen) Gläubigen - Wir wissen wohl, was Wir ihnen hinsichtlich ihrer Gattinnen und dessen, was ihre rechte Hand (an Sklavinnen) besitzt, verpflichtend gemacht haben -, damit für dich kein Grund zur Bedrängnis bestehe. Und Allah ist Allvergebend und Barmherzig." });
                        surah33.VerseList.Add(new Verse { Number = 51, TextBosnian = "Možeš zanemariti one među njima koje hoćeš i možeš pozvati sebi onu koju hoćeš, a možeš zatražiti onu koju si udaljio, nijedno od toga nije tvoj grijeh. Najlakše će tako one radosne biti i neće se žalostiti i tako će sve onim što im ti daješ zadovoljne biti - a Allah zna šta je u srcima vašim; Allah je Onaj Koji sve zna i Onaj Koji ne žuri s kaznom.", TextEnglish = "You, [O Muḥammad], may put aside whom you will of them[1206] or take to yourself whom you will. And any that you desire of those [wives] from whom you had [temporarily] separated - there is no blame upon you [in returning her]. That is more suitable that they should be content and not grieve and that they should be satisfied with what you have given them - all of them. And Allāh knows what is in your hearts. And ever is Allāh Knowing and Forbearing.", TextGerman = "Du darfst zurückstellen, wen von ihnen du willst, und du darfst bei dir aufnehmen, wen du willst. Und wenn du doch eine von denjenigen begehrst, die du abgewiesen hast, dann ist das für dich keine Sünde. Das ist eher geeignet, daß sie frohen Mutes, nicht traurig und daß sie alle mit dem zufrieden sind, was du ihnen gibst. Allah weiß, was in euren Herzen ist. Allah ist Allwissend und Nachsichtig." });
                        surah33.VerseList.Add(new Verse { Number = 52, TextBosnian = "Odsada ti nisu dopuštene druge žene, ni da umjesto njih neku drugu uzmeš, makar te i zadivila ljepota njihova, osim onih koje posjeduješ. A Allah sve nadzire.", TextEnglish = "Not lawful to you, [O Muḥammad], are [any additional] women after [this], nor [is it] for you to exchange them for [other] wives, even if their beauty were to please you, except what your right hand possesses. And ever is Allāh, over all things, an Observer.[1207]", TextGerman = "Darüber hinaus ist dir weder erlaubt, Frauen zu heiraten noch sie gegen (andere) Gattinnen einzutauschen, auch wenn ihre Schönheit dir gefallen sollte, mit Ausnahme dessen, was deine rechte Hand (an Sklavinnen) besitzt. Und Allah ist Wächter über alles." });
                        surah33.VerseList.Add(new Verse { Number = 53, TextBosnian = "O vi koji vjerujete, ne ulazite u sobe Vjerovjesnikove, osim ako vam se dopusti radi jela, ali ne da čekate dok se ono zgotovi; tek kad budete pozvani, onda uđite, i pošto jedete, raziđite se ne upuštajući se u razgovor. To smeta Vjerovjesniku, a on se stidi da vam to rekne, a Allah se ne stidi istine. A ako od njih, supruga njegovih, nešto tražite, tražite to od njih iza zastora. To je čistije i za vaša i za njihova srca. Vama nije dopušteno da Allahova Poslanika uznemiravate niti da se suprugama njegovim poslije smrti njegove ikada oženite. To bi, uistinu, kod Allaha bio veliki grijeh,", TextEnglish = "O you who have believed, do not enter the houses of the Prophet except when you are permitted for a meal, without awaiting its readiness. But when you are invited, then enter; and when you have eaten, disperse without seeking to remain for conversation. Indeed, that [behavior] was troubling the Prophet, and he is shy of [dismissing] you. But Allāh is not shy of the truth. And when you ask [his wives] for something, ask them from behind a partition. That is purer for your hearts and their hearts. And it is not [conceivable or lawful] for you to harm the Messenger of Allāh or to marry his wives after him, ever. Indeed, that would be in the sight of Allāh an enormity.", TextGerman = "O die ihr glaubt, tretet nicht in die Häuser des Propheten ein - außer es wird euch erlaubt - zu(r Teilnahme an) einem Essen, ohne auf die rechte Zeit zu warten. Sondern wenn ihr (herein)gerufen werdet, dann tretet ein, und wenn ihr gegessen habt, dann geht auseinander, und (tut dies,) ohne euch mit geselliger Unterhaltung aufzuhalten. Solches fügt dem Propheten Leid zu, aber er schämt sich vor euch. Allah aber schämt sich nicht vor der Wahrheit. Und wenn ihr sie um einen Gegenstand bittet, so bittet sie hinter einem Vorhang. Das ist reiner für eure Herzen und ihre Herzen. Und es steht euch nicht zu, dem Gesandten Allahs Leid zuzufügen, und auch nicht, jemals seine Gattinnen nach ihm zu heiraten. Gewiß, das wäre bei Allah etwas Ungeheuerliches." });
                        surah33.VerseList.Add(new Verse { Number = 54, TextBosnian = "iznosili vi o tome šta u javnost ili to u sebi krili, pa, Allah sve dobro zna.", TextEnglish = "Whether you reveal a thing or conceal it, indeed Allāh is ever, of all things, Knowing.", TextGerman = "Ob ihr etwas offenlegt oder verbergt, gewiß, Allah weiß über alles Bescheid." });
                        surah33.VerseList.Add(new Verse { Number = 55, TextBosnian = "Njima nije grijeh da budu otkrivene pred očevima svojim, i sinovima svojim, i braćom svojom, i sinovima braće svoje, i sinovima sestara svoj ih, i ženama vjernicama, i pred onima koje su u vlasništvu njihovu.I Allaha se bojte!Allah, je, doista, svjedok svemu.", TextEnglish = "There is no blame upon them[i.e., women] concerning their fathers or their sons or their brothers or their brothers' sons or their sisters' sons or their women or those their right hands possess[i.e., slaves].[1208] And fear Allāh.Indeed Allāh is ever, over all things, Witness.", TextGerman = "Es besteht für sie keine Sünde weder hinsichtlich ihrer Väter, noch ihrer Söhne, noch ihrer Brüder, der Söhne ihrer Brüder, noch der Söhne ihrer Schwestern, noch ihrer Frauen noch derjenigen, die ihre rechte Hand besitzt.Und fürchtet Allah.Gewiß, Allah ist über alles Zeuge." });
                        surah33.VerseList.Add(new Verse { Number = 56, TextBosnian = "Zaista Allah i meleci Njegovi blagosiljaju Vjerovjesnika. O vi koji vjerujete, blagosiljajte ga i vi i njemu selam šaljite!", TextEnglish = "Indeed, Allāh confers blessing upon the Prophet, and His angels [ask Him to do so]. O you who have believed, ask [Allāh to confer] blessing upon him and ask [Allāh to grant him] peace.", TextGerman = "Gewiß, Allah und Seine Engel sprechen den Segen über den Propheten. O die ihr glaubt, sprecht den Segen über ihn und grüßt ihn mit gehörigem Gruß." });
                        surah33.VerseList.Add(new Verse { Number = 57, TextBosnian = "One koji Allaha i Poslanika Njegova budu uznemiravali, Allah će, zaista, i na dunjaluku i na ahiretu prokleti, i ponižavajuću im patnju pripremiti.", TextEnglish = "Indeed, those who abuse Allāh and His Messenger - Allāh has cursed them in this world and the Hereafter and prepared for them a humiliating punishment.", TextGerman = "Diejenigen, die Allah und Seinem Gesandten Leid zufügen, verflucht Allah im Diesseits und Jenseits, und für sie hat Er schmachvolle Strafe bereitet." });
                        surah33.VerseList.Add(new Verse { Number = 58, TextBosnian = "A oni koji vjernike i vjernice uznemiravaju, a oni to ne zaslužuju, tovare na sebe klevetu i pravi grijeh.", TextEnglish = "And those who harm believing men and believing women for [something] other than what they have earned [i.e., deserved] have certainly borne upon themselves a slander and manifest sin.", TextGerman = "Und diejenigen, die den gläubigen Männern und den gläubigen Frauen Leid zufügen für etwas, was sie nicht begangen haben, laden damit Verleumdung und offenkundige Sünde auf sich." });
                        surah33.VerseList.Add(new Verse { Number = 59, TextBosnian = "O Vjerovjesniče, reci suprugama svojim, i kćerima svojim, i ženama vjernika neka spuste haljine svoje niza se. Tako će se najlakše prepoznati pa neće uznemiravane biti. Allah je oprostitelj grijeha i milostiv je.", TextEnglish = "O Prophet, tell your wives and your daughters and the women of the believers to bring down over themselves [part] of their outer garments.[1209] That is more suitable that they will be known[1210] and not be abused. And ever is Allāh Forgiving and Merciful.[1211]", TextGerman = "O Prophet, sag deinen Gattinnen und deinen Töchtern und den Frauen der Gläubigen, sie sollen etwas von ihrem Überwurf über sich herunterziehen. Das ist eher geeignet, daß sie erkannt und so nicht belästigt werden. Und Allah ist Allvergebend und Barmherzig." });
                        surah33.VerseList.Add(new Verse { Number = 60, TextBosnian = "Ako se licemjeri i oni u čijim srcima je bolest i oni koji po Medini šire laži - ne okane, Mi ćemo ti vlast nad njima prepustiti i oni će samo kratko vrijeme kao susjedi tvoji u njoj ostati,", TextEnglish = "If the hypocrites and those in whose hearts is disease[1212] and those who spread rumors in al-Madīnah do not cease, We will surely incite you against them; then they will not remain your neighbors therein except for a little,", TextGerman = "Wenn nicht die Heuchler und diejenigen, in deren Herzen Krankheit ist, und diejenigen, die beunruhigende Gerüchte in der Stadt verbreiten, (damit) aufhören, werden Wir dich ganz gewiß gegen sie antreiben. Hierauf werden sie nur noch kurze Zeit darin deine Nachbarn sein." });
                        surah33.VerseList.Add(new Verse { Number = 61, TextBosnian = "prokleti neka su! Gdje god se nađu, neka budu hvatani i ubijani", TextEnglish = "Accursed wherever they are found, [being] seized and massacred completely.", TextGerman = "(Sie sind) verflucht. Wo immer sie (im Kampf) angetroffen werden, werden sie ergriffen und allesamt getötet." });
                        surah33.VerseList.Add(new Verse { Number = 62, TextBosnian = "prema Allahovu zakonu koji je vrijedio za one koji su bili i nestali! A ti nećeš u Allahovu zakonu izmjene naći!", TextEnglish = "[This is] the established way of Allāh with those who passed on before; and you will not find in the way of Allāh any change.", TextGerman = "(So war) Allahs Gesetzmäßigkeit mit denjenigen, die zuvor dahingegangen sind. Und du wirst in Allahs Gesetzmäßigkeit keine Änderung finden." });
                        surah33.VerseList.Add(new Verse { Number = 63, TextBosnian = "Pitaju te ljudi o Času kijameta; reci: 'To jedino Allah zna! A ti ne znaš, možda je Čas kijameta blizu!'", TextEnglish = "People ask you concerning the Hour. Say, 'Knowledge of it is only with Allāh. And what may make you perceive? Perhaps the Hour is near.'", TextGerman = "Die Menschen fragen dich nach der Stunde. Sag: Das Wissen darüber ist nur bei Allah. Was läßt dich wissen? Vielleicht ist die Stunde nahe." });
                        surah33.VerseList.Add(new Verse { Number = 64, TextBosnian = "Allah je nevjernike prokleo i za njih Oganj razbuktali pripremio,", TextEnglish = "Indeed, Allāh has cursed the disbelievers and prepared for them a Blaze.", TextGerman = "Gewiß, Allah hat die Ungläubigen verflucht und für sie eine Feuerglut bereitet," });
                        surah33.VerseList.Add(new Verse { Number = 65, TextBosnian = "u njemu će vječno, zauvijek boraviti, ni zaštitnika ni pomagača neće naći.", TextEnglish = "Abiding therein forever, they will not find a protector or a helper.", TextGerman = "ewig und auf immer darin zu bleiben, ohne einen Schutzherrn noch Helfer zu finden." });
                        surah33.VerseList.Add(new Verse { Number = 66, TextBosnian = "Na Dan kad njihova lica u Vatri prevrtana budu, govorit će: 'Kamo sreće da smo se Allahu pokoravali i da smo Poslanika slušali!'", TextEnglish = "The Day their faces will be turned about in the Fire, they will say, 'How we wish we had obeyed Allāh and obeyed the Messenger.'", TextGerman = "Am Tag, da ihre Gesichter im (Höllen)feuer hin und her gedreht werden, sagen sie: „O hätten wir doch Allah gehorcht und hätten wir dem Gesandten gehorcht!" });
                        surah33.VerseList.Add(new Verse { Number = 67, TextBosnian = "I govorit će: 'Gospodaru naš, mi smo prvake svoje i starješine svoje slušali, pa su nas oni s Pravog puta odveli;", TextEnglish = "And they will say, 'Our Lord, indeed we obeyed our masters and our dignitaries,[1213] and they led us astray from the [right] way.", TextGerman = "Und sie sagen: „Unser Herr, gewiß, wir haben unseren Herrschern und unseren Großen gehorcht, und da haben sie uns vom Weg abirren lassen." });
                        surah33.VerseList.Add(new Verse { Number = 68, TextBosnian = "I Gospodaru naš, podaj im dvostruku patnju i prokuni ih prokletstvom velikim!'", TextEnglish = "Our Lord, give them double the punishment and curse them with a great curse.'", TextGerman = "Unser Herr, lasse ihnen zweifache Strafe zukommen, und verfluche sie mit einem großen Fluch." });
                        surah33.VerseList.Add(new Verse { Number = 69, TextBosnian = "O vi koji vjerujete, ne budite kao oni koji su Musaa uznemiravali, pa ga je Allah oslobodio onoga što su govorili, i on kod Allaha ugled uživa!", TextEnglish = "O you who have believed, be not like those who abused Moses; then Allāh cleared him of what they said. And he, in the sight of Allāh, was distinguished.", TextGerman = "O die ihr glaubt, seid nicht wie diejenigen, die Musa Leid zufügten, worauf Allah ihn freisprach von dem, was sie (über ihn) sagten; und er war bei Allah angesehen." });
                        surah33.VerseList.Add(new Verse { Number = 70, TextBosnian = "O vi koji vjerujete, bojte se Allaha i govorite samo istinu,", TextEnglish = "O you who have believed, fear Allāh and speak words of appropriate justice.", TextGerman = "O die ihr glaubt, fürchtet Allah und sagt treffende Worte," });
                        surah33.VerseList.Add(new Verse { Number = 71, TextBosnian = "On će vas za vaša dobra djela nagraditi i grijehe vam oprostiti! A onaj ko se Allahu i Poslaniku Njegovu bude pokoravao - postići će veliki uspjeh.", TextEnglish = "He will [then] amend for you your deeds and forgive you your sins. And whoever obeys Allāh and His Messenger has certainly attained a great attainment.", TextGerman = "so läßt Er eure Werke als gut gelten und vergibt euch eure Sünden. Und wer Allah und Seinem Gesandten gehorcht, der erzielt ja einen großartigen Erfolg," });
                        surah33.VerseList.Add(new Verse { Number = 72, TextBosnian = "Mi smo nebesima, Zemlji i planinama ponudili emanet, pa su se ustegli i pobojali da ga ponesu, ali ga je preuzeo čovjek - a on je prema sebi baš zulum učinio i neznalica je bio,", TextEnglish = "Indeed, We offered the Trust[1214] to the heavens and the earth and the mountains, and they declined to bear it and feared it; but man [undertook to] bear it. Indeed, he was unjust and ignorant.[1215]", TextGerman = "Wir haben das anvertraute Gut den Himmeln und der Erde und den Bergen angeboten, aber sie weigerten sich, es zu tragen, sie scheuten sich davor. Der Mensch trug es - gewiß, er ist sehr oft ungerecht und sehr oft töricht." });
                        surah33.VerseList.Add(new Verse { Number = 73, TextBosnian = "da bi Allah licemjere i licemjerke, i višebošce i višeboškinje kaznio, a vjernicima i vjernicama oprostio. Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "[It[1216] was] so that Allāh may punish the hypocrite men and hypocrite women and the men and women who associate others with Him and that Allāh may accept repentance from the believing men and believing women. And ever is Allāh Forgiving and Merciful.", TextGerman = "(Das ist so,) damit Allah die Heuchler, Männer und Frauen, und die Götzendiener, Männer und Frauen, straft und sich den Gläubigen, Männern und Frauen, Reue-Annehmend zuwendet. Allah ist Allvergebend und Barmherzig." });
                        result = surah33;
                        break;
                    #endregion

                    // 
                    #region
                    case 34:
                        Surah surah34 = new Surah(); surah34.Name = Constants.Surah34Name; surah34.Number = 34; surah34.VerseList = new List<Verse>();
                        surah34.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah34.VerseList.Add(new Verse { Number = 1, TextBosnian = "Svaka hvala neka je Allahu, Kojem pripada sve ono na nebesima i sve ono na Zemlji, a svaka hvala neka mu je i na ahiretu! On je Mudri i Onaj Koji sve u potpunosti zna.", TextEnglish = "[All] praise is [due] to Allāh, to whom belongs whatever is in the heavens and whatever is in the earth, and to Him belongs [all] praise in the Hereafter. And He is the Wise, the Aware.", TextGerman = "Alles) Lob gehört Allah, Dem gehört, was in den Himmeln und was auf der Erde ist, und (alles) Lob gehört Ihm im Jenseits. Er ist der Allweise und Allkundige." });
                        surah34.VerseList.Add(new Verse { Number = 2, TextBosnian = "On zna šta u zemlju ulazi a šta iz nje izlazi, i šta se s nebesa spušta, a šta se na njih uspinje; On je Milostivi i grijeha Oprostitelj.", TextEnglish = "He knows what penetrates into the earth and what emerges from it and what descends from the heaven and what ascends therein. And He is the Merciful, the Forgiving.", TextGerman = "Er weiß, was in die Erde eindringt und was aus ihr herauskommt, was vom Himmel herabkommt und was dorthin aufsteigt. Und Er ist der Barmherzige und Allvergebende." });
                        surah34.VerseList.Add(new Verse { Number = 3, TextBosnian = "A oni koji ne vjeruju govore: 'Čas oživljenja neće nam doći!' Reci: 'Hoće, tako mi Gospodara moga, Koji zna i ono što je čulima nedokučivo, sigurno će vam doći!' Njemu ne može ništa, ni koliko trunčica jedna, ni na nebesima ni na Zemlji, izmaći, i ne postoji ništa, ni manje ni veće od toga, što nije u jasnoj Knjizi -", TextEnglish = "But those who disbelieve say, 'The Hour will not come to us.' Say, 'Yes, by my Lord, it will surely come to you. [Allāh is] the Knower of the unseen.' Not absent from Him is an atom's weight[1218] within the heavens or within the earth or [what is] smaller than that or greater, except that it is in a clear register -", TextGerman = "Diejenigen, die ungläubig sind, sagen: „Die Stunde kommt nicht über uns. Sag: Doch bei meinem Herrn - sie kommt ganz gewiß über euch -, (bei Ihm,) dem Kenner des Verborgenen! Es entgeht Ihm nicht das Gewicht eines Stäubchens, weder in den Himmeln noch auf der Erde. Und es gibt nichts, was kleiner ist als dies oder größer, das nicht in einem deutlichen Buch (verzeichnet) wäre," });
                        surah34.VerseList.Add(new Verse { Number = 4, TextBosnian = "da nagradi one koji vjeruju i dobra djela čine - to su oni kojima pripada oprost i opskrba plemenita -", TextEnglish = "That He may reward those who believe and do righteous deeds. Those will have forgiveness and noble provision.", TextGerman = "damit Er (es) denjenigen, die glauben und rechtschaffene Werke tun, vergelte. Für diese wird es Vergebung und ehrenvolle Versorgung geben." });
                        surah34.VerseList.Add(new Verse { Number = 5, TextBosnian = "a oni koji se protiv ajeta i znakova Naših bore, odvraćajući od njih - to su oni kojima pripada najbolnija patnja.", TextEnglish = "But those who strive against Our verses [seeking] to cause failure[1219] - for them will be a painful punishment of foul nature.", TextGerman = "Und diejenigen, die ständig gegen Unsere Zeichen eifern und ihnen Machtlosigkeit nachweisen wollen, für sie wird es schmerzhafte Strafe von Züchtigung geben." });
                        surah34.VerseList.Add(new Verse { Number = 6, TextBosnian = "A vidjet će oni kojima je dato znanje da je ono što ti se objavljuje od Gospodara tvoga Istina, i da vodi na Put Silnog i Hvaljenog.", TextEnglish = "And those who have been given knowledge see that what is revealed to you from your Lord is the truth, and it guides to the path of the Exalted in Might, the Praiseworthy.", TextGerman = "Und diejenigen, denen das Wissen gegeben worden ist, sehen, (daß) das, was zu dir von deinem Herrn (als Offenbarung) herabgesandt worden ist, die Wahrheit ist und zum Weg des Allmächtigen und Lobenswürdigen führt." });
                        surah34.VerseList.Add(new Verse { Number = 7, TextBosnian = "A oni koji ne vjeruju govore: 'Hoćete li da vam pokažemo čovjeka koji će vas obavijestiti da ćete, kada se sasvim raspadnete, zaista, ponovno stvoreni biti?", TextEnglish = "But those who disbelieve say, 'Shall we direct you to a man who will inform you [that] when you have disintegrated in complete disintegration, you will [then] be [recreated] in a new creation?", TextGerman = "Und diejenigen, die ungläubig sind, sagen: „Sollen wir euch auf einen Mann hinweisen, der euch kundtut, daß ihr, wenn ihr vollständig in Stücke gerissen worden seid, doch wirklich in neuer Schöpfung (erstehen) werdet?" });
                        surah34.VerseList.Add(new Verse { Number = 8, TextBosnian = "I Iznosili on o Allahu laži ili je lud?' Naprotiv, oni koji u ahiret neće da vjeruju u patnji su i u zabludi dubokoj.", TextEnglish = "Has he invented about Allāh a lie or is there in him madness?' Rather, they who do not believe in the Hereafter will be in the punishment and [are in] extreme error.", TextGerman = "Hat er gegen Allah eine Lüge ersonnen, oder leidet er an Besessenheit? Nein! Vielmehr stecken diejenigen, die an das Jenseits nicht glauben, in der Strafe und befinden sich im tiefen Irrtum." });
                        surah34.VerseList.Add(new Verse { Number = 9, TextBosnian = "Zar ne vide nebo i Zemlju, ono što je ispred njih i ono što je iza njih?! Kad bismo htjeli, u zemlju bismo ih utjerali ili komade neba na njih sručili. To je, zaista, znak svakom robu koji se Allahu s pokajanjem vraća.", TextEnglish = "Then, do they not look at what is before them and what is behind them of the heaven and earth? If We should will, We could cause the earth to swallow them or [could] let fall upon them fragments from the sky. Indeed in that is a sign for every servant turning back [to Allāh].", TextGerman = "Sehen sie denn nicht auf das, was vom Himmel und von der Erde vor ihnen und was hinter ihnen ist? Wenn Wir wollen, lassen Wir die Erde mit ihnen versinken oder Stücke vom Himmel auf sie herabfallen. Darin ist wahrlich ein Zeichen für jeden Diener, der sich (Allah) reuig zuwendet." });
                        surah34.VerseList.Add(new Verse { Number = 10, TextBosnian = "Mi smo Davudu od Nas dobro dali: 'O brda, ponavljajte zajedno s njim hvalu, i vi, ptice!' - i omekšali smo mu gvožđe.", TextEnglish = "And We certainly gave David from Us bounty. [We said], 'O mountains, repeat [Our] praises with him, and the birds [as well].' And We made pliable for him iron,", TextGerman = "Und Wir gaben ja Dawud eine Huld von Uns. - „Ihr Berge, preist (Allah) im Widerhall mit ihm, und auch ihr Vögel. Und Wir machten für ihn das Eisen geschmeidig:" });
                        surah34.VerseList.Add(new Verse { Number = 11, TextBosnian = "I pravi široke pancire i odmjeri pri vezivanju halki, i činite dobro! Ja zaista dobro vidim šta radite vi.'", TextEnglish = "[Commanding him], 'Make full coats of mail and calculate [precisely] the links, and work [all of you] righteousness. Indeed I, of what you do, am Seeing.'", TextGerman = "„Fertige Panzergewänder an und füge im richtigen Maß die Panzermaschen aneinander. Und handelt rechtschaffen, denn Ich sehe wohl, was ihr tut." });
                        surah34.VerseList.Add(new Verse { Number = 12, TextBosnian = "A Sulejmanu - vjetar, ujutro je prevaljivao rastojanje od mjesec dana, a navečer rastojanje od mjesec dana; i učinili smo da mu iz izvora rastopljen bakar teče i da džini, dozvolom njegova Gospodara, pred njim rade, a ko od njih otkaže poslušnost naređenju Našem, Mi učinimo da ognjenu patnju osjeti.", TextEnglish = "And to Solomon [We subjected] the wind - its morning [journey was that of] a month - and its afternoon [journey was that of] a month, and We made flow for him a spring of [liquid] copper. And among the jinn were those who worked for him by the permission of his Lord. And whoever deviated among them from Our command - We will make him taste of the punishment of the Blaze.", TextGerman = "Und Sulaiman (machten Wir) den Wind (dienstbar), dessen Morgenlauf einen Monat und dessen Abendlauf einen Monat beträgt. Und Wir ließen die Quelle des geschmolzenen Kupfers für ihn fließen. Und unter den Ginn gab es manche, die mit der Erlaubnis seines Herrn vor ihm tätig waren. Wer von ihnen von Unserem Befehl abweicht, den lassen Wir von der Strafe der Feuerglut kosten." });
                        surah34.VerseList.Add(new Verse { Number = 13, TextBosnian = "Oni su mu izrađivali što god je htio: bogomolje i spomenike, i zdjele kao čatrnje, i kotlove nepokretne. 'Radite i budite zahvalni, o porodico Davudova!' A malo je zahvalnih među robovima Mojim.", TextEnglish = "They made for him what he willed of elevated chambers,[1220] statues,[1221] bowls like reservoirs, and stationary kettles. [We said], 'Work, O family of David, in gratitude.' And few of My servants are grateful.", TextGerman = "Sie machten ihm, was er wollte, an Gebetsräumen, Bildwerken, Schüsseln wie Wasserbecken und feststehenden Kesseln. - „Verrichtet, ihr Sippe Dawuds, eure Arbeit in Dankbarkeit, denn (nur) wenige von Meinen Dienern sind wirklich dankbar." });
                        surah34.VerseList.Add(new Verse { Number = 14, TextBosnian = "A kad smo odredili da umre, samo ih je crv iz zemlje koji je bio rastočio štap njegov - uputio na smrt njegovu, i kad se on srušio, džini shvatiše, da ne bi u patnji ponižavajućoj ostali, da su ono što je čulima nedokučivo znali.", TextEnglish = "And when We decreed for him [i.e., Solomon] death, nothing indicated to them [i.e., the jinn] his death except a creature of the earth eating his staff.[1222] But when he fell, it became clear to the jinn that if they had known the unseen, they would not have remained in humiliating punishment.[1223]", TextGerman = "Und als Wir für ihn den Tod bestimmt hatten, wies sie auf sein Ableben nur das Tier der Erde hin, das seinen Stab fraß. Als er dann niederstürzte, wurde den Ginn klar, daß sie, wenn sie das Verborgene gewußt, nicht (weiter) in der schmachvollen Strafe verweilt hätten." });
                        surah34.VerseList.Add(new Verse { Number = 15, TextBosnian = "Stanovnici Sabe imali su znak u mjestu u kom su živjeli: vrtove, zdesna i slijeva. Jedite iz opskrbe Gospodara svoga i budite Mu zahvalni; ovo je lijep predio, a Gospodar je grijeha Oprostitelj!", TextEnglish = "There was for [the tribe of] Saba’ in their dwelling place a sign: two [fields of] gardens on the right and on the left. [They were told], 'Eat from the provisions of your Lord and be grateful to Him. A good land [have you], and a forgiving Lord.'", TextGerman = "Die Sabäer hatten ja in ihrem Wohnort ein Zeichen: zwei Gärten zur Rechten und zur Linken. - „Eßt von der Versorgung eures Herrn und dankt Ihm. Eine gute Ortschaft (ist es) und ein Allvergebender Herr." });
                        surah34.VerseList.Add(new Verse { Number = 16, TextBosnian = "Ali oni su se okrenuli, pa smo na njih vodenu bujicu pustili, i zamijenili im njihove vrtove drugim vrtovima sa plodovima gorkim i tamariskom i nešto malo divljeg lotosa.", TextEnglish = "But they turned away [refusing], so We sent upon them the flood of the dam,[1224] and We replaced their two [fields of] gardens with gardens of bitter fruit, tamarisks and something of sparse lote trees.", TextGerman = "Aber sie wandten sich ab. Da sandten Wir gegen sie die Flut des Staudammes, und Wir tauschten ihnen ihre zwei Gärten aus gegen zwei Gärten mit bitterem Ernteertrag und Tamarisken und einigen wenigen Zizyphusbäumen." });
                        surah34.VerseList.Add(new Verse { Number = 17, TextBosnian = "Kaznili smo ih tako zato što su bili nezahvalni, a da li Mi kažnjavamo ikog drugog do nevjernika, nezahvalnika?!", TextEnglish = "[By] that We repaid them because they disbelieved. And do We [thus] repay except the ungrateful?", TextGerman = "Das vergalten Wir ihnen, daß sie undankbar waren. Vergelten Wir denn sonst jemand anderem als dem Undankbaren?" });
                        surah34.VerseList.Add(new Verse { Number = 18, TextBosnian = "A između njih i naselja koje smo blagoslovili - načinili smo bili naselja vidljiva i po njima im odredili razdaljinu za putovanje. Putujte njima noću i danju, sigurni!", TextEnglish = "And We placed between them and the cities which We had blessed[1225] [many] visible cities. And We determined between them the [distances of] journey,[1226] [saying], 'Travel between them by night or by day in safety.'", TextGerman = "Und Wir legten zwischen ihnen und den Städten, die Wir gesegnet haben, sichtbare Städte an. Und Wir setzten das rechte Maß der Reise zwischen ihnen fest: „Reist in ihnen - Nächte und Tage - in Sicherheit umher." });
                        surah34.VerseList.Add(new Verse { Number = 19, TextBosnian = "Ali oni rekoše: 'Gospodaru naš, učini veće rastojanje prilikom putovanja naših!' - i zulum sebi učiniše, i Mi učinismo da se o njima samo priča, a njih posvuda raselismo. Doista, su u tome znaci za svakog strpljivog i zahvalnog.", TextEnglish = "But [insolently] they said, 'Our Lord, lengthen the distance between our journeys,' and wronged themselves, so We made them narrations[1227] and dispersed them in total dispersion. Indeed in that are signs for everyone patient and grateful.", TextGerman = "Sie aber sagten: „Unser Herr, vergrößere die Entfernungen zwischen unseren Reise(abschnitte)n. Sie taten sich selbst Unrecht. So machten Wir sie zu(m Gegenstand von) Geschichten und rissen sie vollständig in Stücke. Darin sind wahrlich Zeichen für jeden sehr Standhaften und sehr Dankbaren." });
                        surah34.VerseList.Add(new Verse { Number = 20, TextBosnian = "A Iblis se uvjerio u ono što je o njima mislio, i oni su se poveli za njim, osim grupe vjernika.", TextEnglish = "And Iblees had already confirmed through them[1228] his assumption,[1229] so they followed him, except for a party of believers.", TextGerman = "Wahrlich, Iblis fand seine Meinung von ihnen bestätigt. Sie folgten ihm, außer einer Gruppe der Gläubigen." });
                        surah34.VerseList.Add(new Verse { Number = 21, TextBosnian = "A nad njima on nikakva argumenta nije imao; osim što smo ih mu prepustili da bi znali onog ko vjeruje u ahiret, a ko u njega sumnja. A Gospodar tvoj bdi nad svim.", TextEnglish = "And he had over them no authority except [it was decreed] that We might make evident who believes in the Hereafter from who is thereof in doubt. And your Lord, over all things, is Guardian.[1230]", TextGerman = "Doch hatte er keine Macht über sie; (es geschah) nur, damit Wir in Erfahrung bringen, wer an das Jenseits glaubt, und (ihn unterscheiden) von demjenigen, der darüber im Zweifel ist. Dein Herr ist Hüter über alles." });
                        surah34.VerseList.Add(new Verse { Number = 22, TextBosnian = "Reci: 'Zovite one koje, pored Allaha, bogovima smatrate. Oni ništa ne posjeduju, ni koliko trun jedan, ni na nebesima ni na Zemlji; oni u njima nemaju nikakva udjela i On nema od njih nikakve pomoći.'", TextEnglish = "Say, [O Muḥammad], 'Invoke those you claim [as deities] besides Allāh.' They do not possess an atom's weight [of ability] in the heavens or on the earth, and they do not have therein any partnership [with Him], nor is there for Him from among them any assistant.", TextGerman = "Sag: Ruft diejenigen an, die ihr anstatt Allahs angebt. Sie verfügen nicht (einmal) über das Gewicht eines Stäubchens weder in den Himmeln noch auf der Erde. Und sie haben an ihnen beiden keinen Anteil, noch nahm Er sich unter ihnen jemanden, der Ihm Beistand leisten sollte." });
                        surah34.VerseList.Add(new Verse { Number = 23, TextBosnian = "Kod Njega će se moći zauzimati za nekoga samo onaj kome On dopusti. I kad iz srca njihovih nestane straha, oni će upitati: 'Šta je to rekao Gospodar vaš 'Istinu!', odgovorit će, On je Uzvišeni i Veliki.", TextEnglish = "And intercession does not benefit with Him except for one whom He permits. [And those wait] until, when terror is removed from their hearts,[1231] they will say [to one another], 'What has your Lord said?' They will say, 'The truth.' And He is the Most High, the Grand.", TextGerman = "Auch nützt bei Ihm die Fürsprache nicht - außer, wem Er es erlaubt. Wenn dann der Schrecken von ihren Herzen genommen ist, sagen sie: „Was hat euer Herr gesagt? Sie sagen: „Die Wahrheit, und Er ist der Erhabene, der Große." });
                        surah34.VerseList.Add(new Verse { Number = 24, TextBosnian = "Upitaj: 'Ko vas opskrbljuje i iz neba i iz zemlje?' I odgovori: 'Allah! Ko je onda, mi ili vi, na pravom putu, a ko je u zabludi očitoj!?'", TextEnglish = "Say, 'Who provides for you from the heavens and the earth?' Say, 'Allāh. And indeed, we or you are either upon guidance or in clear error.'", TextGerman = "Sag: Wer versorgt euch von den Himmeln und der Erde? Sag: Allah. Fürwahr, entweder wir oder ihr (die einen von uns) befinden sich in Rechtleitung oder in deutlichem Irrtum." });
                        surah34.VerseList.Add(new Verse { Number = 25, TextBosnian = "Reci: 'Vi nećete odgovarati za grijehe koje mi počinimo, niti ćemo mi odgovarati za ono što uradite vi.'", TextEnglish = "Say, 'You will not be asked about what we committed, and we will not be asked about what you do.'", TextGerman = "Sag: Ihr werdet nicht danach befragt werden, was wir an Übeltaten begangen haben, noch werden wir danach befragt werden, was ihr tut." });
                        surah34.VerseList.Add(new Verse { Number = 26, TextBosnian = "Reci: 'Gospodar naš će nas sabrati i onda nam pravedno presuditi, On je Sudija Pravedni i Sveznajući.'", TextEnglish = "Say, 'Our Lord will bring us together; then He will judge between us in truth. And He is the Knowing Judge.'[1232]", TextGerman = "Sag: Zusammenbringen wird uns unser Herr, hierauf wird Er zwischen uns der Wahrheit entsprechend entscheiden. Er ist der wahrhaft gerechte Entscheider, der Allwissende." });
                        surah34.VerseList.Add(new Verse { Number = 27, TextBosnian = "Reci: 'Pokažite mi one koje Njemu u obožavanju pridružujete!' Ne postoje; ali Bog je samo On, Allah Silni i Mudri!'", TextEnglish = "Say, 'Show me those whom you have attached to Him as partners. No! Rather, He [alone] is Allāh, the Exalted in Might, the Wise.'", TextGerman = "Sag: Zeigt mir diejenigen, die ihr Ihm als Teilhaber beigegeben habt. Keineswegs! Vielmehr ist Er Allah, der Allmächtige und Allweise." });
                        surah34.VerseList.Add(new Verse { Number = 28, TextBosnian = "Mi smo te poslali baš svim ljudima kao radosnih vijesti donositelja i upozoritelja, ali većina ljudi ne zna", TextEnglish = "And We have not sent you except comprehensively[1233] to mankind as a bringer of good tidings and a warner. But most of the people do not know.", TextGerman = "Und Wir haben dich für die Menschen allesamt nur als Frohboten und Warner gesandt. Aber die meisten Menschen wissen nicht." });
                        surah34.VerseList.Add(new Verse { Number = 29, TextBosnian = "i govore: 'Kada će već jednom to prijeteće obećanje, ako istinu govorite?'", TextEnglish = "And they say, 'When is this promise, if you should be truthful?'", TextGerman = "Und sie sagen: „Wann wird dieses Versprechen eintreten, wenn ihr wahrhaftig seid?" });
                        surah34.VerseList.Add(new Verse { Number = 30, TextBosnian = "Reci: 'Dan vam je već određen, ne možete ga ni za čas jedan zaustaviti niti ubrzati.'", TextEnglish = "Say, 'For you is the appointment of a Day [when] you will not remain thereafter an hour, nor will you precede [it].'", TextGerman = "Sag: Für euch ist eine Verabredung auf einen Tag (festgelegt), von dem ihr (euer Schicksal) weder um eine Stunde hinausschieben noch vorverlegen könnt." });
                        surah34.VerseList.Add(new Verse { Number = 31, TextBosnian = "A oni koji ne vjeruju govore: 'Mi nećemo vjerovati u ovaj Kur'an niti u Knjige prije njega!' A kad bi samo vidio kad oni koji su učinili zulum pred Gospodarom svojim budu sabrani i kad stanu među sobom razgovarati: 'Da vas nije bilo, sigurno bismo bili vjernici!', reći će oni koji su tlačeni bili onima koji su oholi bili.", TextEnglish = "And those who disbelieve say, 'We will never believe in this Qur’ān nor in that before it.' But if you could see when the wrongdoers are made to stand before their Lord, refuting each others' words...[1234] Those who were oppressed will say to those who were arrogant, 'If not for you, we would have been believers.'", TextGerman = "Und diejenigen, die ungläubig sind, sagen: „Wir werden nicht an diesen Qur’an glauben und auch nicht an das, was (an Offenbarungen) vor ihm da war. Könntest du nur sehen, wenn die Ungerechten vor ihren Herrn gestellt werden und untereinander die Worte wechseln! Diejenigen, die unterdrückt wurden, sagen zu denjenigen, die sich hochmütig verhielten: „Wenn ihr nicht gewesen wäret, so wären wir wahrlich gläubig gewesen." });
                        surah34.VerseList.Add(new Verse { Number = 32, TextBosnian = "A zar smo vas mi od Upute odvratili nakon što vam je ona došla?! Ne, sami ste vi prestupnici bili', reći će oni koji su bili oholi onima koji su bili tlačeni.", TextEnglish = "Those who were arrogant will say to those who were oppressed, 'Did we avert you from guidance after it had come to you? Rather, you were criminals.'", TextGerman = "Diejenigen, die sich hochmütig verhielten, sagen zu denjenigen, die unterdrückt wurden: „Sind wir es gewesen, die euch von der Rechtleitung abgehalten haben, nachdem sie zu euch gekommen war? Nein! Vielmehr wart ihr Übeltäter." });
                        surah34.VerseList.Add(new Verse { Number = 33, TextBosnian = "Nije bilo tako!',odgovorit će oni koji su bili tlačeni onima koji su bili oholi, 'nego ste i danju i noću spletkarili kad ste od nas tražili da u Allaha ne vjerujemo i da Mu takmace pripisujemo.' I prikrit će kajanje kad vide da će kažnjeni biti, a mi ćemo na vratove nevjernika sindžire staviti; zar će biti kažnjeni drukčije nego prema onome kako su radili?!", TextEnglish = "Those who were oppressed will say to those who were arrogant, 'Rather, [it was your] conspiracy of night and day when you were ordering us to disbelieve in Allāh and attribute to Him equals.' But they will [all] confide regret when they see the punishment; and We will put shackles on the necks of those who disbelieved. Will they be recompensed except for what they used to do?", TextGerman = "Und diejenigen, die unterdrückt wurden, sagen zu denjenigen, die sich hochmütig verhielten: „Nein! Vielmehr waren es (eure) Ränke bei Nacht und bei Tag, da ihr uns befohlen hattet, Allah zu verleugnen und Ihm andere als Seinesgleichen zur Seite zu stellen. Und sie werden insgeheim Reue hegen, wenn sie die Strafe sehen. Und Wir werden die Fesseln um die Hälse derjenigen legen, die ungläubig waren. Wird ihnen (denn) etwas anderes vergolten als das, was sie zu tun pflegten?" });
                        surah34.VerseList.Add(new Verse { Number = 34, TextBosnian = "Mi ni u jedno naselje upozoritelja nismo poslali a da nisu rekli oni koji su na raskošan život bili navikli: 'Ne vjerujemo mi u ono što je po vama poslano!'", TextEnglish = "And We did not send into a city any warner except that its affluent said, 'Indeed we, in that with which you were sent, are disbelievers.'", TextGerman = "Wir haben in keine Stadt einen Warner gesandt, ohne daß diejenigen, die in ihr üppig lebten, gesagt hätten: „Gewiß, das, womit ihr gesandt seid, verleugnen wir." });
                        surah34.VerseList.Add(new Verse { Number = 35, TextBosnian = "I uz to su govorili: 'Imamo više imetka i djece; mi nećemo biti patnji podvrgnuti!'", TextEnglish = "And they[1235] said, 'We are more [than the believers] in wealth and children, and we are not to be punished.'", TextGerman = "Und sie sagen: „Wir haben mehr Besitz und Kinder, und wir werden nicht gestraft werden." });
                        surah34.VerseList.Add(new Verse { Number = 36, TextBosnian = "Reci: 'Gospodar moj daje obilnu opskrbu onome kome hoće, a i uskraćuje, ali, većina ljudi ne zna.'", TextEnglish = "Say, 'Indeed, my Lord extends provision for whom He wills and restricts [it], but most of the people do not know.'", TextGerman = "Sag: Gewiß, mein Herr gewährt die Versorgung großzügig, wem Er will, und bemißt auch. Aber die meisten Menschen wissen nicht." });
                        surah34.VerseList.Add(new Verse { Number = 37, TextBosnian = "Ni imeci vaši ni djeca vaša neće vas učiniti Nama bliskim; izuzev onih koji vjeruju i dobra djela čine. Njima pripada višestruka nagrada za ono što su radili, i oni će u visokim odajama biti sigurni.", TextEnglish = "And it is not your wealth or your children that bring you nearer to Us in position, but it is [by being] one who has believed and done righteousness. For them there will be the double reward for what they did, and they will be in the upper chambers [of Paradise], safe [and secure].", TextGerman = "Nicht euer Besitz ist es, und auch nicht eure Kinder sind es, die euch Zutritt in Unsere Nähe verschaffen, außer jemandem, der glaubt und rechtschaffen handelt. Diese erhalten mehrfachen Lohn für das, was sie getan haben, und sie werden in den Obergemächern in Sicherheit sein." });
                        surah34.VerseList.Add(new Verse { Number = 38, TextBosnian = "A oni koji se budu trudili da se ajetima i znakovima Našim suprotstave, odvraćajući od njih, oni će biti u patnju privedeni.", TextEnglish = "And the ones who strive against Our verses to cause [them] failure[1236] - those will be brought into the punishment [to remain].", TextGerman = "Diejenigen aber, die ständig gegen Unsere Zeichen eifern und ihnen Machtlosigkeit nachweisen wollen, sie werden zur Strafe vorgeführt werden." });
                        surah34.VerseList.Add(new Verse { Number = 39, TextBosnian = "Reci: 'Gospodar moj daje obilnu opskrbu onome kome hoće od robova Svojih, a kome hoće uskraćuje; što god vi udijelite, On će to nadoknaditi, On je najbolji Opskrbitelj.'", TextEnglish = "Say, 'Indeed, my Lord extends provision for whom He wills of His servants and restricts [it] for him. But whatever thing you spend [in His cause] - He will compensate it; and He is the best of providers.'", TextGerman = "Sag: Gewiß, mein Herr gewährt die Versorgung großzügig, wem von Seinen Dienern Er will, und bemißt auch. Und was immer ihr auch ausgebt, so wird Er es euch ersetzen, und Er ist der Beste der Versorger." });
                        surah34.VerseList.Add(new Verse { Number = 40, TextBosnian = "A na Dan kad ih sve sabere, pa meleke upita: 'Jesu li ovi vas obožavali?',", TextEnglish = "And [mention] the Day when He will gather them all and then say to the angels, 'Did these [people] used to worship you?'", TextGerman = "Und am Tag, da Er sie alle versammelt, hierauf Er zu den Engeln sagen wird: „Diese da, pflegten sie euch zu dienen?," });
                        surah34.VerseList.Add(new Verse { Number = 41, TextBosnian = "oni će odgovoriti: 'Uzvišen neka si, Ti si Gospodar naš, a nisu oni! Oni su džine obožavali i većina njih je vjerovala u njih.'", TextEnglish = "They will say, 'Exalted are You! You, [O Allāh], are our benefactor excluding [i.e., not] them. Rather, they used to worship the jinn; most of them were believers in them.'", TextGerman = "werden sie sagen:„Preis sei Dir! Du bist unser Schutzherr, nicht sie. Nein! Vielmehr dienten sie den Ginn; die meisten von ihnen glauben an sie." });
                        surah34.VerseList.Add(new Verse { Number = 42, TextBosnian = "Taj dan jedni drugima nećete moći ni koristiti ni nauditi, a Mi ćemo onima koji su zulum učinili reći: 'Kušajte patnju u Vatri koju ste poricali!'", TextEnglish = "But today [i.e., the Day of Judgement] you do not hold for one another [the power of] benefit or harm, and We will say to those who wronged, 'Taste the punishment of the Fire, which you used to deny.'", TextGerman = "„Heute vermögt ihr einer dem anderen weder Nutzen noch Schaden zu bringen. Und Wir sagen zu denjenigen, die Unrecht getan haben: „Kostet die Strafe des (Höllen)feuers, das ihr für Lüge zu erklären pflegtet." });
                        surah34.VerseList.Add(new Verse { Number = 43, TextBosnian = "Kad im se Naši jasni ajeti kazuju, oni govore: 'Ovaj čovjek samo hoće da vas odvrati od onog što su preci vaši obožavali', i govore: 'Ovo nije ništa drugo nego laž - izmišljotina. A oni koji ne vjeruju govore o Istini kada im dolazi: 'Ovo nije ništa drugo nego magija jasna!'", TextEnglish = "And when Our verses are recited to them as clear evidences, they say, 'This is not but a man who wishes to avert you from that which your fathers were worshipping.' And they say, 'This is not except a lie invented.' And those who disbelieve say of the truth when it has come to them, 'This is not but obvious magic.'", TextGerman = "Und wenn ihnen Unsere Zeichen als klare Beweise verlesen werden, sagen sie: „Dieser ist nur ein Mann, der euch von dem abhalten will, dem eure Väter dienten. Und sie sagen: „Das ist nur eine ersonnene ungeheuerliche Lüge. Und diejenigen, die ungläubig sind, sagen von der Wahrheit, nachdem sie zu ihnen gekommen ist: „Das ist nur deutliche Zauberei." });
                        surah34.VerseList.Add(new Verse { Number = 44, TextBosnian = "A Mi im nismo dali nikakve knjige da ih izučavaju i Mi im, prije tebe, nismo bili poslali nikoga ko bi ih upozoravao.", TextEnglish = "And We had not given them any scriptures which they could study, and We had not sent to them before you, [O Muḥammad], any warner.", TextGerman = "Und wir haben ihnen weder Schriften gegeben, die sie (hätten) eingehend lesen (können), noch haben Wir vor dir einen Warner zu ihnen gesandt." });
                        surah34.VerseList.Add(new Verse { Number = 45, TextBosnian = "A i oni prije njih su poslanike u laž ugonili, a ovi nisu postigli ni deseti dio od onoga što smo onima bili dali, pa su, ipak, poslanike Moje lažnim smatrali, i kakva je samo bila kazna Moja!", TextEnglish = "And those before them denied, and they [i.e., the people of Makkah] have not attained a tenth of what We had given them. But they [i.e., the former peoples] denied My messengers, so how [terrible] was My reproach.", TextGerman = "Für Lüge erklärt haben (es auch) diejenigen, die vor ihnen waren, wobei diese nicht einmal das Zehntel dessen erreichten, was Wir jenen gegeben hatten. Sie bezichtigten Meine Gesandten der Lüge. Wie war da Meine Mißbilligung!" });
                        surah34.VerseList.Add(new Verse { Number = 46, TextBosnian = "Reci: 'Savjetujem vam samo jedno: ustanite zarad Allaha, dvojica po dvojica, ili jedan po jedan, pa zatim razmislite da drug vaš nije lud. On vas samo prije teške patnje upozorava.'", TextEnglish = "Say, 'I only advise you of one [thing] - that you stand for Allāh, [seeking truth] in pairs and individually, and then give thought.' There is not in your companion any madness. He is only a warner to you before a severe punishment.", TextGerman = "Sag: Ich ermahne euch nur zu einem: daß ihr euch zu zweit und einzeln um Allahs willen hinstellt und hierauf nachdenkt. Euer Gefährte leidet nicht an Besessenheit. Er ist euch nur ein Warner vor einer (bevorstehenden) strengen Strafe." });
                        surah34.VerseList.Add(new Verse { Number = 47, TextBosnian = "Reci: 'Kakvu god nagradu da zatražim od vas, nek ostane vama, mene će nagraditi Allah, On je svjedok svemu.", TextEnglish = "Say, 'Whatever payment I might have asked of you - it is yours. My payment is only from Allāh, and He is, over all things, Witness.'", TextGerman = "Sag: Was auch immer an Lohn ich von euch hätte verlangen können, das gehört euch. Mein Lohn obliegt nur Allah. Und Er ist über alles Zeuge." });
                        surah34.VerseList.Add(new Verse { Number = 48, TextBosnian = "Reci: 'Gospodar moj šalje Istinu, On je Znalac onoga što je čulima nedokučivo.", TextEnglish = "Say, 'Indeed, my Lord projects the truth, Knower of the unseen.'", TextGerman = "Sag: Mein Herr kommt mit der Wahrheit, (Er,) der Allwisser der verborgenen Dinge." });
                        surah34.VerseList.Add(new Verse { Number = 49, TextBosnian = "Reci: 'Došla je Istina, a laži je nestalo!'", TextEnglish = "Say, 'The truth has come, and falsehood can neither begin [anything] nor repeat [it].'[1237]", TextGerman = "Sag: Die Wahrheit ist gekommen. Das Falsche kann weder (etwas) neu von Anfang an machen noch es wiederholen." });
                        surah34.VerseList.Add(new Verse { Number = 50, TextBosnian = "Reci: 'Ako zalutam, zalutao sam na svoju štetu, a ako sam na Pravom putu, to je zbog onoga što mi Gospodar moj objavljuje; On je, zaista, Onaj Koji sve čuje i blizu je.'", TextEnglish = "Say, 'If I should err, I would only err against myself. But if I am guided, it is by what my Lord reveals to me. Indeed, He is Hearing and near.'", TextGerman = "Sag: Wenn ich irregehe, gehe ich nur zu meinem eigenen Nachteil irre, und wenn ich rechtgeleitet bin, so ist es durch das, was mir mein Herr (als Offenbarung) eingibt. Gewiß, Er ist Allhörend und nahe." });
                        surah34.VerseList.Add(new Verse { Number = 51, TextBosnian = "A da ti je vidjeti kad ih strava uhvati - umaći neće moći; izbliza bit će ščepani -", TextEnglish = "And if you could see[1238] when they are terrified but there is no escape, and they will be seized from a place nearby.", TextGerman = "Könntest du nur sehen, wenn sie erschrecken! - Da gibt es kein Entrinnen -, und sie werden von einem nahen Ort aus weggenommen." });
                        surah34.VerseList.Add(new Verse { Number = 52, TextBosnian = "i kada reknu: 'Vjerujemo u Istinu!' A kako vjerovanje mogu prihvatiti s ovog dalekog mjesta?!", TextEnglish = "And they will [then] say, 'We believe in it!' But how for them will be the taking[1239] [of faith] from a place far away?[1240]", TextGerman = "Und sie sagen: „Wir glauben daran. Aber wie könnten sie (den Glauben) von einem fernen Ort aus erlangen," });
                        surah34.VerseList.Add(new Verse { Number = 53, TextBosnian = "U nju oni nisu prije vjerovali, nagađajući izdaleka o onome što je čulima nedokučivo;", TextEnglish = "And they had already disbelieved in it before and would assault[1241] the unseen from a place far away.[1242]", TextGerman = "wo sie ihn doch zuvor verleugnet haben? Und sie schleudern (Vermutungen) auf das Verborgene von einem fernen Ort aus." });
                        surah34.VerseList.Add(new Verse { Number = 54, TextBosnian = "i između njih i onoga što budu željeli bit će prepreka, kao što je prije bilo učinjeno s njima sličnim, jer su svi oni, doista, mnogo sumnjali.", TextEnglish = "And prevention will be placed between them and what they desire,[1243] as was done with their kind before. Indeed, they were in disquieting doubt [i.e., denial].", TextGerman = "Und es wird eine Trennung vorgenommen zwischen ihnen und dem, was sie begehren, so wie es zuvor mit ihresgleichen gemacht wurde; sie waren ja in starkem Zweifel." });
                        result = surah34;
                        break;
                    #endregion

                    // 
                    #region
                    case 35:
                        Surah surah35 = new Surah(); surah35.Name = Constants.Surah35Name; surah35.Number = 35; surah35.VerseList = new List<Verse>();
                        surah35.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah35.VerseList.Add(new Verse { Number = 1, TextBosnian = "Sva hvala neka je Allahu, Stvoritelju nebesa i Zemlje, Koji meleke sa po dva, tri i četiri krila čini izaslanicima; a u stvaranju dodaje što hoće, On, uistinu, nad svime ima moć.", TextEnglish = "[All] praise is [due] to Allāh, Creator of the heavens and the earth, [who] made the angels messengers having wings, two or three or four. He increases in creation what He wills. Indeed, Allāh is over all things competent.", TextGerman = "(Alles) Lob gehört Allah, dem Erschaffer der Himmel und der Erde, Der die Engel zu Gesandten gemacht hat mit Flügeln, (je) zwei, drei und vier! Er fügt der Schöpfung hinzu, was Er will. Gewiß, Allah hat zu allem die Macht." });
                        surah35.VerseList.Add(new Verse { Number = 2, TextBosnian = "Milost koju Allah podari ljudima- niko ne može uskratiti, a ono što On uskrati - niko ne može, poslije Njega, dati; On je Silni i Mudri.", TextEnglish = "Whatever Allāh grants to people of mercy - none can withhold it; and whatever He withholds - none can release it thereafter. And He is the Exalted in Might, the Wise.", TextGerman = "Was Allah den Menschen an Barmherzigkeit auftut, das kann keiner zurückhalten. Und was Er zurückhält, das kann keiner nach Ihm freigeben. Und Er ist der Allmächtige und Allweise." });
                        surah35.VerseList.Add(new Verse { Number = 3, TextBosnian = "O ljudi, sjetite se blagodati kojom vas Allah obasipa! Postojili, osim Allaha, ikakav drugi stvoritelj koji vas s neba i iz zemlje opskrbljuje?! Osim Njega nema drugog boga, pa kuda se onda odmećete?!", TextEnglish = "O mankind, remember the favor of Allāh upon you. Is there any creator other than Allāh who provides for you from the heaven and earth? There is no deity except Him, so how are you deluded?", TextGerman = "O ihr Menschen, gedenkt der Gunst Allahs an euch. Gibt es einen anderen Schöpfer als Allah, Der euch vom Himmel und von der Erde versorgt? Es gibt keinen Gott außer Ihm. Wie laßt ihr euch also abwendig machen?" });
                        surah35.VerseList.Add(new Verse { Number = 4, TextBosnian = "Ako te oni u laž utjeruju - pa i prije tebe su poslanike u laž utjerivali, a Allahu će se sve vratiti.", TextEnglish = "And if they deny you, [O Muḥammad] - already were messengers denied before you. And to Allāh are returned [all] matters.", TextGerman = "Wenn sie dich der Lüge bezichtigen, so wurden bereits vor dir Gesandte der Lüge bezichtigt. Und zu Allah werden (all) die Angelegenheiten zurückgebracht." });
                        surah35.VerseList.Add(new Verse { Number = 5, TextBosnian = "O ljudi, Allahovo obećanje je, zaista, istinito, pa neka vas nikako život dunjalučki ne zavara i neka vas obmanitelj - šejtan o Allahu ne zavara.", TextEnglish = "O mankind, indeed the promise of Allāh is truth, so let not the worldly life delude you and be not deceived about Allāh by the Deceiver [i.e., Satan].", TextGerman = "O ihr Menschen, gewiß, Allahs Versprechen ist wahr. So soll euch das diesseitige Leben nicht täuschen, und nicht täuschen soll euch hinsichtlich Allahs der Täuscher." });
                        surah35.VerseList.Add(new Verse { Number = 6, TextBosnian = "Šejtan je, uistinu, vaš neprijatelj, pa ga takvim i smatrajte! On poziva pristaše svoje da budu stanovnici Vatre.", TextEnglish = "Indeed, Satan is an enemy to you; so take him as an enemy. He only invites his party to be among the companions of the Blaze.", TextGerman = "Gewiß, der Satan ist euch ein Feind; so nehmt ihn euch zum Feind. Er ruft ja seine Anhängerschaft nur dazu auf, zu den Insassen der Feuerglut zu gehören." });
                        surah35.VerseList.Add(new Verse { Number = 7, TextBosnian = "Onima koji ne vjeruju pripada patnja žestoka, a onima koji vjeruju i dobra djela čine pripada oprost i nagrada velika.", TextEnglish = "Those who disbelieve will have a severe punishment, and those who believe and do righteous deeds will have forgiveness and great reward.", TextGerman = "Diejenigen, die ungläubig sind, für sie wird es strenge Strafe geben; und diejenigen, die glauben und rechtschaffene Werke tun, für sie wird es Vergebung und großen Lohn geben." });
                        surah35.VerseList.Add(new Verse { Number = 8, TextBosnian = "Zar pomoći možeš onome kome su njegova ružna djela uljepšana, a i on ih smatra lijepim?! Allah, doista, kome hoće daje da je u zabludi, a na Pravi put upućuje koga hoće, pa ne izgaraj od žalosti za njima; Allah, doista, dobro zna sve što oni čine.", TextEnglish = "Then is one to whom the evil of his deed has been made attractive so he considers it good [like one rightly guided]? For indeed, Allāh sends astray whom He wills and guides whom He wills. So do not let yourself perish over them in regret. Indeed, Allāh is Knowing of what they do.", TextGerman = "Soll denn derjenige, dem sein böses Tun ausgeschmückt wird und der es dann für schön befindet, (wie jemand sein, der rechtgeleitet ist)? Allah läßt in die Irre gehen, wen Er will, und Er leitet recht, wen Er will. Darum soll deine Seele nicht über sie in Bedauern zergehen, denn Allah weiß Bescheid über das, was sie machen." });
                        surah35.VerseList.Add(new Verse { Number = 9, TextBosnian = "Allah je Onaj Koji šalje vjetrove koji pokreću oblake, te ih ka mrtvim predjelima navodimo i njima zemlju, nakon što je mrtva bila, oživljavamo: takvo će i proživljenje biti.", TextEnglish = "And it is Allāh who sends the winds, and they stir the clouds, and We drive them to a dead land and give life thereby to the earth after its lifelessness. Thus is the resurrection.", TextGerman = "Und Allah ist es, Der die Winde sendet, und da wühlen sie die Wolken auf. Dann treiben Wir sie zu einem toten Land und machen damit dann die Erde nach ihrem Tod wieder lebendig. Ebenso wird es auch mit der Auferstehung sein." });
                        surah35.VerseList.Add(new Verse { Number = 10, TextBosnian = "Ako neko želi veličinu - pa, u Allaha je sva veličina! K Njemu se dižu lijepe riječi, a dobra djela ih uzdižu. A onima koji smišljaju loša djela, pripada patnja bolna, i njihovo spletkarenje je posao bezuspješni.", TextEnglish = "Whoever desires honor [through power] - then to Allāh belongs all honor.[1245] To Him ascends good speech, and righteous work raises it.[1246] But they who plot evil deeds will have a severe punishment, and the plotting of those - it will perish.", TextGerman = "Wenn immer einer die Macht anstrebt, so gehört alle Macht Allah. Zu Ihm steigt das gute Wort hinauf, und die rechtschaffene Tat hebt Er (zu sich) empor. Und für diejenigen, die Ränke böser Taten schmieden, wird es strenge Strafe geben, und die Ränke jener werden zu Fall kommen." });
                        surah35.VerseList.Add(new Verse { Number = 11, TextBosnian = "Allah vas je od zemlje stvorio, zatim od kapi sjemena, i najzad vas čini muškarcima i ženama. I nijedna žena ne zanese, niti rodi, a da On to ne zna. I ničiji život se ne produži, niti skrati, a da to nije zabilježeno u Knjizi; to je Allahu, uistinu, lahko!", TextEnglish = "And Allāh created you from dust, then from a sperm-drop; then He made you mates. And no female conceives nor does she give birth except with His knowledge. And no aged person is granted [additional] life nor is his lifespan lessened but that it is in a register. Indeed, that for Allāh is easy.", TextGerman = "Und Allah hat euch aus Erde, hierauf aus einem Samentropfen erschaffen und euch hierauf zu Paaren gemacht. Nicht schwanger wird ein weibliches Wesen noch kommt es nieder, außer mit Seinem Wissen. Und keinem, dem ein langes Leben gewährt wird, wird das Altwerden oder eine Verkürzung seiner Lebenszeit zuteil, ohne daß es in einem Buch (verzeichnet) wäre. Gewiß, dies ist Allah ein leichtes." });
                        surah35.VerseList.Add(new Verse { Number = 12, TextBosnian = "Ni dva mora nisu jednaka: jedno je slatko i prijatno - voda mu se lahko pije, a drugo je slano i gorko; vi iz svakog jedete svježe meso i vadite nakit kojim se kitite; i ti vidiš lađe kako po njemu sijeku vodu da stječete iz obilja Njegova, i da biste bili zahvalni.", TextEnglish = "And not alike are the two seas [i.e., bodies of water]. One is fresh and sweet, palatable for drinking, and one is salty and bitter. And from each you eat tender meat and extract ornaments which you wear, and you see the ships plowing through [them] that you might seek of His bounty; and perhaps you will be grateful.", TextGerman = "Nicht gleich sind die beiden Meere: dies ist süß, erfrischend und angenehm zu trinken; jenes ist salzig und (auf der Zunge) brennend. Und aus allen (beiden) eßt ihr frisches Fleisch und holt Schmuck heraus, den ihr anlegt. Und du siehst die Schiffe, wie sie es durchpflügen, damit ihr nach etwas von Seiner Huld trachtet, und auf daß ihr dankbar sein möget!" });
                        surah35.VerseList.Add(new Verse { Number = 13, TextBosnian = "On uvodi noć u dan i uvodi dan u noć, On je potčinio Sunce i Mjesec - svako plovi do roka određenog; to vam je, eto, Allah, Gospodar vaš; sve je u posjedu Njegovom! A oni koje, pored Njega, molite ne posjeduju ništa, ni koliko je opna na hurminoj košpici.", TextEnglish = "He causes the night to enter the day, and He causes the day to enter the night and has subjected the sun and the moon - each running [its course] for a specified term. That is Allāh, your Lord; to Him belongs sovereignty. And those whom you invoke other than Him do not possess [as much as] the membrane of a date seed.", TextGerman = "Er läßt die Nacht in den Tag eindringen, und Er läßt den Tag in die Nacht eindringen. Und Er hat die Sonne und den Mond dienstbar gemacht - ein jedes läuft auf eine festgesetzte Frist zu. Dies ist doch Allah, euer Herr; Ihm gehört die Herrschaft. Diejenigen aber, die ihr anstatt Seiner anruft, verfügen nicht einmal über das Häutchen eines Dattelkerns." });
                        surah35.VerseList.Add(new Verse { Number = 14, TextBosnian = "Ako ih molite, ne čuju vašu molbu, a da i čuju, ne bi vam se odazvali; na Kijametskom danu oni će poreći da ste ih Njemu u obožavanju pridruživali. I niko te neće obavijestiti kao Onaj Koji sve u potpunosti zna.", TextEnglish = "If you invoke them, they do not hear your supplication; and if they heard, they would not respond to you. And on the Day of Resurrection they will deny your association.[1247] And none can inform you like [one] Aware [of all matters].", TextGerman = "Wenn ihr sie anruft, hören sie euer Bittgebet nicht, und wenn sie (es) auch hörten, würden sie euch doch nicht erhören. Und am Tag der Auferstehung verleugnen sie, daß ihr (sie Allah) beigesellt habt. Keiner kann dir kundtun wie Einer, der Kenntnis von allem hat." });
                        surah35.VerseList.Add(new Verse { Number = 15, TextBosnian = "O ljudi, vi ste siromasi, Allah vama treba, a Allah je Neovisni i Hvaljeni.", TextEnglish = "O mankind, you are those in need of Allāh, while Allāh is the Free of need, the Praiseworthy.", TextGerman = "O ihr Menschen, ihr seid es, die Allahs bedürftig sind; Allah aber ist der Unbedürftige und Lobenswürdige." });
                        surah35.VerseList.Add(new Verse { Number = 16, TextBosnian = "Ako hoće, uklonit će vas i nova stvorenja dovesti,", TextEnglish = "If He wills, He can do away with you and bring forth a new creation.", TextGerman = "Wenn Er will, läßt Er euch vergehen und bringt eine neue Schöpfung." });
                        surah35.VerseList.Add(new Verse { Number = 17, TextBosnian = "to Allahu nije teško.", TextEnglish = "And that is for Allāh not difficult.", TextGerman = "Und dies ist für Allah keineswegs schwer." });
                        surah35.VerseList.Add(new Verse { Number = 18, TextBosnian = "I nijedan grešnik neće tuđe grijehe nositi; ako grijesima pretovareni pozove da mu oni budu poneseni, niko mu ih neće ponijeti, pa ni bližnji. A ti ćeš upozoriti samo one koji se Gospodara svoga boje, i kada ih niko ne vidi, i koji obavljaju namaz - onaj ko se očisti, očistio se za svoje dobro - a kod Allaha je konačno odredište!", TextEnglish = "And no bearer of burdens will bear the burden of another. And if a heavily laden soul calls [another] to [carry some of] its load, nothing of it will be carried, even if he should be a close relative. You can only warn those who fear their Lord unseen and have established prayer. And whoever purifies himself only purifies himself for [the benefit of] his soul. And to Allāh is the [final] destination.", TextGerman = "Keine lasttragende (Seele) nimmt die Last einer anderen auf sich. Und wenn eine Schwerbeladene (zum Mittragen) ihrer Last aufruft, wird nichts davon (für sie) getragen, und handelte es sich dabei um einen Verwandten . Du kannst nur diejenigen warnen, die ihren Herrn im Verborgenen fürchten und das Gebet verrichten. Und wer sich läutert, läutert sich nur zu seinem eigenen Vorteil. Und zu Allah ist der Ausgang." });
                        surah35.VerseList.Add(new Verse { Number = 19, TextBosnian = "Nisu nikako isti slijepac i onaj koji vidi,", TextEnglish = "Not equal are the blind and the seeing,", TextGerman = "Nicht gleich sind der Blinde und der Sehende," });
                        surah35.VerseList.Add(new Verse { Number = 20, TextBosnian = "ni tmine i svjetlo,", TextEnglish = "Nor are the darknesses and the light,", TextGerman = "noch die Finsternisse und das Licht," });
                        surah35.VerseList.Add(new Verse { Number = 21, TextBosnian = "ni hladovina i vrućina,", TextEnglish = "Nor are the shade and the heat,[1248]", TextGerman = "noch der Schatten und die (Sonnen)hitze." });
                        surah35.VerseList.Add(new Verse { Number = 22, TextBosnian = "i nisu nikako isti živi i mrtvi. Zaista će Allah, koga On hoće, učiniti da čuje, a ti ne možeš one u kaburovima dozvati,", TextEnglish = "And not equal are the living and the dead. Indeed, Allāh causes to hear whom He wills, but you cannot make hear those in the graves.[1249]", TextGerman = "Und nicht gleich sind auch die Lebenden und die Toten. Allah läßt hören, wen Er will. Und du vermagst nicht, diejenigen hören zu lassen, die in den Gräbern sind." });
                        surah35.VerseList.Add(new Verse { Number = 23, TextBosnian = "ti si samo upozoritelj.", TextEnglish = "You, [O Muḥammad], are not but a warner.", TextGerman = "Du bist nur ein Warner." });
                        surah35.VerseList.Add(new Verse { Number = 24, TextBosnian = "Mi smo te poslali s Istinom kao radosnih vijesti donositelja i upozoritelja; a nije bilo naroda kome nije došao upozoritelj.", TextEnglish = "Indeed, We have sent you with the truth as a bringer of good tidings and a warner. And there was no nation but that there had passed within it a warner.", TextGerman = "Wir haben dich ja mit der Wahrheit gesandt als Frohboten und als Warner. Und es gibt keine Gemeinschaft, in der nicht ein Warner vorangegangen wäre." });
                        surah35.VerseList.Add(new Verse { Number = 25, TextBosnian = "Ako te oni u laž utjeruju - pa, i oni prije njih su poslanike u laž utjerivali, a oni su im jasne dokaze donosili, i Listove i Knjigu svjetilju.", TextEnglish = "And if they deny you - then already have those before them denied. Their messengers came to them with clear proofs and written ordinances and with the enlightening Scripture.", TextGerman = "Und wenn sie dich der Lüge bezichtigen, so haben bereits diejenigen, die vor ihnen waren, (die Gesandten) der Lüge bezichtigt. Ihre Gesandten kamen zu ihnen mit den klaren Beweisen und mit den Schriften und den erleuchtenden Büchern." });
                        surah35.VerseList.Add(new Verse { Number = 26, TextBosnian = "A onda bih one koji nisu vjerovali kaznom dohvatio, pa kakva je samo bila osuda Moja!", TextEnglish = "Then I seized the ones who disbelieved, and how [terrible] was My reproach.", TextGerman = "Hierauf ergriff Ich diejenigen, die ungläubig waren. Wie war da Meine Mißbilligung!" });
                        surah35.VerseList.Add(new Verse { Number = 27, TextBosnian = "Zar ne znaš da Allah s neba pušta vodu i da Mi pomoću nje stvaramo plodove različitih vrsta; postoje brda bijelih i crvenih staza, različitih boja, i sasvim crnih;", TextEnglish = "Do you not see that Allāh sends down rain from the sky, and We produce thereby fruits of varying colors? And in the mountains are tracts, white and red of varying shades and [some] extremely black.", TextGerman = "Siehst du nicht, daß Allah vom Himmel Wasser herabkommen läßt? Damit bringen Wir dann Früchte von unterschiedlichen Farben hervor. Und von den Bergen gibt es Schichten, weiße und rote - von unterschiedlichen Farben - und rabenschwarze." });
                        surah35.VerseList.Add(new Verse { Number = 28, TextBosnian = "i ljudi i životinja i stoke ima, isto tako, različitih vrsta. A samo se Allaha, među robovima Njegovim, boje učeni. Allah je, doista, silan i Onaj Koji oprašta grijehe.", TextEnglish = "And among people and moving creatures and grazing livestock are various colors similarly. Only those fear Allāh, from among His servants, who have knowledge. Indeed, Allāh is Exalted in Might and Forgiving.", TextGerman = "Und unter den Menschen und den Tieren und dem Vieh gibt es (auf ähnliche Weise) unterschiedliche Farben. So ist es. Allah fürchten von Seinen Dienern eben nur die Gelehrten. Gewiß, Allah ist Allmächtig und Allvergebend." });
                        surah35.VerseList.Add(new Verse { Number = 29, TextBosnian = "Oni koji Allahovu Knjigu čitaju i namaz obavljaju i od onoga čime ih Mi opskrbljujemo udjeljuju, i tajno i javno, mogu da se nadaju dobiti koja neće nestati,", TextEnglish = "Indeed, those who recite the Book of Allāh and establish prayer and spend [in His cause] out of what We have provided them, secretly and publicly, [can] expect a transaction [i.e., profit] that will never perish -", TextGerman = "Gewiß, diejenigen, die Allahs Buch verlesen, das Gebet verrichten und von dem, womit Wir sie versorgt haben, heimlich und öffentlich ausgeben, hoffen auf einen Handel, der nicht zu Fall kommen wird," });
                        surah35.VerseList.Add(new Verse { Number = 30, TextBosnian = "da ih On prema onome što su radili nagradi i još im iz obilja Svoga da; On je Onaj Koji oprašta grijehe i zahvalan je.", TextEnglish = "That He may give them in full their rewards and increase for them of His bounty. Indeed, He is Forgiving and Appreciative.[1250]", TextGerman = "damit Er ihnen ihren Lohn in vollem Maß zukommen lasse und ihnen von Seiner Huld noch mehr gebe. Gewiß, Er ist Allvergebend und stets zu Dank bereit." });
                        surah35.VerseList.Add(new Verse { Number = 31, TextBosnian = "A ono što ti iz Knjige objavljujemo sušta je Istina, ona potvrđuje da su istinite i one prije nje. Allah, zaista, o robovima svojim sve upotpunosti zna i sve dobro vidi.", TextEnglish = "And that which We have revealed to you, [O Muḥammad], of the Book is the truth, confirming what was before it. Indeed Allāh, of His servants, is Aware and Seeing.", TextGerman = "Und was Wir dir vom Buch (als Offenbarung) eingegeben haben, ist die Wahrheit, das zu bestätigen, was vor ihm war. Allah hat wahrlich Kenntnis von Seinen Dienern und sieht sie wohl." });
                        surah35.VerseList.Add(new Verse { Number = 32, TextBosnian = "Mi ćemo učiniti da Knjigu poslije naslijede oni Naši robovi koje Mi izaberemo; bit će onih koji će prema sebi nasilje učiniti, bit će onih umjerenih, bit će i onih koji će, Allahovom voljom, u dobrima prednjačiti - za to će veliku blagodat dobiti:", TextEnglish = "Then We caused to inherit the Book those We have chosen of Our servants;[1251] and among them is he who wrongs himself [i.e., sins], and among them is he who is moderate, and among them is he who is foremost in good deeds by permission of Allāh. That [inheritance] is what is the great bounty.", TextGerman = "Hierauf gaben Wir das Buch denjenigen von Unseren Dienern, die Wir auserwählten, zum Erbe. Mancher von ihnen tut sich selbst Unrecht, mancher von ihnen zeigt ein gemäßigtes Verhalten, und mancher von ihnen geht mit den guten Dingen mit Allahs Erlaubnis voran. Das ist die große Huld." });
                        surah35.VerseList.Add(new Verse { Number = 33, TextBosnian = "perivoje Adna, u koje će ući, u kojima će se zlatnim narukvicama, biserom ukrašenim, kititi, a haljine će im, u njima, od svile biti.", TextEnglish = "[For them are] gardens of perpetual residence which they will enter. They will be adorned therein with bracelets of gold and pearls, and their garments therein will be silk.", TextGerman = "Die Gärten Edens (sind es), in die sie eingehen, worin sie mit Armreifen aus Gold und mit Perlen geschmückt sein werden, und ihre Kleidung wird darin aus Seide sein." });
                        surah35.VerseList.Add(new Verse { Number = 34, TextBosnian = "Hvaljen neka je Allah!', govorit će, 'koji je od nas tugu odstranio. Gospodar naš je, zaista, Onaj Koji oprašta grijehe i zahvalan je.", TextEnglish = "And they will say, 'Praise to Allāh, who has removed from us [all] sorrow. Indeed, our Lord is Forgiving and Appreciative -", TextGerman = "Und sie werden sagen: „(Alles) Lob gehört Allah, Der den Kummer von uns hinweggenommen hat! Unser Herr ist wahrlich Allvergebend und stets zu Dank bereit." });
                        surah35.VerseList.Add(new Verse { Number = 35, TextBosnian = "Koji nam je, od dobrote Svoje, vječno boravište darovao, gdje nas umor neće doticati i u kome nas klonulost neće snalaziti.'", TextEnglish = "He who has settled us in the home of duration [i.e., Paradise] out of His bounty. There touches us not in it any fatigue, and there touches us not in it weariness [of mind].'", TextGerman = "(Er), Der uns durch Seine Huld in die (ewig) bleibende Wohnstätte versetzt hat, in der uns keine Mühsal widerfährt und in der uns keine Ermüdung befällt." });
                        surah35.VerseList.Add(new Verse { Number = 36, TextBosnian = "A nevjernike čeka Vatra džehennemska, oni neće biti na smrt osuđeni i neće umrijeti, i neće im se patnja u njemu ublažiti - eto, tako ćemo svakog nevjernika kazniti,", TextEnglish = "And for those who disbelieve will be the fire of Hell. [Death] is not decreed for them[1252] so they may die, nor will its torment be lightened for them. Thus do We recompense every ungrateful one.", TextGerman = "Diejenigen aber, die ungläubig sind, für sie wird es das Feuer der Hölle geben, wobei mit ihnen kein Ende gemacht wird, so daß sie sterben, und ihnen auch seine Strafe nicht erleichtert wird. So vergelten Wir jedem sehr Ungläubigen." });
                        surah35.VerseList.Add(new Verse { Number = 37, TextBosnian = "oni će u njemu jaukati: 'Gospodaru naš, izbavi nas, činit ćemo dobra djela, drukčija od onih koja smo činili!' A zar vas nismo ostavili da živite dovoljno dugo da bi onaj koji je trebalo da razmisli imao vremena da razmisli, a bio vam je došao i upozoritelj? Zato iskusite patnju; zulumćarima nema pomoći!", TextEnglish = "And they will cry out therein, 'Our Lord, remove us;[1253] we will do righteousness - other than what we were doing!' But did We not grant you life enough for whoever would remember therein to remember, and the warner had come to you? So taste [the punishment], for there is not for the wrongdoers any helper.", TextGerman = "Und sie schreien darin laut: „Unser Herr, bringe uns heraus, so werden wir rechtschaffen handeln, anders als das, was wir zu tun pflegten. - „Haben Wir euch nicht ein so langes Leben gewährt, daß jeder, der hätte bedenken wollen, darin hätte bedenken können? Und ist nicht der Warner zu euch gekommen? So kostet nun, denn die Ungerechten haben keinen Helfer." });
                        surah35.VerseList.Add(new Verse { Number = 38, TextBosnian = "Allah sigurno zna tajne nebesa i Zemlje; On dobro zna sve što grudi kriju.", TextEnglish = "Indeed, Allāh is Knower of the unseen [aspects] of the heavens and earth. Indeed, He is Knowing of that within the breasts.", TextGerman = "Gewiß, Allah ist der Kenner des Verborgenen der Himmel und der Erde; gewiß, Er weiß über das Innerste der Brüste Bescheid." });
                        surah35.VerseList.Add(new Verse { Number = 39, TextBosnian = "On čini da se smjenjujete na Zemlji; ko ne bude vjerovao, nevjerovanje njegovo je na njegovu štetu; nevjernicima će njihovo nevjerovanje kod Gospodara njihova samo povećati prezir, i nevjernicima će njihovo nevjerovanje povećati propast.", TextEnglish = "It is He who has made you successors upon the earth. And whoever disbelieves - upon him will be [the consequence of] his disbelief. And the disbelief of the disbelievers does not increase them in the sight of their Lord except in hatred; and the disbelief of the disbelievers does not increase them except in loss.", TextGerman = "Er ist es, Der euch zu Nachfolgern auf der Erde gemacht hat. Wer nun ungläubig ist, dessen Unglaube lastet auf ihm. Und den Ungläubigen mehrt ihr Unglaube nur noch die Abscheu bei ihrem Herrn; und den Ungläubigen mehrt ihr Unglaube nur noch den Verlust." });
                        surah35.VerseList.Add(new Verse { Number = 40, TextBosnian = "Reci: 'Kažite vi meni koji su dio Zemlje stvorila božanstva vaša koja, umjesto Allaha, molite, i recite mi imaju li oni u stvaranju nebesa ikakva udjela, ili smo višebošcima Mi dali Knjigu, pa imaju u njoj dokaz za to! Nijedno, već zulumćari jedni drugima samo obmanu obećavaju.", TextEnglish = "Say, 'Have you considered[1254] your 'partners' whom you invoke besides Allāh? Show me what they have created from the earth, or have they partnership [with Him] in the heavens? Or have We given them a book so they are [standing] on evidence therefrom? [No], rather, the wrongdoers do not promise each other except delusion.'[1255]", TextGerman = "Sag: Was meint ihr zu euren Teilhabern, die ihr anstatt Allahs anruft? Zeigt mir, was sie von der Erde erschaffen haben. Oder haben sie etwa einen Anteil an den Himmeln? Oder haben Wir ihnen (sonst) eine Schrift gegeben, so daß sie sich von daher auf einen klaren Beweis stützten? Nein! Vielmehr versprechen die Ungerechten einander nur Trügerisches." });
                        surah35.VerseList.Add(new Verse { Number = 41, TextBosnian = "Allah brani da se nebesa i Zemlja s mjesta svoga pomaknu. A da se pomaknu, niko ih drugi osim Njega ne bi zadržao; On je, zaista, Onaj Koji ne žuri s kaznom i Onaj Koji grijehe oprašta.", TextEnglish = "Indeed, Allāh holds the heavens and the earth, lest they cease. And if they should cease, no one could hold them [in place] after Him. Indeed, He is Forbearing[1256] and Forgiving.", TextGerman = "Allah hält die Himmel und die Erde, daß sie nicht vergehen. Und wenn sie vergehen würden, so könnte niemand nach Ihm sie halten. Gewiß, Er ist Nachsichtig und Allvergebend." });
                        surah35.VerseList.Add(new Verse { Number = 42, TextBosnian = "Oni su se zaklinjali Allahom, najtežom zakletvom, da će se, bolje nego bilo koji narod, držati Pravog puta - samo ako im dođe onaj koji će ih opominjati. I kad im je došao upozoritelj, njegov dolazak samo je povećao odbojnost njihovu;", TextEnglish = "And they swore by Allāh their strongest oaths that if a warner came to them, they would be more guided than [any] one of the [previous] nations. But when a warner came to them, it did not increase them except in aversion", TextGerman = "Und sie haben bei Allah ihren kräftigsten Eid geschworen, sie würden, wenn ein Warner zu ihnen käme, wahrlich eher rechtgeleitet sein als irgendeine der Gemeinschaften. Als dann aber ein Warner zu ihnen kam, mehrte es in ihnen nur (noch) die Abneigung," });
                        surah35.VerseList.Add(new Verse { Number = 43, TextBosnian = "oholeći se na Zemlji smišljali su zlo - a smišljanje zla pogodit će upravo one koji se njime služe. Zar oni mogu očekivati nešto drugo već ono što je zadesilo narode drevne?! U Allahovim zakonima ti nikad nećeš naći promjene; u Allahovim zakonima ti nećeš naći odstupanja.", TextEnglish = "[Due to] arrogance in the land and plotting of evil; but the evil plot does not encompass except its own people. Then do they await except the way [i.e., fate] of the former peoples? But you will never find in the way [i.e., established method] of Allāh any change, and you will never find in the way of Allāh[1257] any alteration.[1258]", TextGerman = "indem sie sich auf der Erde hochmütig verhielten und Ränke böser Taten schmiedeten. Aber die bösen Ränke umschließen nur ihre Urheber. Erwarten sie denn (für sich) etwas anderes als die Gesetzmäßigkeit (, nach) der (an den) Früheren (verfahren wurde)? Du wirst in Allahs Gesetzmäßigkeit keine Änderung finden, und du wirst in Allahs Gesetzmäßigkeit keine Abwandlung finden." });
                        surah35.VerseList.Add(new Verse { Number = 44, TextBosnian = "Zašto oni ne putuju po Zemlji da vide kako su završili oni prije njih, koji su bili jači od njih? Allahu ne može ništa umaći ni na nebesima ni na Zemlji; On, uistinu, sve zna i sve može.", TextEnglish = "Have they not traveled through the land and observed how was the end of those before them? And they were greater than them in power. But Allāh is not to be caused failure [i.e., prevented] by anything in the heavens or on the earth. Indeed, He is ever Knowing and Competent.", TextGerman = "Sind sie denn nicht auf der Erde umhergereist, so daß sie schauen (konnten), wie das Ende derjenigen war, die vor ihnen waren und die eine stärkere Kraft als sie hatten? Und Allah kann sich nichts entziehen - weder in den Himmeln noch auf der Erde, denn Er ist Allwissend und Allmächtig." });
                        surah35.VerseList.Add(new Verse { Number = 45, TextBosnian = "Da Allah kažnjava ljude prema onom što zasluže, ništa živo na površini Zemljinoj ne bi ostavio; ali, On ih ostavlja do roka određenog i kad im rok dođe - pa, Allah dobro vidi robove Svoje.", TextEnglish = "And if Allāh were to impose blame on the people for what they have earned, He would not leave upon it [i.e., the earth] any creature. But He defers them for a specified term. And when their time comes, then indeed Allāh has ever been, of His servants, Seeing.", TextGerman = "Und wenn Allah die Menschen für das belangen wollte, was sie verdient haben, würde Er auf ihrer Oberfläche kein Tier übriglassen. Aber Er stellt sie auf eine festgesetzte Frist zurück. Und wenn dann ihre Frist kommt (-, so wird Er sie belangen), denn gewiß, Allah sieht Seine Diener wohl." });
                        result = surah35;
                        break;
                    #endregion

                    // 
                    #region
                    case 36:
                        Surah surah36 = new Surah(); surah36.Name = Constants.Surah36Name; surah36.Number = 36; surah36.VerseList = new List<Verse>();
                        surah36.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah36.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ja-sin.", TextEnglish = "Yā, Seen.[1260]", TextGerman = "Ya-Sin." });
                        surah36.VerseList.Add(new Verse { Number = 2, TextBosnian = "Tako Mi Kur'ana usavršenog,", TextEnglish = "By the wise[1261] Qur’ān,", TextGerman = "Bei dem weisen Qur’an," });
                        surah36.VerseList.Add(new Verse { Number = 3, TextBosnian = "ti si, uistinu, jedan od poslanih,", TextEnglish = "Indeed you, [O Muḥammad], are from among the messengers,", TextGerman = "du bist wahrlich einer der Gesandten" });
                        surah36.VerseList.Add(new Verse { Number = 4, TextBosnian = "na Pravom putu.", TextEnglish = "On a straight path.", TextGerman = "auf einem geraden Weg." });
                        surah36.VerseList.Add(new Verse { Number = 5, TextBosnian = "On - Kur'an je objava Silnoga i Milostivoga,", TextEnglish = "[This is] a revelation of the Exalted in Might, the Merciful,", TextGerman = "(Er ist) die Offenbarung des Allmächtigen und Barmherzigen," });
                        surah36.VerseList.Add(new Verse { Number = 6, TextBosnian = "da upozoravaš narod čiji preci nisu bili upozoravani, pa je nehajan!", TextEnglish = "That you may warn a people whose forefathers were not warned, so they are unaware.", TextGerman = "damit du ein Volk warnst, dessen Väter nicht gewarnt wurden, so daß sie (gegenüber allem) unachtsam sind." });
                        surah36.VerseList.Add(new Verse { Number = 7, TextBosnian = "O većini njih već se obistinila Riječ - pa oni neće vjerovati.", TextEnglish = "Already the word [i.e., decree] has come into effect upon most of them, so they do not believe.", TextGerman = "Das Wort ist ja gegen die meisten von ihnen unvermeidlich fällig geworden, so glauben sie nicht." });
                        surah36.VerseList.Add(new Verse { Number = 8, TextBosnian = "Mi smo na njihove vratove sindžire 'stavili', a ruke su im na podbratke 'privezane' - pa su oni glava zadignutih,", TextEnglish = "Indeed, We have put shackles on their necks, and they are to their chins, so they are with heads [kept] aloft.", TextGerman = "Gewiß, Wir haben um ihre Hälse Fesseln gelegt. Sie reichen bis zum Kinn, so daß sie den Kopf hochhalten (müssen)." });
                        surah36.VerseList.Add(new Verse { Number = 9, TextBosnian = "ispred i iza njih smo pregradu metnuli i na oči im koprenu stavili - pa oni ne vide,", TextEnglish = "And We have put before them a barrier and behind them a barrier and covered them, so they do not see.", TextGerman = "Und Wir haben vor ihnen eine Sperrmauer errichtet und hinter ihnen eine Sperrmauer und sie so überdeckt, daß sie nicht sehen (können)." });
                        surah36.VerseList.Add(new Verse { Number = 10, TextBosnian = "i njima je svejedno upozoravao ih ti ili ih ne upozoravao, oni neće vjerovati.", TextEnglish = "And it is all the same for them whether you warn them or do not warn them - they will not believe.", TextGerman = "Und gleich ist es in Bezug auf sie, ob du sie warnst oder sie nicht warnst; sie glauben nicht." });
                        surah36.VerseList.Add(new Verse { Number = 11, TextBosnian = "Tvoje upozoravanje koristi samo onome koji Opomenu slijedi i Svemilosnog se boji, i kada ga niko ne vidi; njega obraduj oprostom i nagradom lijepom!", TextEnglish = "You can only warn one who follows the message and fears the Most Merciful unseen. So give him good tidings of forgiveness and noble reward.", TextGerman = "Du kannst nur jemanden warnen, der der Ermahnung folgt und den Allerbarmer im Verborgenen fürchtet. So verkünde ihm Vergebung und trefflichen Lohn." });
                        surah36.VerseList.Add(new Verse { Number = 12, TextBosnian = "Mi, zaista, mrtve oživljavamo i Mi bilježimo ono što su uradili i tragove koje iza sebe ostave; sve smo Mi to u Knjizi jasnoj pobrojali.", TextEnglish = "Indeed, it is We who bring the dead to life and record what they have put forth and what they left behind, and all things We have enumerated in a clear register.", TextGerman = "Gewiß, Wir sind es, Die Wir die Toten wieder lebendig machen. Und Wir schreiben auf, was sie vorausgeschickt haben und (auch) ihre Spuren. Alles haben Wir in einem deutlichen Verzeichnis erfaßt." });
                        surah36.VerseList.Add(new Verse { Number = 13, TextBosnian = "Navedi im kao primjer stanovnike jednog naselja kad su im došli poslanici;", TextEnglish = "And present to them an example: the people of the city, when the messengers came to it -", TextGerman = "Und präge ihnen als Gleichnis die (Geschichte der) Bewohner der Stadt, als die Gesandten zu ihr kamen." });
                        surah36.VerseList.Add(new Verse { Number = 14, TextBosnian = "kad im Mi poslasmo dvojicu, ali ih oni lažnim smatraše, i pojačasmo trećim, pa rekoše: 'Mi smo, zaista, vama poslani!'", TextEnglish = "When We sent to them two but they denied them, so We strengthened [them] with a third, and they said, 'Indeed, we are messengers to you.'", TextGerman = "Als Wir zwei zu ihnen sandten, da bezichtigten sie beide der Lüge, und so brachten Wir durch einen dritten Verstärkung. Sie sagten: „Gewiß, wir sind zu euch gesandt." });
                        surah36.VerseList.Add(new Verse { Number = 15, TextBosnian = "Vi ste ljudi kao i mi', oni odgovoriše, 'Svemilosni nije objavio ništa; vi neistinu govorite!'", TextEnglish = "They said, 'You are not but human beings like us, and the Most Merciful has not revealed a thing. You are only telling lies.'", TextGerman = "Sie sagten: „Ihr seid nur menschliche Wesen wie wir. Nichts (als Offenbarung) hat der Allerbarmer herabgesandt. Ihr lügt nur." });
                        surah36.VerseList.Add(new Verse { Number = 16, TextBosnian = "Gospodar naš zna da smo, doista, vama poslani', rekoše oni,", TextEnglish = "They said, 'Our Lord knows that we are messengers to you,", TextGerman = "Sie sagten: „Unser Herr weiß (es), wir sind wirklich zu euch gesandt." });
                        surah36.VerseList.Add(new Verse { Number = 17, TextBosnian = "i dužni smo samo da jasno obznanimo.'", TextEnglish = "And we are not responsible except for clear notification.'", TextGerman = "Und uns obliegt nur die deutliche Übermittelung (der Botschaft)." });
                        surah36.VerseList.Add(new Verse { Number = 18, TextBosnian = "Oni rekoše: 'Mi slutimo da nam nesreću donosite; ako se ne okanite, kamenovat ćemo vas i sigurno će vas zadesiti bolna patnja od nas.'", TextEnglish = "They said, 'Indeed, we consider you a bad omen. If you do not desist, we will surely stone you, and there will surely touch you, from us, a painful punishment.'", TextGerman = "Sie sagten: „Wir sehen in euch ein böses Vorzeichen. Wenn ihr nicht aufhört, werden wir euch ganz gewiß steinigen, und euch wird ganz gewiß schmerzhafte Strafe von uns widerfahren." });
                        surah36.VerseList.Add(new Verse { Number = 19, TextBosnian = "Vaša zloslutnja je s vama!', rekoše oni. 'Zar zato što ste upozoreni?! Štaviše, vi ste narod koji svaku mjeru prelazi !'", TextEnglish = "They said, 'Your omen [i.e., fate] is with yourselves. Is it[1262] because you were reminded? Rather, you are a transgressing people.'", TextGerman = "Sie sagten: „Euer Vorzeichen ist bei euch (selbst). (Ist es ein böses Vorzeichen,) wenn ihr ermahnt werdet? Aber nein! Ihr seid maßlose Leute." });
                        surah36.VerseList.Add(new Verse { Number = 20, TextBosnian = "I s kraja grada žurno dođe jedan čovjek i reče: 'O narode moj, slijedite one koji su poslani,", TextEnglish = "And there came from the farthest end of the city a man, running. He said, 'O my people, follow the messengers.", TextGerman = "Und es kam vom äußersten Ende der Stadt ein Mann gelaufen. Er sagte: „O mein Volk, folgt den Gesandten." });
                        surah36.VerseList.Add(new Verse { Number = 21, TextBosnian = "slijedite one koji od vas ne traže nikakvu nagradu, a i sami slijede Uputu!", TextEnglish = "Follow those who do not ask of you [any] payment, and they are [rightly] guided.", TextGerman = "Folgt denjenigen, die von euch keinen Lohn verlangen und (selbst) rechtgeleitet sind." });
                        surah36.VerseList.Add(new Verse { Number = 22, TextBosnian = "Zašto da ne budem u ibadetu Onome Koji me je stvorio, a Njemu ćete biti vraćeni?!", TextEnglish = "And why should I not worship He who created me and to whom you will be returned?", TextGerman = "Und warum sollte ich nicht Demjenigen dienen, Der mich erschaffen hat und zu Dem ihr zurückgebracht werdet?" });
                        surah36.VerseList.Add(new Verse { Number = 23, TextBosnian = "Zar da prihvatim druge bogove mimo Njega?! Ako Svemilosni hoće da me snađe neko zlo, njihovo posredovanje neće mi biti ni od kakve koristi i oni me neće moći spasiti,", TextEnglish = "Should I take other than Him [false] deities [while], if the Most Merciful intends for me some adversity, their intercession will not avail me at all, nor can they save me?", TextGerman = "Soll ich mir etwa anstatt Seiner (andere) Götter nehmen? Wenn der Allerbarmer für mich Unheil will, nützt mir ihre Fürsprache nichts, und sie (können) mich nicht erretten." });
                        surah36.VerseList.Add(new Verse { Number = 24, TextBosnian = "a sigurno bih tada u očitoj zabludi bio;", TextEnglish = "Indeed, I would then be in manifest error.", TextGerman = "Ich würde mich dann wahrlich in deutlichem Irrtum befinden." });
                        surah36.VerseList.Add(new Verse { Number = 25, TextBosnian = "ja vjerujem u Gospodara vašeg, čujte me!'", TextEnglish = "Indeed, I have believed in your Lord, so listen to me.'", TextGerman = "Gewiß, ich glaube an euren Herrn, so hört auf mich." });
                        surah36.VerseList.Add(new Verse { Number = 26, TextBosnian = "I bi rečeno: 'Uđi u Džennet!' - a on reče: 'Kad bi narod moj samo znao", TextEnglish = "It was said, 'Enter Paradise.'[1263] He said, 'I wish my people could know", TextGerman = "Es wurde (zu ihm) gesagt: Geh in den (Paradies)garten ein. Er sagte: O wüßte doch mein Volk davon," });
                        surah36.VerseList.Add(new Verse { Number = 27, TextBosnian = "zašto mi je Gospodar moj oprostio i učinio da budem među počastvovanim!'", TextEnglish = "Of how my Lord has forgiven me and placed me among the honored.'", TextGerman = "daß mein Herr mir vergeben und mich zu den Geehrten hat gehören lassen!" });
                        surah36.VerseList.Add(new Verse { Number = 28, TextBosnian = "I protiv naroda njegova, poslije njega, Mi nismo vojsku s neba poslali, niti smo je inače slali,", TextEnglish = "And We did not send down upon his people after him any soldiers from the heaven, nor would We have done so.", TextGerman = "Wir sandten gegen sein Volk nach ihm keine Heerschar vom Himmel herab; Wir brauchten auch sonst nichts (gegen sie) herabzusenden." });
                        surah36.VerseList.Add(new Verse { Number = 29, TextBosnian = "samo je bio jedan užasan krik, i oni odjednom utrnuti - pomrli.", TextEnglish = "It was not but one shout,[1264] and immediately they were extinguished.", TextGerman = "Es war nur ein einziger Schrei (nötig), da waren sie sogleich ausgelöscht." });
                        surah36.VerseList.Add(new Verse { Number = 30, TextBosnian = "O ljudske žalosti! Nijedan poslanik im nije došao a da ga nisu izrugivali.", TextEnglish = "How regretful for the servants. There did not come to them any messenger except that they used to ridicule him.", TextGerman = "O schade um die Diener! Es kommt kein Gesandter zu ihnen, ohne daß sie sich über ihn lustig machen würden." });
                        surah36.VerseList.Add(new Verse { Number = 31, TextBosnian = "Kako oni ne uvide koliko smo prije njih naroda uništili i da im se oni neće vratiti,", TextEnglish = "Have they not considered how many generations We destroyed before them - that they to them[1265] will not return?", TextGerman = "Sehen sie denn nicht, wie viele Geschlechter Wir vor ihnen vernichtet haben, und daß sie zu ihnen nicht zurückkehren?" });
                        surah36.VerseList.Add(new Verse { Number = 32, TextBosnian = "a svi oni bit će zajedno pred Nas dovedeni.", TextEnglish = "And indeed, all of them will yet be brought present before Us.", TextGerman = "Und sie werden alle insgesamt bei Uns vorgeführt werden." });
                        surah36.VerseList.Add(new Verse { Number = 33, TextBosnian = "Znak im je mrtva zemlja: Mi joj život dajemo i činimo da iz nje niče žito pa ga oni jedu;", TextEnglish = "And a sign for them is the dead earth. We have brought it to life and brought forth from it grain, and from it they eat.", TextGerman = "Und ein Zeichen ist für sie die tote Erde. Wir machen sie lebendig und bringen aus ihr Körner hervor, so daß sie davon essen (können)." });
                        surah36.VerseList.Add(new Verse { Number = 34, TextBosnian = "Mi smo po njoj stvorili bašče, palmike i vinograde, i učinili da iz nje izvori izviru,", TextEnglish = "And We placed therein gardens of palm trees and grapevines and caused to burst forth therefrom some springs -", TextGerman = "Und Wir haben auf ihr Gärten mit Palmen und Rebstöcken geschaffen und auf ihr Quellen hervorströmen lassen," });
                        surah36.VerseList.Add(new Verse { Number = 35, TextBosnian = "da oni jedu plodove njihove - a nisu to ruke njihove privrijedile - pa zašto neće da budu zahvalni?!", TextEnglish = "That they may eat of His fruit.[1266] And their hands have not produced it,[1267] so will they not be grateful?", TextGerman = "damit sie von seinen Früchten essen (können). Nicht ihre Hände haben sie geschaffen. Wollen sie denn nicht dankbar sein?" });
                        surah36.VerseList.Add(new Verse { Number = 36, TextBosnian = "Neka je slavljen i Uzvišen Onaj Koji je u svemu parove stvorio: u onome što iz zemlje niče, u njima samima, i u onome što oni ne znaju!", TextEnglish = "Exalted is He who created all pairs[1268] - from what the earth grows and from themselves and from that which they do not know.", TextGerman = "Preis sei Demjenigen, Der die Paare alle erschaffen hat von dem, was die Erde wachsen läßt, von ihnen selbst und von dem, was sie nicht wissen!" });
                        surah36.VerseList.Add(new Verse { Number = 37, TextBosnian = "I noć im je znak. Odvajamo od nje dan i oni odjednom u mraku.", TextEnglish = "And a sign for them is the night. We remove[1269] from it the [light of] day, so they are [left] in darkness.", TextGerman = "Und ein Zeichen ist für sie die Nacht. Wir ziehen von ihr den Tag weg, und sogleich befinden sie sich im Finstern." });
                        surah36.VerseList.Add(new Verse { Number = 38, TextBosnian = "I Sunce se kreće do svoga staništa, to je odredba Silnoga i Sveznajućeg.", TextEnglish = "And the sun runs [on course] toward its stopping point. That is the determination of the Exalted in Might, the Knowing.", TextGerman = "Und die Sonne läuft zu einem für sie bestimmten Aufenthaltsort. Das ist die Anordnung des Allmächtigen und Allwissenden." });
                        surah36.VerseList.Add(new Verse { Number = 39, TextBosnian = "I Mjesecu smo odredili položaje; i on se uvijek ponovo vraća kao stari savijeni palmin prut.", TextEnglish = "And the moon - We have determined for it phases, until it returns [appearing] like the old date stalk.", TextGerman = "Und dem Mond haben Wir das rechte Maß in Himmelspunkten festgesetzt, bis er wieder wie ein alter Dattelrispenstiel wird." });
                        surah36.VerseList.Add(new Verse { Number = 40, TextBosnian = "Niti Sunce može Mjesec dostići, niti noć dan preteći, svi oni u svemiru plove.", TextEnglish = "It is not allowable [i.e., possible] for the sun to reach the moon, nor does the night overtake the day, but each, in an orbit, is swimming.", TextGerman = "Weder ziemt es der Sonne, den Mond einzuholen, noch wird die Nacht dem Tag zuvorkommen; alle laufen in einer (jeweils eigenen) Umlaufbahn." });
                        surah36.VerseList.Add(new Verse { Number = 41, TextBosnian = "Znak im je i to što smo pretke njihove u lađi krcatoj prevezli", TextEnglish = "And a sign for them is that We carried their forefathers[1270] in a laden ship.", TextGerman = "Und ein Zeichen ist es für sie, daß Wir ihre Nachkommenschaft auf dem vollbeladenen Schiff trugen." });
                        surah36.VerseList.Add(new Verse { Number = 42, TextBosnian = "i što smo za njih, slične njoj, stvorili, ono na čemu se voze.", TextEnglish = "And We created for them from the likes of it that which they ride.", TextGerman = "Und Wir haben ihnen etwas gleicher Art erschaffen, was sie besteigen." });
                        surah36.VerseList.Add(new Verse { Number = 43, TextBosnian = "I ako želimo, Mi ih potopima, i niko im u pomoć neće priteći, niti će biti izbavljeni", TextEnglish = "And if We should will, We could drown them; then no one responding to a cry would there be for them, nor would they be saved", TextGerman = "Und wenn Wir wollen, lassen Wir sie ertrinken; dann gibt es niemanden, zu dem sie um Hilfe schreien könnten, und sie werden nicht gerettet," });
                        surah36.VerseList.Add(new Verse { Number = 44, TextBosnian = "osim Našom milošću, i da bi do roka određenog uživali.", TextEnglish = "Except as a mercy from Us and provision for a time.", TextGerman = "außer aus Barmherzigkeit von Uns und als Nießbrauch auf Zeit." });
                        surah36.VerseList.Add(new Verse { Number = 45, TextBosnian = "A kad im se rekne: 'Bojte se onoga što se prije vas dogodilo i onoga što vas čeka da biste pomilovani bili .", TextEnglish = "But when it is said to them, 'Beware of what is before you and what is behind you;[1271] perhaps you will receive mercy...'[1272]", TextGerman = "Und wenn zu ihnen gesagt wird: Hütet euch vor dem, was vor euch, und dem, was hinter euch ist, auf daß ihr Erbarmen finden möget! (..., wenden sie sich ab.)" });
                        surah36.VerseList.Add(new Verse { Number = 46, TextBosnian = "I ne dođe im nijedan znak od znakova Gospodara njihova kojem oni leđa ne okrenuše.", TextEnglish = "And no sign comes to them from the signs of their Lord except that they are from it turning away.", TextGerman = "Kein Zeichen von den Zeichen ihres Herrn kommt zu ihnen, ohne daß sie sich davon abwenden." });
                        surah36.VerseList.Add(new Verse { Number = 47, TextBosnian = "A kad im se kaže: 'Udjeljujte od onoga što vam Allah daje'', onda nevjernici govore onima koji vjeruju: 'Zar da hranimo onoga koga je Allah, da je htio, mogao nahraniti? Vi ste, uistinu, u jasnoj zabludi!'", TextEnglish = "And when it is said to them, 'Spend from that which Allāh has provided for you,' those who disbelieve say to those who believe, 'Should we feed one whom, if Allāh had willed, He would have fed? You are not but in clear error.'", TextGerman = "Und wenn zu ihnen gesagt wird: Gebt von dem aus, womit Allah euch versorgt hat, sagen diejenigen, die ungläubig sind, zu denjenigen, die glauben: Sollen wir jemanden ernähren, den Allah, wenn Er wollte, ernähren würde? Ihr befindet euch nur in deutlichem Irrtum." });
                        surah36.VerseList.Add(new Verse { Number = 48, TextBosnian = "I govore: 'Kad će već jednom to obećanje, ako istinu govorite.", TextEnglish = "And they say, 'When is this promise, if you should be truthful?'", TextGerman = "Und sie sagen: „Wann wird dieses Versprechen eintreten, wenn ihr wahrhaftig seid?" });
                        surah36.VerseList.Add(new Verse { Number = 49, TextBosnian = "A ne čekaju drugo do jedan strašni zvuk koji će ih, dok se budu jedni s drugima prepirali, obuzeti,", TextEnglish = "They do not await except one blast[1273] which will seize them while they are disputing.", TextGerman = "Sie haben nur einen einzigen Schrei zu erwarten, der sie ergreift, während sie noch miteinander streiten." });
                        surah36.VerseList.Add(new Verse { Number = 50, TextBosnian = "pa neće moći ništa oporučiti, niti se porodici svojoj vratiti.", TextEnglish = "And they will not be able [to give] any instruction, nor to their people can they return.", TextGerman = "Da werden sie kein Vermächtnis (mehr) hinterlassen können und auch nicht zu ihren Angehörigen zurückkehren." });
                        surah36.VerseList.Add(new Verse { Number = 51, TextBosnian = "I puhnut će se u rog, a oni iz grobova prema Gospodaru svome hrle.", TextEnglish = "And the Horn will be blown;[1274] and at once from the graves to their Lord they will hasten.", TextGerman = "Es wird ins Horn geblasen werden, und da laufen sie sogleich schnell aus den Gräbern zu ihrem Herrn herbei." });
                        surah36.VerseList.Add(new Verse { Number = 52, TextBosnian = "Reći će: 'Teško nama! Ko nas iz naših grobova oživi 'Ovo je ono što je obećao Svemilosni, a poslanici su istinu govorili!'", TextEnglish = "They will say, 'O woe to us! Who has raised us up from our sleeping place?' [The reply will be], 'This is what the Most Merciful had promised, and the messengers told the truth.'", TextGerman = "Sie sagen: O wehe uns! Wer hat uns von unserer Schlafstätte auferweckt? Das ist, was der Allerbarmer versprochen hat, und die Gesandten haben die Wahrheit gesagt." });
                        surah36.VerseList.Add(new Verse { Number = 53, TextBosnian = "Bit će to samo jedan strašni zvuk, a oni pred Nas svi dovedeni.", TextEnglish = "It will not be but one blast, and at once they are all brought present before Us.", TextGerman = "Es wird nur ein einziger Schrei (nötig) sein, da werden sie sogleich alle bei Uns vorgeführt." });
                        surah36.VerseList.Add(new Verse { Number = 54, TextBosnian = "Danas se nikome neće zulum učiniti i vi ćete, samo prema onom kako ste radili, nagrađeni biti.", TextEnglish = "So today [i.e., the Day of Judgement] no soul will be wronged at all, and you will not be recompensed except for what you used to do.", TextGerman = "Heute wird keiner Seele irgendein Unrecht zugefügt. Und euch wird nur das vergolten, was ihr zu tun pflegtet." });
                        surah36.VerseList.Add(new Verse { Number = 55, TextBosnian = "Doista će stanovnici Dženneta taj dan biti zauzeti, u nasladama uživajući,", TextEnglish = "Indeed the companions of Paradise, that Day, will be amused in [joyful] occupation -", TextGerman = "Gewiß, die Insassen des (Paradies)gartens sind heute in Beschäftigung und Vergnügen." });
                        surah36.VerseList.Add(new Verse { Number = 56, TextBosnian = "oni i supruge njihove bit će u hladovini na ukrašenim divanima naslonjeni,", TextEnglish = "They and their spouses - in shade, reclining on adorned couches.", TextGerman = "Sie und ihre Gattinnen befinden sich im Schatten und lehnen sich auf überdachte Liegen." });
                        surah36.VerseList.Add(new Verse { Number = 57, TextBosnian = "u njemu će imati voća, i ono što budu željeli.", TextEnglish = "For them therein is fruit, and for them is whatever they request [or wish]", TextGerman = "Sie haben darin Früchte, und sie haben, was sie erbeten." });
                        surah36.VerseList.Add(new Verse { Number = 58, TextBosnian = "Selam vama!' - bit će riječi Gospodara Milostivog.", TextEnglish = "[And] 'Peace,' a word from a Merciful Lord.", TextGerman = "Friede!, als (Gruß)wort von einem barmherzigen Herrn." });
                        surah36.VerseList.Add(new Verse { Number = 59, TextBosnian = "A vi, o prestupnici, danas se odvojite!", TextEnglish = "[Then He will say], 'But stand apart today, you criminals.[1275]", TextGerman = "Sondert euch heute (von den Gottesfürchtigen) ab, ihr Übeltäter." });
                        surah36.VerseList.Add(new Verse { Number = 60, TextBosnian = "O sinovi Ademovi, zar vam nisam naredio: Ne obožavajte šejtana, on vam je neprijatelj otvoreni,", TextEnglish = "Did I not enjoin upon you, O children of Adam, that you not worship Satan - [for] indeed, he is to you a clear enemy -", TextGerman = "Habe Ich euch, o Kinder Adams, nicht als Verpflichtung auferlegt, daß ihr nicht dem Satan dienen sollt - gewiß er ist euch ein deutlicher Feind -," });
                        surah36.VerseList.Add(new Verse { Number = 61, TextBosnian = "već ibadet činite Meni; to je Put pravi.", TextEnglish = "And that you worship [only] Me? This is a straight path.", TextGerman = "und daß ihr Mir dienen sollt?, - das ist ein gerader Weg!" });
                        surah36.VerseList.Add(new Verse { Number = 62, TextBosnian = "On je mnoge od vas u zabludu odveo, kako niste razumjeli?!", TextEnglish = "And he had already led astray from among you much of creation, so did you not use reason?", TextGerman = "Er hat ja doch viele Geschöpfe von euch in die Irre geführt. Hattet ihr denn nicht begriffen?" });
                        surah36.VerseList.Add(new Verse { Number = 63, TextBosnian = "Ovo je Džehennem koji vam je obećan,", TextEnglish = "This is the Hellfire which you were promised.", TextGerman = "Das ist die Hölle, die euch stets angedroht wurde." });
                        surah36.VerseList.Add(new Verse { Number = 64, TextBosnian = "uđite danas u njega i vrelinu njegovu trpite zato što ste poricali!'", TextEnglish = "[Enter to] burn therein today for what you used to deny.'[1276]", TextGerman = "Ihr sollt ihr heute ausgesetzt sein dafür, daß ihr ungläubig wart." });
                        surah36.VerseList.Add(new Verse { Number = 65, TextBosnian = "Danas ćemo im usta zapečatiti, ruke njihove će Nam govoriti, a noge njihove će o onom što su radili svjedočiti.", TextEnglish = "That Day, We will seal over their mouths, and their hands will speak to Us, and their feet will testify about what they used to earn.", TextGerman = "Heute versiegeln Wir ihnen ihre Münder. Ihre Hände werden zu Uns sprechen und ihre Füße Zeugnis ablegen über das, was sie erworben haben." });
                        surah36.VerseList.Add(new Verse { Number = 66, TextBosnian = "Da smo htjeli, oči bi njihove poravnali - slijepim ih učinili, pa kad bi na put pošli, kako bi vidjeli?!", TextEnglish = "And if We willed, We could have obliterated their eyes, and they would race to [find] the path, and how could they see?[1277]", TextGerman = "Und wenn Wir wollten, würden Wir ihre Augen auslöschen. Und sie laufen dann zum Weg um die Wette, aber wie können sie da sehen?" });
                        surah36.VerseList.Add(new Verse { Number = 67, TextBosnian = "A da smo htjeli, mogli smo ih na mjestu na kome su u nešto pretvoriti, pa ne bi mogli ni naprijed ni nazad ići.", TextEnglish = "And if We willed, We could have deformed them, [paralyzing them] in their places so they would not be able to proceed, nor could they return.[1278]", TextGerman = "Wenn Wir wollten, würden Wir sie auf ihrer Stelle verwandeln, so daß sie nicht mehr vorangehen können und auch nicht zurückkehren." });
                        surah36.VerseList.Add(new Verse { Number = 68, TextBosnian = "I Onome kome dug život damo, Mi mu izgled nagore izmijenimo. Zar oni ne razumiju?", TextEnglish = "And he to whom We grant long life We reverse in creation;[1279] so will they not understand?", TextGerman = "Wem Wir ein langes Leben gewähren, den lassen Wir in seiner Gestalt eine Kehrtwendung machen. Begreifen sie denn nicht?" });
                        surah36.VerseList.Add(new Verse { Number = 69, TextBosnian = "Mi Poslanika nismo pjesništvu učili, to mu ne priliči. Ovo je samo pouka - Kur'an jasni,", TextEnglish = "And We did not give him [i.e., Prophet Muḥammad (ﷺ)] knowledge of poetry, nor is it befitting for him. It is not but a message and a clear Qur’ān", TextGerman = "Und Wir haben ihn nicht das Dichten gelehrt, und es ziemt ihm (auch) nicht. Das ist doch nur eine Ermahnung und ein deutlicher Qur’an," });
                        surah36.VerseList.Add(new Verse { Number = 70, TextBosnian = "da upozorava onoga ko živ je, i da se Riječ o kazni za nevjernike obistini.", TextEnglish = "To warn whoever is alive[1280] and justify the word [i.e., decree] against the disbelievers.", TextGerman = "damit er warne, wer (da) lebt, und das Wort gegen die Ungläubigen unvermeidlich fällig werde." });
                        surah36.VerseList.Add(new Verse { Number = 71, TextBosnian = "Kako oni ne vide da Mi sami, od onoga što su Naše ruke učinile, njima stoku stvaramo i da oni njome raspolažu kao vlasnici!", TextEnglish = "Do they not see that We have created for them from what Our hands[1281] have made, grazing livestock, and [then] they are their owners?", TextGerman = "Sehen sie denn nicht, daß Wir ihnen unter dem, was Unsere Hände gemacht haben, Vieh erschaffen haben, über das sie verfügen?" });
                        surah36.VerseList.Add(new Verse { Number = 72, TextBosnian = "i da smo im dali da se njome služe - na nekima jašu, a nekima se hrane,", TextEnglish = "And We have tamed them for them, so some of them they ride, and some of them they eat.", TextGerman = "Und Wir haben es ihnen fügsam gemacht, so daß sie davon Reittiere haben und davon essen (können)." });
                        surah36.VerseList.Add(new Verse { Number = 73, TextBosnian = "i drugih koristi od nje imaju, i pića, pa zašto nisu zahvalni?!", TextEnglish = "And for them therein are [other] benefits and drinks, so will they not be grateful?", TextGerman = "Und sie haben an ihm (allerlei) Nutzen und (etwas) zu trinken. Wollen sie denn nicht dankbar sein?" });
                        surah36.VerseList.Add(new Verse { Number = 74, TextBosnian = "I pored Allaha druge kao bogove prihvataju ne bi li potpomognuti bili;", TextEnglish = "But they have taken besides Allāh [false] deities that perhaps they would be helped.", TextGerman = "Und sie haben sich anstatt Allahs Götter genommen, auf daß sie Hilfe finden mögen." });
                        surah36.VerseList.Add(new Verse { Number = 75, TextBosnian = "oni im, međutim, neće moći pomoći, a oni su im vojska poslušna.", TextEnglish = "They are not able to help them, and they [themselves] are for them soldiers in attendance.[1282]", TextGerman = "Sie können ihnen jedoch keine Hilfe gewähren, obwohl sie ihnen als eine (dienstbereite) Heerschar vorgeführt werden." });
                        surah36.VerseList.Add(new Verse { Number = 76, TextBosnian = "I nek te ne žaloste riječi njihove; Mi, doista, znamo i ono što oni kriju i ono što pokazuju.", TextEnglish = "So let not their speech grieve you. Indeed, We know what they conceal and what they declare.", TextGerman = "Ihre Worte sollen dich nicht traurig machen. Wir wissen ja, was sie geheimhalten und was sie offenlegen." });
                        surah36.VerseList.Add(new Verse { Number = 77, TextBosnian = "Kako čovjek ne vidi da ga Mi od kapi sjemena stvaramo, i opet otvoreno osporava,", TextEnglish = "Does man not consider that We created him from a [mere] sperm-drop - then at once[1283] he is a clear adversary?", TextGerman = "Sieht denn der Mensch nicht, daß Wir ihn aus einem Samentropfen erschaffen haben, und doch ist er sogleich ein deutlicher Widersacher." });
                        surah36.VerseList.Add(new Verse { Number = 78, TextBosnian = "i Nama navodi primjer, a zaboravlja kako je stvoren, i govori: 'Ko će oživiti kosti, kad budu truhle?'", TextEnglish = "And he presents for Us an example[1284] and forgets his [own] creation. He says, 'Who will give life to bones while they are disintegrated?'", TextGerman = "Er führt Uns ein Beispiel an und vergißt seine (eigene) Erschaffung. Er sagt: Wer macht die Knochen wieder lebendig, wenn sie zerfallen sind?" });
                        surah36.VerseList.Add(new Verse { Number = 79, TextBosnian = "Reci: 'Oživit će ih Onaj Koji ih je prvi put stvorio; On dobro zna sve što je stvorio,", TextEnglish = "Say, 'He will give them life who produced them the first time; and He is, of all creation, Knowing.'", TextGerman = "Sag: Wieder lebendig macht sie Derjenige, Der sie das erste Mal hat entstehen lassen. Und Er weiß über jede Schöpfung Bescheid," });
                        surah36.VerseList.Add(new Verse { Number = 80, TextBosnian = "Onaj Koji vam iz zelenog drveća vatru stvara i vi njome potpaljujete.'", TextEnglish = "[It is] He who made for you from the green tree, fire, and then from it you ignite.", TextGerman = "(Er,) Der euch aus grünen Bäumen Feuer gemacht hat, so daß ihr sogleich damit anzünden könnt." });
                        surah36.VerseList.Add(new Verse { Number = 81, TextBosnian = "Zar Onaj Koji je stvorio nebesa i Zemlju nije kadar stvoriti njima slične? Jeste, On je Stvoritelj, Sveznajući.", TextEnglish = "Is not He who created the heavens and the earth Able to create the likes of them? Yes, [it is so]; and He is the Knowing Creator.", TextGerman = "Hat nicht Derjenige, Der die Himmel und die Erde erschaffen hat, (auch) die Macht, ihresgleichen zu erschaffen? Ja doch! Und Er ist der Allerschaffer und Allwissende." });
                        surah36.VerseList.Add(new Verse { Number = 82, TextBosnian = "A Njegova naredba, kada nešto hoće, jeste da samo za to rekne: 'Budi!' - i ono bude.", TextEnglish = "His command is only when He intends a thing that He says to it, 'Be,' and it is.", TextGerman = "Sein Befehl, wenn Er etwas will, ist, dazu nur zu sagen: ,Sei!’, und so ist es." });
                        surah36.VerseList.Add(new Verse { Number = 83, TextBosnian = "Pa neka je uzvišen Onaj u Čijoj je ruci vlast nad svim, i Njemu ćete vraćeni biti!", TextEnglish = "So exalted is He in whose hand is the realm of all things, and to Him you will be returned.", TextGerman = "So sei Preis Demjenigen, in Dessen Hand die Herrschaftsgewalt über alles ist und zu Dem ihr zurückgebracht werdet!" });
                        result = surah36;
                        break;
                    #endregion

                    // 
                    #region
                    case 37:
                        Surah surah37 = new Surah(); surah37.Name = Constants.Surah37Name; surah37.Number = 37; surah37.VerseList = new List<Verse>();
                        surah37.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah37.VerseList.Add(new Verse { Number = 1, TextBosnian = "Tako mi onih u redove poređanih,", TextEnglish = "By those [angels] lined up in rows", TextGerman = "Bei den sich reihenweise Reihenden," });
                        surah37.VerseList.Add(new Verse { Number = 2, TextBosnian = "i onih koji oblake tjeraju,", TextEnglish = "And those who drive [the clouds]", TextGerman = "dann den einen Schelteschrei Ausstoßenden," });
                        surah37.VerseList.Add(new Verse { Number = 3, TextBosnian = "i onih koji Opomenu čitaju,", TextEnglish = "And those who recite the message,[1286]", TextGerman = "dann den eine Ermahnung Verlesenden!" });
                        surah37.VerseList.Add(new Verse { Number = 4, TextBosnian = "vaš Bog je, uistinu, jedan,", TextEnglish = "Indeed, your God is One,", TextGerman = "Euer Gott ist wahrlich ein Einziger," });
                        surah37.VerseList.Add(new Verse { Number = 5, TextBosnian = "Gospodar nebesa i Zemlje i onoga što je između njih, i Gospodar istoka.", TextEnglish = "Lord of the heavens and the earth and that between them and Lord of the sunrises.[1287]", TextGerman = "der Herr der Himmel und der Erde und dessen, was dazwischen ist, und der Herr der Osten." });
                        surah37.VerseList.Add(new Verse { Number = 6, TextBosnian = "Mi smo najbliže nebo ukrasom- zvijezdama ukrasili,", TextEnglish = "Indeed, We have adorned the nearest heaven with an adornment of stars", TextGerman = "Wir haben den untersten Himmel mit einem Schmuck geziert: mit den Himmelskörpern’;" });
                        surah37.VerseList.Add(new Verse { Number = 7, TextBosnian = "i čuvamo ga od svakoga šejtana prkosnoga", TextEnglish = "And as protection against every rebellious devil", TextGerman = "und als Schutz vor jedem rebellischen Satan." });
                        surah37.VerseList.Add(new Verse { Number = 8, TextBosnian = "da ne prisluškuju meleke - skup najviši, a sa svih strana bivaju gađani,", TextEnglish = "[So] they may not listen to the exalted assembly [of angels] and are pelted from every side,[1288]", TextGerman = "So können sie der höchsten führenden Schar (der Engel) nicht zuhören, und sie werden von allen Seiten beworfen," });
                        surah37.VerseList.Add(new Verse { Number = 9, TextBosnian = "da bi bili otjerani: njih čeka patnja neprekidna,", TextEnglish = "Repelled; and for them is a constant punishment,", TextGerman = "damit sie weggetrieben werden - und für sie wird es immerwährende Strafe geben -," });
                        surah37.VerseList.Add(new Verse { Number = 10, TextBosnian = "a onoga koji šta ugrabi- stigne buktinja svijetleća.", TextEnglish = "Except one who snatches [some words] by theft, but they are pursued by a burning flame, piercing [in brightness].", TextGerman = "außer jemandem, der rasch etwas aufschnappt und den dann ein durchbohrend heller Leuchtkörper verfolgt." });
                        surah37.VerseList.Add(new Verse { Number = 11, TextBosnian = "Upitaj ih da li je teže njih stvoriti ili sve ostalo što smo stvorili? Njih smo, zaista, stvorili od ljepljive gline.", TextEnglish = "Then inquire of them, [O Muḥammad], 'Are they a stronger [or more difficult] creation or those [others] We have created?' Indeed, We created them [i.e., men] from sticky clay.", TextGerman = "Frage sie doch um ihre Meinung: Ist es etwa schwerer, sie zu erschaffen als wen Wir (sonst) erschaffen haben? Wir haben sie ja aus haftendem Lehm erschaffen." });
                        surah37.VerseList.Add(new Verse { Number = 12, TextBosnian = "Međutim, ti se čudiš, a oni se izruguju,", TextEnglish = "But you wonder, while they mock,", TextGerman = "Aber nein! Du wunderst dich, während sie spotten." });
                        surah37.VerseList.Add(new Verse { Number = 13, TextBosnian = "a kad ih se opominje, oni opomenu ne prihvataju,", TextEnglish = "And when they are reminded, they remember not.", TextGerman = "Und wenn sie ermahnt werden, gedenken sie nicht." });
                        surah37.VerseList.Add(new Verse { Number = 14, TextBosnian = "i kad znak vide, oni se podsmijavaju", TextEnglish = "And when they see a sign, they ridicule.", TextGerman = "Und wenn sie ein Zeichen sehen, spotten sie darüber." });
                        surah37.VerseList.Add(new Verse { Number = 15, TextBosnian = "i govore: 'Ovo nije ništa drugo do prava čarolija!", TextEnglish = "And say, 'This is not but obvious magic.", TextGerman = "Und sie sagen: Das ist nur deutliche Zauberei." });
                        surah37.VerseList.Add(new Verse { Number = 16, TextBosnian = "Zar kad poumiremo i kad kosti i zemlja postanemo, zar ćemo mi, zaista, biti oživijeni", TextEnglish = "When we have died and become dust and bones, are we indeed to be resurrected?", TextGerman = "Wenn wir gestorben und zu Erde und Knochen geworden sind, sollen wir denn dann wirklich auferweckt werden?" });
                        surah37.VerseList.Add(new Verse { Number = 17, TextBosnian = "i naši preci davni?'", TextEnglish = "And our forefathers [as well]?'", TextGerman = "Und auch unsere Vorväter?" });
                        surah37.VerseList.Add(new Verse { Number = 18, TextBosnian = "Reci: 'Da, a bit ćete i poniženi!'", TextEnglish = "Say, 'Yes, and you will be [rendered] contemptible.'", TextGerman = "Sag: Ja, und ihr werdet euch demütig verhalten." });
                        surah37.VerseList.Add(new Verse { Number = 19, TextBosnian = "Bit će to samo naredba jedna, i oni će odjednom gledati", TextEnglish = "It will be only one shout, and at once they will be observing.", TextGerman = "Es wird nur ein einziger erschreckender Schrei sein, da schauen sie sogleich hin." });
                        surah37.VerseList.Add(new Verse { Number = 20, TextBosnian = "i reći će: 'Teško nama, ovo je - Dan suda!'", TextEnglish = "They will say, 'O woe to us! This is the Day of Recompense.'", TextGerman = "Und sie werden sagen: O wehe uns! Das ist der Tag des Gerichts!" });
                        surah37.VerseList.Add(new Verse { Number = 21, TextBosnian = "Da, ovo je Dan razlučivanja u koji vi niste vjerovali!", TextEnglish = "[They will be told], 'This is the Day of Judgement which you used to deny.'", TextGerman = "Das ist der Tag der Entscheidung, den ihr für Lüge zu erklären pflegtet." });
                        surah37.VerseList.Add(new Verse { Number = 22, TextBosnian = "Sakupite one koji su zulum učinili i njima slične i one koje su obožavali", TextEnglish = "[The angels will be ordered], 'Gather those who committed wrong, their kinds,[1289] and what they used to worship", TextGerman = "- (Zu den Engeln des Gerichts wird gesagt:) Versammelt (nun) diejenigen, die Unrecht getan haben, ihre Gattinnen und das, dem sie dienten" });
                        surah37.VerseList.Add(new Verse { Number = 23, TextBosnian = "mimo Allaha, i uputite ih putem koji u Džehennem vodi", TextEnglish = "Other than Allāh, and guide them to the path of Hellfire", TextGerman = "anstatt Allahs. Dann leitet sie zum Weg des Höllenbrandes" });
                        surah37.VerseList.Add(new Verse { Number = 24, TextBosnian = "i zaustavite ih, oni će, zaista, biti pitani:", TextEnglish = "And stop them; indeed, they are to be questioned.'", TextGerman = "und stellt sie auf, denn sie werden befragt werden." });
                        surah37.VerseList.Add(new Verse { Number = 25, TextBosnian = "Šta vam je, zašto jedni drugima ne pomognete?'", TextEnglish = "[They will be asked], 'What is [wrong] with you? Why do you not help each other?'", TextGerman = "- (Und es wird zu ihnen gesagt:) Was ist mit euch, daß ihr einander nicht unterstützt?" });
                        surah37.VerseList.Add(new Verse { Number = 26, TextBosnian = "Ali, toga Dana oni će se sasvim prepustiti.", TextEnglish = "But they, that Day, are in surrender.", TextGerman = "Nein! Vielmehr ergeben sie sich heute." });
                        surah37.VerseList.Add(new Verse { Number = 27, TextBosnian = "i pristupit će jedni drugima raspitujući se.", TextEnglish = "And they will approach one another asking [i.e., blaming] each other.", TextGerman = "Und sie wenden sich einander zu und fragen sich gegenseitig." });
                        surah37.VerseList.Add(new Verse { Number = 28, TextBosnian = "Reći će: 'Zaista ste nam vi s pozicije moći prilazili.'", TextEnglish = "They will say, 'Indeed, you used to come at us from the right.'[1290]", TextGerman = "Sie sagen: Ihr kamt doch zu uns (stets) von rechts her’." });
                        surah37.VerseList.Add(new Verse { Number = 29, TextBosnian = "Nismo', odgovorit će, 'nego vi niste bili vjernici.", TextEnglish = "They [i.e., the oppressors] will say, 'Rather, you [yourselves] were not believers,", TextGerman = "Sie sagen: Aber nein! Ihr wart ja nicht gläubig." });
                        surah37.VerseList.Add(new Verse { Number = 30, TextBosnian = "I mi nikakva dokaza nad vama nismo imali, nego ste obijestan narod bili", TextEnglish = "And we had over you no authority, but you were a transgressing people.", TextGerman = "Wir aber hatten keine Macht über euch, sondern vielmehr wart ihr Leute, die das Maß (an Frevel) überschreiten." });
                        surah37.VerseList.Add(new Verse { Number = 31, TextBosnian = "i riječ Gospodara našeg da ćemo, doista, kaznu iskusiti - na nama se ispunila,", TextEnglish = "So the word [i.e., decree] of our Lord has come into effect upon us; indeed, we will taste [punishment].", TextGerman = "So ist das Wort unseres Herrn gegen uns unvermeidlich fällig geworden. Wir werden bestimmt (die Strafe) kosten." });
                        surah37.VerseList.Add(new Verse { Number = 32, TextBosnian = "a u zabludu smo vas pozivali, a i sami smo, zaista, u zabludi bili.'", TextEnglish = "And we led you to deviation; indeed, we were deviators.'", TextGerman = "Und so haben wir euch in Verirrung fallen lassen, denn wir waren ja selbst in Verirrung gefallen." });
                        surah37.VerseList.Add(new Verse { Number = 33, TextBosnian = "I oni će taj Dan zajedno u patnji biti,", TextEnglish = "So indeed they, that Day, will be sharing in the punishment.", TextGerman = "Gewiß denn, an jenem Tag werden sie an der(selben) Strafe teilhaben." });
                        surah37.VerseList.Add(new Verse { Number = 34, TextBosnian = "a Mi ćemo, zaista, tako s prestupnicima postupiti.", TextEnglish = "Indeed, that is how We deal with the criminals.", TextGerman = "Gewiß, so verfahren Wir mit den Übeltätern," });
                        surah37.VerseList.Add(new Verse { Number = 35, TextBosnian = "Kad im je govoreno: 'Samo je Allah Bog!', oni su se, doista, oholili", TextEnglish = "Indeed they, when it was said to them, 'There is no deity but Allāh,' were arrogant", TextGerman = "denn sie pflegten, wenn zu ihnen gesagt wurde: Es gibt keinen Gott außer Allah, sich hochmütig zu verhalten," });
                        surah37.VerseList.Add(new Verse { Number = 36, TextBosnian = "i govorili: 'Zar da napustimo božanstva naša zbog jednog ludog pjesnika?!'", TextEnglish = "And were saying, 'Are we to leave our gods for a mad poet?'", TextGerman = "und sagten: Sollen wir denn wahrlich unsere Götter verlassen wegen eines besessenen Dichters?" });
                        surah37.VerseList.Add(new Verse { Number = 37, TextBosnian = "Naprotiv, Istinu je donio i iskrenost poslanika potvrdio.", TextEnglish = "Rather, he [i.e., the Prophet (ﷺ)] has come with the truth and confirmed the [previous] messengers.", TextGerman = "Nein! Vielmehr ist er mit der Wahrheit gekommen und hat die Gesandten bestätigt." });
                        surah37.VerseList.Add(new Verse { Number = 38, TextBosnian = "I Vi ćete sigurno bolnu patnju iskusiti,", TextEnglish = "Indeed, you [disbelievers] will be tasters of the painful punishment,", TextGerman = "Ihr werdet bestimmt die schmerzhafte Strafe kosten," });
                        surah37.VerseList.Add(new Verse { Number = 39, TextBosnian = "i samo ćete, srazmjerno onom što ste radili, kažnjeni biti.", TextEnglish = "And you will not be recompensed except for what you used to do -", TextGerman = "und euch wird nur das vergolten, was ihr zu tun pflegtet," });
                        surah37.VerseList.Add(new Verse { Number = 40, TextBosnian = "Ali ne Allahovi iskreni robovi!'", TextEnglish = "But not the chosen servants of Allāh.", TextGerman = "außer Allahs auserlesenen Dienern." });
                        surah37.VerseList.Add(new Verse { Number = 41, TextBosnian = "Oni će baš opskrbu poznatu imati;", TextEnglish = "Those will have a provision determined -", TextGerman = "Für diese wird es eine festgesetzte Versorgung geben:" });
                        surah37.VerseList.Add(new Verse { Number = 42, TextBosnian = "razno voće, a bit će i počašćeni", TextEnglish = "Fruits;[1291] and they will be honored", TextGerman = "Früchte, und sie werden geehrt" });
                        surah37.VerseList.Add(new Verse { Number = 43, TextBosnian = "u džennetskim baščama naslada,", TextEnglish = "In gardens of pleasure", TextGerman = "in den Gärten der Wonne." });
                        surah37.VerseList.Add(new Verse { Number = 44, TextBosnian = "na divanima, jedni naspram drugih, sjedit će;", TextEnglish = "On thrones facing one another.", TextGerman = "auf Liegen (ruhend), einander gegenüber," });
                        surah37.VerseList.Add(new Verse { Number = 45, TextBosnian = "bit će posluženi pićem - iz izvora koji stalno teče -", TextEnglish = "There will be circulated among them a cup [of wine] from a flowing spring,", TextGerman = "wobei ihnen ein Becher (voll) Quellwasser herumgereicht wird," });
                        surah37.VerseList.Add(new Verse { Number = 46, TextBosnian = "bijelim i prijatnim onima koji budu pili,", TextEnglish = "White and delicious to the drinkers;", TextGerman = "weiß, köstlich für diejenigen, die (daraus) trinken." });
                        surah37.VerseList.Add(new Verse { Number = 47, TextBosnian = "od njega neće muke imati i zbog njega se neće pamet gubiti.", TextEnglish = "No bad effect is there in it, nor from it will they be intoxicated.", TextGerman = "Darin steckt nichts Beeinträchtigendes, und dadurch werden sie nicht benommen." });
                        surah37.VerseList.Add(new Verse { Number = 48, TextBosnian = "Pored njih bit će one koje će preda se gledati, očiju prekrasnih,", TextEnglish = "And with them will be women limiting [their] glances,[1292] with large, [beautiful] eyes,", TextGerman = "Und bei ihnen sind solche (weiblichen Wesen), die ihre Blicke zurückhalten, mit schönen, großen Augen," });
                        surah37.VerseList.Add(new Verse { Number = 49, TextBosnian = "kao da su one jaja zaštićena.", TextEnglish = "As if they were [delicate] eggs, well-protected.", TextGerman = "als ob sie wohlverwahrte Eier wären." });
                        surah37.VerseList.Add(new Verse { Number = 50, TextBosnian = "I pristupit će jedni drugima raspitujući se,", TextEnglish = "And they will approach one another, inquiring of each other.", TextGerman = "Sie wenden sich einander zu und fragen sich gegenseitig." });
                        surah37.VerseList.Add(new Verse { Number = 51, TextBosnian = "i jedan od njih će reći: 'Imao sam druga jednog,", TextEnglish = "A speaker among them will say, 'Indeed, I had a companion [on earth].", TextGerman = "Einer von ihnen sagt: Gewiß, ich hatte einen Gesellen," });
                        surah37.VerseList.Add(new Verse { Number = 52, TextBosnian = "koji je govorio: 'Zar i ti vjeruješ", TextEnglish = "Who would say, 'Are you indeed of those who believe", TextGerman = "der da sagte: .Gehörst du wirklich zu denjenigen, die es für wahr halten?" });
                        surah37.VerseList.Add(new Verse { Number = 53, TextBosnian = "da ćemo, kad poumiremo i zemlja i kosti postanemo, doista, račun polagati?''", TextEnglish = "That when we have died and become dust and bones, we will indeed be recompensed?''", TextGerman = "Wenn wir gestorben und zu Erde und Knochen geworden sind, sollen wir denn dann wirklich vor Gericht gestellt werden’?" });
                        surah37.VerseList.Add(new Verse { Number = 54, TextBosnian = "Hoćete li da pogledate?', reći će.", TextEnglish = "He will say,[1293] 'Would you [care to] look?'", TextGerman = "Er sagt: Wollt ihr denn hinschauen?" });
                        surah37.VerseList.Add(new Verse { Number = 55, TextBosnian = "I on će pogledati, i toga usred Džehennema ugledati.", TextEnglish = "And he will look and see him[1294] in the midst of the Hellfire.", TextGerman = "Er schaut selbst hin und sieht ihn mitten im Höllenbrand." });
                        surah37.VerseList.Add(new Verse { Number = 56, TextBosnian = "Allaha mi', reći će, 'zamalo me nisi upropastio.", TextEnglish = "He will say, 'By Allāh, you almost ruined me.", TextGerman = "Er sagt: Bei Allah, beinahe hättest du mich fürwahr ins Verderben gestürzt." });
                        surah37.VerseList.Add(new Verse { Number = 57, TextBosnian = "Da nije bilo milosti Gospodara moga, i ja bih bio među privedenim.'", TextEnglish = "If not for the favor of my Lord, I would have been of those brought in [to Hell].", TextGerman = "Und ohne die Gnade meines Herrn würde ich nun wahrlich zu den Vorgeführten gehören." });
                        surah37.VerseList.Add(new Verse { Number = 58, TextBosnian = "A mi, je li, više nećemo umirati?", TextEnglish = "Then, are we not to die", TextGerman = "Werden wir tatsächlich nicht mehr sterben," });
                        surah37.VerseList.Add(new Verse { Number = 59, TextBosnian = "Jednom smo umrli - više nećemo biti patnji podvrgnuti,", TextEnglish = "Except for our first death, and we will not be punished?'", TextGerman = "außer unserem ersten Tod, und gehören wir tatsächlich nicht zu den Bestraften?" });
                        surah37.VerseList.Add(new Verse { Number = 60, TextBosnian = "ovo je, zaista, uspjeh veliki!'", TextEnglish = "Indeed, this is the great attainment.", TextGerman = "Das ist doch wahrlich der großartige Erfolg!" });
                        surah37.VerseList.Add(new Verse { Number = 61, TextBosnian = "Za ovako nešto neka se trude trudbenici!", TextEnglish = "For the like of this let the workers [on earth] work.", TextGerman = "Für ein Solches sollen diejenigen, die handeln, (ihre Werke) tun." });
                        surah37.VerseList.Add(new Verse { Number = 62, TextBosnian = "A da li je to bolje kao ugošćenje ili drvo zekkum?", TextEnglish = "Is that [i.e., Paradise] a better accommodation or the tree of zaqqūm?", TextGerman = "Ist dies als gastliche Aufnahme besser oder der Zaqqum-Baum?" });
                        surah37.VerseList.Add(new Verse { Number = 63, TextBosnian = "Mi smo njega zulumćarima kušnjom učinili.", TextEnglish = "Indeed, We have made it a torment for the wrongdoers.", TextGerman = "Den haben Wir zu einer Versuchung für die Ungerechten gemacht." });
                        surah37.VerseList.Add(new Verse { Number = 64, TextBosnian = "To je drvo koje usred Džehennema raste,", TextEnglish = "Indeed, it is a tree issuing from the bottom of the Hellfire,", TextGerman = "Er ist ein Baum, der im Grund des Höllenbrandes hervorkommt," });
                        surah37.VerseList.Add(new Verse { Number = 65, TextBosnian = "plod mu je poput glava šejtanskih.", TextEnglish = "Its emerging fruit as if it was heads of the devils.", TextGerman = "dessen Fruchtscheide so ist, als wären es Köpfe von Satanen." });
                        surah37.VerseList.Add(new Verse { Number = 66, TextBosnian = "Oni će se s njega hraniti i trbuhe će svoje s njega puniti,", TextEnglish = "And indeed, they will eat from it and fill with it their bellies.", TextGerman = "Sie werden wahrlich davon essen und werden (sich) so davon die Bäuche füllen." });
                        surah37.VerseList.Add(new Verse { Number = 67, TextBosnian = "zatim će im se na to mješavina ključale vode dodati,", TextEnglish = "Then indeed, they will have after it a mixture of scalding water.", TextGerman = "Hierauf erhalten sie fürwahr eine Mischung von heißem Wasser." });
                        surah37.VerseList.Add(new Verse { Number = 68, TextBosnian = "a potom će se, sigurno, opet u Džehennem vratiti.", TextEnglish = "Then indeed, their return will be to the Hellfire.", TextGerman = "Hierauf kehren sie fürwahr zum Höllenbrand zurück." });
                        surah37.VerseList.Add(new Verse { Number = 69, TextBosnian = "Oni su očeve svoje u zabludi zatekli,", TextEnglish = "Indeed they found their fathers astray.", TextGerman = "Sie haben ja ihre Väter im Irrtum vorgefunden," });
                        surah37.VerseList.Add(new Verse { Number = 70, TextBosnian = "pa su i oni stopama njihovim žurili.", TextEnglish = "So they hastened [to follow] in their footsteps.", TextGerman = "und so sind sie auf ihren Spuren eilig getrieben worden." });
                        surah37.VerseList.Add(new Verse { Number = 71, TextBosnian = "A i prije njih su narodi drevni većinom u zabludi bili,", TextEnglish = "And there had already strayed before them most of the former peoples,", TextGerman = "Vor ihnen sind bereits die meisten der Früheren abgeirrt," });
                        surah37.VerseList.Add(new Verse { Number = 72, TextBosnian = "iako smo im slali one koji su ih upozoravali;", TextEnglish = "And We had already sent among them warners.", TextGerman = "und Wir sandten ja zu ihnen Überbringer von Warnungen." });
                        surah37.VerseList.Add(new Verse { Number = 73, TextBosnian = "zato pogledaj kakav je bio kraj onih koji su bili upozoravani,", TextEnglish = "Then look how was the end of those who were warned -", TextGerman = "So schau, wie das Ende der Gewarnten war," });
                        surah37.VerseList.Add(new Verse { Number = 74, TextBosnian = "tako nije bilo samo s Allahovim robovima iskrenim.", TextEnglish = "But not the chosen servants of Allāh.", TextGerman = "außer Allahs auserlesenen Dienern." });
                        surah37.VerseList.Add(new Verse { Number = 75, TextBosnian = "A kada Nas je Nuh zovnuo, Mi smo se lijepo odazvali:", TextEnglish = "And Noah had certainly called Us, and [We are] the best of responders.", TextGerman = "Und Nuh rief Uns ja bereits zu - welch trefflicher Erhörer sind Wir fürwahr!" });
                        surah37.VerseList.Add(new Verse { Number = 76, TextBosnian = "njega i porodicu njegovu nevolje teške smo spasili,", TextEnglish = "And We saved him and his family from the great affliction.", TextGerman = "Und Wir erretteten ihn und seine Angehörigen aus der großen Trübsal" });
                        surah37.VerseList.Add(new Verse { Number = 77, TextBosnian = "i samo potomke njegove u životu ostavili,", TextEnglish = "And We made his descendants those remaining [on the earth]", TextGerman = "und machten seine Nachkommenschaft zu denjenigen, die Fortbestand haben." });
                        surah37.VerseList.Add(new Verse { Number = 78, TextBosnian = "i u naraštajima kasnijim mu spomen sačuvali:", TextEnglish = "And left for him [favorable mention] among later generations:", TextGerman = "Und Wir ließen für ihn (den Ruf) unter den späteren (Geschlechtern lauten):" });
                        surah37.VerseList.Add(new Verse { Number = 79, TextBosnian = "Selam Nuhu od svjetova svih!", TextEnglish = "Peace upon Noah among the worlds.'", TextGerman = "Friede sei auf Nuh unter den Weltenbewohnern!" });
                        surah37.VerseList.Add(new Verse { Number = 80, TextBosnian = "Eto, tako Mi nagrađujemo one koji dobra djela čine,", TextEnglish = "Indeed, We thus reward the doers of good.", TextGerman = "Gewiß, so vergelten Wir den Rechtschaffenen." });
                        surah37.VerseList.Add(new Verse { Number = 81, TextBosnian = "on je bio rob Naš, vjernik,", TextEnglish = "Indeed, he was of Our believing servants.", TextGerman = "Er gehört ja zu Unseren gläubigen Dienern." });
                        surah37.VerseList.Add(new Verse { Number = 82, TextBosnian = "a ostale smo poslije potopili.", TextEnglish = "Then We drowned the others [i.e., disbelievers].", TextGerman = "Hierauf ließen Wir die anderen ertrinken." });
                        surah37.VerseList.Add(new Verse { Number = 83, TextBosnian = "Iste vjere kao i on bio je i Ibrahim,", TextEnglish = "And indeed, among his kind was Abraham,", TextGerman = "Zu seiner Gemeinde gehörte fürwahr Ibrahim." });
                        surah37.VerseList.Add(new Verse { Number = 84, TextBosnian = "kad je Gospodaru svome čista srca došao,", TextEnglish = "When he came to his Lord with a sound heart", TextGerman = "Als er zu seinem Herrn mit heilem Herzen kam." });
                        surah37.VerseList.Add(new Verse { Number = 85, TextBosnian = "kad je ocu svome i narodu svome rekao: 'Šta to vi obožavate?", TextEnglish = "[And] when he said to his father and his people, 'What do you worship?", TextGerman = "Als er zu seinem Vater und seinem Volk sagte: Wem dient ihr da?" });
                        surah37.VerseList.Add(new Verse { Number = 86, TextBosnian = "Zar lažna božanstva umjesto Allaha hoćete?", TextEnglish = "Is it falsehood [as] gods other than Allāh you desire?", TextGerman = "Wollt ihr außer Allah falsche Götter haben?" });
                        surah37.VerseList.Add(new Verse { Number = 87, TextBosnian = "I šta o Gospodaru svjetova mislite?'", TextEnglish = "Then what is your thought about the Lord of the worlds?'", TextGerman = "Welche Meinung habt ihr denn vom Herrn der Weltenbewohner?" });
                        surah37.VerseList.Add(new Verse { Number = 88, TextBosnian = "I on baci pogled na zvijezde,", TextEnglish = "And he cast a look at the stars.", TextGerman = "Dann warf er einen Blick zu den Sternen" });
                        surah37.VerseList.Add(new Verse { Number = 89, TextBosnian = "pa reče: 'Zbilja, ja sam bolestan';", TextEnglish = "And said, 'Indeed, I am [about to be] ill.'", TextGerman = "und sagte: Gewiß, ich bin krank." });
                        surah37.VerseList.Add(new Verse { Number = 90, TextBosnian = "oni od njega, leđa mu okrenuvši, odoše,", TextEnglish = "So they turned away from him, departing.", TextGerman = "Da kehrten sie ihm den Rücken." });
                        surah37.VerseList.Add(new Verse { Number = 91, TextBosnian = "a on se kumirima njihovim prikrade, pa reče: 'Zašto ne jedete?", TextEnglish = "Then he turned to their gods and said, 'Do you not eat?[1295]", TextGerman = "Nun schlich er sich zu ihren Göttern und sagte: Wollt ihr nicht essen?" });
                        surah37.VerseList.Add(new Verse { Number = 92, TextBosnian = "Šta vam je te ne govorite?'", TextEnglish = "What is [wrong] with you that you do not speak?'.", TextGerman = "Was ist mit euch, daß ihr nicht redet?" });
                        surah37.VerseList.Add(new Verse { Number = 93, TextBosnian = "I krišom im priđe desnom rukom ih udarajući,", TextEnglish = "And he turned upon them a blow with [his] right hand.", TextGerman = "Und er wandte sich gegen sie und schlug auf sie mit der Rechten ein." });
                        surah37.VerseList.Add(new Verse { Number = 94, TextBosnian = "pa mu narod trkom dođe.", TextEnglish = "Then they [i.e., the people] came toward him, hastening.", TextGerman = "Da kamen sie auf ihn zugeeilt." });
                        surah37.VerseList.Add(new Verse { Number = 95, TextBosnian = "Kako možete obožavati one koje sami klešete?'', upita,", TextEnglish = "He said, 'Do you worship that which you [yourselves] carve,", TextGerman = "Er sagte: Wie könnt ihr denn dem dienen, was ihr selbst aushaut," });
                        surah37.VerseList.Add(new Verse { Number = 96, TextBosnian = "kad Allah stvara i vas i ono što napravite", TextEnglish = "While Allāh created you and that which you do?'", TextGerman = "wo doch Allah euch und das, was ihr tut, erschaffen hat?" });
                        surah37.VerseList.Add(new Verse { Number = 97, TextBosnian = "Pripremite za njega lomaču'', povikaše, 'pa ga u vatru bacite!'", TextEnglish = "They said, 'Construct for him a structure [i.e., furnace] and throw him into the burning fire.'", TextGerman = "Sie sagten: Errichtet für ihn einen Bau und werft ihn in den Feuersbrand." });
                        surah37.VerseList.Add(new Verse { Number = 98, TextBosnian = "I htjedoše mu zamku načiniti, ali Mi njih učinismo najponiženijim.", TextEnglish = "And they intended for him a plan [i.e., harm], but We made them the most debased.", TextGerman = "Sie wollten mit einer List gegen ihn vorgehen, doch da machten Wir sie zu den Unterlegenen." });
                        surah37.VerseList.Add(new Verse { Number = 99, TextBosnian = "Idem onamo gdje mi je Gospodar moj naredio', reče, 'On će mene kuda treba uputiti.", TextEnglish = "And [then] he said, 'Indeed, I will go to [where I am ordered by] my Lord; He will guide me.", TextGerman = "Er sagte: Gewiß, ich gehe zu meinem Herrn; Er wird mich rechtleiten." });
                        surah37.VerseList.Add(new Verse { Number = 100, TextBosnian = "Gospodaru moj, daruj mi porod čestit!'", TextEnglish = "My Lord, grant me [a child] from among the righteous.'", TextGerman = "Mein Herr, schenke mir einen von den Rechtschaffenen." });
                        surah37.VerseList.Add(new Verse { Number = 101, TextBosnian = "I Mi smo ga obradovali dječakom obazrivim.", TextEnglish = "So We gave him good tidings of a forbearing boy.", TextGerman = "Da verkündeten Wir ihm einen nachsichtigen Jungen." });
                        surah37.VerseList.Add(new Verse { Number = 102, TextBosnian = "I kad on odraste toliko da mu poče u poslu pomagati, Ibrahim reče: 'O sinko moj, u snu sam vidio da treba da te zakoljem, pa šta ti misliš 'O oče moj', reče, 'onako kako ti se naređuje postupi; vidjet ćeš, ako Bog da, da ću strpljiv biti.'", TextEnglish = "And when he reached with him [the age of] exertion,[1296] he said, 'O my son, indeed I have seen in a dream that I [must] sacrifice you, so see what you think.' He said, 'O my father, do as you are commanded. You will find me, if Allāh wills, of the steadfast.'", TextGerman = "Als dieser das Alter erreichte, daß er mit ihm laufen konnte, sagte er: O mein lieber Sohn, ich sehe im Schlaf, daß ich dich schlachte. Schau jetzt, was du (dazu) meinst. Er sagte: O mein lieber Vater, tu, was dir befohlen wird. Du wirst mich, wenn Allah will, als einen der Standhaften finden." });
                        surah37.VerseList.Add(new Verse { Number = 103, TextBosnian = "I kad njih dvojica poslušaše, i on ga čelom prema zemlji položi,", TextEnglish = "And when they had both submitted[1297] and he put him down upon his forehead,", TextGerman = "Als sie sich beide ergeben gezeigt hatten und er ihn auf die Seite der Stirn niedergeworfen hatte," });
                        surah37.VerseList.Add(new Verse { Number = 104, TextBosnian = "Mi ga zovnusmo: 'O Ibrahime,", TextEnglish = "We called to him, 'O Abraham,", TextGerman = "riefen Wir ihm zu: O Ibrahim," });
                        surah37.VerseList.Add(new Verse { Number = 105, TextBosnian = "ti si san svoj odlučio obistiniti - a Mi ovako nagrađujemo one koji dobra djela čine.'", TextEnglish = "You have fulfilled the vision.' Indeed, We thus reward the doers of good.", TextGerman = "du hast das Traumgesicht bereits wahr gemacht. Gewiß, so vergelten Wir den Gutes Tuenden." });
                        surah37.VerseList.Add(new Verse { Number = 106, TextBosnian = "To je, zaista, bilo očito iskušenje!", TextEnglish = "Indeed, this was the clear trial.", TextGerman = "Das ist wahrlich die deutliche Prüfung." });
                        surah37.VerseList.Add(new Verse { Number = 107, TextBosnian = "Kurbanom velikim ga iskupismo,", TextEnglish = "And We ransomed him with a great sacrifice,[1298]", TextGerman = "Und Wir lösten ihn mit einem großartigen Schlachtopfer aus." });
                        surah37.VerseList.Add(new Verse { Number = 108, TextBosnian = "i u naraštajima kasnijim mu spomen sačuvasmo:", TextEnglish = "And We left for him [favorable mention] among later generations:", TextGerman = "Und Wir ließen für ihn (den Ruf) unter den späteren (Geschlechtern lauten):" });
                        surah37.VerseList.Add(new Verse { Number = 109, TextBosnian = "Selam neka je Ibrahimu!'", TextEnglish = "Peace upon Abraham.'", TextGerman = "Friede sei auf Ibrahim!" });
                        surah37.VerseList.Add(new Verse { Number = 110, TextBosnian = "Eto, tako Mi nagrađujemo one koji dobra djela čine,", TextEnglish = "Indeed, We thus reward the doers of good.", TextGerman = "So vergelten Wir den Gutes Tuenden." });
                        surah37.VerseList.Add(new Verse { Number = 111, TextBosnian = "a on je, doista, bio rob Naš, vjernik,", TextEnglish = "Indeed, he was of Our believing servants.", TextGerman = "Er gehört ja zu Unseren gläubigen Dienern." });
                        surah37.VerseList.Add(new Verse { Number = 112, TextBosnian = "i obradovali smo ga Ishakorn, vjerovjesnikom i čovjekom dobrim,", TextEnglish = "And We gave him good tidings of Isaac, a prophet from among the righteous.[1299]", TextGerman = "Und Wir verkündeten ihm Ishaq als einen Propheten von den Rechtschaffenen." });
                        surah37.VerseList.Add(new Verse { Number = 113, TextBosnian = "i blagosloviti smo i njega i Ishaka; a među potomcima njihovim ima vjernika i onih koji sebi očito nasilje čine.", TextEnglish = "And We blessed him and Isaac. But among their descendants is the doer of good and the clearly unjust to himself [i.e., sinner].", TextGerman = "Und Wir segneten ihn und Ishaq. Unter ihrer Nachkommenschaft gibt es manche, die Gutes tun, und manche, die sich selbst offenkundig Unrecht zufügen." });
                        surah37.VerseList.Add(new Verse { Number = 114, TextBosnian = "I Musau i Harunu smo blagodati dali,", TextEnglish = "And We did certainly confer favor upon Moses and Aaron.", TextGerman = "Und Wir erwiesen bereits Musa und Harun eine Wohltat" });
                        surah37.VerseList.Add(new Verse { Number = 115, TextBosnian = "pa i njih i narod njihov nevolje velike spasili,", TextEnglish = "And We saved them and their people from the great affliction,", TextGerman = "und erretteten sie beide und ihr Volk aus der großen Trübsal." });
                        surah37.VerseList.Add(new Verse { Number = 116, TextBosnian = "i pomogli im i oni bijahu pobjednici,", TextEnglish = "And We supported them so it was they who overcame.", TextGerman = "Und Wir halfen ihnen, da waren sie es, die Sieger wurden." });
                        surah37.VerseList.Add(new Verse { Number = 117, TextBosnian = "i Knjigu im jasnu dali,", TextEnglish = "And We gave them the explicit Scripture [i.e., the Torah],", TextGerman = "Und Wir gaben ihnen beiden die verdeutlichende Schrift" });
                        surah37.VerseList.Add(new Verse { Number = 118, TextBosnian = "i obojicu na Pravi put uputili,", TextEnglish = "And We guided them on the straight path.", TextGerman = "und leiteten sie den geraden Weg." });
                        surah37.VerseList.Add(new Verse { Number = 119, TextBosnian = "i u naraštajima kasnijim im spomen sačuvali:", TextEnglish = "And We left for them [favorable mention] among later generations:", TextGerman = "Und Wir ließen für beide (den Ruf) unter den späteren (Geschlechtern lauten):" });
                        surah37.VerseList.Add(new Verse { Number = 120, TextBosnian = "Neka je selam Musau i Harunu!", TextEnglish = "Peace upon Moses and Aaron.'", TextGerman = "Friede sei auf Musa und Harun!" });
                        surah37.VerseList.Add(new Verse { Number = 121, TextBosnian = "Eto, tako Mi nagrađujemo one koji dobra djela čine,", TextEnglish = "Indeed, We thus reward the doers of good.", TextGerman = "Gewiß, so vergelten Wir den Rechtschaffenen." });
                        surah37.VerseList.Add(new Verse { Number = 122, TextBosnian = "a njih dvojica su, uistinu, bili robovi Naši, vjernici.", TextEnglish = "Indeed, they were of Our believing servants.", TextGerman = "Sie beide gehören ja zu Unseren gläubigen Dienern." });
                        surah37.VerseList.Add(new Verse { Number = 123, TextBosnian = "I Il'jas je bio poslanik.", TextEnglish = "And indeed, Elias was from among the messengers,", TextGerman = "Auch Ilyas gehörte wahrlich zu den Gesandten." });
                        surah37.VerseList.Add(new Verse { Number = 124, TextBosnian = "Kad on reče narodu svome: 'Zar se uščuvati nećete?!", TextEnglish = "When he said to his people, 'Will you not fear Allāh?", TextGerman = "Als er zu seinem Volk sagte: Wollt ihr nicht gottesfürchtig sein?" });
                        surah37.VerseList.Add(new Verse { Number = 125, TextBosnian = "Zar se Ba'lu molite, a najboljeg Stvoritelja ostavljate,", TextEnglish = "Do you call upon Baʿl[1300] and leave the best of creators -", TextGerman = "Wie könnt ihr Bacl anrufen und den besten Schöpfer verlassen," });
                        surah37.VerseList.Add(new Verse { Number = 126, TextBosnian = "Allaha, Gospodara svoga i Gospodara vaših predaka drevnih?'", TextEnglish = "Allāh, your Lord and the Lord of your first forefathers?'", TextGerman = "Allah, euren Herrn und den Herrn eurer Vorväter?" });
                        surah37.VerseList.Add(new Verse { Number = 127, TextBosnian = "Oni ga lašcem nazvaše i zato će, sigurno, svi oni u Vatru biti privedeni,", TextEnglish = "And they denied him, so indeed, they will be brought [for punishment],", TextGerman = "Da bezichtigten sie ihn der Lüge. So werden sie bestimmt vorgeführt werden," });
                        surah37.VerseList.Add(new Verse { Number = 128, TextBosnian = "samo neće oni Allahovi robovi koji su Mu bili odani.", TextEnglish = "Except the chosen servants of Allāh.", TextGerman = "außer Allahs auserlesenen Dienern." });
                        surah37.VerseList.Add(new Verse { Number = 129, TextBosnian = "I sačuvasmo mu spomen u naraštajima kasnijim:", TextEnglish = "And We left for him [favorable mention] among later generations:", TextGerman = "Und Wir ließen für ihn (den Ruf) unter den späteren (Geschlechtern lauten):" });
                        surah37.VerseList.Add(new Verse { Number = 130, TextBosnian = "Neka je selam Il'jasinu!'", TextEnglish = "Peace upon Elias.'[1301]", TextGerman = "Friede sei auf Ilyas!" });
                        surah37.VerseList.Add(new Verse { Number = 131, TextBosnian = "Eto, tako Mi nagrađujemo one koji dobra djela čine,", TextEnglish = "Indeed, We thus reward the doers of good.", TextGerman = "Gewiß, so vergelten Wir den Rechtschaffenen." });
                        surah37.VerseList.Add(new Verse { Number = 132, TextBosnian = "a on je bio rob Naš, vjernik.", TextEnglish = "Indeed, he was of Our believing servants.", TextGerman = "Er gehört ja zu Unseren gläubigen Dienern." });
                        surah37.VerseList.Add(new Verse { Number = 133, TextBosnian = "I Lut je bio poslanik.", TextEnglish = "And indeed, Lot was among the messengers.", TextGerman = "Auch Lut gehörte wahrlich zu den Gesandten." });
                        surah37.VerseList.Add(new Verse { Number = 134, TextBosnian = "Kad Mi njega i njegovu porodicu cijelu spasismo,", TextEnglish = "[So mention] when We saved him and his family, all,", TextGerman = "Als Wir ihn und seine Angehörigen allesamt erretteten," });
                        surah37.VerseList.Add(new Verse { Number = 135, TextBosnian = "osim starice, ona je bila sa onima koji su nastradali,", TextEnglish = "Except an old woman [i.e., his wife] among those who remained [with the evildoers].", TextGerman = "außer einer alten Frau unter denjenigen, die zurückblieben." });
                        surah37.VerseList.Add(new Verse { Number = 136, TextBosnian = "a ostale smo uništili,", TextEnglish = "Then We destroyed the others.", TextGerman = "Hierauf vertilgten Wir die anderen." });
                        surah37.VerseList.Add(new Verse { Number = 137, TextBosnian = "i vi pored nastambi njihovih prolazite i danju", TextEnglish = "And indeed, you pass by them in the morning", TextGerman = "Ihr kommt ja an ihnen vorbei, bei Tagesanbruch" });
                        surah37.VerseList.Add(new Verse { Number = 138, TextBosnian = "i noću, pa zašto se ne urazumite?", TextEnglish = "And at night. Then will you not use reason?", TextGerman = "und bei Nacht. Begreift ihr denn nicht?’" });
                        surah37.VerseList.Add(new Verse { Number = 139, TextBosnian = "I Junus je bio poslanik.", TextEnglish = "And indeed, Jonah was among the messengers.", TextGerman = "Auch Yunus gehörte wahrlich zu den Gesandten." });
                        surah37.VerseList.Add(new Verse { Number = 140, TextBosnian = "Kad on odbježe na lađu", TextEnglish = "[Mention] when he ran away to the laden ship.", TextGerman = "Als er zum vollbeladenen Schiff davonlief." });
                        surah37.VerseList.Add(new Verse { Number = 141, TextBosnian = "i s njima baci kocku i kocka na njega pade,", TextEnglish = "And he drew lots[1302] and was among the losers.", TextGerman = "Er warf Lose und wurde einer der Unterlegenen." });
                        surah37.VerseList.Add(new Verse { Number = 142, TextBosnian = "i riba ga proguta, a bio je zaslužio prijekor,", TextEnglish = "Then the fish swallowed him, while he was blameworthy.[1303]", TextGerman = "Da verschlang ihn der (große) Fisch, während er sich Tadel zugezogen hatte." });
                        surah37.VerseList.Add(new Verse { Number = 143, TextBosnian = "i da nije bio jedan od onih koji Allaha slave i veličaju,", TextEnglish = "And had he not been of those who exalt Allāh,", TextGerman = "Und hätte er nicht zu denjenigen gehört, die (Allah) preisen," });
                        surah37.VerseList.Add(new Verse { Number = 144, TextBosnian = "sigurno bi ostao u utrobi njenoj do Dana kad će svi biti oživljeni,", TextEnglish = "He would have remained inside its belly until the Day they are resurrected.[1304]", TextGerman = "hätte er wahrlich in seinem Bauch verweilt bis zu dem Tag, an dem sie auferweckt werden." });
                        surah37.VerseList.Add(new Verse { Number = 145, TextBosnian = "i Mi ga izbacismo na jedno pusto mjesto, a on je bio nemoćan,", TextEnglish = "But We threw him onto the open shore while he was ill.", TextGerman = "Da warfen Wir ihn auf das kahle Land -, und dabei war er krank." });
                        surah37.VerseList.Add(new Verse { Number = 146, TextBosnian = "i učinismo da iznad njega izraste vriježa jedne tikve,", TextEnglish = "And We caused to grow over him a gourd vine.[1305]", TextGerman = "Und Wir ließen eine Kürbisstaude über ihm wachsen." });
                        surah37.VerseList.Add(new Verse { Number = 147, TextBosnian = "i poslasmo ga stotini hiljada ljudi, i više,", TextEnglish = "And We sent him[1306] to [his people of] a hundred thousand or more.", TextGerman = "Und Wir sandten ihn zu Hunderttausend oder sogar noch mehr." });
                        surah37.VerseList.Add(new Verse { Number = 148, TextBosnian = "i oni povjerovaše, i njima dadosmo da do roka određenog uživaju.", TextEnglish = "And they believed, so We gave them enjoyment [of life] for a time.", TextGerman = "Da glaubten sie, und so gewährten Wir ihnen Nießbrauch auf Zeit." });
                        surah37.VerseList.Add(new Verse { Number = 149, TextBosnian = "A upitaj ih: 'Zar su za Gospodara tvoga - kćeri, a za njih sinovi", TextEnglish = "So inquire of them, [O Muḥammad], 'Does your Lord have daughters while they have sons?[1307]", TextGerman = "Frage sie doch nach ihrer Meinung: Gehören deinem Herrn etwa die Töchter und ihnen die Söhne?" });
                        surah37.VerseList.Add(new Verse { Number = 150, TextBosnian = "Zar smo u njihovu prisustvu meleke kao žene stvorili?'", TextEnglish = "Or did We create the angels as females while they were witnesses?'", TextGerman = "Oder haben Wir die Engel als weibliche Wesen erschaffen, während sie anwesend waren?" });
                        surah37.VerseList.Add(new Verse { Number = 151, TextBosnian = "Eto, oni, potvarajući, doista govore:", TextEnglish = "Unquestionably, it is out of their [invented] falsehood that they say,", TextGerman = "Dabei ist es fürwahr aus ihrer ungeheuren Lügenhaftigkeit, daß sie sagen:" });
                        surah37.VerseList.Add(new Verse { Number = 152, TextBosnian = "Allah je rodio!' - oni su, baš, lažljivci.", TextEnglish = "Allāh has begotten,' and indeed, they are liars.", TextGerman = "Allah hat gezeugt. Wahrlich, sie sind Lügner." });
                        surah37.VerseList.Add(new Verse { Number = 153, TextBosnian = "Zar je On kćeri sinovima pretpostavio?!", TextEnglish = "Has He chosen daughters over sons?", TextGerman = "Hat Er die Töchter vor den Söhnen auserwählt?" });
                        surah37.VerseList.Add(new Verse { Number = 154, TextBosnian = "Šta vam je, kako rasuđujete?!", TextEnglish = "What is [wrong] with you? How do you make judgement?", TextGerman = "Was ist mit euch? Wie urteilt ihr?" });
                        surah37.VerseList.Add(new Verse { Number = 155, TextBosnian = "Zašto ne razmislite?", TextEnglish = "Then will you not be reminded?", TextGerman = "Bedenkt ihr denn nicht?" });
                        surah37.VerseList.Add(new Verse { Number = 156, TextBosnian = "Ili, vi, možda, imate dokaz očit?!", TextEnglish = "Or do you have a clear authority?[1308]", TextGerman = "Oder habt ihr eine deutliche Ermächtigung?" });
                        surah37.VerseList.Add(new Verse { Number = 157, TextBosnian = "Donesite Knjigu svoju, ako istinu govorite!", TextEnglish = "Then produce your scripture, if you should be truthful.", TextGerman = "Bringt doch eure Schrift bei, wenn ihr wahrhaftig seid." });
                        surah37.VerseList.Add(new Verse { Number = 158, TextBosnian = "Višebošci između Njega i džina srodstvo uspostavljaju, a džini odavno znaju da će takvi u Vatru biti privedeni,", TextEnglish = "And they have made [i.e., claimed] between Him and the jinn a lineage, but the jinn have already known that they [who made such claims] will be brought [to punishment].", TextGerman = "Und sie stellen zwischen Ihm und den Ginn eine Verwandtschaft her. Aber die Ginn wissen ja, daß sie bestimmt vorgeführt werden" });
                        surah37.VerseList.Add(new Verse { Number = 159, TextBosnian = "čist je Allah i daleko od onoga kako Ga oni opisuju!", TextEnglish = "Exalted is Allāh above what they describe,", TextGerman = "Preis sei Allah! (Und Erhaben ist Er) über das, was sie (Ihm) zuschreiben -," });
                        surah37.VerseList.Add(new Verse { Number = 160, TextBosnian = "To ne čine Allahovi iskreni robovi.", TextEnglish = "Except the chosen servants of Allāh [who do not share in that sin].", TextGerman = "außer Allahs auserlesenen Dienern." });
                        surah37.VerseList.Add(new Verse { Number = 161, TextBosnian = "Ali, ni vi, ni ono što obožavate,", TextEnglish = "So indeed, you [disbelievers] and whatever you worship,", TextGerman = "Ihr aber und das, dem ihr dient," });
                        surah37.VerseList.Add(new Verse { Number = 162, TextBosnian = "ne možete nikoga u zabludu zavesti,", TextEnglish = "You cannot tempt [anyone] away from Him", TextGerman = "könnt niemanden gegen Ihn verführen," });
                        surah37.VerseList.Add(new Verse { Number = 163, TextBosnian = "osim onoga koji će ionako u Vatri gorjeti.", TextEnglish = "Except he who is to [enter and] burn in the Hellfire.[1309]", TextGerman = "außer denjenigen, der dem Höllenbrand ausgesetzt wird." });
                        surah37.VerseList.Add(new Verse { Number = 164, TextBosnian = "Svakome od nas mjesto je određeno,", TextEnglish = "[The angels say],[1310] 'There is not among us any except that he has a known position.[1311]", TextGerman = "Und es gibt niemanden unter uns, der nicht einen bestimmten Platz hätte." });
                        surah37.VerseList.Add(new Verse { Number = 165, TextBosnian = "mi smo u redove poređani,", TextEnglish = "And indeed, we are those who line up [for prayer].", TextGerman = "Wahrlich, wir sind es, die sich (reihenweise) reihen," });
                        surah37.VerseList.Add(new Verse { Number = 166, TextBosnian = "i samo Njega slavimo i veličamo!", TextEnglish = "And indeed, we are those who exalt Allāh.'", TextGerman = "und wahrlich, wir sind es, die preisen." });
                        surah37.VerseList.Add(new Verse { Number = 167, TextBosnian = "A oni su govorili:", TextEnglish = "And indeed, they [i.e., the disbelievers] used to say,[1312]", TextGerman = "Und sie pflegten zu sagen:" });
                        surah37.VerseList.Add(new Verse { Number = 168, TextBosnian = "Da smo mi Knjigu imali kao što su je imali narodi prijašnji,", TextEnglish = "If we had a message from [those of] the former peoples,", TextGerman = "Wenn wir nur eine Ermahnung (gleich derjenigen) der Früheren hätten," });
                        surah37.VerseList.Add(new Verse { Number = 169, TextBosnian = "sigurno bismo bili Allahovi robovi iskreni!'", TextEnglish = "We would have been the chosen servants of Allāh.'", TextGerman = "dann wären wir fürwahr Allahs auserlesene Diener." });
                        surah37.VerseList.Add(new Verse { Number = 170, TextBosnian = "Ali u Kur'an ne htjedoše povjerovati, i znat će oni!", TextEnglish = "But they disbelieved in it,[1313] so they are going to know.", TextGerman = "Dennoch verleugnen sie Ihn. Aber sie werden (es noch) erfahren." });
                        surah37.VerseList.Add(new Verse { Number = 171, TextBosnian = "A riječ Naša već rečena je Našim robovima, poslanicima!", TextEnglish = "And Our word [i.e., decree] has already preceded for Our servants, the messengers,", TextGerman = "Es erging bereits früher Unser Wort an Unsere gesandten Diener:" });
                        surah37.VerseList.Add(new Verse { Number = 172, TextBosnian = "Oni će biti, doista, potpomognuti", TextEnglish = "[That] indeed, they would be those given victory", TextGerman = "Wahrlich, sie sind es, denen Hilfe zuteil wird." });
                        surah37.VerseList.Add(new Verse { Number = 173, TextBosnian = "i vojska Naša zacijelo će pobijediti!", TextEnglish = "And [that] indeed, Our soldiers [i.e., the believers] will be those who overcome.[1314]", TextGerman = "Und wahrlich, Unsere Heerschar ist es, die Sieger sein wird." });
                        surah37.VerseList.Add(new Verse { Number = 174, TextBosnian = "Zato se okreni od njih neko vrijeme", TextEnglish = "So, [O Muḥammad], leave them for a time.", TextGerman = "So kehre dich für eine gewisse Zeit von ihnen ab" });
                        surah37.VerseList.Add(new Verse { Number = 175, TextBosnian = "i posmatraj ih, i oni će posmatrati!", TextEnglish = "And see [what will befall] them, for they are going to see.", TextGerman = "und sieh sie dir an. Auch sie werden sehen." });
                        surah37.VerseList.Add(new Verse { Number = 176, TextBosnian = "Zar oni kaznu Našu da požuruju?", TextEnglish = "Then for Our punishment are they impatient?", TextGerman = "Wünschen sie denn Unsere Strafe zu beschleunigen?" });
                        surah37.VerseList.Add(new Verse { Number = 177, TextBosnian = "Kad se ona spusti na stanište njihovo, zlo jutro osvanut će onima koji su bili upozoreni!", TextEnglish = "But when it descends in their territory, then evil is the morning of those who were warned.", TextGerman = "Wenn sie sich aber in ihre Höfe niederläßt, wie böse wird dann der Morgen der Gewarnten sein!" });
                        surah37.VerseList.Add(new Verse { Number = 178, TextBosnian = "Zato se okreni od njih neko vrijeme", TextEnglish = "And leave them for a time.", TextGerman = "Und kehre dich für eine gewisse Zeit von ihnen ab" });
                        surah37.VerseList.Add(new Verse { Number = 179, TextBosnian = "i posmatraj, i oni će posmatrati!", TextEnglish = "And see, for they are going to see.", TextGerman = "und sieh zu. Auch sie werden sehen." });
                        surah37.VerseList.Add(new Verse { Number = 180, TextBosnian = "Uzvišen je Gospodar tvoj, Gospodar dostojanstva, u odnosu na ono kako ga opisuju!", TextEnglish = "Exalted is your Lord, the Lord of might, above what they describe.", TextGerman = "Preis sei deinem Herrn, dem Herrn der Macht! (Erhaben ist Er) über das, was sie (Ihm) zuschreiben." });
                        surah37.VerseList.Add(new Verse { Number = 181, TextBosnian = "I selam neka je na poslanike", TextEnglish = "And peace upon the messengers.", TextGerman = "Und Friede sei auf den Gesandten!" });
                        surah37.VerseList.Add(new Verse { Number = 182, TextBosnian = "a hvala Allahu, svjetova Gospodaru!", TextEnglish = "And praise to Allāh, Lord of the worlds.", TextGerman = "Und (alles) Lob gehört Allah, dem Herrn der Weltenbewohner!" });
                        result = surah37;
                        break;
                    #endregion

                    // 
                    #region
                    case 38:
                        Surah surah38 = new Surah(); surah38.Name = Constants.Surah38Name; surah38.Number = 38; surah38.VerseList = new List<Verse>();
                        surah38.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah38.VerseList.Add(new Verse { Number = 1, TextBosnian = "Sad. Tako Mi Kur'ana koji opomenu sadrži,", TextEnglish = "Ṣād.[1316] By the Qur’ān containing reminder...[1317]", TextGerman = "Sad. Beim Qur’an, voll der Ermahnung!" });
                        surah38.VerseList.Add(new Verse { Number = 2, TextBosnian = "doista su oni koji ne vjeruju u osionosti i raskolu!", TextEnglish = "But those who disbelieve are in pride and dissension.", TextGerman = "Aber nein! Diejenigen, die ungläubig sind, befinden sich in (falschem) Stolz und in Widerstreit." });
                        surah38.VerseList.Add(new Verse { Number = 3, TextBosnian = "Koliko smo Mi naroda prije njih uništili. I oni su za pomoć vapili, ali nije bilo vremena za uzmicanje!", TextEnglish = "How many a generation have We destroyed before them, and they [then] called out; but it was not a time for escape.", TextGerman = "Wie viele Geschlechter haben Wir vor ihnen vernichtet! Sie riefen, doch da war keine Zeit mehr zum Entrinnen." });
                        surah38.VerseList.Add(new Verse { Number = 4, TextBosnian = "Oni se čude što im je jedan od njih došao da ih upozorava, i govore nevjernici: 'Ovo je čarobnjak, lažov?", TextEnglish = "And they wonder that there has come to them a warner [i.e., Prophet Muḥammad (ﷺ)] from among themselves. And the disbelievers say, 'This is a magician and a liar.", TextGerman = "Sie wundern sich darüber, daß zu ihnen ein Überbringer von Warnungen von ihnen gekommen ist. Die Ungläubigen sagen: Dies ist ein verlogener Zauberer." });
                        surah38.VerseList.Add(new Verse { Number = 5, TextBosnian = "Zar on da bogove svede na Boga jednog?! To je, zaista, nešto veoma čudno!'", TextEnglish = "Has he made the gods [only] one God? Indeed, this is a curious thing.'", TextGerman = "Macht er denn die Götter zu einem einzigen Gott? Das ist fürwahr etwas sehr Verwunderliches." });
                        surah38.VerseList.Add(new Verse { Number = 6, TextBosnian = "I oni ugledni među njima krenuše: 'Idite, nastavite i ustrajte uz božanstva svoja! Uistinu, to je nešto što se hoće!", TextEnglish = "And the eminent among them went forth, [saying], 'Continue, and be patient over [the defense of] your gods. Indeed, this is a thing intended.[1318]", TextGerman = "Und so ging die führende Schar unter ihnen fort: Geht hin und haltet an euren Göttern beharrlich fest. Das ist fürwahr etwas, was (zu eurem Nachteil) gewollt wird." });
                        surah38.VerseList.Add(new Verse { Number = 7, TextBosnian = "Za ovo nismo čuli u vjeri posljednjoj, ovo nije ništa drugo već izmišljotina;", TextEnglish = "We have not heard of this in the latest religion.[1319] This is not but a fabrication.", TextGerman = "Wir haben hiervon nicht in dem vorherigen Glaubensbekenntnis gehört; dies ist nur eine Erfindung." });
                        surah38.VerseList.Add(new Verse { Number = 8, TextBosnian = "zašto baš njemu, između nas, da bude poslana Opomena?!' Ali, oni u Opomenu Moju sumnjaju zato što kaznu Moju nisu iskusili.", TextEnglish = "Has the message been revealed to him out of [all of] us?' Rather, they are in doubt about My message. Rather, they have not yet tasted My punishment.", TextGerman = "Soll die Ermahnung (ausgerechnet) auf ihn aus unserer Mitte herabgesandt worden sein? Nein! Vielmehr sind sie über Meine Ermahnung im Zweifel. Nein! Vielmehr haben sie noch nicht Meine Strafe gekostet." });
                        surah38.VerseList.Add(new Verse { Number = 9, TextBosnian = "Ili, da nisu u njih riznice milosti Gospodara tvoga, Silnoga i obilnog Darovatelja?!", TextEnglish = "Or do they have the depositories of the mercy of your Lord, the Exalted in Might, the Bestower?[1320]", TextGerman = "Oder besitzen sie etwa die Schatzkammern der Barmherzigkeit deines Herrn, des Allmächtigen, des unablässig Schenkenden?" });
                        surah38.VerseList.Add(new Verse { Number = 10, TextBosnian = "Ili, oni možda imaju vlast na nebesima i na Zemlji i nad onim što je između njih?! - neka se, onda, uz konopce popnu!", TextEnglish = "Or is theirs the dominion of the heavens and the earth and what is between them? Then let them ascend through [any] ways of access.[1321]", TextGerman = "Oder gehört (etwa) ihnen die Herrschaft der Himmel und der Erde und dessen, was dazwischen ist? So sollen sie doch an Seilen emporsteigen." });
                        surah38.VerseList.Add(new Verse { Number = 11, TextBosnian = "Oni su samo neznatna vojska udruženih skupina koja će tamo biti poražena.", TextEnglish = "[They are but] soldiers [who will be] defeated there among the companies [of disbelievers].", TextGerman = "Manch eine Heerschar von den Gruppierungen wird da geschlagen stehen!" });
                        surah38.VerseList.Add(new Verse { Number = 12, TextBosnian = "I prije njih su narod Nuhov, i Ad, i faraon, koji je vojsku imao, poricali,", TextEnglish = "The people of Noah denied before them, and [the tribe of] ʿAad and Pharaoh, the owner of stakes,[1322]", TextGerman = "Der Lüge bezichtigten (ihre Gesandten) schon vor ihnen das Volk Nuhs, die ’Ad und Fir’aun, der Besitzer der Pfähle," });
                        surah38.VerseList.Add(new Verse { Number = 13, TextBosnian = "i Semud i Liltov narod i stanovnici Ejke; oni su baš bili udružene skupine!", TextEnglish = "And [the tribe of] Thamūd and the people of Lot and the companions of the thicket [i.e., people of Madyan]. Those are the companies.[1323]", TextGerman = "und die Tamud und das Volk Luts und die Bewohner des Dickichts; das sind die Gruppierungen." });
                        surah38.VerseList.Add(new Verse { Number = 14, TextBosnian = "Svi su oni poslanike u laž utjerivali i kaznu Moju zaslužili.", TextEnglish = "Each of them denied the messengers, so My penalty was justified.", TextGerman = "Al]e ausnahmslos bezichtigten die Gesandten der Lüge, so wurde Meine Bestrafung unvermeidlich fällig." });
                        surah38.VerseList.Add(new Verse { Number = 15, TextBosnian = "A i ovi samo čekaju jedan zvuk koji neće biti ponovljen,", TextEnglish = "And these [disbelievers] await not but one blast [of the Horn]; for it there will be no delay.[1324]", TextGerman = "Diese erwarten wohl nur einen einzigen Schrei, der keine Unterbrechung hat." });
                        surah38.VerseList.Add(new Verse { Number = 16, TextBosnian = "i govore: 'Gospodaru naš, požuri i kazni nas, prije Dana u kojem će se račun polagati!'", TextEnglish = "And they say, 'Our Lord, hasten for us our share [of the punishment] before the Day of Account.'", TextGerman = "Und sie sagen: Unser Herr, gib uns schnell unser Buch noch vor dem Tag der Abrechnung." });
                        surah38.VerseList.Add(new Verse { Number = 17, TextBosnian = "Ti otrpi ono što oni govore i sjeti se roba Našeg Davuda, čvrstog u vjeri, koji se uvijek Allahu pokajnički obraćao.", TextEnglish = "Be patient over what they say and remember Our servant, David, the possessor of strength; indeed, he was one who repeatedly turned back [to Allāh].", TextGerman = "Ertrage standhaft, was sie sagen. Und gedenke Unseres Dieners Dawud, des Kraftvollen. Gewiß, er war immer wieder umkehrbereit." });
                        surah38.VerseList.Add(new Verse { Number = 18, TextBosnian = "Mi smo brda potčinili da zajedno s njim slave i veličaju Allaha prije nego što Sunce zađe i poslije, pošto grane,", TextEnglish = "Indeed, We subjected the mountains [to praise] with him, exalting [Allāh] in the [late] afternoon and [after] sunrise.", TextGerman = "Wir machten ja die Berge dienstbar, daß sie mit ihm zusammen abends und bei Sonnenaufgang (Allah) preisen," });
                        surah38.VerseList.Add(new Verse { Number = 19, TextBosnian = "a i ptice okupljene - svi su oni s njim pokorno ponavljali.", TextEnglish = "And the birds were assembled, all with him repeating [praises].", TextGerman = "und auch die (in Scharen) versammelten Vögel. Alle waren immer wieder zu ihm umkehrbereit.’" });
                        surah38.VerseList.Add(new Verse { Number = 20, TextBosnian = "I učvrstili smo carstvo njegovo i dali mu mudrost i govor razlučujući.", TextEnglish = "And We strengthened his kingdom and gave him wisdom and discernment in speech.", TextGerman = "Und Wir festigten seine Herrschaft und gaben ihm die Weisheit und die Entschiedenheit im Spruch." });
                        surah38.VerseList.Add(new Verse { Number = 21, TextBosnian = "A da li je do tebe doprla vijest o parničarima kad su se do mihraba ispeli,", TextEnglish = "And has there come to you the news of the adversaries, when they climbed over the wall of [his] prayer chamber -", TextGerman = "Ist zu dir die Kunde von den Widersachern gekommen? Als sie über die Mauern in die Andachtsräume einstiegen." });
                        surah38.VerseList.Add(new Verse { Number = 22, TextBosnian = "kad su Davudu bahnuli, pa se on od njih uplašio. 'Ne boj se', rekli su, 'mi smo dvojica parničara, jedan drugom smo nepravdu učinili, pa nam po pravdi presudi; ne budi pristrasan, i na Pravi put nas uputi.", TextEnglish = "When they entered upon David and he was alarmed by them? They said, 'Fear not. [We are] two adversaries, one of whom has wronged the other, so judge between us with truth and do not exceed [it] and guide us to the sound path.", TextGerman = "Als sie bei Dawud eintraten. Da erschrak er vor ihnen. Sie sagten: Fürchte dich nicht. (Wir sind) zwei Widersacher, von denen der eine den anderen unterdrückt hat. So urteile zwischen uns der Wahrheit entsprechend, handle nicht ungerecht und führe uns zum rechten Weg." });
                        surah38.VerseList.Add(new Verse { Number = 23, TextBosnian = "Ovaj prijatelj moj ima devedeset i devet ovaca, a ja samo jednu, i on mi reče: 'Daj ti nju meni!' - i u govoru me pobijedi.'", TextEnglish = "Indeed this, my brother, has ninety-nine ewes, and I have one ewe; so he said, 'Entrust her to me,' and he overpowered me in speech.'", TextGerman = "Dieser da, mein Bruder, hat neunundneunzig weibliche Schafe, ich aber (nur) ein einziges Schaf. Dann sagte er: .Vertraue es mir an’, und er überwand mich in der Rede." });
                        surah38.VerseList.Add(new Verse { Number = 24, TextBosnian = "Učinio ti je zulum!', reče Davud, 'time što je tražio da tvoju ovcu doda ovcama svojim; mnogi ortaci čine nepravdu jedni drugima, takvi nisu jedino oni koji vjeruju i čine dobra djela; a takvih je malo.' I Davud se uvjeri da smo Mi baš njega na kušnju stavili, pa oprost od Gospodara svoga zamoli, pade licem na tlo i pokaja se.", TextEnglish = "[David] said, 'He has certainly wronged you in demanding your ewe [in addition] to his ewes. And indeed, many associates oppress one another, except for those who believe and do righteous deeds - and few are they.' And David became certain that We had tried him, and he asked forgiveness of his Lord[1325] and fell down bowing [in prostration] and turned in repentance [to Allāh].", TextGerman = "Er sagte: Er hat dir Unrecht getan, daß er dein Schaf zu seinen Schafen hinzu verlangte. Gewiß, viele von den Teilhabern begehen gegeneinander Übergriffe, außer denjenigen, die glauben und rechtschaffene Werke tun - und das sind nur wenige. Und Dawud verstand, daß Wir ihn nur der Versuchung ausgesetzt hatten. Da bat er seinen Herrn um Vergebung und fiel in Verbeugung nieder und wandte sich (Ihm) reuig zu." });
                        surah38.VerseList.Add(new Verse { Number = 25, TextBosnian = "I Mi smo mu to oprostili, i on je, doista, blizak Nama i divno prebivalište ga čeka.", TextEnglish = "So We forgave him that; and indeed, for him is nearness to Us and a good place of return.", TextGerman = "Und so vergaben Wir ihm dies. Für ihn wird es fürwahr (den Zutritt in) Unsere Nähe und eine schöne Heimstatt geben." });
                        surah38.VerseList.Add(new Verse { Number = 26, TextBosnian = "O Davude, Mi smo te namjesnikom na Zemlji učinili, zato sudi ljudima po pravdi i ne povodi se za hirom da te ne odvede s Allahova puta; one koji skreću s Allahova puta čeka teška patnja zato što su Dan obračuna zaboravljali.", TextEnglish = "[We said], 'O David, indeed We have made you a successor upon the earth, so judge between the people in truth and do not follow [your own] desire, as it will lead you astray from the way of Allāh.' Indeed, those who go astray from the way of Allāh will have a severe punishment for having forgotten the Day of Account.", TextGerman = "O Dawud, Wir haben dich zu einem Statthalter auf der Erde gemacht. So richte zwischen den Menschen der Wahrheit entsprechend und folge nicht der Neigung, auf daß sie dich nicht von Allahs Weg abirren läßt, denn für diejenigen, die von Allahs Weg abirren, wird es strenge Strafe dafür geben, daß sie den Tag der Abrechnung vergessen haben." });
                        surah38.VerseList.Add(new Verse { Number = 27, TextBosnian = "Mi nismo uzalud stvorili nebo i Zemlju i ono što je između njih; tako misle nevjernici, pa teško nevjernicima kad budu u Vatri!", TextEnglish = "And We did not create the heaven and the earth and that between them aimlessly. That is the assumption of those who disbelieve, so woe to those who disbelieve from the Fire.", TextGerman = "Und Wir haben den Himmel und die Erde und das, was dazwischen ist, nicht umsonst erschaffen. Das ist die Meinung derjenigen, die ungläubig sind. Doch wehe denjenigen, die ungläubig sind, vor dem (Höllen)feuer!" });
                        surah38.VerseList.Add(new Verse { Number = 28, TextBosnian = "Zar ćemo postupiti s onima koji vjeruju i čine dobro kao s onima koji prave nered na Zemlji, i zar ćemo postupiti s onima koji se grijeha klone isto kao i s grešnicima?!", TextEnglish = "Or should We treat those who believe and do righteous deeds like corrupters in the land? Or should We treat those who fear Allāh like the wicked?", TextGerman = "Oder sollen Wir etwa diejenigen, die glauben und rechtschaffene Werke tun, den Unheilstiftern auf der Erde gleichstellen oder die Gottesfürchtigen den Lasterhaften?" });
                        surah38.VerseList.Add(new Verse { Number = 29, TextBosnian = "Knjiga koju ti objavljujemo blagoslovljena je, da bi oni o riječima njezinim razmislili i da bi oni koji su razumom obdareni pouku primili.", TextEnglish = "[This is] a blessed Book which We have revealed to you, [O Muḥammad], that they might reflect upon its verses and that those of understanding would be reminded.", TextGerman = "(Dies ist) ein gesegnetes Buch, das Wir zu dir hinabgesandt haben, damit sie über seine Zeichen nachsinnen und damit diejenigen bedenken, die Verstand besitzen." });
                        surah38.VerseList.Add(new Verse { Number = 30, TextBosnian = "Mi smo Davudu poklonili Sulejmana; divan je on rob bio, i stalno se Allahu pokajnički obraćao!", TextEnglish = "And to David We gave Solomon. An excellent servant, indeed he was one repeatedly turning back [to Allāh].", TextGerman = "Und Wir schenkten Dawud Sulaiman. Welch ein trefflicher Diener! Er war immer wieder umkehrbereit." });
                        surah38.VerseList.Add(new Verse { Number = 31, TextBosnian = "Kad su jedne večeri pred njega bili izvedeni brzi konji koji su na tri noge stajali, a četvrtom jedva zemlju doticali,", TextEnglish = "[Mention] when there were exhibited before him in the afternoon the poised [standing] racehorses.", TextGerman = "Als ihm am Abend die edlen, schnellen Pferde vorgeführt wurden," });
                        surah38.VerseList.Add(new Verse { Number = 32, TextBosnian = "on reče: 'Umjesto da mislim na Gospodara svoga, ja pokazujem ljubav prema blagu!', i oni se izgubiše iz vida.", TextEnglish = "And he said, 'Indeed, I gave preference to the love of good [things] over the remembrance of my Lord until it [i.e., the sun] disappeared into the curtain [of darkness].'", TextGerman = "da sagte er: Ich habe mich der Liebe der (irdischen) Güter hingegeben und es darüber unterlassen, meines Herrn zu gedenken, bis sie sich hinter dem Vorhang verbarg." });
                        surah38.VerseList.Add(new Verse { Number = 33, TextBosnian = "Vratite mi ih!', i on ih poče gladiti po nogama i vratovima.", TextEnglish = "[He said], 'Return them to me,' and set about striking[1326] [their] legs and necks.", TextGerman = "Bringt sie mir wieder her. Da begann er, ihnen die Beine und den Hals zu zerhauen." });
                        surah38.VerseList.Add(new Verse { Number = 34, TextBosnian = "Mi smo Sulejmana u iskušenje doveli i na njegovo prijestolje tijelo bacili, ali se poslije povratio.", TextEnglish = "And We certainly tried Solomon and placed on his throne a body;[1327] then he returned.[1328]", TextGerman = "Und Wir setzten ja Sulaiman der Versuchung aus und setzten eine Gestalt auf seinen Thron. Hierauf wandte er sich (Allah) reuig zu." });
                        surah38.VerseList.Add(new Verse { Number = 35, TextBosnian = "Gospodaru moj - rekao je - oprosti mi i daruj mi vlast kakvu niko osim mene neće imati! Ti Si uistinu Darovatelj obilni!", TextEnglish = "He said, 'My Lord, forgive me and grant me a kingdom such as will not belong to anyone after me. Indeed, You are the Bestower.'", TextGerman = "Er sagte: Mein Herr, vergib mir und schenke mir eine Königsherrschaft, die niemandem nach mir geziemt. Gewiß, Du bist ja der unablässig Schenkende." });
                        surah38.VerseList.Add(new Verse { Number = 36, TextBosnian = "I Mi smo dali da mu služe: vjetar, koji je prema zapovijedi njegovoj blago puhao onamo kuda je on htio,", TextEnglish = "So We subjected to him the wind blowing by his command, gently, wherever he directed,", TextGerman = "Da machten Wir ihm den Wind dienstbar, daß er nach seinem Befehl sanft wehte, wohin er es für treffend hielt;" });
                        surah38.VerseList.Add(new Verse { Number = 37, TextBosnian = "i šejtani, sve graditelji i gnjurci,", TextEnglish = "And [also] the devils [of jinn] - every builder and diver.", TextGerman = "und auch die Satane, jeden Bauarbeiter und Taucher," });
                        surah38.VerseList.Add(new Verse { Number = 38, TextBosnian = "i drugi u bukagije okovani.", TextEnglish = "And others bound together in irons.", TextGerman = "und andere, in Ketten zusammengebunden." });
                        surah38.VerseList.Add(new Verse { Number = 39, TextBosnian = "Ovo je Naš dar, pa ti daruj ili uskrati, nećeš zbog toga odgovarati!", TextEnglish = "[We said], 'This is Our gift, so grant or withhold without account.'", TextGerman = "Das ist Unsere Gabe. So erweise Wohltaten oder sei zurückhaltend, (und dies,) ohne zu rechnen." });
                        surah38.VerseList.Add(new Verse { Number = 40, TextBosnian = "On je, doista, blizak Nama i čeka ga krasno prebivalište.", TextEnglish = "And indeed, for him is nearness to Us and a good place of return.", TextGerman = "Für ihn wird es fürwahr (den Zutritt in) Unsere Nähe und eine schöne Heimstatt geben." });
                        surah38.VerseList.Add(new Verse { Number = 41, TextBosnian = "I sjeti se roba Našeg Ejjuba, kada je Gospodaru svome zavapio: 'Šejtan me pogodio nedaćama i patnjom!'", TextEnglish = "And remember Our servant Job, when he called to his Lord, 'Indeed, Satan has touched me with hardship and torment.'", TextGerman = "Und gedenke Unseres Dieners Ayyub. Als er zu seinem Herrn rief: Mich hat der Satan mit Mühsal und Pein heimgesucht." });
                        surah38.VerseList.Add(new Verse { Number = 42, TextBosnian = "Udari nogom o zemlju - eto hladne vode za kupanje i piće!", TextEnglish = "[So he was told], 'Strike [the ground] with your foot; this is a [spring for a] cool bath and drink.'", TextGerman = "- Tritt kräftig mit deinem Fuß auf; da ist kühles Wasser zum Waschen und zum Trinken." });
                        surah38.VerseList.Add(new Verse { Number = 43, TextBosnian = "I Mi smo mu, iz milosti Naše, porodicu darovali i još toliko uz njih, da bude opomena za one koji imaju pameti.", TextEnglish = "And We granted him his family and a like [number] with them as mercy from Us and a reminder for those of understanding.", TextGerman = "Und Wir schenkten ihm seine Angehörigen (wieder) und noch einmal die gleiche Zahl dazu, aus Barmherzigkeit von Uns und als Ermahnung für diejenigen, die Verstand besitzen." });
                        surah38.VerseList.Add(new Verse { Number = 44, TextBosnian = "I uzmi rukom svojom snop i njime udari, samo zakletvu ne prekrši.' Mi smo, zaista, našli da je on strpljiv; divan je rob on bio i stalno se Allahu pokajnički obraćao!", TextEnglish = "[We said], 'And take in your hand a bunch [of grass] and strike with it and do not break your oath.'[1329] Indeed, We found him patient, an excellent servant. Indeed, he was one repeatedly turning back [to Allāh].", TextGerman = "Und: Nimm in deine Hand ein Bündel (dünner Zweige) und schlag damit zu und sei nicht eidbrüchig. Gewiß, Wir fanden ihn standhaft. Welch ein trefflicher Diener! Er war immer wieder umkehrbereit." });
                        surah38.VerseList.Add(new Verse { Number = 45, TextBosnian = "I sjeti se robova naših: Ibrahima i Ishaka i Jakuba, sve u vjeri čvrstih i dalekovidih.", TextEnglish = "And remember Our servants, Abraham, Isaac and Jacob - those of strength and [religious] vision.", TextGerman = "Und gedenke Unserer Diener Ibrahim, Ishaq und Ya’qub, die Kraft und Einsicht besaßen." });
                        surah38.VerseList.Add(new Verse { Number = 46, TextBosnian = "Mi ih posebno obdarismo vrlinom jednom: da im je uvijek bio na umu onaj svijet;", TextEnglish = "Indeed, We chose them for an exclusive quality: remembrance of the home [of the Hereafter].", TextGerman = "Wir erlasen sie durch eine besondere Eigenschaft aus, mit dem Gedenken an die (jenseitige) Wohnstätte." });
                        surah38.VerseList.Add(new Verse { Number = 47, TextBosnian = "i oni su, zaista, u Nas od onih odabranih dobrih ljudi.", TextEnglish = "And indeed they are, to Us, among the chosen and outstanding.", TextGerman = "Sie gehören bei Uns wahrlich zu den Auserwählten und Besten." });
                        surah38.VerseList.Add(new Verse { Number = 48, TextBosnian = "I sjeti se Ismaila i El-Jese'a i Zulkifla, svi su oni bili čestiti.", TextEnglish = "And remember Ishmael, Elisha and Dhul-Kifl, and all are among the outstanding.", TextGerman = "Und gedenke Isma’ils, Alyasa’s und Du ’-Kifls. Alle gehören zu den Besten." });
                        surah38.VerseList.Add(new Verse { Number = 49, TextBosnian = "Ovo je lijep spomen! A bogobojazne čeka divno prebivalište:", TextEnglish = "This is a reminder. And indeed, for the righteous is a good place of return -", TextGerman = "Dies ist eine Ermahnung. Und für die Gottesfürchtigen wird es wahrlich eine schöne Heimstatt geben," });
                        surah38.VerseList.Add(new Verse { Number = 50, TextBosnian = "vrtovi Adna, čije će kapije biti za njih otvorene,", TextEnglish = "Gardens of perpetual residence, whose doors will be opened to them.", TextGerman = "die Gärten Edens, deren Tore ihnen geöffnet stehen," });
                        surah38.VerseList.Add(new Verse { Number = 51, TextBosnian = "u kojima će naslonjeni biti, i raznovrsno voće i piće tražiti.", TextEnglish = "Reclining within them, they will call therein for abundant fruit and drink.", TextGerman = "worin sie sich lehnen und worin sie nach vielen Früchten und nach Getränk rufen." });
                        surah38.VerseList.Add(new Verse { Number = 52, TextBosnian = "Pored njih bit će hurije, istih godina, koje će preda se gledati.", TextEnglish = "And with them will be women limiting [their] glances[1330] and of equal age.", TextGerman = "Und bei ihnen sind gleichaltrige (weibliche Wesen), die ihre Blicke zurückhalten." });
                        surah38.VerseList.Add(new Verse { Number = 53, TextBosnian = "To je ono što vam se obećava za Dan polaganja računa;", TextEnglish = "This is what you, [the righteous], are promised for the Day of Account.", TextGerman = "Das ist, was euch für den Tag der Abrechnung versprochen wird." });
                        surah38.VerseList.Add(new Verse { Number = 54, TextBosnian = "to će, doista, opskrba Naša biti, koja nikada neće prestajati!", TextEnglish = "Indeed, this is Our provision; for it there is no depletion.", TextGerman = "Das ist fürwahr Unsere Versorgung; sie geht nicht zu Ende." });
                        surah38.VerseList.Add(new Verse { Number = 55, TextBosnian = "Eto tako! A one koji budu obijesni bili čeka najgore prebivalište:", TextEnglish = "This [is so]. But indeed, for the transgressors is an evil place of return -", TextGerman = "Das ist (für die Gottesfürchtigen). Für diejenigen jedoch, die das Maß (an Frevel) überschreiten, wird es wahrlich eine schlechte Heimstatt geben," });
                        surah38.VerseList.Add(new Verse { Number = 56, TextBosnian = "Džehennem, u kojem će gorjeti; a grozne li postelje!", TextEnglish = "Hell, which they will [enter to] burn, and wretched is the resting place.", TextGerman = "die Hölle, der sie ausgesetzt sein werden - eine schlimme Lagerstatt!" });
                        surah38.VerseList.Add(new Verse { Number = 57, TextBosnian = "Eto, tako! Pa neka kušaju vodu ključalu i kapljevinu hladnu kao led,", TextEnglish = "This - so let them taste it - is scalding water and [foul] purulence.", TextGerman = "Das ist - sie sollen es kosten - heißes Wasser und stinkender Eiter" });
                        surah38.VerseList.Add(new Verse { Number = 58, TextBosnian = "i druge slične ovima muke, mnogostruke.", TextEnglish = "And other [punishments] of its type [in various] kinds.", TextGerman = "und anderes dergleichen in verschiedenen Arten." });
                        surah38.VerseList.Add(new Verse { Number = 59, TextBosnian = "Ta gomila zajedno s vama će, tiskajući se, u Džehennem ući! Ne bilo im prostrano! U Vatri će oni, doista, gorjeti!", TextEnglish = "[Its inhabitants will say], 'This is a company bursting in with you. No welcome for them. Indeed, they will burn in the Fire.'", TextGerman = "- Da ist eine Schar, die sich mit euch hineinstürzt. - Sie sind nicht willkommen, denn sie werden dem (Höllen)feuer ausgesetzt sein." });
                        surah38.VerseList.Add(new Verse { Number = 60, TextBosnian = "Vama ne bilo prostrano!', reći će oni, 'vi ste nam ovo pripremili, a grozna li boravišta!'", TextEnglish = "They will say, 'Nor you! No welcome for you. You, [our leaders], brought this upon us, and wretched is the settlement.'", TextGerman = "Sie sagen: Nein! Vielmehr seid ihr es, die ihr nicht willkommen seid. Das habt ihr uns doch bereitet - ein schlimmer Aufenthalt!" });
                        surah38.VerseList.Add(new Verse { Number = 61, TextBosnian = "Gospodaru naš', reći će, 'udvostruči patnju u Vatri onima koji su nam ovo priredili!'", TextEnglish = "They will say, 'Our Lord, whoever brought this upon us - increase for him double punishment in the Fire.'", TextGerman = "Sie sagen: Unser Herr, erhöhe demjenigen, der uns dies bereitet hat, das (Strafmaß) als mehrfache Strafe im (Höllen)feuer." });
                        surah38.VerseList.Add(new Verse { Number = 62, TextBosnian = "I govorit će: 'Zašto ne vidimo ljude koje smo u zle ubrajali", TextEnglish = "And they will say, 'Why do we not see men whom we used to count among the worst?[1331]", TextGerman = "Und sie sagen: Wie kommt das, daß wir (gewisse) Männer nicht sehen, die wir zu den Bösewichten zählten?" });
                        surah38.VerseList.Add(new Verse { Number = 63, TextBosnian = "i koje smo ismijavati? Da nam se nisu iz vida izgubili", TextEnglish = "Is it [because] we took them in ridicule, or has [our] vision turned away from them?'", TextGerman = "Haben wir sie (etwa zu Unrecht) zum Gegenstand des Spottes genommen? Oder haben die Blicke sie verfehlt?" });
                        surah38.VerseList.Add(new Verse { Number = 64, TextBosnian = "Istina je, sigurno, da će se stanovnici Džehennema među sobom raspravljati.", TextEnglish = "Indeed, that is truth [i.e., reality] - the quarreling of the people of the Fire.", TextGerman = "Das ist tatsächlich wahr: (so ist) der Streit der Bewohner des (Höllen)feuers miteinander." });
                        surah38.VerseList.Add(new Verse { Number = 65, TextBosnian = "Reci: 'Ja sam samo upozoritelj; nema boga osim Allaha, Jedinoga i Potčinitelja svega,", TextEnglish = "Say, [O Muḥammad], 'I am only a warner, and there is not any deity except Allāh, the One, the Prevailing,[1332]", TextGerman = "Sag: Ich bin nur ein Überbringer von Warnungen, und keinen Gott gibt es außer Allah, dem Einen, dem Allbezwinger," });
                        surah38.VerseList.Add(new Verse { Number = 66, TextBosnian = "Gospodara nebesa i Zemlje i onoga što je između njih, Silnoga i grijeha Oprostitelja.", TextEnglish = "Lord of the heavens and the earth and whatever is between them, the Exalted in Might,[1333] the Perpetual Forgiver.'[1334]", TextGerman = "dem Herrn der Himmel und der Erde und dessen, was dazwischen ist, dem Allmächtigen und Allvergebenden." });
                        surah38.VerseList.Add(new Verse { Number = 67, TextBosnian = "Reci: 'Ovo je vijest velika,", TextEnglish = "Say, 'It is great news", TextGerman = "Sag: Es ist eine gewaltige Kunde," });
                        surah38.VerseList.Add(new Verse { Number = 68, TextBosnian = "a vi od nje glave okrećete.", TextEnglish = "From which you turn away.", TextGerman = "von der ihr euch abwendet." });
                        surah38.VerseList.Add(new Verse { Number = 69, TextBosnian = "Ja nisam ništa znao o Skupu Visokome kada su se prepirali -", TextEnglish = "I had no knowledge of the exalted assembly [of angels] when they were disputing [the creation of Adam].", TextGerman = "Ich hatte kein Wissen über die höchste führende Schar’, als sie miteinander stritten." });
                        surah38.VerseList.Add(new Verse { Number = 70, TextBosnian = "meni se objavljuje da sam samo upozoritelj jasni.'", TextEnglish = "It has not been revealed to me except that I am a clear warner.'", TextGerman = "Mir wird ja (als Offenbarung) eingegeben, daß ich nur ein deutlicher Warner bin." });
                        surah38.VerseList.Add(new Verse { Number = 71, TextBosnian = "I kad je melecima Gospodar tvoj rekao: 'Stvorit ću čovjeka od gline,", TextEnglish = "[So mention] when your Lord said to the angels, 'Indeed, I am going to create a human being from clay.", TextGerman = "Als dein Herr zu den Engeln sagte: Ich werde ein menschliches Wesen aus Lehm erschaffen." });
                        surah38.VerseList.Add(new Verse { Number = 72, TextBosnian = "pa kad mu oblik upotpunim i u njega duha Svog udahnem, vi mu na sedždu padnite!'", TextEnglish = "So when I have proportioned him and breathed into him of My [created] soul,[1335] then fall down to him in prostration.'", TextGerman = "Wenn Ich es zurechtgeformt und ihm von Meinem Geist eingehaucht habe, dann fallt und werft euch vor ihm nieder." });
                        surah38.VerseList.Add(new Verse { Number = 73, TextBosnian = "Meleci su, svi do posljednjeg, zajedno sedždu učinili", TextEnglish = "So the angels prostrated - all of them entirely,", TextGerman = "Da warfen sich die Engel alle zusammen nieder," });
                        surah38.VerseList.Add(new Verse { Number = 74, TextBosnian = "osim lblisa; on se uzoholio i postao nevjernik.", TextEnglish = "Except Iblees;[1336] he was arrogant and became among the disbelievers.", TextGerman = "außer Iblis; er verhielt sich hochmütig und gehörte zu den Ungläubigen." });
                        surah38.VerseList.Add(new Verse { Number = 75, TextBosnian = "O Iblisu!', rekao je On, 'šta te navelo da ne učiniš sedždu onome koga sam Ja Svojim dvjema rukama stvorio? Jesi li se uzoholio ili misliš da si uzvišen.", TextEnglish = "[Allāh] said, 'O Iblees, what prevented you from prostrating to that which I created with My hands?[1337] Were you arrogant [then], or were you [already] among the haughty?'", TextGerman = "Er sagte: O Iblis, was hat dich davon abgehalten, dich vor dem niederzuwerfen, was Ich mit Meinen Händen erschaffen habe? Verhältst du dich hochmütig, oder gehörst du etwa zu den Überheblichen?" });
                        surah38.VerseList.Add(new Verse { Number = 76, TextBosnian = "Bolji sam od njega', rekao je on, 'mene si stvorio od vatre, a njega od gline.'", TextEnglish = "He said, 'I am better than him. You created me from fire and created him from clay.'", TextGerman = "Er sagte: Ich bin besser als er. Mich hast Du aus Feuer erschaffen, wohingegen Du ihn aus Lehm erschaffen hast." });
                        surah38.VerseList.Add(new Verse { Number = 77, TextBosnian = "E, izlazi onda iz Dženneta!', reče On, 'proklet da si!", TextEnglish = "[Allāh] said, 'Then get out of it [i.e., Paradise], for indeed, you are expelled.", TextGerman = "Er sagte: Dann geh aus ihm hinaus, denn du bist der Steinigung würdig," });
                        surah38.VerseList.Add(new Verse { Number = 78, TextBosnian = "Moje će te prokletstvo do Sudnjeg dana pratiti!'", TextEnglish = "And indeed, upon you is My curse until the Day of Recompense.'", TextGerman = "und gewiß, auf dir liegt Mein Fluch bis zum Tag des Gerichts." });
                        surah38.VerseList.Add(new Verse { Number = 79, TextBosnian = "Gospodaru moj!', reče on, 'daj mi vremena do dana kada će oni oživljeni biti!'", TextEnglish = "He said, 'My Lord, then reprieve me until the Day they are resurrected.'", TextGerman = "Er sagte: Mein Herr, gewähre mir Aufschub bis zu dem Tag, da sie auferweckt werden." });
                        surah38.VerseList.Add(new Verse { Number = 80, TextBosnian = "Dajem ti', reče On,", TextEnglish = "[Allāh] said, 'So indeed, you are of those reprieved", TextGerman = "Er sagte: Gewiß, du gehörst zu denjenigen, denen Aufschub gewährt wird," });
                        surah38.VerseList.Add(new Verse { Number = 81, TextBosnian = "do dana već određenog.'", TextEnglish = "Until the Day of the time well-known.'", TextGerman = "bis zum Tag der (wohl)bekannten Zeit." });
                        surah38.VerseList.Add(new Verse { Number = 82, TextBosnian = "E tako mi dostojanstva Tvoga', reče, 'sigurno ću ih sve na stranputicu navesti,", TextEnglish = "[Iblees] said, 'By Your might, I will surely mislead them all.", TextGerman = "Er sagte: Nun, bei Deiner Macht, ich werde sie allesamt ganz gewiß in Verirrung fallen lassen," });
                        surah38.VerseList.Add(new Verse { Number = 83, TextBosnian = "osim onih među Tvojim robovima koji su u vjerovanju iskreni!'", TextEnglish = "Except, among them, Your chosen servants.'", TextGerman = "außer Deinen Dienern, den auserlesenen unter ihnen." });
                        surah38.VerseList.Add(new Verse { Number = 84, TextBosnian = "Istinom se kunem i istinu govorim', reče Allah,", TextEnglish = "[Allāh] said, 'The truth [is My oath], and the truth I say -", TextGerman = "Er sagte: Es ist die Wahrheit - und Ich sage ja die Wahrheit;" });
                        surah38.VerseList.Add(new Verse { Number = 85, TextBosnian = "sigurno ću svima, tobom i onima koji se budu poveli za tobom, Džehennem napuniti!'", TextEnglish = "[That] I will surely fill Hell with you and those of them that follow you all together.'", TextGerman = "Ich werde die Hölle ganz gewiß mit dir und mit all denjenigen von ihnen füllen, die dir folgen." });
                        surah38.VerseList.Add(new Verse { Number = 86, TextBosnian = "Reci: 'Ne tražim ja od vas za ovo nikakvu nagradu i ja ne dodajem preko onog čime me je Allah obavezao.", TextEnglish = "Say, [O Muḥammad], 'I do not ask you for it [i.e., the Qur’ān] any payment, and I am not of the pretentious.", TextGerman = "Sag: Ich verlange von euch keinen Lohn dafür. Und ich gehöre nicht zu denjenigen, die Unzumutbares auferlegen." });
                        surah38.VerseList.Add(new Verse { Number = 87, TextBosnian = "Kur'an je, doista, svijetu cijelom opomena!", TextEnglish = "It is but a reminder to the worlds.", TextGerman = "Es ist nur eine Ermahnung für die Weltenbewohner," });
                        surah38.VerseList.Add(new Verse { Number = 88, TextBosnian = "i vi ćete nakon kratkog vremena saznati vijest njegovu.", TextEnglish = "And you will surely know [the truth of] its information after a time.'", TextGerman = "Und ihr werdet die Kunde darüber ganz bestimmt nach einer gewissen Zeit erfahren." });
                        result = surah38;
                        break;
                    #endregion

                    // 
                    #region
                    case 39:
                        Surah surah39 = new Surah(); surah39.Name = Constants.Surah39Name; surah39.Number = 39; surah39.VerseList = new List<Verse>();
                        surah39.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah39.VerseList.Add(new Verse { Number = 1, TextBosnian = "Objavljivanje Knjige od Allaha je Silnog i Mudrog!", TextEnglish = "The revelation of the Book [i.e., the Qur’ān] is from Allāh, the Exalted in Might, the Wise.", TextGerman = "(Dies ist) die Offenbarung des Buches von Allah, dem Allmächtigen und Allweisen." });
                        surah39.VerseList.Add(new Verse { Number = 2, TextBosnian = "Mi ti, doista, objavljujemo Knjigu, s Istinom, pa u ibadetu Allahu budi, ispovijedajući Mu čistu vjeru.", TextEnglish = "Indeed, We have sent down to you the Book, [O Muḥammad], in truth. So worship Allāh, [being] sincere to Him in religion.", TextGerman = "Wir haben das Buch mit der Wahrheit zu dir hinabgesandt. So diene Allah und sei Ihm gegenüber aufrichtig in der Religion." });
                        surah39.VerseList.Add(new Verse { Number = 3, TextBosnian = "Zar ne, Čista vjera pripada samo Allahu. A oni koji pored Njega uzimaju zaštitnike govoreći: 'Mi ih obožavamo samo zato da bi nas što više Allahu približili' Allah će njima, zaista, presuditi o onome u čemu su se oni razilazili. Allah nikako neće uputiti na Pravi put onoga ko je lažljivac i nevjernik.", TextEnglish = "Unquestionably, for Allāh is the pure religion.[1339] And those who take protectors besides Him [say], 'We only worship them that they may bring us nearer to Allāh in position.' Indeed, Allāh will judge between them concerning that over which they differ. Indeed, Allāh does not guide he who is a liar and [confirmed] disbeliever.", TextGerman = "Sicherlich, Allah gehört die aufrichtige Religion. Diejenigen aber, die sich anstatt Seiner Schutzherren nehmen (, sagen:) Wir dienen ihnen nur, damit sie uns Zutritt in Allahs Nähe verschaffen -, gewiß, Allah wird zwischen ihnen richten über das, worüber sie uneinig sind. Gewiß, Allah leitet nicht recht, wer ein Lügner und beharrlicher Ungläubiger ist." });
                        surah39.VerseList.Add(new Verse { Number = 4, TextBosnian = "Da je Allah htio dijete imati, izabrao bi, između onih koje je stvorio, onoga koga bi On htio. Uzvišen je On, On je Allah, Jedini i Potčinitelj svega!", TextEnglish = "If Allāh had intended to take a son, He could have chosen from what He creates whatever He willed. Exalted is He; He is Allāh, the One, the Prevailing.[1340]", TextGerman = "Wenn Allah sich hätte Kinder nehmen wollen, hätte Er wahrlich aus dem, was Er erschaffen hat, sich auserwählt, was Er will. Preis sei Ihm! Er ist Allah, der Eine, der Allbezwinger." });
                        surah39.VerseList.Add(new Verse { Number = 5, TextBosnian = "Nebesa i Zemlju je s Istinom stvorio; On noću obavija dan, i danom obavija noć; On je Sunce i Mjesec potčinio, svako se kreće do roka određenog. Eto, On je Silni i Oprostitelj stalni!", TextEnglish = "He created the heavens and earth in truth. He wraps the night over the day and wraps the day over the night and has subjected the sun and the moon, each running [its course] for a specified term. Unquestionably, He is the Exalted in Might, the Perpetual Forgiver.[1341]", TextGerman = "Er hat die Himmel und die Erde in Wahrheit erschaffen. Er läßt die Nacht den Tag umschlingen, und Er läßt den Tag die Nacht umschlingen. Und Er hat die Sonne und den Mond dienstbar gemacht - ein jedes läuft auf eine festgesetzte Frist zu. Sicherlich, Er ist der Allmächtige und Allvergebende." });
                        surah39.VerseList.Add(new Verse { Number = 6, TextBosnian = "On vas od jedne osobe stvori - a od nje je drugu njegovu stvorio - i On vam je dao osam vrsta stoke; On vas stvara u utrobama majki vaših, dajući vam oblike, jedan za drugim, u tri tmine. To vam je, eto, Allah, Gospodar vaš, Njegova je vlast, nema boga osim Njega, pa kuda se onda okrećete?", TextEnglish = "He created you from one soul. Then He made from it its mate, and He produced for you from the grazing livestock eight mates.[1342] He creates you in the wombs of your mothers, creation after creation, within three darknesses.[1343] That is Allāh, your Lord; to Him belongs dominion. There is no deity except Him, so how are you averted?", TextGerman = "Er schuf euch aus einem einzigen Wesen, hierauf machte Er aus ihm seine Gattin. Und Er hat für euch an Vieh acht (Tiere) in Paaren herabgesandt. Er erschafft euch in den Leibern eurer Mütter, eine Schöpfung nach der anderen in dreifacher Finsternis. Dies ist doch Allah, euer Herr. Ihm gehört die Herrschaft. Es gibt keinen Gott außer Ihm. Wie laßt ihr euch also abwenden?" });
                        surah39.VerseList.Add(new Verse { Number = 7, TextBosnian = "Ako vi ne budete vjerovali, pa, Allahu vi niste potrebni, ali On nije zadovoljan nevjerovanjem Svojih robova. A ako budete zahvalni, On će tim biti zadovoljan. A nijedna duša neće nositi grijehe druge! Poslije ćete se svome Gospodaru vratiti, pa će vas On o onom što ste radili obavijestiti. Zaista, On zna šta se u grudima krije.", TextEnglish = "If you disbelieve - indeed, Allāh is Free from need of you. And He does not approve for His servants disbelief. And if you are grateful, He approves [i.e., likes] it for you; and no bearer of burdens will bear the burden of another. Then to your Lord is your return, and He will inform you about what you used to do. Indeed, He is Knowing of that within the breasts.", TextGerman = "Wenn ihr ungläubig seid, so ist Allah eurer unbedürftig, obgleich Er mit dem Unglauben für Seine Diener nicht zufrieden ist. Wenn ihr aber dankbar seid, ist Er damit zufrieden für euch. Und keine lasttragende (Seele) nimmt die Last einer anderen auf sich. Hierauf wird eure Rückkehr zu eurem Herrn sein. Dann wird Er euch kundtun, was ihr zu tun pflegtet. Gewiß, Er weiß über das Innerste der Brüste Bescheid." });
                        surah39.VerseList.Add(new Verse { Number = 8, TextBosnian = "Kad čovjeka nevolja snađe, Gospodaru svome se moli, Njemu se obraća, a onda, pošto mu Allah blagodat Svoju daruje, zaboravi Onoga Kome se prije molio, i druge Njemu jednakim smatra, da bi s puta Njegova na stranputicu odvodio. Reci: 'Uživaj neko vrijeme u nevjerovanju svome, bit ćeš, sigurno, stanovnik u Vatri!'", TextEnglish = "And when adversity touches man, he calls upon his Lord, turning to Him [alone]; then when He bestows on him a favor from Himself, he forgets Him whom he called upon before,[1344] and he attributes to Allāh equals to mislead [people] from His way. Say, 'Enjoy your disbelief for a little; indeed, you are of the companions of the Fire.'", TextGerman = "Und wenn dem Menschen Unheil widerfährt, ruft er seinen Herrn an, indem er sich Ihm reuig zuwendet. Wenn Er ihm hierauf Gunst von Sich gewährt, vergißt er, worum er zuvor angerufen hat, und er stellt Allah andere als Seinesgleichen zur Seite, um (die Menschen) von Seinem Weg ab in die Irre zu führen. Sag: Genieße deinen Unglauben ein wenig; du gehörst ja zu den Insassen des (Höllen)feuers." });
                        surah39.VerseList.Add(new Verse { Number = 9, TextBosnian = "Zar je takav kao onaj koji u noćnim satima u molitvi vrijeme provodi, padajući licem na tlo i stojeći, strahujući od ahireta i nadajući se milosti Gospodara svoga? Reci: 'Zar su isti oni koji znaju i oni koji ne znaju?! Samo se prisjećaju oni koji pameti imaju!'", TextEnglish = "Is one who is devoutly obedient during periods of the night, prostrating and standing [in prayer], fearing the Hereafter and hoping for the mercy of his Lord, [like one who does not]? Say, 'Are those who know equal to those who do not know?' Only they will remember [who are] people of understanding.", TextGerman = "Ist etwa einer, der sich zu (verschiedenen) Stunden der Nacht in demütiger Andacht befindet, (ob er) sich niederwirft oder aufrecht steht, der sich vor dem Jenseits vorsieht und auf seines Herrn Barmherzigkeit hofft ...? - Sag: Sind etwa diejenigen, die wissen, und diejenigen, die nicht wissen, gleich? Doch bedenken nur diejenigen, die Verstand besitzen." });
                        surah39.VerseList.Add(new Verse { Number = 10, TextBosnian = "Reci: 'O robovi Moji koji vjerujete, bojte se Gospodara svoga! Oni koji na ovom dunjaluku dobra djela budu činili imat će dobro, a Allahova je zemlja prostrana; samo će onima koji budu strpljivi nagrada njihova biti isplaćena bez obračuna. '", TextEnglish = "Say,[1345] 'O My servants who have believed, fear your Lord. For those who do good in this world is good, and the earth of Allāh is spacious. Indeed, the patient will be given their reward without account [i.e., limit].'", TextGerman = "Sag: O meine Diener, die ihr gläubig seid, fürchtet euren Herrn. Für diejenigen, die Gutes tun, gibt es hier im Diesseits Gutes. Und Allahs Erde ist weit. Gewiß, den Standhaften wird ihr Lohn ohne Berechnung in vollem Maß zukommen." });
                        surah39.VerseList.Add(new Verse { Number = 11, TextBosnian = "Reci: 'Naređeno mi je da ibadet Allahu činim, ispovijedajući Mu čistu vjeru.", TextEnglish = "Say, [O Muḥammad], 'Indeed, I have been commanded to worship Allāh, [being] sincere to Him in religion.", TextGerman = "Sag: Mir ist befohlen worden, Allah zu dienen und dabei Ihm gegenüber aufrichtig in der Religion zu sein." });
                        surah39.VerseList.Add(new Verse { Number = 12, TextBosnian = "I naređeno mi je da budem prvi musliman.", TextEnglish = "And I have been commanded to be the first [among you] of the Muslims.'", TextGerman = "Und mir ist befohlen worden, der erste der (Ihm) Ergebenen zu sein." });
                        surah39.VerseList.Add(new Verse { Number = 13, TextBosnian = "Reci: 'Ja se bojim patnje na Velikom danu, ako Gospodaru svome budem neposlušan.'", TextEnglish = "Say, 'Indeed I fear, if I should disobey my Lord, the punishment of a tremendous Day.'", TextGerman = "Sag: Gewiß, ich fürchte, wenn ich mich meinem Herrn widersetze, die Strafe eines gewaltigen Tages." });
                        surah39.VerseList.Add(new Verse { Number = 14, TextBosnian = "Reci: 'Samo Allahu ibadet činim, ispovijedajući Mu čistu vjeru,", TextEnglish = "Say, 'Allāh [alone] do I worship, sincere to Him in my religion,", TextGerman = "Sag: Allah (allein) diene ich und bin dabei Ihm gegenüber aufrichtig in meiner Religion." });
                        surah39.VerseList.Add(new Verse { Number = 15, TextBosnian = "a vi, pored Njega, obožavajte koga hoćete!' Reci: 'Gubitnici su, uistinu, oni koji na Kijametskom danu izgube i sebe i porodice svoje. Eto, to je očevidan gubitak!'", TextEnglish = "So worship what you will besides Him.' Say, 'Indeed, the losers are the ones who will lose themselves and their families on the Day of Resurrection. Unquestionably, that is the manifest loss.'", TextGerman = "So dient nun, wem ihr wollt, anstatt Seiner. Sag: Die (wahren) Verlierer sind diejenigen, die am Tag der Auferstehung sich selbst und ihre Angehörigen verlieren. Sicherlich, das ist der deutliche Verlust." });
                        surah39.VerseList.Add(new Verse { Number = 16, TextBosnian = "Nad njima će biti natkrilnice od vatre, a i ispod njih natkrilnice; time Allah straši robove Svoje. O robovi Moji, bojte se Mene!", TextEnglish = "They will have canopies [i.e., layers] of fire above them and below them, canopies. By that Allāh threatens [i.e., warns] His servants. O My servants, then fear Me.", TextGerman = "Sie haben über sich Schattendächer von Feuer und unter sich (ebensolche) Schattendächer. Damit flößt Allah Seinen Dienern Furcht ein: O Meine Diener, fürchtet Mich also." });
                        surah39.VerseList.Add(new Verse { Number = 17, TextBosnian = "Za one koji izbjegavaju obožavanje taguta - kumira i koji se Allahu obraćaju - njima su namijenjene radosne vijesti, zato obraduj robove Moje", TextEnglish = "But those who have avoided ṭāghūt,[1346] lest they worship it, and turned back to Allāh - for them are good tidings. So give good tidings to My servants", TextGerman = "Diejenigen aber, die die falschen Götter meiden, um ihnen nicht zu dienen, und sich Allah reuig zuwenden, für sie gibt es die frohe Botschaft. So verkünde frohe Botschaft Meinen Dienern," });
                        surah39.VerseList.Add(new Verse { Number = 18, TextBosnian = "koji govor slušaju, pa slijede ono što je bolje u njemu; to su oni koje je Allah na Pravi put uputio, i oni baš pameti imaju.", TextEnglish = "Who listen to speech and follow the best of it. Those are the ones Allāh has guided, and those are people of understanding.", TextGerman = "die auf das Wort hören und dann dem Besten davon folgen. Das sind diejenigen, die Allah rechtleitet, und das sind diejenigen, die Verstand besitzen." });
                        surah39.VerseList.Add(new Verse { Number = 19, TextBosnian = "Zar možeš da uputiš onoga koji je zaslužio da se nad njim obistine riječi kazne, zar možeš da izbaviš ko u Vatri je?!", TextEnglish = "Then, is one who has deserved the decree of punishment [to be guided]? Then, can you save one who is in the Fire?", TextGerman = "Kannst du etwa jemanden retten, der im (Höllen)feuer ist... -jemanden, gegen den der Spruch der Strafe unvermeidlich fällig geworden ist?" });
                        surah39.VerseList.Add(new Verse { Number = 20, TextBosnian = "A one koji se Gospodara svoga boje čekaju odaje, sve jedne iznad drugih sagrađene, ispod kojih rijeke teku - obećanje je Allahovo, a Allah neće obećanje prekršiti.", TextEnglish = "But those who have feared their Lord - for them are chambers,[1347] above them chambers built high, beneath which rivers flow. [This is] the promise of Allāh. Allāh does not fail in [His] promise.", TextGerman = "Aber diejenigen, die ihren Herrn fürchten, für sie wird es Obergemächer geben, über denen (andere) Obergemächer gebaut sind, unterhalb derer Flüsse strömen. (Das ist) Allahs Versprechen. Allah bricht nicht, was Er versprochen hat." });
                        surah39.VerseList.Add(new Verse { Number = 21, TextBosnian = "Zar ne vidiš da Allah spušta s neba vodu, pa je kao izvore po zemlji razvodi, a onda pomoću nje raznobojno bilje izvodi, zatim se osuši i ti ga vidiš požutjelo, i najzad ga skrši?! To je, doista, opomena za one koji pameti imaju.", TextEnglish = "Do you not see that Allāh sends down rain from the sky and makes it flow as springs [and rivers] in the earth; then He produces thereby crops of varying colors; then they dry and you see them turned yellow; then He makes them [scattered] debris. Indeed in that is a reminder for those of understanding.", TextGerman = "Siehst du nicht, daß Allah vom Himmel Wasser herabkommen und es dann als Quellen die Erde durchziehen läßt? Hierauf bringt Er damit Pflanzen von unterschiedlichen Arten hervor. Hierauf läßt Er sie austrocknen, und da siehst du sie gelb werden. Hierauf macht Er sie zu zermalmtem Zeug. Darin ist wahrlich eine Ermahnung für diejenigen, die Verstand besitzen." });
                        surah39.VerseList.Add(new Verse { Number = 22, TextBosnian = "Zar onaj čije je srce Allah učinio sklonim islamu, pa on slijedi svjetlo Gospodara svoga ... ?! Teško onima čija su srca tvrda kad se spomene Allah; oni su u očitaj zabludi!", TextEnglish = "So is one whose breast Allāh has expanded to [accept] Islām and he is upon [i.e., guided by] a light from his Lord [like one whose heart rejects it]? Then woe to those whose hearts are hardened against the remembrance of Allāh. Those are in manifest error.", TextGerman = "Ist denn jemand, dessen Brust Allah für den Islam auftut, so daß er sich nach einem Licht von seinem Herrn richtet, (einem beharrlich Ungläubigen gleich)? So wehe denjenigen, deren Herzen gegen Allahs Ermahnung verhärtet sind! Sie befinden sich in deutlichem Irrtum." });
                        surah39.VerseList.Add(new Verse { Number = 23, TextBosnian = "Allah objavljuje najljepši govor, Knjigu sličnu po smislu, čije se pouke ponavljaju. Od nje se koža ježi u onih koji se Gospodara svoga boje, a zatim, kad se spomene ime Allahovo, kože se njihove i srca njihova smiruju. To je Allahova Uputa kojom On upućuje koga hoće; a onoga kome Allah da daje u zabludi, njemu nema uputitelja.", TextEnglish = "Allāh has sent down the best statement: a consistent Book wherein is reiteration. The skins shiver therefrom of those who fear their Lord; then their skins and their hearts relax at the remembrance [i.e., mention] of Allāh. That is the guidance of Allāh by which He guides whom He wills. And one whom Allāh sends astray - for him there is no guide.", TextGerman = "Allah hat die beste Botschaft offenbart, ein Buch mit gleichartigen, sich wiederholenden (Versen), vor dem die Haut derjenigen, die ihren Herrn fürchten, erschauert. Hierauf werden ihre Haut und ihr Herz weich (und neigen sich) zu Allahs Gedenken hin. Das ist Allahs Rechtleitung. Er leitet damit recht, wen Er will. Und wen Allah in die Irre gehen läßt, der hat niemanden, der ihn rechtleitet." });
                        surah39.VerseList.Add(new Verse { Number = 24, TextBosnian = "Zar onaj koji će se na Kijametskom danu licem svojim zaklanjati od zla patnje?! Zulumćarima bit će rečeno: 'Trpite ono što ste zaslužili!'", TextEnglish = "Then is he who will shield with his face[1348] the worst of the punishment on the Day of Resurrection [like one secure from it]? And it will be said to the wrongdoers, 'Taste what you used to earn.'", TextGerman = "Ist denn jemand, der sich mit seinem Gesicht vor der bösen Strafe am Tag der Auferstehung hütet, ...? Und es wird zu den Ungerechten gesagt: Kostet, was ihr erworben habt." });
                        surah39.VerseList.Add(new Verse { Number = 25, TextBosnian = "Oni prije njih poricali su, pa ih je stigla kazna odakle nisu očekivali.", TextEnglish = "Those before them denied, and punishment came upon them from where they did not perceive.", TextGerman = "Für Lüge erklärt haben es diejenigen, die vor ihnen waren. Da kam die Strafe über sie, von wo sie nicht merkten." });
                        surah39.VerseList.Add(new Verse { Number = 26, TextBosnian = "Allah je dao da osjete poniženje u životu na dunjaluku, a patnja na ahiretu bit će, zaista, još veća - kad bi oni samo znali!", TextEnglish = "So Allāh made them taste disgrace in worldly life. But the punishment of the Hereafter is greater, if they only knew.", TextGerman = "So ließ Allah sie im diesseitigen Leben die Schande kosten; aber die Strafe des Jenseits ist wahrlich größer, wenn sie nur wüßten!" });
                        surah39.VerseList.Add(new Verse { Number = 27, TextBosnian = "Mi u ovom Kur'anu navodimo ljudima svakojake primjere, da bi se prisjećali i pouku primili;", TextEnglish = "And We have certainly presented for the people in this Qur’ān from every [kind of] example - that they might remember.", TextGerman = "Und Wir haben ja den Menschen in diesem Qur’an allerlei Gleichnisse geprägt, auf daß sie bedenken mögen -," });
                        surah39.VerseList.Add(new Verse { Number = 28, TextBosnian = "u Kur'anu na arapskom jeziku, u kome nema nikakve proturječnosti, da bi se čuvali.", TextEnglish = "[It is] an Arabic Qur’ān, without any deviance[1349] that they might become righteous.[1350]", TextGerman = "(in diesem) arabischen Qur’an, an dem nichts Krummes ist, - auf daß sie gottesfürchtig werden mögen." });
                        surah39.VerseList.Add(new Verse { Number = 29, TextBosnian = "Allah navodi kao primjer čovjeka koji je u vlasti ortaka i za kojeg se oni otimaju, i čovjeka koji je u vlasti samo jednog čovjeka - da li je primjer njih dvojice isti?! Sva hvala neka je Allahu, ali većina njih ne zna.", TextEnglish = "Allāh presents an example: a man [i.e., slave] owned by quarreling partners and another belonging exclusively to one man - are they equal in comparison? Praise be to Allāh! But most of them do not know.", TextGerman = "Allah prägt als Gleichnis dasjenige von einem Mann, in dem sich (mehrere) Herren (als Eigentümer) teilen, die sich miteinander nicht vertragen, und einem Mann, der nur einem Herrn gehört. Sind die beiden im Gleichnis etwa gleich? (Alles) Lob gehört Allah! Aber die meisten von ihnen wissen nicht." });
                        surah39.VerseList.Add(new Verse { Number = 30, TextBosnian = "Ti ćeš, sigurno, umrijeti, a i oni će, također, pomrijeti.", TextEnglish = "Indeed, you are to die, and indeed, they are to die.", TextGerman = "Du wirst gewiß sterben, und auch sie werden sterben." });
                        surah39.VerseList.Add(new Verse { Number = 31, TextBosnian = "I poslije, na Kijametskom danu, pred Gospodarom svojim jedan s drugim ćete se sporiti.", TextEnglish = "Then indeed you, on the Day of Resurrection, before your Lord, will dispute.", TextGerman = "Hierauf werdet ihr am Tag der Auferstehung bei eurem Herrn miteinander streiten." });
                        surah39.VerseList.Add(new Verse { Number = 32, TextBosnian = "Ima li većeg zulumćara od onoga koji o Allahu govori laži i smatra Istinu lažnom kada mu dolazi?! Zar u Džehennemu prebivalište za nevjernike neće biti?!", TextEnglish = "So who is more unjust than one who lies about Allāh and denies the truth when it has come to him? Is there not in Hell a residence for the disbelievers?", TextGerman = "Wer ist denn ungerechter, als wer gegen Allah lügt und die Wahrheit, wenn sie zu ihm kommt, für Lüge erklärt? Ist nicht in der Hölle ein Aufenthaltsort für die Ungläubigen?" });
                        surah39.VerseList.Add(new Verse { Number = 33, TextBosnian = "A onaj koji donosi Istinu i oni koji u nju vjeruju, oni se čuvaju i boje,", TextEnglish = "And the one who has brought the truth [i.e., the Prophet (ﷺ)] and [they who] believed in it - those are the righteous.", TextGerman = "Derjenige aber, der mit der Wahrheit kommt (,) und (derjenige andere, der) sie für wahr hält, - das sind die Gottesfürchtigen." });
                        surah39.VerseList.Add(new Verse { Number = 34, TextBosnian = "sve što zažele u Gospodara svoga naći će, to će biti nagrada onima koji su dobra djela činili,", TextEnglish = "They will have whatever they desire with their Lord. That is the reward of the doers of good -", TextGerman = "Sie haben, was sie wollen, bei ihrem Herrn; das ist der Lohn der Gutes Tuenden," });
                        surah39.VerseList.Add(new Verse { Number = 35, TextBosnian = "da bi Allah preko ružnih postupaka njihovih prešao, i da bi ih prema onom boljem što su radili nagradio.", TextEnglish = "That Allāh may remove from them the worst of what they did and reward them their due for the best of what they used to do.", TextGerman = "auf daß Allah ihnen das Schlechteste von dem, was sie getan haben, tilge und ihnen ihren Lohn vergelte für das Beste von dem, was sie taten." });
                        surah39.VerseList.Add(new Verse { Number = 36, TextBosnian = "Zar Allah nije zaštitinik robu Svome?! A oni te plaše onima koje, pored Njega, obožavaju. Onoga kome Allah da da je u zabludi - niko ne može na Pravi put uputiti.", TextEnglish = "Is not Allāh sufficient for His Servant [i.e., Prophet Muḥammad (ﷺ)]? And [yet], they threaten you with those [they worship] other than Him. And whoever Allāh leaves astray - for him there is no guide.", TextGerman = "Wird Allah nicht Seinem Diener genügen? Dennoch wollen sie dir mit denjenigen, die es außer Ihm geben soll, Furcht einflößen. Und wen Allah in die Irre gehen läßt, der hat niemanden, der ihn rechtleitet." });
                        surah39.VerseList.Add(new Verse { Number = 37, TextBosnian = "A onoga koga On na Pravi put uputi - niko ne može u zabludu dovesti. Zar Allah nije silan i Onaj Koji kažnjava?!", TextEnglish = "And whoever Allāh guides - for him there is no misleader. Is not Allāh Exalted in Might and Owner of Retribution?", TextGerman = "Wen aber Allah rechtleitet, den kann niemand in die Irre führen. Ist nicht Allah Allmächtig und Besitzer von Vergeltungsgewalt?" });
                        surah39.VerseList.Add(new Verse { Number = 38, TextBosnian = "Ako ih upitaš ko je stvorio nebesa i Zemlju, sigurno će reći: 'Allah!' A ti reci: 'Mislite li vi, da li bi oni koje vi, pored Allaha, molite mogli otkloniti štetu, ako Allah hoće da mi je učini, ili, da li bi mogli zadržati milost Njegovu, ako On hoće da mi je podari Reci: 'Meni je dovoljan Allah, oni koji oslonac traže, na Njega se oslanjaju.'", TextEnglish = "And if you asked them, 'Who created the heavens and the earth?' they would surely say, 'Allāh.' Say, 'Then have you considered[1351] what you invoke besides Allāh? If Allāh intended me harm, are they removers of His harm; or if He intended me mercy, are they withholders of His mercy?' Say, 'Sufficient for me is Allāh; upon Him [alone] rely the [wise] reliers.'", TextGerman = "Und wenn du sie fragst, wer die Himmel und die Erde erschaffen hat, sagen sie ganz gewiß: Allah. Sag: Was meint ihr wohl zu dem, was ihr anstatt Allahs anruft; wenn Allah für mich Unheil will, können sie (dann) Sein Unheil hinwegnehmen? Oder wenn Er für mich Barmherzigkeit will, können sie (dann) Seine Barmherzigkeit zurückhalten? Sag: Meine Genüge ist Allah. Auf Ihn verlassen sich diejenigen, die sich (überhaupt auf jemanden) verlassen." });
                        surah39.VerseList.Add(new Verse { Number = 39, TextBosnian = "Reci: 'O narode moj, ti postupaj onako kako postupaš, a i ja ću postupati, pa ćete znati", TextEnglish = "Say, 'O my people, work according to your position, [for] indeed, I am working; and you are going to know", TextGerman = "Sag: O mein Volk, handelt nach eurer Stellung, ich werde (ebenfalls so) handeln. Dann werdet ihr erfahren," });
                        surah39.VerseList.Add(new Verse { Number = 40, TextBosnian = "koga će stići patnja koja će ga poniziti, i koga će patnja vječna snaći!'", TextEnglish = "To whom will come a torment disgracing him and on whom will descend an enduring punishment.'", TextGerman = "über wen eine Strafe kommen wird, die ihn in Schande stürzt, und (über wen) eine beständige Strafe hereinbrechen wird." });
                        surah39.VerseList.Add(new Verse { Number = 41, TextBosnian = "Mi tebi, doista, objavljujemo Knjigu s Istinom za svijet sav; onaj ko bude išao Pravim putem - sebi će koristiti, a onaj ko bude išao stranputicom - sebi će nauditi, ti nisi za njihovu uputu zadužen.", TextEnglish = "Indeed, We sent down to you the Book for the people in truth. So whoever is guided - it is for [the benefit of] his soul; and whoever goes astray only goes astray to its detriment. And you are not a manager [i.e., authority] over them.", TextGerman = "Gewiß, Wir haben für die Menschen das Buch mit der Wahrheit auf dich hinabgesandt. Wer sich nun rechtleiten läßt, der (tut das) zu seinem eigenen Vorteil; und wer in die Irre geht, der geht nur zu seinem eigenen Nachteil in die Irre. Und du bist nicht (als) Sachwalter über sie (eingesetzt)." });
                        surah39.VerseList.Add(new Verse { Number = 42, TextBosnian = "Allah uzima duše u času njihove smrti, a i onih koji spavaju, pa zadržava one kojima je odredio da umru, a ostavlja one druge do roka određenog. To su, zaista, znakovi za one koji razmišljaju.", TextEnglish = "Allāh takes the souls at the time of their death, and those that do not die [He takes] during their sleep. Then He keeps those for which He has decreed death and releases the others for a specified term. Indeed in that are signs for a people who give thought.", TextGerman = "Allah beruft die Seelen zur Zeit ihres Todes ab und auch diejenigen, die nicht gestorben sind, während ihres Schlafes’. Er hält die eine, für die Er den Tod beschlossen hat, zurück und gibt die andere auf eine festgesetzte Frist frei. Darin sind wahrlich Zeichen für Leute, die nachdenken." });
                        surah39.VerseList.Add(new Verse { Number = 43, TextBosnian = "Zar oni mimo Allaha zagovornike uzimaju?! Reci: 'Zar i onda kada su bez ikakve moći i kada ništa ne razumiju.", TextEnglish = "Or have they taken other than Allāh as intercessors? Say, 'Even though they do not possess [power over] anything, nor do they reason?'", TextGerman = "Oder haben sie sich anstatt Allahs Fürsprecher genommen? Sag: Auch wenn sie über nichts verfügen und nicht begreifen?" });
                        surah39.VerseList.Add(new Verse { Number = 44, TextBosnian = "Reci: 'Sve zagovorništvo pripada Allahu, vlast na nebesima i na Zemlji Njegova je, a poslije - Njemu ćete se vratiti.'", TextEnglish = "Say, 'To Allāh belongs [the right to allow] intercession entirely. To Him belongs the dominion of the heavens and the earth. Then to Him you will be returned.'", TextGerman = "Sag: Alle Fürsprache gehört Allah (allein). Ihm gehört die Herrschaft der Himmel und der Erde. Hierauf werdet ihr zu Ihm zurückgebracht." });
                        surah39.VerseList.Add(new Verse { Number = 45, TextBosnian = "Kad se spomene samo Allah, grče se srca onih koji u ahiret ne vjeruju, a kada se spomenu oni koje oni pored Njega obožavaju, odjednom ih radost obuzme.", TextEnglish = "And when Allāh is mentioned alone, the hearts of those who do not believe in the Hereafter shrink with aversion, but when those [worshipped] other than Him are mentioned, immediately they rejoice.", TextGerman = "Und wenn Allah allein erwähnt wird, verkrampfen sich die Herzen derjenigen, die nicht an das Jenseits glauben. Wenn aber diejenigen erwähnt werden, die es außer Ihm geben soll, freuen sie sich sogleich." });
                        surah39.VerseList.Add(new Verse { Number = 46, TextBosnian = "Reci: 'Allahu, Koji si stvorio nebesa i Zemlju bez prethodnog primjera, Ti Koji znaš ono što je čulima nedokučivo i ono što je pojavno, Ti ćeš robovima Svojim presuditi u onome u čemu su se razilazili!'", TextEnglish = "Say, 'O Allāh, Creator of the heavens and the earth, Knower of the unseen and the witnessed, You will judge between your servants concerning that over which they used to differ.'", TextGerman = "Sag: O Allah, Erschaffer der Himmel und der Erde, Kenner des Verborgenen und des Offenbaren! Du wirst zwischen Deinen Dienern über das richten, worüber sie uneinig zu sein pflegten." });
                        surah39.VerseList.Add(new Verse { Number = 47, TextBosnian = "Kad bi oni koji zulum čine imali sve što je na Zemlji, i još toliko, na Kijametskom danu oni bi sve to dali kako bi se od teške patnje iskupili; a od Allaha će im se ukazati kazna na koju ni mislili nisu;", TextEnglish = "And if those who did wrong had all that is in the earth entirely and the like of it with it, they would [attempt to] ransom themselves thereby from the worst of the punishment on the Day of Resurrection. And there will appear to them from Allāh that which they had not taken into account.[1352]", TextGerman = "Wenn diejenigen, die Unrecht tun, alles hätten, was auf der Erde ist, und noch einmal das Gleiche dazu, würden sie sich sicherlich damit von der bösen Strafe am Tag der Auferstehung loskaufen. Ihnen wird dann (nämlich) von Allah her klar, womit sie nicht gerechnet haben." });
                        surah39.VerseList.Add(new Verse { Number = 48, TextBosnian = "i ukazat će im se hrđava djela njihova koja su radili i skolit će ih baš ono čemu su se izrugivali.", TextEnglish = "And there will appear to them the evils they had earned, and they will be enveloped by what they used to ridicule.", TextGerman = "Und ihnen werden die bösen Taten klar, die sie begangen haben, und es umschließt sie das, worüber sie sich lustig zu machen pflegten." });
                        surah39.VerseList.Add(new Verse { Number = 49, TextBosnian = "Kad čovjeka kakva nevolja snađe, Nama se moli; a kad mu Mi poslije blagodat pružimo, onda govori: 'Ovo mi je dato na osnovu znanja.' A nije tako, to je samo kušnja, ali većina njih ne zna.", TextEnglish = "And when adversity touches man, he calls upon Us; then when We bestow on him a favor from Us, he says, 'I have only been given it because of [my] knowledge.' Rather, it is a trial, but most of them do not know.", TextGerman = "Wenn dem Menschen Unheil widerfährt, ruft er Uns an. Wenn Wir ihm hierauf Gunst von Uns gewähren, sagt er: Es ist mir nur gegeben worden aufgrund von Wissen. Nein! Vielmehr ist es eine Versuchung. Aber die meisten von ihnen wissen nicht." });
                        surah39.VerseList.Add(new Verse { Number = 50, TextBosnian = "Tako su govorili i oni prije njih, pa im nije koristilo ono što su bili stekli,", TextEnglish = "Those before them had already said it, but they were not availed by what they used to earn.", TextGerman = "Das haben bereits diejenigen, die vor ihnen waren, gesagt. Aber nicht nützte ihnen, was sie zu. erwerben pflegten." });
                        surah39.VerseList.Add(new Verse { Number = 51, TextBosnian = "pa ih pogodi kazna za zla djela koja su zaradili. A i one među ovima koji zulum čine - stići će kazna za ono što čine, oni neće umaći.", TextEnglish = "And the evil consequences of what they earned struck them. And those who have wronged of these [people] will be struck [i.e., afflicted] by the evil consequences of what they earned; and they will not cause failure.[1353]", TextGerman = "So trafen sie (schließlich) die bösen Taten, die sie begangen hatten. Und (auch) diejenigen unter diesen da, die Unrecht tun, werden die bösen Taten treffen, die sie begangen haben. Und sie können sich (Allah) nicht entziehen." });
                        surah39.VerseList.Add(new Verse { Number = 52, TextBosnian = "Zar oni ne znaju da Allah daje opskrbu u izobilju onome kome hoće, i da uskraćuje; to su, zaista, znakovi narodu koji vjeruje.", TextEnglish = "Do they not know that Allāh extends provision for whom He wills and restricts [it]? Indeed in that are signs for a people who believe.", TextGerman = "Wissen sie denn nicht, daß Allah die Versorgung großzügig gewährt, wem Er will, und auch bemißt? Darin sind wahrlich Zeichen für Leute, die glauben." });
                        surah39.VerseList.Add(new Verse { Number = 53, TextBosnian = "Reci: 'O robovi Moji, koji ste protiv sebe u grijesima pretjerivali, ne gubite nadu u Allahovu milost! Allah će, sigurno, sve grijehe oprostiti. On je, doista, Oprostitelj grijeha, Milostivi.'", TextEnglish = "Say, 'O My servants who have transgressed against themselves [by sinning], do not despair of the mercy of Allāh. Indeed, Allāh forgives all sins.[1354] Indeed, it is He who is the Forgiving, the Merciful.'", TextGerman = "Sag: O Meine Diener, die ihr gegen euch selbst maßlos gewesen seid, verliert nicht die Hoffnung auf Allahs Barmherzigkeit. Gewiß, Allah vergibt die Sünden alle. Er ist ja der Allvergebende und Barmherzige." });
                        surah39.VerseList.Add(new Verse { Number = 54, TextBosnian = "I povratite se Gospodaru svome i pokorite Mu se prije nego što vam kazna dođe - poslije vam niko neće u pomoć priskočiti.", TextEnglish = "And return [in repentance] to your Lord and submit to Him before the punishment comes upon you; then you will not be helped.", TextGerman = "Und wendet euch eurem Herrn reuig zu und seid Ihm ergeben, bevor die Strafe über euch kommt, worauf euch keine Hilfe zuteil werden wird." });
                        surah39.VerseList.Add(new Verse { Number = 55, TextBosnian = "I slijedite ono najbolje što vam Gospodar vaš objavljuje, prije nego što vam iznenada kazna dođe, za čiji dolazak nećete znati.", TextEnglish = "And follow the best of what was revealed to you from your Lord [i.e., the Qur’ān] before the punishment comes upon you suddenly while you do not perceive,", TextGerman = "Und folgt dem Besten von dem, was zu euch von eurem Herrn (als Offenbarung) herabgesandt worden ist, bevor die Strafe plötzlich über euch kommt, ohne daß ihr merkt," });
                        surah39.VerseList.Add(new Verse { Number = 56, TextBosnian = "Da čovjek ne bi uzviknuo: 'Teško meni, koliko sam samo dužnosti prema Allahu propustio, doista sam i u one koji se izruguju spadao!'", TextEnglish = "Lest a soul should say,[1355] 'Oh, [how great is] my regret over what I neglected in regard to Allāh and that I was among the mockers.'", TextGerman = "daß ja keine Seele sage(n muß): O welch gramvolle Reue für mich wegen dessen, was ich (an Pflichten) gegenüber Allah vernachlässigte. Ich gehörte ja wirklich zu den Spöttern." });
                        surah39.VerseList.Add(new Verse { Number = 57, TextBosnian = "Ili, da ne bi rekao: 'Da me je Allah Pravim putem uputio, sigurno bih među bogobojaznim bio.'", TextEnglish = "Or [lest] it say, 'If only Allāh had guided me, I would have been among the righteous.'", TextGerman = "Oder daß sie nicht etwa sage(n soll): Wenn Allah mich nur rechtgeleitet hätte, würde ich bestimmt zu den Gottesfürchtigen gehören." });
                        surah39.VerseList.Add(new Verse { Number = 58, TextBosnian = "Ili, da ne bi rekao kad doživi patnju: 'Da mi se samo vratiti-među dobročinitelje bih se svrstao!'", TextEnglish = "Or [lest] it say when it sees the punishment, 'If only I had another turn[1356] so I could be among the doers of good.'", TextGerman = "Oder daß sie nicht etwa, wenn sie die Strafe sieht, sage(n soll): Wenn ich doch nur eine (Möglichkeit zur) Wiederholung hätte, dann würde ich zu den Gutes Tuenden gehören!" });
                        surah39.VerseList.Add(new Verse { Number = 59, TextBosnian = "Da, već su ti dolazili Moji znaci, pa si ih poricao i oholio se, i u nevjernike si spadao.", TextEnglish = "But yes, there had come to you My verses, but you denied them and were arrogant, and you were among the disbelievers.", TextGerman = "Aber ja! Meine Zeichen sind doch zu dir gekommen. Da hast du sie für Lüge erklärt und dich hochmütig verhalten und hast zu den Ungläubigen gehört." });
                        surah39.VerseList.Add(new Verse { Number = 60, TextBosnian = "Na Kijametskom danu vidjet ćeš one koji su o Allahu laži govorili, a lica im pocrnjela. A zar u Džehennemu neće biti boravište oholih?!", TextEnglish = "And on the Day of Resurrection you will see those who lied about Allāh [with] their faces blackened. Is there not in Hell a residence for the arrogant?", TextGerman = "Und am Tag der Auferstehung siehst du diejenigen, die gegen Allah gelogen haben; ihre Gesichter sind schwarz geworden. Ist nicht in der Hölle ein Aufenthaltsort für die Hochmütigen?" });
                        surah39.VerseList.Add(new Verse { Number = 61, TextBosnian = "Allah će spasiti one koji su Njegova naređenja izvršavali a Njegovih se zabrana klonili, zbog njihova uspjeha; zlo ih neće doticati i oni neće tugovati.", TextEnglish = "And Allāh will save those who feared Him by their attainment;[1357] no evil will touch them, nor will they grieve.", TextGerman = "Aber Allah errettet diejenigen, die gottesfürchtig waren, indem Er sie entrinnen läßt. Böses wird ihnen nicht widerfahren, noch werden sie traurig sein." });
                        surah39.VerseList.Add(new Verse { Number = 62, TextBosnian = "Allah je Stvoritelj svega i On upravlja svim,", TextEnglish = "Allāh is the Creator of all things, and He is, over all things, Disposer of affairs.", TextGerman = "Allah ist der Schöpfer von allem, und Er ist Sachwalter über alles." });
                        surah39.VerseList.Add(new Verse { Number = 63, TextBosnian = "u Njega su ključevi nebesa i Zemlje! A oni koji u Allahove ajete i znakove ne vjeruju - oni će baš biti gubitnici!", TextEnglish = "To Him belong the keys of the heavens and the earth. And they who disbelieve in the verses of Allāh - it is those who are the losers.", TextGerman = "Ihm gehören die Schlüssel der Himmel und der Erde. Diejenigen, die Allahs Zeichen verleugnen, das sind die Verlierer." });
                        surah39.VerseList.Add(new Verse { Number = 64, TextBosnian = "Reci: 'Zar od mene tražite da nekoga drugog, osim Allaha, obožavam, o neznalice!'", TextEnglish = "Say, [O Muḥammad], 'Is it other than Allāh that you order me to worship, O ignorant ones?'", TextGerman = "Sag: Wollt ihr mir denn wirklich befehlen, einem anderen als Allah zu dienen, ihr Toren?" });
                        surah39.VerseList.Add(new Verse { Number = 65, TextBosnian = "A tebi, i onima prije tebe, objavljeno je: 'Ako budeš druge Allahu u obožavanju pridruživao tvoja djela će sigurno propasti, a ti ćeš među gubitnicima biti.'", TextEnglish = "And it was already revealed to you and to those before you that if you should associate [anything] with Allāh, your work would surely become worthless, and you would surely be among the losers.", TextGerman = "Dir und denjenigen, die vor dir waren, ist ja (als Offenbarung) eingegeben worden: Wenn du (Allah andere) beigesellst, wird dein Werk ganz gewiß hinfällig, und du gehörst ganz gewiß zu den Verlierern." });
                        surah39.VerseList.Add(new Verse { Number = 66, TextBosnian = "Nego, Allahu jedino ibadet čini i među zahvalnima budi!", TextEnglish = "Rather, worship [only] Allāh and be among the grateful.", TextGerman = "Nein! Vielmehr Allah (allein) sollst du dienen und zu den Dankbaren sollst du gehören." });
                        surah39.VerseList.Add(new Verse { Number = 67, TextBosnian = "Oni nisu veličali Allaha onako kako Ga treba veličati; a čitava će Zemlja na Kijametskom danu u šaci Njegovoj biti, a nebesa će u desnici Njegovoj smotana ostati. Čist je On i Uzvišen iznad onih koje Njemu smatraju ravnim!", TextEnglish = "They have not appraised Allāh with true appraisal,[1358] while the earth entirely will be [within] His grip[1359] on the Day of Resurrection, and the heavens will be folded in His right hand.[1360] Exalted is He and high above what they associate with Him.", TextGerman = "Sie haben Allah nicht eingeschätzt, wie es Ihm gebührt’, wo die ganze Erde am Tag der Auferstehung in Seiner Hand gehalten wird und (auch) die Himmel in Seiner Rechten zusammengefaltet sein werden. Preis sei Ihm! Erhaben ist Er über das, was sie (Ihm) beigesellen." });
                        surah39.VerseList.Add(new Verse { Number = 68, TextBosnian = "I u rog će biti puhnuto, i umrijet će oni na nebesima i oni na Zemlji, ostat će samo oni koje bude Allah odabrao; poslije će se u rog drugi put puhnuti i oni će, odjednom, ustati i čekati.", TextEnglish = "And the Horn will be blown, and whoever is in the heavens and whoever is on the earth will fall dead except whom Allāh wills. Then it will be blown again, and at once they will be standing, looking on.", TextGerman = "Und es wird ins Horn geblasen, und da bricht zusammen, wie vom Donnerschlag getroffen, wer in den Himmeln und wer auf der Erde ist, außer wem Allah will. Hierauf wird ein weiteres Mal hineingeblasen, da.stehen sie sogleich auf und schauen hin." });
                        surah39.VerseList.Add(new Verse { Number = 69, TextBosnian = "I Zemlja će svjetlošću Gospodara svoga zasjati, i Knjiga će postavljena biti, vjerovjesnici i svjedoci bit će dovedeni, i po pravdi će im svima presuđeno biti, nikome zulum neće učinjen biti;", TextEnglish = "And the earth will shine with the light of its Lord, and the record [of deeds] will be placed, and the prophets and the witnesses will be brought, and it will be judged between them in truth, and they will not be wronged.", TextGerman = "Und die Erde wird im Licht ihres Herrn erstrahlen; das Buch wird hingelegt, und die Propheten und die Zeugen werden herbeigebracht; und es wird zwischen ihnen der Wahrheit entsprechend entschieden, und es wird ihnen kein Unrecht zugefügt." });
                        surah39.VerseList.Add(new Verse { Number = 70, TextBosnian = "svako će u potpunosti dobiti ono što je zaslužio, a On najbolje zna šta svi rade.", TextEnglish = "And every soul will be fully compensated [for] what it did; and He is most knowing[1361] of what they do.", TextGerman = "Und jeder Seele wird in vollem Maß zukommen, was sie getan hat. Er weiß sehr wohl Bescheid über das, was sie tun." });
                        surah39.VerseList.Add(new Verse { Number = 71, TextBosnian = "Oni koji nisu vjerovali u gomilama će u Džehennem biti natjerani, i kad do njega dođu, kapije njegove će pootvarane biti i čuvari će ih upitati: 'Zar vam nisu dolazili vaši poslanici, koji su vam ajete Gospodara vašeg kazivali, i upozoravali vas da ćete ovaj svoj dan doživjeti?!' 'Jesu', reći će oni, 'ali su se obistinile riječi o kazni za nevjernike.'", TextEnglish = "And those who disbelieved will be driven to Hell in groups until, when they reach it, its gates are opened and its keepers will say, 'Did there not come to you messengers from yourselves, reciting to you the verses of your Lord and warning you of the meeting of this Day of yours?' They will say, 'Yes, but the word [i.e., decree] of punishment has come into effect upon the disbelievers.'", TextGerman = "Zur Hölle getrieben werden diejenigen, die ungläubig waren, in Scharen. Wenn sie dann dort ankommen, werden ihre Tore geöffnet und ihre Wärter sagen zu ihnen: Sind nicht zu euch Gesandte von euch (selbst) gekommen, die euch die Zeichen eures Herrn verlesen und euch die Begegnung mit diesem eurem Tag warnend ankündigen? Sie werden sagen: Ja doch! Aber das Wort der Strafe ist gegen die Ungläubigen unvermeidlich fällig geworden." });
                        surah39.VerseList.Add(new Verse { Number = 72, TextBosnian = "I bit će rečeno: 'Ulazite na džehennemske kapije, vječno ćete u njemu boraviti! Grozna li prebivališta onima koji su se oholili!'", TextEnglish = "[To them] it will be said, 'Enter the gates of Hell to abide eternally therein, and wretched is the residence of the arrogant.'", TextGerman = "Es wird (zu ihnen) gesagt werden: Betretet die Tore der Hölle, ewig darin zu bleiben. Schlimm ist der Aufenthaltsort der Hochmütigen!" });
                        surah39.VerseList.Add(new Verse { Number = 73, TextBosnian = "A oni koji su se Gospodara svoga bojali u povorkama će u Džennet biti povedeni, i kad do njega dođu - kapije njegove već širom otvorene - čuvari će im njegovi reći: 'Selam vama, od grijeha ste čisti, zato uđite u njega, u njemu ćete vječno boraviti!'", TextEnglish = "But those who feared their Lord will be driven to Paradise in groups until, when they reach it while its gates have been opened and its keepers say, 'Peace be upon you; you have become pure; so enter it to abide eternally therein,' [they will enter].[1362]", TextGerman = "Und in den (Paradies)garten geführt werden diejenigen, die ihren Herrn fürchteten, in Scharen. Wenn sie dann dort ankommen und seine Tore geöffnet werden und seine Wärter zu ihnen sagen: Friede sei auf euch! Gut wart ihr, so betretet ihn, ewig (darin) zu bleiben." });
                        surah39.VerseList.Add(new Verse { Number = 74, TextBosnian = "I oni će reći: 'Sva hvala neka je Allahu Koji nam je obećanje Svoje ispunio, i u Džennetu nam mjesto darovao, da se u njemu nastanimo gdje hoćemo. Divne li nagrade onima koji su se trudili!'", TextEnglish = "And they will say, 'Praise to Allāh, who has fulfilled for us His promise and made us inherit the earth [so] we may settle in Paradise wherever we will. And excellent is the reward of [righteous] workers.'", TextGerman = "Und sie werden sagen: (Alles) Lob gehört Allah, Der Sein Versprechen an uns gehalten und uns die Erde zum Erbe gegeben hat, so daß wir uns im (Paradies)garten aufhalten können, wo immer wir wollen! Wie trefflich ist der Lohn derjenigen, die (gut) handeln!" });
                        surah39.VerseList.Add(new Verse { Number = 75, TextBosnian = "I vidjet ćeš meleke kako Arš okružuju, veličajući i hvaleći Gospodara svoga; i svima je po pravdi presuđeno, i bit će rečeno: 'Sva hvala neka je Allahu, Gospodaru svjetova!'", TextEnglish = "And you will see the angels surrounding the Throne, exalting [Allāh] with praise of their Lord. And it will be judged between them in truth, and it will be said, '[All] praise to Allāh, Lord of the worlds.'", TextGerman = "Und du wirst die Engel den Thron umgeben und ihren Herrn lobpreisen sehen. Und es wird zwischen ihnen der Wahrheit entsprechend entschieden. Und es wird gesagt: (Alles) Lob gehört Allah, dem Herrn der Weltenbewohner!" });
                        result = surah39;
                        break;
                    #endregion

                    // 
                    #region
                    case 40:
                        Surah surah40 = new Surah(); surah40.Name = Constants.Surah40Name; surah40.Number = 40; surah40.VerseList = new List<Verse>();
                        surah40.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah40.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ha-mim.", TextEnglish = "Ḥā, Meem.[1364]", TextGerman = "Ha-Mim." });
                        surah40.VerseList.Add(new Verse { Number = 2, TextBosnian = "Objavljivanje Knjige je od Allaha Silnoga, Sveznajućeg,", TextEnglish = "The revelation of the Book [i.e., the Qur’ān] is from Allāh, the Exalted in Might, the Knowing,", TextGerman = "(Dies ist) die Offenbarung des Buches von Allah, dem Allmächtigen und Allwissenden," });
                        surah40.VerseList.Add(new Verse { Number = 3, TextBosnian = "Oprostitelja grijeha i Primatelja pokajanja, Onoga Koji žestoko kažnjava i Koji je Posjednik obilnog dobra; istinskog Boga osim Njega nema, kod Njega je konačno odredište.", TextEnglish = "The forgiver of sin, acceptor of repentance, severe in punishment, owner of abundance. There is no deity except Him; to Him is the destination.", TextGerman = "dem Vergebenden der Sünde und dem Anehmenden der Reue, dem Strengen in der Bestrafung und Dem, Der voller Wohltätigkeit ist. Es gibt keinen Gott außer Ihm. Zu Ihm ist der Ausgang." });
                        surah40.VerseList.Add(new Verse { Number = 4, TextBosnian = "O Allahovim ajetima i znakovima raspravljaju samo oni koji ne vjeruju, pa neka te ne obmanjuje to što oni putujući po zemlji uživaju.", TextEnglish = "No one disputes concerning the signs of Allāh except those who disbelieve, so be not deceived by their [uninhibited] movement throughout the land.", TextGerman = "Über Allahs Zeichen streiten nur diejenigen, die ungläubig sind. Lasse dich durch ihren Wechsel in den Landstrichen nicht täuschen." });
                        surah40.VerseList.Add(new Verse { Number = 5, TextBosnian = "I Nuhov je narod prije njih poricao, a i skupine poslije njega. Svaki narod nastojao je ščepati svoga poslanika, i rasprave svoje na neistini temeljio da njome Istinu uništi, pa sam ga Ja kažnjavao - a kakva je bila kazna Moja!", TextEnglish = "The people of Noah denied before them and the [disbelieving] factions after them, and every nation intended [a plot] for their messenger to seize him, and they disputed by [using] falsehood to [attempt to] invalidate thereby the truth. So I seized them, and how [terrible] was My penalty.", TextGerman = "(Schon) vor ihnen haben das Volk Nuhs und nach diesen die Gruppierungen (ihre Gesandten) der Lüge bezichtigt. Jede Gemeinschaft hatte vor, (gegen) ihren Gesandten (vorzugehen, um ihn) zu ergreifen. Und sie stritten mit dem Falschen, um damit die Wahrheit zu widerlegen. Da ergriff Ich sie. Wie war da Meine Bestrafung!" });
                        surah40.VerseList.Add(new Verse { Number = 6, TextBosnian = "I tako će se riječ Gospodara tvoga ispuniti da će oni koji nisu vjerovali - stanovnici u Vatri biti.", TextEnglish = "And thus has the word [i.e., decree] of your Lord come into effect upon those who disbelieved that they are companions of the Fire.", TextGerman = "Und so wurde das Wort deines Herrn gegen diejenigen, die ungläubig sind, unvermeidlich fällig, nämlich daß sie Insassen des (Höllen)feuers sind." });
                        surah40.VerseList.Add(new Verse { Number = 7, TextBosnian = "Oni koji nose Arš i oni koji su oko njega, slave i hvale Gospodara svoga i vjeruju u Njega, i mole se da budu oprošteni grijesi vjernicima: 'Gospodaru naš, Ti sve obuhvataš milošću i znanjem; zato oprosti onima koji se pokaju i koji slijede Tvoj put i sačuvaj ih patnje u Vatri!", TextEnglish = "Those [angels] who carry the Throne and those around it exalt [Allāh] with praise of their Lord and believe in Him and ask forgiveness for those who have believed, [saying], 'Our Lord, You have encompassed all things in mercy and knowledge, so forgive those who have repented and followed Your way and protect them from the punishment of Hellfire.", TextGerman = "Diejenigen, die den Thron tragen, und diejenigen, die in seiner Umgebung sind, lobpreisen ihren Herrn und glauben an Ihn und bitten um Vergebung für diejenigen, die glauben: Unser Herr, Du umfaßt alles in Deiner Barmherzigkeit und Deinem Wissen. So vergib denjenigen, die bereuen und Deinem Weg folgen, und bewahre sie vor der Strafe des Höllenbrandes." });
                        surah40.VerseList.Add(new Verse { Number = 8, TextBosnian = "Gospodaru naš, uvedi ih u vrtove Adna koje si im obećao, i one koji su bili dobri od predaka njihovih, i žena njihovih, i potomstva njihovog. Ti si, uistinu, Silni i Mudri.", TextEnglish = "Our Lord, and admit them to gardens of perpetual residence which You have promised them and whoever was righteous among their forefathers, their spouses and their offspring. Indeed, it is You who is the Exalted in Might, the Wise.", TextGerman = "Unser Herr, lasse sie in die Gärten Edens, die Du ihnen versprochen hast, eingehen, (sie) und wer rechtschaffen ist von ihren Vätern, ihren Gattinnen und ihren Nachkommen, Du bist ja der Allmächtige und Allweise." });
                        surah40.VerseList.Add(new Verse { Number = 9, TextBosnian = "I sačuvaj ih zala, jer, koga Ti toga dana poštediš zala - Ti si mu se smilovao, a to će, zaista, veliki uspjeh biti!'", TextEnglish = "And protect them from the evil consequences [of their deeds]. And he whom You protect from evil consequences that Day - You will have given him mercy. And that is the great attainment.'", TextGerman = "Und bewahre sie vor den bösen Taten. Wen Du an jenem Tag vor den bösen Taten bewahrst, dessen hast Du dich ja erbarmt, und das ist der großartige Erfolg." });
                        surah40.VerseList.Add(new Verse { Number = 10, TextBosnian = "Onima koji nisu vjerovali bit će doviknuto: 'Allahov prezir prema vama kad ste, pozivani da vjerujete, ostali nevjernici - bio je, doista, veći od vašeg prezira sada prema sebi.'", TextEnglish = "Indeed, those who disbelieve will be addressed, 'The hatred of Allāh for you was [even] greater than your hatred of yourselves [this Day in Hell] when you were invited to faith, but you disbelieved [i.e., refused].'", TextGerman = "Gewiß, denjenigen, die ungläubig waren, wird zugerufen: Allahs Abscheu ist wahrlich größer als eure Abscheu vor euch selbst, da ihr zum Glauben aufgerufen worden, aber ungläubig geblieben seid." });
                        surah40.VerseList.Add(new Verse { Number = 11, TextBosnian = "Gospodaru naš', reći će oni, 'dva puta si nas usmrtio i dva puta si nas oživio; mi priznajemo grijehe naše, pa ima li za izlazak puta kakva ?", TextEnglish = "They will say, 'Our Lord, You made us lifeless twice and gave us life twice, and we have confessed our sins. So is there to an exit any way?'", TextGerman = "Sie sagen: Unser Herr, Du hast uns zweimal sterben lassen und zweimal lebendig gemacht. So bekennen wir unsere Sünden. Gibt es denn einen Weg, (hier) herauszukommen?" });
                        surah40.VerseList.Add(new Verse { Number = 12, TextBosnian = "To vam je zato što niste vjerovali kad je prizivan Allah Jedini, a vjerovali ste ako bi Mu neko drugi u obožavanju pridružen bio! Presuda pripada jedino Allahu, Uzvišenom i Velikom.'", TextEnglish = "[They will be told], 'That is because, when Allāh was called upon alone, you disbelieved; but if others were associated with Him, you believed. So the judgement is with Allāh, the Most High, the Grand.'", TextGerman = "Dies ist so, weil ihr, wenn (immer) Allah allein angerufen wurde, ungläubig bliebt, ihr aber, wenn Ihm (andere) beigesellt wurden, glaubtet. Das Urteil gehört Allah, dem Erhabenen und Großen." });
                        surah40.VerseList.Add(new Verse { Number = 13, TextBosnian = "On vam pokazuje znakove Svoje i spušta vam opskrbu s neba, a pouku će prihvatiti samo onaj koji se Njemu obraća.", TextEnglish = "It is He who shows you His signs and sends down to you from the sky, provision. But none will remember except he who turns back [in repentance].", TextGerman = "Er ist es, Der euch Seine Zeichen zeigt und euch vom Himmel Versorgung herabsendet, doch bedenkt nur, wer sich (Allah) reuig zuwendet." });
                        surah40.VerseList.Add(new Verse { Number = 14, TextBosnian = "Molite zato Allaha, iskreno Mu ispovijedajući vjeru - pa makar to nevjernicima bilo mrsko!", TextEnglish = "So invoke Allāh, [being] sincere to Him in religion, although the disbelievers dislike it.", TextGerman = "So ruft Allah an, (wobei ihr) Ihm gegenüber aufrichtig in der Religion (seid), auch wenn es den Ungläubigen zuwider ist." });
                        surah40.VerseList.Add(new Verse { Number = 15, TextBosnian = "On je vlasnik visokih stupnjeva, Gospodar Arša Koji šalje objavu iz odredbe Svoje - kome hoće od robova Svojih, da upozori na Dan susreta međusobnog,", TextEnglish = "[He is] the Exalted above [all] degrees, Owner of the Throne; He places the inspiration of His command [i.e., revelation] upon whom He wills of His servants to warn of the Day of Meeting.", TextGerman = "Der Inhaber der hohen Rangstufen und der Herr des Thrones sendet den Geist von Seinem Befehl, wem von Seinen Dienern Er will, damit er den Tag der Begegnung warnend ankündige," });
                        surah40.VerseList.Add(new Verse { Number = 16, TextBosnian = "na Dan kada će se oni pojaviti, kada Allahu neće o njima ništa skriveno biti. Kome vlast pripada danas? Allahu, Jedinom, Potčinitelju svega.", TextEnglish = "The Day they come forth nothing concerning them will be concealed from Allāh. To whom belongs [all] sovereignty this Day? To Allāh, the One, the Prevailing.[1365]", TextGerman = "den Tag, an dem sie vortreten, wobei nichts von ihnen vor Allah verborgen bleibt. - Wem gehört heute die Herrschaft? - Allah, dem Einen, dem Allbezwinger." });
                        surah40.VerseList.Add(new Verse { Number = 17, TextBosnian = "Svaki će čovjek taj Dan prema zasluzi kažnjen ili nagrađen biti; taj Dan neće biti nepravde! Allah, zaista, brzo račun svodi.", TextEnglish = "This Day every soul will be recompensed for what it earned. No injustice today! Indeed, Allāh is swift in account.", TextGerman = "Heute wird jeder Seele das vergolten, was sie erworben hat. Heute gibt es kein Unrecht. Gewiß, Allah ist schnell im Abrechnen." });
                        surah40.VerseList.Add(new Verse { Number = 18, TextBosnian = "I upozori ih na Bliski dan, kada će srca do grkljana doprijeti i popriječiti se, kada zulumćari ni prisna prijatelja ni posrednika neće imati čiji zagovor bi uslišan bio.", TextEnglish = "And warn them, [O Muḥammad], of the Approaching Day, when hearts are at the throats, filled [with distress]. For the wrongdoers there will be no devoted friend and no intercessor [who is] obeyed.", TextGerman = "Und warne sie vor dem Tag der immer näher kommenden (Stunde des Gerichts), an dem die Herzen (vor Angst) in der Kehle sitzen und sie unterdrücken (ihren Grimm), an dem die Ungerechten weder einen warmherzigen Freund noch einen Fürsprecher haben, dem man gehorchen würde." });
                        surah40.VerseList.Add(new Verse { Number = 19, TextBosnian = "On zna poglede koji kriomice u ono što je zabranjeno gledaju, a i ono što grudi kriju;", TextEnglish = "He knows that which deceives the eyes and what the breasts conceal.", TextGerman = "Er kennt die verräterischen Augen und weiß, was die Brüste verbergen." });
                        surah40.VerseList.Add(new Verse { Number = 20, TextBosnian = "Allah će po pravdi presuditi, a oni koje pored Njega mole - neće ni po čemu suditi. Allah, je zaista, Svečujući i Svevideći.", TextEnglish = "And Allāh judges with truth, while those they invoke[1366] besides Him judge not with anything. Indeed, Allāh - He is the Hearing, the Seeing.", TextGerman = "Und Allah entscheidet der Wahrheit entsprechend. Diejenigen aber, die sie anstatt Seiner anrufen, haben nichts zu entscheiden. Gewiß, Allah ist der Allhörende und Allsehende." });
                        surah40.VerseList.Add(new Verse { Number = 21, TextBosnian = "Zašto oni ne putuju po Zemlji, pa da vide kakav je bio kraj onih prije njih? Bili su od njih moćniji i više tragova su na Zemlji ostavili, ali ih je Allah, zbog grijeha njihovih, kaznio i nije im bilo od Allaha zaštitnika.", TextEnglish = "Have they not traveled through the land and observed how was the end of those who were before them? They were greater than them in strength and in impression on the land, but Allāh seized them for their sins. And they had not from Allāh any protector.", TextGerman = "Sind sie nicht auf der Erde umhergereist, so daß sie schauen (konnten), wie das Ende derjenigen war, die vor ihnen waren? Sie hatten ja mehr Kraft und Wirkung auf der Erde als sie. Da ergriff sie Allah für ihre Sünden, und sie hatten vor Allah keinen Beschützer." });
                        surah40.VerseList.Add(new Verse { Number = 22, TextBosnian = "Allah ih je kaznio zato što su poricali jasne dokaze koje su im poslanici njihovi donosili; On je, zaista, Moćni, On žestoko kažnjava.", TextEnglish = "That was because their messengers were coming to them with clear proofs, but they disbelieved, so Allāh seized them. Indeed, He is Powerful and severe in punishment.", TextGerman = "Dies, weil ihre Gesandten immer wieder mit den klaren Beweisen zu ihnen kamen, sie aber ungläubig blieben. So ergriff sie Allah, denn Er ist Stark und streng im Bestrafen." });
                        surah40.VerseList.Add(new Verse { Number = 23, TextBosnian = "Mi smo poslali Musaa sa znakovima Našim i dokazom jasnim,", TextEnglish = "And We did certainly send Moses with Our signs and a clear authority[1367]", TextGerman = "Und Wir sandten bereits Musa mit Unseren Zeichen und mit einer deutlichen Ermächtigung" });
                        surah40.VerseList.Add(new Verse { Number = 24, TextBosnian = "faraonu i Hamanu i Karunu, ali su oni rekli: 'Čarobnjak i lažov!'", TextEnglish = "To Pharaoh, Hāmān and Qārūn, but they said, '[He is] a magician and a liar.'", TextGerman = "zu Fir’aun und Haman und Qarun. Sie aber sagten: Ein verlogener Zauberer." });
                        surah40.VerseList.Add(new Verse { Number = 25, TextBosnian = "A kada im je on donio Istinu od Nas, rekli su: 'Ubijajte mušku djecu onih koji s njime vjeruju, a ostavljajte u životu njihovu žensku djecu!' Ali, spletke nevjernika uvijek su uzaludne.", TextEnglish = "And when he brought them the truth from Us, they said, 'Kill the sons of those who have believed with him and keep their women alive.' But the plan of the disbelievers is not except in error.", TextGerman = "Als er ihnen nun die Wahrheit von Uns brachte, sagten sie: Tötet die Söhne derjenigen, die mit ihm glauben, und laßt (nur) ihre Frauen am Leben. Aber die List der Ungläubigen geht bestimmt verloren." });
                        surah40.VerseList.Add(new Verse { Number = 26, TextBosnian = "Pustite vi meni', reče faraon, 'da ubijem Musaa, a on neka moli pomoć od Gospodara svoga, jer bojim se da vam on vjeru vašu ne izmijeni ili da u zemlji nered ne izazove.'", TextEnglish = "And Pharaoh said, 'Let me kill Moses and let him call upon his Lord. Indeed, I fear that he will change your religion or that he will cause corruption[1368] in the land.'", TextGerman = "Fir’aun sagte: Laßt mich Musa töten; soll er (doch) seinen Herrn anrufen! Ich fürchte, daß er (sonst) eure Religion abändern oder daß er Unheil im Land hervorrufen wird." });
                        surah40.VerseList.Add(new Verse { Number = 27, TextBosnian = "Musa reče: 'Molim Gospodara svoga i Gospodara vašeg da me zaštiti od svakog oholog koji ne vjeruje u Dan u kome će se račun polagati!'", TextEnglish = "But Moses said, 'Indeed, I have sought refuge in my Lord and your Lord from every arrogant one who does not believe in the Day of Account.'", TextGerman = "Und Musa sagte: Ich nehme Zuflucht zu meinem Herrn und eurem Herrn vor jedem Hochmütigen, der nicht an den Tag der Abrechnung glaubt." });
                        surah40.VerseList.Add(new Verse { Number = 28, TextBosnian = "A jedan čovjek, vjernik, iz porodice faraonove, koji je krio vjerovanje svoje, reče: 'Zar da ubijete čovjeka zato što govori: 'Gospodar moj je Allah!', a donio vam je jasne dokaze od Gospodara vašeg? Ako je lažov, njegova laž će njemu nauditi, a ako govori istinu, onda će vas stići bar nešto od onoga što vam se prijeteći obećava; zaista Allah neće uputiti na Pravi put onoga koji u zlu pretjeruje i koji mnogo laže.", TextEnglish = "And a believing man from the family of Pharaoh who concealed his faith said, 'Do you kill a man [merely] because he says, 'My Lord is Allāh' while he has brought you clear proofs from your Lord? And if he should be lying, then upon him is [the consequence of] his lie; but if he should be truthful, there will strike you some of what he promises you. Indeed, Allāh does not guide one who is a transgressor and a liar.", TextGerman = "Ein gläubiger Mann von den Leuten Fir’auns, der seinen Glauben verborgen hielt, sagte: Wollt ihr denn einen Mann töten, weil er sagt: ,Mein Herr ist Allah’, wo er doch mit den klaren Beweisen von eurem Herrn zu euch gekommen ist? Wenn er ein Lügner ist, so trägt er (die Last) seiner Lügen. Wenn er aber die Wahrheit sagt, wird euch etwas von dem treffen, was er euch androht. Gewiß, Allah leitet nicht recht, wer maßlos und verlogen ist." });
                        surah40.VerseList.Add(new Verse { Number = 29, TextBosnian = "O narode moj, danas vama pripada vlast i nadmoćni ste u zemlji, pa ko će nas odbraniti od Allahove kazne ako nas ona stigne A faraon reče: 'Savjetujem vam samo ono što mislim, a samo ću vas na Pravi put uputiti.'", TextEnglish = "O my people, sovereignty is yours today, [your being] dominant in the land. But who would protect us from the punishment of Allāh if it came to us?' Pharaoh said, 'I do not show you except what I see, and I do not guide you except to the way of right conduct.'", TextGerman = "O mein Volk, es gehört euch heute die Herrschaft, und ihr habt die Oberhand im Land. Wer wird uns dann gegen Allahs Gewalt helfen, wenn sie über uns kommt? Fir’aun sagte: Ich weise euch nur auf das hin, was ich sehe, und leite euch nur den Weg des besonnenen Handelns." });
                        surah40.VerseList.Add(new Verse { Number = 30, TextBosnian = "A onda, onaj koji je vjerovao reče: 'O narode moj, ja se za vas bojim dana poput dana kada su kažnjene skupine koje su se protiv poslanika bile urotile,", TextEnglish = "And he who believed said, 'O my people, indeed I fear for you [a fate] like the day of the companies[1369] -", TextGerman = "Derjenige, der glaubte, sagte: O mein Volk, gewiß, ich fürchte für euch etwas Gleiches wie den Tag der Gruppierungen," });
                        surah40.VerseList.Add(new Verse { Number = 31, TextBosnian = "kao što je to bilo s Nuhovim narodom, i Adom i Semudom, i onima poslije njih. A Allah ne želi zulum robovima Svojim.", TextEnglish = "Like the custom of the people of Noah and of ʿAad and Thamūd and those after them. And Allāh wants no injustice for [His] servants.", TextGerman = "etwas Gleichartiges wie das, was dem Volk Nuhs, den ’Ad, den Tamud und denjenigen, die nach ihnen waren, (widerfuhr). Und Allah will keine Ungerechtigkeit für die Diener." });
                        surah40.VerseList.Add(new Verse { Number = 32, TextBosnian = "O narode moj, plašim se šta će biti s vama na Dan kada jedni druge budete dozivali,", TextEnglish = "And O my people, indeed I fear for you the Day of Calling[1370] -", TextGerman = "O mein Volk, gewiß, ich fürchte für euch den Tag des gegenseitigen Zurufens," });
                        surah40.VerseList.Add(new Verse { Number = 33, TextBosnian = "na Dan kada budete okrenutih leđa uzmicali, kada vas od Allaha niko neće moći zaštititi. A onome kome Allah da da je zabludi, tome nema uputitelja.'", TextEnglish = "The Day you will turn your backs fleeing; there is not for you from Allāh any protector. And whoever Allāh sends astray - there is not for him any guide.", TextGerman = "den Tag, an dem ihr den Rücken kehren werdet, wobei ihr nichts vor Allah Schützendes haben werdet. Und wen Allah in die Irre gehen läßt, der hat niemanden, der ihn rechtleitet." });
                        surah40.VerseList.Add(new Verse { Number = 34, TextBosnian = "Jusuf vam je, još davno, donio jasne dokaze, ali vi ste stalno sumnjali u ono šta vam je donio. A kad je on umro, vi ste rekli: 'Allah više neće poslije njega poslati poslanika!' Eto, tako Allah da da je u zabludi svako ko u zlu pretjeruje i sumnja -", TextEnglish = "And Joseph had already come to you before with clear proofs, but you remained in doubt of that which he brought to you, until when he died, you said, 'Never will Allāh send a messenger after him.' Thus does Allāh leave astray he who is a transgressor and skeptic.'", TextGerman = "Bereits zuvor kam Yusuf zu euch mit den klaren Beweisen. Ihr aber bliebt weiterhin im Zweifel über das, was er euch gebracht hatte. Als er dann gestorben war, sagtet ihr: ,Allah wird nach ihm keinen Gesandten mehr erstehen lassen. So läßt Allah in die Irre gehen, wer maßlos und voller Zweifel ist." });
                        surah40.VerseList.Add(new Verse { Number = 35, TextBosnian = "oni koji o Allahovim ajetima i znakovima raspravljaju, iako im nikakav dokaz nije došao. To itekako preziru Allah i oni koji vjeruju. Tako Allah pečati srce svakog oholog silnika.", TextEnglish = "Those who dispute concerning the signs of Allāh without an authority[1371] having come to them - great is hatred [of them] in the sight of Allāh and in the sight of those who have believed. Thus does Allāh seal over every heart [belonging to] an arrogant tyrant.", TextGerman = "Diejenigen, die über Allahs Zeichen streiten, ohne eine Ermächtigung erhalten zu haben, - welch schwerwiegende Abscheu erregen diese bei Allah und bei denjenigen, die gläubig sind. So versiegelt Allah das Herz eines jeden Hochmütigen und Gewalttätigen." });
                        surah40.VerseList.Add(new Verse { Number = 36, TextBosnian = "O Hamane', reče faraon, 'sagradi mi toranj ne bih li stigao do staza,", TextEnglish = "And Pharaoh said, 'O Hāmān, construct for me a tower that I might reach the ways[1372] -", TextGerman = "Und Fir’aun sagte: O Haman, errichte mir einen Hochbau, vielleicht kann ich die Seile erreichen," });
                        surah40.VerseList.Add(new Verse { Number = 37, TextBosnian = "staza nebeskih, ne bih li se popeo do Musaova Boga, a ja smatram da je on, zaista, lažac.' I eto tako su faraonu njegova ružna djela uljepšana, i on je bio odvraćen od Pravog puta, a lukavstvo se faraonovo završilo na njegovu štetu.", TextEnglish = "The ways into the heavens - so that I may look at the deity of Moses; but indeed, I think he is a liar.' And thus was made attractive to Pharaoh the evil of his deed, and he was averted from the [right] way. And the plan of Pharaoh was not except in ruin.", TextGerman = "die Seile der Himmel, daß ich zum Gott Musas emporsteige, und ich halte ihn wahrlich für einen Lügner. So wurde Fir’aun sein böses Tun ausgeschmückt, und er wurde vom (rechten) Weg abgehalten. Aber Fir’auns listige Tat wird nur zugrunde gehen." });
                        surah40.VerseList.Add(new Verse { Number = 38, TextBosnian = "I onaj koji je vjerovao reče: 'O narode moj, mene slijedite, ja ću vam na Pravi put ukazati!", TextEnglish = "And he who believed said, 'O my people, follow me; I will guide you to the way of right conduct.", TextGerman = "Derjenige, der gläubig war, sagte: O mein Volk, folgt mir, dann leite ich euch den Weg des besonnenen Handelns." });
                        surah40.VerseList.Add(new Verse { Number = 39, TextBosnian = "O narode moj, ovaj dunjalučki život samo je uživanje, a ahiret je, zaista, Kuća vječnog boravka.", TextEnglish = "O my people, this worldly life is only [temporary] enjoyment, and indeed, the Hereafter - that is the home of [permanent] settlement.", TextGerman = "O mein Volk, dieses irdische Leben ist nur Nießbrauch; das Jenseits aber ist die Wohnstätte zum (bleibenden) Aufenthalt." });
                        surah40.VerseList.Add(new Verse { Number = 40, TextBosnian = "Ko god učini zlo, samo će zbog njega biti kažnjen, a ko god učini dobro - bio muškarac ili žena, a vjernik je- taj će u Džennet ući; u njemu će bez računa biti opskrbljeni.", TextEnglish = "Whoever does an evil deed will not be recompensed except by the like thereof; but whoever does righteousness, whether male or female, while he is a believer - those will enter Paradise, being given provision therein without account.", TextGerman = "Wer etwas Böses tut, dem wird nur gleichviel vergolten. Wer aber rechtschaffen handelt, ob Mann oder Frau, und dabei gläubig ist, jene werden dann in den (Paradies)garten eingehen, wo sie versorgt werden ohne Abrechnung." });
                        surah40.VerseList.Add(new Verse { Number = 41, TextBosnian = "narode moj! Šta je ovo? Ja vas pozivam u spas, a vi mene pozivate u Vatru;", TextEnglish = "And O my people, how is it that I invite you to salvation while you invite me to the Fire?", TextGerman = "O mein Volk, seht ihr, wie ich euch zur Rettung rufe, ihr mich aber zum (Höllen)feuer ruft?" });
                        surah40.VerseList.Add(new Verse { Number = 42, TextBosnian = "Pozivate me da ne vjerujem u Allaha i da Njemu u obožavanju pridružujem ono o čemu nemam znanja, a ja vas pozivam Silnome, Oprostitelju stalnome.", TextEnglish = "You invite me to disbelieve in Allāh and associate with Him that of which I have no knowledge, and I invite you to the Exalted in Might, the Perpetual Forgiver.[1373]", TextGerman = "Ihr ruft mich dazu auf, Allah zu verleugnen und Ihm das beizugesellen, wovon ich kein Wissen habe, während ich euch zum Allmächtigen und Allvergebenden rufe." });
                        surah40.VerseList.Add(new Verse { Number = 43, TextBosnian = "Nema nimalo sumnje u to da se ono čemu vi mene pozivate neće nikom ni na ovom ni na onom svijetu odazvati, i da je naš povratak Allahu i da će oni koji su pretjerivali stanovnici u Ognju biti.", TextEnglish = "Assuredly, that to which you invite me has no [response to a] supplication in this world or in the Hereafter; and indeed, our return is to Allāh, and indeed, the transgressors will be companions of the Fire.", TextGerman = "Es ist zweifellos, daß demjenigen, zu dem ihr mich ruft, keine Anrufung zusteht, weder im Diesseits noch im Jenseits, daß unsere Rückkehr zu Allah sein wird und daß die Maßlosen Insassen des (Höllen)feuers sein werden." });
                        surah40.VerseList.Add(new Verse { Number = 44, TextBosnian = "Pa, sjetit ćete se ovog što vam kažem! A ja Allahu prepuštam svoj slučaj; Allah, uistinu, robove Svoje dobro vidi.'", TextEnglish = "And you will remember what I [now] say to you, and I entrust my affair to Allāh. Indeed, Allāh is Seeing of [His] servants.'", TextGerman = "Dann werdet ihr an das denken, was ich euch sage. Ich überlasse meine Angelegen heit Allah. Gewiß, Allah sieht die Diener wohl." });
                        surah40.VerseList.Add(new Verse { Number = 45, TextBosnian = "I Allah ga je sačuvao od zala onog što su spletkarili, a faraonove pristalice zla patnja zadesi,", TextEnglish = "So Allāh protected him from the evils they plotted, and the people of Pharaoh were enveloped by the worst of punishment -", TextGerman = "So bewahrte Allah ihn vor den Bosheiten dessen, was sie an Ränken geschmiedet hatten, und die Leute Fir’auns umschloß die böse Strafe," });
                        surah40.VerseList.Add(new Verse { Number = 46, TextBosnian = "Vatra kojoj će izloženi jutrom i večerom biti, a kada nastupi Čas: 'Uvedite faraonove ljude u patnju najtežu!'", TextEnglish = "The Fire; they are exposed to it morning and evening.[1374] And the Day the Hour appears [it will be said], 'Make the people of Pharaoh enter the severest punishment.'", TextGerman = "das (Höllen)feuer, dem sie morgens und abends vorgeführt werden. Und am Tag, da sich die Stunde erhebt (, wird es heißen): Laßt die Leute Fir’auns in die strengste Strafe eingehen." });
                        surah40.VerseList.Add(new Verse { Number = 47, TextBosnian = "I kada se u Vatri budu prepirali, a oni koji su bili potlačeni reknu glavešinama svojim: 'Mi smo bili sljedbenici vaši, možete li nas Vatre makar malo osloboditi.", TextEnglish = "And [mention] when they will argue within the Fire, and the weak will say to those who had been arrogant, 'Indeed, we were [only] your followers, so will you relieve us of a share of the Fire?'", TextGerman = "Und (denke daran,) wenn sie (dereinst) im (Höllen)feuer miteinander streiten, dann sagen die Schwachen zu denjenigen, die sich hochmütig verhielten: Wir waren doch eure Gefolgsleute; könnt ihr uns nun einen Teil des (Höllen)feuers abnehmen?" });
                        surah40.VerseList.Add(new Verse { Number = 48, TextBosnian = "Onda će glavešine reći: 'Doista smo svi u njoj! Allah je već presudio robovima Svojim.'", TextEnglish = "Those who had been arrogant will say, 'Indeed, all [of us] are in it. Indeed, Allāh has judged between the servants.'", TextGerman = "Diejenigen, die sich hochmütig verhielten, werden sagen: Wir befinden uns doch alle darin. Allah hat nun zwischen den Dienern gerichtet." });
                        surah40.VerseList.Add(new Verse { Number = 49, TextBosnian = "I oni koji će u Vatri biti govorit će čuvarima Džehennema: 'Zamolite Gospodara svoga da nam bar jedan dan patnju ublaži!'", TextEnglish = "And those in the Fire will say to the keepers of Hell, 'Supplicate your Lord to lighten for us a day from the punishment.'", TextGerman = "Diejenigen, die sich im (Höllen)feuer befinden, werden zu den Wärtern der Hölle sagen: Ruft euren Herrn an, daß Er uns einen Tag von der Strafe erlasse." });
                        surah40.VerseList.Add(new Verse { Number = 50, TextBosnian = "A zar vam poslanici vaši nisu jasne dokaze donosili?'', upitat će oni. 'Jesu!', odgovorit će. 'Molite onda vi! A molba nevjernika uzaludna je'', reći će oni.", TextEnglish = "They will say, 'Did there not come to you your messengers with clear proofs?' They will say, 'Yes.' They will reply, 'Then supplicate [yourselves], but the supplication of the disbelievers is not except in error [i.e., futility].'", TextGerman = "Sie sagen: Pflegten nicht doch eure Gesandten mit den klaren Beweisen zu euch zu kommen? Sie sagen: Ja, doch! Sie sagen: So ruft an. Aber das Anrufen der Ungläubigen geht bestimmt verloren’." });
                        surah40.VerseList.Add(new Verse { Number = 51, TextBosnian = "Mi ćemo, doista, pomoći poslanike Naše i one koji vjeruju u životu dunjalučkom, a i na Dan kad se dignu svjedoci,", TextEnglish = "Indeed, We will support Our messengers and those who believe during the life of this world and on the Day when the witnesses will stand -", TextGerman = "Wahrlich, Wir helfen Unseren Gesandten und denjenigen, die glauben, im diesseitigen Leben und am Tag, da die Zeugen auftreten," });
                        surah40.VerseList.Add(new Verse { Number = 52, TextBosnian = "na Dan kad zulumćarima pravdanje njihovo od koristi neće biti; njih će prokletstvo i najgore prebivalište čekati.", TextEnglish = "The Day their excuse will not benefit the wrongdoers, and they will have the curse, and they will have the worst home [i.e., Hell].", TextGerman = "am Tag, da den Ungerechten ihre Entschuldigung nicht nützt und es für sie den Fluch geben und es für sie die böse Wohnstätte geben wird." });
                        surah40.VerseList.Add(new Verse { Number = 53, TextBosnian = "Mi smo Musau uputu dali i sinovima Israilovim u nasljedstvo Knjigu ostavili,", TextEnglish = "And We had certainly given Moses guidance, and We caused the Children of Israel to inherit the Scripture", TextGerman = "Wir gaben bereits Musa die Rechtleitung und ließen die Kinder Isra’ils die Schrift erben" });
                        surah40.VerseList.Add(new Verse { Number = 54, TextBosnian = "da bude uputstvo i opomena onima koji pameti budu imali.", TextEnglish = "As guidance and a reminder for those of understanding.", TextGerman = "als Rechtleitung und Ermahnung für diejenigen, die Verstand besitzen." });
                        surah40.VerseList.Add(new Verse { Number = 55, TextBosnian = "Pa ti budi strpljiv - Allahovo je obećanje Istina - i moli da ti budu oprošteni grijesi, i Gospodara svoga slavi i hvali uvečer i ujutro!", TextEnglish = "So be patient, [O Muḥammad]. Indeed, the promise of Allāh is truth. And ask forgiveness for your sin[1375] and exalt [Allāh] with praise of your Lord in the evening and the morning.", TextGerman = "So sei standhaft. Gewiß, Allahs Versprechen ist wahr. Und bitte um Vergebung für deine Sünde und lobpreise deinen Herrn am Abend und am frühen Morgen." });
                        surah40.VerseList.Add(new Verse { Number = 56, TextBosnian = "Zaista oni koji o Allahovim ajetima i znakovima raspravljaju, iako im nikakav dokaz nije došao, u srcima svojim imaju samo oholost koja ih neće dovesti do cilja željenog, pa ti moli od Allaha zaštitu. On je, uistinu, Svečujući i Svevideći.", TextEnglish = "Indeed, those who dispute concerning the signs of Allāh without [any] evidence having come to them - there is not within their breasts except pride, [the extent of] which they cannot reach. So seek refuge in Allāh. Indeed, it is He who is the Hearing, the Seeing.", TextGerman = "Gewiß, diejenigen, die über Allahs Zeichen streiten, ohne daß sie eine Ermächtigung erhalten hätten, haben in ihren Brüsten nur Neid (auf das), was sie nicht erreichen werden können. So suche Zuflucht bei Allah, denn Er ist der Allhörende und Allsehende." });
                        surah40.VerseList.Add(new Verse { Number = 57, TextBosnian = "Stvaranje nebesa i Zemlje sigurno je veće nego stvaranje roda ljudskog, ali većina ljudi ne zna.", TextEnglish = "The creation of the heavens and earth is greater than the creation of mankind, but most of the people do not know.", TextGerman = "Wahrlich die Erschaffung der Himmel und der Erde ist größer als die Erschaffung der Menschen. Aber die meisten Menschen wissen nicht." });
                        surah40.VerseList.Add(new Verse { Number = 58, TextBosnian = "Nisu isto slijepac i onaj koji vidi, nisu isto vjernici koji dobra djela čine i zločinitelji. Kako se malo prisjećate!", TextEnglish = "And not equal are the blind and the seeing, nor are those who believe and do righteous deeds and the evildoer. Little do you remember.", TextGerman = "Nicht gleich sind der Blinde und der Sehende und auch nicht diejenigen, die glauben und rechtschaffene Werke tun, und der Missetäter. Wie wenig ihr bedenkt!" });
                        surah40.VerseList.Add(new Verse { Number = 59, TextBosnian = "Doći će Čas sudnji, u to nema sumnje, ali većina ljudi ne vjeruje.", TextEnglish = "Indeed, the Hour is coming - no doubt about it - but most of the people do not believe.", TextGerman = "Gewiß, die Stunde kommt bestimmt, an ihr gibt es keinen Zweifel. Aber die meisten Menschen glauben nicht." });
                        surah40.VerseList.Add(new Verse { Number = 60, TextBosnian = "Gospodar vaš je rekao: 'Dove Mi upućujte, Ja ću vam se odazvati! Zbilja, oni koji se ohole da Mi ibadet čine - ući će, sigurno, u Džehennem poniženi.'", TextEnglish = "And your Lord says, 'Call upon Me; I will respond to you.' Indeed, those who disdain My worship will enter Hell [rendered] contemptible.", TextGerman = "Euer Herr sagt: Ruft Mich an, so erhöre Ich euch. Gewiß, diejenigen, die sich aus Hochmut weigern, Mir zu dienen, werden in die Hölle gedemütigt eingehen." });
                        surah40.VerseList.Add(new Verse { Number = 61, TextBosnian = "Allah vam je dao noć da se u njoj odmarate, a dan da vidite. Allah je neizmjerno darežljiv prema ljudima, ali većina ljudi ne zahvaljuje.", TextEnglish = "It is Allāh who made for you the night that you may rest therein and the day giving sight.[1376] Indeed, Allāh is the possessor of bounty for the people, but most of them are not grateful.", TextGerman = "Allah ist es, Der euch die Nacht gemacht hat, damit ihr in ihr ruht, und den Tag hell’. Allah ist wahrlich voll Huld gegen die Menschen. Aber die meisten Menschen sind nicht dankbar." });
                        surah40.VerseList.Add(new Verse { Number = 62, TextBosnian = "To vam je, eto, Allah, Gospodar vaš, Stvoritelj svega, drugog boga osim Njega nema, pa kuda se onda odmećete?!", TextEnglish = "That is Allāh, your Lord, Creator of all things; there is no deity except Him, so how are you deluded?", TextGerman = "Dies ist doch Allah, euer Herr, der Schöpfer von allem. Es gibt keinen Gott außer Ihm. Wie laßt ihr euch also abwendig machen?" });
                        surah40.VerseList.Add(new Verse { Number = 63, TextBosnian = "Tako su se odmetali i oni koji su Allahove ajete i znakove poricali.", TextEnglish = "Thus were those [before you] deluded who were rejecting the signs of Allāh.", TextGerman = "So lassen sich diejenigen abwendig machen, die Allahs Zeichen zu verleugnen pflegen." });
                        surah40.VerseList.Add(new Verse { Number = 64, TextBosnian = "Allah vam je učinio Zemlju staništem, a nebo zdanjem, i oblikovao vas, pa je obličja vaša lijepim učinio, i lijepom hranom i pićem vas opskrbio. To je Allah, Gospodar vaš, i neka je Uzvišen Allah, Gospodar svjetova!", TextEnglish = "It is Allāh who made for you the earth a place of settlement and the sky a structure [i.e., ceiling] and formed you and perfected your forms and provided you with good things. That is Allāh, your Lord; then blessed is Allāh, Lord of the worlds.", TextGerman = "Allah ist es, Der euch die Erde zu einem festen Grund und den Himmel zu einem Gebäude gemacht, euch gestaltet und dabei eure Gestalten schön geformt hat und euch von den guten Dingen versorgt. Dies ist doch Allah, euer Herr. Segensreich ist Allah, der Herr der Weltenbewohner!" });
                        surah40.VerseList.Add(new Verse { Number = 65, TextBosnian = "On je Živi, nema boga osim Njega, pa, molite Ga, ispovijedajući Mu čistu vjeru. Pa, svaka hvala neka je Allahu, Gospodaru svjetova!", TextEnglish = "He is the Ever-Living;[1377] there is no deity except Him, so call upon Him, [being] sincere to Him in religion. [All] praise is [due] to Allāh, Lord of the worlds.", TextGerman = "Er ist der Lebendige. Es gibt keinen Gott außer Ihm. So ruft Ihn an, (wobei ihr) Ihm gegenüber aufrichtig in der Religion (seid). (Alles) Lob gehört Allah, dem Herrn der Weltenbewohner!" });
                        surah40.VerseList.Add(new Verse { Number = 66, TextBosnian = "Reci: 'Meni je zabranjeno obožavati one koje vi mimo Allaha molite, pošto su mi došli jasni dokazi od Gospodara moga, a naređeno mi je da se predam Gospodaru svjetova.'", TextEnglish = "Say, [O Muḥammad], 'Indeed, I have been forbidden to worship those you call upon besides Allāh once the clear proofs have come to me from my Lord, and I have been commanded to submit to the Lord of the worlds.'", TextGerman = "Sag: Mir wurde verboten, als die klaren Zeichen von meinem Herrn zu mir kamen, denjenigen zu dienen, die ihr anstatt Allahs anruft; und mir wurde befohlen, mich dem Herrn der Weltenbewohner zu ergeben." });
                        surah40.VerseList.Add(new Verse { Number = 67, TextBosnian = "On je Onaj Koji vas je od zemlje stvorio, potom od kapi sjemena, zatim od zakvačka, zatim vas kao dojenčad izvodi, i da do muževnog doba stignete, i da starci postanete - a neki od vas umiru prije - i da do određenog roka stignete, a i da biste razumjeli.", TextEnglish = "It is He who created you from dust, then from a sperm-drop, then from a clinging clot; then He brings you out as a child; then [He develops you] that you reach your [time of] maturity, then [further] that you become elders. And among you is he who is taken in death before [that], so that you reach a specified term;[1378] and perhaps you will use reason.", TextGerman = "Er ist es, Der euch aus Erde erschaffen hat, hierauf aus einem Samentropfen, hierauf aus einem Anhängsel’. Hierauf läßt Er euch als kleine Kinder hervorkommen. Hierauf (läßt Er euch heranwachsen), damit ihr eure Vollreife erlangt und damit ihr dann Greise werdet - manch einer von euch wird vorher abberufen - und damit ihr eine festgesetzte Frist erreicht, und auf daß ihr begreifen möget." });
                        surah40.VerseList.Add(new Verse { Number = 68, TextBosnian = "On život i smrt daje! A kad nešto odluči, samo rekne: 'Budi', i ono bude.", TextEnglish = "He it is who gives life and causes death; and when He decrees a matter, He but says to it, 'Be,' and it is.", TextGerman = "Er ist es, Der lebendig macht und sterben läßt. Wenn Er dann eine Angelegenheit bestimmt, sagt Er zu ihr nur: Sei!, und so ist sie." });
                        surah40.VerseList.Add(new Verse { Number = 69, TextBosnian = "Zar ne vidiš one koji raspravljaju o Allahovim ajetima i znakovima; kuda se oni okreću?!", TextEnglish = "Do you not consider those who dispute concerning the signs of Allāh - how are they averted?", TextGerman = "Siehst du nicht diejenigen, die über Allahs Zeichen streiten? Wie sie sich doch abwenden lassen!" });
                        surah40.VerseList.Add(new Verse { Number = 70, TextBosnian = "Oni koji poriču Knjigu i ono što smo slali po poslanicima?! A saznat će oni!", TextEnglish = "Those who deny the Book [i.e., the Qur’ān] and that with which We sent Our messengers - they are going to know,", TextGerman = "(Sie,) die die Schrift und das, womit Wir Unsere Gesandten gesandt haben, für Lüge erklären. Aber sie werden (es noch) erfahren," });
                        surah40.VerseList.Add(new Verse { Number = 71, TextBosnian = "Kada okovi budu na vratovima njihovim, i lanci, a oni vučeni", TextEnglish = "When the shackles are around their necks and the chains; they will be dragged", TextGerman = "wenn die Fesseln und die Ketten um ihre Hälse (angebracht) sind und sie gezerrt werden" });
                        surah40.VerseList.Add(new Verse { Number = 72, TextBosnian = "u ključalu vodu, a zatim u Vatri prženi!", TextEnglish = "In boiling water; then in the Fire they will be filled [with flame].", TextGerman = "ins heiße Wasser und hierauf ins (Höllen)feuer als Brennstoff geworfen werden." });
                        surah40.VerseList.Add(new Verse { Number = 73, TextBosnian = "Zatim će upitani biti: 'Gdje su oni koje ste obožavali,", TextEnglish = "Then it will be said to them, 'Where is that which you used to associate [with Him in worship]", TextGerman = "Dann wird zu ihnen gesagt werden: Wo ist das, was ihr (Allah) beigesellt habt (, und dem ihr gedient habt)" });
                        surah40.VerseList.Add(new Verse { Number = 74, TextBosnian = "mimo Allaha 'Oni su se od nas izgubili', odgovorit će, 'mi se prije, ustvari, nismo nikome ni molili.' Eto, tako Allah da da su u zabludi nevjernici.", TextEnglish = "Other than Allāh?' They will say, 'They have departed from us; rather, we did not used to invoke previously anything.' Thus does Allāh put astray the disbelievers.", TextGerman = "anstatt Allahs? Sie werden sagen: Sie sind uns entschwunden. Aber nein! Wir riefen zuvor (doch gar) nichts an. So läßt Allah die Ungläubigen in die Irre gehen." });
                        surah40.VerseList.Add(new Verse { Number = 75, TextBosnian = "To vam je zato što ste se bez ikakva osnova na Zemlji veselili i drski bili.", TextEnglish = "[The angels will say], 'That was because you used to exult upon the earth without right and you used to behave insolently.", TextGerman = "Dies (geschieht deshalb), weil ihr auf der Erde ohne Recht froh zu sein und (zu sehr) frohsinnig zu leben pflegtet." });
                        surah40.VerseList.Add(new Verse { Number = 76, TextBosnian = "Ulazite kroz kapije Džehennema, u njemu ćete vječno ostati - a ružno je prebivalište oholih.", TextEnglish = "Enter the gates of Hell to abide eternally therein, and wretched is the residence of the arrogant.'", TextGerman = "Betretet (nun) die Tore der Hölle, ewig darin zu bleiben. Schlimm ist der Aufent haltsort der Hochmütigen!" });
                        surah40.VerseList.Add(new Verse { Number = 77, TextBosnian = "A ti se strpi! Doista je Allahovo obećanje istina! Bilo da ti pokažemo dio onoga što im Mi obećavamo, bilo da ti prije život oduzmemo - Nama će se oni vratiti.", TextEnglish = "So be patient, [O Muḥammad]; indeed, the promise of Allāh is truth. And whether We show you some of what We have promised them or We take you in death, it is to Us they will be returned.", TextGerman = "So sei standhaft. Gewiß, Allahs Versprechen ist wahr. Ob Wir dich einen Teil dessen, was Wir ihnen androhen, sehen lassen oder dich (vorher) abberufen, zu Uns werden sie zurückgebracht." });
                        surah40.VerseList.Add(new Verse { Number = 78, TextBosnian = "I prije tebe poslanike smo slali, o nekima od njih kazivali smo ti, a o nekima nismo. I nijedan poslanik nije mogao donijeti nekakav znak osim s Allahovom dozvolom. A kada dođe Allahova odredba, bit će presuđeno po pravdi i zabludjeli će tada biti gubitnici.", TextEnglish = "And We have already sent messengers before you. Among them are those [whose stories] We have related to you, and among them are those [whose stories] We have not related to you. And it was not for any messenger to bring a sign [or verse] except by permission of Allāh. So when the command of Allāh comes, it will be concluded [i.e., judged] in truth, and the falsifiers will thereupon lose [all].", TextGerman = "Wir haben doch bereits vor dir Gesandte gesandt. Unter ihnen gibt es manche, von denen Wir dir berichtet haben, und unter ihnen gibt es manche, von denen Wir dir nicht bereichtet haben. Kein Gesandter kann ein Zeichen bringen - außer mit Allahs Erlaubnis. Wenn dann Allahs Befehl kommt, wird der Wahrheit entsprechend entschieden, und verloren haben dann diejenigen, die (die Botschaft) für falsch erklären." });
                        surah40.VerseList.Add(new Verse { Number = 79, TextBosnian = "Allah je za vas stvorio životinje, da na nekima jašete - a neke jedete,", TextEnglish = "It is Allāh who made for you the grazing animals upon which you ride, and some of them you eat.", TextGerman = "Allah ist es, Der für euch das Vieh gemacht hat, damit ihr (teils) auf ihm reiten könnt; und ihr könnt (teils) von ihm essen." });
                        surah40.VerseList.Add(new Verse { Number = 80, TextBosnian = "a vi od njih i drugih koristi imate - i da bi na njima potrebu koju u prsima imate ispunili; i na njima i na lađama vi se vozite.", TextEnglish = "And for you therein are [other] benefits and that you may realize upon them a need which is in your breasts;[1379] and upon them and upon ships you are carried.", TextGerman = "Und ihr habt an ihm (allerlei) Nutzen. Und damit ihr, auf ihm (reitend,) ein Ziel’, das ihr in euren Brüsten (hegt), erreichen könnt. Und auf ihm und auf den Schiffen werdet ihr getragen." });
                        surah40.VerseList.Add(new Verse { Number = 81, TextBosnian = "On vam pokazuje znakove Svoje, pa koje od Allahovih znakova vi negirate?", TextEnglish = "And He shows you His signs. So which of the signs of Allāh do you deny?", TextGerman = "Und Er zeigt euch Seine Zeichen. Welches von Allahs Zeichen wollt ihr nun nicht anerkennen?" });
                        surah40.VerseList.Add(new Verse { Number = 82, TextBosnian = "Zašto oni ne putuju po Zemlji, pa da vide kako su završili oni prije njih? Oni su bili od njih brojniji i jači i više su tragova na Zemlji ostavili, i ništa im nije koristilo ono što su stekli.", TextEnglish = "Have they not traveled through the land and observed how was the end of those before them? They were more numerous than themselves and greater in strength and in impression on the land, but they were not availed by what they used to earn.", TextGerman = "Sind sie denn nicht auf der Erde umhergereist, so daß sie schauen (konnten), wie das Ende derjenigen war, die vor ihnen waren? Sie waren zahlreicher als sie und hatten mehr Kraft und Wirkung auf der Erde als sie. Aber nicht nützte ihnen, was sie zu erwerben pflegten." });
                        surah40.VerseList.Add(new Verse { Number = 83, TextBosnian = "Kada su im poslanici njihovi jasne dokaze donosili, oni su se znanjem koje su imali dičili, i snašlo ih je ono čemu su se stalno rugali.", TextEnglish = "And when their messengers came to them with clear proofs, they [merely] rejoiced in what they had of knowledge, but they were enveloped by what they used to ridicule.", TextGerman = "Als nun ihre Gesandten zu ihnen mit den klaren Beweisen kamen, waren sie froh über das Wissen, das sie besaßen, und es umschloß sie das, worüber sie sich lustig zu machen pflegten." });
                        surah40.VerseList.Add(new Verse { Number = 84, TextBosnian = "A kad bi kaznu Našu vidjeli, onda bi govorili: 'Mi vjerujemo u Allaha, u Njega jedinog, a odričemo se onih koje smo Mu u obožavanju pridruživali!'", TextEnglish = "And when they saw Our punishment, they said, 'We believe in Allāh alone and disbelieve in that which we used to associate with Him.'", TextGerman = "Als sie dann Unsere Gewalt sahen, sagten sie: Wir glauben an Allah allein und verleugnen das, was wir Ihm beizugesellen pflegten." });
                        surah40.VerseList.Add(new Verse { Number = 85, TextBosnian = "Ali im vjerovanje njihovo, kada bi kaznu Našu vidjeli, ne bi nimalo koristilo. To je prema Allahovom zakonu koji je primijenjen za sve robove Njegove - i tada bi nevjernici stradali.", TextEnglish = "But never did their faith benefit them once they saw Our punishment. [It is] the established way of Allāh which has preceded among His servants. And the disbelievers thereupon lost [all].", TextGerman = "Aber nicht mehr nützen konnte ihnen ihr Glaube, als sie Unsere Gewalt sahen - (so war) Allahs Gesetzmäßigkeit, die bereits (in der Vergangenheit) an Seine Diener ergangen war, und verloren waren da die Ungläubigen." });
                        result = surah40;
                        break;
                    #endregion

                    // 
                    #region
                    case 41:
                        Surah surah41 = new Surah(); surah41.Name = Constants.Surah41Name; surah41.Number = 41; surah41.VerseList = new List<Verse>();
                        surah41.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah41.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ha-mim.", TextEnglish = "Ḥā, Meem.[1381]", TextGerman = "Ha-Mim." });
                        surah41.VerseList.Add(new Verse { Number = 2, TextBosnian = "Objava je od Svemilosnog Milostivog,", TextEnglish = "[This is] a revelation from the Entirely Merciful, the Especially Merciful -", TextGerman = "(Dies ist) eine Offenbarung vom Allerbarmer, dem Barmherzigen," });
                        surah41.VerseList.Add(new Verse { Number = 3, TextBosnian = "Knjiga čiji su ajeti jasno izloženi, Kur'an na arapskom jeziku za ljude koji znaju,", TextEnglish = "A Book whose verses have been detailed, an Arabic Qur’ān[1382] for a people who know,", TextGerman = "ein Buch, dessen Zeichen ausführlich dargelegt sind, als ein arabischer Qur’an, für Leute, die Bescheid wissen," });
                        surah41.VerseList.Add(new Verse { Number = 4, TextBosnian = "obveselitelj i upozoritelj - ali se većina njih opet okreće, pa ne čuju.", TextEnglish = "As a giver of good tidings and a warner; but most of them turn away, so they do not hear.", TextGerman = "als Frohboten und als Warner. Aber die meisten von ihnen wenden sich ab, so daß sie nicht hören." });
                        surah41.VerseList.Add(new Verse { Number = 5, TextBosnian = "I govore: 'Srca naša su u omotačima, odvojena od onoga čemu nas ti pozivaš. U ušima je našim gluhoća, a između nas i tebe je pregrada, pa ti radi, i mi ćemo raditi.'", TextEnglish = "And they say, 'Our hearts are within coverings [i.e., screened] from that to which you invite us, and in our ears is deafness, and between us and you is a partition, so work;[1383] indeed, we are working.'", TextGerman = "Sie sagen: Unsere Herzen sind in Hüllen vor dem, wozu du uns aufrufst, und in unseren Ohren ist Schwerhörigkeit, und zwischen uns und dir ist ein Vorhang. Handle also, wir werden ebenfalls handeln." });
                        surah41.VerseList.Add(new Verse { Number = 6, TextBosnian = "Reci: 'Ja sam samo čovjek kao i vi, meni se objavljuje da je vaš Bog samo jedan Bog, zato se Njemu iskreno upravite i od Njega oprosta tražite. A teško višebošcima,", TextEnglish = "Say, [O Muḥammad], 'I am only a man like you to whom it has been revealed that your god is but one God; so take a straight course to Him and seek His forgiveness.' And woe to those who associate others with Allāh", TextGerman = "Sag: Gewiß, ich bin ja nur ein menschliches Wesen wie ihr; mir wird (als Offenbarung) eingegeben, daß euer Gott nur ein Einziger Gott ist. So richtet euch zu Ihm hin und bittet Ihn um Vergebung. Und wehe den Götzendienern," });
                        surah41.VerseList.Add(new Verse { Number = 7, TextBosnian = "koji zekat ne daju i koji u ahiret ne vjeruju!", TextEnglish = "Those who do not give zakāh, and in the Hereafter they are disbelievers.", TextGerman = "die die Abgabe nicht entrichten, und sie, die sie das Jenseits verleugnen!" });
                        surah41.VerseList.Add(new Verse { Number = 8, TextBosnian = "Onima koji vjeruju i dobra djela čine, zbilja pripada nagrada neprekidna.'", TextEnglish = "Indeed, those who believe and do righteous deeds - for them is a reward uninterrupted.", TextGerman = "Gewiß, diejenigen, die glauben und rechtschaffene Werke tun, für sie wird es einen Lohn geben, der nicht aufhört." });
                        surah41.VerseList.Add(new Verse { Number = 9, TextBosnian = "Reci: 'Zar vi zbilja ne vjerujete u Onoga Koji je Zemlju stvorio u dva dana- i još Mu takmace pripisujete?! To je Gospodar svjetova!", TextEnglish = "Say, 'Do you indeed disbelieve in He who created the earth in two days and attribute to Him equals? That is the Lord of the worlds.'", TextGerman = "Sag: Wollt ihr denn wirklich denjenigen verleugnen, Der die Erde in zwei Tagen erschaffen hat, und Ihm andere als Seinesgleichen zur Seite stellen? Das ist der Herr der Weltenbewohner." });
                        surah41.VerseList.Add(new Verse { Number = 10, TextBosnian = "On je nepomična brda po njoj stvorio i blagoslovljenom je učinio i hranu za stanovnike njene na njoj odredio, sve to u četiri dana ravnomjerna - to je za one koji pitaju.", TextEnglish = "And He placed on it [i.e., the earth] firmly set mountains over its surface, and He blessed it and determined therein its [creatures'] sustenance in four days without distinction[1384] - for [the information of] those who ask.", TextGerman = "Er hat in ihr festgegründete Berge gemacht, (die) über ihr (aufragen), und hat sie gesegnet und in ihr die Nahrung im rechten Maß in vier Tagen festgelegt, gleichmäßig für diejenigen, die danach fragen." });
                        surah41.VerseList.Add(new Verse { Number = 11, TextBosnian = "Zatim se na nebo usmjerio dok je ono još maglina bilo, pa njemu i Zemlji rekao: 'Pojavite se milom ili silom!' 'Pojavljujemo se drage volje!', odgovorili su.", TextEnglish = "Then He directed Himself[1385] to the heaven while it was smoke and said to it and to the earth, 'Come [into being],[1386] willingly or by compulsion.' They said, 'We have come willingly.'", TextGerman = "Hierauf wandte Er sich dem Himmel zu, während er noch aus Rauch bestand, und sagte dann zu ihm und zur Erde: Kommt beide her, freiwillig oder widerwillig. Sie sagten: Wir kommen in Gehorsam." });
                        surah41.VerseList.Add(new Verse { Number = 12, TextBosnian = "Tada ih je u dva dana, kao sedam nebesa stvorio, i odredio šta će se na svakom nebu nalaziti. A najbliže nebo smo svjetiljkama ukrasili i njih kao zaštitu učinili. To je odredba Silnoga i Sveznajućeg.", TextEnglish = "And He completed them as seven heavens within two days and inspired [i.e., made known] in each heaven its command. And We adorned the nearest heaven with lamps [i.e., stars, for beauty] and as protection.[1387] That is the determination of the Exalted in Might, the Knowing.", TextGerman = "So führte Er sie als sieben Himmel in zwei Tagen aus und gab jedem Himmel seine Aufgabe ein. Und Wir haben den untersten Himmel mit Lampen geschmückt, und auch als Schutz. Das ist die Anordnung des Allmächtigen und Allwissenden." });
                        surah41.VerseList.Add(new Verse { Number = 13, TextBosnian = "Pa ako se okrenu, ti reci: 'Upozoravam vas na onakav udar kakav je pogodio Ad i Semud',", TextEnglish = "But if they turn away, then say, 'I have warned you of a thunderbolt like the thunderbolt [that struck] ʿAad and Thamūd.", TextGerman = "Wenn sie sich also abwenden, dann sag: Ich warne euch vor einem Donnerschlag gleich dem Donnerschlag der ’Ad und der Tamud," });
                        surah41.VerseList.Add(new Verse { Number = 14, TextBosnian = "Kad su im, sa svih strana, poslanici njihovi dolazili govoreći: 'Ne obožavajte nikoga osim Allaha!', a oni odgovarali: 'Da je Gospodar naš htio, On bi meleke poslao! Doista mi ne vjerujemo u ono što je po vama poslano!'", TextEnglish = "[That occurred] when the messengers had come to them before them and after them, [saying], 'Worship not except Allāh.' They said, 'If our Lord had willed,[1388] He would have sent down the angels, so indeed we, in that with which you have been sent, are disbelievers.'", TextGerman = "als die Gesandten von vorn und von hinten zu ihnen kamen: Dient nur Allah (allein). Sie sagten: Wenn unser Herr gewollt hätte, hätte Er Engel herabgesandt. So verleugnen wir das, womit ihr gesandt worden seid." });
                        surah41.VerseList.Add(new Verse { Number = 15, TextBosnian = "A kad je Ad posrijedi, oni su se na Zemlji bez ikakva prava oholili i govorili: 'Ko je od nas snagom jači A zar nisu znali da je Allah, Koji ih je stvorio, jači od njih - a i znakove Naše su poricali.", TextEnglish = "As for ʿAad, they were arrogant upon the earth without right and said, 'Who is greater than us in strength?' Did they not consider that Allāh who created them was greater than them in strength? But they were rejecting Our signs.", TextGerman = "Was nun die ’Ad angeht, so verhielten sie sich hochmütig auf der Erde ohne Recht und sagten: Wer hat eine stärkere Kraft als wir? Sahen sie denn nicht, daß Allah, Der sie erschaffen hatte, eine stärkere Kraft hat als sie? Aber sie pflegten Unsere Zeichen zu verleugnen." });
                        surah41.VerseList.Add(new Verse { Number = 16, TextBosnian = "I Mi poslasmo protiv njih, u danima uzastopnim, vjetar leden, da bismo im još u dunjalučkom životu dali da osjete patnju poniženja. A patnja na ahiretu bit će, zaista, još više ponižavajuća i niko im neće u pomoć priteći.", TextEnglish = "So We sent upon them a screaming wind during days of misfortune to make them taste the punishment of disgrace in the worldly life; but the punishment of the Hereafter is more disgracing, and they will not be helped.", TextGerman = "Da sandten Wir gegen sie einen eiskalten Wind an unheilvollen Tagen, um sie die schändliche Strafe im diesseitigen Leben kosten zu lassen. Aber die Strafe des Jenseits ist wahrlich schändlicher, und es wird ihnen keine Hilfe zuteil werden." });
                        surah41.VerseList.Add(new Verse { Number = 17, TextBosnian = "A Semud kad je posrijedi, njih smo bili uputili, ali njima je bila milija sljepoća od upute, pa ih je stigao udar ponižavajuće patnje, prema onome kako su zaslužili,", TextEnglish = "And as for Thamūd, We guided them, but they preferred blindness over guidance, so the thunderbolt of humiliating punishment seized them for what they used to earn.", TextGerman = "Was aber die Tamud angeht, so wollten Wir sie rechtleiten, sie aber liebten die Blindheit mehr als die Rechtleitung. Da ergriff sie der Donnerschlag der schmählichen Strafe für das, was sie erworben hatten." });
                        surah41.VerseList.Add(new Verse { Number = 18, TextBosnian = "a one koji su vjerovali i Allaha se bojali Mi smo spasili.", TextEnglish = "And We saved those who believed and used to fear Allāh.", TextGerman = "Und Wir erretteten diejenigen, die glaubten und gottesfürchtig waren." });
                        surah41.VerseList.Add(new Verse { Number = 19, TextBosnian = "A na Dan kad Allahovi neprijatelji za Vatru budu proživljeni i sakupljeni - oni prvi bit će zadržani, pa će zajedno svi biti u stroju potjerani,", TextEnglish = "And [mention, O Muḥammad], the Day when the enemies of Allāh will be gathered to the Fire while they are [driven], assembled in rows,", TextGerman = "Und (denke an) den Tag, da Allahs Feinde zum (Höllen)feuer versammelt und in Reihen gebracht werden." });
                        surah41.VerseList.Add(new Verse { Number = 20, TextBosnian = "i kad dođu do nje, uši njihove, i oči njihove, i kože njihove svjedočit će protiv njih o onom što su radili.", TextEnglish = "Until, when they reach it, their hearing and their eyes and their skins will testify against them of what they used to do.", TextGerman = "Wenn sie dann dort angekommen sind, legen ihr Gehör, ihre Augen und ihre Häute gegen sie Zeugnis ab über das, was sie zu tun pflegten." });
                        surah41.VerseList.Add(new Verse { Number = 21, TextBosnian = "Zašto svjedočite protiv nas?', upitat će oni kože svoje. 'Allah, Koji je dao sposobnost govora svemu, obdario je darom govora i nas', odgovorit će. 'On vas je prvi put stvorio i Njemu ste se, evo, vratili.'", TextEnglish = "And they will say to their skins, 'Why have you testified against us?' They will say, 'We were made to speak by Allāh, who has made everything speak; and He created you the first time, and to Him you are returned.", TextGerman = "Sie werden zu ihren Häuten sagen: Warum habt ihr gegen uns Zeugnis abgelegt? Sie sagen: Allah, Der alles reden läßt, hat uns reden lassen. Er ist es, Der euch das erste Mal erschaffen hat, und zu Ihm werdet ihr zurückgebracht." });
                        surah41.VerseList.Add(new Verse { Number = 22, TextBosnian = "Vi se niste krili da ne bi uši vaše i oči vaše i kože vaše protiv vas svjedočile, već zato što ste mislili da Allah neće saznati mnogo štošta što ste radili.", TextEnglish = "And you were not covering [i.e., protecting] yourselves,[1389] lest your hearing testify against you or your sight or your skins, but you assumed that Allāh does not know much of what you do.", TextGerman = "Und ihr pflegtet euch nicht so zu verstecken, daß euer Gehör, eure Augen und eure Häute nicht Zeugnis gegen euch ablegten. Aber ihr meintet, daß Allah nicht viel wisse von dem, was ihr tut." });
                        surah41.VerseList.Add(new Verse { Number = 23, TextBosnian = "I to uvjerenje koje ste o Gospodaru svome imali upropastilo vas je, pa gubitnici postali ste.", TextEnglish = "And that was your assumption which you assumed about your Lord. It has brought you to ruin, and you have become among the losers.'", TextGerman = "Und diese eure Meinung, die ihr von eurem Herrn hattet, hat euch ins Verderben gestürzt, so daß ihr jetzt zu den Verlierern gehört." });
                        surah41.VerseList.Add(new Verse { Number = 24, TextBosnian = "Pa i ako bi otrpjeli, njihovo boravište bit će Vatra; a ako budu tražili naklonost da se vrate, oni neće biti od onih koji će naklonost zadobiti.", TextEnglish = "So [even] if they are patient, the Fire is a residence for them; and if they ask to appease [Allāh], they will not be of those who are allowed to appease.", TextGerman = "(Auch) wenn sie standhaft sind, so ist (doch) das (Höllen)feuer ein Aufenthaltsort für sie’. Und wenn sie um Erweisung von Gnade bitten, so werden sie doch nicht zu den Begnadigten gehören." });
                        surah41.VerseList.Add(new Verse { Number = 25, TextBosnian = "I Mi smo im bili dosudili drugove - pratioce pa su im lijepim prikazivali ono što je za njima i ono što je pred njima, i na njima se obistinilo ono što je rečeno za narode, džine i ljude koji su prije njih bili i nestali - doista su nastradali.", TextEnglish = "And We appointed for them companions[1390] who made attractive to them what was before them and what was behind them [of sin], and the word [i.e., decree] has come into effect upon them among nations which had passed on before them of jinn and men. Indeed, they [all] were losers.", TextGerman = "Und Wir hatten ihnen Gesellen verschafft, die ihnen dann ausschmückten, was vor ihnen und was hinter ihnen lag. Und unvermeidlich fällig wurde das Wort gegen sie, unter (anderen) Gemeinschaften von den Ginn und den Menschen, die vor ihnen dahingegangen waren. Gewiß, sie waren ja Verlierer." });
                        surah41.VerseList.Add(new Verse { Number = 26, TextBosnian = "Oni koji ne vjeruju govore: 'Ne slušajte ovaj Kur'an, nego pravite buku da biste ga nadvikali!'", TextEnglish = "And those who disbelieve say, 'Do not listen to this Qur’ān and speak noisily[1391] during [the recitation of] it that perhaps you will overcome.'", TextGerman = "Diejenigen, die ungläubig sind, sagen: Hört nicht auf diesen Qur’an, und führt dazwischen unbedachte Reden, auf daß ihr siegen möget." });
                        surah41.VerseList.Add(new Verse { Number = 27, TextBosnian = "Zato ćemo Mi, sigurno, dati da nevjernici iskuse bolnu patnju, i kaznit ćemo ih za ono loše što su činili.", TextEnglish = "But We will surely cause those who disbelieve to taste a severe punishment, and We will surely recompense them for the worst of what they had been doing.", TextGerman = "Ganz gewiß werden Wir diejenigen, die ungläubig sind, eine strenge Strafe kosten lassen, und ganz gewiß werden Wir ihnen das Schlechteste von dem vergelten, was sie zu tun pflegten." });
                        surah41.VerseList.Add(new Verse { Number = 28, TextBosnian = "To je kazna za Allahove neprijatelje - Vatra u kojoj će im vječna kuća biti, kao naknada što su ajete i znakove Naše stalno poricali.", TextEnglish = "That is the recompense of the enemies of Allāh - the Fire. For them therein is the home of eternity as recompense for what they, of Our verses, were rejecting.", TextGerman = "Das ist der Lohn der Feinde Allahs: das (Höllen)feuer, in dem sie die ewige Wohnstätte haben, als Vergeltung dafür, daß sie Unsere Zeichen zu verleugnen pflegten." });
                        surah41.VerseList.Add(new Verse { Number = 29, TextBosnian = "I oni koji nisu vjerovali reći će: 'Gospodaru naš, pokaži nam džine i ljude, one koji su nas zaveli, da ih stavimo pod noge svoje, neka budu među onima koji su najniže.'", TextEnglish = "And those who disbelieved will [then] say, 'Our Lord, show us those who misled us of the jinn and men [so] we may put them under our feet[1392] that they will be among the lowest.'", TextGerman = "Und diejenigen, die ungläubig sind, werden sagen: Unser Herr, zeige uns die beiden von den Ginn und den Menschen, die uns in die Irre geführt haben, daß wir sie unter unsere Füße legen, damit sie zu den Unterlegenen gehören." });
                        surah41.VerseList.Add(new Verse { Number = 30, TextBosnian = "Doista onima koji govore: 'Gospodar naš je Allah', pa poslije budu ustrajni, silaze meleci: 'Ne bojte se i ne žalostite se, i radujte se Džennetu koji vam je obećan.'", TextEnglish = "Indeed, those who have said, 'Our Lord is Allāh' and then remained on a right course - the angels will descend upon them, [saying], 'Do not fear and do not grieve but receive good tidings of Paradise, which you were promised.", TextGerman = "Gewiß, diejenigen, die sagen: Unser Herr ist Allah, und sich hierauf recht verhalten, auf sie kommen die Engel herab: Fürchtet euch nicht, seid nicht traurig, und vernehmt die frohe Botschaft vom (Paradies)garten’, der euch stets versprochen wurde." });
                        surah41.VerseList.Add(new Verse { Number = 31, TextBosnian = "Mi smo zaštitnici vaši u životu dunjalučkom, a i na ahiretu; u njemu ćete imati sve ono što duše vaše zažele, i što god zatražite - imat ćete,", TextEnglish = "We [angels] were your allies in worldly life and [are so] in the Hereafter. And you will have therein whatever your souls desire, and you will have therein whatever you request [or wish]", TextGerman = "Wir sind eure Beschützer im diesseitigen Leben und im Jenseits. Ihr werdet darin haben, was eure Seelen begehren, und ihr werdet darin haben, was ihr erbetet," });
                        surah41.VerseList.Add(new Verse { Number = 32, TextBosnian = "bit će to ugošćenje Oprostitelja grijeha, Milostivog.", TextEnglish = "As accommodation from a [Lord who is] Forgiving and Merciful.'", TextGerman = "eine gastliche Aufnahme von einem Allvergebenden, einem Barmherzigen." });
                        surah41.VerseList.Add(new Verse { Number = 33, TextBosnian = "A ko govori ljepše od onoga koji poziva Allahu, koji dobra djela čini i koji govori: 'Ja sam, doista, musliman!'", TextEnglish = "And who is better in speech than one who invites to Allāh and does righteousness and says, 'Indeed, I am of the Muslims.'", TextGerman = "Und wer spricht bessere Worte als wer zu Allah ruft, rechtschaffen handelt und sagt: Gewiß doch, ich gehöre zu den (Allah) Ergebenen ?" });
                        surah41.VerseList.Add(new Verse { Number = 34, TextBosnian = "Dobro i zlo nisu isto! Zlo dobrim odagnaj, pa će ti dušmanin tvoj odjednom prisni prijatelj postati.", TextEnglish = "And not equal are the good deed and the bad. Repel [evil] by that [deed] which is better; and thereupon, the one whom between you and him is enmity [will become] as though he was a devoted friend.", TextGerman = "Nicht gleich sind die gute Tat und die schlechte Tat. Wehre mit einer Tat, die besser ist, (die schlechte) ab, dann wird derjenige, zwischen dem und dir Feindschaft besteht, so, als wäre er ein warmherziger Freund." });
                        surah41.VerseList.Add(new Verse { Number = 35, TextBosnian = "To mogu postići samo strpljivi; to može postići samo onaj koji ima obilnog udjela u dobru.", TextEnglish = "But none is granted it except those who are patient, and none is granted it except one having a great portion [of good].", TextGerman = "Aber dies wird nur denjenigen dargeboten, die standhaft sind, ja es wird nur demjenigen dargeboten, der ein gewaltiges Glück hat." });
                        surah41.VerseList.Add(new Verse { Number = 36, TextBosnian = "A kad te šejtan pokuša na zle misli navesti, ti zatraži utočište u Allaha. On je, uistinu, Svečujući i Sveznajući.", TextEnglish = "And if there comes to you from Satan an evil suggestion, then seek refuge in Allāh. Indeed, He is the Hearing, the Knowing.", TextGerman = "Und wenn dich vom Satan eine Eingebung aufstachelt, dann suche Zuflucht bei Allah, denn Er ist ja der Allhörende und Allwissende." });
                        surah41.VerseList.Add(new Verse { Number = 37, TextBosnian = "Među znakovima Njegovim su noć i dan, i Sunce i Mjesec. Ne činite sedždu ni Suncu ni Mjesecu, već činite sedždu Allahu, Koji ih je stvorio, ako samo Njemu ibadet činite.", TextEnglish = "And of His signs are the night and day and the sun and moon. Do not prostrate to the sun or to the moon, but prostrate to Allāh, who created them, if it should be Him that you worship.[1393]", TextGerman = "Und zu Seinen Zeichen gehören die Nacht und der Tag, die Sonne und der Mond. Werft euch weder vor der Sonne noch vor dem Mond nieder, sondern werft euch vor Allah nieder, Der sie erschaffen hat, wenn ihr (tatsächlich) Ihm allein dient." });
                        surah41.VerseList.Add(new Verse { Number = 38, TextBosnian = "A ako se oni uzohole- pa, oni koji su kod Gospodara tvoga, slave i veličaju Ga i noću i danju i ne dosađuju se.", TextEnglish = "But if they are arrogant - then those who are near your Lord [i.e., the angels] exalt Him by night and by day, and they do not become weary.", TextGerman = "Wenn sie sich jedoch hochmütig weigern ... (, dann lasse sie,) - diejenigen, die bei deinem Herrn sind, preisen Ihn doch bei Nacht und Tag, ohne Überdruß zu empfinden." });
                        surah41.VerseList.Add(new Verse { Number = 39, TextBosnian = "A od znakova je Njegovih i to što ti vidiš suhu zemlju, a kad na nju spustimo kišu, ona se pokrene i uzbuja. Onaj Ko njoj daje život oživit će sigurno i umrle. On nad svime ima moć.", TextEnglish = "And of His signs is that you see the earth stilled, but when We send down upon it rain, it quivers and grows. Indeed, He who has given it life is the Giver of Life to the dead. Indeed, He is over all things competent.", TextGerman = "Zu Seinen Zeichen gehört es, daß du die Erde demütig siehst. Wenn Wir aber Wasser auf sie herabkommen lassen, regt sie sich und schwillt. Gewiß, Derjenige, Der sie wieder belebt, wird (auch) die Toten wieder lebendig machen, denn gewiß, Er hat zu allem die Macht." });
                        surah41.VerseList.Add(new Verse { Number = 40, TextBosnian = "Zbilja, oni koji ajete Naše izvrću neće se od Nas sakriti. Pa da li je bolji onaj koji u Vatru bude bačen ili onaj koji na Kijametski dan dođe siguran? Radite šta hoćete, On, zaista, dobro vidi šta vi radite.", TextEnglish = "Indeed, those who inject deviation into Our verses[1394] are not concealed from Us. So, is he who is cast into the Fire better or he who comes secure on the Day of Resurrection? Do whatever you will; indeed, He is Seeing of what you do.", TextGerman = "Gewiß, diejenigen, die mit Unseren Zeichen abwegig umgehen, sind Uns nicht verborgen. Ist denn einer, der ins (Höllen)feuer geworfen wird, besser oder jemand, der am Tag der Auferstehung in Sicherheit kommt. Tut, was ihr wollt, denn was ihr tut, sieht Er wohl." });
                        surah41.VerseList.Add(new Verse { Number = 41, TextBosnian = "Zaista oni koji ne vjeruju u Opomenu, pošto im je došla, bit će kažnjeni! To je, zaista, knjiga zaštićena,", TextEnglish = "Indeed, those who disbelieve in the message [i.e., the Qur’ān][1395] after it has come to them...[1396] And indeed, it is a mighty[1397] Book.", TextGerman = "Gewiß, diejenigen, die die Ermahnung verleugnen, nachdem sie zu ihnen gekommen ist ... Es ist fürwahr ein wehrhaftes Buch," });
                        surah41.VerseList.Add(new Verse { Number = 42, TextBosnian = "laž joj ne može prići ni sprijeda ni ozada; to je Objava od Mudroga i Hvaljenoga.", TextEnglish = "Falsehood cannot approach it from before it or from behind it; [it is] a revelation from a [Lord who is] Wise and Praiseworthy.", TextGerman = "an das das Falsche weder von vorn noch von hinten herankommt, eine Offenbarung von einem Allweisen und Lobenswürdigen." });
                        surah41.VerseList.Add(new Verse { Number = 43, TextBosnian = "Tebi se ne govori ništa što već nije rečeno poslanicima prije tebe. Gospodar tvoj zaista prašta, a i bolno kažnjava.", TextEnglish = "Nothing is said to you, [O Muḥammad], except what was already said to the messengers before you. Indeed, your Lord is a possessor of forgiveness and a possessor of painful penalty.", TextGerman = "Es wird dir nur das gesagt, was schon den Gesandten vor dir gesagt wurde. Dein Herr ist wahrlich voll der Vergebung und verhängt (auch) schmerzhafte Strafe." });
                        surah41.VerseList.Add(new Verse { Number = 44, TextBosnian = "A da smo Kur'an sačinili na tuđem jeziku, oni bi sigurno rekli: 'Da su mu bar ajeti razgovijetni! Zar jezik tuđ, a onaj kome se objavljuje Arap Reci: 'On je onima koji vjeruju uputa i lijek. A oni koji ne vjeruju - u ušima njihovim gluhoća je, i slijepi su za njega! Oni kao da se izdaleka dozivaju.", TextEnglish = "And if We had made it a foreign [i.e., non-Arabic] Qur’ān, they would have said, 'Why are its verses not explained in detail [in our language]? Is it a foreign [recitation] and an Arab [messenger]?' Say, 'It is, for those who believe, a guidance and cure.' And those who do not believe - in their ears is deafness, and it is upon them blindness. Those are being called from a distant place.[1398]", TextGerman = "Hätten Wir ihn zu einem fremdsprachigen Qur’an gemacht, hätten sie sicherlich gesagt: Wären doch seine Zeichen ausführlich dargelegt worden! Wie, ein fremdsprachiger (Qur’an) und ein Araber? Sag: Er ist für diejenigen, die glauben, eine Rechtleitung und eine Heilung. Und diejenigen, die nicht glauben, haben Schwerhörigkeit in ihren Ohren, und er ist für sie (wie) Blindheit. Diese sind, als würde ihnen von einem fernen Ort aus zugerufen." });
                        surah41.VerseList.Add(new Verse { Number = 45, TextBosnian = "I Musau smo Knjigu dali, pa su se zbog nje podvojili. A da nije riječi Gospodara tvoga prije izrečene, s ovima bi bilo već svršeno. I oni su spram njega zbilja u sumnji dubokoj.", TextEnglish = "And We had already given Moses the Scripture, but it came under disagreement.[1399] And if not for a word [i.e., decree][1400] that preceded from your Lord, it would have been concluded between them. And indeed they are, concerning it [i.e., the Qur’ān], in disquieting doubt.", TextGerman = "Und Wir gaben bereits Musa die Schrift, doch wurde man darüber uneinig. Und wenn es nicht ein früher ergangenes Wort von deinem Herrn gegeben hätte, so wäre zwischen ihnen wahrlich entschieden worden. Und sie sind darüber fürwahr in starkem Zweifel." });
                        surah41.VerseList.Add(new Verse { Number = 46, TextBosnian = "Ko čini dobro, u svoju korist čini, a ko radi zlo, na svoju štetu radi. A Gospodar tvoj ne čini zulum robovima Svojim.", TextEnglish = "Whoever does righteousness - it is for his [own] soul; and whoever does evil [does so] against it. And your Lord is not ever unjust to [His] servants.", TextGerman = "Wer rechtschaffen handelt, der (tut es) zu seinem eigenen Vorteil, und wer Böses tut, der (tut es) zu seinem eigenen Nachteil. Und dein Herr ist keiner, der den Dienern Unrecht zufügt." });
                        surah41.VerseList.Add(new Verse { Number = 47, TextBosnian = "Samo On zna kad će Smak svijeta nastupiti. A ni plodovi ne izlaze iz cvjetnih čaški svojih, i nijedno žensko ne zanese i ne rodi, a da On to ne zna. A na Dan kad ih On upita: 'Gdje su oni koje ste smatrali Meni ravnim?', oni će odgovoriti: 'Javljamo Ti da niko od nas to ne tvrdi.'", TextEnglish = "To Him [alone] is attributed knowledge of the Hour. And fruits emerge not from their coverings nor does a female conceive or give birth except with His knowledge. And the Day He will call to them, 'Where are My 'partners'?' they will say, 'We announce to You that there is [no longer] among us any witness [to that].'", TextGerman = "Ihm ist das Wissen um die Stunde vorbehalten. Keine Früchte kommen aus ihren Hüllen hervor und kein weibliches Wesen wird schwanger und kommt nieder, außer mit Seinem Wissen. Und am Tag, da Er ihnen zurufen wird: Wo sind denn Meine Teilhaber?, werden sie sagen: Wir geben Dir zur Antwort’: Es gibt unter uns keinen Zeugen (hierfür)." });
                        surah41.VerseList.Add(new Verse { Number = 48, TextBosnian = "I neće biti onih koje su prije molili i uvjerit će se da nikud ne mogu umaći.", TextEnglish = "And lost from them will be those they were invoking before, and they will be certain that they have no place of escape.", TextGerman = "Und entschwunden wird ihnen sein, was sie zuvor anzurufen pflegten, und sie werden überzeugt sein, daß es für sie kein Entrinnen gibt." });
                        surah41.VerseList.Add(new Verse { Number = 49, TextBosnian = "Čovjeku ne dosadi da dobro traži, a kada ga zlo zadesi, onda očajava i nadu gubi.", TextEnglish = "Man is not weary of supplication for good [things], but if evil touches him, he is hopeless and despairing.", TextGerman = "Nicht überdrüssig wird der Mensch, um das Gute zu beten, doch wenn ihm Schlechtes widerfährt, dann ist er sehr verzweifelt und verliert alle Hoffnung." });
                        surah41.VerseList.Add(new Verse { Number = 50, TextBosnian = "Kad ga obaspemo milošću Našom, poslije nesreće koja ga zadesi, on govori: 'Ovo sam i zaslužio, i ja ne mislim da će Čas sudnji doći. A ako budem Gospodaru svome vraćen, kod Njega me čeka Džennet.' A Mi ćemo one koji nisu vjerovali sigurno o onom što su radili obavijestiti i da iskuse patnju tešku - doista ćemo im dati.", TextEnglish = "And if We let him taste mercy from Us after an adversity which has touched him, he will surely say, 'This is [due] to me,[1401] and I do not think the Hour will occur; and [even] if I should be returned to my Lord, indeed, for me there will be with Him the best.' But We will surely inform those who disbelieved about what they did, and We will surely make them taste a massive punishment.", TextGerman = "Und wenn Wir ihn Barmherzigkeit von Uns kosten lassen nach Leid, das ihm widerfuhr, sagt er ganz gewiß: Das steht mir zu. Und ich glaube nicht, daß die Stunde sich einstellen wird. Und wenn ich zu meinem Herrn zurückgebracht werde, werde ich sicherlich das Beste bei Ihm erhalten. Aber Wir werden denjenigen, die ungläubig sind, ganz gewiß kundtun, was sie getan haben; und Wir werden sie ganz gewiß von harter Strafe kosten lassen." });
                        surah41.VerseList.Add(new Verse { Number = 51, TextBosnian = "Kada čovjeku milost Svoju darujemo, on se okreće i udalji se oholo, a kad ga nevolja dotakne, onda se dugo moli.", TextEnglish = "And when We bestow favor upon man, he turns away and distances himself; but when evil touches him, then he is full of extensive supplication.", TextGerman = "Und wenn Wir dem Menschen Gunst erweisen, wendet er sich ab und entfernt sich beiseite. Wenn ihm aber Schlechtes widerfährt, dann verfällt er in ausgedehntes Bittgebet." });
                        surah41.VerseList.Add(new Verse { Number = 52, TextBosnian = "Reci: 'Šta mislite, ako je Kur'an od Allaha, a vi u njega nećete da vjerujete - ko je onda u većoj zabludi od onoga koji je u protivrječju dalekom od Istine", TextEnglish = "Say, 'Have you considered: if it [i.e., the Qur’ān] is from Allāh and you disbelieved in it, who would be more astray than one who is in extreme dissension?'", TextGerman = "Sag: Was meint ihr, wenn er doch von Allah stammt und ihr ihn hierauf verleugnet? Wer ist weiter abgeirrt als jemand, der sich in tiefem Widerstreit befindet?" });
                        surah41.VerseList.Add(new Verse { Number = 53, TextBosnian = "Mi ćemo im pokazati znakove Naše u daljini - na obzorjima, a i u njima samim, dok im ne bude sasvim jasno da je Kur'an istina. A zar nije dovoljno to što je Gospodar tvoj svemu svjedok?", TextEnglish = "We will show them Our signs in the horizons and within themselves until it becomes clear to them that it is the truth.[1402] But is it not sufficient concerning your Lord that He is, over all things, a Witness?[1403]", TextGerman = "Wir werden ihnen Unsere Zeichen am Gesichtskreis und in ihnen selbst zeigen, bis es ihnen klar wird, daß es die Wahrheit ist. Genügt es denn nicht, daß dein Herr über alles Zeuge ist?" });
                        surah41.VerseList.Add(new Verse { Number = 54, TextBosnian = "Oni sumnjaju da će pred Gospodara svoga stati, a On, zaista, obuhvata sve.", TextEnglish = "Unquestionably, they are in doubt about the meeting with their Lord. Unquestionably He is, of all things, encompassing.", TextGerman = "Sicherlich, sie sind im Zweifel über die Begegnung mit ihrem Herrn. Sicherlich, Er umfaßt doch alles." });
                        result = surah41;
                        break;
                    #endregion

                    // 
                    #region
                    case 42:
                        Surah surah42 = new Surah(); surah42.Name = Constants.Surah42Name; surah42.Number = 42; surah42.VerseList = new List<Verse>();
                        surah42.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah42.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ha-mim.", TextEnglish = "Ḥā, Meem.", TextGerman = "Ha-Mim." });
                        surah42.VerseList.Add(new Verse { Number = 2, TextBosnian = "Ajn-sin-kaf", TextEnglish = "ʿAyn, Seen, Qāf.[1405]", TextGerman = "Ayn-Sin-Qaf." });
                        surah42.VerseList.Add(new Verse { Number = 3, TextBosnian = "Eto, tako Allah, Silni i Mudri objavljuje tebi, kao i onima prije tebe.", TextEnglish = "Thus has He revealed to you, [O Muḥammad], and to those before you - Allāh, the Exalted in Might, the Wise.", TextGerman = "So gibt dir Allah (als Offenbarung) ein und (hat auch zuvor) denjenigen, die vor dir waren, (als Offenbarungen eingegeben), (Er), der Allmächtige und Allweise." });
                        surah42.VerseList.Add(new Verse { Number = 4, TextBosnian = "Njegovo je sve što je na nebesima i sve što je na Zemlji, i On je Svevišnji, Veličanstveni!", TextEnglish = "To Him belongs whatever is in the heavens and whatever is in the earth, and He is the Most High, the Most Great.[1406]", TextGerman = "Ihm gehört, was in den Himmeln und was auf der Erde ist, und Er ist der Erhabene und Allgewaltige." });
                        surah42.VerseList.Add(new Verse { Number = 5, TextBosnian = "Gotovo da se nebesa odozgo raspadnu! A meleki veličaju i hvale Gospodara svoga i mole oprost za one koji su na Zemlji. Nije li Allah, zaista, Oprostitelj grijeha, Milostivi?!", TextEnglish = "The heavens almost break from above them,[1407] and the angels exalt [Allāh] with praise of their Lord and ask forgiveness for those on earth. Unquestionably, it is Allāh who is the Forgiving, the Merciful.", TextGerman = "Beinahe brechen die Himmel auseinander von oben her. Und die Engel lobpreisen ihren Herrn und bitten (Ihn) um Vergebung für diejenigen, die auf der Erde sind. Ja sicherlich, Allah ist der Allvergebende und Barmherzige." });
                        surah42.VerseList.Add(new Verse { Number = 6, TextBosnian = "Allah motri na one koji, pored Njega, zaštitnike uzimaju, a ti nisi za njihovu uputu zadužen.", TextEnglish = "And those who take as allies other than Him - Allāh is [yet] Guardian over them; and you, [O Muḥammad], are not over them a manager.", TextGerman = "Über diejenigen, die sich anstatt Seiner Schutzherren nehmen, ist Allah Hüter, und du bist nicht ihr Sachwalter." });
                        surah42.VerseList.Add(new Verse { Number = 7, TextBosnian = "Eto, tako Mi tebi objavljujemo Kur'an, na arapskom jeziku, da bi upozoravao Majku naselja i one oko nje i upozorio na Dan sakupljanja - u koji nema nikakve sumnje. Jedni će u Džennet, a drugi u Džehennem.", TextEnglish = "And thus We have revealed to you an Arabic Qur’ān that you may warn the Mother of Cities [i.e., Makkah] and those around it[1408] and warn of the Day of Assembly, about which there is no doubt. A party will be in Paradise and a party in the Blaze.", TextGerman = "Und so haben Wir dir einen arabischen Qur’an (als Offenbarung) eingegeben, damit du die Mutter der Städte und diejenigen rings umher warnst, und damit du vor dem Tag der Versammlung warnst, an dem es keinen Zweifel gibt. Eine Gruppe wird im (Paradies)garten sein und eine Gruppe in der Feuerglut." });
                        surah42.VerseList.Add(new Verse { Number = 8, TextBosnian = "A da Allah hoće, učinio bi ih sljedbenicima jedne vjere, ali, On u milost Svoju uvodi koga On hoće, a zulumćari nemaju ni zaštitnika, ni pomagača.", TextEnglish = "And if Allāh willed, He could have made them [of] one religion, but He admits whom He wills[1409] into His mercy. And the wrongdoers have not any protector or helper.", TextGerman = "Und wenn Allah gewollt hätte, hätte Er sie wahrlich zu einer einzigen Gemeinschaft gemacht. Aber Er läßt, wen Er will, in Seine Barmherzigkeit eingehen. Und die Ungerechten haben weder Schutzherrn noch Helfer." });
                        surah42.VerseList.Add(new Verse { Number = 9, TextBosnian = "Zar oni da druge, a ne Njega, za zaštitnike uzimaju?! Pa Allah-baš On je jedini Zaštitnik, On oživljava mrtve i On nad svime ima moć.", TextEnglish = "Or have they taken protectors [or allies] besides Him? But Allāh - He is the Protector, and He gives life to the dead, and He is over all things competent.", TextGerman = "Oder haben sie sich anstatt Seiner Schutzherren genommen? Allah (allein) ist doch der Schutzherr. Er macht die Toten wieder lebendig, und Er hat zu allem die Macht." });
                        surah42.VerseList.Add(new Verse { Number = 10, TextBosnian = "A u čemu god se vi razišli, presuda za to u Allaha je! To vam je, eto, Allah, Gospodar moj-u Njega se ja uzdam i Njemu se obraćam;", TextEnglish = "And in anything over which you disagree - its ruling is [to be referred] to Allāh. [Say], 'That is Allāh, my Lord; upon Him I have relied, and to Him I turn back.'[1410]", TextGerman = "Und worüber ihr auch immer uneinig seid, das Urteil darüber steht Allah (allein) zu. Dies ist doch Allah, mein Herr. Auf Ihn verlasse ich mich, und Ihm wende ich mich reuig zu." });
                        surah42.VerseList.Add(new Verse { Number = 11, TextBosnian = "Stvoritelj nebesa i Zemlje bez prethodnog primjera! Učinio vam je od vaše vrste parove, i od stoke parove. Tako vas rasprostire. Ništa nije kao On! On je Svečujući i Svevideći.", TextEnglish = "[He is] Creator of the heavens and the earth. He has made for you from yourselves, mates, and among the cattle, mates; He multiplies you thereby. There is nothing like unto Him,[1411] and He is the Hearing, the Seeing.[1412]", TextGerman = "(Er ist) der Erschaffer der Himmel und der Erde. Er hat euch aus euch selbst Gattinnen gemacht, und auch aus dem Vieh Paare, wodurch Er euch vermehrt. Nichts ist Ihm gleich; und Er ist der Allhörende und Allsehende." });
                        surah42.VerseList.Add(new Verse { Number = 12, TextBosnian = "U Njega su ključevi nebesa i Zemlje, On daje opskrbu u izobilju kome hoće, a i uskraćuje; On, uistinu, zna sve.", TextEnglish = "To Him belong the keys of the heavens and the earth. He extends provision for whom He wills and restricts [it]. Indeed He is, of all things, Knowing.", TextGerman = "Ihm gehören die Schlüssel der Himmel und der Erde. Er gewährt die Versorgung großzügig, wem Er will, und bemißt auch. Gewiß, Er weiß über alles Bescheid." });
                        surah42.VerseList.Add(new Verse { Number = 13, TextBosnian = "On vam propisuje u vjeri isto ono što je naredio Nuhu i ono što objavljujemo tebi, i ono što smo naredili Ibrahimu i Musau i Isau: 'Pravu vjeru ispovijedajte i u tome se ne podvajajte!' Teško je višebošcima ono u što ih pozivaš. Allah odabire za Svoju vjeru onoga koga On hoće i upućuje u nju onoga ko Mu se iskreno obrati.", TextEnglish = "He has ordained for you of religion what He enjoined upon Noah and that which We have revealed to you, [O Muḥammad], and what We enjoined upon Abraham and Moses and Jesus - to establish the religion and not be divided therein. Difficult for those who associate others with Allāh is that to which you invite them. Allāh chooses for Himself whom He wills and guides to Himself whoever turns back [to Him].", TextGerman = "Er hat euch von der Religion festgelegt, was Er Nuh anbefahl und was Wir dir (als Offenbarung) eingegeben haben und was Wir Ibrahim, Musa und ’Isa anbefahlen: Haltet die (Vorschriften der) Religion ein und spaltet euch nicht darin (in Gruppen). Den Götzendienern setzt das schwer zu, wozu du sie aufrufst. Allah erwählt dazu, wen Er will, und leitet dazu, wer sich (Ihm) reuig zuwendet." });
                        surah42.VerseList.Add(new Verse { Number = 14, TextBosnian = "A oni su se, baš onda kad im je došlo znanje, iz zlobe međusobne podvojili. I da nije Riječi Gospodara tvoga prije izrečene o odgađanju kazne do roka određenog, njima bi bilo već presuđeno. A oni kojima se poslije njih Knjiga u nasljedstvo ostavlja, spram nje su u sumnji velikoj.", TextEnglish = "And they did not become divided until after knowledge had come to them - out of jealous animosity between themselves. And if not for a word[1413] that preceded from your Lord [postponing the penalty] until a specified time, it would have been concluded between them. And indeed, those who were granted inheritance of the Scripture after them are, concerning it, in disquieting doubt.", TextGerman = "Und sie spalteten sich erst, nachdem das Wissen zu ihnen gekommen war - aus Mißgunst untereinander. Und wenn es nicht ein früher ergangenes Wort von deinem Herrn auf eine festgesetzte Frist gäbe, so wäre wahrlich zwischen ihnen entschieden worden. Gewiß, diejenigen, denen die Schrift nach ihnen zum Erbe gegeben wurde, sind darüber in starkem Zweifel." });
                        surah42.VerseList.Add(new Verse { Number = 15, TextBosnian = "K tome ti pozivaj i budi ustrajan, onako kako ti se naređuje, i ne slijedi hirove njihove, i reci: 'Ja vjerujem u knjige koje je Allah objavio, i naređeno mi je da među vama pravedan budem; Allah je i naš i vaš Gospodar, nama naša, a vama vaša djela; nema prepiranja između nas i vas; Allah će nas sve sabrati, a kod Njega je konačno odredište.", TextEnglish = "So to that [religion of Allāh] invite, [O Muḥammad],[1414] and remain on a right course as you are commanded and do not follow their inclinations but say, 'I have believed in what Allāh has revealed of scripture [i.e., the Qur’ān], and I have been commanded to do justice among you. Allāh is our Lord and your Lord. For us are our deeds, and for you your deeds.[1415] There is no [need for] argument between us and you.[1416] Allāh will bring us together, and to Him is the [final] destination.'", TextGerman = "Darum rufe du auf und verhalte dich recht, wie dir befohlen wurde. Und folge nicht ihren Neigungen und sag: Ich glaube an das, was Allah an Büchern herabgesandt hat, und mir ist befohlen worden, unter euch gerecht zu handeln. Allah ist unser Herr und euer Herr. Uns unsere Werke und euch eure Werke. Es gibt keine (gemeinsame) Beweisgrundlage zwischen uns und euch. Allah wird uns zusammenbringen. Und zu Ihm ist der Ausgang." });
                        surah42.VerseList.Add(new Verse { Number = 16, TextBosnian = "A oni koji se o Allahu raspravljaju nakon što je Njegov poziv prihvaćen - njihovi dokazi su ništavni kod njihova Gospodara; na njima je gnjev i njima pripada patnja žestoka.", TextEnglish = "And those who argue concerning Allāh after He has been responded to[1417] - their argument is invalid with their Lord, and upon them is [His] wrath, and for them is a severe punishment.", TextGerman = "Diejenigen, die über Allah streiten, nachdem auf Ihn gehört worden ist, deren Beweisgrundlage wird bei ihrem Herrn widerlegt; auf ihnen liegt Zorn, und für sie wird es strenge Strafe geben." });
                        surah42.VerseList.Add(new Verse { Number = 17, TextBosnian = "Allah je Onaj Koji je Knjigu s Istinom i Mjerilo objavio! A šta ti znaš - možda je Čas kijameta blizu!", TextEnglish = "It is Allāh who has sent down the Book in truth and [also] the balance [i.e., justice]. And what will make you perceive? Perhaps the Hour is near.", TextGerman = "Allah ist es, Der das Buch mit der Wahrheit herabgesandt hat, und (auch) die Waage. Was läßt dich wissen? Vielleicht ist die Stunde nahe." });
                        surah42.VerseList.Add(new Verse { Number = 18, TextBosnian = "Požuruju ga oni koji u njega ne vjeruju, a oni koji vjeruju, zbog njega strahuju i daje istina zbilja znaju. Eto, doista su oni koji raspravljaju o Času kijameta u velikoj zabludi!", TextEnglish = "Those who do not believe in it are impatient for it,[1418] but those who believe are fearful of it and know that it is the truth. Unquestionably, those who dispute concerning the Hour are in extreme error.", TextGerman = "Diejenigen, die nicht an sie glauben, wünschen, sie zu beschleunigen. Diejenigen aber, die glauben, sind besorgt wegen ihr und wissen, daß sie Wirklichkeit ist. Doch eben diejenigen, die über die Stunde streiten, befinden sich wahrlich in tiefem Irrtum." });
                        surah42.VerseList.Add(new Verse { Number = 19, TextBosnian = "Allah je dobrostiv prema robovima Svojim; On daje opskrbu kome hoće, i On je Moćni i Silni.", TextEnglish = "Allāh is Subtle[1419] with His servants; He gives provision to whom He wills. And He is the Powerful, the Exalted in Might.", TextGerman = "Allah ist Feinfühlig zu Seinen Dienern. Er versorgt, wen Er will. Und Er ist der Starke und Allmächtige." });
                        surah42.VerseList.Add(new Verse { Number = 20, TextBosnian = "Ko bude želio usjev drugog svijeta, njegov usjev ćemo i povećati! A ko bude želio usjev dunjaluka, dat ćemo mu ga, ali na ahiretu on nema nikakva udjela!", TextEnglish = "Whoever desires the harvest of the Hereafter - We increase for him in his harvest [i.e., reward]. And whoever desires the harvest [i.e., benefits] of this world - We give him thereof, but there is not for him in the Hereafter any share.", TextGerman = "Wer immer die (Ernte) des Saatfeldes für das Jenseits haben will, dem mehren Wir noch (die Ernte) in seinem Saatfeld. Und wer die (Ernte) des Saatfeldes für das Diesseits haben will, dem lassen Wir etwas davon zukommen, für den wird es aber im Jenseits keinen Anteil geben." });
                        surah42.VerseList.Add(new Verse { Number = 21, TextBosnian = "Zar oni da imaju ortake koji im propisuju da vjeruju ono što Allah nije naredio? Da nije Riječi odluke, njima bi već bilo presuđeno. A zulumćarima, doista, pripada patnja bolna.", TextEnglish = "Or have they partners [i.e., other deities] who have ordained for them a religion to which Allāh has not consented? But if not for the decisive word,[1420] it would have been concluded between them. And indeed, the wrongdoers will have a painful punishment.", TextGerman = "Oder haben sie (etwa) Teilhaber, die ihnen als Religion festgelegt haben, was Allah nicht erlaubt hat? Wenn es nicht das (bereits ergangene) Wort der Entscheidung (des Aufschubs bis zum Jüngsten Gericht) gäbe, wäre zwischen ihnen wahrlich (schon jetzt) entschieden worden. Und gewiß, für die Ungerechten wird es schmerzhafte Strafe geben." });
                        surah42.VerseList.Add(new Verse { Number = 22, TextBosnian = "Vidjet ćeš zulumćare kako strepe zbog onoga što su činili, a to će ih sigurno stići; a oni koji su vjerovali i dobra djela činili bit će u divnim džennetskim baščama, sve što zažele imat će u Gospodara svoga; bit će to blagodat velika.", TextEnglish = "You will see the wrongdoers fearful of what they have earned, and it will [certainly] befall them. And those who have believed and done righteous deeds will be in lush regions of the gardens [in Paradise] having whatever they will in the presence of their Lord. That is what is the great bounty.", TextGerman = "Du wirst (dann) die Ungerechten besorgt sehen wegen dessen, was sie verdient haben, und es wird über sie hereinbrechen. Diejenigen aber, die glauben und rechtschaffene Werke tun, werden sich auf den Auen des (Paradies)gartens befinden. Sie haben, was sie wollen, bei ihrem Herrn; das ist die große Huld." });
                        surah42.VerseList.Add(new Verse { Number = 23, TextBosnian = "To je ono čime Allah obveseljava Svoje robove koji vjeruju i čine dobra djela. Reci: 'Ne tražim ja za ovo nikakvu nagradu od vas, osim što tražim pažnju rodbinsku.' A ko učini dobro, Mi ćemo njemu za to povećati dobro. Doista je, Allah, Onaj Koji oprašta grijehe i zahvalan je.", TextEnglish = "It is that of which Allāh gives good tidings to His servants who believe and do righteous deeds. Say, [O Muḥammad], 'I do not ask you for it [i.e., this message] any payment [but] only good will through [i.e., due to] kinship.' And whoever commits a good deed - We will increase for him good therein. Indeed, Allāh is Forgiving and Appreciative.[1421]", TextGerman = "Das ist die frohe Botschaft, die Allah Seinen Dienern, die glauben und rechtschaffene Werke tun, verkündet. Sag: Ich verlange von euch keinen Lohn dafür, es sei denn die Liebe wie zu den Verwandten’. Und wer ein gutes Werk tut, dem schenken Wir dafür noch mehr Gutes. Gewiß, Allah ist Allvergebend und stets zu Dank bereit." });
                        surah42.VerseList.Add(new Verse { Number = 24, TextBosnian = "Zar oni da govore: 'On izmišlja o Allahu laži!' A ako hoće, Allah će tvoje srce zapečatiti! Allah poništava neistinu i utvrđuje Istinu riječima Svojim; On dobro zna šta se u grudima krije.", TextEnglish = "Or do they say, 'He has invented about Allāh a lie'? But if Allāh willed, He could seal over your heart.[1422] And Allāh eliminates falsehood and establishes the truth by His words. Indeed, He is Knowing of that within the breasts.", TextGerman = "Oder sagen sie etwa: Er hat eine Lüge gegen Allah ersonnen? Wenn nun Allah will, kann Er dir das Herz versiegeln. Und Allah löscht das Falsche aus und setzt die Wahrheit mit Seinen Worten durch. Er weiß über das innere Geheimnis Bescheid." });
                        surah42.VerseList.Add(new Verse { Number = 25, TextBosnian = "On je Onaj Koji prima pokajanje od robova Svojih i prašta loše postupke i zna šta radite;", TextEnglish = "And it is He who accepts repentance from His servants and pardons misdeeds, and He knows what you do.", TextGerman = "Er ist es, Der die Reue von Seinen Dienern annimmt und die Missetaten verzeiht. Und Er weiß, was ihr tut." });
                        surah42.VerseList.Add(new Verse { Number = 26, TextBosnian = "I odaziva se onima koji vjeruju i čine dobra djela i iz dobrote Svoje im i više pruža, a nevjernike čeka žestoka patnja.", TextEnglish = "And He answers [the supplication of] those who have believed and done righteous deeds and increases [for] them from His bounty. But the disbelievers will have a severe punishment.", TextGerman = "Und Er erhört diejenigen, die glauben und rechtschaffene Werke tun, und Er gibt ihnen noch mehr von Seiner Huld. Für die Ungläubigen aber wird es strenge Strafe geben." });
                        surah42.VerseList.Add(new Verse { Number = 27, TextBosnian = "A kad bi Allah Svojim robovima preobilnu opskrbu davao, oni bi se na Zemlji osilili, ali On je daje s mjerom, onoliko koliko hoće! Zaista On u potpunosti zna i vidi robove Svoje.", TextEnglish = "And if Allāh had extended [excessively] provision for His servants, they would have committed tyranny throughout the earth. But He sends [it] down in an amount which He wills. Indeed He is, of His servants, Aware and Seeing.", TextGerman = "Und würde Allah Seinen Dienern die Versorgung großzügig zuteilen, würden sie auf der Erde Ungerechtigkeit verüben. Aber Allah läßt im richtigen Maß herabkommen, was Er will. Er hat Kenntnis von Seinen Dienern und sieht sie wohl." });
                        surah42.VerseList.Add(new Verse { Number = 28, TextBosnian = "On je i Onaj Koji spušta kišu kad oni izgube svaku nadu i rasprostire milost Svoju; On je, uistinu, Zaštitnik, Hvaljeni.", TextEnglish = "And it is He who sends down the rain after they had despaired and spreads His mercy. And He is the Protector, the Praiseworthy.", TextGerman = "Und Er ist es, Der den Regen herabkommen läßt, nachdem sie die Hoffnung aufgegeben haben, und Der Seine Barmherzigkeit ausbreitet. Und Er ist der Schutzherr und Lobenswürdige." });
                        surah42.VerseList.Add(new Verse { Number = 29, TextBosnian = "Među znakovima Njegovim je stvaranje nebesa i Zemlje i živih bića koja je rasijao po njima; On ih je kadar sve sabrati kad bude htio.", TextEnglish = "And of His signs is the creation of the heavens and earth and what He has dispersed throughout them of creatures. And He, for gathering them when He wills, is competent.", TextGerman = "Und zu Seinen Zeichen gehört die Erschaffung der Himmel und der Erde und das, was Er an Tieren in ihnen beiden sich ausbreiten läßt. Und Er hat die Macht, sie zu versammeln, wenn Er will." });
                        surah42.VerseList.Add(new Verse { Number = 30, TextBosnian = "Nesreća koja vas zadesi je zbog onoga što su ruke vaše stekle, a mnogo toga On i oprosti.", TextEnglish = "And whatever strikes you of disaster - it is for what your hands have earned; but He pardons much.", TextGerman = "Und was immer euch an Unglück trifft, es ist für das, was eure Hände erworben haben. Und Er verzeiht vieles." });
                        surah42.VerseList.Add(new Verse { Number = 31, TextBosnian = "A vi Mu na Zemlji nećete moći umaći, i vi osim Allaha nemate ni zaštitnika ni pomagača!", TextEnglish = "And you will not cause failure [to Allāh][1423] upon the earth. And you have not besides Allāh any protector or helper.", TextGerman = "Ihr könnt euch (Ihm) auf der Erde nicht entziehen; und ihr habt außer Allah weder Schutzherrn noch Helfer." });
                        surah42.VerseList.Add(new Verse { Number = 32, TextBosnian = "I među znakovima Njegovim su lađe nalik na brda koje morem plove.", TextEnglish = "And of His signs are the ships in the sea, like mountains.", TextGerman = "Und zu Seinen Zeichen gehören die auf dem Meer fahrenden (Schiffe), wie die Berge." });
                        surah42.VerseList.Add(new Verse { Number = 33, TextBosnian = "Ako želi, On umiri vjetar i one na površini njegovoj ostaju nepokretne - doista u tome ima znakova za svakog strpljivog, zahvalnog -", TextEnglish = "If He willed, He could still the wind, and they would remain motionless on its surface. Indeed in that are signs for everyone patient and grateful.", TextGerman = "Wenn Er will, läßt Er den Wind sich legen, dann bleiben sie bewegungslos auf seiner Oberfläche stehen. Darin sind wahrlich Zeichen für jeden sehr Standhaften und sehr Dankbaren;" });
                        surah42.VerseList.Add(new Verse { Number = 34, TextBosnian = "ili ih potopi, zbog onog što su zaradili - a mnogo toga On i oprosti,", TextEnglish = "Or He could destroy them[1424] for what they earned; but He pardons much.", TextGerman = "oder Er läßt sie untergehen für das, was sie verdient haben. Und Er verzeiht vieles." });
                        surah42.VerseList.Add(new Verse { Number = 35, TextBosnian = "da bi oni koji raspravljaju o ajetima i znakovima Našim znali da nemaju kud umaći.", TextEnglish = "And [that is so] those who dispute concerning Our signs may know that for them there is no place of escape.", TextGerman = "Und diejenigen, die über Unsere Zeichen streiten, sollen wissen, daß es für sie kein Entrinnen gibt." });
                        surah42.VerseList.Add(new Verse { Number = 36, TextBosnian = "Ono što vam je dato, samo je uživanje u životu dunjalučkom, a ono što je u Allaha - bolje je i trajnije za one koji vjeruju i na Gospodara svoga se oslanjaju;", TextEnglish = "So whatever thing you have been given - it is but [for] enjoyment of the worldly life. But what is with Allāh is better and more lasting for those who have believed and upon their Lord rely", TextGerman = "Was immer euch gegeben worden ist, ist Nießbrauch des diesseitigen Lebens. Was aber bei Allah ist, ist besser und beständiger für diejenigen, die glauben und sich auf ihren Herrn verlassen," });
                        surah42.VerseList.Add(new Verse { Number = 37, TextBosnian = "I za one koji se klone velikih grijeha i razvrata i koji, kad se rasrde, opraštaju;", TextEnglish = "And those who avoid the major sins and immoralities, and when they are angry, they forgive,", TextGerman = "und diejenigen, die schwerwiegende Sünden und Abscheulichkeiten meiden und, wenn sie zornig sind, (doch) vergeben," });
                        surah42.VerseList.Add(new Verse { Number = 38, TextBosnian = "I za one koji se Gospodaru svome odazivaju, i namaz klanjaju, i o poslovima svojim se dogovaraju, a dio od onoga čime smo ih opskrbili udjeljuju,", TextEnglish = "And those who have responded to their Lord and established prayer and whose affair is [determined by] consultation among themselves, and from what We have provided them, they spend,", TextGerman = "und diejenigen, die auf ihren Herrn hören und das Gebet verrichten, ihre Angelegenheit(en) durch Beratung untereinander (regeln) und von dem ausgeben, womit Wir sie versorgt haben," });
                        surah42.VerseList.Add(new Verse { Number = 39, TextBosnian = "i za one koji, kada ugnjetavanju budu izvrgnuti, uzvrate.", TextEnglish = "And those who, when tyranny strikes them, they retaliate [in a just manner].[1425]", TextGerman = "und diejenigen, die, wenn Gewalttätigkeit gegen sie verübt wird, sich selbst helfen." });
                        surah42.VerseList.Add(new Verse { Number = 40, TextBosnian = "Kazna za zlo je slično zlo, a onoga koji oprosti i izmiri se Allah će nagraditi; On, uistinu, ne voli zulumćare.", TextEnglish = "And the retribution for an evil act is an evil one like it, but whoever pardons and makes reconciliation - his reward is [due] from Allāh. Indeed, He does not like wrongdoers.", TextGerman = "Die Vergeltung für eine böse Tat ist etwas gleich Böses. Wer aber verzeiht und Besserung bringt, dessen Lohn obliegt Allah. Er liebt ja nicht die Ungerechten." });
                        surah42.VerseList.Add(new Verse { Number = 41, TextBosnian = "A onaj ko uzvrati nakon što mu je zulum učinjen, pa takvi ne mogu biti kažnjeni.", TextEnglish = "And whoever retaliates after having been wronged - those have not upon them any cause [for blame].", TextGerman = "Und wer immer sich selbst hilft, nachdem ihm Unrecht zugefügt wurde, gegen jene gibt es keine Möglichkeit (, sie zu belangen)." });
                        surah42.VerseList.Add(new Verse { Number = 42, TextBosnian = "Bit će kažnjeni oni koji ljude tlače i bez ikakva osnova red na Zemlji narušavaju; njih čeka bolna patnja.", TextEnglish = "The cause is only against the ones who wrong the people and tyrannize upon the earth without right. Those will have a painful punishment.", TextGerman = "Eine Möglichkeit (zu belangen) gibt es nur gegen diejenigen, die den Menschen Unrecht zufügen und auf der Erde ohne Recht Gewalttätigkeiten begehen. Für sie wird es schmerzhafte Strafe geben." });
                        surah42.VerseList.Add(new Verse { Number = 43, TextBosnian = "A ko se strpi i oprosti, doista to u istinske postupke spada.", TextEnglish = "And whoever is patient and forgives - indeed, that is of the matters [worthy] of resolve.[1426]", TextGerman = "Wahrlich, wenn einer standhaft erträgt und vergibt, so gehört dies zur Entschlossenheit (in der Handhabe) der Angelegenheiten." });
                        surah42.VerseList.Add(new Verse { Number = 44, TextBosnian = "A kome Allah da da je u zabludi, tome pomagača poslije Njega neće biti! I ti ćeš vidjeti kako će zulumćari, kad ugledaju patnju, povikati: 'Postoji li ikakav način da budemo opet vraćeni?'", TextEnglish = "And he whom Allāh sends astray - for him there is no protector beyond Him. And you will see the wrongdoers, when they see the punishment, saying, 'Is there for return [to the former world] any way?'", TextGerman = "Wen Allah in die Irre gehen läßt, der hat keinen Schutzherrn nach Ihm. Und du siehst, wie die Ungerechten, wenn sie die Strafe sehen, sagen: Gibt es denn einen Weg zur Rückkehr?" });
                        surah42.VerseList.Add(new Verse { Number = 45, TextBosnian = "I vidjet ćeš kako se poniženi i klonuli Vatri izlažu, a oni je kradimice gledaju. Oni koji su vjerovali, reći će: 'Doista su stradalnici oni koji na Kijametskom danu i sebe i porodice svoje upropaste!' A zar zulumćari neće biti u neprestanoj patnji?!", TextEnglish = "And you will see them being exposed to it [i.e., the Fire], humbled from humiliation, looking from [behind] a covert glance. And those who had believed will say, 'Indeed, the [true] losers are the ones who lost themselves and their families on the Day of Resurrection. Unquestionably, the wrongdoers are in an enduring punishment.'", TextGerman = "Und du siehst, wie sie ihm vorgeführt werden, demütig vor Unterwürfigkeit und mit verborgenen Blicken schauend. Und diejenigen, die glauben, sagen: Die (wahren) Verlierer sind diejenigen, die sich selbst und ihre Angehörigen am Tag der Auferstehung verlieren. Ja gewiß, die Ungerechten befinden sich in beständiger Strafe." });
                        surah42.VerseList.Add(new Verse { Number = 46, TextBosnian = "Njima neće biti zaštitnika da im pomognu, osim Allaha; a kome Allah da da je u zabludi, takav put spasa neće ni imati!", TextEnglish = "And there will not be for them any allies to aid them other than Allāh. And whoever Allāh sends astray - for him there is no way.", TextGerman = "Und sie werden keine Schutzherren haben, die ihnen anstatt Allahs helfen (könnten). Wen Allah in die Irre gehen läßt, für den gibt es keinen (Aus)weg." });
                        surah42.VerseList.Add(new Verse { Number = 47, TextBosnian = "Odazovite se Gospodaru svome prije nego što dođe Dan koji Allah neće zaustaviti. Taj Dan utočišta nećete imati i nećete moći poricati.", TextEnglish = "Respond to your Lord before a Day comes from Allāh of which there is no repelling. No refuge will you have that Day, nor for you will there be any denial.[1427]", TextGerman = "Hört auf euren Herrn, bevor von Allah ein Tag kommt, der nicht zurückgewiesen wird. Keinen Zufluchtsort werdet ihr an jenem Tag haben und keine Möglichkeit, (etwas) zu mißbilligen’." });
                        surah42.VerseList.Add(new Verse { Number = 48, TextBosnian = "A ako se okrenu - pa, Mi tebe nismo ni poslali da nad njima bdiješ, ti si dužan samo saopćiti. Kad čovjeku damo da milost Našu osjeti, on joj se obraduje, a kad ih zadesi kakva nesreća zbog onoga što su uradile ruke njihove, onda čovjek blagodati ne priznaje.", TextEnglish = "But if they turn away - then We have not sent you, [O Muḥammad], over them as a guardian; upon you is only [the duty of] notification. And indeed, when We let man taste mercy from Us, he rejoices in it; but if evil afflicts him for what his hands have put forth, then indeed, man is ungrateful.", TextGerman = "Wenn sie sich nun abwenden, so haben Wir dich nicht als Hüter über sie gesandt. Dir obliegt nur die Übermittelung (der Botschaft). Und siehe, wenn Wir den Menschen von Uns Barmherzigkeit kosten lassen, ist er froh darüber. Wenn sie aber etwas Böses trifft für das, was ihre Hände vorausgeschickt haben, gewiß, dann ist der Mensch sehr undankbar." });
                        surah42.VerseList.Add(new Verse { Number = 49, TextBosnian = "Allahova je vlast na nebesima i na Zemlji. On stvara šta hoće! Dariva ženskinje kome hoće, a kome hoće - muškinje,", TextEnglish = "To Allāh belongs the dominion of the heavens and the earth; He creates what He wills. He gives to whom He wills female [children], and He gives to whom He wills males.", TextGerman = "Allah gehört die Herrschaft der Himmel und der Erde. Er erschafft, was Er will. Er schenkt, wem Er will, (nur) weibliche, und Er schenkt, wem Er will, (nur) männliche (Nachkommen)." });
                        surah42.VerseList.Add(new Verse { Number = 50, TextBosnian = "ili im daje i muškinje i ženskinje, a kome hoće, da da je bez poroda; On je, uistinu, Onaj Koji sve zna i svemoćan je.", TextEnglish = "Or He makes them [both] males and females, and He renders whom He wills barren. Indeed, He is Knowing and Competent.", TextGerman = "Oder (Er schenkt) beides zusammen, männliche und weibliche (Nachkommen). Und Er macht, wen Er will, unfruchtbar. Gewiß, Er ist Allwissend und Allmächtig." });
                        surah42.VerseList.Add(new Verse { Number = 51, TextBosnian = "Nijednom čovjeku nije dato da mu se Allah obraća, osim objavom, ili iza zastora, ili da pošalje izaslanika, pa s dozvolom Njegovom objavi ono što On hoće. On je, zaista, uzvišen i mudar.", TextEnglish = "And it is not for any human being that Allāh should speak to him except by revelation or from behind a partition or that He sends a messenger [i.e., angel] to reveal, by His permission, what He wills. Indeed, He is Most High and Wise.", TextGerman = "Und es steht keinem menschlichen Wesen zu, daß Allah zu ihm spricht, außer durch Eingeben (von Offenbarung) oder hinter einem Vorhang, oder indem Er einen Boten sendet, der (ihm) dann mit Seiner Erlaubnis (als Offenbarung) eingibt, was Er will. Gewiß, Er ist Erhaben und Allweise." });
                        surah42.VerseList.Add(new Verse { Number = 52, TextBosnian = "Tako Mi i tebi objavljujemo Kur'an iz odredbe Naše. Ti nisi znao šta je Knjiga, niti što je vjerovanje, ali smo je Mi učinili svjetlom pomoću kojeg upućujemo one robove Naše koje želimo. A Ti, zaista, ukazuješ na Pravi put,", TextEnglish = "And thus We have revealed to you an inspiration of Our command [i.e., the Qur’ān]. You did not know what is the Book or [what is] faith, but We have made it a light by which We guide whom We will of Our servants. And indeed, [O Muḥammad], you guide to a straight path -", TextGerman = "Und ebenso haben Wir dir Geist von Unserem Befehl (als Offenbarung) eingegeben. Du wußtest (vorher) weder, was das Buch noch was der Glaube ist; doch haben Wir es zu einem Licht gemacht, mit dem Wir rechtleiten, wen Wir wollen von Unseren Dienern. Und du leitest ja wahrlich zu einem geraden Weg," });
                        surah42.VerseList.Add(new Verse { Number = 53, TextBosnian = "na put Allahov, Kome pripada sve što je na nebesima i sve što je na Zemlji. I, eto, Allahu se sve vraća!", TextEnglish = "The path of Allāh, to whom belongs whatever is in the heavens and whatever is on the earth. Unquestionably, to Allāh do [all] matters evolve [i.e., return].", TextGerman = "Allahs Weg, Dem gehört, was in den Himmeln und was auf der Erde ist. Sicherlich, zu Allah nehmen die Angelegenheiten ihren Ausgang." });
                        result = surah42;
                        break;
                    #endregion

                    // 
                    #region
                    case 43:
                        Surah surah43 = new Surah(); surah43.Name = Constants.Surah43Name; surah43.Number = 43; surah43.VerseList = new List<Verse>();
                        surah43.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah43.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ha-mim,", TextEnglish = "Ḥā, Meem.[1429]", TextGerman = "Ha-Mim." });
                        surah43.VerseList.Add(new Verse { Number = 2, TextBosnian = "Tako Mi Knjige jasne,", TextEnglish = "By the clear Book,", TextGerman = "Bei dem deutlichen Buch!" });
                        surah43.VerseList.Add(new Verse { Number = 3, TextBosnian = "Mi je objavljujemo kao Kur'an na arapskom jeziku, da biste razumjeli,", TextEnglish = "Indeed, We have made it an Arabic Qur’ān that you might understand.", TextGerman = "Wir haben es ja zu einem arabischen Qur’an gemacht, auf daß ihr begreifen möget;" });
                        surah43.VerseList.Add(new Verse { Number = 4, TextBosnian = "a on je u Glavnoj knjizi, u Nas, cijenjen i savršen.", TextEnglish = "And indeed it is, in the Mother of the Book[1430] with Us, exalted and full of wisdom.[1431]", TextGerman = "und gewiß, er ist in der Urschrift des Buches bei Uns wahrlich erhaben und weise." });
                        surah43.VerseList.Add(new Verse { Number = 5, TextBosnian = "Zar da odustanemo Opomenu vam saopćavati zato što svaku mjeru zla prelazite?", TextEnglish = "Then should We turn the message away, disregarding you, because you are a transgressing people?", TextGerman = "Sollen Wir denn euch bei der Ermahnung unbeachtet lassen, weil ihr maßlose Leute seid?" });
                        surah43.VerseList.Add(new Verse { Number = 6, TextBosnian = "A koliko smo Mi prijašnjim narodima vjerovjesnika poslali", TextEnglish = "And how many a prophet We sent among the former peoples,", TextGerman = "Und wie viele Propheten haben Wir zu den früheren Geschlechtern gesandt!" });
                        surah43.VerseList.Add(new Verse { Number = 7, TextBosnian = "i nijedan im vjerovjesnik nije došao, a da ga nisu izrugivali!", TextEnglish = "But there would not come to them a prophet except that they used to ridicule him.", TextGerman = "Und kein Gesandter kam zu ihnen, ohne daß sie sich über ihn lustig gemacht hätten." });
                        surah43.VerseList.Add(new Verse { Number = 8, TextBosnian = "Zato smo uništavali one koji su od ovih moćniji bili, a prethodio je primjer prijašnjih naroda.", TextEnglish = "And We destroyed greater than them[1432] in [striking] power, and the example of the former peoples has preceded.", TextGerman = "So haben Wir solche vernichtet, die eine stärkere Gewalt hatten als diese. Und vollzogen hat sich das Beispiel an den Früheren." });
                        surah43.VerseList.Add(new Verse { Number = 9, TextBosnian = "Pa ako ih upitaš ko je stvorio nebesa i Zemlju, oni će sigurno reći: 'Stvorio ih je Silni i Sveznajući!'", TextEnglish = "And if you should ask them, 'Who has created the heavens and the earth?' they would surely say, 'They were created by the Exalted in Might, the Knowing,'", TextGerman = "Und wenn du sie fragst, wer die Himmel und die Erde erschaffen hat, sagen sie ganz gewiß: Erschaffen hat sie der Allmächtige und Allwissende," });
                        surah43.VerseList.Add(new Verse { Number = 10, TextBosnian = "Onaj Koji vam je Zemlju kolijevkom učinio i po njoj vam prolaze stvorio, da biste se usmjeravali;", TextEnglish = "[The one] who has made for you the earth a bed and made for you upon it roads that you might be guided", TextGerman = "Der euch die Erde zu einer Lagerstatt gemacht und euch auf ihr Wege gemacht hat, auf daß ihr rechtgeleitet werden möget," });
                        surah43.VerseList.Add(new Verse { Number = 11, TextBosnian = "Onaj Koji s neba s mjerom spušta vodu, pomoću koje u život vraćamo mrtve predjele - tako ćete i vi biti iz grobova izvedeni.", TextEnglish = "And who sends down rain from the sky in measured amounts, and We revive thereby a dead land - thus will you be brought forth -", TextGerman = "und Der Wasser vom Himmel in (bestimmtem) Maß herabkommen läßt. Damit lassen Wir (manch) totes Land auferstehen. So werdet auch ihr hervorgebracht werden -," });
                        surah43.VerseList.Add(new Verse { Number = 12, TextBosnian = "Onaj Koji stvara stvorenja svake vrste i daje vam lađe i stoku da na njima putujete,", TextEnglish = "And who created the species, all of them, and has made for you of ships and animals those which you mount", TextGerman = "und Der die Paare alle erschaffen und euch an Schiffen und Vieh gemacht hat, was ihr besteigen könnt," });
                        surah43.VerseList.Add(new Verse { Number = 13, TextBosnian = "da se na palubama i leđima njihovim smjestite i da se, potom, kada se smjestite na njih, sjetite blagodati Gospodara svoga i da reknete: 'Slavljen i Uzvišen neka je Onaj Koji nam je ovo potčinio, mi to sami ne bismo mogli postići,", TextEnglish = "That you may settle yourselves upon their backs and then remember the favor of your Lord when you have settled upon them and say, 'Exalted is He who has subjected this to us, and we could not have [otherwise] subdued it.[1433]", TextGerman = "damit ihr euch auf ihren Rücken zurechtsetzt und hierauf der Gunst eures Herrn gedenkt, wenn ihr euch darauf zurechtgesetzt habt, und sagt: Preis sei demjenigen, Der uns dies dienstbar gemacht hat! Wir wären hierzu ja nicht imstande gewesen." });
                        surah43.VerseList.Add(new Verse { Number = 14, TextBosnian = "i mi ćemo se sigurno Gospodaru svome vratiti!'", TextEnglish = "And indeed we, to our Lord, will [surely] return.'", TextGerman = "Und wir werden ganz gewiß zu unserem Herrn zurückkehren." });
                        surah43.VerseList.Add(new Verse { Number = 15, TextBosnian = "A oni Njemu jedan dio Njegovih robova pripisuju. Čovjek je, zaista, očevidni nezahvalnik.", TextEnglish = "But they have attributed to Him from His servants a portion.[1434] Indeed, man is clearly ungrateful.", TextGerman = "Und sie stellen Ihm einen Teil von Seinen Dienern (als Seinesgleichen zur Seite). Der Mensch ist ja offenkundig sehr undankbar." });
                        surah43.VerseList.Add(new Verse { Number = 16, TextBosnian = "Zar da između onih koje On stvara uzima Sebi kćeri, a vama odabire sinove?!", TextEnglish = "Or has He taken, out of what He has created, daughters and chosen you for [having] sons?", TextGerman = "Oder hat Er sich etwa unter Seinen Geschöpfen Töchter genommen und für euch die Söhne erwählt?" });
                        surah43.VerseList.Add(new Verse { Number = 17, TextBosnian = "A kad neko od njih bude obaviješten o onome što Svemilosnom sličnim pripisuje, lice mu se ojađenom pomrači.", TextEnglish = "And when one of them is given good tidings of that which he attributes to the Most Merciful in comparison [i.e., a daughter], his face becomes dark, and he suppresses grief.", TextGerman = "Wenn einem von ihnen die frohe Botschaft (von der Geburt) dessen verkündet wird, was er dem Allerbarmer zum Gleichnis zuschreibt, bleibt sein Gesicht finster, und er hält (seinen Grimm) zurück." });
                        surah43.VerseList.Add(new Verse { Number = 18, TextBosnian = "Zar da ono koje u nakitu biva podizano i koje pri sporenju nejasno zbori pripisuju Allahu?!", TextEnglish = "So is one brought up in ornaments while being during conflict unevident[1435] [attributed to Allāh]?", TextGerman = "(Wollen sie Allah) denn jemanden (zuschreiben), der im Schmuck aufgezogen wird und im Streit nicht deutlich wird?" });
                        surah43.VerseList.Add(new Verse { Number = 19, TextBosnian = "Oni meleke koji su robovi Svemilosnog ženskinjem smatraju. Da nisu prisustvovali stvaranju njihovu?! Njihova tvrdnja bit će zapisana i oni će odgovarati!", TextEnglish = "And they described the angels, who are servants of the Most Merciful, as females. Did they witness their creation? Their testimony will be recorded, and they will be questioned.", TextGerman = "Und sie machen die Engel, die sie ja Diener des Allerbarmers sind, zu weiblichen Wesen. Waren sie denn Zeugen ihrer Erschaffung? Ihr Zeugnis wird aufgeschrieben, und sie werden befragt werden." });
                        surah43.VerseList.Add(new Verse { Number = 20, TextBosnian = "I oni vele: 'Da je htio Svemilosni, njih ne bismo obožavali!' Oni o tome ništa ne znaju, oni samo lažu.", TextEnglish = "And they said, 'If the Most Merciful had willed, we would not have worshipped them.' They have of that no knowledge. They are not but misjudging.", TextGerman = "Und sie sagen: Wenn der Allerbarmer gewollt hätte, hätten wir ihnen nicht gedient. Sie haben kein Wissen davon; sie stellen nur Schätzungen an." });
                        surah43.VerseList.Add(new Verse { Number = 21, TextBosnian = "Ili smo im dali Knjigu prije toga pa se nje pridržavaju?!", TextEnglish = "Or have We given them a book before it [i.e., the Qur’ān] to which they are adhering?", TextGerman = "Oder haben Wir ihnen etwa vor ihm eine Schrift gegeben, an der sie sich festhalten könnten?" });
                        surah43.VerseList.Add(new Verse { Number = 22, TextBosnian = "Oni čak govore: 'Mi smo zatekli pretke naše kako ispovijedaju vjeru i, prateći njihove tragove, mi smo na Pravom putu.'", TextEnglish = "Rather, they say, 'Indeed, we found our fathers upon a religion, and we are in their footsteps [rightly] guided.'", TextGerman = "Nein! Vielmehr sagen sie: Wir haben ja (bereits) unsere Väter in einer (bestimmten) Glaubensrichtung vorgefunden, und auf ihren Spuren sind wir rechtgeleitet." });
                        surah43.VerseList.Add(new Verse { Number = 23, TextBosnian = "Isto tako, prije tebe, Mi ni u jedan grad nismo poslanika poslali, a da oni koji su raskošno živjeli nisu govorili: 'Zatekli smo pretke naše kako ispovijedaju vjeru i mi tragove njihove slijedimo.'", TextEnglish = "And similarly, We did not send before you any warner into a city except that its affluent said, 'Indeed, we found our fathers upon a religion, and we are, in their footsteps, following.'", TextGerman = "So haben Wir (auch) vor dir in eine Stadt keinen Warner gesandt, ohne daß diejenigen, die in ihr üppig lebten, gesagt hätten: Wir haben ja (bereits) unsere Väter in einer bestimmten Glaubensrichtung vorgefunden, und auf ihren Spuren folgen wir ihrem Vorbild." });
                        surah43.VerseList.Add(new Verse { Number = 24, TextBosnian = "Zar i onda', govorio bi on, 'kad vam ja donosim bolju od one koju ste od predaka vaših upamtili?' A oni bi odgovarali: 'Ne vjerujemo mi u ono što je po vama poslano!'", TextEnglish = "[Each warner] said, 'Even if I brought you better guidance than that [religion] upon which you found your fathers?' They said, 'Indeed we, in that with which you were sent, are disbelievers.'", TextGerman = "Er sagte: Etwa auch, wenn ich euch bringe, was eine bessere Rechtleitung enthält als das, worin ihr eure Väter vorgefunden habt? Sie sagten: Wir verleugnen ja das, womit ihr gesandt worden seid." });
                        surah43.VerseList.Add(new Verse { Number = 25, TextBosnian = "I Mi smo ih kažnjavali, pa vidi kakav je bio kraj onih koji su poslanike u laž ugonili.", TextEnglish = "So We took retribution from them; then see how was the end of the deniers.", TextGerman = "Da übten Wir an ihnen Vergeltung. So schau, wie das Ende der Leugner war." });
                        surah43.VerseList.Add(new Verse { Number = 26, TextBosnian = "A kad Ibrahim reče ocu svome i narodu svome: 'Nemam ja ništa s onima koje vi obožavate,", TextEnglish = "And [mention, O Muḥammad], when Abraham said to his father and his people, 'Indeed, I am disassociated from that which you worship", TextGerman = "Und als Ibrahim zu seinem Vater und seinem Volk sagte: Gewiß, ich sage mich los von dem, dem ihr dient," });
                        surah43.VerseList.Add(new Verse { Number = 27, TextBosnian = "osim s Onim Koji me je stvorio, jer će me On, doista, na Pravi put uputiti.'", TextEnglish = "Except for He who created me; and indeed, He will guide me.'", TextGerman = "außer Demjenigen, Der mich erschaffen hat; denn Er wird mich gewiß rechtleiten." });
                        surah43.VerseList.Add(new Verse { Number = 28, TextBosnian = "I On to učini riječju trajnom za potomstvo svoje, da bi se vratili.", TextEnglish = "And he made it[1436] a word remaining among his descendants that they might return [to it].", TextGerman = "Und er machte es zu einem bleibenden Wort unter seinen Nachkommen, auf daß sie umkehren mögen." });
                        surah43.VerseList.Add(new Verse { Number = 29, TextBosnian = "A Ja sam čak dopustio ovima, a i precima njihovim da uživaju sve dok im nije došla Istina i Poslanik očevidni.", TextEnglish = "However, I gave enjoyment to these [people of Makkah] and their fathers[1437] until there came to them the truth and a clear Messenger.[1438]", TextGerman = "Aber nein! Ich habe diese da und ihre Väter genießen lassen, bis die Wahrheit und ein deutlicher Gesandter zu ihnen gekommen ist." });
                        surah43.VerseList.Add(new Verse { Number = 30, TextBosnian = "A kad im je Istina došla, rekoše: 'Ovo je vradžbina, i mi u nju nikako ne vjerujemo!'", TextEnglish = "But when the truth came to them, they said, 'This is magic, and indeed we are, concerning it, disbelievers.'", TextGerman = "Als nun die Wahrheit zu ihnen gekommen ist, sagen sie: Das ist Zauberei, und wir verleugnen ihn." });
                        surah43.VerseList.Add(new Verse { Number = 31, TextBosnian = "I rekoše: 'Zašto ovaj Kur'an nije objavljen nekom velikom čovjeku iz ova dva grada?!'", TextEnglish = "And they said, 'Why was this Qur’ān not sent down upon a great man from [one of] the two cities?'[1439]", TextGerman = "Und sie sagen: Wäre doch dieser Qur’an einem bedeutenden Mann aus den beiden Städten offenbart worden!" });
                        surah43.VerseList.Add(new Verse { Number = 32, TextBosnian = "A da oni ne raspodjeljuju milost Gospodara tvoga?! Mi raspodjeljujemo među njima ono što im je potrebno za život na dunjaluku i Mi jedne nad drugima uzdižemo po stepenima, da bi jedni druge uzimali da im služe. A milost Gospodara tvoga bolja je od onoga što oni gomilaju.", TextEnglish = "Do they distribute the mercy of your Lord? It is We who have apportioned among them their livelihood in the life of this world and have raised some of them above others in degrees [of rank] that they may make use of one another for service. But the mercy of your Lord is better than whatever they accumulate.", TextGerman = "Verteilen etwa sie die Barmherzigkeit deines Herrn? Wir verteilen doch unter ihnen ihren Lebensunterhalt im diesseitigen Leben und erhöhen die einen von ihnen über die anderen um Rangstufen, damit die einen von ihnen die anderen in Dienst nehmen. Aber die Barmherzigkeit deines Herrn ist besser als das, was sie zusammentragen." });
                        surah43.VerseList.Add(new Verse { Number = 33, TextBosnian = "A da neće svi ljudi postati jedan ummet - nevjernici, Mi bismo krovove kuća, onih koji ne vjeruju u Svemilosnog, od srebra učinili, a i stepeništa uz koja se penju,", TextEnglish = "And if it were not that the people would become one community [of disbelievers],[1440] We would have made for those who disbelieve in the Most Merciful - for their houses - ceilings and stairways of silver upon which to mount.", TextGerman = "Und damit die Menschen nicht eine einzige Gemeinschaft werden (, sahen Wir davon ab, sonst), hätten Wir wahrlich denjenigen, die den Allerbarmer verleugnen, für ihre Häuser Decken aus Silber gemacht und (auch) Treppen, auf denen sie hätten hochsteigen können," });
                        surah43.VerseList.Add(new Verse { Number = 34, TextBosnian = "i vrata kuća njihovih i divane na kojima se odmaraju,", TextEnglish = "And for their houses - doors and couches [of silver] upon which to recline", TextGerman = "und Türen für ihre Häuser, und Liegen, auf denen sie sich lehnen können," });
                        surah43.VerseList.Add(new Verse { Number = 35, TextBosnian = "i ukrase od zlata bismo im dali, jer sve je to samo uživanje u životu dunjalučkom, a ahiret, u Gospodara tvoga, bit će za bogobojazne.", TextEnglish = "And gold ornament. But all that is not but the enjoyment of worldly life. And the Hereafter with your Lord is for the righteous.", TextGerman = "und (weitere) Zierde. All das ist aber nur Nießbrauch des diesseitigen Lebens. Das Jenseits bei deinem Herrn ist jedoch für die Gottesfürchtigen (bestimmt)." });
                        surah43.VerseList.Add(new Verse { Number = 36, TextBosnian = "Onome ko se bude slijepim pravio pred Opomenom Svemilosnog, Mi ćemo šejtana natovariti, pa će mu on nerazdvojni drug postati;", TextEnglish = "And whoever is blinded from remembrance of the Most Merciful - We appoint for him a devil, and he is to him a companion.", TextGerman = "Wer für die Ermahnung des Allerbarmers blind ist, dem verschaffen Wir einen Satan, der ihm dann zum Gesellen wird." });
                        surah43.VerseList.Add(new Verse { Number = 37, TextBosnian = "oni - šejtani ljude će od Pravog puta odvraćati, a oni će misliti da su na Pravom putu.", TextEnglish = "And indeed, they [i.e., the devils] avert them from the way [of guidance] while they think that they are [rightly] guided", TextGerman = "Und sie halten sie wahrlich vom Weg ab, und diese meinen, sie seien rechtgeleitet." });
                        surah43.VerseList.Add(new Verse { Number = 38, TextBosnian = "I kada koji od njih dođe pred Nas, reći će: 'Kamo sreće da je između mene i tebe bila tolika razdaljina kolika je između istoka i zapada! Kako si ti bio zao drug!'", TextEnglish = "Until, when he comes to Us [at Judgement], he says [to his companion], 'How I wish there was between me and you the distance between the east and west; and what a wretched companion.'", TextGerman = "Wenn er dann (schließlich) zu Uns kommt, sagt er: O wäre doch zwischen mir und dir eine Entfernung wie zwischen Osten und Westen ! - ein schlimmer Geselle!" });
                        surah43.VerseList.Add(new Verse { Number = 39, TextBosnian = "Taj dan neće vam koristiti to što ćete u patnji zajedno biti, kad ste zulum činili.", TextEnglish = "And never will it benefit you that Day, when you have wronged, that you are [all] sharing in the punishment.", TextGerman = "Heute wird euch, da ihr Unrecht getan habt, nicht nützen, daß ihr an der(selben) Strafe teilhabt." });
                        surah43.VerseList.Add(new Verse { Number = 40, TextBosnian = "Zar ti da dozoveš gluhe i uputiš slijepe i one koji su u očitaj zabludi?!", TextEnglish = "Then will you make the deaf hear, [O Muḥammad], or guide the blind or he who is in clear error?", TextGerman = "Bist du es etwa, der die Tauben hören läßt oder die Blinden und diejenigen recht leitet, die sich in deutlichem Irrtum befinden?" });
                        surah43.VerseList.Add(new Verse { Number = 41, TextBosnian = "Ako bismo ti dušu uzeli, njih bismo, sigurno, kaznili,", TextEnglish = "And whether [or not] We take you away [in death], indeed, We will take retribution upon them.", TextGerman = "Sollten Wir dich (vorher) fortnehmen, so werden Wir (doch) an ihnen Vergeltung üben." });
                        surah43.VerseList.Add(new Verse { Number = 42, TextBosnian = "ili, kad bismo ti htjeli pokazati ono što smo im obećali, pa Mi nad njima, zaista, potpunu moć imamo.", TextEnglish = "Or whether [or not] We show you that which We have promised them, indeed, We are Perfect in Ability.", TextGerman = "Oder Wir zeigen dir, was Wir ihnen angedroht haben; so haben Wir ja ganz die Macht über sie." });
                        surah43.VerseList.Add(new Verse { Number = 43, TextBosnian = "Zato se drži onoga što ti se objavljuje, a ti si, uistinu, na Pravom putu.", TextEnglish = "So adhere to that which is revealed to you. Indeed, you are on a straight path.", TextGerman = "Halte also fest an dem, was dir (als Offenbarung) eingegeben worden ist; gewiß, du befindest dich auf einem geraden Weg." });
                        surah43.VerseList.Add(new Verse { Number = 44, TextBosnian = "Kur'an je, zaista, čast i tebi i narodu tvome; pa bit ćete pitani!", TextEnglish = "And indeed, it is a remembrance[1441] for you and your people, and you [all] are going to be questioned.", TextGerman = "Das ist wahrlich eine Ermahnung für dich und dein Volk. Und ihr werdet befragt werden." });
                        surah43.VerseList.Add(new Verse { Number = 45, TextBosnian = "A pitaj one koje smo prije tebe od Naših poslanika poslali-da li smo Mi naređivali da se, osim Svemilosnog, božanstva obožavaju?", TextEnglish = "And ask those We sent before you of Our messengers; have We made besides the Most Merciful deities to be worshipped?", TextGerman = "Und frage wen von Unseren Gesandten Wir vor dir gesandt haben, ob Wir anstatt des Allerbarmers (andere) Götter eingesetzt haben, denen man dienen soll." });
                        surah43.VerseList.Add(new Verse { Number = 46, TextBosnian = "Još davno Mi smo poslali Musaa sa znamenjima Našim faraonu i glavešinama njegovim, i on je rekao: 'Ja sam, doista, Gospodara svjetova poslanik!'", TextEnglish = "And certainly did We send Moses with Our signs to Pharaoh and his establishment, and he said, 'Indeed, I am the messenger of the Lord of the worlds.'", TextGerman = "Und Wir sandten bereits Musa mit Unseren Zeichen zu Fir’aun und seiner führenden Schar. Er sagte: Gewiß, ich bin der Gesandte des Herrn der Weltenbewohner." });
                        surah43.VerseList.Add(new Verse { Number = 47, TextBosnian = "I pošto im je donio znakove Naše, oni su ih, odjednom, počeli ismijavati.", TextEnglish = "But when he brought them Our signs, at once they laughed at them.", TextGerman = "Als er nun zu ihnen mit Unseren Zeichen kam, lachten sie sogleich über sie." });
                        surah43.VerseList.Add(new Verse { Number = 48, TextBosnian = "A nismo im pokazivali nijedan znak a da nije veći bio nego prethodni! I patnji smo ih podvrgli, ne bi li se povratili!", TextEnglish = "And We showed them not a sign except that it was greater than its sister, and We seized them with affliction that perhaps they might return [to faith].", TextGerman = "Und Wir zeigten ihnen kein Zeichen, das nicht größer gewesen wäre als das vorhergehende. Und Wir ergriffen sie mit der Strafe, auf daß sie umkehren mochten." });
                        surah43.VerseList.Add(new Verse { Number = 49, TextBosnian = "A oni su govorili: 'O ti, čarobnjače! Moli za nas Gospodara svoga, na temelju tebi datog obećanja, mi ćemo sigurno Pravim putem poći!'", TextEnglish = "And they said [to Moses], 'O magician, invoke for us your Lord by what He has promised you. Indeed, we will be guided.'", TextGerman = "Und sie sagten: O du Zauberer, rufe für uns deinen Herrn an auf Grund dessen, was Er dir anvertraut hat, dann werden wir uns wahrlich rechtleiten lassen." });
                        surah43.VerseList.Add(new Verse { Number = 50, TextBosnian = "A čim bismo ih nevolje oslobodili, začas bi obavezu prekršili.", TextEnglish = "But when We removed from them the affliction, at once they broke their word.", TextGerman = "Als Wir dann die Strafe von ihnen hinwegnahmen, da brachen sie sogleich ihr Wort." });
                        surah43.VerseList.Add(new Verse { Number = 51, TextBosnian = "I faraon obznani narodu svome: 'O narode moj', reče on, 'zar meni ne pripada vlast u Misiru i ove rijeke koje ispred mene teku - ne vidite li?!'", TextEnglish = "And Pharaoh called out among his people; he said, 'O my people, does not the kingdom of Egypt belong to me, and these rivers flowing beneath me; then do you not see?", TextGerman = "Und Fir’aun ließ unter seinem Volk ausrufen; er sagte: O mein Volk, gehört mir nicht die Herrschaft über Ägypten und (auch) diese Flüsse, die unter mir strömen? Wollt ihr denn nicht einsichtig sein?" });
                        surah43.VerseList.Add(new Verse { Number = 52, TextBosnian = "Svakako sam ja bolji od ovoga bijednika koji jedva razgovijetno govori!", TextEnglish = "Or am I [not] better than this one [i.e., Moses] who is insignificant and hardly makes himself clear?[1442]", TextGerman = "Oder bin ich nicht besser als dieser, der verächtlich ist und sich kaum deutlich äußern kann?" });
                        surah43.VerseList.Add(new Verse { Number = 53, TextBosnian = "Zašto mu nisu stavljene narukvice od zlata, ili zašto zajedno s njim nisu došli meleci?", TextEnglish = "Then why have there not been placed upon him bracelets of gold or come with him the angels in conjunction?'", TextGerman = "Würden doch Armringe aus Gold auf ihn herabgeworfen oder die Engel mit ihm als Begleitung kommen!" });
                        surah43.VerseList.Add(new Verse { Number = 54, TextBosnian = "I on lahkoumnim učini narod svoj, pa mu se pokoriše; oni su, doista, bili narod buntovni.", TextEnglish = "So he bluffed his people, and they obeyed him. Indeed, they were [themselves] a people defiantly disobedient [of Allāh].", TextGerman = "Er fand sein Volk leicht (zu beeinflussen), und da gehorchten sie ihm. Gewiß, sie waren ein Volk von Frevlern." });
                        surah43.VerseList.Add(new Verse { Number = 55, TextBosnian = "A kad izazvaše Naš gnjev, Mi ih kaznismo i sve ih potopismo,", TextEnglish = "And when they angered Us, We took retribution from them and drowned them all.", TextGerman = "Als sie Uns Kummer bereiteten, übten Wir an ihnen Vergeltung, und so ließen Wir sie allesamt ertrinken." });
                        surah43.VerseList.Add(new Verse { Number = 56, TextBosnian = "i učinismo ih primjerom i poukom narodima kasnijim.", TextEnglish = "And We made them a precedent and an example for the later peoples.", TextGerman = "Wir machten sie zu Vorgängern und zu einem Beispiel für die späteren (Geschlechter)." });
                        surah43.VerseList.Add(new Verse { Number = 57, TextBosnian = "A kad je narodu tvome kao primjer naveden sin Merjemin, odjednom su oni, zbog toga, uz smijeh zagalamili", TextEnglish = "And when the son of Mary was presented as an example,[1443] immediately your people laughed aloud.", TextGerman = "Und als der Sohn Maryams als Beispiel angeführt wurde, da brach dein Volk sogleich in Geschrei aus." });
                        surah43.VerseList.Add(new Verse { Number = 58, TextBosnian = "i rekli: 'Da li su naša božanstva bolja ili on?' A naveli su ti ga kao primjer samo zato da bi se prepirali. Štaviše, oni su narod svađalački.", TextEnglish = "And they said, 'Are our gods better, or is he?'[1444] They did not present it [i.e., the comparison] except for [mere] argument. But, [in fact], they are a people prone to dispute.", TextGerman = "Und sie sagten: Sind unsere Götter besser oder er? Sie führten ihn dir nur zum Streiten an. Nein! Vielmehr sind sie streitsüchtige Leute." });
                        surah43.VerseList.Add(new Verse { Number = 59, TextBosnian = "On je bio samo rob na koga smo Mi blagodati prosuli i učinili ga primjerom sinovima Israilovim.", TextEnglish = "He [i.e., Jesus] was not but a servant upon whom We bestowed favor, and We made him an example for the Children of Israel.", TextGerman = "Er ist nur ein Diener, dem Wir Gunst erwiesen und den Wir zu einem Beispiel für die Kinder Isra’ils gemacht haben." });
                        surah43.VerseList.Add(new Verse { Number = 60, TextBosnian = "A da hoćemo, Mi bismo učinili da vas na Zemlji meleci naslijede;", TextEnglish = "And if We willed, We could have made [instead] of you angels succeeding [one another][1445] on the earth.", TextGerman = "Und wenn Wir wollten, könnten Wir euch wahrlich zu Engeln machen, die zu Statthaltern auf der Erde würden." });
                        surah43.VerseList.Add(new Verse { Number = 61, TextBosnian = "zaista je on predznak Smaka svijeta, zato nikako ne sumnjajte u njega i slijedite Mene! Ovo je Pravi put,", TextEnglish = "And indeed, he [i.e., Jesus] will be [a sign for] knowledge of the Hour, so be not in doubt of it, and follow Me.[1446] This is a straight path.", TextGerman = "Und er ist wahrlich Wissen von der Stunde (des Gerichts)’. So hegt ja keinen Zweifel an ihr und folgt mir. Das ist ein gerader Weg." });
                        surah43.VerseList.Add(new Verse { Number = 62, TextBosnian = "i neka vas šejtan nikako ne odvrati; on vam je, doista, neprijatelj otvoreni.", TextEnglish = "And never let Satan avert you. Indeed, he is to you a clear enemy.", TextGerman = "Und der Satan soll euch ja nicht abhalten. Gewiß, er ist euch ein deutlicher Feind." });
                        surah43.VerseList.Add(new Verse { Number = 63, TextBosnian = "A kad je Isa očite znakove donio, on je rekao: 'Donosim vam mudrost i dolazim da vam objasnim ono u čemu se razilazite. Zato se Allaha bojte i meni se pokoravajte.", TextEnglish = "And when Jesus brought clear proofs, he said, 'I have come to you with wisdom [i.e., prophethood] and to make clear to you some of that over which you differ, so fear Allāh and obey me.", TextGerman = "Und als ’Isa mit den klaren Beweisen kam, sagte er: Ich komme ja zu euch mit der Weisheit, und um euch einiges von dem klarzumachen, worüber ihr uneinig seid. So fürchtet Allah und gehorcht mir." });
                        surah43.VerseList.Add(new Verse { Number = 64, TextBosnian = "Allah je i moj i vaš Gospodar, pa Njemu u ibadetu budite! Ovo je Pravi put!'", TextEnglish = "Indeed, Allāh is my Lord and your Lord, so worship Him. This is a straight path.'", TextGerman = "Gewiß, Allah ist ja mein Herr und euer Herr; so dient Ihm. Das ist ein gerader Weg." });
                        surah43.VerseList.Add(new Verse { Number = 65, TextBosnian = "Ali su se stranke iz redova njihovih podvojile. Pa teško onima koji su zulum učinili od kazne na Danu bolnome!", TextEnglish = "But the denominations from among them differed [and separated], so woe to those who have wronged from the punishment of a painful Day.", TextGerman = "Doch wurden die Gruppierungen untereinander uneinig; so wehe denjenigen, die Unrecht tun, vor der Strafe eines schmerzhaften Tages!" });
                        surah43.VerseList.Add(new Verse { Number = 66, TextBosnian = "Čekaju li oni drugo doli da im Smak svijeta dođe nenadano, i da oni i ne osjete?!", TextEnglish = "Are they waiting except for the Hour to come upon them suddenly while they perceive not?", TextGerman = "Erwarten sie nur die Stunde, daß sie plötzlich über sie kommt, ohne daß sie merken?" });
                        surah43.VerseList.Add(new Verse { Number = 67, TextBosnian = "Taj dan prijatelji će jedni drugima neprijatelji biti, osim onih koji su se Allaha bojali i grijeha klonili.", TextEnglish = "Close friends, that Day, will be enemies to each other, except for the righteous", TextGerman = "Die Freunde werden an jenem Tag einer des anderen Feind sein, außer den Gottesfürchtigen." });
                        surah43.VerseList.Add(new Verse { Number = 68, TextBosnian = "O robovi Moji, za vas danas straha neće biti, niti ćete tugovati!'", TextEnglish = "[To whom Allāh will say], 'O My servants, no fear will there be concerning you this Day, nor will you grieve,", TextGerman = "O meine Diener, keine Furcht soll heute über euch kommen, noch sollt ihr traurig sein," });
                        surah43.VerseList.Add(new Verse { Number = 69, TextBosnian = "A onima koji su u ajete Naše vjerovali i koji su muslimani bili - bit će rečeno:", TextEnglish = "[You] who believed in Our verses and were Muslims.", TextGerman = "(ihr,) die ihr an Unsere Zeichen glaubtet und (Uns) ergeben wart." });
                        surah43.VerseList.Add(new Verse { Number = 70, TextBosnian = "Uđite u Džennet, vi i supružnici vaši, radosni!'", TextEnglish = "Enter Paradise, you and your kinds,[1447] delighted.'", TextGerman = "Geht in den (Paradies)garten ein, euch und euren Gattinnen wird Freude bereitet." });
                        surah43.VerseList.Add(new Verse { Number = 71, TextBosnian = "Oni će biti služeni iz posuda i čaša od zlata. U njemu će biti sve što duše zažele i čime se oči naslađuju, i u njemu ćete vječno boraviti.", TextEnglish = "Circulated among them will be plates and vessels of gold. And therein is whatever the souls desire and [what] delights the eyes, and you will abide therein eternally.", TextGerman = "Es werden ihnen Schüsseln aus Gold und Trinkschalen herumgereicht; darin gibt es, was die Seelen begehren und köstlich für die Augen ist. Und ewig werdet ihr darin bleiben." });
                        surah43.VerseList.Add(new Verse { Number = 72, TextBosnian = "Eto, to je Džennet koji ste u nasljedstvo dobili za ono što ste radili;", TextEnglish = "And that is Paradise which you are made to inherit for what you used to do.", TextGerman = "Das ist der (Paradies)garten, der euch zum Erbe gegeben worden ist für das, was ihr zu tun pflegtet." });
                        surah43.VerseList.Add(new Verse { Number = 73, TextBosnian = "u njemu ćete raznovrsnog voća imati koje ćete jesti.", TextEnglish = "For you therein is much fruit[1448] from which you will eat.", TextGerman = "Darin habt ihr vielerlei Früchte, von denen ihr eßt." });
                        surah43.VerseList.Add(new Verse { Number = 74, TextBosnian = "Doista će prestupnici u patnji džehennemskoj vječno ostati,", TextEnglish = "Indeed, the criminals will be in the punishment of Hell, abiding eternally.", TextGerman = "Gewiß, die Übeltäter (hingegen) werden in der Strafe der Hölle ewig bleiben." });
                        surah43.VerseList.Add(new Verse { Number = 75, TextBosnian = "ona im neće ublažena biti i nikakve nade u spas neće imati. Oni će u njoj očajni biti.", TextEnglish = "It will not be allowed to subside for them, and they, therein, are in despair.", TextGerman = "Sie wird ihnen nicht herabgesetzt, und sie werden darin ganz verzweifelt sein." });
                        surah43.VerseList.Add(new Verse { Number = 76, TextBosnian = "Nismo im Mi zulum učinili, nego su oni baš zulumćari bili.", TextEnglish = "And We did not wrong them, but it was they who were the wrongdoers.", TextGerman = "Nicht Wir haben ihnen Unrecht getan, sondern sie sind es, die Unrecht getan haben." });
                        surah43.VerseList.Add(new Verse { Number = 77, TextBosnian = "Oni će dozivati: 'O Malik! Neka nas Gospodar tvoj dokrajči!', a on će reći: 'Doista vi tu ostajete!'", TextEnglish = "And they will call, 'O Mālik,[1449] let your Lord put an end to us!' He will say, 'Indeed, you will remain.'", TextGerman = "Und sie rufen: O Malik, dein Herr soll unserem Leben ein Ende setzen. Er sagt: Gewiß, ihr werdet (hier) bleiben." });
                        surah43.VerseList.Add(new Verse { Number = 78, TextBosnian = "Mi smo vam slali Istinu, ali većina vas istinu prezire.", TextEnglish = "We had certainly brought you the truth, but most of you, to the truth, were averse.", TextGerman = "Wir sind zu euch mit der Wahrheit gekommen, aber den meisten von euch ist die Wahrheit zuwider." });
                        surah43.VerseList.Add(new Verse { Number = 79, TextBosnian = "Ako oni pletu zamke, i Mi ćemo zamke njima postaviti.", TextEnglish = "Or have they devised [some] affair?[1450] But indeed, We are devising [a plan].", TextGerman = "Oder haben sie sich eine Sache ausgedacht? Auch Wir können Uns (etwas) ausdenken." });
                        surah43.VerseList.Add(new Verse { Number = 80, TextBosnian = "Zar oni misle da Mi ne čujemo šta oni nasamo razgovaraju i kako se među sobom dogovaraju? Čujemo Mi, a izaslanici Naši, koji su uz njih, zapisuju.", TextEnglish = "Or do they think that We hear not their secrets and their private conversations? Yes, [We do], and Our messengers [i.e., angels] are with them recording.", TextGerman = "Oder meinen sie, daß Wir ihr Geheimes und ihre vertraulichen Gespräche nicht hören? Ja doch, Unsere Boten sind bei ihnen und schreiben (alles) auf." });
                        surah43.VerseList.Add(new Verse { Number = 81, TextBosnian = "Reci: 'Kad bi Svemilosni imao dijete, ja bih Mu i tada prvi ibadet činio!'", TextEnglish = "Say, [O Muḥammad], 'If the Most Merciful had a son, then I would be the first of [his] worshippers.'[1451]", TextGerman = "Sag: Wenn der Allerbarmer Kinder hätte, wäre ich der erste derjenigen, die (Ihm) dienen." });
                        surah43.VerseList.Add(new Verse { Number = 82, TextBosnian = "Neka je Slavljen i Uzvišen Gospodar nebesa i Zemlje, i Gospodar Arša, od onoga kako Ga oni opisuju!", TextEnglish = "Exalted is the Lord of the heavens and the earth, Lord of the Throne, above what they describe.", TextGerman = "Preis sei dem Herrn der Himmel und der Erde, dem Herrn des Thrones! (Erhaben ist Er) über das, was sie (Ihm) zuschreiben." });
                        surah43.VerseList.Add(new Verse { Number = 83, TextBosnian = "Pa, ti ih ostavi neka se upuštaju u zabludu i zabavljaju dok ne dožive Dan kojim im se prijeti.", TextEnglish = "So leave them to converse vainly and amuse themselves until they meet their Day which they are promised.", TextGerman = "So lasse sie nur schweifende Gespräche führen und ihr Spiel treiben, bis sie ihrem Tag begegnen, der ihnen angedroht ist," });
                        surah43.VerseList.Add(new Verse { Number = 84, TextBosnian = "On je Onaj Koji je i na nebu Bog, a i na Zemlji Bog. On je Mudri i Sveznajući!", TextEnglish = "And it is He [i.e., Allāh] who is [the only] deity in the heaven, and on the earth [the only] deity. And He is the Wise, the Knowing.", TextGerman = "Er ist es, Der im Himmel Gott und auf der Erde Gott ist; Er ist der Allweise und Allwissende." });
                        surah43.VerseList.Add(new Verse { Number = 85, TextBosnian = "I neka je uzvišen Onaj Čija je vlast na nebesima i na Zemlji, i između njih, On jedini zna kada će Smak svijeta biti, i Njemu ćete se svi vratiti!", TextEnglish = "And blessed is He to whom belongs the dominion of the heavens and the earth and whatever is between them and with whom is knowledge of the Hour and to whom you will be returned.", TextGerman = "Und Segensreich ist Derjenige, Dem die Herrschaft der Himmel und der Erde und dessen, was dazwischen ist, gehört, Der das Wissen über die Stunde besitzt und zu Dem ihr zurückgebracht werdet!" });
                        surah43.VerseList.Add(new Verse { Number = 86, TextBosnian = "Oni koje oni, pored Njega, mole - neće se moći za druge zauzimati; moći će samo oni koji Istinu priznaju, oni koji znaju.", TextEnglish = "And those they invoke[1452] besides Him do not possess [power of] intercession; but only those who testify to the truth [can benefit], and they know.[1453]", TextGerman = "Und diejenigen, die sie anstatt Seiner anrufen, verfügen nicht über die Fürsprache, außer wer der Wahrheit entsprechend bezeugt, und sie wissen (es)." });
                        surah43.VerseList.Add(new Verse { Number = 87, TextBosnian = "A ako ih zapitaš ko ih je stvorio, sigurno će reći: 'Allah!' Pa kuda se onda odmeću?!", TextEnglish = "And if you asked them[1454] who created them, they would surely say, 'Allāh.' So how are they deluded?", TextGerman = "Wenn du sie fragst, wer sie erschaffen hat, sagen sie ganz gewiß: Allah. Wie lassen sie sich also abwendig machen?" });
                        surah43.VerseList.Add(new Verse { Number = 88, TextBosnian = "A Poslanik reče: 'Gospodaru moj, ovo su, zaista, ljudi koji ne vjeruju!'", TextEnglish = "And [Allāh acknowledges] his saying,[1455] 'O my Lord, indeed these are a people who do not believe.'", TextGerman = "Und bei seinem Ausspruch: O mein Herr, gewiß, das sind Leute, die nicht glauben." });
                        surah43.VerseList.Add(new Verse { Number = 89, TextBosnian = "Pa ti se okreni od njih i reci: 'Selam!' Sigurno će oni znati!", TextEnglish = "So turn aside from them and say, 'Peace.'[1456] But they are going to know.", TextGerman = "Übe Nachsicht mit ihnen und sag: Friede! Sie werden (es noch) erfahren." });
                        result = surah43;
                        break;
                    #endregion

                    // 
                    #region
                    case 44:
                        Surah surah44 = new Surah(); surah44.Name = Constants.Surah44Name; surah44.Number = 44; surah44.VerseList = new List<Verse>();
                        surah44.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah44.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ha-mim.", TextEnglish = "Ḥā, Meem.[1458]", TextGerman = "Ha-Mim." });
                        surah44.VerseList.Add(new Verse { Number = 2, TextBosnian = "Tako mi Knjige jasne,", TextEnglish = "By the clear Book,", TextGerman = "Bei dem deutlichen Buch!" });
                        surah44.VerseList.Add(new Verse { Number = 3, TextBosnian = "Mi smo je objavili u Blagoslovljenoj noći, i Mi, doista, upozoravamo;", TextEnglish = "Indeed, We sent it down during a blessed night.[1459] Indeed, We were to warn [mankind].", TextGerman = "Wir haben es wahrlich in einer gesegneten Nacht herabgesandt - Wir haben ja (die Menschen) immer wieder gewarnt -," });
                        surah44.VerseList.Add(new Verse { Number = 4, TextBosnian = "u njoj se raščlanjuje svaka odredba savršena!", TextEnglish = "Therein [i.e., on that night] is made distinct[1460] every precise matter -", TextGerman = "in der jede weise Angelegenheit einzeln entschieden wird" });
                        surah44.VerseList.Add(new Verse { Number = 5, TextBosnian = "po zapovijedi Našoj! Mi smo, zaista, slali poslanike", TextEnglish = "[Every] matter [proceeding] from Us. Indeed, We were to send [a messenger]", TextGerman = "als eine Angelegenheit von Uns aus - Wir haben ja immer wieder (Warner) gesandt -," });
                        surah44.VerseList.Add(new Verse { Number = 6, TextBosnian = "kao milost Gospodara tvoga - a On je, uistinu, Svečujući i Sveznajući -", TextEnglish = "As mercy from your Lord. Indeed, He is the Hearing, the Knowing,", TextGerman = "als eine Barmherzigkeit von deinem Herrn - gewiß, Er ist der Allhörende und Allwissende -," });
                        surah44.VerseList.Add(new Verse { Number = 7, TextBosnian = "Gospodara nebesa i Zemlje, i onoga što je između njih - ako čvrsto vjerujete,", TextEnglish = "Lord of the heavens and the earth and that between them, if you would be certain.", TextGerman = "dem Herrn der Himmel und der Erde und dessen, was dazwischen ist, wenn ihr überzeugt seid." });
                        surah44.VerseList.Add(new Verse { Number = 8, TextBosnian = "drugog boga osim Njega nema; On život i smrt daje - Gospodar vaš i Gospodar vaših predaka davnih!", TextEnglish = "There is no deity except Him; He gives life and causes death. [He is] your Lord and the Lord of your first forefathers.", TextGerman = "Es gibt keinen Gott außer Ihm. Er macht lebendig und läßt sterben, (Er), euer Herr und der Herr eurer Vorväter." });
                        surah44.VerseList.Add(new Verse { Number = 9, TextBosnian = "Ali, oni sumnjaju i zabavljaju se.", TextEnglish = "But they are in doubt, amusing themselves.", TextGerman = "Aber nein! Sie sind im Zweifel und treiben ihr Spiel." });
                        surah44.VerseList.Add(new Verse { Number = 10, TextBosnian = "Pa ti zato sačekaj Dan kad će se na nebu vidljivi dim pojaviti", TextEnglish = "Then watch for the Day when the sky will bring a visible smoke", TextGerman = "So erwarte den Tag, an dem der Himmel deutlichen Rauch hervorbringt," });
                        surah44.VerseList.Add(new Verse { Number = 11, TextBosnian = "koji će ljude prekriti: 'Ovo je bolna patnja!", TextEnglish = "Covering the people; this is a painful torment.", TextGerman = "der die Menschen überdecken wird. Das ist eine schmerzhafte Strafe." });
                        surah44.VerseList.Add(new Verse { Number = 12, TextBosnian = "Gospodaru naš, otkloni patnju od nas, mi ćemo, sigurno, vjerovati!'", TextEnglish = "[They will say], 'Our Lord, remove from us the torment; indeed, we are believers.'", TextGerman = "Unser Herr, nimm die Strafe von uns hinweg; wir sind (jetzt) ja gläubig." });
                        surah44.VerseList.Add(new Verse { Number = 13, TextBosnian = "A kako da tada pouku prime, a već im je bio došao Poslanik jasni", TextEnglish = "How will there be for them a reminder [at that time]? And there had come to them a clear Messenger.", TextGerman = "Wie soll ihnen (jetzt) die Selbstbesinnung nützen, wo bereits ein deutlicher Gesandter zu ihnen kam," });
                        surah44.VerseList.Add(new Verse { Number = 14, TextBosnian = "od koga se oni potom okrenuše i rekoše: 'Poučeni - umno poremećeni!'", TextEnglish = "Then they turned away from him and said, '[He was] taught [and is] a madman.'", TextGerman = "sie sich hierauf aber von ihm abkehrten und sagten: Einer, dem vorgesagt wird, ein Besessener?" });
                        surah44.VerseList.Add(new Verse { Number = 15, TextBosnian = "A kad bismo malo patnju otklonili, vi biste se sigurno u nevjerovanje vratili;", TextEnglish = "Indeed, We will remove the torment for a little. Indeed, you [disbelievers] will return [to disbelief].", TextGerman = "Wir werden die Strafe ein wenig hinwegnehmen. Ihr werdet sicher zurückkehren." });
                        surah44.VerseList.Add(new Verse { Number = 16, TextBosnian = "ali na Dan kada ih najžešće zgrabimo, zbilja ćemo ih kazniti.", TextEnglish = "The Day We will strike with the greatest assault, indeed, We will take retribution.", TextGerman = "Am Tag, da Wir mit der größten Gewalt zupacken werden, gewiß, da werden Wir Vergeltung üben." });
                        surah44.VerseList.Add(new Verse { Number = 17, TextBosnian = "A Mi smo i prije njih faraonov narod u iskušenje stavili, i došao im je plemeniti poslanik:", TextEnglish = "And We had already tried before them the people of Pharaoh, and there came to them a noble messenger [i.e., Moses],", TextGerman = "Und Wir setzten bereits vor ihnen das Volk Fir’auns der Versuchung aus. Zu ihnen kam ein edler Gesandter:" });
                        surah44.VerseList.Add(new Verse { Number = 18, TextBosnian = "Predajte meni Allahove robove! Ja sam vam, zaista, poslanik pouzdani,", TextEnglish = "[Saying], 'Render to me the servants of Allāh.[1461] Indeed, I am to you a trustworthy messenger,'", TextGerman = "Übergebt mir Allahs Diener. Gewiß, ich bin für euch ein vertrauenswürdiger Gesandter." });
                        surah44.VerseList.Add(new Verse { Number = 19, TextBosnian = "i ne uzdižite se iznad Allaha, ja vam donosim dokaz očevidni,", TextEnglish = "And [saying], 'Be not haughty with Allāh. Indeed, I have come to you with clear evidence.", TextGerman = "Und seid Allah gegenüber nicht überheblich. Gewiß, ich bringe euch eine deutliche Ermächtigung." });
                        surah44.VerseList.Add(new Verse { Number = 20, TextBosnian = "i ja se obraćam i svome i vašem Gospodaru, da me ne gađate,", TextEnglish = "And indeed, I have sought refuge in my Lord and your Lord, lest you stone me.[1462]", TextGerman = "Und gewiß, ich nehme Zuflucht bei meinem Herrn und eurem Herrn davor, daß ihr mich steinigt." });
                        surah44.VerseList.Add(new Verse { Number = 21, TextBosnian = "a ako mi ne vjerujete, onda me na miru ostavite!'", TextEnglish = "But if you do not believe me, then leave me alone.'", TextGerman = "Und wenn ihr mir nicht glaubt, dann haltet euch (wenigstens) fern von mir." });
                        surah44.VerseList.Add(new Verse { Number = 22, TextBosnian = "I on pozva u pomoć Gospodara svoga: 'Ovo je, doista, narod prestupnički!'", TextEnglish = "And [finally] he called to his Lord that these were a criminal people.", TextGerman = "Da rief er zu seinem Herrn: Diese sind ein Volk von Übeltätern." });
                        surah44.VerseList.Add(new Verse { Number = 23, TextBosnian = "Izvedi noću robove Moje, vi ćete slijeđeni biti,", TextEnglish = "[Allāh said], 'Then set out with My servants by night. Indeed, you are to be pursued.", TextGerman = "- Zieh bei Nacht mit Meinen Dienern fort; ihr werdet ja verfolgt werden." });
                        surah44.VerseList.Add(new Verse { Number = 24, TextBosnian = "i ostavi more nek miruje, oni su vojska koja će zaista potopljena biti.'", TextEnglish = "And leave the sea in stillness.[1463] Indeed, they are an army to be drowned.'", TextGerman = "Und laß das Meer in Ruhe zurück’. Gewiß, sie sind eine Heerschar, die ertränkt werden soll." });
                        surah44.VerseList.Add(new Verse { Number = 25, TextBosnian = "I koliko ostaviše bašča i izvora,", TextEnglish = "How much they left behind of gardens and springs", TextGerman = "Wie viele Gärten und Quellen ließen sie zurück" });
                        surah44.VerseList.Add(new Verse { Number = 26, TextBosnian = "i njiva zasijanih i boravišta divnih,", TextEnglish = "And crops and noble sites", TextGerman = "und Getreidefelder und treffliche Stätte" });
                        surah44.VerseList.Add(new Verse { Number = 27, TextBosnian = "i blagodati u kojima su uživali!", TextEnglish = "And comfort wherein they were amused.", TextGerman = "und ein angenehmes Leben, in dem sie vergnügt waren!" });
                        surah44.VerseList.Add(new Verse { Number = 28, TextBosnian = "Tako to bi, i Mi smo to u nasljedstvo drugima ostavili,", TextEnglish = "Thus. And We caused to inherit it another people.", TextGerman = "So (war es). Und Wir gaben es anderen Leuten zum Erbe." });
                        surah44.VerseList.Add(new Verse { Number = 29, TextBosnian = "ni nebo ih ni Zemlja nisu oplakivali, i nisu bili sačekani.", TextEnglish = "And the heaven and earth wept not for them, nor were they reprieved.", TextGerman = "Weder Himmel noch Erde weinte über sie, und ihnen wurde kein Aufschub gewährt." });
                        surah44.VerseList.Add(new Verse { Number = 30, TextBosnian = "A sinove smo Israilove ponižavajuće patnje spasili,", TextEnglish = "And We certainly saved the Children of Israel from the humiliating torment -", TextGerman = "Und Wir erretteten bereits die Kinder lsra’ils von der schmachvollen Qual," });
                        surah44.VerseList.Add(new Verse { Number = 31, TextBosnian = "od faraona - on je bio zbilja nadmen, jedan od onih koji su u zlu svaku mjeru prevršili,", TextEnglish = "From Pharaoh. Indeed, he was a haughty one among the transgressors.", TextGerman = "von Fir’aun. Er war überheblich und einer der Maßlosen." });
                        surah44.VerseList.Add(new Verse { Number = 32, TextBosnian = "i znajući kakvi su, nad svjetovima smo ih odabrali,", TextEnglish = "And We certainly chose them by knowledge over [all] the worlds.", TextGerman = "Und Wir erwählten sie ja mit Wissen vor den (anderen) Weltenbewohnern" });
                        surah44.VerseList.Add(new Verse { Number = 33, TextBosnian = "i neka smo im znamenja puna očite kušnje dali.", TextEnglish = "And We gave them of signs that in which there was a clear trial.", TextGerman = "und ließen ihnen von den Zeichen solche zukommen, die eine deutliche Prüfung enthielten." });
                        surah44.VerseList.Add(new Verse { Number = 34, TextBosnian = "A oni, doista, govore:", TextEnglish = "Indeed, these [disbelievers] are saying,", TextGerman = "Diese sagen fürwahr:" });
                        surah44.VerseList.Add(new Verse { Number = 35, TextBosnian = "Postoji samo naša prva smrt, mi nećemo biti oživljeni;", TextEnglish = "There is not but our first death, and we will not be resurrected.", TextGerman = "Es gibt nur unseren ersten Tod. Und wir werden nicht auferstehen." });
                        surah44.VerseList.Add(new Verse { Number = 36, TextBosnian = "pretke naše nam dovedite, ako je istina to što govorite!'", TextEnglish = "Then bring [back] our forefathers, if you should be truthful.'", TextGerman = "Bringt doch unsere Väter zurück, wenn ihr wahrhaftig seid." });
                        surah44.VerseList.Add(new Verse { Number = 37, TextBosnian = "Da li su bolji oni ili narod Tubbe'ov i oni prije njega? Njih smo uništili jer su prestupnici bili.", TextEnglish = "Are they better or the people of Tubbaʿ[1464] and those before them? We destroyed them, [for] indeed, they were criminals.", TextGerman = "Sind sie etwa besser oder das Volk Tubba’s und diejenigen vor ihnen? Wir vernichteten sie; sie waren ja Übeltäter." });
                        surah44.VerseList.Add(new Verse { Number = 38, TextBosnian = "Mi nismo stvorili nebesa i Zemlju i ono što je između njih igrajući se.", TextEnglish = "And We did not create the heavens and earth and that between them in play.", TextGerman = "Und Wir haben die Himmel und die Erde und was dazwischen ist, nicht zum Spiel erschaffen." });
                        surah44.VerseList.Add(new Verse { Number = 39, TextBosnian = "Mi smo ih stvorili s Istinom, ali većina njih ne zna.", TextEnglish = "We did not create them except in truth, but most of them do not know.", TextGerman = "Wir haben sie nur in Wahrheit erschaffen. Aber die meisten von ihnen wissen nicht." });
                        surah44.VerseList.Add(new Verse { Number = 40, TextBosnian = "Zbilja će Dan suda svima njima rok određeni biti,", TextEnglish = "Indeed, the Day of Judgement is the appointed time for them all -", TextGerman = "Gewiß, der Tag der Entscheidung ist ihrer aller festgesetzte Zeit," });
                        surah44.VerseList.Add(new Verse { Number = 41, TextBosnian = "Dan, kada bližnji neće nimalo od koristi bližnjem biti, niti će biti pomognuti,", TextEnglish = "The Day when no relation[1465] will avail a relation at all, nor will they be helped -", TextGerman = "der Tag, an dem kein Schutzherr seinem Schützling etwas nützen kann und ihnen keine Hilfe zuteil wird," });
                        surah44.VerseList.Add(new Verse { Number = 42, TextBosnian = "I osim onih kojima se Allah smiluje. On je, uistinu, Silni i Milostivi.", TextEnglish = "Except those [believers] on whom Allāh has mercy. Indeed, He is the Exalted in Might, the Merciful.", TextGerman = "außer demjenigen, dessen Allah sich erbarmt. Er ist ja der Allmächtige und Barmherzige." });
                        surah44.VerseList.Add(new Verse { Number = 43, TextBosnian = "A drvo zekkum zbilja je", TextEnglish = "Indeed, the tree of zaqqūm", TextGerman = "Gewiß, der Zaqqum-Baum" });
                        surah44.VerseList.Add(new Verse { Number = 44, TextBosnian = "hrana grešnika,", TextEnglish = "Is food for the sinful.", TextGerman = "ist die Speise des Sünders;" });
                        surah44.VerseList.Add(new Verse { Number = 45, TextBosnian = "kao uljani talog u trbuhu će vreti,", TextEnglish = "Like murky oil, it boils within bellies", TextGerman = "wie siedendes Öl kocht er in den Bäuchen," });
                        surah44.VerseList.Add(new Verse { Number = 46, TextBosnian = "kao što voda ključala vrije.", TextEnglish = "Like the boiling of scalding water.", TextGerman = "wie das heiße Wasser kocht." });
                        surah44.VerseList.Add(new Verse { Number = 47, TextBosnian = "Ščepajte ga i usred Ognja odvucite,", TextEnglish = "[It will be commanded], 'Seize him and drag him into the midst of the Hellfire,", TextGerman = "Nehmt ihn und schleppt ihn mitten in den Höllenbrand hinein." });
                        surah44.VerseList.Add(new Verse { Number = 48, TextBosnian = "a zatim mu, za kaznu, na glavu vodu koja ključa izlijevajte!", TextEnglish = "Then pour over his head from the torment of scalding water.'", TextGerman = "Hierauf gießt über seinen Kopf von der Strafe des heißen Wassers." });
                        surah44.VerseList.Add(new Verse { Number = 49, TextBosnian = "Kušaj, ta ti si, baš, 'moćan' i 'poštovan'?", TextEnglish = "[It will be said], 'Taste! Indeed, you are the honored, the noble![1466]", TextGerman = "Koste (doch); du bist ja der Mächtige und Edle!" });
                        surah44.VerseList.Add(new Verse { Number = 50, TextBosnian = "Ovo je, zaista, ono u što ste sumnjali!'", TextEnglish = "Indeed, this is what you used to dispute.'", TextGerman = "Das ist (es) ja, worüber ihr zu zweifeln pflegtet." });
                        surah44.VerseList.Add(new Verse { Number = 51, TextBosnian = "A bogobojazni će, zaista, na sigurnom mjestu biti,", TextEnglish = "Indeed, the righteous will be in a secure place:", TextGerman = "Gewiß, die Gottesfürchtigen befinden sich an sicherer Stätte," });
                        surah44.VerseList.Add(new Verse { Number = 52, TextBosnian = "usred bašča i izvora,", TextEnglish = "Within gardens and springs,", TextGerman = "in Gärten und an Quellen." });
                        surah44.VerseList.Add(new Verse { Number = 53, TextBosnian = "u dibu i kadifu obučeni i jedni prema drugima okrenuti.", TextEnglish = "Wearing [garments of] fine silk and brocade, facing each other.", TextGerman = "Sie tragen (Gewänder) aus Seidenbrokat und schwerem Brokat (,) und (sie liegen) einander gegenüber." });
                        surah44.VerseList.Add(new Verse { Number = 54, TextBosnian = "Eto, tako će biti i Mi ćemo ih hurijama, krupnih očiju, ženiti.", TextEnglish = "Thus. And We will marry them to fair women with large, [beautiful] eyes.", TextGerman = "So ist es. Und Wir geben ihnen als Gattinnen Huris mit schönen, großen Augen." });
                        surah44.VerseList.Add(new Verse { Number = 55, TextBosnian = "Tamo će moći, sigurni, koju hoće vrstu voća tražiti;", TextEnglish = "They will call therein for every [kind of] fruit - safe and secure.", TextGerman = "Sie rufen darin nach Früchten aller Art (und sind dort) in Sicherheit." });
                        surah44.VerseList.Add(new Verse { Number = 56, TextBosnian = "tu, poslije one prve smrti, smrt više neće okusiti i On će ih patnje u Ognju sačuvati,", TextEnglish = "They will not taste death therein except the first death, and He will have protected them from the punishment of Hellfire", TextGerman = "Sie kosten darin nicht den Tod, außer dem ersten Tod. Und Er bewahrt sie vor der Strafe des Höllenbrandes" });
                        surah44.VerseList.Add(new Verse { Number = 57, TextBosnian = "blagodat će to od Gospodara tvoga biti; to će, zaista, biti uspjeh veliki!", TextEnglish = "As bounty from your Lord. That is what is the great attainment.", TextGerman = "als Huld von deinem Herrn. Das ist der großartige Erfolg." });
                        surah44.VerseList.Add(new Verse { Number = 58, TextBosnian = "A njega-Kur'an lahkim smo učinili, na jeziku tvome, da bi se oni prisjetili.", TextEnglish = "And indeed, We have eased it [i.e., the Qur’ān] in your tongue that they might be reminded.", TextGerman = "Wir haben ihn in deiner Sprache wahrlich leicht gemacht, auf daß sie bedenken mögen." });
                        surah44.VerseList.Add(new Verse { Number = 59, TextBosnian = "Ti zato čekaj, a i oni čekaju!", TextEnglish = "So watch, [O Muḥammad]; indeed, they are watching [for your end].", TextGerman = "Warte nun ab; auch sie warten ab." });
                        result = surah44;
                        break;
                    #endregion

                    // 
                    #region
                    case 45:
                        Surah surah45 = new Surah(); surah45.Name = Constants.Surah45Name; surah45.Number = 45; surah45.VerseList = new List<Verse>();
                        surah45.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah45.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ha-mim,", TextEnglish = "Ḥā, Meem.[1468]", TextGerman = "Ha-Mim." });
                        surah45.VerseList.Add(new Verse { Number = 2, TextBosnian = "Knjigu objavljuje Allah, Silni i Mudri!", TextEnglish = "The revelation of the Book is from Allāh, the Exalted in Might, the Wise.", TextGerman = "(Dies ist) die Offenbarung des Buches von Allah, dem Allmächtigen und Allweisen." });
                        surah45.VerseList.Add(new Verse { Number = 3, TextBosnian = "Na nebesima i na Zemlji, zaista, postoje znakovi za vjernike.", TextEnglish = "Indeed, within the heavens and earth are signs for the believers.", TextGerman = "In den Himmeln und auf der Erde sind wahrlich Zeichen für die Gläubigen." });
                        surah45.VerseList.Add(new Verse { Number = 4, TextBosnian = "U stvaranju vas i živih bića koja je razasuo, znakovi su za ljude koji su čvrsto uvjereni;", TextEnglish = "And in the creation of yourselves and what He disperses of moving creatures are signs for people who are certain [in faith].", TextGerman = "Und in eurer Erschaffung und in dem, was Er an Tieren sich ausbreiten läßt, sind Zeichen für Leute, die überzeugt sind." });
                        surah45.VerseList.Add(new Verse { Number = 5, TextBosnian = "i u smjeni noći i dana, i opskrbi - kiši koju Allah s neba spušta da pomoću nje zemlju, nakon mrtvila njezina, oživi i usmjeravanju vjetrova - znakovi su za ljude koji razumiju.", TextEnglish = "And [in] the alternation of night and day and [in] what Allāh sends down from the sky of provision [i.e., rain] and gives life thereby to the earth after its lifelessness and [in His] directing of the winds are signs for a people who reason.", TextGerman = "Und (auch in) dem Unterschied von Nacht und Tag und (in) dem, was Allah an Versorgung vom Himmel herabkommen läßt und dann damit die Erde nach ihrem Tod wieder lebendig macht, und im Wechsel der Winde sind Zeichen für Leute, die begreifen." });
                        surah45.VerseList.Add(new Verse { Number = 6, TextBosnian = "To su Allahovi ajeti i znakovi koje ti kao istinu navodimo, pa u koje će, ako ne u Allahove riječi i znakove Njegove, oni vjerovati?", TextEnglish = "These are the verses of Allāh which We recite to you in truth. Then in what statement after Allāh and His verses will they believe?", TextGerman = "Dies sind Allahs Zeichen, die Wir dir der Wahrheit entsprechend verlesen. An welche Aussage nach (derjenigen) Allah(s) und Seinen Zeichen wollen sie denn (sonst) glauben?" });
                        surah45.VerseList.Add(new Verse { Number = 7, TextBosnian = "Teško svakom lašcu, velikom grešniku,", TextEnglish = "Woe to every sinful liar", TextGerman = "Wehe jedem durch und durch sündigen Lügner," });
                        surah45.VerseList.Add(new Verse { Number = 8, TextBosnian = "I koji čuje Allahove ajete kad mu se uče, zatim ustraje u nevjerovanju, oholeći se kao da ih ni čuo nije - njemu patnju bolnu navijesti.", TextEnglish = "Who hears the verses of Allāh recited to him, then persists arrogantly as if he had not heard them. So give him tidings of a painful punishment.", TextGerman = "der hört, wie ihm Allahs Zeichen verlesen werden, und hierauf hochmütig (in seinem Unglauben) verharrt, als ob er sie nicht gehört hätte! So verkünde ihm schmerzhafte Strafe." });
                        surah45.VerseList.Add(new Verse { Number = 9, TextBosnian = "A kad sazna za neke Naše ajete, on im se ruga. Takve ponižavajuća patnja čeka.", TextEnglish = "And when he knows anything of Our verses, he takes them in ridicule. Those will have a humiliating punishment.", TextGerman = "Und wenn er etwas von Unseren Zeichen kennenlernt, macht er sich darüber lustig. Für solche wird es schmachvolle Strafe geben." });
                        surah45.VerseList.Add(new Verse { Number = 10, TextBosnian = "Pred njima je Džehennem, i ni od kakve koristi im neće biti ono što su stekli, ni oni koje su, pored Allaha, za zaštitnike uzeli, i njih čeka patnja golema.", TextEnglish = "Before them[1469] is Hell, and what they had earned will not avail them at all nor what they had taken besides Allāh as allies. And they will have a great punishment.", TextGerman = "Hinter ihnen (wartet) die Hölle. Und es nützt ihnen nicht, was sie erworben und (auch) nicht, was sie sich anstatt Allahs zu Schutzherren genommen haben; für sie wird es gewaltige Strafe geben." });
                        surah45.VerseList.Add(new Verse { Number = 11, TextBosnian = "Ovo je Uputa, a one koji ne vjeruju u ajete i znakove Gospodara svoga čeka bolna, najveća kazna.", TextEnglish = "This [Qur’ān] is guidance. And those who have disbelieved in the verses of their Lord will have a painful punishment of foul nature.", TextGerman = "Dies ist eine Rechtleitung. Und diejenigen, die die Zeichen ihres Herrn verleugnen, für sie wird es schmerzhafte Strafe von Züchtigung geben." });
                        surah45.VerseList.Add(new Verse { Number = 12, TextBosnian = "Allah je Onaj Koji vam je potčinio more da bi lađe, odredbom Njegovom, po njemu plovile, da biste mogli tražiti blagodati Njegove i da biste bili zahvalni.", TextEnglish = "It is Allāh who subjected to you the sea so that ships may sail upon it by His command and that you may seek of His bounty; and perhaps you will be grateful.", TextGerman = "Allah ist es, Der euch das Meer dienstbar gemacht hat, damit die Schiffe darauf auf Seinen Befehl fahren und damit ihr nach etwas von Seiner Huld trachtet, und auf daß ihr dankbar sein möget." });
                        surah45.VerseList.Add(new Verse { Number = 13, TextBosnian = "I potčinio vam je ono što je na nebesima i na Zemlji, sve je od Njega! To su, zaista, znaci za ljude koji razmišljaju.", TextEnglish = "And He has subjected to you whatever is in the heavens and whatever is on the earth - all from Him. Indeed in that are signs for a people who give thought.", TextGerman = "Und Er hat euch alles, was in den Himmeln und was auf der Erde ist, dienstbar gemacht, alles von Sich aus. Darin sind wahrlich Zeichen für Leute, die nachdenken." });
                        surah45.VerseList.Add(new Verse { Number = 14, TextBosnian = "Reci onima koji vjeruju da oproste onima koji se ne nadaju Allahovim blagodatima da bi On 'nagradio' te ljude za ono što su radili.", TextEnglish = "Say, [O Muḥammad], to those who have believed that they [should] forgive those who expect not the days of Allāh [i.e., of His retribution] so that He may recompense a people[1470] for what they used to earn.", TextGerman = "Sag zu denjenigen, die glauben, sie sollen denjenigen vergeben, die nicht Allahs Tage erhoffen, auf daß Er einem (jeden) Volk das vergelte, was sie erworben haben." });
                        surah45.VerseList.Add(new Verse { Number = 15, TextBosnian = "Onaj koji čini dobro - sebi ga čini, a onaj koji čini zlo - protiv sebe radi; a na kraju ćete se Gospodaru svome vratiti.", TextEnglish = "Whoever does a good deed - it is for himself; and whoever does evil - it is against it [i.e., the self or soul]. Then to your Lord you will be returned.", TextGerman = "Wer rechtschaffen handelt, der (tut es) zu seinem eigenen Vorteil, und wer Böses tut, der (tut es) zu seinem eigenen Nachteil. Hierauf werdet ihr zu eurem Herrn zurückgebracht." });
                        surah45.VerseList.Add(new Verse { Number = 16, TextBosnian = "Sinovima smo Israilovim Knjigu i vlast, i vjerovjesništvo dali, i iz ljepota ih opskrbili, i nad ostalim svjetovima ih odlikovali!", TextEnglish = "And We did certainly give the Children of Israel the Scripture and judgement[1471] and prophethood, and We provided them with good things and preferred them over the worlds.", TextGerman = "Wir gaben ja den Kindern Isra’ils die Schrift, das Urteil und das Prophetentum und versorgten sie von den guten Dingen und bevorzugten sie vor den (anderen) Weltenbewohnern." });
                        surah45.VerseList.Add(new Verse { Number = 17, TextBosnian = "I jasne smo im dokaze o vjeri dali, a oni su se podvojili baš onda kad su do saznanja došli, i to zbog neprijateljstva i međusobne zavisti. Gospodar tvoj će im na Kijametskom danu sigurno presuditi u vezi s onim u čemu su se razilazili.", TextEnglish = "And We gave them clear proofs of the matter [of religion]. And they did not differ except after knowledge had come to them - out of jealous animosity between themselves. Indeed, your Lord will judge between them on the Day of Resurrection concerning that over which they used to differ.", TextGerman = "Und Wir gaben ihnen klare Beweise in der Angelegenheit (der Religion). Sie wurden aber erst uneinig, nachdem das Wissen zu ihnen gekommen war - aus Mißgunst untereinander. Gewiß, dein Herr wird am Tag der Auferstehung zwischen ihnen über das entscheiden, worüber sie uneinig zu sein pflegten." });
                        surah45.VerseList.Add(new Verse { Number = 18, TextBosnian = "A tebi smo poslije odredili da u vjeri ideš pravcem određenim, zato ga slijedi i ne povodi se za hirovima onih koji ne znaju,", TextEnglish = "Then We put you, [O Muḥammad], on an ordained way concerning the matter [of religion]; so follow it and do not follow the inclinations of those who do not know.", TextGerman = "Hierauf haben Wir dich auf eine Richtung in der Angelegenheit (der Religion) festgelegt. So folge ihr und folge nicht den Neigungen derjenigen, die nicht Bescheid wissen." });
                        surah45.VerseList.Add(new Verse { Number = 19, TextBosnian = "oni te, zbilja, od Allaha nimalo odbraniti ne mogu! Zulumćari su jedni drugima zaštitnici, a Allah je zaštitnik bogobojaznih.", TextEnglish = "Indeed, they will never avail you against Allāh at all. And indeed, the wrongdoers are allies of one another; but Allāh is the protector of the righteous.", TextGerman = "Gewiß, sie werden dir vor Allah nichts nützen. Aber gewiß, die Ungerechten sind einer des anderen Schutzherren. Allah aber ist der Schutzherr der Gottesfürchtigen." });
                        surah45.VerseList.Add(new Verse { Number = 20, TextBosnian = "Ovaj Kur' an putokaz je ljudima, i uputa i milost onima koji čvrsto vjeruju.", TextEnglish = "This [Qur’ān] is enlightenment for mankind and guidance and mercy for a people who are certain [in faith].", TextGerman = "Dies sind einsichtbringende Zeichen für die Menschen und Rechtleitung und Barmherzigkeit für Leute, die überzeugt sind." });
                        surah45.VerseList.Add(new Verse { Number = 21, TextBosnian = "Misle li oni koji čine zla djela da ćemo s njima postupiti jednako kao s onima koji vjeruju i dobra djela čine, da će u ovom životu i nakon smrti biti isti?! Kako loše rasuđuju!", TextEnglish = "Or do those who commit evils think We will make them like those who have believed and done righteous deeds - [make them] equal in their life and their death?[1472] Evil is that which they judge [i.e., assume].", TextGerman = "Oder meinen diejenigen, die böse Taten verüben, daß Wir sie denjenigen, die glauben und rechtschaffene Werke tun, gleichstellen sowohl in ihrem Leben als in ihrem Tod? Wie böse ist, was sie urteilen!" });
                        surah45.VerseList.Add(new Verse { Number = 22, TextBosnian = "A Allah je nebesa i Zemlju s Istinom stvorio i zato da bi svaka osoba bila nagrađena ili kažnjena prema onom šta je zaradila, i njima zulum učinjen neće biti.", TextEnglish = "And Allāh created the heavens and earth in truth and so that every soul may be recompensed for what it has earned, and they will not be wronged.", TextGerman = "Und Allah hat die Himmel und die Erde in Wahrheit erschaffen, und damit jeder Seele das vergolten wird, was sie erworben hat, und es wird ihnen kein Unrecht zugefügt." });
                        surah45.VerseList.Add(new Verse { Number = 23, TextBosnian = "Šta misliš o onome koji je svoju strast za boga svoga uzeo, onome kome je Allah, a on svjestan bio dao da je u zabludi, i sluh njegov, i srce njegovo zapečatio, a pred oči njegove koprenu stavio? Ko će ga, ako neće Allah, na Pravi put uputiti? Zašto se ne prisjetite?", TextEnglish = "Have you seen he who has taken as his god his [own] desire, and Allāh has sent him astray due to knowledge[1473] and has set a seal upon his hearing and his heart and put over his vision a veil? So who will guide him after Allāh? Then will you not be reminded?", TextGerman = "Was meinst du wohl zu jemandem, der sich als seinen Gott seine Neigung genommen hat, den Allah trotz (seines) Wissens hat in die Irre gehen lassen und dem Er das Gehör und das Herz versiegelt und auf dessen Augenlicht eine Hülle gelegt hat? Wer könnte ihn nach Allah rechtleiten? Bedenkt ihr denn nicht?" });
                        surah45.VerseList.Add(new Verse { Number = 24, TextBosnian = "I oni vele: 'Postoji samo život naš zemaljski, živimo i umiremo, jedino nas vrijeme uništi.' A oni o tome baš ništa ne znaju, oni samo nagađaju.", TextEnglish = "And they say, 'There is not but our worldly life; we die and live,[1474] and nothing destroys us except time.' And they have of that no knowledge; they are only assuming.", TextGerman = "Und sie sagen: Es gibt nur unser diesseitiges Leben; wir sterben und leben, und nur die Zeiten vernichten uns. Sie haben (aber) kein Wissen davon, sie stellen nur Mutmaßungen an." });
                        surah45.VerseList.Add(new Verse { Number = 25, TextBosnian = "A kad im se Naši ajeti jasni kazuju, drugog dokaza nemaju osim što kažu: 'Pretke naše u život povratite, ako je istina to što govorite.'", TextEnglish = "And when Our verses are recited to them as clear evidences, their argument is only that they say, 'Bring [back] our forefathers, if you should be truthful.'", TextGerman = "Und wenn ihnen Unsere Zeichen als klare Beweise verlesen werden, ist ihre Beweisgrundlage nur, daß sie sagen: Bringt unsere Väter herbei, wenn ihr wahrhaftig seid." });
                        surah45.VerseList.Add(new Verse { Number = 26, TextBosnian = "Reci: 'Allah vam daje život, zatim će učiniti da poumirete, a poslije će vas na Kijametskom danu sabrati, u to nema nikakve sumnje, ali većina ljudi ne zna.'", TextEnglish = "Say, 'Allāh causes you to live, then causes you to die; then He will assemble you for the Day of Resurrection, about which there is no doubt, but most of the people do not know.'", TextGerman = "Sag: Allah macht euch lebendig und läßt euch hierauf sterben; hierauf wird Er euch zum Tag der Auferstehung versammeln, an dem es keinen Zweifel gibt. Aber die meisten Menschen wissen nicht." });
                        surah45.VerseList.Add(new Verse { Number = 27, TextBosnian = "Allahova je vlast na nebesima i na Zemlji! A na dan kad nastupi Čas oživljenja, taj dan oni koji su laži slijedili bit će izgubljeni.", TextEnglish = "And to Allāh belongs the dominion of the heavens and the earth. And the Day the Hour appears - that Day the falsifiers will lose.", TextGerman = "Allah gehört die Herrschaft der Himmel und der Erde. Und am Tag, da sich die Stunde erhebt, an jenem Tag werden diejenigen verlieren, die Nichtiges tun." });
                        surah45.VerseList.Add(new Verse { Number = 28, TextBosnian = "I vidjet ćeš sve ummete kako kleče; svaki ummet bit će pozvan prema svojoj Knjizi: 'Danas ćete biti nagrađeni ili kažnjeni prema tome kako ste postupali!'", TextEnglish = "And you will see every nation kneeling [from fear]. Every nation will be called to its record [and told], 'Today you will be recompensed for what you used to do.", TextGerman = "Und du wirst jede Gemeinschaft kniend sehen. Jede Gemeinschaft wird zu ihrem Buch gerufen: Heute wird euch das vergolten, was ihr zu tun pflegtet." });
                        surah45.VerseList.Add(new Verse { Number = 29, TextBosnian = "Ova Knjiga Naša o vama će samo istinu reći, jer smo naredili da se zapiše sve što ste radili.", TextEnglish = "This, Our record, speaks about you in truth. Indeed, We were having transcribed[1475] whatever you used to do.'", TextGerman = "Dies ist Unser Buch, das mit der Wahrheit gegen euch spricht. Wir ließen (alles) niederschreiben, was ihr zu tun pflegtet." });
                        surah45.VerseList.Add(new Verse { Number = 30, TextBosnian = "One koji su vjerovali i dobra djela činili Gospodar njihov u milost Svoju će uvesti. To će biti uspjeh očiti!", TextEnglish = "So as for those who believed and did righteous deeds, their Lord will admit them into His mercy. That is what is the clear attainment.", TextGerman = "Was nun diejenigen angeht, die geglaubt und rechtschaffene Werke getan haben, so wird ihr Herr sie in Seine Barmherzigkeit eingehen lassen. Das ist der deutliche Erfolg." });
                        surah45.VerseList.Add(new Verse { Number = 31, TextBosnian = "A onima koji nisu vjerovali: 'Zar vam ajeti Moji nisu kazivani, ali vi ste se oholili i narod prestupnički bili.", TextEnglish = "But as for those who disbelieved, [it will be said], 'Were not Our verses recited to you, but you were arrogant and became a criminal people?", TextGerman = "Was aber diejenigen angeht, die ungläubig waren, so (wird zu ihnen gesagt werden): Sind euch Meine Zeichen nicht stets verlesen worden? Aber ihr habt euch hochmütig verhalten und seid ein Volk von Übeltätern gewesen." });
                        surah45.VerseList.Add(new Verse { Number = 32, TextBosnian = "Kad se govorilo: 'Allahovo je obećanje, zaista, istina! I u Čas oživljenja nema nimalo sumnje', vi ste govorili: 'Mi ne znamo šta je Čas oživljenja, mi samo zamišljamo, mi nismo uvjereni.'", TextEnglish = "And when it was said, 'Indeed, the promise of Allāh is truth and the Hour [is coming] - no doubt about it,' you said, 'We know not what is the Hour. We assume only assumption, and we are not convinced.''", TextGerman = "Und wenn gesagt wurde: .Gewiß, Allahs Versprechen ist wahr, und an der Stunde gibt es keinen Zweifel’, sagtet ihr: ,Wir wissen nicht, was die Stunde ist. Wir stellen ja nur Mutmaßungen an und sind gar nicht überzeugt’." });
                        surah45.VerseList.Add(new Verse { Number = 33, TextBosnian = "I ukazat će im se loša djela njihova, i sa svih strana skolit će ih ono čemu su se rugali,", TextEnglish = "And the evil consequences of what they did will appear to them, and they will be enveloped by what they used to ridicule.", TextGerman = "Und es werden ihnen die bösen Taten, die sie vollbracht haben, klar, und es umschließt sie das, worüber sie sich lustig zu machen pflegten." });
                        surah45.VerseList.Add(new Verse { Number = 34, TextBosnian = "i bit će rečeno: 'Danas ćemo mi vas zaboraviti kao što ste i vi susret na Dan ovaj vaš zaboravili; Vatra će vam prebivalište biti i niko vam pomoći neće moći,", TextEnglish = "And it will be said, 'Today We will forget you as you forgot the meeting of this Day of yours, and your refuge is the Fire, and for you there are no helpers.", TextGerman = "Und es wird gesagt: Heute werden Wir euch vergessen, wie ihr die Begegnung mit diesem eurem Tag vergessen habt. Euer Zufluchtsort ist das (Höllen)feuer, und ihr werdet keine Helfer haben." });
                        surah45.VerseList.Add(new Verse { Number = 35, TextBosnian = "zato što ste se Allahovim ajetima i znakovima rugali i što vas je život na Zemlji bio obmanuo.' Zato danas iz Vatre neće biti izvedeni, niti će im biti udovoljeno da blagonaklonost za povratak traže.", TextEnglish = "That is because you took the verses of Allāh in ridicule, and worldly life deluded you.' So that Day they will not be removed from it, nor will they be asked to appease [Allāh].", TextGerman = "Dies ist dafür, daß ihr euch über Allahs Zeichen lustig machtet und daß euch das diesseitige Leben täuschte. Heute werden sie nicht (mehr) aus ihm herausgebracht, und es wird ihnen keine Gnade (mehr) erwiesen." });
                        surah45.VerseList.Add(new Verse { Number = 36, TextBosnian = "Pa, neka je sva hvala Allahu, Gospodaru nebesa i Gospodaru Zemlje, Gospodaru svih svjetova!", TextEnglish = "Then, to Allāh belongs [all] praise - Lord of the heavens and Lord of the earth, Lord of the worlds.", TextGerman = "So gehört (alles) Lob Allah, dem Herrn der Himmel und dem Herrn der Erde, dem Herrn der Weltenbewohner’!" });
                        surah45.VerseList.Add(new Verse { Number = 37, TextBosnian = "Njemu pripada veličanstvenost na nebesima i na Zemlji, On je Silni i Mudri!", TextEnglish = "And to Him belongs [all] grandeur within the heavens and the earth, and He is the Exalted in Might, the Wise.", TextGerman = "Ihm gehört die Oberhoheit in den Himmeln und auf der Erde, und Er ist der Allmächtige und Allweise." });
                        result = surah45;
                        break;
                    #endregion

                    // 
                    #region
                    case 46:
                        Surah surah46 = new Surah(); surah46.Name = Constants.Surah46Name; surah46.Number = 46; surah46.VerseList = new List<Verse>();
                        surah46.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah46.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ha-mim,", TextEnglish = "Ḥā, Meem.[1477]", TextGerman = "Ha-Mim." });
                        surah46.VerseList.Add(new Verse { Number = 2, TextBosnian = "Knjigu objavljuje Allah, Silni i Mudri!", TextEnglish = "The revelation of the Book is from Allāh, the Exalted in Might, the Wise.", TextGerman = "(Dies ist) die Offenbarung des Buches von Allah, dem Allmächtigen und Allweisen." });
                        surah46.VerseList.Add(new Verse { Number = 3, TextBosnian = "Mi smo nebesa i Zemlju, i ono što je između njih, samo s Istinom stvorili i do roka određenog, ali oni koji ne vjeruju okreću se od onoga čime upozoravani budu.", TextEnglish = "We did not create the heavens and earth and what is between them except in truth and [for] a specified term. But those who disbelieve, from that of which they are warned, are turning away.", TextGerman = "Wir haben die Himmel und die Erde und was dazwischen ist nur in Wahrheit und (auf) eine festgesetzte Frist erschaffen. Aber diejenigen, die ungläubig sind, wenden sich von dem ab, wovor sie gewarnt werden." });
                        surah46.VerseList.Add(new Verse { Number = 4, TextBosnian = "Reci: 'Šta mislite o onima koje mimo Allaha molite? Pokažite mi koji dio Zemlje su stvorili, ili oni možda imaju kakva udjela u nebesima?! Donesite mi Knjigu objavljenu prije ove, ili samo kakav ostatak znanja, ako istinu govorite.'", TextEnglish = "Say, [O Muḥammad], 'Have you considered that which you invoke besides Allāh? Show me what they have created of the earth; or did they have partnership in [creation of] the heavens? Bring me a scripture [revealed] before this or a [remaining] trace of knowledge, if you should be truthful.'", TextGerman = "Sag: Was meint ihr zu dem, was ihr anstatt Allahs anruft? Zeigt mir, was sie von der Erde erschaffen haben. Oder haben sie etwa an den Himmeln teil? Bringt mir doch ein Buch vor diesem herbei oder die (geringste) Spur von Wissen, wenn ihr wahrhaftig seid." });
                        surah46.VerseList.Add(new Verse { Number = 5, TextBosnian = "Ko je u većoj zabludi od onih koji, umjesto Allaha, mole one koji im se do Kijametskog dana neće odazvati i koji su prema njihovim molbama ravnodušni.", TextEnglish = "And who is more astray than he who invokes besides Allāh those who will not respond to him until the Day of Resurrection [i.e., never], and they, of their invocation, are unaware.", TextGerman = "Und wer ist weiter abgeirrt als jemand, der anstatt Allahs jemanden anruft, der ihn nicht erhört bis zum Tag der Auferstehung? Und sie achten nicht auf ihr Bittgebet." });
                        surah46.VerseList.Add(new Verse { Number = 6, TextBosnian = "Kada ljudi budu sabrani, oni - idoli će im biti neprijatelji i poreći će obožavanje njihovo.", TextEnglish = "And when the people are gathered [that Day], they [who were invoked] will be enemies to them, and they will be deniers of their worship.", TextGerman = "Und wenn die Menschen versammelt werden, werden sie ihnen feind sein, und sie werden den von ihnen (empfangenen) Dienst verleugnen." });
                        surah46.VerseList.Add(new Verse { Number = 7, TextBosnian = "A kad se ovima Naši jasni ajeti uče, onda oni koji ne vjeruju govore o Istini čim im dođe: 'Ovo je očita čarolija!'", TextEnglish = "And when Our verses are recited to them as clear evidences, those who disbelieve say of the truth when it has come to them, 'This is obvious magic.'", TextGerman = "Und wenn ihnen Unsere Zeichen als klare Beweise verlesen werden, sagen diejenigen, die ungläubig sind, über die Wahrheit, da sie zu ihnen gekommen ist: Das ist deutliche Zauberei." });
                        surah46.VerseList.Add(new Verse { Number = 8, TextBosnian = "Ili govore: 'On ga izmišlja!' Reci: 'Pa, ako ga izmišljam, vi nemate nimalo moći da me od Allaha odbranite; On najbolje zna šta o Kur'anu govorite. On je dovoljan svjedok i meni i vama; On je Oprostitelj grijeha, Milostivi.'", TextEnglish = "Or do they say, 'He has invented it'? Say, 'If I have invented it, you will not possess for me [the power of protection] from Allāh at all. He is most knowing of that in which you are involved.[1478] Sufficient is He as Witness between me and you, and He is the Forgiving, the Merciful.'", TextGerman = "Oder sagen sie etwa: Er hat ihn ersonnen? Sag: Wenn ich ihn ersonnen haben sollte, so vermögt ihr für mich nichts gegen Allah (auszurichten). Er weiß sehr wohl, worüber ihr euch (ausgiebig) auslaßt. Er genügt als Zeuge zwischen mir und euch. Und Er ist der Allvergebende und Barmherzige." });
                        surah46.VerseList.Add(new Verse { Number = 9, TextBosnian = "Reci: 'Ja nisam prvi poslanik i ne znam šta će biti sa mnom ili s vama; ja slijedim samo ono što mi se objavljuje, i ja sam samo upozoritelj jasni.'", TextEnglish = "Say, 'I am not something original among the messengers,[1479] nor do I know what will be done with me or with you. I only follow that which is revealed to me, and I am not but a clear warner.'", TextGerman = "Sag: Ich bin kein Neubeginn unter den Gesandten, und ich weiß nicht, was mit mir, und auch nicht, was mit euch geschehen wird. Ich folge nur dem, was mir (als Offenbarung) eingegeben wird, und ich bin nur ein deutlicher Warner." });
                        surah46.VerseList.Add(new Verse { Number = 10, TextBosnian = "Reci: 'Kažite vi meni šta će s vama biti ako je on - Kur'an od Allaha, a vi u njega ne vjerujete, i ako je jedan od sinova Israilovih posvjedočio slično onome što je u njemu, pa on povjerovao, a vi se uzoholili Allah, zaista, ne upućuje na Pravi put ljude koji su zulumćari.", TextEnglish = "Say, 'Have you considered: if it [i.e., the Qur’ān] was from Allāh, and you disbelieved in it while a witness from the Children of Israel has testified to something similar[1480] and believed while you were arrogant...?'[1481] Indeed, Allāh does not guide the wrongdoing people.", TextGerman = "Sag: Was meint ihr, wenn er doch von Allah ist und ihr ihn aber verleugnet, während ein Zeuge von den Kindern Isra’ils etwas bezeugt, was ihm gleich ist, und so glaubt er an ihn, während ihr euch hochmütig verhaltet? Gewiß, Allah leitet das Volk der Ungerechten nicht recht." });
                        surah46.VerseList.Add(new Verse { Number = 11, TextBosnian = "I govore nevjernici o vjernicima: 'Da je kakvo dobro, nas oni ne bi u tome pretekli.' A kako pomoću njega Uputu nisu tražili, sigurno će reći: 'Ovo je još davna izmišljotina.'", TextEnglish = "And those who disbelieve say of those who believe, 'If it had [truly] been good, they would not have preceded us to it.' And when they are not guided by it, they will say, 'This is an ancient falsehood.'", TextGerman = "Und diejenigen, die ungläubig sind, sagen von denjenigen, die glauben: Wenn er etwas Gutes wäre, wären sie uns damit nicht zuvorgekommen. Und da sie sich nicht durch ihn rechtleiten lassen, werden sie sagen: Das ist eine alte ungeheuerliche Lüge." });
                        surah46.VerseList.Add(new Verse { Number = 12, TextBosnian = "Knjiga Musaova bila je predvodnik i milost prije njega. A ovo je Knjiga na arapskom jeziku koja nju potvrđuje, da bi upozorila one koji zulum čine, i da bi obradovala one koji dobro čine.", TextEnglish = "And before it was the scripture of Moses to lead and as a mercy. And this is a confirming Book in an Arabic tongue to warn those who have wronged and as good tidings to the doers of good.", TextGerman = "Und vor ihm (war) die Schrift Musas als Vorbild und Barmherzigkeit. Und dies ist ein bestätigendes Buch in arabischer Sprache, um diejenigen, die Unrecht tun, zu warnen, und als frohe Botschaft für die Gutes Tuenden." });
                        surah46.VerseList.Add(new Verse { Number = 13, TextBosnian = "Oni koji govore: 'Naš Gospodar je Allah!', i ustraju na Pravom putu, za njih straha nema i ni za čim nek ne tuguju!", TextEnglish = "Indeed, those who have said, 'Our Lord is Allāh,' and then remained on a right course - there will be no fear concerning them, nor will they grieve.", TextGerman = "Gewiß, diejenigen, die sagen: Unser Herr ist Allah und sich hierauf recht verhalten, über sie soll keine Furcht kommen, noch sollen sie traurig sein." });
                        surah46.VerseList.Add(new Verse { Number = 14, TextBosnian = "Oni će stanovnici Dženneta biti, u njemu će vječno boraviti, i to će im nagrada biti za ono što su radili.", TextEnglish = "Those are the companions of Paradise, abiding eternally therein as reward for what they used to do.", TextGerman = "Das sind die Insassen des (Paradies)gartens, ewig darin zu bleiben als Lohn für das, was sie zu tun pflegten." });
                        surah46.VerseList.Add(new Verse { Number = 15, TextBosnian = "A čovjeka smo zadužili da roditeljima svojim čini dobro; majka ga njegova tegobno nosi i tegobno ga rađa! Njegovo nošenje i dojenje trideset je mjeseci. Pa kad zrelost svoju dostigne i kad dostigne četrdeset godina, on rekne: 'Gospodaru moj, uputi me da Ti zahvalim na Tvojoj blagodati, koju si darovao meni i mojim roditeljima, i da dobro radim kojim si zadovoljan Ti, i potomstvo moje Ti učini dobrim! Ja se Tebi, doista, kajem, i ja sam musliman, predan Tebi.'", TextEnglish = "And We have enjoined upon man, to his parents, good treatment. His mother carried him with hardship and gave birth to him with hardship, and his gestation and weaning [period] is thirty months. [He grows] until, when he reaches maturity and reaches [the age of] forty years, he says, 'My Lord, enable me[1482] to be grateful for Your favor which You have bestowed upon me and upon my parents and to work righteousness of which You will approve and make righteous for me my offspring. Indeed, I have repented to You, and indeed, I am of the Muslims.'", TextGerman = "Und Wir haben dem Menschen anempfohlen, zu seinen Eltern gütig zu sein. Seine Mutter hat ihn unter Widerwillen getragen und unter Widerwillen zur Welt gebracht. Die (Zeit der) Schwangerschaft mit ihm bis zu seiner Entwöhnung (beträgt) dreißig Monate. Wenn er dann seine Vollreife erlangt hat und das Alter von vierzig Jahren erreicht hat, sagt er: Mein Herr, veranlasse mich, für Deine Gunst zu danken, die Du mir und meinen Eltern erwiesen hast, und rechtschaffen zu handeln, womit Du zufrieden bist. Und gib mir Rechtschaffenheit in meiner Nachkommenschaft. Ich wende mich Dir ja in Reue zu, und ich gehöre ja zu den (Dir) Ergebenen." });
                        surah46.VerseList.Add(new Verse { Number = 16, TextBosnian = "To su oni od kojih ćemo Mi ono bolje što su radili primiti, a preko ružnih postupaka njihovih preći; među stanovnicima Dženneta oni će biti, istinito obećanje koje im je dato Mi ćemo ispuniti.", TextEnglish = "Those are the ones from whom We will accept the best of what they did and overlook their misdeeds, [their being] among the companions of Paradise. [That is] the promise of truth which they had been promised.", TextGerman = "Das sind diejenigen, von denen Wir das Beste von dem, was sie getan haben, annehmen und über deren böse Taten Wir hinwegsehen, unter den Insassen des (Paradies)gartens - (dies ist) das wahrhaftige Versprechen, das ihnen stets gegeben wurde." });
                        surah46.VerseList.Add(new Verse { Number = 17, TextBosnian = "A svako ko roditeljima svojim kaže: 'Ih, vas! Zar mi obećavate da ću biti oživljen kad su prije mene toliki narodi nestali?' A kad njih dvoje Allahu zapomažući zbore: 'Teško tebi, vjeruj! Allahovo obećanje doista je istina!', on odgovara: 'To su izmišljotine naroda drevnih!'", TextEnglish = "But one who says to his parents, 'Uff[1483] to you; do you promise me that I will be brought forth [from the earth] when generations before me have already passed on [into oblivion]?' while they call to Allāh for help [and to their son], 'Woe to you! Believe! Indeed, the promise of Allāh is truth.' But he says, 'This is not but legends of the former peoples' -", TextGerman = "Und derjenige, der zu seinen Eltern sagt: Pfui über euch! Versprecht ihr mir etwa, ich sollte hervorgebracht werden, wo vor mir bereits Geschlechter dahingegangen sind? während sie beide Allah um Hilfe anrufen: Wehe dir! glaube doch! Gewiß, Allahs Versprechen ist wahr. Da sagt er: Das sind nur Fabeln der Früheren." });
                        surah46.VerseList.Add(new Verse { Number = 18, TextBosnian = "To su oni koji zaslužuju da se nad njima ispuni Riječ rečena o ummetima: džinima i ljudima koji su prije njih bili i nestali, oni su, zaista, gubitnici.", TextEnglish = "Those are the ones upon whom the word [i.e., decree] has come into effect, [who will be] among nations which had passed on before them of jinn and men. Indeed, they [all] were losers.", TextGerman = "Das sind diejenigen, gegen die das Wort unvermeidlich fällig geworden war, unter (anderen) Gemeinschaften von den Ginn und den Menschen, die bereits vor ihnen dahingegegangen sind. Gewiß, sie sind ja Verlierer." });
                        surah46.VerseList.Add(new Verse { Number = 19, TextBosnian = "Za sve će biti posebni stepeni, prema tome kako su radili; da ih nagradi ili kazni za djela njihova, zulum im neće učinjen biti.", TextEnglish = "And for all there are degrees [of reward and punishment] for what they have done, and [it is] so that He may fully compensate them for their deeds, and they will not be wronged.", TextGerman = "Für alle wird es Rangstufen geben gemäß dem, was sie getan haben, und damit Er ihnen ihre Taten voll erstatte, und es wird ihnen kein Unrecht zugefügt." });
                        surah46.VerseList.Add(new Verse { Number = 20, TextBosnian = "A na Dan kad budu oni koji nisu vjerovali Vatri izloženi, bit će rečeno: 'Vi ste u svom životu na Zemlji sve svoje naslade iskoristili i u njima uživali, a danas ponižavajućom patnjom bit ćete kažnjeni zato što ste se na Zemlji, bez ikakva osnova, oholo ponašali i što ste buntovni bili.'", TextEnglish = "And the Day those who disbelieved are exposed to the Fire [it will be said], 'You exhausted your pleasures during your worldly life and enjoyed them, so this Day you will be awarded the punishment of [extreme] humiliation because you were arrogant upon the earth without right and because you were defiantly disobedient.'", TextGerman = "Und am Tag, da diejenigen, die ungläubig sind, dem (Höllen)feuer vorgeführt werden: Ihr habt eure guten Dinge im diesseitigen Leben dahingehen lassen und sie genossen. Heute wird euch mit der schmählichen Strafe vergolten, daß ihr euch auf der Erde ohne Recht hochmütig zu verhalten und daß ihr zu freveln pflegtet." });
                        surah46.VerseList.Add(new Verse { Number = 21, TextBosnian = "I spomeni brata Adovog, kad je narod svoj u Ahkafu upozoravao - a bilo je i prije njega i poslije njega upozoravatelja: 'Samo Allahu ibadet činite! Ja se bojim za vas patnje na Velikom danu!'", TextEnglish = "And mention, [O Muḥammad], the brother of ʿAad,[1484] when he warned his people in [the region of] al-Aḥqāf - and warners had already passed on before him and after him - [saying], 'Do not worship except Allāh. Indeed, I fear for you the punishment of a terrible day.'[1485]", TextGerman = "Und gedenke des Bruders der Ad’, als er bei al-Ahqaf sein Volk warnte, wo bereits Warner vor ihm und (auch) nach ihm dahingingen: Ihr sollt nur Allah dienen. Gewiß, ich fürchte für euch die Strafe eines gewaltigen Tages." });
                        surah46.VerseList.Add(new Verse { Number = 22, TextBosnian = "Oni su odgovarali: 'Zar si nam došao da nas od božanstava naših odvratiš? Pa daj nam ono što nam prijeteći obećavaš, ako istinu govoriš!'", TextEnglish = "They said, 'Have you come to delude us away from our gods? Then bring us what you promise us, if you should be of the truthful.'", TextGerman = "Sie sagten: Bist du zu uns gekommen, um uns von unseren Göttern abwendig zu machen? So bring uns doch her, was du uns androhst, wenn du zu den Wahrhaftigen gehörst." });
                        surah46.VerseList.Add(new Verse { Number = 23, TextBosnian = "On reče: 'Znanje o tome u Allaha je! A ja vam saopćavam ono s čime sam poslan, ali ja, doista, vidim da ste vi narod u neznanju ogrezao.'", TextEnglish = "He said, 'Knowledge [of its time] is only with Allāh, and I convey to you that with which I was sent; but I see you [to be] a people behaving ignorantly.'", TextGerman = "Er sagte: Das Wissen (darüber) ist nur bei Allah; ich übermittele euch (nur) das, womit ich gesandt worden bin. Aber ich sehe, ihr seid Leute, die töricht sind." });
                        surah46.VerseList.Add(new Verse { Number = 24, TextBosnian = "Pa kad su je vidjeli u vidu oblaka na obzorju, koji se prema dolinama njihovim kretao, povikaše: 'Ovaj nam oblak kišu donosi!' 'Ne, to je ono što ste požurivali: vjetar koji vam bolnu patnju nosi,", TextEnglish = "And when they saw it as a cloud approaching their valleys, they said, 'This is a cloud bringing us rain!' Rather, it is that for which you were impatient:[1486] a wind, within it a painful punishment,", TextGerman = "Als sie es als sich ausbreitende Wolke sahen, die auf ihre Täler zukam, sagten sie: Das ist eine sich ausbreitende Wolke, die uns Regen bringt. - Nein! Vielmehr ist es das, was ihr zu beschleunigen gewünscht habt: ein Wind, in dem es schmerzhafte Strafe gibt," });
                        surah46.VerseList.Add(new Verse { Number = 25, TextBosnian = "koji, odredbom Gospodara svoga, sve ruši.' I ujutro su se vidjele samo nastambe njihove; tako Mi kažnjavamo narod prestupnički.", TextEnglish = "Destroying everything by command of its Lord. And they became so that nothing was seen [of them] except their dwellings. Thus do We recompense the criminal people.", TextGerman = "der auf den Befehl seines Herrn alles zerstört. So waren sie am Morgen (in einem Zustand), daß nichts anderes (von ihnen) als nur ihre Wohnungen zu sehen war. So vergelten Wir dem Volk der Übeltäter." });
                        surah46.VerseList.Add(new Verse { Number = 26, TextBosnian = "Njima smo dali mogućnosti koje vama nismo dali: i sluh i vid i razum smo im dali, ali im ni sluh njihov ni vid njihov ni razum njihov nisu ni od kakve koristi bili, jer su Allahove znake poricali - i sa svih strana okružilo ih je ono čemu su se ismijavali.", TextEnglish = "And We had certainly established them in such as We have not established you, and We made for them hearing and vision and hearts [i.e., intellect]. But their hearing and vision and hearts availed them not from anything [of the punishment] when they were [continually] rejecting the signs of Allāh; and they were enveloped by what they used to ridicule.", TextGerman = "Und Wir hatten ihnen ja eine feste Stellung verliehen darin, worin Wir euch keine feste Stellung verliehen haben. Und Wir hatten ihnen Gehör, Augenlicht und Herzen gegeben. Aber weder ihr Gehör, noch ihr Augenlicht, noch ihre Herzen nützten ihnen etwas, da sie Allahs Zeichen zu verleugnen pflegten, und es umschloß sie das, worüber sie sich lustig zu machen pflegten." });
                        surah46.VerseList.Add(new Verse { Number = 27, TextBosnian = "A naselja oko vas smo uništili, i raznovrsne znakove im pokazali, ne bi li se pokajali.", TextEnglish = "And We have already destroyed what surrounds you of [those] cities, and We have diversified the signs [or verses] that perhaps they might return [from disbelief].", TextGerman = "Wir hatten bereits vernichtet, was an Städten in eurer Umgebung ist. Und Wir hatten die Zeichen verschiedenartig dargelegt, auf daß sie umkehren mochten." });
                        surah46.VerseList.Add(new Verse { Number = 28, TextBosnian = "A zašto im nisu pomogli oni koje su, pored Allaha, prihvatili da im budu posrednici i bogovi? Ali, njih nije bilo. To je bila njihova laž i ono što su izmišljali.", TextEnglish = "Then why did those they took besides Allāh as deities by which to approach [Him][1487] not aid them? But they had strayed [i.e., departed] from them. And that was their falsehood and what they were inventing.", TextGerman = "Wenn ihnen doch diejenigen geholfen hätten, die sie sich anstatt Allahs zu Göttern nahmen (als Vermittler), um sich Zutritt in Seine Nähe zu verschaffen! Aber nein! Sie entschwanden ihnen. Dies war ihre ungeheuerlich Lüge und das, was sie zu ersinnen pflegten." });
                        surah46.VerseList.Add(new Verse { Number = 29, TextBosnian = "Kada ti poslasmo nekoliko džina da Kur'an slušaju, kada dođoše da ga čuju, oni rekoše: 'Šutite i slušajte!' A kad se završi, vratiše se narodu svome da upozoravaju.", TextEnglish = "And [mention, O Muḥammad], when We directed to you a few of the jinn, listening to the Qur’ān. And when they attended it, they said, 'Listen attentively.' And when it was concluded, they went back to their people as warners.", TextGerman = "Und (gedenke,) als Wir eine kleinere Schar Ginn veranlaßten, sich zu dir zu begeben und dem Qur’an zuzuhören. Als sie zu ihm eingefunden hatten, sagten sie: Horcht hin! Als er dann zum Ende kam, kehrten sie zu ihrem Volk zurück, um sie zu warnen." });
                        surah46.VerseList.Add(new Verse { Number = 30, TextBosnian = "O narode naš', govorili su, 'mi smo slušali Knjigu koja se poslije Musaa objavljuje, koja potvrđuje da su istinite i one prije nje, i koja ka Istini i na Pravi put upućuje.", TextEnglish = "They said, 'O our people, indeed we have heard a [recited] Book revealed after Moses confirming what was before it which guides to the truth and to a straight path.", TextGerman = "Sie sagten: O unser Volk, wir haben ein Buch gehört, das nach Musa (als Offenbarung) herabgesandt worden ist, das zu bestätigen, was vor ihm war, und das zur Wahrheit und zu einem geraden Weg leitet." });
                        surah46.VerseList.Add(new Verse { Number = 31, TextBosnian = "O narode naš, odazovite se Allahovom glasniku i vjerujte u Allaha, On će vam neke grijehe vaše oprostiti i od patnje vas bolne zaštititi!'", TextEnglish = "O our people, respond to the Caller [i.e., Messenger] of Allāh[1488] and believe in him; He [i.e., Allāh] will forgive for you your sins and protect you from a painful punishment.", TextGerman = "O unser Volk, erhört Allahs Rufer und glaubt an ihn, so vergibt Er euch (etwas) von euren Sünden und gewährt euch Schutz vor schmerzhafter Strafe." });
                        surah46.VerseList.Add(new Verse { Number = 32, TextBosnian = "A oni koji se ne odazovu Allahovu glasniku, takvi Mu na Zemlji neće umaći i mimo Njega neće zaštitnika naći. Oni su u velikoj zabludi.", TextEnglish = "But he who does not respond to the Caller of Allāh will not cause failure [to Him] upon earth, and he will not have besides Him any protectors. Those are in manifest error.'", TextGerman = "Wer Allahs Rufer nicht erhört, wird sich (Ihm) auf der Erde doch nicht entziehen können, und er hat außer Ihm keine Schutzherren. Jene befinden sich in deutlichem Irrtum." });
                        surah46.VerseList.Add(new Verse { Number = 33, TextBosnian = "Zar ne znaju da je Allah- Koji je nebesa i Zemlju stvorio i Koji nije, stvarajući ih, iznemogao - kadar oživiti mrtve?! Jeste, uistinu On nad svime ima moć.", TextEnglish = "Do they not see that Allāh, who created the heavens and earth and did not fail in their creation, is able to give life to the dead? Yes. Indeed, He is over all things competent.", TextGerman = "Sehen sie denn nicht, daß Allah, Der die Himmel und die Erde erschaffen hat und bei ihrer Erschaffung nicht ermüdet ist, (auch) die Macht hat, die Toten wieder lebendig zu machen? Ja doch, gewiß, Er hat zu allem die Macht." });
                        surah46.VerseList.Add(new Verse { Number = 34, TextBosnian = "A na Dan kad oni, koji nisu vjerovali, Vatri budu izloženi: 'Zar ovo nije istina?', odgovorit će: 'Jest, Gospodara nam našeg!' 'E pa iskusite patnju', reći će On, 'zbog toga što ste stalno poricali.'", TextEnglish = "And the Day those who disbelieved are exposed to the Fire [it will be said], 'Is this not the truth?' They will say, 'Yes, by our Lord.' He will say, 'Then taste the punishment for what you used to deny.'[1489]", TextGerman = "Und am Tag, da diejenigen, die ungläubig sind, dem (Höllen)feuer vorgeführt werden: Ist dies nicht die Wahrheit? Sie werden sagen: Ja, doch, bei unserem Herrn! Er wird sagen: Kostet nun die Strafe dafür, daß ihr stets ungläubig wart." });
                        surah46.VerseList.Add(new Verse { Number = 35, TextBosnian = "Ti izdrži kao što su izdržali odlučni poslanici i ne traži da im kazna što prije dođe! A onoga Dana kada dožive ono čime im se prijeti, učinit će im se da su ostali samo jedan časak dana. Ovo je obznana! A zar će ko drugi biti uništen do narod buntovni?!", TextEnglish = "So be patient, [O Muḥammad], as were those of determination among the messengers and do not be impatient for them.[1490] It will be - on the Day they see that which they are promised - as though they had not remained [in the world] except an hour of a day. [This is] notification. And will [any] be destroyed except the defiantly disobedient people?", TextGerman = "Sei nun standhaft, wie diejenigen der Gesandten, die Entschlossenheit besaßen, standhaft waren; und wünsche nichts gegen sie zu beschleunigen. Am Tag, da sie sehen, was ihnen angedroht wird, glauben sie, nur eine Stunde eines Tages verweilt zu haben. (Dies ist) eine Botschaft. Wird denn jemand vernichtet außer dem Volk der Frevler?" });
                        result = surah46;
                        break;
                    #endregion

                    // 
                    #region
                    case 47:
                        Surah surah47 = new Surah(); surah47.Name = Constants.Surah47Name; surah47.Number = 47; surah47.VerseList = new List<Verse>();
                        surah47.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah47.VerseList.Add(new Verse { Number = 1, TextBosnian = "Onima koji ne vjeruju i od Allahova Puta odvraćaju On će poništiti djela njihova.", TextEnglish = "Those who disbelieve and avert [people] from the way of Allāh - He will waste their deeds.[1492]", TextGerman = "Denjenigen, die ungläubig sind und von Allahs Weg abhalten, läßt Er ihre Werke fehlgehen." });
                        surah47.VerseList.Add(new Verse { Number = 2, TextBosnian = "A onima koji vjeruju i dobra djela čine i vjeruju u ono što se objavljuje Muhammedu, a to je Istina od Gospodara njihova, On će prekriti njihova loša djela i stanje će njihovo poboljšati.", TextEnglish = "And those who believe and do righteous deeds and believe in what has been sent down upon Muḥammad - and it is the truth from their Lord - He will remove from them their misdeeds and amend their condition.", TextGerman = "Denjenigen aber, die glauben und rechtschaffene Werke tun und an das glauben, was Muhammad offenbart worden ist - und es ist (ja) die Wahrheit von ihrem Herrn -, tilgt Er ihre bösen Taten und bessert ihren Gemütszustand." });
                        surah47.VerseList.Add(new Verse { Number = 3, TextBosnian = "To je zato što oni koji ne vjeruju slijede neistinu, a oni koji vjeruju slijede Istinu od Gospodara svoga. Eto, tako Allah njihove primjere navodi ljudima.", TextEnglish = "That is because those who disbelieve follow falsehood, and those who believe follow the truth from their Lord. Thus does Allāh present to the people their comparisons.[1493]", TextGerman = "Dies, weil diejenigen, die ungläubig sind, dem Falschen folgen, und diejenigen, die glauben, der Wahrheit von ihrem Herrn folgen. So prägt Allah den Menschen ihre Gleichnisse." });
                        surah47.VerseList.Add(new Verse { Number = 4, TextBosnian = "Kada se u borbi s onima koji ne vjeruju sretnete, po šijama ih udarajte sve dok ih ne uništite, a onda onima koje zarobite pritegnite sveze, i poslije, ili ih velikodušno oslobodite, ili otkupninu zahtijevajte, sve dok borba 'svoje terete ne spusti' -dok ne prestane. Tako je to, a da Allah hoće, On bi ih sam savladao, ali On vas želi iskušati jedne drugima. On neće poništiti djela onih koji na Allahovu putu poginu.", TextEnglish = "So when you meet those who disbelieve [in battle], strike [their] necks until, when you have inflicted slaughter upon them, then secure [their] bonds,[1494] and either [confer] favor[1495] afterwards or ransom [them] until the war lays down its burdens.[1496] That [is the command]. And if Allāh had willed, He could have taken vengeance upon them [Himself], but [He ordered armed struggle] to test some of you by means of others. And those who are killed in the cause of Allāh - never will He waste their deeds.", TextGerman = "Wenn ihr auf diejenigen, die ungläubig sind, (im Kampf) trefft, dann schlagt (ihnen auf) die Nacken. Wenn ihr sie schließlich schwer niedergeschlagen habt, dann legt (ihnen) die Fesseln fest an. Danach (laßt sie) als Wohltat frei oder gegen Lösegeld, bis der Krieg seine Lasten ablegt. Dies (soll so sein)! Und wenn Allah wollte, würde Er sie wahrlich (allein) besiegen . Er will aber damit die einen von euch durch die anderen prüfen. Und denjenigen, die auf Allahs Weg getötet werden, wird Er ihre Werke nicht fehlgehen lassen;" });
                        surah47.VerseList.Add(new Verse { Number = 5, TextBosnian = "On će ih, sigurno, uputiti i prilike njihove poboljšati,", TextEnglish = "He will guide them and amend their condition", TextGerman = "Er wird sie rechtleiten und ihren Gemütszustand bessern" });
                        surah47.VerseList.Add(new Verse { Number = 6, TextBosnian = "i u Džennet ih uvesti, o kome ih je već upoznao.", TextEnglish = "And admit them to Paradise, which He has made known to them.", TextGerman = "und sie in den (Paradies)garten eingehen lassen, den Er ihnen kenntlich gemacht hat." });
                        surah47.VerseList.Add(new Verse { Number = 7, TextBosnian = "O vi koji vjerujete, ako Allaha pomognete, i On će vas pomoći i korake vaše učvrstiti.", TextEnglish = "O you who have believed, if you support Allāh, He will support you and plant firmly your feet.", TextGerman = "O die ihr glaubt, wenn ihr Allah(s Sache) helft, hilft Er euch und festigt eure Füße." });
                        surah47.VerseList.Add(new Verse { Number = 8, TextBosnian = "A oni koji ne vjeruju - pa, propast njima! On će djela njihova uzaludnim učiniti.", TextEnglish = "But those who disbelieve - for them is misery, and He will waste their deeds.", TextGerman = "Diejenigen aber, die ungläubig sind, so sollen sie (in Unglück) fallen! Und Er wird ihre Werke fehlgehen lassen." });
                        surah47.VerseList.Add(new Verse { Number = 9, TextBosnian = "To je zato što oni mrze ono što Allah objavljuje, i On će djela njihova poništiti.", TextEnglish = "That is because they disliked what Allāh revealed, so He rendered worthless their deeds.", TextGerman = "Dies, weil ihnen zuwider ist, was Allah (als Offenbarung) herabgesandt hat, und so läßt Er ihre Werke hinfällig werden." });
                        surah47.VerseList.Add(new Verse { Number = 10, TextBosnian = "Zašto oni ne idu po Zemlji da vide kakav je bio kraj onih prije njih? Njih je Allah uništio, a i nevjernicima tako pripada.", TextEnglish = "Have they not traveled through the land and seen how was the end of those before them? Allāh destroyed [everything] over them,[1497] and for the disbelievers is something comparable.", TextGerman = "Sind sie denn nicht auf der Erde umhergereist, so daß sie schauen (konnten), wie das Ende derjenigen vor ihnen war? Allah hat über sie Zerstörung gebracht. Für die Ungläubigen wird es etwas dergleichen geben." });
                        surah47.VerseList.Add(new Verse { Number = 11, TextBosnian = "To je zato što je Allah zaštitnik onih koji vjeruju i što nevjernici zaštitnika nemaju.", TextEnglish = "That is because Allāh is the protector of those who have believed and because the disbelievers have no protector.", TextGerman = "Dies, weil Allah der Schutzherr derjenigen ist, die glauben, und weil die Ungläubigen keinen Schutzherrn haben." });
                        surah47.VerseList.Add(new Verse { Number = 12, TextBosnian = "Allah će one koji vjeruju i dobra djela čine uvesti u džennetske bašče, kroz koje rijeke teku, a oni koji ne vjeruju - koji se naslađuju i žderu kao što stoka ždere - njihovo će prebivalište Vatra biti!", TextEnglish = "Indeed, Allāh will admit those who have believed and done righteous deeds to gardens beneath which rivers flow, but those who disbelieve enjoy themselves and eat as grazing livestock eat, and the Fire will be a residence for them.", TextGerman = "Allah läßt diejenigen, die glauben und rechtschaffene Werke tun, in Gärten eingehen, durcheilt von Bächen’. Diejenigen aber, die ungläubig sind, genießen und essen (im Diesseits), wie das Vieh frißt; das (Höllen)feuer wird ein Aufent haltsort für sie sein." });
                        surah47.VerseList.Add(new Verse { Number = 13, TextBosnian = "A koliko smo gradova uništili, mnogo moćnijih od tvoga grada iz kojeg si protjeran, i nije bilo nikog da im pomogne.", TextEnglish = "And how many a city was stronger than your city [i.e., Makkah] which drove you out? We destroyed them; and there was no helper for them.", TextGerman = "Und wie viele Städte, die eine stärkere Kraft hatten als deine Stadt, die dich vertrieben hat, haben Wir vernichtet; und da hatten sie keine Helfer." });
                        surah47.VerseList.Add(new Verse { Number = 14, TextBosnian = "Zar je onaj koji ima jasnu predstavu o Gospodaru svome kao oni kojima su uljepšana ružna djela njihova i koji se za hirovima svojim povode?!", TextEnglish = "So is he who is on clear evidence from his Lord like him to whom the evil of his work has been made attractive and they follow their [own] desires?", TextGerman = "Ist denn einer, der sich auf einen klaren Beweis von seinem Herrn stützt, wie jemand, dem sein böses Tun ausgeschmückt wird (,) und (wie diejenigen,) die ihren Neigungen folgen?" });
                        surah47.VerseList.Add(new Verse { Number = 15, TextBosnian = "Izgled Dženneta koji je obećan onima koji se Allaha boje ovakav je: u njemu su rijeke od vode neustajale i rijeke od mlijeka nepromijenjena okusa, i rijeke od vina, prijatna onima koji piju, i rijeke od meda procijeđenog, i u njemu za njih ima voća svakovrsnog, a usto i oprosta od Gospodara njihova. Zar su takvi isti kao oni koji će u Vatri vječno boraviti, koji će uzavrelom vodom biti pojeni koja će im crijeva kidati?!", TextEnglish = "Is the description of Paradise, which the righteous are promised, wherein are rivers of water unaltered,[1498] rivers of milk the taste of which never changes, rivers of wine delicious to those who drink, and rivers of purified honey, in which they will have from all [kinds of] fruits and forgiveness from their Lord... [Are its inhabitants] like those who abide eternally in the Fire and are given to drink scalding water that will sever their intestines?", TextGerman = "Das Gleichnis des (Paradies)gartens, der den Gottesfürchtigen versprochen ist: Darin sind Bäche mit Wasser, das nicht schal wird, und Bäche mit Milch, deren Geschmack sich nicht ändert, und Bäche mit Wein, der köstlich ist für diejenigen, die (davon) trinken, und Bäche mit geklärtem Honig. Und sie haben darin von allen Früchten und Vergebung von ihrem Herrn. (Sind diese denn) jemandem gleich, der im (Höllen)feuer ewig bleibt und dem heißes Wasser zu trinken gegeben wird, das seine Gedärme zerreißt?" });
                        surah47.VerseList.Add(new Verse { Number = 16, TextBosnian = "Ima onih koji dolaze da te slušaju, ali čim se od tebe udalje, pitaju one kojima je dato znanje: 'Šta ono on maloprije reče?' To su oni čija je srca Allah zapečatio i koji se za hirovima svojim povode.", TextEnglish = "And among them, [O Muḥammad], are those who listen to you, until when they depart from you, they say to those who were given knowledge,[1499] 'What has he said just now?' Those are the ones of whom Allāh has sealed over their hearts and who have followed their [own] desires.", TextGerman = "Und unter ihnen gibt es manche, die dir zuhören. Wenn sie aber von dir hinausgehen, sagen sie zu denjenigen, denen das Wissen gegeben worden ist: Was hat er eben gesagt? Das sind diejenigen, deren Herzen Allah versiegelt hat und die ihren Neigungen folgen." });
                        surah47.VerseList.Add(new Verse { Number = 17, TextBosnian = "A one koji za Uputom streme, On će im Uputu povećati i bogobojaznost dati.", TextEnglish = "And those who are guided - He increases them in guidance and gives them their righteousness.[1500]", TextGerman = "Und denjenigen, die rechtgeleitet sind, mehrt Er die Rechtleitung und verleiht ihnen ihre Gottesfurcht." });
                        surah47.VerseList.Add(new Verse { Number = 18, TextBosnian = "Pa, da li oni čekaju samo da Čas kijameta iznenada dođe, a već su došli predznaci njegovi? A kakve im koristi od prihvatanja opomene kad im on dođe?", TextEnglish = "Then do they await except that the Hour should come upon them unexpectedly? But already there have come [some of] its indications. Then how [i.e., what good] to them, when it has come, will be their remembrance?", TextGerman = "Erwarten sie denn etwas anderes, als daß die Stunde plötzlich über sie kommt? So sind ihre Vorzeichen bereits eingetroffen. Wie soll ihnen dann ihre Selbstbesinnung nützen, wenn sie über sie kommt?" });
                        surah47.VerseList.Add(new Verse { Number = 19, TextBosnian = "Znaj da nema boga osim Allaha! Traži oprost za svoje grijehe i za vjernike i vjernice! Allah zna vaše kretnje, kud se krećete i mjesto vašeg smiraja gdje boravite.", TextEnglish = "So know, [O Muḥammad], that there is no deity except Allāh and ask forgiveness for your sin[1501] and for the believing men and believing women. And Allāh knows of your movement and your resting place.", TextGerman = "Wisse also, daß es keinen Gott außer Allah gibt. Und bitte um Vergebung für deine Sünde und für die gläubigen Männer und die gläubigen Frauen. Allah kennt euren Wandel und euren Aufenthalt." });
                        surah47.VerseList.Add(new Verse { Number = 20, TextBosnian = "Oni koji vjeruju govore: 'Zašto se ne objavi jedna sura. A kada se objavi jasna sura i u njoj se spomene borba, ti vidiš one u čijim je srcima bolest kako te gledaju pogledom pred smrt onesviješćenog! A preči bi im bili", TextEnglish = "Those who believe say, 'Why has a sūrah[1502] not been sent down?' But when a precise sūrah is revealed and battle is mentioned therein, you see those in whose hearts is disease [i.e., hypocrisy] looking at you with a look of one overcome by death. And more appropriate for them[1503] [would have been]", TextGerman = "Und diejenigen, die glauben, sagen: Wäre doch eine Sura offenbart worden! Wenn aber eine eindeutige Sura herabgesandt wird und darin der Kampf erwähnt wird, siehst du diejenigen, in deren Herzen Krankheit ist, dich anschauen, wie einer schaut, der im Sterben ohnmächtig wird. Näherliegender wären für sie" });
                        surah47.VerseList.Add(new Verse { Number = 21, TextBosnian = "pokornost i razuman govor! A kada borba bude neminovna i situacija postane ozbiljna, bolje bi im bilo da su prema Allahu iskreni.", TextEnglish = "Obedience and good words. And when the matter [of fighting] was determined, if they had been true to Allāh, it would have been better for them.", TextGerman = "Gehorsam und geziemende Worte. Wenn die Angelegenheit beschlossen ist, dann wäre es wahrlich besser für sie, sie würden Allah gegenüber wahrhaftig sein." });
                        surah47.VerseList.Add(new Verse { Number = 22, TextBosnian = "Možda biste i vi, kad biste se okrenuli, nered na Zemlji činili i rodbinske veze kidali!", TextEnglish = "So would you perhaps, if you turned away,[1504] cause corruption on earth and sever your [ties of] relationship?", TextGerman = "Werdet ihr vielleicht, wenn ihr euch abkehrt, auf der Erde Unheil stiften und eure Verwandtschaftsbande zerreißen?" });
                        surah47.VerseList.Add(new Verse { Number = 23, TextBosnian = "To su oni koje je Allah prokleo i gluhim i slijepim ih učinio.", TextEnglish = "Those [who do so] are the ones that Allāh has cursed, so He deafened them and blinded their vision.", TextGerman = "Das sind diejenigen, die Allah verflucht; so macht Er sie taub und läßt ihr Augenlicht erblinden." });
                        surah47.VerseList.Add(new Verse { Number = 24, TextBosnian = "Kako oni ne razmisle o Kur'anu, ili su im na srcima katanci!", TextEnglish = "Then do they not reflect upon the Qur’ān, or are there locks upon [their] hearts?", TextGerman = "Denken sie denn nicht sorgfältig über den Qur’an nach? Oder sind an (diesen) Herzen deren Verriegelungen (angebracht)?" });
                        surah47.VerseList.Add(new Verse { Number = 25, TextBosnian = "Doista je onima koji su se vratili onome na čemu su bili, nakon što im Uputa jasna bi - šejtan to lijepim prikazao i lažnu im nadu ulivao.", TextEnglish = "Indeed, those who reverted back [to disbelief] after guidance had become clear to them - Satan enticed them and prolonged hope for them.", TextGerman = "Gewiß, diejenigen, die den Rücken kehren, nachdem ihnen die Rechtleitung klar geworden ist, denen hat der Satan (etwas) eingeredet und Hoffnung auf Aufschub gemacht." });
                        surah47.VerseList.Add(new Verse { Number = 26, TextBosnian = "To je zato što su govorili onima koji ne vole ono što Allah objavljuje: 'Mi ćemo vam se u nečemu pokoravati', a Allah dobro zna njihove tajne.", TextEnglish = "That is because they said to those who disliked what Allāh sent down,[1505] 'We will obey you in part of the matter.' And Allāh knows what they conceal.", TextGerman = "Dies, weil sie zu denjenigen sagen, denen zuwider ist, was Allah offenbart hat: Wir werden euch in einigen Angelegenheiten gehorchen. Aber Allah weiß, was sie im geheimen sagen." });
                        surah47.VerseList.Add(new Verse { Number = 27, TextBosnian = "A kako će tek biti kad im meleci budu duše uzimali udarajući po obrazima i po leđima njihovim!", TextEnglish = "Then how [will it be] when the angels take them in death, striking their faces and their backs?", TextGerman = "Wie (wird es sein), wenn die Engel sie abberufen, wobei sie ihre Gesichter und ihre Rücken schlagen?" });
                        surah47.VerseList.Add(new Verse { Number = 28, TextBosnian = "Tako će biti zato što su ono što izaziva Allahovu srdžbu slijedili, a ono čime je On zadovoljan prezirali; On će djela njihova poništiti.", TextEnglish = "That is because they followed what angered Allāh and disliked [what earns] His pleasure, so He rendered worthless their deeds.", TextGerman = "Dies, weil sie dem folgen, was Allah mißfällt, und ihnen Sein Wohlgefallen zuwider ist, und so läßt Er ihre Werke hinfällig werden." });
                        surah47.VerseList.Add(new Verse { Number = 29, TextBosnian = "Zar oni bolesnih srca misle da Allah zlobe njihove neće na vidjelo iznijeti?", TextEnglish = "Or do those in whose hearts is disease think that Allāh would never expose their [feelings of] hatred?", TextGerman = "Oder meinen diejenigen, in deren Herzen Krankheit ist, Allah würde ihren Groll nicht zum Vorschein bringen?" });
                        surah47.VerseList.Add(new Verse { Number = 30, TextBosnian = "A da hoćemo, Mi bismo ti ih, uistinu, pokazali i ti bi ih, sigurno, po obilježjima njihovim poznao. Ali, ti ćeš ih poznati po načinu govora njihova, a Allah zna postupke vaše.", TextEnglish = "And if We willed, We could show them to you, and you would know them by their mark; but you will surely know them by the tone of [their] speech. And Allāh knows your deeds.", TextGerman = "Und wenn Wir wollten, würden Wir sie dir fürwahr zeigen, und so würdest du sie sicher an ihrem Merkmal erkennen. Und du wirst sie ganz gewiß an ihrer schiefen Sprache erkennen. Und Allah weiß über eure Werke Bescheid." });
                        surah47.VerseList.Add(new Verse { Number = 31, TextBosnian = "Mi ćemo vas iskušavati da bismo znali borce na Allahovom putu i strpljive među vama, i da bismo otkrili vijesti o vama.", TextEnglish = "And We will surely test you until We make evident those who strive among you [for the cause of Allāh] and the patient, and We will test your affairs.", TextGerman = "Und Wir werden euch ganz gewiß prüfen, bis Wir feststellen, welche sich abmühen von euch und welche standhaft sind, und bis Wir eure Werke prüfen." });
                        surah47.VerseList.Add(new Verse { Number = 32, TextBosnian = "Zaista, oni koji ne vjeruju i od Allahova puta odvraćaju, i koji se Poslaniku suprotstavljaju, nakon što im je postala jasna Uputa, neće nimalo Allahu nauditi, i On će djela njihova poništiti.", TextEnglish = "Indeed, those who disbelieved and averted [people] from the path of Allāh and opposed the Messenger after guidance had become clear to them - never will they harm Allāh at all, and He will render worthless their deeds.", TextGerman = "Gewiß, diejenigen, die ungläubig sind, von Allahs Weg abhalten und dem Gesandten entgegenwirken, nachdem ihnen die Rechtleitung klar geworden ist, können Allah in nichts schaden. Und Er wird ihre Werke hinfällig werden lassen." });
                        surah47.VerseList.Add(new Verse { Number = 33, TextBosnian = "O vi koji vjerujete, pokoravajte se Allahu i pokoravajte se Poslaniku, i ne ništite djela svoja!", TextEnglish = "O you who have believed, obey Allāh and obey the Messenger and do not invalidate your deeds.", TextGerman = "O die ihr glaubt, gehorcht Allah und gehorcht dem Gesandten und macht eure Werke nicht zunichte." });
                        surah47.VerseList.Add(new Verse { Number = 34, TextBosnian = "Onima koji ne vjeruju i od Allahova puta odvraćaju, pa poslije kao nevjernici umiru, Allah, zaista, neće oprostiti.", TextEnglish = "Indeed, those who disbelieved and averted [people] from the path of Allāh and then died while they were disbelievers - never will Allāh forgive them.", TextGerman = "Gewiß, diejenigen, die ungläubig sind und von Allahs Weg abhalten und hierauf als Ungläubige sterben, so wird Allah ihnen nicht vergeben." });
                        surah47.VerseList.Add(new Verse { Number = 35, TextBosnian = "I ne malaksavajte i ne pozivajte primirju kad ste dominantniji, Allah je s vama i On vas neće nagrade za djela vaša lišiti.", TextEnglish = "So do not weaken and call for peace while you are superior; and Allāh is with you and will never deprive you of [the reward of] your deeds.", TextGerman = "So werdet nicht schwach und ruft (nicht) zum Frieden, wo ihr doch die Oberhand haben werdet, denn Allah ist mit euch, und Er wird euch nicht um eure Werke bringen." });
                        surah47.VerseList.Add(new Verse { Number = 36, TextBosnian = "Dunjalučki život samo je igra i zabava! A ako budete vjerovali i Allaha se bojali i grijeha klonili, On će vam nagrade vaše dati i imanja vaša od vas neće tražiti.", TextEnglish = "[This] worldly life is only amusement and diversion. And if you believe and fear Allāh, He will give you your rewards and not ask you for your properties.", TextGerman = "Das diesseitige Leben ist nur Spiel und Zerstreuung. Wenn ihr glaubt und gottesfürchtig seid, wird Er euch euren Lohn geben und von euch nicht euren (gesamten) Besitz verlangen." });
                        surah47.VerseList.Add(new Verse { Number = 37, TextBosnian = "Ako bi ih od vas tražio i prema vama uporan bio, vi biste škrtariti i On bi pokazao vaše kivnje.", TextEnglish = "If He should ask you for them and press you, you would withhold, and He would expose your hatred [i.e., unwillingness].", TextGerman = "Wenn Er ihn von euch verlangte und euch (dadurch) in Bedrängnis brächte, dann würdet ihr damit geizen, und Er würde (dadurch) euren Groll zum Vorschein bringen." });
                        surah47.VerseList.Add(new Verse { Number = 38, TextBosnian = "Vi se, eto, pozivate da trošite na Allahovu putu, ali, neki su od vas škrti, a ko škrtari, na svoju štetu škrtari, jer Allah je bogat, a vi ste siromašni. A ako se okrenete, On će vas drugim narodom zamijeniti, koji, onda, kao što ste vi neće biti.", TextEnglish = "Here you are - those invited to spend in the cause of Allāh - but among you are those who withhold [out of greed]. And whoever withholds only withholds [benefit] from himself; and Allāh is the Free of need, while you are the needy. And if you turn away [i.e., refuse], He will replace you with another people; then they will not be the likes of you.", TextGerman = "Ihr da seid es doch, die ihr aufgerufen werdet, auf Allahs Weg auszugeben. Jedoch gibt es unter euch welche, die geizen. Wer aber geizt, der geizt nur gegen sich selbst. Allah ist der Unbedürftige, ihr aber seid die Bedürftigen. Wenn ihr euch abkehrt, wird Er euch durch ein anderes Volk ersetzen, und sie werden dann nicht gleich euch sein." });
                        result = surah47;
                        break;
                    #endregion

                    // 
                    #region
                    case 48:
                        Surah surah48 = new Surah(); surah48.Name = Constants.Surah48Name; surah48.Number = 48; surah48.VerseList = new List<Verse>();
                        surah48.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah48.VerseList.Add(new Verse { Number = 1, TextBosnian = "Mi smo ti, zaista, dali da pobijediš pobjedom jasnom", TextEnglish = "Indeed, We have given you, [O Muḥammad], a clear conquest[1507]", TextGerman = "Gewiß, Wir haben dir einen deutlichen Sieg verliehen," });
                        surah48.VerseList.Add(new Verse { Number = 2, TextBosnian = "da bi ti Allah oprostio grijehe koji su prethodili i one koji će poslije doći, da bi blagodat Svoju tebi potpunom učinio da bi te na Pravi put uputio", TextEnglish = "That Allāh may forgive for you what preceded of your sin [i.e., errors] and what will follow and complete His favor upon you and guide you to a straight path", TextGerman = "damit dir Allah das von deinen Sünden vergebe, was vorher war und was später sein wird, und damit Er Seine Gunst an dir vollende und dich einen geraden Weg leite" });
                        surah48.VerseList.Add(new Verse { Number = 3, TextBosnian = "i da bi te Allah snažno podržao.", TextEnglish = "And [that] Allāh may aid you with a mighty victory.", TextGerman = "und (damit) Allah dir helfe mit mächtiger Hilfe." });
                        surah48.VerseList.Add(new Verse { Number = 4, TextBosnian = "On je Onaj Koji je smiraj u srca vjernika spustio da bi vjerovanje na vjerovanje svoje povećali, a vojske nebesa i Zemlje su Allahove; Allah je Onaj Koji sve zna i mudar je,", TextEnglish = "It is He who sent down tranquility into the hearts of the believers that they would increase in faith along with their [present] faith. And to Allāh belong the soldiers of the heavens and the earth, and ever is Allāh Knowing and Wise.", TextGerman = "Er ist es, der die innere Ruhe in die Herzen der Gläubigen herabgesandt hat, damit sie in ihrem Glauben noch an Glauben zunehmen. Und Allah gehören die Heerscharen der Himmel und der Erde. Und Allah ist Allwissend und Allweise." });
                        surah48.VerseList.Add(new Verse { Number = 5, TextBosnian = "da bi vjernike i vjernice uveo u džennetske bašče, kroz koje rijeke teku, u kojima će vječno boraviti, i da bi prekrio njihova loša djela - a to je u Allaha veliki uspjeh,", TextEnglish = "[And] that He may admit the believing men and the believing women to gardens beneath which rivers flow to abide therein eternally and remove from them their misdeeds - and ever is that, in the sight of Allāh, a great attainment", TextGerman = "(Dies,) damit Er die gläubigen Männer und die gläubigen Frauen in Gärten eingehen lasse, durcheilt von Bächen - ewig darin zu bleiben -, und ihnen ihre bösen Taten tilge; das ist bei Allah ein großartiger Erfolg." });
                        surah48.VerseList.Add(new Verse { Number = 6, TextBosnian = "i da bi kaznio licemjere i licemjerke i višebošce i višeboškinje, koji o Allahu zlo misle. Zlom su oni okruženi! Allah se na njih rasrdio i prokleo ih i pripremio im Džehennem, a grozno je to boravište!", TextEnglish = "And [that] He may punish the hypocrite men and hypocrite women, and the polytheist men and polytheist women - those who assume about Allāh an assumption of evil nature. Upon them is a misfortune of evil nature; and Allāh has become angry with them and has cursed them and prepared for them Hell, and evil it is as a destination.", TextGerman = "Und (damit) Er die Heuchler und Heuchlerinnen und die Götzendiener und Götzendienerinnen strafe, die von Allah die böse Erwartung hegen. Gegen sie wird die böse Schicksalswendung sein. Allah zürnt ihnen, verflucht sie und bereitet ihnen die Hölle - (wie) böse ist der Ausgang!" });
                        surah48.VerseList.Add(new Verse { Number = 7, TextBosnian = "Allahove su vojske nebesa i Zemlje; Allah je silan i mudar.", TextEnglish = "And to Allāh belong the soldiers of the heavens and the earth. And ever is Allāh Exalted in Might and Wise.", TextGerman = "Und Allah gehören die Heerscharen der Himmel und der Erde. Und Allah ist Allmächtig und Allweise." });
                        surah48.VerseList.Add(new Verse { Number = 8, TextBosnian = "Mi smo tebe poslali kao svjedoka, i radosnih vijesti donositelja, i upozoritelja,", TextEnglish = "Indeed, We have sent you as a witness and a bringer of good tidings and a warner", TextGerman = "Wir haben dich ja als Zeugen, Verkünder froher Botschaft und Warner gesandt," });
                        surah48.VerseList.Add(new Verse { Number = 9, TextBosnian = "da u Allaha i u Poslanika Njegova vjerujete, i da ga uznosite, i štujete, i da Njega -Allaha ujutro i navečer slavite i veličate.", TextEnglish = "That you [people] may believe in Allāh and His Messenger and honor him and respect him [i.e., the Prophet (ﷺ)] and exalt Him [i.e., Allāh] morning and afternoon.", TextGerman = "damit ihr an Allah und Seinen Gesandten glaubt, ihm beisteht und ihn hochachtet und (damit ihr) Ihn preist morgens und abends." });
                        surah48.VerseList.Add(new Verse { Number = 10, TextBosnian = "Oni koji su ti se zakleli na vjernost - zakleli su se, doista, na vjernost samom Allahu - Allahova ruka je iznad ruku njihovih! Onaj ko prekrši zakletvu krši je na svoju štetu, a ko ispuni ono na što se obavezao Allahu, On će mu dati veliku nagradu!", TextEnglish = "Indeed, those who pledge allegiance to you, [O Muḥammad] - they are actually pledging allegiance to Allāh. The hand[1508] of Allāh is over their hands.[1509] So he who breaks his word only breaks it to the detriment of himself. And he who fulfills that which he has promised Allāh - He will give him a great reward.", TextGerman = "Gewiß, diejenigen, die dir den Treueid leisten, leisten (in Wirklichkeit) nur Allah den Treueid; Allahs Hand ist über ihren Händen. Wer nun (sein Wort) bricht, bricht es nur zu seinem eigenen Nachteil; wer aber das einhält, wozu er sich Allah gegenüber verpflichtet hat, dem wird Er großartigen Lohn geben." });
                        surah48.VerseList.Add(new Verse { Number = 11, TextBosnian = "Govorit će ti beduini koji su izostali: 'Zadržali su nas imeci i porodice naše, pa zamoli za nas oprost.“ Oni govore jezicima svojim ono što nije u srcima njihovim. Reci: 'Pa ko vam može išta protiv Allaha pomoći ako vam On hoće nauditi, ili ako vam hoće kakvo dobro učiniti' Naprotiv! Allah u potpunosti zna ono što radite.", TextEnglish = "Those who remained behind[1510] of the bedouins will say to you, 'Our properties and our families occupied us, so ask forgiveness for us.' They say with their tongues what is not within their hearts. Say, 'Then who could prevent Allāh at all if He intended for you harm or intended for you benefit? Rather, ever is Allāh, of what you do, Aware.", TextGerman = "Die Zurückgelassenen der Wüstenaraber werden zu dir sagen: Unser Besitz und unsere Angehörigen haben uns (zu sehr) beschäftigt; so bitte für uns um Vergebung. Sie sagen mit ihren Zungen, was nicht in ihren Herzen ist. Sag: Wer vermag denn für euch bei Allah etwas (zu ändern), wenn Er für euch Schaden will oder wenn Er für euch Nutzen will? Aber nein! Allah ist dessen, was ihr tut, Kundig." });
                        surah48.VerseList.Add(new Verse { Number = 12, TextBosnian = "Čak ste mislili da se Poslanik i vjernici nikada neće vratiti porodicama svojim; vaša su srca bila zadovoljna zbog toga i vi ste zlo mislili, vi ste narod propali.", TextEnglish = "But you thought that the Messenger and the believers would never return to their families, ever, and that was made pleasing in your hearts. And you assumed an assumption of evil and became a people ruined.'", TextGerman = "Aber nein! Ihr meintet, daß der Gesandte und die Gläubigen niemals mehr zu ihren Angehörigen zurückkehren würden, und das ist euch in euren Herzen ausgeschmückt worden. Und ihr hegtet die böse Erwartung und wart ein Volk des Niedergangs." });
                        surah48.VerseList.Add(new Verse { Number = 13, TextBosnian = "A ko god ne vjeruje u Allaha i Poslanika Njegova - pa, Mi smo za nevjernike razbuktali Oganj pripremili!", TextEnglish = "And whoever has not believed in Allāh and His Messenger - then indeed, We have prepared for the disbelievers a Blaze.", TextGerman = "Doch wer an Allah und Seinen Gesandten nicht glaubt - gewiß, so haben Wir für die Ungläubigen eine Feuerglut bereitet." });
                        surah48.VerseList.Add(new Verse { Number = 14, TextBosnian = "Allahova je vlast na nebesima i na Zemlji, On prašta kome hoće, a kažnjava koga hoće - Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And to Allāh belongs the dominion of the heavens and the earth. He forgives whom He wills and punishes whom He wills. And ever is Allāh Forgiving and Merciful.", TextGerman = "Und Allah gehört die Herrschaft der Himmel und der Erde. Er vergibt, wem Er will, und Er straft, wen Er will. Und Allah ist Allvergebend und Barmherzig." });
                        surah48.VerseList.Add(new Verse { Number = 15, TextBosnian = "Oni koji su izostali sigurno će reći, kada pođete da plijen uzmete: 'Pustite i nas da vas pratimo!' Oni žele izmijeniti Allahov govor. Reci: 'Vi nas nećete pratiti, to je još prije Allah rekao!', a oni će reći: 'Nije tako, nego, vi nama zavidite.' A nije ni to, već oni malo šta shvataju.", TextEnglish = "Those who remained[1511] behind will say when you set out toward the war booty to take it, 'Let us follow you.' They wish to change the words of Allāh. Say, 'Never will you follow us. Thus did Allāh say before.' So they will say, 'Rather, you envy us.' But [in fact] they were not understanding except a little.[1512]", TextGerman = "Die Zurückgelassenen werden, wenn ihr loszieht, um Beute zu machen, sagen: Laßt uns euch folgen, indem sie Allahs Wort abändern wollen. Sag: Ihr werdet uns nicht folgen. So hat Allah (schon) zuvor gesprochen. Dann werden sie sagen: Nein! Vielmehr beneidet ihr uns. Aber nein! Sie verstehen ja nur wenig." });
                        surah48.VerseList.Add(new Verse { Number = 16, TextBosnian = "Reci beduinima koji su izostali: 'Bit ćete pozvani protiv naroda žestoke snage, da se protiv njih borite, ili će islam primiti. Pa ako poslušate, Allah će vam lijepu nagradu dati, a ako se okrenete, kao što ste se prije okrenuli, kaznit će vas bolnom patnjom.'", TextEnglish = "Say to those who remained behind of the bedouins, 'You will be called to [face] a people of great military might; you may fight them, or they will submit.[1513] So if you obey, Allāh will give you a good reward; but if you turn away as you turned away before, He will punish you with a painful punishment.'", TextGerman = "Sag zu den Zurückgelassenen der Wüstenaraber: Ihr werdet zu Leuten gerufen werden, die eine starke Gewalt besitzen, gegen die ihr kämpfen sollt, es sei denn, sie werden vorher Muslime. Wenn ihr gehorcht, gibt Allah euch schönen Lohn; wenn ihr euch jedoch abkehrt, wie ihr euch zuvor abgekehrt habt, wird Er euch mit schmerzhafter Strafe strafen." });
                        surah48.VerseList.Add(new Verse { Number = 17, TextBosnian = "Nije grijeh slijepom, ni hromom ni bolesnom! Ko god se pokorava Allahu i Poslaniku Njegovu On će ga u džennetske bašče, kroz koje teku rijeke, uvesti, a onoga ko se okrene, patnjom bolnom će kazniti.", TextEnglish = "There is not upon the blind any guilt or upon the lame any guilt or upon the ill any guilt [for remaining behind]. And whoever obeys Allāh and His Messenger - He will admit him to gardens beneath which rivers flow; but whoever turns away - He will punish him with a painful punishment.", TextGerman = "Keinen Grund zur Bedrängnis gibt es für den Blinden, und keinen Grund zur Bedrängnis gibt es für den Hinkenden, und keinen Grund zur Bedrängnis gibt es für den Kranken. Wer Allah und Seinem Gesandten gehorcht, den wird Er in Gärten eingehen lassen, durcheilt von Bächen. Wer sich jedoch abkehrt, den straft Er mit schmerzhafter Strafe." });
                        surah48.VerseList.Add(new Verse { Number = 18, TextBosnian = "Allah je zadovoljan onim vjernicima koji su ti se pod drvetom na vjernost zakleli. On je znao šta je u srcima njihovim, pa je spustio smiraj na njih, i nagradit će ih skorom pobjedom.", TextEnglish = "Certainly was Allāh pleased with the believers when they pledged allegiance to you, [O Muḥammad], under the tree, and He knew what was in their hearts, so He sent down tranquility upon them and rewarded them with an imminent conquest[1514]", TextGerman = "Allah hatte ja Wohlgefallen an den Gläubigen, als sie dir unter dem Baum den Treueid leisteten. Er wußte, was in ihren Herzen war, und da sandte Er die innere Ruhe auf sie herab und belohnte sie mit einem nahen Sieg" });
                        surah48.VerseList.Add(new Verse { Number = 19, TextBosnian = "I obilnijim plijenom koji će uzeti, a Allah je silan i mudar.", TextEnglish = "And much war booty which they will take. And ever is Allāh Exalted in Might and Wise.", TextGerman = "und viel Beute, die sie machen werden. Und Allah ist Allmächtig und Allweise." });
                        surah48.VerseList.Add(new Verse { Number = 20, TextBosnian = "Allah vam obećava obilni plijen koji ćete uzeti, a s ovim je požurio i ruke ljudi od vas je zadržao - da bi to bio znak za vjernike i da bi vas na Pravi put uputio", TextEnglish = "Allāh has promised you much booty that you will take [in the future] and has hastened for you this [victory] and withheld the hands of people from you - that it may be a sign for the believers and [that] He may guide you to a straight path.", TextGerman = "Allah hat euch versprochen, daß ihr viel Beute machen werdet. So hat Er euch diese schnell gewährt und die Hände der Menschen von euch zurückgehalten, und (dies,) damit es ein Zeichen für die Gläubigen sei und Er euch einen geraden Weg leite." });
                        surah48.VerseList.Add(new Verse { Number = 21, TextBosnian = "i drugi, koji niste bili u stanju uzeti - Allah ga je već obujmio, a Allah nad svime ima moć.", TextEnglish = "And [He promises] other [victories] that you were [so far] unable to [realize] which Allāh has already encompassed.[1515] And ever is Allāh, over all things, competent.", TextGerman = "Auch eine andere (Beute), die ihr (noch) nicht zu erlangen vermochtet, hat Allah bereits umfangen. Allah hat zu allem die Macht." });
                        surah48.VerseList.Add(new Verse { Number = 22, TextBosnian = "A da su nevjernici s vama borbu zametnuli, sigurno bi se u bijeg dali i poslije ne bi ni zaštitnika ni pomagača našli.", TextEnglish = "And if those [Makkans] who disbelieve had fought you, they would have turned their backs [in flight]. Then they would not find a protector or a helper.", TextGerman = "Und wenn diejenigen, die ungläubig sind, gegen euch gekämpft hätten, hätten sie (euch) wahrlich den Rücken gekehrt. Hierauf finden sie weder Schutzherrn noch Helfer." });
                        surah48.VerseList.Add(new Verse { Number = 23, TextBosnian = "To je prema Allahovu zakonu koji oduvijek važi, a ti nećeš vidjeti da se Allahov zakon promijeni.", TextEnglish = "[This is] the established way of Allāh which has occurred before. And never will you find in the way of Allāh any change.", TextGerman = "(So war) Allahs Gesetzmäßigkeit, die bereits zuvor ergangen war. Und du wirst in Allahs Gesetzmäßigkeit keine Änderung finden." });
                        surah48.VerseList.Add(new Verse { Number = 24, TextBosnian = "On je zadržao ruke njihove od vas i vaše od njih usred Mekke, i to nakon što vam je pružio mogućnost da ih pobijedite- a Allah dobro vidi ono što vi radite.", TextEnglish = "And it is He who withheld their hands from you and your hands from them within [the area of] Makkah after He caused you to overcome them. And ever is Allāh, of what you do, Seeing.", TextGerman = "Und Er ist es, Der im Talgrund von Makka ihre Hände von euch und eure Hände von ihnen zurückgehalten hat, nachdem Er euch den Sieg über sie verliehen hatte. Und Allah sieht, was ihr tut, wohl." });
                        surah48.VerseList.Add(new Verse { Number = 25, TextBosnian = "To su oni koji ne vjeruju i brane vam da pristupite Časnoj džamiji, i da kurbani koje vodite sa sobom do mjesta svojih stignu. I da nije bilo muškaraca vjernika i žena vjernica, koje vi ne poznajete, pa da ih iz neznanja pobili ne biste, te bi vas zbog njih neugodnost zadesila - Mi bismo vam ih prepustili. A nismo ih prepustili zato da bi Allah u milost Svoju uveo onoga koga hoće. A da su oni bili odvojeni, doista bismo bolnom patnjom kaznili one među njima koji nisu vjerovali.", TextEnglish = "They are the ones who disbelieved and obstructed you from al-Masjid al-Ḥarām while the offering[1516] was prevented from reaching its place of sacrifice. And if not for believing men and believing women whom you did not know - that you might trample [i.e., kill] them and there would befall you because of them dishonor without [your] knowledge - [you would have been permitted to enter Makkah]. [This was so] that Allāh might admit to His mercy whom He willed. If they had been apart [from them], We would have punished those who disbelieved among them with painful punishment", TextGerman = "Sie sind es, die ungläubig gewesen sind und euch von der geschützten Gebetsstätte abgehalten und die Opfertiere aufgehalten haben, daß sie (nicht) ihren Schlachtort erreichen. Und wenn es (dort) nicht gläubige Männer und gläubige Frauen gegeben hätte, die ihr nicht kanntet, und damit ihr sie nicht niedertretet und damit euch nicht dadurch Schande ohne Wissen trifft, (haben Wir ... Dies,) damit Allah in Seine Barmherzigkeit eingehen läßt, wen Er will. Wenn diese sich (von den Ungläubigen) deutlich getrennt hätten, hätten Wir diejenigen von ihnen, die ungläubig sind, wahrlich mit schmerzhafter Strafe gestraft." });
                        surah48.VerseList.Add(new Verse { Number = 26, TextBosnian = "Kad su nevjernici srca svoja punili žarom, žarom paganskim, Allah je spustio smiraj Svoj na Poslanika Svoga i na vjernike i obavezao ih na Riječ bogobojaznosti - a oni bijahu najdostojniji nje i bijahu nje vrijedni - a Allah sve zna.", TextEnglish = "When those who disbelieved had put into their hearts chauvinism - the chauvinism of the time of ignorance. But Allāh sent down His tranquility upon His Messenger and upon the believers and imposed upon them the word of righteousness, and they were more deserving of it and worthy of it. And ever is Allāh, of all things, Knowing.", TextGerman = "Als diejenigen, die ungläubig waren, in ihren Herzen die Hitzigkeit entfachten, die Hitzigkeit der Unwissenheit -, da sandte Allah Seine innere Ruhe auf Seinen Gesandten und auf die Gläubigen herab und ließ sie an dem Wort der Gottesfurcht festhalten. Sie hatten ja ein größeres Anrecht darauf und waren dessen würdig. Und Allah weiß über alles Bescheid." });
                        surah48.VerseList.Add(new Verse { Number = 27, TextBosnian = "Allah će obistiniti san Poslanika Svoga da ćete doista u Časnu džamiju ući sigurni - ako Allah bude htio - neki obrijanih glava, a neki podrezanih kosa, bez straha. On je ono što vi niste znali znao i zato vam je, prije toga, nedavno pobjedu dao.", TextEnglish = "Certainly has Allāh showed to His Messenger the vision [i.e., dream] in truth. You will surely enter al-Masjid al-Ḥarām, if Allāh wills, in safety, with your heads shaved and [hair] shortened,[1517] not fearing [anyone]. He knew what you did not know and has arranged before that a conquest near [at hand].", TextGerman = "Allah hat ja Seinem Gesandten das Traumgesicht der Wahrheit entsprechend wahr gemacht: Ihr werdet ganz gewiß, wenn Allah will, die geschützte Gebetsstätte in Sicherheit betreten, sowohl mit geschorenem Kopf als auch (mit) gekürzt(em Haar), und ohne euch zu fürchten. Er wußte doch, was ihr nicht wußtet, und so bestimmte Er (für euch) vorher einen nahen Sieg." });
                        surah48.VerseList.Add(new Verse { Number = 28, TextBosnian = "On je poslao Poslanika Svoga s Uputom i vjerom Istine da bi je uzdigao iznad svih vjera. A Allah je dovoljan Svjedok!", TextEnglish = "It is He who sent His Messenger with guidance and the religion of truth to manifest it over all religion. And sufficient is Allāh as Witness.", TextGerman = "Er ist es, Der Seinen Gesandten mit der Rechtleitung und der Religion der Wahrheit gesandt hat, um ihr die Oberhand über alle Religion zu geben. Und Allah genügt als Zeuge." });
                        surah48.VerseList.Add(new Verse { Number = 29, TextBosnian = "Muhammed je Allahov poslanik, a njegovi su sljedbenici strogi prema nevjernicima a samilosni među sobom; vidiš ih kako ruku' i sedždu čine, želeći od Allaha obilje i zadovoljstvo - na licima su im znaci, tragovi od padanja na sedždu. Tako su opisani u Tevratu. A u Indžilu: oni su kao biljka kad izdanak svoj izbaci pa ga onda učvrsti, i on ojača, i uspravi se na svojoj stabljici izazivajući divljenje sijača - da bi On s vjernicima najedio nevjernike. Onima koji vjeruju i dobra djela čine Allah obećava oprost i nagradu veliku.", TextEnglish = "Muḥammad is the Messenger of Allāh; and those with him are forceful against the disbelievers, merciful among themselves. You see them bowing and prostrating [in prayer], seeking bounty from Allāh and [His] pleasure. Their sign is in their faces from the effect of prostration [i.e., prayer]. That is their description in the Torah. And their description in the Gospel is as a plant which produces its offshoots and strengthens them so they grow firm and stand upon their stalks, delighting the sowers - so that He [i.e., Allāh] may enrage by them[1518] the disbelievers. Allāh has promised those who believe and do righteous deeds among them forgiveness and a great reward.", TextGerman = "Muhammad ist Allahs Gesandter. Und diejenigen, die mit ihm sind, sind den Ungläubigen gegenüber hart, zueinander aber barmherzig. Du siehst sie sich verbeugen und niederwerfen, indem sie nach Huld von Allah und Wohlgefallen trachten. Ihr Merkmal steht auf ihren Gesichtern durch die Niederwerfung. Das ist ihr Gleichnis in der Tora. Und ihr Gleichnis im Evangelium ist das eines Getreidefeldes, das seine Triebe hervorbringt und dann stärker werden läßt, so daß sie verdicken und ebenmäßig auf ihren Halmen stehen, so daß es den Anbauern gefällt. (Dies,) damit Er die Ungläubigen durch sie ergrimmen lasse. Allah hat denjenigen von ihnen, die glauben und rechtschaffene Werke tun, Vergebung und großartigen Lohn versprochen." });
                        result = surah48;
                        break;
                    #endregion

                    // 
                    #region
                    case 49:
                        Surah surah49 = new Surah(); surah49.Name = Constants.Surah49Name; surah49.Number = 49; surah49.VerseList = new List<Verse>();
                        surah49.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah49.VerseList.Add(new Verse { Number = 1, TextBosnian = "O vi koji vjerujete, ne istupajte ispred Allaha i Poslanika Njegova, i bojte se Allaha! Allah je, zaista, Onaj Koji sve čuje i Onaj Koji sve zna.", TextEnglish = "O you who have believed, do not put [yourselves] before Allāh and His Messenger[1520] but fear Allāh. Indeed, Allāh is Hearing and Knowing.", TextGerman = "O die ihr glaubt, kommt nicht Allah und Seinem Gesandten zuvor und fürchtet Allah. Gewiß, Allah ist Allhörend und Allwissend." });
                        surah49.VerseList.Add(new Verse { Number = 2, TextBosnian = "O vi koji vjerujete, ne dižite glasove svoje iznad Vjerovjesnikova glasa i ne razgovarajte s njim glasno, kao što glasno jedan s drugim razgovarate, da ne bi bila poništena vaša djela, a da vi i ne osjetite.", TextEnglish = "O you who have believed, do not raise your voices above the voice of the Prophet or be loud to him in speech like the loudness of some of you to others, lest your deeds become worthless while you perceive not.", TextGerman = "O die ihr glaubt, erhebt nicht eure Stimmen über die Stimme des Propheten, und sprecht nicht so laut zu ihm, wie ihr laut zueinander sprecht, auf daß (nicht) eure Werke hinfällig werden, ohne daß ihr merkt." });
                        surah49.VerseList.Add(new Verse { Number = 3, TextBosnian = "Doista onima koji utišaju glasove svoje pred Allahovim Poslanikom, a to su oni čija je srca Allah iskušao u bogobojaznosti - pripada oprost i nagrada velika.", TextEnglish = "Indeed, those who lower their voices before the Messenger of Allāh - they are the ones whose hearts Allāh has tested for righteousness. For them is forgiveness and great reward.", TextGerman = "Gewiß, diejenigen, die ihre Stimmen bei Allahs Gesandtem mäßigen, das sind diejenigen, deren Herzen Allah auf die Gottesfurcht geprüft hat. Für sie wird es Vergebung und großartigen Lohn geben." });
                        surah49.VerseList.Add(new Verse { Number = 4, TextBosnian = "Zbilja, oni koji te dozivaju izvan soba - većina njih ne razumije.", TextEnglish = "Indeed, those who call you, [O Muḥammad], from behind the chambers - most of them do not use reason.", TextGerman = "Gewiß, diejenigen, die dich hinter den (Wänden der) Gemächer rufen, die meisten von ihnen haben keinen Verstand." });
                        surah49.VerseList.Add(new Verse { Number = 5, TextBosnian = "A da su se oni strpjeli dok ti sam među njih iziđeš, bilo bi im bolje; a Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "And if they had been patient until you [could] come out to them, it would have been better for them. But Allāh is Forgiving and Merciful.", TextGerman = "Wenn sie sich gedulden würden, bis du zu ihnen herauskommst, wäre es wahrlich besser für sie. Und Allah ist Allvergebend und Barmherzig." });
                        surah49.VerseList.Add(new Verse { Number = 6, TextBosnian = "O vi koji vjerujete, ako vam nekakav buntovnik donese kakvu vijest, dobro je provjerite, da u neznanju nekome zlo ne učinite, pa da se zbog onog što ste učinili pokajete.", TextEnglish = "O you who have believed, if there comes to you a disobedient one with information, investigate, lest you harm a people out of ignorance and become, over what you have done, regretful.", TextGerman = "O die ihr glaubt, wenn ein Frevler zu euch mit einer Kunde kommt, dann schafft Klarheit, damit ihr (nicht einige) Leute in Unwissenheit (mit einer Anschuldigung) trefft und dann über das, was ihr getan habt, Reue empfinden werdet." });
                        surah49.VerseList.Add(new Verse { Number = 7, TextBosnian = "I znajte da je među vama Allahov Poslanik; kad bi vas on u mnogo čemu poslušao, doista biste nastradali, ali vam je Allah vjerovanje omilio, i u srcima vašim ga lijepim prikazao, a nevjerovanje i razvrat i neposlušnost vam omrazio - oni su baš na ispravnom putu,", TextEnglish = "And know that among you is the Messenger of Allāh. If he were to obey you in much of the matter, you would be in difficulty, but Allāh has endeared to you the faith and has made it pleasing in your hearts and has made hateful to you disbelief, defiance and disobedience. Those are the [rightly] guided.", TextGerman = "Und wißt, daß Allahs Gesandter unter euch ist. Wenn er euch in vielen Angelegenheiten gehorchte, würdet ihr wahrlich in Bedrängnis kommen. Aber Allah hat euch den Glauben lieb gemacht und in euren Herzen ausgeschmückt, und Er hat euch den Unglauben, den Frevel und den Ungehorsam verabscheuen lassen. Das sind diejenigen, die besonnen handeln." });
                        surah49.VerseList.Add(new Verse { Number = 8, TextBosnian = "zbog dobrote i blagodati Allahove; Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "[It is] as bounty from Allāh and favor. And Allāh is Knowing and Wise.", TextGerman = "Dies als Huld und Gunst von Allah. Und Allah ist Allwissend und Allweise." });
                        surah49.VerseList.Add(new Verse { Number = 9, TextBosnian = "Ako se dvije skupine vjernika sukobe, izmirite ih; a ako jedna od njih, ipak, učini nasilje drugoj, onda se borite protiv one koja je učinila nasilje sve dok se Allahovim propisima ne prikloni, onda ih po pravdi izmirite i budite pravični; Allah, zaista, voli pravične.", TextEnglish = "And if two factions among the believers should fight, then make settlement between the two. But if one of them oppresses the other, then fight against the one that oppresses until it returns to the ordinance of Allāh. And if it returns, then make settlement between them in justice and act justly. Indeed, Allāh loves those who act justly.", TextGerman = "Und wenn zwei Gruppen von den Gläubigen miteinander kämpfen, so stiftet Frieden zwischen ihnen. Wenn die eine von ihnen gegen die andere widerrechtlich vorgeht, dann kämpft gegen diejenige, die widerrechtlich vorgeht, bis sie zu Allahs Befehl zurückkehrt. Wenn sie zurückkehrt, dann stiftet Frieden zwischen ihnen nach Gerechtigkeit und handelt dabei gerecht. Allah liebt ja die Gerechten." });
                        surah49.VerseList.Add(new Verse { Number = 10, TextBosnian = "Vjernici su samo braća, zato pomirite svoja dva brata i bojte se Allaha da bi vam milost bila darovana.", TextEnglish = "The believers are but brothers, so make settlement between your brothers. And fear Allāh that you may receive mercy.", TextGerman = "Die Gläubigen sind doch Brüder. So stiftet Frieden zwischen euren beiden Brüdern und fürchtet Allah, auf daß ihr Erbarmen finden möget." });
                        surah49.VerseList.Add(new Verse { Number = 11, TextBosnian = "O vi koji vjerujete, neka se muškarci jedni drugima ne rugaju, možda su oni bolji od njih, a ni žene drugim ženama, možda su one bolje od njih. I ne kudite jedni druge i ne zovite jedni druge ružnim nadimcima! Ružno je nazivati nekoga imenom koje znači neposluh Allahu, nakon vjerovanja! A oni koji se ne pokaju - oni su baš zulumćari.", TextEnglish = "O you who have believed, let not a people ridicule [another] people; perhaps they may be better than them; nor let women ridicule [other] women; perhaps they may be better than them. And do not insult one another and do not call each other by [offensive] nicknames. Wretched is the name [i.e., mention] of disobedience after [one's] faith. And whoever does not repent - then it is those who are the wrongdoers.", TextGerman = "O die ihr glaubt, die einen sollen nicht über die anderen spotten, vielleicht sind eben diese besser als sie. Auch sollen nicht Frauen über andere Frauen (spotten), vielleicht sind eben diese besser als sie. Und beleidigt euch nicht gegenseitig durch Gesten und bewerft euch nicht gegenseitig mit (häßlichen) Beinamen. Wie schlimm ist die Bezeichnung Frevel nach (der Bezeichnung) Glaube! Und wer nicht bereut, das sind die Ungerechten." });
                        surah49.VerseList.Add(new Verse { Number = 12, TextBosnian = "O vi koji vjerujete, klonite se mnogih sumnjičenja, neka su sumnjičenja, zaista, grijeh. I ne uhodite jedni druge i ne ogovarajte jedni druge! Zar bi nekom od vas bilo drago da jede meso umrlog brata svoga - a vama je to odvratno - zato se bojte Allaha; Allah je, zaista, Onaj Koji stalno prima pokajanja i milostiv je.", TextEnglish = "O you who have believed, avoid much [negative] assumption. Indeed, some assumption is sin. And do not spy or backbite each other. Would one of you like to eat the flesh of his brother when dead? You would detest it. And fear Allāh; indeed, Allāh is Accepting of Repentance and Merciful.", TextGerman = "O die ihr glaubt, meidet viel von den Mutmaßungen; gewiß, manche Mutmaßung ist Sünde. Und sucht nicht (andere) auszukundschaften und führt nicht üble Nachrede übereinander. Möchte denn einer von euch gern das Fleisch seines Bruders, wenn er tot sei, essen? Es wäre euch doch zuwider. Fürchtet Allah. Gewiß, Allah ist Reue-Annehmend und Barmherzig." });
                        surah49.VerseList.Add(new Verse { Number = 13, TextBosnian = "O ljudi, Mi smo vas od muškarca i žene stvorili i plemenima i narodima vas učinili da biste se prepoznavali. Najugledniji od vas kod Allaha je onaj koji Ga se najviše boji; Allah je, uistinu, Onaj Koji sve i u potpunosti zna.", TextEnglish = "O mankind, indeed We have created you from male and female and made you peoples and tribes that you may know one another. Indeed, the most noble of you in the sight of Allāh is the most righteous[1521] of you. Indeed, Allāh is Knowing and Aware.", TextGerman = "O ihr Menschen, Wir haben euch ja von einem männlichen und einem weiblichen Wesen erschaffen, und Wir haben euch zu Völkern und Stämmen gemacht, damit ihr einander kennenlernt. Gewiß, der Geehrteste von euch bei Allah ist der Gottesfürchtigste von euch. Gewiß, Allah ist Allwissend und Allkundig." });
                        surah49.VerseList.Add(new Verse { Number = 14, TextBosnian = "Neki beduini govore: 'Mi vjerujemo!' Reci: 'Vi ne vjerujete, ali recite: 'Islam smo primili!' - a iman još nije ušao u vaša srca.' A ako Allahu i Njegovom Poslaniku budete pokorni, On vam nimalo neće umanjiti nagradu za djela vaša. Allah je, uistinu, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "The bedouins say, 'We have believed.' Say, 'You have not [yet] believed; but say [instead], 'We have submitted,' for faith has not yet entered your hearts. And if you obey Allāh and His Messenger, He will not deprive you from your deeds[1522] of anything. Indeed, Allāh is Forgiving and Merciful.'", TextGerman = "Die Wüstenaraber sagen: Wir glauben. Sag: Ihr glaubt nicht (wirklich), sondern sagt: ,Wir sind Muslime geworden’, denn der Glaube ist noch nicht in eure Herzen eingezogen. Wenn ihr aber Allah und Seinem Gesandten gehorcht, verringert Er euch nichts von euren Werken. Gewiß, Allah ist Allvergebend und Barmherzig." });
                        surah49.VerseList.Add(new Verse { Number = 15, TextBosnian = "Pravi su vjernici samo oni koji u Allaha i Poslanika Njegova vjeruju i poslije više ne sumnjaju i bore se na Allahovu putu imecima svojim i životima svojim. Oni su baš iskreni.", TextEnglish = "The believers are only the ones who have believed in Allāh and His Messenger and then doubt not but strive with their properties and their lives in the cause of Allāh. It is those who are the truthful.", TextGerman = "Die (wahren) Gläubigen sind ja diejenigen, die an Allah und Seinen Gesandten glauben und hierauf nicht zweifeln und sich mit ihrem Besitz und mit ihrer eigenen Person auf Allahs Weg abmühen. Das sind die Wahrhaftigen." });
                        surah49.VerseList.Add(new Verse { Number = 16, TextBosnian = "Reci: 'Zar vi da obavještavate Allaha o vjerovanju svome, kad Allah zna sve što je na nebesima i na Zemlji! Allah sve zna.'", TextEnglish = "Say, 'Would you acquaint Allāh with your religion while Allāh knows whatever is in the heavens and whatever is on the earth, and Allāh is Knowing of all things?'", TextGerman = "Sag: Wollt ihr Allah über eure Religion belehren, wo Allah weiß, was in den Himmeln und was auf der Erde ist? Und Allah weiß über alles Bescheid." });
                        surah49.VerseList.Add(new Verse { Number = 17, TextBosnian = "Oni ti prebacuju što su primili islam. Reci: 'Ne prebacujte mi što ste islam primili; naprotiv, Allah je vama milost podario time što vas je u pravu vjeru uputio, ako iskreno govorite.'", TextEnglish = "They consider it a favor to you that they have accepted Islām. Say, 'Do not consider your Islām a favor to me. Rather, Allāh has conferred favor upon you that He has guided you to the faith, if you should be truthful.'", TextGerman = "Sie halten es dir als Wohltat vor, daß sie Muslime geworden sind. Sag: Haltet mir nicht eure (Annahme des) Islams als Wohltat vor. Nein! Vielmehr hält Allah euch die Wohltat vor, daß Er euch zum Glauben geleitet hat, wenn ihr wahrhaftig seid." });
                        surah49.VerseList.Add(new Verse { Number = 18, TextBosnian = "Allah sigurno zna tajne nebesa i Zemlje i Allah dobro vidi ono što radite.", TextEnglish = "Indeed, Allāh knows the unseen [aspects] of the heavens and the earth. And Allāh is Seeing of what you do.", TextGerman = "Gewiß, Allah kennt das Verborgene der Himmel und der Erde. Und Allah sieht wohl, was ihr tut." });
                        result = surah49;
                        break;
                    #endregion

                    // 
                    #region
                    case 50:
                        Surah surah50 = new Surah(); surah50.Name = Constants.Surah50Name; surah50.Number = 50; surah50.VerseList = new List<Verse>();
                        surah50.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah50.VerseList.Add(new Verse { Number = 1, TextBosnian = "Kaf, tako Mi Kur'ana veličanstvenog!", TextEnglish = "Qāf.[1524] By the honored Qur’ān...[1525]", TextGerman = "Qaf. Bei dem ruhmvollen Qur’an!" });
                        surah50.VerseList.Add(new Verse { Number = 2, TextBosnian = "Oni se još i čude što im je kao upozoritelj došao jedan od njih, pa nevjernici govore: 'To je nešto čudno!", TextEnglish = "But they wonder that there has come to them a warner from among themselves, and the disbelievers say, 'This is an amazing thing.", TextGerman = "Aber nein! Sie wundern sich darüber, daß ein Überbringer von Warnungen von ihnen selbst zu ihnen gekommen ist. Da sagen die Ungläubigen: Das ist eine verwunderliche Sache." });
                        surah50.VerseList.Add(new Verse { Number = 3, TextBosnian = "Zar kad pomremo i zemlja postanemo?! Nezamisliv je to povratak.'", TextEnglish = "When we have died and have become dust, [we will return to life]? That is a distant [i.e., unlikely] return.'", TextGerman = "Wenn wir gestorben und zu Erde geworden sind ... (, sollten wir dann wirklich wieder auferstehen)? Das ist doch eine weitentfernte Rückkehr." });
                        surah50.VerseList.Add(new Verse { Number = 4, TextBosnian = "Mi znamo šta će od njih zemlja uništiti, u Nas je Knjiga koja sve čuva.", TextEnglish = "We know what the earth diminishes [i.e., consumes] of them, and with Us is a retaining record.", TextGerman = "Wir wissen bestimmt, was die Erde von ihnen verringert. Und bei Uns ist ein Buch, das alles bewahrt." });
                        surah50.VerseList.Add(new Verse { Number = 5, TextBosnian = "Oni, međutim, poriču Istinu koja im dolazi i oni su smeteni.", TextEnglish = "But they denied the truth when it came to them, so they are in a confused condition.", TextGerman = "Aber nein! Sie haben die Wahrheit für Lüge erklärt, als sie zu ihnen gekommen ist, und so befinden sie sich (stets) in Unbeständigkeit." });
                        surah50.VerseList.Add(new Verse { Number = 6, TextBosnian = "A zašto ne pogledaju nebo iznad sebe? - kako smo ga sazdali i ukrasili i kako u njemu nema pukotina.", TextEnglish = "Have they not looked at the heaven above them - how We structured it and adorned it and [how] it has no rifts?", TextGerman = "Schauen sie denn nicht zum Himmel über ihnen, wie Wir ihn aufgebaut und geschmückt haben, und daß er keine Spalten hat?" });
                        surah50.VerseList.Add(new Verse { Number = 7, TextBosnian = "A Zemlju smo rasprostrli i po njoj nepomična brda pobacali i dali da iz nje niče raznovrsno, prekrasno bilje.", TextEnglish = "And the earth - We spread it out and cast therein firmly set mountains and made grow therein [something] of every beautiful kind,", TextGerman = "Und die Erde haben Wir gedehnt und darauf festgegründete Berge gesetzt, und Wir haben auf ihr von jeder entzückenden (Pflanzen)art wachsen lassen," });
                        surah50.VerseList.Add(new Verse { Number = 8, TextBosnian = "To je zbog toga da bi uvidio i opomenut bio svaki rob koji se Gospodaru svome pokajnički obraća i pokorava.", TextEnglish = "Giving insight and a reminder for every servant who turns [to Allāh].", TextGerman = "zur Einsicht und Ermahnung für jeden reuigen Diener." });
                        surah50.VerseList.Add(new Verse { Number = 9, TextBosnian = "Mi s neba spuštamo blagoslovljenu vodu i činimo da uz pomoć njenu niču vrtovi i žito koje se žanje,", TextEnglish = "And We have sent down blessed rain from the sky and made grow thereby gardens and grain from the harvest", TextGerman = "Und Wir lassen vom Himmel gesegnetes Wasser herabkommen, womit Wir dann Gärten und Korn, das man erntet, wachsen lassen," });
                        surah50.VerseList.Add(new Verse { Number = 10, TextBosnian = "i visoke palme u kojih su zameci nagomilani jedni iznad drugih,", TextEnglish = "And lofty palm trees having fruit arranged in layers -", TextGerman = "und Palmen, hochreichend und mit übereinander gereihten Blütenkolben," });
                        surah50.VerseList.Add(new Verse { Number = 11, TextBosnian = "kao opskrbu robovima svim, a Mi njome i mrtvi predjele oživljavamo; tako će i proživljenje biti.", TextEnglish = "As provision for the servants, and We have given life thereby to a dead land. Thus is the emergence [i.e., resurrection].", TextGerman = "als Versorgung für die Diener. Und Wir machen damit (manch) totes Land wieder lebendig; so wird auch das Herauskommen (erfolgen)." });
                        surah50.VerseList.Add(new Verse { Number = 12, TextBosnian = "Prije njih poricao je narod Nuhov i stanovnici Ressa i Semud.", TextEnglish = "The people of Noah denied before them,[1526] and the companions of the well[1527] and Thamūd", TextGerman = "Der Lüge bezichtigten vor ihnen das Volk Nuhs und die Leute von ar-Rass und die Tamud" });
                        surah50.VerseList.Add(new Verse { Number = 13, TextBosnian = "I Ad i narod faraonov i narod Lutov,", TextEnglish = "And ʿAad and Pharaoh and the brothers [i.e., people] of Lot", TextGerman = "und die ’Ad und Fir’aun und die Brüder Luts" });
                        surah50.VerseList.Add(new Verse { Number = 14, TextBosnian = "I stanovnici Ejke i narod Tubbe'a; svi su oni poslanike lažnim smatrali, pa se Moja prijetnja obistinila.", TextEnglish = "And the companions of the thicket and the people of Tubbaʿ. All denied the messengers, so My threat was justly fulfilled.", TextGerman = "und die Bewohner des Dickichts und das Volk Tubba’s. Alle bezichtigten die Gesandten der Lüge, so ist Meine Androhung unvermeidlich fällig geworden." });
                        surah50.VerseList.Add(new Verse { Number = 15, TextBosnian = "Pa zar smo nakon stvaranja prvog nemoćni postali?! Ne, ali oni u ponovno stvaranje sumnjaju.", TextEnglish = "Did We fail in the first creation? But they are in confusion over a new creation.", TextGerman = "Ermüdeten Wir denn durch die erste Erschaffung? Nein! Vielmehr ist ihnen verdeckt (geblieben), was eine neue Erschaffung betrifft." });
                        surah50.VerseList.Add(new Verse { Number = 16, TextBosnian = "Mi smo stvorili čovjeka i znamo što mu duša njegova šapće, i Mi smo njemu bliži od vratne žile kucavice.", TextEnglish = "And We have already created man and know what his soul whispers to him, and We are closer[1528] to him than [his] jugular vein.", TextGerman = "Wir haben ja den Menschen erschaffen und wissen, was (alles ihm) seine Seele einflüstert, und Wir sind ihm doch näher als seine Halsschlagader," });
                        surah50.VerseList.Add(new Verse { Number = 17, TextBosnian = "Kad dvojica prihvatitelja prihvataju: jedan zdesna, a jedan slijeva sjedi.", TextEnglish = "When the two receivers [i.e., recording angels] receive,[1529] seated on the right and on the left.", TextGerman = "wo die beiden Empfänger (der Taten) empfangen, zur Rechten und zur Linken sitzend." });
                        surah50.VerseList.Add(new Verse { Number = 18, TextBosnian = "On ne izusti nijednu riječ, a da pored njega nema nadziratelj spremni.", TextEnglish = "He [i.e., man] utters no word except that with him is an observer prepared [to record].", TextGerman = "Kein Wort äußert er, ohne daß bei ihm ein Beobachter bereit wäre." });
                        surah50.VerseList.Add(new Verse { Number = 19, TextBosnian = "Smrtne muke će zbilja doći - to je ono od čega si bježao,", TextEnglish = "And the intoxication of death will bring the truth; that is what you were trying to avoid.", TextGerman = "Und es wird die Trunkenheit des Todes mit der Wahrheit kommen: Das ist das, wovor du auszuweichen pflegtest." });
                        surah50.VerseList.Add(new Verse { Number = 20, TextBosnian = "i u rog će biti puhnuto - to je Dan prijetnje.", TextEnglish = "And the Horn will be blown. That is the Day of [carrying out] the threat.", TextGerman = "Und es wird ins Horn geblasen; das ist der angedrohte Tag." });
                        surah50.VerseList.Add(new Verse { Number = 21, TextBosnian = "I svako će doći, a s njim vodič i svjedok.", TextEnglish = "And every soul will come, with it a driver and a witness.[1530]", TextGerman = "Und jede Seele wird kommen und mit ihr ein Treiber und ein Zeuge." });
                        surah50.VerseList.Add(new Verse { Number = 22, TextBosnian = "Ti nisi mario za ovo pa smo skinuli koprenu tvoju, danas ti je oštar vid.", TextEnglish = "[It will be said], 'You were certainly in unmindfulness of this, and We have removed from you your cover,[1531] so your sight, this Day, is sharp.'", TextGerman = "Du warst dessen ja unachtsam. Nun haben Wir deine Decke von dir hinweggenommen, so daß dein Blick heute scharf ist." });
                        surah50.VerseList.Add(new Verse { Number = 23, TextBosnian = "A pratilac njegov će reći: 'Ovo što je kod mene, spremno je.'", TextEnglish = "And his companion, [the angel], will say, 'This [record] is what is with me, prepared.'", TextGerman = "Und sein Geselle wird sagen: Das ist, was bei mir bereit ist." });
                        surah50.VerseList.Add(new Verse { Number = 24, TextBosnian = "Bacite vas dvojica u Džehennem svakog poricatelja, prkosnika", TextEnglish = "[Allāh will say], 'Throw into Hell every obstinate disbeliever,", TextGerman = "- Werft, ihr beide, in die Hölle jeden beharrlichen, widerspenstigen Ungläubigen," });
                        surah50.VerseList.Add(new Verse { Number = 25, TextBosnian = "koji je branio da se čine dobra djela i koji je prelazio granicu i sumnjičav bio.", TextEnglish = "Preventer of good, aggressor, and doubter,", TextGerman = "Verweigerer des Guten, der Übertretungen begeht und sich in starkem Zweifel befindet," });
                        surah50.VerseList.Add(new Verse { Number = 26, TextBosnian = "Koji je, pored Allaha, drugog boga prihvatio, zato ga bacite u patnju žestoku.", TextEnglish = "Who made [as equal] with Allāh another deity; then throw him into the severe punishment.'", TextGerman = "der neben Allah einen anderen Gott setzt. So werft ihn in die strenge Strafe." });
                        surah50.VerseList.Add(new Verse { Number = 27, TextBosnian = "A drugi pratilac njegov će reći: 'Gospodaru naš, ja ga nisam silom zvao, sam je u velikoj zabludi bio.'", TextEnglish = "His [devil] companion will say, 'Our Lord, I did not make him transgress, but he [himself] was in extreme error.'", TextGerman = "Sein Geselle wird sagen: Unser Herr, ich habe ihn nicht zu einem Übermaß (an Frevel) verleitet, sondern er (selbst) befand sich in tiefem Irrtum." });
                        surah50.VerseList.Add(new Verse { Number = 28, TextBosnian = "Ne prepirite se preda Mnom', reći će On, 'još davno sam vam zaprijetio.", TextEnglish = "[Allāh] will say, 'Do not dispute before Me, while I had already presented to you the threat [i.e., warning].", TextGerman = "Er (Allah) wird sagen: Streitet nicht miteinander vor Mir, wo Ich euch doch die Androhung vorausgeschickt habe." });
                        surah50.VerseList.Add(new Verse { Number = 29, TextBosnian = "Moja riječ se ne mijenja i Ja nisam prema robovima Svojim nepravedan.'", TextEnglish = "The word [i.e., decree] will not be changed with Me, and never will I be unjust to the servants.'", TextGerman = "Das Wort wird bei Mir nicht abgeändert, und Ich bin keiner, der den Dienern Unrecht zufügt." });
                        surah50.VerseList.Add(new Verse { Number = 30, TextBosnian = "Na Dan kada upitamo Džehennem: 'Jesi li se napunio?' On će odgovoriti: 'Ima li još?'", TextEnglish = "On the Day We will say to Hell, 'Have you been filled?' and it will say, 'Are there some more,'", TextGerman = "Am Tag, da Wir zur Hölle sagen werden: Bist du voll geworden?, und sie sagt: Gibt es denn noch mehr?" });
                        surah50.VerseList.Add(new Verse { Number = 31, TextBosnian = "A Džennet će biti primaknut bogobojaznima, neće biti ni od jednog daleko.", TextEnglish = "And Paradise will be brought near to the righteous, not far,", TextGerman = "Und nahe herangebracht wird der (Paradies)garten an die Gottesfürchtigen, gar nicht fern:" });
                        surah50.VerseList.Add(new Verse { Number = 32, TextBosnian = "Ovo je ono što vam je obećano, svakome onome ko se Allahu pokajnički obraćao i čuvaran bio.", TextEnglish = "[It will be said], 'This is what you were promised - for every returner [to Allāh] and keeper [of His covenant].", TextGerman = "Das ist, was euch versprochen worden ist, (versprochen) für jeden immer wieder Umkehrbereiten und (Allahs Gebote) Hütenden," });
                        surah50.VerseList.Add(new Verse { Number = 33, TextBosnian = "Koji se Svemilosnog u osami bojao, i koji je srca pokajničkog i pokornog došao.", TextEnglish = "Who feared the Most Merciful in the unseen and came with a heart returning [in repentance].", TextGerman = "der den Allerbarmer im verborgenen fürchtet und mit reuigem Herzen (zu Ihm) kommt." });
                        surah50.VerseList.Add(new Verse { Number = 34, TextBosnian = "Uđite u njega, sa selamom, spašeni. Ovo je Dan vječnosti!'", TextEnglish = "Enter it in peace. This is the Day of Eternity.'", TextGerman = "Betretet ihn in Frieden. Das ist der Tag der Ewigkeit." });
                        surah50.VerseList.Add(new Verse { Number = 35, TextBosnian = "U njemu će imati što god zažele, a kod Nas ima i više.", TextEnglish = "They will have whatever they wish therein, and with Us is more.", TextGerman = "Sie werden, was sie wollen, darin haben. Und bei Uns ist noch mehr." });
                        surah50.VerseList.Add(new Verse { Number = 36, TextBosnian = "A koliko smo naroda prije ovih uništili, koji su moćniji od njih bili, pa su po svijetu tragali - mjesta spasenja ima li?!", TextEnglish = "And how many a generation before them did We destroy who were greater than them in [striking] power and had explored throughout the lands. Is there any place of escape?", TextGerman = "Wie viele Geschlechter vernichteten Wir vor ihnen, die eine stärkere Gewalt hatten als sie und in den Landstrichen umherzogen! Gibt es denn ein Entrinnen?" });
                        surah50.VerseList.Add(new Verse { Number = 37, TextBosnian = "U tome je, zaista, opomena za onoga ko razum ima, ili ko sluša, a priseban je.", TextEnglish = "Indeed in that is a reminder for whoever has a heart or who listens while he is present [in mind].", TextGerman = "Darin ist wahrlich eine Ermahnung für jemanden, der Herz hat oder hinhört, während er (geistig) anwesend ist." });
                        surah50.VerseList.Add(new Verse { Number = 38, TextBosnian = "Mi smo stvorili nebesa i Zemlju i ono što je između njih - za šest dana, i nije Nas ophrvao nikakav umor.", TextEnglish = "And We did certainly create the heavens and earth and what is between them in six days, and there touched Us no weariness.", TextGerman = "Und Wir haben ja die Himmel und die Erde und das, was dazwischen ist, in sechs Tagen erschaffen, wobei Uns keine Ermüdung überkommen hat." });
                        surah50.VerseList.Add(new Verse { Number = 39, TextBosnian = "Zato strpljivo podnosi ono što oni govore i slavi i veličaj Gospodara svoga i zahvaljuj Mu prije sunčeva izlaska i prije zalaska.", TextEnglish = "So be patient, [O Muḥammad], over what they say and exalt [Allāh] with praise of your Lord before the rising of the sun and before its setting,", TextGerman = "So ertrage standhaft, was sie sagen, und lobpreise deinen Herrn vor dem Aufgang der Sonne und vor dem Untergang," });
                        surah50.VerseList.Add(new Verse { Number = 40, TextBosnian = "I slavi i veličaj Ga noću i poslije obavljanja namaza.", TextEnglish = "And [in part] of the night exalt Him and after prostration [i.e., prayer].", TextGerman = "und (in einem Teil) von der Nacht, da preise Ihn, und am Ende der Niederwerfung." });
                        surah50.VerseList.Add(new Verse { Number = 41, TextBosnian = "I osluškuj Dan kada će glasnik pozvati iz mjesta koje je blizu.", TextEnglish = "And listen on the Day when the Caller[1532] will call out from a place that is near -", TextGerman = "Und höre zu am Tag, da der Rufer von einem nahen Ort aus ruft," });
                        surah50.VerseList.Add(new Verse { Number = 42, TextBosnian = "Dan kada će oni, uistinu, čuti glas - to će biti Dan izlaska.", TextEnglish = "The Day they will hear the blast [of the Horn] in truth. That is the Day of Emergence [from the graves].", TextGerman = "am Tag, da sie den Schrei in Wahrheit hören werden; das ist der Tag des Herauskommens." });
                        surah50.VerseList.Add(new Verse { Number = 43, TextBosnian = "Mi, zaista, dajemo život i dajemo smrt i kod Nas je konačno odredište.", TextEnglish = "Indeed, it is We who give life and cause death, and to Us is the destination", TextGerman = "Gewiß, Wir sind es, die Wir wieder lebendig machen und sterben lassen, und zu Uns ist der Ausgang." });
                        surah50.VerseList.Add(new Verse { Number = 44, TextBosnian = "A na Dan kada će nad njima zemlja popucati oni će žurno izići; bit će to oživljenje za Nas lahko.", TextEnglish = "On the Day the earth breaks away from them [and they emerge] rapidly; that is a gathering easy for Us.", TextGerman = "- Am Tag, da die Erde, sie freilassend, sich ihnen aufspalten wird und sie eilig (hervorkommen). Das ist für Uns ein leichtes Versammeln." });
                        surah50.VerseList.Add(new Verse { Number = 45, TextBosnian = "Mi najbolje znamo šta oni govore; ti ih ne možeš prisiliti, nego podsjeti Kur'anom onoga koji se prijetnje Moje boji.", TextEnglish = "We are most knowing of what they say, and you are not over them a tyrant.[1533] But remind by the Qur’ān whoever fears My threat.", TextGerman = "Wir wissen sehr wohl, was sie sagen, und du bist nicht als Gewalthaber über sie (eingesetzt). Darum ermahne (nur) mit dem Qur’an (jeden,) wer Meine Androhung fürchtet." });
                        result = surah50;
                        break;
                    #endregion

