                    case 51:
                        Surah surah51 = new Surah(); surah51.Name = Constants.Surah51Name; surah51.Number = 51; surah51.VerseList = new List<Verse>();
                        surah51.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah51.VerseList.Add(new Verse { Number = 1, TextBosnian = "Tako mi onih koji uzdižu prašinu.", TextEnglish = "By the [winds] scattering [dust], dispersing [it]", TextGerman = "Bei den heftig Zerstreuenden," });
                        surah51.VerseList.Add(new Verse { Number = 2, TextBosnian = "i onih koji teret nose,", TextEnglish = "And the [clouds] carrying a load [of water]", TextGerman = "dann den eine Last Tragenden," });
                        surah51.VerseList.Add(new Verse { Number = 3, TextBosnian = "i onih kojih plove lahko.", TextEnglish = "And the ships sailing with ease", TextGerman = "dann den leicht Dahinziehenden," });
                        surah51.VerseList.Add(new Verse { Number = 4, TextBosnian = "i onih koji naredbe sprovode,", TextEnglish = "And the [angels] apportioning [each] matter,", TextGerman = "dann den Angelegenheiten Regelnden," });
                        surah51.VerseList.Add(new Verse { Number = 5, TextBosnian = "Ono što vam se obećava, zaista, je istina.", TextEnglish = "Indeed, what you are promised is true.", TextGerman = "sicherlich, was euch angedroht wird, ist wahr," });
                        surah51.VerseList.Add(new Verse { Number = 6, TextBosnian = "Polaganje računa sigurno će se desiti.", TextEnglish = "And indeed, the recompense is to occur.", TextGerman = "und das Gericht wird gewiß hereinbrechen." });
                        surah51.VerseList.Add(new Verse { Number = 7, TextBosnian = "Tako Mi neba skladne ljepote,", TextEnglish = "By the heaven containing pathways,[1535]", TextGerman = "Beim Himmel in seiner Vollkommenheit;" });
                        surah51.VerseList.Add(new Verse { Number = 8, TextBosnian = "vi govorite oprečno.", TextEnglish = "Indeed, you are in differing speech.[1536]", TextGerman = "ihr äußert fürwahr unterschiedliche Reden." });
                        surah51.VerseList.Add(new Verse { Number = 9, TextBosnian = "Od njega - Kur'ana biva okrenut onaj ko je zalutao.", TextEnglish = "Deluded away from it [i.e., the Qur’ān] is he who is deluded.", TextGerman = "Abwendig machen läßt sich davon, wer sich abwendig machen läßt." });
                        surah51.VerseList.Add(new Verse { Number = 10, TextBosnian = "Neka prokleti budu poricatelji", TextEnglish = "Destroyed are the misinformers[1537]", TextGerman = "Tod den Mutmaßern," });
                        surah51.VerseList.Add(new Verse { Number = 11, TextBosnian = "koji su, utonuli u nevjerovanje, ravnodušni.", TextEnglish = "Who are within a flood [of confusion] and heedless.", TextGerman = "die in Versenkung zerstreut sind." });
                        surah51.VerseList.Add(new Verse { Number = 12, TextBosnian = "Oni pitaju: 'Kada će Dan sudnji?'", TextEnglish = "They ask, 'When is the Day of Recompense?'", TextGerman = "Sie fragen: Wann wird denn der Tag des Gerichts sein?" });
                        surah51.VerseList.Add(new Verse { Number = 13, TextBosnian = "Bit će onaj dan kada u Vatri budu paćeni.", TextEnglish = "[It is] the Day they will be tormented over the Fire.", TextGerman = "Am Tag, da sie im Feuer geprüft werden." });
                        surah51.VerseList.Add(new Verse { Number = 14, TextBosnian = "Iskusite patnju svoju - to je ono što ste požurivali!'", TextEnglish = "[And will be told], 'Taste your torment. This is that for which you were impatient.'", TextGerman = "Kostet (nun) eure Prüfung. Das ist, was ihr zu beschleunigen wünschtet." });
                        surah51.VerseList.Add(new Verse { Number = 15, TextBosnian = "A bogobojazni će, zaista, u džennetskim baščama, među izvorima, boraviti.", TextEnglish = "Indeed, the righteous will be among gardens and springs,", TextGerman = "Gewiß, die Gottesfürchtigen werden in Gärten und an Quellen sein," });
                        surah51.VerseList.Add(new Verse { Number = 16, TextBosnian = "Primat će ono što im Gospodar njihov bude darovao; oni su prije toga dobra djela činili,", TextEnglish = "Accepting what their Lord has given them. Indeed, they were before that doers of good.", TextGerman = "sie nehmen, was ihr Herr ihnen gegeben hat, denn sie pflegten davor Gutes zu tun." });
                        surah51.VerseList.Add(new Verse { Number = 17, TextBosnian = "noću samo malo spavali,", TextEnglish = "They used to sleep but little of the night,[1538]", TextGerman = "Nur ein wenig pflegten sie in der Nacht zu schlafen," });
                        surah51.VerseList.Add(new Verse { Number = 18, TextBosnian = "i u praskozorje oprost od grijeha molili,", TextEnglish = "And in the hours before dawn they would ask forgiveness,", TextGerman = "und im letzten Teil der Nacht pflegten sie um Vergebung zu bitten," });
                        surah51.VerseList.Add(new Verse { Number = 19, TextBosnian = "a u imecima njihovim bio je udio i za onoga koji prosi i za onoga koji je uskraćen a ne prosi.", TextEnglish = "And from their properties was [given] the right of the [needy] petitioner and the deprived.", TextGerman = "und (sie gestanden) an ihrem Besitz dem Bettler und dem Unbemittelten ein Anrecht (zu)." });
                        surah51.VerseList.Add(new Verse { Number = 20, TextBosnian = "Na Zemlji su znakovi za one koji čvrsto vjeruju,", TextEnglish = "And on the earth are signs for the certain [in faith]", TextGerman = "Und auf der Erde gibt es Zeichen für die Überzeugten" });
                        surah51.VerseList.Add(new Verse { Number = 21, TextBosnian = "a i u vama samima - zar ne vidite?", TextEnglish = "And in yourselves. Then will you not see?", TextGerman = "und (auch) in euch selbst. Seht ihr denn nicht?" });
                        surah51.VerseList.Add(new Verse { Number = 22, TextBosnian = "A na nebu je opskrba vaša i ono što vam se obećava", TextEnglish = "And in the heaven is your provision and whatever you are promised.", TextGerman = "Und im Himmel ist eure Versorgung und das, was euch versprochen wird." });
                        surah51.VerseList.Add(new Verse { Number = 23, TextBosnian = "i, tako Mi Gospodara neba i Zemlje, to je istina, kao što je istina da govorite!", TextEnglish = "Then by the Lord of the heaven and earth, indeed, it is truth - just as [sure as] it is that you are speaking.", TextGerman = "Beim Herrn des Himmels und der Erde, das ist gewiß so wahr, gleichermaßen wie ihr reden könnt." });
                        surah51.VerseList.Add(new Verse { Number = 24, TextBosnian = "Da li je doprla do tebe vijest o počašćenim gostima lbrahimovim.", TextEnglish = "Has there reached you the story of the honored guests of Abraham?[1539] -", TextGerman = "Ist zu dir die Geschichte von den geehrten Gästen Ibrahims gekommen?" });
                        surah51.VerseList.Add(new Verse { Number = 25, TextBosnian = "Kad mu oni uđoše i rekoše: 'Selam vama!', i on reče: 'Selam vama, ljudi neznani.'", TextEnglish = "When they entered upon him and said, '[We greet you with] peace.' He answered, '[And upon you] peace; [you are] a people unknown.'", TextGerman = "Als sie bei ihm eintraten und sagten: Frieden! Er sagte: Friede! - Fremde Leute." });
                        surah51.VerseList.Add(new Verse { Number = 26, TextBosnian = "I on neprimjetno ode ukućanima svojim i donese debelo tele.", TextEnglish = "Then he went to his family and came with a fat [roasted] calf.", TextGerman = "Er schlich sich zu seinen Angehörigen und brachte (zum Essen) dann ein ansehnliches Kalb her." });
                        surah51.VerseList.Add(new Verse { Number = 27, TextBosnian = "primače im ga: 'Zar nećete jesti?', upita", TextEnglish = "And placed it near them; he said, 'Will you not eat?'", TextGerman = "Er setzte es ihnen vor; er sagte: Wollt ihr nicht essen?" });
                        surah51.VerseList.Add(new Verse { Number = 28, TextBosnian = "i osjeti od njih u sebi strah. 'Ne boj se.', rekoše i obradovaše ga dječakom koji će učen biti.", TextEnglish = "And he felt from them apprehension.[1540] They said, 'Fear not,' and gave him good tidings of a learned boy.", TextGerman = "Er empfand in sich Furcht vor ihnen. Sie sagten: Fürchte dich nicht. Und sie verkündeten ihm einen kenntnisreichen Jungen." });
                        surah51.VerseList.Add(new Verse { Number = 29, TextBosnian = "I pojavi se žena njegova uzvikujući i po licu se udarajući i reče: 'Ja sam stara, nerotkinja!", TextEnglish = "And his wife approached with a cry [of alarm] and struck her face and said, '[I am] a barren old woman!'", TextGerman = "Da kam seine Frau in großem Geschrei herbei. Sie schlug sich ins Gesicht und sagte: (Ich bin doch) eine alte, unfruchtbare Frau!" });
                        surah51.VerseList.Add(new Verse { Number = 30, TextBosnian = "Tako je odredio Gospodar tvoj', rekoše oni, 'On je Mudri i Sveznajući.'", TextEnglish = "They said, 'Thus has said your Lord; indeed, He is the Wise, the Knowing.'", TextGerman = "Sie sagten: So hat (es) dein Herr gesagt. Gewiß, Er ist der Allweise und Allwissende." });
                        surah51.VerseList.Add(new Verse { Number = 31, TextBosnian = "A što vi hoćete, o izaslanici?', upita Ibrahim.", TextEnglish = "[Abraham] said, 'Then what is your business [here], O messengers?'", TextGerman = "Er sagte: Was ist nun euer Auftrag, ihr Boten?" });
                        surah51.VerseList.Add(new Verse { Number = 32, TextBosnian = "Poslani smo narodu prestupničkom'', rekoše,", TextEnglish = "They said, 'Indeed, we have been sent to a people of criminals[1541]", TextGerman = "Sie sagten: Wir sind zu einem Volk von Übeltätern gesandt," });
                        surah51.VerseList.Add(new Verse { Number = 33, TextBosnian = "da sručimo na njih grumenje od gline,", TextEnglish = "To send down upon them stones of clay,", TextGerman = "um über sie Steine aus Lehm niederkommen zu lassen," });
                        surah51.VerseList.Add(new Verse { Number = 34, TextBosnian = "obilježeno u Gospodara tvoga za one koji su u razvratu svaku mjeru prešli.'", TextEnglish = "Marked in the presence of your Lord for the transgressors.'", TextGerman = "gekennzeichnet bei deinem Herrn für die Maßlosen." });
                        surah51.VerseList.Add(new Verse { Number = 35, TextBosnian = "Mi iz njega vjernike izvedosmo,", TextEnglish = "So We brought out whoever was in them [i.e., the cities] of the believers.", TextGerman = "Und so ließen Wir wer in ihr gläubig war, fortziehen." });
                        surah51.VerseList.Add(new Verse { Number = 36, TextBosnian = "a u njemu samo jednu kuću muslimansku nađosmo,", TextEnglish = "And We found not within them other than a [single] house of Muslims.[1542]", TextGerman = "Da fanden wir in ihr aber nur ein (einziges) Haus von (Allah) Ergebenen." });
                        surah51.VerseList.Add(new Verse { Number = 37, TextBosnian = "i u njemu, za sve one koji se boje bolne patnje, znak ostavismo.", TextEnglish = "And We left therein a sign for those who fear the painful punishment.", TextGerman = "Und Wir hinterließen in ihr ein Zeichen für diejenigen, die die schmerzhafte Strafe fürchten." });
                        surah51.VerseList.Add(new Verse { Number = 38, TextBosnian = "I u Musau, također - kada ga s očiglednim dokazom faraonu poslasmo,", TextEnglish = "And in Moses [was a sign], when We sent him to Pharaoh with clear authority.[1543]", TextGerman = "Und in Musa, als Wir ihn zu Fir’aun mit einer deutlichen Ermächtigung sandten." });
                        surah51.VerseList.Add(new Verse { Number = 39, TextBosnian = "a on, oholeći se, okrenu se nastranu i reče: 'Čarobnjak je ili lud.'", TextEnglish = "But he turned away with his supporters and said, 'A magician or a madman.'", TextGerman = "Da aber kehrte er sich ab samt seiner Stütze und sagte: Ein Zauberer oder ein Besessener." });
                        surah51.VerseList.Add(new Verse { Number = 40, TextBosnian = "I Mi i njega i vojske njegove dohvatismo, pa ih u more bacismo, jer je bio osudu zaslužio.", TextEnglish = "So We took him and his soldiers and cast them into the sea, and he was blameworthy.", TextGerman = "Da ergriffen Wir ihn und seine Heerscharen und warfen sie dann in das große Gewässer, denn er hatte sich Tadel zugezogen." });
                        surah51.VerseList.Add(new Verse { Number = 41, TextBosnian = "I u Adu - kad na njih vjetar poslasmo u kome nije bilo nikakva dobra;", TextEnglish = "And in ʿAad [was a sign], when We sent against them the barren wind.[1544]", TextGerman = "Und (auch) in den ’Ad, als Wir gegen sie den unheilvollen Wind sandten." });
                        surah51.VerseList.Add(new Verse { Number = 42, TextBosnian = "Pored čega god je prošao, ništa nije poštedio, sve je u gnjilež pretvorio.", TextEnglish = "It left nothing of what it came upon but that it made it like disintegrated ruins.", TextGerman = "Nichts von (all) dem, worüber er kam, ließ er zurück, ohne es wie Zerfallenes werden zu lassen." });
                        surah51.VerseList.Add(new Verse { Number = 43, TextBosnian = "I u Semudu'' - kad im bi rečeno: 'Uživajte još neko vrijeme!'", TextEnglish = "And in Thamūd, when it was said to them, 'Enjoy yourselves for a time.'", TextGerman = "Und (auch) in den Tamud, als zu ihnen gesagt wurde: Genießt für eine gewisse Zeit." });
                        surah51.VerseList.Add(new Verse { Number = 44, TextBosnian = "Oni se oglušiše o naređenje Gospodara svoga, pa ih uništi strašan glas na oči njihove,", TextEnglish = "But they were insolent toward the command of their Lord, so the thunderbolt seized them while they were looking on.", TextGerman = "Sie lehnten sich gegen den Befehl ihres Herrn auf. Da ergriff sie der Donnerschlag, während sie hinschauten," });
                        surah51.VerseList.Add(new Verse { Number = 45, TextBosnian = "i ne mogaše se ni dići ni od kazne odbraniti.", TextEnglish = "And they were unable to arise, nor could they defend themselves.", TextGerman = "so konnten sie nicht (mehr) aufstehen und es wurde ihnen keine Hilfe zuteil." });
                        surah51.VerseList.Add(new Verse { Number = 46, TextBosnian = "I uništili smo prije narod Nuhov: to, zaista, bijaše narod buntovni.", TextEnglish = "And [We destroyed] the people of Noah before; indeed, they were a people defiantly disobedient.", TextGerman = "Und (Wir vernichteten) zuvor (auch) das Volk Nuhs; gewiß, sie waren ein Volk von Frevlern." });
                        surah51.VerseList.Add(new Verse { Number = 47, TextBosnian = "Mi smo nebo snagom sazdali, i Mi smo, uistinu, proširitelji.", TextEnglish = "And the heaven We constructed with strength,[1545] and indeed, We are [its] expander.", TextGerman = "Und den Himmel haben Wir mit Kraft aufgebaut, und Wir weiten (ihn) wahrlich (noch) aus." });
                        surah51.VerseList.Add(new Verse { Number = 48, TextBosnian = "I Zemlju smo prostrli - tako smo divni Mi Koji smo je prostrli!", TextEnglish = "And the earth We have spread out, and excellent is the preparer.", TextGerman = "Und die Erde haben Wir ausgebreitet. Wie trefflich haben Wir (sie) geebnet!" });
                        surah51.VerseList.Add(new Verse { Number = 49, TextBosnian = "I od svega po par stvaramo da biste se vi prisjećali.", TextEnglish = "And of all things We created two mates [i.e., counterparts]; perhaps you will remember.", TextGerman = "Und von allem haben Wir ein Paar erschaffen, auf daß ihr bedenken möget." });
                        surah51.VerseList.Add(new Verse { Number = 50, TextBosnian = "Zato požurite Allahu, ja sam vam Njegov upozoritelj jasni!", TextEnglish = "So flee to Allāh.[1546] Indeed, I am to you from Him a clear warner.", TextGerman = "So flüchtet (nun) zu Allah. Gewiß, ich bin euch von Ihm ein deutlicher Warner." });
                        surah51.VerseList.Add(new Verse { Number = 51, TextBosnian = "I ne pripisujte uz Allaha boga drugoga! Ja sam vam Njegov upozoritelj jasni!", TextEnglish = "And do not make [as equal] with Allāh another deity. Indeed, I am to you from Him a clear warner.", TextGerman = "Und setzt neben Allah keinen anderen Gott. Gewiß, ich bin euch von Ihm ein deutlicher Warner." });
                        surah51.VerseList.Add(new Verse { Number = 52, TextBosnian = "I tako je bilo: ni onima prije ovih nije došao nijedan poslanik, a da nisu rekli: 'Čarobnjak je!' ili: 'Lud je!'", TextEnglish = "Similarly, there came not to those before them any messenger except that they said, 'A magician or a madman.'", TextGerman = "So kam auch zu denjenigen, die vor ihnen waren, kein Gesandter, ohne daß sie gesagt hätten: Ein Zauberer oder ein Besessener." });
                        surah51.VerseList.Add(new Verse { Number = 53, TextBosnian = "Jesu li to oni jedni drugima oporučili? Nisu, nego su oni ljudi koji su u zlu svaku mjeru bili prevršili.", TextEnglish = "Did they suggest it to them?[1547] Rather, they [themselves] are a transgressing people.", TextGerman = "Haben sie es denn einander eindringlich empfohlen? Nein! Vielmehr sind sie ein das Maß (an Frevel) überschreitendes Volk." });
                        surah51.VerseList.Add(new Verse { Number = 54, TextBosnian = "Pa, od njih ti se okreni, prekoren nećeš biti!", TextEnglish = "So leave them, [O Muḥammad], for you are not to be blamed.", TextGerman = "So kehre dich von ihnen ab; du bist (hierin) nicht zu tadeln." });
                        surah51.VerseList.Add(new Verse { Number = 55, TextBosnian = "I opominji! Doista, opomena vjernicima koristi!", TextEnglish = "And remind, for indeed, the reminder benefits the believers.", TextGerman = "Und ermahne, denn die Ermahnung nützt den Gläubigen." });
                        surah51.VerseList.Add(new Verse { Number = 56, TextBosnian = "Džine i ljude sam stvorio samo zato da Meni u ibadetu budu.", TextEnglish = "And I did not create the jinn and mankind except to worship Me.", TextGerman = "Und Ich habe die Ginn und die Menschen nur (dazu) erschaffen, damit sie Mir dienen." });
                        surah51.VerseList.Add(new Verse { Number = 57, TextBosnian = "Ja ne tražim od njih opskrbu niti želim da me hrane,", TextEnglish = "I do not want from them any provision, nor do I want them to feed Me.", TextGerman = "Ich will weder von ihnen irgendeine Versorgung, noch will Ich, daß sie Mir zu essen geben." });
                        surah51.VerseList.Add(new Verse { Number = 58, TextBosnian = "Opskrbitelj je jedino Allah, Snažni i Silni!", TextEnglish = "Indeed, it is Allāh who is the [continual] Provider,[1548] the firm possessor of strength.", TextGerman = "Gewiß, Allah ist der Versorger, der Besitzer von Kraft und der Feste ." });
                        surah51.VerseList.Add(new Verse { Number = 59, TextBosnian = "A one koji su zulum činili stići će kazna kao što je stigla i one koji su bili kao oni, i neka Me ne požuruju;", TextEnglish = "And indeed, for those who have wronged is a portion [of punishment] like the portion of their companions [i.e., predecessors], so let them not impatiently urge Me.", TextGerman = "Gewiß, für diejenigen, die Unrecht tun, wird es einen vorgesehenen Anteil geben wie den Anteil ihrer Gefährten; so sollen sie Mich nicht um Beschleunigung (der Strafe) bitten." });
                        surah51.VerseList.Add(new Verse { Number = 60, TextBosnian = "a, teško nevjernicima na Dan kojim im se prijeti!", TextEnglish = "And woe to those who have disbelieved from their Day which they are promised.", TextGerman = "So wehe denjenigen, die ungläubig sind, vor ihrem Tag, der ihnen angedroht ist!" });
                        result = surah51;
                        break;
                    #endregion

