                    case 52:
                        Surah surah52 = new Surah(); surah52.Name = Constants.Surah52Name; surah52.Number = 52; surah52.VerseList = new List<Verse>();
                        surah52.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah52.VerseList.Add(new Verse { Number = 1, TextBosnian = "Tako Mi brda Tur", TextEnglish = "By the mount", TextGerman = "Bei dem Berg;" });
                        surah52.VerseList.Add(new Verse { Number = 2, TextBosnian = "i Knjige u retke napisane", TextEnglish = "And [by] a Book inscribed[1550]", TextGerman = "und bei einem Buch, das in Zeilen niedergeschrieben ist" });
                        surah52.VerseList.Add(new Verse { Number = 3, TextBosnian = "na koži razvijenoj", TextEnglish = "In parchment spread open", TextGerman = "auf ausgebreiteter Tierhaut;" });
                        surah52.VerseList.Add(new Verse { Number = 4, TextBosnian = "i Bejtul-Ma'mura", TextEnglish = "And [by] the frequented House[1551]", TextGerman = "bei dem vielbesuchten (Gottes)haus" });
                        surah52.VerseList.Add(new Verse { Number = 5, TextBosnian = "i svoda uzdignutog", TextEnglish = "And [by] the ceiling [i.e., heaven] raised high", TextGerman = "und bei dem erhöhten Dach" });
                        surah52.VerseList.Add(new Verse { Number = 6, TextBosnian = "i vatrom mora napunjenog,", TextEnglish = "And [by] the sea set on fire,[1552]", TextGerman = "und bei dem angefüllten Meer!" });
                        surah52.VerseList.Add(new Verse { Number = 7, TextBosnian = "kazna Gospodara tvoga sigurno će se dogoditi.", TextEnglish = "Indeed, the punishment of your Lord will occur.", TextGerman = "Die Strafe deines Herrn wird gewiß hereinbrechen." });
                        surah52.VerseList.Add(new Verse { Number = 8, TextBosnian = "Niko je neće moći otkloniti", TextEnglish = "Of it there is no preventer.", TextGerman = "Es gibt niemanden, der sie abwehren könnte." });
                        surah52.VerseList.Add(new Verse { Number = 9, TextBosnian = "na Dan kad se nebo silno uzburka", TextEnglish = "On the Day the heaven will sway with circular motion", TextGerman = "Am Tag, da der Himmel sich heftig hin und her bewegen" });
                        surah52.VerseList.Add(new Verse { Number = 10, TextBosnian = "a planine s mjesta pomaknu!", TextEnglish = "And the mountains will pass on, departing[1553] -", TextGerman = "und die Berge sich in der Tat bewegen werden," });
                        surah52.VerseList.Add(new Verse { Number = 11, TextBosnian = "Teško na taj dan onima koji su poricali,", TextEnglish = "Then woe, that Day, to the deniers,", TextGerman = "wehe an jenem Tag den Leugnern (der Botschaft)," });
                        surah52.VerseList.Add(new Verse { Number = 12, TextBosnian = "koji su se u laž upuštali, zabavljajući se!", TextEnglish = "Who are in [empty] discourse amusing themselves.", TextGerman = "die in schweifenden Gesprächen ihr Spiel treiben!" });
                        surah52.VerseList.Add(new Verse { Number = 13, TextBosnian = "Na Dan kada će grubo u vatru džehennemsku biti gurnuti:", TextEnglish = "The Day they are thrust toward the fire of Hell with a [violent] thrust, [its angels will say],", TextGerman = "Am Tag, da sie gewaltsam zum Feuer der Hölle gestoßen werden:" });
                        surah52.VerseList.Add(new Verse { Number = 14, TextBosnian = "Ovo je Vatra koju ste poricali", TextEnglish = "This is the Fire which you used to deny.", TextGerman = "Das ist das Feuer, das ihr für Lüge zu erklären pflegtet." });
                        surah52.VerseList.Add(new Verse { Number = 15, TextBosnian = "pa, je li ovo vradžbina, ili vi ne vidite?!", TextEnglish = "Then is this magic, or do you not see?", TextGerman = "Ist dies (nun) etwa Zauberei, oder könnt ihr nicht sehen?" });
                        surah52.VerseList.Add(new Verse { Number = 16, TextBosnian = "Pržite se u njoj, isto vam je trpjeli ili ne trpjeli - to vam je kazna za ono što ste radili!'", TextEnglish = "[Enter to] burn therein; then be patient or impatient - it is all the same for you. You are only being recompensed [for] what you used to do.'", TextGerman = "Seid ihm ausgesetzt und ertragt es dann standhaft oder nicht, gleich ist es in Bezug auf euch; euch wird nur das vergolten, was ihr zu tun pflegtet." });
                        surah52.VerseList.Add(new Verse { Number = 17, TextBosnian = "A bogobojazni će, zbilja, u džennetskim baščama i blagodatima biti,", TextEnglish = "Indeed, the righteous will be in gardens and pleasure,", TextGerman = "Die Gottesfürchtigen aber werden in Gärten und Wonne sein." });
                        surah52.VerseList.Add(new Verse { Number = 18, TextBosnian = "i u onom će što im je Gospodar njihov dao uživati - njih će Gospodar njihov patnje u Ognju sačuvati.", TextEnglish = "Enjoying what their Lord has given them, and their Lord protected them from the punishment of Hellfire.", TextGerman = "Sie lassen es sich wohl sein an dem, was ihnen ihr Herr gibt. Und ihr Herr bewahrt sie vor der Strafe des Höllenbrandes." });
                        surah52.VerseList.Add(new Verse { Number = 19, TextBosnian = "Jedite i pijte i neka vam je prijatno, to je za ono što ste radili.", TextEnglish = "[They will be told], 'Eat and drink in satisfaction for what you used to do.'", TextGerman = "Eßt und trinkt als wohlbekömmlich für das, was ihr zu tun pflegtet." });
                        surah52.VerseList.Add(new Verse { Number = 20, TextBosnian = "Bit će naslonjeni na divanima poredanim, a vjenčat ćemo ih s hurijama krupnih očiju.", TextEnglish = "They will be reclining on thrones lined up, and We will marry them to fair women with large, [beautiful] eyes.", TextGerman = "indem ihr euch auf (voreinander)gereihten Liegen lehnt. Und Wir geben ihnen als Gattinnen Huris mit schönen, großen Augen." });
                        surah52.VerseList.Add(new Verse { Number = 21, TextBosnian = "Onima koji su vjerovali i za kojima su se djeca njihova u vjerovanju povela priključit ćemo djecu njihovu, a djela njihova nećemo nimalo umanjiti - svaki čovjek je zalog za ono što je uradio.", TextEnglish = "And those who believed and whose descendants followed them in faith - We will join with them their descendants, and We will not deprive them of anything of their deeds.[1554] Every person, for what he earned, is retained.[1555]", TextGerman = "Und denjenigen, die glauben und denen ihre Nachkommenschaft im Glauben nachfolgt, lassen Wir ihre Nachkommenschaft sich (ihnen) anschließen. Und Wir verringern ihnen gar nichts von ihren Werken. Jedermann ist an das, was er erworben hat, gebunden." });
                        surah52.VerseList.Add(new Verse { Number = 22, TextBosnian = "Još ćemo ih darovati voćem i mesom kakvo budu željeli,", TextEnglish = "And We will provide them with fruit and meat from whatever they desire.", TextGerman = "Und Wir versorgen sie reichlich mit Früchten und Fleisch von dem, was sie begehren." });
                        surah52.VerseList.Add(new Verse { Number = 23, TextBosnian = "jedni drugima će, u njemu, pune čaše dodavati - zbog njih neće biti praznih besjeda i pobuda na grijeh,", TextEnglish = "They will exchange with one another a cup [of wine] wherein [results] no ill speech or commission of sin.", TextGerman = "Sie greifen darin miteinander nach einem Becher, in dem nichts darin ist, was zu unbedachter Rede verleitet und zu keiner Versündigung." });
                        surah52.VerseList.Add(new Verse { Number = 24, TextBosnian = "a služit će ih posluga njihova nalik na biser skriveni,", TextEnglish = "There will circulate among them [servant] boys [especially] for them, as if they were pearls well-protected.", TextGerman = "Und unter ihnen gehen Jünglinge umher, die zu ihnen gehören, als wären sie wohlverwahrte Perlen." });
                        surah52.VerseList.Add(new Verse { Number = 25, TextBosnian = "i primaknut će se jedni drugima i jedni druge pitati:", TextEnglish = "And they will approach one another, inquiring of each other.", TextGerman = "Und sie wenden sich einander zu und fragen sich gegenseitig." });
                        surah52.VerseList.Add(new Verse { Number = 26, TextBosnian = "Prije smo među svojim porodicama strahovali,", TextEnglish = "They will say, 'Indeed, we were previously among our people fearful [of displeasing Allāh].", TextGerman = "Sie sagen: Gewiß, früher waren wir inmitten unserer Angehörigen besorgt." });
                        surah52.VerseList.Add(new Verse { Number = 27, TextBosnian = "pa nam je Allah milost darovao i od patnje u Ognju nas sačuvao.", TextEnglish = "So Allāh conferred favor upon us and protected us from the punishment of the Scorching Fire.", TextGerman = "Da erwies Allah uns eine Wohltat und bewahrte uns vor der Strafe des Glutwindes." });
                        surah52.VerseList.Add(new Verse { Number = 28, TextBosnian = "Mi smo Ga u dovi molili, On je, doista, Dobročinitelj, Milostivi.", TextEnglish = "Indeed, we used to supplicate[1556] Him before. Indeed, it is He who is the Beneficent, the Merciful.'", TextGerman = "Gewiß, früher pflegten wir Ihn anzurufen. Gewiß, Er ist ja der Gütige und Barmherzige." });
                        surah52.VerseList.Add(new Verse { Number = 29, TextBosnian = "Zato ti opominji, jer ti, milošću Gospodara svoga, nisi ni prorok ni lud.", TextEnglish = "So remind, [O Muḥammad], for you are not, by the favor of your Lord, a soothsayer or a madman.", TextGerman = "So ermahne (nur); du bist ja durch die Gunst deines Herrn weder ein Wahrsager noch ein Besessener." });
                        surah52.VerseList.Add(new Verse { Number = 30, TextBosnian = "Zar oni da govore: 'Pjesnik je, sačekat ćemo dok potresen nedaćama ne umre.'", TextEnglish = "Or do they say [of you], 'A poet for whom we await a misfortune of time'?[1557]", TextGerman = "Oder sagen sie etwa: Ein Dichter, gegen den wir die (Unglücks)fälle der Zeit abwarten?" });
                        surah52.VerseList.Add(new Verse { Number = 31, TextBosnian = "Pa čekajte', reci ti, 'i ja ću zajedno s vama čekati.'", TextEnglish = "Say, 'Wait, for indeed I am, with you, among the waiters.'", TextGerman = "Sag: Wartet ab! Gewiß, ich gehöre mit euch zu denjenigen, die abwarten." });
                        surah52.VerseList.Add(new Verse { Number = 32, TextBosnian = "Da li im ovo umovi njihovi naređuju?! Ne, nego su oni obijesan narod.", TextEnglish = "Or do their minds[1558] command them to [say] this, or are they a transgressing people?", TextGerman = "Oder befiehlt ihnen etwa ihr Verstand dies, oder sind sie (vielmehr) Leute, die das Maß (an Frevel) überschreiten?" });
                        surah52.VerseList.Add(new Verse { Number = 33, TextBosnian = "Zar oni da govore: 'Izmišlja ga!' Štaviše, oni ne vjeruju.", TextEnglish = "Or do they say, 'He has made it up'? Rather, they do not believe.", TextGerman = "Oder sagen sie etwa: Er hat ihn sich selbst ausgedacht’? Nein! Vielmehr glauben sie nicht." });
                        surah52.VerseList.Add(new Verse { Number = 34, TextBosnian = "Zato, neka oni sastave govor sličan Kur'anu, ako istinu govore.", TextEnglish = "Then let them produce a statement like it, if they should be truthful.", TextGerman = "So sollen sie doch eine Aussage gleicher Art beibringen, wenn sie wahrhaftig sind." });
                        surah52.VerseList.Add(new Verse { Number = 35, TextBosnian = "Da li su oni bez Stvoritelja stvoreni ili su oni sami sebe stvorili?!", TextEnglish = "Or were they created by nothing, or were they the creators [of themselves]?", TextGerman = "Oder sind sie etwa aus dem Nichts erschaffen worden, oder sind sie (gar) selbst die Schöpfer?" });
                        surah52.VerseList.Add(new Verse { Number = 36, TextBosnian = "Da li su oni nebesa i Zemlju stvorili?! Ne, nego, oni nisu uvjereni!", TextEnglish = "Or did they create the heavens and the earth? Rather, they are not certain.", TextGerman = "Oder haben sie (etwa) die Himmel und die Erde erschaffen? Nein! Vielmehr sind sie nicht überzeugt." });
                        surah52.VerseList.Add(new Verse { Number = 37, TextBosnian = "Da li su kod njih riznice Gospodara tvoga, ili oni vladaju?!", TextEnglish = "Or have they the depositories [containing the provision] of your Lord? Or are they the controllers [of them]?", TextGerman = "Oder besitzen sie (etwa) die Schatzkammern deines Herrn, oder sind sie es, die die Oberherrschaft ausüben?" });
                        surah52.VerseList.Add(new Verse { Number = 38, TextBosnian = "Da li oni imaju ljestve, pa na njima prisluškuju?! Neka onaj među njima koji tvrdi daje nešto čuo donese potvrdu očitu.", TextEnglish = "Or have they a stairway [into the heaven] upon which they listen? Then let their listener produce a clear authority [i.e., proof].", TextGerman = "Oder haben sie eine Leiter, mit der sie (den Himmel) abhören (können)? Dann soll doch ihr Abhörer eine deutliche Ermächtigung beibringen." });
                        surah52.VerseList.Add(new Verse { Number = 39, TextBosnian = "Zar da su za Njega - kćeri, a za vas da su - sinovi?!", TextEnglish = "Or has He daughters while you have sons?", TextGerman = "Oder hat Er (für Sich selbst) die Töchter und habt ihr (für euch selbst) die Söhne (bestimmt)?" });
                        surah52.VerseList.Add(new Verse { Number = 40, TextBosnian = "Da li ti tražiš od njih naknadu, pa su nametom opterećeni?", TextEnglish = "Or do you, [O Muḥammad], ask of them a payment, so they are by debt burdened down?", TextGerman = "Oder verlangst du (etwa) von ihnen einen Lohn, so daß sie mit Schulden belastet wären?" });
                        surah52.VerseList.Add(new Verse { Number = 41, TextBosnian = "Ili, da nije u njih znanje o onome što je čulima nedokučivo, pa oni prepisuju?!", TextEnglish = "Or have they [knowledge of] the unseen, so they write [it] down?", TextGerman = "Oder besitzen sie (das Wissen über) das Verborgene, so daß sie (es) aufschreiben (können)?" });
                        surah52.VerseList.Add(new Verse { Number = 42, TextBosnian = "Zar oni spletke žele?! Pa, oni koji ne vjeruju, oni padaju u spletke!", TextEnglish = "Or do they intend a plan? But those who disbelieve - they are the object of a plan.", TextGerman = "Oder wollen sie eine List anwenden? Aber diejenigen, die ungläubig sind, sind es, die der List erliegen." });
                        surah52.VerseList.Add(new Verse { Number = 43, TextBosnian = "Zar oni drugog boga osim Allaha da imaju? Uzvišen je Allah od onoga što Mu u obožavanju pridružuju.", TextEnglish = "Or have they a deity other than Allāh? Exalted is Allāh above whatever they associate with Him.", TextGerman = "Oder haben sie einen anderen Gott als Allah? Preis sei Allah! (und Erhaben ist Er) über das, was sie (Ihm) beigesellen." });
                        surah52.VerseList.Add(new Verse { Number = 44, TextBosnian = "I kad bi vidjeli da komad neba pada, rekli bi: 'Oblaci nagomilani.'", TextEnglish = "And if they were to see a fragment from the sky falling,[1559] they would say, '[It is merely] clouds heaped up.'", TextGerman = "Und wenn sie ein Stück vom Himmel herabfallen sähen, würden sie sagen: Aufgeschichtete Wolken." });
                        surah52.VerseList.Add(new Verse { Number = 45, TextBosnian = "Zato ih pusti dok se ne suoče s Danom u kome će pomrijeti,", TextEnglish = "So leave them until they meet their Day in which they will be struck insensible -", TextGerman = "Lasse sie nur, bis sie ihrem Tag begegnen, an dem sie wie vom Donnerschlag getroffen zusammenbrechen werden," });
                        surah52.VerseList.Add(new Verse { Number = 46, TextBosnian = "Danom kada im spletke njihove nimalo neće koristiti i kada im niko neće pomoći.", TextEnglish = "The Day their plan will not avail them at all, nor will they be helped.", TextGerman = "dem Tag, da ihre List ihnen nichts nützen und ihnen keine Hilfe zuteil werden wird." });
                        surah52.VerseList.Add(new Verse { Number = 47, TextBosnian = "A za sve koji su zulum činili i druga će kazna prije one biti, ali većina njih ne zna.", TextEnglish = "And indeed, for those who have wronged is a punishment[1560] before that, but most of them do not know.", TextGerman = "Gewiß, für diejenigen, die Unrecht tun, wird es eine Strafe außer dieser geben, aber die meisten von ihnen wissen nicht." });
                        surah52.VerseList.Add(new Verse { Number = 48, TextBosnian = "A ti strpljivo čekaj presudu Gospodara svoga, jer ti si pred Našim očima; i veličaj i hvali Gospodara svoga kad ustaješ,", TextEnglish = "And be patient, [O Muḥammad], for the decision of your Lord, for indeed, you are in Our eyes [i.e., sight]. And exalt [Allāh] with praise of your Lord when you arise", TextGerman = "Sei standhaft gegenüber dem Urteil deines Herrn. Gewiß, du bist ja vor Unseren Augen. Und lobpreise deinen Herrn, wenn du aufstehst," });
                        surah52.VerseList.Add(new Verse { Number = 49, TextBosnian = "i noću Ga veličaj kad se zvijezde gube.", TextEnglish = "And in a part of the night exalt Him and after [the setting of] the stars.", TextGerman = "und (in einem Teil) von der Nacht, da preise Ihn, und beim Schwinden der Sterne." });
                        result = surah52;
                        break;
                    #endregion

                    // 
                    #region
                    case 53:
                        Surah surah53 = new Surah(); surah53.Name = Constants.Surah53Name; surah53.Number = 53; surah53.VerseList = new List<Verse>();
                        surah53.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah53.VerseList.Add(new Verse { Number = 1, TextBosnian = "Tako Mi zvijezde kad zapada,", TextEnglish = "By the star when it descends,", TextGerman = "Bei dem Stern, wenn er sinkt!" });
                        surah53.VerseList.Add(new Verse { Number = 2, TextBosnian = "vaš drug nije s Pravog puta skrenuo i nije zalutao!", TextEnglish = "Your companion [i.e., Muḥammad] has not strayed, nor has he erred,", TextGerman = "Nicht in die Irre geht euer Gefährte, und auch nicht einem Irrtum ist er erlegen," });
                        surah53.VerseList.Add(new Verse { Number = 3, TextBosnian = "On ne govori po hiru svome.", TextEnglish = "Nor does he speak from [his own] inclination.", TextGerman = "und er redet nicht aus (eigener) Neigung." });
                        surah53.VerseList.Add(new Verse { Number = 4, TextBosnian = "To je samo Objava koja mu se obznanjuje.", TextEnglish = "It is not but a revelation revealed,", TextGerman = "Es ist nur eine Offenbarung, die eingegeben wird." });
                        surah53.VerseList.Add(new Verse { Number = 5, TextBosnian = "Uči ga jedan velikih moći;", TextEnglish = "Taught to him by one intense in strength [i.e., Gabriel] -", TextGerman = "Belehrt hat ihn der Besitzer starker Kräfte," });
                        surah53.VerseList.Add(new Verse { Number = 6, TextBosnian = "snažni, koji se uspravio u liku svome,", TextEnglish = "One of soundness.[1562] And he rose to [his] true form[1563]", TextGerman = "der (geistige) Macht besitzt. So stand Er da" });
                        surah53.VerseList.Add(new Verse { Number = 7, TextBosnian = "na obzorju najvišem.", TextEnglish = "While he was in the higher [part of the] horizon.[1564]", TextGerman = "und war am obersten Gesichtskreis." });
                        surah53.VerseList.Add(new Verse { Number = 8, TextBosnian = "Zatim se približio, pa nadnio", TextEnglish = "Then he approached and descended", TextGerman = "Hierauf kam er näher und stieg herunter," });
                        surah53.VerseList.Add(new Verse { Number = 9, TextBosnian = "blizu koliko dva luka ili bliže", TextEnglish = "And was at a distance of two bow lengths or nearer.", TextGerman = "so daß er (nur) zwei Bogenlängen entfernt war oder noch näher." });
                        surah53.VerseList.Add(new Verse { Number = 10, TextBosnian = "i objavio robu Njegovu ono što je objavio.", TextEnglish = "And he revealed to His Servant[1565] what he revealed [i.e., conveyed].", TextGerman = "Da gab Er Seinem Diener (als Offenbarung) ein, was Er eingab." });
                        surah53.VerseList.Add(new Verse { Number = 11, TextBosnian = "Srce nije poreklo ono što je vidio.", TextEnglish = "The heart[1566] did not lie [about] what it saw.", TextGerman = "Nicht hat sein Herz erlogen, was es sah." });
                        surah53.VerseList.Add(new Verse { Number = 12, TextBosnian = "Pa zašto se prepirete s njim o onom što je vidio?", TextEnglish = "So will you dispute with him over what he saw?", TextGerman = "Wollt ihr denn mit ihm streiten über das, was er sieht?" });
                        surah53.VerseList.Add(new Verse { Number = 13, TextBosnian = "On ga je i drugi put vidio.", TextEnglish = "And he certainly saw him in another descent[1567]", TextGerman = "Und er sah ihn ja ein anderes Mal herabkommen," });
                        surah53.VerseList.Add(new Verse { Number = 14, TextBosnian = "Kod Sidretul-Muntehaa,", TextEnglish = "At the Lote Tree of the Utmost Boundary -", TextGerman = "beim Sidr-Baum des Endziels," });
                        surah53.VerseList.Add(new Verse { Number = 15, TextBosnian = "gdje je džennetsko utočište.", TextEnglish = "Near it is the Garden of Refuge [i.e., Paradise] -", TextGerman = "bei dem der Garten der Zuflucht ist." });
                        surah53.VerseList.Add(new Verse { Number = 16, TextBosnian = "Kad je Sidru prekrilo ono što ju je prekrilo,", TextEnglish = "When there covered the Lote Tree that which covered [it].[1568]", TextGerman = "Als den Sidr-Baum überdeckte, was (ihn) überdeckte," });
                        surah53.VerseList.Add(new Verse { Number = 17, TextBosnian = "pogled mu nije skrenuo i nije prekoračio.", TextEnglish = "The sight [of the Prophet (ﷺ)] did not swerve, nor did it transgress [its limit].", TextGerman = "da wich der Blick nicht ab, noch überschritt er das Maß." });
                        surah53.VerseList.Add(new Verse { Number = 18, TextBosnian = "Vidio je najveličanstvenije znakove svoga Gospodara.", TextEnglish = "He certainly saw of the greatest signs of his Lord.", TextGerman = "Wahrlich, er sah von den Zeichen seines Herrn die größten." });
                        surah53.VerseList.Add(new Verse { Number = 19, TextBosnian = "Vidjeste li Lata i Uzzaa?!", TextEnglish = "So have you considered al-Lāt and al-ʿUzzā?", TextGerman = "Was meint ihr wohl zu al-Lat und al-’Uzza" });
                        surah53.VerseList.Add(new Verse { Number = 20, TextBosnian = "I Menata trećeg, posljednjeg od njih?!", TextEnglish = "And Manāt, the third - the other one?[1569]", TextGerman = "und auch zu Manat, dieser anderen, der dritten?" });
                        surah53.VerseList.Add(new Verse { Number = 21, TextBosnian = "Zar za vas da su sinovi, a za Njega kćeri?!", TextEnglish = "Is the male for you and for Him the female?", TextGerman = "Ist denn für euch das, was männlich ist, und für Ihn das, was weiblich ist (bestimmt)?" });
                        surah53.VerseList.Add(new Verse { Number = 22, TextBosnian = "To bi tada bila podjela nepravedna.", TextEnglish = "That, then, is an unjust division.[1570]", TextGerman = "Das wäre dann eine ungerechte Verteilung." });
                        surah53.VerseList.Add(new Verse { Number = 23, TextBosnian = "To su samo imena koja ste im vi i preci vaši nadjenuli; Allah o njima nikakav dokaz nije poslao; oni se povode samo za pretpostavkama i onim za čim duše žude, a već im dođe od Gospodara njihova Uputa.", TextEnglish = "They are not but [mere] names you have named them - you and your forefathers - for which Allāh has sent down no authority. They follow not except assumption and what [their] souls desire, and there has already come to them from their Lord guidance.", TextGerman = "Das sind nur Namen, die ihr genannt habt, ihr und eure Väter, für die Allah (aber) keine Ermächtigung herabgesandt hat. Sie folgen nur Vermutungen und dem, wozu sie selbst neigen, wo doch bereits von ihrem Herrn die Rechtleitung zu ihnen gekommen ist." });
                        surah53.VerseList.Add(new Verse { Number = 24, TextBosnian = "I zar da čovjek sve što poželi dobije?!", TextEnglish = "Or is there for man whatever he wishes?", TextGerman = "Oder soll (etwa) der Mensch haben, was (immer) er (sich) wünscht?" });
                        surah53.VerseList.Add(new Verse { Number = 25, TextBosnian = "Pa, Allahu pripada i prvi i drugi svijet.", TextEnglish = "Rather, to Allāh belongs the Hereafter and the first [life].", TextGerman = "So gehört Allah das Jenseits und das Diesseits." });
                        surah53.VerseList.Add(new Verse { Number = 26, TextBosnian = "A koliko na nebesima ima meleka čije zauzimanje nikome neće biti od koristi, sve dok Allah to ne dozvoli onome kome On hoće i u korist onoga kojim je zadovoljan.", TextEnglish = "And how many angels there are in the heavens whose intercession will not avail at all except [only] after Allāh has permitted [it] to whom He wills and approves.", TextGerman = "Und wie viele Engel gibt es in den Himmeln, deren Fürsprache nichts nützt, außer nachdem Allah es erlaubt hat, wem Er will und wer Sein Wohlgefallen findet." });
                        surah53.VerseList.Add(new Verse { Number = 27, TextBosnian = "Oni koji ne vjeruju u ahiret nazivaju meleke imenima ženskim.", TextEnglish = "Indeed, those who do not believe in the Hereafter name the angels female names,", TextGerman = "Diejenigen, die an das Jenseits nicht glauben, benennen die Engel fürwahr mit Namen, wie weibliche Wesen benannt werden." });
                        surah53.VerseList.Add(new Verse { Number = 28, TextBosnian = "A o tome ništa ne znaju, slijede samo pretpostavke, a pretpostavka Istini baš nimalo ne koristi.", TextEnglish = "And they have thereof no knowledge. They follow not except assumption, and indeed, assumption avails not against the truth at all.", TextGerman = "Sie haben jedoch kein Wissen hierüber. Sie folgen nur Vermutungen; die Vermutungen nützen aber nichts gegenüber der Wahrheit." });
                        surah53.VerseList.Add(new Verse { Number = 29, TextBosnian = "Pa, ti se okani onoga ko se od Opomene Naše okrenuo i koji samo život dunjalučki želi.", TextEnglish = "So turn away from whoever turns his back on Our message and desires not except the worldly life.", TextGerman = "Laß nun ab von jemandem, der sich von Unserer Ermahnung abkehrt und nur das diesseitige Leben will." });
                        surah53.VerseList.Add(new Verse { Number = 30, TextBosnian = "To je vrhunac njihova znanja - Gospodar tvoj najbolje zna one koji su skrenuli s Njegova puta i On najbolje zna one koji su na Pravom putu.", TextEnglish = "That is their sum of knowledge. Indeed, your Lord is most knowing of who strays from His way, and He is most knowing of who is guided.", TextGerman = "Das ist ihr erreichter Wissensstand. Gewiß, dein Herr kennt sehr wohl wer von Seinem Weg abirrt, und Er kennt sehr wohl die Rechtgeleiteten." });
                        surah53.VerseList.Add(new Verse { Number = 31, TextBosnian = "Allahovo je sve što je na nebesima i što je na Zemlji, da bi, prema onome kako su radili, kaznio one koji rade zlo, a najljepšom nagradom nagradio one koji čine dobro,", TextEnglish = "And to Allāh belongs whatever is in the heavens and whatever is in the earth - that He may recompense those who do evil with [the penalty of] what they have done and recompense those who do good with the best [reward] -", TextGerman = "Allah gehört (alles), was in den Himmeln und was auf der Erde ist, auf daß Er denjenigen, die Böses tun, das vergelte, was sie tun, und daß Er denjenigen, die Gutes tun, mit dem (Aller)besten vergelte." });
                        surah53.VerseList.Add(new Verse { Number = 32, TextBosnian = "one koji se klone velikih grijeha i razvrata, a one bezazlene On će oprostiti, jer Gospodar tvoj, zaista, neizmjerno prašta, On najbolje zna sve o vama, otkad vas je stvorio od zemlje i od kad ste bili zameci u utrobama majki vaših; zato se ne hvališite-On najbolje zna svakog onoga koji se grijeha kloni.", TextEnglish = "Those who avoid the major sins and immoralities, only [committing] slight ones. Indeed, your Lord is vast in forgiveness. He was most knowing of you when He produced you from the earth and when you were fetuses in the wombs of your mothers. So do not claim yourselves to be pure; He is most knowing of who fears Him.", TextGerman = "Diejenigen, die schwerwiegende Sünden und Abscheulichkeiten meiden, außer leichten Verfehlungen... Gewiß, dein Herr ist Allumfassend in (Seiner) Vergebung. Er weiß sehr wohl über euch Bescheid, als Er euch aus der Erde hervorgebracht hat und als ihr Keimlinge in den Leibern eurer Mütter gewesen seid. So erklärt nicht euch selbst für lauter. Er weiß sehr wohl, wer gottesfürchtig ist." });
                        surah53.VerseList.Add(new Verse { Number = 33, TextBosnian = "Šta misliš o onome ko se okrenuo,", TextEnglish = "Have you seen the one who turned away", TextGerman = "Was meinst du wohl zu demjenigen, der sich abkehrt" });
                        surah53.VerseList.Add(new Verse { Number = 34, TextBosnian = "i malo udijelio, a onda posve prestao udjeljivati.", TextEnglish = "And gave a little and [then] refrained?", TextGerman = "und (nur) wenig gibt und dann (ganz) aussetzt?" });
                        surah53.VerseList.Add(new Verse { Number = 35, TextBosnian = "Zar on ima znanje o onome što je čulima nedokučivo, pa vidi?!", TextEnglish = "Does he have knowledge of the unseen, so he sees?[1571]", TextGerman = "Besitzt er (etwa) Wissen über das Verborgene, daß er sehen könnte?" });
                        surah53.VerseList.Add(new Verse { Number = 36, TextBosnian = "Zar on nije obaviješten o onom što se nalazi u listovima Musaovim", TextEnglish = "Or has he not been informed of what was in the scriptures of Moses", TextGerman = "Oder wurde ihm nicht kundgetan, was auf den Blättern Musas steht" });
                        surah53.VerseList.Add(new Verse { Number = 37, TextBosnian = "i Ibrahimovim - koji je obaveze potpuno ispunjavao", TextEnglish = "And [of] Abraham, who fulfilled [his obligations] -", TextGerman = "und Ibrahims, der (seine Pflichten) erfüllte?" });
                        surah53.VerseList.Add(new Verse { Number = 38, TextBosnian = "da ni jedan grešnik tuđe grijehe neće nositi,", TextEnglish = "That no bearer of burdens will bear the burden of another", TextGerman = "(Nämlich,) daß keine lasttragende (Seele) die Last einer anderen auf sich nehmen wird" });
                        surah53.VerseList.Add(new Verse { Number = 39, TextBosnian = "i da je čovjekovo samo ono što sam uradi,", TextEnglish = "And that there is not for man except that [good] for which he strives", TextGerman = "und daß es für den Menschen nichts anderes geben wird als das, worum er sich (selbst) bemüht," });
                        surah53.VerseList.Add(new Verse { Number = 40, TextBosnian = "i da će trud njegov sigurno primijećen biti,", TextEnglish = "And that his effort is going to be seen -", TextGerman = "daß sein Bemühen gesehen werden wird," });
                        surah53.VerseList.Add(new Verse { Number = 41, TextBosnian = "i da će prema njemu u potpunosti nagrađen ili kažnjen biti.", TextEnglish = "Then he will be recompensed for it with the fullest recompense -", TextGerman = "daß ihm hierauf nach vollem Maß vergolten wird" });
                        surah53.VerseList.Add(new Verse { Number = 42, TextBosnian = "da će se Gospodaru tvome ponovno vratiti,", TextEnglish = "And that to your Lord is the finality", TextGerman = "und daß zu deinem Herrn das Endziel sein wird" });
                        surah53.VerseList.Add(new Verse { Number = 43, TextBosnian = "i da On na smijeh i na plač navodi,", TextEnglish = "And that it is He who makes [one] laugh and weep", TextGerman = "und daß Er es ist, Der lachen und weinen läßt," });
                        surah53.VerseList.Add(new Verse { Number = 44, TextBosnian = "i da On usmrćuje i oživljava,", TextEnglish = "And that it is He who causes death and gives life", TextGerman = "und daß Er es ist, Der sterben läßt und wieder lebendig macht," });
                        surah53.VerseList.Add(new Verse { Number = 45, TextBosnian = "i da On par, muško i žensko, stvara,", TextEnglish = "And that He creates the two mates - the male and female -", TextGerman = "daß Er beide Teile des Paares, das Männliche und das Weibliche, erschafft" });
                        surah53.VerseList.Add(new Verse { Number = 46, TextBosnian = "od kapi sjemena kad se izbaci,", TextEnglish = "From a sperm-drop when it is emitted", TextGerman = "aus einem Samentropfen, wenn er ausgespritzt wird," });
                        surah53.VerseList.Add(new Verse { Number = 47, TextBosnian = "i da će ih On ponovno oživiti,", TextEnglish = "And that [incumbent] upon Him is the other [i.e., next] creation.", TextGerman = "und daß Ihm die letzte Schöpfung obliegt" });
                        surah53.VerseList.Add(new Verse { Number = 48, TextBosnian = "i da On daje bogatstvo i moć da stječu,", TextEnglish = "And that it is He who enriches and suffices", TextGerman = "und daß Er es ist, Der reich macht und Der genügsam macht," });
                        surah53.VerseList.Add(new Verse { Number = 49, TextBosnian = "i da je On Sirijusa Gospodar,", TextEnglish = "And that it is He who is the Lord of Sirius[1572].", TextGerman = "und daß Er es ist, Der der Herr des Hundssterns ist," });
                        surah53.VerseList.Add(new Verse { Number = 50, TextBosnian = "i da je On drevni narod Ad uništio,", TextEnglish = "And that He destroyed the first [people of] ʿAad.", TextGerman = "daß Er die früheren ’Ad vernichtete" });
                        surah53.VerseList.Add(new Verse { Number = 51, TextBosnian = "i Semud, i da nikog nije poštedio,", TextEnglish = "And Thamūd - and He did not spare [them] -", TextGerman = "und (auch) die Tamud; so ließ Er nichts übrig;" });
                        surah53.VerseList.Add(new Verse { Number = 52, TextBosnian = "i još prije Nuhov narod, koji je najokrutniji i najobjesniji bio,", TextEnglish = "And the people of Noah before. Indeed, it was they who were [even] more unjust and oppressing.", TextGerman = "und zuvor das Volk Nuhs - gewiß, sie waren ja noch ungerechter und übermäßiger (in ihrem Frevel)." });
                        surah53.VerseList.Add(new Verse { Number = 53, TextBosnian = "i prevrnuta naselja On je na zemlju sručio,", TextEnglish = "And the overturned towns[1573] He hurled down.", TextGerman = "Und die umgestürzte Stadt ließ Er stürzen." });
                        surah53.VerseList.Add(new Verse { Number = 54, TextBosnian = "i snašlo ih je ono što ih je snašlo.", TextEnglish = "And covered them by that which He covered.[1574]", TextGerman = "Und so überdeckte sie, was (sie dann) überdeckte." });
                        surah53.VerseList.Add(new Verse { Number = 55, TextBosnian = "Pa, u koju blagodat Gospodara svoga ti još sumnjaš?", TextEnglish = "Then which of the favors of your Lord do you doubt?", TextGerman = "Welche der Wohltaten deines Herrn willst du nun bestreiten?" });
                        surah53.VerseList.Add(new Verse { Number = 56, TextBosnian = "Ovo je upozoritelj iz reda prijašnjih upozoritelja:", TextEnglish = "This [Prophet (ﷺ)] is a warner from [i.e., like] the former warners.", TextGerman = "Das ist ein Warner wie die früheren Warner." });
                        surah53.VerseList.Add(new Verse { Number = 57, TextBosnian = "Bliski čas se približava,", TextEnglish = "The Approaching Day has approached.", TextGerman = "Es steht bevor die immer näher kommende (Stunde des Gerichts);" });
                        surah53.VerseList.Add(new Verse { Number = 58, TextBosnian = "osim Allaha niko ga ne može otkriti, niti otkloniti!", TextEnglish = "Of it, [from those] besides Allāh, there is no remover.", TextGerman = "niemand außer Allah kann sie beheben." });
                        surah53.VerseList.Add(new Verse { Number = 59, TextBosnian = "Pa zar se ovom govoru iščuđavate,", TextEnglish = "Then at this statement do you wonder?", TextGerman = "Wundert ihr euch denn über diese Aussage" });
                        surah53.VerseList.Add(new Verse { Number = 60, TextBosnian = "i smijete se, a ne plačete,", TextEnglish = "And you laugh and do not weep", TextGerman = "und lacht ihr, und weint ihr nicht," });
                        surah53.VerseList.Add(new Verse { Number = 61, TextBosnian = "gordo dignutih glava?!", TextEnglish = "While you are proudly sporting?[1575]", TextGerman = "und seid ihr noch belustigt?" });
                        surah53.VerseList.Add(new Verse { Number = 62, TextBosnian = "Zato, na sedždu Allahu padajte i Njemu ibadet činite.", TextEnglish = "So prostrate to Allāh and worship [Him].", TextGerman = "Werft euch doch vor Allah nieder und dient (Ihm)." });
                        result = surah53;
                        break;
                    #endregion

                    // 
                    #region
                    case 54:
                        Surah surah54 = new Surah(); surah54.Name = Constants.Surah54Name; surah54.Number = 54; surah54.VerseList = new List<Verse>();
                        surah54.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah54.VerseList.Add(new Verse { Number = 1, TextBosnian = "Bliži se Čas i Mjesec se raspolutio!", TextEnglish = "The Hour has come near, and the moon has split [in two].[1577]", TextGerman = "Näher ist die Stunde (des Gerichts) gekommen, und gespalten hat sich der Mond." });
                        surah54.VerseList.Add(new Verse { Number = 2, TextBosnian = "A oni, uvijek kada vide znak, okreću se i govore: 'Magija prolazna.'", TextEnglish = "And if they see a sign [i.e., miracle], they turn away and say, 'Passing magic.'[1578]", TextGerman = "Und wenn sie ein Zeichen sehen, wenden sie sich ab und sagen: Fortdauernde Zauberei." });
                        surah54.VerseList.Add(new Verse { Number = 3, TextBosnian = "Oni ne vjeruju i povode se za hirovima svojim, a sve će kod svoga cilja skončati.", TextEnglish = "And they denied and followed their inclinations. But for every matter is a [time of] settlement.", TextGerman = "Und sie erklären (es) für Lüge und folgen ihren Neigungen. Doch jede Angelegenheit ist festgesetzt." });
                        surah54.VerseList.Add(new Verse { Number = 4, TextBosnian = "I došle su im vijesti u kojima je ono što treba da ih odvrati", TextEnglish = "And there has already come to them of information that in which there is deterrence.", TextGerman = "Zu ihnen ist doch von den Berichten gekommen, was reichliche Verweisung enthält," });
                        surah54.VerseList.Add(new Verse { Number = 5, TextBosnian = "mudrost savršena - pa kako upozoritelji takvima mogu koristiti?!", TextEnglish = "Extensive wisdom - but warning does not avail [them].", TextGerman = "eine durchdringende Weisheit. Aber was nützen da die Warnungen?" });
                        surah54.VerseList.Add(new Verse { Number = 6, TextBosnian = "Zato se okreni od njih! Na Dan kad ih glasnik pozove na nešto užasno,", TextEnglish = "So leave them, [O Muḥammad]. The Day the Caller[1579] calls to something forbidding,", TextGerman = "So kehre dich von ihnen ab. Am Tag, da der Rufer zu etwas Entsetzlichem ruft," });
                        surah54.VerseList.Add(new Verse { Number = 7, TextBosnian = "oni će oborenih pogleda iz grobova izlaziti, kao skakavci rasuti,", TextEnglish = "Their eyes humbled, they will emerge from the graves as if they were locusts spreading,", TextGerman = "werden sie mit gedemütigten Blicken aus den Gräbern herauskommen wie ausschwärmende Heuschrecken," });
                        surah54.VerseList.Add(new Verse { Number = 8, TextBosnian = "I žureći ka glasniku, nevjernici će govoriti: 'Ovo je težak dan.'", TextEnglish = "Racing ahead toward the Caller. The disbelievers will say, 'This is a difficult Day.'", TextGerman = "zu dem Rufer hastend. Die Ungläubigen werden sagen: Das ist ein schwerer Tag." });
                        surah54.VerseList.Add(new Verse { Number = 9, TextBosnian = "Poricao je Nuhov narod prije njih pa su roba Našeg u laž utjerivali, govoreći: 'Luđak!' - i Nuh je odbijen bio.", TextEnglish = "The people of Noah denied before them, and they denied Our servant and said, 'A madman,' and he was repelled.", TextGerman = "Vor ihnen (schon) bezichtigte das Volk Nuhs (ihren Gesandten) der Lüge. Sie bezichtigten Unseren Diener der Lüge und sagten: Ein Besessener. Und er wurde (von ihnen) gescholten." });
                        surah54.VerseList.Add(new Verse { Number = 10, TextBosnian = "I on je Gospodara svoga zamolio: 'Ja sam pobijeđen, pa Ti pomozi!'", TextEnglish = "So he invoked his Lord, 'Indeed, I am overpowered, so help.'", TextGerman = "Da rief er seinen Herrn an: Ich bin überwältigt, so leiste Du (mir) Hilfe." });
                        surah54.VerseList.Add(new Verse { Number = 11, TextBosnian = "I Mi smo kapije nebeske pootvarali vodi koja je neprestano lila,", TextEnglish = "Then We opened the gates of the heaven with rain pouring down", TextGerman = "Da öffneten Wir die Tore des Himmels mit niederströmendem Wasser" });
                        surah54.VerseList.Add(new Verse { Number = 12, TextBosnian = "i učinili da iz zemlje izvori provru, i vode su se sastale po onome što je već bilo određeno,", TextEnglish = "And caused the earth to burst with springs, and the waters met for a matter already predestined.", TextGerman = "und ließen aus der Erde Quellen hervorströmen; so traf das Wasser zu einer bereits festgesetzten Angelegenheit zusammen." });
                        surah54.VerseList.Add(new Verse { Number = 13, TextBosnian = "a njega smo nosili na lađi od dasaka i klinova sagrađenoj,", TextEnglish = "And We carried him on a [construction of] planks and nails,", TextGerman = "Und Wir trugen ihn auf einem Schiff aus Planken und Nägeln," });
                        surah54.VerseList.Add(new Verse { Number = 14, TextBosnian = "koja je plovila pred Našim očima, a nagrada je to bila za onoga koji je porican bio.", TextEnglish = "Sailing under Our observation as reward for he who had been denied.", TextGerman = "das vor Unseren Augen dahinfuhr: (Dies) als Lohn für jemanden, der immer verleugnet worden war." });
                        surah54.VerseList.Add(new Verse { Number = 15, TextBosnian = "I Mi to ostavismo kao znak - pa ima li ikoga ko bi se prisjetio?", TextEnglish = "And We left it as a sign, so is there any who will remember?", TextGerman = "Und Wir ließen es ja als Zeichen zurück. Gibt es aber jemanden, der bedenkt?" });
                        surah54.VerseList.Add(new Verse { Number = 16, TextBosnian = "O, kakva je bila kazna Moja i pomoć upozoriteljima Mojim!", TextEnglish = "And how [severe] were My punishment and warning.[1580]", TextGerman = "Wie waren da Meine Strafe und Meine Warnungen!" });
                        surah54.VerseList.Add(new Verse { Number = 17, TextBosnian = "A Mi smo Kur'an olakšali za opomenu, pa ima li ikoga ko bi se prisjetio?", TextEnglish = "And We have certainly made the Qur’ān easy for remembrance, so is there any who will remember?", TextGerman = "Und Wir haben den Qur’an ja leicht zum Bedenken gemacht. Aber gibt es jemanden, der bedenkt?" });
                        surah54.VerseList.Add(new Verse { Number = 18, TextBosnian = "Poricao je Ad, pa kakva je bila kazna Moja i pomoć upozoriteljima Mojim!", TextEnglish = "ʿAad denied; and how [severe] were My punishment and warning.", TextGerman = "Die ’Ad bezichtigten (ihren Gesandten) der Lüge. Wie waren da Meine Strafe und Meine Warnungen!" });
                        surah54.VerseList.Add(new Verse { Number = 19, TextBosnian = "Jednog kobnog dana poslali smo na njih leden vjetar koji je neprestano puhao", TextEnglish = "Indeed, We sent upon them a screaming wind on a day of continuous misfortune,", TextGerman = "Wir sandten gegen sie ja einen eiskalten Wind an einem Tag fortdauernden Unheils," });
                        surah54.VerseList.Add(new Verse { Number = 20, TextBosnian = "i ljude dizao, kao da su palmina stabla iščupana,", TextEnglish = "Extracting the people[1581] as if they were trunks of palm trees uprooted.", TextGerman = "der die Menschen fortnahm, als wären sie Stämme entwurzelter Palmen." });
                        surah54.VerseList.Add(new Verse { Number = 21, TextBosnian = "i kakve su bile kazne Moje i pomoć upozoriteljima Mojim!", TextEnglish = "And how [severe] were My punishment and warning.", TextGerman = "Wie waren da Meine Strafe und Meine Warnungen!" });
                        surah54.VerseList.Add(new Verse { Number = 22, TextBosnian = "A Mi smo Kur'an lahkim za opomenu učinili, pa ima li koga ko bi se prisjetio?", TextEnglish = "And We have certainly made the Qur’ān easy for remembrance, so is there any who will remember?", TextGerman = "Und Wir haben den Qur’an ja leicht zum Bedenken gemacht. Aber gibt es jemanden, der bedenkt?" });
                        surah54.VerseList.Add(new Verse { Number = 23, TextBosnian = "I Semud je upozoritelje poricao.", TextEnglish = "Thamūd denied the warning.", TextGerman = "Die Tamud erklärten die Warnungen für Lüge." });
                        surah54.VerseList.Add(new Verse { Number = 24, TextBosnian = "Zar da slijedimo jednog od nas!', govorili su. 'Tada bismo, uistinu, bili u zabludi i muci.", TextEnglish = "And said, 'Is it one human being[1582] among us that we should follow? Indeed, we would then be in error and madness.", TextGerman = "Sie sagten: Sollen wir denn einem menschlichen Wesen von uns, einem einzelnen, folgen? Dann befänden wir uns wahrlich im Irrtum und Wahnsinn." });
                        surah54.VerseList.Add(new Verse { Number = 25, TextBosnian = "Zar baš njemu, između nas, da bude poslana Objava?! Ne, on je lažljivac oholi!'", TextEnglish = "Has the message been sent down upon him from among us? Rather, he is an insolent liar.'", TextGerman = "Ist die Ermahnung gerade ihm aus unserer Mitte offenbart worden? Nein! Vielmehr ist er ein selbstgefälliger Lügner." });
                        surah54.VerseList.Add(new Verse { Number = 26, TextBosnian = "Saznat će oni sutra ko je lažljivac oholi!", TextEnglish = "They will know tomorrow who is the insolent liar.", TextGerman = "Sie werden morgen erfahren, wer der selbstgefällige Lügner ist." });
                        surah54.VerseList.Add(new Verse { Number = 27, TextBosnian = "Mi ćemo poslati kamilu da bismo ih iskušali, pa pričekaj ih i budi strpljiv.", TextEnglish = "Indeed, We are sending the she-camel as trial for them, so watch them and be patient.[1583]", TextGerman = "Wir werden die Kamelstute senden als Versuchung für sie. So warte mit ihnen ab und sei beharrlich." });
                        surah54.VerseList.Add(new Verse { Number = 28, TextBosnian = "upozori ih da će se voda između njih i nje dijeliti, svakom obroku pristupit će onaj čiji je red!", TextEnglish = "And inform them that the water is shared between them,[1584] each [day of] drink attended [by turn].", TextGerman = "Und tu ihnen kund, daß das Wasser zwischen ihnen (und der Kamelstute) zu teilen ist. Jeder Trinkanteil soll (dann abwechselnd) wahrgenommen werden." });
                        surah54.VerseList.Add(new Verse { Number = 29, TextBosnian = "Ali oni pozvaše jednog od svojih, pa se on spremi i prekla je.", TextEnglish = "But they called their companion,[1585] and he dared[1586] and hamstrung [her].", TextGerman = "Da riefen sie ihren Gefährten her. Er griff zu und schnitt dann (der Kamelstute) die Sehnen durch." });
                        surah54.VerseList.Add(new Verse { Number = 30, TextBosnian = "O, kakva je samo bila kazna Moja i pomoć upozoriteljima Mojim!", TextEnglish = "And how [severe] were My punishment and warning.", TextGerman = "Wie waren da Meine Strafe und Meine Warnungen!" });
                        surah54.VerseList.Add(new Verse { Number = 31, TextBosnian = "Mi poslasmo na njih jedan jedini krik, i oni postadoše poput zdrobljenog suhog lišća.", TextEnglish = "Indeed, We sent upon them one shriek [i.e., blast from the sky], and they became like the dry twig fragments of an [animal] pen.", TextGerman = "Wir sandten ja gegen sie einen einzigen Schrei, da waren sie (sogleich) wie das vertrocknete Zeug von Viehgehegen." });
                        surah54.VerseList.Add(new Verse { Number = 32, TextBosnian = "A Mi smo Kur'an olakšali za opomenu, pa ima li ikoga ko bi se prisjetio?", TextEnglish = "And We have certainly made the Qur’ān easy for remembrance, so is there any who will remember?", TextGerman = "Und Wir haben den Qur’an ja leicht zum Bedenken gemacht. Aber gibt es jemanden, der bedenkt?" });
                        surah54.VerseList.Add(new Verse { Number = 33, TextBosnian = "Narod Lutov poricao je upozoritelje i upozorenja.", TextEnglish = "The people of Lot denied the warning.", TextGerman = "Das Volk Luts erklärte die Warnungen für Lüge." });
                        surah54.VerseList.Add(new Verse { Number = 34, TextBosnian = "Na njih vjetar, pun pijeska, poslasmo, samo ne na Lutovu porodicu, nju u svitanje spasismo", TextEnglish = "Indeed, We sent upon them a storm of stones, except the family of Lot - We saved them before dawn.", TextGerman = "Wir sandten ja gegen sie einen Sturm von Steinchen, außer den Angehörigen Luts; Wir erretteten sie im letzten Teil der Nacht" });
                        surah54.VerseList.Add(new Verse { Number = 35, TextBosnian = "iz milosti Naše. Eto, tako Mi nagrađujemo one koji zahvaljuju.", TextEnglish = "As favor from Us. Thus do We reward he who is grateful.", TextGerman = "aus Gunst von Uns. So vergelten Wir, wer dankbar ist." });
