                        surah54.VerseList.Add(new Verse { Number = 40, TextBosnian = "A Mi smo Kur'an olakšali za opomenu, pa ima li ikoga ko bi se prisjetio?!", TextEnglish = "And We have certainly made the Qur’ān easy for remembrance, so is there any who will remember?", TextGerman = "Und Wir haben den Qur’an ja leicht zum Bedenken gemacht. Aber gibt es jemanden, der bedenkt?" });
                        surah54.VerseList.Add(new Verse { Number = 41, TextBosnian = "I faraonovoj porodici upozoritelji i upozorenja dolaziše,", TextEnglish = "And there certainly came to the people of Pharaoh warning.", TextGerman = "Und (auch) zu Fir’auns Leuten kamen ja die Warnungen." });
                        surah54.VerseList.Add(new Verse { Number = 42, TextBosnian = "ali oni porekoše sve znakove Naše, pa ih Mi kaznismo onako kako kažnjava Silni i Apsulutno Moćni.", TextEnglish = "They denied Our signs, all of them, so We seized them with a seizure of one Exalted in Might and Perfect in Ability.", TextGerman = "Sie erklärten alle Unsere Zeichen für Lüge, und da ergriffen Wir sie mit dem Griff eines Unüberwindlichen und Allmächtigen." });
                        surah54.VerseList.Add(new Verse { Number = 43, TextBosnian = "Da li su nevjernici vaši bolji od njih, ili vi u knjigama nebeskim imate kakvu garanciju?!", TextEnglish = "Are your disbelievers better than those [former ones], or have you immunity in the scriptures?", TextGerman = "Sind etwa eure Ungläubigen besser als jene da? Oder gibt es für euch einen Freispruch in den Schriften?" });
                        surah54.VerseList.Add(new Verse { Number = 44, TextBosnian = "Zar ovi da govore: 'Mi smo skup nepobjedivi!'", TextEnglish = "Or do they say, 'We are an assembly supporting [each other]'?", TextGerman = "Oder sagen sie: Wir halten zusammen und werden siegen?" });
                        surah54.VerseList.Add(new Verse { Number = 45, TextBosnian = "Skup će sigurno poražen biti, a oni će se u bijeg dati!", TextEnglish = "[Their] assembly will be defeated, and they will turn their backs [in retreat].[1587]", TextGerman = "Die Ansammlung wird gewiß besiegt werden, und sie werden den Rücken kehren." });
                        surah54.VerseList.Add(new Verse { Number = 46, TextBosnian = "Međutim, Čas oživljenja im je rok, a Čas oživljenja je užasniji i gorči.", TextEnglish = "But the Hour is their appointment [for due punishment], and the Hour is more disastrous and more bitter.", TextGerman = "Nein! Vielmehr ist die Stunde (des Gerichts) der ihnen versprochene Zeitpunkt. Und die Stunde ist noch schrecklicher und bitterer." });
                        surah54.VerseList.Add(new Verse { Number = 47, TextBosnian = "Prestupnici su, doista, u zabludi i patnji bili, i bit će", TextEnglish = "Indeed, the criminals are in error and madness.[1588].", TextGerman = "Gewiß, die Übeltäter befinden sich im Irrtum und Wahnsinn." });
                        surah54.VerseList.Add(new Verse { Number = 48, TextBosnian = "na Dan kada budu u Vatru odvučeni, s licima dolje okrenutim: 'Iskusite dodir Ognja!'", TextEnglish = "The Day they are dragged into the Fire on their faces [it will be said], 'Taste the touch of Saqar.'[1589]", TextGerman = "Am Tag, da sie auf ihren Gesichtern ins (Höllen)feuer gezerrt werden: Kostet die Berührung der Sengenden." });
                        surah54.VerseList.Add(new Verse { Number = 49, TextBosnian = "Mi doista sve s određenjem stvaramo,", TextEnglish = "Indeed, all things We created with predestination.", TextGerman = "Gewiß, Wir haben alles in (bestimmtem) Maß erschaffen." });
