                        surah54.VerseList.Add(new Verse { Number = 51, TextBosnian = "A Mi smo već vama slične uništili, pa ima li ikoga ko bi se prisjetio?!", TextEnglish = "And We have already destroyed your kinds,[1590] so is there any who will remember?", TextGerman = "Und Wir haben doch bereits manche Lager gleich euch vernichtet. Aber gibt es jemanden, der bedenkt?" });
                        surah54.VerseList.Add(new Verse { Number = 52, TextBosnian = "I sve što su uradili u listovima je,", TextEnglish = "And everything they did is in written records.", TextGerman = "Alles, was sie getan haben, (steht) in den Schriften." });
