                        surah55.VerseList.Add(new Verse { Number = 33, TextBosnian = "O družine džina i ljudi, ako možete kroz slojeve nebesa i Zemlje prodrijeti, prodrite! Moći ćete prodrijeti jedino uz veliku moć!", TextEnglish = "O company of jinn and mankind, if you are able to pass beyond the regions of the heavens and the earth, then pass. You will not pass except by authority [from Allāh].", TextGerman = "O ihr Gesellschaft der Ginn und der Menschen, wenn ihr aus den Bezirken der Himmel und der Erde hinausdringen könnt, so dringt doch heraus. Ihr werdet nicht herausdringen, außer mit einer Ermächtigung." });
