                        surah56.VerseList.Add(new Verse { Number = 66, TextBosnian = "Mi smo, doista, oštećeni,", TextEnglish = "[Saying], 'Indeed, we are [now] in debt;", TextGerman = "Wir sind wahrlich mit Schulden belastet." });
                        surah56.VerseList.Add(new Verse { Number = 67, TextBosnian = "čak smo svega lišeni!'", TextEnglish = "Rather, we have been deprived.'", TextGerman = "Nein! Vielmehr ist uns alles verwehrt worden." });
                        surah56.VerseList.Add(new Verse { Number = 68, TextBosnian = "Kažite vi Meni: vodu koju pijete -", TextEnglish = "And have you seen the water that you drink?", TextGerman = "Was meint ihr denn zu dem Wasser, das ihr trinkt?" });
                        surah56.VerseList.Add(new Verse { Number = 69, TextBosnian = "da li je vi ili Mi iz oblaka spuštamo?", TextEnglish = "Is it you who brought it down from the clouds, or is it We who bring it down?", TextGerman = "Seid ihr es etwa, die es von den Wolken herabkommen lassen, oder sind doch nicht Wir es, die herabkommen lassen?" });
                        surah56.VerseList.Add(new Verse { Number = 70, TextBosnian = "Ako želimo, možemo je slanom učiniti - pa zašto niste zahvalni?", TextEnglish = "If We willed, We could make it bitter, so why are you not grateful?", TextGerman = "Wenn Wir wollten, könnten Wir es (auf der Zunge) brennend machen. Wenn ihr doch dankbar sein würdet!" });
                        surah56.VerseList.Add(new Verse { Number = 71, TextBosnian = "Kažite vi Meni: vatru koju palite -", TextEnglish = "And have you seen the fire that you ignite?", TextGerman = "Was meint ihr denn zu dem Feuer, das ihr (durch Reiben) zündet?" });
                        surah56.VerseList.Add(new Verse { Number = 72, TextBosnian = "da li drvo za nju Mi stvaramo ili vi?", TextEnglish = "Is it you who produced its tree, or are We the producer?", TextGerman = "Seid ihr es etwa, die den dazu nötigen Baum entstehen lassen, oder sind nicht doch Wir es, die entstehen lassen?" });
                        surah56.VerseList.Add(new Verse { Number = 73, TextBosnian = "Mi činimo da ona podsjeća i da bude korisna putnicima kada konače;", TextEnglish = "We have made it a reminder[1616] and provision for the travelers,[1617]", TextGerman = "Wir haben es ja zur Erinnerung (an das Höllenfeuer) und als Nießbrauch für die Wanderer gemacht." });
                        surah56.VerseList.Add(new Verse { Number = 74, TextBosnian = "zato slavi i veličaj ime Gospodara svoga Veličanstvenog!", TextEnglish = "So exalt the name of your Lord, the Most Great.", TextGerman = "Darum preise den Namen deines Allgewaltigen Herrn." });
                        surah56.VerseList.Add(new Verse { Number = 75, TextBosnian = "I kunem se mjestima postepenog spuštanja Kur'ana,", TextEnglish = "Then I swear by the setting of the stars,[1618]", TextGerman = "Nein! Ich schwöre bei den Standorten der Sterne" });
                        surah56.VerseList.Add(new Verse { Number = 76, TextBosnian = "a to je, da znate, zakletva velika!", TextEnglish = "And indeed, it is an oath - if you could know - [most] great.", TextGerman = "- und das ist wahrlich, wenn ihr (nur) wüßtet, ein gewaltiger Schwur:" });
                        surah56.VerseList.Add(new Verse { Number = 77, TextBosnian = "On je, zaista, Kur'an plemeniti", TextEnglish = "Indeed, it is a noble Qur’ān.", TextGerman = "Das ist wahrlich ein ehrwürdiger Qur’an" });
                        surah56.VerseList.Add(new Verse { Number = 78, TextBosnian = "u Knjizi brižljivo čuvanoj.", TextEnglish = "In a Register well-protected;[1619].", TextGerman = "in einem wohlverwahrten Buch," });
                        surah56.VerseList.Add(new Verse { Number = 79, TextBosnian = "Dodirnuti ga smiju samo oni koji su čisti!", TextEnglish = "None touch it except the purified [i.e., the angels].", TextGerman = "das nur diejenigen berühren (dürfen), die vollkommen gereinigt sind;" });
                        surah56.VerseList.Add(new Verse { Number = 80, TextBosnian = "On je objava od Gospodara svjetova,", TextEnglish = "[It is] a revelation from the Lord of the worlds.", TextGerman = "(er ist) eine Offenbarung vom Herrn der Weltenbewohner." });
                        surah56.VerseList.Add(new Verse { Number = 81, TextBosnian = "pa kako ovaj govor omalovažavate,", TextEnglish = "Then is it to this statement that you are indifferent", TextGerman = "Wollt ihr denn über diese Aussage nur schön reden" });
                        surah56.VerseList.Add(new Verse { Number = 82, TextBosnian = "i umjesto zahvalnosti što vam je opskrba darovana, vi u njega ne vjerujete?", TextEnglish = "And make [the thanks for] your provision that you deny [the Provider]?", TextGerman = "und (als Dankbarkeit) für eure Versorgung soll euer Ableugnen sein?" });
                        surah56.VerseList.Add(new Verse { Number = 83, TextBosnian = "A zašto vi, kad duša do guše dopre,", TextEnglish = "Then why, when it [i.e., the soul at death] reaches the throat", TextGerman = "Wenn sie die Kehle erreicht," });
                        surah56.VerseList.Add(new Verse { Number = 84, TextBosnian = "i kad vi budete tada gledali -", TextEnglish = "And you are at that time looking on -", TextGerman = "während ihr dabei zuschaut" });
                        surah56.VerseList.Add(new Verse { Number = 85, TextBosnian = "a Mi smo mu bliži od vas, ali vi ne vidite -", TextEnglish = "And We [i.e., Our angels] are nearer to him than you, but you do not see -", TextGerman = "und Wir ihm näher sind als ihr - aber ihr könnt es nicht sehen -," });
                        surah56.VerseList.Add(new Verse { Number = 86, TextBosnian = "zašto je onda, ako račun nećete polagati,", TextEnglish = "Then why do you not, if you are not to be recompensed,", TextGerman = "könntet ihr dann, wenn ihr doch nicht vor Gericht gestellt werden sollt (, wie ihr behauptet)," });
                        surah56.VerseList.Add(new Verse { Number = 87, TextBosnian = "ne povratite, ako istinu govorite?", TextEnglish = "Bring it back,[1620] if you should be truthful?", TextGerman = "sie zurückbringen, wenn ihr wahrhaftig seid?" });
                        surah56.VerseList.Add(new Verse { Number = 88, TextBosnian = "I ako bude jedan od onih koji su Allahu bliski -", TextEnglish = "And if he [i.e., the deceased] was of those brought near [to Allāh],", TextGerman = "Wenn er nun zu den (Allah) Nahegestellten gehört," });
                        surah56.VerseList.Add(new Verse { Number = 89, TextBosnian = "udobnost i opskrba lijepa i džennetske blagodati pripast će njemu!", TextEnglish = "Then [for him is] rest and bounty and a garden of pleasure.", TextGerman = "dann (wird es für ihn) Ruhe, duftende Pflanzen und einen Garten der Wonne (geben)." });
                        surah56.VerseList.Add(new Verse { Number = 90, TextBosnian = "A ako bude jedan od onih koji su s desne strane,", TextEnglish = "And if he was of the companions of the right,", TextGerman = "Und wenn er nun zu den Gefährten der rechten Seite gehört," });
                        surah56.VerseList.Add(new Verse { Number = 91, TextBosnian = "pa, selam tebi od onih s desne sretne!", TextEnglish = "Then [the angels will say], 'Peace for you; [you are] from the companions of the right.'", TextGerman = "so: Friede sei dir von den Gefährten der rechten Seite." });
                        surah56.VerseList.Add(new Verse { Number = 92, TextBosnian = "A ako bude jedan od onih koji su poricali i u zabludi ostali,", TextEnglish = "But if he was of the deniers [who were] astray,", TextGerman = "Wenn er aber zu den Leugnern, den Irregehenden gehört," });
                        surah56.VerseList.Add(new Verse { Number = 93, TextBosnian = "pa, ključalom vodom bit će ugošćen", TextEnglish = "Then [for him is] accommodation of scalding water", TextGerman = "dann wird ihm eine gastliche Aufnahme aus heißem Wasser (zuteil) werden," });
                        surah56.VerseList.Add(new Verse { Number = 94, TextBosnian = "i u Ognju pržen.", TextEnglish = "And burning in Hellfire.", TextGerman = "und das Erleiden des Höllenbrandes." });
                        surah56.VerseList.Add(new Verse { Number = 95, TextBosnian = "Sama je istina, zbilja, sve ovo!", TextEnglish = "Indeed, this is the true certainty,", TextGerman = "Dies ist wahrlich die reine Gewißheit." });
                        surah56.VerseList.Add(new Verse { Number = 96, TextBosnian = "Pa, slavi i veličaj ime Gospodara svoga Veličanstvenog!", TextEnglish = "So exalt the name of your Lord, the Most Great.", TextGerman = "Darum preise den Namen deines Allgewaltigen Herrn." });
                        result = surah56;
                        break;
                    #endregion

                    // 
                    #region
                    case 57:
                        Surah surah57 = new Surah(); surah57.Name = Constants.Surah57Name; surah57.Number = 57; surah57.VerseList = new List<Verse>();
                        surah57.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah57.VerseList.Add(new Verse { Number = 1, TextBosnian = "Allah slavi i veliča sve što je na nebesima i na Zemlji, i On je Silni i Mudri.", TextEnglish = "Whatever is in the heavens and earth exalts Allāh,[1622] and He is the Exalted in Might, the Wise.", TextGerman = "Es preist Allah (alles), was in den Himmeln und auf der Erde ist. Und Er ist der Allmächtige und Allweise." });
                        surah57.VerseList.Add(new Verse { Number = 2, TextBosnian = "Njegova je vlast na nebesima i na Zemlji; On život i smrt daje, i On nad svime ima moć.", TextEnglish = "His is the dominion of the heavens and earth. He gives life and causes death, and He is over all things competent.", TextGerman = "Ihm gehört die Herrschaft der Himmel und der Erde. Er macht lebendig und läßt sterben. Und Er hat zu allem die Macht." });
                        surah57.VerseList.Add(new Verse { Number = 3, TextBosnian = "On je Prvi i Posljednji, i nad svime Dominantni i u bit svega Pronicljivi; i On zna sve!", TextEnglish = "He is the First[1623] and the Last,[1624] the Ascendant[1625] and the Intimate,[1626] and He is, of all things, Knowing.", TextGerman = "Er ist der Erste und der Letzte, der Offenbare und der Verborgene. Und Er weiß über alles Bescheid." });
                        surah57.VerseList.Add(new Verse { Number = 4, TextBosnian = "On je Onaj Koji je i nebesa i Zemlju za šest dana stvorio, a zatim se nad Aršom uzvisio; On zna šta u zemlju ulazi i šta iz nje izlazi i šta s neba silazi i šta se prema njemu diže, On je s vama gdje god bili, i sve što radite Allah vidi.", TextEnglish = "It is He who created the heavens and earth in six days and then established Himself above the Throne.[1627] He knows what penetrates into the earth and what emerges from it and what descends from the heaven and what ascends therein; and He is with you[1628] wherever you are. And Allāh, of what you do, is Seeing.", TextGerman = "Er ist es, der die Himmel und die Erde in sechs Tagen erschuf und Sich hierauf über den Thron erhob’. Er weiß, was in die Erde eindringt und was aus ihr herauskommt, was vom Himmel herabkommt und was dorthin aufsteigt. Und Er ist mit euch, wo immer ihr auch seid. Und was ihr tut, sieht Allah wohl." });
                        surah57.VerseList.Add(new Verse { Number = 5, TextBosnian = "Njegova je vlast na nebesima i na Zemlji, i Allahu će se sve vratiti!", TextEnglish = "His is the dominion of the heavens and earth. And to Allāh are returned [all] matters.", TextGerman = "Ihm gehört die Herrschaft der Himmel und der Erde. Und zu Allah werden die Angelegenheiten zurückgebracht." });
                        surah57.VerseList.Add(new Verse { Number = 6, TextBosnian = "On uvodi noć u dan i dan u noć, i On zna što se u grudima skriva.", TextEnglish = "He causes the night to enter the day and causes the day to enter the night, and He is Knowing of that within the breasts.", TextGerman = "Er läßt die Nacht in den Tag eindringen und läßt den Tag in die Nacht eindringen. Und Er weiß über das Innerste der Brüste Bescheid." });
                        surah57.VerseList.Add(new Verse { Number = 7, TextBosnian = "Vjerujte u Allaha i Poslanika Njegova i udjeljujte iz onoga što vam je dao da naslijedite, jer one od vas koji budu vjerovali i udjeljivali čeka nagrada velika.", TextEnglish = "Believe in Allāh and His Messenger and spend out of that in which He has made you successive inheritors. For those who have believed among you and spent,[1629] there will be a great reward.", TextGerman = "Glaubt an Allah und Seinen Gesandten und gebt aus von dem, worüber Er euch zu Statthaltern eingesetzt hat. Diejenigen von euch, die glauben und ausgeben, für sie wird es großen Lohn geben." });
                        surah57.VerseList.Add(new Verse { Number = 8, TextBosnian = "Kako da ne vjerujete u Allaha, a Poslanik vas zove da vjerujete u Gospodara svoga - a On je od vas već zavjet uzeo - ako ste vjernici.", TextEnglish = "And why do you not believe in Allāh while the Messenger invites you to believe in your Lord and He has taken your covenant, if you should [truly] be believers?", TextGerman = "Was ist mit euch, daß ihr nicht an Allah glauben wollt, wo doch der Gesandte euch dazu aufruft, an euren Herrn zu glauben, und wo Er bereits ein Abkommen mit euch getroffen hat, wenn ihr gläubig seid?" });
                        surah57.VerseList.Add(new Verse { Number = 9, TextBosnian = "On robu Svome objavljuje jasne ajete, da bi vas iz tmina na svjetlo izveo - a Allah je, uistinu, prema vama milosrdan i milostiv.", TextEnglish = "It is He who sends down upon His Servant [Muḥammad (ﷺ)] verses of clear evidence that He may bring you out from darknesses into the light. And indeed, Allāh is to you Kind and Merciful.", TextGerman = "Er ist es, Der Seinem Diener klare Zeichen offenbart, damit Er euch aus den Finsternissen ins Licht hinausbringt. Und Allah ist wahrlich mit euch Gnädig und Barmherzig." });
                        surah57.VerseList.Add(new Verse { Number = 10, TextBosnian = "kako da ne trošite na Allahovu putu, a Allahu pripada nasljedstvo nebesa i Zemlje?! Nisu jednaki oni među vama koji su davali priloge prije pobjede i lično se borili - oni su na višem stupnju od onih koji su poslije davali priloge i lično se borili, a Allah svima obećava nagradu najljepšu; Allah upotpunosti zna ono što radite.", TextEnglish = "And why do you not spend in the cause of Allāh while to Allāh belongs the heritage of the heavens and the earth? Not equal among you are those who spent before the conquest [of Makkah] and fought [and those who did so after it]. Those are greater in degree than they who spent afterwards and fought. But to all Allāh has promised the best [reward]. And Allāh, of what you do, is Aware.", TextGerman = "Und was ist mit euch, daß ihr nicht auf Allahs Weg ausgeben sollt, wo doch das Erbe der Himmel und der Erde Allah gehört. Nicht gleich sind diejenigen von euch, die vor dem Sieg ausgegeben und gekämpft haben, ... Diese haben eine höhere Rangstufe als diejenigen, die erst nachher ausgegeben und gekämpft haben. Allen aber hat Allah das Beste versprochen. Und Allah ist dessen, was ihr tut, Kundig." });
                        surah57.VerseList.Add(new Verse { Number = 11, TextBosnian = "Ko je taj ko će Allahu lijep zajam dati da bi mu ga On mnogostruko vratio, a uz to će on i nagradu divnu imati?", TextEnglish = "Who is it that would loan Allāh a goodly loan so He will multiply it for him and he will have a noble reward?", TextGerman = "Wer ist es denn, der Allah ein gutes Darlehen gibt? So wird Er es ihm vervielfachen; und für ihn wird es trefflichen Lohn geben." });
                        surah57.VerseList.Add(new Verse { Number = 12, TextBosnian = "Na Dan kad budeš vidio kako se pred vjernicima i vjernicama, i s desne strane njihove, svjetlo njihovo bude kretalo: 'Blago vama danas: džennetske bašče, kroz koje teku rijeke, u kojima ćete vječno boraviti - to je veliki uspjeh!'", TextEnglish = "On the Day you see the believing men and believing women, their light proceeding before them and on their right, [it will be said], 'Your good tidings today are [of] gardens beneath which rivers flow, wherein you will abide eternally.' That is what is the great attainment.", TextGerman = "Am Tag, da du siehst, wie den gläubigen Männern und den gläubigen Frauen ihr Licht vorauseilt und (ebenso) zu ihrer Rechten: Die frohe Botschaft für euch heute: Gärten, durcheilt von Bächen, ewig darin zu bleiben. Das ist der großartige Erfolg." });
                        surah57.VerseList.Add(new Verse { Number = 13, TextBosnian = "Na Dan kada će licemjeri i licemjerke vjernicima govoriti: 'Pričekajte nas da se svjetlom vašim poslužimo!' 'Vratite se natrag, pa drugo svjetlo potražite?', bit će rečeno. I između njih će pregrada postavljena biti koja će vrata imati; unutar nje bit će milost, a izvan nje patnja.", TextEnglish = "On the [same] Day the hypocrite men and hypocrite women will say to those who believed, 'Wait for us that we may acquire some of your light.' It will be said, 'Go back behind you[1630] and seek light.' And a wall will be placed between them with a door, its interior containing mercy, but on the outside of it is torment.", TextGerman = "Am Tag, da die Heuchler und die Heuchlerinnen zu denjenigen, die glauben, sagen: Wartet auf uns, daß wir (unser Licht) von eurem Licht nehmen. Es wird (zu ihnen) gesagt werden: Geht doch nach hinten zurück und sucht dort nach Licht. Da wird zwischen ihnen eine (Schutz)mauer gesetzt mit einem Tor, zu dessen Innenseite die Barmherzigkeit und zu dessen Außenseite, davor, die Strafe ist." });
                        surah57.VerseList.Add(new Verse { Number = 14, TextBosnian = "Zar nismo s vama bili?', dozivat će ih. 'Jeste', odgovorit će, 'ali ste sami sebe zaveli i iščekivali ste i sumnjali ste, i puste želje su vas zavaravale, dok nije došla Allahova odredba, i obmanitelj - šejtan vas o Allahu obmanuo.", TextEnglish = "They [i.e., the hypocrites] will call to them [i.e., the believers], 'Were we not with you?' They will say, 'Yes, but you afflicted yourselves[1631] and awaited [misfortune for us] and doubted, and wishful thinking deluded you until there came the command of Allāh. And the Deceiver [i.e., Satan] deceived you concerning Allāh.", TextGerman = "Sie rufen ihnen zu: Waren wir nicht mit euch? Sie werden sagen: Ja doch, aber ihr habt euch selbst der Versuchung ausgesetzt, ihr habt abgewartet und gezweifelt, und die Wünsche haben euch getäuscht, bis Allahs Befehl kam. Und getäuscht hat euch hinsichtlich Allahs der Täuscher." });
                        surah57.VerseList.Add(new Verse { Number = 15, TextBosnian = "Nikakva otkupnina danas od vas neće primljena biti, a ni od onih koji nisu vjerovali; Vatra će biti prebivalište vaše, ona vam baš odgovara, A užasno je ona krajnje odredište!'", TextEnglish = "So today no ransom will be taken from you or from those who disbelieved. Your refuge is the Fire. It is most worthy of you, and wretched is the destination.'", TextGerman = "So wird heute von euch kein Lösegeld angenommen und auch nicht von denjenigen, die ungläubig sind. Euer Zufluchtsort ist das (Höllen)feuer; es ist euer Schutzherr - ein schlimmer Ausgang!" });
                        surah57.VerseList.Add(new Verse { Number = 16, TextBosnian = "Zar nije vrijeme da se onima koji vjeruju srca smekšaju zbog spomena Allaha i Istine koja se objavljuje i da oni ne budu kao oni kojima je još davno data Knjiga, pa su srca njihova, zato što je proteklo mnogo vremena, postala nemilosrdna i mnogi su od njih buntovni.", TextEnglish = "Has the time not come for those who have believed that their hearts should become humbly submissive at the remembrance of Allāh and what has come down of the truth? And let them not be like those who were given the Scripture before, and a long period passed over them, so their hearts hardened; and many of them are defiantly disobedient.", TextGerman = "Ist es denn nicht Zeit für diejenigen, die glauben, daß ihre Herzen demütig werden vor Allahs Ermahnung und vor dem, was von der Wahrheit herabgekommen ist, und daß sie nicht wie diejenigen sind, denen zuvor die Schrift gegeben wurde, es ihnen aber zu lang gedauert hat, und so ihre Herzen sich verhärtet haben? Und viele von ihnen sind Frevler." });
                        surah57.VerseList.Add(new Verse { Number = 17, TextBosnian = "Znajte da Allah daje život već mrtvoj zemlji! Mi vam objasnismo ajete i znakove da biste razumjeli.", TextEnglish = "Know that Allāh gives life to the earth after its lifelessness. We have made clear to you the signs; perhaps you will understand.[1632]", TextGerman = "Wißt, daß Allah die Erde nach ihrem Tod wieder lebendig macht. Wir haben euch die Zeichen bereits klargemacht, auf daß ihr begreifen möget." });
                        surah57.VerseList.Add(new Verse { Number = 18, TextBosnian = "Onima koji milostinju i zekat budu udjeljivali i onima koje je budu udjeljivale, i koji drage volje Allahu zajam budu davali - mnogostruko će se vratiti i njih čeka nagrada divna.", TextEnglish = "Indeed, the men who practice charity and the women who practice charity and [they who] have loaned Allāh a goodly loan - it will be multiplied for them, and they will have a noble reward.", TextGerman = "Gewiß, denjenigen Männern, die Almosen geben und denjenigen Frauen, die Almosen geben und (damit) ein gutes Darlehen geben, wird es vervielfacht werden; und für sie wird es trefflichen Lohn geben." });
                        surah57.VerseList.Add(new Verse { Number = 19, TextBosnian = "A oni koji vjeruju u Allaha i Njegove poslanike - to su oni koji srcem u potpunosti vjeruju! A šehidima - u njihova Gospodara pripada njihova nagrada, i svjetlo njihovo! A oni koji ne vjeruju i Naše ajete i znakove poriču, to su oni koji pripadaju Vatri!", TextEnglish = "And those who have believed in Allāh and His messengers - those are [in the ranks of] the supporters of truth and the martyrs, with their Lord. For them is their reward and their light.[1633] But those who have disbelieved and denied Our verses - those are the companions of Hellfire.", TextGerman = "Und diejenigen, die an Allah und Seine Gesandten glauben, das sind die stets Wahrhaftigen und die Zeugen vor ihrem Herrn. Sie erhalten ihren Lohn und ihr Licht. Diejenigen aber, die ungläubig sind und Unsere Zeichen für Lüge erklären, das sind die Insassen des Höllenbrandes." });
                        surah57.VerseList.Add(new Verse { Number = 20, TextBosnian = "Znajte da je dunjalučki život igra, i zabava, i gizdanje, i međusobno hvalisanje, i nadmetanje u mnoštvu imetka i djece! On je nalik kiši, i bilje od nje sijače zadivi, i ono potom, nakon kiše, uzbuja, ali ga zatim vidiš požutjela i potom sasušeno biva. Na ahiretu patnja teška je, a ima i oprost Allahov i zadovoljstvo. A šta je dunjalučki život doli uživanje varljivo!", TextEnglish = "Know that the life of this world is but amusement and diversion and adornment and boasting to one another and competition in increase of wealth and children - like the example of a rain whose [resulting] plant growth pleases the tillers; then it dries and you see it turned yellow; then it becomes [scattered] debris. And in the Hereafter is severe punishment and forgiveness from Allāh and approval. And what is the worldly life except the enjoyment of delusion.", TextGerman = "Wißt, daß das diesseitige Leben nur Spiel und Zerstreuung ist, Schmuck und gegenseitige Prahlerei und Wettstreit nach noch mehr Besitz und Kindern. Es ist wie das Gleichnis von Regen, dessen Pflanzenwuchs den Ungläubigen gefällt. Hierauf aber trocknet er aus, und da siehst du ihn gelb werden. Hierauf wird es zu zermalmtem Zeug. Im Jenseits aber gibt es strenge Strafe und (auch) Vergebung von Allah und Wohlgefallen. Und das diesseitige Leben ist nur trügerischer Genuß." });
                        surah57.VerseList.Add(new Verse { Number = 21, TextBosnian = "Nadmećite se da u Gopodara svoga zaslužite oprost i Džennet, prostran koliko su nebo i Zemlja prostrani, i pripremljen za one koji u Allaha i poslanike Njegove vjeruju. To je Allahovo obilje koje će dati onome kome On hoće; a Allah je posjednik obilja velikog.", TextEnglish = "Race [i.e., compete] toward forgiveness from your Lord and a Garden whose width is like the width of the heavens and earth, prepared for those who believed in Allāh and His messengers. That is the bounty of Allāh which He gives to whom He wills, and Allāh is the possessor of great bounty.", TextGerman = "Wetteilt zu Vergebung von eurem Herrn und (zu) einem (Paradies)garten, dessen Breite wie die Breite der Himmel und der Erde ist, bereitet für diejenigen, die an Allah und Seine Gesandten glauben. Das ist Allahs Huld, die Er gewährt, wem Er will. Und Allah besitzt große Huld." });
                        surah57.VerseList.Add(new Verse { Number = 22, TextBosnian = "Nema nevolje koja zadesi Zemlju i vas, a koja nije, prije nego što je damo, zapisana u Knjizi - to je Allahu, uistinu, lahko -", TextEnglish = "No disaster strikes upon the earth or among yourselves except that it is in a register[1634] before We bring it into being - indeed that, for Allāh, is easy -", TextGerman = "Kein Unglück trifft ein auf der Erde oder bei euch selbst, ohne daß es in einem Buch (verzeichnet) wäre, bevor Wir es erschaffen - gewiß, dies ist Allah ein leichtes -," });
                        surah57.VerseList.Add(new Verse { Number = 23, TextBosnian = "da ne biste tugovali za onim što vam je promaklo, a i da se ne biste previše radovali onome što vam On dadne. Allah ne voli nikakve razmetljivce, hvalisavce,", TextEnglish = "In order that you not despair over what has eluded you and not exult [in pride] over what He has given you. And Allāh does not like everyone self-deluded and boastful -", TextGerman = "damit ihr nicht betrübt seid über das, was euch entgangen ist, und euch nicht (zu sehr) freut über das, was Er euch gegeben hat. Und Allah liebt niemanden, der eingebildet und prahlerisch ist," });
                        surah57.VerseList.Add(new Verse { Number = 24, TextBosnian = "koji škrtare i traže da i ljudi škrti budu. A onaj ko neće da udjeljuje - pa, Allah je, uistinu, Neovisni i Hvaljeni.", TextEnglish = "[Those] who are stingy and enjoin upon people stinginess. And whoever turns away[1635] - then indeed, Allāh is the Free of need, the Praiseworthy.", TextGerman = "diejenigen, die geizen und den Menschen befehlen, geizig zu sein. Und wenn einer sich abkehrt, gewiß, so ist Allah der Unbedürftige und Lobenswürdige." });
                        surah57.VerseList.Add(new Verse { Number = 25, TextBosnian = "Mi smo izaslanike Naše s jasnim dokazima slali i po njima Knjige i Mjerilo - pravdu slali, da bi ljudi pravedno postupali, a gvožđe smo stvorili, u kome je velika snaga i koje ljudima koristi, a da bi Allah znao one koji pomažu Njega i poslanike Njegove iako ih i ne vide. Allah je, uistinu, moćan i silan.", TextEnglish = "We have already sent Our messengers with clear evidences and sent down with them the Scripture and the balance that the people may maintain [their affairs] in justice. And We sent down iron, wherein is great military might and benefits for the people, and so that Allāh may make evident those who support Him and His messengers unseen. Indeed, Allāh is Powerful and Exalted in Might.", TextGerman = "Wir haben ja Unsere Gesandten mit den klaren Beweisen gesandt und mit ihnen die Schrift und die Waage herabgesandt, damit die Menschen für die Gerechtigkeit eintreten. Und Wir haben das Eisen herabgsandt. In ihm ist starke Gewalt und Nutzen für die Menschen -, damit Allah kennt, wer Ihm und Seinen Gesandten im Verborgenen hilft. Gewiß, Allah ist Stark und Allmächtig." });
                        surah57.VerseList.Add(new Verse { Number = 26, TextBosnian = "Mi smo Nuha i Ibrahima poslali, i nekima od potomaka njihovih objavu i vjerovjesništvo dali; neki su od potomaka njihovih na Pravom putu, a mnogi od njih su buntovnici.", TextEnglish = "And We have already sent Noah and Abraham and placed in their descendants prophethood and scripture; and among them is he who is guided, but many of them are defiantly disobedient.", TextGerman = "Und Wir sandten bereits Nuh und Ibrahim und richteten in ihrer Nachkommenschaft das Prophetentum und die Schrift ein. Unter ihnen gab es (einige, die) rechtgeleitet (waren), aber viele von ihnen waren Frevler." });
                        surah57.VerseList.Add(new Verse { Number = 27, TextBosnian = "Zatim smo, njihovim tragovima, jednog za drugim poslanike Naše slali, dok nismo Isaa, sina Merjemina, poslali. Njemu smo Indžil dali, i učinili da u srcima sljedbenika njegovih blagost i samilost budu. A isposništvo su oni sami kao novotariju uveli u želji za Allahovim zadovoljstvom - Mi im ga nismo propisali; već smo im propisali traganje za Allahovim zadovoljstvom, ali oni o njemu nisu vodili brigu onako kako bi trebalo, pa ćemo one među njima koji budu ispravno vjerovali nagraditi, a mnogi od njih su buntovnici.", TextEnglish = "Then We sent following their footsteps [i.e., traditions] Our messengers and followed [them] with Jesus, the son of Mary, and gave him the Gospel. And We placed in the hearts of those who followed him compassion and mercy and monasticism, which they innovated; We did not prescribe it for them except [that they did so] seeking the approval of Allāh. But they did not observe it with due observance. So We gave the ones who believed among them their reward, but many of them are defiantly disobedient.", TextGerman = "Hierauf ließen Wir auf ihren Spuren Unsere Gesandten folgen; und Wir ließen ’Isa, den Sohn Maryams, folgen und gaben ihm das Evangelium. Und Wir setzten in die Herzen derjenigen, die ihm folgten, Mitleid und Barmherzigkeit, und (auch) Mönchtum, das sie erfanden - Wir haben es ihnen nicht vorgeschrieben -, (dies) nur im Trachten nach Allahs Wohlgefallen. Sie beachteten es jedoch nicht, wie es ihm zusteht. Und so gaben Wir denjenigen von ihnen, die glaubten, ihren Lohn. Aber viele von ihnen waren Frevler." });
                        surah57.VerseList.Add(new Verse { Number = 28, TextBosnian = "O vi koji vjerujete, Allaha se bojte i u Poslanika Njegova vjerujte. On će vam dvostruku milost Svoju darovati, i dat će vam svjetlo pomoću kog ćete ići, i oprostit će vam, a Allah je oprostitelj grijeha i milostiv je.", TextEnglish = "O you who have believed, fear Allāh and believe in His Messenger; He will [then] give you a double portion of His mercy and make for you a light by which you will walk and forgive you; and Allāh is Forgiving and Merciful.", TextGerman = "O die ihr glaubt, fürchtet Allah und glaubt an Seinen Gesandten, dann gibt Er euch einen zweifachen Anteil an Seiner Barmherzigkeit und macht euch ein Licht, in dem ihr gehen könnt, und vergibt euch. Allah ist Allvergebend und Barmherzig." });
                        surah57.VerseList.Add(new Verse { Number = 29, TextBosnian = "I neka sljedbenici Knjige znaju da oni nikakvu Allahovu blagodat neće dobiti, jer je blagodat samo u Allahovoj ruci - daje je onome kome On hoće; a Allah je posjednik obilja velikog.", TextEnglish = "[This is] so that the People of the Scripture may know that they are not able [to obtain] anything from the bounty of Allāh[1636] and that [all] bounty is in the hand[1637] of Allāh; He gives it to whom He wills. And Allāh is the possessor of great bounty.", TextGerman = "(Dies ist) damit die Leute der Schrift wissen, daß sie über nichts von Allahs Huld Macht haben, sondern daß die Huld in Allahs Hand liegt. Er gewährt sie, wem Er will. Und Allah besitzt große Huld." });
                        result = surah57;
                        break;
                    #endregion

                    // 
                    #region
                    case 58:
                        Surah surah58 = new Surah(); surah58.Name = Constants.Surah58Name; surah58.Number = 58; surah58.VerseList = new List<Verse>();
                        surah58.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah58.VerseList.Add(new Verse { Number = 1, TextBosnian = "Allah je čuo riječi one koja se s tobom o mužu svome raspravljala i Allahu se jadala, a Allah čuje razgovor vaš međusobni. Allah je, uistinu, Onaj Koji sve čuje i Onaj Koji sve vidi.", TextEnglish = "Certainly has Allāh heard the speech of the one who argues [i.e., pleads] with you, [O Muḥammad], concerning her husband and directs her complaint to Allāh. And Allāh hears your dialogue; indeed, Allāh is Hearing and Seeing.", TextGerman = "Gehört hat ja Allah die Aussage derjenigen, die mit dir über ihren Gatten streitet und sich bei Allah beklagt, während Allah euren Wortwechsel hört. Gewiß, Allah ist Allhörend und Allsehend." });
                        surah58.VerseList.Add(new Verse { Number = 2, TextBosnian = "Oni od vas koji ženama svojim reknu da su im leđa njihova poput leđa njihovih majki, a one nisu majke njihove - majke njihove su samo one koje su ih rodile, oni, zaista, govore strašne riječi i neistinu-a Allah je zbilja Onaj Koji grijehe poništava i oprašta.", TextEnglish = "Those who pronounce ẓihār[1639] among you [to separate] from their wives - they are not [consequently] their mothers. Their mothers are none but those who gave birth to them. And indeed, they are saying an objectionable statement and a falsehood. But indeed, Allāh is Pardoning and Forgiving.", TextGerman = "Diejenigen von euch, die sich von ihren Frauen durch den Rückenschwur trennen - sie sind doch nicht ihre Mütter. Ihre Mütter sind nur diejenigen, die sie geboren haben. Sie sagen da fürwahr etwas Verwerfliches an Worten und etwas Falsches. Und Allah ist wahrlich Allverzeihend und Allvergebend." });
                        surah58.VerseList.Add(new Verse { Number = 3, TextBosnian = "Oni koji ženama svojim reknu da su im leđa njihova poput leđa njihovih majki, a onda se povrate u onome što su rekli, dužni su, prije nego što jedni drugo dodirnu, jednog roba ropstva osloboditi. To je ono čime se opominjete - a Allah u potpunosti zna što vi radite.", TextEnglish = "And those who pronounce ẓihār from their wives and then [wish to] go back on what they said - then [there must be] the freeing of a slave before they touch one another. That is what you are admonished thereby; and Allāh is Aware of what you do.", TextGerman = "Diejenigen, die sich von ihren Frauen durch den Rückenschwur trennen und hierauf dann doch zu dem zurückkehren, was sie gesagt haben, (sollen) einen Sklaven befreien, bevor sie beide einander berühren. Damit werdet ihr ja ermahnt. Und Allah ist dessen, was ihr tut, Kundig." });
                        surah58.VerseList.Add(new Verse { Number = 4, TextBosnian = "Onaj koji ne nađe, dužan je dva mjeseca uzastopce postiti prije nego jedno drugo dodirnu. A onaj ko ne može, dužan je šezdeset siromaha nahraniti, zato da biste potvrdili da u Allaha i Poslanika Njegova vjerujete - to su Allahovi propisi. A nevjernike čeka kazna bolna.", TextEnglish = "And he who does not find [a slave] - then a fast for two months consecutively[1640] before they touch one another; and he who is unable - then the feeding of sixty poor persons. That is for you to believe [completely] in Allāh and His Messenger; and those are the limits [set by] Allāh. And for the disbelievers is a painful punishment.", TextGerman = "Wer aber keine (Möglichkeit dazu) findet, (der hat) zwei aufeinanderfolgende Monate (zu) fasten, bevor sie beide einander berühren. Wer (es) aber nicht kann, (der hat) sechzig Arme (zu) speisen. Dies, damit ihr an Allah und Seinen Gesandten glaubt. Dies sind Allahs Grenzen. Und für die Ungläubigen wird es schmerzhafte Strafe geben." });
                        surah58.VerseList.Add(new Verse { Number = 5, TextBosnian = "Doista, oni koji se suprotstavljaju Allahu i Poslaniku Njegovu bit će odbačeni i poniženi, kao što su bili odbačeni i poniženi i oni prije njih. A Mi smo objavili jasne ajete i znakove, a nevjernike čeka ponižavajuća patnja.", TextEnglish = "Indeed, those who oppose Allāh and His Messenger are abased as those before them were abased. And We have certainly sent down verses of clear evidence. And for the disbelievers is a humiliating punishment", TextGerman = "Gewiß, diejenigen, die Allah und Seinem Gesandten zuwiderhandeln, werden niedergeworfen, wie diejenigen vor ihnen niedergeworfen worden sind. Wir haben ja doch klare Zeichen hinabgesandt. Und für die Ungläubigen wird es schmachvolle Strafe geben." });
                        surah58.VerseList.Add(new Verse { Number = 6, TextBosnian = "na Dan kad ih Allah sve oživi, pa ih obavijesti o onom šta su radili; Allah je o tome račun sveo, a oni su to zaboravili -Allah je svemu svjedok.", TextEnglish = "On the Day when Allāh will resurrect them all and inform them of what they did. Allāh had enumerated it, while they forgot it; and Allāh is, over all things, Witness.[1641]", TextGerman = "Am Tag, da Allah sie alle auferwecken und ihnen kundtun wird, was sie getan haben. Allah hat es erfaßt, sie aber haben es vergessen. Und Allah ist über alles Zeuge." });
                        surah58.VerseList.Add(new Verse { Number = 7, TextBosnian = "Zar ne znaš da Allah zna sve što je na nebesima i što je na Zemlji?! Nema tajnih razgovora među trojicom, a da On nije četvrti, ni kad ih je manje, ni kad ih je više, a da On nije s njima gdje god oni bili; On će ih na Kijametskom danu obavijestiti o onom što su radili. Allah zaista sve dobro zna.", TextEnglish = "Have you not considered that Allāh knows what is in the heavens and what is on the earth? There are not three in a private conversation but that He is the fourth of them,[1642] nor are there five but that He is the sixth of them - and no less than that and no more except that He is with them [in knowledge] wherever they are. Then He will inform them of what they did, on the Day of Resurrection. Indeed Allāh is, of all things, Knowing.", TextGerman = "Siehst du nicht, daß Allah weiß, was in den Himmeln und was auf der Erde ist? Es gibt kein vertrauliches Gespräch zwischen dreien, ohne daß Er ihr vierter wäre, und keines zwischen fünfen, ohne daß Er ihr sechster wäre, und auch nicht weniger oder mehr als dieser (Zahl), ohne daß Er mit ihnen wäre, wo immer sie sein mögen. Hierauf tut Er ihnen am Tag der Auferstehung kund, was sie getan haben. Gewiß, Allah weiß über alles Bescheid." });
                        surah58.VerseList.Add(new Verse { Number = 8, TextBosnian = "Ne vidje li one kojima je zabranjeno da se sašaptavaju kako se zatim vraćaju onome što im je zabranjeno i sašaptavaju se o grijehu, neprijateljstvu i neposlušnosti prema Poslaniku?! A kad ti dolaze, pozdravljaju te onako kako te Allah nikad nije pozdravio, i među sobom govore: 'Trebalo bi da nas Allah već jednom kazni za ono što govorimo!' Dovoljan će im biti Džehennem! U njemu će gorjeti, a užasno je to krajnje odredište!", TextEnglish = "Have you not considered those who were forbidden from private conversation [i.e., ridicule and conspiracy] and then return to that which they were forbidden and converse among themselves about sin and aggression and disobedience to the Messenger? And when they come to you, they greet you with that [word] by which Allāh does not greet you[1643] and say among themselves, 'Why does Allāh not punish us for what we say?' Sufficient for them is Hell, which they will [enter to] burn, and wretched is the destination.", TextGerman = "Siehst du nicht jene, denen verboten wurde, vertrauliche Gespräche zu führen, die aber hierauf zurückkehren zu dem, was ihnen verboten wurde und miteinander über Sünde, feindseliges Vorgehen und Ungehorsam gegen den Gesandten vertraulich sprechen? Und wenn sie zu dir kommen, grüßen sie dich, wie nicht einmal Allah dich grüßt, und sagen unter sich: Würde Allah uns doch nur strafen für das, was wir sagen! Ihre Genüge ist die Hölle, der sie ausgesetzt sein werden - ein schlimmer Ausgang!" });
                        surah58.VerseList.Add(new Verse { Number = 9, TextBosnian = "O vi koji vjerujete, kada se među sobom došaptavate, ne došaptavajte se o grijehu, neprijateljstvu i neposlušnosti prema Poslaniku, već se došaptavajte o dobročinstvu i bogobojaznosti, i bojte se Allaha, pred Kojim ćete okupljeni biti.", TextEnglish = "O you who have believed, when you converse privately, do not converse about sin and aggression and disobedience to the Messenger but converse about righteousness and piety. And fear Allāh, to whom you will be gathered.", TextGerman = "O die ihr glaubt, wenn ihr vertraulich miteinander sprecht, dann sprecht nicht vertraulich miteinander über Sünde, feindseliges Vorgehen und Ungehorsam gegen den Gesandten, sondern sprecht vertraulich miteinander über Frömmigkeit und Gottesfurcht. Und fürchtet Allah, zu Dem ihr versammelt werdet." });
                        surah58.VerseList.Add(new Verse { Number = 10, TextBosnian = "Takvo sašaptavanje posao je šejtanski, da bi rastužio one koji vjeruju, a to ne može njima nimalo nauditi, osim ako to Allah dopusti. A vjernici neka se samo u Allaha pouzdaju!", TextEnglish = "Private conversation is only from Satan that he may grieve those who have believed,[1644] but he will not harm them at all except by permission of Allāh. And upon Allāh let the believers rely.", TextGerman = "Vertrauliche Gespräche sind nur vom Satan, damit diejenigen traurig seien, die gläubig sind. Aber er kann ihnen keinen Schaden zufügen, außer mit Allahs Erlaubnis. Und auf Allah sollen sich die Gläubigen verlassen." });
                        surah58.VerseList.Add(new Verse { Number = 11, TextBosnian = "O vi koji vjerujete, kad vam se kaže: 'Načinite mjesta drugima tamo gdje se sjedi', vi načinite, pa i vama će Allah mjesto načiniti; a kad vam se rekne: 'Dignite se', vi se dignite, i Allah će na visoke stepene uzdignuti one među vama koji vjeruju i kojima je dato znanje. A Allah u potpunosti zna ono što radite.", TextEnglish = "O you who have believed, when you are told, 'Space yourselves' in assemblies, then make space; Allāh will make space for you.[1645] And when you are told, 'Arise,'[1646] then arise; Allāh will raise those who have believed among you and those who were given knowledge, by degrees. And Allāh is Aware of what you do.", TextGerman = "O die ihr glaubt, wenn zu euch gesagt wird: Macht Platz! in den Versammlungen!, dann schafft Platz, so schafft auch Allah euch Platz. Und wenn gesagt wird: Erhebt euch!, dann erhebt euch eben, so erhöht auch Allah diejenigen von euch, die glauben, und diejenigen, denen das Wissen gegeben worden ist, um Rangstufen. Und Allah ist dessen, was ihr tut, Kundig." });
                        surah58.VerseList.Add(new Verse { Number = 12, TextBosnian = "O vi koji vjerujete, kad hoćete da se s Poslanikom sašaptavate, prije toga sadaku udijelite. To je za vas bolje i čistije. A ako ne nađete ništa - pa, Allah je, zaista, Oprostitelj grijeha, Milostivi.", TextEnglish = "O you who have believed, when you [wish to] privately consult the Messenger, present before your consultation a charity. That is better for you and purer. But if you find not [the means] - then indeed, Allāh is Forgiving and Merciful.", TextGerman = "O die ihr glaubt, wenn ihr mit dem Gesandten vertraulich sprechen wollt, dann gebt schon vor eurem vertraulichen Gespräch ein Almosen. Das ist besser und reiner für euch. Wenn ihr jedoch nicht(s dafür) findet, gewiß, dann ist Allah Allvergebend und Barmherzig." });
                        surah58.VerseList.Add(new Verse { Number = 13, TextBosnian = "Zar vas je strah da prije razgovora svoga sadaku udijelite?! A ako ne udijelite i Allah vam oprosti, onda namaz obavljajte i zekat dajite i budite pokorni Allahu i Poslaniku Njegovu! Allah u potpunosti zna ono što vi radite.", TextEnglish = "Have you feared to present before your consultation charities? Then when you do not and Allāh has forgiven you, then [at least] establish prayer and give zakāh and obey Allāh and His Messenger. And Allāh is Aware of what you do.", TextGerman = "Scheut ihr davor, schon vor eurem vertraulichen Gespräch Almosen zu geben? Da ihr (es) nicht getan habt und Allah sich euch wieder Reue-Annehmend zugewendet hat, so verrichtet das Gebet und entrichtet die Abgabe’, und gehorcht Allah und Seinem Gesandten. Und Allah ist Kundig dessen, was ihr tut." });
                        surah58.VerseList.Add(new Verse { Number = 14, TextBosnian = "Zar ti ne vidje one koji za prijatelje i zaštitnike uzimaju ljude na koje se Allah rasrdio?! Oni nisu ni vaši ni njihovi, i zaklinju se na laži, a oni znaju.", TextEnglish = "Have you not considered those who make allies of a people with whom Allāh has become angry? They are neither of you nor of them, and they swear to untruth while they know [they are lying].", TextGerman = "Siehst du nicht jene, die sich Leute zu Schutzherren nehmen, denen Allah zürnt? Sie gehören weder zu euch noch zu ihnen, und sie schwören auf (ihre) Lügen, wobei sie (es) wissen." });
                        surah58.VerseList.Add(new Verse { Number = 15, TextBosnian = "Allah je njima pripremio žestoku patnju, a zaista je ružno ono što rade.", TextEnglish = "Allāh has prepared for them a severe punishment. Indeed, it was evil that they were doing.", TextGerman = "Allah hat für sie strenge Strafe bereitet. Gewiß, wie böse ist, was sie zu tun pflegen." });
                        surah58.VerseList.Add(new Verse { Number = 16, TextBosnian = "Uzeli su svoje zakletve štitom, i odvraćaju od Allahova puta, pa im zato pripada ponižavajuća patnja.", TextEnglish = "They took their [false] oaths as a cover, so they averted [people] from the way of Allāh, and for them is a humiliating punishment.", TextGerman = "Sie haben sich ihre Eide zu einem Schutzschirm genommen und halten so von Allahs Weg ab. Für sie wird es schmachvolle Strafe geben." });
                        surah58.VerseList.Add(new Verse { Number = 17, TextBosnian = "Ni imeci njihovi, ni djeca njihova im nimalo neće pomoći kod Allaha, oni će biti stanovnici u Vatri, u njoj će vječno boraviti.", TextEnglish = "Never will their wealth or their children avail them against Allāh at all. Those are the companions of the Fire; they will abide therein eternally", TextGerman = "Weder ihr Besitz noch ihre Kinder werden ihnen vor Allah etwas nützen. Das sind die Insassen des (Höllen)feuers; ewig werden sie darin bleiben." });
                        surah58.VerseList.Add(new Verse { Number = 18, TextBosnian = "Na Dan u koji će ih Allah sve oživiti oni će se Njemu zaklinjati, kao što se vama zaklinju, misleći da će im to nešto koristiti. Oni su, doista, pravi lažljivci.", TextEnglish = "On the Day Allāh will resurrect them all, and they will swear to Him as they swear to you and think that they are [standing] on something.[1647] Unquestionably, it is they who are the liars.", TextGerman = "Am Tag, da Allah sie alle auferweckt, da werden sie Ihm schwören, wie sie euch schwören, und meinen, sie hätten eine Grundlage. Aber sicherlich, sie sind ja Lügner." });
                        surah58.VerseList.Add(new Verse { Number = 19, TextBosnian = "Njima je ovladao šejtan i učinio da zaborave spominjati Allaha, oni su šejtanova stranka, a zbilja, oni iz stranke šejtanove gubitnici su pravi.", TextEnglish = "Satan has overcome them and made them forget the remembrance of Allāh. Those are the party of Satan. Unquestionably, the party of Satan - they will be the losers.", TextGerman = "Der Satan hat sie in seine Gewalt gebracht und sie dann Allahs Ermahnung vergessen lassen. Jene sind die Gruppierung des Satans. Aber sicherlich, die Gruppierung des Satans, das sind ja die Verlierer." });
                        surah58.VerseList.Add(new Verse { Number = 20, TextBosnian = "Oni koji se suprotstavljaju Allahu i Poslaniku Njegovu bit će, sigurno, među onim najponiženijim.", TextEnglish = "Indeed, the ones who oppose Allāh and His Messenger - those will be among the most humbled.", TextGerman = "Gewiß, diejenigen, die Allah und Seinem Gesandten zuwiderhandeln, sie gehören zu den Niedrigsten." });
                        surah58.VerseList.Add(new Verse { Number = 21, TextBosnian = "Allah je zapisao: 'Ja i poslanici Moji sigurno ćemo pobijediti!' Allah je, zaista, moćan i silan.", TextEnglish = "Allāh has written [i.e., decreed], 'I will surely overcome, I and My messengers.' Indeed, Allāh is Powerful and Exalted in Might.", TextGerman = "Allah hat (vor)geschrieben: Siegen werde Ich ganz gewiß, (Ich) und Meine Gesandten. Gewiß, Allah ist Stark und Allmächtig." });
                        surah58.VerseList.Add(new Verse { Number = 22, TextBosnian = "Nećeš naći da ljudi koji u Allaha i u ahiret vjeruju budu u ljubavi s onima koji se Allahu i Poslaniku Njegovu suprotstavljaju, pa bili to očevi njihovi, ili sinovi njihovi, ili braća njihova, ili rođaci njihovi. Njima je On u srca vjerovanje usadio i svjetlom Svojim ih osnažio, i On će ih uvesti u džennetske bašče, kroz koje rijeke teku, da u njima vječno ostanu. Allah je njima zadovoljan, a i oni su zadovoljni Njime. Oni su Allahova stranka a, doista, Allahova su stranka oni koji će sigurno uspjeti.", TextEnglish = "You will not find a people who believe in Allāh and the Last Day having affection for those who oppose Allāh and His Messenger, even if they were their fathers or their sons or their brothers or their kindred. Those - He has decreed within their hearts faith and supported them with spirit[1648] from Him. And We will admit them to gardens beneath which rivers flow, wherein they abide eternally. Allāh is pleased with them, and they are pleased with Him - those are the party of Allāh. Unquestionably, the party of Allāh - they are the successful.", TextGerman = "Du findest keine Leute, die an Allah und den Jüngsten Tag glauben und denjenigen Zuneigung bezeigen, die Allah und Seinem Gesandten zuwiderhandeln, auch wenn diese ihre Väter wären oder ihre Söhne oder ihre Brüder oder ihre Sippenmitglieder. Jene - in ihre Herzen hat Er den Glauben geschrieben und sie mit Geist von Sich gestärkt. Er wird sie in Gärten eingehen lassen, durcheilt von Bächen, ewig darin zu bleiben. Allah hat Wohlgefallen an ihnen, und sie haben Wohlgefallen an Ihm. Jene sind Allahs Gruppierung. Sicherlich, Allahs Gruppierung, dies sind diejenigen, denen es wohl ergeht." });
                        result = surah58;
                        break;
