                    #region
                    case 59:
                        Surah surah59 = new Surah(); surah59.Name = Constants.Surah59Name; surah59.Number = 59; surah59.VerseList = new List<Verse>();
                        surah59.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah59.VerseList.Add(new Verse { Number = 1, TextBosnian = "Allaha slavi i veliča sve što je na nebesima i što je na Zemlji, On je silan i mudar.", TextEnglish = "Whatever is in the heavens and whatever is on the earth exalts Allāh,[1650] and He is the Exalted in Might, the Wise.", TextGerman = "Es preist Allah (alles), was in den Himmeln und was auf der Erde ist. Und Er ist der Allmächtige und Allweise." });
                        surah59.VerseList.Add(new Verse { Number = 2, TextBosnian = "On je Onaj Koji je prilikom prvog progonstva protjerao iz domova njihovih one sljedbenike Knjige koji nisu vjerovali. Vi niste mislili da će otići, a oni su mislili da će ih utvrde njihove od Allaha odbraniti, ali ih je Allah kaznio odakle se nisu nadali i On je u srca njihova strah ulio; vlastitim rukama i rukama vjernika svoje domove su rušili. Zato uzmite iz toga pouku, o vi razboriti!", TextEnglish = "It is He who expelled the ones who disbelieved among the People of the Scripture[1651] from their homes at the first gathering.[1652] You did not think they would leave, and they thought that their fortresses would protect them from Allāh; but [the decree of] Allāh came upon them from where they had not expected, and He cast terror into their hearts [so] they destroyed their houses by their [own] hands and the hands of the believers. So take warning, O people of vision.", TextGerman = "Er ist es, Der diejenigen von den Leuten der Schrift, die ungläubig sind, aus ihren Wohnstätten zur ersten Versammlung vertrieben hat. Ihr habt nicht geglaubt, daß sie fortziehen würden; und sie meinten, daß ihre Festungen sie vor Allah schützten. Da kam Allah über sie, von wo sie nicht (damit) rechneten, und jagte in ihre Herzen Schrecken, so daß sie ihre Häuser mit ihren (eigenen) Händen und den Händen der Gläubigen zerstörten. Darum zieht die Lehre daraus, o die ihr Einsicht besitzt." });
                        surah59.VerseList.Add(new Verse { Number = 3, TextBosnian = "A da im Allah nije već bio propisao izgnanstvo, On bi ih drugoj patnji još na dunjaluku podvrgao! A njima na ahiretu pripada patnja u Vatri.", TextEnglish = "And if not that Allāh had decreed for them evacuation, He would have punished them in [this] world, and for them in the Hereafter is the punishment of the Fire.", TextGerman = "Und wenn Allah ihnen nicht die Verbannung vorgeschrieben hätte, hätte Er sie wahrlich im Diesseits gestraft; und im Jenseits wird es für sie die Strafe des (Höllen)feuers geben." });
                        surah59.VerseList.Add(new Verse { Number = 4, TextBosnian = "To je zato što su se Allahu i Poslaniku Njegovu suprotstavljali; a onaj ko se Allahu suprotstavlja - pa Allah, zaista, žestoko kažnjava.", TextEnglish = "That is because they opposed Allāh and His Messenger. And whoever opposes Allāh - then indeed, Allāh is severe in penalty.", TextGerman = "Dies dafür, daß sie Allah und Seinem Gesandten entgegengewirkt haben. Und wer Allah entgegenwirkt, gewiß, so ist Allah streng im Bestrafen." });
                        surah59.VerseList.Add(new Verse { Number = 5, TextBosnian = "To što ste neke palme posjekli ili ih, da uspravno stoje, ostavili - s Allahovim dopuštenjem ste učinili, i zato da On buntovnike ponizi.", TextEnglish = "Whatever you have cut down of [their] palm trees or left standing on their trunks - it was by permission of Allāh and so He would disgrace the defiantly disobedient.", TextGerman = "Was an Palmen ihr umgehauen habt oder auf ihren Wurzeln habt stehenlassen, so geschah es mit Allahs Erlaubnis, und damit Er die Frevler in Schande stürze." });
                        surah59.VerseList.Add(new Verse { Number = 6, TextBosnian = "A vi niste kasom tjerali konje ni kamile zbog onoga što je Allah od njih, kao plijen, darovao Poslaniku Svome, nego, Allah prepušta vlast poslanicima Svojim nad onima nad kojima On hoće; Allah nad svime ima moć.", TextEnglish = "And what Allāh restored [of property] to His Messenger from them - you did not spur for it [in an expedition] any horses or camels,[1653] but Allāh gives His messengers power over whom He wills, and Allāh is over all things competent.", TextGerman = "Und für das, was Allah Seinem Gesandten von ihnen als kampflose Beute zugeteilt hat, habt ihr weder Pferde noch Reitkamele in Trab gebracht, sondern Allah gibt Seinen Gesandten Gewalt, über wen Er will. Und Allah hat zu allem die Macht." });
                        surah59.VerseList.Add(new Verse { Number = 7, TextBosnian = "Plijen od stanovnika sela i gradova koji Allah Poslaniku Svome daruje pripada: Allahu i Poslaniku Njegovu, i bližnjima njegovim, i siročadi, i siromašnim, i putnicima namjernicima -da ne bi prelazio samo iz ruku u ruke bogataša vaših; ono što vam Poslanik da, to prihvatite, a ono što vam zabrani -ostavite, i bojte se Allaha. Allah, zaista, žestoko kažnjava.", TextEnglish = "And what Allāh restored to His Messenger from the people of the towns - it is for Allāh and for the Messenger and for [his] near relatives[1654] and orphans and the needy and the[stranded] traveler[1655] - so that it will not be a perpetual distribution among the rich from among you.And whatever the Messenger has given you - take; and what he has forbidden you -refrain from.And fear Allāh; indeed, Allāh is severe in penalty.", TextGerman = "Was Allah Seinem Gesandten von den Bewohnern der Städte als kampflose Beute zugeteilt hat, das gehört Allah, Seinem Gesandten und den Verwandten, den Waisen, den Armen und dem Sohn des Weges.Dies, damit es nicht nur im Kreis der Reichen von euch bleibt.Was nun der Gesandte euch gibt, das nehmt; und was er euch untersagt, dessen enthaltet euch.Und fürchtet Allah.Gewiß, Allah ist streng im Bestrafen." });
                        surah59.VerseList.Add(new Verse { Number = 8, TextBosnian = "I siromašnim muhadžirima koji su iz svojih domova i iz posjeda svojih protjerani, tragajući za Allahovim obiljem i zadovoljstvom, i Allahu i Poslaniku Njegovu pomažu, to su, zaista, iskreni.", TextEnglish = "For the poor emigrants who were expelled from their homes and their properties, seeking bounty from Allāh and [His] approval and supporting [the cause of] Allāh and His Messenger, [there is also a share]. Those are the truthful.", TextGerman = "(Das gehört) den armen Auswanderern, die aus ihren Wohnstätten und von ihrem Besitz vertrieben worden sind, weil sie nach Huld von Allah und Wohlgefallen trachten und Allah und Seinem Gesandten helfen. Das sind die Wahrhaftigen." });
                        surah59.VerseList.Add(new Verse { Number = 9, TextBosnian = "A oni koji su prije njih Medinu nastanili i iman prihvatili, oni vole one koji im doseljavaju, i u grudima svojim nikakvu zavist, zato što im se daje, ne osjećaju, i više vole njima nego sebi, mada im je i samima potrebno. A oni koji se uščuvaju gramzivosti i tvrdičluka, oni će sigurno uspjeti.", TextEnglish = "And [also for] those who were settled in the Home [i.e.,al-Madīnah] and [adopted] the faith before them.[1656] They love those who emigrated to them and find not any want in their breasts of what they [i.e., the emigrants] were given but give [them] preference over themselves, even though they are in privation. And whoever is protected from the stinginess of his soul - it is those who will be the successful.", TextGerman = "Und diejenigen, die in der Wohnstätte und im Glauben vor ihnen zu Hause waren, lieben (all die,) wer zu ihnen ausgewandert ist, und empfinden in ihren Brüsten kein Bedürfnis nach dem, was (diesen) gegeben worden ist, und sie ziehen (sie) sich selbst vor, auch wenn sie selbst Mangel erlitten. Und diejenigen, die vor ihrer eigenen Habsucht bewahrt bleiben, das sind diejenigen, denen es wohl ergeht." });
                        surah59.VerseList.Add(new Verse { Number = 10, TextBosnian = "Oni koji poslije njih dolaze - govore: 'Gospodaru naš, oprosti nama i braći našoj koja su nas u vjeri pretekla i ne dopusti da u srcima našim bude imalo zlobe prema vjernicima; Gospodaru naš, Ti si, zaista, milosrdan i milostiv.'", TextEnglish = "And [there is a share for] those who come after them, saying, 'Our Lord, forgive us and our brothers who preceded us in faith and put not in our hearts [any] resentment toward those who have believed. Our Lord, indeed You are Kind and Merciful.'", TextGerman = "Und diejenigen, die nach ihnen gekommen sind, sagen: Unser Herr, vergib uns und unseren Brüdern, die uns im Glauben vorausgegangen sind, und lasse in unseren Herzen keinen Groll sein gegen diejenigen, die glauben. Unser Herr, Du bist ja Gnädig und Barmherzig." });
                        surah59.VerseList.Add(new Verse { Number = 11, TextBosnian = "Zar ne vidje licemjere kako govore svojim prijateljima, sljedbenicima Knjige koji ne vjeruju: 'Ako budete protjerani, mi ćemo sigurno s vama poći i kad se vas bude ticalo, nikada se nikome nećemo pokoriti; a ako budete napadnuti, sigurno ćemo vam u pomoć priteći', a Allah je svjedok da su oni, zaista, lašci.", TextEnglish = "Have you not considered those who practice hypocrisy, saying to their brothers [i.e., associates] who have disbelieved among the People of the Scripture, 'If you are expelled, we will surely leave with you, and we will not obey, in regard to you, anyone - ever; and if you are fought, we will surely aid you.' But Allāh testifies that they are liars.", TextGerman = "Siehst du nicht jene, die heucheln? Sie sagen zu ihren Brüdern, die ungläubig sind von den Leuten der Schrift: Wenn ihr tatsächlich vertrieben werdet, werden wir ganz gewiß mit euch fortziehen, und wir werden niemals jemandem gegen euch gehorchen. Und wenn gegen euch tatsächlich gekämpft wird, werden wir euch ganz gewiß helfen. Doch Allah bezeugt, daß sie wahrlich lügen." });
                        surah59.VerseList.Add(new Verse { Number = 12, TextBosnian = "Ako bi bili protjerani, oni ne bi s njima pošli; ako bi bili napadnuti, oni im ne bi u pomoć pritekli; a ako bi im u pomoć pritekli, sigurno bi pobjegli i oni bi bez pomoći ostali.", TextEnglish = "If they are expelled, they will not leave with them, and if they are fought, they will not aid them. And [even] if they should aid them, they will surely turn their backs; then [thereafter] they will not be aided.", TextGerman = "Wenn sie tatsächlich vertrieben werden, werden sie nicht mit ihnen fortziehen; und wenn tatsächlich gegen sie gekämpft wird, werden sie ihnen nicht helfen. Und wenn sie ihnen (auch) tatsächlich helfen sollten, werden sie ganz gewiß den Rücken kehren, und hierauf wird ihnen (selbst) keine Hilfe zuteil werden." });
                        surah59.VerseList.Add(new Verse { Number = 13, TextBosnian = "Oni se više boje vas nego Allaha, zato što su oni ljudi koji ne shvataju.", TextEnglish = "You [believers] are more fearful within their breasts than Allāh. That is because they are a people who do not understand.", TextGerman = "Vor euch empfinden sie wahrlich größere Angst in ihren Brüsten als vor Allah. Dies (ist so), weil sie Leute sind, die nicht verstehen." });
                        surah59.VerseList.Add(new Verse { Number = 14, TextBosnian = "Samo u utvrđenim gradovima ili iza zidina oni se protiv vas smiju skupa boriti. Njihovo međusobno neprijateljstvo žestoko je. Ti misliš da su oni složni, međutim, srca su njihova razjedinjena, zato što su to ljudi koji ne razumiju.", TextEnglish = "They will not fight you all except within fortified cities or from behind walls. Their violence [i.e., enmity] among themselves is severe. You think they are together, but their hearts are diverse. That is because they are a people who do not reason.", TextGerman = "Sie werden nicht alle gegen euch kämpfen, außer in befestigten Städten oder hinter Mauern. Ihre Gewalt ist untereinander stark. Du meinst, sie halten zusammen, doch ihre Herzen sind verschieden. Dies (ist so), weil sie Leute sind, die nicht begreifen." });
                        surah59.VerseList.Add(new Verse { Number = 15, TextBosnian = "Slični su onima koji su, tu nedavno, svu pogubnost postupaka svojih iskusili - njih čeka patnja bolna.", TextEnglish = "[Theirs is] like the example of those shortly before them: they tasted the bad consequence of their affair, and they will have a painful punishment.", TextGerman = "Es ist wie das Gleichnis derjenigen (, die es) nahe vor ihnen (erlebten). Sie haben die schlimmen Folgen ihres Verhaltens gekostet, und für sie wird es schmerzhafte Strafe geben." });
                        surah59.VerseList.Add(new Verse { Number = 16, TextBosnian = "Slični su šejtanu kad kaže čovjeku: 'Budi nevjernik!', pa kad postane nevjernik, on onda rekne: 'Ja više nemam ništa s tobom; ja se, zaista, Allaha, Gopodara svjetova bojim.'", TextEnglish = "[The hypocrites are] like the example of Satan when he says to man, 'Disbelieve.' But when he disbelieves, he says, 'Indeed, I am disassociated from you. Indeed, I fear Allāh, Lord of the worlds.'", TextGerman = "Es ist wie das Gleichnis des Satans, wenn er zum Menschen sagt: Sei ungläubig. Wenn er dann ungläubig geworden ist, sagt er: Gewiß, ich bin unschuldig an dir. Gewiß, ich fürchte Allah, den Herrn der Weltenbewohner." });
                        surah59.VerseList.Add(new Verse { Number = 17, TextBosnian = "Obojicu ih, na kraju, čeka Vatra u kojoj će vječno boraviti, a to će biti kazna za sve zulumćare.", TextEnglish = "So the outcome for both of them is that they will be in the Fire, abiding eternally therein. And that is the recompense of the wrongdoers.", TextGerman = "Und so wird das Ende von beiden sein, daß sie im (Höllen)feuer sein werden; ewig werden beide darin bleiben; das ist der Lohn der Ungerechten." });
                        surah59.VerseList.Add(new Verse { Number = 18, TextBosnian = "O vi koji vjerujete, Allaha se bojte, i neka svaki čovjek gleda šta je za sutra pripremio, i Allaha se bojte. Allah, zaista, u potpunosti zna ono što radite.", TextEnglish = "O you who have believed, fear Allāh. And let every soul look to what it has put forth for tomorrow - and fear Allāh. Indeed, Allāh is Aware of what you do.", TextGerman = "O die ihr glaubt, fürchtet Allah. Und eine jede Seele schaue, was sie für morgen vorausschickt. Und fürchtet Allah; gewiß, Allah ist Kundig dessen, was ihr tut." });
                        surah59.VerseList.Add(new Verse { Number = 19, TextBosnian = "I ne budite kao oni koji su zaboravili Allaha, pa je On učinio da sami sebe zaborave; oni su baš buntovnici.", TextEnglish = "And be not like those who forgot Allāh, so He made them forget themselves. Those are the defiantly disobedient.", TextGerman = "Und seid nicht wie diejenigen, die Allah vergessen haben und die Er dann sich selbst hat vergessen lassen. Das sind die Frevler." });
                        surah59.VerseList.Add(new Verse { Number = 20, TextBosnian = "Nisu jednaki stanovnici Džehennema i stanovnici Dženneta; stanovnici Dženneta baš su uspješni.", TextEnglish = "Not equal are the companions of the Fire and the companions of Paradise. The companions of Paradise - they are the attainers [of success].", TextGerman = "Nicht gleich sind die Insassen des (Höllen)feuers und die Insassen des (Paradies)gartens. Die Insassen des (Paradies)gartens sind die Erfolgreichen." });
                        surah59.VerseList.Add(new Verse { Number = 21, TextBosnian = "Da ovaj Kur'an kakvom brdu objavimo, ti bi vidio kako je strahopoštovanja puno i kako bi se od straha pred Allahom raspalo. Takve primjere navodimo ljudima da bi razmislili.", TextEnglish = "If We had sent down this Qur’ān upon a mountain, you would have seen it humbled and splitting from fear of Allāh. And these examples We present to the people that perhaps they will give thought.", TextGerman = "Wenn Wir diesen Qur’an (als Offenbarung) auf einen Berg hinabsendeten, würdest du ihn wahrlich aus Furcht vor Allah demütig werden und sich spalten sehen. Diese Gleichnisse prägen Wir den Menschen, auf daß sie nachdenken mögen." });
                        surah59.VerseList.Add(new Verse { Number = 22, TextBosnian = "On je Allah, osim Kojeg nema drugog boga, poznavatelj onog što je čulima nedokučivo, a i onog što je pojavno, Svemilosni, Milostivi.", TextEnglish = "He is Allāh, other than whom there is no deity, Knower of the unseen and the witnessed.[1657] He is the Entirely Merciful, the Especially Merciful.", TextGerman = "Er ist Allah, außer dem es keinen Gott gibt, der Kenner des Verborgenen und des Offenbaren. Er ist der Allerbarmer und Barmherzige." });
                        surah59.VerseList.Add(new Verse { Number = 23, TextBosnian = "On je Allah, osim Kojeg nema drugog boga, Vladar, Blagoslovljeni i Štovani, Savršeni - Onaj Koji je bez nedostataka, Onaj Koji pruža sigurnost, Onaj Koji nad svim bdi, Silni, Uzvišeni, Gordi. Slavljen i Uzvišen je Allah od onoga što mu pridružuju!", TextEnglish = "He is Allāh, other than whom there is no deity, the Sovereign,[1658] the Pure,[1659] the Perfection,[1660] the Grantor of Security,[1661] the Overseer,[1662] the Exalted in Might,[1663] the Compeller,[1664] the Superior.[1665] Exalted is Allāh above whatever they associate with Him.", TextGerman = "Er ist Allah, außer dem es keinen Gott gibt, der König, der Heilige, der Friede, der Gewährer der Sicherheit, der Wächter, der Allmächtige, der Gewalthaber, der Stolze. Preis sei Allah! (Und Erhaben ist Er) über das, was sie (Ihm) beigesellen." });
                        surah59.VerseList.Add(new Verse { Number = 24, TextBosnian = "On je Allah, Kreator, Tvorac, Oblikovatelj, On ima najljepša imena. Njega slavi i veliča sve što je na nebesima i na Zemlji, On je Silni i Mudri.", TextEnglish = "He is Allāh, the Creator,[1666] the Producer,[1667] the Fashioner;[1668] to Him belong the best names.[1669] Whatever is in the heavens and earth is exalting Him. And He is the Exalted in Might, the Wise.[1670]", TextGerman = "Er ist Allah, der Schöpfer, der Erschaffer, der Gestalter. Sein sind die schönsten Namen. Ihn preist (alles), was in den Himmeln und auf der Erde ist. Und Er ist der Allmächtige und Allweise." });
                        result = surah59;
                        break;
                    #endregion

