                        Surah surah60 = new Surah(); surah60.Name = Constants.Surah60Name; surah60.Number = 60; surah60.VerseList = new List<Verse>();
                        surah60.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah60.VerseList.Add(new Verse { Number = 1, TextBosnian = "O vi koji vjerujete! Ne uzimajte Moje neprijatelje i svoje neprijatelje za prisne prijatelje, ljubav im darivajući, a oni poriču Istinu koja došla je vama, i izgone Poslanika i vas zato što vjerujete u Allaha, vašega Gospodara, ako ste već izišli radi džihada na Putu Mojemu i tražeći zadovoljstvo Moje. Vi njima ljubav tajno iskazujete, a Ja najbolje znam što vi skrivate i što obznanjujete. A ko to od vas bude činio, pa - taj je zalutao s Pravog puta.", TextEnglish = "O you who have believed, do not take My enemies and your enemies as allies,[1672] extending to them affection while they have disbelieved in what came to you of the truth, having driven out the Prophet and yourselves [only] because you believe in Allāh, your Lord. If you have come out for jihād [i.e., fighting or striving] in My cause and seeking means to My approval, [take them not as friends]. You confide to them affection [i.e., instruction], but I am most knowing of what you have concealed and what you have declared. And whoever does it among you has certainly strayed from the soundness of the way.", TextGerman = "O die ihr glaubt, nehmt nicht Meine Feinde und eure Feinde zu Schutzherren, indem ihr ihnen Zuneigung entgegenbringt, wo sie doch das verleugnen, was von der Wahrheit zu euch gekommen ist, und den Gesandten und euch selbst vertreiben, weil ihr an Allah, euren Herrn, glaubt. (Nehmt sie nicht zu Schutzherren), wenn ihr wirklich ausgezogen seid zum Abmühen auf Meinem Weg und im Trachten nach Meiner Zufriedenheit. (Tut das nicht, indem) ihr ihnen heimlich Zuneigung zeigt, wo Ich doch besser weiß, was ihr verbergt und was ihr offenlegt. Und wer von euch das tut, der ist fürwahr vom rechten Weg abgeirrt." });
                        surah60.VerseList.Add(new Verse { Number = 2, TextBosnian = "Ako vas se oni domognu, bit će neprijatelji vaši i pružit će prema vama, u zloj namjeri, ruke svoje i jezike svoje, i poželjet će da uznevjerujete.", TextEnglish = "If they gain dominance over you, they would be [i.e., behave] to you as enemies and extend against you their hands and their tongues with evil, and they wish you would disbelieve.", TextGerman = "Wenn sie auf euch treffen, sind sie euch feind und strecken gegen euch ihre Hände und ihre Zungen zum Bösen aus. Sie möchten gern, ihr würdet ungläubig." });
                        surah60.VerseList.Add(new Verse { Number = 3, TextBosnian = "Ni rodbina vaša ni djeca vaša neće vam biti od koristi, na Kijametskom danu On će vas razdvojiti. Allah dobro vidi ono što radite.", TextEnglish = "Never will your relatives or your children benefit you; the Day of Resurrection He will judge between you. And Allāh, of what you do, is Seeing.", TextGerman = "Nicht nützen werden euch eure Blutsverwandten und auch nicht eure Kinder. Am Tag der Auferstehung wird Er (Allah) zwischen euch entscheiden. Und was ihr tut, sieht Allah wohl." });
                        surah60.VerseList.Add(new Verse { Number = 4, TextBosnian = "Imali ste dobar uzor u Ibrahimu i u onima koji su s njim kad su narodu svome rekli: 'Mi s vama nemamo ništa, a ni s onim što vi, umjesto Allaha, obožavate, mi vas se odričemo, i neprijateljstvo i mržnja između nas stalno će se javljati sve dok ne budete u Allaha, Njega Jedinog, vjerovali!', ali ne i u Ibrahimovim riječima ocu svome: 'Ja ću za tebe moliti oprost, ali te ne mogu od Allaha odbraniti. Gospodaru naš, na Tebe se oslanjamo, Tebi se obraćamo i kod Tebe je krajnje odredište.", TextEnglish = "There has already been for you an excellent pattern[1673] in Abraham and those with him, when they said to their people, 'Indeed, we are disassociated from you and from whatever you worship other than Allāh. We have denied you, and there has appeared between us and you animosity and hatred forever until you believe in Allāh alone' - except for the saying of Abraham to his father, 'I will surely ask forgiveness for you, but I have not [power to do] for you anything against Allāh. Our Lord, upon You we have relied, and to You we have returned, and to You is the destination.", TextGerman = "Ihr habt doch ein schönes Vorbild in Ibrahim und denjenigen, die mit ihm waren, als sie zu ihrem Volk sagten: Wir sind unschuldig an euch und an dem, dem ihr anstatt Allahs dient. Wir verleugnen euch, und zwischen uns und euch haben sich Feindschaft und Haß auf immer offenkundig gezeigt, bis ihr an Allah allein glaubt. (Dies), außer das Wort Ibrahims zu seinem Vater: Ich werde ganz gewiß für dich um Vergebung bitten; doch vermag ich für dich vor Allah gar nichts auszurichten. Unser Herr, auf Dich verlassen wir uns, und Dir wenden wir uns reuig zu. Und zu Dir ist der Ausgang." });
                        surah60.VerseList.Add(new Verse { Number = 5, TextBosnian = "Gospodaru naš, ne učini da budemo iskušenje onima koji ne vjeruju i oprosti nam, Gospodaru naš! Ti si, zaista, Silni i Mudri.'", TextEnglish = "Our Lord, make us not [objects of] torment for the disbelievers and forgive us, our Lord. Indeed, it is You who is the Exalted in Might, the Wise.'", TextGerman = "Unser Herr, mache uns nicht zu einer Versuchung für diejenigen, die ungläubig sind. Und vergib uns, unser Herr. Du bist ja der Allmächtige und Allweise." });
                        surah60.VerseList.Add(new Verse { Number = 6, TextBosnian = "Bijahu oni vama dobar uzor¬svakom onom ko se nada Allahu i Posljednjem danu. A svako onaj ko se okrene - pa, Allah je, zaista, Neovisni i Hvaljeni.", TextEnglish = "There has certainly been for you in them an excellent pattern for anyone whose hope is in Allāh and the Last Day. And whoever turns away - then indeed, Allāh is the Free of need, the Praiseworthy.", TextGerman = "Ihr habt ja in ihnen ein schönes Vorbild, und zwar für einen (jeden), der auf Allah und (auf) den Jüngsten Tag hofft. Wer sich jedoch abkehrt, so ist Allah der Unbedürftige und Lobenswürdige." });
                        surah60.VerseList.Add(new Verse { Number = 7, TextBosnian = "Allah će sigurno uspostaviti ljubav između vas i onih s kojima ste u neprijateljstvu; Allah je svemoćan, i Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "Perhaps Allāh will put, between you and those to whom you have been enemies among them, affection. And Allāh is competent,[1674] and Allāh is Forgiving and Merciful.", TextGerman = "Vielleicht setzt Allah zwischen euch und denjenigen von ihnen, mit denen ihr verfeindet seid, Zuneigung! Und Allah ist Allmächtig. Und Allah ist Allvergebend und Barmherzig." });
                        surah60.VerseList.Add(new Verse { Number = 8, TextBosnian = "Allah vam ne zabranjuje da činite dobro i da budete pravedni prema onima koji nisu ratovali protiv vas zbog vjere i koji vas iz domova vaših nisu izgnali - Allah, zaista, voli one koji su pravični,", TextEnglish = "Allāh does not forbid you from those who do not fight you because of religion and do not expel you from your homes - from being righteous toward them and acting justly toward them. Indeed, Allāh loves those who act justly.", TextGerman = "Allah verbietet euch nicht, gegenüber denjenigen, die nicht gegen euch der Religion wegen gekämpft und euch nicht aus euren Wohnstätten vertrieben haben, gütig zu sein und sie gerecht zu behandeln. Gewiß, Allah liebt die Gerechten." });
                        surah60.VerseList.Add(new Verse { Number = 9, TextBosnian = "ali vam zabranjuje da budete prisni s onima koji su ratovali protiv vas zbog vjere i koji su vas iz domova vaših izgnali i koji su pomogli da budete prognani. Oni koji su s njima prisni baš su zulumćari.", TextEnglish = "Allāh only forbids you from those who fight you because of religion and expel you from your homes and aid in your expulsion - [forbids] that you make allies[1675] of them. And whoever makes allies of them, then it is those who are the wrongdoers.", TextGerman = "Er verbietet euch nur, diejenigen, die gegen euch der Religion wegen gekämpft und euch aus euren Wohnstätten vertrieben und zu eurer Vertreibung Beistand geleistet haben, zu Schutzherren zu nehmen. Diejenigen, die sie zu Schutzherren nehmen, das sind die Ungerechten." });
                        surah60.VerseList.Add(new Verse { Number = 10, TextBosnian = "O vi koji vjerujete, kad vam vjernice kao muhadžirke dođu, ispitajte ih; Allah najbolje zna kakvo je vjerovanje njihovo, pa ako se uvjerite da su vjernice, onda ih ne vraćajte nevjernicima; one njima nisu dopuštene, niti su oni njima dopušteni; a njima dajte ono što su potrošili. Nije vam grijeh da se njima ženite kad im vjenčane darove njihove date. U braku nevjernice ne zadržavajte! Tražite ono što ste potrošili, a neka i oni traže ono što su potrošili! To je Allahov sud, On sudi među vama, a Allah je Onaj Koji sve zna i mudar je.", TextEnglish = "O you who have believed, when the believing women come to you as emigrants, examine [i.e., test] them. Allāh is most knowing as to their faith. And if you know them to be believers, then do not return them to the disbelievers; they are not lawful [wives] for them, nor are they lawful [husbands] for them. But give them [i.e., the disbelievers] what they have spent.[1676] And there is no blame upon you if you marry them when you have given them their due compensation [i.e., mahr]. And hold not to marriage bonds with disbelieving women, but ask for what you have spent and let them [i.e., the disbelievers] ask for what they have spent.[1677] That is the judgement of Allāh; He judges between you. And Allāh is Knowing and Wise.", TextGerman = "O die ihr glaubt, wenn gläubige Frauen als Auswanderer zu euch kommen, dann prüft sie. Allah weiß besser über ihren Glauben Bescheid. Wenn ihr sie dann als gläubig erkennt, dann schickt sie nicht zu den Ungläubigen zurück. Weder sind sie ihnen (zur Ehe) erlaubt, noch sind sie ihnen (diesen Frauen) erlaubt. Und gebt ihnen (jedoch), was sie (früher als Morgengabe) ausgegeben haben. Es ist für euch kein Vergehen, sie zu heiraten, wenn ihr ihnen ihren Lohn gebt. Und haltet nicht an der Ehe mit den ungläubigen Frauen fest, und fordert, was ihr (als Morgengabe) ausgegeben habt, zurück. Auch sie sollen zurückfordern, was sie ausgegeben haben. Das ist Allahs Urteil; Er richtet zwischen euch. Und Allah ist Allwissend und Allweise." });
                        surah60.VerseList.Add(new Verse { Number = 11, TextBosnian = "A ako neka od žena vaših nevjernicima umakne, i ako vi poslije u borbi plijen zarobite, onda onima čije su žene umakle vjenčane darove koje su im dali namirite. I bojte se Allaha, u Koga vjerujete!", TextEnglish = "And if you have lost any of your wives to the disbelievers and you subsequently obtain [something],[1678] then give those whose wives have gone the equivalent of what they had spent. And fear Allāh, in whom you are believers.", TextGerman = "Und wenn euch etwas von euren Gattinnen bei den Ungläubigen verlorengeht und ihr dann (in) eine(r) Strafmaßnahme (Beute) macht, dann gebt denjenigen, deren Gattinnen fortgegangen sind, soviel, wie sie (zuvor für sie) ausgegeben haben. Und fürchtet Allah, an Den ihr glaubt." });
                        surah60.VerseList.Add(new Verse { Number = 12, TextBosnian = "O Vjerovjesniče, kada ti dođu vjernice da ti polože prisegu: da neće Allahu nikoga u obožavanju pridruživati, i da neće krasti, i da neće muževima tuđu djecu podmetati, i da ti neće ni u čemu, što je dobro, poslušnost odbijati, ti prisegu njihovu prihvati i moli Allaha da im oprosti; Allah je, zaista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "O Prophet, when the believing women come to you pledging to you that they will not associate anything with Allāh, nor will they steal, nor will they commit unlawful sexual intercourse, nor will they kill their children, nor will they bring forth a slander they have invented between their arms and legs,[1679] nor will they disobey you in what is right - then accept their pledge and ask forgiveness for them of Allāh. Indeed, Allāh is Forgiving and Merciful.", TextGerman = "O Prophet, wenn gläubige Frauen zu dir kommen, um dir den Treueid zu leisten, daß sie Allah nichts beigesellen, nicht stehlen, keine Unzucht begehen, ihre Kinder nicht töten, keine Verleumdung vorbringen, die sie vor ihren (eigenen) Händen und Füßen ersinnen, und sich dir nicht widersetzen in dem, was recht ist, dann nimm ihren Treueid an und bitte Allah für sie um Vergebung. Gewiß, Allah ist Allvergebend und Barmherzig." });
                        surah60.VerseList.Add(new Verse { Number = 13, TextBosnian = "O vi koji vjerujete, ne uzimajte za prisne prijatelje i zaštitnike ljude na koje se Allah rasrdio; oni su izgubili nadu da će bilo kakvu nagradu na ahiretu imati, isto kao što su nevjernici izgubili nadu u vezi s onim koji su u grobovima.", TextEnglish = "O you who have believed, do not make allies of a people with whom Allāh has become angry. They have despaired of [reward in] the Hereafter just as the disbelievers have despaired of [meeting] the companions [i.e., inhabitants] of the graves.", TextGerman = "O die ihr glaubt, nehmt nicht Leute zu Schutzherren, denen Allah zürnt. Sie haben ja die Hoffnung auf das Jenseits aufgegeben, so wie die Ungläubigen die Hoffnung (auf Wiederauferstehung) der (Toten) in den Gräbern aufgegeben haben." });
                        result = surah60;
                        break;
                    #endregion

