                    #region
                    case 61:
                        Surah surah61 = new Surah(); surah61.Name = Constants.Surah61Name; surah61.Number = 61; surah61.VerseList = new List<Verse>();
                        surah61.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah61.VerseList.Add(new Verse { Number = 1, TextBosnian = "Allaha slavi i veliča ono što je na nebesima i ono što je na Zemlji; On je Silni i Mudri!", TextEnglish = "Whatever is in the heavens and whatever is on the earth exalts Allāh,[1681] and He is the Exalted in Might, the Wise.", TextGerman = "Allah preist (alles), was in den Himmeln und was auf der Erde ist. Und Er ist der Allmächtige und Allweise." });
                        surah61.VerseList.Add(new Verse { Number = 2, TextBosnian = "O vi koji vjerujete, zašto govorite ono što ne činite?!", TextEnglish = "O you who have believed, why do you say what you do not do?", TextGerman = "O die ihr glaubt, warum sagt ihr, was ihr nicht tut?" });
                        surah61.VerseList.Add(new Verse { Number = 3, TextBosnian = "Kako samo Allah prezire da govorite ono što ne činite!", TextEnglish = "Greatly hateful in the sight of Allāh is that you say what you do not do.", TextGerman = "Welch schwerwiegende Abscheu erregt es bei Allah, daß ihr sagt, was ihr nicht tut." });
                        surah61.VerseList.Add(new Verse { Number = 4, TextBosnian = "Allah, zaista, voli one koji se na Njegovu putu bore u redovima kao da su zdanje utvrđeno.", TextEnglish = "Indeed, Allāh loves those who fight in His cause in a row as though they are a [single] structure joined firmly.", TextGerman = "Gewiß, Allah liebt diejenigen, die auf Seinem Weg kämpfen in Reihe, als wären sie ein zusammengefügter Bau." });
                        surah61.VerseList.Add(new Verse { Number = 5, TextBosnian = "Kada Musa reče narodu svome: 'O narode moj, zašto me uznemiravate, a dobro znate da sam vam ja Allahov poslanik!', i kad oni skrenuše ustranu, Allah njihova srca skrenu, a Allah neće uputiti narod koji je buntovan.", TextEnglish = "And [mention, O Muḥammad], when Moses said to his people, 'O my people, why do you harm me while you certainly know that I am the messenger of Allāh to you?' And when they deviated, Allāh caused their hearts to deviate. And Allāh does not guide the defiantly disobedient people.", TextGerman = "Und als Musa zu seinem Volk sagte: O mein Volk, warum fügt ihr mir Leid zu, wo ihr doch sicher wißt, daß ich Allahs Gesandter an euch bin? Als sie nun abschweiften, ließ Allah ihre Herzen abschweifen. Und Allah leitet das Volk der Frevler nicht recht." });
                        surah61.VerseList.Add(new Verse { Number = 6, TextBosnian = "A kada Isa, sin Merjemin, reče: 'O sinovi Israilovi, ja sam vam Allahov poslanik, da vam potvrdim prije mene objavljeni Tevrat i da vam donesem radosnu vijest o poslaniku čije je ime Ahmed, koji će poslije mene doći', i kad im je donio jasne dokaze, oni rekoše: 'Ovo je očita vradžbina!'", TextEnglish = "And [mention] when Jesus, the son of Mary, said, 'O Children of Israel, indeed I am the messenger of Allāh to you confirming what came before me of the Torah and bringing good tidings of a messenger to come after me, whose name is Aḥmad.'[1682] But when he came to them with clear evidences, they said, 'This is obvious magic.'[1683]", TextGerman = "Und als ’Isa, der Sohn Maryams, sagte: O Kinder Isra’ils, gewiß, ich bin Allahs Gesandter an euch, das bestätigend, was von der Tora vor mir (offenbart) war, und einen Gesandten verkündend, der nach mir kommen wird: sein Name ist Ahmad’. Als er nun mit den klaren Beweisen zu ihnen kam, sagten sie: Das ist deutliche Zauberei." });
                        surah61.VerseList.Add(new Verse { Number = 7, TextBosnian = "A ima li većeg zulumćara od onoga koji o Allahu laži iznosi dok se u islam poziva?! A Allah neće uputiti ljude zulumćare.", TextEnglish = "And who is more unjust than one who invents about Allāh untruth while he is being invited to Islām. And Allāh does not guide the wrongdoing people.", TextGerman = "Und wer ist ungerechter, als wer gegen Allah Lügen ersinnt, während er zum Islam aufgerufen wird? Und Allah leitet das Volk der Ungerechten nicht recht." });
                        surah61.VerseList.Add(new Verse { Number = 8, TextBosnian = "Oni žele utrnuti Allahovo svjetlo ustima svojim, a Allah će upotpuniti svjetlo Svoje, makar to mrzili nevjernici.", TextEnglish = "They want to extinguish the light of Allāh with their mouths, but Allāh will perfect His light, although the disbelievers dislike it.", TextGerman = "Sie wollen gern Allahs Licht mit ihren Mündern auslöschen. Aber Allah wird Sein Licht vollenden, auch wenn es den Ungläubigen zuwider ist." });
                        surah61.VerseList.Add(new Verse { Number = 9, TextBosnian = "On je Onaj Koji je poslao Svog Poslanika s Uputom i Vjerom Istine da bi je uzdigao iznad svih vjera, makar to višebošci mrzili.", TextEnglish = "It is He who sent His Messenger with guidance and the religion of truth to manifest it over all religion, although those who associate others with Allāh dislike it.", TextGerman = "Er ist es, Der Seinen Gesandten mit der Rechtleitung und der Religion der Wahrheit gesandt hat, um ihr die Oberhand über alle Religion zu geben, auch wenn es den Götzendienern zuwider ist." });
                        surah61.VerseList.Add(new Verse { Number = 10, TextBosnian = "O vi koji vjerujete, hoćete li da vam ukažem na trgovinu, koja će vas iz patnje bolne spasiti?", TextEnglish = "O you who have believed, shall I guide you to a transaction that will save you from a painful punishment?", TextGerman = "O die ihr glaubt, soll ich euch auf einen Handel hinweisen, der euch vor schmerzhafter Strafe rettet?" });
                        surah61.VerseList.Add(new Verse { Number = 11, TextBosnian = "U Allaha i Poslanika Njegova vjerujte i i mecima svojim i životima svojim na Allahovu putu se borite - to vam je, da znate, bolje.", TextEnglish = "[It is that] you believe in Allāh and His Messenger and strive in the cause of Allāh with your wealth and your lives. That is best for you, if you only knew.", TextGerman = "Ihr sollt an Allah und Seinen Gesandten glauben und euch auf Allahs Weg mit eurem Besitz und mit eurer eigenen Person abmühen; das ist besser für euch, wenn ihr nur wißt -," });
                        surah61.VerseList.Add(new Verse { Number = 12, TextBosnian = "On će vam grijehe vaše oprostiti i uvesti vas u džennetske bašče, kroz koje rijeke teku, i u divne dvorove u vrtovima Adna; to je uspjeh veliki.", TextEnglish = "He will forgive for you your sins and admit you to gardens beneath which rivers flow and pleasant dwellings in gardens of perpetual residence. That is the great attainment.", TextGerman = "dann wird Er euch eure Sünden vergeben und euch eingehen lassen in Gärten, durcheilt von Bächen, und in angenehme Wohnungen in den Gärten Edens. Das ist der großartige Erfolg." });
                        surah61.VerseList.Add(new Verse { Number = 13, TextBosnian = "A dat će vam i drugu blagodat koju volite: Allahovu pomoć i skoru pobjedu! Zato obraduj vjernike!", TextEnglish = "And [you will obtain] another [favor] that you love - victory from Allāh and an imminent conquest; and give good tidings to the believers.", TextGerman = "Und (noch) eine andere (Huld), die ihr liebt (, wird euch gewährt): Hilfe von Allah und ein naher Sieg. Und (so) verkünde (diese) frohe Botschaft den Gläubigen." });
                        surah61.VerseList.Add(new Verse { Number = 14, TextBosnian = "O vi koji vjerujete, budite pomagači Allahovi, kao što je rekao Isa, sin Merjemin, prisnim sljedbenicima: 'Ko su moji pomagači u pozivanju Allahu?' Prisni sljedbenici odgovoriše: 'Mi smo Allahovi pomagači!' Onda je jedna skupina iz potomaka Israilovih povjerovala, a druga skupina uznevjerovala! Pa smo one koji su vjerovali protiv njihovih neprijatelja osnažili pa su postali nadmoćni.", TextEnglish = "O you who have believed, be supporters of Allāh, as when Jesus, the son of Mary, said to the disciples, 'Who are my supporters for Allāh?' The disciples said, 'We are supporters of Allāh.' And a faction of the Children of Israel believed and a faction disbelieved. So We supported those who believed against their enemy, and they became dominant.", TextGerman = "O die ihr glaubt, seid Allahs Helfer, so wie ’Isa, der Sohn Maryams, zu den Jüngern sagte: Wer sind meine Helfer (auf dem Weg) zu Allah (hin)? Die Jünger sagten: Wir sind Allahs Helfer. So glaubte ein Teil von den Kindern Isra’ils, während ein (anderer) Teil ungläubig war. Da stärkten Wir diejenigen, die glaubten, gegen ihre Feinde, und so bekamen sie die Oberhand." });
                        result = surah61;
                        break;
                    #endregion

                    // 
                    #region
                    case 62:
                        Surah surah62 = new Surah(); surah62.Name = Constants.Surah62Name; surah62.Number = 62; surah62.VerseList = new List<Verse>();
                        surah62.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah62.VerseList.Add(new Verse { Number = 1, TextBosnian = "Allaha slavi i veliča sve što je na nebesima i ono što je na Zemlji, Vladara, Blagoslovljenoga i Štovanoga, Silnoga i Mudroga.", TextEnglish = "Whatever is in the heavens and whatever is on the earth is exalting Allāh,[1685] the Sovereign, the Pure, the Exalted in Might, the Wise.[1686]", TextGerman = "Es preist Allah (alles), was in den Himmeln und was auf der Erde ist, (Ihn), den König, den Heiligen, den Allmächtigen und Allweisen." });
                        surah62.VerseList.Add(new Verse { Number = 2, TextBosnian = "On je nepismenima poslao Poslanika, jednog između njih, da im ajete Njegove kazuje i da ih očisti i da ih Knjizi i mudrosti nauči, iako su prije bili u očitaj zabludi,", TextEnglish = "It is He who has sent among the unlettered [Arabs] a Messenger from themselves reciting to them His verses and purifying them and teaching them the Book [i.e., the Qur’ān] and wisdom [i.e., the sunnah] - although they were before in clear error -", TextGerman = "Er ist es, Der unter den Schriftunkundigen einen Gesandten von ihnen hat erstehen lassen, der ihnen Seine Zeichen verliest, sie läutert und sie das Buch und die Weisheit lehrt, obgleich sie sich ja zuvor in deutlichem Irrtum befanden -," });
                        surah62.VerseList.Add(new Verse { Number = 3, TextBosnian = "i drugima koji im se još nisu priključili, On je Silni i Mudri.", TextEnglish = "And [to] others of them who have not yet joined them. And He is the Exalted in Might, the Wise.", TextGerman = "und andere vor ihnen, die sich ihnen noch nicht angeschlossen haben. Und Er ist der Allmächtige und Allweise." });
                        surah62.VerseList.Add(new Verse { Number = 4, TextBosnian = "To je Allahova milost koju On daje onome kome hoće - a Allah je posjednik obilja velikog.", TextEnglish = "That is the bounty of Allāh, which He gives to whom He wills, and Allāh is the possessor of great bounty.", TextGerman = "Das ist Allahs Huld, die Er gewährt, wem Er will. Und Allah besitzt große Huld." });
                        surah62.VerseList.Add(new Verse { Number = 5, TextBosnian = "Oni kojima je Tevrat da ga nose dat, pa ga potom ne nose, slični su magarcu koji knjige nosi. O kako su loši oni koji Allahove ajete i znakove poriču! A Allah neće uputiti na Pravi put ljude zulumćare.", TextEnglish = "The example of those who were entrusted with the Torah and then did not take it on[1687] is like that of a donkey who carries volumes [of books].[1688] Wretched is the example of the people who deny the signs of Allāh. And Allāh does not guide the wrongdoing people.", TextGerman = "Das Gleichnis derjenigen, denen die Tora auferlegt wurde, die sie aber hierauf doch nicht getragen haben, ist das eines Esels, der Bücher trägt. Schlimm ist das Gleichnis der Leute, die Allahs Zeichen für Lüge erklären. Und Allah leitet das ungerechte Volk nicht recht." });
                        surah62.VerseList.Add(new Verse { Number = 6, TextBosnian = "Reci: 'O jevreji, ako tvrdite da ste vi od svih ljudi jedini Allahovi miljenici, onda smrt poželite, ako istinu govorite.'", TextEnglish = "Say, 'O you who are Jews, if you claim that you are allies of Allāh, excluding the [other] people, then wish for death, if you should be truthful.'", TextGerman = "Sag: O die ihr dem Judentum angehört, wenn ihr behauptet, daß ihr Allahs Schützlinge unter Ausschluß der (anderen) Menschen seid, dann wünscht (euch doch) den Tod, wenn ihr wahrhaftig seid." });
                        surah62.VerseList.Add(new Verse { Number = 7, TextBosnian = "A zbog onoga što ruke njihove čine neće je nikad poželjeti; Allah dobro zna zulumćare.", TextEnglish = "But they will not wish for it, ever, because of what their hands have put forth. And Allāh is Knowing of the wrongdoers.", TextGerman = "Aber sie wünschen sich ihn niemals wegen dessen, was ihre Hände vorausgeschickt haben. Und Allah weiß über die Ungerechten Bescheid." });
                        surah62.VerseList.Add(new Verse { Number = 8, TextBosnian = "Reci: 'Smrt od koje bježite zaista će vas stići. Zatim ćete Poznavatelju onoga što je čulima nedokučivo i onoga što je pojavno vraćeni biti i On će vas, o onom što ste radili, obavijestiti.'", TextEnglish = "Say, 'Indeed, the death from which you flee - indeed, it will meet you. Then you will be returned to the Knower of the unseen and the witnessed, and He will inform you about what you used to do.'", TextGerman = "Sag: Gewiß, der Tod, vor dem ihr flieht -, gewiß, er wird euch begegnen. Hierauf werdet ihr zu dem Kenner des Verborgenen und des Offenbaren zurückgebracht, und dann wird Er euch kundtun, was ihr zu tun pflegtet." });
                        surah62.VerseList.Add(new Verse { Number = 9, TextBosnian = "O vi koji vjerujete, kad u petak na namaz budete pozvani, požurite na spominjanje Allaha, i kupoprodaju ostavite, to vam je bolje, ako znate!", TextEnglish = "O you who have believed, when [the adhān] is called for the prayer on the day of Jumuʿah [Friday], then proceed to the remembrance of Allāh and leave trade. That is better for you, if you only knew.", TextGerman = "O die ihr glaubt, wenn zum Gebet gerufen wird am Freitag, dann eilt zu Allahs Gedenken und laßt das Kaufgeschäft. Das ist besser für euch, wenn ihr wißt." });
                        surah62.VerseList.Add(new Verse { Number = 10, TextBosnian = "A kad se namaz završi, onda se po zemlji raziđite i za Allahovim dobrima tragajte i Allaha mnogo spominjite, da biste uspjeli.", TextEnglish = "And when the prayer has been concluded, disperse within the land and seek from the bounty of Allāh, and remember Allāh often that you may succeed.", TextGerman = "Wenn das Gebet beendet ist, dann breitet euch im Land aus und trachtet nach etwas von Allahs Huld. Und gedenkt Allahs viel, auf daß es euch wohl ergehen möge!" });
                        surah62.VerseList.Add(new Verse { Number = 11, TextBosnian = "Ali, kad oni kakvu robu trgovačku ili zabavu ugledaju, pohrle joj i tebe ostave sama da stojiš. Reci: 'Ono što je u Allaha bolje je i od zabave i od trgovine. A Allah je Opskrbitelj najbolji.'", TextEnglish = "But [on one occasion] when they saw a transaction or a diversion, [O Muḥammad], they rushed to it and left you standing. Say, 'What is with Allāh is better than diversion and than a transaction, and Allāh is the best of providers.'", TextGerman = "Und wenn sie einen Handel oder eine Zerstreuung sehen, laufen sie dorthin auseinander und lassen dich stehen. Sag: Was bei Allah ist, ist besser als Zerstreuung und als Handel. Und Allah ist der beste Versorger." });
                        result = surah62;
                        break;
