                    #region
                    case 63:
                        Surah surah63 = new Surah(); surah63.Name = Constants.Surah63Name; surah63.Number = 63; surah63.VerseList = new List<Verse>();
                        surah63.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah63.VerseList.Add(new Verse { Number = 1, TextBosnian = "Kad ti licemjeri dolaze, oni govore: 'Mi svjedočimo da si ti, zaista, Allahov poslanik!', i Allah zna da si ti, zaista, Njegov poslanik, a Allah svjedoči i da su licemjeri pravi lašci.", TextEnglish = "When the hypocrites come to you, [O Muḥammad], they say, 'We testify that you are the Messenger of Allāh.' And Allāh knows that you are His Messenger, and Allāh testifies that the hypocrites are liars.", TextGerman = "Wenn die Heuchler zu dir kommen, sagen sie: Wir bezeugen, daß du wahrlich Allahs Gesandter bist. Allah weiß, daß du fürwahr Sein Gesandter bist; doch Allah bezeugt, daß die Heuchler wahrlich lügen." });
                        surah63.VerseList.Add(new Verse { Number = 2, TextBosnian = "Oni su svoje zakletve kao štit uzeli, pa od Allahova puta odvraćaju. Ružno je, doista, kako postupaju.", TextEnglish = "They have taken their oaths as a cover, so they averted [people] from the way of Allāh. Indeed, it was evil that they were doing.", TextGerman = "Sie haben sich ihre Eide zu einem Schutzschirm genommen und halten so von Allahs Weg ab. Gewiß, wie böse ist, was sie zu tun pflegen." });
                        surah63.VerseList.Add(new Verse { Number = 3, TextBosnian = "To je zato što su vjernici bili, pa nevjernici postali, i onda su im srca zapečaćena, pa ne shvaćaju.", TextEnglish = "That is because they believed, and then they disbelieved; so their hearts were sealed over, and they do not understand.", TextGerman = "Dies, weil sie (zuerst) gläubig gewesen, hierauf aber ungläubig geworden sind. Da wurden ihre Herzen versiegelt; so verstehen sie nicht." });
                        surah63.VerseList.Add(new Verse { Number = 4, TextBosnian = "Kad ih pogledaš, njihov te izgled zadivljuje; a kad progovore, ti slušaš riječi njihove - međutim, oni su kao šuplji naslonjeni balvani, i misle da je svaki povik protiv njih. Oni su pravi neprijatelji, pa ih se pričuvaj! Allah ih ubio kuda se odmeću!", TextEnglish = "And when you see them, their forms please you, and if they speak, you listen to their speech. [They are] as if they were pieces of wood propped up[1690] - they think that every shout is against them. They are the enemy, so beware of them. May Allāh destroy them; how are they deluded?", TextGerman = "Und wenn du sie siehst, gefällt dir ihr Äußeres, und wenn sie reden, hörst du ihren Worten zu. Sie sind wie angelehnte Hölzer. Sie meinen, jeder Schrei sei gegen sie. Sie sind die (wahren) Feinde; so sieh dich vor ihnen vor. Allah bekämpfe sie! Wie sie sich (doch) abwendig machen lassen!" });
                        surah63.VerseList.Add(new Verse { Number = 5, TextBosnian = "A kad im bude rečeno: 'Dođite, Allahov Poslanik će moliti da vam se oprosti!', oni glavama svojim tresu i vidiš ih kako nadmeno odbijaju.", TextEnglish = "And when it is said to them, 'Come, the Messenger of Allāh will ask forgiveness for you,' they turn their heads aside and you see them evading while they are arrogant.", TextGerman = "Und wenn zu ihnen gesagt wird: Kommt her, damit Allahs Gesandter für euch um Vergebung bittet, drehen sie ihre Köpfe zur Seite, und du siehst sie sich hochmütig abwenden." });
                        surah63.VerseList.Add(new Verse { Number = 6, TextBosnian = "Isto im je - molio ti oprost za njih ili ne molio, Allah im neće oprostiti. Allah, doista, neće na Pravi put uputiti narod koji je buntovan.", TextEnglish = "It is all the same for them whether you ask forgiveness for them or do not ask forgiveness for them; never will Allāh forgive them. Indeed, Allāh does not guide the defiantly disobedient people.", TextGerman = "Gleich ist es in Bezug auf sie, ob du für sie um Vergebung bittest oder nicht bittest; Allah wird ihnen nicht vergeben. Gewiß, Allah leitet das Volk der Frevler nicht recht." });
                        surah63.VerseList.Add(new Verse { Number = 7, TextBosnian = "Oni govore: 'Ne udjeljujte ništa onima koji su uz Allahova Poslanika, da bi ga napustili!' A riznice nebesa i Zemlje Allahove su, ali licemjeri ne shvataju.", TextEnglish = "They are the ones who say, 'Do not spend on those who are with the Messenger of Allāh until they disband.' And to Allāh belong the depositories of the heavens and the earth, but the hypocrites do not understand.", TextGerman = "Sie sind es, die sagen: Gebt nicht aus für diejenigen, die bei Allahs Gesandtem sind, bis sie (von ihm weg) auseinander (und davon)laufen. Doch Allah gehören die Schatzkammern der Himmel und der Erde. Aber die Heuchler verstehen nicht." });
                        surah63.VerseList.Add(new Verse { Number = 8, TextBosnian = "Oni govore: 'Ako se vratimo u Medinu, sigurno će jači istjerati iz nje slabijeg!' A snaga je u Allaha i u Poslanika Njegova i u vjernika, ali licemjeri ne znaju.", TextEnglish = "They say, 'If we return to al-Madīnah, the more honored [for power] will surely expel therefrom the more humble.' And to Allāh belongs [all] honor, and to His Messenger, and to the believers, but the hypocrites do not know.", TextGerman = "Sie sagen: Wenn wir nach al-Madina zurückkehren, so wird ganz gewiß der Mächtigere den Niedrigeren aus ihr vertreiben. Doch Allah gehört die Macht, und auch Seinem Gesandten und den Gläubigen. Aber die Heuchler wissen nicht." });
                        surah63.VerseList.Add(new Verse { Number = 9, TextBosnian = "O vi koji vjerujete, neka vas imanja vaša i djeca vaša ne zabave od spominjanja Allaha. A oni koji to učine, pravi su gubitnici.", TextEnglish = "O you who have believed, let not your wealth and your children divert you from the remembrance of Allāh. And whoever does that - then those are the losers.", TextGerman = "O die ihr glaubt, nicht ablenken sollen euch euer Besitz und eure Kinder von Allahs Gedenken. Diejenigen, die dies tun, das sind die Verlierer." });
                        surah63.VerseList.Add(new Verse { Number = 10, TextBosnian = "I udjeljujte od onoga čime vas Mi opskrbljujemo prije nego nekom od vas smrt dođe, pa da onda rekne: 'Gospodaru moj, da me još samo kratko vrijeme zadržiš, pa da zekat udjeljujem i da dobar budem!'", TextEnglish = "And spend [in the way of Allāh] from what We have provided you before death approaches one of you and he says, 'My Lord, if only You would delay me for a brief term so I would give charity and be of the righteous.'", TextGerman = "Und gebt aus von dem, womit Wir euch versorgt haben, bevor zu einem von euch der Tod kommt und er dann sagt: Mein Herr, würdest Du mich doch auf eine kurze Frist zurückstellen! Dann würde ich Almosen geben und zu den Rechtschaffenen gehören." });
                        surah63.VerseList.Add(new Verse { Number = 11, TextBosnian = "Allah sigurno neće ostaviti u životu nikoga kome smrt njegova dođe. Allah u potpunosti zna ono što vi radite.", TextEnglish = "But never will Allāh delay a soul when its time has come. And Allāh is Aware of what you do.", TextGerman = "Allah wird aber keine Seele zurückstellen, wenn ihre Frist kommt. Und Allah ist Kundig dessen, was ihr tut." });
                        result = surah63;
                        break;
                    #endregion

                    // 
                    #region
                    case 64:
                        Surah surah64 = new Surah(); surah64.Name = Constants.Surah64Name; surah64.Number = 64; surah64.VerseList = new List<Verse>();
                        surah64.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah64.VerseList.Add(new Verse { Number = 1, TextBosnian = "Allaha slavi i veliča sve što je na nebesima i sve što je na Zemlji. Njemu sva vlast i Njemu sva pohvala pripada; On nad svime ima moć!", TextEnglish = "Whatever is in the heavens and whatever is on the earth is exalting Allāh.[1692] To Him belongs dominion, and to Him belongs [all] praise, and He is over all things competent.", TextGerman = "Es preist Allah (alles), was in den Himmeln und (alles) was auf der Erde ist. Ihm gehört die Herrschaft, und Ihm gehört (alles) Lob, und Er hat zu allem die Macht." });
                        surah64.VerseList.Add(new Verse { Number = 2, TextBosnian = "On je Onaj Koji vas stvara, pa ima među vama i nevjernika, a ima među vama i vjernika. Sve što vi radite Allah dobro vidi.", TextEnglish = "It is He who created you, and among you is the disbeliever, and among you is the believer. And Allāh, of what you do, is Seeing.", TextGerman = "Er ist es, Der euch erschaffen hat. So gibt es unter euch Ungläubige und gibt es unter euch Gläubige, und was ihr tut, sieht Allah wohl." });
                        surah64.VerseList.Add(new Verse { Number = 3, TextBosnian = "On je nebesa i Zemlju s Istinom stvorio i uobličio vas i likove vaše čini najljepšim, i kod Njega je konačno odredište.", TextEnglish = "He created the heavens and earth in truth and formed you and perfected your forms; and to Him is the [final] destination.", TextGerman = "Er hat die Himmel und die Erde in Wahrheit erschaffen und euch gestaltet, und da(bei) eure Gestalten schön gemacht. Und zu Ihm ist der Ausgang." });
                        surah64.VerseList.Add(new Verse { Number = 4, TextBosnian = "On zna šta na nebesima i na Zemlji postoji i zna šta krijete i šta pokazujete! Allah dobro zna šta grudi kriju.", TextEnglish = "He knows what is within the heavens and earth and knows what you conceal and what you declare. And Allāh is Knowing of that within the breasts.", TextGerman = "Er weiß, was in den Himmeln und auf der Erde ist, und Er weiß, was ihr geheimhaltet und was ihr offenlegt. Und Allah weiß über das Innerste der Brüste Bescheid." });
                        surah64.VerseList.Add(new Verse { Number = 5, TextBosnian = "Nije li do vas doprla vijest o onima koji još davno nisu vjerovali, pa su kobnost postupka svoga iskusili - a još ih i kazna bolna čeka?", TextEnglish = "Has there not come to you the news of those who disbelieved before? So they tasted the bad consequence of their affair, and they will have a painful punishment.", TextGerman = "Ist zu euch nicht die Kunde von denjenigen gekommen, die zuvor ungläubig waren und dann die schlimmen Folgen ihres Verhaltens kosteten? Und für sie wird es schmerzhafte Strafe geben." });
                        surah64.VerseList.Add(new Verse { Number = 6, TextBosnian = "To je zato što su govorili, kad su im poslanici njihovi jasne dokaze donosili: 'Zar da nas ljudi upućuju Pa nisu vjerovali i okretali su se; a Allahu nije niko potreban, Allah je neovisan i hvaljen!", TextEnglish = "That is because their messengers used to come to them with clear evidences, but they said, 'Shall human beings guide us?' and disbelieved and turned away. And Allāh dispensed [with them]; and Allāh is Free of need and Praiseworthy.", TextGerman = "Dies, weil ihre Gesandten immer wieder zu ihnen mit den klaren Beweisen kamen, sie aber sagten: Sollen (etwa) menschliche Wesen uns rechtleiten? So wurden sie ungläubig und kehrten sich ab. Doch Allah bedurfte (ihrer) nicht, und Allah ist Unbedürftig und Lobenswürdig." });
                        surah64.VerseList.Add(new Verse { Number = 7, TextBosnian = "Nevjernici misle da neće biti oživljeni. Reci: 'Hoćete, Gospodara mi moga, sigurno ćete biti oživljeni, pa ćete o onom što ste radili, doista, biti obaviješteni!', a to je Allahu lahko.", TextEnglish = "Those who disbelieve have claimed that they will never be resurrected. Say, 'Yes, by my Lord, you will surely be resurrected; then you will surely be informed of what you did. And that, for Allāh, is easy.'", TextGerman = "Diejenigen, die ungläubig sind, behaupten, daß sie nicht auferweckt werden. Sag: Aber ja doch, bei meinem Herrn, ihr werdet ganz gewiß auferweckt werden; hierauf wird euch ganz gewiß das kundgetan, was ihr getan habt. Und dies ist Allah ein leichtes." });
                        surah64.VerseList.Add(new Verse { Number = 8, TextBosnian = "Zato, vjerujte u Allaha i Poslanika Njegova i u Svjetlo koje objavljujemo, Allah u potpunosti zna ono što vi radite.", TextEnglish = "So believe in Allāh and His Messenger and the light [i.e., the Qur’ān] which We have sent down. And Allāh is Aware of what you do.", TextGerman = "Darum glaubt an Allah und Seinen Gesandten und das Licht, das Wir hinabgesandt haben. Und Allah ist dessen, was ihr tut, Kundig." });
                        surah64.VerseList.Add(new Verse { Number = 9, TextBosnian = "Na Dan kad vas On sakupi, na Dan zbora, to je Dan međusobne obmane. Ko bude vjerovao i dobra djela činio, On će mu prekriti loša djela njegova i uvest će ga u džennetske bašče kroz koje rijeke teku, u njima će vječno, zanavijek boraviti. To će uspjeh veliki biti!", TextEnglish = "The Day He will assemble you for the Day of Assembly - that is the Day of Deprivation.[1693] And whoever believes in Allāh and does righteousness - He will remove from him his misdeeds and admit him to gardens beneath which rivers flow, wherein they will abide forever. That is the great attainment.", TextGerman = "Am Tag, da Er euch zum Tag der Versammlung versammeln wird. Das ist der Tag der Übervorteilung. Wer an Allah glaubt und rechtschaffen handelt, dem tilgt Er seine bösen Taten, und den wird Er in Gärten eingehen lassen, durcheilt von Bächen, ewig und auf immer darin zu bleiben; das ist der großartige Erfolg." });
                        surah64.VerseList.Add(new Verse { Number = 10, TextBosnian = "A oni koji nisu vjerovali i koji su ajete i znakove Naše poricali, bit će stanovnici u Vatri; u njoj će vječno ostati, a to gdje će skončati bit će užasno mjesto!", TextEnglish = "But the ones who disbelieved and denied Our verses - those are the companions of the Fire, abiding eternally therein; and wretched is the destination.", TextGerman = "Und diejenigen, die ungläubig sind und Unsere Zeichen für Lüge erklären, das sind Insassen des (Höllen)feuers, ewig darin zu bleiben - ein schlimmer Ausgang!" });
                        surah64.VerseList.Add(new Verse { Number = 11, TextBosnian = "Nikakva nevolja ne pogađa osim s Allahovom dozvolom! A ko u Allaha vjeruje, On će uputiti njegovo srce, a Allah sve dobro zna.", TextEnglish = "No disaster strikes except by permission of Allāh. And whoever believes in Allāh - He will guide his heart. And Allāh is Knowing of all things.", TextGerman = "Kein Unglück trifft (jemanden), außer mit Allahs Erlaubnis. Und wer an Allah glaubt, dessen Herz leitet Er recht. Und Allah weiß über alles Bescheid." });
                        surah64.VerseList.Add(new Verse { Number = 12, TextBosnian = "I pokoravajte se Allahu i Poslaniku Njegovu; a ako se okrenete, pa obaveza Našeg Poslanika je samo jasna obznana!", TextEnglish = "And obey Allāh and obey the Messenger; but if you turn away - then upon Our Messenger is only [the duty of] clear notification.", TextGerman = "Und gehorcht Allah und gehorcht dem Gesandten. Wenn ihr euch jedoch abkehrt, so obliegt Unserem Gesandten nur die deutliche Übermittlung (der Botschaft)." });
                        surah64.VerseList.Add(new Verse { Number = 13, TextBosnian = "Samo je Allah Bog, i neka se vjernici samo u Allaha pouzdaju!", TextEnglish = "Allāh - there is no deity except Him. And upon Allāh let the believers rely.", TextGerman = "Allah - es gibt keinen Gott außer Ihm. Und auf Allah sollen sich die Gläubigen verlassen." });
                        surah64.VerseList.Add(new Verse { Number = 14, TextBosnian = "O vi koji vjerujete, među suprugama vašim i djecom vašom, doista, imate neprijatelja, pa ih se pričuvajte! A ako preko toga pređete i ne korite i oprostite, pa Allah je, zaista, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "O you who have believed, indeed, among your spouses and your children are enemies to you, so beware of them. But if you pardon and overlook and forgive - then indeed, Allāh is Forgiving and Merciful.", TextGerman = "O die ihr glaubt, unter euren Gattinnen und euren Kindern gibt es welche, die euch feind sind; so seht euch vor ihnen vor. Wenn ihr aber verzeiht, nachsichtig seid und vergebt - gewiß, so ist Allah Allvergebend und Barmherzig." });
                        surah64.VerseList.Add(new Verse { Number = 15, TextBosnian = "Imanja vaša i djeca vaša samo su iskušenje, a u Allaha je nagrada velika.", TextEnglish = "Your wealth and your children are but a trial, and Allāh has with Him a great reward.", TextGerman = "Euer Besitz und eure Kinder sind nur eine Versuchung; Allah aber - bei Ihm gibt es großartigen Lohn." });
                        surah64.VerseList.Add(new Verse { Number = 16, TextBosnian = "Pa Allaha se bojte koliko god možete, i slušajte i pokoravajte se i udjeljujte za svoje dobro. A oni koji budu sačuvani gramzivosti i tvrdičluka, pa takvi su baš uspjeli.", TextEnglish = "So fear Allāh as much as you are able and listen and obey and spend [in the way of Allāh]; it is better for your selves. And whoever is protected from the stinginess of his soul - it is those who will be the successful.", TextGerman = "Daher fürchtet Allah, soweit ihr könnt. Und hört zu und gehorcht und gebt Gutes für euch selbst aus. Und diejenigen, die vor ihrer eigenen Habsucht bewahrt bleiben, das sind diejenigen, denen es wohl ergeht." });
                        surah64.VerseList.Add(new Verse { Number = 17, TextBosnian = "A ako Allahu lijep zajam date, On će vam ga uvećati i oprostit će vam. Allah je Zahvalan i Onaj Koji ne žuri s kaznom.", TextEnglish = "If you loan Allāh a goodly loan, He will multiply it for you and forgive you. And Allāh is [most] Appreciative[1694] and Forbearing,[1695]", TextGerman = "Wenn ihr Allah ein gutes Darlehen gebt, wird Er es euch vervielfachen und euch vergeben. Und Allah ist stets zu Dank bereit und Nachsichtig." });
                        surah64.VerseList.Add(new Verse { Number = 18, TextBosnian = "Poznavatelj onoga što je čulima nedokučivo i onoga što je pojavno, Silni i Mudri.", TextEnglish = "Knower of the unseen and the witnessed, the Exalted in Might, the Wise.", TextGerman = "(Er ist) der Kenner des Verborgenen und des Offenbaren, der Allmächtige und Allweise." });
                        result = surah64;
                        break;
                    #endregion

