                        Surah surah65 = new Surah(); surah65.Name = Constants.Surah65Name; surah65.Number = 65; surah65.VerseList = new List<Verse>();
                        surah65.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah65.VerseList.Add(new Verse { Number = 1, TextBosnian = "O Vjerovjesniče, kad htjednete žene pustiti, vi ih u vrijeme kad su čiste pustite. i brojte to vrijeme, i Allaha, Gospodara svoga, bojte se. Ne izgonite ih iz kuća njihovih, a ni one neka ne izlaze, osim ako očito sramno djelo učine. To su Allahove granice. Onaj koji Allahove propise krši, sam sebi je zulum učinio. Šta znaš, Allah će, možda, poslije toga nešto novo odrediti!?", TextEnglish = "O Prophet, when you [Muslims] divorce women, divorce them for [the commencement of] their waiting period and keep count of the waiting period,[1697] and fear Allāh, your Lord. Do not turn them out of their [husbands'] houses, nor should they [themselves] leave [during that period] unless they are committing a clear immorality. And those are the limits [set by] Allāh. And whoever transgresses the limits of Allāh has certainly wronged himself. You know not; perhaps Allāh will bring about after that a [different] matter.[1698]", TextGerman = "O Prophet, wenn ihr euch von Frauen scheidet, dann scheidet euch von ihnen auf ihre Wartezeit hin, und berechnet die Wartezeit. Und fürchtet Allah, euren Herrn. Weist sie nicht aus ihren Häusern aus; sie sollen auch nicht selbst ausziehen, außer, sie begehen etwas klar Abscheuliches. Dies sind Allahs Grenzen.Wer aber Allahs Grenzen übertritt, der fügt sich ja selbst Unrecht zu. Du weißt nicht, vielleicht führt Allah danach eine neue Lage herbei." });
                        surah65.VerseList.Add(new Verse { Number = 2, TextBosnian = "Pa kada one ispune vrijeme svog čekanja, vi ih ili na lijep način zadržite, ili se lijepo od njih konačno rastavite, i kao svjedoke dvojicu svojih pravednih ljudi uzmite, i svjedočenje radi Allaha obavite! To je savjet za onog koji u Allaha i u Posljednji dan vjeruje - a onome koji se Allaha boji, On će izlaz dati.", TextEnglish = "And when they have [nearly] fulfilled their term, either retain them according to acceptable terms or part with them according to acceptable terms. And bring to witness two just men from among you and establish the testimony for [the acceptance of] Allāh. That is instructed to whoever should believe in Allāh and the Last Day. And whoever fears Allāh - He will make for him a way out[1699]", TextGerman = "Wenn sie dann ihre Frist erreichen, so behaltet sie in rechtlicher Weise oder trennt euch von ihnen in rechtlicher Weise. Und nehmt zwei gerechte Personen von euch zu Zeugen, und legt das Zeugnis (in Aufrichtigkeit) um Allahs willen ab. Damit wird ermahnt, wer an Allah und den Jüngsten Tag glaubt. Und wer Allah fürchtet, dem schafft Er einen Ausweg" });
                        surah65.VerseList.Add(new Verse { Number = 3, TextBosnian = "i opskrbit će ga odakle se i ne nada; onome koji se u Allaha pouzdaje, On mu je dovoljan. Allah će, zaista, ispuniti ono što je odlučio; Allah je svemu već rok odredio.", TextEnglish = "And will provide for him from where he does not expect. And whoever relies upon Allāh - then He is sufficient for him. Indeed, Allāh will accomplish His purpose. Allāh has already set for everything a [decreed] extent.", TextGerman = "und gewährt ihm Versorgung, von wo (aus) er damit nicht rechnet. Und wer sich auf Allah verläßt, dem ist Er seine Genüge. Allah wird gewiß (die Durchführung) seine(r) Angelegenheit erreichen. Allah legt ja für alles ein Maß fest." });
                        surah65.VerseList.Add(new Verse { Number = 4, TextBosnian = "A one žene vaše, koje su nadu u mjesečno pranje izgubile - ako sumnjate - one treba da čekaju tri mjeseca, a i one koje ga još nisu dobile! Vrijeme čekanja za trudne žene jeste porod. A onome ko se Allaha boji, On će u svemu lahkoću dati.", TextEnglish = "And those who no longer expect menstruation among your women - if you doubt, then their period is three months, and [also for] those who have not menstruated. And for those who are pregnant, their term is until they give birth.[1700] And whoever fears Allāh - He will make for him of his matter ease.", TextGerman = "Und diejenigen von euren Frauen, die keine Monatsblutung mehr erwarten, wenn ihr im Zweifel seid, so ist ihre Wartezeit drei Monate; und ebenso derjenigen, die (noch) keine Monatsblutung haben. Diejenigen, die schwanger sind - ihre Frist ist (erreicht), wenn sie mit dem niederkommen, was sie (in ihren Leibern) tragen. Und wer Allah fürchtet, dem schafft Er in seiner Angelegenheit Erleichterung." });
                        surah65.VerseList.Add(new Verse { Number = 5, TextBosnian = "To je Allahov propis koji vam On objavljuje. A onome ko se bude Allaha bojao, On će loše postupke pokriti i još mu nagradu uvećati.", TextEnglish = "That is the command of Allāh, which He has sent down to you; and whoever fears Allāh - He will remove for him his misdeeds and make great for him his reward.", TextGerman = "Das ist Allahs Anordnung, die Er zu euch (als Offenbarung) herabgesandt hat. Und wer Allah fürchtet, dem tilgt Er seine bösen Taten und dem gewährt Er großartigen Lohn." });
                        surah65.VerseList.Add(new Verse { Number = 6, TextBosnian = "Njih ostavite da stanuju tamo gdje i vi stanujete, prema svojim mogućnostima, i ne činite im teškoće, zato da biste na njih pritisak izvršili. Ako su trudne, snosite troškove njihovog izdržavanja sve dok se ne porode; a ako vam djecu doje, onda im dajte naknadu i sporazumite se međusobno na lijep način. A ako nastanu razmirice, neka mu onda druga doji dijete.", TextEnglish = "Lodge them[1701] [in a section] of where you dwell out of your means and do not harm them in order to oppress them.[1702] And if they should be pregnant, then spend on them until they give birth. And if they breastfeed for you, then give them their payment and confer among yourselves in the acceptable way; but if you are in discord, then there may breastfeed for him [i.e., the father] another woman.[1703]", TextGerman = "Laßt sie dort wohnen, wo ihr (selbst) wohnt, von dem, was ihr euch leisten könnt. Und fügt ihnen keinen Schaden zu, um sie in die Enge zu treiben. Und wenn sie schwanger sind, dann gebt für sie (das Nötige) aus, bis sie mit dem niederkommen, was sie (in ihren Leibern) tragen. Wenn sie für euch (das Kind) stillen, dann gebt ihnen ihren Lohn, und beratet untereinander (darüber) in rechtlicher Weise. Wenn ihr aber einander Schwierigkeiten bereitet, dann wird (das Kind) für ihn eine andere stillen." });
                        surah65.VerseList.Add(new Verse { Number = 7, TextBosnian = "Neka imućan prema bogatstvu svome snosi trošak dojenja, a onaj koji je u oskudici, neka izdvaja prema tome koliko mu je Allah dao. Allah nikog ne zadužuje više nego što mu je dao; Allah će, sigurno, poslije tegobe, olakšanje dati.", TextEnglish = "Let a man of wealth spend from his wealth, and he whose provision is restricted - let him spend from what Allāh has given him. Allāh does not charge a soul except [according to] what He has given it. Allāh will bring about, after hardship, ease [i.e., relief].", TextGerman = "Der Wohlhabende soll entsprechend seinem Wohlstand (die Aufwendungen) ausgeben. Und wem seine Versorgung bemessen (zugeteilt) wurde, der soll (eben) von dem ausgeben, was Allah ihm gegeben hat. Allah erlegt keiner Seele mehr auf als das, was Er ihr gegeben hat. Allah wird nach Schwierigkeit Erleichterung schaffen." });
                        surah65.VerseList.Add(new Verse { Number = 8, TextBosnian = "A koliko li samo naselja bi što se o zapovijed Gospodara svoga i Njegovih poslanika ogluši?! Pa smo s njima žestoko račun sveli i kaznili ih - a i kaznit ćemo ih - patnjom užasnom!", TextEnglish = "And how many a city was insolent toward the command of its Lord and His messengers, so We took it to severe account and punished it with a terrible punishment.", TextGerman = "Und wieviele Städte lehnten sich gegen den Befehl ihres Herrn und Seiner Gesandten auf, worauf Wir sie einer strengen Abrechnung unterzogen und mit entsetzlicher Strafe straften." });
                        surah65.VerseList.Add(new Verse { Number = 9, TextBosnian = "Oni su pogubnost postupaka svojih iskusili, a propast je bila - i bit će - njihov kraj.", TextEnglish = "And it tasted the bad consequence of its affair [i.e., rebellion], and the outcome of its affair was loss.", TextGerman = "Da kosteten sie die schlimmen Folgen ihres Verhaltens, und das Ende ihres Verhaltens war (nur) Verlust." });
                        surah65.VerseList.Add(new Verse { Number = 10, TextBosnian = "Allah je za njih bolnu patnju pripremio; zato se bojte Allaha, o vi koji ste razumom obdareni, vi koji vjerujete! Allah vam je već spustio Opomenu,", TextEnglish = "Allāh has prepared for them a severe punishment; so fear Allāh, O you of understanding who have believed. Allāh has sent down to you a message [i.e., the Qur’ān].[1704]", TextGerman = "Allah hat für sie strenge Strafe bereitet. So fürchtet Allah, o die ihr Verstand besitzt und die ihr glaubt. Allah hat ja zu euch eine Ermahnung (als Offenbarung) herabgesandt," });
                        surah65.VerseList.Add(new Verse { Number = 11, TextBosnian = "i poslao Poslanika, koji vam Allahove ajete jasne kazuje, da bi iz tmina na svjetlo izveo one koji vjeruju i dobra djela čine. A onoga koji bude u Allaha vjerovao i dobro činio, uvest će u džennetske bašče kroz koje rijeke teku. U njima će vječno, zanavijek ostati; najljepšu opskrbu Allah mu je pripremio.", TextEnglish = "[He sent] a Messenger [i.e., Muḥammad (ﷺ)] reciting to you the distinct verses of Allāh that He may bring out those who believe and do righteous deeds from darknesses into the light. And whoever believes in Allāh and does righteousness - He will admit him into gardens beneath which rivers flow to abide therein forever. Allāh will have perfected for him a provision.", TextGerman = "einen Gesandten, der euch Allahs Zeichen verliest, die alles klar machen, um diejenigen, die glauben und rechtschaffene Werke tun, aus den Finsternissen ins Licht hinauszubringen. Und wer an Allah glaubt und rechtschaffen handelt, den wird Er in Gärten eingehen lassen, durcheilt von Bächen, ewig und auf immer darin zu bleiben. Allah hat ihm (damit) ja eine schöne Versorgung gewährt." });
                        surah65.VerseList.Add(new Verse { Number = 12, TextBosnian = "Allah je sedam nebesa i isto toliko slojeva zemlje stvorio; Njegova odredba do svih njih dospijeva da biste vi znali da Allah nad svime ima moć i da Allah znanjem Svojim sve obuhvaća!", TextEnglish = "It is Allāh who has created seven heavens and of the earth, the like of them.[1705] [His] command descends among them so you may know that Allāh is over all things competent and that Allāh has encompassed all things in knowledge.", TextGerman = "Allah ist es, Der sieben Himmel erschaffen hat, und von der Erde gleich (viel). Der Befehl (Allahs) kommt wahrhaftig zwischen ihnen herab, damit ihr wißt, daß Allah zu allem die Macht hat und daß Allah ja alles mit Seinem Wissen umfaßt." });
                        result = surah65;
                        break;
                    #endregion

                    // 
                    #region
                    case 66:
                        Surah surah66 = new Surah(); surah66.Name = Constants.Surah66Name; surah66.Number = 66; surah66.VerseList = new List<Verse>();
                        surah66.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah66.VerseList.Add(new Verse { Number = 1, TextBosnian = "O Vjerovjesniče, zašto sebi zabranjuješ ono što ti je Allah dozvolio-želeći zadovoljstvo svojih supruga? A Allah je Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "O Prophet, why do you prohibit [yourself from] what Allāh has made lawful for you, seeking the approval of your wives? And Allāh is Forgiving and Merciful.", TextGerman = "O Prophet, warum verbietest du, was Allah dir erlaubt hat, indem du da nach trachtest, die Zufriedenheit deiner Gattinnen zu erlangen? Und Allah ist Allvergebend und Barmherzig." });
                        surah66.VerseList.Add(new Verse { Number = 2, TextBosnian = "Allah vam je propisao kako da svoje zakletve iskupite; Allah je vaš Gospodar; On je Sveznajući i Mudri.", TextEnglish = "Allāh has already ordained for you [Muslims] the dissolution of your oaths.[1707] And Allāh is your protector, and He is the Knowing, the Wise.", TextGerman = "Allah hat ja für euch festgelegt, womit eure Eide zu lösen sind. Und Allah ist euer Schutzherr, und Er ist der Allwissende und Allweise." });
                        surah66.VerseList.Add(new Verse { Number = 3, TextBosnian = "A kad Poslanik jednoj supruzi svojoj tajno govor povjeri, pa ga ona razglasi. a Allah mu to obznani, on je upozna s nečim što je kazala, a nešto prešuti. Pa kad je on o tome obavijesti, ona upita: 'Ko tebe o tome izvijesti On odgovori: 'lzvijestio me je Sveznajući, Onaj Koji u potpunosti sve zna.'", TextEnglish = "And [remember] when the Prophet confided to one of his wives a statement; and when she informed [another] of it and Allāh showed it to him, he made known part of it and ignored a part. And when he informed her about it, she said, 'Who told you this?' He said, 'I was informed by the Knowing, the Aware.'", TextGerman = "Als der Prophet einer seiner Gattinnen eine Mitteilung im geheimen anvertraute. Als sie sie dann kundtat und Allah es ihm offen darlegte, gab er (ihr) einen Teil davon bekannt und überging einen (anderen) Teil. Als er es ihr nun kundtat, sagte sie: Wer hat dir das mitgeteilt? Er sagte: Kundgetan hat (es) mir der Allwissende und Allkundige." });
                        surah66.VerseList.Add(new Verse { Number = 4, TextBosnian = "Ako vas dvije učinite pokajanje Allahu, pa, vi ste bile učinile ono zbog čega je trebalo da se pokajete. A ako se protiv njega udružite, pa, Allah je zaštitnik njegov, i Džibril, i čestiti vjernici; najposlije, i svi meleki će mu podrška biti.", TextEnglish = "If you two [wives] repent to Allāh, [it is best], for your hearts have deviated. But if you cooperate against him - then indeed Allāh is his protector, and Gabriel and the righteous of the believers and the angels, moreover, are [his] assistants.", TextGerman = "Wenn ihr beide euch in Reue zu Allah umkehrt - so werden eure Herzen sich ja (der Aufrichtigkeit) zuneigen - ... Und wenn ihr einander gegen ihn beisteht, so ist Allah sein Schutzherr; und Gibril, die Rechtschaffenen von den Gläubigen, und die Engel werden darüber hinaus (ihm) Beistand sein." });
                        surah66.VerseList.Add(new Verse { Number = 5, TextBosnian = "Ako vas on pusti, Gospodar njegov će mu dati, umjesto vas, bolje supruge; muslimanke, muminke, pokorne Allahu, pokajnice, pobožne, koje poste, i udavane i djevice.", TextEnglish = "Perhaps his Lord, if he divorced you [all], would substitute for him wives better than you - submitting [to Allāh], believing, devoutly obedient, repentant, worshipping, and traveling[1708] - [ones] previously married and virgins.", TextGerman = "Vielleicht wird sein Herr ihm, wenn er sich von euch (allen) scheidet, zum Tausch (andere) Gattinnen geben, (die) besser (sind) als ihr: muslimische, gläubige, demütig ergebene, in Reue umkehrende, (Allah) dienende und umherziehende, früher verheiratete oder Jungfrauen." });
                        surah66.VerseList.Add(new Verse { Number = 6, TextBosnian = "O vi koji vjerujete, sebe i porodice svoje čuvajte od Vatre kojoj su ljudi i kamenje gorivo, o kojoj se meleki strogi i snažni brinu, koji se onome što im Allah zapovjedi ne opiru, i koji ono što im se naredi izvršavaju.", TextEnglish = "O you who have believed, protect yourselves and your families from a Fire whose fuel is people and stones, over which are [appointed] angels, harsh and severe; they do not disobey Allāh in what He commands them but do what they are commanded.", TextGerman = "O die ihr glaubt, bewahrt euch selbst und eure Angehörigen vor einem Feuer, dessen Brennstoff Menschen und Steine sind, über das hartherzige, strenge Engel (gesetzt) sind, die sich Allah nicht widersetzen in dem, was Er ihnen befiehlt, sondern tun, was ihnen befohlen wird." });
                        surah66.VerseList.Add(new Verse { Number = 7, TextBosnian = "O vi koji niste vjerovali, danas se ne pravdajte, bit ćete kažnjeni samo za ono što ste radili.'", TextEnglish = "O you who have disbelieved, make no excuses that Day. You will only be recompensed for what you used to do.", TextGerman = "O die ihr ungläubig seid, entschuldigt euch heute nicht. Euch wird nur das vergolten, was ihr zu tun pflegtet." });
                        surah66.VerseList.Add(new Verse { Number = 8, TextBosnian = "O vi koji vjerujete, pokajte se čineći Allahu tevbu iskrenu, ne bi li Gospodar vaš loše postupke vaše izbrisao i da bi vas u džennetske bašče, kroz koje rijeke teku, uveo, na Dan u kojem Allah neće poniziti Vjerovjesnika i one koji su zajedno s njim vjerovali; svjetlo njihovo ići će ispred njih, s njihove desne strane. 'Gospodaru naš', govorit će oni, 'učini potpunim svjetlo naše i oprosti nam. Ti, doista, nad svime imaš moć.'", TextEnglish = "O you who have believed, repent to Allāh with sincere repentance. Perhaps[1709] your Lord will remove from you your misdeeds and admit you into gardens beneath which rivers flow [on] the Day when Allāh will not disgrace the Prophet and those who believed with him. Their light will proceed before them and on their right; they will say, 'Our Lord, perfect for us our light and forgive us. Indeed, You are over all things competent.'", TextGerman = "O die ihr glaubt, kehrt zu Allah um in aufrichtiger Reue; vielleicht wird euer Herr euch eure bösen Taten tilgen und euch in Gärten eingehen lassen, durcheilt von Bächen, am Tag, da Allah den Propheten und diejenigen, die mit ihm geglaubt haben, nicht in Schande stürzen wird. Ihr Licht eilt ihnen voraus und (ebenso) zu ihrer Rechten. Sie werden sagen: Unser Herr, vollende für uns unser Licht und vergib uns. Gewiß, Du hast zu allem die Macht." });
                        surah66.VerseList.Add(new Verse { Number = 9, TextBosnian = "O Vjerovjesniče, bori se protiv nevjernika i licemjera i budi strog prema njima! Njihovo prebivalište bit će Džehennem, a užasno je to krajnje odredište.", TextEnglish = "O Prophet, strive against the disbelievers and the hypocrites and be harsh upon them. And their refuge is Hell, and wretched is the destination.", TextGerman = "O Prophet, mühe dich gegen die Ungläubigen und die Heuchler ab und sei hart gegen sie. Ihr Zufluchtsort wird die Hölle sein - ein schlimmer Ausgang!" });
                        surah66.VerseList.Add(new Verse { Number = 10, TextBosnian = "Allah navodi kao primjer onih koji ne vjeruju ženu Nuhovu i ženu Lutovu: bile su udate za dva čestita roba Naša, pa su ih iznevjerile, i njih dvojica im neće ništa moći kod Allaha pomoći, i bit će rečeno: 'Ulazite vas dvije u Vatru, s onima koji u nju ulaze!'", TextEnglish = "Allāh presents an example of those who disbelieved: the wife of Noah and the wife of Lot. They were under two of Our righteous servants but betrayed them,[1710] so they [i.e., those prophets] did not avail them from Allāh at all, and it was said, 'Enter the Fire with those who enter.'", TextGerman = "Allah hat als Gleichnis für diejenigen, die ungläubig sind, dasjenige von Nuhs Frau und Luts Frau geprägt. Sie beide unterstanden zwei rechtschaffenen (Dienern) von Unseren Dienern, doch handelten sie verräterisch an ihnen, und so haben ihnen diese vor Allah nichts genützt. Und es wurde gesagt: Geht beide ins (Höllen)feuer ein mit denjenigen, die hineingehen." });
                        surah66.VerseList.Add(new Verse { Number = 11, TextBosnian = "A Allah navodi kao primjer onih koji vjeruju ženu faraonovu, kad je rekla: 'Gospodaru moj, sagradi mi kod Sebe kuću u Džennetu i spasi me od faraona i njegovih postupaka, i izbavi me od ljudi zulumćara!'", TextEnglish = "And Allāh presents an example of those who believed: the wife of Pharaoh, when she said, 'My Lord, build for me near You a house in Paradise and save me from Pharaoh and his deeds and save me from the wrongdoing people.'", TextGerman = "Und Allah hat als Gleichnis für diejenigen, die glauben, dasjenige von Fir’auns Frau geprägt. Als sie sagte: Mein Herr, baue mir bei Dir ein Haus im (Paradies)garten, und errette mich von Fir’aun und seinem Werk, und errette mich von dem Volk der Ungerechten." });
                        surah66.VerseList.Add(new Verse { Number = 12, TextBosnian = "I Merjemu, kćer Imranovu, koja je nevinost svoju sačuvala, pa smo u nju duha Svog udahnuli, i ona je u riječi Gospodara svoga i knjige Njegove povjerovala i od onih koji su pokorni bila.", TextEnglish = "And [the example of] Mary, the daughter of ʿImrān, who guarded her chastity, so We blew into [her garment] through Our angel [i.e., Gabriel], and she believed in the words of her Lord and His scriptures and was of the devoutly obedient.", TextGerman = "Und (auch von) Maryam, ’Imrans Tochter, die ihre Scham unter Schutz stellte, worauf Wir in sie von Unserem Geist einhauchten. Und sie hielt die Worte ihres Herrn und Seine Bücher für wahr und gehörte zu den (Allah) demütig Ergebenen." });
                        result = surah66;
                        break;
                    #endregion

                    // 
                    #region
                    case 67:
                        Surah surah67 = new Surah(); surah67.Name = Constants.Surah67Name; surah67.Number = 67; surah67.VerseList = new List<Verse>();
                        surah67.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah67.VerseList.Add(new Verse { Number = 1, TextBosnian = "Uzvišen i blagoslovljen je Onaj u Čijoj je ruci vlast i On nad svime ima moć,", TextEnglish = "Blessed is He in whose hand is dominion, and He is over all things competent -", TextGerman = "Segensreich ist Derjenige, in Dessen Hand die Herrschaft ist, und Er hat zu allem die Macht." });
                        surah67.VerseList.Add(new Verse { Number = 2, TextBosnian = "Onaj Koji je stvorio smrt i život da bi iskušao koji će od vas bolja djela imati; On je Silni, Oprostitelj grijeha,", TextEnglish = "[He] who created death and life to test you [as to] which of you is best in deed - and He is the Exalted in Might, the Forgiving -", TextGerman = "(Er,) Der den Tod und das Leben erschaffen hat, damit Er euch prüfe, wer von euch die besten Taten begeht. Und Er ist der Allmächtige und Allvergebende." });
                        surah67.VerseList.Add(new Verse { Number = 3, TextBosnian = "Onaj koji je sedam nebesa kao slojeve, jedne iznad drugih, stvorio - ti u onome što Svemilosni stvara ne vidiš nikakva nesklada, pa ponovo pogledaj, vidiš li ikakvu pukotinu!?", TextEnglish = "[And] who created seven heavens in layers.[1712] You do not see in the creation of the Most Merciful any inconsistency. So return [your] vision [to the sky]; do you see any breaks?", TextGerman = "(Er,) Der sieben Himmel in Schichten (übereinander) erschaffen hat. Du kannst in der Schöpfung des Allerbarmers keine Ungesetzmäßigkeit sehen. Wende den Blick zurück: Siehst du irgendwelche Risse?" });
                        surah67.VerseList.Add(new Verse { Number = 4, TextBosnian = "Zatim, ponovo više puta pogledaj, pogled će ti se vratiti klonuo i umoran.", TextEnglish = "Then return [your] vision twice again.[1713] [Your] vision will return to you humbled while it is fatigued.", TextGerman = "Hierauf wende den Blick wiederholt zurück. Der Blick wird zu dir erfolglos und ermüdet zurückkehren." });
                        surah67.VerseList.Add(new Verse { Number = 5, TextBosnian = "Mi smo najbliže nebo sjajnim svjetiljkama ukrasili i učinili smo ih za gađanje šejtana, i patnju u Ognju im pripremili.", TextEnglish = "And We have certainly beautified the nearest heaven with lamps [i.e., stars] and have made [from] them what is thrown at the devils[1714] and have prepared for them the punishment of the Blaze.", TextGerman = "Wir haben ja den untersten Himmel mit Lampen geschmückt und haben sie zu Wurfgeschossen gegen die Satane gemacht. Und bereitet haben Wir für sie die Strafe der Feuerglut." });
                        surah67.VerseList.Add(new Verse { Number = 6, TextBosnian = "A onima koji ne vjeruju u Gospodara svoga, pripada patnja u Džehennemu. Užasno je to boravište!", TextEnglish = "And for those who disbelieved in their Lord is the punishment of Hell, and wretched is the destination.", TextGerman = "Und für diejenigen, die ihren Herrn verleugnen, wird es die Strafe der Hölle geben - ein schlimmer Ausgang!" });
                        surah67.VerseList.Add(new Verse { Number = 7, TextBosnian = "Kad budu u njega bačeni, hučanje njegovo čut će, i on će ključati,", TextEnglish = "When they are thrown into it, they hear from it a [dreadful] inhaling while it boils up.", TextGerman = "Wenn sie hineingeworfen werden, hören sie von ihr stoßweises Aufheulen, während sie brodelt." });
                        surah67.VerseList.Add(new Verse { Number = 8, TextBosnian = "gotovo da se od bijesa raspadne. Kad god se koja gomila u njega baci, stražari u njemu će je upitati: 'Zar nije niko dolazio da vas upozorava?'", TextEnglish = "It almost bursts with rage. Every time a company is thrown into it, its keepers ask them, 'Did there not come to you a warner?'", TextGerman = "Beinahe würde sie vor Grimm bersten. Jedesmal, wenn eine Schar hineingeworfen wird, werden ihre Wärter sie fragen: Ist zu euch denn kein Warner gekommen?" });
                        surah67.VerseList.Add(new Verse { Number = 9, TextBosnian = "Jest, dolazio nam je onaj koji nas je upozoravao', odgovorit će, 'a mi smo poricali i govorili: 'Allah nije objavio ništa, vi ste u velikoj zabludi.''", TextEnglish = "They will say, 'Yes, a warner had come to us, but we denied and said, 'Allāh has not sent down anything. You are not but in great error.''", TextGerman = "Sie werden sagen: Ja doch, zu uns kam bereits ein Warner; aber wir haben (ihn) der Lüge bezichtigt und gesagt: , Allah hat nichts offenbart; ihr befindet euch nur in großem Irrtum’." });
                        surah67.VerseList.Add(new Verse { Number = 10, TextBosnian = "I reći će: 'Da smo slušali ili razmišljali, ne bismo među stanovnicima Ognja bili!'", TextEnglish = "And they will say, 'If only we had been listening or reasoning, we would not be among the companions of the Blaze.'", TextGerman = "Und sie werden sagen: Hätten wir nur gehört und begriffen, wären wir (nun) nicht unter den Insassen der Feuerglut." });
                        surah67.VerseList.Add(new Verse { Number = 11, TextBosnian = "I oni će priznati svoje grijehe, pa daleko bili stanovnici Ognja!", TextEnglish = "And they will admit their sin, so [it is] alienation[1715] for the companions of the Blaze.", TextGerman = "So werden sie ihre Sünde bekennen. Weit weg darum mit den Insassen der Feuerglut!" });
                        surah67.VerseList.Add(new Verse { Number = 12, TextBosnian = "Onima koji se Gospodara svoga boje i kad ih niko ne vidi - pripada oprost i nagrada velika!", TextEnglish = "Indeed, those who fear their Lord unseen will have forgiveness and great reward.", TextGerman = "Gewiß, diejenigen, die ihren Herrn im Verborgenen fürchten, für sie wird es Vergebung und großen Lohn geben." });
                        surah67.VerseList.Add(new Verse { Number = 13, TextBosnian = "Govorili vi tiho ili glasno - pa, On sigurno zna šta grudi kriju!", TextEnglish = "And conceal your speech or publicize it; indeed, He is Knowing of that within the breasts.", TextGerman = "Haltet eure Worte geheim oder äußert sie laut vernehmbar, gewiß, Er weiß über das Innerste der Brüste Bescheid." });
                        surah67.VerseList.Add(new Verse { Number = 14, TextBosnian = "A kako i ne bi znao Onaj Koji stvara, Koji zna i ono što je najsitnije, Onaj Koji u potpunosti sve zna!", TextEnglish = "Does He who created not know,[1716] while He is the Subtle, the Aware?", TextGerman = "Sollte denn Derjenige, Der erschaffen hat, nicht Bescheid wissen? Und Er ist der Feinfühlige und Allkundige." });
                        surah67.VerseList.Add(new Verse { Number = 15, TextBosnian = "On vam je Zemlju pogodnom učinio, pa hodajte predjelima njezinim i hranite se onim što On daje, ali Njemu se sve vraća.", TextEnglish = "It is He who made the earth tame[1717] for you - so walk among its slopes and eat of His provision - and to Him is the resurrection.", TextGerman = "Er ist es, Der euch die Erde fügsam gemacht hat. So geht auf ihrem Rücken einher und eßt von dem, womit Er (euch) versorgt. Und zu Ihm wird die Auferstehung sein." });
                        surah67.VerseList.Add(new Verse { Number = 16, TextBosnian = "Jeste li sigurni! da vas Onaj Koji je na nebesima neće u zemlju utjerati, kad se ona počne iznenada tresti?!", TextEnglish = "Do you feel secure that He who is above[1718] would not cause the earth to swallow you and suddenly it would sway?[1719]", TextGerman = "Glaubt ihr in Sicherheit davor zu sein, daß Wer im Himmel ist, die Erde mit euch versinken läßt, so daß sie sich dann hin und her bewegt?" });
                        surah67.VerseList.Add(new Verse { Number = 17, TextBosnian = "Ili ste sigurni da Onaj Koji je na nebesima na vas neće vjetar s kamenjem poslati?! Pa, znat ćete kakvo je upozorenje Moje!", TextEnglish = "Or do you feel secure that He who is above would not send against you a storm of stones? Then you would know how [severe] was My warning.", TextGerman = "Oder glaubt ihr euch in Sicherheit davor, daß Wer im Himmel ist, gegen euch einen Sturm von Steinchen sendet? Dann werdet ihr (noch) erfahren, wie Meine Warnung ist." });
                        surah67.VerseList.Add(new Verse { Number = 18, TextBosnian = "A poricali su i oni prije njih, pa kakva je bila osuda Moja!", TextEnglish = "And already had those before them denied, and how [terrible] was My reproach.", TextGerman = "Auch diejenigen, die vor ihnen waren, haben bereits (die Gesandten) der Lüge bezichtigt. Wie war da Meine Mißbilligung!" });
                        surah67.VerseList.Add(new Verse { Number = 19, TextBosnian = "Zar oni ne vide ptice iznad sebe kako raširenih krila lete, a i skupljaju ih? Samo ih Svemilosni drži; On, zaista, sve dobro vidi.", TextEnglish = "Do they not see the birds above them with wings outspread and [sometimes] folded in? None holds them [aloft] except the Most Merciful. Indeed He is, of all things, Seeing.", TextGerman = "Sehen sie denn nicht auf die Vögel über ihnen, wie sie ihre Flügel ausgebreitet halten und einziehen? Nur der Allerbarmer hält sie. Gewiß, Er sieht alles wohl." });
                        surah67.VerseList.Add(new Verse { Number = 20, TextBosnian = "I, ko je taj koji će biti vojska vaša i vama pomoći, osim Svemilosnog?! Ništa osim obmane nevjernici nemaju!", TextEnglish = "Or who is it that could be an army for you to aid you other than the Most Merciful? The disbelievers are not but in delusion.", TextGerman = "Oder wer ist denn dieser, der euch eine Heerschar wäre, die euch helfen (sollte) außer dem Allerbarmer? Die Ungläubigen befinden sich nur im Trug." });
                        surah67.VerseList.Add(new Verse { Number = 21, TextBosnian = "A, i ko je taj koji će vas nahraniti, ako On hranu Svoju uskrati?! Ali, oni su ustrajni u osionosti i odbojnosti.", TextEnglish = "Or who is it that could provide for you if He withheld His provision? But they have persisted in insolence and aversion.", TextGerman = "Oder wer ist denn dieser, der euch versorgen (sollte), wenn Er Seine Versorgung zurückhält? Aber nein! Sie fahren weiter in Auflehnung und Abneigung." });
                        surah67.VerseList.Add(new Verse { Number = 22, TextBosnian = "Da li je bolje upućen onaj koji ide pognut s licem prema zemlji ili onaj koji pravim putem uspravno hodi?", TextEnglish = "Then is one who walks fallen on his face better guided or one who walks erect on a straight path?", TextGerman = "Ist denn jemand, der, auf sein Gesicht gestürzt, einhergeht, eher rechtgeleitet, oder jemand, der aufrecht auf einem geraden Weg einhergeht?" });
                        surah67.VerseList.Add(new Verse { Number = 23, TextBosnian = "Reci: 'On vas je prvi put stvorio i dao vam sluh, vid i razum, pa kako samo malo zahvaljujete!'", TextEnglish = "Say, 'It is He who has produced you and made for you hearing and vision and hearts [i.e., intellect]; little are you grateful.'", TextGerman = "Sag: Er ist es, Der euch hat entstehen lassen und euch Gehör, Augenlicht und Herzen gemacht hat. Wie wenig ihr dankbar seid!" });
                        surah67.VerseList.Add(new Verse { Number = 24, TextBosnian = "Reci: 'On vas je po Zemlji razasuo i pred Njim ćete biti sakupljeni.'", TextEnglish = "Say, 'It is He who has multiplied you throughout the earth, and to Him you will be gathered.'", TextGerman = "Sag: Er ist es, Der euch auf der Erde (an Zahl) hat anwachsen lassen, und zu Ihm werdet ihr versammelt werden." });
                        surah67.VerseList.Add(new Verse { Number = 25, TextBosnian = "A oni govore: 'Kad će već jednom ta prijetnja, ako istinu govorite?!'", TextEnglish = "And they say, 'When is this promise, if you should be truthful?'", TextGerman = "Und sie sagen: „Wann wird dieses Versprechen eintreten, wenn ihr wahrhaftig seid?" });
                        surah67.VerseList.Add(new Verse { Number = 26, TextBosnian = "Reci: 'Samo Allah zna! A ja sam samo upozoritelj jasni.'", TextEnglish = "Say, 'The knowledge is only with Allāh, and I am only a clear warner.'", TextGerman = "Sag: Nur Allah weiß (darüber) Bescheid. Und ich bin nur ein deutlicher Warner." });
                        surah67.VerseList.Add(new Verse { Number = 27, TextBosnian = "Kad je izbliza ugledaju ojađena bit će lica onih koji nisu vjerovali i bit će rečeno: 'Evo, ovo je ono što ste požurivali!'", TextEnglish = "But when they see it[1720] approaching, the faces of those who disbelieve will be distressed, and it will be said, 'This is that for which you used to call.'[1721]", TextGerman = "Wenn sie es aber nahe (bei sich) sehen, dann werden die Gesichter derjenigen, die ungläubig sind, böse betroffen sein. Und es wird gesagt werden: Das ist das, was ihr stets herbeizurufen wünschtet." });
                        surah67.VerseList.Add(new Verse { Number = 28, TextBosnian = "Reci: 'Kažite vi meni: ako Allah pošalje smrt meni i onima koji su sa mnom, ili ako nam se smiluje, ko će nevjernike od bolne patnje zaštititi.'", TextEnglish = "Say, [O Muḥammad], 'Have you considered:[1722] whether Allāh should cause my death and those with me or have mercy upon us, who can protect the disbelievers from a painful punishment?'", TextGerman = "Sag: Was meint ihr, wenn Allah mich und wer mit mir ist, vernichten oder wenn Er Sich unser erbarmen sollte, wer wird dann den Ungläubigen Schutz vor schmerzhafter Strafe gewähren?" });
                        surah67.VerseList.Add(new Verse { Number = 29, TextBosnian = "Reci: 'On je Svemilosni, u Njega vjerujemo i na Njega se oslanjamo, a vi ćete sigurno saznati ko je u očitoj zabludi.'", TextEnglish = "Say, 'He is the Most Merciful; we have believed in Him, and upon Him we have relied. And you will [come to] know who it is that is in clear error.'", TextGerman = "Sag: Er ist der Allerbarmer. Wir glauben an Ihn, und auf Ihn verlassen wir uns. Ihr werdet (noch) erfahren, wer sich in deutlichem Irrtum befindet." });
                        surah67.VerseList.Add(new Verse { Number = 30, TextBosnian = "Reci: 'Šta mislite, ako vode vaše ponornice postanu, ko će vam vodu na površinu izvesti?'", TextEnglish = "Say, 'Have you considered: if your water was to become sunken [into the earth], then who could bring you flowing water?'", TextGerman = "Sag: Was meint ihr, wenn euer Wasser versickert sein sollte, wer sollte euch dann hervorquellendes Wasser bringen?" });
                        result = surah67;
                        break;
                    #endregion

                    // 
                    #region
                    case 68:
                        Surah surah68 = new Surah(); surah68.Name = Constants.Surah68Name; surah68.Number = 68; surah68.VerseList = new List<Verse>();
                        surah68.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah68.VerseList.Add(new Verse { Number = 1, TextBosnian = "Nun. Tako Mi pera i onoga što oni pišu,", TextEnglish = "Nūn.[1724] By the pen and what they inscribe,", TextGerman = "Nun. Beim Schreibrohr und dem, was sie in Zeilen niederschreiben." });
                        surah68.VerseList.Add(new Verse { Number = 2, TextBosnian = "ti, uz blagodati Gospodara svoga, nisi lud,", TextEnglish = "You are not, [O Muḥammad], by the favor of your Lord, a madman.", TextGerman = "Du bist durch die Gunst deines Herrn kein Besessener." });
                        surah68.VerseList.Add(new Verse { Number = 3, TextBosnian = "ti ćeš, sigurno, nagradu nepresušnu imati.", TextEnglish = "And indeed, for you is a reward uninterrupted.", TextGerman = "Es wird für dich wahrlich Lohn geben, der nicht aufhört." });
                        surah68.VerseList.Add(new Verse { Number = 4, TextBosnian = "I, zaista, tvoj ahlak je veličanstven", TextEnglish = "And indeed, you are of a great moral character.", TextGerman = "Und du bist wahrlich von großartiger Wesensart." });
                        surah68.VerseList.Add(new Verse { Number = 5, TextBosnian = "i ti ćeš vidjeti, a i oni će vidjeti,", TextEnglish = "So you will see and they will see.", TextGerman = "Du wirst sehen, und (auch) sie werden sehen," });
                        surah68.VerseList.Add(new Verse { Number = 6, TextBosnian = "ko je od vas zaveden!", TextEnglish = "Which of you is the afflicted [by a devil].", TextGerman = "wer von euch (durch Besessenheit) der Versuchung ausgesetzt ist." });
                        surah68.VerseList.Add(new Verse { Number = 7, TextBosnian = "Gospodar tvoj najbolje zna ko je s puta Njegova skrenuo i On najbolje zna one koji su na Pravom putu.", TextEnglish = "Indeed, your Lord is most knowing of who has gone astray from His way, and He is most knowing of the [rightly] guided.", TextGerman = "Gewiß, dein Herr weiß sehr wohl, wer von Seinem Weg abirrt, und Er kennt sehr wohl die Rechtgeleiteten." });
                        surah68.VerseList.Add(new Verse { Number = 8, TextBosnian = "Pa, ne pokoravaj se poricateljima,", TextEnglish = "Then do not obey the deniers.", TextGerman = "So gehorche nicht den Leugnern (der Botschaft)." });
                        surah68.VerseList.Add(new Verse { Number = 9, TextBosnian = "oni bi jedva dočekali da ti popustiš, pa bi i oni popustili,", TextEnglish = "They wish that you would soften [in your position], so they would soften [toward you].", TextGerman = "Sie möchten gern, daß du schmeichelst, so daß (auch) sie schmeicheln (können)." });
                        surah68.VerseList.Add(new Verse { Number = 10, TextBosnian = "i ne slušaj nijednog krivokletnika, prezrena,", TextEnglish = "And do not obey every worthless habitual swearer", TextGerman = "Und gehorche keinem verächtlichen Schwörer," });
                        surah68.VerseList.Add(new Verse { Number = 11, TextBosnian = "onoga koji tuđe riječi radi smutnje prenosi,", TextEnglish = "[And] scorner, going about with malicious gossip -", TextGerman = "Stichler und Verbreiter von Verleumdungen," });
                        surah68.VerseList.Add(new Verse { Number = 12, TextBosnian = "koji sprečava dobro, onoga koji skrnavi propisanu granicu, grešnika", TextEnglish = "A preventer of good, transgressing and sinful,", TextGerman = "Verweigerer des Guten, der Übertretungen begeht und ein Sünder ist," });
                        surah68.VerseList.Add(new Verse { Number = 13, TextBosnian = "surova i, osim toga, po zlu poznata,", TextEnglish = "Cruel, moreover, and an illegitimate pretender.[1725]", TextGerman = "einem von grobem Benehmen und darüber hinaus einem Eindringling" });
                        surah68.VerseList.Add(new Verse { Number = 14, TextBosnian = "samo zato što ima mnogo imetka i sinova,", TextEnglish = "Because he is a possessor of wealth and children,", TextGerman = "(nur deshalb), weil er Besitz und Söhne hat." });
                        surah68.VerseList.Add(new Verse { Number = 15, TextBosnian = "koji govori, kad mu se ajeti Naši kazuju: 'To su samo naroda drevnih priče!'", TextEnglish = "When Our verses are recited to him, he says, 'Legends of the former peoples.'", TextGerman = "Wenn ihm Unsere Zeichen verlesen werden, sagt er: (Es sind) Fabeln der Früheren." });
                        surah68.VerseList.Add(new Verse { Number = 16, TextBosnian = "Na nosinu ćemo Mi njemu biljeg utisnuti!", TextEnglish = "We will brand him upon the snout.[1726]", TextGerman = "Wir werden ihn mit einem Merkmal auf der Nase zeichnen." });
                        surah68.VerseList.Add(new Verse { Number = 17, TextBosnian = "Mi smo ih na kušnju stavili, kao što smo vlasnike jedne bašče na kušnju stavili kad su se zakleli da će je sigurno rano izjutra obrati,", TextEnglish = "Indeed, We have tried them as We tried the companions of the garden, when they swore to cut its fruit in the [early] morning", TextGerman = "Wir haben sie geprüft, wie Wir die Besitzer des Gartens prüften, als sie schworen, sie würden ihn bei Tagesanbruch abpflücken," });
                        surah68.VerseList.Add(new Verse { Number = 18, TextBosnian = "a nisu rekli: 'Ako Allah da!'", TextEnglish = "Without making exception.[1727]", TextGerman = "und nicht(s) ausnahmen." });
                        surah68.VerseList.Add(new Verse { Number = 19, TextBosnian = "I dok su oni spavali, nju od Gospodara tvoga zadesi nesreća", TextEnglish = "So there came upon it [i.e., the garden] an affliction from your Lord while they were asleep.", TextGerman = "Da ging darin Verheerendes von deinem Herrn umher, während sie schliefen," });
                        surah68.VerseList.Add(new Verse { Number = 20, TextBosnian = "i ona osvanu opustošena.", TextEnglish = "And it became as though reaped.", TextGerman = "und er war dann am Morgen wie abgepflückt." });
                        surah68.VerseList.Add(new Verse { Number = 21, TextBosnian = "A u zoru oni su jedni druge dozivali:", TextEnglish = "And they called one another at morning,", TextGerman = "Da riefen sie bei Tagesanbruch einander zu:" });
                        surah68.VerseList.Add(new Verse { Number = 22, TextBosnian = "Poranite do zasada svoga ako ga mislite obrati!'", TextEnglish = "[Saying], 'Go early to your crop if you would cut the fruit.'", TextGerman = "Geht in der Morgenfrühe zu eurer Pflanzung, wenn ihr pflücken wollt." });
                        surah68.VerseList.Add(new Verse { Number = 23, TextBosnian = "I oni krenuše tiho razgovarajući:", TextEnglish = "So they set out, while lowering their voices,", TextGerman = "Da zogen sie los und flüsterten dabei einander zu:" });
                        surah68.VerseList.Add(new Verse { Number = 24, TextBosnian = "Neka vam danas u nju nikako nijedan siromah ne ulazi!'", TextEnglish = "[Saying], 'There will surely not enter it today upon you [any] poor person.'", TextGerman = "In ihn soll heute ja kein Armer zu euch hineingelangen." });
                        surah68.VerseList.Add(new Verse { Number = 25, TextBosnian = "I oni poraniše odlučni da će je moći obrati,", TextEnglish = "And they went early in determination, [assuming themselves] able.[1728]", TextGerman = "Und sie gingen hin in der Morgenfrühe, zum Verwehren bereit." });
                        surah68.VerseList.Add(new Verse { Number = 26, TextBosnian = "a kad je ugledaše, povikaše: 'Mi smo zalutali,", TextEnglish = "But when they saw it, they said, 'Indeed, we are lost;", TextGerman = "Als sie ihn aber sahen, sagten sie: Wir haben uns fürwahr geirrt." });
                        surah68.VerseList.Add(new Verse { Number = 27, TextBosnian = "štaviše - svega smo lišeni!'", TextEnglish = "Rather, we have been deprived.'", TextGerman = "Nein! Vielmehr entbehren wir alles." });
                        surah68.VerseList.Add(new Verse { Number = 28, TextBosnian = "Ponajbolji između njih reče: 'Nisam li vam ja govorio zašto Allaha ne slavite!'", TextEnglish = "The most moderate of them said, 'Did I not say to you, 'Why do you not exalt [Allāh]?''[1729]", TextGerman = "Der Mittlere von ihnen sagte: Habe ich nicht zu euch gesagt: Würdet ihr doch Allah preisen!" });
                        surah68.VerseList.Add(new Verse { Number = 29, TextBosnian = "Slavljen i Uzvišen je Gospodar naš!', rekoše, 'mi smo, uistinu, zulumćari bili!'", TextEnglish = "They said, 'Exalted is our Lord! Indeed, we were wrongdoers.'", TextGerman = "Sie sagten: Preis sei unserem Herrn! Wir waren gewiß ungerecht." });
                        surah68.VerseList.Add(new Verse { Number = 30, TextBosnian = "I onda počeše jedni druge koriti.", TextEnglish = "Then they approached one another, blaming each other.", TextGerman = "Da wandten sie sich einander zu, sich gegenseitig tadelnd." });
                        surah68.VerseList.Add(new Verse { Number = 31, TextBosnian = "Teško nama', govorili su, 'mi smo, zaista, obijesni bili.", TextEnglish = "They said, 'O woe to us; indeed we were transgressors.", TextGerman = "Sie sagten: O wehe uns! Wir pflegten ja das Maß (an Frevel) zu überschreiten." });
                        surah68.VerseList.Add(new Verse { Number = 32, TextBosnian = "Gospodar naš nam može bolju od nje dati, samo od Gospodara našeg mi se nadamo naknadi!'", TextEnglish = "Perhaps our Lord will substitute for us [one] better than it. Indeed, we are toward our Lord desirous.'[1730]", TextGerman = "Vielleicht wird unser Herr uns zum Tausch dafür einen besseren (Garten) als ihn geben! Gewiß, nach Allah steht unser Begehren." });
                        surah68.VerseList.Add(new Verse { Number = 33, TextBosnian = "Eto, takva je bila patnja, a na ahiretu je patnja još veća, kad bi samo oni znali!", TextEnglish = "Such is the punishment [of this world]. And the punishment of the Hereafter is greater, if they only knew.", TextGerman = "So ist die Strafe; aber die Strafe des Jenseits ist wahrlich größer, wenn sie nur wüßten!" });
                        surah68.VerseList.Add(new Verse { Number = 34, TextBosnian = "Za one koji se budu bojali Allaha bit će, zaista, dženneti uživanja u Gospodara njihova,", TextEnglish = "Indeed, for the righteous with their Lord are the Gardens of Pleasure.", TextGerman = "Gewiß, für die Gottesfürchtigen wird es bei ihrem Herrn die Gärten der Wonne geben." });
                        surah68.VerseList.Add(new Verse { Number = 35, TextBosnian = "zar ćemo muslimane sa prestupnicima izjednačiti?!", TextEnglish = "Then will We treat the Muslims like the criminals?", TextGerman = "Sollen Wir etwa die Gottergebenen den Übeltätern gleichstellen?" });
                        surah68.VerseList.Add(new Verse { Number = 36, TextBosnian = "Šta vam je, kako rasuđujete?", TextEnglish = "What is [the matter] with you? How do you judge?", TextGerman = "Was ist mit euch? Wie urteilt ihr?" });
                        surah68.VerseList.Add(new Verse { Number = 37, TextBosnian = "Ili, možda imate Knjigu, pa u njoj čitate", TextEnglish = "Or do you have a scripture in which you learn", TextGerman = "Oder habt ihr eine Schrift, in der ihr lernt (und sucht);" });
                        surah68.VerseList.Add(new Verse { Number = 38, TextBosnian = "da ćete imati ono što vi izaberete?!", TextEnglish = "That indeed for you is whatever you choose?", TextGerman = "ihr sollt darin wahrlich das haben, was ihr euch auswählt." });
                        surah68.VerseList.Add(new Verse { Number = 39, TextBosnian = "Ili, možda imate od Nas zakletve koje će do Kijametskog dana vrijediti da ćete ono što vi prosudite imati?!", TextEnglish = "Or do you have oaths [binding] upon Us, extending until the Day of Resurrection, that indeed for you is whatever you judge?", TextGerman = "Oder habt ihr von Uns verbindliche Eide, die bis zum Tag der Auferstehung reichen; ihr bekommt wahrlich das, was ihr urteilt." });
                        surah68.VerseList.Add(new Verse { Number = 40, TextBosnian = "Upitaj ih ko je od njih jamac za to.", TextEnglish = "Ask them which of them, for that [claim], is responsible.", TextGerman = "Frage sie, wer von ihnen dafür Bürge ist." });
                        surah68.VerseList.Add(new Verse { Number = 41, TextBosnian = "Ili, imaju li oni saučesnike?! Pa neka saučesnike svoje dovedu, ako govore istinu!", TextEnglish = "Or do they have partners?[1731] Then let them bring their partners, if they should be truthful.", TextGerman = "Oder haben sie (etwa) Teilhaber? So sollen sie doch ihre Teilhaber beibringen, wenn sie wahrhaftig sind." });
                        surah68.VerseList.Add(new Verse { Number = 42, TextBosnian = "Na Dan kada se potkoljenica otkrije i kada budu pozvani da licem na tlo padnu, pa ne budu mogli.", TextEnglish = "The Day the shin will be uncovered[1732] and they are invited to prostration but they [i.e., the disbelievers] will not be able,", TextGerman = "Am Tag, da (manch) ein Schenkel entblößt wird und sie aufgerufen werden, sich (anbetend) niederzuwerfen, aber da werden sie es nicht können," });
                        surah68.VerseList.Add(new Verse { Number = 43, TextBosnian = "Oborenih pogleda svojih, poklopit će ih poniženje! A bili su pozvani da licem na tlo padaju dok su zdravi bili.", TextEnglish = "Their eyes humbled, humiliation will cover them. And they used to be invited to prostration while they were sound.[1733]", TextGerman = "während ihre Blicke demütig sind und Erniedrigung sie bedeckt. Dabei waren sie doch bereits aufgefordert worden, sich (anbetend) niederzuwerfen, als sie (noch) heil waren." });
                        surah68.VerseList.Add(new Verse { Number = 44, TextBosnian = "Pa, ostavi Mene i one koji ovaj Govor poriču, Mi ćemo ih postepeno, odakle se i ne nadaju, patnji približavati", TextEnglish = "So leave Me, [O Muḥammad], with [the matter of] whoever denies this statement [i.e., the Qur’ān]. We will progressively lead them [to punishment] from where they do not know.[1734]", TextGerman = "Lasse Mich (allein) mit denjenigen, die diese Aussage für Lüge erklären; Wir werden sie stufenweise (dem Verderben) näherbringen, von wo sie nicht wissen." });
                        surah68.VerseList.Add(new Verse { Number = 45, TextBosnian = "i vremena ću im Ja davati, a obmana moja je, zaista, čvrsta!", TextEnglish = "And I will give them time. Indeed, My plan is firm.", TextGerman = "Und Ich gewähre ihnen Aufschub. (Aber) gewiß, Meine List ist fest." });
                        surah68.VerseList.Add(new Verse { Number = 46, TextBosnian = "Ili ti tražiš od njih nagradu, pa su nametom opterećeni?!", TextEnglish = "Or do you ask of them a payment, so they are by debt burdened down?", TextGerman = "Oder verlangst du (etwa) von ihnen einen Lohn, so daß sie mit Schulden belastet wären?" });
                        surah68.VerseList.Add(new Verse { Number = 47, TextBosnian = "Ili je kod njih znanje o onome što je čulima nedokučivo, pa oni prepisuju?!", TextEnglish = "Or have they [knowledge of] the unseen, so they write [it] down?", TextGerman = "Oder besitzen sie (das Wissen über) das Verborgene, so daß sie (es) aufschreiben (können)?" });
                        surah68.VerseList.Add(new Verse { Number = 48, TextBosnian = "Ti strpljivo čekaj presudu Gospodara svoga i ne budi kao onaj što u kitu bi, kad je zavapio, a ogorčen je bio,", TextEnglish = "Then be patient for the decision of your Lord, [O Muḥammad], and be not like the companion of the fish [i.e., Jonah] when he called out while he was distressed.", TextGerman = "So sei standhaft in Bezug auf das Urteil deines Herrn. Und sei nicht wie der Gefährte des (großen) Fisches, als er voller Gram (zu Allah) rief." });
                        surah68.VerseList.Add(new Verse { Number = 49, TextBosnian = "i da ga nije stigla Allahova milost, na pusto mjesto bi izbačen bio i prijekor bi zaslužio,", TextEnglish = "If not that a favor [i.e., mercy] from his Lord overtook him, he would have been thrown onto the naked shore while he was censured.[1735]", TextGerman = "Wenn ihn nicht eine Gunst von seinem Herrn rechtzeitig erreicht hätte, wäre er wahrlich auf das kahle Land geworfen worden und hätte sich dabei Vorwürfe zugezogen." });
                        surah68.VerseList.Add(new Verse { Number = 50, TextBosnian = "ali, Gospodar njegov ga je odabrao i učinio ga jednim od onih koji su dobri.", TextEnglish = "And his Lord chose him and made him of the righteous.", TextGerman = "Da erwählte ihn sein Herr und machte ihn zu einem der Rechtschaffenen." });
                        surah68.VerseList.Add(new Verse { Number = 51, TextBosnian = "Gotovo da te nevjernici pogledima svojim obore kad Opomenu slušaju, govoreći: 'On je, uistinu, lud!'", TextEnglish = "And indeed, those who disbelieve would almost make you slip with their eyes [i.e., looks] when they hear the message, and they say, 'Indeed, he is mad.'", TextGerman = "Diejenigen, die ungläubig sind, würden dich, wenn sie die Ermahnung hören, mit ihren Blicken wahrlich beinahe ins Straucheln bringen. Und sie sagen: Er ist ja fürwahr besessen." });
                        surah68.VerseList.Add(new Verse { Number = 52, TextBosnian = "A on - Kur'an je samo svijetu cijelom opomena!", TextEnglish = "But it is not except a reminder to the worlds.", TextGerman = "Doch es ist nur eine Ermahnung für die Weltenbewohner." });
                        result = surah68;
