                        surah69.VerseList.Add(new Verse { Number = 4, TextBosnian = "Semud i Ad zastrašujući Udar su poricali,", TextEnglish = "Thamūd and ʿAad denied the Striking Calamity [i.e., the Resurrection].", TextGerman = "Die Tamud und die ’Ad erklärten das Verhängnis für Lüge." });
