                        surah69.VerseList.Add(new Verse { Number = 6, TextBosnian = "a Ad uništen vjetrom ledenim, silovitim.", TextEnglish = "And as for ʿAad, they were destroyed by a screaming,[1737] violent wind", TextGerman = "Was aber die ’Ad angeht, so wurden sie durch einen heftig wehenden eiskalten Wind vernichtet," });
                        surah69.VerseList.Add(new Verse { Number = 7, TextBosnian = "Njemu je On vlast nad njima sedam noći i osam dana uzastopnih bio prepustio, pa si u njima ljude povaljane kao šuplja palmina debla vidio,", TextEnglish = "Which He [i.e., Allāh] imposed upon them for seven nights and eight days in succession, so you would see the people therein fallen as if they were hollow trunks of palm trees.", TextGerman = "den Er entscheidende sieben Nächte und acht Tage fortgesetzt gegen sie einsetzte. Da hättest du in ihnen die Leute (auf dem Boden) niedergestreckt sehen können, als wären sie Stämme hohler Palmen." });
                        surah69.VerseList.Add(new Verse { Number = 8, TextBosnian = "i vidiš li da je iko od njih ostao?", TextEnglish = "Then do you see of them any remains?", TextGerman = "Siehst du denn etwas von ihnen (übrig)geblieben?" });
                        surah69.VerseList.Add(new Verse { Number = 9, TextBosnian = "A faraon, i oni prije njega, i prevrnuta naselja grijehe činiše,", TextEnglish = "And there came Pharaoh and those before him and the overturned cities[1738] with sin.", TextGerman = "Und es begingen Fir’aun und wer vor ihm war und die umgestürzten Städte Vergehen." });
                        surah69.VerseList.Add(new Verse { Number = 10, TextBosnian = "pa su bili neposlušni poslaniku Gospodara svoga, pa ih je On dohvatio žestokom kaznom.", TextEnglish = "And they disobeyed the messenger of their Lord, so He seized them with a seizure exceeding [in severity].", TextGerman = "So widersetzten sie sich den Gesandten ihres Herrn. Da ergriff Er sie mit stärker werdendem Griff." });
                        surah69.VerseList.Add(new Verse { Number = 11, TextBosnian = "Mi smo vas, kad je voda preplavila sve, u lađi nosili,", TextEnglish = "Indeed, when the water overflowed, We carried you [i.e., your ancestors] in the sailing ship[1739]", TextGerman = "Gewiß, als das Wasser das Maß überschritt, trugen ja Wir euch auf dem fahrenden (Schiff)," });
                        surah69.VerseList.Add(new Verse { Number = 12, TextBosnian = "da vam je poukom učinimo i da to od zaborava sačuva uho koje pamti.", TextEnglish = "That We might make it for you a reminder and [that] a conscious ear would be conscious of it.", TextGerman = "um es für euch zu einer Erinnerung zu machen, und damit es von einem jeden aufnahmefähigen Ohr aufgenommen wird." });
                        surah69.VerseList.Add(new Verse { Number = 13, TextBosnian = "A kad jednom u rog bude puhnuto,", TextEnglish = "Then when the Horn is blown with one blast", TextGerman = "Wenn ins Horn ein (einziges) Mal geblasen wird" });
                        surah69.VerseList.Add(new Verse { Number = 14, TextBosnian = "pa Zemlja i brda budu dignuta i od jednog udara zdrobljena,", TextEnglish = "And the earth and the mountains are lifted and leveled with one blow [i.e., stroke] -", TextGerman = "und Erde und Berge gehoben und dann mit einem einzigen Schlag zu Staub eingeebnet werden," });
                        surah69.VerseList.Add(new Verse { Number = 15, TextBosnian = "taj dan će se Događaj strahoviti dogoditi,", TextEnglish = "Then on that Day, the Occurrence [i.e., Resurrection] will occur,", TextGerman = "an jenem Tag dann trifft (die Stunde) ein, die eintreffen wird," });
                        surah69.VerseList.Add(new Verse { Number = 16, TextBosnian = "i nebo će se razdvojiti - tada će labavo biti.", TextEnglish = "And the heaven will split [open], for that Day it is infirm.[1740]", TextGerman = "und der Himmel spaltet sich, so daß er an jenem Tag brüchig wird," });
                        surah69.VerseList.Add(new Verse { Number = 17, TextBosnian = "I meleci će na krajevima njegovim stajati, a Arš Gospodara tvoga će taj dan iznad njih osmerica držati.", TextEnglish = "And the angels are at its edges. And there will bear the Throne of your Lord above them, that Day, eight [of them].", TextGerman = "und die Engel (befinden sich) an seinen Seiten. Und den Thron deines Herrn werden über ihnen an jenem Tag acht tragen." });
                        surah69.VerseList.Add(new Verse { Number = 18, TextBosnian = "Taj dan izloženi bit ćete, i nijedna tajna vaša neće skrivena ostati.", TextEnglish = "That Day, you will be exhibited [for judgement]; not hidden among you is anything concealed.[1741]", TextGerman = "An jenem Tag werdet ihr vorgeführt; nichts von euch wird verborgen bleiben." });
                        surah69.VerseList.Add(new Verse { Number = 19, TextBosnian = "Onaj kome se knjiga njegova u desnu ruku njegovu da, reći će: 'Evo vam, čitajte knjigu moju,", TextEnglish = "So as for he who is given his record in his right hand, he will say, 'Here, read my record!", TextGerman = "Was nun jemanden angeht, dem dann sein Buch in seine Rechte gegeben wird, der wird sagen: Ihr da, lest mein Buch." });
                        surah69.VerseList.Add(new Verse { Number = 20, TextBosnian = "ja sam zbilja i mislio da ću račun svoj polagati.'", TextEnglish = "Indeed, I was certain that I would be meeting my account.'", TextGerman = "Ich glaubte ja, daß ich meiner Abrechnung begegnen werde." });
                        surah69.VerseList.Add(new Verse { Number = 21, TextBosnian = "I on će biti u životu zadovoljan,", TextEnglish = "So he will be in a pleasant life -", TextGerman = "So wird er in einem zufriedenen Leben sein." });
                        surah69.VerseList.Add(new Verse { Number = 22, TextBosnian = "u Džennetu visokom,", TextEnglish = "In an elevated garden,", TextGerman = "in einem hohen Garten," });
                        surah69.VerseList.Add(new Verse { Number = 23, TextBosnian = "čiji će plodovi nadohvat ruke biti.", TextEnglish = "Its [fruit] to be picked hanging near.", TextGerman = "dessen Pflückobst herabhängt." });
                        surah69.VerseList.Add(new Verse { Number = 24, TextBosnian = "Jedite i pijte radosni, za ono što ste u danima minulim zaradili!'", TextEnglish = "[They will be told], 'Eat and drink in satisfaction for what you put forth[1742] in the days past.'", TextGerman = "Eßt und trinkt als wohlbekömmlich für das, was ihr früher in den vergangenen Tagen getan habt." });
                        surah69.VerseList.Add(new Verse { Number = 25, TextBosnian = "A onaj kome se knjiga da u lijevu ruku njegovu, reći će: 'Kamo sreće da mi knjiga moja ni data nije", TextEnglish = "But as for he who is given his record in his left hand, he will say, 'Oh, I wish I had not been given my record", TextGerman = "Was aber jemanden angeht, dem sein Buch in seine Linke gegeben wird, der wird sagen: O wäre mir doch mein Buch nicht gegeben worden!" });
                        surah69.VerseList.Add(new Verse { Number = 26, TextBosnian = "i da ni saznao nisam za obračun svoj!", TextEnglish = "And had not known what is my account.", TextGerman = "Und wüßte ich nicht, was meine Abrechnung ist!" });
                        surah69.VerseList.Add(new Verse { Number = 27, TextBosnian = "Kamo sreće da me je smrt dokrajčila,", TextEnglish = "I wish it [i.e., my death] had been the decisive one.[1743]", TextGerman = "O wäre dies doch nur das Ende (meines Lebens)!" });
                        surah69.VerseList.Add(new Verse { Number = 28, TextBosnian = "imetak moj mi nije od koristi,", TextEnglish = "My wealth has not availed me.", TextGerman = "Nicht nützt mir mein Besitz." });
