                        surah69.VerseList.Add(new Verse { Number = 30, TextBosnian = "Zgrabite ga i u okove okujte,", TextEnglish = "[Allāh will say], 'Seize him and shackle him.", TextGerman = "Nehmt ihn und fesselt ihn dann." });
                        surah69.VerseList.Add(new Verse { Number = 31, TextBosnian = "zatim ga samo u vatri pržite,", TextEnglish = "Then into Hellfire drive him.", TextGerman = "Hierauf setzt ihn dem Höllenbrand aus." });
                        surah69.VerseList.Add(new Verse { Number = 32, TextBosnian = "a onda ga u sindžire sedamdeset lakata duge vežite;", TextEnglish = "Then into a chain whose length is seventy cubits insert him.'", TextGerman = "Hierauf steckt ihn in eine Kette, deren Länge siebzig Ellen ist." });
                        surah69.VerseList.Add(new Verse { Number = 33, TextBosnian = "zaista, on u Allaha Velikog nije vjerovao", TextEnglish = "Indeed, he did not used to believe in Allāh, the Most Great,", TextGerman = "Er pflegte nämlich nicht an Allah, den Allgewaltigen zu glauben" });
                        surah69.VerseList.Add(new Verse { Number = 34, TextBosnian = "i da se nahrani nevoljnik nije podsticao,", TextEnglish = "Nor did he encourage the feeding of the poor.", TextGerman = "und nicht zur Speisung des Armen anzuhalten." });
                        surah69.VerseList.Add(new Verse { Number = 35, TextBosnian = "zato on ovdje danas nema prisna prijatelja", TextEnglish = "So there is not for him here this Day any devoted friend", TextGerman = "So hat er hier heute keinen warmherzigen Freund" });
                        surah69.VerseList.Add(new Verse { Number = 36, TextBosnian = "ni drugog jela osim smrdljive kapljevine", TextEnglish = "Nor any food except from the discharge of wounds;", TextGerman = "und auch keine Speise außer Jauche," });
                        surah69.VerseList.Add(new Verse { Number = 37, TextBosnian = "koju će samo nevjernici jesti.'", TextEnglish = "None will eat it except the sinners.", TextGerman = "die nur diejenigen essen, die Verfehlungen begangen haben." });
                        surah69.VerseList.Add(new Verse { Number = 38, TextBosnian = "A Ja se kunem onim što vidite", TextEnglish = "So I swear by what you see", TextGerman = "Nein! Ich schwöre bei dem, was ihr seht," });
                        surah69.VerseList.Add(new Verse { Number = 39, TextBosnian = "i onim što ne vidite,", TextEnglish = "And what you do not see", TextGerman = "und dem, was ihr nicht seht." });
