                        surah69.VerseList.Add(new Verse { Number = 41, TextBosnian = "a nije govor nikakva pjesnika - kako vi malo vjerujete!", TextEnglish = "And it is not the word of a poet; little do you believe.", TextGerman = "das sind nicht die Worte eines Dichters. Wie wenig ihr glaubt!" });
                        surah69.VerseList.Add(new Verse { Number = 42, TextBosnian = "I nisu riječi nikakva proroka - kako vi malo razmišljate!", TextEnglish = "Nor the word of a soothsayer; little do you remember.", TextGerman = "Und es sind auch nicht die Worte eines Wahrsagers. Wie wenig ihr bedenkt!" });
