                        surah69.VerseList.Add(new Verse { Number = 45, TextBosnian = "Mi bismo ga desnicom dohvatili,", TextEnglish = "We would have seized him by the right hand;[1745]", TextGerman = "hätten Wir ihn sicherlich an der Rechten gefaßt" });
