                        surah69.VerseList.Add(new Verse { Number = 47, TextBosnian = "i niko ga između vas ne bi mogao od toga odbraniti.", TextEnglish = "And there is no one of you who could prevent [Us] from him.", TextGerman = "und niemand von euch hätte (Uns) dann von ihm abhalten können." });
                        surah69.VerseList.Add(new Verse { Number = 48, TextBosnian = "Zaista je on opomena bogobojaznima,", TextEnglish = "And indeed, it [i.e., the Qur’ān] is a reminder for the righteous.", TextGerman = "Er ist wahrlich eine Erinnerung für die Gottesfürchtigen." });
                        surah69.VerseList.Add(new Verse { Number = 49, TextBosnian = "a Mi sigurno znamo da će među vama poricatelja biti.", TextEnglish = "And indeed, We know that among you are deniers.", TextGerman = "Und Wir wissen wahrlich, daß es unter euch Leugner gibt." });
                        surah69.VerseList.Add(new Verse { Number = 50, TextBosnian = "I on je zbilja jad nevjernicima,", TextEnglish = "And indeed, it will be [a cause of] regret upon the disbelievers.", TextGerman = "Und er ist wahrlich (ein Grund zum) Gram für die Ungläubigen." });
