                        surah70.VerseList.Add(new Verse { Number = 7, TextBosnian = "a mi smatramo da je blizu.", TextEnglish = "But We see it [as] near.", TextGerman = "Wir aber sehen sie nahe." });
                        surah70.VerseList.Add(new Verse { Number = 8, TextBosnian = "Na dan kada nebo bude kao talog od zejtina,", TextEnglish = "On the Day the sky will be like murky oil,[1749]", TextGerman = "Am Tag, da der Himmel wie siedendes Öl" });
                        surah70.VerseList.Add(new Verse { Number = 9, TextBosnian = "a brda kao vuna raščupana,", TextEnglish = "And the mountains will be like wool,[1750]", TextGerman = "und die Berge wie gefärbte Wolle sein werden" });
                        surah70.VerseList.Add(new Verse { Number = 10, TextBosnian = "kada bližnji neće bližnjeg ništa pitati,", TextEnglish = "And no friend will ask [anything of] a friend,", TextGerman = "und kein warmherziger Freund seinen Freund (irgend etwas) fragt." });
                        surah70.VerseList.Add(new Verse { Number = 11, TextBosnian = "iako će jedni druge vidjeti. Prestupnik će htjeti da se od patnje taj dan iskupi sinovima svojim,", TextEnglish = "They will be shown each other. The criminal will wish that he could be ransomed from the punishment of that Day by his children.", TextGerman = "obwohl sie ihrem Anblick ausgesetzt sein werden. Der Übeltäter hätte es gern, wenn er sich von der Strafe jenes Tages loskaufte mit seinen Söhnen" });
                        surah70.VerseList.Add(new Verse { Number = 12, TextBosnian = "i suprugom svojom, i bratom svojim,", TextEnglish = "And his wife and his brother", TextGerman = "und seiner Gefährtin und seinem Bruder" });
                        surah70.VerseList.Add(new Verse { Number = 13, TextBosnian = "i porodicom svojom koja ga štiti,", TextEnglish = "And his nearest kindred who shelter him.", TextGerman = "und seiner Familie, die ihn aufgenommen hat," });
                        surah70.VerseList.Add(new Verse { Number = 14, TextBosnian = "i svima ostalima na Zemlji, samo da se izbavi.", TextEnglish = "And whoever is on earth entirely [so] then it could save him.", TextGerman = "und allen, die auf der Erde sind. (Er wünscht,) dies möge ihn hierauf retten." });
                        surah70.VerseList.Add(new Verse { Number = 15, TextBosnian = "Nikako! Ona je, zaista, buktinja žestoka", TextEnglish = "No![1751] Indeed, it is the Flame [of Hell],", TextGerman = "Keineswegs! Es ist doch ein loderndes Feuer," });
                        surah70.VerseList.Add(new Verse { Number = 16, TextBosnian = "koja će kože guliti,", TextEnglish = "A remover of exteriors.[1752]", TextGerman = "das die Kopfhaut abzieht," });
                        surah70.VerseList.Add(new Verse { Number = 17, TextBosnian = "zvat će onoga ko se okretao i izbjegavao", TextEnglish = "It invites he who turned his back [on truth] and went away [from obedience]", TextGerman = "das (den) rufen wird, wer den Rücken kehrt und sich abkehrt," });
                        surah70.VerseList.Add(new Verse { Number = 18, TextBosnian = "i zgrtao i skrivao.", TextEnglish = "And collected [wealth] and hoarded.", TextGerman = "(Besitz) zusammenträgt und dann in Behältern hortet." });
                        surah70.VerseList.Add(new Verse { Number = 19, TextBosnian = "Čovjek je, uistinu, stvorenje lakomo:", TextEnglish = "Indeed, mankind was created anxious:", TextGerman = "Gewiß, der Mensch ist als kleinmütig erschaffen." });
                        surah70.VerseList.Add(new Verse { Number = 20, TextBosnian = "kada ga nevolja snađe - očajan je,", TextEnglish = "When evil touches him, impatient,", TextGerman = "Wenn ihm Schlechtes widerfährt, ist er sehr mutlos;" });
                        surah70.VerseList.Add(new Verse { Number = 21, TextBosnian = "a kada ga zadesi dobro, on ga uskraćuje,", TextEnglish = "And when good touches him, withholding [of it],", TextGerman = "und wenn ihm Gutes widerfährt, (ist er) ein stetiger Verweigerer," });
                        surah70.VerseList.Add(new Verse { Number = 22, TextBosnian = "osim klanjača", TextEnglish = "Except the observers of prayer -", TextGerman = "außer den Betenden," });
                        surah70.VerseList.Add(new Verse { Number = 23, TextBosnian = "koji su u svojim namazima ustrajni,", TextEnglish = "Those who are constant in their prayer", TextGerman = "diejenigen, die in ihrem Gebet beharrlich sind" });
                        surah70.VerseList.Add(new Verse { Number = 24, TextBosnian = "i oni u čijim imecima ima poznato pravo", TextEnglish = "And those within whose wealth is a known right[1753]", TextGerman = "und die ein festgesetztes Recht an ihrem Besitz (zugestehen)" });
                        surah70.VerseList.Add(new Verse { Number = 25, TextBosnian = "onaj koji prosi i za onoga koji je uskraćen a ne prosi,", TextEnglish = "For the petitioner and the deprived -", TextGerman = "dem Bettler und dem Unbemittelten" });
                        surah70.VerseList.Add(new Verse { Number = 26, TextBosnian = "i oni koji u Sudnji dan vjeruju,", TextEnglish = "And those who believe in the Day of Recompense", TextGerman = "und die den Tag des Gerichts für wahr halten" });
                        surah70.VerseList.Add(new Verse { Number = 27, TextBosnian = "i oni koji od kazne Allahove strahuju,", TextEnglish = "And those who are fearful of the punishment of their Lord -", TextGerman = "und diejenigen, die wegen der Strafe ihres Herrn besorgt sind," });
                        surah70.VerseList.Add(new Verse { Number = 28, TextBosnian = "a od kazne Gospodara svoga niko nije siguran,", TextEnglish = "Indeed, the punishment of their Lord is not that from which one is safe -", TextGerman = "- denn gewiß, vor der Strafe ihres Herrn (kann) sich niemand sicher glauben," });
                        surah70.VerseList.Add(new Verse { Number = 29, TextBosnian = "i oni koji stidna mjesta svoja čuvaju,", TextEnglish = "And those who guard their private parts", TextGerman = "-und diejenigen, die ihre Scham hüten," });
                        surah70.VerseList.Add(new Verse { Number = 30, TextBosnian = "osim od supruga svojih ili onih koje su u vlasništvu njihovu - oni, doista, prijekor ne zaslužuju.", TextEnglish = "Except from their wives or those their right hands possess,[1754] for indeed, they are not to be blamed -", TextGerman = "außer gegenüber ihren Gattinnen oder was ihre rechte Hand (an Sklavinnen) besitzt, denn sie sind (hierin) nicht zu tadeln," });
                        surah70.VerseList.Add(new Verse { Number = 31, TextBosnian = "Oni koji i pored toga nešto traže, to su baš oni koji prelaze granicu,", TextEnglish = "But whoever seeks beyond that, then they are the transgressors -", TextGerman = "- wer aber darüber hinaus (etwas) begehrt, das sind die Übertreter," });
                        surah70.VerseList.Add(new Verse { Number = 32, TextBosnian = "i oni koji na povjerene im amanete i obaveze svoje paze,", TextEnglish = "And those who are to their trusts and promises attentive.", TextGerman = "- und diejenigen, die auf die ihnen anvertrauten Güter und ihre Verpflichtung achtgeben," });
                        surah70.VerseList.Add(new Verse { Number = 33, TextBosnian = "i oni koji iza svojih svjedočenja čvrsto stoje,", TextEnglish = "And those who are in their testimonies upright", TextGerman = "und diejenigen, die ihr Zeugnis in Aufrichtigkeit ablegen," });
                        surah70.VerseList.Add(new Verse { Number = 34, TextBosnian = "i oni koji na namaze svoje paze,", TextEnglish = "And those who [carefully] maintain their prayer:", TextGerman = "und diejenigen, die ihr Gebet einhalten." });
                        surah70.VerseList.Add(new Verse { Number = 35, TextBosnian = "oni će u džennetskim baščama biti počašćeni.", TextEnglish = "They will be in gardens,[1755] honored.", TextGerman = "Jene werden sich in Gärten befinden und (darin) geehrt." });
                        surah70.VerseList.Add(new Verse { Number = 36, TextBosnian = "Šta je onima oko tebe koji ne vjeruju pa žure,", TextEnglish = "So what is [the matter] with those who disbelieve, hastening [from] before you, [O Muḥammad],", TextGerman = "Was ist mit denjenigen, die ungläubig sind, daß sie hastig auf dich zukommen" });
                        surah70.VerseList.Add(new Verse { Number = 37, TextBosnian = "zdesna i slijeva, u gomilama?!", TextEnglish = "[To sit] on [your] right and [your] left in separate groups?[1756]", TextGerman = "rechts und links, in Gruppen aufgeteilt?" });
                        surah70.VerseList.Add(new Verse { Number = 38, TextBosnian = "Zar svaki od njih žudi da u Džennet uživanja uđe?!", TextEnglish = "Does every person among them aspire to enter a garden of pleasure?", TextGerman = "Begehrt (etwa) jedermann von ihnen, in einen Garten der Wonne eingelassen zu werden?" });
                        surah70.VerseList.Add(new Verse { Number = 39, TextBosnian = "Naprotiv! Mi smo ih stvorili od onoga što oni znaju!", TextEnglish = "No! Indeed, We have created them from that which they know.[1757]", TextGerman = "Keineswegs! Wir haben sie doch aus dem erschaffen, was sie wissen." });
                        surah70.VerseList.Add(new Verse { Number = 40, TextBosnian = "Ja se kunem Gospodarom istoka i zapada da ih možemo", TextEnglish = "So I swear by the Lord of [all] risings and settings[1758] that indeed We are able", TextGerman = "Nein! Ich schwöre beim Herrn der Osten und der Westen, Wir haben fürwahr die Macht dazu," });
                        surah70.VerseList.Add(new Verse { Number = 41, TextBosnian = "boljim od njih zamijeniti i niko Nas u tome ne može spriječiti.", TextEnglish = "To replace them with better than them; and We are not to be outdone.", TextGerman = "daß Wir sie durch bessere als sie austauschen. Und niemand kann Uns zuvorkommen." });
                        surah70.VerseList.Add(new Verse { Number = 42, TextBosnian = "Zato ih ostavi neka se u besposlice udubljuju i zabavljaju dok Dan kojim im se prijeti ne dočekaju,", TextEnglish = "So leave them to converse vainly and amuse themselves until they meet their Day which they are promised -", TextGerman = "So lasse sie nur schweifende Gespräche führen und ihr Spiel treiben, bis sie ihrem Tag begegnen, der ihnen angedroht ist," });
                        surah70.VerseList.Add(new Verse { Number = 43, TextBosnian = "Dan u kome će žurno, kao da kumirima hrle, iz grobova izići,", TextEnglish = "The Day they will emerge from the graves rapidly as if they were, toward an erected idol, hastening.[1759]", TextGerman = "dem Tag, da sie aus den Gräbern eilig herauskommen werden, als würden sie hastig zu einem aufgerichteten Opferstein laufen," });
                        surah70.VerseList.Add(new Verse { Number = 44, TextBosnian = "oborenih pogleda i poniženjem ophrvani. To će biti Dan kojim im je prijećeno!", TextEnglish = "Their eyes humbled, humiliation will cover them. That is the Day which they had been promised.", TextGerman = "mit demütigen Blicken, bedeckt mit Erniedrigung. Das ist der Tag, der ihnen immer wieder angedroht wurde." });
                        result = surah70;
                        break;
