                    case 71:
                        Surah surah71 = new Surah(); surah71.Name = Constants.Surah71Name; surah71.Number = 71; surah71.VerseList = new List<Verse>();
                        surah71.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah71.VerseList.Add(new Verse { Number = 1, TextBosnian = "Mi smo poslali Nuha narodu njegovu: 'Upozoravaj narod svoj prije nego što ga stigne patnja bolna!'", TextEnglish = "Indeed, We sent Noah to his people, [saying], 'Warn your people before there comes to them a painful punishment.'", TextGerman = "Wir sandten ja Nuh zu seinem Volk: Warne dein Volk, bevor schmerzhafte Strafe über sie kommt." });
                        surah71.VerseList.Add(new Verse { Number = 2, TextBosnian = "O narode moj', govorio je on, 'ja sam vam zbilja upozoritelj jasni!", TextEnglish = "He said, 'O my people, indeed I am to you a clear warner -", TextGerman = "Er sagte: O mein Volk, ich bin euch ja ein deutlicher Warner," });
                        surah71.VerseList.Add(new Verse { Number = 3, TextBosnian = "Allahu u ibadetu budite, Njega se bojte i meni poslušni budite.", TextEnglish = "To worship Allāh, fear Him and obey me.", TextGerman = "(euch zu mahnen:) Dient Allah und fürchtet Ihn, und gehorcht mir," });
                        surah71.VerseList.Add(new Verse { Number = 4, TextBosnian = "On će vam grijehe vaše oprostiti i u životu vas do Određenog časa ostaviti, a kada Allahov Određeni čas dođe, zaista se neće, neka znate, odgoditi.'", TextEnglish = "He [i.e., Allāh] will forgive you of your sins and delay you for a specified term. Indeed, the time [set by] Allāh, when it comes, will not be delayed, if you only knew.'", TextGerman = "so wird Er euch (etwas) von euren Sünden vergeben und euch auf eine festgesetzte Frist zurückstellen. Gewiß, Allahs Frist, wenn sie kommt, kann nicht zurückgestellt werden, wenn ihr nur wüßtet!" });
                        surah71.VerseList.Add(new Verse { Number = 5, TextBosnian = "On reče: 'Gospodaru moj, ja sam narod svoj i noću i danju, doista, pozivao,", TextEnglish = "He said, 'My Lord, indeed I invited my people [to truth] night and day.", TextGerman = "Er sagte: Mein Herr, ich habe mein Volk bei Nacht und bei Tag aufgerufen." });
                        surah71.VerseList.Add(new Verse { Number = 6, TextBosnian = "ali ga je pozivanje moje još više udaljilo.", TextEnglish = "But my invitation increased them not except in flight [i.e., aversion].", TextGerman = "Aber mein Rufen hat sie nur in ihrer Flucht bestärkt." });
                        surah71.VerseList.Add(new Verse { Number = 7, TextBosnian = "I kad god sam ih pozivao da im oprostiš, prste su svoje u uši stavljali i haljinama svojim se pokrivali - bili su uporni i pretjerano oholi.", TextEnglish = "And indeed, every time I invited them that You may forgive them, they put their fingers in their ears, covered themselves with their garments,[1761] persisted, and were arrogant with [great] arrogance.", TextGerman = "Gewiß, jedesmal, wenn ich sie aufrief, damit Du ihnen vergibst, steckten sie ihre Finger in ihre Ohren, überdeckten sich mit ihren Gewändern, verharrten (im Irrtum) und verhielten sich sehr hochmütig." });
                        surah71.VerseList.Add(new Verse { Number = 8, TextBosnian = "Zatim sam ih ja otvoreno pozivao,", TextEnglish = "Then I invited them publicly.", TextGerman = "Hierauf richtete ich an sie den Ruf dann öffentlich." });
                        surah71.VerseList.Add(new Verse { Number = 9, TextBosnian = "a onda sam im javno objavljivao i u povjerenju im šaputao,", TextEnglish = "Then I announced to them and [also] confided to them secretly", TextGerman = "Hierauf sprach ich zu ihnen offen und ganz im geheimen." });
                        surah71.VerseList.Add(new Verse { Number = 10, TextBosnian = "i govorio: 'Tražite od Gospodara svoga oprost. On, doista, mnogo prašta;", TextEnglish = "And said, 'Ask forgiveness of your Lord. Indeed, He is ever a Perpetual Forgiver.", TextGerman = "Ich sagte: .Bittet euren Herrn um Vergebung - Er ist ja Allvergebend -," });
                        surah71.VerseList.Add(new Verse { Number = 11, TextBosnian = "On će vam kišu obilatu slati", TextEnglish = "He will send [rain from] the sky upon you in [continuing] showers", TextGerman = "so wird Er den Regen auf euch ergiebig (herab)senden" });
                        surah71.VerseList.Add(new Verse { Number = 12, TextBosnian = "i mnogo vam imanja i sinova dati. I dat će vam bašče, a i rijeke će vam dati.", TextEnglish = "And give you increase in wealth and children and provide for you gardens and provide for you rivers.", TextGerman = "und euch mit Besitz und Söhnen unterstützen und für euch Gärten machen und für euch Bäche machen." });
                        surah71.VerseList.Add(new Verse { Number = 13, TextBosnian = "Šta vam je, pa se Allahove veličine ne bojite,", TextEnglish = "What is [the matter] with you that you do not attribute to Allāh [due] grandeur", TextGerman = "Was ist mit euch, daß ihr nicht glaubt, von Allah würdevoll behandelt zu werden," });
                        surah71.VerseList.Add(new Verse { Number = 14, TextBosnian = "a On vas postepeno stvara?!", TextEnglish = "While He has created you in stages?[1762]", TextGerman = "wo Er euch doch in Entwicklungsabschnitten erschaffen hat?" });
                        surah71.VerseList.Add(new Verse { Number = 15, TextBosnian = "Zar ne vidite kako je Allah sedam nebesa, jedno iznad drugog, stvorio,", TextEnglish = "Do you not consider how Allāh has created seven heavens in layers[1763]", TextGerman = "Seht ihr nicht, wie Allah sieben Himmel in Schichten (übereinander) erschaffen hat," });
                        surah71.VerseList.Add(new Verse { Number = 16, TextBosnian = "i na njima Mjesec svijetlim dao, a Sunce svjetiljkom učinio?!", TextEnglish = "And made the moon therein a [reflected] light and made the sun a burning lamp?", TextGerman = "den Mond darin zu einem Licht gemacht und die Sonne zu einem Leuchtkörper gemacht hat?" });
                        surah71.VerseList.Add(new Verse { Number = 17, TextBosnian = "Allah vas od zemlje poput bilja stvara,", TextEnglish = "And Allāh has caused you to grow from the earth a [progressive] growth.", TextGerman = "Und Allah hat euch aus der Erde heranwachsen lassen." });
                        surah71.VerseList.Add(new Verse { Number = 18, TextBosnian = "zatim vas u nju vraća i iz nje će vas sigurno izvesti.", TextEnglish = "Then He will return you into it and extract you [another] extraction.", TextGerman = "Hierauf wird Er euch in sie zurückbringen und gewiß (wieder aus ihr) hervorbringen." });
                        surah71.VerseList.Add(new Verse { Number = 19, TextBosnian = "Allah vam je Zemlju učinio ravnom,", TextEnglish = "And Allāh has made for you the earth an expanse", TextGerman = "Und Allah hat euch die Erde zu einer ausgebreiteten Unterlage gemacht," });
                        surah71.VerseList.Add(new Verse { Number = 20, TextBosnian = "da biste po njoj hodili putevima prostranim.''", TextEnglish = "That you may follow therein roads of passage.''", TextGerman = "damit ihr auf ihr dahinziehen könnt - auf Wegen als breiten Durchgängen’." });
                        surah71.VerseList.Add(new Verse { Number = 21, TextBosnian = "Nuh reče: 'Gospodaru moj, oni me ne slušaju i povode se za onima čija bogatstva i djeca samo njihovu propast uvećavaju", TextEnglish = "Noah said, 'My Lord, indeed they have disobeyed me and followed him whose wealth and children will not increase him except in loss.", TextGerman = "Nuh sagte: Mein Herr, sie haben sich mir widersetzt und sind jemandem gefolgt, dem sein Besitz und seine Kinder nur den Verlust mehren." });
                        surah71.VerseList.Add(new Verse { Number = 22, TextBosnian = "i spletke velike snuju.", TextEnglish = "And they conspired an immense conspiracy", TextGerman = "Und sie haben ungeheure Ränke geschmiedet." });
                        surah71.VerseList.Add(new Verse { Number = 23, TextBosnian = "I govore: 'Nikako božanstva svoja ne ostavljajte, i nikako, ni Vedda, ni Suvaa, a ni Jegusa, ni Jelika, ni Nesra ne napuštajte!',", TextEnglish = "And said, 'Never leave your gods and never leave Wadd or Suwāʿ or Yaghūth and Yaʿūq and Nasr.'[1764]", TextGerman = "Und sie haben gesagt: .Verlaßt doch nicht eure Götter; verlaßt doch nicht Wadd, noch Suwa, noch Yagut,Ya’uq und Nasr.’" });
                        surah71.VerseList.Add(new Verse { Number = 24, TextBosnian = "a već su mnoge u zabludu doveli, pa Ti zulumćarima samo zabludu povećaj!'", TextEnglish = "And already they have misled many. And, [my Lord], do not increase the wrongdoers except in error.'", TextGerman = "Sie haben ja viele in die Irre geführt. So lasse die Ungerechten nur noch tiefer in die Irre gehen." });
                        surah71.VerseList.Add(new Verse { Number = 25, TextBosnian = "I oni su zbog grijeha svojih potopljeni i u Vatru će biti uvedeni i nikoga sebi, mimo Allaha, kao pomagače neće naći.", TextEnglish = "Because of their sins they were drowned and put into the Fire, and they found not for themselves besides Allāh [any] helpers.", TextGerman = "Wegen ihrer Verfehlungen wurden sie ertränkt und dann in ein (Höllen)feuer hineingebracht. Und da fanden sie für sich außer Allah keine Helfer." });
                        surah71.VerseList.Add(new Verse { Number = 26, TextBosnian = "I Nuh reče: 'Gospodaru moj, ne ostavi na Zemlji nijednog nevjernika,", TextEnglish = "And Noah said, 'My Lord, do not leave upon the earth from among the disbelievers an inhabitant.", TextGerman = "Und Nuh sagte: Mein Herr, lasse auf der Erde von den Ungläubigen nicht einen einzigen Bewohner." });
                        surah71.VerseList.Add(new Verse { Number = 27, TextBosnian = "jer ako ih ostaviš, oni će robove Tvoje u zabludu zavoditi i samo će grešnika i nevjernika rađati!", TextEnglish = "Indeed, if You leave them, they will mislead Your servants and not beget except [every] wicked one and [confirmed] disbeliever.", TextGerman = "Denn, gewiß, wenn Du sie (leben) läßt, führen sie Deine Diener in die Irre und zeugen nur Sittenlose und sehr beharrliche Ungläubige." });
                        surah71.VerseList.Add(new Verse { Number = 28, TextBosnian = "Gospodaru moj, oprosti meni, i roditeljima mojim, i onome koji kao vjernik u dom moj uđe, i vjernicima i vjernicama, a zulumćarima samo propast povećaj!'", TextEnglish = "My Lord, forgive me and my parents and whoever enters my house a believer and the believing men and believing women. And do not increase the wrongdoers except in destruction.'", TextGerman = "Mein Herr, vergib mir und meinen Eltern und demjenigen, der als Gläubiger mein Haus betritt, und den gläubigen Männern und den gläubigen Frauen. Und stürze die Ungerechten nur noch tiefer ins Verderben." });
                        result = surah71;
                        break;
