                        surah72.VerseList.Add(new Verse { Number = 1, TextBosnian = "Reci: 'Meni je objavljeno da je nekoliko džina prisluškivalo i reklo: 'Mi smo, doista, Kur'an divni' slušali;", TextEnglish = "Say, [O Muḥammad], 'It has been revealed to me that a group of the jinn listened and said, 'Indeed, we have heard an amazing Qur’ān [i.e., recitation].", TextGerman = "Sag: Mir ist (als Offenbarung) eingegeben worden, daß eine kleinere Schar Ginn zuhörte. Sie sagten: Wir haben einen wunderbaren Qur’an gehört," });
                        surah72.VerseList.Add(new Verse { Number = 2, TextBosnian = "koji na ono što je ispravno upućuje - i mi smo u njega povjerovali i više nikoga nećemo Gospodaru našem u obožavanju pridruživati,", TextEnglish = "It guides to the right course, and we have believed in it. And we will never associate with our Lord anyone.", TextGerman = "der zur Besonnenheit leitet; so haben wir an ihn geglaubt, und wir werden unserem Herrn niemanden beigesellen." });
                        surah72.VerseList.Add(new Verse { Number = 3, TextBosnian = "a On - uzvišena je moć Gospodara našeg! - nije uzeo Sebi ni druge ni djeteta!", TextEnglish = "And [it teaches] that exalted is the nobleness of our Lord; He has not taken a wife or a son", TextGerman = "Und erhaben ist die Größe unseres Herrn. Er hat sich weder eine Gefährtin noch Kinder genommen." });
                        surah72.VerseList.Add(new Verse { Number = 4, TextBosnian = "Jedan naš maloumnik o Allahu je veliku laž govorio,", TextEnglish = "And that our foolish one [i.e., Iblees][1766] has been saying about Allāh an excessive transgression.", TextGerman = "Und es pflegte der Tor unter uns etwas Abwegiges gegen Allah zu sagen." });
                        surah72.VerseList.Add(new Verse { Number = 5, TextBosnian = "a mi smo mislili da ni ljudi ni džini o Allahu laži ne govore;", TextEnglish = "And we had thought that mankind and the jinn would never speak about Allāh a lie.", TextGerman = "Und wir meinten, daß weder die Menschen noch die Ginn gegen Allah jemals eine Lüge sagen würden." });
                        surah72.VerseList.Add(new Verse { Number = 6, TextBosnian = "i bilo je ljudi koji su zaštitu od džina tražili, pa su im još više strah povećali;", TextEnglish = "And there were men from mankind who sought refuge in men from the jinn, so they [only] increased them in burden [i.e., sin].", TextGerman = "Und (manche) Männer von den Menschen pflegten Zuflucht zu nehmen bei (einigen) Männern von den Ginn, doch mehrten sie so (bei) ihnen die Drangsal." });
                        surah72.VerseList.Add(new Verse { Number = 7, TextBosnian = "i oni misle, kao što i vi mislite, da Allah nikoga neće oživiti.", TextEnglish = "And they had thought, as you thought, that Allāh would never send anyone [as a messenger].", TextGerman = "Und sie meinten -, wie (auch) ihr meint -, daß Allah niemanden mehr (als Gesandten) schicken würde." });
                        surah72.VerseList.Add(new Verse { Number = 8, TextBosnian = "I mi smo nebo izvidjeli i utvrdili smo da je moćnih čuvara i plamtećih svjetlica puno;", TextEnglish = "And we have sought [to reach] the heaven but found it filled with powerful guards and burning flames.", TextGerman = "Und wir haben den Himmel abgetastet, aber festgestellt, daß er mit strengen Wächtern und Leuchtkörpern gefüllt worden ist." });
                        surah72.VerseList.Add(new Verse { Number = 9, TextBosnian = "i posjedali bismo po njemu na raznim mjestima da bismo šta čuli, ali će onaj, ko sada prisluškuje, na plamteću svjetlicu koja vreba naići;", TextEnglish = "And we used to sit therein in positions for hearing,[1767] but whoever listens now will find a burning flame lying in wait for him.", TextGerman = "Und wir pflegten ja an Stellen von ihm Sitze zum Abhören einzunehmen; wer aber jetzt abhört, der findet einen auf ihn wartenden Leuchtkörper." });
                        surah72.VerseList.Add(new Verse { Number = 10, TextBosnian = "i mi ne znamo da li se onima na Zemlji želi zlo ili im Gospodar njihov želi dobro.", TextEnglish = "And we do not know [therefore] whether evil is intended for those on earth or whether their Lord intends for them a right course.", TextGerman = "Und wir wissen doch nicht, ob für diejenigen auf der Erde Schlechtes gewollt wird oder ob ihr Herr sie zur Besonnenheit (führen) will." });
                        surah72.VerseList.Add(new Verse { Number = 11, TextBosnian = "A među nama ima i dobrih i onih koji to nisu, ima nas vrsta različitih;", TextEnglish = "And among us are the righteous, and among us are [others] not so; we were [of] divided ways.[1768]", TextGerman = "Und unter uns gibt es Rechtschaffene, und unter uns gibt es solche, die weniger rechtschaffen sind; wir sind getrennte(n) Wege(n gefolgt)." });
                        surah72.VerseList.Add(new Verse { Number = 12, TextBosnian = "i mi znamo da se ne možemo suprotstaviti Allahu na Zemlji, niti Mu umaći.", TextEnglish = "And we have become certain that we will never cause failure to Allāh upon earth, nor can we escape Him by flight.", TextGerman = "Und wir meinten, daß wir uns auf der Erde Allah niemals entziehen können und daß wir uns Ihm niemals durch Flucht entziehen können." });
                        surah72.VerseList.Add(new Verse { Number = 13, TextBosnian = "I mi smo, čim smo Uputu- Kur'an čuli, u njega povjerovali; a ko u Gospodara svoga vjeruje, ni umanjivanja dobrih, ni dodavanja loših djela ne treba da se boji;", TextEnglish = "And when we heard the guidance [i.e., the Qur’ān], we believed in it. And whoever believes in his Lord will not fear deprivation or burden.[1769]", TextGerman = "Und als wir die Rechtleitung hörten, glaubten wir daran. Wer an seinen Herrn glaubt, der befürchtet keine (Lohn)schmälerung und keine Drangsal." });
                        surah72.VerseList.Add(new Verse { Number = 14, TextBosnian = "i ima nas muslimana, a ima nas i zalutalih, oni koji islam prihvate ono što je ispravno odabrali su,", TextEnglish = "And among us are Muslims [in submission to Allāh], and among us are the unjust.[1770] And whoever has become Muslim - those have sought out the right course.", TextGerman = "Und unter uns sind Gottergebene; und unter uns sind (vom rechten Weg) Abschweifende. Was nun jemanden angeht, der sich (Allah) ergeben hat, so bemüht er sich um (mehr) Besonnenheit." });
                        surah72.VerseList.Add(new Verse { Number = 15, TextBosnian = "a oni koji skrenuli su, u Džehennemu gorivo bit će.", TextEnglish = "But as for the unjust, they will be, for Hell, firewood.'", TextGerman = "Was aber die (vom rechten Weg) Abschweifenden angeht, so werden sie Brennholz für die Hölle sein." });
                        surah72.VerseList.Add(new Verse { Number = 16, TextBosnian = "A da su ustrajali na putu Istine, Mi bismo ih vodom obilnom pojili,", TextEnglish = "And [Allāh revealed] that if they had remained straight on the way, We would have given them abundant rain [i.e., provision].", TextGerman = "Und wenn sie sich auf dem Weg recht verhielten, würden Wir ihnen wahrlich Wasser im Überfluß zu trinken geben," });
                        surah72.VerseList.Add(new Verse { Number = 17, TextBosnian = "I da bismo ih time na kušnju stavili; a onog ko se okrene od Opomene Gospodara svoga - On će u patnju tešku uvesti.", TextEnglish = "So We might test them therein. And whoever turns away from the remembrance of his Lord[1771] He will put into arduous punishment.", TextGerman = "damit Wir sie dadurch der Versuchung aussetzen. Und wer sich von der Ermahnung seines Herrn abwendet, den wird Er in überwältigende Strafe stecken." });
                        surah72.VerseList.Add(new Verse { Number = 18, TextBosnian = "Mesdžidi jesu Allaha radi, i ne molite, pored Allaha, nikoga!", TextEnglish = "And [He revealed] that the masjids[1772] are for Allāh, so do not invoke[1773] with Allāh anyone.", TextGerman = "Und die Gebetsstätten gehören doch Allah; so ruft neben Allah niemanden an." });
                        surah72.VerseList.Add(new Verse { Number = 19, TextBosnian = "A kada je Allahov rob ustao zazivajući Ga, oni su se skoro svi na njega u gomilama okomili.", TextEnglish = "And that when the Servant [i.e., Prophet] of Allāh stood up supplicating Him, they almost became about him a compacted mass.'[1774]", TextGerman = "Und als Allahs Diener aufstand, um Ihn anzurufen, hätten sie ihn (alle) beinahe erdrückt." });
                        surah72.VerseList.Add(new Verse { Number = 20, TextBosnian = "Reci: 'Ja se samo Gospodaru svome molim i nikoga Njemu u obožavanju ne pridružujem.'", TextEnglish = "Say, [O Muḥammad], 'I only invoke my Lord and do not associate with Him anyone.'", TextGerman = "Sag: Ich rufe nur meinen Herrn an, und ich geselle Ihm niemanden bei." });
                        surah72.VerseList.Add(new Verse { Number = 21, TextBosnian = "Reci: 'Ja nisam u stanju od vas kakvu štetu otkloniti niti nekom od vas neku korist pribaviti.'", TextEnglish = "Say, 'Indeed, I do not possess for you [the power of] harm or right direction.'", TextGerman = "Sag: Ich vermag euch weder Schaden noch Besonnenheit (zu bringen)." });
                        surah72.VerseList.Add(new Verse { Number = 22, TextBosnian = "Reci: 'Mene niko od Allaha ne može zaštititi; samo u Njega mogu utočište naći,", TextEnglish = "Say, 'Indeed, there will never protect me from Allāh anyone [if I should disobey], nor will I find in other than Him a refuge.", TextGerman = "Sag: Niemand kann mir gegen Allah Schutz gewähren, und ich werde außer (bei) Ihm keinen Ort der Sicherheit finden." });
                        surah72.VerseList.Add(new Verse { Number = 23, TextBosnian = "samo mogu oglasiti ono što je od Allaha i poslanice Njegove.' A onoga koji Allahu i Poslaniku Njegovu ne bude poslušan - sigurno čeka vatra džehennemska; u njoj će vječno, zauvijek ostati.", TextEnglish = "But [I have for you] only notification from Allāh, and His messages.' And whoever disobeys Allāh and His Messenger - then indeed, for him is the fire of Hell; they will abide therein forever.", TextGerman = "(Ich vermag) nur etwas zu übermitteln von Allah und Seinen Botschaften. Und wer sich Allah und Seinem Gesandten widersetzt, gewiß, für ihn wird es das Feuer der Hölle geben, ewig und auf immer darin zu bleiben." });
                        surah72.VerseList.Add(new Verse { Number = 24, TextBosnian = "I kada oni vide ono čime im se prijeti, saznat će ko je slabiji i malobrojniji.", TextEnglish = "[The disbelievers continue] until, when they see that which they are promised, then they will know who is weaker in helpers and less in number.", TextGerman = "Wenn sie dann sehen, was ihnen versprochen worden ist, da werden sie wissen, wer die schwächeren Helfer und die geringere Zahl hat." });
                        surah72.VerseList.Add(new Verse { Number = 25, TextBosnian = "Reci: 'Ja ne znam da li će uskoro biti ono čime vam se prijeti, ili je Gospodar moj odredio da će to poslije dugo vremena biti.", TextEnglish = "Say, 'I do not know if what you are promised is near or if my Lord will grant for it a [long] period.'", TextGerman = "Sag: Ich weiß nicht, ob das, was euch versprochen worden ist, nahe ist, oder ob mein Herr ihm eine (längere) Frist setzt," });
                        surah72.VerseList.Add(new Verse { Number = 26, TextBosnian = "Poznavatelj je On onoga što je čulima nedokučivo, i On to ne otkriva nikome,", TextEnglish = "[He is] Knower of the unseen, and He does not disclose His [knowledge of the] unseen to anyone", TextGerman = "(Er,) der Kenner des Verborgenen - Er enthüllt niemandem das, was bei Ihm verborgen ist," });
                        surah72.VerseList.Add(new Verse { Number = 27, TextBosnian = "osim onome za koga je zadovoljan da poslanik bude; On, zaista, i ispred njega i iza njega postavlja one koji će ga čuvati,", TextEnglish = "Except whom He has approved of messengers, and indeed, He sends before him [i.e., each messenger] and behind him observers[1775]", TextGerman = "außer dem Gesandten, den Er bewilligt; da läßt Er vor ihm und hinter ihm Wächter einhergehen," });
                        surah72.VerseList.Add(new Verse { Number = 28, TextBosnian = "da bi znao da su poslanice Gospodara svoga dostavili; On je obujmio ono što je u njih i baš sve zbrojio.'", TextEnglish = "That he [i.e., Muḥammad (ﷺ)] may know[1776] that they have conveyed the messages of their Lord; and He has encompassed whatever is with them and has enumerated all things in number.", TextGerman = "damit er weiß, ob sie wohl die Botschaften ihres Herrn ausgerichtet haben, und daß Er das, was bei ihnen ist, umfaßt, und Er die Zahl von allem erfaßt." });
                        result = surah72;
                        break;
                    #endregion

