                    case 73:
                        Surah surah73 = new Surah(); surah73.Name = Constants.Surah73Name; surah73.Number = 73; surah73.VerseList = new List<Verse>();
                        surah73.VerseList.Add(new Verse { Number = 0, TextBosnian = "", TextEnglish = "", TextGerman = "0" });
                        surah73.VerseList.Add(new Verse { Number = 1, TextBosnian = "O ti umotani!", TextEnglish = "O you who wraps himself [in clothing][1778]", TextGerman = "O du Eingehüllter," });
                        surah73.VerseList.Add(new Verse { Number = 2, TextBosnian = "Probdij noć, osim malog dijela;", TextEnglish = "Arise [to pray] the night, except for a little -", TextGerman = "steh (zum Gebet) die (ganze) Nacht auf- bis auf einen kleinen Teil," });
                        surah73.VerseList.Add(new Verse { Number = 3, TextBosnian = "polovinu njezinu i malo manje od nje;", TextEnglish = "Half of it - or subtract from it a little", TextGerman = "ihre Hälfte, oder verringere sie um einen kleinen Teil" });
                        surah73.VerseList.Add(new Verse { Number = 4, TextBosnian = "ili malo više od nje, i uči Kur'an polagahno.", TextEnglish = "Or add to it, and recite the Qur’ān with measured recitation.", TextGerman = "oder füge etwas hinzu. Und trage den Qur’an wohlgeordnet vor." });
                        surah73.VerseList.Add(new Verse { Number = 5, TextBosnian = "Mi ćemo ti, doista, teške riječi slati.", TextEnglish = "Indeed, We will cast upon you a heavy word.[1779]", TextGerman = "Gewiß, Wir werden dir gewichtige Worte offenbaren." });
                        surah73.VerseList.Add(new Verse { Number = 6, TextBosnian = "Ustajanje noću, zaista, jače djeluje i izgovor je razboritiji,", TextEnglish = "Indeed, the hours of the night are more effective for concurrence [of heart and tongue][1780] and more suitable for words.[1781]", TextGerman = "Das Aufstehen in der Nacht hat stärkeren Einfluß (auf die Seele) und bringt eher aufklärende Worte." });
                        surah73.VerseList.Add(new Verse { Number = 7, TextBosnian = "a ti danju, doista, imaš vremena mnogo.", TextEnglish = "Indeed, for you by day is prolonged occupation.", TextGerman = "Du hast ja am Tage lange Zeit für Beschäftigung." });
                        surah73.VerseList.Add(new Verse { Number = 8, TextBosnian = "I spominji ime Gospodara svoga i Njemu u potpunosti odan budi,", TextEnglish = "And remember the name of your Lord and devote yourself to Him with [complete] devotion.", TextGerman = "Und gedenke des Namens deines Herrn und widme dich Ihm ganz allein." });
                        surah73.VerseList.Add(new Verse { Number = 9, TextBosnian = "On je Gospodar istoka i zapada, nema boga osim Njega, Njega uzmi za zaštitnika!", TextEnglish = "[He is] the Lord of the East and the West; there is no deity except Him, so take Him as Disposer of [your] affairs.[1782]", TextGerman = "(Er ist) der Herr des Ostens und des Westens. Es gibt keinen Gott außer Ihm. So nimm Ihn dir zum Sachwalter." });
                        surah73.VerseList.Add(new Verse { Number = 10, TextBosnian = "I otrpi ono što oni govore i izbjegavaj ih na lijep način,", TextEnglish = "And be patient over what they say and avoid them with gracious avoidance.", TextGerman = "Und ertrage standhaft, was sie sagen, und meide sie auf schöne Weise." });
                        surah73.VerseList.Add(new Verse { Number = 11, TextBosnian = "a prepusti Meni one koji te u laž ugone, koji raskošno žive, i daj im malo vremena;", TextEnglish = "And leave Me with [the matter of] the deniers, those of ease [in life], and allow them respite a little.", TextGerman = "Und lasse Mich (allein) mit den Leugnern (der Botschaft), die ein angenehmes Leben haben, und lasse ihnen noch kurze Zeit." });
                        surah73.VerseList.Add(new Verse { Number = 12, TextBosnian = "bit će u Nas, doista, okova i ognja,", TextEnglish = "Indeed, with Us [for them] are shackles and burning fire.", TextGerman = "Gewiß, bei Uns gibt es schwere Fesseln und einen Höllenbrand" });
                        surah73.VerseList.Add(new Verse { Number = 13, TextBosnian = "i jela koje u grlu zastaje, i patnje nesnosne,", TextEnglish = "And food that chokes and a painful punishment -", TextGerman = "und Speise, die im Hals Würgen hervorruft, und schmerzhafte Strafe" });
                        surah73.VerseList.Add(new Verse { Number = 14, TextBosnian = "na Dan kada se Zemlja i planine zatresu i planine mehke pješčane dine postanu.", TextEnglish = "On the Day the earth and the mountains will convulse and the mountains will become a heap of sand pouring down.", TextGerman = "am Tag, da die Erde und die Berge zittern und die Berge ein zerrinnender Sandhügel sein werden." });
                        surah73.VerseList.Add(new Verse { Number = 15, TextBosnian = "Mi smo vam zaista poslali Poslanika da bi svjedočio protiv vas, isto onako kao što smo i faraonu poslanika poslali,", TextEnglish = "Indeed, We have sent to you a Messenger as a witness upon you just as We sent to Pharaoh a messenger.", TextGerman = "Wir haben zu euch ja einen Gesandten als Zeugen über euch gesandt, so wie Wir zu Fir’aun einen Gesandten sandten." });
                        surah73.VerseList.Add(new Verse { Number = 16, TextBosnian = "ali faraon nije poslušao poslanika, pa smo ga teškom kaznom kaznili.", TextEnglish = "But Pharaoh disobeyed the messenger, so We seized him with a ruinous seizure.", TextGerman = "Es widersetzte sich Fir’aun dem Gesandten. Da ergriffen Wir ihn mit hartem Griff." });
                        surah73.VerseList.Add(new Verse { Number = 17, TextBosnian = "Kako ćete se, ako ostanete nevjernici, sačuvati Dana koji će djecu sijedom učiniti?!", TextEnglish = "Then how can you fear, if you disbelieve, a Day that will make the children white-haired?[1783]", TextGerman = "Wie wollt ihr euch denn, wenn ihr ungläubig seid, vor einem Tag bewahren, der die Kinder weißhaarig (wie Greise) macht?" });
                        surah73.VerseList.Add(new Verse { Number = 18, TextBosnian = "Nebo će se tada rascijepiti, i obećanje Njegovo bit će ispunjeno.", TextEnglish = "The heaven will break apart therefrom;[1784] ever is His promise fulfilled.", TextGerman = "Der Himmel wird an ihm zerbrechen. Sein Versprechen wird ausgeführt." });
                        surah73.VerseList.Add(new Verse { Number = 19, TextBosnian = "Ovo je, doista, pouka; i ko hoće, put ka Gospodaru svome prihvatit će!", TextEnglish = "Indeed, this is a reminder, so whoever wills may take to his Lord a way.", TextGerman = "Gewiß, dies ist eine Erinnerung; wer nun will, (der) nimmt (so diesen) einen Weg zu seinem Herrn." });
                        surah73.VerseList.Add(new Verse { Number = 20, TextBosnian = "Gospodar tvoj sigurno zna da ti klanjajući provodiš manje od dvije trećine noći, polovinu njezinu, ili trećinu njezinu, a i skupina od onih koji su uz tebe. Allah određuje dužinu noći i dana, On zna da vi to nećete moći izdržati pa vam prašta, a vi iz Kur'ana učite ono što je lahko, On zna da će među vama biti bolesnih, i onih koji će po svijetu putovati, i Allahove blagodati tražiti, i onih koji će se na Allahovu putu boriti. Pa učite iz njega ono što je lahko, i namaz obavljajte, i zekat dajite, i Allahu lijep zajam dajte! A dobro koje za sebe unaprijed date naći ćete kod Allaha da je još bolje i s nagradom većom. I molite Allaha da vam oprosti. Allah je, uistinu, Onaj Koji oprašta grijehe i milostiv je.", TextEnglish = "Indeed, your Lord knows, [O Muḥammad], that you stand [in prayer] almost two thirds of the night or half of it or a third of it, and [so do] a group of those with you. And Allāh determines [the extent of] the night and the day. He has known that you [Muslims] will not be able to do it[1785] and has turned to you in forgiveness, so recite what is easy [for you] of the Qur’ān. He has known that there will be among you those who are ill and others traveling throughout the land seeking [something] of the bounty of Allāh and others fighting for the cause of Allāh. So recite what is easy from it and establish prayer and give zakāh and loan Allāh a goodly loan.[1786] And whatever good you put forward for yourselves - you will find it with Allāh. It is better and greater in reward. And seek forgiveness of Allāh. Indeed, Allāh is Forgiving and Merciful.", TextGerman = "Gewiß, dein Herr weiß, daß du etwas weniger als zwei Drittel der Nacht (zum Gebet) aufstehst, oder die Hälfte oder ein Drittel davon, und (ebenso) ein Teil von denjenigen, die mit dir sind. Und Allah setzt das Maß der Nacht und des Tages fest. Er weiß, daß ihr es nicht erfassen würdet. Da wandte Er Sich euch zu und erließ es euch. So lest (bei Nacht), was euch vom Qur’an leichtfällt. Er weiß, daß es unter euch Kranke geben wird und andere, die im Land umherreisen, wo sie nach (etwas) von Allahs Huld trachten, und (wieder) andere, die auf Allahs Weg kämpfen. So lest davon, was euch leichtfällt, und verrichtet das Gebet und entrichtet die Abgabe und gebt Allah ein gutes Darlehen. Und was ihr für euch selbst an Gutem vorausschickt, das werdet ihr noch besser und großartiger belohnt bei Allah finden. Und bittet Allah um Vergebung. Gewiß, Allah ist Allvergebend und Barmherzig." });
                        result = surah73;
                        break;
                    #endregion

