                        surah74.VerseList.Add(new Verse { Number = 1, TextBosnian = "O ti pokriveni!", TextEnglish = "O you who covers himself [with a garment],[1788]", TextGerman = "O du Zugedeckter," });
                        surah74.VerseList.Add(new Verse { Number = 2, TextBosnian = "Ustani i upozoravaj,", TextEnglish = "Arise and warn.", TextGerman = "stehe auf und warne;" });
                        surah74.VerseList.Add(new Verse { Number = 3, TextBosnian = "i Gospodara svoga veličaj!", TextEnglish = "And your Lord glorify.", TextGerman = "und deinen Herrn, Den preise als den Größten," });
                        surah74.VerseList.Add(new Verse { Number = 4, TextBosnian = "I haljine svoje očisti!", TextEnglish = "And your clothing purify.", TextGerman = "und deine Gewänder, die reinige," });
                        surah74.VerseList.Add(new Verse { Number = 5, TextBosnian = "I kumira se kloni!", TextEnglish = "And uncleanliness[1789] avoid.", TextGerman = "und die (Unreinheit des) Götzen(dienstes), die meide," });
                        surah74.VerseList.Add(new Verse { Number = 6, TextBosnian = "I ne daruj nešto nadajući se da će ti biti uzvraćeno više!", TextEnglish = "And do not confer favor to acquire more[1790].", TextGerman = "und halte nicht (deinen Verdienst) vor, und halte ihn nicht für zu groß," });
                        surah74.VerseList.Add(new Verse { Number = 7, TextBosnian = "I radi Gospodara svoga trpi!", TextEnglish = "But for your Lord be patient.", TextGerman = "und ertrage standhaft (alles) für deinen Herrn." });
                        surah74.VerseList.Add(new Verse { Number = 8, TextBosnian = "A kada u rog bude puhnuto,", TextEnglish = "And when the trumpet is blown,", TextGerman = "Wenn dann ins Horn gestoßen wird," });
                        surah74.VerseList.Add(new Verse { Number = 9, TextBosnian = "to će tada biti naporan dan,", TextEnglish = "That Day will be a difficult day", TextGerman = "so wird jener Tag ein schwerer Tag sein" });
                        surah74.VerseList.Add(new Verse { Number = 10, TextBosnian = "nevjernicima on neće biti lahak.", TextEnglish = "For the disbelievers - not easy.", TextGerman = "für die Ungläubigen nicht leicht." });
                        surah74.VerseList.Add(new Verse { Number = 11, TextBosnian = "Ostavi Mene i onoga koga sam Ja samog stvorio", TextEnglish = "Leave Me with the one I created alone[1791]", TextGerman = "Lasse Mich (allein) mit wem Ich allein erschaffen habe," });
                        surah74.VerseList.Add(new Verse { Number = 12, TextBosnian = "i imetak mu ogroman dao,", TextEnglish = "And to whom I granted extensive wealth", TextGerman = "und dem Ich ausgedehnten Besitz gegeben habe" });
                        surah74.VerseList.Add(new Verse { Number = 13, TextBosnian = "i sinove koji su s njim,", TextEnglish = "And children present [with him]", TextGerman = "und Söhne (als ständige Begleiter) anwesend" });
                        surah74.VerseList.Add(new Verse { Number = 14, TextBosnian = "i život mu ugodnim učinio,", TextEnglish = "And spread [everything] before him, easing [his life].", TextGerman = "und dem Ich alles schön zurechtgemacht habe;" });
                        surah74.VerseList.Add(new Verse { Number = 15, TextBosnian = "i žudi da mu još uvećam!", TextEnglish = "Then he desires that I should add more.", TextGerman = "doch begehrt er hierauf, daß Ich (ihm) noch mehr gebe." });
                        surah74.VerseList.Add(new Verse { Number = 16, TextBosnian = "Nikako! On, doista, prkosi ajetima Našim", TextEnglish = "No! Indeed, he has been toward Our verses obstinate.", TextGerman = "Keineswegs! Er ist gegen Unsere Zeichen wiederholt widerspenstig gewesen." });
