                        surah74.VerseList.Add(new Verse { Number = 20, TextBosnian = "i još jednom, proklet bio, kako je proračunao!", TextEnglish = "Then may he be destroyed [for] how he deliberated.", TextGerman = "Abermals: Tod ihm, wie er abgewogen hat!" });
