                        surah74.VerseList.Add(new Verse { Number = 22, TextBosnian = "pa se onda smrknuo i namrštio,", TextEnglish = "Then he frowned and scowled;", TextGerman = "hierauf hat er düster geblickt und ein finsteres Gesicht gemacht," });
