                        surah74.VerseList.Add(new Verse { Number = 24, TextBosnian = "i rekao: 'Ovo nije ništa drugo do vradžbina koja se nasljeđuje,", TextEnglish = "And said, 'This is not but magic imitated [from others].", TextGerman = "Er sagt: Das ist nur Zauberei, die überliefert wird." });
                        surah74.VerseList.Add(new Verse { Number = 25, TextBosnian = "ovo su samo čovjekove riječi!'", TextEnglish = "This is not but the word of a human being.'", TextGerman = "Das sind nur die Worte von Menschenwesen." });
                        surah74.VerseList.Add(new Verse { Number = 26, TextBosnian = "U Sekar ću ja njega uroniti.", TextEnglish = "I will drive him into Saqar.[1793]", TextGerman = "Ich werde ihn der Saqar aussetzen." });
                        surah74.VerseList.Add(new Verse { Number = 27, TextBosnian = "A znaš li ti šta je Sekar?!", TextEnglish = "And what can make you know what is Saqar?", TextGerman = "Und was läßt dich wissen, was Saqar ist?" });
                        surah74.VerseList.Add(new Verse { Number = 28, TextBosnian = "Ništa on neće poštedjeti,", TextEnglish = "It lets nothing remain and leaves nothing [unburned],", TextGerman = "Sie läßt nichts übrig und läßt nichts bestehen." });
                        surah74.VerseList.Add(new Verse { Number = 29, TextBosnian = "kože će pržiti, pa crnim učiniti,", TextEnglish = "Altering [i.e., blackening] the skins.", TextGerman = "Sie verändert die Menschen ganz." });
                        surah74.VerseList.Add(new Verse { Number = 30, TextBosnian = "nad njim su devetnaesterica.", TextEnglish = "Over it are nineteen [angels].", TextGerman = "Über ihr gibt es neunzehn (Wächter)." });
                        surah74.VerseList.Add(new Verse { Number = 31, TextBosnian = "Mi smo čuvarima Vatre samo meleke postavili i odredili njihov broj kao iskušenje onima koji ne vjeruju - da se oni kojima je Knjiga data uvjere, i da se onima koji vjeruju vjerovanje poveća, i da oni kojima je Knjiga data i oni koji su vjernici ne sumnjaju, i da oni čija su srca bolesna i oni koji su nevjernici kažu: 'Šta je Allah htio ovim primjerom?' Tako Allah kome hoće daje da je u zabludi, i na Pravi put upućuje koga hoće. A vojske Gospodara tvoga samo On zna. I ona- Vatra ljudima je samo opomena.", TextEnglish = "And We have not made the keepers of the Fire except angels. And We have not made their number except as a trial for those who disbelieve - that those who were given the Scripture will be convinced and those who have believed will increase in faith and those who were given the Scripture and the believers will not doubt and that those in whose hearts is disease [i.e., hypocrisy] and the disbelievers will say, 'What does Allāh intend by this as an example?' Thus does Allāh send astray whom He wills and guide whom He wills. And none knows the soldiers of your Lord except Him. And it [i.e., mention of the Fire] is not but a reminder to humanity.", TextGerman = "Wir haben als Wächter des (Höllen)feuers nur Engel eingesetzt, und Wir haben ihre Zahl nur zu einer Versuchung gemacht für diejenigen, die ungläubig sind, damit diejenigen Überzeugung gewinnen, denen die Schrift gegeben wurde, und damit diejenigen, die glauben, an Glauben zunehmen, und damit diejenigen, denen die Schrift gegeben wurde, und (auch) die Gläubigen nicht zweifeln und damit diejenigen, in deren Herzen Krankheit ist, und (auch) die Ungläubigen sagen: Was will denn Allah damit als Gleichnis? So läßt Allah in die Irre gehen, wen Er will, und leitet recht, wen Er will. Aber niemand weiß über die Heerscharen deines Herrn Bescheid außer Ihm. Und es ist nur eine Ermahnung für die Menschenwesen." });
