                        surah74.VerseList.Add(new Verse { Number = 45, TextBosnian = "I u besposlice smo se sa besposlenjacima upuštali,", TextEnglish = "And we used to enter into vain discourse with those who engaged [in it],", TextGerman = "und wir pflegten auf schweifende Reden mit denjenigen einzugehen, die solche führten," });
                        surah74.VerseList.Add(new Verse { Number = 46, TextBosnian = "i Sudnji dan smo poricali,", TextEnglish = "And we used to deny the Day of Recompense", TextGerman = "und wir erklärten stets den Tag des Gerichts für Lüge" });
