                        surah74.VerseList.Add(new Verse { Number = 49, TextBosnian = "Pa zašto se oni okreću od opomene?", TextEnglish = "Then what is [the matter] with them that they are, from the reminder, turning away.", TextGerman = "Was ist denn mit ihnen, daß sie sich von der Ermahnung abwenden," });
                        surah74.VerseList.Add(new Verse { Number = 50, TextBosnian = "Kao da su divlji magarci preplašeni", TextEnglish = "As if they were alarmed donkeys.", TextGerman = "als wären sie aufgeschreckte Wildesel," });
                        surah74.VerseList.Add(new Verse { Number = 51, TextBosnian = "pobjegli od lavova!", TextEnglish = "Fleeing from a lion?", TextGerman = "die vor einem Löwen fliehen?" });
