                        surah74.VerseList.Add(new Verse { Number = 53, TextBosnian = "Nikada, jer oni se ahireta ne plaše!", TextEnglish = "No! But they do not fear the Hereafter.", TextGerman = "Keineswegs! Vielmehr fürchten sie nicht das Jenseits." });
                        surah74.VerseList.Add(new Verse { Number = 54, TextBosnian = "Uistinu! Kur'an je opomena,", TextEnglish = "No! Indeed, it [i.e., the Qur’ān] is a reminder.", TextGerman = "Keineswegs! Er ist ja eine Erinnerung." });
                        surah74.VerseList.Add(new Verse { Number = 55, TextBosnian = "i ko hoće, na umu će ga imati,", TextEnglish = "Then whoever wills will remember it.", TextGerman = "Wer nun will, gedenkt seiner." });
                        surah74.VerseList.Add(new Verse { Number = 56, TextBosnian = "a na umu će ga imati samo ako Allah bude htio, On je jedini dostojan da Ga se boje i dostojan je da oprašta.", TextEnglish = "And they will not remember except that Allāh wills. He is worthy of fear and adequate for [granting] forgiveness.", TextGerman = "Sie werden aber (seiner) nicht gedenken, außer daß Allah es will. Ihm gebührt die Gottesfurcht und Ihm gebührt die Vergebung." });
