                        surah75.VerseList.Add(new Verse { Number = 1, TextBosnian = "Kunem se Kijametskim danom", TextEnglish = "I swear by the Day of Resurrection", TextGerman = "Nein! Ich schwöre beim Tag der Auferstehung." });
