                        surah75.VerseList.Add(new Verse { Number = 13, TextBosnian = "taj dan čovjek će o onome što je pripremio, a što je propustio, obaviješten biti.", TextEnglish = "Man will be informed that Day of what he sent ahead[1802] and kept back.[1803]", TextGerman = "Dem Menschen wird an jenem Tag kundgetan, was er vorausgeschickt und zurückgestellt hat." });
                        surah75.VerseList.Add(new Verse { Number = 14, TextBosnian = "Štaviše, čovjek sebe dobro zna - sam će protiv sebe svjedok biti,", TextEnglish = "Rather, man, against himself, will be a witness,[1804]", TextGerman = "Aber nein! Der Mensch wird bezüglich seiner selbst durchblicken," });
                        surah75.VerseList.Add(new Verse { Number = 15, TextBosnian = "makar iznosio svoja opravdanja.", TextEnglish = "Even if he presents his excuses.", TextGerman = "auch wenn er seine Entschuldigungen vorbrächte ." });
                        surah75.VerseList.Add(new Verse { Number = 16, TextBosnian = "Ne izgovaraj Kur'an jezikom svojim da bi ga što prije zapamtio,", TextEnglish = "Move not your tongue with it, [O Muḥammad], to hasten with it [i.e., recitation of the Qur’ān].", TextGerman = "Bewege deine Zunge nicht damit, um ihn übereilt weiterzugeben." });
