                        surah75.VerseList.Add(new Verse { Number = 24, TextBosnian = "Taj dan neka lica smrknuta će biti,", TextEnglish = "And [some] faces, that Day, will be contorted,", TextGerman = "Und (die anderen) Gesichter werden an jenem Tag finster sein;" });
                        surah75.VerseList.Add(new Verse { Number = 25, TextBosnian = "znat će da će ih snaći velika nesreća!", TextEnglish = "Expecting that there will be done to them [something] backbreaking.", TextGerman = "die meinen, es würde ihnen das Rückgrat gebrochen." });
                        surah75.VerseList.Add(new Verse { Number = 26, TextBosnian = "Pazi, kada duša dopre do ključnih kostiju", TextEnglish = "No! When it [i.e., the soul] has reached the collar bones[1806]", TextGerman = "Keineswegs! Wenn sie das Schlüsselbein erreicht" });
