                        surah75.VerseList.Add(new Verse { Number = 36, TextBosnian = "Zar čovjek misli da će zaludu ostavljen biti?!", TextEnglish = "Does man think that he will be left neglected?[1809]", TextGerman = "Meint der Mensch (etwa), daß er außer acht gelassen wird?" });
