                        surah76.VerseList.Add(new Verse { Number = 1, TextBosnian = "Ne prođe li čovjeku dio vremena u kojem nije bio spomena vrijedan?!", TextEnglish = "Has there [not] come upon man a period of time when he was not a thing [even] mentioned?", TextGerman = "Ist (nicht) über den Menschen eine Spanne der endlosen Zeit gekommen, in der er nichts Nennenswertes ist?" });
                        surah76.VerseList.Add(new Verse { Number = 2, TextBosnian = "Mi čovjeka od smjese sjemena stvaramo da bismo ga na kušnju stavili i činimo da on čuje i vidi.", TextEnglish = "Indeed, We created man from a sperm-drop mixture[1811] that We may try him; and We made him hearing and seeing.", TextGerman = "Wir haben den Menschen ja aus einem Samentropfen, einem Gemisch erschaffen, (um) ihn zu prüfen. Und so haben Wir ihn mit Gehör und Augenlicht versehen." });
                        surah76.VerseList.Add(new Verse { Number = 3, TextBosnian = "Mi mu na Pravi put ukazujemo, pa ili je zahvalan ili nezahvalan.", TextEnglish = "Indeed, We guided him to the way, be he grateful or be he ungrateful.", TextGerman = "Wir haben ihn ja den (rechten) Weg geleitet, ob er nun dankbar oder undankbar sein mag." });
                        surah76.VerseList.Add(new Verse { Number = 4, TextBosnian = "Mi smo za nevjernike okove i sindžire i oganj razbuktali pripremili.", TextEnglish = "Indeed, We have prepared for the disbelievers chains and shackles and a blaze.", TextGerman = "Wir haben ja für die Ungläubigen Ketten und Fesseln und eine Feuerglut bereitet." });
                        surah76.VerseList.Add(new Verse { Number = 5, TextBosnian = "Čestiti će iz pehara piće kamforom začinjeno piti,", TextEnglish = "Indeed, the righteous will drink from a cup [of wine] whose mixture is of Kāfūr,[1812]", TextGerman = "Gewiß, die Frommen trinken aus einem Becher, dessen Beimischung Kampfer ist," });
                        surah76.VerseList.Add(new Verse { Number = 6, TextBosnian = "sa izvora iz kog će samo Allahovi robovi piti, i koji će kuda hoće bez muke razvoditi.", TextEnglish = "A spring of which the [righteous] servants of Allāh will drink; they will make it gush forth in force [and abundance].", TextGerman = "aus einer Quelle, aus der Allahs Diener trinken, die sie sprudelnd hervorströmen lassen." });
                        surah76.VerseList.Add(new Verse { Number = 7, TextBosnian = "Oni su zavjet ispunili i plašili se dana čija će kob svuda prisutna biti,", TextEnglish = "They [are those who] fulfill [their] vows and fear a Day whose evil will be widespread.", TextGerman = "Sie erfüllen das Gelübde und fürchten einen Tag, dessen Übel sich wie im Flug ausbreitet," });
                        surah76.VerseList.Add(new Verse { Number = 8, TextBosnian = "i hranu su davali, mada su je i sami voljeli, siromahu, siročetu i sužnju.", TextEnglish = "And they give food in spite of love for it[1813] to the needy, the orphan, and the captive,", TextGerman = "und sie geben - obwohl man sie liebt - Speise zu essen einem Armen, einer Waisen und einem Gefangenen:" });
                        surah76.VerseList.Add(new Verse { Number = 9, TextBosnian = "Mi vas samo zarad Allahova lica hranimo, od vas ni priznanja ni zahvalnosti ne tražimo!", TextEnglish = "[Saying], 'We feed you only for the face [i.e., approval] of Allāh. We wish not from you reward or gratitude.", TextGerman = "Wir speisen euch nur um Allahs Angesicht willen. Wir wollen von euch weder Belohnung noch Dank." });
                        surah76.VerseList.Add(new Verse { Number = 10, TextBosnian = "Mi se Gospodara našeg bojimo, na Dan kada će lica smrknuta i namrgođena biti.'", TextEnglish = "Indeed, We fear from our Lord a Day austere and distressful.'", TextGerman = "Wir fürchten ja von unserem Herrn einen Tag, der düster blicken läßt, einen unheilvollen." });
                        surah76.VerseList.Add(new Verse { Number = 11, TextBosnian = "Njih će Allah strahote taj dan sačuvati i blistavost i radost im darovati,", TextEnglish = "So Allāh will protect them from the evil of that Day and give them radiance and happiness", TextGerman = "So bewahrt sie Allah vor dem Übel jenes Tages und wird ihnen strahlendes Glück und Freude darbieten" });
                        surah76.VerseList.Add(new Verse { Number = 12, TextBosnian = "i Džennetom i svilom ih za ono što su trpjeli nagraditi.", TextEnglish = "And will reward them for what they patiently endured [with] a garden [in Paradise] and silk [garments].", TextGerman = "und ihnen vergelten, daß sie standhaft gewesen sind, mit einem Garten und mit Seide," });
                        surah76.VerseList.Add(new Verse { Number = 13, TextBosnian = "Naslonjeni na divane, oni u njemu ni Sunce ni veliku hladnoću neće osjetiti,", TextEnglish = "[They will be] reclining therein on adorned couches. They will not see therein any [burning] sun or [freezing] cold.", TextGerman = "worin sie sich auf überdachten Liegen lehnen, und worin sie weder (heiße) Sonne noch bittere Kälte wahrnehmen werden." });
                        surah76.VerseList.Add(new Verse { Number = 14, TextBosnian = "u blizini će im hladovina njegova biti, a plodovi njegovi nadohvat ruke će im stajati.", TextEnglish = "And near above them are its shades, and its [fruit] to be picked will be lowered in compliance.", TextGerman = "Nahe über ihnen sind seine Schatten, und seine Früchte sind ihnen (zum Pflücken) sehr gefügig gemacht." });
                        surah76.VerseList.Add(new Verse { Number = 15, TextBosnian = "Bit će usluživani iz srebrenih posuda i čaša koje će prozirne biti,", TextEnglish = "And there will be circulated among them vessels of silver and cups having been [created] clear [as glass],", TextGerman = "Herumgereicht werden ihnen Gefäße aus Silber und Trinkschalen, die (wie) aus Glas sind," });
                        surah76.VerseList.Add(new Verse { Number = 16, TextBosnian = "od srebra prozirnog napravljene, čiju će veličinu prema željama njihovim odrediti.", TextEnglish = "Clear glasses [made] from silver of which they have determined the measure.", TextGerman = "Gläser aus Silber, die sie im rechten Maß bemessen." });
                        surah76.VerseList.Add(new Verse { Number = 17, TextBosnian = "U njemu će se iz čaše piće đumbirom začinjeno piti,", TextEnglish = "And they will be given to drink a cup [of wine] whose mixture is of ginger", TextGerman = "Und zu trinken gegeben wird ihnen darin ein Becher, dessen Beimischung Ingwer ist," });
                        surah76.VerseList.Add(new Verse { Number = 18, TextBosnian = "sa izvora u Džennetu, koji se Selsebil zove.", TextEnglish = "[From] a fountain within it [i.e., Paradise] named Salsabeel.", TextGerman = "aus einer Quelle darin, die Salsabil genannt wird." });
                        surah76.VerseList.Add(new Verse { Number = 19, TextBosnian = "Služit će ih vječno mlada posluga; da ih vidiš, pomislio bi da su biser prosuti.", TextEnglish = "There will circulate among them young boys made eternal. When you see them, you would think them [as beautiful as] scattered pearls.", TextGerman = "Und unter ihnen gehen ewig junge Knaben umher. Wenn du sie siehst, hältst du sie für ausgestreute Perlen." });
                        surah76.VerseList.Add(new Verse { Number = 20, TextBosnian = "I kud god pogledaš, vidjet ćeš blagostanje i carstvo prostrano.", TextEnglish = "And when you look there [in Paradise], you will see pleasure and great dominion.", TextGerman = "Und wenn du dort hinsiehst, wirst du Wonne und ein großes Reich sehen." });
                        surah76.VerseList.Add(new Verse { Number = 21, TextBosnian = "Na njima bit će odijela od tanke zelene svile i od brokata, nakićeni narukvicama od srebra, i dat će im Gospodar njihov da piju čisto piće.", TextEnglish = "Upon them [i.e., the inhabitants] will be green garments of fine silk and brocade. And they will be adorned with bracelets of silver, and their Lord will give them a purifying drink.", TextGerman = "Auf ihnen werden grüne Gewänder aus Seidenbrokat sein und schwerer Brokat, und geschmückt werden sie sein mit Armreifen aus Silber. Und zu trinken wird ihr Herr ihnen ein reines Getränk geben." });
                        surah76.VerseList.Add(new Verse { Number = 22, TextBosnian = "To vam je nagrada; vaš trud je dostojan hvale bio!'", TextEnglish = "[And it will be said], 'Indeed, this is for you a reward, and your effort has been appreciated.'", TextGerman = "Dies ist ja der Lohn für euch, und euer Bemühen wird gedankt sein." });
                        surah76.VerseList.Add(new Verse { Number = 23, TextBosnian = "Od vremena do vremena Mi objavljujemo Kur'an tebi,", TextEnglish = "Indeed, it is We who have sent down to you, [O Muḥammad], the Qur’ān progressively.", TextGerman = "Gewiß, Wir sind es, die Wir dir den Qur’an offenbart, ja offenbart haben." });
                        surah76.VerseList.Add(new Verse { Number = 24, TextBosnian = "Zato, izdrži do odluke Gospodara tvoga, i ne slušaj ni grešnika ni nevjernika njihova!", TextEnglish = "So be patient for the decision of your Lord and do not obey from among them a sinner or ungrateful [disbeliever].", TextGerman = "So sei standhaft in Bezug auf das Urteil deines Herrn, und gehorche von ihnen keinem Sündhaften oder sehr Undankbaren." });
                        surah76.VerseList.Add(new Verse { Number = 25, TextBosnian = "I spominji ime Gospodara svoga ujutro i predvečer,", TextEnglish = "And mention the name of your Lord [in prayer] morning and evening", TextGerman = "Und gedenke des Namens deines Herrn morgens und abends," });
                        surah76.VerseList.Add(new Verse { Number = 26, TextBosnian = "i u jednom dijelu noći Njemu na sedždu padaj i dugo Ga noću veličaj.", TextEnglish = "And during the night prostrate to Him and exalt [i.e., praise] Him a long [part of the] night.", TextGerman = "und in (einem Teil) der Nacht, da wirf dich vor Ihm nieder und preise Ihn lange Zeit zur Nacht." });
                        surah76.VerseList.Add(new Verse { Number = 27, TextBosnian = "A oni, oni život na prolaznom svijetu vole doista, a ništa ih se ne tiče Dan tegobni koji ih čeka.", TextEnglish = "Indeed, these [disbelievers] love the immediate and leave behind them[1814] a grave Day.", TextGerman = "Diese da lieben ja das schnell Eintreffende und lassen hinter sich einen schwer lastenden Tag." });
                        surah76.VerseList.Add(new Verse { Number = 28, TextBosnian = "Mi ih čvrstog tijela stvaramo i zglobove im povezujemo, a ako htjednemo, zamijenit ćemo ih njima sličnim.", TextEnglish = "We have created them and strengthened their forms, and when We will, We can change their likenesses with [complete] alteration.", TextGerman = "Wir doch haben sie erschaffen und ihren Körperbau vollendet. Und wenn Wir wollen, tauschen Wir sie durch andere ihresgleichen aus." });
                        surah76.VerseList.Add(new Verse { Number = 29, TextBosnian = "Ovo je opomena, pa ko hoće držat će se puta koji Gospodaru njegovu vodi,", TextEnglish = "Indeed, this is a reminder, so he who wills may take to his Lord a way.", TextGerman = "Gewiß, dies ist eine Erinnerung; wer nun will, nimmt (so diesen) einen Weg zu seinem Herrn." });
                        surah76.VerseList.Add(new Verse { Number = 30, TextBosnian = "a vi ćete htjeti samo ono što Allah hoće. Allah je, uistinu, Onaj Koji sve zna i mudar je.", TextEnglish = "And you do not will except that Allāh wills. Indeed, Allāh is ever Knowing and Wise.", TextGerman = "Und ihr könnt nicht(s) wollen, außer daß Allah (es) will. Gewiß, Allah ist All-wissend und Allweise." });
                        surah76.VerseList.Add(new Verse { Number = 31, TextBosnian = "On koga hoće uvodi u milost Svoju, a zulumćarima je pripremio veliku patnju.", TextEnglish = "He admits whom He wills into His mercy; but the wrongdoers - He has prepared for them a painful punishment.", TextGerman = "Er läßt, wen Er will, in Seine Barmherzigkeit eingehen. Und die Ungerechten, für sie hat Er schmerzhafte Strafe bereitet." });
                        result = surah76;
                        break;
