                        surah77.VerseList.Add(new Verse { Number = 4, TextBosnian = "i onih što razdvajaju", TextEnglish = "And those [angels] who bring criterion[1816]", TextGerman = "den klar Trennenden," });
                        surah77.VerseList.Add(new Verse { Number = 5, TextBosnian = "i onih što Opomenu dostavljaju,", TextEnglish = "And those [angels] who deliver a message.", TextGerman = "den Ermahnung Überbringenden" });
                        surah77.VerseList.Add(new Verse { Number = 6, TextBosnian = "kao opravdanje ili upozorenje,", TextEnglish = "As justification or warning,", TextGerman = "zur Pflichterfüllung oder zur Warnung!" });
                        surah77.VerseList.Add(new Verse { Number = 7, TextBosnian = "sigurno će biti ono čime vam se prijeti!", TextEnglish = "Indeed, what you are promised is to occur.", TextGerman = "Gewiß, was euch versprochen wird, wird sicher hereinbrechen." });
                        surah77.VerseList.Add(new Verse { Number = 8, TextBosnian = "Kada zvijezde sjaj izgube,", TextEnglish = "So when the stars are obliterated", TextGerman = "Wenn dann die Sterne ausgelöscht werden" });
                        surah77.VerseList.Add(new Verse { Number = 9, TextBosnian = "i kada se nebo otvori,", TextEnglish = "And when the heaven is opened", TextGerman = "und wenn der Himmel gespalten wird" });
                        surah77.VerseList.Add(new Verse { Number = 10, TextBosnian = "i kada se planine u prah zdrobe,", TextEnglish = "And when the mountains are blown away", TextGerman = "und wenn die Berge zersprengt werden" });
                        surah77.VerseList.Add(new Verse { Number = 11, TextBosnian = "i kada se poslanici u određeno vrijeme okupe.", TextEnglish = "And when the messengers' time has come...[1817]", TextGerman = "und wenn für die Gesandten ihre Zeit gesetzt wird." });
                        surah77.VerseList.Add(new Verse { Number = 12, TextBosnian = "Do kojeg je to dana odloženo?", TextEnglish = "For what Day was it postponed?[1818]", TextGerman = "Auf weichen Tag ist ihre Frist festgelegt worden?" });
                        surah77.VerseList.Add(new Verse { Number = 13, TextBosnian = "Do Dana sudnjeg!", TextEnglish = "For the Day of Judgement.", TextGerman = "Auf den Tag der Entscheidung." });
                        surah77.VerseList.Add(new Verse { Number = 14, TextBosnian = "A znaš li ti šta je Dan sudnji?", TextEnglish = "And what can make you know what is the Day of Judgement?", TextGerman = "Und was läßt dich wissen, was der Tag der Entscheidung ist?" });
                        surah77.VerseList.Add(new Verse { Number = 15, TextBosnian = "Teško toga dana poricateljima!", TextEnglish = "Woe,[1819] that Day, to the deniers.", TextGerman = "Wehe an jenem Tag den Leugnern!" });
                        surah77.VerseList.Add(new Verse { Number = 16, TextBosnian = "Zar Mi nismo drevne narode uništili", TextEnglish = "Did We not destroy the former peoples?", TextGerman = "Haben Wir nicht die Früheren vernichtet?" });
                        surah77.VerseList.Add(new Verse { Number = 17, TextBosnian = "i poslije njih i one koji su za njima dolazili?!", TextEnglish = "Then We will follow them with the later ones.", TextGerman = "Hierauf lassen Wir ihnen die Späteren folgen." });
                        surah77.VerseList.Add(new Verse { Number = 18, TextBosnian = "Tako ćemo sa svim prestupnicima postupiti!", TextEnglish = "Thus do We deal with the criminals.", TextGerman = "So verfahren Wir mit den Übeltätern." });
                        surah77.VerseList.Add(new Verse { Number = 19, TextBosnian = "Teško taj dan poricateljima!", TextEnglish = "Woe, that Day, to the deniers.", TextGerman = "Wehe an jenem Tag den Leugnern!" });
                        surah77.VerseList.Add(new Verse { Number = 20, TextBosnian = "Zar vas od neznatne tekućine ne stvaramo,", TextEnglish = "Did We not create you from a liquid disdained?", TextGerman = "Haben Wir euch nicht aus verächtlichem Wasser erschaffen," });
                        surah77.VerseList.Add(new Verse { Number = 21, TextBosnian = "koju na pouzdano mjesto stavljamo", TextEnglish = "And We placed it in a firm lodging [i.e., the womb]", TextGerman = "das Wir dann in einem festen Aufenthaltsort haben sein lassen," });
                        surah77.VerseList.Add(new Verse { Number = 22, TextBosnian = "do roka određenoga?!", TextEnglish = "For a known extent.", TextGerman = "bis zu einem bekannten Zeitpunkt?" });
                        surah77.VerseList.Add(new Verse { Number = 23, TextBosnian = "Tako smo odredili, a divni li smo odreditelji!", TextEnglish = "And We determined [it], and excellent [are We] to determine.", TextGerman = "So haben Wir bemessen. Welch trefflicher Bemesser sind Wir!" });
                        surah77.VerseList.Add(new Verse { Number = 24, TextBosnian = "Teško taj dan poricateljima!", TextEnglish = "Woe, that Day, to the deniers.", TextGerman = "Wehe an jenem Tag den Leugnern!" });
                        surah77.VerseList.Add(new Verse { Number = 25, TextBosnian = "Zar Mi nismo učinili Zemlju sabiralištem", TextEnglish = "Have We not made the earth a container", TextGerman = "Haben Wir nicht die Erde zu einem Ort der Aufbewahrung gemacht" });
                        surah77.VerseList.Add(new Verse { Number = 26, TextBosnian = "živih i mrtvih", TextEnglish = "Of the living and the dead?", TextGerman = "- für die Lebenden und die Toten -" });
                        surah77.VerseList.Add(new Verse { Number = 27, TextBosnian = "i na njoj nepomične visoke planine postavili, i zar vam Mi ne dajemo da slatku vodu pijete?!", TextEnglish = "And We placed therein lofty, firmly set mountains and have given you to drink sweet water.", TextGerman = "und auf ihr festgegründete, hoch aufragende Berge gemacht und euch frisches Wasser zu trinken gegeben?" });
                        surah77.VerseList.Add(new Verse { Number = 28, TextBosnian = "A teško taj dan poricateljima!", TextEnglish = "Woe, that Day, to the deniers.", TextGerman = "Wehe an jenem Tag den Leugnern!" });
                        surah77.VerseList.Add(new Verse { Number = 29, TextBosnian = "Idite prema onome što ste neistinom smatrali.", TextEnglish = "[They will be told], 'Proceed to that which you used to deny.", TextGerman = "- Zieht los zu dem, was ihr stets für Lüge erklärt habt," });
                        surah77.VerseList.Add(new Verse { Number = 30, TextBosnian = "Idite prema dimu u tri prama razdvojenom,", TextEnglish = "Proceed to a shadow [of smoke] having three columns.", TextGerman = "zieht los zu einem Schatten mit drei Verzweigungen," });
                        surah77.VerseList.Add(new Verse { Number = 31, TextBosnian = "koji hlada neće davati i koji od plamena neće zaklanjati.", TextEnglish = "[But having] no cool shade and availing not against the flame.'", TextGerman = "der kein (wirklicher) Schattenspender ist und nicht (als Schutz) gegen die Flammen nützt." });
                        surah77.VerseList.Add(new Verse { Number = 32, TextBosnian = "On će kao kule iskre bacati", TextEnglish = "Indeed, it throws sparks [as huge] as a fortress,", TextGerman = "Sie wirft mit Funken wie Schlösser," });
                        surah77.VerseList.Add(new Verse { Number = 33, TextBosnian = "kao da su kamile riđe.", TextEnglish = "As if they were yellowish [black] camels.", TextGerman = "als wären sie gelbe Kamele." });
                        surah77.VerseList.Add(new Verse { Number = 34, TextBosnian = "Teško taj dan poricateljima!", TextEnglish = "Woe, that Day, to the deniers.", TextGerman = "Wehe an jenem Tag den Leugnern!" });
                        surah77.VerseList.Add(new Verse { Number = 35, TextBosnian = "Dan je to u kome oni neće ni prozboriti", TextEnglish = "This is a Day they will not speak,", TextGerman = "Das ist der Tag, an dem sie nicht reden werden" });
                        surah77.VerseList.Add(new Verse { Number = 36, TextBosnian = "i pravdanje im neće dozvoljeno biti.", TextEnglish = "Nor will it be permitted for them to make an excuse.", TextGerman = "und (es) ihnen nicht erlaubt wird, daß sie sich entschuldigen." });
                        surah77.VerseList.Add(new Verse { Number = 37, TextBosnian = "Teško taj dan poricateljima!", TextEnglish = "Woe, that Day, to the deniers.", TextGerman = "Wehe an jenem Tag den Leugnern!" });
                        surah77.VerseList.Add(new Verse { Number = 38, TextBosnian = "To je Dan sudnji, i vas i narode drevne ćemo sakupiti,", TextEnglish = "This is the Day of Judgement; We will have assembled you and the former peoples.", TextGerman = "- Das ist der Tag der Entscheidung; Wir haben euch mit den Früheren versammelt." });
                        surah77.VerseList.Add(new Verse { Number = 39, TextBosnian = "pa ako se lukavstvom kakvim budete znali poslužiti, dovijte se protiv Mene!", TextEnglish = "So if you have a plan, then plan against Me.", TextGerman = "Wenn ihr also eine List habt, so führt sie gegen Mich aus." });
                        surah77.VerseList.Add(new Verse { Number = 40, TextBosnian = "Teško taj dan poricateljima.", TextEnglish = "Woe, that Day, to the deniers.", TextGerman = "Wehe an jenem Tag den Leugnern!" });
                        surah77.VerseList.Add(new Verse { Number = 41, TextBosnian = "Oni koji su se Allaha bojali bit će među izvorima, u gustim baščama.", TextEnglish = "Indeed, the righteous will be among shades and springs", TextGerman = "Gewiß, die Gottesfürchtigen werden sich in Schatten und an Quellen befinden" });
                        surah77.VerseList.Add(new Verse { Number = 42, TextBosnian = "I među voćem koje budu željeli.", TextEnglish = "And fruits from whatever they desire,", TextGerman = "und bei Früchten von dem, was sie begehren." });
                        surah77.VerseList.Add(new Verse { Number = 43, TextBosnian = "Jedite i pijte, prijatno nek vam je, zbog onog što ste radili.'", TextEnglish = "[Being told], 'Eat and drink in satisfaction for what you used to do.'", TextGerman = "Eßt und trinkt als wohlbekömmlich für das, was ihr zu tun pflegtet." });
                        surah77.VerseList.Add(new Verse { Number = 44, TextBosnian = "Tako ćemo Mi one koji čine dobra djela nagraditi.", TextEnglish = "Indeed, We thus reward the doers of good.", TextGerman = "Gewiß, so vergelten Wir den Rechtschaffenen." });
                        surah77.VerseList.Add(new Verse { Number = 45, TextBosnian = "Teško taj dan poricateljima!", TextEnglish = "Woe, that Day, to the deniers.", TextGerman = "Wehe an jenem Tag den Leugnern!" });
                        surah77.VerseList.Add(new Verse { Number = 46, TextBosnian = "Jedite i naslađujte se, ali zakratko! - Vi ste zaista prestupnici!'", TextEnglish = "[O disbelievers], eat and enjoy yourselves a little; indeed, you are criminals.", TextGerman = "Eßt und genießt ein wenig, ihr seid ja Übeltäter." });
                        surah77.VerseList.Add(new Verse { Number = 47, TextBosnian = "Teško taj dan poricateljima!", TextEnglish = "Woe, that Day, to the deniers.", TextGerman = "Wehe an jenem Tag den Leugnern!" });
                        surah77.VerseList.Add(new Verse { Number = 48, TextBosnian = "A kad im se kaže: 'Namaz obavljajte!', oni namaz ne obavljaju.", TextEnglish = "And when it is said to them, 'Bow [in prayer],' they do not bow.", TextGerman = "Und wenn zu ihnen gesagt wird: Verbeugt euch!, verbeugen sie sich nicht." });
