                        surah83.VerseList.Add(new Verse { Number = 4, TextBosnian = "Zar ne misle takvi da će oživljeni biti", TextEnglish = "Do they not think that they will be resurrected", TextGerman = "Glauben jene nicht, daß sie auferweckt werden" });
