                        surah83.VerseList.Add(new Verse { Number = 7, TextBosnian = "Uistinu! Zapisano je razvratnicima da su u Sidždžinu,", TextEnglish = "No! Indeed, the record of the wicked is in sijjeen.", TextGerman = "Keineswegs! Das Buch der Sittenlosen ist wahrlich in Siggin." });
                        surah83.VerseList.Add(new Verse { Number = 8, TextBosnian = "a znaš li ti šta je Sidždžin?!", TextEnglish = "And what can make you know what is sijjeen?", TextGerman = "Und was läßt dich wissen, was Siggin ist?" });
