                        surah83.VerseList.Add(new Verse { Number = 12, TextBosnian = "a njega ne poriče drugi doli svaki prestupnik, grešnik,", TextEnglish = "And none deny it except every sinful transgressor.", TextGerman = "Und nur derjenige erklärt ihn für Lüge, der Übertretungen begeht und ein Sünder ist." });
