                        surah83.VerseList.Add(new Verse { Number = 14, TextBosnian = "Ne, naprotiv! Ono što su radili prekrilo je srca njihova,", TextEnglish = "No! Rather, the stain has covered their hearts of that which they were earning.[1880]", TextGerman = "Keineswegs! Vielmehr hat sich das, was sie zu erwerben pflegten, über ihren Herzen angesetzt." });
