                        surah83.VerseList.Add(new Verse { Number = 20, TextBosnian = "Knjiga je ispisana!", TextEnglish = "It is [their destination[1882] recorded in] a register inscribed", TextGerman = "(Es ist) ein Buch mit festen Eintragungen," });
                        surah83.VerseList.Add(new Verse { Number = 21, TextBosnian = "Uz nju su prisutni oni koji su bliski.", TextEnglish = "Which is witnessed by those brought near [to Allāh].", TextGerman = "in das die (Allah) Nahegestellten Einsicht nehmen." });
                        surah83.VerseList.Add(new Verse { Number = 22, TextBosnian = "Čestiti će, zaista, u užitku biti,", TextEnglish = "Indeed, the righteous will be in pleasure", TextGerman = "Die Frommen werden wahrlich in Wonne sein," });
