                        surah83.VerseList.Add(new Verse { Number = 26, TextBosnian = "na čijem kraju misk će biti, i neka se za to natječu oni koji hoće da se natječu!", TextEnglish = "The last of it[1884] is musk. So for this let the competitors compete.", TextGerman = "dessen Siegel Moschus ist -, und darum sollen die Wettbewerber wettkämpfen -," });
                        surah83.VerseList.Add(new Verse { Number = 27, TextBosnian = "A mješavina mu je iz Tesnima,", TextEnglish = "And its mixture is of Tasneem,[1885]", TextGerman = "und dessen Beimischung Tasnim ist," });
                        surah83.VerseList.Add(new Verse { Number = 28, TextBosnian = "vrela iz kojeg će oni bliski piti.", TextEnglish = "A spring from which those near [to Allāh] drink.", TextGerman = "aus einer Quelle, aus der die (Allah) Nahegestellten trinken." });
                        surah83.VerseList.Add(new Verse { Number = 29, TextBosnian = "Zaista su razvratnici ismijavali one koji vjeruju,", TextEnglish = "Indeed, those who committed crimes used to laugh at those who believed.", TextGerman = "Gewiß, diejenigen, die Übeltaten begingen, pflegten über diejenigen zu lachen, die glauben," });
                        surah83.VerseList.Add(new Verse { Number = 30, TextBosnian = "i kada su pored njih prolazili, jedni drugima su namigivali.", TextEnglish = "And when they passed by them, they would exchange derisive glances.", TextGerman = "und, wenn sie an ihnen vorbeikamen, einander zuzuzwinkern." });
                        surah83.VerseList.Add(new Verse { Number = 31, TextBosnian = "A kad su se porodicama svojim vraćali, vraćali su se šale zbijajući.", TextEnglish = "And when they returned to their people, they would return jesting.", TextGerman = "Und, wenn sie zu ihren Angehörigen zurückkehrten, kehrten sie zurück, indem sie es sich wohl sein ließen." });
                        surah83.VerseList.Add(new Verse { Number = 32, TextBosnian = "A kad bi ih vidjeli, rekli bi: 'Ovi su, doista, zalutali',", TextEnglish = "And when they saw them, they would say, 'Indeed, those are truly lost.'", TextGerman = "Und, wenn sie sie sahen, sagten sie: Diese gehen fürwahr in die Irre." });
                        surah83.VerseList.Add(new Verse { Number = 33, TextBosnian = "a oni nisu poslani da ih čuvaju.", TextEnglish = "But they had not been sent as guardians over them.", TextGerman = "Dabei waren sie doch nicht als Hüter über sie gesandt worden." });
                        surah83.VerseList.Add(new Verse { Number = 34, TextBosnian = "Taj dan oni koji su vjerovali nevjernicima će se podsmijavati,", TextEnglish = "So Today[1886] those who believed are laughing at the disbelievers,", TextGerman = "Heute aber lachen diejenigen, die glauben, über die Ungläubigen," });
