                        surah84.VerseList.Add(new Verse { Number = 12, TextBosnian = "i u Ognju će gorjeti.", TextEnglish = "And [enter to] burn in a Blaze.", TextGerman = "und der Feuerglut ausgesetzt sein." });
                        surah84.VerseList.Add(new Verse { Number = 13, TextBosnian = "Zaista je on sa porodicom svojom radostan bio,", TextEnglish = "Indeed, he had [once] been among his people in happiness;", TextGerman = "Er war ja froh inmitten seiner Angehörigen." });
                        surah84.VerseList.Add(new Verse { Number = 14, TextBosnian = "i mislio da se nikada neće vratiti,", TextEnglish = "Indeed, he had thought he would never return [to Allāh].", TextGerman = "Er meinte ja, daß er nicht zurückkehren würde." });
                        surah84.VerseList.Add(new Verse { Number = 15, TextBosnian = "a hoće! Gospodar njegov o njemu, zaista, sve zna!", TextEnglish = "But yes! Indeed, his Lord was ever, of him, Seeing.", TextGerman = "Ja doch! Gewiß, sein Herr sieht ihn wohl." });
                        surah84.VerseList.Add(new Verse { Number = 16, TextBosnian = "Ali ne! Kunem se rumenilom večernjim,", TextEnglish = "So I swear by the twilight glow", TextGerman = "Nein! Ich schwöre beim Abendrot" });
                        surah84.VerseList.Add(new Verse { Number = 17, TextBosnian = "i noći, i onim što ona sakuplja,", TextEnglish = "And [by] the night and what it envelops", TextGerman = "und der Nacht und dem, was sie zusammentreibt," });
                        surah84.VerseList.Add(new Verse { Number = 18, TextBosnian = "i Mjesecom kada se ispuni,", TextEnglish = "And [by] the moon when it becomes full", TextGerman = "und dem Mond, wenn er voll geworden ist." });
