                        surah84.VerseList.Add(new Verse { Number = 20, TextBosnian = "Pa šta im je, da ne vjeruju", TextEnglish = "So what is [the matter] with them [that] they do not believe,", TextGerman = "Was ist denn mit ihnen, daß sie nicht glauben" });
                        surah84.VerseList.Add(new Verse { Number = 21, TextBosnian = "i kad im se Kur'an uči na tlo licem ne padaju?!", TextEnglish = "And when the Qur’ān is recited to them, they do not prostrate [to Allāh]?", TextGerman = "und, wenn ihnen der Qur’an vorgelesen wird, sich nicht niederwerfen?" });
                        surah84.VerseList.Add(new Verse { Number = 22, TextBosnian = "Naprotiv, oni koji ne vjeruju poriču,", TextEnglish = "But those who have disbelieved deny,", TextGerman = "Aber nein! Diejenigen, die ungläubig sind, erklären (ihn) für Lüge." });
                        surah84.VerseList.Add(new Verse { Number = 23, TextBosnian = "a Allah najbolje zna šta oni u sebi kriju,", TextEnglish = "And Allāh is most knowing of what they keep within themselves.", TextGerman = "Doch Allah weiß sehr wohl, was sie (in ihren Herzen) für sich behalten." });
