                        surah85.VerseList.Add(new Verse { Number = 11, TextBosnian = "Zaista onima koji su vjerovali i radili dobra djela pripadaju bašče džennetske kroz koje rijeke teku, a to je uspjeh veliki.", TextEnglish = "Indeed, those who have believed and done righteous deeds will have gardens beneath which rivers flow. That is the great attainment.", TextGerman = "Gewiß, diejenigen, die glauben und rechtschaffene Werke tun, für sie wird es Gärten geben, durcheilt von Bächen; das ist der große Erfolg." });
                        surah85.VerseList.Add(new Verse { Number = 12, TextBosnian = "Kažnjavanje Gospodara tvoga zaista je žestoko!", TextEnglish = "Indeed, the assault [i.e., vengeance] of your Lord is severe.", TextGerman = "Das Zupacken deines Herrn ist wahrlich hart." });
                        surah85.VerseList.Add(new Verse { Number = 13, TextBosnian = "On ni iz čega stvara, i ponovo će to učiniti;", TextEnglish = "Indeed, it is He who originates [creation] and repeats.", TextGerman = "Gewiß, Er macht den Anfang und läßt wiederkehren." });
                        surah85.VerseList.Add(new Verse { Number = 14, TextBosnian = "On je Oprostitelj grijeha, Onaj koji puno voli,", TextEnglish = "And He is the Forgiving, the Affectionate,", TextGerman = "Und Er ist der Allvergebende und Liebevolle," });
                        surah85.VerseList.Add(new Verse { Number = 15, TextBosnian = "Gospodar Arša, Veličanstveni.", TextEnglish = "Honorable Owner of the Throne,", TextGerman = "der Herr des Thrones, der Ruhmvolle." });
                        surah85.VerseList.Add(new Verse { Number = 16, TextBosnian = "On radi što hoće.", TextEnglish = "Effecter of what He intends.", TextGerman = "(Er) tut alles, was Er will." });
